export default [
  ["D", "<big>D</big>\n\n<b>D, d</b>\n<i>четвёртая буква латинского алфавита; в сокращениях</i> (<i>преим. в надписях</i>): D. = Decimus (<i>praenomen</i>), dies, divus, deus, dominus, decurio; dabam я дал, учинил, написал (<i>перед указанием даты</i>); D. D. = dono (donum) dedit принес в дар; D. O. M. = deo optimo maxumo (maximo); <i>в надгробных надписях</i> D. M. = dis manibus божественным теням, <i>т. е.</i> душе, памяти (такого-то); D. P. S = de pecunia sua на свои средства, своим иждивением; D. N. = dominus noster (<i>при упоминании одного из последних цезарей</i>); D. D. D. = dat, dicat (<i>или</i> donat), dedicat даёт, дарит, посвящает (такой-то); a. d. = ante diem до (такого-то) дня (<i>в датах</i>); D = 500 (из |Ɔ, так как старинное Φ или C|Ɔ обозначало 1 000)."],
  ["da", "<big>da</big>\n\n<b>dā</b> <i>imper. sg. к</i>\ndo."],
  ["Daae", "<big>Daae</big>\n\n<b>Daae, ārum</b> <i>v. l.</i>\n= Dahae."],
  ["dabin", "<big>dabin</big>\n\n<b>dabin</b> <i>Pl</i>\n= dabisne (<i>к</i> do)."],
  ["Daci", "<big>Daci</big>\n\n<b>Dācī, ōrum</b> <i>m</i>\nдаки, <i>народность фракийск. происхождения, обитавшая в Дакии</i> <i>C, V, H etc.</i>"],
  ["Dacia", "<big>Dacia</big>\n\n<b>Dācia, ae</b> <i>f</i>\nДакия (<i>прибл. нын. Румыния с Седмиградией</i>) <i>T</i>."],
  ["Dacicus", "<big>Dacicus</big>\n\n<b>Dācicus, a, um</b> [Daci <i>и</i> Dacus I]\nдакский (bellum <i>PM, Spart</i>; arma <i>Cld</i>)."],
  ["Daciscanus", "<big>Daciscanus</big>\n\n<b>Dāciscānus</b> <i>v. l.</i>\n= Daciscus."],
  ["Dacisci", "<big>Dacisci</big>\n\n<b>Dāciscī, ōrum</b> <i>m</i>\nдакиски, <i>племя в Месии</i> <i>Vop</i>."],
  ["Daciscus", "<big>Daciscus</big>\n\n<b>Dāciscus</b> <i>и</i> <b>Dāciscānus, a, um</b> [Dacisci]\nдакискский <i>Lact, Treb</i>."],
  ["dacr-", "<big>dacr-</big>\n\n<b>dacr-</b> <i>арх.</i>\n= lacr-."],
  ["Dactyli Idaei", "<big>Dactyli Idaei</big>\n\n<b>Dactylī Idaeī</b> <i>m</i> (<i>греч.</i>; <i>лат.</i> Digiti Idaei)\nидейские дактили, <i>демоны фригийской горы Иды, жрецы богини Кибелы</i> <i>PM</i>."],
  ["dactylicus", "<big>dactylicus</big>\n\n<b>dactylicus, a, um</b> [dactylus] <i>стих.</i>\nдактилический (pes, versus; numerus <i>C</i>)."],
  ["dactyliotheca", "<big>dactyliotheca</big>\n\n<b>dactyliothēca, ae</b> <i>f</i> (<i>греч.</i>)\n<b>1)</b> ларчик для хранения колец и перстней <i>M</i>;\n<b>2)</b> дактилиотека, коллекция перстней с печатями <i>PM</i>."],
  ["dactylis", "<big>dactylis</big>\n\n<b>dactylis, idis</b> <i>f</i> (<i>греч.</i>)\nдактилида, <i>сорт винограда с удлинёнными («пальцевидными») ягодами</i> <i>PM</i>."],
  ["dactylos", "<big>dactylos</big>\n\n<b>dactylos, ī</b> <i>m</i>\n= dactylus."],
  ["dactylus", "<big>dactylus</big>\n\n<b>dactylus, ī</b> <i>m</i> (<i>греч.</i>)\n<b>1)</b> финик <i>PM, Pall</i>;\n<b>2)</b> разновидность проса (<i>Panicum dactylon</i>, L.) <i>PM</i>;\n<b>3)</b> дактиль, стихотворная стопа –UU <i>C, Q</i>."],
  ["Dacus", "<big>Dacus</big>\n\n<b>I Dācus, ī</b> <i>m sg. к</i>\nDaci.\n\n<b>II Dācus, a, um</b> <i>St</i>\n= Dacicus."],
  ["daduchus", "<big>daduchus</big>\n\n<b>dādūchus, ī</b> <i>m</i> (<i>греч.</i>)\nфакелоносец (<i>в элевсинских мистериях</i>) <i>Fronto</i>."],
  ["Daedala", "<big>Daedala</big>\n\n<b>I Daedala, ōrum</b> <i>n</i>\nДедалы, <i>горная цепь и приморский город в Карий, на границе с Ликией</i> <i>L, PM</i>.\n\n<b>II Daedala, ae</b> <i>f</i>\nДедала, <i>область и город на реке Хоасп</i> (<i>сев.-зап. Индия</i>) <i>QC</i>."],
  ["Daedaleus", "<big>Daedaleus</big>\n\n<b>Daedalēus (-eus), a, um</b> [Daedalus 1]\nдедалов (Icarus <i>H</i>; iter <i>Prp</i>)."],
  ["Daedali montes", "<big>Daedali montes</big>\n\n<b>Daedalī montes</b>\nДедаловы горы (<i>в сев.-зап. Индии</i>) <i>Just</i>."],
  ["Daedalion", "<big>Daedalion</big>\n\n<b>Daedaliōn, ōnis</b> <i>m</i>\nДедалион, <i>сын Люцифера, превращенный в ястреба</i> <i>O</i>."],
  ["Daedalus", "<big>Daedalus</big>\n\n<b>I Daedalus (-os), ī</b> <i>m</i> (<i>греч.</i> «художник»)\nДедал, <i>афинянин из рода Эрехтидов, отец Икара, строитель критского Лабиринта</i> <i>O, V, H etc.</i>\n\n<b>II daedalus, a, um</b> (<i>греч.</i>)\n<b>1)</b> искусный, мастерской, затейливый (natura daedala rerum <i>Lcr</i>; verborum daedala lingua <i>Lcr</i>);\n<b>2)</b> хитрый, коварный (Circe <i>L</i>);\n<b>3)</b> искусно сделанный, художественный, изукрашенный (tellus <i>Lcr</i>; tecta <i>V</i>)."],
  ["daemon", "<big>daemon</big>\n\n<b>daemōn, onis</b> (<i>acc.</i> onem <i>и</i> ona; <i>acc. pl.</i> ones <i>и</i> onas) <i>m</i> (<i>греч.</i>; <i>лат.</i> genius)\n<b>1)</b> демон, дух, гений (daemones sunt genere animalia, corpore aeria, tempore aeterna <i>Ap</i>);\n<b>2)</b> демон, злой дух, бес (Satanas princeps hujus mali generis, <i>sc.</i> daemonum <i>Tert</i>)."],
  ["daemoniacus", "<big>daemoniacus</big>\n\n<b>I daemoniacus, a, um</b> [daemon]\nдемонский, бесовский <i>Eccl</i>.\n\n<b>II daemoniacus, ī</b> <i>m</i>\nодержимый бесом <i>Eccl</i>."],
  ["daemonicola", "<big>daemonicola</big>\n\n<b>daemoni–cola, ae</b> <i>m</i> [colo II]\nпочитатель демонов, <i>т. е.</i> язычник <i>Aug</i>."],
  ["daemonicus", "<big>daemonicus</big>\n\n<b>daemonicus, a, um</b> <i>Tert</i>\n= daemoniacus I."],
  ["daemonie", "<big>daemonie</big>\n\n<b>daemoniē, ēs</b> <i>f</i> <i>Man</i>\n= daemonium."],
  ["daemonium", "<big>daemonium</big>\n\n<b>daemonium, ī</b> <i>n</i>\n<b>1)</b> божество (<i>мелкое</i>), дух <i>Man, Tert</i>;\n<b>2)</b> злой дух <i>Tert</i>."],
  ["daemonius", "<big>daemonius</big>\n\n<b>daemonius, a, um</b>\nбожественный, дивный <i>Man</i>."],
  ["Dafn-", "<big>Dafn-</big>\n\n<b>Dafn-</b> <i>поздн.</i>\n= Daphn-."],
  ["Dahae", "<big>Dahae</big>\n\n<b>Dahae, ārum</b> <i>m</i>\nдаги, <i>многочисленная скифская народность на сев. Кавказе (в Дагестане) и в Средней Азии</i> <i>V, QC, L, T</i>."],
  ["Dalmatae", "<big>Dalmatae</big>\n\n<b>Dalmatae, ārum</b> <i>m</i>\nдалматы, <i>народность, населявшая Далматию</i> <i>C, VP, Fl</i>."],
  ["Dalmatensis", "<big>Dalmatensis</big>\n\n<b>Dalmatēnsis, e</b> <i>Treb</i>\n= Dalmaticus I."],
  ["Dalmatia", "<big>Dalmatia</big>\n\n<b>Dalmatia, ae</b> <i>f</i>\nДалматия, <i>область на вост. побережье Адриатического моря, к сев.-зап. от Иллирии</i> (<i>позднее вошла в состав Иллирика</i>) <i>C, T, O</i>."],
  ["dalmaticatus", "<big>dalmaticatus</big>\n\n<b>dalmaticātus, a, um</b>\nодетый в далматик (<i>см.</i> Dalmaticus I) <i>Lampr</i>."],
  ["Dalmaticus", "<big>Dalmaticus</big>\n\n<b>I Dalmaticus, a, um</b> [Dalmatae]\nдалматский (bellum <i>C, VP, Su</i>; triumphus, <i>sc.</i> Asinii Pollionis <i>H</i>): Dalmatica vestis <i>Is</i> далматик (<i>просторная, длинная туника с длинными и широкими рукавами</i>).\n\n<b>II Dalmaticus, ī</b> <i>m</i>\n<i>agnomen</i> Л. Цецилия Метелла <i>C</i>."],
  ["Dama", "<big>Dama</big>\n\n<b>I Dāma, ae</b> <i>m</i>\n<i>имя раба</i> <i>H</i>.\n\n<b>II dāma (damma), ae</b> <i>f</i>\nдикая коза, серна <i>H, V etc.</i>"],
  ["damalio", "<big>damalio</big>\n\n<b>damalio, ōnis</b> <i>m</i> (<i>греч.</i>)\nтелёнок <i>Lampr</i>."],
  ["Damalis", "<big>Damalis</big>\n\n<b>Damalis, idis</b> <i>f</i>\nДамалида, <i>женское имя</i> <i>H</i>."],
  ["Damaratus", "<big>Damaratus</big>\n\n<b>Dāmarātus, ī</b> <i>m</i> <i>дор. v. l.</i>\n= Demaratus."],
  ["Damascena", "<big>Damascena</big>\n\n<b>I Damascēna, ōrum</b> <i>n</i> (<i>sc.</i> pruna)\nдамасские сливы <i>M</i>.\n\n<b>II Damascēna, ae</b> <i>и</i> <b>Damascēnē, ēs</b> <i>f</i>\nобласть Дамаска (<i>часть Келесирии</i>) <i>PM, Mela</i>."],
  ["Damascenus", "<big>Damascenus</big>\n\n<b>I Damascēnus, a, um</b> [Damascus]\nдамасский <i>PM etc.</i>\n\n<b>II Damascēnus, ī</b> <i>m</i>\nжитель Дамаска <i>Vlg</i>."],
  ["Damascus", "<big>Damascus</big>\n\n<b>Damascus, ī</b> <i>f</i>\nДамаск, <i>столица Келесирии на реке Хрисорроэ</i> <i>QC, Fl, St, Vlg</i>."],
  ["Damasichton", "<big>Damasichton</big>\n\n<b>Damasichtōn, onis</b> <i>m</i>\nДамасихтон, <i>один из сыновей Ниобы</i> <i>O</i>."],
  ["Damasippus", "<big>Damasippus</big>\n\n<b>Damasippus, ī</b> <i>m</i>\n<b>1)</b> L. Junius D., <i>один из самых преданных приверженцев Мария, казнённый Суллой</i> <i>Sl, C</i>;\n<b>2)</b> Licinius D., <i>римский ценитель статуй <i>C</i>; собеседник Горация, впоследствии философ-стоик</i> <i>H</i>."],
  ["damiurgus", "<big>damiurgus</big>\n\n<b>dāmiūrgus, ī</b> <i>m</i> <i>дор. v. l.</i>\n= demiurgus."],
  ["damma", "<big>damma</big>\n\n<b>damma, ae</b> <i>f</i> <i>v. l.</i>\n= dama."],
  ["dammula", "<big>dammula</big>\n\n<b>dammula, ae</b> <i>f</i> [<i>demin. к</i> dama]\nкозочка <i>Ap</i>."],
  ["damnabilis", "<big>damnabilis</big>\n\n<b>damnābilis, e</b> [damno]\nзаслуживающий осуждения, постыдный, недостойный <i>Treb, Sid</i>."],
  ["damnabiliter", "<big>damnabiliter</big>\n\n<b>damnābiliter</b>\nпостыдным образом <i>Aug</i>."],
  ["damnas", "<big>damnas</big>\n\n<b>damnās</b> <i>indecl.</i> [<i>из</i> damnat(u)s <i>от</i> damno]\nприсуждённый, обязанный (dare alicui aliquid <i>Q, G, Dig</i>)."],
  ["damnaticius", "<big>damnaticius</big>\n\n<b>damnātīcius, a, um</b>\nподлежащий осуждению <i>или</i> осуждённый <i>Tert</i>."],
  ["damnatio", "<big>damnatio</big>\n\n<b>damnātio, ōnis</b> <i>f</i> [damno]\n<b>1)</b> осуждение, приговор, обвинительный вердикт (dignus damnatione <i>C</i>; d. ad aeterna supplicia <i>Lact</i>): d. tantae pecuniae <i>C</i> осуждение по делу о столь большой сумме; in confessa damnatione esse <i>PM</i> быть безусловно отвергнутым;\n<b>2)</b> осуждение (вечное), проклятие <i>Eccl</i>."],
  ["damnator", "<big>damnator</big>\n\n<b>damnātor, ōris</b> <i>m</i> [damno]\nосуждающий <i>Eccl</i>."],
  ["damnatorius", "<big>damnatorius</big>\n\n<b>damnātōrius, a, um</b> [damno]\nотносящийся к осуждению, обвинительный (tabella <i>Su</i>; judicium <i>C</i>): ferrum damnatorium <i>Amm</i> меч палача."],
  ["damnatus", "<big>damnatus</big>\n\n<b>damnātus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> damno;\n<b>2.</b> <i>adj.</i> достойный осуждения, преступный (homo <i>C</i>; manus <i>Prp, Pt</i>)."],
  ["damnifico", "<big>damnifico</big>\n\n<b>damni–fico, —, —, āre</b>\n<b>1)</b> причинять ущерб (d. aliquem <i>Aug</i>);\n<b>2)</b> осуждать, карать (aliquem pecunia <i>Eccl</i>)."],
  ["damnificus", "<big>damnificus</big>\n\n<b>damni–ficus, a, um</b> [facio]\nприносящий убытки, наносящий ущерб <i>Pl, Pall</i>."],
  ["damnigerulus", "<big>damnigerulus</big>\n\n<b>damnigerulus, a, um</b> <i>Pl</i>\n= damnificus."],
  ["damno", "<big>damno</big>\n\n<b>damno, āvī, ātum, āre</b> [damnum]\n<b>1)</b> признавать виновным, осуждать, приговаривать (aliquem inauditum <i>T</i>): damnari inter sicarios <i>C</i> быть осуждённым за убийство; damnari alicujus rei <i>C, Cs, L etc.</i>, de aliqua re <i>C, T etc.</i> <i>и</i> ob aliquam rem <i>T</i> быть осуждённым за что-л.; existimatione damnatus <i>C</i> осуждённый общественным мнением; damnari lege repetundarum <i>T</i> быть осуждённым на основании закона о лихоимстве; contra edictum fecisse damnari <i>C</i> быть осуждённым за неподчинение указу; d. aliquem exsilio <i>Sen, T</i> присудить кого-л. к изгнанию; tertia parte agri damnari <i>L</i> быть присуждённым к конфискации одной трети территории; d. aliquem capite <i>C, Cs, Nep, L</i> (capitis <i>C, L</i>) приговорить кого-л. к высшей мере наказания (<i>смерти, лишению гражданских прав, тюремному заключению, изгнанию</i>); damnari capitali crimine (judicio) <i>VM</i> <i>или</i> capitalis poenae <i>L</i> (<i>тж.</i> morte <i>Fl, SenT</i>, ad mortem <i>и</i> ad extremum supplicium <i>T</i>) быть приговорённым к смертной казни; damnari furti <i>C</i> быть осуждённым за кражу; d. aliquem ad bestias <i>Su</i> приговорить кого-л. к отдаче на растерзание диким зверям; damnari alicui <i>VM, Sen</i> быть осуждённым в чью-л. пользу; d. aliquem morti (Orco) <i>Lcr, O</i> осудить (обречь) кого-л. на смерть;\n<b>2)</b> порицать, критиковать (libros, scriptorem <i>Q</i>): judex damnatur, ubi nocens absolvitur <i>погов. PS</i> оправдание преступника — позор для судьи || отвергать, считать негодным (boves ad laborem <i>PM</i>): d. fidem alicujus <i>QC</i> отказать кому-л. в доверии; d. aliquem summae stultitiae <i>C</i> приписывать кому-л. величайшую глупость;\n<b>3)</b> обязывать (d. aliquem aliquid dare <i>H</i>): d. aliquem votis <i>V</i> связать кого-л. обетами, <i>т. е.</i> исполнить просьбу (<i>того, кто дал обет</i>); damnari voti (<i>редко</i> voto) <i>L etc.</i> достичь желаемого;\n<b>4)</b> обвинять, добиваться осуждения (aliquem lege Julia <i>T</i>);\n<b>5)</b> отвергать, отказываться: ales (<i>собир.</i>) damnavit vesci <i>Sil</i> (священные) куры отказались клевать (<i>дурная примета в птицегадании</i>); numquam somno damnatus lumina (<i>acc. gr.</i>) <i>Lcn</i> никогда не засыпающий."],
  ["damnose", "<big>damnose</big>\n\n<b>damnōsē</b> [damnosus]\nс нанесением убытка, с причинением ущерба: d. bibere <i>H</i> пить хозяину в убыток, <i>т. е.</i> разорительно много, безмерно."],
  ["damnosus", "<big>damnosus</big>\n\n<b>damnōsus, a, um</b> [damnum]\n<b>1)</b> вредный, вредоносный, гибельный, пагубный, опасный (homo <i>Pl</i>; libido <i>H</i>; bellum <i>L</i>); разрушительный (senectus <i>O</i>); damnosa dies <i>H</i> всеразрушающее время;\n<b>2)</b> потерпевший ущерб, понёсший убытки <i>или</i> вводящий себя в убытки, расточительный (senex <i>Pl</i>; d. in aedificando <i>Su</i>)."],
  ["damnum", "<big>damnum</big>\n\n<b>damnum, ī</b> <i>n</i> [<i>из</i> *dapnom, <i>одного корня с</i> daps; <i>по др.</i> — <i>одного корня с</i> dare]\n<b>1)</b> убыток, потеря, ущерб, урон (damna ac detrimenta <i>C</i>): d. naturae <i>L</i> природный недостаток; damno alicujus <i>Su</i> в ущерб кому-л.; damno esse alicui <i>O</i> быть вредным (опасным) для кого-л.; duarum cohortium damno <i>Cs</i> потеряв (в боях) две когорты; cum damno meo magno <i>Pl</i> с большим для меня ущербом; damna (caelestia) lunae <i>Sen, H</i> ущерб луны; damno augeri <i>Ter</i> (affici <i>C</i>), <i>тж.</i> d. capere <i>Pl</i>, accipere <i>H</i>, facere (contrahere) <i>C</i>, ferre <i>O</i> <i>или</i> pati <i>Sen</i> понести ущерб; d. dare <i>C, Dig</i> причинить ущерб; damnum infectum — <i>см.</i> infectus 3; damni nihil facis <i>C</i> ты ничего (от этого) не теряешь; unius verbi damna grandia ferre <i>O</i> тяжело поплатиться за единое слово; ingentibus damnis <i>PJ</i> ценой огромных затрат (жертв);\n<b>2)</b> поражение (rei publicae <i>VP</i>; militiae <i>Just</i>);\n<b>3)</b> пеня, штраф <i>Pl, C, L, AG</i>."],
  ["Damocles", "<big>Damocles</big>\n\n<b>Dāmoclēs, is</b> <i>m</i>\nДамокл, <i>приближённый Дионисия Сиракузского Старшего</i> <i>C</i>."],
  ["Damoetas", "<big>Damoetas</big>\n\n<b>Dāmoetās, ae</b> <i>m</i>\nДамэт, <i>имя пастуха</i> <i>V</i>."],
  ["Damon", "<big>Damon</big>\n\n<b>Dāmōn, ōnis</b> <i>m</i>\nДамон:\n<b>1)</b> <i>афинский музыкант, учитель Перикла и Сократа</i> <i>C</i>;\n<b>2)</b> <i>философ-пифагореец в Сиракузах</i> (<i>времён Дионисия</i>) <i>C</i>."],
  ["dan", "<big>dan</big>\n\n<b>dān</b> <i>Pl</i>\n= dasne (<i>к</i> do)."],
  ["Danae", "<big>Danae</big>\n\n<b>Danaē, ēs</b> <i>f</i>\nДаная, <i>мать Персея; по италийским преданиям, мать Давна, предка царя Турна</i> <i>H, O, V</i>."],
  ["Danaeius", "<big>Danaeius</big>\n\n<b>Danaēius, a, um</b> [Danae]\nданаин: heros D. <i>O</i> = Perseus."],
  ["Danai", "<big>Danai</big>\n\n<b>Danaī, ōrum</b> <i>и</i> <b>um</b> <i>m</i>\nданайцы, <i>т. е.</i> аргивяне, греки (<i>преим. об осаждавших Трою</i>) <i>Lcr, C, V, Prp</i>."],
  ["Danaidae", "<big>Danaidae</big>\n\n<b>Danaidae, ārum</b> <i>m</i> <i>SenT</i>\n= Danai."],
  ["Danaides", "<big>Danaides</big>\n\n<b>Danaides, um</b> <i>f</i>\nДанаиды, <i>50 дочерей Даная; выйдя замуж ва 50 сыновей Эгипта, все они, ва исключением Гиперместры, жены Линкея, в первую же ночь, по приказанию Даная, умертвили своих мужей; в наказание за это остальные 49 Данаид были осуждены вечно наполнять в подземном царстве бездонную бочку</i> <i>SenT</i>."],
  ["Danaus", "<big>Danaus</big>\n\n<b>I Danaus, ī</b> <i>m</i>\nДанай, <i>сын Бела и брат Эгипта, от которого он со своими 50 дочерьми бежал в Пелопоннес, где основал Аргос</i> (<i>отсюда</i> — Danai) <i>C</i>: agmen Danai <i>O</i>, Danai puellae <i>и</i> genus infame <i>H</i>, <i>тж.</i> Danai proles <i>Tib</i> <i>и</i> Danai femina turba <i>Prp</i> = Danaides.\n\n<b>II Danaus, a, um</b>\nданайский, <i>перен.</i> аргивский, греческий <i>Prp, O etc.</i>"],
  ["Dandari", "<big>Dandari</big>\n\n<b>Dandarī, ōrum</b> <i>и</i> <b>Dandaridae, ārum</b> <i>m</i>\nдандары, <i>скифская народность на берегах Дона и Palus Maeotis</i> (<i>Азовского моря</i>) <i>PM, T</i>."],
  ["Dandarica", "<big>Dandarica</big>\n\n<b>Dandarica, ae</b> <i>f</i>\nобласть народа Dandari <i>T</i>."],
  ["Dandaridae", "<big>Dandaridae</big>\n\n<b>Dandaridae, ārum</b> <i>v. l.</i>\n= Dandari."],
  ["danista", "<big>danista</big>\n\n<b>danīsta, ae</b> <i>m</i> (<i>греч.</i>; <i>лат.</i> fenerator)\nростовщик <i>Pl</i>."],
  ["danisticus", "<big>danisticus</big>\n\n<b>danīsticus, a, um</b> [danista]\nростовщический (genus <i>Pl</i>)."],
  ["dano", "<big>dano</big>\n\n<b>dano, —, —, ere</b> <i>арх.</i> <i>Pl</i>\n= do."],
  ["dans", "<big>dans</big>\n\n<b>dāns, dantis</b> <i>m</i> [do]\nдающий, податель <i>H</i>."],
  ["Danubinus", "<big>Danubinus</big>\n\n<b>Dānubinus, a, um</b> [Danubius]\nдунайский (limes <i>Sid</i>)."],
  ["Danubius", "<big>Danubius</big>\n\n<b>Dānubius (Dānuvius), ī</b> <i>m</i>\nДунай (<i>преим. в верхнем и среднем его течении; нижняя часть обычно именуется</i> Hister) <i>Cs, VP, Sen etc.</i>"],
  ["dapalis", "<big>dapalis</big>\n\n<b>dapālis, e</b> [daps]\nтрапезный, торжественно пиршественный (cena <i>Aus</i>): Juppiter d. <i>Cato</i> Юпитер, которому приносится в жертву трапеза."],
  ["Daphnaeus", "<big>Daphnaeus</big>\n\n<b>Daphnaeus, a, um</b> <i>Amm</i>\n= Daphnensis I."],
  ["daphne", "<big>daphne</big>\n\n<b>I daphnē, ēs</b> <i>f</i> (<i>греч.</i>)\nлавровое дерево, лавр <i>Pt</i>.\n\n<b>II Daphnē, ēs</b> <i>f</i>\nДафна:\n<b>1)</b> <i>нимфа, дочь речного бога Пенея, превращенная в лавр</i> <i>O</i>;\n<b>2)</b> <i>предместье Антиохии Сирийской с храмом Аполлона</i> <i>Vop, Capit, Amm etc.</i>"],
  ["Daphnensis", "<big>Daphnensis</big>\n\n<b>I Daphnēnsis, e</b> [Daphne II, 2]\nдафнийский (palatium <i>CTh</i>).\n\n<b>II Daphnēnsis, is</b> <i>m</i>\nжитель Дафны <i>Eutr</i>."],
  ["Daphnicus", "<big>Daphnicus</big>\n\n<b>Daphnicus, a, um</b> <i>Vulc</i>\n= Daphnensis I."],
  ["Daphnis", "<big>Daphnis</big>\n\n<b>Daphnis, idis</b> <i>m</i>\nДафнид:\n<b>1)</b> <i>сын Меркурия, сицилийский (или критский) юноша-пастух, создатель пастушеской («буколической») песни</i> <i>O, V</i>;\n<b>2)</b> Lutatius D., <i>римск. грамматик</i> <i>Su</i>."],
  ["daphnon", "<big>daphnon</big>\n\n<b>daphnōn, ōnis</b> (<i>acc.</i> ōna, <i>acc. pl.</i> ōnas) <i>m</i>\nлавровая роща <i>M, Pt</i>."],
  ["dapino", "<big>dapino</big>\n\n<b>dapino, —, —, āre</b> [daps]\nставить на стол, подавать к столу (victum <i>Pl</i>)."],
  ["dapis", "<big>dapis</big>\n\n<b>dapis, is</b> <i>f</i> <i>Jvc</i>\n= daps."],
  ["daps", "<big>daps</big>\n\n<b>daps, dapis</b> <i>f</i>\n<b>1)</b> торжественная (культовая) трапеза (reddere Jovi dapem <i>H</i>; ad dapem convenire <i>Ap</i>);\n<b>2)</b> пища, питание: humana dape pascere <i>O</i> кормить человечьим мясом (<i>ср.</i> 4); dapes inemptae <i>H</i> непокупные (домашние, свои) продукты;\n<b>3)</b> обед, пиршество: veniam dapibus orare <i>O</i> извиняться за (скромный) обед;\n<b>4)</b> <i>pl.</i> навоз (humanae dapes <i>PM</i> — <i>ср.</i> 1)."],
  ["dapsile", "<big>dapsile</big>\n\n<b>dapsile</b> (<i>греч.</i>)\nизобильно, богато (convivari <i>Su</i>)."],
  ["dapsilis", "<big>dapsilis</big>\n\n<b>dapsilis, e</b> (<i>греч.</i>)\nбогатый, обильный (dotes <i>Pl</i>; copia facundiae <i>Ap</i>); роскошный (lectus <i>Pl</i>)."],
  ["dapsiliter", "<big>dapsiliter</big>\n\n<b>dapsiliter</b> <i>Naev</i>\n= dapsile."],
  ["dardanarius", "<big>dardanarius</big>\n\n<b>dardanārius, ī</b> <i>m</i>\nскупщик зерна (для повышения цен на него), хлебный спекулянт <i>Dig</i>."],
  ["Dardani", "<big>Dardani</big>\n\n<b>Dardanī, ōrum</b> <i>m</i>\nжители Дардании <i>Cs, C</i>."],
  ["Dardania", "<big>Dardania</big>\n\n<b>Dardania, ae</b> <i>f</i>\nДардания:\n<b>1)</b> <i>область и город в предгорьях Иды, метрополия Трои, впоследствии — область Энея</i> <i>O</i> (<i>отсюда</i> Дарданеллы); <i>поэт. V</i> = Troja;\n<b>2)</b> <i>область в Верхней Месии</i> (<i>территориально отчасти соответствует нын. Югославии</i>) <i>Vr</i>."],
  ["Dardanides", "<big>Dardanides</big>\n\n<b>Dardanidēs, ae</b> (<i>pl.</i> <b>Dardanidae, ārum</b> <i>или</i> <b>um</b>) <i>m</i>\nсын <i>или</i> потомок Дардана <i>O</i>; <i>поэт.</i> = Aeneas <i>V</i>, <i>тж.</i> = Scipio Africanus <i>Sil</i>; <i>pl. V</i> = троянцы."],
  ["Dardanis", "<big>Dardanis</big>\n\n<b>Dardanis, idis</b> <i>f</i> [Dardanides]\nДарданида:\n<b>1)</b> <i>поэт.</i> троянка <i>O</i>;\n<b>2)</b> <i>V</i> = Creusa 2."],
  ["Dardanius", "<big>Dardanius</big>\n\n<b>Dardan(i)us, a, um</b> [Dardania]\nдарданский, <i>перен.</i> троянский (gens <i>H, V</i>) <i>или</i> римский (ductor, <i>sc.</i> Scipio Africanus <i>Sil</i>): Dardanius senex <i>O</i> = Priamus; Dardanius minister <i>M</i> = Ganymedes; Dardania Roma <i>O</i> основанный дарданцами (троянцами) Рим."],
  ["Dardanus", "<big>Dardanus</big>\n\n<b>I Dardanus, ī</b> <i>f</i>\nДардан, <i>город в Троаде на Геллеспонте</i> <i>L</i>.\n\n<b>II Dardanus, ī</b> <i>m</i>\nДардан, <i>сын Юпитера и Электры, дочери Атланта, родоначальник троянцев</i> (<i>по римск. версии — и римлян</i>) <i>V etc.</i>\n\n<b>III Dardanus, ī</b> <i>m</i>\n<b>1)</b> <i>sg. к</i> Dardani;\n<b>2)</b> <i>имя известного финикийского мага</i> <i>PM, Ap</i>."],
  ["Dares", "<big>Dares</big>\n\n<b>Darēs, ētis</b> <i>m</i>\nДарет, <i>спутник Энея, искусный в кулачном бою</i> <i>V</i>."],
  ["Dareus", "<big>Dareus</big>\n\n<b>Dārēus (Dārīus), ī</b> <i>m</i>\nДарий:\n<b>1)</b> D. Hystaspes, <i>отец Ксеркса, царств. в 521-485 гг. до н. э.</i> <i>C, Nep, Just etc.</i>;\n<b>2)</b> D. Ochus (<i>или</i> Nothus), <i>сын Артаксеркса Долгорукого, отец Артаксеркса и Кира Младшего, царств. в 424-405 гг. до н. э.</i> <i>Just</i>;\n<b>3)</b> D. Codomannus, <i>царств. в 336-330 гг. до н. э.; потерпел поражение от Александра Македонского, убит Бессом</i> <i>Pl, C, QC etc.</i>"],
  ["darius", "<big>darius</big>\n\n<b>dārīus, (-os), ī</b> <i>m</i>\nдарик (<i>персидская золотая монета</i>) <i>Aus</i>."],
  ["dasios", "<big>dasios</big>\n\n<b>dasios, ī</b> <i>m</i> (<i>греч.</i>)\nдасий, стихотворная стопа UUU– –."],
  ["data", "<big>data</big>\n\n<b>data, ōrum</b> <i>n</i> [do]\n<b>1)</b> даяния, дары <i>Pl, Prp</i>;\n<b>2)</b> расходы: ratio acceptorum et datorum <i>C</i> счёт поступлений (доходов) и расходов."],
  ["Datames", "<big>Datames</big>\n\n<b>Datamēs, is</b> <i>m</i>\nДатам, <i>кариец, сатрап и полководец Артаксеркса Мемнона и Пария Оха</i> <i>Nep</i>."],
  ["datarius", "<big>datarius</big>\n\n<b>datārius, a, um</b> [do] <i>шутл.</i>\nмогущий быть данным, <i>т. е.</i> даровой: nulla est mihi salus dataria <i>Pl</i> зря я не кланяюсь; lingua dataria <i>Pl</i> щедрый на посулы язык."],
  ["datatim", "<big>datatim</big>\n\n<b>datātim</b> [dato]\nпередавая, давая друг другу, перекидываясь (d. ludere, <i>sc.</i> pila <i>Pl</i>)."],
  ["datin", "<big>datin</big>\n\n<b>datin</b> <i>Pl</i>\n= datisne (<i>к</i> do)."],
  ["datio", "<big>datio</big>\n\n<b>datio, ōnis</b> <i>f</i> [do]\n<b>1)</b> давание, подача: d. signi <i>PM</i> подача сигнала (<i>к бою</i>); d. legum <i>C</i> законодательство;\n<b>2)</b> право отчуждения (<i>дарения и пр.</i>) <i>L</i>."],
  ["Datis", "<big>Datis</big>\n\n<b>Dātis, idis</b> <i>m</i> (<i>acc.</i> im <i>и</i> in)\nДатис, <i>родом мидянин, вместе с Атаферном командовал персидской армией при Марафоне, где потерпел поражение</i> (<i>490 г. до н. э.</i>) <i>Nep</i>."],
  ["dativus", "<big>dativus</big>\n\n<b>I datīvus, a, um</b>\nданный, назначенный (по завещанию) (tutor <i>Q</i>).\n\n<b>II datīvus, ī</b> <i>m</i> [do]\n(<i>sc.</i> casus) дательный падеж <i>Q, AG</i>."],
  ["dato", "<big>dato</big>\n\n<b>dato, āvī, —, āre</b> [<i>intens. к</i> do]\nдавать, подавать, отпускать (fenore argentum <i>Pl</i>; frumentum populo <i>PM</i>)."],
  ["dator", "<big>dator</big>\n\n<b>dator, ōris</b> <i>m</i> [do]\n<b>1)</b> податель: d. laetitiae <i>V</i> = Bacchus; d. salutis <i>Eccl</i> = Aesculapius; d. legis <i>Eccl</i> = Moses;\n<b>2)</b> подающий мяч (<i>в игре</i>) <i>Pl</i>."],
  ["datum", "<big>datum</big>\n\n<b>datum, ī</b> <i>n</i> [do]\nдар, подарок, подношение <i>Aug</i>; <i>см. тж.</i> data."],
  ["datus", "<big>datus</big>\n\n<b>I datus, (ūs)</b> <i>m</i> [do]\nдаяние, подача: meo datu <i>Pl</i> через меня (как подателя).\n\n<b>II datus, a, um</b> <i>part. pf. к</i>\ndo."],
  ["daucos", "<big>daucos</big>\n\n<b>daucos, ī</b> <i>m</i> <i>PM</i>\n= daucum."],
  ["daucum", "<big>daucum</big>\n\n<b>daucum, ī</b> <i>n</i> (<i>греч.</i>)\nморковь (<i>Daucus Carota</i>, L.) <i>CC</i>."],
  ["Daulias", "<big>Daulias</big>\n\n<b>Daulias, adis</b> <i>f</i> [Daulis I]\nдавлидский: ales D. <i>O</i> = hirundo; puella D. <i>V</i> = Procne <i>и</i> Philomele."],
  ["Daulis", "<big>Daulis</big>\n\n<b>I Daulis, idis</b> <i>f</i>\nДавлида, <i>укреплённый город в Фокиде, к сев.-вост. от Дельф</i> <i>L, O</i>.\n\n<b>II Daulis, idis</b> <i>f</i> <i>SenT</i>\n= Daulias."],
  ["Daulius", "<big>Daulius</big>\n\n<b>Daulius, a, um</b> [Daulis]\nдавлидский <i>O</i> (<i>v. l.</i>)."],
  ["Daunia", "<big>Daunia</big>\n\n<b>Daunia, ae</b> <i>и</i> <b>Daunias, adis</b> <i>f</i>\nДавния, <i>т. е. северная часть Апулии</i> <i>PM, Mela</i>; <i>поэт.</i> Апулия <i>H</i>."],
  ["Dauniacus", "<big>Dauniacus</big>\n\n<b>Dauniacus, a, um</b> <i>Sil</i>\n= Daunius."],
  ["Daunias", "<big>Daunias</big>\n\n<b>Daunias, adis</b> <i>f</i> <i>v. l.</i>\n= Daunia."],
  ["Daunius", "<big>Daunius</big>\n\n<b>Daunius, a, um</b> [Daunia]\n<b>1)</b> давнийский: D. heros <i>V</i> = Turnus; Daunia gens <i>V</i> = рутулы; Daunia dea <i>V</i> = Juturna, <i>сестра Турна</i>;\n<b>2)</b> <i>поэт.</i> апулийский; <i>у</i> <i>H</i> (<i>уроженца этой местности</i>) — римский (Camena <i>H</i>)."],
  ["Daunus", "<big>Daunus</big>\n\n<b>Daunus, ī</b> <i>m</i>\nДавн, <i>миф. царь сев. Апулии (Давнии), сын Пилумна и Данаи, отец (или предок) Турна</i> <i>V, H, O</i>."],
  ["Davus", "<big>Davus</big>\n\n<b>Dāvus (-os), ī</b> <i>m</i>\nДав, <i>имя раба</i> <i>Pl, Ter, H</i>."],
  ["de", "<big>de</big>\n\n<b>I dē</b> <i>praep. cum abl.</i>\n1) с: dejicere se de muro <i>Cs</i> броситься со стены; decedere de foro <i>Nep</i> уйти с форума (<i>т. е.</i> оставить политическую деятельность); descendere de rostris <i>C</i> сойти с трибуны; de tergo <i>Pl</i> сзади; diem de die <i>L</i> со дня на день (изо дня в день); detrahere anulum de digito <i>Ter, C</i> снять кольцо с пальца; de via lassus <i>Pl</i> (fessus <i>Cs</i>) усталый (устав) с дороги; pendere de aliqua re <i>O etc.</i> свисать с чего-л., <i>т. е.</i> висеть на чём-л.;\n<b>2)</b> из: de finibus suis exire <i>Cs</i> выйти за пределы своей территории; homo de plebe <i>C</i> человек из народа; nemo de nobis <i>C</i> никто из нас; unus de multis <i>C</i> один из многих; aliquis de ponte <i>J</i> кто-л. из стоящих на мосту (<i>т. е.</i> нищих); signum factum de marmore <i>O</i> статуя, сделанная из мрамора; verno de flore corona <i>O</i> венок из весенних цветов; de eodem genere <i>C</i> того же рода; de suis pecuniis <i>C</i> на свой счёт; de publico <i>C</i> на счёт государства;\n<b>3)</b> от, у: emere de aliquo <i>Cato</i> покупать у кого-л.; quaerere (audire) de aliquo <i>C</i> спрашивать у кого-л., слышать от кого-л.;\n<b>4)</b> о: loqui de re aliqua <i>C</i> говорить о чём-л.; traditur de Homero <i>C</i> передают о Гомере; liber de contemnenda morte <i>Sen</i> книга о презрении к смерти; flere de filii morte <i>C</i> оплакивать смерть сына;\n<b>5)</b> по поводу, по вопросу о, касательно: Regulus de captivis commutandis Romam missus est <i>C</i> Регул был послан в Рим по вопросу об обмене пленными; de numero dierum fidem servare <i>Cs</i> соблюсти установленный срок; de cetero <i>Sen, QC</i> <i>и</i> de ceteris <i>Sl</i> что касается остального;\n<b>6)</b> согласно, по: de mea sententia <i>Pl</i> по моему мнению; de amicorum consilio <i>C</i> по совету друзей; ea de re, ea de causa <i>C, Cs etc.</i> по этой причине; de improviso <i>C, Cs</i> непредвиденным образом, неожиданно; de integro <i>C</i> сызнова, заново;\n<b>7)</b> в, во время в течение: de tertia vigilia <i>Cs</i> в третью смену; de mense Decembri <i>C</i> (ещё) в декабре месяце; de nocte <i>Ter, C etc.</i> ночью; de die <i>Pl, Ter, H etc.</i> днём; multa de nocte <i>C</i> глубокой ночью; de tempore <i>bH</i> в обычное время; de prandio <i>Pl</i> (тотчас же) после еды.\n\n<b>II dē-</b>\n<i>приставка, выражающая преим.</i>:\n<b>1)</b> <i>отделение</i> (decedere);\n<b>2)</b> <i>устранение, лишение</i> (dearmare);\n<b>3)</b> <i>движение вниз</i> (decidere);\n<b>4)</b> <i>недостаток, отсутствие</i> (deesse, dementia);\n<b>5)</b> <i>завершение действия</i> (debellare)."],
  ["dea", "<big>dea</big>\n\n<b>dea, ae</b> (<i>dat. и abl. pl.</i> di(i)s, deis <i>и</i> deabus) <i>f</i> [deus]\nбогиня: d. bellica <i>O</i> богиня-воительница (= Minerva); d. siderea <i>Prp</i> звёздная богиня, <i>т. е.</i> ночь; d. venatrix <i>или</i> silvarum <i>O</i> богиня-охотница <i>или</i> богиня лесов (= Diana); deae novem <i>O</i> девять богинь, <i>т. е.</i> муз; deae triplices <i>O</i> = Parcae."],
  ["deacinatus", "<big>deacinatus</big>\n\n<b>de–acinātus, a, um</b> [acinus]\nосвобождённый (очищенный) от виноградных ягод (dolia <i>Cato</i>)."],
  ["dealbatio", "<big>dealbatio</big>\n\n<b>dealbātio, ōnis</b> <i>f</i> [dealbo]\n<b>1)</b> беление, побелка, <i>перен.</i> очищение (peccatorum <i>Aug</i>);\n<b>2)</b> белизна <i>Aug</i>."],
  ["dealbator", "<big>dealbator</big>\n\n<b>dealbātor, ōris</b> <i>m</i> [dealbo]\nпроизводящий побелку, маляр, штукатур <i>CJ</i>."],
  ["dealbo", "<big>dealbo</big>\n\n<b>de–albo, āvī, ātum, āre</b> [albus]\n<b>1)</b> белить, покрывать извёсткой, штукатурить (columnas <i>C</i>; parietem calce <i>Vtr</i>);\n<b>2)</b> <i>перен.</i> обелять, очищать (peccata <i>Vlg</i>)."],
  ["deambulacrum", "<big>deambulacrum</big>\n\n<b>deambulācrum, ī</b> <i>n</i> [deambulo]\nместо (аллея) для гуляния, проход (между колоннадами) <i>Vlg</i>."],
  ["deambulatio", "<big>deambulatio</big>\n\n<b>deambulātio, ōnis</b> <i>f</i> [deambulo]\nпрогулка, гуляние <i>Ter, CA, Vlg, Eccl</i>."],
  ["deambulatorium", "<big>deambulatorium</big>\n\n<b>de–ambulātōrium, ī</b> <i>n</i>\nместо для прогулок <i>Capit</i>."],
  ["deambulo", "<big>deambulo</big>\n\n<b>de–ambulo, āvī, ātum, āre</b>\nгулять, прогуливаться, прохаживаться (eamus deambulatum <i>Cato</i>; d. in litore <i>Su</i>)."],
  ["deamo", "<big>deamo</big>\n\n<b>de–amo, āvī, ātum, āre</b>\n<b>1)</b> безмерно, без ума любить (aliquem <i>Pl</i>);\n<b>2)</b> полностью одобрять, находить весьма приятным (munera <i>Pl</i>);\n<b>3)</b> быть весьма обязанным, искренно признательным (deamo te <i>Ter</i>)."],
  ["deargento", "<big>deargento</big>\n\n<b>de–argento, āvī, ātum, āre</b> [argentum]\n<b>1)</b> отнять деньги, ограбить (aliquem deargentassere <i>LM</i> — <i>арх. inf. fut.</i>);\n<b>2)</b> покрывать серебром, серебрить (idola <i>Hier</i>)."],
  ["dearmo", "<big>dearmo</big>\n\n<b>de–armo, āvī, ātum, āre</b>\n<b>1)</b> разоружать, обезоруживать (exercitum hostium <i>L</i>);\n<b>2)</b> (<i>об оружии</i>) отнимать (sagittas <i>Ap</i>)."],
  ["deartuo", "<big>deartuo</big>\n\n<b>de–artuo, āvī, ātum, āre</b> [artus]\nразрывать на части, <i>перен.</i> обездоливать, разорять (deartuatus sum <i>Pl</i>)."],
  ["deascio", "<big>deascio</big>\n\n<b>de–ascio, āvī, ātum, āre</b> [ascia]\n<b>1)</b> обрубать (обтёсывать) топором (deasciatus stipes <i>Eccl</i>);\n<b>2)</b> <i>шутл.</i> обмануть, обобрать (aliquem <i>Pl</i>)."],
  ["deaurator", "<big>deaurator</big>\n\n<b>de–aurātor, ōris</b> <i>m</i>\nзолотильщик <i>CJ</i>."],
  ["deauro", "<big>deauro</big>\n\n<b>de–auro, āvī, ātum, āre</b>\nпокрывать золотом, золотить <i>CTh, Eccl</i>."],
  ["debacchor", "<big>debacchor</big>\n\n<b>dē–bacchor, ātus sum, ārī</b>\nбезумствовать, неистовствовать (ingenti luxuria <i>Capit</i>); бушевать (ignes debacchantur <i>H</i>)."],
  ["debattuo", "<big>debattuo</big>\n\n<b>dē–battuo, —, —, ere</b>\nтолкать, бить, <i>sensu obsceno</i> обрабатывать (aliquam <i>Pt</i>)."],
  ["debellator", "<big>debellator</big>\n\n<b>dēbellātor, ōris</b> <i>m</i> [debello]\nпобедитель, усмиритель (ferarum <i>V</i>); покоритель (Britanni litoris <i>Cld</i>)."],
  ["debellatrix", "<big>debellatrix</big>\n\n<b>dēbellātrīx, īcis</b> <i>f</i> [debellator]\nпобедительница, покорительница <i>Tert, Lact</i>."],
  ["debello", "<big>debello</big>\n\n<b>dē–bello, āvī, ātum, āre</b>\n<b>1)</b> оканчивать войну (cum aliquo <i>L</i>); <i>impers.</i> debellatum est <i>L, T</i> война окончена; debellato (<i>abl. abs.</i>) <i>L</i> по окончании войны;\n<b>2)</b> окончательно побеждать, сразить (aliquem <i>T, PM etc.</i>); одолевать (hostem <i>T</i>);\n<b>3)</b> укрощать, побороть (voluptates <i>Sen</i>); подавлять (superbos <i>V</i>);\n<b>4)</b> решать, окончить (rixam <i>H</i>)."],
  ["debeo", "<big>debeo</big>\n\n<b>dēbeo, uī, itum, ēre</b> [<i>из</i> *dehibeo <i>от</i> de + habeo]\n<b>1)</b> быть должным, задолжать (alicui pecuniam <i>C</i>): aliquid satis dato d. <i>C</i> получить что-л. в долг под соответствующий залог; illi, quibus debeo <i>C</i> мои кредиторы; illi, qui debent <i>C</i> (debentes <i>L, Sen</i>) должники; animam d. <i>погов. Ter</i> задолжать душу (<i>т. е.</i> быть по уши в долгах) || <i>pass.</i> deberi причитаться, следовать (pecunia debetur alicui <i>C etc.</i>): maxima debetur puero reverentia <i>J</i> к ребёнку следует относиться с величайшим уважением;\n<b>2)</b> быть обязанным (d. alicui gratiam <i>C</i>, salutem, vitam <i>O</i>); ощущать необходимость: illa multo gravius aestimare debemus <i>Cs</i> мы должны это считать гораздо более серьёзным; dicere debentia dici <i>H</i> сказать то, что должно быть сказано;\n<b>3)</b> <i>преим. pass.</i> быть обречённым: urbem cerno d. <i>O</i> я вижу, что (троянцам) суждено построить город (Рим); deberi bustis <i>O</i> подлежать сожжению на кострах; habet, quod sibi debebatur <i>Pt</i> он получил то, что заслужил; debitus conjunx <i>O</i> предназначенный судьбой супруг (суженый); Pergama debita <i>V</i> обречённый (на гибель) Пергам; deberi morti <i>H</i> (fatis <i>V</i>) быть подвластным смерти (обречённым на смерть); tu, nisi ventis debes ludibrium, cave <i>H</i> остерегайся, если ты не должен (<i>т. е.</i> чтобы тебе не) стать игралищем ветров. — <i>См. тж.</i> debitum <i>и</i> debitus."],
  ["debibo", "<big>debibo</big>\n\n<b>dē–bibo, —, —, ere</b>\nпить (<i>из чего-л.</i>) (<i>sc.</i> flumen <i>Sol</i>)."],
  ["debil", "<big>debil</big>\n\n<b>dēbil</b> <i>adj.</i> <i>арх. Enn</i>\n= debilis I."],
  ["debilis", "<big>debilis</big>\n\n<b>I dēbilis, e</b> [de + habilis]\n<b>1)</b> слабый (manus <i>O</i>); немощный (corpus <i>C</i>); дряхлый (senex <i>C</i>); бессильный (ferrum <i>V</i>); обессилевший (metu <i>Ter</i>); слабосильный (equi <i>L</i>); разбитый параличом (crus <i>Su</i>);\n<b>2)</b> пустой, призрачный (umbra <i>O</i>).\n\n<b>II dēbilis, is</b> <i>m</i> [debilis I]\nинвалид, калека <i>Sen, QC, Lad</i>."],
  ["debilitas", "<big>debilitas</big>\n\n<b>dēbilitās, ātis</b> <i>f</i> [debilis I]\nрасслабленность (corporis <i>C</i>); бессилие, дряхлость (debilitate pedum invalidus <i>T</i>); слабость (animi <i>C</i>): d. linguae <i>C</i> косноязычие."],
  ["debilitatio", "<big>debilitatio</big>\n\n<b>dēbilitātio, ōnis</b> <i>f</i> [debilito]\nрасслабленность, бессилие, немощь, изнеможение <i>Ap</i>: d. animi <i>C</i> угнетённое (подавленное) настроение."],
  ["debiliter", "<big>debiliter</big>\n\n<b>dēbiliter</b> <i>adv.</i> [debilis I]\nбессильно, немощно (stupere <i>Pac</i>)."],
  ["debilito", "<big>debilito</big>\n\n<b>dēbilito, āvī, ātum, āre</b> [debilis I]\n<b>1)</b> ослаблять, расслаблять, обессиливать, превращать в инвалида: debilitari morbo <i>CC</i> обессилеть от болезни; debilitari corpore <i>C</i> ослабеть телом; hiems oppositis pumicibus debilitat mare <i>H</i> зима ослабляет напор моря, противопоставляя ему скалы (<i>т. е.</i> скалами разбивает морские валы);\n<b>2)</b> калечить, увечить (membra lapidibus <i>C</i>);\n<b>3)</b> лишать энергии, отнимать мужество (aliquem metu, dolore <i>C</i>): debilitari lacrimis <i>C</i> дать волю слезам; debilitari a jure cognoscendo <i>C</i> убояться изучения права (возыметь отвращение к нему)."],
  ["debitio", "<big>debitio</big>\n\n<b>dēbitio, ōnis</b> <i>f</i> [debeo]\n<b>1)</b> задолженность (pecuniae <i>C</i>);\n<b>2)</b> долг (d. gratiae <i>C</i>)."],
  ["debitor", "<big>debitor</big>\n\n<b>dēbitor, ōris</b> <i>m</i> [debeo]\n<b>1)</b> должник <i>C, Cs, H etc.</i>: d. idoneus <i>PJ, Dig</i> платёжеспособный должник;\n<b>2)</b> обязанный: d. vitae <i>O</i> обязанный (кому-л.) жизнью; d. voti <i>M</i> обязанный выполнить обет (<i>ввиду исполнившегося желания</i>)."],
  ["debitrix", "<big>debitrix</big>\n\n<b>dēbitrīx, īcis</b> <i>f</i> [debitor]\nдолжница <i>Dig</i>: d. delictorum <i>Tert</i> обязанная отвечать за свои проступки."],
  ["debitum", "<big>debitum</big>\n\n<b>dēbitum, ī</b> <i>n</i> [debeo]\n<b>1)</b> долг, задолженность (d. alicui solvere <i>C</i>): d. naturae reddere <i>Nep</i> уплатить долг природе, <i>т. е.</i> умереть;\n<b>2)</b> обязанность (d. nepotum nutriendorum <i>VM</i>): super d. <i>и</i> plus debito <i>Sen</i> сверх меры, чересчур; minus debito <i>Sen</i> меньше, чем следует."],
  ["debitus", "<big>debitus</big>\n\n<b>dēbitus, a, um</b> [debeo]\n<b>1)</b> должный, предназначенный (alicui debita tellus <i>V</i>);\n<b>2)</b> причитающийся (в виде долга) (pecunia <i>Cs</i>);\n<b>3)</b> заслуженный (poena <i>C</i>; praemia <i>V</i>);\n<b>4)</b> настоятельный, необходимый (officia <i>C</i>);\n<b>5)</b> обречённый (morti <i>H</i>). — <i>См. тж.</i> debeo."],
  ["deblatero", "<big>deblatero</big>\n\n<b>dē–blatero, āvī, —, āre</b>\nвыбалтывать, разбалтывать (vicinis omnibus <i>Pl</i>); оттараторить, отбарабанить (deblateratis versuum multis milibus <i>AG</i>)."],
  ["debriatus", "<big>debriatus</big>\n\n<b>dēbriātus, a, um</b> [de + ebrius]\n<b>1)</b> хмельной, пьяный <i>Eccl</i>;\n<b>2)</b> <i>перен.</i> опьянённый, упоённый (spiritus sancti gratia <i>Aug</i>)."],
  ["debrio", "<big>debrio</big>\n\n<b>dēbrio, āvī, ātum, āre</b> [de + ebrio]\nпоить допьяна (aliquem aliqua re <i>Eccl</i>)."],
  ["debucino", "<big>debucino</big>\n\n<b>dē–būcino, —, —, āre</b>\nраструбить, <i>перен.</i> расславить, раззвонить <i>Tert</i>."],
  ["debui", "<big>debui</big>\n\n<b>dēbuī</b> <i>pf. к</i>\ndebeo."],
  ["decachinno", "<big>decachinno</big>\n\n<b>dē–cachinno, —, —, āre</b>\nвысмеивать; <i>pass.</i> быть предметом насмешек <i>Tert</i>."],
  ["decacuminatio", "<big>decacuminatio</big>\n\n<b>dēcacūminātio, ōnis</b> <i>f</i> [decacumino]\nсрезание верхушки (d. cupressi, cedri <i>PM</i>)."],
  ["decacumino", "<big>decacumino</big>\n\n<b>dē–cacūmino, —, —, āre</b> [cacumen]\nсрезать верхушку (<i>у дерева</i>) <i>Col</i>."],
  ["decada", "<big>decada</big>\n\n<b>decada, ae</b> <i>f</i> <i>Eccl</i>\n= decas."],
  ["decalautico", "<big>decalautico</big>\n\n<b>dē–calautico, —, —, āre</b> [calautica]\nсорвать головную повязку, снять с лица покров <i>LM</i>."],
  ["decalogus", "<big>decalogus</big>\n\n<b>decalogus, ī</b> <i>m</i> (<i>греч.</i>)\nдесятословие, десять заповедей <i>Eccl</i>."],
  ["decalvatio", "<big>decalvatio</big>\n\n<b>dēcalvātio, ōnis</b> <i>f</i> [decalvo]\nснятие волос <i>Hier</i>."],
  ["decalvo", "<big>decalvo</big>\n\n<b>decālvo, —, —, āre</b>\nлишать волос <i>Veg, Hier</i>."],
  ["decania", "<big>decania</big>\n\n<b>decānia, ōrum</b> <i>n</i>\nгруппа из десяти знаков Зодиака <i>Man</i>."],
  ["decanicum", "<big>decanicum</big>\n\n<b>decānicum, ī</b> <i>n</i>\nжилое помещение на десять монахов <i>CTh</i>."],
  ["decano", "<big>decano</big>\n\n<b>dē–cano, —, —, ere</b>\nвоспевать, славить (aliquem <i>Eccl</i>)."],
  ["decantatio", "<big>decantatio</big>\n\n<b>dēcantātio, ōnis</b> <i>f</i> [decanto]\n<b>1)</b> распевание (psalmorum <i>Eccl</i>);\n<b>2)</b> болтливость, болтовня <i>Hier</i>."],
  ["decanto", "<big>decanto</big>\n\n<b>dē–canto, āvī, ātum, āre</b>\n<b>1)</b> распевать, пропеть (elegos <i>H</i>); кончать пение (jam decantant <i>C</i>);\n<b>2)</b> <i>презр.</i> отбарабанить, надоедливо повторять одно и то же, бубнить (fabulas <i>C, Sen</i>; praecepta <i>C</i>);\n<b>3)</b> провозглашать, громко называть (tribus <i>Lcn</i>);\n<b>4)</b> околдовать, заворожить (carmine <i>Ap</i>); заморочить (verbis <i>Ap</i>)."],
  ["decanus", "<big>decanus</big>\n\n<b>decānus, ī</b> <i>m</i> [decem]\n<b>1)</b> командир отделения из 10 человек <i>Veg</i>;\n<b>2)</b> декан, старший в группе из 10 монахов <i>Hier</i>;\n<b>3)</b> старший могильщик <i>CTh</i>."],
  ["decaproti", "<big>decaproti</big>\n\n<b>decaprōtī, ōrum</b> <i>m</i> (<i>греч.</i>) <i>Dig</i>\n= decemprimi."],
  ["decaprotia", "<big>decaprotia</big>\n\n<b>decaprōtia, ae</b> <i>f</i> (<i>греч.</i>) <i>Dig</i>\n= decemprimatus."],
  ["decargyrus", "<big>decargyrus</big>\n\n<b>decargyrus, a, um</b> (<i>греч.</i>)\nравный 10 серебряным денариям (nummus <i>CTh</i>)."],
  ["decarno", "<big>decarno</big>\n\n<b>dē–carno, —, —, āre</b> [caro]\nснимать мясо <i>Veg</i>."],
  ["decarpo", "<big>decarpo</big>\n\n<b>dēcarpo</b> <i>Cato</i>\n= decerpo."],
  ["decas", "<big>decas</big>\n\n<b>decas, adis</b> <i>f</i> (<i>греч.</i>)\nдекада, десяток <i>Eccl</i>."],
  ["decastylos", "<big>decastylos</big>\n\n<b>decastȳlos, on</b> (<i>греч.</i>)\nдесятиколонный <i>Vtr</i>."],
  ["decaulesco", "<big>decaulesco</big>\n\n<b>dē–caulēsco, —, —, ere</b> [caulis]\nпускать (расти в) стебель <i>PM</i>."],
  ["decedo", "<big>decedo</big>\n\n<b>dē–cēdo, cessī, cessum, ere</b>\n<b>1)</b> уходить, уезжать, отправляться (d. Italia <i>Sl</i>; d. ex Sicilia <i>C</i> <i>и</i> d. Sicilia <i>Nep</i>; d. domum <i>L</i>); сниматься, удаляться (ex his regionibus <i>Cs</i>; de colle <i>Cs</i>); выходить, покидать (d. pugna <i>L</i>); отбывать (de <i>и</i> ex, e provincia <i>C</i>);\n<b>2)</b> отклоняться (suo cursu <i>Cs</i>): de via decessisse <i>C</i> отклониться от своего пути, <i>тж. перен.</i> изменить своим привычкам; de generis nobilitate d. <i>Pall</i> (<i>о растениях</i>) вырождаться; luminibus exstinctis d. via <i>Su</i> из-за погасших огней сбиться с пути;\n<b>3)</b> сторониться, избегать (alicui <i>Cs</i>): d. calori <i>V</i> спасаться от (дневного) зноя;\n<b>4)</b> прекращать, оставлять: d. de foro <i>Nep</i> оставить политическую (общественную) деятельность; d. de scaena <i>C</i> бросить сцену;\n<b>5)</b> (<i>тж.</i> d. de vita <i>C</i> <i>и</i> d. vita <i>Dig</i>) расставаться с жизнью, умирать (d. sine liberis <i>Q</i>; d. morbo <i>Su, Eutr</i>); adulescens decessit <i>Sen</i> он умер в юношеском возрасте;\n<b>6)</b> спадать, схлынуть (amnis decedens <i>PM</i>; Nilus tarde decessit <i>Sen</i>);\n<b>7)</b> (<i>о небесных светилах</i>) заходить (sol decedens <i>V</i>): decedente die <i>V</i> на закате;\n<b>8)</b> быть на ущербе, убывать (luna decedens <i>AG</i>);\n<b>9)</b> утихать, униматься, проходить (febris decessit <i>CC</i>; ira mente decessit <i>QC</i>): priusquam ea cura decederet patribus Romanis <i>L</i> не успел римский сенат освободиться от этой заботы (как...);\n<b>10)</b> исчезать, выдыхаться (odor decedit <i>H</i>);\n<b>11)</b> отказываться, отступать(ся) (d. de sententia <i>C</i> <i>и</i> d. sententia <i>T</i>);\n<b>12)</b> уклоняться (officio <i>и</i> de officio <i>L</i>);\n<b>13)</b> уступать, поступаться (jure suo <i>L</i> <i>и</i> de suo jure <i>C</i>): d. alicui de via <i>C</i> <i>и</i> via <i>Su</i> уступать кому-л. дорогу; d. peritis <i>H</i> уступать (место более) опытным; de altera parte tertia agri d. <i>Cs</i> уступить другую треть территории;\n<b>14)</b> уступать, быть ниже (хуже) (ubi non Hymetto mella decedunt <i>H</i>);\n<b>15)</b> пропадать, убавляться (de summa nil decedet <i>Ter</i>): aliquid d. suis opibus credere <i>L</i> считать что-л. ущербом для своего могущества;\n<b>16)</b> прекращаться, кончаться: questioni materia decessit <i>L</i> материал расследования оказался исчерпанным; nobis opinio decedat... <i>T</i> откажемся от мнения (не будем думать) (будто...);\n<b>17)</b> протекать, развиваться: prospere decedentibus rebus <i>Su</i> ввиду благоприятного хода (завершения) событий; decedentia certis tempora momentis <i>H</i> регулярно сменяющие друг друга периоды."],
  ["Decelea", "<big>Decelea</big>\n\n<b>Decelēa (-īa), ae</b> <i>f</i>\nДекелея, <i>укреплённый город в Аттике, к сев.-вост. от Афин</i> (<i>с 413 г. до н. э. и до конца Пелопоннесской войны служил базой для спартанцев</i>) <i>Nep</i>."],
  ["decem", "<big>decem</big>\n\n<b>decem</b> <i>adj. num. card.</i>\n<b>1)</b> десять (hominum milia d. <i>Cs</i>): decem primi <i>или</i> decemprimi <i>C</i> десять старшин (начальников) десяти декурий (<i>на которые делились сенаты муниципий и колоний</i>), <i>т. е.</i> 10 старших — обыкновенно из 100 — сенаторов;\n<b>2)</b> неопределённое множество (animi d. in pectore incerti certant <i>Pl</i>)."],
  ["December", "<big>December</big>\n\n<b>I December, bris</b> (<i>abl.</i> ī) <i>m</i> (<i>sc.</i> mensis)\nдекабрь (gelidus, fumosus <i>O</i>).\n\n<b>II december, bris, bre</b> [decem]\nдесятый (<i>тк. о месяце; римский год начинался с 1 марта, и лишь впоследствии декабрь стал 12-м месяцем года</i>); декабрьский (Calendae, Nonae, Idus <i>C</i>): libertas decembris <i>H</i> свобода, которую предоставляют декабрьские Сатурналии."],
  ["decemjugis", "<big>decemjugis</big>\n\n<b>decem–jugis, e</b> [jugum]\nзапряжённый десятью лошадьми, десятиконный (currus <i>Su</i>)."],
  ["decemmestris", "<big>decemmestris</big>\n\n<b>decem–mēstris, e</b> [mensis]\nдесятимесячный (annus, partus <i>Cens</i>)."],
  ["decemmodia", "<big>decemmodia</big>\n\n<b>decem–modia, ae</b> <i>f</i> (<i>sc.</i> corbula)\nкорзина ёмкостью в 10 модиев <i>Col</i>."],
  ["decemmodius", "<big>decemmodius</big>\n\n<b>decem–modius, a, um</b>\nсодержащий десять модиев (мер) (corbulae <i>Col</i>)."],
  ["decempeda", "<big>decempeda</big>\n\n<b>decem–peda, ae</b> <i>f</i> [pes]\nизмерительный шест в десять футов (<i>см.</i> pes): urbem decempeda dividere <i>C</i> разделить город с помощью 10-футового шеста."],
  ["decempedalis", "<big>decempedalis</big>\n\n<b>decem–pedālis, e</b>\nдесятифутовый (spatium <i>CJ</i>)."],
  ["decempedator", "<big>decempedator</big>\n\n<b>decempedātor, ōris</b> <i>m</i> [decempeda]\nмежевщик, землемер <i>C</i>."],
  ["decemplex", "<big>decemplex</big>\n\n<b>decem–plex, icis</b> [plico]\nдесятеричный, десятикратный <i>Vr, Nep, Tert</i>."],
  ["decemplicatus", "<big>decemplicatus</big>\n\n<b>decem–plicātus, a, um</b>\nдесятикратный, удесятерённый <i>Vr</i>."],
  ["decemprimatus", "<big>decemprimatus</big>\n\n<b>decemprīmātus, ūs</b>\nдецемпримат, звание decemprimi <i>Dig</i>."],
  ["decemprimi", "<big>decemprimi</big>\n\n<b>decem–prīmī, ōrum</b> <i>m</i> — <i>см.</i>\ndecem."],
  ["decemremis", "<big>decemremis</big>\n\n<b>decem–rēmis, e</b> [remus]\nс десятью рядами вёсел (гребцов) (navis <i>PM</i>)."],
  ["decemscalmus", "<big>decemscalmus</big>\n\n<b>decem–scalmus, a, um</b>\nдесятивесельный (actuariolae <i>C</i>)."],
  ["decemvir", "<big>decemvir</big>\n\n<b>decem–vir, ī</b>\n<i>обыкн. <i>pl.</i>; писали преим.</i> Xvir, Xviri (<i>gen. pl.</i> ōrum <i>и</i> um) <i>m</i> децемвир, <i>член центрального административного или законодательного органа из 10 человек; член коллегии десяти</i>:\n<b>1)</b> decemviri legibus scribundis <i>L, C</i> децемвиры-законодатели (<i>авторы законов XII таблиц, 451-449 гг. до н. э.</i>);\n<b>2)</b> decemviri sacrorum <i>или</i> sacris faciundis <i>L</i> коллегия жрецов, хранителей и истолкователей Сивиллиных книг (<i>с течением времени число этих децемвиров дошло до 60</i>);\n<b>3)</b> decemviri stlitibus (<i>т. е.</i> litibus) judicandis <i>C</i> один из судебных органов древнего Рима;\n<b>4)</b> decemviri agris metiendis dividendisque <i>L, C</i> орган по распределению новых земель и землеустройству переселенцев."],
  ["decemviralis", "<big>decemviralis</big>\n\n<b>decemvirālis, e</b> [decemvir]\nотносящийся к децемвирам, децемвирский (potestas <i>L, T</i>): leges decemvirales <i>L</i> = законы XII таблиц; odium decemvirale <i>L</i> ненависть к тирании децемвиров legibus scribundis (<i>см.</i> decemvir)."],
  ["decemviraliter", "<big>decemviraliter</big>\n\n<b>decemvirāliter</b> [decemvir]\nпо примеру децемвиров (loqui <i>Sid</i>)."],
  ["decemviratus", "<big>decemviratus</big>\n\n<b>decemvirātus, ūs</b> <i>m</i>\nдецемвират, достоинство <i>или</i> звание децемвира <i>C, Q, Fl</i>: decemviratum petere <i>L</i> домогаться поста децемвира."],
  ["decennalia", "<big>decennalia</big>\n\n<b>decennālia, ium</b> <i>и</i> <b>ōrum</b> <i>n</i>\nпразднества в честь десятилетия <i>Treb etc.</i>"],
  ["decennalis", "<big>decennalis</big>\n\n<b>decennālis, e</b> [decem + annus]\nдесятилетний (bellum <i>Amm, Hier</i>)."],
  ["decennis", "<big>decennis</big>\n\n<b>decennis, e</b> [decem + annus]\nдесятилетний (proelium <i>Pt</i>; bellum <i>Q</i>)."],
  ["decennium", "<big>decennium</big>\n\n<b>decennium, ī</b> <i>n</i> [decennis]\nдесятилетие <i>Ap</i>."],
  ["decens", "<big>decens</big>\n\n<b>decēns, entis</b> [deceo]\n<b>1)</b> приличный, подобающий, подходящий, пристойный (amictus <i>O</i>; ornatus, motus corporis <i>Q</i>);\n<b>2)</b> изящный, прелестный, миловидный, красивый (facies <i>O</i>; Gratiae <i>H</i>; equus <i>Q</i>)."],
  ["decenter", "<big>decenter</big>\n\n<b>decenter</b> [decens]\nприлично, пристойно, подобающе, надлежащим образом (d. uti aliqua re <i>PJ</i>; lacrimare <i>O</i>)."],
  ["decentia", "<big>decentia</big>\n\n<b>decentia, ae</b> <i>f</i> [decens]\nприличие, пристойность (ordo et d. <i>C</i>)."],
  ["deceo", "<big>deceo</big>\n\n<b>deceo, uī, —, ēre</b> [<i>одного корня с</i> decor <i>и</i> decus; <i>употребляется тк. в <i>inf.</i> и 3-м л. обоих чисел</i>]\n<b>1)</b> украшать, быть к лицу, идти (toga picta decet aliquem <i>Prp</i>);\n<b>2)</b> приличествовать, подобать (aliquem <i>C etc.</i>, <i>арх.</i> alicui <i>Pl</i>; quod honestum est, decet <i>C</i>; id facere quod decet, non quod licet <i>SenT</i>): decet magnanimitas quemlibet mortalem <i>Sen</i> величие души пристало всякому человеку (должно быть свойством всех); verba, qui formam ejus egregie decebant <i>Pt</i> слова, которые великолепно гармонировали с его наружностью; istuc facinus alicui non decet <i>Pl</i> этот поступок не подобает кому-л.; ita nobis decet <i>Ter</i> так нам и следует (делать); perge, decet <i>V</i> так начинай же (пора действовать)."],
  ["deceptio", "<big>deceptio</big>\n\n<b>dēceptio, ōnis</b> <i>f</i> [decipio]\nобман, ложь <i>CJ, CTh, Vlg, CA, Aug</i>."],
  ["deceptiosus", "<big>deceptiosus</big>\n\n<b>dēceptiōsus, a, um</b> <i>Eccl</i>\n= deceptorius."],
  ["deceptor", "<big>deceptor</big>\n\n<b>dēceptor, ōris</b> <i>m</i> [decipio]\nобманщик <i>Sen, Aus, Eccl</i>."],
  ["deceptorius", "<big>deceptorius</big>\n\n<b>dēceptōrius, a, um</b>\nобманчивый <i>Sen etc.</i>"],
  ["deceptum", "<big>deceptum</big>\n\n<b>dēceptum</b> <i>супплет. supin. к</i>\nfallo."],
  ["deceptus", "<big>deceptus</big>\n\n<b>I dēceptus, a, um</b> <i>part. pf. к</i>\ndecipio.\n\n<b>II dēceptus,</b> <i>тк.</i> <i>dat.</i> <b>uī</b> <i>m</i> <i>Tert</i>\n= deceptio."],
  ["deceris", "<big>deceris</big>\n\n<b>decēris, is</b> <i>f</i> (<i>греч.</i>)\nдекера (<i>судно с десятью рядами вёсел, т. е. гребцов</i>) <i>Su</i>."],
  ["decerno", "<big>decerno</big>\n\n<b>dē–cerno, crēvī, crētum, ere</b>\n<b>1)</b> решать (aliquid <i>или</i> de aliqua re <i>C etc.</i>); разрешать (rem dubiam <i>L</i>);\n<b>2)</b> уразуметь, понять (nequeo satis d. <i>Ter</i>);\n<b>3)</b> <i>юр.</i> постановлять в судебном порядке, выносить приговор: si caedes facta, Druides decernunt <i>Cs</i> если совершено убийство, то решение (по нему) выносят друиды; alias revocabat eos, inter quos jam decreverat, decretumque mutabat, alias inter alios contrarium decernebat ac proximis paulo ante decreverat <i>C</i> (Beppec) то обратно вызывал людей, по делу которых уже вынес решение, и изменял приговор, то выносил решения, противоположные тем, которые совсем недавно были вынесены по последним (аналогичным) делам; sententia decernentis <i>Pt</i> постановление исполняющего роль судьи;\n<b>4)</b> постановлять в законодательном порядке (senatus decrevit <i>L</i>; consules decreverunt <i>C</i>): d. classem ingentem <i>Just</i> вынести решение о сооружении огромного флота; tumultum d. <i>C</i> объявить страну на положении войны;\n<b>5)</b> присуждать (alicui praemium <i>Sl</i>); назначать (alicui triumphum <i>C, VM</i>; alicui provinciam <i>Cs, Su</i>); выделять (tempus ad aliquam rem <i>Sl</i>); ассигновать (pecunias ad ludos <i>C</i>): alicui bellum d. <i>Just</i> возложить на кого-л. ведение войны (главное командование);\n<b>6)</b> решаться, принимать решение: mihi decretum est <i>Pl</i> я решил(ся); Caesar Rhenum transire decreverat <i>Cs</i> Цезарь решил перейти Рейн; <i>pf.</i> decrevi <i>Ter, Sl, C etc.</i> я полагаю, убеждён, думается мне;\n<b>7)</b> судиться, тягаться, спорить на суде (pro sua fama fortunisque <i>C</i>): qui judicio decernunt <i>Q</i> тяжущиеся стороны;\n<b>8)</b> решать оружием, сражаться, бороться (d. pugnam <i>L</i>; d. equestri proelio <i>QC</i>): trahere hostem ad decernendum <i>L</i> втянуть неприятеля в бой."],
  ["decerpo", "<big>decerpo</big>\n\n<b>dē–cerpo, cerpsī, cerptum, ere</b> [carpo]\n<b>1)</b> обрывать (aristas <i>O</i>; folia <i>QC</i>); срывать (flores <i>Lcr</i>; poma <i>O</i>; undique decerpta oliva <i>H</i>);\n<b>2)</b> слегка посасывать (apes decerpunt thymum <i>PM</i>); <i>перен.</i> прикасаться поцелуем (d. candida ora puellae <i>V</i>);\n<b>3)</b> брать, взять, урвать, присвоить (nihil sibi ex aliqua re d. <i>C</i>): decerptum esse ex aliqua re <i>C</i> быть частицей (<i>т. е.</i> порождением) чего-л.;\n<b>4)</b> стяжать, добывать (decus primae pugnae <i>Sil</i>): fructus ex aliqua re d. <i>H</i> пожать плоды (извлечь пользу из) чего-л.;\n<b>5)</b> отнимать (aliquid de gravitate <i>C</i>; nihil ex dignitate <i>PJ</i>); разрушать, уничтожить (spes tantas <i>Q</i>)."],
  ["decerptio", "<big>decerptio</big>\n\n<b>dēcerptio, ōnis</b> <i>f</i> [decerpo]\nсрывание (pomorum <i>Aug</i>)."],
  ["decertatio", "<big>decertatio</big>\n\n<b>dēcertātio, ōnis</b> <i>f</i> [decerto]\nрешение оружием, решительное сражение: d. harum rerum omnium <i>C</i> бой, который все это решит."],
  ["decerto", "<big>decerto</big>\n\n<b>dē–certo, āvī, ātum, āre</b>\n<b>1)</b> вести решительный бой (d. pugna <i>Cs</i>; d. vi et armis contra vim <i>C</i>; d. pro liberate <i>C</i>): omnia facienda ne armis decertetur <i>C</i> нужно сделать все, чтобы (попытаться) не доводить до вооружённой борьбы; decertandi fortitudo <i>C</i> разрешение (спора) оружием;\n<b>2)</b> спорить, бороться (inter se <i>C</i>; Africus decertans Aquilonibus — <i>dat. H</i>): de aliqua re jure d., armis non contendere <i>C</i> решать что-л. путём юридического спора, а не оружием;\n<b>3)</b> оспаривать: ad laudes decertandas venire <i>AG</i> явиться для взаимного оспаривания славы;\n<b>4)</b> сталкивать друг с другом, наполнять борьбой (ventis decertata aequora <i>St</i>); decertati labores <i>Cld</i> боевые труды, воинские подвиги."],
  ["decervicatus", "<big>decervicatus</big>\n\n<b>dē–cervīcātus, a, um</b> [cervix]\nобезглавленный (cadaver <i>Sid</i>)."],
  ["decesse", "<big>decesse</big>\n\n<b>dēcesse</b>\n<i>Ter</i> = decessisse (<i>inf. pf. к</i> decedo)."],
  ["decessi", "<big>decessi</big>\n\n<b>dēcessī</b> <i>pf. к</i>\ndecedo."],
  ["decessio", "<big>decessio</big>\n\n<b>dēcessio, ōnis</b> <i>f</i> [decedo]\n<b>1)</b> уход, отъезд, отбытие, удаление <i>C</i>;\n<b>2)</b> утрата, потеря (molesta d. bonorum <i>C</i>);\n<b>3)</b> оставление служебного поста (в провинции) <i>C</i>;\n<b>4)</b> убыль, уменьшение (decessionem de summa facere <i>C</i>);\n<b>5)</b> переход: d. verborum <i>AG</i> переход слова от основного значения к производному <i>или</i> переносному."],
  ["decessor", "<big>decessor</big>\n\n<b>dēcessor, ōris</b> <i>m</i> [decedo]\nоставивший служебный пост, <i>т. е.</i> предшественник <i>C, T, Dig etc.</i>"],
  ["decessus", "<big>decessus</big>\n\n<b>dēcessus, ūs</b> <i>m</i> [decedo]\n<b>1)</b> уход, отъезд, отбытие, удаление (Gallorum ex urbe <i>Vr</i>; post Dionysii decessum <i>Nep</i>);\n<b>2)</b> оставление служебного поста (post M. Bruti decessum <i>C</i>);\n<b>3)</b> спад, понижение (Nili <i>PM</i>): d. aestus <i>Cs</i> морской отлив;\n<b>4)</b> ослабление, убыль (febris <i>CC</i>; <i>sc.</i> morbi <i>AG</i>);\n<b>5)</b> кончина, смерть (amici <i>C</i>)."],
  ["decet", "<big>decet</big>\n\n<b>decet</b> — <i>см.</i>\ndeceo."],
  ["Decetia", "<big>Decetia</big>\n\n<b>Decetia, ae</b> <i>f</i>\nДекетия, <i>город эдуев в Gallia Lugdunensis, на реке Liger</i> (<i>ныне</i> Decize) <i>Cs</i>."],
  ["decharmido", "<big>decharmido</big>\n\n<b>dē–charmido, —, —, āre</b> [Charmides]:\nd. se <i>шутл. досл.</i> «расхармидиться», <i>т. е.</i> перестать называться Хармидом, стать безрадостным (charma <i>греч.</i> радость) <i>Pl</i>."],
  ["Decianus", "<big>Decianus</big>\n\n<b>Deciānus, a, um</b> [Decius]\nдециев (exercitus <i>L</i>)."],
  ["decibilis", "<big>decibilis</big>\n\n<b>decibilis, e</b> [deceo]\nподобающий, подходящий <i>Eccl</i>."],
  ["Decidius", "<big>Decidius</big>\n\n<b>Decidius, ī</b> <i>m</i>:\nL. D. Saxa Децидий, <i>кельтибер, народный трибун в 44 г. до н. э., служил в армии Цезаря, а затем Антония и Октавиана</i> <i>Cs, C, VP</i>."],
  ["decido", "<big>decido</big>\n\n<b>I dē–cido, cidī, —, ere</b> [cado]\n<b>1)</b> падать (imber decidit <i>H</i>); спадать (caelo <i>PM</i> <i>или</i> de caelo <i>Pl</i>; glandes decidunt arbore <i>O</i>; poma ex arboribus decidunt <i>C</i>); ниспадать, спускаться (tunica ad pedes decidit <i>Su</i>); выпадать (comae <i>O</i>, dentes decidunt <i>PM</i>); рухнуть, рушиться (gravi casu <i>H</i>); упасть, свалиться (equo <i>Cs</i>; ab equo in arva <i>O</i>; ex equo in terram <i>Nep</i>);\n<b>2)</b> попадать, впадать (in lacum <i>Su</i>; <i>перен.</i> in fraudem <i>C</i>); потерпеть неудачу, провалиться (perfidia amicorum <i>Nep</i>): ex astris d. <i>погов. C</i> пасть с большой высоты (утратить былое значение); nunc eo decidit, ut exsul de senatore fieret <i>PJ</i> теперь он так пал, что из сенатора стал изгнанником; d. spe <i>Su</i>, de spe <i>Ter</i> <i>и</i> a spe <i>L</i> утратить надежду;\n<b>3)</b> погибать, сойти в могилу (morbo <i>Pl</i>): abhinc annos centum qui decidit <i>H</i> умерший сто лет тому назад.\n\n<b>II dē–cīdo, cīdī, cīsum, ere</b> [caedo]\n<b>1)</b> срубать (silvas <i>PM</i>); отрубать, отсекать (caput <i>Sen, VP, QC</i>); отрезать (folia arboribus <i>PM</i>); подрезать, обрезать (pennas <i>H</i>);\n<b>2)</b> сечь (aliquem verberibus <i>Dig</i>);\n<b>3)</b> покончить, порешить (negotium <i>H, Su</i>; quaestionem <i>Dig</i>): magno suam partem d. <i>C</i> договориться о получении своей части на выгодных условиях || столковаться, закончить (cum aliquo, de re aliqua <i>C</i>): omnibus rebus actis atque decisis <i>C</i> когда всё было сделано и закончено (улажено);\n<b>4)</b> точно определять, выражать (aliquid propriis verbis <i>Q</i>)."],
  ["deciduus", "<big>deciduus</big>\n\n<b>dēciduus, a, um</b> [decido I]\nопадающий, отпавший (folia <i>PM</i>); выпадающий, шатающийся (dentes <i>PM</i>): sidera decidua <i>PM</i> падающие звёзды."],
  ["decies", "<big>decies</big>\n\n<b>deciēs</b> <i>или</i> <b>deciēns</b> <i>adv. num.</i> [decem]\nдесять раз, десятикратно: HS (= sestertium) d. centena milia <i>C</i> 1 миллион сестерциев; bis d. <i>M</i> = vicies."],
  ["decima", "<big>decima</big>\n\n<b>decima, ae</b> <i>f</i>\n= decuma."],
  ["decimana", "<big>decimana</big>\n\n<b>decimāna, ae</b> <i>f</i> [decimanus]\nжена <i>или</i> любовница арендатора десятины <i>C</i>."],
  ["decimanus", "<big>decimanus</big>\n\n<b>I decimānus</b> <i>или</i> <b>decumānus, a, um</b> [decimus]\n<b>1)</b> относящийся к десятой части, к десятине, десятинный: ager d. <i>C</i> поле, с которого отдаётся десятая часть урожая (десятина);\n<b>2)</b> взимаемый в качестве десятины (frumentum <i>C</i>; oleum <i>LM</i>);\n<b>3)</b> находящийся, состоящий в десятой когорте <i>или</i> в десятом легионе (milites decumani <i>bAfr, T etc.</i>);\n<b>4)</b>: porta decumana <i>Cs etc.</i> задний вход в лагерь (<i>у помещений 10-й когорты легиона, против porta praetoria</i>);\n<b>5)</b>: d. (limes) <i>PM, Col</i> межа, проведённая с запада на восток (<i>см.</i> cardo);\n<b>6)</b> значительный, крупный, огромный (acipenser <i>LM</i>).\n\n<b>II decimānus, ī</b> <i>m</i> [decimanus I]\n<b>1)</b> арендатор десятины (десятинного оброка) <i>C</i>;\n<b>2)</b> (<i>чаще</i> decumanus) солдат X легиона <i>bAfr, T etc.</i>"],
  ["decimaria", "<big>decimaria</big>\n\n<b>decimāria, ae</b> <i>f</i>\nпостановление относительно десятинной доли (участия в наследовании) <i>CJ</i>."],
  ["decimarius", "<big>decimarius</big>\n\n<b>decimārius, a, um</b> [decima]\nобязанный платить десятину <i>Ambr</i>."],
  ["decimatio", "<big>decimatio</big>\n\n<b>decimātio, ōnis</b> <i>f</i> [decimo]\n<b>1)</b> <i>воен.</i> децимация, <i>т. е.</i> казнь каждого десятого солдата <i>Capit</i>;\n<b>2)</b> отчисление десятины <i>Vlg, Eccl</i>."],
  ["decimo", "<big>decimo</big>\n\n<b>I decimo, āvī, ātum, āre</b> [decimus]\n<b>1)</b> наказывать (<i>или</i> казнить) каждого десятого, децимировать (d. cohortes, legiones <i>Su</i>);\n<b>2)</b> облагать десятиной (aliquid <i>и</i> aliquem <i>Vlg</i>);\n<b>3)</b> отбирать в качестве десятины: decimatus <i>Symm</i> избранный, отборный, исключительный.\n\n<b>II decimō</b> <i>adv.</i>\nв-десятых <i>Eccl</i>."],
  ["decimum", "<big>decimum</big>\n\n<b>decimum</b>\n<b>1)</b> <i>adv.</i> в десятый раз <i>L</i>;\n<b>2)</b> <i>v. l.</i> = decumum."],
  ["decimus", "<big>decimus</big>\n\n<b>I decimus, a, um</b> [decem]\n<b>1)</b> десятый (pars, hora <i>C</i>): septima dies post decimam <i>V</i> семнадцатый день;\n<b>2)</b> значительный, крупный, огромный (unda <i>O</i>; fluctus <i>Lcn</i>) (<i>ср.</i> «девятый вал»).\n\n<b>II decimus, ī</b> <i>m</i>\n<b>1)</b> (<i>sc.</i> dies) десятый день <i>Col</i>;\n<b>2)</b> (<i>sc.</i> liber) десятая книга (глава) <i>AG</i>.\n\n<b>III Decimus, ī</b> <i>m</i>\nДецим, <i>римск. praenomen</i>(<i>сокр.</i> D.)."],
  ["decineratus", "<big>decineratus</big>\n\n<b>dē–cinerātus, a, um</b> [cinis]\nиспепелённый <i>Tert</i>."],
  ["decineresco", "<big>decineresco</big>\n\n<b>dē–cinerēsco, —, —, ere</b>\nпревращаться в пепел <i>Tert</i>."],
  ["decipio", "<big>decipio</big>\n\n<b>dē–cipio, cēpī, ceptum, ere</b> [capio]\nвводить в заблуждение, обманывать (aliquem inanibus promissis <i>Just</i>; deceptus aliqua re, per aliquid <i>Pl etc.</i>; in prima spe <i>L</i>): singuli d. et decipi possunt <i>PJ</i> отдельные люди могут и обманывать, и (сами) обманываться; decipi via <i>L</i> ошибиться дорогой; vitia amicae amatorem decipiunt <i>H</i> недостатки подруги ускользают от внимания (остаются незамеченными для) влюблённого; d. diem (noctem) <i>O</i> делать незаметным, <i>т. е.</i> коротать, убивать день (ночь); decipi laborum <i>H</i> забыть о (своих) страданиях."],
  ["decipula", "<big>decipula</big>\n\n<b>dēcipula, ae</b> <i>f</i> <i>Vlg, Eccl, Sid</i>\n= decipulum."],
  ["decipulum", "<big>decipulum</big>\n\n<b>dēcipulum, ī</b> <i>n</i> [decipio]\nзападня, силок, <i>перен.</i> крючок, коварный приём (decipula adversantium <i>Ap</i>)."],
  ["decircino", "<big>decircino</big>\n\n<b>dē–circino, —, —, āre</b>\n(<i>об окружности</i>) описывать, обводить (arcum, orbem <i>Man</i>)."],
  ["decisio", "<big>decisio</big>\n\n<b>dēcīsio, ōnis</b> <i>f</i> [decido II]\n<b>1)</b> уменьшение, убыль (luminis <i>Ap</i>);\n<b>2)</b> соглашение, сделка, решение (d. nostra de aliqua re <i>C</i>): decisionem facere <i>C</i> прийти к соглашению;\n<b>3)</b> раздел, часть <i>Boët</i>."],
  ["decisus", "<big>decisus</big>\n\n<b>dēcīsus, a, um</b> <i>part. pf. к</i>\ndecido."],
  ["decito", "<big>decito</big>\n\n<b>dē–cito, —, —, āre</b>\nбыстро передвигать (instabile vestigium <i>Ap</i> — <i>v. l.</i>)."],
  ["Decius", "<big>Decius</big>\n\n<b>Decius, a, um</b>\nДеций, <i>римск. nomen; наиболее известны</i>:\n<b>1)</b> P. D. Mus, <i>участник 1-й Самнитской войны</i> <i>L, C etc.</i>;\n<b>2)</b> P. D. Mus, <i>сын предыдущего, участник 2-й Самнитской войны</i> <i>L, C, Sen etc.</i>"],
  ["declamatio", "<big>declamatio</big>\n\n<b>dēclāmātio, ōnis</b> <i>f</i> [declamo]\n<b>1)</b> упражнение в красноречии (d. cotidiana <i>C</i>); декламация <i>C, T</i>;\n<b>2)</b> текст для школьного упражнения (d. fieri <i>J</i>); речь, доклад, учебная лекция <i>Sen etc.</i>; предмет, тема для речи (<i>чаще</i> causa) <i>Q</i>;\n<b>3)</b> суматоха, шум, крик (d. vulgaris et pervagata <i>C</i>)."],
  ["declamatiuncula", "<big>declamatiuncula</big>\n\n<b>dēclāmātiuncula, ae</b> <i>f</i> [<i>demin. к</i> declamatio]\nнебольшая речь, упражненьице <i>AG, Sid</i>."],
  ["declamator", "<big>declamator</big>\n\n<b>dēclāmātor, ōris</b> <i>m</i>\nучитель теории словесности, красноречия, школьный ритор <i>C, Q, J</i>."],
  ["declamatorie", "<big>declamatorie</big>\n\n<b>dēclāmātōriē</b> <i>adv.</i>\nдекламаторски, по-риторски <i>GT</i>."],
  ["declamatorius", "<big>declamatorius</big>\n\n<b>dēclāmātōrius, a, um</b>\nотносящийся к упражнению в красноречии, риторический, декламаторский <i>C, Q, T</i>."],
  ["declamito", "<big>declamito</big>\n\n<b>dēclāmito, āvī, ātum, āre</b> [<i>frequ. к</i> declamo]\n<b>1)</b> часто упражняться в произнесении речей (cum aliquo cotidie <i>C</i>); декламировать (Graece <i>Su</i>);\n<b>2)</b> произносить для упражнения (causas <i>C</i>)."],
  ["declamo", "<big>declamo</big>\n\n<b>dē–clāmo, āvī, ātum, āre</b>\n<b>1)</b> упражняться в произнесении речей, декламировать (ex aliqua oratione <i>C</i>; d. controversiam <i>Sen</i>);\n<b>2)</b> говорить с жаром, с негодованием <i>или</i> кричать, орать (vehementer d. alicui <i>O</i>, in <i>или</i> contra aliquem <i>C</i>)."],
  ["declaratio", "<big>declaratio</big>\n\n<b>dēclārātio, ōnis</b> <i>f</i> [declaro]\nвысказывание, проявление, обнаруживание (amoris sui <i>C</i>)."],
  ["declarativus", "<big>declarativus</big>\n\n<b>dēclārātīvus, a, um</b>\nразъясняющий, объяснительный (pars <i>Ap</i>)."],
  ["declarator", "<big>declarator</big>\n\n<b>dēclārātor, ōris</b> <i>m</i> [declaro]\nвестник, глашатай (<i>объявляющий о выборах того или иного сановника</i>) (<i>магистрата</i>) <i>PJ</i>."],
  ["declaro", "<big>declaro</big>\n\n<b>dē–clāro, āvī, ātum, āre</b>\n<b>1)</b> заявлять (<i>о чём-л.</i>) (praesentiam suam <i>C</i>); провозглашать (aliquem consulem <i>C</i>); объявлять (aliquem victorem <i>V</i>);\n<b>2)</b> обнаруживать (senecta equorum dentibus declaratur <i>PM</i>);\n<b>3)</b> свидетельствовать (<i>о чём-л.</i>), показывать (summam benevolentiam <i>C</i>); en hic declarat, quales sitis judices <i>Ph</i> это показывает, что вы за судьи;\n<b>4)</b> изъявлять, выражать (voluntatem suam <i>Q</i>; gaudia vultu <i>Ctl</i>);\n<b>5)</b> обозначать: littera C inversa mulierem declarat <i>Q</i> опрокинутая буква C (Ↄ) обозначает женщину (Гаю); verba idem declarantia <i>C</i> равнозначащие слова (синонимы);\n<b>6)</b> разъяснять (aliquam rem breviter <i>Nep</i>): res ipsa declarat <i>C</i> само собой разумеется;\n<b>7)</b> представить, изложить (imaginem vitae alicujus <i>Nep</i>)."],
  ["declinabilis", "<big>declinabilis</big>\n\n<b>dēclīnābilis, e</b> [declino] <i>грам.</i>\nизменяемый, <i>преим.</i> склоняемый."],
  ["declinatio", "<big>declinatio</big>\n\n<b>dēclīnātio, ōnis</b> <i>f</i> [declino]\n<b>1)</b> наклон, поворот, уклончивое движение: alicujus petitiones declinatione corporis effugere <i>C</i> увернуться от чьих-л. нападений уклончивым движением тела;\n<b>2)</b> отклонение (atomorum <i>C</i>); отступление, удаление (a proposito <i>C, Q</i>);\n<b>3)</b> уклонение, стремление избежать (laboris et periculi <i>C</i>);\n<b>4)</b> покатость, склон (в сторону полюса): declinationes caeli (mundi) <i>Col</i> страны света;\n<b>5)</b> нерасположение, отвращение (appetitiones et declinationes <i>AG</i>);\n<b>6)</b> <i>грам.</i> изменение формы слов (<i>склонение, спряжение и пр.</i>) <i><i>Vr, Q</i>, впоследствии тк.</i> склонение."],
  ["declinatus", "<big>declinatus</big>\n\n<b>dēclīnātus, ūs</b> <i>m</i> [declino]\nизменение формы слов <i>Vr</i>."],
  ["declinis", "<big>declinis</big>\n\n<b>dēclīnis, e</b>\nповёрнутый (juga <i>St</i>)."],
  ["declino", "<big>declino</big>\n\n<b>dē–clīno, āvī, ātum, āre</b>\n<b>1)</b> отклонять, отводить (ictum <i>L</i>; suspicionem <i>Pt</i>); уводить в сторону (agmen aliquo d. <i>L</i>): d. se extra viam <i>Pl</i> (d. de via <i>C</i>) свернуть с пути; se d. recta regione viae <i>Lcr</i> отклониться от отвесного направления (<i>об атомах</i>); d. impetum <i>C</i> отразить нападение; ab aliorum ingenio declinatus <i>Ter</i> характером непохожий на других; d. lumina ex aliquo <i>O</i> отвести взоры (оторваться) от кого-л.;\n<b>2)</b> смежить, закрыть (d. lumina dulci somno <i>V</i>);\n<b>3)</b> сдвинуть, пошатнуть, поколебать: neque spe neque metu declinatus animus <i>Q</i> дух, которого не поколебали ни надежды, ни страх;\n<b>4)</b> избегать, обходить (periculum <i>Just</i>; a malis <i>C</i>; ea, quae nocitura videantur <i>C</i>);\n<b>5)</b> отклоняться (atomi de via declinantes <i>C</i>): d. a proposito <i>C</i> отвлечься от темы;\n<b>6)</b> обращаться (ad discendum jus <i>Q</i>); ощущать влечение, тяготеть (d. in aliquam <i>H</i>);\n<b>7)</b> уклониться, сбиться (a recto itinere <i>Q</i>);\n<b>8)</b> <i>грам.</i> изменять форму (<i>склонять, спрягать и пр.</i>): in casus declinari <i>Vr</i> склоняться (по падежам); verba declinata <i>Vr</i> производные глаголы;\n<b>9)</b> клониться к закату: die in vesperem declinante <i>Col</i> когда день клонился к вечеру; aetate declinata <i>Q</i> в преклонном возрасте;\n<b>10)</b> принять другой оборот (d. in pejus <i>Q</i>); ослабеть, утихнуть (declinante morbo <i>PM</i>);\n<b>11)</b> относить на счёт (<i>чего-л.</i>), приписывать (d. adversa in inscitiam alicujus <i>T</i>): secunda in casum d. <i>Sl</i> приписывать успехи случайности."],
  ["declive", "<big>declive</big>\n\n<b>dēclīve, is</b> <i>n</i> [declivis]\nспуск, скат, покатость (in d ruere <i>L</i>): per d. <i>Cs</i> на скате, по скату; declivia et devexa <i>Cs</i> покатости и склоны."],
  ["declivis", "<big>declivis</big>\n\n<b>dē–clīvis, e</b> [clivus]\n<b>1)</b> пологий, покатый, наклонный collis ab summo aequaliter d. <i>Cs</i> холм, имеющий сверх (до низу) одинаковый наклон; locus leniter d. <i>Cs</i> отлоге место;\n<b>2)</b> склоняющийся к закату (sol occasui d. <i>MF</i>) <i>или</i> к концу (aestas <i>Calp</i>); пожилой, преклонный (mulier aetate declivis <i>PJ</i>);\n<b>3)</b> склонный, тяготеющий (in peccatorum genera universa <i>Eccl</i>). — <i>См. тж.</i> declive <i>и</i> declivum."],
  ["declivitas", "<big>declivitas</big>\n\n<b>dēclīvitās, ātis</b> <i>f</i> [declivis]\nпологость, покатость <i>Cs, Amm</i>."],
  ["declivum", "<big>declivum</big>\n\n<b>dēclīvum, ī</b> <i>n</i> [declivis]\nскат, склон, спуск, покатость <i>Boët</i>."],
  ["decoco", "<big>decoco</big>\n\n<b>dēcoco</b> <i>v. l.</i>\n= decoquo."],
  ["decocta", "<big>decocta</big>\n\n<b>dēcocta, ae</b> <i>f</i> [decoquo]\n(<i>sc.</i> aqua) охлаждённая кипячёная вода (<i>вид прохладительного напитка</i>) <i>Su, M</i>."],
  ["decoctio", "<big>decoctio</big>\n\n<b>dēcoctio, ōnis</b> <i>f</i> [decoquo]\n<b>1)</b> кипячение <i>CA</i>;\n<b>2)</b> отвар, декокт (herbarum <i>Ap</i>);\n<b>3)</b> разложение, разрушение (cibi et corporis <i>Is</i>);\n<b>4)</b> разорение, банкротство <i>CJ, CTh</i>."],
  ["decoctor", "<big>decoctor</big>\n\n<b>dēcoctor, ōris</b> <i>m</i> [decoquo]\nмот, расточитель <i>C, Sen</i>; растратчик (bonorum suorum <i>Spart</i>; pecuniae publicae <i>CJ</i>)."],
  ["decoctum", "<big>decoctum</big>\n\n<b>dēcoctum, ī</b> <i>n</i> [decoquo]\nотвар, декокт <i>PM</i>."],
  ["decoctus", "<big>decoctus</big>\n\n<b>I dēcoctus, ūs</b> <i>m</i> [decoquo]\nотваривание, вываривание <i>PM</i>.\n\n<b>II dēcoctus, a, um</b> <i>part. pf. к</i>\ndecoquo."],
  ["decollo", "<big>decollo</big>\n\n<b>dē–collo, āvī, ātum, āre</b> [collum]\n<b>1)</b> отрубить голову, обезглавить (aliquem <i>Sen etc.</i>; jussisse aliquem decollari <i>Pt</i>);\n<b>2)</b> лишать (aliqua re aliquem <i>LM</i>)."],
  ["decolo", "<big>decolo</big>\n\n<b>dē–cōlo, āvī, —, āre</b> [colum 1]\nпросачиваться, процеживаться, <i>перен.</i> уплывать, исчезать (si spes decolabit <i>Pl</i>)."],
  ["decolor", "<big>decolor</big>\n\n<b>dē–color, ōris</b>\n<b>1)</b> выцветший, обесцвеченный (sanguis <i>C</i>);\n<b>2)</b> потемневший, тёмный, смуглый (cutis <i>PM</i>; ustus et d. Indus <i>Prp</i>);\n<b>3)</b> мутный (Rhenus sanguine d. <i>O</i>);\n<b>4)</b> выродившийся, развращённый (aetas <i>V</i>);\n<b>5)</b> обезображенный, искажённый, отвратительный (fama <i>O</i>)."],
  ["decoloratio", "<big>decoloratio</big>\n\n<b>dēcolōrātio, ōnis</b> <i>f</i> [decoloro]\nизменение цвета <i>или</i> обесцвечивание <i>C</i>."],
  ["decoloro", "<big>decoloro</big>\n\n<b>dē–colōro, āvī, ātum, āre</b>\n<b>1)</b> обесцвечивать (pallor labra decolorat <i>CC</i>): decolorari ex albo <i>Col</i> терять белизну;\n<b>2)</b> пачкать, марать (manus collybo <i>Su</i>);\n<b>3)</b> (кровью) обагрять (mare <i>H</i>);\n<b>4)</b> опалять, покрывать загаром (aliquem <i>Vlg</i>);\n<b>5)</b> портить, искажать (famam <i>Aug</i>); развращать (mores <i>Aug</i>)."],
  ["decolorus", "<big>decolorus</big>\n\n<b>dēcolōrus, a, um</b> <i>Eccl</i>\n= decolor."],
  ["deconcilio", "<big>deconcilio</big>\n\n<b>dē–concilio, —, —, āre</b>\nотнимать (aliquid alicui <i>Pl</i>)."],
  ["decondo", "<big>decondo</big>\n\n<b>dē–condo, —, —, ere</b>\nзарывать, глубоко похоронить (aliquem in ventrem immensae beluae <i>Sen</i>)."],
  ["decontor", "<big>decontor</big>\n\n<b>dēcontor, —, ārī</b> <i>Ap</i>\n= decunctor."],
  ["decoquo", "<big>decoquo</big>\n\n<b>dē–coquo, coxī, coctum, ere</b>\n<b>1)</b> отварить, варить (olus <i>H</i>; lentem in aqua marina <i>PM</i>): d. corpus <i>шутл. Sen</i> развариться, распариться (<i>в бане</i>);\n<b>2)</b> взлелеять, дать созреть, <i>pass.</i> созревать (benificio solis <i>Pall</i>): acini decoquuntur in callum <i>PM</i> виноград по мере созревания становится жёстким;\n<b>3)</b> дать вывариться (выкипеть, улетучиться), выпаривать (d. aquam in dimidiam partem <i>Col</i>): pars quarta argenti decocta erat <i>L</i> четвёртая часть серебра убыла при плавке; multa inde decoquent anni <i>Q</i> многое из этого поубавится с годами; suavitas decocta <i>C</i> водянистая (безвкусная) сладость;\n<b>4)</b> <i>перен.</i> дать перекипеть, остудить (d. iram <i>Cld</i>);\n<b>5)</b> разорять (hunc alea decoquit <i>Pers</i>);\n<b>6)</b> прогорать: d. creditoribus <i>PM, Sen</i> разориться в ущерб (<i>т. е.</i> не заплатить) кредиторам, обанкротиться."],
  ["decor", "<big>decor</big>\n\n<b>I decor, ōris</b> <i>m</i> [deceo]\n<b>1)</b> приличие, пристойность, уместность: mobilibus d. naturis dandus et annis <i>H</i> (в драматургии) нужно разным характерам и возрастам придавать то, что (с ними) совместимо; in personis d. <i>Q</i> надлежащая трактовка характеров;\n<b>2)</b> красота, прелесть, изящество, миловидность (oris <i>T</i>): suus cuique est d. <i>Q</i> всему свойственна своя прелесть.\n\n<b>II decor, oris</b> <i>adj.</i> [decus]\n<b>1)</b> прекрасный, красивый, изящный (arma decora <i>Sl</i>);\n<b>2)</b> выдающийся, отличный (ingenium <i>T</i>)."],
  ["decoramen", "<big>decoramen</big>\n\n<b>decorāmen, inis</b> <i>n</i>\nукрашение <i>Sil, Aus</i>."],
  ["decoramentum", "<big>decoramentum</big>\n\n<b>decorāmentum, ī</b> <i>n</i> <i>Eccl</i>\n= decoramen."],
  ["decore", "<big>decore</big>\n\n<b>decōrē</b> [decorus]\nприлично, пристойно, с достоинством (loqui <i>C</i>; res publica d. gesta <i>Sl</i>)."],
  ["decorio", "<big>decorio</big>\n\n<b>dē–corio, āvī, ātum, āre</b> [corium]\nсдирать кожу <i>или</i> кожицу, обдирать (aliquem <i>и</i> pellem alicujus <i>Eccl</i>; amygdala decoriata <i>Pall</i>)."],
  ["decoriter", "<big>decoriter</big>\n\n<b>decōriter</b> [decor II]\nизящно, красиво <i>Ap</i>."],
  ["decoro", "<big>decoro</big>\n\n<b>decoro, āvī, ātum, āre</b> [decus]\n<b>1)</b> украшать (deorum simulacra <i>QC</i>; urbem monumentis <i>C</i>);\n<b>2)</b> прославлять (rem publicam <i>C</i>): d. aliquem lacrimis <i>Enn ap. C</i> чтить слезами, <i>т. е.</i> оплакивать кого-л.;\n<b>3)</b> прикрашивать (aliquid inani vocis sono <i>C</i>)."],
  ["decorosus", "<big>decorosus</big>\n\n<b>decorōsus, a, um</b> <i>Eccl</i>\n= decor II."],
  ["decorticatio", "<big>decorticatio</big>\n\n<b>[dēcorticātio, ōnis</b> <i>f</i> [decortico]\nочистка от коры, снятие шелухи (кожицы) <i>PM</i>."],
  ["decortico", "<big>decortico</big>\n\n<b>dē–cortico, āvī, ātum, āre</b> [cortex]\nснимать кору, очищать от шелухи (кожицы) <i>PM</i>."],
  ["decorum", "<big>decorum</big>\n\n<b>decōrum, ī</b> <i>n</i> [decorus]\nприличие, пристойность, достоинство <i>C, T</i>."],
  ["decorus", "<big>decorus</big>\n\n<b>decōrus, a, um</b> [decor I]\n<b>1)</b> приличный, пристойный, <i>тж.</i> достойный, благородный, подобающий, подходящий (vox <i>Q</i>; sermo <i>C</i>): blandimenta nequaquam virtuti decora <i>Ap</i> красоты, отнюдь не подобающие добродетели; dulce et decorum est pro patria mori <i>H</i> приятно и почётно умереть за родину;\n<b>2)</b> красивый, изящный, прелестный, миловидный (adulescens <i>Ap</i>): d. re aliqua <i>H, O etc.</i> украшенный чём-л."],
  ["decoxi", "<big>decoxi</big>\n\n<b>dēcoxī</b> <i>pf. к</i>\ndecoquo."],
  ["decrementum", "<big>decrementum</big>\n\n<b>dēcrēmentum, ī</b> <i>n</i> [decresco]\nуменьшение (rerum <i>AG</i>); убыль, ущерб (lunae <i>Ap</i>)."],
  ["decremo", "<big>decremo</big>\n\n<b>dē–cremo, —, —, āre</b>\nполностью сжигать <i>Tert</i>."],
  ["decrepitus", "<big>decrepitus</big>\n\n<b>dēcrepitus, a, um</b>\nпрестарелый, дряхлый (senex <i>Pl, Ter etc.</i>; aetas <i>C</i>)."],
  ["decrescentia", "<big>decrescentia</big>\n\n<b>dēcrēscentia, ae</b> <i>f</i> [decresco]\nуменьшение, убыль, ущерб (lunae <i>Vtr</i>)."],
  ["decresco", "<big>decresco</big>\n\n<b>dē–crēsco, crēvī, crētum, ere</b>\n<b>1)</b> уменьшаться, убавляться, убывать (luna decrescit <i>C, Col etc.</i>; dies crevit decrevitque <i>PJ</i>); иссякать (fons decrescit <i>PM</i>); спадать, понижаться (aestus decrescunt <i>Vr</i>; decrescentia flumina <i>H</i>; febris decrescit <i>CC</i>): crescunt loca decrescentibus undis <i>O</i> по мере спада воды ширится суша;\n<b>2)</b> пропадать (из виду), исчезать (decrescit refugo Trinacria visu <i>Cld</i>)."],
  ["decretalis", "<big>decretalis</big>\n\n<b>dēcrētālis, e</b> [decretum]\nопределяемый декретом, декретный (successio bonorum <i>Dig</i>)."],
  ["decretorius", "<big>decretorius</big>\n\n<b>dēcrētōrius, a, um</b> [decerno]\nрешающий, решительный, критический (hora <i>Sen</i>; pugna <i>Q</i>): accedere ad decretorium stilum <i>Sen</i> написать роковое заявление (<i>о лишении сына наследства</i>)."],
  ["decretum", "<big>decretum</big>\n\n<b>dēcrētum, ī</b> <i>n</i> [decerno]\n<b>1)</b> решение, постановление, определение, указ, декрет (patrum <i>T</i>; senatus <i>C</i>): decreta facere <i>C</i> выносить (издавать) постановления; decreto stare <i>Cs, L</i> подчиниться постановлению (выполнять решение);\n<b>2)</b> принцип, главное правило, основоположение, тезис (id habebant d. Academici <i>C</i>)."],
  ["decrevi", "<big>decrevi</big>\n\n<b>dēcrēvī</b> <i>pf. к</i>\n<b>1)</b> decerno <i>и</i>\n<b>2)</b> decresco."],
  ["decubo", "<big>decubo</big>\n\n<b>dē–cubo, —, —, āre</b>\nлежать вне: de suo lecto d. <i>Fabius Pictor ap. AG</i> ночевать не в своей постели."],
  ["decubui", "<big>decubui</big>\n\n<b>dēcubuī</b> <i>pf. к</i>\ndecumbo."],
  ["decucurri", "<big>decucurri</big>\n\n<b>dēcucurrī</b> <i>pf. к</i>\ndecurro."],
  ["deculco", "<big>deculco</big>\n\n<b>dē–culco, —, —, āre</b> [calco]\nрастаптывать, раздавливать (bacas pedibus <i>PM</i>); вбивать, втаптывать (aspros molares <i>St</i>)."],
  ["deculpatus", "<big>deculpatus</big>\n\n<b>dē–culpātus, a, um</b> [culpo]\nзаслуживающий порицания, достойный осуждения (verbum <i>AG</i>)."],
  ["decum-", "<big>decum-</big>\n\n<b>decum-</b> <i>v. l.</i>\n= decim-."],
  ["decuma", "<big>decuma</big>\n\n<b>decuma, ae</b> <i>f</i> (<i>sc.</i> hora)\nдесятый час (<i>т. е.</i> 4 часа пополудни) <i>Sen</i>;\n<b>2)</b> (<i>sc.</i> pars) десятая часть, десятина (d. hordei <i>C</i>; duae decumae frumenti <i>L</i>); десятинная жертва (vovere Apollini decumas praedae <i>Just</i>)."],
  ["decumanus", "<big>decumanus</big>\n\n<b>decumātēs, ium</b> <i>v. l.</i>\n= decimanus."],
  ["decumates", "<big>decumates</big>\n\n<b>decumātēs, ium</b> [decumus]\nдесятинные: agri d. <i>T</i> десятинные поля (<i>земли между Рейном и Дунаем, облагавшиеся десятинным налогом</i>)."],
  ["decumbo", "<big>decumbo</big>\n\n<b>dē–cumbo, cubuī, (cubitum), ere</b> [cubo]\n<b>1)</b> ложиться (super lectum <i>и</i> in lecto <i>Su</i>; tenera in herba <i>Col</i>); лежать больным (accedente febri d. <i>AG</i>); возлежать за столом (in triclinio <i>C</i>);\n<b>2)</b> погибнуть (honeste <i>C</i>);\n<b>3)</b> падать на землю, <i>т. е.</i> отказываться от дальнейшей борьбы (<i>о гладиаторах</i>) <i>C</i>."],
  ["decumum", "<big>decumum</big>\n\n<b>decumum, ī</b> <i>n</i>\nдесятикратность: ager effert (<i>или</i> efficit) cum decumo <i>C</i> поле даёт урожай сам-десят."],
  ["decunctor", "<big>decunctor</big>\n\n<b>dē–cūnctor, —, ārī</b> <i>depon.</i>\nвнимательно обдумывать, тщательно взвешивать <i>Ap</i>."],
  ["decunx", "<big>decunx</big>\n\n<b>decūnx, ūncis</b> <i>m</i> [decem + uncia]\nвесовая единица в 10 унций (= 272,88 <i>г</i>) <i>Eccl</i>."],
  ["decuplo", "<big>decuplo</big>\n\n<b>decuplo, āvī, ātum, āre</b> [decuplus]\nудесятерять <i>Eccl</i>."],
  ["decuplus", "<big>decuplus</big>\n\n<b>decuplus, a, um</b> [decem + plico]\nудесятерённый, десятикратный <i>Vlg</i>."],
  ["decuria", "<big>decuria</big>\n\n<b>decuria, ae</b> <i>f</i> [decem]\n<b>1)</b> десяток, группа из десяти (hominum <i>Vtr</i>); отделение (рабов) (cocus ex quadragesima decuria <i>Pt</i>) (<i>в больших рабовладельческих хозяйствах рабы разбивались на «декурии» или «отделения» по специальностям</i>: d. viatorum <i>и. т. п.</i>); (<i>о вещах</i>) десяток (pellium decuriae triginta <i>Treb</i>);\n<b>2)</b> декурия (<i>подразделение конницы, близкое к эскадрону</i>) <i>Vr</i>;\n<b>3)</b> сословие, класс (d. judicum, scribarum <i>C</i>): d. senatoria <i>C</i> судьи из числа сенаторов;\n<b>4)</b> <i>шутл.</i> пирующая компания <i>Pl</i>."],
  ["decurialis", "<big>decurialis</big>\n\n<b>I decuriālis, e</b> [decuria]\n<b>1)</b> десятичный (numerus <i>Tert</i>);\n<b>2)</b> касающийся декуриалов (<i>см.</i> decurialis II) <i>CJ</i>.\n\n<b>II decuriālis, is</b> <i>m</i>\nдекуриал, сенатский чиновник (<i>в Риме; эти чиновники разделялись на декурии</i>) <i>CJ</i>."],
  ["decuriatio", "<big>decuriatio</big>\n\n<b>decuriātio, ōnis</b> <i>f</i> [decurio]\nразделение на декурии (tribulium <i>C</i>)."],
  ["decuriatus", "<big>decuriatus</big>\n\n<b>[decuriātus, ūs</b> <i>m</i>\nраспределение легионеров по декуриям (ad decuriatum aut centuriatum venire <i>L</i>)."],
  ["decurio", "<big>decurio</big>\n\n<b>I decurio, āvī, ātum, āre</b> [decuria]\n<b>1)</b> делить на декурии (десятки) (equites decuriati <i>L</i>);\n<b>2)</b> разбивать народ на группы (d. tribules <i>C</i>).\n\n<b>II decurio, ōnis</b> <i>m</i> [decuria]\n<b>1)</b> декурион, командир декурии <i>Cs, T etc.</i>;\n<b>2)</b> сенатор, советник (<i>в римск. муниципиях и колониях</i>) <i>C etc.</i>; <i>pl.</i> сенат (<i>там же</i>) <i>C etc.</i>;\n<b>3)</b> начальник дворцовой прислуги <i>Su</i>."],
  ["decurionatus", "<big>decurionatus</big>\n\n<b>decuriōnātus, ūs</b> <i>m</i> [decurio II]\n<b>1)</b> <i>воен.</i> звание декуриона, декурионат <i>Cato</i>;\n<b>2)</b> (<i>в муниципиях и колониях</i>) звание сенаторов и советников: decurionatum alicui expedire <i>M</i> доставить кому-л. звание декуриона."],
  ["decurro", "<big>decurro</big>\n\n<b>dē–curro, currī</b> (<i>редко</i> <b>cucurrī</b>), <b>cursum, ere</b>\n<b>1)</b> сбегать, устремляться, быстро спускаться, сходить вниз (d. jugis <i>V</i>; summa ab arce <i>V</i>, <i>но</i>: alta arce <i>V</i>; ex montibus in vallem <i>Cs</i>); стекать (amnis monte decurrens <i>H</i>); выезжать: d. rus <i>C</i> отправляться на прогулку в деревню;\n<b>2)</b> падать, опадать: festinat d. velox flosculus vitae <i>J</i> быстро осыпается недолговечный цветок жизни;\n<b>3)</b> ринуться (barbari catervis decurrentes <i>T</i>); пробегать (septingenta milia passuum <i>C</i>; <i>перен.</i> per totas quaestiones <i>Q</i>): inceptum una decurre laborem <i>V</i> будь мне спутником в начатом (мною) труде || пронестись, промчаться: quadrigae meae decucurrerunt <i>Pt</i> колесницы мои пронеслись, <i>перен.</i> миновали мои красные денёчки;\n<b>4)</b> сходиться, съезжаться, подбегать: d. ad hamum <i>O</i> подплывать к крючку (<i>о рыбах</i>) || собираться (ad naves <i>Cs</i>);\n<b>5)</b> идти, двигаться, следовать (ad calces <i>C</i>; d. per vestigia paternae virtutis <i>Just</i>): versus una ratione decurrit <i>Q</i> стих движется равномерно;\n<b>6)</b> спешить (Caesar ad cohortandos milites decucurrit <i>Cs</i>);\n<b>7)</b> <i>воен.</i> маневрировать (cum legionibus <i>L</i>); проходить торжественным маршем, дефилировать (sine gladio d. <i>Q</i>);\n<b>8)</b> плыть, проплывать (pelago aperto <i>V</i>); протекать (saxosas inter valles <i>V</i>);\n<b>9)</b> проходить, пережить, прожить (vitae spatium <i>O</i>; tristem vitam <i>Ph</i>; spatium amoris <i>Lcr</i>): d. omnes honores <i>PJ</i> пройти всю иерархию высоких государственных постов;\n<b>10)</b> прибегать, обращаться (ad cohortationem, <i>C</i>; medicamenta <i>CC</i>; ad miseras preces <i>H</i>; ad extremum atque ultimum senatus consultum <i>C</i>);\n<b>11)</b> развивать, рассказывать (breviter de aliqua re <i>C</i>; pugnas d. versu <i>St</i>);\n<b>12)</b> завершать, оканчивать: aetate decursa <i>C</i> на склоне жизни;\n<b>13)</b> приходить, достигать: eo decursum est, ut... <i>L</i> пришли к тому (договорились о том), что...; d. in aliquam formam <i>Lcr</i> принять какую-л. форму;\n<b>14)</b> доходить, простираться (ad Pontum <i>PM</i>): populi cum Indo flumine decurrentes <i>PM</i> народы, живущие вдоль реки Инда;\n<b>15)</b> впадать (amnis in mare decurrit <i>L</i>; <i>перен.</i> d. ad iras <i>Lcr</i>)."],
  ["decursio", "<big>decursio</big>\n\n<b>dēcursio, ōnis</b> <i>f</i> [decurro]\n<b>1)</b> сбегание вниз, стекание (aquae <i>Eccl</i>);\n<b>2)</b> <i>воен.</i> учебные занятия, упражнения, манёвры <i>Su, Veg</i>; парад (d. campestris <i>Su</i>);\n<b>3)</b> набег, нападение, натиск (d. barbarorum <i>Hirt</i>; decursiones facere <i>bAl</i>)."],
  ["decursito", "<big>decursito</big>\n\n<b>dēcursito, —, —, āre</b> [<i>intens. к</i> decurro]\nбыстро пробегать <i>Ap</i>."],
  ["decursus", "<big>decursus</big>\n\n<b>I dēcursus, a, um</b> <i>part. pf. к</i>\ndecurro.\n\n<b>II dēcursus, ūs</b> <i>m</i> [decurro]\n<b>1)</b> сбегание вниз (d. praeceps <i>Pt</i>); стекание, падение (aquarum <i>O</i>; fluminis <i>Sen</i>; montibus ex altis d. aquae <i>Lcr</i>); понижение, уклон (planitiei <i>bH</i>);\n<b>2)</b> <i>воен.</i> учебные занятия, упражнения, манёвры <i>L, T</i>; парад (d. legionum <i>T</i>);\n<b>3)</b> набег, нападение (d. in litora aut urbem <i>T</i>);\n<b>4)</b> прохождение (пути): desistere ante decursum <i>Su</i> остановиться до окончания пути (не достигнув цели);\n<b>5)</b> прибытие, приход (navium <i>PJ</i>);\n<b>6)</b> ритмическое движение (versus <i>Q</i>);\n<b>7)</b> прохождение государственной службы (d. honorum <i>C</i>)."],
  ["decurto", "<big>decurto</big>\n\n<b>dē–curto, āvī, ātum, āre</b>\n<b>1)</b> укорачивать, подрезывать (radices decurtatae <i>PM</i>);\n<b>2)</b> увечить <i>Sen</i>; (<i>о речи</i>) комкать <i>C</i>."],
  ["decus", "<big>decus</big>\n\n<b>decus, oris</b> <i>n</i> [deceo]\n<b>1)</b> красота (d. enitet ore, <i>sc.</i> Aeneae <i>V</i>);\n<b>2)</b> краса, украшение, честь, слава, гордость (d. ornamentumque senectutis <i>C</i>; d. ac lumen imperii Romani <i>C</i> —<i>о Помпее</i>; praesidium et d. <i>H</i>): d. muliebre (matronale) <i>L</i> женская честь; decori esse alicui <i>Sl etc.</i> служить чьим-л. украшением (делать честь кому-л.); aliquid alicui d. dare <i>V</i> преподнести что-л. кому-л. в виде почётного дара; d. belli <i>L</i> воинская честь; d. hoc aevi <i>V</i> эта славная эпоха;\n<b>3)</b> славное деяние: belli decora <i>L etc.</i> блистательные воинские подвиги;\n<b>4)</b> славная родословная: longa decora praeferre <i>T</i> показывать длинный ряд (<i>т. е.</i> происходить от многих) славных предков;\n<b>5)</b> отборная часть, цвет (ex exercitu lecta decora <i>T</i>): d. innuptarum <i>Ctl</i> прекраснейшие из дев;\n<b>6)</b> <i>филос.</i> добродетель, нравственная высота (d. et virtus <i>C</i>): pulcherrimus cum decore <i>Sen</i> сочетающий замечательную внешнюю красоту с духовной."],
  ["decus-", "<big>decus-</big>\n\n<b>decūs-</b> <i>v. l.</i>\n= decuss-."],
  ["decussatim", "<big>decussatim</big>\n\n<b>decussātim</b> [decusso]\nв форме буквы X, накрест, крестообразно (perducere lineas <i>Vtr</i>; aliquid incidere <i>Col</i>)."],
  ["decussatio", "<big>decussatio</big>\n\n<b>decussātio, ōnis</b> <i>f</i> [decusso]\nкрестообразное пересечение двух линий <i>Vtr</i>."],
  ["decussi", "<big>decussi</big>\n\n<b>dēcussī</b> <i>pf. к</i>\ndecutio I."],
  ["decussio", "<big>decussio</big>\n\n<b>dēcussio, ōnis</b> <i>f</i> [decutio I]\nстряхивание, <i>перен.</i> отбрасывание (redundantioris nitoris <i>Tert</i>)."],
  ["decussis", "<big>decussis</big>\n\n<b>decussis, is</b> <i>m</i> [decem + as]\n<b>1)</b> монета в 10 ассов <i>LM, Vr, St</i>;\n<b>2)</b> число десять, десяток-» десятка: d. sexis <i>Vtr</i> (<i>тж. слитно</i>) число 16;\n<b>3)</b> скрещение в форме буквы X <i>Vtr, PM</i>."],
  ["decusso", "<big>decusso</big>\n\n<b>decusso, āvī, ātum, āre</b> [decussis]\nрасполагать крестообразно, наподобие буквы X <i>C, Col</i>."],
  ["decussus", "<big>decussus</big>\n\n<b>I dēcussus, a, um</b> <i>part. pf. к</i>\ndecutio I.\n\n<b>II dēcussus, (ūs)</b> <i>m</i>\nстряхивание, сбивание <i>PM</i>."],
  ["decutio", "<big>decutio</big>\n\n<b>I dē–cutio, cussī, cussum, ere</b> [quatio]\n<b>1)</b> стряхивать (rorem <i>V</i>); обивать (<i>о граде</i>) (fructum <i>Sen</i>); сбивать (papaverum capita baculo <i>L</i>); сшибать (nidos sagittis <i>PM</i>);\n<b>2)</b> изгонять, извергать, свергать (aliquem Capitolio <i>VM</i>);\n<b>3)</b> вывихнуть (digitus alicui decutitur <i>VM</i>);\n<b>4)</b> расшатывать, разрушать (murum arietibus <i>L</i>);\n<b>5)</b> <i>шутл.</i> захватить, цапнуть (argentum ex armario <i>Pl</i>);\n<b>6)</b> устранять, уносить, <i>перен.</i> сбрасывать со счетов (cetera aetate jam sunt decussa <i>C</i>).\n\n<b>II dē–cutio, —, —, īre</b> [cutis]\nснимать кожу, обдирать <i>Tert</i>."],
  ["dedamno", "<big>dedamno</big>\n\n<b>dē–damno, —, —, āre</b>\nснимать вину, отпускать грехи, очищать (aliquem <i>Tert</i>)."],
  ["dedeceo", "<big>dedeceo</big>\n\n<b>dē–deceo, decuī, —, ēre</b> (<i>употр. преим.</i> <i>3 л. sg. и pl.</i>)\n<b>1)</b> не быть к лицу, не идти, безобразить (motae comae non dedecuerunt aliquam <i>O</i>);\n<b>2)</b> не подобать, быть неприличным: preces quarum me dedecet usus <i>O</i> мольбы, которые мне не подобают (с которыми мне не пристало обращаться); non dedecet aliquem <i>C, Q</i> допустимо для кого-л.; ut ne dedeceat <i>C</i> чтобы не погрешить против такта;\n<b>3)</b> позорить, бесчестить (aliquem <i>Eccl</i>);\n<b>4)</b> презреть, пренебречь (jussa alicujus <i>St</i>)."],
  ["dedecet", "<big>dedecet</big>\n\n<b>dēdecet</b> — <i>см.</i>\ndedeceo."],
  ["dedecor", "<big>dedecor</big>\n\n<b>dēdecor, oris</b> <i>adj.</i>\n<b>1)</b> некрасивый, безобразный (alga <i>Sid</i>);\n<b>2)</b> позорный, постыдный (vita <i>St</i>)."],
  ["dedecoramentum", "<big>dedecoramentum</big>\n\n<b>dēdecorāmentum, ī</b> <i>n</i> [dedecor]\nбесчестье <i>Is</i>."],
  ["dedecoratio", "<big>dedecoratio</big>\n\n<b>dēdecorātio, ōnis</b> <i>f</i>\nбесчестье, позор <i>Tert</i>."],
  ["dedecorator", "<big>dedecorator</big>\n\n<b>dēdecorātor, ōris</b> <i>m</i>\nпокрывающий позором (d. deorum <i>Tert</i>)."],
  ["dedecoro", "<big>dedecoro</big>\n\n<b>dēdecoro, āvī, ātum, āre</b> [dedecus]\n<b>1)</b> бесчестить, позорить, срамить (aliquem <i>Ter</i>; se flagitiis omnibus <i>Su</i>);\n<b>2)</b> безобразить (faciem alicujus aliqua re <i>Prp</i>)."],
  ["dedecorosus", "<big>dedecorosus</big>\n\n<b>dēdecorōsus, a, um</b> [dedecus]\nпозорный, порочащий, постыдный <i>Eccl, CTh</i>."],
  ["dedecorus", "<big>dedecorus</big>\n\n<b>dē–decōrus, a, um</b> <i>T</i>\n= dedecorosus."],
  ["dedecus", "<big>dedecus</big>\n\n<b>dē–decus, oris</b> <i>n</i>\n<b>1)</b> бесчестье, позор, стыд, срам (ignominia atque d., d. atque infamia <i>C</i>): dedecori esse (fieri) <i>Pl, C</i> ложиться позором, быть постыдным; per dedecora (per d.) <i>Sl</i> позорным образом, постыдно;\n<b>2)</b> позорное деяние, бесчестный поступок (d. admittere <i>Cs</i>; dedecore se abstinere <i>C</i>); dedecora militiae <i>L</i> постыдное поведение на войне;\n<b>3)</b> физическое уродство, безобразие: d. Midae <i>O</i> уродство (<i>т. е.</i> ослиные уши) царя Мидаса;\n<b>4)</b> <i>бран.</i> выродок, позорное пятно (tu, vitae d. primum <i>Ap</i>; d. naturae <i>Ph</i>);\n<b>5)</b> <i>филос.</i> порок, безнравственность <i>C</i>."],
  ["dedi", "<big>dedi</big>\n\n<b>I dedī</b> <i>pf. к</i>\ndo.\n\n<b>II dēdī</b> <i>inf. praes. pass. к</i>\ndedo."],
  ["dedicatio", "<big>dedicatio</big>\n\n<b>dēdicātio, ōnis</b> <i>f</i> [dedico]\nпосвящение, освящение (templi <i>L</i>; theatri <i>PM, Su</i>; pontis <i>Su</i>)."],
  ["dedicativus", "<big>dedicativus</big>\n\n<b>dēdicātīvus, a, um</b> <i>лог.</i>\nутвердительный <i>Ap</i>."],
  ["dedicator", "<big>dedicator</big>\n\n<b>dēdicātor, ōris</b> <i>m</i>\nсовершивший освящение, <i>перен.</i> виновник: d. damnationis <i>Tert</i> порицатель, хулитель."],
  ["dedicatus", "<big>dedicatus</big>\n\n<b>dēdicātus, (ūs)</b> <i>m</i> [dedico]\nпосвящение <i>Ap</i> (<i>v. l.</i>)."],
  ["dedico", "<big>dedico</big>\n\n<b>dē–dico, āvī, ātum, āre</b>\n<b>1)</b> показывать, обнаруживать: gravius plus in se corporis esse dedicat <i>Lcr</i> более тяжёлое (тело) обнаруживает в себе больше вещества || указывать, объявлять (aliquid in censu <i>C</i>, <i>реже</i> in censum <i>AG</i> — <i>см.</i> census II);\n<b>2)</b> <i>культ.</i> посвящать (templum alicui <i>C</i>); почитать, чтить (d. Concordiam aede <i>O</i>): d. Junonem <i>L</i> (= d. templum Junoni) воздвигнуть храм Юноне; Apollo dedicatus (= cui templum dedicatum est) <i>H</i> Аполлон, которому сооружён храм; d. librum alicui <i>Ph, Q, PM</i> посвятить кому-л. книгу; vitam alicui rei d. <i>Capit</i> посвятить жизнь чему-л.;\n<b>3)</b> освящать, открывать (theatrum, domum <i>Su</i>);\n<b>4)</b> впервые надеть (togam in funus filio <i>Dig</i>)."],
  ["dedignatio", "<big>dedignatio</big>\n\n<b>dēdīgnātio, ōnis</b> <i>f</i> [dedignor]\nпренебрежение, неприятие (tacita <i>Q</i>); отказ (d. parendi <i>PJ</i>)."],
  ["dedignor", "<big>dedignor</big>\n\n<b>dē–dīgnor, ātus sum, ārī</b> <i>depon.</i>\nне признавать достойным, отвергать, презирать: aliquem d. maritum <i>V, O</i> отвергнуть чьё-л. брачное предложение; aliquid intueri d. <i>Sen</i> отказываться разглядывать что-л.; is me nec comitem nec dedignatus amicum est <i>O</i> он не отверг меня ни как товарища, ни как друга."],
  ["dedisco", "<big>dedisco</big>\n\n<b>dē–disco, didicī, —, ere</b>\nразучиться (d. Latine loqui <i>O</i>; d haec verba <i>C</i>; quod dedidicit, id dediscit <i>Pl</i>; dediscit animus sero, quod dedidicit diu <i>Sen</i>); забыть (aliquem, aliquid <i>Sen</i>): dedidicit jam pace ducem <i>Lcn</i> в условиях мира (Помпей) уже забыл искусство полководца."],
  ["dedistin", "<big>dedistin</big>\n\n<b>dēdistin</b> <i>Pl</i>\n= dedistine (<i>к</i> do)."],
  ["dediticius", "<big>dediticius</big>\n\n<b>dēditīcius, a, um</b> [deditio]\nсдавшийся на милость победителя, капитулировавший (Belgae <i>Cs</i>): dediticii <i>Sl</i> покорённые (подданные) италики (<i>в отличие от</i> socii)."],
  ["deditio", "<big>deditio</big>\n\n<b>dēditio, ōnis</b> <i>f</i> [dedo]\n<b>1)</b> сдача, капитуляция: compellere ad (in <i>L</i>) deditionem <i>QC, Su</i> принудить к сдаче; significare deditionem <i>Cs</i> дать знать о желании сдаться; agere de deditione <i>Cs</i> договариваться о сдаче; venire in deditionem (facere deditionem <i>Cs etc.</i>) hosti <i>или</i> ad hostem <i>L, Q</i> сдаться на капитуляцию противнику;\n<b>2)</b> выдача (с головой), передача (d. noxae <i>или</i> ad noxam <i>Dig</i> — <i>о рабе или животном, причинившем ущерб</i>)."],
  ["deditus", "<big>deditus</big>\n\n<b>dēditus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> dedo: dedita opera <i>Pl, Ter etc.</i> умышленно, нарочно, с намерением <i>или</i> с нарочным (epistulam mittere <i>C</i>);\n<b>2.</b> <i>adj.</i> преданный, пристрастившийся, предающийся (libidini <i>C</i>; studio litterarum <i>C</i>; somno ciboque <i>T</i>; in pugnae studio <i>Lcr</i>)."],
  ["dedo", "<big>dedo</big>\n\n<b>dē–do, didī, ditum, ere</b>\n<b>1)</b> отдавать, сдавать: d. se (<i>тж.</i> d. manus <i>Lcr</i>) сдаваться (se suaque omnia Caesari <i>Cs</i>);\n<b>2)</b> передавать, предавать (aliquid alicui <i>C etc.</i>); выдавать (aliquem alicui ad supplicium <i>L</i> <i>и</i> in cruciatum <i>Cs</i>): d. aliquem omnibus periculis <i>C</i> подвергать кого-л. всяческим опасностям;\n<b>3)</b> <i>перен.</i>: d. se предаваться, посвящать себя (doctrinae, litteris, amicis, studio litterarum <i>C etc.</i>; pro re publica <i>Sl</i>): d. se duritiae <i>Nep</i> повести суровый образ жизни; d. operam <i>или</i> operas alicui <i>Pl</i> оказать помощь кому-л., быть к услугам кого-л.; dedita opera <i>Pl, Ter, C, L</i> преднамеренно, умышленно, нарочно."],
  ["dedoceo", "<big>dedoceo</big>\n\n<b>dē–doceo, —, —, ēre</b>\nотучать, заставлять забыть (d. aliquem geometriam <i>C</i>): onus dedocendi gravius ac prius est quam docendi <i>Q</i> задача отучить (от дурного) труднее и важнее, чем задача научить (полезному)."],
  ["dedoleo", "<big>dedoleo</big>\n\n<b>dē–doleo, uī, —, ēre</b>\nперестать печалиться, не горевать больше <i>O</i>."],
  ["dedolo", "<big>dedolo</big>\n\n<b>dē–dolo, āvī, ātum, āre</b>\n<b>1)</b> срубать, вырубать (ridicas <i>Col</i>); вырезывать, высекать (vasculum crystallo <i>Ap</i>);\n<b>2)</b> обтёсывать (arborem <i>PM</i>);\n<b>3)</b> поколотить (aliquem crebris ictibus <i>Ap</i>);\n<b>4)</b> (<i>sensu obsceno</i>) разделывать (aliquam <i>Ap</i>)."],
  ["deduco", "<big>deduco</big>\n\n<b>dē–dūco, dūxī, ductum, ere</b>\n<b>1)</b> сводить, спускать вниз (milites ex locis superioribus in campum <i>Cs</i>): d. aliquem de rostris <i>C</i> увести кого-л. с трибуны; equites (equitatum) d. in pedes <i>L</i> спешить конницу;\n<b>2)</b> низводить (imbres nivesque deducunt Jovem <i>H</i>);\n<b>3)</b> снимать (tiaram <i>Su</i>);\n<b>4)</b> опускать, зачёсывать вниз (pectine crines <i>O</i>); поглаживать (caesariem longae barbae <i>O</i>);\n<b>5)</b> пригибать (ramos pondere suo <i>O</i>): talos ad terram d. <i>Pt</i> ходить на пятках;\n<b>6)</b> приводить (elephantus Romam ingenti pompa <i>Eutr</i>): uxorem domum d. <i>Ter</i> взять в жёны, жениться;\n<b>7)</b> уводить, отводить (aliquem in carcerem <i>Sl</i>; exercitum ex his regionibus <i>Cs</i>; milites in hiberna <i>L</i>; navem in aliquem portum <i>Pt</i>); повести (classem in proelium <i>Nep</i>); пригонять (boves <i>O</i>; pecora <i>Cs</i>);\n<b>8)</b> нести, катить (amnes deducunt undas in mare <i>O</i>); переносить: d. Aeolium carmen ad Italos modos <i>H</i> перенести эолийскую поэзию в италийские лады; d. aliquem ex possessione <i>L</i> (de fundo <i>C</i>) изгнать кого-л. из владения; d. coloniam <i>C etc.</i> основать колонию; d. navem (in aquam) <i>или</i> d. navem litore <i>V etc.</i> спустить корабль на воду;\n<b>9)</b> переселять, поселять (colonos in omnes colonias totius Italiae <i>C</i>);\n<b>10)</b> вводить (aliquem in regnum, in possessionem alicujus rei <i>T</i>): d. vela (carbasa) <i>O</i> распустить (поднять) паруса;\n<b>11)</b> сводить (orationem de caelo ad citeriora <i>C</i>);\n<b>12)</b> отклонять, отвлекать (aliquem de sententia <i>C</i>; a tristitia <i>C</i>; lunam cursu <i>O</i> <i>или</i> e cursu <i>Tib</i>; a pristino victu <i>Nep</i>): d. aliquem a recta via <i>rhH</i> <i>или</i> de fide <i>C</i> совратить (сбить) кого-л. с прямого пути;\n<b>13)</b> завлекать, заманивать (in insidias <i>Just</i>);\n<b>14)</b> скидывать, вычитать (aliquid de summa <i>C</i>): d. de capite aliquid <i>C</i> что-л. вычитать (отчислять) из капитала; d. cibum <i>Ter</i> сократить питание, поменьше кормить;\n<b>15)</b> лишать, отнимать, удалять (corpore febres, animo curas <i>H</i>): d. alicui aliquid <i>C etc.</i> лишить кого-л. чего-л.;\n<b>16)</b> выводить, производить (originem, nomen ab aliquo <i>PM, O etc.</i>);\n<b>17)</b> приводить (d. aliquem ad consulem <i>L</i>; in conspectum Caesaris <i>Cs</i>): d. aquam in fossas <i>PM</i> провести воду в рвы (каналы); lis ad forum deducta est <i>Ph</i> дело было передано в суд; d. aliquem ad fletum <i>C</i> довести кого-л. до слёз; res ad anna deducitur <i>Cs</i> начинается вооружённая борьба; si in eum casum deduceretur <i>Cs</i> если дело дойдёт с ним до этого; audi, quo rem deducam <i>H</i> послушай, к чему я веду; d. aliquem in periculum <i>Cs</i> подвергнуть кого-л. опасности; rem huc (in eum locum) deduxi, ut... <i>Cs, C</i> я довёл дело до того, что...; in tempora (Augusti) d. opus <i>O</i> довести повествование до эпохи Августа;\n<b>18)</b> побуждать, склонять (aliquem ad eam sententiam <i>Cs</i>; d. aliquem, ut... <i>L etc.</i>);\n<b>19)</b> провожать (d. aliquem domum <i>rhH</i>; ad forum, de domo <i>C</i>; corpus Alexandri in Hammonis templum <i>Just</i>);\n<b>20)</b> вытягивать, тянуть: d. filum (fila) <i>O, Ctl</i> прясть; d. vetus argumentum in tela <i>O</i> выткать древнюю повесть (<i>о Палладе</i>);\n<b>21)</b> изготовлять, сочинять (mille die versus <i>H</i>; d. carmen <i>O</i>);\n<b>22)</b> изображать, написать, начертить (litteram mero <i>O</i>; formam alicujus <i>Sen</i>);\n<b>23)</b> вовлечь, втянуть (aliquem in bellum <i>Just</i>): deduci попасть, впасть (in egestatem <i>Sen</i>)."],
  ["deducta", "<big>deducta</big>\n\n<b>dēducta, ae</b> <i>f</i> [deduco]\n(<i>sc.</i> pecunia <i>или</i> pars) отказ от части (вычет из) наследства (во <i>избежание несения расходов по связанными с завещанием sacra</i>) <i>C</i>."],
  ["deductio", "<big>deductio</big>\n\n<b>dēductio, ōnis</b> <i>f</i> [deduco]\n<b>1)</b> отведение, увод (militum in oppida <i>C</i>);\n<b>2)</b> отведение, отвод (rivorum a fonte <i>C</i>);\n<b>3)</b> переселение, колонизация (in aliquos agros <i>C</i>);\n<b>4)</b> провожание, проводы (dare caeco deductionem <i>Aug</i>);\n<b>5)</b> основание (d. oppidi <i>PM</i>);\n<b>6)</b> вычитание, вычет (ex pecunia <i>C</i>): sine ulla deductione <i>Sen</i> без всяких скидок;\n<b>7)</b> изгнание (ex possessione <i>C</i>);\n<b>8)</b> выведение, дедукция: d. rationis <i>C</i> доказывание, доказательство;\n<b>9)</b> (<i>тж.</i> d. sponsae in domum mariti <i>Dig</i>) женитьба <i>Dig</i>."],
  ["deductivus", "<big>deductivus</big>\n\n<b>dēductīvus, a, um</b> <i>грам.</i>\nпроизводный (nomina, pronomina <i>Is etc.</i>)."],
  ["deductor", "<big>deductor</big>\n\n<b>dēductor, ōris</b> <i>m</i> [deduco]\n<b>1)</b> провожатый, спутник, попутчик <i>C, PJ</i>;\n<b>2)</b> руководитель, наставник (omnis veritatis <i>Tert</i>)."],
  ["deductorium", "<big>deductorium</big>\n\n<b>dēductōrium, ī</b> <i>n</i>\nотводной канал <i>Pall</i>."],
  ["deductorius", "<big>deductorius</big>\n\n<b>dēductōrius, a, um</b> [deduco]\n<b>1)</b> отводный (cuniculus <i>Veg</i>);\n<b>2)</b> выводящий (из организма), <i>т. е.</i> слабительный (medicamenta <i>CA</i>)."],
  ["deductus", "<big>deductus</big>\n\n<b>I dēductus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> deduco;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> загнутый внутрь: nasus d. <i>Su, Lampr</i> орлиный нос;\n<b>2)</b> приглушённый, пониженный (vox <i>LM</i>); тихий (carmen <i>V</i>).\n\n<b>II dēductus, (ūs)</b> <i>m</i>\nстаскивание (ponderis <i>Ap</i>)."],
  ["dedux", "<big>dedux</big>\n\n<b>dēdux, ducis</b> <i>adj.</i> [deduco]\nпроисшедший, порождённый (Gallia d. Heliconis <i>Symm</i>)."],
  ["deduxi", "<big>deduxi</big>\n\n<b>dēdūxī</b> <i>pf. к</i>\ndeduco."],
  ["deeram", "<big>deeram</big>\n\n<b>deeram</b> <i>impf. к</i>\ndesum."],
  ["deerro", "<big>deerro</big>\n\n<b>de–erro, āvī, ātum, āre</b>\n<b>1)</b> блуждать, заблудиться, сбиваться (itinere <i>Q</i> <i>и</i> in itinere <i>C</i>); отстать (in navigando a ceteris <i>Sl</i>): in alienum d. tramitem <i>PM</i> последовать по неправильному пути;\n<b>2)</b> заблуждаться, отклоняться, уклоняться (d. a vero <i>Lcr</i>)."],
  ["defaecabilis", "<big>defaecabilis</big>\n\n<b>dēfaecābilis, e</b> [defaeco]\nлегко очищаемый (cisterna <i>Sid</i>)."],
  ["defaecatio", "<big>defaecatio</big>\n\n<b>dēfaecātio, ōnis</b> <i>f</i>\nочищение (carnis <i>Tert</i>)."],
  ["defaeco", "<big>defaeco</big>\n\n<b>dē–faeco, āvī, ātum, āre</b> [faex]\nочищать от осадка, гущи, мути (vinum <i>Col, PM</i>); чистить, мыть (aliquem <i>Pl</i> <i>и</i> aliquid <i>Veg</i>): animus defaecatus <i>Pl</i> безмятежное настроение."],
  ["defaen-", "<big>defaen-</big>\n\n<b>dēfaen-</b> <i>v. l.</i>\n= defen-."],
  ["defamatus", "<big>defamatus</big>\n\n<b>dē–fāmātus, a, um</b> [fama]\nопороченный, низкий, позорный (vocabula <i>AG</i>; turpitudine vitae d. <i>AG</i>)."],
  ["defamis", "<big>defamis</big>\n\n<b>dēfāmis, e</b> <i>Ap</i>\n= infamis."],
  ["defanatus", "<big>defanatus</big>\n\n<b>dē–fānātus, a, um</b> [fanum]\nлишённый святости, осквернённый <i>Eccl</i>."],
  ["defarinatus", "<big>defarinatus</big>\n\n<b>dē–farīnātus, a, um</b> [farina]\nпревращенный в муку, размолотый (semen <i>Tert</i>)."],
  ["defatigatio", "<big>defatigatio</big>\n\n<b>dēfatīgātio, ōnis</b> <i>f</i> [defatigo]\nутомление, усталость, изнурённость (hostium, equorum <i>Cs</i>): d. exercitationum <i>C</i> усталость от (вследствие) упражнений."],
  ["defatigo", "<big>defatigo</big>\n\n<b>dē–fatīgo, āvī, ātum, āre</b>\nутомлять, изнурять (aliquem assiduo labore <i>Cs</i>; defatigati proelio ac fuga <i>QC</i>)."],
  ["defatiscor", "<big>defatiscor</big>\n\n<b>dēfatīscor</b> <i>v. l.</i>\n= defetiscor."],
  ["defeci", "<big>defeci</big>\n\n<b>dēfēcī</b> <i>pf. к</i>\ndeficio."],
  ["defeco", "<big>defeco</big>\n\n<b>dēfēco</b> <i>v. l.</i>\n= defaeco."],
  ["defectio", "<big>defectio</big>\n\n<b>dēfectio, ōnis</b> <i>f</i> [deficio]\n<b>1)</b> отпадение, отложение (alicujus ab aliquo, ad aliquem <i>L etc.</i>): sollicitare ad defectionem <i>L</i> подстрекать к отпадению;\n<b>2)</b> уменьшение, исчезновение, недостаток, убыль (aquarum <i>Frontin</i>; pecuniae <i>Macr</i>): d. (virium) <i>C etc.</i> слабость, изнеможение; d. animae <i>CC</i> обморок; d. animi <i>или просто</i> d. <i>Sen</i> уныние, малодушие; d. solis (lunae) <i>C, Q</i> затмение солнца (луны);\n<b>3)</b> <i>ритор.</i> эллипсис (per defectionem dicere <i>AG</i>)."],
  ["defectivus", "<big>defectivus</big>\n\n<b>dēfectīvus, a, um</b> [deficio]\n<b>1)</b> <i>мед.</i> перемежающийся (febris <i>CA</i>);\n<b>2)</b> несовершенный, порочный (genitura <i>Tert</i>);\n<b>3)</b> <i>астр.</i> подвергающийся затмению <i>Amm</i>;\n<b>4)</b> <i>грам.</i> недостаточный, неполный (nomen, verbum)."],
  ["defector", "<big>defector</big>\n\n<b>dēfector, ōris</b> <i>m</i> [deficio]\nотступник, отщепенец (patris sui <i>T</i>)."],
  ["defectrix", "<big>defectrix</big>\n\n<b>dēfectrīx, īcis</b> <i>adj. f</i>\nнесовершенная, неполная (virtus <i>Tert</i>)."],
  ["defectus", "<big>defectus</big>\n\n<b>I dēfectus, ūs</b> <i>m</i> [deficio]\n<b>1)</b> отпадение, отложение (legionum <i>Capit</i>);\n<b>2)</b> убывание, уменьшение, исчезновение, недостаток (aquarum <i>L</i>); d. animae <i>PM</i> обморок;\n<b>3)</b> затмение (solis ac lunae <i>Sen</i>);\n<b>4)</b> усталость, утомление, изнеможение (membrorum <i>CA</i>).\n\n<b>II dēfectus, a, um</b> [deficio]\n<b>1.</b> <i>part. pf. к</i> deficio;\n<b>2.</b> <i>adj.</i> обессиленный, изнурённый, расслабленный, слабый (d. annis et viribus <i>Col</i>); истощённый (d. senio arbor <i>Col</i>); изношенный, потрёпанный (ratis <i>VF</i>); <i>юр.</i> (<i>о долгах</i>) безнадёжный (defecta nomina <i>Dig</i>)."],
  ["defendo", "<big>defendo</big>\n\n<b>dē–fendo, fendī, fēnsum, ere</b>\n<b>1)</b> отражать, давать отпор (hostem <i>Enn etc.</i>; vi vis illata defenditur <i>C</i>); отвращать, отводить (pericula <i>C</i>): d. crimen <i>C</i> отклонить от себя обвинение; d. bellum <i>Cs</i> вести оборонительную войну; d. aliquem ab aliqua re <i>Cs etc.</i> (alicui rei <i>V</i>) отвращать что-л. от кого-л., ограждать кого-л. от чего-л.;\n<b>2)</b> защищать, оборонять (aliquem ab injuria alicujus <i>Cs</i>; castra ab hostibus <i>Cs</i>; d. se adversus populum Romanum <i>C</i>): d. frigus <i>H</i> <i>и</i> a frigore <i>V</i> защищать от холода;\n<b>3)</b> утверждать (aliquid <i>C</i>): ille nihil ex his sponte susceptum, sed principi paruisse defendebat <i>T</i> он (Суилий) утверждал, что всё это он делал не по собственной воле, а подчиняясь государю; verissime defenditur numquam aequitatem ab utilitate posse sejungi <i>C</i> совершенно правильно утверждают, что справедливость не может быть отделена от полезности;\n<b>4)</b> исполнять (officium censurae <i>C</i>); играть роль (d. actorum partas officiumque virile <i>H</i>);\n<b>5)</b> <i>юр.</i> возбуждать судебное дело, преследовать по суду (necem alicujus <i>Dig</i>): d. aliquem in libertatem <i>Dig</i> требовать возвращения свободы кому-л."],
  ["defenero", "<big>defenero</big>\n\n<b>dē–fēnero, āvī, ātum, āre</b>\nпо-ростовщически эксплуатировать, опутывать долгами (hominem egentem <i>Pl</i>; provincias <i>C</i>): defeneratus <i>Ap, Ambr</i> увязший (запутавшийся) в долгах."],
  ["defensa", "<big>defensa</big>\n\n<b>dēfēnsa, ae</b> <i>f</i>\nзащита <i>Tert</i>."],
  ["defensabilis", "<big>defensabilis</big>\n\n<b>dēfēnsābilis, e</b>\nмогущий быть защищенным <i>Ambr</i>."],
  ["defensaculum", "<big>defensaculum</big>\n\n<b>dēfēnsāculum, ī</b> <i>n</i>\nсредство защиты <i>Dig, Aug</i>."],
  ["defensator", "<big>defensator</big>\n\n<b>dēfēnsātor, ōris</b> <i>m</i> <i>Hier</i>\n= defensor."],
  ["defensatrix", "<big>defensatrix</big>\n\n<b>dēfēnsātrīx, īcis</b> <i>f</i>\nзащитница <i>Eccl</i>."],
  ["defensio", "<big>defensio</big>\n\n<b>dēfēnsio, ōnis</b> <i>f</i> [defendo]\n<b>1)</b> оборона (urbis, castrorum <i>Cs</i>); защита (miserorum <i>C</i>): d. criminis <i>C etc.</i> отклонение обвинения, оправдание; defensionem non habere in aliqua re <i>C</i> не уметь оправдаться в чём-л.;\n<b>2)</b> мотив защиты, оправдательный довод (probare defensionem <i>C</i>);\n<b>3)</b> защитительная речь, апология (defensionem Demosthenis legere <i>PM</i>);\n<b>4)</b> преследование по суду (d. mortis <i>Dig</i>)."],
  ["defensito", "<big>defensito</big>\n\n<b>dēfēnsito, āvī, —, āre</b> [<i>frequ. к</i> defendo]\nчасто защищать, постоянно брать под свою защиту <i>C, AG</i>."],
  ["defensiuncula", "<big>defensiuncula</big>\n\n<b>dēfēnsiuncula, ae</b> <i>f</i> [<i>demin. к</i> defensio]\nнекоторого рода <i>или</i> незначительная защита <i>C</i> (<i>v. l.</i>)."],
  ["defenso", "<big>defenso</big>\n\n<b>dēfēnso, —, —, āre</b> [<i>intens. к</i> defendo]\n<b>1)</b> упорно защищать, оборонять (clipeo humeros <i>O</i>; aliquem ab hostibus <i>Sl</i>; aliquem injuria <i>Pl</i>);\n<b>2)</b> решительно отклонять, давать отпор (d. invidiam <i>AG</i>)."],
  ["defensor", "<big>defensor</big>\n\n<b>dēfēnsor, ōris</b> <i>m</i> [defendo]\n<b>1)</b> дающий отпор, отклоняющий (periculi, calamitatum <i>C</i>);\n<b>2)</b> защитник (urbis <i>Just</i>); поборник (juris et libertatis <i>C</i>); заступник (causae alicujus <i>C</i>): moenia vacua (a) defensoribus <i>L</i> стены без гарнизона."],
  ["defenstrix", "<big>defenstrix</big>\n\n<b>dēfēnstrīx, īcis</b> <i>f</i> <i>C</i>\n= defensatrix."],
  ["defero", "<big>defero</big>\n\n<b>dē–fero, tulī, lātum, ferre</b>\n<b>1)</b> переносить вниз, сносить, снимать (ramalia tecto <i>O</i>): deferri in praeceps <i>L</i> свалиться в пропасть;\n<b>2)</b> спускать (nudos pedes in terram <i>Pt</i>); погружать, вонзать (d. ferrum elatum in pectus <i>T</i>): deferri sub aequora <i>O</i> погрузиться в морскую пучину;\n<b>3)</b> (<i>тж.</i> <i>pass.</i>) течь: flumina in mare deferuntur <i>PJ</i> реки текут в море; flumina deferentia <i>PJ</i> текущие (не скованные льдом) реки;\n<b>4)</b> перемещать, передвигать, переносить (castra in viam <i>L</i>): secundo Tiberi ad urbem deferri <i>L</i> уноситься (плыть) к городу по течению Тибра; aestus defert naves in terram <i>L</i> прибой относит корабли к земле; Germani ad castra Caesaris delati sunt <i>Cs</i> германцы оказались приведёнными к лагерю (неожиданно для себя очутились у лагеря) Цезаря; lectica deferri in curiam <i>Su</i> отправиться на носилках в сенат;\n<b>5)</b> забросить, занести: alicui in sinum deferri <i>погов. C</i> неожиданно выпасть на чью-л. долю;\n<b>6)</b> вносить (pecuniam in aerarium <i>L</i>): d. aliquem ad aerarium in beneficiis <i>C</i> внести кого-л. в список граждан, получающих денежное вознаграждение || (<i>в игре</i>) ставить (lamnam <i>Pt</i>);\n<b>7)</b> свергнуть, сбросить (aliquem in abruptum barathrum <i>Ctl</i>); deferri ad terram <i>L, Sen</i> рухнуть на землю;\n<b>8)</b> нести (вести <i>или</i> везти) на продажу (onus deferendum ad mercatum <i>Pt</i>); продавать (aliquid eodem pretio d. <i>PM</i>);\n<b>9)</b> предоставлять, передавать, возлагать, поручать (aliquam rem ad aliquem <i>или</i> alicui <i>C etc.</i>): omnem rem ad Pompejum d. <i>C</i> поручить (предоставить) всё Помпею; totius belli summam ad aliquem d. <i>Cs</i> возложить все военные дела на кого-л.; praemium alicui d. <i>C</i> выдать кому-л. награду; d. causas ad aliquem <i>C</i> поручить кому-л. ведение судебных дел; d. ad aliquem controversias litesque <i>Eutr</i> предоставить кому-л. решение споров и тяжб;\n<b>10)</b> доносить, докладывать, сообщать (aliquid ad aliquem <i>L, C etc.</i>; dicta alicujus <i>V</i>): haec a compluribus ad Caesarem deferebantur <i>Cs</i> об этом доносили Цезарю многие; fama detulit armari classem <i>V</i> донёсся слух, что снаряжается флот; d. aliquam rem (<i>или</i> de aliqua re) ad senatum <i>L, C, T etc.</i> доложить дело в сенате;\n<b>11)</b> объявлять (alicui aliquam rem <i>или</i> de aliqua re <i>L, C, Cs etc.</i>);\n<b>12)</b> подать жалобу, привлечь к судебной ответственности (d. aliquem <i>или</i> nomen alicujus <i>C etc.</i>): d. crimen in aliquem <i>C</i> возвести на кого-л. обвинение;\n<b>13)</b> указывать, приводить: d. censum <i>L</i> указать (декларировать) имущественное положение (<i>при переписи</i>); mille quingentum aeris in censum d. <i>AG</i> заявить при переписи о наличии имущества на 1500 ассов."],
  ["defervefacio", "<big>defervefacio</big>\n\n<b>dē–fervē–facio, fēcī, factum, ere</b> [ferveo]\nсварить (brassicam <i>Cato</i>); вскипятить (aquam <i>Vtr</i>); отварить (radicem in vino <i>PM</i>)."],
  ["deferveo", "<big>deferveo</big>\n\n<b>dē–ferveo, —, —, ēre</b>\n<b>1)</b> перебродить <i>PM</i>;\n<b>2)</b> вывариться (ad tertias <i>Eccl</i>)."],
  ["defervesco", "<big>defervesco</big>\n\n<b>dē–fervēsco, fervī (ferbuī), —, ere</b>\n<b>1)</b> перестать кипеть (ubi lupinus deferverit <i>Cato</i>); перебродить (musteus fructus deferbuit <i>Col</i>);\n<b>2)</b> утихать, успокаиваться (cupiditates defervescunt <i>C</i>): mare deferbuit <i>AG</i> море улеглось;\n<b>3)</b> отстояться, очиститься, проясниться (quasi deferverat oratio <i>C</i>)."],
  ["defervo", "<big>defervo</big>\n\n<b>dēfervo, —, —, ere</b> <i>St</i>\n= deferveo."],
  ["defessus", "<big>defessus</big>\n\n<b>dēfessus, a, um</b> [defetiscor]\nусталый, утомлённый, измученный, обессиленный (defessi cultu agrorum aratores <i>C</i>)."],
  ["defetig-", "<big>defetig-</big>\n\n<b>dēfetīg-</b> <i>v. l.</i>\n= defatig-."],
  ["defetiscentia", "<big>defetiscentia</big>\n\n<b>dēfetīscentia, ae</b> <i>f</i>\nусталость, утомление <i>Tert</i>."],
  ["defetiscor", "<big>defetiscor</big>\n\n<b>dē–fetīscor, fessus sum, fetīscī</b> <i>depon.</i> [fatiscor]\nустать, утомиться, измучиться (d. quaeritando <i>Pl</i>): defessa ac refrigerata accusatione <i>C</i> когда обвинитель устанет и остынет; defessae res <i>Sil</i> расстроенные дела, разруха."],
  ["defexit", "<big>defexit</big>\n\n<b>dēfexit</b> <i>арх.</i> <i>L</i>\n= defecerit (<i>к</i> deficio)."],
  ["deficiens", "<big>deficiens</big>\n\n<b>dēficiēns, entis</b> [deficio] <i>грам.</i>\nнедостаточный, неполный (verbum)."],
  ["deficio", "<big>deficio</big>\n\n<b>dē–ficio, fēcī, fectum, ere</b> [facio]\n<b>1)</b> отложиться, отпасть, изменить (d. ab aliquo <i>L, C etc.</i>; d. ab amicitia populi Romani <i>Cs</i>): d. a patribus ad plebem <i>L</i> перейти от патрициев на сторону плебса; d. a se <i>C</i> изменить самому себе; illis legibus d. <i>L</i> отказаться от соблюдения этих законов; d. a virtute <i>C</i> утратить доблесть (добродетельность);\n<b>2)</b> оказываться недостаточным, не хватать, недоставать (si cibaria defecerint in navigatione <i>Dig</i>): aqua ciboque defecti <i>Q</i> лишённые воды и продовольствия; dies deficiat, si velim eam causam defendere <i>C</i> не хватило бы дня, если бы я пожелал защищать это дело; vires deficiunt <i>Cs</i> силы слабеют (сил не хватает); membrum quod deficit <i>CC</i> парализованная часть тела; pecunia deficit <i>L</i> не хватает денег; negandi causa avaro numquam deficit <i>PS</i> у скупца никогда дело не станет за причиной отказа; horrea fecundas ad messes deficientia <i>Tib</i> житницы, которые не в состоянии вместить богатый урожай; non me deficiet rogitare <i>Prp</i> я не перестану спрашивать;\n<b>3)</b> приходить в упадок (pictura defecit <i>Pt</i>): memoria deficit <i>C etc.</i> память слабеет (начинает изменять); d. vita <i>Pl etc.</i> находиться при смерти; deficiens inquit <i>Pl</i> он в изнеможении сказал; nec animo defecit, nec consilio <i>L</i> он не утратил ни мужества, ни благоразумия; non deficiente crumena <i>H</i> с достаточными средствами; d. aliquem (<i>редко</i> alicui) недоставать у кого-л. (tempus deficit aliquem <i>VM</i>; tela nostris — <i>v. l.</i> nostros — deficiunt <i>Cs</i>): linguam defecerat umor <i>O</i> язык пересох; dubiis ne defice rebus <i>V</i> не оставляй (меня) в затруднительных обстоятельствах; <i>pass.</i> deficitur aliquis (aliquid) (ab) aliqua re кто-(что-)л. лишен(о) чего-л. (defici a viribus <i>Cs</i>; defici pecunia <i>Sen</i>): defici animo <i>QC</i> лишиться сознания, <i>но</i> (<i>см. выше</i>): animo deficere <i>Lcr, Cs etc.</i> <i>или просто</i> d. <i>Cs, C etc.</i> пасть духом, приуныть; defectum corpore caput <i>PJ</i> голова, отделённая от тела; sanguine defecti artus <i>O</i> обескровленные члены;\n<b>4)</b> беднеть, нищать, банкротиться, становиться неплатёжеспособным (si debitores defecerint <i>Dig</i>);\n<b>5)</b> гаснуть, угасать: ignis deficit <i>V</i> огонь гаснет (догорает), <i>но</i>: tenent Danai qua deficit ignis <i>V</i> данайцы захватили (всё), что пощадил огонь; sol deficit <i>C</i> происходит солнечное затмение; progenies Caesarum in Nerone defecit <i>Su</i> род Цезарей угас с Нероном;\n<b>6)</b> убывать, убавляться, уменьшаться: luna deficiente <i>AG</i> когда луна на ущербе; dies deficit <i>Mela</i> день склоняется к закату; mare deficit <i>L</i> на море отлив; fontes aquae deficiunt <i>O</i> вода в источнике убавляется (высыхает) || подходить к концу, оканчиваться: deficiente die <i>Pt</i> на закате; in deficiente vero jam porticu <i>Pt</i> уже в самом конце колоннады;\n<b>7)</b> (<i>тж.</i> d. vita <i>Pl</i>) умирать, скончаться (d. prope lucem <i>Su</i>; deficit omne, quod nascitur <i>Q</i>): d. sanguine et spiritu <i>VM</i> умереть от потери крови;\n<b>8)</b> <i>юр.</i> быть <i>или</i> оказаться недействительным, утратить силу (deficit interdictum <i>Dig</i>);\n<b>9)</b> уходить, покидать (orbem <i>O</i>; somnus deficit aliquem <i>Tib</i>)."],
  ["defigo", "<big>defigo</big>\n\n<b>dē–fīgo, fīxī, fīxum, ere</b>\n<b>1)</b> втыкать, вбивать, вколачивать (tigna, trabes, asseres in terra <i>Cs</i>; hastas tellure <i>V</i>): d. crucem in campo Martio <i>C</i> водрузить крест на Марсовом поле;\n<b>2)</b> вонзать (sicam in corpore <i>C</i>; cultrum in corde <i>L</i>); пронзать, пригвождать: ingenuo ludo culpam d. <i>погов. Pers</i> добродушно и шутливо порицать недостатки;\n<b>3)</b> укоренять, укреплять (virtus altissimis radicibus defixa <i>C</i>); запечатлевать (d. aliquid in mente <i>C</i>): defixus in cogitatione <i>C</i> погружённый в размышления;\n<b>4)</b> устремлять, вперять (d. oculos in aliquem <i>O</i>): defixo in terram vultu tacere <i>VM</i> потупившись (понурив голову), молчать; mentem orationemque in aliqua re d. <i>C</i> посвятить чему-л. мысли и слова; omnes curas in salute rei publicae d. <i>C</i> посвятить все заботы благу республики;\n<b>5)</b> прикреплять (sidera defixa caelo <i>H</i>): cucurbitulas d. <i>CC</i> поставить банки;\n<b>6)</b> приковать к месту, привести в оцепенение, поразить (pavor omnium animos defixit <i>L</i>): per silentium defixus <i>T</i> застыв в безмолвии;\n<b>7)</b> <i>культ.</i> определить, незыблемо установить, объявить (quae augur injusta defixerit <i>C</i>);\n<b>8)</b> проклинать (d. nomina cera <i>O</i>) (<i>проклятие состояло в том, что булавкой прокалывалось написанное на воске имя предаваемого проклятию</i>)."],
  ["defindo", "<big>defindo</big>\n\n<b>dē–findo, —, —, ere</b>\nраскалывать <i>Enn</i>."],
  ["defingo", "<big>defingo</big>\n\n<b>dē–fingo, fīnxī, fictum, ere</b>\n<b>1)</b> придавать форму (d. panem <i>Cato</i>);\n<b>2)</b> <i>ирон.</i> (на свой лад) изображать, описывать (luteum caput Rheni <i>H</i>)."],
  ["definio", "<big>definio</big>\n\n<b>dē–fīnio, īvī (iī), ītum, īre</b>\n<b>1)</b> ограничивать, замыкать (loca, quae imperium populi Romani definiunt <i>C</i>): d. amicitiam paribus officiis <i>C</i> ограничить дружбу (взаимным) оказыванием равных услуг;\n<b>2)</b> определять (aliquam rem certo termino <i>rhH</i>): bona voluptate d. <i>C</i> определить блага через понятие (по признаку) наслаждения;\n<b>3)</b> устанавливать, назначать (suum cuique locum <i>Cs</i>): definitum est <i>Pl</i> решено;\n<b>4)</b> оканчивать: similiter definita <i>C</i> сходно заканчивающиеся (части фразы);\n<b>5)</b> <i>редко</i> приканчивать, убивать (aliquem <i>Ap</i>)."],
  ["definite", "<big>definite</big>\n\n<b>dēfīnītē</b> [definitus]\nопределённо, конкретно, ясно, точно: vel separatim dicere de aliqua re vel d. <i>C</i> говорить о чём-л. или вообще (абстрактно), или с определённостью (конкретно); lex Gellia et Cornelia, quae d. potestatem Pompejo civitatem donandi dedit <i>C</i> закон Геллия и Корнелия, который ясно (определённо) дал Помпею полномочия предоставлять права гражданства."],
  ["definitio", "<big>definitio</big>\n\n<b>dēfīnītio, ōnis</b> <i>f</i> [definio]\n<b>1)</b> определение, точное указание: hominum et temporum definitione sublata <i>C</i> без точного указания лиц и дат;\n<b>2)</b> <i>филос.</i> логическое определение, дефиниция <i>C, rhH etc.</i>;\n<b>3)</b> требование, предписание: d. judiciorum aequorum <i>C</i> требование (предъявляемое) к справедливым решениям (к правому суду)."],
  ["definitive", "<big>definitive</big>\n\n<b>dēfīnītīvē</b> <i>adv.</i> <i>CA, Tert</i>\n= definite."],
  ["definitivus", "<big>definitivus</big>\n\n<b>dēfīnītīvus, a, um</b> [definio]\n<b>1)</b> определяющий, определительный: constitutio definitiva <i>C</i> ясное обоснование;\n<b>2)</b> решающий, окончательный (sententia <i>CJ, Eccl</i>);\n<b>3)</b> определённый, конкретный (materia <i>Tert</i>)."],
  ["definitor", "<big>definitor</big>\n\n<b>dēfīnītor, ōris</b> <i>m</i>\n<b>1)</b> дающий определения, определяющий понятия <i>Aug</i>;\n<b>2)</b> дающий предписания, устанавливающий (d. unius baptismi <i>Tert</i>)."],
  ["definitus", "<big>definitus</big>\n\n<b>dēfīnītus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> definio;\n<b>2.</b> <i>adj.</i> определённый, конкретный, специальный (locus <i>C</i>): quaestionum duo sunt genera — alterum infinitum, alterum definitum <i>C</i> имеется два рода вопросов — одни общие, другие конкретные (частные)."],
  ["definxi", "<big>definxi</big>\n\n<b>dēfīnxī</b> <i>pf. к</i>\ndefingo."],
  ["defio", "<big>defio</big>\n\n<b>dēfīo, —, fierī</b> [<i>pass. к</i> deficio]\n(<i>употр. тк.</i> <i>inf.</i> <i>и 3-е л. обоих чисел</i>)\n<b>1)</b> исчезать, прекращаться (defit caloris congregatio <i>Sen</i>);\n<b>2)</b> недоставать, не хватать: nil quum est, nil defit tamen <i>Ter</i> хотя (у меня) ничего нет, но ни в чём и нужды нет."],
  ["defioculus", "<big>defioculus</big>\n\n<b>dēfīoculus, a, um</b> <i>M (v. l.</i>)\n= desioculus."],
  ["defit", "<big>defit</big>\n\n<b>dēfit</b> — <i>см.</i>\ndefio."],
  ["defixi", "<big>defixi</big>\n\n<b>dēfīxī</b> <i>pf. к</i>\ndefigo."],
  ["defixus", "<big>defixus</big>\n\n<b>dēfīxus, a, um</b> <i>part. pf. к</i>\ndefigo."],
  ["deflagratio", "<big>deflagratio</big>\n\n<b>dēflagrātio, ōnis</b> <i>f</i> [deflagro]\n<b>1)</b> сгорание дотла (alicujus rei <i>C</i>);\n<b>2)</b> всеобщий пожар, гибель (caeli atque terrarum <i>C</i>)."],
  ["deflagro", "<big>deflagro</big>\n\n<b>dē–flagro, āvī, ātum, āre</b>\n<b>1)</b> сгорать дотла, погибать в пламени (fore aliquando, ut omnis hic mundus ardore deflagret <i>C</i>); погореть, пострадать от пожара (communi incendio d. <i>C</i>): templum deflagravit manu alicujus <i>C</i> храм сгорел от чьей-то руки (кем-то подожжённый); deflagratus сгоревший (ictu fulminis <i>C</i>), <i>перен.</i> уничтоженный, погибший (imperium <i>C</i>);\n<b>2)</b> переставать гореть, угасать, <i>перен.</i> утихать, прекращаться (ira deflagrat <i>L, Lact</i>; deflagrante seditione <i>T</i>);\n<b>3)</b> сжигать, опалять (loca, quae sol deflagrat <i>Vtr</i>)."],
  ["deflammo", "<big>deflammo</big>\n\n<b>dē–flammo, —, —, āre</b>\nгасить, тушить (taedam <i>Ap</i>)."],
  ["deflecto", "<big>deflecto</big>\n\n<b>dē–flecto, flexī, flexum, ere</b>\n<b>1)</b> пригибать (ramum olivae <i>Col</i>);\n<b>2)</b> отводить (amnes in alium cursum <i>C</i>); отклонять (tela ab aliquo <i>V</i>); отвернуть, отвратить (lumina <i>O</i>; oculos ab aliquo <i>L</i>): d. novam viam <i>L</i> проложить новую дорогу в сторону; d. se de aliqua re <i>C</i> уклоняться от чего-л.; d. aliquem de via <i>C</i> совратить кого-л. с (правильного) пути; deflecti a cursu <i>Pt</i> отклониться от (своего) курса (<i>о корабле</i>); d. omnem acerbitatem in senatum <i>Su</i> перенести всю горечь на сенат; d. factum in aliquem <i>Q</i> свалить вину на кого-л.; ad verba rem d. <i>C</i> свести вопрос к словам (<i>оставив в стороне его сущность</i>); aliquid in suam utilitatem d. <i>Q</i> использовать что-л. в своих интересах;\n<b>3)</b> изменять, превращать (aliquid in melius <i>Sen</i>; virtutes in vitia <i>Su</i>);\n<b>4)</b> извращать (d. sententiam <i>C</i>);\n<b>5)</b> отступать (a pristina consuetudine <i>Ph</i>); удаляться: d. de via <i>C</i> свернуть с дороги;\n<b>6)</b> уклониться, сбиться (a veritate <i>C</i>)."],
  ["defleo", "<big>defleo</big>\n\n<b>dē–fleo, flēvī, flētum, flēre</b>\n<b>1)</b> оплакивать (aliquem ingenti gemitu <i>QC</i>; aliena mala <i>Q</i>; mors a multis defleta <i>C</i>); говорить в слезах, с горечью повествовать (d. civilia bella <i>Sen</i>);\n<b>2)</b> (<i>тж.</i> d. oculos <i>Ap</i>) плакать до изнеможения, обливаться слезами (Psyche in scopuli vertice deflens <i>Ap</i>)."],
  ["defletio", "<big>defletio</big>\n\n<b>dēflētio, ōnis</b> <i>f</i>\nплач, оплакивание <i>Jvc</i>."],
  ["deflevi", "<big>deflevi</big>\n\n<b>dēflēvī</b> <i>pf. к</i>\ndefleo."],
  ["deflexio", "<big>deflexio</big>\n\n<b>dēflexio, ōnis</b> <i>f</i> [deflecto]\nотклонение (meatus solis <i>Macr</i>)."],
  ["deflexus", "<big>deflexus</big>\n\n<b>I dēflexus, ūs</b> <i>m</i> [deflecto]\nотклонение (animi ab odio ad gratiam <i>VM</i>).\n\n<b>II dēflexus, a, um</b> <i>part. pf. к</i>\ndeflecto."],
  ["deflo", "<big>deflo</big>\n\n<b>dē–flo, āvī, ātum, āre</b>\n<b>1)</b> сдувать, обдувать <i>Vr, PM</i>;\n<b>2)</b> болтать <i>Ambr</i>."],
  ["deflocco", "<big>deflocco</big>\n\n<b>dē–flocco, —, ātum, āre</b> [floccus]\nделать лысым, плешивым (defloccati senes <i>Pl</i>)."],
  ["defloratio", "<big>defloratio</big>\n\n<b>dēflōrātio, ōnis</b> <i>f</i> [defloro]\n<b>1)</b> срывание цветов, <i>перен.</i> подбор всего лучшего (deflorationes librorum facere <i>Eccl</i>);\n<b>2)</b> (<i>тж.</i> d. virginitatis <i>Ambr</i>) дефлорация <i>Eccl</i>."],
  ["defloreo", "<big>defloreo</big>\n\n<b>dēflōreo, —, —, ēre</b>\n= defloresco."],
  ["defloresco", "<big>defloresco</big>\n\n<b>dē–flōrēsco, flōruī, —, ere</b>\n<b>1)</b> отцветать (papaver deflorescit <i>PM</i>);\n<b>2)</b> увядать, терять свежесть, блёкнуть (aut morbo aut vetustate <i>rhH</i>)."],
  ["defloro", "<big>defloro</big>\n\n<b>dē–flōro, āvī, ātum, āre</b>\n<b>1)</b> срывать цветы, <i>перен.</i> делать выбор наилучшего (ex operibus alicujus d. aliquid <i>Eccl</i>);\n<b>2)</b> помрачать, затуманивать (fulgores solis <i>Amm</i>);\n<b>3)</b> дефлорировать <i>Ambr</i>."],
  ["defluo", "<big>defluo</big>\n\n<b>dē–fluo, flūxī, (flūxum), ere</b>\n<b>1)</b> стекать вниз, свергаться (flumen defluit de monte <i>Sl</i>; defluit saxis umor <i>H</i>; sudor a fronte defluens <i>C</i>): Tanais amnis ex Ripaeis montibus defluit <i>PM</i> река Танаис стекает (берёт начало) с Рипейских гор;\n<b>2)</b> вести род, происходить (ab aliquo <i>Su</i>);\n<b>3)</b> растекаться: Rhenus in plures defluit partes <i>Cs</i> Рейн растекается (распадается) на много рукавов;\n<b>4)</b> ниспадать (vestis defluxit ad imos pedes <i>V</i>) <i>или</i> небрежно свисать (toga defluit <i>H</i>);\n<b>5)</b> спадать (ad <i>или</i> in terram <i>L, Q, C</i>): d. equo <i>или</i> ex equo <i>QC</i> свалиться (<i>редко</i> соскочить <i>V</i>) с лошади;\n<b>6)</b> плыть, плавать (d. secundo amni <i>V</i>; medio amni <i>L</i>; fluvio secundo <i>V</i>);\n<b>7)</b> отступать, удаляться, отхлынуть, оставить: adulescentes defluunt a Seneca <i>Q</i> юноши (молодёжь) удаляются от Сенеки (покидающего);\n<b>8)</b> постепенно переходить (d. a necessariis artificiis ad elegantiora <i>C</i>): hoc totum e sophistarum fontibus defluxit ad forum <i>C</i> всё это проникло на форум (<i>т. е.</i> в широкую публику) из софистических источников;\n<b>9)</b> доставаться, выпадать на долю (ad aliquem <i>C</i>; multa merces tibi defluat ab Jove <i>H</i>);\n<b>10)</b> схлынуть, иссякнуть, перестать течь (amnis defluit <i>H</i>);\n<b>11)</b> проходить, исчезать, пропадать (vires, tempus, ingenium defluxerunt <i>Sl</i>; odor defluit <i>PM</i>);\n<b>12)</b> выпадать, вылезать (comae defluxerunt <i>O</i>); опадать (flores defluunt de arboribus <i>Is</i>);\n<b>13)</b> схлынуть, кончаться (ubi salutatio defluxit, litteris me involvo <i>C</i>);\n<b>14)</b> устареть, выйти из употребления (numerus Saturnius defluxit <i>H</i>)."],
  ["defluus", "<big>defluus</big>\n\n<b>dēfluus, a, um</b> [defluo]\nльющийся вниз, проливающийся (splendor ab alto d. <i>St</i>); идущий вниз по течению (seu d., sive obliquus eat <i>St</i>); струящийся вниз, ниспадающий (caesaries <i>Eccl</i>); vasculum defluum <i>Ap</i> водяные часы."],
  ["defluvium", "<big>defluvium</big>\n\n<b>dēfluvium, ī</b> <i>n</i> [defluo]\n<b>1)</b> истечение <i>PM, Sol</i>;\n<b>2)</b> выпадение (capilli, pilorum <i>PM</i>)."],
  ["defluxi", "<big>defluxi</big>\n\n<b>dēflūxī</b> <i>pf. к</i>\ndefluo."],
  ["defluxio", "<big>defluxio</big>\n\n<b>dēflūxio, ōnis</b> <i>f</i> [defluo]\n<b>1)</b> истечение (sudoris <i>CA</i>);\n<b>2)</b> (<i>тж.</i> d. ventris <i>CA</i>) понос <i>CA</i>."],
  ["defluxus", "<big>defluxus</big>\n\n<b>dēflūxus, ūs</b> <i>m</i> [defluo]\nстекание (in terras <i>Ap</i>)."],
  ["defodio", "<big>defodio</big>\n\n<b>dē–fodio, fōdī, fossum, ere</b>\n<b>1)</b> закапывать, зарывать (signum in terram <i>L</i>; aurum in aliquo loco <i>PM</i>; thesaurum sub lecto <i>C</i>): animalia quae semper defossa vivunt <i>PM</i> животные, ведущие жизнь под землёй; d. se <i>Sen</i> зарываться (в одиночестве), скрываться;\n<b>2)</b> погребать, хоронить (cadaver <i>L</i>);\n<b>3)</b> вскапывать, взрывать (terram <i>H</i>);\n<b>4)</b> рыть, вырывать (scrobem <i>Col</i>; in defossis specubus otia agere <i>V</i>)."],
  ["defoenero", "<big>defoenero</big>\n\n<b>dēfoenero</b> <i>v. l.</i>\n= defenero."],
  ["defore", "<big>defore</big>\n\n<b>dēfore</b> <i>inf. fut. к</i>\ndesum."],
  ["deforis", "<big>deforis</big>\n\n<b>dē–foris</b> <i>adv.</i> (<i>тж. раздельно</i>)\nизвне, снаружи (includere aliquem <i>Vlg</i>)."],
  ["deformatio", "<big>deformatio</big>\n\n<b>I dēfōrmātio, ōnis</b> <i>f</i> [deformo I]\n<b>1)</b> обезображивание, искажение (corporis et coloris <i>C</i>; aedium <i>CTh</i>);\n<b>2)</b> унижение, умаление (tantae majestatis <i>L</i>).\n\n<b>II dēfōrmātio, ōnis</b> <i>f</i> [deformo II]\nформирование, придание формы, <i>тж.</i> обрисовывание, очерчивание <i>Vtr</i>."],
  ["deformia", "<big>deformia</big>\n\n<b>dēfōrmia, ium</b> <i>n</i> [deformis]\nпостыдные дела, гнусности (d. meditari <i>T</i>)."],
  ["deformis", "<big>deformis</big>\n\n<b>dē–fōrmis, e</b> [forma]\n<b>1)</b> искажённый, безобразный, обезображенный, уродливый (formosus an d. puer <i>C</i>; corpus deforme <i>SenT</i>): d. habitu <i>Just</i> с безобразной внешностью; deformia ora cicatricibus <i>QC</i> лицо, обезображенное шрамами;\n<b>2)</b> позорный, отвратительный (locus <i>Pt</i>); гнусный, постыдный (obsequium <i>T</i>; libido <i>Q</i>);\n<b>3)</b> бесформенный, не имеющий материальной формы, бесплотный (animae <i>O</i>)."],
  ["deformitas", "<big>deformitas</big>\n\n<b>dēfōrmitās, ātis</b> <i>f</i> [deformis]\n<b>1)</b> безобразие, уродливость (corporis <i>C, Sen etc.</i>; homo insigni deformitate <i>AG</i> <i>и</i> insignis ad deformitatem <i>C</i>);\n<b>2)</b> гнусность, позор (d. atque indignitas alicujus rei <i>rhH</i>)."],
  ["deformiter", "<big>deformiter</big>\n\n<b>dēfōrmiter</b> [deformis]\n<b>1)</b> безобразно, отвратительно (d. formatus <i>Ap</i>);\n<b>2)</b> позорно, постыдно, гнусно (vivere d. ac turpiter <i>Su</i>)."],
  ["deformo", "<big>deformo</big>\n\n<b>I dē–fōrmo, āvī, ātum, āre</b> [forma]\n<b>1)</b> обезобразить, сделать безобразным, изуродовать, исказить (macies deformat vultum <i>V</i>; aerumnis deformatus <i>Sl</i>): deformatus corpore <i>C</i> физически изуродованный;\n<b>2)</b> обесчестить, обесславить (genus et fortunam honestam <i>L</i>);\n<b>3)</b> обездолить, принизить (aliquem <i>C</i>);\n<b>4)</b> омрачить (victoriam clade <i>L</i>).\n\n<b>II dēfōrmo, āvī, ātum, āre</b>\n<b>1)</b> придавать вид, форму, образовывать, формировать: marmora deformata prima manu <i>Q</i> мрамор в первичной (скульптурной) обработке;\n<b>2)</b> изложить, описать: ille, quem supra deformavi <i>C</i> тот, которого я выше изобразил (представил)."],
  ["deformosus", "<big>deformosus</big>\n\n<b>dēfōrmōsus, a, um</b> <i>Sid</i>\n= deformis."],
  ["defossus", "<big>defossus</big>\n\n<b>I dēfossus, a, um</b> <i>part. pf. к</i>\ndefodio.\n\n<b>II dēfossus, (ūs)</b> <i>m</i> [defodio]\nрытьё глубокой ямы <i>PM</i>."],
  ["defractus", "<big>defractus</big>\n\n<b>dēfractus, a, um</b> <i>part. pf. к</i>\ndefringo."],
  ["defraudatio", "<big>defraudatio</big>\n\n<b>dēfraudātio, ōnis</b> <i>f</i> [defraudo]\nлишение, отнятие путём обмана <i>Tert</i>."],
  ["defraudator", "<big>defraudator</big>\n\n<b>dēfraudātor, ōris</b> <i>m</i>\nобманщик, мошенник <i>G</i>."],
  ["defraudatrix", "<big>defraudatrix</big>\n\n<b>dēfraudātrīx, īcis</b> <i>f</i>\nобманщица <i>Tert</i>."],
  ["defraudit", "<big>defraudit</big>\n\n<b>dēfraudit</b> <i>Pt</i>\n= defraudat (<i>к</i> defraudo)."],
  ["defraudo", "<big>defraudo</big>\n\n<b>dē–fraudo (dēfrūdo), āvī, —, āre</b>\nобманывать, обманным путём обобрать <i>Pl, Ter</i>: defraudasse aures <i>C</i> обмануть ожидания слушателей (<i>излишней краткостью</i>); d. aliquem aliqua re <i>Pl, C etc.</i> <i>и</i> aliquem aliquid <i>Pl, Vr, Vlg</i> обманом лишить кого-л. чего-л.; nihil sibi d. <i>Pt</i> ни в чём себе не отказывать."],
  ["defregi", "<big>defregi</big>\n\n<b>dēfrēgī</b> <i>pf. к</i>\ndefringo."],
  ["defremo", "<big>defremo</big>\n\n<b>dē–fremo, uī, —, ere</b>\nотшуметь, успокоиться (procella defremuit <i>Sid</i>)."],
  ["defrenatus", "<big>defrenatus</big>\n\n<b>dē–frēnātus, a, um</b> [freno]\nнеобузданный, бешеный (cursus <i>O</i>)."],
  ["defricate", "<big>defricate</big>\n\n<b>dēfricātē</b> [defrico]\nязвительно, насмешливо <i>Naev</i>."],
  ["defricatio", "<big>defricatio</big>\n\n<b>dē–fricātio, ōnis</b> <i>f</i>\nнатирание, втирание <i>CA</i>."],
  ["defrico", "<big>defrico</big>\n\n<b>dē–frico, fricuī, fricātum (frictum), āre</b>\n<b>1)</b> тереть, обтирать (amphoram <i>Cato</i>); натирать (aliquid pumice <i>PM</i>); чистить (dentes <i>O, Ctl</i>);\n<b>2)</b> пробирать, осмеивать, бичевать (aliquem sale multo <i>H</i>)."],
  ["defrigesco", "<big>defrigesco</big>\n\n<b>dē–frīgēsco, frīxī, —, ere</b>\nстановиться холодным, остывать <i>Col</i>."],
  ["defringo", "<big>defringo</big>\n\n<b>dē–fringo, frēgī, frāctum, ere</b> [frango]\n<b>1)</b> отломить, переломить (ramum arboris <i>C</i>; ferrum ab hasta <i>V</i>; articulum a crure <i>Ap</i>): amphora defracto collo <i>Cato</i> амфора с отбитым горлышком;\n<b>2)</b> наносить ущерб, повреждать (bonum, quod nunquam defringitur <i>Sen</i>): nihil ex aliqua re defregisse <i>Ap</i> ничем не умалить чего-л."],
  ["defritum", "<big>defritum</big>\n\n<b>dēfrītum</b> <i>Vr</i>\n(<i>v. l.</i>)= defrutum."],
  ["defrudo", "<big>defrudo</big>\n\n<b>dēfrūdo</b> <i>арх.</i>\n= defraudo."],
  ["defrugo", "<big>defrugo</big>\n\n<b>dē–frūgo, —, —, āre</b> [fruges]\nотнимать плоды (d. segetem <i>PM</i> — <i>v. l. к</i> defrudo)."],
  ["defruor", "<big>defruor</big>\n\n<b>dē–fruor, —, fruī</b> <i>depon.</i>\nполностью использовать (d. autumnitate <i>Symm</i>.)."],
  ["defrusto", "<big>defrusto</big>\n\n<b>dē–frūsto, —, —, āre</b> [frustum]\nразрывать на куски (tunica in pannulos defrustata <i>Amm</i>)."],
  ["defrustror", "<big>defrustror</big>\n\n<b>dē–frūstror, —, ārī</b> <i>depon.</i>\nобманывать, надувать (aliquem <i>Pl</i>)."],
  ["defrutarium", "<big>defrutarium</big>\n\n<b>dēfrutārium, ī</b> <i>n</i>\nсосуд для сусла <i>Col</i>."],
  ["defrutarius", "<big>defrutarius</big>\n\n<b>dēfrutārius, a, um</b> [defrutum]\nпредназначенный для сусла (vas <i>Col</i>)."],
  ["defruto", "<big>defruto</big>\n\n<b>dēfruto, —, —, āre</b> [defrutum]\nприготовлять из вина морс (vinum d. <i>Cato, Col</i>)."],
  ["defrutum", "<big>defrutum</big>\n\n<b>dēfrutum, ī</b> <i>n</i>\nотваренный морс, сусло <i>Pl, V, Vr</i>."],
  ["defudi", "<big>defudi</big>\n\n<b>dēfūdī</b> <i>pf. к</i>\ndefundo."],
  ["defuga", "<big>defuga</big>\n\n<b>dēfuga, ae</b> <i>f</i> [defugio]\nбеглец, дезертир <i>CTh, Eccl</i>."],
  ["defugio", "<big>defugio</big>\n\n<b>dē–fugio, fūgī, (fugitum), ere</b>\n<b>1)</b> убегать, бежать (totum sinistrum cornu defugit <i>L</i>);\n<b>2)</b> избегать, уклоняться, отвергать (d. sermonem alicujus, proelium <i>Cs</i>; disputationem <i>C</i>)."],
  ["defui", "<big>defui</big>\n\n<b>dēfuī</b> <i>pf. к</i>\ndesum."],
  ["defulguro", "<big>defulguro</big>\n\n<b>dē–fulguro, —, —, āre</b>\nметать словно молнии (ignem <i>Sid</i>)."],
  ["defunctio", "<big>defunctio</big>\n\n<b>dēfūnctio, ōnis</b> <i>f</i> [defungor]\n<b>1)</b> исполнение, свершение <i>Eccl</i>;\n<b>2)</b> кончина, смерть <i>Vlg, Eccl</i>."],
  ["defunctorie", "<big>defunctorie</big>\n\n<b>dēfūnctōriē</b> [defunctorius]\nвскользь, слегка, поверхностно (causam suam agere <i>Sen</i>)."],
  ["defunctorius", "<big>defunctorius</big>\n\n<b>dēfūnctōrius, a, um</b> [defungor]\nповерхностный, неосновательный, лёгкий (ictus <i>Pt</i>): apodixis defunctoria <i>Pt</i> свидетельство об инвалидности."],
  ["defunctus", "<big>defunctus</big>\n\n<b>I dēfūnctus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> defungor;\n<b>2.</b> <i>adj.</i> мёртвый, покойный, усопший (pater <i>Su</i>; corpus <i>QC, PM</i>).\n\n<b>II dēfūnctus, ī</b> <i>m</i> [defungor] (<i>тж.</i> terra d. <i>O</i>)\nмертвец, покойник <i>Q etc.</i>\n\n<b>III dēfūnctus, ūs</b> <i>m</i>\nкончина, смерть <i>Tert</i>."],
  ["defundo", "<big>defundo</big>\n\n<b>dē–fundo, fūdī, fūsum, ere</b>\n<b>1)</b> лить вниз, сливать, струить, переливать (vinum ex dolio in amphoram <i>H</i>); выливать (aquam in vas plumbeum <i>Col</i>);\n<b>2)</b> проливать (imbres <i>St</i>; defuso sanguine procubuit moriens <i>Sil</i>);\n<b>3)</b> сыпать (fruges pleno cornu <i>H</i>);\n<b>4)</b> <i>культ.</i> совершать возлияние (merum pateris <i>H</i>)."],
  ["defungor", "<big>defungor</big>\n\n<b>dē–fungor, fūnctus sum, fungī</b> <i>depon.</i>\n<b>1)</b> справиться, завершить, совершить, окончить, проделать (aliqua re): d. bello <i>L</i> закончить войну; d. vita <i>V, Sen, QC</i> окончить жизнь (умереть); defuncta (<i>n pl.</i>) suis temporibus <i>H</i> то, что закончило своё существование, <i>т. е.</i> старина, древность; defunctus honoribus <i>C</i> занимавший все высокие государственные посты;\n<b>2)</b> выполнить (d. imperio regis <i>L</i>; d. responso oraculi <i>Just</i>): d. tribus decumis pro una <i>C</i> уплатить три десятины вместо одной;\n<b>3)</b> отделаться, освободиться: d. levi poena <i>L</i> отделаться лёгким наказанием; se defunctum esse periculis arbitrari <i>C</i> считать себя вне опасностей; utinam hic sit modo defunctum! <i>Ter</i> только бы этим всё кончилось!;\n<b>4)</b> довольствоваться (parvo victu <i>QC</i>): defunctus jam sum <i>Ter</i> теперь я спокоен (в безопасности);\n<b>5)</b> (<i>тж.</i> vita defungi <i>V etc.</i>) умереть, почить (d. sua morte <i>Su</i>, egregia morte <i>QC</i>): defuncto Marcello <i>T</i> после смерти Марцелла."],
  ["defusio", "<big>defusio</big>\n\n<b>dēfūsio, ōnis</b> <i>f</i> [defundo]\nразлив (по бочкам), наполнение <i>Col</i>."],
  ["defusus", "<big>defusus</big>\n\n<b>dēfūsus, a, um</b> <i>part. pf. к</i>\ndefundo."],
  ["defututus", "<big>defututus</big>\n\n<b>dē–futūtus, a, um</b>\n= venere effetus (defatigatus) (puella <i>Ctl</i>)."],
  ["degemo", "<big>degemo</big>\n\n<b>dē–gemo, —, —, ere</b>\nстонать, оплакивать <i>PM</i> (<i>v. l.</i>)."],
  ["degener", "<big>degener</big>\n\n<b>dē–gener, eris</b> <i>adj.</i> [genus]\n<b>1)</b> выродившийся, испортившийся, переродившийся (d. sanguinis <i>St</i>; proles <i>T</i>; taurus <i>Sen</i>; herbae <i>M</i>): juvenis patrii non d. oris <i>O</i> юноша, достойный красноречия (унаследовавший красноречие) отца;\n<b>2)</b> ложный, ненастоящий, самозванный (rex <i>T</i>);\n<b>3)</b> низкий, низменный, неблагородный (animus <i>V</i>; preces <i>T</i>): dignitate formae haud d. <i>T</i> не без благородства в наружности; d. ad pericula <i>T</i> боящийся опасностей."],
  ["degenero", "<big>degenero</big>\n\n<b>dēgenero, āvī, ātum, āre</b> [degener]\n<b>1)</b> выродиться, переродиться, измениться, испортиться (poma, semina degenerant <i>V</i>): d. a civili more <i>Su</i> уйти от традиций общества (не считаться с общественными нравами); d. a parentibus <i>L</i> не походить на родителей; d. a virtute majorum <i>C</i> не иметь доблести предков;\n<b>2)</b> перерождаться, превращаться, переходить (degenerat hordeum in avenam <i>PM</i>): costae sex inferiores in cartilaginem degenerant <i>CC</i> шесть нижних рёбер переходят в хрящи;\n<b>3)</b> (за)пятнать, унижать, опорочивать (aliquem <i>Prp, O</i>; tantum honorem <i>St</i>)."],
  ["degero", "<big>degero</big>\n\n<b>dē–gero, gessī, (gestum), ere</b>\nуносить, вывозить (foras aliquid <i>Pl</i>; ad aliquem <i>или</i> alicui <i>Pl</i>)."],
  ["degi", "<big>degi</big>\n\n<b>dēgī</b> <i>pf. к</i>\ndego."],
  ["deglabro", "<big>deglabro</big>\n\n<b>dē–glabro, āvī, ātum, āre</b>\n<b>1)</b> снимать кору, обдирать (arbores <i>Dig</i>);\n<b>2)</b> выстригать, делать гладким (corpus <i>Lact</i>)."],
  ["deglubo", "<big>deglubo</big>\n\n<b>dē–glūbo, glūpsī, glūptum, ere</b> (<i>Tert</i> are)\n<b>1)</b> очищать от шелухи, кожуры, скорлупы (granum <i>Vr</i>); сдирать кожу, обдирать (d. aliquem vivum <i>Vr</i>; deglupta maena <i>Pl</i>): boni pastoris est tondere pecus, non d. <i>погов. Tiberius ap. Su</i> хороший пастух стрижёт овец, а не сдирает с них шкуру;\n<b>2)</b> (<i>sensu obsceno</i>) masturbare <i>Aus</i>."],
  ["deglutino", "<big>deglutino</big>\n\n<b>dē–glūtino, —, —, āre</b>\nрасклеить, раскрыть (palpebras <i>PM</i>)."],
  ["degluttio", "<big>degluttio</big>\n\n<b>dē–gluttio (dēglūtio), īvī, —, īre</b>\n<b>1)</b> проглатывать, поглощать (aliquid <i>и</i> aliquem <i>Vlg, Eccl</i>);\n<b>2)</b> терпеливо сносить (aliquid <i>Boët, Vlg</i>)."],
  ["dego", "<big>dego</big>\n\n<b>dēgo, dēgī, —, ere</b> [de + ago]\n<b>1)</b> прожить (in tranquillitate omnem aetatem <i>C</i>); проводить (otia in thalamo <i>Ctl</i>; omne tempus aetatis sine molestia <i>Sen</i>); влачить (senectutem turpem <i>Sen</i>);\n<b>2)</b> жить, обитать: animalia, quae nobiscum degunt <i>PM</i> наши домашние животные; aves, quae in aqua degunt <i>CC</i> водяные птицы."],
  ["degradatio", "<big>degradatio</big>\n\n<b>dēgradātio, ōnis</b> <i>f</i> [degrado]\nдеградация, понижение <i>Eccl</i>."],
  ["degrado", "<big>degrado</big>\n\n<b>dē–grado, —, —, āre</b> [gradus]\nпонижать, снижать в ранге <i>CJ, Eccl</i>."],
  ["degrandinat", "<big>degrandinat</big>\n\n<b>dē–grandinat</b> <i>impers.</i>\nград утихает, перестаёт падать <i>O</i>."],
  ["degrassor", "<big>degrassor</big>\n\n<b>dē–grassor, ātus sum, ārī</b> <i>depon.</i>\n<b>1)</b> низвергаться (deorsum degrassantur aquae <i>Ap</i>);\n<b>2)</b> бранить, нападать (d. aliquem <i>St</i>)."],
  ["degravo", "<big>degravo</big>\n\n<b>dē–gravo, āvī, ātum, āre</b>\n<b>1)</b> тяготить, отягощать, придавливать, пригибать к земле (aliquam rem pondere suo <i>Col</i>);\n<b>2)</b> обременять, мешать, одолевать, мучить (vulnus degravat aliquem <i>L</i>)."],
  ["degredior", "<big>degredior</big>\n\n<b>dē–gredior, gressus sum, edī</b> <i>depon.</i> [gradior]\n<b>1)</b> сходить, спускаться (monte <i>Sl</i> <i>и</i> de montibus <i>L</i>; ex arce <i>L</i> <i>и</i> ab arce <i>Fl</i>; in campum <i>L</i>); ad pedes d. <i>L</i> слезть с лошадей, спешиться;\n<b>2)</b> уходить, уезжать (via <i>PM</i>; in urbem <i>T</i>)."],
  ["degressio", "<big>degressio</big>\n\n<b>dē–gressio</b> <i>v. l.</i>\n= digressio."],
  ["degrumo", "<big>degrumo</big>\n\n<b>dē–grūmo, —, —, āre</b> [de + grumus]\nвыравнивать (forum <i>Enn</i>; viam <i>LM</i>)."],
  ["degrunnio", "<big>degrunnio</big>\n\n<b>dē–grunnio, —, —, īre</b>\nпохрюкать <i>Ph</i>."],
  ["degulator", "<big>degulator</big>\n\n<b>dē–gulātor, ōris</b> <i>m</i> [gula]\nкутила, обжора, расточитель <i>Ap</i>."],
  ["degustatio", "<big>degustatio</big>\n\n<b>dēgustātio, ōnis</b> <i>f</i> [degusto]\nотведывание, проба (aceti, vini <i>Dig</i>; carnis <i>Tert</i>)."],
  ["degusto", "<big>degusto</big>\n\n<b>dē–gusto, āvī, ātum, āre</b>\n<b>1)</b> отведывать, пробовать (vinum <i>Cato, Dig</i>; carnem, novas fruges <i>PM</i>);\n<b>2)</b> ознакомиться, узнавать (litteras <i>Q</i>): ex ea oratione licet pauca degustes <i>C</i> позволь ознакомить тебя немножко с этой речью;\n<b>3)</b> изведать (eandem fortunam <i>C</i>);\n<b>4)</b> слегка касаться, оцарапать (lancea degustat summum corpus <i>V</i>);\n<b>5)</b> затронуть, задеть (d. rem, materiam <i>Q</i>)."],
  ["dehabeo", "<big>dehabeo</big>\n\n<b>de–habeo, —, —, ēre</b>\nощущать недостаток, не иметь (aliquid <i>Eccl</i>)."],
  ["dehaurio", "<big>dehaurio</big>\n\n<b>de–haurio, hausī, haustum, īre</b>\n<b>1)</b> вычерпывать, снимать (amurcam <i>Cato</i>);\n<b>2)</b> проглатывать (carnem <i>Tert</i>)."],
  ["dehibeo", "<big>dehibeo</big>\n\n<b>de–hibeo, hibuī, —, ēre</b> [habeo] (<i>нестяжённый вариант к</i> debeo)\nбыть должным (mille drachumas <i>Pl</i>)."],
  ["dehinc", "<big>dehinc</big>\n\n<b>dehinc</b> <i>adv.</i> (<i>поэт.</i> <i>иногда односложно</i> dejnc)\n<b>1)</b> <i>пространство</i>: отсюда, далее: Aegyptus, d. initio ab Syria usque ad flumen Euphraten <i>T</i> Египет и, далее, от Сирии вплоть до реки Евфрата;\n<b>2)</b> <i>время</i>: отныне, впредь; с тех пор, с (э)того времени (nemo est, quem jam d. metuam <i>Pt</i>);\n<b>3)</b> тогда, потом, после того: d. talia fatur <i>V</i> потом он говорит следующее || в дальнейшем, впоследствии: mors, de qua d. dicam <i>Su</i> кончина (Августа), о которой я скажу ниже;\n<b>4)</b> <i>следствие</i>: а потому, следовательно: d. postulo, ut... <i>Ter</i> поэтому я требую, чтобы...;\n<b>5)</b> <i>при перечислении</i> deinde: primum (<i>или</i> primo)... d. <i>Sl, V etc.</i> сначала (сперва)... затем."],
  ["dehisco", "<big>dehisco</big>\n\n<b>de–hisco, —, —, ere</b>\nраскрываться (rosa dehiscit <i>PM</i>); разверзаться, расступаться (unda dehiscens <i>V</i>); трескаться, раскалываться (rimis dehiscit cymba <i>O</i>); расседаться: terra (tellus) dehiscat mihi! <i>V</i> пусть разверзнется подо мной земля! (провалиться мне!); dehiscens intervallis acies <i>L</i> боевой порядок, разделённый интервалами."],
  ["dehisse", "<big>dehisse</big>\n\n<b>dehisse</b> <i>Vr inf. pf. к</i>\ndehisco."],
  ["dehonestamentum", "<big>dehonestamentum</big>\n\n<b>dehonestāmentum, ī</b> <i>n</i> [dehonesto]\nпозор, бесчестие, бесславие: d. corporis <i>Sl</i> физический позор (<i>о ранах на спине</i>); d. oris <i>T</i> отсутствие одного глаза; amicitiarum dehonestamenta <i>T</i> позорящие связи (дружба)."],
  ["dehonestatio", "<big>dehonestatio</big>\n\n<b>dehonestātio, ōnis</b> <i>f</i>\nбесславие, бесчестие, позор <i>Tert</i>."],
  ["dehonesto", "<big>dehonesto</big>\n\n<b>de–honesto, āvī, ātum, āre</b>\nпокрывать позором, порочить, (за)пятнать, (о)бесславить (famam d. <i>L</i>): dehoriestari aliqua re <i>Just, T</i> опозорить себя чём-л."],
  ["dehonestus", "<big>dehonestus</big>\n\n<b>de–honestus, a, um</b>\nнедостойный, непристойный (verbum <i>AG</i>)."],
  ["dehorio", "<big>dehorio</big>\n\n<b>dehorio</b> <i>Cato</i>\n= dehaurio."],
  ["dehortatio", "<big>dehortatio</big>\n\n<b>de–hortātio, ōnis</b> <i>f</i>\nотговаривание <i>Tert</i>."],
  ["dehortator", "<big>dehortator</big>\n\n<b>de–hortātor, ōris</b> <i>m</i>\nотговаривающий, разубеждающий <i>Aug</i>."],
  ["dehortatorius", "<big>dehortatorius</big>\n\n<b>de–hortātōrius, a, um</b>\nотговаривающий, отклоняющий (daemonium dehortatorium a bono <i>Tert</i>)."],
  ["dehortor", "<big>dehortor</big>\n\n<b>de–hortor, ātus sum, ārī</b> <i>depon.</i>\nразубеждать, отсоветовать, отговаривать (aliquem <i>Pl, C</i>; aliquid facere <i>Sl, T</i>): d. aliquem ab aliquo <i>Sl</i> пытаться увести (удалить) кого-л. от кого-л. (разлучить кого-л. с кем-либо)."],
  ["Deianira", "<big>Deianira</big>\n\n<b>Dēianīra, ae</b> <i>f</i>\nДеианира, <i>дочь этолийского царя Энея (Oeneus), сестра Мелеагра, последняя жена Геркулеса</i> <i>C, O</i>."],
  ["deicio", "<big>deicio</big>\n\n<b>dēicio</b> <i>v. l.</i>\n= dejicio."],
  ["deicola", "<big>deicola</big>\n\n<b>dei–cola, ae</b> <i>f</i> [colo]\nбогопоклонник <i>Hier</i>."],
  ["Deidamia", "<big>Deidamia</big>\n\n<b>Dēidamīa, ae</b> <i>f</i>\nДеидамия, <i>дочь Ликомеда, царя Скира, и мать Пирра</i> (<i>сына Ахилла</i>) <i>Prp, O</i>."],
  ["deifico", "<big>deifico</big>\n\n<b>dei–fico, —, —, āre</b> [deus + facio]\nобожествлять <i>Aug</i>."],
  ["dein", "<big>dein</big>\n\n<b>dein</b> <i>C etc.</i>\n= deinde."],
  ["deinceps", "<big>deinceps</big>\n\n<b>I deinceps</b> (<i>поэт.</i> <i>иногда двусложно</i>, <i>т. е.</i> dejnceps) <i>adv.</i> [dein + capio]\n<b>1)</b> <i>пространство</i>: подряд, друг за другом, один за другим (tres d. turres <i>L</i>): omnis d. ager <i>L</i> вся область сплошь;\n<b>2)</b> <i>время</i>: сряду (reliqui d. dies <i>Cs</i>): d. aliquot diebus <i>L</i> в течение ряда следующих дней;\n<b>3)</b> <i>при перечислении</i>: et d. usque ad Romulum <i>C</i> и так далее вплоть до Ромула || по порядку (septimus sum d. <i>Vr</i>); далее, затем: perge d. <i>Vr</i> продолжай; principes sint patria et parentes, proximi liberi totaque domus, d. propinqui <i>C</i> на первом месте должны быть родина и родители, потом дети и вся семья, а затем (остальные) родственники.\n\n<b>II deinceps, cipitis</b> <i>adj.</i>\n(по)следующий: deincipiti (<i>v. l.</i> deincipite) die <i>Ap</i> на следующий день."],
  ["deinde", "<big>deinde</big>\n\n<b>deinde</b> (<i>часто, а в поэзии всегда ei</i> — <i>дифтонг</i>) [<i>из</i> de + inde]\n<b>1)</b> <i>пространство</i>: отсюда, далее: via perangusta, d. paulo latior patescit campus <i>L</i> дорога крайне узкая, далее местность несколько расширяется;\n<b>2)</b> <i>время</i>: потом, вслед за тем, после того: plebs Montem Sacrum prius, d. Aventinum occupavit <i>C</i> плебеи заняли сначала Священную гору, а потом Авентинский холм;\n<b>3)</b> даже, наконец : veniam dedimus precantibus, pacem cum victis fecimus, tutelae d. nostrae duximus <i>L</i> мы дали им пощаду, о которой они просили, заключили с ними, побеждёнными, мир и даже приняли их под своё покровительство;\n<b>4)</b> <i>при перечислении</i>: потом, затем, далее (primum... d... item... postremo <i>Cs, C etc.</i>); <i>часто в сочетаниях</i>: d. tum (tunc) <i>Vr, L, Sen</i>; d. post (postea, posthaec) <i>Ter, Sen, C etc.</i>; d. porro <i>Pl</i>; d. rursus <i>Lact</i>; d. postremo, d. ad extremum <i>C etc.</i>"],
  ["deintus", "<big>deintus</big>\n\n<b>de–intus</b> <i>adv.</i>\nизнутри <i>Veg, Vlg</i>."],
  ["Deionides", "<big>Deionides</big>\n\n<b>Dēionidēs, ae</b> <i>f</i>\nДеионид, <i>сын Деионы и Аполлона, т. е.</i> Милет <i>O</i>."],
  ["Deiopea", "<big>Deiopea</big>\n\n<b>Dēiopēa, ae</b> <i>f</i>\nДеиопея, <i>одна из нимф Юноны</i> <i>V</i>."],
  ["deipara", "<big>deipara</big>\n\n<b>dei–para, ae</b> <i>f</i> [deus + pario]\nбогородица <i>CJ</i>."],
  ["Deiphobe", "<big>Deiphobe</big>\n\n<b>Dēiphobē, ēs</b> <i>f</i>\nДеифоба, <i>кумская сивилла, дочь вещего морского бога Главка</i> <i>V</i>."],
  ["Deiphobus", "<big>Deiphobus</big>\n\n<b>Dēiphobus, ī</b> <i>m</i>\nДеифоб, <i>сын Приама и Гекубы, один из троянских героев</i> <i>V, O</i>."],
  ["deitas", "<big>deitas</big>\n\n<b>deitās, ātis</b> <i>f</i> <i>Aug etc.</i>\n= divinitas."],
  ["dejeci", "<big>dejeci</big>\n\n<b>dējēcī</b> <i>pf. к</i>\ndejicio."],
  ["dejecte", "<big>dejecte</big>\n\n<b>dējectē</b> <i>adv.</i> [dejectus]\nсмиренно (<i>тк.</i> <i>compar.</i>) <i>Tert</i>."],
  ["dejectio", "<big>dejectio</big>\n\n<b>dējectio, ōnis</b> <i>f</i> [dejicio]\n<b>1)</b> сбрасывание, низвержение (imaginum <i>Eccl</i>);\n<b>2)</b> <i>юр.</i> изгнание, вытеснение, экспроприация <i>C, Dig</i>;\n<b>3)</b> <i>мед.</i> (<i>тж.</i> d. alvi <i>CC</i>) стул <i>CC, Sen</i>;\n<b>4)</b> снижение, понижение (gradus <i>Dig</i>);\n<b>5)</b> подавленность: d. animi <i>Sen</i> робость, страх, малодушие."],
  ["dejectiuncula", "<big>dejectiuncula</big>\n\n<b>dējectiuncula, ae</b> <i>f</i> [<i>demin. к</i> dejectio 3] <i>мед.</i>\nнебольшой стул <i>Scr</i>."],
  ["dejecto", "<big>dejecto</big>\n\n<b>dējecto, —, —, āre</b> [<i>intens. к</i> dejicio]\nсбрасывать, бросать вниз <i>AG</i> (<i>v. l.</i>)."],
  ["dejector", "<big>dejector</big>\n\n<b>dējector, ōris</b> <i>m</i>\nсбрасывающий, выбрасывающий <i>Dig</i>."],
  ["dejectus", "<big>dejectus</big>\n\n<b>I dējectus, ūs</b> <i>m</i>\n<b>1)</b> сбрасывание, сваливание (arborum <i>L</i>);\n<b>2)</b> падение вниз, низвержение (aquae <i>L, Sen</i>);\n<b>3)</b> стремительность: in iram d. animorum <i>Sen</i> вспыльчивость, гневливость;\n<b>4)</b> набрасывание: velatus geminae dejectu lyncis <i>St</i> (конь) в попоне из двух рысьих шкур;\n<b>5)</b> склон, крутизна, обрыв, стремнина (d. collis <i>Cs</i>);\n<b>6)</b> грань (gemmae <i>PM</i>).\n\n<b>II dējectus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> dejicio;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> низменный (loca <i>Cs</i>);\n<b>2)</b> подавленный, растерявшийся, унылый, <i>тж.</i> малодушный <i>V, Q, St</i>;\n<b>3)</b> незначительный (по положению) <i>Tert</i>."],
  ["dejeratio", "<big>dejeratio</big>\n\n<b>dējerātio, ōnis</b> <i>f</i> [dejero]\nпринесение присяги, клятва <i>Tert</i>."],
  ["dejero", "<big>dejero</big>\n\n<b>dējero (dējūro), āvī, ātum, āre</b>\n<b>1)</b> приносить торжественную клятву, клясться (per Herculem <i>AG</i>; persancte <i>Ter</i>);\n<b>2)</b> освящать торжественной клятвой (doctrinae dejeratae <i>Tert</i>)."],
  ["dejeror", "<big>dejeror</big>\n\n<b>dējeror, —, ārī</b> <i>Ap</i>\n= dejero."],
  ["dejicio", "<big>dejicio</big>\n\n<b>dē–jicio, jēcī, jectum, ere</b> [jacio]\n<b>1)</b> сбрасывать, сваливать, низвергать (aliquem de ponte in Tiberim <i>C</i>; de saxo <i>L</i> <i>и</i> saxo <i>T</i>; d. aliquem equo <i>V</i>); валить, опрокидывать (arbores vi ventorum dejectae <i>Dig</i>); разрушать, рушить (d. statuas <i>C</i>; muros <i>L</i>; monumenta templaque <i>H</i>); обрушить (elatam securim in caput alicujus <i>L</i>): d. lacrimas <i>Prp</i> проливать слёзы; d. crines <i>T</i> в беспорядке распустить волосы; d. se in mare <i>Nep</i> броситься в море; aqua se dejecit <i>PM</i> вода хлынула;\n<b>2)</b> пускать (equum in viam <i>L</i>);\n<b>3)</b> срывать (libellos <i>C</i>);\n<b>4)</b> спускать (togam de umero <i>или</i> ab umeris <i>Su</i>);\n<b>5)</b> срубать (arbores <i>L</i>); отрубать, сносить (d. caput ictu ensis <i>V</i>);\n<b>6)</b> бросать (sortem <i>L, Cs, V</i>); отбрасывать, удалять (quantum mali de humana condicione <i>C</i>);\n<b>7)</b> очищать (alvum <i>Cato, CC</i>): d. alvum superiorem <i>Cato</i> извергать с рвотой;\n<b>8)</b> отнести, прибить (navem ad inferiorem partem insulae <i>Cs</i>);\n<b>9)</b> опровергать (omnia negotia <i>Sen</i>); отвергать, отклонять (senatus consulta <i>Spart</i>);\n<b>10)</b> опускать, потупить (vultum <i>V, Sen</i>; oculos <i>Q etc.</i>): dejectus oculos (vultum) <i>V, St</i> опустив глаза; dejecto capite <i>Q, Ap</i> понурив (повесив) голову;\n<b>11)</b> уложить, убить (paucis dejectis <i>Cs</i>; d. feras <i>QC</i>);\n<b>12)</b> сбить (avem ab alto caelo <i>V</i>);\n<b>13)</b> свергнуть (jugum servile a cervicibus <i>C</i>); согнать, прогнать, отбросить (d. hostes muro <i>Cs</i>): dejectae vertice saxi caprae <i>V</i> спугнутые с вершины скалы козы || выбить (praesidium loco munito <i>H</i>; exercitum ex castellis <i>L</i>); вытеснить (d. aliquem de agro communi <i>C</i>; de possessione fundi <i>Dig</i>): d. de gradu (statu) <i>C</i> привести в замешательство, выбить из колеи;\n<b>14)</b> отвергнуть домогательства, лишить надежды (на что-л.) (d. aliquem aedilitate, praetura <i>C</i>; honore <i>L</i> <i>или</i> de honore <i>C</i>): d. aliquem <i>L, C</i> отклонить чью-л. кандидатуру; dejectus ea spe <i>Cs</i> лишившись этой надежды; dejectus opinione trium legionum <i>Cs</i> лишённый надежды на получение трёх легионов; dejecta conjuge tanto <i>V</i> лишившаяся такого супруга;\n<b>15)</b> отвращать, удалять (d. multum mali de humana condicione <i>C</i>); отводить (d. oculos ab <i>или</i> de aliquo): oculos nusquam d. de aliqua re <i>C</i> не сводить глаз с чего-л.; si tantulum oculos dejecerimus <i>C</i> если мы немного отведём взоры, <i>т. е.</i> стоит нам хоть на минуту утратить бдительность;\n<b>16)</b> отклонить, отговорить, разубедить (d. aliquem de sententia <i>C</i>)."],
  ["Dejotarus", "<big>Dejotarus</big>\n\n<b>Dējotarus, ī</b> <i>m</i>\nДейотар, <i>тетрарх Галатии и царь Малой Армении, сторонник Помпея и противник Цезаря</i> <i>C, T</i>."],
  ["dejugis", "<big>dejugis</big>\n\n<b>dē–jugis, e</b> [jugum]\nнаклонный, отлогий (properare dejuge dorso <i>Aus</i>)."],
  ["dejugo", "<big>dejugo</big>\n\n<b>dē–jugo, —, —, āre</b> [jugum]\nотделять, удалять (aliquid <i>Pac</i>)."],
  ["dejungo", "<big>dejungo</big>\n\n<b>dē–jungo, (jūnxī), jūnctum, ere</b>\nраспрягать, выпрягать, <i>перен.</i> освобождать: se d. ab aliqua re <i>T</i> отделаться (освободиться) от чего-л."],
  ["dejuratio", "<big>dejuratio</big>\n\n<b>dējūrātio</b> <i>v. l.</i>\n= dejeratio."],
  ["dejurium", "<big>dejurium</big>\n\n<b>dējūrium, ī</b> <i>n</i>\nклятва, присяга <i>AG</i>."],
  ["dejuro", "<big>dejuro</big>\n\n<b>dējūro</b> <i>v. l.</i>\n= dejero."],
  ["dejuvo", "<big>dejuvo</big>\n\n<b>dē–juvo, —, —, āre</b>\nне оказывать больше помощи, не помогать (d. aliquem in rebus adversis <i>Pl</i>)."],
  ["delabor", "<big>delabor</big>\n\n<b>dē–lābor, lāpsus sum, lābī</b> <i>depon.</i>\n<b>1)</b> спадать, ниспадать, соскальзывать, падать (de caelo <i>и</i> e caelo <i>C</i>; in flumen <i>C</i>): sera delapsa cecidit <i>Pt</i> соскользнувший засов упал || сваливаться (ex equo <i>L</i>); выпадать (arma de manibus delabuntur <i>C</i>);\n<b>2)</b> спускаться, сойти, слететь (caelo <i>V, L</i> <i>и</i> e caelo <i>Lact</i>; ab astris <i>V</i>; ab aethere <i>O</i>);\n<b>3)</b> стекать (ex utraque parte tecti <i>C</i>; flumina ab Alpibus delapsa <i>Mela</i>); втекать, впадать (in mare <i>H</i>);\n<b>4)</b> брать начало, происходить (ab aliqua re <i>C</i>);\n<b>5)</b> переходить, нисходить (a majoribus ad minora <i>C</i>; in istum sermonem <i>C</i>): d. in morbum <i>C</i> заболеть;\n<b>6)</b> склоняться (ad aequitatem <i>C</i>);\n<b>7)</b> попадать, оказаться, очутиться (medios in hostes <i>V</i>);\n<b>8)</b> впадать (in vitium <i>T</i>): d. ad praecipiendi rationem <i>C</i> впасть в назидательный (менторский) тон; in somnum delapsus est <i>Pt</i> он заснул."],
  ["delacero", "<big>delacero</big>\n\n<b>dē–lacero, āvī, —, āre</b>\nразрывать, растерзать, <i>перен.</i> разрушать, расстраивать (opes <i>Pl</i>)."],
  ["delacrimatio", "<big>delacrimatio</big>\n\n<b>dēlacrimātio, ōnis</b> <i>f</i> [delacrimo]\n<b>1)</b> слезоточивость, слезотечение (<i>болезнь</i>) <i>PM</i>;\n<b>2)</b> прекращение плача <i>Scr</i>."],
  ["delacrimo", "<big>delacrimo</big>\n\n<b>dē–lacrimo, —, —, āre</b>\nлить слёзы, плакать, <i>перен.</i> (<i>о растениях</i>) пускать сок, сочиться <i>Col</i>."],
  ["delacrumo", "<big>delacrumo</big>\n\n<b>dēlacrumo</b> <i>арх.</i>\n= delacrimo."],
  ["delaevo", "<big>delaevo</big>\n\n<b>dēlaevo</b> <i>v. l.</i>\n= delevo."],
  ["delambo", "<big>delambo</big>\n\n<b>dē–lambo, —, —, ere</b>\nоблизывать, вылизывать (vellera lingua <i>St</i>)."],
  ["delamentor", "<big>delamentor</big>\n\n<b>dē–lāmentor, —, ārī</b> <i>depon.</i>\nжаловаться (на что-л.), оплакивать (natam ademptam <i>O</i>)."],
  ["delapido", "<big>delapido</big>\n\n<b>dē–lapido, —, —, āre</b>\nочищать от камней (locum <i>Cato</i>)."],
  ["delapsus", "<big>delapsus</big>\n\n<b>I dēlāpsus, ūs</b> <i>m</i> [delabor]\nпадение вниз, стекание (d. aquae <i>Vr</i>).\n\n<b>II dēlāpsus, a, um</b> <i>part. pf. к</i>\ndelabor."],
  ["delassabilis", "<big>delassabilis</big>\n\n<b>dēlassābilis, e</b> [delasso]\nутомляющийся: non d. неутомимый (pectus <i>Man</i>)."],
  ["delasso", "<big>delasso</big>\n\n<b>dē–lasso, —, ātum, āre</b>\n<b>1)</b> утомлять, изнурять (aliquem <i>Pl, H</i>);\n<b>2)</b> <i>перен.</i> <i>и</i> <i>ирон.</i> беспощадно использовать (omnes fabulas poetarum <i>M</i>)."],
  ["delatio", "<big>delatio</big>\n\n<b>dēlātio, ōnis</b> <i>f</i> [defero]\n<b>1)</b> донесение (exploratorum <i>Amm</i>);\n<b>2)</b> <i>юр.</i> обвинительное сообщение, обвинение: delationem (<i>или</i> delationis partes <i>T</i>) dare alicui <i>C</i> предоставить кому-л. роль обвинителя; delationem nominis postulare in aliquem <i>C</i> просить разрешения привлечь кого-л. к судебной ответственности;\n<b>3)</b> (<i>в эпоху империи</i>) донос <i>T, PJ</i>."],
  ["delator", "<big>delator</big>\n\n<b>dēlātor, ōris</b> <i>m</i> [defero]\n<b>1)</b> обвинитель (criminis delatores <i>L</i>): d. legis Papiae <i>Su</i> обвиняющий (кого-л.) в нарушении закона Папия;\n<b>2)</b> доносчик (d. majestatis <i>T</i>)."],
  ["delatorius", "<big>delatorius</big>\n\n<b>dēlātōrius, a, um</b> [delator]\nдоносительский, наушнический (curiositas <i>Dig</i>; libelli <i>CTh</i>)."],
  ["delatro", "<big>delatro</big>\n\n<b>dē–lātro, āvī, —, āre</b>\nговорить в жалобно-визгливом тоне <i>Boët</i>."],
  ["delatura", "<big>delatura</big>\n\n<b>dēlātūra, ae</b> <i>f</i> [defero]\n<b>1)</b> обвинение <i>Tert</i>;\n<b>2)</b> клевета, наушничество <i>Vlg</i>."],
  ["delatus", "<big>delatus</big>\n\n<b>dēlātus, a, um</b> <i>part. pf. к</i>\ndefero."],
  ["delebilis", "<big>delebilis</big>\n\n<b>dēlēbilis, e</b> [deleo]\nподверженный разрушению (уничтожению), тленный (opus nullis delebile annis <i>M</i>)."],
  ["delectabilis", "<big>delectabilis</big>\n\n<b>dēlectābilis, e</b> [delecto]\n<b>1)</b> услаждающий, увеселительный (apologus <i>AG</i>);\n<b>2)</b> приятный (tibia questu delectabilior <i>Ap</i>); тонкий на вкус (cibus <i>T</i>)."],
  ["delectabiliter", "<big>delectabiliter</big>\n\n<b>dēlectābiliter</b> <i>adv.</i>\nприятно, увлекательно (d. et decore <i>AG</i>)."],
  ["delectamentum", "<big>delectamentum</big>\n\n<b>dēlectāmentum, ī</b> <i>n</i> [delecto]\nзабава, утеха, веселье (puerorum <i>C</i>); развлечение, времяпрепровождение (aliquem pro delectamento putare <i>Ter</i>)."],
  ["delectatio", "<big>delectatio</big>\n\n<b>dēlectātio, ōnis</b> <i>f</i> [delecto]\n<b>1)</b> увеселение, забава, удовольствие, наслаждение (audiendi <i>C</i>; conviviorum <i>C</i>): lectio sine ulla delectatione <i>C</i> крайне скучное чтение; delectationem habere <i>C</i> доставлять удовольствие, радовать, веселить;\n<b>2)</b> <i>мед.</i> позыв (urinae reddendae <i>CA</i>; ventris egerendi <i>CA</i>)."],
  ["delectatiuncula", "<big>delectatiuncula</big>\n\n<b>dēlectātiuncula, ae</b> <i>f</i> [<i>demin. к</i> delectatio]\nразвлеченьице, некоторое удовольствие <i>AG</i>."],
  ["delecto", "<big>delecto</big>\n\n<b>dēlecto, āvī, ātum, āre</b> [<i>intens. к</i> delicio]\n<b>1)</b> забавлять, веселить, развлекать, услаждать, восхищать (aliquem <i>Ter, C etc.</i>): se d. охотно заниматься, с увлечением предаваться (aliqua re <i>C, Sen etc.</i>); se in otio d. <i>C</i> наслаждаться отдыхом;\n<b>2)</b> <i>med.-pass.</i> delectari находить удовольствие, радоваться, восхищаться (imperio <i>Cs</i>; carminibus <i>H</i>; d. in aliqua re <i>C</i>): aliqua re delectari <i>C etc.</i> быть любителем чего-л.; vir bonus dici delector <i>H</i> мне приятно, когда меня называют честным человеком."],
  ["delector", "<big>delector</big>\n\n<b>I dēlector, —, ārī</b> <i>depon.</i> <i>Pt</i>\n= delecto.\n\n<b>II dēlēctor, ōris</b> <i>m</i> [deligo II]\nвербовщик (<i>солдат</i>) <i>Frontin</i>."],
  ["delectus", "<big>delectus</big>\n\n<b>I dēlēctus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> deligo II;\n<b>2.</b> <i>adj.</i> отборный, лучший (pedites <i>Sl</i>; heroes <i>V</i>).\n\n<b>II dēlēctus (dīlēctus), ūs</b> <i>m</i> [deligo II]\n<b>1)</b> выбор, отбор, подбор (verborum <i>C, Q</i>): sine delectu <i>C etc.</i> без разбора; cum delectu <i>PM, PJ</i> с разбором, выбирая; rerum delectum habere (tenere) <i>C</i> выбирать, поступать с разбором, делать различие (<i>ср.</i> 2);\n<b>2)</b> <i>воен.</i> (<i>тж.</i> d. militaris <i>T</i>) вербовка рекрутов, набор войска: d. provincialis <i>C</i> набор в провинции; delectum habere <i>C</i> (agere <i>PM</i>, instituere <i>bAl</i>) производить набор (<i>ср.</i> 1)."],
  ["delegatio", "<big>delegatio</big>\n\n<b>dēlēgātio, ōnis</b> <i>f</i> [delego]\n<b>1)</b> предписание о выдаче денег, поручение об уплате: solutio delegatione perfecta est <i>Sen</i> уплата произведена согласно приказу о выплате (ассигновке); <i>перен.</i> delegationem res ista non recipit <i>Sen</i> в этой области нельзя действовать через посредников (<i>о нравственной деятельности</i>);\n<b>2)</b> установление (развёрстка) налогов, податное обложение <i>Dig</i>."],
  ["delegatoria", "<big>delegatoria</big>\n\n<b>dēlēgātōria, ae</b> <i>f</i> [delego] (<i>sc.</i> epistula)\nналоговый документ <i>CTh</i>."],
  ["delegi", "<big>delegi</big>\n\n<b>dēlēgī</b> <i>pf. к</i>\ndeligo II."],
  ["delego", "<big>delego</big>\n\n<b>dē–lēgo, āvī, ātum, āre</b>\n<b>1)</b> направлять, отправлять (aliquem in carcerem <i>L</i>; ad senatum <i>L</i>);\n<b>2)</b> поручать, возлагать (officium <i>Q etc.</i>; laborem alicui <i>C</i>; infans delegatur ancillae <i>T</i>);\n<b>3)</b> приписывать (d. alicui crimen aliquid <i>C</i>): d. decus rei bene gestae ad aliquem <i>L</i> приписать кому-л. честь славного дела;\n<b>4)</b> отсылать (aliquem ad illud volumen <i>Nep</i>);\n<b>5)</b> сделать распоряжение об уплате, дать переводную расписку на получение денег: d. alicui fratrem <i>C</i> направить кого-л. к брату для получения денег; delegabo te ad Epicurum, ab illo fiet numeratio <i>Sen</i> я направлю тебя к Эпикуру, который учинит с тобой расчёт (<i>т. е.</i> даст ответ на твои вопросы)."],
  ["deleiro", "<big>deleiro</big>\n\n<b>dēleiro</b> <i>арх.</i>\n= deliro."],
  ["delenificus", "<big>delenificus</big>\n\n<b>dēlēni–ficus, a, um</b> [delenio + facio]\nнежный, ласкательный, чарующий (facta <i>Pl</i>; facundia <i>Aus</i>)."],
  ["delenimentum", "<big>delenimentum</big>\n\n<b>dēlēnīmentum (dēlīnīmentum), ī</b> <i>n</i> [delenio]\n<b>1)</b> болеутоляющее, смягчающее средство (delenimenta podagrae componere <i>Sen</i>);\n<b>2)</b> облегчение, отрада (curarum <i>T</i>);\n<b>3)</b> прелесть, очарование (facundia alicujus <i>Fronto</i>; aurium <i>PJ etc.</i>);\n<b>4)</b> приманка, соблазн (vitiorum <i>T</i>)."],
  ["delenio", "<big>delenio</big>\n\n<b>dē–lēnio (dēlīnio), īvī (iī), ītum, īre</b>\n<b>1)</b> смягчать, утолять (dolorem remediis <i>Ph</i>);\n<b>2)</b> увлекать, привлекать (aliquem suis dictis <i>Pl</i>); склонять на свою сторону, пленять, очаровывать (animos <i>C</i>; aliquem blanditiis voluptatum <i>C</i>)."],
  ["delenitor", "<big>delenitor</big>\n\n<b>dēlēnītor, ōris</b> <i>m</i> [delenio]\n<b>1)</b> убеждающий, склоняющий на свою сторону, пленяющий (judicis d. debet esse orator <i>C</i>);\n<b>2)</b> укротитель (immanium bestiarum <i>Ap</i>)."],
  ["deleo", "<big>deleo</big>\n\n<b>dēleo, ēvī, ētum, ēre</b>\n<b>1)</b> уничтожать, истреблять (hostes <i>Cs</i>, exercitum <i>C</i>); разрушать (urbes <i>L</i>; aedificia <i>C</i>); нарушать, ломать (amicitiae sacramentum <i>Pt</i>);\n<b>2)</b> изглаживать, стирать (nomen alicujus <i>L</i>; omnem suspicionem ex alicujus animo <i>C</i>): omnem memoriam alicujus rei oblivione sempiterna d. <i>C</i> предать что-л. вечному забвению || заглаживать (turpitudinem fugae virtute <i>Cs</i>);\n<b>3)</b> совершенно <i>или</i> навсегда прекращать (non modo praesentia, verum etiam futura bella <i>C</i>): morte deleti <i>C</i> усопшие, мёртвые."],
  ["deler-", "<big>deler-</big>\n\n<b>dēlēr-</b> <i>v. l.</i>\n= delir-."],
  ["deleticius", "<big>deleticius</big>\n\n<b>dēlētīcius, a, um</b> [deleo]\nстёртый, подчищенный (charta <i>Dig</i>)."],
  ["deletilis", "<big>deletilis</big>\n\n<b>dēlētilis, e</b> [deleo]\nстирающий, вытирающий <i>или</i> осушающий (spongia <i>Vr</i>)."],
  ["deletio", "<big>deletio</big>\n\n<b>dēlētio, ōnis</b> <i>f</i> [deleo]\nуничтожение, истребление (exercitus alicujus <i>LM</i>)."],
  ["deletrix", "<big>deletrix</big>\n\n<b>dēlētrīx, īcis</b> <i>f</i> [deleo]\nистребительница, разрушительница (imperii <i>C</i>)."],
  ["deletus", "<big>deletus</big>\n\n<b>I dēlētus, a, um</b> <i>part. pf. к</i>\ndeleo.\n\n<b>II dēlētus, ūs</b> <i>m</i>\nуничтожение <i>Tert</i>."],
  ["delevi", "<big>delevi</big>\n\n<b>dēlēvī</b> <i>pf. к</i>\ndeleo."],
  ["delevo", "<big>delevo</big>\n\n<b>dē–lēvo, —, —, āre</b>\nразгладить, сделать гладким (plagam — <i>sc.</i> arboris — ferro <i>Col</i>)."],
  ["Delf-", "<big>Delf-</big>\n\n<b>Dēlf-</b> <i>v. l.</i>\n= Delph-."],
  ["Delia", "<big>Delia</big>\n\n<b>Dēlia, ae</b> <i>f</i>\nДелия, <i>т. е.</i> делоска (Diana <i>V</i>)."],
  ["Deliacus", "<big>Deliacus</big>\n\n<b>Dēliacus, a, um</b> [Delos]\nделосский (aes <i>PM</i>; vasa <i>C</i>)."],
  ["Deliadae", "<big>Deliadae</big>\n\n<b>Dēliadae, ārum</b> <i>m, f</i>\nделосцы, <i>т. е.</i> Аполлон и Диана <i>Man ap. Vr</i>."],
  ["delibamentum", "<big>delibamentum</big>\n\n<b>dēlībāmentum, ī</b> <i>n</i> [delibo]\nвино, которым совершено возлияние, <i>тж.</i> возлияние <i>VM</i>."],
  ["delibatio", "<big>delibatio</big>\n\n<b>dēlībātio, ōnis</b> <i>f</i> [delibo]\nуменьшение, сокращение (hereditatis <i>Dig</i>)."],
  ["deliberabundus", "<big>deliberabundus</big>\n\n<b>dēlīberābundus, a, um</b> [delibero]\nпогружённый в размышление, размышляющий <i>L</i>."],
  ["deliberatio", "<big>deliberatio</big>\n\n<b>dēlīberātio, ōnis</b> <i>f</i> [delibero]\nтщательное взвешивание, обсуждение, размышление: res habet (<i>или</i> cadit in) deliberationem <i>C</i> дело требует размышления; d. omnis haec est <i>C</i> весь вопрос в следующем; consilii capiendi d. <i>C</i> обсуждение вопроса о том, какое решение следует принять."],
  ["deliberativus", "<big>deliberativus</big>\n\n<b>dēlīberātīvus, a, um</b> [delibero]\nотносящийся к обсуждению, обсуждающий, совещательный (causa <i>C, Q</i>; pars <i>Q</i>)."],
  ["deliberator", "<big>deliberator</big>\n\n<b>dēlīberātor, ōris</b> <i>m</i> [delibero]\nразмышляющий, обдумывающий, обсуждающий <i>C</i>."],
  ["deliberatus", "<big>deliberatus</big>\n\n<b>dēlīberātus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> delibero;\n<b>2.</b> <i>adj.</i> решённый, определённый <i>C, AG</i>."],
  ["delibero", "<big>delibero</big>\n\n<b>dē–lībero, āvī, ātum, āre</b> [libra]\n<b>1)</b> взвешивать, обсуждать, обдумывать, размышлять (d. rem aliquam <i>Ter, C etc.</i> <i>или</i> de re aliqua <i>Cs, C</i>): deliberandum est diu, quod statuendum est semel <i>PS</i> нужно долго размышлять. прежде чем окончательно решить; d. cum aliquo <i>C</i> совещаться с кем-л.; consilium fidele deliberanti dare <i>C</i> дать дельный совет колеблющемуся (раздумывающему); deliberandi spatium sibi relinquere <i>Nep</i> взять время на размышление; re deliberata <i>Cs</i> обдумав вопрос (дело); deliberari potest <i>CC, Sl, L</i> (об этом) можно спрашивать (это ещё вопрос);\n<b>2)</b> вопрошать (Delphos deliberatum mittere <i>Nep</i>);\n<b>3)</b> решать, определять: mihi deliberatum est <i>C</i> я решился; deliberata morte <i>H</i> решившись умереть."],
  ["delibo", "<big>delibo</big>\n\n<b>dē–lībo, āvī, ātum, āre</b>\n<b>1)</b> зачерпнуть (parvam partem ab aequore <i>Lcr</i>); отведать, брать немного, попробовать (d. paululum carnis <i>Pt</i>): summa oscula d. <i>V</i> коснуться беглыми поцелуями;\n<b>2)</b> задевать (truncum summo jugo <i>Col</i> — <i>о быке</i>);\n<b>3)</b> извлекать, выбирать, заимствовать (flosculos ex oratione <i>C</i>): ex universa mente delibati animi <i>C</i> души, составляющие частицы мирового разума;\n<b>4)</b> <i>перен.</i> хлебнуть, вкусить (novum honorem <i>L</i>);\n<b>5)</b> слегка касаться (omnia narratione <i>Q</i>);\n<b>6)</b> уменьшать, суживать, умалять (de aliqua re <i>или</i> aliquam rem): d. aliquid de laude alicujus <i>C</i> несколько умалить чью-л. славу; nulla res animi pacem delibat <i>Lcr</i> ничто не нарушает спокойствия души."],
  ["delibro", "<big>delibro</big>\n\n<b>dē–libro, āvī, ātum, āre</b> [liber II]\nсдирать кору, обдирать (arborem <i>Col</i>; cacumina ramorum <i>Cs</i>); снимать (corticem <i>Col</i>)."],
  ["delibuo", "<big>delibuo</big>\n\n<b>dē–libuo, buī, būtum, ere</b> [<i>одного корня с</i> libo]\nмазать, покрывать (sanguine delibutus <i>Sen</i>); умащивать (unguentis delibutus <i>Sen, Pl</i>): delibuto capillo <i>C</i> с напомаженными волосами; delibutus gaudio <i>Ter</i> упоённый радостью."],
  ["delibutus", "<big>delibutus</big>\n\n<b>dēlibūtus, a, um</b> <i>part. pf. к</i>\ndelibuo."],
  ["delicate", "<big>delicate</big>\n\n<b>dēlicātē</b> [delicatus]\n<b>1)</b> приятно, мило, изящно, тонко (aliquid facere <i>Nep</i>);\n<b>2)</b> роскошно, пышно, изнеженно (vivere <i>C</i>); с изысканной роскошью (iter conficere <i>Su</i>)."],
  ["delicatus", "<big>delicatus</big>\n\n<b>I dēlicātus, a, um</b> [deliciae]\n<b>1)</b> приятный, красивый (litus <i>C</i>; hortuli <i>Ph</i>); прелестный, милый (puerorum comitatus <i>C</i>);\n<b>2)</b> тонкий, изысканный (cibus <i>PM</i>; convivium <i>C</i>); изящный (versus <i>C</i>);\n<b>3)</b> тихоструйный, медленный (amnis <i>PJ</i>);\n<b>4)</b> нежный (puella <i>Ctl</i>); кроткий (oves <i>PJ</i>);\n<b>5)</b> роскошный: merces delicatae <i>Sen</i> предметы роскоши;\n<b>6)</b> изнеженный (adulescens, juventus <i>C</i>); избалованный, капризный, разборчивый, взыскательный, привередливый, требовательный (homo <i>Pl, C</i>; aures <i>Q</i>).\n\n<b>II dēlicātus, ī</b> <i>m</i>\nлюбимчик <i>Pl, Su, Pt</i>."],
  ["delicia", "<big>delicia</big>\n\n<b>I dēlicia, ae</b> <i>f</i> <i>Pl</i>\n= deliciae.\n\n<b>II dēlicia (dēliquia), ae</b> <i>f</i>\nкровельная балка <i>Vtr</i>."],
  ["deliciae", "<big>deliciae</big>\n\n<b>dēliciae, ārum</b> <i>f</i> [<i>одного корня с</i> laqueus <i>и</i> lacio]\n<b>1)</b> веселье, отрада, утеха, наслаждение, радость: habere aliquem in deliciis <i>C etc.</i> любить, баловать кого-л.;\n<b>2)</b> любимец (d. tuae, <i>sc.</i> Trimalchionis <i>Pt</i>): esse alicui in deliciis <i>C</i> быть чьим-л. любимцем;\n<b>3)</b> роскошь, пышность, изысканность (supellectilis <i>C</i>; cenarum ciborumque <i>AG</i>): d. ventris <i>Ap</i> обжорство; d. feminarum <i>PM</i> женские украшения (драгоценности); d. aestivae <i>Fl</i> летние увеселения;\n<b>4)</b> избалованность, капризность (vix ferendae <i>C</i>): se ad delicias alicujus componere <i>Sen</i> приноровиться к чьим-л. капризам;\n<b>5)</b> забавные вещи (a talibus deliciis vel potius ineptiis abesse <i>C</i>): delicias facere <i>Pl etc.</i> балагурить, шутить, <i>но</i> <i>Ctl</i> быть счастливым, наслаждаться (in aliquo)."],
  ["delicies", "<big>delicies</big>\n\n<b>dēliciēs, ēī</b> <i>f</i> <i>Ap</i>\n= deliciae."],
  ["delicio", "<big>delicio</big>\n\n<b>dē–licio, —, —, ere</b> [lacio]\nсманивать, соблазнять (aliquem <i>LM</i>)."],
  ["deliciolae", "<big>deliciolae</big>\n\n<b>dēliciolae, ārum</b> <i>f</i> [<i>demin. к</i> deliciae] <i>ласк.</i>\nлюбимчик, радость (моя) <i>C</i>."],
  ["deliciolum", "<big>deliciolum</big>\n\n<b>dēliciolum, ī</b> <i>n</i> <i>Sen</i>\n= deliciolae."],
  ["deliciose", "<big>deliciose</big>\n\n<b>dēliciōsē</b>\nс наслаждением (requiescere <i>Aug</i>)."],
  ["deliciosus", "<big>deliciosus</big>\n\n<b>dēliciōsus, a, um</b> [deliciae]\nизнеженный <i>Ambr</i>; полный неги (lassitudines <i>Aug</i>)."],
  ["delicium", "<big>delicium</big>\n\n<b>dēlicium, ī</b> <i>n</i> <i>Ph, M etc.</i>\n= deliciae."],
  ["delico", "<big>delico</big>\n\n<b>dēlico</b> <i>арх.</i> <i>Pl</i>\n= deliquo."],
  ["delictor", "<big>delictor</big>\n\n<b>dēlictor, ōris</b> <i>m</i> [delinquo]\nпровинившийся, грешник <i>Eccl</i>."],
  ["delictum", "<big>delictum</big>\n\n<b>dēlictum, ī</b> <i>n</i> [delinquo]\nпроступок, правонарушение (d. committere <i>Cs</i> <i>и</i> facere <i>Pl</i>; delicta fateri <i>O</i>); погрешность, ошибка <i>H</i>."],
  ["deliculus", "<big>deliculus</big>\n\n<b>dēliculus, a, um</b> [<i>demin. к</i> delicus]\nтолько что отнятый от материнских сосцов (oves <i>Cato</i>)."],
  ["delicus", "<big>delicus</big>\n\n<b>dēlicus, a, um</b> [de + lac]\nотнятый от материнской груди (porcus <i>Vr</i>)."],
  ["delicuus", "<big>delicuus</big>\n\n<b>dēlicuus, a, um</b> [delinquo]\nнедостающий, не хватающий, имеющийся в недостатке <i>Pl</i>."],
  ["deligo", "<big>deligo</big>\n\n<b>I dē–ligo, āvī, ātum, āre</b> [ligo I]\n<b>1)</b> привязывать (naves ad terram <i>Cs</i>; aliquem ad palum <i>C</i>); связывать (alicui manus post terga <i>Sen</i>);\n<b>2)</b> повязать, завязать (super oculum lanam imponere et d. <i>CC</i>); перевязать (saucios <i>bAfr</i>; vulnus <i>Q</i>; bracchium <i>CC</i>).\n\n<b>II dē–ligo, lēgī, lēctum, ere</b> [lego I]\n<b>1)</b> собирать, срывать (maturam uvam, primam rosam <i>O</i>; fructum <i>Col</i>);\n<b>2)</b> доставать (manu astra <i>Prp</i>);\n<b>3)</b> выбирать (diem ad aliquam rem <i>Nep</i>; locum castris <i>Cs</i>); избирать (consulem <i>C</i>; aliquem ad eas res conficiendas <i>Cs</i>);\n<b>4)</b> отобрать, отделить (longaevos senes <i>V</i>). — <i>См. тж.</i> delectus II."],
  ["delimo", "<big>delimo</big>\n\n<b>dē–līmo, (āvī), ātum, āre</b>\nспиливать, опиливать: delimata scobis <i>PM</i> опилки."],
  ["delineatio", "<big>delineatio</big>\n\n<b>dēlīneātio, ōnis</b> <i>f</i> [delineo]\nочерк, набросок <i>Tert</i>."],
  ["delineo", "<big>delineo</big>\n\n<b>dē–līneo, āvī, —, āre</b>\nчертить, набрасывать (imaginem alicujus <i>PM</i>): effigiem alicujus coloribus d. <i>Ap</i> написать красками чей-л. портрет."],
  ["delingo", "<big>delingo</big>\n\n<b>dē–lingo, līnxī, līnctum, ere</b>\nоблизывать (aliquem <i>Lampr</i>); слизывать (aliquid <i>CC, Tert</i>): d. salem <i>погов. Pl</i> лизать соль (<i>т. е.</i> скудно питаться)."],
  ["delinimentum", "<big>delinimentum</big>\n\n<b>dēlīnīmentum</b> <i>v. l.</i>\n= delenimentum."],
  ["delinio", "<big>delinio</big>\n\n<b>I dēlīnio</b> <i>v. l.</i>\n= delenio.\n\n<b>II dēlīnio</b> <i>v. l.</i>\n= delineo."],
  ["delinitor", "<big>delinitor</big>\n\n<b>dēlīnītor</b> <i>v. l.</i>\n= delenitor."],
  ["delino", "<big>delino</big>\n\n<b>dē–lino, (lēvī), litum, ere</b>\n<b>1)</b> обмазывать, покрывать (aliquid gypso <i>CC</i>);\n<b>2)</b> (<i>тк. в</i> <i>part. pf.</i>) снимать, соскабливать (tectorium vetus delitum ex aliqua re <i>C</i>);\n<b>3)</b> <i>перен.</i> (= deleo) уничтожать, стирать (litterae delitae <i>Vr, C</i>)."],
  ["delinquentia", "<big>delinquentia</big>\n\n<b>dēlinquentia, ae</b> <i>f</i> [delinquo]\nпроступок, прегрешение <i>Tert</i>."],
  ["delinquio", "<big>delinquio</big>\n\n<b>dēlinquio</b> <i>v. l.</i>\n= deliquio."],
  ["delinquo", "<big>delinquo</big>\n\n<b>dē–linquo, līquī, lictum, ere</b>\n<b>1)</b> <i>редко</i> недоставать, отсутствовать (luna delinquens <i>Ap</i>);\n<b>2)</b> совершить проступок, допустить погрешность, провиниться (d. adversus aliquem <i>L</i>; d. aliqua re <i>T</i>, in aliqua re <i>и</i> aliquid <i>Pl etc.</i>): quod venti et fluxus deliquerunt <i>T</i> то, что натворили ветер и волны; nihil a me delictum puto <i>C</i> я ни в чём, по-моему, не провинился; haec, quae deliquit <i>C</i> допущенные им ошибки."],
  ["deliquesco", "<big>deliquesco</big>\n\n<b>dē–liquēsco, licuī, —, ere</b>\n<b>1)</b> растопляться, (рас)таять (nix delicuit <i>O</i>);\n<b>2)</b> исчезать, пропадать <i>C, Lact</i>."],
  ["deliqui", "<big>deliqui</big>\n\n<b>dēlīquī</b> <i>pf. к</i>\ndelinquo."],
  ["deliquia", "<big>deliquia</big>\n\n<b>dēliquia, ae</b> <i>f</i> <i>v. l.</i>\n= delicia II."],
  ["deliquio", "<big>deliquio</big>\n\n<b>dēliquio, ōnis</b> <i>f</i> [delinquo]\nнедостаток, отсутствие <i>AG</i>: d. libertatis <i>Pl</i> лишение свободы."],
  ["deliquium", "<big>deliquium</big>\n\n<b>I dēliquium, ī</b> <i>n</i> [delinquo]\nнедостаток, отсутствие, <i>т. е.</i> затмение (d. solis <i>PM</i>; luminum <i>Aug</i>).\n\n<b>II dēliquium, ī</b> <i>n</i> [deliquo]\nстекание <i>Eccl</i>."],
  ["deliquo", "<big>deliquo</big>\n\n<b>dē–liquo (dēlico), —, —, āre</b>\n<b>1)</b> процеживать, очищать (quae sunt turbida <i>Vr</i>, vinum <i>Col</i>);\n<b>2)</b> разъяснять, точно рассказывать <i>Pl</i>."],
  ["deliquus", "<big>deliquus</big>\n\n<b>dēliquus, a, um</b> <i>v. l.</i>\n= delicuus."],
  ["deliramentum", "<big>deliramentum</big>\n\n<b>dēlīrāmentum, ī</b> <i>n</i> [deliro]\nвздор, бредии, бессмыслица (deliramenta loqui <i>Pl</i> <i>и</i> effutire <i>Ap</i>)."],
  ["deliratio", "<big>deliratio</big>\n\n<b>dēlīrātio, ōnis</b> <i>f</i> [deliro]\n<b>1)</b> отклонение от колеи (борозды) <i>PM</i>;\n<b>2)</b> бредни, вздор, нелепость <i>C, PJ</i>;\n<b>3)</b> <i>мед.</i> безумие, помешательство <i>CA</i>."],
  ["delirium", "<big>delirium</big>\n\n<b>dēlīrium, ī</b> <i>n</i> [delirus] <i>мед.</i>\nбезумие, помешательство <i>CC</i>."],
  ["deliro", "<big>deliro</big>\n\n<b>dē–līro, āvī, ātum, āre</b> [lira]\n<b>1)</b> (<i>редко</i>) отклоняться от прямой линии (nil ut deliret amussis <i>Aus</i>);\n<b>2)</b> безумствовать, говорить вздор, нести чепуху (lingua delirat <i>Lcr</i>): quidquid delirant reges, plectuntur Achivi <i>впоследствии погов.</i> <i>H</i> из-за сумасбродства царей страдают (простые) ахеяне; se deum d. <i>Tert</i> в своём безумии возомнить себя богом."],
  ["delirus", "<big>delirus</big>\n\n<b>dēlīrus, a, um</b> [deliro]\nбезумный, помешанный, слабоумный (senex <i>C</i>; insipiens et d. <i>Su</i>); delira fari <i>Lcr</i> говорить вздор, бредить."],
  ["delitesco", "<big>delitesco</big>\n\n<b>dē–litēsco (dēlitīsco), tuī, —, ere</b> [latesco I]\n<b>1)</b> прятаться (in latere templi <i>L</i>); укрываться, скрываться (noctu in silvis <i>Cs</i>); таиться (anima in corporis latibulis latescens <i>Ap</i>);\n<b>2)</b> прикрываться (umbra magni hominis <i>Q</i>; in alicujus auctoritate <i>C</i>): d. in calumnia <i>C</i> искать спасения в клевете."],
  ["delitigo", "<big>delitigo</big>\n\n<b>dē–lītigo, —, —, āre</b>\nссориться, браниться (tumido ore <i>H</i>)."],
  ["delitisco", "<big>delitisco</big>\n\n<b>dēlitīsco</b> <i>v. l.</i>\n= delitesco."],
  ["delitor", "<big>delitor</big>\n\n<b>dēlitor, ōris</b> <i>m</i> [delino]\nистребитель, разрушитель <i>Acc</i>."],
  ["delitus", "<big>delitus</big>\n\n<b>dēlitus, a, um</b> — <i>см.</i>\ndelino 2."],
  ["Delium", "<big>Delium</big>\n\n<b>Dēlium, ī</b> <i>n</i>\nДелий, <i>городок на южн. побережье Беотии</i> (<i>на реке Эврип</i>) <i>L, C</i>."],
  ["Delius", "<big>Delius</big>\n\n<b>I Dēlius, a, um</b> [Delos]\nделосский: Delia folia <i>H</i> = laurus; Delia dea <i>H</i> = Diana.\n\n<b>II Dēlius, ī</b> <i>m</i>\nделосец, <i>т. е.</i> Аполлон <i>O</i>."],
  ["Delmat-", "<big>Delmat-</big>\n\n<b>Delmat-</b> <i>v. l.</i>\n= Dalmat-."],
  ["delocatio", "<big>delocatio</big>\n\n<b>dē–locātio, ōnis</b> <i>f</i>\nсмещение, вывих (articulorum <i>CA</i>)."],
  ["delonge", "<big>delonge</big>\n\n<b>dē–longē</b> (<i>тж. раздельно</i>)\nпоодаль, вдали (stare <i>Vlg</i>)."],
  ["Delos", "<big>Delos</big>\n\n<b>Dēlos, ī</b> <i>f</i>\nДелос, <i>один из Кикладских о-вов с городом того же имени, некогда, по преданию, плавучий, но укреплённый Юпитером, чтобы дать пристанище Латоне, которая родила на нём Аполлона и Диану</i> <i>LM, C etc.</i>"],
  ["Delphi", "<big>Delphi</big>\n\n<b>Delphī, ōrum</b> <i>m</i>\n<b>1)</b> Дельфы (<i>тж.</i> Pytho), <i>город в Фокиде у подошвы Парнаса; местонахождение оракула Аполлона; по представлению греков, центр земли</i> <i>Pac, Pl etc.</i>;\n<b>2)</b> жители Дельф <i>Just, Dig</i>."],
  ["Delphica", "<big>Delphica</big>\n\n<b>Delphica, ae</b> <i>f</i> (<i>sc.</i> mensa) <i>M</i>\n= <i>см.</i> Delphicus I."],
  ["Delphicus", "<big>Delphicus</big>\n\n<b>I Delphicus, a, um</b> [Delphi]\nдельфийский: Delphica mensa <i>C</i> дельфийский стол (<i>изящной работы треножник в Дельфийском храме Аполлона</i>); D. ales <i>Pt</i> = ворон.\n\n<b>II Delphicus, ī</b> <i>m</i>\nдельфиец, <i>т. е.</i> Аполлон <i>O</i>."],
  ["delphin", "<big>delphin</big>\n\n<b>delphīn</b> (<i>реже</i> <b>delphīs</b>), <b>īnis</b> <i>m</i> <i>V, O etc.</i>\n= delphinus."],
  ["delphinus", "<big>delphinus</big>\n\n<b>delphīnus, ī</b> <i>m</i> (<i>acc. sg.</i> <i>иногда</i> īna) (<i>греч.</i>)\n<b>1)</b> дельфин <i>Acc, C etc.</i>;\n<b>2)</b> рычаг в водяном органе <i>Vtr</i>."],
  ["Delphis", "<big>Delphis</big>\n\n<b>I Delphis, idis</b> <i>f</i> [Delphi]\nдельфийка, <i>т. е.</i> Дельфийская пифия <i>M</i>.\n\n<b>II Delphis, idis</b> <i>adj. f</i>\nдельфийская (Sibylla <i>Lact</i>).\n\n<b>III delphis</b>\n<i>редко</i> = delphin."],
  ["delta", "<big>delta</big>\n\n<b>I delta</b> <i>n indecl.</i> <i>и</i> <b>delta, ae</b> <i>f</i> (<i>греч.</i>)\nдельта (<i>четвёртая буква греч. алфавита</i>) <i>Mela, Aus</i>.\n\n<b>II Delta</b> <i>n indecl.</i>\nНильская дельта <i>bAfr, Mela</i>."],
  ["Deltoton", "<big>Deltoton</big>\n\n<b>Deltōton, ī</b> <i>n</i> (<i>греч.</i>)\nсозвездие Треугольника <i>C</i>."],
  ["delubrum", "<big>delubrum</big>\n\n<b>dē–lūbrum, ī</b> <i>n</i> [luo]\nхрам, святилище (d. Junonis <i>L</i>): deorum templa ac delubra <i>C</i>."],
  ["delucto", "<big>delucto</big>\n\n<b>dēlucto, āvī, —, āre</b> <i>Pl</i>\n= deluctor."],
  ["deluctor", "<big>deluctor</big>\n\n<b>dē–luctor, —, ārī</b> <i>depon.</i>\nбороться, вести решительный бой (cum aliquo, cum aliqua re <i>и</i> alicui rei <i>Pl</i>)."],
  ["deludifico", "<big>deludifico</big>\n\n<b>dēlūdi–fico, āvī, —, āre</b> [deludo + facio]\nнасмехаться, вышучивать, трунить, дурачить (aliquem <i>Pl</i>)."],
  ["deludo", "<big>deludo</big>\n\n<b>dē–lūdo, lūsī, lūsum, ere</b>\n<b>1)</b> насмехаться, вышучивать, дурачить, обманывать (aliquem <i>Pl, Ter etc.</i>; spes delusit aliquem <i>Ph</i>);\n<b>2)</b> закончить, прекратить игру (gladiatores deluserunt <i>Vr ap. PM</i>)."],
  ["delumbis", "<big>delumbis</big>\n\n<b>dē–lumbis, e</b> [lumbus]\n<b>1)</b> с расслабленной поясницей, хромающий (coturnix <i>PM</i>);\n<b>2)</b> <i>перен.</i> слабый, расслабленный, бессильный (dictio <i>Sid</i>): d. saliva <i>Pers</i> обильная слюна."],
  ["delumbo", "<big>delumbo</big>\n\n<b>dēlumbo, (āvī), ātum, āre</b> [delumbis]\n<b>1)</b> причинять боль в крестце, вызывать поясничный прострел (d. aliquem <i>Sen</i>);\n<b>2)</b> страдать болью в пояснице <i>PM</i>;\n<b>3)</b> ослаблять, лишать сил: d. sententias <i>C</i> ослаблять (<i>т. е.</i> опровергать) положения;\n<b>4)</b> искривлять: lacunar delumbatum <i>Vtr</i> вогнутый (сводчатый) потолок."],
  ["deluo", "<big>deluo</big>\n\n<b>dē–luo, luī, —, ere</b>\nсполаскивать, смывать, очищать (alvum aqua <i>CC</i> — <i>v. l.</i>; <i>перен.</i> aliquid <i>AG, Aug</i>)."],
  ["Delus", "<big>Delus</big>\n\n<b>Dēlus</b> <i>v. l.</i>\n= Delos."],
  ["delusi", "<big>delusi</big>\n\n<b>dēlūsī</b> <i>pf. к</i>\ndeludo."],
  ["delusio", "<big>delusio</big>\n\n<b>dēlūsio, ōnis</b> <i>f</i> [deludo]\nвысмеивание, насмешка <i>Eccl</i>."],
  ["delustro", "<big>delustro</big>\n\n<b>dē–lūstro, —, —, āre</b>\nосвобождать от чар, завораживать от зла, оберегать от несчастий (d. aliquem radice herbae <i>Ap</i>)."],
  ["deluto", "<big>deluto</big>\n\n<b>dē–luto, —, —, āre</b>\nобмазывать (покрывать) глиной (habitationem <i>Cato</i>)."],
  ["Demades", "<big>Demades</big>\n\n<b>Dēmādēs, is</b> <i>m</i>\nДемад, <i>политик и оратор, противник Демосфена, сторонник мира с Македонией; в 319 г. до н. э. казнён Антипатром</i> <i>C, Nep</i>."],
  ["demadesco", "<big>demadesco</big>\n\n<b>dē–madēsco, maduī, —, ere</b>\nстановиться мокрым, увлажняться <i>O</i>; пропитываться водой, промокать, размягчаться <i>Scr</i>."],
  ["demagis", "<big>demagis</big>\n\n<b>dē–magis</b> <i>adv.</i> (= valde magis)\nвесьма, чересчур <i>LM</i>."],
  ["demandatio", "<big>demandatio</big>\n\n<b>dēmandātio, ōnis</b> <i>f</i>\nрекомендация <i>Tert</i>."],
  ["demando", "<big>demando</big>\n\n<b>dē–mando, āvī, ātum, āre</b>\nпоручать, вверять, передавать (pueros curae paedagogi <i>L</i>): d. aliquem in proximam civitatem <i>Su</i> перевести кого-л. на соседнюю территорию (<i>для безопасности</i>); d. conjuges liberosque abditis insulis <i>Just</i> укрыть жён и детей на отдалённых островах."],
  ["demano", "<big>demano</big>\n\n<b>dē–māno, āvī, ātum, āre</b>\n<b>1)</b> течь, струиться <i>Ctl, AG</i>;\n<b>2)</b> <i>перен.</i> проистекать (ex eodem fonte <i>Ambr</i>);\n<b>3)</b> происходить (de patriarcharum genere <i>Ambr</i>)."],
  ["Demaratus", "<big>Demaratus</big>\n\n<b>Dēmarātus, ī</b> <i>m</i>\nДемарат:\n<b>1)</b> <i>царь Спарты, бежал к Дарию и участвовал в походе Ксеркса на Грецию</i> (<i>480 г. до н. э.</i>) <i>Just, Sen</i>;\n<b>2)</b> <i>коринфянин, бежавший от тирана Кипсела в Тарквинии (Этрурия); отец Тарквиния Приска</i> <i>L, C</i>."],
  ["demarchus", "<big>demarchus</big>\n\n<b>dēmarchus, ī</b> <i>m</i> (<i>греч.</i>)\nдемарх, <i>выборный руководитель греч. общины (дема) (у римск. писателей приравнивается к</i> tribunus plebis) <i>Pl, Spart</i>."],
  ["dematrico", "<big>dematrico</big>\n\n<b>dē–mātrīco, —, —, āre</b> [matrix]\nпускать кровь из маточной вены (<i>у животных</i>) <i>Veg</i>."],
  ["demeaculum", "<big>demeaculum</big>\n\n<b>dēmeāculum, ī</b> <i>n</i> [demeo]\nсошествие, спуск (в подземный мир) <i>Ap</i>."],
  ["demens", "<big>demens</big>\n\n<b>I dē–mēns, entis</b> <i>adj.</i>\nбезумный, сумасшедший, помешанный (Orestes <i>C, H</i>); ослеплённый, неразумный (consilium <i>C</i>; actio <i>VM</i>).\n\n<b>II dēmēns, entis</b> <i>m</i>\nбезумец, глупец <i>C, Sen</i>."],
  ["demensio", "<big>demensio</big>\n\n<b>dēmēnsio, ōnis</b> <i>f</i> [demetior]\nотмеривание <i>Aus</i>."],
  ["demensum", "<big>demensum</big>\n\n<b>dēmēnsum, ī</b> <i>n</i> [demetior]\n(<i>sc.</i> frumentum) ежемесячный хлебный паёк (<i>римск. рабов</i>) <i>Ter</i>."],
  ["demensus", "<big>demensus</big>\n\n<b>dēmēnsus, a, um</b> <i>part. pf. к</i>\ndemetior."],
  ["dementer", "<big>dementer</big>\n\n<b>dēmenter</b> [demens]\nбезумно, нелепо <i>C, O, Sen</i>."],
  ["dementia", "<big>dementia</big>\n\n<b>dēmentia, ae</b> <i>f</i>\nбезумие, сумасшествие, помешательство <i>C etc.</i>: illud extremae (postremae <i>Ap</i>) dementiae est <i>Sl</i> это верх безумия."],
  ["dementio", "<big>dementio</big>\n\n<b>dēmentio, —, —, īre</b> [demens]\nбезумствовать, быть сумасшедшим <i>Lcr, Ap, Lact</i>."],
  ["demento", "<big>demento</big>\n\n<b>dēmento, āvī, ātum, āre</b> [demens]\n<b>1)</b> сводить с ума, <i>перен.</i> обманывать, дурачить (aliquem magiis suis <i>Vlg</i>);\n<b>2)</b> <i>Lact</i> = dementio."],
  ["demeo", "<big>demeo</big>\n\n<b>dē–meo, —, —, āre</b>\nспускаться, сходить вниз (caelo <i>Ap</i>)."],
  ["demereo", "<big>demereo</big>\n\n<b>dē–mereo, ruī, ritum, ēre</b> (= demereor)\n<b>1)</b> приносить доход, зарабатывать (aliquid mercedis alicui <i>Pl</i>; grandem pecuniam <i>AG</i>);\n<b>2)</b> оказываться достойным, заслуживать: aliquem aliqua re demeruisse <i>O</i> оказаться достойным кого-л. благодаря чему-л.;\n<b>3)</b> склонять на свою сторону, располагать в свою пользу (aliquem <i>O, Su</i>)."],
  ["demereor", "<big>demereor</big>\n\n<b>dē–mereor, ritus (sum), ērī</b> <i>depon.</i>\nпривлекать благодеяниями (aliquem <i>T, Q etc.</i>): Otho alium alia via militem demerebatur <i>Su</i> Отон различными способами старался заслужить расположение солдат. — <i>См. тж.</i> demereo."],
  ["demergo", "<big>demergo</big>\n\n<b>dē–mergo, mersī, mersum, ere</b>\n<b>1)</b> погружать, опускать (candens ferrum in gelidum imbrem <i>Lcr</i>): in corde cerebrove demersus mens <i>C</i> душевная жизнь, тесно связанная с сердцем и мозгом || потопить, пустить ко дну (naves <i>L, QC</i>; navigia <i>PM</i>; triremem hostium <i>bAl</i>): demergi in aqua <i>C</i> погрузиться в воду;\n<b>2)</b> поглощать (dapes avidam in alvum <i>O</i>);\n<b>3)</b> вонзать (gladium alicui <i>Ap</i>);\n<b>4)</b> втягивать (colla umeris <i>St</i>);\n<b>5)</b> сажать (stirpem <i>Col</i>);\n<b>6)</b> удручать, угнетать (aliquem <i>L</i>); in fossas exhauriendas demersus <i>L</i> вынужденный заниматься копанием рвов; patriam demersam efferre <i>C</i> возвысить (воскресить) поверженное (в прах) отечество; aere alieno demersum esse <i>L</i> увязнуть в долгах;\n<b>7)</b> обрекать (filiam in perpetuam virginitatem <i>Just</i>);\n<b>8)</b> <i>поздн.</i> заходить, садиться (sol demergit <i>MF</i>)."],
  ["demersio", "<big>demersio</big>\n\n<b>dēmersio, ōnis</b> <i>f</i> [demergo]\nопускание, оседание (urbium demersiones <i>Sol</i>); <i>перен.</i> погружение, падение (animae <i>Macr</i>)."],
  ["demersus", "<big>demersus</big>\n\n<b>dēmersus, ūs</b> [demergo]\nопускание, погружение <i>Ap</i>."],
  ["demessus", "<big>demessus</big>\n\n<b>dēmessus, a, um</b> <i>part. pf. к</i>\ndemeto II."],
  ["demetior", "<big>demetior</big>\n\n<b>dē–mētior, mēnsus sum, īrī</b> <i>depon.</i>\nотмерять <i>Pl, C</i> (<i>тк. в</i> <i>part. pass.</i>)."],
  ["demeto", "<big>demeto</big>\n\n<b>I dēmēto</b> <i>v. l.</i>\n= dimeto.\n\n<b>II dē–meto, messuī, messum, ere</b>\n<b>1)</b> косить, жать, убирать (hordeum <i>Cato</i>; frumentum <i>Cs</i>; agros <i>C</i>); срывать, собирать (fructus <i>C</i>; flores <i>V</i>);\n<b>2)</b> вынимать (из улья), выламывать (favos <i>Col</i>);\n<b>3)</b> отрубать, сносить (caput ense <i>O</i>)."],
  ["demetor", "<big>demetor</big>\n\n<b>dēmētor</b> <i>v. l.</i>\n= dimetor."],
  ["Demetrias", "<big>Demetrias</big>\n\n<b>Dēmētrias, adis</b> <i>f</i>\nДеметриада, <i>прежде</i> Pagasae, <i>город в Фессалии, колонизованный Полиоркетом</i> <i>L</i>."],
  ["Demetrius", "<big>Demetrius</big>\n\n<b>Dēmētrius, ī</b> <i>m</i>\nДеметрий:\n<b>1)</b> D. Poliorcetes, <i>царь Македонии в 294-283 гг. до н. э.</i> <i>C, Just</i>;\n<b>2)</b> D. Soter, <i>царь Сирии 162-150 гг. до н. э.</i> <i>Just</i>;\n<b>3)</b> D. Nicator, <i>царь Сирии 145-140 гг. и 130-126 гг. до н. э., умер в 126 г. до н. э.</i> <i>PM</i>;\n<b>4)</b> D. Magnes, <i>историк и грамматик, современник Цицерона</i> <i>C</i>;\n<b>5)</b> D. Phalereus, <i>ученик Теофраста и Менандра, в 317-307 гг. до н. э. наместник Кассандра в Афинах, затем советник Птолемея Лага в Александрии Египетской</i> <i>C, Nep, Q</i>;\n<b>6)</b> D. <i>из Суния, философ-киник в эпоху Калигулы — Домициана</i> <i>Sen, T</i>."],
  ["demigratio", "<big>demigratio</big>\n\n<b>dēmigrātio, ōnis</b> <i>f</i> [demigro]\nпереселение, выселение <i>Nep</i>."],
  ["demigro", "<big>demigro</big>\n\n<b>dē–migro, āvī, ātum, āre</b>\n<b>1)</b> переселяться, выселяться (ex agris in urbem <i>L</i>); покидать, оставлять (d. de suo statu <i>C</i>): d. hinc (<i>или</i> ex vita) <i>C</i> умереть;\n<b>2)</b> исчезать, проходить, сходить (strumae ab ore demigraverunt <i>C</i>)."],
  ["deminoratio", "<big>deminoratio</big>\n\n<b>dēminōrātio, ōnis</b> <i>f</i> [deminoro]\nумаление <i>Vlg</i>."],
  ["deminoro", "<big>deminoro</big>\n\n<b>dē–minōro, —, —, āre</b>\nумалять, принижать (aliquem <i>Tert</i>)."],
  ["deminuo", "<big>deminuo</big>\n\n<b>dē–minuo, minuī, minūtum, ere</b>\n<b>1)</b> уменьшать, умалять, суживать, убавлять (copias, vires <i>Cs</i>): d. potentiam alicujus <i>Cs</i> ограничить чью-л. власть; <i>pass.</i> deminui <i>или</i> se deminuere capite <i>юр. C, L, Dig</i> лишиться прав гражданина (<i>полностью или частично</i>);\n<b>2)</b> вычитать, отнимать (d. quinque nummos de una mina <i>Pl</i>; d. aliquid ex potestate alicujus <i>L</i>): d. aliquid de bonis <i>C</i> продать часть имущества; d. praedia <i>C</i> частично продать поместья;\n<b>3)</b> <i>грам.</i> делать уменьшительным («sacellum» ex «sacro» deminutum est <i>AG</i>)."],
  ["deminutio", "<big>deminutio</big>\n\n<b>dēminūtio, ōnis</b> <i>f</i> [deminuo]\n<b>1)</b> уменьшение, сокращение, понижение (civium <i>C</i>; vectigalium <i>C</i>); убывание, убыль (luminis <i>C</i>; lunae <i>Vtr</i>): d. sui <i>T</i> умаление собственного достоинства; d. provinciae <i>C</i> сокращение срока службы в провинции;\n<b>2)</b> <i>юр.</i> право отчуждения <i>L</i>;\n<b>3)</b> <i>юр.</i> правовое ограничение: d. capitis <i>Cs etc.</i> ограничение <i>или</i> лишение гражданских прав; d. maxima (<i>тж.</i> d. libertatis <i>C, Cs</i>) <i>G, Dig</i> — с лишением свободы; d. minor (media) <i>G, Dig</i> — без лишения свободы; d. minima <i>G, Dig</i> — в связи с переходом в другую семью;\n<b>4)</b> ослабление: d. mentis <i>Su</i> слабоумие <i>или</i> бредовое состояние;\n<b>5)</b> <i>грам.</i> придание слову уменьшительной формы <i>Vr</i>; <i>тж.</i> уменьшительная форма слова <i>Q</i>."],
  ["deminutive", "<big>deminutive</big>\n\n<b>dēminūtīvē</b> <i>adv.</i> <i>грам.</i>\nв уменьшительной форме <i>Macr</i>."],
  ["deminutivum", "<big>deminutivum</big>\n\n<b>dēminūtīvum, ī</b> <i>n</i> [deminuo]\n(<i>sc.</i> nomen) <i>грам.</i> уменьшительное слово."],
  ["deminutivus", "<big>deminutivus</big>\n\n<b>dēminūtīvus, a, um</b> [deminuo] <i>грам.</i>\nуменьшительный (vocabulum <i>Veg</i>; vox <i>Tert</i>)."],
  ["deminutus", "<big>deminutus</big>\n\n<b>dēminūtus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> deminuo;\n<b>2.</b> <i>adj.</i> малый, маленький (qualitas <i>Tert</i>)."],
  ["demiror", "<big>demiror</big>\n\n<b>dē–mīror, ātus sum, ārī</b>\nсильно удивляться, поражаться (d. aliquem, aliquam rem <i>C etc.</i>): nihil te ad me scripsisse demiror <i>C</i> удивляюсь, что ты ничего мне не написал; demiror, ubi nunc ambulet Messenio <i>Pl</i> желал бы я знать, где теперь гуляет Мессенион; facere multa demiranda <i>AG</i> обнаруживать множество поразительных явлений."],
  ["demisi", "<big>demisi</big>\n\n<b>dēmīsī</b> <i>pf. к</i>\ndemitto."],
  ["demisse", "<big>demisse</big>\n\n<b>dēmissē</b> [demissus]\n<b>1)</b> низко (volare <i>O</i>);\n<b>2)</b> скромно, смиренно (respondere <i>C</i>);\n<b>3)</b> малодушно, униженно (humiliter demisseque sentire <i>C</i>)."],
  ["demissicius", "<big>demissicius</big>\n\n<b>dēmissīcius, a, um</b> [demitto]\nспущенный, опущенный, свисающий (tunica <i>Pl</i>)."],
  ["demissio", "<big>demissio</big>\n\n<b>dēmissio, ōnis</b> <i>f</i> [demitto]\n<b>1)</b> опускание, спускание (storiae <i>C</i>);\n<b>2)</b> отпускание, отращивание (barbae <i>Macr</i>);\n<b>3)</b> <i>мед.</i> облегчение (болезни), ремиссия <i>CA</i>;\n<b>4)</b> угнетённость, подавленность, уныние (infractio et d. animi <i>C</i>)."],
  ["demissus", "<big>demissus</big>\n\n<b>dēmissus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> demitto;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> спускающийся, висящий, обвислый (umeri <i>Ter</i>; aures <i>V</i>); ниспадающий (laena demissa ex umeris <i>V</i>);\n<b>2)</b> низменный, низкий (loca <i>Cs</i>; ripae <i>bAl</i>);\n<b>3)</b> опущенный, поникший, понуренный (caput <i>C, Cs</i>): demissus it <i>O</i> он движется (летает) низко;\n<b>4)</b> глубокий (vulnus <i>Sen</i>);\n<b>5)</b> тихий, приглушенный (vox <i>V</i>);\n<b>6)</b> скромный, смиренный, простой (sermo d. atque humilis <i>C</i>);\n<b>7)</b> малодушный, <i>тж.</i> подавленный, приунывший, унылый (oratio <i>AG</i>; animus <i>C</i>)."],
  ["demitigo", "<big>demitigo</big>\n\n<b>dē–mītigo, —, —, āre</b>\nсмягчать, делать снисходительным, кротким (aliquem <i>C</i>)."],
  ["demitto", "<big>demitto</big>\n\n<b>dē–mitto, mīsī, missum, ere</b>\n<b>1)</b> спускать, опускать (usque ad talos demissa purpura <i>C</i>; antemnam <i>O</i> <i>и</i> armamenta <i>Sen</i>; aures <i>H</i>; calculum in urnam <i>O</i>; rami ad terram demissi <i>Su</i>): d. arma <i>bAfr</i> опустить оружие (<i>воинский салют</i>) || спускать, сводить, переводить (agmen in vallem <i>L</i>; equites in inferiorem campum <i>I</i>; se in aequum locum d. <i>Cs</i>; castra ad ripas d. <i>Hirt</i>): d. se in Ciliciam <i>C</i> отправиться в Киликию; (de) caelo demissus <i>L, Q</i> упавший с неба (<i>тж.</i> <i>ирон.</i>); d. oculos <i>или</i> vultum (<i>тж.</i> in terram) <i>L, Q, C etc.</i> потупить взор, <i>но</i>: d. oculos <i>VF</i> смежить глаза, заснуть; fugere manibus demissis <i>погов. Pl</i> бежать опрометью || спускать, ввергать (in inferiorem demissus carcerem <i>L</i>): se paulatim ad planitiem d. <i>QC</i> отлого спускаться к равнине (<i>о горе</i>); animum d. <i>C</i>, d. mentem <i>V</i> <i>и</i> se animo d. <i>Cs</i> пасть духом, приуныть;\n<b>2)</b> (<i>тж.</i> d. secundo amni <i>L</i>) пускать вниз по течению (naves <i>V</i>; classem Rheno <i>T</i>);\n<b>3)</b> погружать (se in aquam calidam <i>CC</i>): d. se <i>перен.</i> погружаться, предаваться, ввязываться (in res turbulentissimas <i>C</i>; in causam <i>C</i>); demissa in discrimen dignitas <i>L</i> оказавшееся под угрозой достоинство;\n<b>4)</b> вонзать (ferrum in pectus <i>T</i>; gladium in jugulum <i>Pl</i>; ferrum jugulo <i>O</i>);\n<b>5)</b> втыкать, вбивать, вколачивать (sublicas in terram <i>Cs</i>; cuneum inter corticem et materiem <i>Col</i>);\n<b>6)</b> сажать (arbores <i>PM</i>);\n<b>7)</b> закладывать (fundamenta in terram demissa <i>QC</i>);\n<b>8)</b> рыть, выкапывать (puteum alte <i>V</i>);\n<b>9)</b> наклонять, нагибать (se ob assem d. <i>H</i>): se d. ad aures alicujus <i>C</i> наклониться к чьим-л. ушам, <i>т. е.</i> шептать кому-л. на ухо; caput ad fornicem Fabii d. <i>Crassus ap. C</i> наклонить голову, чтобы пройти под аркой Фабия;\n<b>10)</b> впускать, вводить (fistulam in iter urinae <i>CC</i>);\n<b>11)</b> валить, рубить (robora ferro <i>VF</i>);\n<b>12)</b> сбивать (volucrem caelo <i>Sil</i>);\n<b>13)</b> сбрасывать, ронять: d. flores <i>Lcr</i> осыпаться, отцветать;\n<b>14)</b> низвергать, ниспосылать (imbrem caelo <i>V</i>): d. aliquem ad imos manes <i>V</i> (<i>тж.</i> Stygiae nocti <i>O</i>, Orco, morti <i>и</i> neci <i>V</i>, umbris <i>Sil</i>) отправить кого-л. в царство теней, <i>т. е.</i> умертвить;\n<b>15)</b> отпускать, отращивать (barbam <i>Lcr</i>; demissi capilli <i>O</i>);\n<b>16)</b> низводить (vim dicendi ad unum auditorem <i>Q</i>): se d. <i>и</i> demitti опускаться, унижаться (se in preces d. <i>Sen</i>; se ad servilem patientiam d., demitti in adulationem <i>T</i>);\n<b>17)</b> вливать, наливать (ternos cyathos alicujus rei <i>Col</i>); проливать, лить (lacrimas <i>Lcr</i>): se d. литься вниз, стекать (Scamander ab Idaeo monte demissus <i>Mela</i>);\n<b>18)</b> допускать, принимать: d. aliquid in pectus suum <i>Sl</i> <i>или</i> in pectora animosque <i>L</i> принять что-л. близко к сердцу, <i>тж.</i> крепко запомнить что-л.; negare d. dicta alicujus in aures <i>V</i> оставаться глухим к чьим-л. просьбам;\n<b>19)</b> отклонять, отводить: d. aliquem periculo <i>Prp</i> избавить кого-л. от опасности;\n<b>20)</b> <i>pass.</i> demitti брать начало, происходить (Romanus Troja demissus <i>T</i>; nomen a magno Iulo demissum <i>V</i>);\n<b>21)</b> доходить: eo rem demittit <i>C</i> он договаривается до того, что... — <i>См. тж.</i> demissus."],
  ["demiurgus", "<big>demiurgus</big>\n\n<b>dēmiūrgus, ī</b> <i>m</i> (<i>греч.</i>)\nдемиург:\n<b>1)</b> <i>высшее должностное лицо в некоторых греч. республиках</i> <i>L</i>;\n<b>2)</b> <i>творец мира</i> (<i>у гностиков</i>) <i>Tert etc.</i>"],
  ["demo", "<big>demo</big>\n\n<b>dēmo, dēmpsī, dēmptum, ere</b> [de + emo]\n<b>1)</b> снимать (barbam <i>C</i>; caput gladio <i>QC</i>; juga bobus <i>H</i>; e suis umeris amictum <i>O</i>); вынимать (secures de fascibus <i>C</i>); срывать (pomum ex arbore <i>Cato</i>);\n<b>2)</b> избавлять, освобождать (d. alicui onus <i>L</i>, molestiam <i>Ter</i>, acerbam necessitudinem <i>Sl</i>): d. silentia furto <i>O</i> раскрыть кражу; clauditatem d. <i>Ap</i> излечить от хромоты;\n<b>3)</b> отнимать, устранять: dempto fine <i>O</i> без конца; dempto auctore <i>L</i> без (независимо от) автора; si demas juvare deos <i>O</i> если отрицать помощь со стороны богов;\n<b>4)</b> отнимать, вычитать: una uncia dempta <i>Vr</i> по вычете одной унции; partem solido d. de die <i>H</i> урвать часть рабочего дня (<i>для отдыха</i>)."],
  ["Demochares", "<big>Demochares</big>\n\n<b>Dēmocharēs, is</b> <i>m</i>\nДемохар, <i>племянник Демосфена, афинский оратор</i> <i>C, Sen</i>."],
  ["Democritea", "<big>Democritea</big>\n\n<b>Dēmocritēa, ōrum</b> <i>n</i>\nположения (учение) Демокрита <i>C</i>."],
  ["Democriteus", "<big>Democriteus</big>\n\n<b>I Dēmocritēus (-īus), a, um</b> [Democritus]\nДемокритов(ский) <i>C</i>.\n\n<b>II Dēmocritēus, ī</b> <i>m</i>\nученик, приверженец Демокрита <i>C</i>."],
  ["Democritus", "<big>Democritus</big>\n\n<b>Dēmocritus, ī</b> <i>m</i>\nДемокрит, <i>из Абдеры, крупнейший представитель античного материализма, ученик Левкиппа, основатель атомистической теории</i> (<i>род. ок. 460 г. до н. э.</i>) <i>Lcr, Vr, C, H</i>."],
  ["demogrammateus", "<big>demogrammateus</big>\n\n<b>dēmogrammate͡us, ī</b> <i>m</i> (<i>греч.</i>)\nобщественный писец <i>CTh</i>."],
  ["demolio", "<big>demolio</big>\n\n<b>dē–mōlio, īvī (iī), —, īre</b> <i>Naev, Vr</i>\n= demolior."],
  ["demolior", "<big>demolior</big>\n\n<b>dē–mōlior, mōlītus sum, īrī</b> <i>depon.</i>\n<b>1)</b> ломать, разрушать, (по)валить (columnam, statuam, domum <i>C</i>);\n<b>2)</b> разбирать, разбрасывать (rogum <i>Ap</i>);\n<b>3)</b> уничтожать (jus <i>L</i>);\n<b>4)</b> сваливать (culpam de se <i>Pl</i>);\n<b>5)</b> искажать (faciem <i>Hier</i>)."],
  ["demolitio", "<big>demolitio</big>\n\n<b>dēmōlītio, ōnis</b> <i>f</i> [demolior]\nразрушение (statuarum <i>C</i>; <i>перен.</i> veritatis <i>Tert</i>); срытие (castelli <i>Vtr</i>)."],
  ["demolitor", "<big>demolitor</big>\n\n<b>dēmōlītor, ōris</b> <i>m</i> [demolior]\nразрушитель: corvus d. <i>Vtr</i> «ворон», <i>род стенобитной машины.</i>"],
  ["demonstrabilis", "<big>demonstrabilis</big>\n\n<b>dēmōnstrābilis, e</b>\nдоказуемый <i>Boët</i>."],
  ["demonstratio", "<big>demonstratio</big>\n\n<b>dēmōnstrātio, ōnis</b> <i>f</i> [demonstro]\n<b>1)</b> указывание, показывание, точное описание <i>C, Dig</i>;\n<b>2)</b> наглядное изображение <i>rhH</i>;\n<b>3)</b> доказательство, довод <i>C</i>;\n<b>4)</b> торжественная речь, посвященная чьей-л. характеристике <i>C</i>;\n<b>5)</b> <i>грам.</i> обозначение, определение (<i>sc.</i> verbi <i>AG</i>)."],
  ["demonstrative", "<big>demonstrative</big>\n\n<b>dēmōnstrātīvē</b> <i>adv.</i>\nс точным указанием, наглядно (dicere aliquid <i>Macr</i>)."],
  ["demonstrativus", "<big>demonstrativus</big>\n\n<b>dēmōnstrātīvus, a, um</b> [demonstro]\n<b>1)</b> указательный (digitus <i>CA</i>; pronomina <i>Is</i>);\n<b>2)</b> <i>ритор.</i> излагающий, посвященный чьей-л. характеристике (genus orationis <i>C</i>; causa <i>Q</i>)."],
  ["demonstrator", "<big>demonstrator</big>\n\n<b>dēmōnstrātor, ōris</b> <i>m</i> [demonstro]\nдоказывающий, доказавший (alicujus rei <i>C</i>); указывающий (d. hujus rationis <i>Col</i>)."],
  ["demonstro", "<big>demonstro</big>\n\n<b>dē–mōnstro, āvī, ātum, āre</b>\n<b>1)</b> показывать, указывать (itinera <i>C, L</i>; viam <i>PM</i>; aliquid digito <i>C, Nep, Su</i>): ut supra (ante, antea) demonstravimus <i>C, Cs</i> как мы указали ранее;\n<b>2)</b> излагать, объяснять (alicui rem aliquam <i>Pl etc.</i>): quae demonstrari scripto non possunt <i>Q</i> что не может быть выражено письменно;\n<b>3)</b> доказывать (d. aliquem esse felicissimum <i>C</i>);\n<b>4)</b> <i>грам.</i> обозначать, означать (vocabula aliquam rem demonstrantia <i>AG</i>)."],
  ["Demophoon", "<big>Demophoon</big>\n\n<b>Dēmophoōn, ōntis</b> <i>m</i>\nДемофоонт:\n<b>1)</b> <i>сын Тезея</i> <i>O, Just</i>;\n<b>2)</b> <i>спутник Энея</i> <i>V</i>."],
  ["demordeo", "<big>demordeo</big>\n\n<b>dē–mordeo, mordī, morsum, ēre</b>\nоткусывать, отгрызать (aliquid <i>PM</i>); кусать (ungues <i>Pers</i>)."],
  ["demorior", "<big>demorior</big>\n\n<b>dē–morior, mortuus sum, morī</b> <i>depon.</i>\n<b>1)</b> умирать (fame <i>Pl</i>): familiares demortui sunt <i>C</i> друзья умерли один за другим;\n<b>2)</b> быть без памяти влюблённым (d. aliquem <i>Pl</i>)."],
  ["demoror", "<big>demoror</big>\n\n<b>dē–moror, ātus sum, ārī</b> <i>depon.</i>\n<b>1)</b> задерживать (aliquem diutius <i>C</i>); замедлять (iter <i>Cs</i>): d. annos <i>V</i> всё ещё оставаться в живых;\n<b>2)</b> задерживаться, медлить: ille, nihil demoratus, exsurgit <i>T</i> он (Вестиний) тотчас же (без всякого промедления) поднимается;\n<b>3)</b> ожидать (mortalia arma <i>V</i>)."],
  ["demorsico", "<big>demorsico</big>\n\n<b>dē–morsico (dēmorsito), —, ātum, āre</b>\nоткусывать, отгрызать (aliquid <i>Ap</i>)."],
  ["demos", "<big>demos</big>\n\n<b>dēmos, ī</b> <i>m</i> (<i>греч.</i>)\nнарод (d. Atheniensium <i>PM</i>)."],
  ["Demosthenes", "<big>Demosthenes</big>\n\n<b>Dēmosthenēs, is</b> <i>и</i> <b>ī</b> <i>m</i>\nДемосфен:\n<b>1)</b> <i>сын Алкисфена, афинский полководец во время Пелопоннесской войны, в 413 г. до н. э. вместе с Никием совершил морской поход в Сицилию, но потерпел поражение и был казнён.</i> <i>Just</i>;\n<b>2)</b> <i>сын Демосфена, уроженец аттического дема Пеании, величайший аттический оратор, решительный враг Македонии</i> (<i>381-322 гг. до н. э.</i>) <i>C, Q, J, PJ</i>."],
  ["demoveo", "<big>demoveo</big>\n\n<b>dē–moveo, mōvī, mōtum, ēre</b>\n<b>1)</b> сдвигать, отодвигать (manu aliquid <i>C</i>); оттеснять, вытеснять, прогонять (d. aliquem loco <i>или</i> de loco <i>C</i>; d. aliquem de suis possessionibus <i>и</i> ex aliqua possessione <i>C</i>): d. hostes gradu <i>L</i> сбить противника с позиции; d. animum alicujus de statu <i>C</i> привести кого-л. в замешательство (сбить с толку);\n<b>2)</b> удалять, устранять, снимать (centuriones, aliquem praefectura <i>T</i>);\n<b>3)</b> ссылать (aliquem in insulam <i>T</i>);\n<b>4)</b> отвращать, отводить, отворачивать (d. oculos ab aliquo <i>Ter</i>; culpam, odium ab aliquo <i>C</i>); отклонять, отговаривать (aliquem de sententia <i>C</i>); отвлекать (aliquem lucro <i>H</i>)."],
  ["dempsi", "<big>dempsi</big>\n\n<b>dēmpsī</b> <i>pf. к</i>\ndemo."],
  ["demptio", "<big>demptio</big>\n\n<b>dēmptio, ōnis</b> <i>f</i> [demo]\nотнятие (d. aut additio alicujus rei <i>Vr</i>)."],
  ["demptus", "<big>demptus</big>\n\n<b>dēmptus, a, um</b> <i>part. pf. к</i>\ndemo."],
  ["demugitus", "<big>demugitus</big>\n\n<b>dē–mūgītus, a, um</b> [mugio]\nоглашаемый мычанием (paludes <i>O</i>)."],
  ["demulceo", "<big>demulceo</big>\n\n<b>dē–mulceo, mulsī, mulsum (mulctum), ēre</b>\n<b>1)</b> гладить рукою, поглаживать (alicui caput <i>Ter</i>; dorsum, <i>sc.</i> equi <i>L</i>);\n<b>2)</b> лизать (lingua manus alicujus <i>AG</i>);\n<b>3)</b> ласкать, льстить (d. animum <i>AG</i>): motus et demulctus et captus <i>AG</i> глубоко тронутый и польщённый."],
  ["demum", "<big>demum</big>\n\n<b>dēmum</b>\n<b>1)</b> <i>при местоимениях</i>: именно (ille d. <i>C</i>; quae d. causae? <i>CC</i>): hac d. terra <i>V</i> в этой именно стране; ea d. firma amicitia est <i>Sl</i> в этом-то и состоит прочная дружба;\n<b>2)</b> лишь, только: suis d. oculis credere <i>Q</i> верить только своим глазам; decimo d. anno <i>O</i> лишь на десятом году; ita d., ut <i>Just</i> при том лишь условии, что; nunc d. <i>Pl, Ter, C etc.</i> теперь лишь; tum (tunc) d. <i>Pl, C etc.</i> тогда лишь; modo d. <i>Ter</i> недавно только; postera d. die <i>T</i> лишь на следующий день;\n<b>3)</b> <i>при перечислении</i>: (= denique) наконец: ut d. palam factum est <i>Su</i> когда (это) стало, наконец, общеизвестным."],
  ["demuneror", "<big>demuneror</big>\n\n<b>dē–mūneror, —, ārī</b> <i>depon.</i>\nбогато одарить, вознаградить (aliquem <i>Ter</i> — <i>v. l.</i>)."],
  ["demurmuro", "<big>demurmuro</big>\n\n<b>dē–murmuro, —, —, āre</b>\nпробормотать (aliquid magico ore <i>O</i>)."],
  ["demus", "<big>demus</big>\n\n<b>dēmus</b> <i>арх. LA, Pl</i>\n= demum."],
  ["demusso", "<big>demusso</big>\n\n<b>dē–musso, (āvī), ātum, āre</b>\nмолча терпеть, сносить (contumeliam <i>Ap</i>; injurias omnes <i>Amm</i>)."],
  ["demutabilis", "<big>demutabilis</big>\n\n<b>dēmūtābilis, e</b> [demuto]\nизменчивый (natura <i>Tert</i>): non d. <i>Eccl</i> неизменный."],
  ["demutassit", "<big>demutassit</big>\n\n<b>dēmūtassit</b> (= demutaverit) <i>Pl fut. II к</i>\ndemuto."],
  ["demutatio", "<big>demutatio</big>\n\n<b>dēmūtātio, ōnis</b> <i>f</i> [demuto]\nизменение, перемена: d. morum <i>C</i> перемена (ухудшение) нравов."],
  ["demutator", "<big>demutator</big>\n\n<b>dēmūtātor, ōris</b> <i>m</i>\nизменяющий, совершающий перемену <i>Ter</i>."],
  ["demutilo", "<big>demutilo</big>\n\n<b>dē–mutilo, —, —, āre</b>\nсрезать, подрезать (cacumina virgarum <i>Col</i>)."],
  ["demuto", "<big>demuto</big>\n\n<b>dē–mūto, āvī, ātum, āre</b>\n<b>1)</b> изменять (aliquam rem <i>Pl etc.</i>; nihil d. aliqua re <i>T</i>); переменить (sententiam suam <i>AG</i>);\n<b>2)</b> ухудшать, портить (si demutant mores ingenium tuum <i>Pl</i>); искажать, извращать (d. de veritate <i>Tert</i>);\n<b>3)</b> отличаться, разниться (ab aliqua re <i>Pl etc.</i>); меняться: non demutabo <i>Pl</i> так тому и быть;\n<b>4)</b> уклоняться, отклоняться (paululum ab aliqua re <i>Ap</i>);\n<b>5)</b> быть иным, обстоять иначе: numquid videtur d. atque ut quidem dixi? <i>Pl</i> разве дело обстоит иначе, чем я сказал?"],
  ["demuttio", "<big>demuttio</big>\n\n<b>dē–muttio, —, —, īre</b>\nчуть слышно говорить, шептать <i>Is</i>."],
  ["denarismus", "<big>denarismus</big>\n\n<b>dēnārismus, ī</b> <i>m</i> (<i>греч.</i>)\nплата в денариях <i>CTh</i>."],
  ["denarius", "<big>denarius</big>\n\n<b>I dēnārius, a, um</b> [deni]\nсостоящий из десяти (d. numerus digitorum <i>Vtr</i>); denariae fistulae <i>PM</i> трубы в 10 дюймов в диаметре.\n\n<b>II dēnārius, ī</b> <i>m</i> (<i>gen. pl.</i> <i>чаще</i> um) (<i>sc.</i> nummus)\nденарий:\n<b>1)</b> <i>римская серебряная монета, содержавшая сначала 10, а потом 16 ассов</i> (<i>4 сестерция</i>): ad denarium solvere <i>C, Col, Sen etc.</i> уплатить в денариях;\n<b>2)</b> <i>Macr</i> = as;\n<b>3)</b> драхма (<i>аттическая мера веса = 0,16 римск. унции</i>) <i>PM</i>."],
  ["denarro", "<big>denarro</big>\n\n<b>dē–nārro, āvī, ātum, āre</b>\nобстоятельно рассказать (alicui aliquid <i>Ter</i>; d. rem, sicut fuerat <i>AG</i>): d. alicui, ut... <i>H</i> рассказать кому-л., что (как)."],
  ["denascor", "<big>denascor</big>\n\n<b>dē–nāscor, —, nāscī</b> <i>depon.</i>\nотжить, умереть (quae nata sunt, ea omnia denasci ajunt <i>Vr</i>)."],
  ["denaso", "<big>denaso</big>\n\n<b>dē–nāso, —, —, āre</b> [nasus]\nлишать носа (d. os alicui <i>Pl</i>)."],
  ["denato", "<big>denato</big>\n\n<b>dē–nato, —, —, āre</b>\n<b>1)</b> плыть вниз по течению реки (Tusco alveo <i>H</i>);\n<b>2)</b> сплывать, стекать (a culmine <i>Aug</i>)."],
  ["dendrophorus", "<big>dendrophorus</big>\n\n<b>dendrophorus, ī</b> <i>m</i> (<i>греч.</i>)\n(<i>gen. pl.</i> orum <i>и</i> um)\n<b>1)</b> древоносец (<i>член особой коллегии жрецов Кибелы, которые в установленные праздники совершали шествия, неся срезанные кусты или ветви</i>) <i>CTh</i>;\n<b>2)</b> плотник <i>или</i> столяр <i>CTh</i>."],
  ["denecalis", "<big>denecalis</big>\n\n<b>dēnecālis, e</b> <i>v. l.</i>\n= denicalis."],
  ["denego", "<big>denego</big>\n\n<b>dē–nego, āvī, ātum, āre</b>\n<b>1)</b> отказывать наотрез (alicui aliquid <i>Cs, C etc.</i>): terra tibi fruges, amnis tibi deneget undas <i>O</i> земля да откажет тебе в плодах, а река — в воде; d. facere aliquid <i>Pl, Ter etc.</i> решительно отказываться (не желать) сделать что-л.;\n<b>2)</b> отпираться, отрицать: d. datum esse, quod datum est <i>Pl</i> отрицать, что дано то, что (в действительности) было дано."],
  ["deni", "<big>deni</big>\n\n<b>dēnī, ae, a</b> (<i>gen. pl.</i> ōrum <i>и</i> um) <i>num. distr.</i> [decem]\n<b>1)</b> по десяти: uxores habent deni duodenique inter se communes <i>Cs</i> каждые десять-двенадцать (из древних британцев) имеют общих жён; <i>реже</i> <i>sg.</i>: denaque luciferos Luna movebat equos <i>O</i> уж десятая луна правила лучезарными конями (<i>т. е.</i> была десятая ночь);\n<b>2)</b> <i>при <i>adv.</i> num.</i> десять: bis deni <i>V etc.</i> дважды десять; denis vicenisque annis <i>Sen</i> в течение тридцати лет."],
  ["denicalis", "<big>denicalis</big>\n\n<b>dēnicālis, e</b> [de + nex]\nпогребальный, смертный: feriae denicales <i>V etc.</i> поминальное торжество, тризна, поминки."],
  ["denigro", "<big>denigro</big>\n\n<b>dē–nigro, —, —, āre</b>\nчернить, делать чёрным, красить в чёрный цвет (lanam, capillum <i>PM</i>); <i>перен.</i> чернить, поносить (d. decus prioris famae <i>Eccl</i>)."],
  ["denique", "<big>denique</big>\n\n<b>dēnique</b>\n<b>1)</b> наконец (primum... deinde... tum... d. <i>C etc.</i>): d. non omnes eadem mirantur amantque <i>H</i> в конце концов не все восхищаются одним и тем же и не все любят одно и то же;\n<b>2)</b> вообще, даже: qui non civium non d. hominum numero sunt <i>L</i> (люди), которые не считаются ни гражданами, ни даже людьми вообще; d. omnia <i>C</i> <i>или</i> quidquid d. <i>CC</i> всё вообще;\n<b>3)</b> лишь, только: nunc d. <i>C</i> лишь теперь; tum d. <i>C</i> только тогда;\n<b>4)</b> именно, как раз (is d. honor <i>C</i>);\n<b>5)</b> в крайнем (<i>или</i> лучшем) случае: d., ut perliberaliter ageretur <i>C</i> в крайнем уж случае, если совсем расщедриться;\n<b>6)</b> одним словом: d. sit finis quaerendi <i>H</i> короче говоря, довольно заниматься стяжательством."],
  ["denixe", "<big>denixe</big>\n\n<b>dēnīxē</b> <i>Pl</i>\n(<i>v. l.</i>) = enixe."],
  ["denominatio", "<big>denominatio</big>\n\n<b>dēnōminātio, ōnis</b> <i>f</i> [denomino]\n<b>1)</b> наименование, называние, обозначение <i>CTh</i>;\n<b>2)</b> метонимия (<i>употребление слова в несобственном смысле, напр.</i> Juppiter <i>вместо</i> caelum, Mars <i>вместо</i> bellum <i>u m. n.</i>) <i>rhH, Q</i>."],
  ["denomino", "<big>denomino</big>\n\n<b>dē–nōmino, āvī, ātum, āre</b>\nименовать, называть (multa sunt non denominata <i>Q</i>): «parcus» ab eo, quod est «parum», denominatus est <i>AG</i> слово <i>parcus</i> (скупой, скудный) происходит от слова <i>parum</i> (мало)."],
  ["denormo", "<big>denormo</big>\n\n<b>dē–nōrmo, —, —, āre</b> [norma]\nпридавать косоугольную, <i>т. е.</i> неправильную форму: d. agellum <i>H</i> вдаваться в поле (<i>и тем нарушать правильность его формы</i>)."],
  ["denotatio", "<big>denotatio</big>\n\n<b>dē–notātio, ōnis</b> <i>f</i> [denoto]\n<b>1)</b> обозначение <i>Q, Tert</i>;\n<b>2)</b> брань, оскорбление <i>CTh</i>."],
  ["denotatus", "<big>denotatus</big>\n\n<b>I dēnotātus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> denoto;\n<b>2.</b> <i>adj.</i> оскорбительный, обидный (derisus <i>Tert</i>).\n\n<b>II dēnotātus, ūs</b> <i>m</i> <i>Tert</i>\n= denotatio 1."],
  ["denoto", "<big>denoto</big>\n\n<b>dē–noto, āvī, ātum, āre</b>\n<b>1)</b> отмечать, обозначать, помечать (aliquid creta <i>PM</i>);\n<b>2)</b> делать заметным (metum alicujus <i>T</i>);\n<b>3)</b> клеймить, позорить (aliquem omni probro <i>Su</i>)."],
  ["dens", "<big>dens</big>\n\n<b>dēns, dentis</b> <i>m</i> (<i>gen. pl.</i> <i>иногда</i> um)\n<b>1)</b> зуб: dentes adversi <i>C</i>, <i>тж.</i> dentes qui secant <i>CC</i> передние зубы; dentes pueriles <i>Sen</i> молочные зубы; d. eburneus <i>L</i> слоновый бивень; d. Indus <i>O</i> (Libycus <i>Prp</i>) слоновая кость; dentes empti <i>M</i> вставные зубы; d. invidus (ater) <i>H</i> <i>или</i> maledicus <i>C</i> зависть, злословие; albis dentibus deridere aliquem <i>погов. Pl</i> открыто смеяться над кем-л.; venire alicui sub dentem <i>погов. Pt</i> попасться кому-л. на зуб;\n<b>2)</b> зубец, остриё, кончик, крючок: d. ancorae <i>V</i> лапа якоря; d. clavi <i>Tib</i> бородка ключа; dentes pectinis <i>Vr</i> (serrae <i>Vtr, PM</i>) зубья гребня (пилы); densus d. <i>Tib</i> частый гребень; d. Saturni <i>V</i> серп Сатурна (садовый нож); d. uncus <i>V</i> мотыга; d. aratri <i>Col</i> сошник, лемех."],
  ["densabilis", "<big>densabilis</big>\n\n<b>dēnsābilis, e</b> [denso] <i>мед.</i>\nзакрепляющий (cibus <i>CA</i>)."],
  ["densatio", "<big>densatio</big>\n\n<b>dēnsātio, ōnis</b> <i>f</i>\nуплотнение, сгущение <i>PM, CA</i>."],
  ["densativus", "<big>densativus</big>\n\n<b>dēnsātīvus, a, um</b> <i>CA</i>\n= densabilis."],
  ["dense", "<big>dense</big>\n\n<b>dēnsē</b> [densus]\n<b>1)</b> плотно, густо (densius caesae alni <i>PM</i>; densissime calcare aliquid <i>Vtr</i>);\n<b>2)</b> (во <i>времени</i>) часто, непрерывно <i>C, CA, Amm</i>: nulla subeunt mihi tempora densius illis <i>O</i> ни одна пора (моей жизни) не вспоминается чаще, чем эта."],
  ["Denseletae", "<big>Denseletae</big>\n\n<b>Dēnsēlētae, ārum</b> <i>m</i> <i>C</i>\n= Dentheleti."],
  ["denseo", "<big>denseo</big>\n\n<b>dēnseo, —, ētum, ēre</b>\n<b>1)</b> сгущать, уплотнять (tenebrae densentur <i>V</i>); заволакивать тучами (caelum densetur <i>O</i>); скучивать, смыкать (agmina densentur campis <i>V</i>);\n<b>2)</b> нагромождать, <i>т. е.</i> непрерывно метать (hastilia <i>V</i>); часто наносить (ictus <i>T</i>): mixta senum ac juvenum densentur funera <i>H</i> непрерывно хоронят одного за другим то стариков, то юношей."],
  ["densitas", "<big>densitas</big>\n\n<b>dēnsitās, ātis</b> <i>f</i> [densus]\n<b>1)</b> густота, плотность (umoris <i>PM</i>; nemorum <i>Amm</i>); густая масса (nubium <i>Ap</i>; hominum <i>Amm</i>);\n<b>2)</b> частота, частая повторяемость (sententiarum <i>Q</i>)."],
  ["densito", "<big>densito</big>\n\n<b>dēnsito, —, ātum, āre</b> [<i>intens. к</i> denso]\nрасставлять сомкнутыми массами (densitatae acies <i>Amm</i>)."],
  ["denso", "<big>denso</big>\n\n<b>dēnso, āvī, ātum, āre</b> [densus]\n<b>1)</b> сгущать, уплотнять (d. quae rara erant <i>V</i>); сбивать (lac in butyrum <i>PM</i>): densari in salem <i>PM</i> давать осадок соли (кристаллизоваться);\n<b>2)</b> <i>воен.</i> плотно смыкать (catervas <i>V</i>; scuta super capita <i>L</i>);\n<b>3)</b> нагромождать, скоплять (figuras in oratione <i>Q</i>);\n<b>4)</b> плотно заполнять (loca <i>Calp</i>). — <i>См. тж.</i> denseo., ."],
  ["densus", "<big>densus</big>\n\n<b>dēnsus, a, um</b>\n<b>1)</b> густой (silva <i>Cs, C etc.</i>; umbra <i>Ctl</i>; <i>перен.</i> vox <i>Q</i>); густо заросший (nemus arboribus densum <i>O</i>); плотный (corpus <i>Lcr</i>; terra <i>C</i>); тёмный, непроглядный (nox <i>O</i>); частый, густо расположенный (foramina <i>O</i>; aristae <i>V</i>): plagae densae <i>H</i> частые сети;\n<b>2)</b> застывший, замёрзший (aequor <i>Lcr</i>);\n<b>3)</b> частый, многократный, непрерывный (ictus <i>V</i>; pericula <i>O</i>);\n<b>4)</b> плотно сомкнутый (hostes <i>V</i>);\n<b>5)</b> сжатый (Thucydides d. et brevis <i>Q</i>);\n<b>6)</b> стойкий, упорный, суровый (densa frigoris asperitas <i>O</i>)."],
  ["dentalia", "<big>dentalia</big>\n\n<b>dentālia, ium</b> <i>n</i> [dens]\n<b>1)</b> рассоха (<i>у плуга</i>) <i>V, Col</i>;\n<b>2)</b> сошник, лемех <i>Pers</i>."],
  ["dentaria", "<big>dentaria</big>\n\n<b>dentāria, ae</b> <i>f</i> [dens]\n(<i>sc.</i> herba) белена <i>Ap</i>."],
  ["dentarius", "<big>dentarius</big>\n\n<b>dentārius, ī</b> <i>m</i> [dens]\nлютик ядовитый (<i>Ranunculus sceleratus</i>, L.) <i>Ap</i>."],
  ["dentarpaga", "<big>dentarpaga</big>\n\n<b>dentarpaga</b> <i>v. l.</i>\n= dentharpaga."],
  ["dentatus", "<big>dentatus</big>\n\n<b>dentātus, a, um</b> [dens]\n<b>1)</b> имеющий зубы, зубастый (homo male d. <i>O</i>);\n<b>2)</b> с зубцами, зазубренный, зубчатый (serrula <i>C</i>): dentata manu capillos ducere <i>Pt</i> причесать волосы пальцами (пятернёй);\n<b>3)</b> колкий, язвительный (vir <i>Pl</i>);\n<b>4)</b> лощёный (charta <i>C</i>)."],
  ["dentex", "<big>dentex</big>\n\n<b>dentex, icis</b> <i>m</i> [dens]\nзубатка, <i>род морской рыбы</i> (<i>Sparus dentex</i>, Oken) <i>Col</i>."],
  ["Denthalias", "<big>Denthalias</big>\n\n<b>Denthāliās, ātis</b> [Denthalii]\nденталийский (ager <i>T</i>)."],
  ["Denthalii", "<big>Denthalii</big>\n\n<b>Denthāliī, ōrum</b> <i>m</i>\nДенталии, <i>город в Пелопоннесе на лаконско-мессенской границе</i> (<i>см.</i> Denthalias)."],
  ["dentharpaga", "<big>dentharpaga</big>\n\n<b>dentharpaga, ae</b> <i>f</i> [dens + <i>греч.</i> harpage]\nщипцы для удаления зубов <i>Vr</i>."],
  ["Dentheleti", "<big>Dentheleti</big>\n\n<b>Denthēlētī, ōrum</b> <i>m</i>\n<i>народность в западной Фракии</i> (<i>в верхнем течении Стримона</i>) <i>L</i>."],
  ["denticulatus", "<big>denticulatus</big>\n\n<b>denticulātus, a, um</b> [denticulus]\nзубчатый, зазубренный (falx, concha <i>PM</i>)."],
  ["denticulus", "<big>denticulus</big>\n\n<b>denticulus, ī</b> <i>m</i> [<i>demin. к</i> dens]\n<b>1)</b> зубок <i>Ap, Pall</i>;\n<b>2)</b> <i>архит.</i> зубец, зубчик, зубчатый орнамент (<i>на колонне</i>) <i>Vtr</i>."],
  ["dentiducum", "<big>dentiducum</big>\n\n<b>denti–dūcum, ī</b> <i>n</i> [dens + duco] <i>CA</i>\n= dentharpaga."],
  ["dentifer", "<big>dentifer</big>\n\n<b>denti–fer, fera, ferum</b> [dens + lero]\nзубчатый, с зубьями (rastri <i>Enn</i>)."],
  ["dentifrangibula", "<big>dentifrangibula</big>\n\n<b>dentifrangibula, ōrum</b> <i>n</i> [dens + frango]\n(<i>sc.</i> instrumenta) <i>шутл.</i> зубодробилки, <i>т. е.</i> кулаки <i>Pl</i>."],
  ["dentifrangibulus", "<big>dentifrangibulus</big>\n\n<b>I dentifrangibulus, a, um</b> [dens + frango]\nзубодробительный <i>Pl</i>.\n\n<b>II dentifrangibulus, ī</b> <i>m</i> [dens + frango]\nзубодробитель <i>Pl</i>."],
  ["dentifricium", "<big>dentifricium</big>\n\n<b>denti–fricium, ī</b> <i>n</i> [dens + frico]\nзубной порошок <i>PM, M, Ap</i>."],
  ["dentilegus", "<big>dentilegus</big>\n\n<b>denti–legus, ī</b> <i>m</i> [dens + lego] <i>шутл.</i>\nсобиратель зубов (<i>т. е.</i> выбитых): dentilegum aliquem facere <i>Pl</i> вышибать кому-л. зубы."],
  ["dentio", "<big>dentio</big>\n\n<b>dentio, —, —, īre</b> [dens]\n<b>1)</b> получать зубы (<i>о ребёнке</i>): puer tarde dentiens <i>PM</i> ребёнок с поздно прорезывающимися зубами;\n<b>2)</b> (<i>о зубах</i>) прорезываться (dentes dentiunt <i>Pl</i>)."],
  ["dentiscalpium", "<big>dentiscalpium</big>\n\n<b>dentiscalpium, ī</b> <i>n</i> [dens + scalpo]\nзубочистка <i>M</i>."],
  ["dentitio", "<big>dentitio</big>\n\n<b>dentītio, ōnis</b> <i>f</i> [dentio]\nпрорезывание зубов <i>PM</i>."],
  ["dentix", "<big>dentix</big>\n\n<b>dentīx (dentrīx), īcis</b> <i>и</i> <b>is</b>\n= dentex."],
  ["denubo", "<big>denubo</big>\n\n<b>dē–nūbo, nūpsī, nūptum, ere</b>\nвыходить замуж (alicui <i>Ap</i>; in domum alicujus <i>T</i>)."],
  ["denudatio", "<big>denudatio</big>\n\n<b>dēnūdātio, ōnis</b> <i>f</i> [denudo]\n<b>1)</b> обнажение (capitis <i>Is</i>);\n<b>2)</b> разоблачение, раскрытие (alicujus rei <i>Vlg, Eccl</i>)."],
  ["denudo", "<big>denudo</big>\n\n<b>dē–nūdo, āvī, ātum, āre</b>\n<b>1)</b> обнажать (aliquem <i>C, Su etc.</i>); обнаруживать (inscientiam alicujus <i>Vr</i>); открывать (consilium suum alicui <i>L</i>);\n<b>2)</b> обобрать, ограбить (aliquem <i>C, Vlg</i>)."],
  ["denumeratio", "<big>denumeratio</big>\n\n<b>dēnumerātio, ōnis</b> <i>f</i>\nисчисление, подсчет <i>Dig</i>."],
  ["denumero", "<big>denumero</big>\n\n<b>dē–numero, āvī, ātum, āre</b>\n<b>1)</b> подсчитывать, исчислять (vernas suos <i>Eccl</i>);\n<b>2)</b> отсчитывать, выплачивать (argentum <i>Pl</i>)."],
  ["denuntiatio", "<big>denuntiatio</big>\n\n<b>dēnūntiātio, ōnis</b> <i>f</i> [denuntio]\n<b>1)</b> объявление (belli <i>C</i>); уведомление, извещение (periculi <i>C</i>);\n<b>2)</b> угроза (d. ingentis terroris <i>T</i>; denantiatione conterritus <i>L</i>);\n<b>3)</b> показание (accusatoris <i>Su</i>);\n<b>4)</b> приглашение дать показание (d. testimonii <i>C</i>);\n<b>5)</b> распоряжение (senatus <i>L</i>);\n<b>6)</b> предупреждение, предваряющее извещение (d. calamitatum <i>C</i>)."],
  ["denuntiativus", "<big>denuntiativus</big>\n\n<b>dēnūntiātīvus, a, um</b> <i>мед.</i>\nпоказательный, симптоматический (motus <i>CA</i>)."],
  ["denuntio", "<big>denuntio</big>\n\n<b>dē–nūntio, āvī, ātum, āre</b>\n<b>1)</b> давать знать, уведомлять (alicui aliquid <i>C etc.</i>); объявлять (d. bellum populo Romano <i>C</i>);\n<b>2)</b> угрожать (d. alicui catenas <i>C</i>; alicui mortem <i>Sen</i>; manu sublata d. <i>Q</i>);\n<b>3)</b> предвещать (mortem alicujus <i>C</i>; nigram horam alicui <i>Tib</i>; pluviam <i>V</i>);\n<b>4)</b> отдать распоряжение, предписать (d. ut arma capiant <i>L</i>): d. alicui testimonium <i>C</i> требовать от кого-л. свидетельского показания."],
  ["denuo", "<big>denuo</big>\n\n<b>dēnuō</b> [<i>из</i> de novo]\nзаново (urbes subversas condere <i>Su</i>); ещё раз, снова (dicere <i>Pl</i>); вновь, опять (recitare <i>C</i>; redire <i>Pl</i>)."],
  ["denus", "<big>denus</big>\n\n<b>dēnus, a, um</b> <i>Sil sg. к</i>\ndeni."],
  ["Deo", "<big>Deo</big>\n\n<b>Dēō, ūs</b> <i>f</i> (<i>греч.</i>, <i>поэт.</i>)\n= Ceres I (<i>см. тж.</i> Deois)."],
  ["deocco", "<big>deocco</big>\n\n<b>de–occo, —, —, āre</b>\nборонить <i>PM</i>."],
  ["Deois", "<big>Deois</big>\n\n<b>Dēōis, idis</b> <i>f</i>\nДеоида, дочь Цереры, <i>т. е.</i> Прозерпина <i>O</i>."],
  ["Deoius", "<big>Deoius</big>\n\n<b>Dēōius</b> <i>и</i> <b>Dēōus, a, um</b> [Deo]\nпосвященный Церере <i>O</i>."],
  ["deonero", "<big>deonero</big>\n\n<b>de–onero, āvī, ātum, āre</b>\n<b>1)</b> разгружать (naves <i>Amm</i>);\n<b>2)</b> облегчать, убавлять (aliquid ex invidia alicujus <i>C</i>)."],
  ["deoperio", "<big>deoperio</big>\n\n<b>de–operio, operuī, opertum, īre</b>\n<b>1)</b> обнажать (caput <i>VM</i>);\n<b>2)</b> вскрывать (tellurem <i>PM</i>)."],
  ["deorsum", "<big>deorsum</big>\n\n<b>deorsum</b> [de + vorsum]\n<b>1)</b> вниз: sursum (ac) d. <i>Ter, C, Sen</i> вверх и вниз;\n<b>2)</b> внизу <i>Pl, Ter, Vr</i>."],
  ["deorsus", "<big>deorsus</big>\n\n<b>deorsus</b> (= deorsum)\nвниз (tunicam d. ad pedes dejectus <i>Ap</i>)."],
  ["deosculor", "<big>deosculor</big>\n\n<b>de–ōsculor, ātus sum, ārī</b> <i>depon.</i>\n<b>1)</b> крепко целовать, расцеловать (aliquem <i>Pl, Ap</i>; dexteram alicujus <i>VM</i>);\n<b>2)</b> превозносить, хвалить (ingenium alicujus <i>AG</i> — <i>v. l.</i>)."],
  ["depaciscor", "<big>depaciscor</big>\n\n<b>dē–pacīscor, pactus sum, cīscī</b> <i>v. l.</i>\n= depeciscor."],
  ["depactus", "<big>depactus</big>\n\n<b>dēpactus, a, um</b> <i>part. pf. к</i>\n<b>1)</b> depaciscor <i>и</i>\n<b>2)</b> depango."],
  ["depalator", "<big>depalator</big>\n\n<b>dēpālātor, ōris</b> <i>m</i> [depalo I]\nустановитель вех, <i>т. е.</i> основатель (disciplinae divinae <i>Tert</i>)."],
  ["depalmo", "<big>depalmo</big>\n\n<b>dē–palmo, —, —, āre</b> [palma]\nнадавать оплеух, шлепков (d. aliquem <i>Labeo ap. AG</i>)."],
  ["depalo", "<big>depalo</big>\n\n<b>I dē–pālo, —, —, āre</b> [palus I]\nводружать вехи, <i>т. е.</i> основывать (civitatem <i>Tert</i>).\n\n<b>II dē–palo, —, —, āre</b> [palam I]\nраскрывать, разоблачать (aliquid <i>Eccl</i>)."],
  ["depango", "<big>depango</big>\n\n<b>dē–pango, —, pāctum, ere</b>\nвтыкать, вколачивать, вбивать (aliquid in terram <i>PM</i>): vitae depactus terminus <i>Lcr</i> поставленный жизни предел."],
  ["deparcus", "<big>deparcus</big>\n\n<b>dē–parcus, a, um</b>\nскупой, скаредный <i>Su</i>."],
  ["depasco", "<big>depasco</big>\n\n<b>dē–pāsco, pāvī, pāstum, ere</b>\n<b>1)</b> съедать траву, потравить, объедать (поля) (luxuriem segetum <i>V</i>): d. altaria <i>V</i> съесть то, что было возложено на алтари; d. summa Lycaei <i>V</i> пастись на вершине Ликея;\n<b>2)</b> дать поесть, засыпать в виде корма, скармливать (farraginem <i>Col</i>);\n<b>3)</b> устранять, удалять, уничтожать: aliquid stilo d. <i>Col</i> вычёркивать что-л. — <i>См. тж.</i> depascor."],
  ["depascor", "<big>depascor</big>\n\n<b>dē–pāscor, pāstus sum, scī</b> <i>depon.</i>\n<b>1)</b> съедать траву, потравить (segetes <i>PM</i>; florem salicti <i>V</i>);\n<b>2)</b> съедать, пожирать (miseros artus <i>V</i>);\n<b>3)</b> истощать, изнурять, снедать (febris depascitur artus <i>V</i>);\n<b>4)</b> жадно читать <i>или</i> слушать (d. aurea dicta, <i>sc.</i> Epicuri <i>Lcr</i>)."],
  ["depastio", "<big>depastio</big>\n\n<b>dēpāstio, ōnis</b> <i>f</i> [depascor]\nпоедание, объедание (пастбищ), потрава, стравливание (скоту) <i>PM</i>."],
  ["depavi", "<big>depavi</big>\n\n<b>dēpāvī</b> <i>pf. к</i>\ndepasco."],
  ["depavitus", "<big>depavitus</big>\n\n<b>dē–pavītus, a, um</b> [pavio]\nутоптанный, выровненный <i>Sol</i> (<i>v. l. к</i> depastus)."],
  ["depeciscor", "<big>depeciscor</big>\n\n<b>dē–pecīscor (dēpacīscor), pectus sum, scī</b> [paciscor]\n<b>1)</b> условиться, договориться (ad condicionem alicujus <i>C</i>): jam d. morte cupere <i>Ter</i> желать уже договориться со смертью (<i>т. е.</i> желать себе скорейшей смерти);\n<b>2)</b> выговорить себе, обусловить (d. cum aliquo partem suam <i>C</i>; d. tria praedia sibi <i>C</i>)."],
  ["depectio", "<big>depectio</big>\n\n<b>dēpectio, ōnis</b> <i>f</i> [depeciscor]\nдоговор, соглашение <i>CTh</i>."],
  ["depecto", "<big>depecto</big>\n\n<b>dē–pecto, pēxī, pexum, ere</b>\n<b>1)</b> зачёсывать, причёсывать, расчёсывать (crines <i>O</i>; barbam <i>Sen</i>);\n<b>2)</b> чесать лён (ars depectendi lini <i>PM</i>);\n<b>3)</b> собирать (vellera foliis <i>V</i>);\n<b>4)</b> <i>перен.</i> поколотить (adeo depexus, ut meminerit semper mei <i>Ter</i>)."],
  ["depector", "<big>depector</big>\n\n<b>dēpector, ōris</b> <i>m</i> [depeciscor]\nдоговаривающийся (о чём-л.), <i>т. е.</i> поверенный, стряпчий (d. litium <i>Ap</i>)."],
  ["depectus", "<big>depectus</big>\n\n<b>dēpectus, a, um</b> <i>part. pf. к</i>\ndepeciscor."],
  ["depeculator", "<big>depeculator</big>\n\n<b>dēpecūlātor, ōris</b> <i>m</i> [depeculor]\nграбитель, расхититель (aerarii <i>C</i>)."],
  ["depeculatus", "<big>depeculatus</big>\n\n<b>dēpecūlātus, (ūs)</b> <i>m</i> [depeculor]\nограбление, обкрадывание <i>Pl</i>."],
  ["depeculo", "<big>depeculo</big>\n\n<b>dēpecūlo, —, —, āre</b> <i>арх. LM</i>\n= depeculor."],
  ["depeculor", "<big>depeculor</big>\n\n<b>dē–pecūlor, ātus sum, ārī</b> <i>depon.</i> [peculium]\n<b>1)</b> ограбить, разграбить (domos <i>rhH</i>; fana <i>C</i>): aliquem omni argento d. <i>C</i> отобрать у кого-л. все деньги;\n<b>2)</b> отнимать, лишать (laudem, honorem <i>C</i>)."],
  ["depello", "<big>depello</big>\n\n<b>dē–pello, pulī, pulsum, ere</b>\n<b>1)</b> сталкивать, сбрасывать (d. defensores vallo <i>Cs</i>); сваливать (equitem dorso, <i>sc.</i> equi <i>H</i>); свергать, низвергать (simulacra deorum <i>C</i>): d. aliquem loco <i>и</i> de loco <i>C</i> (gradu <i>Nep</i>) сбить кого-л. с места (с позиции);\n<b>2)</b> гнать (ovium fetus <i>V</i>): aquam de agro d. <i>Cato</i> очистить поле от воды;\n<b>3)</b> выгонять, изгонять (d. aliquem ex urbe <i>C</i> <i>и</i> urbe <i>T</i>); смещать (aliquem tribunatu <i>C</i>); исключать (aliquem senatu <i>T</i>); прогонять (d. praesidia barbarorum ex his regionibus <i>Nep</i>): injuria depulsus <i>Pt</i> оскорбительно прогнанный;\n<b>4)</b> загонять (adversante vento portum depelli <i>T</i>);\n<b>5)</b> отстранять, удалять, прекращать (latrocinia <i>C</i>); aliquem d. a matre <i>и</i> a mamma <i>Vr</i>; <i>тж.</i> ab ubere matris <i>и</i> a lacte <i>V</i> отнять кого-л. от груди;\n<b>6)</b> отвести, отбросить (omnes molestias, suspicionem a se <i>C</i>): spe (de spe <i>C</i>) depulsus <i>L</i> утративший надежду;\n<b>7)</b> утолять (famem sitimque <i>Sen</i>);\n<b>8)</b> отвращать, отклонять (periculum <i>C</i>; morbos <i>Cs, C</i>); отговаривать, разубеждать (d. aliquem de causa suscepta <i>C</i>; d. sententia <i>C</i>; a superioribus consiliis <i>Cs</i>): d. alicui multam <i>C</i> избавить кого-л. от штрафа; di depellentes (<i>ср.</i> Averruncus) <i>Pers</i> спасающие (от зол) боги."],
  ["dependeo", "<big>dependeo</big>\n\n<b>dē–pendeo, —, —, ēre</b>\n<b>1)</b> висеть, свисать, ниспадать (ex umeris <i>V</i>): malo d. <i>Su</i> быть подвешенным к мачте; d. collo alicujus <i>Sen</i> повиснуть у кого-л. на шее; nec d., nec propendere <i>Pl</i> не весить (<i>т. е.</i> не быть) ни больше, ни меньше;\n<b>2)</b> вызываться, причиняться, зависеть (d. ex hoc malo <i>Sen</i>; a veniente die <i>O</i>);\n<b>3)</b> возникать, происходить: origine alicujus verbi d. <i>O</i> этимологически происходить от какого-л. слова;\n<b>4)</b> склоняться, клониться (in deteriora <i>Sen</i>)."],
  ["dependo", "<big>dependo</big>\n\n<b>dē–pendo, pendī, pēnsum, ere</b>\n<b>1)</b> платить, уплачивать (alicui pecuniam <i>Sen</i>; mercedem alicui <i>Ap</i>);\n<b>2)</b> искупать: rei publicae d. poenas <i>C</i> искупить свою вину перед государством, <i>т. е.</i> понести наказание;\n<b>3)</b> тратить, расходовать (tempora amori <i>Lcn</i>): aliter incassum dependitur opera <i>Col</i> иначе труд пропадёт даром; felicibus armis dependisse caput <i>Lcn</i> пожертвовать жизнью ради победы."],
  ["dependulus", "<big>dependulus</big>\n\n<b>dēpendulus, a, um</b> [dependeo]\nниспадающий (crines cervice dependuli <i>Ap</i>)."],
  ["depennatus", "<big>depennatus</big>\n\n<b>dēpennātus, a, um</b> <i>v. l.</i>\n= depinnatus."],
  ["depensio", "<big>depensio</big>\n\n<b>dēpēnsio, ōnis</b> <i>f</i> [dependo]\nтрата, расход <i>CTh</i>."],
  ["deperditus", "<big>deperditus</big>\n\n<b>dēperditus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> deperdo;\n<b>2.</b> <i>adj.</i> пропащий, нечестивый <i>AG</i>."],
  ["deperdo", "<big>deperdo</big>\n\n<b>dē–perdo, didī, ditum, ere</b>\n<b>1)</b> терять, лишаться, утрачивать (usum linguae <i>O</i>; bona <i>C</i>; purpureos deperdit terra colores <i>Tib</i>);\n<b>2)</b> губить, разорять: deperditus fletu <i>Ctl</i> обессилевший от плача; deperditus alicujus amore <i>VM, Su</i> (<i>или</i> d. aliqua <i>Prp</i> <i>и</i> in aliqua <i>Ctl</i>) без памяти влюбившийся в кого-л.; inopia deperditus <i>Ph</i> доведённый до нищеты, обнищавший."],
  ["depereo", "<big>depereo</big>\n\n<b>dē–pereo, iī, (itum), īre</b> [<i>pass. к</i> deperdo]\nпогибать, пропадать (naves deperierunt <i>Cs</i>; igne d. <i>PM</i>): d. in usu <i>Col</i> изнашиваться; d. amore alicujus <i>L etc.</i>, d. in aliquo <i>QC</i> <i>или</i> d. aliquem (aliquam) <i>Pl, Ter, Ctl etc.</i> быть без памяти влюблённым в кого-л.; quid deperit legibus (<i>dat.</i>) si...? <i>Tert</i> какой ущерб потерпят законы, если...?"],
  ["deperiet", "<big>deperiet</big>\n\n<b>dēperiet</b>\n<i>Vlg, GT</i> = deperibit (<i>fut. к</i> depereo)."],
  ["depetigo", "<big>depetigo</big>\n\n<b>dē–petīgo, inis</b> <i>f</i>\nпарша <i>или</i> лишай <i>C etc.</i>"],
  ["depeto", "<big>depeto</big>\n\n<b>dē–peto, —, —, ere</b>\nвыпрашивать <i>Tert</i>."],
  ["depexus", "<big>depexus</big>\n\n<b>dēpexus, a, um</b> <i>part. pf. к</i>\ndepecio."],
  ["depictio", "<big>depictio</big>\n\n<b>dēpictio, ōnis</b> <i>f</i> [depingo]\nрисование, изображение <i>Boët</i>."],
  ["depictus", "<big>depictus</big>\n\n<b>dēpictus, a, um</b> <i>part. pf. к</i>\ndepingo."],
  ["depilis", "<big>depilis</big>\n\n<b>dē–pilis, e</b> [pilus]\nбезволосый (genae <i>Ap</i>)."],
  ["depilo", "<big>depilo</big>\n\n<b>dē–pilo, āvī, ātum, āre</b> [pilus]\nлишать волос (aliquem <i>Sen etc.</i>); ощипывать (struthiocamelum <i>Sen</i>); <i>перен.</i> обирать (aliquem <i>LM</i>)."],
  ["depingo", "<big>depingo</big>\n\n<b>dē–pingo, pīnxī, pictum, ere</b>\n<b>1)</b> писать красками, расписывать (navis eleganter depicta <i>Ap</i>);\n<b>2)</b> описывать, изображать (aliquem, vitam alicujus <i>C</i>); живописать (pugnam Marathoniam <i>Nep</i>);\n<b>3)</b> представлять себе, воображать (aliquid cogitatione <i>C</i>);\n<b>4)</b> вышивать, заткать (auro depicta chlamys <i>VF</i>);\n<b>5)</b> подкрашивать (faciem cerussa <i>Hier</i>)."],
  ["depinnatus", "<big>depinnatus</big>\n\n<b>dē–pinnātus, a, um</b> [pinna]\nоперённый, пернатый, <i>перен.</i> окрылённый, возвышенный (orationis eloquium <i>Vr</i>)."],
  ["depinxti", "<big>depinxti</big>\n\n<b>dēpīnxtī</b>\n<i>Pl</i> = depinxisti (<i>pf. к</i> depingo)."],
  ["deplango", "<big>deplango</big>\n\n<b>dē–plango, plānxī, plānctum, ere</b>\nоплакивать, скорбеть (d. aliquem <i>и</i> aliquid <i>O, Sen etc.</i>)."],
  ["deplano", "<big>deplano</big>\n\n<b>dē–plāno, āvī, ātum, āre</b>\nсглаживать, выровнять (montes <i>Lact</i>): d. vulnus <i>Veg</i> закрыть рану."],
  ["deplanto", "<big>deplanto</big>\n\n<b>dē–planto, āvī, ātum, āre</b>\n<b>1)</b> отламывать (ramum <i>Col</i>);\n<b>2)</b> пересаживать с корнями (vitis deplantatur <i>PM</i>)."],
  ["depleo", "<big>depleo</big>\n\n<b>dē–pleo, plēvī, plētum, ēre</b>\nвычерпывать, опорожнять (fontes digno haustu <i>St</i>); переливать (oleum <i>Cato</i>): d. sanguinem <i>PM</i> пускать кровь; vitam supervacuis querelis d. <i>Man</i> изгнать из жизни бесполезные жалобы."],
  ["deplexus", "<big>deplexus</big>\n\n<b>dē–plexus, a, um</b> [plecto]\nобвивший, охвативший, вцепившийся (leae <i>Lcr</i>)."],
  ["deplorabundus", "<big>deplorabundus</big>\n\n<b>dēplōrābundus, a, um</b> [deploro]\nгорько плачущий, оплакивающий, предающийся скорби (homo <i>Pl</i>)."],
  ["deploratio", "<big>deploratio</big>\n\n<b>dēplōrātio, ōnis</b> <i>f</i> [deploro]\nоплакивание <i>Sen, Tert</i>."],
  ["deploro", "<big>deploro</big>\n\n<b>dē–plōro, āvī, ātum, āre</b>\n<b>1)</b> горько плакать, горевать (lamentabili voce <i>C</i>; de aliqua re <i>C</i>);\n<b>2)</b> оплакивать (aliquid <i>C, L, Sen etc.</i>); <i>перен.</i> отчаяться, считать потерянным, безнадёжным, погибшим (nomen Romanum <i>C</i>; spem Capuae retinendae <i>L</i>): deploratus a medicis <i>PM</i> признанный врачами безнадёжно больным; deplorata tussis <i>PM</i> отчаянный кашель;\n<b>3)</b> источать соки (vites deplorando vim roboris sui avertunt fructu <i>Pall</i>)."],
  ["deplumatus", "<big>deplumatus</big>\n\n<b>dē–plūmātus, a, um</b> <i>Is</i>\n= deplumis."],
  ["deplumis", "<big>deplumis</big>\n\n<b>dēplūmis, e</b> [pluma]\nбеспёрый (hirundo <i>PM</i>)."],
  ["depluo", "<big>depluo</big>\n\n<b>dē–pluo, —, —, ere</b>\nпадать дождём (lapis depluit <i>Tib</i>)."],
  ["depoculo", "<big>depoculo</big>\n\n<b>dē–poculo, —, —, āre</b> [poculum]\nпропить, прокутить <i>LM</i>."],
  ["depolio", "<big>depolio</big>\n\n<b>dē–polio, (īvī), ītum, īre</b>\n<b>1)</b> выстрогать, сделать гладким, отполировать (aliquid cote <i>PM</i>);\n<b>2)</b> <i>ирон.</i> высечь, поколотить (d. virgis dorsum alicujus <i>Pl</i>)."],
  ["depolitio", "<big>depolitio</big>\n\n<b>dēpolītio, ōnis</b> <i>f</i> [depolio]\nпрелестное владение, поместье <i>Vr</i>."],
  ["depompatio", "<big>depompatio</big>\n\n<b>dēpompātio, ōnis</b> <i>f</i> [depompo]\nлишение былой пышности, бесчестье, позор <i>Hier</i>."],
  ["depompo", "<big>depompo</big>\n\n<b>dē–pompo, —, —, āre</b> [pompa]\nлишать прежней красы, бесчестить <i>Hier</i>."],
  ["depondero", "<big>depondero</big>\n\n<b>dē–pondero, —, —, āre</b> [pondus]\nтянуть (своей тяжестью) вниз <i>Pt</i>."],
  ["deponens", "<big>deponens</big>\n\n<b>dēpōnēns, entis</b> [depono] <i>грам.</i>\nотложительный (verbum)."],
  ["depono", "<big>depono</big>\n\n<b>dē–pōno, posuī</b> (<b>posīvī</b> <i>Pl, Ctl</i>), <b>positum, ere</b>\n<b>1)</b> класть, положить (caput terrae <i>O</i>): d. aliquem vino <i>шутл. Pl</i> мертвецки напоить кого-л. || складывать, откладывать (onus <i>C</i>): d. jumentis onera <i>Cs</i> разгружать вьючных животных; corpora sub ramis d. <i>V</i> улечься в тени ветвей; d. arma <i>Cs</i> сложить оружие (<i>т. е.</i> признать себя побеждённым), <i>но тж.</i> <i>L, V etc.</i> снимать с себя (откладывать в сторону) оружие; d. animam <i>Nep</i> испустить дух, умереть || снимать (personam <i>C, Ap</i>; cadaver de cruce <i>Vlg</i>);\n<b>2)</b> возлагать (coronam in aram Apollinis <i>L</i>);\n<b>3)</b> слагать с себя (imperium <i>Cs, Su</i>; magistratum <i>Cs</i>; tutelam <i>L</i>); отказываться, оставлять (simultates <i>C</i>; consilium adeundae Syriae <i>Cs</i>; spem <i>H</i>); отбрасывать прочь (omnes molestias et sollicitudines <i>C</i>); отклонять (provinciam <i>C</i>; triumphum <i>L</i>);\n<b>4)</b> (<i>тж.</i> d. in terram <i>Just</i>) высаживать на берег (legiones <i>bAfr</i>);\n<b>5)</b> снимать, срезывать, состригать (crinem <i>T</i>; ungues et capillos <i>Pt</i>);\n<b>6)</b> сворачивать, убирать (vela <i>Sil</i>);\n<b>7)</b> сеять, бросать (semina sulco <i>Col</i>);\n<b>8)</b> сажать (malleolum in terram <i>Col</i>; plantam in hortis <i>O</i>);\n<b>9)</b> (<i>в качестве премии или ставки</i>) ставить (vitulam <i>V</i>);\n<b>10)</b> задерживать, затаивать (spiritum <i>Q</i>);\n<b>11)</b> производить на свет, рожать (onus naturae <i>Ph</i>; fetum <i>Ph</i>; Latoniam prope Deliam olivam <i>Ctl</i>);\n<b>12)</b> утолять (sitim <i>O</i>);\n<b>13)</b> (<i>тж.</i> d. memoriam alicujus rei <i>и</i> d. ex memoria aliquid <i>C</i>) предавать забвению: deposita omni offensa <i>Pt</i> забыв все обиды;\n<b>14)</b> отдавать на хранение (pecuniam apud aliquem <i>C</i>); укрывать, прятать (liberos, uxores suaque omnia in silvis <i>Cs</i>); <i>юр.</i> депонировать (est depositum quod custodiendum reddendumque traditur <i>Dig</i>); вверять: d. rimosa in aure aliquid <i>H</i> доверить что-л. дырявому уху, <i>т. е.</i> болтливому человеку;\n<b>15)</b> склонять, преклонять (colla <i>GT</i>);\n<b>16)</b> опрокидывать, сносить, разрушать (statuas alicujus <i>Spart</i>; aedes usque ad aream deposita <i>Dig</i>);\n<b>17)</b> погружать, топить (aliquem medio ponto <i>VF</i>);\n<b>18)</b> смещать с поста, увольнять (aliquem <i>Eccl</i>). — <i>См. тж.</i> depositus."],
  ["deponto", "<big>deponto</big>\n\n<b>dē–ponto, —, —, āre</b> [pons]\nлишать права голосования <i>Vr</i> (<i>мужчины, достигшие 60 лет, освобождались от участия в общественно-политической жизни, а потому не проходили по мосту, который вёл в comitium, где производилось голосование.</i>)"],
  ["depoposci", "<big>depoposci</big>\n\n<b>dēpoposcī</b> <i>pf. к</i>\ndeposco."],
  ["depopulatio", "<big>depopulatio</big>\n\n<b>dēpopulātio, ōnis</b> <i>f</i> [depopulor]\nопустошение, ограбление, разорение (publicorum operum <i>C</i>)."],
  ["depopulator", "<big>depopulator</big>\n\n<b>dēpopulātor, ōris</b> <i>m</i> [depopulor]\nопустошитель, разоритель <i>C</i>."],
  ["depopulo", "<big>depopulo</big>\n\n<b>dēpopulo, āvī, —, āre</b> <i>L, bH, Just, VF</i>\n= depopulor."],
  ["depopulor", "<big>depopulor</big>\n\n<b>dē–populor, ātus sum, ārī</b> <i>depon.</i>\nопустошать, грабить, разорять (agros <i>Cs, QC</i>; hostium fines <i>L, T</i>); расхищать (hereditates <i>Dig</i>)."],
  ["deportatio", "<big>deportatio</big>\n\n<b>dēportātio, ōnis</b> <i>f</i> [deporto]\n<b>1)</b> увоз, перевозка (<i>sc.</i> oleae <i>Cato</i>);&gt;;\n<b>2)</b> пожизненная ссылка, высылка, изгнание <i>Dig</i>."],
  ["deportatorius", "<big>deportatorius</big>\n\n<b>dēportātōrius, a, um</b>\nпредназначенный для перевозки (onus <i>CJ</i>)."],
  ["deporto", "<big>deporto</big>\n\n<b>dē–porto, āvī, ātum, āre</b>\n<b>1)</b> уносить, увозить <i>или</i> перевозить (frumentum in castra <i>Cs</i>; ossa alicujus in Cappadociam <i>Nep</i>): nihil ex tanta praeda domum suam deportavit <i>C</i> из столь большой добычи он не принёс домой (<i>т. е.</i> не получил, не взял) ничего;\n<b>2)</b> привозить, доставлять (exercitum e Graecia <i>L</i>);\n<b>3)</b> приводить (testes <i>C</i>);\n<b>4)</b> добиваться, получать (triumphum, cognomen <i>C</i>); стяжать (gloriam <i>QC</i>);\n<b>5)</b> ссылать, заточать (in insulam Amorgum <i>T</i>); d Italia <i>T</i> изгнать (выслать) из Италии."],
  ["deposco", "<big>deposco</big>\n\n<b>dē–posco, poposcī, —, ere</b>\n<b>1)</b> настоятельно просить, требовать (sibi consulatum <i>Su</i>): aliquid non modo non recusare, sed d. <i>C</i> не только не отказываться от чего-л., но и настойчиво просить (об этом); d. aliquem morti <i>T</i> <i>или</i> ad mortem <i>Cs</i> (ad supplicium <i>Hirt</i>) настойчиво требовать чьей-л. казни; d. aliquem in poenam <i>L</i>, ad poenam <i>Su</i> <i>или просто</i> d. aliquem <i>O</i> настаивать на чьём-л. наказании;\n<b>2)</b> вытребовать, требовать выдачи (d. Hannibalem <i>L</i>; d. auctorem culpae <i>L</i>);\n<b>3)</b> вызывать (на бой), бросать вызов (aliquem sibi <i>L</i>; hostes <i>VF</i>)."],
  ["depositarius", "<big>depositarius</big>\n\n<b>dēpositārius, ī</b> <i>m</i> [depono]\n<b>1)</b> депозитор, депонент, вкладчик (d. = qui deposuit <i>Dig</i>);\n<b>2)</b> хранитель вклада (d. = qui depositum suscepit <i>Dig</i>)."],
  ["depositio", "<big>depositio</big>\n\n<b>dēpositio, ōnis</b> <i>f</i> [depono]\n<b>1)</b> вклад, сдача на хранение (pecuniae <i>Dig, Veg</i>);\n<b>2)</b> показание, свидетельство (testium depositiones <i>CJ</i>);\n<b>3)</b> понижение, умаление (dignitatis <i>Dig</i>);\n<b>4)</b> снос, разрушение (aedificii <i>Dig</i>);\n<b>5)</b> отбрасывание (от себя) (carnis sordium <i>Aug</i>);\n<b>6)</b> <i>ритор.</i> депозиция, заключение, заключительная часть периода <i>Q</i>;\n<b>7)</b> (<i>тж.</i> d. a statu) смещение с поста <i>Eccl</i>;\n<b>8)</b> кончина <i>Eccl</i>."],
  ["depositor", "<big>depositor</big>\n\n<b>dēpositor, ōris</b> <i>m</i>\n<b>1)</b> депонент, вкладчик <i>Dig</i>;\n<b>2)</b> разрушитель, отрицатель <i>Eccl</i>."],
  ["depositum", "<big>depositum</big>\n\n<b>dēpositum, ī</b> <i>n</i>\nвещь, отданная на хранение, депозит <i>C, Dig, Pt</i>; <i>перен.</i> резерв (esse in deposito <i>Q</i>)."],
  ["depositus", "<big>depositus</big>\n\n<b>dēpositus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> depono;\n<b>2.</b> <i>adj.</i> (<i>так как умирающих или мертвецов было принято класть на землю — terra parens</i>) умирающий <i>или</i> умерший <i>V, O, Pt</i>; погибший (pars alicujus rei <i>C</i>)."],
  ["deposivi", "<big>deposivi</big>\n\n<b>dēposīvī</b>\n= deposui (<i>см.</i> depono)."],
  ["depostulator", "<big>depostulator</big>\n\n<b>dēpostulātor, ōris</b> <i>m</i> [depostulo]\nтребующий наказания <i>или</i> казни, обвинитель <i>Tert</i>."],
  ["depostulo", "<big>depostulo</big>\n\n<b>dē–postulo, —, —, āre</b>\nнастойчиво просить, настоятельно требовать (sibi auxilia <i>bH</i>)."],
  ["depostus", "<big>depostus</big>\n\n<b>dēpostus, a, um</b> <i>LM</i>\n= depositus."],
  ["depraedatio", "<big>depraedatio</big>\n\n<b>dēpraedātio, ōnis</b> <i>f</i>\nразграбление <i>CJ, Eccl</i>."],
  ["depraedator", "<big>depraedator</big>\n\n<b>dēpraedātor, ōris</b> <i>m</i>\nграбитель, расхититель <i>Aug</i>."],
  ["depraedo", "<big>depraedo</big>\n\n<b>dēpraedo, āvī, ātum, āre</b> <i>Eccl</i>\n= depraedor."],
  ["depraedor", "<big>depraedor</big>\n\n<b>dē–praedor, ātus sum, ārī</b> <i>depon.</i>\nразграбить, опустошить (agros <i>Just</i>; regionem <i>Ap</i>)."],
  ["depraesentiarum", "<big>depraesentiarum</big>\n\n<b>dē–praesentiārum</b> <i>adv.</i>\nтеперь же, в настоящее время <i>Pt</i>."],
  ["deprans", "<big>deprans</big>\n\n<b>dē–prāns, prandis</b> [prandeo]\nничего не евший, натощак (leo <i>Naev</i>)."],
  ["depravate", "<big>depravate</big>\n\n<b>dēprāvātē</b> [depravatus <i>к</i> depravo]\nпревратно, неправильно (judicare <i>C</i>)."],
  ["depravatio", "<big>depravatio</big>\n\n<b>dēprāvātio, ōnis</b> <i>f</i> [depravo]\nискажение, порча, обезображивание (manuum, articulorum <i>Sen</i>; animi, verbi <i>C</i>): d. oris <i>C</i> гримасы, гримасничанье."],
  ["depravo", "<big>depravo</big>\n\n<b>dē–prāvo, āvī, ātum, āre</b> [pravus]\n<b>1)</b> искривлять (depravata crura corrigere <i>Vr</i>; depravata arbor <i>Sen</i>);\n<b>2)</b> искажать (faciem <i>Sen</i>); nihil est, quin male narrando possit depravarier <i>Ter</i> нет ничего такого, чего нельзя было бы извратить плохим пересказом || портить (verba <i>Q</i>); развращать (puerum indulgentia, mores <i>C</i>): depravata imitatio <i>C</i> шарж, карикатура."],
  ["deprecabilis", "<big>deprecabilis</big>\n\n<b>dēprecābilis, e</b>\nмогущий быть умилостивленным: d. esto <i>Vlg</i> смилуйся."],
  ["deprecabundus", "<big>deprecabundus</big>\n\n<b>dēprecābundus, a, um</b> [deprecor]\nумоляющий, молящий (d. et genibus accidens <i>T</i>)."],
  ["deprecaneus", "<big>deprecaneus</big>\n\n<b>dēprecāneus, a, um</b> [deprecor]\nумолимый: deprecanea (fulmina), quae speciem periculi sine periculo afferunt <i>Sen</i> «умолимыми» называются те молнии, которые (лишь) кажутся опасными, но сами по себе не опасны."],
  ["deprecatio", "<big>deprecatio</big>\n\n<b>dēprecātio, ōnis</b> <i>f</i> [deprecor]\n<b>1)</b> мольба, просьба о предотвращении (d. periculi <i>C</i>); просьба о прощении, о милости (d. facti alicujus <i>C</i>): d. aequitatis <i>C</i> (<i>gen.</i> <i>subj.</i>) справедливая просьба, основательное ходатайство;\n<b>2)</b> <i>культ.</i> посылание проклятий, предание проклятию (dirae deprecationes <i>PM</i>): d. deorum <i>C</i> призывание богов на чью-л. голову."],
  ["deprecator", "<big>deprecator</big>\n\n<b>dēprecātor, ōris</b> <i>m</i> [deprecor]\n<b>1)</b> ходатай, заступник (deprecatorem ad aliquem mittere <i>C</i>): eo deprecatore <i>Cs</i> по его ходатайству;\n<b>2)</b> отвращающий мольбой, отклоняющий просьбой, избавитель (d. periculi <i>C</i>)."],
  ["deprecatorius", "<big>deprecatorius</big>\n\n<b>dēprecātōrius, a, um</b>\nпросящий о милости, умилостивительный (verba <i>Vlg</i>)."],
  ["deprecatrix", "<big>deprecatrix</big>\n\n<b>dēprecātrix, īcis</b> <i>f</i>\nзащитница, заступница <i>Tert</i>."],
  ["deprecatus", "<big>deprecatus</big>\n\n<b>dēprecātus, a, um</b> [deprecor, <i>но в знач.</i> <i>pass.</i>]\n<b>1)</b> просимый, призываемый (numen <i>Ap</i>);\n<b>2)</b> предотвращённый мольбами (bellum <i>Just</i>)."],
  ["deprecor", "<big>deprecor</big>\n\n<b>dē–precor, ātus sum, ārī</b> <i>depon.</i>\n<b>1)</b> молить о предотвращении (d. periculum <i>Cs</i>); стараться отвратить мольбой, отклонить просьбой (calamitatem a se <i>C</i>; iram senatus <i>L</i>; mortem <i>Cs, Sl, O</i>): d. poenam <i>L, Su</i> просить об избавлении от наказания;\n<b>2)</b> умолять, упрашивать: d. aliquem ne festinet <i>L</i> умолять кого-л. не спешить; unum hoc deprecor <i>O</i> я прошу лишь об одном; non deprecor, quin sic existimetis <i>Ap</i> я не прошу вас изменить это ваше мнение;\n<b>3)</b> вымаливать, выпрашивать (aliquid sibi ab aliquo <i>C etc.</i>; d. pacem <i>T</i>); просить, ходатайствовать (pro aliquo <i>C, Su</i>; aliquem, ut alicui remitteret poenam <i>Pt</i>): d. aliquem ab aliquo <i>C etc.</i> ходатайствовать (заступаться) перед кем-л. за кого-л.; errasse aliquem d. <i>Sl</i> просить извинить чью-л. ошибку; d. ab aliquo vitam alicujus <i>C</i> просить кого-л. сохранить чью-л. жизнь; praecipiendi munus d. <i>Q</i> отказаться от должности преподавателя;\n<b>4)</b> клясть, проклинать (aliquem <i>Ctl</i>): diras devotiones in aliquem d. <i>Ap</i> осыпать кого-л. жестокими проклятиями."],
  ["deprehendo", "<big>deprehendo</big>\n\n<b>dē–prehendo (dēprēndo), prehendī (prēndī), prehēnsum (prēnsum), ere</b>\n<b>1)</b> схватить, поймать, захватить (aliquem ex itinere <i>Cs</i>; naves onerarias <i>Cs</i>); перехватить (litteras <i>L</i>); догнать, настигнуть (navigia <i>Lcr</i>): deprehensus negare non potuit <i>C</i> будучи уличён (пойман), он не смог отпираться;\n<b>2)</b> застать, застигнуть (d. hostes sine duce <i>Cs</i>; d. aliquem in manifesto scelere <i>C</i>); застигать врасплох, <i>т. е.</i> ставить в тупик (testes deprehensi <i>Q</i>); подметить, открыть, обнаружить (facinus <i>C</i>; astrorum caelique motus <i>Pt</i>): pudore deprehensi sceleris <i>T</i> из-за стыда, что преступление было раскрыто || увидеть (res magnas in minimis rebus <i>C</i>; aliquid in theatro <i>Ap</i>); заметить (gladium <i>L</i>, venenum apud aliquem <i>C</i>);\n<b>3)</b> <i>pass.</i> deprehendi оказаться (stultior aliquo <i>H</i>)."],
  ["deprehensio", "<big>deprehensio</big>\n\n<b>dēprehēnsio, ōnis</b> <i>f</i> [deprehendo]\n<b>1)</b> поимка на месте преступления (deprehensione fieri manifestum furem <i>Dig</i>);\n<b>2)</b> открытие, обнаружение (alicujus rei <i>C</i>)."],
  ["deprendo", "<big>deprendo</big>\n\n<b>dēprēndo</b> <i>v. l.</i>\n= deprehendo."],
  ["depresse", "<big>depresse</big>\n\n<b>dēpressē</b> (<i>тк. в</i> <i>compar.</i>) [depressus]\nнизко, глубоко (fodere <i>Sen</i>; pastinare solum <i>Col</i>)."],
  ["depressus", "<big>depressus</big>\n\n<b>dēpressus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> deprimo;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> низменный, низкий (convallis <i>V</i>); низко расположенный (domus <i>C</i>);\n<b>2)</b> пониженный (vox <i>rhH</i>)."],
  ["depretiator", "<big>depretiator</big>\n\n<b>dēpretiātor, ōris</b> <i>m</i>\nобесценивающий, принижающий, хулитель (alicujus rei <i>Tert</i>)."],
  ["depretio", "<big>depretio</big>\n\n<b>dē–pretio, āvī, ātum, āre</b> [pretium]\nобесценивать, недооценивать, низко ставить (aliquid <i>Dig, Tert, Sid</i> <i>и</i> aliquem <i>Tert</i>)."],
  ["deprimo", "<big>deprimo</big>\n\n<b>dē–primo, pressī, pressum, ere</b> [premo]\n<b>1)</b> придавливать, давить вниз (onus deprimit me <i>Pl</i>);\n<b>2)</b> опускать, оттягивать (inferius labrum manu <i>Pt</i>): lanx in libra deprimitur <i>C</i> чаша весов опускается;\n<b>3)</b> погружать (aratrum, <i>sc.</i> in terram <i>V</i>); пригибать (depressus aliqua re <i>rhH</i>): d. navem <i>Cs</i> потопить (пустить ко дну) корабль;\n<b>4)</b> рыть, вырывать, углублять (puteum <i>Vtr</i>); глубже сажать (vites in terram <i>Cato</i>): vallis magis in altitudinem depressa, quam late patens <i>Hirt</i> долина большей глубины, чем ширины; locus circiter duodecim pedes humi depressus <i>Sl</i> место, представляющее углубление в 12 футов;\n<b>5)</b> понижать (vocem <i>Sen</i>);\n<b>6)</b> подавлять (improbitate depressa veritas <i>C</i>); прижимать, притеснять (alium <i>L</i>); смирять (hostem <i>L</i>); принижать, низко ставить (alicujus causam per contemptionem <i>C</i>);\n<b>7)</b> прекращать, пресекать: d. preces alicujus <i>Nep</i> положить конец просьбам."],
  ["deproelior", "<big>deproelior</big>\n\n<b>dē–proelior, —, ārī</b> <i>depon.</i>\nбороться с ожесточением, вступать в бой, <i>перен.</i> свирепствовать (venti deproeliantes aequore <i>H</i>)."],
  ["depromo", "<big>depromo</big>\n\n<b>dē–prōmo, prōmpsī, prōmptum, ere</b>\n<b>1)</b> вынимать, доставать (pecuniam ex aerario <i>C</i>; sagittam pharetra <i>V</i>);\n<b>2)</b> черпать (merum diota <i>H</i>); брать, почерпать (argumenta ex his locis <i>C</i>); заимствовать (vel a peritis vel de libris <i>C</i>)."],
  ["depropero", "<big>depropero</big>\n\n<b>dē–propero, —, —, āre</b>\n<b>1)</b> торопиться, спешить: cito deproperate <i>Pl</i> скорей же, поспешите;\n<b>2)</b> спешить, поспешно делать, приготовлять (alicui coronas <i>H</i>): d. sacrificare <i>Pl</i> спешить с жертвоприношением."],
  ["deproperus", "<big>deproperus</big>\n\n<b>dēproperus, a, um</b>\nспешащий, торопливый <i>CTh</i>."],
  ["depropitiatio", "<big>depropitiatio</big>\n\n<b>dēpropitiātio, ōnis</b> <i>f</i> [depropitio]\nумилостивление <i>Aug</i>."],
  ["depropitio", "<big>depropitio</big>\n\n<b>dē–propitio, —, —, āre</b>\nумилостивлять <i>Aug</i>."],
  ["depropitius", "<big>depropitius</big>\n\n<b>dē–propitius, a, um</b>\nнеблагоприятно настроенный, немилостивый (dii <i>Tert</i>)."],
  ["depsiticus", "<big>depsiticus</big>\n\n<b>depsitīcus</b> <i>v. l.</i>\n= depsticius."],
  ["depso", "<big>depso</big>\n\n<b>depso, depsuī, depstum, ere</b> (<i>греч.</i>)\n<b>1)</b> месить (farinam, lutum <i>Cato</i>);\n<b>2)</b> выделывать, дубить (coria <i>Cato</i>);\n<b>3)</b> насиловать <i>C</i>."],
  ["depsticius", "<big>depsticius</big>\n\n<b>depstīcius (depsitīcus), a, um</b> [depso]\nхорошо замешенный (panis <i>Cato</i>)."],
  ["depudesco", "<big>depudesco</big>\n\n<b>dē–pudēsco, —, ere</b>\nотбросить всякий стыд, стать бесстыдным <i>Ap</i>."],
  ["depudet", "<big>depudet</big>\n\n<b>dē–pudet, puduit, —, ēre</b> <i>impers.</i>\n<b>1)</b> стыдиться: depudet me hoc facere <i>VP</i> мне стыдно сделать это; d. alicui rei <i>Aug</i> устыдиться чего-л.;\n<b>2)</b> перестать стыдиться, не стыдиться: quae depuduit (<i>v. l.</i> non puduit) (me) ferre, tulisse pudet <i>O</i> мне стыдно, что я переносил то, что (прежде) не стыдился переносить (<i>т. е.</i> любовные страдания)."],
  ["depudico", "<big>depudico</big>\n\n<b>dē–pudīco, āvī, —, āre</b> [pudicus]\nпозорить, бесчестить <i>Laberius ap. AG</i>."],
  ["depugis", "<big>depugis</big>\n\n<b>dēpūgis, e</b>\n<i>v. l.</i>.= depygis."],
  ["depugnatio", "<big>depugnatio</big>\n\n<b>dēpugnātio, ōnis</b> <i>f</i> [depugno]\nрешающая борьба, решительный бой <i>Cato, Veg</i>."],
  ["depugno", "<big>depugno</big>\n\n<b>dē–pugno, āvī, ātum, āre</b>\nвести решительный бой, бороться с ожесточением (cum aliquo <i>C etc.</i>; d. feras <i>Dig</i>): si depugnandum foret <i>Ap</i> на случай, если придётся вступить в бой || дать решительное сражение (d. acie instructa <i>Cs</i>); выдержать бой: depugnato proelio <i>Pl</i> если бой окончен."],
  ["depuli", "<big>depuli</big>\n\n<b>dēpulī</b> <i>pf. к</i>\ndepello."],
  ["depulsio", "<big>depulsio</big>\n\n<b>dēpulsio, ōnis</b> <i>f</i> [depello]\n<b>1)</b> отражение, предотвращение, удаление, отпор (mali, doloris <i>C</i>): d. luminum <i>C</i> отражение лучей света;\n<b>2)</b> <i>ритор.</i> отклонение (<i>обвинения</i>) <i>C, Q</i>."],
  ["depulso", "<big>depulso</big>\n\n<b>dē–pulso, —, —, āre</b> [<i>frequ. к</i> depello]\nотталкивать, сталкивать (cubitis de via <i>Pl</i>)."],
  ["depulsor", "<big>depulsor</big>\n\n<b>dēpulsor, ōris</b> <i>m</i> [depello]\n<b>1)</b> отвращающий, отклоняющий (malorum <i>Boët, Lact</i>);\n<b>2)</b> разрушитель (dominatus <i>C</i>)."],
  ["depulsoriae", "<big>depulsoriae</big>\n\n<b>dēpulsōriae, ārum</b> <i>f</i> [depulsorius]\n(<i>sc.</i> precationes) предотвращающие заклинания, заговоры, магические формулы (<i>для избавления от какого-л. зла</i>) <i>Pl</i>."],
  ["depulsorius", "<big>depulsorius</big>\n\n<b>dēpulsōrius, a, um</b> [depello]\nпредотвращающий, отклоняющий беды (sacra <i>Amm</i>)."],
  ["depungo", "<big>depungo</big>\n\n<b>dē–pungo, —, —, ere</b>\nотмечать, обозначать: depunge, ubi sistam <i>Pers</i> укажи, где мне остановиться."],
  ["depurgatio", "<big>depurgatio</big>\n\n<b>dēpūrgātio, ōnis</b> <i>f</i> [depurgo] <i>мед.</i>\nочистка (ventris <i>CA</i>)."],
  ["depurgativus", "<big>depurgativus</big>\n\n<b>dēpūrgātīvus, a, um</b> <i>мед.</i>\nочищающий, слабительный (clystēres depurgativae virtutis <i>CA</i>)."],
  ["depurgo", "<big>depurgo</big>\n\n<b>dē–pūrgo, āvi, ātum, āre</b>\nчистить (pisces <i>Pl</i>); очищать (terram ab herbis <i>Cato</i>); прочищать (potionibus, <i>sc.</i> alvum <i>Vtr</i>)."],
  ["depuro", "<big>depuro</big>\n\n<b>dē–pūro, —, ātum, āre</b> [pus]\nочищать от гноя <i>Veg</i>."],
  ["deputo", "<big>deputo</big>\n\n<b>dē–puto, āvī, ātum, āre</b>\n<b>1)</b> считать, оценивать (aliquid parvi pretii <i>Ter</i>): d. aliquid in lucro <i>Ter</i> считать что-л. выигрышем (записать на приход);\n<b>2)</b> предназначать (vaccas steriles aratro <i>Pall</i>);\n<b>3)</b> отдавать (vitam alicui rei <i>Treb</i>); раздавать (patrimonia sua mimis <i>Vop</i>);\n<b>4)</b> приписывать, вменять (aliquid delicto <i>Tert</i>);\n<b>5)</b> подрезывать, подстригать (vineam <i>Cato, Col</i>): nimias d. umbras <i>O</i> подрезыванием ветвей уменьшить излишнюю тенистость."],
  ["depuvio", "<big>depuvio</big>\n\n<b>dē–puvio, pūvī, —, īre</b> [pavio]\nпобить, поколотить (aliquem <i>Naev, LM</i>)."],
  ["depuvo", "<big>depuvo</big>\n\n<b>dēpuvo, pūvī, —, ere</b> <i>v. l.</i>\n= depuvio."],
  ["depygis", "<big>depygis</big>\n\n<b>dēpȳgis, e</b> [de + <i>греч.</i> pyge]\nбезбёдрый, со слабыми, тонкими бёдрами, сухопарый <i>H</i>."],
  ["deque", "<big>deque</big>\n\n<b>dē–que</b> — <i>см.</i>\nsusque."],
  ["dequeror", "<big>dequeror</big>\n\n<b>dē–queror, questus sum, querī</b> (<i>тк. в</i> <i>part. pf.</i>)\nгорько жаловаться (d. aliquid <i>St, VF</i>)."],
  ["dequoquo", "<big>dequoquo</big>\n\n<b>dēquoquo</b> <i>H</i>\n= decoquo."],
  ["derado", "<big>derado</big>\n\n<b>dē–rādo, rāsī, rāsum, ere</b>\n<b>1)</b> соскабливать (de virga lauri <i>Cato</i>);\n<b>2)</b> стереть, изгладить (ex memoria nominum <i>AG</i>): margo derasus <i>PJ</i> обточенные (гладкие) края;\n<b>3)</b> состригать наголо <i>или</i> сбривать (capillum ex capite <i>AG</i>); остричь (caput <i>AG</i>)."],
  ["Derbe", "<big>Derbe</big>\n\n<b>Derbē, ēs</b> <i>f</i>\nДерба, <i>город в Ликаонии, на границе Исаврии</i> (<i>резиденция тирана Антипатра, друга Цицерона</i>) <i>Vlg</i>."],
  ["Derbetes", "<big>Derbetes</big>\n\n<b>Derbētēs, ae</b> <i>или</i> <b>is</b> <i>m</i>\nжитель Дербы <i>C</i>."],
  ["Derbices", "<big>Derbices</big>\n\n<b>Derbicēs, um</b> <i>m</i>\nдербики, <i>народность к востоку от Каспийского моря, в нижнем течении реки Oxus</i> <i>Mela, PM, QC</i>."],
  ["Dercenna", "<big>Dercenna</big>\n\n<b>Dercenna, ae</b> <i>f</i>\nДерценна, <i>источник близ города Bilbilis в Испании</i> <i>M</i>."],
  ["Dercetis", "<big>Dercetis</big>\n\n<b>Dercetis, is</b> <i>и</i> <b>Dercetō, ūs</b> <i>f</i>\nДеркето, <i>сирийская богиня любви и плодородия</i> (<i>изображалась с рыбьим хвостом</i>) <i>O, PM</i>."],
  ["derectus", "<big>derectus</big>\n\n<b>dērēctus, a, um</b> <i>v. l.</i>\n= directus."],
  ["derelictio", "<big>derelictio</big>\n\n<b>dērelictio, ōnis</b> <i>f</i> [derelinquo]\nнедостаток заботливости, отсутствие внимания, пренебрежение (communis utilitatis <i>C</i>)."],
  ["derelictus", "<big>derelictus</big>\n\n<b>I dērelictus, a, um</b> <i>part. pf. к</i>\nderelinquo.\n\n<b>II dērelictus, (ūs)</b> <i>m</i>\nпренебрежение, запущенность: aliquid derelictui habere <i>AG</i> забросить (запустить) что-л."],
  ["derelinquo", "<big>derelinquo</big>\n\n<b>dē–relinquo, līquī, lictum, ere</b>\n<b>1)</b> оставлять, покидать, бросать (aliquem <i>Sen</i>; castra derelicta <i>L</i>): naves ab aestu derelictae <i>Cs</i> суда, оставшиеся (на берегу) после прилива, <i>т. е.</i> застигнутые отливом;\n<b>2)</b> пренебрегать, запускать (agrum <i>Vr</i>): aliquid pro derelicto habere <i>C</i> считать что-л. брошенным (выморочным, бесхозным); derelicta regio <i>rhH</i> безлюдная местность;\n<b>3)</b> <i>поздн.</i> оставлять после своей смерти (magnum aes alienum <i>Hier</i>)."],
  ["derepente", "<big>derepente</big>\n\n<b>dē–repentē</b>\nвнезапно, неожиданно <i>Enn, Pl, Ter etc.</i>"],
  ["derepentino", "<big>derepentino</big>\n\n<b>dē–repentīno</b> <i>adv.</i> (<i>тж. раздельно</i>) <i>Ap</i>\n= derepente."],
  ["derepo", "<big>derepo</big>\n\n<b>dē–rēpo, rēpsī, —, ere</b>\nползти, сползать (ad cubile <i>Ph</i>; arborem <i>PM</i>)."],
  ["dereptus", "<big>dereptus</big>\n\n<b>dēreptus, a, um</b> <i>part. pf. к</i>\nderipio."],
  ["derideo", "<big>derideo</big>\n\n<b>dē–rīdeo, rīsī, rīsum, ēre</b>\n<b>1)</b> насмехаться, издеваться, высмеивать, <i>тж.</i> дурачить (aliquem <i>C, Pt etc.</i> <i>и</i> aliquid <i>C, Sen</i>): derides <i>Pt</i> ты смеёшься (шутишь), <i>т. е.</i> говоришь несерьёзно; satius est rideri, quam derideri <i>Pt</i> лучше давать повод к смеху, чем к насмешкам;\n<b>2)</b> уйти от наказания, дёшево отделаться: non deridebis <i>Pt</i> тебе это даром не пройдёт."],
  ["deridiculum", "<big>deridiculum</big>\n\n<b>dērīdiculum, ī</b> <i>n</i>\n<b>1)</b> посмешище: deridiculo esse (haberi) <i>Pl, T, Ap</i> быть предметом осмеяния; d. corporis <i>T</i> до смешного уродливая внешность;\n<b>2)</b> осмеивание, смех: ad deridicula <i>Q</i> для развлечения."],
  ["deridiculus", "<big>deridiculus</big>\n\n<b>dērīdiculus, a, um</b> [derideo]\nдостойный смеха, смешной, уморительный <i>Pl, Vr, L, AG</i>."],
  ["derigesco", "<big>derigesco</big>\n\n<b>dē–rigēsco (dīrigēsco), riguī, —, ere</b>\n<b>1)</b> коченеть, цепенеть, стынуть (derigescit formidine sanguis <i>V</i>); становиться неподвижным (deriguere oculi <i>V, O</i>); deriguit malis <i>O</i> (Ниоба) окаменела от горя;\n<b>2)</b> становиться дыбом (comae derigescunt <i>O</i>)."],
  ["derigo", "<big>derigo</big>\n\n<b>dērigo</b> <i>v. l.</i>\n= dirigo."],
  ["deripio", "<big>deripio</big>\n\n<b>dē–ripio, ripuī, reptum, ere</b> [rapio]\n<b>1)</b> срывать, отрывать (velamina de umeris <i>O</i>; ferrum a latere <i>T</i>); вырывать, выхватывать (aliquid de manu <i>C</i>; ensem vagina <i>O</i>): d. rates navalibus <i>V</i> оторвать суда от причалов; derepta leoni pellis <i>O</i> снятая со льва шкура;\n<b>2)</b> отнимать: d. aliquid de aliqua re <i>C</i> отнять что-л. от чего-л., <i>т. е.</i> умалить, унизить что-л."],
  ["derisio", "<big>derisio</big>\n\n<b>dērīsio, ōnis</b> <i>f</i>\nнасмешка, осмеяние <i>Eccl</i>."],
  ["derisor", "<big>derisor</big>\n\n<b>dērīsor, ōris</b> <i>m</i> [derideo]\nнасмешник, остряк, шутник <i>Pt, H, Ph etc.</i>"],
  ["derisorius", "<big>derisorius</big>\n\n<b>dērīsōrius, a, um</b>\nсмехотворный, смешной (condicio <i>Dig</i>)."],
  ["derisus", "<big>derisus</big>\n\n<b>I dērīsus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> derideo;\n<b>2.</b> <i>adj.</i> смешной <i>Vr</i>.\n\n<b>II dērīsus, ūs</b> <i>m</i> [derideo]\nосмеивание, высмеивание <i>VM, Ph, Sen</i>: alicui esse derisui <i>Ph</i> быть для кого-л. посмешищем; a derisu non procul abest risus <i>Q</i> от смеха недалеко до высмеивания."],
  ["derivatio", "<big>derivatio</big>\n\n<b>dērīvātio, ōnis</b> <i>f</i> [derivo]\n<b>1)</b> отведение, отвод (aquae <i>L</i>; fluminum <i>C</i>);\n<b>2)</b> заимствование (fructum legendi in aliquem usum derivatione convertere <i>Macr</i>);\n<b>3)</b> <i>грам.</i> образование: d. verborum <i>PM</i> словообразование, словопроизводство;\n<b>4)</b> <i>ритор.</i> деривация, <i>замена одного слова другим, близким по значению, но более мягким</i> (<i>напр.</i> liberalis <i>вместо</i> prodigus) <i>Q</i>."],
  ["derivativus", "<big>derivativus</big>\n\n<b>dērīvātīvus, a, um</b> [derivo] <i>грам.</i>\nпроизводный (nomina)."],
  ["derivo", "<big>derivo</big>\n\n<b>dē–rīvo, āvī, ātum, āre</b> [rivus]\n<b>1)</b> отводить, проводить (aquam de fluvio <i>Pl</i> <i>и</i> ex flumine <i>Cs</i>): nihil in suam domum d. <i>C</i> ничего не использовать в личных интересах;\n<b>2)</b> отклонять, отвлекать, развлекать (animum <i>Lcr</i>);\n<b>3)</b> навлекать (iram alicujus in se <i>Ter</i>): d. culpam in aliquem <i>C</i> переложить вину на кого-л.; d. cogitationem in aliquam rem <i>C</i> обратить мысли к чему-л.;\n<b>4)</b> производить (<i>слова друг от друга</i>) (derivata, <i>sc.</i> nomina, ut a «velocitate» dicitur «velox» <i>Q</i>);\n<b>5)</b> <i>pass.</i> derivari происходить, проистекать (ex his fontibus <i>Q</i>)."],
  ["derogatio", "<big>derogatio</big>\n\n<b>dērogātio, ōnis</b> <i>f</i> [derogo]\nчастичная отмена, ограничение (закона) (legum derogationes <i>C</i>)."],
  ["derogator", "<big>derogator</big>\n\n<b>dērogātor, ōris</b> <i>m</i> [derogo]\nпорицатель, хулитель <i>Sid</i>."],
  ["derogatorius", "<big>derogatorius</big>\n\n<b>dērogātōrius, a, um</b> <i>юр.</i>\nограничивающий действие закона (edictum <i>Dig</i>)."],
  ["derogo", "<big>derogo</big>\n\n<b>dē–rogo, āvī, ātum, āre</b>\nотнимать (alicui aliquid, aliquid de <i>и</i> ex aliqua re <i>C</i>): d. fidem alicui <i>или</i> de fide alicujus <i>C</i> не верить (отказывать в доверии) кому-л.; d. auctoritatem alicui <i>Ap</i> умалять чей-л. авторитет; aliqua d. legi <i>rhH</i>, de lege <i>C</i> <i>и</i> e(x) lege <i>C, Sen</i> частично отменить, ограничить закон."],
  ["derosus", "<big>derosus</big>\n\n<b>dē–rōsus, a, um</b> [rodo]\nобглоданный, изъеденный (clipeus d. a muribus <i>C</i>)."],
  ["derro", "<big>derro</big>\n\n<b>dērro</b> <i>стяж. v. l.</i>\n= deerro."],
  ["Dertona", "<big>Dertona</big>\n\n<b>Dertōna, ae</b> <i>f</i>\nДертона, <i>город лигуров к северу от Генуи, на дороге в Плаценцию</i> (<i>ныне</i> Tortona) <i>C</i>."],
  ["deruncino", "<big>deruncino</big>\n\n<b>dē–runcino, āvī, ātum, āre</b>\nвыстрогать, <i>перен.</i> одурачить, обмануть (aliquem <i>Pl</i>)."],
  ["deruo", "<big>deruo</big>\n\n<b>dē–ruo, ruī, (rutum), ere</b>\n<b>1)</b> низвергать, обрушивать (immensam vim aquarum <i>Sen</i>): de laudibus alicujus cumulum d. <i>C</i> кое-что поубавить из похвал кому-л.;\n<b>2)</b> падать, рушиться, валиться (prolapsus derui <i>Ap</i>); отпадать, отваливаться (aures deruunt <i>Ap</i>)."],
  ["derupta", "<big>derupta</big>\n\n<b>dērupta, ōrum</b> <i>n</i> [deruptus]\nкрутизна, крутые места <i>L, T</i>."],
  ["deruptus", "<big>deruptus</big>\n\n<b>dē–ruptus, a, um</b> [rumpo]\nкрутой, обрывистый (saxa <i>Lcr</i>; collis <i>T</i>; ripa <i>L</i>)."],
  ["des", "<big>des</big>\n\n<b>I dēs</b> <i>арх. Vr</i>\n= bes."],
  ["des.", "<big>des.</big>\n\n<b>II dēs.</b>\n<i>в надписях</i> = designatus."],
  ["desacro", "<big>desacro</big>\n\n<b>dē–sacro (dēsecro), āvī, ātum, āre</b>\n<b>1)</b> посвящать (quercum Triviae <i>St</i>);\n<b>2)</b> предназначать: per singula membra desecratus <i>PM</i> (хамелеон), каждый член которого имеет особое применение (в медицине)."],
  ["desaevio", "<big>desaevio</big>\n\n<b>dē–saevio, iī, ītum, īre</b>\n<b>1)</b> бесноваться, буйствовать, неистовствовать (hiems desaevit pelago <i>V</i>);\n<b>2)</b> успокоиться, утихнуть, улечься (tempestas desaevit <i>Sen</i>)."],
  ["desalto", "<big>desalto</big>\n\n<b>dē–salto, āvī, (ātum), āre</b>\nпротанцевать, отплясать (canticum <i>Su</i>)."],
  ["descendentes", "<big>descendentes</big>\n\n<b>dēscendentēs, ium</b> <i>m</i> [descendo]\nродственники по нисходящей линии, потомки <i>Dig</i>."],
  ["descendo", "<big>descendo</big>\n\n<b>dē–scendo, scendī, scēnsum, ere</b> [scando]\n<b>1)</b> сходить, спускаться (d. de rostris <i>C</i>; monte <i>V, Sl</i>; in mare de caelo <i>L</i>, caelo <i>H</i> <i>и</i> a caelo <i>V</i>; exercitus descendit in planitiem <i>Cs</i>): d. ex equo <i>C</i> <i>или</i> d. equo <i>Sl</i> слезать с лошади;\n<b>2)</b> вступать, начинать: exercitus descendit in certamen <i>C</i> (ad pugnam <i>VF</i>, in aciem <i>L</i>) войско начинает сражение; in ambiguam litem d. <i>Ap</i> затевать рискованную тяжбу;\n<b>3)</b> идти, приходить (in <i>или</i> ad forum <i>C</i>; ad comitia <i>Su</i>);\n<b>4)</b> проникать (injuria descendit altius <i>Sen</i>; in aures alicujus d. <i>H</i>; verbum descendit in pectus <i>Sl</i>): argentum, in quod solidi auri caelatura descendit <i>Sen</i> серебряная посуда с золотой инкрустацией || войти, погрузиться (ferrum descendit in corpus <i>L</i>);\n<b>5)</b> (<i>sensu obsceno</i>) ложиться <i>Ctl, J</i>;\n<b>6)</b> оседать, опускаться (montis altitudo descendit <i>Sen</i>);\n<b>7)</b> стекать, нахлынуть (in campos ante siccos <i>QC</i>);\n<b>8)</b> выделяться из организма (olera celeriter descendunt <i>CC</i>);\n<b>9)</b> ниспадать (vestis descendit intra genua <i>QC</i>; capilli descendunt <i>Pt</i>);\n<b>10)</b> переходить (a vita pastorali ad agriculturam <i>Vr</i>);\n<b>11)</b> понижаться (vox descendit <i>Q</i>);\n<b>12)</b> снисходить, соглашаться (d. ad condicionem <i>C</i>; ad sententiam alicujus <i>Sen</i>): paratus d. ad omnia <i>C</i> готовый согласиться на всё;\n<b>13)</b> доходить, достигать (nostram usque aetatem <i>Q</i>);\n<b>14)</b> решаться, прибегать (d. in preces <i>V</i> <i>и</i> ad preces <i>SenT</i>; ad extrema <i>Pollio ap. C</i>; ad innocentium supplicia <i>C</i>; ad accusandum <i>C</i>): d. ad vim <i>Cs</i> решиться прибегнуть к насилию; an eo descensum credebant, ut...? <i>T</i> уж не дошло ли дело до того, подумали они, что...?;\n<b>15)</b> вести свой род, происходить (a patriciis <i>Dig</i>; ex aliquo <i>Dig</i>);\n<b>16)</b> уходить, отправляться (ad aliquem <i>QC</i>); высаживаться (ad litus <i>Su</i>; Xerxes in Graeciam descendit <i>Nep</i>);\n<b>17)</b> отклоняться, удаляться (ab antiquis <i>Sen</i>);\n<b>18)</b> быть сходным (d. ad aliquam rem <i>или</i> ab aliqua re <i>PM</i>)."],
  ["descensio", "<big>descensio</big>\n\n<b>dēscēnsio, ōnis</b> <i>f</i> [descendo]\n<b>1)</b> нисхождение, сошествие (in hunc mundum <i>Eccl</i>); спуск: d. Tiberina <i>C</i> плавание вниз по Тибру;\n<b>2)</b> поездка, путешествие (usque ad Aegyptum <i>Hier</i>);\n<b>3)</b> глубина (d. saltus <i>Vlg</i>);\n<b>4)</b> спуск, углубление (<i>род ванны</i>) (tres descensiones <i>PJ</i>)."],
  ["descensorius", "<big>descensorius</big>\n\n<b>dēscēnsōrius, a, um</b> [descendo]\nнисходящий (motus <i>Ambr</i>)."],
  ["descensus", "<big>descensus</big>\n\n<b>dēscēnsus, ūs</b> <i>m</i> [descendo]\n<b>1)</b> нисхождение (d. Averno — <i>dat. V</i>);\n<b>2)</b> спуск (d. difficilis et praeruptus <i>Hirt</i>);\n<b>3)</b> сошествие (dei de caelo <i>Eccl</i>);\n<b>4)</b> опускание (ascensus et descensus <i>Macr</i>)."],
  ["descisco", "<big>descisco</big>\n\n<b>dē–scīsco, scīvī (sciī), scītum, ere</b>\n<b>1)</b> отпасть, отложиться (a populo Romano <i>C</i>): d. ab Latinis ad Romanos <i>L</i> отложившись от латинян, перейти на сторону римлян; d. contra aliquem <i>Capit</i> восстать против кого-л.;\n<b>2)</b> удаляться, уклоняться (a veritate <i>C</i>; a consuetudine parentum <i>PJ</i>): d. a se <i>C</i> изменить своим принципам; d. a vita <i>C</i> покончить с собой;\n<b>3)</b> вырождаться (vitis desciscit <i>Col</i>)."],
  ["descobino", "<big>descobino</big>\n\n<b>dē–scobīno, āvī, ātum, āre</b> [scobina]\nопиливать, полировать (simulacra descobinata <i>Eccl</i>); выскабливать, сдирать, обдирать (crura descobinata <i>Vr</i>)."],
  ["describo", "<big>describo</big>\n\n<b>dē–scrībo, scrīpsī, scrīptum, ere</b>\n<b>1)</b> списывать, переписывать (librum ab aliquo <i>C</i>; tabulas <i>Q</i>);\n<b>2)</b> изображать, описывать (regionem aut pugnam <i>C</i>; versibus facta <i>Nep</i>; mores hominum <i>C</i>): d. aliquem latronem <i>C</i> представить (изобразить) кого-л. разбойником;\n<b>3)</b> начертать, чертить (carmina in cortice <i>V</i>; sphaeram <i>C</i>; geometricas formas in arena <i>C</i>);\n<b>4)</b> расписывать, развёрстывать, распределять (frumentum populo <i>L</i>; pecuniam <i>VP</i>; bona suis comitibus <i>C</i>);\n<b>5)</b> разбивать, разделять, расчленять (populum in tribus tres <i>C</i>; annum in duodecim menses <i>L</i>; civitatem in provincias <i>C</i>);\n<b>6)</b> устанавливать (leges, jura <i>C</i>); определять (officia <i>C</i>); назначать (suum cuique munus <i>C</i>): d. duodena jugera in singulos homines <i>C</i> отвести по 12 югеров (земли) на каждого."],
  ["descripte", "<big>descripte</big>\n\n<b>dēscrīptē</b> [descriptus]\nопределённым образом, в установленном порядке (digerere <i>C</i>)."],
  ["descriptio", "<big>descriptio</big>\n\n<b>dēscrīptio, ōnis</b> <i>f</i> [describo]\n<b>1)</b> копия, список (d. tabularum <i>C</i>);\n<b>2)</b> чертёж (numeri aut descriptiones <i>C</i>): d. orbis terrarum <i>Vtr</i> географическая карта; d. ventorum <i>Vtr</i> роза ветров (картушка компаса); d. aedificandi <i>C</i> строительный план (проект);\n<b>3)</b> опись, перечень (servorum omnium <i>C</i>);\n<b>4)</b> изображение, описание (locorum <i>C, Q</i>);\n<b>5)</b> разбивка, разделение (Siciliae <i>Q</i>; regionum <i>C</i>; legionum <i>Su</i>); распределение (possessionum <i>C</i>);\n<b>6)</b> определение (nominis <i>C</i>); разграничение, установление (expetendarum fugiendarumque rerum <i>C</i>);\n<b>7)</b> устройство, организация (d. magistratuum, civitatis <i>C</i>)."],
  ["descriptiuncula", "<big>descriptiuncula</big>\n\n<b>dēscrīptiuncula, ae</b> <i>f</i> [<i>demin. к</i> descriptio]\nкраткое описание, очерк <i>Sen</i>."],
  ["descriptivus", "<big>descriptivus</big>\n\n<b>dēscrīptīvus, a, um</b> [descriptio]\nописательный (definitio <i>Boët</i>)."],
  ["descriptor", "<big>descriptor</big>\n\n<b>dēscrīptor, ōris</b> <i>m</i>\nописывающий, изобразитель (morum vitiorumque <i>Lact</i>): d. gentium <i>Amm</i> этнограф."],
  ["descriptus", "<big>descriptus</big>\n\n<b>dēscrīptus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> describo;\n<b>2.</b> <i>adj.</i> упорядоченный (materies orationis <i>C</i>); благоустроенный (nihil est natura descriptius <i>C</i>); организованный (ordo <i>C</i>)."],
  ["descrobo", "<big>descrobo</big>\n\n<b>dē–scrobo, —, —, āre</b> [scrobis]\nвставлять, оправлять (gemmam auro <i>Tert</i>)."],
  ["desculpo", "<big>desculpo</big>\n\n<b>dē–sculpo, —, —, ere</b>\nваять (simulacrum lapide <i>Tert</i>)."],
  ["deseco", "<big>deseco</big>\n\n<b>dē–seco, secuī, sectum, āre</b>\nсрезывать, отрезать (partes ex toto <i>C</i>); отсекать (aures <i>C</i>); отпиливать (aliquid serra <i>Col</i>); косить, жать (herbam <i>Cs</i>; segetes et prata <i>Col</i>); вырубать, выламывать (saxa metallis <i>St</i>); снимать, удалять (prooemium <i>C</i>)."],
  ["desecro", "<big>desecro</big>\n\n<b>dēsecro</b> <i>v. l.</i>\n= desacro."],
  ["desectio", "<big>desectio</big>\n\n<b>dēsectio, ōnis</b> <i>f</i> [deseco]\nсрезывание, отсекание (stramentorum <i>Col</i>)."],
  ["desedi", "<big>desedi</big>\n\n<b>dēsēdī</b> <i>pf. к</i>\ndesideo."],
  ["desenesco", "<big>desenesco</big>\n\n<b>dē–senēsco, senuī, —, ere</b> (<i>встреч. тк.</i> <i>pf.</i>)\nветшать, стариться, <i>перен.</i> выдыхаться, проходить, утихать (ira belli desenuit <i>Sl</i>)."],
  ["desero", "<big>desero</big>\n\n<b>I dē–sero, —, situm, ere</b>\nсеять, бросать внутрь (semina <i>Vr</i>).\n\n<b>II dē–sero, seruī, sertum, ere</b>\n<b>1)</b> оставлять, покидать, бросать (aliquem <i>C, T etc.</i>): d. aliquem alicui <i>V</i> покидать кого-л. на кого-л.;\n<b>2)</b> дезертировать, бежать (d. castra <i>L</i>; exercitum <i>C, Cs</i>);\n<b>3)</b> забросить, запустить (agros fertiles <i>C</i>); оставить без внимания, пренебречь (cultum deorum <i>L</i>): tempus maturius, quam res, me deseret <i>Sl</i> у меня не хватит скорее времени, чем материала; causa deseritur <i>C</i> дело лежит без движения; desertus viribus <i>Ph</i> обессилевший; d. officium <i>C</i> не исполнять обязанности (пренебречь долгом); d. rem publicam <i>Sl</i> не исполнять своих обязанностей по отношению к государству;\n<b>4)</b> отказаться, отступиться, счесть безнадёжным (d. jus suum <i>C</i>; d. causam <i>C</i>); non d. se <i>Cs</i> не отчаиваться (всё ещё надеяться); a mente deseri <i>C</i> потерять голову, растеряться; d. vadimonium <i>C</i> пропустить срок явки."],
  ["deserpo", "<big>deserpo</big>\n\n<b>dē–serpo, —, —, ere</b>\nсползать, спускаться, <i>т. е.</i> покрывать (lanugo deserpit genis <i>St</i>, malis <i>Ap</i>)."],
  ["deserta", "<big>deserta</big>\n\n<b>dēserta, ōrum</b> <i>n</i> [desertus]\nпустынные, безлюдные места, пустыни, степи (d. ferarum <i>V</i>)."],
  ["desertio", "<big>desertio</big>\n\n<b>dēsertio, ōnis</b> <i>f</i> [desero II]\n<b>1)</b> оставление, неисполнение (<i>sc.</i> officii <i>Dig</i>);\n<b>2)</b> пренебрежение, нерадение <i>Dig</i>;\n<b>3)</b> недостаток, нехватка (nutrimenti <i>CA</i>);\n<b>4)</b> побег, дезертирство <i>Dig</i>."],
  ["desertitudo", "<big>desertitudo</big>\n\n<b>dēsertitūdo, inis</b> <i>f</i> [desertus]\nодиночество <i>Pac</i>."],
  ["desertor", "<big>desertor</big>\n\n<b>dēsertor, ōris</b> <i>m</i> [desero II]\n<b>1)</b> оставляющий без помощи, покидающий, пренебрегающий (d. patris <i>Sen</i>; d. communis utilitatis aut salutis <i>C</i>);\n<b>2)</b> (<i>тж.</i> d. exercitus <i>VP</i>) дезертир <i>Cs, L, T etc.</i>;\n<b>3)</b> беглец (d. Asiae <i>V</i>; d. socii Amoris <i>O</i>)."],
  ["desertrix", "<big>desertrix</big>\n\n<b>dēsertrīx, īcis</b> <i>f</i>\nпренебрегающая (divinae legis <i>Tert</i>)."],
  ["desertum", "<big>desertum</big>\n\n<b>dēsertum, ī</b> <i>n</i>\nпустыня <i>Eccl</i>."],
  ["desertus", "<big>desertus</big>\n\n<b>dēsertus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> desero II;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> необитаемый, безлюдный, пустынный (locus <i>C etc.</i>; ora <i>C</i>): deserta siti regio <i>V</i> безводный край;\n<b>2)</b> уединённый, одинокий (cubile <i>Ctl</i>; lectus <i>O</i>): arbores desertae <i>Prp</i> одиноко стоящие деревья; reditus d. <i>C</i> никем не замеченное возвращение; desertissima solitudo <i>C</i> полное одиночество;\n<b>3)</b> брошенный, покинутый, забытый (in lecto caelibe <i>Ctl</i>; in deversorio Graecae urbis <i>Pt</i>). — <i>См. тж.</i> deserta."],
  ["deservio", "<big>deservio</big>\n\n<b>dē–servio, —, —, īre</b>\n<b>1)</b> усердно служить, выбиваться из сил (omni labore <i>Ap</i>);\n<b>2)</b> употребляться, предназначаться, служить (ad aliquem usum <i>и</i> alicui usui <i>Dig</i>);\n<b>3)</b> оказывать услуги, обслуживать (cuivis d. <i>C</i>);\n<b>4)</b> угождать (corpori <i>C</i>);\n<b>5)</b> предаваться (voracitati suae <i>Ap</i>);\n<b>6)</b> посвящать себя (studiis <i>PJ</i>)."],
  ["deservitio", "<big>deservitio</big>\n\n<b>dēservītio, ōnis</b> <i>f</i>\nслужение <i>Eccl</i>."],
  ["deses", "<big>deses</big>\n\n<b>dēses, idis</b> <i>adj.</i> [desideo]\nпраздный (vita <i>L</i>; d. ab opere suo <i>Cato</i>): desides domi sedemus <i>L</i> мы в бездействии сидим дома (<i>т. е.</i> в Риме)."],
  ["desi", "<big>desi</big>\n\n<b>dēsī</b>\n<i>M, Ctl, C</i> = desii (<i>pf. к</i> desino)."],
  ["desicco", "<big>desicco</big>\n\n<b>dē–sicco, (āvī, ātum), āre</b>\nвысушивать (vasa <i>Pl</i>); засушивать (herbam <i>Ap</i>)."],
  ["desideo", "<big>desideo</big>\n\n<b>dē–sideo, sēdī, sessum, ēre</b> [sedeo]\n<b>1)</b> сидеть сложа руки, быть праздным, ничего не делать (d. totum diem <i>Ter</i>; amoenioribus locis <i>Q</i>);\n<b>2)</b> <i>мед.</i> (<i>тж.</i> d. ad sellam <i>Scr</i>) иметь стул <i>CC</i>."],
  ["desiderabilis", "<big>desiderabilis</big>\n\n<b>dēsīderābilis, e</b> [desidero]\n<b>1)</b> желательный, желанный <i>C etc.</i>: aliquem desiderabilem efficere <i>L</i> внушить сожаление о ком-л.;\n<b>2)</b> незабвенный, любимый (princeps <i>L</i>; filius <i>Aug</i>)."],
  ["desiderabiliter", "<big>desiderabiliter</big>\n\n<b>dēsīderābiliter</b>\nстрастно желая, с вожделением <i>Aug</i>."],
  ["desiderans", "<big>desiderans</big>\n\n<b>dēsīderāns, antis</b>\n<b>1.</b> <i>part. praes. к</i> desidero;\n<b>2.</b> <i>adj.</i> (<i>тк. в</i> <i>superl.</i> desiderantissimus) желаннейший, дражайший <i>Fronto, Aug</i>."],
  ["desideratio", "<big>desideratio</big>\n\n<b>dēsīderātio, ōnis</b> <i>f</i> [desidero]\n<b>1)</b> желание <i>Vtr, C</i>;\n<b>2)</b> нерешённый вопрос: relinquetur d. <i>Vtr</i> останется ещё решить."],
  ["desiderativus", "<big>desiderativus</big>\n\n<b>dēsīderātīvus, a, um</b> <i>грам.</i>\nвыражающий пожелание (verba <i>Aug</i>)."],
  ["desideratus", "<big>desideratus</big>\n\n<b>dēsīderātus, a, um</b> [desidero]\nжеланный, желательный (promissa <i>PM</i>)."],
  ["desiderium", "<big>desiderium</big>\n\n<b>dēsīderium, ī</b> <i>n</i> [desidero]\n<b>1)</b> желание, томление, тоска (nihil est desiderio suorum gravius <i>Q</i>): esse in desiderio alicujus rei <i>C</i> томиться по чему-л., <i>но</i>: erat in desiderio civitatis <i>C</i> (весь) город жалел о нём: alicui d. alicujus rei facere <i>L</i> внушить кому-л. желание чего-л.; me tenet d. alicujus <i>C</i> я страстно тоскую по ком-л.; teneri magno desiderio alicujus <i>C</i> весьма желать видеть кого-л.; d. sui reliquit apud omnes <i>C</i> все жалели о нём;\n<b>2)</b> скорбь, печаль: d. tam cari capitis <i>H</i> скорбь о столь дорогом человеке;\n<b>3)</b> причина скорби, предмет тоски <i>или</i> любви (d. meum <i>Ctl, C, Pt</i>); valete, mea desideria <i>C</i> прощайте, мои дорогие;\n<b>4)</b> утрата, потеря: d. ejus ferre non posse <i>C</i> не быть в состоянии вынести его утрату;\n<b>5)</b> потребность (cibi potionisque d. naturale <i>L</i>);\n<b>6)</b> просьба, прошение, ходатайство (desideria militum <i>Su</i>, provinciarum <i>PJ</i>)."],
  ["desidero", "<big>desidero</big>\n\n<b>dēsīdero, āvī, ātum, āre</b> [<i>одного корня с</i> sidus]\n<b>1)</b> желать (aliquid <i>C etc.</i>); тосковать, томиться (d. aliquem <i>Ap</i>): palmes viduas desiderat ulmos <i>J</i> виноградная лоза тянется к одиноким (<i>т. е.</i> не обвитым ещё) вязам;\n<b>2)</b> требовать (aliquid ab aliquo <i>C, Cs</i>): hoc desiderat longiorem orationem <i>C</i> это требует пространного изложения; accusatio desiderat crimen <i>C</i> обвинение предполагает наличие преступления;\n<b>3)</b> не видеть, не находить: ex me audies, quid in oratione tua desiderem <i>C</i> от меня ты услышишь то, чего я не нахожу (что считаю пробелом) в твоей речи;\n<b>4)</b> терять, утрачивать: in eo proelio ducentos milites desideravit <i>Cs</i> в этом сражении (Цезарь) потерял двести солдат; fortiter d. suos <i>Sen</i> мужественно переносить смерть близких; neminem d. <i>C</i> не иметь потерь в живой силе;\n<b>5)</b> <i>pass.</i> desiderari погибнуть: nulla omnino navis desiderata est <i>Cs</i> решительно ни один корабль не погиб;\n<b>6)</b> <i>pass.</i> недоставать: perpaucis ex hostis numero desideratis, quin cuncti caperentur <i>Cs</i> лишь очень немногих врагов не хватало, чтобы все были взяты в плен, <i>т. е.</i> в плен взяты были почти все;\n<b>7)</b> подвергать рассмотрению, возбуждать вопрос: antequam desideraretur <i>Vtr</i> прежде, чем поставить (такой) вопрос."],
  ["desidia", "<big>desidia</big>\n\n<b>I dēsidia, ae</b> <i>f</i> [desideo] (<i>реже</i> <i>pl.</i>)\n<b>1)</b> продолжительное сидение, торчание: longa faciem quaerere desidia <i>Prp</i> долго приводить в порядок своё лицо (<i>перед зеркалом</i>);\n<b>2)</b> бездеятельность, бездействие, праздность (corde suo expellere desidiam <i>Pl</i>; luxus desidiaeque <i>Lcr</i>; ab industria ad desidiam avocari <i>C</i>);\n<b>3)</b> лежание под паром (ager post longam desidiam laetas segetes affert <i>Col</i>).\n\n<b>II dēsīdia, ae</b> <i>f</i> [desido]\n<b>1)</b> понижение, отлив (maris <i>Ap</i>);\n<b>2)</b> упадок, падение (<i>sc.</i> artium <i>Pt</i>)."],
  ["desidiabulum", "<big>desidiabulum</big>\n\n<b>dēsidiābulum, ī</b> <i>n</i> [desideo]\nместо, где проводят время в праздности <i>Pl</i>."],
  ["desidies", "<big>desidies</big>\n\n<b>dēsidiēs, ēī</b> <i>f</i> <i>Lact</i>\n= desidia I."],
  ["desidiose", "<big>desidiose</big>\n\n<b>dēsidiōsē</b> [desidiosus]\nпраздно, лениво (agere aetatem <i>Lcr</i>)."],
  ["desidiosus", "<big>desidiosus</big>\n\n<b>dēsidiōsus, a, um</b> [desidia I]\n<b>1)</b> праздный, ленивый (homo <i>PJ</i>);\n<b>2)</b> располагающий к праздности, к лени (delectatio, otium <i>C</i>): desidiosa occupatio <i>Sen</i> праздное занятие."],
  ["desido", "<big>desido</big>\n\n<b>dē–sīdo, sēdī (sīdī), —, ere</b>\n<b>1)</b> оседать, опускаться, оползать (terra desedit <i>C</i>): tumor desidit <i>CC</i> опухоль уменьшается;\n<b>2)</b> приходить в упадок, портиться (mores desidentes <i>L</i>);\n<b>3)</b> падать, снижаться (ad tertiam partem <i>Lampr</i>)."],
  ["designatio", "<big>designatio</big>\n\n<b>dēsīgnātio, ōnis</b> <i>f</i> [designo]\n<b>1)</b> обозначение, указание (personarum et temporum <i>C</i>);\n<b>2)</b> определение, назначение: d. consulatus <i>Su</i> назначение в консулы;\n<b>3)</b> устройство, организация, распорядок (totius operis <i>C</i>)."],
  ["designator", "<big>designator</big>\n\n<b>dēsīgnātor, ōris</b> <i>m</i> [designo]\nраспорядитель, устроитель (<i>зрелищ, общественных игр, похорон и пр.</i>) <i>C, Dig</i>. — <i>См. тж.</i> dissignator."],
  ["designo", "<big>designo</big>\n\n<b>dē–sīgno, āvī, ātum, āre</b>\n<b>1)</b> обозначать, отмечать (urbem aratro <i>V</i>); указывать, показывать (aliquem digito <i>O</i>); очертить (fines alicui rei <i>L</i>); отводить (alicui locum <i>Vtr</i>): d. locum circo <i>L</i> отметить (отвести) место для цирка; d. aliquid imagine <i>O</i> изобразить что-л.; d. aliquem oculis <i>C</i> глазами (<i>т. е.</i> молчаливо <i>или</i> про себя) намечать кого-л. (<i>в качестве своей жертвы</i>); d. aliquid verbis (oratione) <i>C</i> словами намекать на что-л. (иметь в виду что-л.);\n<b>2)</b> обнаруживать, изобличать (nimiam luxuriam <i>Cs</i>): d. vulnere frontem <i>St</i> нанести рану в лоб;\n<b>3)</b> назначать, намечать, определять (constituere et d. <i>C</i>; d. et conficere <i>C</i>): consul designatus <i>Sl, Cs</i> избранный консулом (<i>но ещё не вступивший в исполнение консульских обязанностей</i>); civis designatus <i>C</i> (<i>о ещё не родившемся ребёнке</i>) будущий гражданин || предназначать (consulatus alicui designatus <i>AG</i>);\n<b>4)</b> устраивать, затевать, делать: quid non ebrietas designat? (<i>v. l. к</i> dissignat) <i>H</i> чего только не делает опьянение?"],
  ["desii", "<big>desii</big>\n\n<b>dēsiī</b> <i>pf. к</i>\ndesino."],
  ["desilio", "<big>desilio</big>\n\n<b>dē–silio, siluī</b> (<b>silīvī</b> <i>и</i> <b>siliī</b>, <i>арх. Pl</i> <b>suluī</b>), <b>sultum, īre</b> [salio]\n<b>1)</b> соскочить, спрыгнуть (de muro <i>Su</i>; ad terram <i>Pl</i>; ab equo <i>V</i> <i>и</i> ex equo <i>Cs</i>; altis turribus <i>H</i>; in medias undas <i>O</i>; ex navi in terram <i>Cs</i>): d. in pedes <i>Cs, Ap</i> спешиться; d. in artum <i>H</i> стать в тупик, попасть в затруднительное положение;\n<b>2)</b> бить ключом, струиться, брызгать (lympha desilit <i>H</i>);\n<b>3)</b> низвергаться, падать (fulmina aetherea desiluerunt domo <i>Prp</i>)."],
  ["desim", "<big>desim</big>\n\n<b>dēsim</b> <i>praes. conjct. к</i>\ndesum."],
  ["desino", "<big>desino</big>\n\n<b>dē–sino, siī (sīvī), situm, ere</b>\n<b>1)</b> переставать, прекращать, кончать (aliquam rem, (ab) aliqua re <i>или</i> alicujus rei): d. facere aliquid <i>C etc.</i> перестать делать что-л.; d. artem <i>C</i> бросить искусство (перестать заниматься им); illud timere desino <i>C</i> я больше этого не боюсь; vivere (esse) d. <i>Sen</i> прекратить существование (умереть); desitum est disputari <i>C</i> спор прекратился; desine, quaeso, communibus locis <i>C</i> перестань, пожалуйста, говорить общими фразами; d. querelarum <i>H</i> прекратить жалобы; desines timere, si sperare desieris <i>Hecato ap. Sen</i> если перестанешь надеяться, то перестанешь и бояться;\n<b>2)</b> покидать (aliquem <i>Su</i>);\n<b>3)</b> заканчивать: d. in lacrimas <i>O</i> закончить (речь) слезами;\n<b>4)</b> прекращаться (bellum desinit <i>Sl</i>);\n<b>5)</b> завершаться, заканчиваться: similiter d. <i>C</i> иметь одинаковые окончания; d. in piscem <i>H</i> иметь рыбий хвост; aestas in autumnum desinit <i>Sen</i> лето переходит в осень; in violam d. <i>PM</i> отливать лиловым цветом;\n<b>6)</b> <i>ритор.</i> закругляться: apud Ciceronem omnia desinunt, apud Pollionem omnia cadunt <i>Q</i> у (в речах) Цицерона всё заканчивается спокойно (всё закруглено), у Поллиона — всё круто обрывается."],
  ["desioculus", "<big>desioculus</big>\n\n<b>dēsioculus, ī</b> <i>m</i>\nодноглазый, кривой <i>M</i> (<i>v. l.</i>)."],
  ["desipiens", "<big>desipiens</big>\n\n<b>dēsipiēns, entis</b> <i>adj.</i> [desipio]\nбезрассудный, безумный (arrogantia <i>C</i>)."],
  ["desipientia", "<big>desipientia</big>\n\n<b>dēsipientia, ae</b> <i>f</i> [desipio]\nбезрассудство, безумие <i>Lcr</i>."],
  ["desipio", "<big>desipio</big>\n\n<b>dē–sipio, sipuī, —, ere</b> [sapio]\n<b>1)</b> (<i>тж.</i> d. mentis <i>Pl</i>) быть безрассудным, быть безумным, поступать неблагоразумно <i>Lcr, C</i>: d. intra verba <i>CC</i> бредить; dulce est d. in loco <i>H</i> приятно и побезумствовать (предаваться мечтам) в своё время."],
  ["desisto", "<big>desisto</big>\n\n<b>dē–sisto, stitī, stitum, ere</b>\n<b>1)</b> отступать, отступаться, отказываться (aliqua re <i>или</i> ab <i>и</i> de aliqua re): d. accusatione <i>C</i> отказаться от обвинения; d. ab defensione <i>Cs</i> отказаться от обороны;\n<b>2)</b> переставать, прекращать(ся) (d. labori <i>St</i>): d. pugnae (<i>v. l.</i> pugna) <i>V</i> прекратить сражение; desistente autumno <i>Vr</i> на исходе осени; sonus destitit <i>O</i> звук замер."],
  ["desitus", "<big>desitus</big>\n\n<b>dēsitus, a, um</b> <i>part. pf. к</i>\n<b>1)</b> desino <i>и</i>\n<b>2)</b> desero I."],
  ["desivi", "<big>desivi</big>\n\n<b>dēsīvī</b> <i>pf. к</i>\ndesino."],
  ["desolatio", "<big>desolatio</big>\n\n<b>dēsōlātio, ōnis</b> <i>f</i> [desolo]\n<b>1)</b> опустошённость, запустение <i>Vlg</i>: abominatio desolationis — <i>см.</i> abominatio;\n<b>2)</b> уединённое место, пустынь <i>Eccl</i>; пустыня <i>Eccl</i>."],
  ["desolator", "<big>desolator</big>\n\n<b>dēsōlātor, ōris</b> <i>m</i>\nопустошитель, разоритель <i>Hier</i>."],
  ["desolatorius", "<big>desolatorius</big>\n\n<b>dēsōlātōrius, a, um</b>\nопустошающий <i>или</i> делающий одиноким <i>Eccl</i>."],
  ["desolo", "<big>desolo</big>\n\n<b>dē–sōlo, āvī, ātum, āre</b> [solus]\nоставлять, покидать, делать безлюдным (agros <i>V</i>; terras <i>PJ</i>; urbes <i>St</i>): aevo desolata senectus <i>Pt</i> старческая расслабленность, дряхлость; exsilio desolatus <i>Ap</i> обрёкший себя на изгнание и одиночество; desolatus virorum <i>Sil</i> обезлюдевший."],
  ["desolvo", "<big>desolvo</big>\n\n<b>dē–solvo, —, solūtum, ere</b>\nуплачивать <i>Dig</i>."],
  ["desomnis", "<big>desomnis</big>\n\n<b>dē–somnis, e</b> [somnus]\nбессонный: aliquem desomnem facere <i>Pt</i> не давать кому-л. спать."],
  ["desorbeo", "<big>desorbeo</big>\n\n<b>dē–sorbeo, —, —, ēre</b>\nпроглатывать <i>Tert</i>."],
  ["despectatio", "<big>despectatio</big>\n\n<b>dēspectātio, ōnis</b> <i>f</i>\nвид сверху <i>Vtr</i>."],
  ["despectator", "<big>despectator</big>\n\n<b>dēspectātor, ōris</b> <i>m</i> [despecto]\nпрезиратель (divinarum sententiarum <i>Tert</i>)."],
  ["despectio", "<big>despectio</big>\n\n<b>dēspectio, ōnis</b> <i>f</i> [despicio]\n<b>1)</b> смотрение вниз (ex alto <i>CA</i>);\n<b>2)</b> презрение <i>C</i>."],
  ["despecto", "<big>despecto</big>\n\n<b>dēspecto, —, —, āre</b> [<i>intens. к</i> despicio]\n<b>1)</b> смотреть сверху вниз, обозревать с высоты (terras ex alto <i>O</i>);\n<b>2)</b> господствовать, занимать командное положение, выситься (populi, quos despectant moenia Abellae <i>V</i>; mons despectat Oceanum <i>Amm</i>);\n<b>3)</b> презирать (aliquem <i>T</i>; omnia terrena <i>Amm</i>)."],
  ["despector", "<big>despector</big>\n\n<b>dēspector, ōris</b> <i>m</i> <i>Tert</i>\n= despectator."],
  ["despectrix", "<big>despectrix</big>\n\n<b>dēspectrīx, īcis</b> <i>f</i>\nпрезирательница (virtus d. mundi <i>Tert</i>)."],
  ["despectus", "<big>despectus</big>\n\n<b>I dēspectus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> despicio;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> презираемый, презренный <i>C, T, Boët</i>;\n<b>2)</b> незаметный (rivus despectus emoritur <i>Mela</i>).\n\n<b>II dēspectus, ūs</b> <i>m</i> [despicio]\n<b>1)</b> вид сверху вниз (in mare <i>Cs</i>);\n<b>2)</b> презрение: alicui despectui esse <i>rhH, T, Su etc.</i> быть предметом чьего-л. презрения."],
  ["desperabilis", "<big>desperabilis</big>\n\n<b>dēspērābilis, e</b>\nбезнадёжный <i>Vlg, Eccl</i>."],
  ["desperanter", "<big>desperanter</big>\n\n<b>dēspēranter</b> [despero]\nбез всякой надежды, в отчаянии <i>C</i>."],
  ["desperate", "<big>desperate</big>\n\n<b>dēspērātē</b> <i>Aug</i>\n= desperanter."],
  ["desperatio", "<big>desperatio</big>\n\n<b>dēspērātio, ōnis</b> <i>f</i> [despero]\n<b>1)</b> безнадёжность (alicujus rei <i>C, L etc.</i>); отчаяние (esse in desperatione <i>Frontin</i>; adducere ad desperationem <i>Su</i>): desperationem alicujus rei afferre <i>C</i> сделать что-л. невероятным (невозможным);\n<b>2)</b> отчаянная выходка <i>Ap</i>."],
  ["desperatum", "<big>desperatum</big>\n\n<b>dēspērātum</b> <i>adv.</i>\nотчаянно, в высшей степени (d. valde ingeniosus est <i>Pt</i>)."],
  ["desperatus", "<big>desperatus</big>\n\n<b>dēspērātus, a, um</b> [despero]\nбезнадёжный, находящийся в безнадёжном состоянии (res publica <i>C</i>); пропащий (homo <i>Cs</i>); неисправимый (latro <i>Ap</i>); отчаянный, ужасный (res <i>Nep</i>); неизлечимый (morbus <i>C etc.</i>)."],
  ["desperno", "<big>desperno</big>\n\n<b>dē–sperno, —, —, ere</b>\nс презрением отвергать (aliquid <i>Enn, Col</i>)."],
  ["despero", "<big>despero</big>\n\n<b>dē–spēro, āvī, ātum, āre</b>\nне иметь надежды, отчаиваться (de aliqua re <i>C, Cs etc.</i>, alicui rei <i>C, Cs</i>, aliquid <i>C, rhH, L etc.</i> <i>и</i> in aliqua re <i>Aug</i>): nil desperandum <i>H</i> никогда не следует впадать в отчаяние; d. de se <i>или</i> d. sibi (saluti suae) <i>C</i> отчаяться в своём спасении; d. victoriam <i>Fl</i> потерять надежду на победу; d. pacem <i>C</i> не питать никакой надежды на мир; rebus desperatis <i>L</i> в отчаянном положении."],
  ["despexi", "<big>despexi</big>\n\n<b>dēspēxī</b> <i>pf. к</i>\ndespicio."],
  ["despica", "<big>despica</big>\n\n<b>dēspica, ae</b> <i>f</i>\nпрезирательница <i>Naev</i>."],
  ["despicabilis", "<big>despicabilis</big>\n\n<b>dēspicābilis, e</b>\nпрезренный <i>Amm, Ambr, Sid</i>."],
  ["despicatio", "<big>despicatio</big>\n\n<b>dēspicātio, ōnis</b> <i>f</i> [despicio]\nпрезрение <i>C</i>."],
  ["despicatus", "<big>despicatus</big>\n\n<b>I dēspicātus, a, um</b>\n<b>1.</b> <i>part. pf.</i> <i>к</i> <i>поздн.</i> despicor = despicio 3;\n<b>2.</b> <i>adj.</i> презренный (aliquem habere despicatum <i>Pl</i>; homo despicatissimus <i>C, AG</i>).\n\n<b>II dēspicātus, (ūs)</b> <i>m</i> (<i>употр. тк.</i> <i>dat.</i>) [despicio]\nпрезрение: aliquem despicatui habere <i>Pt</i> (ducere <i>C</i>) презирать кого-л."],
  ["despicientia", "<big>despicientia</big>\n\n<b>dēspicientia, ae</b> <i>f</i> [despicio]\nпрезрение (alicujus rei <i>C</i>)."],
  ["despicio", "<big>despicio</big>\n\n<b>dē–spicio, spēxī, spectum, ere</b> [specio]\n<b>1)</b> смотреть вниз (ad aliquem <i>Pl</i>; medios in agros <i>O</i>; a summo caelo in aequora <i>O</i>);\n<b>2)</b> взирать, оглядывать, обозревать (terras <i>O</i>; gentes et urbes <i>C</i>);\n<b>3)</b> быть обращенным, выходить: cubiculum despicit pratum <i>PJ</i> комната выходит (из комнаты открывается вид) на луг;\n<b>4)</b> смотреть свысока, с презрением, презирать (aliquem <i>и</i> aliquid <i>C, Cs etc.</i>; injurias <i>Sen</i>): despectus alicui <i>V</i> презираемый кем-л.; d. legionem propter paucitatem <i>Cs</i> отнестись к легиону с презрением ввиду его малочисленности;\n<b>5)</b> (<i>о брачных предложениях</i>) отклонять, отвергать (despecti Thraces <i>O</i>): despectus Iarbas <i>V</i> отвергнутый (Дидоной) Иарб."],
  ["despoliatio", "<big>despoliatio</big>\n\n<b>dēspoliātio, ōnis</b> <i>f</i> [despolio]\n<b>1)</b> ограбление <i>Tert</i>;\n<b>2)</b> денежная кара, штраф <i>CTh</i>."],
  ["despoliator", "<big>despoliator</big>\n\n<b>dēspoliātor, ōris</b> <i>m</i> [despolio]\nразбойник, грабитель <i>Pl, CTh</i>."],
  ["despolio", "<big>despolio</big>\n\n<b>dē–spolio, āvī, ātum, āre</b>\n<b>1)</b> раздевать догола (aliquem <i>Pt</i>): despoliatus <i>Pt</i> совершенно голый;\n<b>2)</b> ограбить, обобрать (templum <i>C</i>; d. aliquem aliqua re <i>Cs etc.</i>);\n<b>3)</b> лишать (despoliari triumpho <i>L</i>)."],
  ["despondeo", "<big>despondeo</big>\n\n<b>dē–spondeo, spondī, spōnsum, ēre</b>\n<b>1)</b> торжественно обещать (alicui aliquid <i>C, L</i>): d. aliquid sibi <i>C</i> обусловить (выговорить себе) что-л.; d. alicui filiam <i>C</i> просватать дочь за кого-л.; d. aliquam sibi <i>C</i> обручиться с кем-л.;\n<b>2)</b> возлагать (spes despondetur aliqua re <i>C</i>);\n<b>3)</b> посвящать (librum alicui <i>C</i>);\n<b>4)</b> предавать, обрекать (perjuria meritis poenis <i>VF</i>);\n<b>5)</b> потерять (в качестве поручителя) (<i>sc.</i> mille drachumas <i>Pl</i>);\n<b>6)</b> утратить, отчаяться: d. animum (animos) <i>Pl, Vr, L</i> потерять надежду, пасть духом; d. sapientiam <i>Col</i> отчаяться в возможности достичь мудрости."],
  ["desponsatio", "<big>desponsatio</big>\n\n<b>dēspōnsātio, ōnis</b> <i>f</i> [despondeo 1]\nобручение, помолвка <i>Vlg, Eccl</i>."],
  ["desponsio", "<big>desponsio</big>\n\n<b>dēspōnsio, ōnis</b> <i>f</i> [despondeo]\n<b>1)</b> <i>Hier</i> = desponsatio;\n<b>2)</b> (<i>тж.</i> d. animi <i>CA</i>) утрата надежды, отчаяние <i>CA</i>."],
  ["desponso", "<big>desponso</big>\n\n<b>dēspōnso, (āvī), ātum, āre</b> [despondeo 1]\nобручить, помолвить, просватать (aliquam alicui <i>Pl, Su, Capit, Eccl</i>)."],
  ["desponsor", "<big>desponsor</big>\n\n<b>dēspōnsor, ōris</b> <i>m</i> [despondeo 1]\nобручающий, сватающий <i>Vr</i>."],
  ["desponsus", "<big>desponsus</big>\n\n<b>dēspōnsus, a, um</b> <i>part. pf. к</i>\ndespondeo."],
  ["despopondi", "<big>despopondi</big>\n\n<b>dēspopondī</b> <i>Tert pf. к</i>\ndespondeo."],
  ["despumatio", "<big>despumatio</big>\n\n<b>dēspūmātio, ōnis</b> <i>f</i> [despumo]\nснимание накипи <i>или</i> пены <i>Tert</i>."],
  ["despumo", "<big>despumo</big>\n\n<b>dē–spūmo, āvī, ātum, āre</b>\n<b>1)</b> снимать пену <i>или</i> накипь (d, undam <i>V</i>; mel <i>Col</i>): d. Falernum <i>Prp</i> протрезвиться после фалернского вина, проспать свой хмель;\n<b>2)</b> выскоблить, отполировать (aliquid cote <i>PM</i>);\n<b>3)</b> переставать пениться, <i>перен.</i> отшуметь, переставать шуметь, ослабевать (nimius fervor despumat <i>Sen</i>);\n<b>4)</b> пускать кровь (d. animal <i>Veg</i>);\n<b>5)</b> проливать, струить (aliquid lacrimarum <i>Sen</i>; rutilas arenas <i>Cld</i>)."],
  ["despuo", "<big>despuo</big>\n\n<b>dē–spuo, spuī, spūtum, ere</b>\n<b>1)</b> выплёвывать, плевать <i>Vr, L etc.</i>; сплёвывать (<i>для предотвращения какой-л. беды</i>) (d. sibi in sinus <i>или</i> ter d. <i>Tib</i>; d. comitiales morbos <i>PM</i>);\n<b>2)</b> извергать, выдыхать (tenerum ignem <i>Lcn</i>);\n<b>3)</b> жёлчно критиковать, злобно бранить (acre d. in mores alicujus <i>Pers</i>);\n<b>4)</b> отвергать, пренебрегать, презирать (preces <i>Ctl</i>; voluptates <i>Sen</i>)."],
  ["desputum", "<big>desputum</big>\n\n<b>dēspūtum, ī</b> <i>n</i> [despuo]\nслюна <i>CA</i>."],
  ["desquamata", "<big>desquamata</big>\n\n<b>dēsquāmāta, ōrum</b> <i>n</i> [desquamo]\nссадины <i>PM</i>."],
  ["desquamo", "<big>desquamo</big>\n\n<b>dē–squāmo, (āvī), ātum, āre</b>\n<b>1)</b> снимать чешую, чистить (piscem <i>Pl, Ap</i>);\n<b>2)</b> снимать грязь, очищать (vestem <i>PM</i>);\n<b>3)</b> снимать, обдирать (corticem <i>PM</i>)."],
  ["desterno", "<big>desterno</big>\n\n<b>dē–sterno, strāvī, strātum, ere</b>\nрассёдлывать (equos <i>Veg</i>); развьючивать (camelos <i>Vlg</i>)."],
  ["desterto", "<big>desterto</big>\n\n<b>dē–sterto, tuī, —, ere</b>\nперестать храпеть, <i>перен.</i> очнуться от мечтаний <i>Pers</i>."],
  ["destico", "<big>destico</big>\n\n<b>dēstico, —, —, āre</b>\nпищать по-мышиному <i>Su</i>."],
  ["destillatio", "<big>destillatio</big>\n\n<b>dēstīllātio, ōnis</b> <i>f</i>\nкапание, истечение, <i>мед.</i> катар <i>CC, Sen</i>: d. narium <i>PM</i> насморк; d. ventris <i>PM</i> понос."],
  ["destillo", "<big>destillo</big>\n\n<b>dē–stīllo, āvī, ātum, āre</b>\nстекать каплями, капать <i>V, CC</i>; сочиться (puro nardo <i>Tib</i>): d. odore <i>PM</i> струить аромат."],
  ["destina", "<big>destina</big>\n\n<b>dēstina, ae</b> <i>f</i> [destino]\nкрепление, устой, подпора <i>Vtr, Aug</i>."],
  ["destinata", "<big>destinata</big>\n\n<b>I dēstināta, ōrum</b> <i>n</i> [destino]\nнамерения, планы (destinatis alicujus adversari <i>T</i>).\n\n<b>II dēstināta, ae</b> <i>f</i>\nневеста <i>Su</i>."],
  ["destinate", "<big>destinate</big>\n\n<b>dēstinātē</b> <i>adv.</i>\nупорно, непоколебимо (certare <i>Amm</i>)."],
  ["destinatio", "<big>destinatio</big>\n\n<b>dēstinātio, ōnis</b> <i>f</i> [destino]\n<b>1)</b> определение, назначение, избрание (consulum <i>PJ</i>);\n<b>2)</b> твёрдое решение, план действий (ex destinatione alicujus <i>PM</i>): haud dubia destinatione discesserunt <i>T</i> приняв твёрдое решение, они разошлись;\n<b>3)</b> упорство <i>Amm</i>."],
  ["destino", "<big>destino</big>\n\n<b>dē–stino, āvī, ātum, āre</b> [<i>одного корня с</i> stare]\n<b>1)</b> утверждать, укреплять, прикреплять (naves ancoris <i>Cs</i>, antennas ad malos <i>Cs</i>): negotio destinari <i>Ap</i> быть связанным делом;\n<b>2)</b> определять, назначать (tempus locumque ad certamen <i>L</i>; materiam ad scribendum <i>Q</i>; alicui provinciam <i>L</i>): dies destinatus <i>Just</i> назначенный день;\n<b>3)</b> обрекать (aliquem morti <i>QC, Fl</i> <i>или</i> ad mortem <i>L</i>): supplicio destinatus <i>T</i> осуждённый на казнь; operi destinatus <i>Cs</i> занятый на (фортификационных) работах; d. aliquem animo auctorem caedis <i>L</i> уверенно считать кого-л. убийцей; sententia destinata <i>C</i> определённое мнение; destinatus obdura <i>Ctl</i> будь упорен и твёрд; destinatum est mihi facere aliquid <i>Su, PJ</i> я твёрдо решился сделать что-л.; ex destinato <i>Sen, Su</i> <i>или</i> destinato <i>Su</i> с умыслом, преднамеренно;\n<b>4)</b> наметить в качестве цели, метить, целить (во что-л.) (d. locum oris <i>L</i>): destinata ferire <i>QC</i> попадать в намеченную цель;\n<b>5)</b> предназначать, сватать (alicui filiam suam <i>PJ</i>); назначать, выдвигать (aliquem consulem <i>T</i>);\n<b>6)</b> намечать к покупке, покупать: quanti destinat? <i>Pl</i> за сколько он намерен купить (этот дом)?"],
  ["destituo", "<big>destituo</big>\n\n<b>dē–stituo, uī, ūtum, ere</b> [statuo]\n<b>1)</b> водружать, устанавливать (palum in foro <i>Gracchus ap. AG</i>); ставить, помещать (aliquem ante tribunal <i>L</i>; cohortes extra vallum <i>L</i>): destitui in miserrima fortuna <i>C</i> оказаться в самом бедственном положении;\n<b>2)</b> оставить, покинуть, бросить (freta destituunt in litore pisces <i>V</i>): d. aliquem <i>C etc.</i> бросить кого-л. на произвол судьбы; destitutus (ab) omnibus <i>Q etc.</i> всеми покинутый; destitutus (a) spe <i>QC, L</i> потеряв(ший) (всякую) надежду; spes me destituit <i>Tib, L</i> <i>или</i> destitutus sum spe <i>L</i> я лишился надежды; (Octavium) destituit ventus <i>L</i> флот Октавия был застигнут штилем; conata alicujus d. <i>VP</i> провалить чьи-л. попытки; partem verborum d. <i>Q</i> проглатывать часть слов; d. fugam <i>O</i> перестать бежать, остановиться;\n<b>3)</b> обмануть: d. aliquem mercede pacta <i>H</i> обманом лишить кого-л. обусловленной мзды; d. spem alicujus <i>Just, L, QC</i> обмануть чью-л. надежду."],
  ["destitutio", "<big>destitutio</big>\n\n<b>dēstitūtio, ōnis</b> <i>f</i> [destituo]\n<b>1)</b> оставление (на произвол судьбы) <i>Su, Eccl</i>;\n<b>2)</b> обман, вероломство (destitutione iratus <i>C</i>);\n<b>3)</b> уничтожение: d. peccati <i>Vlg</i> очищение от греха."],
  ["destitutor", "<big>destitutor</big>\n\n<b>dēstitūtor, ōris</b> <i>m</i> [destituo]\nоставляющий, покинувший <i>Priap, Tert</i>."],
  ["destricte", "<big>destricte</big>\n\n<b>dēstrictē</b> [destrictus]\nрезко, строго, решительно <i>PJ, Veg, Ap etc.</i>"],
  ["destrictio", "<big>destrictio</big>\n\n<b>dēstrictio</b> <i>v. l.</i>\n= districtio."],
  ["destrictivus", "<big>destrictivus</big>\n\n<b>dēstrictīvus</b> <i>v. l.</i>\n= districtivus."],
  ["destrictus", "<big>destrictus</big>\n\n<b>dēstrictus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> destringo;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> резкий, решительный (accusator <i>T</i>); строгий (officium <i>PJ</i>);\n<b>2)</b> вынутый из ножен, обнажённый (ensis <i>H</i>)."],
  ["destringo", "<big>destringo</big>\n\n<b>dē–stringo, strīnxī, strictum, ere</b>\n<b>1)</b> срывать, обрывать (bacam myrti <i>Col</i>; frondem <i>Q</i>); сдёргивать, стягивать (tunicam ab umeris <i>Ph</i>): d. gladium (ensem) <i>C, H etc.</i> обнажать меч;\n<b>2)</b> отнимать (destringi aliquid bonis <i>PJ</i>);\n<b>3)</b> соскабливать (curvo ferro <i>M</i>);\n<b>4)</b> гладить, поглаживать (d. se <i>PM</i>); слегка задевать, касаться (d aequora alis <i>O</i>; d. pectus sagitta <i>O</i>; destricta levi vulnere cutis <i>Sen</i>);\n<b>5)</b> изливать (amaritudinem <i>PM</i>); обрушивать (severitatem suam in aliquem <i>Sen</i>); злобно критиковать, разносить (scripta alicujus <i>Ph</i>; aliquem mordaci carmine <i>O</i>)."],
  ["destructilis", "<big>destructilis</big>\n\n<b>dēstrūctilis</b> <i>и</i> <b>dēstrūctibilis, e</b>\nподверженный разрушению (omnia quae manu fiunt <i>Lact</i>)."],
  ["destructio", "<big>destructio</big>\n\n<b>dēstrūctio, ōnis</b> <i>f</i> [destruo]\n<b>1)</b> разрушение (murorum <i>Su</i>);\n<b>2)</b> опровержение (sententiae <i>Q</i>)."],
  ["destructivus", "<big>destructivus</big>\n\n<b>dēstrūctīvus, a, um</b> [destruo]\nразрушительный <i>CA</i>."],
  ["destructor", "<big>destructor</big>\n\n<b>dēstrūctor, ōris</b> <i>m</i>\nразрушитель <i>Tert</i>."],
  ["destruo", "<big>destruo</big>\n\n<b>dē–struo, strūxī, strūctum, ere</b>\n<b>1)</b> ломать (navem <i>C</i>); разрушать (templum <i>Su</i>; moenia <i>V</i>);\n<b>2)</b> разорять, уничтожать (hominem <i>VP, PJ</i>; hostem <i>T</i>);\n<b>3)</b> свергать (tyrannidem <i>Q</i>);\n<b>4)</b> отнимать, лишать: d. constantiam alicujus <i>Pt</i> поколебать чью-л. решимость, лишить кого-л. мужества; d. crinem manumque <i>St</i> отнять корону и скипетр;\n<b>5)</b> опровергать (finitionem <i>Q</i>)."],
  ["desuadeo", "<big>desuadeo</big>\n\n<b>dē–suādeo, —, —, ēre</b>\nотговаривать, отсоветовать (aliquid alicui <i>Aug</i>)."],
  ["desub", "<big>desub</big>\n\n<b>dē–sub</b> <i>praep. cum abl. Sen, Fl и acc. Col</i> (<i>тж. раздельно</i>)\nиз-под."],
  ["desubito", "<big>desubito</big>\n\n<b>dē–subitō</b> (<i>тж. раздельно</i>)\nвнезапно, вдруг <i>Enn, Pl, Lcr, C</i>."],
  ["desubulo", "<big>desubulo</big>\n\n<b>dē–sūbulo, āvī, —, āre</b> [subula]\nпросверливать, <i>перен.</i> проложить (viam <i>Vr</i>)."],
  ["desuctus", "<big>desuctus</big>\n\n<b>dēsūctus, a, um</b> <i>part. pf. к</i>\ndesugo."],
  ["desudasco", "<big>desudasco</big>\n\n<b>dēsūdāsco, —, —, ere</b> [<i>inchoat. к</i> desudo]\nсильно вспотеть <i>Pl</i>."],
  ["desudesco", "<big>desudesco</big>\n\n<b>dēsūdēsco, —, —, ere</b> [desudo]\nзапотевать, покрываться сыростью (<i>о стенах</i>) <i>Vtr</i>."],
  ["desudo", "<big>desudo</big>\n\n<b>dē–sūdo, āvī, ātum, āre</b>\n<b>1)</b> сильно потеть (in balneo <i>CC</i>);\n<b>2)</b> усердно трудиться, выбиваться из сил (in aliqua re <i>C</i>): alio d. Marte <i>Cld</i> быть занятым другой трудной войной; desudatis remotus judiciis <i>Cld</i> оставив тяжёлую судебную деятельность;\n<b>3)</b> источать (balsama <i>Cld</i>); проливать (sudorem <i>Ap</i>)."],
  ["desuefacio", "<big>desuefacio</big>\n\n<b>dēsuē–facio, fēcī, factum, ere</b> [desuesco + facio]\nотучать <i>Tert</i>."],
  ["desuefio", "<big>desuefio</big>\n\n<b>dēsuēfīo, factus sum, fierī</b> <i>depon.</i> (<i>служит</i> <i>pass. к</i> desuefacio)\nотучаться, отвыкать (ab aliqua re <i>C</i>); быть отнятым, отлучённым (a matre <i>Vr</i>)."],
  ["desuesco", "<big>desuesco</big>\n\n<b>dē–suēsco, suēvī, suētum, ere</b>\n<b>1)</b> отучать: d. aliquam rem <i>V, L etc.</i> отучать от чего-л.; desueta sidera <i>O</i> звёзды, от которых отвык глаз; desueta verba <i>O</i> забытые слова; res desueta <i>L</i> вещь, вышедшая из употребления;\n<b>2)</b> отучаться, отвыкать: vocem d. <i>Ap</i> разучиться говорить; desueta triumphis agmina <i>V</i> войска, отвыкшие от триумфов (<i>т. е.</i> войн); desuetus Samnis clamorem Romani exercitus pati <i>L</i> самниты уже не были в состоянии устоять перед (боевым) кличем римской армии."],
  ["desuetudo", "<big>desuetudo</big>\n\n<b>dēsuētūdo, inis</b> <i>f</i> [desuetus]\n<b>1)</b> непривычка, утрата привычки (armorum <i>L</i>): desuetudine longa <i>O</i> вследствие долгого отсутствия практики;\n<b>2)</b> бездействие (d. pigritiam parit <i>Ap</i>)."],
  ["desuetus", "<big>desuetus</big>\n\n<b>dēsuētus, a, um</b> <i>part. pf. к</i>\ndesuesco."],
  ["desuevi", "<big>desuevi</big>\n\n<b>dēsuēvī</b> <i>pf. к</i>\ndesuesco."],
  ["desugo", "<big>desugo</big>\n\n<b>dē–sūgo, —, sūctum, ere</b>\nвысасывать, всасывать <i>Pall</i>."],
  ["desulto", "<big>desulto</big>\n\n<b>dēsulto, —, —, āre</b> [<i>intens. к</i> desilio]\nспрыгивать (mari <i>Tert</i>)."],
  ["desultor", "<big>desultor</big>\n\n<b>dēsultor, ōris</b> <i>m</i> [desilio]\nнаездник-вольтижёр (<i>перепрыгивающий на скачках с одной лошади на другую</i>) <i>L, Vr, Man</i>: d. amoris <i>O</i> непостоянный в любви, волокита."],
  ["desultorius", "<big>desultorius</big>\n\n<b>I dēsultōrius, a, um</b> [desultor]\n<b>1)</b> обученный для вольтижировки, вольтижировочный (equus <i>Su</i>);\n<b>2)</b> неустойчивый, перескакивающий от одного вопроса к другому, распыляющийся (scientia desultoria <i>Ap</i>).\n\n<b>II dēsultōrius, ī</b> <i>m</i> <i>C</i>\n= desultor."],
  ["desultura", "<big>desultura</big>\n\n<b>dēsultūra, ae</b> <i>f</i> [desilio]\nпрыжок с лошади <i>Pl</i>."],
  ["desului", "<big>desului</big>\n\n<b>dēsuluī</b> <i>Pl pf. к</i>\ndesilio."],
  ["desum", "<big>desum</big>\n\n<b>dē–sum, fuī, —, esse</b>\n<b>1)</b> недоставать, не хватать (inopiae desunt multa, avaritiae omnia <i>PS</i>): mihi desunt verba (vires) <i>C, Cs</i> у меня не хватает слов (сил); pecunia deesse coepit <i>L</i> начинает ощущаться недостаток в деньгах; aliquando superesse, aliquando d. <i>Sen</i> то находиться в избытке, то не хватать; non defuit quod responderetur, deerat qui daret responsum <i>L</i> было что отвечать, но не было того, кто мог бы дать ответ; hoc unum defuit <i>C</i> этого только не хватало;\n<b>2)</b> быть недостаточным, неполным <i>или</i> несовершенным (cui si quid adjici potest, defuit <i>Sen</i>);\n<b>3)</b> не участвовать (bello <i>C</i>); не присутствовать, отсутствовать (convivio <i>C</i>);\n<b>4)</b> упустить (alicui rei): d. occasioni <i>Cs</i> не использовать случая; d. tempori <i>L</i> упустить время; nihil contumeliarum defuit, quin subiret <i>Su</i> не было оскорбления, которого бы он не испытал;\n<b>5)</b> оставлять без помощи, не помогать (alicui opera, consilio, labore non d. <i>C</i>): causae communi non d. <i>C</i> не отказывать в помощи общему делу; nullo loco d. alicui <i>L</i> помогать (служить) кому-л. везде и повсюду;\n<b>6)</b> не заботиться, пренебрегать (rei publicae <i>C</i>; saluti communi <i>Cs</i>): tibi neque hortanti deero, neque roganti <i>C</i> я не пренебрегу ни твоими уговорами, ни твоими просьбами;\n<b>7)</b> не исполнять (d. officio <i>C</i>); d. sibi <i>C</i> не обращать на себя внимания, вредить самому себе; nos, consules, desumus <i>C</i> (это) мы, консулы, бездействуем (не исполняем своих обязанностей); non deero <i>C etc.</i> за мной дело не станет."],
  ["desumo", "<big>desumo</big>\n\n<b>dē–sūmo, sūmpsī, sūmptum, ere</b>\nбрать, выбирать (sibi aliquem <i>L</i> <i>и</i> aliquid <i>L, H, PJ, T</i>)."],
  ["desuo", "<big>desuo</big>\n\n<b>dē–suo, —, —, ere</b>\nукрепить, прикрепить (aliquid imbricibus ferreis <i>Cato</i>)."],
  ["desuper", "<big>desuper</big>\n\n<b>dē–super</b> <i>adv.</i>\n<b>1)</b> сверху, с возвышенности (adversas aspectare urbes <i>V</i>; aprum jaculis petere <i>Su</i>);\n<b>2)</b> наверху, поверх (extentas imponere togas <i>O</i>)."],
  ["desuperne", "<big>desuperne</big>\n\n<b>dē–supernē</b> <i>adv.</i> <i>Vtr</i>\n(<i>v. l.</i>) = desuper 1."],
  ["desurgo", "<big>desurgo</big>\n\n<b>dē–surgo, surrēxī, surrēctum, ere</b>\n<b>1)</b> вставать, подниматься (cena <i>H</i>); восходить (sol desurgit <i>Lcr</i>);\n<b>2)</b> отправляться в туалет, иметь стул <i>PM, Scr</i>."],
  ["desursum", "<big>desursum</big>\n\n<b>dē–sursum (dēsūsum)</b> <i>adv.</i>\nсверху вниз <i>Vlg, Eccl</i>."],
  ["detectio", "<big>detectio</big>\n\n<b>dētēctio, ōnis</b> <i>f</i> [detego]\nраскрытие, откровение <i>Tert</i>."],
  ["detector", "<big>detector</big>\n\n<b>dētēctor, ōris</b> <i>m</i>\nоткрыватель, обнаруживающий <i>Tert</i>."],
  ["detego", "<big>detego</big>\n\n<b>dē–tego, tēxī, tēctum, ere</b>\n<b>1)</b> открывать, раскрывать, обнажать (aliquid): ventus detexit villam <i>Pl</i> ветер сорвал крышу с дачи; ossa alicujus d. <i>Su</i> вырыть чьи-л. кости (останки); detecto capite <i>Su</i> с непокрытой головой; paries detectus <i>Pt</i> стена с облупившейся штукатуркой;\n<b>2)</b> раздевать (omni vi d. recusantem <i>Pt</i>);\n<b>3)</b> открывать, обнаруживать, изобличать, разоблачать, раскрывать (d. insidias <i>L</i>; consilia conjuratorum <i>Su</i>);\n<b>4)</b> поймать, застигнуть (in furto detectus <i>Lampr</i>)."],
  ["detendo", "<big>detendo</big>\n\n<b>dē–tendo, (tendī), tēnsum, ere</b>\nослаблять, спускать: d. tabernacula <i>Cs, L</i> снять палатки."],
  ["detensus", "<big>detensus</big>\n\n<b>dētēnsus, a, um</b> <i>part. pf. к</i>\ndetendo."],
  ["detentator", "<big>detentator</big>\n\n<b>dētentātor, ōris</b> <i>m</i>\nудерживающий за собой <i>CTh</i>."],
  ["detentio", "<big>detentio</big>\n\n<b>dētentio, ōnis</b> <i>f</i> [detineo]\nудерживание за собой, владение <i>Dig, Vlg</i>."],
  ["detento", "<big>detento</big>\n\n<b>dētento, —, —, āre</b> [<i>intens. к</i> detineo]\nудерживать за собой, задерживать <i>CTh</i>."],
  ["detentor", "<big>detentor</big>\n\n<b>dētentor, ōris</b> <i>m</i> <i>CJ</i>\n= detentator."],
  ["detentus", "<big>detentus</big>\n\n<b>I dētentus, a, um</b> <i>part. pf. к</i>\ndetineo.\n\n<b>II dētentus, (ūs)</b> <i>m</i>\nудерживание <i>Tert</i>."],
  ["detepesco", "<big>detepesco</big>\n\n<b>dē–tepēsco, tepuī, —, ere</b>\nостывать <i>Sid</i>."],
  ["detergeo", "<big>detergeo</big>\n\n<b>dē–tergeo, tersī, tersum, ēre</b>\n<b>1)</b> стирать, вытирать, утирать (sudorem frontis <i>Pl</i>; lacrimas pollice <i>O</i>);\n<b>2)</b> разгонять, прогонять (Notus deterget nubila caelo <i>H</i>; d. somnum <i>Cld</i>; d. fastidia <i>Col</i>);\n<b>3)</b> <i>разг.-шутл.</i> выколачивать, раздобыть (<i>sc.</i> pecuniam <i>C</i>);\n<b>4)</b> чистить, очищать (mensam <i>Pl</i>; cloacas <i>L</i>); прочищать, протирать (oculos <i>Pt</i>); очищать, прояснять (animum elleboro <i>Pt</i>): se lingua d. <i>Col</i> облизывать себя (<i>о животных</i>);\n<b>5)</b> отламывать (remos <i>Cs, L</i>; pinnas, <i>sc.</i> murorum <i>L</i>)."],
  ["deterior", "<big>deterior</big>\n\n<b>dēterior, ius</b> <i>adj. compar.</i> (<i>superl.</i> deterrimus) [<i>от неупотр.</i> *deterus <i>из</i> de + tero]\nменее хороший, более плохой, худший (mores <i>Pl</i>; obsonia <i>PM</i>): deteriorem facere <i>Cs, PM</i> ухудшать, портить; deteriore tempore <i>C</i> в менее благоприятное время; in deterius к худшему (inclinari <i>VP</i>; mutare <i>T</i>); interpres in deteriora inclinatus <i>L</i> склонный толковать в худшую сторону; deteriore statu esse <i>C</i> быть в худшем состоянии; deterior peditatu <i>Nep</i> слабее пехотой; timor eventus deterioris <i>O</i> страх перед худшим."],
  ["deterioro", "<big>deterioro</big>\n\n<b>dēteriōro, —, —, āre</b> [deterior]\nухудшать, портить <i>Symm</i>."],
  ["deterius", "<big>deterius</big>\n\n<b>dēterius</b> <i>adv.</i> [deterior]\nменее хорошо, похуже: nilo (nihilo) d. <i>H</i> тем не менее; d. interpretari <i>T</i> объяснить (истолковать) в менее благоприятном смысле; d. peccare <i>J</i> совершать ещё более тяжкие проступки; spe d. nostra (<i>pl.</i> = <i>sg.</i>) <i>H</i> хуже, чем я мог бы надеяться."],
  ["determinabilis", "<big>determinabilis</big>\n\n<b>dēterminābilis, e</b>\nограниченный, конечный (materia <i>Tert</i>)."],
  ["determinatio", "<big>determinatio</big>\n\n<b>dēterminātio, ōnis</b> <i>f</i> [determino]\n<b>1)</b> предел, конец (mundi <i>C</i>);\n<b>2)</b> заключение (orationis <i>C</i>)."],
  ["determinator", "<big>determinator</big>\n\n<b>dēterminātor, ōris</b> <i>m</i>\nопределяющий, устанавливающий (d. disciplinae <i>Tert</i>)."],
  ["determino", "<big>determino</big>\n\n<b>dē–termino, āvī, ātum, āre</b>\n<b>1)</b> ограничивать, отделять, отмежёвывать, определять (d. regiones ab oriente ad occasum <i>L</i>): d. Asiam ab occidente Phrygia <i>PM</i> ограничить Азию с запада Фригией;\n<b>2)</b> очертить (imaginem alicujus rei <i>PM</i>);\n<b>3)</b> расчленять: id, quod dicit, spiritu, non arte determinat <i>C</i> (оратор) расчленяет свои предложения не логически, а так, как велит дыхание;\n<b>4)</b> устанавливать (diem jejunii <i>Tert</i>)."],
  ["detero", "<big>detero</big>\n\n<b>dē–tero, trīvī, trītum, ere</b>\n<b>1)</b> стирать, натирать (strata viarum pedibus detrita <i>Lcr</i>): catena collum deterit cani <i>Ph</i> цепь натирает собаке шею; vestis usu detrita <i>PM</i> поношенное платье;\n<b>2)</b> шлифовать, тщательно отделывать <i>или</i> устранять (sibi multa <i>H</i> — <i>о стихах</i>);\n<b>3)</b> молотить (frumenta <i>Col</i>; segetem <i>Vop</i>);\n<b>4)</b> ослаблять, понижать (ardorem et ferociam militum <i>T</i>); уменьшать, умалять (laudes alicujus <i>H</i>);\n<b>5)</b> ухудшать, портить: nimia cura deterit magis, quam emendat <i>PJ</i> чрезмерное усердие больше портит, чем улучшает (дело)."],
  ["deterreo", "<big>deterreo</big>\n\n<b>dē–terreo, terruī, territum, ēre</b>\n<b>1)</b> устрашать, пугать (aliquem ense <i>O</i>); отпугивать, (страхом) удерживать, отклонять (aliquem a <i>или</i> de aliqua re): pudore deterreor illud commemorare <i>C</i> стыд мешает мне (мне стыдно) вспомнить об этом; d. aliquem a bello faciendo <i>C</i> уговорить кого-л. не затевать войны; nunquam me potes d., quin... <i>Pl</i> ты никогда не уговоришь меня, чтоб я не...;\n<b>2)</b> отгонять (catulos verberibus <i>Vr</i>);\n<b>3)</b> отражать, предотвращать (vim ab aliquo <i>L</i>; nefas <i>O</i>)."],
  ["deterrimus", "<big>deterrimus</big>\n\n<b>dēterrimus</b>\n<b>1.</b> <i>adj. superl. к</i> deterior;\n<b>2. ī</b> <i>m</i> отъявленный негодяй <i>T</i>."],
  ["detersio", "<big>detersio</big>\n\n<b>dētersio, ōnis</b> <i>f</i> [detergeo]\nстирание, вытирание <i>CA</i>."],
  ["detersus", "<big>detersus</big>\n\n<b>dētersus, a, um</b> <i>part. pf. к</i>\ndetergeo."],
  ["detestabilis", "<big>detestabilis</big>\n\n<b>dētestābilis, e</b> [detestor]\n<b>1)</b> гнусный, ненавистный (scelus <i>C</i>; homo <i>C</i>);\n<b>2)</b> ужасный (omen <i>C</i>; vox <i>Su</i>)."],
  ["detestabiliter", "<big>detestabiliter</big>\n\n<b>dētestābiliter</b>\nотвратительно <i>Lact</i>."],
  ["detestatio", "<big>detestatio</big>\n\n<b>I dētestātio, ōnis</b> <i>f</i> [detestor]\n<b>1)</b> проклятие <i>L, H, Sen etc.</i>;\n<b>2)</b> искупление: pro detestatione tot scelerum <i>C</i> во искупление стольких преступлений;\n<b>3)</b> <i>юр.</i> торжественный отказ, отречение (d. sacrorum <i>AG</i>).\n\n<b>II dē–testātio, ōnis</b> <i>f</i> [testis II]\nоскопление <i>Ap</i>."],
  ["detestator", "<big>detestator</big>\n\n<b>dētestātor, ōris</b> <i>m</i>\nпроклинающий <i>Tert</i>."],
  ["detestatus", "<big>detestatus</big>\n\n<b>dētestātus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> detestor;\n<b>2.</b> <i>adj.</i> проклятый, ненавистный (bella matribus detestata <i>H</i>)."],
  ["detestor", "<big>detestor</big>\n\n<b>dē–testor, ātus sum, ārī</b> <i>depon.</i>\n<b>1)</b> торжественно отрекаться, отказываться (d. sacra <i>AG</i>);\n<b>2)</b> отклонять, удалять, отгонять (querimoniam a se, memoriam rei alicujus <i>C</i>); отвращать (avertere et detestari omen <i>C</i>): invidiae detestandae gratia <i>C</i> чтобы отвести (отвлечь от себя) недоброжелательство;\n<b>3)</b> призывать проклятие (на чью-л. голову) (pericula <i>L</i> <i>и</i> deorum iram in caput alicujus <i>PJ</i>); проклинать (aliquem <i>Cs</i>; bellorum detestanda memoria <i>VM</i>);\n<b>4)</b> гнушаться, ненавидеть (causam et auctorem cladis <i>T</i>; aliquem tamquam auspicium malum <i>C</i>)."],
  ["detexi", "<big>detexi</big>\n\n<b>dētēxī</b> <i>pf. к</i>\ndetego."],
  ["detexo", "<big>detexo</big>\n\n<b>dē–texo, texuī, textum, ere</b>\n<b>1)</b> соткать (telam <i>Pl</i>); сплести (fiscellam vimine junci <i>Tib</i>);\n<b>2)</b> изобразить, описать (aliquem <i>Poeta ap. rhH</i>);\n<b>3)</b> закончить, завершить (fabellam paucis <i>Sulp</i>);\n<b>4)</b> <i>шутл.</i> стянуть, украсть (pallium <i>Pl</i>)."],
  ["detineo", "<big>detineo</big>\n\n<b>dē–tineo, tinuī, tentum, ēre</b> [teneo]\n<b>1)</b> крепко держать (aliquem gremio <i>V</i>); удерживать, отвлекать (aliquem ab aliqua re <i>Sl</i>; aliquem de negotio <i>Pl</i>); не пускать, останавливать (aliquem pede comprehenso <i>Su</i>); задерживать (aliquem ruri <i>Pl</i>; naves tempestatibus detinentur <i>Cs</i>): nisi quid te detinet <i>H</i> если тебя ничто не удерживает (если ты свободен); d. se продлить свою жизнь, продержаться, протянуть (nonum ad diem <i>T</i>);\n<b>2)</b> занимать (locum <i>PM</i>; curulem sellam <i>PJ</i>; animum studiis <i>O</i>): mentes hominum circa aliquid d. <i>PM</i> занимать чём-л. внимание людей; d. aliquem in admiratione sui <i>Su</i> вызвать в ком-л. восхищение, собой;\n<b>3)</b> привлекать, приковывать (oculos alicujus <i>O, Q</i>);\n<b>4)</b> проводить (tempus <i>O</i>): euntem d. sermone diem <i>O</i> беседой задерживать течение дня, <i>т. е.</i> делать . незаметным, как проходит день."],
  ["detondeo", "<big>detondeo</big>\n\n<b>dē–tondeo, tondī, tōnsum, ēre</b>\n<b>1)</b> обрезывать, срезывать (virgulta <i>Col</i>); стричь, обстригать (lanam <i>Col</i>; capillos <i>Sen</i>; oves <i>Cato</i>; aliquem <i>PJ, O</i>): frondes detonsae <i>O</i> опавшие листья;\n<b>2)</b> объедать (gramina vaccae detondent <i>Nem</i>);\n<b>3)</b> опустошать (agros <i>Enn</i>)."],
  ["detono", "<big>detono</big>\n\n<b>dē–tono, tonuī, —, āre</b>\n<b>1)</b> прогреметь (Juppiter detonuit <i>O</i>);\n<b>2)</b> вспыхнуть, разразиться (procellae bellorum detonuerunt <i>Sol</i>); свирепствовать, бушевать (Sullana tempestas detonuerat <i>Fl</i>); говорить громовым голосом <i>Fl, Sil</i>;\n<b>3)</b> отгреметь, улечься (nubes belli detonat <i>V</i>): ut ambitiosus detonuit imber <i>Pt</i> когда иссякли потоки притворных слёз."],
  ["detonsio", "<big>detonsio</big>\n\n<b>dētōnsio, ōnis</b> <i>f</i> [detondeo]\nстрижка <i>CA</i>."],
  ["detonso", "<big>detonso</big>\n\n<b>dē–tōnso, —, —, āre</b> [<i>intens. к</i> detondeo]\nостричь, состричь <i>Fabius Pictor ap. AG</i>."],
  ["detorno", "<big>detorno</big>\n\n<b>dē–torno, (āvī), ātum, āre</b>\n<b>1)</b> обтачивать (anulos <i>PM</i>);\n<b>2)</b> отделывать (sententiam <i>AG</i>)."],
  ["detorqueo", "<big>detorqueo</big>\n\n<b>dē–torqueo, torsī, tortum, ēre</b>\n<b>1)</b> отворачивать, поворачивать, сворачивать (aliquid in dextram partem <i>C</i>): d. cursus ad aliquem <i>V</i> направиться к кому-л.;\n<b>2)</b> отвлекать, отвращать, отклонять (voluptates animos a virtute detorquent <i>C</i>);\n<b>3)</b> выворачивать, увечить (corporis partes detortae <i>C</i>);\n<b>4)</b> (<i>о словах</i>) производить, выводить (de Marso detorsum — <i>т. е.</i> detortum — nomen <i>Cato</i>);\n<b>5)</b> искажать, извращать смысл, перетолковывать (d. verba in pejus <i>Sen</i>)."],
  ["detorreo", "<big>detorreo</big>\n\n<b>dē–torreo, uī, —, ēre</b>\nобжигать, опалять (aliquem <i>Sid</i>)."],
  ["detorsi", "<big>detorsi</big>\n\n<b>dētorsī</b> <i>pf. к</i>\ndetorqueo."],
  ["detortus", "<big>detortus</big>\n\n<b>dētortus, a, um</b> <i>part. pf. к</i>\ndetorqueo."],
  ["detract-", "<big>detract-</big>\n\n<b>dētract-</b> <i>v. l.</i>\n= detrect-."],
  ["detractatus", "<big>detractatus</big>\n\n<b>dētractātus, ūs</b> <i>m</i>\nтрактат <i>Tert</i>."],
  ["detractio", "<big>detractio</big>\n\n<b>dētractio, ōnis</b> <i>f</i> [detraho]\n<b>1)</b> отнятие, лишение, устранение, удаление (cuneorum <i>Vtr</i>; capillorum <i>CA</i>): d. sanguinis <i>CC, CA</i> кровопускание || удаление из организма (cibi <i>C</i>; humoris <i>CA</i>): d. privatioque omnis doloris <i>AG</i> полное освобождение от всех страданий;\n<b>2)</b> присвоение, кража (alieni <i>C</i>);\n<b>3)</b> злословие (a detractione parcite linguae <i>Vlg</i>);\n<b>4)</b> <i>грам.</i> опущение буквы <i>или</i> слога <i>Q</i>;\n<b>5)</b> <i>ритор.</i> пропуск слова, эллипсис <i>Q</i>."],
  ["detractor", "<big>detractor</big>\n\n<b>dētractor, ōris</b> <i>m</i> [detraho]\nклеветник, хулитель <i>T, Vlg, Eccl</i>."],
  ["detractus", "<big>detractus</big>\n\n<b>I dētractus, a, um</b> <i>part. pf. к</i>\ndetraho.\n\n<b>II dētractus, ūs</b> <i>m</i>\nотнятие, опущение (syllabae <i>Sen</i>)."],
  ["detraho", "<big>detraho</big>\n\n<b>dē–traho, trāxī, tractum, ere</b>\n<b>1)</b> стаскивать, снимать (aliquem equo <i>L</i>, de curru <i>C</i>; stramenta e mulis <i>Cs</i>; anulum de digito <i>Ter, C</i> <i>и</i> e manu <i>VM</i>; vestem corpori <i>QC</i>; armillas lacertis <i>Pt</i>; figuram ab ore <i>Prp</i>);\n<b>2)</b> валить, низвергать, разрушать (statuas <i>Just</i>; muros <i>T</i>): tauros ad terram cornibus d. <i>Su</i> пригнуть быков за рога к земле;\n<b>3)</b> вытаскивать (navem ad terram <i>bAl</i>);\n<b>4)</b> сцарапывать, стирать (nomen ex scuto <i>bAl</i>);\n<b>5)</b> сдирать (pellem <i>H, Ph</i>; coria occisis <i>Mela</i>);\n<b>6)</b> отрывать (malleolus viti <i>Col</i>): detractis pinnis <i>Ap</i> вырвав перья, <i>т. е.</i> «подрезав крылья»;\n<b>7)</b> состригать, снимать (pecori lanas <i>Q</i>; capillos <i>CA</i>);\n<b>8)</b> <i>мед.</i> выпускать, удалять (sanguinem venis <i>Lcr</i>; materiam per alvum <i>Scr</i>);\n<b>9)</b> отнимать (scutum militi <i>Cs</i>; feros habitus homini <i>O</i>); лишать (alicui dignitatem d. <i>Sl, C</i>);\n<b>10)</b> вынимать (lapidem ex opere <i>PJ</i>; fetus nido <i>V</i>);\n<b>11)</b> выпускать, пропускать (litteras, syllabas <i>Q</i>);\n<b>12)</b> выделять, уводить (ex acie singulas cohortes <i>Cs</i>);\n<b>13)</b> уносить, похищать (spolia hostium templis <i>L</i>; gladium delubro Martis <i>Su</i>);\n<b>14)</b> сбавлять, скинуть (aliquid de tota summa <i>C</i>; d. pondus <i>Nep</i>); d. de fama alicujus <i>C</i> порочить чью-л. славу; d. alicui fidem <i>Q</i> лишить доверия кого-л. (не верить кому-л.);\n<b>15)</b> уменьшать, низводить, умалять, принижать (majestatem regum ad medium <i>L</i>): d. de aliquo <i>C, Nep</i> унижать кого-л.; celeritas detracta de causa est <i>C</i> ход дела замедлился (дело затянулось);\n<b>16)</b> вредить, наносить ущерб: multum detraxit ei, quod alienae erat civitatis <i>Nep</i> ему (Эвмену) сильно вредило то, что он был иноземцем; ut ipse de me detraham <i>C</i> скажу это в ущерб самому себе;\n<b>17)</b> отвлекать, удалять (d. aliquem Gallia <i>C</i>; Hannibalem ex Italia <i>L</i>): ut detrahas ista <i>Sen</i> если отвлечься от этого || изгнать, вытеснить (inimicum ex Gallia <i>C</i>);\n<b>18)</b> вынуждать (aliquem ad certamen <i>C</i>): d. aliquem ad accusationem <i>C</i> заставить кого-л. выступить обвинителем; d. aliquem in judicium <i>C</i> привлечь кого-л. к судебной ответственности;\n<b>19)</b> освобождать, избавлять (d. alicui laborem, calamitatem <i>C</i>): animis alicujus d. errorem <i>O</i> вывести кого-л. из состояния неуверенности; detractis vitiis orator <i>C</i> безупречный оратор; detracta opinione alicujus rei <i>C</i> если не верить чему-л.;\n<b>20)</b> чернить, клеветать (de absentibus detrahendi causa dicere <i>C</i>): detrahens <i>Vlg, Eccl</i> клеветник."],
  ["detraxe", "<big>detraxe</big>\n\n<b>dētrāxe</b>\n<i>Pl</i> = detraxisse (<i>inf. pf. к</i> detraho)."],
  ["detraxi", "<big>detraxi</big>\n\n<b>dētrāxī</b> <i>pf. к</i>\ndetraho."],
  ["detrectatio", "<big>detrectatio</big>\n\n<b>dētrectātio, ōnis</b> <i>f</i> [detrecto]\n<b>1)</b> отклонение, отказ (militiae <i>L</i>; heredis <i>PM</i>);\n<b>2)</b> принижение, умаление <i>Eccl</i>."],
  ["detrectator", "<big>detrectator</big>\n\n<b>dētrectātor, ōris</b> <i>m</i> [detrecto]\n<b>1)</b> уменьшающий, умаляющий (d. laudum suarum <i>L</i>);\n<b>2)</b> отказывающийся (alicujus rei <i>Pt</i>)."],
  ["detrecto", "<big>detrecto</big>\n\n<b>dē–trecto, āvī, ātum, āre</b> [tracto]\n<b>1)</b> отклонять, отказываться, отвергать (militiam <i>Cs</i>; pugnam <i>L</i>; jussa alicujus <i>T</i>);\n<b>2)</b> унижать, уменьшать, умалять (laudes alicujus <i>O</i>; aliquem <i>Sl etc.</i>)."],
  ["detrimentosus", "<big>detrimentosus</big>\n\n<b>dētrīmentōsus, a, um</b> [detrimentum]\nвредный, пагубный <i>Cs</i>."],
  ["detrimentum", "<big>detrimentum</big>\n\n<b>dētrīmentum, ī</b> <i>n</i> [detero]\n<b>1)</b> убыль от трения, шлифовки (limae tenuantis d. <i>Ap</i>);\n<b>2)</b> убыль, ущерб, убыток, потеря (detrimenta muneribus explere <i>Just</i>); урон: capere (accipere, facere) d. <i>C etc.</i> понести убыток, потерпеть ущерб; d. afferre <i>и</i> inferre <i>Cs</i>, importare <i>C</i> причинить убыток; detrimento esse <i>Cs</i> быть во вред, вредить;\n<b>3)</b> проигрыш (ex magnis detrimentis retrahi <i>Su</i>);\n<b>4)</b> уменьшение (sine auctu et detrimento <i>Sen</i>; lunaria incrementa atque detrimenta <i>Aug</i>);\n<b>5)</b> несчастье, поражение: accipere d. <i>Cs</i> потерпеть поражение;\n<b>6)</b> <i>pl.</i> жалкие остатки: detrimenta ergastuli <i>QC</i> искалеченные (долгим) тюремным заключением."],
  ["detritus", "<big>detritus</big>\n\n<b>I dētrītus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> detero;\n<b>2.</b> <i>adj.</i> приевшийся, избитый <i>Q</i>.\n\n<b>II dētrītus, ūs</b> <i>m</i> [detero]\nтрение, стирание <i>Vr</i>."],
  ["detriumpho", "<big>detriumpho</big>\n\n<b>dē–triumpho, āvī, ātum, āre</b>\nвосторжествовать, побеждать <i>Eccl</i>."],
  ["detrivi", "<big>detrivi</big>\n\n<b>dētrīvī</b> <i>pf. к</i>\ndetero."],
  ["detrudo", "<big>detrudo</big>\n\n<b>dē–trūdo, trūsī, trēsum, ere</b>\n<b>1)</b> сталкивать, сбрасывать (aliquem in mare <i>Su</i>); относить, отбрасывать (vi tempestatum detrudi <i>T</i>): d. aliquem de sententia <i>C</i> заставить кого-л. переменить мнение;\n<b>2)</b> вытеснять, прогонять (d. aliquem de saltu agroque <i>C</i>; ex praedio <i>C</i>; hostem finibus <i>V</i>): d. hostem <i>L etc.</i> выбить противника с позиции; d. aliquem <i>Su</i> устранить кого-л. (<i>преим. из числа претендентов</i>); d. comitia in adventum Caesaris <i>Cs</i> (заставить) отложить комиции до прибытия Цезаря;\n<b>3)</b> ввергать, доводить (d. aliquem in paupertatem <i>T</i>); повергать (in luctum detrudi <i>C</i>); вынуждать (necessitas detrudit aliquem ad aliquid <i>C</i>): d. aliquem ad necessitatem belli <i>T</i> поставить кого-л. в необходимость воевать."],
  ["detruncatio", "<big>detruncatio</big>\n\n<b>dētruncātio, ōnis</b> <i>f</i> [detrunco]\nобрубание (ramorum <i>PM</i>); отсечение (membri <i>Tert</i>)."],
  ["detrunco", "<big>detrunco</big>\n\n<b>dē–trunco, āvī, ātum, āre</b>\n<b>1)</b> отрубать, отсекать (superiorem partem arboris <i>Col</i>; caput <i>O</i>); обрубать (arborem <i>L</i>);\n<b>2)</b> калечить, увечить (corpora <i>L</i>)."],
  ["detrusio", "<big>detrusio</big>\n\n<b>dētrūsio, ōnis</b> <i>f</i> [detrudo]\nсбрасывание, сталкивание (in lacum <i>Hier</i>)."],
  ["detrusus", "<big>detrusus</big>\n\n<b>dētrūsus, a, um</b> <i>part. pf. к</i>\ndetrudo."],
  ["detuli", "<big>detuli</big>\n\n<b>dētulī</b> <i>pf. к</i>\ndefero."],
  ["detumesco", "<big>detumesco</big>\n\n<b>dē–tumēsco, tumuī, —, ere</b>\nперестать набухать, <i>т. е.</i> улечься (stagna lacusque detumuerunt <i>St</i>); утихать (imber detumuit <i>Pt</i>; <i>перен.</i> odia detumescunt <i>Pt</i>)."],
  ["detundo", "<big>detundo</big>\n\n<b>dē–tundo, —, tūnsum, ere</b>\nразбивать (digitos pedum ob lapides <i>Ap</i>)."],
  ["deturbo", "<big>deturbo</big>\n\n<b>dē–turbo, āvī, ātum, āre</b>\n<b>1)</b> низвергать, сбрасывать (statuam <i>C</i>; aliquid de tecto <i>Pl</i>); ломать, сносить (aedificium <i>C</i>): caput alicujus terrae d. <i>V</i> отрубить кому-л. голову;\n<b>2)</b> выбить, сгонять, прогонять (nostros de vallo <i>Cs</i>; hostes ex stationibus <i>L</i>);\n<b>3)</b> лишать (d. aliquem de fortunis omnibus <i>C</i>; deturbari spe <i>и</i> ex magna spe <i>C</i>; d. aliquem possessione <i>C</i>)."],
  ["deturpo", "<big>deturpo</big>\n\n<b>dē–turpo, —, —, āre</b>\nуродовать, (о)безобразить (aliquem aliqua re <i>Su</i>; poma rugis <i>PM</i>)."],
  ["Deucalion", "<big>Deucalion</big>\n\n<b>Deucaliōn, ōnis</b> <i>m</i>\nДевкалион, <i>сын Прометея, супруг Пирры, царь Фтии (в Фессалии), спасшийся с женой от потопа и ставший родоначальником нового человеческого рода</i>, <i>V, O, Just</i>."],
  ["Deucalioneus", "<big>Deucalioneus</big>\n\n<b>Deucaliōnēus, a, um</b> [Deucalion]\nдевкалионов (undae <i>O</i>; imbres <i>Lcn</i>; cautes <i>Col</i>)."],
  ["deum", "<big>deum</big>\n\n<b>deum</b>\n<b>1)</b> <i>acc. sg. к</i> deus;\n<b>2)</b> <i>поэт.</i> (— deorum) <i>gen. pl. к</i> deus."],
  ["deungo", "<big>deungo</big>\n\n<b>de–ungo, —, —, ere</b>\nобильно смазывать: se vino d. <i>Pl</i> сильно напиться (<i>v. l.</i>)."],
  ["deunx", "<big>deunx</big>\n\n<b>de–ūnx, ūncis</b> <i>m</i> [uncia]\n<b>1)</b> одиннадцать двенадцатых римского фунта (as <i>или</i> libra), <i>т. е.</i> 300,16 г: heres ex deunce <i>C</i> наследник, получающий 11/12 наследства;\n<b>2)</b> 11/12, <i>т. е.</i> свыше 90 процентов югера (<i>см.</i> jugerum) <i>Col</i>;\n<b>3)</b> 11/12 секстария, т. е. 11 киатов (<i>см.</i> cyathus) (potare deunces <i>M</i>)."],
  ["deuro", "<big>deuro</big>\n\n<b>de–ūro, ussī, ustum, ere</b>\n<b>1)</b> сжигать (oppidum <i>bH</i>; tres libros ex novem <i>AG</i>; frumenta <i>T</i>);\n<b>2)</b> повреждать, портить (hiems arbores deusserat <i>L</i>)."],
  ["deus", "<big>deus</big>\n\n<b>deus, ī</b> <i>или</i> <b>di</b>; <i>gen. pl.</i> deorum <i>и</i> deum; <i>dat. f abl.</i> deis, diis <i>или</i> dis) <i>m</i>\n<b>1)</b> бог, божество: deos colere <i>C, QC etc.</i> чтить богов; dii (di) meliora <i>или</i> melius! (<i>sc.</i> velint, duint = dent <i>или</i> ferant) <i>Ter, Tib, C, O</i> да сохранят боги!; di, vestram fidem (<i>sc.</i> obsecro)! <i>Pl, Ter</i> видят боги! (<i>клятва</i>); di bene vortant! <i>Pl, Ter</i> да помогут боги!; di te amenti <i>Pl</i> да хранят тебя боги! (<i>формула приветствия</i>); (cum) dis volentibus <i>Pt, Sl</i> при содействии богов (если богам будет угодно); per deos! <i>C</i> клянусь богами!; majorum gentium di, <i>тж.</i> dei majores <i>C</i> старшие боги (<i>перечисленные в двустишии</i>: Juno, Vesta, Minerva, Ceres, Diana, Venus, Mars, Mercurius, Jovi, Neptunus, Vulcanus, Apollo <i>Enn</i>; <i>тж.</i> dii consentes <i>или</i> complices <i>Vr, Eccl</i>); <i>остальные божества назывались</i> dei minores <i>или</i> minuti;\n<b>2)</b> богиня: deo ducente <i>V</i> под покровительством богини (Венеры)."],
  ["deustio", "<big>deustio</big>\n\n<b>deustio, ōnis</b> <i>f</i> [deuro]\nсжигание, сожжение <i>Hier</i>."],
  ["deustus", "<big>deustus</big>\n\n<b>deustus, a, um</b> <i>part. pf. к</i>\ndeuro."],
  ["deuterius", "<big>deuterius</big>\n\n<b>deuterius, a, um</b> (<i>греч.</i>; <i>лат.</i> secundarius)\nвторичный: vinum deuterium (<i>лат.</i> lora) <i>PM</i> вино из виноградных выжимок."],
  ["Deuteronomium", "<big>Deuteronomium</big>\n\n<b>Deuteronomium, ī</b> <i>n</i> (<i>греч.</i>)\nВторозаконие (<i>традиционное название 5-й книги Пятикнижия</i>) <i>Vlg, Eccl</i>."],
  ["deutor", "<big>deutor</big>\n\n<b>de–ūtor, ūsus sum, ūtī</b> <i>depon.</i>\nнехорошо поступать, дурно обходиться (aliquo <i>Nep</i>)."],
  ["devagor", "<big>devagor</big>\n\n<b>dē–vagor, —, ārī</b>\nсбиться с пути, уклониться в сторону <i>CTh</i>."],
  ["devastatio", "<big>devastatio</big>\n\n<b>dēvāstātio, ōnis</b> <i>f</i>\nразорение, опустошение <i>Eccl</i>."],
  ["devasto", "<big>devasto</big>\n\n<b>dē–vāsto, —, ātum, āre</b>\nопустошать, разорять (fines, agros <i>L</i>); уничтожать (agmina ferro <i>O</i>)."],
  ["deveho", "<big>deveho</big>\n\n<b>dē–veho, vēxī, vectum, ere</b>\n<b>1)</b> увозить, отвозить <i>или</i> привозить, доставлять (conjuges liberosque Carthaginem <i>QC</i>; frumentum in Galliam <i>Cs</i>); Tiberis devehit commeatus <i>L</i> по Тибру доставляется продовольствие;\n<b>2)</b> <i>pass.</i> devehi ехать, плыть (navi Veliam <i>C</i>; per flumen in Oceanum <i>Just</i>); переходить, приступать (devehor ad aliquid <i>Prp</i>)."],
  ["devello", "<big>devello</big>\n\n<b>dē–vello, vellī (volsī), vulsum (volsum), ere</b>\nотрывать, срывать, обламывать (ramum trunco <i>O</i>); вырывать, выщипывать (pennas <i>Pl</i>); удалять волосы, ощипывать (aliquem <i>Su, Capit</i>)."],
  ["develo", "<big>develo</big>\n\n<b>dē–vēlo, —, —, āre</b>\nснимать покрывало, открывать (ora alicui <i>O</i> — <i>v. l.</i> alicujus)."],
  ["deveneror", "<big>deveneror</big>\n\n<b>dē–veneror, ātus sum, ārī</b> <i>depon.</i>\n<b>1)</b> благоговейно чтить (deos <i>O</i>);\n<b>2)</b> отклонять, отвращать мольбами (somnia <i>Tib</i>)."],
  ["devenio", "<big>devenio</big>\n\n<b>dē–venio, vēnī, ventum, īre</b>\n<b>1)</b> спускаться, нисходить: quo Numa devenit <i>H</i> (туда), куда ушёл Нума, <i>т. е.</i> в загробный мир;\n<b>2)</b> приходить, прибывать, приезжать (ad legionem decimam <i>Cs</i>; in eum locum <i>L</i>): viam qui nescit, qua deveniat ad mare <i>Pl</i> не знающий дороги к морю || вступать, входить (eandem speluncam <i>V</i>);\n<b>3)</b> доходить, додумываться (ad hanc rationem <i>C</i>);\n<b>4)</b> обращаться (ad juris studium <i>C</i>);\n<b>5)</b> попадать (in alienas manus <i>C</i>)."],
  ["devenusto", "<big>devenusto</big>\n\n<b>dē–venusto, —, —, āre</b>\nлишить красоты, обезобразить (aliquid <i>AG, Aus, Sid</i>)."],
  ["deverbero", "<big>deverbero</big>\n\n<b>dē–verbero, āvī, —, āre</b>\nотколотить, избить (aliquem usque ad necem <i>Ter</i>)."],
  ["deverbium", "<big>deverbium</big>\n\n<b>dēverbium, ī</b> <i>n</i> <i>Pt</i>\n= diverbium."],
  ["devergentia", "<big>devergentia</big>\n\n<b>dēvergentia, ae</b> <i>f</i> [devergo]\nнаклон, кривизна <i>или</i> выгнутость (mundi <i>AG</i>)."],
  ["devergo", "<big>devergo</big>\n\n<b>dē–vergo, —, —, ere</b>\nопускаться, тяготеть вниз (animalia terrena devergunt pondere <i>Ap</i>)."],
  ["deverro", "<big>deverro</big>\n\n<b>dē–verro</b> (<i>арх.</i> <b>dēvorro</b>), <b>—, —, ere</b>\n<b>1)</b> сметать, выметать, <i>перен.</i> очищать, удалять (humorem omnem urinae <i>Col</i>);\n<b>2)</b> подметать (limen scopis <i>Vr ap. Aug</i>);\n<b>3)</b> убирать со стола (devorare omnia ac d. <i>LM</i>)."],
  ["deversito", "<big>deversito</big>\n\n<b>dēversito, —, —, āre</b> [<i>intens. к</i> deverto]\nзаезжать, завернуть: d. ad aliquid <i>AG</i> остановиться (задержаться) на чём-л."],
  ["deversitor", "<big>deversitor</big>\n\n<b>dēversitor, ōris</b> <i>m</i> [deversor I]\nгость, приезжий, постоялец <i>Pt</i>."],
  ["deversor", "<big>deversor</big>\n\n<b>I dēversor, ātus sum, ārī</b> <i>depon.</i> [deverto]\nделать остановку в пути, останавливаться, заезжать, гостить (apud aliquem <i>C</i>; in illa domo <i>C</i>).\n\n<b>II dēversor, ōris</b> <i>m</i> <i>C</i>\n= diversor."],
  ["deversoriolum", "<big>deversoriolum</big>\n\n<b>dēversōriolum, ī</b> <i>n</i> [<i>demin. к</i> deversorium]\nпристанище, маленький постоялый двор, ночлег <i>C</i>"],
  ["deversorium", "<big>deversorium</big>\n\n<b>dēversōrium, ī</b> <i>n</i>\n<b>1)</b> постоялый двор, гостиница <i>C, L etc.</i>;\n<b>2)</b> пристанище, убежище, прибежище (studiorum, libidinum <i>C</i>; nequitiae atque luxuriae <i>Ap</i>): commorandi natura d. nobis, non habitandi dedit <i>C</i> природа предоставила нам временное пристанище, отнюдь не постоянное жилье."],
  ["deversorius", "<big>deversorius</big>\n\n<b>dēversōrius, a, um</b> [deverto]\nзаезжий, постоялый (taberna <i>Pl, Su</i>)."],
  ["deverticulum", "<big>deverticulum</big>\n\n<b>dēverticulum, ī</b> <i>n</i> [deverto]\n<b>1)</b> боковая дорога, дорога в сторону <i>Ter, C, Su</i>: d. fluminis <i>Dig</i> ответвление (рукав) реки;\n<b>2)</b> отклонение, уклонение, отступление <i>O, VM</i>: a deverticulo repetatur fabula <i>J</i> после этого отступления вернёмся к (нашему) рассказу;\n<b>3)</b> увёртка, отговорка (deverticula peccatis dare <i>C</i>);\n<b>4)</b> (= deversorium) пристанище <i>Pl, C, Q, PM</i>; <i>перен.</i> место отдыха, убежище, уголок (deverticula amoena quaerere <i>L</i>);\n<b>5)</b> притон (lupanaria et deverticula <i>T</i>)."],
  ["deverto", "<big>deverto</big>\n\n<b>dē–verto</b> (<i>арх.</i> <b>dēvorto</b>), <b>vertī, versum, ere</b>\n<b>1)</b> сворачивать, поворачивать в сторону (aliquam rem): ventura fata suo cursu d. <i>Lcn</i> повернуть (изменить) ход будущих событий; d. via <i>L, PJ</i> свернуть с дороги;\n<b>2)</b> отговорить, разубедить (aliquem <i>Lcn</i>);\n<b>3)</b> <i>med.-pass.</i> deverti завернуть, заехать (deverti ad aliquem in hospitium <i>Pl</i>; apud aliquem <i>Pl, L</i>; in <i>и</i> ad villam <i>C</i>);\n<b>4)</b> уклоняться (<i>от главн. предмета</i>), удаляться (redeamus ad illud, unde devertimus <i>C</i>);\n<b>5)</b> обращаться (d. ad artes magicas <i>O</i>)."],
  ["devescor", "<big>devescor</big>\n\n<b>dē–vescor, —, vescī</b> <i>depon.</i>\nсъедать, пожирать (aliquem morsu cruento <i>St</i>)."],
  ["devestio", "<big>devestio</big>\n\n<b>dē–vestio, —, —, īre</b>\nраздевать (sese <i>Ap</i>): d. se aliqua re <i>Ap</i> снять с себя что-л."],
  ["devestivus", "<big>devestivus</big>\n\n<b>dēvestīvus, a, um</b>\nнеодетый <i>Tert</i>."],
  ["devexi", "<big>devexi</big>\n\n<b>dēvēxī</b> <i>pf. к</i>\ndeveho."],
  ["devexitas", "<big>devexitas</big>\n\n<b>dēvexitās, ātis</b> <i>f</i> [devexus]\n<b>1)</b> покатость, отлогость (loci <i>PJ</i>); уклон (aquarum <i>M</i>);\n<b>2)</b> скат, спуск <i>Amm</i>."],
  ["devexo", "<big>devexo</big>\n\n<b>dēvexo</b> <i>v. l.</i>\n= divexo."],
  ["devexum", "<big>devexum</big>\n\n<b>dēvexum, ī</b> <i>n</i>\nсклон, спуск, скат (aqua in devexo fluit <i>Sen</i>): per d. ire <i>Sen</i> идти под гору, <i>т. е.</i> легко, без напряжения."],
  ["devexus", "<big>devexus</big>\n\n<b>dēvexus, a, um</b>\n<b>1)</b> покатый, отлогий (litus molliter devexum <i>Q</i>); спускающийся (lucus devexus in novam viam <i>C</i>);\n<b>2)</b> катящийся вниз (amnis d. ab Indis <i>V</i>); склоняющийся: sol d. <i>C</i> солнце на закате; aetas devexa ad otium <i>C</i> возраст, тяготеющий к покою."],
  ["devia", "<big>devia</big>\n\n<b>dēvia, ōrum</b> <i>n</i> [devius]\nбоковые дороги, окольные пути (invia ac devia <i>L</i>)."],
  ["deviatio", "<big>deviatio</big>\n\n<b>dēviātio, ōnis</b> <i>f</i> [devio]\nотклонение <i>Eccl</i>."],
  ["devici", "<big>devici</big>\n\n<b>dēvīcī</b> <i>pf. к</i>\ndevinco."],
  ["devictio", "<big>devictio</big>\n\n<b>dēvictio, ōnis</b> <i>f</i> [devinco]\nокончательная победа, подавление (mortis <i>Tert</i>)."],
  ["devigesco", "<big>devigesco</big>\n\n<b>dē–vigēsco, —, —, ere</b>\nутрачивать силу, ослабевать <i>Tert</i>."],
  ["devincio", "<big>devincio</big>\n\n<b>dē–vincio, vīnxī, vīnctum, īre</b>\n<b>1)</b> связывать (aliquid fasciis <i>C</i>): d. se vino <i>Pl</i> сильно напиться || привязывать (aliquem ad aliquid <i>Pl</i>); перевязывать, увенчивать (tempora lauro <i>Tib</i>);\n<b>2)</b> соединять (homines juris societate <i>C</i>): d. se affinitate cum aliquo <i>C</i> породниться с кем-л.;\n<b>3)</b> привязывать к себе, пленять (aliquem variis artibus <i>T</i>): assuetudine alicujus devinctus <i>T</i> крепко привязанный к (преданный) кому-л.; d. sibi aliquem benevolentia <i>C</i> привязывать кого-л. к себе лаской; summo beneficio (me) in perpetuum tibi devinxeris <i>C</i> (этой) великой услугой ты обяжешь меня вечной благодарностью тебе; d. aliquem jure jurando <i>C</i> обязать кого-л. клятвой; d. verba comprenensione <i>или</i> una complexione <i>C</i> связывать слова в периоды;\n<b>4)</b> подчинять, покорять (omnes omnium gentium partes <i>C</i>): d. urbem praesidiis <i>C</i> держать город в повиновении с помощью вооружённых сил."],
  ["devinco", "<big>devinco</big>\n\n<b>dē–vinco, vīcī, victum, ere</b>\nодерживать полную победу, окончательно побеждать, одолевать (aliquem una pugna <i>L</i>): devicta Gallia <i>Cs</i> в случае покорения Галлии; bella devicta <i>V</i> победоносные войны."],
  ["devinctio", "<big>devinctio</big>\n\n<b>dēvīnctio, ōnis</b> <i>f</i> [devincio]\nсвязывание: magicae devinctiones <i>Tert</i> чары, колдовство."],
  ["devinctus", "<big>devinctus</big>\n\n<b>dēvīnctus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> devincio;\n<b>2.</b> <i>adj.</i> совершенно преданный (alicui <i>C, H etc.</i>)."],
  ["devinxi", "<big>devinxi</big>\n\n<b>dēvīnxī</b> <i>pf. к</i>\ndevincio."],
  ["devinxti", "<big>devinxti</big>\n\n<b>dēvīnxtī</b> <i>Pl</i>\n(— devinxisti) <i>pf. к</i> devincio."],
  ["devio", "<big>devio</big>\n\n<b>dē–vio, āvī, ātum, āre</b> [via]\nуклоняться в сторону, сбиться с пути <i>Aug, Macr, Amm etc.</i>"],
  ["devirginatio", "<big>devirginatio</big>\n\n<b>dēvirginātio, ōnis</b> <i>f</i> [devirgino]\nдефлорация <i>Scr</i>."],
  ["devirgino", "<big>devirgino</big>\n\n<b>dē–virgino, āvī, ātum, āre</b> [virgo]\nлишать девственности (aliquam <i>Pt, Dig, Vlg</i>)."],
  ["devitatio", "<big>devitatio</big>\n\n<b>dēvītātio, ōnis</b> <i>f</i> [devito]\nизбегание (alicujus rei <i>C, Aug</i>)."],
  ["devito", "<big>devito</big>\n\n<b>dē–vīto, āvī, ātum, āre</b>\nизбегать (aliquid <i>C, H, Sen</i>, <i>но</i>: alicui rei <i>Ap</i>)."],
  ["devius", "<big>devius</big>\n\n<b>dē–vius, a, um</b> [via]\n<b>1)</b> лежащий в стороне от дороги (oppidum <i>C</i>): iter devium <i>C</i> дорога, ведущая в сторону (просёлочная <i>или</i> окольная);\n<b>2)</b> отдалённый (gentes <i>L</i>); недоступный, уединённый (limina <i>Prp</i>); одинокий (homo, avis <i>O</i>);\n<b>3)</b> мечущийся в сторону, неспокойный, пугливый (sonipes <i>St</i>);\n<b>4)</b> уклонившийся: devium loqui <i>PJ</i> отклониться от темы, говорить о посторонних вещах; nihil ab hac secta vel paululum d. <i>Ap</i> (Платон), который ни в чём или почти ни в чём не расходится с этой (пифагорейской) школой; d. aequi <i>Sil</i> несправедливый;\n<b>5)</b> непостоянный, сумбурный, беспутный (vita <i>C, Lact</i>): homo in omnibus rebus praeceps et d. <i>C</i> человек опрометчивый и безрассудный во всём. — <i>См. тж.</i> devia."],
  ["devoco", "<big>devoco</big>\n\n<b>dē–voco, āvī, ātum, āre</b>\n<b>1)</b> сзывать вниз (aliquem ab tumulo <i>L</i>): d. philosophiam e caelo <i>C</i> свести философию с неба (на землю);\n<b>2)</b> отзывать, вызывать (aliquem de provincia <i>C</i>, in certamen <i>VM</i>, ex praesidiis <i>L</i>);\n<b>3)</b> призывать (deos ad auxilium <i>L</i>; aliquem in judicium <i>VM</i>); апеллировать (d. rem ad populum <i>VM</i>);\n<b>4)</b> отвлекать (aliquem ab instituto cursu <i>C</i>);\n<b>5)</b> увлекать, вовлекать (libido devocat ad voluptatem <i>C</i>; d. mortales ad perniciem <i>Ph</i>): d. aliquam rem in dubium <i>Cs</i> подвергать что-л. опасности (риску)."],
  ["devolo", "<big>devolo</big>\n\n<b>dē–volo, āvī, ātum, āre</b>\n<b>1)</b> слетать, прилетать (Iris per caelum devolat <i>V</i>): alicui nihil agenti de caelo in sinum d. <i>ирон. L</i> слетать кому-л. с неба на грудь без всяких с его стороны усилий || (<i>о хищных птицах</i>) налетать, нападать (in multas aves <i>O</i>); улетать (phoenix devolat in Aegyptum <i>PM</i>);\n<b>2)</b> поспешно удаляться (omnes devolant <i>rhH</i>); спешить (d. in forum <i>L</i>)."],
  ["devolvo", "<big>devolvo</big>\n\n<b>dē–volvo, volvī, volūtum, ere</b>\n<b>1)</b> катить вниз, скатывать, сваливать (saxa muro <i>Cs</i>; clipeos e muris <i>QC</i>; corpora in humum <i>O</i>): devolutis tonitribus <i>Ph</i> среди раскатов грома; fusis mollia pensa d. <i>V</i> разматывать мягкую шерсть с веретён (<i>т. е.</i> прясть); per audaces dithyrambos nova verba d. <i>H</i> сочинять смелые дифирамбы в новых словах; aliquem vita sua d. <i>Pl</i> лишить кого-л. жизни; se toro d. <i>VF</i> скатиться с постели;\n<b>2)</b> <i>pass.</i> devolvi скатываться, низвергаться, устремляться вниз (torrens devolvitur monte <i>L</i>); <i>перен.</i> докатиться, быть доведённым (ad inanem spem <i>C</i>; in ultima flagitia <i>Aug</i>)."],
  ["devomo", "<big>devomo</big>\n\n<b>dē–vomo, —, —, ere</b>\nизрыгать, извергать <i>Caec</i>."],
  ["devoratio", "<big>devoratio</big>\n\n<b>dēvorātio, ōnis</b> <i>f</i> [devoro]\nпожирание, поедание (carnis <i>Tert</i>)."],
  ["devorator", "<big>devorator</big>\n\n<b>dēvorātor, ōris</b> <i>m</i>\nпожиратель <i>Tert</i>."],
  ["devoratorium", "<big>devoratorium</big>\n\n<b>dēvorātōrium, ī</b> <i>n</i> [devoro]\nпожирающая (ненасытная) утроба (mortis <i>Ambr</i>)."],
  ["devoratrix", "<big>devoratrix</big>\n\n<b>dēvorātrīx, īcis</b> <i>f</i>\nпожирательница <i>Vlg</i>."],
  ["devoro", "<big>devoro</big>\n\n<b>I dē–voro, āvī, ātum, āre</b>\n<b>1)</b> съедать, проглатывать, пожирать (aliquid <i>Cato, C etc.</i>): devorandos servos objicere murenis <i>Sen</i> бросить рабов на съедение муренам; d. aliquid oculis <i>Just</i> пожирать что-л. глазами; oculis devorantibus <i>M</i> не спуская глаз; d. librum <i>C</i> с жадностью читать книгу; d. orationem <i>Pl</i> слушать с жадностью (не пропускать ни одного слова) (<i>ср.</i> 5); d. verba <i>Q</i> проглатывать (неясно произносить) слова; d. pudorem <i>Ap</i> откинуть стыд;\n<b>2)</b> поглощать (terra ab aqua devorata <i>Vtr, Sen etc.</i>): rotae devorantur <i>Vtr</i> колёса утопают (вязнут);\n<b>3)</b> терпеливо переносить (stultitiam hominum <i>C</i>): paucorum dierum molestiam devorate <i>C</i> потерпите несколько дней || скрывать, подавлять, сдерживать (lacrimas <i>O</i>; gemitus <i>Sen</i>): vox devoratur <i>PM</i> голос замирает (становится неслышным); devorari aliqua re <i>PM</i> стушеваться перед чём-л.;\n<b>4)</b> проедать, проматывать, расточать (patrimonium <i>C</i>): d. aliquem <i>Pl</i> промотать чьё-л. имение;\n<b>5)</b> не разжевав, проглотить, <i>перен.</i> плохо понять: oratio a multitudine devorabatur <i>C</i> ораторское искусство (Кальва) прошло незамеченным для толпы (было плохо усвоено ею) (<i>ср.</i> 1) || плохо запомнить, забыть (alicujus nomen <i>Pl</i>).\n\n<b>II dēvōro</b> <i>Acc</i> (= devovero) <i>fut. II к</i>\ndevoveo."],
  ["devorro", "<big>devorro</big>\n\n<b>dēvorro</b> <i>арх. v. l.</i> \n= deverro."],
  ["devorticulum", "<big>devorticulum</big>\n\n<b>dēvorticulum, ī</b> <i>n</i> <i>v. l.</i>\n= deverticulum."],
  ["devortium", "<big>devortium</big>\n\n<b>dēvortium, ī</b> <i>n</i> [devorto]\nповорот, боковая дорога: devortia itinerum <i>T</i> окольные пути."],
  ["devorto", "<big>devorto</big>\n\n<b>dēvorto</b> <i>арх. v. l.</i>\n= deverto."],
  ["devotamentum", "<big>devotamentum</big>\n\n<b>dēvōtāmentum, ī</b> <i>n</i> [devoveo]\nпроклятие, анафема <i>Tert</i>."],
  ["devotatio", "<big>devotatio</big>\n\n<b>dēvōtātio, ōnis</b> <i>f</i> [devoto]\nмольба, молитва <i>Vlg</i>."],
  ["devote", "<big>devote</big>\n\n<b>dēvōtē</b> <i>adv.</i>\n<b>1)</b> преданно, самоотверженно (d. ac strenue militare <i>CTh</i>);\n<b>2)</b> благоговейно, набожно <i>Eccl</i>."],
  ["devotio", "<big>devotio</big>\n\n<b>dēvōtio, ōnis</b> <i>f</i> [devoveo]\n<b>1)</b> обет, обетование богам (<i>преим.</i> подземным) <i>C, Ap</i>;\n<b>2)</b> пожертвование (vitae <i>C</i>); самопожертвование, обречение себя на смерть (d. Decii consulis <i>L, C</i>);\n<b>3)</b> заклинание, магическая формула <i>T, Su, Pt</i>;\n<b>4)</b> осыпание проклятиями, проклятие <i>Nep, Pt</i>;\n<b>5)</b> благоговение, набожность <i>Eccl</i>."],
  ["devoto", "<big>devoto</big>\n\n<b>dēvōto, āvī, ātum, āre</b> [<i>intens. к</i> devoveo]\n<b>1)</b> заклинать (sortes <i>Pl</i>; numina <i>Ap</i>);\n<b>2)</b> жертвовать, обрекать на смерть (aliquem <i>C</i>);\n<b>3)</b> проклинать (aliquem <i>Aug</i>)."],
  ["devotus", "<big>devotus</big>\n\n<b>dēvōtus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> devoveo;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> всецело преданный (alicui rei <i>J, Su etc.</i>): cum suis devotis <i>Cs</i> со своими приверженцами;\n<b>2)</b> проклятый (arbor, sanguis <i>H</i>; devotissima sub Romano jugo servitus <i>Aug</i>);\n<b>3)</b> благоговейно преданный (deo <i>Eccl</i>); благочестивый, набожный (jejunia <i>Aus</i>);\n<b>4)</b> приверженный, весьма склонный (vino <i>Ph</i>);\n<b>5)</b> всегда готовый (in externa proelia <i>Lcn</i>)."],
  ["devoveo", "<big>devoveo</big>\n\n<b>dē–voveo, vōvī, vōtum, ēre</b>\n<b>1)</b> обрекать, отдавать в жертву, жертвовать (aliquid Dianae <i>C</i>, Marti <i>H</i>): d. se diis <i>или</i> d. se обречь себя на смерть, жертвовать собой (pro re publica <i>C, L</i>; pro patria <i>Sen</i>); d. sicam <i>C</i> посвятить кинжал, <i>т. е.</i> предназначить для убийства;\n<b>2)</b> посвящать (d. se amicitiae alicujus <i>Cs</i>); отдавать (Andromedam monstris marinis <i>Prp</i>);\n<b>3)</b> посвящать подземным богам <i>Tib</i>; проклинать, предавать проклятию (aliquem <i>Nep etc.</i>; suas artes <i>O</i>). — <i>См. тж.</i> devotus."],
  ["devulsus", "<big>devulsus</big>\n\n<b>dēvulsus, a, um</b> <i>part. pf. к</i>\ndevello."],
  ["devus", "<big>devus</big>\n\n<b>dēvus, a, um</b> <i>арх.</i>\n= divus I."],
  ["dextans", "<big>dextans</big>\n\n<b>dēxtāns, antis</b> <i>m</i> [de + sextans]\n<b>1)</b> десять унций (10/12 асса) = 272,88 г <i>Vtr, Su etc.</i>;\n<b>2)</b> десять двенадцатых (jugeri <i>Col</i>)."],
  ["dextella", "<big>dextella</big>\n\n<b>dextella, ae</b> <i>f</i> [<i>demin. к</i> dextra]\nкак бы правая рука, нечто вроде правой руки (Quintus filius est Antonii d. <i>C</i>)."],
  ["dexter", "<big>dexter</big>\n\n<b>dexter, tra, trum</b> (<i>или</i> <b>tera, terum</b>) [<i>compar.</i> dexterior, ius, <i>superl.</i> dextimus]\n<b>1)</b> правый (oculus <i>Nep</i>; cornu <i>Cs</i>; ripa <i>PM</i>): rota dexterior <i>O</i> правое колесо; equus dexterior <i>Su</i> подручная («заводная») лошадь;\n<b>2)</b> ловкий, умелый, искусный (d. in omne genus <i>O</i>); удобный: dextro tempore <i>H</i> когда представится удобный случай; rebus d. modus <i>V</i> целесообразный образ действий;\n<b>3)</b> спасительный, благотворный, милостивый (Apollo <i>O</i>; numen <i>Q</i>): dexter adi! (<i>обращение к Геркулесу</i>) <i>V</i> прийди благосклонно на помощь! || благоприятный, счастливый (sidera <i>V</i>; omen <i>VF</i>). — <i>См. тж.</i> dextera <i>и</i> dextra."],
  ["dextera", "<big>dextera</big>\n\n<b>I dextera, ae</b> <i>f</i> <i>v. l.</i>\n= dextra I.\n\n<b>II dextera (dextra), ōrum</b> <i>n</i>\nнаходящееся направо, правое (transmutare dextera laevis <i>Lcr</i>)."],
  ["dextere", "<big>dextere</big>\n\n<b>dexterē</b> (<i>и</i> <b>dextrē</b>) [dexter]\nловко, искусно <i>L, H, Sen</i>."],
  ["dexteritas", "<big>dexteritas</big>\n\n<b>dexteritās, ātis</b> <i>f</i> [dexter]\n<b>1)</b> ловкость, умение, искусность <i>L, AG</i>;\n<b>2)</b> благотворность, спасительность (dii dexteritate pollentes <i>Eccl</i>)."],
  ["dextimus", "<big>dextimus</big>\n\n<b>dextimus, a, um</b> <i>adj. superl. к</i>\ndexter."],
  ["dextra", "<big>dextra</big>\n\n<b>I dextra</b> (<i>и</i> <b>dextera</b>), <b>ae</b> <i>f</i> [dexter]\n<b>1)</b> (<i>sc.</i> manus) правая рука, десница: dextram dare alicui <i>V</i> протянуть кому-л. правую руку (<i>в знак дружбы</i>); dextris inter se datis <i>L</i> обменявшись рукопожатием (<i>на прощание</i>); dextras jungere <i>V</i> подать друг другу руки (<i>т. е.</i> — установить дружеские отношения между собой); dextras mittere <i>Nep, T</i> (ferre <i>Just</i>) дать друг другу слово; dextras renovare <i>T</i> возобновить прежние обязательства; data d. <i>V</i> данная рука, <i>т. е.</i> клятва, честное слово; d. fidesque <i>V</i> добросовестность, верность клятве; dextram alicui tendere (porrigere) <i>C</i> протянуть кому-л. руку помощи; dextras fallere <i>V</i> нарушить клятву дружбы;\n<b>2)</b> (<i>sc.</i> pars) сторона: dextra atque sinistra <i>Su</i> с правой и с левой стороны; dextra viam <i>L</i> справа от дороги; dextra Danuvio (<i>dat.</i>) <i>Eutr</i> правее Дуная;\n<b>3)</b> <i>pl.</i> бойцы, боевые отряды (pugnae avidae dextrae <i>Sil</i>);\n<b>4)</b> борьба; civilis d. <i>Lcn</i> гражданская война.\n\n<b>II dextra, ōrum</b> <i>n</i> <i>v. l.</i>\n= dextera II.\n\n<b>III dextrā</b> <i>adv.</i>\nсправа, направо <i>L, Su etc.</i>"],
  ["dextrale", "<big>dextrale</big>\n\n<b>dextrāle, is</b> <i>f</i> [dexter]\nбраслет, запястье <i>Vlg</i>."],
  ["dextraliolum", "<big>dextraliolum</big>\n\n<b>dextrāliolum, ī</b> <i>n</i> [<i>demin. к</i> dextrale]\nбраслетик <i>Vlg</i>."],
  ["dextralis", "<big>dextralis</big>\n\n<b>dextrālis, is</b> <i>f</i> [dextra I]\nтопорик <i>Is</i>."],
  ["dextratio", "<big>dextratio</big>\n\n<b>dextrātio, ōnis</b> <i>f</i> [dexter]\nдвижение слева направо <i>Sol</i>."],
  ["dextre", "<big>dextre</big>\n\n<b>dextrē</b> <i>v. l.</i>\n= dextere."],
  ["dextrocherium", "<big>dextrocherium</big>\n\n<b>dextrō–chērium, ī</b> <i>n</i> [dexter + <i>греч.</i> cheir рука]\nбраслет <i>Capit, Treb</i>."],
  ["dextrorsum", "<big>dextrorsum</big>\n\n<b>dextrōrsum (dextrōrsus)</b> <i>adv.</i>\nнаправо, вправо, на правую сторону <i>Acc, H, L etc.</i>"],
  ["dextroversum", "<big>dextroversum</big>\n\n<b>dextrōversum (dextrōvorsum)</b> <i>Pl</i>\n= dextrorsum."],
  ["dextumus", "<big>dextumus</big>\n\n<b>dextumus</b> <i>v. l.</i>\n= dextimus."],
  ["di", "<big>di</big>\n\n<b>I dī</b> (= dii) <i>pl. к</i>\ndeus.\n\n<b>II dī-</b>\n= dis- <i>перед некоторыми согласными основного слова.</i>"],
  ["Dia", "<big>Dia</big>\n\n<b>Dīa, ae</b> <i>f</i>\nДия, <i>древнее название о-ва Наксоса</i> (<i>посвященного Вакху</i>) <i>O, PM</i>."],
  ["diabathrarius", "<big>diabathrarius</big>\n\n<b>diabathrārius, ī</b> <i>m</i> [diabathrum]\nбашмачник, изготовляющий сандални (диабатры) <i>Pl</i>."],
  ["diabathrum", "<big>diabathrum</big>\n\n<b>diabathrum, ī</b> <i>n</i> (<i>греч.</i>)\nдиабатр, <i>род лёгкой женской обуви</i> <i>Naev</i>."],
  ["diabetes", "<big>diabetes</big>\n\n<b>diabētēs, ae</b> <i>m</i> (<i>греч.</i>)\nводоподъёмная трубка, сифон <i>Col</i>."],
  ["Diablintes", "<big>Diablintes</big>\n\n<b>Diablintēs, um</b> <i>и</i> <b>Diablintī, ōrum</b> <i>m</i>\nдиаблинты, <i>ветвь народа Aulerci</i> (<i>см.</i>) <i>Cs, PM</i>."],
  ["diabolicus", "<big>diabolicus</big>\n\n<b>diabolicus, a, um</b> [diabolus]\nдьявольский <i>Eccl</i>."],
  ["diabolus", "<big>diabolus</big>\n\n<b>diabolus, ī</b> <i>m</i> (<i>греч.</i>)\nдьявол, дух зла и лжи <i>Eccl</i>."],
  ["diacatochia", "<big>diacatochia</big>\n\n<b>diacatochia, ae</b> <i>f</i> (<i>греч.</i>; <i>лат.</i> possessio)\nвладение, обладание <i>CJ</i>."],
  ["diacatochus", "<big>diacatochus</big>\n\n<b>diacatochus, ī</b> <i>m</i> (<i>греч.</i>; <i>лат.</i> possessor)\nвладелец, обладатель, собственник <i>CTh</i>."],
  ["diacecaumene", "<big>diacecaumene</big>\n\n<b>diacecaumenē, ēs</b> <i>f</i>\nжаркий пояс (земли) <i>Sol</i>."],
  ["diacon", "<big>diacon</big>\n\n<b>diācōn, onis</b> <i>m</i> <i>CTh</i>\n= diaconus."],
  ["diaconatus", "<big>diaconatus</big>\n\n<b>diāconātus, ūs</b> <i>m</i>\nдиаконат, диаконство <i>Eccl</i>."],
  ["diaconissa", "<big>diaconissa</big>\n\n<b>diāconissa, ae</b> <i>f</i> (<i>греч.</i>)\nдиаконисса, церковная служительница <i>CTh, Eccl</i>."],
  ["diaconus", "<big>diaconus</big>\n\n<b>diāconus, ī</b> <i>m</i> (<i>греч.</i>)\nдиакон, церковный служитель <i>CTh, Amm, Eccl</i>."],
  ["diacopi", "<big>diacopi</big>\n\n<b>diacopī, ōrum</b> <i>m</i> (<i>греч.</i>)\nоросительные канавы (<i>прорытые через плотину</i>) <i>Dig</i>."],
  ["diadema", "<big>diadema</big>\n\n<b>diadēma, atis</b> <i>n</i> <i>и</i> <b>diadēma, ae</b> <i>f</i> (<i>греч.</i>; <i>лат.</i> regium capitis insigne)\nсиняя с белым повязка на тиаре персидских царей, диадема <i>C, H, T etc.</i>"],
  ["diadematus", "<big>diadematus</big>\n\n<b>diadēmātus, a, um</b> [diadema]\nукрашенный головной повязкой, увенчанный диадемой (Apollo <i>PM</i>)."],
  ["diadota", "<big>diadota</big>\n\n<b>diadota, ae</b> <i>f</i> (<i>греч.</i>)\nраспределитель <i>CTh</i>."],
  ["diadumenos", "<big>diadumenos</big>\n\n<b>diadūmenos (-us), ē, on</b>\n<i>Sen, PM</i>. = diadematus."],
  ["Diadumenus", "<big>Diadumenus</big>\n\n<b>Diadūmenus, ī</b> <i>m</i>\n(Antoninus) Диадумен, <i>сын императора Макрина, казнённый, как и его отец, в 218 г. н. э.</i> <i>Lampr</i>."],
  ["diaeresis", "<big>diaeresis</big>\n\n<b>diaeresis, is</b> <i>f</i> (<i>греч.</i>) <i>грам.</i>\nдиэреза, <i>разложение одного слога (дифтонга) на два: напр.</i> materiai <i>вместо</i> materiae <i>Is</i>."],
  ["diaeta", "<big>diaeta</big>\n\n<b>diaeta, ae</b> <i>f</i> (<i>греч.</i>)\n<b>1)</b> правильный образ жизни, диета (diaeta curare) <i>C, CA</i>;\n<b>2)</b> комната, зал, жилище <i>PM, Su, Dig etc.</i>; <i>тж.</i> беседка <i>PJ, Su</i>;\n<b>3)</b> каюта <i>Pt</i>."],
  ["diaetarius", "<big>diaetarius</big>\n\n<b>diaetārius, ī</b> <i>m</i> [diaeta 2 <i>и</i> 3]\nслужитель, уборщик <i>Dig</i>."],
  ["diaeteta", "<big>diaeteta</big>\n\n<b>diaetēta, ae</b> <i>m</i> (<i>греч.</i>; <i>лат.</i> arbiter)\nарбитр, третейский судья <i>CJ</i>."],
  ["diaetetica", "<big>diaetetica</big>\n\n<b>diaetētica, ae</b> <i>и</i> <b>diaetēticē, ēs</b> <i>f</i> (<i>греч.</i>)\nдиететика, учение о медицинско-правильном образе жизни; <i>тж.</i> терапевтическая медицина (<i>в отличие от хирургии</i>) <i>Scr, CA</i>."],
  ["diagonalis", "<big>diagonalis</big>\n\n<b>diagōnālis, e</b> <i>Vtr</i>\n= diagonios."],
  ["diagonios", "<big>diagonios</big>\n\n<b>diagōnios, on</b> (<i>греч.</i>)\nдиагональный (linea <i>Vtr</i>)."],
  ["Diagoras", "<big>Diagoras</big>\n\n<b>Diagorās, ae</b> <i>m</i>\nДиагор:\n<b>1)</b> <i>родосский атлет, современник Пиндара</i> <i>C, AG</i>;\n<b>2)</b> <i>с острова Мелоса, ученик Демокрита Абдерского, прозванный Atheos («безбожный»); в 411 г. до н. э. был обвинён в нечестии и бежал из Афин</i> <i>C, Lact, MF</i>."],
  ["diagramma", "<big>diagramma</big>\n\n<b>diagramma, matis</b> <i>n</i> (<i>греч.</i>)\n<b>1)</b> музыкальная гамма <i>Vtr</i>;\n<b>2)</b> план, очерк <i>Vtr</i>."],
  ["dialectica", "<big>dialectica</big>\n\n<b>I dialectica, ae</b> <i>f</i> (<i>греч.</i>)\nискусство рассуждения, диалектика: eorum consuetudo dicendi non satis acuta est, propter ignorationem dialecticae <i>C</i> их (перипатетиков) манера говорить недостаточно остра, так как они незнакомы с диалектикой.\n\n<b>II dialectica, ōrum</b> <i>n</i>\nдиалектические исследования <i>C</i>."],
  ["dialectice", "<big>dialectice</big>\n\n<b>I dialecticē</b> [dialecticus]\nдиалектически, диалектическим методом (d. disputare <i>C</i>; probare <i>Q</i>).\n\n<b>II dialecticē, ēs</b> <i>f</i>\n= dialectica I."],
  ["dialecticus", "<big>dialecticus</big>\n\n<b>I dialecticus, a, um</b>\nдиалектический (disputationes <i>Q</i>; captiones <i>C</i>).\n\n<b>II dialecticus, ī</b> <i>m</i>\nдиалектик, знаток и учитель диалектики <i>C</i>."],
  ["dialectos", "<big>dialectos</big>\n\n<b>dialectos, ī</b> <i>f</i> (<i>греч.</i>)\nдиалект, наречие (d. Doris <i>Su</i>)."],
  ["Dialis", "<big>Dialis</big>\n\n<b>I Diālis, e</b> [<i>adj. к</i> Diespiter <i>и</i> deus]\nюпитеров: (flamen) D. <i>Vr, O, L, AG etc.</i> диал, <i>жрец Юпитера.</i>\n\n<b>II Diālis, e</b> [<i>adj. к</i> (flamen) D.]\nдиалов (D. apex <i>L</i>; conjux <i>O</i>): Diale flaminium <i>Su</i> сан жреца-фламина.\n\n<b>III diālis, e</b>\nнебесный, воздушный (viae <i>Ap</i>).\n\n<b>IV diālis, e</b> [dies]\nоднодневный: solent esse flamines diales, modo consules diales habemus <i>C ap. Macr</i> обычно бывали фламины-диалы, теперь же у нас появились и консулы-диалы (<i>игра на двойном значении слова dialis, по поводу скоропостижной смерти Кв. Фобия Максима, только что избранного консулом</i>)."],
  ["dialogista", "<big>dialogista</big>\n\n<b>dialogista, ae</b> <i>m</i> (<i>греч.</i>)\nискусный собеседник <i>Vulc</i>."],
  ["dialogus", "<big>dialogus</big>\n\n<b>dialogus, ī</b> <i>m</i> (<i>греч.</i>)\nразговор, беседа, диалог <i>C, Sen, Ap</i>."],
  ["dialutensis", "<big>dialutensis</big>\n\n<b>dialutēnsis, e</b> [<i>греч.</i> dia + <i>лат.</i> lutum]\nживущий в илистой почве (purpura <i>PM</i>)."],
  ["diamastigosis", "<big>diamastigosis</big>\n\n<b>diamastigōsis, is</b> <i>f</i> (<i>греч.</i>)\nбичевание <i>Tert</i>."],
  ["diameter", "<big>diameter</big>\n\n<b>diameter, trī</b> <i>m</i> <i>Boët</i>\n= diametros."],
  ["diametros", "<big>diametros</big>\n\n<b>diametros, ī</b> <i>f</i> (<i>греч.</i>; <i>лат.</i> dimetiens)\nпоперечник, диаметр <i>Vtr, Col, Macr</i>."],
  ["Diana", "<big>Diana</big>\n\n<b>Diāna</b> <i>и</i> <b>Dīāna</b>, ae, ae <i>f</i> [<i>из</i> Diviana = diva Jana]\nДиана, <i>дочь Юпитера и Латоны, сестра Аполлона, вначале богиня луны, позднее — и охоты, а тж. ночных чар (под именем Hecate) и родовспомогательного искусства (Lucina); соотв. греч. Артемиде (отсюда её тройственный характер</i> — diva triformis <i>H</i>: Luna <i>на небе</i>, D. <i>на земле и</i> Hecate <i>в подземном мире</i>); <i>перен.</i> луна (nocturna D. <i>O</i>) <i>и</i> охота (discrimina saeva Dianae <i>M</i>)."],
  ["Dianium", "<big>Dianium</big>\n\n<b>Diānium, ī</b> <i>n</i>\nДианий:\n<b>1)</b> <i>храм Дианы</i> <i>L</i>;\n<b>2)</b> <i>мыс на вост. побережье Испании</i> (<i>ныне</i> Denia) <i>C, PM</i>."],
  ["Dianius", "<big>Dianius</big>\n\n<b>Diānius, a, um</b> [Diana]\nдианин (lucus <i>Cato</i>; turba, <i>sc.</i> canum <i>O</i>)."],
  ["dianome", "<big>dianome</big>\n\n<b>dianomē, ēs</b> <i>f</i> (<i>греч.</i>)\nраздача, распределение (<i>денег</i>) <i>PJ</i>."],
  ["diapasma", "<big>diapasma</big>\n\n<b>diapasma, matis</b> <i>n</i> (<i>греч.</i>)\nблаговонный порошок (<i>для посыпания и окуривания</i>) <i>PM, M</i>."],
  ["diapason", "<big>diapason</big>\n\n<b>diapāsōn</b> <i>indecl.</i> (<i>греч.</i>)\nмузыкальная октава <i>Vtr, PM</i>."],
  ["diapente", "<big>diapente</big>\n\n<b>diapente</b> <i>indecl.</i> (<i>греч.</i>)\nмузыкальная квинта <i>Vtr</i>."],
  ["diaphoreticus", "<big>diaphoreticus</big>\n\n<b>diaphorēticus, a, um</b> (<i>греч.</i>)\nпотогонный <i>CA</i>."],
  ["diaphragma", "<big>diaphragma</big>\n\n<b>diaphragma, atis</b> <i>n</i> (<i>греч.</i>; <i>лат.</i> transversum saeptum, <i>поздн.</i> discretorium)\nгрудобрюшная преграда <i>CC, CA</i>."],
  ["Diapontius", "<big>Diapontius</big>\n\n<b>Diapontius, ī</b> <i>m</i> (<i>греч.</i>; <i>лат.</i> transmarinus)\nЗаморский <i>шутл. собств. имя</i> <i>Pl</i>."],
  ["diarium", "<big>diarium</big>\n\n<b>diārium, ī</b> <i>n</i> [dies]\n<b>1)</b> дневная порция, рацион, паёк (<i>преим. римск. солдат и рабов</i>) <i>C, H, Sen</i>: post asellum diaria non sumo <i>погов. <i>Pt</i> — см.</i> asellus II;\n<b>2)</b> подённая плата, дневное жалование (aliquid sibi de diariis facere <i>Pt</i>);\n<b>3)</b> подённая запись, дневник (d. scribere <i>Asellio ap. AG</i>)."],
  ["diarrhoea", "<big>diarrhoea</big>\n\n<b>diarrhoea, ae</b> <i>f</i> (<i>греч.</i>)\nдиаррея, понос <i>C, CA, Is</i>."],
  ["diaschisma", "<big>diaschisma</big>\n\n<b>diaschisma, atis</b> <i>n</i> (<i>греч.</i>) <i>муз.</i>\nдиасхизма, половина диеза <i>Boët</i>."],
  ["diastema", "<big>diastema</big>\n\n<b>diastēma, atis</b> <i>n</i> (<i>греч.</i>; <i>лат.</i> distantia и intervallum) (<i>dat. pl.</i> diastematis <i>Cens</i>)\nрасстояние, промежуток, интервал <i>Sid, Cens, Is</i>."],
  ["diastoleus", "<big>diastoleus</big>\n\n<b>diastole͡us, ī</b> <i>m</i> (<i>греч.</i>)\nревизор, контролёр <i>CJ</i>."],
  ["diastylos", "<big>diastylos</big>\n\n<b>diastȳlos, on</b> (<i>греч.</i>)\nс широко расставленными колоннами (<i>т. е.</i> <i>с промежутками в размере тройного диаметра каждой колонны</i>) (d. species aedium <i>Vtr</i>)."],
  ["diasyrticus", "<big>diasyrticus</big>\n\n<b>diasyrticus, a, um</b> (<i>греч.</i>)\nиздевательский, насмешливый <i>Spart, Hier</i>."],
  ["diatessaron", "<big>diatessaron</big>\n\n<b>diatessarōn</b> <i>indecl.</i> (<i>греч.</i>)\nмузыкальная кварта <i>Vtr</i>."],
  ["diatonicon", "<big>diatonicon</big>\n\n<b>diatonicon, ī</b> <i>n</i> (<i>греч.</i>)\nстена, заполненная внутри щебнем <i>PM</i>."],
  ["diatonum", "<big>diatonum</big>\n\n<b>diatonum, ī</b> <i>n</i>\nнатуральный (диатонический) звуковой ряд <i>Vtr, Macr</i>."],
  ["diatonus", "<big>diatonus</big>\n\n<b>diatonus, a, um</b> (<i>греч.</i>)\nпротяжённый: lateres diatoni <i>Vtr</i> кирпичи, равные по ширине самой стене (и служащие для её скрепления)."],
  ["diatreta", "<big>diatreta</big>\n\n<b>diatrēta, ōrum</b> <i>n</i> (<i>греч.</i>)\n(<i>sc.</i> pocula) чаши тонкой работы <i>M</i>."],
  ["diatretarius", "<big>diatretarius</big>\n\n<b>diatrētārius, ī</b> <i>m</i> [diatretus]\nмастер тонких токарных работ <i>CJ</i>."],
  ["diatretus", "<big>diatretus</big>\n\n<b>diatrētus, a, um</b>\nтонкой токарной работы (calix <i>Dig</i>)."],
  ["diatriba", "<big>diatriba</big>\n\n<b>diatriba, ae</b> <i>f</i> (<i>греч.</i>)\n<b>1)</b> философская <i>или</i> риторическая школа <i>AG</i>;\n<b>2)</b> спор, обсуждение <i>AG</i>."],
  ["diaulos", "<big>diaulos</big>\n\n<b>diaulos, ī</b> <i>m</i> (<i>греч.</i>)\nдвойной бег на состязаниях (<i>до меты и обратно</i>) <i>Vtr</i>."],
  ["diazoma", "<big>diazoma</big>\n\n<b>diazōma, matis</b> <i>n</i> (<i>греч.</i>; <i>лат.</i> praecinctio <i>или</i> balteus)\nузкий проход между смежными рядами зрителей в театре <i>Vtr</i>."],
  ["dibalo", "<big>dibalo</big>\n\n<b>dībālo, —, —, āre</b> [dis + balo]\nшумно выражать неодобрение, ошикать <i>Caec ap. Vr</i>."],
  ["dibaphus", "<big>dibaphus</big>\n\n<b>I dibaphus, (a), um</b> (<i>греч.</i>)\nдважды крашенный (purpura <i>PM</i>).\n\n<b>II dibaphus, ī</b> <i>f</i> (<i>sc.</i> vestis)\nверхняя, шитая пурпуром одежда высших должностных лиц: dibaphum cogitare <i>C</i> домогаться высокого поста."],
  ["dibrachys", "<big>dibrachys</big>\n\n<b>dibrachys</b> (<i>acc.</i> yn) (<i>реже</i> <b>dibrachus, ī</b>)\n<b>1)</b> <i>m</i> (<i>греч.</i>) дибрахий, <i>стопа из двух кратких слогов UU.</i>"],
  ["dic", "<big>dic</big>\n\n<b>dīc</b> <i>imper. к</i>\ndico II."],
  ["dica", "<big>dica</big>\n\n<b>dica, ae</b> <i>f</i> (<i>греч.</i>)\nсудебное дело, тяжба, процесс: alicui dicam scribere <i>C</i> (impingere <i>Ter</i> <i>и</i> subscribere <i>Pl</i>) затеять судебный процесс с кем-л.; sortiri dicas <i>C</i> назначить по жребию судей (<i>для решения дела</i>)."],
  ["dicabula", "<big>dicabula</big>\n\n<b>dīcābula</b> <i>или</i> <b>dīcibula, ōrum</b> <i>n</i> [dico II]\nпустая болтовня, вздор, басни <i>Tert</i>."],
  ["dicacitas", "<big>dicacitas</big>\n\n<b>dicācitās, ātis</b> <i>f</i> [dicax]\nколкость, остроумие, язвительность <i>C, Q, Su, Ap</i>."],
  ["dicacule", "<big>dicacule</big>\n\n<b>dicāculē</b> [dicaculus]\nязвительно, колко, насмешливо (aliquem tractare <i>Ap</i>)."],
  ["dicaculus", "<big>dicaculus</big>\n\n<b>dicāculus, a, um</b> [<i>demin. к</i> dicax]\nязвительный, колкий, насмешливый, едкий (amatrix <i>Pl</i>; sermo <i>Ap</i>)."],
  ["Dicaearcheus", "<big>Dicaearcheus</big>\n\n<b>Dicaearchēus, a, um</b> <i>St</i>\n= Dicarcheus."],
  ["Dicaearchia", "<big>Dicaearchia</big>\n\n<b>Dicaearchīa (-ēa), ae</b> <i>PM</i>\n= Dicarchis."],
  ["Dicaearchus", "<big>Dicaearchus</big>\n\n<b>Dicaearchus, ī</b> <i>m</i>\nДикеарх, <i>родом из Мессаны (Сицилия), ученик Аристотеля, философ и географ</i> <i>Vr, C</i>."],
  ["Dicarcheus", "<big>Dicarcheus</big>\n\n<b>Dicarchēus, a, um</b> [Dicarchis]\nдикархидский, <i>т. е.</i> путеоланский (urbs, proles <i>Sil</i>; sinus <i>St</i>; arena <i>Sid</i>)."],
  ["Dicarchis", "<big>Dicarchis</big>\n\n<b>Dicarchis, idos</b> <i>f</i>\nДикархида, <i>древнее название Puteoli</i> <i>Pt</i>."],
  ["Dicarchus", "<big>Dicarchus</big>\n\n<b>Dicarchus, ī</b> <i>m</i>\nДикарх, <i>основатель Дикархиды</i> <i>St</i>."],
  ["dicatio", "<big>dicatio</big>\n\n<b>dicātio, ōnis</b> <i>f</i> [dico I]\n<b>1)</b> просьба о предоставлении права гражданства (<i>другого государства</i>) <i>C</i>;\n<b>2)</b> прославление <i>CTh</i>;\n<b>3)</b> (<i>как почётное звание</i>) честь, благородие <i>или</i> преподобие <i>CTh, Eccl</i>."],
  ["dicatus", "<big>dicatus</big>\n\n<b>dicātus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> dico I;\n<b>2.</b> <i>adj.</i> священный (loca Christo dicatissima <i>Aug</i>)."],
  ["dicax", "<big>dicax</big>\n\n<b>dicāx, ācis</b> <i>adj.</i> [dico II]\nколкий, язвительный, насмешливый, едкий <i>Pl, C, Ctl, H etc.</i>: male d. <i>Pl, Macr</i> злой насмешник, острослов."],
  ["dice", "<big>dice</big>\n\n<b>dīce</b>\n<i>Pl</i> = dic (<i>imper. к</i> dico II)."],
  ["dicebo", "<big>dicebo</big>\n\n<b>dīcēbo</b> (= dicam) <i>арх. fut. к</i>\ndico II."],
  ["dichalcon", "<big>dichalcon</big>\n\n<b>dichalcon, ī</b> <i>n</i> (<i>греч.</i>)\nдвойной халкон (<i>мелкая монета</i> = <i>1/4 или 1/5 обола</i>) <i>Vtr</i>."],
  ["dichoneutus", "<big>dichoneutus</big>\n\n<b>dīchōneutus, a, um</b> (<i>греч.</i>)\nдважды плавленный, <i>т. е.</i> испорченный, фальшивый (aes <i>CTh</i>)."],
  ["dichoreus", "<big>dichoreus</big>\n\n<b>dichorēus, ī</b> <i>m</i> (<i>греч.</i>)\nстопа дихорей, двойной хорей <i>или</i> двойной трохей: –U–U <i>C</i>."],
  ["dichotomos", "<big>dichotomos</big>\n\n<b>dichotomos, on</b> (<i>греч.</i>; <i>лат.</i> dimidiatus)\nполовинный: d. luna <i>Macr</i> полулуние."],
  ["dicibilis", "<big>dicibilis</big>\n\n<b>dīcibilis, e</b> [dico II]\nвыразимый <i>Aug</i>."],
  ["dicibula", "<big>dicibula</big>\n\n<b>dīcibula</b> <i>v. l.</i>\n= dicabula."],
  ["dicio", "<big>dicio</big>\n\n<b>dicio, ōnis</b> (<i>nom. sg. неупотр.</i>) <i>f</i> [dico II]\nгосподство, власть: haec regio fuit dicionis regiae <i>Su</i> эта область находилась под властью царя; facere regionem dicionis suae <i>Q, C, Su</i> (redigere in dicionem suam <i>C</i>) подчинить область своей власти; venire in dicionem alicujus <i>L</i> покориться кому-л.; aures meas dedo in dicionem tuam <i>шутл. Pl</i> я слушаю тебя (весь обратился в слух)."],
  ["dicis", "<big>dicis</big>\n\n<b>dicis</b> <i>gen.</i> [<i>от неупотр.</i> <i>nom.</i> *dix]\n<i>встреч. в выраж.</i>: dicis causa (gratia) <i>V, C, Nep, Dig</i> для формы, для виду, формально."],
  ["dico", "<big>dico</big>\n\n<b>I dico, āvī, ātum, āre</b> [<i>intens. к</i> dico II]\n<b>1)</b> возвещать, провозглашать, предрекать (pugnam <i>LM</i>);\n<b>2)</b> жертвовать, посвящать (totum diem alicui <i>C</i>; librum Maecenati <i>PM</i>): d. Jovi aram <i>C</i> посвятить жертвенник Юпитеру; cygni Apollini dicati <i>C</i> лебеди, посвященные Аполлону; d. se totum alicui <i>C</i> совершенно предаться кому-л. (перейти на чью-л. сторону); se alicui in clientelam d. <i>Cs</i> отдаться под чьё-л. покровительство (поставить себя в клиентские отношения к кому-л.); d. se alii civitati <i>или</i> in aliam civitatem <i>C</i> сделаться гражданином другого государства; aliquam propriam d. <i>V</i> отдать кого-л. в настоящие жёны, <i>т. е.</i> по-настоящему выдать замуж;\n<b>3)</b> освящать (templum Jovis <i>L</i>): d. aquilam <i>T</i> освящать знамя; d. aliquem deum <i>QC</i> обожествлять кого-л.; inter numina dicatus Augustus <i>T</i> причисленный к сонму богов Август.\n\n<b>II dīco, dīxī, dictum, ere</b>\n<b>1)</b> говорить, сказать (aliquid alicui; d. mendacium <i>Pl, Nep</i>); произносить (litteram <i>C, Q</i>): dicit patrem abiisse <i>Pl</i> он говорит, что отец ушёл; dicar princeps Aeolium carmen ad Italos deduxisse modos <i>H</i> обо мне скажут, что я первый ввёл эолийский лад в италийскую поэзию; aedes, ubi dicitur habitare <i>Pl</i> дом, в котором он, говорят, живёт; quid dicis? <i>Pt</i> (ну), что скажешь?; quos supra <i>или</i> ante diximus <i>Cs, C etc.</i> о которых мы упомянули выше; vir dicendus <i>VP</i> человек, заслуживающий упоминания; d. orationem <i>C</i> произносить речь; salutem alicui d. <i>C etc.</i> (<i>преим. в письмах</i>) посылать кому-л. привет, приветствовать кого-л.; ars dicendi <i>C</i> искусство красноречия, риторика; d. docere <i>C</i> учить красноречию; dicam (<i>вводно</i>) <i>C</i> я сказал бы; dicam nunc <i>O</i> дай же сказать; ut ita dicam <i>или</i> dixerim <i>C, Ap</i> так сказать; quod inter nos liceat d. <i>C</i> говоря между нами; ut parcissime dicam <i>Q</i> выражаясь как можно мягче; nisi quid dicis (<i>вводно</i>) <i>C</i> если ты не возражаешь; excellere dicendo <i>C</i> быть выдающимся оратором; genus dicendi <i>C, Q</i> манера изъясняться, способ изложения, слог; d. causam <i>C</i> защищаться на суде; causam in foro d. <i>C</i> вести (чьё-л.) дело на суде; non (haud) <i>или</i> nullam causam dico <i>Pl, Ter</i> я не возражаю; sententiam d. <i>C</i> высказываться, голосовать (<i>в сенате</i>); d. jus <i>C</i> вершить суд, судить; dictum (ac) factum <i>Ter</i> сказано — сделано, <i>т. е.</i> немедленно; dicto citius <i>погов. V, H, L</i> быстрее слова, <i>т. е.</i> проворно, мигом; et quo magis diceres <i>Ter</i> и что ещё замечательнее; male d. alicui <i>Pt</i> бранить кого-л.; veteres illi, Herodotum dico et Thucydidem <i>C</i> эти древние (писатели), я имею в виду Геродота и Фукидида; bene dixti (= dixisti) <i>Ter</i> ты прав, <i>но</i>: bene dicite <i>Pl</i> ни слова больше! (<i>ср.</i> favete linguis); non dici potest, quam... <i>C</i> нельзя выразить, до какой степени...;\n<b>2)</b> утверждать: quem esse negas, eumdem esse dicis <i>C</i> ты утверждаешь существование того, кто, по твоим же словам, не существует; quid dicis igitur? <i>C</i> что же ты утверждаешь?;\n<b>3)</b> называть, именовать (d. aliquem perfectum oratorem <i>C</i>): dicta a Pallade terra <i>O</i> земля, названная именем Паллады (<i>т. е.</i> Афины); quae harmonia dicitur <i>C</i> так называемая гармония; esse quid hoc dicam? <i>M</i> как мне это назвать?, <i>т. е.</i> что бы это могло значить?;\n<b>4)</b> назначать, определять, устанавливать (diem alicui rei <i>Ter, C etc.</i>; locum <i>L</i>): d. leges pacis <i>L</i> устанавливать условия мира;\n<b>5)</b> избирать (aliquem dictatorem <i>Cs</i>; magistrum equitum <i>C</i>);\n<b>6)</b> обещать (sua bona cognatis <i>Pl</i>);\n<b>7)</b> описывать, воспевать (d. bella <i>L, H</i>; laudes Phoebi et Dianae <i>H</i>);\n<b>8)</b> возвещать, предсказывать (sortes per carmina <i>H</i>);\n<b>9)</b> слагать, сочинять (versus, carmen <i>V, H</i>)."],
  ["dicrota", "<big>dicrota</big>\n\n<b>dicrota, ae</b> <i>f</i> (<i>sc.</i> navis) <i>bAl</i>\n= dicrotum."],
  ["dicrotum", "<big>dicrotum</big>\n\n<b>dicrotum, ī</b> <i>n</i> (<i>греч.</i>)\nдикрот, судно, галера с двойным рядом вёсел <i>C</i>."],
  ["Dicta", "<big>Dicta</big>\n\n<b>Dicta, ae</b> <i>и</i> <b>Dictē, ēs</b> <i>f</i>\nДикта, <i>гора в вост. части Крита, посвященная Юпитеру</i> <i>PM</i>."],
  ["Dictaeus", "<big>Dictaeus</big>\n\n<b>Dictaeus, a, um</b> [Dicta]\nдиктейский, <i>поэт.</i> критский: D. rex <i>O</i> = Minos, <i>но тж.</i> <i>V</i> = Juppiter; Dictaea arundo <i>Sil</i> стрелы критских лучников."],
  ["dictamnum", "<big>dictamnum</big>\n\n<b>dictamnum, ī</b> <i>n</i> <i>V, PM, VM</i>\n= dictamnus."],
  ["dictamnus", "<big>dictamnus</big>\n\n<b>dictamnus, ī</b> <i>f</i> <i>бот.</i>\nясенец, дикий бадьян (<i>Origanum Dictamnum, <i>L</i>.</i>) <i>C</i>."],
  ["dictata", "<big>dictata</big>\n\n<b>dictāta, ōrum</b> <i>n</i> [dicto]\n<b>1)</b> записи лекций, записки, письменные задания <i>C, H</i>, <i>тж.</i> правила <i>J, Su</i>; тезисы (d. perdiscere <i>C</i>);\n<b>2)</b> правила команды (<i>в фехтовании</i>): ad d. pugnare <i>Pt</i> сражаться согласно правилам."],
  ["dictatio", "<big>dictatio</big>\n\n<b>dictātio, ōnis</b> <i>f</i> [dicto]\nдиктовка <i>Dig, Sid etc.</i>"],
  ["dictatiuncula", "<big>dictatiuncula</big>\n\n<b>dictātiuncula, ae</b> <i>f</i> [<i>demin. к</i> dictatio]\nмаленькая диктовка <i>Hier</i>."],
  ["dictator", "<big>dictator</big>\n\n<b>dictātor, ōris</b> <i>m</i> [dicto]\nдиктатор:\n<b>1)</b> <i>в Риме — носитель временной неограниченной власти, избиравшийся в чрезвычайных случаях</i> (dictatorem dicere <i>C, Cs, L</i>, facere <i>C</i>, legere, creare <i>L</i>); <i>тж. лицо, избиравшееся, в случае отсутствия консулов, для выполнения отдельных заданий</i> (d. ludorum faciendorum <i>L</i>);\n<b>2)</b> <i>в некоторых городах Италии</i> (<i>Ланувии, Альбе и др.</i>) — <i>высшее должностное лицо</i> <i>C, L</i>."],
  ["dictatorius", "<big>dictatorius</big>\n\n<b>dictātōrius, a, um</b> [dictator]\nдиктаторский (gladius <i>C</i>): juvenis d. <i>L</i> сын диктатора."],
  ["dictatrix", "<big>dictatrix</big>\n\n<b>dictātrīx, trīcis</b> <i>f</i> [dictator]\nдиктаторша, <i>шутл.</i> повелительница <i>Pl</i>."],
  ["dictatura", "<big>dictatura</big>\n\n<b>dictātūra, ae</b> <i>f</i> [dictator]\n<b>1)</b> достоинство (звание) диктатора, диктаторство, диктатура (dictaturam gerere <i>C</i>; dictaturam — <i>или</i> dictatura se <i>Cs</i> — abdicare <i>L</i>);\n<b>2)</b> обязанность давать ученикам задания (<i>см.</i> dictata) <i>Su</i>."],
  ["Dicte", "<big>Dicte</big>\n\n<b>Dictē, ēs</b> <i>f</i> <i>v. l.</i>\n= Dicta."],
  ["dicteria", "<big>dicteria</big>\n\n<b>dictēria, ōrum</b> <i>n</i> [dictum]\nедкие остроты, колкости (d. dicere in aliquem <i>M</i>)."],
  ["dictio", "<big>dictio</big>\n\n<b>dictio, ōnis</b> <i>f</i> [dico]\n<b>1)</b> произнесение, высказывание, изрекание (sententiae <i>C</i>); изречение (oraculi <i>Acc ap. C, L</i>): d. multae <i>C</i> установление пени (штрафа);\n<b>2)</b> произнесение речи, доклад, речь: dictioni operam dare <i>C</i> произносить речи; d. testimonii <i>Ter</i> свидетельское показание; dictiones discipulorum <i>C</i> ученические упражнения в составлении речей; dictiones subitae <i>C</i> (extemporales <i>Q</i>) экспромтные выступления; dictio causae <i>C</i> ведение (защита) дела;\n<b>3)</b> способ изложения, слог (d. vel elocutio <i>Q</i>); оборот речи <i>Q, AG</i>; выражение <i>или</i> слово (d. propria <i>Q</i>);\n<b>4)</b> разговор, беседа (semota dictio <i>T</i>)."],
  ["dictiosus", "<big>dictiosus</big>\n\n<b>dictiōsus, a, um</b> [dictum]\nостроумный, язвительный <i>Vr</i>."],
  ["dictito", "<big>dictito</big>\n\n<b>dictito, āvī, ātum, āre</b> [<i>frequ. к</i> dico II]\n<b>1)</b> часто говорить (aliquid <i>L etc.</i>): d. causas <i>C</i> вести судебные процессы;\n<b>2)</b> постоянно называть (d. terrarum orbem esse deorum matrem <i>Lcr</i>): d. aliquem sanum recteque valentem <i>H</i> беспрестанно говорить о чьём-л. безукоризненном здоровье;\n<b>3)</b> говорить, утверждать: profectus, ut dictitabat, ad Caesarem <i>Cs</i> отправившись, как он говорил, к Цезарю; male dictitatur tibi vulgo in sermonibus <i>Pl</i> нехорошие толки идут о тебе в народе."],
  ["dictiuncula", "<big>dictiuncula</big>\n\n<b>dictiuncula, ae</b> <i>f</i> [<i>demin. к</i> dictio]\nсловечко <i>Ap</i>."],
  ["dicto", "<big>dicto</big>\n\n<b>dicto, āvī, ātum, āre</b> [<i>intens. к</i> dico II]\n<b>1)</b> часто говорить, повторять (aliquid <i>C etc.</i>): hoc penus et haec penus veteres dictaverunt <i>AG</i> в старину слово penus употреблялось как в среднем, так и в женском роде;\n<b>2)</b> диктовать (epistulam alicui <i>C</i>); (диктуя) сочинять, составлять (carmen <i>H</i>; testamentum <i>Su</i>): fabulas in scaenam d. <i>Ap</i> писать театральные пьесы;\n<b>3)</b> предлагать, предписывать (quod natura dictat <i>Q</i>)."],
  ["dictoaudientia", "<big>dictoaudientia</big>\n\n<b>dicto–audientia, ae</b> <i>f</i>\nпослушание всякому слову, безусловное повиновение <i>Eccl</i>."],
  ["dictor", "<big>dictor</big>\n\n<b>dictor, ōris</b> <i>m</i> [dico II]\nговорящий <i>Aug</i>."],
  ["dictum", "<big>dictum</big>\n\n<b>dictum, ī</b> <i>n</i> [dico II]\n<b>1)</b> выражение, слово (d. ridiculum <i>Pl</i>): dicti studiosus <i>Enn</i> заботящийся о чистоте (<i>или</i> изяществе) речи; dicta cum factis componere <i>Sl</i> сопоставить слова с делами; dicta testium <i>C</i> показания свидетелей; dicta tristia <i>O</i> жалобы, <i>но тж.</i> <i>V</i> мрачные предсказания (оракула); haec ubi dicta dedit <i>V, L</i> когда он это сказал; mutua dicta reddere <i>L</i> беседовать; res dicta secuta est <i>O</i> сказано, сделано; dictis aliquem compellare (castigare) <i>V</i> бранить кого-л.;\n<b>2)</b> изречение (d. Catonis <i>C</i>);\n<b>3)</b> (<i>тж.</i> bonum d. <i>Enn</i>) острое словцо, острота: d. arcessitum <i>C</i> натянутая острота;\n<b>4)</b> слово, обещание: dicto captus <i>Nep</i> связанный (будучи связан) словом;\n<b>5)</b> предписание, приказание (dicto parere <i>L</i>; dicta peragere <i>O</i>)."],
  ["dicturio", "<big>dicturio</big>\n\n<b>dicturio, —, —, īre</b> [<i>desiderat. к</i> dico II]\nжелать говорить: fortiter a se facta d. <i>Macr</i> гореть желанием рассказать о своих подвигах."],
  ["dictus", "<big>dictus</big>\n\n<b>I dictus, a, um</b> <i>part. pf. к</i>\ndico II.\n\n<b>II dictus, ūs</b> <i>m</i> [dico II]\nслово, выражение <i>AV</i>."],
  ["Dictynna", "<big>Dictynna</big>\n\n<b>Dictynna, ae</b> <i>f</i> (<i>греч.</i>)\n«Расставляющая тенёта», <i>эпитет Дианы, как богини охоты</i> <i>O</i>."],
  ["Dictynneum", "<big>Dictynneum</big>\n\n<b>Dictynnēum, ī</b> <i>n</i>\n<i>храм Дианы, близ Спарты</i> <i>L</i>."],
  ["Dictys", "<big>Dictys</big>\n\n<b>Dictys, yis</b> <i>и</i> <b>yos</b> <i>m</i>\nДиктис:\n<b>1)</b> D. Cretensis, <i>греч. историк Троянской войны</i>;\n<b>2)</b> <i>моряк, превращенный Вакхом в дельфина</i> <i>O</i>;\n<b>3)</b> <i>один из Кентавров</i> <i>O</i>."],
  ["Didascalica", "<big>Didascalica</big>\n\n<b>Didascalica, ōrum</b> <i>n</i> (<i>греч.</i>)\n«Дидаскалии», <i>дидактическое сочинение Л. Аттия о сценическом искусстве</i> <i>AG</i>."],
  ["didascalicus", "<big>didascalicus</big>\n\n<b>didascalicus, a, um</b> (<i>греч.</i>)\nпоучительный, назидательный (opusculum <i>Aus</i>)."],
  ["didici", "<big>didici</big>\n\n<b>didicī</b> <i>pf. к</i>\ndisco I."],
  ["dididi", "<big>dididi</big>\n\n<b>dīdidī</b> <i>pf. к</i>\ndido."],
  ["diditus", "<big>diditus</big>\n\n<b>dīditus, a, um</b> <i>part. pf. к</i>\ndido."],
  ["Didius", "<big>Didius</big>\n\n<b>Dīdius, a, um</b>\nДидий, <i>римск. nomen; наиболее известны</i>:\n<b>1)</b> T. D., <i>претор в Македонии ок. 100 г. до н. э., консул в 98 г. до н. э., затем проконсул в Испании; пал в сражении с марсами в 89 г. до н. э.</i> <i>Fl, VM, VP, O</i>;\n<b>2)</b> C. D., <i>легат Цезаря, пал в Испании, сражаясь с войсками сыновей Помпея</i> (<i>46 г. до н. э.</i>) <i>bH</i>;\n<b>3)</b> M. D. Salvius Julianus, <i>римск. император в 193 г. н. э., преемник Пертинакса</i> (<i>убит после двухмесячного царствования</i>) <i>Spart</i>."],
  ["dido", "<big>dido</big>\n\n<b>I dīdo, dīdidī, dīditum, ere</b> [dis + do]\n<b>1)</b> раздавать, распределять (munia servis <i>H</i>);\n<b>2)</b> <i>pass.</i> didi распространяться (rumor diditur <i>V</i>; didita per provincias fama <i>T</i>): diditur in venas cibus <i>Lcr</i> питательные вещества расходятся по жилам.\n\n<b>II Dīdō, ūs</b> <i>и</i> <b>ōnis</b> <i>f</i> (<i>тж.</i> Elisa <i>и</i> Elissa)\nДидона, <i>дочь широкого царя Бела, сестра Пигмалиона, супруга Сихея (или Ацерба); ок. 888 г. до н. э. основала в Сев. Африке, близ Утики, Карфаген; чтобы избежать брака с ливийским царём Иарбом, покончила с собой (по Вергилию, из-за безнадёжной любви к Энею</i>) <i>V, Just etc.</i>"],
  ["didrachma", "<big>didrachma</big>\n\n<b>didrachma, atis</b> <i>n</i> (<i>иногда</i> <b>ae</b> <i>f</i>) <i>и</i> <b>didrachmum, ī</b> <i>n</i> (<i>греч.</i>)\nдидрахма, <i>монета достоинством в 2 драхмы</i> <i>Eccl</i>."],
  ["diduco", "<big>diduco</big>\n\n<b>dī–dūco, dūxī, ductum, ere</b> [dis + duco]\n<b>1)</b> разводить, раздвигать, растопыривать (digitos <i>C</i>); разгонять, развеивать (ventus nubes diducit <i>Lcr</i>); развязывать (nodos manu <i>O</i>); рассеивать (hostes <i>T</i>);\n<b>2)</b> раскрывать (os <i>Pt</i>; labra <i>PJ, Q</i>; oculum alicui <i>CC</i>): rima diducta <i>Pt</i> проделанная (в двери) щель; rictum alicujus risu d. <i>H</i> заставлять хохотать (потешать) кого-л. || растворять (fores <i>T</i>): terram ad capita d. <i>V</i> разрыхлять почву у корней; d. summam arenam <i>L</i> снять верхний слой песка;\n<b>3)</b> разделять (d. assem in partes <i>H</i>): d. aliquem ab aliquo <i>C, O</i> поссорить кого-л. с кем-л.; civitas diducta bello civili <i>T</i> государство, разделённое гражданской войной; ea ultio senatum in studia diduxit <i>T</i> эта месть разделила сенат на партии; d. matrimonium <i>Su</i> (nuptias <i>Sen</i>) расстроить <i>или</i> расторгнуть брак; d. animum in multiplex officium <i>Q</i> заниматься разнообразной деятельностью; oratio rivis diducta est, non fontibus <i>C</i> речь разделена на отдельные ручьи, а не на источники (<i>т. е.</i> несмотря на расчленённость, сохраняет внутреннее единство);\n<b>4)</b> <i>воен.</i> развёртывать, растягивать, вытягивать в одну линию (copias <i>Cs</i>; cornua <i>L</i>);\n<b>5)</b> разобщать, раздроблять (diducendi erant milites <i>Cs</i>); рассредоточивать (diductae naves <i>Cs</i>); разъединять (arva et urbes diductae <i>V</i>): animus varietate rerum diductus <i>C</i> внимание, распылившееся вследствие разнообразия дел; in multas cogitationes diductus sum, quare... <i>Pt</i> я терялся во множестве догадок, отчего...;\n<b>6)</b> проводить (sulcum <i>J</i> — <i>v. l. к</i> deduco);\n<b>7)</b> отделять, различать (vocabulum ab appellatione <i>Q</i>)."],
  ["diductio", "<big>diductio</big>\n\n<b>dīductio, ōnis</b> <i>f</i> [diduco]\n<b>1)</b> растяжение, расширение, распространение (spiritus <i>Sen</i>);\n<b>2)</b> развитие (rationis <i>C</i>);\n<b>3)</b> разделение, разобщение, разлучение: d. rerum <i>Sen</i> разделение (первичного хаоса) на стихии."],
  ["Didyma", "<big>Didyma</big>\n\n<b>Didyma, ōn</b> <i>n</i> (<i>pl.</i>)\nДидимы, <i>городок к югу от Милета с храмом и оракулом Аполлона</i> <i>PM</i>."],
  ["Didymae", "<big>Didymae</big>\n\n<b>Didymae, ārum</b> <i>f</i>\nДидимы (<i>греч.</i> «близнецы»), <i>два островка в Эгейском море близ о-ва Сирос</i> <i>PM</i>."],
  ["Didymeum", "<big>Didymeum</big>\n\n<b>Didymēum (-on), ī</b> <i>n</i>\n<i>храм Аполлона близ города Didyma</i> <i>PM, Macr</i>."],
  ["die", "<big>die</big>\n\n<b>diē</b> <i>арх. <i>gen. sg. Pl, V, Sl</i> и арх.</i> <i>dat. Pl к</i>\ndies."],
  ["diecula", "<big>diecula</big>\n\n<b>diēcula, ae</b> <i>f</i> [<i>demin. к</i> dies]\nденёк, <i>перен.</i> короткий срок, короткое время, <i>тж.</i> некоторая отсрочка <i>Pl, Ter, Ap</i>: dieculam ducere <i>C</i> оттягивать срок (платежа)."],
  ["Diensis", "<big>Diensis</big>\n\n<b>Diēnsis, e</b> <i>adj. к</i>\n<b>1)</b> Dium, <i>город в Македонии</i> <i>PM</i> <i>и</i>\n<b>2)</b> Dea (<i>или</i> Dia) Vocontiorum, <i>город в Галлии</i> <i>GT</i>."],
  ["diepristine", "<big>diepristine</big>\n\n<b>diē–prīstine (-inī)</b> <i>adv.</i> (<i>тж. раздельно</i>)\nнакануне <i>AG, Macr</i>."],
  ["dierecte", "<big>dierecte</big>\n\n<b>dīērēctē</b> <i>adv.</i> [dierectus] <i>бран.</i>\nв состоянии повешенного: abi (abin hinc) d.! <i>Pl</i> проваливай!, сгинь!"],
  ["dierectus", "<big>dierectus</big>\n\n<b>dīērēctus, a, um</b> [dis + erigo]\nподнятый, вытянутый, растянутый (<i>о распятом на кресте</i>): lien d. est <i>Pl</i> селезёнка распухла (<i>или</i> мучительно болит); <i>преим. в бранных выражениях</i>: i (abi) d.! <i>Pl</i> пропади ты пропадом!; apage in dierectum istam insanitatem! <i>Vr</i> пошёл прочь, сумасброд!"],
  ["dies", "<big>dies</big>\n\n<b>diēs, ēī</b> (<i>арх. gen.</i> <b>diē</b> <i>Pl, Sl, V</i> <i>и</i> <b>diī</b> <i>V</i>) <i>m, f</i> (<i>в pl. тк. m</i>)\n<b>1.</b> <i>m</i>\n<b>1)</b> день: postero die <i>C</i>, postera die <i>Sl</i>, altero die <i>и</i> postridie ejus diei <i>Cs</i> на другой день; diem ex die expectare <i>C</i> <i>и</i> diem de die prospectare <i>L</i> со дня на день ожидать; hesterno (hodierno) die <i>C etc.</i> вчера (сегодня); multo die <i>Cs</i> (ad multum diem <i>C</i> <i>или</i> ad multum diei <i>L</i>) до позднего дневного часа; in (singulos) dies <i>T, C</i> изо дня в день (со дня на день); rogare aliquid in diem <i>L</i> попросить что-л. на один день; cum die <i>O</i> с наступлением дня; de die <i>Pl</i> днём (среди бела дня); ad diem secundo vel tertio <i>Treb</i> два-три раза в день; bis in die <i>C</i> <i>и</i> bis die <i>CC</i> дважды в день; in diem vivere <i>L</i> жить сегодняшним днём; die ac nocte <i>PM</i> за один день и одну ночь (в одни сутки); diem noctemque <i>Cs</i>, dies noctesque <i>C</i> <i>и</i> nocte dieque <i>M</i> день и ночь (круглые сутки, беспрерывно); d. (<i>или</i> d. natalis <i>C</i> <i>и</i> d. natalicius <i>M</i>) день рождения (d. meus <i>C</i>); plurium dierum hiems <i>PJ</i> буря, длившаяся (длящаяся) ряд дней; d. supremus <i>O, Nep etc.</i> день смерти (смертный час); obire diem supremum <i>Nep</i> (suum <i>C</i>), obire diem <i>Nep</i> <i>или</i> diem fungi <i>Just</i> умереть;\n<b>2)</b> время (d. levat luctum <i>C</i>; <i>тж.</i> d. adimit aegritudinem <i>Ter</i>): ante (suam) diem <i>V</i> прежде времени, безвременно <i>или</i> <i>O</i> до срока; paucis illis diebus <i>C</i> на этих днях, несколько дней тому назад; die aliquid vendere <i>Cato</i> продать что-л. вовремя;\n<b>3)</b> погода (d. tranquillus <i>PM</i>); климатические условия (sub quocumque die <i>Lcn</i>);\n<b>4)</b> свет (non cernitur dies <i>PM</i>): diem videre <i>O</i> увидеть свет (родиться);\n<b>5)</b> дневной путь: dierum plus triginta in longitudinem patere <i>L</i> простираться на тридцать дней пути с лишком;\n<b>6)</b> дата, число (epistulae <i>C</i>);\n<b>2.</b> <i>f</i> <i>преим.</i> (<i>у <i>C</i> всегда</i>)\n<b>1)</b> срок: diem dare (dicere) <i>C, Cs</i> назначить день, срок; ad (certam) diem <i>Cs</i> в установленный срок; obire diem <i>C</i> явиться в (соблюсти) срок; emere aliquid in diem <i>Nep</i> <i>или</i> die caeca <i>Pl</i> покупать что-л. в кредит; prodicere diem <i>L</i> продлить срок (отсрочить); d. pecuniae <i>C, L</i> <i>и</i> d. solutionis <i>Dig</i> срок платежа; d. annua <i>C</i> годичный срок (платежа); aliqua die <i>Pt</i> в какой-л. день, когда-нибудь;\n<b>2)</b> отсрочка, передышка (postulare perexiguam diem <i>C</i>)."],
  ["diesis", "<big>diesis</big>\n\n<b>diesis, eos</b> (<i>acc.</i> in, <i>abl.</i> i) <i>f</i> (<i>греч.</i>)\nдиез, <i>четверть музыкального тона</i> (<i>в древнейшей музыке; у Пифагора — полтона</i>) <i>Vtr, Macr</i>."],
  ["Diespiter", "<big>Diespiter</big>\n\n<b>Diēspiter, tris</b> <i>m</i> (<i>греч.</i>) <i>арх. Vr, Pl, H etc.</i> = Juppiter; <i>Lact</i>\n= Pluto."],
  ["dieteris", "<big>dieteris</big>\n\n<b>dietēris, idis</b> <i>f</i> (<i>греч.</i>)\nдвухлетие <i>Cens</i>."],
  ["diezeugmena", "<big>diezeugmena</big>\n\n<b>diezeugmena, ōn</b> <i>n pl.</i>\n«разобщённые», <i>особый вид двух тетрахордов, не имеющих общих тонов</i> <i>Vtr</i>."],
  ["dif-", "<big>dif-</big>\n\n<b>dif-</b>\n<i>приставка</i> — dis- <i>перед начальным</i> f <i>основного слова.</i>"],
  ["diffamatio", "<big>diffamatio</big>\n\n<b>diffāmātio, ōnis</b> <i>f</i> [diffamo]\nразглашение, распространение (Christianae religionis <i>Aug</i>)."],
  ["diffamatus", "<big>diffamatus</big>\n\n<b>diffāmātus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> diffamo;\n<b>2.</b> <i>adj.</i> пресловутый, покрытый позором (homo turpitudine vitae diffamatissimus <i>AG</i>)."],
  ["diffamo", "<big>diffamo</big>\n\n<b>dif–fāmo, āvī, ātum, āre</b> [fama]\n<b>1)</b> разглашать, предавать гласности, распространять (sermonem <i>Vlg</i>; diffamata religio Christiana <i>Aug</i>); расславить (adulterium <i>O</i>): d. prava <i>T</i> распускать дурные слухи;\n<b>2)</b> обесславить, лишить доброго имени, опорочить (aliquem probroso carmine <i>T</i>)."],
  ["differens", "<big>differens</big>\n\n<b>differēns, entis</b>\n<b>1.</b> <i>part. praes. к</i> differo;\n<b>2.</b> <i>n</i> разница, различие, расхождение <i>Q</i>."],
  ["differenter", "<big>differenter</big>\n\n<b>differenter</b> [differo]\nс различием, различно <i>Sol</i>."],
  ["differentia", "<big>differentia</big>\n\n<b>differentia, ae</b> <i>f</i> [differo]\n<b>1)</b> разница, различие (naturarum <i>C</i>; morum <i>Pt</i>);\n<b>2)</b> <i>лог.</i> видовое различие: genus est notio ad plures differentias pertinens <i>C</i> род есть понятие, охватывающее ряд предметов с видовыми различиями."],
  ["differitas", "<big>differitas</big>\n\n<b>differitās, ātis</b> <i>f</i> [differo]\nразличие, разница <i>Lcr</i>."],
  ["differo", "<big>differo</big>\n\n<b>dif–fero, distulī, dīlātum, differre</b>\n<b>1)</b> разносить (late d. favillam <i>Lcr</i>); раздувать, распространять (ignem distulit ventus <i>Cs</i>); растаскивать (aliquem frustillatim <i>Pl</i>); разгонять, рассеивать (classem <i>H</i>; nubila <i>V</i>); широко (пошире) рассаживать (arbores <i>V</i>): d. ulmos in versum <i>V</i> — <i>см.</i> versus III;\n<b>2)</b> распространять, разглашать, ширить (rumores <i>Ter, Nep</i>): d. aliquid sermonibus <i>и</i> d. aliquem rumoribus <i>L</i> распускать слухи о чём-л. <i>или</i> о ком -л.;\n<b>3)</b> откладывать, переносить (d. rem in aliud tempus <i>C</i>): d. aliquid in annum <i>H</i> отложить что-л. на год; d. tempus <i>C</i> дать отсрочку, отсрочить; stipendium militum d. <i>Su</i> задерживать выплату жалованья солдатам; d. aliquem (in aliud tempus) <i>C, T</i> назначать кому-л. другое время, <i>т. е.</i> заставлять его ждать; si foret hoc nostrum fato dilatus in aevum <i>H</i> если бы (Луцилию) суждено было жить в наше время; d. posse <i>Lcn</i> уметь выжидать (удобного случая); d. se venturas in horas <i>O</i> откладывать на будущее время, <i>т. е.</i> медлить, тянуть;\n<b>4)</b> уезжать, быть в отсутствии: a conspectu alicujus distulisse <i>Ap</i> перестать видеться с кем-л.;\n<b>5)</b> изгонять, удалять, вытеснять (dilato Mithridate <i>Fl</i>);\n<b>6)</b> озадачивать, ошеломлять (aliquem dictis suis <i>Pl</i>);\n<b>7)</b> <i>pass.</i> differri мучиться, изнывать, томиться (amore alicujus <i>Pl</i>); laetitia differri <i>Pl</i> быть вне себя от радости;\n<b>8)</b> (<i>без</i> <i>pf. и supin.</i>) различаться, разниться (inter se <i>C etc.</i>; ab aliquo <i>и</i> ab aliqua re <i>C etc.</i>; cum aliqua re <i>C</i>; alicui rei <i>H etc.</i>): quid differt? <i>H</i> какая разница?; aliquid differt <i>C</i> есть некоторая разница; nihil differt <i>C</i> нет никакой разницы; inter se magnitudine, non genere d. <i>C</i> различаться между собой количественно, но не качественно; rebus congruentes nominibus differebant <i>C</i> соглашаясь друг с другом по существу, они расходились в словах."],
  ["differtus", "<big>differtus</big>\n\n<b>dif–fertus, a, um</b> [farcio]\nнабитый, наполненный, переполненный, кишащий (forum differtum nautis atque cauponibus <i>H</i>; provincia differta praefectis <i>Cs</i>): corpus differtum odoribus <i>T</i> тело, насыщенное благовониями, <i>т. е.</i> набальзамированное."],
  ["diffibulo", "<big>diffibulo</big>\n\n<b>dif–fībulo, —, —, āre</b>\nрасстёгивать, отстёгивать (chlamydem auro <i>St</i>)."],
  ["difficile", "<big>difficile</big>\n\n<b>difficile</b> <i>adv.</i> (<i>редко</i>) <i>VP, PM</i>\n= difficulter."],
  ["difficilis", "<big>difficilis</big>\n\n<b>dif–ficilis, e</b> [facilis]\n<b>1)</b> трудный, нелёгкий (res d. factu <i>C</i>): difficilem habere aurem ad aliquid <i>PS</i> неохотно слушать о чём-л., <i>т. е.</i> критически относиться к рассказам об этом; difficiles obitus <i>V</i> мучительная кончина; d. concoctioni <i>PM</i> неудобоваримый; d. precibus <i>O</i> не уступающий просьбам, непреклонный || тяжёлый, опасный (tempus rei publicae <i>C</i>; iter <i>Sl</i>; ascensus <i>Cs</i>);\n<b>2)</b> обладающий тяжёлым характером, капризный (senex d. ac morosus <i>C</i>); суровый, строгий (parens d. in liberos <i>Acc ap. C</i>)."],
  ["difficiliter", "<big>difficiliter</big>\n\n<b>difficiliter</b> <i>C</i>\n= difficulter."],
  ["difficul", "<big>difficul</big>\n\n<b>difficul</b> <i>арх. Vr</i>\n= difficulter."],
  ["difficultas", "<big>difficultas</big>\n\n<b>difficultās, ātis</b> (<i>gen. pl.</i> <i>иногда</i> ium) <i>f</i> [difficilis]\n<b>1)</b> трудность, затруднение, неудобство (loci <i>Sl</i>; navigandi <i>Cs</i>): difficultatem habere <i>C</i> представлять трудности;\n<b>2)</b> затруднительное положение, запутанные обстоятельства (agnoscere difficultatem alicujus <i>Ap</i>);\n<b>3)</b> недостаток, нехватка, нужда (d. navium <i>C</i>; rei frumentariae <i>Cs</i>): d. nummaria <i>или</i> rei nummariae <i>C</i> денежные затруднения; d. domestica <i>C</i> стеснённые обстоятельства;\n<b>4)</b> капризность, придирчивость, своенравие, тяжёлый характер <i>C</i>."],
  ["difficulter", "<big>difficulter</big>\n\n<b>difficulter</b>\nс трудом, нелегко, трудно <i>Cs, Sl, L etc.</i>: aegre ac d. <i>Ap</i> с большим трудом."],
  ["diffidens", "<big>diffidens</big>\n\n<b>diffīdēns, entis</b>\n<b>1.</b> <i>part. praes. к</i> diffido;\n<b>2.</b> <i>adj.</i> недоверчивый <i>Pl, Sl, Su etc.</i>"],
  ["diffidenter", "<big>diffidenter</big>\n\n<b>diffīdenter</b> [diffido]\nнедоверчиво, <i>тж.</i> неуверенно, боязливо <i>C, L etc.</i>"],
  ["diffidentia", "<big>diffidentia</big>\n\n<b>diffīdentia, ae</b> <i>f</i> [diffido]\nнедоверчивость, недоверие <i>C etc.</i>; неуверенность <i>Sl, PJ, Su etc.</i>"],
  ["diffidi", "<big>diffidi</big>\n\n<b>diffidī</b> <i>pf. к</i>\ndiffindo."],
  ["diffido", "<big>diffido</big>\n\n<b>dif–fīdo, fīsus sum, ere</b> <i>semidepon.</i>\nне доверять, не верить (huic sententiae <i>C</i>); не надеяться, не рассчитывать (alicui rei <i>C etc.</i> <i>или</i> aliqua re <i>Su</i>; viribus suis <i>L</i>); сомневаться, отчаиваться (de belli eventu <i>Eutr</i>): valde d. coeperunt <i>C</i> они сильно пали духом."],
  ["diffindo", "<big>diffindo</big>\n\n<b>dif–findo, fidī, fissum, ere</b>\nраскалывать (saxum <i>C</i>); расщеплять (ramum <i>Vr</i>); разверзать (altam terram <i>Lcr</i>): d. portas muneribus <i>H</i> открыть ворота посредством подкупа; tenacitatem alicujus d. <i>Ap</i> сломить чьё-л. упорство; nihil hinc d. possum <i>H</i> ничего не могу убавить (от сказанного тобой), <i>т. е.</i> во всём должен с тобой согласиться; diem somno d. <i>Vr</i> поспать днём; d. diem <i>L, AG</i> (прервав заседание) отложить решение дела."],
  ["diffingo", "<big>diffingo</big>\n\n<b>dif–fingo, fīnxī, fictum, ere</b>\nпреобразовывать, переделывать (quod semel hora vexit <i>H</i>); перековывать (ferrum incude <i>H</i>)."],
  ["diffinio", "<big>diffinio</big>\n\n<b>diffīnio, —, —, īre</b> <i>v. l.</i>\n= definio."],
  ["diffinitio", "<big>diffinitio</big>\n\n<b>diffīnītio, ōnis</b> <i>f</i> <i>Symm</i>\n= definitio."],
  ["diffissio", "<big>diffissio</big>\n\n<b>diffissio, ōnis</b> <i>f</i> [diffindo]\nрасщепление, <i>перен.</i> отсрочка (diffissiones dierum <i>AG</i>)."],
  ["diffissus", "<big>diffissus</big>\n\n<b>diffissus, a, um</b> <i>part. pf. к</i>\ndiffindo."],
  ["diffisus", "<big>diffisus</big>\n\n<b>diffīsus, a, um</b> <i>part. pf. к</i>\ndiffido."],
  ["diffiteor", "<big>diffiteor</big>\n\n<b>dif–fiteor, —, ērī</b> <i>depon.</i> [fateor]\nне признаваться, запираться, отрицать (d. se aliquid fecisse <i>C etc.</i>; aliquid <i>O</i>): neque enim diffitebor <i>Ap</i> я и не собираюсь отрицать это."],
  ["difflatus", "<big>difflatus</big>\n\n<b>difflātus, (ūs)</b> <i>m</i> [difflo]\nпротивное (встречное) веяние, неблагоприятное направление ветра (ventorum difflatu jactari <i>Amm</i>)."],
  ["diffletus", "<big>diffletus</big>\n\n<b>dif–flētus, a, um</b> [fleo]\nзалитый слезами, заплаканный (oculi <i>Ap</i>)."],
  ["difflo", "<big>difflo</big>\n\n<b>dif–flo, āvī, ātum, āre</b>\nразвеять, рассеять, разогнать (aliquid spiritu <i>Pl</i>; difflari vento <i>LM</i>; d. nubila <i>Aus</i>)."],
  ["diffluentia", "<big>diffluentia</big>\n\n<b>diffluentia, ae</b> <i>f</i> [diffluo]\nрастекание, <i>перен.</i> излишество (libidinis <i>Aug</i>)."],
  ["diffluo", "<big>diffluo</big>\n\n<b>dif–fluo, flūxī, flūxum, ere</b>\n<b>1)</b> расплываться, разливаться (humor diffluit <i>Lcr</i>); разделяться в своём течении, растекаться (Rhenus diffluit — <i>v. l.</i> defluit — in plures partes <i>Cs</i>);\n<b>2)</b> истекать, обливаться (d. sudore <i>Ph, PM</i>);\n<b>3)</b> тонуть, утопать (luxuria et lascivia <i>Ter</i>; delicus, otio <i>C</i>): risu d. <i>Ap</i> давиться от смеха;\n<b>4)</b> рассыпаться, разлетаться, рассеиваться (diffluunt juga montium <i>Sen</i>); погибать (natura animantium diffluit <i>Lcr</i>)."],
  ["diffluus", "<big>diffluus</big>\n\n<b>diffluus, a, um</b> [diffluo]\nрастекающийся, обильно текущий (lacte <i>Macr</i>)."],
  ["diffluvio", "<big>diffluvio</big>\n\n<b>dif–fluvio, —, —, āre</b> [fluvius]\nрасщеплять (vitem <i>Col</i>)."],
  ["diffluxio", "<big>diffluxio</big>\n\n<b>difflūxio, ōnis</b> <i>f</i> [diffluo]\nистечение <i>CA</i>."],
  ["diffringo", "<big>diffringo</big>\n\n<b>dif–fringo, frēgī, frāctum, ere</b> [frango]\nразламывать, разбивать, переламывать (crura alicui <i>Pl</i>; gubernaculum diffractum <i>Su</i>)."],
  ["diffudi", "<big>diffudi</big>\n\n<b>diffūdī</b> <i>pf. к</i>\ndiffundo."],
  ["diffugio", "<big>diffugio</big>\n\n<b>dif–fugio, fūgī, —, ere</b>\n<b>1)</b> разбегаться, рассеиваться (diffugiunt metu perterriti <i>C</i>): diffugerunt nives <i>H</i> снега стаяли;\n<b>2)</b> рассеиваться, улетучиваться (spiritus unguenti suavis diffugit in auras <i>Lcr</i>)."],
  ["diffugium", "<big>diffugium</big>\n\n<b>diffugium, ī</b> <i>n</i> [diffugio]\nбегство (в разные стороны) (proximorum diffugia <i>T</i>)."],
  ["diffugo", "<big>diffugo</big>\n\n<b>dif–fugo, —, —, āre</b>\nобращать в бегство, рассеивать <i>Aug</i>."],
  ["diffulguro", "<big>diffulguro</big>\n\n<b>dif–fulguro, —, —, āre</b>\nосыпать молниями (aliquid <i>Sid</i>)."],
  ["diffulmino", "<big>diffulmino</big>\n\n<b>dif–fulmino, —, —, āre</b>\nрассеивать словно молния (omnem obstantum turbam <i>Sil</i>)."],
  ["diffundito", "<big>diffundito</big>\n\n<b>diffundito, —, —, āre</b> [<i>intens. к</i> diffundo]\nразбрасывать, расточать (amoris vi <i>Pl</i>); рассеивать, рассыпать (scintillas <i>Amm</i>)."],
  ["diffundo", "<big>diffundo</big>\n\n<b>dif–fundo, fūdī, fūsum, ere</b>\n<b>1)</b> разливать (vina <i>H, Col etc.</i>): aqua se diffundit <i>C</i> вода растекается; d. animam cruore <i>V</i> умереть от кровотечения (потери крови); diffundi ab aliquo <i>V</i> происходить от кого-л.; mare in tres sinus diffunditur <i>Mela</i> море образует три залива;\n<b>2)</b> распространять (ambrosiae odorem <i>V</i>; vim suam <i>C</i>): error longe lateque diffusus <i>C</i> широко распространённое заблуждение; late diffundi <i>Vr, Cs</i> широко ветвиться (<i>о ветвях, корнях и рогах</i>);\n<b>3)</b> разглашать (d. aliquid in ora virorum <i>V</i>);\n<b>4)</b> рассыпать, рассеивать (equitem latis campis <i>V</i>): diffusis capillis <i>O</i> с распущенными волосами; caelo d. signa <i>H</i> усеять небо звёздами; d. dolorem flendo <i>O</i> рассеять (облегчить) горе слезами; ex lite in risum diffusi <i>Pt</i> когда наш спор разрешился смехом;\n<b>5)</b> развлекать, веселить (animos munere Bacchi <i>O</i>; Juppiter diffusus nectare <i>O</i>): vultus diffusus <i>O</i> весёлое лицо."],
  ["diffuse", "<big>diffuse</big>\n\n<b>diffūsē</b> [diffusus]\n<b>1)</b> пространно, обстоятельно (dicere <i>C</i>);\n<b>2)</b> бессвязно, путано (disperse et d. <i>C</i>)."],
  ["diffusilis", "<big>diffusilis</big>\n\n<b>diffūsilis, e</b> [diffundo]\nлегко распространяющийся, растекающийся, текучий (aether <i>Lcr</i>)."],
  ["diffusio", "<big>diffusio</big>\n\n<b>diffūsio, ōnis</b> <i>f</i> [diffundo]\n<b>1)</b> распространение, растекание, расширение <i>Boët</i>;\n<b>2)</b> развлечение, веселье (animi <i>Sen</i>)."],
  ["diffusus", "<big>diffusus</big>\n\n<b>diffūsus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> diffundo;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> далеко простирающийся, обширный (planities <i>PJ</i>);\n<b>2)</b> раскидистый, развесистый (platanus diffusa ramis <i>C</i>);\n<b>3)</b> толстый, жирный (sus <i>PM</i>);\n<b>4)</b> многоречивый, пространный, растянутый (opus <i>PJ</i>);\n<b>5)</b> многообразный, разносторонний (meritum <i>PJ</i>);\n<b>6)</b> протяжный (vocis genus <i>C</i>)."],
  ["diffututus", "<big>diffututus</big>\n\n<b>dif–futūtus, a, um</b> [futuo]\nvenere confectus <i>Ctl</i>."],
  ["digamia", "<big>digamia</big>\n\n<b>digamia, ae</b> <i>f</i> (<i>греч.</i>)\nвторичный брак <i>Eccl</i>."],
  ["digamma", "<big>digamma</big>\n\n<b>digamma</b> <i>n indecl.</i> <i>или</i> <b>digamma, ae</b> <i>f</i> (<i>греч.</i>)\n<b>1)</b> дигамма, <i>т. е.</i> двойная гамма, <i>древнегреческая (эолийская) буква</i> Ϝ <i>соответствовавшая латинской</i> v <i>Q</i>;\n<b>2)</b> <i>шутл.</i> книга ссуд под проценты (<i>от сходной с дигаммой латинской буквы</i> F = fenus <i>или</i> fundorum reditus) <i>C</i>."],
  ["digammon", "<big>digammon</big>\n\n<b>digammon, ī</b> <i>n</i> <i>и</i> <b>digammos, ī</b> <i>f</i> <i>Q etc.</i>\n= digamma."],
  ["digamus", "<big>digamus</big>\n\n<b>digamus, a</b> (<i>греч.</i>)\nвторично вступивший в брак <i>Eccl</i>."],
  ["Digentia", "<big>Digentia</big>\n\n<b>Dīgentia, ae</b> <i>f</i>\nДигенция, <i>ручей в Латии, в Сабинском поместье Горация, приток Аниена</i> (<i>ныне</i> Licenza) <i>H</i>."],
  ["digeries", "<big>digeries</big>\n\n<b>dīgeriēs, ēī</b> <i>f</i> [digero]\n<b>1)</b> расположение, упорядочение <i>Macr</i>;\n<b>2)</b> пищеварение <i>CTh</i>."],
  ["digero", "<big>digero</big>\n\n<b>dī–gero, gessī, gestum, ere</b> [dis + gero]\n<b>1)</b> разделять (Nilus digestus in septem cornua <i>O</i>);\n<b>2)</b> разносить: nubes modo congregantur, modo digeruntur <i>Sen</i> облака то собираются, то расходятся;\n<b>3)</b> переваривать (cibum <i>PM, Sen, CC etc.</i>);\n<b>4)</b> распределять, располагать, упорядочивать (d. omne jus civile in genera <i>C</i>; d. poenam in omnes <i>O</i>; ad solis cursum anni tempora digessisse <i>Macr</i>);\n<b>5)</b> рассаживать (digesta per agros arbor <i>V</i>); рассеивать (digesta seges <i>V</i>);\n<b>6)</b> приводить в порядок (capillos <i>O, M</i>; bibliothecam <i>Su</i>); выполнять по порядку (mandata <i>C</i>); записывать, вносить (nomina in codicem <i>C</i>);\n<b>7)</b> считать (annos alicujus <i>O</i>);\n<b>8)</b> истолковывать по порядку (omina <i>V</i>);\n<b>9)</b> ослаблять (aegrum aliqua re <i>CC</i>; sudore digeri <i>CC</i>);\n<b>10)</b> переносить, передвигать (ut minus digeratur aeger <i>CC</i>);\n<b>11)</b> выводить из организма (materiam <i>CC</i>);\n<b>12)</b> подробно обсуждать, улаживать (rem per secreta colloquia <i>Amm</i>);\n<b>13)</b> обстоятельно описывать, излагать (bella charactere historico digesta <i>Vop</i>)."],
  ["digesta", "<big>digesta</big>\n\n<b>dīgesta, ōrum</b> <i>n</i> [digero]\n<b>1)</b> дигесты, <i>юридические сборники</i> (in libro digestorum tricesimo et quarto <i>AG</i>); = pandectae (<i>см.</i>);\n<b>2)</b> Библия <i>Tert</i>: digestum Lucae <i>Tert</i> евангелие от Луки."],
  ["digestibilis", "<big>digestibilis</big>\n\n<b>dīgestibilis, e</b> [digero]\n<b>1)</b> удобоваримый (cibus <i>CA</i>);\n<b>2)</b> способствующий пищеварению, пищеварительный (sucus <i>CA</i>)."],
  ["digestilis", "<big>digestilis</big>\n\n<b>dīgestilis, e</b>\nпищеварительный (meatus <i>Eccl</i>)."],
  ["digestim", "<big>digestim</big>\n\n<b>dīgestim</b> <i>adv.</i> [digero]\nв должном порядке (scribere <i>Eccl</i>)."],
  ["digestio", "<big>digestio</big>\n\n<b>dīgestio, ōnis</b> <i>f</i> [digero]\n<b>1)</b> распределение (по организму) (sive concoctio, sive tantum d. <i>CC</i>);\n<b>2)</b> переваривание, усвоение (ciborum <i>Q, Capit</i>);\n<b>3)</b> <i>ритор.</i> разложение, расчленение по пунктам <i>C, Q</i>;\n<b>4)</b> исчисление (annorum <i>VP</i>);\n<b>5)</b> методическое описание (d. in litteras, <i>sc.</i> Italiae <i>PM</i>)."],
  ["digestum", "<big>digestum</big>\n\n<b>dīgestum, ī</b> <i>n</i> — <i>см.</i>\ndigesta 2."],
  ["digestus", "<big>digestus</big>\n\n<b>dīgestus, ūs</b> <i>m</i> [digero]\nраспределение, заведование: d. sanctarum opum <i>St</i> управление императорской казной."],
  ["digitabulum", "<big>digitabulum</big>\n\n<b>digitābulum, ī</b> <i>n</i> [digitus]\nперчатка (<i>надевавшаяся при собирании маслин</i>) <i>Vr</i>."],
  ["digitalis", "<big>digitalis</big>\n\n<b>digitālis, e</b> [digitus]\n<b>1)</b> толщиной (величиной) в палец (crassitudo <i>PM</i>);\n<b>2)</b> пальцевой (gracilitas <i>PM</i>)."],
  ["digitatus", "<big>digitatus</big>\n\n<b>digitātus, a, um</b> [digitus]\nимеющий пальцы, с пальцами (avis <i>PM</i>)."],
  ["digitellum", "<big>digitellum</big>\n\n<b>digitellum, ī</b> <i>n</i> <i>v. l.</i>\n= digitillum."],
  ["Digiti Idaei", "<big>Digiti Idaei</big>\n\n<b>Digitī Idaeī</b> <i>C</i>\n= Dactyli Idaei."],
  ["digitillum", "<big>digitillum</big>\n\n<b>digitillum, ī</b> <i>n</i> [<i>demin. к</i> digitus] <i>бот.</i>\nнаперстянка <i>Col, PM</i>."],
  ["digitulus", "<big>digitulus</big>\n\n<b>digitulus, ī</b> <i>m</i> [<i>demin. к</i> digitus]\n<b>1)</b> пальчик <i>Pl, Ter, C etc.</i>;\n<b>2)</b> коготок <i>Ap</i>."],
  ["digitus", "<big>digitus</big>\n\n<b>digitus, ī</b> <i>m</i>\n<b>1)</b> палец (<i>руки или ноги</i>): d. pollex <i>Cs</i> большой палец; d. index <i>H</i> (salutaris <i>Su</i>, gustator <i>Hier</i>) указательный палец; d. medius <i>Q</i> (medianus <i>Veg</i>, infamis <i>Pers</i>, impudicus <i>M, Is</i>) средний палец; d. anularis <i>Is</i> (medicus <i>Is</i>, medicinalis <i>и</i> minimo vicinus <i>и</i> proximus <i>Macr</i>) безымянный палец; d. minimus <i>AG</i> (auricularis <i>Is</i>, brevissimus <i>Macr</i>) мизинец; digito se caelum attingere putare <i>погов. C</i> быть на седьмом небе, блаженствовать; digitum tollere <i>C</i> поднимать палец (<i>на торгах, при голосовании</i>); computare digitis <i>Pl, PM</i> (numerare per digitos <i>O</i>) считать по пальцам; novi digitos tuos <i>C</i> знаю, что ты мастер считать; ne digitum quidem porrigere <i>погов. C</i> не пошевелить пальцем (ничего не сделать); proferre digitum <i>C</i> пошевеливаться, приступать к делу; pugnare (concurrere <i>M</i>) ad digitum <i>Q</i> бороться до тех пор, пока одна из сторон не поднимет пальца (<i>т. е.</i> не признает себя побеждённой); digito orbem temperare <i>Su</i> пальцами ударять в бубен <i>или</i> управлять миром (<i>игра слов</i>); insistere (summis) digitis <i>O, CC, Sen</i> становиться на цыпочки;\n<b>2)</b> клешня (cancri <i>Vr</i>);\n<b>3)</b> (<i>у птиц</i>) коготь <i>Vr etc.</i>;\n<b>4)</b> сучок <i>PM</i>;\n<b>5)</b> палец, перст, <i>мера длины = 18,5 мм</i> (<i>римский дюйм</i>): d. transversus <i>или</i> d. <i>Cato</i> ширина пальца; d. transversum non discedere ab aliqua re <i>C</i> нисколько не уступить в чём-л."],
  ["digladiabilis", "<big>digladiabilis</big>\n\n<b>dīgladiābilis, e</b> [digladior]\nборющийся, ожесточённый (discidium <i>Eccl</i>)."],
  ["digladior", "<big>digladior</big>\n\n<b>dī–gladior, ātus sum, ārī</b> <i>depon.</i> [dis + gladius]\n<b>1)</b> сражаться, драться (inter se, cum aliquo <i>C</i>);\n<b>2)</b> горячо спорить (de aliqua re <i>C</i>)."],
  ["digma", "<big>digma</big>\n\n<b>[dīgma, atis</b> <i>n</i> (<i>греч.</i>)\n<b>1)</b> проба, образчик <i>CTh</i>;\n<b>2)</b> (<i>лат.</i> signum) отличительный знак (на солдатском щите) <i>Veg</i>."],
  ["dignanter", "<big>dignanter</big>\n\n<b>dīgnanter</b> [dignor]\nлюбезно, учтиво, милостиво <i>Vop, Symm, Eccl</i>."],
  ["dignatio", "<big>dignatio</big>\n\n<b>dīgnātio, ōnis</b> <i>f</i> [dignor]\n<b>1)</b> почтение, уважение, признание, высокая оценка (alicujus <i>VP, Su, Just</i>);\n<b>2)</b> высокое положение, достоинство, почёт <i>C, Sen, L, T</i>."],
  ["digne", "<big>digne</big>\n\n<b>dīgnē</b> [dignus]\nдостойно, с достоинством (ornatus <i>Pl</i>); по достоинству (loqui de aliquo <i>C</i>); d. ac mereor <i>Cassius ap. C</i> так, как я заслуживаю."],
  ["dignitas", "<big>dignitas</big>\n\n<b>dīgnitās, ātis</b> <i>f</i> [dignus]\n<b>1)</b> достоинство: agere cum dignitate <i>C</i> поступать с достоинством; non esse dignitatis alicujus <i>C</i> не соответствовать чьему-л. достоинству; d. consularis <i>C</i> данные для занятия консульского поста; otium cum dignitate <i>C</i> достойный (<i>т. е.</i> честно заслуженный) отдых;\n<b>2)</b> положение, звание (d. imperatoria <i>Just</i>; equestris <i>Nep</i>): secundum locum dignitatis obtinere <i>C</i> занять второе место по положению; aliquem ad summam dignitatem perducere <i>Cs</i> доставить кому-л. высшие почести; dignitatem non habere <i>C</i> не быть совместимым с достоинством;\n<b>3)</b> статный вид, мужественная осанка, представительность, благородная наружность (corporis <i>Nep, Vtr</i>; formae <i>T, Su</i>);\n<b>4)</b> великолепие (domus <i>C</i>; urbis <i>Nep</i>);\n<b>5)</b> благородство, внушительность (orationis <i>T</i>)."],
  ["dignitosus", "<big>dignitosus</big>\n\n<b>dīgnitōsus (dīgnitossus), a, um</b> [dignitas]\nзначительный, видный, достойный (homo <i>Pt</i>)."],
  ["digno", "<big>digno</big>\n\n<b>dīgno, āvī, ātum, āre</b>\nчтить (deos <i>Pac</i>); удостаивать (aliquem aliqua re <i>C</i>); <i>преим.</i> <i>pass.</i> dignari удостаиваться, быть достойным (aliqua re <i>Lcr, C etc.</i>). — <i>См. тж.</i> dignor."],
  ["dignor", "<big>dignor</big>\n\n<b>dīgnor, ātus sum, ārī</b> <i>depon.</i> [dignus]\n<b>1)</b> считать достойным, удостоить (aliquem aliqua re <i>V, T, Su</i>): d. aliquem virum alicujus <i>O</i> считать кого-л. достойным быть чьим-л. мужем;\n<b>2)</b> считать приличным (d. illud facere <i>Sen etc.</i>);\n<b>3)</b> благоволить, соизволить: alicui viro d. se jungere <i>V</i> соизволить выйти за кого-л. замуж."],
  ["dignoscentia", "<big>dignoscentia</big>\n\n<b>dīgnōscentia, ae</b> <i>f</i> [dignosco]\nразличение (appetendorum et vitandorum <i>Aug</i>)."],
  ["dignosco", "<big>dignosco</big>\n\n<b>dī–gnōsco (dīnōsco), (g)nōvī, (g)nōtum, ere</b> [dis + nosco]\n<b>1)</b> распознавать, узнавать (homines sonis <i>Q</i>);\n<b>2)</b> различать (aliquid sapore <i>Col</i>); отличать (aliquid aliqua re <i>H etc.</i> <i>или</i> ab aliqua re <i>PM etc.</i>; d. civem hoste <i>H</i>; d. dominum ac servum <i>T</i>)."],
  ["dignus", "<big>dignus</big>\n\n<b>dīgnus, a, um</b>\n<b>1)</b> достойный, заслуживающий (aliqua re <i>C</i> <i>etc, редко — и как</i> <i>v. l.</i> — alicujus rei <i>Pl</i> <i>и</i> alicui rei <i>Vop</i>): d. laude <i>C, H</i> достойный хвалы, похвальный; d. adorari <i>Sen</i> заслуживающий восхищения; d. memoria <i>C etc.</i>, memoratu <i>VM, AG, Macr</i> <i>и</i> memoratui <i>Vop</i> достопамятный; d. qui imperet <i>C</i> достойный повелевать; digna causa <i>L</i> достаточное основание; d. alter eligi, alter eligere <i>PJ</i> один достойный быть избранным, другой — осуществить избрание; digna (atque) indigna pati <i>V, Sen</i> терпеть и по заслугам, и безвинно;\n<b>2)</b> приличествующий, подобающий: dignum est подобает, следует (aliquid facere <i>C etc.</i>); aliquid minus aegre, quam dignum erat, tulisse <i>L</i> перенести что-л. более спокойно, чем подобало бы."],
  ["digredior", "<big>digredior</big>\n\n<b>dī–gredior, gressus sum, gredī</b> <i>depon.</i> [dis + gradior]\n<b>1)</b> расходиться, покидать (digressa a marito <i>Su</i>; nunquam est a me digressus <i>C</i>); отходить, уходить, удаляться (e loco <i>Cs</i>; in sua castra <i>Sl</i>; domo <i>Sl</i> <i>и</i> domum <i>T</i>; a mari <i>bAfr</i>; in urbem <i>T</i>);\n<b>2)</b> отклоняться, уклоняться (officio <i>Ter</i>; a <i>и</i> de causa <i>C</i>; ab eo, quod propositum est <i>C</i>)."],
  ["digressio", "<big>digressio</big>\n\n<b>dīgressio, ōnis</b> <i>f</i> [digredior]\n<b>1)</b> уход, отъезд, разлука (congressio, tum vero d. nostra <i>C</i>);\n<b>2)</b> уклонение, отступление (a proposita oratione <i>C</i>)."],
  ["digressus", "<big>digressus</big>\n\n<b>dīgressus, ūs</b> <i>m</i> <i>C, Q</i>\n= digressio."],
  ["dii", "<big>dii</big>\n\n<b>I diī</b> <i>nom. pl. к</i>\ndeus.\n\n<b>II diī</b> <i>арх. V gen. sg. к</i>\ndies."],
  ["diiambus", "<big>diiambus</big>\n\n<b>diiambus, ī</b> <i>m</i> (<i>греч.</i>)\nдвойной ямб (<i>стопа</i> U–U–)."],
  ["diibus", "<big>diibus</big>\n\n<b>diibus</b> <i>Pt v. l. dat./abl. pl. к</i>\ndeus."],
  ["Dijovis", "<big>Dijovis</big>\n\n<b>Dijovis, is</b> <i>m</i>\n<i>древнеиталийское наименование Юпитера</i> <i>Vr, AG</i>."],
  ["dijudicatio", "<big>dijudicatio</big>\n\n<b>dījūdicātio, ōnis</b> <i>f</i> [dijudico]\nокончательное решение, определение <i>C</i>."],
  ["dijudicatrix", "<big>dijudicatrix</big>\n\n<b>dījūdicātrīx, īcis</b> <i>f</i>\nвыносящая решение (omnium rerum <i>Ap</i>)."],
  ["dijudico", "<big>dijudico</big>\n\n<b>dī–jūdico, āvī, ātum, āre</b>\n<b>1)</b> рассудить (inter has sententias <i>C</i>); решить (d. controversiam <i>C</i>); dijudicari non potuit <i>Cs</i> невозможно было решить; aliquem omnium optimum esse d. <i>Pl</i> считать кого-л. лучшим из всех;\n<b>2)</b> различать (recta ac prava <i>C</i>; vera a falsis <i>и</i> vera et falsa <i>C</i>)."],
  ["dijugatio", "<big>dijugatio</big>\n\n<b>dījugātio, ōnis</b> <i>f</i> [dijugo]\nразделение <i>Eccl</i>."],
  ["dijugo", "<big>dijugo</big>\n\n<b>dī–jugo, —, ātum, āre</b>\nразделять (aliquem ab aliquo <i>Eccl</i>)."],
  ["dijunct-", "<big>dijunct-</big>\n\n<b>dījūnct-</b> <i>v. l.</i>\n= disjunct-."],
  ["dijungo", "<big>dijungo</big>\n\n<b>dījungo</b> <i>v. l.</i>\n= disjungo."],
  ["dilabidus", "<big>dilabidus</big>\n\n<b>dīlābidus, a, um</b> [dilabor]\nлегко распадающийся, быстро изнашивающийся (vestes <i>PM</i>)."],
  ["dilabor", "<big>dilabor</big>\n\n<b>dī–lābor, lāpsus sum, lābī</b> <i>depon.</i>\n<b>1)</b> распадаться, разрушаться (aedes vetustate dilapsa <i>L</i>; monumenta dilapsa <i>Lcr</i>); истлевать (vestis dilabitur <i>Col</i>); рассыпаться (in cineres <i>H</i>); разливаться, растекаться (dilapsa cadavera tabo <i>V</i>);\n<b>2)</b> протекать, течь: Araxes amnis presso in solum alveo dilabitur <i>QC</i> река Аракс течёт в глубоком русле;\n<b>3)</b> пропадать, погибать (res familiaris dilabitur <i>C</i>): male parta male dilabuntur <i>погов. Poeta ap. C</i> дурно приобретённое, дурно и гибнет (<i>или</i> «пришло махом, ушло прахом»);\n<b>4)</b> схлынуть, отступить: dilabens aestus <i>T</i> отлив;\n<b>5)</b> таять (dilapsa glacies <i>C</i>; pruina dilabitur <i>Pt</i>);\n<b>6)</b> расходиться, разбегаться (in sua tecta <i>L</i>; ad resumenda arma <i>Su</i>; exercitus dilabitur <i>Sl</i>; in oppida <i>L</i>);\n<b>7)</b> ускользать (impune d. <i>rhH</i>);\n<b>8)</b> рассеиваться, исчезать (nebula dilabitur <i>L</i>): aliquid dilabitur memoria <i>C</i> что-л. исчезает из памяти (забывается); dilapso tempore <i>Sl</i> по прошествии (некоторого) времени."],
  ["dilaceratio", "<big>dilaceratio</big>\n\n<b>dīlacerātio, ōnis</b> <i>f</i> [dilacero]\nразрывание, растерзывание <i>CTh, Vlg, Eccl</i>."],
  ["dilacero", "<big>dilacero</big>\n\n<b>dī–lacero, āvī, ātum, āre</b>\n<b>1)</b> разрывать, терзать, растерзывать (aliquem <i>Ctl, O</i>; corpus <i>T</i>);\n<b>2)</b> разрушать, расшатывать (rem publicam <i>Sl</i>);\n<b>3)</b> растрачивать, расточать (dilacerantur opes <i>O</i>)."],
  ["dilamino", "<big>dilamino</big>\n\n<b>dī–lāmino, —, —, āre</b> [lamina]\nраскалывать (nuces ictu <i>O</i>)."],
  ["dilancino", "<big>dilancino</big>\n\n<b>dī–lancino, āvī, ātum, āre</b>\nразрывать, растерзывать (membra <i>Amm</i>)."],
  ["dilanio", "<big>dilanio</big>\n\n<b>dī–lanio, āvī, ātum, āre</b>\nтерзать, разрывать (cadaver alicujus <i>C</i>); dilaniata veste <i>T</i> в лохмотьях; dilaniata comas (<i>acc. gr.</i>) <i>O</i> с растрёпанными волосами."],
  ["dilapidatio", "<big>dilapidatio</big>\n\n<b>dīlapidātio, ōnis</b> <i>f</i>\nрастрачивание, расточение (bonorum <i>CTh</i>)."],
  ["dilapido", "<big>dilapido</big>\n\n<b>dī–lapido, āvī, ātum, āre</b>\n<b>1)</b> разбрасывать (словно камни), <i>т. е.</i> растрачивать, расточать, мотать (triginta minas <i>Ter</i>; pecuniam publicam <i>C</i>);\n<b>2)</b> побить (<i>как камнями</i>): grandine dilapidati labores <i>Col</i> плоды трудов, уничтоженные (побитые) градом."],
  ["dilapsio", "<big>dilapsio</big>\n\n<b>dīlāpsio, ōnis</b> <i>f</i> [dilabor]\nразложение, распад (dilapsiones corporum mortuorum <i>Aug</i>)."],
  ["dilargior", "<big>dilargior</big>\n\n<b>dī–largior, ītus sum, īrī</b> <i>depon.</i>\nщедро раздавать, дарить (aliquid alicui <i>C, T, Su</i>); <i>pass.</i> быть раздаваемым <i>Sl, Lact</i>."],
  ["dilatatio", "<big>dilatatio</big>\n\n<b>dīlātātio, ōnis</b> <i>f</i> [dilato]\nрасширение <i>Tert</i>."],
  ["dilatator", "<big>dilatator</big>\n\n<b>dīlātātor, ōris</b> <i>m</i>\nраспространитель (linguae Latinae <i>Eccl</i>)."],
  ["dilatio", "<big>dilatio</big>\n\n<b>dīlātio, ōnis</b> <i>f</i> [differo]\n<b>1)</b> откладывание, затяжка, отсрочка (temporis <i>C, Ph</i>; comitiorum <i>C</i>): haec res nullam dilationem recipit (<i>или</i> patitur) <i>L</i> это не терпит никакого отлагательства;\n<b>2)</b> промежуток (d. spatiosa <i>Ap</i>)."],
  ["dilato", "<big>dilato</big>\n\n<b>dī–lāto, āvī, ātum, āre</b> [latus]\n<b>1)</b> расширять, растягивать (castra, aciem <i>L</i>);\n<b>2)</b> широко раскрывать (manum <i>C</i>);\n<b>3)</b> распространять, ширить, разносить (imperium, gloriam <i>C</i>);\n<b>4)</b> затягивать: d. orationem распространяться, обстоятельно излагать;\n<b>5)</b> протяжно произносить (litteras <i>C</i>);\n<b>6)</b> расширяться (spatia montis in cubiculo dilatantia <i>PM</i>)."],
  ["dilator", "<big>dilator</big>\n\n<b>dīlātor, ōris</b> <i>m</i> [differo]\nоткладывающий, медлитель <i>H, Is, Aug</i>."],
  ["dilatorius", "<big>dilatorius</big>\n\n<b>dīlātōrius, a, um</b> [differo]\nприводящий к отсрочке, затягивающий (exceptio <i>G</i>)."],
  ["dilatus", "<big>dilatus</big>\n\n<b>dīlātus, a, um</b> <i>part. pf. к</i>\ndiffero."],
  ["dilaudo", "<big>dilaudo</big>\n\n<b>dī–laudo, —, —, āre</b>\nвсячески восхвалять, усердно хвалить (aliquid <i>C</i>)."],
  ["dilectio", "<big>dilectio</big>\n\n<b>dīlēctio, ōnis</b> <i>f</i> [diligo]\nлюбовь <i>Eccl</i>."],
  ["dilector", "<big>dilector</big>\n\n<b>dīlēctor, ōris</b> <i>m</i> [diligo]\nлюбящий, поклонник <i>Ap, Tert</i>."],
  ["dilectus", "<big>dilectus</big>\n\n<b>I dīlēctus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> diligo;\n<b>2.</b> <i>adj.</i> дорогой, любезный, любимый (alicui <i>H etc.</i>; luce mihi dilectior <i>Macr</i>).\n\n<b>II dīlēctus, ūs</b> <i>m</i> (= delectus II)\n<b>1)</b> выбор (prudentia in dilectu bonorum et malorum <i>C</i>);\n<b>2)</b> отборный отряд <i>T</i>.\n\n<b>III dīlēctus, ī</b> <i>m</i>\nлюбимец, фаворит <i>Su</i>."],
  ["dilexi", "<big>dilexi</big>\n\n<b>dīlēxī</b> <i>pf. к</i>\ndiligo."],
  ["dilido", "<big>dilido</big>\n\n<b>dī–līdo, —, —, ere</b> [dis + laedo]\nразбивать (caput alicujus <i>Pl</i>)."],
  ["diligens", "<big>diligens</big>\n\n<b>dīligēns, entis</b>\n<b>1.</b> <i>part. praes. к</i> diligo;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> усердный (cura <i>Q</i>); старательный, тщательный, внимательный, основательный, аккуратный (homo d. omnibus rebus <i>C</i>): d. veritatis <i>Nep</i> правдивый; d. verborum <i>AG</i> разборчивый в словах, изысканный в выражениях; d. litterarum <i>AG</i> образованный; d. juris <i>Q</i> тщательно соблюдающий закон; d. ad reportandum <i>C</i> исправно возвращающий (взятое в долг);\n<b>2)</b> расчётливый, бережливый, экономный (homo frugi ac d. <i>C</i>; rei suae parcus ac d. <i>Capit</i>);\n<b>3)</b> старательный, тщательно обработанный (scriptura <i>C</i>; stilus <i>T, Q</i>)."],
  ["diligenter", "<big>diligenter</big>\n\n<b>dīligenter</b> [diligens]\nусердно, тщательно, старательно (iter facere <i>Cs</i>); внимательно (aliquem audire <i>C</i>): Latine et d. loqui <i>C</i> говорить чистым латинским языком; d. errare <i>M ирон.</i> глубоко заблуждаться."],
  ["diligentia", "<big>diligentia</big>\n\n<b>dīligentia, ae</b> <i>f</i> [diligens]\n<b>1)</b> усердие, старание, <i>тж.</i> точность, аккуратность, основательность (cura atque d., assiduitas et d. <i>C etc.</i>): testamentorum d. <i>C</i> тщательность в составлении завещаний;\n<b>2)</b> расчётливость, экономия, бережливость (d. rei familiaris <i>Q</i>);\n<b>3)</b> любовь <i>Symm</i>."],
  ["diligibilis", "<big>diligibilis</big>\n\n<b>dīligibilis, e</b>\nуважаемый, почтенный <i>Hier</i>."],
  ["diligo", "<big>diligo</big>\n\n<b>dī–ligo, lēxī, lēctum, ere</b> [dis + lego I]\nвысоко ценить, уважать (consilia alicujus <i>C</i>); любить, почитать (aliquem d. ac carum habere <i>C</i>; <i>перен.</i> montes et valles diligit robur <i>PM</i>): d. inter se <i>C</i> <i>и</i> d. se mutuo amore <i>MF</i> любить друг друга || быть влюблённым <i>VM, Su, Nep, Pt</i>."],
  ["dilorico", "<big>dilorico</big>\n\n<b>dīlōrīco, (āvī), ātum, āre</b> [dis + lorico]\nразрывать, раздирать (tunicam <i>C</i>); срывать (vestem <i>Ap</i>)."],
  ["diloris", "<big>diloris</big>\n\n<b>dīlōris, e</b> [<i>греч.</i> dis + <i>лат.</i> lorum]\nдвухремённый, <i>перен.</i> (<i>о ткани</i>) в две полоски (vestis <i>Vop</i>)."],
  ["diluceo", "<big>diluceo</big>\n\n<b>dī–lūceo, —, —, ēre</b>\nбыть светлым, ясным, <i>перен.</i> быть очевидным, выясняться (d. fraus coepit <i>L</i>)."],
  ["dilucesco", "<big>dilucesco</big>\n\n<b>dīlūcēsco, lūxī, —, ere</b> [<i>inchoat. к</i> diluceo]\n<b>1)</b> светать, воссиять (dies, qui post eam noctem diluxerit <i>AG</i>): omne crede diem tibi diluxisse supremum <i>H</i> имей в виду, что любой день может оказаться для тебя последним;\n<b>2)</b> становиться ясным, выясняться (abdita dilucescant <i>Aug</i>);\n<b>3)</b> <i>impers.</i> dilucescit <i>C</i> (рас)светает; diluxit, patet <i>C</i> (всё теперь) ясно, очевидно."],
  ["dilucide", "<big>dilucide</big>\n\n<b>dīlūcidē</b> [dilucidus]\n<b>1)</b> светло (flagrare <i>PM</i>);\n<b>2)</b> ясно, понятно (d. planeque dicere <i>C</i>; explicare rem <i>rhH</i>)."],
  ["diluciditas", "<big>diluciditas</big>\n\n<b>dīlūciditās, ātis</b> <i>f</i>\nясность <i>Boet</i>."],
  ["dilucido", "<big>dilucido</big>\n\n<b>dīlūcido, āvī, —, āre</b> [dis + lucidus]\nразъяснить (aliquid <i>Tert</i>)."],
  ["dilucidus", "<big>dilucidus</big>\n\n<b>dīlūcidus, a, um</b>\n<b>1)</b> светлый, яркий (smaragdus <i>PM</i>);\n<b>2)</b> ясный, понятный, очевидный (expositio <i>rhH</i>; oratio, verba <i>C</i>)."],
  ["diluculat", "<big>diluculat</big>\n\n<b>dīlūculat</b> <i>impers.</i> [diluculum]\nсветает <i>AG</i>."],
  ["diluculum", "<big>diluculum</big>\n\n<b>dī–lūculum, ī</b> <i>n</i> [dis + lux]\nпредрассветная пора, рассвет: diluculo <i>C</i> (cum diluculo <i>Pl</i>, primo diluculo <i>C</i>) на рассвете, с рассветом."],
  ["diludium", "<big>diludium</big>\n\n<b>dī–lūdium, ī</b> <i>n</i> [ludus]\nдень отдыха (<i>у гладиаторов</i>); diludia poscere <i>H</i> просить передышки."],
  ["diluo", "<big>diluo</big>\n\n<b>dī–luo, uī, ūtum, ere</b>\n<b>1)</b> размывать, размягчать (vulnus aceto <i>Pt</i>): diluta absinthia <i>Lcr</i> полынный настой || смачивать, размачивать (d. lateres aqua <i>Cs</i>; labellam guttis <i>Ctl</i>; tellurem sanguine <i>AG</i>);\n<b>2)</b> разводить (водой), разбавлять, разжижать (vinum <i>M</i>; favos lacte <i>V</i>);\n<b>3)</b> растворять (aliquid aceto <i>H</i>);\n<b>4)</b> приготовлять (d. medicamentum <i>QC</i>; venenum <i>L</i>);\n<b>5)</b> промывать, споласкивать (dolia <i>Cato</i>; os <i>CC</i>): color diluitur <i>O</i> краска линяет (сходит);\n<b>6)</b> смягчать, ослаблять (invidiam <i>Su</i>);\n<b>7)</b> подрывать (auctoritatem <i>Sen</i>);\n<b>8)</b> разгонять, удалять (curam mero <i>O</i>; omnes molestias <i>C</i>);\n<b>9)</b> разъяснять, объяснять (mihi, quod rogavi, dilue <i>Pl</i>);\n<b>10)</b> заглаживать, искупать (injurias aere <i>AG</i>);\n<b>11)</b> опровергать (infirmare ac d. <i>C</i>; d. sophisma <i>AG</i>)."],
  ["dilute", "<big>dilute</big>\n\n<b>dīlūtē</b> [dilutus]\nв разбавленном виде (dilutius potare <i>C</i>)."],
  ["dilutio", "<big>dilutio</big>\n\n<b>dīlūtio, ōnis</b> <i>f</i> [diluo]\nомовение <i>Hier</i>."],
  ["dilutum", "<big>dilutum</big>\n\n<b>dīlūtum, ī</b> <i>n</i> [dilutus]\nраствор <i>PM</i>."],
  ["dilutus", "<big>dilutus</big>\n\n<b>dīlūtus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> diluo;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> разведённый водой, разбавленный (vinum <i>Cs</i>);\n<b>2)</b> бледный, светлый (urina <i>CC</i>; rubor <i>PM</i>);\n<b>3)</b> слабый (odor <i>PM</i>)."],
  ["diluvialis", "<big>diluvialis</big>\n\n<b>dīluviālis, e</b> [diluvium]\nотносящийся к наводнению: d. irruptio <i>Sol</i> разлив, паводок."],
  ["diluvies", "<big>diluvies</big>\n\n<b>dīluviēs, ēī</b> <i>f</i> [diluo]\nразлитие вод, наводнение, паводок <i>Lcr, H, PM</i>."],
  ["diluvio", "<big>diluvio</big>\n\n<b>I dīluvio, —, —, āre</b> [diluo]\nзатоплять (omnia <i>Lcr</i>).\n\n<b>II dīluvio, ōnis</b> <i>f</i>\nнаводнение, паводок <i>Cens, Tert</i>."],
  ["diluvium", "<big>diluvium</big>\n\n<b>dīluvium, ī</b> <i>n</i> [diluo]\n<b>1)</b> разлив вод, наводнение <i>V, PJ, Fl etc.</i>;\n<b>2)</b> потоп <i>O, Sen, Vr, Eccl</i>;\n<b>3)</b> вторжение, набег (иноземных полчищ) (diluvio tali urgere <i>VF</i>);\n<b>4)</b> гибель, катастрофа (diluvio ex illo litus rogamus innocuum <i>V</i>)."],
  ["dimachae", "<big>dimachae</big>\n\n<b>dimachae, ārum</b> <i>m</i> (<i>греч.</i>)\nконница, сражавшаяся то в конном, то в пешем строю <i>QC</i>."],
  ["dimadesco", "<big>dimadesco</big>\n\n<b>dī–madēsco, maduī, —, ere</b>\nтаять (nives dimaduerunt <i>Lcn</i>)."],
  ["dimano", "<big>dimano</big>\n\n<b>dī–māno, āvī, —, āre</b>\nрастекаться, разливаться, <i>перен.</i> распространяться, шириться <i>C</i>."],
  ["dimensio", "<big>dimensio</big>\n\n<b>dīmēnsio, ōnis</b> <i>f</i> [dimetior]\n<b>1)</b> обмер, измерение (quadrati <i>C</i>);\n<b>2)</b> размер, протяжение (terrae dimensiones <i>Ap</i>);\n<b>3)</b> измерение (omnium corporum tres sunt dimensiones: longitudo, latitudo, profunditas <i>Macr</i>);\n<b>4)</b> размеренность (d. versuum, pedum <i>Q</i>);\n<b>5)</b> хлебная мера, солдатский паёк <i>Veg</i>."],
  ["dimensus", "<big>dimensus</big>\n\n<b>dīmēnsus, a, um</b> <i>part. pf. к</i>\ndimetior."],
  ["dimeter", "<big>dimeter</big>\n\n<b>dimeter (dimetrus), trī</b> <i>m</i> (<i>греч.</i>)\nдиметр, <i>двухтактный стих, состоящий из двух метров или из четырёх стоп.</i>"],
  ["dimetiens", "<big>dimetiens</big>\n\n<b>dīmētiēns, entis</b> <i>f</i> [dimetior]\nпоперечник, диаметр <i>PM</i>."],
  ["dimetior", "<big>dimetior</big>\n\n<b>dī–mētior, mēnsus sum, īrī</b> <i>depon. и pass.</i>\n<b>1)</b> производить обмер, измерять, размерять (tigna dimensa ad altitudinem <i>Cs</i>; profundum abyssi qui dimensus est? <i>Vlg</i>): dimensum tempus <i>PJ</i> точно установленное время; digitis d. aliquid <i>C</i> исчислять что-л. по пальцам;\n<b>2)</b> размечать, намечать (castra <i>Sil</i>);\n<b>3)</b> придавать размеренность, сообщать размер (d. verba ad pedes <i>C, Q</i>)."],
  ["dimeto", "<b>dimeto, avi, atum, are</b> <i>и</i> <b>dimetor, —, ari</b> <i>depon.</i> [meto II]\nизмерять, отмерять <i>C</i>: d. locum castris <i>L</i> ламетить место для лагеря."],
  ["dimetria", "<big>dimetria</big>\n\n<b>dimetria, ae</b> <i>f</i> <i>греч.</i>\nдиметрия, стихотворение в ямбических диметрах <i>Aus</i>."],
  ["dimetron", "<big>dimetron</big>\n\n<b>dimetron, ī</b> <i>n</i>\n= dimeter."],
  ["dimetrus", "<big>dimetrus</big>\n\n<b>dimetrus, trī</b> <i>m</i> <i>v. l.</i>\n= dimeter."],
  ["dimicatio", "<big>dimicatio</big>\n\n<b>dīmicātio, ōnis</b> <i>f</i> [dimico]\n<b>1)</b> бой, сражение, битва: d. universa (d. universae rei) <i>L</i> генеральное сражение, решительный бой;\n<b>2)</b> упорная борьба: non modo contentione, sed etiam dimicatione in aliqua re elaborare <i>C</i> не только напряжённо работать над чём-л., но упорно бороться;\n<b>3)</b> (<i>тж.</i> vitae d. <i>C</i>) отчаянная борьба, крайняя опасность: in tanta dimicatione capitis, famae fortunarumque omnium <i>C</i> в столь опасный для жизни, славы и всего достояния час."],
  ["dimico", "<big>dimico</big>\n\n<b>dī–mico, āvī (<i>редко</i> <i>O</i> uī), ātum, āre</b>\n<b>1)</b> махать (наносить удары) мечом, бороться, сражаться (cum hoste <i>Nep, rhH</i>; adversus aliquem <i>Nep</i>; pro patria <i>C, L</i>; de imperio <i>C</i>; inter se <i>PM</i>): d. in mortem <i>VP</i> вести смертельный бой;\n<b>2)</b> всеми силами домогаться, упорно бороться (d. de gloria <i>и</i> gloriae causa <i>C</i>; dimicantes competitores <i>L</i>); omni ratione erit dimicandum (ut) <i>C</i> придётся упорно добиваться (чтобы);\n<b>3)</b> рисковать, подвергаться опасности лишиться (чего-л.): dimicans de fama <i>C</i> рискующий своим добрым именем; d. de repulsa <i>L</i> подвергаться опасности получить отказ (не быть избранным); d. capite suo <i>L</i> (de vita <i>C</i>) рисковать жизнью."],
  ["dimidia", "<big>dimidia</big>\n\n<b>dīmidia, ae</b> <i>f</i> [dimidius]\n(<i>sc.</i> pars) половина (latitudinis <i>VP</i>): ad dimidias <i>PM</i> до половины, наполовину."],
  ["dimidiatio", "<big>dimidiatio</big>\n\n<b>dīmidiātio, ōnis</b> <i>f</i>\nразделение пополам <i>Tert</i>."],
  ["dimidiatus", "<big>dimidiatus</big>\n\n<b>dīmidiātus, a, um</b> [dimidius]\nразделённый надвое, пополам: d. mensis <i>Cs</i> полмесяца; aliquem in terram dimidiatum defodere <i>Cato ap. AG</i> закопать кого-л. по пояс в землю; dimidiati procumbunt <i>Pl</i> они наполовину навалились на стол (от жадности)."],
  ["dimidietas", "<big>dimidietas</big>\n\n<b>dīmidietās, ātis</b> <i>f</i>\nполовина <i>Boet</i>."],
  ["dimidio", "<big>dimidio</big>\n\n<b>dīmidio, —, —, āre</b> [dimidium]\nделить пополам <i>Eccl</i>. — <i>См. тж.</i> dimidiatus."],
  ["dimidium", "<big>dimidium</big>\n\n<b>dīmidium, ī</b> <i>n</i> [dimidius]\nполовина (pecuniae <i>C</i>; militum <i>L</i>): dimidio carius <i>C</i> наполовину (в полтора раза) дороже; d. facti, qui coepit, habet <i>погов. <i>H</i>, тж.</i> d. facti est coepisse <i>Aus</i> положивший начало сделал половину дела (<i>т. е.</i> «лиха беда начало»)."],
  ["dimidius", "<big>dimidius</big>\n\n<b>dī–midius, a, um</b> [medius]\nполовинный (luna <i>Vtr, O, PM</i>; mensis <i>Col</i>): d. patrum, d. plebis <i>L</i> (по происхождению) полупатриций, полуплебей; d. Priapus <i>M</i> бюст Приапа."],
  ["diminuo", "<big>diminuo</big>\n\n<b>dī–minuo, (nuī), nūtum, ere</b>\nраздробить, размозжить (caput alicui <i>Pl</i> <i>и</i> alicui cerebrum <i>Ter</i>)."],
  ["diminut-", "<big>diminut-</big>\n\n<b>dīminūt-</b> <i>v. l.</i>\n= deminut-."],
  ["dimisi", "<big>dimisi</big>\n\n<b>dīmīsī</b> <i>pf. к</i>\ndimitto."],
  ["dimissio", "<big>dimissio</big>\n\n<b>dīmissio, ōnis</b> <i>f</i> [dimitto]\n<b>1)</b> отпуск, увольнение (propugnatorum atque remigum <i>C</i>);\n<b>2)</b> отправление в разные стороны, рассылка (dimissiones libertorum ad diripiendas provincias <i>C</i>);\n<b>3)</b> выпускание (sanguinis <i>AG</i>);\n<b>4)</b> <i>мед.</i> ослабление (болезни), ремиссия <i>CA</i>;\n<b>5)</b> отпущение, прощение (delictorum <i>Aug</i>)."],
  ["dimissor", "<big>dimissor</big>\n\n<b>dīmissor, ōris</b> <i>m</i> [dimitto]\nотпускающий, прощающий (d. peccatorum <i>Tert</i>)."],
  ["dimissorius", "<big>dimissorius</big>\n\n<b>dīmissōrius, a, um</b>\nпосылаемый в качестве отчёта (высшим инстанциям) (litterae, libelli <i>Dig</i>)."],
  ["dimissus", "<big>dimissus</big>\n\n<b>dīmissus, a, um</b> <i>part. pf. к</i>\ndimitto."],
  ["dimitto", "<big>dimitto</big>\n\n<b>dī–mitto, mīsī, missum, ere</b>\n<b>1)</b> распускать (exercitum <i>Cs</i>; senatum <i>C</i>; ludos <i>L</i>);\n<b>2)</b> рассылать (nuntios in omnes partes <i>Cs</i>; litteras per omnes provincias <i>C</i> <i>и</i> circum municipia <i>Cs</i>): d. aciem (oculorum) in omnes partes <i>O</i> озираться вокруг; d. animum ignotas in artes <i>O</i> предаться размышлению о неведомом (ещё) искусстве;\n<b>3)</b> увольнять (milites in oppidum <i>Cs</i>); отпускать, отсылать (aliquem a se <i>C</i>; d. legatos domum <i>Su</i>): d. uxorem (e matrimonio) <i>Su</i> развестись с женой; d. equos <i>Cs, V, T</i> сойти с коней (<i>преим. для боя в пешем строю</i>); d. creditorem <i>PJ, Dig</i> удовлетворить кредитора; d. debitorem <i>Dig</i> простить должнику долг;\n<b>4)</b> выпускать из рук, ронять, бросать (signa ex metu <i>Cs</i>; librum <i>C</i>; d. praedam speratam e manibus <i>Cs</i>): arbor folia dimittit <i>PM</i> дерево роняет листья; febris dimittit aliquem <i>CC</i> лихорадка проходит у кого-л.; dimissis manibus <i>Pl</i> опрометью;\n<b>5)</b> упускать (d. maximarum rerum opportunitatem <i>QC</i>); утрачивать, терять (fortunas morte <i>C</i>): cavendi nulla est dimittenda occasio <i>PS</i> ни в каком случае не следует пренебрегать предосторожностью; dimissa anima <i>Lcr</i> с утратой души;\n<b>6)</b> оставлять, отказываться, снимать, прекращать (d. quaestionem <i>C</i>; oppugnationem <i>Cs</i>); расторгнуть (matrimonium <i>Su</i>): d. aliquid (oblivione) <i>T, PJ etc.</i> забыть о чём-л.; in discrimen aliquid d. <i>L</i> поставить что-л. на карту (рисковать чём-л.); d. jus suum <i>C</i> отказываться от своего права; d. injuriam impunitam <i>C</i> оставить правонарушение безнаказанным;\n<b>7)</b> бросать, оставлять мысль (о чём-л.) (d. Italiam <i>Cs</i>; provinciam <i>L</i>; triumphum <i>Su</i>);\n<b>8)</b> освобождать (aliquem e custodia <i>L</i>): d. aliquem a suspicione <i>Pt</i> снять с кого-л. подозрение; d. alicui tributa <i>T</i> простить кому-л. (не взыскивать) подати; judicio dimitti <i>Ap</i> быть освобождённым по суду (от претензий истца);\n<b>9)</b> отпускать, прощать (debita, peccata, delicta alicui <i>Eccl</i>);\n<b>10)</b> оставлять после себя (в качестве наследников <i>или</i> наследства) (patrem matremque <i>CTh</i>; usum fructum alicui <i>CTh</i>)."],
  ["dimminuo", "<big>dimminuo</big>\n\n<b>dimminuo</b> <i>арх. v. l.</i>\n= diminuo."],
  ["dimoveo", "<big>dimoveo</big>\n\n<b>dī–moveo, mōvī, mōtum, ēre</b>\n<b>1)</b> раздвигать (plagulas <i>Su</i>; turbam <i>T, Su</i>): d. obstantes propinquus <i>H</i> протиснуться сквозь обступившую толпу; d. ora <i>O</i> раскрыть рот;\n<b>2)</b> разделять, рассекать (undas <i>Lcr</i>, aquam corpore <i>O</i>); разрезать, бороздить (terram aratro <i>V</i>); разгребать (cinerem <i>O</i>);\n<b>3)</b> сдвигать, рассеивать, разгонять (umbram polo <i>V</i>); отодвигать, удалять (aliquid statu suo <i>L</i>): d. vocis moras <i>SenT</i> прервать молчание;\n<b>4)</b> отклонять, отвлекать, удалять: d. aliquem, ut faciat aliquid <i>H</i> отговорить кого-л. от чего-л. (<i>v. l. к</i> demoveo); spes societatis dimovit equites a plebe <i>Sl</i> надежда соединиться (с патрициями) отклонила (оторвала) всадников от плебеев; terror fide socios dimovit <i>L</i> страх лишил союзников верности;\n<b>5)</b> (<i>в целях упражнения</i>) двигать, шевелить (manus <i>CC</i>; ambulatione se d. <i>или</i> dimoveri <i>CC</i>)."],
  ["Dinarchus", "<big>Dinarchus</big>\n\n<b>Dīnarchus, ī</b> <i>m</i>\nДинарх, <i>родом из Коринфа, последний ив «десяти аттических ораторов»</i> (<i>361-290 гг. до н. э.</i>) <i>C</i>."],
  ["Dindyma", "<big>Dindyma</big>\n\n<b>Dindyma, ōrum <i>n</i> <i>и</i> Dindymus (-os), ī</b> <i>m</i>\nДиндимы, <i>гора во Фригии, на границе Галатии, близ города Пессин, посвященная Кибеле</i> <i>Ctl, O, H, V etc.</i>"],
  ["Dindymene", "<big>Dindymene</big>\n\n<b>Dindymēnē, ēs <i>и</i> Dindymēna, ae</b> <i>f</i> [Dindyma]\n<i>эпитет Кибелы</i> <i>Ctl, H</i>."],
  ["Dinomache", "<big>Dinomache</big>\n\n<b>Dīnomachē, ēs</b> <i>f</i>\nДиномаха, <i>мать Алкивиада</i> <i>Pers</i>."],
  ["Dinomachus", "<big>Dinomachus</big>\n\n<b>Dīnomachus, ī</b> <i>m</i>\nДиномах, <i>философ, усматривавший высшее благо в сочетании добродетели с наслаждением</i> <i>C</i>."],
  ["Dinon", "<big>Dinon</big>\n\n<b>Dīnō(n), ōnis</b> <i>m</i>\nДинон, <i>историк Персии, современник Филиппа Македонского</i> <i>C, PM</i>."],
  ["dinoscentia", "<big>dinoscentia</big>\n\n<b>dīnōscentia, ae</b> <i>f</i> <i>v. l.</i>\n= dignoscentia."],
  ["dinosco", "<big>dinosco</big>\n\n<b>dīnōsco</b> <i>v. l.</i>\n= dignosco."],
  ["dinumeratio", "<big>dinumeratio</big>\n\n<b>dīnumerātio, ōnis</b> <i>f</i> [dinumero]\nисчисление, перечисление, счёт (dierum ac noctium <i>C</i>)."],
  ["dinumero", "<big>dinumero</big>\n\n<b>dī–numero, āvī, ātum, āre</b>\n<b>1)</b> исчислять (tempora <i>V</i>; noctes <i>O</i>); пересчитывать, считать (stellas <i>C</i>; minas quinquaginta <i>Pl</i>);\n<b>2)</b> отсчитывать, выплачивать (stipendium <i>Pl</i>)."],
  ["dinummium", "<big>dinummium</big>\n\n<b>dī–nummium, ī</b> <i>n</i> [<i>греч.</i> dis + nummus]\nпошлина в два денария <i>CTh</i>."],
  ["Dio", "<big>Dio</big>\n\n<b>Diō</b> — <i>см.</i>\nDion."],
  ["diobolaris", "<big>diobolaris</big>\n\n<b>diōbolāris, e</b> (<i>греч.</i>)\nдвухоболовый, продающийся за два обола, грошовый (scorta <i>Pl</i>)."],
  ["Diochares", "<big>Diochares</big>\n\n<b>Diocharēs, is</b> <i>m</i>\nДиохар, <i>вольноотпущенник Цезаря</i> <i>C</i>."],
  ["Diocharinus", "<big>Diocharinus</big>\n\n<b>Diocharīnus, a, um</b> [Diochares]\nдиохаров (litterae <i>C</i>)."],
  ["Diocletianus", "<big>Diocletianus</big>\n\n<b>Dioclētiānus, ī</b> <i>m</i>\n(C. Aurelius Valerius) Диоклетиан (Гай Аврелий Валерий), <i>римск. император в 284-305 гг. н. э.</i> <i>AV, Eutr</i>."],
  ["diocmitae", "<big>diocmitae</big>\n\n<b>diōcmītae</b> <i>v. l.</i>\n= diogmitae."],
  ["Diodorus", "<big>Diodorus</big>\n\n<b>Diodōrus, ī</b> <i>m</i>\nДиодор:\n<b>1)</b> D. Cronos, <i>кариец, философ Мегарской школы при дворе Птолемея Сотера</i> <i>C, PM, AG</i>;\n<b>2)</b> <i>родом из Тира, философ перипатетической школы</i> (<i>ок. 110 г. до н. э.</i>) <i>C</i>;\n<b>3)</b> D. Siculus («Сицилийский»), <i>современник Ю. Цезаря и Августа, греч. автор истории с древнейших времён до Галльских походов Цезаря</i> <i>PM, Lad</i>."],
  ["Diodotus", "<big>Diodotus</big>\n\n<b>Diodotus, ī</b> <i>m</i>\nДиодот, <i>философ-стоик, учитель Цицерона, у которого он жил и в 59 г. до н. э. умер</i> <i>C</i>."],
  ["dioecesis", "<big>dioecesis</big>\n\n<b>dioecēsis, eos</b> <i>и</i> <b>is</b> (<i>acc.</i> in) <i>f</i> (<i>греч.</i>)\n<b>1)</b> округ, район <i>C</i>;\n<b>2)</b> (<i>в императ. эпоху</i>) область (<i>охватывавшая ряд провинций</i>) <i>Amm</i>;\n<b>3)</b> епархия <i>Sid, Eccl</i>;\n<b>4)</b> церковный приход <i>Sid, Eccl</i>."],
  ["dioecetes", "<big>dioecetes</big>\n\n<b>dioecētēs, ae</b> <i>m</i> (<i>греч.</i>)\nуправляющий царскими финансами, интендант <i>C</i>."],
  ["Diogenes", "<big>Diogenes</big>\n\n<b>Diogenēs, is</b> <i>m</i>\nДиоген:\n<b>1)</b> <i>из Аполлонии (Крит), философ-материалист ионийской школы, считавший воздух основным видом вещества (ок. 440 г. до н. э.</i>) <i>C</i>;\n<b>2)</b> <i>из Синопа, философ-киник</i> (<i>404-323 гг. до н. э.</i>) <i>C, VM</i>;\n<b>3)</b> <i>из Селевкии на Тигре (Вавилон), афинский философ-стоик, ученик Хрисиппа, ездивший с Карнеадом и Критолаем с посольством в Рим (155 г. до н. э.</i>) <i>C</i>."],
  ["diogmitae", "<big>diogmitae</big>\n\n<b>diōgmītae, ārum</b> <i>m</i> (<i>греч.</i>)\nдиогмиты, <i>легковооружённые солдаты, нёсшие пограничную службу</i> <i>Capit, Amm</i>."],
  ["Diomedes", "<big>Diomedes</big>\n\n<b>Diomēdēs, is</b> <i>m</i>\nДиомед:\n<b>1)</b> <i>сын Марса, царь фракийск. племени бистонов, кормивший своих коней человеческим мясом</i> <i>Lcr, Sol</i>;\n<b>2)</b> <i>сын Тидея из Калидона (Этолия), царь Аргоса, один из греч. вождей при осаде Трои, переселившийся в Апулию</i> <i>V, O, Just</i>: Diomedis urbs <i>V</i> = Arpi; Diomedis campus <i>L</i> Диомедовы поля (<i>в Апулии, близ г. Arpi, основанного Диомедом Аргосским</i>)."],
  ["Diomedeus", "<big>Diomedeus</big>\n\n<b>Diomēdēus (-īus), a, um</b> [Diomedes]\nдиомедов: Diomedeae aves (volucres) <i>Aug, Is</i> — <i>цапли, в которых были превращены спутники Диомеда, безутешно скорбевшие о его смерти</i>; Diomedeae insulae <i>Mela, PM</i> — <i>пять островков в Адриатическом море, к северу от мыса Garganum в Апулии</i>; Diomedeum furtum <i>St</i> <i>и</i> Diomedea ausa <i>Cld</i> диомедово похищение (троянского палладия)."],
  ["Dion", "<big>Dion</big>\n\n<b>Diō(n), ōnis</b> <i>m</i>\nДион, <i>знатный сиракузец, шурин Дионисия Старшего, друг Платона; в 357 г. до н. э. освободил родной город от тирании Дионисия Младшего, но сам пал жертвой заговора</i> (<i>409-353 гг. до н. э.</i>) <i>Nep, C</i>."],
  ["Diona", "<big>Diona</big>\n\n<b>Diōna, ae</b> <i>и</i> <b>Diōnē, ēs</b> <i>f</i>\nДиона:\n<b>1)</b> <i>дочь Океана и Фетиды, мать Венеры и  — по официальной римской традиции — родоначальница рода Юлиев</i> <i>C, Cld</i>;\n<b>2)</b> <i>O</i> = Venus."],
  ["Dionaeus", "<big>Dionaeus</big>\n\n<b>Diōnaeus, a, um</b>\n<b>1)</b> <i>adj. к</i> Diona: Dionaea mater <i>V</i> = Venus;\n<b>2)</b> эпитет Цезаря (<i>как «потомка Энея», сына Венеры</i>) <i>V</i>."],
  ["Dione", "<big>Dione</big>\n\n<b>Diōnē, ēs</b> <i>f</i> <i>v. l.</i>\n= Diona."],
  ["Dionyseus", "<big>Dionyseus</big>\n\n<b>Dionȳsēus, a, um</b> [Dionysus]\nдионисов, вакхов <i>Sil</i>."],
  ["Dionysia", "<big>Dionysia</big>\n\n<b>Dionȳsia, ōrum</b> <i>n</i>\nДионисийские (Вакховы) празднества <i>Pl, Ter</i>."],
  ["Dionysiacus", "<big>Dionysiacus</big>\n\n<b>Dionȳsiacus, a, um</b> <i>Aus</i>\n= Dionyseus."],
  ["Dionysius", "<big>Dionysius</big>\n\n<b>I Dionȳsius, ī</b> <i>m</i>\nДионисий:\n<b>1)</b> Д. Старший (D. Superior <i>C</i> <i>или</i> Prior <i>Nep</i>), <i>из Сиракуз, сын Гермократа (431-367 гг. до н. э.), с 405 г. — тиран Сиракуз</i> <i>C, Nep</i>;\n<b>2)</b> Д. Младший, <i>сын и преемник предыдущего (с 367 г. до н. э.); в 357 г. до н. э. был на непродолжительное время свергнут Дионом; в 343 г. до н. э. был свергнут окончательно Тимолеонтом и остаток жизни прожил в Коринфе</i> <i>Nep, C, VM</i>;\n<b>3)</b> <i>из Гераклеи (Лукания), философ, ученик Зенона</i> <i>C</i>;\n<b>4)</b> Cassius D., <i>уроженец Утики, латинский переводчик сочинения карфагенского писателя Магона о земледелии</i> <i>Vr, Col</i>;\n<b>5)</b> D. Magnes, <i>из Магнесии, ритор, современник Цицерона</i> <i>C</i>;\n<b>6)</b> <i>учёный раб Цицерона</i> <i>C</i>;\n<b>7)</b> <i>вольноотпущенник Аттика</i> <i>C</i>;\n<b>8)</b> D. Cato, <i>современник Диоклетиана или обоих Антонинов, предполагаемый автор сборника нравоучительных двустиший.</i>\n\n<b>II Dionȳsius, a, um</b> <i>Pl, Ter</i>\n= Dionyseus."],
  ["Dionysus", "<big>Dionysus</big>\n\n<b>Dionȳsus, ī</b> <i>m</i> <i>Pl, C, Aus</i>\n= Bacchus."],
  ["diopetes", "<big>diopetes</big>\n\n<b>diopetēs, is</b> <i>f</i> (<i>греч.</i> «упавший с неба»)\n(<i>sc.</i> rana) дождевая лягушка <i>PM</i>."],
  ["dioptra", "<big>dioptra</big>\n\n<b>dioptra, ae</b> <i>f</i> (<i>греч.</i>)\nдиоптра, <i>инструмент для измерения высот и солнечных теней</i> <i>Vtr, PM</i>."],
  ["dioryx", "<big>dioryx</big>\n\n<b>diōryx, ygis</b> <i>f</i> (<i>греч.</i>)\nводоотводный канал <i>Mela, Vlg</i>."],
  ["Dioscuri", "<big>Dioscuri</big>\n\n<b>Dioscūrī, ōrum</b> <i>m</i> <i>C</i>\n(<i>тж.</i> pueri Ledae <i>H</i>) Диоскуры (Кастор <i>и</i> Поллукс) Tyndaridae."],
  ["diota", "<big>diota</big>\n\n<b>diōta, ae</b> <i>f</i> (<i>греч.</i>)\nсосуд с двумя ручками, кувшин для вина <i>H</i>."],
  ["Diovis", "<big>Diovis</big>\n\n<b>Diovis</b>\n= Dijovis."],
  ["Diphilus", "<big>Diphilus</big>\n\n<b>Dīphilus, ī</b> <i>m</i>\nДифил:\n<b>1)</b> <i>греч. драматург-комик, современник Менандра</i> <i>Pl, Ter</i>;\n<b>2)</b> <i>актёр-трагик в Риме, современник Помпея</i> <i>C</i>;\n<b>3)</b> <i>писец и чтец Л. Красса</i> <i>C</i>;\n<b>4)</b> <i>римск. зодчий</i> <i>C, Vtr</i>."],
  ["diphtongo", "<big>diphtongo</big>\n\n<b>diphtongo, —, —, āre</b> [diphtongus]\nпроизносить <i>или</i> писать в виде дифтонга <i>Ap</i>."],
  ["diphtongos", "<big>diphtongos</big>\n\n<b>diphtongos (diphtongus), ī</b> <i>m</i> (<i>греч.</i>)\nдвугласно-односложный звук, дифтонг <i>Ap</i>."],
  ["diplasius", "<big>diplasius</big>\n\n<b>dīplasius, a, um</b> (<i>греч.</i>)\nдвойной (numerus <i>C</i>)."],
  ["diplinthius", "<big>diplinthius</big>\n\n<b>dīplinthius, a, um</b> (<i>греч.</i>)\nтолщиной в два кирпича (paries <i>Vtr</i>)."],
  ["diploma", "<big>diploma</big>\n\n<b>diplōma, atis</b> <i>n</i> (<i>греч.</i>)\nдиплом:\n<b>1)</b> письмо, записка (diplomata consignata<i>Macr</i>);\n<b>2)</b> официальное рекомендательное письмо, дававшееся тем, которые отправлялись в провинции с поручениями от центральной власти <i>C</i>;\n<b>3)</b> подорожная <i>Sen, T, PJ</i>;\n<b>4)</b> грамота о помиловании <i>C</i>;\n<b>5)</b> грамота о принятии в римское гражданство <i>Su</i>;\n<b>6)</b> охранная грамота <i>Sen, Su</i>."],
  ["dipodia", "<big>dipodia</big>\n\n<b>dipodia, ae</b> <i>f</i> (<i>греч.</i>)\nдиподия, <i>сочетание двух двусложных стоп в одной.</i>"],
  ["dipond-", "<big>dipond-</big>\n\n<b>dipond-</b> <i>v. l.</i>\n= dupond-."],
  ["dipsacos", "<big>dipsacos</big>\n\n<b>dipsacos, ī</b> <i>f</i> (<i>греч.</i>) <i>бот.</i>\nворсянка сукновальная, кардовник (<i>Dipsacus fullonum</i>, L.) <i>PM</i>."],
  ["Dipsas", "<big>Dipsas</big>\n\n<b>I Dipsas, adis</b> <i>f</i>\nДипсада, <i>женское имя</i> <i>O</i>.\n\n<b>II Dipsās, antis</b> <i>m</i>\nДипсант, <i>река в Киликии</i> <i>Lcn</i>.\n\n<b>III dipsas, adis</b> (<i>acc. pl.</i> adas) <i>f</i> (<i>греч.</i>)\nдипсада, <i>род ядовитой вмеи, укус которой якобы вызывал сильную жажду</i> (<i>предпол. Coluber Vipera</i>, L.) <i>PM, Lcn, Sil, M etc.</i>"],
  ["dipteros", "<big>dipteros</big>\n\n<b>dipteros, on</b> (<i>греч.</i>)\nдвукрылый: d. aedes <i>Vtr</i> здание с двумя рядами колонн."],
  ["diptychum", "<big>diptychum</big>\n\n<b>diptychum, ī</b> <i>n</i> (<i>греч.</i>) (<i>преим.</i> <i>pl.</i>)\nдиптих, <i>складывавшаяся вдвое писчая табличка</i> <i>Symm, CTh</i>; опись, список, перечень <i>Eccl</i>."],
  ["dipund-", "<big>dipund-</big>\n\n<b>dipund-</b> <i>v. l.</i>\n= dupond-."],
  ["Dipylon", "<big>Dipylon</big>\n\n<b>Dipylon, ī</b> <i>n</i>\n«Двойные врата» <i>в Афинах, через которые шла дорога из внутреннего Керамика во внешний и, далее, в Академию</i> <i>C, L</i>."],
  ["dipyros", "<big>dipyros</big>\n\n<b>dipyros, on</b> (<i>греч.</i>)\nдважды обожженный: dipyron facere <i>M</i> вторично обжечь (<i>об энкаустическом изображении, сожжённого Юпитером Фаэтонта</i>)."],
  ["dira", "<big>dira</big>\n\n<b>dīra, ōrum</b> <i>n</i> <i>C, Tib</i>\n= dirae."],
  ["Dirae", "<big>Dirae</big>\n\n<b>I Dīrae, ārum</b> <i>f</i> <i>V</i>\n= Furiae.\n\n<b>II dīrae, ārum</b> <i>f</i> [dirus]\n<b>1)</b> зловещие признаки, страшные предзнаменования (inter diras haberi <i>PM</i>);\n<b>2)</b> проклятия: diras alicui imprecari <i>T</i> <i>или</i> diris agere aliquem <i>H</i> проклинать кого-л."],
  ["dirapio", "<big>dirapio</big>\n\n<b>dī–rapio (disrapio), —, —, ere</b>\nотрывать, <i>перен.</i> отвлекать (aliquem ab aliqua re <i>Ap</i>)."],
  ["diraro", "<big>diraro</big>\n\n<b>dīrāro</b> <i>v. l.</i>\n= disraro."],
  ["Dircaeus", "<big>Dircaeus</big>\n\n<b>Dircaeus, a, um</b> [Dirce]\nфиванский, беотийский <i>V, SenT</i>: D. cycnus <i>H</i> = Pindarus."],
  ["Dirce", "<big>Dirce</big>\n\n<b>Dircē, ēs</b> <i>и</i> <b>Dirca, ae</b> <i>f</i>\nДирка:\n<b>1)</b> <i>дочь Солнца, жена фиванского царя Лика, превращенная в источник</i> (<i>близ Фив</i>) <i>Pl, Prp</i>;\n<b>2)</b> <i>источник к сев.-зап. от Фив Беотийских</i> <i>Pl, St</i>;\n<b>3)</b> <i>St</i> = Boeotia."],
  ["dire", "<big>dire</big>\n\n<b>dīrē</b> [dirus]\nжестоко (agere <i>SenT</i>)."],
  ["directarius", "<big>directarius</big>\n\n<b>dīrēctārius, ī</b> <i>m</i>\nвор-взломщик <i>Dig</i>."],
  ["directe", "<big>directe</big>\n\n<b>dīrēctē</b> [directus]\n<b>1)</b> прямо: d. ad perpendiculum <i>Cs</i> перпендикулярно;\n<b>2)</b> напрямик, без обиняков (dicere <i>C</i>)."],
  ["directim", "<big>directim</big>\n\n<b>dīrēctim</b> [directus]\n<b>1)</b> прямо (d. e monte lapidem afferre <i>Ap</i>);\n<b>2)</b> прямо-таки, просто <i>Ap</i>."],
  ["directio", "<big>directio</big>\n\n<b>dīrēctio, ōnis</b> <i>f</i> [dirigo]\n<b>1)</b> выпрямление <i>Vtr</i>;\n<b>2)</b> прямая линия <i>Ap</i>: d. plana <i>Vtr</i> ровная поверхность, плоскость;\n<b>3)</b> направление (directiones linearum <i>Vtr</i>; d. rationis ad veritatem <i>Q</i>);\n<b>4)</b> прямота, искренность (cordis <i>Vlg</i>);\n<b>5)</b> справедливость <i>Vlg</i>."],
  ["directo", "<big>directo</big>\n\n<b>dīrēctō</b> <i>C, Sen</i>\n= directim."],
  ["director", "<big>director</big>\n\n<b>dīrēctor, ōris</b> <i>m</i> [dirigo]\nпроводник, руководитель <i>Eccl</i>."],
  ["directorium", "<big>directorium</big>\n\n<b>dīrēctōrium, ī</b> <i>n</i>\nмаршрут <i>CTh</i>."],
  ["directura", "<big>directura</big>\n\n<b>dīrēctūra, ae</b> <i>f</i> [dirigo]\n<b>1)</b> направление <i>Frontin</i>;\n<b>2)</b> ровный слой штукатурки <i>Vtr</i>."],
  ["directus", "<big>directus</big>\n\n<b>dīrēctus (dērēctus), a, um</b> [dirigo]\n<b>1)</b> прямой (iter <i>C</i>); прямо <i>или</i> под прямым углом поставленный (trabs <i>Cs</i>; paries <i>C</i>): capillus d. <i>Vtr</i> гладкие волосы; per directum <i>PM</i> (in directum <i>Sen</i>, in directo <i>Vr</i> <i>и</i> directum <i>Pt</i>) прямо, напрямик <i>или</i> по существу;\n<b>2)</b> отвесный, крутой (locus <i>Cs</i>; jugum <i>Mela</i>): fossa directis lateribus <i>Cs</i> ров с отвесными стенками;\n<b>3)</b> прямой, непосредственный: percunctatio directa <i>L</i> непосредственные расспросы;\n<b>4)</b> прямодушный, откровенный (homo, verba <i>C</i>)."],
  ["diremi", "<big>diremi</big>\n\n<b>dirēmī</b> <i>pf. к</i> \ndirimo."],
  ["diremptio", "<big>diremptio</big>\n\n<b>dirēmptio, ōnis</b> <i>f</i> <i>VM, Aug</i>\n= diremptus."],
  ["diremptus", "<big>diremptus</big>\n\n<b>dirēmptus, ūs</b> <i>m</i> [dirimo]\n<b>1)</b> разделение, расчленение (partium <i>C</i>);\n<b>2)</b> разрыв (amicitiae <i>C</i>)."],
  ["direptio", "<big>direptio</big>\n\n<b>dīreptio, ōnis</b> <i>f</i> [diripio]\nразграбление, расхищение (urbis, bonorum <i>C</i>); ограбление (civium <i>C</i>); похищение (aurei velleris <i>Amm</i>)."],
  ["direptor", "<big>direptor</big>\n\n<b>dīreptor, ōris</b> <i>m</i> [diripio]\nграбитель, похититель <i>C, T, Eccl</i>."],
  ["direptus", "<big>direptus</big>\n\n<b>I dīreptus, a, um</b> <i>part. pf. к</i>\ndiripio.\n\n<b>II dīreptus, ūs</b> <i>m</i> <i>Spart</i>\n= direptio."],
  ["direxi", "<big>direxi</big>\n\n<b>dīrēxī</b> <i>pf. к</i>\ndirigo."],
  ["direxti", "<big>direxti</big>\n\n<b>dīrēxtī</b> <i>V</i>\n= direxisti."],
  ["diribeo", "<big>diribeo</big>\n\n<b>diribeo, uī, itum, ēre</b> [dis + habeo]\n<b>1)</b> раздавать, распределять (gentes, regna <i>PM</i>);\n<b>2)</b> сортировать для подсчёта голосов (d. tabellas <i>C</i>; suffragia <i>Vr</i>; sententias <i>VM</i>)."],
  ["diribitio", "<big>diribitio</big>\n\n<b>diribitio, ōnis</b> <i>f</i> [diribeo]\nсортировка вотивных табличек, <i>т. е.</i> подсчёт голосов <i>C</i>."],
  ["diribitor", "<big>diribitor</big>\n\n<b>diribitor, ōris</b> <i>m</i> [diribeo]\n<b>1)</b> слуга, деливший кушанья и распределявший их между гостями <i>Ap</i>;\n<b>2)</b> счётчик голосов <i>C, Aus</i>;\n<b>3)</b> распределитель <i>Amm</i>."],
  ["diribitorium", "<big>diribitorium</big>\n\n<b>diribitōrium, ī</b> <i>n</i> [diribeo]\nдирибиторий, <i>здание в Риме, служившее вначале для подсчёта поданных в комициях голосов, а вашем для раздачи денег, продовольствия и т. п. народу и для выплаты жалованья солдатам</i> <i>PM, Su</i>."],
  ["dirigesco", "<big>dirigesco</big>\n\n<b>dīrigēsco</b> <i>v. l.</i>\n= derigesco."],
  ["dirigo", "<big>dirigo</big>\n\n<b>dī–rigo (dērigo), rēxī, rēctum, ere</b> [dis + rego]\n<b>1)</b> выстраивать в прямую линию (d. naves in pugnam <i>L</i>); выстраивать в боевом порядке (d. aciem <i>Cs</i>); выравнивать, проводить <i>или</i> ставить по прямой линии (tigna ad perpendiculum <i>Cs</i>); располагать (arborum ordines in quincuncem <i>C</i>); намечать, размечать (regiones lituo <i>C</i>); выпрямлять (flumina <i>C</i>): amnis direxit aquas <i>Lcn</i> река выпрямила своё течение (<i>т. е.</i> перестала быть извилистой);\n<b>2)</b> покрывать прямыми линиями, линовать (membrana plumbo directa <i>Ctl</i>);\n<b>3)</b> направлять (cursum ad litora <i>Cs</i>; iter ad Mulinam <i>C</i>): d. bracchia contra torrentem <i>J</i> плыть против течения; d. arcum in aliquid <i>Pers</i> целиться из лука во что-л.; directis operibus <i>Cs</i> дав должное направление осадным орудиям || отправлять, посылать (litteras <i>C</i>); обращать (os in aliquem locum <i>AG</i>; cogitationes ad aliquid <i>C</i>; intentionem in aliquid <i>Q</i>): omnia consilia factaque ad utilitatem vitae d. <i>T</i> направить все свои помыслы и дела на житейскую полезность || d. <i>Lcr, Sen</i> <i>или</i> se d. <i>VM, Ap</i> направляться, отправляться;\n<b>4)</b> бросать, метать, пускать (sagittas <i>Su</i>; hastam in aliquem <i>O</i> <i>и</i> alicui <i>V</i>): a. aciem (oculorum) ad aliquem <i>Ctl etc.</i> устремить взор на кого-л.;\n<b>5)</b> определять (d. aliquid aliqua re <i>или</i> ad aliquid <i>Vtr, C</i>): direxisse finem alicui veterem viam <i>L</i> установить в качестве чьей-л. границы старую дорогу || приспособлять, приноравливать (leges hominum ad naturam <i>C</i>): vitam ad certam rationis normam d. <i>C</i> строить жизнь согласно строгим правилам разума; d. se ad id, quod est optimum <i>Q</i> ориентироваться на лучшее || стремиться (d. ad veritatem <i>C</i>): d. ad duas metas <i>Vr</i> иметь в виду (сразу) две цели."],
  ["dirimo", "<big>dirimo</big>\n\n<b>dir–imo, ēmī, ēmptum, ere</b> [dis + emo]\n<b>1)</b> разнимать, разъединять, разобщать, разделять (urbs flumine dirempta <i>L</i>): quod dirimi non possit <i>C</i> нечто неделимое; nox incertos, victi victoresne essent, diremit <i>L</i> ночь разняла (развела) (бойцов), которые недоумевали, побеждённые они или победители; oppida unius diei itinere dirimuntur <i>PM</i> города находятся на расстоянии одного дня пути друг от друга;\n<b>2)</b> разбирать, сортировать: d. sententias occidentis et relegantis <i>PJ</i> отделять мнения тех, кто стоит за смертную казнь, от мнения тех, кто голосует за изгнание; d. suffragia (tabellas) <i>C</i> подсчитывать голоса (<i>по табличкам, на которых участники собраний записывали свои мнения при голосовании</i>);\n<b>3)</b> прерывать, прекращать (d. proelium <i>Sl, L etc.</i>; rem susceptam <i>C</i>; colloquium <i>Cs</i>): ira diremptae pacis <i>L</i> возмущение по поводу того, что нарушен мир;\n<b>4)</b> разрешать, улаживать (litem <i>O</i>; controversiam <i>C</i>);\n<b>5)</b> расстраивать, расторгать (societatem <i>C</i>; amicitias <i>T</i>; nuptias <i>Su</i>)."],
  ["diripio", "<big>diripio</big>\n\n<b>dī–ripio, ripuī, reptum, ere</b> [rapio]\n<b>1)</b> разграблять, расхищать, грабить, опустошать (urbem <i>L</i>; provincias <i>C</i>; bona alicujus <i>C</i>);\n<b>2)</b> срывать, сдирать (a pectore vestem <i>O</i>; ex capite insigne <i>QC</i>); отрывать, выхватывать (ferrum a latere <i>T</i>);\n<b>3)</b> разрывать, раздирать на части (membra alicujus <i>O</i>); потрясать, волновать (venti diripiunt fretum <i>St</i>)."],
  ["diritas", "<big>diritas</big>\n\n<b>dīritās, ātis</b> <i>f</i> [dirus]\n<b>1)</b> ужас, жуть (ominis <i>AG</i>; diei <i>Su</i>);\n<b>2)</b> бедствие, несчастье <i>C</i>;\n<b>3)</b> свирепость, жестокость (morum <i>Su</i>)."],
  ["dirrumptus", "<big>dirrumptus</big>\n\n<b>dirrumptus</b> <i>Pl</i> (= diruptus) <i>part. pf. к</i>\ndirumpo."],
  ["dirum", "<big>dirum</big>\n\n<b>dīrum</b> <i>adv.</i> [dirus]\nстрашно (fremens <i>SenT</i>)."],
  ["dirumpo", "<big>dirumpo</big>\n\n<b>dī–rumpo (disrumpo), rūpī, ruptum, ere</b> [rumpo]\n<b>1)</b> разрывать, разбивать (imagines alicujus <i>T</i>): homo diruptus <i>C</i> человек с расстроенным здоровьем || размозжить (alicui caput <i>Pl</i>); взломать (januas <i>Vop</i>); рвать (vetus funis disrumpitur <i>Ap</i>);\n<b>2)</b> прерывать, расторгать (d. amicitiam, societatem <i>C</i>);\n<b>3)</b> терзать, мучить: d. se <i>или</i> dirumpi надрываться, лезть из кожи вон (furia <i>C</i>) || быть снедаемым (dirumpi dolore <i>C</i>): unum omnia posse dirumpi <i>C</i> возмущаться тем, что одни человек обладает всей полнотой власти; risu dirumpi <i>Ap</i> лопаться со смеху."],
  ["diruo", "<big>diruo</big>\n\n<b>dī–ruo, ruī, rutum, ere</b>\n<b>1)</b> разрушать, сносить (urbem <i>C</i>; muros <i>Nep, L</i>; diruta Pergama <i>O</i>); разгонять, рассеивать (agmina <i>H</i>);\n<b>2)</b> разорять: homo (aere) dirutus <i>C, Aus</i> разорившийся (обанкротившийся) человек; miles aere dirutus <i>Vr, PM</i> солдат, в наказание лишённый жалованья."],
  ["dirupi", "<big>dirupi</big>\n\n<b>dīrūpī</b> <i>pf. к</i>\ndirumpo."],
  ["diruptio", "<big>diruptio</big>\n\n<b>dīruptio, ōnis</b> <i>f</i> [dirumpo]\nразрывание, разрыв (alicujus rei <i>Sen</i>)."],
  ["dirus", "<big>dirus</big>\n\n<b>dīrus, a, um</b>\n<b>1)</b> ужасный, страшный (omen <i>O</i>): dirae preces <i>T</i> проклятия || зловещий (aves <i>T</i>; somnia <i>VF</i>); грозный, жуткий (tempus <i>Poeta ap. C</i>); жестокий (dea, noverca <i>O</i>); дикий, неистовый (libido <i>Lcr</i>; amores <i>O</i>);\n<b>2)</b> мощный, крепкий (dira portas quassare trabs <i>Sil</i>)."],
  ["Dis", "<big>Dis</big>\n\n<b>I Dīs, Dītis</b> <i>m</i> [<i>одного корня с</i> dives]\n<b>1)</b> Дит = Плутон, <i>бог подземного царства</i> <i>V, Q etc.</i>;\n<b>2)</b> подземное царство <i>V</i>.\n\n<b>II dīs, dītis</b> <i>Ter, H, O etc.</i>\n= ditis I.\n\n<b>III dīs</b> <i>dat./abl. pl. к</i>\ndeus.\n\n<b>dis-</b>\n<i>приставка, означающая разделение, разъединение, расчленение</i> (<i>не изменяется перед</i> c, p, t, j, <i>перед</i> s <i>с последующей гласной</i>; s <i>между двумя гласными переходит в</i> r, <i>напр.</i> dirimo; <i>перед</i> f — <i>ассимилируется, напр.</i> differo; <i>перед</i> d, g, l, m, n, r, v <i>и</i> s <i>с последующей согласной</i> dis- <i>переходит в </i>dī-)."],
  ["discalceatus", "<big>discalceatus</big>\n\n<b>dis–calceātus, a, um</b>\nразутый, необутый <i>Su, Veg, Vlg, Eccl</i>."],
  ["discapedino", "<big>discapedino</big>\n\n<b>dis–capēdino, āvī, —, āre</b>\nдержать врозь, раздвинуть (manus in canendo <i>Ap</i>)."],
  ["discaveo", "<big>discaveo</big>\n\n<b>dis–caveo, —, —, ēre</b>\nтщательно остерегаться, беречься (aliqua re <i>Pl</i> — <i>v. l.</i>)."],
  ["discedo", "<big>discedo</big>\n\n<b>dis–cēdo, cessī, cessum, ere</b>\n<b>1)</b> расходиться, разделяться (inter se <i>Nep</i>; Numidae discedunt in duas partes <i>Sl</i>); распадаться, делиться (in duo genera, in tres partes <i>Q</i>); раскрываться, разверзаться (terra discedit <i>C</i>): sulcus vomere discessit <i>Lcn</i> сошник провёл борозду, <i>т. е.</i> вспахал землю || проясняться (caelum discedit <i>C, V</i>); раскалываться (omnis Italia animis discessit <i>Sl</i>);\n<b>2)</b> уходить, удаляться (ab aliquo <i>Cs</i>; ex contione <i>Sl</i>; de foro <i>C</i>; finibus <i>O</i>; ad urbem <i>V</i>; in silvas <i>Cs</i>): te discedente <i>O</i> при твоём отъезде; d. ex hibernis <i>Cs</i> выступить из зимней стоянки; d. ab amicis <i>C</i> расстаться с друзьями; d. ab oppugnatione castrorum <i>Cs</i> отказаться от осады лагеря; viduo d. lecto <i>O</i> оставить вдовье ложе, <i>т. е.</i> вступить в новый брак; d. ab armis <i>Cs</i> сложить оружие; sine detrimento d. <i>Cs</i> не потерпеть никакого ущерба, счастливо отделаться; superior discessit <i>Cs, C</i> <i>или</i> victor discessit <i>Cs, Amm</i> он ушёл (остался) победителем; tanta injuria impunita discessit <i>C</i> столь крупное преступление осталось безнаказанным;\n<b>3)</b> отделяться (calx ab arena discedit <i>Vtr</i>): d. in alicujus sententiam <i>L</i> присоединиться к чьему-л. мнению (<i>в сенате</i>);\n<b>4)</b> отступать, удаляться, отклоняться, уклоняться (d. a fide et justitia <i>C</i>): d. ab officio <i>Cs, C</i> изменить своему долгу; d. a re <i>C</i> уклониться от главной темы; d. a sua sententia <i>Cs</i> отказаться от своего мнения; d. ex (a) vita <i>C</i> (<i>поздн.</i> <i>просто</i> d. <i>Amm, MF</i>) умереть; d. in alia omnia <i>C</i> быть совсем противоположного мнения (<i>см.</i> discessio 1);\n<b>5)</b> исчезнуть: nunquam ex animo meo discedit illius viri memoria <i>C</i> никогда не покидает меня воспоминание об этом муже;\n<b>6)</b> отвлекаться: d. ab aliquo (ab aliqua re) <i>C etc.</i> отвлекаться от кого (чего)-л., <i>т. е.</i> исключить кого-л. (что-л.); quum a vobis discesserim <i>C</i> исключая вас."],
  ["discens", "<big>discens</big>\n\n<b>discēns, entis</b> <i>m</i> [disco I]\nученик <i>L, Sen</i>."],
  ["discentia", "<big>discentia</big>\n\n<b>discentia, ae</b> <i>f</i> [disco I]\nучение, наука <i>Tert</i>."],
  ["disceptatio", "<big>disceptatio</big>\n\n<b>disceptātio, ōnis</b> <i>f</i> [discepto]\n<b>1)</b> рассуждение, <i>тж.</i> обмен мнений, прения, спор (cum aliquo <i>L etc.</i>);\n<b>2)</b> спорный вопрос <i>C</i>;\n<b>3)</b> исследование (veritatis <i>C</i>);\n<b>4)</b> судебное решение (disceptationem ab aliquo ad aliquem revocare <i>L</i>)."],
  ["disceptator", "<big>disceptator</big>\n\n<b>disceptātor, ōris</b> <i>m</i> [discepto]\nразбирающий спорное дело, судья (d. juris <i>C</i>; agrorum <i>T</i>; litium et jurgiorum disceptator atque arbiter <i>Ap</i>)."],
  ["disceptatrix", "<big>disceptatrix</big>\n\n<b>disceptātrīx, īcis</b> <i>C, Lampr, Aug f к</i>\ndisceptator."],
  ["discepto", "<big>discepto</big>\n\n<b>dis–cepto, āvī, ātum, āre</b> [capto]\n<b>1)</b> разбирать, рассуждать (de jure <i>L, C</i>; de cunctis negotiis inter se <i>Sl</i>);\n<b>2)</b> обсуждать, рассматривать, разбирать, решать (controversias <i>C</i>): d. inter aliquos <i>L, PJ</i> быть посредником (судьёй) между кем-л.; multum invicem disceptato <i>T</i> после того, как вопрос был обсуждён с разных сторон; d. bella <i>C</i> решать вопрос об объявлении войны; in uno proelio omnis fortuna rei publicae disceptat (<i>v. l.</i> disceptatur) <i>C</i> вся судьба государства зависит от одного сражения."],
  ["discernenter", "<big>discernenter</big>\n\n<b>discernenter</b> <i>adv.</i>\nс различием, различая <i>CA</i>."],
  ["discernibilis", "<big>discernibilis</big>\n\n<b>discernibilis, e</b> [discerno]\nразличимый <i>Aug</i>."],
  ["discerniculum", "<big>discerniculum</big>\n\n<b>discerniculum, ī</b> <i>n</i> [discerno]\n<b>1)</b> шпилька для волос <i>LM, Vr</i>;\n<b>2)</b> разница, различие <i>AG, Eccl</i>."],
  ["discerno", "<big>discerno</big>\n\n<b>dis–cerno, crēvī, crētum, ere</b>\n<b>1)</b> отделять, разделять (Lusitaniam a Baetica discernit mons <i>PM</i>): septem discretus in ostia Nilus <i>O</i> Нил, разделённый (распадающийся) на семь устий (рукавов); telas d. tenui auro <i>V</i> расшить ткань золотыми нитями;\n<b>2)</b> различать (aliquid visu <i>PM</i>); отличать (jus et injuriam <i>T</i>; aliquid ab aliqua re <i>Lcr, C</i>);\n<b>3)</b> распознавать, узнавать (suos <i>Cs</i>);\n<b>4)</b> решать, улаживать (lites ruricolarum <i>Calp</i>)."],
  ["discerpo", "<big>discerpo</big>\n\n<b>dis–cerpo, cerpsī, cerptum, ere</b> [carpo]\n<b>1)</b> разрывать, раздирать на части, разделять (aliquem <i>L, C, Su</i>; aliquid in parvas partes <i>Lcr</i>);\n<b>2)</b> рассеивать, развеивать (venti discerpunt aliquid <i>Ctl</i>); дробить, распылять (quasi in membra <i>C</i>);\n<b>3)</b> раскритиковать, выбранить, разнести (aliquem infestis dictis <i>Ctl</i>)."],
  ["discerptim", "<big>discerptim</big>\n\n<b>discerptim</b> <i>adv.</i>\nпо частям (dicere aliquid <i>Boët</i>)."],
  ["discerptio", "<big>discerptio</big>\n\n<b>discerptio, ōnis</b> <i>f</i> [discerpo]\nразрывание, растерзывание <i>Vlg</i>."],
  ["discerptus", "<big>discerptus</big>\n\n<b>discerptus, a, um</b> <i>part. pf. к</i>\ndiscerpo."],
  ["discertatio", "<big>discertatio</big>\n\n<b>discertātio, ōnis</b> <i>f</i> [discerto]\nразъяснение, обсуждение (philosophiae discertationes <i>AG</i>)."],
  ["discerto", "<big>discerto</big>\n\n<b>dis–certo, —, —, āre</b>\nобсуждать, спорить <i>Pl</i> (<i>v. l.</i>)."],
  ["discessi", "<big>discessi</big>\n\n<b>discessī</b> <i>pf. к</i>\ndiscedo."],
  ["discessio", "<big>discessio</big>\n\n<b>discessio, ōnis</b> <i>f</i> [discedo]\n<b>1)</b> расхождение, разделение (populi in duas partes <i>AG</i>); уход (plebei a patribus <i>Sl</i>); удаление (stellarum <i>AG</i>); развод, расторжение брака <i>Ter</i>;\n<b>2)</b> <i>воен.</i> выступление <i>T</i>;\n<b>3)</b> дисцессия, голосование в порядке перехода сенаторов на ту или другую сторону (<i>без прений</i>): in Catonis sententiam est facta d. <i>C</i> сенаторы согласились с мнением Катона; senatus consultum factum est per discessionem <i>C</i> сенатское определение было вынесено в порядке дисцессии."],
  ["discessus", "<big>discessus</big>\n\n<b>discessus, ūs</b> <i>m</i> [discedo]\n<b>1)</b> отъезд, разлука (d. longinquus <i>C</i>): tuum post discessum in Tusculano <i>C</i> после твоего отъезда из Тускулана;\n<b>2)</b> отделение, расставание (animi a corpore <i>C</i>);\n<b>3)</b> отступление, удаление, уход (militum <i>Cs</i>);\n<b>4)</b> разрыв: d. caeli <i>C</i> зарницы."],
  ["discesti", "<big>discesti</big>\n\n<b>discestī</b> <i>Pl</i>\n= discessisti (<i>2 л. sg. pf. к</i> discedo)."],
  ["disceus", "<big>disceus</big>\n\n<b>disce͡us, eī</b> <i>m</i> (<i>греч.</i>)\nдискообразный метеор <i>PM</i>."],
  ["discibilis", "<big>discibilis</big>\n\n<b>discibilis, e</b> [disco I]\nподдающийся усвоению, которому можно научиться (virtutes <i>Boët</i>)."],
  ["discidi", "<big>discidi</big>\n\n<b>discidī</b> <i>pf. к</i>\ndiscindo."],
  ["discidium", "<big>discidium</big>\n\n<b>discidium, ī</b> <i>n</i> [discindo]\n<b>1)</b> разрыв (nubis <i>Lcr</i>); расторжение (publicorum foederum <i>L</i>); развод <i>C etc.</i>;\n<b>2)</b> расставание, разлука (corporis atque animae <i>Lcr</i>): d. tuum <i>C</i> разлука с тобой;\n<b>3)</b> раздор (d. ac dissensio <i>C</i>)."],
  ["discido", "<big>discido</big>\n\n<b>dis–cīdo, —, —, ere</b> [caedo]\nразрезать, разрубать (aliquid in multas partes <i>Lcr</i>)."],
  ["discinctus", "<big>discinctus</big>\n\n<b>discīnctus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> discingo;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> безалаберный, неряшливый (verna <i>Pers</i>);\n<b>2)</b> беззаботный, беспечный, легкомысленный (nepos <i>H</i>; otia <i>O</i>)."],
  ["discindo", "<big>discindo</big>\n\n<b>di–scindo, scidī, scissum, ere</b> [scindo]\n<b>1)</b> раздирать, разрывать (librum <i>AG</i>; vestem <i>Ter, Su</i>); сорвать (tunicam a pectore <i>L</i>); раздробить (alicui maxillam <i>Su</i>); рассекать, разрезать (d. cotem novacula <i>C</i>): oratio inter respondentem et interrogantem discissa <i>Sen</i> речь, расчленённая на ответы и вопросы;\n<b>2)</b> сечь, пороть (aliquem flagellis <i>Su</i>);\n<b>3)</b> расторгать: d. amicitiam <i>C</i> порвать дружеские связи."],
  ["discingo", "<big>discingo</big>\n\n<b>dis–cingo, cīnxī, cīnctum, ere</b>\n<b>1)</b> распоясывать, развязывать, распускать (tunicam <i>H</i>): tunicatus et discinctus <i>Su</i> в неподпоясанной тунике (<i>знак траура</i>); centurio discinctus <i>L</i> центурион без пояса (<i>воинское взыскание</i>);\n<b>2)</b> уничтожать, обезвреживать (dolos <i>Sil</i>);\n<b>3)</b> расслаблять, лишать силы (d. ingenium rebus secundis <i>Sen</i>);\n<b>4)</b> разоружать (Afros <i>J</i>): discingi armis <i>Sil</i> сложить оружие."],
  ["disciplina", "<big>disciplina</big>\n\n<b>disciplīna, ae</b> <i>f</i> [disco I]\n<b>1)</b> учение, обучение, образование: tradere aliquem alicui in disciplinam <i>C</i> отдать кого-л. кому-л. в учение; disciplinae causa concurrere <i>Cs</i> съезжаться для того, чтобы учиться; res, quarum d. est <i>C</i> предметы, которые преподаются (предметы обучения); disciplinae (<i>dat.</i>) aliis esse <i>Pl</i> быть примером для других;\n<b>2)</b> наставление, воспитание (d. pueritiae <i>и</i> puerilis <i>C</i>);\n<b>3)</b> учение, теория, система <i>или</i> искусство (d. Stoicorum <i>C</i>); наука (animum disciplinis meliorem fieri <i>Q</i>); qui sunt ab ea disciplina <i>C</i> представители этой (философской) школы (этого направления); Anaxagoras accepit ab Anaximene disciplinam <i>C</i> Анаксагор воспринял учение Анаксимена; d. bellica <i>C</i> (militaris <i>Nep</i>) военная наука (военное дело); d. dicendi <i>C</i> риторика; d. populorum <i>C</i> искусство управлять народами; d. juris civilis <i>C</i> наука гражданского права;\n<b>4)</b> строгий порядок, организация, дисциплина (d. domestica <i>Su</i>): d. rei publicae <i>C</i> государственное устройство;\n<b>5)</b> обыкновения, навыки, <i>тж.</i> принципы, политические приёмы: d. Macedonum regum <i>QC</i> политика македонских царей; d. vivendi <i>C</i> образ жизни."],
  ["disciplinabilis", "<big>disciplinabilis</big>\n\n<b>disciplīnābilis, e</b> [disciplina]\nудобопреподаваемый, поддающийся обучению <i>Is</i>."],
  ["disciplinaris", "<big>disciplinaris</big>\n\n<b>disciplīnāris, e</b>\nнаучный (principia <i>Boët</i>)."],
  ["disciplinariter", "<big>disciplinariter</big>\n\n<b>disciplīnāriter</b>\nпо-научному, научно (versari in mathematicis <i>Boët</i>)."],
  ["disciplinatus", "<big>disciplinatus</big>\n\n<b>disciplīnātus, a, um</b>\nобученный, сведущий <i>Tert</i>."],
  ["disciplinosus", "<big>disciplinosus</big>\n\n<b>disciplīnōsus, a, um</b> [disciplina]\nобученный, искусный (gladiator <i>Cato</i>)."],
  ["discipula", "<big>discipula</big>\n\n<b>discipula, ae</b> <i>f</i> [disco I]\n<b>1)</b> ученица <i>Pl, H, Q etc.</i>;\n<b>2)</b> подражательница (luminis solis luna d. <i>Ap</i>)."],
  ["discipulatus", "<big>discipulatus</big>\n\n<b>discipulātus, ūs</b> <i>m</i> [discipulus]\nученичество <i>Tert</i>."],
  ["discipulina", "<big>discipulina</big>\n\n<b>discipulīna, ae</b> <i>f</i> <i>арх.</i> <i>Pl</i>\n= disciplina."],
  ["discipulus", "<big>discipulus</big>\n\n<b>discipulus, ī</b> <i>m</i> [disco I]\n<b>1)</b> ученик <i>Pl, C etc.</i>;\n<b>2)</b> последователь (d. Platonis <i>C</i>)."],
  ["discissio", "<big>discissio</big>\n\n<b>discissio, ōnis</b> <i>f</i> [discindo]\nразрыв, раскол <i>Eccl</i>."],
  ["discissus", "<big>discissus</big>\n\n<b>discissus, a, um</b> <i>part. pf. к</i>\ndiscindo."],
  ["discludo", "<big>discludo</big>\n\n<b>dis–clūdo, clūsī, clūsum, ere</b> [claudo]\n<b>1)</b> заключать в отдельные помещения (dispares disci usos habere pisces <i>Vr</i>); держать отдельно, класть врозь (tigna <i>Cs</i>); разрознивать, разобщать, отделять, разделять (mons discludit Arvernos ab Helviis <i>Cs</i>);\n<b>2)</b> разрушать (ictu turres <i>Lcr</i>);\n<b>3)</b> раскрывать, разнимать: morsus roboris d. <i>V</i> раздвинуть щель дерева (<i>чтобы вырвать ущемлённое в нём копьё</i>);\n<b>4)</b> прерывать, прекращать: spiritus officia d. <i>Ap</i> задерживать дыхание, душить."],
  ["disclusio", "<big>disclusio</big>\n\n<b>disclūsio, ōnis</b> <i>f</i>\nразделение, отделение (alicujus rei <i>Ap</i>)."],
  ["disco", "<big>disco</big>\n\n<b>I disco, didicī, —, ere</b>\n<b>1)</b> учиться (aliquid ab, de <i>или</i> ex aliquo <i>Ter, C etc.</i>; apud aliquem <i>C</i>): a bove majore discit arare minor <i>погов. O</i> у старшего вола учится младший пахать;\n<b>2)</b> изучать (litteras Graecas <i>C</i>; Latine loqui <i>Sl</i>): d. fidibus <i>C</i> учиться игре на лире; vera dicere didici <i>Pl</i> я привык (приучен) говорить правду; puer discens <i>Pt, Dig</i> ученик, подмастерье;\n<b>3)</b> узнавать, знакомиться (d. causam <i>C, Q</i>): quae didici, dixi omnia <i>Pl</i> я рассказал всё, что знаю; haec facta olim didiceram <i>Ap</i> эти события мне давно уже известны; disce, quae censet amiculus <i>H</i> прислушайся к тому, что советует друг; crimine ab uno disce omnes <i>V</i> <i>впоследствии погов.</i> по преступным делам одного (данайца) узнай, каковы они все.\n\n<b>II disco</b> <i>dat. abl. sg. к</i>\ndiscus."],
  ["discobino", "<big>discobino</big>\n\n<b>discobīno</b> <i>v. l.</i>\n= descobino."],
  ["discobolos", "<big>discobolos</big>\n\n<b>discobolos, ī</b> <i>m</i> (<i>греч.</i>)\nметатель диска, дискобол <i>PM, Q</i>."],
  ["discoctus", "<big>discoctus</big>\n\n<b>discoctus, a, um</b> <i>part. pf. к</i>\ndiscoquo."],
  ["discolor", "<big>discolor</big>\n\n<b>dis–color, ōris</b>\n<b>1)</b> разноцветный, пёстрый (cerae <i>Vr</i>; signa <i>C</i>; aves <i>PM</i>);\n<b>2)</b> контрастирующий, непохожий, несоответствующий (d. alicui <i>H</i>): rerum d. usus <i>Pers</i> различие вкусов; vestis alba d. fatis meis <i>O</i> белая одежда, столь мало соответствующая моей участи."],
  ["discolorius", "<big>discolorius</big>\n\n<b>dis–colōrius, a, um</b> [color]\nразноцветный, пёстрый (vestis <i>Pt</i>)."],
  ["discolorus", "<big>discolorus</big>\n\n<b>discolōrus, a, um</b> <i>Ap</i>\n= discolorius."],
  ["disconduco", "<big>disconduco</big>\n\n<b>dis–condūco, —, —, ere</b>\nне содействовать, противодействовать, вредить, быть вредным (alicui rei <i>Pl</i>)."],
  ["disconvenientia", "<big>disconvenientia</big>\n\n<b>disconvenientia, ae</b> <i>f</i>\nнесоответствие, разногласие <i>Tert</i>."],
  ["disconvenio", "<big>disconvenio</big>\n\n<b>dis–convenio, —, —, īre</b>\nне согласовываться, не соответствовать (alicui rei <i>H</i>); eo disconvenit (<i>impers.</i>) inter meque et te <i>H</i> в этом разница между нами."],
  ["discooperio", "<big>discooperio</big>\n\n<b>dis–cooperio, peruī, pertum, īre</b>\nоткрывать, обнажать (turpitudinem alicujus <i>Vlg</i>)."],
  ["discophorus", "<big>discophorus</big>\n\n<b>discophorus, ī</b> <i>m</i> (<i>греч.</i>)\nдискофор, разносящий блюда <i>Hier</i>."],
  ["discoquo", "<big>discoquo</big>\n\n<b>dis–coquo, coxī, coctum, ere</b>\nразваривать (aliquid in aqua <i>PM</i>)."],
  ["discordabilis", "<big>discordabilis</big>\n\n<b>discordābilis, e</b> [discordo]\nсклонный к ссорам, неуживчивый (ingenium <i>Pl</i>)."],
  ["discordia", "<big>discordia</big>\n\n<b>I discordia, ae</b> <i>f</i> [discors]\nразлад, раздор, несогласие (discordiae civiles S;, <i>Fl</i>; d. erga aliquem <i>T</i>): discordia fit carior concordia <i>PS</i> раздор придаёт большую ценность согласию; d., non homo <i>Pt ирон.</i> не человек, а (ходячая) склока; d. ponti <i>Lcn</i> морское волнение; d. mentis <i>O</i> растерянность, смущение || разногласие, противоречие (rerum, verborum <i>C, Lcr, Vr</i>): d. animi <i>Sen</i> непоследовательность; discordiae malum <i>Just</i> яблоко раздора.\n\n<b>II Discordia, ae</b> <i>f</i>\nДискордия, <i>дочь Эреба и Ночи, богиня раздора</i> <i>V</i>."],
  ["discordiosus", "<big>discordiosus</big>\n\n<b>discordiōsus, a, um</b> [discordia]\nсварливый <i>Sl, Sid</i>."],
  ["discorditas", "<big>discorditas</big>\n\n<b>discorditās, ātis</b> <i>f Pac</i> <i>и</i> <b>discordium, ī</b> <i>n</i> <i>Calp</i>\n= discordia."],
  ["discordo", "<big>discordo</big>\n\n<b>discordo, āvī, —, āre</b> [discors]\n<b>1)</b> быть несогласным, не ладить, враждовать (illi discordant inter se <i>Ter</i>; d. cum aliquo <i>T</i> <i>или</i> adversus aliquem <i>Q</i>); не хотеть, не соглашаться <i>H etc.</i>; бунтовать <i>T</i>;\n<b>2)</b> не согласоваться, не соответствовать (vox discordat ab oratione <i>Q</i>);\n<b>3)</b> не быть сходным, различаться (discordat parcus avaro <i>H</i>)."],
  ["discors", "<big>discors</big>\n\n<b>dis–cors, dis</b> [dis + cor]\n<b>1)</b> несогласный (cum aliquo <i>L</i> <i>или</i> alicui <i>VP, Fl, T</i>); противоречивый (responsa inter se discordia <i>L</i>): concordia d. <i>H, Lcn</i> примирение непримиримого, сочетание противоречивых вещей, <i>т. е.</i> неустойчивое равновесие;\n<b>2)</b> недружный, раздираемый междоусобиями (civitas <i>L, T</i>);\n<b>3)</b> непохожий, различный (homines discordes moribus et linguis <i>QC</i>): tempore discordes aestus <i>PM</i>. приливы, происходящие в разное время (нерегулярные)."],
  ["discredo", "<big>discredo</big>\n\n<b>dis–crēdo, —, —, ere</b>\nне верить (aliquid <i>JV</i>)."],
  ["discrepantia", "<big>discrepantia</big>\n\n<b>discrepantia, ae</b> <i>f</i> [discrepo]\nразногласие <i>L, Eccl</i>; противоречие, расхождение (scripti et voluntatis <i>C</i>; inter Stoicos et Peripateticos <i>C</i>)."],
  ["discrepatio", "<big>discrepatio</big>\n\n<b>discrepātio, ōnis</b> <i>f</i> [discrepo]\nнесогласие <i>L</i>."],
  ["discrepito", "<big>discrepito</big>\n\n<b>discrepito, —, —, āre</b> [<i>intens. к</i> discrepo]\nполностью различаться (inter se <i>Lcr</i>)."],
  ["discrepo", "<big>discrepo</big>\n\n<b>dis–crepo, āvī (uī), —, āre</b>\n<b>1)</b> расходиться, противоречить, не согласоваться, быть несогласным (cum aliquo, cum aliqua re <i>C</i>; ab aliqua re <i>C, VP</i>; nulla in re <i>C</i>);\n<b>2)</b> разниться, различаться, быть предметом спора: tibiae discrepant <i>C</i> флейты звучат нестройно; facta ejus discrepant cum dictis <i>C</i> его слова расходятся с его делами; longe mea discrepat istis et vox et ratio <i>H</i> я далёк от них и по языку, и по мыслям; duces discrepant inter se <i>L</i> между военачальниками происходят разногласия; causa discrepat <i>O</i> причина неясна;\n<b>3)</b> <i>impers.</i> discrepat нет согласия, мнения различны, неясно (discrepat inter scriptores <i>L</i>): non discrepat, quin dictator eo anno Cornelius fuerit <i>L</i> никто не спорит, что в этом году диктатором был Корнелий."],
  ["discresco", "<big>discresco</big>\n\n<b>dis–crēsco, crēvī, —, ere</b>\nразрастаться <i>Lact</i>."],
  ["discretim", "<big>discretim</big>\n\n<b>discrētim</b> <i>adv.</i> [discerno]\nотдельно, раздельно, порознь, врозь (singillatim ac d. <i>Ap</i>)."],
  ["discretio", "<big>discretio</big>\n\n<b>discrētio, ōnis</b> <i>f</i> [discerno]\n<b>1)</b> разделение <i>Dig, Macr, Eccl</i>;\n<b>2)</b> различение (boni et mali <i>Vlg</i>): sine <i>и</i> absque discretione <i>Pall, Amm</i> не делая различия."],
  ["discretor", "<big>discretor</big>\n\n<b>discrētor, ōris</b> <i>m</i> [discerno]\nотделяющий, различающий (sermo dei d. cogitationum et intentionum cordis <i>Vlg</i>; d. veritatis a falsitate <i>Aug</i>)."],
  ["discretorium", "<big>discretorium</big>\n\n<b>discrētōrium, ī</b> <i>n</i>\nгрудобрюшная преграда, диафрагма <i>CA</i>."],
  ["discretus", "<big>discretus</big>\n\n<b>discrētus, a, um</b> <i>part. pf. к</i>\ndiscerno."],
  ["discrevi", "<big>discrevi</big>\n\n<b>discrēvī</b> <i>pf. к</i>\n<b>1)</b> discerno <i>и</i>\n<b>2)</b> discresco."],
  ["discribo", "<big>discribo</big>\n\n<b>di–scrībo, scrīpsī, scrīptum, ere</b>\n<b>1)</b> расписывать, распределять (argentum alicui <i>Ter</i>);\n<b>2)</b> <i>см.</i> describo."],
  ["discrimen", "<big>discrimen</big>\n\n<b>discrīmen, inis</b> <i>n</i> [discerno]\n<b>1)</b> расстояние: aequo discrimine <i>V</i> на равном расстоянии || промежуток, просвет (dentium <i>Q</i>); музыкальный интервал: septem discrimina vocum <i>V</i> семиголосая (семиструнная) лира; duo maria pertenui discrimine separantur <i>C</i> оба моря разделены узким перешейком; d. comae (crinis) <i>O</i> пробор;\n<b>2)</b> разница, различие (d. recti pravique <i>Q</i>; sine ullo sexus discrimine <i>Su</i>): haud in magno ponere discrimine <i>L</i> не придавать большого значения; parvi discriminis umbrae <i>O</i> близкие друг к другу (лёгкие) оттенки; discriminum tenuitates <i>AG</i> тонкие нюансы;\n<b>3)</b> сила распознавания, способность различать (non est d. in vulgo <i>C</i>);\n<b>4)</b> решающий момент, решение (res venit in d. <i>QC</i>; agere <i>Lcr</i> <i>или</i> deducere, <i>тж.</i> adducere rem in d. <i>C</i>): gladio permittere mundi d. <i>Lcn</i> предоставить мечу решить судьбу мира; in discrimine est humanum genus, utrum vos an Carthaginienses principes orbis terrarum videat <i>L</i> человечество стоит перед решающим вопросом, будет ли принадлежать господство над миром вам или карфагенянам; ad ipsum d. ejus temporis <i>C</i> как раз в самый решающий момент;\n<b>5)</b> критическое положение, опасность (d. improvisum evasisse <i>T</i>): esse in summo (ultimo) discrimine <i>C</i> находиться в величайшей опасности; in ultimo discrimine vitae esse <i>L</i> находиться при последнем издыхании; adduci in d. alicujus rei <i>C</i> подвергаться опасности лишиться чего-л.; d. periculi <i>L</i> критическая (опасная) минута; omnium rerum certamen et d. <i>C</i> потрясение всех основ, всеобщая смута; capitis discrimine <i>J</i> рискуя головой;\n<b>6)</b> решительный бой (d. vehemens <i>QC</i>): res versata (est) in d. <i>L</i> дело решается оружием (сражением); discrimine nullo perire <i>Lcn</i> погибнуть, не вступив в бой;\n<b>7)</b> решающее значение, важность: consilia magni discriminis <i>C</i> планы большого значения;\n<b>8)</b> испытание, проба: experiri discrimine aperto <i>O</i> проверить на открытом опыте."],
  ["discriminale", "<big>discriminale</big>\n\n<b>discrīmināle, is</b> <i>n</i>\nзаколка для волос <i>Hier</i>."],
  ["discriminalis", "<big>discriminalis</big>\n\n<b>discrīminālis, e</b> [discrimen]\nслужащий для разделения (и укрепления причёски) (acus <i>Hier</i>)."],
  ["discriminatim", "<big>discriminatim</big>\n\n<b>discrīminātim</b> [discrimen]\nотдельно, раздельно, обособленно <i>Vr</i>."],
  ["discriminatio", "<big>discriminatio</big>\n\n<b>discrīminātio, ōnis</b> <i>f</i> [discrimino]\nобособление, различение <i>Vr</i>."],
  ["discriminator", "<big>discriminator</big>\n\n<b>discrīminātor, ōris</b> <i>m</i> [discrimen]\nразличающий, разделяющий <i>Aug</i>."],
  ["discrimino", "<big>discrimino</big>\n\n<b>dis–crīmino, āvī, ātum, āre</b>\nотделять (similia inter se <i>Sen</i>); разделять, рассекать (Etruriam discriminat via Cassia <i>C</i>): d. capillum <i>Ap</i> расчёсывать волосы на пробор."],
  ["discriptio", "<big>discriptio</big>\n\n<b>discrīptio, ōnis</b> <i>f</i>\n<b>1)</b> разделение (civitatis <i>C</i>);\n<b>2)</b> <i>v. l.</i> = descriptio."],
  ["discruciatus", "<big>discruciatus</big>\n\n<b>discruciātus, ūs</b> <i>m</i> [discrucio]\nмучение, пытка <i>Eccl</i>."],
  ["discrucio", "<big>discrucio</big>\n\n<b>dis–crucio, āvī, ātum, āre</b>\nсильно мучить, истязать (aliquem <i>C, Sen, Su, Ap</i>): discrucior <i>C</i> <i>и</i> discrucior animi <i>Ter</i> мне в высшей степени тяжело, я сильно страдаю."],
  ["discubitio", "<big>discubitio</big>\n\n<b>discubitio, ōnis</b> <i>f</i>\nложе, <i>по др.</i> столовая с застольными ложами <i>Hier</i>."],
  ["discubitus", "<big>discubitus</big>\n\n<b>discubitus, (ūs)</b> <i>m</i> [discumbo]\nзанятие места (ложа) за столом <i>VM</i>."],
  ["disculciatus", "<big>disculciatus</big>\n\n<b>disculciātus</b> <i>Vlg v. l.</i>\n= discalceatus."],
  ["discumbo", "<big>discumbo</big>\n\n<b>dis–cumbo, cubuī, cubitum, ere</b> [cubo; <i>ср.</i> accumbo <i>и др.</i>]\nложиться (каждому на своё место), <i>т. е.</i> занимать свое место за столом: discumbentes <i>Pt</i> возлежащие (за столом), <i>т. е.</i> сотрапезники; jussi discubuimus <i>Pt</i> следуя приглашению, мы расположились за столом || возлежать (ad cenandum <i>Sen</i>; in convivio <i>Su</i> <i>и</i> convivio <i>Treb</i>; in herba <i>Tib</i>; supra aliquem <i>Pt</i>): discubitum noctu ire <i>Pl</i> отправиться спать; discumbitur <i>V, C etc.</i> располагаются (вокруг стола)."],
  ["discuneatus", "<big>discuneatus</big>\n\n<b>dis–cuneātus, a, um</b> [cuneo]\nрасколотый, раскрытый (concha <i>PM</i>)."],
  ["discupio", "<big>discupio</big>\n\n<b>dis–cupio, pīvī, pītum, ere</b>\nсильно желать (dicere <i>Pl</i>; aliquem videre <i>C</i>)."],
  ["discurri", "<big>discurri</big>\n\n<b>dis(cu)currī</b> <i>pf. к</i>\ndiscurro."],
  ["discurro", "<big>discurro</big>\n\n<b>dis–curro, (cu)currī, cursum, ere</b>\n<b>1)</b> бегать туда и сюда, бегать в разные стороны, разбегаться (d. in muris <i>Cs</i>; ad arma <i>L</i>; huc atque illuc <i>Pt</i>): d. in cornua <i>L, QC</i> бегом занять места на флангах;\n<b>2)</b> растекаться, распадаться (Nilus discurrit in septem ora <i>V</i>); разделяться (in tribus ad suffragium ferendum <i>L</i>);\n<b>3)</b> распространяться (fama discurrit tota urbe <i>QC</i> <i>и</i> per orbem <i>Lcn</i>);\n<b>4)</b> рассказывать, излагать (super aliquid pauca d. <i>Amm</i>)."],
  ["discursatio", "<big>discursatio</big>\n\n<b>discursātio, ōnis</b> <i>f</i> [discurso]\nбеготня, бегание туда и сюда, взад и вперёд (per urbem <i>Sen</i>; apium, avium <i>Lact</i>)."],
  ["discursator", "<big>discursator</big>\n\n<b>discursātor, ōris</b> <i>m</i> [discurso]\nсовершающий вылазки, беспокоящий противника (hostis <i>Amm</i>)."],
  ["discursio", "<big>discursio</big>\n\n<b>discursio, ōnis</b> <i>f</i> [discurro]\n<b>1)</b> беспорядочная беготня <i>Lact</i>;\n<b>2)</b> <i>воен.</i> набег, стычка <i>Amm</i>;\n<b>3)</b> быстрый переход, марш (discursione rapida maturare <i>Amm</i>)."],
  ["discurso", "<big>discurso</big>\n\n<b>discurso, —, —, āre</b> [<i>intens. к</i> discurro]\n<b>1)</b> носиться взад и вперёд, метаться <i>Q, Sen, Fl, Amm</i>;\n<b>2)</b> быстро проходить (Gallias <i>Amm</i>)."],
  ["discursus", "<big>discursus</big>\n\n<b>discursus, ūs</b> <i>m</i> [discurro]\n<b>1)</b> бегание туда и сюда, беготня в разные стороны: discursu fugae se mandare <i>Hirt</i> обратиться в беспорядочное бегство; latis discursibus vallem implere <i>O</i> быстро передвигаться во все стороны по долине;\n<b>2)</b> <i>воен.</i> манёвр (subitus d. <i>T</i>);\n<b>3)</b> набег (hostilis <i>Amm</i>);\n<b>4)</b> движение, круговорот (stellarum <i>Sen</i>);\n<b>5)</b> беспрерывное мелькание (telorum <i>VM</i>);\n<b>6)</b> бестолковая беготня, суета (inanem discursum relinquere <i>PJ</i>);\n<b>7)</b> разрастание, разветвление (venarum, radicum <i>PM</i>);\n<b>8)</b> барахтанье: discursu ferri vario <i>O</i> метаться во все стороны, биться (<i>о пойманной рыбе</i>);\n<b>9)</b> беседа, разговор <i>CTh</i>."],
  ["discus", "<big>discus</big>\n\n<b>discus, ī</b> <i>m</i> (<i>греч.</i>)\n<b>1)</b> диск (<i>металлический или каменный метательный круг</i>) <i>Pl, H etc.</i>: discum audire <i>погов. C</i> слушать полёт диска (<i>т. е.</i> заниматься пустяками);\n<b>2)</b> круглая плита (солнечных часов), циферблат <i>Vtr</i>;\n<b>3)</b> круглое блюдо <i>Ap, Lampr etc.</i>"],
  ["discussio", "<big>discussio</big>\n\n<b>discussio, ōnis</b> <i>f</i> [discutio]\n<b>1)</b> сотрясение <i>Sen</i>;\n<b>2)</b> исследование, рассмотрение <i>Macr</i>;\n<b>3)</b> выведение из организма: d. seminis <i>CA</i> истечение семени;\n<b>4)</b> финансовая ревизия <i>CJ, Symm</i>."],
  ["discussor", "<big>discussor</big>\n\n<b>discussor, ōris</b> <i>m</i> [discutio]\n<b>1)</b> исследователь, испытатель <i>Macr, Eccl</i>;\n<b>2)</b> финансовый ревизор <i>CJ, Eccl</i>."],
  ["discussorius", "<big>discussorius</big>\n\n<b>discussōrius, a, um</b>\nразделяющий, разлагающий (vis <i>PM</i>)."],
  ["discutio", "<big>discutio</big>\n\n<b>dis–cutio, cussī, cussum, ere</b> [quatio]\n<b>1)</b> разбивать, раздроблять, громить, разрушать (arietibus murum <i>L</i>); размозжить (ossa saxo <i>O</i>): discussis claustris <i>Pt</i> сломав запоры;\n<b>2)</b> разгонять, рассеивать (hostiles turmas <i>Amm</i>; caliginem <i>L, QC</i>; tenebras <i>Lcn, O</i>; nubem <i>O</i>; somnum <i>Prp etc.</i>): ebrietate discussa <i>Pt</i> когда хмель прошёл || уничтожать, устранять (morbum <i>CC</i>; disceptationem, periculum <i>L</i>); подавлять (seditionem <i>VP</i>): res est discussa <i>L</i> дело не состоялось; risum d. <i>Pt</i> дать знак, чтобы перестали смеяться; expectatione discussa <i>Pt</i> положив конец ожиданию;\n<b>3)</b> расстраивать, опровергать (captiones alicujus <i>C</i>);\n<b>4)</b> <i>поздн.</i> исследовать, обсуждать (somnii verba <i>Macr</i>; quaestionem <i>Tert</i>)."],
  ["disdiapason", "<big>disdiapason</big>\n\n<b>disdiapāsōn</b> (<i>греч. gen. pl.</i>) <i>indecl.</i>\nдвойная октава (<i>музыкальная</i>) <i>Vtr</i>."],
  ["disdo", "<big>disdo</big>\n\n<b>dis–do</b> <i>арх. Cato</i>\n= dido I."],
  ["diserte", "<big>diserte</big>\n\n<b>disertē</b> [disertus]\n<b>1)</b> ясно, точно, обстоятельно (scriptum est <i>L</i>);\n<b>2)</b> красноречиво (dicere <i>C, Nep</i>)."],
  ["disertim", "<big>disertim</big>\n\n<b>disertim</b> <i>LA, Acc, Pl</i>\n= diserte 1."],
  ["disertitudo", "<big>disertitudo</big>\n\n<b>disertitūdo, inis</b> <i>f</i> [disertus]\nкрасноречие <i>Eccl</i>."],
  ["disertus", "<big>disertus</big>\n\n<b>disertus, a, um</b> [dissero II]\n<b>1)</b> ясный, обстоятельный, точный (verba, oratio, epistula <i>C etc.</i>);\n<b>2)</b> красноречивый (homo, orator <i>Ter, C, Ctl etc.</i>);\n<b>3)</b> искусный, сведущий (leporum <i>Ctl</i>)."],
  ["dishiasco", "<big>dishiasco</big>\n\n<b>dis–hiāsco, —, —, ere</b>\nрасседаться, раскрываться, раскалывать (arbores dishiascunt <i>Cato</i>)."],
  ["disicio", "<big>disicio</big>\n\n<b>disicio</b> <i>v. l.</i>\n= disjicio."],
  ["disjecto", "<big>disjecto</big>\n\n<b>disjecto, —, —, āre</b> [<i>intens. к</i> disjicio]\nразбросать, разметать (transtra <i>Lcr</i>); разгонять, рассеивать (conspiratas gentes <i>Amm</i>)."],
  ["disjectus", "<big>disjectus</big>\n\n<b>I disjectus, ūs</b> <i>m</i> [disjicio]\nразбросанность, рассеяние, беспорядок <i>Lcr</i>.\n\n<b>II disjectus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> disjicio;\n<b>2.</b> <i>adj.</i> разбросанный, рассеянный (aedificia <i>Cs</i>)."],
  ["disjicio", "<big>disjicio</big>\n\n<b>dis–jicio, jēcī, jectum, ere</b> [jacio]\n<b>1)</b> разбрасывать (disjecta membra <i>O</i>): d. pecuniam <i>VM</i> растратить деньги;\n<b>2)</b> разгонять, рассеивать (nubila <i>O</i>; naves <i>L</i>; phalangem hostium <i>Cs</i>): disjecta comas (<i>acc. gr.</i>) <i>O</i> с распущенными (развевающимися) волосами;\n<b>3)</b> разрубать, разрезать, раскалывать (aliquid securi <i>V</i>; machaera <i>Pl</i>);\n<b>4)</b> уничтожать, разрушать (arcem a fundamentis <i>Nep</i>; statuas <i>Su</i>; vestris disjecta lacertis Ilios <i>O</i>); нарушить, расстроить, сорвать (compositam pacem <i>V</i>; consilia <i>L</i>): d. exspectationem <i>Su</i> обмануть ожидание."],
  ["disjug-", "<big>disjug-</big>\n\n<b>disjug-</b> <i>v. l.</i>\n= dijug-."],
  ["disjuncte", "<big>disjuncte</big>\n\n<b>disjūnctē</b> [disjunctus]\nраздельно, отдельно, обособленно <i>Dig</i>; в форме альтернативы (quae disjunctius dicuntur <i>C</i>)."],
  ["disjunctim", "<big>disjunctim</big>\n\n<b>disjūnctim</b> <i>G, Dig</i>\n= disjuncte."],
  ["disjunctio", "<big>disjunctio</big>\n\n<b>disjūnctio, ōnis</b> <i>f</i> [disjungo]\n<b>1)</b> разобщение, обособление; <i>лог.</i> разделительное суждение <i>C</i>;\n<b>2)</b> расставание, разлука: d. alicujus <i>C</i> разлука с кем-л.; disjunctionem facere <i>C</i> расставаться;\n<b>3)</b> различие, несходство, расхождение (animorum <i>C</i>);\n<b>4)</b> <i>грам.</i> (= asyndeton) бессоюзное сочетание предложений (<i>напр.</i> populus Romanus Numantiam delevit, Carthaginem sustulit <i>C</i>);\n<b>5)</b> противопоставление <i>rhH, C</i>."],
  ["disjunctivus", "<big>disjunctivus</big>\n\n<b>disjūnctīvus, a, um</b> [disjungo]\nразделительный, содержащий противопоставление (proloquium <i>AG</i>; condiciones <i>Dig</i>)."],
  ["disjunctus", "<big>disjunctus</big>\n\n<b>disjūnctus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> disjungo;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> отдалённый (loca disjunctissima <i>C</i>);\n<b>2)</b> не находящийся в связи, отличный, противоположный (nihil est ab ea cogitatione disjunctius <i>C</i>; inter se d. discrepitansque <i>Lcr</i>);\n<b>3)</b> несвязный, отрывистый (oratio <i>C</i>)."],
  ["disjungo", "<big>disjungo</big>\n\n<b>dis–jungo (dījungo), jūnxī, jūnctum, ere</b>\n<b>1)</b> разобщать, отделять (flumen illud disjungit Cappadociam ab Armenia <i>L</i>);\n<b>2)</b> распрягать (bovem <i>Col</i>; jumenta <i>C</i>);\n<b>3)</b> отнять от материнской груди (agnos a mamma <i>Vr</i>);\n<b>4)</b> удалять, отклонять, отвлекать, отрывать (aliquem ab aliquo <i>Ter etc.</i>; ab alicujus amicitia <i>C</i>): d. populum a senatu <i>C</i> нарушить единство между народом и сенатом;\n<b>5)</b> проводить различие, различать, не смешивать (d. insaniam a furore <i>C</i>);\n<b>6)</b> <i>лог.</i> противопоставлять друг другу (duo, quae dijunguntur <i>AG</i>)."],
  ["dispalesco", "<big>dispalesco</big>\n\n<b>dispālēsco, —, —, ere</b> [<i>inchoat. к</i> dispalor]\nраспространяться, получать распространение <i>Pl</i>."],
  ["dispalor", "<big>dispalor</big>\n\n<b>dis–pālor, ātus (sum), ārī</b> <i>depon.</i>\nповсюду бродить, разбредаться (multitudo dispalata <i>Nep</i>)."],
  ["dispando", "<big>dispando</big>\n\n<b>dis–pando, (pandī), pānsum, ere</b>\nшироко расставлять, растопыривать (dispansa manus palma <i>Su</i>); широко распростирать, раскладывать (vestes dispansae in sole <i>Lcr</i>)."],
  ["dispar", "<big>dispar</big>\n\n<b>dis–pār, aris</b>\nнеравный, несходный, различный (inter se <i>C</i>; alicui <i>C</i>; dispares aliqua re <i>QC</i>): non d. sortis (<i>gen.</i>) <i>Sil</i> не более счастливый, которому не больше повезло || разной длины (gladii <i>L</i>); контрастирующий (colores <i>C</i>): haud disparibus verbis <i>T</i> такими же словами; in dispar <i>H</i> в отношении другого рода (вида)."],
  ["disparatio", "<big>disparatio</big>\n\n<b>disparātio, ōnis</b> <i>f</i> [disparo]\nотделение, выделение <i>Amm</i>: d. procreationis <i>Vtr</i> разрешение от бремени."],
  ["disparatum", "<big>disparatum</big>\n\n<b>disparātum, ī</b> <i>n</i> <i>рит.</i>\nпротивоположение <i>C, Q</i>."],
  ["dispare", "<big>dispare</big>\n\n<b>disparē</b> <i>adv.</i> [dispar]\nнесвоевременно, не в пору (pullare <i>Pt</i> — <i>v. l.</i>)."],
  ["dispareo", "<big>dispareo</big>\n\n<b>dis–pāreo, uī, —, ēre</b>\nисчезать <i>Eccl</i>."],
  ["dispargo", "<big>dispargo</big>\n\n<b>dispargo</b> <i>v. l.</i>\n= dispergo."],
  ["disparilis", "<big>disparilis</big>\n\n<b>disparilis, e</b> <i>Vr, C, Q, AG</i>\n= dispar."],
  ["disparilitas", "<big>disparilitas</big>\n\n<b>disparilitās, ātis</b> <i>f</i> [disparilis]\n<b>1)</b> неравенство, несходство, различие (rerum <i>AG</i>);\n<b>2)</b> <i>грам.</i> неправильность, отклонение от правила <i>Vr</i>."],
  ["dispariliter", "<big>dispariliter</big>\n\n<b>dispariliter</b> [disparilis]\n<b>1)</b> различно, по-разному <i>M</i>;\n<b>2)</b> <i>грам.</i> неправильно, с отклонением от правила <i>Vr</i>."],
  ["disparo", "<big>disparo</big>\n\n<b>dis–paro, āvī, ātum, āre</b>\nразделять, обособлять (seniores a junioribus <i>C</i>); рассылать в разные стороны (alium alia <i>Pl</i>): tot sententias in eandem rem d. <i>AG</i> осветить один и тот же вопрос со столь различных сторон."],
  ["dispartibilis", "<big>dispartibilis</big>\n\n<b>dispartibilis, e</b> [dispartio]\nделимый <i>Tert</i>."],
  ["dispartio", "<big>dispartio</big>\n\n<b>dispartio</b> <i>v. l.</i>\n= dispertio."],
  ["dispateo", "<big>dispateo</big>\n\n<b>dis–pateo, —, —, ēre</b>\nбыть со всех сторон открытым <i>Lact</i>."],
  ["dispectio", "<big>dispectio</big>\n\n<b>dispectio, ōnis</b> <i>f</i> <i>Tert</i>\n= dispectus I."],
  ["dispecto", "<big>dispecto</big>\n\n<b>dispecto, —, —, āre</b> [<i>intens. к</i> dispicio]\nтщательно рассматривать <i>Eccl</i>."],
  ["dispector", "<big>dispector</big>\n\n<b>dispector, ōris</b> <i>m</i> [dispecto]\nисследователь, наблюдатель (alienarum sententiarum <i>Tert</i>)."],
  ["dispectus", "<big>dispectus</big>\n\n<b>I dispectus, ūs</b> <i>m</i> [dispicio]\nвсестороннее рассмотрение <i>Sen</i>.\n\n<b>II dispectus, a, um</b> <i>v. l.</i>\n= despectus I.\n\n<b>III dis–pectus, a, um</b> [paciscor]\nрасторгнутый, расстроенный (nuptiae <i>Ap</i>)."],
  ["dispello", "<big>dispello</big>\n\n<b>dis–pello, pulī, pulsum, ere</b>\n<b>1)</b> разгонять, рассеивать (pecudes <i>C</i>; hostes <i>V, VF etc.</i>; umbras <i>V</i>; caliginem ab animo <i>C</i>; per undas nos <i>V</i>; omnes luctus omnesque e pectore curas <i>Sil</i>);\n<b>2)</b> рассекать (aequora <i>St</i>)."],
  ["dispendiosus", "<big>dispendiosus</big>\n\n<b>dispendiōsus, a, um</b> [dispendium]\nубыточный, вредный (cunctatio <i>Col</i>)."],
  ["dispendium", "<big>dispendium</big>\n\n<b>dis–pendium, ī</b> <i>m</i> [pendo]\n<b>1)</b> лишний расход, убыток (sine damno et dispendio <i>Pl</i>): d. facere <i>Lcr</i> понести убыток; dispendia viarum <i>M</i> длительные и трудные путешествия; dispendia silvae <i>St</i> непроходимые лесные дебри;\n<b>2)</b> убыль, ущерб (lunae senescentis dispendia <i>Ap</i>): dispendia morae <i>V</i> потеря времени."],
  ["dispendo", "<big>dispendo</big>\n\n<b>I dis–pendo, (pendī), pēnsum, ere</b>\n<b>1)</b> <i>v. l.</i> = dispando;\n<b>2)</b> развесить, взвесить (<i>на весах</i>) <i>Vr</i>;\n<b>3)</b> распределять, раздавать (panem <i>Eccl</i>).\n\n<b>II dispendo</b> <i>v. l.</i> <i>Pl</i>\n= dispenno."],
  ["dispenno", "<big>dispenno</big>\n\n<b>dispenno (dispendo), —, pessum, ere</b> <i>Pl, Lcr</i>\n= dispando."],
  ["dispensatio", "<big>dispensatio</big>\n\n<b>dispēnsātio, ōnis</b> <i>f</i> [dispenso]\n<b>1)</b> раздача, разделение, распределение, раскладка: d. inopiae <i>L</i> раздача незначительного запаса (<i>хлеба</i>);\n<b>2)</b> управление, заведование (d. aerarii <i>C</i>; pecuniae, annonae <i>L</i>);\n<b>3)</b> должность интенданта, казначея (d. regia <i>C</i>)."],
  ["dispensative", "<big>dispensative</big>\n\n<b>dispēnsātīvē</b> <i>adv.</i>\nпо-хозяйски <i>Hier</i>."],
  ["dispensativus", "<big>dispensativus</big>\n\n<b>dispēnsātīvus, a, um</b> [dispenso]\nхозяйственный <i>Is</i>."],
  ["dispensator", "<big>dispensator</big>\n\n<b>dispēnsātor, ōris</b> <i>m</i> [dispenso]\nуправитель, интендант, казначей <i>C, T, Pt</i>."],
  ["dispensatorius", "<big>dispensatorius</big>\n\n<b>dispēnsātōrius, a, um</b> <i>Eccl</i>\n= dispensativus."],
  ["dispensatrix", "<big>dispensatrix</big>\n\n<b>dispēnsātrīx, īcis</b> <i>f</i>\nзаведующая хозяйством <i>Hier</i>."],
  ["dispenso", "<big>dispenso</big>\n\n<b>dis–pēnso, āvī, ātum, āre</b> [<i>intens. к</i> dispendo]\n<b>1)</b> распределять, планировать, регулировать (annum <i>L</i>): d. candelae filum <i>J</i> обрезать (до надлежащей длины) фитиль свечи;\n<b>2)</b> раздавать (nummos <i>Pl</i>; oscula per natos <i>O</i>);\n<b>3)</b> распоряжаться, управлять, заведовать (d. res domesticas <i>C</i>; pecuniam <i>Nep</i>): d. victoriam <i>L</i> отдавать распоряжения в связи с одержанной победой."],
  ["dispercutio", "<big>dispercutio</big>\n\n<b>dis–percutio, —, —, ere</b>\nразбить, размозжить (cerebrum <i>Pl</i>)."],
  ["disperditio", "<big>disperditio</big>\n\n<b>disperditio, ōnis</b> <i>f</i> [disperdo]\nразорение, уничтожение, разрушение (urbis <i>C</i> — <i>v. l.</i>)."],
  ["disperdo", "<big>disperdo</big>\n\n<b>dis–perdo, didī, ditum, ere</b>\nразорять, уничтожать, губить (aliquem <i>Pl, C, O</i>; possessiones <i>C</i>); проматывать, расточать (rem <i>Pl</i>); портить (libellum <i>PJ</i>); коверкать (miserum carmen <i>V</i>)."],
  ["dispereo", "<big>dispereo</big>\n\n<b>dis–pereo, periī, —, īre</b>\nпогибать, гибнуть, пропадать <i>Vr, Lcr, C</i>: male partum male disperit <i>впоследствии погов.</i> <i>Pl</i> что дурно нажито, то дурно и прожито; dispeream (nisi...) <i>Ctl, H</i> пусть я погибну (если не...)."],
  ["dispergo", "<big>dispergo</big>\n\n<b>di–spergo, persī, persum, ere</b> [spargo]\n<b>1)</b> рассыпать, рассеивать, разбрасывать (membra per agros <i>Poeta ap. C</i>; milites dispersi <i>Cs</i>); разбрызгивать (cerebrum <i>Ter</i>): dispersum bellum <i>C</i> война, ведущаяся в разных местах; dispersa capillos (<i>acc. gr.</i>) <i>Lcn</i> с развевающимися волосами; d. vitam in auras <i>V</i> испускать дух, умереть || обрызгивать (viam cerebro <i>Ter</i>);\n<b>2)</b> разносить, распространять (falsos rumores <i>T</i>): (vulgus) disperserat <i>T</i> разнёсся слух в народе;\n<b>3)</b> разводить (vitem <i>Col</i>);\n<b>4)</b> разбивать (aries dispergit saxa <i>Lcn</i>);\n<b>5)</b> двигать во все стороны, размахивать (d. brachia et crura <i>CC</i>)."],
  ["disperno", "<big>disperno</big>\n\n<b>di–sperno, —, —, ere</b>\nпрезирать (jussa <i>Jvc</i>)."],
  ["disperse", "<big>disperse</big>\n\n<b>dispersē</b> [dispergo]\nрассеянно, разбросанно, там и сям <i>C</i>."],
  ["dispersim", "<big>dispersim</big>\n\n<b>dispersim</b> <i>Vr, Su</i>\n= disperse."],
  ["dispersio", "<big>dispersio</big>\n\n<b>dispersio, ōnis</b> <i>f</i> [dispergo]\n<b>1)</b> рассеяние (populi <i>Tert</i>; exercitus <i>Is</i>);\n<b>2)</b> уничтожение (urbis <i>C</i> — <i>v. l.</i>)."],
  ["dispersus", "<big>dispersus</big>\n\n<b>I dispersus, (ūs)</b> <i>m</i> [dispergo]\nрассеяние, разбросанность <i>C</i> (<i>v. l.</i>).\n\n<b>II dispersus, a, um</b> <i>part. pf. к</i>\ndispergo."],
  ["dispertio", "<big>dispertio</big>\n\n<b>dis–pertio (dispartio), īvī (iī), ītum, īre</b> [partio]\n<b>1)</b> распределять, размещать (exercitum per oppida <i>L</i>; tirones inter legiones <i>bAfr</i>); (рас)планировать (tempora voluptatis laborisque <i>C</i>);\n<b>2)</b> раздавать (pecuniam judicibus <i>C</i>; aliquid servis <i>Nep</i>);\n<b>3)</b> разбивать, делить(aliquid in quattuor genera <i>C</i>);\n<i>pass.</i> dispertior <i>Pl</i> разлучаться, расставаться."],
  ["dispertior", "<big>dispertior</big>\n\n<b>dispertior, —, īri</b> <i>depon.</i> <i>C, L</i>\n= dispertio."],
  ["dispertitio", "<big>dispertitio</big>\n\n<b>dispertītio, ōnis</b> <i>f</i> [dispertio]\nраспределение, разделение <i>C</i> (<i>v. l.</i>), <i>Tert</i>."],
  ["dispesco", "<big>dispesco</big>\n\n<b>dis–pēsco, pēscui, pestum, ere</b> [pasco]\n<b>1)</b> разделять, разобщать, разъединять, обособлять (Africam ab Aethiopia <i>PM</i>; non multo pelagi ab insula dispesci <i>Ap</i>);\n<b>2)</b> нарушать, расстраивать (nuptias <i>Ap</i> — <i>v. l.</i>)."],
  ["dispessus", "<big>dispessus</big>\n\n<b>dispessus, a, um</b> <i>арх. Lcr part. pf. к</i>\ndispando."],
  ["dispicio", "<big>dispicio</big>\n\n<b>di–spicio, spēxī, spectum, ere</b> [*specio]\n<b>1)</b> открывать глаза: catuli, qui jam dispecturi sunt <i>C</i> щенята, которые вот-вот прозреют;\n<b>2)</b> увидать, узреть, разглядеть, усмотреть (rem <i>Lcr</i>);\n<b>3)</b> внимательно рассматривать, разбирать (res Romanas <i>C</i>; de aliqua re <i>G</i>);\n<b>4)</b> понимать, постигать (verum <i>C</i>): quid velit, non dispicio <i>C</i> не понимаю, чего он хочет."],
  ["displano", "<big>displano</big>\n\n<b>dis–plāno, —, —, āre</b>\nразровнять (rutro caput <i>Vr</i>)."],
  ["displicentia", "<big>displicentia</big>\n\n<b>displicentia, ae</b> <i>f</i> [displiceo]\n<b>1)</b> неудовольствие: taedium et d. sui <i>Sen</i> неудовлетворённость самим собой;\n<b>2)</b> (<i>тж.</i> d. corporis <i>CA</i>) недомогание <i>CA</i>."],
  ["displiceo", "<big>displiceo</big>\n\n<b>dis–pliceo, plicuī, plicitum, ēre</b> [placeo]\nне нравиться, быть неприятным (alicui <i>C etc.</i>): d. in frivolis <i>Ap</i> не понравиться несерьёзностью своих речей; d. sibi <i>C etc.</i> быть недовольным самим собой, быть в дурном настроении, <i>тж.</i> чувствовать недомогание; corpus displicens <i>Scr</i> больной организм; non displicet nobis (= mihi)... <i>Augustus ap. Su</i> я не прочь..."],
  ["displodo", "<big>displodo</big>\n\n<b>dis–plōdo, (plōsī), plōsum, ere</b> [plaudo]\n<b>1)</b> разрывать, взрывать, <i>pass.</i> лопаться (vesicula displosa repente <i>Lcr</i>; displosa sonat quantum vesica <i>H</i>);\n<b>2)</b> широко расставлять, раздвигать (pedes disploduntur <i>Vr</i>)."],
  ["displosus", "<big>displosus</big>\n\n<b>displōsus, a, um</b> <i>part. pf. к</i>\ndisplodo."],
  ["displuviatus", "<big>displuviatus</big>\n\n<b>dis–pluviātus, a, um</b> [pluvia]\nимеющий сток для дождевой воды (cavum aedium <i>Vtr</i>)."],
  ["dispolio", "<big>dispolio</big>\n\n<b>dīspolio, āvī, ātum, āre</b> [dis + spolio]\nграбить (aliquem <i>Sen</i>); расхищать (templum <i>Treb</i>). — <i>См. тж.</i> despolio."],
  ["dispono", "<big>dispono</big>\n\n<b>dis–pōno, posuī, positum, ere</b>\nрасставлять (pocula <i>O</i>; vigilias per urbem <i>L</i>); располагать, размещать (milites in muris, praesidia ad ripas <i>Cs</i>); распределять, приводить в порядок (libros Homeri <i>C</i>; comas <i>O</i>); выстраивать (aciem <i>QC</i>); устраивать, распределять, планировать (diem <i>Sen, PJ</i>; ordinem quaestionum <i>Q</i>): consilia in omnem fortunam d. <i>L</i> строить свои планы с учётом всяких возможностей."],
  ["disposite", "<big>disposite</big>\n\n<b>dispositē</b> [dispositus]\nв порядке, по плану, в надлежащей последовательности (accusare <i>C</i>, dicere <i>Q</i>)."],
  ["dispositio", "<big>dispositio</big>\n\n<b>dispositio, ōnis</b> <i>f</i> [dispono]\n<b>1)</b> расстановка, размещение, расположение, правильное распределение (columnarum, verborum <i>Vtr</i>); распорядок (vitae <i>Sen</i>; epularum <i>Ap</i>); военная организация (d. Romana <i>Capit</i>);\n<b>2)</b> распоряжение: d. ultima <i>CJ</i> = testamentum;\n<b>3)</b> установление (dispositiones dei <i>Eccl</i>)."],
  ["dispositor", "<big>dispositor</big>\n\n<b>dispositor, ōris</b> <i>m</i> [dispono]\nраспорядитель, устроитель (mundi <i>Sen</i>; rerum naturae <i>Lact</i>)."],
  ["dispositura", "<big>dispositura</big>\n\n<b>dispositūra, ae</b> <i>f</i> [dispono]\nпорядок, расположение, <i>pl.</i> обстоятельства <i>Lcr</i>."],
  ["dispositus", "<big>dispositus</big>\n\n<b>I dispositus, (ūs)</b> <i>m</i> [dispono]\nрасположение, устройство: dispositu provisuque civilium rerum peritus <i>T</i> опытный в устройстве и в предвидении государственных дел.\n\n<b>II dispositus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> dispono;\n<b>2.</b> <i>adj.</i> упорядоченный, расположенный, распределённый, построенный (agmina per sexum et aetatem disposita <i>T</i>): vir d. <i>PJ</i> человек, последовательно излагающий свои мысли; ex disposito <i>Sen</i> по порядку, <i>но</i> <i>Macr</i> согласно установлению, преднамеренно."],
  ["dispostus", "<big>dispostus</big>\n\n<b>dispostus</b> <i>Lcr</i>\n= dispositus II."],
  ["disposui", "<big>disposui</big>\n\n<b>disposuī</b> <i>pf. к</i>\ndispono."],
  ["dispudet", "<big>dispudet</big>\n\n<b>dis–pudet, uit, —, ēre</b> <i>impers.</i>\n(кому-л.) очень стыдно (alicujus rei <i>Ap</i>; aliquid memorare <i>Pl</i>)."],
  ["dispuli", "<big>dispuli</big>\n\n<b>dispulī</b> <i>pf. к</i>\ndispello."],
  ["dispulsus", "<big>dispulsus</big>\n\n<b>dispulsus, a, um</b> <i>part. pf. к</i>\ndispello."],
  ["dispulvero", "<big>dispulvero</big>\n\n<b>dis–pulvero, —, —, āre</b>\nраспылять, обращать в прах (montes <i>Naev</i>)."],
  ["dispunctio", "<big>dispunctio</big>\n\n<b>dispūnctio, ōnis</b> <i>f</i> [dispungo]\n<b>1)</b> проверка счетов, денежная ревизия <i>Dig</i>;\n<b>2)</b> испытание, рассмотрение (boni et mali operis <i>Tert</i>);\n<b>3)</b> завершение, конец (vitae <i>Tert</i>)."],
  ["dispunctor", "<big>dispunctor</big>\n\n<b>dispūnctor, ōris</b> <i>m</i> [dispungo]\nисследователь, судья (meritorum <i>Tert</i>)."],
  ["dispungo", "<big>dispungo</big>\n\n<b>dis–pungo, pūnxī, pūnctum, ere</b>\n<b>1)</b> размечать точками, <i>перен.</i> разделять, перемежать (intervalla negotiorum otio <i>VP</i>);\n<b>2)</b> разбирать, проверять (d. est conferre accepta et data <i>Dig</i>): d. rationes acceptorum et expensorum <i>Sen</i> проверять приходы и расходы (составлять баланс);\n<b>3)</b> подвергать проверке, мысленно пробегать (dies vitae suae <i>Sen</i>);\n<b>4)</b> заканчивать, завершать (ordinem coeptum <i>Tert</i>)."],
  ["disputabilis", "<big>disputabilis</big>\n\n<b>disputābilis, e</b> [disputo]\nподдающийся обсуждению, могущий быть предметом рассуждения (res d. in utramque partem <i>Sen</i>)."],
  ["disputatio", "<big>disputatio</big>\n\n<b>disputātio, ōnis</b> <i>f</i> [disputo]\n<b>1)</b> расчёт, рассмотрение (venire in disputationem <i>Col</i>);\n<b>2)</b> научное изыскание, учёный труд, рассуждение, исследование (alicujus rei <i>или</i> de re aliqua <i>C</i>);\n<b>3)</b> обсуждение (in utramque partem <i>Cs</i>)."],
  ["disputatiuncula", "<big>disputatiuncula</big>\n\n<b>disputātiuncula, ae</b> <i>f</i> [<i>demin. к</i> disputatio]\nнебольшое исследование (обсуждение) <i>Sen, AG, Eccl</i>."],
  ["disputativus", "<big>disputativus</big>\n\n<b>disputātīvus, a, um</b>\nотносящийся к обсуждению <i>или</i> спору (intentiones <i>Eccl</i>)."],
  ["disputator", "<big>disputator</big>\n\n<b>disputātor, ōris</b> <i>m</i> [disputo]\nучастник обсуждения, исследователь <i>C</i>: suae artis d. <i>VM</i> защитник своей теории."],
  ["disputatorie", "<big>disputatorie</big>\n\n<b>disputātōriē</b> <i>adv.</i>\nв порядке обсуждения (aliquid scribere <i>Sid</i>)."],
  ["disputatorius", "<big>disputatorius</big>\n\n<b>disputātōrius, a, um</b>\nкасающийся обсуждения: disputatoria ars <i>Aug</i> диалектика."],
  ["disputatrix", "<big>disputatrix</big>\n\n<b>disputātrīx, trīcis</b> <i>f</i>\n<b>1)</b> <i>f к</i> disputator <i>Q</i>;\n<b>2)</b> диалектика <i>Q</i>."],
  ["disputo", "<big>disputo</big>\n\n<b>dis–puto, āvī, ātum, āre</b>\n<b>1)</b> подсчитывать: d. rationem cum aliquo <i>Pl</i> рассчитаться с кем-л.;\n<b>2)</b> исследовать, истолковывать, рассуждать, спорить (multa de re aliqua <i>C</i>): d. in aliquam sententiam <i>C</i> говорить в защиту какого-л. мнения; d. ad aliquid <i>C</i> высказываться по какому-л. вопросу; aliquid alicui examussim d. <i>Pl</i> точно рассказать что-л. кому-л.; d. in nullam partem <i>C</i> не высказываться ни в чью пользу; d. in contrarias partes (<i>или</i> in utramque partem) <i>C</i> говорить за и против."],
  ["disquammo", "<big>disquammo</big>\n\n<b>di–squām(m)o, —, —, āre</b> [squama]\nснимать чешую, чистить (d. et exdorsare pisces <i>Ap</i>)."],
  ["disquiro", "<big>disquiro</big>\n\n<b>dis–quīro, —, —, ere</b> [quaero]\nразыскивать, исследовать <i>H, Cld, Lampr, Vlg</i>."],
  ["disquisitio", "<big>disquisitio</big>\n\n<b>disquīsītio, ōnis</b> <i>f</i> [disquiro]\nрозыск, расследование <i>C etc.</i>; исследование <i>rhH, L, T, Su</i>."],
  ["disrapio", "<big>disrapio</big>\n\n<b>disrapio</b> <i>v. l.</i>\n= dirapio."],
  ["disraro", "<big>disraro</big>\n\n<b>dis–rāro (dīrāro), —, ātum, āre</b>\n<b>1)</b> разрежать (arundinetum, vitem <i>Col</i>);\n<b>2)</b> разжижать, разбавлять (<i>sc.</i> cibum <i>CA</i>);\n<b>3)</b> вызывать испарину (motus dirarat <i>CA</i>)."],
  ["disrumpo", "<big>disrumpo</big>\n\n<b>disrumpo</b> <i>v. l.</i>\n= dirumpo."],
  ["dissaepio", "<big>dissaepio</big>\n\n<b>dis–saepio, saepsī, saeptum, īre</b>\nразгораживать, разобщать, отделять, обносить (aliquid tenui muro <i>C</i>; omnia certis limitibus <i>O</i>)."],
  ["dissaeptio", "<big>dissaeptio</big>\n\n<b>dissaeptio, ōnis</b> <i>f</i> [dissaepio]\nперегораживание, разобщение <i>Vtr</i>."],
  ["dissaeptum", "<big>dissaeptum</big>\n\n<b>dissaeptum, ī</b> <i>m</i> [dissaepio]\n<b>1)</b> перегородка (saxeum <i>Lcr</i>);\n<b>2)</b> грудобрюшная преграда, диафрагма <i>Macr</i>."],
  ["dissavior", "<big>dissavior</big>\n\n<b>dis–sāvior, —, ārī</b> <i>depon.</i>\nнежно целовать, расцеловать (aliquid <i>C</i>)."],
  ["disseco", "<big>disseco</big>\n\n<b>dis–seco, secuī, sectum, āre</b>\nрассекать, разрубать, разрезать (aliquem <i>и</i> aliquid <i>PM, Lampr, Ap, Su, AG</i>)."],
  ["disseminatio", "<big>disseminatio</big>\n\n<b>dissēminātio, ōnis</b> <i>f</i>\nсеяние, сев, <i>перен.</i> распространение (malevolorum <i>Ap</i>; euangelii <i>Tert</i>)."],
  ["dissemino", "<big>dissemino</big>\n\n<b>dis–sēmino, āvī, ātum, āre</b>\nрассеивать, рассыпать, <i>перен.</i> распространять (malum <i>C</i>; rumorem <i>Ap</i>)."],
  ["dissensio", "<big>dissensio</big>\n\n<b>dissēnsio, ōnis</b> <i>f</i> [dissentio]\n<b>1)</b> разногласие, несогласие, расхождение (inter homines de aliqua re <i>C</i>): quid sit animus, magna d. est <i>C</i> в вопросе о том, что такое дух, существуют большие разногласия;\n<b>2)</b> раздор, раскол, разлад, распря (dissensiones civiles <i>Cs</i>);\n<b>3)</b> противоречие, несоответствие (utilium cum honestis <i>C</i>)."],
  ["dissensus", "<big>dissensus</big>\n\n<b>dissēnsus, ūs</b> <i>m</i> <i>V, Cld, St</i>\n= dissensio."],
  ["dissentaneus", "<big>dissentaneus</big>\n\n<b>dissentāneus, a, um</b> [dissentio]\nнесогласный, несоответствующий, противоречащий (rei alicui <i>C</i>)."],
  ["dissentio", "<big>dissentio</big>\n\n<b>dis–sentio, sēnsī, sēnsum, īre</b>\n<b>1)</b> не согласовываться, расходиться во мнениях (cum <i>или</i> ab aliquo, inter se d. <i>C etc.</i>); быть несогласным, противоречить (a communi salute, a ceterarum gentium <i>C</i>; cum scripto <i>rhH</i>; orationi <i>Sen</i> <i>и</i> ab oratione <i>Q</i>): d. condicionibus <i>H</i> не соглашаться на условия; d. sibi in re aliqua <i>rhH</i> противоречить себе (быть непоследовательным) в чём-л.;\n<b>2)</b> жить в несогласии, в раздоре (acerrime d. <i>C</i>)."],
  ["dissep-", "<big>dissep-</big>\n\n<b>dissēp-</b> <i>v. l.</i>\n= dissaep-."],
  ["disserenascit", "<big>disserenascit</big>\n\n<b>dis–serēnāscit, āvit, —, ere</b> <i>impers.</i> [serenus]\n(небо) начинает проясняться, проясняется <i>L</i>."],
  ["disserenat", "<big>disserenat</big>\n\n<b>dis–serēnat, —, āre</b> <i>impers.</i> [serenus]\nстоит хорошая погода, ясно <i>PM</i>."],
  ["dissereno", "<big>dissereno</big>\n\n<b>dis–serēno, —, —, āre</b> [serenus]\nпрояснять, просветлять (oculis nostris nubilum <i>Aug</i>)."],
  ["dissero", "<big>dissero</big>\n\n<b>I dis–sero, sēvī</b> (<i>редко</i> <b>seruī</b> <i>ap. Macr</i>), <b>situm, ere</b>\nсеять, рассеивать (semina <i>Col</i>); сажать врозь (lactucam <i>Col</i>); рассаживать, расставлять (taleas <i>Cs</i>); <i>перен.</i> распространять (pars animae per totum dissita corpus <i>Lcr</i>).\n\n<b>II dis–sero, seruī, sertum, ere</b>\n<b>1)</b> разбирать, обсуждать: ut ista non disserantur <i>C</i> не входя в обсуждение этого || излагать (aliquid pluribus verbis <i>C</i>);\n<b>2)</b> подробно говорить, рассуждать (cum aliquo de aliqua re <i>C</i>): ratio disserendi <i>C</i> диалектика; seditiosa d. <i>T</i> произносить зажигательные речи (подстрекать к мятежу); disseritur inter eos <i>C</i> между ними происходит беседа."],
  ["disserpo", "<big>disserpo</big>\n\n<b>dis–serpo, —, —, ere</b>\nнезаметно разбрестись, расползаться, <i>перен.</i> распространяться (magno concussu late disserpunt tremores <i>Lcr</i>)."],
  ["dissertatio", "<big>dissertatio</big>\n\n<b>dissertātio, ōnis</b> <i>f</i> [dissero II]\nрассуждение, изыскание, доклад <i>AG</i>."],
  ["dissertio", "<big>dissertio</big>\n\n<b>dissertio, ōnis</b> <i>f</i> [dissero II]\n<b>1)</b> разложение, распад (juris humani <i>L</i> — <i>v. l.</i>);\n<b>2)</b> <i>AG, Eccl</i> = dissertatio."],
  ["disserto", "<big>disserto</big>\n\n<b>disserto, āvī, ātum, āre</b> [<i>intens. к</i> dissero II]\nобсуждать, рассуждать, развивать в форме доклада, обмениваться мнениями (d. aliquid cum aliquo <i>Cato</i>; de aliqua re <i>T</i>)."],
  ["dissertus", "<big>dissertus</big>\n\n<b>dissertus</b> <i>v. l.</i>\n= disertus."],
  ["disserui", "<big>disserui</big>\n\n<b>disseruī</b>\n<b>1)</b> <i>pf. к</i> dissero II <i>и</i>\n<b>2)</b> <i>ap. Macr</i> = dissevi."],
  ["dissevi", "<big>dissevi</big>\n\n<b>dissēvī</b> <i>pf. к</i>\ndissero I."],
  ["dissicio", "<big>dissicio</big>\n\n<b>dissicio</b> <i>Lcr v. l.</i>\n= disjicio."],
  ["dissico", "<big>dissico</big>\n\n<b>dissico</b> <i>v. l.</i>\n= disseco."],
  ["dissidentia", "<big>dissidentia</big>\n\n<b>dissidentia, ae</b> <i>f</i> [dissideo]\nразличие, противоположность, разлад, противоречивость (rerum <i>PM</i>)."],
  ["dissideo", "<big>dissideo</big>\n\n<b>dis–sideo, sēdī, (sessum), ēre</b> [sedeo]\n<b>1)</b> быть вдали, отстоять, находиться далеко (ab omni turba <i>Sen</i>; Eridano <i>Prp</i> — <i>dat.</i>): supercilia dissidentia <i>Q</i> широко расставленные брови;\n<b>2)</b> не согласоваться, быть несогласным, расходиться (inter se, cum <i>и</i> ab aliquo <i>C</i>; alicui <i>H</i>; verbis, non re <i>C</i>): d. in Arminium et Segestem <i>T</i> распасться на группировки Арминия и Сегеста; humiles laborant, ubi potentes dissident <i>Ph</i> плохо приходится простому народу, когда сильные заспорят между собою; animus dissidens <i>Pt</i> душевный разлад;\n<b>3)</b> отличаться (nostra non dissident a Peripateticis <i>C</i>); противоречить (scriptum dissidet a sententia <i>C</i>; sensus dissidentes <i>Nep</i>);\n<b>4)</b> восставать, бунтовать (dissidet miles <i>T</i>);\n<b>5)</b> сидеть неровно (toga dissidet impar <i>H</i>)."],
  ["dissidiosus", "<big>dissidiosus</big>\n\n<b>dissidiōsus, a, um</b> [dissidium]\nразделяющий (objex <i>Ambr</i>)."],
  ["dissidium", "<big>dissidium</big>\n\n<b>dissidium, ī</b> <i>n</i> [dissideo]\nразлад, раздор, раскол <i>Eccl</i>."],
  ["dissido", "<big>dissido</big>\n\n<b>dis–sīdo, sēdī, —, ere</b>\n<b>1)</b> разделяться, находиться (стоять лагерем) вдали друг от друга (d. partitis ante maniplis <i>Sil</i>);\n<b>2)</b> разойтись, рассориться (d. ab aliquo <i>C</i>)."],
  ["dissignatio", "<big>dissignatio</big>\n\n<b>dissīgnātio, ōnis</b> <i>f</i> <i>C v. l.</i>\n= designatio 3."],
  ["dissignator", "<big>dissignator</big>\n\n<b>dissīgnātor, ōris</b> <i>m</i>\nраспорядитель (<i>на зрелищах, похоронах и пр.</i>) <i>Pl, H</i>."],
  ["dissigno", "<big>dissigno</big>\n\n<b>dissīgno</b> <i>v. l.</i>\n= designo."],
  ["dissilio", "<big>dissilio</big>\n\n<b>dis–silio, siluī, (sultum), īre</b> [salio]\n<b>1)</b> отскакивать друг от друга (duo de concursu corpora dissiliunt <i>Lcr</i>);\n<b>2)</b> расседаться (haec loca dissiluisse ferunt <i>V</i>);\n<b>3)</b> расходиться, расторгаться (gratia fratrum dissiluit <i>H</i>);\n<b>4)</b> трескаться, лопаться, разрываться (dissilit uva <i>O</i>); разлетаться на куски (mucro dissiluit <i>V</i>): d. frigore <i>V, Pall</i> лопаться от колода; d. risu <i>Sen</i> надрываться от смеха; gaudimonio dissilio, quod te talem video <i>Pt</i> я вне себя от радости, что вижу тебя в таком состоянии."],
  ["dissimilis", "<big>dissimilis</big>\n\n<b>dis–similis, e</b>\nнепохожий, несходный, отличный (alicujus <i>или</i> alicui <i>C etc.</i>): dissimiles inter se aliqua re <i>T</i> <i>и</i> in aliqua re <i>C etc.</i> непохожие друг на друга в чём-л.; d. in aliquem <i>T</i> несходный с кем-л.; dissimillima natura esse <i>L</i> коренным образом отличаться друг от друга."],
  ["dissimiliter", "<big>dissimiliter</big>\n\n<b>dissimiliter</b> [dissimilis]\nнесходно, различно, по-разному <i>C, L, Sl, AG</i>: haud d. alicui rei <i>L</i> подобно чему-л."],
  ["dissimilitudo", "<big>dissimilitudo</big>\n\n<b>dissimilitūdo, inis</b> <i>f</i> [dissimilis]\nнесходство, различие (locorum <i>C</i>): dissimilitudinem habere cum <i>и</i> ab aliqua re <i>C</i> отличаться от чего-л."],
  ["dissimulabiliter", "<big>dissimulabiliter</big>\n\n<b>dissimulābiliter</b> [dissimulo]\nисподтишка, втихомолку, тайно <i>Pl</i>."],
  ["dissimulamentum", "<big>dissimulamentum</big>\n\n<b>dissimulāmentum, ī</b> <i>n</i> <i>Ap</i>\n= dissimulatio 2."],
  ["dissimulanter", "<big>dissimulanter</big>\n\n<b>dissimulanter</b> [dissimulo]\nскрытно, тайно, исподтишка (non aperte, sed d. <i>C</i>; d. et furtim <i>PJ</i>): non d. <i>C, Su, VM</i> явно, открыто, искренно."],
  ["dissimulantia", "<big>dissimulantia</big>\n\n<b>dissimulantia, ae</b> <i>f</i> <i>C</i>\n= dissimulatio 3."],
  ["dissimulatio", "<big>dissimulatio</big>\n\n<b>dissimulātio, ōnis</b> <i>f</i> [dissimulo]\n<b>1)</b> скрывание, сокрытие, утаивание: in dissimulationem sui <i>T</i> чтобы не быть узнанным;\n<b>2)</b> притворство, лицемерие (d. tollenda est <i>C</i>): per dissimulationem <i>Just</i> притворно;\n<b>3)</b> ирония (<i>в сократовском смысле: скрывание тех своих мнений, к которым, в результате беседы, должен самостоятельно прийти и собеседник</i>) <i>C, Q, AG</i>;\n<b>4)</b> невнимание, пренебрежение (aliquid dissimulatione et silentio praeterire <i>PJ</i>)."],
  ["dissimulator", "<big>dissimulator</big>\n\n<b>dissimulātor, ōris</b> <i>m</i> [dissimulo]\n<b>1)</b> скрывающий, утаивающий, укрыватель (alienae culpae <i>T</i>);\n<b>2)</b> притворщик, лицемер: cujus rei lubet simulator ac d. <i>Sl</i> мастер всяческого лицемерия и притворства."],
  ["dissimulo", "<big>dissimulo</big>\n\n<b>dis–simulo, āvī, ātum, āre</b>\n<b>1)</b> прятать (capillos <i>O</i>); не показывать вида, утаивать, скрывать (nomen suum <i>O</i>; originem <i>Su</i>): d. de conjuratione <i>Sl</i> скрывать своё участие в заговоре; d. se <i>O</i> прикидываться иным (не хотеть быть узнанным); Liris dissimulat cursum <i>Sil</i> (река) Лирис кажется неподвижной; ex dissimulato <i>Sen, VP</i> скрыто, незаметно; dissimulavi dolens <i>C</i> я не показал виду, что страдаю; d. aliquem <i>O</i> появляться под чужим видом;\n<b>2)</b> игнорировать, пренебрегать, не обращать внимания, оставлять без внимания (d. aliquam rem <i>и</i> aliquem <i>C etc.</i>): d. consulatum alicujus <i>T</i> пренебречь чьей-л. кандидатурой в консулы; d. consonantem <i>Q</i> не выговаривать (какого-л.) согласного звука."],
  ["dissipabilis", "<big>dissipabilis</big>\n\n<b>dissipābilis, e</b> [dissipo]\nлегко рассеивающийся, делимый, распространяющийся (aer d. est <i>C</i>)."],
  ["dissipatio", "<big>dissipatio</big>\n\n<b>dissipātio, ōnis</b> <i>f</i> [dissipo]\n<b>1)</b> рассеяние (civium <i>C</i>);\n<b>2)</b> разрушение, распад (corporum <i>C</i>);\n<b>3)</b> расхищение (praedae <i>C</i>);\n<b>4)</b> <i>ритор.</i> разложение, расчленение <i>C</i>."],
  ["dissipator", "<big>dissipator</big>\n\n<b>dissipātor, ōris</b> <i>m</i> [dissipo]\nразрушитель <i>Eccl</i>."],
  ["dissipatrix", "<big>dissipatrix</big>\n\n<b>dissipātrīx, īcis</b> <i>f</i>\nразрушительница <i>Aug</i>."],
  ["dissipatus", "<big>dissipatus</big>\n\n<b>dissipātus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> dissipo;\n<b>2.</b> <i>adj.</i> несвязный, лишённый надлежащей связи, беспорядочный (oratio <i>C</i>): orator d. <i>C</i> оратор, говорящий бессвязно, разбрасывающийся."],
  ["dissipo", "<big>dissipo</big>\n\n<b>dis–sipo, āvī, ātum, āre</b>\n<b>1)</b> рассыпать, разбрасывать (membra alicujus <i>C</i>); рассеивать, обращать в беспорядочное бегство (hostes <i>C</i>): dissipari in finitimas civitates <i>Hirt</i> разбегаться по соседним областям;\n<b>2)</b> разгонять, откинуть прочь (curas <i>H</i>);\n<b>3)</b> распространять (famam <i>C</i>): ignis se dissipat <i>L</i> огонь ширится; bellum dissipatum <i>L</i> война, ведущаяся в различных местах;\n<b>4)</b> <i>мед.</i> способствовать рассасыванию, рассасывать (medicamenta, quae humorem dissipant <i>CC</i>);\n<b>5)</b> разрушать, уничтожать (statuam <i>C</i>; tecta <i>L</i>);\n<b>6)</b> проматывать, расточать (d. patrimonium <i>C</i>; opes per luxum <i>T</i>)."],
  ["dissitus", "<big>dissitus</big>\n\n<b>dissitus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> dissero I;\n<b>2.</b> <i>adj.</i> отдалённый, далёкий (genera <i>Ap</i>); широко расставленный (femora <i>Ap</i>)."],
  ["dissociabilis", "<big>dissociabilis</big>\n\n<b>dissociābilis, e</b> [dissocio]\n<b>1)</b> несовместимый, несоединимый (res olim dissociabiles: principatus ac libertas <i>T</i>);\n<b>2)</b> разделяющий, разобщающий (Oceanus <i>H</i>)."],
  ["dissocialis", "<big>dissocialis</big>\n\n<b>dis–sociālis, e</b>\nотвергающий, отбрасывающий (d. alicui rei <i>Rut</i>)."],
  ["dissociatio", "<big>dissociatio</big>\n\n<b>dissociātio, ōnis</b> <i>f</i> [dissocio]\n<b>1)</b> разъединение, разделение (spiritus corporisque <i>T</i>);\n<b>2)</b> взаимное отвращение, антипатия (corporum <i>PM</i>)."],
  ["dissocio", "<big>dissocio</big>\n\n<b>dis–socio, āvī, ātum, āre</b>\n<b>1)</b> разъединять, разлучать, разделять (homines <i>C</i>; montes dissociantur valle <i>H</i>): d. animos alicujus <i>Nep</i> нарушить согласие (поселить рознь) между кем-л.; d. amicitias <i>C</i> порвать дружеские отношения;\n<b>2)</b> отделять (causam suam, legionem a legione <i>T</i>); отличать (disertos a doctis <i>C</i>)."],
  ["dissolubilis", "<big>dissolubilis</big>\n\n<b>dissolūbilis, e</b> [dissolvo]\nразложимый (omne animal dissolubile et dividuum <i>C</i>)."],
  ["dissolute", "<big>dissolute</big>\n\n<b>dissolūtē</b> [dissolutus]\n<b>1)</b> несвязно (dicere <i>C</i>);\n<b>2)</b> небрежно, беспорядочно (facere <i>C</i>); легкомысленно (aliquid vendere <i>C</i>);\n<b>3)</b> <i>ритор.</i> бессоюзно, без соединительных частиц <i>C</i>;\n<b>4)</b> распущенно, нескромно (non d. hilaris <i>Aug</i>)."],
  ["dissolutio", "<big>dissolutio</big>\n\n<b>dissolūtio, ōnis</b> <i>f</i> [dissolvo]\n<b>1)</b> разложение, разрушение, распад (d. naturae <i>C</i>); слабость, вялость (stomachi <i>PM</i>); расшатанность, поломка (navigii <i>T</i>);\n<b>2)</b> отмена, уничтожение (legum <i>C</i>);\n<b>3)</b> опровержение (criminum <i>C</i>);\n<b>4)</b> небрежность, нерадение, расслабленность (remissio animi ac d. <i>C</i>);\n<b>5)</b> <i>ритор.</i> опущение соединительных частиц <i>C, Q</i>."],
  ["dissolutive", "<big>dissolutive</big>\n\n<b>dissolūtīvē</b>\nразрушительным образом <i>Boët</i>."],
  ["dissolutivus", "<big>dissolutivus</big>\n\n<b>dissolūtīvus, a, um</b>\nразрушительный <i>Boët</i>."],
  ["dissolutor", "<big>dissolutor</big>\n\n<b>dissolūtor, ōris</b> <i>m</i>\nразрушитель <i>CTh</i>."],
  ["dissolutrix", "<big>dissolutrix</big>\n\n<b>dissolūtrīx, īcis</b> <i>f</i>\nразрушительница: d. corporis <i>Tert</i> = mors."],
  ["dissolutum", "<big>dissolutum</big>\n\n<b>dissolūtum, ī</b> <i>n</i> <i>C, Q</i>\n= asyndeton."],
  ["dissolutus", "<big>dissolutus</big>\n\n<b>dissolūtus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> dissolvo;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> небрежный, нерадивый, легкомысленный (in tantis rei publicae periculis <i>C</i>; d. in re familiari <i>Q</i>);\n<b>2)</b> расшатанный (navigium <i>C</i>); расслабленный (stomachus <i>PM</i>);\n<b>3)</b> распутный, беспутный (adulescens, vita <i>C</i>);\n<b>4)</b> слишком вольный, развязный (comitas <i>Q</i>);\n<b>5)</b> <i>ритор.</i> дурно связанный, несвязный, беспорядочный, сумбурный (genus orationis dissolutum <i>Q</i>)."],
  ["dissolvo", "<big>dissolvo</big>\n\n<b>dis–solvo, solvī, solūtum, ere</b>\n<b>1)</b> развязывать (nodum <i>Lcr</i>); распускать (comas <i>Tib</i>);\n<b>2)</b> рассеивать (nubila solis calore <i>Lcr</i>; tenebras luce <i>Vr</i>); прогонять (frigus <i>H</i>);\n<b>3)</b> расшатывать (navem <i>Ph</i>); разрушать (pontem <i>Nep</i>);\n<b>4)</b> расторгать, прекращать (amicitiam, societatem <i>C</i>); отменять (leges, acta Caesaris <i>C</i>);\n<b>5)</b> распускать (collegia dissoluta <i>T</i>);\n<b>6)</b> разлагать (corpora <i>Lcr</i>); растопить (glaciem <i>Lcr</i>); плавить (aes <i>Lcr</i>); растворять (fel aqua dissolutum <i>PM</i>);\n<b>7)</b> раскалывать <i>или</i> распиливать (pinus gracili lamna <i>VF</i>);\n<b>8)</b> расслаблять (stomachum <i>PM</i>): risu ilia d. <i>Pt</i> смеяться до упаду;\n<b>9)</b> разрознить, разбросать (scopas <i>C</i>);\n<b>10)</b> разжижать (resinam oleo <i>PM</i>);\n<b>11)</b> опровергать (crimina, criminationem <i>C</i>);\n<b>12)</b> (раз)решать (interrogationes <i>C</i>; quaestiones <i>Spart</i>);\n<b>13)</b> освобождать (aliquem <i>Pl</i>; se <i>Ter</i>);\n<b>14)</b> лишать ритмической связанности (versum <i>H</i>; orationem <i>C</i>);\n<b>15)</b> уплачивать (pecuniam alicui <i>C</i>); оплатить (aes alienum <i>C</i>);\n<b>16)</b> ослаблять, смягчать (severitatem <i>C</i>);\n<b>17)</b> снимать, устранять (cuncta mala <i>Sl</i>; dolorem capitis <i>PM</i>)."],
  ["dissonanter", "<big>dissonanter</big>\n\n<b>dissonanter</b> [dissono]\nнесогласно, нестройно <i>Aug</i>."],
  ["dissonantia", "<big>dissonantia</big>\n\n<b>dissonantia, ae</b> <i>f</i>\nнестройность, диссонанс <i>Boët</i>."],
  ["dissono", "<big>dissono</big>\n\n<b>dis–sono, sonuī, sonitum, āre</b>\n<b>1)</b> нестройно звучать (loci dissonantes <i>Vtr</i>);\n<b>2)</b> не соответствовать, не согласоваться (ab aliqua re <i>Q, Amm</i> <i>и</i> alicui rei <i>Amm</i>)."],
  ["dissonus", "<big>dissonus</big>\n\n<b>dis–sonus, a, um</b>\n<b>1)</b> разноголосый, несогласный, нестройный (clamor <i>L, PJ</i>; voces <i>L</i>);\n<b>2)</b> различный, несхожий (gentes sermone et moribus dissonae <i>L</i>): dissona carmina <i>St</i> стихи различных размеров (<i>т. е.</i> в элегических двустишиях) || отличающийся (ab aliqua re <i>L</i>);\n<b>3)</b> враждебно настроенный (dissona corda <i>Sil</i>)."],
  ["dissors", "<big>dissors</big>\n\n<b>dis–sors, sortis</b> <i>adj.</i>\nимеющий иную участь, не имеющий ничего общего, чуждый: ab omni milite d. gloria <i>O</i> слава, отнюдь не похожая на воинскую."],
  ["dissuadeo", "<big>dissuadeo</big>\n\n<b>dis–suādeo, suāsī, suāsum, ēre</b>\nотсоветовать, отговаривать (alicui bellum <i>O</i>; d. alicui mori <i>SenT</i>); разубеждать, возражать, оспаривать (legem <i>C</i>; pacem <i>L</i>): d. de captivis <i>C</i> советовать (сенату) не отпускать пленных."],
  ["dissuasio", "<big>dissuasio</big>\n\n<b>dissuāsio, ōnis</b> <i>f</i> [dissuadeo]\nотговаривание (alicujus rei <i>C</i>; dissuasiones et adhortationes <i>Su</i>)."],
  ["dissuasor", "<big>dissuasor</big>\n\n<b>dissuāsor, ōris</b> <i>m</i> [dissuadeo]\nотговаривающий (alicujus rei <i>C, Sen</i>): d. legis <i>L</i> оратор, высказывающийся против законопроекта."],
  ["dissuavior", "<big>dissuavior</big>\n\n<b>dissuāvior, —, ārī</b> <i>depon.</i> <i>v. l.</i>\n= dissavior."],
  ["dissuetudo", "<big>dissuetudo</big>\n\n<b>dissuētūdo, inis</b> <i>f</i> <i>Ambr</i>\n= desuetudo."],
  ["dissulco", "<big>dissulco</big>\n\n<b>dissulco</b> <i>v. l.</i>\n= disulco."],
  ["dissulio", "<big>dissulio</big>\n\n<b>dissulio</b> <i>v. l.</i>\n= dissilio."],
  ["dissulto", "<big>dissulto</big>\n\n<b>dissulto, —, —, āre</b> [<i>intens. к</i> dissilio]\n<b>1)</b> разрываться, расходиться (dissultant ripae <i>V</i>);\n<b>2)</b> отскакивать (tela dissultant <i>Sen</i>); отражаться (aquae splendor dissultat per tecta <i>Sil</i>);\n<b>3)</b> распространяться, разноситься (crepitus dissultant <i>V</i>)."],
  ["dissuo", "<big>dissuo</big>\n\n<b>dis–suo, suī, ūtum, ere</b>\n<b>1)</b> раскрывать (sinum <i>O</i>);\n<b>2)</b> разевать (malas <i>Pers</i>);\n<b>3)</b> постепенно расторгать, прекращать (amicitias <i>C</i>)."],
  ["dissupo", "<big>dissupo</big>\n\n<b>dissupo</b> <i>арх.</i>\n= dissipo."],
  ["dissutus", "<big>dissutus</big>\n\n<b>dissūtus, a, um</b> <i>part. pf. к</i>\ndissuo."],
  ["distabesco", "<big>distabesco</big>\n\n<b>dis–tābēsco, tābuī, —, ere</b>\n<b>1)</b> таять, плавиться, растворяться (sal distabescit <i>Cato</i>);\n<b>2)</b> погрязать, коснеть (in iniquitates <i>Aug</i>)."],
  ["distaedet", "<big>distaedet</big>\n\n<b>dis–taedet, —, ēre</b> <i>impers.</i>\nнадоело, опостылело: haud tui me neque domi distaedet <i>Pl</i> мне быть здесь с тобой не наскучило; cum hoc ipso distaedet loqui <i>Ter</i> (мне) надоело с ним разговаривать."],
  ["distantia", "<big>distantia</big>\n\n<b>distantia, ae</b> <i>f</i> [disto]\n<b>1)</b> расстояние, промежуток (intervallorum <i>Vtr</i>);\n<b>2)</b> различие, разница (morum <i>C</i>; formarum <i>Lcr</i>)."],
  ["distendo", "<big>distendo</big>\n\n<b>dis–tendo, tendī, tentum (tēnsum), ere</b>\n<b>1)</b> растягивать (aciem <i>Cs</i>); распростереть (bracchia <i>O</i>); расширять, раздувать (ventrem <i>PM</i> — <i>ср.</i> 2);\n<b>2)</b> наполнять (lacte distenta ubera <i>V</i>; cellas nectare <i>V</i>); переполнять (ventrem <i>Pl</i> — <i>ср.</i> 1);\n<b>3)</b> разделять, отвлекать: d. copias hostium <i>L</i> отвлечь (распылить) неприятельские силы; d. curas in duo bella <i>L</i> раздвоить свои усилия между двумя войнами (<i>т. е.</i> воевать на два фронта); d. animum alicujus <i>L</i> смутить (привести в нерешительное состояние) кого-л.;\n<b>4)</b> пытать растягиванием (d. aliquem tormento <i>Su</i>). — <i>См. тж.</i> distentus I."],
  ["distennite", "<big>distennite</big>\n\n<b>distennite</b> <i>Pl</i>\n(<i>v. l.</i>)= distendite (<i>к</i> distendo)."],
  ["distensio", "<big>distensio</big>\n\n<b>distēnsio, ōnis</b> <i>f</i> [distendo]\nпротяжение (molis <i>Aug</i>)."],
  ["distensus", "<big>distensus</big>\n\n<b>distēnsus</b> <i>Tert</i>\n= distentus I."],
  ["distentio", "<big>distentio</big>\n\n<b>distentio, ōnis</b> <i>f</i> [distendo]\nрастягивание (membrorum <i>CC</i>); искривление (oris <i>CC</i>); судороги (nervorum <i>CC</i>)."],
  ["distentus", "<big>distentus</big>\n\n<b>I distentus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> distendo I;\n<b>2.</b> <i>adj.</i> полный, переполненный, набухший (distentae lacte capellae <i>V</i>): d. ac madens <i>Su</i> наевшийся до отвала и напившийся допьяна.\n\n<b>II distentus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> distineo;\n<b>2.</b> <i>adj.</i> занятый, заваленный, загруженный (tantis negotiis <i>C</i>): distentum tempus officiis <i>PJ</i> время, посвященное исполнению обязанностей.\n\n<b>III distentus, (ūs)</b> <i>m</i> [distentio]\nрасширение, растяжение (cutis <i>PM</i>)."],
  ["disterminator", "<big>disterminator</big>\n\n<b>disterminātor, ōris</b> <i>m</i> [distermino]\nразграничивающий (axis divisor et d. mundi <i>Ap</i>)."],
  ["distermino", "<big>distermino</big>\n\n<b>dis–termino, āvī, ātum, āre</b>\nразмежёвывать, разграничивать, разделять (stellas <i>C</i>; Hispanias Galliasque <i>PM</i>); отделять (Judaeam ab Aegypto <i>PM</i>)."],
  ["disterminus", "<big>disterminus</big>\n\n<b>dis–terminus, a, um</b>\nотделённый, разобщённый (latis terris <i>Sil</i>)."],
  ["disterno", "<big>disterno</big>\n\n<b>di–sterno, strāvī, strātum, ere</b>\nрасстилать, стлать (lectum <i>Ap</i>)."],
  ["distero", "<big>distero</big>\n\n<b>dis–tero, trīvī, —, ere</b>\nтолочь, растирать (caseum in mortario <i>Cato</i>); давить, душить (aliquem basiis <i>Pt</i>)."],
  ["distexo", "<big>distexo</big>\n\n<b>dis–texo, —, —, ere</b>\nнавивать основу (ткани) <i>Sid</i>."],
  ["distichon", "<big>distichon</big>\n\n<b>distichon, ī</b> <i>n</i> (<i>греч.</i>)\nдистих, элегическое двустишие (гексаметр и пентаметр) <i>Su, M</i>."],
  ["distichus", "<big>distichus</big>\n\n<b>distichus, a, um</b> (<i>греч.</i>)\nдвухрядный (hordeum <i>Col</i>)."],
  ["distill-", "<big>distill-</big>\n\n<b>distīll-</b> <i>v. l.</i>\n= destill-."],
  ["distimulo", "<big>distimulo</big>\n\n<b>distimulo, —, —, āre</b> [dis + stimulo]\nколоть, искалывать, <i>перен.-ирон.</i> расточать, растрачивать (bona <i>Pl</i>)."],
  ["distincte", "<big>distincte</big>\n\n<b>distīnctē</b> [distinctus I]\n<b>1)</b> раздельно, с расстановкой (dicere <i>C</i>);\n<b>2)</b> ясно, определённо, отчётливо (designare <i>PJ</i>; scribere <i>C</i>): d. concisa brevitas <i>C</i> сжатость в сочетании с ясностью."],
  ["distinctio", "<big>distinctio</big>\n\n<b>distīnctio, ōnis</b> <i>f</i> [distinguo]\n<b>1)</b> разделение (pugnantium sententiarum <i>PJ</i>);\n<b>2)</b> различение, распознавание (alicujus rei ab aliqua re <i>C</i>; harum rerum d. est facilis <i>C</i>);\n<b>3)</b> различие, разница (quae est d. inter illa? <i>C</i>): solis, lunae siderumque omnium d. <i>C</i> различные пути солнца, луны и всех звёзд;\n<b>4)</b> специфичность, отличительный признак, отличие (vocum <i>C</i>);\n<b>5)</b> краса, блеск, украшение (d. honosque civitatis <i>PM</i>);\n<b>6)</b> <i>ритор.</i> разделение, расчленение <i>C, Q</i>;\n<b>7)</b> <i>грам.</i> изменение слова по падежам и родам <i>C, Q</i>;\n<b>8)</b> <i>грам.</i> (<i>тж.</i> d. ultima) точка: d. media точка на уровне середины букв (<i>соотв. точке с запятой</i>)."],
  ["distinctor", "<big>distinctor</big>\n\n<b>distīnctor, ōris</b> <i>m</i>\nпроводящий различие, различающий (justorum injustorumque <i>Amm</i>)."],
  ["distinctus", "<big>distinctus</big>\n\n<b>I distīnctus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> distinguo;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> надлежащим образом распределённый, правильно распланированный (vitae genus <i>PJ</i>); ясный, определённый, упорядоченный (sermo <i>Q</i>; orator <i>T</i>);\n<b>2)</b> удалённый, разделённый: Hesiodus circa centum viginti annos d. ab Homeri aetate <i>VP</i> Гесиод, отделённый от Гомера промежутком около 120 лет;\n<b>3)</b> украшенный, усеянный (pocula gemmis distincta <i>C</i>): floribus distinctus <i>O</i> пестреющий цветами;\n<b>4)</b> расчленённый (acies <i>L</i>);\n<b>5)</b> разнообразный (genera delictorum <i>C</i>);\n<b>6)</b> выдающийся, высокий (gradus dignitatis <i>C</i>).\n\n<b>II distīnctus, (ūs)</b> <i>m</i>\nразличие, разнообразие (pinnarum <i>T</i>)."],
  ["distineo", "<big>distineo</big>\n\n<b>dis–tineo, tinuī, tentum, ere</b> [teneo]\n<b>1)</b> разделять, разобщать (flumen distinet legiones <i>Cs</i>; Isthmus distinet freta <i>O</i>): tigna distinebantur <i>Cs</i> сваи были расставлены на известном расстоянии друг от друга; duae sententiae distinebant senatum <i>L</i> мнения в сенате раскололись надвое;\n<b>2)</b> занимать, заваливать, загружать (aliquem multis negotiis <i>PJ</i>);\n<b>3)</b> заставить распылить (раздробить) свои силы (d. regem ancipiti proelio <i>L</i>; d. copias hostium <i>Cs</i>);\n<b>4)</b> приводить в замешательство, подавлять (aliquem dolore <i>C</i>);\n<b>5)</b> задерживать, мешать, замедлять, препятствовать (d. pacem <i>C</i>; distineri novis legibus <i>C</i>): distineor, quominus illud faciam <i>C</i> мне мешают это сделать; in bivio distineri <i>O</i> — <i>см.</i> bivium; d. aliquem a domo <i>H</i> мешать кому-л. вернуться домой."],
  ["distinguo", "<big>distinguo</big>\n\n<b>di–stinguo, stīnxī, stīnctum, ere</b>\n<b>1)</b> разделять, распределять (ubique amnium fluores, pratorum virores <i>Ap</i>);\n<b>2)</b> размечать: distinguente bulla notari <i>Pt</i> обозначаться различными (по цвету) шариками;\n<b>3)</b> отделять, расчленять (d. oratorum genera aetatibus <i>C</i>; d. versum <i>Q</i>); строго различать (vera a falsis <i>C</i>);\n<b>4)</b> расчёсывать, приводить в порядок (crinem <i>Sen</i>);\n<b>5)</b> заканчивать (cunctationem <i>Ap</i>);\n<b>6)</b> испещрять, усыпать, усеивать, украшать (caelum distinctum astris <i>C</i>; litora distincta tectis et urbibus <i>C</i>; autumnus distinguit racemos purpureo colore <i>H</i>);\n<b>7)</b> придавать разнообразие, разнообразить (historiam varietate locorum <i>C</i>);\n<b>8)</b> решать, улаживать (causas <i>Lampr, CJ</i>; jurgia <i>Amm</i>)."],
  ["disto", "<big>disto</big>\n\n<b>di–sto, distitī, —, āre</b>\n<b>1)</b> отстоять, находиться на расстоянии (pari spatio <i>O</i>, <i>но</i>: modicum spatium <i>L</i>): distantibus digitis <i>Ap</i> (перебирать струны) раздвинутыми пальцами || быть удалённым (ab aliquo loco <i>L</i>); находиться далеко (turres distant inter se <i>Cs</i>): sidera terra ut distant, sic utile recto <i>Lcn</i> от пользы до справедливости так же далеко, как от земли до звёзд;\n<b>2)</b> относиться к разным моментам, не совпадать во времени (d. tempore <i>O</i>; aetate <i>Q</i>): haud multum distanti tempore <i>T</i> немного времени спустя;\n<b>3)</b> разниться, быть различным, различаться (d. ab aliqua re <i>C etc.</i> <i>и</i> alicui rei <i>H</i>; illi distant inter se <i>C</i>): maledicus a malefico non distat nisi occasione <i>Q</i> только случай мешает человеку злоречивому стать злодеем; <i>impers.</i> distat имеется разница (clarus an obscurus, plurimum distat <i>Q</i>): stultitiane erret, nihilum distabit, an ira <i>H</i> неважно, будет ли он заблуждаться по глупости или в припадке гнева."],
  ["distorqueo", "<big>distorqueo</big>\n\n<b>dis–torqueo, torsī, tortum, ēre</b>\n<b>1)</b> выворачивать, искривлять, перекашивать (ora cachinno <i>O</i>): os sibi d. <i>Ter</i> гримасничать;\n<b>2)</b> вращать <i>или</i> закатывать (oculos <i>H</i>);\n<b>3)</b> пытать, мучить (aliquem <i>Sen, Su</i>): non diu cogitatione distorta <i>Pt</i> недолго поломав себе голову. — <i>См. тж.</i> distortus."],
  ["distorsio", "<big>distorsio</big>\n\n<b>distorsio, ōnis</b> <i>f</i> <i>Aug</i>\n= distortio."],
  ["distortio", "<big>distortio</big>\n\n<b>distortio, ōnis</b> <i>f</i> [distorqueo]\nискривление, выворачивание (membrorum <i>C, Aug</i>)."],
  ["distortor", "<big>distortor</big>\n\n<b>distortor, ōris</b> <i>m</i> [distorqueo]\nисказитель, извратитель (legum <i>Ter</i>)."],
  ["distortus", "<big>distortus</big>\n\n<b>distortus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> distorqueo;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> изуродованный, искривлённый (manus morbo distortae <i>Su</i>); горбатый (homo <i>Su</i>);\n<b>2)</b> исковерканный, превратный, извращённый (genus enuntiandi <i>C</i>);\n<b>3)</b> развращённый, распутный <i>Aug</i>."],
  ["distractio", "<big>distractio</big>\n\n<b>distractio, ōnis</b> <i>f</i> [distraho]\n<b>1)</b> расторжение, разъединение (animorum <i>C</i>; animi corporisque <i>Sen</i>);\n<b>2)</b> разлад, несогласие (d. nobis est cum tyrannis <i>C</i>);\n<b>3)</b> выворачивание (membrorum <i>AG</i>);\n<b>4)</b> нарушение (harum voluptalum <i>Pl</i>);\n<b>5)</b> распродажа <i>Dig</i>."],
  ["distractor", "<big>distractor</big>\n\n<b>distractor, ōris</b> <i>m</i> [distraho]\n<b>1)</b> разрывающий на части, <i>перен.</i> распродающий по частям <i>CJ</i>;\n<b>2)</b> (<i>тж.</i> d. argenti <i>CJ</i>) меняла <i>CJ</i>."],
  ["distractus", "<big>distractus</big>\n\n<b>I distractus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> distraho;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> раздробленный <i>Lcr</i>;\n<b>2)</b> занятый, поглощённый делом <i>VP</i>.\n\n<b>II distractus, ūs</b> <i>m</i>\nрасторжение договора <i>Dig</i>."],
  ["distraho", "<big>distraho</big>\n\n<b>dis–traho, trāxī, tractum, ere</b>\n<b>1)</b> тянуть, влечь в разные стороны (in diversa distrahi <i>L</i> — <i>о казни четвертованием</i>); раздроблять (corpus nullum, quod distrahi non possit <i>C</i>); отрывать, отторгать (de <i>и</i> a corpore <i>Lcr, Sen</i>); разделять, разъединять (Taurus mediam distrahens Asiam <i>PM</i>): d. voces <i>C</i> разделять гласные, <i>т. е.</i> допускать зияние (hiatus) || рассеивать, разбрасывать (fuga distraxit aliquos <i>C</i>); раскалывать (domum alicujus in partes <i>Su</i>; res publica distracta lacerataque <i>L</i>); распылять (oratoris industriam in plura studia <i>C</i>): Euphrates distrahitur in paludes <i>PM</i> Евфрат теряется в болотах; d. animum alicujus <i>C</i> заставить кого-л. колебаться; distrahi in contrarias partes <i>или</i> sententias <i>C</i> колебаться между противоположными мнениями, быть в нерешительности; in subtilitatem inutilem distrahi <i>Sen</i> уйти в ненужные тонкости, размениваться на мелочи;\n<b>2)</b> распродавать (bona venum <i>AG</i>; agros <i>T</i>; merces <i>CJ</i>);\n<b>3)</b> разрывать (aliquem equis <i>Vr, L</i>); разламывать (saxa <i>Sen</i>); <i>воен.</i> прорывать (aciem <i>Cs</i>); разметать (vallum <i>L</i>); растрепать (comam <i>O</i>); растерзывать (genas <i>O</i>); расторгать (matrimonium <i>Dig</i>); (по)рвать, разрушать (societatem <i>C</i>); распускать (collegia <i>Su</i>); расстраивать, (по)мешать (rem <i>Cs</i>); прекращать, улаживать (controversias <i>C</i>);\n<b>4)</b> (ра)ссорить (Caesarem et Pompejum <i>C</i>): distrahi cum aliquo <i>C</i> поссориться (порвать) с кем-л.; distrahi fama <i>T</i> быть обесславленным;\n<b>5)</b> отвлекать, отрывать, разлучать (aliquem ab aliquo <i>Ter, C</i>); d. aliquem a complexu suorum <i>C</i> вырвать кого-л. из объятий близких."],
  ["distribuo", "<big>distribuo</big>\n\n<b>dis–tribuo, tribuī, tribūtum, ere</b>\nразделять, разбивать (copias in tres partes <i>Cs</i>); распределять (d. naves legatis <i>Cs</i>); развёрстывать: d. frumentum civitatibus <i>C</i> разверстать поставку хлеба между общинами || раздавать (minutas summulas servis <i>Sen</i>); размещать (legiones provinciatim <i>Su</i>); расчленять (causam in crimen et in audaciam <i>C</i>)."],
  ["distribute", "<big>distribute</big>\n\n<b>distribūtē</b> [distribuo]\nв строгом порядке, организованно (scribere, tractare <i>C</i>)."],
  ["distributim", "<big>distributim</big>\n\n<b>distribūtim</b> <i>Boët</i>\n= distribute."],
  ["distributio", "<big>distributio</big>\n\n<b>distribūtio, ōnis</b> <i>f</i> [distribuo]\nразделение (caeli <i>C</i>); распределение (aquarum <i>Frontin</i>); расчленение (quadripertita d. totius accusationis <i>C</i>)."],
  ["distributor", "<big>distributor</big>\n\n<b>distribūtor, ōris</b> <i>m</i> [distribuo]\nраспределитель, податель (bonorum <i>Ap</i>)."],
  ["distributus", "<big>distributus</big>\n\n<b>distribūtus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> distribuo;\n<b>2.</b> <i>adj.</i> расчленённый, логически упорядоченный (expositio <i>C</i>)."],
  ["districte", "<big>districte</big>\n\n<b>districtē</b> (= destricte)\nстрого, сурово (minari <i>PJ</i>; vivere <i>Hier</i>)."],
  ["districtio", "<big>districtio</big>\n\n<b>districtio, ōnis</b> <i>f</i> [distringo]\n<b>1)</b> строгость, суровость <i>Eccl</i>;\n<b>2)</b> помеха, препятствие <i>Dig</i> (<i>v. l. к</i> distinctio)."],
  ["districtivus", "<big>districtivus</big>\n\n<b>districtīvus, a, um</b> [distringo] <i>мед.</i>\nразрешающий, расслабляющий (adjutoria <i>CA</i>)."],
  ["districtus", "<big>districtus</big>\n\n<b>districtus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> distringo;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> занятый (разными делами), поглощённый (contentione ancipiti <i>C</i>; bellis <i>Nep</i>);\n<b>2)</b> <i>v. l.</i> = destrictus."],
  ["distringo", "<big>distringo</big>\n\n<b>di–stringo, strīnxī, strictum, ere</b>\n<b>1)</b> растягивать, распяливать, распинать (aliquem radiis rotarum <i>V</i>): omni sollicitudine distringi <i>H</i> быть страшно озабоченным, сильно беспокоиться, терзаться;\n<b>2)</b> отвлекать, заставлять распылить свои силы (внимание) (distringit — <i>sc.</i> animum — librorum multitudo <i>Sen</i>): districtus mihi videris esse <i>C</i> мне кажется, что ты растерялся; Hannibalem in Africam mittere ad distringendos Romanos <i>L</i> послать Ганнибала в Африку для военной диверсии против римлян (для отвлечения римских сил);\n<b>3)</b> донимать, засыпать (Jovem votis <i>PJ</i>);\n<b>4)</b> обвивать, окружать (corona distringit verticem <i>Ap</i>): urbem incendiis d. <i>Fl</i> поджечь город со всех сторон."],
  ["distrivi", "<big>distrivi</big>\n\n<b>distrīvī</b> <i>pf. к</i>\ndistero."],
  ["distrunco", "<big>distrunco</big>\n\n<b>dis–trunco, —, —, āre</b>\nразрубать, разрезать (aliquem medium <i>Pl</i>)."],
  ["distuli", "<big>distuli</big>\n\n<b>distulī</b> <i>pf. к</i>\ndiffero."],
  ["disturbatio", "<big>disturbatio</big>\n\n<b>disturbātio, ōnis</b> <i>f</i> [disturbo]\nразрушение (Corinthi <i>C</i>)."],
  ["disturbo", "<big>disturbo</big>\n\n<b>dis–turbo, āvī, ātum, āre</b>\n<b>1)</b> разгонять, расстраивать, приводить в замешательство (contionem <i>C</i>): auster disturbat freta <i>Sen</i> австр волнует море; noli, obsecro, istum d. <i>VM</i> прошу тебя, не трогай этого (чертежа) (<i>предсмертные слова Архимеда римск. солдату</i>);\n<b>2)</b> разрушать (pontes <i>rhH</i>; domum <i>C</i>; opera <i>Cs</i>);\n<b>3)</b> нарушать, расторгать (nuptias <i>Ter</i>; legem, societatem <i>C</i>; concordiam <i>Sl</i>)."],
  ["disulco", "<big>disulco</big>\n\n<b>dī–sulco, —, —, āre</b>\nразрезать бороздой, врезаться (terram lucis penetratione d. <i>Aug</i>)."],
  ["disyllabon", "<big>disyllabon</big>\n\n<b>disyllabon, ī</b> <i>n</i> [disyllabus]\nдвусложное слово <i>LM</i>."],
  ["disyllabus", "<big>disyllabus</big>\n\n<b>disyllabus, a, um</b> (<i>греч.</i>)\nдвусложный <i>Q</i>."],
  ["ditator", "<big>ditator</big>\n\n<b>dītātor, ōris</b> <i>m</i> [dito]\nобогащающий (spiritus consolator et d. fidelium <i>Aug</i>)."],
  ["ditesco", "<big>ditesco</big>\n\n<b>dītēsco, —, —, ere</b> [dis, ditis]\nбогатеть, становиться богатым, обогащаться <i>H, Lcr, Pers etc.</i>"],
  ["dithalassus", "<big>dithalassus</big>\n\n<b>di–thalassus, a, um</b> (<i>греч.</i>; <i>лат.</i> bimaris)\nомываемый двумя морями (locus <i>Vlg</i>)."],
  ["dithyrambicus", "<big>dithyrambicus</big>\n\n<b>dīthyrambicus, a, um</b> [dithyrambus]\nдифирамбический: dithyrambicum poema <i>C</i> дифирамб."],
  ["dithyrambus", "<big>dithyrambus</big>\n\n<b>dīthyrambus, ī</b> <i>m</i> (<i>греч.</i>)\nдифирамб, возвышенный гимн (<i>первоначально в честь Вакха</i>) <i>C, H</i>."],
  ["ditiae", "<big>ditiae</big>\n\n<b>dītiae, ārum</b> <i>f</i> [<i>из</i> divitiae]\nбогатство <i>Ter, LM</i>: summis ditiis <i>Pl</i> из очень богатого дома."],
  ["ditio", "<big>ditio</big>\n\n<b>ditio, ōnis</b> <i>f</i> <i>неправ.</i>\n= dicio."],
  ["ditior", "<big>ditior</big>\n\n<b>dītior, dītissimus</b> <i>compar. и superl. к</i>\ndives."],
  ["ditis", "<big>ditis</big>\n\n<b>I dītis, e</b> (<i>или</i> <b>dīs</b>) <i>adj.</i>\nбогатый, изобильный (homo, domus <i>L</i>; solum <i>VF</i>): d. alicujus rei <i>St</i> <i>и</i> aliqua re <i>O, QC, L</i> богатый (изобилующий) чём-л.\n\n<b>II dītis, is</b> <i>m</i> [ditis I]\nбогач: dites et egentes <i>Ap</i> богачи и бедняки.\n\n<b>III Dītis</b> <i>Pt</i>\n= Dis."],
  ["ditius", "<big>ditius</big>\n\n<b>dītius</b> <i>и</i> <b>dītissimē</b> <i>St, Ap compar. и superl.</i>\n<i>к неупотр.</i> <i>adv.</i> *<b>dītē</b> богато."],
  ["dito", "<big>dito</big>\n\n<b>dīto, āvī, ātum, āre</b> [dis, dives]\n<b>1)</b> обогащать (aliquem aliqua re <i>L, VM etc.</i>; mare ditans Arabas et Indos <i>H</i>);\n<b>2)</b> развивать, усложнять, расширять (sermonem patrium <i>H</i>);\n<b>3)</b> одарять, осыпать (largifica stipe, <i>sc.</i> Idaeam matrem <i>Lcr</i>)."],
  ["ditrochaeus", "<big>ditrochaeus</big>\n\n<b>ditrochaeus, ī</b> <i>m</i> (<i>греч.</i>)\nдвойной трохей (стопа –U–U)."],
  ["diu", "<big>diu</big>\n\n<b>I diu</b> (<i>compar.</i> diutius, <i>superl.</i> diutissime) [<i>одного корня с</i> dudum]\n<b>1)</b> долго, в течение долгого времени (d. multumque <i>C</i>): parum d. <i>C</i> слишком мало времени, очень недолго; d. mori (perire) <i>Sen</i> медленно умирать; quam d. <i>Pt</i> (до тех пор,) пока;\n<b>2)</b> давно (jam d. <i>Pl etc.</i>): nec d. hic fui <i>Pl</i> я лишь недавно прибыл сюда.\n\n<b>II diū</b> <i>adv.</i> (<i>арх.</i> <b>dius</b>)\nднём <i>Man, Ap</i>: noctu (nocte) diuque <i>или</i> diu noctuque <i>Pl, Sl, T etc.</i> днём и ночью; neque noctu neque diu <i>Pl</i> ни ночью, ни днём.\n\n<b>III dīū</b> <i>арх. abl. к</i>\ndium."],
  ["dium", "<big>dium</big>\n\n<b></b>*<b>dīum, ī</b> <i>n</i> [dius] <i>арх. поэт.</i>\nнебо: sub dio <i>Col</i> <i>и</i> <i>арх.</i> sub diu <i>Pl, Lcr</i> под открытым небом."],
  ["diureticus", "<big>diureticus</big>\n\n<b>diūrēticus, a, um</b> (<i>греч.</i>; <i>лат.</i> mictorius <i>и</i> mictualis)\nмочегонный (vinum <i>Pall</i>; potio <i>Veg</i>)."],
  ["diurnarius", "<big>diurnarius</big>\n\n<b>diurnārius, ī</b> <i>m</i> [diurnus]\nиздающий «acta diurna», редактор дневника, журналист <i>CTh</i>."],
  ["diurno", "<big>diurno</big>\n\n<b>diurno, —, —, āre</b> [diurnus]\nдолго жить (inusitate «d.» dixit Q. Claudius pro «diu vivere» <i>AG</i>)."],
  ["diurnum", "<big>diurnum</big>\n\n<b>diurnum, ī</b> <i>n</i> [diurnus]\n<b>1)</b> (<i>sc.</i> frumentum) однодневный паёк <i>Sen</i>;\n<b>2)</b> (<i>sc.</i> commentariolum) дневник, журнал <i>J, Is</i>;\n<b>3)</b> <i>pl.</i> (<i>sc.</i> acta populi) официальные известия, хроника <i>T</i>;\n<b>4)</b> <i>pl.</i> (= dies) дни: noctibus, diurnis <i>CA</i> ночами и днями;\n<b>5)</b> <i>pl.</i> повседневные потребности <i>Eccl</i>."],
  ["diurnus", "<big>diurnus</big>\n\n<b>diurnus, a, um</b> [<i>одного корня с</i> dies]\nдневной (vestimentum <i>Vr</i>; spatium, labores <i>C</i>; lumen <i>Lcr, Sen, O</i> <i>и</i> lux <i>Lcr</i>); однодневный (opus <i>C</i>; actus <i>Su</i>); ежедневный, повседневный (cibus <i>L</i>; victus <i>Su</i>): diurna acta <i>T H</i> diurni commentarii <i>Su</i> ежедневные известия (<i>род официального бюллетеня</i>); d. currus <i>O</i> колесница Аполлона; diurna stella <i>Pl</i> утренняя звезда."],
  ["dius", "<big>dius</big>\n\n<b>I diūs</b> <i>adv.</i> <i>арх.</i>\n= diu II.\n\n<b>II dīus, a, um</b> [<i>арх.</i> <i>из</i> divus]\n<b>1)</b> божественный, <i>перен.</i> великий <i>или</i> благородный (dia Camilla <i>V</i>); божественно-прекрасный (dia sententia <i>H</i>);\n<b>2)</b> огромный (dium profundum <i>O</i>)."],
  ["diuscule", "<big>diuscule</big>\n\n<b>diūsculē</b> <i>adv.</i> <i>Aug</i>\n= diutule."],
  ["diutine", "<big>diutine</big>\n\n<b>diūtinē</b> [diutinus]\nдолго <i>Pl, Ap</i>."],
  ["diutino", "<big>diutino</big>\n\n<b>diūtinō</b> <i>Ap v. l.</i>\n= diutine."],
  ["diutinus", "<big>diutinus</big>\n\n<b>diūtinus, a, um</b> [diu 1]\nпродолжительный, долговременный, слишком долгий (supplicium <i>Pl</i>; labor <i>Cs</i>); затяжной (bellum <i>L</i>); застарелый, давнишний, хронический (aeger <i>Sen</i>)."],
  ["diutius", "<big>diutius</big>\n\n<b>diūtius</b> <i>compar. к</i>\ndiu I."],
  ["diutule", "<big>diutule</big>\n\n<b>diūtulē</b> [<i>demin. к</i> diu I]\nдовольно долго (tacere <i>Ap</i>); немного (времени) <i>Macr</i>."],
  ["diuturne", "<big>diuturne</big>\n\n<b>diūturnē</b> [diuturnus]\nдолго, продолжительно <i>Sid, Boët</i>."],
  ["diuturnitas", "<big>diuturnitas</big>\n\n<b>diūturnitās, ātis</b> <i>f</i> [diuturnus]\nпродолжительность, долговременность, длительность (belli <i>Cs</i>; pacis <i>C</i>); долговечность (alicujus <i>VM</i>)."],
  ["diuturnus", "<big>diuturnus</big>\n\n<b>diūturnus, a, um</b> (<i>поэт. иногда O \n </i><b>diŭturnus</b>)<b></b> [diu I]\nпродолжительный, длительный, долгий, затяжной (bellum <i>C, Sl</i>; obsidio <i>O</i>); многолетний, <i>т. е.</i> опытный (dux <i>Amm</i>); долговечный: sit nostris diuturnior annis <i>O</i> пусть проживёт она дольше, чем я."],
  ["diva", "<big>diva</big>\n\n<b>dīva, ae</b> <i>f</i> [divus]\nбогиня <i>L, Ctl, V etc.</i>; <i>перен.</i> возлюбленная (mea d. <i>Ctl</i>)."],
  ["divagor", "<big>divagor</big>\n\n<b>dī–vagor, —, ārī</b>\nстранствовать, блуждать <i>CTh, Lact</i>."],
  ["divalis", "<big>divalis</big>\n\n<b>dīvālis, e</b> [divus]\n<b>1)</b> божественный <i>Spart</i>;\n<b>2)</b> императорский <i>CJ</i>."],
  ["divarico", "<big>divarico</big>\n\n<b>dī–vārico, āvī, ātum, āre</b>\n<b>1)</b> раздвигать, растягивать (taleas super terram <i>Cato</i>; crura alicui <i>Hier</i>); aliquem divaricari jubere <i>C</i> велеть посадить верхом кого-л.;\n<b>2)</b> раздвигаться (ungulae divaricant <i>Vr</i>)."],
  ["divasto", "<big>divasto</big>\n\n<b>dī–vāsto, —, —, āre</b>\nсовершенно разрушать, истреблять (rapta <i>Eccl</i>)."],
  ["divello", "<big>divello</big>\n\n<b>dī–vello, vellī (vulsī), vulsum (volsum), ere</b>\n<b>1)</b> разрывать (corpus <i>V</i>): d. vulnus <i>bAfr</i> сорвать повязку с раны; divelli dolore <i>C</i> терзаться скорбью;\n<b>2)</b> расторгать (affinitatem <i>C</i>); прерывать, нарушать (somnum <i>H</i>; amicitiam <i>Sen</i>; amor divulsus querimoniis <i>H</i>);\n<b>3)</b> отрывать, отторгать (divelli a suis <i>C</i>; d. ramum trunco <i>O</i>);\n<b>4)</b> отклонять, отучать (d. aliquem ab otio <i>C</i>)."],
  ["divendo", "<big>divendo</big>\n\n<b>dī–vēndo, (didī), ditum, ere</b>\nпродавать порознь, распродавать по частям (bona <i>C, T</i>; praedam <i>L</i>)."],
  ["diventilo", "<big>diventilo</big>\n\n<b>dī–ventilo, —, ātum, āre</b>\nразносить ветром, <i>перен.</i> распространять (diventilatae in vulgus opiniones <i>Tert</i>)."],
  ["diverbero", "<big>diverbero</big>\n\n<b>dī–verbero, āvī, ātum, āre</b>\n<b>1)</b> разбивать, разрубать, разрывать (terram <i>Sen</i>; ubera vehementer <i>Ap</i>; umbram noctis <i>V</i>); развеять (vento diverberari <i>Aug</i>);\n<b>2)</b> избить, отколотить (aliquem arundine <i>Tert</i>)."],
  ["diverbium", "<big>diverbium</big>\n\n<b>dīverbium, ī</b> <i>n</i> [dis + verbum]\nразговор, диалог <i>L</i>, <i>Pl</i>."],
  ["diverse", "<big>diverse</big>\n\n<b>dīversē</b> [diversus]\n<b>1)</b> в разные стороны, в противоположных направлениях (d. discedere <i>Nep</i>; tendere <i>Su</i>; animum trahere <i>Ter</i>): paulo diversius <i>Sl</i> в некотором отдалении друг от друга;\n<b>2)</b> в разных местах, там и сям (corpora d. jacebant <i>bAfr</i>);\n<b>3)</b> по-разному, различным образом, то так, то этак (dicere <i>C</i>);\n<b>4)</b> иначе: d. a tua sententia <i>AG</i> не так, как думаешь ты."],
  ["diversimode", "<big>diversimode</big>\n\n<b>dīversi–modē</b> <i>adv.</i> [modus]\nразличным образом <i>Aug</i>."],
  ["diversitas", "<big>diversitas</big>\n\n<b>dīversitās, ātis</b> <i>f</i> [diversus]\n<b>1)</b> противоположность, противоречие (d. naturae <i>T</i>; d. inter exercitum imperatoremque <i>T</i>); разногласие (auctorum <i>PM</i>);\n<b>2)</b> разница, различие, несходство (animorum <i>Sen, T</i>); многообразие (ciborum <i>Q</i>; linguarum <i>Aug</i>)."],
  ["diversitor", "<big>diversitor</big>\n\n<b>dīversitor</b> <i>v. l.</i>\n= deversitor."],
  ["diversor", "<big>diversor</big>\n\n<b>dīversor, ōris</b> <i>m</i> [diverto]\nприезжий, постоялец <i>C, Amm</i>."],
  ["diversorium", "<big>diversorium</big>\n\n<b>dīversōrium, ī</b> <i>n</i> <i>v. l.</i>\n= deversorium."],
  ["diversus", "<big>diversus</big>\n\n<b>dīversus, a, um</b> [diverto]\n<b>1)</b> обращенный в другую сторону, лежащий в другом направлении (itinera <i>Cs, Sl, T</i>; aditus <i>C</i>): diversi fugiunt (discedunt, abeunt) <i>L</i> они бегут (уходят) в разные стороны; omnes diversi audistis <i>Sl</i> каждый из всех вас в отдельности слышал (об этом); quo diversus abis? <i>V</i> отчего ты уходишь в сторону?; diversi flebant <i>O</i> они плакали, отвернувшись; diversi pugnant <i>Cs</i> (bellum gerunt <i>L</i>) они сражаются в различных местах (одни здесь, другие там); diversis legionibus <i>Cs</i> поскольку легионы сражались разрозненно; amnes diversis ex Alpibus decurrentes <i>L</i> реки, текущие с различных склонов Альп; morum d. <i>T</i> (человек) совсем другого характера;\n<b>2)</b> разный, различный (diversi homines <i>C</i>; d. ab aliquo <i>C etc.</i> <i>и</i> alicui rei <i>Cs, H etc.</i>): in diversum auctores trahunt <i>L</i> мнения авторов расходятся;\n<b>3)</b> противоположный (litus <i>QC</i>): d. rei alicui <i>C</i> противоположный чему-л.; diversa factio <i>Su</i> (pars <i>Just, Su</i>) противная сторона, враждебная группировка; diversa acies <i>воен. T</i> противник; in diversum <i>T</i> в (на) другую сторону; per diversa <i>T</i> исходя из противоположных оснований; ex diverso <i>VP etc.</i> с противоположной стороны <i>или</i> на противоположной стороне, <i>тж.</i> <i>Su etc.</i> напротив, наоборот: e diverso hostibus <i>Just</i> против неприятеля; diversus animi <i>T</i> колеблющийся; diversa simulatione <i>T</i> симулируя противоположные чувства, <i>т. е.</i> с притворной печалью; huic diversum vitio vitium <i>H</i> порок, прямо противоположный этому; metu et libidine diversus agitur <i>Sl</i> он колеблется между страхом и влечением; res diversissimae <i>Sl</i> совершенно противоположные, <i>т. е.</i> несовместимые вещи;\n<b>4)</b> поссорившийся, повздоривший (amantes <i>Prp</i>): componere diversos <i>T</i> примирить враждующие стороны;\n<b>5)</b> отдалённый (regio <i>L</i>): diverso terrarum <i>T</i> в далёких краях; proficisci in diversam provinciae partem <i>PJ</i> отправиться в отдалённую часть провинции."],
  ["diverticulum", "<big>diverticulum</big>\n\n<b>dīverticulum, ī</b> <i>n</i> <i>v. l.</i>\n= deverticulum."],
  ["divertium", "<big>divertium</big>\n\n<b>dīvertium, ī</b> <i>n</i> <i>v. l.</i>\n= divortium."],
  ["diverto", "<big>diverto</big>\n\n<b>dī–verto (dīvorto), vertī, versum, ere</b>\n<b>1)</b> расходиться, направляться в другую сторону: matrimonia divertentia <i>AG</i> расторгаемые браки, разводы || разводиться (ab aliquo <i>Dig</i>);\n<b>2)</b> отличаться, различаться (divortunt mores longe <i>Pl</i>);\n<b>3)</b> уносить (aliquid ad se <i>Vlg</i>)."],
  ["dives", "<big>dives</big>\n\n<b>dīves, itis</b> (<i>abl. sg.</i> e, <i>реже</i> i); <i>compar.</i> divitior, ditior, <i>superl.</i> divitissimus, ditissimus\n<b>1)</b> богатый (ex pauperrimo d. factus <i>C</i>): d. aliqua re <i>H, O etc.</i>, alicujus rei <i>V, Ap etc.</i>, <i>реже</i> ab aliqua re <i>VF</i> <i>и</i> in aliquid <i>Aug</i> богатый (изобилующий) чём-л. (agris <i>H</i>; pecoris <i>V</i>; opum variarum <i>V</i>) || изобильный (regio <i>QC</i>); роскошный, великолепный (mensa <i>H</i>; cultus <i>O</i>); прибыльный, выгодный (bellum <i>T</i>); плодоносный (ager <i>VF</i>); многоводный (Nilus <i>O</i>);\n<b>2)</b> многообещающий (epistula <i>O</i>);\n<b>3)</b> красноречивый (lingua <i>H</i>)."],
  ["divescor", "<big>divescor</big>\n\n<b>dīvescor, —, vescī</b> <i>St v. l.</i>\n= devescor."],
  ["divexo", "<big>divexo</big>\n\n<b>dī–vexo, āvī, —, āre</b>\n<b>1)</b> дёргать, рвать, попирать (reliquias alicujus <i>Pac ap. C</i>);\n<b>2)</b> разорять, опустошать (agros civium <i>C</i>);\n<b>3)</b> мучить, терзать, плохо обращаться (aliquem <i>Su, Lact</i>)."],
  ["divexus", "<big>divexus</big>\n\n<b>dī–vexus, a, um</b> [veho]\nпростёртый, возложенный (divexae alicui manus <i>Aug</i>)."],
  ["Diviciacus", "<big>Diviciacus</big>\n\n<b>Dīviciācus, ī</b> <i>m</i> <i>v. l.</i>\n= Divitiacus."],
  ["dividendus", "<big>dividendus</big>\n\n<b>dīvidendus, ī</b> <i>m</i> [divido] (<i>sc.</i> numerus) <i>мат.</i>\nделимое <i>Boët</i>."],
  ["dividia", "<big>dividia</big>\n\n<b>dīvidia, ae</b> <i>f</i> [divido]\n<b>1)</b> раздор, разлад <i>Acc</i>;\n<b>2)</b> забота, тревога, неприятность <i>Acc, Pl, Ap</i>."],
  ["divido", "<big>divido</big>\n\n<b>dīvido, vīsī, vīsum, ere</b> [<i>одного корня с</i> viduo]\n<b>1)</b> разделять, делить (aliquid in duas partes <i>C</i>; bona inter se <i>Nep</i>); распределять, раздавать (praedam militibus <i>Sl</i>; agros viritim civibus <i>C</i> <i>и</i> per veteranos <i>Su</i>); отделять (flumen Rhenus dividit agrum Helvetium a Germanis <i>Cs</i>): annum ex aequo d. <i>O</i> разделить год пополам; hoc iter ignavi dividimus <i>H</i> из лени мы прошли этот путь не сразу || отрубать, отсекать (divisa a corpore capita <i>L</i>);\n<b>2)</b> поселять раздор, разъединять: haec res concentum nostrum dividit <i>H</i> это нарушает наше согласие;\n<b>3)</b> проламывать (muros <i>V</i>); раскалывать (aliquid cuneis <i>PM</i>); разрубать (aliquem securi medium <i>H</i>): alio mentes, alio d. aures <i>Ctl</i> слушать одно, а думать о другом; animum nunc huc, nunc illuc dividit <i>V</i> (ему) на ум приходит то одно, то другое; susurri divisi <i>H</i> шушукающиеся пары;\n<b>4)</b> положить конец, пресечь (iram <i>H</i>);\n<b>5)</b> отличать (legem bonam a mala <i>C</i>);\n<b>6)</b> распределять, размещать (omnia temporibus <i>Just</i>; equitatum in omnes partes <i>Cs</i>; copias hiematum <i>Nep</i>): sic belli ratio est divisa <i>Cs</i> таково соотношение сил на войне || ритмически <i>или</i> в такт распевать (grata feminis carmina cithara <i>H</i>);\n<b>7)</b> разлучать, держать вдали (aliquem <i>V</i>);\n<b>8)</b> голосовать по отдельным пунктам (sententiam <i>C, PJ, Sen</i>);\n<b>9)</b> покрывать выпуклыми украшениями (gemma, quae fulvum dividit aurum <i>V</i>);\n<b>10)</b> <i>грам.</i> делить на слоги (verba <i>Su</i>);\n<b>11)</b> <i>Pl, Pt</i> = paedico."],
  ["dividuitas", "<big>dividuitas</big>\n\n<b>dīviduitās, ātis</b> <i>f</i> [dividuus]\nраздел, разделение <i>Dig</i>."],
  ["dividus", "<big>dividus</big>\n\n<b>dīvidus, a, um</b> [divido]\nразделённый, отделившийся <i>Acc, AG</i>."],
  ["dividuus", "<big>dividuus</big>\n\n<b>dīviduus, a, um</b> [divido]\n<b>1)</b> делимый (materia <i>C</i>);\n<b>2)</b> разделённый (<i>преим.</i> пополам) (munus <i>H</i>): dividua coma <i>O</i> расчёсанные на обе стороны волосы; dividua luna <i>O</i> полумесяц; dividua amnis aqua <i>O</i> река, разделившаяся на два рукава; facere dividuum talentum <i>Pl</i> разделить талант пополам || раздвоенный (arbor <i>PM</i>);\n<b>3)</b> несовместимый <i>VM</i> (<i>см.</i> contubernium 4);\n<b>4)</b> <i>грам.</i> разделительный (<i>о словах, выделяющих единичные предметы из группы однородных, вроде</i> uterque, alteruter, quisque, singuli <i>и т. п.</i>)."],
  ["divina", "<big>divina</big>\n\n<b>dīvīna, ae</b> <i>f</i> [divinus]\nпрорицательница <i>Pt</i>."],
  ["divinalis", "<big>divinalis</big>\n\n<b>dīvīnālis, e</b> [divinus]\nбожественный: d. philosophia <i>Is</i> = theologia."],
  ["divinatio", "<big>divinatio</big>\n\n<b>dīvīnātio, ōnis</b> <i>f</i> [divino]\n<b>1)</b> вдохновение: d. animi <i>C, QC</i> наитие, предчувствие, <i>тж.</i> дар прорицания, предвидение (rerum futurarum <i>C</i>);\n<b>2)</b> <i>юр.</i> дивинация, <i>т. е.</i> разбор и решение, кому из ряда претендентов выступить на суде обвинителем <i>C, Q</i>;\n<b>3)</b> <i>юр.</i> речь против соперника во время дивинации <i>C</i>."],
  ["divinatrix", "<big>divinatrix</big>\n\n<b>dīvīnātrīx, īcis</b> <i>f</i> <i>Tert</i>\n= divina."],
  ["divine", "<big>divine</big>\n\n<b>dīvīnē</b> [divinus]\n<b>1)</b> по-божественному <i>Pl</i>;\n<b>2)</b> по воле богов, по вдохновению свыше <i>C</i>;\n<b>3)</b> божественно, превосходно <i>C</i>."],
  ["divinitas", "<big>divinitas</big>\n\n<b>dīvīnitās, ātis</b> <i>f</i> [divinus]\n<b>1)</b> божественность <i>C etc.</i>; обожествлённость (<i>sc.</i> Romuli <i>L</i>; Augusti <i>Su</i>);\n<b>2)</b> вдохновение свыше, пророческий дар, <i>тж.</i> высшая мудрость (d. Anaxagorae <i>PM</i>);\n<b>3)</b> совершенство (memoriae <i>Q</i>; loquendi <i>C</i>); божественное великолепие (divinitates astrorum <i>Vtr</i>)."],
  ["divinitus", "<big>divinitus</big>\n\n<b>dīvīnitus</b> <i>adv.</i> [divinus]\n<b>1)</b> по определению богов, по воле богов (Tiberis super ripas d. effusus <i>L</i>; si id d. accidit <i>C</i>; homini vox d. data <i>Ap</i>); по вдохновению свыше, по наитию (scire aliquid d. <i>Pl</i>);\n<b>2)</b> <i>перен.</i> божественно, чудесно, прекрасно, превосходно (aliquid d. dicere <i>C</i>)."],
  ["divino", "<big>divino</big>\n\n<b>dīvīno, āvī, ātum, āre</b> [divinus]\n<b>1)</b> обладать даром прорицания, пророчествовать, предсказывать (d. futura <i>C</i>; de aliqua re <i>C, Nep</i>);\n<b>2)</b> предчувствовать (nihil boni divinat animus <i>L</i>): quiddam praesentiens atque divinans <i>C</i> какой-то дар предчувствия."],
  ["divinum", "<big>divinum</big>\n\n<b>dīvīnum, ī</b> <i>n</i> [divinus]\n<b>1)</b> божественность <i>Sen, Ap</i>;\n<b>2)</b> принесение жертвы богам, богослужебный обряд <i>L</i>;\n<b>3)</b> пророчество, предсказание <i>Eccl</i>."],
  ["divinus", "<big>divinus</big>\n\n<b>I dīvīnus, a, um</b> [divus]\n<b>1)</b> божественный, божеский, божий (honores <i>Just, Q</i>; nihil ratione divinius <i>C</i>): scelera divina <i>L</i> преступления против богов; res divina <i>или</i> res divinae <i>C</i> богослужение, жертвоприношение; <i>тж.</i> res divinae <i>C</i> естество, природа (<i>в отличие от</i> res humanae); jura divina <i>C</i> законы природы; divina humanaque omnia <i>Pl</i> всё божественное и человеческое, <i>т. е.</i> всё на свете;\n<b>2)</b> свыше вдохновенный (pectus <i>Lcr</i>; vates <i>H</i>); пророческий, предвещающий (d. futuri <i>H</i>);\n<b>3)</b> богоподобный, превосходный, чудесный (vir <i>C</i>; nitor loquendi <i>Q</i>): d. morbus <i>Ap</i> падучая болезнь, эпилепсия;\n<b>4)</b> (<i>в эпоху империи как эпитет цезарей</i>) божественный, императорский: divina domus <i>Ph</i> дом (семья, династия) Цезарей.\n\n<b>II dīvīnus, ī</b> <i>m</i>\nпророк, прорицатель <i>C etc.</i>"],
  ["Divio", "<big>Divio</big>\n\n<b>Dīvio, ōnis</b> <i>m</i>\nДивион, <i>город в Галлии</i> (<i>ныне</i> Dijon) <i>GT</i>."],
  ["divise", "<big>divise</big>\n\n<b>dīvīsē</b> [divido]\nотдельно, раздельно <i>AG</i>."],
  ["divisi", "<big>divisi</big>\n\n<b>dīvīsī</b> <i>pf. к</i>\ndivido."],
  ["divisibilis", "<big>divisibilis</big>\n\n<b>dīvīsibilis, e</b> [divido]\nделимый <i>Tert</i>."],
  ["divisim", "<big>divisim</big>\n\n<b>dīvīsim</b> <i>Hier</i>\n= divise."],
  ["divisio", "<big>divisio</big>\n\n<b>dīvīsio, ōnis</b> <i>f</i> [divido]\n<b>1)</b> разделение, деление (regni inter aliquos <i>Just</i>);\n<b>2)</b> расчленение, разложение <i>C, Q</i>;\n<b>3)</b> продовольственный паёк <i>Dig</i>;\n<b>4)</b> <i>мат.</i> деление: d. binaria <i>Aug</i> деление пополам;\n<b>5)</b> раздача, распределение (agrorum <i>T</i>);\n<b>6)</b> <i>C, Q</i> = fututio."],
  ["divisor", "<big>divisor</big>\n\n<b>dīvīsor, ōris</b> <i>m</i> [divido]\n<b>1)</b> разделяющий (axis est d. mundi <i>Ap</i>);\n<b>2)</b> <i>мат.</i> делитель <i>Boët</i>;\n<b>3)</b> распределяющий, раздающий (d. regni inter filios <i>Eutr</i>);\n<b>4)</b> дивизор, <i>т. е.</i> наёмное лицо, подкупающее избирателей в пользу того или иного кандидата <i>C, Su</i>."],
  ["divisse", "<big>divisse</big>\n\n<b>dīvisse</b> (= divisisse) <i>H inf. pf. к</i>\ndivido."],
  ["divisura", "<big>divisura</big>\n\n<b>dīvīsūra, ae</b> <i>f</i> [divido]\n<b>1)</b> раздвоенность, разветвление (<i>sc.</i> arboris <i>PM</i>); ;\n<b>2)</b> надрез <i>CA, Pall</i>."],
  ["divisus", "<big>divisus</big>\n\n<b>I dīvīsus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> divido;\n<b>2.</b> <i>adj.</i> делимый <i>Lcr</i>.\n\n<b>II dīvīsus, (ūs)</b> <i>m</i> [divido]\nразделение: divisui esse <i>L</i> подлежать разделению; facilis divisui <i>L</i> легко делимый."],
  ["divitatio", "<big>divitatio</big>\n\n<b>dīvitātio, ōnis</b> <i>f</i> [divito]\nобогащение <i>Pt</i> (<i>v. l.</i>)."],
  ["Divitiacus", "<big>Divitiacus</big>\n\n<b>Dīvitiācus, ī</b> <i>m</i>\nДивитиак:\n<b>1)</b> <i>влиятельный эдуй, друг Цезаря, политический противник своего брата Думнорига</i> <i>Cs</i>;\n<b>2)</b> <i>царь свессионов</i> <i>Cs</i>."],
  ["divitiae", "<big>divitiae</big>\n\n<b>dīvitiae, ārum</b> (<i>крайне редко</i> (<i>Acc</i>) <i>sg.</i> <b>divitia, ae</b>) <i>f</i> [dives]\n<b>1)</b> богатство (superare Crassum divitiis <i>C</i>);\n<b>2)</b> пышность, драгоценности, драгоценные вещи (templum inclutum divitiis <i>L</i>);\n<b>3)</b> плодородие (soli <i>PM</i>); изобилие, плодовитость (ingenii <i>C</i>);\n<b>4)</b> богатая семья, богатый дом (aliquam despondere in divitias maximas <i>Pl</i>)."],
  ["divitior", "<big>divitior</big>\n\n<b>dīvitior</b> <i>compar. к</i>\ndives."],
  ["divito", "<big>divito</big>\n\n<b>dīvito, —, —, āre</b> [dives]\nобогащать <i>Acc ap. AG</i>."],
  ["Divodurum", "<big>Divodurum</big>\n\n<b>Dīvodūrum, ī</b> <i>n</i>\nДиводур, <i>главн. город медиоматриков в Gallia Belgica</i> (<i>впоследствии</i> Metis <i>и</i> Mettis, <i>ныне</i> Metz) <i>T</i>."],
  ["divolg-", "<big>divolg-</big>\n\n<b>dīvolg-</b> <i>v. l.</i>\n= divulg-."],
  ["divolsus", "<big>divolsus</big>\n\n<b>dīvolsus</b> <i>v. l.</i>\n= divulsus."],
  ["divolvo", "<big>divolvo</big>\n\n<b>dī–volvo, —, —, ere</b>\nперекатывать, поворачивать, <i>перен.</i> обдумывать (secum <i>Amm</i>)."],
  ["Divona", "<big>Divona</big>\n\n<b>Dīvona, ae</b> <i>f</i>\nДивона, <i>город кадурков в Аквитании</i> (<i>ныне</i> Cahors) <i>Aus</i>."],
  ["divors-", "<big>divors-</big>\n\n<b>dīvors-</b> <i>v. l.</i>\n= divers-."],
  ["divortium", "<big>divortium</big>\n\n<b>dīvortium</b> (<b>dīvertium</b> <i>C</i>), <b>ī</b> <i>n</i> [diverto]\n<b>1)</b> точка расхождения, раздвоения (aquarum <i>LM, C etc.</i>); распутье (d. itinerum <i>L</i>);\n<b>2)</b> разногласие (d. doctrinarum <i>C</i>);\n<b>3)</b> граница, рубеж (d. inter Europam et Asiam <i>T</i>): veris et hiemis d. <i>Col</i> переход от зимы к весне, предвесенняя пора;\n<b>4)</b> расторжение, разрыв (rerum <i>Ap</i>); расторжение брака, развод (cum uxore <i>Su</i>): d. facere cum aliqua <i>C</i> развестись с кем-л."],
  ["divorto", "<big>divorto</big>\n\n<b>dīvorto</b> <i>v. l.</i>\n= diverto."],
  ["divulgatio", "<big>divulgatio</big>\n\n<b>dīvulgātio, ōnis</b> <i>f</i>\nраспространение (в народе) (litteraturae <i>Tert</i>)."],
  ["divulgatus", "<big>divulgatus</big>\n\n<b>dīvulgātus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> divulgo;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> обыкновенный, простой (magistratus divulgatissimus <i>C</i>);\n<b>2)</b> широко распространённый (gloria <i>Lcr</i>)."],
  ["divulgo", "<big>divulgo</big>\n\n<b>dī–vulgo, āvī, ātum, āre</b>\nделать общим, доступным для всех, делать известным, распространять, широко публиковать (librum <i>C</i>); разглашать (audita res est et divulgata <i>C</i>)."],
  ["divulsio", "<big>divulsio</big>\n\n<b>dīvulsio, ōnis</b> <i>f</i> [divello]\nразрыв, распадение, разделение (familiarum <i>Sen</i>)."],
  ["divulsus", "<big>divulsus</big>\n\n<b>dīvulsus, a, um</b> <i>part. pf. к</i>\ndivello."],
  ["divum", "<big>divum</big>\n\n<b>I dīvum, ī</b> <i>n</i>\nоткрытое небо (sub divo <i>C, V, Nep etc.</i>).\n\n<b>II dīvum</b> (= deorum) <i>gen. pl. к</i>\ndivus II."],
  ["divus", "<big>divus</big>\n\n<b>I dīvus (dīus), a, um</b> [deus]\n(= divinus) божественный (diva parens <i>V</i>).\n\n<b>II dīvus, ī</b> <i>m</i>\nбог <i>V, L, H etc.</i>; <i>в императ. эпоху эпитет умерших цезарей</i> (d. Julius <i>Su</i> = C. J. Caesar)."],
  ["dixe", "<big>dixe</big>\n\n<b>dīxe</b>\n<i>Pl, Vr</i> = dixisse (<i>inf. pf. к</i> dico)."],
  ["dixi", "<big>dixi</big>\n\n<b>dīxī</b> <i>pf. к</i>\ndico."],
  ["dixis", "<big>dixis</big>\n\n<b>dīxīs</b> <i>Pl conjct. к</i>\ndico."],
  ["dixti", "<big>dixti</big>\n\n<b>dīxtī</b> <i>Pl, C</i>\n= dixisti."],
  ["do", "<big>do</big>\n\n<b>I dō, dedī, datum, dare</b>\n<b>1)</b> давать (d. alicui bibere <i>Pl, Cato</i>; d. pecuniam <i>C</i>): qui dedit beneficium, taceat <i>Sen</i> кто сделал доброе дело, пусть молчит (об этом) || подавать, протягивать (alicui manum <i>O etc.</i>; aliquid in manum <i>Pl</i>): <i>перен.</i> d. manus <i>C, Cs, Nep</i> сдаваться, признавать себя побеждённым || предлагать (accipere quod datur <i>C</i>); раздавать (frumentum plebi <i>VP</i>); выдавать (praemium pro pietate <i>C</i>): d. filiam in matrimonium <i>Cs</i> выдавать дочь замуж || даровать (vitam <i>C, V</i>; libertatem <i>C</i>); разрешать, позволять (d. bonum malumque dignoscere <i>Sen</i>): iter alicui per provinciam d. <i>Cs</i> разрешить кому-л. проход через (свою) провинцию || преподносить, дарить, приносить (в дар) (alicui dona <i>Pl</i>; alicui aliquid dono <i>Ter, Nep</i>); передавать, вручать (alicui litteras ad aliquem <i>C</i>): d. libellum <i>C</i> подать жалобу; oscula <i>O</i> (basia <i>Ctl</i>) d. покрывать поцелуями, целовать; aliquem defensum d. <i>V</i> защитить кого-л.; d. motus <i>Lcr</i> приводить в движение; plus stomacho, quam consilio d. <i>Q</i> руководствоваться больше своим пылом, чем рассудком; scripta foras d. <i>C</i> выпускать в свет сочинения;\n<b>2)</b> приносить в жертву (deo exta <i>O</i>); посвящать, воздвигать, строить (deo templum <i>O</i>);\n<b>3)</b> порождать, производить на свет (liberos <i>Ctl</i>; prolem <i>V</i>);\n<b>4)</b> образовывать (longos tortus <i>V</i>); d. impetum <i>L</i> ворваться;\n<b>5)</b> отправлять, посылать (litteras ex Trebulano <i>C</i>);\n<b>6)</b> вносить, платить (aes <i>и</i> aera <i>H, O etc.</i>); понести (poenas <i>C etc.</i>); доставлять (apes mella dant <i>O</i>): materiam d. invidiae <i>C</i> давать пищу ненависти;\n<b>7)</b> делать ход в игре (d. calculum <i>C</i>): calculus datus <i>C, Q</i> ход;\n<b>8)</b> отпускать, ослаблять (lora <i>V</i>): d. laxas habenas <i>V</i> опустить поводья;\n<b>9)</b> устраивать (alicui cenam <i>Pl, Ter, C etc.</i>; ludos populo <i>L</i>); ставить на сцене, инсценировать (fabulam <i>Ter, C</i>);\n<b>10)</b> наносить, причинять (alicui damnum <i>Ter</i>; vulnera ferro <i>O</i>): d. ruinas (stragem) <i>Lcr</i> производить разгром || предоставлять (opportunam facultatem ad aliquid <i>C</i>): vela d. ventis <i>O, V</i> распустить паруса по ветру; d. verba ventis <i>O</i> пускать слова на ветер; d. verba alicui <i>Ter</i> надуть кого-л.; d. alicui civitatem <i>VP</i> предоставить кому-л. право гражданства;\n<b>11)</b> проливать (lacrimas <i>O</i>); наливать (vinum in manus <i>Pt</i>); испускать (lucem <i>H</i>): d. fumos <i>O</i> дымиться || издавать (sonum <i>V</i>; gemitum <i>O</i>);\n<b>12)</b> произносить (voces, dicta <i>O, V</i>): inter se fidem et jus jurandum d. <i>Cs</i> дать друг другу клятвенное заверение || сообщать, рассказывать, называть (unum da mihi ex oratoribus illis <i>C</i>): paucis dabo <i>Ter</i> (почему это случилось), я расскажу в немногих словах; iste qui sit, da nobis <i>V</i> скажи нам, кто он; quem mihi dabis, qui aliquod pretium tempori ponat? <i>Sen</i> кого ты мне назовёшь, кто хоть сколько-нибудь умел бы ценить время?;\n<b>13)</b> закидывать (retro capillos <i>O</i>); класть, вкладывать, опускать (corpus tumulo <i>O</i>; urnae ossa <i>Pers</i>); погружать (in fluvios ardentia membra <i>Lcr</i>): d. bracchia collo <i>V</i> обнять руками за шею;\n<b>14)</b> заковывать (monstrum catenis <i>H</i>);\n<b>15)</b> ставить, натягивать (vela carinae <i>O</i>; fila lyrae <i>O</i>); протягивать (bracchia ad aliquid <i>O</i>);\n<b>16)</b> подставлять (alicui cervices <i>C</i>): terga d. (= vertere) <i>C, L, O etc.</i> обратиться в бегство;\n<b>17)</b> отводить (alicui locum in theatro <i>T</i>); посвящать (noctem somno <i>O</i> <i>и</i> quieti <i>L</i>);\n<b>18)</b> уступать (aliquid precibus alicujus <i>QC</i>): si das hoc <i>H</i> если ты с этим согласен; quae dederam supra, relego <i>Pers</i> то, что я допустил раньше, беру назад; dasne animos morte interire? — Do vero <i>C</i> признаёшь ли, что со смертью погибают (и) души? — Да, признаю;\n<b>19)</b> вменять (aliquid laudi, crimini alicui <i>C</i>); назначать (alicui diem <i>PJ</i>); обрекать (aliquem exitio <i>Lcr, O</i>; morbo mortique <i>Lcr</i>; urbem excidio ac ruinis <i>L</i>): d. aliquid famae <i>H, T</i> сделать что-л. в интересах репутации || повергать (aliquem in luctum <i>O</i>, in timorem <i>Pl</i>);\n<b>20)</b> бросать, швырять (aliquem in <i>и</i> ad terram <i>Lcr, Pl</i>): ad terram datus <i>L</i> упав (с коня) на землю; ambitione praeceps datus est <i>Sl</i> честолюбие (Мария) его и погубило || ввергать (aliquem in caveam <i>Pl</i>); предавать (corpus sepulturae <i>Su</i>): d. aliquem in ruborem <i>Pl</i> вогнать кого-л. в краску (заставить покраснеть);\n<b>21)</b> помещать, отдавать (aliquem in hanc domum <i>Ter</i>); отправлять (aliquem Luceriam <i>L</i>);\n<b>22)</b> вверять, передавать (filiam genero <i>V, O</i>; infantem nutrici <i>O</i>);\n<b>23)</b> внушать, вдохнуть (spem <i>C</i>; animos <i>O</i>); наводить, навеять (alicui somnum <i>O</i>);\n<b>24)</b> причинять (alicui dolorem <i>C</i>);\n<b>25)</b> назначать (aliquem comitem <i>QC</i>; arbitrum <i>C</i>); определять, класть (finem alicui <i>и</i> alicui rei <i>O etc.</i>);\n<b>26)</b> обращать (hostes in fugam <i>Cs</i>): sese d. in fugam <i>и</i> fugae <i>C</i> обращаться в бегство (<i>см. тж. ниже sub</i> dare se 4) || принуждать (aliquos in deditionem <i>L</i>);\n<b>27)</b> прощать (aliquid alicui <i>C</i>);\n<b>28)</b> решать: d. (litem) secundum aliquem <i>C, L, Sen</i> решить судебный процесс в чью-л. пользу; sic datur! <i>Pl</i> вот тебе за это!; d. rationem alicujus rei <i>Pl, rhH</i> отдавать отчёт в чём-л.; nomen d. <i>C</i> явиться, записаться, поступить (<i>преим.</i> на военную службу); do, dico, addico <i>Vr, Macr</i> предоставляю (<i>права</i>), произношу (<i>приговоры</i>), присуждаю (<i>юридическая формула</i> — «tria verba» <i>O</i> — <i>которой претор официально выражал свои права и функции</i>);\n<b>dare se</b> (<i>или</i> <b>dari</b>)\n<b>1)</b> даваться <i>или</i> быть данным, разрешаться, быть возможным: ubi sidere detur <i>O</i> (облетая затопленную землю, птица ищет), где бы можно было сесть; potestates, quas licet sentire, non datur cernere <i>Ap</i> силы, которые можно ощущать, но которых нельзя видеть;\n<b>2)</b> показываться (dare se urbi <i>Vop</i>): jam dudum optabam te dari <i>Ter</i> я давно уже хотел встретить тебя; det mihi se! <i>O</i> пусть он мне только попадётся! (попадись он мне);\n<b>3)</b> обнаруживать себя, выказать себя (d. se alicui placidum <i>O</i>): aequo se d. campo <i>V</i> выйти в чистое поле (для сражения); se obvium alicui d. <i>L</i> повстречаться с кем-л.; se d. alicui in conspectum <i>C</i> предстать перед кем-л.;\n<b>4)</b> войти (in medias acies <i>V</i>): se d. intro <i>C</i> проникнуть внутрь; sese fluvio (<i>dat.</i>) d. <i>V</i> броситься в реку; se tecto d. <i>H</i> спрыгнуть с крыши; se in viam d. <i>C</i> пуститься в путь; d. se fugae <i>C</i> пуститься бежать; sese in pedes d. <i>Pl</i> удирать;\n<b>5)</b> принимать участие (convivio <i>Su</i>; in bella <i>V</i>);\n<b>6)</b> покоряться, подчиняться, уступать, поддаваться, сдаваться (alicui <i>Pl, Ter, C etc.</i>);\n<b>7)</b> представиться, быть в распоряжении: quocumque tempore se dabunt vires <i>V</i> когда только будут силы; multae causae suspicionum dantur <i>C</i> имеется много поводов для подозрений; dent modo se superi <i>Sil</i> только бы помогли боги;\n<b>8)</b> предаваться (quieti, somno <i>C, PJ etc.</i>; voluptatibus <i>C</i>): d. se vento <i>Cs</i> плыть по воле ветра || посвящать себя (huic generi litterarum <i>C</i>; ad legendos libros <i>C</i>): d. se auctoritati senatus <i>C</i> стараться поднять авторитет сената;\n<b>9)</b> вести себя: ita dat se res <i>Poeta ap. C</i> так обстоит дело; turpiter se d. <i>Ter</i> срамиться; prout tempus ac res se daret <i>L</i> в зависимости от того, как сложатся время и обстоятельства (смотря по обстоятельствам).\n\n<b>II dō</b> (<i>греч.</i>) <i>Enn, Aus</i>\n= domum (<i>acc.</i>)."],
  ["doceo", "<big>doceo</big>\n\n<b>doceo, docuī, doctum, ēre</b> [<i>одного корня с</i> disco]\n<b>1)</b> учить, обучать (d. atque erudire juventutem <i>C</i>): nihil recte sine exemplo docetur aut discitur <i>Col</i> без примера (показа) нельзя надлежащим образом ни учить, ни учиться; d. aliquem (in) aliqua re <i>или</i> (ad) aliquid обучать кого-л. чему-л. (equo armisque <i>L</i>; ad legem <i>C</i>; omnia <i>Ter</i>; magnis de rebus <i>Lcr</i>): doceri ab aliquo <i>O</i> учиться у кого-л.; idoneus ad docendum <i>AG</i> поучительный; doctus militiam <i>Sl</i> <i>и</i> ad militiam <i>Ter</i> обученный военному делу;\n<b>2)</b> учить, утверждать (d. mundum a dis administrari <i>C</i>);\n<b>3)</b> уведомлять, извещать, ставить в известность (aliquem de aliqua re <i>Sl etc.</i>);\n<b>4)</b> объяснять, показывать, указывать: ut supra docuimus <i>C etc.</i> как мы показали выше;\n<b>5)</b> <i>юр.</i> излагать, докладывать (aliquem causam <i>и</i> de causa <i>C</i>);\n<b>6)</b> инсценировать, (под)готовить к театральной постановке, разучивать (fabulam <i>C</i>);\n<b>7)</b> преподавать (apud aliquem <i>C</i>; Romae <i>Sen, Su</i>): d. mercede <i>C</i> преподавать за плату; homo aptus ad docendum <i>C</i> человек с педагогическими способностями. — <i>См. тж.</i> doctus."],
  ["dochmius", "<big>dochmius</big>\n\n<b>dochmius, ī</b> <i>m</i> (<i>греч.</i>)\nдохмий (<i>стопа, состоящая из ямба и кретика, т. е.</i> U– | –U– <i>C, Q</i>)."],
  ["docibilis", "<big>docibilis</big>\n\n<b>docibilis, e</b>\nлегко воспринимающийся, хорошо усваивающийся <i>Eccl</i>."],
  ["docilis", "<big>docilis</big>\n\n<b>docilis, e</b> [doceo]\n<b>1)</b> толковый, сообразительный, восприимчивый, способный, понятливый (ad aliquid <i>C</i>): d. alicujus rei <i>H</i> <i>и</i> aliqua re <i>J</i> легко приучающийся к чему-л.;\n<b>2)</b> послушный (equorum genus <i>L</i>; alicui <i>Aug</i>); податливый, уступчивый (ingenium <i>Q</i>; <i>перен.</i> dociles capilli <i>O</i>);\n<b>3)</b> понятный (sermo <i>CA</i>; usus <i>Prp</i>)."],
  ["docilitas", "<big>docilitas</big>\n\n<b>docilitās, ātis</b> <i>f</i> [docilis]\n<b>1)</b> понятливость, восприимчивость (ad aliquid <i>C etc.</i>);\n<b>2)</b> послушность, кротость (animi <i>Eutr</i>)."],
  ["docis", "<big>docis</big>\n\n<b>docis, idis</b> <i>f</i> (<i>греч.</i>)\nдокида (<i>род огненного метеора</i>) <i>Ap</i>."],
  ["docte", "<big>docte</big>\n\n<b>doctē</b> [doctus]\n<b>1)</b> учёно, учёным образом, основательно (litteris Graecis doctissime eruditus <i>Sl</i>);\n<b>2)</b> умно, искусно, умело, ловко (d. atque astute <i>Pl</i>)."],
  ["doctiloquus", "<big>doctiloquus</big>\n\n<b>docti–loquus, a, um</b> [doctus + loquor]\nговорящий учёно <i>Enn</i>."],
  ["doctisonus", "<big>doctisonus</big>\n\n<b>docti–sonus, a, um</b> [sono]\nзвучащий по-учёному <i>Sid</i>."],
  ["doctitanter", "<big>doctitanter</big>\n\n<b>doctitanter</b> <i>adv.</i> [doctus]\nпо-учёному <i>Aug</i>."],
  ["doctiuscule", "<big>doctiuscule</big>\n\n<b>doctiusculē</b> [doctus]\nс некоторой учёностью <i>AG</i>."],
  ["doctor", "<big>doctor</big>\n\n<b>doctor, ōris</b> <i>m</i> [doceo]\nучитель (Plato, Xenocratis d. <i>C</i>); преподаватель (sapientiae <i>C</i>; liberalium artium <i>Su</i>); <i>в средние века преим.</i> учитель богословия, <i>причём наиболее выдающимся присваивались особые эпитеты</i>: D. gentium = Paulus; quattuor Doctores = Ambrosius, Augustinus, Gregorius I <i>и</i> Hieronymus; D. Seraphicus (= Bonaventura), D. Angelicus (= Thomas Aquinas), D. Mirabilis (= Roger Bacon), D. Subtilis (= Duns Scotus) <i>и пр.</i>"],
  ["doctrina", "<big>doctrina</big>\n\n<b>doctrīna, ae</b> <i>f</i> [doceo]\n<b>1)</b> учение (Graecis doctrinis eruditus <i>C</i>); наука (animos doctrina excolere <i>C</i>);\n<b>2)</b> обучение: doctrinam alicujus habere <i>Just</i> обучать кого-л.;\n<b>3)</b> учёность, образованность (homo omni virtute et doctrina ornatissimus <i>C</i>; doctrina aliquem superare <i>C</i>)."],
  ["doctrinalis", "<big>doctrinalis</big>\n\n<b>doctrīnālis, e</b> [doctrina]\nнаучный, теоретический <i>Tert</i>."],
  ["doctrix", "<big>doctrix</big>\n\n<b>doctrīx, īcis</b> <i>f</i>\nучительница, наставница <i>Vlg, Aug</i>."],
  ["doctus", "<big>doctus</big>\n\n<b>doctus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> doceo;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> учёный, образованный, просвещённый (vir, homo <i>C</i>);\n<b>2)</b> знающий, сведущий (et Graecis litteris et Latinis <i>Sl, C</i>; sermones utriusque linguae <i>H</i>; lingua utraque <i>M</i> <i>и</i> utriusque linguae <i>Aug</i>; nec minus Graece quam Latine <i>Su</i>; ex disciplinis Stoicorum <i>C</i>; artis alicujus <i>Cld</i>; sagittas tendere <i>H</i> <i>и</i> sagittarum <i>AV</i>);\n<b>3)</b> умелый, ловкий (ad aliquid <i>Pl, Ter</i>); искусный (manus <i>O</i>)."],
  ["documen", "<big>documen</big>\n\n<b>documen, inis</b> <i>n</i> <i>Lcr</i>\n= documentum."],
  ["documentum", "<big>documentum</big>\n\n<b>documentum, ī</b> <i>n</i> [doceo]\n<b>1)</b> поучение, пример, образец (habere aliquem sibi d. <i>C</i>); предостерегающий пример, назидание, урок, предостережение: d. periculi <i>L</i> предостережение от опасности;\n<b>2)</b> свидетельство, доказательство (virtutis, prudentiae <i>C</i>): mentium documenta <i>Ap</i> показатели умственного развития; documento esse <i>L</i> служить доказательством; d. fidei dare <i>L</i> доказать (свою) верность; esse d. adversus aliquid <i>T</i> свидетельствовать против чего-л."],
  ["dodecaeteris", "<big>dodecaeteris</big>\n\n<b>dōdecaëtēris, idis</b> <i>f</i> (<i>греч.</i>)\nдвенадцатилетие <i>Cens</i>."],
  ["dodecatemorion", "<big>dodecatemorion</big>\n\n<b>dōdecatēmorion, ī</b> <i>n</i> (<i>греч.</i>)\nдвенадцатая часть (знаков Зодиака) <i>Man</i>."],
  ["dodecatheon", "<big>dodecatheon</big>\n\n<b>dōdecatheon, ī</b> <i>n</i> (<i>греч.</i>) <i>предпол.</i>\nпервоцвет (<i>Primula vulgaris</i>, L.) <i>PM</i>."],
  ["Dodona", "<big>Dodona</big>\n\n<b>Dōdōna, ae</b> <i>и</i> <b>Dōdōnē, ēs</b> <i>f</i>\nДодона, <i>город в центре Эпира со священной дубравой и оракулом Юпитера</i> <i>C, O etc.</i>"],
  ["Dodonaeus", "<big>Dodonaeus</big>\n\n<b>Dōdōnaeus, a, um</b> [Dodona]\nдодонский (oraculum <i>C</i>; lebetes <i>V</i>)."],
  ["Dodonigena", "<big>Dodonigena</big>\n\n<b>Dōdōni–gena, ae</b> <i>m</i> [gigno]\nжитель Додоны, <i>перен.</i> питающийся желудями (Додонской дубравы) (populi <i>Sid</i>)."],
  ["Dodonis", "<big>Dodonis</big>\n\n<b>Dōdōnis, idis</b> <i>f</i> [Dodona]\nдодонская (terra <i>O</i>; quercus <i>VF</i>)."],
  ["dodra", "<big>dodra</big>\n\n<b>dōdra, ae</b> <i>f</i> (<i>тж.</i> <b>dōdrālis pōtio</b>) [dodrans 2]\nдодра, <i>напиток, приготовлявшийся из девяти различных материалов</i> <i>Aus</i>."],
  ["dodrans", "<big>dodrans</big>\n\n<b>dōdrāns, antis</b> <i>m</i> [de + quadrans]\n<b>1)</b> додрант, три четверти (aedificii reliquus d. <i>C</i>): heres ex dodrante <i>Su</i> наследник, получающий три четверти наследства; solvere dodrantem <i>M</i> платить (лишь) 3/4 своего долга;\n<b>2)</b> 3/4 асса, <i>т. е.</i> 9 унций = 245,59 <i>г</i> <i>C, Nep etc.</i>;\n<b>3)</b> 3/4 римского фута (pes)= 21,75 <i>см</i> <i>Su, Vtr</i>;\n<b>4)</b> 3/4 часа (dodrantes horarum <i>PM</i>);\n<b>5)</b> 3/4 югера, <i>т. е.</i> ок. 1890 <i>кв. м</i> <i>L, Col etc.</i>"],
  ["dodrantalis", "<big>dodrantalis</big>\n\n<b>dōdrantālis, e</b> [dodrans]\nравный додранту <i>Vtr, Col, PM</i>."],
  ["dodrantarius", "<big>dodrantarius</big>\n\n<b>dōdrantārius, a, um</b> [dodrans]\nтрёхчетвертной: tabulae dodrantariae <i>C</i> долговые книги, в которых задолженность была сокращена до 1/4 (<i>на основании lex Valeria feneratoria 86 г. до н. э.</i>)."],
  ["doga", "<big>doga</big>\n\n<b>doga, ae</b> <i>f</i> (<i>греч.</i>)\nдога (<i>род сосуда</i>) <i>Vop</i>."],
  ["dogma", "<big>dogma</big>\n\n<b>dogma, atis</b> <i>n</i> (<i>греч.</i>; <i>лат.</i> decretum, placitum)\nосновоположение, решение, мнение <i>C, M, J</i>; догмат <i>Eccl</i>."],
  ["dogmaticus", "<big>dogmaticus</big>\n\n<b>dogmaticus, a, um</b> [dogma]\nкасающийся (философских) основоположений (lites <i>Aus</i>)."],
  ["dogmatistes", "<big>dogmatistes</big>\n\n<b>dogmatistēs, ae</b> <i>m</i> (<i>греч.</i>)\nосновоположник учения <i>Hier</i>."],
  ["dogmatizo", "<big>dogmatizo</big>\n\n<b>dogmatizo, āvī, —, āre</b> (<i>греч.</i>)\nустанавливать основоположения, формулировать догмы <i>Aug</i>."],
  ["Dolabella", "<big>Dolabella</big>\n\n<b>I Dolābella, ae</b> <i>m</i>\nДолабелла, <i>cognomen в роде Корнелиев; наиболее известны</i>:\n<b>1)</b> Cn. Cornelius D., <i>претор в Киликии в 80 г. до н. э.; подвергся изгнанию за злоупотребления</i> <i>C</i>;\n<b>2)</b> P. Cornelius D., <i>зять Цицерона, сторонник Цезаря</i> <i>C</i>.\n\n<b>II dolābella, ae</b> <i>f</i> [<i>demin. к</i> dolabra]\nмаленькая мотыга, скобель <i>Col</i>."],
  ["dolabra", "<big>dolabra</big>\n\n<b>dolābra, ae</b> <i>f</i> [dolo]\nмотыга, кирка <i>Col, Pall</i>; стеноломная секира <i>L etc.</i>; топор для разрубания мясных туш <i>Dig</i>."],
  ["dolabratus", "<big>dolabratus</big>\n\n<b>dolābrātus, a, um</b> [dolabra]\nимеющий формы мотыги (securis <i>Pall</i>)."],
  ["dolamen", "<big>dolamen</big>\n\n<b>dolāmen, inis</b> <i>n</i> [dolo I]\nобрубание, обтёсывание (truncus dolamine effigiatus <i>Ap</i>)."],
  ["dolatio", "<big>dolatio</big>\n\n<b>dolātio, ōnis</b> <i>f</i> <i>Hier</i>\n= dolamen."],
  ["dolatorium", "<big>dolatorium</big>\n\n<b>dolātōrium, ī</b> <i>n</i>\nкаменотёсный молоток <i>Hier, Veg</i>."],
  ["dolatus", "<big>dolatus</big>\n\n<b>dolātus, ūs</b> <i>m</i>\nобтёсывание, шлифовка <i>Eccl</i>."],
  ["dolens", "<big>dolens</big>\n\n<b>dolēns, entis</b>\n<b>1.</b> <i>part. praes. к</i> doleo;\n<b>2.</b> <i>adj.</i> мучительный <i>Sl, O</i>."],
  ["dolenter", "<big>dolenter</big>\n\n<b>dolenter</b> [doleo]\nскорбно, с болью, с прискорбием (aliquid dicere <i>C</i>; ferre <i>PJ, VM</i>)."],
  ["dolentia", "<big>dolentia</big>\n\n<b>dolentia, ae</b> <i>f</i> <i>арх.</i>\n<i>Laevius ap. AG</i>\n= dolor."],
  ["doleo", "<big>doleo</big>\n\n<b>doleo, uī, (itūrus), ēre</b>\n<b>1)</b> болеть (pes dolet <i>C</i>; vulnus mihi dolet <i>M</i>; oculi dolent alicui <i>PM</i>);\n<b>2)</b> ощущать, чувствовать боль, страдать: doleo ab oculis <i>Pl</i> <i>или</i> doleo oculos <i>Fronto</i> у меня болят глаза; cui dolet, meminit <i>C</i> кто страдает, (тот) помнит; <i>impers.</i> mihi dolet <i>Ter etc.</i> мне больно;\n<b>3)</b> огорчаться, печалиться, скорбеть (de <i>и</i> ex aliqua re <i>и</i> aliquid <i>C, Cs etc.</i>): d. delicto <i>C</i> горько раскаиваться в (своём) проступке; d. alicujus mortem <i>C</i> скорбеть о чьей-л. смерти; dolenda referre <i>VP</i> рассказывать о печальных событиях; d. frustra voluisse <i>Sen</i> жалеть о несбывшихся желаниях; aeque dolendo <i>C</i> из сострадания; doleo, me ab illo superari <i>O</i> мне больно (прискорбно), что он превзошёл (победил) меня; dixisse suis familiaribus d. et plangere <i>Ap</i> приказать своим близким скорбеть и рыдать, <i>т. е.</i> умереть (ср. «приказать долго жить»);\n<b>4)</b> причинять боль (d. aliquem aliqua re <i>Prp</i>)."],
  ["doliaris", "<big>doliaris</big>\n\n<b>dōliāris, e</b> [dolium 1]\n<b>1)</b> бочкообразный (anus <i>Pl</i>);\n<b>2)</b> хранящийся в бочках, <i>т. е.</i> ещё не разлитый по кувшинам, молодой (vinum <i>Dig</i>)."],
  ["doliarium", "<big>doliarium</big>\n\n<b>dōliārium, ī</b> <i>n</i> [dolium I]\nвинный погреб <i>Dig</i>."],
  ["dolidus", "<big>dolidus</big>\n\n<b>dolidus, a, um</b> [doleo]\nболезненный, причиняющий боль (motus <i>CA</i>)."],
  ["doliolum", "<big>doliolum</big>\n\n<b>dōliolum, ī</b> <i>n</i> [<i>demin. к</i> dolium I]\nбочонок <i>Vr, L, Col</i>: d. floris <i>PM</i> цветочный нектарник."],
  ["dolito", "<big>dolito</big>\n\n<b>dolito, —, —, āre</b> [<i>intens. к</i> doleo]\nпричинять сильную боль, сильно болеть <i>Cato</i>."],
  ["dolitus", "<big>dolitus</big>\n\n<b>dolītus, a, um</b>\n<i>Vr</i> (= dolatus) <i>part. pf. к</i> dolo I."],
  ["dolium", "<big>dolium</big>\n\n<b>I dōlium, ī</b> <i>n</i>\nбочка (vinarium <i>Cato</i>; fictile, vitreum <i>Col</i>): in pertussum ingerere dicta d. <i>погов. Pl</i> говорить на ветер.\n\n<b>II dolium, ī</b> <i>n</i> [doleo]\nболь, скорбь (cordis <i>Eccl</i>)."],
  ["dolo", "<big>dolo</big>\n\n<b>I dolo, āvī, ātum, āre</b>\n<b>1)</b> обделывать, обтёсывать (lignum <i>J etc.</i>): d. fuste <i>H</i> избить палкой;\n<b>2)</b> обрабатывать вчерне, набрасывать (opus non perpolire, sed d. <i>C</i>);\n<b>3)</b> замышлять, строить: dolum d. <i>Pl</i> интриговать, строить козни.\n\n<b>II dolo (dolōn), ōnis</b> <i>m</i> (<i>греч.</i>)\n<b>1)</b> долон, <i>т. е.</i> палка с железным остриём, пика <i>V, Is</i> <i>или</i> палка с кинжалом, кинжал <i>Su, Sil, Dig</i>;\n<b>2)</b> жало (muscae <i>Ph</i>);\n<b>3)</b> передний парус (<i>самый маленький</i>) <i>L</i>.\n\n<b>III dolo</b> <i>dat. abl. sg. к</i>\ndolus."],
  ["Dolon", "<big>Dolon</big>\n\n<b>I Dolōn, ōnis</b> <i>m</i>\nДолон, <i>троянский разведчик, убитый Одиссеем и Диомедом</i> <i>V</i>.\n\n<b>II dolōn, ōnis</b> <i>m</i> <i>v. l.</i>\n= dolo II."],
  ["Dolopeius", "<big>Dolopeius</big>\n\n<b>Dolopēius, a, um</b> [Dolopes]\nдолопский <i>VF</i>."],
  ["Dolopes", "<big>Dolopes</big>\n\n<b>Dolopes, um</b> <i>m</i>\nдолопы, <i>ветвь пеласгов в Фессалии, на реке Энипей, позднее в Эпире у горы Пинд</i> <i>C, L, V etc.</i>"],
  ["Dolopia", "<big>Dolopia</big>\n\n<b>Dolopia, ae</b> <i>f</i>\nДолопия, <i>страна долопов</i> (<i>часть Эпира</i>)."],
  ["dolor", "<big>dolor</big>\n\n<b>dolor, ōris</b> <i>m</i> [doleo]\n<b>1)</b> боль, страдание (corporis <i>C</i>; dentium <i>CC etc.</i>): e dolore laborare <i>Ter</i> испытывать родовые муки;\n<b>2)</b> скорбь, огорчение, печаль: dolorem alicui facere (efficere, dare, afferre) <i>C etc.</i> огорчить, опечалить кого-л.; dolore affici <i>или</i> dolorem capere (accipere, percipere, suscipere) (ab, in, ex, pro) aliqua re <i>C etc.</i> печалиться, огорчаться чём-л.; d. conjugis amissae <i>O</i> скорбь (тоска) о потерянной супруге; alicui dolori esse <i>C</i> быть причиной чьего-л. огорчения;\n<b>3)</b> гнев, негодование, досада (dolore incensus <i>Nep</i>): dolore exardescere <i>Cs</i> воспламениться гневом, вознегодовать;\n<b>4)</b> <i>ритор.</i> скорбный пафос (detrahere actionis dolorem <i>C</i>)."],
  ["dolorosus", "<big>dolorosus</big>\n\n<b>dolōrōsus, a, um</b> [dolor]\n<b>1)</b> болезненный, причиняющий боль (locus <i>Veg</i>; exstantia <i>CA</i>);\n<b>2)</b> скорбящий (mater <i>Eccl</i>)."],
  ["dolose", "<big>dolose</big>\n\n<b>dolōsē</b> [dolosus]\nхитро, коварно, обманным путём (aliquam rem aggrediri = aggredi <i>Pl</i>; agere <i>C</i>)."],
  ["dolositas", "<big>dolositas</big>\n\n<b>dolōsitās, ātis</b> <i>f</i> [dolosus]\nхитрость, коварство <i>Vlg, Eccl</i>."],
  ["dolosus", "<big>dolosus</big>\n\n<b>dolōsus, a, um</b> [dolus]\n<b>1)</b> хитрый, лукавый, коварный (mulier <i>H</i>; consilium <i>C</i>; vulpes <i>Ph</i>); amici ferre jugum pariter dolosi <i>H</i> друзья, коварно отказывающиеся разделить бремя (несчастья);\n<b>2)</b> обманчивый: cinis d. <i>H</i> пепел, скрывающий в себе огонь."],
  ["dolus", "<big>dolus</big>\n\n<b>dolus, ī</b> <i>m</i>\n<b>1)</b> хитрость, уловка, лукавство, обман (per dolum et insidias <i>Cs</i>): haud dolo <i>Pl</i> <i>и</i> sine dolo malo <i>L</i> без надувательства, открыто, честно; non (ne) dicam dolo <i>Pl, Ter</i> откровенно говоря; d. malus <i>Ter, C etc.</i> злонамеренность, злой умысел;\n<b>2)</b> средства, орудия обмана: subterranei doli <i>Fl</i> подкопы, козни; doli <i>O</i> сети."],
  ["doma", "<big>doma</big>\n\n<b>dōma, atis</b> <i>n</i> (<i>греч.</i>)\nкровля, кров <i>Eccl</i>."],
  ["domabilis", "<big>domabilis</big>\n\n<b>domābilis, e</b> [domo]\nукротимый, тот, кого можно одолеть (Cantaber non ante d. <i>H</i>): nulla d. flamma <i>O</i> которого никакой огонь не сожжёт."],
  ["domatus", "<big>domatus</big>\n\n<b>domātus, a, um</b>\n<i>Pt</i> = domitus (<i>part. pf. к</i> domo)."],
  ["domefactus", "<big>domefactus</big>\n\n<b>domefactus, a, um</b> [domo + facio]\nпокорённый, освоенный (aratro domefacta tellus <i>Pt</i>)."],
  ["domesticatim", "<big>domesticatim</big>\n\n<b>domesticātim</b> [domesticus]\nв частных домах, у себя дома <i>Su</i>."],
  ["domesticatus", "<big>domesticatus</big>\n\n<b>domesticātus, ūs</b> <i>m</i>\nдоместикат, должность начальника домашней прислуги <i>Eccl</i>."],
  ["domestice", "<big>domestice</big>\n\n<b>domesticē</b> <i>adv.</i>\nу себя дома, частным образом <i>Eccl</i>."],
  ["domestici", "<big>domestici</big>\n\n<b>domesticī, ōrum</b> <i>m</i>\n<b>1)</b> члены семейства, домочадцы (<i>в широком смысле, т. е. включая близких друзей, клиентов и вольноотпущенников</i>) <i>C, L</i>;\n<b>2)</b> прислуга, челядь, рабы <i>Su</i>;\n<b>3)</b> (<i>тж.</i> d. milites <i>Vop</i>) личная охрана, телохранители <i>CJ, CTh</i>."],
  ["domesticus", "<big>domesticus</big>\n\n<b>domesticus, a, um</b> [domus]\n<b>1)</b> принадлежащий (относящийся) к дому, домашний (vestitus <i>C</i>; aves, pecudes <i>Col</i>; canes <i>C</i>); семейный (luctus <i>C</i>); homo d. <i>C</i> домочадец, член семьи <i>или</i> друг дома; inter domesticos parietes <i>C</i> у себя дома, в домашней обстановке;\n<b>2)</b> собственный, частный (judicium <i>Cs</i>); личный (res <i>C</i>): litterae domesticae <i>C</i> частная переписка; consilium domesticum capere <i>C</i> принять решение самостоятельно (на собственный страх и риск); domesticis exemplis <i>L</i> по собственному опыту; d. otior <i>H</i> я отдыхаю дома;\n<b>3)</b> туземный, отечественный, местный (mos, opes <i>C</i>): bellum domesticum <i>Cs</i> междоусобная (внутренняя) война; crudelitas domestica <i>C</i> жестокость по отношению к согражданам;\n<b>4)</b> внутренний, находящийся в душе (Furiae <i>C</i>)."],
  ["domi", "<big>domi</big>\n\n<b>domī</b> <i>adv.</i> [<i>арх.</i> locativus <i>к</i> domus]\nдома (<i>см.</i> domus)."],
  ["domicenium", "<big>domicenium</big>\n\n<b>domicēnium, ī</b> <i>n</i> [domus + cena]\nдомашняя трапеза <i>M</i>."],
  ["domicilium", "<big>domicilium</big>\n\n<b>domicilium, ī</b> <i>n</i> [domus]\nжилище, жильё, резиденция (d. constituere <i>Nep</i> <i>или</i> collocare in aliquo loco <i>C</i>); местопребывание, средоточие, центр (gloriae <i>C</i>): d. imperii <i>C</i> = Roma; <i>перен.</i> proprium d. alicui verbo <i>C</i> подлинное значение (смысл) какого-л. слова."],
  ["domicoenium", "<big>domicoenium</big>\n\n<b>domicoenium, ī</b> <i>n</i> <i>неправ. v. l.</i>\n= domicenium."],
  ["Domiduca", "<big>Domiduca</big>\n\n<b>Domi–dūca, ae</b> <i>f</i> <i>и</i> <b>Domidūcus, ī</b> <i>m</i> [duco]\nприводящая (-ий) в дом (жениха, невесту) (<i>эпитеты Юноны и Юпитера, как покровителей бракосочетания</i>) <i>Aug, Tert</i>."],
  ["domina", "<big>domina</big>\n\n<b>domina, ae</b> <i>f</i> [domus]\n<b>1)</b> госпожа, хозяйка <i>Pl, Ter, C etc.</i>;\n<b>2)</b> повелительница, владычица (fortuna, rerum humanarum d. <i>C</i>; d. Cybela <i>V</i>); жена Цезаря-императора (domino et dominae feliciter! <i>Su</i>);\n<b>3)</b> возлюбленная, подруга <i>Tib, Prp</i>;\n<b>4)</b> супруга <i>V, O</i>."],
  ["dominans", "<big>dominans</big>\n\n<b>domināns, antis</b>\n<b>1.</b> <i>part. praes. к</i> dominor;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> повелительный, властный (animus dominantior ad vitam, quam vis animae <i>Lcr</i>);\n<b>2)</b> наиболее употребительный, обычный (nomina <i>H</i>);\n<b>3.</b> <i>m</i> повелитель, властелин <i>T, bAl, Lad</i>."],
  ["dominatio", "<big>dominatio</big>\n\n<b>dominātio, ōnis</b> <i>f</i> [dominor]\n<b>1)</b> господство, владычество (d. Cinnae <i>C</i>; d. in aliquem <i>Sl, C</i> <i>и</i> in aliquo <i>Sl</i>);\n<b>2)</b> единовластие, верховная власть: d. unius <i>C</i> единодержавие; dominationes <i>T, Fl</i> (= dominantes) повелители."],
  ["dominator", "<big>dominator</big>\n\n<b>dominātor, ōris</b> <i>m</i> [dominor]\nвластитель, владыка: d. rerum <i>C</i> = deus."],
  ["dominatrix", "<big>dominatrix</big>\n\n<b>dominātrīx, īcis</b> <i>f</i> [dominator]\nповелительница, владычица <i>C, Sen, Vlg</i>."],
  ["dominatus", "<big>dominatus</big>\n\n<b>dominātus, ūs</b> <i>m</i> <i>C, Vr</i>\n= dominatio."],
  ["dominica", "<big>dominica</big>\n\n<b>dominica, ae</b> <i>f</i> <i>Tert etc.</i>\n= dominicus II."],
  ["dominicarius", "<big>dominicarius</big>\n\n<b>dominicārius, a, um</b> [dominicus II]\nвоскресный <i>Aug</i>."],
  ["dominicum", "<big>dominicum</big>\n\n<b>dominicum, ī</b> <i>n</i>\n<b>1)</b> сборник стихов Нерона (aliquid de dominico dicere <i>Su</i>);\n<b>2)</b> (<i>sc.</i> sacrificium) воскресное богослужение <i>Eccl</i>;\n<b>3)</b> (<i>sc.</i> aedificium) церковь, храм <i>Eccl</i>."],
  ["dominicus", "<big>dominicus</big>\n\n<b>I dominicus, a, um</b> [dominus]\n<b>1)</b> господский, хозяйский (jussus <i>Pt</i>): vinum dominicum <i>Pt</i> господское (<i>т. е.</i> лучшее) вино;\n<b>2)</b> императорский (coloni <i>CJ</i>);\n<b>3)</b> господень: dies d. <i>Eccl</i> = dominicus II; cena dominica <i>Eccl</i> тайная вечеря; dominica in palmis <i>Eccl</i> вербное воскресенье; oratio dominica <i>Eccl</i> молитва господня, <i>т. е.</i> «Отче наш».\n\n<b>II dominicus, ī</b> <i>m</i> (<i>sc.</i> dies)\nвоскресный день, воскресенье <i>Eccl</i>."],
  ["dominium", "<big>dominium</big>\n\n<b>dominium, ī</b> <i>n</i> [dominus II]\n<b>1)</b> обладание, владение (rerum suarum <i>VM etc.</i>): d. et jus <i>L</i> право собственности;\n<b>2)</b> власть, господство: d. alicujus rei <i>Sen etc.</i> господство над чём-л.;\n<b>3)</b> властелин <i>Sen</i>;\n<b>4)</b> пиршество, пирушка <i>LM, C, AG</i>."],
  ["domino", "<big>domino</big>\n\n<b>domino</b> <i>Tert</i>\n= dominor."],
  ["dominor", "<big>dominor</big>\n\n<b>dominor, ātus sum, ārī</b> <i>depon.</i> [dominus II]\nгосподствовать, властвовать: dominandi avidus <i>T</i> жаждущий власти; d. in aliquem <i>C, L</i>, in aliquo (in aliqua re) <i>C, V, VP etc.</i>, alicui rei <i>Cld, Ap</i> <i>и</i> alicujus rei <i>Ap, Eccl</i> властвовать над кем (чем)-л., повелевать кем-л. (чём-л.); inornata et dominantia nomina <i>H</i> простые и общеупотребительные слова (значения); id unum in oratore dominatur <i>C</i> в этом главная сила оратора."],
  ["dominula", "<big>dominula</big>\n\n<b>dominula, ae</b> <i>f</i> <i>M. Aurelius ap. Frontonem demin. к</i>\ndomina."],
  ["dominulus", "<big>dominulus</big>\n\n<b>dominulus, ī</b> <i>m</i> <i>Dig demin. к</i>\ndominus II."],
  ["dominus", "<big>dominus</big>\n\n<b>I dominus, a, um</b>\nгосподский, хозяйский (manus, torus <i>O</i>; hasta <i>J</i>).\n\n<b>II dominus, ī</b> <i>m</i> [domus]\n<b>1)</b> господин, хозяин, владелец (alicujus rei <i>Pl, C etc.</i>); устроитель, распорядитель (ludorum, rei <i>C</i>);\n<b>2)</b> повелитель (in aliquem <i>C</i>; gentium <i>C</i>): aliquem dominum in regna recipere <i>V</i> принять кого-л. в качестве своего соправителя, <i>т. е.</i> разделить с кем-л. царскую власть;\n<b>3)</b> государь (<i>эпитет императоров после Августа и Тиберия</i>) <i>Su</i>;\n<b>4)</b> возлюбленный <i>V, O</i>;\n<b>5)</b> <i>поздн.</i> (<i>как вежливое обращение</i>) сударь, милостивый государь <i>Sen, Su</i>;\n<b>6)</b> господь <i>Eccl</i>."],
  ["domiporta", "<big>domiporta</big>\n\n<b>domiporta, ae</b> <i>f</i> [domus + porto]\n«носящая (на себе) свой дом», <i>sc.</i> cochlea <i>C</i>."],
  ["Domitia", "<big>Domitia</big>\n\n<b>Domitia, ae</b> <i>f</i>\nДомиция:\n<b>1)</b> D. Lepida, <i>мать Мессалины, тётка Нерона</i> <i>T</i>;\n<b>2)</b> D. Calvilla, <i>мать Марка Аврелия</i> <i>Capit</i>."],
  ["Domitianus", "<big>Domitianus</big>\n\n<b>I Domitiānus, ī</b> <i>m</i>\nДомициан (T. Flavius), <i>сын Веспасиана, брат Тита, римск. император с 81 до 96 г. н. э.; убит заговорщиками</i> <i>Su, AV</i>.\n\n<b>II Domitiānus, a, um</b> [<i>adj. к</i> Domitius I <i>и к</i> Domitianus I]:\nDomitiana via <i>St</i> Домицианова дорога (<i>ответвление via Appia, от Sinuessa do Puteoli</i>); D. mensis <i>Su</i> = October."],
  ["domitio", "<big>domitio</big>\n\n<b>domitio</b> <i>v. l.</i>\n= domuitio."],
  ["Domitius", "<big>Domitius</big>\n\n<b>I Domitius, a, um</b>\nДомиций, <i>римск. nomen; наиболее известны</i>:\n<b>1)</b> Cn. D. Ahenobarbus («Рыжебородый»), <i>консул 192 г. до н. э.; в 167 г. до н. э. вместе с Эмилием Павлом участвовал в Македонском походе</i> <i>VM</i>;\n<b>2)</b> Cn. D. Ahenobarbus, <i>внук предыдущего, консул в 122 г. до н. э., покоритель аллоброгов и арвернов; строитель via Domitia в Галлии</i> <i>C, VP, VM, Fl</i>;\n<b>3)</b> Cn. D. Ahenobarbus, <i>сын предыдущего, консул в 96 г. до н. э., цензор в 92 г. до н. э.</i> <i>C, VP, VM, AG</i>;\n<b>4)</b> L. D. Ahenobarbus, <i>брат предыдущего, консул в 94 г. до н. э., приверженец Суллы, умерщвлён по приказанию Мария Младшего</i> <i>VM, Fl</i>;\n<b>5)</b> Cn. D. Ahenobarbus, <i>сторонник Мария, бежал в Африку, где погиб, сражаясь с Помпеем</i> <i>VM</i>;\n<b>6)</b> L. D. Ahenobarbus, <i>друг Цицерона, муж Порции, сестры Катона Утического, противник Цезаря; погиб, сражаясь против Антония при Фарсале</i> <i>C, Cs, Su</i>;\n<b>7)</b> Cn. D. Ahenobarbus, <i>сын предыдущего, участник заговора против Цезаря, затем сторонник Антония и, наконец, Октавиана</i> <i>C, Su</i>;\n<b>8)</b> Cn. D. Calvinus Maximus, <i>консул в 283 г. до н. э., разбил соединённые войска сенонов, бойев и этрусков, первый цензор-плебей в 280 г. до н. э.</i> <i>L, Eutr etc.</i>;\n<b>9)</b> Cn. D. Calvinus, <i>консул в 53 г. до н. э., легат Цезаря, подавлял восстание церретанов в Испании</i> <i>C, Cs</i>.\n\n<b>II Domitius, ī</b> <i>m</i> <i>Aug</i>\n= Domiducus."],
  ["domito", "<big>domito</big>\n\n<b>domito, —, —, āre</b> [<i>intens. к</i> domo]\nукрощать, усмирять (boves <i>V</i>; populos <i>Sil</i>; ventos <i>PJ</i>); приручать (elephantos <i>QC, PM</i>); возделывать (vitiferos agros <i>Sil</i>)."],
  ["domitor", "<big>domitor</big>\n\n<b>domitor, ōris</b> <i>m</i> [domo]\n<b>1)</b> укротитель (ferarum <i>Sen</i>); объездчик (equorum <i>C</i>);\n<b>2)</b> покоритель, усмиритель (victor domitorque Persarum <i>C</i>; maris <i>V, Sil</i>);\n<b>3)</b> разрушитель (curarum d. somnus <i>SenT</i>)."],
  ["domitrix", "<big>domitrix</big>\n\n<b>domitrīx, īcis</b> <i>f</i> [domitor]\nукротительница, усмирительница (equorum <i>V</i>; ferarum <i>O</i>); разрушительница (vis aceti <i>PM</i>)."],
  ["domitura", "<big>domitura</big>\n\n<b>domitūra, ae</b> <i>f</i> [domo]\nукрощение, усмирение, приручение (boum <i>Col, PM</i>)."],
  ["domitus", "<big>domitus</big>\n\n<b>I domitus, ūs</b> <i>m</i> <i>C</i>\n= domitura.\n\n<b>II domitus, a, um</b> <i>part. pf. к</i>\ndomo."],
  ["domna", "<big>domna</big>\n\n<b>domna, ae</b> <i>f</i> <i>Ctl</i>\n= domina."],
  ["domnaedius", "<big>domnaedius</big>\n\n<b>domn–aedius, ī</b> <i>m</i> [domnus + aedes]\nдомохозяин <i>Aug</i>."],
  ["domnul-", "<big>domnul-</big>\n\n<b>domnul-</b> <i>Sid etc.</i>\n= dominul-."],
  ["domnus", "<big>domnus</big>\n\n<b>domnus, ī</b> <i>m</i> <i>Aug</i>\n= dominus II."],
  ["domo", "<big>domo</big>\n\n<b>domo, uī, itum (<i>Pt</i> ātum), āre</b>\n<b>1)</b> укрощать (leones <i>O</i>; feras <i>Ph</i>); объезжать (equos <i>O etc.</i>); приручать (leones et tigres ad mansuetudinem d. <i>QC</i>): domita mansuetudo <i>Just</i> приручённость;\n<b>2)</b> усмирять, покорять (gentes <i>C</i>; orbem terrarum <i>VP</i>); обуздывать, преодолевать, побеждать, сдерживать, подавлять (invidiam <i>H</i>; libidines <i>C</i>; vim fluminis <i>L</i>); унимать (horrida verba <i>Tib</i>);\n<b>3)</b> приводить в порядок, приглаживать (impexos crines <i>St</i>);\n<b>4)</b> возделывать (terram rastris <i>V</i>); обрабатывать (ferrum igne <i>PM</i>); разводить, выращивать (arbores <i>V</i>);\n<b>5)</b> варить, разваривать (aliquid ferventibus undis <i>O</i>);\n<b>6)</b> утолять (cibus famem domat, potio sitim <i>Sen</i>); прогонять, побеждать (frigus famemque <i>Sil</i>);\n<b>7)</b> обезвреживать (venena serpentium <i>PM</i>);\n<b>8)</b> карать (delicta <i>Sil, Cld</i>);\n<b>9)</b> убивать (aliquem hasta <i>O</i>);\n<b>10)</b> выдавливать, выжимать (prelo domita uva <i>H</i>);\n<b>11)</b> смягчать (durum Bacchi saporem <i>V</i>)."],
  ["domui", "<big>domui</big>\n\n<b>I domuī</b> <i>pf. к</i>\ndomo.\n\n<b>II domuī</b> <i>dat. loc. sg. к</i>\ndomus."],
  ["domuis", "<big>domuis</big>\n\n<b>domuis</b> <i>Vr, Nigidius ap. AG gen. sg. к</i>\ndomus."],
  ["domuitio", "<big>domuitio</big>\n\n<b>domuitio, ōnis</b> <i>f</i> [<i>из</i> domumitio]\nвозвращение домой, на родину <i>Pac, Acc</i>."],
  ["domuncula", "<big>domuncula</big>\n\n<b>domuncula, ae</b> <i>f</i> [<i>demin. к</i> domus]\nдомик, домишко <i>Vtr, VM, Ap, Dig</i>."],
  ["domus", "<big>domus</big>\n\n<b>domus, ūs, <i>реже</i> ī</b> (<i>dat. арх.</i> domo <i>и</i> domu; <i>gen. pl.</i> domuum <i>и</i> domorum) <i>f</i>\n<b>1)</b> дом, жилище, местопребывание: domi <i>и</i> domui <i>C</i> дома; domi meae (tuae) <i>C</i> у меня (тебя) дома, в моём (твоём) доме; domi Caesaris <i>C</i> в доме Цезаря; domi se tenere <i>C</i> не выходить из дому (сидеть дома); domum <i>C etc.</i> домой; domo <i>C etc.</i> из дому; aliquem tecto et domo invitare <i>C</i> пригласить кого-л. к себе; domi самостоятельно, лично: domi parta dignatio <i>T</i> добытое личным трудом положение; bonae domi artes <i>T</i> личные достоинства; domi habeo (domi est mihi) <i>Ter, C</i> сам имею (знаю), <i>т. е.</i> в посторонней помощи не нуждаюсь; domi habet fallacias <i>Pl</i> за тем, чтобы обмануть, у неё дело не станет; id quidem domi est <i>C</i> в этом ведь недостатка нет; sed quid ego nunc haec ad te, cujus domi nascuntur? <i>C</i> но зачем я всё это говорю тебе, который и сам это знает?; domum abducere aliquem <i>C</i> склонить кого-л. на свою сторону; domo afferre <i>Q</i> прийти с заранее подготовленной речью; domo doctus <i>Pl</i> сам достаточно умён; experior domo <i>Pl</i> знаю по личному опыту; domo па собственный счёт (pecuniam solvere <i>или</i> reddere <i>Pl</i>);\n<b>2)</b> хозяйство: domus officia exsequi <i>T</i> заниматься домашним хозяйством;\n<b>3)</b> родина, отечество: unde domo? <i>V</i> из какой (вы) страны?; domi <i>Cs</i> на родине; domo emigrare <i>Cs</i> покинуть родину; domum reverti <i>Cs</i> вернуться на родину; domi inilitiaeque <i>C</i>, militiae et domi <i>Ter, Sl</i>, domi bellique (belloque) <i>L etc.</i> во время мира и на войне;\n<b>4)</b> семья, семейство: tota d. nostra te salutat <i>C</i> вся наша семья тебя приветствует (шлёт привет);\n<b>5)</b> род, племя: d. Assaraci <i>V</i> = populus Romanus;\n<b>6)</b> секта, философская школа (d. Socratica <i>H</i>)."],
  ["domuscula", "<big>domuscula</big>\n\n<b>domuscula, ae</b> <i>f</i> [<i>demin. к</i> domus]\nдомик <i>Ap</i>."],
  ["domusio", "<big>domusio</big>\n\n<b>domūsio, ōnis</b> <i>f</i> [domus + usio]\nдомашние обычаи, домашние надобности: in domusionem <i>Pt</i> для домашнего употребления, для себя лично."],
  ["donabilis", "<big>donabilis</big>\n\n<b>dōnābilis, e</b> [dono]\n<b>1)</b> достойный дара: homo infortunio d. <i>Pl</i> человек, заслуживающий тягчайшей кары;\n<b>2)</b> могущий (достойный) быть поднесённым в дар <i>Ambr</i>."],
  ["donarium", "<big>donarium</big>\n\n<b>dōnārium, ī</b> <i>n</i> (<i>преим. в</i> <i>pl.</i>) [donum]\n<b>1)</b> дар по обету, приношение в храм <i>AV</i>;\n<b>2)</b> место, куда приносили дары, <i>т. е.</i> алтарь <i>или</i> храм, святилище <i>V, O</i>; место хранения даров, храмовая сокровищница <i>Ap, Lcn</i>."],
  ["donarius", "<big>donarius</big>\n\n<b>dōnārius, a, um</b> [donum] <i>культ.</i>\nприносимый в дар (<i>богам</i>) (lanx <i>Pt</i> — <i>v. l. к</i> ducenarius 1)."],
  ["donaticus", "<big>donaticus</big>\n\n<b>dōnāticus, a, um</b> [dono]\nпреподносимый в виде подарка: hasta donatica <i>Cato</i> копьё, которое выдавалось солдатам в виде боевой награды."],
  ["donatio", "<big>donatio</big>\n\n<b>dōnātio, ōnis</b> <i>f</i> [dono]\nдарение, приношение в дар <i>C, CTh</i>."],
  ["donativum", "<big>donativum</big>\n\n<b>dōnātīvum, ī</b> <i>n</i> [dono]\nденежный подарок (<i>цезаря солдатам</i>) <i>T, Su</i>."],
  ["donator", "<big>donator</big>\n\n<b>dōnātor, ōris</b> <i>m</i> [dono]\n<b>1)</b> даритель, приносящий в дар <i>SenT, Dig, Eccl</i>;\n<b>2)</b> отпускающий грехи <i>Aug</i>."],
  ["donatrix", "<big>donatrix</big>\n\n<b>dōnātrīx, īcis</b> <i>f</i> [donator]\nдарительница <i>CTh, CJ Eccl</i>."],
  ["donax", "<big>donax</big>\n\n<b>donax, acis</b> <i>m</i> (<i>греч.</i>)\n<b>1)</b> кипрский тростник <i>PM</i>;\n<b>2)</b> <i>предпол.</i> морской черенок (<i>моллюск</i>) <i>PM</i>."],
  ["donec", "<big>donec</big>\n\n<b>dōnec</b> <i>conj.</i> [<i>из</i> doneque, donique]\n<b>1)</b> пока, покуда, до тех пор пока: d. eris felix multos numerabis amicos <i>O</i> пока будешь счастлив, много у тебя будет друзей;\n<b>2)</b> пока не: haud desinam, d. perfecero hoc <i>Ter</i> не перестану, пока не добьюсь этого; jam ad pedes pugna venerat, donec Numidae ab tergo se ostenderunt <i>L</i> бой стал уже пешим, как вдруг с тыла показались нумидийцы."],
  ["doneque", "<big>doneque</big>\n\n<b>dōneque, dōnicum <i>Pl, Cato, Nep</i>, dōnique</b> <i>Lcr conj. арх.</i>\n= donec."],
  ["dono", "<big>dono</big>\n\n<b>dōno, āvī, ātum, āre</b> [donum]\n<b>1)</b> дарить: alicui aliquid <i>C, Cs etc.</i>, (aliquem aliqua re <i>C, L, Su, QC</i>) подарить кому-л. что-л. (одарить кого-л. чём-л.) || уделить (alicui totam horam <i>M</i>): d. aliquem civitate <i>C</i> присвоить кому-л. права гражданства; d. meritos in proeliis <i>Sl</i> наградить отличившихся в боях || даровать (alicui immortalitatem <i>C</i>); преподносить (alicui munus <i>C</i>);\n<b>2)</b> не взыскивать, прощать, отпускать (d. alicui aes alienum <i>C</i>; poenam <i>Sen</i>; alicui syllabam barbare pronuntiatam <i>Ap</i>; peccatoribus delicta <i>Eccl</i>): d. patrem filio (<i>dat.</i>) <i>Just</i>. помиловать отца ради сына; d. legem <i>Pt</i> отказаться от судебного преследования;\n<b>3)</b> отказываться: d. inimicitias suas rei publicae (<i>dat.</i>) <i>C</i> отказаться от вражды в интересах (ради) государства; spes suas ambitioni d. <i>Pt</i> пожертвовать своими чаяниями ради (мелкого) тщеславия."],
  ["donum", "<big>donum</big>\n\n<b>dōnum, ī</b> <i>n</i>\n<b>1)</b> дар, подарок: dare (accipere) aliquid dono <i>Ter, Sl</i> отдать (получить) что-л. в дар; dona ultima (suprema) <i>O, VF, Sen</i> последний долг (последние почести) покойнику;\n<b>2)</b> жертва, жертвоприношение <i>Pl, Lcr, C etc.</i>: d. Apollinis <i>L</i> дар Аполлону."],
  ["Donusa", "<big>Donusa</big>\n\n<b>Donūsa, ae</b> <i>f</i>\nДонуса, <i>островок из группы Спорадских, к вост. от Наксоса</i> (<i>ныне</i> Деноса) <i>V, T, PM</i>."],
  ["dorcas", "<big>dorcas</big>\n\n<b>dorcas, cadis</b> (<i>acc.</i> dorcada) <i>f</i> (<i>греч.</i>)\nгазель, антилопа (<i>Antilope Dorcas</i>, L.) <i>M, Lcr</i>."],
  ["Dores", "<big>Dores</big>\n\n<b>Dōrēs, um <i>и</i> Dōriēnsēs, ium</b> <i>m</i>\nдоряне, <i>одна из трёх глав и племенных ветвей греков</i> <i>C, J etc.</i>"],
  ["Dori", "<big>Dori</big>\n\n<b>Dōrī, ōrum</b> <i>m</i> <i>Is</i>\n= Dores."],
  ["Dorice", "<big>Dorice</big>\n\n<b>Dōricē</b> <i>adv.</i>\nна дорическом диалекте (loqui <i>Su</i>)."],
  ["Doricus", "<big>Doricus</big>\n\n<b>Dōri(c)us, a, um</b> [Dores]\nдорический <i>Vtr, PM etc.</i>; <i>перен.</i> греческий <i>V, O etc.</i>"],
  ["Dorienses", "<big>Dorienses</big>\n\n<b>Dōriēnsēs, ium</b> <i>m</i> <i>v. l.</i>\n= Dores."],
  ["Doris", "<big>Doris</big>\n\n<b>I Dōris, idis</b> <i>f</i> [Dores]\nдорическая (dialectos <i>Su</i>): D. tellus <i>SenT</i> = Sicilia.\n\n<b>II Dōris, idis</b> (<i>и</i> <b>idos</b>) <i>f</i>\nДорида:\n<b>1)</b> <i>дочь Океана и Фетиды, жена Нерея, мать 50 Нереид <i>O</i>; перен.</i> <i>V etc.</i> = mare;\n<b>2)</b> <i>родом из Локриды, жена Дионисия Старшего Сиракузского</i> <i>C, VM</i>."],
  ["doriscos", "<big>doriscos</big>\n\n<b>dōriscos, ī</b> <i>m</i> (<i>греч.</i>)\n(<i>sc.</i> versus) стих – –UU–U."],
  ["Dorius", "<big>Dorius</big>\n\n<b>Dōrius, a, um</b> <i>H, PM, Ap etc.</i>\n= Doricus."],
  ["dormibo", "<big>dormibo</big>\n\n<b>dormībo</b> <i>Pl, Cato арх. fut. к</i>\ndormio."],
  ["dormio", "<big>dormio</big>\n\n<b>dormio, īvī (iī), ītum, īre</b>\n<b>1)</b> спать: d. in lucem <i>H</i> спать поздно; d. altum <i>J</i> крепко спать; tota mihi dormitur hiems <i>M</i> всю зиму я провожу во сне; non omnibus dormio <i>C</i> не ко всем я отношусь со снисхождением;\n<b>2)</b> ничего не делать, ни о чём не заботиться: d. in aurem utramvis (<i>или</i> dextram) <i>погов. Ter, PJ</i> крепко спать, <i>перен.</i> быть беспечным, ни о чём не тужить; quibus omnia beneficia dormientibus deferuntur <i>C</i> (представители знати), которым все благодеяния достаются во время сна (<i>т. е.</i> без всяких усилий с их стороны)."],
  ["dormitatio", "<big>dormitatio</big>\n\n<b>dormītātio, ōnis</b> <i>f</i> [dormito]\nдремота, сон <i>Vlg, Aug</i>."],
  ["dormitator", "<big>dormitator</big>\n\n<b>dormītātor, ōris</b> <i>m</i> [dormio]\nночной вор (днём предающийся сну) <i>Pl</i>."],
  ["dormitio", "<big>dormitio</big>\n\n<b>dormītio, ōnis</b> <i>f</i> [dormio] (<i>тж.</i> d. somni <i>Hier</i>)\nсон (дремота) <i>Vr, Vlg, Eccl</i>; <i>перен.</i> успение, смерть <i>Eccl</i>."],
  ["dormito", "<big>dormito</big>\n\n<b>dormīto, āvī, ātum, āre</b> [dormio]\n<b>1)</b> быть сонным, хотеть спать, засыпать <i>Pl, C</i>;\n<b>2)</b> гаснуть, погасать: sub Aurora, jam dormitante lucerna <i>O</i> на заре, когда лампада уже гасла;\n<b>3)</b> поступать как во сне <i>или</i> спросонок, говорить бессвязно, путаться, ошибаться: dormitas! <i>Pl</i> ты бредишь!; indignari quandoque bonus dormitat Humerus <i>H</i> негодовать, как только ошибётся почтенный Гомер (<i>т. е.</i> быть беспощадным к мелким промахам гениев)."],
  ["dormitor", "<big>dormitor</big>\n\n<b>dormītor, ōris</b> <i>m</i> [dormio]\nспящий, погружённый в (вечный) сон (Endymion <i>M</i>)."],
  ["dormitorium", "<big>dormitorium</big>\n\n<b>dormītōrium, ī</b> <i>n</i> [dormio]\nспальня <i>PM</i>."],
  ["dormitorius", "<big>dormitorius</big>\n\n<b>dormītōrius, a, um</b> [dormio]\nспальный (cubiculum <i>PJ</i>)."],
  ["doron", "<big>doron</big>\n\n<b>dōron, ī</b> <i>n</i> (<i>греч.</i>)\n<b>1)</b> <i>PM</i> = donum;\n<b>2)</b> <i>PM</i> = palmus 2."],
  ["dorsualia", "<big>dorsualia</big>\n\n<b>dorsuālia, ium</b> <i>n</i> [dorsualis]\nчепраки, попоны <i>Treb</i>."],
  ["dorsualis", "<big>dorsualis</big>\n\n<b>dorsuālis, e</b> [dorsum]\nнаходящийся на спине, спинной (crustae — <i>v. l.</i> cristae — ferarum <i>Amm</i>; notae equi <i>Ap</i>; pinnae <i>Sol</i>)."],
  ["dorsum", "<big>dorsum</big>\n\n<b>dorsum, ī</b> <i>n</i>\n<b>1)</b> спина (dorso aliquid portare <i>QC</i>);\n<b>2)</b> хребет (jugi <i>Cs etc.</i>; montis <i>L, QC etc.</i>);\n<b>3)</b> выпуклость, возвышение, гребень: d. vadi <i>V</i> мель; d. saxeum <i>PJ</i> каменная плотина; duplex d. <i>V</i> двойной лемех (плуга);\n<b>4)</b> поверхность (viae <i>St</i>; maris <i>MF</i>)."],
  ["dorsuosus", "<big>dorsuosus</big>\n\n<b>dorsuōsus, a, um</b> [dorsum]\nхолмообразный, бугорчатый <i>Sol, Amm</i>."],
  ["dorsus", "<big>dorsus</big>\n\n<b>dorsus, ī</b> <i>m</i> <i>Pl</i>\n= dorsum."],
  ["Dorylaeum", "<big>Dorylaeum</big>\n\n<b>Dorylaeum, ī</b> <i>n</i>\nДорилей, <i>город в сев. Фригии, на реке Thymbris; узел дорог на Пессин, Иконий и Апамею</i> <i>C</i>."],
  ["Dorylenses", "<big>Dorylenses</big>\n\n<b>Dorylēnsēs, ium</b> <i>m</i>\nжители города Дорилей <i>PM</i>."],
  ["doryphorus", "<big>doryphorus</big>\n\n<b>doryphorus(-os), ī</b> (<i>acc.</i> um <i>и</i> on) <i>m</i> (<i>греч.</i>)\nкопьеносец, копейщик <i>C, QC, Q</i>."],
  ["dos", "<big>dos</big>\n\n<b>dōs, dōtis</b> <i>f</i> (<i>gen. pl.</i> um <i>и</i> ium)\n<b>1)</b> приданое: doti (<i>или</i> dotem) dicere <i>C</i> <i>или</i> dare <i>Col etc.</i>; in dotem conferre <i>PJ</i> давать в приданое; dotis tabellae <i>Su</i> брачные договоры; veniunt a dote sagittae <i>J</i> стрелы (не от любви, а) от приданого (<i>о браке по расчёту</i>);\n<b>2)</b> дарование, дар, талант (dotes naturae <i>PM</i>; ingenii <i>O etc.</i>; dotes corporis animique <i>Su</i>);\n<b>3)</b> достоинство (duplex libelli d. est <i>Ph</i>);\n<b>4)</b> богатство, состояние (probam pauperiem sine dote quaerere <i>H</i>; d. est magna parentium virtus <i>H</i>)."],
  ["Dossennus", "<big>Dossennus</big>\n\n<b>Dossēn(n)us, ī</b> <i>m</i>\nДоссен, <i>одно из постоянных действующих лиц римск. ludi Atellani — горбатый обжора — плут</i> <i>H, Sen, PM</i>."],
  ["dossuarius", "<big>dossuarius</big>\n\n<b>dossuārius, a, um</b> [dorsum]\nвьючный (asellus <i>Vr</i>)."],
  ["dotalis", "<big>dotalis</big>\n\n<b>dōtālis, e</b> [dos]\nотносящийся к приданому, отданный в приданое (agri <i>H</i>; praedium <i>C</i>): aliquid dotale permittere alicui <i>V</i> принести что-л. в приданое."],
  ["dotatus", "<big>dotatus</big>\n\n<b>dōtātus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> doto II;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> достаточно наделённый, имеющий богатое приданое, богатый (dotata uxor <i>Pl</i>);\n<b>2)</b> снабжённый: vite dotata ulmus <i>PM</i> вяз, обвитый виноградной лозой || одарённый: dotatissimus forma <i>O</i> одарённый редкой красотой, замечательно красивый."],
  ["Doto", "<big>Doto</big>\n\n<b>I Dōtō, ūs</b> <i>f</i>\nДото, <i>одна из Нереид</i> <i>V</i>.\n\n<b>II dōto, āvī, ātum, āre</b> [dos]\nдавать в приданое, наделять приданым (d. filiam <i>Su etc.</i>); наделять, одарять: olea dotatur lacrima <i>PM</i> (в Аравии) масличное дерево содержит камедь."],
  ["drachma", "<big>drachma</big>\n\n<b>drachma</b>, <i>арх. Pl, Ter</i> <b>drachuma, ae</b> (<i>gen. pl.</i> <i>иногда</i> um) <i>f</i> (<i>греч.</i>)\nдрахма:\n<b>1)</b> <i>греческая монета = 0,01 аттической мины, приравнивавшаяся к римск.</i> denarius <i>Pl, Ter, C, H etc.</i>;\n<b>2)</b> <i>мера веса</i> = 1/8 uncia = 1/96 as, <i>т. е.</i> 3,411 <i>г</i> <i>PM</i>."],
  ["drachumisso", "<big>drachumisso</big>\n\n<b>drachumisso, —, —, āre</b> [drachuma = drachma]\nслужить за одну драхму <i>Pl</i>."],
  ["Draco", "<big>Draco</big>\n\n<b>I Dracō, ōnis</b> <i>m</i>\nДракон(т), <i>афинский архонт и первый законодатель, законы которого (thesmoi, в отличие от nomoi Солона) отличались неслыханной строгостью (ок. 625 г. до н. э.</i>) <i>C, AG</i>.\n\n<b>II draco, ōnis</b> <i>m</i> (<i>греч.</i>)\n<b>1)</b> змея (<i>из группы крупных неядовитых</i>) <i>C etc.</i>; дракон, баснословный змей <i>C</i>;\n<b>2)</b> (<i>лат.</i> Serpens <i>и</i> Anguis) созвездие Дракона (<i>в сев. полушарии</i>) <i>C</i>;\n<b>3)</b> прибор со змеевиком для быстрого нагревания воды <i>Sen</i>;\n<b>4)</b> d. (marinus) <i>PM</i> морской дракон (рыба <i>Trachinus Draco</i>, L.);\n<b>5)</b> военный значок (знамя) когорты (<i>заимствованный у парфян</i>) <i>Veg, VF</i>."],
  ["dracon", "<big>dracon</big>\n\n<b>dracōn, ōntis</b> <i>m</i> (<i>греч.</i>)\nдракон <i>Acc</i>."],
  ["draconarius", "<big>draconarius</big>\n\n<b>dracōnārius, ī</b> <i>m</i> [draco]\nдракононосец (<i>знаменосец в частях, знаменем которых был дракон</i>) <i>Veg, Vop, Amm</i>."],
  ["draconigena", "<big>draconigena</big>\n\n<b>dracōnigena, ae</b> <i>m, f</i> [draco + gigno]\nрождённый от дракона: urbs d. <i>O</i> драконорождённый город, <i>т. е.</i> Фивы Беотийские (<i>построенные воинами, вышедшими из посеянных зубов дракона</i>); d. hostis <i>Sid</i> = Alexander Magnus."],
  ["dracontarium", "<big>dracontarium</big>\n\n<b>dracontārium, ī</b> <i>n</i> [draco]\nдраконтарий, змеевидное ожерелье <i>или</i> извитая головная повязка <i>Tert</i>."],
  ["dracunculus", "<big>dracunculus</big>\n\n<b>dracunculus, ī</b> <i>m</i> [<i>demin. к</i> draco]\n<b>1)</b> маленький дракон, змейка <i>Lampr</i>;\n<b>2)</b> дракункул, <i>разновидность полыни</i> (<i>Artemisia dracunculus</i>, L.) <i>PM</i>."],
  ["dragantum", "<big>dragantum</big>\n\n<b>dragantum, ī</b> <i>PM</i>\n= tragacanthum."],
  ["drama", "<big>drama</big>\n\n<b>drāma, atis</b> <i>n</i> (<i>греч.</i>)\nдрама, драматическое произведение <i>Aus</i>."],
  ["Drancae", "<big>Drancae</big>\n\n<b>Drancae (Drangae), ārum</b> <i>m</i>\nдранги, жители персидской сатрапии Дрангианы (<i>прибл. нын. Сеистан в вост. Иране</i>) <i>PM, Just, QC</i>."],
  ["Drancaeus", "<big>Drancaeus</big>\n\n<b>Drancaeus, a, um</b> [Drancae]\nдрангийский (phalanx, legio <i>VF</i>)."],
  ["Drangae", "<big>Drangae</big>\n\n<b>Drangae</b> <i>v. l.</i>\n= Drancae."],
  ["drapeta", "<big>drapeta</big>\n\n<b>drāpeta, ae</b> <i>m</i> (<i>греч.</i>; <i>лат.</i> fugitivus)\nбеглец, беглый раб <i>Pl</i>."],
  ["draucus", "<big>draucus</big>\n\n<b>draucus, ī</b> <i>m</i> <i>M</i>\n= paedico II."],
  ["drenso", "<big>drenso</big>\n\n<b>drēnso, —, —, āre</b>\nкричать по-лебединому <i>Su</i>."],
  ["Drepana", "<big>Drepana</big>\n\n<b>Drepana, ōrum <i>n</i>, Drepanē, ēs <i>f</i> <i>и</i> Drepanum, ī</b> <i>n</i> (<i>греч.</i> «серп»)\nДрепан, <i>город и порт на сев.-зап. побережье Сицилии</i> (<i>на серповидной косе; ныне</i> Capo Trapani) <i>Cato, V, L, Sil</i>."],
  ["drepanis", "<big>drepanis</big>\n\n<b>drepanis, idis</b> (<i>acc.</i> in) <i>f</i>\nстриж (<i>Hirundo apus, <i>L</i>.</i>) <i>PM</i>."],
  ["Drepanitanus", "<big>Drepanitanus</big>\n\n<b>I Drepanitānus, a, um</b> [Drepana]\nдрепанский <i>C, PM</i>.\n\n<b>II Drepanitānus, ī</b> <i>m</i>\nжитель города Дрепана <i>C, PM</i>."],
  ["Drepanum", "<big>Drepanum</big>\n\n<b>Drepanum, ī</b> <i>n</i> <i>v. l.</i>\n= Drepana."],
  ["drimyphagia", "<big>drimyphagia</big>\n\n<b>drīmȳphagia, ae</b> <i>f</i> (<i>греч.</i>)\nупотребление Острых блюд, пристрастие к пряностям <i>CA</i>."],
  ["drindro", "<big>drindro</big>\n\n<b>drindro, —, —, āre</b>\nкричать (<i>о ласке</i>) <i>Su</i>."],
  ["drino", "<big>drino</big>\n\n<b>drino, ōnis</b> <i>m</i>\nдринон (<i>разновидность тунца</i>) <i>PM</i>."],
  ["dromas", "<big>dromas</big>\n\n<b>dromas, adis</b> <i>m</i> (<i>греч.</i>)\nбегущий: d. camelus <i>QC, L</i> дромадер, одногорбый верблюд."],
  ["dromeda", "<big>dromeda</big>\n\n<b>dromeda, ae <i>f Vop</i> <i>и</i> dromedārius, ī</b> <i>m</i> <i>Vlg, Hier</i>\n= dromas."],
  ["dromon", "<big>dromon</big>\n\n<b>dromō(n), ōnis</b> <i>m</i>\nдромон, быстроходное судно <i>CJ, Is</i>."],
  ["Dromos", "<big>Dromos</big>\n\n<b>I Dromos, ī</b> <i>m</i> (<i>греч.</i> «бег», «ристалище»)\n<i>равнина близ Спарты</i> <i>L</i>.\n\n<b>II dromos, ī</b> <i>m</i> (<i>греч.</i>)\nристалище, ипподром <i>PM</i>."],
  ["dropax", "<big>dropax</big>\n\n<b>drōpax, acis</b> <i>m</i> (<i>греч.</i>)\nдропак, <i>смолистое вещество для удаления волос</i> <i>M, Aus, CA</i>."],
  ["Druentia", "<big>Druentia</big>\n\n<b>Druentia, ae</b> <i>f</i>\nДруенция, <i>левый приток нижнего Родана</i> (<i>ныне</i> Durance) <i>L, Sil, Aus, Amm</i>."],
  ["Druias", "<big>Druias</big>\n\n<b>Druias, adis</b> <i>и</i> <b>Druis, idis</b> <i>f</i> [Druidae]\nжрица-друидесса <i>Lampr, Vop</i>."],
  ["Druidae", "<big>Druidae</big>\n\n<b>Druidae, ārum <i>и</i> Druidēs, um</b> <i>m</i>\nдруиды, <i>жрецы у древних кельтов в Галлии и Британии</i> <i>C, Cs, T, Su</i>."],
  ["Druis", "<big>Druis</big>\n\n<b>Druis, idis</b> <i>f</i> <i>v. l.</i>\n= Druias."],
  ["drungarius", "<big>drungarius</big>\n\n<b>drungārius, ī</b> <i>m</i> [drungus]\nкомандир друнга <i>Veg</i>."],
  ["drungus", "<big>drungus</big>\n\n<b>drungus, ī</b> <i>m</i>\nдрунг, отряд солдат <i>Veg, Vop</i>."],
  ["druppa", "<big>druppa</big>\n\n<b>druppa, ae</b> <i>f</i> (<i>греч.</i>)\nспелая маслина <i>PM</i>."],
  ["Drusianus", "<big>Drusianus</big>\n\n<b>Drūsiānus (Drūsīnus), a, um</b> [Drusus]\nдрузов: fossa Drusiana <i>T, Su</i> канал Друза (<i>проведённый при Нероне Клавдии Друзе, между Рейном и Исселем</i>)."],
  ["Drusilla", "<big>Drusilla</big>\n\n<b>Drūsilla, ae</b> <i>f</i>\nДрузилла:\n<b>1)</b> Livia D., <i>вторая жена Августа, мать Тиберия</i> <i>Su, O</i>;\n<b>2)</b> Julia D., <i>дочь Германика и Агриппины, жена Л. Кассия Лонгина, а затем М. Э. Лепида, сестра и возлюбленная Калигулы; умерла в 38 г. н. э.</i> <i>T, Su</i>."],
  ["Drusinus", "<big>Drusinus</big>\n\n<b>Drūsīnus</b> <i>v. l.</i>\n= Drusianus."],
  ["Drusus", "<big>Drusus</big>\n\n<b>Drūsus, ī</b> <i>m</i>\nДруз, <i>cognomen в родах Ливиев и Клавдиев; наиболее известны</i>:\n<b>1)</b> M. Livius D., <i>коллега и противник народного трибуна Т. Гракха, консул в 112 г. до н. э.</i> <i>C, Fl</i>;\n<b>2)</b> M. Livius D., <i>сын предыдущего, народный трибун в 91 г. до н. э.; продолжатель политики Гракхов</i> <i>C, VP</i>;\n<b>3)</b> Nero Claudius D., <i>обычно просто</i> D., <i>сын Тиберия Клавдия Нерона, сводный брат Тиберия, успешно воевал с германцами (12 г. до н. э.), покорил узипетов (11 г. до н. э.), проник до реки Visurgis (Везера); умер в 9 г. н. э.</i> <i>T, Su</i>;\n<b>4)</b> D. Caesar, <i>единственный сын императора Тиберия, консул в 15 и 21 гг. н. э., убит Сеяном в 23 г. н. э.</i> <i>T</i>;\n<b>5)</b> D., <i>младший сын Германика и Агриппины; умерщвлён по приказанию Тиберия в 33 г. н. э.</i> <i>T, Su</i>."],
  ["Dryantides", "<big>Dryantides</big>\n\n<b>Dryantīdēs, ae</b> <i>m</i>\nсын Дрианта, <i>т. е.</i> Ликург <i>O</i>."],
  ["Dryas", "<big>Dryas</big>\n\n<b>I Dryas, adis</b> (<i>pl. dat.</i> Dryasin, <i>acc.</i> Dryadas) <i>f</i>\nдриада, <i>нимфа деревьев или лесов</i> <i>V, M, Prp etc.</i>\n\n<b>II Dryās, antis</b> <i>m</i>\nДриант:\n<b>1)</b> <i>отец фракийск. царя Ликурга</i> <i>Naev</i>;\n<b>2)</b> <i>участник Калидонской охоты</i> <i>O</i>."],
  ["Drymae", "<big>Drymae</big>\n\n<b>Drȳmae, ārum</b> <i>f</i>\nДримы, <i>город в Дориде</i> <i>L</i>."],
  ["Drymo", "<big>Drymo</big>\n\n<b>Drȳmō, ūs</b> <i>f</i>\nДримо, <i>имя нимфы</i> <i>V</i>."],
  ["Drymussa", "<big>Drymussa</big>\n\n<b>Drymūs(s)a, ae</b> <i>f</i>\nДримуса, <i>о-в в Гермейском заливе против Клазомен</i> <i>L, PM</i>."],
  ["Dryope", "<big>Dryope</big>\n\n<b>Dryopē, ēs</b> <i>f</i>\nДриопа:\n<b>1)</b> <i>дочь Эврита, царя Эхалии, мать Амфисса, превращенная в лотос</i> <i>O</i>;\n<b>2)</b> <i>нимфа, мать Тарквита</i> <i>V</i>."],
  ["Dryopes", "<big>Dryopes</big>\n\n<b>Dryopes, um</b> <i>m</i>\nдриопы, <i>ветвь пеласгов в области горы Эта (южн. Фессалия), вытесненная дорянами в Мессению</i> <i>V, PM</i>."],
  ["Dryops", "<big>Dryops</big>\n\n<b>Dryops, opis</b> <i>m</i> <i>O sg. к</i>\nDryopes."],
  ["dualis", "<big>dualis</big>\n\n<b>duālis, e</b> [duo]\nдвойственный: numerus d. <i>Lact</i> двойственность, двоица, <i>Q грам.</i> двойственное число."],
  ["duapondo", "<big>duapondo</big>\n\n<b>dua–pondō</b> <i>n indecl.</i>\nдва фунта <i>Q</i>."],
  ["duas", "<big>duas</big>\n\n<b>duās</b> <i>арх.</i>\n= duis (<i>см.</i> duim)."],
  ["dubie", "<big>dubie</big>\n\n<b>dubiē</b> [dubius]\n<b>1)</b> неуверенно, нерешительно, колеблясь: inter confessum dubie dubieque negantem <i>O</i> не решаясь ни признать, ни отвергнуть;\n<b>2)</b> сомнительно: non (haud) d. <i>L, O, J etc.</i> без сомнения, бесспорно."],
  ["dubietas", "<big>dubietas</big>\n\n<b>dubietās, ātis</b> <i>f</i> [dubius]\nсомнение <i>Eutr, Amm</i>."],
  ["dubio", "<big>dubio</big>\n\n<b>dubiō</b> <i>adv.</i> [dubius]\nсомнительно: nec d. <i>Ap</i> несомненно, бесспорно."],
  ["dubiosus", "<big>dubiosus</big>\n\n<b>dubiōsus, a, um</b> [dubius]\nсомнительный <i>AG</i>."],
  ["Dubis", "<big>Dubis</big>\n\n<b>Dūbis, is</b> <i>m</i>\nДубис, <i>левый приток реки Арар</i> (<i>ныне</i> Doubs) <i>Cs</i>."],
  ["dubitabilis", "<big>dubitabilis</big>\n\n<b>dubitābilis, e</b> [dubius]\n<b>1)</b> сомнительный, подвергаемый сомнению (verum, virtus <i>O</i>);\n<b>2)</b> сомневающийся (pectus <i>Eccl</i>)."],
  ["dubitanter", "<big>dubitanter</big>\n\n<b>dubitanter</b> [dubito]\nс сомнением, нерешительно, колеблясь <i>C, AG, VM, Aug</i>."],
  ["dubitatio", "<big>dubitatio</big>\n\n<b>dubitātio, ōnis</b> <i>f</i> [dubito]\n<b>1)</b> сомнение (alicujus rei <i>или</i> de aliqua re <i>C etc.</i>): res habet dubitationem <i>C etc.</i> это сомнительно;\n<b>2)</b> нерешительность, колебание (angunt me dubitationes tuae <i>C</i>): d. alicujus rei <i>C etc.</i> колебания насчёт чего-л.; nulla interposita dubitatione <i>Cs</i> без малейших колебаний."],
  ["dubitative", "<big>dubitative</big>\n\n<b>dubitātīvē</b> <i>adv.</i> <i>Tert</i>\n= dubitanter."],
  ["dubitativus", "<big>dubitativus</big>\n\n<b>dubitātīvus, a, um</b> [dubito]\n<b>1)</b> сомнительный, недостоверный (sensus <i>Tert</i>);\n<b>2)</b> <i>грам.</i> выражающий сомнение (adverbia)."],
  ["dubitator", "<big>dubitator</big>\n\n<b>dubitātor, ōris</b> <i>m</i>\nсомневающийся (alicujus rei <i>Tert</i>)."],
  ["dubito", "<big>dubito</big>\n\n<b>dubito, āvī, ātum, āre</b> [dubius]\n<b>1)</b> сомневаться, питать сомнения (de aliqua re <i>C etc.</i>): non dubito, quin venturus sit <i>C</i> не сомневаюсь, что он придёт; restat, ut hoc dubitemus <i>C</i> остаётся лишь следующее сомнение; non erat unde dubitarem <i>Aug</i> у меня не было причин сомневаться; an dea sim dubitor <i>O</i> сомневаются в том, что я богиня (<i>слова Латоны</i>); dubitat agnoscere matrem <i>St</i> (Ахилл) сомневается, действительно ли перед ним его мать;\n<b>2)</b> не решаться, колебаться, медлить (quid dubitas? <i>Cs</i>; non dubitavit affirmare <i>C</i>);\n<b>3)</b> быть сомнительным, нетвёрдым (manus dubitat <i>Q</i>): si fortuna dubitabit <i>L</i> если счастье пошатнётся (изменит)."],
  ["dubium", "<big>dubium</big>\n\n<b>dubium, ī</b> <i>n</i> [dubius]\n<b>1)</b> сомнение: sine (<i>или</i> procul) ; dubio <i>C, L etc.</i> без сомнения, несомненно; nihil est dubii (quin) <i>Sen</i> нет никакого сомнения (что); in dubio ponere aliquid <i>C etc.</i> считать что-л. сомнительным; nunquam mihi d. fuit de aliqua re <i>C</i> у меня никогда не было сомнения насчёт чего-л.; in d. vocare (revocare) <i>C</i> делать сомнительным, подвергать сомнению;\n<b>2)</b> критическое положение, опасность (esse in dubio, in d. venire <i>Ter, C etc.</i>): in d. devocare <i>Cs</i> подвергнуть опасности."],
  ["dubius", "<big>dubius</big>\n\n<b>dubius, a, um</b> [duo]\n<b>1)</b> сомневающийся, нерешительный, колеблющийся (mens <i>V</i>; d. sum quid verum sit <i>C</i>): d. quid faciat <i>H, O</i> не знающий (не зная), что делать; d. sententiae <i>L</i>, animi <i>bAl</i> <i>или</i> mentis <i>O</i> не знающий на что решиться <i>или</i> что подумать; inter spem metumque d. <i>V</i> колеблющийся между надеждой и страхом;\n<b>2)</b> сомнительный, подлежащий сомнению, ненадёжный (victoria dubia <i>Cs</i>; socii dubii <i>L</i>): nox dubia (dubiae crepuscula lucis) <i>O</i> вечерние сумерки; d. dies <i>PJ</i> <i>или</i> dubia lux <i>O</i> рассвет; dubium caelum <i>V</i> пасмурное небо; annus haud dubiis consulibus <i>L</i> год, консулы которого достоверно известны; dubio Fortuna stans in orbe <i>O</i> Фортуна, стоящая на непостоянном (<i>т. е.</i> вечно вращающемся) колесе;\n<b>3)</b> трудный, затруднительный, опасный, критический (res <i>Pl, Sl etc.</i>; tempora <i>H</i>): mons ascensu d. <i>Prp</i> гора с опасным подъёмом (опасная для восхождения); aeger d. <i>O</i> опасно больной; dubia cena <i>Ter, H, Aus</i> изобильнейшие яства (<i>затрудняющие выбор</i>)."],
  ["duc", "<big>duc</big>\n\n<b>dūc</b> <i>imper. sg. к</i>\nduco."],
  ["ducalis", "<big>ducalis</big>\n\n<b>ducālis, e</b> [dux]\nотносящийся к (принадлежащий) полководцу, предводительский (tunica <i>Vop</i>)."],
  ["ducaliter", "<big>ducaliter</big>\n\n<b>ducāliter</b> [ducalis]\nпо-полководчески, как полководец (antecedere, administrare rem militarem <i>Sid</i>)."],
  ["ducatio", "<big>ducatio</big>\n\n<b>ducātio, ōnis</b> <i>f</i> [duco]\nпредводительство, руководство <i>Tert</i>."],
  ["ducator", "<big>ducator</big>\n\n<b>ducātor, ōris</b> <i>m</i> [duco]\nруководитель (civitatis <i>Tert</i>); начальник корабля (= magister navis) <i>Dig</i>."],
  ["ducatrix", "<big>ducatrix</big>\n\n<b>ducātrīx, īcis</b> <i>f</i>\nпредводительница (vitiorum ducatrices iracundia et libido <i>Ap</i>)."],
  ["ducatus", "<big>ducatus</big>\n\n<b>ducātus, ūs</b> <i>m</i> [duco]\n<b>1)</b> должность и звание предводителя, начальство, командование <i>Just, Su, Fl</i>;\n<b>2)</b> направление, течение, ход (temporum <i>Tert</i>)."],
  ["duce", "<big>duce</big>\n\n<b>dūce</b> <i>арх.</i> <i>Pl</i>\n= duc."],
  ["ducena", "<big>ducena</big>\n\n<b>ducēna, ae</b> <i>f</i> [ducenarius II]\nдолжность дуценария <i>CJ</i>."],
  ["ducenaria", "<big>ducenaria</big>\n\n<b>ducēnāria, ae</b> <i>f</i> [ducenarius]\nдолжность procurator ducenarius (<i>см.</i>) <i>Ap</i>."],
  ["ducenarius", "<big>ducenarius</big>\n\n<b>I ducēnārius, a, um</b> [duceni]\nдвухсотенный, содержащий 200: ducenarium pondus <i>PM</i> 200 фунтов; lanx ducenaria <i>Pt</i> блюдо весом в 200 фунтов; judex d. <i>Su</i> судья, избранный из граждан с имущественным цензом всего в 200 тыс. сестерциев, <i>т. е.</i> которому подсудны лишь мелкие дела; procurator d. (PROC. CC) <i>Su</i> прокуратор с окладом в 200 тысяч сестерциев.\n\n<b>II ducēnārius, ī</b> <i>m</i>\nдуценарий, командир отряда в 200 человек (в две центурии) <i>Veg</i>."],
  ["duceni", "<big>duceni</big>\n\n<b>ducēnī, ae, a</b> (<i>gen.</i> <i>иногда</i> um)\n<b>1)</b> <i>adj. num. distr.</i> по двести <i>Hirt, L etc.</i>;\n<b>2)</b> двести <i>PM, M</i>."],
  ["ducentesima", "<big>ducentesima</big>\n\n<b>ducentēsima, ae</b> <i>f</i> [ducentesimus]\n(<i>sc.</i> pars) налог в полпроцента <i>T, Su</i>."],
  ["ducentesimus", "<big>ducentesimus</big>\n\n<b>ducentēsimus, a, um</b> <i>adj. num. ord.</i>\nдвухсотый <i>T etc.</i>"],
  ["ducenti", "<big>ducenti</big>\n\n<b>ducentī, ae, a</b> (<i>gen. pl.</i> <i>иногда</i> um) <i>adj. num. card.</i>\n(duo + centum)\n<b>1)</b> двести <i>C etc.</i>;\n<b>2)</b> неопределённо большое число, множество <i>Pl, Ctl, C etc.</i>"],
  ["ducenties", "<big>ducenties</big>\n\n<b>ducentiēs (-iēns)</b> <i>adv. num.</i> [ducenti]\n<b>1)</b> двести раз <i>C etc.</i>;\n<b>2)</b> множество раз <i>Ctl</i>."],
  ["ducentum", "<big>ducentum</big>\n\n<b>ducentum</b> <i>num. indecl.</i> [duo + centum]\nдвести <i>LM, Col</i>."],
  ["ducianus", "<big>ducianus</big>\n\n<b>I duciānus, a, um</b> [dux]\nполководческий (officium, apparitores <i>CJ</i>).\n\n<b>II duciānus, ī</b> <i>m</i>\nадъютант <i>или</i> ординарец полководца <i>CTh</i>."],
  ["ducis", "<big>ducis</big>\n\n<b>I ducis</b> <i>gen. к</i>\ndux.\n\n<b>II dūcis</b> <i>2 л. sg. praes. к</i>\nduco."],
  ["duco", "<big>duco</big>\n\n<b>dūco, dūxī, ductum, ere</b>\n<b>1)</b> водить, вести (aliquem secum <i>Pl</i>; aliquem intro ad aliquem <i>Ter</i>; equum loro <i>L</i>; iter ducit ad urbem <i>O</i>): d. choros <i>H</i> водить хороводы; d. funera, pompam <i>O</i> (exsequias <i>PM</i>) следовать в похоронной процессии; d. aliquem ad mortem <i>C</i> вести кого-л. на казнь; se d. ad aliquem <i>Pl</i> отправиться к кому-л.; d. curru aliquem victorem <i>H</i> везти кого-л. как победителя в колеснице; d. originem ab aliquo <i>H</i> вести свой род от кого-л.; Janus, quod ab eundo nomen est ductum <i>C</i> Янус, имя которого произведено от глагола ire;\n<b>2)</b> <i>воен.</i> вести за собой (exercitum ab Allobrogibus in Segusiavos <i>Cs</i>); предводительствовать, командовать (d. primum pilum <i>Cs</i>); d. ordinem <i>Cs</i> командовать центурией || продвигаться (с войском) (contra hostes <i>и</i> ad hostem <i>L</i>);\n<b>3)</b> стоять во главе, быть главным (первым) (d. familiam <i>C</i>; d. classem discipulorum <i>Q</i>);\n<b>4)</b> проводить (aquam per fundum alicujus <i>C</i>; fossam <i>Cs, L</i>; vallum <i>Cs, L</i>; sulcum <i>Col</i>; lineam, orbem <i>Q</i>); прокладывать (viam <i>L</i>);\n<b>5)</b> производить, выделывать, создавать (vivos de marmore vultus <i>V</i>; ocreas argento <i>V</i>; effigiem alicujus aere <i>Ap</i>): lateres d. <i>Vtr</i> изготовлять кирпичи || слагать, сочинять (epos <i>H</i>; versus, carmina <i>O</i>);\n<b>6)</b> тянуть, вытягивать, протягивать: d. vagina ferrum <i>O</i> (ensem <i>Sil</i>) извлекать меч из ножен; ducto mucrone <i>V</i> с обнажённым мечом; diversa bracchia d. <i>V</i> широко раскинуть (распростереть) руки; d. remos <i>O</i> работать вёслами, грести; nunc volucrem laqueo, nunc piscem d. hamo <i>O</i> ловить то птицу силками, то рыбу удочкой || затягивать, стягивать (frena manu <i>O</i>); втягивать, вдыхать (spiritum naribus <i>Vr</i> <i>и</i> per fauces <i>Sen</i>; aerem <i>и</i> animum spiritu <i>C</i> — <i>ср.</i> 15): vitam et spiritum d. aliqua re <i>C</i> жить и дышать благодаря чему-л.; d. ilia <i>H</i> тяжело дышать || тянуть, вынимать (sortes <i>C</i>): sorte duci <i>C, V, T, Su</i> быть вытянутым по жребию; d. sanguinem <i>PM</i> пускать кровь; d. alvum <i>CC</i> очищать кишечник; d. pocula Lesbii <i>H</i> пить лесбосское вино; d. fletum <i>Prp</i> испустить стон; longas in fletum d. voces <i>V</i> жалобно ныть (<i>о филине</i>) || притягивать (magnes ducit ferrum <i>Prp</i>); стягивать, морщить, кривить (os <i>C</i>; vultus <i>M</i>); тянуть, тащить (capellam <i>V</i>; navem per adversas undas <i>O</i>); тянуть, затягивать (d. rem in noctem, bellum in hiemem <i>Cs</i>): tempus d. <i>C, Nep</i> стараться выиграть время; verba longa mora d. <i>Prp</i> растягивать слова; diem ex die d. <i>Cs</i> откладывать со дня на день; d. aliquem <i>Ter, Prp, Cs</i> томить кого-л. постоянными отсрочками;\n<b>7)</b> поглаживать, массировать (digitulos alicui <i>Sen</i>): d. ubera <i>O</i> доить;\n<b>8)</b> (<i>чаще</i> d. domum, d. in matrimonium <i>и</i> d. uxorem <i>Pl, Ter, Cs, C etc.</i>) брать в жёны, жениться (d. alicujus filiam <i>C</i>): d. ex plebe <i>или</i> d. plebejam <i>L</i> жениться на плебейке;\n<b>9)</b> принимать, приобретать, получать (purpureum colorem <i>O</i>): d. formam <i>O</i> приобретать (человеческие) очертания; d. rimam <i>O</i> дать трещину; pallorem d. <i>O</i> покрываться бледностью; cicatricem d. <i>O</i> рубцеваться, заживать; situm d. <i>Q</i> ржаветь; nomina alicujus d. <i>H</i> получить чьё-л. имя, <i>т. е.</i> быть названным по кому-л.;\n<b>10)</b> прясть (lanam, stamina <i>O</i>; pensa manu <i>J</i>);\n<b>11)</b> (<i>о времени</i>) проводить (aetatem in litteris <i>C</i>; jucundissimis sermonibus nox ducebatur <i>PJ</i>): sine quibus vita non ducitur <i>Sen</i> то, без чего жизнь невозможна; somnos d. <i>V</i> предаваться сну;\n<b>12)</b> приводить (aliquem secum <i>C, Nep</i>); наводить, навевать (soporem <i>Tib</i>);\n<b>13)</b> водить за нос, дурачить (aliquem dictis <i>Ter</i>; promissis <i>Prp</i>);\n<b>14)</b> устраивать, давать (ludos <i>T</i>): d. alapam alicui <i>ирон. Ph</i> шлёпнуть кого-л.; colaphum alicui d. <i>Q</i> ударить кого-л. кулаком;\n<b>15)</b> склонять, побуждать (oratio tua me ducit ad credendum <i>C</i>): caritate patriae ductus <i>Nep</i> движимый любовью к отечеству; errore ductus <i>C</i> поддавшись заблуждению; gloria duci <i>C</i> руководствоваться жаждой славы; d. animam (spiritum) <i>L</i> томиться, изнывать (<i>ср.</i> 6);\n<b>16)</b> привлекать (fabulae ducere solent animos <i>Q</i>);\n<b>17)</b> (<i>тж.</i> d. rationem <i>C etc.</i>) подсчитывать, насчитывать, вести счёт, считать (nonaginta milia medimnum duximus <i>C</i>);\n<b>18)</b> принимать в расчёт, учитывать (rationem officii, non commodi d. <i>C</i>; rationem salutis suae <i>C</i>): non duco in hac ratione eos, qui... <i>C</i> я не включаю сюда тех, которые...; d. suam rationem <i>C</i> заботиться о своей выгоде;\n<b>19)</b> полагать: d. aliquem in hostium numero <i>C</i> считать кого-л. врагом; d. aliquem victorem <i>Nep</i> считать кого-л. победителем; d. aliquid inter prospera <i>T</i> относить что-л. к числу счастливых обстоятельств; d. aliquem despicatui <i>C</i> презирать кого-л.; d. aliquid in malis <i>C</i> отнести что-л. к числу зол; d. aliquid pro nihilo <i>C</i> ни во что не ставить что-л.; nullum beneficium aliquid d. <i>Pl</i> не считать что-л. благодеянием; d. aliquid parvi (pluris) <i>C</i> мало (более) ценить, уважать что-л.; inter curas et seria d. habendum <i>J</i> серьёзно задаваться (каким-л.) вопросом;\n<b>20)</b> вменять: d. aliquid honori <i>Sl</i> (laudi <i>Nep</i>) считать что-л. достойным уважения (славы)."],
  ["ductarius", "<big>ductarius</big>\n\n<b>ductārius, a, um</b> [ducto]\nтяговый, натяжной (funis <i>Vtr</i>)."],
  ["ductilis", "<big>ductilis</big>\n\n<b>ductilis, e</b> [duco]\n<b>1)</b> растяжимый, тягучий, ковкий (aes <i>PM</i>; aurum <i>Vlg</i>);\n<b>2)</b> тянутый <i>или</i> чеканный (tubae argenteae <i>Vlg</i>);\n<b>3)</b> могущий быть отведённым (flumen riguae aquae <i>M</i>)."],
  ["ductim", "<big>ductim</big>\n\n<b>ductim</b> [duco]\n<b>1)</b> волоком, таща (по земле) (opus in vinea facere <i>Col</i>);\n<b>2)</b> большими глотками, залпом (invergere in se liquores <i>Pl</i>)."],
  ["ductio", "<big>ductio</big>\n\n<b>ductio, ōnis</b> <i>f</i> [duco]\n<b>1)</b> ведение, увод (hominis, jus ductionis <i>Dig</i>);\n<b>2)</b> отведение (aquae <i>Vtr</i>);\n<b>3)</b> очистка (alvi <i>CC</i>);\n<b>4)</b> натягивание (rudentium <i>Vtr</i>)."],
  ["ductito", "<big>ductito</big>\n\n<b>ductito, āvī, ātum, āre</b> [<i>frequ. к</i> ducto]\n<b>1)</b> вести, уводить <i>Pl, Eccl</i>;\n<b>2)</b> приводить в дом жену, жениться (aliquam <i>Pl</i>);\n<b>3)</b> провести, обмануть (aliquem <i>Pl</i>)."],
  ["ducto", "<big>ducto</big>\n\n<b>ducto, āvī, ātum, āre</b> [<i>intens. к</i> duco]\n<b>1)</b> вести, водить (exercitum per saltuosa loca <i>Sl</i>);\n<b>2)</b> предводительствовать, командовать (exercitum <i>Sl, T</i>);\n<b>3)</b> приводить в дом (aliquam <i>Pl, Ter</i>);\n<b>4)</b> обмануть, провести, одурачить (aliquem <i>Pl</i>)."],
  ["ductor", "<big>ductor</big>\n\n<b>ductor, ōris</b> <i>m</i> [duco]\n<b>1)</b> проводник, вожатый (itineris <i>L</i>); вожак (leonis <i>Lcr</i>);\n<b>2)</b> предводитель, командир (classis <i>V</i>): ordinum d. <i>L</i> центурион;\n<b>3)</b> вождь, полководец (ductores, quos Africa terra alit <i>V</i>); d. aquarum Tibris <i>St</i> важнейшая из рек Тибр;\n<b>4)</b> мастер: d. ferreus <i>Priap</i> кузнец."],
  ["ductus", "<big>ductus</big>\n\n<b>I ductus, a, um</b> <i>part. pf. к</i>\nduco.\n\n<b>II ductus, ūs</b> <i>m</i> [duco]\n<b>1)</b> ведение (d. falcis <i>Col</i>);\n<b>2)</b> сморщивание, искривление, искажение (labearum <i>AG</i>);\n<b>3)</b> проведение, начертание, форма (litterae <i>Col, Q</i>);\n<b>4)</b> постройка (muri <i>C</i>);\n<b>5)</b> черты, выражение (oris <i>C</i>);\n<b>6)</b> направление, ход (macularum <i>PM</i>);\n<b>7)</b> предводительствование, командование: alicujus ductu <i>C, Cs etc.</i> под чьим-л. командованием;\n<b>8)</b> отвод (aquarum <i>C etc.</i>); <i>тж.</i> водоотводный канал <i>PJ, Frontin</i>;\n<b>9)</b> последовательность, внутренняя связь: est d. rei credibilis <i>Q</i> внутренняя связь произведения делает (его) правдоподобным;\n<b>10)</b> <i>ритор.</i> период <i>Q</i>."],
  ["dudum", "<big>dudum</big>\n\n<b>dūdum</b>\n<b>1)</b> перед тем, только что, прежде (id quod tibi d. videbatur <i>C</i>): (jam) d. <i>V, O etc.</i> сейчас же, тотчас же; ut d. hinc abii <i>Pl</i> как только я отсюда ушёл;\n<b>2)</b> давно: jam d. <i>Ter, C etc.</i> уже давно; quam d.? <i>Pl, Ter, C</i> давно ли?; haud d. <i>Pl etc.</i> недавно."],
  ["Duell-", "<big>Duell-</big>\n\n<b>Duēl(l)-</b> <i>v. l.</i>\n= Duil(l)-."],
  ["duella", "<big>duella</big>\n\n<b>duella, ae</b> <i>f</i> [duo]\nдве sextulae, <i>т. е.</i> одна треть унции (9,096 <i>г</i>) <i>Frontin, Is</i>."],
  ["duellator", "<big>duellator</big>\n\n<b>duellātor, ōris</b> <i>m</i> <i>арх.</i> <i>Pl</i>\n= bellator 1."],
  ["duellicus", "<big>duellicus</big>\n\n<b>duellicus, a, um</b> <i>арх. Pl, Lcr</i>\n= bellicus."],
  ["duellis", "<big>duellis</big>\n\n<b>duellis, is</b> <i>m</i> [duellum]\nвоюющая сторона, противник <i>Eccl</i>."],
  ["Duellona", "<big>Duellona</big>\n\n<b>Duellōna, ae</b> <i>f</i> <i>SCB</i>\n= Bellona."],
  ["duellum", "<big>duellum</big>\n\n<b>duellum, ī</b> <i>n</i> <i>арх. Pl, H etc.</i>\n(= bellum) война: domi duellique <i>Pl</i> в мирное время и на войне."],
  ["Duillius", "<big>Duillius</big>\n\n<b>Duīl(l)ius, a, um</b>\nДуиллий, <i>римск. nomen; наиболее известны</i>:\n<b>1)</b> M. (?) D., <i>народный трибун, по совету которого плебеи ушли на Mons Sacer</i> <i>L</i>;\n<b>2)</b> C. D., <i>консул в 260 г. до н. э.; одержал победу над карфагенским флотом в I Пунической войне, в честь чего была воздвигнута Columna Rostrata</i> <i>C, Fl, Sil</i>."],
  ["duim", "<big>duim</big>\n\n<b>duim, duīs, duit, duint</b> <i>арх. <i>praes.</i> opt.</i> <i>conjct. к</i>\ndo (= dem, des, det, dent)."],
  ["duini", "<big>duini</big>\n\n<b>duīnī</b> <i>Vr</i>\n= bini."],
  ["duipes", "<big>duipes</big>\n\n<b>duipēs</b> <i>Naev</i>\n= bipes."],
  ["duis", "<big>duis</big>\n\n<b>duīs</b>\n<b>1)</b> <i>см.</i> duim;\n<b>2)</b> <i>C</i> = bis."],
  ["duitae", "<big>duitae</big>\n\n<b>duītae, ārum</b> <i>m</i> [duo]\nдвоебожники, секта дуалистов <i>Hier</i>."],
  ["duitas", "<big>duitas</big>\n\n<b>duitās, ātis</b> <i>f</i> [duo]\nчисло два <i>Dig</i>."],
  ["duitor", "<big>duitor</big>\n\n<b>duitor</b> <i>арх. LXIIT ap. PM</i> (= dator) <i>imper. pass. к</i>\ndo (<i>v. l.</i>)."],
  ["dulce", "<big>dulce</big>\n\n<b>I dulce, is</b> <i>n</i> [dulcis]\nсладкое (блюдо, <i>преим.</i> вино) <i>Pl, O, PM</i>.\n\n<b>II dulcē</b> <i>Ctl, Tib, H adv.</i>\n= dulciter."],
  ["dulcedo", "<big>dulcedo</big>\n\n<b>dulcēdo, inis</b> <i>f</i> [dulcis]\n<b>1)</b> сладкий вкус, сладость (mellis <i>Ap</i>; vini <i>PM</i>);\n<b>2)</b> прелесть, очарование (doctae linguae, vocis <i>O</i>; otii <i>T</i>): dulcedine captus <i>O</i> очарованный || привлекательность, удовольствие (cum aliquo loquendi <i>PJ</i>);\n<b>3)</b> влечение (perpotandi insana d. <i>QC</i>): faucium d. <i>Sen</i> страсть лакомиться."],
  ["dulcesco", "<big>dulcesco</big>\n\n<b>dulcēsco, —, —, ere</b> [dulcis]\n<b>1)</b> приобретать сладкий вкус, становиться сладким (uva maturata dulcescit <i>C</i>);\n<b>2)</b> делаться пресным, терять солёность (pluviā dulcescit sal <i>PM</i>)."],
  ["dulcia", "<big>dulcia</big>\n\n<b>dulcia, ium</b> <i>n</i> [dulcis]\nсладости <i>Vop, Lampr</i>."],
  ["dulciarius", "<big>dulciarius</big>\n\n<b>dulciārius, ī</b> <i>m</i> [dulcis] (<i>тж.</i> pistor d. <i>M, Ap</i>)\nпирожник, кондитер <i>Lampr, Treb, Veg</i>."],
  ["dulciculus", "<big>dulciculus</big>\n\n<b>dulciculus, a, um</b> [<i>demin. к</i> dulcis]\nсладенький; сладковатый (potio <i>C</i>): d. caseus <i>перен.</i> <i>Pl</i> любимейшее блюдо, наслаждение, радость."],
  ["dulcifer", "<big>dulcifer</big>\n\n<b>dulcifer, fera, ferum</b> [dulcis + fero]\nимеющий сладкий вкус, сладкий (ficus <i>Enn</i>; cantharus <i>Pl</i>)."],
  ["dulcifico", "<big>dulcifico</big>\n\n<b>dulci–fico, —, —, āre</b>\nделать сладким (aliquid <i>Eccl</i>)."],
  ["dulciloquus", "<big>dulciloquus</big>\n\n<b>dulciloquus, a, um</b> [dulcis + loquor]\nсладкоречивый (calamus <i>Ap, Aus</i>; libri <i>Aug</i>; ars Orphei <i>Sid</i>)."],
  ["dulciola", "<big>dulciola</big>\n\n<b>dulciola, ōrum</b> <i>n</i> [dulcis]\nсладкие пирожки, пирожное (d. mellita <i>Ap</i>)."],
  ["dulciorelocus", "<big>dulciorelocus</big>\n\n<b>dulciōrelocus (-loquus), ī</b> <i>m</i> [dulcis + os + loquor]\nзлатоуст, красноречивый оратор <i>AG</i>."],
  ["dulcis", "<big>dulcis</big>\n\n<b>dulcis, e</b>\n<b>1)</b> сладкий (pomum <i>Lcr</i>; uva <i>O</i>; sapor <i>H</i>);\n<b>2)</b> свежий (olivum <i>H</i>); приятный на вкус <i>или</i> сочный (cibus <i>PM</i>);\n<b>3)</b> пресный (aqua <i>Lcr etc.</i>; unda <i>O</i>); пресноводный (pisces <i>Vr</i>); содержащий пресную воду (piscina <i>Vr</i>);\n<b>4)</b> прелестный, очаровательный, сладостный, приятный (nomen libertatis <i>C</i>; somnus <i>V, Ap</i>; carmen <i>Q</i>); нежный, милый (amicus <i>C, H etc.</i>; liberi <i>H</i>); dulcissime rerum! <i>H</i> милейший мой!"],
  ["dulcisonus", "<big>dulcisonus</big>\n\n<b>dulci–sonus, a, um</b>\nсладкозвучный, приятный (murmur <i>Sid</i>)."],
  ["dulcitas", "<big>dulcitas</big>\n\n<b>dulcitās, ātis</b> <i>f</i> [dulcis]\nсладкий вкус, сладость (fici <i>Ap</i>); <i>перен.</i> нежность, кротость (animae <i>Acc</i>)."],
  ["dulciter", "<big>dulciter</big>\n\n<b>dulciter</b> [dulcis]\nсладко, сладостно, приятно <i>C, Q etc.</i>"],
  ["dulcitudo", "<big>dulcitudo</big>\n\n<b>dulcitūdo, inis</b> <i>f</i>\n<b>1)</b> <i>C etc.</i> = dulcedo;\n<b>2)</b> мягкость, умеренность (usurarum <i>Dig</i>)."],
  ["dulco", "<big>dulco</big>\n\n<b>dulco, —, —, āre</b> [dulcis]\nделать сладким (aliquid <i>Sid</i>)."],
  ["dulcor", "<big>dulcor</big>\n\n<b>dulcor, ōris</b> <i>m</i> <i>Vlg, Tert</i>\n= dulcedo."],
  ["dulcoro", "<big>dulcoro</big>\n\n<b>dulcōro, —, ātum, āre</b> <i>Vlg, Eccl</i>\n= dulco."],
  ["Dulgibini", "<big>Dulgibini</big>\n\n<b>Dulgibīnī</b> <i>и</i> <b>Dulgubniī, ōrum</b> <i>m</i>\nдульгибины, <i>германское племя между реками Amisia <i>и</i> Lupia</i> <i>T</i>."],
  ["dulice", "<big>dulice</big>\n\n<b>dūlicē</b> (<i>греч.</i>)\nпо-рабски, как раб <i>Pl</i>."],
  ["Dulichia", "<big>Dulichia</big>\n\n<b>Dūlichia, ae</b> <i>f</i> <i>и</i> <b>Dūlichium, ī</b> <i>n</i>\nДулихия, <i>о-в к юго.-вост. от Итаки, у побережья Акарнании, перед устьем Ахелоя</i> <i>Prp, V, O, Mela</i>."],
  ["Dulichius", "<big>Dulichius</big>\n\n<b>Dūlichius, a, um</b> [Dulichia]\nдулихийский: d. dux <i>O</i> = Ulixes; <i>перен.</i> Одиссеев (<i>т. к. по преданию о-в Дулихия относился к владениям Одиссея</i>) (rates <i>V</i>; Zacynthus <i>Sil</i>)."],
  ["dum", "<big>dum</big>\n\n<b>I dum</b> <i>conj.</i>\n<b>1)</b> между тем как, в то время как, пока: d. haec geruntur <i>или</i> aguntur (quae d. fiunt) <i>Cs, C, L etc.</i> пока (всё) это происходит;\n<b>2)</b> до тех пор пока, покуда, пока (не): haec faciebam, d. licebat <i>C</i> я делал это (до тех пор), пока было можно; delibera hoc, d. ego redeo (rediero) <i>Ter</i> подумай об этом, пока я не вернусь; d. vires annique sinunt <i>O</i> пока позволяют силы и годы; d. defervescat ira <i>C</i> пока не утихнет гнев;\n<b>3)</b> только бы: recta et honesta negligunt, d. potentiam consequantur <i>C</i> (те, которые) пренебрегают правдой и честностью, лишь бы добиться могущества; occidat, dum imperet <i>T</i> пусть убьёт (меня), только бы царствовал (<i>ответ Агриппины звездочётам а судьбе её сына Нерона</i>).\n\n<b>II dum</b> <i>adv.</i> (<i>с</i> non, nullus, nihil, vix) (<i>тж. слитно</i>)\nещё: nondum <i>C etc.</i> ещё не; neque d. satis <i>C</i> ещё недостаточно; nullus d. <i>L etc.</i> никто ещё; nedum (<i>sc.</i> dicam) <i>C etc.</i> не говоря уже.\n\n<b>III dum</b> <i>с <i>imper.</i> и др.</i> (<i>тж. слитно</i>)\nже: dicdum <i>Pl</i> говори (скажи) же; age (agite) dum <i>C, L etc.</i> ну же (делай, делайте); ehodum <i>Ter</i> послушай же; quidum? <i>Pl</i> как (же) так?"],
  ["dumetum", "<big>dumetum</big>\n\n<b>dūmētum, ī</b> <i>n</i> [dumus]\n<b>1)</b> место, покрытое тернистым кустарником, чаща, заросли <i>C, V, H etc.</i>;\n<b>2)</b> трудности, дебри (dumeta Stoicorum <i>C</i>)."],
  ["dummodo", "<big>dummodo</big>\n\n<b>dum–modo</b> <i>conj.</i> (<i>тж. раздельно</i>)\n<b>1)</b> пока (не): d. spument dolia musto <i>Prp</i> пока не запенятся бочки вином;\n<b>2)</b> если только, только бы, пока, покуда <i>Pl, C etc.</i>: decretum est pati, dum illum modo habeam mecum <i>Ter</i> я решил покориться, только бы он был со мной."],
  ["Dumnorix", "<big>Dumnorix</big>\n\n<b>Dumnorix, īgis</b> <i>m</i>\nДумнориг, <i>эдуй, глава национальной (антиримской) партии, брат и противник Дивитиака</i> <i>Cs</i>."],
  ["dumnulus", "<big>dumnulus</big>\n\n<b>dumnulus, ī</b> <i>m</i> <i>Sid</i>\n= dominulus."],
  ["dumosus", "<big>dumosus</big>\n\n<b>dūmōsus, a, um</b> [dumus]\nпокрытый тернистым кустарником (saxa <i>O</i>; rupes <i>V</i>)."],
  ["dumtaxat", "<big>dumtaxat</big>\n\n<b>dumtaxat (duntaxat)</b> <i>adv.</i> [dum + taxo]\n<b>1)</b> насколько, поскольку: d. ut hoc promittere possis <i>Lcr</i> насколько ты можешь за это поручиться;\n<b>2)</b> только, не более (potestatem habere d. annuam <i>C</i>): excepto d. uno <i>Su</i> за исключением лишь одного; non d..., sed <i>L etc.</i> не только..., но и;\n<b>3)</b> по крайней мере, хотя бы: d. aspectu <i>C</i> по крайней мере на вид; denique sit quodvis simplex d. et unum <i>H</i> так пусть же каждому (произведению) будут присущи, по крайней мере, простота и единство."],
  ["dumus", "<big>dumus</big>\n\n<b>dūmus, ī</b> <i>m</i>\nгустая заросль, тернистый кустарник <i>C, V, Sen etc.</i>"],
  ["duntaxat", "<big>duntaxat</big>\n\n<b>duntaxat</b> <i>v. l.</i>\n= dumtaxat."],
  ["duo", "<big>duo</big>\n\n<b>duo, duae, duo</b> (<i>gen.</i> <i>иногда</i> duum, <i>в acc.</i> duo) <i>adj. num. card.</i>\nдва, двое <i>Pl, C etc.</i>; <i>часто</i> (= ambo) оба <i>Pl etc.</i>"],
  ["duodecajugum", "<big>duodecajugum</big>\n\n<b>duō–decajugum, ī</b> <i>n</i>\nзапряжка в 12 лошадей <i>Ambr</i>."],
  ["duodecas", "<big>duodecas</big>\n\n<b>duōdecas, adis</b> <i>f</i> (<i>греч.</i>)\nчисло 12, дюжина <i>Tert</i>."],
  ["duodecasyllabus", "<big>duodecasyllabus</big>\n\n<b>duōdeca–syllabus, a, um</b> <i>стих.</i>\nдвенадцатисложный."],
  ["duodecennium", "<big>duodecennium</big>\n\n<b>duodecennium, ī</b> <i>n</i> [duodecim + annus]\nдвенадцатилетие <i>CTh</i>."],
  ["duodecies", "<big>duodecies</big>\n\n<b>duo–deciēs (duodeciēns)</b> <i>adv. num.</i>\nдвенадцать раз <i>C</i>."],
  ["duodecim", "<big>duodecim</big>\n\n<b>duodecim</b> <i>adj. num. card.</i> [duo + decem]\nдвенадцать: d. (tabulae) <i>C</i> законы XII таблиц."],
  ["duodecimo", "<big>duodecimo</big>\n\n<b>duodecimō</b> <i>adv.</i>\nв 12-й раз (d. consul <i>Capit</i>)."],
  ["duodecimus", "<big>duodecimus</big>\n\n<b>duo–decimus, a, um</b> <i>adj. num. ord.</i>\nдвенадцатый <i>Cs, C etc.</i>"],
  ["duodenarius", "<big>duodenarius</big>\n\n<b>duodēnārius, a, um</b> [duodeni]\nсодержащий двенадцать (numerus d. <i>Vr, Tert etc.</i>): fistula duodenaria <i>Frontin</i> двенадцатидюймовая (в диаметре) труба."],
  ["duodeni", "<big>duodeni</big>\n\n<b>duodēnī, ae, a</b> <i>adj. num. distr.</i> (<i>gen. pl. Cs</i> um)\n<b>1)</b> по двенадцати <i>Cs, C etc.</i>;\n<b>2)</b> (= duodecim): duodena astra <i>V</i> <i>или</i> signa <i>O</i> двенадцать знаков Зодиака."],
  ["duodennis", "<big>duodennis</big>\n\n<b>duodennis, e</b>\nдвенадцатилетний (puer <i>Aug</i>)."],
  ["duodenonaginta", "<big>duodenonaginta</big>\n\n<b>duo–dē–nōnāgintā</b> <i>adj. num. card.</i>\nвосемьдесят восемь <i>PM</i>."],
  ["duodenus", "<big>duodenus</big>\n\n<b>duo–dēnus, a, um</b>\nдвенадцатикратный (d. labor Herculeus <i>Ap</i>)."],
  ["duodeoctoginta", "<big>duodeoctoginta</big>\n\n<b>duo–dē–octōgintā</b> <i>adj. num. card.</i>\nсемьдесят восемь <i>PM</i>."],
  ["duodequadrageni", "<big>duodequadrageni</big>\n\n<b>duo–dē–quadrāgēnī, ae, a</b> <i>adj. num. distr.</i>\nпо тридцати восьми <i>PM</i>."],
  ["duodequadragesimus", "<big>duodequadragesimus</big>\n\n<b>duo–dē–quadrāgēsimus, a, um</b> <i>adj. num. ord.</i>\nтридцать восьмой <i>L</i>."],
  ["duodequadraginta", "<big>duodequadraginta</big>\n\n<b>duo–dē–quadrāgintā</b> <i>adj. num. card.</i>\nтридцать восемь <i>C</i>."],
  ["duodequinquageni", "<big>duodequinquageni</big>\n\n<b>duo–dē–quīnquāgēnī, ae, a</b> <i>adj. num. distr.</i>\nпо сорока восьми <i>PM</i>."],
  ["duodequinquagesimus", "<big>duodequinquagesimus</big>\n\n<b>duo–dē–quīnquāgēsimus, a, um</b> <i>adj. num. ord.</i>\nсорок восьмой <i>C</i>."],
  ["duodequinquaginta", "<big>duodequinquaginta</big>\n\n<b>duo–dē–quīnquāgintā</b> <i>adj. num. card.</i>\nсорок восемь <i>Col</i>."],
  ["duodesexagesimus", "<big>duodesexagesimus</big>\n\n<b>duo–dē–sexāgēsimus, a, um</b> <i>adj. num. ord.</i>\nпятьдесят восьмой <i>VP</i>."],
  ["duodesexaginta", "<big>duodesexaginta</big>\n\n<b>duo–dē–sexāgintā</b> <i>adj. num. card.</i>\nпятьдесят восемь <i>PM</i>."],
  ["duodetricesimus", "<big>duodetricesimus</big>\n\n<b>duo–dē–trīcēsimus, a, um</b> <i>adj. num. ord.</i>\nдвадцать восьмой <i>Vr ap. AG, Aug</i>."],
  ["duodetricies", "<big>duodetricies</big>\n\n<b>duo–dē–trīciēs (-trīciēns)</b> <i>adv. num.</i>\nдвадцать восемь раз <i>C</i>."],
  ["duodetriginta", "<big>duodetriginta</big>\n\n<b>duo–dē–trīgintā</b> <i>adj. num. card.</i>\nдвадцать восемь <i>L, Su</i>."],
  ["duodeviceni", "<big>duodeviceni</big>\n\n<b>duo–dē–vīcēnī, ae, a</b> <i>adj. num. distr.</i>\nпо восемнадцати <i>L</i>."],
  ["duodevicensimus", "<big>duodevicensimus</big>\n\n<b>duo–dē–vīcē(n)simus (-vīgēsimus), a, um</b> <i>adj. num. ord.</i>\nвосемнадцатый <i>Vr, PM</i>."],
  ["duodevicesimum", "<big>duodevicesimum</big>\n\n<b>duo–dē–vīcēsimum</b> <i>adv.</i>\nв восемнадцатый раз <i>MA</i>."],
  ["duodeviginti", "<big>duodeviginti</big>\n\n<b>duo–dē–vīgintī</b> <i>adj. num. card.</i>\nвосемнадцать <i>C etc.</i>"],
  ["duoetvicesimani", "<big>duoetvicesimani</big>\n\n<b>duo–et–vīcēsimānī, ōrum</b> <i>m</i>\nсолдаты 22-го легиона <i>T</i>."],
  ["duoetvicesimus", "<big>duoetvicesimus</big>\n\n<b>duo–et–vīcēsimus, a, um</b> <i>adj. num. ord.</i>\nдвадцать второй <i>T</i>."],
  ["duovir", "<big>duovir</big>\n\n<b>duovir, ī</b> <i>m</i> <i>v. l.</i>\n= duumvir."],
  ["dupla", "<big>dupla</big>\n\n<b>dupla, ae</b> <i>f</i> [duplus]\n(<i>sc.</i> pecunia) двойная цена (duplam dare <i>Pl, Sen</i>)."],
  ["duplaris", "<big>duplaris</big>\n\n<b>duplāris, e</b> [duplus]\nдвойной (numerus <i>Macr</i>): duplares milites <i>Veg</i> солдаты, получавшие двойной паёк (<i>в виде награды</i>)."],
  ["duplatio", "<big>duplatio</big>\n\n<b>duplātio, ōnis</b> <i>f</i> <i>Dig</i>\n= duplicatio."],
  ["duplex", "<big>duplex</big>\n\n<b>duplex, icis</b> [duo + plico]\n<b>1)</b> двойной (fossa, stipendium <i>Cs</i>); составной, сваренный из многих вещей (jus <i>H</i>); удвоенный: frumentum d. <i>L</i> удвоенный паёк хлеба;\n<b>2)</b> сложенный <i>или</i> сшитый вдвое (pannus <i>H</i>); состоящий из двух частей <i>или</i> разделённый на две части (lex <i>Q</i>); раздвоенный (folia palmae <i>PM</i>);\n<b>3)</b> плотный, толстый (amiculum <i>Nep</i>);\n<b>4)</b> двоедушный, двуличный, лицемерный, лукавый (Ulixes <i>H</i>);\n<b>5)</b> двусмысленный (verba <i>Q</i>);\n<b>6)</b> <i>поэт. pl.</i> (= uterque) оба: duplices oculi <i>Lcr</i> оба глаза; duplices palmae <i>V</i> обе руки."],
  ["duplicarius", "<big>duplicarius</big>\n\n<b>duplicārius, ī</b> <i>m</i> [duplex]\nполучающий двойное жалованье <i>или</i> довольствие (miles <i>Vr, L</i>)."],
  ["duplicatio", "<big>duplicatio</big>\n\n<b>duplicātio, ōnis</b> <i>f</i> [duplico]\nудвоение (columnarum <i>Vtr</i>; temporis <i>Dig</i>): d. radiorum <i>Sen</i> отражение (отблеск) лучей."],
  ["duplicato", "<big>duplicato</big>\n\n<b>duplicātō</b> [duplico]\nвдвое (больше <i>или</i> дольше) <i>PM</i>."],
  ["duplicator", "<big>duplicator</big>\n\n<b>duplicātor, ōris</b> <i>m</i> [duplico]\nудваивающий <i>Sid</i>."],
  ["duplicitas", "<big>duplicitas</big>\n\n<b>duplicitās, ātis</b> <i>f</i> [duplex]\n<b>1)</b> двойное количество, парность: d. aurium <i>Lact</i> два уха;\n<b>2)</b> двусмысленность, двузначность <i>Aug</i>;\n<b>3)</b> двоедушие, коварство <i>Aug</i>."],
  ["dupliciter", "<big>dupliciter</big>\n\n<b>dupliciter</b> [duplex]\n<b>1)</b> вдвое, вдвойне, двояко (d. delectatus sum tuis litteris <i>C</i>);\n<b>2)</b> по двум причинам (aliquid facere certare <i>Lcr</i>)."],
  ["duplico", "<big>duplico</big>\n\n<b>duplico, āvī, ātum, āre</b> [duplex]\n<b>1)</b> вдвое увеличивать, удваивать (numerum <i>C</i>): d. verba <i>C</i> повторять слова, <i>но тж.</i> <i>L</i> образовывать составные (двух коренные) слова;\n<b>2)</b> умножать, увеличивать (gloriam <i>C</i>; curam <i>Sl</i>): aliquid in pejus d. <i>Amm</i> представить что-л. в слишком мрачном свете, сгустить краски || удлинять (hastae modum <i>Nep</i>); возобновлять (bellum <i>Sl</i>);\n<b>3)</b> сгибать (poplitem <i>V</i>): corpus frigore duplicatum <i>VM</i> скорчившееся от холода тело."],
  ["duplio", "<big>duplio</big>\n\n<b>duplio, ōnis</b> <i>f</i> [duplus] <i>арх.</i>\nдвойное количество, двойная мера <i>LXIIT, PM</i>."],
  ["duplo", "<big>duplo</big>\n\n<b>I duplō</b> <i>adv.</i> [duplus]\nвдвое, вдвойне <i>Vlg</i>.\n\n<b>II duplo, —, —, āre</b>\nудваивать <i>Dig</i>."],
  ["duplum", "<big>duplum</big>\n\n<b>duplum, ī</b> <i>n</i> [duplus]\nдвойное количество: duplo major <i>PM</i> вдвое больший; poenam dupli subire (in duplum ire) <i>C</i> нести двойное наказание."],
  ["duplus", "<big>duplus</big>\n\n<b>duplus, a, um</b> [duplex]\nвдвое больший, двойной, удвоенный (intervallum <i>C</i>; pecunia <i>L</i>): duplam gratiam alicui debere <i>Ap</i> быть вдвойне обязанным кому-л."],
  ["dupondiarius", "<big>dupondiarius</big>\n\n<b>I dupondiārius, a, um</b> <i>adj.</i> [dupondius]\nравный дупондию (2 ассам) <i>Col</i>; <i>перен.</i> грошовый, дрянной (dominus, homo <i>Pt</i>).\n\n<b>II dupondiārius, ī</b> <i>m</i> (<i>sc.</i> nummus)\nмонета в 2 асса, двухассовик <i>PM</i>."],
  ["dupondium", "<big>dupondium</big>\n\n<b>dupondium, ī</b> <i>n</i> <i>Pt</i>\n= dupondius."],
  ["dupondius", "<big>dupondius</big>\n\n<b>dupondius, ī</b> <i>m</i> [duo + pondo]\n<b>1)</b> монета в 2 асса (половина сестерция): d. tuus <i>C</i> на 2 асса (<i>т. е.</i> ничтожная часть) твоего имущества; alicujus dupondii non facere <i>погов. Pt</i> не ставить ни во что (не признавать) кого-л.;\n<b>2)</b> дупондий, <i>мера в 2 римск. фута</i> (= 58 <i>см</i>) <i>Col</i>."],
  ["durabilis", "<big>durabilis</big>\n\n<b>dūrābilis, e</b> [duro]\n<b>1)</b> продолжительный, длительный (spiritus <i>Q</i>);\n<b>2)</b> прочный (materies <i>Ap</i>);\n<b>3)</b> твердеющий (limus <i>Ap</i>)."],
  ["durabilitas", "<big>durabilitas</big>\n\n<b>dūrābilitās, ātis</b> <i>f</i>\nпрочность, стойкость <i>или</i> твёрдость (frumentorum <i>Pall</i>)."],
  ["durabiliter", "<big>durabiliter</big>\n\n<b>dūrābiliter</b>\nдлительным образом <i>Eccl</i>."],
  ["duracinus", "<big>duracinus</big>\n\n<b>dūracinus, a, um</b> [durus + acinus]\nс твёрдой кожицей, жесткокожий (uva <i>Cato, PM, M, Su</i>; persica <i>PM, Pall</i>)."],
  ["duramen", "<big>duramen</big>\n\n<b>dūrāmen, inis</b> <i>n</i> [duro]\n<b>1)</b> затвердение, твёрдость: d. aquae <i>Lcr</i> лёд;\n<b>2)</b> <i>Col</i> = duramentum 2."],
  ["duramentum", "<big>duramentum</big>\n\n<b>dūrāmentum, ī</b> <i>n</i> [duro]\n<b>1)</b> средство закалки (d. efficax <i>VM</i>);\n<b>2)</b> отвердевшая (одеревеневшая) лоза <i>Col, PM, Pall</i>;\n<b>3)</b> долговечность, прочность, стойкость (tempore virtutibus d. accedit <i>Sen</i>)."],
  ["durateus", "<big>durateus</big>\n\n<b>dūrateus, a, um</b> (<i>греч.</i>)\nдеревянный: d. equus <i>Lcr</i> деревянный (Троянский) конь."],
  ["duratrix", "<big>duratrix</big>\n\n<b>dūrātrīx, īcis</b> <i>f</i> [duro]\nпридающая прочность (firmitas <i>PM</i>)."],
  ["dure", "<big>dure</big>\n\n<b>dūrē</b> [durus]\n<b>1)</b> тяжело, с трудом (duci <i>Vtr</i>);\n<b>2)</b> грубо, тяжело, тяжеловесно (infabre et d. <i>H</i>);\n<b>3)</b> сурово, строго (durissime aliquem punire <i>Dig</i>); жестоко, неблагоприятно, резко (dicere <i>H</i>): durius cadentibus rebus <i>Su</i> ввиду неблагоприятного оборота дел; durius vitae suae consulere <i>Cs</i> наложить на себя руки."],
  ["duresco", "<big>duresco</big>\n\n<b>dūrēsco, ruī, —, ere</b> [durus]\nтвердеть, уплотняться (limus durescit <i>V</i>): frigoribus durescit umor <i>C</i> влага замерзает от холода || <i>перен.</i> окостеневать, застывать (in lectione alicujus <i>Q</i>)."],
  ["dureta", "<big>dureta</big>\n\n<b>dūrēta, ae</b> <i>f</i> (<i>исп.</i>)\nдеревянная ванна <i>Su</i>."],
  ["duricordia", "<big>duricordia</big>\n\n<b>dūri–cordia, ae</b> <i>f</i> [cor]\nжестокосердие <i>Tert</i>."],
  ["duricorius", "<big>duricorius</big>\n\n<b>dūricorius, a, um</b> <i>Macr</i>\n= duracinus."],
  ["duritas", "<big>duritas</big>\n\n<b>dūritās, ātis</b> <i>f</i> [durus]\nсуровость, грубость (orationis <i>C</i>)."],
  ["duriter", "<big>duriter</big>\n\n<b>dūriter</b> <i>Ter, Lcr, rhH etc.</i>\n= dure."],
  ["duritia", "<big>duritia</big>\n\n<b>dūritia, ae</b> <i>f</i> [durus]\n<b>1)</b> жёсткость (pellis <i>O</i>), твёрдость, крепость (ferri <i>PM</i>; rostri, <i>sc.</i> psittaci <i>Ap</i>): d. alvi <i>Su</i> запор;\n<b>2)</b> терпкость (vini <i>PM</i>);\n<b>3)</b> закалённость, выдержка (d. et patientia <i>C, T</i>);\n<b>4)</b> строгость (veterum <i>T</i>);\n<b>5)</b> чёрствость, бездушие, суровость, жёсткость (animi <i>C</i>; imperii <i>T</i>; legum <i>Su</i>)."],
  ["durities", "<big>durities</big>\n\n<b>dūritiēs, ēī</b> <i>f</i> [durus]\n<b>1)</b> твёрдость (saxi <i>Lcr</i>); затвердение, огрубение, жёсткость (cutis <i>CC</i>);\n<b>2)</b> чёрствость, суровость (animi <i>C</i>)."],
  ["duritudo", "<big>duritudo</big>\n\n<b>dūritūdo, inis</b> <i>f</i> [durus]\nогрубелость, нечувствительность <i>Cato ap. AG</i>."],
  ["durius", "<big>durius</big>\n\n<b>dūrius, a, um</b> (<i>греч.</i>)\nдеревянный: d. equus <i>AV</i> Троянский конь; duria (<i>v. l.</i> Dorica) nox <i>VF</i> ночь, в которую греки вышли из деревянного коня и начали разгром Трои."],
  ["duriusculus", "<big>duriusculus</big>\n\n<b>dūriusculus, a, um</b> [<i>demin. к</i> durus]\nжестковатый, <i>перен.</i> грубоватый (versus <i>PM</i>)."],
  ["duro", "<big>duro</big>\n\n<b>dūro, āvī, ātum, āre</b>\n<b>1)</b> делать твёрдым, крепким, закалять (hastas igne <i>QC</i>): d. ferrum ictibus <i>PM</i> ковать железо; duratur cortice pellis <i>O</i> кожа твердеет, превращаясь в кору; d. corpus <i>CC</i> вызывать запор;\n<b>2)</b> сгущать, замораживать (undam in glaciem <i>Tib</i>);\n<b>3)</b> высушивать (aliquid calore <i>Lcr</i>): pisces durati <i>QC, Q</i> сушёная рыба;\n<b>4)</b> закалять, приучать к трудностям, воспитывать выносливость (d. se labore <i>Cs</i>; d. exercitum crebris expeditionibus <i>VP</i>); делать нечувствительным (ad plagas <i>Q</i>);\n<b>5)</b> выдерживать, переносить, выносить (d. quemvis laborem <i>V</i>);\n<b>6)</b> быть твёрдым, твердеть (solum d. coepit <i>V</i>);\n<b>7)</b> сохранять твёрдость, выдерживать, терпеть (hiemem in castris <i>L</i>): durate et vosmet rebus servate secundis <i>V</i> терпите и храните себя для счастливых дней; duravi interrogare illum <i>Pt</i> я отважился расспросить его;\n<b>8)</b> длиться, продолжать существование, продолжаться (ad senectutem usque <i>Q</i>; corpus non durat post mortem <i>Lcr</i>): vivere d. <i>Lcn</i> продолжать жить; Troja durante <i>O</i> когда (ещё) существовала Троя; ad caedem duratura odia <i>T</i> ненависть, которая не остановится перед убийством;\n<b>9)</b> укореняться, становиться застарелым (multa vitia durantur <i>Q</i>);\n<b>10)</b> тянуться, не прерываться (colles durant <i>T</i>);\n<b>11)</b> оставаться, сидеть безвыходно (d. nequeo in aedibus <i>Pl</i>)."],
  ["Durocortorum", "<big>Durocortorum</big>\n\n<b>Dūrocortorum, ī</b> <i>n</i>\n<i>город ремов в Gallia Belgica, позднее Remi</i> (<i>ныне</i> Reims) <i>Cs</i>."],
  ["Durrachium", "<big>Durrachium</big>\n\n<b>Durrachium</b> <i>v. l.</i>\n= Dyrr(h)achium."],
  ["durus", "<big>durus</big>\n\n<b>dūrus, a, um</b>\n<b>1)</b> жёсткий, недоваренный (gallina <i>CC</i>); твёрдый, крепкий (ferrum <i>H</i>; lapis <i>PM</i>); крутой (ovum, alvus <i>CC</i>);\n<b>2)</b> крутой, обрывистый: duro clivo <i>Sil</i> по крутому склону;\n<b>3)</b> пристально глядящий (oculi <i>Pl</i>);\n<b>4)</b> терпкий, острый на вкус (vinum <i>Cato, Pall</i>);\n<b>5)</b> жёсткий (aqua <i>CC</i>);\n<b>6)</b> тяжёлый, неприятный для слуха (versus <i>H</i>; vox, signa <i>C</i>);\n<b>7)</b> неотёсанный, некультурный, грубый (verba <i>QC</i>): dura vita <i>C</i> полудикая жизнь, <i>но тж.</i> <i>Ter</i> тяжёлая жизнь;\n<b>8)</b> жестокий, резкий (frigus <i>Pl, Lcr</i>);\n<b>9)</b> тяжёлый, опасный (morbus <i>Pl</i>);\n<b>10)</b> бесстыдный, дерзкий, наглый (os <i>C</i>);\n<b>11)</b> суровый (tempora <i>C</i>); трудный, тягостный, тяжкий (servitus <i>C</i>; labor <i>Lcr, V</i>; praecepta <i>O</i>);\n<b>12)</b> суровый, строгий (Catonis frons <i>M</i>; pater <i>Ter</i>; judex <i>C</i>);\n<b>13)</b> закалённый, выносливый (ad aliquid <i>C</i>; Spartiatae <i>C</i>);\n<b>14)</b> дорогой (по цене) (annona fit durior <i>C</i>);\n<b>15)</b> крепкий, могучий (Atlas <i>V</i>; durum in armis genas, <i>sc.</i> Ligures <i>L</i>)."],
  ["dusmus", "<big>dusmus</big>\n\n<b>dusmus</b> <i>арх. LA</i>\n= dumus <i>или</i> dumosus."],
  ["duum", "<big>duum</big>\n\n<b>duum</b> (= duorum) <i>gen. pl. к</i>\nduo."],
  ["duumvir", "<big>duumvir</big>\n\n<b>duumvir (duovir <i>L</i>), virī</b> <i>m</i> [duo + vir]\nдуумвир, член комиссии двух; — обычно <i>pl.</i>:\n<b>1)</b> duumviri perduellionis (capitales) <i>C, L, Su</i> два следователя, назначавшиеся для расследования дела о государственной измене;\n<b>2)</b> duumviri sacrorum (sacris faciundis) <i>L</i> дуумвиры, которые имели надзор за сивиллиными книгами (<i>позднее их было 10 — decemviri — и даже 15 — quindecimviri</i>);\n<b>3)</b> duumviri aedis faciendae (locandae, dedicandae) <i>L</i> дуумвиры, ведавшие сооружением храма;\n<b>4)</b> duumviri navales <i>L</i> дуумвиры, ведавшие сооружением и оснащением военных кораблей и командовавшие ими;\n<b>5)</b> duumviri (juri dicundo) <i>C, Cs</i> носители высшей власти в муниципиях и колониях (<i>председательствовавшие в сенате и в верховном суде</i>)."],
  ["duumviralicius", "<big>duumviralicius</big>\n\n<b>duumvirālicius, ī</b> <i>m</i>\n(vir) <i>Aug</i> = duumviralis."],
  ["duumviralis", "<big>duumviralis</big>\n\n<b>duumvirālis, is</b> <i>m</i> [duumvir]\nбывший дуумвир <i>Dig, Ap</i>."],
  ["duumviralitas", "<big>duumviralitas</big>\n\n<b>duumvirālitās, ātis</b> <i>f</i> <i>CJ</i>\n= duumviratus."],
  ["duumviratus", "<big>duumviratus</big>\n\n<b>duumvirātus, ūs</b> <i>m</i> [duumvir]\nдуумвират, звание и достоинство дуумвира <i>PM, Dig, CJ</i>."],
  ["dux", "<big>dux</big>\n\n<b>dux, ducis</b> <i>m, f</i> [duco]\n<b>1)</b> вожатый, проводник (d. itineris <i>QC, Pt</i>);\n<b>2)</b> вожак (gregis <i>O</i>);\n<b>3)</b> глава, начальник (d. superorum <i>V</i> — Juppiter); командир, полководец, военачальник (d. classis <i>C</i>); руководитель (seditionis <i>C</i>); предводитель, атаман (praedonum <i>C</i>): naturam ducem sequi <i>C</i> следовать указаниям природы; natura duce <i>C</i> по естественным побуждениям, <i>т. е.</i> инстинктивно; te duce <i>H</i> под твоим водительством;\n<b>4)</b> главарь, зачинщик (impietatis <i>C</i>): auctor et d. <i>C</i> инициатор и руководитель;\n<b>5)</b> государь <i>O, Ph</i>."],
  ["duxe", "<big>duxe</big>\n\n<b>dūxe</b> <i>Vr</i>\n= duxisse."],
  ["duxi", "<big>duxi</big>\n\n<b>dūxī</b> <i>pf. к</i>\nduco."],
  ["duxti", "<big>duxti</big>\n\n<b>dūxtī</b> (= duxisti) <i>Vr, Ctl, Prp 2 л. sg. pf. к</i>\nduco."],
  ["dyas", "<big>dyas</big>\n\n<b>dyas, adis</b> <i>f</i> (<i>греч.</i>; <i>лат.</i> numerus binarius)\nдиада, двоица <i>Macr, Aug</i>."],
  ["Dymae", "<big>Dymae</big>\n\n<b>Dymae, ārum</b> <i>f</i> <i>v. l.</i>\n= Dyme."],
  ["Dymaeus", "<big>Dymaeus</big>\n\n<b>I Dymaeus, a, um</b> [Dyme]\nдимский (ager <i>L</i>).\n\n<b>II Dymaeus, ī</b> <i>m</i>\nжитель города Dyme <i>C, L</i>."],
  ["Dymantis", "<big>Dymantis</big>\n\n<b>Dymantis, idis</b> <i>f</i>\nдочь Диманта, <i>т. е.</i> Гекуба <i>O</i>."],
  ["Dymas", "<big>Dymas</big>\n\n<b>Dymās, antis</b> <i>m</i>\nДимант, <i>фригийский царь, отец Гекубы</i> <i>O</i>."],
  ["Dyme", "<big>Dyme</big>\n\n<b>Dymē, ēs <i>f</i> <i>и</i> Dymae, ārum</b> <i>f</i>\nДимы, <i>приморский город в зап. Ахайе</i> <i>PM, L</i>."],
  ["dynamis", "<big>dynamis</big>\n\n<b>dynamis, is</b> (<i>acc.</i> in) <i>f</i>\n<b>1)</b> (<i>греч.</i>; <i>лат.</i> vis) множество, большое количество (olivi dynamin domi habere <i>Pl</i>);\n<b>2)</b> <i>мат.</i> (<i>лат.</i> potestas) число в квадрате <i>Eccl</i>."],
  ["dynastes", "<big>dynastes</big>\n\n<b>dynastēs, ae</b> <i>m</i> (<i>греч.</i>; <i>лат.</i> regulus)\n<b>1)</b> властитель, властелин, повелитель <i>C</i>;\n<b>2)</b> могущественное (влиятельное) лицо <i>C</i>."],
  ["dyodecas", "<big>dyodecas</big>\n\n<b>dyōdecas, adis</b> <i>f</i> <i>Tert</i>\n= duodecas."],
  ["Dyrrhachinus", "<big>Dyrrhachinus</big>\n\n<b>I Dyrr(h)achinus, a, um</b> [Dyrr(h)achium]\nдиррахийский <i>C</i>.\n\n<b>II Dyrr(h)achinus, ī</b> <i>m</i>\nжитель города Диррахия <i>C</i>."],
  ["Dyrrhachium", "<big>Dyrrhachium</big>\n\n<b>Dyrr(h)achium, ī</b> <i>n</i>\nДиррахий, <i>приморский город Иллирии на Адриатическом море, прежде</i> Эпидамн (<i>ныне албанский порт</i> Durres, <i>итал.</i> Durazzo) <i>C, Cs, L, Ctl etc.</i>"],
  ["dyscolus", "<big>dyscolus</big>\n\n<b>dyscolus, a, um</b> (<i>греч.</i>)\nнеласковый, злобный <i>Vlg</i>."],
  ["dysenteria", "<big>dysenteria</big>\n\n<b>dysenteria, ae</b> <i>f</i> (<i>греч.</i>; <i>лат.</i> tormina intestinorum)\nтяжёлый понос, дизентерия <i>PM, CA, Vlg</i>."],
  ["dyseros", "<big>dyseros</big>\n\n<b>dyserōs, ōtis</b> (<i>греч.</i>)\nнесчастный в любви <i>Aus</i>."],
  ["dyspar", "<big>dyspar</big>\n\n<b>dyspār, aris</b> <i>Acc</i>\n= dispar."],
  ["dyspepsia", "<big>dyspepsia</big>\n\n<b>dyspepsia, ae</b> <i>f</i> (<i>греч.</i>)\nдурное пищеварение <i>Cato</i>."],
  ["dyspnoea", "<big>dyspnoea</big>\n\n<b>dyspnoea, ae</b> <i>f</i> (<i>греч.</i>; <i>лат.</i> anhelatio)\nзатруднённое дыхание, одышка <i>PM</i>."],
  ["dysuria", "<big>dysuria</big>\n\n<b>dysūria, ae</b> <i>f</i> (<i>греч.</i>)\nзатруднённое мочеиспускание <i>C, CA</i>."]
]