export default [
  ["E", "<big>E</big>\n\n<b>E, e</b>\n<i>пятая буква латинского алфавита; в сокращениях</i>: E. = emeritus <i>или</i> evocatus; E. M. V. = egregiae memoriae vir; E. P. = equo publico; E. Q. R. = eques Romanus; ER. = eres (<i>т. е.</i> heres)."],
  ["e", "<big>e</big>\n\n<b>&#275;</b> <i>praep.</i> — <i>см.</i>\nex."],
  ["e-", "<big>e-</big>\n\n<b>&#275;-</b>\n<i>приставка — см.</i> ex-."],
  ["ea 1", "<big>ea</big>\n\n<b>I ea</b> <i>f <i>sg.</i> <i>и</i> n</i> <i>pl. к</i>\nis."],
  ["ea 2", "<big>ea</big>\n\n<b>II e&#257;</b> <i>adv.</i> [<i>из</i> <i>abl. к</i> ea — <i>см.</i> is]\n(<i>sc.</i> via, parte) по той дороге, тем (же) путём, там <i>C, L, Nep etc.</i>"],
  ["eadem", "<big>eadem</big>\n\n<b>e&#257;–dem</b> <i>adv.</i> [<i>abl.</i> <i>от</i> eadem, <i>см.</i> ea]\n<b>1)</b> по той же дороге, там же <i>C, L</i>;\n<b>2)</b> (<i>sc.</i> opera) одновременно <i>Pl, Ter</i>. e... e. <i>Pl</i> то... то;\n<b>3)</b> равным образом, точно так же <i>Pl</i>."],
  ["eam 1", "<big>eam</big>\n\n<b>I eam</b> <i>acc. sg. к</i>\nea I."],
  ["eam 2", "<big>eam</big>\n\n<b>II eam</b> <i>conjct. praes. к</i>\neo I."],
  ["eampse", "<big>eampse</big>\n\n<b>eampse</b> <i>Pl</i>\n= eam ipsam."],
  ["eapropter", "<big>eapropter</big>\n\n<b>e&#257;–propter</b>\n(= propterea) (<i>тж. раздельно</i>) ввиду (вследствие) этого <i>Ter, Lcr, Sol</i>."],
  ["eapse", "<big>eapse</big>\n\n<b>e&#257;pse</b> <i>Pl</i>\n= ea ipsa."],
  ["earinus", "<big>earinus</big>\n\n<b>earinus, a, um</b> (<i>греч.</i>)\nвесенний, <i>тж.</i> зеленоватый (oves <i>Tert</i>)."],
  ["eatenus", "<big>eatenus</big>\n\n<b>e&#257;tenus</b> [ea tenus (<i>sc.</i> parte)]\n<b>1)</b> до тех пор, пока <i>CC, Dig</i>, покуда <i>C etc.</i>; поскольку, в той мере как <i>C, Q etc.</i>;\n<b>2)</b> до этих пор, до (э)того времени (Gordianus qui Caesar e. fuerat <i>Capit</i>)."],
  ["ebeninus", "<big>ebeninus</big>\n\n<b>ebeninus, a, um</b>\nиз эбенового дерева (tigna <i>Hier</i>)."],
  ["ebenum", "<big>ebenum</big>\n\n<b>ebenum, &#299;</b> <i>n</i> <i>V</i>\n= ebenus."],
  ["ebenus", "<big>ebenus</big>\n\n<b>ebenus, &#299;</b> <i>f</i>\nэбеновое (чёрное) дерево (<i>Diospyros Ebenum</i>, L.) <i>PM, Ap etc.</i>"],
  ["ebibo", "<big>ebibo</big>\n\n<b>&#275;–bibo, bib&#299;, bibitum, ere</b>\n<b>1)</b> выпивать (aquam <i>Ph</i>; poculum <i>Pl</i>); высасывать (ubera lactantia <i>O</i>; cruorem e vulnere <i>Mela</i>); (<i>о море</i>) поглощать, принимать в себя (amnes <i>O</i>): e. annos alicujus <i>O</i> пить столько бокалов, сколько кому-л. лет;\n<b>2)</b> пропивать (<i>sc.</i> patrimonium <i>H</i>):/: cum vino imperium e. <i>Pl</i> за вином забыть про приказ;\n<b>3)</b> впитывать (saniem <i>PM</i>)."],
  ["ebiscum", "<big>ebiscum</big>\n\n<b>ebiscum, &#299;</b> <i>n</i> <i>Scr</i>\n= hibiscum."],
  ["ebito", "<big>ebito</big>\n\n<b>&#275;–b&#299;to, —, —, ere</b>\nвыходить <i>Pl</i> (<i>v. l.</i>)."],
  ["eblandior", "<big>eblandior</big>\n\n<b>&#275;–blandior, &#299;tus sum, &#299;r&#299;</b> <i>depon.</i>\n<b>1)</b> достигать, добиваться лестью, выманивать лаской (aliquid <i>L, T</i>); выпрашивать, вымаливать (caelo fecunditatem <i>PM</i>; eblandita suffragia <i>C</i>);\n<b>2)</b> смягчать (igneam saevitiam, <i>sc.</i> morbi <i>Col</i>)."],
  ["Eboracum", "<big>Eboracum</big>\n\n<b>Eboracum, &#299;</b> <i>n</i>\nЭборак, <i>город в области бригантов (Британия); стоянка 6-го римск. легиона и резиденция приезжавших в Британию римск. императоров (ныне</i> York); <i>здесь умерли Септимий Север и Констанций Хлор</i> <i>Eutr</i>."],
  ["eborarius", "<big>eborarius</big>\n\n<b>ebor&#257;rius, &#299;</b> <i>m</i> [ebur]\nрезчик по слоновой кости, косторез <i>CJ</i>."],
  ["eboratus", "<big>eboratus</big>\n\n<b>ebor&#257;tus, a, um</b> <i>v. l.</i>\n= eburatus."],
  ["eboreus", "<big>eboreus</big>\n\n<b>eboreus, a, um</b> [ebur]\nсделанный из слоновой кости (circulus <i>Pt</i>; Juppiter <i>PM</i>)."],
  ["ebriamen", "<big>ebriamen</big>\n\n<b>&#275;bri&#257;men, inis</b> <i>n</i> [ebrio]\nопьяняющий напиток <i>Tert</i>."],
  ["ebrietas", "<big>ebrietas</big>\n\n<b>&#275;briet&#257;s, &#257;tis</b> <i>f</i> [ebrius]\n<b>1)</b> опьянение, хмель <i>C, H, Sen etc.</i>;\n<b>2)</b> повышенная сочность (pomi <i>PM</i>);\n<b>3)</b> <i>Vlg</i> = ebriamen."],
  ["ebrio", "<big>ebrio</big>\n\n<b>&#275;brio, —, &#257;tum, &#257;re</b> [ebrius]\nпоить допьяна (mulieres raro ebriantur <i>Macr</i>); <i>перен.</i> опьянять, пьянить (animas <i>Macr</i>)."],
  ["ebriolus", "<big>ebriolus</big>\n\n<b>&#275;briolus, a, um</b> [<i>demin. к</i> ebrius]\nподвыпивший <i>Pl</i>."],
  ["ebriositas", "<big>ebriositas</big>\n\n<b>&#275;bri&#333;sit&#257;s, &#257;tis</b> <i>f</i> [ebriosus]\nпьянство, пристрастие к вину <i>C, Aug</i>."],
  ["ebriosus 1", "<big>ebriosus</big>\n\n<b>I &#275;bri&#333;sus, a, um</b> [ebrius]\n<b>1)</b> пьянствующий, предающийся пьянству <i>C, Sen etc.</i>;\n<b>2)</b> сочный (acina <i>Ctl</i>)."],
  ["ebriosus 2", "<big>ebriosus</big>\n\n<b>II &#275;bri&#333;sus, &#299;</b> <i>m</i>\nпьяница <i>C</i>."],
  ["ebrius", "<big>ebrius</big>\n\n<b>&#275;brius, a, um</b>\n<b>1)</b> пьяный, опьянённый, хмельной (homo <i>Pl, C etc.</i>); vestigia ebria trahere <i>Prp</i> ходить пьяной походкой; ebria nox <i>M</i> ночь пьянства;\n<b>2)</b> напоённый, напитанный: ebria lana de sanguine Sidoniae conchae <i>M</i> шерстяная ткань, пропитанная кровью сидонской улитки (<i>т. е.</i> окрашенная в пурпур);\n<b>3)</b> <i>перен.</i> упоённый, преисполненный (dulci fortuna <i>H</i>); curis e. <i>Lcr</i> угнетённый (снедаемый) заботами."],
  ["ebullio", "<big>ebullio</big>\n\n<b>&#275;–bullio, &#299;v&#299; (i&#299;), —, &#299;re</b>\n<b>1)</b> вскипать, бить ключом, клокотать (aqua ebullit <i>Sen</i>): risus ebullit <i>Ap</i> слышен (раздаётся) хохот;\n<b>2)</b> хвалиться, хвастаться (e. virtutes <i>C</i>);\n<b>3)</b> выбрасывать, извергать (os fatuorum ebullit stultitiam <i>Vlg</i>): e. (= edere) animam <i>Sen, Pt</i> (<i>ирон.</i> <i>тж.</i> e. <i>Pers</i>) испустить дух, умереть;\n<b>4)</b> шумно веселиться (ludicris motibus <i>Aug</i>)."],
  ["ebullitio", "<big>ebullitio</big>\n\n<b>&#275;bull&#299;tio, &#333;nis</b> <i>f</i> [ebullio]\nвскипание <i>Hier</i>."],
  ["ebulum", "<big>ebulum</big>\n\n<b>ebulum, &#299;</b> <i>n</i>\nбузина яловая (<i>Sambucus Ebulus</i>, L.) <i>Cato, Col, V, Lcn, PM</i>."],
  ["ebulus", "<big>ebulus</big>\n\n<b>ebulus, &#299;</b> <i>f</i> <i>PM</i>\n= ebulum."],
  ["ebur", "<big>ebur</big>\n\n<b>ebur, oris</b> <i>n</i>\n<b>1)</b> слоновая кость <i>C, V etc.</i>;\n<b>2)</b> изделия из слоновой кости, <i>напр.</i>: статуя (e. illacrimat <i>V</i> <i>и</i> lacrimavit <i>O</i>); флейта (inflavit e. <i>V</i>); ножны (e. ense vacuum <i>O</i>); отделанное слоновой костью курульное кресло: premere e. <i>O</i> сидеть в курульном кресле;\n<b>3)</b> слон (e. ducitur ad aras <i>J</i>)."],
  ["eburarius", "<big>eburarius</big>\n\n<b>ebur&#257;rius</b> <i>v. l.</i>\n= eborarius."],
  ["eburatus", "<big>eburatus</big>\n\n<b>ebur&#257;tus, a, um</b> [ebur]\nукрашенный (выложенный) слоновой костью (lectus <i>Pl</i>; sella <i>Lampr</i>)."],
  ["eburneolus", "<big>eburneolus</big>\n\n<b>eburneolus, a, um</b> [<i>demin. к</i> eburneus]\nизящно сделанный из слоновой кости (fistula <i>C</i>)."],
  ["eburneus", "<big>eburneus</big>\n\n<b>eburneus, a, um</b> [ebur]\n<b>1)</b> (с отделкой) из слоновой кости (signum <i>C, O</i>; porta <i>V</i>): dens e. <i>L</i> слоновый бивень;\n<b>2)</b> цвета слоновой кости, <i>перен.</i> блистающий, ослепительный (brachia, colla <i>O</i>)."],
  ["eburnus", "<big>eburnus</big>\n\n<b>eburnus, a, um</b> <i>Prp, V, H, O</i>\n= eburneus."],
  ["Eburones", "<big>Eburones</big>\n\n<b>Ebur&#333;n&#275;s, um</b> <i>m</i>\nэбуроны, <i>народность в Gallia Belgica, с главн. городом Aduatuca</i> <i>Cs</i>."],
  ["Eburovices", "<big>Eburovices</big>\n\n<b>Eburov&#299;c&#275;s, um</b> <i>m</i>\nэбуровики, <i>ветвь кельтской народности Aulerci</i> <i>Cs</i>."],
  ["Ebusia", "<big>Ebusia</big>\n\n<b>Ebusia, ae</b> <i>f</i> <i>St</i>\n= Ebusus."],
  ["Ebusus", "<big>Ebusus</big>\n\n<b>Ebusus, &#299;</b> <i>f</i>\nЭбуз, <i>самый крупный из Питиузских о-вов, у вост. побережья Испании</i> (<i>ныне</i> Ibiza) <i>L, Sil etc.</i>"],
  ["ec", "<big>ec</big>\n\n<b>ec</b>\n<i>арх. <i>LXIIT, LM</i> = ex.</i>"],
  ["ecastor", "<big>ecastor</big>\n\n<b>&#275;–castor</b> [ecce + Castor]\nклянусь Кастором <i>Pl</i>."],
  ["ecaudis", "<big>ecaudis</big>\n\n<b>&#275;–caudis, e</b> [cauda] <i>стих.</i>\nбесхвостый, <i>т. е.</i> усечённый (versus)."],
  ["Ecbatana", "<big>Ecbatana</big>\n\n<b>Ecbatana, &#333;rum<span style=\"font-weight:normal\"> <i>n</i>, Ecbatanae, &#257;rum<span style=\"font-weight:normal\"> <i>f</i> <i>и</i> Ecbatana, ae</b> <i>n</i>, <b>Ecbatanae, arum</b> <i>f</i> <i>и</i> <b>Ecbatana, ae</b> <i>f</i>\nЭкбатаны, <i>главн. город Мидии, летняя резиденция персидских (впоследствии — парфянских) царей (ныне прибл.</i> Хамадан) <i>QC, T, Ap, LM</i>."],
  ["ecbibo", "<big>ecbibo</big>\n\n<b>ecbibo</b> <i>арх.</i> <i>Pl</i>\n= ebibo."],
  ["ecca", "<big>ecca</big>\n\n<b>ecca</b> <i>Pl, Ter</i> = ecce ea; <b>eccam</b> <i>Pl, Ter</i> = ecce eam (<i>см. тж.</i> eccillam)."],
  ["ecce", "<big>ecce</big>\n\n<b>ecce</b> [<i>из</i> ence, <i>от</i> en + ce]\n<b>1)</b> вот, смотри, гляди (e. tuae litterae <i>C</i>): e. me <i>Pl</i> вот я; quem quaero, optime e. obviam mihi est <i>Pl</i> вот идёт кстати тот, кого я ищу;\n<b>2)</b> и вот, вдруг (e. subitum divortium <i>C</i>; e. trahebatur Priameia virgo <i>V</i>): e. tibi exortus Isocrates <i>C</i> и вот появляется Исократ."],
  ["eccere", "<big>eccere</big>\n\n<b>eccer&#275;</b> [e Ceres!; <i>по др.</i> ecce re!]\nправо же!; ну конечно!; вот-вот (вот как)! <i>Pl, Ter</i>."],
  ["eccheuma", "<big>eccheuma</big>\n\n<b>eccheuma, atis</b> <i>n</i> (<i>греч.</i>)\nразлитие, поток <i>Pl</i> (<i>v. l.</i>)."],
  ["eccillam", "<big>eccillam</big>\n\n<b>eccillam</b> <i>и</i> <b>eccam</b> [ecce + illam]\nвот она <i>Pl</i>."],
  ["eccillum", "<big>eccillum</big>\n\n<b>eccillum</b> [ecce + illum]\nвот он <i>Pl</i>."],
  ["eccistam", "<big>eccistam</big>\n\n<b>eccistam</b> [ecce + istam]\nвот она <i>Pl</i>."],
  ["ecclesia", "<big>ecclesia</big>\n\n<b>eccl&#275;sia, ae</b> <i>f</i> (<i>греч.</i>; <i>лат.</i> contio)\n<b>1)</b> народное собрание (<i>в греч. республиках</i>): bule et e. <i>PJ</i> (<i>лат.</i> senatus populusque) (греческие) сенат и народ;\n<b>2)</b> собрание, сходка <i>Aus</i>;\n<b>3)</b> церковь <i>Vop, Eccl</i>."],
  ["ecclesiastes", "<big>ecclesiastes</big>\n\n<b>eccl&#275;siast&#275;s, ae</b> <i>и</i> <b>is</b> <i>m</i> (<i>греч.</i>)\nэкклесиаст, <i>т. е.</i> оратор, проповедник, <i>тж.</i> название одной из книг Библии, приписываемой царю Соломону."],
  ["ecclesiasticus 1", "<big>ecclesiasticus</big>\n\n<b>I eccl&#275;siasticus, a, um</b> [ecclesia]\nцерковный (scriptores <i>Hier</i>)."],
  ["ecclesiasticus 2", "<big>ecclesiasticus</big>\n\n<b>II eccl&#275;siasticus, &#299;</b> <i>m</i>\nуправляющий церковными делами <i>CTh, CJ</i>; <i>тж.</i> название одной из книг Библии, приписываемой Иисусу бен-Сира(х)."],
  ["ecclesiecdicus", "<big>ecclesiecdicus</big>\n\n<b>eccl&#275;siecdicus, &#299;</b> <i>m</i> (<i>греч.</i>)\nповеренный по делам церкви <i>CTh</i>."],
  ["eccos", "<big>eccos</big>\n\n<b>eccos</b> [ecce + illos]\nвот они <i>Pl, Ter</i>."],
  ["eccum", "<big>eccum</big>\n\n<b>eccum</b> [ecce + illum]\nвот он <i>Pl, Ter</i>."],
  ["ecdicus", "<big>ecdicus</big>\n\n<b>ecdicus, &#299;</b> <i>m</i> (<i>греч.</i>; <i>лат.</i> cognitor civitatis)\nпредставитель интересов общества (общины, государства) <i>PJ</i>; государственный прокурор <i>C</i>."],
  ["ecdurus", "<big>ecdurus</big>\n\n<b>ecd&#363;rus</b> <i>v. l.</i>\n= edurus."],
  ["ecere", "<big>ecere</big>\n\n<b>&#275;cer&#275;</b> <i>v. l.</i>\n= eccere."],
  ["Ecetra", "<big>Ecetra</big>\n\n<b>Ecetra, ae</b> <i>f</i>\n<i>город вольсков, к юго-вост. от Рима</i> <i>L</i>."],
  ["Ecetranus", "<big>Ecetranus</big>\n\n<b>Ecetr&#257;nus, &#299;</b> <i>m</i>\nжитель города Ecetra <i>L</i>."],
  ["ecf-", "<big>ecf-</big>\n\n<b>ecf-</b> <i>v. l.</i>\n= eff-."],
  ["echea", "<big>echea</big>\n\n<b>&#275;ch&#275;a, &#333;rum</b> <i>n</i> (<i>греч.</i>)\nэхеи, голосники, <i>театральные звукоусилители</i> <i>Vtr</i>."],
  ["Echecrates", "<big>Echecrates</big>\n\n<b>Echecrat&#275;s, is</b> <i>m</i>\nЭхекрат, <i>философ-пифагореец, учитель Платона</i> <i>C</i>."],
  ["echeneis", "<big>echeneis</big>\n\n<b>echen&#275;is (-&#257;is), idis</b> <i>f</i> (<i>греч.</i>; <i>лат.</i> remora)\nприлипало (<i>рыба Echeneis Remora</i>, L.) <i>PM</i>."],
  ["echidna 1", "<big>echidna</big>\n\n<b>I echidna, ae</b> <i>f</i> (<i>греч.</i>)\nгадюка, змея <i>O etc.</i>"],
  ["Echidna 2", "<big>Echidna</big>\n\n<b>II Echidna, ae</b> <i>f</i>\nЭхидна, <i>чудовище подземного мира, жившее в области аримов (Киликия), полудева-полузмея, жена Тифона, мать Сфинкса, Скиллы, Химеры, Кербера, Лернейской гидры, Немейского льва и др. чудовищ</i> <i>O</i>: E. Lernaea <i>O</i> Лернейская гидра."],
  ["Echidneus", "<big>Echidneus</big>\n\n<b>Echidn&#275;us, a, um</b> <i>adj. к</i>\nEchidna: canis e. <i>O</i> = Cerberus."],
  ["Echinades", "<big>Echinades</big>\n\n<b>Ech&#299;nades, um</b> <i>f</i> (<i>греч.</i> &laquo;ежовые&raquo;)\nЭхинады, <i>нимфы, превращенные Нептуном в группу островков у юго-зап. побережья Акарнании</i> (<i>против устья Ахелоя</i>) <i>O</i>."],
  ["echinatus", "<big>echinatus</big>\n\n<b>ech&#299;n&#257;tus, a, um</b> [echinus]\nимеющий шипы, колючий (folia <i>PM</i>)."],
  ["echinus", "<big>echinus</big>\n\n<b>ech&#299;nus, &#299;</b> <i>m</i> (<i>греч.</i>)\n<b>1)</b> ёж <i>Cld, Pt</i>; <i>преим.</i> морской ёж (<i>съедобный, Echinus esculentus</i>, L.) <i>Pl, Vr, H, CC etc.</i>;\n<b>2)</b> полоскательная чашка <i>LM, H</i>;\n<b>3)</b> <i>архит.</i> подушка капители колонны <i>Vtr</i>;\n<b>4)</b> шиповатый плод каштана <i>Calp, Pall</i>."],
  ["Echion", "<big>Echion</big>\n\n<b>Ech&#299;&#333;n, onis</b> <i>m</i>\nЭхион:\n<b>1)</b> (<i>греч.</i> &laquo;змеиный&raquo;) <i>один из тех 5 &laquo;spartoi&raquo; (&laquo;посеянных&raquo;, т. е. воинов, выросших из посеянных Кадмом зубов дракона), которые остались в живых — муж Агавы, отец Пенфея</i> <i>O, St</i>: Echione natus <i>O</i> = Pentheus;\n<b>2)</b> <i>сын Меркурия и Антианиры, участник Калидонской охоты и похода Аргонавтов</i> <i>O</i>."],
  ["Echionides", "<big>Echionides</big>\n\n<b>Ech&#299;onid&#275;s, ae</b> <i>m</i>\nсын (потомок) Эхиона, <i>т. е.</i> Пенфей <i>O</i>."],
  ["Echionius", "<big>Echionius</big>\n\n<b>Ech&#299;onius, a, um</b> [Echion]\nэхионов, <i>т. е.</i> фиваиский, <i>перен.</i> беотийский <i>V, H, O, VF</i>."],
  ["Echo 1", "<big>Echo</big>\n\n<b>I &#274;ch&#333;, &#363;s</b> <i>f</i>\nЭхо, <i>нимфа, которая, по проклятию Юноны, не могла ни заговаривать первой, ни молчать, когда заговаривали другие; отвергнутая Нарциссом, она зачахла от любви к нему и превратилась в камень, и лишь голос её сохранился</i> <i>O, Ap, Aus</i>."],
  ["echo 2", "<big>echo</big>\n\n<b>II &#275;ch&#333;, &#363;s</b> <i>f</i> (<i>греч.</i>; <i>лат.</i> imago)\nотголосок, эхо <i>Acc, Pers, PM, St etc.</i>"],
  ["echoicus", "<big>echoicus</big>\n\n<b>&#275;ch&#333;icus, a, um</b> [echo]\nдающий отголосок, откликающийся (elegi <i>Sid</i>)."],
  ["ecligma", "<big>ecligma</big>\n\n<b>ecl&#299;gma, atis</b> <i>n</i> (<i>греч.</i>) <i>PM</i>\n= electarium."],
  ["eclipsis", "<big>eclipsis</big>\n\n<b>ecl&#299;psis, is</b> (<i>acc.</i> in) <i>f</i> (<i>греч.</i>)\nисчезновение, <i>перен.</i> (<i>лат.</i> defectus) затмение (solis, lunae <i>rhH etc.</i>)."],
  ["eclipticus", "<big>eclipticus</big>\n\n<b>ecl&#299;pticus, a, um</b> [eclipsis]\nподверженный затмениям (signa <i>Man</i>)."],
  ["ecloga", "<big>ecloga</big>\n\n<b>ecloga, ae</b> <i>f</i> (<i>греч.</i>)\n<b>1)</b> избранное небольшое произведение <i>или</i> отрывок <i>Vr</i>;\n<b>2)</b> маленькое стихотворение <i>PJ, Su</i>;\n<b>3)</b> эклога, пастушеская песня (<i>напр.</i>, <i>в собрании</i> &laquo;Bucolica&raquo; <i>V</i>)."],
  ["eclogarii", "<big>eclogarii</big>\n\n<b>eclog&#257;ri&#299;, &#333;rum</b> <i>m</i> (<i>греч.</i>; <i>лат.</i> loci electi)\nизбранные места, отрывки (из сочинений) <i>C</i>."],
  ["eclogarium", "<big>eclogarium</big>\n\n<b>eclog&#257;rium, &#299;</b> <i>n</i>\nэклогарий, сборник избранных стихотворений <i>Aus</i>."],
  ["econtra", "<big>econtra</big>\n\n<b>&#275;–contra</b> <i>AV, Hier</i>\n= contra II."],
  ["ecphora", "<big>ecphora</big>\n\n<b>ecphora, ae</b> <i>f</i> (<i>греч.</i>)\nвыступ (в здании) <i>Vtr</i>."],
  ["ecpyrosis", "<big>ecpyrosis</big>\n\n<b>ecp&#563;r&#333;sis, is</b> <i>f</i> (<i>греч.</i>; <i>лат.</i> mundi incendium)\nмировой пожар <i>Cens</i>."],
  ["ecqualis", "<big>ecqualis</big>\n\n<b>ec–qu&#257;lis, e</b>\nкакой же, какого же свойства <i>AG</i>."],
  ["ecquando", "<big>ecquando</big>\n\n<b>ec–quand&#333;</b>\n<b>1)</b> когда-либо, когда-нибудь: ecquandone similem civem habebit res publica? <i>VP</i> будет ли когда-л. у республики подобный гражданин?;\n<b>2)</b> да неужели (не) <i>L</i>: e. te rationem factorum tuorum redditurum putasti (= putavisti)? <i>C</i> да неужели ты не думал, что тебе придётся дать отчёт в своих действиях?"],
  ["ecqui 1", "<big>ecqui</big>\n\n<b>I ec–qu&#299;</b>\n(= num aliqui) ли: ecqui scis? <i>Ctl</i> знаешь ли ты?"],
  ["ecqui 2", "<big>ecqui</big>\n\n<b>II ec–qui (-quis), ecquae (-qua), ecquod</b> <i>pron. interr. adj.</i> (= numqui) <i>и</i> <i>subst.</i> (= numquis)\nкакой-нибудь (quaeris ecqua spes sit? <i>C</i>)."],
  ["ecquid", "<big>ecquid</big>\n\n<b>ec–quid</b>\n(= numquid, num) ли, разве (ecquid audis? <i>Pl</i>; ecquid placeant me, rogas? <i>Pl</i>)."],
  ["ecquis", "<big>ecquis</big>\n\n<b>ec–quis, ecquid</b>\nкто-нибудь, что-нибудь: e. hic est? <i>Pl</i> есть здесь кто-нибудь?; e. fuit, quin lacrimaret? <i>C</i> разве был такой, кто не заплакал бы?"],
  ["ecquo", "<big>ecquo</big>\n\n<b>ec–qu&#333;</b>\n(= num aliquo) куда-либо?, разве куда-нибудь? <i>C</i>."],
  ["ecscreo", "<big>ecscreo</big>\n\n<b>ecscreo</b> <i>v. l.</i>\n= exscreo."],
  ["ecstasis", "<big>ecstasis</big>\n\n<b>ecstasis, is</b> <i>f</i> (<i>acc.</i> in) (<i>греч.</i>)\nисступление, восторг, экстаз <i>Eccl</i>."],
  ["ectasis", "<big>ectasis</big>\n\n<b>ectasis, is</b> (<i>acc.</i> in) <i>f</i> (<i>греч.</i>) <i>стих.</i>\nудлинение (растяжение) краткого гласного звука."],
  ["ectroma", "<big>ectroma</big>\n\n<b>ectr&#333;ma, atis</b> <i>n</i> (<i>греч.</i>; <i>лат.</i> abortus)\nпреждевременные роды, выкидыш <i>Tert</i>."],
  ["ectropa", "<big>ectropa</big>\n\n<b>ectropa, ae</b> <i>f</i> (<i>греч.</i>; <i>лат.</i> deversorium)\nпристанище, убежище <i>Vr</i>."],
  ["ectypus", "<big>ectypus</big>\n\n<b>ectypus, a, um</b> (<i>греч.</i>)\nвыпуклый (imago <i>Sen</i>; gemma <i>PM</i>)."],
  ["ecu-", "<big>ecu-</big>\n\n<b>ecu-</b> <i>v. l.</i>\n= equu-."],
  ["edacitas", "<big>edacitas</big>\n\n<b>ed&#257;cit&#257;s, &#257;tis</b> <i>f</i> [edax]\nпрожорливость <i>Pl, C etc.</i>"],
  ["edax", "<big>edax</big>\n\n<b>ed&#257;x, &#257;cis</b> [edo]\n<b>1)</b> прожорливый (homo <i>Ter, C</i>; animal <i>Sen etc.</i>; parasitus <i>Ap</i>);\n<b>2)</b> едкий, разъедающий (humor <i>CA</i>); острый, пряный (cibus <i>CA</i>); разрушительный, истребляющий (ignis <i>V</i>; imber <i>H</i>; tempus <i>O</i>, vetustas <i>O</i>);\n<b>3)</b> гложущий (cura <i>H</i>)."],
  ["edecimo", "<big>edecimo</big>\n\n<b>&#275;–decimo (-decumo), —, —, &#257;re</b>\nтщательно подбирать, отбирать <i>Macr etc.</i>"],
  ["edento", "<big>edento</big>\n\n<b>&#275;dento, &#257;v&#299;, &#257;tum, &#257;re</b> [ex + dens]\nлишать зубов (edentata belua <i>Macr</i>); e. malas alicui <i>Pl</i> лишать чьи-л. челюсти зубов, <i>т. е.</i> вышибать у кого-л. зубы."],
  ["edentulus", "<big>edentulus</big>\n\n<b>&#275;dentulus, a, um</b> [ex + dens]\n<b>1)</b> беззубый (vetula <i>Pl</i>; bestia <i>Tert</i>);\n<b>2)</b> <i>шутл.</i> старый (vinum edentulum <i>Pl</i>)."],
  ["edepol", "<big>edepol</big>\n\n<b>edepol</b> [ecce + deus +  Pollux]\nклянусь Поллуксом!, право же! (e. profecto <i>Pl</i>: certe e. <i>Pl, Ter</i>: non e. <i>Pl, Ter</i>; e. vero <i>Pl</i>)."],
  ["eder-", "<big>eder-</big>\n\n<b>eder-</b> <i>v. l.</i>\n= heder-."],
  ["Edessa", "<big>Edessa</big>\n\n<b>Edessa, ae</b> <i>f</i>\nЭдесса:\n<b>1)</b> <i>город в сев.-зап. Месопотамии, в области Osroene</i> <i>PM, T etc.</i>;\n<b>2)</b> <i>город в македонской области Эмафия, к западу от Пеллы</i> (<i>прежде</i> Aegae) <i>L, Just</i>."],
  ["Edessaeus", "<big>Edessaeus</big>\n\n<b>Edessaeus, a, um</b> [Edessa 2] <i>и</i> <b>Edess&#275;nus, a, um</b> [Edessa 1]\nэдесский <i>L, Amm</i>."],
  ["edi 1", "<big>edi</big>\n\n<b>I &#275;d&#299;</b> <i>pf. к</i>\nedo II."],
  ["edi 2", "<big>edi</big>\n\n<b>II &#275;d&#299;</b> <i>inf. pass. к</i>\nedo I."],
  ["edice", "<big>edice</big>\n\n<b>&#275;d&#299;ce</b> <i>V, St imper. к</i>\nedico."],
  ["edico", "<big>edico</big>\n\n<b>&#275;–d&#299;co, d&#299;x&#299;, dictum, ere</b>\n<b>1)</b> объявлять (edixit praedam militum fore <i>L</i>);\n<b>2)</b> приказывать (edixit, ut omnes adessent <i>C</i>; e. universi orbi, ne quis... <i>Ap</i>);\n<b>3)</b> назначать (e. diem comitiis <i>L</i>): e. senatum in posterum diem <i>L</i> назначить заседание сената на следующий день."],
  ["edictalis", "<big>edictalis</big>\n\n<b>&#275;dict&#257;lis, e</b> [edictum]\nпредусмотренный (предписанный, установленный) эдиктом (possessio bonorum <i>Dig</i>)."],
  ["edictio", "<big>edictio</big>\n\n<b>&#275;dictio, &#333;nis</b> <i>f</i> [edico]\nобъявление, распоряжение (basilicae edictiones <i>Pl</i>)."],
  ["edicto", "<big>edicto</big>\n\n<b>&#275;dicto, &#257;v&#299;, —, &#257;re</b> [<i>intens. к</i> edico]\nобъявлять, заявлять во всеуслышание <i>Pl</i>."],
  ["edictum", "<big>edictum</big>\n\n<b>&#275;dictum, &#299;</b> <i>n</i> [edico]\n<b>1)</b> высказывание, положение <i>Sen</i> (<i>v. l. к</i> dictum);\n<b>2)</b> приказ, предписание <i>Ter</i>; <i>преим.</i> эдикт, официальное распоряжение (e. constituere, proponere <i>C</i>): e. praemittere <i>Cs</i> послать распоряжение вперёд;\n<b>3)</b> эдикт, <i>т. е. распоряжение претора при вступлении его в должность с указанием принципов, которыми он будет руководствоваться в области судопроизводства</i> <i>C</i>: e. perpetuum <i>Eutr</i> постоянный преторский эдикт (<i>выработанный на основании прежде обнародованных и издававшийся каждым новым претором лишь с небольшими изменениями</i>);\n<b>4)</b> объявление об общественных играх (edictum et ludorum ordinem perlegere <i>Sen</i>)."],
  ["edidi", "<big>edidi</big>\n\n<b>&#275;did&#299;</b> <i>pf. к</i>\nedo I."],
  ["edidici", "<big>edidici</big>\n\n<b>&#275;didic&#299;</b> <i>pf. к</i>\nedisco."],
  ["edim", "<big>edim</big>\n\n<b>edim, &#299;s, it</b> <i>арх. Pl, H praes. conjct. к</i>\nedo II."],
  ["edisco", "<big>edisco</big>\n\n<b>&#275;–disco, didic&#299;, —, ere</b>\n<b>1)</b> выучивать наизусть (magnum numerum versuum <i>Cs</i>): e. aliquid ad verbum <i>C</i> заучивать что-л. слово в слово;\n<b>2)</b> изучать (artem <i>C, O</i>; linguam, usum herbarum <i>O</i>): edidici <i>Tib, O</i> я узнал, <i>т. е.</i> знаю."],
  ["edissero", "<big>edissero</big>\n\n<b>&#275;–dissero, ru&#299;, rtum, ere</b>\nподробно излагать, развивать, объяснять (res gestas <i>L</i>; de aliqua re <i>Lampr</i>)."],
  ["edissertatio", "<big>edissertatio</big>\n\n<b>&#275;dissert&#257;tio, &#333;nis</b> <i>f</i> [edisserto]\nобстоятельное изложение, обсуждение <i>PM</i>."],
  ["edissertator", "<big>edissertator</big>\n\n<b>&#275;dissert&#257;tor, &#333;ris</b> <i>m</i> [edisserto]\nобъяснитель, истолкователь (rerum vetustarum <i>Aus</i>)."],
  ["edissertio", "<big>edissertio</big>\n\n<b>&#275;dissertio, &#333;nis</b> <i>f</i> <i>Hier</i>\n= edissertatio."],
  ["edisserto", "<big>edisserto</big>\n\n<b>&#275;disserto, &#257;v&#299;, &#257;tum, &#257;re</b> [<i>intens. к</i> edissero]\nобстоятельно обсуждать, излагать, развивать (aliquid <i>Pl etc.</i>)."],
  ["edita", "<big>edita</big>\n\n<b>&#275;dita, &#333;rum</b> <i>n</i> [edo I]\n<b>1)</b> приказания, повеления (peragere edita alicujus <i>O</i>);\n<b>2)</b> дела, творения, подвиги (praeclara e. <i>Ap</i>);\n<b>3)</b> <i>см.</i> editum."],
  ["editicius", "<big>editicius</big>\n\n<b>&#275;dit&#299;cius, a, um</b> [edo I]\nуказанный, предложенный: judices editicii <i>C</i> судьи, намеченные из числа всадников и эрарных трибунов (<i>в количестве 125 человек, из которых обвиняемый мог отвести до 75</i>)."],
  ["editio", "<big>editio</big>\n\n<b>&#275;ditio, &#333;nis</b> <i>f</i> [edo I]\n<b>1)</b> издание (libri <i>Q, PM, Sen</i>);\n<b>2)</b> изданное произведение (hic versus in omni editione invenitur <i>Q</i>);\n<b>3)</b> указание, сообщение, заявление <i>L, Dig</i>;\n<b>4)</b> подготовка, устройство, организация (munerum <i>Eutr</i>; arenae <i>Symm</i>);\n<b>5)</b> назначение, выделение: e. tribuum <i>C</i> указание обвинителем триб, из которых должны быть избраны судьи;\n<b>6)</b> рождение (детей) <i>Dig, Tert</i>."],
  ["editor", "<big>editor</big>\n\n<b>&#275;ditor, &#333;ris</b> <i>m</i> [edo I]\n<b>1)</b> выделяющий из себя, производящий (nocturnae e. aurae, <i>sc.</i> flumen <i>Lcn</i>);\n<b>2)</b> устроитель, организатор (ludorum <i>Capit, Vop</i>)."],
  ["editum", "<big>editum</big>\n\n<b>&#275;ditum, &#299;</b> <i>n</i> [editus II]\nвысота, возвышенность (in edito <i>Su</i>; ex edito <i>PM</i>): edita (montium) <i>T, Q, C</i> возвышенность, высокогорная местность, нагорье."],
  ["editus 1", "<big>editus</big>\n\n<b>I &#275;ditus, &#299;</b> <i>m</i> [edo I] <i>поэт.</i>\nпотомок, отпрыск: e. atavis regibus <i>H</i> отпрыск царственных предков."],
  ["editus 2", "<big>editus</big>\n\n<b>II &#275;ditus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> edo I;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> возвышенный, высокий (montes <i>Sen</i>); возвышающийся (collis ex planitie paululum e. <i>Cs</i>): loca edita <i>C, Sen</i> возвышенность;\n<b>2)</b> высоко расположенный (villa editissima <i>T</i>);\n<b>3)</b> выдающийся (aliqua re <i>H</i>): e. in altum <i>C</i> высоко вознесённый, высокопоставленный."],
  ["editus 3", "<big>editus</big>\n\n<b>III &#275;ditus, (&#363;s)</b> <i>m</i> [edo I]\nпомёт, экскременты (bubum <i>Dig</i>)."],
  ["edo 1", "<big>edo</big>\n\n<b>I &#275;–do, did&#299;, ditum, ere</b>\n<b>1)</b> издавать, испускать (animam, extremum vitae spiritum <i>C</i>): e. clamorem <i>C</i> кричать; e. latratus <i>O</i> лаять; e. vitam <i>C</i> отдать жизнь, умереть; anima edita <i>Ap</i> после кончины; aetate jam edita <i>AG</i> на склоне жизни; e. scintillas <i>PM</i> метать искры; flumen editur in mare <i>L</i> река впадает в море; e. se ex aedibus foras <i>Pl</i> уйти из дому;\n<b>2)</b> выделять (stercus <i>Col</i>; urinam <i>PM</i>); e. tussi откашливать (sputa <i>Lcr</i>);\n<b>3)</b> производить (terra edit innumeras species <i>O</i>); рождать (Latona geminos edidit <i>O</i>): edi in lucem <i>C, O</i> родиться на свет; arbor edit frondem <i>Col</i> дерево покрывается листвой; partum e. <i>C, L, T</i> производить потомство;\n<b>4)</b> выпускать в свет, издавать (librum <i>C</i>);\n<b>5)</b> учинять, совершать (e. caedem <i>L</i>; scelus <i>C</i>): ruinas e. <i>C</i> производить опустошения;\n<b>6)</b> творить, создавать (opera immortalia <i>L</i>): e. pugnam <i>Lcr</i> дать сражение; e. exemplum <i>C</i> показать пример;\n<b>7)</b> устраивать (ludos, spectaculum gladiatorium <i>T</i>): fortium virorum operam e. <i>L</i> выказать себя храбрыми мужами; nullum fructum ex se e. <i>C</i> не давать никакого дохода (прибыли); corpus super equum e. <i>Tib</i> вскочить на лошадь; e. operam <i>Su</i> помогать;\n<b>8)</b> объявлять, сообщать (nomen <i>L</i>): ede, quid ista velit sibi fabula <i>Pl</i> расскажи, что означает эта басня || произносить, высказывать (verba, judicium, oraculum <i>C</i>): nec scriptum habeo nec possum e. <i>C</i> записей у меня нет, а потому ничего сказать не могу;\n<b>9)</b> назначать (e. aliud tempus ac locum <i>Q</i>): e. aliquem auctorem <i>L</i> назвать (указать, упомянуть) кого-л. в качестве автора (виновника, свидетеля); opinio in vulgus edita <i>Cs</i> широко распространённое мнение; e. tribus <i>C</i> указать (назвать) четыре трибы, из которых должны быть избраны судьи (<i>истец мог отвести лишь одну трибу</i>);\n<b>10)</b> <i>поэт.</i> воспевать (arma, bella <i>O</i>)."],
  ["edo 2", "<big>edo</big>\n\n<b>II edo, &#275;d&#299;, &#275;sum (&#275;ssum), edere (&#275;sse)</b> (<i>формы гл.</i> sum <i>на</i> es- <i>могут употребляться в значении соответствующих форм гл.</i> edo)\n<b>1)</b> есть, кушать (avide <i>O</i>; aliquid cum pane <i>PM</i>): esse (= edere) oportet, ut vivas, non vivere, ut edas <i>rhH</i> нужно есть, чтобы жить, а не жить, чтобы есть; pugnos e. <i>погов. Pl</i> попробовать кулаков, <i>т. е.</i> быть избитым; multos modios salis simul edisse <i>погов. C</i> съесть вместе немало модиев соли, <i>т. е.</i> издавна дружить; e. de patella <i>погов. C</i> презрительно относиться к религиозным обрядам (<i>см.</i> patella); dies noclesque estur, bibitur (<i>impers.</i>) <i>Pl</i> круглые сутки едят и пьют;\n<b>2)</b> проедать, прокучивать (bona <i>Pl</i>);\n<b>3)</b> пожирать, истреблять (culmos est — <i>или</i> edit — robigo <i>V</i>; carinas est lentus vapor = flamma <i>V</i>; est flamma medullas <i>V</i>): estur vitiata teredine navis <i>O</i> корабль разрушается древоточцами || снедать, глодать (si quid est animum <i>H</i>)."],
  ["edo 3", "<big>edo</big>\n\n<b>III edo, &#333;nis</b> <i>m</i> [edo 11]\nобжора <i>Macr</i>."],
  ["edocenter", "<big>edocenter</big>\n\n<b>&#275;docenter</b> [edoceo]\nвесьма поучительно, основательно разъясняя (scribere <i>AG</i>)."],
  ["edoceo", "<big>edoceo</big>\n\n<b>&#275;–doceo, cu&#299;, ctum, &#275;re</b>\n<b>1)</b> выучивать, учить, обучать (aliquem aliquid <i>Pl, Sl</i>): edoctus artes belli <i>L</i> сведущий в военных науках;\n<b>2)</b> подробно рассказывать, уведомлять, извещать (aliquem de aliqua re <i>Just</i>); сообщить (omnia ordine <i>L</i>; e. praecepta parentis <i>V</i>): cuncta edoctus <i>Sl</i> извещённый (знающий) обо всём."],
  ["edolo", "<big>edolo</big>\n\n<b>&#275;–dolo, &#257;v&#299;, &#257;tum, &#257;re</b>\n<b>1)</b> обтёсывать, обрубать, обделывать (aliquid <i>Col</i>);\n<b>2)</b> отделывать, оканчивать (quod jusseras, edolavi <i>C</i>);\n<b>3)</b> <i>шутл.</i> смастерить, сколачивать (libellum <i>Enn ap. Macr</i>)."],
  ["edomo", "<big>edomo</big>\n\n<b>&#275;–domo, domu&#299;, domitum</b> (<b>dom&#257;tum</b> <i>Aug</i>), <b>&#257;re</b>\n<b>1)</b> укрощать, обуздывать, приручать (equos <i>Cld etc.</i>); одолевать, покорять (orbem terrarum <i>O</i>): edomita lumina <i>Sil</i> сонные глаза;\n<b>2)</b> одомашнивать, делать культурным (planta edomita <i>PM</i>);\n<b>3)</b> обрабатывать (aes igni <i>PM</i>);\n<b>4)</b> смягчать, воспитывать (vitiosam naturam doctrina <i>C</i>)."],
  ["Edoni", "<big>Edoni</big>\n\n<b>&#274;d&#333;n&#299;, &#333;rum</b> <i>m</i>\nэдоны, <i>фракийск. народность между реками Стримоном и Нестом, особо чтившая Вакха</i> <i>PM, H</i>."],
  ["Edonis", "<big>Edonis</big>\n\n<b>&#274;d&#333;nis, idis</b> <i>f</i> [Edoni]\n<b>1.</b> <i>adj.</i> эдонская, <i>перен.</i> фракийская (matres <i>O</i>);\n<b>2.</b> <i>subst.</i> вакханка <i>Prp, Sil, Lcn</i>."],
  ["Edonus", "<big>Edonus</big>\n\n<b>&#274;d&#333;nus, a, um</b> [Edoni]\nэдонский (populi <i>Sol</i>); <i>перен.</i> фракийский (Boreae <i>V</i>; juga <i>O</i>; hiemes <i>Lcn, Sil</i>)."],
  ["edormio", "<big>edormio</big>\n\n<b>&#275;–dormio, &#299;v&#299;, &#299;tum, &#299;re</b>\n<b>1)</b> проспаться (vinolentus edormivit <i>C</i>);\n<b>2)</b> проспать (dimidium ex hoc tempore <i>Sen</i>): e. crapulam <i>C</i> проспать хмель, протрезвиться; Ilionam e. <i>H</i> играть роль спящей Илионы."],
  ["edormisco", "<big>edormisco</big>\n\n<b>&#275;dormisco, —, —, ere</b> [<i>inchoat. к</i> edormio]\nпроспать (crapulam <i>Pl</i>): e. unum somnum <i>Pl</i> вздремнуть, поспать."],
  ["educ", "<big>educ</big>\n\n<b>&#275;d&#363;c</b>, <i>арх.</i> <b>&#275;d&#363;ce</b> <i>imper. к</i>\neduco II."],
  ["educatio", "<big>educatio</big>\n\n<b>&#275;duc&#257;tio, &#333;nis</b> <i>f</i> [educo I]\n<b>1)</b> воспитание (liberorum <i>C</i>);\n<b>2)</b> разведение (<i>sc.</i> bestiarum <i>Col</i>);</i>; выращивание (<i>sc.</i> plantarum <i>PM, Macr</i>)."],
  ["educator", "<big>educator</big>\n\n<b>&#275;duc&#257;tor, &#333;ris</b> <i>m</i> [educo 1]\n<b>1)</b> воспитатель (pueritiae alicujus <i>T</i>); кормилец <i>C etc.</i>;\n<b>2)</b> руководитель, наставник <i>T, Sen</i>."],
  ["educatrix", "<big>educatrix</big>\n\n<b>&#275;duc&#257;tr&#299;x, &#299;cis</b> <i>f</i>\nвоспитательница, наставница <i>C etc.</i>"],
  ["educatus", "<big>educatus</big>\n\n<b>&#275;duc&#257;tus, &#363;s</b> <i>m</i> <i>Tert</i>\n= educatio."],
  ["educe", "<big>educe</big>\n\n<b>&#275;d&#363;ce</b> — <i>см.</i>\neduc."],
  ["educo 1", "<big>educo</big>\n\n<b>I &#275;–duco, &#257;v&#299;, &#257;tum, &#257;re</b> [<i>intens. к</i> educo II]\n<b>1)</b> воспитывать (homo bene educatus <i>Pl</i>); выращивать, создавать (oratorem <i>Q</i>);\n<b>2)</b> <i>поэт.</i> взращивать, производить, кормить, питать (imber educat flores <i>Ctl</i>; ager educat uvas <i>O</i>)."],
  ["educo 2", "<big>educo</big>\n\n<b>II &#275;–d&#363;co, d&#363;x&#299;, ductum, ere</b>\n<b>1)</b> выводить (aliquem de senatu <i>C</i>; copias e castris <i>Cs</i>); уводить (aliquem ad consules <i>C</i>; aliquem ab domo secum <i>Cs</i>): se e. уходить, удаляться (foras <i>Pl</i>; multitudini <i>Sen</i>) || привлекать, вызывать (aliquem in jus <i>C</i>); выступить, двинуться (ex hibernis <i>Cs</i>); вывозить (equos ex Italia <i>L</i>); отводить (lacum <i>C</i>; aquam in fossas <i>PM</i>); извлекать (gladium e vagina <i>C</i>); вынимать, тянуть (sortem <i>C</i>); вырывать, извлекать (radicem e terra <i>Vr</i>; corpore telum <i>rhH</i>); выпивать (aliquid <i>Pl</i>);\n<b>2)</b> выстраивать, развёртывать (in aciem <i>L</i>);\n<b>3)</b> высиживать, выводить (pullos suos <i>Pl</i>); рождать (aliquem <i>V</i>);\n<b>4)</b> подводить: rationem e. <i>Pl</i> подводить итог, сосчитывать;\n<b>5)</b> воспитывать (aliquem a parvulo <i>Ter</i>);\n<b>6)</b> поднимать (<i>sc.</i> aulaea <i>O</i>);\n<b>7)</b> (пре)возносить (aliquem ad superas auras <i>V</i> <i>и</i> in astra <i>H</i>);\n<b>8)</b> воздвигать (aram caelo <i>V</i>); возводить, выстраивать (turrim sub astra <i>V</i>);\n<b>9)</b> прожить (pios annos <i>Prp</i>); провести (insomnem noctem <i>St</i>);\n<b>10)</b> переводить (aliquid de potentia in actum <i>Eccl</i>)."],
  ["eductio", "<big>eductio</big>\n\n<b>&#275;ductio, &#333;nis</b> <i>f</i> [educo II]\n<b>1)</b> выступление (e castris <i>Cato</i>);\n<b>2)</b> продолжение <i>или</i> расширение (uberis <i>Pall</i>)."],
  ["eductor", "<big>eductor</big>\n\n<b>&#275;ductor, &#333;ris</b> <i>m</i> [educo I]\nвоспитатель <i>Fronto</i>."],
  ["edulco", "<big>edulco</big>\n\n<b>&#275;–dulco, —, —, &#257;re</b>\nподслащивать, <i>перен.</i> услаждать (vitam <i>Matius ap. AG</i>)."],
  ["edulia", "<big>edulia</big>\n\n<b>ed&#363;lia, ium</b> <i>n</i>\nсъестные припасы, продовольствие <i>Su, AG</i>; кушанья, яства <i>Ap</i>."],
  ["edulis", "<big>edulis</big>\n\n<b>ed&#363;lis, e</b> [edo II]\nгодный в пищу, съедобный (capreae <i>H</i>)."],
  ["edulium", "<big>edulium</big>\n\n<b>ed&#363;lium, &#299;</b> <i>n</i> <i>Vr, AG etc.</i>\n= edulia."],
  ["eduresco", "<big>eduresco</big>\n\n<b>&#275;d&#363;r&#275;sco, —, —, ere</b>\nстановиться твёрдым, твердеть <i>CA</i>."],
  ["eduro", "<big>eduro</big>\n\n<b>&#275;–d&#363;ro, —, —, &#257;re</b>\n<b>1)</b> закалять (rusticis operibus edurandus <i>Cato</i>);\n<b>2)</b> продолжаться <i>AG</i>: in ortus e. <i>T</i> длиться до рассвета."],
  ["edurus", "<big>edurus</big>\n\n<b>&#275;–d&#363;rus (ecd&#363;rus), a, um</b>\n<b>1)</b> довольно жёсткий, твёрдый (pirus <i>V</i>);\n<b>2)</b> безжалостный (os <i>O</i>)."],
  ["edus", "<big>edus</big>\n\n<b>&#275;dus, &#299;</b> <i>m</i> <i>v. l.</i>\n= haedus."],
  ["eduxi", "<big>eduxi</big>\n\n<b>&#275;d&#363;x&#299;</b> <i>pf. к</i>\neduco II."],
  ["edyllium", "<big>edyllium</big>\n\n<b>&#275;dyllium</b> <i>v. l.</i>\n= idyllium."],
  ["Eetion", "<big>Eetion</big>\n\n<b>&#274;eti&#333;n, &#333;nis</b> <i>m</i>\n<i>царь города Thebe Hypoplakie (Мисия), отец Андромахи, убитый Ахиллом</i> <i>O</i>."],
  ["Eetioneus", "<big>Eetioneus</big>\n\n<b>&#274;eti&#333;n&#275;us, a, um</b> [Eetion]\nээтионов <i>O</i>."],
  ["ef-", "<big>ef-</big>\n\n<b>ef-</b>\n= ex- <i>перед начальным</i> f <i>основного слова.</i>"],
  ["effabilis", "<big>effabilis</big>\n\n<b>eff&#257;bilis, e</b> [effor]\nвыразимый (nemini <i>Ap</i>)."],
  ["effaecatus", "<big>effaecatus</big>\n\n<b>ef–faec&#257;tus, a, um</b> [faex]\nпроцеженный, отстоявшийся, освобождённый от гущи, <i>перен.</i> очищенный (animi voluptas <i>Ap</i>)."],
  ["effarcio", "<big>effarcio</big>\n\n<b>ef–farcio (effercio), —, fertum, &#299;re</b>\nнабивать, наполнять (intervalla saxis <i>Cs</i>): se e. <i>Pl</i> наесться до отвала."],
  ["effascinatio", "<big>effascinatio</big>\n\n<b>effascin&#257;tio, &#333;nis</b> <i>f</i> [effascino]\nоколдовывание, зачаровывание <i>PM</i>."],
  ["effascino", "<big>effascino</big>\n\n<b>ef–fascino, —, —, &#257;re</b>\nоколдовать, заворожить <i>PM, AG</i>."],
  ["effatio", "<big>effatio</big>\n\n<b>ef–f&#257;tio, &#333;nis</b> <i>f</i> [for]\n<b>1)</b> произнесение, возвещение (verborum <i>Eccl</i>);\n<b>2)</b> утрата голоса <i>или</i> речи <i>CA</i>."],
  ["effatum", "<big>effatum</big>\n\n<b>eff&#257;tum, &#299;</b> <i>n</i> [effor]\n<b>1)</b> изречение, пророчество (fatidicorum <i>C</i>);\n<b>2)</b> заклятие, магическая формула (e. augurum <i>Vr</i>);\n<b>3)</b> <i>лог.</i> высказывание, положение <i>Sen</i>."],
  ["effatus 1", "<big>effatus</big>\n\n<b>I eff&#257;tus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> effor;\n<b>2.</b> <i>adj. культ.</i> очерченный (авгурами), отведённый, освящённый (locus templo e. <i>C, L</i>)."],
  ["effatus 2", "<big>effatus</big>\n\n<b>II eff&#257;tus, &#363;s</b> <i>m</i>\nразговор, речь <i>Tert</i>."],
  ["effecatus", "<big>effecatus</big>\n\n<b>eff&#275;c&#257;tus, a, um</b> <i>v. l.</i>\n= effaecatus."],
  ["effeci", "<big>effeci</big>\n\n<b>eff&#275;c&#299;</b> <i>pf. к</i>\nefficio."],
  ["effecte", "<big>effecte</big>\n\n<b>effect&#275;</b> [efficio]\nпревосходно, отлично <i>M, Ap, Amm</i>."],
  ["effectio", "<big>effectio</big>\n\n<b>effectio, &#333;nis</b> <i>f</i> [efficio]\n<b>1)</b> делание, совершение, приведение в действие: e. artis <i>C</i> показывание (своего) искусства (умения); recta e. <i>C</i> правильный образ действий;\n<b>2)</b> действующая (созидательная) сила <i>C, Aug</i>."],
  ["effectivus", "<big>effectivus</big>\n\n<b>effect&#299;vus, a, um</b> [efficio]\nтворческий, действенный, созидательный (ars <i>Q</i>; causa <i>Bo&euml;t</i>)."],
  ["effector", "<big>effector</big>\n\n<b>effector, &#333;ris</b> <i>m</i> [efficio]\nсоздатель, зиждитель, творец (mundi <i>C</i>; rerum creatarum <i>Aug</i>): dicendi e. <i>C</i> учитель красноречия (стиля)."],
  ["effectorius", "<big>effectorius</big>\n\n<b>effect&#333;rius, a, um</b> <i>Aug</i>\n= effectivus."],
  ["effectrix", "<big>effectrix</big>\n\n<b>effectr&#299;x, &#299;cis</b> <i>f</i> [efficio]\n<b>1)</b> создательница, виновница (diei noctisque <i>C</i>);\n<b>2)</b> источник (pecunia e. multarum voluptatum <i>C</i>)."],
  ["effectus 1", "<big>effectus</big>\n\n<b>I effectus, &#363;s</b> <i>m</i> [efficio]\n<b>1)</b> исполнение, осуществление (conatus, spei <i>L</i>): ad effectum adducere <i>L</i> привести в исполнение; in effectu esse <i>C</i> близиться к окончанию;\n<b>2)</b> действие, эффект (herbarum <i>C</i>); воздействие, влияние (eloquentiae <i>C</i>; e. alicujus rei in aliqua re <i>Ap</i>);\n<b>3)</b> результат (sine ullo effectu <i>L</i>)."],
  ["effectus 2", "<big>effectus</big>\n\n<b>II effectus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> efficio;\n<b>2.</b> <i>adj.</i> завершённый, законченный, развитой <i>Q, PJ</i>: qua nihil videor effectius cognovisse <i>Ap</i> (статуя), совершеннее которой я, кажется, ничего не знал."],
  ["effecundo", "<big>effecundo</big>\n\n<b>ef–f&#275;cundo, —, —, &#257;re</b> [fecundo]\nделать плодородным (solum <i>Vop</i>)."],
  ["effeminate", "<big>effeminate</big>\n\n<b>eff&#275;min&#257;t&#275;</b> [effeminatus]\nизнеженно, слабо, вяло <i>C, Sen, VM</i>."],
  ["effeminatio", "<big>effeminatio</big>\n\n<b>eff&#275;min&#257;tio, &#333;nis</b> <i>f</i>\nженоподобность, изнеженность (animi <i>Hier, Tert</i>)."],
  ["effeminatus", "<big>effeminatus</big>\n\n<b>eff&#275;min&#257;tus, a, um</b> [effemino]\nженоподобный, женственный, изнеженный (languor <i>C</i>; corpus <i>L</i>; vox <i>Sen</i>; gens <i>Just etc.</i>)."],
  ["effemino", "<big>effemino</big>\n\n<b>ef–f&#275;mino, &#257;v&#299;, &#257;tum, &#257;re</b> [femina]\n<b>1)</b> делать женоподобным, расслаблять, изнеживать (animos <i>Cs</i>; corpus animumque virilem <i>Sl</i>);\n<b>2)</b> превращать в существо женского пола (aerem <i>C</i>);\n<b>3)</b> позорить, осквернять (titulum anni <i>Cld</i>)."],
  ["efferasco", "<big>efferasco</big>\n\n<b>effer&#257;sco, —, —, ere</b> [efferus]\nдичать <i>Amm</i>."],
  ["efferate", "<big>efferate</big>\n\n<b>effer&#257;t&#275;</b>\nдико, свирепо (saevire <i>Lact</i>)."],
  ["efferatio", "<big>efferatio</big>\n\n<b>effer&#257;tio, &#333;nis</b> <i>f</i> [effero II]\nодичание <i>Hier</i>."],
  ["efferatus", "<big>efferatus</big>\n\n<b>effer&#257;tus, a, um</b> [effero II]\nодичалый, свирепый, дикий (gens <i>C</i>; mores <i>L</i>); обезумевший (gaudio <i>Amm</i>): ex dolore in rabiem e. <i>Pt</i> пришедший в бешенство от горя."],
  ["effercio", "<big>effercio</big>\n\n<b>effercio</b> <i>v. l.</i>\n= effarcio."],
  ["efferitas", "<big>efferitas</big>\n\n<b>efferit&#257;s, &#257;tis</b> <i>f</i> [efferus]\nдикость <i>C</i>."],
  ["effero 1", "<big>effero</big>\n\n<b>I ef–fero (ecfero), extul&#299;, &#275;l&#257;tum, efferre</b> [fero]\n<b>1)</b> выносить (aliquid ex aedibus <i>C</i>): e. pedem domo <i>C</i> (<i>или</i> se e. <i>V, T etc.</i>) выходить из дому; e. signa (vexilla, arma) <i>L</i> выступить в поход; arma extra fines e. <i>L</i> перейти границы с оружием в руках || выгружать (aliquos pro sarcinis <i>Pt</i>);\n<b>2)</b> вывозить (frumentum ex hibernis <i>L</i>);\n<b>3)</b> издавать, испускать (clamorem <i>Pl</i>): e. lucem <i>V</i> испускать свет, сиять;\n<b>4)</b> приносить (effer aquam <i>V</i>; ager effert fruges <i>C</i>);\n<b>5)</b> хоронить (aliquem <i>Pl etc.</i>): sine ulla funeris pompa efferri <i>Nep</i> быть похороненным без всякой торжественности; bene elatus est <i>Pt</i> ему были устроены пышные похороны;\n<b>6)</b> претерпевать, выносить (quemvis laborem <i>Lcr</i>; malum patiendo e. <i>Poeta ap. C</i>);\n<b>7)</b> вносить, записывать (in album <i>C</i>);\n<b>8)</b> произносить, выражать (graves sententias <i>C</i>): alio atque alio elatus verbo <i>C</i> выраженный в той или иной форме;\n<b>9)</b> делать известным, разглашать, распространять (e. clandestina consilia <i>Cs</i>): efferri in vulgum <i>Cs</i> стать достоянием широких масс;\n<b>10)</b> уносить, увлекать, завлекать (impetus eum extulit ad castra <i>L</i>): efferri gaudio <i>C, Su</i> не помнить себя от радости; efferri dolore (odio) <i>C etc.</i> быть вне себя от скорби (ненависти);\n<b>11)</b> воздвигать (turris in altitudinem elata <i>Cs</i>);\n<b>12)</b> поднимать (aliquem in murum <i>Cs</i>): pulvis elatus <i>L</i> поднявшаяся пыль; corvus e conspectu elatus orientem petit <i>L</i> ворон направил свой полёт на восток и скрылся из виду; elatis super capita scutis <i>T</i> подняв щиты над головой; aliquem in summum odium e. <i>T</i> сделать кого-л. предметом сильнейшей ненависти;\n<b>13)</b> возвысить (aliquem ad summum imperium <i>C</i>; aliquem supra leges <i>T</i>): equus se effert <i>Q</i> лошадь становится на дыбы;\n<b>14)</b> превозносить, прославлять, хвалить (aliquem laudibus <i>C</i>; aliquid verbis <i>C</i>): e. se проявляться, выказывать себя (virtus se effert <i>C</i>); e. se <i>или</i> <i>pass.</i> efferri зазнаваться, возгордиться (recenti victoria <i>Cs</i>; opibus <i>Nep</i>)."],
  ["effero 2", "<big>effero</big>\n\n<b>II effero, &#257;v&#299;, &#257;tum, &#257;re</b> [efferus]\n<b>1)</b> делать диким, свирепым (animos <i>L</i>): speciem oris e. <i>L</i> придавать лицу свирепый вид;\n<b>2)</b> приводить в бешенство, озлоблять (ea caedes efferavit Thebanos <i>L</i>): dolore efferatus <i>QC</i> обезумевший от боли;\n<b>3)</b> придавать вид (форму) дикого животного (e. argentum <i>Ap</i>), <i>но</i>: e. aurum <i>St</i> использовать золото для войны."],
  ["effertus", "<big>effertus</big>\n\n<b>effertus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> effarcio;\n<b>2.</b> <i>adj.</i> набитый, наполненный: e. fame <i>Pl</i> сильно проголодавшийся; hereditas effertissima <i>Pl</i> богатейшее наследство."],
  ["efferus", "<big>efferus</big>\n\n<b>ef–ferus, a, um</b>\n<b>1)</b> одичалый, лютый, дикий, свирепый (proles <i>Lcr</i>; facta tyranni <i>V</i>);\n<b>2)</b> обезумевший (Dido <i>V</i>)."],
  ["effervens", "<big>effervens</big>\n\n<b>efferv&#275;ns, entis</b>\n<b>1.</b> <i>part. praes. к</i> efferveo;\n<b>2.</b> <i>adj.</i> волнующийся, взволнованный (gaudio <i>AG</i>)."],
  ["efferveo", "<big>efferveo</big>\n\n<b>efferveo, —, —, &#275;re</b> <i>Vtr</i>\n= effervo."],
  ["effervesco", "<big>effervesco</big>\n\n<b>ef–ferv&#275;sco, ferv&#299; (ferbu&#299;), —, ere</b>\n<b>1)</b> вскипать, взбурлить (aquae effervescunt <i>C</i>; aestus effervescit <i>Vr</i>);\n<b>2)</b> разгорячаться, вознегодовать (in ira <i>Lcr</i>); вспыхивать (sidera toto effervescunt caelo <i>O</i>);\n<b>3)</b> бродить (<i>о вине</i>) (merum effervescit <i>Sen</i>);\n<b>4)</b> сильно размножаться (locustae effervescunt <i>Eccl</i>);\n<b>5)</b> бурно устремляться, обрушиваться (Pontus armatus effervescens in Asiam <i>C</i>)."],
  ["effervo", "<big>effervo</big>\n\n<b>ef–fervo, —, —, ere</b> [ferveo]\n<b>1)</b> кипеть, клокотать, бурлить, извергаться (Aetna effervit in agros <i>V</i>);\n<b>2)</b> кишеть (vermes effervunt <i>Lcr</i>);\n<b>3)</b> взвиваться, клубиться: pulverea nube e. <i>St</i> окутаться облаком пыли."],
  ["effetus", "<big>effetus</big>\n\n<b>ef–f&#275;tus, a, um</b>\n<b>1)</b> родивший: canis effeta <i>Col</i> ощенившаяся сука;\n<b>2)</b> ослабленная частыми родами (uxor vetula et effeta <i>Ap</i>); <i>перен.</i> изнурённый (innumeris annis <i>O</i>); истощённый (tellus <i>Lcr</i>); слабый (corpus <i>C</i>; vires <i>V</i>): e. veri <i>V</i> неспособный постичь истину."],
  ["effexim", "<big>effexim</big>\n\n<b>effexim</b> <i>арх. Pl pf. conjct. к</i>\nefficio."],
  ["efficacia", "<big>efficacia</big>\n\n<b>effic&#257;cia, ae</b> <i>f</i> <i>PM, Amm</i>\n= efficacitas."],
  ["efficacitas", "<big>efficacitas</big>\n\n<b>effic&#257;cit&#257;s, &#257;tis</b> <i>f</i> [efficax]\nуспешная деятельность, действенность, эффективность <i>C</i>."],
  ["efficaciter", "<big>efficaciter</big>\n\n<b>effic&#257;citer</b> [efficax]\nдеятельно, действенно, успешно, с успехом <i>Sen, L, Q, PJ</i>."],
  ["efficax", "<big>efficax</big>\n\n<b>effic&#257;x, &#257;cis</b> [efficio]\n<b>1)</b> успешно действующий (preces <i>T</i>); верный, надёжный (breve et e. est iter per exempla <i>Sen</i>); действительный, действенный, эффективный (herba e. adversus serpentium venena <i>PM</i>): amara curarum eluere e. <i>H</i> способный отогнать горькие заботы;\n<b>2)</b> кипучий, неутомимый, деятельный (Hercules <i>H</i>)."],
  ["efficiens", "<big>efficiens</big>\n\n<b>effici&#275;ns, entis</b>\n<b>1.</b> <i>part. praes. к</i> efficio;\n<b>2.</b> <i>adj.</i> производящий, влекущий за собой (virtus e. est utilitatis <i>C</i>): causa e. <i>C</i> побудительная (действующая) причина."],
  ["efficienter", "<big>efficienter</big>\n\n<b>efficienter</b> [efficiens]\nдейственно, эффективно, действительно, успешно <i>C</i>."],
  ["efficientia 1", "<big>efficientia</big>\n\n<b>I efficientia, ium</b> <i>n</i>\nфакторы, предпосылки, побудительные причины <i>Q</i>."],
  ["efficientia 2", "<big>efficientia</big>\n\n<b>II efficientia, ae</b> <i>f</i> [efficio]\nдействие, действенность, сила, влияние <i>C, Eccl</i>."],
  ["efficio", "<big>efficio</big>\n\n<b>ef–ficio, f&#275;c&#299;, fectum, ere</b> [facio]\n<b>1)</b> делать, изготовлять (pontem, turres <i>Cs</i>): ex arena funis effici non potest <i>погов. Col</i> из песка верёвки не свить || приготовлять (panem <i>Cs</i>); лепить (deos <i>Q</i>); созидать, творить (mundum <i>C</i>); воздвигать (columnam <i>C</i>); строить (urbem ex latere <i>C</i>; naves <i>Cs</i>); совершать (mirabilia facinora <i>C</i>); превращать (aliquem puerum de virgine e. <i>O</i>);\n<b>2)</b> вызывать (clamores et admirationes <i>C</i>); причинять: bene uti pugnes, bene pugnans efficit hostis <i>O</i> хорошо сражающийся враг заставляет и тебя хорошо сражаться;\n<b>3)</b> назначать, избирать (aliquem consulem <i>C</i>);\n<b>4)</b> исполнять (mandata <i>C</i>); e. aliquid alicui <i>C</i> доставить кому-л. что-л.; e. aliquid ab aliquo <i>C</i> достать (получить) что-л. от кого-л.;\n<b>5)</b> образовать (sphaeram <i>C</i>; insulam <i>Cs</i>); echinos e. <i>Pt</i> казаться ежами, быть похожими на ежей; trunco, non frondibus e. umbram <i>Lcn</i> отбрасывать тень не листвой, а (лишь) стволом (<i>о старом, засохшем дубе</i>);\n<b>6)</b> производить (magnas commutationes <i>Cs</i>); id vitioso more effici solet <i>C</i> это обычно вызывается испорченностью нравов || приносить (ager ille plurimum efficit <i>C</i>): e. cum octavo <i>C</i> давать урожай сам-восемь;\n<b>7)</b> составлять (hoc efficiet tertium volumen <i>C</i>): vix, quod satis sit, e. <i>C</i> едва хватать;\n<b>8)</b> <i>воен.</i> набирать (exercitum <i>L</i>);\n<b>9)</b> доказывать (e., ut mors malum non sit <i>C</i>): ex quo efficitur <i>C</i> из чего следует (явствует) || подтверждать (rumorem <i>T</i>);\n<b>10)</b> переносить, терпеть (paupertatem non iniqua mente <i>O</i>)."],
  ["effictio", "<big>effictio</big>\n\n<b>effictio, &#333;nis</b> <i>f</i> [effingo]\nизображение, описание внешности (человека), внешняя характеристика <i>rhH</i>."],
  ["effictus", "<big>effictus</big>\n\n<b>effictus, a, um</b> <i>part. pf. к</i>\neffingo."],
  ["effieri", "<big>effieri</big>\n\n<b>effier&#299;</b> <i>Pl inf. praes. pass. к</i>\nefficio."],
  ["effigia", "<big>effigia</big>\n\n<b>effigia, ae</b> <i>f</i> <i>Pl, Lcr</i>\n= effigies."],
  ["effigiatus", "<big>effigiatus</big>\n\n<b>effigi&#257;tus, (&#363;s)</b> <i>m</i> [effigio]\nизображение, описание наружности: magna vis aeris vario effigiatu <i>Ap</i> множество бронзы в виде различных фигур."],
  ["effigies", "<big>effigies</big>\n\n<b>effigi&#275;s, &#275;&#299;</b> <i>f</i> [effingo]\n<b>1)</b> образ, портрет, изображение (Jovis ac Junonis <i>Su</i>);\n<b>2)</b> скульптурное изображение (e. saxea <i>Ctl</i>);\n<b>3)</b> кукла (e. cerea <i>H</i>);\n<b>4)</b> подобие (spirantis mortui <i>C</i>);\n<b>5)</b> отражение (in liquore <i>Ph</i>);\n<b>6)</b> образец, идеал (e. justi imperii <i>C</i>; perfectae eloquentiae <i>C</i>);\n<b>7)</b> призрак, тень (<i>умершего</i>) <i>Su, PJ, Sil, O</i>;\n<b>8)</b> описание (e. virtutum <i>C</i>)."],
  ["effigio", "<big>effigio</big>\n\n<b>effigio, &#257;v&#299;, &#257;tum, &#257;re</b> [effigies]\n<b>1)</b> изображать, воспроизводить (aliquem imaginibus <i>Sid</i>);\n<b>2)</b> обрабатывать (truncus dolamine effigiatus <i>Ap</i>)."],
  ["effindo", "<big>effindo</big>\n\n<b>ef–findo, —, —, ere</b>\nрассекать, <i>перен.</i> переплывать (fluctus <i>Man</i>)."],
  ["effingo", "<big>effingo</big>\n\n<b>ef–fingo, f&#299;nx&#299;, fictum, ere</b>\n<b>1)</b> представлять, изображать (aliquem coloribus, cera <i>PJ</i>); отобразить (pulchritudinem Veneris <i>C</i>): e. animo <i>C</i> представлять в воображении, воображать;\n<b>2)</b> выражать, обрисовывать, описывать (aliquem <i>C etc.</i>; virtutes alicujus <i>Ap</i>; quod sentio, carmine effingam <i>Pt</i>);\n<b>3)</b> воспроизводить, подражать (e. Horatium in lyricis <i>PJ</i>);\n<b>4)</b> ощупывать, гладить, ласкать (manus, <i>sc.</i> alicujus <i>O</i>);\n<b>5)</b> стирать (sanguinem spongiis <i>C</i>);\n<b>6)</b> вытирать, чистить (aliquid spongia <i>Cato</i>)."],
  ["effio", "<big>effio</big>\n\n<b>eff&#299;o, —, fier&#299;</b> <i>Pl pass. к</i>\nefficio."],
  ["efflagitatio", "<big>efflagitatio</big>\n\n<b>effl&#257;git&#257;tio, &#333;nis</b> <i>f</i> [efflagito]\nнастоятельная просьба, категорическое требование (alicujus rei <i>или</i> ad aliquid <i>C</i>)."],
  ["efflagitatus", "<big>efflagitatus</big>\n\n<b>effl&#257;git&#257;tus, (&#363;s)</b> <i>m</i> <i>C</i>\n= efflagitatio."],
  ["efflagito", "<big>efflagito</big>\n\n<b>ef–fl&#257;gito, &#257;v&#299;, &#257;tum, &#257;re</b>\nнастоятельно требовать, усиленно просить (aliquid ab aliquo <i>C, L, T etc.</i>): e. aliquem ad destinatam poenam <i>Ap</i> подвергать кого-л. назначенной каре."],
  ["efflatio", "<big>efflatio</big>\n\n<b>effl&#257;tio, &#333;nis</b> <i>f</i> [efflo]\nвыдыхание <i>Hier</i>."],
  ["efflatus", "<big>efflatus</big>\n\n<b>effl&#257;tus, &#363;s</b> <i>m</i> [efflo]\nвыход наружу (<i>воздуха, ветра</i>) (apertum ad efflatus iter <i>Sen</i>)."],
  ["effleo", "<big>effleo</big>\n\n<b>ef–fleo, fl&#275;v&#299;, —, &#275;re</b>\nвыплакать (totos oculos <i>Q</i>)."],
  ["efflicte", "<big>efflicte</big>\n\n<b>effl&#299;ct&#275;</b> [effligo]\nсильнейшим образом, смертельно (cupere aliquid <i>Ap</i>; diligere <i>Symm</i>)."],
  ["efflictim", "<big>efflictim</big>\n\n<b>effl&#299;ctim</b> <i>Pl, Ap</i>\n(<i>v. l.</i>) = efflicte."],
  ["efflicto", "<big>efflicto</big>\n\n<b>effl&#299;cto, —, —, &#257;re</b> [<i>intens. к</i> effligo]\nизбивать до смерти (homines <i>Pl</i>)."],
  ["effligo", "<big>effligo</big>\n\n<b>ef–fl&#299;go, fl&#299;x&#299;, fl&#299;ctum, ere</b>\n<b>1)</b> убивать, умерщвлять (aliquem <i>Pl, C, Sen, Ap</i>);\n<b>2)</b> портить (efflicti mores <i>AG</i>)."],
  ["efflo", "<big>efflo</big>\n\n<b>ef–flo, &#257;v&#299;, &#257;tum, &#257;re</b>\n<b>1)</b> выдыхать, изрыгать, извергать (Aetnaeos faucibus ignes <i>V</i>);\n<b>2)</b> испускать, излучать (solis equi lucem naribus efflant <i>V</i>);\n<b>3)</b> выдувать, извергать, выбрасывать (mare naribus <i>O</i>; candentes lapides <i>LJ</i>);\n<b>4)</b> (<i>об умирающих</i>) испускать: e. animam <i>Pl, C etc.</i>, Vitam <i>Cld</i> <i>и</i> vitam in nubila <i>Sil</i>, extremum halitum <i>C</i> <i>и</i> extremum spiritum <i>Aug</i> испустить дух, скончаться; efflans et laceratus <i>Poeta ap. C</i> истерзанный и умирающий; efflantes plagae <i>St</i> зияющие, <i>т. е.</i> смертельные раны;\n<b>5)</b> изрекать, вымолвить: moriens efflavit <i>Fl</i> умирая, (Брут) проговорил; nondum efflaverat omnia, quum... <i>Pt</i> не успел он полностью высказаться, как...; inter discrimina dentium efflanda est <i>Q</i> (буква <i>f</i>) должна произноситься сквозь зубы;\n<b>6)</b> утрачивать, терять (omnem colorem <i>Lcr</i>);\n<b>7)</b> пыхать, вырываться (flamma foras efflat <i>Lcr</i>)."],
  ["effloreo", "<big>effloreo</big>\n\n<b>ef–fl&#333;reo, —, —, &#275;re</b>\nрасцветать, быть в цвету <i>Vlg, Eccl</i>."],
  ["effloresco", "<big>effloresco</big>\n\n<b>ef–fl&#333;r&#275;sco, fl&#333;ru&#299;, —, ere</b>\n<b>1)</b> расцветать, зацветать (ante messem <i>Vlg</i>; <i>перен.</i> corpus in juventam efflorescit <i>Aug</i>);\n<b>2)</b> вырастать, возникать, проистекать (utilitas efflorescit ex amicitia <i>C</i>);\n<b>3)</b> начинать процветать, заблистать (ingenii laudibus <i>C</i>)."],
  ["effluo", "<big>effluo</big>\n\n<b>ef–fluo, fl&#363;x&#299;, —, ere</b>\n<b>1)</b> вытекать, течь (umor e cavis arboris effluens <i>PM</i>): amnis effluit in Oceanum <i>PM</i> река впадает в океан;\n<b>2)</b> изливаться (dolor per lacrimas effluit <i>Sen</i>); изливать, проливать (ne effluant — <i>sc.</i> amphorae — vinum <i>Pt</i>);\n<b>3)</b> растекаться, разливаться, расплываться, распространяться (aer effluit huc et illuc <i>C</i>);\n<b>4)</b> выходить наружу, делаться известным, обнаруживаться: effluunt multa ex vostra disciplina <i>C</i> многое из вашего учения становится общеизвестным;\n<b>5)</b> выскользать, выпадать (urnae effluunt manibus <i>O</i>; capilli effluunt <i>PM</i>);\n<b>6)</b> исчезать, стираться (prima littera nominis effluxit <i>Su</i>): quod praeteriit, effluxit <i>C</i> что прошло, того уже нет || протекать, проходить: aestas effluit <i>C</i> лето проходит; aliquid effluit ex animo <i>C</i> (<i>или</i> animo <i>Ctl</i>) что-л. изглаживается из памяти; mens ei effluit <i>C</i> он забывает, что хотел сказать (теряется)."],
  ["effluvium", "<big>effluvium</big>\n\n<b>effluvium, &#299;</b> <i>n</i> [effluo]\n<b>1)</b> исток (lacus <i>T</i>);\n<b>2)</b> истечение (umoris e corpore <i>PM</i>)."],
  ["effluxi", "<big>effluxi</big>\n\n<b>effl&#363;x&#299;</b> <i>pf. к</i>\neffluo."],
  ["effluxio", "<big>effluxio</big>\n\n<b>effl&#363;xio, &#333;nis</b> <i>f</i> <i>Aug, Dig</i>\n= effluvium."],
  ["effluxus", "<big>effluxus</big>\n\n<b>effl&#363;xus, a, um</b> [effluo]\nнеустойчивый, переменчивый (mens <i>Is</i>)."],
  ["effoco", "<big>effoco</big>\n\n<b>ef–f&#333;co, —, —, &#257;re</b> [faux]\nдушить; <i>pass.</i> effocari задыхаться <i>Sen</i> (<i>v. l.</i>)."],
  ["effodio", "<big>effodio</big>\n\n<b>ef–fodio, f&#333;d&#299;, fossum, ere</b>\n<b>1)</b> рыть, вырывать (latebras <i>V</i>; lacum <i>Su</i>);\n<b>2)</b> выкапывать (aes, thesaurum <i>Pl, Pt</i>; ferrum <i>C</i>; effodiuntur opes <i>O</i>);\n<b>3)</b> выкалывать: e. oculos (lumen <i>V</i>) alicui <i>Pl, Ter, C etc.</i> выколоть (выцарапать) кому-л. глаза;\n<b>4)</b> рыть, вскапывать (terram <i>L</i>; humum <i>Su</i>); окапывать (mala <i>Pall</i>);\n<b>5)</b> перерывать, обыскивать (domos <i>Cs</i>);\n<b>6)</b> вырабатывать, изобретать, находить (verba atroci stilo <i>Pt</i>)."],
  ["effoec-", "<big>effoec-</big>\n\n<b>effoec-</b> <i>v. l.</i>\n= effec-."],
  ["effoedo", "<big>effoedo</big>\n\n<b>ef–foedo, —, —, &#257;re</b>\nмарать (aliquid sanguine <i>Aug</i>)."],
  ["effoem-", "<big>effoem-</big>\n\n<b>effoem-, effoet-</b> <i>v. l.</i>\n= effem-, effet-."],
  ["effor", "<big>effor</big>\n\n<b>ef–for (ecfor), f&#257;tus sum, f&#257;r&#299;</b>\n<b>1)</b> произносить (verba <i>C</i>); говорить (incipit effari <i>V</i>); вещать (Pythia effata est <i>C</i>);\n<b>2)</b> высказывать, разглашать (e. celanda <i>L</i>); формулировать (quod ita effabimur <i>C</i>);\n<b>3)</b> <i>культ.</i> произносить торжественную формулу освящения, освящать (templum <i>C</i>)."],
  ["efforo", "<big>efforo</big>\n\n<b>ef–foro, —, —, &#257;re</b>\nпросверлить, выдолбить (truncum <i>Col</i>)."],
  ["effossio", "<big>effossio</big>\n\n<b>effossio, &#333;nis</b> <i>f</i> [effodio]\n<b>1)</b> выкапывание, раскопки <i>CJ</i>;\n<b>2)</b> выкалывание (luminum <i>Aug</i>)."],
  ["effoveo", "<big>effoveo</big>\n\n<b>ef–foveo, —, —, &#275;re</b>\nраздражать, бередить (carnem <i>Veg</i> — <i>v. l.</i>)."],
  ["effractarius", "<big>effractarius</big>\n\n<b>effr&#257;ct&#257;rius, &#299;</b> <i>m</i> [effringo]\nгромила, взломщик <i>Sen</i>."],
  ["effractor", "<big>effractor</big>\n\n<b>effr&#257;ctor, &#333;ris</b> <i>m</i> <i>Dig, Aug, Sid</i>\n= effractarius."],
  ["effractura", "<big>effractura</big>\n\n<b>effr&#257;ct&#363;ra, ae</b> <i>f</i>\nвзлом, кража со взломом <i>Dig, Aug</i>."],
  ["effractus", "<big>effractus</big>\n\n<b>effr&#257;ctus, a, um</b> <i>part. pf. к</i>\neffringo."],
  ["effregi", "<big>effregi</big>\n\n<b>effr&#275;g&#299;</b> <i>pf. к</i>\neffringo."],
  ["effrenate", "<big>effrenate</big>\n\n<b>effr&#275;n&#257;t&#275;</b> [effrenatus]\nнеобузданно, разнузданно, неукротимо, неудержимо <i>C</i>."],
  ["effrenatio", "<big>effrenatio</big>\n\n<b>effr&#275;n&#257;tio, &#333;nis</b> <i>f</i> [effrenatus]\nнеобузданность, своеволие, несдержанность (animi impotentis <i>C</i>; irae <i>Bo&euml;t</i>)."],
  ["effrenatus", "<big>effrenatus</big>\n\n<b>ef–fr&#275;n&#257;tus, a, um</b> [freno]\n<b>1)</b> невзнузданный, разнузданный (equus <i>L</i>);\n<b>2)</b> необузданный, неукротимый, безудержный (furor <i>C</i>; libido <i>Sl</i>): secundis rebus e. <i>C</i> обнаглевший вследствие улыбнувшегося ему счастья."],
  ["effrenis", "<big>effrenis</big>\n\n<b>effr&#275;nis, e</b> <i>PM, Eccl</i>\n= effrenus."],
  ["effreno", "<big>effreno</big>\n\n<b>ef–fr&#275;no, —, &#257;tum, &#257;re</b>\nразнуздывать, <i>перен.</i> развязывать, давать волю (e. aliquem <i>Sil</i>)."],
  ["effrenus", "<big>effrenus</big>\n\n<b>ef–fr&#275;nus, a, um</b> [frenum]\n<b>1)</b> невзнузданный (equus <i>L</i>);\n<b>2)</b> необузданный, распущенный (gens <i>V, Cld</i>); неукротимый (amor <i>O</i>)."],
  ["effrico", "<big>effrico</big>\n\n<b>ef–frico, frix&#299;, fric&#257;tum, &#257;re</b>\n<b>1)</b> вытирать, стирать (sudorem fronte <i>Ap</i>);\n<b>2)</b> снимать, счищать (rubiginem animorum <i>Sen</i>)."],
  ["effringo", "<big>effringo</big>\n\n<b>ef–fringo, fr&#275;g&#299;, fr&#257;ctum, ere</b> [frango]\n<b>1)</b> выламывать, разламывать, взламывать (januam <i>Pt</i>; carcerem <i>T</i>);\n<b>2)</b> разбить, размозжить (crus <i>Sl</i>; cerebrum <i>V</i>): montes effracti <i>M</i> обрывистые горы;\n<b>3)</b> прорываться (ventus effringit <i>Ap</i>)."],
  ["effrondesco", "<big>effrondesco</big>\n\n<b>effrond&#275;sco, du&#299;, —, ere</b> [<i>inchoat. к</i> frondeo]\nпокрываться листвой <i>Vop</i>."],
  ["effrons", "<big>effrons</big>\n\n<b>ef–fr&#333;ns, ontis</b>\nбесстыдный <i>Vop</i>."],
  ["effrutico", "<big>effrutico</big>\n\n<b>ef–frutico, —, —, &#257;re</b>\n<b>1)</b> производить, рождать (aliquid <i>Tert</i>);\n<b>2)</b> вырастать, возникать, рождаться <i>Tert</i>."],
  ["effugio", "<big>effugio</big>\n\n<b>ef–fugio, f&#363;g&#299;, fugit&#363;rus, ere</b>\n<b>1)</b> убегать (e carcere <i>Lcn</i>; foras <i>Pl, Ter</i>; ad aliquem <i>QC</i>; e proelio <i>C</i>): e. e — <i>v. l.</i> de — manibus <i>C</i> ускользнуть из рук; e. aliquem cursu <i>O</i> спасаться от кого-л. бегством;\n<b>2)</b> избегать, уклоняться (periculum <i>Cs</i>; invidiam <i>Nep</i>; mortem <i>Cs, QC</i>): e. visum <i>O</i> остаться незамеченным; parum effugerat (ne) <i>T</i> он едва избежал (чтобы не); aliquid me effugit <i>C etc.</i> что-то ускользает от моего внимания (остаётся для меня незамеченным); cura rei alicujus me effugit <i>L</i> я забыл позаботиться о чём-л.; e. rogos <i>O</i> уцелеть от огня костров."],
  ["effugium", "<big>effugium</big>\n\n<b>effugium, &#299;</b> <i>n</i> [effugio]\n<b>1)</b> бегство, побег (nullam navem habere ad e. <i>L</i>);\n<b>2)</b> возможность бежать: effugia pennarum <i>C</i> крылья (для того), чтобы улететь;\n<b>3)</b> возможность избежать (e. mortis <i>C</i>);\n<b>4)</b> выход, лазейка, исход (quaerendum est aliquod effugium <i>Pt</i>): insidere effugia <i>T</i> занять выходы."],
  ["effugo", "<big>effugo</big>\n\n<b>ef–fugo, &#257;v&#299;, &#257;tum, &#257;re</b>\nобращать в бегство, прогонять (aliquem <i>Eccl</i>)."],
  ["effulgeo", "<big>effulgeo</big>\n\n<b>ef–fulgeo, fuls&#299;, —, &#275;re</b> (<b>ere</b> <i>V, Cld</i>)\n<b>1)</b> засиять, просиять (sol inter nubes effulget <i>L</i>); заблистать, засверкать (auro <i>V</i>);\n<b>2)</b> отличаться, блистать (audacia <i>T</i>)."],
  ["effultus", "<big>effultus</big>\n\n<b>ef–fultus, a, um</b> [fulcio]\nопёршийся, облокотившийся, возлежащий (in cubitum <i>Ap</i>; velleribus stratis <i>V</i>)."],
  ["effumigatus", "<big>effumigatus</big>\n\n<b>ef–f&#363;mig&#257;tus, a, um</b> [fumigo]\nпрогнанный с помощью дыма (coluber <i>Tert</i>)."],
  ["effumo", "<big>effumo</big>\n\n<b>ef–f&#363;mo, —, —, &#257;re</b>\nдымиться <i>LJ</i>."],
  ["effundo", "<big>effundo</big>\n\n<b>ef–fundo (ecfundo), f&#363;d&#299;, f&#363;sum, ere</b>\n<b>1)</b> изливать, проливать (umorem <i>CC</i>; sanguinem <i>C, QC</i>): flumen effundit se (effunditur) in mare <i>PM etc.</i> река впадает в море; amnis effunditur super ripas <i>L</i> ручей выступает из берегов;\n<b>2)</b> совершать возлияние, возливать (dimidias potiones super ossucula alicujus <i>Pt</i>);\n<b>3)</b> опорожнять (pelves <i>J</i>): e. poculum in barathrum <i>шутл. Pl</i> осушить бокал;\n<b>4)</b> <i>перен.</i> изливать, испускать: e. iram in aliquem <i>L</i> излить (обрушить) свой гнев на кого-л.; effudi omnia, quae sentiebam <i>C</i> я высказал всё, что чувствовал; e. vitam <i>O</i> (animam <i>V, Sil etc.</i> <i>и</i> spiritum <i>C, T etc.</i>) скончаться, умереть;\n<b>5)</b> произносить (tales voces <i>V</i>; ejusmodi carmina <i>Pt</i>); издавать (tuba sonum effundit <i>Sen</i>): e. risum <i>и</i> effundi in risum <i>Pt</i> разразиться смехом, расхохотаться;\n<b>6)</b> производить в изобилии (fruges <i>C, H</i>; herbas <i>C</i>; copiam oratorum <i>C</i>);\n<b>7)</b> распространять, ширить (late effusum incendium <i>L</i>); простирать, протягивать (nudos per aera ramos <i>Lcn</i>);\n<b>8)</b> пускать, метать (tela <i>V</i>);\n<b>9)</b> опускать (habenas <i>V</i>); ослаблять (frena <i>St</i>); распускать (sinum, <i>sc.</i> togae <i>L, Tib</i>): crinem (<i>acc. gr.</i>) effusa sacerdos <i>V</i> жрица с распущенными волосами;\n<b>10)</b> высыпать (nummorum saccos <i>H</i>; anulos <i>L</i>; tomacula cum botulis effusa <i>Pt</i>);\n<b>11)</b> во множестве выпускать, бросать, устремлять, обрушивать (equitatum <i>L</i>; auxilium alicui castris (<i>abl.</i>) <i>V</i>; universus in hostes <i>QC</i>): e. impetum (in hostem) <i>L etc.</i> ударить на (атаковать) неприятеля; e. se <i>или</i> <i>pass.</i> effundi во множестве бросаться, устремляться, хлынуть (se effuderunt carceribus quadrigae <i>V</i>); effundi obviam <i>L</i> ринуться навстречу; effundi in fugam <i>L</i> обратиться в бегство; procella effusa <i>QC</i> разразившаяся буря;\n<b>12)</b> щедро раздавать (honores <i>C</i>); расточать, растрачивать (patrimonia <i>C</i>; censum <i>Man</i>); лишаться по легкомыслию, упускать (gratiam alicujus <i>C</i>); истощать (aerarium <i>C</i>); нерасчётливо <i>или</i> до конца тратить (vires <i>L, V</i>);\n<b>13)</b> отбрасывать: omnem curam sui e. <i>Sen</i> совсем не обращать на себя внимания, не следить за собой;\n<b>14)</b> выбивать (alicui oculum <i>Dig</i>);\n<b>15)</b> сбрасывать, низвергать (equus effudit aliquem <i>L</i>; e. aliquid in flumen <i>Dig</i>); сваливать, опрокидывать (aliquem solo, arena <i>V</i>); e. se <i>или</i> effundi целиком отдаваться, предаваться, быть невоздержным (in libidine <i>C</i> <i>и</i> in omnes libidines <i>T</i>; in vinum <i>QC</i>; in <i>и</i> ad luxuriam <i>L</i>): ad preces lacrimasque effundi <i>L</i> слёзно и неотступно умолять; effundi lacrimis <i>V</i> <i>и</i> in lacrimas <i>T</i> обливаться слезами. — <i>См. тж.</i> effusa <i>и</i> effusus."],
  ["effusa", "<big>effusa</big>\n\n<b>eff&#363;sa, &#333;rum</b> <i>n</i> [effusus]\nмоча <i>Sen</i>."],
  ["effuse", "<big>effuse</big>\n\n<b>eff&#363;s&#275;</b> [effusus]\n<b>1)</b> рассеянно, врозь, врассыпную, разбросанно, в беспорядке (ire <i>Sl</i>; fugere <i>L</i>; praedari <i>L</i>);\n<b>2)</b> слишком щедро, в изобилии, расточительно, на широкую ногу (donare, vivere <i>C</i>); восторженно (laudare aliquem <i>Pt</i>);\n<b>3)</b> чрезмерно, сверх меры (exsultare <i>C</i>; flere <i>QC</i>); слишком подробно, многословно, растянуто (dicere <i>PJ</i>);\n<b>4)</b> широко, кругом, на большом протяжении (agrum vastare <i>L</i>)."],
  ["effusio", "<big>effusio</big>\n\n<b>eff&#363;sio, &#333;nis</b> <i>f</i> [effundo]\n<b>1)</b> расплывание, растекание, разлитие (aquae <i>C</i>); проливание, пролитие (sanguinis <i>Nep, SR, Eccl</i>);\n<b>2)</b> массовое выступление, выбегание, выход (hominum ex oppidis <i>C</i>);\n<b>3)</b> чрезмерность, неумеренность, разнузданность, необузданность (animi in laetitia <i>C</i>);\n<b>4)</b> расточительность, безрассудная трата (pecuniarum <i>C</i>): liberalitatem e. imitatur <i>C</i> расточительность подражает щедрости."],
  ["effusor", "<big>effusor</big>\n\n<b>eff&#363;sor, &#333;ris</b> <i>m</i> [effundo]\n<b>1)</b> проливающий: e. sanguinis <i>Aug</i> проливающий (чью-л.) кровь, убийца;\n<b>2)</b> щедрый даритель <i>Aug</i>."],
  ["effusorie", "<big>effusorie</big>\n\n<b>eff&#363;s&#333;ri&#275;</b> <i>adv.</i>\nшироко, на большом протяжении (digredi per provincias <i>Amm</i>)."],
  ["effusorium", "<big>effusorium</big>\n\n<b>eff&#363;s&#333;rium, &#299;</b> <i>n</i> [effundo]\nсосуд для поливания, ковш <i>Hier</i>."],
  ["effusus", "<big>effusus</big>\n\n<b>eff&#363;sus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> effundo;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> широко раскинутый, далеко простирающийся (mare <i>H</i>; loca <i>T</i>);\n<b>2)</b> разбросанный, рассеянный (hostes, agmen <i>L</i>): effusa fuga <i>L</i> бегство во все стороны (беспорядочное); comae effusae <i>O</i> распущенные волосы;\n<b>3)</b> неумеренный, чрезмерный (honores <i>Nep</i>; laetitia <i>C</i>); преувеличенный <i>или</i> восторженный (effusissimae laudationes <i>Pl</i>); неумеренно щедрый, расточительный (e. in largitione <i>C</i>): effusissimis habenis <i>L</i> во весь опор; effuso cursu <i>L, QC</i> во всю прыть, опрометью;\n<b>4)</b> попусту растраченный, напрасный, бесплодный (labor <i>V</i>)."],
  ["effuticius", "<big>effuticius</big>\n\n<b>eff&#363;t&#299;cius, a, um</b> [effutio]\nсказанный наобум, <i>т. е.</i> бессодержательный (id verbum nihil significat, sed effuticium est <i>Vr</i>)."],
  ["effutio", "<big>effutio</big>\n\n<b>ef–f&#363;tio, &#299;v&#299; (i&#299;), &#299;tum, &#299;re</b> [<i>одного корня с</i> futilis, fundo] (<i>тж.</i> e. ore <i>Lcr</i>)\nговорить наобум, болтать вздор (de aliqua re <i>C</i>; nescio quas afannas <i>Ap</i>)."],
  ["effutuo", "<big>effutuo</big>\n\n<b>ef–futuo (ecfutuo), futu&#299;, fut&#363;tus, ere</b>\n<b>1)</b> изнурять развратом (ecfututa latera <i>Ctl</i>);\n<b>2)</b> растрачивать в разврате (aurum <i>Poeta ap. Su</i>)."],
  ["egelido", "<big>egelido</big>\n\n<b>&#275;gelido, —, —, &#257;re</b> [egelidus]\nвызывать таяние, оттаивать (fibras glaciales <i>Sid</i>)."],
  ["egelidus", "<big>egelidus</big>\n\n<b>&#275;–gelidus, a, um</b>\n<b>1)</b> холодноватый, прохладный (flumen <i>V</i>; ventus <i>O</i>); тепловатый (tepores <i>Ctl</i>; aqua <i>Su</i>);\n<b>2)</b> очень холодный (Hister <i>Aus</i>)."],
  ["egelo", "<big>egelo</big>\n\n<b>&#275;–gelo, —, —, &#257;re</b>\nотогревать <i>CA</i>."],
  ["egens", "<big>egens</big>\n\n<b>eg&#275;ns, entis</b>\n<b>1.</b> <i>part. praes. к</i> egeo;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> неимущий, бедный, нуждающийся (locupletes ex egentibus facere <i>Cs</i>);\n<b>2)</b> не имеющий, лишённый (e. virtutis, abundans felicitatis <i>rhH</i>): insula cultorum e. <i>L</i> безлюдный остров; verborum non e. <i>C</i> не лишённый дара слова; e. consilii <i>Pt</i> смущённый, растерявшийся."],
  ["egentia", "<big>egentia</big>\n\n<b>egentia, ae</b> <i>f</i> [egeo]\nбедность, нужда <i>Aug</i>."],
  ["egenus", "<big>egenus</big>\n\n<b>eg&#275;nus, a, um</b> [egeo]\n<b>1)</b> (<i>тж.</i> fortuna e. <i>Ap</i>) терпящий нужду, лишённый, бедный, нуждающийся (alicujus rei <i>или</i> aliqua re): e. omnium (rerum) <i>V, L, T</i> нуждающийся во всём; (in) rebus egenis <i>Pl, V</i> в тяжёлых обстоятельствах; egena aquarum regio <i>T</i> маловодный край;\n<b>2)</b> скудный: in egeno <i>Col</i> на скудной почве."],
  ["egeo", "<big>egeo</big>\n\n<b>egeo, u&#299;, —, &#275;re</b>\n<b>1)</b> иметь нужду, нуждаться (aliqua re <i>или</i> alicujus rei): is minimo eget, qui minimum cupit <i>PS</i> у кого меньше всего желаний, у того меньше всего нужды; egetur (<i>impers.</i>) acriter <i>Pl</i> ощущается крайняя нужда; nostri majores neque consilii neque audaciae umquam eguere <i>Sl</i> у наших предков никогда не было недостатка ни в благоразумии, ни в храбрости; res eget exercitatione non parva <i>C</i> это требует немалого упражнения;\n<b>2)</b> не иметь, быть лишённым (auctoritate <i>C</i>);\n<b>3)</b> обходиться (без чего-л.): si (quid) non est, egeo <i>Cato ap. AG</i> если чего-л. нет, обхожусь (без него)."],
  ["Egeria", "<big>Egeria</big>\n\n<b>&#274;geria, ae</b> <i>f</i>\nЭгерия, <i>италийская вещая нимфа, супруга и советница царя Нумы Помпилия; ей были посвящены рощи у Капенских ворот и близ Ариции у храма богини Diana Nemorensis</i> <i>L, V, O etc.</i>"],
  ["egeries", "<big>egeries</big>\n\n<b>&#275;geri&#275;s, &#275;&#299;</b> <i>f</i> [egero I]\nпомёт, навоз <i>Sol</i>."],
  ["egermino", "<big>egermino</big>\n\n<b>&#275;–germino, &#257;v&#299;, —, &#257;re</b>\nдавать ростки, прорастать <i>Col</i>."],
  ["egero 1", "<big>egero</big>\n\n<b>I &#275;–gero, gess&#299;, gestum, ere</b>\n<b>1)</b> выносить, вытаскивать (lapides e mari <i>bAl</i>); уносить, похищать (praedam <i>T</i>; pecuniam ex aerario <i>L</i>); опустошать (Dorica castra <i>Prp</i>);\n<b>2)</b> удалять, прогонять (egeritur lacrimis dolor <i>O</i>); убирать (nivem <i>L</i>); опорожнять (ventrem <i>CA</i>); выгонять, вытеснять (gravitas caeli egerit populos <i>Sen</i>);\n<b>3)</b> изливать, выражать (iras ululatibus <i>Sil</i>): e. querelas <i>Cld</i> жаловаться;\n<b>4)</b> извергать (dapes <i>O</i>; aquam vomitu <i>PM</i>): e. sanguinem <i>PM</i> иметь кровавую рвоту; e. animam <i>Lcn</i> умирать || выбрасывать (moles arenarum <i>Just</i>);\n<b>5)</b> излагать письменно, записывать (sermones <i>Sen</i>);\n<b>6)</b> проводить (nox egesta metu <i>VF</i>)."],
  ["egero 2", "<big>egero</big>\n\n<b>II &#275;gero</b> <i>fut. II к</i>\nago."],
  ["egestas", "<big>egestas</big>\n\n<b>egest&#257;s, &#257;tis</b> <i>f</i> [egeo]\n<b>1)</b> бедность, нужда (vitam in egestate degere <i>C</i>): egestate <i>T</i> из нужды;\n<b>2)</b> скудость, недостаток (frumenti <i>Sl</i>): e. rationis <i>Lcr</i> невежественность; e. animi <i>C</i> малодушие."],
  ["egestio", "<big>egestio</big>\n\n<b>&#275;gestio, &#333;nis</b> <i>f</i> [egero I]\n<b>1)</b> уборка (ruderum <i>Su</i>);\n<b>2)</b> расточение, растрачивание (opum publicarum <i>PJ</i>);\n<b>3)</b> извержение, выведение из организма (ventris et urinae <i>CA</i>)."],
  ["egestosus", "<big>egestosus</big>\n\n<b>egest&#333;sus (egestu&#333;sus), a, um</b> <i>AV, Aug</i>\n= egenus."],
  ["egestus 1", "<big>egestus</big>\n\n<b>I &#275;gestus, a, um</b> <i>part. pf. к</i>\negero I."],
  ["egestus 2", "<big>egestus</big>\n\n<b>II &#275;gestus, &#363;s</b> <i>m</i>\n<b>1)</b> выбрасывание, выкапывание (alto egestu cavare terram <i>St</i>);\n<b>2)</b> удаление, извержение (<i>sc.</i> ventris <i>Sen</i>)."],
  ["egi", "<big>egi</big>\n\n<b>&#275;g&#299;</b> <i>pf. к</i>\nago."],
  ["egigno", "<big>egigno</big>\n\n<b>&#275;–gigno, —, —, ere</b>\nпроизводить, <i>pass.</i> egigni вырастать <i>Lcr</i>."],
  ["Egnatius", "<big>Egnatius</big>\n\n<b>Egn&#257;tius, a, um</b>\n<i>римск. nomen (самнитского происхождения); наиболее известны</i>:\n<b>1)</b> Gellius E., <i>вождь самнитов во время 3-й Самнитской войны, пал в сражении с римлянами в 295 г. до н. э.</i> <i>L</i>;\n<b>2)</b> Marius E., <i>один из вождей италийских союзников во время Союзнической войны, убит в 89 г. до н. э.</i> <i>Fl, L</i>;\n<b>3)</b> M. E. Rufus, <i>эдил в 20 г. и претор в 19 г. до н. э., казнён за участие в заговоре против Августа</i> <i>VP, Su</i>."],
  ["ego", "<big>ego</big>\n\n<b>eg&#333;</b> (<i>тж.</i> <b>o</b>) <i>pron. pers.</i> (<i>gen.</i> mei)\nя: e. et frater meus <i>C</i> мы с братом; ad me <i>Ter, C</i> ко мне (домой); in me omnis spes mihi est <i>Ter</i> вся моя надежда — на себя самого; a me argentum dedi <i>Pl</i> я дал деньги из своего кармана; me consule <i>C</i> когда я был консулом; alter e. <i>или</i> e. alter <i>C, PJ</i> моё второе я, <i>т. е.</i> мой ближайший друг <i>или</i> моя &laquo;правая рука&raquo;."],
  ["egomet", "<big>egomet</big>\n\n<b>ego–met</b>\n(= <i>усил.</i> ego) я сам, я-то, что до меня лично <i>и т. п.</i>"],
  ["egredior", "<big>egredior</big>\n\n<b>&#275;–gredior, gressus sum, gred&#299;</b> [e + gradior]\n<b>1)</b> выходить, выезжать (ex urbe <i>C</i>, urbe <i>Cs etc.</i>, ab urbe <i>Su</i> <i>и</i> urbem <i>L</i>; extra fines <i>C</i> <i>и</i> finibus <i>L</i>): egressus Roma <i>H</i> покинув(ший) Рим; egressus porta Capena <i>C</i> выйдя за Капенские ворота || <i>воен.</i> выступать (exercitus egreditur e castris <i>или</i> castris <i>Cs, Sl</i>); уходить (ex convivio <i>L, Su</i> <i>и</i> convivio <i>QC</i>; ad portam <i>L</i>): e. ordine <i>Sl</i> покинуть ряды (выйти из строя) || отплывать (e portu <i>C etc.</i>);\n<b>2)</b> (<i>тж.</i> e. in terram <i>C</i> <i>и</i> in litus <i>PJ</i>) сходить (выходить) на берег, высаживаться <i>Cs</i>: e. (ex) navi <i>Cs</i> сойти с корабля;\n<b>3)</b> удаляться, уклоняться (e. veritatem <i>PJ</i>): e. a proposito <i>C</i> уклониться от темы (предмета);\n<b>4)</b> всходить, подниматься (e. ad summum montis <i>Sl</i>; in tumulum <i>L</i>; e. altius <i>O</i>);\n<b>5)</b> переступать, переходить (munitiones <i>Cs</i>; flumen <i>Sl</i>): limen egressus <i>Pt</i> выйдя за порог; e. modum <i>T</i> переступить меру (выйти из пределов);\n<b>6)</b> превосходить, превышать (altitudinem moenium <i>Sen</i>);\n<b>7)</b> перешагнуть (septemdecim annos <i>T</i>)."],
  ["egregia", "<big>egregia</big>\n\n<b>&#275;gregia, &#333;rum</b> <i>n</i> [egregius]\nподвиги <i>или</i> заслуги, славные дела <i>Sl, T</i>."],
  ["egregiatus", "<big>egregiatus</big>\n\n<b>&#275;gregi&#257;tus, &#363;s</b> <i>m</i>\nтитул egregius (<i>см.</i> egregius 3) <i>CTh</i>."],
  ["egregie", "<big>egregie</big>\n\n<b>&#275;gregi&#275;</b> [egregius]\n<b>1)</b> отлично, превосходно, замечательно, блестяще (Graece loqui <i>C</i>);\n<b>2)</b> весьма, чрезвычайно (e. fortis <i>C</i>)."],
  ["egregium", "<big>egregium</big>\n\n<b>&#275;gregium, &#299;</b> <i>n</i> [egregius]\nслава, краса (e. publicum <i>T</i>)."],
  ["egregius", "<big>egregius</big>\n\n<b>&#275;gregius, a, um</b> [e + grex]\n<b>1)</b> отличный, превосходный, блистательный, славный (civis <i>C</i>; victoria <i>L</i>; usus — magister e. <i>PJ</i>): virgo egregia forma <i>Ter, AG</i> девушка замечательной красоты; e. moribus <i>T</i> <i>и</i> morum <i>Cld</i> высоконравственный; loca egregia ad ancoras tenendas <i>bAl</i> превосходные для якорной стоянки места; egregia in aliquem voluntas <i>Cs</i> особое расположение к кому-л.;\n<b>2)</b> особо предрасположенный (ad aliquid <i>Ter, L</i>);\n<b>3)</b> (<i>как титул в эпоху империи</i>) высокородный <i>или</i> высокочтимый <i>CTh</i>."],
  ["egressio", "<big>egressio</big>\n\n<b>&#275;gressio, &#333;nis</b> <i>f</i> [egredior]\n<b>1)</b> выход, выступление (e. nocturna, <i>sc.</i> e castello <i>Ap</i>); исход (ex Aegypto <i>Vlg</i>);\n<b>2)</b> отступление, уклонение (<i>от предмета</i>) <i>Q</i>."],
  ["egressus 1", "<big>egressus</big>\n\n<b>I &#275;gressus, a, um</b> <i>part. pf. к</i>\negredior."],
  ["egressus 2", "<big>egressus</big>\n\n<b>II &#275;gressus, &#363;s</b> <i>m</i> [egredior]\n<b>1)</b> выход, выезд (alicujus <i>C, T</i>): itinera egressusque <i>Sl</i> пути и ходы (передвижения); e. introitusque <i>O</i> движение туда и обратно; egressibus alicujus adhaerescere <i>T</i> всюду сопровождать кого-л.; rarus egressu <i>T</i> редко выходящий (из дому);\n<b>2)</b> (<i>тж.</i> e. navis <i>Su</i>) причаливание, высадка <i>Cs</i>;\n<b>3)</b> вылет (avium <i>Col</i>; ventorum <i>O</i>);\n<b>4)</b> отступление, уклонение (<i>от предмета</i>) <i>Q</i>: libero egressu aliquid memorare <i>T</i> свободно (не будучи ничем стеснённым) повествовать о чём-л.;\n<b>5)</b> выход (tenebrosus <i>Pt</i>); obsidere omnes egressus <i>T</i> занять все выходы;\n<b>6)</b> устье (e. fluminis <i>O</i>)."],
  ["egui", "<big>egui</big>\n\n<b>egu&#299;</b> <i>pf. к</i>\negeo."],
  ["egula", "<big>egula</big>\n\n<b>egula, ae</b> <i>f</i>\nбелильная сера <i>PM</i>."],
  ["egurgito", "<big>egurgito</big>\n\n<b>&#275;gurgito, —, —, &#257;re</b> [e + gurges]\nизливать, выкидывать, выбрасывать, <i>перен.</i> расточать (argentum domo <i>Pl</i>)."],
  ["ehem", "<big>ehem</big>\n\n<b>ehem</b> <i>interj.</i>\nага!, а!, вот как! (e., scio jam, quid vis dicere <i>Pl</i>)."],
  ["eheu", "<big>eheu</big>\n\n<b>&#275;heu</b> (<i>тж.</i> <b>eheu</b>)<b>!</b> <i>interj.</i>\nувы!, ах! <i>Pt, Ter etc.</i>: e., fugaces labuntur anni! <i>H</i> увы, проходят быстротечные годы!"],
  ["eho", "<big>eho</big>\n\n<b>eho</b> <i>interj.</i>\nэй! <i>Pl, Ter</i>."],
  ["ehodum", "<big>ehodum</big>\n\n<b>ehodum</b> <i>interj.</i> <i>Ter</i>\n= <i>усил.</i> eho."],
  ["ei 1", "<big>ei</big>\n\n<b>I ei</b> <i>interj.</i>\nувы!, ах!, ай!, ой! (e. misero mihi! <i>Ter, Ctl</i>)."],
  ["ei 2", "<big>ei</big>\n\n<b>II ei</b> <i>Pl</i> (= <b>&#299;</b>) <i>imper. к</i>\neo I."],
  ["ei 3", "<big>ei</big>\n\n<b>III e&#299;</b> <i>dat. sg. к</i>\nis, ea, id."],
  ["eia", "<big>eia</big>\n\n<b>eia (heia)</b> <i>interj.</i>\n<b>1)</b> (<i>удивление</i>) вот как! <i>Pl</i>;\n<b>2)</b> (<i>поощрение</i>) ну!, ну же!, смело! (e., quid statis? <i>H</i>)."],
  ["eicio", "<big>eicio</big>\n\n<b>&#275;icio</b> <i>v. l.</i>\n= ejicio."],
  ["eidus", "<big>eidus</big>\n\n<b>eidus</b> <i>арх.</i>\n(<i>преим. в надписях</i>) = idus."],
  ["eis", "<big>eis</big>\n\n<b>e&#299;s </b>(<b>i&#299;s</b>)<b></b> <i>dat. abl. pl. к</i>\nis, ea, id."],
  ["ejaculo", "<big>ejaculo</big>\n\n<b>&#275;jaculo, &#257;v&#299;, —, &#257;re</b> <i>AG</i>\n= ejaculor."],
  ["ejaculor", "<big>ejaculor</big>\n\n<b>&#275;–jaculor, &#257;tus sum, &#257;r&#299;</b> <i>depon.</i>\nвыбрасывать, извергать (aquas <i>O</i>): se e. in altum <i>O</i> брызнуть вверх, забить ключом."],
  ["ejeci", "<big>ejeci</big>\n\n<b>&#275;j&#275;c&#299;</b> <i>pf. к</i>\nejicio."],
  ["ejecta", "<big>ejecta</big>\n\n<b>&#275;jecta, &#333;rum</b> <i>n</i> [ejicio]\n<b>1)</b> <i>архит.</i> выступы <i>PJ</i>;\n<b>2)</b> вывихи <i>Veg</i>."],
  ["ejectamentum", "<big>ejectamentum</big>\n\n<b>&#275;ject&#257;mentum, &#299;</b> <i>n</i> [ejecto]\nто, что выброшено, изверженное (e. maris <i>T, Ap</i>)."],
  ["ejecticius", "<big>ejecticius</big>\n\n<b>&#275;ject&#299;cius, a, um</b> [ejicio]\nвыбросивший (преждевременно), выкинувший (vulva <i>PM</i>)."],
  ["ejectio", "<big>ejectio</big>\n\n<b>&#275;jectio, &#333;nis</b> <i>f</i> [ejicio]\n<b>1)</b> выбрасывание, выплёвывание: sanguinis e. <i>Vtr</i> кровохаркание;\n<b>2)</b> изгнание (mortem et ejectionem timere <i>C</i>);\n<b>3)</b> вывих (articuli <i>CA</i>)."],
  ["ejecto", "<big>ejecto</big>\n\n<b>&#275;jecto, —, —, &#257;re</b> [<i>intens. к</i> ejicio]\n<b>1)</b> выбрасывать, выкидывать (cineres <i>O</i>; ejectata favilla <i>O</i>);\n<b>2)</b> выплёвывать, извергать (cruorem ore <i>V</i>; cruentas dapes <i>O</i>)."],
  ["ejectura", "<big>ejectura</big>\n\n<b>&#275;ject&#363;ra, ae</b> <i>f</i> [ejecto]\nвыступ <i>Bo&euml;t</i>."],
  ["ejectus", "<big>ejectus</big>\n\n<b>&#275;jectus, &#363;s</b> <i>m</i> [ejicio]\nвыбрасывание, извержение (foras <i>Lcr</i>)."],
  ["ejer-", "<big>ejer-</big>\n\n<b>&#275;jer-</b> <i>v. l.</i>\n= ejur-."],
  ["ejicio", "<big>ejicio</big>\n\n<b>&#275;jicio, j&#275;c&#299;, jectum, ere</b> [e + jacio]\n<b>1)</b> выбрасывать (aliquem in alto de navi <i>C</i>): e. se броситься (foras <i>L</i>; ex navibus <i>bAl</i>), ринуться, хлынуть (ex castris <i>Cs</i>; in agros <i>L</i>);\n<b>2)</b> причалить, пригнать, пристать к берегу (e. naves in terram <i>Cs</i>, ad Chium <i>L</i>); выбросить на берег (classis ejicitur ad insulam <i>L</i>): ejectus litore <i>V</i>, in litore <i>Cs</i> <i>и</i> in litora <i>L</i> выброшенный на берег (потерпевший кораблекрушение);\n<b>3)</b> изгонять (aliquem ex oppido <i>Cs</i>; finibus <i>Sl</i>; domu <i>и</i> domo <i>C, Cs</i>; aliquem civitate <i>Sl</i> <i>и</i> e civitate <i>Nep</i>; in exsilium <i>C</i>); исключать (aliquem de senatu <i>L</i>, e senatu <i>C, L</i> <i>и</i> senatu <i>PJ</i>); прогнать, разогнать, подавить (e. curam ex animo <i>Pl</i>; amorem ex corde <i>Ter</i>);\n<b>4)</b> вырывать, удалять (dentes <i>CC</i>); отнимать, лишать: ejectus die <i>St</i> лишённый зрения, слепой; se e. вырываться, прорываться (voluptates subito se ejiciunt <i>C</i>); e. stirpes rei alicujus <i>C</i> вырвать с корнем (искоренить) что-л.;\n<b>5)</b> извергать (mare extra litus ejectum <i>Sen</i>; e. hanc sentinam hujus urbis <i>C</i>); извергать из организма, выплёвывать (sanguinem <i>PM</i>): e. linguam <i>C</i> высунуть язык;\n<b>6)</b> отвергать, отбрасывать (rationem Stoicorum <i>C</i>); освистывать, ошикать (oratorem, histrionem <i>C, rhH</i>; et actorem et carmen <i>Sen</i>);\n<b>7)</b> издавать (vocem <i>Lcr, C</i>);\n<b>8)</b> вывихнуть (armum <i>V</i>; membrum <i>Veg</i>);\n<b>9)</b> (<i>тж.</i> e. partum <i>VM</i>) производить аборт;\n<b>10)</b> (<i>о растениях</i>) выпускать, давать (vitem ex se <i>Vr</i>)."],
  ["ejulabilis", "<big>ejulabilis</big>\n\n<b>&#275;jul&#257;bilis, e</b> [ejulo]\nрыдающий, жалобный, скорбный (plangor <i>Ap</i>)."],
  ["ejulatio", "<big>ejulatio</big>\n\n<b>&#275;jul&#257;tio, &#333;nis</b> <i>f</i> [ejulo]\nрыдание, сетование, вопль <i>Pl, C, Sen</i>."],
  ["ejulatus", "<big>ejulatus</big>\n\n<b>&#275;jul&#257;tus, &#363;s</b> <i>m</i> <i>C etc.</i>\n= ejulatio."],
  ["ejulito", "<big>ejulito</big>\n\n<b>&#275;julito, —, —, &#257;re</b> [<i>intens. к</i> ejulo]\nвопить, громко сетовать <i>LM</i>."],
  ["ejulo", "<big>ejulo</big>\n\n<b>&#275;julo, &#257;v&#299;, &#257;tum, &#257;re</b>\nрыдать, вопить, громко сетовать (magnitudine dolorum <i>C</i>; e. fortunas suas <i>Ap</i>)."],
  ["ejuncesco", "<big>ejuncesco</big>\n\n<b>&#275;junc&#275;sco, —, —, ere</b> [e + juncus]\nвырастать наподобие тростника (vitis ejuncescit <i>PM</i>)."],
  ["ejuncidus", "<big>ejuncidus</big>\n\n<b>&#275;juncidus, a, um</b> [e + juncus]\nвыросший как тростник, камышевидный (gracilis atque ejuncida vitis <i>PM</i>)."],
  ["ejuratio", "<big>ejuratio</big>\n\n<b>&#275;j&#363;r&#257;tio, &#333;nis</b> <i>f</i> [ejuro]\nклятвенный отказ, формальное отречение (от должности) (a. consulum <i>VM</i>): e. bonae spei <i>Sen</i> отказ от всяких надежд, полное отчаяние."],
  ["ejuro", "<big>ejuro</big>\n\n<b>&#275;–j&#363;ro (&#275;jero), &#257;v&#299;, &#257;tum, &#257;re</b>\n<b>1)</b> клятвенно отрекаться, отказываться, слагать с себя (imperium <i>T</i>; magistratum <i>PJ</i>): e. bonam copiam <i>юр. C</i> объявить себя несостоятельным; e. militiam <i>Pl</i> клятвенно заявить о своей непригодности к несению военной службы; e. forum <i>или</i> judicem iniquum sibi <i>C</i> не признавать кого-л. беспристрастным судьёй, <i>т. е.</i> заявить отвод;\n<b>2)</b> торжественно отрекаться, отбрасывать, отвергать (liberos <i>Sen</i>; patriam <i>T</i>; ejurata fides <i>St</i>)."],
  ["ejus", "<big>ejus</big>\n\n<b>&#275;jus</b> <i>gen. sg. к</i>\nis, ea, id."],
  ["ejuscemodi", "<big>ejuscemodi</big>\n\n<b>&#275;juscemod&#299;</b> <i>AG</i>\n= ejusmodi."],
  ["ejusdemmodi", "<big>ejusdemmodi</big>\n\n<b>&#275;jusdemmod&#299;</b> <i>adj.</i> [idem + modus]\nтого же рода, такого же свойства (качества) <i>Vr, C etc.</i>"],
  ["ejusmodi", "<big>ejusmodi</big>\n\n<b>&#275;jusmod&#299;</b> <i>adj.</i> [is + modus]\n<b>1)</b> такого рода, такой (genus belli est e. <i>C</i>);\n<b>2)</b> так же точно (aliquid exigere <i>C</i>)."],
  ["elabor", "<big>elabor</big>\n\n<b>&#275;–l&#257;bor, l&#257;psus sum, l&#257;b&#299;</b> <i>depon.</i>\n<b>1)</b> выскальзывать, ускользать (de manibus <i>и</i> e manibus <i>C</i>, manibus <i>O, QC</i>; anguilla est, elabitur <i>Pl</i>): cuspis trans cervicem equi elapsa est <i>L</i> копьё пролетело над самой шеей коня;\n<b>2)</b> исчезать, пропадать: e. e memoria <i>rhH</i> <i>и</i> memoria <i>C</i> забываться; spes elapsa <i>Pl</i> исчезнувшая надежда;\n<b>3)</b> смещаться, выходить из суставов (articuli elabuntur <i>CC</i>);\n<b>4)</b> удирать, убегать (ex proelio <i>Cs</i>; per portam <i>QC</i>; e. custodiae — <i>dat.</i> — <i>и</i> custodias <i>T</i>): e. pugnam <i>T</i> бежать с поля сражения;\n<b>5)</b> отделываться, выпутываться (ex judicio <i>C</i>; ex tot criminibus <i>C</i>);\n<b>6)</b> попадать (in servitutem <i>L</i>);\n<b>7)</b> ползти вверх, подниматься (ignis elapsus in frondes altas <i>V</i>)."],
  ["elaboratio", "<big>elaboratio</big>\n\n<b>&#275;lab&#333;r&#257;tio, &#333;nis</b> <i>f</i> [elaboro]\nревностная работа, усердие, старание <i>rhH</i>."],
  ["elaboratus 1", "<big>elaboratus</big>\n\n<b>I &#275;lab&#333;r&#257;tus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> elaboro;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> тщательно обработанный, отделанный (versus <i>C</i>); подготовленный (<i>в отличие от импровизированного</i>) (opera <i>Ap</i>);\n<b>2)</b> изысканный, неестественный (concinnitas <i>C</i>)."],
  ["elaboratus 2", "<big>elaboratus</big>\n\n<b>II &#275;lab&#333;r&#257;tus, (&#363;s)</b> <i>m</i> <i>Ap</i>\n= elaboratio."],
  ["elaboro", "<big>elaboro</big>\n\n<b>&#275;–lab&#333;ro, &#257;v&#299;, &#257;tum, &#257;re</b>\n<b>1)</b> стараться, трудиться (in litteris <i>C</i>): elaboro, ut prosim illis <i>C</i> я тружусь (стараюсь), чтобы быть им полезным; e. in re aliqua <i>C</i> неутомимо трудиться над чём-л.;\n<b>2)</b> тщательно обрабатывать, отделывать (opus tam elaboratum <i>C</i>); разрабатывать, совершенствовать (artem <i>PM</i>; a Graecis elaborata dicendi vis <i>C</i>);\n<b>3)</b> вырабатывать, приобретать (dulcem saporem <i>H</i>);\n<b>4)</b> доставлять, добывать: imperium a parentibus elaboratum <i>Just</i> власть, добытая трудами предков."],
  ["elactesco", "<big>elactesco</big>\n\n<b>&#275;–lact&#275;sco, —, —, ere</b>\nлишать(ся) соков (in arboribus nimia germinatio elactescit <i>PM</i>)."],
  ["Elaea", "<big>Elaea</big>\n\n<b>Elaea, ae</b> <i>f</i>\nЭлея, <i>приморский город в Эолиде против о-ва Лесбос</i> <i>Mela, PM, L</i>."],
  ["elaeemporia", "<big>elaeemporia</big>\n\n<b>elaeemporia</b> <i>v. l.</i>\n= elemporia."],
  ["elaeon", "<big>elaeon</big>\n\n<b>elae&#333;n, &#333;nis</b> <i>m</i> (<i>греч.</i>)\nмасличная роща <i>Tert</i>."],
  ["elaeothesium", "<big>elaeothesium</big>\n\n<b>elaeothesium, &#299;</b> <i>n</i> (<i>греч.</i>)\nкомната для умащивания (<i>в банях</i>) <i>Vtr</i>."],
  ["Elaeus", "<big>Elaeus</big>\n\n<b>Elae&#363;s, &#363;ntis</b> <i>f</i>\nЭлеунт, <i>город во Фракии</i> <i>L</i>."],
  ["Elagabalus", "<big>Elagabalus</big>\n\n<b>Elagabalus, &#299;</b> <i>m</i> <i>v. l.</i>\n= Heliogabalus."],
  ["Elaites", "<big>Elaites</big>\n\n<b>Ela&#299;t&#275;s, ae</b> <i>m</i> [Elaea]\nэлеец, <i>житель города Элей</i> <i>Q</i>."],
  ["elamentabilis", "<big>elamentabilis</big>\n\n<b>&#275;–l&#257;ment&#257;bilis, e</b>\nжалобный, рыдающий, скорбный (gemitus <i>C</i>)."],
  ["elanguesco", "<big>elanguesco</big>\n\n<b>&#275;–langu&#275;sco, gu&#299;, —, ere</b>\nослабевать, изнемогать (vis elanguescit <i>PM</i>); хиреть, чахнуть (arbor elanguescit <i>Su</i>); становиться вялым, <i>перен.</i> угасать, утихать (proelium elanguescit <i>QC</i>)."],
  ["elapido", "<big>elapido</big>\n\n<b>&#275;–lapido, —, (&#257;tum), &#257;re</b>\nочищать, освобождать от камней (solum <i>PM</i>)."],
  ["elapsio", "<big>elapsio</big>\n\n<b>&#275;l&#257;psio, &#333;nis</b> <i>f</i> [elabor]\nбегство, исчезновение <i>Aug</i>."],
  ["elapsus", "<big>elapsus</big>\n\n<b>&#275;l&#257;psus, a, um</b> <i>part. pf. к</i>\nelabor."],
  ["elaqueo", "<big>elaqueo</big>\n\n<b>&#275;–laqueo, (&#257;v&#299;), &#257;tum, &#257;re</b>\nосвобождать от пут (aliquem <i>Amm</i>); освобождать (aliquem ab aliqua re <i>Aug, Sid</i>)."],
  ["elargior", "<big>elargior</big>\n\n<b>&#275;–largior, &#299;tus sum, &#299;r&#299;</b> <i>depon.</i>\nщедро раздавать (patriae carisque propinquis aliquid <i>Pers</i>)."],
  ["elassesco", "<big>elassesco</big>\n\n<b>&#275;–lass&#275;sco, —, —, ere</b>\nизнемогать, хиреть <i>PM</i> (<i>v. l. к</i> elactesco)."],
  ["elate", "<big>elate</big>\n\n<b>&#275;l&#257;t&#275;</b> [elatus]\n<b>1)</b> возвышенно, выспренно (dicere <i>C</i>);\n<b>2)</b> гордо, надменно (elatius se gerere <i>Nep</i>)."],
  ["Elatea", "<big>Elatea</big>\n\n<b>Elat&#275;a (-&#299;a), ae</b> <i>f</i>\n<b>1)</b> <i>город в Фокиде, на пути из Фессалии в Беотию</i> <i>L</i>;\n<b>2)</b> <i>город в Фессалии</i> <i>L</i>."],
  ["Elateius", "<big>Elateius</big>\n\n<b>Elat&#275;ius, a, um</b> [Elatus]\nэлатов (proles <i>O</i>)."],
  ["elatio", "<big>elatio</big>\n\n<b>&#275;l&#257;tio, &#333;nis</b> <i>f</i> [effero I]\n<b>1)</b> поднятие (oneris <i>Vtr</i>);\n<b>2)</b> возвышенный характер, благородство (magnitudo et e. animi <i>C</i>);\n<b>3)</b> подъём, полёт, выспренность (orationis <i>C</i>); увлечение, порыв (e. voluptaria <i>C</i>);\n<b>4)</b> возвышение, усиление: parium comparatio nec elationem habet, nec submissionem <i>C</i> в сравнении равного нет ни возвышения (одного), ни подчинения (другого);\n<b>5)</b> (<i>тж.</i> e. mentis <i>Vlg</i>) высокомерие, гордыня <i>Eccl</i>;\n<b>6)</b> вынос тела, <i>тж.</i> похороны (mortui <i>Dig</i>)."],
  ["elativus", "<big>elativus</big>\n\n<b>&#275;l&#257;t&#299;vus, &#299;</b> <i>m</i> [effero I] <i>грам.</i>\n(<i>sc.</i> gradus) элатив, <i>т. е.</i> абсолютная превосходная степень (<i>обозначающая очень высокую степень свойства без сравнения с этим же свойством других предметов</i>)."],
  ["elato", "<big>elato</big>\n\n<b>&#275;–l&#257;to, —, —, &#257;re</b> [latus II]\nраспространять, ширить (verbum alicujus <i>Eccl</i>)."],
  ["elatro", "<big>elatro</big>\n\n<b>&#275;–l&#257;tro, —, —, &#257;re</b>\nвыкрикивать, высказывать громко (aliquid <i>H, Tert</i>)."],
  ["elatus 1", "<big>elatus</big>\n\n<b>I &#275;l&#257;tus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> effero I;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> возвышенный, высокий (rupes <i>Amm</i>); направленный вверх (gestus <i>Q</i>); повышенный, приподнятый (modi <i>Q</i>);\n<b>2)</b> возвышенного образа мыслей (animus <i>C</i>); патетический (verba <i>C</i>);\n<b>3)</b> надменный, гордый (stulta arrogantia <i>Cs</i>)."],
  ["Elatus 2", "<big>Elatus</big>\n\n<b>II Elatus, &#299;</b> <i>m</i>\n<i>царь лапифов в Лариссе (Фессалия), отец Кенея и Полифема (см.</i> Elateius)."],
  ["elautus", "<big>elautus</big>\n\n<b>&#275;lautus, a, um</b> <i>part. pf. к</i>\nelavo."],
  ["Elaver", "<big>Elaver</big>\n\n<b>El&#257;ver, eris</b> <i>n</i>\n<i>левый приток реки Лигера (ныне Allier) <i>Cs</i>.</i>"],
  ["elavo", "<big>elavo</big>\n\n<b>&#275;–lavo, l&#257;v&#299;, lautum (l&#333;tum), &#257;re</b>\n<b>1)</b> вымывать, отмывать, купать (elotus in balineis <i>Pl</i>);\n<b>2)</b> быть вымытым, выкупаться: e. bonis <i>ирон. Pl</i> лишиться имущества, разориться; e. in mari <i>Pl</i> искупаться в море, <i>т. е.</i> потерпеть кораблекрушение;\n<b>3)</b> очищать, рушить, обдирать (elota genera frumenti <i>CC</i>)."],
  ["elbus", "<big>elbus</big>\n\n<b>elbus</b> <i>v. l.</i>\n= helvus."],
  ["Elea", "<big>Elea</big>\n\n<b>Elea, ae</b> (= Velia) <i>f</i>\nЭлея, <i>город в Лукании, место рождения философов Парменида и Зенона, родина &laquo;элейской&raquo; школы философов</i> <i>C</i>."],
  ["Eleates", "<big>Eleates</big>\n\n<b>Ele&#257;t&#275;s, ae</b> <i>m</i>\nэлеат, элеец, житель города Элей <i>C</i>."],
  ["Eleaticus", "<big>Eleaticus</big>\n\n<b>Ele&#257;ticus, a, um</b> [Elea]\nэлейский (philosophi <i>C</i>)."],
  ["elecebra", "<big>elecebra</big>\n\n<b>&#275;lecebra, ae</b> <i>f</i> [elicio]\nвыманивающая деньги, соблазнительница, шантажистка <i>Pl</i>."],
  ["electa", "<big>electa</big>\n\n<b>&#275;l&#275;cta, &#333;rum</b> <i>n</i> [electus I]\nизбранные места <i>PJ</i>."],
  ["electarium", "<big>electarium</big>\n\n<b>&#275;l&#275;ct&#257;rium, &#299;</b> <i>n</i>\nмазь <i>CA</i>."],
  ["electe", "<big>electe</big>\n\n<b>&#275;l&#275;ct&#275;</b> [electus]\nс разбором, изысканно (digerere in genus quoddam causae <i>C</i>; loqui <i>AG</i>)."],
  ["electilis", "<big>electilis</big>\n\n<b>&#275;l&#275;ctilis, e</b> [eligo]\nотборный, изысканный (piscatus <i>Pl</i>)."],
  ["electio", "<big>electio</big>\n\n<b>&#275;l&#275;ctio, &#333;nis</b> <i>f</i> [eligo]\n<b>1)</b> тщательный отбор (verborum <i>C</i>); выбор: facilis inventio, non facilis e. <i>погов. PJ</i> найти легко, выбрать трудно; si detur (dabitur, ponitur) e. <i>Sen</i> если позволено выбирать; electionem alicui permittere <i>T, PJ</i> предоставить кому-л. выбор;\n<b>2)</b> избрание (legatorum <i>T</i>)."],
  ["electo 1", "<big>electo</big>\n\n<b>I &#275;lecto, —, —, &#257;re</b> [<i>intens. к</i> elicio]\nвыманивать (тайну), выведывать (quidquid est <i>Pl</i>)."],
  ["electo 2", "<big>electo</big>\n\n<b>II &#275;l&#275;cto, —, —, &#257;re</b> [<i>intens. к</i> eligo]\nвыбирать, делать отбор, избирать <i>Pl</i> (<i>v. l.</i>)."],
  ["elector 1", "<big>elector</big>\n\n<b>I &#275;l&#275;ctor, &#333;ris</b> <i>m</i> [eligo]\nвыбирающий <i>rhH</i>; избиратель <i>Eccl</i>."],
  ["Elector 2", "<big>Elector</big>\n\n<b>II &#274;lect&#333;r, oris</b> <i>m</i> (<i>греч.</i>)\nсияющий (sol vocitatus est E. <i>PM</i>)."],
  ["Electra", "<big>Electra</big>\n\n<b>&#274;lectra, ae</b> (<i>acc.</i> am <i>и</i> an) <i>f</i>\nЭлектра:\n<b>1)</b> <i>дочь Атланта и Плейоны, одна из семи Плеяд, мать Дардана</i> (<i>от Юпитера</i>) <i>V, O</i>;\n<b>2)</b> <i>дочь Агамемнона и Клитемнестры, сестра Ореста, впоследствии жена Пилада</i> <i>Prp, H, VP</i>."],
  ["electreus", "<big>electreus</big>\n\n<b>&#275;lectreus, a, um</b> <i>Lampr</i>\n(<i>v. l.</i>) = electrinus."],
  ["electrifer", "<big>electrifer</big>\n\n<b>&#275;lectri–fer, fera, ferum</b>\nдающий янтарную камедь (alnus <i>Cld</i>)."],
  ["electrinus", "<big>electrinus</big>\n\n<b>&#275;lectrinus, a, um</b> [electrum 2]\nсделанный из электра (patera <i>Treb</i>; vasa <i>Dig</i>)."],
  ["Electrius", "<big>Electrius</big>\n\n<b>&#274;lectrius, a, um</b> [Electra 1]\nэлектрин: Electria tellus <i>VF</i> = Samothrace."],
  ["electrum", "<big>electrum</big>\n\n<b>&#275;lectrum, &#299;</b> <i>n</i> (<i>греч.</i>; <i>лат.</i> sucinum)\n<b>1)</b> янтарь <i>или</i> жёлтая амбра <i>V, PM etc.</i>; изделие из янтаря <i>J</i>; <i>pl.</i> янтарные шарики (<i>для охлаждения рук</i>) <i>O</i>;\n<b>2)</b> электр, <i>янтарного цвета сплав золота (4 части) и серебра (1 часть</i>) <i>V, Sil</i>."],
  ["electrus 1", "<big>electrus</big>\n\n<b>I &#275;lectrus, a, um</b> [electrum]\nсделанный из электра (nummus <i>Lampr</i>)."],
  ["Electrus 2", "<big>Electrus</big>\n\n<b>II &#274;lectrus, &#299;</b> <i>m</i> (<i>греч.</i>)\nЭлектр (<i>прав.</i> Электрион), <i>отец Алкмены</i> <i>Pl</i>."],
  ["electus 1", "<big>electus</big>\n\n<b>I &#275;l&#275;ctus, a, um</b> [eligo]\nизбранный (multitudo <i>bAl</i>); отборный (verba <i>C</i>); лучший (electissima pessimi poetae scripta <i>Ctl</i>)."],
  ["electus 2", "<big>electus</big>\n\n<b>II &#275;l&#275;ctus, &#363;s</b> <i>m</i> [eligo]\nвыбор (alicujus rei <i>O</i>)."],
  ["eleemosyna", "<big>eleemosyna</big>\n\n<b>ele&#275;mosyna, ae</b> <i>f</i> (<i>греч.</i>; <i>тж.</i> <i>pl.</i>)\nмилостыня <i>Eccl</i>."],
  ["elegans", "<big>elegans</big>\n\n<b>&#275;leg&#257;ns, antis</b> [eligo]\n<b>1)</b> разборчивый, прихотливый (homo cuppes, avarus, e. <i>Pl</i>; &laquo;e. homo&raquo; non dicebatur cum laude <i>AG</i>);\n<b>2)</b> изысканный, изящный (homo, orator <i>C etc.</i>; mulier <i>VM</i>); утончённый, тонкий, искусный (homo in aliqua re elegantissimus <i>C</i>); образцовый, красивый (lingua <i>Ctl</i>; epistula <i>C, PJ</i>; genus dicendi <i>C</i>);\n<b>3)</b> логичный, стройный, безупречный (in judicio <i>C</i>)."],
  ["eleganter", "<big>eleganter</big>\n\n<b>&#275;leganter</b> [elegans]\n<b>1)</b> со вкусом, изысканно (e. acta aetas <i>C</i>); достойным образом (aliquem accipere <i>C</i>); изящно (dicere, scribere <i>C</i>); безукоризненно (Latine loqui <i>C</i>);\n<b>2)</b> остроумно, искусно (loca capere <i>L</i>);\n<b>3)</b> строго логически (dividere <i>C</i>)."],
  ["elegantia", "<big>elegantia</big>\n\n<b>&#275;legantia, ae</b> <i>f</i> [elegans]\n<b>1)</b> утончённость, изысканность, изящество (morum <i>T</i>; vitae, verborum <i>C</i>): e. capilli <i>PM</i> изящная причёска; materies crispioris elegantiae <i>PM</i> древесина с изящными прожилками;\n<b>2)</b> тонкость, остроумие (e. Socraticorum <i>Q</i>): e. doctrinae <i>C</i> высокая образованность;\n<b>3)</b> стройность, логичность (disserendi <i>C</i>)."],
  ["elegatus", "<big>elegatus</big>\n\n<b>eleg&#257;tus, &#299;</b> <i>m</i>\nэлегат (<i>неизвестная нам рыба</i>) <i>Aus</i>."],
  ["elegea", "<big>elegea</big>\n\n<b>eleg&#275;a, eleg&#275;ia</b> <i>v. l.</i>\n= elegia."],
  ["elegeion", "<big>elegeion</big>\n\n<b>elegeion, &#299;</b> <i>n</i> (<i>греч.</i>)\nэлегическая надпись <i>Aus</i>."],
  ["elegeus", "<big>elegeus</big>\n\n<b>eleg&#275;us (eleg&#299;us), a, um</b> (<i>греч.</i>) <i>стих.</i>\nэлегический (metrum)."],
  ["elegi", "<big>elegi</big>\n\n<b>eleg&#299;, &#333;rum</b> <i>m</i> (<i>греч.</i>)\nэлегические стихи, элегия <i>H, T etc.</i>"],
  ["elegia", "<big>elegia</big>\n\n<b>eleg&#299;a (-g&#275;a, -g&#275;ia), ae</b> <i>f</i> (<i>греч.</i>)\n<b>1)</b> элегия <i>Q, O</i>;\n<b>2)</b> род низкорослого камыша <i>PM</i>."],
  ["elegiacus", "<big>elegiacus</big>\n\n<b>elegiacus, a, um</b>\n= elegeus."],
  ["elegibilis", "<big>elegibilis</big>\n\n<b>&#275;legibilis, e</b>\nмогущий быть избранным <i>Bo&euml;t</i>."],
  ["elegidarion", "<big>elegidarion</big>\n\n<b>eleg&#299;d&#257;rion, &#299;</b> <i>n</i> <i>и</i> <b>eleg&#299;dion, &#299;</b> <i>n</i> (<i>греч.</i>)\nнебольшая элегия <i>Pt, Pers</i>."],
  ["elegion", "<big>elegion</big>\n\n<b>eleg&#299;on, &#299;</b> <i>n</i> (<i>греч.</i>)\n<b>1)</b> элегическое двустишие (<i>состоящее из гексаметра и пентаметра</i>);\n<b>2)</b> элегия <i>Aus</i>."],
  ["elegius", "<big>elegius</big>\n\n<b>eleg&#299;us, a, um</b> <i>v. l.</i>\n= elegeus."],
  ["elego", "<big>elego</big>\n\n<b>&#275;l&#275;go, &#257;v&#299;, —, &#257;re</b> [ex + legare]\nзавещать (<i>чужому человеку</i>) (patrimonium alicui <i>Pt</i>)."],
  ["Eleis", "<big>Eleis</big>\n\n<b>&#274;l&#275;is, idis</b> [Elis]\nэлидская (humus <i>V</i>)."],
  ["Eleleis", "<big>Eleleis</big>\n\n<b>Elel&#275;is, idis</b> <i>f</i>\nвакханка <i>O</i>."],
  ["Eleleus", "<big>Eleleus</big>\n\n<b>Elele&#865;us, e&#299;</b> <i>m</i> (<i>греч.</i> &laquo;ликующий&raquo;)\n<i>эпитет Вакха</i> <i>O</i>."],
  ["elementarius", "<big>elementarius</big>\n\n<b>element&#257;rius, a, um</b> [elementum]\n<b>1)</b> начальный, элементарный (litterae <i>Capit</i>);\n<b>2)</b> начинающий учиться <i>Sen</i>."],
  ["elementicius", "<big>elementicius</big>\n\n<b>element&#299;cius, a, um</b> [elementum]\nпервичный, изначальный (substantiae <i>Tert</i>)."],
  ["elementum", "<big>elementum</big>\n\n<b>elementum, &#299;</b> <i>n</i> [<i>предпол. от</i> LMN <i>начальных букв второго ряда лат. алфавита</i>]\n<b>1)</b> первичная материя, стихия, первоначало (terra, tam densum naturae e. <i>PM</i>; rerum elementa sunt quattuor <i>Sen</i>): elementa per omnia quaerere <i>J</i> искать по всем стихиям, <i>т. е.</i> решительно всюду;\n<b>2)</b> <i>грам. pl.</i> буквы (<i>как таковые, в отличие от litterae, как звуковых символов</i>);\n<b>3)</b> <i>pl.</i> алфавит (quarta elementorum littera <i>Su</i>);\n<b>4)</b> <i>pl.</i> начатки, элементы, начальные правила, первоначальные основания (elementa loquendi <i>C</i>; e. discere, docere <i>H etc.</i>); prima elementa <i>H</i> начатки грамоты, азбука;\n<b>5)</b> начало, возникновение (prima elementa Romae <i>O</i>);\n<b>6)</b> <i>филос.</i> категория, основное и всеобщее свойство вещей (Aristoteles elementa decem constituit <i>Q</i>)."],
  ["elemporia", "<big>elemporia</big>\n\n<b>elemporia (elaeemporia), ae</b> <i>f</i> (<i>греч.</i>)\nторговля (растительным) маслом <i>Dig</i>."],
  ["elenchus", "<big>elenchus</big>\n\n<b>elenchus, &#299;</b> <i>m</i> (<i>греч.</i>)\n<b>1)</b> серьга с грушевидными жемчужными подвесками <i>J, PM, Dig</i>;\n<b>2)</b> указатель, индекс <i>PM, Su</i>."],
  ["elenium", "<big>elenium</big>\n\n<b>elenium, &#299;</b> <i>n</i> <i>v. l.</i>\n= helenium."],
  ["elentesco", "<big>elentesco</big>\n\n<b>&#275;–lent&#275;sco, —, —, ere</b>\nстановиться мягким, приобретать нежность (panis elentescit <i>CA</i>)."],
  ["elephans", "<big>elephans</big>\n\n<b>eleph&#257;ns, antis</b> <i>m</i> <i>bAfr, PM</i>\n= elephas."],
  ["elephantia", "<big>elephantia</big>\n\n<b>elephantia, ae</b> <i>f</i> <i>Scr</i>\n= elephantiasis."],
  ["elephantiacus", "<big>elephantiacus</big>\n\n<b>elephantiacus, a, um</b>\nслоновый: e. morbus <i>Is</i> = elephantiasis."],
  ["elephantiasis", "<big>elephantiasis</big>\n\n<b>elephantiasis, is</b> <i>f</i> (<i>греч.</i>) <i>мед.</i>\nэлефантиаз(ис), слоновая болезнь <i>PM</i>."],
  ["Elephantine", "<big>Elephantine</big>\n\n<b>Elephant&#299;n&#275;, &#275;s</b> <i>f</i>\nЭлефантина, <i>о-в на Ниле в Верхнем Египте</i> (<i>у города Сиены</i>) <i>Vr, T</i>."],
  ["elephantinus 1", "<big>elephantinus</big>\n\n<b>I elephantinus, a, um</b> [elephas]\nприготовленный (сделанный) из слоновой кости (atramentum <i>PM</i>; liber <i>Vop</i>; compositio <i>CC</i>)."],
  ["elephantinus 2", "<big>elephantinus</big>\n\n<b>II elephant&#299;nus, a, um</b> [elephantus]\nслоновый (caput <i>VM</i>)."],
  ["elephantiosis", "<big>elephantiosis</big>\n\n<b>elephanti&#333;sis</b> <i>Veg</i>\n= elephantiasis."],
  ["elephantiosus", "<big>elephantiosus</big>\n\n<b>elephanti&#333;sus, a, um</b> <i>Aug</i>\n= elephantiacus."],
  ["Elephantis", "<big>Elephantis</big>\n\n<b>Elephantis, idis</b> <i>f</i> <i>Vtr, PM</i>\n= Elephantine."],
  ["elephantus", "<big>elephantus</big>\n\n<b>lephantus, &#299;</b> <i>m, f</i> (<i>греч.</i>)\n<b>1)</b> слон (слониха): elephanti corio circumtentus <i>Pl</i> обтянутый слоновой кожей, <i>перен.</i> толстокожий, тупой;\n<b>2)</b> слоновая кость <i>V</i>;\n<b>3)</b> <i>род морской рыбы</i> <i>PM</i>."],
  ["elephas", "<big>elephas</big>\n\n<b>eleph&#257;s, antis</b> <i>m</i> (<i>греч.</i>)\n1)слон <i>H, etc.</i>;\n<b>2)</b> элефантиаз(ис), слоновая болезнь (= elephantiasis) <i>Lcr</i>."],
  ["Eleus 1", "<big>Eleus</big>\n\n<b>I &#274;l&#275;us, a, um</b> [Elis]\nэлидский <i>C etc.</i>"],
  ["Eleus 2", "<big>Eleus</big>\n\n<b>II &#274;l&#275;us, &#299;</b> <i>m</i>\nэлеец, житель Элиды <i>L etc.</i>"],
  ["Eleusin", "<big>Eleusin</big>\n\n<b>Eleus&#299;n (Eleusis), &#299;nis</b> <i>f</i> (<i>acc</i> &#299;nem, &#299;na <i>и</i> in), <i>поздн. Just, Fronto тж.</i> <b>Eleus&#299;na, ae</b> <i>f</i>\nЭлевсин, <i>приморский город в сев.-зап. Аттике, с храмом Цереры; центр культа этой богини</i> (<i>Элевсинских мистерий</i>) <i>C, L, T, Sen etc.</i>"],
  ["Eleusinius", "<big>Eleusinius</big>\n\n<b>Eleus&#299;n(i)us, a, um</b> [Eleusin]\nэлевсинский (sacra <i>Su, AG</i>)."],
  ["eleutheria 1", "<big>eleutheria</big>\n\n<b>I eleutheria, ae</b> <i>f</i> (<i>греч.</i>)\nсвобода <i>Pl</i>."],
  ["Eleutheria 2", "<big>Eleutheria</big>\n\n<b>II Eleutheria, &#333;rum</b> <i>n</i> (<i>sc.</i> sacra)\nЭлевтерии, <i>празднества в честь Юпитера-Освободителя</i> <i>Pl</i>."],
  ["Eleutherius", "<big>Eleutherius</big>\n\n<b>Eleutherius, i</b> <i>m</i> (<i>греч.</i>; <i>лат.</i> Liberator)\nОсвободитель, <i>эпитет Юпитера <i>T</i> и Вакха</i> (<i>Liber</i>) <i>Eccl</i>."],
  ["Eleutherocilices", "<big>Eleutherocilices</big>\n\n<b>Eleutherocilices, um</b> <i>m</i> (<i>греч.</i>)\n&laquo;свободные киликийцы&raquo;, <i>враждебные римлянам народности вост. Киликии</i> <i>C</i>."],
  ["elevatio", "<big>elevatio</big>\n\n<b>&#275;lev&#257;tio, &#333;nis</b> <i>f</i> [elevo]\n<b>1)</b> поднятие (manuum <i>Vlg</i>);\n<b>2)</b> <i>грам.</i> повышение, подъём: e. vocis <i>Is</i> повышение голоса, <i>т. е.</i> arsis;\n<b>3)</b> <i>ритор.</i> ироническое восхваление, <i>т. е.</i> принижение <i>Q</i>."],
  ["elevator", "<big>elevator</big>\n\n<b>&#275;lev&#257;tor, &#333;ris</b> <i>m</i> [elevo]\nподнимающий, <i>т. е.</i> избавитель (deus e. meus <i>Vlg</i>)."],
  ["elevi", "<big>elevi</big>\n\n<b>&#275;l&#275;v&#299;</b> <i>pf. к</i>\nelino."],
  ["elevo", "<big>elevo</big>\n\n<b>&#275;–levo, &#257;v&#299;, &#257;tum, &#257;re</b>\n<b>1)</b> поднимать (contabulationem <i>Cs</i>; aliquid in altum <i>Ap</i>);\n<b>2)</b> убирать, собирать (fructus <i>Col</i>);\n<b>3)</b> облегчать (aegritudinem <i>C</i>); смягчать (suspiciones, offensiones <i>C</i>);\n<b>4)</b> уменьшать, ослаблять (auctoritatem alicujus <i>L</i>); умалять, принижать, унижать (res gestas <i>L</i>; aliquem <i>C, L</i>): e. preces alicujus <i>Prp</i> мешать исполнению чьих-л. просьб || дискредитировать (indicem indiciumque <i>L</i>)."],
  ["Elias", "<big>Elias</big>\n\n<b>&#274;lias, adis</b> <i>f</i> [Elis]\nэлидская <i>V</i>."],
  ["elices", "<big>elices</big>\n\n<b>&#275;lic&#275;s</b> <i>pl. к</i>\nelix."],
  ["elicio", "<big>elicio</big>\n\n<b>&#275;licio, licu&#299;, licitum, ere</b> [ex + *lacio]\n<b>1)</b> выманивать (hostes ex paludibus <i>Cs</i> <i>и</i> in proelium <i>T</i>; aliquem domo <i>H</i>);\n<b>2)</b> заманивать (aliquem ad proelium <i>L</i>); соблазнять, побуждать (aliquem ad disputandum <i>C</i>);\n<b>3)</b> пробуждать (e. misericordiam alicujus <i>L</i>);\n<b>4)</b> призывать, вызывать магическими средствами, заклинать (inferorum animas <i>C</i>; aquas pluvias <i>O</i>; manes <i>H</i>);\n<b>5)</b> добиться (e. alicui responsionem <i>C</i>);\n<b>6)</b> исторгать (alicui lacrimas <i>Pl</i>); вызывать (sudorem <i>PM</i>): cruorem e. <i>T</i> выпускать кровь; alvum e. <i>PM</i> очищать кишечник; ignem lapidum conflictu e. <i>C</i> высекать огонь из камней;\n<b>7)</b> извлекать (sonos <i>C</i>); добывать (ferrum e cavernis terrae <i>C</i>); выведывать, разузнавать (arcana alicujus <i>L</i>; causas alicujus rei <i>C</i>)."],
  ["Elicius", "<big>Elicius</big>\n\n<b>&#274;licius, &#299;</b> <i>m</i> [elicio]\n&laquo;Громовержец&raquo;, <i>эпитет Юпитера, связанный с верой в то, что известными религиозно-магическими обрядами можно было вызывать молнии</i> <i>O, L, Vr, PM</i>."],
  ["Elidensis", "<big>Elidensis</big>\n\n<b>&#274;lid&#275;nsis, e</b> [Elis]\nэлидский <i>AG</i>."],
  ["elido", "<big>elido</big>\n\n<b>&#275;l&#299;do, &#299;s&#299;, &#299;sum, ere</b> [e + laedo]\n<b>1)</b> выталкивать, выбрасывать (на берег) (corpora elisa <i>T</i>; spuma elisa <i>V</i>); выбивать, сшибать (aurigam e curru <i>C</i>); выдавливать, вышибать (oculos <i>Pl</i>);\n<b>2)</b> выжимать (vina prelis elisa Falernis <i>Prp</i>);\n<b>3)</b> опускать, не произносить (litteram <i>AG</i>);\n<b>4)</b> исцелять, вылечивать (tussim <i>CC</i>; morbum <i>H</i>);\n<b>5)</b> высекать (ignem e. silice <i>PM</i>);\n<b>6)</b> вытравлять (partum <i>CC</i>);\n<b>7)</b> извергать (flammas <i>PM</i>); метать (fulmina <i>Sen</i>);\n<b>8)</b> изрекать (sententias <i>Q</i>);\n<b>9)</b> издавать (sonum <i>PM</i>);\n<b>10)</b> (за)душить (leonem brachiorum nexibus <i>Su</i>); сдавливать, сжимать (fauces <i>VP, Sen</i>): e. spiritum <i>CC</i> вызывать удушье; e. laqueo vitam <i>Amm</i> удавить; elidi aegritudine <i>C</i> сокрушаться; e. nervos virtutis <i>C</i> расслаблять силу мужества;\n<b>11)</b> разбивать (naves eliduntur <i>Cs</i>; caput alicujus saxo <i>L</i>): eliso aer pennis <i>O</i> рассекая воздух крыльями;\n<b>12)</b> растирать (herbas <i>O</i>)."],
  ["eligo", "<big>eligo</big>\n\n<b>&#275;ligo, l&#275;g&#299;, l&#275;ctum, are</b> [e + legere]\n<b>1)</b> выдёргивать, удалять, полоть (herbas <i>Vr</i>; gramina <i>Col</i>); вынимать (pedes e capite <i>Vr</i>);\n<b>2)</b> вырывать с корнем, искоренять (superstitionis stirpes <i>C</i>);\n<b>3)</b> выбирать, избирать (sedem sibi <i>QC</i>; ex malis e. minima oportet <i>C</i>; amicos <i>C</i>; imperatorem <i>Su, Vop</i>); действовать с разбором, быть разборчивым: haud semper errat fama, aliquando et eligit <i>T</i> не всегда слава ошибается, иногда и (правильно) разберётся."],
  ["elimate", "<big>elimate</big>\n\n<b>&#275;l&#299;m&#257;t&#275;</b> [elimo I]\nтщательно, точно <i>Ambr</i>."],
  ["elimatio", "<big>elimatio</big>\n\n<b>&#275;l&#299;m&#257;tio, &#333;nis</b> <i>f</i> [elimo I]\n<b>1)</b> обтачивание, <i>перен.</i> уточнение, разъяснение <i>CJ</i>;\n<b>2)</b> уменьшение, убыль (carnis <i>CA</i>)."],
  ["elimator", "<big>elimator</big>\n\n<b>&#275;l&#299;m&#257;tor, &#333;ris</b> <i>m</i>\nпроизводящий чистку, очищающий (от грехов) <i>Tert</i>."],
  ["elimino", "<big>elimino</big>\n\n<b>&#275;l&#299;mino, &#257;v&#299;, &#257;tum, &#257;re</b> [e + limen]\nвыносить за порог: e. dicta foras <i>H</i> разглашать сказанное; e. gradus <i>Poeta ap. Q</i> выходить из дома; e. aliquem <i>Sid</i> выгнать кого-л.; e. se <i>Enn</i> удалиться."],
  ["elimo 1", "<big>elimo</big>\n\n<b>I &#275;–l&#299;mo, &#257;v&#299;, &#257;tum, &#257;re</b>\n<b>1)</b> опиливать, отшлифовывать: elimata scobis <i>PM</i> опилки || искусно изготовлять (graciles ex aere catenas <i>O</i>);\n<b>2)</b> разрабатывать, тщательно обрабатывать, отделывать (Aeneida <i>AG</i>): e. aliquid ad tenue <i>C</i> разработать что-л. до тонкостей (во всех частностях);\n<b>3)</b> опиливанием убавлять, <i>перен.</i> ослаблять (vires elimatae <i>CA</i>)."],
  ["elimo 2", "<big>elimo</big>\n\n<b>II &#275;–l&#299;mo, —, —, &#257;re</b> [limus II]\nчистить (dentes <i>Tert</i>); очищать (animam contagione vitiorum <i>Macr</i>)."],
  ["elimpido", "<big>elimpido</big>\n\n<b>&#275;–limpido, &#257;v&#299;, —, &#257;re</b>\nочищать (vulnera <i>Veg</i>)."],
  ["elingo", "<big>elingo</big>\n\n<b>&#275;–lingo, l&#299;nx&#299;, —, ere</b>\nвылизывать, высасывать (aliquid <i>Vlg, Aug</i>)."],
  ["elinguis", "<big>elinguis</big>\n\n<b>&#275;linguis, e</b> [e + lingua]\n<b>1)</b> безъязычный, лишённый языка, немой (e. et mutus <i>AG</i>);\n<b>2)</b> лишённый дара слова, некрасноречивый <i>C, L etc.</i>;\n<b>3)</b> обрёкший себя на безмолвие, давший обет молчания (<i>sc.</i> discipuli Pythagorae <i>Ap</i>)."],
  ["elinguo", "<big>elinguo</big>\n\n<b>&#275;linguo, &#257;v&#299;, —, &#257;re</b> [elinguis]\nвырезать язык, лишить языка (aliquem <i>Pl, Tert</i>)."],
  ["elino", "<big>elino</big>\n\n<b>&#275;–lino, l&#275;v&#299;, —, ere</b>\nизмазать, испачкать (vestimenta luto <i>LM</i>)."],
  ["eliquatio", "<big>eliquatio</big>\n\n<b>&#275;–liqu&#257;tio, &#333;nis</b> <i>f</i> [liquesco]\nразжижение (materiae <i>CA</i>)."],
  ["eliquesco", "<big>eliquesco</big>\n\n<b>&#275;–liqu&#275;sco, —, —, ere</b>\nделаться жидким, быть выжимаемым (olea eliquescit <i>Vr</i>)."],
  ["eliquium", "<big>eliquium</big>\n\n<b>&#275;liquium, &#299;</b> <i>n</i> [eliquo]\n<b>1)</b> истечение (eliquia Oceani <i>Sol</i>);\n<b>2)</b> убывание, убыль (lunae <i>Sol</i>)."],
  ["eliquo", "<big>eliquo</big>\n\n<b>&#275;–liquo, —, &#257;tum, &#257;re</b>\n<b>1)</b> процеживать (vinum a faecibus <i>Col</i>);\n<b>2)</b> очищать (aquam <i>Ap etc.</i>; mcommodum alvi <i>Ap</i>);\n<b>3)</b> цедить сквозь зубы, &laquo;тянуть&raquo; (aliquid plorabile <i>Pers</i>);\n<b>4)</b> петь вполголоса, тихо напевать (canticum semihiantibus labellis <i>Ap</i>);\n<b>5)</b> тщательно обыскивать (aliquid <i>Eccl</i>)."],
  ["Elis", "<big>Elis</big>\n\n<b>&#274;lis, idis</b> <i>f</i>\nЭлида, <i>город и область на крайнем западе Пелопоннеса, с городом Олимпией, центром Олимпийских игр</i> <i>C, V, Nep etc.</i>"],
  ["Elisa", "<big>Elisa</big>\n\n<b>El&#299;sa (Elissa), ae</b> <i>f</i>\n<i>пуническое имя Дидоны</i> <i>V, O etc.</i>"],
  ["elisio", "<big>elisio</big>\n\n<b>&#275;l&#299;sio, &#333;nis</b> <i>f</i> [elido]\n<b>1)</b> выдавливание, выталкивание: lacrimae per elisionem cadunt <i>Sen</i> слёзы текут сами собой (непроизвольно);\n<b>2)</b> <i>грам.</i> элизия, опущение буквы (<i>преим.</i> гласной)."],
  ["Eliss-", "<big>Eliss-</big>\n\n<b>Eliss-</b> <i>v. l.</i>\n= Elis-."],
  ["Elissaeus", "<big>Elissaeus</big>\n\n<b>El&#299;s(s)aeus, a, um</b> [Elisa]\nэлиссин, <i>перен.</i> пунический, карфагенский: E. tyrannus <i>Sil</i> = Hannibal."],
  ["elisus", "<big>elisus</big>\n\n<b>&#275;l&#299;sus, a, um</b> <i>part. pf. к</i>\nelido."],
  ["Elius", "<big>Elius</big>\n\n<b>&#274;lius (&#274;l&#275;us), a, um</b> [Elis]\nэлидский <i>C, L etc.</i>"],
  ["elix", "<big>elix</big>\n\n<b>&#275;lix, icis</b> <i>m</i> [elicio]\nсток (для избыточной воды в полях), канава <i>Vr, Col</i>."],
  ["elixus", "<big>elixus</big>\n\n<b>&#275;–lixus, a, um</b> [lix]\n<b>1)</b> варёный (vervex <i>J</i>; vitulus <i>Pt</i>);\n<b>2)</b> вымоченный, промокший (calcei <i>Vr</i>); распаренный, вспотевший (balneator <i>M</i>)."],
  ["ellam", "<big>ellam</big>\n\n<b>ellam</b> <i>interj.</i> (= en illam)\n— <i>см.</i> en."],
  ["elleboro", "<big>elleboro</big>\n\n<b>elleboro, —, —, &#257;re</b> [elleborus]\nлечить эллебором <i>CA</i>."],
  ["elleborosus", "<big>elleborosus</big>\n\n<b>ellebor&#333;sus, a, um</b> [elleborus]\nнуждающийся в эллеборе, <i>т. е.</i> сумасшедший <i>Pl</i>."],
  ["elleborum", "<big>elleborum</big>\n\n<b>elleborum, &#299;</b> <i>n</i> (<i>греч.</i>) <i>бот.</i>\nэллебор, чемерица, морозник (<i>Helleborus orientalis</i>, Willd.), <i>применявшийся как средство против душевных болезней и эпилепсии, а также как слабительное и рвотное</i> (e. accipere <i>Sen</i>; elleboro animum detergere <i>Pt</i>); e. alicui opus est <i>погов. Pl</i> кому-л. нужен эллебор (<i>т. е.</i> кто-л. не в своём уме); tristi tristius elleboro <i>погов. Ctl</i> горше горького эллебора."],
  ["elleborus", "<big>elleborus</big>\n\n<b>elleborus, &#299;</b> <i>m</i> <i>V, Col, CA</i>\n= elleborum."],
  ["ellipsis", "<big>ellipsis</big>\n\n<b>ell&#299;psis, is</b> <i>f</i> (<i>греч.</i>; <i>лат.</i> detractio)\nэллипс(ис), опущение слова <i>Q</i>."],
  ["ellops", "<big>ellops</big>\n\n<b>ellops, lopis</b> <i>m</i> <i>v. l.</i>\n= helops."],
  ["ellum", "<big>ellum</big>\n\n<b>ellum</b> <i>interj.</i> (= en illum)\n— <i>см.</i> en."],
  ["elluor", "<big>elluor</big>\n\n<b>elluor</b> <i>v. l.</i>\n= hel(l)uor."],
  ["ellychnium", "<big>ellychnium</big>\n\n<b>ellychnium, &#299;</b> <i>n</i> (<i>греч.</i>; <i>лат.</i> linamentum)\nламповая светильня, фитиль <i>Vtr, PM, Is</i>."],
  ["eloco", "<big>eloco</big>\n\n<b>&#275;–loco, &#257;v&#299;, &#257;tum, &#257;re</b>\nотдавать внаём, в аренду, на откуп (fundum <i>C</i>): e. gentem <i>C</i> сдавать на откуп дань, взимаемую с (какого-л.) народа."],
  ["elocutio", "<big>elocutio</big>\n\n<b>&#275;loc&#363;tio, &#333;nis</b> <i>f</i> [eloquor]\n<b>1)</b> выражение, речь: e. pluralis <i>Dig</i> употребление слова во множественном числе;\n<b>2)</b> способ изложения, слог, стиль <i>C</i>: elocutionis ratio <i>Q</i> стилистика."],
  ["elocutoria", "<big>elocutoria</big>\n\n<b>&#275;loc&#363;t&#333;ria, ae</b> <i>f</i> [eloquor]\n(<i>sc.</i> ars) учение об ораторском искусстве, риторика <i>Q</i>."],
  ["elocutrix", "<big>elocutrix</big>\n\n<b>&#275;loc&#363;tr&#299;x, &#299;cis</b> <i>f</i> (<i>sc.</i> ars)\nриторика <i>Q</i>."],
  ["elogio", "<big>elogio</big>\n\n<b>&#275;logio, &#257;v&#299;, &#257;tum, &#257;re</b> [elogium]\nкратко упоминать <i>или</i> описывать (aliquid <i>CA</i>)."],
  ["elogium", "<big>elogium</big>\n\n<b>&#275;logium, &#299;</b> <i>n</i> [<i>предпол. греч.</i>]\n<b>1)</b> краткое изречение (Solonis <i>C</i>);\n<b>2)</b> надпись на надгробных памятниках (monumentorum <i>Cato, C etc.</i>; e. tumulo insculpsisse <i>Su</i>) <i>и пр.</i>;\n<b>3)</b> юридический документ (<i>обвинительный протокол, завещание, лишение наследства и др.</i>): cum elogio exheredari <i>Pt</i> лишаться права наследования с приведением законных оснований; ultima elogia <i>CJ</i> = testamentum."],
  ["elongatio", "<big>elongatio</big>\n\n<b>&#275;long&#257;tio, &#333;nis</b> <i>f</i> [elongo]\nудаление <i>Bo&euml;t</i>."],
  ["elonginquo", "<big>elonginquo</big>\n\n<b>&#275;–longinquo, —, —, &#257;re</b> [longinquus] (<i>тж.</i> se e.)\nудаляться <i>Ambr</i>."],
  ["elongo", "<big>elongo</big>\n\n<b>&#275;–longo, &#257;v&#299;, —, &#257;re</b> [longus]\n<b>1)</b> удалять, держать вдали, <i>т. е.</i> не оказывать (auxilium suum <i>Vlg</i>);\n<b>2)</b> удаляться (ab aliquo <i>Vlg</i>)."],
  ["elops", "<big>elops</big>\n\n<b>elops, lopis</b> <i>m</i> <i>v. l.</i>\n= helops."],
  ["eloquens", "<big>eloquens</big>\n\n<b>&#275;loqu&#275;ns, entis</b> [eloquor]\n<b>1)</b> говорящий, имеющий дар речи (res mutae et eloquentes <i>rhH</i>);\n<b>2)</b> в совершенстве красноречивый (<i>об ораторах</i>): M. Antonius disertos ait se vidisse multos, eloquentem omnino neminem <i>C</i> М. Антоний говорит, что речистых (ораторов) он видел много, но истинно красноречивого решительно никого."],
  ["eloquenter", "<big>eloquenter</big>\n\n<b>&#275;loquenter</b> [eloquens]\nкрасноречиво (scribere, respondere <i>PM etc.</i>)."],
  ["eloquentia", "<big>eloquentia</big>\n\n<b>&#275;loquentia, ae</b> <i>f</i> [eloquens]\nдар слова, красноречие <i>C, Q, Su etc.</i>"],
  ["eloquium", "<big>eloquium</big>\n\n<b>&#275;loquium, &#299;</b> <i>n</i> [eloquor]\n<b>1)</b> речь, язык (muti, quibus pro eloquio nutus est <i>Mela</i>);\n<b>2)</b> красноречие (maximus auctor eloquii Romani Tullius <i>Lcn</i>; eloquio tonare <i>V</i>);\n<b>3)</b> красноречивое обращение (eloquio aliquem mollire <i>O</i>);\n<b>4)</b> национальный (племенной) язык (aliquid ex Hebraeo in Latinum e. vertere <i>Is</i>);\n<b>5)</b> изречение (oraculi <i>Aug</i>)."],
  ["eloquor", "<big>eloquor</big>\n\n<b>&#275;–loquor, loqu&#363;tus (loc&#363;tus) sum, loqu&#299;</b> <i>depon.</i>\n<b>1)</b> высказывать, излагать (rem, ut facta est <i>Pl</i>);\n<b>2)</b> произносить речь, говорить (polite aliquid <i>C</i>); выражаться (ornate et copiose <i>C</i>)."],
  ["Elor-", "<big>Elor-</big>\n\n<b>El&#333;r-</b> <i>v. l.</i>\n= Helor-."],
  ["elotus", "<big>elotus</big>\n\n<b>&#275;l&#333;tus, a, um</b> <i>part. pf. к</i>\nelavo."],
  ["elovies", "<big>elovies</big>\n\n<b>&#275;lovi&#275;s, &#275;&#299;</b> <i>f</i> <i>арх. LM</i>\n= eluvies."],
  ["Elpenor", "<big>Elpenor</big>\n\n<b>Elp&#275;n&#333;r, oris</b> <i>m</i>\nЭльпенор, <i>спутник Одиссея, который в опьянении свалился с крыши дворца Кирки и разбился насмерть</i> <i>O</i>."],
  ["Elpinice", "<big>Elpinice</big>\n\n<b>Elpin&#299;c&#275;, &#275;s</b> <i>f</i>\nЭльпиника, <i>дочь Мильтиада, сестра Кимона</i> <i>Nep</i>."],
  ["eluacrus", "<big>eluacrus</big>\n\n<b>&#275;lu&#257;crus, a, um</b> [eluo]\nполоскательный (labrum <i>Cato</i>)."],
  ["elucens", "<big>elucens</big>\n\n<b>&#275;l&#363;c&#275;ns, entis</b>\n<b>1.</b> <i>part. praes. к</i> eluceo;\n<b>2.</b> <i>adj.</i> сияющий, блистательный, яркий (edocendi genus <i>Vr</i>)."],
  ["eluceo", "<big>eluceo</big>\n\n<b>&#275;–l&#363;ceo, l&#363;x&#299;, —, &#275;re</b>\n<b>1)</b> засиять, заблистать (splendidissimo candore <i>C</i>);\n<b>2)</b> явствовать, выказываться, проявляться: scintilla ingenii jam tum elucebat in puero <i>C</i> уже тогда в мальчике обнаружилась искра дарования;\n<b>3)</b> отличаться (virtutibus <i>Nep</i>)."],
  ["elucesco", "<big>elucesco</big>\n\n<b>&#275;l&#363;c&#275;sco, l&#363;x&#299;, —, ere</b> [<i>inchoat. к</i> eluceo]\n<b>1)</b> засиять, заблистать (donec dies elucescat <i>Vlg</i>); <i>impers.</i>: quum jam elucesceret <i>Vlg</i> когда уже рассвело;\n<b>2)</b> становиться ясным, проявляться <i>Eccl</i>."],
  ["elucido", "<big>elucido</big>\n\n<b>&#275;–l&#363;cido, —, —, &#257;re</b> [lucidus]\nразъяснять, возвещать (<i>sc.</i> sapientiam <i>Vlg</i>)."],
  ["elucifico", "<big>elucifico</big>\n\n<b>&#275;–l&#363;cifico, —, —, &#257;re</b> [lux + facio]\nлишать зрения, ослеплять <i>Laberius ap. AG</i>."],
  ["eluctabilis", "<big>eluctabilis</big>\n\n<b>&#275;luct&#257;bilis, e</b> [eluctor]\nпреодолимый (aquae neque pediti eluctabiles, neque navigio <i>Sen</i>)."],
  ["eluctor", "<big>eluctor</big>\n\n<b>&#275;–luctor, &#257;tus sum, &#257;r&#299;</b> <i>depon.</i>\n<b>1)</b> пробиваться, прорываться, протискиваться, с трудом проходить (e. per angusta <i>Sen</i>; aqua eluctabitur <i>V</i>): homo eluctantium verborum <i>T</i> человек, лишённый красноречия;\n<b>2)</b> выпутываться, вывёртываться, преодолевать, побороть (obstantia <i>Sen</i>; difficultates <i>T</i>); подавлять (furorem <i>St</i>)."],
  ["elucubratus", "<big>elucubratus</big>\n\n<b>&#275;l&#363;cubr&#257;tus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> elucubro;\n<b>2.</b> <i>adj.</i> тщательно подготовленный (ad ea, quae dicenda sunt <i>Bo&euml;t</i>)."],
  ["elucubro", "<big>elucubro</big>\n\n<b>&#275;–l&#363;cubro, &#257;v&#299;, &#257;tum, &#257;re</b>\nработать при свете лампы (поздно ночью), <i>перен.</i> ценой упорного труда изучать <i>или</i> создавать (librum <i>T</i>; causas <i>C</i>; opus <i>VM</i>)."],
  ["elucubror", "<big>elucubror</big>\n\n<b>&#275;l&#363;cubror, &#257;tus sum, &#257;r&#299;</b> <i>C</i>\n= elucubro."],
  ["elucus", "<big>elucus</big>\n\n<b>&#275;l&#363;cus, &#299;</b> <i>m</i> [ex + lux]\n<b>1)</b> заспанный человек, сонливец <i>AG</i>;\n<b>2)</b> сонливость <i>AG, Tert</i>."],
  ["eludo", "<big>eludo</big>\n\n<b>&#275;–l&#363;do, l&#363;s&#299;, l&#363;sum, ere</b>\n<b>1)</b> избегать, увёртываться, увиливать (e. pugnam <i>L</i>; aliquem <i>V, VF, O</i>); обходить (vim legis <i>Su</i>); уклоняться, парировать (hastam <i>M</i>; telum opposita veste <i>Sen</i>);\n<b>2)</b> издеваться, высмеивать (aliquem <i>C etc.</i>; omnia <i>Pt</i>);\n<b>3)</b> проводить, обманывать (aliquem <i>C, Cs, L</i>); elusus <i>Pt</i> обманутый, одураченный; aliquem anulum e. (in alea) <i>Pl</i> обманом выиграть у кого-л. кольцо;\n<b>4)</b> плескаться (qua fluctus eludit <i>C, Q</i>)."],
  ["elugeo", "<big>elugeo</big>\n\n<b>&#275;–l&#363;geo, l&#363;x&#299;, —, &#275;re</b>\n<b>1)</b> закончить траур, перестать оплакивать: quum eluxerunt <i>L</i> когда дни траура у них закончились;\n<b>2)</b> скорбеть (о), оплакивать (unicum filium <i>C</i>)."],
  ["elumbis", "<big>elumbis</big>\n\n<b>&#275;lumbis, e</b> [ex + lumbus]\n<b>1)</b> бессильный, немощный, вялый (orator <i>T</i>; animus <i>Eccl</i>);\n<b>2)</b> обессиливающий, парализующий (virus <i>Eccl</i>)."],
  ["eluminatus", "<big>eluminatus</big>\n\n<b>&#275;–l&#363;min&#257;tus, a, um</b> [lumen]\nлишённый зрения, ослепший (gemino obtutu <i>Sid</i>)."],
  ["eluo", "<big>eluo</big>\n\n<b>&#275;–luo, lu&#299;, l&#363;tum, ere</b>\n<b>1)</b> вымывать, ополаскивать (patinas <i>Pl</i>; corpus <i>O</i>); смывать (colorem <i>Lcr, Q</i>; sanguinem <i>C</i>; maculas vestium <i>PM</i>);\n<b>2)</b> смывать, искупать (crimen <i>O</i>); разгонять, удалять, уничтожать (amara curarum <i>H</i>);\n<b>3)</b> очищать, освобождать (stagna, <i>sc.</i> avibus <i>Col</i>);\n<b>4)</b> промотать, разориться <i>Pl</i>."],
  ["Elusates", "<big>Elusates</big>\n\n<b>Elus&#257;t&#275;s, ium</b> <i>m</i>\nэлусаты, <i>племя в Аквитании с главн. городом Elusa</i> (<i>ныне</i> Eauze) <i>C</i>."],
  ["elusco", "<big>elusco</big>\n\n<b>&#275;–l&#363;sco, —, —, &#257;re</b> [luscus]\nослепить на один глаз (aliquem <i>Dig</i>)."],
  ["elusi", "<big>elusi</big>\n\n<b>&#275;l&#363;s&#299;</b> <i>pf. к</i>\neludo."],
  ["elutio", "<big>elutio</big>\n\n<b>&#275;l&#363;tio, &#333;nis</b> <i>f</i> [eluo]\nмытьё, омовение <i>CA</i>."],
  ["elutrio", "<big>elutrio</big>\n\n<b>&#275;l&#363;trio, &#257;v&#299;, &#257;tum, &#257;re</b>\n<b>1)</b> мыть, стирать (lintea <i>Laberius ap. AG</i>; vellus <i>PM</i>);\n<b>2)</b> процеживать (<i>sc.</i> aquas pluvias <i>PM</i>)."],
  ["elutus", "<big>elutus</big>\n\n<b>&#275;l&#363;tus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> eluo;\n<b>2.</b> <i>adj.</i> водянистый, безвкусный: irriguo nihil est elutior horto <i>H</i> нет ничего безвкуснее (овощей) слишком обильно орошаемого огорода."],
  ["eluvies", "<big>eluvies</big>\n\n<b>&#275luvi&#275;s, &#275;&#299;</b> <i>f</i> [elno]\n<b>1)</b> разлитые, разлив (maris <i>T</i>; aquarum <i>Lact</i>): e. ventris <i>AV</i> понос || наводнение, затопление: eluvie mons est deductus in aequor <i>O</i> гора смыта наводнением;\n<b>2)</b> помои, нечистоты (e. sordium <i>Ap</i>);\n<b>3)</b> гибель (civitatis <i>C</i>);\n<b>4)</b> рытвина, овраг (e. praerupta <i>QC</i>)."],
  ["eluvio", "<big>eluvio</big>\n\n<b>&#275;luvio, &#333;nis</b> <i>f</i> [eluo]\n<b>1)</b> наводнение, разлитие (aquarum <i>C</i>);\n<b>2)</b> затопление (eluviones terrarum <i>C</i>)."],
  ["eluxi", "<big>eluxi</big>\n\n<b>&#275;l&#363;x&#299;</b> <i>pf. к</i>\n<b>1)</b> eluceo <i>и</i> elucesco <i>и к</i>\n<b>2)</b> elugeo."],
  ["eluxurior", "<big>eluxurior</big>\n\n<b>&#275;–luxurior, —, &#257;r&#299;</b>\nбурно расти, буйно разрастаться <i>Col</i>."],
  ["elvella", "<big>elvella</big>\n\n<b>elvella, ae</b> <i>f</i> <i>v. l.</i>\n= helvella."],
  ["Elvina", "<big>Elvina</big>\n\n<b>Elv&#299;na</b> <i>v. l.</i>\n= Helvina."],
  ["Elymaei", "<big>Elymaei</big>\n\n<b>Elymae&#299;, &#333;rum</b> <i>m</i>\nэлимеи, <i>жители области Элимаиды (Elymais) в Сузиане</i> <i>L</i>."],
  ["Elysii", "<big>Elysii</big>\n\n<b>&#274;lysi&#299;, &#333;rum</b> <i>M</i>\n= Elysium."],
  ["Elysium", "<big>Elysium</big>\n\n<b>&#274;lysium, &#299;</b> <i>n</i>\nЭлисий, Элисийские (<i>неправ.</i> Елисейские) поля, <i>обитель блаженных в царстве мертвых</i> <i>V</i>."],
  ["Elysius", "<big>Elysius</big>\n\n<b>&#274;lysius, a, um</b> [Elysium]\nэлисийский (campi <i>V</i>; domus <i>O</i>): Elysia puella <i>M</i> = Proserpina."],
  ["em 1", "<big>em</big>\n\n<b>I em</b> <i>interj.</i>\n<b>1)</b> вот! (em illae sunt aedes <i>Pl</i>);\n<b>2)</b> послушай!, эй!: em tibi male dictis pro istis <i>Pl</i> вот тебе за эти (твои) злые речи;\n<b>3)</b> как?, вот как?: em, nuncin demum? <i>Ter</i> как, только теперь? (<i>v. l. к</i> hem)."],
  ["em 2", "<big>em</big>\n\n<b>II &#275;m</b> <i>арх. LXIIT ap. AG</i>\n= eum."],
  ["emacero", "<big>emacero</big>\n\n<b>&#275;–macero, —, &#257;tum, &#257;re</b>\nистощать (terram satu <i>PM</i>; morborum variis generibus <i>Sen</i>)."],
  ["emacio", "<big>emacio</big>\n\n<b>&#275;–macio, (&#257;v&#299;), &#257;tum, &#257;re</b>\nистощать, изнурять (armentum <i>Col</i>)."],
  ["emacitas", "<big>emacitas</big>\n\n<b>em&#257;cit&#257;s, &#257;tis</b> <i>f</i> [emax]\nстрасть покупать <i>PJ, Col</i>."],
  ["emacresco", "<big>emacresco</big>\n\n<b>&#275;–macr&#275;sco, eru&#299;, —, ere</b>\nхудеть, тощать <i>CC</i>."],
  ["emaculo", "<big>emaculo</big>\n\n<b>&#275;–maculo, &#257;v&#299;, &#257;tum, &#257;re</b>\n<b>1)</b> выводить пятна (e. pallium <i>Macr</i>); чистить (dentes pulvere <i>Ap</i>); счищать (nigras vitiligines <i>PM</i>);\n<b>2)</b> окуривать (domum odoribus Arabicis <i>Amm</i>)."],
  ["emanatio", "<big>emanatio</big>\n\n<b>&#275;m&#257;n&#257;tio, &#333;nis</b> <i>f</i> [emano]\nистечение, эманация (sapientia e. claritatis dei <i>Vlg</i>)."],
  ["emancipatio", "<big>emancipatio</big>\n\n<b>&#275;mancip&#257;tio, &#333;nis</b> <i>f</i> [emancipo]\nэманципация:\n<b>1)</b> формальное освобождение сына из-под отцовской власти (<i>снятие</i> patria potestas) <i>G, Dig</i>;\n<b>2)</b> официальный отказ от права собственности (<i>путём фиктивной продажи</i> per aes et libram) <i>PJ</i>: e. familiae <i>AG</i> формальный отказ от прав главы семейства."],
  ["emancipator", "<big>emancipator</big>\n\n<b>&#275;mancip&#257;tor, &#333;ris</b> <i>m</i> [emancipo]\nосвободитель, избавитель <i>Eccl</i>."],
  ["emancipo", "<big>emancipo</big>\n\n<b>&#275;–mancipo, &#257;v&#299;, &#257;tum, &#257;re</b>\n<b>1)</b> освобождать сына из-под отцовской власти (<i>путём троекратных</i> mancipatio <i>и</i> manumissio) <i>L, PJ, CJ, Dig</i>;\n<b>2)</b> отказываться, уступать, отдавать (filium in adoptionem alicui <i>C</i>; praedia paterna <i>Q</i>; agrum <i>Su</i>);\n<b>3)</b> передавать (se alicui <i>Pl</i>)."],
  ["emanco", "<big>emanco</big>\n\n<b>&#275;manco, &#257;v&#299;, —, &#257;re</b> [e + mancus]\nкалечить, увечить (aliquem <i>Labienus ap. Sen</i>)."],
  ["emancupo", "<big>emancupo</big>\n\n<b>&#275;mancupo</b> <i>v. l.</i>\n= emancipo."],
  ["emaneo", "<big>emaneo</big>\n\n<b>&#275;–maneo, m&#257;ns&#299;, m&#257;nsum, &#275;re</b>\n<b>1)</b> оставаться в стороне, быть вне, удаляться (aliqua re <i>Sl</i>);\n<b>2)</b> находиться в самовольной отлучке (<i>о рабах</i>) <i>Dig</i>;\n<b>3)</b> не вернуться вовремя из отпуска (<i>о солдатах</i>) <i>Dig</i>."],
  ["emano", "<big>emano</big>\n\n<b>&#275;–m&#257;no, &#257;v&#299;, &#257;tum, &#257;re</b>\n<b>1)</b> вытекать, струиться (fons, unde emanat aqua <i>C</i>);\n<b>2)</b> проистекать, иметь начало (mala istinc emanant <i>C</i>);\n<b>3)</b> <i>impers.</i>: emanat <i>L etc.</i> выясняется, становится известным;\n<b>4)</b> распространяться, разноситься (consilia alicujus emanant <i>L</i>; in vulgus e. <i>Su</i>)."],
  ["emansio", "<big>emansio</big>\n\n<b>&#275;m&#257;nsio, &#333;nis</b> <i>f</i> [emaneo]\nневозвращение из (просрочка) отпуска <i>Dig</i>."],
  ["emansor", "<big>emansor</big>\n\n<b>&#275;m&#257;nsor, &#333;ris</b> <i>m</i> [emaneo]\nнаходящийся в самовольной отлучке <i>или</i> не возвратившийся вовремя из отпуска <i>Dig</i>."],
  ["emarcesco", "<big>emarcesco</big>\n\n<b>&#275;–marc&#275;sco, marcu&#299;, —, ere</b>\nувядать, хиреть <i>Sen, PM, Hier</i>."],
  ["emarcus", "<big>emarcus</big>\n\n<b>&#275;marcus, &#299;</b> <i>m</i> (<i>галльск.</i>)\nэмарк, <i>сорт винограда</i> <i>Col</i>."],
  ["emargino", "<big>emargino</big>\n\n<b>&#275;–margino, —, —, &#257;re</b>\nудалять края, расширять (ulcera <i>PM</i>)."],
  ["emasculator", "<big>emasculator</big>\n\n<b>&#275;mascul&#257;tor, &#333;ris</b> <i>m</i> [emasculo]\nрастлитель <i>Ap</i>."],
  ["emasculo", "<big>emasculo</big>\n\n<b>&#275;masculo, —, —, &#257;re</b> [e + masculus]\nоскоплять, кастрировать (asinum <i>Ap</i>)."],
  ["Emathia", "<big>Emathia</big>\n\n<b>&#274;mathia, ae</b> <i>f</i>\nЭматия:\n<b>1)</b> <i>древнее название Македонии</i> <i>V, Just, PM</i>;\n<b>2)</b> <i>область в сев. и центр. Македонии</i> <i>L</i>;\n<b>3)</b> <i>поэт.</i> Македония <i>или</i> Фессалия <i>V</i>."],
  ["Emathion", "<big>Emathion</big>\n\n<b>&#274;mathi&#333;n, onis</b> <i>m</i>\nЭматион, <i>миф. основатель Македонского царства</i> (<i>Эматии</i>) <i>Just</i>."],
  ["Emathis", "<big>Emathis</big>\n\n<b>&#274;mathis, idis</b> <i>f</i>\n<b>1.</b> <i>adj.</i> [Emathia] эматийская (tellus <i>Lcn</i>);\n<b>2.</b> <i>subst.</i> Пиерида, <i>дочь македонского царя Пиера</i> <i>O</i>."],
  ["Emathius", "<big>Emathius</big>\n\n<b>&#274;mathius, a, um</b> [Emathia]\nмакедонский: dux E. <i>O</i> = Alexander Magnus; Emathia tecta <i>Lcn</i> = Alexandria || фессалийский (campi, litus <i>Lcn</i>): Emathia acies <i>Lcn</i> бой при Фарсале; E. vertex <i>V</i> = Pelion || фракийский (ventus <i>LM</i>)."],
  ["ematuresco", "<big>ematuresco</big>\n\n<b>&#275;–m&#257;t&#363;r&#275;sco, ru&#299;, —, ere</b>\n<b>1)</b> созревать, поспевать (segetes ematurescunt <i>AG</i>);\n<b>2)</b> смягчаться (ira Caesaris ematuruit <i>O</i>)."],
  ["emax", "<big>emax</big>\n\n<b>em&#257;x, &#257;cis</b> <i>adj.</i> [emo]\nлюбящий покупать, одержимый страстью к покупкам <i>Cato, C, Nep etc.</i>: prece emaci poscere <i>Pers</i> выпрашивать, обещая уплату."],
  ["embadon", "<big>embadon</big>\n\n<b>embadon (-um), &#299;</b> <i>n</i> (<i>греч.</i>; <i>лат.</i> area)\nплощадь, поверхность <i>Bo&euml;t</i>."],
  ["embamma", "<big>embamma</big>\n\n<b>embamma, atis</b> <i>n</i> (<i>греч.</i>)\nсоус, подливка <i>PM, Dig</i>."],
  ["embasicoetas", "<big>embasicoetas</big>\n\n<b>embasicoet&#257;s, ae</b> (<i>acc.</i> an) <i>m</i> (<i>греч.</i>)\n(&laquo;ложащийся в постель&raquo;)\n<b>1)</b> = cinaedus I, 1 <i>Pt</i>;\n<b>2)</b> глоток перед сном, <i>по др.</i> чаша (<i>определённой формы</i>) <i>Pt</i>."],
  ["embasis", "<big>embasis</big>\n\n<b>embasis</b>, <i>acc.</i> im, <i>abl.</i> i <i>f</i> (<i>греч.</i>)\nкупание <i>CA</i>."],
  ["embates", "<big>embates</big>\n\n<b>embat&#275;s, is</b> <i>m</i> (<i>греч.</i>; <i>лат.</i> modulus) <i>архит.</i>\nмодуль (<i>масштаб для соразмерного построения орнаментальных деталей</i>) <i>Vtr</i>."],
  ["emblema", "<big>emblema</big>\n\n<b>embl&#275;ma, atis</b> <i>n</i> (<i>греч.</i>)\n<b>1)</b> <i>архит.</i> накладной орнамент, рельефные украшения <i>C, Dig</i>;\n<b>2)</b> мозаичная работа, инкрустация <i>Vr, LM, C</i>."],
  ["embola", "<big>embola</big>\n\n<b>embola, ae</b> <i>f</i> (<i>греч.</i>)\nпогрузка на корабль <i>CJ</i>."],
  ["emboliaria", "<big>emboliaria</big>\n\n<b>emboli&#257;ria, ae</b> <i>f</i> [embolium]\nактриса интермедий <i>PM</i>."],
  ["embolimaeus", "<big>embolimaeus</big>\n\n<b>embolimaeus (-mus), a, um</b> (<i>греч.</i>)\nвставной (<i>в високосном году</i>) (dies <i>Aus</i>)."],
  ["embolismus", "<big>embolismus</big>\n\n<b>embolismus, a, um</b>\n<b>1)</b> <i>Sol</i> = embolimaeus;\n<b>2)</b> високосный (annus <i>Is</i>)."],
  ["embolium", "<big>embolium</big>\n\n<b>embolium, &#299;</b> <i>n</i> (<i>греч.</i>)\nвставная пантомима, интермедия, <i>ирон.-перен.</i> любовная интрижка <i>C</i>."],
  ["embolum", "<big>embolum</big>\n\n<b>embolum, &#299;</b> <i>n</i> (<i>греч.</i>)\nметаллический нос (клюв) судна (e. aheneum <i>Pt</i>)."],
  ["embolus", "<big>embolus</big>\n\n<b>embolus, &#299;</b> <i>m</i> (<i>греч.</i>)\nпоршень насоса <i>Vtr</i>."],
  ["emeditatus", "<big>emeditatus</big>\n\n<b>&#275;medit&#257;tus, a, um</b> [ex + meditor]\nнадуманный, притворный (fletus <i>Ap</i>)."],
  ["emedullatus", "<big>emedullatus</big>\n\n<b>&#275;medull&#257;tus, a, um</b> [ex + medulla]\n<b>1)</b> лишённый костного мозга <i>или</i> сердцевины (radix <i>PM</i>);\n<b>2)</b> бесхребетный, немощный (virtus <i>Sid</i>)."],
  ["emendabilis", "<big>emendabilis</big>\n\n<b>&#275;mend&#257;bilis, e</b> [emendo]\nисправимый, поправимый (error <i>L</i>); могущий исправиться (aetas <i>Sen</i>)."],
  ["emendate", "<big>emendate</big>\n\n<b>&#275;mend&#257;t&#275;</b> [emendatus]\nбезошибочно, правильно, безупречно (pure et e. loqui <i>C, Q</i>; perficere <i>Hirt</i>)."],
  ["emendatio", "<big>emendatio</big>\n\n<b>&#275;mend&#257;tio, &#333;nis</b> <i>f</i> [emendo]\n<b>1)</b> исправление, улучшение, усовершенствование (philosophiae veteris <i>C</i>);\n<b>2)</b> способ исправления, целительное средство: si malorum causa bellum est, erit e. pax <i>Q</i> если война — причина зол, то мир будет их исцелением;\n<b>3)</b> исправительная мера, наказание, кара <i>Tert</i>."],
  ["emendator", "<big>emendator</big>\n\n<b>&#275;mend&#257;tor, &#333;ris</b> <i>m</i> [emendo]\nисправитель, усовершенствователь (sermonis usitati <i>C</i>)."],
  ["emendatorius", "<big>emendatorius</big>\n\n<b>&#275;mend&#257;t&#333;rius, a, um</b> [emendo]\nисправляющий, очищающий (ignis <i>Aug</i>)."],
  ["emendatrix", "<big>emendatrix</big>\n\n<b>&#275;mend&#257;tr&#299;x, &#299;cis</b> <i>f</i> [emendator]\nвносящая улучшения, исправительница (vitae, vitiorum <i>C</i>)."],
  ["emendatus", "<big>emendatus</big>\n\n<b>&#275;mend&#257;tus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> emendo;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> безошибочный, верный (locutio <i>C</i>);\n<b>2)</b> безукоризненный (carmen <i>H</i>; mores <i>C</i>; homo <i>PJ</i>)."],
  ["emendico", "<big>emendico</big>\n\n<b>&#275;–mend&#299;co, —, —, &#257;re</b>\nвыпрашивать (stipem, pecuniam <i>Su</i>; emendicata suffragia <i>CTh</i>)."],
  ["emendo", "<big>emendo</big>\n\n<b>&#275;mendo, &#257;v&#299;, &#257;tum, &#257;re</b> [e + mendum]\n<b>1)</b> поправлять, исправлять, улучшать (consuetudinem vitiosam <i>C</i>; res Italas <i>H</i>): pati, quod e. non possis <i>Sen</i> (приходится) переносить то, что непоправимо; emendaturis ignibus dedisse (<i>sc.</i> carmina vitiosa) <i>шутл. O</i> для исправления плохих стихов бросить их в огонь;\n<b>2)</b> лечить, исцелять (cutem, tussim <i>PM</i>);\n<b>3)</b> усмирять, приводить к послушанию (boves <i>Col</i>)."],
  ["emensio", "<big>emensio</big>\n\n<b>&#275;m&#275;nsio, &#333;nis</b> <i>f</i> [emetior]\nпрохождение, путь (lunaris <i>Is</i>)."],
  ["emensus", "<big>emensus</big>\n\n<b>&#275;m&#275;nsus, a, um</b> <i>part. pf. к</i>\nemetior."],
  ["ementior", "<big>ementior</big>\n\n<b>&#275;-mentior, &#299;tus sum, &#299;r&#299;</b> <i>depon.</i>\nлгать, выдумывать, измышлять (auspicia <i>C</i>; falsa naufragia <i>L</i>): e. in aliquem <i>C, Su</i> клеветать на кого-л.; e. aliquem <i>Pl</i> выдавать себя за кого-л.; e. aliquem aliquem <i>O, T</i> выдавать кого-л. за кого-л.; <i>part. pf. pass.</i> ementitus <i>C, Ap</i> выдуманный, вымышленный."],
  ["emeo", "<big>emeo</big>\n\n<b>&#275;–meo, —, &#257;tum, &#257;re</b>\n<b>1)</b> выходить (invenire emeandi locum <i>Aug</i>);\n<b>2)</b> переезжать, переплывать (emeato mari <i>Amm</i>)."],
  ["emercor", "<big>emercor</big>\n\n<b>&#275;–mercor, &#257;tus sum, &#257;r&#299;</b> <i>depon.</i>\n<b>1)</b> выторговать, купить (adulterium donis, aditum principis <i>T</i>); <i>pass.</i> быть покупаемым <i>Amm</i>;\n<b>2)</b> подкупить (aliquem ad exuendam fidem <i>T</i>)."],
  ["emereo", "<big>emereo</big>\n\n<b>&#275;–mereo, meru&#299;, meritum, &#275;re</b>\n<b>1)</b> заслужить (favorem <i>Q</i>);\n<b>2)</b> наживать, зарабатывать (pecuniam ex aliqua re <i>AG</i>); приобретать, стяжать (honores <i>Prp</i>): e. aliquem <i>Tib, O</i> оказывать кому-л. услуги; emeritus <i>O</i> заслуженный;\n<b>3)</b> выслуживать (stipendia emerita <i>L, C</i>): annuae mihi operae emerentur <i>C</i> мои годичные служебные обязанности подходят к концу; (miles) emeritus <i>Su, Lcn</i> (солдат), отслуживший свой срок; aratrum emeritum <i>O</i> негодный (вышедший из употребления) плуг."],
  ["emereor", "<big>emereor</big>\n\n<b>&#275;mereor, meritus sum, &#275;r&#299;</b> <i>depon.</i> <i>O, J, VM</i>\n= emereo."],
  ["emergo", "<big>emergo</big>\n\n<b>&#275;–mergo, mers&#299;, mersum, ere</b>\n<b>1)</b> вынырнуть, выплыть (e palude <i>L</i> <i>и</i> paludibus <i>T</i>); выходить, появляться (ex saltibus in campos <i>L</i>; flos emergit e caule <i>PM</i>);\n<b>2)</b> вытекать, явствовать, следовать: ex quo emergit <i>C</i> из чего следует (явствует);\n<b>3)</b> возникать (fama emersit <i>V</i>; bellum emersit <i>C</i>);\n<b>4)</b> выпутываться, вывёртываться (ex judicio <i>C</i>);\n<b>5)</b> подниматься, восходить (sol emergens <i>T</i>): emersae in lucem noctes <i>O</i> ночи, близящиеся к рассвету;\n<b>6)</b> оправляться, приходить в себя (e. ex mendicitate <i>C</i>; ab admiratione <i>L</i>);\n<b>7)</b> выводить наружу, поднимать (e gurgite vultus <i>Ctl</i>);\n<b>8)</b> (<i>чаще</i> e. se <i>или</i> emergi) выходить, выплывать, выбираться, вынырнуть (emergi e flumine <i>C</i>): e. se ex malis <i>Ter, Nep</i> выпутаться из бед; haud facile emergunt, quorum virtutibus obstat res angusta domi <i>J</i> нелегко подняться тем, чьи способности скованы нуждой."],
  ["Emerita", "<big>Emerita</big>\n\n<b>&#274;merita, ae</b> (<i>тж.</i> <b>Augusta E.</b>) <i>f</i>\nЭмерита, <i>город в Лувитании</i> (<i>ныне</i> Merida) <i>Mela, PM</i>."],
  ["Emeritanus 1", "<big>Emeritanus</big>\n\n<b>I &#274;merit&#257;nus, a, um</b> [Emerita]\nэмеритский <i>T</i>."],
  ["Emeritanus 2", "<big>Emeritanus</big>\n\n<b>II &#274;merit&#257;nus, &#299;</b> <i>m</i>\nжитель Эмериты <i>T</i>."],
  ["Emeritensis", "<big>Emeritensis</big>\n\n<b>&#274;merit&#275;nsis, e</b> <i>T</i>\n= Emeritanus I."],
  ["emeritum", "<big>emeritum</big>\n\n<b>&#275;meritum, &#299;</b> <i>n</i> [emereo]\nсолдатская пенсия за выслугу лет <i>Dig</i>."],
  ["emersus 1", "<big>emersus</big>\n\n<b>I &#275;mersus, a, um</b> <i>part. pf. к</i>\nemergo."],
  ["emersus 2", "<big>emersus</big>\n\n<b>II &#275;mersus, &#363;s</b> <i>m</i> [emergo]\nпоявление (serpentium <i>PM</i>; hostium <i>Vr</i>); возникновение, зарождение (fluminis <i>PM</i>); восход (stellae <i>Col</i>)."],
  ["Emesa", "<big>Emesa</big>\n\n<b>Emesa, Emis(s)a, ae</b> <i>и</i> <b>Emesus, &#299;</b> <i>f</i>\nЭмеса, <i>город в Сирии, на вост. берегу Оронта, место рождения Гелиогабала и Александра Севера</i> (<i>впоследствии</i> Hems) <i>Vop, Amm</i>."],
  ["emetica", "<big>emetica</big>\n\n<b>emetica, ae</b> <i>f</i> (<i>греч.</i>)\nрвотное средство <i>Caelius ap. C</i>."],
  ["emeticus", "<big>emeticus</big>\n\n<b>emeticus, a, um</b> (<i>греч.</i>)\nрвотный (bulbus <i>Ap</i>)."],
  ["emetior", "<big>emetior</big>\n\n<b>&#275;–m&#275;tior, m&#275;nsus sum, &#299;r&#299;</b> <i>depon.</i>\n<b>1)</b> измерять, обмеривать (spatium oculis <i>V</i>);\n<b>2)</b> пройти, проехать, изъездить, исходить, исколесить, избороздить (maria terrasque <i>QC</i>; longum iter <i>L</i>); <i>pass.</i> быть пройденным (major pars itineris emensa <i>L</i>) <i>или</i> проведённым (emensae in lucem noctes <i>O</i>);\n<b>3)</b> провести (saevas noctes <i>Sil</i>); прожить, пережить (Galba quinque principes emensus <i>T</i>);\n<b>4)</b> уделять, давать (aliquid patriae <i>H</i>): voluntatem alicui e. <i>C</i> быть целиком к чьим-л. услугам."],
  ["emeto", "<big>emeto</big>\n\n<b>&#275;–meto, (messu&#299;), messum, ere</b>\nскосить, сжать (frumentum <i>H</i>)."],
  ["emi", "<big>emi</big>\n\n<b>&#275;m&#299;</b> <i>pf. к</i>\nemo."],
  ["emicatim", "<big>emicatim</big>\n\n<b>&#275;mic&#257;tim</b> <i>adv.</i> [emico]\nодним прыжком, вмиг (prosiliens <i>Sid</i>)."],
  ["emicatio", "<big>emicatio</big>\n\n<b>&#275;mic&#257;tio, &#333;nis</b> <i>f</i> [emico 8]\nгромада: emicationes silvarum <i>Ap</i> лесные массивы."],
  ["emico", "<big>emico</big>\n\n<b>&#275;–mico, cu&#299; (c&#257;v&#299;), c&#257;tum, &#257;re</b>\n<b>1)</b> сверкать, блистать, искриться (scintillae emicantes <i>Q</i>): flamma emicat in oculis <i>O</i> глаза горят огнём; fulmen emicuit <i>Lcn</i> молния блеснула; sanguis emicuit <i>O</i> брызнула кровь; pavor alicui emicat <i>T</i> внезапный страх объемлет кого-л.;\n<b>2)</b> устремляться, бросаться (juvenum manus emicat in litus <i>V</i>; e. carcere <i>O</i>);\n<b>3)</b> <i>перен.</i> воссиять, выделиться, отличиться (magnitudine animi <i>QC</i>);\n<b>4)</b> мелькнуть, слететь (emicuit nervo telum <i>O</i>);\n<b>5)</b> быстро подниматься, вырастать (castanea celeriter emicat <i>Col</i>);\n<b>6)</b> пульсировать, биться (cor emicat <i>VP</i>);\n<b>7)</b> подскочить (solo <i>V</i>); вскочить (saltu in currum <i>V</i>); воспарить, взлететь, подняться (in cogitationes divinas, ad summa <i>Sen</i>);\n<b>8)</b> вздыматься, выступать (scopulus emicat in alto gurgite <i>O</i>)."],
  ["emigratio", "<big>emigratio</big>\n\n<b>&#275;migr&#257;tio, &#333;nis</b> <i>f</i> [emigro]\nвыселение, выезд (inquilinorum <i>Dig</i>)."],
  ["emigro", "<big>emigro</big>\n\n<b>&#275;–migro, &#257;v&#299;, &#257;tum, &#257;re</b>\n<b>1)</b> выселяться, переселяться: e. domo <i>Cs</i> выселяться из родины; e. e vita <i>C</i> расставаться с жизнью, умирать;\n<b>2)</b> выселять, изгонять (aliquem tabernaculo suo <i>Vlg</i>)."],
  ["emina", "<big>emina</big>\n\n<b>&#275;m&#299;na, ae</b> <i>f</i> <i>v. l.</i>\n= hemina."],
  ["eminatio", "<big>eminatio</big>\n\n<b>&#275;min&#257;tio, &#333;nis</b> <i>f</i> [eminor]\nугроза <i>Pl</i>."],
  ["eminens", "<big>eminens</big>\n\n<b>&#275;min&#275;ns, entis</b>\n<b>1.</b> <i>part. praes. к</i> emineo;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> выступающий (nasus <i>Su</i>): oculi eminentes <i>C</i> глаза на выкате;\n<b>2)</b> высокий (arbor <i>QC</i>; statura <i>Su</i>; eminentes lecti pari&#277;tes <i>T</i>);\n<b>3)</b> резкий, отчётливый (effigies <i>C</i>);\n<b>4)</b> блистательный, выдающийся (ingenium, vir <i>VP</i>): res dictu non e. <i>VP</i> вещь, о которой распространяться не стоит."],
  ["eminenter", "<big>eminenter</big>\n\n<b>&#275;minenter</b> [eminens]\nпреимущественно <i>Aug</i>; <i>compar.</i> eminentius <i>Aug, Amm</i> более заметным образом: eminentius natus <i>Sid</i> более знатного происхождения."],
  ["eminentes", "<big>eminentes</big>\n\n<b>&#275;minent&#275;s, ium</b> <i>m</i> [eminens]\nвидные (высокопоставленные) лица <i>T, CJ</i>."],
  ["eminentia 1", "<big>eminentia</big>\n\n<b>I &#275;minentia, ium</b> <i>n</i> [eminens]\n<b>1)</b> выступы, шероховатости (rupis <i>L</i>);\n<b>2)</b> замечательные места (orationis <i>Q</i>);\n<b>3)</b> выдающиеся достоинства <i>или</i> качества (nunquam eminentia invidi&amacr; carent <i>VP</i>)."],
  ["eminentia 2", "<big>eminentia</big>\n\n<b>II &#275;minentia, ae</b> <i>f</i> [eminens]\n<b>1)</b> выступ, нарост (eminentiae similes verrucis <i>PM</i>);\n<b>2)</b> выпуклость (culminum <i>Ap</i>); телесность, осязаемость (soliditas et e. <i>C</i>);\n<b>3)</b> освещенное место (<i>картины</i>) <i>C</i>;\n<b>4)</b> превосходство, совершенство (formarum <i>AG</i>);\n<b>5)</b> выдающееся дарование (duae eminentiae, <i>sc.</i> Xenophon et Plato <i>AG</i>);\n<b>6)</b> преимущество: per eminentiam <i>Dig</i> по преимуществу."],
  ["emineo", "<big>emineo</big>\n\n<b>&#275;–mineo, u&#299;, —, &#275;re</b>\n<b>1)</b> выступать, выдаваться, торчать (aliquid eminet e mari <i>C</i>): caro eminens <i>Scr</i> мясистый нарост || подниматься (triginta stadia in altitudinem <i>QC</i>); выделяться, рельефно выступать (in pictura <i>Q</i>);\n<b>2)</b> выявляться, обнаруживаться, быть заметным (aliquid in omnium vultu eminet <i>L</i>): e. ante oculos бросаться в глаза;\n<b>3)</b> отличаться (e. inter omnes <i>C</i>); возвышаться (supra cetera <i>L</i>); превосходить (ceteros aliqua re <i>QC</i>). — <i>См. тж.</i> eminens."],
  ["eminiscor", "<big>eminiscor</big>\n\n<b>&#275;–min&#299;scor, mentus sum, min&#299;sc&#299;</b> [<i>одного корня с</i> comminiscor, mens, memini]\nвыдумывать, придумывать (aliquid <i>Vr, Nep, rhH, Ap</i>)."],
  ["eminor", "<big>eminor</big>\n\n<b>&#275;–minor, —, &#257;r&#299;</b> <i>depon.</i>\nугрожающе заявлять <i>Pl</i>."],
  ["eminulus", "<big>eminulus</big>\n\n<b>&#275;minulus, a, um</b> [emineo]\nнемного выступающий вперёд, несколько выдающийся (genua boum <i>Vr</i>; dens <i>LM, Vr</i>): angulis eminula <i>Ap</i> угловатые выступы, <i>перен.</i> угловатости, шероховатости."],
  ["eminus", "<big>eminus</big>\n\n<b>&#275;minus</b> <i>adv.</i> [ex + manus]\nиздали, на расстоянии : e. hastis aut comminus gladiis uti <i>C</i> пользоваться на расстоянии копьями, в рукопашном бою — мечами; e. pugnare <i>Cs</i> сражаться на расстоянии (выстрела) друг от друга; opem ferre e. <i>O</i> помочь, находясь вдали."],
  ["emiror", "<big>emiror</big>\n\n<b>&#275;–m&#299;ror, (&#257;tus sum), &#257;r&#299;</b> <i>depon.</i>\nсильно удивляться, с изумлением видеть (aspera aequora <i>H</i>)."],
  ["Emissa", "<big>Emissa</big>\n\n<b>Emis(s)a</b> <i>v. l.</i>\n= Emesa."],
  ["emissarium", "<big>emissarium</big>\n\n<b>&#275;miss&#257;rium, &#299;</b> <i>n</i> [emitto]\n<b>1)</b> отводной канал, канава, сток <i>C, PM, Su</i>;\n<b>2)</b> <i>мед.</i> отток (e. vomicae facere <i>Scr</i>)."],
  ["emissarius 1", "<big>emissarius</big>\n\n<b>I &#275;miss&#257;rius, a, um</b> [emitto]\nпосылаемый: caper e. <i>Vlg</i> козёл отпущения; equus e. <i>Vlg</i> случной жеребец."],
  ["emissarius 2", "<big>emissarius</big>\n\n<b>II &#275;miss&#257;rius, &#299;</b> <i>m</i> [emitto]\n<b>1)</b> посланец, эмиссар <i>Vlg</i>;\n<b>2)</b> соглядатай, лазутчик <i>C, VP, Su</i>;\n<b>3)</b> молодой побег, отпрыск <i>PM</i>."],
  ["emissicius", "<big>emissicius</big>\n\n<b>&#275;miss&#299;cius, a, um</b> [emitto]\nнаправленный вперёд, <i>т. е.</i> выслеживающий, подглядывающий (oculi <i>Pl</i>)."],
  ["emissio", "<big>emissio</big>\n\n<b>&#275;missio, &#333;nis</b> <i>f</i> [emitto]\n<b>1)</b> выпускание (serpentis <i>C</i>);\n<b>2)</b> испускание, излучение (radiorum <i>AG</i>);\n<b>3)</b> бросание, метание (telorum <i>C</i>)."],
  ["emissor", "<big>emissor</big>\n\n<b>&#275;missor, &#333;ris</b> <i>m</i> [emitto]\nвыпускающий: e. seminum <i>Aug</i> вызывающий истечение семени."],
  ["emissus", "<big>emissus</big>\n\n<b>&#275;missus, &#363;s</b> <i>m</i> [emitto]\nвысылание, выпускание, испускание (<i>sc.</i> corpusculorum <i>Lcr</i>)."],
  ["emitesco", "<big>emitesco</big>\n\n<b>&#275;–m&#299;t&#275;sco, —, —, ere</b>\nразмягчаться, становиться мягким, <i>перен.</i> нежным на вкус (mel emitescit <i>Col</i>)."],
  ["emitigo", "<big>emitigo</big>\n\n<b>&#275;–m&#299;tigo, —, —, &#257;re</b>\nсмягчать, успокаивать (iram <i>Jvc</i>)."],
  ["emitto", "<big>emitto</big>\n\n<b>&#275;–mitto, m&#299;s&#299;, missum, ere</b>\n<b>1)</b> высылать (equites in hostem <i>L</i>);\n<b>2)</b> изгонять (aliquem ex domo, Catilinam oratione <i>C</i>);\n<b>3)</b> высовывать (aculeum <i>C</i>);\n<b>4)</b> выпускать, отпускать на волю (aliquem e carcere <i>и</i> de carcere <i>C</i>; columbam <i>Vr</i>; aliquem extra januam <i>Pt</i>): e. aliquem sub jugum <i>L</i> отпустить кого-л., пропустив предварительно под игом (<i>см.</i> jugum); emitti e <i>и</i> de manibus <i>L</i> выскользнуть из рук; e. scutum manu <i>Cs</i> отбрасывать щит; e. animam <i>Pl, Nep</i> скончаться;\n<b>5)</b> пускать (sanguinem venis <i>PM</i>);\n<b>6)</b> выпускать в свет (librum <i>Q</i>);\n<b>7)</b> издавать (varios sonitus <i>Lcr</i>): nullum posse verbum e. <i>Pt</i> не быть в состоянии произнести ни единого слова || испускать (odorem <i>PM</i>); излучать (radios <i>PM</i>): e. folia <i>PM</i> <i>или</i> frondem <i>Sen</i> одеваться листвой; arbores emittuntur in ramos <i>PM</i> деревья пускают ветви; flumen e. <i>QC</i> дать начало реке (<i>о горных потоках</i>), <i>но</i>: ex lacu aqua emissa <i>L</i> спущенная из озера вода; lacus emissus <i>C</i> озеро, которому дан отток;\n<b>8)</b> бросать, метать (hastas <i>L</i>; sagittas <i>QC</i>; fulmina <i>C</i>)."],
  ["emo", "<big>emo</big>\n\n<b>emo, &#275;m&#299;, &#275;mptum, ere</b>\nпокупать, приобретать (aliquid ab <i>и</i> de aliquo <i>C</i>; vestes de textrina <i>Ap</i>): quanti emisti? <i>Pl</i> за сколько ты купил?; e. tribus minis <i>Pl</i> купить за три мины; parvo (bene) e. <i>C etc.</i> дёшево купить; magno (male) e. <i>C etc.</i> дорого заплатить; quae pluris emuntur <i>J</i> то, что подороже; quanti emi potest minimo? <i>Pl</i> какова крайняя цена?; e. sententias judicum <i>C</i> подкупать судей; pecunia emptus <i>C</i> подкупленный; empta dolore voluptas <i>H</i> наслаждение, купленное ценой страдания; empti dentes <i>M</i> искусственные (вставные) зубы; e. aliquem aliqua re <i>Pl</i> склонить кого-л. на свою сторону чём-л."],
  ["emoderor", "<big>emoderor</big>\n\n<b>&#275;–moderor, —, &#257;r&#299;</b> <i>depon.</i>\nумерять, успокаивать (dolorem verbis <i>O</i>)."],
  ["emodulor", "<big>emodulor</big>\n\n<b>&#275;–modulor, —, &#257;r&#299;</b> <i>depon.</i>\nвоспевать (Musam <i>O</i>)."],
  ["emola", "<big>emola</big>\n\n<b>emola, ae</b> <i>f</i> <i>Vlg</i>\n= (h)amula."],
  ["emolimentum", "<big>emolimentum</big>\n\n<b>&#275;m&#333;limentum, &#299;</b> <i>n</i> [emolior]\nздание, постройка <i>CTh</i>."],
  ["emolior", "<big>emolior</big>\n\n<b>&#275;–m&#333;lior, &#299;tus sum, &#299;r&#299;</b> <i>depon.</i>\n<b>1)</b> заканчивать, совершать (negotium <i>Pl</i>);\n<b>2)</b> вздымать, волновать (fretum <i>Sen</i>);\n<b>3)</b> выделять (pituitam per nares <i>Col</i>)."],
  ["emolitus 1", "<big>emolitus</big>\n\n<b>I &#275;molitus, a, um</b> <i>part. pf. к</i>\nemolo."],
  ["emolitus 2", "<big>emolitus</big>\n\n<b>II &#275;m&#333;l&#299;tus, a, um</b> <i>part. pf. к</i>\nemolior."],
  ["emollio", "<big>emollio</big>\n\n<b>&#275;–mollio, &#299;v&#299;, &#299;tum, &#299;re</b>\n<b>1)</b> размягчать (amenta jaculorum <i>L</i>);\n<b>2)</b> смягчать, успокаивать (mores <i>O</i>; colores <i>PM</i>);\n<b>3)</b> расслаблять, изнеживать (aliquem <i>L, T</i>); ослаблять (auctoritatem alicujus <i>AV</i>)."],
  ["emolo", "<big>emolo</big>\n\n<b>&#275;–molo, u&#299;, itum, ere</b>\nмолоть, перемалывать (hordeum <i>Veg</i>); <i>перен.</i> перемолоть для немедленного потребления, <i>т. е.</i> растратить (granaria <i>Pers</i>)."],
  ["emolumenticius", "<big>emolumenticius</big>\n\n<b>&#275;molument&#299;cius, a, um</b>\nполезный, спасительный <i>Eccl</i>."],
  ["emolumentum", "<big>emolumentum</big>\n\n<b>&#275;molumentum, &#299;</b> <i>n</i> [emolo]\nуспех, результат, благо, выгода, польза (emolumenta pacis <i>T</i>, laborum <i>J</i>; e. ac damnum commune sentire <i>Dig</i>): esse alicui emolumento <i>C</i> <i>или</i> e. <i>L</i> быть на пользу кому-л."],
  ["emoneo", "<big>emoneo</big>\n\n<b>&#275;–moneo, —, —, &#275;re</b>\nубеждать, увещевать (aliquem <i>C</i>)."],
  ["emorior", "<big>emorior</big>\n\n<b>&#275;–morior, mortuus sum, mor&#299;</B> (<i>арх. Pl, Ter</i> <B>mor&#299;r&#299;</b>) <i>depon.</i>\n<b>1)</b> умирать (aut vincere aut e. <i>C</i>): e. per virtutem <i>Sl</i> умереть смертью героя;\n<b>2)</b> мертветь, отмирать (membrum, quod emoritur, abscindere <i>CC</i>); засыхать (arbor emoritur <i>PM</i>); истощаться (terra emoriens <i>QC</i>); гаснуть (carbo emoriens <i>PM</i>); угасать, прекращаться, совершенно исчезать (laus eorum e. non potest <i>C</i>)."],
  ["emortualis", "<big>emortualis</big>\n\n<b>&#275;mortu&#257;lis, e</b> [emorior]\nсмертный: e. dies <i>Pl</i> день смерти."],
  ["emoveo", "<big>emoveo</big>\n\n<b>&#275;–moveo, m&#333;v&#299;, m&#333;tum, &#275;re</b>\n<b>1)</b> выдвигать, вынимать (cardine postes <i>V</i>);\n<b>2)</b> отодвигать, удалять (multitudinem e foro <i>L</i>);\n<b>3)</b> вывихнуть (articulos <i>Sen</i>): mens emota <i>Sen</i> безумие, помешательство;\n<b>4)</b> вытеснять (aliquid ex pectore <i>Pl</i>); прогонять (pestilentiam <i>L</i>; curas <i>V</i>);\n<b>5)</b> перекопать, взрыть (solum <i>Col</i>);\n<b>6)</b> потрясать (muros fundamentaque <i>V</i>); волновать, колебать (pontum <i>Sil</i>)."],
  ["Empedocles", "<big>Empedocles</big>\n\n<b>Empedocl&#275;s, is</b> <i>и</i> <b>&#299;</b> <i>m</i> (<i>acc.</i> em, en <i>и</i> ea)\nЭмпедокл, <i>греч. философ из Агригента (Сицилия) (ок. 450 г. до н. э.</i>) <i>Lcr, C etc.</i>"],
  ["Empedocleus", "<big>Empedocleus</big>\n\n<b>Empedocl&#275;us, a, um</b>\nэмпедоклов <i>C</i>."],
  ["emphasis", "<big>emphasis</big>\n\n<b>emphasis, is (-eos)</b> (<i>acc.</i> im, <i>abl.</i> i) <i>f</i> (<i>греч.</i>)\nвыразительность, подъём, сила выражения, эмфаза <i>Q</i>."],
  ["emphractus", "<big>emphractus</big>\n\n<b>emphractus, a, um</b> (<i>греч.</i>)\nкрытый: emphractae naves <i>Dig</i> палубные суда."],
  ["emphragma", "<big>emphragma</big>\n\n<b>emphragma, atis</b> <i>n</i> (<i>греч.</i>; <i>лат.</i> praeoccupatio) <i>мед.</i>\nхронический запор <i>Veg</i>."],
  ["emphyteuma", "<big>emphyteuma</big>\n\n<b>emphyteuma, atis</b> <i>n</i> (<i>греч.</i>)\nпредмет долгосрочной (наследственной) аренды <i>CJ</i>."],
  ["emphyteusis", "<big>emphyteusis</big>\n\n<b>emphyteusis, is</b> <i>f</i> (<i>греч.</i>)\nдолгосрочная (наследственная) аренда <i>CJ</i>."],
  ["emphyteuta", "<big>emphyteuta</big>\n\n<b>emphyteuta, ae</b> <i>и</i> <b>emphyteutic&#257;rius, &#299;</b> <i>m</i> (<i>греч.</i>)\nдолгосрочный арендатор <i>CJ</i>."],
  ["empirice", "<big>empirice</big>\n\n<b>emp&#299;ric&#275;, &#275;s</b> <i>f</i> (<i>греч.</i>)\nэмпирическая, <i>т. е.</i> чисто практическая медицина <i>PM</i>."],
  ["empiricus 1", "<big>empiricus</big>\n\n<b>I emp&#299;ricus, a, um</b>\nоснованный на опыте, чисто практический, эмпирический (haeresis medicorum <i>Is</i>)."],
  ["empiricus 2", "<big>empiricus</big>\n\n<b>II emp&#299;ricus, &#299;</b> <i>m</i>\nврач-эмпирик (<i>последователь эмпирического направления</i>) <i>C</i>."],
  ["emplastratio", "<big>emplastratio</big>\n\n<b>emplastr&#257;tio, &#333;nis</b> <i>f</i> [emplastro]\nпрививка щитком (путём наложения коры) <i>Col, PM</i>."],
  ["emplastro", "<big>emplastro</big>\n\n<b>emplastro, (&#257;v&#299;), &#257;tum, &#257;re</b> [emplastrum]\nпрививать щитком (посредством наложения коры) <i>Col, Pall</i>."],
  ["emplastrum", "<big>emplastrum</big>\n\n<b>emplastrum, &#299;</b> <i>n</i> (<i>греч.</i>)\n<b>1)</b> пластырь <i>Cato, CC, PM</i>;\n<b>2)</b> прививочный щиток, повязка, накладываемая на дерево после прививки <i>Col, PM</i>."],
  ["emplecton", "<big>emplecton</big>\n\n<b>emplecton, &#299;</b> <i>n</i> (<i>греч.</i>)\nстена, внутренняя полость которой заполнена щебнем (забутовкой) <i>Vtr</i>."],
  ["empneumatosis", "<big>empneumatosis</big>\n\n<b>empneumat&#333;sis, is</b> <i>f</i> (<i>греч.</i>; <i>лат.</i> inflatio) <i>мед.</i>\nвздутие, раздутость <i>CA</i>."],
  ["emponema", "<big>emponema</big>\n\n<b>empon&#275;ma, atis</b> <i>n</i> (<i>греч.</i>)\nмелиорация <i>CJ</i>."],
  ["emporeticus", "<big>emporeticus</big>\n\n<b>empor&#275;ticus, a, um</b> (<i>греч.</i>)\nкупеческий, торговый: charta emporetica <i>PM</i> обёрточная бумага."],
  ["Emporiae", "<big>Emporiae</big>\n\n<b>Emporiae, &#257;rum</b> <i>f</i>\nЭмпории, <i>приморский город в сев.-вост. Испании</i> (<i>ныне</i> Ampurias); <i>здесь высадился Сципион во время II Пунической войны</i> <i>L, Mela, Sil</i>."],
  ["emporium", "<big>emporium</big>\n\n<b>emporium, &#299;</b> <i>n</i> (<i>греч.</i>)\nторговая площадь, рынок, <i>тж.</i> торговый город <i>C, L, Mela</i>."],
  ["emporos", "<big>emporos</big>\n\n<b>emporos (-us), &#299;</b> <i>m</i> (<i>греч.</i>; <i>лат.</i> mercator)\nкупец, коммерсант <i>Pl, Aus</i>."],
  ["emprosthotonia", "<big>emprosthotonia</big>\n\n<b>emprosthotonia, ae</b> <i>f</i> (<i>греч.</i>; <i>лат.</i> pronus raptus)\nэмпростотония, <i>тоническая судорога, сводящая члены вперёд</i> <i>CA</i>."],
  ["empsim", "<big>empsim</big>\n\n<b>&#275;mpsim</b> <i>арх. Pl</i> (= emerem) <i>conjct. к</i>\nemo."],
  ["empticius", "<big>empticius</big>\n\n<b>&#275;mpt&#299;cius, a, um</b> [emo]\nпокупной <i>Vr</i>: e. an domi natus? <i>Pt</i> купленный или родившийся здесь? (<i>о рабе</i>)."],
  ["emptio", "<big>emptio</big>\n\n<b>&#275;mptio, &#333;nis</b> <i>f</i> [emo]\n<b>1)</b> купля, покупка, приобретение (mercium <i>Su</i>; agrorum <i>T</i>): tabella emptionis <i>Sen</i> купчая; mala e. <i>PJ</i> невыгодная покупка (с переплатой);\n<b>2)</b> купленный предмет (e. nova <i>PJ</i>);\n<b>3)</b> (<i>тж.</i> e. et venditio) договор о купле-продаже <i>Dig, CJ</i>."],
  ["emptito", "<big>emptito</big>\n\n<b>&#275;mptito, &#257;v&#299;, &#257;tum, &#257;re</b> [<i>frequ. к</i> emo]\n<b>1)</b> скупать, закупать <i>Col, T, PJ</i>;\n<b>2)</b> добиваться подкупом (e. honores <i>Cato</i>)."],
  ["emptor", "<big>emptor</big>\n\n<b>&#275;mptor, &#333;ris</b> <i>m</i> [emo]\nпокупатель, скупщик (bonorum <i>C</i>): e. pretiosus alicujus rei <i>H</i> покупающий что-л. дорогой ценой; e. familiae pecuniaeque <i>Su</i> фиктивный покупатель наследственной массы per aes et libram (<i>см.</i> libra), <i>т. е.</i> назначаемый завещателем душеприказчик."],
  ["emptrix", "<big>emptrix</big>\n\n<b>&#275;mptr&#299;x, &#299;cis</b> <i>f</i> [emptor]\nпокупательница <i>Dig, CJ</i>."],
  ["empturio", "<big>empturio</big>\n\n<b>&#275;mpturio, —, —, &#299;re</b> [<i>desiderat. к</i> emo]\nстремиться покупать, скупать <i>Vr</i>."],
  ["emptus", "<big>emptus</big>\n\n<b>&#275;mptus, a, um</b> <i>part. pf. к</i>\nemo."],
  ["Empulum", "<big>Empulum</big>\n\n<b>Empulum, &#299;</b> <i>n</i>\n<i>городок в Латии, близ Тибура</i> <i>L</i>."],
  ["empyrius", "<big>empyrius</big>\n\n<b>empyrius, a, um</b> (<i>греч.</i>)\nогненный (mundi sublimitates <i>Aug</i>)."],
  ["emugio", "<big>emugio</big>\n\n<b>&#275;–m&#363;gio, —, —, &#299;re</b>\nпрореветь, <i>перен.</i> выкрикнуть (omnia <i>Q</i>)."],
  ["emulceo", "<big>emulceo</big>\n\n<b>&#275;–mulceo, —, —, &#275;re</b>\nсмягчать <i>Ambr</i>."],
  ["emulgeo", "<big>emulgeo</big>\n\n<b>&#275;–mulgeo, muls&#299;, mulsum, &#275;re</b>\nдоить, выдаивать (emulgendum est exiguum lactis <i>Col</i>); <i>перен.</i> отводить воду (emulsa palus <i>Ctl</i>)."],
  ["emulsus", "<big>emulsus</big>\n\n<b>&#275;mulsus, a, um</b> <i>part. pf. к</i>\nemulgeo."],
  ["emunctio", "<big>emunctio</big>\n\n<b>&#275;m&#363;nctio, &#333;nis</b> <i>f</i> [emungo]\nсморкание <i>Q</i>."],
  ["emunctorium", "<big>emunctorium</big>\n\n<b>&#275;m&#363;nct&#333;rium, &#299;</b> <i>n</i> [emungo]\nщипцы для снятия нагара <i>Vlg</i>."],
  ["emunctus", "<big>emunctus</big>\n\n<b>&#275;m&#363;nctus, a, um</b> <i>part. pf. к</i>\nemungo."],
  ["emundatio", "<big>emundatio</big>\n\n<b>&#275;mund&#257;tio, &#333;nis</b> <i>f</i> [emundo]\nочищение <i>Eccl</i>."],
  ["emundo", "<big>emundo</big>\n\n<b>&#275;–mundo, &#257;v&#299;, &#257;tum, &#257;re</b>\nочищать, чистить (ovilia <i>Col</i>); вычищать, убирать (faeces <i>Col</i>)."],
  ["emungo", "<big>emungo</big>\n\n<b>&#275;–mungo, m&#363;nx&#299;, m&#363;nctum, ere</b>\n<b>1)</b> высморкать: e. se <i>rhH</i> <i>или</i> emungi <i>J</i> высморкаться; homo emunctus <i>Q</i> <i>или</i> home emunctae naris <i>погов. Ph, H</i> тонкий человек, умница;\n<b>2)</b> &laquo;утереть нос&raquo;, обмануть, надуть (aliquem <i>Pl</i>): e. aliquem argento <i>Ter</i> выманить деньги у кого-л."],
  ["emunio", "<big>emunio</big>\n\n<b>&#275;–m&#363;nio, &#299;v&#299; (i&#299;), &#299;tum, &#299;re</b>\n<b>1)</b> сильно укреплять (locus emunitus <i>L</i>);\n<b>2)</b> ограждать, защищать (vites ab injuria pecoris <i>Col</i>);\n<b>3)</b> делать удобопроходимым: e. silvam (paludes) <i>T</i> проложить дорогу через лес (болота);\n<b>4)</b> закалять (animum adversus metum <i>Sen</i>);\n<b>5)</b> убирать, устилать (toros ostro <i>St</i>)."],
  ["emunxti", "<big>emunxti</big>\n\n<b>&#275;m&#363;nxt&#299;</b>\n<i>Pl</i> = emunxisti (<i>pf. к</i> emungo)."],
  ["emusco", "<big>emusco</big>\n\n<b>&#275;musco, —, —, &#257;re</b> [ex + muscus]\nочищать от мха (oleas <i>Col</i>)."],
  ["emussitatus", "<big>emussitatus</big>\n\n<b>&#275;mussit&#257;tus</b> <i>Pl v. l.</i>\n= amussitatus."],
  ["emutatio", "<big>emutatio</big>\n\n<b>&#275;m&#363;t&#257;tio, &#333;nis</b> <i>f</i> [emuto]\nизменение, перемена <i>Q</i>."],
  ["emuto", "<big>emuto</big>\n\n<b>&#275;–m&#363;to, (&#257;v&#299;), &#257;tum, &#257;re</b>\nизменять (aliquid aliqua re <i>Man</i>): e. in perversum <i>Q</i> извращать, искажать."],
  ["en", "<big>en</big>\n\n<b>&#275;n</b> <i>interj.</i>\n<b>1)</b> <i>cum <i>nom.</i> et</i> <i>acc.</i> вот (en aras <i>V</i>; en causa <i>и</i> en causam <i>C</i>): ellum <i>Pl, Ter</i> = en illum; ellam <i>Pl, Ter</i> = en illam;\n<b>2)</b> <i>в вопросе</i> послушай (en, quid agis? <i>Pers</i>); неужели, ли, разве (en unquam aspiciam te? <i>Pl</i>);\n<b>3)</b> <i>при</i> <i>imper.</i> ну же (en accipe <i>V</i>): en age, rumpe moras <i>V</i> да ну же, не медли."],
  ["Enaesimus", "<big>Enaesimus</big>\n\n<b>Enaesimus, &#299;</b> <i>m</i>\nЭнесим, <i>имя воина</i> <i>O</i>."],
  ["enallage", "<big>enallage</big>\n\n<b>enallag&#275;, &#275;s</b> <i>f</i> (<i>греч.</i>) <i>ритор.</i>\nзамена одного слова другим <i>или</i> одной формы другой (<i>напр.</i> tenet ille vestras, Eure, domos <i>V</i>, <i>где в обращении к одному лицу местоимение взято во мн. числе</i>)."],
  ["enargia", "<big>enargia</big>\n\n<b>enarg&#299;a, ae</b> <i>f</i> (<i>греч.</i>) <i>ритор.</i>\nяркое описание <i>Is</i>."],
  ["enarmonius", "<big>enarmonius</big>\n\n<b>enarmonius (enharmonius), a, um</b> (<i>греч.</i>)\nэнгармонический (genus melodiae musicae <i>Macr</i>)."],
  ["enarrabilis", "<big>enarrabilis</big>\n\n<b>&#275;n&#257;rr&#257;bilis, e</b> [enarro]\nмогущий быть рассказанным, описуемый (non e. <i>V, Pers</i>; vix e. <i>Sen</i>)."],
  ["enarrate", "<big>enarrate</big>\n\n<b>&#275;n&#257;rr&#257;t&#275;</b> [enarro]\nобстоятельно, с изложением подробностей (enarratius scribere <i>AG</i>)."],
  ["enarratio", "<big>enarratio</big>\n\n<b>&#275;n&#257;rr&#257;tio, &#333;nis</b> <i>f</i> [enarro]\nподробное изложение, объяснение <i>Q</i>: e. syllabarum <i>Sen</i> скандирование."],
  ["enarrator", "<big>enarrator</big>\n\n<b>&#275;n&#257;rr&#257;tor, &#333;ris</b> <i>m</i> [enarro]\nподробно излагающий, объясняющий, толкователь (Sallustii <i>AG</i>)."],
  ["enarro", "<big>enarro</big>\n\n<b>&#275;–n&#257;rro, &#257;v&#299;, &#257;tum, &#257;re</b>\n<b>1)</b> подробно рассказывать, излагать (rem ordine <i>Ter</i>);\n<b>2)</b> объяснять (versum Plauti <i>AG</i>; operis singulas partes <i>PM</i>); истолковывать (somnium alicui <i>C</i>)."],
  ["enascor", "<big>enascor</big>\n\n<b>&#275;–n&#257;scor, n&#257;tus sum, n&#257;sc&#299;</b> <i>depon.</i>\n<b>1)</b> вырастать, произрастать (cornua enascuntur cutibus <i>PM</i>): suboles enata ex arbore <i>Col</i> пущенные деревом побеги;\n<b>2)</b> образовываться, возникать, появляться (quasi enata subito classis <i>Fl</i>): enata dies <i>SenT</i> наступивший день, рассвет; Roma casis enata <i>Man</i> Рим, возникший из (на месте прежних) хижин."],
  ["enato", "<big>enato</big>\n\n<b>&#275;–nato, &#257;v&#299;, &#257;tum, &#257;re</b>\n<b>1)</b> выплывать (cum sarcinis <i>Sen</i>); спасаться вплавь (ex naufragio <i>rhH</i>);\n<b>2)</b> выпутаться, вывернуться <i>C, Pt</i>."],
  ["enatus", "<big>enatus</big>\n\n<b>&#275;n&#257;tus, a, um</b> <i>part. pf. к</i>\nenascor."],
  ["enavigo", "<big>enavigo</big>\n\n<b>&#275;–n&#257;vigo, &#257;v&#299;, &#257;tum, &#257;re</b>\n<b>1)</b> отплывать, отправляться на корабле (e. Rhodum <i>Su</i>; de Euboea insula <i>Ap</i>);\n<b>2)</b> объезжать на корабле, плавать, проплывать (Indum <i>PM</i>): unda omnibus enaviganda <i>H</i> река, которую всем суждено переплыть (<i>т. е.</i> Стикс)."],
  ["encaenia", "<big>encaenia</big>\n\n<b>encaenia, &#333;rum</b> <i>n</i> (<i>греч.</i>)\nпраздник освящении <i>Aug</i>."],
  ["encaenio", "<big>encaenio</big>\n\n<b>encaenio, —, —, &#257;re</b>\nосвящать <i>Aug</i>."],
  ["encanthis", "<big>encanthis</big>\n\n<b>encanthis, idis</b> <i>f</i> (<i>греч.</i>)\nопухоль в углу глаза <i>CC</i>."],
  ["encarpa", "<big>encarpa</big>\n\n<b>encarpa, &#333;rum</b> <i>n</i> (<i>греч.</i>) <i>архит.</i>\nорнамент в виде плодов, фестон, гирлянда <i>Vtr</i>."],
  ["encathisma", "<big>encathisma</big>\n\n<b>encathisma, atis</b> <i>n</i> (<i>греч.</i>)\n<b>1)</b> (<i>лат.</i> sessio) сидячая ванна <i>CA</i>;\n<b>2)</b> тёплая примочка, припарка <i>CA</i>."],
  ["encaustica", "<big>encaustica</big>\n\n<b>encaustica, ae</b> <i>f</i> [encausticus]\n(<i>sc.</i> ars) живопись горячими восковыми красками, энкаустика <i>PM</i>."],
  ["encausticus", "<big>encausticus</big>\n\n<b>encausticus, a, um</b> <i>PM</i>\n= encaustus."],
  ["encaustum", "<big>encaustum</big>\n\n<b>encaustum, &#299;</b> <i>n</i> (<i>греч.</i>)\n<b>1)</b> энкаустическая картина <i>PM</i>;\n<b>2)</b> (<i>тж.</i> encautum <i>и</i> e. sacrum) пурпурные чернила (<i>которыми подписывались императоры</i>) <i>CTh, CJ</i>;\n<b>3)</b> <i>PM</i> = encaustica."],
  ["encaustus", "<big>encaustus</big>\n\n<b>encaustus, a, um</b> (<i>греч.</i>)\nэнкаустический, изображённый энкаустикой (Phaethon <i>M</i>)."],
  ["encautarium", "<big>encautarium</big>\n\n<b>encaut&#257;rium, &#299;</b> <i>n</i> [encautum]\nгосударственный архив <i>CTh</i>."],
  ["encautum", "<big>encautum</big>\n\n<b>encautum</b> — <i>см.</i>\nencaustum 2."],
  ["Enceladus", "<big>Enceladus</big>\n\n<b>Enceladus, &#299;</b> <i>m</i>\nЭнкелад, <i>один из сторуких Гигантов, сын Тартара и Геи, убитый молнией Юпитера и погребённый под Этной</i> <i>V, O, St</i>."],
  ["enchiridion", "<big>enchiridion</big>\n\n<b>ench&#299;ridion, &#299;</b> <i>n</i> (<i>греч.</i>)\nучебник, руководство <i>Dig</i>."],
  ["enchorius", "<big>enchorius</big>\n\n<b>ench&#333;rius, a, um</b> (<i>греч.</i>)\nместный, туземный (aves <i>Is</i>)."],
  ["enchymos", "<big>enchymos</big>\n\n<b>ench&#563;mos</b> <i>v. l.</i>\n= encymos."],
  ["enchytus", "<big>enchytus</big>\n\n<b>enchytus, &#299;</b> <i>m</i> (<i>греч.</i>)\nоладья, блин <i>Cato</i>."],
  ["enclima", "<big>enclima</big>\n\n<b>enclima, atis</b> <i>n</i> (<i>греч.</i>)\nнаклон экватора к горизонту, склонение полюса <i>Vtr</i>."],
  ["encliticum", "<big>encliticum</big>\n\n<b>encliticum, &#299;</b> <i>n</i> (<i>греч.</i>) <i>грам.</i>\nэнклитика, <i>слово, произносимое безударно и слитно с предыдущим</i> (<i>напр.</i> <i>-que</i>)."],
  ["encolpias", "<big>encolpias</big>\n\n<b>encolpi&#257;s, ae</b> <i>m</i> (<i>греч.</i>)\nэнколпий, ветер, образующийся в морском заливе <i>Ap</i>."],
  ["encomma", "<big>encomma</big>\n\n<b>encomma</b> <i>v. l.</i>\n= incomma."],
  ["encrinomenos", "<big>encrinomenos</big>\n\n<b>encr&#299;nomenos, &#299;</b> <i>m</i> (<i>греч.</i>)\nизбранный, допущенный (<i>в число атлетов</i>) (<i>название статуи атлета</i>) <i>PM</i>."],
  ["encyclios", "<big>encyclios</big>\n\n<b>encyclios, on</b> (<i>греч.</i>)\nкруговой, совокупный, систематический (encyclios omnium doctrinarum disciplina <i>Vtr</i>)."],
  ["encymos", "<big>encymos</big>\n\n<b>enc&#563;mos, on</b> (<i>греч.</i>)\nсочный <i>PM</i>."],
  ["encytus", "<big>encytus</big>\n\n<b>encytus, &#299;</b> <i>m</i> <i>v. l.</i>\n= enchytus."],
  ["endo", "<big>endo</big>\n\n<b>endo</b> <i>praep.</i> <i>арх.</i>\n(= in): e. mari <i>Lcr</i> в море."],
  ["endoploro", "<big>endoploro</big>\n\n<b>endo–pl&#333;ro</b> <i>арх. LXIIT ap. C</i>\n= imploro."],
  ["endovolo", "<big>endovolo</big>\n\n<b>endo–volo</b> <i>Enn ap. Macr</i>\n= involo."],
  ["endromidatus", "<big>endromidatus</big>\n\n<b>endromid&#257;tus, a, um</b> [endromis]\nодетый в эндромиду <i>Sid</i>."],
  ["endromis", "<big>endromis</big>\n\n<b>endromis, idis</b> (<i>acc.</i> idem, ida <i>или</i> idam; <i>acc. pl.</i> idas) <i>f</i> (<i>греч.</i>)\nэндромида, плащ из шерстяной ворсистой ткани, тёплый халат <i>M, J</i>."],
  ["Endymion", "<big>Endymion</big>\n\n<b>Endymi&#333;n, &#333;nis</b> <i>m</i>\nЭндимион, <i>прекрасный юноша, любимец Селены, погружённый ею в вечный сон на горе Латм</i> (<i>Кария</i>) <i>C, Prp, O etc.</i>"],
  ["Endymioneus", "<big>Endymioneus</big>\n\n<b>Endymi&#333;neus, a, um</b>\nэндимионов (sopores <i>Aus</i>)."],
  ["enecatrix", "<big>enecatrix</big>\n\n<b>&#275;nec&#257;tr&#299;x, &#299;cis</b> <i>f</i> [eneco]\nумерщвляющая, убийца <i>Tert</i>."],
  ["enecatus", "<big>enecatus</big>\n\n<b>&#275;nec&#257;tus</b>\n<i>PM</i> = enectus (<i>part. pf. к</i> eneco)."],
  ["eneco", "<big>eneco</big>\n\n<b>&#275;–neco (&#275;nico), necu&#299;, nectum, &#257;re</b>\n<b>1)</b> убивать, душить (avem <i>Vr</i>; anguem <i>Pt</i>); морить (enectus veneno <i>Su</i>);\n<b>2)</b> глушить, заглушать (plantam <i>PM</i>);\n<b>3)</b> томить (siti enectus Tantalus <i>LM ap. C</i>); мучить (enectus frigore <i>L</i>); истощать (provinciam <i>C</i>); донимать, изводить (aliquem rogitando <i>Ter</i>): enecas (enicas) <i>Pl, Ter</i> ты мучаешь меня (<i>т. е.</i> говори, не томи)."],
  ["energema", "<big>energema</big>\n\n<b>energ&#275;ma, atis</b> <i>n</i> (<i>греч.</i>)\nвоздействие (злого духа), <i>т. е.</i> одержимость <i>Tert</i>."],
  ["energia", "<big>energia</big>\n\n<b>energ&#299;a, ae</b> <i>f</i> (<i>греч.</i>)\nэнергия, активность <i>Eccl</i>."],
  ["energumenos", "<big>energumenos</big>\n\n<b>energ&#363;menos, &#299;</b> <i>m</i> [energema]\nодержимый бесами <i>Eccl</i>."],
  ["enervatio", "<big>enervatio</big>\n\n<b>&#275;nerv&#257;tio, &#333;nis</b> <i>f</i> [enervo]\nрасслабленность, истома (voluptatis <i>Eccl</i>)."],
  ["enervatus", "<big>enervatus</big>\n\n<b>&#275;nerv&#257;tus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> enervo;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> изнеженный, слабый (homo <i>C, L etc.</i>);\n<b>2)</b> тусклый, бесцветный (sententia <i>C</i>);\n<b>3)</b> немощный, вялый (orator <i>C</i>);\n<b>4)</b> кастрированный <i>AV</i>."],
  ["enervis", "<big>enervis</big>\n\n<b>&#275;–nervis, e</b>\n<b>1)</b> обессиленный, ослабленный, изнеженный (corpus <i>Pt</i>; vita <i>VM</i>);\n<b>2)</b> обессиливающий, расслабляющий (spectaculum <i>PJ</i>)."],
  ["enervo", "<big>enervo</big>\n\n<b>&#275;nervo, &#257;v&#299;, &#257;tum, &#257;re</b> [e + nervus]\n<b>1)</b> вынимать сухожилия, извлекать жилы (poplites e. securi <i>Ap</i>);\n<b>2)</b> кастрировать (aliquem <i>Cld</i>);\n<b>3)</b> ослаблять, обессиливать (corpora animosque <i>L</i>; enervari et cadere <i>Pt</i>); парализовать (vires alicujus <i>H</i>; bellum <i>C</i>)."],
  ["enervus", "<big>enervus</big>\n\n<b>&#275;nervus, a, um</b> <i>Ap</i>\n= enervis."],
  ["Engonasin", "<big>Engonasin</big>\n\n<b>Engonasi(n)</b> <i>indecl.</i> (<i>греч.</i> &laquo;на коленях&raquo;, коленопреклонённый; <i>лат.</i> Nixus <i>или</i> Ingeniculatus)\n<i>название созвездия Геркулеса</i> <i>C, Man, Vtr</i>."],
  ["Enguinus 1", "<big>Enguinus</big>\n\n<b>I Engu&#299;nus, a, um</b> [Enguion]\nэнгийский (civitas <i>C</i>)."],
  ["Enguinus 2", "<big>Enguinus</big>\n\n<b>II Engu&#299;nus, &#299;</b> <i>m</i>\nжитель города Энгий <i>C, PM</i>."],
  ["Enguion", "<big>Enguion</big>\n\n<b>Enguion</b> <i>и</i> <b>Engyon, &#299;</b> <i>n</i>\nЭнгий, <i>город в сев. Сицилии, к юго-вост. от Гимеры</i> <i>Sil</i>."],
  ["enhaemon", "<big>enhaemon</big>\n\n<b>enhaemon, &#299;</b> <i>n</i> (<i>греч.</i>)\nкровоостанавливающее средство <i>PM</i>."],
  ["enharm-", "<big>enharm-</big>\n\n<b>enharm-</b> <i>v. l.</i>\n= enarm-."],
  ["enicasso", "<big>enicasso</big>\n\n<b>&#275;nicasso</b>\n<i>Pl</i> (= enecavero) <i>fut. II к</i> eneco."],
  ["enico", "<big>enico</big>\n\n<b>&#275;–nico, u&#299; (&#257;v&#299;), —, &#257;re</b> <i>v. l.</i>\n= eneco."],
  ["enim 1", "<big>enim</big>\n\n<b>I enim</b> <i>conj.</i> [e <i>demonstr.</i> + nam]\n<b>1)</b> ибо, ведь (corpuscula, ita e. atomi appellantur <i>C</i>);\n<b>2)</b> же: quid e. (censes)? <i>Pl, H, C etc.</i> так как же?, что же (ты полагаешь)?"],
  ["enim 2", "<big>enim</big>\n\n<b>II enim</b> <i>adv.</i>\nв самом деле, действительно, поистине, конечно (at e. <i>Pl, Ter, C etc.</i>; sed e. <i>C, V etc.</i>)."],
  ["enimvero", "<big>enimvero</big>\n\n<b>enim–v&#275;r&#333;</b> (<i>тж. раздельно</i>)\n<b>1)</b> конечно, действительно, в самом деле: ita e. <i>Pl, Ter</i> да, конечно; vero e. <i>C</i> в том-то и дело;\n<b>2)</b> мало того: e. non tantum..., sed <i>T</i> мало того, не только..., но и;\n<b>3)</b> право же: e. ferendum hoc quidem non est <i>C</i> ну этого допустить нельзя;\n<b>4)</b> в действительности же: ille e. negat <i>C</i> факт тот, что он ответил отказом; e. non fuit <i>Pl</i> да нет же, не был он (свободным человеком)."],
  ["Enipeus", "<big>Enipeus</big>\n\n<b>En&#299;pe&#865;us, e&#299;</b> <i>и</i> <b>eos</b> <i>m</i>\nЭнипей:\n<b>1)</b> <i>приток реки Пенея</i> (<i>Фессалия</i>) <i>V, Lcn</i>;\n<b>2)</b> <i>бог этой реки</i> <i>Prp, O</i>;\n<b>3)</b> <i>речка в Македонии</i> <i>L</i>."],
  ["enise", "<big>enise</big>\n\n<b>&#275;n&#299;s&#275;</b> <i>v. l.</i>\n= enixe."],
  ["enisus", "<big>enisus</big>\n\n<b>&#275;n&#299;sus</b> <i>v. l.</i>\n= enixus."],
  ["eniteo", "<big>eniteo</big>\n\n<b>&#275;–niteo, u&#299;, —, &#275;re</b>\n<b>1)</b> блистать, сиять, представать во всём блеске (enitet campus <i>V</i>): decus enitet ore <i>V</i> лицо (Энея) сияет красотой;\n<b>2)</b> проясняться (caelum enitet <i>AG</i>);\n<b>3)</b> выдвинуться, отличиться (in bello <i>C, L, PJ</i>; in philosophia <i>Vulc</i>)."],
  ["enitesco", "<big>enitesco</big>\n\n<b>&#275;–nit&#275;sco, —, —, ere</b> [<i>inchoat. к</i> eniteo]\n<b>1)</b> заблистать, засиять (oculi enitescunt <i>Q</i>; virtus enitescit <i>Sl</i>);\n<b>2)</b> отличиться (studiis honestis <i>T</i>; facundia <i>Q</i>)."],
  ["enitor", "<big>enitor</big>\n\n<b>&#275;–n&#299;tor, n&#299;sus (n&#299;xus) sum, n&#299;t&#299;</b> <i>depon.</i>\n<b>1)</b> пробиваться, выбираться (per angustias et ingentem multitudinem <i>T</i>);\n<b>2)</b> взбираться, добираться, (вс)карабкаться (in verticem montis <i>QC</i>; in altiora <i>T</i>; aggerem <i>T</i>);\n<b>3)</b> силиться, добиваться, стараться изо всех сил (ut illud fiat <i>C</i>; pro aliquo <i>Ter</i>; in re aliqua <i>C</i>): e. ad dicendum <i>C</i> стремиться овладеть искусством красноречия; quod quidem certe enitar <i>C</i> уж здесь я, конечно, постараюсь;\n<b>4)</b> рожать (e. partus plures <i>L</i>; duos pueros <i>Just</i>): in luco Martis enixi (<i>pass.</i>) <i>Just</i> (Рем и Ромул), рождённые в Марсовой роще."],
  ["enixe", "<big>enixe</big>\n\n<b>&#275;n&#299;x&#275;</b> [enixus I]\nстарательно, ревностно, усердно (aliquem juvare <i>Cs</i>)."],
  ["enixus 1", "<big>enixus</big>\n\n<b>I &#275;n&#299;xus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> enitor;\n<b>2.</b> <i>adj.</i> старательный, ревностный (studium <i>L</i>)."],
  ["enixus 2", "<big>enixus</big>\n\n<b>II &#275;n&#299;xus, &#363;s</b> <i>m</i> [enitor 4]\nроды, рождение <i>PM</i>."],
  ["Enna", "<big>Enna</big>\n\n<b>Enna</b> <i>v. l.</i>\n= Henna."],
  ["ennaeteris", "<big>ennaeteris</big>\n\n<b>enna&euml;t&#275;ris (enneaet&#275;ris), idis</b> <i>f</i> (<i>греч.</i>)\nдевятилетие <i>Cens</i>."],
  ["enneas", "<big>enneas</big>\n\n<b>enneas, adis</b> <i>f</i> (<i>греч.</i>; <i>лат.</i> numerus nonarius)\nэннеада, число девять <i>Cens</i>."],
  ["Ennianista", "<big>Ennianista</big>\n\n<b>Enni&#257;nista, ae</b> <i>m</i>\nподражатель Энния <i>AG</i>."],
  ["Ennianus", "<big>Ennianus</big>\n\n<b>Enni&#257;nus, a, um</b> [Ennius]\nэнниев <i>Sen, Q, AG</i>."],
  ["Ennius", "<big>Ennius</big>\n\n<b>Ennius, &#299;</b> <i>m</i>\n(Quintus) Энний (Квинт), <i>родом из Rudiae (Калабрия), создатель римск. эпоса и родоначальник латинской поэзии, автор Annales, трагедий, комедий и пр. (239-169 гг. до н. э.</i>) <i>Ter, C etc.</i>"],
  ["Ennomos", "<big>Ennomos</big>\n\n<b>Ennomos, &#299;</b> <i>m</i>\nЭнном, царь Миссии, <i>убитый Улиссом</i> <i>O</i>."],
  ["Ennosigaeus", "<big>Ennosigaeus</big>\n\n<b>Ennosigaeus, &#299;</b> <i>m</i> (<i>греч.</i> &laquo;колебатель земли&raquo;)\n<i>эпитет Нептуна</i> <i>J</i>."],
  ["eno", "<big>eno</big>\n\n<b>&#275;–no, &#257;v&#299;, &#257;tum, &#257;re</b>\n<b>1)</b> выплывать, уплывать (in Erythraeam <i>L</i>); спасаться вплавь (in terram <i>L</i>); <i>поэт.</i> улетать (praepetibus pennis ad Arctos <i>V</i>);\n<b>2)</b> проплывать, переплывать (aliquem locum <i>VF, Sil</i>)."],
  ["enodate", "<big>enodate</big>\n\n<b>&#275;n&#333;d&#257;t&#275;</b> [enodatus]\nясно, обстоятельно (narrare <i>C</i>)."],
  ["enodatio", "<big>enodatio</big>\n\n<b>&#275;n&#333;d&#257;tio, &#333;nis</b> <i>f</i> [enodo]\nраспутывание, <i>перен.</i> объяснение, раскрытие значения (e. nominum <i>C</i>)."],
  ["enodator", "<big>enodator</big>\n\n<b>&#275;n&#333;d&#257;tor, &#333;ris</b> <i>m</i> [enodo]\nобъясняющий, истолкователь (vocis <i>Tert</i>)."],
  ["enodatus", "<big>enodatus</big>\n\n<b>&#275;n&#333;d&#257;tus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> enodo;\n<b>2.</b> <i>adj.</i> ясный, понятный (praeceptum <i>C</i>; enodatissimus sermo <i>Aug</i>)."],
  ["enodis", "<big>enodis</big>\n\n<b>&#275;n&#333;dis, e</b> [e + nodus]\n<b>1)</b> не имеющий узлов, без сучьев, <i>т. е.</i> гладкий (truncus <i>V</i>; arbor <i>O, Vtr, PM, Cld</i>; calamus <i>CC</i>);\n<b>2)</b> гибкий (artus <i>Cld</i>);\n<b>3)</b> плавный, лёгкий (elegi <i>PJ</i>)."],
  ["enodo", "<big>enodo</big>\n\n<b>&#275;n&#333;do, &#257;v&#299;, &#257;tum, &#257;re</b> [e + nodus]\n<b>1)</b> развязывать узлы, распутывать (laqueos <i>AG</i>); удалять узлы, сглаживать (vitem <i>Cato</i>): e. arcum <i>Ap</i> снимать с лука тетиву (завязанную узлом);\n<b>2)</b> объяснять, толковать (fata <i>Ap</i>; nomina <i>C</i>)."],
  ["enormis", "<big>enormis</big>\n\n<b>&#275;n&#333;rmis, e</b> [e + norma]\n<b>1)</b> неправильный (versus <i>AG</i>; vicus <i>T</i>); плохо сшитый (toga <i>Q</i>);\n<b>2)</b> чрезмерный, непомерный, огромный (corpus <i>Su</i>; gladius <i>T</i>; loquacitas <i>PJ, Pt, Sen</i>)."],
  ["enormitas", "<big>enormitas</big>\n\n<b>&#275;n&#333;rmit&#257;s, &#257;tis</b> <i>f</i> [enormis]\n<b>1)</b> неправильность (saxorum rudium <i>Q</i>);\n<b>2)</b> чрезмерность, огромность (pedum <i>Sen</i>; donationis <i>CJ</i>)."],
  ["enormiter", "<big>enormiter</big>\n\n<b>&#275n&#333;rmiter</b> [enormis]\n<b>1)</b> неправильно <i>Sen, PM</i>;\n<b>2)</b> непомерно, чрезмерно <i>Vlg</i>."],
  ["enotesco", "<big>enotesco</big>\n\n<b>&#275;–n&#333;t&#275;sco, tu&#299;, —, ere</b>\nстановиться известным, приобретать гласность <i>T, PJ, Su, Sen</i>."],
  ["enoto", "<big>enoto</big>\n\n<b>&#275;–noto, &#257;v&#299;, &#257;tum, &#257;re</b>\nотмечать, записывать (meditabar aliquid enotabamque <i>PJ</i>); чертить, вычерчивать (figuras <i>PJ</i>): &laquo;cui&raquo; tribus litteris enotamus <i>Q</i> (слово) &laquo;cui&raquo; мы пишем тремя буквами."],
  ["ens", "<big>ens</big>\n\n<b>&#275;ns, entis</b> <i>n</i> [<i>part. praes. к</i> sum]\n<b>1)</b> существующее, сущее <i>Cs</i>;\n<b>2)</b> существо <i>или</i> вещь <i>Q</i>."],
  ["ensiculus", "<big>ensiculus</big>\n\n<b>&#275;nsiculus, &#299;</b> <i>m</i> [<i>demin. к</i> ensis]\nкороткий меч, кинжал <i>Pl</i>."],
  ["ensifer", "<big>ensifer</big>\n\n<b>&#275;nsifer, fera, ferum</b> [ensis + fero]\nмеченосный, носящий меч (Orion <i>O, Lcn</i>; catervae <i>St</i>)."],
  ["ensiger", "<big>ensiger</big>\n\n<b>&#275;nsiger, gera, gerum</b> <i>O</i>\n= ensifer."],
  ["ensis", "<big>ensis</big>\n\n<b>&#275;sis, is</b> <i>m</i>\n<b>1)</b> меч (<i>обоюдоострый, для рубки</i>): e. civilis <i>Sen</i> меч палача (<i>для гражданских казней</i>); lateri accomodare ensem <i>V</i> прицепить сбоку меч || <i>поэт.</i> война, резня <i>Sil, St, Lcn</i>;\n<b>2)</b> созвездие Ориона <i>VF</i>."],
  ["entelechia", "<big>entelechia</big>\n\n<b>entelech&#299;a, ae</b> <i>f</i> (<i>греч.</i>) <i>филос.</i>\nэнтелехия, внутренняя целеустремленность, осуществляемая действенностью цель, законченная действительность (<i>по Аристотелю</i>) <i>C, Tert</i>."],
  ["Entella", "<big>Entella</big>\n\n<b>Entella, ae</b> <i>f</i>\nЭнтелла, <i>город в зап. Сицилии на реке Кримиссе, к сев.-вост. от Селинунта</i> <i>Sil</i>."],
  ["Entellinus 1", "<big>Entellinus</big>\n\n<b>I Entell&#299;nus, a, um</b> <i>C adj. к</i>\nEntella."],
  ["Entellinus 2", "<big>Entellinus</big>\n\n<b>II Entell&#299;nus, &#299;</b> <i>m</i>\nжитель города Энтеллы <i>C</i>."],
  ["Entellus", "<big>Entellus</big>\n\n<b>Entellus, &#299;</b> <i>m</i>\nЭнтелл, <i>троянец, основатель города Энтеллы</i> <i>V</i>."],
  ["entheatus", "<big>entheatus</big>\n\n<b>enthe&#257;tus, a, um</b> <i>M</i>\n= entheus 1."],
  ["entheca", "<big>entheca</big>\n\n<b>enth&#275;ca, ae</b> <i>f</i> (<i>греч.</i>)\n<b>1)</b> денежные сбережения <i>Aug</i>;\n<b>2)</b> (<i>лат.</i> dos) имущество, утварь, инвентарь (e. praedii <i>Dig</i>);\n<b>3)</b> общественный склад, хранилище <i>CTh</i>."],
  ["entheus", "<big>entheus</big>\n\n<b>entheus, a, um</b> (<i>греч.</i>)\n<b>1)</b> воодушевлённый, мечтательно настроенный, вдохновенный (turba <i>M</i>); исступлённый (cursus, <i>sc.</i> maenadis <i>SenT</i>);\n<b>2)</b> вдохновляющий: enthea Mater <i>M</i> = Cybele."],
  ["enthymema", "<big>enthymema</big>\n\n<b>enth&#563;m&#275;ma, atis</b> <i>n</i> (<i>греч.</i>; <i>лат.</i> commentum <i>или</i> commentatio)\nэнтимема:\n<b>1)</b> сжатое, выразительное суждение <i>LM, Sen, J</i>;\n<b>2)</b> заключение от противного <i>C, Q</i>."],
  ["enubilo", "<big>enubilo</big>\n\n<b>&#275;–n&#363;bilo, &#257;v&#299;, &#257;tum, &#257;re</b>\n<b>1)</b> прояснять, озарять, освещать (vestibula lucernis <i>Tert</i>);\n<b>2)</b> выяснять (<i>sc.</i> veritatem <i>Eccl</i>)."],
  ["enubo", "<big>enubo</big>\n\n<b>&#275;–n&#363;bo, n&#363;ps&#299;, n&#363;ptum, ere</b>\nоставить вследствие замужества свою семью, род, сословие, город и т. д.: e. e patribus <i>L</i> выйти замуж за непатриция (<i>о патрицианке</i>)."],
  ["enucleate", "<big>enucleate</big>\n\n<b>&#275;nucle&#257;t&#275;</b> [enucleatus]\nясно, просто <i>C, Aug</i>."],
  ["enucleatus", "<big>enucleatus</big>\n\n<b>&#275;nucle&#257;tus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> enucleo;\n<b>2.</b> <i>adj.</i> ясный, простой (genus dicendi <i>C</i>)."],
  ["enucleo", "<big>enucleo</big>\n\n<b>&#275;–nucleo, &#257;v&#299;, &#257;tum, &#257;re</b> [e + nucleus]\n<b>1)</b> удалять косточку, вынимать ядро (e. bacas <i>Scr</i>);\n<b>2)</b> рассматривать, объяснять (aliquid <i>C</i>); обстоятельно разбирать (haec nunc e. non ita necesse est <i>C</i>)."],
  ["enumeratio", "<big>enumeratio</big>\n\n<b>&#275;numer&#257;tio, &#333;nis</b> <i>f</i> [enumero]\n<b>1)</b> перечень, перечисление (bonorum ac malorum <i>C</i>);\n<b>2)</b> <i>ритор.</i> суммирование, подытоживание, рекапитуляция <i>rhH, C, Q</i>."],
  ["enumero", "<big>enumero</big>\n\n<b>&#275;–numero, &#257;v&#299;, &#257;tum, &#257;re</b>\n<b>1)</b> пересчитывать (peculium <i>Pl</i>);\n<b>2)</b> рассчитывать, вычислять: jamne enumerasti, quod ad te rediturum putes? <i>Ter</i> высчитал ты уже, сколько заработаешь?;\n<b>3)</b> перечислять (vulnera <i>Prp</i>; proelia <i>Nep</i>; plurima fando <i>V</i>); приводить (singulorum nomina <i>Su</i>)."],
  ["enundino", "<big>enundino</big>\n\n<b>&#275;–n&#363;ndino, —, —, &#257;re</b>\nвыторговывать, покупать (aliquid <i>Tert</i>)."],
  ["enuntiatio", "<big>enuntiatio</big>\n\n<b>&#275;n&#363;nti&#257;tio, &#333;nis</b> <i>f</i> [enuntio]\n<b>1)</b> высказывание, выражение <i>C, Q</i>; изложение (rei propositae <i>C</i>);\n<b>2)</b> <i>ритор.</i> положение, предложение (breviter comprehensa verbis e. <i>Q</i>)."],
  ["enuntiativus", "<big>enuntiativus</big>\n\n<b>&#275;n&#363;nti&#257;t&#299;vus, a, um</b> [enuntio]\nотносящийся к высказыванию, высказывающий, повествующий <i>Sen</i>."],
  ["enuntiatrix", "<big>enuntiatrix</big>\n\n<b>&#275;n&#363;nti&#257;tr&#299;x, &#299;cis</b> <i>f</i>\nвыразительница (lingua e. sensuum <i>Eccl</i>): ars e. <i>Q</i> риторика."],
  ["enuntiatum", "<big>enuntiatum</big>\n\n<b>&#275;n&#363;nti&#257;tum, &#299;</b> <i>n</i> [enuntio]\n<b>1)</b> высказывание <i>Sen</i>;\n<b>2)</b> <i>ритор.</i> положение <i>C</i>."],
  ["enuntio", "<big>enuntio</big>\n\n<b>&#275;–n&#363;ntio, &#257;v&#299;, &#257;tum, &#257;re</b>\n<b>1)</b> высказывать, выражать (aliquid per ambitum verborum <i>Su</i>; aliquid verbis <i>C</i>); произносить (litteras <i>Q</i>);\n<b>2)</b> пересказывать (consilia sociorum <i>C</i>);\n<b>3)</b> сообщать, разглашать, разбалтывать (mysteria <i>C</i>; silenda <i>L</i>)."],
  ["enuptio", "<big>enuptio</big>\n\n<b>&#275;n&#363;ptio, &#333;nis</b> <i>f</i> [enubo]\nпереход (после замужества) в другую семью, род, сословие и т. д.: e. gentis <i>L</i> выход из рода вследствие замужества."],
  ["enutrio", "<big>enutrio</big>\n\n<b>&#275;–n&#363;trio, &#299;v&#299;, &#299;tum, &#299;re</b>\nвскармливать, воспитывать (puerum <i>O</i>); взращивать (platanum <i>PM</i>); (вы)пестовать, (вз)лелеять (ingenia <i>Q</i>)."],
  ["Enyo", "<big>Enyo</big>\n\n<b>Eny&#333;, &#363;s</b> <i>f</i> (<i>греч.</i>)\n<b>1)</b> Энио, <i>т. е.</i> Беллона <i>Sil, St</i>;\n<b>2)</b> война <i>или</i> битва <i>M</i>."],
  ["eo 1", "<big>eo</big>\n\n<b>I eo, i&#299; (&#299;v&#299;), itum, &#299;re</b>\n<b>1)</b> идти, ходить, ездить, шествовать (huc atque illuc <i>L</i>; via recta <i>Sen</i>): ire pedibus <i>L</i> идти пешком; ire equis <i>L</i> <i>и</i> super equos <i>Just</i> ехать верхом; maximis itineribus ire <i>L</i> идти ускоренным маршем; ire dormitum <i>Pl</i> (cubitum <i>C</i>, in somnum <i>PM</i>) идти спать; ire novam (notam) viam <i>Prp</i> идти по новой (знакомой) дороге; sic itur ad astra <i>V</i> таков путь (восхождения) к звёздам; non qua eundum, sed qua itur <i>Sen</i> (идущие) не тем путём, каким нужно, а тем, по которому ходят (другие);\n<b>2)</b> плыть, плавать (ire navibus <i>Su</i>, puppibus <i>O</i>, cum classe <i>L</i>): (mare), per quod saepe ieras, per quod iturus eras <i>O</i> море, которое ты часто переплывал, да и собирался ещё переплывать; ire exsequias <i>Ter</i>, pompam funeris <i>O</i> следовать в похоронном шествии;\n<b>3)</b> (<i>тж.</i> ire alis <i>VF</i>) лететь, пролетать (telum it <i>O</i>);\n<b>4)</b> течь (sanguis it <i>V</i>; lacrimae eunt <i>O</i>); проходить, протекать, проноситься (eunt anni <i>O</i>);\n<b>5)</b> возноситься, подниматься (in caelum <i>Su</i>);\n<b>6)</b> переходить: ire in lacrimas <i>V</i> заплакать, разразиться слезами; ire in rixam <i>Q</i> заспорить; ire in saecula <i>PJ</i> распространяться среди будущих поколений;\n<b>7)</b> превращаться, претворяться (sanguis it in sucos <i>O</i>): ire in corpus <i>Q</i> становиться мясистым; (in) melius ire <i>C, T</i> улучшаться, поправляться;\n<b>8)</b> приступать: ire in suffragium <i>L</i> приступить к подаче голосов; ire in poenas <i>O</i> наказывать; ire in scelus <i>O</i> совершать преступление; ire in duplum <i>C</i> удвоить наказание; ire in causam praeceps <i>L</i> целиком отдаться делу; ire infitias <i>Ter</i> отпираться, отрицать; ire per disciplinas <i>Q</i> заниматься науками; ire in eosdem pedes <i>Q</i> соблюдать тот же размер (ритм); ire per leges <i>O</i> принять законы; ire per laudes <i>O</i> хвалить;\n<b>9)</b> прибегать (ad illud <i>Ter</i>): ire ad arma (saga) <i>C</i> взяться за оружие;\n<b>10)</b> происходить, совершаться: it stridor <i>V</i> стоит шум; nec miror ista sic ire <i>Sen</i> да я не удивляюсь, что дело обстоит (именно) так; sic eat <i>L</i> пусть так и будет; pugna it ad pedes <i>L</i> сражение происходит в пешем строю;\n<b>11)</b> c ad <i>или с</i> <i>supin.</i> желать, стараться, собираться (dominationem raptum ire <i>T</i>): ad eloquentiam ituri <i>Pt</i> готовящиеся овладеть искусством красноречия; ire prohibitum aliquid <i>Cato</i> стараться воспрепятствовать чему-л.; ire ultum injurias <i>Sl, L</i> стараться отомстить за обиды; ire in (ad, contra) aliquem <i>L, V, Cs</i> броситься на кого-л.; (pedibus) ire in sententiam alicujus <i>C, L</i> одобрить чьё-л. мнение (при подаче голосов в сенате); ire in alia omnia <i>C</i> быть совершенно противоположного мнения."],
  ["eo 2", "<big>eo</big>\n\n<b>II e&#333;</b> <i>adv.</i>\n<b>1.</b> [<i>из</i> <i>dat. к</i> id, <i>см.</i> is]\n<b>1)</b> туда (eo abire, pervenire <i>C etc.</i>);\n<b>2)</b> до тех пор, до того (eo rem adduxit, ut <i>C</i>): eo usque <i>или</i> usque eo <i>C etc.</i> настолько, до такой степени <i>или</i> столь долго, до тех пор; eo rerum ventum est <i>QC</i> дошло до того; eo desperationis aliquem adducere <i>Just</i> довести кого-л. до такого отчаяния; eo magnitudinis procedere <i>Sl</i> достичь такого (такой степени) величия;\n<b>3)</b> сюда, к тому же (eo accessit, ut <i>C</i>);\n<b>2.</b> [<i>abl. sg. к</i> id]\n<b>1)</b> тем (eo magis <i>C etc.</i>); с тем (намерением) (eo scripsi, ut <i>C</i>): eo illum ad te misi (ut) <i>C</i> я послал его к тебе с тем (чтобы);\n<b>2)</b> а потому, поэтому (eo, quod <i>C, Cs</i>; eo, quid <i>C etc.</i>; eo, quia <i>Pl, Ter, C etc.</i>; eo, quoniam <i>AG</i>; eo, ut <i>C</i>): eo fit <i>H</i> вот почему и получается; non eo haec dico, quin... <i>Pl</i> я говорю не потому, что(бы) не...;\n<b>3)</b> там (eo loci = in eo loco <i>C, T</i>)."],
  ["eoad", "<big>eoad</big>\n\n<b>e&#333;–ad</b> <i>adv.</i> (<i>тж. раздельно</i>)\nдо тех пор, дотоле (e..., dum <i>Ap</i>)."],
  ["eodem", "<big>eodem</big>\n\n<b>e&#333;dem</b> <i>adv.</i>\n<b>1)</b> [<i>из</i> <i>dat. к</i> idem] туда же (aliquem e. mittere <i>Cs</i>; e. pertinere <i>C, Cs</i>; e. proficisci <i>Nep</i>; reverti <i>C, Pt</i>); к этому же (e. addere <i>C</i>);\n<b>2)</b> [<i>abl. к</i> idem] там же (e. loci = in eodem loco <i>C etc.</i>)."],
  ["eopse", "<big>eopse</big>\n\n<b>e&#333;pse</b> <i>Pl</i>\n= eo ipso, <i>см.</i> ipse."],
  ["Eordaea", "<big>Eordaea</big>\n\n<b>Eordaea, ae</b> <i>f</i>\nЭордея, <i>область в Македонии</i> <i>L</i>."],
  ["Eordaei", "<big>Eordaei</big>\n\n<b>Eordae&#299;, &#333;rum</b> <i>m</i>\nжители Эордеи <i>L</i>."],
  ["Eos 1", "<big>Eos</big>\n\n<b>I &#274;&#333;s</b> <i> <i>и</i> <b>E&#333;s</b> f indecl.</i> (<i>греч.</i>; <i>лат.</i> Aurora)\n<b>1)</b> Эос, <i>богиня утренней зари</i>; заря <i>O, SenT</i>;\n<b>2)</b> восток <i>Lcn</i>."],
  ["eos 2", "<big>eos</big>\n\n<b>II e&#333;s</b> <i>acc. pl. к</i>\nis."],
  ["eous 1", "<big>eous</big>\n\n<b>I &#275;&#333;us, a, um</b> [Eos]\n<b>1)</b> утренний, ранний (Atlantides <i>V</i>);\n<b>2)</b> восточный (mare <i>Tib</i>; aquae <i>Ap</i>)."],
  ["Eous 2", "<big>Eous</big>\n\n<b>II &#274;&#333;us (E&#333;us), &#299;</b> <i>m</i>\n<b>1)</b> утренняя звезда, денница: primo Eoo <i>V</i> на рассвете;\n<b>2)</b> восток <i>O, Prp</i>;\n<b>3)</b> житель востока <i>O</i>;\n<b>4)</b> <i>имя одного из солнечных коней Феба</i> <i>O</i>."],
  ["epactae", "<big>epactae</big>\n\n<b>epactae, &#257;rum</b> <i>f</i> (<i>греч.</i>)\nвставные дни (високосных лет) <i>Is</i>."],
  ["Epaminondas", "<big>Epaminondas</big>\n\n<b>Epam&#299;n&#333;nd&#257;s, ae</b> <i>m</i>\nЭпаминонд, <i>фиванский полководец, победитель лакедемонян при Левктрах и при Мантинее</i> (<i>418-362 гг. до н. э.</i>) <i>C, Nep, L etc.</i>"],
  ["epaphaeresis", "<big>epaphaeresis</big>\n\n<b>epaphaeresis, is</b> <i>f</i> (<i>греч.</i>)\nповторное снятие, удаление (barbae <i>M</i>)."],
  ["Epaphroditus", "<big>Epaphroditus</big>\n\n<b>Epaphrod&#299;tus, &#299;</b> <i>m</i>\nЭпафродит, <i>вольноотпущенник и приближённый Нерона, господин философа Эпиктета</i> <i>Su, T</i>."],
  ["Epaphus", "<big>Epaphus</big>\n\n<b>Epaphus, &#299;</b> <i>m</i>\nЭпаф, <i>сын Юпитера и Ио, миф. основатель города Мемфиса, чтившийся в Египте под именем Аписа</i> <i>O</i>."],
  ["epar", "<big>epar</big>\n\n<b>&#275;par</b> <i>v. l.</i>\n= hepar."],
  ["epastus", "<big>epastus</big>\n\n<b>&#275;–p&#257;stus, a, um</b> [pascor]\nсъеденный (escae <i>O</i>)."],
  ["epaticus", "<big>epaticus</big>\n\n<b>&#275;paticus</b> <i>v. l.</i>\n= bepaticus."],
  ["ependytes", "<big>ependytes</big>\n\n<b>ependyt&#275;s, ae</b> <i>m</i> (<i>греч.</i>)\nверхняя одежда <i>Hier</i>."],
  ["epenthesis", "<big>epenthesis</big>\n\n<b>epenthesis, is</b> <i>f</i> (<i>греч.</i>) <i>грам.</i>\nвставка буквы <i>или</i> слога (в слово)."],
  ["Epeus", "<big>Epeus</big>\n\n<b>Ep&#275;us (-os) (Ep&#299;us), &#299;</b> <i>m</i>\nЭпей, <i>сын Панопея, построивший Троянского коня</i> <i>Vr, V, Pl</i>."],
  ["epexegesis", "<big>epexegesis</big>\n\n<b>epex&#275;g&#275;sis, is</b> (<i>acc.</i> in) <i>f</i> (<i>греч.</i>) <i>грам.</i>\nразъяснительное добавление, истолкование, приложение."],
  ["epexegeticus", "<big>epexegeticus</big>\n\n<b>epex&#275;g&#275;ticus, a, um</b> [epexegesis] <i>грам.</i>\nразъяснительный: genetivus e. (<i>лат.</i> explicativus <i>или</i> appositionalis) родительный разъяснительный, <i>напр.</i> nomen calamitatis <i>C</i> слово calamitas (<i>ср.</i> созвездие Большой Медведицы)."],
  ["ephebeum", "<big>ephebeum</big>\n\n<b>eph&#275;b&#275;um (-&#299;um), &#299;</b> <i>n</i> [ephebus]\nэфебей, <i>спортивная площадка, гимнастический плац</i> (<i>для эфебов, в палестре</i>) <i>Vtr</i>."],
  ["ephebicus", "<big>ephebicus</big>\n\n<b>eph&#275;bicus, a, um</b> [ephebus]\nэфебовский, юношеский (chlamys <i>Ap</i>)."],
  ["ephebus", "<big>ephebus</big>\n\n<b>eph&#275;bus, &#299;</b> <i>m</i> (<i>греч.</i>)\nэфеб, юноша (<i>в возрасте от 16 до 20 лет</i>) <i>Vr, C, Nep etc.</i>"],
  ["ephelis", "<big>ephelis</big>\n\n<b>eph&#275;lis, idis</b> (<i>acc. pl.</i> idas) <i>f</i> (<i>греч.</i>)\nвеснушка <i>CC</i>."],
  ["ephemerida", "<big>ephemerida</big>\n\n<b>eph&#275;merida, ae</b> <i>f</i> <i>Aug</i>\n= ephemeris."],
  ["ephemeris", "<big>ephemeris</big>\n\n<b>eph&#275;meris, idis</b> <i>f</i>\nэфемерида, книжка для заметок, журнал, дневник <i>C, Nep, O</i>."],
  ["Ephesius 1", "<big>Ephesius</big>\n\n<b>I Ephesius, a, um</b> [Ephesus]\nэфесский (Diana <i>Pl, C, L, PM</i>)."],
  ["Ephesius 2", "<big>Ephesius</big>\n\n<b>II Ephesius, &#299;</b> <i>m</i>\nжитель города Эфеса <i>C</i>."],
  ["Ephesus", "<big>Ephesus</big>\n\n<b>Ephesus (-os), &#299;</b> <i>f</i>\nЭфес, <i>приморский город в Ионии близ устья реки Каистр, против о-ва Самос, с храмом Дианы</i> (<i>главн. из 12 ионийских городов</i>) <i>Pl, L, H etc.</i>"],
  ["ephi", "<big>ephi</big>\n\n<b>ephi</b> <i>n indecl.</i>\nэфа, <i>древнееврейская мера ёмкости и сыпучих тел</i> (<i>предпол. ок. 30 литров</i>) <i>Vlg</i>."],
  ["ephippiatus", "<big>ephippiatus</big>\n\n<b>ephippi&#257;tus, a, um</b> [ephippium]\nпользующийся чепраком, <i>т. е.</i> ездящий на осёдланной лошади (eques e. <i>Cs</i>)."],
  ["ephippium", "<big>ephippium</big>\n\n<b>ephippium, &#299;</b> <i>n</i> (<i>греч.</i>)\nчепрак, попона (<i>употреблявшаяся вместо седла</i>) <i>Vr, C, Cs, Ap</i>: optat ephippia bos piger, optat arare caballus <i>H</i> ленивый вол хочет ходить под седлом, а конь — пахать (<i>т. е.</i> никто не доволен своей судьбой)."],
  ["ephorus 1", "<big>ephorus</big>\n\n<b>I ephorus, &#299;</b> <i>m</i> (<i>греч.</i>)\nэфор, <i>высшее (из числа пяти) должностное лицо в Спарте</i> <i>C, Nep etc.</i>"],
  ["Ephorus 2", "<big>Ephorus</big>\n\n<b>II Ephorus, &#299;</b> <i>m</i>\nЭфор, <i>из города Кимы (Эолида), ученик Исократа, автор всемирной истории (ок. 360 г. до н. э.</i>) <i>C, Q</i>."],
  ["Ephyra", "<big>Ephyra</big>\n\n<b>Ephyra, ae</b> <i>f</i> <i>и</i> <b>Ephyr&#275;, &#275;s</b> <i>f</i>\nЭфира:\n<b>1)</b> <i>древнее название Коринфа</i> <i>O, St</i>;\n<b>2)</b> <i>нимфа, дочь Океана и Фетиды</i> <i>V</i>."],
  ["Ephyraeus", "<big>Ephyraeus</big>\n\n<b>Ephyraeus (Ephyr&#275;ius, Ephyr&#275;us), a, um</b> [Ephyra 1]\nэфирский, <i>т. е.</i> коринфский (Creusa <i>O</i>; isthmos <i>Ap</i>) <i>или</i> сиракузский (<i>так как Сиракузы возникли как колония Коринфа</i>) (moenia <i>Sil</i>)."],
  ["Ephyreiades", "<big>Ephyreiades</big>\n\n<b>Ephyr&#275;iad&#275;s, ae</b> <i>m</i> <i>поэт.</i>\nкоринфянин <i>St</i>."],
  ["Ephyreias", "<big>Ephyreias</big>\n\n<b>Ephyr&#275;ias, adis</b> (<i>acc. pl.</i> adas)\nэфирская, <i>т. е.</i> коринфская (puella <i>Cld</i>)."],
  ["Ephyreius", "<big>Ephyreius</big>\n\n<b>Ephyr&#275;ius, Ephyr&#275;us</b> <i>v. l.</i>\n= Ephyraeus."],
  ["epibata", "<big>epibata</big>\n\n<b>epibata (-&#275;s), ae</b> <i>m</i> (<i>греч.</i>)\nморской солдат, моряк <i>bAl, bAfr, Vtr</i>."],
  ["epicedion", "<big>epicedion</big>\n\n<b>epic&#275;d&#299;on, &#299;</b> <i>n</i> (<i>греч.</i>)\nэпикедий, погребальная песнь <i>St</i>."],
  ["Epicharis", "<big>Epicharis</big>\n\n<b>Epicharis, is (idis)</b> <i>f</i>\nЭпихарида, <i>имя римлянки, участницы заговора против Нерона</i> <i>T</i>."],
  ["Epicharmius", "<big>Epicharmius</big>\n\n<b>Epicharm&#299;us, a, um</b> [Epicharmus]\nэпихармов <i>AG</i>."],
  ["Epicharmus", "<big>Epicharmus</big>\n\n<b>Epicharmus, &#299;</b> <i>m</i>\nЭпихарм, <i>родом из Коса, впоследствии житель Сиракуз, главн. представитель дорийско-сицилийской комедии</i> (<i>ок. 540-450 гг. до н. э.</i>) <i>C, H</i>."],
  ["epichirema", "<big>epichirema</big>\n\n<b>epich&#299;r&#275;ma, atis</b> <i>n</i> (<i>греч.</i>)\nэпихирема, <i>род сжатого цепного умозаключения</i> <i>Q</i>."],
  ["epichysis", "<big>epichysis</big>\n\n<b>epichysis, is</b> (<i>acc.</i> in, <i>abl.</i> i) <i>f</i> (<i>греч.</i>)\nковшик <i>Pl, Vr</i>."],
  ["epici", "<big>epici</big>\n\n<b>epic&#299;, &#333;rum</b> <i>m</i>\nэпические поэты <i>Q</i>."],
  ["epicoenos", "<big>epicoenos</big>\n\n<b>epicoenos, on</b> (<i>греч.</i>; <i>лат.</i> promiscuus) <i>грам.</i>\nобщий по грамматическому роду (nomina <i>Q</i>) (<i>напр.</i>, <i>слова</i> aquila, canis)."],
  ["epicopus", "<big>epicopus</big>\n\n<b>epic&#333;pus, a, um</b> (<i>греч.</i>)\nснабжённый вёслами, весельный, гребной, приводимый в движение вёслами (phaselus <i>C</i>)."],
  ["Epicrates", "<big>Epicrates</big>\n\n<b>Epicrat&#275;s, is</b> <i>m</i>\nЭпикрат:\n<b>1)</b> <i>афинский философ</i> <i>C</i>;\n<b>2)</b> (<i>греч.</i> &laquo;могущественный&raquo;) <i>эпитет Помпея</i> <i>C</i>."],
  ["epicrocum", "<big>epicrocum</big>\n\n<b>epicrocum, &#299;</b> <i>n</i> (<i>греч.</i>)\n<b>1)</b> платье из тонкой, прозрачной ткани, <i>по др.</i> шафранового цвета <i>Naev, Vr</i>;\n<b>2)</b> <i>шутл.</i> жидкий, водянистый суп <i>Pl</i>."],
  ["Epictetus", "<big>Epictetus</big>\n\n<b>Epict&#275;tus, &#299;</b> <i>m</i>\nЭпиктет, <i>из Гиераполиса (Фригия), вольноотпущенник Эпафродита, философ-стоик</i> <i>AG</i>."],
  ["Epicureus 1", "<big>Epicureus</big>\n\n<b>I Epic&#363;r&#275;us (-&#299;us), a, um</b> [Epicurus]\nэпикуров, эпикурейский <i>C, Su, Sen</i>."],
  ["Epicureus 2", "<big>Epicureus</big>\n\n<b>II Epic&#363;r&#275;us, &#299;</b> <i>m</i>\nэпикуреец <i>C, Sen</i>."],
  ["Epicurus", "<big>Epicurus</big>\n\n<b>Epic&#363;rus, &#299;</b> <i>m</i>\nЭпикур, <i>родом из Самоса, афинский философ, основатель эпикурейской школы, один из самых выдающихся представителей античного материализма</i> (<i>341-270 гг. до н. э.</i>) <i>C, Sen</i>."],
  ["epicus", "<big>epicus</big>\n\n<b>epicus, a, um</b> [epos] (<i>греч.</i>)\nэпический (carmen <i>Q</i>; poeta <i>C</i>)."],
  ["Epidamnus", "<big>Epidamnus</big>\n\n<b>Epidamnus (-os), &#299;</b> <i>f</i>\nЭпидамн, <i>приморский город в Иллирии</i> (<i>впоследствии</i> Dyrrhachium) <i>Pl, Mela, PM</i>."],
  ["Epidaphna", "<big>Epidaphna</big>\n\n<b>Epidaphna, ae</b> <i>и</i> <b>Epidaphn&#275;, &#275;s</b> <i>f</i>\nЭпидафна, <i>предместье Антиохии Сирийской</i> <i>T, PM</i>."],
  ["Epidaurius 1", "<big>Epidaurius</big>\n\n<b>I Epidaurius (-&#275;us, -icus), a, um</b> [Epidaurus]\nэпидаврский: E. deus <i>Prp</i> = Aesculapius; serpens E. <i>H</i> эпидаврская змея, <i>т. е.</i> посвященная богу врачевания Эскулапию."],
  ["Epidaurius 2", "<big>Epidaurius</big>\n\n<b>II Epidaurius, &#299;</b> <i>m</i>\nжитель Эпидавра <i>Mela</i>; <i>перен.</i> <i>O</i> = Aesculapius."],
  ["Epidauros", "<big>Epidauros</big>\n\n<b>Epidauros</b> <i>v. l.</i>\n= Epidaurus."],
  ["Epidaurum", "<big>Epidaurum</big>\n\n<b>Epidaurum, &#299;</b> <i>n</i> <i>PM</i>\n= Epidaurus."],
  ["Epidaurus", "<big>Epidaurus</big>\n\n<b>Epidaurus (-os), &#299;</b> <i>f</i>\nЭпидавр:\n<b>1)</b> <i>приморский город в Далматии</i> (<i>ныне</i> Рагуза) <i>bAl</i>;\n<b>2)</b> <i>город на вост. побережье Арголиды, в Саронском заливе</i> (<i>с культом Эскулапия</i>) <i>PM</i>."],
  ["epidemetica", "<big>epidemetica</big>\n\n<b>epid&#275;m&#275;tica, &#333;rum</b> <i>n</i> (<i>греч.</i>)\nденьги, уплачиваемые за освобождение дома от постоя войск <i>CJ</i>."],
  ["epidemus", "<big>epidemus</big>\n\n<b>epid&#275;mus, a, um</b> (<i>греч.</i>)\nраспространённый в данной местности, местный (species luis <i>Amm</i>)."],
  ["epidermis", "<big>epidermis</big>\n\n<b>epidermis, idis</b> <i>f</i> (<i>греч.</i>)\nструп <i>Veg</i>."],
  ["epidicazomenos", "<big>epidicazomenos</big>\n\n<b>epidicazomenos, &#299;</b> <i>m</i> (<i>греч.</i>)\nистец (<i>название комедии Аполлодора</i>) <i>Ter</i>."],
  ["epidicticus", "<big>epidicticus</big>\n\n<b>epid&#299;cticus, a, um</b> (<i>греч.</i>) <i>C</i>\n= demonstrativus."],
  ["Epidicus", "<big>Epidicus</big>\n\n<b>Epidicus, &#299;</b> <i>m</i>\nЭпидик, <i>имя раба</i> (<i>отсюда название комедии Плавта</i>)."],
  ["epidipnis", "<big>epidipnis</big>\n\n<b>epid&#299;pnis, idis</b> <i>f</i> (<i>греч.</i>)\nдесерт <i>Pt, M</i>."],
  ["epidromus", "<big>epidromus</big>\n\n<b>epidromus, &#299;</b> <i>m</i> (<i>греч.</i>)\nверёвка, бечёвка (<i>для стягивания сетей</i>) <i>PM</i>."],
  ["Epigoni", "<big>Epigoni</big>\n\n<b>Epigon&#299;, &#333;rum</b> <i>m</i> (<i>греч.</i> &laquo;послерождённые&raquo;)\nЭпигоны:\n<b>1)</b> <i>сыновья &laquo;Семерых против Фив&raquo;, которые, спустя 10 лет после неудачного похода их отцов, завладели Фивами</i> <i>C</i>;\n<b>2)</b> <i>дети солдат Александра Македонского, женившихся в Азии</i> <i>Just</i>."],
  ["epigramma", "<big>epigramma</big>\n\n<b>epigramma, atis</b> (<i>gen. pl.</i> epigrammaton) <i>n</i> (<i>греч.</i>)\n<b>1)</b> надпись <i>C, Pt, Nep</i>;\n<b>2)</b> эпиграмма, <i>короткое стихотворение сатирического или назидательного содержания</i> <i>C, Q, M</i>."],
  ["epigrammatarius", "<big>epigrammatarius</big>\n\n<b>epigrammat&#257;rius, &#299;</b> <i>m</i>\nэпиграмматист, автор эпиграмм <i>Vop</i>."],
  ["epigrammaticus", "<big>epigrammaticus</big>\n\n<b>epigrammaticus, a, um</b>:\ne. poeta <i>Spart</i> = epigrammatarius."],
  ["epigrammation", "<big>epigrammation</big>\n\n<b>epigrammation, &#299;</b> <i>n</i>\nмаленькая эпиграмма <i>Vr</i>."],
  ["epigrammatista", "<big>epigrammatista</big>\n\n<b>epigrammatista, ae</b> <i>m</i> <i>Sid</i>\n= epigrammatarius."],
  ["epigrus", "<big>epigrus</big>\n\n<b>epigrus, &#299;</b> <i>m</i>\nдеревянный гвоздь, колышек, шип <i>Sen, Is</i>."],
  ["epilepsia", "<big>epilepsia</big>\n\n<b>epil&#275;psia, ae</b> <i>f</i> (<i>греч.</i>; <i>лат.</i> morbus comitialis)\nэпилепсия, падучая болезнь <i>Lampr, CA</i>."],
  ["epilepticus", "<big>epilepticus</big>\n\n<b>epil&#275;pticus, a, um</b>\nэпилептический (passio <i>CA</i>)."],
  ["epileus", "<big>epileus</big>\n\n<b>epil&#275;us, &#299;</b> <i>m</i> (<i>греч.</i>) <i>зоол.</i>\nкобчик <i>PM</i>."],
  ["epilogus", "<big>epilogus</big>\n\n<b>epilogus, &#299;</b> <i>m</i> (<i>греч.</i>; <i>лат.</i> peroratio)\nзаключительная часть речи, эпилог <i>C, Q</i>."],
  ["epimenia", "<big>epimenia</big>\n\n<b>epim&#275;nia, &#333;rum</b> <i>n</i> (<i>греч.</i>)\nмесячное довольствие, месячный паёк (<i>рабов</i>) <i>J</i>."],
  ["Epimenides", "<big>Epimenides</big>\n\n<b>Epimenid&#275;s, is</b> (<i>греч. gen.</i> u) <i>m</i>\nЭпименид, <i>критский жрец и прорицатель, который, по легенде, в 596 г. до н. э. освободил Афины от проклятия, тяготевшего над ними после &laquo;Килоновой скверны&raquo;</i> (<i>см.</i> Cylon) <i>C, PM</i>."],
  ["Epimetheus", "<big>Epimetheus</big>\n\n<b>Epim&#275;the&#865;us, e&#299;</b> (<i>и</i> <b>eos</b>) <i>m</i> (<i>греч. &laquo;размышляющий после&raquo;, &laquo;крепкий задним умом&raquo;</i>)\nЭпиметей, <i>сын Япета, неразумный брат Прометея (&laquo;наперёд размышляющего&raquo;), женившийся на Пандоре и тем навлёкший на людей всякие беды (см.</i> Epimethis)."],
  ["Epimethis", "<big>Epimethis</big>\n\n<b>Epim&#275;this, idis</b> <i>f</i>\nЭпиметида, <i>дочь Эпиметея, т. е.</i> Pyrrha, <i>жена Девкалиона</i> <i>O</i>."],
  ["epimetrum", "<big>epimetrum</big>\n\n<b>epimetrum, &#299;</b> <i>n</i> (<i>греч.</i>; <i>лат.</i> cumulus)\nизлишек в мере, избыток <i>CTh</i>."],
  ["epinicium", "<big>epinicium</big>\n\n<b>epin&#299;cium, &#299;</b> <i>n</i> (<i>греч.</i>)\n<b>1)</b> эпиникий, победная песнь (epinicia cantare <i>Su</i>);\n<b>2)</b> <i>pl.</i> праздник победы <i>Vlg</i>."],
  ["epionicus", "<big>epionicus</big>\n\n<b>epi&#333;nicus, &#299;</b> <i>m</i> (<i>sc.</i> versus)\n= versus ionicus a majore (<i>см.</i> ionicus)."],
  ["epipedus", "<big>epipedus</big>\n\n<b>epipedus, a, um</b> (<i>греч.</i>; <i>лат.</i> planus)\nплоский, плоскостной (figurae <i>Bo&euml;t</i>)."],
  ["Epiphanea", "<big>Epiphanea</big>\n\n<b>Epiphan&#275;a (-&#299;a), ae</b> <i>f</i>\nЭпифанея, <i>город в Киликии, к сев. от реки Исса</i> <i>C, PM</i>."],
  ["Epiphanes", "<big>Epiphanes</big>\n\n<b>Epiphan&#275;s, is</b> <i>m</i> (<i>греч.</i> &laquo;славный&raquo;)\n<i>прозвище Антиохов IV <i>и</i> XI, царей Сирии</i> <i>T</i>."],
  ["Epiphania 1", "<big>Epiphania</big>\n\n<b>I Epiphan&#299;a, ae</b> <i>f</i> <i>v. l.</i>\n= Epiphanea."],
  ["epiphania 2", "<big>epiphania</big>\n\n<b>II epiphan&#299;a, &#333;rum</b> <i>n</i> <i>и</i> <b>epiphan&#299;ae, &#257;rum</b> <i>f</i> (<i>греч.</i>)\nпраздник богоявления <i>CTh, Amm, Eccl</i>."],
  ["epiphonema", "<big>epiphonema</big>\n\n<b>epiph&#333;n&#275;ma, atis</b> <i>n</i> (<i>греч.</i>)\nвосклицание <i>Sen, Q</i>."],
  ["epiphora", "<big>epiphora</big>\n\n<b>epiphora, ae</b> <i>f</i> (<i>греч.</i>; <i>лат.</i> destillatio)\nкатар, воспаление слизистой оболочки (oculi <i>Col, PM</i>; ventris <i>PM</i>), насморк <i>PM</i>."],
  ["Epipolae", "<big>Epipolae</big>\n\n<b>Epipolae, &#257;rum</b> <i>f</i>\n<i>название одного из районов города Сиракуз</i> <i>L</i>."],
  ["epiraedium", "<big>epiraedium</big>\n\n<b>epiraedium (epir&#275;dium), &#299;</b> <i>n</i> (<i>греко-галльск.</i>)\n<b>1)</b> постромка <i>Q</i>;\n<b>2)</b> повозка <i>J</i>."],
  ["Epirensis", "<big>Epirensis</big>\n\n<b>&#274;p&#299;r&#275;nsis, e</b> <i>и</i> <b>&#274;p&#299;r&#333;ticus, a, um</b> [Epirus]\nэпирский <i>L, Vr, C etc.</i>"],
  ["Epirotes", "<big>Epirotes</big>\n\n<b>&#274;p&#299;r&#333;t&#275;s, ae</b> <i>m</i>\nжитель Эпира <i>L, PM</i>."],
  ["Epirus 1", "<big>Epirus</big>\n\n<b>I &#274;p&#299;rus (-os), &#299;</b> <i>f</i>\nЭпир, <i>гористая область в зап. Греции</i> (<i>от Амбракийского до Акрокеравнского мыса</i>) <i>C, Vr, V</i>."],
  ["epirus 2", "<big>epirus</big>\n\n<b>II epirus, &#299;</b> <i>m</i>\nколышек, деревянный гвоздь <i>Aug</i> (<i>v. l.</i>)."],
  ["episcenium", "<big>episcenium</big>\n\n<b>episc&#275;nium, &#299;</b> <i>n</i> <i>Vtr</i>\n= episcenos."],
  ["episcenos", "<big>episcenos</big>\n\n<b>episc&#275;nos, &#299;</b> <i>f</i> (<i>греч.</i>)\nнадстройка на сцене, верхняя часть сцены <i>Vtr</i>."],
  ["episcopalis", "<big>episcopalis</big>\n\n<b>episcop&#257;lis, e</b> [episcopus]\nепископский <i>Amm, Eccl</i>."],
  ["episcopaliter", "<big>episcopaliter</big>\n\n<b>episcop&#257;liter</b>\nпо-епископски <i>Aug</i>."],
  ["episcopatus", "<big>episcopatus</big>\n\n<b>episcop&#257;tus, &#363;s</b> <i>m</i>\nепископат, сан епископа <i>Tert etc.</i>"],
  ["episcopium", "<big>episcopium</big>\n\n<b>episcopium, &#299;</b> <i>n</i>\n<b>1)</b> <i>Aug</i> = episcopatus;\n<b>2)</b> епископство, резиденция епископа <i>Eccl</i>."],
  ["episcopus", "<big>episcopus</big>\n\n<b>episcopus, &#299;</b> <i>m</i> (<i>греч.</i>)\n<b>1)</b> смотритель, надзиратель (episcopi, qui praesunt pani et ceteris venalibus rebus <i>Dig</i>);\n<b>2)</b> епископ <i>CJ, Amm, Eccl</i>."],
  ["episcynium", "<big>episcynium</big>\n\n<b>episcynium, &#299;</b> <i>n</i> (<i>греч.</i>)\nнадбровье, <i>перен.</i> строгость, суровость <i>Tert</i>."],
  ["epistalma", "<big>epistalma</big>\n\n<b>epistalma, atis</b> <i>n</i> (<i>греч.</i>)\nимператорское послание <i>CJ</i>."],
  ["epistates", "<big>epistates</big>\n\n<b>epistat&#275;s, ae</b> <i>m</i> (<i>греч.</i>)\nсмотритель, попечитель <i>Cato, Tert</i>."],
  ["epistola", "<big>epistola</big>\n\n<b>epistola, ae</b> <i>f</i> (<i>греч.</i>) <i>v. l.</i>\n= epistula."],
  ["epistolaris 1", "<big>epistolaris</big>\n\n<b>I epistol&#257;ris, e</b> [epistola]\nслужащий для писем (charta <i>M, Dig</i>); эпистолярный: epistolare colloquium <i>Aug</i> переписка."],
  ["epistolaris 2", "<big>epistolaris</big>\n\n<b>II epistol&#257;ris, is</b> <i>m</i>\nимператорский секретарь <i>CTh</i>."],
  ["epistolicus", "<big>epistolicus</big>\n\n<b>epistolicus, a, um</b> [epistola]\nизложенный в форме писем, эпистолярный <i>AG</i>."],
  ["epistolium", "<big>epistolium</big>\n\n<b>epistolium, &#299;</b> <i>n</i>\n<b>1)</b> небольшое письмо, письмецо, записка <i>M</i>;\n<b>2)</b> небольшое стихотворение <i>Ap</i>."],
  ["epistomium", "<big>epistomium</big>\n\n<b>epistomium, &#299;</b> <i>n</i> <i>Sen</i>\n(<i>v. l.</i>) = epitonion."],
  ["epistula", "<big>epistula</big>\n\n<b>epistula, ae</b> <i>f</i> (<i>греч.</i>)\n<b>1)</b> послание, письмо (ab aliquo, ad aliquem <i>C</i>): ab epistulis <i>T, Su</i> секретарь; tuae litterae, quas pluribus epistulis accepi <i>C</i> твои письма, которые я получил в виде ряда посланий;\n<b>2)</b> императорский рескрипт <i>PJ, Amm etc.</i>"],
  ["epistylium", "<big>epistylium</big>\n\n<b>epist&#563;lium, &#299;</b> <i>n</i> (<i>греч.</i>) <i>архит.</i>\nэпистиль, перекладина в верхней части колонн, архитрав <i>Vtr, PM</i>."],
  ["episynaliphe", "<big>episynaliphe</big>\n\n<b>episynal&#299;ph&#275;, &#275;s</b> <i>f</i> (<i>греч.</i>) <i>грам.</i>\nслияние двух слогов в один (в произношении)."],
  ["epitaphista", "<big>epitaphista</big>\n\n<b>epitaphista, ae</b> <i>m</i>\nсоставитель надгробных речей <i>Sid</i>."],
  ["epitaphius", "<big>epitaphius</big>\n\n<b>epitaphius, &#299;</b> <i>m</i> (<i>греч.</i>)\nнадгробное слово <i>C</i>."],
  ["epithalamium", "<big>epithalamium</big>\n\n<b>epithalamium, &#299;</b> <i>n</i> (<i>греч.</i>)\nэпиталама, свадебная песнь <i>Q, Treb, Hier</i>."],
  ["epitheca", "<big>epitheca</big>\n\n<b>epith&#275;ca, ae</b> <i>f</i> (<i>греч.</i>)\nдобавление, надбавка <i>Pl</i>."],
  ["epithema", "<big>epithema</big>\n\n<b>epithema, atis</b> <i>n</i> (<i>греч.</i>)\nпримочка, припарка, компресс <i>Scr</i>."],
  ["epitheton", "<big>epitheton</big>\n\n<b>epitheton, &#299;</b> <i>n</i> (<i>греч.</i>)\nприлагательное, определение, эпитет <i>Q, Macr</i>."],
  ["epitogium", "<big>epitogium</big>\n\n<b>epitogium, &#299;</b> <i>n</i> [<i>греч.</i> epi + toga]\nэпитога (<i>одежда, надевающаяся поверх тоги</i>) <i>Q</i>."],
  ["epitoma", "<big>epitoma</big>\n\n<b>epitoma, ae</b> <i>f</i> <i>C, Aus</i>\n= epitome."],
  ["epitome", "<big>epitome</big>\n\n<b>epitom&#275;, &#275;s</b> <i>f</i> (<i>греч.</i>)\nизвлечение, краткое изложение <i>C, Col</i>."],
  ["epitomo", "<big>epitomo</big>\n\n<b>epitomo, &#257;v&#299;, &#257;tum, &#257;re</b> [epitome]\nделать извлечение, сжато излагать (historiam <i>Treb</i>)."],
  ["epitonion", "<big>epitonion</big>\n\n<b>epitonion, &#299;</b> <i>n</i> (<i>греч.</i>)\nкран (<i>трубы</i>) <i>Vr, Vtr, Sen, Dig</i>."],
  ["epitoxis", "<big>epitoxis</big>\n\n<b>epitoxis, idis</b> <i>f</i> (<i>греч.</i>)\nжелобок (паз) в катапульте для тетивы <i>Vtr</i>."],
  ["epitrapezios", "<big>epitrapezios</big>\n\n<b>epitrapezios, on</b> (<i>греч.</i>)\nнаходящийся на столе (<i>в качестве украшения</i>) (Hercules <i>St</i>)."],
  ["Epitrepontes", "<big>Epitrepontes</big>\n\n<b>Epitrepontes</b> (<i>греч.</i>; <i>лат.</i> Arbitrium capientes)\nОбращающиеся (к третейскому судье), <i>название комедии Менандра</i> <i>Q, Sid</i>."],
  ["epitritus", "<big>epitritus</big>\n\n<b>epitritus, a, um</b> (<i>греч.</i>; <i>лат.</i> sesquitertius)\nсодержащий целое и одну треть (<i>т. е.</i> в 1 1/3 раза больший) <i>AG, Macr</i>: numerus e. <i>AG</i> отношение 4 к 3; pes e. эпитрит, стопа из трёх долгих и одного краткого: e. primus <span style=white-space:nowrap>U&ndash;&thinsp;&ndash;&thinsp;&ndash;; e. secundus <span style=white-space:nowrap>&ndash;U&ndash;&thinsp;&ndash;; e. tertius <span style=white-space:nowrap>&ndash;&thinsp;&ndash;U&ndash;; e. quartus <span style=white-space:nowrap>&ndash;&thinsp;&ndash;&thinsp;&ndash;U."],
  ["epitropos", "<big>epitropos</big>\n\n<b>epitropos, &#299;</b> <i>m</i> (<i>греч.</i>)\nсмотритель, надзиратель <i>Aus</i>."],
  ["epityrum", "<big>epityrum</big>\n\n<b>epit&#563;rum, &#299;</b> <i>n</i> (<i>греч.</i>)\nсалат из маслин <i>Pl, Cato, Col</i>."],
  ["epiurus", "<big>epiurus</big>\n\n<b>epi&#363;rus, &#299;</b> <i>m</i> (<i>греч.</i>)\nподпора (для деревца), тычина <i>Pall</i>."],
  ["Epius", "<big>Epius</big>\n\n<b>Ep&#299;us</b> <i>v. l.</i>\n= Epeus."],
  ["epizygis", "<big>epizygis</big>\n\n<b>epizygis, idis</b> <i>f</i> (<i>греч.</i>)\nжелезный рычаг в баллисте для укрепления и скручивания верёвки <i>Vtr</i>."],
  ["epodes", "<big>epodes</big>\n\n<b>epodes, um</b> <i>m</i>\nэподы, <i>род морских рыб</i> <i>O, PM</i>."],
  ["epodos", "<big>epodos</big>\n\n<b>ep&#333;dos, &#299;</b> <i>m</i> (<i>греч.</i>)\nэпод, <i>лирическое стихотворение (за исключением элегического дистиха), в котором длинный стих чередуется с коротким (напр.</i> iambus trimeter <i>с</i> dimeter) <i>Q, Cens, Aus, H</i>."],
  ["epogdous", "<big>epogdous</big>\n\n<b>epogdous, a, um (-os, -on)</b> (<i>греч.</i>; <i>лат.</i> sesquioctavus)\nвыражающий отношение 9:8 (numerus <i>Macr</i>)."],
  ["Epona", "<big>Epona</big>\n\n<b>Epona, ae</b> <i>f</i>\n<i>богиня-покровительница ослов и лошадей</i> <i>J</i>."],
  ["epops", "<big>epops</big>\n\n<b>epops, opis</b> <i>m</i> (<i>греч.</i>; <i>лат.</i> upupa)\nудод <i>O, V</i>."],
  ["Eporedia", "<big>Eporedia</big>\n\n<b>Eporedia, ae</b> <i>f</i>\nЭпоредия, <i>город в Gallia Cisalpina на реке Duria</i> (<i>ныне</i> Ivree) <i>T, PM, VP</i>."],
  ["Eporedorix", "<big>Eporedorix</big>\n\n<b>Eporedorix, &#299;gis</b> <i>m</i>\nЭпоредориг, <i>предводитель эдуев, восставший против Цезаря и присоединившийся к Верцингеторигу</i> <i>Cs</i>."],
  ["epos", "<big>epos</big>\n\n<b>epos</b> (<i>тк.</i> <i>nom. и acc. sg.</i>) <i>n</i> (<i>греч.</i>)\nгероическая песнь, эпос, эпопея, эпическая поэма <i>H, M, Aus</i>."],
  ["epoto", "<big>epoto</big>\n\n<b>&#275;–p&#333;to, p&#333;t&#257;v&#299;, p&#333;tum, &#257;re</b> (<i>part. fut. act.</i> epotaturus)\n<b>1)</b> выпивать (poculum epotum <i>C, L, Q</i>);\n<b>2)</b> всасывать, впитывать (umorem <i>Lcr</i>);\n<b>3)</b> пропивать (argentum epotum <i>Pl</i>)."],
  ["epta-", "<big>epta-</big>\n\n<b>epta-</b> <i>v. l.</i>\n= hepta-."],
  ["epulae", "<big>epulae</big>\n\n<b>epulae, &#257;rum</b> <i>f</i>\n<b>1)</b> яства, кушанья, еда (e. conquisitissimae <i>C</i>);\n<b>2)</b> торжественный обед, трапеза, пиршество, угощение (alicui epulas dare <i>T, Pt</i>); in epulis <i>C</i> <i>и</i> inter epulas <i>T</i> за (пиршественным) столом;\n<b>3)</b> наслаждение, услада (bonarum cogitationum e. <i>C</i>; oculis epulas dare <i>Pl</i>)."],
  ["epularis", "<big>epularis</big>\n\n<b>epul&#257;ris, e</b> [epulum]\nпиршественный, застольный (sermo <i>Ap</i>; accubitio <i>C</i>)."],
  ["epulatio", "<big>epulatio</big>\n\n<b>epul&#257;tio, &#333;nis</b> <i>f</i> [epulor]\nпиршество <i>C, VM, Pt, Su</i>."],
  ["epulo 1", "<big>epulo</big>\n\n<b>I epulo, &#333;nis</b> <i>m</i> [epulor]\n<b>1)</b> устроитель пира: triumviri (IIIviri), <i>позднее</i> septemviri (VIIviri) epulones <i>L, PJ, AG</i> эпулоны, <i>коллегия жрецов, на обязанности которых лежало устройство религиозных пиршеств</i>;\n<b>2)</b> бражник, участник пиршества, кутила, гурман <i>C, Ap</i>."],
  ["epulo 2", "<big>epulo</big>\n\n<b>II epulo, —, —, &#257;re</b> <i>поздн.</i>\n= epulor."],
  ["epulor", "<big>epulor</big>\n\n<b>epulor, &#257;tus sum, &#257;r&#299;</b> <i>depon.</i> [epulae]\n<b>1)</b> кушать, обедать (cum aliquo <i>C etc.</i>; apud aliquem <i>VP</i>);\n<b>2)</b> съедать (pullos <i>PM</i>; corpora epulanda <i>O</i>);\n<b>3)</b> пировать (dapibus opimis <i>V</i>)."],
  ["epulum", "<big>epulum</big>\n\n<b>epulum, &#299;</b> <i>n</i> <i>C, VM, Sen etc.</i>\n= epulae 2."],
  ["Epytides", "<big>Epytides</big>\n\n<b>&#274;pytid&#275;s, ae</b> <i>m</i>\nсын Эпита, <i>т. е.</i> Periphas, <i>спутник Иула</i> (<i>Аскания</i>) <i>V</i>."],
  ["Epytus", "<big>Epytus</big>\n\n<b>&#274;pytus, &#299;</b> <i>m</i>\nЭпит:\n<b>1)</b> <i>троянец, один из спутников Энея</i> <i>V</i>;\n<b>2)</b> <i>царь Альбы Лонги</i> <i>O</i>."],
  ["equa", "<big>equa</big>\n\n<b>equa, ae</b> <i>f</i> (<i>dat. abl. pl.</i> <i>тж.</i> equabus <i>Pall, CJ</i>) [equus]\nкобылица, кобыла <i>Lcr, C etc.</i>"],
  ["equaria", "<big>equaria</big>\n\n<b>equ&#257;ria, ae</b> <i>f</i> [equarius I]\nконская ферма, конский завод <i>Vr</i>."],
  ["equarius 1", "<big>equarius</big>\n\n<b>I equ&#257;rius, a, um</b> [equus]\nлошадиный, конский: medicus e. <i>VM</i> ветеринар."],
  ["equarius 2", "<big>equarius</big>\n\n<b>II equ&#257;rius, &#299;</b> <i>m</i> [equarius I]\nконюх <i>Sol</i>."],
  ["eque", "<big>eque</big>\n\n<b>&#275;–que</b> <i>Man</i>\n= et ex."],
  ["eques", "<big>eques</big>\n\n<b>eques, itis</b> <i>m</i> [equus]\n<b>1)</b> всадник, верховой, ездок, наездник: ille eques longitudinem Italiae transcurrit <i>L</i> он проехал верхом вдоль всей Италии;\n<b>2)</b> конный солдат, кавалерист: equites peditesque <i>Cs, L etc.</i> конница и пехота; <i>собир.</i> eques конница (e. optimus fuit in Graecia <i>L</i>);\n<b>3)</b> всадник, член сословия всадников (<i>среднего из трёх сословий римск. общества</i>) <i>C, L etc.</i>: spectare in equite <i>Su</i> сидеть на скамьях всадников (<i>в театре</i>);\n<b>4)</b> (<i>редко</i>) (<i>тж.</i> quadrupes e. <i>Enn</i>) <i>MF, GT</i> = equus."],
  ["equester 1", "<big>equester</big>\n\n<b>I equester</b> (<i>реже</i> <b>equestris</b>), <b>stris, stre</b> [eques]\n<b>1)</b> конный (statua <i>C, Su</i>; proelium <i>Cs, Su</i>); принадлежащий к коннице, кавалерийский (arma, scuta <i>L</i>): copiae equestres <i>C</i> конница; militia e. <i>Su, Pl, PJ</i> служба в коннице;\n<b>2)</b> конский, лошадиный (fremitus <i>Fronto</i>);\n<b>3)</b> принадлежащий к сословию всадников, всаднический (ordo, census <i>C</i>; dignitas <i>Nep</i>);\n<b>4)</b> верховой (equi <i>Vlg</i>)."],
  ["equester 2", "<big>equester</big>\n\n<b>II equester, tris</b> <i>m</i> <i>T</i>\n= eques 3."],
  ["equestria", "<big>equestria</big>\n\n<b>equestria, ium</b> <i>n</i> [equester]\n(<i>sc.</i> loca) скамьи для лиц всаднического сословия (<i>в театре</i>) <i>Sen, Pt, Su</i>."],
  ["equestris", "<big>equestris</big>\n\n<b>equestris, is</b>\n<i>редко</i> <i>v. l.</i> = equester I."],
  ["equidem", "<big>equidem</big>\n\n<b>e–quidem</b> <i>adv.</i> [<i>intens. к</i> quidem]\n<b>1)</b> подлинно, право, же, -то (<i>преим. при глаголе в 1 л. ед. и мн. ч.</i>): e. nolim <i>C</i> я, право, не хотел бы; e. nihil, <i>sc.</i> audivi <i>H</i> что до меня, то я (я-то) ничего не слышал; non e. hoc dubites <i>Pers</i> уж в этом-то ты не сомневайся; non e. invideo, miror magis <i>V</i> да я нисколько не завидую, а скорее удивляюсь;\n<b>2)</b> конечно, ведь: e. scitis <i>C etc.</i> вы, конечно (верно), знаете; nihil, inquit, e. novi <i>C</i> я, говорит, ведь ничего не знаю."],
  ["equiferus", "<big>equiferus</big>\n\n<b>equi–ferus, &#299;</b> <i>m</i>\nдикая лошадь <i>PM</i>."],
  ["equila", "<big>equila</big>\n\n<b>equila, ae</b> <i>f</i> <i>Vr</i>\n= equula."],
  ["equile", "<big>equile</big>\n\n<b>equ&#299;le, is</b> <i>n</i> [equus]\nконюшня <i>Cato, Vr, Su</i>."],
  ["equimentum", "<big>equimentum</big>\n\n<b>equ&#299;mentum, &#299;</b> <i>n</i> [equio]\nплата за случку (<i>с жеребцом</i>) <i>Vr</i>."],
  ["equimulga", "<big>equimulga</big>\n\n<b>equi–mulga, ae</b>, <i>v. l.</i> <b>equimulgus, &#299;</b> <i>m</i> [mulgeo]\nдоящий кобылиц, <i>т. е.</i> питающийся кобыльим молоком <i>Sid</i>."],
  ["equinus", "<big>equinus</big>\n\n<b>equ&#299;nus, a, um</b> [equus]\nлошадиный, конский (caput <i>PM</i>; saeta <i>C</i>); кобылий (lac <i>Vr</i>)."],
  ["equio", "<big>equio</big>\n\n<b>equio, —, —, &#299;re</b> [equus]\nнаходиться в периоде течки (<i>о кобылицах</i>) <i>PM</i>."],
  ["Equirria", "<big>Equirria</big>\n\n<b>Equir(r)ia, &#333;rum</b> <i>и</i> <b>um</b> <i>n</i> [equus + curro]\nпраздник с конскими состязаниями в честь Марса (<i>ежегодно 27 февраля и 14 марта</i>) <i>Vr, O</i>."],
  ["equisaet-", "<big>equisaet-</big>\n\n<b>equisaet-</b> <i>v. l.</i>\n= equiset-."],
  ["equiseta", "<big>equiseta</big>\n\n<b>equis&#275;ta, ae</b> <i>и</i> <b>equis&#275;tis, is</b> <i>f</i> <i>Ap, PM</i>\n= equisetum."],
  ["equisetum", "<big>equisetum</big>\n\n<b>equis&#275;tum, &#299;</b> <i>n</i> [equus + seta]\nхвощ (<i>Equisetum arvense</i>, L.) <i>PM, Ap</i>."],
  ["equiso", "<big>equiso</big>\n\n<b>equ&#299;so, &#333;nis</b> <i>m</i> [equus]\nконюх <i>Vr, VM, Ap</i>: nauticus e. <i>Vr</i> буксировщик, бурлак."],
  ["equitabilis", "<big>equitabilis</big>\n\n<b>equit&#257;bilis, e</b> [equito]\nудобный для верховой езды (planities <i>QC</i>)."],
  ["equitatio", "<big>equitatio</big>\n\n<b>equit&#257;tio, &#333;nis</b> <i>f</i> [equito]\nверховая езда <i>PM</i>."],
  ["equitatus 1", "<big>equitatus</big>\n\n<b>I equit&#257;tus, &#363;s</b> <i>m</i> [equito]\n<b>1)</b> конница <i>C, Cs</i>;\n<b>2)</b> верховая езда <i>PM</i>;\n<b>3)</b> всадническое сословие <i>C, PM, Aus</i>."],
  ["equitatus 2", "<big>equitatus</big>\n\n<b>II equit&#257;tus, &#363;s</b> <i>m</i> [equio]\nпериод течки (<i>у кобыл</i>) <i>LM</i> (<i>v. l.</i>)."],
  ["equitium", "<big>equitium</big>\n\n<b>equitium, &#299;</b> <i>n</i> [equus]\nтабун лошадей, коневодческое хозяйство <i>Col, Dig</i>."],
  ["equito", "<big>equito</big>\n\n<b>equito, &#257;v&#299;, &#257;tum, &#257;re</b> [eques]\n<b>1)</b> ездить верхом (equitandi peritus <i>Su</i>); скакать: e. in arundine longa <i>H</i> скакать на длинной тросточке верхом, <i>т. е.</i> играть в лошадки;\n<b>2)</b> бушевать (Eurus equitat per undas <i>H</i>);\n<b>3)</b> проезжать верхом (glacie flumen equitatur <i>Fl</i>)."],
  ["equula", "<big>equula</big>\n\n<b>equula, ae</b> <i>f</i> [<i>demin. к</i> equa]\nмолодая кобылица, кобылка <i>Pl, Vr</i>."],
  ["equuleus", "<big>equuleus</big>\n\n<b>equuleus (eculeus), &#299;</b> <i>m</i> [<i>demin. к</i> equus]\n<b>1)</b> жеребёнок, лошадка <i>Vr, C, L</i>;\n<b>2)</b> снаряд для пытки, дыба (in eculeum conjici <i>C</i>, imponi <i>C, QC, Sen</i>)."],
  ["equulus", "<big>equulus</big>\n\n<b>equulus, &#299;</b> <i>m</i> [<i>demin. к</i> equus]\nлошадка, жеребчик <i>Vr</i>."],
  ["equus", "<big>equus</big>\n\n<b>equus, &#299;</b> <i>m</i>\n<b>1)</b> лошадь, конь, жеребец: e. bellator <i>V, T</i> боевой конь; e. nudus <i>Sil</i> неосёдланный конь; equi juncti <i>Pl, V</i> (quadrijugi <i>O</i>) запряжка четвёркой; in equum insilire <i>L</i> (ascendere <i>C</i>) сесть на коня; ex equo descendere <i>C</i> сойти с коня; equos alere <i>Ter</i> держать лошадей; vehi (in) equo <i>C, Nep</i> <i>или</i> ire (in) equis <i>O, L</i> ездить верхом; merere equo <i>C</i> служить в коннице; equi virique <i>L</i> конница и пехота; equis virisque <i>L</i> соединёнными силами конницы и пехоты, <i>перен.</i> <i>C etc.</i> всеми силами; e. ligneus <i>шутл. Pl</i> = navis; e. bipes <i>V предпол.</i> морской лев; e. fluviatilis <i>PM</i> гиппопотам, бегемот;\n<b>2)</b> &laquo;конь&raquo;, <i>род стеноломной (осадной) машины</i> <i>PM</i>;\n<b>3)</b> (<i>sensu obsceno</i>) Hectoreus e. <i>M</i> = Hector;\n<b>4)</b> созвездие Пегаса <i>Col, C</i>."],
  ["Equus Tuticus", "<big>Equus Tuticus</big>\n\n<b>Equus Tuticus</b>\n<i>городок гирпинов в южн. Самнии</i> <i>C</i>."],
  ["er", "<big>er</big>\n\n<b>&#275;r, &#275;ris</b> <i>m</i>\nёж <i>Nem</i>."],
  ["era", "<big>era</big>\n\n<b>era (hera), ae</b> <i>f</i> [herus]\n<b>1)</b> хозяйка, госпожа <i>Pt, Ter etc.</i>;\n<b>2)</b> владычица (сердца), возлюбленная <i>Ctl, O</i>;\n<b>3)</b> повелительница, богиня (e. Fors <i>Enn</i>): tergemina e. <i>VF</i> = Hecate; rapidi Tritonis e. <i>Ctl</i> = Minerva."],
  ["eradicatio", "<big>eradicatio</big>\n\n<b>&#275;r&#257;d&#299;c&#257;tio, &#333;nis</b> <i>f</i> [eradico]\nискоренение, истребление <i>Tert, Aug</i>."],
  ["eradicator", "<big>eradicator</big>\n\n<b>&#275;r&#257;d&#299;c&#257;tor, &#333;ris</b> <i>m</i>\nистребитель <i>Aug</i>."],
  ["eradicitus", "<big>eradicitus</big>\n\n<b>&#275;r&#257;d&#299;citus, a, um</b> <i>v. l.</i>\n= exradicitus."],
  ["eradico", "<big>eradico</big>\n\n<b>&#275;r&#257;d&#299;co, &#257;v&#299;, &#257;tum, &#257;re</b> [e + radix]\n<b>1)</b> вырывать с корнем, искоренять (ex terra enata <i>Vr</i>);\n<b>2)</b> уничтожать, истреблять: di te eradicent! <i>бран. Ter</i> да истребят тебя боги!; aures alicujus aliqua re e. <i>шутл. Pl</i> докучать кому-л. россказнями о чём-л."],
  ["erado", "<big>erado</big>\n\n<b>&#275;–r&#257;do, r&#257;s&#299;, r&#257;sum, ere</b>\n<b>1)</b> выскоблить, соскоблить (terram rostris <i>Vr</i>; ubique titulos <i>Su</i>); сцарапать, стереть: aliquem albo senatorio e. <i>T</i> вычеркнуть кого-л. из списка сенаторов; erasi (<i>sc.</i> senatores) <i>PJ</i> исключённые из сенаторского списка; genas e. <i>Prp</i> брить щёки;\n<b>2)</b> истреблять, уничтожать (vestigia alicujus rei <i>Sen</i>); изгладить из памяти (vitae tempora <i>O</i>)."],
  ["eram", "<big>eram</big>\n\n<b>eram</b> <i>impf. к</i>\nsum."],
  ["Erana", "<big>Erana</big>\n\n<b>Erana, ae</b> <i>f</i>\nЭрана, <i>главн. город элевтерокиликийцев</i> (<i>на горе Amanus</i>) <i>C</i>."],
  ["eranus", "<big>eranus</big>\n\n<b>eranus, &#299;</b> <i>m</i> (<i>греч.</i>)\nблаготворительное общество помощи бедным согражданам, <i>тж.</i> союз взаимопомощи (<i>в Греции и её колониях</i>) <i>PJ</i>."],
  ["erasi", "<big>erasi</big>\n\n<b>&#275;r&#257;s&#299;</b> <i>pf. к</i>\nerado."],
  ["Erasinus", "<big>Erasinus</big>\n\n<b>Eras&#299;nus, &#299</b> <i>m</i>\nЭрасин, <i>река в Арголиде</i> (<i>впоследствии</i> Кефалари) <i>PM, O, Mela</i>."],
  ["Erato", "<big>Erato</big>\n\n<b>Erat&#333;, &#363;s</b> <i>f</i>\nЭрато:\n<b>1)</b> <i>муза лирической (преим. любовной) поэзии</i> <i>O, Aus</i>;\n<b>2)</b> = Musa (<i>вообще</i>) <i>V</i>."],
  ["Eratosthenes", "<big>Eratosthenes</big>\n\n<b>Eratosthen&#275;s, is</b> <i>m</i>\nЭратосфен, <i>греч. учёный, родом из Кирены; при Птолемее Эвергете заведовал Александрийской библиотекой</i> (<i>275-194 гг. до н. э.</i>) <i>C, Cs, PM</i>."],
  ["ercisco", "<big>ercisco</big>\n\n<b>erc&#299;sco, erctum</b> <i>v. l.</i>\n= hercisco, herctum."],
  ["Erebeus", "<big>Erebeus</big>\n\n<b>Ereb&#275;us, a, um</b> [Erebus]\nподземный, относящийся к преисподней <i>V, O</i>."],
  ["Erebus", "<big>Erebus</big>\n\n<b>Erebus, &#299;</b> <i>m</i>\nЭреб:\n<b>1)</b> <i>бог подземной тьмы, сын Хаоса, муж Ночи, отец Эфира и Гемеры</i> (<i>Дня</i>) <i>C, V</i>;\n<b>2)</b> подземный мир, царство теней <i>V</i>."],
  ["Erechtheus 1", "<big>Erechtheus</big>\n\n<b>I Erechthe&#865;us, e&#299;</b> <i>m</i>\nЭрехтей, <i>афинский герой, сын Геи, воспитанный Минервой, в честь которой он воздвиг в Афинах храм Erechtheum</i> <i>C, O</i>."],
  ["Erechtheus 2", "<big>Erechtheus</big>\n\n<b>II Erechth&#275;us, a, um</b> [Erechtheus]\nэрехтеев, <i>перен.</i> афинский (arces <i>O</i>)."],
  ["Erechthidae", "<big>Erechthidae</big>\n\n<b>Erechth&#299;dae, &#257;rum</b> <i>m</i>\nпотомки Эрехтея, <i>т. е.</i> афиняне <i>O</i>."],
  ["Erechthis", "<big>Erechthis</big>\n\n<b>Erechthis, idis</b> <i>f</i>\nдочь Эрехтея, <i>т. е.</i> Orithya <i>и</i> Procris <i>O</i>."],
  ["erecte", "<big>erecte</big>\n\n<b>&#275;r&#275;ct&#275;</b> (<i>тк. в</i> <i>compar.</i>) [erigo]\nмужественно, отважно (erectius judicare <i>AG</i>, loqui <i>Amm</i>)."],
  ["erectio", "<big>erectio</big>\n\n<b>&#275;r&#275;ctio, &#333;nis</b> <i>f</i> [erigo]\n<b>1)</b> водружение, установка (в отвесном направлении) (tignorum <i>Vtr</i>); воздвижение (obelisci <i>Amm</i>);\n<b>2)</b> надменность, гордыня <i>Vlg</i>."],
  ["erectus", "<big>erectus</big>\n\n<b>&#275;r&#275;ctus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> erigo;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> направляющийся вверх, стоящий отвесно (prora <i>Cs</i>; status <i>C</i>); поднятый (устремлённый) ввысь (vultus <i>O</i>);\n<b>2)</b> отважный, мужественный (animus <i>C, H, T</i>);\n<b>3)</b> возвышенный (ingenium <i>Sen, T</i>);\n<b>4)</b> гордый, прямой (incessus <i>T</i>);\n<b>5)</b> напряжённый, насторожившийся (judex <i>C</i>); внимательный: e. expectatione <i>L</i> с нетерпением ожидающий;\n<b>6)</b> устремлённый, рвущийся (ad libertatem recuperandam <i>C</i>)."],
  ["eremigo", "<big>eremigo</big>\n\n<b>&#275;–r&#275;migo, &#257;v&#299;, &#257;tum, &#257;re</b>\nпроплывать на вёслах, загребать (словно) вёслами (pedibus undas <i>Sil</i> — <i>о лебеде</i>; septentrio eremigatus <i>PM</i>)."],
  ["eremita", "<big>eremita</big>\n\n<b>er&#275;m&#299;ta, ae</b> <i>m</i> (<i>греч.</i>)\nотшельник, пустынник <i>Eccl</i>."],
  ["eremiticus", "<big>eremiticus</big>\n\n<b>er&#275;m&#299;ticus, a, um</b>\nотшельнический <i>Eccl</i>."],
  ["eremitis", "<big>eremitis</big>\n\n<b>er&#275;m&#299;tis, idis</b> <i>adj. f</i> <i>Sid</i>\n= eremitica."],
  ["eremodicium", "<big>eremodicium</big>\n\n<b>er&#275;modicium, &#299;</b> <i>n</i> (<i>греч.</i>; <i>лат.</i> lis deserta)\n<b>1)</b> неявка в назначенный срок <i>Dig, Tert</i>;\n<b>2)</b> заочное (ввиду неявки ответчика) разбирательство <i>CJ</i>."],
  ["eremus 1", "<big>eremus</big>\n\n<b>I er&#275;mus, a, um</b> (<i>греч.</i>)\nпустынный, безлюдный <i>Eccl, CJ</i>."],
  ["eremus 2", "<big>eremus</big>\n\n<b>II er&#275;mus, &#299;</b> <i>m</i>\nпустыня <i>Eccl</i>."],
  ["erepo", "<big>erepo</big>\n\n<b>&#275;–r&#275;po, r&#275;ps&#299;, r&#275;ptum, ere</b>\n<b>1)</b> выползать (a cubili <i>Sen</i>; e. ruinis <i>Sen</i>); проползать (agrum genibus <i>J</i>);\n<b>2)</b> взлезать, вскарабкаться, вползать, взбираться (montes, quos nunquam erepsemus = erepsissemus <i>H</i>; per aspera et devia <i>Su</i>)."],
  ["erepticius", "<big>erepticius</big>\n\n<b>&#275;rept&#299;cius, a, um</b> [eripio]\nподлежащий конфискации <i>Dig</i>."],
  ["ereptio", "<big>ereptio</big>\n\n<b>&#275;reptio, &#333;nis</b> <i>f</i> [eripio]\nнасильственное лишение, отнятие силой <i>C</i>."],
  ["ereptor", "<big>ereptor</big>\n\n<b>&#275;reptor, &#333;ris</b> <i>m</i> [eripio]\nотнимающий силой, похититель (bonorum, libertatis <i>C</i>; terrarum <i>T</i>)."],
  ["eres", "<big>eres</big>\n\n<b>&#275;r&#275;s, &#275;dis</b> <i>m</i> <i>v. l.</i>\n= heres."],
  ["Eretinus", "<big>Eretinus</big>\n\n<b>&#274;r&#275;t&#299;nus, a, um</b> [Eretum]\nэретский <i>Tib</i>."],
  ["Eretria", "<big>Eretria</big>\n\n<b>Eretria, ae</b> <i>f</i>\nЭретрия, <i>город на Эвбее, к юго-вост. от Эврипа, местопребывание философской школы Менедема, ученика Платона</i> <i>C, L</i>."],
  ["Eretriacus 1", "<big>Eretriacus</big>\n\n<b>I Eretri(a)cus, a, um</b> [Eretria]\nэретрийский (philosophus <i>C</i>)."],
  ["Eretriacus 2", "<big>Eretriacus</big>\n\n<b>II Eretri(a)cus, &#299;</b>\nэретриец, <i>т. е.</i> ученик Менедема <i>C</i>."],
  ["Eretriensis 1", "<big>Eretriensis</big>\n\n<b>I  Eretri&#275;nsis, e</b> <i>Nep</i>\n= Eretrius."],
  ["Eretriensis 2", "<big>Eretriensis</big>\n\n<b>II Eretri&#275;nsis, is</b> <i>m</i>\nжитель Эретрии <i>L</i>."],
  ["Eretrius", "<big>Eretrius</big>\n\n<b>Eretrius, a, um</b> <i>PM</i>\n= Eretriacus I."],
  ["Eretum", "<big>Eretum</big>\n\n<b>&#274;r&#275;tum, &#299;</b> <i>m</i>\nЭрет, <i>город сабинян близ Тибра, к сев.-вост. от Рима</i> <i>V, L, VM</i>."],
  ["erexi", "<big>erexi</big>\n\n<b>&#275;r&#275;x&#299;</b> <i>pf. к</i>\nerigo."],
  ["erga", "<big>erga</big>\n\n<b>erg&#257;</b> <i>praep. cum acc.</i> [<i>одного корня с</i> vergo]\n<b>1)</b> около, в области, против (nostram e. aedem <i>Pl</i>): e. regiam capitis constitutus <i>Ap</i> (орган), помещающийся в области головы;\n<b>2)</b> в отношении, по отношению к (benignus e. aliquem <i>Pl</i>; invidia e. aliquem <i>T</i>): raro quisquam e. bona sua satis cautus est <i>QC</i> мало кто достаточно бережёт своё благополучие."],
  ["ergasterium", "<big>ergasterium</big>\n\n<b>ergast&#275;rium, &#299;</b> <i>m</i> (<i>греч.</i>)\nмастерская <i>CJ</i>."],
  ["ergastularis", "<big>ergastularis</big>\n\n<b>ergastul&#257;ris, e</b> [ergastulum]\nтюремный (tenebrae <i>Sid</i>)."],
  ["ergastularius 1", "<big>ergastularius</big>\n\n<b>I ergastul&#257;rius, a, um</b> <i>Amm</i>\n= ergastularis."],
  ["ergastularius 2", "<big>ergastularius</big>\n\n<b>II ergastul&#257;rius, &#299;</b> <i>m</i> [ergastulum]\nнадсмотрщик, надзиратель за принудительными работами <i>Col</i>."],
  ["ergastulum", "<big>ergastulum</big>\n\n<b>ergastulum, &#299;</b> <i>m</i> (<i>греч.</i>)\n<b>1)</b> каторжная тюрьма (aliquem in e. ducere <i>или</i> dare <i>L</i>);\n<b>2)</b> <i>pl.</i> заключённые (ergastula armare <i>Fl</i>; soluta ergastula <i>Cs</i>)."],
  ["ergastulus", "<big>ergastulus</big>\n\n<b>ergastulus, &#299;</b> <i>m</i> [ergastulum]\nкаторжник, заключённый <i>LM</i>."],
  ["ergata", "<big>ergata</big>\n\n<b>ergata, ae</b> <i>f</i> (<i>греч.</i>)\nподъёмное сооружение, ворот <i>Vtr</i>."],
  ["ergo 1", "<big>ergo</big>\n\n<b>I erg&#333;</b> (<i>редко</i> <b>ergo</b>) <i>praep.</i> <i>с предшеств.</i> <i>gen.</i>\nдля, ради, из-за (victoriae, non valetudinis e. <i>L</i>): nominis e. <i>Lcr</i> ради славы; formidinis e. <i>Lcr</i> для устрашения (врагов)."],
  ["ergo 2", "<big>ergo</big>\n\n<b>II erg&#333;</b> <i>adv.</i>\nвследствие этого, поэтому, а потому, следовательно, итак (e. pueri hoc potuerunt, viri non poterunt? <i>C</i>): quid e.? <i>C etc.</i> как же (так)?"],
  ["ergolabus", "<big>ergolabus</big>\n\n<b>ergolabus, &#299;</b> <i>m</i> (<i>греч.</i>; <i>лат.</i> redemptor operis)\nпредприниматель, подрядчик <i>CJ</i>."],
  ["ericaeus", "<big>ericaeus</big>\n\n<b>er&#299;caeus, a, um</b>\nвересковый (mel <i>PM</i>)."],
  ["erice", "<big>erice</big>\n\n<b>er&#299;c&#275;, &#275;s</b> <i>f</i> (<i>греч.</i>)\nвереск <i>PM</i>."],
  ["Erichtho", "<big>Erichtho</big>\n\n<b>Erichth&#333;, &#363;s</b> <i>f</i>\nЭрихто, <i>имя колдуньи</i> <i>O, Lcn</i>."],
  ["Erichthonius 1", "<big>Erichthonius</big>\n\n<b>I Eric(h)thonius, &#299;</b> <i>m</i>\nЭрихтоний:\n<b>1)</b> <i>сын Гефеста, миф. царь Афин, изобретатель квадриги</i> <i>V, O</i>;\n<b>2)</b> <i>сын Дардана, отец Троя</i> <i>O</i>."],
  ["Erichthonius 2", "<big>Erichthonius</big>\n\n<b>II Eric(h)thonius, a, um</b>\nэрихтониев, <i>т. е.</i> афинский (populus <i>Prp</i>) <i>или</i> троянский (arces <i>V</i>)."],
  ["ericinus", "<big>ericinus</big>\n\n<b>&#275;ric&#299;nus, a, um</b> [ericius]\nежовый (caro <i>Aug</i>)."],
  ["ericius", "<big>ericius</big>\n\n<b>&#275;ricius, &#299;</b> <i>m</i>\n<b>1)</b> ёж <i>Vr, Vlg</i>;\n<b>2)</b> <i>воен.</i> &laquo;ёж&raquo;, заграждение (<i>брус с железными крючьями для задержания противника</i>) <i>Cs, Sl</i>."],
  ["Eridanus", "<big>Eridanus</big>\n\n<b>&#274;ridanus, &#299;</b> <i>m</i>\nЭридан:\n<b>1)</b> <i>миф. река на крайнем западе Европы</i> <i>V etc.</i>;\n<b>2)</b> <i>PM etc.</i> = Padus (<i>ныне</i> По);\n<b>3)</b> <i>созвездие</i> <i>Cld</i>."],
  ["erifuga", "<big>erifuga</big>\n\n<b>erifuga, ae</b> <i>m</i> [erus + fugio]\nраб, бежавший (бегущий) от своего господина <i>Ctl</i>."],
  ["erigo", "<big>erigo</big>\n\n<b>&#275;rigo, r&#275;x&#299;, r&#275;ctum, ere</b> [e + rego]\n<b>1)</b> ставить прямо, поднимать, водружать (malum <i>C</i>); придавать прямое (стоячее) положение (natura hominem erexit <i>C</i>): e. oculos <i>C</i> <i>или</i> vultus <i>Pt</i> поднимать глаза; e. scalas ad moenia <i>L</i> приставлять лестницы к стене; e. tenebras <i>T</i> окутываться мраком;\n<b>2)</b> вести вверх (agmen in adversum clivum <i>L</i>): e. se <i>или</i> erigi подниматься (fumus erigitur <i>V</i>): erigi in digitos <i>Q</i> подняться на цыпочки;\n<b>3)</b> возводить, воздвигать, строить (turrim <i>Cs</i>; aram <i>J</i>; pontem <i>QC</i>; pyram <i>V</i>);\n<b>4)</b> ободрять, воодушевлять (animos militum <i>L</i>): e. aliquem ad (<i>реже</i> in <i>L, T</i>) spem <i>C, QC, Fl, Just</i> обнадёживать кого-л.; e. animum <i>или</i> e. se <i>C etc.</i> воспрянуть духом, ободриться; e. mentem (aures) <i>C etc.</i> насторожиться, напрячь внимание, сделаться внимательным; haec res senatum erexit <i>L</i> это возбудило внимание сената; e. animum ad audiendum <i>C</i> внимательно вслушиваться;\n<b>5)</b> разжигать, возбуждать (iras <i>Sil</i>)."],
  ["Erigone", "<big>Erigone</big>\n\n<b>&#274;rigon&#275;, &#275;s</b> <i>f</i>\nЭригона, <i>дочь афинянина Икария, удавившаяся от тоски по своему убитому отцу и превращенная Вакхом в созвездие Девы</i> <i>V, O</i>."],
  ["Erigoneius", "<big>Erigoneius</big>\n\n<b>&#274;rigon&#275;ius, a, um</b> [Erigone]\nэригонин: E. canis <i>O</i> = canicula 2 (<i>собака отца Эригоны была превращена в эту звезду</i>)."],
  ["Erigonus", "<big>Erigonus</big>\n\n<b>&#274;rig&#333;nus, &#299;</b> <i>m</i>\nЭригон, <i>правый приток Аксия в Пеонии</i> (<i>Македония</i>) <i>L</i>."],
  ["erilis", "<big>erilis</big>\n\n<b>er&#299;lis, e</b> [erus]\nгосподский, хозяйский (filius <i>Pl</i>; mensa <i>V</i>): e. metus <i>Pl</i> страх перед господином (госпожой)."],
  ["erinaceus", "<big>erinaceus</big>\n\n<b>&#275;rin&#257;ceus</b> <i>v. l.</i>\n= herinaceus."],
  ["Erinne", "<big>Erinne</big>\n\n<b>&#274;rinn&#275;, &#275;s</b> <i>и</i> <b>&#274;rinna, ae</b> <i>f</i>\nЭринна, <i>поэтесса с о-ва Лесбос</i> <i>Prp, PM</i>."],
  ["Erinnys", "<big>Erinnys</big>\n\n<b>Er&#299;n(n)&#563;s, yos</b> <i>f</i>\nЭриния:\n<b>1)</b> <i>V, Prp etc.</i> = Furia, <i>богиня мести</i>;\n<b>2)</b> гибель, бедствие, бич (Trojae <i>V</i>);\n<b>3)</b> безумие, бешенство <i>Lcn</i>."],
  ["erioxylon", "<big>erioxylon</big>\n\n<b>erioxylon, &#299;</b> <i>n</i> (<i>греч.</i>)\nхлопок <i>Dig</i>."],
  ["Eriphyla", "<big>Eriphyla</big>\n\n<b>Eriph&#563;la, ae</b> <i>и</i> <b>Eriph&#563;l&#275;, &#275;s</b> <i>f</i>\nЭрифила, <i>жена Амфиарая, заставившая мужа отправиться в поход на Фивы, в котором он погиб, и убитая за это своим же сыном Алкмеоном</i> <i>C, Prp, V, O</i>."],
  ["eripio", "<big>eripio</big>\n\n<b>&#275;ripio, ripu&#299;, reptum, ere</b> [e + rapio]\n<b>1)</b> вырывать, исторгать (aliquid e manibus alicujus <i>QC, Q</i>; alicui gladium <i>Pl</i>); выхватывать (ensem vagina <i>V</i>); похищать, отнимать, лишать: e. alicui vitam <i>Sl</i> лишить кого-л. жизни; eripuit Jovi fulmen <i>Man</i> (человек) вырвал у Юпитера молнию; e. alicui favorem <i>Pt</i> затмить (превзойти) кого-л.; eripitur persona, manet res <i>Lcr</i> маска срывается, суть остаётся; erepto aequore <i>Sil</i> обогнав в состязании; posse loqui eripitur <i>O</i> пропадает способность говорить;\n<b>2)</b> стащить (aliquem ex equo <i>L</i>);\n<b>3)</b> подхватывать (vocem alicujus <i>V</i>);\n<b>4)</b> спасать, избавлять (aliquem ex media morte <i>и</i> a morte <i>C</i>; a miseria <i>C</i>; e. aliquem poenae <i>Pt</i>); освобождать (e. alicui dolorem, errorem <i>C</i>): lupo agnum e. <i>погов. Pl</i> отнять ягнёнка у волка (<i>о невозможном</i>); e. alicui timorem <i>C</i> избавить кого-л. от страха; e. fugam <i>V</i> поспешно обратиться в бегство; e. se ex manibus hostium <i>Cs</i> вырваться (освободиться) из рук врагов;\n<b>5)</b> похитить (uxor fato erepta <i>L</i>; e. virginem ab aliquo <i>Ter</i>)."],
  ["erisma", "<big>erisma</big>\n\n<b>er&#299;sma, ae</b> <i>f</i> <i>или</i> <b>atis</b> <i>n</i> (<i>греч.</i>)\nподпора, контрфорс <i>Vtr</i>."],
  ["erithace", "<big>erithace</big>\n\n<b>erithac&#275;, &#275;s</b> <i>f</i> (<i>греч.</i>)\n<b>1)</b> пчелиный клей, прополис <i>Vr</i>;\n<b>2)</b> перга <i>PM</i>."],
  ["erivo", "<big>erivo</big>\n\n<b>&#275;–r&#299;vo, —, —, &#257;re</b> [rivus]\nотводить (aquas pluvias <i>PM</i> — <i>v. l. к</i> derivo)."],
  ["erneum", "<big>erneum</big>\n\n<b>erneum, &#299;</b> <i>n</i>\nпирог (<i>испечённый в глиняной форме</i>) <i>Cato</i>."],
  ["ero 1", "<big>ero</big>\n\n<b>I &#275;ro, &#333;nis</b> <i>m</i>\nплетёная корзина <i>Vtr</i>."],
  ["ero 2", "<big>ero</big>\n\n<b>II ero</b> <i>fut. к</i>\nsum."],
  ["erodentia", "<big>erodentia</big>\n\n<b>&#275;r&#333;dentia, ium</b> <i>n</i> [erodo]\n(<i>sc.</i> medicamenta) едкие лекарства (вещества) <i>CC, PM</i>."],
  ["erodo", "<big>erodo</big>\n\n<b>&#275;–r&#333;do, (r&#333;s&#299;), r&#333;sum, ere</b>\nразъедать (cutem <i>CC</i>; ferrum <i>PM</i>); объедать (frondes <i>Col</i>)."],
  ["erogatio", "<big>erogatio</big>\n\n<b>&#275;rog&#257;tio, &#333;nis</b> <i>f</i> [erogo]\n<b>1)</b> выдача, раздача (pecuniae <i>C</i>); распределение (aquarum <i>Frontin</i>); расход, расходование: necessitas erogationum <i>T</i> необходимые издержки;\n<b>2)</b> отмена, упразднение (legis <i>Tert</i>)."],
  ["erogator", "<big>erogator</big>\n\n<b>&#275;rog&#257;tor, &#333;ris</b> <i>m</i> [erogo]\n<b>1)</b> раздатчик жалованья и продовольствия солдатам <i>CJ</i>;\n<b>2)</b> выдающий замуж (e. virginis <i>Tert</i>)."],
  ["erogatorius", "<big>erogatorius</big>\n\n<b>&#275;rog&#257;t&#333;rius, a, um</b> [erogo]\nраспределительный (modulus <i>Frontin</i>)."],
  ["erogito", "<big>erogito</big>\n\n<b>&#275;–rogito, —, —, &#257;re</b>\nрасспрашивать, выведывать (ex aliquo <i>Pl</i> <i>и</i> aliquid <i>Sil</i>)."],
  ["erogo", "<big>erogo</big>\n\n<b>&#275;–rogo, &#257;v&#299;, &#257;tum, &#257;re</b>\n<b>1)</b> выплачивать, выдавать, расходовать (pecuniam ex aerario <i>C</i>; annonam <i>Dig</i>): e. pecuniam in aliquid <i>C</i> издержать (израсходовать) деньги на что-л.;\n<b>2)</b> уничтожать, истреблять (tot innocentes <i>Tert</i>);\n<b>3)</b> упрашивать, склонять просьбами (precibus erogatus <i>Ap</i>);\n<b>4)</b> взыскивать, собирать (tributa vectigaliaque <i>bAfr</i>)."],
  ["eromenion", "<big>eromenion</big>\n\n<b>er&#333;menion, &#299;</b> <i>n</i> (<i>греч.</i>)\nдушечка <i>Lcr</i>."],
  ["Eros", "<big>Eros</big>\n\n<b>Er&#333;s, &#333;tis</b> <i>m</i>\nЭрот, <i>божок любви у греков</i> <i>Nem</i>."],
  ["erosio", "<big>erosio</big>\n\n<b>&#275;r&#333;sio, &#333;nis</b> <i>f</i> [erodo]\nразъедание <i>PM</i>."],
  ["eroticus", "<big>eroticus</big>\n\n<b>er&#333;ticus, a, um</b> (<i>греч.</i>)\nлюбовный (<i>sc.</i> carmina <i>AG</i>)."],
  ["erotopaegnia", "<big>erotopaegnia</big>\n\n<b>er&#333;topaegnia, &#333;n</b> <i>n</i> (<i>греч.</i>)\nлюбовные стихи <i>Aus</i>."],
  ["erotundatus", "<big>erotundatus</big>\n\n<b>&#275;–rotund&#257;tus, a, um</b>\nокруглённый (structura verborum <i>Sid</i>)."],
  ["errabundus", "<big>errabundus</big>\n\n<b>err&#257;bundus, a, um</b> [erro I]\nблуждающий (agmen <i>QC</i>; vestigia <i>Ctl</i>); рассеянный (particulae <i>Vtr</i>)."],
  ["errantia", "<big>errantia</big>\n\n<b>errantia, ae</b> <i>f</i> [erro I]\nблуждание, заблуждение (animi <i>Acc</i>)."],
  ["erraticus", "<big>erraticus</big>\n\n<b>err&#257;ticus, a, um</b> [erro I]\n<b>1)</b> странствующий, бродячий (homo <i>AG</i>); плавучий (Delos <i>O</i>); блуждающий (stella <i>Sen, AG</i>);\n<b>2)</b> дикорастущий (salix <i>Vtr</i>);\n<b>3)</b> стелющийся, вьющийся (vitis <i>C</i>);\n<b>4)</b> заставляющий блуждать, <i>т. е.</i> неправильный (semitae <i>Aug</i>)."],
  ["erratio", "<big>erratio</big>\n\n<b>err&#257;tio, &#333;nis</b> <i>f</i> [erro I]\n<b>1)</b> блуждание, отклонение от верного пути <i>Pl, Ter, C</i>;\n<b>2)</b> заблуждение <i>Lact</i>."],
  ["errator", "<big>errator</big>\n\n<b>err&#257;tor, &#333;ris</b> <i>m</i> [erro I]\nблуждающий <i>O</i> (<i>v. l.</i>)."],
  ["erratum", "<big>erratum</big>\n\n<b>err&#257;tum, &#299;</b> <i>n</i> [erro I]\nзаблуждение, промах, погрешность, ошибка: errata aetatis <i>C</i> заблуждения юности."],
  ["erratus", "<big>erratus</big>\n\n<b>err&#257;tus, &#363;s</b> <i>m</i> <i>O</i>\n= erratio."],
  ["erro 1", "<big>erro</big>\n\n<b>I erro, &#257;v&#299;, &#257;tum, &#257;re</b>\n<b>1)</b> заблудиться: e. via <i>V</i> сбиться с дороги;\n<b>2)</b> заблуждаться, ошибаться (in <i>или</i> de aliqua re <i>C etc.</i>; erras, si id credis <i>Ter</i>): quid erro? <i>Pl</i>. в чём моя ошибка?; sine nescius errem <i>O</i> позволь мне остаться в своём заблуждении; errasse humanum est <i>Hier</i> быть в заблуждении — удел человека; temporibus e. <i>C</i> ошибиться в датах;\n<b>3)</b> блуждать, бродить, скитаться (per lucos <i>H</i>; per litora <i>Sen</i>): in media luce e. <i>погов. Sen</i> блуждать среди бела дня; ante oculos e. <i>O</i> проноситься перед (мысленным) взором; stellae errantes <i>V, C</i> = planetae; hedera errans <i>V</i> ползучий плющ;\n<b>4)</b> метаться, безумствовать (animae errantes <i>Pt</i>);\n<b>5)</b> развеваться (capilli errant <i>Prp</i>);\n<b>6)</b> колебаться, быть неуверенным: erro, quam insistas viam <i>Pl</i> недоумеваю, к чему ты ведёшь (речь); opinio errans <i>Q</i> шаткое мнение;\n<b>7)</b> проходить в странствиях, обойти, проехать (terrae erratae <i>O</i>)."],
  ["erro 2", "<big>erro</big>\n\n<b>II erro, &#333;nis</b> <i>m</i> [erro I]\nбродяга, скиталец <i>H, Sen, Dig etc.</i>: errones<i>Nigidius ap. AG</i> = planetae."],
  ["erroneus", "<big>erroneus</big>\n\n<b>err&#333;neus, a, um</b> [erro I]\nстранствующий, бродячий, скитающийся (canes <i>Col</i>); заблудший (ovis <i>Hier</i>); блуждающий (stella <i>Ap</i>)."],
  ["error", "<big>error</big>\n\n<b>error, &#333;ris</b> <i>m</i> [erro I]\n<b>1)</b> отклонение от прямого <i>или</i> правильного пути (errore deferri in locum aliquem <i>VF</i>): timere errorem <i>Pt</i> боязнь сбиться с пути;\n<b>2)</b> промах, ошибка, погрешность: e. veri <i>T</i> отклонение (уклонение) от истины; inducere aliquem in errorem <i>C</i> вводить кого-л. в заблуждение; errore duci <i>или</i> in errorem rapi (induci) <i>C</i> быть в заблуждении (ошибаться); eripere alicui errorem <i>C</i> вывести кого-л. из заблуждения; e. <i>H, V</i> <i>и</i> e. mentis <i>C</i> помрачение ума, безумие;\n<b>3)</b> обман, ловушка; aliquis latet e. <i>V</i> (здесь) таится какой-то обман;\n<b>4)</b> блуждание, скитание, странствие (dissipatio atque e. <i>C</i>): errores Ulixis <i>C</i> странствование Улисса;\n<b>5)</b> колебание, недоумение, сомнение, неуверенность (e. viarum <i>L</i> — <i>ср.</i> 6);\n<b>6)</b> <i>поэт.</i> изгиб, поворот, извилина, излучина (amnis <i>O</i>; viarum, <i>sc.</i> labyrinthi <i>O</i> — <i>ср.</i> 5);\n<b>7)</b> панический страх (diffugiunt quo quemque agit e. <i>O</i>)."],
  ["erubescentia", "<big>erubescentia</big>\n\n<b>&#275;rub&#275;scentia, ae</b> <i>f</i> [erubesco]\nстыдливость <i>Tert</i>."],
  ["erubesco", "<big>erubesco</big>\n\n<b>&#275;–rub&#275;sco, rubu&#299;, —, ere</b>\n<b>1)</b> краснеть, румяниться (genae erubuerunt <i>O</i>);\n<b>2)</b> стыдиться, стесняться, совеститься (propter aliquid <i>Pl</i>, ex aliqua re <i>VP</i>, de aliqua re <i>Aug</i>, ad aliquid <i>Hier</i>, pro aliquo <i>Aug</i> <i>и</i> aliqua re <i>L, O</i>): e. loqui de aliqua re <i>C</i> стыдиться говорить о чём-л.; non erubui eum interrogare <i>Pt</i> я отважился спросить его; erubescendus <i>H, Sen etc.</i> постыдный, позорный;\n<b>3)</b> уважать, чтить (jura fidemque <i>V</i>)."],
  ["eruca", "<big>eruca</big>\n\n<b>&#275;r&#363;ca, ae</b> <i>f</i>\n<b>1)</b> разновидность дикой капусты (<i>предпол. Brassica eruca</i>, L.) <i>H, J, M, CC etc.</i>;\n<b>2)</b> капустная гусеница <i>Col, PM</i>."],
  ["eructatio", "<big>eructatio</big>\n\n<b>&#275;r&#363;ct&#257;tio, &#333;nis</b> <i>f</i> [eructo]\nотрыжка, <i>перен.</i> извержение <i>или</i> испарение (terrenae eructationes <i>Ap</i>)."],
  ["eructo", "<big>eructo</big>\n\n<b>&#275;–r&#363;cto, &#257;v&#299;, &#257;tum, &#257;re</b> [<i>frequ. к</i> erugo II]\n<b>1)</b> изрыгать, выплёвывать (saniem <i>V</i>); изливать, извергать (flammam <i>Just</i>; aquam <i>Vr</i>); испускать, издавать (odorem <i>Vr</i>); выбрасывать (arenam <i>V</i>): e. caedem <i>C</i> угрожать смертью || произносить (eructavit cor meum verbum bonum <i>Vlg</i>);\n<b>2)</b> прорываться наружу (ignis e terra eructans <i>Tert</i>);\n<b>3)</b> врываться, вторгаться (in Africam <i>Tert</i>)."],
  ["eructuo", "<big>eructuo</big>\n\n<b>&#275;r&#363;ctuo, &#257;v&#299;, —, &#257;re</b> <i>Aug</i>\n= eructo."],
  ["eructus", "<big>eructus</big>\n\n<b>&#275;r&#363;ctus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> erugo II;\n<b>2.</b> <i>adj.</i> нечистый, плохой: eructum vinum <i>AG</i> прокисшее вино."],
  ["erudero", "<big>erudero</big>\n\n<b>&#275;–r&#363;dero, &#257;v&#299;, &#257;tum, &#257;re</b> [ex + rudus I]\nочищать от щебня (solum <i>Vr</i>); <i>перен.</i> очищать, освобождать (aures opportunitate falsi sermonis <i>Sid</i>)."],
  ["erudio", "<big>erudio</big>\n\n<b>&#275;–rudio, &#299;v&#299; (i&#299;), &#299;tum, &#299;re</b> [e + rudis]\n(<i>impf.</i> <i>тж.</i> erudibam <i>Pt</i>)\n<b>1)</b> просвещать, давать образование, наставлять, обучать (aliquem primis litteris <i>Q</i> <i>и</i> in litteris <i>Spart</i>; aliquem in jure civili <i>C</i>; ad Graecorum disciplinam <i>C</i>; aliquem omni disciplina <i>Nep</i>; aliquem damnosas artes <i>O</i>);\n<b>2)</b> извещать (aliquem de re aliqua <i>C</i>);\n<b>3)</b> совершенствовать (toreuticen <i>PM</i>)."],
  ["erudite", "<big>erudite</big>\n\n<b>&#275;rud&#299;t&#275;</b> [eruditus]\nучёным образом, просвещённо, искусно (dicere <i>PJ</i>; scribere <i>C</i>)."],
  ["eruditio", "<big>eruditio</big>\n\n<b>&#275;rud&#299;tio, &#333;nis</b> <i>f</i> [erudio]\n<b>1)</b> просвещение, образование, обучение <i>C, AG etc.</i>;\n<b>2)</b> просвещённость, учёность, познания <i>Q, Su etc.</i>: summam eruditionem sitam censere in aliqua re <i>C</i> усматривать в чём-л. высший уровень культуры."],
  ["eruditor", "<big>eruditor</big>\n\n<b>&#275;rud&#299;tor, &#333;ris</b> <i>m</i> [erudio]\nучитель, наставник <i>Tert, Hier</i>."],
  ["eruditrix", "<big>eruditrix</big>\n\n<b>&#275;rud&#299;tr&#299;x, &#299;cis</b> <i>f</i>\nучительница, наставница <i>Fl</i>."],
  ["eruditulus", "<big>eruditulus</big>\n\n<b>&#275;rud&#299;tulus, a, um</b> [<i>demin. к</i> eruditus I] <i>ирон.</i>\nловкий, опытный, знающий толк (<i>в любовных делах</i>) <i>Ctl</i>."],
  ["eruditus 1", "<big>eruditus</big>\n\n<b>I &#275;rud&#299;tus, a, um</b> [erudio]\n<b>1)</b> просвещённый (saecula <i>C</i>); образованный, учёный (aliqua re <i>C etc.</i>): tot doctoribus e. <i>Ap</i> получивший образование у стольких учителей;\n<b>2)</b> искусный, тонкий, опытный (manus <i>Sen</i>; aures <i>C</i>); утончённый (palata <i>Col</i>)."],
  ["eruditus 2", "<big>eruditus</big>\n\n<b>II &#275;rud&#299;tus, &#363;s</b> <i>m</i>\nобучение <i>Tert</i>."],
  ["erugatio", "<big>erugatio</big>\n\n<b>&#275;r&#363;g&#257;tio, &#333;nis</b> <i>f</i> [erugo I]\nудаление морщин, разглаживание (cutis <i>PM</i>)."],
  ["erugo 1", "<big>erugo</big>\n\n<b>I &#275;–r&#363;go, —, —, &#257;re</b>\nудалять морщины, разглаживать (cutem in facie <i>PM</i>)."],
  ["erugo 2", "<big>erugo</big>\n\n<b>II &#275;r&#363;go, (r&#363;x&#299;), r&#363;ctum, ere</b>\nизрыгать, <i>перен.</i> извергать, изливать (fontes, quibus sese erugit aquae vis <i>Enn</i>)."],
  ["Eruli", "<big>Eruli</big>\n\n<b>Erul&#299;</b> <i>v. l.</i>\n= Heruli."],
  ["erumpo", "<big>erumpo</big>\n\n<b>&#275;–rumpo, r&#363;p&#299;, ruptum, ere</b>\n<b>1)</b> прорываться наружу, вырываться (ignes ex Aetnae vertice erumpunt <i>C</i>); разражаться (risus repente erupit <i>C</i>; furor erumpit <i>C</i>); брызнуть (sanguis erupit <i>Lcn</i>); обнаруживаться (conjuratio erupit <i>C, Q</i>); прорастать (folium e latere erumpit <i>PM</i>); прорезываться (dentes erumpunt <i>PM</i>); вспыхивать (seditio erupit <i>L</i>): e. portis <i>Sl, L</i> (ex urbe, ex castris <i>Cs</i>) устремиться (броситься) из ворот (города, лагеря) || стремительно исчезать, бежать (Catilina erupit <i>C</i>); <i>воен.</i> сделать вылазку (внезапное нападение) (in hostem <i>L</i>); пробиваться (e. per hostes <i>L</i>);\n<b>2)</b> внезапно перейти, впасть (ad majora vitia <i>Su</i>; ad minas <i>T</i>; in jurgia <i>Just</i>; in omne genus crudelitatis <i>Su</i>);\n<b>3)</b> вылиться, привести (ad <i>или</i> in aliquid <i>C, L etc.</i>): quo haec eruptura sint <i>C</i> (не знаю), каков будет исход (чем это кончится);\n<b>4)</b> выводить наружу, выбрасывать, извергать (faucibus ignes erupti <i>Lcr</i>): e. iram (stomachum <i>C</i>, iracundiam <i>Cs</i>) in aliquem <i>L</i> излить (обрушить) свой гнев на кого-л.; e. se вырваться, броситься (portis se foras <i>Cs</i>);\n<b>5)</b> прорывать (nubem <i>V</i>)."],
  ["erunco", "<big>erunco</big>\n\n<b>&#275;–runco, —, —, &#257;re</b>\nполоть, очищать от сорняков (areas <i>Col</i>); выпалывать (herbas <i>Col</i>)."],
  ["eruo", "<big>eruo</big>\n\n<b>&#275;–ruo, ru&#299;, rutum, ere</b>\n<b>1)</b> выкапывать, вырывать (corporum reliquias <i>T</i>; aurum terra <i>O</i>; annoso cum robore quercum <i>V</i>);\n<b>2)</b> отыскивать, открывать (argumenta <i>C</i>; arcana <i>SenT</i>; veritatem <i>Q</i>); discordiae eruebantur <i>T</i> обнаружились (возникли) разногласия || извлекать (aliquid ex tenebris <i>C</i>);\n<b>3)</b> исторгать, отнимать: hoc mihi erui non potest <i>C</i> от этого меня отговорить нельзя;\n<b>4)</b> избавлять (aliquem difficultate nummaria <i>C</i>);\n<b>5)</b> взрывать, разрывать (humum <i>O</i>; segetem <i>V</i>); бороздить (aquam remis <i>O</i>);\n<b>6)</b> вырвать, выколоть (alicui oculos <i>Sen etc.</i>); проколоть, пронзить (latus hasta <i>O</i>);\n<b>7)</b> срыть до основания, разрушать, разорять (urbem, regnum <i>V</i>; Corinthum funditus <i>VP</i>)."],
  ["erupi", "<big>erupi</big>\n\n<b>&#275;r&#363;p&#299;</b> <i>pf. к</i>\nerumpo."],
  ["eruptio", "<big>eruptio</big>\n\n<b>&#275;ruptio, &#333;nis</b> <i>f</i> [erumpo]\n<b>1)</b> внезапное выступление, появление (vitiorum <i>Sen</i>): e. sanguinis <i>PM</i> внезапное кровотечение; e. Aethnaeorum ignium <i>C</i> внезапное извержение Этны;\n<b>2)</b> <i>воен.</i> вылазка, нападение (hostium <i>L</i>; ex oppido eruptionem facere <i>Cs</i>); набег, вторжение (in provinciam eruptionem facere <i>Cs</i>);\n<b>3)</b> прорастание (seminis <i>PM</i>);\n<b>4)</b> <i>мед.</i> высыпание (papularum <i>PM</i>)."],
  ["erus", "<big>erus</big>\n\n<b>erus (herus), &#299;</b> <i>m</i>\n<b>1)</b> хозяин, господин <i>Pl, Ter, C etc.</i>;\n<b>2)</b> владелец, обладатель (propriae telluris <i>H</i>);\n<b>3)</b> супруг <i>Ctl</i>;\n<b>4)</b> <i>поэт.</i> властитель, повелитель (caelestes eri <i>Ctl</i>): invitis eris <i>Ctl</i> против воли богов."],
  ["eruscus", "<big>eruscus</big>\n\n<b>&#275;ruscus, &#299;</b> <i>m</i>\nежевика <i>Ap</i>."],
  ["erutor", "<big>erutor</big>\n\n<b>&#275;rutor, &#333;ris</b> <i>m</i> [eruo]\nосвободитель, избавитель <i>Aug</i>."],
  ["ervilia", "<big>ervilia</big>\n\n<b>ervilia (ervilla), ae</b> <i>f</i> [ervum]\nтурецкий горох (<i>разновидность</i>) <i>Vr, Col, Pt</i>."],
  ["ervum", "<big>ervum</big>\n\n<b>ervum, &#299;</b> <i>n</i>\nвиковая чечевица (<i>Vicia <i>или</i> Ervum Ervilia</i>, L.) <i>Pl, H, V etc.</i>"],
  ["Erycina", "<big>Erycina</big>\n\n<b>Eryc&#299;na, ae</b> <i>f</i> (<i>sc.</i> dea)\nЭрицина (<i>эпитет Венеры</i>) <i>H, C, O</i>."],
  ["Erycinus", "<big>Erycinus</big>\n\n<b>Eryc&#299;nus, a, um</b> [Eryx]\nэрикский, эрицинский (Venus <i>C</i>; concha <i>Prp</i>)."],
  ["Erycus mons", "<big>Erycus mons</big>\n\n<b>Erycus m&#333;ns</b> <i>C, T</i>\n= Eryx 2."],
  ["Erymantheus", "<big>Erymantheus</big>\n\n<b>Erymanth&#275;us (-ius), a, um</b> [Erymanthus]\nэриманфский (aper <i>C</i>)."],
  ["Erymanthias", "<big>Erymanthias</big>\n\n<b>Erymanthias, adis</b> <i>f</i> [Erymanthus]\nэриманфская (nymphae <i>St</i>)."],
  ["Erymanthis", "<big>Erymanthis</big>\n\n<b>Erymanthis, idos</b> <i>adj. f к</i>\nErymanthus: E. ursa <i>O</i> = Callisto; custos ursae Erymanthidos <i>O</i> = Bootes."],
  ["Erymanthus", "<big>Erymanthus</big>\n\n<b>Erymanthus (-os), &#299;</b> <i>m</i>\nЭриманф:\n<b>1)</b> <i>гора на границе Аркадии с Ахайей и Элидой, известная в мифологии как убежище свирепого Эриманфского вепря</i> (<i>убитого впоследствии Геркулесом</i>) <i>V, O, St</i>;\n<b>2)</b> <i>река в Аркадии и Элиде, приток Алфея</i> <i>O</i>."],
  ["erynge", "<big>erynge</big>\n\n<b>&#275;ryng&#275;, &#275;s</b> <i>f</i> <i>и</i> <b>&#275;ryngion (-um), &#299;</b> <i>n</i> (<i>греч.</i>) <i>бот. предпол.</i>\nсинеголовник (<i>Eryngium campestre</i>, L.) <i>PM, Col</i>."],
  ["Erysichthon", "<big>Erysichthon</big>\n\n<b>Erysichth&#333;n, onis</b> <i>m</i>\nЭрисихтон, <i>сын Триопа, царя Фессалии, срубивший священные деревья в роще Цереры и наказанный за это неутолимым голодом</i> <i>O</i>."],
  ["erysipelas", "<big>erysipelas</big>\n\n<b>erysipelas, atis</b> <i>n</i> (<i>греч.</i>)\nрожистое воспаление кожи <i>CC</i>."],
  ["Erythea", "<big>Erythea</big>\n\n<b>Eryth&#275;a (-&#299;a), ae</b> <i>f</i>\nЭритея, о-в в <i>устье Гвадалквивира, родина &laquo;трёхтелого&raquo; Гериона</i> <i>Prp, Mela</i>."],
  ["Erytheis", "<big>Erytheis</big>\n\n<b>Eryth&#275;is, idis</b> <i>f</i> [Erythea]\nэритейская <i>O</i>."],
  ["Erytheus", "<big>Erytheus</big>\n\n<b>Eryth&#275;us (-&#299;us), a, um</b> [Erythea]\nэритейский <i>Sil</i>."],
  ["Erythia", "<big>Erythia</big>\n\n<b>Eryth&#299;a</b> <i>v. l.</i>\n= Erythea."],
  ["Erythrae", "<big>Erythrae</big>\n\n<b>Erythrae, &#257;rum</b> <i>f</i>\nЭритры:\n<b>1)</b> <i>город в Беотии на Кифероне, к вост. от Платеи</i> <i>PM, St</i>;\n<b>2)</b> <i>приморский город в Ионии, один из 12 ионических городов Малой Азии</i> <i>C, L</i>;\n<b>3)</b> <i>город в Этолии к вост. от Навпакта</i> <i>L</i>."],
  ["Erythraea", "<big>Erythraea</big>\n\n<b>Erythraea, ae</b> <i>f</i> (<i>sc.</i> terra)\nЭритрея, <i>область города</i> Erythrae 2 <i>L</i>."],
  ["Erythraeus 1", "<big>Erythraeus</big>\n\n<b>I Erythraeus, a, um</b> [Erythrae <i>и</i> Erythras]\nэритрейский (Sibylla <i>Vr, C</i>); <i>перен.</i> индийский (litus <i>Tib</i>; orae <i>St</i>): dens E. <i>M</i> слоновая кость."],
  ["erythraeus 2", "<big>erythraeus</big>\n\n<b>II erythraeus, a, um</b> (<i>греч.</i>)\nкрасноватый, рыжий (oves <i>Col</i>)."],
  ["Erythras", "<big>Erythras</big>\n\n<b>Erythr&#257;s, ae</b> <i>или</i> <b>Erythrus, &#299;</b> <i>m</i>\nЭритр, <i>миф. царь Аравии и Персии, по имени которого якобы названо mare Erythraeum, т. е. Красное море</i> (<i>mare Rubrum</i>) <i>Mela, PM, Sol</i>."],
  ["Eryx", "<big>Eryx</big>\n\n<b>Eryx, ycis</b> <i>m</i>\nЭрик:\n<b>1)</b> <i>сын Венеры, убитый Геркулесом и погребённый под горой, получившей его имя</i> <i>V</i>;\n<b>2)</b> <i>гора и город на сев.-зап. побережье Сицилии</i> (<i>с древним культом Венеры</i>) <i>Nep, L, O etc.</i>"],
  ["es 1", "<big>es</big>\n\n<b>I es</b> <i>2 л. sg. praes. к</i>\nsum."],
  ["es 2", "<big>es</big>\n\n<b>II &#275;s</b> <i>2 л. sg. praes. к</i>\nedo II."],
  ["esca", "<big>esca</big>\n\n<b>&#275;sca, ae</b> <i>f</i> [edo II]\n<b>1)</b> пища, еда, корм <i>Pl, H, Col etc.</i>: escis et potionibus vesci <i>C</i> есть и пить;\n<b>2)</b> приманка, наживка (imponere hamis escam <i>Pt</i>);\n<b>3)</b> <i>перен.</i> соблазн (voluptas — e. malorum <i>C</i>)."],
  ["escalis", "<big>escalis</big>\n\n<b>&#275;sc&#257;lis, e</b> [esca]\nотносящийся к еде: argentum escale <i>Dig</i> столовое серебро."],
  ["escaria", "<big>escaria</big>\n\n<b>&#275;sc&#257;ria, &#333;rum</b> <i>n</i> [escarius]\nстоловая посуда <i>J</i>."],
  ["escarius", "<big>escarius</big>\n\n<b>&#275;sc&#257;rius, a, um</b> [esca]\n<b>1)</b> относящийся к пище, пищевой, предназначенный для еды, столовый (vasa, uva <i>PM</i>): escaria mensa <i>Vr</i> обеденный стол;\n<b>2)</b> связанный с приманкой: escaria vincla <i>Pl</i> соблазн хорошо покушать."],
  ["escatilis", "<big>escatilis</big>\n\n<b>&#275;sc&#257;tilis, e</b> [esca]\nсъедобный, годный в пищу <i>Tert</i>."],
  ["escendo", "<big>escendo</big>\n\n<b>&#275;–scendo, scend&#299;, sc&#275;nsum, ere</b> [e + scando]\n<b>1)</b> всходить, подниматься (in navem <i>Nep</i> <i>и</i> navem <i>Caec</i>; in arcem <i>L</i>; in rostra <i>C</i> <i>и</i> rostra <i>T</i>; in equos <i>L</i> <i>и</i> equos <i>Sol</i>; in contionem <i>L</i>); взлезать, вскарабкиваться (in malum <i>Vr</i>);\n<b>2)</b> идти, ехать, отправляться (Delphos, Pergamum <i>L</i>)."],
  ["escensio", "<big>escensio</big>\n\n<b>&#275;sc&#275;nsio, &#333;nis</b> <i>f</i> [escendo]\nвыгрузка (высадка) с корабля, выход на берег (escensionem facere ab navibus in terram <i>L</i>)."],
  ["escensus", "<big>escensus</big>\n\n<b>&#275;sc&#275;nsus, &#363;s</b> <i>m</i> [escendo]\nвосхождение, подъём (munimenta escensu capere <i>T</i>)."],
  ["eschara", "<big>eschara</big>\n\n<b>eschara, ae</b> <i>f</i> (<i>греч.</i>)\n<b>1)</b> основание баллисты <i>Vtr</i>;\n<b>2)</b> <i>мед.</i> короста, струп(ья) <i>CA, Scr</i>."],
  ["escharosis", "<big>escharosis</big>\n\n<b>eschar&#333;sis, is</b> <i>f</i> (<i>греч.</i>)\nобразование коросты <i>CA</i>."],
  ["escifer", "<big>escifer</big>\n\n<b>&#275;scifer, fera, ferum</b> [esca + fero]\nдающий пищу (volucres <i>Eccl</i>)."],
  ["escit", "<big>escit</big>\n\n<b>&#275;scit</b> <i>арх. LXIIT ap. AG, Lcr</i>\n= erit (<i>3 л. sg. fut. к</i> sum)."],
  ["esco", "<big>esco</big>\n\n<b>&#275;sco, —, —, &#257;re</b>\nкушать, есть <i>Sol</i>."],
  ["esculenta", "<big>esculenta</big>\n\n<b>&#275;sculenta, &#333;rum</b> <i>n</i> [esculentus]\nсъестное <i>C, AG</i>."],
  ["esculentia", "<big>esculentia</big>\n\n<b>&#275;sculentia, ae</b> <i>f</i>\nпитание, пища <i>Hier</i>."],
  ["esculentus", "<big>esculentus</big>\n\n<b>&#275;sculentus, a, um</b> [esca]\n<b>1)</b> съедобный, годный в пищу (animalia <i>PM</i>; frusta <i>C</i>): vasa ad esculentos usus <i>PM</i> столовая посуда;\n<b>2)</b> питательный (esculentioribus cibis abstinere <i>Hier</i>);\n<b>3)</b> полный пищи (os <i>PM</i>)."],
  ["esculetum", "<big>esculetum</big>\n\n<b>escul&#275;tum, &#299;</b> <i>n</i> <i>v. l.</i>\n= aesculetum."],
  ["esculeus", "<big>esculeus</big>\n\n<b>esculeus, a, um</b> <i>v. l.</i>\n= aesculeus."],
  ["esculus", "<big>esculus</big>\n\n<b>esculus, &#299;</b> <i>f</i> <i>v. l.</i>\n= aesculus."],
  ["escunt", "<big>escunt</big>\n\n<b>escunt</b> <i>арх. C</i>\n= erunt (<i>3 л. pl. fut. к</i> sum)."],
  ["esilio", "<big>esilio</big>\n\n<b>&#275;silio</b> <i>v. l.</i>\n= exsilio."],
  ["esito", "<big>esito</big>\n\n<b>&#275;sito (&#275;ssito), &#257;v&#299;, —, &#257;re</b> [<i>frequ. к</i> edo II]\n(обыкновенно, часто) есть, едать (brassicam <i>Cato</i>; panem <i>AG</i>)."],
  ["esor", "<big>esor</big>\n\n<b>&#275;sor, &#333;ris</b> <i>m</i> [edo II]\nедок, пожиратель (prandiorum optimorum <i>Fronto</i>)."],
  ["esox", "<big>esox</big>\n\n<b>esox, ocis</b> <i>m</i> (<i>греч.</i>) <i>предпол.</i>\nщука <i>PM</i>."],
  ["Esquiliae", "<big>Esquiliae</big>\n\n<b>Esquiliae, &#257;rum</b> <i>f</i>\nЭсквилинский холм, <i>самый большой и высокий из семи холмов Рима, в сев.-вост. части города</i> <i>Vr, C</i>."],
  ["Esquilinus", "<big>Esquilinus</big>\n\n<b>Esquil&#299;nus, Esquili&#257;rius</b> <i>и</i> <b>Esquilius, a, um</b> [Esquiliae]\nэсквилинский: E. campus <i>C, H</i> Эсквилинское поле (<i>место погребений, преим. беднейшего населения</i>)."],
  ["esse 1", "<big>esse</big>\n\n<b>I esse</b> <i>inf. praes. к</i>\nsum."],
  ["esse 2", "<big>esse</big>\n\n<b>II &#275;sse</b> <i>inf. praes. к</i>\nedo II."],
  ["esseda", "<big>esseda</big>\n\n<b>esseda, ae</b> <i>f</i> <i>Sen</i>\n= essedum."],
  ["essedaria", "<big>essedaria</big>\n\n<b>essed&#257;ria, ae</b> <i>f</i> (<i>sc.</i> mulier)\nженщина-возница <i>Pt</i>."],
  ["essedarius", "<big>essedarius</big>\n\n<b>essed&#257;rius, &#299;</b> <i>m</i> [essedum]\nвоин <i>или</i> гладиатор, сражающийся с колесницы <i>Cs, Su, Pt</i>."],
  ["essedum", "<big>essedum</big>\n\n<b>essedum, &#299;</b> <i>n</i> (<i>кельт.</i>)\n<b>1)</b> боевая двухколёсная колесница (<i>у галлов, бельгов и британцев</i>) <i>Cs, V, L</i>;\n<b>2)</b> дорожный экипаж (<i>у римлян</i>) <i>C, O</i>."],
  ["essentia", "<big>essentia</big>\n\n<b>essentia, ae</b> <i>f</i> [sum]\n<b>1)</b> сущность <i>C ap. Sen, Q, Ap, Aug</i>;\n<b>2)</b> бытие <i>Aug</i>."],
  ["essentialis", "<big>essentialis</big>\n\n<b>essenti&#257;lis, e</b> [essentia]\nсущественный, относящийся к сущности <i>Is</i>."],
  ["essentialiter", "<big>essentialiter</big>\n\n<b>essenti&#257;liter</b> [essentialis]\nсущественным образом, по существу <i>Aug</i>."],
  ["essito", "<big>essito</big>\n\n<b>&#275;ssito</b> <i>Cato, Pl v. l.</i>\n= esito."],
  ["essu", "<big>essu</big>\n\n<b>&#275;ss&#363;</b> (= esu) <i>Pl supin. к</i>\nedo II."],
  ["essurio", "<big>essurio</big>\n\n<b>&#275;ssurio</b> <i>v. l.</i>\n= esurio I, II."],
  ["est 1", "<big>est</big>\n\n<b>I est</b> <i>3 л. sg. praes. к</i>\nsum."],
  ["est 2", "<big>est</big>\n\n<b>II &#275;st</b> <i>3 л. sg. praes. к</i>\nedo II."],
  ["esto", "<big>esto</big>\n\n<b>est&#333;</b> <i>2 и 3 л. sg. imper. fut. к</i>\nsum."],
  ["estrix", "<big>estrix</big>\n\n<b>&#275;str&#299;x, &#299;cis</b> <i>f</i> [edo II]\nпрожорливая женщина, обжора <i>Pl</i> (<i>v. l. к</i> ambestrix)."],
  ["estur", "<big>estur</big>\n\n<b>&#275;stur</b> <i>impers.</i> [<i>арх. 3 л. <i>sg. praes.</i> indic.</i> <i>pass. к</i> edo II]\nestur et potatur <i>Ap</i> едят и пьют."],
  ["esu", "<big>esu</big>\n\n<b>&#275;s&#363;</b> <i>v. l.</i>\n= essu."],
  ["Esubii", "<big>Esubii</big>\n\n<b>Esubi&#299;</b> <i>v. l.</i>\n= Esuvii."],
  ["esud-", "<big>esud-</big>\n\n<b>es&#363;d-</b> <i>v. l.</i>\n= exsud-."],
  ["esum", "<big>esum</big>\n\n<b>&#275;sum (essum)</b> <i>supin. к</i>\nedo II."],
  ["esurialis", "<big>esurialis</big>\n\n<b>&#275;suri&#257;lis, e</b> [esuries]\nголодный (feriae <i>Pl</i>)."],
  ["esurienter", "<big>esurienter</big>\n\n<b>&#275;surienter</b> [esurio I]\nс (голодной) жадностью (escas appetere <i>Ap</i>)."],
  ["esuries", "<big>esuries</big>\n\n<b>&#275;suri&#275;s, &#275;&#299;</b> <i>f</i> [esurio I]\nголод <i>Hier, Sid</i>."],
  ["esurigo", "<big>esurigo</big>\n\n<b>&#275;sur&#299;go, inis</b> <i>f</i> [esurio I]\nголод <i>Vr</i>."],
  ["esurio 1", "<big>esurio</big>\n\n<b>I &#275;surio (essurio), &#299;v&#299;, (&#299;tum), &#299;re</b> [<i>desiderat. к</i> edo II]\n<b>1)</b> хотеть есть, быть голодным: cum esuriente panem suum dividere <i>Sen</i> делить свой хлеб с голодным;\n<b>2)</b> терпеть голод, голодать (e. consuere <i>Sen</i>);\n<b>3)</b> сильно желать, жаждать, алкать (aliquid <i>PM, Pt etc.</i> <i>и</i> alicujus rei <i>Ap</i>): nil ibi, quod nobis esuriatur, erit <i>O</i> ничего там не будет (такого), что пришлось бы нам по вкусу;\n<b>4)</b> жадно впитывать (vellera esuriunt, <i>sc.</i> purpuram <i>PM</i>)."],
  ["esurio 2", "<big>esurio</big>\n\n<b>II &#275;surio, &#333;nis</b> <i>m</i> [esurio I]\nголодный человек, голодающий <i>Pl</i>."],
  ["esuritio", "<big>esuritio</big>\n\n<b>&#275;sur&#299;tio, &#333;nis</b> <i>f</i> [esurio I]\nголодание, жизнь впроголодь <i>Ctl, M, Pt</i>."],
  ["esuritor", "<big>esuritor</big>\n\n<b>&#275;sur&#299;tor, &#333;ris</b> <i>m</i> <i>M</i>\n= esurio II."],
  ["esus 1", "<big>esus</big>\n\n<b>I &#275;sus, a, um</b> <i>part. pf. к</i>\nedo II."],
  ["esus 2", "<big>esus</big>\n\n<b>II &#275;sus, &#363;s</b> <i>m</i> [edo II]\nеда, питание <i>Vr, AG, Ap etc.</i>: esui alicui dare <i>CC</i> давать кому-л. поесть."],
  ["Esuvii", "<big>Esuvii</big>\n\n<b>Esuvi&#299; (Esubi&#299;), &#333;rum</b> <i>m</i>\nэзубии, <i>кельтское племя в Арморике</i> <i>Cs</i>."],
  ["et", "<big>et</big>\n\n<b>et</b> <i>conj.</i>\n<b>1)</b> и (mater et soror <i>C</i>): quoad possem et liceret <i>C</i> насколько было для меня возможно и допустимо; et... et... как... так... (et mari et terra <i>Nep</i>); et quidem и притом (duo milia jugerum et quidem immunia <i>C</i>); et non и не (patior et non moleste fero <i>C</i>) <i>или</i> а не (ii, qui nati sunt, et non ii, qui mortui sunt <i>C</i>);\n<b>2)</b> и к тому ещё, и к тому же, и сверх того, и притом (laudat, et saepe, virtutem <i>C</i>; errabas et vehementer errabas <i>C</i>): nec... et... не только не..., но и ... (nec miror et gaudeo <i>C</i>);\n<b>3)</b> также и: quidquid coepit et desinit <i>Sen</i> что началось, (то) также и кончится;\n<b>4)</b> <i>после прил. и нареч., выражающих сходство или несходство</i> (<i>напр.</i> par, idem, similis, aeque, alius) чем, нежели (lux longe alia est solis et lychnorum <i>C</i>)."],
  ["etenim", "<big>etenim</big>\n\n<b>et–enim</b> <i>conj.</i>\nтак как, поскольку, ибо, ведь: video potuisse fieri, ut aerario nihil darent; e. vel Capitolium gratis exaedificari potuit <i>C</i> я считаю возможным, что они ничего из казны не дали; можно ведь было (в своё время) даром построить даже Капитолий."],
  ["Eteocles", "<big>Eteocles</big>\n\n<b>Eteocl&#275;s, is</b> <i>и</i> <b>eos</b> <i>m</i>\nЭтеокл, <i>сын Эдипа и Иокасты, который, после изгнания своего отца, должен был царствовать в Фивах, чередуясь ежегодно со своим братом Полиником; их ссора из-за власти привела к походу &laquo;Семерых против Фив&raquo;, в котором оба брата убили друг друга одновременно</i> <i>C, St</i>."],
  ["Eteocleus", "<big>Eteocleus</big>\n\n<b>Eteocl&#275;us, a, um</b>\nэтеоклов (contentiones <i>Ap</i>)."],
  ["etesiae", "<big>etesiae</big>\n\n<b>et&#275;siae, &#257;rum</b> <i>f</i> [etesius]\nпассатные ветры (<i>дующие с севера и северо-запада в Эгейском море в течение 40 жарких, &laquo;каникулярных&raquo;, дней года</i>) <i>Lcr, C, Cs, L etc.</i>"],
  ["etesius", "<big>etesius</big>\n\n<b>et&#275;sius, a, um</b> (<i>греч.</i>)\nгодовой, ежегодный (flabra aquilonum <i>Lcr</i>)."],
  ["ethe", "<big>ethe</big>\n\n<b>&#275;th&#275;</b> (<i>dat.</i> ethesin) <i>PM pl. к</i>\nethos."],
  ["ethica", "<big>ethica</big>\n\n<b>&#275;thica, ae</b> <i>f</i> <i>Lact</i>\n= ethice."],
  ["ethice", "<big>ethice</big>\n\n<b>&#275;thic&#275;, &#275;s</b> <i>f</i> (<i>греч.</i>)\nучение о нравственности, этика, нравственная философия <i>Q</i>."],
  ["ethicos", "<big>ethicos</big>\n\n<b>&#275;thic&#333;s</b> <i>adv.</i> (<i>греч.</i>)\nэтически, нравственно <i>Sen</i>."],
  ["ethicus", "<big>ethicus</big>\n\n<b>&#275;thicus, a, um</b> (<i>греч.</i>)\nнравственный (controversiae <i>Sen</i>): res ethica <i>AG</i> этика || отражающий нравственные свойства (характер) (dictio <i>Sid</i>)."],
  ["ethnicalis", "<big>ethnicalis</big>\n\n<b>ethnic&#257;lis, e</b> [ethnicus]\nязыческий (litterae <i>Tert</i>)."],
  ["ethnice", "<big>ethnice</big>\n\n<b>ethnic&#275;</b> <i>adv.</i>\nпо--язычески (vivere <i>Tert</i>)."],
  ["ethnicus 1", "<big>ethnicus</big>\n\n<b>I ethnicus, a, um</b> (<i>греч.</i>)\nязыческий <i>Eccl</i>."],
  ["ethnicus 2", "<big>ethnicus</big>\n\n<b>II ethnicus, &#299;</b> <i>m</i>\nязычник <i>Eccl</i>."],
  ["ethologia", "<big>ethologia</big>\n\n<b>&#275;thologia, ae</b> <i>f</i> (<i>греч.</i>)\nописание нравов, изображение характеров <i>Sen, Q, Su</i>."],
  ["ethologus", "<big>ethologus</big>\n\n<b>&#275;thologus, &#299;</b> <i>m</i> (<i>греч.</i>)\nподражающий для забавы чьим-л. привычкам и манерам: mimus e. <i>C</i> мим, исполняющий характерные роли."],
  ["ethos", "<big>ethos</big>\n\n<b>&#275;thos, eos</b> <i>n</i> (<i>греч.</i>)\n<b>1)</b> нравы <i>Vr, PM</i>;\n<b>2)</b> нравственность <i>Sid</i>."],
  ["etiam", "<big>etiam</big>\n\n<b>etiam</b> <i>conj.</i> [et + jam]\n<b>1)</b> также (e. aliae virtutes <i>C</i>); и (e. tum, quum <i>C</i>);\n<b>2)</b> еще, даже ещё (e. major, e. gravius <i>C etc.</i>): hoc e. mirabilius debet videri, quia... <i>C</i> это должно казаться тем более удивительным, что...;\n<b>3)</b> ещё, всё ещё (quum iste e. cubaret <i>C</i>): quamdiu e.? <i>C</i> как долго ещё?; e. atque e. ещё и ещё, неоднократно, усиленно, настоятельно, настойчиво, весьма (hoc te e. atque e. rogo <i>C</i>): e. atque e. hoc cogita <i>Ter</i> хорошенько подумай об этом; non satis me pernosti e., qualis sim <i>Ter</i> ты ещё недостаточно знаешь (меня), что я за человек; non dico fortasse e., quod sentio <i>C</i> я, по-видимому, ещё не так выразил свою мысль;\n<b>4)</b> да, вот именно, конечно, так точно (numquid vis? — E. <i>Pl</i>): aut &laquo;е.&raquo; aut &laquo;non&raquo; respondere <i>C</i> отвечать &laquo;да&raquo; или &laquo;нет&raquo;; quid superest (<i>или</i> praeterea)? E. <i>C</i> что ещё (я хотел сказать)? Да, вот что;\n<b>5)</b> ну, же, ли: e. aperis? <i>Pl</i> откроешь ты или нет? (<i>т. е.</i> да поскорее же открой); e. abis? <i>Ter</i> уйдёшь ты (или нет)?;\n<b>6)</b> в крайнем случае, пусть даже: sestertiis binis aut e. ternis <i>C</i> за два или, в крайнем случае, за три сестерция."],
  ["etiamnum", "<big>etiamnum</big>\n\n<b>etiam–num<span style=\"font-weight:normal\"> <i>и</i> etiam–nunc</b> (<i>тж. раздельно</i>)\n<b>1)</b> даже и теперь, всё ещё (vos dubitatis e.? <i>C</i>);\n<b>2)</b> ещё (nullus e. <i>Cs</i>; e. infirmior <i>PM</i>): quaeritur e. <i>C etc.</i> это ещё вопрос; nihil e. <i>C</i> ничего больше; e. vale <i>Pl</i> ещё раз прощай."],
  ["etiamsi", "<big>etiamsi</big>\n\n<b>etiam–s&#299;</b> <i>conj.</i> (<i>тж. раздельно</i>)\nдаже если, если бы, хотя бы: e. hominum studia deficiant <i>C</i> если бы даже человеческие усилия оказались недостаточными."],
  ["etiamtum", "<big>etiamtum</big>\n\n<b>etiam–tum<span style=\"font-weight:normal\"> <i>и</i> etiam–tunc</b> (<i>тж. раздельно</i>)\nтогда ещё, (тогда) всё ещё: multum diei processerat, quum e. eventus in incerto erat <i>Sl</i> был уже поздний час дня, а исход (боя) всё ещё был неясен; quum e. ceterae naves uno in loco moliebantur <i>C</i> в то время как остальные корабли всё ещё не могли сдвинуться с места."],
  ["Etruria", "<big>Etruria</big>\n\n<b>Etr&#363;ria, ae</b> <i>f</i> (<i>или</i> Tuscia, <i>у греков</i> Tyrrhenia)\nЭтрурия, <i>область на зап. побережье Италии между Тибром, Тирренским морем и Апеннинами</i> (<i>ныне</i> Toscana) <i>V, C, L etc.</i>"],
  ["Etruscus 1", "<big>Etruscus</big>\n\n<b>I Etr&#363;scus, a, um</b> [Etruria]\nэтрусский (mare <i>H</i>; bellum <i>L</i>)."],
  ["Etruscus 2", "<big>Etruscus</big>\n\n<b>II Etr&#363;scus, &#299;</b> <i>m</i>\nэтруск, житель Этрурии <i>C etc.</i>"],
  ["etsi", "<big>etsi</big>\n\n<b>et–s&#299;</b> <i>conj.</i>\n<b>1)</b> хотя и, даже если: e. non auctor, at Conscius <i>Su</i> если (даже) не виновник, то соумышленник; e. satis docui..., tamen... <i>C</i> и хотя я достаточно ясно доказал..., всё же...;\n<b>2)</b> хотя, впрочем: do poenas temeritatis meae. E. quae fuit illa temeritas? <i>C</i> я наказан за своё безрассудство. А, впрочем, в чём состояло это безрассудство?"],
  ["etussio", "<big>etussio</big>\n\n<b>&#275;tussio</b> <i>v. l.</i>\n= extussio."],
  ["etymologia", "<big>etymologia</big>\n\n<b>etymologia, ae</b> <i>f</i> (<i>греч.</i>)\nсловопроизводство, этимология <i>V, Sen, Q etc.</i>"],
  ["etymologice", "<big>etymologice</big>\n\n<b>etymologic&#275;, &#275;s</b> <i>f</i> (<i>греч.</i>)\nучение о словопроизводстве, этимология <i>Vr</i>."],
  ["etymologicus", "<big>etymologicus</big>\n\n<b>etymologicus, a, um</b> (<i>греч.</i>)\nэтимологический <i>AG</i>."],
  ["etymologos", "<big>etymologos</big>\n\n<b>etymologos, &#299;</b> <i>m</i> (<i>греч.</i>)\nэтимолог <i>Vr</i>."],
  ["etymon", "<big>etymon</big>\n\n<b>etymon, &#299;</b> <i>n</i> (<i>греч.</i>)\nподлинное (основное) значение, корень слова (etyma vocum et origines <i>AG</i>)."],
  ["eu", "<big>eu</big>\n\n<b>eu</b> <i>interj.</i> (<i>греч.</i>)\nхорошо!, прекрасно!, великолепно! <i>Pl, Ter, H</i>."],
  ["Euadne", "<big>Euadne</big>\n\n<b>Euadn&#275;, &#275;s</b> <i>f</i>\nЭвадна, <i>жена Капанея, одного из &laquo;Семерых против Фив&raquo;, не пожелавшая пережить своего мужа и бросившаяся в его погребальный костёр</i> <i>Prp, V, O</i>."],
  ["Euagoras", "<big>Euagoras</big>\n\n<b>Euagor&#257;s, ae</b> <i>m</i>\nЭвагор, <i>царь Саламина на Кипре</i> (<i>ок. 400 г. до н. э.</i>) <i>Nep</i>."],
  ["euan 1", "<big>euan</big>\n\n<b>I eu&#257;n (euh&#257;n)</b> <i>interj.</i> (<i>греч.</i>)\nэван! (<i>ликующий возглас в честь Вакха</i>) <i>Enn etc.</i>"],
  ["Euan 2", "<big>Euan</big>\n\n<b>II Eu&#257;n (Euh&#257;n)</b> <i>m</i> <i>Lcr, O, St</i>\n= Bacchus."],
  ["Euander", "<big>Euander</big>\n\n<b>Euander (Euandrus), dr&#299;</b> <i>m</i>\nЭвандр, <i>сын Меркурия и прорицательницы Карменты, отец Палланта, переселившийся до Троянской войны из Аркадии в Италию, где на месте позднейшего Рима основал колонию (Pallanteum); ему приписывалось введение в Италии алфавита, искусств и законов нравственности</i> <i>H, V, O, L</i>."],
  ["Euandrius", "<big>Euandrius</big>\n\n<b>Euandrius, a, um</b> [Euander]\nэвандров (ensis <i>V</i>); <i>перен.</i> римский (regna <i>Sil</i>); палатинский (mons <i>Cld</i>; collis <i>St</i>)."],
  ["Euandrus", "<big>Euandrus</big>\n\n<b>Euandrus</b> <i>v. l.</i>\n= Euander."],
  ["euangelicus", "<big>euangelicus</big>\n\n<b>euangelicus, a, um</b> (<i>греч.</i>)\nевангельский <i>Eccl</i>."],
  ["euangelista", "<big>euangelista</big>\n\n<b>euangelista, ae</b> <i>m</i>\nевангелист <i>Eccl</i>."],
  ["euangelium", "<big>euangelium</big>\n\n<b>euangelium, &#299;</b> <i>n</i> (<i>греч.</i>)\nевангелие <i>Vlg, Eccl</i>."],
  ["euangelizator", "<big>euangelizator</big>\n\n<b>euangeliz&#257;tor, &#333;ris</b> <i>m</i>\nпроповедник евангелия <i>Tert</i>."],
  ["euans", "<big>euans</big>\n\n<b>eu&#257;ns (euh&#257;ns), antis</b> <i>adj.</i>\nвосклицающий &laquo;euan!&raquo; (ел.) (chori <i>Prp</i>; orgia <i>V</i>)."],
  ["euax", "<big>euax</big>\n\n<b>euax</b> <i>interj.</i> (<i>греч.</i>)\nура!, браво! <i>Pl, Vr</i>."],
  ["Eubius", "<big>Eubius</big>\n\n<b>Eubius, &#299;</b> <i>m</i>\nЭвбий, <i>греч. писатель, упоминаемый Овидием.</i>"],
  ["Euboea", "<big>Euboea</big>\n\n<b>Euboea, ae</b> <i>f</i>\nЭвбея, <i>ову побережья Аттики и Беотии</i> <i>Nep, L, O etc.</i>"],
  ["Euboeus", "<big>Euboeus</big>\n\n<b>Euboeus, a, um</b> <i>St</i>\n= Euboicus."],
  ["Euboicus", "<big>Euboicus</big>\n\n<b>Euboicus, a, um</b> [Euboea]\nэвбейский (litus <i>Prp</i>): eu. cultor aquarum <i>O</i> = Glaucus (<i>морской бог</i>); <i>перен.</i> кумский (<i>так как город Кумы был эвбейской колонией</i>) <i>Lcn</i>: urbs Euboica <i>O</i> = Cumae."],
  ["Eubois", "<big>Eubois</big>\n\n<b>Eubois, idis</b> <i>f</i> [Euboea]\nэвбейская (ora <i>St</i>) <i>или</i> кумская (tellus <i>St</i>)."],
  ["Eubuleus", "<big>Eubuleus</big>\n\n<b>Eub&#363;le&#865;us, e&#299;</b> <i>m</i> (<i>греч.</i> &laquo;благой советник&raquo;)\n<i>эпитет Диоскуров</i> <i>C</i>."],
  ["Eubulidas", "<big>Eubulidas</big>\n\n<b>Eub&#363;lid&#257;s (-&#275;s), ae</b> <i>m</i>\nЭвбулид:\n<b>1)</b> <i>учитель Демосфена</i> <i>Ap</i>;\n<b>2)</b> <i>афинский скульптор</i> <i>PM</i>."],
  ["eucharis", "<big>eucharis</big>\n\n<b>eucharis</b> <i>m, f</i> (<i>греч.</i>)\nпрелестный, изящный (lingua <i>Vlg</i>)."],
  ["eucharistia", "<big>eucharistia</big>\n\n<b>eucharistia, ae</b> <i>f</i> (<i>греч.</i>)\n<b>1)</b> благодарение <i>Tert</i>;\n<b>2)</b> евхаристия, таинство причащения <i>Eccl</i>."],
  ["eucharisticon", "<big>eucharisticon</big>\n\n<b>eucharisticon, &#299;</b> <i>n</i> (<i>греч.</i>)\n<b>1)</b> <i>Tert</i> = eucharistia I;\n<b>2)</b> благодарственные стихи (<i>заглавие стихотворения, посвященного императору Домициану</i>) <i>St</i>."],
  ["Euclides", "<big>Euclides</big>\n\n<b>Eucl&#299;d&#275;s, is</b> <i>m</i>\nЭвклид:\n<b>1)</b> <i>основатель Мегарской философской школы, ученик Сократа</i> <i>C, AG, Sid</i>;\n<b>2)</b> <i>математик, создатель научной геометрии, жил в Александрии при первом Птолемее в IV-III вв. до н. э.</i> <i>C</i>."],
  ["eucnemos", "<big>eucnemos</big>\n\n<b>eucn&#275;mos, on</b> (<i>греч.</i>)\nс красивыми голенями <i>или</i> икрами (Amazon <i>PM</i>)."],
  ["eudaemon", "<big>eudaemon</big>\n\n<b>eudaem&#333;n, onis</b> (<i>acc. pl.</i> onas) <i>adj.</i> (<i>греч.</i>; <i>лат.</i> felix)\nблаженный, счастливый (Arabia <i>Mela</i>): Arabes eudaemones <i>Vop</i> жители Счастливой Аравии."],
  ["Eueninus", "<big>Eueninus</big>\n\n<b>Eu&#275;n&#299;nus, a, um</b> [Euenus]\nэвенский <i>O</i>."],
  ["Euenus", "<big>Euenus</big>\n\n<b>Eu&#275;nus, &#299;</b> <i>m</i>\nЭвен, <i>река в Этолии близ Калидона</i> (<i>по имени этолийского царя</i>) <i>O, PM</i>."],
  ["euerganeus", "<big>euerganeus</big>\n\n<b>euerg&#257;neus, a, um</b> (<i>греч.</i>)\nхорошо сколоченный, плотно пригнанный (trabes <i>Vtr</i>)."],
  ["euethes", "<big>euethes</big>\n\n<b>eu&#275;th&#275;s</b>, <i>acc.</i> en <i>adj. m, f</i> (<i>греч.</i>)\nпростоватый, недалёкий <i>Amm</i>."],
  ["Euganei", "<big>Euganei</big>\n\n<b>Eugane&#299;, &#333;rum</b> <i>m</i>\nэвганеи, <i>народность, жившая прежде в области венетов и вытесненная последними в Альпы</i> <i>L, PM, Sid</i>."],
  ["Euganeus", "<big>Euganeus</big>\n\n<b>Eug&#257;neus, a, um</b> [Euganei]\nэвганейский (gentes <i>PM</i>; montes <i>Sid</i>)."],
  ["euge", "<big>euge</big>\n\n<b>euge</b> <i>interj.</i> (<i>греч.</i>)\nхорошо!, прекрасно!, великолепно! <i>Pl, Ter, M, Pers, Vlg</i>."],
  ["eugeneus", "<big>eugeneus</big>\n\n<b>eugen&#275;us (-&#299;us), a, um</b> (<i>греч.</i>)\nблагородный, высшего сорта (vinum <i>Cato</i>; uva <i>Col</i>)."],
  ["eugepae", "<big>eugepae</big>\n\n<b>eugepae</b> <i>interj.</i> <i>Pl</i>\n= euge."],
  ["eugium", "<big>eugium</big>\n\n<b>eug&#299;um (-on), &#299;</b> <i>n</i> (<i>греч.</i>) <i>анат.</i>\nвлагалище <i>LM</i>."],
  ["euhan 1", "<big>euhan</big>\n\n<b>I euh&#257;n</b> <i>interj.</i> <i>v. l.</i>\n= euan."],
  ["Euhan 2", "<big>Euhan</big>\n\n<b>II Euh&#257;n</b> <i>m</i> <i>v. l.</i>\n= Euan."],
  ["euhans", "<big>euhans</big>\n\n<b>euh&#257;ns, antis</b> <i>v. l.</i>\n= euans."],
  ["Euhemerus", "<big>Euhemerus</big>\n\n<b>Euh&#275;merus, &#299;</b> <i>m</i>\nЭвгемер, <i>греч. философ, близкий к киренской школе, живший при дворе Кассандра Македонского (ок. 300 г. до н. э.); богов греч. религии он считал обожествлёнными людьми</i> <i>Vr, C, Eccl</i>."],
  ["Euhias", "<big>Euhias</big>\n\n<b>Eu(h)ias, adis</b> <i>f</i> (<i>греч.</i>)\nвакханка <i>H</i>."],
  ["Euhippe", "<big>Euhippe</big>\n\n<b>Eu(h)ipp&#275;, &#275;s</b> <i>f</i>\nЭвгиппа, <i>жена Пиера, мать Нереид</i> <i>O</i>."],
  ["Euhius", "<big>Euhius</big>\n\n<b>Eu(h)ius, &#299;</b> <i>m</i> (<i>греч.</i> &laquo;взывающий euoe&raquo;, &laquo;ликующий&raquo;)\n<i>эпитет Вакха</i> <i>Lcr, C, Pt</i>."],
  ["euhoe", "<big>euhoe</big>\n\n<b>euhoe</b> <i>interj.</i> <i>Ctl, V, O, H etc.</i>\n= euan."],
  ["eulogia", "<big>eulogia</big>\n\n<b>eulogia, ae</b> <i>f</i>\n<b>1)</b> подношение, дар <i>Aug</i>;\n<b>2)</b> освящённый хлеб <i>Eccl</i>."],
  ["eum", "<big>eum</big>\n\n<b>eum</b> <i>acc. sg. к</i>\nis."],
  ["eumdem", "<big>eumdem</big>\n\n<b>eumdem</b> <i>acc. sg. m</i> <i>к</i>\nidem."],
  ["Eumedes", "<big>Eumedes</big>\n\n<b>Eum&#275;d&#275;s, is</b> <i>m</i>\nЭвмед, <i>троянец, отец Долона</i> <i>V</i>."],
  ["Eumelus", "<big>Eumelus</big>\n\n<b>Eum&#275;lus, &#299;</b> <i>m</i>\nЭвмел:\n<b>1)</b> <i>царь Патр, друг Триптолема</i> <i>O</i>;\n<b>2)</b> <i>троянец, один из спутников Энея</i> <i>V</i>."],
  ["Eumenes", "<big>Eumenes</big>\n\n<b>Eumen&#275;s, is</b> <i>m</i>\nЭвмен:\n<b>1)</b> <i>полководец Александра Македонского, после смерти последнего — наместник в Пафлагонии и Каппадокии; в 316 г. до н. э. убит Антигоном</i> <i>Nep, Just, QC</i>;\n<b>2)</b> <i>царь Пергама с 263 по 241 г. до н. э., одержавший победу над Антиохом Сотером</i> <i>Fl</i>;\n<b>3)</b> <i>царь Пергама с 197 по 159 г. до н. э., поддержавший римлян в их войне с Антиохом</i> <i>Fl</i>."],
  ["Eumenides", "<big>Eumenides</big>\n\n<b>Eumenides, um</b> (<i>sg.</i> Eumenis, idis) <i>f</i> (<i>греч.</i>)\nЭвмениды (<i>доел.</i> благосклонные) <i>C, H, V, Sil, St</i> = Furiae."],
  ["Eumolpidae", "<big>Eumolpidae</big>\n\n<b>Eumolpidae, &#257;rum</b> <i>m</i>\nпотомки Эвмолпа, <i>т. е. древний жреческий род в Афинах</i> <i>C, Nep</i>."],
  ["Eumolpus", "<big>Eumolpus</big>\n\n<b>Eumolpus, &#299;</b> <i>m</i>\nЭвмолп, <i>сын Нептуна, царь Фракии, переселившийся в Аттику и основавший там культ Деметры (Цереры) и Элевсинские мистерии</i> <i>PM, O</i>."],
  ["eumpse", "<big>eumpse</big>\n\n<b>eumpse</b> <i>Pl усил.</i>\n= eum."],
  ["euntis", "<big>euntis</big>\n\n<b>euntis</b> <i>gen. sg. к</i>\niens (<i>от</i> eo 1)."],
  ["eunuchinus", "<big>eunuchinus</big>\n\n<b>eun&#363;ch&#299;nus, a, um</b> [eunuchus]\nскопческий (facies <i>Hier</i>)."],
  ["eunuchismus", "<big>eunuchismus</big>\n\n<b>eun&#363;chismus, &#299;</b> <i>n</i>\nоскопление <i>CA</i>."],
  ["eunuchizo", "<big>eunuchizo</big>\n\n<b>eun&#363;chizo, —, —, &#257;re</b> <i>Eccl</i>\n= eunucho."],
  ["eunucho", "<big>eunucho</big>\n\n<b>eun&#363;cho, —, —, &#257;re</b> [eunuchus]\nоскоплять (aliquem <i>Vr</i>)."],
  ["eunuchus", "<big>eunuchus</big>\n\n<b>eun&#363;chus, &#299;</b> <i>m</i> (<i>греч.</i>)\nскопец, евнух <i>Ter, C, Cs etc.</i>"],
  ["euoe", "<big>euoe</big>\n\n<b>euoe</b> <i>interj.</i> <i>Ctl, V</i>\n= euan."],
  ["eupatereia", "<big>eupatereia</big>\n\n<b>eupatereia, ae</b> <i>f</i> (<i>греч.</i>)\nдочь благородного отца (Helena <i>LM</i>)."],
  ["eupatoria", "<big>eupatoria</big>\n\n<b>eupatoria, ae</b> <i>f</i> (<i>греч.</i>; <i>лат.</i> agri monia)\nрепейник (<i>Agrimonia Eupatoria</i>, L.) <i>PM</i>."],
  ["euphorbeum", "<big>euphorbeum</big>\n\n<b>euphorbeum, &#299;</b> <i>n</i> <i>и</i> <b>euphorbia, ae</b> <i>f</i> (<i>греч.</i>)\nмолочай (<i>Euphorbia</i>, L.) <i>PM, Sol</i>."],
  ["Euphorbus", "<big>Euphorbus</big>\n\n<b>Euphorbus, &#299;</b> <i>m</i>\nЭвфорб, <i>троянец, сын Панфоя, ранивший Патрокла, а затем убитый Менелаем</i> (<i>Пифагор утверждал, что этот Эвфорб был одним из его прошлых перевоплощений</i>) <i>O, AG</i>."],
  ["Euphorion", "<big>Euphorion</big>\n\n<b>Euphori&#333;n, &#333;nis</b> <i>m</i>\nЭвфорион, <i>родом из Халкиды (Эвбея), поэт и библиотекарь Антиоха Великого (ок. 250 г. до н. э.</i>) <i>C, Q, Su</i>."],
  ["Euphranor", "<big>Euphranor</big>\n\n<b>Euphr&#257;n&#333;r, oris</b> <i>m</i>\nЭвфранор, <i>живописец и скульптор, современник Праксителя</i> <i>Vr, Q, J, PM</i>."],
  ["Euphrataeus", "<big>Euphrataeus</big>\n\n<b>Euphr&#257;taeus, a, um</b> [Euphrates]\nевфратский, <i>перен.</i> армянский (diademata <i>St</i>)."],
  ["Euphrates", "<big>Euphrates</big>\n\n<b>Euphr&#257;t&#275;s, is</b> (<i>тж.</i> <b>&#299;</b> <i>и</i> <b>ae</b>) <i>m</i>\nЕвфрат, <i>одна из двух больших рек Месопотамии</i> <i>C, T etc.</i>"],
  ["Euphrosyne", "<big>Euphrosyne</big>\n\n<b>Euphrosyn&#275;, &#275;s</b> <i>и</i> <b>Euphrosyna, ae</b> <i>f</i> (<i>греч.</i> &laquo;радость&raquo;)\nЭвфросина, <i>одна из трёх Граций</i> <i>Sen</i>."],
  ["euplocamos", "<big>euplocamos</big>\n\n<b>euplocamos, on</b> <i>adj.</i> (<i>греч.</i>)\nпрекраснокудрый, с красивыми локонами <i>LM</i>."],
  ["Eupolis", "<big>Eupolis</big>\n\n<b>Eupolis, idis</b> (<i>acc.</i> im, in <i>и</i> idem) <i>m</i>\nЭвполид, <i>представитель староаттической комедии, современник Аристофана</i> (<i>ок. 425 г. до н. э.</i>) <i>C, H, Q, Pers</i>."],
  ["eurhythmia", "<big>eurhythmia</big>\n\n<b>eurhythmia, ae</b> <i>f</i> (<i>греч.</i>)\nсоразмерность частей, гармония <i>Vtr</i>."],
  ["eurinus", "<big>eurinus</big>\n\n<b>eurinus, a, um</b> (<i>греч.</i>)\nвосточный (ventus <i>Col</i>)."],
  ["Euripides", "<big>Euripides</big>\n\n<b>Eur&#299;pid&#275;s, is</b> <i>и</i> <b>&#299;</b> <i>m</i>\nЭврипид, <i>знаменитый афинский поэт-трагик, умер в Пелле при дворе Архелая Македонского</i> (<i>480-405 гг. до н. э.</i>) <i>Pl, C, Q etc.</i>"],
  ["Euripideus", "<big>Euripideus</big>\n\n<b>Eur&#299;pid&#275;us, a, um</b> [Euripides]\nэврипидов(ский) (carmen <i>C</i>)."],
  ["euripus 1", "<big>euripus</big>\n\n<b>I eur&#299;pus (-os), &#299;</b> <i>m</i> (<i>греч.</i>)\n<b>1)</b> пролив <i>C, PM</i>;\n<b>2)</b> канава, канал <i>C, Sen, O, Su etc.</i>"],
  ["Euripus 2", "<big>Euripus</big>\n\n<b>II Eur&#299;pus (-os), &#299;</b> <i>m</i>\nЭврип, <i>пролив между Эвбеей и европейским материком</i> <i>C, L, Lcn</i>."],
  ["euroaquilo", "<big>euroaquilo</big>\n\n<b>euro–aquilo, &#333;nis</b> <i>m</i> [eurus]\nсеверо-восточный ветер <i>Vlg</i>."],
  ["euroauster", "<big>euroauster</big>\n\n<b>euro–auster, str&#299;</b> <i>m</i> <i>Is</i>\n= euronotus."],
  ["euroborus", "<big>euroborus</big>\n\n<b>euroborus, &#299;</b> <i>m</i> <i>Veg</i>\n= eurocircias."],
  ["eurocircias", "<big>eurocircias</big>\n\n<b>eurocirci&#257;s, ae</b> <i>m</i> (<i>греч.</i>)\nвосточно-юго-восточный ветер <i>Vtr</i>."],
  ["euronotus", "<big>euronotus</big>\n\n<b>euronotus, &#299;</b> <i>m</i> (<i>греч.</i>)\nюго-юго--восточный ветер <i>Col, PM</i>."],
  ["Europa", "<big>Europa</big>\n\n<b>Eur&#333;pa, ae</b> <i>и</i> <b>Europ&#275;, &#275;s</b> (<i>acc.</i> &#275;n) <i>f</i>\nЕвропа:\n<b>1)</b> <i>дочь финикийского царя Агенора, сестра Кадма, похищенная Юпитером, принявшим образ быка, и унесённая им на Крит, где она родила ему Миноса, Радаманта и Сарпедона</i> <i>Vr, C, H</i>;\n<b>2)</b> <i>часть света</i> <i>V, C, Mela, H</i>."],
  ["Europaeus", "<big>Europaeus</big>\n\n<b>Eur&#333;paeus, a, um</b> <i>и</i> <b>Eur&#333;p&#275;nsis, e</b> <i>adj. к</i>\nEuropa 1 <i>и</i> 2 <i>O, Nep, QC etc.</i>"],
  ["Europe", "<big>Europe</big>\n\n<b>Eur&#333;p&#275;, Eur&#333;p&#275;nsis</b> <i>v. l.</i>\n= Europa, Europaeus."],
  ["Europus", "<big>Europus</big>\n\n<b>Eur&#333;pus, &#299;</b> <i>m</i>\nЕвроп, <i>сын Македона и Орифии, миф. царь Македонии</i> <i>Just</i>."],
  ["Eurotas", "<big>Eurotas</big>\n\n<b>Eur&#333;t&#257;s, ae</b> <i>m</i>\nЭврот, <i>главн. река Лаконии</i> <i>C, O</i>."],
  ["eurous", "<big>eurous</big>\n\n<b>eur&#333;us, a, um</b> [eurus]\nвосточный (fluctus <i>V</i>)."],
  ["eurus", "<big>eurus</big>\n\n<b>eurus, &#299;</b> <i>m</i> (<i>греч.</i>; <i>лат.</i> vulturnus)\n<b>1)</b> эвр, юго-восточный ветер <i>L, Sen etc.</i>;\n<b>2)</b> <i>поэт.</i> восточный ветер, <i>тж.</i> буря <i>H, V, St</i>; ветер (<i>вообще</i>): primo sub euro <i>Lcn</i> при первом порыве ветра;\n<b>3)</b> <i>поэт.</i> восток <i>VF, Cld</i>."],
  ["Euryale", "<big>Euryale</big>\n\n<b>Euryal&#275;, &#275;s</b> <i>f</i>\nЭвриала, <i>одна из цариц амазонок</i> <i>VF</i>."],
  ["Euryalus", "<big>Euryalus</big>\n\n<b>Euryalus, &#299;</b> <i>m</i>\nЭвриал, <i>троянец, убитый в ночной стычке с рутулами</i> <i>V</i>."],
  ["Eurybiades", "<big>Eurybiades</big>\n\n<b>Eurybiad&#275;s, is</b> <i>m</i>\nЭврибиад, <i>спартанец, командовавший греч. флотом против Ксеркса</i> (<i>480 г. до н. э.</i>) <i>Nep</i>."],
  ["Eurydamas", "<big>Eurydamas</big>\n\n<b>Eurydam&#257;s, antis</b> <i>m</i> (<i>греч.</i> &laquo;широко укрощающий&raquo;)\nЭвридамант:\n<b>1)</b> <i>собственное имя</i> <i>Sil</i>;\n<b>2)</b> <i>прозвище Гектора</i> <i>O</i>."],
  ["Eurydice", "<big>Eurydice</big>\n\n<b>Eurydic&#275;, &#275;s</b> <i>f</i>\nЭвридика, <i>жена Орфея, умершая от укуса змеи; Орфей упросил Плутона отпустить её на землю, но так как он, нарушив запрет, оглянулся, ей пришлось вернуться в область теней</i> <i>V, O</i>."],
  ["Eurylochus", "<big>Eurylochus</big>\n\n<b>Eurylochus, &#299;</b> <i>m</i>\nЭврилох, <i>спутник Одиссея, отказавшийся выпить колдовское снадобье Кирки</i> <i>O</i>."],
  ["Eurymedon", "<big>Eurymedon</big>\n\n<b>Eurymed&#333;n, ontis</b> <i>m</i>\nЭвримедонт:\n<b>1)</b> <i>река в Памфилии</i> (<i>место двойной победы Кимона в 468 г. до н. э.</i>) <i>L, Amm</i>;\n<b>2)</b> <i>афинский полководец в годы Пелопоннесской войны</i> <i>Just</i>."],
  ["Eurymides", "<big>Eurymides</big>\n\n<b>Eurymid&#275;s, ae</b> <i>m</i>\nЭвримид (<i>сын прорицателя Эврима</i>), <i>т. е.</i> Telemus <i>O</i>."],
  ["Eurynome", "<big>Eurynome</big>\n\n<b>Eurynom&#275;, &#275;s</b> <i>f</i>\nЭвринома, <i>дочь Океана и Фетиды, морская богиня, мать Левкофои</i> <i>O</i>."],
  ["Eurypylus", "<big>Eurypylus</big>\n\n<b>Eurypylus, &#299;</b> <i>m</i>\nЭврипил:\n<b>1)</b> <i>сын Эвемона, царя Фессалии, один из греч. героев во время осады Трои</i> <i>O, V</i>;\n<b>2)</b> <i>миф. царь о-ва Кос</i> <i>Prp, O</i>."],
  ["Eurysthenes", "<big>Eurysthenes</big>\n\n<b>Eurysthen&#275;s, is</b> <i>m</i>\nЭврисфен, <i>родоначальник царского спартанского рода Эврисфенидов</i> <i>O, V</i>."],
  ["Eurystheus 1", "<big>Eurystheus</big>\n\n<b>I Eurysthe&#865;us, e&#299;</b> <i>и</i> <b>eos</b> <i>m</i>\nЭврисфей, <i>внук Персея, сын Сфенела, царь Микен, заставивший Геркулеса совершить &laquo;двенадцать подвигов&raquo;</i> <i>V, O, St</i>."],
  ["Eurystheus 2", "<big>Eurystheus</big>\n\n<b>II Eurysth&#275;us, a, um</b>\nэврисфеев <i>St</i>."],
  ["Eurytion", "<big>Eurytion</big>\n\n<b>Euryti&#333;n, &#333;nis</b> <i>m</i>\nЭвритион:\n<b>1)</b> <i>сын Актора из Фтии, участник похода Аргонавтов и Калидонской охоты</i> <i>O</i>;\n<b>2)</b> <i>троянец, сын Ликаона, спутник Энея</i> <i>V</i>."],
  ["Eurytis", "<big>Eurytis</big>\n\n<b>Eurytis, idis</b> <i>и</i> <b>idos</b> <i>f</i>\nдочь Эврита, <i>т. е.</i> Iole <i>O</i>."],
  ["Eurytus", "<big>Eurytus</big>\n\n<b>Eurytus, &#299;</b> <i>m</i>\nЭврит, <i>сын Меланея, царь Эхалии (Эвбея), отец Иолы, которого Геркулес сбросил со стен Тиринфа</i> <i>O</i>."],
  ["euscheme", "<big>euscheme</big>\n\n<b>eusch&#275;m&#275;</b> <i>adv.</i> (<i>греч.</i>)\nкрасиво, изящно <i>Pl</i>."],
  ["eustylos", "<big>eustylos</big>\n\n<b>eust&#563;los, on</b> <i>adj.</i> (<i>греч.</i>)\nс красивыми колоннами <i>Vtr</i>."],
  ["Euterpe", "<big>Euterpe</big>\n\n<b>Euterp&#275;, &#275;s</b> <i>f</i>\nЭвтерпа, <i>муза музыкального искусства, изобретательница флейты</i> <i>H, Aus</i>."],
  ["euthygrammum", "<big>euthygrammum</big>\n\n<b>euthygrammum, &#299;</b> <i>n</i> (<i>греч.</i>; <i>лат.</i> norma)\nлинейка, отвес <i>Vtr</i>."],
  ["Eutrapelus", "<big>Eutrapelus</big>\n\n<b>Eutrapelus, &#299;</b> <i>m</i> (<i>греч.</i> &laquo;ловкий&raquo;, &laquo;остроумный&raquo;)\n<i>прозвище римск. всадника П. Волумния</i> <i>C, H</i>."],
  ["Eutropius", "<big>Eutropius</big>\n\n<b>Eutropius, &#299;</b> <i>m</i>\n(Flavius) Евтропий (Флавий), <i>римск. историк времён Константина Великого и Юлиана Отступника, автор</i> &laquo;Breviarium Historiae Romanae&raquo;."],
  ["Euxinus", "<big>Euxinus</big>\n\n<b>Eux&#299;nus, a, um</b> [Euxinus Pontus]\nчерноморский (undae, litus <i>O</i>)."],
  ["Euxinus (Pontus)", "<big>Euxinus (Pontus)</big>\n\n<b>Eux&#299;nus, &#299;</b> <i>m</i>\nЧёрное море <i>C, O etc.</i>"],
  ["evacuatio", "<big>evacuatio</big>\n\n<b>&#275;vacu&#257;tio, &#333;nis</b> <i>f</i> [evacuo]\n<b>1)</b> опорожнение (vasorum <i>CA</i>);\n<b>2)</b> ослабление, разрушение (fidei <i>Tert</i>)."],
  ["evacuo", "<big>evacuo</big>\n\n<b>&#275;–vacuo, &#257;v&#299;, &#257;tum, &#257;re</b>\n<b>1)</b> опорожнять (alvum <i>PM</i>);\n<b>2)</b> отбрасывать прочь (quae parvuli sunt <i>Vlg</i>);\n<b>3)</b> ослаблять <i>или</i> уничтожать, отменять (obligatio evacuata <i>CJ</i>)."],
  ["Evadne", "<big>Evadne</big>\n\n<b>Evadn&#275;</b> <i>v. l.</i>\n= Euadne."],
  ["evado", "<big>evado</big>\n\n<b>&#275;–v&#257;do, v&#257;s&#299;, v&#257;sum, ere</b>\n<b>1)</b> выходить (ex balneis <i>C</i>; undis <i>V</i>; in terram <i>L</i>; extra vallum <i>L</i>; superas ad auras <i>V</i>);\n<b>2)</b> выезжать (oppido <i>Sl</i>);\n<b>3)</b> доходить, добираться (primo Praeneste, inde Neapolin <i>Su</i>): ad conjecturam e. <i>Pl</i> найти разгадку;\n<b>4)</b> изливаться, впадать (flumen evadit in mare <i>QC</i>);\n<b>5)</b> становиться, делаться (ex ganeone philosophus evasit <i>VM</i>): quod tu ejusmodi evasisti <i>C</i> ибо таким ты вышел;\n<b>6)</b> переходить (pestilentia in longos morbos evasit <i>L</i>);\n<b>7)</b> оказываться (somnium verum evasit <i>C</i>): nemo dupondii evadit <i>Pt</i> никто (из них) и полушки не стоит;\n<b>8)</b> кончаться, заканчиваться, завершаться: nescio, quorsum haec evasura sint <i>C</i> не знаю, что из этого выйдет (чем это кончится); timeo, quorsum evades <i>Ter</i> я со страхом жду, чем ты закончишь (свою речь);\n<b>9)</b> убегать, спасаться (ex manibus hostium <i>L</i>; e morbo, ex insidiis, ab judicibus <i>C</i>; pugnae <i>V</i>; periculo <i>или</i> e periculo <i>L</i>; per tela hostium <i>L</i>): evasisse gravem casum <i>T</i> избежать серьёзной опасности; evasisse ictu levi saucium <i>T</i> отделаться лёгким ушибом;\n<b>10)</b> всходить, взбираться, подыматься (ad summi fastigia culminis <i>V</i>; ardua <i>L</i>; in muros <i>L</i>): e. gradus altos <i>V</i> подняться по высоким ступеням;\n<b>11)</b> проходить (media castra, angustias <i>L</i>; viam <i>V</i>); проплывать (amnem <i>T</i>);\n<b>12)</b> уходить, убегать (Catilina evasit <i>C</i>): e. vitam <i>Ap</i> умереть."],
  ["evagatio", "<big>evagatio</big>\n\n<b>&#275;vag&#257;tio, &#333;nis</b> <i>f</i> [evagor]\nотступление, удаление, уход (libertas et e., <i>sc.</i> animi <i>Sen</i>); блуждание (stellarum <i>PM</i>)."],
  ["evagino", "<big>evagino</big>\n\n<b>&#275;v&#257;g&#299;no, &#257;v&#299;, &#257;tum, &#257;re</b> [e + vagina]\nвынимать из ножен, обнажать (gladium <i>Just</i>; pugionem <i>Vlg</i>)."],
  ["evago", "<big>evago</big>\n\n<b>&#275;vago</b> <i>Acc</i>\n= evagor."],
  ["evagor", "<big>evagor</big>\n\n<b>&#275;–vagor, &#257;tus sum, &#257;r&#299;</b> <i>depon.</i>\n<b>1)</b> выходить из берегов, разливаться (Nilus evagatur <i>PM</i>); разрастаться (per agros <i>PM</i>); шириться, распространяться (late evagata est vis morbi <i>L</i>);\n<b>2)</b> отклоняться, отступать, удаляться от предмета: hactenus e. satis fuerit <i>Q</i> но довольно отступлений (не будем больше удаляться в сторону);\n<b>3)</b> бродить, рыскать (effuse <i>L</i>);\n<b>4)</b> <i>воен.</i> делать фланговый (обходный) манёвр (spatium ad evagandum <i>L</i>);\n<b>5)</b> преступать, не соблюдать (ordinem rectum <i>H</i>): supra plantam e. <i>погов. Amm</i> судить выше колодки, <i>т. е.</i> о том, что выше понимания."],
  ["evalesco", "<big>evalesco</big>\n\n<b>&#275;–val&#275;sco, valu&#299;, —, ere</b>\n<b>1)</b> становиться сильным, крепнуть, укрепляться, усиливаться (rami evaluerunt <i>PM</i>): evaluisse paullatim <i>T</i> постепенно получить преобладание || перерастать (affectatio in tumultum evaluit <i>T</i>; adusque bellum e. <i>T</i>);\n<b>2)</b> быть в состоянии, мочь (cuspidis ictum medicari <i>V</i>);\n<b>3)</b> цениться, стоить (margarita centum sestertium evaluit <i>Macr</i>);\n<b>4)</b> входить в употребление, укореняться (nomen evalescit <i>T, Q</i>)."],
  ["evalidus", "<big>evalidus</big>\n\n<b>&#275;–validus, a, um</b>\nсильный, крепкий, могучий (corpus <i>PM</i>)."],
  ["evallo 1", "<big>evallo</big>\n\n<b>I &#275;vallo, &#257;v&#299;, —, &#257;re</b> [e + vallum]\nвыкидывать, выбрасывать (aliquem foras <i>Vr</i>)."],
  ["evallo 2", "<big>evallo</big>\n\n<b>II &#275;vallo, —, —, ere</b> [e + vallus II]\nочищать от шелухи, просеивать, веять (triticum <i>PM</i>)."],
  ["evalui", "<big>evalui</big>\n\n<b>&#275;valu&#299;</b> <i>pf. к</i>\nevalesco."],
  ["evan", "<big>evan</big>\n\n<b>&#275;v&#257;n</b> <i>interj.</i> <i>v. l.</i>\n= euan."],
  ["Evander", "<big>Evander</big>\n\n<b>&#274;vander</b> <i>v. l.</i>\n= Euander."],
  ["evanesco", "<big>evanesco</big>\n\n<b>&#275;–v&#257;n&#275;sco, v&#257;nu&#299;, —, ere</b>\n<b>1)</b> исчезать, пропадать (ex oculis <i>V</i>; evanuit spes <i>C</i>); проходить, униматься, утихать (dolor evanescit <i>PM</i>); испаряться, улетучиваться (aqua evanescit <i>Sen</i>); выцветать, бледнеть (color evanescit <i>Lcr</i>);\n<b>2)</b> терять силу, слабеть, выдыхаться (vinum evanescit <i>C</i>); ослабевать, становиться вялым (bellum evanescit <i>T</i>); таять (in tenuem auram <i>Sil</i>);\n<b>3)</b> разбегаться (exercitus evanuit <i>Fl</i>);\n<b>4)</b> утрачивать законную силу (donatio evanescit <i>Dig</i>);\n<b>5)</b> забываться (orationes alicujus jam evanuerunt <i>C</i>)."],
  ["evangel-", "<big>evangel-</big>\n\n<b>&#275;vangel-</b> <i>v. l.</i>\n= euangel-."],
  ["evanidus", "<big>evanidus</big>\n\n<b>&#275;v&#257;nidus, a, um</b> [evanesco]\nисчезающий, теряющий силу, слабеющий (gaudium <i>Sen</i>; amor <i>O</i>); гаснущий (flamma <i>Sen</i>); истощённый, худосочный (languidus et e. <i>Sen</i>); чахлый (arbores <i>Vtr</i>); вялый (oleae <i>Col</i>)."],
  ["evaniturus", "<big>evaniturus</big>\n\n<b>&#275;v&#257;nit&#363;rus, a, um</b> <i>Lact part. fut. к</i>\nevanesco."],
  ["evanno", "<big>evanno</big>\n\n<b>&#275;–vanno, —, —, ere</b> [vannus]\nвеять, просеивать (acus <i>Vr</i>)."],
  ["evans", "<big>evans</big>\n\n<b>&#275;v&#257;ns, antis</b> <i>part.</i> [evoe]\nвосклицающий &laquo;evoe&raquo;, шумно ликующий, неистовствующий <i>Ctl, Prp etc.</i>: orgia evantes <i>V</i> участники вакханалии."],
  ["evaporatio", "<big>evaporatio</big>\n\n<b>&#275;vap&#333;r&#257;tio, &#333;nis</b> <i>f</i> [evaporo]\nиспарение (terrae <i>Sen</i>; aquae <i>AG</i>)."],
  ["evaporativus", "<big>evaporativus</big>\n\n<b>&#275;vap&#333;r&#257;t&#299;vus, a, um</b>\nиспарительный (vis <i>CA</i>)."],
  ["evaporo", "<big>evaporo</big>\n\n<b>&#275;–vap&#333;ro, &#257;v&#299;, &#257;tum, &#257;re</b>\nиспарять <i>AG, Sol</i>."],
  ["evasio", "<big>evasio</big>\n\n<b>&#275;v&#257;sio, &#333;nis</b> <i>f</i> [evado]\nуход <i>Vlg</i>."],
  ["evasti", "<big>evasti</big>\n\n<b>&#275;v&#257;st&#299;</b> <i>H, Sil</i>\n= evasisti (<i>2 л. sg. pf. к</i> evado)."],
  ["evasto", "<big>evasto</big>\n\n<b>&#275;–v&#257;sto, &#257;v&#299;, &#257;tum, &#257;re</b>\nопустошать (vallem <i>L</i>; rura <i>Sil</i>)."],
  ["evax", "<big>evax</big>\n\n<b>&#275;vax</b> <i>interj.</i> <i>v. l.</i>\n= euax."],
  ["evectatio", "<big>evectatio</big>\n\n<b>&#275;vect&#257;tio, &#333;nis</b> <i>f</i> [eveho]\nотплытие (судна) <i>Aug</i>."],
  ["evectio", "<big>evectio</big>\n\n<b>&#275;vectio, &#333;nis</b> <i>f</i> [eveho]\n<b>1)</b> подорожная, путёвка <i>CJ, Aug</i>;\n<b>2)</b> подъём, взлёт (e. sublimis <i>Ap</i>)."],
  ["evectus 1", "<big>evectus</big>\n\n<b>I &#275;vectus, a, um</b> <i>part. pf. к</i>\neveho."],
  ["evectus 2", "<big>evectus</big>\n\n<b>II &#275;vectus, &#363;s</b> <i>m</i> [eveho]\nвывоз <i>Vr, PM</i>."],
  ["eveho", "<big>eveho</big>\n\n<b>&#275;–veho, v&#275;x&#299;, vectum, ere</b>\nвывозить (merces <i>Vr</i>); поднимать, возносить (aliquem ad deos <i>H</i>, ad aethera <i>V</i>, ad <i>и</i> in consulatum <i>T, VP</i>; aquila evecta alis <i>Ap</i>); se e. <i>и</i> <i>med.-pass.</i> evehi:\n<b>1)</b> выезжать, отправляться (curru, equo <i>L</i>); выплывать, отплывать (ad portum, in altum <i>L</i>);\n<b>2)</b> устремляться, нападать (acri impetu in hostem <i>QC</i>);\n<b>3)</b> увлекаться (inconsultius <i>L</i>; evectus spe <i>L</i>): evehi longius <i>Q</i> слишком отклониться от темы;\n<b>4)</b> распространяться: fama ejus evecta est insulas <i>T</i> его слава вышла за пределы островов;\n<b>5)</b> въезжать наверх: evehi equo in collem <i>L</i> въехать верхом на холм;\n<b>6)</b> подниматься, быть доведённым: ingentes opes evectae supra modum privatum <i>T</i> огромные богатства, превышающие средства частного человека."],
  ["evello", "<big>evello</big>\n\n<b>&#275;–vello, vell&#299; (vuls&#299;), vulsum, ere</b>\n<b>1)</b> вырывать (dentes <i>PM</i>; linguam, aculeum alicui <i>C</i>); срывать (poma ex arboribus <i>C</i>); взламывать (claustrum <i>Ap</i>); сбрасывать (statuam de monumento <i>Dig</i>); выкорчёвывать (arborem radicitus <i>Sen</i>); выдёргивать (clavos, hastam <i>C</i>); рвать, трепать (linum <i>PM</i>);\n<b>2)</b> исторгать, отнимать, удалять: e. suspicionem <i>C</i> положить конец подозрению; alicui ex animo scrupulum e. <i>C</i> успокоить чьё-л. сердце;\n<b>3)</b> уничтожать, искоренять (mala <i>Lcr</i>; pravas opiniones <i>Aug</i>): e. consules ex fastis <i>C</i> исключить имена консулов из списков;\n<b>4)</b> освобождать (castra obsessa, <i>sc.</i> ex obsidione <i>Sil</i>)."],
  ["evenat", "<big>evenat</big>\n\n<b>&#275;venat</b> <i>арх.</i>\n( = eveniat) <i>Enn, Pl praes. conjct. к</i> evenio."],
  ["evenio", "<big>evenio</big>\n\n<b>&#275;–venio, v&#275;n&#299;, ventum, &#299;re</b>\n<b>1)</b> выходить, появляться (<i>sc.</i> mari <i>H</i>);e;\n<b>2)</b> оканчиваться, принимать (тот или иной) оборот (e. alicui bene atque feliciter <i>C</i>; male <i>Pl</i>; praeter spem <i>Ter</i>): res aliter evenit <i>Pl</i> дело вышло иначе (приняло другой оборот);\n<b>3)</b> исполняться, сбываться, осуществляться (eveniunt optata <i>O</i>); (вос)последовать (pax evenit <i>Sl</i>); приключаться, бывать, случаться (quod plerumque evenit <i>C</i>; persaepe evenit, ut <i>Q</i>): et ii, quibus evenit jam, et ii, quibus eventurum est <i>C</i> как те, с которыми это уже произошло, так и те, с которыми (это) произойдёт; evenit iter illi in Lemnum ut esset <i>Ter</i> ему довелось совершить путешествие на Лемнос; male eveniat (<i>sc.</i> aedilibus)! <i>Pt</i> провалиться бы этим эдилам!; eventura somniare <i>PJ</i> видеть вещие сны; si quid sibi eveniret (<i>sc.</i> mali) <i>Su</i> если бы с ним что-л. случилось (<i>т. е.</i> если бы он умер);\n<b>4)</b> доставаться, выпадать на долю (illi haec provincia evenit <i>L</i>);\n<b>5)</b> прибывать, приезжать (Capuam <i>Pl</i>)."],
  ["eventilo", "<big>eventilo</big>\n\n<b>&#275;–ventilo, &#257;v&#299;, &#257;tum, &#257;re</b>\n<b>1)</b> проветривать, очищать (aera <i>PM</i>);\n<b>2)</b> провеивать (frumentum <i>Col</i>);\n<b>3)</b> развеивать, расточать (opes <i>Sid</i>);\n<b>4)</b> разбирать, критиковать <i>Hier</i>."],
  ["eventum", "<big>eventum</big>\n\n<b>&#275;ventum, &#299;</b> <i>n</i> [evenio]\n<b>1)</b> событие, происшествие (memoria eventorum <i>C</i>);\n<b>2)</b> исход, результат (ex evento existimare <i>C</i>);\n<b>3)</b> <i>филос.</i> внешнее свойство, случайный признак (<i>в отличие от</i> conjunctum, <i>см.</i>); omnia, quorum adventu manet incolumis natura abituque, haec soliti sumus &laquo;eventa&raquo; vocare <i>Lcr</i> всё, от добавления или исчезновения чего природа (вещи) не изменяется, мы обычно называем &laquo;случайными признаками&raquo;."],
  ["eventus", "<big>eventus</big>\n\n<b>&#275;ventus, &#363;s</b> <i>m</i> [evenio]\n<b>1)</b> исход, конец, результат (pugnae, diei <i>Cs</i>); успех, удача (pugnare dubio eventu <i>L</i>): non sine eventu <i>PJ</i> не без успеха; certus eventus <i>T</i> уверенный в успехе || развязка (ad eventum festinare <i>H</i>);\n<b>2)</b> приключение, случай (gravis e. <i>O</i>): ex eventu, non ex voluntate <i>C</i> случайно, а не преднамеренно;\n<b>3)</b> участь, удел, судьба (navium suarum <i>Cs</i>)."],
  ["Evenus", "<big>Evenus</big>\n\n<b>&#274;v&#275;nus, &#299;</b> <i>m</i> <i>v. l.</i>\n= Euenus."],
  ["everbero", "<big>everbero</big>\n\n<b>&#275;–verbero, &#257;v&#299;, &#257;tum, &#257;re</b>\n<b>1)</b> сильно ударять, бить, хлестать, бичевать (mare remis <i>QC</i>);\n<b>2)</b> разбивать, терзать (os oculosque hostis <i>Q</i>);\n<b>3)</b> стряхивать (cineres alis <i>O</i>);\n<b>4)</b> подстёгивать, возбуждать (animum alicujus ad inquirendum <i>AG</i>): aures alicujus e. <i>Amm</i> доходить до чьих-л. ушей."],
  ["evergo", "<big>evergo</big>\n\n<b>&#275;–vergo, —, —, ere</b>\nиспускать, изливать, источать (rivos <i>L</i>)."],
  ["everriculum", "<big>everriculum</big>\n\n<b>&#275;verriculum, &#299;</b> <i>n</i> [everro]\n<b>1)</b> метла <i>C</i>;\n<b>2)</b> орудие удаления, чистки (malitiarum omnium <i>C</i>);\n<b>3)</b> невод, рыболовная сеть <i>Vr</i>."],
  ["everro", "<big>everro</big>\n\n<b>&#275;–verro, verr&#299;, versum, ere</b>\n<b>1)</b> выметать (aliquid ex aede <i>Vr</i>);\n<b>2)</b> подметать, вычищать (stabulum <i>Vr, Col</i>);\n<b>3)</b> ловить неводом (piscem <i>Ap</i>): aequor retibus e. <i>Man</i> сетями ловить рыбу в море;\n<b>4)</b> обирать, грабить (fanum <i>C</i>)."],
  ["eversio", "<big>eversio</big>\n\n<b>&#275;versio, &#333;nis</b> <i>f</i> [everto]\n<b>1)</b> опрокидывание (columnae <i>C</i>); разрушение (urbis <i>Fl</i>; templorum <i>Q</i>);\n<b>2)</b> потрясение, свержение, ниспровержение (omnis vitae <i>C</i>; rei publicae <i>C</i>; probitatis et honestatis <i>Aug</i>);\n<b>3)</b> расточение (rei familiaris <i>T</i>)."],
  ["eversor", "<big>eversor</big>\n\n<b>&#275;versor, &#333;ris</b> <i>m</i> [everto]\n<b>1)</b> разоритель, разрушитель (regnorum Priami <i>V</i>; Carthaginis <i>Q</i>; juris humani <i>PM</i>);\n<b>2)</b> расточитель, растратчик (pecuniae <i>CTh</i>)."],
  ["eversus 1", "<big>eversus</big>\n\n<b>I &#275;versus, a, um</b> <i>part. pf. к</i>\n<b>1)</b> everro <i>и</i>\n<b>2)</b> everto."],
  ["eversus 2", "<big>eversus</big>\n\n<b>II &#275;versus, &#363;s</b> <i>m</i>\nопровержение <i>Julianus ap. Aug</i>."],
  ["everto", "<big>everto</big>\n\n<b>&#275;–verto, vert&#299;, versum, ere</b>\n<b>1)</b> опрокидывать, переворачивать (navem <i>C</i>; currum <i>QC</i>); валить (arborem <i>V</i>); выворачивать (cervicem <i>Ter</i>);\n<b>2)</b> свергать (aliquem <i>C</i>): e. rem publicam <i>Su, Q</i> производить государственный переворот || ниспровергать (funditus civitates <i>C</i>); разрушать (Troja eversa <i>Man</i>); отменять, упразднять (leges, testamenta <i>C</i>); расстраивать, расторгать (amicitiam <i>C</i>);\n<b>3)</b> вздымать, волновать (aequor ventis <i>V</i>; mare evertitur <i>Sen</i>); взрывать, вспахивать (campum <i>VF</i>);\n<b>4)</b> разорять, разрушать (urbem <i>C</i>; castellum <i>H</i>); губить (aliquem <i>J</i>); уничтожать (spem <i>O</i>; simul parta ac sperata decora <i>L</i>);\n<b>5)</b> выгонять, изгонять (aliquem agro <i>Pl</i>): e. aliquem bonis (fortunis) omnibus <i>Pl, C</i> лишить кого-л. всего имущества;\n<b>6)</b> расточать, растрачивать (patrimonium <i>Dig</i>)."],
  ["evestigatus", "<big>evestigatus</big>\n\n<b>&#275;vest&#299;g&#257;tus, a, um</b> [e + vestigo]\nвыслеженный, разысканный, найденный, открытый <i>O, Sen</i>."],
  ["Evias", "<big>Evias</big>\n\n<b>&#274;vias, adis</b> <i>f</i> <i>v. l.</i>\n= Eu(h)ias."],
  ["evibro", "<big>evibro</big>\n\n<b>&#275;–vibro, —, —, &#257;re</b>\n<b>1)</b> выбрасывать, метать (rotundos lapides <i>Amm</i>);\n<b>2)</b> поднимать, воодушевлять (excitare atque e. animos <i>AG</i>)."],
  ["evici", "<big>evici</big>\n\n<b>&#275;v&#299;c&#299;</b> <i>pf. к</i>\nevinco."],
  ["evictio", "<big>evictio</big>\n\n<b>&#275;victio, &#333;nis</b> <i>f</i> [evinco]\nвзыскание по суду своей собственности <i>CJ</i>."],
  ["evictus", "<big>evictus</big>\n\n<b>&#275;victus, a, um</b> <i>part. pf. к</i>\nevinco."],
  ["evidens", "<big>evidens</big>\n\n<b>&#275;vid&#275;ns, entis</b> <i>adj.</i> [e + video]\n<b>1)</b> заметный, находящийся на виду, видимый (flos non e., sed intus occultus <i>PM</i>);\n<b>2)</b> очевидный, явный, ясный (res <i>C, L</i>; argumentum <i>PM</i>; quid est evidentius? <i>C</i>);\n<b>3)</b> выдающийся, видный (auctores Graeciae <i>PM</i>)."],
  ["evidenter", "<big>evidenter</big>\n\n<b>&#275;videnter</b> [evidens]\nочевидным образом, явственно ясно <i>L, VM, Q, CC etc.</i>"],
  ["evidentia", "<big>evidentia</big>\n\n<b>&#275;videntia, ae</b> <i>f</i> [evidens]\n<b>1)</b> ясная видимость: magnam facere evidentiam alicujus rei <i>Vlg</i> явить что-л. со всей очевидностью || очевидность, ясность: e. sui <i>Ap</i> самоочевидность;\n<b>2)</b> <i>ритор.</i> наглядное изображение, живость изложения <i>C, Q</i>."],
  ["evigesco", "<big>evigesco</big>\n\n<b>&#275;–vig&#275;sco, —, —, ere</b>\nутрачивать силу, слабеть <i>Tert</i>."],
  ["evigilatio", "<big>evigilatio</big>\n\n<b>&#275;–vigil&#257;tio, &#333;nis</b> <i>f</i>\nпробуждение <i>Aug</i>."],
  ["evigilo", "<big>evigilo</big>\n\n<b>&#275;–vigilo, &#257;v&#299;, &#257;tum, &#257;re</b>\n<b>1)</b> просыпаться, пробуждаться (maturius, de nocte <i>Su</i>);\n<b>2)</b> не спать, бодрствовать, проводить без сна (nox evigilanda <i>Tib</i>; ante mediam noctem e. <i>Su</i>);\n<b>3)</b> неусыпно трудиться, стараться, заботиться (in aliqua re <i>C</i>);\n<b>4)</b> тщательно прорабатывать, обдумывать (libros <i>O</i>; consilium <i>C</i>)."],
  ["evigoratus", "<big>evigoratus</big>\n\n<b>&#275;–vig&#333;r&#257;tus, a, um</b>\nутративший силу, ослабевший <i>Tert</i>."],
  ["evilesco", "<big>evilesco</big>\n\n<b>&#275;–v&#299;l&#275;sco, lu&#299;, —, ere</b>\nстановиться маловажным, утрачивать цену, терять значение <i>VM, T, Su</i>."],
  ["evincio", "<big>evincio</big>\n\n<b>&#275;–vincio, v&#299;nx&#299;, v&#299;nctum, &#299;re</b>\nобвязывать, обвивать (caput diademate <i>T</i>); связывать (aliquem <i>AG, Sil</i>): evinctus brachia (<i>acc. gr.</i>) <i>Tib</i> со скованными руками."],
  ["evinco", "<big>evinco</big>\n\n<b>&#275;–vinco, v&#299;c&#299;, victum, ere</b>\n<b>1)</b> побеждать, одолевать (aliquem <i>T</i>); evictus dolore <i>V</i> сломленный скорбью;\n<b>2)</b> превозмогать, бороться, подавлять (somnum <i>O</i>; dolorem <i>Sen</i>; virtus paulatim evicta <i>Sil</i>): amnis evicit moles <i>V</i> река прорвала плотины; e. spississimam noctem <i>Pt</i> быть заметным сквозь непроглядную ночную тьму || вытеснять (platanus evincet ulmos <i>H</i>);\n<b>3)</b> преодолевать, <i>т. е.</i> благополучно проезжать, миновать (Charybdin remis <i>O</i>; os Ponti <i>PM</i>);\n<b>4)</b> настоять, добиться, поставить на своём, достигать: patres evicerunt ut Camillus crearetur <i>L</i> патриции добились того, что Камилл был избран (консулом); evinci in lacrimas <i>T</i> быть доведённым до слёз;\n<b>5)</b> убеждать, склонять, смягчать (aliquem precibus, donis <i>T</i>): evictus lacrimis alicujus <i>V</i> уступая чьим-л. слезам;\n<b>6)</b> изобличать (testibus evictus <i>C</i>);\n<b>7)</b> неопровержимо доказывать (si ratio evincet puerilius esse amare <i>H</i>);\n<b>8)</b> <i>юр.</i> возвращать себе в судебном порядке (hereditatem <i>Dig</i>)."],
  ["eviratio", "<big>eviratio</big>\n\n<b>&#275;vir&#257;tio, &#333;nis</b> <i>f</i> [eviro]\nкастрирование: e. pilorum <i>PM</i> удаление волос."],
  ["eviratus", "<big>eviratus</big>\n\n<b>&#275;vir&#257;tus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> eviro;\n<b>2.</b> <i>adj.</i> женоподобный (corpus <i>Eccl</i>; eviratior spadone <i>M</i>)."],
  ["eviresco", "<big>eviresco</big>\n\n<b>&#275;–vir&#275;sco, —, —, ere</b>\nутрачивать живость, блёкнуть (color evirescit <i>Vr</i>)."],
  ["eviro", "<big>eviro</big>\n\n<b>&#275;–viro, &#257;v&#299;, &#257;tum, &#257;re</b> [e + vir]\n<b>1)</b> кастрировать (pueros <i>Vr</i>);\n<b>2)</b> лишать мужественности, ослаблять (corpus <i>Ctl, CA</i>)."],
  ["eviscero", "<big>eviscero</big>\n\n<b>&#275;–v&#299;scero, (&#257;v&#299;), &#257;tum, &#257;re</b> [e + viscus]\n<b>1)</b> вынимать внутренности, потрошить (evisceratum corpus <i>C</i>); терзать (columbam <i>V</i>);\n<b>2)</b> размывать (amnes eviscerant terras <i>Sen</i>);\n<b>3)</b> расточать (opes <i>CJ</i>);\n<b>4)</b> вынимать из внутренностей (unio e concha evisceratus <i>Sol</i>)."],
  ["evitabilis", "<big>evitabilis</big>\n\n<b>&#275;v&#299;t&#257;bilis, e</b> [evito I]\nкоторого можно избежать (mala <i>Sen</i>): non evitabile telum <i>O</i> стрела, от которой нельзя ускользнуть."],
  ["evitatio", "<big>evitatio</big>\n\n<b>&#275;v&#299;t&#257;tio, &#333;nis</b> <i>f</i> [evito I]\nизбегание (periculi <i>Sen</i>; malorum <i>Q</i>)."],
  ["evito 1", "<big>evito</big>\n\n<b>I &#275;–v&#299;to, &#257;v&#299;, &#257;tum, &#257;re</b>\n<b>1)</b> избегать (suspicionem, dolorem <i>C</i>; vitia <i>Q</i>);\n<b>2)</b> огибать (metam rotis <i>H</i>)."],
  ["evito 2", "<big>evito</big>\n\n<b>II &#275;–v&#299;to, &#257;v&#299;, —, &#257;re</b> [vita]\nлишать жизни, умерщвлять (aliquem <i>Ap</i>): e. vitam alicui <i>Enn ap. C</i> лишать кого-л. жизни."],
  ["Evius", "<big>Evius</big>\n\n<b>&#274;vius, &#299;</b> <i>m</i> <i>v. l.</i>\n= Eu(h)ius."],
  ["evocati", "<big>evocati</big>\n\n<b>&#275;voc&#257;t&#299;, &#333;rum</b> <i>m</i> [evoco]\nсверхсрочные солдаты (<i>отслужившие свой срок, но вновь призванные в добровольном порядке в войска</i>) <i>C, Cs, Sl</i>."],
  ["evocatio", "<big>evocatio</big>\n\n<b>&#275;voc&#257;tio, &#333;nis</b> <i>f</i> [evoco]\n<b>1)</b> вызов <i>bAl</i>; призыв <i>rhH, Macr</i>;\n<b>2)</b> вызывание, заклинание (inferorum <i>PM</i>)."],
  ["evocator", "<big>evocator</big>\n\n<b>&#275;voc&#257;tor, &#333;ris</b> <i>m</i> [evoco]\nпризывающий к оружию, возмутитель, подстрекатель (servorum et civium perditorum <i>C</i> — <i>о Катилине</i>)."],
  ["evocatoria", "<big>evocatoria</big>\n\n<b>&#275;voc&#257;t&#333;ria, ae</b> <i>f</i> (<i>sc.</i> epistula)\nписьменный вызов, предложение явиться (к императорскому двору) <i>CJ, CTh</i>."],
  ["evocatorius", "<big>evocatorius</big>\n\n<b>&#275;voc&#257;t&#333;rius, a, um</b> [evoco]\nсодержащий вызов (epistula <i>Sid</i>)."],
  ["evoco", "<big>evoco</big>\n\n<b>&#275;–voco, &#257;v&#299;, &#257;tum, &#257;re</b>\n<b>1)</b> звать, вызывать, приглашать (aliquem foras <i>Ter</i>; ad colloquium <i>L</i>; aliquos ad pugnam <i>Cs</i>; e convivio <i>J</i>); призывать (aliquem ad honorem <i>Cs</i>; magistratus ad se <i>C</i>);\n<b>2)</b> вызывать, возбуждать (risum, lacrimas <i>Sen</i>; sudorem <i>CC</i>; vomitiones <i>PM</i>; iram alicujus <i>Sen</i>);\n<b>3)</b> доводить (aliquem ad saevitiam <i>Sen</i>; vitis evocata ad fructus <i>PM</i>);\n<b>4)</b> извлекать, вытягивать (sucum quasi per siphonem <i>Col</i>; <i>перен.</i> aliquem e tenebris in lucem <i>C</i>);\n<b>5)</b> <i>культ.</i> вызывать, заклинать (aliquem ab inferis <i>C</i>; proavos sepulchris <i>O</i>): e. deos <i>L, Macr</i> склонять на свою сторону богов осаждаемого (неприятельского) города;\n<b>6)</b> <i>воен.</i> вызывать добровольцев, вербовать, приглашать, привлекать (oppidanos ad bellum <i>Cs</i>): aliquem spe praedae e. <i>Cs</i> завербовать кого-л. перспективами (военной) добычи. — <i>См. тж.</i> evocati."],
  ["evoe", "<big>evoe</big>\n\n<b>&#275;voe</b> (<i>двусложн.</i>) <i>interj.</i>\nэвое! (<i>ликующий возглас на празднествах в честь Вакха</i>) <i>Ctl, V etc.</i>"],
  ["evolito", "<big>evolito</big>\n\n<b>&#275;volito, —, —, &#257;re</b> [<i>frequ. к</i> evolo]\nвылетать (aves ad requirendos cibos evolitant <i>Col</i>)."],
  ["evolo", "<big>evolo</big>\n\n<b>&#275;–volo, &#257;v&#299;, &#257;tum, &#257;re</b>\n<b>1)</b> вылетать (madidis Notus evolat alis <i>O</i>); слетать (ex quercu <i>C</i>); устремляться, поспешно выбегать, бросаться (hostes evolant ex omnibus partibus silvae <i>Cs</i>); улетать, поспешно удаляться, убегать, скрываться (e conspectu <i>C</i>);\n<b>2)</b> избежать, ускользнуть (e poena <i>C</i>);\n<b>3)</b> взлетать, взвиваться (pennis <i>O</i>);\n<b>4)</b> возвышаться, подниматься (altius <i>C</i>)."],
  ["evolsus", "<big>evolsus</big>\n\n<b>&#275;volsus, a, um</b> <i>арх. v. l.</i>\n= evulsus."],
  ["evoluam", "<big>evoluam</big>\n\n<b>&#275;voluam</b> <i>Ctl</i> (4 <i>слога</i>) <i>и</i> <b>&#275;voluisse</b> <i>Prp, O</i> (5 <i>слогов</i>)\n= evolvam, evolvisse."],
  ["evolute", "<big>evolute</big>\n\n<b>&#275;vol&#363;t&#275;</b> [evolvo]\nобстоятельно <i>Bo&euml;t</i>."],
  ["evolutio", "<big>evolutio</big>\n\n<b>&#275;vol&#363;tio, &#333;nis</b> <i>f</i> [evolvo]\nразвёртывание (свитка), раскрывание (книги), <i>т. е.</i> чтение (poetarum <i>C</i>)."],
  ["evolutus", "<big>evolutus</big>\n\n<b>&#275;vol&#363;tus, a, um</b> <i>part. pf. к</i>\nevolvo."],
  ["evolvo", "<big>evolvo</big>\n\n<b>&#275;–volvo, volv&#299; (volu&#299;), vol&#363;tum, ere</b>\n<b>1)</b> разворачивать, раскатывать, развёртывать (serpens evolvitur <i>Sil</i>; e. volumen epistularum <i>C</i>); распахивать (vestem <i>O</i>): e. aliquem opertum amiculo <i>Pt</i> сдёрнуть с кого-л. плащ; e. librum <i>C, Sen etc.</i> развернуть свиток (<i>т. е.</i> раскрыть, читать, изучать книгу);\n<b>2)</b> выворачивать, вырывать (arbusta radicitus <i>Lcr</i>);\n<b>3)</b> излагать, изъяснять (causas belli <i>Enn</i>; naturam rerum <i>C</i>); развивать (notionem animi <i>C</i>); возвещать (fatorum arcana <i>V</i>);\n<b>4)</b> обдумывать (aliquid secum <i>SenT</i>);\n<b>5)</b> выкатывать, сваливать (saxa lacertis <i>Lcn</i>);\n<b>6)</b> лущить, очищать (panicum evolutum furfure <i>Col</i>);\n<b>7)</b> добыть, достать, наскрести (argentum aut terra aut mari <i>Pl</i>);\n<b>8)</b> катить (prorutam terram in mare <i>L</i>); скатывать, отбрасывать (с себя) (jactas silvas <i>O</i>); se e. <i>или</i> evolvi устремляться (extra munimenta <i>QC</i>), катиться, перекатываться (per humum <i>T</i>) <i>или</i> течь (in mare <i>V</i>): multi anni evoluti sunt <i>Aug</i> прошло много лет || стремиться, клубиться, валить (fumus specu evolvitur <i>QC</i>) <i>или</i> докатываться, доходить, достигать (dicta ad aures alicujus evolvebantur <i>L</i>);\n<b>9)</b> выталкивать, вытеснять, изгонять (aliquem sede patria <i>T</i>); se e. выпутаться, выбраться (omni turba <i>и</i> ex turbis <i>Ter</i>);\n<b>10)</b> лишать (e. bonis <i>Sen</i>; aliquem ex praeda <i>L</i>); разоблачать (evolutus tegumentis dissimulationis <i>C</i>);\n<b>11)</b> развивать, раскручивать: e. fusos (fila) <i>O</i> прясть нить жизни, <i>т. е.</i> решать участь людей (<i>о Парках</i>)."],
  ["evomo", "<big>evomo</big>\n\n<b>&#275;–vomo, u&#299;, itum, ere</b>\n<b>1)</b> изрыгать, извергать, выплёвывать (bilem <i>CC</i>; venenum <i>Su</i>); выбрасывать (multam arenam <i>QC</i>); <i>перен.</i> изливать (iram in aliquem <i>Ter</i>; virus acerbitatis apud aliquem <i>C</i>); se e. изливаться, впадать (Nilus in Aegyptium mare se evomit <i>PM</i>);\n<b>2)</b> изгонять (tantam pestem <i>C</i>);\n<b>3)</b> выпускать из себя, рождать (herbas <i>Col</i>);\n<b>4)</b> сочинять, создавать (librum spirante deo <i>Eccl</i>);\n<b>5)</b> <i>ирон.</i> возвращать (devoratam pecuniam <i>C</i>)."],
  ["evorto", "<big>evorto</big>\n\n<b>&#275;vorto</b> <i>арх. v. l.</i>\n= everto."],
  ["evulgo", "<big>evulgo</big>\n\n<b>&#275;–vulgo, &#257;v&#299;, &#257;tum, &#257;re</b>\nопубликовать, обнародовать (jus civile <i>L</i>); разглашать, делать явным (arcanum <i>T</i>, pudor evulgatus <i>T</i>)."],
  ["evulsio", "<big>evulsio</big>\n\n<b>&#275;vulsio, &#333;nis</b> <i>f</i> [evello]\n<b>1)</b> вырывание, удаление (dentis <i>C</i>);\n<b>2)</b> истребление, уничтожение (<i>sc.</i> gentis <i>Vlg</i>)."],
  ["evulsus", "<big>evulsus</big>\n\n<b>&#275;vulsus, a, um</b> <i>part. pf. к</i>\nevello."],
  ["ex 2", "<big>ex</big>\n\n<b>II ex</b> <i>и</i> <b>&#275;</b> (<i>оба — перед согласными; перед гласными тк.</i> ex) <i>praep. cum abl.</i>\n<b>1)</b> из (exire ex urbe <i>C</i>; pocula ex auro <i>C</i>; unus ex iis <i>C</i>): aqua ex lauro decocta <i>CC</i> отвар лаврового листа;\n<b>2)</b> с: ex equo delabi <i>L</i> упасть с лошади; ejicere e saxo <i>H</i> сбросить со скалы; ex loco superiore pugnare <i>Cs</i> вести бой с возвышенного места; ex equo colloqui <i>Cs</i> говорить с (сидя на) лошади; ex vinclis causam dicere <i>Cs</i> произнести защитительную речь, находясь в оковах;\n<b>3)</b> от (oriri, nasci <i>или</i> gigni ex aliquo <i>Ter etc.</i>): non longe ex eo loco <i>Cs</i> недалеко от этого места; collis paululum ex planitie editus <i>Cs</i> холм, немного возвышающийся над равниной; statim e somno <i>T</i> тотчас же встав ото сна; requiescere ex aliqua re <i>Sl</i> отдохнуть (оправиться) от чего-л.; ex sua persona <i>C</i> от своего имени; ex persona alicujus <i>VP, Just</i> в чьей-л. роли (в качестве кого-л.); agri ex hostibus capti <i>Cs</i> земли, захваченные у противника;\n<b>4)</b> по причине, вследствие, из-за (ex ea causa <i>C</i>; qua ex re <i>C etc.</i>): ex vino vacillare <i>Q</i> шататься от (выпитого) вина; ex vulnere mori <i>L</i> умереть от раны; laborare ex pedibus <i>C</i> страдать ногами; ex eadem causa <i>C</i> по той же причине; ex te duplex nos afficit sollicitudo <i>C</i> из-за тебя у нас двойная забота; urbem e suo nomine Romam jussit nominari <i>C</i> (Ромул) велел назвать город по своему имени Римом;\n<b>5)</b> на основании, в силу, сообразно, по (ex senatus consulto <i>Sl</i>; ex jure, ex lege <i>C</i>; ex foedere <i>Lcn</i>); ex hoc <i>C</i> в силу этого; pactum, ex quo... <i>Ap</i> договор, на условиях которого... || по соображениям, в соответствии, для: e communi utilitate <i>T</i> для общего блага; e re mea <i>Ter, C etc.</i> (ex usu meo <i>Cs</i>) в моих интересах; e re publica <i>C</i> в интересах (для пользы) государства; ex nullius injuria <i>L</i> без чьего-л. ущерба; ex animo <i>C</i> от всей души (искренно) <i>или</i> глубоко, действительно (miser ex animo <i>Pl</i>); ex ratione officii <i>C</i> по соображениям долга; ex senatus sententia <i>Sl, C</i> согласно с мнением сената, <i>но</i>: ex animi sententia <i>или</i> ex sententia <i>C</i> по желанию (<i>т. е.</i> наилучшим образом); ex abundanti <i>Q</i> в изобилии; ex memoria <i>C</i> наизусть (на память); ex parte <i>C</i> частично; ex facili <i>O</i> легко; ex integro <i>PJ</i> сызнова; ex vero <i>Sl</i> действительно; ex inesperato <i>O</i> неожиданно; ex consuetudine <i>C</i> по привычке; ex opinione hominum <i>C</i> на основании общественного мнения;\n<b>6)</b> <i>для определения времени</i>: с, со времени, непосредственно после, вслед за: ex illo tempore (die) <i>C etc.</i> с того времени (дня); ex quo <i>QC, Pt</i> с тех пор как; ex quo (tempore)? <i>C</i> с каких пор?; dies ex eo die quintus <i>Cs</i> пятый после этого день; ex quo natus es <i>Sen</i> с самого твоего рождения; Cotta ex consulatu profectus est in Galliam <i>C</i> тотчас же по окончании (своего) консульства Котта отправился в Галлию; ex itinere <i>C</i> с дороги; diem ex die <i>C</i> день за днём (со дня на день); ex consule <i>Eutr, CJ</i> экс-консул (бывший консул); <i>но</i>: ex Metello consule <i>H</i> со времени консульства Метелла; e vestigio <i>Cs</i> тотчас же; ex omni occasione <i>PJ</i> при (во) всяком случае; aliud ex alio <i>C</i> одно за другим; <i>иногда для обозначения будущего</i>: ex Idibus Martiis <i>C</i> ко (со) времени (наступающих) Мартовских Ид;\n<b>7)</b> <i>для определения перехода или превращения</i> (dii ex hominibus facti <i>C</i>): e viridi pallens <i>PM</i> иззелена-бледный."],
  ["ex- 1", "<big>ex-</big>\n\n<b>I ex-</b> <i>и</i> <b>&#275;-</b>\n<i>приставка</i> (<i>в которой звук</i> x <i>обыкновенно сохраняется перед начальными гласными и</i> c, p, q, s, t <i>основного слова, а в остальных случаях выпадает</i>: e–duco, <i>или ассимилируется</i>: ef–fero; <i>реже заменяется</i> c: ec–fero); <i>обозначает преим.</i>:\n<b>1)</b> <i>отделение</i> (ex–eo);\n<b>2)</b> <i>движение вверх</i> (e–rigo);\n<b>3)</b> <i>лишение, освобождение</i> (ef–freno);\n<b>4)</b> <i>изменение качества</i> (ef–femino);\n<b>5)</b> <i>завершение</i> (e–vasto);\n<b>6)</b> <i>усиление</i> (e–gelidus)."],
  ["exabundo", "<big>exabundo</big>\n\n<b>ex–abundo, —, —, &#257;re</b>\nпреизбыточествовать, весьма изобиловать (alicujus re <i>Eccl</i>)."],
  ["exabusus", "<big>exabusus</big>\n\n<b>ex–ab&#363;sus, a, um</b> [abutor]\nиспользовавший полностью (spatio temporis <i>Amm</i>)."],
  ["exacerbatio", "<big>exacerbatio</big>\n\n<b>exacerb&#257;tio, &#333;nis</b> <i>f</i> [exacerbo]\nраздражение, озлобленность <i>Eccl</i>."],
  ["exacerbesco", "<big>exacerbesco</big>\n\n<b>exacerb&#275;sco, —, —, ere</b> [exacerbo]\nраздражаться, озлобляться <i>Ap, Sid</i>."],
  ["exacerbo", "<big>exacerbo</big>\n\n<b>ex–acerbo, &#257;v&#299;, &#257;tum, &#257;re</b>\n<b>1)</b> огорчать, <i>pass.</i> огорчаться (hoc magis exacerbatur, quod amisit <i>PJ</i>);\n<b>2)</b> озлоблять, раздражать (aliquem aliqua re <i>L, Su</i>);\n<b>3)</b> отягощать, усиливать (supplicia <i>Dig</i>)."],
  ["exacervo", "<big>exacervo</big>\n\n<b>ex–acervo, &#257;v&#299;, —, &#257;re</b>\n<b>1)</b> накоплять, нагромождать (discordias in se <i>Bo&euml;t</i>);\n<b>2)</b> накопляться (exacervantia in se tela <i>Amm</i>)."],
  ["exacesco", "<big>exacesco</big>\n\n<b>ex–ac&#275;sco, acu&#299;, —, ere</b>\nкиснуть, прокисать, становиться кислым (ficus exacescit <i>Col</i>)."],
  ["exacte", "<big>exacte</big>\n\n<b>ex&#257;ct&#275;</b> [exactus I]\nточно, тщательно <i>AG, Sid</i>."],
  ["exactio", "<big>exactio</big>\n\n<b>ex&#257;ctio, &#333;nis</b> <i>f</i> [exigo]\n<b>1)</b> изгнание (regum <i>C</i>);\n<b>2)</b> взыскание, взимание (pecuniarum <i>L</i>; frumenti, tributi <i>T</i>);\n<b>3)</b> сбор, обложение налогом, побор : e. capitum <i>T</i> подушная подать; exactionum coactor <i>Su</i> сборщик податей;\n<b>4)</b> надзор за ходом работ (e. operum publicorum <i>C</i>);\n<b>5)</b> окончание, завершение (alicujus rei <i>Vtr, Aus</i>)."],
  ["exactor", "<big>exactor</big>\n\n<b>ex&#257;ctor, &#333;ris</b> <i>m</i> [exigo]\n<b>1)</b> изгнавший (regum <i>L</i>);\n<b>2)</b> сборщик податей <i>Cs, L</i>;\n<b>3)</b> надзиратель, блюститель (disciplinae <i>Su</i>; studiorum <i>Q</i>): e. recte loquendi <i>Q</i> блюститель чистоты языка; e. promissorum <i>L</i> требующий исполнения обещаний;\n<b>4)</b> исполнитель: e. supplicii <i>L</i> исполнитель смертного приговора, палач;\n<b>5)</b> критик (sermonis <i>Su</i>; operis <i>Col</i>)."],
  ["exactrix", "<big>exactrix</big>\n\n<b>ex&#257;ctr&#299;x, &#299;cis</b> <i>f</i> [exactor]\nтребующая (epistula e. rescriptorum <i>Aug</i>)."],
  ["exactus 1", "<big>exactus</big>\n\n<b>I ex&#257;ctus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> exigo;\n<b>2.</b> <i>adj.</i> точный (numerus <i>L</i>); тщательный, аккуратный (ingenium <i>Sen</i>; vir <i>PJ</i>)."],
  ["exactus 2", "<big>exactus</big>\n\n<b>II ex&#257;ctus, &#363;s</b> <i>m</i> [exigo]\nсбыт, продажа (mercis <i>Q</i>)."],
  ["exacui", "<big>exacui</big>\n\n<b>exacu&#299;</b> <i>pf. к</i>\n<b>1)</b> exacesco <i>и</i>\n<b>2)</b> exacuo."],
  ["exacuo", "<big>exacuo</big>\n\n<b>ex–acuo, acu&#299;, ac&#363;tum, ere</b>\n<b>1)</b> острить, оттачивать, точить (ferramenta cote <i>PM</i>; mucronem in aliquem <i>C</i>); заострять (vallos furcasque <i>V</i>);\n<b>2)</b> изощрять (ut oculorum, sic ingenii aciem <i>C</i>);\n<b>3)</b> побуждать, подстрекать (aliquem aliqua re <i>Nep</i>, ad <i>или</i> in aliquid <i>PJ, H</i>);\n<b>4)</b> обострять, усиливать (morbos <i>Col</i>)."],
  ["exacutio", "<big>exacutio</big>\n\n<b>exac&#363;tio, &#333;nis</b> <i>f</i> [exacuo]\nоттачивание, заострение (calami <i>PM</i>)."],
  ["exadversum 1", "<big>exadversum</big>\n\n<b>I ex–adversum</b> <i>praep. cum acc.</i>\n(на)против (classem e. Athenas constituere <i>Nep</i>); <i>редко</i> <i>с dat.</i> (e. ei loco tonstrina erat <i>Ter</i>); против (aliquem pugnare <i>Nep</i>)."],
  ["exadversum 2", "<big>exadversum</big>\n\n<b>II ex–adversum</b> <i>adv.</i>\nнапротив (situm esse <i>Ter</i>; se collocare <i>Ap</i>)."],
  ["exadversus", "<big>exadversus</big>\n\n<b>ex–adversus</b> <i>C etc.</i>\n= exadversum."],
  ["exadvocatus", "<big>exadvocatus</big>\n\n<b>ex–advoc&#257;tus, &#299;</b> <i>m</i>\nбывший advocatus (<i>см.</i>) <i>Aug</i>."],
  ["exadvorsum", "<big>exadvorsum</big>\n\n<b>ex–advorsum (-us)</b> <i>арх. v. l.</i>\n= exadversum."],
  ["exaedificatio", "<big>exaedificatio</big>\n\n<b>exaedific&#257;tio, &#333;nis</b> <i>f</i> [exaedifico]\n<b>1)</b> построение (theatri <i>Capit</i>);\n<b>2)</b> составление, сочинение (orationis <i>C</i>)."],
  ["exaedifico", "<big>exaedifico</big>\n\n<b>ex–aedifico, &#257;v&#299;, &#257;tum, &#257;re</b>\n<b>1)</b> выстраивать, строить, воздвигать (oppidum <i>Cs</i>; domos et villas <i>Sl</i>); сооружать (navem <i>C</i>); создавать, творить (mundum <i>C</i>);\n<b>2)</b> <i>шутл.</i> выпроваживать, выгонять (aliquem ex aedibus <i>Pl</i>);\n<b>3)</b> завершать, совершать (opus <i>C</i>); доводить до крайних пределов (suam incohatam ignaviam <i>Pl</i>)."],
  ["exaequatio", "<big>exaequatio</big>\n\n<b>exaequ&#257;tio, &#333;nis</b> <i>f</i> [exaequo]\n<b>1)</b> выравнивание, уравнивание <i>L</i>;\n<b>2)</b> равнина, выровненное место <i>Vtr</i>."],
  ["exaequo", "<big>exaequo</big>\n\n<b>ex–aequo, &#257;v&#299;, &#257;tum, &#257;re</b>\n<b>1)</b> выравнивать, делать ровным (planitiem <i>Vtr</i>);\n<b>2)</b> уравнивать, делать равным (tumulos tumulis <i>bH</i>; omnium dignitatem <i>C</i>): e. libertatem <i>L</i> сделать свободу равной для всех; omnem vitam ad regulam unam e. <i>Sen</i> подчинить всю жизнь одному правилу (принципу); argentum argento e. <i>Pl</i> серебро возместить серебром же; e. aliquem cum aliquo (<i>или</i> alicui) <i>C etc.</i> ставить кого-л. наряду с кем-л. (приравнивать кого-л. к кому-л.); exaequari cum aliquo <i>Sl</i> (alicui <i>C</i>), se e. cum aliquo <i>C</i> <i>или</i> exaequare aliquem <i>O</i> стать на один уровень с кем-л., быть равным кому-л.; e. aliquem dignitate <i>Cs</i> сравняться с кем-л. в достоинстве; facta dictis exaequanda sunt <i>Sl</i> деяния (подвиги) должны излагаться соответствующим (достойным) образом."],
  ["exaeresimus", "<big>exaeresimus</big>\n\n<b>exaeresimus, a, um</b> <i>v. l.</i>\n= exhaeresimus."],
  ["exaestuatio", "<big>exaestuatio</big>\n\n<b>exaestu&#257;tio, &#333;nis</b> <i>f</i> [exaestuo]\nволнение, бурление, клокотание (amnis <i>Sol</i>)."],
  ["exaestuo", "<big>exaestuo</big>\n\n<b>ex–aestuo, &#257;v&#299;, &#257;tum, &#257;re</b>\n<b>1)</b> волноваться, приходить в волнение, бурлить, бушевать, клокотать (mare exaestuat <i>L</i>; Aetna a fundo exaestuat imo <i>V</i>);\n<b>2)</b> нагреваться, становиться горячим, раскаляться (torrente calore solis <i>Just</i>);\n<b>3)</b> вскипать, вздыматься, кипя выступать наружу (bitumen e terra exaestuat <i>Just</i>);\n<b>4)</b> разгорячаться, вспыхивать (ira <i>V</i>);\n<b>5)</b> извергать, выбрасывать (largos aestus <i>Lcr</i>)."],
  ["exaggeranter", "<big>exaggeranter</big>\n\n<b>exaggeranter</b> <i>adv.</i>\nпреувеличенно, чрезмерно (inculcare <i>Tert</i>; dicere <i>Aug</i>)."],
  ["exaggeratio", "<big>exaggeratio</big>\n\n<b>exagger&#257;tio, &#333;nis</b> <i>f</i> [exaggero]\n<b>1)</b> возведение насыпи, <i>перен.</i> нагромождение (calamitatum <i>Aug</i>);\n<b>2)</b> подъём (e. animi <i>C</i>);\n<b>3)</b> нагромождение слов, преувеличенность (orationis <i>AG</i>)."],
  ["exaggerator", "<big>exaggerator</big>\n\n<b>exagger&#257;tor, &#333;ris</b> <i>m</i>\nпреувеличивающий, склонный к раздуванию (e. famae <i>Hier</i>)."],
  ["exaggeratus", "<big>exaggeratus</big>\n\n<b>exagger&#257;tus, a, um</b>\nпреувеличенный, усиленный <i>AG</i>."],
  ["exaggero", "<big>exaggero</big>\n\n<b>ex–aggero, &#257;v&#299;, &#257;tum, &#257;re</b>\n<b>1)</b> делать насыпь, возвышать, поднимать уровень (e. planitiem <i>QC</i>);\n<b>2)</b> нагромождать, класть в кучу, накоплять (magnas opes <i>Ph</i>);\n<b>3)</b> увеличивать, приумножать (rem familiarem <i>C</i>);\n<b>4)</b> придавать силу, воодушевлять, возвышать (exaggerata altius oratio <i>C</i>; animus exaggeratus <i>C</i>);\n<b>5)</b> возвышать, возвеличивать, поднимать (aliquem honoribus <i>VP</i>);\n<b>6)</b> превозносить, восхвалять (virtutem <i>C</i>; artem oratione <i>C</i>);\n<b>7)</b> преувеличивать, усиливать, подчёркивать (injuriam <i>Q</i>)."],
  ["exagitatio", "<big>exagitatio</big>\n\n<b>exagit&#257;tio, &#333;nis</b> <i>f</i> [exagito]\nпреследование (omnium bonorum et malorum <i>Aug</i>)."],
  ["exagitator", "<big>exagitator</big>\n\n<b>exagit&#257;tor, &#333;ris</b> <i>m</i> [exagito]\nхулитель, порицатель (omnium rhetorum <i>C</i>)."],
  ["exagito", "<big>exagito</big>\n\n<b>ex–agito, &#257;v&#299;, &#257;tum, &#257;re</b>\n<b>1)</b> тревожить, вспугивать, травить, выгонять, гнать (leporem <i>O</i>): e. lustra ferarum <i>Sil</i> поднять зверей из их нор;\n<b>2)</b> взбалтывать (faecem <i>Col</i>);\n<b>3)</b> волновать (exagitata vis, <i>sc.</i> venti <i>Lcr</i>);\n<b>4)</b> преследовать, не давать покоя, беспокоить, изводить, мучить (aliquem injuriis <i>C</i>); донимать насмешками, пробирать, порицать (aliquem <i>C etc.</i>; fraudes alicujus <i>C</i>);\n<b>5)</b> возбуждать (maerorem <i>C</i>; dissensionem <i>C</i>; se ipsum <i>Sen</i>); res rumoribus exagitata <i>Sl</i> дело, возбуждавшее много толков;\n<b>6)</b> волновать, возмущать, подстрекать (vulgum, plebem <i>Sl</i>); потрясать (rem publicam seditionibus <i>Sl</i>)."],
  ["exagoga", "<big>exagoga</big>\n\n<b>exag&#333;ga, ae</b> <i>f</i> (<i>греч.</i>; <i>лат.</i> evectio)\nвывоз (bonorum <i>Pl</i>)."],
  ["exalapo", "<big>exalapo</big>\n\n<b>ex–alapo, —, —, &#257;re</b>\nдавать пощёчины, бить по щекам (aliquem <i>Aug</i>)."],
  ["exalbesco", "<big>exalbesco</big>\n\n<b>ex–alb&#275;sco, bu&#299;, —, ere</b>\n<b>1)</b> белеть <i>AG</i>;\n<b>2)</b> бледнеть <i>Enn, C</i>."],
  ["exalbidus", "<big>exalbidus</big>\n\n<b>ex–albidus, a, um</b>\nбеловатый, белесоватый (folia, vinum <i>PM</i>)."],
  ["exalbo", "<big>exalbo</big>\n\n<b>ex–albo, &#257;v&#299;, —, &#257;re</b>\nделать белым, белить (aliquid <i>Tert</i>)."],
  ["exalburnatus", "<big>exalburnatus</big>\n\n<b>ex–alburn&#257;tus, a, um</b> [alburnum]\nлишённый заболони (robur <i>PM</i>)."],
  ["exalo", "<big>exalo</big>\n\n<b>ex&#257;lo</b> <i>v. l.</i>\n= exhalo."],
  ["exaltatio", "<big>exaltatio</big>\n\n<b>exalt&#257;tio, &#333;nis</b> <i>f</i>\n<b>1)</b> величие <i>Vlg</i>;\n<b>2)</b> гордыня <i>Tert</i>."],
  ["exaltator", "<big>exaltator</big>\n\n<b>exalt&#257;tor, &#333;ris</b> <i>m</i>\nвозвеличивающий <i>Aug</i>."],
  ["exalto", "<big>exalto</big>\n\n<b>ex–alto, &#257;v&#299;, &#257;tum, &#257;re</b>\n<b>1)</b> возвышать, возвеличивать (alia e., alia submittere <i>Sen</i> — <i>v. l.</i>);\n<b>2)</b> углублять (sulcum <i>Col</i>)."],
  ["exaltus", "<big>exaltus</big>\n\n<b>ex–altus, a, um</b>\nочень высокий (trames <i>Ap</i> — <i>v. l.</i>)."],
  ["exaluminatus", "<big>exaluminatus</big>\n\n<b>ex–al&#363;min&#257;tus, a, um</b> [alumen]\nцвета квасцов, похожий на квасцы <i>PM</i>."],
  ["exambio", "<big>exambio</big>\n\n<b>ex–ambio, &#299;v&#299;, &#299;tum, &#299;re</b>\nидти на поклон (ad martyras <i>Eccl</i>); просить, молить (aliquem <i>Amm</i>); выпрашивать (aliquid <i>Eccl</i>)."],
  ["exambulo", "<big>exambulo</big>\n\n<b>ex–ambulo, —, —, &#257;re</b>\nвыходить гулять (foras <i>Pl</i>)."],
  ["examen", "<big>examen</big>\n\n<b>ex&#257;men, inis</b> <i>n</i> [ex + agmen <i>от</i> ago]\n<b>1)</b> вереница, куча, толпа, множество (servorum <i>C</i>; infantium <i>PJ</i>; malorum <i>Eccl</i>); стая (piscium <i>PM</i>); рой (apium <i>C</i>);\n<b>2)</b> стрелка у весов: aequato examine <i>V</i> в равновесии;\n<b>3)</b> взвешивание, исследование, испытание (legum <i>O</i>). examinate <i>adv.</i> испытующе, тщательно, осмотрительно <i>Amm, Eccl</i>."],
  ["examinate", "<big>examinate</big>\n\n<b>ex&#257;min&#257;t&#275;</b> <i>adv.</i>\nиспытующе, тщательно, осмотрительно <i>Amm, Eccl</i>."],
  ["examinatio", "<big>examinatio</big>\n\n<b>ex&#257;min&#257;tio, &#333;nis</b> <i>f</i> [examino]\nвзвешивание <i>Vtr</i>; исследование, испытание <i>Dig, Eccl</i>."],
  ["examinator", "<big>examinator</big>\n\n<b>ex&#257;min&#257;tor, &#333;ris</b> <i>m</i>\n<b>1)</b> взвешивающий <i>Eccl</i>;\n<b>2)</b> испытатель, исследователь <i>Amm, Eccl</i>."],
  ["examinatorius", "<big>examinatorius</big>\n\n<b>ex&#257;min&#257;t&#333;rius, a, um</b>\nиспытательный (fidei <i>Tert</i>)."],
  ["examinatrix", "<big>examinatrix</big>\n\n<b>ex&#257;min&#257;tr&#299;x, &#299;cis</b> <i>f</i>\nиспытующая (tentatio <i>Aug</i>)."],
  ["examinatus", "<big>examinatus</big>\n\n<b>ex&#257;min&#257;tus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> examino;\n<b>2.</b> <i>adj.</i> испытанный (diligentia <i>Aug</i>)."],
  ["examino", "<big>examino</big>\n\n<b>ex&#257;mino, &#257;v&#299;, &#257;tum, &#257;re</b> [examen]\n<b>1)</b> роиться (examinant alvi <i>Col</i>);\n<b>2)</b> (точно) взвешивать, отвешивать (aliquid ad certum pondus <i>Cs</i>);\n<b>3)</b> уравновешивать (aliquid <i>C etc.</i>): paribus ponderibus examinatus <i>C</i> находящийся в состоянии равновесия;\n<b>4)</b> (тщательно) исследовать, испытывать (aliquid amicorum judicio <i>PJ</i>): nulla res potest esse eadem festinata simul et examinata <i>Ap</i> ни в чём нельзя сочетать поспешности с тщательностью."],
  ["examplexor", "<big>examplexor</big>\n\n<b>ex–amplexor, —, &#257;r&#299;</b> <i>depon.</i>\nобхватывать, обнимать <i>rhH</i>."],
  ["examurco", "<big>examurco</big>\n\n<b>ex–amurco, —, —, &#257;re</b> [amurca]\nвыжимать, отжимать, сушить (aliquid <i>Ap</i>)."],
  ["examussim", "<big>examussim</big>\n\n<b>ex–amussim</b> <i>adv.</i>\nточно, тщательно, в совершенстве <i>Pl, Ap</i>."],
  ["exancillatus", "<big>exancillatus</big>\n\n<b>ex–ancill&#257;tus, a, um</b> [ancillor]\nпо-рабски служащий (falsis dis <i>Tert</i>)."],
  ["exanclo", "<big>exanclo</big>\n\n<b>exanclo</b> <i>C</i>\n= exantio."],
  ["exanguis", "<big>exanguis</big>\n\n<b>exanguis, e</b> <i>v. l.</i>\n= exsanguis."],
  ["exanima", "<big>exanima</big>\n\n<b>exanima, &#333;rum</b> <i>n</i> [exanimus]\nнеодушевлённые предметы <i>Lact</i>."],
  ["exanimabiliter", "<big>exanimabiliter</big>\n\n<b>exanim&#257;biliter</b>\nв смертельном страхе <i>Naev</i>."],
  ["exanimalis", "<big>exanimalis</big>\n\n<b>ex–anim&#257;lis, e</b>\n<b>1)</b> убийственный (curae <i>Pl</i>);\n<b>2)</b> безжизненный, бездыханный (aliquem exanimalem facere <i>Pl</i>)."],
  ["exanimatio", "<big>exanimatio</big>\n\n<b>exanim&#257;tio, &#333;nis</b> <i>f</i> [exanimo]\n<b>1)</b> удушье: vulvarum exanimationes <i>PM</i> истерические припадки;\n<b>2)</b> смертельный страх, ужас <i>C</i>."],
  ["exanimis", "<big>exanimis</big>\n\n<b>ex–animis, e</b> [anima]\n<b>1)</b> бездыханный, безжизненный, мёртвый (corpus <i>O etc.</i>): favillae exanimes <i>St</i> потухший пепел; hiems e. <i>St</i> утихшая буря;\n<b>2)</b> полумёртвый (pavore <i>T</i>); ни живой ни мёртвый (pavidus et e. <i>H</i>)."],
  ["exanimo", "<big>exanimo</big>\n\n<b>ex–animo, &#257;v&#299;, &#257;tum, &#257;re</b> [anima]\n<b>1)</b> лишать жизни, умерщвлять, убивать (aliquem <i>Lcr, C etc.</i>): se taxo e. <i>Cs</i> лишить себя жизни, приняв тисовый яд; <i>pass.</i> exanimari испустить дух, умереть (gravi vulnere <i>C</i>);\n<b>2)</b> лишать дыхания, захватывать дух, <i>pass.</i> exanimari запыхаться (milites cursu exanimati <i>Cs</i>);\n<b>3)</b> поражать, приводить в ужас: metu exanimari <i>Ter</i> обезуметь (онеметь) от страха; exanimatus conticuit <i>Pt</i> он остолбенел и замолк;\n<b>4)</b> извести, замучить (aliquem querelis <i>H</i>);\n<b>5)</b> расслаблять, лишать силы, <i>pass.</i> выдыхаться (vini faex exanimatur <i>PM</i>): verba exanimata <i>C</i> чуть слышные (слабо произнесённые) слова;\n<b>6)</b> выпускать (сжатый) воздух: folles e. <i>LJ</i> раздувать мехами (огонь)."],
  ["exanimus", "<big>exanimus</big>\n\n<b>exanimus, a, um</b> <i>Lcr, V, L, T</i>\n= exanimis."],
  ["exantlo", "<big>exantlo</big>\n\n<b>exantlo<span style=\"font-weight:normal\"> (<i>C</i> exanclo<span style=\"font-weight:normal\">), &#257;v&#299;, &#257;tum, &#257;re</b>), <b>avi, atum, are</b>\n<b>1)</b> вычерпывать, выпивать (vinum <i>Pl</i>);\n<b>2)</b> проливать (sanguinem alicujus <i>Enn</i>);\n<b>3)</b> претерпевать, выносить, переносить (labores <i>C</i>; multa aspera <i>C</i>)."],
  ["exaperio", "<big>exaperio</big>\n\n<b>ex–aperio, —, —, &#299;re</b>\nраскрывать, <i>перен.</i> распутывать (implicatissimam nodositatem <i>Aug</i>)."],
  ["exapto", "<big>exapto</big>\n\n<b>ex–apto, —, —, &#257;re</b>\nприладить, укрепить, надеть (coronas alicui <i>Ap</i>)."],
  ["exaptus", "<big>exaptus</big>\n\n<b>ex–aptus, a, um</b>\nприлаженный, укреплённый, надетый (exapta catenis tintinnabula <i>PM</i>)."],
  ["exaquesco", "<big>exaquesco</big>\n\n<b>ex–aqu&#275;sco, —, —, ere</b> [aqua]\nпревращаться в воду (mundus tum exignescere, tum e. videtur <i>Cens</i>)."],
  ["exaratio", "<big>exaratio</big>\n\n<b>exar&#257;tio, &#333;nis</b> <i>f</i> [exaro 3]\nписание, рукопись <i>Sid</i>."],
  ["exarcio", "<big>exarcio</big>\n\n<b>exarcio</b> <i>v. l.</i>\n= exsarcio."],
  ["exardeo", "<big>exardeo</big>\n\n<b>ex–&#257;rdeo, —, —, &#275;re</b>\nпылать (ignis exardebit <i>Vlg</i>)."],
  ["exardesco", "<big>exardesco</big>\n\n<b>ex–&#257;rd&#275;sco, &#257;rs&#299;, &#257;rsum, ere</b>\n<b>1)</b> загораться (materies facilis ad exardescendum <i>C</i>; ignis exardescit <i>Vlg</i>);\n<b>2)</b> <i>перен.</i> возгораться, вспыхивать (bellum, seditio exarsit <i>C, T</i>); воспылать, воспламеняться (libidinibus <i>T</i>; desiderio <i>и</i> ad spem libertatis <i>C</i>; in iras <i>V</i> <i>и</i> iracundia <i>C</i>): exarsit dies <i>M</i> день запылал зноем;\n<b>3)</b> вознегодовать, вспылить (Socratis responso judices exarserunt <i>C</i>);\n<b>4)</b> (<i>о ценах</i>) подскочить (pretia in immensum exarserunt <i>Su</i>)."],
  ["exarefio", "<big>exarefio</big>\n\n<b>ex–&#257;ref&#299;o, —, fier&#299;</b>\nсовершенно высыхать <i>PM</i>."],
  ["exareno", "<big>exareno</big>\n\n<b>ex–ar&#275;no, —, —, &#257;re</b>\nочищать от песка (aliquid <i>PM</i>) ."],
  ["exaresco", "<big>exaresco</big>\n\n<b>ex–&#257;r&#275;sco, &#257;ru&#299;, —, ere</b>\n<b>1)</b> высыхать (exarescunt vestimenta uvida <i>Pl</i>); засыхать (silva omnis exaruit <i>Su</i>); пересыхать, иссякать (fontes, amnes exarescunt <i>Cs, C</i>);\n<b>2)</b> блёкнуть, терять свежесть, увядать, пропадать (flos exaruit <i>C</i>; exaruisset facultas orationis <i>C</i>)."],
  ["exaridus", "<big>exaridus</big>\n\n<b>ex–&#257;ridus, a, um</b>\nсовершенно высохший, зачахший <i>Tert</i>."],
  ["exarmo", "<big>exarmo</big>\n\n<b>ex–armo, &#257;v&#299;, &#257;tum, &#257;re</b>\n<b>1)</b> обезоруживать, разоружать (cohortes <i>T</i>);\n<b>2)</b> расснастить (navem <i>Sen</i>);\n<b>3)</b> обезвреживать, делать безвредным, лишать силы (aliquem <i>Pt</i>; accusationem <i>PJ</i>): serpentem veneno e. <i>Sil</i> лишать змею яда;\n<b>4)</b> смягчать (aliquem lacrimis <i>Fl</i>);\n<b>5)</b> укрощать (tigres <i>Man</i>; aequor <i>Cld</i>)."],
  ["exaro", "<big>exaro</big>\n\n<b>ex–aro, &#257;v&#299;, &#257;tum, &#257;re</b>\n<b>1)</b> распахивать (agrum <i>Col</i>); выкорчёвывать сохой (radices <i>PM</i>); добывать, приобретать землепашеством (tantum frumenti <i>C</i>);\n<b>2)</b> бороздить, морщить (frontem rugis <i>H</i>); exaratus vulneribus <i>Ambr</i> покрытый ранами;\n<b>3)</b> начертать, написать, <i>шутл.</i> нацарапать (hoc litterularum <i>C</i>; librum stilo <i>Ph</i>)."],
  ["exarsi", "<big>exarsi</big>\n\n<b>ex&#257;rs&#299;</b> <i>pf. к</i>\nexardesco."],
  ["exarticulatus", "<big>exarticulatus</big>\n\n<b>ex–articul&#257;tus, a, um</b>\nнечленораздельный (sonus <i>Tert</i>)."],
  ["exartus", "<big>exartus</big>\n\n<b>ex–artus, a, um</b>\nочень узкий, тесный (trames <i>Ap</i>)."],
  ["exarui", "<big>exarui</big>\n\n<b>ex&#257;ru&#299;</b> <i>pf. к</i>\nexaresco."],
  ["exascio", "<big>exascio</big>\n\n<b>ex–ascio, (&#257;v&#299;), &#257;tum, &#257;re</b> [ascia]\nобтёсывать топором, <i>перен.</i> обрабатывать, тщательно подготовлять (aliquid <i>Pl</i>)."],
  ["exasperatio", "<big>exasperatio</big>\n\n<b>ex–asper&#257;tio, &#333;nis</b> <i>f</i>\n<b>1)</b> хриплость, хрипота (oris <i>Macr</i>; faucium <i>Veg</i>);\n<b>2)</b> раздражение, озлобленность <i>Tert</i>."],
  ["exasperatrix", "<big>exasperatrix</big>\n\n<b>exasper&#257;tr&#299;x, &#299;cis</b> <i>f</i>\nвносящая озлобление, мятежная (domus <i>Vlg</i>)."],
  ["exaspero", "<big>exaspero</big>\n\n<b>ex–aspero, &#257;v&#299;, &#257;tum, &#257;re</b>\n<b>1)</b> делать шероховатым, неровным (cutem <i>CC</i>);\n<b>2)</b> <i>мед.</i> поражать (oculum, fauces <i>CC</i>);\n<b>3)</b> волновать, вздымать (fretum <i>O</i>; exasperatum fluctibus mare <i>L</i>);\n<b>4)</b> делать грубым, хриплым (e. vocem <i>Q</i>);\n<b>5)</b> раздражать, ожесточать (aliquem, animos <i>L</i>); натравливать (canes <i>Ap</i>);\n<b>6)</b> ухудшать, обострять, усиливать (morbus exasperatur <i>CC</i>);\n<b>7)</b> чернить, представлять в дурном свете (e. rem verbis <i>Q</i>);\n<b>8)</b> <i>pass.</i> exasperari становиться диким, дичать (gens exasperata <i>L</i>);\n<b>9)</b> шлифовать, обтачивать, точить (ensem saxo <i>Sil</i>);\n<b>10)</b> покрывать выпуклыми изображениями (e. aegida signis <i>Cld</i>)."],
  ["exauctoritas", "<big>exauctoritas</big>\n\n<b>ex–auct&#333;rit&#257;s, &#257;tis</b> <i>f</i>\nосвобождение от военной присяги, <i>т. е.</i> увольнение со службы <i>CTh</i>."],
  ["exauctoro", "<big>exauctoro</big>\n\n<b>ex–auct&#333;ro, &#257;v&#299;, &#257;tum, &#257;re</b>\n<b>1)</b> увольнять (исключать) с военной службы (aliquem <i>L, T etc.</i>): se e. <i>L</i> выйти в отставку;\n<b>2)</b> <i>pass.</i> выходить из употребления, устаревать (verba exauctorata <i>Macr</i>)."],
  ["exaudibilis", "<big>exaudibilis</big>\n\n<b>ex–aud&#299;bilis, e</b>\nдостойный быть выслушанным <i>Aug</i>."],
  ["exaudio", "<big>exaudio</big>\n\n<b>ex–audio, &#299;v&#299;, &#299;tum, &#299;re</b>\n<b>1)</b> слышать издали, заслышать (hinnitum <i>C</i>): clamor exauditus est <i>Cs</i> послышался крик; non exaudita Cethegis <i>H</i> нечто неведомое Цетегам (<i>т. е.</i> древним римлянам) || расслышать (aliquid in strepitu <i>C</i>); прослышать, услышать, узнать стороной (quae exaudio dissimulare non possum <i>C</i>);\n<b>2)</b> прислушиваться, выслушивать, внимать (vota populi <i>Sen</i>; preces <i>V, O, PM</i>): monitor non exauditus <i>H</i> человек, советы которого оставлены без внимания; super Indos exauditus <i>Sil</i> известный (и) за пределами Индии;\n<b>3)</b> понимать (verba, secundum voluntatem testatoris <i>Dig</i>; nomina ex communi usu <i>Dig</i>)."],
  ["exauditio", "<big>exauditio</big>\n\n<b>ex–aud&#299;tio, &#333;nis</b> <i>f</i>\nвыслушивание (exauditione dei dignior <i>Aug</i>)."],
  ["exauditor", "<big>exauditor</big>\n\n<b>ex–aud&#299;tor, &#333;ris</b> <i>m</i>\nвыслушивающий, внемлющий <i>Vlg</i>."],
  ["exaugeo", "<big>exaugeo</big>\n\n<b>ex–augeo, aux&#299;, —, &#275;re</b>\nувеличивать, усиливать (benefacta alicujus <i>Pl</i>; orationem <i>rhH</i>)."],
  ["exauguratio", "<big>exauguratio</big>\n\n<b>exaugur&#257;tio, &#333;nis</b> <i>f</i> [exauguro]\nлишение святости, профанирование (sacellorum <i>C</i>)."],
  ["exauguro", "<big>exauguro</big>\n\n<b>ex–auguro, —, —, &#257;re</b>\nлишать святости, профанировать (fana sacellaque <i>L</i>)."],
  ["exauspico", "<big>exauspico</big>\n\n<b>ex–auspico, &#257;v&#299;, —, &#257;re</b>\nвидеть предзнаменование (в чём-л.): e. ex vinculis <i>Pl</i> рассчитывать освободиться от оков."],
  ["exballisto", "<big>exballisto</big>\n\n<b>ex–ballisto, &#257;v&#299;, —, &#257;re</b> [ballista] <i>шутл.</i>\nопрокинуть (<i>камнем, брошенным из баллисты</i>), <i>т. е.</i> прикончить (aliquem <i>Pl</i>)."],
  ["exbibo", "<big>exbibo</big>\n\n<b>exbibo</b> <i>v. l.</i>\n= ebibo."],
  ["exbola", "<big>exbola</big>\n\n<b>exbola, ae</b> <i>f</i> (<i>греч.</i>)\nметательный снаряд, <i>по др.</i> гончарный лом <i>Naev</i>."],
  ["excaecatio", "<big>excaecatio</big>\n\n<b>ex–caec&#257;tio, &#333;nis</b> <i>f</i>\n<b>1)</b> ослепление <i>Aug</i>;\n<b>2)</b> ослеплённость <i>Aug</i>."],
  ["excaeco", "<big>excaeco</big>\n\n<b>ex–caeco, &#257;v&#299;, &#257;tum, &#257;re</b>\n<b>1)</b> лишать зрения, ослеплять (aliquem <i>C, Sen</i>; Oedipus excaecatus <i>Su</i>); <i>pass.</i> excaecari <i>PM</i> ослепнуть;\n<b>2)</b> лишать глазков (<i>о растениях</i>) <i>Col, PM</i>;\n<b>3)</b> загромождать, преграждать, заваливать (itinera <i>CC</i>); запруживать (flumina <i>O</i>);\n<b>4)</b> делать тусклым, невзрачным (formam <i>Pt</i>; fulgorem argenti <i>PM</i>)."],
  ["excalceati", "<big>excalceati</big>\n\n<b>excalce&#257;t&#299;, &#333;rum</b> <i>m</i> [excalceo]\n&laquo;разутые, босые&raquo;, <i>т. е.</i> мимические актёры (<i>т. к. они не носили ни трагических cothurni, ни комических socci, а лишь сандалии</i>) <i>Sen</i>."],
  ["excalceo", "<big>excalceo</big>\n\n<b>ex–calceo, &#257;v&#299;, &#257;tum, &#257;re</b>\n<b>1)</b> разувать, снимать обувь (e. pedes <i>Su</i>);\n<b>2)</b> <i>med.</i> excalceari (<i>о трагических актёрах</i>) снять котурны (<i>см.</i> excalceati) <i>Sen</i>."],
  ["excalceor", "<big>excalceor</big>\n\n<b>excalceor, —, &#257;r&#299;</b> <i>Vr</i>\n= excalceo 1."],
  ["excalceus", "<big>excalceus</big>\n\n<b>ex–calceus, a, um</b>\nнеобутый, босой <i>Eccl</i>."],
  ["excalcio", "<big>excalcio</big>\n\n<b>excalcio</b> <i>v. l.</i>\n= excalceo."],
  ["excaldatio", "<big>excaldatio</big>\n\n<b>ex–cald&#257;tio, &#333;nis</b> <i>f</i> [caldus]\nгорячая баня <i>Capit, Vulc</i>."],
  ["excalefacio", "<big>excalefacio</big>\n\n<b>excalefacio</b> <i>Is</i>\n= excalfacio."],
  ["excalefio", "<big>excalefio</big>\n\n<b>excalef&#299;o</b> <i>Scr</i>\n= excalfio."],
  ["excalfacio", "<big>excalfacio</big>\n\n<b>ex–calfacio, f&#275;c&#299;, factum, ere</b>\nсогревать, нагревать (aliquid <i>PM</i>)."],
  ["excalfactio", "<big>excalfactio</big>\n\n<b>ex–calfactio, &#333;nis</b> <i>f</i>\nнагревание <i>PM</i>."],
  ["excalfactorius", "<big>excalfactorius</big>\n\n<b>excalfact&#333;rius, a, um</b> [excalfacio]\nнагревающий, согревающий (vis <i>PM</i>)."],
  ["excalfio", "<big>excalfio</big>\n\n<b>excalf&#299;o, factus sum, fier&#299;</b> [<i>pass. к</i> excalfacio]\nнагреваться <i>PM</i>."],
  ["excandefacio", "<big>excandefacio</big>\n\n<b>ex–candefacio, f&#275;c&#299;, factum, ere</b>\n<b>1)</b> зажигать, нагревать, <i>перен.</i> воспламенять (aliquem cupiditate <i>Vr</i> — <i>in tmesi</i>);\n<b>2)</b> (<i>о ценах</i>) взвинчивать (e. annonam <i>Vr</i>)."],
  ["excandescentia", "<big>excandescentia</big>\n\n<b>excand&#275;scentia, ae</b> <i>f</i> [excandesco]\nраздражительность, горячность, вспыльчивость, запальчивость <i>C, Ap</i>."],
  ["excandesco", "<big>excandesco</big>\n\n<b>ex–cand&#275;sco, du&#299;, —, ere</b>\n<b>1)</b> раскаляться <i>или</i> воспламеняться <i>Cato, Col, PM</i>;\n<b>2)</b> <i>мед.</i> воспаляться <i>Col</i>;\n<b>3)</b> <i>перен.</i> загораться, вспыхивать (ira <i>C</i>): excanduit et inquit <i>Pt</i> вспылив, он сказал."],
  ["excanto", "<big>excanto</big>\n\n<b>ex–canto, &#257;v&#299;, &#257;tum, &#257;re</b>\nвызывать чарами (Lethaeos deos <i>Lcn</i>; clausas puellas <i>Prp</i>); прогонять чарами (pestis excantata fugit <i>Lcn</i>); околдовывать: e. fruges <i>LXIIT ap. Sen, PM</i> волшебством перенести плоды с чужой земли на свою (<i>т. е.</i> забрать себе чужой урожай)."],
  ["excarnifico", "<big>excarnifico</big>\n\n<b>ex–carnifico, &#257;v&#299;, &#257;tum, &#257;re</b>\nистязать (aliquem tormentis <i>Ap</i>; aliquem ad interitum <i>Amm</i>); изводить, (за)мучить (aliquem <i>Ter, Sen</i>)."],
  ["excastro", "<big>excastro</big>\n\n<b>ex–castro, (&#257;v&#299;), &#257;tum, &#257;re</b>\n<b>1)</b> кастрировать, оскоплять (caper excastratus <i>AG</i>);\n<b>2)</b> очищать от шелухи (sinapi non excastratum <i>Scr</i>)."],
  ["excatarisso", "<big>excatarisso</big>\n\n<b>excatarisso, &#257;v&#299;, —, &#257;re</b> [ex + <i>греч.</i> katharizo]\nсовершенно очистить, обобрать, разорить (aliquem <i>Pt</i>)."],
  ["excaudico", "<big>excaudico</big>\n\n<b>excaudico</b> <i>v. l.</i>\n= excodico."],
  ["excavatio", "<big>excavatio</big>\n\n<b>excav&#257;tio, &#333;nis</b> <i>f</i> [excavo]\nуглубление, полость (e. rotunda lapidis <i>Sen</i>)."],
  ["excavo", "<big>excavo</big>\n\n<b>ex–cavo, &#257;v&#299;, &#257;tum, &#257;re</b>\n<b>1)</b> долбить, выдалбливать (sibi cavernam rostro <i>PM</i>): ex gemma trulla excavata <i>C</i> ковшик, сделанный из цельного самоцветного камня;\n<b>2)</b> выветривать (loca injuria temporis excavata <i>Sen</i>);\n<b>3)</b> <i>Priap</i> = paedicare."],
  ["excedo", "<big>excedo</big>\n\n<b>ex–c&#275;do, cess&#299;, cessum, ere</b>\n<b>1)</b> уходить, уезжать, удаляться (ex pugna <i>St</i>; oppido <i>Cs</i>; urbe <i>C</i>; finibus <i>и</i> ex finibus <i>L, Cs</i>; ex proelio <i>и</i> proelio <i>Cs</i>; via <i>L</i> <i>и</i> ex via <i>Cs</i>; in Boeotiam <i>L</i>; urbem <i>L</i>; patrios muros <i>Lcn</i>);\n<b>2)</b> выходить: e pueris e. <i>C</i> выйти из детского возраста; e memoria e. <i>L</i> изглаживаться из памяти (приходить в забвение, забываться); e. palma <i>V</i> уступить пальму первенства;\n<b>3)</b>: e. <i>T etc.</i> <i>или</i> e. (e) vita <i>C</i> <i>или</i> e medio <i>Ter</i> умереть;\n<b>4)</b> выступать, выдаваться вперёд, торчать (nulla pars excedit extra <i>C</i>); возвышаться (rupes quattuor stadia in altitudinem excedit <i>QC</i>); выходить за пределы, перешагнуть, переступать (supra rationem <i>VM</i>; ultra fidem <i>VM</i>; radicem montis <i>Sl</i>; justum modum <i>L, Q etc.</i>; summam octoginta milium <i>L</i>): res fidem (<i>тж.</i> fidem veritatis) excedit <i>O, PM, VP</i> это невероятно;\n<b>5)</b> превосходить (aliquem eloquentia <i>Su</i>); достигать, подниматься (ad summum imperii fastigium <i>VM</i>); превышать (nubes excedit Olympus <i>Lcn</i>): excessere metum mea jam bona <i>O</i> то, что есть уже у меня, позволяет мне ничего не бояться (<i>слова Ниобы</i>);\n<b>6)</b> удаляться, отклоняться (ex pristina consuetudine <i>bAfr</i>): e, in fabellam <i>Sen</i> рассказать (в порядке отступления от темы) басню;\n<b>7)</b> продвигаться, проникать (Scythiae rex usque Aegyptum excessit <i>Just</i>);\n<b>8)</b> переходить, превращаться (res parva dictu in magnum certamen excessit <i>L</i>);\n<b>9)</b> приходиться, совпадать: excessisse in annum Servilii et Flaminii <i>L</i> прийтись на год консульства Сервилия и Фламиния."],
  ["excellens", "<big>excellens</big>\n\n<b>excell&#275;ns, entis</b>\n<b>1.</b> <i>part. praes. к</i> excello;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> возвышенный, высокий (locus <i>bH</i>): corpore e. <i>VP</i> рослый;\n<b>2)</b> отличный, превосходный, необыкновенный, замечательный (natura, pulchritudo <i>C</i>; e. in re militari gloria <i>C</i>; cultus <i>Su</i>)."],
  ["excellenter", "<big>excellenter</big>\n\n<b>excellenter</b> [excellens]\nотлично, превосходно, замечательно (aliquid gerere <i>C</i>)."],
  ["excellentia", "<big>excellentia</big>\n\n<b>excellentia, ae</b> <i>f</i> [excellens]\n<b>1)</b> превосходство, преимущество: e. animantium reliquarum <i>C</i> превосходство (человека) над другими животными;\n<b>2)</b> высокие свойства, великолепные качества (animi e. magnitudoque <i>C</i>): propter (per <i>Sen</i>) excellentiam <i>C</i> по преимуществу."],
  ["excellentis", "<big>excellentis</big>\n\n<b>excellentis, e</b> <i>Pt</i>\n= excellens."],
  ["excelleo", "<big>excelleo</big>\n\n<b>excelleo</b> <i>C, QC, AG</i>\n= excello."],
  ["excello", "<big>excello</big>\n\n<b>ex–cello, (cellu&#299;), (celsum), ere</b>\nвыделяться, выдвигаться, превосходить, выдаваться, возвышаться, отличаться (aliqua re <i>или</i> in aliqua re <i>C etc.</i>; aut ingenio aut virtute <i>C</i>): animum e. <i>Cato</i> возгордиться; e. inter <i>C</i> (praeter <i>C</i>, super <i>L</i>) ceteros <i>или</i> e. ceteris (<i>dat.</i>) <i>C</i> превосходить остальных; e. ex omnibus <i>C</i> быть выше всех."],
  ["excelse", "<big>excelse</big>\n\n<b>excels&#275;</b> [excelsus]\n<b>1)</b> высоко, в вышину (scandere <i>Col</i>);\n<b>2)</b> возвышенно, высокопарно, напыщенно (dicere <i>C</i>);\n<b>3)</b> в высшей степени, чрезвычайно: excelsissime florere <i>VP</i> быть в полном цвету (в расцвете сил)."],
  ["excelsitas", "<big>excelsitas</big>\n\n<b>excelsit&#257;s, &#257;tis</b> <i>f</i> [excelsus]\n<b>1)</b> высота, величие (montium <i>PM</i>): in excelsitatem nasci <i>PM</i> расти в вышину;\n<b>2)</b> стройность (capitulorum Corinthiorum <i>Vtr</i>);\n<b>3)</b> возвышенность (animi <i>C</i>)."],
  ["excelsum", "<big>excelsum</big>\n\n<b>excelsum, &#299;</b> <i>n</i> [excelsus]\nвысшая степень, высокий сан (in excelso vitam agere <i>Sl</i>; excelsa et alta sperare <i>L</i>): in excelso esse <i>Sl</i> быть в самом цветущем состоянии."],
  ["excelsus", "<big>excelsus</big>\n\n<b>excelsus, a, um</b> [excello]\n<b>1)</b> высокий (mons e. <i>Cs</i> <i>и</i> excelsissimus <i>Pt</i>; locus <i>C</i>; statura <i>Su</i>); выдающийся, выступающий (cornu <i>Cs</i>; rupes <i>PM</i>): in excelso <i>C</i> <i>и</i> in excelsum <i>PM</i> высоко, в вышину, вверх;\n<b>2)</b> возвышенный (animus <i>C</i>);\n<b>3)</b> высокий, высокопоставленный (locus dignitatis <i>Just</i>);\n<b>4)</b> выдающийся, замечательный (victoria, dux <i>VP</i>)."],
  ["excepi", "<big>excepi</big>\n\n<b>exc&#275;p&#299;</b> <i>pf. к</i>\nexcipio."],
  ["exceptaculum", "<big>exceptaculum</big>\n\n<b>except&#257;culum, &#299;</b> <i>n</i> [excipio]\nуловитель, приёмник (maliloquii <i>Tert</i> — <i>sc.</i> auris)."],
  ["excepticius", "<big>excepticius</big>\n\n<b>except&#299;cius, a, um</b> [excipio]\nоставшийся, выделившийся (<i>при провеивании</i>) (alica <i>PM</i>)."],
  ["exceptio", "<big>exceptio</big>\n\n<b>exceptio, &#333;nis</b> <i>f</i> [excipio]\n<b>1)</b> исключение, изъятие, ограничение (sine ulla exceptione <i>C</i>): alicui exceptionem dare <i>C</i> сделать изъятие для кого-л.;\n<b>2)</b> <i>юр.</i> особое условие, оговорка <i>C, Sen</i>;\n<b>3)</b> протест, возражение <i>G, Dig, CJ</i>: dare alicui exceptionem <i>C</i> позволить кому-л. возразить."],
  ["exceptiuncula", "<big>exceptiuncula</big>\n\n<b>exceptiuncula, ae</b> <i>f</i> [<i>demin. к</i> exceptio]\nмаленькое ограничение, исключеньице <i>Sen</i>."],
  ["excepto", "<big>excepto</big>\n\n<b>excepto, &#257;v&#299;, &#257;tum, &#257;re</b> [<i>intens. к</i> excipio]\n<b>1)</b> вынимать (mullos de piscina <i>C</i>);\n<b>2)</b> приподнимать (aliquem <i>Cs</i>);\n<b>3)</b> вбирать, ловить (aliquid ore hiante <i>Sen</i>): e. auras <i>V</i> вдыхать (в себя) воздух; per pectora e. mortes <i>Sil</i> подставлять грудь смертельным опасностям."],
  ["exceptor", "<big>exceptor</big>\n\n<b>exceptor, &#333;ris</b> <i>m</i> [excipio]\n<b>1)</b> секретарь, делопроизводитель <i>Dig, Aug</i>;\n<b>2)</b> канцелярист, писец <i>CTh, CJ</i>."],
  ["exceptorium", "<big>exceptorium</big>\n\n<b>except&#333;rium, &#299;</b> <i>n</i>\nвместилище, приемник, сосуд (e. lactis <i>Aug</i>): e. aquarum <i>Vlg</i> водоём."],
  ["exceptorius", "<big>exceptorius</big>\n\n<b>except&#333;rius, a, um</b> [excipio]\nслужащий вместилищем (quali exceptorii, in quibus uvae comportantur <i>Dig</i>)."],
  ["excerebro", "<big>excerebro</big>\n\n<b>ex–cerebro, —, &#257;tum, &#257;re</b> [cerebrum]\n<b>1)</b> обезглавливать (canem <i>Vlg</i>);\n<b>2)</b> оглушать, опьянять (excerebratus novo vino <i>Tert</i>)."],
  ["excerno", "<big>excerno</big>\n\n<b>ex–cerno, cr&#275;v&#299;, cr&#275;tum, ere</b>\n<b>1)</b> отделять, отбирать (aliquem ex numero captorum <i>L</i>; furfures a farina <i>Col</i>);\n<b>2)</b> выделять (urinam <i>CC</i>)."],
  ["excerpo", "<big>excerpo</big>\n\n<b>ex–cerpo, cerps&#299;, cerptum, ere</b> [carpo]\n<b>1)</b> выбирать, вылущивать, вынимать (semina pomis <i>H</i>);\n<b>2)</b> извлекать: e. ex malis, si quid inest boni <i>C</i> извлекать из дурного то, что в нём может быть хорошего;\n<b>3)</b> делать извлечение (выписки), выписывать (librum <i>PJ</i>; pauca ex &laquo;Gorgia&raquo; Platonis <i>Q</i>); nihil legit, quod non excerperet <i>PJ</i> (Плиний Старший) не читал ничего, не делая выписок || цитировать (verba ex libro alicujus <i>Su</i>);\n<b>4)</b> выделять, отмечать (paucos <i>Q</i>);\n<b>5)</b> исключать (aliquem numero <i>H</i> <i>или</i> de numero illorum <i>C</i>): e. se alicui rei <i>Sen</i> стать в стороне (уклониться, сторониться) от чего-л.;\n<b>6)</b> уничтожать, истреблять (omnem nobilitatis indolem <i>VM</i>)."],
  ["excerptio", "<big>excerptio</big>\n\n<b>excerptio, &#333;nis</b> <i>f</i> [excerpo]\nизвлечение, <i>pl.</i> избранные места <i>AG</i>."],
  ["excerptum", "<big>excerptum</big>\n\n<b>excerptum, &#299;</b> <i>n</i> [excerpo]\nизвлечение, отрывок, выписка <i>Q, Sen, Fronto</i>."],
  ["excervicatio", "<big>excervicatio</big>\n\n<b>ex–cerv&#299;c&#257;tio, &#333;nis</b> <i>f</i> [cervix]\nупрямство, упорство <i>Hier</i>."],
  ["excessi", "<big>excessi</big>\n\n<b>excess&#299;</b> <i>pf. к</i>\nexcedo."],
  ["excessio", "<big>excessio</big>\n\n<b>excessio, &#333;nis</b> <i>f</i> <i>Hier</i>\n= excessus."],
  ["excessus", "<big>excessus</big>\n\n<b>excessus, &#363;s</b> <i>m</i> [excedo]\n<b>1)</b> выход, уход, отъезд <i>VP</i>: e. mentis <i>Aug</i> восторг, экстаз; e. <i>Su, T</i> <i>или</i> e. vitae (e vita) <i>C</i> уход из жизни, кончина, смерть;\n<b>2)</b> выступ (os excessus habet <i>CC</i>);\n<b>3)</b> уклонение (ab aliqua re <i>VM</i>);\n<b>4)</b> <i>ритор.</i> отступление <i>Q, PJ, T etc.</i>"],
  ["excetra", "<big>excetra</big>\n\n<b>excetra, ae</b> <i>f</i>\nзмея <i>Pl</i>; <i>бран.</i> гадюка <i>Pl, L, Ap</i>."],
  ["excidio", "<big>excidio</big>\n\n<b>exc&#299;dio, &#333;nis</b> <i>f</i> [excido II]\nразрушение, разорение (excidionem facere oppidis <i>Pl</i>)."],
  ["excidium", "<big>excidium</big>\n\n<b>excidium, &#299;</b> <i>n</i> [excido I]\n<b>1)</b> падение, разрушение, разорение, уничтожение (Trojae <i>Eutr</i>): petere excidiis urbem <i>V</i> угрожать городу разрушением;\n<b>2)</b> <i>pl.</i> развалины, руины (fumantia excidia <i>V</i>);\n<b>3)</b> гибель (legionum <i>T</i>);\n<b>4)</b> выпадение (vulvae <i>PM</i> — <i>v. l.</i>);\n<b>5)</b> заход, закат (solis <i>Eccl</i>)."],
  ["excido 1", "<big>excido</big>\n\n<b>I ex–cido, cid&#299;, —, ere</b> [cado]\n<b>1)</b> падать (equo <i>Sen</i>; in flumen <i>L</i>): e. metu <i>O</i> упасть в обморок от страха || выпадать (de manibus <i>C</i>): e. sorte <i>L</i> выпадать по жребию;\n<b>2)</b> попадать (sagitta excidit in pedem <i>PM</i>);\n<b>3)</b> исчезать, пропадать (spes excidit <i>O</i>): excidit omnis constantia attonitis <i>Pt</i> (мы) были так поражены, что вся наша решимость пропала; memoria alicujus rei excidit <i>L</i> <i>или</i> aliquid excidit de memoria <i>L</i> (ex animo <i>L</i> <i>или</i> animo <i>V, O</i>) что-л. изглаживается из памяти (забывается); pacis mentio excidit ex omnium animis <i>L</i> никто не думал более о мире; nomen tuum mihi excidit <i>O</i> я забыл твоё имя; non excidit mihi me id fecisse <i>C</i> я не забыл, что сделал это; non e. sibi <i>Sen</i> не терять самообладания; excidens <i>Q</i> забывчивый человек; non e. ab aevo <i>Prp</i> стать достоянием вечности, остаться навеки в памяти потомства; excidat illa dies aevo! <i>St</i> да изгладится этот день из вечности (<i>т. е.</i> будь он проклят)!;\n<b>4)</b> вырваться, освободиться (vinculis <i>V</i>); <i>перен.</i> выскальзывать, ускользать (a digitis <i>O</i>): liber excidit me invito <i>C</i> книга вышла в свет (получила известность) без моего ведома;\n<b>5)</b> невольно вырываться: verbum alicui <i>или</i> ex ore alicujus excidit <i>C</i> слово сорвалось с чьих-л. уст;\n<b>6)</b> терять, утрачивать, лишаться (e. ex familia <i>Pl</i>; de regno <i>L</i> <i>или</i> regno <i>QC</i>; uxore <i>Ter</i>): e. formula <i>Sen, Su</i> (= cadere causa) проиграть (судебное) дело; e. ausis <i>O</i> терпеть провал в начинаниях;\n<b>7)</b> отклониться, разойтись (ab aliquo <i>LM</i>);\n<b>8)</b> переходить, перерождаться (in vitium <i>H</i>): versus in breves excidunt <i>Q</i> стихи заканчиваются короткими слогами."],
  ["excido 2", "<big>excido</big>\n\n<b>II exc&#299;do, c&#299;d&#299;, c&#299;sum, ere</b> [ex + caedo]\n<b>1)</b> вырубать, выламывать (lapides e terra <i>C</i>); срубать (arborem <i>C</i>); высекать (columnas rupibus <i>V</i>); продолбить, прорыть (montem <i>Su</i>; saxum <i>C</i>); прорубать, прокладывать (vias inter montes <i>PM</i>); выкраивать, вырезать (peltam <i>V</i>); отрезать (alicui linguam <i>C, Pt</i>); отрубать (alicui caput <i>PM etc.</i>);\n<b>2)</b> (<i>тж.</i> e. genitalia <i>Sen</i> <i>и</i> virilitatem <i>Q</i>) кастрировать (aliquem <i>M</i>);\n<b>3)</b> разорять (urbes <i>C</i>); опустошать (agros <i>VP</i>); разбивать, взламывать (portas <i>Cs</i>); разрушать (murum <i>H</i>; domos <i>C</i>); уничтожать, истреблять (exercitum <i>VP</i>; gentem Teutonum <i>VP</i>);\n<b>4)</b> искоренять, подавлять (mala, vitia <i>C, Sen etc.</i>); вычеркнуть, изъять, исключить (aliquid ex animo <i>C</i>; aliquem numero civium <i>PJ</i>)."],
  ["excieo", "<big>excieo</big>\n\n<b>excieo, &#299;v&#299;, itum, &#275;re</b> <i>Pl, L, V etc.</i>\n= excio."],
  ["excindo", "<big>excindo</big>\n\n<b>excindo</b> <i>v. l.</i>\n= exscindo."],
  ["excio", "<big>excio</big>\n\n<b>ex–cio, &#299;v&#299; (i&#299;), &#299;tum, &#299;re</b> (<i>см. тж.</i> excieo)\n<b>1)</b> вызывать, призывать (consulem ex urbe <i>L</i>; hostem ad dimicandum <i>L</i>); вызывать чарами, заклинать (animas imis sepulcris <i>V</i>); звать, приглашать (principes coloniae Romam <i>L</i>);\n<b>2)</b> исторгать, вызывать (alicui lacrimas <i>Pl</i>): e. molem <i>V</i> вздымать валы (<i>на море</i>);\n<b>3)</b> возбуждать, причинять (tumultum, terrorem <i>L</i>): excitur pedibus sonitus <i>Lcr</i> слышен топот ног || потрясать (pulsu pedum tremit excita tellus <i>V</i>);\n<b>4)</b> побуждать, привлекать, подстрекать (aliquem largitionibus <i>Sl</i>);\n<b>5)</b> вспугивать, выгонять (suem latebris <i>O</i>); заставлять уйти, отпугивать (aliquem ab urbe <i>L</i>); (вс)тревожить (mens excita <i>Sl</i>);\n<b>6)</b> будить, пробуждать (aliquem somno <i>и</i> ex somno <i>St, L etc.</i>)."],
  ["excipio", "<big>excipio</big>\n\n<b>ex–cipio, c&#275;p&#299;, ceptum, ere</b> [ex + capio]\n<b>1)</b> вынимать, извлекать (telum e vulnere <i>CC</i>); вытаскивать (aliquem e mari <i>C</i>): e. aliquem e servitute <i>L</i> освободить кого-л. от рабства; excipi alicui rei <i>Sen etc.</i> ускользнуть от чего-л.;\n<b>2)</b> делать изъятие, исключать (neminem de antiquis <i>C</i>): Platonem excipio <i>C</i> для Платона я делаю исключение; illis exceptis <i>C</i> исключая тех; excepto (quod <i>H, Q</i> <i>или</i> si <i>Pers</i>) за исключением (того, что); si unum Hippocratem excipias <i>Ap</i> за исключением разве одного лишь Гиппократа;\n<b>3)</b> предусматривать, определять, постановлять: in foederibus exceptum est (ne) <i>C</i> в договорах предусмотрено (чтобы не); lex excipit (ut) <i>C</i> закон гласит (что);\n<b>4)</b> принимать, встречать (aliquem clamore <i>C</i>, plausu <i>V</i>, tumultu <i>Pt</i>; extremum spiritum alicujus <i>C</i>): e. aliquem manu et complexu <i>T</i> принять коголибо с распростёртыми объятиями; e. suasoriam alicujus <i>Pt</i> ответить на чью-л. защитительную речь; e. aliquem cantico <i>Pt</i> откликнуться на чью-л. просьбу песней; quis te casus excipit? <i>V</i> какая судьба постигла тебя?; virgunculam septimus aetatis annus excepit <i>Hier</i> девочке пошёл седьмой год || давать убежище, приютить (aliquem hospitaliter <i>QC</i>): aliquem epulis e. <i>T</i> угостить кого-либо;\n<b>5)</b> поглощать: ora clamantia patrium nomen excipiuntur aqua <i>O</i> уста (Икара), выкликающие отцовское имя, поглощаются пучиной;\n<b>6)</b> находиться лицом к лицу (с чём-л.): aliquem bellum excipit <i>L</i> кому-л. предстоит война; porticus excipit Arcton <i>H</i> портик обращен на север;\n<b>7)</b> записывать: dictante aliquo e. orationem <i>Su</i> записывать речь под чью-л. диктовку;\n<b>8)</b> излагать, понимать, толковать (sententiam alicujus gravius <i>Su</i>): excepit motus futuros <i>V</i> (Дидона) догадалась о предстоящих переменах; longum erat singula e. <i>Pt</i> было бы долго рассказывать обо всех подробностях;\n<b>9)</b> получать (partes <i>Nep</i>; vulnera <i>C</i>; telum <i>C</i>; vulnus humero <i>T</i>); брать, принимать на себя: e. rem publicam <i>L</i> взять на себя защиту государства; e. pericula <i>C</i> подвергнуть себя опасностям;\n<b>10)</b> выдерживать, выносить (vim fluminis <i>Cs</i>; impetum hostium <i>Cs</i>; aliquid aequo animo <i>Sen</i>);\n<b>11)</b> нападать (aliquem incautum <i>V</i>); поражать, ранить (aliquem in latus <i>V</i>);\n<b>12)</b> подхватывать, подслушивать, (у)ловить, (у)слышать (rumores <i>C</i>; sermonem eorum <i>L</i>): excepi legem parari <i>C</i> (я) слышал, что подготовляется закон;\n<b>13)</b> слушать (aliquid avidissimis auribus <i>PJ</i>);\n<b>14)</b> поддерживать (hominem labentem <i>C</i>; moribundum <i>L</i>); подпирать: corpus poplitibus exceptum <i>QC</i> коленопреклонённое тело; rostro se e. <i>Ap</i> цепляться клювом (<i>о попугае</i>); e. se in pedes <i>L</i> <i>и</i> pedibus <i>QC</i> соскочить (с лошади); in genua se e. <i>Sen</i> опуститься на колени;\n<b>15)</b> поймать, перехватывать, ловить (fugientes <i>C</i>; bestias <i>Cs</i>; turdos volitantes. <i>Pt</i>); приобретать, стяжать, снискивать (laudes, voluntates hominum <i>C</i>);\n<b>16)</b> следовать: hiemem aetas excepit <i>L</i> за зимой последовало лето; Herculis vitam immortalitas excepit <i>C</i> за жизнью Геркулеса последовало (его) бессмертие; hunc excepit Labienus <i>Cs</i> после него выступил Лабиен; tum sic excepit Juno <i>V</i> тогда Юнона сказала следующее; e. fabulae partem <i>Pt</i> вступить в разговор; linguam excipit stomachus <i>C</i> за языком начинается пищевод;\n<b>17)</b> продлить, продолжать (proelium <i>L</i>; gentem <i>V</i>);\n<b>18)</b> смешивать (aliquid aqua <i>CC</i>);\n<b>19)</b> <i>юр.</i> выдвигать оговорку <i>или</i> возражение (adversus aliquem, aliquid <i>и</i> de aliqua re <i>Dig</i>)."],
  ["excipium", "<big>excipium</big>\n\n<b>excipium, &#299;</b> <i>n</i> [excipio]\nохотничье копьё, рогатина <i>Is</i>."],
  ["excipula", "<big>excipula</big>\n\n<b>excipula, ae</b> <i>f</i> [excipio]\n<b>1)</b> сосуд <i>PM</i>;\n<b>2)</b> бассейн, водоём <i>PM</i>."],
  ["excipulum", "<big>excipulum</big>\n\n<b>excipulum, &#299;</b> <i>n</i> <i>v. l.</i>\n= excipula."],
  ["excisio", "<big>excisio</big>\n\n<b>exc&#299;sio, &#333;nis</b> <i>f</i> [excido II]\n<b>1)</b> разрушение (urbium, tectorum <i>C</i>);\n<b>2)</b> вырезывание (plagae <i>Pall</i>);\n<b>3)</b> вырез, выемка (<i>sc.</i> tignorum <i>Vtr</i>)."],
  ["excisorius", "<big>excisorius</big>\n\n<b>exc&#299;s&#333;rius, a, um</b> [excido II]\nслужащий для отсечения, хирургический (scalper <i>CC</i>)."],
  ["excissatus", "<big>excissatus</big>\n\n<b>exciss&#257;tus, a, um</b> [excindo]\nотрезанный, отрубленный (aures <i>Pl</i>)."],
  ["excitabilis", "<big>excitabilis</big>\n\n<b>excit&#257;bilis, e</b> [excito]\nвозбуждающий, вызывающий подъём (modulatio <i>CA</i>)."],
  ["excitate", "<big>excitate</big>\n\n<b>excit&#257;t&#275;</b> [excitatus]\nоживлённо, сильно (clamitare <i>Amm</i>); ярко (fulgere <i>PM</i>)."],
  ["excitatio", "<big>excitatio</big>\n\n<b>excit&#257;tio, &#333;nis</b> <i>f</i>\nвозбуждение, ободрение <i>Eccl</i>."],
  ["excitator", "<big>excitator</big>\n\n<b>excit&#257;tor, &#333;ris</b> <i>m</i>\nбудящий, возбудитель (mentium <i>Eccl</i>); раздувающий (cinerum <i>Julianus ap. Aug</i>)."],
  ["excitatorius", "<big>excitatorius</big>\n\n<b>excit&#257;t&#333;rius, a, um</b>\nвозбуждающий, бодрящий (labores <i>Aug</i>)."],
  ["excitatus", "<big>excitatus</big>\n\n<b>excit&#257;tus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> excito;\n<b>2.</b> <i>adj.</i> сильный, громкий (sonus <i>C</i>; clamor <i>L</i>); резкий (odor <i>PM</i>); яркий, сверкающий (lumina <i>Q</i>)."],
  ["excito", "<big>excito</big>\n\n<b>ex–cito, &#257;v&#299;, &#257;tum, &#257;re</b>\n<b>1)</b> заставлять подняться <i>или</i> выйти, поднимать на ноги, вызывать (aliquem foras <i>Pl</i>); отсылать, усылать (aliquem a portu <i>Pl</i>): e. feram <i>C etc.</i> поднимать (с места), выгонять зверя (<i>на охоте</i>); e. aliis leporem <i>погов. Pt</i> охотиться за зайцем для других (<i>т. е.</i> работать на чужих);\n<b>2)</b> (раз)будить (aliquem de <i>или</i> e somno <i>C, L etc.</i>): non posse videtur muscam e. <i>погов. Sen</i> кажется, он и мухи не обидит;\n<b>3)</b> поднимать (caput altius <i>CC</i>): excitata fortuna <i>C</i> возрастающее счастье;\n<b>4)</b> возбуждать, вызывать (suspicionem, risum <i>C</i>): memoriam suam ad cogitationem alicujus rei e. <i>C</i> восстановить что-л. в своей памяти;\n<b>5)</b> поднимать, укреплять (vires largioribus cibis <i>Pt</i>);\n<b>6)</b> выводить, производить, создавать (ubertatem lactis <i>PM</i>);\n<b>7)</b> (вс)тревожить (aliquem nuntio <i>L</i>);\n<b>8)</b> побуждать, понуждать, поощрять (aliquem ad laborem <i>C</i>; ad <i>и</i> in virtutem <i>Cs, V</i>; aliquem ad exquirendas causas <i>C</i>);\n<b>9)</b> разводить, раздувать (ignem <i>Cs</i>); разжигать (incendium <i>C, QC</i>); возбуждать, раздражать (aviditatem <i>PM</i>);\n<b>10)</b> ободрять (afflictos <i>C</i>; jacentem animum alicujus <i>C</i>);\n<b>11)</b> воздвигать (aras <i>V</i>; sepulcrum <i>C</i>): e. turrim <i>Cs</i> построить башню;\n<b>12)</b> выращивать (nova sarmenta <i>PM</i>);\n<b>13)</b> воскрешать (aliquem a mortuis <i>C</i>);\n<b>14)</b> выделять голосом, акцентировать (syllaba excitatur <i>Q</i>)."],
  ["excitus 1", "<big>excitus</big>\n\n<b>I excitus, a, um</b> <i>part. pf. к</i>\nexcieo."],
  ["excitus 2", "<big>excitus</big>\n\n<b>II exc&#299;tus, a, um</b> <i>part. pf. к</i>\nexcio."],
  ["excitus 3", "<big>excitus</big>\n\n<b>III exc&#299;tus, (&#363;s)</b> <i>m</i> [excio]\nзов, призыв (vocis <i>Ap</i>)."],
  ["excivi", "<big>excivi</big>\n\n<b>exc&#299;v&#299;</b> <i>pf. к</i>\nexcieo <i>и</i> excio."],
  ["exclamatio", "<big>exclamatio</big>\n\n<b>excl&#257;m&#257;tio, &#333;nis</b> <i>f</i> [exclamo]\nвосклицание, возглас <i>rhH, C, Q, T</i>."],
  ["exclamito", "<big>exclamito</big>\n\n<b>excl&#257;mito, —, —, &#257;re</b> [<i>frequ. к</i> exclamo]\nчасто <i>или</i> громко восклицать <i>Hier</i>."],
  ["exclamo", "<big>exclamo</big>\n\n<b>ex–cl&#257;mo, &#257;v&#299;, &#257;tum, &#257;re</b>\n<b>1)</b> восклицать, возглашать, издавать крики одобрения <i>C</i>; кричать (maximum <i>Pl</i> <i>и</i> quam maxime <i>C</i>);\n<b>2)</b> громко рассказывать (multa memoria digna <i>Q</i>); громко называть (aliquem <i>Pl etc.</i>);\n<b>3)</b> громко трещать (ignis exclamat <i>St</i>)."],
  ["exclaro", "<big>exclaro</big>\n\n<b>ex–cl&#257;ro, —, —, &#257;re</b>\nосвещать, озарять (exclarari Solis cursu <i>Vtr</i>)."],
  ["excludo", "<big>excludo</big>\n\n<b>ex–cl&#363;do, cl&#363;s&#299;, cl&#363;sum, ere</b> [claudo]\n<b>1)</b> исключать, выгонять (aliquem foras <i>Pl</i>); отказывать (aliquem a domo sua <i>C</i>); не допускать (aliquem ab hereditate <i>C</i>); удалять: e. aliquem moenibus <i>C</i> удалить кого-л. из стен (черты) города;\n<b>2)</b> отрезать (aliquem reditu in Asiam <i>Nep</i>): e. exercitum a re frumentaria <i>Cs</i> отрезать войско от продовольственной базы;\n<b>3)</b> удерживать, мешать, препятствовать: e. fervidos ictus <i>H</i> защищать от знойных лучей; e. aliquem <i>Pt</i> запереть за кем-л. дверь (<i>чтобы не допустить его возвращения</i>) || предотвращать, делать невозможным (vitia, actiones <i>C</i>); <i>pass.</i> excludi не иметь возможности (excludi anni tempore a navigatione <i>Cs</i>): tempore exclusus <i>Cs</i> будучи стеснён во времени, за недостатком времени;\n<b>4)</b> отделять (Euphrates excludit Armeniam a Cappadocia <i>PM</i>);\n<b>5)</b> высиживать (pullos suos in nido <i>C</i>); <i>pass.</i> вылупляться (pullus exclusus est <i>Su</i>);\n<b>6)</b> выставлять наружу, оставлять непокрытым (caput pallio <i>Pt</i>; aures <i>Sen</i>);\n<b>7)</b> заканчивать, служить заключением (excludit volumen Genethliacon Lucani <i>St</i>);\n<b>8)</b> выбивать, вышибать (oculum alicui <i>Pl</i> — <i>v. l.</i>); вынимать, извлекать (gemmam inclusam auro alieno <i>Dig</i>)."],
  ["exclusio", "<big>exclusio</big>\n\n<b>excl&#363;sio, &#333;nis</b> <i>f</i> [excludo]\n<b>1)</b> исключение, устранение, удаление <i>Ter</i>: e. ventorum <i>Vtr</i> защита от ветров;\n<b>2)</b> <i>юр.</i> отказ в приёме иска к рассмотрению <i>Dig</i>."],
  ["exclusor", "<big>exclusor</big>\n\n<b>excl&#363;sor, &#333;ris</b> <i>m</i> [excludo]\nизгоняющий (e. daemoniorum <i>Aug</i>)."],
  ["exclusorius", "<big>exclusorius</big>\n\n<b>excl&#363;s&#333;rius, a, um</b> <i>юр.</i>\nисключающий (replicatio <i>Dig</i>)."],
  ["exclusti", "<big>exclusti</big>\n\n<b>excl&#363;st&#299;</b> <i>Ter</i>\n= exclusisti (<i>2 л. sg. pf. к</i> excludo)."],
  ["exclusus", "<big>exclusus</big>\n\n<b>excl&#363;sus, a, um</b> <i>part. pf. к</i>\nexcludo: exclusissimus <i>Pl ирон.</i> окончательно изгнанный."],
  ["excoco", "<big>excoco</big>\n\n<b>excoco</b> <i>PM</i>\n= excoquo."],
  ["excoctio", "<big>excoctio</big>\n\n<b>excoctio, &#333;nis</b> <i>f</i> [excoquo]\n<b>1)</b> выпечка (panis <i>CJ</i>);\n<b>2)</b> обжигание (calcis <i>CTh, CJ</i>)."],
  ["excoctus", "<big>excoctus</big>\n\n<b>excoctus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> excoquo;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> перезрелый, <i>т. е.</i> чрезмерный (maturitas hordei <i>Col</i>);\n<b>2)</b> очищенный (argentum <i>AG</i>)."],
  ["excodico", "<big>excodico</big>\n\n<b>ex–c&#333;dico (excaudico), —, —, &#257;re</b> [caudex]\n<b>1)</b> выкорчёвывать (arbores, arundineta <i>lex ap. Frontin</i>; <i>перен.</i> omnem silvam libidinum <i>Tert</i>);\n<b>2)</b> окапывать (vites <i>Pall</i>)."],
  ["excogitatio", "<big>excogitatio</big>\n\n<b>exc&#333;git&#257;tio, &#333;nis</b> <i>f</i> [excogito]\n<b>1)</b> выдумывание, придумывание <i>C etc.</i>;\n<b>2)</b> изобретательность (inventio atque e. <i>C</i>)."],
  ["excogitator", "<big>excogitator</big>\n\n<b>exc&#333;git&#257;tor, &#333;ris</b> <i>m</i> [excogito]\nизобретатель, придумавший (e. alicujus rei <i>Q</i>)."],
  ["excogitatus", "<big>excogitatus</big>\n\n<b>exc&#333;git&#257;tus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> excogito;\n<b>2.</b> <i>adj.</i> изысканный, отборный <i>Su</i>."],
  ["excogito", "<big>excogito</big>\n\n<b>ex–c&#333;gito, &#257;v&#299;, &#257;tum, &#257;re</b>\nвыдумывать, придумывать (multa ad ornatum locorum <i>Hirt</i>): nihil non e. ad aliquam rem <i>Su</i> сделать всё для чего-л.; non excogito (cur) <i>QC</i> ума не приложу (почему); ad haec cogita vel potius excogita <i>C</i> подумай или, лучше сказать, придумай, что тут делать."],
  ["excolo 1", "<big>excolo</big>\n\n<b>I ex–colo, colu&#299;, cultum, ere</b>\n<b>1)</b> тщательно обрабатывать (agrum <i>PJ</i>); выращивать, разводить (silvas <i>Vr</i>); подвергать обработке, прясть (lanas <i>O</i>): e. victus <i>PM</i> добывать обработкой земли пищу, <i>но тж.</i> <i>C</i> улучшать питание || лелеять, пестовать, ухаживать, холить (aliquem <i>PJ</i>);\n<b>2)</b> делать гладким, брить (hirsutas genas <i>M</i>);\n<b>3)</b> образовывать, облагораживать, совершенствовать (animos doctrina <i>C</i>; vitam per artes <i>V</i>): non satis excultus doctrina <i>C</i> не вполне культурный (человек) || благоустраивать, украшать (urbem <i>Su</i>);\n<b>4)</b> возвеличивать, прославлять (aliquem <i>O, PJ</i>); почитать, чтить (deos <i>Ph etc.</i>)."],
  ["excolo 2", "<big>excolo</big>\n\n<b>II ex–c&#333;lo, —, —, &#257;re</b>\nпроцеживать (acetum <i>Pall</i>): e. culicem, camelum autem glutire <i>погов. Vlg</i> оцеживать комара, а верблюда проглатывать."],
  ["excomedo", "<big>excomedo</big>\n\n<b>ex–comedo, —, —, ere</b>\nсъедать, потреблять (aliquid <i>Ap</i>)."],
  ["excommunicatio", "<big>excommunicatio</big>\n\n<b>ex–comm&#363;nic&#257;tio, &#333;nis</b> <i>f</i>\nотлучение от церкви <i>Eccl</i>."],
  ["excommunico", "<big>excommunico</big>\n\n<b>ex–comm&#363;nico, —, —, &#257;re</b>\nотлучать от церкви <i>Eccl</i>."],
  ["exconcinno", "<big>exconcinno</big>\n\n<b>ex–concinno, &#257;v&#299;, —, &#257;re</b>\nтщательно приготовлять, надлежащим образом устраивать (aliquid lepide <i>Pl</i> — <i>v. l.</i>)."],
  ["excondo", "<big>excondo</big>\n\n<b>ex–condo, —, —, ere</b>\nформировать, создавать (aliquem <i>Tert</i>)."],
  ["excongruus", "<big>excongruus</big>\n\n<b>ex–congruus, a, um</b>\nнесоответствующий (laus <i>Symm</i>)."],
  ["exconsul", "<big>exconsul</big>\n\n<b>ex–c&#333;nsul, ulis</b> <i>m</i>\nбывший консул <i>Eccl</i>."],
  ["excoquo", "<big>excoquo</big>\n\n<b>ex–coquo, cox&#299;, coctum, ere</b>\n<b>1)</b> кипятить, вываривать (aliquid vino <i>PM</i>): e. corpus in sole <i>ирон. Sen</i> жариться (загорать) на солнце || выпаривать (aliquid ad dimidium <i>Col</i>); прокаливать, закалять (ferrum <i>O</i>); сжигать, уничтожать огнём (omne vitium <i>V</i>); очищать огнём (argentum <i>AG</i>); высушивать (terram <i>Lcr</i>); расплавлять (imagines flammis <i>PJ</i>); переплавлять (arenas in vitrum <i>T</i>);\n<b>2)</b> придумывать, изобретать (alicui malum <i>Pl</i>);\n<b>3)</b> жечь как огнём, удручать, мучить (mentem <i>Sen</i>)."],
  ["excorio", "<big>excorio</big>\n\n<b>ex–corio, &#257;v&#299;, —, &#257;re</b> [corium] (<i>тж.</i> pellem e. <i>Vlg</i>)\nсдирать шкуру, снимать кожу, обдирать (aliquid <i>Eccl</i>)."],
  ["excornis", "<big>excornis</big>\n\n<b>ex–cornis, e</b> [cornu]\nбезрогий (bestia <i>Tert</i>)."],
  ["excors", "<big>excors</big>\n\n<b>ex–cors, cordis</b> [cor]\nбезумный, неразумный, глупый <i>Pl, C, H etc.</i>"],
  ["excortico", "<big>excortico</big>\n\n<b>ex–cortico, —, —, &#257;re</b> [cortex]\nснимать кору, обдирать (virgas <i>Aug</i>)."],
  ["excreat-", "<big>excreat-</big>\n\n<b>excre&#257;t-</b> <i>v. l.</i>\n= exscreat-."],
  ["excrementum 1", "<big>excrementum</big>\n\n<b>I excr&#275;mentum, &#299;</b> <i>n</i> [excerno]\nотход, отсев, высевки (frugum <i>Pall</i>); выделение (excrementa oris, narium <i>T</i>); экскременты, кал <i>PM</i>."],
  ["excrementum 2", "<big>excrementum</big>\n\n<b>II excr&#275;mentum, &#299;</b> <i>n</i> [excresco]\nмыщелка, выступ (costarum <i>Sid</i>)."],
  ["excremo", "<big>excremo</big>\n\n<b>ex–cremo, —, —, &#257;re</b>\nсжигать (aliquid <i>Tert</i>)."],
  ["excreo 1", "<big>excreo</big>\n\n<b>I ex–creo, —, —, &#257;re</b>\nсоздавать, творить <i>Is</i>."],
  ["excreo 2", "<big>excreo</big>\n\n<b>II excreo</b> <i>v. l.</i>\n= exscreo."],
  ["excrescentia", "<big>excrescentia</big>\n\n<b>excr&#275;scentia, ium</b> <i>n</i> [excresco]\nнаросты (<i>на теле</i>) <i>PM</i>."],
  ["excresco", "<big>excresco</big>\n\n<b>ex–cr&#275;sco, cr&#275;v&#299;, cr&#275;tum, ere</b>\n<b>1)</b> разрастаться (in longitudinem <i>PM</i>); нарастать, образовывать нарост (in latere <i>Su</i>); вырастать (palma excrevit <i>Col</i>);\n<b>2)</b> усиливаться, увеличиваться, разбухать: litium series excrevit <i>Su</i> число тяжб возросло."],
  ["excreta", "<big>excreta</big>\n\n<b>excr&#275;ta, &#333;rum</b> <i>n</i> [excerno]\nмякина, высевки (tritici <i>Col</i>)."],
  ["excretus", "<big>excretus</big>\n\n<b>excr&#275;tus, a, um</b> <i>part. pf. к</i>\n<b>1)</b> excerno <i>и</i>\n<b>2)</b> excresco."],
  ["excrevi", "<big>excrevi</big>\n\n<b>excr&#275;v&#299;</b> <i>pf. к</i>\n<b>1)</b> excerno <i>и</i>\n<b>2)</b> excresco."],
  ["excribo", "<big>excribo</big>\n\n<b>exc&#299;ibo</b> <i>v. l.</i>\n= exscribo."],
  ["excruciabilis", "<big>excruciabilis</big>\n\n<b>excruci&#257;bilis, e</b> [excrucio]\n<b>1)</b> заслуживающий истязаний, достойный пыток <i>Pl</i>;\n<b>2)</b> мучительный (exitium <i>Eccl</i>)."],
  ["excruciatio", "<big>excruciatio</big>\n\n<b>excruci&#257;tio, &#333;nis</b> <i>f</i> <i>Aug</i>\n= excruciatus."],
  ["excruciator", "<big>excruciator</big>\n\n<b>excruci&#257;tor, &#333;ris</b> <i>m</i>\nмучитель, палач <i>Aug</i>."],
  ["excruciatus", "<big>excruciatus</big>\n\n<b>excruci&#257;tus, &#363;s</b> <i>m</i> [excrucio]\nпытка, истязание, мучение (corporis <i>Eccl</i>)."],
  ["excrucio", "<big>excrucio</big>\n\n<b>ex–crucio, &#257;v&#299;, &#257;tum, &#257;re</b>\n<b>1)</b> истязать, мучить (aliquem omni supplicio <i>C</i>); причинять страдания, терзать (sese animi e. <i>Pl</i>); <i>pass.</i> страдать (excrucior, male mihi est <i>Pl</i>);\n<b>2)</b> вынуждать пытками (res excruciata <i>Tert</i>)."],
  ["excubatio", "<big>excubatio</big>\n\n<b>excub&#257;tio, &#333;nis</b> <i>f</i> [excubo]\n<b>1)</b> стояние на карауле, несение охраны <i>Dig</i>;\n<b>2)</b> оберегание (amicorum <i>VM</i>)."],
  ["excubiae", "<big>excubiae</big>\n\n<b>excubiae, &#257;rum</b> <i>f</i> [excubo]\n<b>1)</b> охрана: agere excubias alicui <i>O, T</i> стоять на страже, охранять кого-л.;\n<b>2)</b> караул, стража, пост, пикет (militum <i>Su</i>; <i>перен.</i> vigil ignis, e. divum (= deorum) aeternae <i>V</i>);\n<b>3)</b> <i>Pl</i> (<i>sensu obsceno</i>) = adulterium."],
  ["excubialis", "<big>excubialis</big>\n\n<b>excubi&#257;lis, e</b> [excubiae]\nкараульный, сторожевой (curae <i>Sid</i>)."],
  ["excubicularius", "<big>excubicularius</big>\n\n<b>ex–cubicul&#257;rius, &#299;</b> <i>m</i>\nбывший cubicularius (<i>см.</i>) <i>CJ</i>."],
  ["excubitor", "<big>excubitor</big>\n\n<b>excubitor, &#333;ris</b> <i>m</i> [excubo]\nкараульный, часовой, страж <i>Col, V, Su</i>; <i>pl.</i> караульный пост, стража, пикет <i>Cs, Su</i>."],
  ["excubitrix", "<big>excubitrix</big>\n\n<b>excubitr&#299;x, &#299;cis</b> <i>f</i>\nстоящая на страже, хранительница <i>Sen</i>."],
  ["excubitus", "<big>excubitus</big>\n\n<b>excubitus, &#363;s</b> <i>m</i> [excubo]\nнесение охраны, стояние на карауле, караул <i>bH</i>."],
  ["excubo", "<big>excubo</big>\n\n<b>ex–cubo, cubu&#299;, cubitum, &#257;re</b>\n<b>1)</b> находиться на открытом воздухе, ночевать вне дома (in agro <i>C</i>);\n<b>2)</b> стоять лагерем (in armis <i>Cs</i>);\n<b>3)</b> охранять, стоять на карауле, караулить (ad portum, pro castris <i>Cs</i>; per muros <i>V</i>);\n<b>4)</b> бодрствовать, неусыпно заботиться, печься (pro aliquo <i>C, VM etc.</i>; alicui <i>и</i> alicui rei <i>Lcn, PM</i>; in <i>или</i> ad aliquid <i>Sen, Cs</i>): e. animo <i>C</i> быть настороже;\n<b>5)</b> <i>поэт.</i> покоиться (Cupido excubat pulchris in genis, <i>sc.</i> puellae <i>H</i>);\n<b>6)</b> поджидать (adventui alicujus <i>Ap</i>)."],
  ["excudo", "<big>excudo</big>\n\n<b>ex–c&#363;do, c&#363;d&#299;, c&#363;sum, ere</b>\n<b>1)</b> выбивать, высекать: e. scintillam silici <i>V</i> высекать искру из кремня;\n<b>2)</b> вырезать, выковывать, чеканить (aera <i>V</i>): e. effigiem alicujus caelamine <i>Ap</i> выгравировать чьё-л. изображение;\n<b>3)</b> выделывать, изготовлять (ceras <i>V</i>); высиживать (pullos <i>C etc.</i>; ova <i>Vr</i>): e. librum <i>T</i> сочинить книгу."],
  ["exculcator", "<big>exculcator</big>\n\n<b>exculc&#257;tor, &#333;ris</b> <i>m</i> <i>воен.</i>\nсолдат авангарда, разведчик <i>Veg</i>."],
  ["exculcatus", "<big>exculcatus</big>\n\n<b>exculc&#257;tus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> exculco;\n<b>2.</b> <i>adj.</i> избитый, <i>перен.</i> вышедший из употребления, устарелый (verba <i>AG</i>)."],
  ["exculco", "<big>exculco</big>\n\n<b>ex–culco, &#257;v&#299;, &#257;tum, &#257;re</b> [calco]\n<b>1)</b> вытаптывать, <i>перен.</i> выбивать (pugnis aliquid ex aliquo <i>Pl</i>);\n<b>2)</b> утаптывать (terram <i>Cs</i>)."],
  ["exculpo", "<big>exculpo</big>\n\n<b>exculpo</b> <i>v. l.</i>\n= exsculpo."],
  ["excultor", "<big>excultor</big>\n\n<b>ex–cultor, &#333;ris</b> <i>m</i>\nвозделывающий, разводящий: e. florum <i>Tert</i> цветовод."],
  ["excultus", "<big>excultus</big>\n\n<b>excultus, a, um</b> <i>part. pf. к</i>\nexcolo I."],
  ["excuneatus", "<big>excuneatus</big>\n\n<b>ex–cune&#257;tus, a, um</b> [cuneus]\nвытесненный (со своего места) <i>Ap</i>."],
  ["excurator", "<big>excurator</big>\n\n<b>ex–c&#363;r&#257;tor, &#333;ris</b> <i>m</i>\nбывший curator (<i>см.</i>) <i>Aug</i>."],
  ["excuratus", "<big>excuratus</big>\n\n<b>exc&#363;r&#257;tus, a, um</b>\nстарательно приготовленный, тщательно отобранный (victus <i>Pl</i>)."],
  ["excurio", "<big>excurio</big>\n\n<b>ex–c&#363;rio, —, —, &#257;re</b> [curia]\nизгонять из курии (сената) (aliquem <i>Vr</i>)."],
  ["excurro", "<big>excurro</big>\n\n<b>ex–curro, cucurr&#299; (curr&#299;), cursum, ere</b>\n<b>1)</b> выбегать, устремляться (foras, <i>sc.</i> domo <i>C</i>); вытекать, сбегать (fons excurrit ex summo montis cacumine <i>QC</i>);\n<b>2)</b> совершать экскурсию, поездку, уезжать (in Pompejanum <i>C</i>);\n<b>3)</b> <i>воен.</i> совершать вылазку, делать нападение, набег (omnibus portis <i>L</i>; in agros Romanos <i>L</i>);\n<b>4)</b> расходиться, распространяться, <i>перен.</i> проявляться, обнаруживаться : campus in quo virtus e. possit <i>C</i> поприще, на котором дарования могут развернуться; ne oratio excurrat longius <i>C</i> чтобы не отклониться от основной темы; oratio excurrit pariter cum sententia <i>C</i> речь развивается параллельно с развитием темы (<i>т. е.</i> не прерывается отступлениями);\n<b>5)</b> выступать, выдаваться (in aequora <i>O</i>; promunturium excurrit in altum <i>L</i>); простираться (usque ad mare <i>QC</i>);\n<b>6)</b> оканчиваться (versus in quinque syllabas excurrit <i>Q</i>);\n<b>7)</b> быть в излишке, превышать (decem — <i>sc.</i>auri pondo — et quod excurrit <i>Dig</i>);\n<b>8)</b> пробегать: excurso spatio <i>Ter</i> пройдя (свой жизненный) путь;\n<b>9)</b> обходить молчанием (multa, ne modum excedam, excurro <i>Sen</i>)."],
  ["excursatio", "<big>excursatio</big>\n\n<b>excurs&#257;tio, &#333;nis</b> <i>f</i> [excurro]\nвылазка, нападение, набег <i>VM</i> (<i>v. l.</i>)."],
  ["excursator", "<big>excursator</big>\n\n<b>excurs&#257;tor, &#333;ris</b> <i>m</i> [excurso] <i>воен.</i>\nзастрельщик, ведущий разведку боем <i>Amm</i>."],
  ["excursio", "<big>excursio</big>\n\n<b>excursio, &#333;nis</b> <i>f</i> [excurro]\n<b>1)</b> <i>воен.</i> вылазка, нападение, набег (barbarorum <i>Nep</i>): excursionem facere ex oppido <i>Cs</i> делать вылазку из города;\n<b>2)</b> поездка, экскурсия (excursiones crebrae <i>PJ</i>, longinquae aut breves <i>Dig</i>);\n<b>3)</b> простор, свобода: libera adjiciendi e. <i>Q</i> возможность внести дополнения;\n<b>4)</b> уклонение, отступление (<i>от основного предмета</i>) (ex narratione <i>Q</i>);\n<b>5)</b> выбегание вперёд (oratoris <i>Q</i>); <i>перен.</i> забегание вперёд, упреждение (prima orationis e. <i>C</i>)."],
  ["excurso", "<big>excurso</big>\n\n<b>excurso, —, —, &#257;re</b> [<i>frequ. к</i> excurro]\nсовершать набеги (innumeris latebris <i>St</i>)."],
  ["excursor", "<big>excursor</big>\n\n<b>excursor, &#333;ris</b> <i>m</i> [excurro]\nразведчик, соглядатай, лазутчик <i>C, VM, Ap</i>."],
  ["excursus", "<big>excursus</big>\n\n<b>excursus, &#363;s</b> <i>m</i> [excurro]\n<b>1)</b> вылет, полёт (apum <i>V</i>);\n<b>2)</b> истечение, исток (fontis <i>PJ</i>);\n<b>3)</b> <i>воен.</i> нападение, набег <i>Cs</i>; вылазка (e. subitus <i>T</i>);\n<b>4)</b> выход в море, отплытие (navigiorum <i>bAl</i>);\n<b>5)</b> выступ <i>или</i> отрог (montis <i>PM</i>);\n<b>6)</b> <i>ритор.</i> отступление, экскурс <i>Q, PJ</i>."],
  ["excusabilis", "<big>excusabilis</big>\n\n<b>exc&#363;s&#257;bilis, e</b> [excuso]\nизвинительный, простительный (delicti pars <i>O</i>; error <i>VM</i>)."],
  ["excusabiliter", "<big>excusabiliter</big>\n\n<b>exc&#363;s&#257;biliter</b>\nпростительно (excusabilius peccare <i>Aug</i>)."],
  ["excusabundus", "<big>excusabundus</big>\n\n<b>exc&#363;s&#257;bundus, a, um</b> [excuso]\nизвиняющийся, оправдывающийся <i>Ap</i>."],
  ["excusate", "<big>excusate</big>\n\n<b>exc&#363;s&#257;t&#275;</b> [excusatus]\nизвинительно, простительно <i>Q, PJ, Just, T</i>."],
  ["excusatio", "<big>excusatio</big>\n\n<b>exc&#363;s&#257;tio, &#333;nis</b> <i>f</i> [excuso]\n<b>1)</b> извинение, оправдание: e. alicujus <i>Cs</i> оправдание кого-л.; e. alicujus rei извинение чего-л. (peccati <i>C</i>) <i>или</i> на основании чего-л. (valetudinis <i>C</i>); excusationem alicujus accipere <i>C</i> принять чьё-л. извинение; excusationem alicui dare <i>C</i> извинить кого-л.; stultitia excusationem non habet <i>C</i> глупость непростительна; excusatione uti temporis <i>C</i> ссылаться в оправдание на (плохие) обстоятельства; e. minime accipienda <i>C</i> совершенно неприемлемая отговорка (неуважительная причина);\n<b>2)</b> отклонение, отказ (e. legationis obeundae <i>C</i>); уклонение (inertia est laboris e. <i>PS</i>);\n<b>3)</b> <i>юр.</i> освобождение (от каких-л. повинностей) (e. munerum <i>Dig</i>): excusationis jure tutelam suscipere nolle <i>Dig</i> на основании закона отказаться от опекунства."],
  ["excuso", "<big>excuso</big>\n\n<b>ex–c&#363;so, &#257;v&#299;, &#257;tum, &#257;re</b> [causa]\n<b>1)</b> извинять, оправдывать: e. aliquem (se) apud aliquem <i>или</i> alicui <i>C</i> оправдывать кого-л. (оправдываться) перед кем-л.; se e. de aliqua re <i>Cs</i> извиняться за что-л.;\n<b>2)</b> извиняться (чём-л.), приводить в оправдание (morbum <i>C</i>; inopiam <i>Cs</i>);\n<b>3)</b> возместить, искупить (crimen lacrimis <i>St</i>);\n<b>4)</b> вменять себе в вину, упрекать себя: quod solum excusat, solum miraris in illo <i>O</i> то, в чём он единственно винит себя, единственно и вызывает в тебе восхищение;\n<b>5)</b> <i>юр.</i> освобождать, увольнять (от каких-л. обязанностей) (se e. a tutela <i>Dig</i>; aliqua necessitate excusari <i>Dig</i>);\n<b>6)</b> защищать, оберегать (se a calore solis <i>Pall</i>): e., ne poma vermiculosa nascantur <i>Pall</i> предохранять от появления червивых плодов."],
  ["excusor", "<big>excusor</big>\n\n<b>exc&#363;sor, &#333;ris</b> <i>m</i> [excudo]\nмедник, чеканщик <i>Q</i>."],
  ["excusse", "<big>excusse</big>\n\n<b>excuss&#275;</b> [excutio]\nсо всего размаха, изо всех сил (mittere pilam <i>Sen</i>)."],
  ["excussi", "<big>excussi</big>\n\n<b>excuss&#299;</b> <i>pf. к</i>\nexcutio."],
  ["excussio", "<big>excussio</big>\n\n<b>excussio, &#333;nis</b> <i>f</i> [excutio]\n<b>1)</b> встряхивание (olearum <i>Vlg</i>); отряхивание (от пыли) (pedum <i>Hier</i>);\n<b>2)</b> выделение (humoris <i>CA</i>)."],
  ["excussorius", "<big>excussorius</big>\n\n<b>excuss&#333;rius, a, um</b> [excutio]\nслужащий для перетряхивания, для просеивания (cribrum <i>PM</i>)."],
  ["excussus 1", "<big>excussus</big>\n\n<b>I excussus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> excutio;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> вытянутый, простёртый: palma excussissima pulsare <i>Pt</i> ударять со всего размаху ладонью, <i>но</i>: excussis manibus <i>Sen</i> (с) пустыми руками;\n<b>2)</b> обдуманный, взвешенный (consilium <i>VM</i>)."],
  ["excussus 2", "<big>excussus</big>\n\n<b>II excussus, &#363;s</b> <i>m</i>\nвытряхивание, выбивание <i>Eccl</i>."],
  ["excusus", "<big>excusus</big>\n\n<b>exc&#363;sus, a, um</b> <i>part. pf. к</i>\nexcudo."],
  ["excutio", "<big>excutio</big>\n\n<b>ex–cutio, cuss&#299;, cussum, ere</b> [ex + quatio]\n<b>1)</b> отряхивать, стряхивать (pulverem digitis <i>O</i>): e. pulverem pedum <i>и</i> de pedibus <i>Vlg, Eccl</i> отряхнуть прах со своих ног || сбрасывать (equus excussit equitem <i>L</i>); свергать (jugum <i>PJ</i>); швырять, бросать (litteras in terram <i>C</i>); метать (glandem <i>L</i>; tela <i>T</i>); извергать, проливать (imbrem <i>QC</i>);\n<b>2)</b> выбивать (oculum <i>Pl, Su</i>); сбивать (circulus de cupa excussus <i>Pt</i>); молотить (baculis spicas <i>Col</i>): e. onus <i>O</i> вытравить плод;\n<b>3)</b> <i>перен.</i> прогонять, подавлять (metum de corde <i>O</i>; segnitiem <i>Sen</i>); отбрасывать (verecundiam <i>Sen</i>);\n<b>4)</b> утолять (sitim <i>Sen</i>);\n<b>5)</b> вырывать, исторгать (agnam ore lupi <i>O</i>; aliquid de manibus <i>C</i>);\n<b>6)</b> вызывать, вынуждать (risum <i>H</i>): e. sudorem <i>Nep</i> заставить вспотеть, бросить в пот; e. alicui lacrimas <i>Pl, Ter</i> исторгнуть у кого-л. слёзы;\n<b>7)</b> выгонять, изгонять (aliquem patria <i>V</i>; hostem oppidis <i>Fl</i>): se e. domo <i>Ter</i> бежать из дому; e. aliquem somno <i>V, O</i> нарушить чей-л. сон; <i>pass.</i> excuti быть унесённым (cursu <i>V</i>);\n<b>8)</b> разрушать (fundo moenia <i>St</i>); устранять, удалять, уничтожать (febrem <i>PM</i>; verborum jactationem <i>C</i>): e. sibi opinionem aliquam <i>C</i> отказаться от какого-л. мнения; e. somnum <i>O</i> прогнать сон; e. foedus <i>V</i> расторгнуть договор; e. nasum <i>Pers</i> высморкаться; e. aliquem aliqua re <i>V, H, O</i> лишить кого-л. чего-л.;\n<b>9)</b> встряхивать, трясти (vestem <i>Pt</i>; pallium <i>Pl</i>); обшарить, перерыть (bibliothecas <i>Q</i>); обрыскать (freta <i>O</i>): e. aliquem (<i>sc.</i> vestem alicujus) <i>C</i> обыскать кого-л.;\n<b>10)</b> перебирать, рассматривать, исследовать, разбирать (omne scriptorum genus <i>Q</i>; verbum <i>C</i>; delata <i>QC</i>); разъяснять (causam alicujus rei <i>Pt</i>); допрашивать (aliquem <i>C</i>);\n<b>11)</b> развёртывать (rudentem <i>V</i>); простирать, выпрямлять, протягивать (lacertum <i>Sen, O</i>; brachia <i>O</i>);\n<b>12)</b> потрясать, качать, махать (caesariem, pennas <i>O</i>)."],
  ["exdorso", "<big>exdorso</big>\n\n<b>exdorso</b> <i>Ap</i>\n= exdorsuo."],
  ["exdorsuo", "<big>exdorsuo</big>\n\n<b>exdorsuo, —, —, &#257;re</b> [ex + dorsum]\nудалять позвоночник (<i>у рыб</i>) (e. congrum, murenam <i>Pl</i>; pisces <i>Ap</i>)."],
  ["exduco", "<big>exduco</big>\n\n<b>exd&#363;co</b> <i>v. l.</i>\n= educo."],
  ["exduumvir", "<big>exduumvir</big>\n\n<b>ex–duumvir, vir&#299;</b> <i>m</i>\nбывший duumvir (<i>см.</i>) <i>Aug</i>."],
  ["exec-", "<big>exec-</big>\n\n<b>exec-</b> <i>v. l.</i>\n= exsec-."],
  ["exedint", "<big>exedint</big>\n\n<b>exedint</b> <i>арх.</i> <i>Pl</i> <i>3 л. pl.</i> <i>praes. conjct. к</i>\nexedo."],
  ["exedo", "<big>exedo</big>\n\n<b>ex–edo, &#275;d&#299;, &#275;sum (</b><i>арх.</i> <b>&#275;ssum), ere (ex&#275;sse)</b>\n<b>1)</b> поедать, съедать: tibi omne est exedendum <i>T, Aus</i> всё это тебе придётся расхлёбывать;\n<b>2)</b> объедать (aliquem <i>Pl, Ter</i>); уничтожать, истреблять (urbem <i>V</i>; monumenta <i>QC</i>); разъедать: exesa rubigine pila <i>V</i> разъеденные ржавчиной дротики || пожирать, уничтожать (frumentum <i>Vr</i>; fruges <i>Col</i>): flammeus ardor silvas exederat <i>Lcr</i> огненный жар пожирал леса || обгладывать (ossa exesa <i>PJ</i>); размывать (saxa exesa <i>Sen</i>); стирать, изглаживать (exesae partes versiculorum <i>C</i>);\n<b>3)</b> сокрушать, изводить, терзать, снедать (aegritudo exedit <i>или</i> exest animum <i>C</i>; animas cogitationibus <i>QC</i>)."],
  ["exedra", "<big>exedra</big>\n\n<b>exedra (exhedra), ae</b> <i>f</i> (<i>греч.</i>)\nэкседра:\n<b>1)</b> <i>полукруглое расширение портика, ротонда</i> <i>Vtr</i>;\n<b>2)</b> <i>у римлян</i> гостиная, зал <i>Vtr, C, Q</i>;\n<b>3)</b> (<i>в церквах</i>) хоры <i>Aug</i>."],
  ["exedrium", "<big>exedrium</big>\n\n<b>exedrium, &#299;</b> <i>n</i>\nнебольшая экседра <i>C</i>."],
  ["exeduratus", "<big>exeduratus</big>\n\n<b>ex–&#275;d&#363;r&#257;tus, a, um</b> [eduro]\nутративший жёсткость (exuviae <i>Tert</i>)."],
  ["exefficio", "<big>exefficio</big>\n\n<b>ex–efficio, —, —, ere</b>\nзаканчивать, завершать (opus <i>Pl</i>)."],
  ["exemplabilis", "<big>exemplabilis</big>\n\n<b>exempl&#257;bilis, e</b> [exemplo]\nмогущий служить при мером <i>Is</i>."],
  ["exemplar", "<big>exemplar</big>\n\n<b>exemplar, &#257;ris</b> <i>n</i> [exemplum]\n<b>1)</b> образец, пример (ad imitandum <i>C</i>; vitae morumque <i>H</i>);\n<b>2)</b> копия, список (litterarum <i>C</i>; libri <i>PJ</i>; testamenti <i>Pt</i>);\n<b>3)</b> подлинник, прообраз (libri de corruptis exemplaribus facti <i>AG</i>; aeternum e. clementiae <i>T</i>)."],
  ["exemplare", "<big>exemplare</big>\n\n<b>exempl&#257;re, is</b> <i>n</i> <i>арх. Lcr</i>\n= exemplar."],
  ["exemplares", "<big>exemplares</big>\n\n<b>exempl&#257;r&#275;s, ium</b> <i>m</i> (<i>sc.</i> libri)\nкопии, списки <i>T</i>."],
  ["exemplaris", "<big>exemplaris</big>\n\n<b>exempl&#257;ris, e</b> [exemplum]\n<b>1)</b> представляющий копию <i>T, Fronto</i>;\n<b>2)</b> служащий образцом, примерный (virtutes <i>Macr</i>)."],
  ["exemplarium", "<big>exemplarium</big>\n\n<b>exempl&#257;rium, &#299;</b> <i>n</i> <i>Dig, Eccl</i>\n= exemplar."],
  ["exemplatus", "<big>exemplatus</big>\n\n<b>exempl&#257;tus, a, um</b> [exemplum]\nявляющийся копией, переписанный (libellus <i>Sid</i>)."],
  ["exemplo", "<big>exemplo</big>\n\n<b>exemplo, &#257;v&#299;, —, &#257;re</b>\nприводить в качестве примера (aliquid <i>Aug</i>)."],
  ["exemplum", "<big>exemplum</big>\n\n<b>exemplum, &#299;</b> <i>n</i> [<i>одного корня с</i> eximo]\n<b>1)</b> проба, образчик (tritici <i>rhH</i>);\n<b>2)</b> образец (innocentiae, probitatis <i>C</i>); пример (sequi e. alicujus <i>C</i>): aliquam rem in e. intueri <i>Q</i> смотреть на что-л. как на образец; aliquid in e. assumere <i>Q</i> принять что-л. за образец; facere aliquid exemplo alicujus <i>C</i> сделать что-л. по чьему-л. примеру; e. capere de (petere ab <i>C, L</i>) aliquo <i>Ter</i> брать с кого-л. пример; exemplo carens <i>Lcn</i> беспримерный; edere e. crudelitatis <i>C</i> являть пример жестокости; esse exemplo <i>H</i> служить примером; exempli causa (gratia) <i>C, Nep etc.</i> <i>или</i> in e. <i>Su</i> например;\n<b>3)</b> прецедент (jura et exempla <i>C</i>); предостерегающий пример, предостережение (e. statuere in aliquem <i>или</i> in aliquo <i>Ter, C etc.</i>): novissima exempla <i>T</i> самые суровые кары; aliquem omnibus (<i>или</i> pessimis) exemplis (ex)cruciare <i>Pl</i> подвергнуть кого-л. самым жестоким наказаниям;\n<b>4)</b> примерное наказание (arcendis sceleribus e. esse <i>L</i>): exempla in eum fient <i>Ter</i> он будет примерно наказан;\n<b>5)</b> образ, способ: uno exemplo vitam vivere <i>Pl</i> жить на один лад;\n<b>6)</b> подобие (Homeri ingenium sine exemplo <i>VP</i>): quod ad e.? <i>Pl</i> в каком роде? (какой примерно?); ad hoc e. <i>Pl</i> вот так (приблизительно такой);\n<b>7)</b> план, набросок (litterarum e. componere <i>C</i>);\n<b>8)</b> копия, список (epistulae, litterarum <i>C</i>);\n<b>9)</b> содержание: litterae eodem exemplo <i>Cs</i> письмо того же содержания."],
  ["exemptilis", "<big>exemptilis</big>\n\n<b>ex&#275;mptilis, e</b> [eximo]\nвынимающийся (pertica <i>Col</i>; margarita <i>Dig</i>)."],
  ["exemptio", "<big>exemptio</big>\n\n<b>ex&#275;mptio, &#333;nis</b> <i>f</i> [eximo]\n<b>1)</b> удаление, вынимание (favorum <i>Vr, Col</i>);\n<b>2)</b> <i>юр.</i> недопущение явки в суд (justa causa exemptionis <i>Dig</i>)."],
  ["exemptor", "<big>exemptor</big>\n\n<b>ex&#275;mptor, &#333;ris</b> <i>m</i> [eximo]\nкаменолом <i>PM</i>."],
  ["exemptus 1", "<big>exemptus</big>\n\n<b>I ex&#275;mptus, a, um</b> <i>part. pf. к</i>\neximo."],
  ["exemptus 2", "<big>exemptus</big>\n\n<b>II ex&#275;mptus, &#363;s</b> <i>m</i>\nвынимание, удаление (cuneorum <i>Vtr</i>)."],
  ["exentero", "<big>exentero</big>\n\n<b>exentero (exintero), &#257;v&#299;, &#257;tum, &#257;re</b>\n<b>1)</b> (<i>греч.</i>) вынимать внутренности, потрошить (porcum <i>Pt</i>); <i>шутл.</i> выпотрошить, опорожнить (alicujus marsupium <i>Pl</i>);\n<b>2)</b> мучить, терзать (aliquem <i>LM</i>; exedor atque exenteror <i>Pl</i>)."],
  ["exeo", "<big>exeo</big>\n\n<b>ex–eo, &#299;v&#299; (i&#299;), &#299;tum, &#299;re</b>\n<b>1)</b> выходить, уезжать (ex urbe <i>Pl, Ter</i> <i>и</i> ab urbe <i>C</i>; domo <i>Cs</i>; ad pugnam <i>L</i>): e. in provinciam <i>Cs</i> удалиться в провинцию; de a ex navi <i>C, Nep</i>, <i>тж.</i> e. in terram <i>C</i> выйти (высадиться) на берег; e. in turba <i>Pt</i> затеряться в толпе, <i>но</i>: e. in turbam <i>Nep</i> стать достоянием гласности;\n<b>2)</b> вытекать, течь (sanguis e visceribus exit <i>C</i>); втекать, впадать (Nilus in mare exit <i>O</i>); разливаться (amnis exit <i>V</i>): exiit sors <i>C</i> жребий выпал; e. de (ex) vita <i>C</i> умереть; exiisse ex (de) potestate (<i>sc.</i> mentis) <i>C</i> помешаться, <i>но</i>: a, de <i>и</i> ex potestate alicujus e. <i>Dig</i> выйти из под чьей-л. власти; a memoria <i>Sen</i> <i>и</i> memoria e. <i>L</i> изгладиться из памяти; e. ex aere alieno <i>C</i> освободиться от долгов; servitio e. <i>V</i> освободиться от рабства; e. in aliquem <i>St</i> гневно обрушиться на кого-л.; e. in iram <i>Lcn</i> воспылать гневом;\n<b>3)</b> выступать (de finibus suis <i>Cs</i>): e. limen <i>Ter</i> переступить порог; e. modum <i>O</i> перейти меру;\n<b>4)</b> ускользать (vivus exiit <i>VP</i>): e. e patriciis <i>C</i> выйти из сословия патрициев;\n<b>5)</b> становиться известным, распространяться (fama exit <i>Nep</i>; libri exeunt <i>C</i>);\n<b>6)</b> вырастать (folia a radice exeunt <i>PM</i>); получаться (currente rota urceus exit <i>H</i>); происходить (potest ex casa vir magnus e. <i>Sen</i>);\n<b>7)</b> проходить, истекать, быть на исходе: dies indutiarum exiit <i>L</i> день перемирия истёк; exeunte anno <i>C</i> в конце года;\n<b>8)</b> делать отступление, отклоняться в речи (in aliquam rem <i>Q</i>);\n<b>9)</b> возноситься, подниматься, возвышаться (ad auras, ad caelum <i>V</i>; in altitudinem <i>PM</i>): a se e. <i>Pt</i> приходить в исступлённое состояние;\n<b>10)</b> простираться, длиться, продолжаться (supra mille annos <i>Sen</i>);\n<b>11)</b> приходиться (in hunc mensem <i>O</i>);\n<b>12)</b> уклоняться, избегать (tela corpore atque oculis vigilantibus <i>V</i>): vim viribus e. <i>погов. V</i> силе противопоставлять силу;\n<b>13)</b> заканчиваться (folia in angulos exeunt <i>PM</i>): in <i>a</i> exeuntia nomina <i>Q</i> слова, оканчивающиеся на <i>a.</i>"],
  ["exequ-", "<big>exequ-</big>\n\n<b>exequ-</b> <i>v. l.</i>\n= exsequ-."],
  ["exerceo", "<big>exerceo</big>\n\n<b>ex–erceo, cu&#299;, citum, &#275;re</b> [arceo]\n<b>1)</b> выводить из состояния покоя, не давать отдыха, постоянно занимать, заваливать работой (servos <i>Ter</i>): e. tauros <i>V</i> использовать быков (на полевых работах); quantum hominum unus venter exercet! <i>Sen</i> скольких людей затрудняет обжорство одного человека! (<i>о римск. богачах</i>); e. diem <i>V</i> проработать (весь) день || донимать, причинять хлопоты, беспокоить, тревожить, мучить (ambitio exercet animos hominum <i>Sl</i>); преследовать (aliquem odiis <i>V</i>): e. in aliquem noctes amaras <i>Prp</i> мучить кого-л. по ночам; aliquem astricta alvus exercet <i>CC</i> кто-л. страдает запорами; ego te exercebo, ut dignus es <i>Ter</i> ты у меня получишь по заслугам; exerceri de aliqua re <i>C</i> тревожиться о чём-л.;\n<b>2)</b> гонять, гнать (equos <i>V</i>); катить (aquas ad mare <i>O</i>); волновать, колебать (undas exercet Auster <i>H</i>): apes exercentur <i>V</i> пчёлы носятся; corpora assiduo exercita motu <i>Lcr</i> тела, находящиеся: в беспрерывном движении; flumina exercita cursu <i>V</i> безустанно текущие реки;\n<b>3)</b> шевелить: litibus e. linguas <i>O</i> шумно ссориться; e. verba <i>L</i> пустословить; e. vocem <i>Pl</i> возглашать; e. in aliquo vim suam <i>Nep</i> (opes <i>L</i>) оказывать на кого-л. сильное влияние; nomen patris patriae e. <i>PJ</i> носить звание отца отечества;\n<b>4)</b> разрабатывать, эксплуатировать (metalla <i>L</i>; fodinas <i>Vr</i>); возделывать, обрабатывать (colles vomere <i>V</i>; rura paterna bobus <i>H</i>): e. solum <i>V</i> взрыхлить почву; ferrum e. <i>V</i> усердно ковать железо; e. pistrinum <i>Su, Ap</i> держать пекарню; e. studiosius mare, quam terras <i>Just</i> заниматься больше мореходством, чем земледелием;\n<b>5)</b> упражнять (corpus, memoriam <i>C</i>; copias <i>Cs</i>): e. arma <i>V</i> обучаться военному делу, <i>но</i>: e. arma contra aliquem <i>T</i> обратить оружие против кого-л.; e. se, exerceri <i>иногда</i> e. упражняться (re aliqua <i>или</i> in re aliqua <i>C etc.</i> <i>и</i> ad aliquid <i>C</i>) || развивать, совершенствовать (juventutem <i>C</i>; ingenium <i>Q</i>);\n<b>6)</b> заниматься (чём-л.), предаваться (чему-л.) (e. artem <i>H, T</i>; jus civile, medicinam <i>C</i>): e. commercium alicujus rei <i>PM</i> вести торговлю чём-л.; e. quaestionem <i>Sl, L</i> вести следствие; e. vectigalia <i>C</i> собирать подати; e. crudelitatem in aliquo <i>C</i> поступать жестоко с кем-л.; e. avaritiam <i>L</i> обнаруживать жадность; e. victoriam <i>Sl</i> использовать победу; e. justitiam <i>PJ</i> соблюдать (осуществлять) справедливость; e. legem <i>L, T, Su</i> исполнять закон; e. judicium <i>C</i> председательствовать на суде; e. pactos hymenaeus <i>V</i> заключать брачные договоры; e. choros <i>V</i> водить хороводы; e. cantus <i>V</i> петь песню; e. balatum <i>V</i> блеять; e. telas <i>O</i> изготовлять ткани, ткать; e. arbusta <i>PJ</i> выращивать деревца; e. feras <i>Dig</i> охотиться на зверей; e. pacem <i>V</i> жить в мире; e. gratiam <i>Sl</i> быть милостивым; e. inimicitias cum aliquo <i>C</i> жить во вражде с кем-л.; e. vitam venaliciariam <i>Dig</i> заниматься работорговлей, <i>но</i>: curis e. vitam alicujus <i>Lcr</i> отягощать чью-л. жизнь заботами;\n<b>7)</b> <i>редко</i> испытывать: e. sitim <i>Sil</i> страдать от жажды."],
  ["exercibilis", "<big>exercibilis</big>\n\n<b>exercibilis, e</b> [exerceo]\nвыполнимый, осуществимый (regula <i>CA</i>)."],
  ["exercitamentum", "<big>exercitamentum</big>\n\n<b>exercit&#257;mentum, &#299;</b> <i>n</i> [exerceo]\nупражнение (exercitamenta corporis <i>Ap, CA</i>)."],
  ["exercitate", "<big>exercitate</big>\n\n<b>exercit&#257;t&#275;</b> [exercitatus]\nопытной рукой, со знанием дела, умело <i>Sen</i>."],
  ["exercitatio", "<big>exercitatio</big>\n\n<b>exercit&#257;tio, &#333;nis</b> <i>f</i> [exercito]\n<b>1)</b> упражнение (alicujus rei <i>и</i> in aliqua re <i>C, Cs etc.</i>);\n<b>2)</b> приведение в действие, осуществление, исполнение: e. virtutis <i>C, Just</i> следование правилам добродетели;\n<b>3)</b> практика, опытность, опыт (dicendi <i>C</i>; juris civilis <i>C</i>)."],
  ["exercitativus", "<big>exercitativus</big>\n\n<b>exercit&#257;t&#299;vus, a, um</b>\nотносящийся к упражнению (facultas <i>Bo&euml;t</i>)."],
  ["exercitator", "<big>exercitator</big>\n\n<b>exercit&#257;tor, &#333;ris</b> <i>m</i> [exercito]\nучитель, тренер (alicujus rei <i>PM</i>)."],
  ["exercitatrix", "<big>exercitatrix</big>\n\n<b>exercit&#257;tr&#299;x, &#299;cis</b> <i>f</i> [exercito]\nгимнастика <i>Q</i>."],
  ["exercitatus", "<big>exercitatus</big>\n\n<b>exercit&#257;tus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> exercito;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> потрясаемый, волнуемый (Syrtes exercitatae Noto <i>H</i>): non alias exercitatior Britannia fuit <i>T</i> Британия никогда не находилась в состоянии большего брожения;\n<b>2)</b> опытный, умелый, искусный (aliqua re <i>C, Cs</i> <i>и</i> in aliqua re <i>Cs etc.</i>);\n<b>3)</b> сильно занятый, целиком поглощённый (agris subigendis <i>C</i>); озабоченный (animus <i>C</i>; vultus <i>Pt</i>)."],
  ["exercite", "<big>exercite</big>\n\n<b>exercit&#275;</b> [exercitus II]\nс трудом (exercitius cogitationes cogitare <i>Ap</i>)."],
  ["exercitio", "<big>exercitio</big>\n\n<b>exercitio, &#333;nis</b> <i>f</i> [exerceo]\nзанятие, дело (inertia plus detrimenti facit, quam e. <i>Cato ap. AG</i>): e. navis <i>Dig</i> торговое мореплавание, профессия фрахтовщика."],
  ["exercitium", "<big>exercitium</big>\n\n<b>exercitium, &#299;</b> <i>n</i> [exerceo]\nупражнение (omnium militum <i>Vulc</i>; equitum <i>T</i>)."],
  ["exercito", "<big>exercito</big>\n\n<b>exercito, &#257;v&#299;, &#257;tum, &#257;re</b> [<i>intens. к</i> exerceo]\nчасто (много) упражнять, занимать (corpus atque ingenium <i>Sl</i>); обучать (liberos <i>Sen</i>)."],
  ["exercitor", "<big>exercitor</big>\n\n<b>exercitor, &#333;ris</b> <i>m</i>\n<b>1)</b> <i>Pl</i> = exercitator;\n<b>2)</b> предприниматель: e. cauponae <i>G</i> трактирщик; e. navis <i>Dig</i> судовладелец-фрахтовщик."],
  ["exercitorius", "<big>exercitorius</big>\n\n<b>exercit&#333;rius, a, um</b>\n<b>1)</b> служащий для упражнения, учебный (jacula <i>Tert</i>);\n<b>2)</b> касающийся судовладельца (actio <i>Dig</i>)."],
  ["exercitualis", "<big>exercitualis</big>\n\n<b>exercitu&#257;lis, e</b> [exercitus]\nвоенный (vir <i>Eccl</i>)."],
  ["exercitum", "<big>exercitum</big>\n\n<b>exercitum, &#299;</b> <i>n</i> <i>Acc, Naev, Vr, Eccl</i>\n= exercitus I."],
  ["exercitus 1", "<big>exercitus</big>\n\n<b>I exercitus, &#363;s</b> <i>m</i> [exerceo]\n<b>1)</b> упражнение (e. gymnasticus <i>Pl</i>);\n<b>2)</b> обученное войско, армия, вооружённые силы (e. pedester <i>Nep</i>; navalis <i>L</i>);\n<b>3)</b> пехота (e. equitatusque <i>Cs, L</i>);\n<b>4)</b> собрание граждан, народное собрание (<i>в центуриатных комициях</i>) <i>Vr, AG</i>;\n<b>5)</b> множество, стая (corvorum <i>V</i>);\n<b>6)</b> хлопоты, мучение, мука <i>Pl</i>."],
  ["exercitus 2", "<big>exercitus</big>\n\n<b>II exercitus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> exerceo;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> испытанный (adversis probitas exercita rebus <i>O</i>); испытавший невзгоды, закалённый в несчастьях (Iliacis fatis e. <i>V</i>); опытный, обученный (militia <i>T</i>); приученный, привыкший (ad omne flagitium <i>T</i>);\n<b>2)</b> полный невзгод, беспокойный, трудный (militia <i>T</i>);\n<b>3)</b> утомлённый, усталый (curis exercita corpora <i>O</i>);\n<b>4)</b> выделанный, обработанный (pelles <i>CA</i>)."],
  ["exero", "<big>exero</big>\n\n<b>exero</b> <i>v. l.</i>\n= exsero."],
  ["exerro", "<big>exerro</big>\n\n<b>ex–erro, —, —, &#257;re</b>\nсбиться с пути, заблудиться <i>St, Vlg</i>; отклоняться (a via veritatis <i>Eccl</i>)."],
  ["exert-", "<big>exert-</big>\n\n<b>exert-</b> <i>v. l.</i>\n= exsert-."],
  ["exesor", "<big>exesor</big>\n\n<b>ex&#275;sor, &#333;ris</b> <i>m</i> [exedo]\nразъедающий, размывающий: e. litora moerorum (= murorum) <i>Lcr</i> (морской прибой), размывающий стены берегов."],
  ["exest", "<big>exest</big>\n\n<b>ex&#275;st</b> <i>C</i>\n= exedit (<i>к</i> exedo)."],
  ["exesus", "<big>exesus</big>\n\n<b>ex&#275;sus, a, um</b> <i>part. pf. к</i>\nexedo."],
  ["exfafillatus", "<big>exfafillatus</big>\n\n<b>ex–fafill&#257;tus, a, um</b> [<i>предпол.</i> fafilla = papilla]\nобнажённый до самой груди (bracchium <i>Pl</i>)."],
  ["exfodio", "<big>exfodio</big>\n\n<b>exfodio</b> <i>v. l.</i>\n= effodio."],
  ["exfornicatus", "<big>exfornicatus</big>\n\n<b>ex–fornic&#257;tus, a, um</b>\nразвращённый <i>Vlg</i>."],
  ["exfr-", "<big>exfr-</big>\n\n<b>exfr-</b> <i>v. l.</i>\n= effr-."],
  ["exhaer-", "<big>exhaer-</big>\n\n<b>exhaer-</b> <i>v. l.</i>\n= exher-."],
  ["exhaeresimus", "<big>exhaeresimus</big>\n\n<b>exhaeresimus, a, um</b> (<i>греч.</i>)\nмогущий быть удалённым, <i>т. е.</i> вставной: e. dies <i>C</i> високосный день."],
  ["exhalatio", "<big>exhalatio</big>\n\n<b>exh&#257;l&#257;tio, &#333;nis</b> <i>f</i> [exhalo]\nвыдыхание, испарение (exhalationes terrae <i>C</i>)."],
  ["exhalo", "<big>exhalo</big>\n\n<b>ex–h&#257;lo, &#257;v&#299;, &#257;tum, &#257;re</b>\n<b>1)</b> выдыхать, испарять (vaporem <i>Lcr</i>; nebulam <i>V</i>); испускать, струить (odorem <i>Lcr</i>); извергать (Aetna exhalat flammam <i>O</i>): e. animam <i>O, Sen</i> (vitam <i>V</i>) испустить дух, умереть; e. crapulam (vinum) <i>C</i> проспать хмель, протрезвиться;\n<b>2)</b> выдыхаться, испаряться (vestes exhalant <i>Sl</i>);\n<b>3)</b> умирать <i>O</i>."],
  ["exhareno", "<big>exhareno</big>\n\n<b>exhar&#275;no</b> <i>v. l.</i>\n= exareno."],
  ["exhaurio", "<big>exhaurio</big>\n\n<b>ex–haurio, haus&#299;, haustum, &#299;re</b>\n<b>1)</b> вычерпывать (aquam <i>Col</i>); вынимать, брать (pecuniam ex aerario <i>C</i>); рыть (humum manibus <i>Cs</i>); опорожнять (fossam <i>L</i>); уносить (praedam ex agris <i>C</i>): e. poenas <i>V</i> отомстить;\n<b>2)</b> отнимать, лишать (alicui amorem, dolorem, partem ex laudibus <i>C</i>): e. sibi spiritum (vitam) <i>C</i> лишить себя жизни;\n<b>3)</b> выпивать (vinum <i>C</i>); осушать (poculum <i>C</i>);\n<b>4)</b> исчерпывать, истощать (aerarium <i>C</i>; vires <i>Nep</i>; facultates <i>Nep</i>; amicorum benevolentiam <i>C</i>); разорять, делать нищим (aliquem <i>C</i>); разграблять (agros, tecta <i>Nep</i>);\n<b>5)</b> выносить, претерпевать, преодолевать (labores exhausti <i>L</i>; periculum <i>V</i>): bella exhausta <i>V</i> пережитые битвы;\n<b>6)</b> проводить (noctem <i>T</i>);\n<b>7)</b> выплачивать (vim aeris alieni <i>L</i>);\n<b>8)</b> приводить в исполнение, выполнять .(mandata <i>C</i>)."],
  ["exhaustio", "<big>exhaustio</big>\n\n<b>exhaustio, &#333;nis</b> <i>f</i> [exhaurio]\nвычерпывание <i>Aug</i>."],
  ["exhaustus", "<big>exhaustus</big>\n\n<b>exhaustus, a, um</b> <i>part. pf. к</i>\nexhaurio."],
  ["exhedra", "<big>exhedra</big>\n\n<b>exhedra, ae</b> <i>f</i> <i>v. l.</i>\n= exedra."],
  ["exherbo", "<big>exherbo</big>\n\n<b>ex–herbo, —, —, &#257;re</b>\nочищать от травы (locum <i>Col</i>)."],
  ["exheredatio", "<big>exheredatio</big>\n\n<b>exh&#275;r&#275;d&#257;tio, &#333;nis</b> <i>f</i> [exheredo]\nлишение наследства <i>Q, Dig</i>."],
  ["exheredito", "<big>exheredito</big>\n\n<b>exh&#275;r&#275;dito, —, —, &#257;re</b> [<i>frequ. к</i> exheredo]\nлишать наследства <i>Bo&euml;t, Eccl</i>."],
  ["exheredo", "<big>exheredo</big>\n\n<b>exh&#275;r&#275;do, &#257;v&#299;, &#257;tum, &#257;re</b> [exheres]\n<b>1)</b> лишать наследства (aliquem <i>C etc.</i>; testamentis exheredari <i>Pt</i>);\n<b>2)</b> лишать (aliquem aliqua re <i>AG</i>)."],
  ["exheres", "<big>exheres</big>\n\n<b>ex–h&#275;r&#275;s, &#275;dis</b> (<i>тж.</i> e. paternorum bonorum <i>C</i>)\nлишённый наследства <i>Pl, Q</i>; <i>перен.</i> лишённый: e. vitae <i>Pl</i> мертвец."],
  ["exhibeo", "<big>exhibeo</big>\n\n<b>ex–hibeo, bu&#299;, bitum, &#275;re</b> [habeo]\n<b>1)</b> выдавать, передавать (servos <i>C</i>; alicui omnia integra <i>C</i>); выставлять (testem <i>QC</i>);\n<b>2)</b> издавать (sonos ore gemente <i>O</i>);\n<b>3)</b> задавать, устраивать (cenam, convivium <i>Lampr</i>): rem salvam exhibebo <i>Pl</i> я устрою это как следует; vias tutas e. <i>O</i> сделать дороги безопасными;\n<b>4)</b> показывать, предъявлять (librarium legum <i>C</i>; testium nomina <i>Dig</i>); представлять, приводить (debitorem alicujus in judicium <i>Dig</i>); являть, оказывать, выказывать, обнаруживать (humanitatem, liberalitatem et justitiam <i>PJ</i>); показывать (se adorandum alicui <i>Su</i>): Pallada e. <i>O</i> предстать как Паллада; e. faciem parentis <i>PM</i> лицом быть похожим на отца; e. linguam paternam <i>O</i> подражать речи отца (говорить как отец);\n<b>5)</b> излагать, представлять (alicui philosophiam <i>C</i>);\n<b>6)</b> осуществлять, практиковать (imperium <i>Pl</i>): e. fidem <i>O</i> осуществляться, исполняться, <i>но</i>: e. fidem alicui <i>Col</i> быть верным кому-л.;\n<b>7)</b> доставлять (cibum, potum, tectum <i>Dig</i>);\n<b>8)</b> ставить на сцене, инсценировать (comoediam <i>Su</i>); выводить на арену (gladiatoria munera <i>Su</i>);\n<b>9)</b> причинять, доставлять (alicui molestiam <i>Pt, C</i>; curam <i>Tib</i>; negotia <i>Pl, C etc.</i>);\n<b>10)</b> поддерживать, содержать (aliquem alimentis <i>Just</i>; parentes <i>Dig</i>): misere vitam e. <i>Just</i> жить в бедности."],
  ["exhibitio", "<big>exhibitio</big>\n\n<b>exhibitio, &#333;nis</b> <i>f</i> [exhibeo]\n<b>1)</b> предъявление (tabularum testamenti <i>Dig</i>); вручение (chirographi <i>AG</i>);\n<b>2)</b> содержание (thermarum <i>CTh</i>): e. educationis <i>CJ</i> покрытие расходов по воспитанию;\n<b>3)</b> прокормление (frumentum repositum ad mancipiorum exhibitionem <i>Dig</i>)."],
  ["exhibitorius", "<big>exhibitorius</big>\n\n<b>exhibit&#333;rius, a, um</b>\nкасающийся предъявления <i>или</i> выдачи (interdictum <i>G</i>)."],
  ["exhilaratio", "<big>exhilaratio</big>\n\n<b>exhilar&#257;tio, &#333;nis</b> <i>f</i> [exhilaro]\nпридание весёлости (e. voluntatis <i>Aug</i>)."],
  ["exhilaro", "<big>exhilaro</big>\n\n<b>ex–hilaro, &#257;v&#299;, &#257;tum, &#257;re</b>\n<b>1)</b> веселить, развеселять (aliquem <i>M</i>);\n<b>2)</b> придавать весёлый вид, оживлять (colores sulphure <i>PM</i>)."],
  ["exhinc", "<big>exhinc</big>\n\n<b>exhinc</b> <i>adv.</i> (= exinde)\nпосле этого, с этих пор <i>Ap</i>."],
  ["exhodium", "<big>exhodium</big>\n\n<b>exhodium, &#299;</b> <i>n</i> <i>v. l.</i>\n= exodium."],
  ["exhonoratio", "<big>exhonoratio</big>\n\n<b>exhon&#333;r&#257;tio, &#333;nis</b> <i>f</i>\nлишение чести <i>Aug</i>."],
  ["exhonoro", "<big>exhonoro</big>\n\n<b>ex–hon&#333;ro, —, —, &#257;re</b>\nлишать чести, обесчещивать (aliquem <i>Aug</i>)."],
  ["exhorreo", "<big>exhorreo</big>\n\n<b>ex–horreo, —, —, &#275;re</b>\nсодрогаться, ужасаться, страшиться (aliquid <i>Col, Aug</i>)."],
  ["exhorresco", "<big>exhorresco</big>\n\n<b>ex–horr&#275;sco, horru&#299;, —, ere</b>\nужасаться, содрогаться, (за)трепетать (metu <i>C</i>): e. in aliquo <i>C</i> затрепетать перед кем-л.; e. aliquid <i>V, Su, VM</i> страшиться чего-л."],
  ["exhortatio", "<big>exhortatio</big>\n\n<b>exhort&#257;tio, &#333;nis</b> <i>f</i> [exhortor]\nободрение, поощрение, побуждение <i>T, Q, Sen etc.</i>"],
  ["exhortativus", "<big>exhortativus</big>\n\n<b>exhort&#257;t&#299;vus, a, um</b> [exhortor]\nободряющий, поощрительный <i>Q, Macr etc.</i>"],
  ["exhortatorius", "<big>exhortatorius</big>\n\n<b>exhort&#257;t&#333;rius, a, um</b>\nободряющий (litterae <i>Aug</i>; epistula <i>Hier</i>)."],
  ["exhortor", "<big>exhortor</big>\n\n<b>ex–hortor, &#257;tus sum, &#257;r&#299;</b> <i>depon.</i>\nободрять, поощрять, побуждать (aliquem in arma <i>V</i>; in spem <i>Q</i> <i>и</i> ad spem <i>VM</i>; in hostem <i>O</i>; ad ultionem <i>PM</i>)."],
  ["exhumoro", "<big>exhumoro</big>\n\n<b>ex–h&#363;m&#333;ro, —, —, &#257;re</b>\nосвобождать от жидкостей (corpus <i>CA</i>)."],
  ["exhydriae", "<big>exhydriae</big>\n\n<b>exhydriae, &#257;rum</b> <i>m</i> (<i>греч.</i>)\nветры с дождём <i>Ap</i>."],
  ["exi", "<big>exi</big>\n\n<b>ex&#299;</b> <i>imper. к</i>\nexeo."],
  ["exibeo", "<big>exibeo</big>\n\n<b>exibeo</b> <i>Pl</i>\n= exhibeo."],
  ["exibilo", "<big>exibilo</big>\n\n<b>exibilo</b> <i>v. l.</i>\n= exsibilo."],
  ["exicc-", "<big>exicc-</big>\n\n<b>exicc-</b> <i>v. l.</i>\n= exsicc-."],
  ["exico", "<big>exico</big>\n\n<b>exico</b> <i>v. l.</i>\n= exseco."],
  ["exies", "<big>exies</big>\n\n<b>exi&#275;s, exiet</b> <i>etc.</i> <i>Vlg, Tert</i>\n= exibis, exibit (<i>от</i> exeo)."],
  ["exigentia", "<big>exigentia</big>\n\n<b>exigentia, ae</b> <i>f</i>\nтребование <i>Vlg, Eccl</i>."],
  ["exignesco", "<big>exignesco</big>\n\n<b>ex–ign&#275;sco, —, —, ere</b>\nвоспламеняться <i>Cens</i>."],
  ["exigo", "<big>exigo</big>\n\n<b>ex–igo, &#275;g&#299;, &#257;ctum, ere</b> [ago]\n<b>1)</b> изгонять (aliquem domo <i>L</i>; reges ex civitate <i>L</i>); вытеснять (hostem e campo <i>L</i>): e. uxorem (matrimonio) <i>Pl, Ter, Su</i> развестись с женой; aliquem vita e. <i>Sen</i> лишать кого-л. жизни; e. corpus e stratis <i>Sil</i> встать со (своего) ложа;\n<b>2)</b> катить, изливать (qua exigit Hebrus aquas <i>O</i>);\n<b>3)</b> пускать (radices <i>CC</i>);\n<b>4)</b> устранять, отгонять (lassitudinem ex corpore <i>Pl</i>);\n<b>5)</b> втыкать, вонзать (ferrum per viscera <i>Lcn</i>; ensem per juvenem <i>V</i>); пронзать (aliquem hasta <i>VF</i>);\n<b>6)</b> бросать, метать (tela in aliquem <i>Sen</i>);\n<b>7)</b> размахнуться, занести (exactus ensis <i>O</i>);\n<b>8)</b> вывозить на продажу (agrorum fructus <i>L</i>; merces <i>Col</i>);\n<b>9)</b> отвергать: e. fabulam <i>Ter</i> освистать (ошикать) пьесу;\n<b>10)</b> требовать (jus jurandum <i>L</i>; veritatem a teste <i>C</i>; obsides ab aliquo <i>Cs</i>): sarta tecta e. <i>C, L</i> требовать исправного выполнения строительных работ, <i>т. е.</i> принять заказ в должном состоянии; ut res exiget <i>Q</i> в зависимости от (будущих) обстоятельств;\n<b>11)</b> спрашивать: exactum a Labeone, cur ultionem legis omisisset <i>T</i> Лабеона спросили, почему он не поступил по строгости закона; e. facta ab aliquo <i>O</i> просить кого-л. рассказать о происшедшем; e. opus <i>O, Col</i> смотреть за (руководить) работой (<i>ср.</i> 13);\n<b>12)</b> взыскивать, взимать, собирать (pecunias, vectigalia <i>C</i>; portorium <i>AG</i>): dum aes exigitur <i>H</i> пока происходят денежные расчёты; e. poenas ab (de, ex) aliquo <i>и</i> alicui <i>O, L etc.</i> наказать кого-л. (<i>первонач.</i> взыскивать штраф);\n<b>13)</b> совершать, завершать, оканчивать (opus <i>O</i> — <i>ср.</i> 11; monumentum <i>H</i>): quattuor spatiis Juppiter exegit annum <i>O</i> Юпитер четырьмя временами (года) закончил год (<i>т. е.</i> разделил год на 4 времени); ante exactam hiemem <i>Cs</i> до окончания зимы; osculis exactis <i>Pt</i> после взаимных поцелуев;\n<b>14)</b> переносить, (вы)терпеть (aerumnam <i>Pl</i>);\n<b>15)</b> проводить, (про)жить (aetatem in armis <i>Sen</i>; vitam miserabiliter <i>VM</i>): exacta aetate <i>C</i> в преклонном возрасте (в глубокой старости); tribus aestatibus exactis <i>V</i> по прошествии трёх лет;\n<b>16)</b> проезжать, проходить (equis Athon <i>VF</i>); проплывать (mare <i>PM</i>);\n<b>17)</b> исследовать, взвешивать, разбирать (veram originem juris <i>Sen</i>);\n<b>18)</b> приспособлять (e. aliquid ad nostras leges <i>L</i>): e. rem ad aliquid <i>QC</i> сообразоваться с чём-л. (приспособлять что-л. к чему-л.) || соразмерять (opus ad vires suas <i>O</i>);\n<b>19)</b> оценивать, измерять (pondus alicujus rei manu <i>Su</i>); обдумывать: tempus secum modumque e. <i>V</i> мысленно определять время и образ действий; illa (vitia) non nisi aure exigentur <i>Q</i> эти недостатки (стиха) могут определяться лишь на слух; non satis exactum est, quid agam <i>C</i> ещё не вполне решено, как мне поступить; e. cum aliquo de aliqua re <i>C, PJ</i> переговорить (посоветоваться) с кем-л. о чём-л."],
  ["exigue", "<big>exigue</big>\n\n<b>exigu&#275;</b> [exiguus]\n<b>1)</b> мало, недостаточно, скудно (e. atque frigide laudari <i>AG</i>); едва: frumentum e. dierum triginta habere <i>Cs</i> иметь едва 30-дневный запас хлеба;\n<b>2)</b> кратко, сжато (epistulam scribere <i>C</i>)."],
  ["exiguitas", "<big>exiguitas</big>\n\n<b>exiguit&#257;s, &#257;tis</b> <i>f</i> [exiguus]\n<b>1)</b> малые размеры, незначительность (terrae <i>Just</i>; castrorum <i>Cs</i>); недостаточность, скудость, нехватка, недостаток (cibi <i>Col</i>; copiarum <i>Cs, C</i>);\n<b>2)</b> краткость (temporis <i>Cs</i>)."],
  ["exiguo", "<big>exiguo</big>\n\n<b>exigu&#333;</b> <i>adv.</i>\nнемного, чуть-чуть: e. post <i>PM</i> немного спустя; e. tangere <i>Scr</i> слегка прикоснуться."],
  ["exiguum", "<big>exiguum</big>\n\n<b>exiguum, &#299;</b> <i>n</i> [exiguus]\nчуть-чуть, капелька (tritici <i>QC</i>; aquae <i>O</i>): e. campi <i>L</i> клочок поля; e. salutis <i>Sil</i> ничтожная надежда на спасение."],
  ["exiguus", "<big>exiguus</big>\n\n<b>exiguus, a, um</b> [exigo]\n<b>1)</b> незначительный, небольшой, недостаточный, маленький, крошечный (spatium <i>L</i>; numerus <i>C</i>; copiae <i>C, Nep</i>); exiguo mero pingere aliquid <i>O</i> с помощью нескольких капель вина начертить что-л. (на столе);\n<b>2)</b> короткий, краткий (tempus, vita <i>C</i>);\n<b>3)</b> худой, худощавый, тощий (corpus <i>H</i>);\n<b>4)</b> бедный, жалкий, скудный (dapes <i>O</i>; toga <i>H</i>);\n<b>5)</b> слабый (vires <i>V</i>; sonus <i>O</i>)."],
  ["exilio", "<big>exilio</big>\n\n<b>exilio</b> <i>v. l.</i>\n= exsilio."],
  ["exilis", "<big>exilis</big>\n\n<b>ex&#299;lis, e</b> [<i>одного корня с</i> exiguus]\n<b>1)</b> тонкий (ramus <i>Su</i>); худой, тощий (membra <i>O</i>; corpus <i>Su</i>);\n<b>2)</b> недостаточный (aqua <i>Amm</i>); малочисленный (legio <i>C</i>); короткий (via <i>O</i>);\n<b>3)</b> скудный, бедный (domus <i>H</i>; solum <i>C</i>; res <i>Nep</i>);\n<b>4)</b> бледный, вялый, сухой (oratio, genus dicendi <i>C</i>); тусклый (sidera <i>PJ</i>);\n<b>5)</b> высокий, тонкий (vox <i>Q</i>)."],
  ["exilitas", "<big>exilitas</big>\n\n<b>ex&#299;lit&#257;s, &#257;tis</b> <i>f</i> [exilis]\n<b>1)</b> тонкость, незначительность (aculei apum <i>PM</i>); слабость, худосочие <i>PM</i>;\n<b>2)</b> скудость, бесплодие (soli <i>Col</i>);\n<b>3)</b> тонкость (vocis femineae <i>Q</i>);\n<b>4)</b> бесцветность, сухость (litterarum <i>Q</i>; in dicendo <i>C</i>)."],
  ["exiliter", "<big>exiliter</big>\n\n<b>ex&#299;liter</b> [exilis]\n<b>1)</b> слабо (disputare <i>C</i>); скудно, мало (exilius de aliqua re dicere <i>C</i>);\n<b>2)</b> сухо, вяло (annales e. scripti <i>C</i>)."],
  ["exilium", "<big>exilium</big>\n\n<b>exilium, &#299;</b> <i>n</i> <i>v. l.</i>\n= exsilium."],
  ["exim", "<big>exim</big>\n\n<b>exim</b> <i>adv.</i> <i>Lcr, L</i>\n= exinde."],
  ["eximie", "<big>eximie</big>\n\n<b>eximi&#275;</b> [eximius]\n<b>1)</b> отменно, отлично, замечательно, исключительно (cenare <i>J</i>);\n<b>2)</b> щедро (polliceri <i>L</i> — <i>v. l. к</i> enixe);\n<b>3)</b> весьма (utilis <i>PM</i>): e. optimus <i>Col</i> отличнейший."],
  ["eximietas", "<big>eximietas</big>\n\n<b>eximiet&#257;s, &#257;tis</b> <i>f</i>\nпревосходные качества, совершенство <i>Symm, Aug</i>."],
  ["eximius", "<big>eximius</big>\n\n<b>eximius, a, um</b> [eximo]\n<b>1)</b> составляющий исключение, исключительный (tu unus e. es <i>L</i>): neminem eximium habere <i>Ter</i> не делать исключений ни для кого;\n<b>2)</b> отменный, отличный, превосходный, особенный (homo, ingenium <i>C</i>; virtutes <i>L</i>);\n<b>3)</b> чрезвычайный, сильнейший (voluptas <i>Ap</i>; ignes Aetnae <i>Lcr</i>)."],
  ["eximo", "<big>eximo</big>\n\n<b>ex–imo, &#275;m&#299;, &#275;mptum, ere</b> [emo]\n<b>1)</b> вынимать (telum <i>Q</i>; clavem <i>Pt</i>); удалять (dentem alicui <i>PM</i>); извлекать, вытаскивать (ulmos cum radicibus <i>Cato</i>); изымать, исключать (aliquem numero <i>или</i> de numero aliquorum <i>H, Nep</i>): e. aliquem de (ex) reis <i>C</i> исключить кого-л. из числа подсудимых; si ea quaestio eximeretur <i>T</i> если не говорить об этом вопросе (отвлечься от этого);\n<b>2)</b> отнимать, устранять, удалять (dubitationem <i>Q</i>; ruditatem <i>Ap</i> — <i>v. l.</i>); освобождать, избавлять, спасать (urbem obsidione <i>или</i> ex obsidione <i>L</i>; aliquem ex vinculis <i>C</i>, de nervo <i>L</i>; agrum de vectigalibus <i>C</i>; onus sollicitis animis <i>H</i>; aliquem morti <i>T</i>): e. alicui curas <i>H</i> избавить кого-л. от забот;\n<b>3)</b> снимать (digito anulum <i>Just</i>): e. aliquem ex culpa <i>C</i> снять с кого-л. вину; e. aliquem alicui rei <i>T, O etc.</i> лишить кого-л. чего-л. (освобождать кого-л. от чего-л.); exemptus fine <i>Lcr</i> бесконечный; eis non eximitur (quin) <i>T</i> их невозможно разубедить (в том, что);\n<b>4)</b> терять, употреблять, тратить (tempus <i>Su</i>): ea res diem exemit <i>L</i> это заняло (весь) день; diem concilio e. <i>L</i> отложить голосование собрания на другой день; e. male aetatem <i>Sen</i> плохо использовать (свою) жизнь; e. diem dicendo <i>C</i> проговорить весь день (затянуть речь на целый день)."],
  ["exin", "<big>exin</big>\n\n<b>exin</b> <i>Pl, C</i>\n= exinde."],
  ["exinanio", "<big>exinanio</big>\n\n<b>ex–in&#257;nio, &#299;v&#299; (i&#299;), &#299;tum, &#299;re</b> [inanis]\n<b>1)</b> опоражнивать, разгружать (navem <i>C</i>; vehicula <i>PM</i>);\n<b>2)</b> разорять, (раз)грабить, опустошать (civitatem, agros <i>C</i>); истощать (ubertatem <i>PM</i>; patrimonium <i>Dig</i>): regionem frumento e. <i>bAfr</i> выкачать весь хлеб из области;\n<b>3)</b> пожирать (lienem <i>PM</i>);\n<b>4)</b> выводить из организма (alvum, bilem <i>PM</i>)."],
  ["exinanitio", "<big>exinanitio</big>\n\n<b>exin&#257;n&#299;tio, &#333;nis</b> <i>f</i> [exinanio]\n<b>1)</b> опоражнивание (alvi <i>PM</i>);\n<b>2)</b> истощение (e. florescendi, <i>sc.</i> arborum <i>PM</i>)."],
  ["exinde", "<big>exinde</big>\n\n<b>ex–inde</b> (<i>Pl, C</i> exin) <i>adv.</i>\n<b>1)</b> <i>пространство</i>: отсюда (petere Commagenam, e. Cappadociam <i>T</i>);\n<b>2)</b> <i>перечисление</i>: потом, затем (auxiliares, post quos pedites sagittarii, e. aliae legiones <i>T</i>);\n<b>3)</b> <i>время</i>: после того, затем: ad Mundam e. castra Punica mota <i>L</i> после этого карфагеняне перенесли свой лагерь в Мунду; <i>поздн.</i> с тех пор: e. beate vixit <i>Ap</i> с тех пор он зажил счастливо;\n<b>4)</b> <i>происхождение</i>: оттуда: e. vulnera cepimus <i>Ap</i> это нанесло нам раны;\n<b>5)</b> соответственно, так (ut... e. <i>Pl</i>): e. ut pabuli facultas est <i>Vr</i> в зависимости от кормовых средств."],
  ["exintero", "<big>exintero</big>\n\n<b>exintero</b> <i>Pt v. l.</i>\n= exentero."],
  ["existentia", "<big>existentia</big>\n\n<b>existentia</b> <i>v. l.</i>\n= exsistentia."],
  ["existimabilis", "<big>existimabilis</big>\n\n<b>ex&#299;stim&#257;bilis, e</b> [existimo]\nпредположительный, вероятный <i>CA</i>."],
  ["existimantes", "<big>existimantes</big>\n\n<b>ex&#299;stimant&#275;s, ium</b> <i>m</i> [existimo]\nкритики, рецензенты <i>C</i>."],
  ["existimatio", "<big>existimatio</big>\n\n<b>ex&#299;stim&#257;tio, &#333;nis</b> <i>f</i> [existimo]\n<b>1)</b> суждение, мнение, . оценка: omnium existimatione <i>C</i> по общему мнению; judicia summae existimationis <i>C</i> тяжбы, имеющие чрезвычайно большое значение; vestra e. est <i>L</i> об этом (надлежит) судить вам; communis e. est <i>L</i> всякий пусть судит, как хочет;\n<b>2)</b> доброе имя, репутация, слава (bona, turpis <i>C</i>): e. morum ac studiorum <i>Ap</i> слава человека добродетельного и трудолюбивого; violare (offendere, laedere <i>C</i>, lacerare <i>Su</i>) existimationem alicujus <i>C</i> подрывать (пятнать) чью-л. репутацию;\n<b>3)</b> денежный кредит (debitorum <i>Cs</i>)."],
  ["existimator", "<big>existimator</big>\n\n<b>ex&#299;stim&#257;tor, &#333;ris</b> <i>m</i> [existimo]\nценитель, судья, критик <i>C, AG</i>."],
  ["existimo", "<big>existimo</big>\n\n<b>ex–&#299;stimo</b> (<i>арх.</i> <b>ex&#299;stumo</b>), <b>&#257;v&#299;, &#257;tum, &#257;re</b> [aestimo]\n<b>1)</b> судить, полагать, считать (ut Cicero existimat <i>Q</i>; in hostium numero existimari <i>C</i>): aliquem sapientem et appellare et e. <i>C</i> называть и (действительно) считать кого-л. мудрым; disciplina in Britannia reperta esse existimatur <i>Cs</i> думают, что учение (друидов) создалось в Британии; aliquid milia octingenta in longitudinem esse existimatur <i>Cs</i> протяжение чего-л. определяется в 800 (римских) миль; quod ego nullo modo existimo <i>C</i> чего я никак не думаю;\n<b>2)</b> судить, решать, высказываться (bene de aliquo <i>C etc.</i>);\n<b>3)</b> <i>редко</i> (= aestimare) оценивать, ценить: e. aliquid magni <i>Nep</i> высоко что-л. ценить; aliquid flocci non e. <i>Pl</i> не ставить что-л. ни во что."],
  ["existo", "<big>existo</big>\n\n<b>existo</b> <i>v. l.</i>\n= exsisto."],
  ["existumo", "<big>existumo</big>\n\n<b>ex&#299;stumo</b> <i>арх.</i>\n= existimo."],
  ["exitiabilis", "<big>exitiabilis</big>\n\n<b>exiti&#257;bilis, e</b> [exitium]\nгибельный, пагубный, губительный (morbus <i>T</i>; telum <i>O</i>; discordiae <i>Su</i>)."],
  ["exitiabiliter", "<big>exitiabiliter</big>\n\n<b>exiti&#257;biliter</b> <i>Aug</i>\n= exitialiter."],
  ["exitialis", "<big>exitialis</big>\n\n<b>exiti&#257;lis, e</b> [exitium]\nгибельный, пагубный, губительный (donum Minervae <i>V</i>); тлетворный (spiritus <i>VP</i>)."],
  ["exitialiter", "<big>exitialiter</big>\n\n<b>exiti&#257;liter</b>\nпагубным образом (circum amare <i>Aug</i>)."],
  ["exitio", "<big>exitio</big>\n\n<b>exitio, &#333;nis</b> <i>f</i> [exeo]\nвыход (ex utero <i>Pl</i>): neque exitium (<i>acc.</i>) e. est <i>Pl</i> из гибели нет выхода, <i>т. е.</i> от гибели не уйти."],
  ["exitiose", "<big>exitiose</big>\n\n<b>exiti&#333;s&#275;</b> <i>Aug</i>\n= exitiabiliter."],
  ["exitiosus", "<big>exitiosus</big>\n\n<b>exiti&#333;sus, a, um</b> <i>C, T etc.</i>\n= exitiabilis."],
  ["exitium", "<big>exitium</big>\n\n<b>exitium, &#299;</b> <i>n</i> [exeo]\n(<i>gen. pl.</i> um <i>Enn</i>)\n<b>1)</b> погибель, гибель, падение, катастрофа, разрушение (urbis <i>C</i>; orbis terrarum <i>C</i>; e. invenire <i>Ph</i>): exitio alicui esse <i>C, H</i> быть причиной чьей-л. гибели;\n<b>2)</b> конец, роковой исход (vitae <i>Sl</i>)."],
  ["exitus", "<big>exitus</big>\n\n<b>exitus, &#363;s</b> <i>m</i> [exeo]\n<b>1)</b> место выхода, выход (e domo <i>L</i>; ab urbe <i>L</i>): omni exitu intercludere <i>Cs</i> отрезать все пути к отступлению; e. classis <i>Lcr</i> отплытие флота;\n<b>2)</b> устье (Istri <i>VF</i>): e. cibi <i>анат. PM</i> = anus;\n<b>3)</b> повод (exitus dare ad aliquid <i>C</i>);\n<b>4)</b> цель: exitum non habere <i>C</i> быть бесцельным;\n<b>5)</b> <i>грам.</i> окончание (exitum habere in <i>a</i> <i>Vr</i>);\n<b>6)</b> развязка (fabulae <i>C</i>); заключение (orationis <i>C, Cs</i>); исход, конец, завершение (anni <i>L</i>; vitae <i>Nep, VP</i>): perducere aliquid ad exitum <i>C</i> довести что-л. до конца; bonum exitum facere <i>Pt</i> хорошо закончиться; quemcumque dederit exitum casus <i>SenT</i> каков бы ни был исход дела;\n<b>7)</b> результат, успех (e. rerum <i>C</i>): e. acta probat <i>погов. O</i> конечным успехом оправдывается дело;\n<b>8)</b> кончина, смерть <i>C, PM, PJ</i>."],
  ["exjuro", "<big>exjuro</big>\n\n<b>ex–j&#363;ro, &#257;v&#299;, —, &#257;re</b>\nклятвенно подтверждать <i>Pl</i>."],
  ["exlecebra", "<big>exlecebra</big>\n\n<b>exlecebra, ae</b> <i>f</i> <i>v. l.</i>\n= elecebra."],
  ["exlex", "<big>exlex</big>\n\n<b>ex–l&#275;x, l&#275;gis</b> <i>adj.</i>\n<b>1)</b> не подчиняющийся (не подчинённый) закону <i>Vr, LM, C</i>;\n<b>2)</b> разнузданный (potus et e. <i>H</i>)."],
  ["exlido", "<big>exlido</big>\n\n<b>exl&#299;do</b> <i>v. l.</i>\n= elido."],
  ["exloquor", "<big>exloquor</big>\n\n<b>exloquor</b> <i>v. l.</i>\n= eloquor."],
  ["exmoveo", "<big>exmoveo</big>\n\n<b>exmoveo</b> <i>v. l.</i>\n= emoveo."],
  ["exnunc", "<big>exnunc</big>\n\n<b>ex–nunc</b> <i>adv.</i>\nс этих пор, отныне <i>Amm</i>."],
  ["exobrutus", "<big>exobrutus</big>\n\n<b>ex–obrutus, a, um</b>\nвырытый, вытащенный (dolium <i>Ap</i>)."],
  ["exobsecro", "<big>exobsecro</big>\n\n<b>ex–obsecro, —, —, &#257;re</b>\nнастоятельно просить, умолять <i>Pl</i>."],
  ["exoculo", "<big>exoculo</big>\n\n<b>ex–oculo, &#257;v&#299;, &#257;tum, &#257;re</b> [oculus]\nлишать глаз (caput alicui <i>Pl</i>; caeca et exoculata Fortuna <i>Ap</i>)."],
  ["exodiarius", "<big>exodiarius</big>\n\n<b>exodi&#257;rius, &#299;</b> <i>m</i> [exodium]\nэксодиарий, актёр, выступающий в эксодии <i>Amm</i>."],
  ["exodium", "<big>exodium</big>\n\n<b>exodium, &#299;</b> <i>n</i> (<i>греч.</i>)\n<b>1)</b> исход, конец, заключение (aliquid ad e. ducere <i>Vr</i>);\n<b>2)</b> эксодий, заключительный дивертисмент (e. Atellanicum <i>Su</i> <i>и</i> e. Atellanae <i>J</i>)."],
  ["exodoratus", "<big>exodoratus</big>\n\n<b>ex–od&#333;r&#257;tus, a, um</b>\nлишённый запаха <i>Tert</i>."],
  ["exodus", "<big>exodus</big>\n\n<b>exodus, &#299;</b> <i>f</i> (<i>греч.</i>)\nисход (<i>вторая книга Пятикнижия</i>) <i>Vlg, Eccl</i>."],
  ["exolatus", "<big>exolatus</big>\n\n<b>exol&#257;tus</b> <i>Pl v. l.</i>\n= exsulatus (<i>к</i> exsulo)."],
  ["exolesco", "<big>exolesco</big>\n\n<b>ex–ol&#275;sco, &#275;v&#299;</b> (<i>арх.</i> <b>u&#299;</b>), <b>&#275;tum, ere</b> [<i>из</i> alesco]\n<b>1)</b> достигнуть зрелости (<i>см.</i> exoletus 2);\n<b>2)</b> чахнуть от перезревания (lactucae veteres exolescunt <i>Ap</i>);\n<b>3)</b> устаревать, терять силу, ветшать, выходить из употребления, исчезать, изглаживаться из памяти (nondum iis dolor exoleverat <i>T</i>): paene jam exolescentes litterae <i>Su</i> почти уже стёршиеся буквы; e. vetustate <i>L</i> ослабеть за давностью (<i>о ненависти</i>)."],
  ["exoletus", "<big>exoletus</big>\n\n<b>exol&#275;tus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> exolesco: вышедший из употребления, устарелый, забытый (mos <i>Su</i>; odium <i>L</i>; exoleta revocare <i>Su</i>);\n<b>2.</b> <i>adj.</i>\n<b>1)</b> взрослый, перезрелый (virgo <i>Pl</i>);\n<b>2)</b> развратный, распутный (scortum <i>Pl etc.</i>)."],
  ["exolo", "<big>exolo</big>\n\n<b>exolo</b> <i>арх. v. l.</i>\n= exsulo."],
  ["exolvo", "<big>exolvo</big>\n\n<b>exolvo</b> <i>v. l.</i>\n= exsolvo."],
  ["exoneratio", "<big>exoneratio</big>\n\n<b>exoner&#257;tio, &#333;nis</b> <i>f</i> [exonero]\nосвобождение, льгота (e. mercedis <i>Dig</i>)."],
  ["exonero", "<big>exonero</big>\n\n<b>ex–onero, &#257;v&#299;, &#257;tum, &#257;re</b>\nвыгружать, разгружать (naves <i>Pl</i>); освобождать, избавлять (aliquem metu <i>L</i>): e. aes alienum <i>Dig</i> уплатить свои долги || опорожнять (stomachum <i>Su</i>; exonerata vesica <i>Pt</i>): se e. изливаться, впадать (amnes in Padum sese exonerantes <i>PM</i>); e. colos <i>O</i> разгрузить прялку, <i>т. е.</i> спрясть всё; e. conscientiam (<i>или</i> fidem) suam <i>QC, L</i> облегчить свою совесть || выселять, удалять (proximas in terras exonerari <i>T</i>): e. aliquid in aures alicujus <i>Sen</i> высказать что-л. кому-л.; e. aliquem <i>Pl</i> удалить (избавиться от) кого-л."],
  ["exopinisso", "<big>exopinisso</big>\n\n<b>ex–op&#299;nisso, —, —, ere</b>\nдумать, полагать: viderint alii, quid de hoc exopinissent <i>Pt</i> другие пусть думают об этом, как им угодно."],
  ["exoptabilis", "<big>exoptabilis</big>\n\n<b>exopt&#257;bilis, e</b> <i>Pl, Sil</i>\n= exoptatus."],
  ["exoptatus", "<big>exoptatus</big>\n\n<b>exopt&#257;tus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> exopto;\n<b>2.</b> <i>adj.</i> желанный, приятный <i>Pl, Ter, C</i>."],
  ["exopto", "<big>exopto</big>\n\n<b>ex–opto, &#257;v&#299;, &#257;tum, &#257;re</b>\nсильно желать, жаждать, томиться желанием (e. aliquem <i>Pl</i> <i>и</i> aliquid <i>Ter, C etc.</i>; e. videre aliquem <i>C</i>; e. alicui aliquid <i>C</i>)."],
  ["exorabilis", "<big>exorabilis</big>\n\n<b>ex&#333;r&#257;bilis, e</b> [exoro]\n<b>1)</b> доступный просьбам, снисходительный (pater <i>Pl</i>): non e. auro Orcus <i>H</i> Орк, от которого (никаким) золотом не откупиться;\n<b>2)</b> преодолимый, устранимый (vitium <i>Sen</i>);\n<b>3)</b> умилостивительный (carmen <i>VF</i>)."],
  ["exorabulum", "<big>exorabulum</big>\n\n<b>ex&#333;r&#257;bulum, &#299;</b> <i>n</i> [exoro]\nупрашивание, довод, просительная уловка <i>Pl</i>: e. judicantium <i>Ap</i> уловки, рассчитанные на то, чтобы растрогать судей."],
  ["exoratio", "<big>exoratio</big>\n\n<b>ex&#333;r&#257;tio, &#333;nis</b> <i>f</i> [exoro]\nвымаливание <i>Vlg</i>; замаливание (peccatorum <i>Aug</i>)."],
  ["exorator", "<big>exorator</big>\n\n<b>ex&#333;r&#257;tor, &#333;ris</b> <i>m</i> [exoro]\nблагосклонно выслушанный проситель <i>Ter, Tert</i>."],
  ["exoratorium", "<big>exoratorium</big>\n\n<b>ex&#333;r&#257;t&#333;rium, &#299;</b> <i>n</i>\nумилостивительное средство <i>Aug</i>."],
  ["exorbeo", "<big>exorbeo</big>\n\n<b>exorbeo</b> <i>v. l.</i>\n= exsorbeo."],
  ["exorbitatio", "<big>exorbitatio</big>\n\n<b>exorbit&#257;tio, &#333;nis</b> <i>f</i>\nуклон, отклонение (disciplinae <i>Tert</i>)."],
  ["exorbitator", "<big>exorbitator</big>\n\n<b>exorbit&#257;tor, &#333;ris</b> <i>m</i>\nнарушитель (e. et destructor alicujus rei <i>Tert</i>)."],
  ["exorbito", "<big>exorbito</big>\n\n<b>ex–orbito, &#257;v&#299;, &#257;tum, &#257;re</b> [orbita]\n<b>1)</b> отклоняться в сторону (exorbitantes boves, qui vehiculum trahebant <i>Aug</i>; e. a praestitutis itineribus <i>Lact</i>);\n<b>2)</b> отклонять (animum a tramite <i>Sid</i>)."],
  ["exorcismus", "<big>exorcismus</big>\n\n<b>exorcismus, &#299;</b> <i>m</i> (<i>греч.</i>)\nизгнание бесов <i>Tert</i>."],
  ["exorciso", "<big>exorciso</big>\n\n<b>exorc&#299;so</b> <i>v. l.</i>\n= exorcizo."],
  ["exorcista", "<big>exorcista</big>\n\n<b>exorcista, ae</b> <i>m</i> (<i>греч.</i>)\nизгоняющий бесов <i>CJ, Eccl</i>."],
  ["exorcizo", "<big>exorcizo</big>\n\n<b>exorcizo, —, —, &#257;re</b>\nизгонять бесов <i>Dig, Eccl</i>."],
  ["exordior", "<big>exordior</big>\n\n<b>ex–&#333;rdior, &#333;rsus sum, &#299;r&#299;</b> <i>depon.</i>\n<b>1)</b> закладывать основу (<i>ткани</i>), начинать ткать (exorsa tela <i>Pl</i>);\n<b>2)</b> приступать, затевать, замышлять (facinus <i>Pl</i>; bellum <i>L</i>); начинать (ab aliqua re <i>C</i>): talem fabulam exorsus est <i>Pt</i> он начал следующий рассказ."],
  ["exordium", "<big>exordium</big>\n\n<b>ex&#333;rdium, &#299;</b> <i>n</i> [exordior]\n<b>1)</b> закладывание основы (<i>ткани</i>) (<i>sc.</i> togae <i>Q</i>);\n<b>2)</b> начало (vitae, hujus mali <i>C</i>; rerum <i>Lcr</i>); вступление, введение (orationis <i>C, Q</i>): quae prima exordia sumat? <i>V</i> с чего ему начать (свою речь)?;\n<b>3)</b> сочинение, трактат <i>Col</i>."],
  ["exoriens", "<big>exoriens</big>\n\n<b>exori&#275;ns, entis</b> <i>m</i> [exorior]\n(<i>sc.</i> sol)\n<b>1)</b> восходящее солнце, восход <i>или</i> утро <i>Prp</i>;\n<b>2)</b> восток <i>Col</i>."],
  ["exorior", "<big>exorior</big>\n\n<b>ex–orior, ortus sum, &#299;r&#299;</b> <i>depon.</i> (<i>формы иногда по 3-му спряж.</i>: <i>imper.</i> exorere <i>Ter</i>; <i>praes. ind.</i> exoritur <i>Lcr, V, O</i>; <i>impf. conjct.</i> exorerentur <i>Lcr, L</i>)\n<b>1)</b> подниматься, восходить (sol exoriens <i>V</i>): exoriente aurora diei <i>Amm</i> когда занялась утренняя заря || появляться (repentinus Sulla exoritur <i>C</i>); показываться, начинаться (exorti dolores <i>Pl</i>; exoriens annus <i>Tib</i>); становиться, делаться (sic exortus est rex Lydiae <i>C</i>);\n<b>2)</b> воспрянуть, оправиться, свободно вздохнуть (ego nunc paulum exorior <i>C</i>);\n<b>3)</b> возникать, проистекать, происходить (fama exoritur de aliqua re <i>и</i> alicujus rei <i>L</i>; honestum, quod ex virtutibus exoritur <i>C</i>): clamor exortus est <i>Sl</i> поднялся крик; exoriare (= exoriaris) aliquis nostris ex ossibus ultor <i>V</i> да восстанет из наших костей некий мститель (<i>слова Дидоны</i>)."],
  ["exornatio", "<big>exornatio</big>\n\n<b>ex&#333;rn&#257;tio, &#333;nis</b> <i>f</i> [exorno]\n<b>1)</b> украшение (aliquid ad exornationem exponere <i>C</i>);\n<b>2)</b> <i>ритор.</i> ораторские украшения <i>rhH</i>; цветистая речь <i>C</i>."],
  ["exornator", "<big>exornator</big>\n\n<b>ex&#333;rn&#257;tor, &#333;ris</b> <i>m</i> [exorno]\nукрашающий, прикрашивающий (non e. rerum, sed tantummodo narrator <i>C</i>)."],
  ["exorno", "<big>exorno</big>\n\n<b>ex–&#333;rno, &#257;v&#299;, &#257;tum, &#257;re</b>\n<b>1)</b> снабжать (vicinitatem armis <i>Sl</i>); располагать в боевом порядке (aciem <i>Sl</i>); снаряжать (classem <i>Just</i>); справлять (nuptias <i>Pl</i>); устраивать (convivium omni opulentia <i>C</i>);\n<b>2)</b> убирать, украшать, наряжать (digitos anulis <i>Sen</i>; aliquem veste varia <i>Ter</i>; domum, locum <i>C</i>): exornari in peregrinum modum <i>Pl</i> нарядиться по-иноземному; e. orationem <i>C</i> расцветить речь;\n<b>3)</b> увенчивать, возвеличивать, хвалить, славить (philosophiam <i>C</i>): e. aliquem praetura <i>PJ</i> облечь кого-л. званием претора;\n<b>4)</b> лишать украшений <i>Tert</i>."],
  ["exoro", "<big>exoro</big>\n\n<b>ex–&#333;ro, &#257;v&#299;, &#257;tum, &#257;re</b>\n<b>1)</b> упрашивать, уговаривать, склонять просьбами, умолять (aliquem <i>Pl etc.</i>, <i>реже</i> ab aliquo <i>Just</i>); <i>поэт.</i> смягчать, трогать, умилостивлять (Jovem peculio <i>Pt</i>; carmina exorant deos <i>O</i>): exoratus a nobis missionem dedit puero <i>Pt</i> по нашей просьбе он пощадил юношу;\n<b>2)</b> выпрашивать (aliquam rem ab aliquo <i>Pl etc.</i> <i>или</i> aliquem aliquam rem <i>Pt, St</i>);\n<b>3)</b> вызывать, внушать (facies exorat amorem <i>O</i>)."],
  ["exors", "<big>exors</big>\n\n<b>exors, ortis</b> <i>v. l.</i>\n= exsors."],
  ["exorsa", "<big>exorsa</big>\n\n<b>ex&#333;rsa, &#333;rum</b> <i>n</i> [exorsus]\n<b>1)</b> начало, вступление (per ambages et longa e. aliquem tenere <i>V</i>);\n<b>2)</b> начинание: sua cuique e. laborem fortunamque ferent <i>V</i> начинания принесут одному трудную жизнь, другому же счастье."],
  ["exorsus 1", "<big>exorsus</big>\n\n<b>I ex&#333;rsus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> exordior;\n<b>2.</b> <i>adj.</i> начатый <i>Pl, C</i>."],
  ["exorsus 2", "<big>exorsus</big>\n\n<b>II ex&#333;rsus, &#363;s</b> <i>m</i> [exordior]\nначало, вступление (orationis <i>C</i>)."],
  ["exortiva", "<big>exortiva</big>\n\n<b>exort&#299;va, &#333;rum</b> <i>n</i> [exortivus]\nвосточные области (страны), восток <i>PM</i>."],
  ["exortivus", "<big>exortivus</big>\n\n<b>exort&#299;vus, a, um</b> [exorior]\n<b>1)</b> относящийся к восходу (mensura <i>PM</i>);\n<b>2)</b> восточный (pars <i>PM</i>)."],
  ["exortus 1", "<big>exortus</big>\n\n<b>I exortus, a, um</b> <i>part. pf. к</i>\nexorior."],
  ["exortus 2", "<big>exortus</big>\n\n<b>II exortus, &#363;s</b> <i>m</i> [exorior]\n<b>1)</b> восход (solis <i>rhH, PM</i>; stellarum <i>PM</i>);\n<b>2)</b> начало, возникновение (Aquilonis <i>PM</i>); происхождение, зарождение, исток (Danuvii <i>PM</i>);\n<b>3)</b> восшествие на престол (imperatoris <i>PJ</i>)."],
  ["exos", "<big>exos</big>\n\n<b>ex–os, ossis</b> <i>adj.</i>\nбескостный, лишённый костей (e. et exsanguis, <i>sc.</i> vermis <i>Lcr</i>)."],
  ["exosculatio", "<big>exosculatio</big>\n\n<b>ex&#333;scul&#257;tio, &#333;nis</b> <i>f</i> [exosculor]\nлобызание, взаимные поцелуи (<i>sc.</i> columbarum <i>PM</i>)."],
  ["exosculor", "<big>exosculor</big>\n\n<b>ex–&#333;sculor, &#257;tus sum, &#257;r&#299;</b> <i>depon.</i>\n<b>1)</b> расцеловать, сердечно целовать (aliquem <i>T, PJ, Su</i>; manus alicujus <i>T</i>);\n<b>2)</b> восхищаться, расхваливать (sententiam <i>Sen</i>; ingenium alicujus <i>AG</i>)."],
  ["exossis", "<big>exossis</big>\n\n<b>ex–ossis, e</b> [ex + os]\n<b>1)</b> не имеющий костей, бескостный (lepus marinus <i>Ap</i>);\n<b>2)</b> гибкий (e. et enervis <i>Ap</i>);\n<b>3)</b> вялый, неряшливый (dictio <i>Sid</i>)."],
  ["exosso", "<big>exosso</big>\n\n<b>ex–osso, &#257;v&#299;, &#257;tum, &#257;re</b> [os]\n<b>1)</b> вынимать кости (e. piscem <i>Ter, Eccl</i>; exossatae gallinae <i>Pt</i>); exossatum pectus <i>Lcr</i> гибкая грудь (туловище);\n<b>2)</b> очищать от камней, <i>по др.</i> истощать (exossatus ager <i>Pers</i>);\n<b>3)</b> обгладывать до костей (gregem dispersum <i>Vlg</i>)."],
  ["exossus", "<big>exossus</big>\n\n<b>exossus, a, um</b> <i>Ap</i>\n= exossis."],
  ["exostra", "<big>exostra</big>\n\n<b>ex&#333;stra, ae</b> <i>f</i> (<i>греч.</i>)\nэксостра:\n<b>1)</b> <i>театральная машина, поворачивающая к зрителям внутреннюю часть дома, т. е. вращающаяся сцена</i>: in exostra <i>C</i> на глазах у всех, публично, открыто, явно;\n<b>2)</b> опускной мостик (<i>осадной башни</i>) <i>Veg</i>."],
  ["exosus", "<big>exosus</big>\n\n<b>ex–&#333;sus, a, um</b> [odi]\n<b>1)</b> ненавидящий (aliquem <i>V</i>; verba superba <i>O</i>);\n<b>2)</b> ненавидимый, ненавистный (universis ob scelera <i>Eutr</i>; omnibus castris <i>Spart</i>)."],
  ["exotericus", "<big>exotericus</big>\n\n<b>ex&#333;tericus, a, um</b> (<i>греч.</i>)\nэксотерический, предназначенный для непосвящённых, общедоступный, популярный (liber <i>AG</i>)."],
  ["exoticum", "<big>exoticum</big>\n\n<b>ex&#333;ticum, &#299;</b> <i>n</i> [exoticus]\nиноземное платье <i>Pl</i>."],
  ["exoticus", "<big>exoticus</big>\n\n<b>ex&#333;ticus, a, um</b> (<i>греч.</i>)\nиноземный, чужестранный (unguentum <i>Pl</i>; vinum <i>AG</i>): Graecia exotica <i>Pl</i> = Magna Graecia."],
  ["expallesco", "<big>expallesco</big>\n\n<b>ex–pall&#275;sco, pallu&#299;, —, ere</b>\nбледнеть, белеть от страха (toto ore <i>O</i>): e. aliquid <i>Sil</i> испугаться чего-л.; non e. Pindarici fontis haustus <i>H</i> не побояться пить из пиндарова источника."],
  ["expalliatus", "<big>expalliatus</big>\n\n<b>ex–palli&#257;tus, a, um</b> [pallium]\nлишённый плаща, раздетый <i>Pl</i>."],
  ["expallidus", "<big>expallidus</big>\n\n<b>ex–pallidus, a, um</b>\nсовершенно бледный, побелевший <i>Su, Tert</i>."],
  ["expalmo", "<big>expalmo</big>\n\n<b>ex–palmo, —, &#257;tum, &#257;re</b> [palma]\nдавать пощёчины, бить по лицу (expalmatus manibus <i>Aug</i>)."],
  ["expalpo", "<big>expalpo</big>\n\n<b>ex–palpo, —, —, &#257;re</b>\nвыманить лестью, выпросить ласками <i>Pl</i>."],
  ["expalpor", "<big>expalpor</big>\n\n<b>expalpor</b> <i>Pl</i>\n= expalpo."],
  ["expando", "<big>expando</big>\n\n<b>ex–pando, pand&#299;, p&#257;nsum (passum), ere</b>\n<b>1)</b> распростирать (alas <i>PM</i>); распахивать, растворять (expassae fores <i>T</i>); распускать (florem <i>PM</i>); <i>pass.</i> expandi расширяться (amnis expanditur <i>PJ</i>);\n<b>2)</b> развивать, излагать, объяснять (rerum naturam dictis <i>Lcr</i>)."],
  ["expansio", "<big>expansio</big>\n\n<b>exp&#257;nsio, &#333;nis</b> <i>f</i> [expando]\nраспростирание, растягивание (corporis <i>CA</i>); распростёртость (manuum in cruce fixarum <i>Eccl</i>)."],
  ["expapillatus", "<big>expapillatus</big>\n\n<b>ex–papill&#257;tus, a, um</b> [papilla]\nобнажённый до груди (brachium <i>Pl</i> — <i>v. l.</i>)."],
  ["expartus", "<big>expartus</big>\n\n<b>ex–partus, a, um</b> [pario I]\nутративший способность рождать, ставший бесплодным (pecudes jam expartae <i>Vr</i>)."],
  ["expassus", "<big>expassus</big>\n\n<b>expassus, a, um</b> <i>part. pf. к</i>\nexpando."],
  ["expatior", "<big>expatior</big>\n\n<b>expatior</b> <i>v. l.</i>\n= exspatior."],
  ["expatricius", "<big>expatricius</big>\n\n<b>ex–patricius, &#299;</b> <i>m</i>\nбывший патриций <i>CJ</i>."],
  ["expatro", "<big>expatro</big>\n\n<b>ex–patro, &#257;v&#299;, —, &#257;re</b>\nпромотать в распутстве <i>Ctl</i>."],
  ["expausatus", "<big>expausatus</big>\n\n<b>ex–paus&#257;tus, a, um</b> [pausa]\nраспряжённый (jumentum <i>Veg</i>)."],
  ["expavefacio", "<big>expavefacio</big>\n\n<b>ex–pavefacio, —, (factus), ere</b>\nпугать, устрашать (aliquem <i>Sen</i> — <i>v. l.</i>)."],
  ["expaveo", "<big>expaveo</big>\n\n<b>ex–paveo, —, —, &#275;re</b>\nпугаться, страшиться, ужасаться (aliquid <i>PM, St, Pt</i>)."],
  ["expavesco", "<big>expavesco</big>\n\n<b>ex–pav&#275;sco, p&#257;v&#299;, —, ere</b>\nиспугаться, ужаснуться (ad aliquid <i>Sen</i>, ab aliqua re <i>PM</i> <i>и</i> aliquem <i>или</i> aliquam rem <i>Cld, H, T, PJ</i>)."],
  ["expect-", "<big>expect-</big>\n\n<b>expect-</b> <i>v. l.</i>\n= exspect-."],
  ["expectoro", "<big>expectoro</big>\n\n<b>ex–pectoro, —, —, &#257;re</b> [pectus]\nизгонять, удалять из груди (pavorem <i>Acc</i>; sapientiam <i>Enn</i>)."],
  ["expeculiatus", "<big>expeculiatus</big>\n\n<b>ex–pec&#363;li&#257;tus, a, um</b> [peculium]\nлишённый имущества, обездоленный <i>Pl</i>."],
  ["expedientia", "<big>expedientia</big>\n\n<b>expedientia, ae</b> <i>f</i> [expedio]\nблагоприятные обстоятельства, преимущество <i>Bo&euml;t</i>."],
  ["expedimentum", "<big>expedimentum</big>\n\n<b>exped&#299;mentum, &#299;</b> <i>n</i>\nвыход из трудного положения, решение трудного вопроса <i>Tert</i>."],
  ["expedio", "<big>expedio</big>\n\n<b>ex–pedio, &#299;v&#299; (i&#299;), &#299;tum, &#299;re</b> [pes]\n<b>1)</b> распутывать (nodum <i>C</i>); высвобождать (gressum <i>Ap</i>): expedita dextra <i>Ap</i> освободив правую руку; e. se выпутаться (ex laqueis <i>C</i>; turba, <i>v. l.</i> ex turba <i>Ter</i>): e. se crimine <i>Ter</i> оправдаться в обвинении || развязывать (restim <i>Ap</i>; aliquem illigatum <i>H</i>);\n<b>2)</b> освобождать, избавлять (aliquem ex servitute <i>Pl</i>; aliquem aliqua re <i>или</i> ab, de <i>и</i> ex aliqua re <i>C etc.</i>); помогать, вызволять: e. errantem nemori <i>O</i> вывести заблудившегося из чащи || поставить на ноги, вылечить (aegrotum <i>CC</i>); благополучно провести (aliquem per acuta belli <i>H</i>);\n<b>3)</b> налаживать, обеспечивать (rem frumentariam <i>Cs</i>); достигать, одерживать (victoriam <i>Cs</i>);\n<b>4)</b> вынимать, доставать (Cererem — <i>sc.</i> panem — canistris <i>V</i>); приготовлять (virgas <i>C</i>; arma <i>Cs, V, T</i>; pecunias <i>Su</i>);\n<b>5)</b> снаряжать, оснащать (naves <i>Cs, T</i>; classem <i>bAl</i>); приводить в боевой порядок (legiones <i>Cs</i>); подготовлять (agrum sationibus faciendis <i>Col</i>);\n<b>6)</b> приводить в порядок, улаживать (negotia <i>C</i>): multa ne nunc quidem expedita sunt <i>C</i> многое и поныне ещё не улажено; e. sarcinulam <i>Pt</i> уложить свои вещи; e. nomina sua <i>C</i> оплатить свои долги; e. salutem <i>C</i> обеспечить безопасность, спасти жизнь; se e. приготовиться (ad pugnam <i>L</i>);\n<b>7)</b> бросать, метать (discum, jaculum <i>H</i>);\n<b>8)</b> устраивать (convivia mediis tectis <i>VF</i>);\n<b>9)</b> приводить в исполнение, осуществлять (consilia sua <i>T</i>);\n<b>10)</b> ликвидировать, сбывать, продавать (musti annonam <i>Col</i>; scruta <i>Pt</i>);\n<b>11)</b> (<i>тж.</i> verbo e. <i>Ter</i>) излагать, сообщать, рассказывать (aliquid <i>и</i> de aliqua re <i>Pl, Ter, V etc.</i>; originem alicujus rei <i>T</i>; alicujus rei causam <i>V, T</i>);\n<b>12)</b> содействовать, благоприятствовать: quid in commune expedit <i>Ap</i> то, что служит общему благу;\n<b>13)</b> <i>impers.</i> expedit полезно, выгодно (si ita expedit <i>C</i>): expedit omnibus (ut) <i>C, Just etc.</i> всем полезно (важно), (чтобы); aliis aliud expedit <i>C</i> у всякого свои интересы."],
  ["expedite", "<big>expedite</big>\n\n<b>exped&#299;t&#275;</b> [expeditus I]\n<b>1)</b> легко, скоро, проворно (aliquid fabricari <i>Amm</i>); с лёгкостью, беспрепятственно (navigare <i>C</i>);\n<b>2)</b> бегло, плавно (explicare <i>C</i>; loqui <i>Su, Amm</i>)."],
  ["expeditio", "<big>expeditio</big>\n\n<b>exped&#299;tio, &#333;nis</b> <i>f</i> [expedio]\n<b>1)</b> приведение в порядок, установление (multarum rerum <i>rhH</i>);\n<b>2)</b> устройство, сооружение (aedificiorum <i>Vtr</i>);\n<b>3)</b> <i>воен.</i> набег, экспедиция, поход (e. nocturna <i>Hirt</i>; in expeditionem ire <i>L</i>; exercitum educere in expeditionem <i>C</i>; adsuetus expeditionibus miles <i>T</i>);\n<b>4)</b> вылет (apium <i>PM</i>);\n<b>5)</b> <i>ритор.</i> фигура последовательного устранения ложных положений <i>rhH</i>."],
  ["expeditionalis", "<big>expeditionalis</big>\n\n<b>exped&#299;ti&#333;n&#257;lis, e</b> [expeditio 3]\nотносящийся к военным походам, (военно-)полевой (usus <i>Spart</i>; habitationes <i>Eccl</i>)."],
  ["expeditus 1", "<big>expeditus</big>\n\n<b>I exped&#299;tus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> expedio;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> беспрепятственный (iter <i>C</i>); не представляющий затруднений, удобный (locus, res frumentaria <i>Cs</i>); торный, удобопроходимый (via <i>L</i>): in expedito est <i>Q, T</i> легко; in expedito habere <i>L</i> иметь под рукой (в полной готовности); nomina expedita <i>C</i> долг, который легко взыскать, <i>т. е.</i> надёжный; miles e. <i>Cs, L, QC</i> солдат без поклажи, не обременённый багажом, идущий налегке, <i>тж.</i> легковооружённый;\n<b>2)</b> беглый, плавный (oratio <i>C</i>);\n<b>3)</b> (всегда) готовый (ad caedem, ad dicendum <i>C</i>); готовый к отплытию (navis <i>Cs etc.</i>); готовый к бою (milites, copiae, legiones <i>Cs etc.</i>);\n<b>4)</b> наличный, ликвидный (pecunia <i>C</i>);\n<b>5)</b> решительный, окончательный (victoria <i>Cs</i>; senatus consultum <i>C</i>);\n<b>6)</b> быстрый, короткий (cena <i>PJ</i>);\n<b>7)</b> улаженный, приведённый в порядок (negotia <i>C</i>)."],
  ["expeditus 2", "<big>expeditus</big>\n\n<b>II exped&#299;tus, &#363;s</b> <i>m</i> <i>Eccl</i>\n= expeditio."],
  ["expello", "<big>expello</big>\n\n<b>ex–pello, pul&#299;, pulsum, ere</b>\n<b>1)</b> выгонять (vaccas stabulis <i>Tib</i>); вырывать, вышибать, выбивать (oculos genis <i>O</i>); прогонять (expulsi monte juvenci <i>O</i>); отталкивать (ab litore navem in altum <i>L</i>); выталкивать (aliquem manibus <i>O</i>); оттеснять (aliquem in provinciam <i>Cs</i>); свергать (regem, tyrannum <i>C</i>); изгонять (aliquem ex urbe <i>C</i>; domo <i>C</i>; hostes finibus <i>Cs</i>): naturam expellas furca, tamen usque recurret <i>H</i> можешь природу хоть вилами гнать, она всё же вернётся || выбрасывать (naves in ripam <i>QC</i>): e. sagittam arcu <i>O</i> пустить стрелу из лука; lusu expellente <i>Pt</i> перебрасываясь между собой (мячами); e. se in auras <i>O</i> появиться на свет, родиться;\n<b>2)</b> разгонять (somnum <i>V</i>); удалять, устранять, рассеивать (curas pectore <i>Lcn</i>; dubitationem <i>Cs</i>); утолять (famem <i>T</i>); побеждать (paupertatem <i>Pt</i>); исцелять (morbum <i>H</i>); изглаживать (memoriam alicujus rei <i>Cs</i>); уничтожать, разрушать (spem metus expulerat <i>O</i>): e. aliquem aliqua re <i>C, O, Su etc.</i> лишить кого-л. чего-л.; expelli aliqua re <i>bAfr</i> утратить что-л.; se e. periculo <i>Pl</i> ускользнуть от опасности."],
  ["expendo", "<big>expendo</big>\n\n<b>ex–pendo, pend&#299;, p&#275;nsum, ere</b>\n<b>1)</b> взвешивать (aliquem <i>Pl</i> <i>и</i> aliquid <i>CC</i>); уравновешивать (aurum auro <i>Pl</i>); отвешивать (<i>в уплату</i>), выплачивать, платить (nummos <i>H</i>; viginti milia talenta <i>Just</i>): ferre alicui expensum <i>C</i> внести (записать в книгу) уплаченную кому-л. сумму; alicui sine fenore pecunias expensas ferre <i>L</i> записывать за кем-л. выплату денежных сумм без процентов (<i>т. е.</i> давать в долг без процентов); legionem expensam Caesari ferre <i>Caelius ap. C</i> предоставить легион в распоряжение Цезаря; auro expendi <i>Pl</i> быть на вес золота; tabulae (codex) expensi <i>C</i> расходная книга (счёт расходов); e. poenas <i>V</i> понести кару (искупать вину); e. scelus <i>V</i> быть наказанным за преступление;\n<b>2)</b> обсуждать, соображать, мысленно взвешивать (argumenta <i>C</i>; belli consilia <i>T</i>); оценивать по достоинству, давать оценку (e. cives <i>C</i>): e. meritis <i>O</i> оценивать по заслугам. — <i>См. тж.</i> expensum."],
  ["expensa", "<big>expensa</big>\n\n<b>exp&#275;nsa, ae</b> <i>f</i> (<i>sc.</i> pecunia)\nрасход, затраты <i>Cld, Dig, Vlg</i>."],
  ["expensilatio", "<big>expensilatio</big>\n\n<b>exp&#275;nsil&#257;tio, &#333;nis</b> <i>f</i> <i>AG</i>\n= latio expensi (<i>см.</i> expensum)."],
  ["expensio", "<big>expensio</big>\n\n<b>exp&#275;nsio, &#333;nis</b> <i>f</i> <i>CTh, Symm</i>\n= expensa."],
  ["expenso", "<big>expenso</big>\n\n<b>exp&#275;nso, &#257;v&#299;, &#257;tum, &#257;re</b> [<i>intens. к</i> expendo]\n<b>1)</b> уравновешивать, выравнивать (aliquid aliqua re <i>Macr</i>);\n<b>2)</b> выплачивать (nummos alicui <i>Dig</i>)."],
  ["expensum", "<big>expensum</big>\n\n<b>exp&#275;nsum, &#299;</b> <i>n</i> [expendo]\nрасход, затрата: ratio accepti atque expensi <i>Pl</i> счёт приходов и расходов; expensi latio <i>AG</i> запись в счёт расходов; e. ferre alicui <i>C</i> — <i>см.</i> expendo 1."],
  ["expergefacio", "<big>expergefacio</big>\n\n<b>experg&#275;–facio, f&#275;c&#299;, factum, ere</b> [expergo]\n<b>1)</b> (раз)будить, пробуждать (aliquem e somno <i>Su</i>): se e. <i>C</i> прийти в себя, пробудиться, <i>перен.</i> одуматься (expergefactum caput erigere <i>Lcr</i>);\n<b>2)</b> вызывать к жизни, извлекать (из лиры) (mele digitis expergefacta <i>Lcr</i>); производить, совершать (flagitium <i>Pl</i>)."],
  ["expergefactio", "<big>expergefactio</big>\n\n<b>experg&#275;factio, &#333;nis</b> <i>f</i>\nпробуждение (a somno <i>Aug</i>)."],
  ["expergefio", "<big>expergefio</big>\n\n<b>experg&#275;f&#299;o, factus sum, fier&#299;</b> <i>pass. к</i>\nexpergefacio."],
  ["expergifico", "<big>expergifico</big>\n\n<b>expergifico, —, —, &#257;re</b> [expergificus]\nбудить, подбадривать (ingenium <i>AG</i>)."],
  ["expergificus", "<big>expergificus</big>\n\n<b>expergificus, a, um</b> [expergo + facio]\nпробуждающий, вселяющий бодрость (carmen galli <i>Ap</i>)."],
  ["expergiscor", "<big>expergiscor</big>\n\n<b>ex–perg&#299;scor, perr&#275;ctus sum, perg&#299;sc&#299;</b> <i>depon.</i>\n<b>1)</b> просыпаться, пробуждаться (simul cum sole <i>C</i>);\n<b>2)</b> воспрянуть духом, приободриться <i>Ter, Sl</i>; восстать (experrecta nobilitas <i>C</i>);\n<b>3)</b> напрячь силы, <i>перен.</i> придумать: nisi quid Burrus et Seneca expergiscerentur <i>T</i> разве что Бурр и Сенека придумают какой-нибудь выход."],
  ["expergite", "<big>expergite</big>\n\n<b>expergit&#275;</b> [expergo]\nбдительно, внимательно (auscultare <i>Ap</i>): e. munus obire <i>Ap</i> бдительно исполнять свои обязанности."],
  ["expergo", "<big>expergo</big>\n\n<b>ex–pergo, perg&#299;, pergitum, ere</b>\nпробуждать (expergitus exstat <i>Lcr</i>); ободрять (animos juvenum <i>AG</i>)."],
  ["experiens", "<big>experiens</big>\n\n<b>experi&#275;ns, entis</b>\n<b>1.</b> <i>part. praes. к</i> experior;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> деятельный, предприимчивый, деловой (homo fortis et e. <i>C</i>): e. laborum <i>O</i> трудолюбивый, выносливый в трудах;\n<b>2)</b> опытный, искушённый (in aliqua arte <i>Is</i>; rei militaris <i>Eccl</i>)."],
  ["experienter", "<big>experienter</big>\n\n<b>experienter</b>\nс большим опытом, <i>т. е.</i> со знанием дела, умело <i>Bo&euml;t</i>."],
  ["experientia", "<big>experientia</big>\n\n<b>experientia, ae</b> <i>f</i> [experiens]\n<b>1)</b> попытка, проба (patrimonii amplificandi <i>C</i>);\n<b>2)</b> опыт, опытность, практика (vir longa experientia <i>T</i>)."],
  ["experimentum", "<big>experimentum</big>\n\n<b>exper&#299;mentum, &#299;</b> <i>n</i> [experior]\n<b>1)</b> проба, (законченный) опыт, практика (crescit audacia experimento <i>PJ</i>): experimentis cognitum est <i>Sl</i> известно из опыта; minus valent praecepta, quam experimenta <i>Q</i> (теоретические) указания менее важны, чем опытные данные;\n<b>2)</b> наглядный довод, основанное на опытах доказательство (hoc maximum est e. <i>C</i>; dare experimenta virtutis <i>VP</i>): e. oculorum capere <i>Pt</i> воочию убедиться."],
  ["experio", "<big>experio</big>\n\n<b>experio</b> <i>Capit</i>\n= experior."],
  ["experior", "<big>experior</big>\n\n<b>ex–perior, pertus sum, &#299;r&#299;</b> <i>depon.</i> [<i>одного корня с</i> peritus <i>и</i> periculum]\n<b>1)</b> (ис)пробовать, испытывать (vim veneni in aliquo <i>C</i>; multa <i>PS</i>): e. aliquem <i>C</i> подвергать кого-л. испытанию, <i>но тж.</i> <i>Nep</i> помериться силами с кем-л.; omnia de pace e. <i>Cs</i> испробовать всё для сохранения мира; e. se aliqua re <i>PJ</i> <i>и</i> in aliqua re <i>Q</i> испробовать (попытать) свои силы в чём-л.; expertus vacuum aera <i>H</i> попытавшийся подняться в воздушное пространство (Дедал); alteram fortunam expertus <i>T</i> изведавший превратности судьбы, <i>но</i>: e. praesentem fortunam <i>Q</i> пользоваться настоящим благополучием; expertus id scio <i>C</i> (de me experior <i>C</i>), <i>тж.</i> usu experior <i>Ap</i> <i>и</i> experiendo cognovi <i>C</i> я знаю это по опыту; experiundo comprobare <i>Ap</i> проверить на опыте; e. licentiam linguae <i>L</i> позволить себе заговорить свободно; e. ultima <i>CC</i> <i>или</i> extrema <i>Sl</i> решиться на крайние средства; multos e. ingratos <i>Sen</i> встречать в своей жизни много неблагодарных людей; e. aliquid <i>L</i> поставить что-л. на карту (рискнуть чём-л.); judicium populi Romani e. <i>L</i> подчиниться решению римского народа; e. balneum <i>CC</i> прибегнуть к купанию (<i>как к лечебному средству</i>);\n<b>2)</b> <i>юр.</i> (<i>тж.</i> jus e. <i>Dig</i>) судиться (de injuriis <i>и</i> injuriarum <i>Dig</i>); тягаться (cum aliquo <i>C</i>). — <i>См. тж.</i> expertus."],
  ["experrectus", "<big>experrectus</big>\n\n<b>experr&#275;ctus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> expergiscor;\n<b>2.</b> <i>adj.</i> бодрый, бодрствующий <i>или</i> проснувшийся (triclinarches <i>Pt</i>); бдительный (insomnis et e. <i>Sen</i>)."],
  ["expers", "<big>expers</big>\n\n<b>expers, pertis</b> <i>adj.</i> [ex + pars]\n<b>1)</b> не принимающий участия, непричастный, не имеющий отношения (alicujus rei <i>Ter, C etc.</i>, <i>реже</i> aliqua re <i>Pl, Sl</i>); thalami e. <i>V</i> обречённый на безбрачие || недопущенный (publici consilii <i>C</i>); непосвящённый (consilii <i>Sl</i>);\n<b>2)</b> несведущий (litterarum Graecarum <i>Nep</i>); лишённый, не имеющий (eruditionis <i>C</i>);\n<b>3)</b> неподверженный (periculorum <i>C</i>): e. omnis curae <i>L</i> беззаботный; e. metu <i>Pl</i> бесстрашный; e. sui <i>Sen</i> вне себя; e. culpae <i>Su</i> невинный; vinum maris e. <i>H</i> чистое вино (<i>без примеси морской воды</i>)."],
  ["experte", "<big>experte</big>\n\n<b>expert&#275;</b> <i>Bo&euml;t</i>\n= experienter."],
  ["expertio", "<big>expertio</big>\n\n<b>expertio, &#333;nis</b> <i>f</i> [experior]\nопыт, проба <i>Vtr</i>."],
  ["expertis 1", "<big>expertis</big>\n\n<b>I expertis</b> <i>gen. sg. к</i>\nexpers."],
  ["expertis 2", "<big>expertis</big>\n\n<b>II expertis</b> <i>dat. abl. pl. к</i>\nexpertus."],
  ["expertus", "<big>expertus</big>\n\n<b>expertus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> experior;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> знающий по опыту, лично изведавший, опытный (alicujus rei <i>V etc.</i> <i>или</i> aliqua re <i>T</i>); homo e. servitutis <i>L</i> человек, испытавший рабство; miles e. belli <i>V</i> <i>и</i> bellis <i>T</i> солдат с боевым опытом; e. metuit <i>H</i> умудрённый опытом поостережётся; experto credite <i>V, O</i> поверьте (моему) опыту;\n<b>2)</b> испытанный, изведанный, проверенный (vir virtutis expertae <i>Just</i>): per omnia e. <i>L</i> всесторонне проверенный."],
  ["expetendus", "<big>expetendus</big>\n\n<b>expetendus, a, um</b> [expeto]\nжелательный, желанный (gloriam expetendam putare <i>C</i>)."],
  ["expetens", "<big>expetens</big>\n\n<b>expet&#275;ns, entis</b>\n<b>1.</b> <i>part. praes. к</i> expeto;\n<b>2.</b> <i>adj.</i> жадный, алчный <i>C</i>."],
  ["expetesso", "<big>expetesso</big>\n\n<b>expetesso, —, —, ere</b> [expeto]\n<b>1)</b> желать, томиться желанием (e. aliquem, aliquid <i>Pl</i>);\n<b>2)</b> просить, требовать (consilium ab aliquo <i>Pl</i>): e. preces ab aliquo <i>Pl</i> обращаться с мольбами к кому-л."],
  ["expetibilis", "<big>expetibilis</big>\n\n<b>expet&#299;bilis, e</b> [expeto]\nдостигаемый сверх непосредственной цели, получаемый сверх желательного, обретаемый в качестве побочного результата: expetendum est quod bonum est, expetibile — quod nobis contingit, quum bonum consecuti sumus <i>Sen</i> (<i>мнение стоиков</i>) &laquo;expetendum&raquo; есть (самое) благо, а &laquo;expetibile&raquo; — то, что мы обретаем, достигнув блага."],
  ["expetitor", "<big>expetitor</big>\n\n<b>expet&#299;tor, &#333;ris</b> <i>m</i>\nстремящийся, томимый желанием (animus alicujus e. <i>Symm</i>)."],
  ["expeto", "<big>expeto</big>\n\n<b>ex–peto, pet&#299;v&#299;, pet&#299;tum, ere</b>\n<b>1)</b> стараться достать, добиваться, домогаться, желать (pecuniam <i>C</i>; vincere <i>C</i>; quid est quod expetas amplius? <i>C</i>): e. vitam alicujus <i>C</i> посягать на чью-л. жизнь; e. aliquid ab aliquo <i>C</i> просить (требовать) чего-л. у кого-л.; sibi consilia e. <i>Pl</i> принять решения; e. poenas ab aliquo <i>L, C</i> наказать кого-л.;\n<b>2)</b> устремляться (medium terrae locum <i>C</i>); обращаться, обрушиваться (in aliquem, <i>редко</i> <i>Pl</i> alicui); omnes clades belli in aliquem expetunt <i>L</i> все бедствия войны падают на (обращаются против) кого-л.; in servitute expetunt multa iniqua <i>Pl</i> много невзгод приходится испытать в рабстве;\n<b>3)</b> достигать, продолжаться: aetatem e. <i>Pl</i> длиться вечно;\n<b>4)</b> расспрашивать: e, aliquid <i>и</i> aliquem <i>VF</i> расспрашивать о чём-л. <i>и</i> о ком-л."],
  ["expiatio", "<big>expiatio</big>\n\n<b>expi&#257;tio, &#333;nis</b> <i>f</i> [expio]\nочищение, умилостивительная <i>или</i> очистительная жертва (dis iratis e. debetur <i>C</i>); искупление, заглаживание (scelerum et impietatum <i>C</i>)."],
  ["expiator", "<big>expiator</big>\n\n<b>expi&#257;tor, &#333;ris</b> <i>m</i> [expio]\nискупитель <i>Eccl</i>."],
  ["expiatorius", "<big>expiatorius</big>\n\n<b>expi&#257;t&#333;rius, a, um</b>\nискупительный (poenae <i>Aug</i>)."],
  ["expiatus", "<big>expiatus</big>\n\n<b>expi&#257;tus, &#363;s</b> <i>m</i> <i>Tert</i>\n= expiatio."],
  ["expictus", "<big>expictus</big>\n\n<b>expictus, a, um</b> <i>part. pf. к</i>\nexpingo."],
  ["expilatio", "<big>expilatio</big>\n\n<b>exp&#299;l&#257;tio, &#333;nis</b> <i>f</i> [expilo]\nразграбление, расхищение (Asiae <i>C</i>); ограбление, разорение (fanorum, sociorum et civium <i>C</i>)."],
  ["expilator", "<big>expilator</big>\n\n<b>exp&#299;l&#257;tor, &#333;ris</b> <i>m</i> [expilo]\nграбитель, расхититель <i>LM, C, Dig</i>."],
  ["expilo", "<big>expilo</big>\n\n<b>ex–p&#299;lo, &#257;v&#299;, &#257;tum, &#257;re</b>\n<b>1)</b> разграблять, расхищать (aerarium <i>C</i>); стащить, украсть (lagoenam <i>Pt</i>); ограбить (aliquem <i>C</i>);\n<b>2)</b> вырвать (oculos genis <i>O</i> — <i>v. l.</i>)."],
  ["expingo", "<big>expingo</big>\n\n<b>ex–pingo, p&#299;nx&#299;, pictum, ere</b>\n<b>1)</b> расписывать, разрисовывать, раскрашивать (genas <i>M</i>);\n<b>2)</b> <i>перен.</i> украшать, прикрашивать (pericula <i>PM</i>);\n<b>3)</b> изображать, описывать (regiones et oras <i>C</i>)."],
  ["expinso", "<big>expinso</big>\n\n<b>ex–p&#299;nso, —, —, ere</b>\nистолочь, размолоть (far <i>Cato</i>)."],
  ["expio", "<big>expio</big>\n\n<b>ex–pio, &#257;v&#299;, &#257;tum, &#257;re</b>\n<b>1)</b> очищать жертвами, очищать от грехов (forum a sceleris vestigiis <i>C</i>; aliquem <i>Pl etc.</i>);\n<b>2)</b> умилостивлять, смягчать (numen, manes mortuorum <i>C</i>); отвращать, обезвреживать, делать безвредным (prodigium <i>L</i>; vocem nocturnam <i>L</i>);\n<b>3)</b> (по)нести наказание, искупать (scelus supplicio <i>C</i>); заглаживать, исправлять (incommodum virtute <i>Cs</i>; errorem <i>PJ</i>; cladem victoriis <i>Fl</i>): e. scelus alicujus in aliquem <i>Q</i> покарать кого-л. за чьё-л. (чужое) преступление."],
  ["expiro", "<big>expiro</big>\n\n<b>exp&#299;ro</b> <i>v. l.</i>\n= exspiro."],
  ["expiscor", "<big>expiscor</big>\n\n<b>ex–piscor, &#257;tus sum, &#257;r&#299;</b> <i>depon.</i>\nвылавливать рыбу, <i>перен.</i> выуживать, выспрашивать, разузнавать, выведывать (omnia ab aliquo <i>C</i>)."],
  ["explanabilis", "<big>explanabilis</big>\n\n<b>expl&#257;n&#257;bilis, e</b> [explano]\nясный, отчётливый, членораздельный (vox <i>Sen</i>)."],
  ["explanate", "<big>explanate</big>\n\n<b>expl&#257;n&#257;t&#275;</b> [explanatus]\nясно, отчётливо (definire aliquid explanatius <i>C</i>)."],
  ["explanatio", "<big>explanatio</big>\n\n<b>expl&#257;n&#257;tio, &#333;nis</b> <i>f</i> [explano]\n<b>1)</b> объяснение, (ис)толкование (naturae <i>C</i>; sententiae <i>Q</i>); <i>ритор.</i> разъяснение, гипотипоз <i>C, Q</i>;\n<b>2)</b> внятное произношение (vocum <i>Q</i>)."],
  ["explanator", "<big>explanator</big>\n\n<b>expl&#257;n&#257;tor, &#333;ris</b> <i>m</i> [explano]\nпояснитель, (ис)толкователь (oraculorum <i>C</i>)."],
  ["explanatus", "<big>explanatus</big>\n\n<b>expl&#257;n&#257;tus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> explano;\n<b>2.</b> <i>adj.</i> ясный, отчётливый: explanata vocum impressio <i>C</i> способность издавать членораздельные звуки; os explanatum <i>Q</i> отчётливое произношение."],
  ["explano", "<big>explano</big>\n\n<b>ex–pl&#257;no, &#257;v&#299;, &#257;tum, &#257;re</b>\n<b>1)</b> выравнивать, распластывать, развёртывать (aliquid in denos pedes <i>PM</i>);\n<b>2)</b> объяснять (aliquid alicui <i>C</i>); истолковывать, толковать (rem obscuram <i>C</i>);\n<b>3)</b> внятно произносить (verba <i>PJ</i>)."],
  ["explanto", "<big>explanto</big>\n\n<b>ex–planto, —, —, &#257;re</b>\nвырвать, вырыть из земли (pampinos <i>Col</i>)."],
  ["explaudo", "<big>explaudo</big>\n\n<b>explaudo</b> <i>v. l.</i>\n= explodo."],
  ["explementum", "<big>explementum</big>\n\n<b>expl&#275;mentum, &#299;</b> <i>n</i> [expleo]\n<b>1)</b> наполнение (inanimentis e. <i>Pl</i>);\n<b>2)</b> корм, пища (ventris <i>Sen</i>);\n<b>3)</b> дополнение, прибавление <i>Sen</i>;\n<b>4)</b> исполнение (desiderii <i>Lact</i>)."],
  ["explendesco", "<big>explendesco</big>\n\n<b>explend&#275;sco</b> <i>v. l.</i>\n= exsplendesco."],
  ["expleo", "<big>expleo</big>\n\n<b>ex–pleo, &#275;v&#299;, &#275;tum, &#275;re</b>\n<b>1)</b> наполнять, заполнять (rimas <i>C</i>; fossam aggere <i>Cs</i>);\n<b>2)</b> занимать (cohortes explent locum <i>Cs</i>);\n<b>3)</b> насыщать, (на)кормить досыта (bovem frondibus <i>H</i>): e. sitim <i>C</i> утолить жажду;\n<b>4)</b> унимать (expletur lacrimis dolor <i>O</i>); преисполнять (animum gaudio <i>Ter</i>); исполнять (spem <i>L</i>);\n<b>5)</b> насыщать, удовлетворять (aliquem <i>Ter</i>; cupiditatem, avaritiam <i>C</i>; odium <i>L</i>);\n<b>6)</b> пополнять, дополнять (numerum <i>C</i>); explenda simulatione <i>T</i> чтобы довести притворство до конца || укомплектовывать (legiones <i>Sl</i>; exercitum <i>L</i>);\n<b>7)</b> вознаграждать, исправлять, возмещать (damna <i>L</i>);\n<b>8)</b> выполнять, исполнять (munus, officium <i>C</i>; mandatum <i>G</i>);\n<b>9)</b> достигать, составлять: quinque orbes cursu e. <i>V</i> пробежать пять раз по кругу; mollioribus numeris e. sententias <i>C</i> округлять фразы с помощью более гибких ритмов (<i>о прозе Исократа</i>); aurum, quod summam talenti expleat <i>L</i> золото стоимостью в 1 талант;\n<b>10)</b> доводить (patrum numerum ad trecentorum summam <i>L</i>): e. justam altitudinem muri <i>C</i> довести стену до надлежащей вышины;\n<b>11)</b> прожить (vitam beatam <i>C</i>): e. fatales annos <i>Tib</i> прожить положенное судьбой число лет; unum et tricesimum aetatis annum explet <i>T</i> ему исполняется 31 год; expletus annus <i>C</i> полный (законченный) год; supremum diem e. <i>T</i> умереть;\n<b>12)</b> победить большинством голосов: e. tribus (centurias) <i>L</i> получить достаточное число голосов; e. condemnationem <i>C</i> большинством голосов высказаться за осуждение;\n<b>13)</b> <i>арх.</i> разгружать: se e. выгружаться, высаживаться (navibus <i>Enn</i> — <i>v. l.</i>). — <i>См. тж.</i> expletus."],
  ["expletio", "<big>expletio</big>\n\n<b>expl&#275;tio, &#333;nis</b> <i>f</i> [expleo]\n<b>1)</b> наполнение, <i>перен.</i> насыщение, удовлетворение (cupiditatis, indigentiarum <i>Aug</i>);\n<b>2)</b> исполнение, осуществление (spei usque ad finem <i>Vlg</i>);\n<b>3)</b> восполнение, завершение, доведение до полноты (naturae <i>C</i>): altera dies expletionis hebdomadae septimae <i>Vlg</i> следующий за окончанием седьмой недели (<i>т. е.</i> 50-ый) день."],
  ["expletivus", "<big>expletivus</big>\n\n<b>expl&#275;t&#299;vus, a, um</b> [expleo] <i>грам.</i>\nвосполняющий, дополнительный (conjunctio, syllaba)."],
  ["expletus", "<big>expletus</big>\n\n<b>expl&#275;tus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> expleo;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> полный (vita expleta virtutibus <i>C</i>); совершенный, законченный (perfectus expletusque <i>C</i>);\n<b>2)</b> сытый, пресыщенный (e. atque saturatus <i>C</i>);\n<b>3)</b> удовлетворённый (his artibus <i>Ap</i>)."],
  ["explicabilis", "<big>explicabilis</big>\n\n<b>explic&#257;bilis, e</b> [explico]\n<b>1)</b> могущий быть распутанным: explicabili errore perplexus <i>Mela</i> запутанный, но из которого можно выбраться (<i>об египетском Лабиринте</i>);\n<b>2)</b> объяснимый, ясный (gentes haud explicabili mensura <i>PM</i>)."],
  ["explicate", "<big>explicate</big>\n\n<b>explic&#257;t&#275;</b> [explicatus]\nясно, обстоятельно (dicere de aliqua re <i>C</i>)."],
  ["explicatio", "<big>explicatio</big>\n\n<b>explic&#257;tio, &#333;nis</b> <i>f</i> [explico]\n<b>1)</b> раскручивание, разматывание (rudentis <i>C</i>);\n<b>2)</b> истолкование (naturae <i>C</i>); изложение, объяснение (fabularum <i>C</i>); развёртывание, развитие: in disserendo mira e. <i>C</i> удивительное умение ясно излагать;\n<b>3)</b> изобретение, устройство (commodas vitae perficere explicationes <i>Vtr</i>)."],
  ["explicator", "<big>explicator</big>\n\n<b>explic&#257;tor, &#333;ris</b> <i>m</i> [explico]\nистолкователь, комментатор (rerum <i>C</i>; quaestionis <i>Aug</i>)."],
  ["explicatrix", "<big>explicatrix</big>\n\n<b>explic&#257;tr&#299;x, &#299;cis</b> <i>f</i> [explicator]\nистолковательница: oratoria vis dicendi e. orationis perpetuae ad persuadendum accomodatae <i>C</i> ораторское искусство (есть) искусство связного изложения, служащего целям убеждения."],
  ["explicatus 1", "<big>explicatus</big>\n\n<b>I explic&#257;tus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> explico;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> простирающийся, раскинутый (planissimo in loco <i>C</i>);\n<b>2)</b> приведённый в порядок, упорядоченный, благоустроенный (provincia <i>C</i>);\n<b>3)</b> ясный, понятный (ratio <i>C</i>; responsum <i>Aug</i>)."],
  ["explicatus 2", "<big>explicatus</big>\n\n<b>II explic&#257;tus, &#363;s</b> <i>m</i>\n<b>1)</b> развитие, развёртывание, <i>перен.</i> объяснение <i>C</i>;\n<b>2)</b> выпрямление, разгибание (crurum <i>PM</i>)."],
  ["explicitus", "<big>explicitus</big>\n\n<b>explicitus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> explico;\n<b>2.</b> <i>adj.</i> не представляющий трудностей, удобный, удобоисполнимый (consilium <i>Cs</i>)."],
  ["explico", "<big>explico</big>\n\n<b>ex–plico, &#257;v&#299; (u&#299;), &#257;tum (itum), &#257;re</b>\n<b>1)</b> развёртывать (volumen <i>C</i>): explicitus ad sua cornua liber <i>M</i> развёрнутая до конца (<i>т. е.</i> прочитанная) книга; explicit (= explicitus est) liber <i>Hier</i> конец книги || разворачивать, раскладывать (merces <i>Pt</i>): e. arida ligna in flammas <i>Lcr</i> превращать сухие дрова в пламя;\n<b>2)</b> раскрывать, распахивать (vestem <i>C</i>); распускать (velum <i>Pl</i>); распростирать (pennas <i>O</i>);\n<b>3)</b> разматывать (fusos <i>M</i>); раскручивать (funem <i>Vtr</i>); распутывать (captiones <i>C</i>);\n<b>4)</b> уложить, убить (aliquem sagitta <i>Lcn</i>);\n<b>5)</b> успокоить, заставить утихнуть (turbidum mare <i>SenT</i>);\n<b>6)</b> расширять (forum usque ad atrium Libertatis <i>C</i>);\n<b>7)</b> распространять (aliquid per omnem terrarum ambitum <i>Su</i>);\n<b>8)</b> <i>воен.</i> ставить в линию, выстраивать, развёртывать (aciem, copias <i>L</i>): multitudo navium explicari non potuit <i>Nep</i> масса кораблей не могла развернуться;\n<b>9)</b> приводить в порядок, устраивать (agmen confusum <i>Hirt</i>; convivia <i>M</i>; negotia <i>C, VM</i>); налаживать, организовывать (rem frumentariam <i>Cs</i>): e. frontem alicujus <i>H</i> разгладить морщины на чьём-л. лбу, <i>т. е.</i> развеселить кого-л.;\n<b>10)</b> приводить в исполнение, выполнять (consilium, praecepta, mandata <i>C</i>): e. fugam <i>L</i> бежать; e. iter <i>PJ</i> совершить путь; e. nomen <i>C</i> <i>или</i> pecuniam <i>Dig</i> уплатить долг;\n<b>11)</b> развивать, объяснять, истолковывать, излагать (causam, rationem alicujus rei, philosophiam, vitam alicujus, de rerum natura <i>C</i>): non satis explicata recordatio <i>C</i> не вполне ясное воспоминание;\n<b>12)</b> переводить, перелагать (summorum oratorum Graecas orationes <i>C</i>);\n<b>13)</b> выпутывать, освобождать (aliquem ex laqueis <i>C, Sen</i>); избавлять, спасать, вызволять (se <i>C etc.</i>; aegrum <i>CC</i>; Siciliam multis cinctam periculis <i>C</i>): expliciti acumine Gitonis <i>Pt</i> спасшиеся благодаря изворотливости Гитона; explicitus ex longa valetudine <i>Sen</i> оправившись после долгой болезни;\n<b>14)</b> доводить до конца, завершать (quae per defunctum inchoata sunt <i>Dig</i>)."],
  ["explodo", "<big>explodo</big>\n\n<b>ex–pl&#333;do (explaudo), pl&#333;s&#299;, pl&#333;sum, ere</b> [plaudo]\n<b>1)</b> прогонять шумом (noctem explaudentes alae, <i>sc.</i> galli <i>Lcr</i>); ошикать, освистать (histrio exsibilatur et exploditur <i>C</i>);\n<b>2)</b> выплёскивать (aliquem in litus <i>Sen</i>);\n<b>3)</b> осуждать, порицать, не одобрять, отвергать (sententiam <i>C</i>)."],
  ["explorate", "<big>explorate</big>\n\n<b>expl&#333;r&#257;t&#275;</b> [exploratus]\nрешительно, надёжно, уверенно (promittere, cognoscere, judicare <i>C</i>)."],
  ["exploratio", "<big>exploratio</big>\n\n<b>expl&#333;r&#257;tio, &#333;nis</b> <i>f</i> [exploro]\n<b>1)</b> исследование (veri <i>Sen</i>);\n<b>2)</b> <i>воен.</i> разведка, рекогносцировка <i>T, Dig etc.</i>"],
  ["explorator", "<big>explorator</big>\n\n<b>expl&#333;r&#257;tor, &#333;ris</b> <i>m</i> [exploro]\n<b>1)</b> исследователь (naturae rerum <i>Ap</i>): exploratores ignes <i>Cld</i> <i>или</i> foci <i>M</i> испытание огнём;\n<b>2)</b> <i>воен.</i> разведчик <i>Cs, L, VP, T</i>;\n<b>3)</b> соглядатай, шпион <i>Pl, Sen etc.</i>"],
  ["exploratorius", "<big>exploratorius</big>\n\n<b>expl&#333;r&#257;t&#333;rius, a, um</b> [explorator] <i>воен.</i>\nразведывательный (navis <i>Veg</i>): corona exploratoria <i>Su</i> венок, дававшийся в награду за успешную разведку."],
  ["exploratus", "<big>exploratus</big>\n\n<b>expl&#333;r&#257;tus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> exploro;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> несомненный, верный, достоверный (spes <i>C</i>; victoria <i>C, Cs</i>): mihi exploratum est <i>C</i> <i>или</i> exploratum (pro explorato <i>Cs</i>) habeo <i>C</i> я доподлинно (наверно) знаю;\n<b>2)</b> испытанный, превосходный (magister <i>AG</i>)."],
  ["exploro", "<big>exploro</big>\n\n<b>expl&#333;ro, &#257;v&#299;, &#257;tum, &#257;re</b>\n<b>1)</b> исследовать, изучать (Africam <i>C</i> <i>и</i> ambitum Africae <i>PM</i>); расследовать, выведывать (rem totam <i>C</i>; animum alicujus <i>L</i>; exploranda est veritas <i>Ph</i>);\n<b>2)</b> <i>воен.</i> разведывать, вести разведку, производить рекогносцировку (itinera, consilia hostium <i>Cs etc.</i>): e. fugam alicujus <i>C</i> высматривать дорогу для чьего-л. бегства;\n<b>3)</b> высматривать, подыскивать (locum castris idoneum <i>Cs</i>);\n<b>4)</b> испытывать, проверять (explorant adversa viros <i>Sil</i>): explorat robora fumus <i>V</i> дымом проверяется дерево (<i>т. е.</i> не имеет ли оно щелей);\n<b>5)</b> пробовать, отведывать (cibos potusque <i>T</i>; panis potionisque bonitatem <i>Col</i>)."],
  ["explosio", "<big>explosio</big>\n\n<b>expl&#333;sio, &#333;nis</b> <i>f</i> [explodo]\nшумное неодобрение, освистывание (ludorum <i>C</i>)."],
  ["expolio 1", "<big>expolio</big>\n\n<b>I expolio, &#257;v&#299;, &#257;tum, &#257;re</b> <i>v. l.</i>\n= exspolio."],
  ["expolio 2", "<big>expolio</big>\n\n<b>II ex–polio, &#299;v&#299;, &#299;tum, &#299;re</b>\n<b>1)</b> разглаживать, полировать (aliquid pumice <i>Ctl</i>);\n<b>2)</b> расписывать, раскрашивать (domum <i>Vtr</i>);\n<b>3)</b> отделывать, изукрашивать (orationem <i>Q</i>);\n<b>4)</b> делать образованным, культурным, облагораживать, совершенствовать (aliquem doctrinis omnibus <i>C</i>)."],
  ["expolitio", "<big>expolitio</big>\n\n<b>expol&#299;tio, &#333;nis</b> <i>f</i> [expolio]\n<b>1)</b> полирование (parietum <i>Vtr</i>);\n<b>2)</b> обработка, отделка (e. urbana <i>C</i>);\n<b>3)</b> <i>ритор.</i> разукрашивание, украшение, оформление (numero, <i>sc.</i> materiae verborum <i>C</i>)."],
  ["expolitus", "<big>expolitus</big>\n\n<b>expol&#299;tus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> expolio;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> гладкий, блестящий, лоснящийся (dens <i>Ctl</i>);\n<b>2)</b> отделанный, красивый (aedes <i>Pl</i>; villa expolitissima <i>Scipio ap. AG</i>);\n<b>3)</b> культурный, образованный (vir excultus atque e. <i>C</i>)."],
  ["expono", "<big>expono</big>\n\n<b>ex–p&#333;no, posu&#299;</b> (<i>арх.</i> <b>pos&#299;v&#299;</b>), <b>positum, ere</b>\n<b>1)</b> выставлять (напоказ), раскладывать (vasa <i>C</i>; apparatum in porticibus <i>Su</i>); расставлять (copias in collibus <i>Cs</i>);\n<b>2)</b> подкидывать (puerum <i>L</i>);\n<b>3)</b> сажать, высаживать (herbam in sole <i>Col</i>; planta exponitur mense Februario <i>Pall</i>);\n<b>4)</b> выкладывать, выгружать (merces <i>Dig</i>; frumentum <i>C</i>); высаживать (milites ex navibus <i>и</i> navibus <i>Cs</i>; aliquem in terram <i>Cs</i> <i>и</i> in terra <i>VP</i>; in litore <i>Su</i> <i>и</i> in litus <i>L</i>): e. aliquem ictu <i>Pl</i> выбросить (выкинуть) вон кого-л. || сбивать с ног (cubito, <i>sc.</i> aliquem <i>Pl</i>);\n<b>5)</b> приставлять (expositae scalae <i>V</i>);\n<b>6)</b> оставлять незащищённым, подвергать (locus expositus ad pericula <i>L</i>; exercitus hosti exponitur <i>Fl</i>; provincias barbaris nationibus e. <i>T</i>); подставлять (aliquid ad omnes ictus <i>QC</i>): ager expositus ventis <i>PM</i> поле, открытое ветрам;\n<b>7)</b> представлять, показывать, предлагать (praemia <i>C</i>; vitam alicujus ad imitandum juventuti <i>C</i>);\n<b>8)</b> опубликовывать, обнародовать (orationem <i>C</i>);\n<b>9)</b> излагать, объяснять, описывать (vitam alicujus <i>C</i>; de vita imperatorum <i>Nep</i>; aliquid multitudini <i>Cs</i>; rem pluribus verbis <i>C</i>);\n<b>10)</b> (<i>тж.</i> ad omnium convicia e. <i>Su</i>) выставлять на посмеяние (vitia alicujus <i>Ambr</i>);\n<b>11)</b> отбрасывать прочь (curas <i>Eccl</i>); совлекать с себя (veterem hominem <i>Tert</i>);\n<b>12)</b> выбрасывать за борт (onus propter necessitatem adversae tempestatis <i>Dig</i>)."],
  ["exporgo", "<big>exporgo</big>\n\n<b>exporgo, —, —, ere</b> <i>Pl, Ter</i>\n= exporrigo."],
  ["exporrectus", "<big>exporrectus</big>\n\n<b>exporr&#275;ctus, a, um</b> <i>part. pf. к</i>\nexporrigo."],
  ["exporrigo", "<big>exporrigo</big>\n\n<b>ex–porrigo, porr&#275;x&#299;, porr&#275;ctum, ere</b>\n<b>1)</b> протягивать, растягивать, выпрямлять, распростирать, расправлять (lumbos <i>Pl</i>): exporrecto labello <i>Pers</i> с отвисшей губой; exporge (= exporrige) frontem <i>Ter</i> разгладь лоб, <i>т. е.</i> не будь так мрачен; se e. простираться, тянуться (montes longo se jugo exporrigunt <i>Mela</i>);\n<b>2)</b> раскладывать для просушки (aliquid in sole <i>PM</i>);\n<b>3)</b> <i>воен.</i> развёртывать (equites in longitudinem <i>bAfr</i>): sibi annos e. <i>Sen</i> рассчитывать на свою долговечность."],
  ["exportatio", "<big>exportatio</big>\n\n<b>export&#257;tio, &#333;nis</b> <i>f</i> [exporto]\n<b>1)</b> вывоз (earum rerum, quibus abundaremus <i>C</i>);\n<b>2)</b> изгнание, высылка <i>Sen</i>."],
  ["exporto", "<big>exporto</big>\n\n<b>ex–porto, &#257;v&#299;, &#257;tum, &#257;re</b>\n<b>1)</b> уносить (omnia signa ex fanis <i>C</i>); выносить, вывозить (aurum ex Italia <i>C</i>);\n<b>2)</b> ссылать, изгонять, заточать (aliquem in ultimas terras <i>C</i>)."],
  ["exposco", "<big>exposco</big>\n\n<b>ex–p&#333;sco, pop&#333;sc&#299;, —, ere</b>\nусиленно просить, настоятельно требовать, умолять (misericordiam <i>C</i>; vocibus precibusque pacem <i>V</i>; victoriam ab diis <i>Cs</i>): e. aliquem <i>Nep, L</i> <i>и</i> e. aliquem ad poenam <i>T</i> требовать выдачи кого-л. для наказания."],
  ["exposita", "<big>exposita</big>\n\n<b>exposita, &#333;rum</b> <i>n</i> [expositus]\n<b>1)</b> внешний вид (<i>sc.</i> villae <i>Sen</i>);\n<b>2)</b> <i>ритор.</i> общие места, банальности <i>Q</i>."],
  ["exposite", "<big>exposite</big>\n\n<b>exposit&#275;</b> [expositus]\nясно, чётко (e. atque aperte ostendere <i>AG</i>)."],
  ["expositicius", "<big>expositicius</big>\n\n<b>exposit&#299;cius, a, um</b> [expono]\nподкинутый (puella <i>PM</i>)."],
  ["expositio", "<big>expositio</big>\n\n<b>expositio, &#333;nis</b> <i>f</i> [expono]\n<b>1)</b> подкидывание (infantis <i>Just</i>);\n<b>2)</b> изложение, описание (brevis <i>Q</i>; e. rerum gestarum <i>rhH</i>)."],
  ["expositiuncula", "<big>expositiuncula</big>\n\n<b>expositiuncula, ae</b> <i>f</i>\nкраткое изложение <i>Hier</i>."],
  ["expositor", "<big>expositor</big>\n\n<b>expositor, &#333;ris</b> <i>m</i> [expono]\n(ис)толкователь, комментатор (interpres aut e. <i>Aug</i>)."],
  ["expositus", "<big>expositus</big>\n\n<b>expositus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> expono;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> открытый (urbs <i>L</i>); доступный (limen <i>St</i>; domus <i>C</i>);\n<b>2)</b> общительный (homo <i>PJ</i>); ласковый, дружелюбный (mores <i>St</i>);\n<b>3)</b> понятный, ясный (candidissimus et maxime e., <i>sc.</i> scriptor <i>Q</i>);\n<b>4)</b> обыденный, пошлый <i>J</i>. — <i>См. тж.</i> exposita."],
  ["exposivi", "<big>exposivi</big>\n\n<b>expos&#299;v&#299;</b> <i>арх. Pl pf. к</i>\nexpono."],
  ["expostulatio", "<big>expostulatio</big>\n\n<b>expostul&#257;tio, &#333;nis</b> <i>f</i> [expostulo]\n<b>1)</b> настоятельное требование (alicujus <i>C, T</i>);\n<b>2)</b> жалоба (expostulationem facere <i>C</i>)."],
  ["expostulatus", "<big>expostulatus</big>\n\n<b>expostul&#257;tus, &#363;s</b> <i>m</i> <i>Symm</i>\n= expostulatio 2."],
  ["expostulo", "<big>expostulo</big>\n\n<b>ex–postulo, &#257;v&#299;, &#257;tum, &#257;re</b>\n<b>1)</b> настоятельно просить, требовать (aliquid ab aliquo <i>C</i>; primas sibi partes <i>T</i>); требовать выдачи (aliquem ad supplicium <i>T</i> <i>или</i> ad poenam <i>Su</i>);\n<b>2)</b> требовать ответа <i>или</i> объяснения, заявлять претензию, жаловаться (e. cum aliquo de aliqua re <i>или</i> aliquid <i>и</i> aliquem <i>C etc.</i>): e. injuriam cum aliquo <i>Ter</i> требовать у кого-л. удовлетворения за причинённую обиду."],
  ["expostus", "<big>expostus</big>\n\n<b>expostus, a, um</b> <i>Cato, V</i>\n= expositus."],
  ["exposui", "<big>exposui</big>\n\n<b>exposu&#299;</b> <i>pf. к</i>\nexpono."],
  ["expotus", "<big>expotus</big>\n\n<b>exp&#333;tus</b> <i>Pl</i>\n(= epotus) <i>part. pf. к</i> epoto."],
  ["expresse", "<big>expresse</big>\n\n<b>express&#275;</b> [expressus I]\n<b>1)</b> сильно (fricare artus <i>Scr</i>);\n<b>2)</b> выразительно (dicere <i>PJ</i>); ясно, чётко (litteram efferre <i>VM</i>)."],
  ["expressim", "<big>expressim</big>\n\n<b>expressim</b> <i>Dig</i>\n= expresse 2."],
  ["expressio", "<big>expressio</big>\n\n<b>expressio, &#333;nis</b> <i>f</i> [exprimo]\n<b>1)</b> выдавливание, выжимание (mellis <i>Pall</i>; pomorum <i>Aug</i>);\n<b>2)</b> <i>архит.</i> валик, выступ, кайма, рамка, край <i>Vtr</i>;\n<b>3)</b> нагнетание (aquarum, spiritus <i>Vtr</i>);\n<b>4)</b> <i>грам.</i> изложение, выражение (<i>sc.</i> sensus);\n<b>5)</b> выразительность (summa expressione describi <i>Ambr</i>)."],
  ["expressor", "<big>expressor</big>\n\n<b>expressor, &#333;ris</b> <i>m</i>\nвыразитель (veritatis <i>Tert</i>)."],
  ["expressus 1", "<big>expressus</big>\n\n<b>I expressus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> exprimo;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> выразительный (signa <i>C</i>); ясный, явный, отчётливый (vestigia, indicia <i>C</i>);\n<b>2)</b> резко выделяющийся, выпуклый, мускулистый (corpus lacertis expressum <i>Q</i>);\n<b>3)</b> внятный (verba <i>Q</i>); резко произнесённый, отчеканиваемый: litterae neque expressae, neque oppressae <i>C</i> буквы, произносимые ни слишком отчётливо, ни слишком слабо."],
  ["expressus 2", "<big>expressus</big>\n\n<b>II expressus, &#363;s</b> <i>m</i> [exprimo]\n<b>1)</b> нагнетание, подъём (воды) <i>Vtr</i>;\n<b>2)</b> нагнетательный насос <i>Vtr</i>."],
  ["expretus", "<big>expretus</big>\n\n<b>expr&#275;tus, a, um</b> <i>Pl</i>\n= exspretus."],
  ["exprimo", "<big>exprimo</big>\n\n<b>ex–primo, press&#299;, pressum, ere</b> [premo]\n<b>1)</b> выжимать (spongiam <i>CC</i>); выдавливать (sucum e semine <i>PM</i>; sucos herbarum <i>Pt</i>): e. nasum <i>M</i> сморкаться; e. spiritum <i>T</i> испустить дух, скончаться;\n<b>2)</b> выпирать, делать выпуклым: exercitatio lacertos exprimit <i>Q</i> упражнение делает руки мускулистыми;\n<b>3)</b> вымогать (pecuniam alicui <i>Su</i>); вынуждать (confessionem alicui <i>L</i>): expressi ut conficere se tabulas negaret <i>C</i> я добился у него признания, что записей он не вёл || выманивать (nummos ab aliquo blanditiis <i>C</i>); вызывать (risum alicui <i>Pt, PJ</i>): e. vocem <i>C</i> добиться слова, заставить (кого-л.) говорить;\n<b>4)</b> внятно произносить, ясно выговаривать, отчеканивать (verba, litteras <i>Q</i>);\n<b>5)</b> вытиснуть, изваять, изобразить (in cera imaginem <i>Pl</i>; aliquid in marmore <i>T</i>; simulacra ex auro <i>QC</i>); обрисовывать (vestis exprimit singulos artus <i>T</i>);\n<b>6)</b> изображать, описывать (mores, speciem versibus <i>C</i>); выражать (aliquid Latine <i>PJ</i>): e. non possum, quanto sim gaudio affectus <i>PJ</i> не могу выразить, как я рад;\n<b>7)</b> переводить (verbum e verbo <i>C</i> <i>и</i> de verbo <i>Ter</i>): aliquid ad verbum de Graecis e. <i>C</i> переводить что-л. с греческого слово в слово;\n<b>8)</b> (<i>тж.</i> e. aliquid imitando <i>и</i> imitatione <i>C</i>) копировать, воспроизводить, представлять (alicujus vitam et consuetudinem <i>C</i>; incessum alicujus <i>Sen</i>; vultum sonumque loquendi <i>O</i>);\n<b>9)</b> нагнетать, поднимать вверх (aquam in altum <i>PM</i> <i>и</i> in altitudinem <i>Vtr</i>; aquam machinationibus e mari <i>Hirt</i>); поднимать (turres <i>Cs</i>)."],
  ["exprobrabilis", "<big>exprobrabilis</big>\n\n<b>exprobr&#257;bilis, e</b>\nдостойный порицания <i>Vlg</i>."],
  ["exprobratio", "<big>exprobratio</big>\n\n<b>exprobr&#257;tio, &#333;nis</b> <i>f</i> [exprobro]\nупрёки, укор, укоризна (alicujus rei <i>Sen, Q, Su</i>): e. alicui veteris fortunae <i>L</i> попрёки по поводу чьего-л. прошлого."],
  ["exprobrator", "<big>exprobrator</big>\n\n<b>exprobr&#257;tor, &#333;ris</b> <i>m</i> [exprobro]\nделающий упрёки, укоряющий <i>Sen</i>."],
  ["exprobratrix", "<big>exprobratrix</big>\n\n<b>exprobr&#257;tr&#299;x, &#299;cis</b> <i>f</i>\nпорицательница, осыпающая укорами (memoria <i>Sen</i>)."],
  ["exprobro", "<big>exprobro</big>\n\n<b>ex–probro, &#257;v&#299;, &#257;tum, &#257;re</b> [probrum]\nукорять, упрекать (alicui aliquid <i>L, QC, O, Su</i> <i>или</i> de aliqua re <i>Nep</i>); попрекать, тоном укоризны рассказывать (sua merita cicatricesque acceptas <i>L</i>): non e. hostibus domitis victoriam suam <i>Fl</i> не колоть поверженному врагу глаза своей победой."],
  ["expromissor", "<big>expromissor</big>\n\n<b>expr&#333;missor, &#333;ris</b> <i>m</i> [expromitto]\nпоручитель, гарант <i>Dig</i>."],
  ["expromitto", "<big>expromitto</big>\n\n<b>ex–pr&#333;mitto, m&#299;s&#299;, missum, ere</b>\n<b>1)</b> обещать, обязываться уплатить (nummos <i>Vr</i>; pecuniam alicui <i>Dig</i>);\n<b>2)</b> выступать поручителем <i>Dig</i>."],
  ["expromo", "<big>expromo</big>\n\n<b>ex–pr&#333;mo, pr&#333;mps&#299;, pr&#333;mptum, ere</b>\n<b>1)</b> вынимать (cibos <i>PM</i>); выкладывать (aliquid <i>L</i>);\n<b>2)</b> выливать (vinum in urceum <i>Pl</i>);\n<b>3)</b> издавать (voces <i>V</i>);\n<b>4)</b> изливать (multas querelas <i>Ctl</i>); обрушивать (crudelitatem suam in aliquo <i>C</i>); обнаруживать, выказывать, проявлять (odium <i>C</i>; vim eloquentiae <i>C</i>);\n<b>5)</b> высказывать, выражать (sententiam <i>T</i>); открывать (omnia sua occulta <i>Ter</i>); излагать (causas belli <i>L</i>);\n<b>6)</b> приводить, цитировать (leges de aliqua re <i>C</i>);\n<b>7)</b> заявлять: expromit repertum in agro suo specum <i>T</i> (Цезеллий Басс) заявил, что на его участке обнаружена пещера."],
  ["expromptus", "<big>expromptus</big>\n\n<b>expr&#333;mptus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> expromo;\n<b>2.</b> <i>adj.</i> находящийся в готовности, имеющийся под рукой (memoria atque astutia <i>Ter</i>)."],
  ["expudoratus", "<big>expudoratus</big>\n\n<b>ex–pud&#333;r&#257;tus, a, um</b> [pudor]\nлишённый стыда, бесстыдный, бесстыжий (frons <i>Pt</i>)."],
  ["expugnabilis", "<big>expugnabilis</big>\n\n<b>expugn&#257;bilis, e</b> [expugno]\nмогущий быть взятым, завоёванным (urbs <i>L</i>): non e. непреодолимый, непобедимый (arx ne magnis quidem exercitibus e. <i>T</i>): situ non expugnabile robur <i>St</i> дуб, не подверженный разрушению; amnis nulli e. astro <i>St</i> река, которую никакое солнце не в состоянии иссушить."],
  ["expugnans", "<big>expugnans</big>\n\n<b>expugn&#257;ns, antis</b>\n<b>1.</b> <i>part. praes. к</i> expugno;\n<b>2.</b> <i>adj.</i> сильный, радикально действующий (herba <i>O</i>)."],
  ["expugnatio", "<big>expugnatio</big>\n\n<b>expugn&#257;tio, &#333;nis</b> <i>f</i> [expugno]\nзавоевание, взятие приступом, завладение, захват (urbis <i>Cs, C etc.</i>): nunc acie, nunc expugnationibus <i>VP</i> то в открытых боях, то путём штурма."],
  ["expugnator", "<big>expugnator</big>\n\n<b>expugn&#257;tor, &#333;ris</b> <i>m</i> [expugno]\nзавоеватель, победитель, покоритель (urbis <i>C</i>); e. pudicitiae <i>C</i> насильник, растлитель."],
  ["expugnatorius", "<big>expugnatorius</big>\n\n<b>expugn&#257;t&#333;rius, a, um</b>\nзавоевательный, достигаемый завоеванием (dominatio <i>Tert</i>)."],
  ["expugnax", "<big>expugnax</big>\n\n<b>expugn&#257;x, &#257;cis</b> <i>O v. l.</i>\n= expugnans."],
  ["expugno", "<big>expugno</big>\n\n<b>ex–pugno, &#257;v&#299;, &#257;tum, &#257;re</b>\n<b>1)</b> завоёвывать, брать с бою, захватывать, брать приступом (urbem, castellum <i>C</i>); завладевать (urbem obsidione, navem <i>C</i>); одолевать, побеждать, покорять (Messenios per insidias <i>Just</i>): fames expugnavit obsessos <i>L</i> голод заставил осаждённых сдаться;\n<b>2)</b> подавлять, обезвреживать (venena aliqua re <i>PM</i>);\n<b>3)</b> нападать, атаковать (intentionem aut assumptionem <i>Q</i>): hostem circuit expugnans <i>St</i> (Алкидамант) со всех сторон атакует врага;\n<b>4)</b> доводить до конца, довершать (coepta <i>O</i>);\n<b>5)</b> пробивать(ся): Euphrates Taurum montem expugnat <i>PM</i> Евфрат пробивается (прокладывает себе путь) через Таврские горы;\n<b>6)</b> преодолевать: e. pertinaciam alicujus <i>L</i> побороть чьё-л. упорство;\n<b>7)</b> склонять (expugnatus precibus <i>Su</i>);\n<b>8)</b> нарушать, насиловать (pudicitiam <i>C</i>);\n<b>9)</b> вымогать, вынуждать (aurum ab aliquo <i>Pl</i>): legationem sibi e. <i>C</i> добиться для себя должности легата."],
  ["expuitio", "<big>expuitio</big>\n\n<b>expuitio, &#333;nis</b> <i>f</i> <i>v. l.</i>\n= exspuitio."],
  ["expuli", "<big>expuli</big>\n\n<b>expul&#299;</b> <i>pf. к</i>\nexpello."],
  ["expullulo", "<big>expullulo</big>\n\n<b>expullulo, —, —, &#257;re</b>\nдавать много отпрысков <i>Is</i>."],
  ["expulsim", "<big>expulsim</big>\n\n<b>expulsim</b> [expulsus]\nотбрасывая, отбивая (pila ludere <i>Vr</i>)."],
  ["expulsio", "<big>expulsio</big>\n\n<b>expulsio, &#333;nis</b> <i>f</i> [expello]\nизгнание (alicujus <i>C, L etc.</i>)."],
  ["expulso", "<big>expulso</big>\n\n<b>expulso, (&#257;v&#299;), &#257;tum, &#257;re</b> [<i>intens. к</i> expello]\n<b>1)</b> отбивать (pilam sinistris <i>M</i>);\n<b>2)</b> прогонять, выгонять, сгонять: per aliquem expulsari <i>Amm</i> быть изгнанным, <i>т. е.</i> побеждённым кем-л."],
  ["expulsor", "<big>expulsor</big>\n\n<b>expulsor, &#333;ris</b> <i>m</i> [expello]\nизгоняющий, изгнавший (tyranni <i>Nep</i>): bonorum e. <i>C</i> изгнавший (Квинкция) из его поместий."],
  ["expulsus", "<big>expulsus</big>\n\n<b>expulsus, a, um</b> <i>part. pf. к</i>\nexpello."],
  ["expultrix", "<big>expultrix</big>\n\n<b>expultr&#299;x, &#299;cis</b> <i>f</i> [expulsor]\nгонительница, изгоняющая (philosophia e. vitiorum <i>C</i>)."],
  ["expumico", "<big>expumico</big>\n\n<b>ex–p&#363;mico, —, —, &#257;re</b>\nочищать, освобождать (ab omnibus injuriis passionis <i>Tert</i>)."],
  ["expumo", "<big>expumo</big>\n\n<b>exp&#363;mo</b> <i>v. l.</i>\n= exspumo."],
  ["expunctio", "<big>expunctio</big>\n\n<b>exp&#363;nctio, &#333;nis</b> <i>f</i> [expungo]\nсовершение, исполнение (operae <i>Tert</i>)."],
  ["expunctor", "<big>expunctor</big>\n\n<b>exp&#363;nctor, &#333;ris</b> <i>m</i> [expungo]\nразрушитель (totius vetustatis <i>Tert</i>)."],
  ["expunctrix", "<big>expunctrix</big>\n\n<b>exp&#363;nctr&#299;x, &#299;cis</b> <i>f</i> [expunctor]\nразрушительница (honestatis <i>Julianus ap. Aug</i>)."],
  ["expungo", "<big>expungo</big>\n\n<b>ex–pungo, p&#363;nx&#299;, p&#363;nctum, ere</b>\n<b>1)</b> вычёркивать (nomen <i>Pl</i>);\n<b>2)</b> увольнять, распускать, расформировывать (manipulum <i>Pl</i>);\n<b>3)</b> удалять, устранять (aliquem <i>Pers</i>);\n<b>4)</b> просматривать, ревизовать (decurias <i>Su</i>; rationes <i>Dig</i>);\n<b>5)</b> погашать, возмещать (munus munere <i>Sen</i>);\n<b>6)</b> перечислять (sidera ad nomen <i>PM</i>);\n<b>7)</b> отмечать, переписывать (milites laureatos <i>Tert</i>);\n<b>8)</b> выполнять, осуществлять (vota Caesarum <i>Tert</i>); совершать (adventum suum <i>Tert</i>)."],
  ["expuo", "<big>expuo</big>\n\n<b>expuo</b> <i>v. l.</i>\n= exspuo."],
  ["expurgatio", "<big>expurgatio</big>\n\n<b>exp&#363;rg&#257;tio, &#333;nis</b> <i>f</i> [expurgo]\nоправдание, извинение: expurgationem habere <i>Pl</i> иметь (наготове) оправдание."],
  ["expurgo", "<big>expurgo</big>\n\n<b>ex–p&#363;rgo, &#257;v&#299;, &#257;tum, &#257;re</b>\n<b>1)</b> чистить, прочищать, очищать (sordida ulcera <i>PM</i>); удалять (quidquid emortuum est <i>Col</i>); освобождать от засорённости, выправлять (sermonem <i>C</i>);\n<b>2)</b> исцелять, излечивать (lepras <i>PM</i>);\n<b>3)</b> оправдывать (consilium <i>AG</i>): sui expurgandi causa <i>Sl</i> чтобы оправдать себя; e. se alicui <i>Pl</i> извиниться перед кем-л."],
  ["expurig-", "<big>expurig-</big>\n\n<b>exp&#363;rig-</b> <i>Pl</i>\n= expurg-."],
  ["exputesco", "<big>exputesco</big>\n\n<b>ex–p&#363;t&#275;sco, —, —, ere</b>\nгнить, загнивать, портиться <i>Pl</i>."],
  ["exputo 1", "<big>exputo</big>\n\n<b>I ex–puto, &#257;v&#299;, &#257;tum, &#257;re</b>\nподчищать, подрезать (veteranam vitem <i>Col</i>)."],
  ["exputo 2", "<big>exputo</big>\n\n<b>II ex–puto, &#257;v&#299;, &#257;tum, &#257;re</b>\nрассматривать, разбирать (aliquam rem <i>Pl</i>); постигать, понимать (e. non posse <i>Plancius ap. C</i>)."],
  ["exquaero", "<big>exquaero</big>\n\n<b>exquaero</b> <i>арх.</i> <i>Pl</i>\n= exquiro."],
  ["exquiro", "<big>exquiro</big>\n\n<b>ex–qu&#299;ro, s&#299;v&#299;, s&#299;tum, ere</b> [quaero]\n<b>1)</b> выискивать, разыскивать, высматривать (aliquem <i>и</i> aliquid <i>Pl, C etc.</i>); обыскивать, обшаривать (terra marique omnia <i>Sl</i>); стараться узнать, разузнавать, выведывать, разведывать (sententias <i>Cs</i>; iter per aliquem <i>Cs</i>; aliquid ab <i>или</i> ex aliquo <i>L, C</i>); выпытывать, допрашивать (quolibet cruciatu <i>Ter</i>); исследовать (veritatem, causas <i>C</i>); (вы)спрашивать (alicujus sententiam <i>Sl</i>; causas, facta alicujus <i>C</i>): exquire quidvis <i>Pl</i> спрашивай о чём хочешь;\n<b>2)</b> придумывать, выбирать, избирать (ex multis amicis unum, qui certus sit <i>Pl</i>); подбирать (verba ad sonum <i>C</i>);\n<b>3)</b> выпрашивать, испрашивать (alicujus consilium <i>C</i>): e. pacem per aras <i>V</i> молиться о мире у алтарей;\n<b>4)</b> требовать, ожидать (aliquid a Graecis <i>C</i>)."],
  ["exquisite", "<big>exquisite</big>\n\n<b>exqu&#299;s&#299;te</b> [exquisitus]\n<b>1)</b> точно, тщательно, аккуратно, основательно (accurate et e. disputare <i>C</i>);\n<b>2)</b> изысканно, отлично (eleganter atque e. dicere aliquid <i>Q</i>)."],
  ["exquisitim", "<big>exquisitim</big>\n\n<b>exqu&#299;s&#299;tim</b> [exquiro]\nс тщательным выбором (medicos convocare <i>Vr</i>)."],
  ["exquisitio", "<big>exquisitio</big>\n\n<b>exqu&#299;s&#299;tio, &#333;nis</b> <i>f</i> [exquiro]\n<b>1)</b> выдумывание (Idolorum <i>Vlg</i>);\n<b>2)</b> исследование, рассмотрение <i>CJ</i>."],
  ["exquisitor", "<big>exquisitor</big>\n\n<b>exqu&#299;s&#299;tor, &#333;ris</b> <i>m</i>\nискатель, исследователь (prudentiae et intellegentiae <i>Vlg</i>)."],
  ["exquisitus", "<big>exquisitus</big>\n\n<b>exqu&#299;s&#299;tus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> exquiro;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> тщательный, изысканный, отборный, превосходный (dicendi genus <i>C</i>; epulae <i>PM</i>);\n<b>2)</b> утончённый (supplicia <i>C</i>);\n<b>3)</b> мелочный, педантичный, преувеличенный (munditia <i>C</i>); натянутый, надуманный, неестественный (verba <i>Q</i>)."],
  ["exradicatus", "<big>exradicatus</big>\n\n<b>exradicatus</b> (= eradicatus) <i>part. pf. к</i>\neradico."],
  ["exradicitus", "<big>exradicitus</big>\n\n<b>ex–radicitus</b> <i>adv.</i> [radix]\nсо всеми корнями, самым коренным образом (non radicitus, verum etiam e. <i>Pl</i>)."],
  ["exsacrifico", "<big>exsacrifico</big>\n\n<b>ex–sacrifico, —, —, &#257;re</b>\nприносить жертву, совершать жертвоприношение <i>Poeta ap. C</i>."],
  ["exsaevio", "<big>exsaevio</big>\n\n<b>ex–saevio, —, —, &#299;re</b>\nотшуметь, утихнуть: dum reliquum tempestatis exsaeviret <i>L</i> (Клавдий подождал), пока буря окончательно не утихла."],
  ["exsanguinatus", "<big>exsanguinatus</big>\n\n<b>exsanguin&#257;tus, a, um</b>\nобескровленный (animalia <i>Vtr</i>)."],
  ["exsanguis", "<big>exsanguis</big>\n\n<b>ex–sanguis, e</b>\n<b>1)</b> бескровный (umbrae <i>V, O</i>; corpora <i>O, C</i>); побелевший, бледный (vultus <i>Sil</i>; e. metu <i>O</i>; corpus mortui <i>C</i>); белый (charta <i>O</i>); бесцветный (herba <i>O</i>); истощённый, изнурённый (multis vulneribus <i>QC</i>); худосочный, тщедушный (corpus <i>Acc</i>); вялый, сухой, безжизненный (vox <i>AG</i>; oratio <i>rhH</i>);\n<b>2)</b> неспособный краснеть, бесстыдный (frons <i>Pt</i>);\n<b>3)</b> делающий бледным, причиняющий бледность (cuminum <i>H</i>; horror <i>Cld</i>)."],
  ["exsanio", "<big>exsanio</big>\n\n<b>ex–sanio, —, —, &#257;re</b> [sanies]\n<b>1)</b> удалять (извлекать) гной, очищать от гноя (vulnus <i>CC</i>);\n<b>2)</b> отжимать, освобождать от соков (carnem salitam <i>Col</i>): e. pressam bacam <i>Col</i> под давлением выжимать сок из ягоды;\n<b>3)</b> удалять, устранить (amaritudinem et pallorem <i>Col</i>)."],
  ["exsarcio", "<big>exsarcio</big>\n\n<b>ex–sarcio (exsercio), —, sart&#363;rus, &#299;re</b>\nчинить, исправлять, <i>перен.</i> заменять, возвращать, вознаграждать (aliquid aliis rebus <i>QC</i>); возмещать, покрывать: sumptum suum e. <i>Ter</i> покрывать расходы по своему содержанию."],
  ["exsatio", "<big>exsatio</big>\n\n<b>ex–satio, &#257;v&#299;, &#257;tum, &#257;re</b>\n<b>1)</b> насыщать, (на)кормить, (на)поить досыта (vino ciboque <i>L</i>; <i>перен.</i> enses multo cruore <i>Sil</i>);\n<b>2)</b> вполне удовлетворять, утолять (aliquem aliqua re <i>L, O</i>)."],
  ["exsaturabilis", "<big>exsaturabilis</big>\n\n<b>exsatur&#257;bilis, e</b> [exsaturo]\nмогущий насытиться: non (nec) e. ненасытный, неутолимый (pectus Junonis <i>V</i>)."],
  ["exsaturo", "<big>exsaturo</big>\n\n<b>ex–saturo, &#257;v&#299;, &#257;tum, &#257;re</b>\n<b>1)</b> насыщать, (на)кормить досыта (belua exsaturanda visceribus, <i>sc.</i> Cephei <i>O</i>);\n<b>2)</b> удовлетворять, утолять (libidinem <i>Poeta ap. C</i>; oculos animumque aliqua re <i>C</i>)."],
  ["exscalpo", "<big>exscalpo</big>\n\n<b>ex–scalpo, —, —, ere</b>\nвысекать, вырубать, вырезать (imagines <i>Vtr</i>)."],
  ["exscendo", "<big>exscendo</big>\n\n<b>exscendo</b> <i>v. l.</i>\n= escendo."],
  ["exscens-", "<big>exscens-</big>\n\n<b>exsc&#275;ns-</b> <i>v. l.</i>\n= escens-."],
  ["exscid-", "<big>exscid-</big>\n\n<b>exscid-</b> <i>v. l.</i>\n= excid-."],
  ["exscindo", "<big>exscindo</big>\n\n<b>ex–scindo (excindo), scid&#299;, scissum, ere</b>\nистреблять, уничтожать (sceleratam gentem ferro <i>V</i>; hostem <i>T</i>)ц разрушать (urbem <i>C, L</i>)."],
  ["exscissatus", "<big>exscissatus</big>\n\n<b>exsciss&#257;tus, a, um</b> [exscindo]\nразрезанный, распоротый (aures <i>Pl</i>)."],
  ["exscreabilis", "<big>exscreabilis</big>\n\n<b>exscre&#257;bilis, e</b> [exscreo]\nотхаркиваемый (vitia pulmonum <i>PM</i>)."],
  ["exscreatio", "<big>exscreatio</big>\n\n<b>exscre&#257;tio, &#333;nis</b> <i>f</i> [exscreo]\nотхаркивание: e. sanguinis <i>и</i> e. cruenta <i>PM</i> кровохаркание."],
  ["exscreatus", "<big>exscreatus</big>\n\n<b>exscre&#257;tus, &#363;s</b> <i>m</i> <i>CA</i>\n= exscreatio."],
  ["exscreo", "<big>exscreo</big>\n\n<b>ex–screo, &#257;v&#299;, &#257;tum, &#257;re</b>\n<b>1)</b> откашливаться <i>Q, O, Su</i>;\n<b>2)</b> отхаркивать (pituitam per tussim <i>CC</i>; pura <i>PM</i>)."],
  ["exscribo", "<big>exscribo</big>\n\n<b>ex–scr&#299;bo, scr&#299;ps&#299;, scr&#299;ptum, ere</b>\n<b>1)</b> выписывать (versus ex comoedia <i>AG</i>);\n<b>2)</b> списывать, переписывать (tabulas <i>C</i>); срисовывать, копировать (imagines <i>PJ</i>);\n<b>3)</b> походить, быть сходным (e. aliquem mira similitudine <i>PJ</i>);\n<b>4)</b> записывать, помечать (nomina <i>Pl</i>)."],
  ["exsculpo", "<big>exsculpo</big>\n\n<b>ex–sculpo, sculps&#299;, sculptum, ere</b>\n<b>1)</b> вырезывать, высекать, вырубать (signum ex lapide <i>Q</i>; simulacrum e quercu <i>C</i>): non ex omni ligno debet Mercurius exsculpi <i>погов. Pythagoras ap. Ap</i> не из всякого дерева следует вырезывать (изображения) Меркурия;\n<b>2)</b> выскабливать (versus <i>Nep</i>);\n<b>3)</b> вырывать (praedam leoni ex ore <i>LM</i>);\n<b>4)</b> добиваться, выжимать, вынуждать (verum ex aliquo <i>Ter</i>)."],
  ["exsculptio", "<big>exsculptio</big>\n\n<b>exsculptio, &#333;nis</b> <i>f</i>\nвысекание, вырубание <i>или</i> лепка <i>Aug</i>."],
  ["exsculptrix", "<big>exsculptrix</big>\n\n<b>exsculptr&#299;x, &#299;cis</b> <i>f</i>\nваятельница <i>Ap</i> (<i>v. l. к</i> expultrix)."],
  ["exseco", "<big>exseco</big>\n\n<b>ex–seco (exsico, execo, exico), secu&#299;, sectum, &#257;re</b>\n<b>1)</b> вырезывать, отрезывать, срезывать (arundinem <i>Pl</i>; linguam <i>Ctl, C</i>); отсекать (vitiosas partes <i>C</i>); спиливать (arborem serra <i>Col</i>): e. pelles <i>O</i> разрезать кожи (<i>на ремни</i>);\n<b>2)</b> кастрировать (aliquem <i>C, M</i>): exsecti <i>bAl</i> скопцы;\n<b>3)</b> сдирать: corio exsecto <i>Ap</i> после того как (с Марсия) была содрана кожа; e. quinas capiti mercedes <i>H</i> выколачивать (извлекать) из капитала по 5% в месяц."],
  ["exsecrabilis", "<big>exsecrabilis</big>\n\n<b>exsecr&#257;bilis, e</b> [exsecror]\n<b>1)</b> достойный проклятия, проклятый, гнусный (nomen <i>L</i>; superbia <i>Eutr</i>);\n<b>2)</b> проклинающий, содержащий заклятие (carmen <i>L</i>);\n<b>3)</b> смертельный, страшный, ужасный (odium, ira <i>L</i>)."],
  ["exsecrabilitas", "<big>exsecrabilitas</big>\n\n<b>exsecr&#257;bilit&#257;s, &#257;tis</b> <i>f</i> [exsecrabilis]\nгнусность, омерзительность <i>Ap</i>."],
  ["exsecrabiliter", "<big>exsecrabiliter</big>\n\n<b>exsecr&#257;biliter</b> [exsecrabilis]\nгнусно, мерзко, ужасно <i>Ap, Eccl</i>."],
  ["exsecramentum", "<big>exsecramentum</big>\n\n<b>exsecr&#257;mentum, &#299;</b> <i>n</i> <i>Eccl</i>\n= exsecratio 1."],
  ["exsecrandus", "<big>exsecrandus</big>\n\n<b>exsecrandus, a, um</b> [exsecror]\nгнусный, достойный проклятия, проклятый <i>Eutr, Vlg, Eccl</i>."],
  ["exsecratio", "<big>exsecratio</big>\n\n<b>exsecr&#257;tio, &#333;nis</b> <i>f</i> [exsecror]\n<b>1)</b> произнесение проклятия, заклятие <i>C, T</i>;\n<b>2)</b> торжественное заверение, клятва (<i>соединённая с проклятием тому, кто нарушит её</i>) <i>C, Sl, L</i>."],
  ["exsecrator", "<big>exsecrator</big>\n\n<b>exsecr&#257;tor, &#333;ris</b> <i>m</i>\nпроклинающий <i>Tert, Aug</i>."],
  ["exsecratus", "<big>exsecratus</big>\n\n<b>exsecr&#257;tus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> (<i>тж.</i> <i>pass. Cato</i>) exsecror;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> проклятый, гнусный, ужасный <i>C, PM</i>: e. alicui <i>C</i> смертельно ненавидимый кем-л.;\n<b>2)</b> сопряжённый с проклятиями (auguria <i>PM</i>)."],
  ["exsecror", "<big>exsecror</big>\n\n<b>ex–secror, &#257;tus sum, &#257;r&#299;</b> <i>depon.</i> [sacer]\n<b>1)</b> проклинать, изрекать проклятия, предавать проклятию (aliquem <i>C</i> <i>и</i> in aliquem <i>L</i>; consilia alicujus <i>Sl</i>; insidias alicujus <i>Pt</i>; e. in caput alicujus <i>L</i>); exsecrantia verba <i>O</i> проклятия;\n<b>2)</b> торжественно клясться (предавая проклятию того, кто нарушит клятву): eamus omnis exsecrata civitas <i>H</i> уйдём все, (о римляне), торжественно поклявшись друг другу (не возвращаться)."],
  ["exsectio", "<big>exsectio</big>\n\n<b>exsectio, &#333;nis</b> <i>f</i> [exseco]\n<b>1)</b> вырезывание, отсечение (linguae <i>C</i>);\n<b>2)</b> взлом (fundi in armario <i>C</i>)."],
  ["exsector", "<big>exsector</big>\n\n<b>exsector, &#333;ris</b> <i>m</i> [exseco]\nпроизводящий кастрацию, оскопитель <i>Ap</i>."],
  ["exsectus", "<big>exsectus</big>\n\n<b>exsectus, a, um</b> <i>part. pf. к</i>\nexseco."],
  ["exsecutio", "<big>exsecutio</big>\n\n<b>exsec&#363;tio, &#333;nis</b> <i>f</i> [exsequor]\n<b>1)</b> выполнение, осуществление (negotii <i>T</i>; instituti operis <i>PM</i>);\n<b>2)</b> исполнительная власть, администрация, управление (Syriae <i>T</i>);\n<b>3)</b> обсуждение, рассмотрение (desiderat hoc longam exsecutionem <i>Sen</i>);\n<b>4)</b> приведение в исполнение (sententiae <i>PJ</i>; poenarum <i>Dig</i>);\n<b>5)</b> судебное преследование (delictorum quorundam <i>Dig</i>)."],
  ["exsecutor", "<big>exsecutor</big>\n\n<b>exsec&#363;tor, &#333;ris</b> <i>m</i> [exsequor]\n<b>1)</b> исполнитель (sententiae <i>Ap, Dig</i>; rerum judicatarum <i>CJ</i>);\n<b>2)</b> свершитель (malorum propositorum <i>VP</i>);\n<b>3)</b> каратель, мститель (offensarum <i>Su</i>)."],
  ["exsecutorius", "<big>exsecutorius</big>\n\n<b>exsec&#363;t&#333;rius, a, um</b>\nисполнительный (potentia <i>Aug</i>)."],
  ["exsensus", "<big>exsensus</big>\n\n<b>ex–s&#275;nsus, a, um</b>\nбессмысленный, безрассудный (mens <i>Laevius ap. AG</i>)."],
  ["exsequens", "<big>exsequens</big>\n\n<b>exsequ&#275;ns, entis</b>\n<b>1.</b> <i>part. praes. к</i> exsequor;\n<b>2.</b> <i>adj.</i> ревностно исследующий, занятый изысканием (memoriarum veterum exsequentissimus <i>AG</i>)."],
  ["exsequiae", "<big>exsequiae</big>\n\n<b>exsequiae, &#257;rum</b> <i>f</i> [exsequor]\n<b>1)</b> похороны, похоронная процессия, <i>тж.</i> погребение: prosequi exsequiae alicujus funeris <i>C</i> <i>или</i> exsequiae ire <i>Ter, Ap</i> провожать чьё-л. тело; justa exsequiarum <i>C</i> похоронные обряды; exsequias dare <i>O</i> совершать (справлять) похоронный обряд;\n<b>2)</b> останки, прах (sepelire exsequias alicujus <i>Eutr</i>)."],
  ["exsequialia", "<big>exsequialia</big>\n\n<b>exsequi&#257;lia, &#333;rum</b> <i>n</i>\nпохоронные торжества <i>St</i>."],
  ["exsequialis", "<big>exsequialis</big>\n\n<b>exsequi&#257;lis, e</b> [exsequiae]\nпохоронный, погребальный (carmen <i>O</i>; honor <i>St</i>)."],
  ["exsequior", "<big>exsequior</big>\n\n<b>exsequior, &#257;tus sum, &#257;r&#299;</b> <i>depon.</i> [exsequiae] (<i>тж.</i> e. funus <i>Vr</i>)\nхоронить, погребать (aliquem <i>Vr</i>)."],
  ["exsequor", "<big>exsequor</big>\n\n<b>ex–sequor, c&#363;tus (qu&#363;tus) sum, sequ&#299;</b> <i>depon.</i>\n<b>1)</b> идти за гробом, участвовать в похоронах, провожать на кладбище (e. uxorem <i>Pl</i>; funus <i>Sabinus ap. AG</i>);\n<b>2)</b> следовать (suam spem, sua consilia <i>L</i>);\n<b>3)</b> быть приверженцем, принадлежать (e. sectam alicujus <i>Ctl</i>);\n<b>4)</b> преследовать (aliquem ferro <i>L</i>);\n<b>5)</b> стремиться, добиваться (aeternitatem <i>C</i>); томиться: aspectum alicujus e. <i>Pl</i> страстно желать увидеть кого-л.;\n<b>6)</b> подвергаться, испытывать, терпеть (aerumnam, egestatem <i>Pl</i>); e. fatum alicujus <i>C</i> разделить чью-л. судьбу;\n<b>7)</b> осуществлять (imperium <i>Ter, Sil</i>); выполнять (officia, munera <i>C</i>; mandata <i>C, QC</i>; jussa divum = deorum <i>V</i>): exsequere mihi hoc unum <i>V</i> исполни одну лишь эту мою просьбу || приводить в исполнение (negotia <i>C</i>); совершать (iter <i>Pl</i>; scelus <i>QC</i>; caedem <i>T</i>); вести (obsidiones <i>T</i>): sermonem e. cum aliquo <i>Pl</i> вести (вступать) с кем-л. (в) разговор;\n<b>8)</b> отстаивать, поддерживать (jus suum armis <i>Cs</i>);\n<b>9)</b> мстить, карать, наказывать (violata jura <i>L</i>; delicta <i>Su</i>; injurias <i>PJ</i>): e. mortem <i>Pl, T</i> покончить с собой;\n<b>10)</b> излагать, передавать (в словах), рассказывать, описывать (aliquid verbis <i>C</i>): ne exsequar singula <i>Su</i> чтобы не вдаваться мне в отдельные подробности || называть, приводить (nomen, numerum <i>L</i>);\n<b>11)</b> выискивать, разузнавать, расследовать; выяснять, устанавливать (aliquid cogitando, quaerendo, sciscitando <i>L</i>);\n<b>12)</b> продолжать <i>или</i> довершать (incepta <i>L</i>); доводить (aliquid usque ad extremum <i>C</i>)."],
  ["exsequut-", "<big>exsequut-</big>\n\n<b>exsequ&#363;t-</b> <i>v. l.</i>\n= exsecut-."],
  ["exsercio", "<big>exsercio</big>\n\n<b>exsercio</b> <i>v. l.</i>\n= exsarcio."],
  ["exsero", "<big>exsero</big>\n\n<b>ex–sero (exero), seru&#299;, sertum, ere</b>\n<b>1)</b> вынимать, вырывать (radicem <i>Col</i>); рвать, разрывать (vincula <i>Col</i>);\n<b>2)</b> высовывать (linguam per os <i>PM</i>); протягивать (ad osculum caput <i>Pt</i>); простирать (brachium <i>Q</i> — <i>ср.</i> 3; manum ad mentum <i>L</i>); e. se выползать (cochleae exserentes se domicilio <i>PM</i>); digitum e. <i>погов. Pers</i> пошевельнуть пальцем, <i>т. е.</i> сделать самое незначительное дело;\n<b>3)</b> обнажать (brachia <i>O</i> — <i>ср.</i> 2; umeros <i>Cs</i>; mammam <i>V</i>);\n<b>4)</b> выказывать, обнаруживать (secreta mentis <i>SenT</i>): exsertus cachinnus — <i>см.</i> exsertus; haec exserit oratio <i>Ph</i> вот смысл рассказа; e. jus in aliquid <i>PJ</i> осуществлять своё право в отношении чего-л.;\n<b>5)</b> освобождать: se e. выпутываться, освобождаться (aere alieno <i>C</i>)."],
  ["exserte", "<big>exserte</big>\n\n<b>exsert&#275;</b> [exsertus]\nсильно, мужественно, энергично (clamitare <i>Ap</i>; aliquid defendere <i>Tert</i>)."],
  ["exserto", "<big>exserto</big>\n\n<b>exserto (exerto), —, —, &#257;re</b> [<i>intens. к</i> exsero]\n<b>1)</b> высовывать (linguam <i>Q. Claudius ap. AG</i>);\n<b>2)</b> вытягивать, протягивать (ora <i>V</i>; gladios <i>Amm</i>);\n<b>3)</b> обнажать (umeros <i>St</i>)."],
  ["exsertus", "<big>exsertus</big>\n\n<b>exsertus (exertus), a, um</b>\n<b>1.</b> <i>part. pf. к</i> exsero;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> выдающийся вперёд, выступающий (dentes, <i>sc.</i> apri <i>PM</i>);\n<b>2)</b> открытый, неприкрытый, явный (bellum <i>St</i>);\n<b>3)</b> несдержанный, громкий (cachinnus <i>Ap</i>)."],
  ["exsibilo", "<big>exsibilo</big>\n\n<b>ex–s&#299;bilo, &#257;v&#299;, &#257;tum, &#257;re</b>\n<b>1)</b> освистать, ошикать (histrionem <i>C</i>);\n<b>2)</b> свистеть, высвистывать (dirum quiddam <i>Sen</i>); произносить с присвистом (nescio quid taetrum <i>Pt</i>)."],
  ["exsiccatio", "<big>exsiccatio</big>\n\n<b>exsicc&#257;tio, &#333;nis</b> <i>f</i>\nвысыхание <i>Ambr</i>."],
  ["exsiccatus", "<big>exsiccatus</big>\n\n<b>exsicc&#257;tus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> exsicco;\n<b>2.</b> <i>adj.</i> сухой (genus orationis <i>C</i>)."],
  ["exsiccesco", "<big>exsiccesco</big>\n\n<b>ex–sicc&#275;sco (exicc&#275;sco), —, —, ere</b>\nсохнуть, высыхать <i>Vtr</i>."],
  ["exsicco", "<big>exsicco</big>\n\n<b>ex–sicco (exicco), &#257;v&#299;, &#257;tum, &#257;re</b>\n<b>1)</b> сушить, высушивать (sulcos <i>PM</i>; arbores exsiccatae <i>C</i>);\n<b>2)</b> осушать, выпивать (amphoram <i>Sen</i>);\n<b>3)</b> <i>pass.</i> exsiccari испаряться, проходить: ebrietas exsiccatur <i>Sen</i> хмель (опьянение) проходит."],
  ["exsico", "<big>exsico</big>\n\n<b>exsico</b> <i>v. l.</i>\n= exseco."],
  ["exsigno", "<big>exsigno</big>\n\n<b>ex–s&#299;gno, &#257;v&#299;, &#257;tum, &#257;re</b>\nпереписывать, записывать, составлять опись, отмечать (aliquid <i>Pl, L</i>)."],
  ["exsilio", "<big>exsilio</big>\n\n<b>ex–silio (exilio), silu&#299;</b> (<i>редко</i> <b>sili&#299;</b>), (<b>sultum</b>), <b>&#299;re</b> [salio]\n<b>1)</b> выскакивать, выпрыгивать (e mari <i>Su</i>; foras <i>Pl</i>);\n<b>2)</b> внезапно возникать, появляться (subitum nemus exsiluit <i>Sil</i>; in forum e. <i>Ap</i>);\n<b>3)</b> подскакивать (ad aliquem <i>Ter</i>; e. gaudio <i>C</i>); вскакивать (de sella <i>C</i> <i>и</i> ex sella <i>QC</i>; cathedra <i>PJ</i>); соскакивать (stratis <i>O</i>);\n<b>4)</b> взвиваться, взлетать (arundo in caelum exsilit <i>SenT</i>);\n<b>5)</b> быстро подниматься, устремляться вверх (exilit — <i>v. l. к</i> exiit — ad caelum arbos <i>V</i>): Cicero, a quo Romana eloquentia exsiluit <i>Sen</i> Цицерон, с которого начался быстрый подъём римского ораторского искусства;\n<b>6)</b> вставать дыбом (exsiliunt crines <i>St</i>);\n<b>7)</b> колотиться, стучать (cor exsilit <i>Sen</i>)."],
  ["exsilium", "<big>exsilium</big>\n\n<b>exsilium (exilium), &#299;</b> <i>n</i> [exsul]\n<b>1)</b> удаление из отечества, изгнание, ссылка: in e. ire <i>и</i> pergere <i>C</i> (secedere <i>Sen</i>) отправляться в изгнание; aliquem in e. mittere <i>L etc.</i>; in e. pellere <i>и</i> ejicere <i>C</i> отправлять кого-л. в изгнание; exsilio vocis puniri <i>Ap</i> караться наложением безмолвия (<i>у пифагорейцев</i>);\n<b>2)</b> место заточения, ссылки <i>C, Sen, T, V, O etc.</i>;\n<b>3)</b> <i>pl.</i> (= exsules) изгнанники, ссыльные <i>T</i>."],
  ["exsinuo", "<big>exsinuo</big>\n\n<b>ex–sinuo, —, &#257;tum, &#257;re</b>\nразвёртывать, раскрывать (amictus <i>Aus</i>)."],
  ["exsistentia", "<big>exsistentia</big>\n\n<b>exsistentia (existentia), ae</b> <i>f</i> [exsisto]\nсуществование <i>Aug etc.</i>"],
  ["exsisto", "<big>exsisto</big>\n\n<b>ex–sisto (existo), stit&#299;, —, ere</b>\n<b>1)</b> выступать, выходить (spelunca <i>C</i>); показываться (ex collibus <i>bAfr</i>); появляться (ex latebris <i>L</i>);\n<b>2)</b> раздаваться (vox exstitit ab arce <i>C</i>);\n<b>3)</b> выступать, подниматься, торчать, вырастать (inter aures unum cornu exsistit <i>Cs</i>);\n<b>4)</b> возникать (exsistit quaestio <i>C</i>; bellum exstitit <i>L</i>; exsistit controversia inter eos <i>Cs</i>): exsistat ille vir cogitatione vestra <i>C</i> восстановите в своём воображении образ этого человека;\n<b>5)</b> становиться, делаться (ex amicis inimici exsistunt <i>Cs</i>);\n<b>6)</b> происходить (ex, de <i>и</i> ab aliqua re <i>C</i>);\n<b>7)</b> следовать, вытекать (ex quo exsistit, ut <i>C</i>);\n<b>8)</b> оказываться, обнаруживаться, существовать: magnus hic vir exstitit <i>Nep</i> этот человек показал себя великим; talis eloquentia in nemine exstitit <i>C</i> ни у кого не было такого красноречия; fides dictis alicujus exstitit <i>L</i> чьи-л. слова подтвердились; promisso fides exstitit <i>QC</i> обещание (было) выполнено."],
  ["exsolens", "<big>exsolens</big>\n\n<b>ex–sol&#275;ns, entis</b> [soleo]\nотучившийся: e. animalibus <i>Tert</i> воздерживающийся от мясной пищи."],
  ["exsolesco", "<big>exsolesco</big>\n\n<b>ex–sol&#275;sco, —, —, ere</b> [soleo]\nотвыкать, отучаться (ab aliqua re <i>Tert</i>). — <i>См. тж.</i> exolesco."],
  ["exsoletus", "<big>exsoletus</big>\n\n<b>exsol&#275;tus, a, um</b> <i>v. l.</i>\n= exoletus."],
  ["exsolo", "<big>exsolo</big>\n\n<b>exsolo</b> <i>v. l.</i>\n= exsulo."],
  ["exsoluo", "<big>exsoluo</big>\n\n<b>exsoluo</b> <i>Lcr, O</i>\n= exsolvo."],
  ["exsolutio", "<big>exsolutio</big>\n\n<b>exsol&#363;tio, &#333;nis</b> <i>f</i> [exsolvo]\n<b>1)</b> освобождение, избавление (dolorum omnium e. et finis <i>Sen</i>);\n<b>2)</b> уплата, погашение (debiti <i>Dig</i>)."],
  ["exsolvo", "<big>exsolvo</big>\n\n<b>ex–solvo</b> (<i>поэт. тж.</i> <b>exsoluo</b>)<b>, solv&#299;, sol&#363;tum, ere</b>\n<b>1)</b> развязывать (nodum <i>L</i>); отвязывать (pugionem a latere <i>T</i>); распутывать (restim <i>Pl</i>); открывать (cistulam <i>Pl</i>): e. amictus <i>St</i> скидывать (с себя) одежды;\n<b>2)</b> освобождать, избавлять (aliquem vinculis <i>Pl</i>, curis <i>V</i>, poena <i>T</i>; aere alieno <i>L</i>; se occupationibus <i>C</i>; animum religionis nodis <i>Lcr</i>);\n<b>3)</b> растоплять (glaciem <i>Lcr</i>);\n<b>4)</b> вскрывать (venas <i>T</i>);\n<b>5)</b> расслаблять: dissoluta alvus <i>T</i> понос;\n<b>6)</b> прекращать, разгонять, прогонять (metum <i>Lcn</i>); утолять (famem <i>O</i>): e. obsidium <i>T</i> снять осаду;\n<b>7)</b> погашать, уплачивать (aliquid alicui <i>C etc.</i>; nomina alicujus <i>C</i>): e. aes alienum <i>PJ, Dig</i> уплачивать долг; e. gratiam (beneficia) <i>L</i> отплатить (воздать) за благодеяния; pretia poenasque e. <i>L</i> получать то награды, то взыскания; alicujus rei poenas morte exsolvisse <i>T</i> поплатиться жизнью за что-л.;\n<b>8)</b> искупать (culpam <i>T</i>);\n<b>9)</b> выполнять, исполнять (promissa <i>T</i>; fidem <i>L, Dig</i>): se voto e. <i>Pt</i> выполнить свой обет || соблюдать (jus jurandum, votum <i>L</i>);\n<b>10)</b> объяснять: animi ratione e. alicui, quare... <i>Lcr</i> дать кому-л. разумное объяснение, почему."],
  ["exsomnis", "<big>exsomnis</big>\n\n<b>ex–somnis, e</b> [somnus]\n<b>1)</b> бессонный, бодрствующий (Tisiphone <i>V</i>; vir <i>VP</i>);\n<b>2)</b> бессонный, проводимый без сна (nox <i>Amm</i>)."],
  ["exsono", "<big>exsono</big>\n\n<b>ex–sono, u&#299;, —, &#257;re</b>\nоглашаться, (за)звучать (omnia risu exsonuerant <i>Pt</i>): ostium impulsum exsonuit <i>Pt</i> ворота загудели от стука; ingenti risu e. <i>Pt</i> залиться громким смехом, расхохотаться."],
  ["exsorbeo", "<big>exsorbeo</big>\n\n<b>ex–sorbeo, sorbu&#299;</b> (<i>редко</i> <b>sorps&#299;</b>), <b>—, &#275;re</b>\n<b>1)</b> выхлёбывать, всасывать, выпивать (ova <i>PM</i>);\n<b>2)</b> поглощать, высасывать, впитывать (sanguinem <i>C</i>; aliquid avidis linguis <i>O</i>): e. praedas <i>C</i> захватывать (грабить) добычу; e. difficultates alicujus <i>C</i> переносить чьи-л. капризы (упрямство);\n<b>3)</b> (<i>о бурной реке</i>) вырывать, уносить с собою (arbores cum magna soli parte <i>QC</i>) <i>или</i> размывать (ripas <i>QC</i>);\n<b>4)</b> высасывать соки, изнурять, истощать (viros <i>J</i>): e. animam alicujus <i>бран. Pl</i> вышибить дух из кого-л."],
  ["exsordesco", "<big>exsordesco</big>\n\n<b>ex–sord&#275;sco, —, —, ere</b>\nпачкаться, становиться грязным, <i>перен.</i> испоганиваться (in hominibus deterrimis <i>AG</i>)."],
  ["exsors", "<big>exsors</big>\n\n<b>ex–sors, sortis</b>\n<b>1)</b> доставшийся вне жребия, <i>т. е.</i> из ряда вон выходящий, чрезвычайный, исключительный (honor <i>V</i>); отборный (equus <i>V</i>; terga pecudum <i>VF</i>);\n<b>2)</b> стоящий вне (чего-л.) (alicujus rei, <i>редко</i> <i>Sid</i> alicui rei): e. periculi <i>T</i> находящийся вне опасности; e. vitae <i>V</i> лишившийся жизни; e. amicitiae foederisque <i>L</i> (Деций), на которого договор о дружбе не распространялся; omnis culpae e. <i>L</i> ни в чём неповинный; cos e. secandi <i>H</i> оселок (острит железо, но сам) резать неспособен."],
  ["exspargo", "<big>exspargo</big>\n\n<b>exspargo</b> <i>v. l.</i>\n= exspergo."],
  ["exspatior", "<big>exspatior</big>\n\n<b>ex–spatior, &#257;tus sum, &#257;r&#299;</b> <i>depon.</i>\n<b>1)</b> уклоняться от пути, двигаться в сторону от дороги (equi exspatiantur <i>O</i>);\n<b>2)</b> шириться, расширяться, распространяться (arbores latissima umbra exspatiantur <i>PM</i>); разводиться, раздвигаться (in latus <i>Q</i>);\n<b>3)</b> разливаться, выступать из берегов (flumen exspatiatur <i>O</i>);\n<b>4)</b> делать отступление (от темы), отклоняться <i>Q</i>."],
  ["exspectabilis", "<big>exspectabilis</big>\n\n<b>exspect&#257;bilis, e</b> [exspecto]\nожидаемый <i>T, Tert</i>."],
  ["exspectatio", "<big>exspectatio</big>\n\n<b>exspect&#257;tio, &#333;nis</b> <i>f</i> [exspecto]\n<b>1)</b> (напряжённое) ожидание, предвидение, интерес (magna audiendi e. <i>C</i>; spes est e. boni <i>C</i>; <i>реже</i> e. de aliquo <i>и</i> de aliqua re <i>C</i>): in exspectatione esse напряжённо ожидать (sum in exspectatione omnium rerum <i>Plancius ap. C</i>) <i>или</i> быть ожидаемым: vide, ne sies (= sis) in exspectatione <i>Pl</i> смотри, не заставляй себя ждать; praeter exspectationem <i>C</i>, <i>тж.</i> contra <i>Hirt, Sen</i> <i>и</i> supra <i>Q</i> exspectationem, non pro exspectatione <i>Pt</i> против (сверх) ожидания; e. alicujus rei <i>Pt</i> виды на что-л.;\n<b>2)</b> опасение, страх (maximi belli, rerum novarum <i>C</i>): exspectatione majore, quam spe <i>L</i> внушая больше опасений, чем надежд."],
  ["exspectato", "<big>exspectato</big>\n\n<b>exspect&#257;t&#333;</b> <i>adv.</i>:\nnon e. неожиданно, внезапно <i>L, Sil</i>."],
  ["exspectator", "<big>exspectator</big>\n\n<b>exspect&#257;tor, &#333;ris</b> <i>m</i>\nожидающий (с нетерпением <i>или</i> с интересом) <i>Eccl</i>."],
  ["exspectatus", "<big>exspectatus</big>\n\n<b>exspect&#257;tus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> exspecto;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> ожидаемый (ad aliquam rem <i>C</i>): ante exspectatum <i>V, O, Sen etc.</i> <i>или</i> exspectato maturius (ocius) <i>VP</i> ранее, чем ожидали;\n<b>2)</b> желанный, приятный (carus omnibus exspectatusque venies <i>C</i>)."],
  ["exspecto", "<big>exspecto</big>\n\n<b>ex–specto, &#257;v&#299;, &#257;tum, &#257;re</b>\n<b>1)</b> ждать, ожидать, выжидать (ad portam <i>C</i>; dum defluat amnis <i>H</i>; adventum alicujus <i>Cs</i>; aliquid ab <i>или</i> ex aliquo <i>C</i>, ab <i>или</i> ex aliqua re <i>Cs</i>): ab alio exspectes, alteri quod feceris <i>погов. PS</i> что ты сделал другому, того и от него жди; non ultra exspectato <i>T</i> без дальнейших проволочек || пробыть, задержаться (Carthagine <i>V</i>); пережидать (cenantes comites <i>H</i>);\n<b>2)</b> требовать, нуждаться (morbus lenta remedia non exspectat <i>QC</i>);\n<b>3)</b> надеяться, страстно желать (mortem alicujus <i>Ter</i>);\n<b>4)</b> опасаться (supplicium <i>C</i>; aliquid tristius <i>Pt</i>)."],
  ["exspergo", "<big>exspergo</big>\n\n<b>ex–spergo, —, —, ere</b> [spargo]\n<b>1)</b> рассеивать, рассыпать (exspergi spatio <i>Lcr</i>);\n<b>2)</b> опрыскивать, забрызгивать (limina sanie <i>V</i>)."],
  ["exspes", "<big>exspes</big>\n\n<b>ex–sp&#275;s</b> (<i>тк. в</i> <i>nom. sg.</i>) <i>adj.</i>\nлишённый надежды, полный отчаяния <i>Pl, H, O</i>: e. vitae <i>T</i> не надеющийся сохранить свою жизнь."],
  ["exspiratio", "<big>exspiratio</big>\n\n<b>exsp&#299;r&#257;tio, &#333;nis</b> <i>f</i> [exspiro]\nиспарение (terrae <i>C</i>)."],
  ["exspiro", "<big>exspiro</big>\n\n<b>ex–sp&#299;ro, &#257;v&#299;, &#257;tum, &#257;re</b>\n<b>1)</b> выдувать, выдыхать, испускать (odorem suo de corpore <i>Lcr</i>); издавать (unda freti exspirat sonos <i>Sil</i>); извергать (flumen sanguinis de pectore <i>Lcr</i>; flammas pectore <i>V</i>); выделять из себя (cadavera vermes exspirant <i>Lcr</i>);\n<b>2)</b> (<i>тж.</i> animam e. <i>V, O, AG</i>) умирать (in pugna et in acie <i>L</i>): mecum exspiratura res publica erat? <i>L</i> разве вместе со мной погибнет и республика?; Sophroniscum Socrates e. non patitur <i>Sen</i> Сократ обессмертил (отца своего) Софрониска;\n<b>3)</b> утрачивать законную силу (obligatione exspirante <i>Dig</i>);\n<b>4)</b> выдыхаться, испаряться (unguenta exspirant <i>PM</i>);\n<b>5)</b> вырываться, устремляться наружу (exspirantes pectoris irae <i>Ctl</i>; per fauces Aetnae exspirant ignes <i>Lcr</i>)."],
  ["exsplendesco", "<big>exsplendesco</big>\n\n<b>ex–splend&#275;sco, splendu&#299;, —, ere</b>\n<b>1)</b> засверкать, заблистать (ignis exsplenduit <i>Sen</i>; Sirio exsplendescente <i>PM</i>);\n<b>2)</b> блистательно выказывать себя, отличаться, выделяться (in eo corporis animique dotes exsplenduerunt <i>Su</i>)."],
  ["exspoliatio", "<big>exspoliatio</big>\n\n<b>exspoli&#257;tio, &#333;nis</b> <i>f</i>\nограбление <i>H, Aug, Eccl</i>."],
  ["exspoliator", "<big>exspoliator</big>\n\n<b>exspoli&#257;tor, &#333;ris</b> <i>m</i>\nграбитель <i>Eccl</i>."],
  ["exspolio", "<big>exspolio</big>\n\n<b>ex–spolio, &#257;v&#299;, &#257;tum, &#257;re</b>\n<b>1)</b> грабить, обирать (fana atque domos <i>Sl</i>);\n<b>2)</b> отнимать, лишать (aliquem aliqua re <i>Pl, C etc.</i>): e. omnem honorem et dignitatem <i>Hirt</i> лишить всех почестей и достоинства; e. se improbis artibus <i>Pl</i> отказаться от дурных замашек; e. se tunica <i>Aug</i> снять с себя тунику."],
  ["exspretus", "<big>exspretus</big>\n\n<b>ex–spr&#275;tus, a, um</b> [sperno]\nпрезираемый, пренебрегаемый <i>Pl</i> (<i>v. l.</i>)."],
  ["exspuitio", "<big>exspuitio</big>\n\n<b>exspuitio, &#333;nis</b> <i>f</i> [exspuo]\nвыплёвывание (sanguinis <i>PM</i>)."],
  ["exspumo", "<big>exspumo</big>\n\n<b>ex–sp&#363;mo, —, —, &#257;re</b>\nзапениться (umor exspumat <i>CC</i>)."],
  ["exspuo", "<big>exspuo</big>\n\n<b>ex–spuo, u&#299;, &#363;tum, ere</b>\n<b>1)</b> сплёвывать, плевать (nec sudavit unquam, nec exspuit <i>Pt</i>);\n<b>2)</b> выплёвывать, извергать (sanguinem <i>CC, PM</i>); выбрасывать (aliquem spumantibus undis <i>Ctl</i>);\n<b>3)</b> испускать (spiritum <i>SenT</i>); проливать (lacrimam <i>Pl</i>);\n<b>4)</b> изгонять (aliquid ex animo <i>Ter, Lcr</i>)."],
  ["exstans", "<big>exstans</big>\n\n<b>exst&#257;ns, antis</b>\n<b>1.</b> <i>part. praes. к</i> exsto;\n<b>2.</b> <i>adj.</i> выступающий наружу, выдающийся вперёд (partes exstantiores <i>CA</i>)."],
  ["exstantia", "<big>exstantia</big>\n\n<b>exstantia, ae</b> <i>f</i> [exsto]\nвыступ, выпуклость (venarum, tumoris <i>CA</i>); вздутость (ventris <i>CA</i>)."],
  ["exstasis", "<big>exstasis</big>\n\n<b>exstasis (extasis), is</b> (<i>abl.</i> i) <i>f</i> (<i>греч.</i>)\nизумление (plenus stupore et exstasi <i>Vlg</i>)."],
  ["exstaturus", "<big>exstaturus</big>\n\n<b>exst&#257;t&#363;rus, a, um</b> <i>PM part. fut. к</i>\nexsto."],
  ["exstercoro", "<big>exstercoro</big>\n\n<b>ex–stercoro, —, —, &#257;re</b> [stercus]\nчистить уборные <i>Dig</i>."],
  ["exsterno", "<big>exsterno</big>\n\n<b>ex–sterno, &#257;v&#299;, &#257;tum, &#257;re</b>\n<b>1)</b> сильно пугать, поражать ужасом, приводить в ужас (exsternati equi <i>O</i>);\n<b>2)</b> поражать, потрясать (aliquem luctibus <i>Ctl</i>)."],
  ["exstillesco", "<big>exstillesco</big>\n\n<b>ex–st&#299;ll&#275;sco, —, —, ere</b> [stilla]\nкапать, струиться, слезиться (oculi exstillescunt <i>Ph</i>)."],
  ["exstillo", "<big>exstillo</big>\n\n<b>ex–st&#299;llo, &#257;v&#299;, —, &#257;re</b>\nкапать, струиться (amurca exstillat <i>Col</i>); слезиться (oculi exstillant <i>Pl</i>); e. lacrimis <i>Pl</i> исходить (обливаться) слезами."],
  ["exstimulator", "<big>exstimulator</big>\n\n<b>exstimul&#257;tor, &#333;ris</b> <i>m</i>\nподстрекатель, поджигатель, возбудитель (rebellionis <i>T</i>)."],
  ["exstimulo", "<big>exstimulo</big>\n\n<b>ex–stimulo, &#257;v&#299;, &#257;tum, &#257;re</b>\n<b>1)</b> подстрекать, поджигать (corda furore bellandi <i>Sil</i>);\n<b>2)</b> поощрять, возбуждать (animos laudatione <i>Sen</i>): e. fata cessantia <i>O</i> ускорить замешкавшуюся смерть."],
  ["exstinctio", "<big>exstinctio</big>\n\n<b>exst&#299;nctio, &#333;nis</b> <i>f</i> [exstinguo]\n<b>1)</b> тушение (ignis <i>Bo&euml;t</i>);\n<b>2)</b> угасание, потухание (accensiones et exstinctiones lunae <i>Eccl</i>);\n<b>3)</b> уничтожение (non e., sed commutatio loci, <i>sc.</i> mors est <i>C</i>)."],
  ["exstinctor", "<big>exstinctor</big>\n\n<b>exst&#299;nctor, &#333;ris</b> <i>m</i> [exstinguo]\n<b>1)</b> гаситель, тушитель (incendii <i>C</i>);\n<b>2)</b> губитель, истребитель, угнетатель (patriae <i>C</i>);\n<b>3)</b> усмиритель (conjurationis <i>C</i>)."],
  ["exstinctus 1", "<big>exstinctus</big>\n\n<b>I exst&#299;nctus, a, um</b> <i>part. pf. к</i>\nexstinguo."],
  ["exstinctus 2", "<big>exstinctus</big>\n\n<b>II exst&#299;nctus, &#363;s</b> <i>m</i>\nтушение (lucernarum <i>PM</i>)."],
  ["exstinguo", "<big>exstinguo</big>\n\n<b>ex–stinguo (extinguo), st&#299;nx&#299;, st&#299;nctum, ere</b>\n<b>1)</b> гасить, тушить (incendium <i>C</i>); <i>pass.</i> угасать, потухать, гаснуть (ignis exstinguitur <i>C</i>): calx exstincta <i>Vtr</i> гашёная известь;\n<b>2)</b> высушивать, осушать (dissipatam rivis aquam <i>L</i>); иссушать, истощать (mammas <i>PM</i>); <i>pass.</i> высыхать (tepore sucus exstinguitur <i>QC</i>);\n<b>3)</b> лишать силы, обезвреживать (venena <i>PM</i>);\n<b>4)</b> подавлять, пресекать (bellum civile <i>PM</i>); rumores exstincti <i>Cs</i> прекратившиеся слухи || устранять (vitae mala <i>PS</i>); унимать, сдерживать (iram <i>Pt</i>); утолять (sitim <i>O, Sen</i>; famem <i>Sen</i>);\n<b>5)</b> убивать, умерщвлять (populumque patresque <i>V</i>; aliquem morbo e. <i>L</i>); <i>pass.</i> exstingui умирать (exstinctum Daphnim flere <i>V</i>): me exstincto <i>O</i> после моей смерти;\n<b>6)</b> уничтожать (urbem <i>V</i>; superiorem gloriam <i>Cs</i>);\n<b>7)</b> заставлять забыть, замять (invidiam atque infamiam <i>C</i>);\n<b>8)</b> предавать забвению (improbitatem, inimicitias <i>C</i>); <i>pass.</i> приходить в забвение, изглаживаться из памяти (oblivione posteritatis <i>C</i>; vetustate <i>rhH</i>);\n<b>9)</b> лишать законной силы, отменять, объявлять недействительным (venditionem <i>Dig</i>); <i>pass.</i> утрачивать законную силу (obligatio extinguitur <i>Dig</i>) <i>или</i> не подлежать больше судебному преследованию (crimen morte exstinctum <i>Dig</i>)."],
  ["exstinxem", "<big>exstinxem</big>\n\n<b>exst&#299;nxem</b>\n<i>V</i> = exstinxissem (<i>ppf. к</i> exstinguo)."],
  ["exstinxit", "<big>exstinxit</big>\n\n<b>exst&#299;nxit</b>\n<i>Pl</i> = exstinxerit (<i>pf. conjct. к</i> exstinguo)."],
  ["exstirpatio", "<big>exstirpatio</big>\n\n<b>exstirp&#257;tio, &#333;nis</b> <i>f</i> [exstirpo]\nискоренение, удаление с корнем, истребление (filicis <i>Col</i>)."],
  ["exstirpator", "<big>exstirpator</big>\n\n<b>exstirp&#257;tor, &#333;ris</b> <i>m</i>\nистребитель (silvae <i>Aug</i>)."],
  ["exstirpo", "<big>exstirpo</big>\n\n<b>ex–stirpo, &#257;v&#299;, &#257;tum, &#257;re</b> [stirps]\n<b>1)</b> вырывать с корнем (pilos de corpore <i>M</i>); выкорчёвывать (arbores <i>QC</i>);\n<b>2)</b> очищать от пней и корней (ager silvestris exstirpatur <i>Col</i>);\n<b>3)</b> искоренять, уничтожать (vitia <i>C</i>)."],
  ["exstiti", "<big>exstiti</big>\n\n<b>exstit&#299;</b> <i>pf. к</i>\nexsisto."],
  ["exsto", "<big>exsto</big>\n\n<b>ex–sto (exto), —, —, &#257;re</b>\n<b>1)</b> торчать, выдаваться (ferrum exstat de pectore <i>O</i>; e. supra terram <i>AG</i>): capite ex aqua e. <i>Cs</i> стоять по шею в воде; puppis exstat aquis <i>O</i> ладья (всё ещё) держится на поверхности воды (не тонет);\n<b>2)</b> выделяться (e. atque eminere <i>C</i>): apparet atque exstat <i>C</i> выходит (выясняется, становится очевидным);\n<b>3)</b> превышать: a. aliquem <i>St</i> быть выше кого-л.;\n<b>4)</b> быть, существовать: exstant epistulae (libri) <i>C</i> имеются (сохранились) письма (книги); cujus nulla scripta exstant <i>C</i> от сочинений которого ни одно не сохранилось; hominum nemo exstat (qui) <i>Pl</i> нет такого человека (который); quantum hominum memoria exstat <i>Ap</i> насколько простирается человеческая память, <i>т. е.</i> с незапамятных времён; quod litteris exstet <i>C</i> насколько известно из литературных источников."],
  ["exstructio", "<big>exstructio</big>\n\n<b>exstr&#363;ctio, &#333;nis</b> <i>f</i> [exstruo]\n<b>1)</b> сооружение, возведение, постройка (tectorum <i>C</i>; villarum <i>Su</i>);\n<b>2)</b> украшения, наряд, убранство <i>Tert</i>."],
  ["exstructor", "<big>exstructor</big>\n\n<b>exstr&#363;ctor, &#333;ris</b> <i>m</i>\nстроитель, зодчий <i>Hier</i>."],
  ["exstructorius", "<big>exstructorius</big>\n\n<b>exstr&#363;ct&#333;rius, a, um</b>\nпоучительный, назидательный (verbum <i>Tert</i>)."],
  ["exstructus", "<big>exstructus</big>\n\n<b>exstr&#363;ctus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> exstruo;\n<b>2.</b> <i>adj.</i> высокий (species aedificiorum <i>Ap</i>)."],
  ["exstruo", "<big>exstruo</big>\n\n<b>ex–struo (extruo), str&#363;x&#299;, str&#363;ctum, ere</b>\n<b>1)</b> возводить, сооружать, воздвигать, строить (templum <i>PJ</i>; turrim <i>Cs</i>; urbem <i>V</i>): sepulcrum alicui exstrui permittere <i>T</i> разрешить воздвигнуть гробницу кому-л.;\n<b>2)</b> настилать, наслаивать (stramenta <i>Col</i>);\n<b>3)</b> нагромождать, (вз)громоздить (acervum librorum <i>C</i>; focum lignis <i>H</i>): e. mare <i>St</i> застраивать (самое) море (<i>о затеях римск. богачей</i>);\n<b>4)</b> накапливать (divitias <i>H, Pt</i>); уставлять (mensas epulis <i>C</i>): mensae exstructae <i>C</i> богато уставленные столы;\n<b>5)</b> возвеличивать (e. alia, alia submittere <i>Sen</i>);\n<b>6)</b> располагать, выстраивать (verba ad poeticum numerum <i>rhH</i>); слагать (poema <i>Pt</i>)."],
  ["exsucatus", "<big>exsucatus</big>\n\n<b>ex–s&#363;c&#257;tus, a, um</b> [sucus]\nлишённый соков (animalia exsanguinata et exsucata <i>Vtr</i>)."],
  ["exsuccidus", "<big>exsuccidus</big>\n\n<b>ex–s&#363;c(c)idus, a, um</b> <i>Tert</i>\n= exsuccus."],
  ["exsucco", "<big>exsucco</big>\n\n<b>ex–s&#363;c(c)o, —, &#257;tum, &#257;re</b> [sucus]\nлишать (жизненных) соков (aliquem <i>CA, Vtr etc.</i>)."],
  ["exsuccus", "<big>exsuccus</big>\n\n<b>ex–s&#363;c(c)us, a, um</b>\n<b>1)</b> лишённый соков, худосочный (corporis substantia <i>Tert</i>);\n<b>2)</b> сухой, вялый, безжизненный (orator <i>Q</i>)."],
  ["exsuctus", "<big>exsuctus</big>\n\n<b>exs&#363;ctus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> exsugo;\n<b>2.</b> <i>adj.</i> истощённый, худосочный (corpus <i>Sen</i>)."],
  ["exsudo", "<big>exsudo</big>\n\n<b>ex–s&#363;do (ex&#363;do), &#257;v&#299;, &#257;tum, &#257;re</b>\n<b>1)</b> (<i>о поте, перен. о других жидкостях</i>) выходить наружу, выделяться (umor exsudat <i>V</i>; e. sanguine <i>Cld</i>);\n<b>2)</b> выделять (sucum <i>PM</i>; liquorem <i>Col</i>);\n<b>3)</b> трудиться в поте лица, делать с величайшими усилиями (e. laborem <i>L</i>; e. causas <i>H</i>): e. ingens certamen <i>L</i> вести ожесточённую борьбу."],
  ["exsufflatio", "<big>exsufflatio</big>\n\n<b>exsuffl&#257;tio, &#333;nis</b> <i>f</i>\nизгнание злых духов <i>Hier</i>."],
  ["exsufflo", "<big>exsufflo</big>\n\n<b>ex–sufflo, —, —, &#257;re</b>\n<b>1)</b> выдувать, сдувать <i>CA</i>: e. in aliquem ignem irae suae <i>Ambr</i> обрушивать на кого-л. свой гнев;\n<b>2)</b> = exorcizo (infantes <i>Aug</i>; aras <i>Tert</i>)."],
  ["exsugeo", "<big>exsugeo</big>\n\n<b>ex–s&#363;geo, —, —, &#275;re</b> <i>Pl, Cato</i>\n= exsugo."],
  ["exsugo", "<big>exsugo</big>\n\n<b>ex–s&#363;go, s&#363;x&#299;, s&#363;ctum, ere</b>\n<b>1)</b> высасывать (alicui sanguinem <i>Pl</i>; liquorem <i>Vtr</i>);\n<b>2)</b> высушивать (vulnus <i>CC</i>)."],
  ["exsul", "<big>exsul</big>\n\n<b>exsul, exsulis</b> (<i>тж.</i> <b>exul, exulis</b>) <i>m, f</i> [<i>предпол. из</i> ex + solum]\n<b>1)</b> эмигрант, <i>преим.</i> ссыльный, изгнанник (exsules damnatique <i>Cs</i>; e. patriae <i>H</i> <i>или</i> patria <i>Sl</i>): ciconia e. hiemis <i>PS ap. Pt</i> аист, улетающий на зимнее время;\n<b>2)</b> беглец (exsulem excipere <i>Pt</i>);\n<b>3)</b> лишённый (mentis domusque <i>O</i>)."],
  ["exsularis", "<big>exsularis</big>\n\n<b>exsul&#257;ris, e</b> [exsul]\nявляющийся уделом изгнанника (maeror <i>Amm</i>): poena e. <i>Amm</i> = exsilium."],
  ["exsulatio", "<big>exsulatio</big>\n\n<b>exsul&#257;tio, &#333;nis</b> <i>f</i> [exsul]\nизгнание, ссылка (exsulatione multare aliquem <i>Fl</i>)."],
  ["exsulo", "<big>exsulo</big>\n\n<b>exsulo (exulo), &#257;v&#299;, &#257;tum, &#257;re</b> [exsul]\n<b>1)</b> жить в изгнании, находиться в ссылке, быть изгнанником (e. Romae <i>C</i>; per oras extremas <i>O</i>): in Volscos exsulatum abire <i>L</i> отправиться в изгнание (бежать) к вольскам; e. domo <i>Ter</i> быть выгнанным из дому;\n<b>2)</b> прекратить своё существование, окончиться (exsulasse rem publicam putare <i>C</i>)."],
  ["exsulor", "<big>exsulor</big>\n\n<b>exsulor, —, &#257;r&#299;</b> <i>Lact</i>\n= exsulo."],
  ["exsultabundus", "<big>exsultabundus</big>\n\n<b>exsult&#257;bundus, a, um</b> [exsulto]\n<b>1)</b> подпрыгивающий, бьющий вверх (fons <i>Sol</i>);\n<b>2)</b> радостный, ликующий <i>Just</i>."],
  ["exsultans", "<big>exsultans</big>\n\n<b>exsult&#257;ns, antis</b>\n<b>1.</b> <i>part. praes. к</i> exsulto;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> прыгающий, подскакивающий: verbum e. <i>Q</i> слово, состоящее из одних кратких слогов;\n<b>2)</b> резвый, несдержанный, неумеренный (orator <i>Q</i>)."],
  ["exsultanter", "<big>exsultanter</big>\n\n<b>exsultanter</b> [exsultans]\nрезво, несдержанно, неумеренно (scribere <i>PJ</i>)."],
  ["exsultantia", "<big>exsultantia</big>\n\n<b>exsultantia, ae</b> <i>f</i> [exsulto]\n<b>1)</b> наскок, набег, нападение (hostilis <i>Eccl</i>);\n<b>2)</b> припадок (morbi, doloris <i>AG</i>)."],
  ["exsultatio", "<big>exsultatio</big>\n\n<b>exsult&#257;tio, &#333;nis</b> <i>f</i> [exsulto]\n<b>1)</b> прыгание, подпрыгивание, скакание, прыжки (puerilis <i>Col</i>; <i>sc.</i> simiarum <i>PM</i>);\n<b>2)</b> резвость, весёлость, ликование (ex minimis causis <i>Sen</i>)."],
  ["exsultim", "<big>exsultim</big>\n\n<b>exsultim</b> [exsulto]\nрезво прыгая, подпрыгивая (ludere <i>H</i>)."],
  ["exsulto", "<big>exsulto</big>\n\n<b>exsulto (exulto), &#257;v&#299;, &#257;tum, &#257;re</b> [<i>intens. к</i> exsilio]\n<b>1)</b> прыгать, подпрыгивать (pila exsultat <i>Sen</i>); скакать, резвиться (taurus exsultat in herba <i>O</i>): e. in numerum <i>Lcr</i> танцевать, плясать;\n<b>2)</b> бить фонтаном, ключом (sanguis emicat exsultans <i>Lcr</i>);\n<b>3)</b> бушевать, клокотать, волноваться (exsultant aestu latices <i>V</i>); вскипать (medicamen exsultat <i>O</i>); бурно разрастаться (exsultant pampini <i>PM</i>); трепетать (exsultat pavor ac metus <i>Lcr</i>); радоваться (in ruinis alicujus <i>C</i>); ликовать (gaudio <i>C</i>; successu <i>V</i>; vana spe <i>Q</i>);\n<b>4)</b> хвастаться, кичиться (aliqua re <i>C</i>);\n<b>5)</b> распространяться: e. in laude <i>C</i> рассыпаться в похвалах; verborum audacia e. <i>C</i> щеголять смелостью слов."],
  ["exsuo", "<big>exsuo</big>\n\n<b>exsuo</b> <i>v. l.</i>\n= exuo."],
  ["exsuperabilis", "<big>exsuperabilis</big>\n\n<b>exsuper&#257;bilis, e</b> [exsupero]\n<b>1)</b> преодолимый: non e. неодолимый (saxum, <i>sc.</i> Sisyphi <i>V</i>);\n<b>2)</b> преодолевающий, побеждающий (fortis et e. <i>Claudius ap. AG</i>)."],
  ["exsuperans", "<big>exsuperans</big>\n\n<b>exsuper&#257;ns, antis</b>\n<b>1.</b> <i>part. praes. к</i> exsupero;\n<b>2.</b> <i>adj.</i> выдающийся, необыкновенный, замечательный (forma, color <i>AG</i>)."],
  ["exsuperantia", "<big>exsuperantia</big>\n\n<b>exsuperantia, ae</b> <i>f</i> [exsupero]\nпревосходство, высшая степень (virtutis <i>C</i>; vel splendoris vel altitudinis, <i>sc.</i> planetum <i>AG</i>)."],
  ["exsuperatio", "<big>exsuperatio</big>\n\n<b>exsuper&#257;tio, &#333;nis</b> <i>f</i> [exsupero] <i>ритор.</i>\nпреувеличение, гипербола (quum plus dictum est, quam patitur veritas <i>rhH</i>)."],
  ["exsuperatorius", "<big>exsuperatorius</big>\n\n<b>exsuper&#257;t&#333;rius, a, um</b> [exsupero]\nпобедительский, названный именем победителя (mensis <i>Lampr</i>)."],
  ["exsupero", "<big>exsupero</big>\n\n<b>ex–supero, &#257;v&#299;, &#257;tum, &#257;re</b>\n<b>1)</b> подниматься, вздыматься, взвиваться, взметнуться (exsuperant flammae <i>V</i>): violentia exsuperat magis <i>V</i> неистовство (Турна) ещё более возрастает;\n<b>2)</b> пробиваться, преодолевать (angustiae exsuperatae <i>L</i>): aegris faucibus exsuperat halitus <i>Pers</i> из больного горла с трудом выходит дыхание, <i>т. е.</i> трудно дышать;\n<b>3)</b> переходить (jugum <i>V</i>; clivum <i>Sen</i>);\n<b>4)</b> выдаваться, торчать наружу: e. undas <i>V</i> выситься над поверхностью воды;\n<b>5)</b> превосходить, перещеголять (aliquem aliqua re <i>Enn, L, Sil etc.</i>);\n<b>6)</b> превышать (aliquid latitudine <i>PM</i>; binas libras ponderis <i>PM</i>; vires alicujus <i>O</i>): id summum exsuperat Jovem <i>Poeta ap. C</i> этого не в силах сделать и сам Юпитер;\n<b>7)</b> жить <i>или</i> длиться дольше, пережить (aliquem <i>VM</i>);\n<b>8)</b> одолевать, побеждать, одерживать верх: si non poterunt e., cadant <i>O</i> если (римляне) не сумеют одержать верх (над галлами), они погибнут."],
  ["exsuppuro", "<big>exsuppuro</big>\n\n<b>ex–supp&#363;ro, —, —, &#257;re</b>\nудалять (словно) гной <i>Tert</i>."],
  ["exsurdo", "<big>exsurdo</big>\n\n<b>ex–surdo (exurdo), —, &#257;tum, &#257;re</b> [surdus]\n<b>1)</b> оглушать, делать глухим (aures alicujus <i>PM, VM</i>; aliquem clamoribus <i>Sen</i>);\n<b>2)</b> заглушать (calamos <i>Calp</i>);\n<b>3)</b> делать нечувствительным, притуплять (vina exsurdant palatum <i>H</i>)."],
  ["exsurgo", "<big>exsurgo</big>\n\n<b>ex–surgo (exurgo), surr&#275;x&#299;, surr&#275;ctum, ere</b>\n<b>1)</b> вставать, подниматься (a genibus <i>Pl</i>): e. in colles <i>T</i> подняться (взобраться) на холмы; pavore e. <i>T</i> вскакивать в страхе; consternatus exsurrexi <i>Pt</i> я в испуге вскочил;\n<b>2)</b> возвышаться, выситься (Taurus ab Indico mari exsurgit <i>PM</i>): naturali pulchritudine e. <i>Pt</i> выделяться своей природной красотой;\n<b>3)</b> оправляться, восстанавливаться (res publica exsurgit <i>C</i>): supra suos dolores e. <i>O</i> подавить свою скорбь;\n<b>4)</b> восставать (adversus <i>или</i> contra aliquem <i>T</i>)."],
  ["exsurrectio", "<big>exsurrectio</big>\n\n<b>exsurr&#275;ctio, &#333;nis</b> <i>f</i> [exsurgo]\nвосстание <i>Vlg</i>."],
  ["exsuscitatio", "<big>exsuscitatio</big>\n\n<b>exsuscit&#257;tio, &#333;nis</b> <i>f</i> [exsuscito]\nпробуждение, возбуждение, привлечение внимания <i>rhH</i>."],
  ["exsuscito", "<big>exsuscito</big>\n\n<b>ex–suscito, &#257;v&#299;, &#257;tum, &#257;re</b>\n<b>1)</b> поднимать (glebas <i>Acc</i>);\n<b>2)</b> будить, пробуждать (aliquem <i>Acc, Pl, C</i>);\n<b>3)</b> раздувать (flammam aura <i>O</i>); вызывать, зажигать (magnum incendium <i>L</i>);\n<b>4)</b> возбуждать, поощрять (animos <i>C</i>): se e. <i>Brutus ap. C</i> приободриться, встряхнуться, собраться с силами."],
  ["exsustuli", "<big>exsustuli</big>\n\n<b>exsustul&#299;</b>\n<i>редко</i>= extuli (<i>pf. к</i> extollo)."],
  ["exta", "<big>exta</big>\n\n<b>exta, &#333;rum</b> <i>n</i>\n<b>1)</b> [<i>предпол. из</i> ex + secta] внутренности (<i>преим.</i> сердце, лёгкие, печень, селезёнка): e. stridentia <i>Pt</i> шипящие (на вертеле) потроха; e. inspicere <i>C</i> наблюдать внутренности (жертвенного животного) (<i>род гадания</i>); e. laeta <i>или</i> bona <i>C</i> благоприятное предсказание;\n<b>2)</b> жертвенный пир (abducere aliquem ad e. <i>Pl</i>)."],
  ["extabesco", "<big>extabesco</big>\n\n<b>(ex–t&#257;b&#275;sco), t&#257;bu&#299;, —, (ere)</b>\n<b>1)</b> худеть, чахнуть, истощаться (macie <i>Poeta ap. C</i>; fame <i>Su</i>);\n<b>2)</b> <i>перен.</i> исчезать, пропадать, стираться, угасать (vetustate — <i>v. l.</i> diuturnitate — extabuisse <i>C</i>)."],
  ["extalis", "<big>extalis</big>\n\n<b>ext&#257;lis, is</b> <i>m</i> [exta]\nпрямая кишка <i>Veg</i>."],
  ["extantia", "<big>extantia</big>\n\n<b>extantia, ae</b> <i>f</i> <i>v. l.</i>\n= exstantia."],
  ["extaris", "<big>extaris</big>\n\n<b>ext&#257;ris, e</b> [exta]\nотносящийся к внутренностям: aula e. <i>Pl</i> горшок для варки потрохов."],
  ["extasis", "<big>extasis</big>\n\n<b>extasis</b> <i>v. l.</i>\n= exstasis."],
  ["extemplo", "<big>extemplo</big>\n\n<b>ex–templ&#333;</b> <i>adv.</i> [templum]\nтотчас же, сейчас же, сию минуту <i>Pl, Ter, C etc.</i>: quum e. <i>Pl</i> как только."],
  ["extemporalis", "<big>extemporalis</big>\n\n<b>ex–tempor&#257;lis, e</b> [tempus]\nнеподготовленный, экспромтный, импровизированный (declamatio <i>Pt</i>; oratio <i>Q</i>): facultas e. <i>Sen, Su</i> дар импровизации."],
  ["extemporalitas", "<big>extemporalitas</big>\n\n<b>extempor&#257;lit&#257;s, &#257;tis</b> <i>f</i> <i>Su</i>\n= facultas extemporalis (<i>см.</i> extemporalis)."],
  ["extemporaliter", "<big>extemporaliter</big>\n\n<b>extempor&#257;liter</b>\nбез подготовки, экспромтом (scribere aliquid <i>Sid</i>)."],
  ["extempulo", "<big>extempulo</big>\n\n<b>extempul&#333;</b> <i>арх.</i> <i>Pl</i>\n= extemplo."],
  ["extendo", "<big>extendo</big>\n\n<b>ex–tendo, tend&#299;, tentum (t&#275;nsum), ere</b>\n<b>1)</b> растягивать (chartam <i>PM</i>); протягивать, простирать (digitos, brachium <i>C</i>): se e. простираться (ad Hellesponticum fretum <i>Mela</i>) || вытягивать (caput <i>L</i>): se e. adversus aliquem <i>Pt</i> важничать (задирать нос) перед кем-л.;\n<b>2)</b> раскручивать, разматывать, натягивать (funem <i>H</i>);\n<b>3)</b> увеличивать, расширять (agros <i>H</i>);\n<b>4)</b> <i>воен.</i> развёртывать в боевой порядок (classem <i>Frontin</i>; aciem <i>QC</i>);\n<b>5)</b> распростирать (pennas <i>H</i>);\n<b>6)</b> уложить, повергнуть (aliquem arena <i>V</i>);\n<b>7)</b> распространять, разносить (nomen alicujus in ultimas oras <i>H</i>);\n<b>8)</b> напрягать (se supra vires <i>L</i>): e. se itineribus <i>Cs</i> идти ускоренным маршем;\n<b>9)</b> повышать, взвинчивать (pretium <i>Su</i>);\n<b>10)</b> удлинять, затягивать (pugnam ad noctem <i>L</i>; epistulam <i>PJ</i>);\n<b>11)</b> проводить, (на)чертить (lineam <i>PM</i>);\n<b>12)</b> продолжать, продлевать (aevum <i>H</i>; consulatum <i>PJ</i>)."],
  ["extense", "<big>extense</big>\n\n<b>ext&#275;ns&#275;</b> [extendo]\nпространно (extensius disponere aliquid <i>Tert</i>)."],
  ["extensio", "<big>extensio</big>\n\n<b>ext&#275;nsio, &#333;nis</b> <i>f</i> <i>v. l.</i>\n= extentio."],
  ["extensipes", "<big>extensipes</big>\n\n<b>ext&#275;nsi–p&#275;s, pedis</b> <i>m</i> [extensus + pes] (<i>греч.</i> molossus)\n<i>стихотворная стопа из трёх долгих слогов</i> (<span style=white-space:nowrap>&ndash;&thinsp;&ndash;&thinsp;&ndash;)."],
  ["extensivus", "<big>extensivus</big>\n\n<b>ext&#275;ns&#299;vus, a, um</b> [extendo]\nрастяжимый (verba <i>Dig</i>)."],
  ["extensor", "<big>extensor</big>\n\n<b>ext&#275;nsor, &#333;ris</b> <i>m</i> [extendo]\nрастягивающий на дыбе, <i>т. е.</i> палач <i>Ambr</i>."],
  ["extensus", "<big>extensus</big>\n\n<b>ext&#275;nsus</b> <i>v. l.</i>\n= extentus."],
  ["extente", "<big>extente</big>\n\n<b>extent&#275;</b> [extentus]\nшироко протягивая (porrecto extentius brachio <i>Amm</i>)."],
  ["extentero", "<big>extentero</big>\n\n<b>extentero</b> <i>v. l.</i>\n= exentero."],
  ["extentio", "<big>extentio</big>\n\n<b>extentio (ext&#275;nsio), &#333;nis</b> <i>f</i> [extendo]\n<b>1)</b> вытягивание, выпрямление (patientium partium <i>CA</i>);\n<b>2)</b> натягивание (chordarum <i>Is</i>);\n<b>3)</b> распростирание (alarum <i>Vlg</i>): e. mentis <i>CA</i> = exstasis;\n<b>4)</b> прямое направление, прямизна <i>Vtr</i>;\n<b>5)</b> протяжение, пространство (caelorum <i>Hier</i>);\n<b>6)</b> разращение <i>или</i> опухоль (extensiones et tubercula <i>Veg</i>)."],
  ["extento", "<big>extento</big>\n\n<b>extento, (&#257;v&#299;), &#257;tum, &#257;re</b> [<i>intens. к</i> extendo]\n<b>1)</b> натягивать, растягивать (nervos <i>Lcr</i>);\n<b>2)</b> пробовать, испытывать (vires <i>Pl</i>): se e. <i>Pl</i> попытать счастья."],
  ["extentus 1", "<big>extentus</big>\n\n<b>I extentus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> extendo;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> протяжённый, раскинувшийся, пространный, обширный (stagnum <i>H</i>; campus <i>Sil</i>);\n<b>2)</b> затяжной, растянутый (spiritus <i>PM</i>)."],
  ["extentus 2", "<big>extentus</big>\n\n<b>II extentus, &#363;s</b> <i>m</i>\nпротяжение (laterum <i>Sil</i>)."],
  ["extenuatio", "<big>extenuatio</big>\n\n<b>extenu&#257;tio, &#333;nis</b> <i>f</i> [extenuo]\n<b>1)</b> разрежение (aeris <i>Sen</i>; vitium <i>PM</i>);\n<b>2)</b> <i>ритор.</i> умаление, ослабление, преуменьшение <i>C</i>."],
  ["extenuatus", "<big>extenuatus</big>\n\n<b>extenu&#257;tus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> extenuo;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> слабый, незначительный (vestigia <i>Ctl</i>); поредевший (copiolae extenuatissimae <i>Brutus ap. C</i>);\n<b>2)</b> жидкий, бессодержательный (oratio <i>rhH</i>)."],
  ["extenuo", "<big>extenuo</big>\n\n<b>ex–tenuo, &#257;v&#299;, &#257;tum, &#257;re</b>\n<b>1)</b> утончать, разрежать (aer extenuatus <i>C</i>); измельчать, распылять (in pulverem <i>PM</i>); делать жидким, разжижать (extenuari in aquas <i>O</i>);\n<b>2)</b> убавлять, уменьшать (cibum <i>C</i>; molestias <i>C</i>): peccatum extenuat, qui celeriter corrigit <i>погов. PS</i> кто быстро исправляет (свою) ошибку, тот смягчает её || умалять (famam belli <i>L</i>); ослаблять (vires <i>L</i>); истощать, изнурять (corpus <i>O</i>); понижать (spem <i>C</i>); <i>pass.</i> худеть, чахнуть (corpus fame extenuatur <i>CC</i>);\n<b>3)</b> <i>воен.</i> растягивать (agmen <i>L</i>)."],
  ["exter", "<big>exter</big>\n\n<b>exter (exterus), era, erum</b> (<i>compar.</i> exterior, ius; <i>superl.</i> extremus, a, um) [ex]\n<b>1)</b> находящийся вне, наружный, внешний (vis <i>Lcr</i>);\n<b>2)</b> иноземный, чужеземный, иностранный (nationes <i>C, Cs etc.</i>; gentes, civitates <i>C</i>)."],
  ["exterebro", "<big>exterebro</big>\n\n<b>ex–terebro, &#257;v&#299;, &#257;tum, &#257;re</b>\n<b>1)</b> высверливать, извлекать бурением (aurum <i>C</i>);\n<b>2)</b> добиваться силой <i>Pl</i>."],
  ["extergeo", "<big>extergeo</big>\n\n<b>ex–tergeo, ters&#299;, tersum, &#275;re</b>\n<b>1)</b> вытирать, обтирать (manus <i>Pl</i>; manantes lacrimas pollice <i>Pt</i>);\n<b>2)</b> <i>ирон.</i> начисто обобрать, обчистить (fanum <i>C</i>)."],
  ["extergo", "<big>extergo</big>\n\n<b>extergo, —, —, ere</b> <i>Col, Dig, Vlg</i>\n= extergeo."],
  ["exteri", "<big>exteri</big>\n\n<b>exter&#299;, &#333;rum</b> <i>m</i> [exter]\nиностранцы <i>PM</i>."],
  ["exterior", "<big>exterior</big>\n\n<b>exterior, ius</b> [<i>compar. к</i> exter]\nнаружный, внешний (orbis <i>C</i>; hostis <i>Cs</i>): comes e. <i>H</i> спутник, идущий с левой стороны."],
  ["exterius", "<big>exterius</big>\n\n<b>exterius</b> <i>adv.</i> [exter]\nвне, в стороне (urbes e. sitae <i>O</i>)."],
  ["extermentarium", "<big>extermentarium</big>\n\n<b>exterment&#257;rium, &#299;</b> <i>n</i> [extero <i>или</i> extergeo]\nполотенце <i>Vr</i>."],
  ["exterminatio", "<big>exterminatio</big>\n\n<b>extermin&#257;tio, &#333;nis</b> <i>f</i>\nизгнание, удаление (injustorum <i>Vlg</i>)."],
  ["exterminator", "<big>exterminator</big>\n\n<b>extermin&#257;tor, &#333;ris</b> <i>m</i>\n<b>1)</b> изгоняющий, гонитель <i>Hier, Is</i>;\n<b>2)</b> истребитель, разрушитель <i>Vlg, Aug</i>."],
  ["exterminium", "<big>exterminium</big>\n\n<b>exterminium, &#299;</b> <i>n</i>\n<b>1)</b> изгнание <i>Aug</i>;\n<b>2)</b> истребление, уничтожение, гибель (civitatis Jerusalem <i>Tert</i>)."],
  ["extermino", "<big>extermino</big>\n\n<b>ex–termino, &#257;v&#299;, &#257;tum, &#257;re</b> [terminus]\n<b>1)</b> выгонять, изгонять, удалять (aliquem urbe <i>или</i> ex urbe <i>C</i>; ex hominum communitate <i>C</i>); <i>pass.</i> exterminari убираться, уходить (cubiculo <i>Ap</i>);\n<b>2)</b> устранять, уничтожать (auctoritatem senatus e civitate <i>C</i>): exterminatus animi <i>Ap</i> не в своём уме || удалять (herbam sulcis <i>Col</i>): e. faciem suam <i>Vlg</i> придать своему лицу напускное выражение."],
  ["externa", "<big>externa</big>\n\n<b>externa, &#333;rum</b> <i>n</i> [externus]\n<b>1)</b> чужие дела, посторонние вопросы (e. et interiora <i>C</i>);\n<b>2)</b> иностранные дела (e. et domestica <i>C, VP</i>);\n<b>3)</b> враждебные чувства, вражда, неприязнь (e. armis falsis velare <i>T</i>): e. moliri <i>T</i> лелеять враждебные замыслы."],
  ["externo", "<big>externo</big>\n\n<b>externo</b> <i>v. l.</i>\n= exsterno."],
  ["externus 1", "<big>externus</big>\n\n<b>I externus, a, um</b> [exter]\n<b>1)</b> находящийся вне, внешний (e. et adventicius tepor <i>C</i>; bona <i>O</i>; hostis <i>C</i>);\n<b>2)</b> чужой, посторонний (res, homines <i>C</i>);\n<b>3)</b> иноземный, чужеземный, иностранный (mores <i>T</i>; gens <i>V</i>): externum conubium <i>O</i> брак с иноземцем."],
  ["externus 2", "<big>externus</big>\n\n<b>II externus, &#299;</b> <i>m</i>\n<b>1)</b> чужак, чужой, посторонний человек (canum odium in externos <i>C</i>);\n<b>2)</b> иностранец, чужеземец (externi alienique <i>VP</i>)."],
  ["extero", "<big>extero</big>\n\n<b>ex–tero, tr&#299;v&#299;, tr&#299;tum, ere</b>\n<b>1)</b> вытирать, стирать, удалять трением (rubiginem ferro <i>PM</i>); добывать трением (ignem <i>Lcr</i>); растирать, разжёвывать (cibum <i>C</i>); вытаптывать, молотить ногами (grana ex spicis <i>Vr</i>); раздавливать (aliquem magno pondere <i>Sen</i>); растаптывать (nives <i>O</i>);\n<b>2)</b> потреблять (merces <i>PM</i> — <i>v. l.</i>);\n<b>3)</b> <i>грам.</i> выбрасывать, элидировать (litteram <i>Vr</i>)."],
  ["exterreo", "<big>exterreo</big>\n\n<b>ex–terreo, u&#299;, itum, &#275;re</b>\n(ис)пугать, всполошить (exterritus somno <i>Enn</i> <i>и</i> per somnum <i>Su</i>; aliquem aliqua re <i>Cs, L</i>): periculo matris exterritus <i>T</i> напуганный опасностью, которой подвергалась мать || привести в ужас, поразить страхом (aliquem repentino incursu <i>Cs</i>): terrore exterritus <i>T</i> охваченный ужасом."],
  ["exterritatio", "<big>exterritatio</big>\n\n<b>exterrit&#257;tio, &#333;nis</b> <i>f</i> [exterreo]\nиспуг <i>Vlg</i>."],
  ["extersus 1", "<big>extersus</big>\n\n<b>I extersus, a, um</b> <i>part. pf. к</i>\nextergeo."],
  ["extersus 2", "<big>extersus</big>\n\n<b>II extersus, &#363;s</b> <i>m</i> [extergeo]\nвытирание: linteum extersui <i>Pl</i> полотенце для вытирания."],
  ["exterus", "<big>exterus</big>\n\n<b>exterus, a, um</b> <i>v. l.</i>\n= exter."],
  ["extexo", "<big>extexo</big>\n\n<b>ex–texo, —, —, ere</b>\nраспускать ткань, <i>перен.</i> обобрать, обчистить (aliquem <i>Pl</i>)."],
  ["extillo", "<big>extillo</big>\n\n<b>ext&#299;llo</b> <i>v. l.</i>\n= exstillo."],
  ["extimatio 1", "<big>extimatio</big>\n\n<b>I extim&#257;tio, &#333;nis</b> <i>f</i> <i>CA</i>\n= existimatio."],
  ["extimatio 2", "<big>extimatio</big>\n\n<b>II extim&#257;tio, &#333;nis</b> <i>f</i> [extimus]\nкрай, оконечность <i>Tert</i>."],
  ["extimesco", "<big>extimesco</big>\n\n<b>extim&#275;sco, timu&#299;, —, ere</b> [timeo]\nпугаться, чувствовать страх, бояться (aliqua re, de aliqua re <i>или</i> aliquam rem <i>C etc.</i>): nihil est praeterea extimescendum <i>C</i> больше уже бояться нечего."],
  ["extimidus", "<big>extimidus</big>\n\n<b>ex–timidus, a, um</b>\nвесьма боязливый <i>AG</i> (<i>v. l. к</i> extrilidus)."],
  ["extimulo", "<big>extimulo</big>\n\n<b>extimulo</b> <i>v. l.</i>\n= exstimulo."],
  ["extimus", "<big>extimus</big>\n\n<b>extimus (extumus), a, um</b> <i>Lcr, Vr, C etc.</i>\n= extremus."],
  ["extin-", "<big>extin-</big>\n\n<b>extin-</b> <i>v. l.</i>\n= exstin-."],
  ["extirpo", "<big>extirpo</big>\n\n<b>extirpo</b> <i>v. l.</i>\n= exstirpo."],
  ["extispex", "<big>extispex</big>\n\n<b>exti–spex, icis</b> <i>m</i> [exta + specio]\nэкстиспик, прорицатель, гадающий по внутренностям жертвенных животных <i>Acc, Vr, C</i>."],
  ["extispicium", "<big>extispicium</big>\n\n<b>extispicium, &#299;</b> <i>n</i> [extispex]\nэкстиспиций, рассматривание внутренностей жертвенных животных <i>и</i> гадание по этим внутренностям <i>Su, PM, Ap</i>."],
  ["exto", "<big>exto</big>\n\n<b>exto</b> <i>v. l.</i>\n= exsto."],
  ["extollentia", "<big>extollentia</big>\n\n<b>extollentia, ae</b> <i>f</i> [extollo]\nвысокомерие, гордыня <i>Vlg, Eccl</i>."],
  ["extollo", "<big>extollo</big>\n\n<b>ex–tollo, extul&#299;</b> (<i>редко</i> <b>exsustul&#299;</b>), <b>—, ere</b>\n<b>1)</b> поднимать (pugionem alte, caput, aliquem jacentem <i>C</i>; oculos ad sidera <i>V</i>; aliquem in murum <i>Cs</i>): ubi primos crastinus ortus extulerit Titan (= <i>Sol</i>) <i>V</i> завтра с первыми же лучами солнца;\n<b>2)</b> вздымать (montium vertices arduos <i>Ap</i>): aliquid in majus e. <i>L, PJ etc.</i> преувеличивать что-л.; se magis e. <i>Sl</i> стремиться к высшему; e. postremum pedem <i>Pl</i> уходить навсегда || воздвигать, строить (fundamentum <i>Pl</i>);\n<b>3)</b> возвышать, возносить (aliquem honoribus, aliquem supra ceteros <i>T</i>); превозносить, прославлять, возвеличивать, восхвалять (aliquid laudibus <i>C</i>; aliquem ad <i>и</i> in caelum <i>C, Amm</i>);\n<b>4)</b> внушать мужество, ободрять (aliquem <i>или</i> animum alicujus <i>C, T etc.</i>);\n<b>5)</b> делать заносчивым (se supra modum <i>Q</i>; animum alicujus ad superbiam <i>T</i>);\n<b>6)</b> поднимать, повышать, усиливать (vocem per gradus <i>L</i>): cachinnum e. <i>Ap</i> залиться смехом;\n<b>7)</b> украшать (hortos <i>T</i>);\n<b>8)</b> откладывать, отсрочивать (aliquid in alium diem <i>Pl</i>)."],
  ["extorno", "<big>extorno</big>\n\n<b>ex–torno, —, —, &#257;re</b>\nобтачивать (turbinem <i>Bo&euml;t</i>)."],
  ["extorqueo", "<big>extorqueo</big>\n\n<b>ex–torqueo, tors&#299;, tortum, &#275;re</b>\n<b>1)</b> вывёртывать, выкручивать, вырывать (alicui arma de <i>и</i> e manibus <i>C</i>): taeterrima voce canticum extorsisse <i>Pt</i> душераздирающим голосом запеть песню || вывихнуть (alicui pollicem <i>Pt</i>): omnibus membris extortus <i>PJ</i> весь искривлённый;\n<b>2)</b> подвергать пытке, пытать, истязать (aliquem <i>Ter, L</i>);\n<b>3)</b> вымогать (pecuniam ab aliquo <i>C, Su</i>); вынуждать, исторгать, добиваться (alicui veritatem e. <i>C</i>): extorsisti, ut faterer... <i>C</i> ты заставил меня признать (что...) || вырывать, спасать (manibus alicujus extortus velut ab inferis <i>Ap</i>); отнимать (regnum alicui <i>L</i>; alicui vitam <i>Lcr</i>); e. alicui pudorem <i>Pt</i> обесчестить кого-л.; e. alicui errorem <i>C</i> вывести кого-л. из заблуждения;\n<b>4)</b> удалять, изгонять (incubantem Italiae Hannibalem <i>Fl</i>)."],
  ["extorreo", "<big>extorreo</big>\n\n<b>ex–torreo, —, —, &#275;re</b>\nвысушивать, сушить (ardens febris extorret <i>CC</i>)."],
  ["extorris", "<big>extorris</big>\n\n<b>extorris, e</b> [<i>предпол.</i> ex + terra]\nизгнанный, сосланный, ссыльный (domo <i>Sl</i>; ab solo patrio <i>L</i>; patria <i>Sl</i>; finibus <i>V</i>)."],
  ["extorsi", "<big>extorsi</big>\n\n<b>extors&#299;</b> <i>pf. к</i>\nextorqueo."],
  ["extorsio", "<big>extorsio</big>\n\n<b>extorsio, &#333;nis</b> <i>f</i> [extorqueo]\nвымогание <i>Hier</i>."],
  ["extortor", "<big>extortor</big>\n\n<b>extortor, &#333;ris</b> <i>m</i>\nвымогатель (bonorum <i>Ter</i>)."],
  ["extra 1", "<big>extra</big>\n\n<b>I extr&#257;</b> <i>praep. cum acc.</i> [exter]\n<b>1)</b> вне, снаружи (e. urbem <i>T</i>; e. portam <i>Pl</i>); без, вне, в стороне от (e. culpam <i>C</i>; e. periculum <i>C, L</i>; e. conjurationem <i>C</i>): magis e. vitia, quam cum virtutibus <i>T</i> (Гальба) скорее не имел пороков, чем обладал достоинствами; e. fortunam esse <i>M</i> быть недосягаемым для превратностей судьбы || вовне, за пределы (egredi e. fines <i>C</i>);\n<b>2)</b> кроме, исключая (omnes e. ducem <i>C</i>): e. jocum <i>C</i> кроме (без) шуток; e. modum <i>C</i> сверх меры, чересчур; e. consuetudinem <i>Cs</i> против обыкновения."],
  ["extra 2", "<big>extra</big>\n\n<b>II extr&#257;</b> <i>adv.</i> [exter]\n<b>1)</b> вне, снаружи (hostem e. et intus habere <i>Cs</i>): ea, quae e. sunt <i>C</i> внешний мир, внешняя обстановка;\n<b>2)</b> кроме: e. quam (qui) <i>L</i> кроме тех (которые); e. quam si <i>C, L</i> за исключением тех случаев (когда)."],
  ["extractorius", "<big>extractorius</big>\n\n<b>extract&#333;rius, a, um</b> [extraho]\nвытяжной (vis <i>PM</i>)."],
  ["extraho", "<big>extraho</big>\n\n<b>ex–traho, tr&#257;x&#299;, tractum, ere</b>\n<b>1)</b> вытягивать, вытаскивать (aliquem in siccum <i>Pt</i>; rete ex aqua <i>Pl</i>); извлекать, вынимать, вырывать (cultrum e vulnere <i>L</i>; aliquem turba <i>H</i>): e. aliquid in lucem <i>L</i> вытащить что-л. на свет, <i>т. е.</i> выяснить, разъяснить;\n<b>2)</b> выводить (copias ex hibernaculis <i>Nep</i>);\n<b>3)</b> стаскивать, снимать (anulum alicui <i>Su</i>);\n<b>4)</b> искоренять, удалять (errorem stirpitus <i>C</i>; religionem ex animis <i>C</i>);\n<b>5)</b> избавлять, спасать (aliquem ex periculo <i>C</i>);\n<b>6)</b> насильно выводить, выгонять (rure extractus in urbem <i>H</i>); вызывать (mortuos ab inferis <i>L</i>);\n<b>7)</b> возвышать (aliquem ad honorem <i>L</i>);\n<b>8)</b> затягивать, тянуть (proelium ad noctem <i>L</i>; bellum in tertium annum <i>L</i>; aliquid in infinitum <i>Q</i>); откладывать (rem in consulis adventum <i>L</i>); задерживать, водить за нос, обманывать обещаниями (aliquem <i>L, Su</i>);\n<b>9)</b> проводить (noctem vigiliis <i>QC</i>);\n<b>10)</b> растрачивать (aestatem sine ullo effectu <i>L</i>)."],
  ["extramuranus", "<big>extramuranus</big>\n\n<b>extr&#257;–m&#363;r&#257;nus, a, um</b> [murus]\nнаходящийся <i>или</i> обитающий вне (городских) стен (meretrices <i>Lampr</i>; basilica <i>Ambr</i>)."],
  ["extranaturalis", "<big>extranaturalis</big>\n\n<b>extr&#257;–n&#257;t&#363;r&#257;lis, e</b>\nсверхъестественный <i>Tert</i>."],
  ["extranee", "<big>extranee</big>\n\n<b>extr&#257;ne&#275;</b> [extraneus]\nпосторонним образом, <i>т. е.</i> в несобственном смысле (definire aliquid <i>Bo&euml;t</i>)."],
  ["extraneo", "<big>extraneo</big>\n\n<b>extr&#257;neo, &#257;v&#299;, —, &#257;re</b> [extraneus]\nсчитать чужим, обращаться как с чужим ребёнком (e. filiam <i>Ap</i>)."],
  ["extraneus 1", "<big>extraneus</big>\n\n<b>I extr&#257;neus, a, um</b> [extra]\n<b>1)</b> внешний, посторонний, приходящий извне (res <i>rhH, C etc.</i>; causa <i>rhH</i>); наружный (ornamenta <i>C</i>);\n<b>2)</b> чужой (heres <i>PJ, Dig</i>);\n<b>3)</b> чужеземный, иностранный (medicus <i>CC</i>);\n<b>4)</b> употребляемый в несобственном смысле (verba <i>rhH</i>);\n<b>5)</b> внесемейный (voluptates <i>Spart</i>)."],
  ["extraneus 2", "<big>extraneus</big>\n\n<b>II extr&#257;neus, &#299;</b> <i>m</i>\n<b>1)</b> чужестранец, иноземец <i>L, Dig</i>;\n<b>2)</b> чужак, посторонний <i>Su, PM</i>."],
  ["extraordinarius", "<big>extraordinarius</big>\n\n<b>extr&#257;–&#333;rdin&#257;rius, a, um</b>\nиз ряда вон выходящий, необыкновенный (cupiditas <i>C</i>); чрезвычайный (potestas <i>C</i>); особый, отборный (equites, cohortes <i>L</i>)."],
  ["extrarius 1", "<big>extrarius</big>\n\n<b>I extr&#257;rius, a, um</b> [extra]\n<b>1)</b> внешний, находящийся снаружи, поступающий извне (lux <i>Lcr</i>);\n<b>2)</b> посторонний, не относящийся к внутренней сущности (bonum <i>Ap</i>);\n<b>3)</b> чуждый, чужой (accusator <i>Q</i>; mulier <i>Ap</i>);\n<b>4)</b> иностранный <i>Ap</i>."],
  ["extrarius 2", "<big>extrarius</big>\n\n<b>II extr&#257;rius, &#299;</b> <i>m</i>\n<b>1)</b> чужой человек <i>Ter</i>;\n<b>2)</b> иноземец, чужестранец (extrario nubere <i>Ap</i>): penes (apud) extrarios <i>Ap</i> за границей."],
  ["extraxi", "<big>extraxi</big>\n\n<b>extr&#257;x&#299;</b> <i>pf. к</i>\nextraho."],
  ["extremior", "<big>extremior</big>\n\n<b>extr&#275;mior, ius</b> <i>Ap, Tert compar. к</i>\nextremus."],
  ["extremissimus", "<big>extremissimus</big>\n\n<b>extr&#275;missimus, a, um</b> <i>Tert superl. к</i>\nextremus."],
  ["extremitas", "<big>extremitas</big>\n\n<b>extr&#275;mit&#257;s, &#257;tis</b> <i>f</i> [extremus]\n<b>1)</b> край, оконечность (mundi <i>C</i>); окраина, предел (regionis <i>C</i>); периферия (circuli <i>PM</i>);\n<b>2)</b> очерк, контур (picturae <i>PM</i>; imaginum <i>Pt</i>); окаймление, обрамление (lacus <i>PJ</i>);\n<b>3)</b> <i>анат.</i> конечность (extremitates corporis <i>PM</i>);\n<b>4)</b> <i>грам.</i> окончание <i>AG</i>;\n<b>5)</b> <i>ритор.</i> крайность, противоположность (inter extremitates mediae sunt species <i>Q</i>);\n<b>6)</b> <i>мат.</i> поверхность, плоскость (e. et quasi libramentum, in qua nulla crassitudo est <i>C</i>)."],
  ["extremum 1", "<big>extremum</big>\n\n<b>I extr&#275;mum, &#299;</b> <i>n</i> [extremus]\nкрай, конец (aestatis <i>Sl</i>; anni <i>L</i>, mundi <i>Sen</i>): ad (in) e. <i>C, L etc.</i> до конца, крайне, под конец, наконец; a primo ad e. <i>C</i> с начала до конца; extremo <i>Nep, Su</i> наконец, в конце концов; in extremo (libro) <i>C</i> в заключение книги; extrema tentare <i>SenT</i> <i>или</i> ad extrema (per)venire <i>T, QC</i> прибегнуть к крайним средствам; extrema de Augusto tradere <i>T</i> рассказывать о последних днях жизни Августа."],
  ["extremum 2", "<big>extremum</big>\n\n<b>II extr&#275;mum</b> <i>adv.</i> [extremus]\n<b>1)</b> в конце концов, наконец <i>O</i>;\n<b>2)</b> в последний раз <i>V, O etc.</i>"],
  ["extremus", "<big>extremus</big>\n\n<b>extr&#275;mus (extimus), a, um</b> [<i>superl. к</i> exter]\n<b>1)</b> крайний (oppidum <i>Cs</i>); конечный (fines <i>Cs</i>); последний (vitae dies <i>C</i>): tenue et extremum lumen <i>Pt</i> слабый, меркнущий свет; extremo anno <i>L</i> в конце года; extremi pollices <i>Pt</i> кончики больших пальцев;\n<b>2)</b> отдалённейший (Indi <i>H</i>); пограничный (flumen <i>Cs</i>);\n<b>3)</b> заключительный (pars epistulae <i>C</i>): statim ab extrema parte verbi <i>Pt</i> как только было произнесено последнее слово; extremum agmen <i>Cs</i> (<i>тж.</i> extremi, orum <i>Cs</i>) арьергард;\n<b>4)</b> чрезвычайный (senatus consultum <i>Cs</i>);\n<b>5)</b> критический, тяжёлый (tempora <i>C</i>);\n<b>6)</b> высший, величайший, <i>т. е.</i> лучший (extremum bonorum <i>C</i>) <i>или</i> худший (sors <i>Just</i>; extremum malorum <i>C</i>): extrema manus <i>C</i> завершение, окончание, окончательная отделка."],
  ["extrico", "<big>extrico</big>\n\n<b>ex–tr&#299;co, &#257;v&#299;, &#257;tum, &#257;re</b> [tricae]\n<b>1)</b> выпутывать (extricata cerva plagis <i>H</i>): margaritae extricatae <i>Dig</i> отдельные (не нанизанные) жемчужины;\n<b>2)</b> делать пригодным для обработки (agrum silvestrem <i>Col</i>);\n<b>3)</b> добывать, отыскивать, доставать (nummos <i>H</i>): de aliquo nihil e. <i>Vatinius ap. C, Ph</i> не выяснить <i>или</i> не сделать чего-л.;\n<b>4)</b> изгонять (omne genus daemoniorum <i>Vlg</i>)."],
  ["extricor", "<big>extricor</big>\n\n<b>extr&#299;cor, —, &#257;r&#299;</b> <i>depon.</i> <i>Pl</i>\n= extrico."],
  ["extrilidus", "<big>extrilidus</big>\n\n<b>extrilidus, a, um</b>\nбесстрашный (impavidus et e. <i>AG</i>), <i>по др.</i> крайне бледный (pavidus et e. <i>AG</i> — <i>v. l.</i>)."],
  ["extrinsecus", "<big>extrinsecus</big>\n\n<b>extr&#299;nsecus</b> <i>adv.</i> [extra + secus]\n<b>1)</b> извне (bellum e. imminens <i>L</i>); вне, снаружи (columna e. inaurata <i>C</i>);\n<b>2)</b> сверх того, кроме того: cepit e. Persarum nobilitatem <i>Eutr</i> (Максимиан) захватил, кроме того, в плен персидскую знать;\n<b>3)</b> вне вопроса: haec, etsi e., non tamen... <i>Col</i> хотя это и не имеет прямого отношения к делу, всё же."],
  ["extritus", "<big>extritus</big>\n\n<b>extr&#299;tus, a, um</b> <i>part. pf. к</i>\nextero."],
  ["extrivi", "<big>extrivi</big>\n\n<b>extr&#299;v&#299;</b> <i>pf. к</i>\nextero."],
  ["extrudo", "<big>extrudo</big>\n\n<b>ex–tr&#363;do, tr&#363;s&#299;, tr&#363;sum, ere</b>\n<b>1)</b> выталкивать, выгонять, прогонять (aliquem domo, foras, ex aedibus <i>Pl</i>; a senatu <i>C</i>);\n<b>2)</b> оттеснять: e. mare <i>Cs</i> сдерживать напор моря;\n<b>3)</b> сбывать с рук (merces <i>H</i>);\n<b>4)</b> вытеснять (rerum novitate extrusa vetustas <i>Lcr</i>)."],
  ["extruo", "<big>extruo</big>\n\n<b>extruo</b> <i>v. l.</i>\n= exstruo."],
  ["extrusi", "<big>extrusi</big>\n\n<b>extr&#363;s&#299;</b> <i>pf. к</i>\nextrudo."],
  ["extuberatio", "<big>extuberatio</big>\n\n<b>ext&#363;ber&#257;tio, &#333;nis</b> <i>f</i> [extubero]\nвздутие, опухоль <i>PM</i>."],
  ["extubero", "<big>extubero</big>\n\n<b>ex–t&#363;bero, &#257;v&#299;, &#257;tum, &#257;re</b>\n<b>1)</b> вздуваться, подниматься, набухать (velut malum <i>PM</i>);\n<b>2)</b> вспучивать, вздувать, вздымать (motus terrarum valles extuberat <i>Sen</i>; aviditate nimia extuberatus venter <i>Amm</i>)."],
  ["extudi", "<big>extudi</big>\n\n<b>extud&#299;</b> <i>pf. к</i>\nextundo."],
  ["extuli", "<big>extuli</big>\n\n<b>extul&#299;</b> <i>pf. к</i>\n<b>1)</b> effero I <i>и</i>\n<b>2)</b> extollo."],
  ["extumefactus", "<big>extumefactus</big>\n\n<b>extumefactus, a, um</b> [extumeo + facio]\nразбухший, вздутый (potu atque pastu <i>C</i> — <i>v. l. к</i> obstupefactus)."],
  ["extumeo", "<big>extumeo</big>\n\n<b>ex–tumeo, —, —, &#275;re</b>\nнабухать, вспухать <i>Pl</i>."],
  ["extumesco", "<big>extumesco</big>\n\n<b>extum&#275;sco, tumu&#299;, —, ere</b> [<i>inchoat. к</i> extumeo]\nнадуваться, пухнуть (ira <i>Ap</i>)."],
  ["extumidus", "<big>extumidus</big>\n\n<b>extumidus, a, um</b>\nнабухающий, вздымающийся (area <i>Vr</i>)."],
  ["extumus", "<big>extumus</big>\n\n<b>extumus (extimus), a, um</b> <i>Vr, Lcr etc.</i>\n= extremus."],
  ["extunc", "<big>extunc</big>\n\n<b>ex–tunc</b> <i>adv.</i> (<i>чаще раздельно</i>)\nс тех пор <i>Vlg</i>."],
  ["extundo", "<big>extundo</big>\n\n<b>ex–tundo, tud&#299;, (t&#363;sum), ere</b>\n<b>1)</b> выбивать молотком, выдавливать, выковывать (ancilia <i>V</i>);\n<b>2)</b> проламывать, пробивать (frontem calcibus <i>Ph</i>);\n<b>3)</b> изготовлять, сколачивать, с большим трудом сочинять (librum <i>T</i>);\n<b>4)</b> добывать, доставлять (hanc artem alicui <i>V</i>);\n<b>5)</b> вырабатывать (eloquentiam <i>AG</i>);\n<b>6)</b> вынуждать, добиваться (aliquid precibus <i>Su</i>);\n<b>7)</b> выталкивать, выгонять (tussis sanguinem extundit <i>CC</i>);\n<b>8)</b> вытеснять, удалять, уничтожать (labor extundit fastidia <i>H</i>)."],
  ["exturbo", "<big>exturbo</big>\n\n<b>ex–turbo, &#257;v&#299;, &#257;tum, &#257;re</b>\n<b>1)</b> выгонять, изгонять (aliquem ex aedibus <i>и</i> aedibus <i>Pl</i>; hostem provincia <i>C</i>);\n<b>2)</b> исключать (aliquem ex numero vivorum <i>C</i>);\n<b>3)</b> вышибать, выбивать (alicui dentes <i>Pl</i>);\n<b>4)</b> вырывать (pinus radicitus exturbata <i>Ctl</i>);\n<b>5)</b> удалять (calculos, <i>sc.</i> renium <i>PM</i>; ex animo aegritudinem <i>Pl</i>);\n<b>6)</b> расстраивать, разрушать (omnem spem pacis <i>L</i>): e. mentem alicujus <i>C</i> привести кого-л. в замешательство (вывести из равновесия)."],
  ["extussio", "<big>extussio</big>\n\n<b>ex–tussio, —, &#299;tum, &#299;re</b>\nоткашливать, выделять с кашлем (sanguinem <i>PM</i>; extussita sputa <i>CA</i>)."],
  ["exuberans", "<big>exuberans</big>\n\n<b>ex&#363;ber&#257;ns, antis</b>\n<b>1.</b> <i>part. praes. к</i> exubero;\n<b>2.</b> чрезвычайный (pernicitas <i>Amm</i>)."],
  ["exuberantia", "<big>exuberantia</big>\n\n<b>ex&#363;berantia, ae</b> <i>f</i> [exubero]\n(пре)избыток, изобилие, чрезмерность (ruboris <i>AG</i>); необыкновенное развитие. (memoriae <i>AG</i>)."],
  ["exuberatio", "<big>exuberatio</big>\n\n<b>ex&#363;ber&#257;tio, &#333;nis</b> <i>f</i> <i>Vtr</i>\n= exuberantia."],
  ["exubero", "<big>exubero</big>\n\n<b>ex–&#363;bero, &#257;v&#299;, &#257;tum, &#257;re</b>\n<b>1)</b> появляться в изобилии, буйно расти, разрастаться (umbra foliorum luxuria exuberat <i>V</i>); пышно расцветать (exuberat eloquentia <i>T</i>);\n<b>2)</b> переполняться, вздуваться, бить ключом (fons exuberat <i>PM</i>; alte exuberat amnis <i>V</i>);\n<b>3)</b> изобиловать (aliqua re <i>V etc.</i>);\n<b>4)</b> производить в изобилии (vindemias, favorum ceras <i>Col</i>);\n<b>5)</b> значительно превышать числом, нахлынуть во множестве (Scythae exuberant Persas <i>Tert</i>)."],
  ["exuccus", "<big>exuccus</big>\n\n<b>exuccus</b> <i>v. l.</i>\n= exsuc(c)us."],
  ["exudo", "<big>exudo</big>\n\n<b>ex&#363;do</b> <i>v. l.</i>\n= exsudo."],
  ["exugo", "<big>exugo</big>\n\n<b>ex&#363;go</b> <i>v. l.</i>\n= exsugo."],
  ["exul", "<big>exul</big>\n\n<b>exul, ulis</b> <i>v. l.</i>\n= exsul."],
  ["exulceratio", "<big>exulceratio</big>\n\n<b>exulcer&#257;tio, &#333;nis</b> <i>f</i> [exulcero]\n<b>1)</b> изъязвление, появление язв <i>CC, PM</i>;\n<b>2)</b> раздражение, растравление (non consolatio, sed e. <i>Sen</i>)."],
  ["exulceratorius", "<big>exulceratorius</big>\n\n<b>exulcer&#257;t&#333;rius, a, um</b> [exulcero]\nисцеляющий язвы, противоязвенный (medicamentum <i>PM</i>)."],
  ["exulceratrix", "<big>exulceratrix</big>\n\n<b>exulcer&#257;tr&#299;x, &#299;cis</b> <i>adj. f</i>\nизъязвляющая (vis septica et e. <i>PM</i>)."],
  ["exulcero", "<big>exulcero</big>\n\n<b>ex–ulcero, &#257;v&#299;, &#257;tum, &#257;re</b>\n<b>1)</b> изъязвлять, покрывать нарывами, вызывать нагноение (e. cutem <i>PM</i>);\n<b>2)</b> бередить, растравлять (cicatrices <i>PM</i>); ухудшать, усиливать, отягощать (dolorem <i>PJ</i>); портить, придавать дурной оборот (res exulceratae <i>C</i>); возмущать, озлоблять, раздражать, ожесточать (animum alicujus ignominia <i>L</i>)."],
  ["exulo", "<big>exulo</big>\n\n<b>exulo</b> <i>v. l.</i>\n= exsulo."],
  ["exulto", "<big>exulto</big>\n\n<b>exulto</b> <i>v. l.</i>\n= exsulto."],
  ["exululatus", "<big>exululatus</big>\n\n<b>exulul&#257;tus, a, um</b> [<i>part. pf. к</i> exululo]\nзавывший, взвывший, возопивший <i>O</i>."],
  ["exululo", "<big>exululo</big>\n\n<b>ex–ululo, &#257;v&#299;, &#257;tum, &#257;re</b>\n<b>1)</b> завывать, вопить <i>O, VF, Sil</i>;\n<b>2)</b> призывать воплями (Cybeleia mater Phrygiis exululata modis <i>O</i>)."],
  ["exunctus", "<big>exunctus</big>\n\n<b>ex&#363;nctus, a, um</b> <i>part. pf. к</i>\nexungo."],
  ["exundanter", "<big>exundanter</big>\n\n<b>exundanter</b>\nв изобилии, во множестве <i>Eccl</i>."],
  ["exundantia", "<big>exundantia</big>\n\n<b>exundantia, ae</b> <i>f</i> <i>Ambr, Aug</i>\n= exundatio."],
  ["exundatio", "<big>exundatio</big>\n\n<b>exund&#257;tio, &#333;nis</b> <i>f</i> [exundo]\nразлив (aquarum <i>Sen</i>; fluminum <i>PM</i>)."],
  ["exundo", "<big>exundo</big>\n\n<b>ex–undo, &#257;v&#299;, &#257;tum, &#257;re</b>\n<b>1)</b> выступать из берегов, разливаться (fons exundat <i>PM</i>); быть затопляемым (solum exundans sanguine <i>Sen</i>);\n<b>2)</b> быть наносимым <i>или</i> быть прибиваемым (волнами) (vi tempestatum in adversa litora <i>T</i>);\n<b>3)</b> обильно вытекать, хлынуть (cruor exundat trunco <i>SenT</i>);\n<b>4)</b> бить ключом, бить через край (ex multa eruditione exundat eloquentia <i>T</i>); клокотать (exundat furor <i>SenT</i>);\n<b>5)</b> выпускать клубами (densum fumum <i>Sil</i>)."],
  ["exungo", "<big>exungo</big>\n\n<b>ex–ungo (exunguo), —, &#363;nctus, ere</b> <i>и</i> <b>exung(u)or, —, ung(u)&#299;</b> <i>depon.</i>\n<b>1)</b> натирать благовонными мазями <i>Pl</i>;\n<b>2)</b> <i>шутл.</i> расточать на благовония, разоряться <i>Pl</i>."],
  ["exunguis", "<big>exunguis</big>\n\n<b>ex–unguis, e</b>\nлишённый когтей (bestia <i>Tert</i>)."],
  ["exunguo", "<big>exunguo</big>\n\n<b>exunguo, exung(u)or</b> <i>v. l.</i>\n= exungo."],
  ["exuo", "<big>exuo</big>\n\n<b>exuo, u&#299;, &#363;tum, ere</b> [<i>одного корня с</i> induo]\n<b>1)</b> извлекать, вынимать (aliquid aliqua re <i>или</i> de, ex aliqua re <i>Pl, V etc.</i>): e. ensem vagina <i>St</i> вынуть меч из ножон || освобождать, выпутывать (se ex laqueis <i>C</i>): vitiis exui <i>Sen</i> освободиться от пороков || отделять (hordea de palea <i>O</i>);\n<b>2)</b> снимать, скидывать (vestem <i>VP</i>): unum exuta pedem (<i>acc. gr.</i>) vinclis <i>V</i> с одной разутой ногой; se e. <i>Pt</i> раздеваться; e. aliquem veste <i>Su</i> снять с кого-л. одежду || сбрасывать (servitutem <i>L</i>); обнажать (lacertos <i>V</i>; membra pellibus <i>H</i>); стряхивать, свергать (jugum <i>L</i>);\n<b>3)</b> оставлять (antiquos mores <i>L</i>); отрекаться, отворачиваться (e. patriam <i>T</i>); отрешаться, отбрасывать (omnem humanitatem <i>C</i>): exue istam mentem <i>V</i> брось эти мысли; e. hominem ex homine <i>C</i> отречься от всего человеческого;\n<b>4)</b> отказываться, расторгать (amicitiam <i>T</i>); e. obsequium in aliquem <i>T</i> отказывать в повиновении кому-л. || не соблюдать, не выполнять (jussa, promissa, pacta <i>T</i>); нарушать (pacem, fidem <i>T</i>); отделываться, устранять (aliquem <i>T</i>): exuto Lepido <i>T</i> когда Лепид сошёл с (политической) сцены (с падением Лепида); e. animam <i>O</i> утратить жизнь, умереть;\n<b>5)</b> освобождаться (e. feritatem <i>O, Sen</i>); отнимать, лишать (aliquem aliqua re): exutus armis <i>Cs</i> обезоруженный; exutus castris <i>L</i> вытесненный из (своего) лагеря; e. alicui aliquid ex animo <i>C</i> разубедить (разуверить) кого-л. в чём-л.; mihi ex animo exui non potest <i>C</i> я не могу отрешиться от мысли (уверенности); e. silvestrem animum <i>V</i> утрачивать дикость; sapientia vanitatem exuit mentibus <i>Sen</i> мудрость освобождает умы от тщеславия."],
  ["exuper-", "<big>exuper-</big>\n\n<b>exuper-</b> <i>v. l.</i>\n= exsuper-."],
  ["exurdo", "<big>exurdo</big>\n\n<b>exurdo</b> <i>v. l.</i>\n= exsurdo."],
  ["exurgeo", "<big>exurgeo</big>\n\n<b>ex–urgeo, —, —, &#275;re</b>\nвыжимать, выдавливать (quidquid umoris est <i>Pl</i>)."],
  ["exurgo", "<big>exurgo</big>\n\n<b>exurgo</b> <i>v. l.</i>\n= exsurgo."],
  ["exuro", "<big>exuro</big>\n\n<b>ex–&#363;ro, uss&#299;, ustum, ere</b>\n<b>1)</b> выжигать, сжигать (aliquem vivum <i>C</i>; classem <i>V</i>); сгорать, догорать (lucerna exusta est <i>Vtr</i>); нагревать, накаливать (antra caminis <i>O</i>);\n<b>2)</b> разъедать (vis veneni exurit ferrum <i>QC</i>); объедать, истреблять, опустошать (eruca exurit semina morsu <i>Col</i>); высушивать (exusta palus <i>V</i>); обжигать, опалять (sol graciles exurit artus <i>Tib</i>);\n<b>3)</b> жечь, мучить, томить (exustus siti <i>C</i>); распалять любовью (aliquem <i>Tib</i>)."],
  ["exustio", "<big>exustio</big>\n\n<b>exustio, &#333;nis</b> <i>f</i> [exuro]\n<b>1)</b> сжигание, сожжение, пожар <i>C etc.</i>;\n<b>2)</b> палящий жар, зной (solis <i>PM, CA</i>)."],
  ["exustus", "<big>exustus</big>\n\n<b>exustus, a, um</b> <i>part. pf. к</i>\nexuro."],
  ["exutio", "<big>exutio</big>\n\n<b>exutio, &#333;nis</b> <i>f</i> [exuo]\nисключение, изъятие: e. a venia <i>Ambr</i> те, которым нет прощения."],
  ["exutus", "<big>exutus</big>\n\n<b>ex&#363;tus, a, um</b> <i>part. pf. к</i>\nexuo."],
  ["exuviae", "<big>exuviae</big>\n\n<b>exuviae, &#257;rum</b> <i>f</i> [exuo]\n<b>1)</b> снятая одежда, одежды <i>Pl, V, Su etc.</i>;\n<b>2)</b> сбруя (<i>sc.</i> equi <i>Ap</i>);\n<b>3)</b> волосяной покров, волосы (capitis <i>SenT</i>; verticis <i>Ctl</i>);\n<b>4)</b> снятые с врага доспехи, добыча (hostiles <i>Tib</i>): e. nauticae <i>C</i> снятые с вражеских кораблей носы; exuvias indutus Achillis <i>V</i> (Гектор) в доспехах Ахилла;\n<b>5)</b> (снятая) шкура (leonis <i>V</i>): e. bubulae <i>Pl</i> ремни из бычачьей кожи;\n<b>6)</b> сброшенная старая кожа, линовище (serpentis <i>Su</i>): exuvias ponere <i>V, Lcn</i> линять."],
  ["exuvium", "<big>exuvium</big>\n\n<b>exuvium, &#299;</b> <i>n</i> <i>Prp</i>\n= exuviae 4."],
  ["exvaporo", "<big>exvaporo</big>\n\n<b>exvap&#333;ro</b>\n= evaporo."],
  ["exveho", "<big>exveho</big>\n\n<b>exveho</b> <i>Dig</i>\n= eveho."]
]