export default [
  ["N", "<big>N</big>\n\n<b>N, n</b>\n<i>четырнадцатая буква латинского алфавита; в сокращениях</i>: N. = Numerius (<i>praenomen</i>), numero, numine, nomine, natus, nepos, novus, Nonae, natione, noster (nostro) <i>etc.</i>; N. L. = non liquet; N. M. V. = nobilis memoriae vir; NN. BB. = nobilissimi."],
  ["Nababes", "<big>Nababes</big>\n\n<b>Nababes, um</b> <i>m</i>\n<i>племя в Мавритании Кесарийской</i> <i>PM</i>."],
  ["Nabalia", "<big>Nabalia</big>\n\n<b>Nabalia, ae</b> <i>f</i>\nНабалия, <i>река в сев.-зап. Германии</i> <i>T</i>."],
  ["Nabathae", "<big>Nabathae</big>\n\n<b>Nabat(h)ae, arum</b> <i>m</i> <i>SenT</i>\n= Nabat(h)aei."],
  ["Nabathaei", "<big>Nabathaei</big>\n\n<b>Nabat(h)aei, orum</b> <i>m</i>\nнабатеи, <i>народ в Каменистой Аравии, к югу от Мёртвого моря</i> (<i>с главн. городом Петра</i>) <i>PM, T</i>."],
  ["Nabathaeus", "<big>Nabathaeus</big>\n\n<b>Nabat(h)aeus, a, um</b> [Nabat(h)aei]\nнабатейский, <i>поэт. тж.</i> аравийский <i>или</i> восточный <i>O, J</i>."],
  ["Nabis", "<big>Nabis</big>\n\n<b>Nabis, idis</b> <i>m</i>\nНабид, <i>тиран спартанский с 206 г. до н. э., сначала сторонник, затем противник римлян; в 192 г. до н. э. убит предводителем этолян Алексаменом</i> <i>L, Eutr</i>."],
  ["nablium", "<big>nablium</big>\n\n<b>nablium, i</b> <i>n</i>\nфиникийская арфа (10-12-и струнная) <i>O</i>."],
  ["nablum", "<big>nablum</big>\n\n<b>nablum, i</b> <i>n</i> <i>Vlg</i>\n= nablium."],
  ["nacca", "<big>nacca</big>\n\n<b>nacca, ae</b> <i>m</i> (<i>греч.</i>; <i>лат.</i> fullo)\nваляльщик <i>Ap</i>."],
  ["naccinus", "<big>naccinus</big>\n\n<b>naccinus, a, um</b> [nacca]\nсвойственный (присущий) валяльщику (truculentia <i>Ap</i>)."],
  ["nactus", "<big>nactus</big>\n\n<b>nactus, a, um</b> <i>part. pf. к</i>\nnanciscor."],
  ["nae", "<big>nae</big>\n\n<b>nae</b> <i>v. l.</i>\n= ne IV."],
  ["naenia", "<big>naenia</big>\n\n<b>naenia, ae</b> <i>f</i> <i>v. l.</i>\n= nenia."],
  ["Naevia porta", "<big>Naevia porta</big>\n\n<b>Naevia porta, ae</b> <i>f</i>\nНевиевы ворота (<i>в южн. части Рима, между Авентинским и Целийским холмами</i>) <i>L</i>."],
  ["Naevianus", "<big>Naevianus</big>\n\n<b>Naevianus, a, um</b> [Naevius]\nневиев (modi <i>C</i>)."],
  ["naevius 1", "<big>naevius</big>\n\n<b>I naevius, a, um</b> [naevus]\nс родимым пятном на теле <i>Eccl</i>."],
  ["Naevius 2", "<big>Naevius</big>\n\n<b>II Naevius, a, um</b>\nНевий, <i>римск. nomen; наиболее известны</i>:\n<b>1)</b> Cn. N., <i>родом из Кампании, римск. драматический и эпический писатель II-й половины III в. до н. э., автор эпической поэмы о I Пунической войне</i> <i>C, AG</i>;\n<b>2)</b> C. N., <i>сторонник Суллы, противник Цицерона в процессе П. Квинкция в 81 г. до н. э.</i> <i>C</i>;\n<b>3)</b> P. N. Turpio, <i>один из приближённых Верреса</i> <i>C</i>."],
  ["naevulus", "<big>naevulus</big>\n\n<b>naevulus, i</b> <i>m</i> [<i>demin. к</i> naevus]\nродимое пятнышко, родинка <i>AG, Ap</i>."],
  ["naevus", "<big>naevus</big>\n\n<b>naevus, i</b> <i>m</i> [<i>из</i> <i>арх.</i> gnaevus <i>от</i> gigno]\nродимое пятно (nullus in egregio corpore n. <i>O</i>); <i>перен.</i> пятно, клеймо <i>Eccl</i>."],
  ["Naharvali", "<big>Naharvali</big>\n\n<b>Naharvali</b> <i>или</i> <b>Nahanarvali, orum</b> <i>m</i>\nнагарвалы <i>и</i> наганарвалы, <i>племя в сев.-вост. Германии</i> <i>T</i>."],
  ["Naias", "<big>Naias</big>\n\n<b>Naias, adis</b> <i>и</i> <b>Nais, idis</b> <i>f</i> (<i>греч.</i>)\n<b>1)</b> наяда, <i>водяная нимфа</i> <i>V, O</i>; нимфа (<i>вообще</i>) <i>O</i>;\n<b>2)</b> <i>поэт.</i> вода (Naida Bacchus amat <i>Tib</i>)."],
  ["Naicus", "<big>Naicus</big>\n\n<b>Naicus, a, um</b> [Naias]\nнаядин (dona <i>Prp</i>)."],
  ["Nais", "<big>Nais</big>\n\n<b>Nais, idis</b> <i>f</i> <i>v. l.</i>\n= Naias."],
  ["nam", "<big>nam</big>\n\n<b>nam</b>\n<b>1.</b> <i>adv.</i> действительно, в самом деле, поистине: n. mehercule ita agamus <i>C</i> да, действительно, давай так и сделаем;\n<b>2.</b> <i>conj.</i>\n<b>1)</b> ибо, так как: n. de Carthagine tacere melius puto, quam parum dicere <i>Sl</i> ибо о Карфагене лучше, по-моему, промолчать, чем сказать мало;\n<b>2)</b> а именно: duplex est ratio veri reperiendi, n.: aut..., aut... <i>C</i> имеется два способа открывать истину, а именно: или..., или...;\n<b>3)</b> дело в том, что: n. omnis civitas Helvetia in quattuor pagos divisa est <i>Cs</i> дело в том, что всё Гельветское государство было разделено на четыре области;\n<b>4)</b> ведь, да ведь: n. profecto memoria tenetis complures in Capitolio res de caelo esse percussas <i>C</i> ведь вы, конечно, помните, что многое на Капитолии было повреждено ударом молнии;\n<b>5)</b> но, однако <i>или</i> зато: Picenis cedunt pomis Tiburtia suco, n. facie praestant <i>H</i> тибуртинские плоды уступают в сочности пиценским, но зато превосходят их внешним видом;\n<b>3.</b> <i>в вопросах</i> (<i>тж. постпозитивно и слитно</i>) же, но: n. quid ago? <i>V</i> но что же мне делать? ; n. quid ita? <i>Ter</i> а что? (<i>т. е.</i> почему ты спрашиваешь?); n. quam ob rem? <i>Pl</i> да почему же?; ubinam? <i>C</i> где же?; quianam? <i>V</i> но почему?; n., obsecro, unde? <i>Pl</i> но откуда же, скажи на милость?"],
  ["Namnetes", "<big>Namnetes</big>\n\n<b>Namnetes, um</b> <i>m</i>\nнамнеты, <i>кельт. племя на сев. берегу реки Лигер, с главн. городом Condivincum</i> (<i>ныне</i> Nantes) <i>Cs</i>."],
  ["namque", "<big>namque</big>\n\n<b>namque</b> <i>conj.</i> [<i>intens. к</i> nam 2]\nибо действительно, ибо ведь, так как в самом деле <i>Pt, C etc.</i>"],
  ["nana", "<big>nana</big>\n\n<b>nana, ae</b> <i>f</i> [nanus]\nкарлица <i>Lampr</i>."],
  ["nancior", "<big>nancior</big>\n\n<b>nancior, —, iri</b> <i>LXIIT</i>\n= nanciscor."],
  ["nanciscor", "<big>nanciscor</big>\n\n<b>nanciscor, nactus</b> (<i>арх.</i> <b>nanctus</b>) <b>sum, cisei</b> <i>depon.</i>\n<b>1)</b> случайно получать, становиться невольным обладателем, оказываться владельцем (n. summam potestatem <i>Sl</i>): habeas, ut nanctus es <i>Pl</i> оставайся при том, что у тебя есть; nactus occasionem <i>Pt</i> воспользовавшись случаем; majorem spem impetrandi nactus sum <i>C</i> моя надежда на успех моей просьбы возросла; libertatem nactus <i>Pt</i> обретя свободу; spem alicujus rei nactus <i>C</i> возымев виды на что-л.; n. spatium <i>Cs</i> выиграть время || <i>перен.</i> схватывать: n. febrim <i>Su</i> заболеть лихорадкой;\n<b>2)</b> находить, наталкиваться, попадать (n. Gallorum castra <i>L</i>): n. aliquem otiosum <i>C</i> застать кого-л. в часы его досуга; maleficam naturam n. <i>Nep</i> испытать на себе превратности природы."],
  ["nanctus", "<big>nanctus</big>\n\n<b>nanctus, a, um</b> <i>арх. part. pf. к</i>\nnanciscor."],
  ["nanque", "<big>nanque</big>\n\n<b>nanque</b> <i>conj.</i> <i>v. l.</i>\n= namque."],
  ["nans", "<big>nans</big>\n\n<b>nans, nantis</b>\n<b>1.</b> <i>part. praes. к</i> no;\n<b>2.</b> <i>m</i> пловец (rari nantes in gurgite vasto <i>V</i>)."],
  ["Nantuates", "<big>Nantuates</big>\n\n<b>Nantuates, ium</b> <i>m</i>\nнантуаты, <i>кельт. племя в районе нын. Савойи</i> <i>Cs, PM</i>."],
  ["nanus", "<big>nanus</big>\n\n<b>nanus, i</b> <i>m</i> (<i>греч.</i>; <i>лат.</i> pumilio)\n<b>1)</b> карлик <i>Prp, J etc.</i>;\n<b>2)</b> маленький плоский сосуд для воды, фляга <i>Vr</i>."],
  ["nanxitor", "<big>nanxitor</big>\n\n<b>nanxitor</b> <i>LXIIT fut. II pass. к</i>\nnancior."],
  ["Napaeae", "<big>Napaeae</big>\n\n<b>Napaeae, arum</b> <i>f</i>\n<i>нимфы лесов и долин</i> <i>V etc.</i>"],
  ["napaeus", "<big>napaeus</big>\n\n<b>napaeus, a, um</b> (<i>греч.</i>)\nиз лесной (горной) долины (nymphae <i>Col</i>)."],
  ["naphtha", "<big>naphtha</big>\n\n<b>naphtha, ae</b> <i>f</i> (<i>егип.</i>)\nнефть <i>PM, Amm</i>."],
  ["naphthas", "<big>naphthas</big>\n\n<b>naphthas, ae</b> <i>m</i> <i>Sl</i>\n= naphtha."],
  ["napina", "<big>napina</big>\n\n<b>napina, ae</b> <i>f</i> [napus]\n(<i>sc.</i> terra) поле, засаженное брюквой <i>Col</i>."],
  ["napocaulis", "<big>napocaulis</big>\n\n<b>napo–caulis, is</b> <i>f</i>\nкольраби <i>Is</i>."],
  ["napus", "<big>napus</big>\n\n<b>napus, i</b> <i>m</i>\nбрюква <i>Col, CC, PM etc.</i>"],
  ["napy", "<big>napy</big>\n\n<b>napy, pyos</b> <i>n</i> <i>PM</i>\n= sinapi."],
  ["Nar", "<big>Nar</big>\n\n<b>Nar, Naris</b> <i>m</i>\nНар, <i>левый приток Тибра в области сабинян</i> (<i>ныне</i> Nera) <i>C, V, O</i>."],
  ["Narbo", "<big>Narbo</big>\n\n<b>Narbo, onis</b> <i>m</i> <i>и</i> <b>Narbona, ae</b> <i>f</i>\nНарбон, <i>торговый город в Gallia Narbonensis на реке Атаке</i> (<i>ныне</i> Narbonne) <i>Cs, C, Sl etc.</i>"],
  ["Narbonensis", "<big>Narbonensis</big>\n\n<b>Narbonensis, e</b> [Narbo(na)]\nнарбонский <i>C, PM etc.</i>"],
  ["narce", "<big>narce</big>\n\n<b>narce, es</b> <i>f</i> (<i>греч.</i>; <i>лат.</i> torpor)\nоцепенение, оглушённость <i>PM</i>."],
  ["narcissinus", "<big>narcissinus</big>\n\n<b>narcissinus, a, um</b> [narcissus]\nнарциссовый (unguentum <i>PM</i>)."],
  ["Narcissus 1", "<big>Narcissus</big>\n\n<b>I Narcissus, i</b> <i>m</i>\nНарцисс:\n<b>1)</b> <i>сын беотийского речного бога Кефиса и нимфы Лириопы, отвергший любовь Эхо; увидев свое изображение в воде, он так влюбился в него, что зачах от тоски и превратился в цветок, сохранивший имя Нарцисс</i> <i>O</i>;\n<b>2)</b> <i>вольноотпущенник и приближённый императора Клавдия, виновник падения Мессалины; умерщвлён по приказанию Агриппины</i> <i>T, J</i>."],
  ["narcissus 2", "<big>narcissus</big>\n\n<b>II narcissus, i</b> <i>m</i> (<i>греч.</i>)\nнарцисс: n. purpureus <i>V</i> пурпурный нарцисс (белый с красной каймой) (<i>Narcissus poeticus</i>, L.); n. sera comans <i>V</i> поздний нарцисс (<i>Narcissus sentinus</i>, L.)."],
  ["nardinum", "<big>nardinum</big>\n\n<b>nardinum, i</b> <i>n</i>\nприправленное нардом вино <i>Pl</i> (<i>v. l.</i>)."],
  ["nardinus", "<big>nardinus</big>\n\n<b>nardinus, a, um</b> [nardus]\nнардовый, приготовленный из нарда (unguentum <i>PM</i>); пахнущий нардом (pirum <i>PM</i>); сдобренный нардом (vinum <i>Pl</i> — <i>v. l.</i>)."],
  ["nardum", "<big>nardum</big>\n\n<b>nardum, i</b> <i>n</i> <i>H, Tib, Prp</i>\n= nardus 2."],
  ["nardus", "<big>nardus</big>\n\n<b>nardus, i</b> <i>f</i> (<i>греч.</i>)\n<b>1)</b> нард, <i>название нескольких благовонных растений</i>: восточный нард (<i>Spica Nardi</i>, L.), галльский нард (<i>Valeriana Celtica</i>, L.) <i>и др.</i> <i>PM etc.</i>;\n<b>2)</b> нардовое масло, нардовый бальзам (ampulla nardi <i>Pt</i>)."],
  ["nare 1", "<big>nare</big>\n\n<b>I nare</b> <i>inf. к</i>\nno."],
  ["nare 2", "<big>nare</big>\n\n<b>II nare</b> <i>abl. sg. к</i>\nnaris."],
  ["nares", "<big>nares</big>\n\n<b>nares, ium</b> <i>f pl. к</i>\nnaris."],
  ["narinosus", "<big>narinosus</big>\n\n<b>narinosus, a, um</b> [naris]\nшироконосый <i>Lact</i> (<i>v. l.</i>)."],
  ["naris", "<big>naris</big>\n\n<b>naris, is</b> <i>f</i>\n<b>1)</b> ноздря, <i>чаще</i> <i>pl.</i> нос: de nare loqui <i>Pers</i> говорить в нос; nares corrugare <i>Q, H</i> морщить нос;\n<b>2)</b> обоняние, чутьё: copia narium <i>H</i> множество благоуханий (цветов);\n<b>3)</b> сообразительность, смётка: homo emunctae n. <i>H, Ph</i> тонкий, остроумный человек; homo obesae n. <i>H</i> тупой, несообразительный человек; naribus uti <i>H</i> издеваться, насмехаться; acutae nares <i>H</i> остроты, тонкий юмор; uncae nares <i>Pers</i> насмешки;\n<b>4)</b> отверстие (nares fossae <i>Vop</i>)."],
  ["Naristi", "<big>Naristi</big>\n\n<b>Naristi, orum</b> <i>m</i>\nнаристы, <i>германск. племя свебской группы в области нын. Верхнего Пфальца</i> <i>T</i>."],
  ["narita", "<big>narita</big>\n\n<b>narita, ae</b> <i>f</i> (<i>греч.</i>)\nнарита, <i>морской моллюск</i> <i>Pl</i>."],
  ["Narnia", "<big>Narnia</big>\n\n<b>Narnia, ae</b> <i>f</i>\nНарния, <i>город на южн. берегу реки Nar, на via Flaminia</i> (<i>ныне</i> Narni) <i>L, T</i>."],
  ["Narniensis", "<big>Narniensis</big>\n\n<b>Narniensis, e</b> [Narnia]\nнарнийский <i>L, PM, PJ</i>."],
  ["narrabilis", "<big>narrabilis</big>\n\n<b>narrabilis, e</b> [narro]\nдостойный (могущий) быть рассказанным: lingua, sile, non est ultra narrabile quidquam <i>O</i> молчи, (мой) язык, говорить больше не о чем."],
  ["narratio", "<big>narratio</big>\n\n<b>narratio, onis</b> <i>f</i> [narro]\nрассказ, повествование (verisimilis <i>C</i>; brevis <i>Ph</i>)."],
  ["narratiuncula", "<big>narratiuncula</big>\n\n<b>narratiuncula, ae</b> <i>f</i> [<i>demin. к</i> narratio]\nнебольшой рассказ, анекдот <i>Q, PJ</i>."],
  ["narrator", "<big>narrator</big>\n\n<b>narrator, oris</b> <i>m</i> [narro]\nрассказчик, повествователь (non exornator rerum, sed tantummodo n. <i>C</i>)."],
  ["narratorius", "<big>narratorius</big>\n\n<b>narratorius, a, um</b> [narro]\nповествовательный <i>Aug</i>."],
  ["narratus", "<big>narratus</big>\n\n<b>narratus, us</b> <i>m</i> [narro]\nрассказ, повествование <i>O, Ap</i>."],
  ["narro", "<big>narro</big>\n\n<b>narro, avi, atum, are</b> [<i>из</i> *gnaro = gnarum facere]\n<b>1)</b> рассказывать, сообщать, извещать, уведомлять, повествовать (alicui aliquid <i>или</i> de aliqua re <i>C etc.</i>): n. aliquid ordine <i>Ter</i> рассказывать что-л. по порядку; bene (male) narras <i>C</i> ты приносишь приятную (неприятную) весть, это мне приятно (неприятно) слышать; narrant <i>PM</i> <i>или</i> narratur <i>PM, T</i> рассказывают, передают;\n<b>2)</b> говорить: narra mihi <i>C</i> скажи-ка мне; quid narras? <i>C</i> что скажешь?; narro tibi <i>C</i> позволь тебе сказать; n. aliquem (aliquid) <i>C</i> говорить о ком-л. (о чём-л.);\n<b>3)</b> посвящать (librum alicui n. <i>PM</i>)."],
  ["Nartes", "<big>Nartes</big>\n\n<b>Nartes, ium</b> <i>m</i>\nжители берегов p. Nar <i>PM</i>."],
  ["narthecia", "<big>narthecia</big>\n\n<b>narthecia, ae</b> <i>f</i>\nнизкорослая разновидность растения narthex <i>PM</i>."],
  ["narthecium", "<big>narthecium</big>\n\n<b>narthecium, i</b> <i>n</i> (<i>греч.</i>)\nцилиндрическая коробочка, ларчик (<i>преим.</i> для косметических средств) <i>C, M</i>."],
  ["narthex", "<big>narthex</big>\n\n<b>narthex, thecis</b> (<i>acc.</i> theca) <i>m</i> (<i>греч.</i>)\nкуст растения ferula (<i>см.</i>) <i>PM</i>."],
  ["narus", "<big>narus</big>\n\n<b>narus, a, um</b> <i>C</i>\n= gnarus."],
  ["Narycia", "<big>Narycia</big>\n\n<b>Narycia, ae</b> <i>f</i>\nНарикия, <i>город локров, метрополия их колонии в Бруттии</i> <i>O</i>."],
  ["Narycius", "<big>Narycius</big>\n\n<b>Narycius, a, um</b> [Naryx]\nнарикский: heros N. <i>O</i> = Эант (Аякс), <i>сын Оилея; перен.</i> локрский <i>или</i> бруттийский (pix <i>V etc.</i>)."],
  ["Narycum", "<big>Narycum</big>\n\n<b>Narycum, i</b> <i>n</i> <i>и</i> <b>Naryx, ycis</b> <i>f</i>\nНарик:\n<b>1)</b> <i>город локров опунтских</i> (<i>в средней Греции</i>) <i>PM</i>;\n<b>2)</b> <i>город в Бруттии</i> (<i>колония локров опунтских</i>) <i>O</i>."],
  ["Nasamones", "<big>Nasamones</big>\n\n<b>Nasamones, um</b> <i>m</i>\nнасамоны, <i>племя в Большом Сирте, к юго-зап. от Киренаики</i> <i>PM, Lcn, Sil</i>."],
  ["Nasamoniacus", "<big>Nasamoniacus</big>\n\n<b>Nasamoniacus, a, um</b> [Nasamones]\nнасамонский <i>O</i>, <i>перен.</i> африканский <i>St</i>."],
  ["nascentia 1", "<big>nascentia</big>\n\n<b>I nascentia, ae</b> <i>f</i> [nascor]\nрождение <i>Vtr</i>."],
  ["nascentia 2", "<big>nascentia</big>\n\n<b>II nascentia, ium</b> <i>n</i> [nascor]\nорганический мир, <i>преим.</i> растения <i>Vtr, Sen</i>."],
  ["nascibilis", "<big>nascibilis</big>\n\n<b>nascibilis, e</b> [nascor]\nмогущий родиться (возникнуть) <i>Tert</i>."],
  ["nasciturus", "<big>nasciturus</big>\n\n<b>nasciturus, a, um</b> <i>Pall, Sid, Eccl part. fut. к</i>\nnascor."],
  ["nasco", "<big>nasco</big>\n\n<b>nasco, —, —, ere</b> <i>Cato</i>\n(<i>v. l.</i>) = nascor."],
  ["nascor", "<big>nascor</big>\n\n<b>nascor</b> (<i>арх.</i> <b>gnascor</b>), <b>natus sum, nasci</b> <i>depon.</i> [<i>одного корня с</i> gigno]\n<b>1)</b> рождаться, происходить (lex universi est, quae jubet n. et mori <i>PS</i>): qui nascentur ab illis (natis) <i>V</i> отдалённые потомки; n. (in) aliquo signo <i>Pt</i> родиться под каким-л. знаком (звездой); n. aliquo (<i>реже</i> ex, de <i>или</i> ab aliquo) происходить (вести свой род) от кого-л. (aliquo patre <i>C etc.</i>; de stirpe dei <i>O</i>; a principibus <i>T</i>; e Phoebo <i>O</i>; amplissima familia <i>Cs</i>; antiquo genere <i>Nep</i>);\n<b>2)</b> возникать, зарождаться, брать начало, начинаться (Ganges in montibus nascitur <i>PM</i>; a flumine collis nascitur <i>Cs</i>): nascens Nilus <i>Lcn</i> истоки Нила; ita nascetur exordium <i>C</i> таково будет (наше) вступление; omnia domi nascuntur <i>Ap</i> всё производится дома (<i>о натуральном хозяйстве</i>); in sermone nato <i>Su</i> в завязавшейся беседе;\n<b>3)</b> следовать, вытекать (ex hoc nascitur, ut... <i>C</i>);\n<b>4)</b> восходить, появляться: nascens luna <i>H</i> новолуние <i>или</i> восход луны;\n<b>5)</b> вырастать (Graeci nati in litteris <i>C</i>);\n<b>6)</b> находиться (plumbum ibi nascitur <i>Cs</i>); водиться, обитать (in ea silva multae ferae nascuntur <i>PM</i>). — <i>См. тж.</i> natus I-III."],
  ["nasica 1", "<big>nasica</big>\n\n<b>I nasica, ae</b> <i>m</i> [nasus]\nостроносый человек <i>Eccl</i>."],
  ["Nasica 2", "<big>Nasica</big>\n\n<b>II Nasica, ae</b> <i>m</i>\n(&laquo;остроносый&raquo;) Назика, <i>cognomen в роде Сципионов; наиболее известны</i>:\n<b>1)</b> P. Cornelius Scipio N., <i>успешно воевал в Испании в 193 г. до н. э., консул в 191 г. до н. э.</i> <i>L</i>;\n<b>2)</b> P. Cornelius Scipio N. Corculum, <i>зять Сципиона Африканского Старшего; будучи в 155 г. до н. э. консулом, покорил далматиев</i> <i>L, AV</i>;\n<b>3)</b> P. Cornelius Scipio N. Serapio, <i>консул в 138 г. до н. э., противник Тиберия Гракха, умер в 132 г. до н. э.</i> <i>VM</i>."],
  ["Nasidianus", "<big>Nasidianus</big>\n\n<b>Nasidianus, a, um</b> [Nasidius]\nназидиев (naves <i>Cs</i>)."],
  ["Nasidienus Rufus", "<big>Nasidienus Rufus</big>\n\n<b>Nasidienus Rufus</b> <i>m</i>\nНазидиен Руф, <i>богатый римск. выскочка времён Горация</i> <i>H</i>."],
  ["Nasidius", "<big>Nasidius</big>\n\n<b>Nasidius, a, um</b>\nНазидий, <i>римск. nomen</i> <i>C, Cs</i>."],
  ["nasiterna", "<big>nasiterna</big>\n\n<b>nasiterna, ae</b> <i>f</i> <i>v. l.</i>\n= nassiterna."],
  ["Naso", "<big>Naso</big>\n\n<b>Naso, onis</b> <i>m</i>\n(&laquo;носатый&raquo;) Назон, <i>cognomen поэта Овидия.</i>"],
  ["Nasos", "<big>Nasos</big>\n\n<b>Nasos (Nasus), i</b> <i>f</i> (<i>дорич.</i> &laquo;остров&raquo;)\nНасос, <i>часть Сиракуз вокруг о-ва Ortygia</i> <i>L</i>."],
  ["nassa", "<big>nassa</big>\n\n<b>nassa, ae</b> <i>f</i>\n<b>1)</b> верша (<i>рыболовная снасть</i>) <i>PM, Sil</i>;\n<b>2)</b> <i>перен.</i> сеть, петля, западня (ex hac nassa exire constitui <i>C</i>)."],
  ["nassiterna", "<big>nassiterna</big>\n\n<b>nassiterna, ae</b> <i>f</i>\nсосуд для поливки земли, лейка <i>Pl</i>."],
  ["nasturcium", "<big>nasturcium</big>\n\n<b>nasturcium, i</b> <i>n</i> <i>v. l.</i>\n= nasturtium."],
  ["nasturtium", "<big>nasturtium</big>\n\n<b>nasturtium (nasturcium), i</b> <i>n</i> <i>бот.</i>\nнастурций, <i>род кресса</i> <i>Vr, C, CC etc.</i>"],
  ["nasum", "<big>nasum</big>\n\n<b>nasum, i</b> <i>n</i>\n<i>редко</i> <i>LM, Pl, Vtr</i> = nasus II."],
  ["Nasus 1", "<big>Nasus</big>\n\n<b>I Nasus, i</b> <i>f</i> <i>v. l.</i>\n= Nasos."],
  ["nasus 2", "<big>nasus</big>\n\n<b>II nasus, i</b> <i>m</i>\n<b>1)</b> нос (acutus <i>Pl</i>; rectus <i>Hier</i>; aduncus <i>Ter etc.</i>): mihi anima in naso est <i>погов. Pt</i> = у меня душа ушла в пятки; naso clamare <i>Pl</i> храпеть;\n<b>2)</b> обоняние, тонкое чутьё (nullus n. alicui est <i>H</i>);\n<b>3)</b> остроумие (non cuicunque datum est habere nasum <i>M</i>); насмешливость, подтрунивание (n. Atticus <i>Sen</i>): aliquem (aliquid) suspendere naso (adunco) <i>H</i> насмехаться, издеваться над кем-л. (чём-л.);\n<b>4)</b> носик (calix nasorum quattuor <i>J</i>)."],
  ["nasute", "<big>nasute</big>\n\n<b>nasute</b> [nasutus]\nостро, колко, насмешливо, язвительно <i>Sen, PM</i>."],
  ["nasutus", "<big>nasutus</big>\n\n<b>nasutus, a, um</b> [nasus]\n<b>1)</b> носатый <i>LM, H</i>;\n<b>2)</b> остроумный <i>Sen</i>;\n<b>3)</b> насмешливый <i>Ph, M</i>."],
  ["nata", "<big>nata</big>\n\n<b>nata, ae</b> <i>f</i> (<i>dat. abl. pl.</i> <i>тж.</i> abus) [natus II]\nдочь (maxima natarum Priami <i>V</i>)."],
  ["natabulum", "<big>natabulum</big>\n\n<b>natabulum, i</b> <i>n</i> [nato]\nместо для плавания (gratissima sanis natabula <i>Ap</i>)."],
  ["natale", "<big>natale</big>\n\n<b>natale, is</b> <i>n</i>\nместо рождения <i>PM</i>."],
  ["natalicia", "<big>natalicia</big>\n\n<b>natalicia, ae</b> <i>f</i> [natalicius]\n(<i>sc.</i> cena) пир в честь дня рождения (nataliciam alicui dare <i>C</i>)."],
  ["natalicium", "<big>natalicium</big>\n\n<b>natalicium, i</b> <i>n</i>\nподарок ко дню рождения <i>Cens</i>."],
  ["natalicius", "<big>natalicius</big>\n\n<b>natalicius, a, um</b> [natalis]\nотносящийся к рождению, связанный с днём рождения (munus <i>VM</i>; dapes <i>M</i>): dies n. <i>M</i> день рождения; sidera (praedicta) natalicia <i>C</i> гороскоп, астрологические предсказания."],
  ["natalis 1", "<big>natalis</big>\n\n<b>I natalis, e</b> [natus]\n<b>1)</b> относящийся к рождению, связанный с моментом рождения (hora <i>H</i>): dies n. <i>Pl, C, T</i> день рождения, <i>тж.</i> день основания (coloniae <i>C</i>) <i>или</i> избавления (urbis <i>C</i>), <i>а тж.</i> годовщина (reditus mei <i>C</i>);\n<b>2)</b> родной, отчий, отечественный (solum <i>Sen</i>; humus <i>O</i>);\n<b>3)</b> покровительствующий рождению (Juno <i>Tib</i>);\n<b>4)</b> прирождённый, врождённый (decus <i>VF</i>; morbus <i>Man</i>)."],
  ["natalis 2", "<big>natalis</big>\n\n<b>II natalis, is</b> (<i>abl. sg.</i> <i>преим.</i> i) <i>m</i>\n<b>1)</b> (<i>sc.</i> dies) день рождения: aliquid alicui natali suo donare <i>Pt</i> подарить что-л. кому-л. ко дню его рождения; natales grate memorare <i>H</i> с благодарностью вести счёт дням своего рождения, <i>т. е.</i> не страшиться наступающей старости;\n<b>2)</b> (<i>sc.</i> locus) место рождения, родина <i>PM</i>;\n<b>3)</b> бог рождения, гений-хранитель <i>Tib, O</i>;\n<b>4)</b> <i>pl.</i> род, происхождение, общественное положение (natales clari <i>T</i>; natalium splendor <i>PJ</i>); homo natalibus clarus <i>O</i> человек знатного рода; natalium periti <i>Sen</i> астрологи; natalibus suis reddi <i>Dig</i> бить восстановленным в правах рождения."],
  ["natantes", "<big>natantes</big>\n\n<b>natantes, ium</b> (<i>и</i> <b>um</b>) <i>n</i> [nato]\nводяные (водоплавающие) животные (<i>рыбы и др.</i>) <i>V</i>."],
  ["natatiles", "<big>natatiles</big>\n\n<b>natatiles, ium</b> <i>n</i> [nato] <i>Aug</i>\n= natantes."],
  ["natatilis", "<big>natatilis</big>\n\n<b>natatilis, e</b> [nato]\nумеющий плавать <i>Tert</i>."],
  ["natatio", "<big>natatio</big>\n\n<b>natatio, onis</b> <i>f</i> [nato]\n<b>1)</b> плавание <i>C</i>; упражнения в плавании <i>Cs</i>;\n<b>2)</b> место для плавания <i>CC, Treb</i>."],
  ["natator", "<big>natator</big>\n\n<b>natator, oris</b> <i>m</i> [nato]\nпловец <i>Vr, O</i>."],
  ["natatoria 1", "<big>natatoria</big>\n\n<b>I natatoria, ae</b> <i>f</i>\nместо для плавания <i>или</i> купания <i>Sid, Vlg, Eccl</i>."],
  ["natatoria 2", "<big>natatoria</big>\n\n<b>II natatoria, orum</b> <i>n</i> (<i>sc.</i> loca) <i>Vlg</i>\n= natatoria I."],
  ["natatorius", "<big>natatorius</big>\n\n<b>natatorius, a, um</b> [natator]\nплавательный (cortex <i>Is</i>)."],
  ["natatus", "<big>natatus</big>\n\n<b>natatus, us</b> <i>m</i> [nato]\nплавание (n. marinus <i>Ap</i>)."],
  ["nates", "<big>nates</big>\n\n<b>nates, ium</b> <i>pl. к</i>\nnatis."],
  ["natinor", "<big>natinor</big>\n\n<b>natinor, —, ari</b> <i>depon.</i>\nсуетиться <i>Cato</i>."],
  ["natio 1", "<big>natio</big>\n\n<b>I natio, onis</b> <i>f</i> [nascor]\n<b>1)</b> рождение, происхождение, род: natione Medusi <i>Vop, QC</i> родом (по происхождению) мидянин;\n<b>2)</b> племя, народность, народ <i>или</i> нация: nationes et gentes (populi) <i>C, Sl</i> племена и народы;\n<b>3)</b> класс, сословие, каста, разряд, слой (optimatium <i>C</i>); секта <i>или</i> школа (Epicureorum <i>C</i>);\n<b>4)</b> порода (equorum <i>Vr</i>);\n<b>5)</b> сорт (mellis nationes <i>PM</i>);\n<b>6)</b> <i>pl.</i> (= gentes) языческие народы <i>Tert</i>."],
  ["Natio 2", "<big>Natio</big>\n\n<b>II Natio, onis</b> <i>f</i>\nНация, <i>богиня рождения</i> <i>C</i>."],
  ["natis", "<big>natis</big>\n\n<b>natis, is</b> <i>f</i> (<i>преим.</i> <i>pl.</i> nates, ium)\nягодица, седалище, зад <i>Pl, H, J, M etc.</i>"],
  ["nativitas", "<big>nativitas</big>\n\n<b>nativitas, atis</b> <i>f</i> [nativus]\n<b>1)</b> рождение <i>Dig, Tert</i>;\n<b>2)</b> поколение (usque ad quartam nativitatem <i>Tert</i>);\n<b>3)</b> (<i>тж.</i> n .dominica) рождество <i>Eccl</i>."],
  ["nativitus", "<big>nativitus</big>\n\n<b>nativitus</b> <i>adv.</i>\nс (самого) рождения <i>Eccl</i>."],
  ["nativus", "<big>nativus</big>\n\n<b>nativus, a, um</b> [nascor]\n<b>1)</b> рождённый, родившийся (dii <i>C</i>); возникший естественным путём (murus <i>Cs</i>);\n<b>2)</b> врождённый, природный (sensus <i>C</i>; lepos <i>Nep</i>);\n<b>3)</b> естественный, натуральный (color <i>PM</i>);\n<b>4)</b> коренной, первообразный (verba <i>C</i>);\n<b>5)</b> родной (sermo <i>QC</i>)."],
  ["nato", "<big>nato</big>\n\n<b>nato, avi, atum, are</b> [<i>intens. к</i> no]\n<b>1)</b> плавать (aequore <i>O</i>; in mari <i>CC</i>; carina natat <i>Prp, V</i>);\n<b>2)</b> <i>поэт.</i> переплывать, проплывать, плыть (по чему-л.) (n. aquas <i>M</i>; n. fretum <i>V</i>): unda natatur piscibus <i>O</i> в воде плавают рыбы;\n<b>3)</b> расплываться, распространяться (radices natant summa parte terrae <i>Col</i>);\n<b>4)</b> утопать, заливаться, быть наполненным, наводняться (arva natant amnibus <i>O</i>; pavimenta natabant vino <i>C</i>);\n<b>5)</b> находиться в волнообразном движении, волноваться (monilia cervice natant <i>Calp</i>); быть нетвёрдым (crura natantia <i>Nem</i>); болтаться (pes natat, <i>sc.</i> in calceo <i>O</i>); развеваться (vestis natat <i>O</i>); колебаться, качаться, колыхаться (seges natat <i>V</i>);\n<b>6)</b> не знать на что решиться, колебаться, быть в недоумении (n. in aliqua re <i>C</i>; animus natat <i>Sen</i>);\n<b>7)</b> (<i>о глазах</i>) блуждать (oculi natantes et quadam voluptate suffusi <i>Q</i>) <i>или</i> угасать, тускнеть (oculis natantibus circumspicere <i>O</i>), <i>тж.</i> быть томным, осовелым (vinis oculi natabant <i>O</i>)."],
  ["natrix", "<big>natrix</big>\n\n<b>natrix, icis</b> <i>f</i> [no]\n<b>1)</b> водяная змея (natrices viperaeque <i>C</i>);\n<b>2)</b> плеть из кожи водяной змеи, <i>перен.</i> <i>LM</i> = mentula;\n<b>3)</b> <i>перен.</i> змея, злой человек (se natricem educare <i>Su</i>)."],
  ["natta", "<big>natta</big>\n\n<b>natta, ae</b> <i>m</i> <i>v. l.</i>\n= пасса."],
  ["natura", "<big>natura</big>\n\n<b>natura, ae</b> <i>f</i> [nascor]\n<b>1)</b> рождение: natura родом, по рождению (tuus natura filius <i>C</i>);\n<b>2)</b> природные свойства, естественное качество (n. montis, fluminis <i>Cs</i>); вид, характер (loci <i>Cs</i>); сущность (n. hominis <i>или</i> humana <i>C</i>): n. rerum <i>Lcr etc.</i> сущность вещей (мира); natura по природе, от природы, естественным образом (locus natura munitus <i>Cs</i>) <i>или</i> в силу природного предрасположения (natura opinari <i>C</i>); <i>иногда описательно</i>: animi n. <i>Lcr</i> = animus; n. animantum <i>Lcr</i> = animantesii высокое качество (n. alicui rei deest <i>T</i>);\n<b>3)</b> образ, форма, внешний вид (n. serpentium <i>Sl</i>): insula natura triquetra <i>Cs</i> остров треугольной формы;\n<b>4)</b> натура, душевный склад, природная склонность, характер (homo dificillima natura <i>Nep</i>): et dicentium et audientium naturae <i>Q</i> (различные) природные свойства как ораторов, так и слушателей; loqui, ut n. fert <i>Ter</i> говорить чистосердечно (прямо); in naturam vertere <i>погов. Sl</i> становиться второй натурой;\n<b>5)</b> природа, миропорядок, мир, вселенная: n. rerum (mundi) <i>C</i> мироздание; est in rerum natura <i>C</i> (cadit in rerum naturam <i>Q</i>) <i>погов.</i> возможно; satisfacere <i>C</i> (cedere <i>C</i>; concedere <i>Sl</i>) naturae <i>погов.</i> = умереть;\n<b>6)</b> первичная материя, основное вещество, стихия (omnia ex quattuor naturis constant <i>C</i>);\n<b>7)</b> существо, создание, особь (mortalis <i>C</i>);\n<b>8)</b> орган тела <i>C</i>;\n<b>9)</b> орган деторождения <i>C, Vr</i>."],
  ["naturabilis", "<big>naturabilis</big>\n\n<b>naturabilis, e</b>\n<i>Ap</i> = naturalis."],
  ["naturalia", "<big>naturalia</big>\n\n<b>naturalia, ium</b> <i>n</i> [naturalis]\n<b>1)</b> естественные дела, природные процессы, природа (n. anteponantur non naturalibus <i>C</i>);\n<b>2)</b> органы деторождения <i>CC, Just etc.</i>"],
  ["naturalis", "<big>naturalis</big>\n\n<b>naturalis, e</b> [natura]\n<b>1)</b> естественный, созданный природой (portus <i>L</i>; jus <i>C</i>); натуральный, соответствующий законам природы (societas <i>C</i>; mors <i>PM</i>);\n<b>2)</b> физический, родной (filius <i>L, Su</i>; avus <i>VP</i>);\n<b>3)</b> внебрачный (filius <i>Dig</i>); pater n. <i>Dig</i> отец внебрачного ребёнка;\n<b>4)</b> природный, врождённый (notio <i>C</i>; bonitas <i>Nep</i>);\n<b>5)</b> действительный, подлинный (unus n., alter fabulosus <i>Lact</i>);\n<b>6)</b> касающийся природы, естественнонаучный (quaestiones naturales <i>C, Sen</i>);\n<b>7)</b> детородный, половой (loca <i>Col</i>)."],
  ["naturalitas", "<big>naturalitas</big>\n\n<b>naturalitas, atis</b> <i>f</i>\nестественный характер (somni <i>Tert</i>)."],
  ["naturaliter", "<big>naturaliter</big>\n\n<b>naturaliter</b> [naturalis]\nсообразно законам природы, по природе, естественно (alacritas n. innata <i>C</i>)."],
  ["naturalitus", "<big>naturalitus</big>\n\n<b>naturalitus</b> <i>adv.</i> [naturalis]\nпо природе, от природы <i>Ap, Sid</i>."],
  ["naturificatus", "<big>naturificatus</big>\n\n<b>naturi–ficatus, a, um</b> [facio]\nставший частью природы, <i>т. е.</i> получивший эмпирическое существование (animae <i>Tert</i>)."],
  ["natus 1", "<big>natus</big>\n\n<b>I natus, a, um</b> [nascor]\n<b>1)</b> рождённый, родившийся, происходящий (aliquo <i>или</i> ex aliquo <i>Ter, C, Nep etc.</i>; ex <i>или</i> ab aliqua re <i>Pl etc.</i>): Macedo n. <i>QC</i> македонец по происхождению; post homines natos (post hominum genus natum) <i>погов. C</i> с тех пор, как появились люди; n. nemo in aedibus <i>Pl</i> ни живой души в доме; anno ante natum Ennium <i>C</i> за год до рождения Энния; aliquem natum non putare <i>Sen, Pt</i> ни во что не ставить (презирать) кого-л.; modo n. flos <i>Pt</i> только что распустившийся цветок; n. ex ipsa mora <i>погов. Pl</i> = воплощённая медлительность; famam non ita, ut nata est, ferre <i>Pl</i> разносить весть не в первоначальном её виде, <i>т. е.</i> искажать её; terra nata (<i>n pl.</i>) <i>C</i> произведения земли;\n<b>2)</b> прирождённый, врождённый, природный, коренной: sermo, qui est n. nobis <i>C</i> наш родной язык;\n<b>3)</b> предназначенный, самой природой приспособленный, рождённый (alicui rei <i>или</i> ad aliquid): n. ad dicendum <i>C</i> рождённый быть оратором (с природными задатками оратора); n. imperio (<i>dat.</i>) <i>C</i> рождённый для того, чтобы повелевать;\n<b>4)</b> устроенный, созданный (locus ita n. <i>L</i>); обречённый (самой природой) (tolerare labores <i>O</i>); сделанный, сочинённый (versus male nati <i>H</i>): pro (e) re nata <i>Ter, C, Ap</i> в зависимости от (ввиду сложившихся) обстоятельств;\n<b>5)</b> (<i>с указанием единицы времени в</i> <i>acc.</i>) в возрасте, от роду: quadraginta sex annos n. <i>Pl etc.</i> 46-и лет от роду; <i>реже с</i> <i>gen.</i>: decem annorum n. <i>Vr</i> десятилетний, в возрасте 10-и лет; n. ad quinquaginta annos <i>C</i> в возрасте около пятидесяти лет."],
  ["natus 2", "<big>natus</big>\n\n<b>II natus, i</b> <i>m</i> [nascor]\n<b>1)</b> сын: n. volucer <i>O</i> крылатый сын (Венеры), <i>т. е.</i> Amor;\n<b>2)</b> <i>pl.</i> дети (parentes et nati <i>C, L etc.</i>): ex fratre et sorore nati <i>Nep</i> двоюродные братья и сестры; nati natorum <i>V</i> дети детей, <i>т. е.</i> (далёкие) потомки;\n<b>3)</b> детёныши (bestiae ex se natos diligunt <i>C</i>)."],
  ["natus 3", "<big>natus</big>\n\n<b>III natus</b>, (<b>us</b>) (<i>встреч. тк.</i> <i>abl. sg.</i>) <i>m</i> [nascor]\n<b>1)</b> рождение: natu летами, годами, по летам, по возрасту: frater major natu <i>C</i> старший брат; minimus natu <i>C</i> самый младший; homo magnus (grandis) natu <i>Pl, C</i> <i>или</i> magno natu <i>Nep, L</i> человек старый (преклонных лет); majores natu <i>C etc.</i> пожилые люди; natu Samius <i>Ap</i> уроженец Самоса;\n<b>2)</b> рост: cupressus natu morosa <i>PM</i> — <i>см.</i> morosus."],
  ["nauarchia", "<big>nauarchia</big>\n\n<b>nauarchia, ae</b> <i>f</i> (<i>греч.</i>)\nкомандование кораблём <i>CTh</i>."],
  ["nauarchus", "<big>nauarchus</big>\n\n<b>nauarchus, i</b> <i>m</i> (<i>греч.</i>)\nнаварх, командир судна, капитан <i>C, T, Dig, CJ</i>."],
  ["Naubolides", "<big>Naubolides</big>\n\n<b>Naubolides, ae</b> <i>m</i>\nсын Навбола, <i>т. е.</i> аргонавт Iphitus <i>VF</i>."],
  ["Naubolus", "<big>Naubolus</big>\n\n<b>Naubolus, i</b> <i>m</i>\nНавбол, <i>царь Фокиды, отец Яфита</i> <i>St</i>."],
  ["naucella", "<big>naucella</big>\n\n<b>naucella</b> <i>Dig</i>\n= navicella."],
  ["nauci", "<big>nauci</big>\n\n<b>nauci</b> — <i>см.</i>\nnaucum."],
  ["nauclericus", "<big>nauclericus</big>\n\n<b>nauclericus, a, um</b> [nauclerus]\nпринадлежащий хозяину судна (ornatus <i>Pl</i>)."],
  ["nauclerus", "<big>nauclerus</big>\n\n<b>nauclerus, i</b> <i>m</i> (<i>греч.</i>)\nхозяин корабля, судовладелец <i>Pl, CTh, Vlg, Eccl</i>."],
  ["Naucrates", "<big>Naucrates</big>\n\n<b>Naucrates, is</b> <i>m</i>\nНавкрат, <i>ученик ритора Исократа, участник риторического состязания в память умершего царя Мавзола Карийского</i> <i>C</i>."],
  ["naucula", "<big>naucula</big>\n\n<b>naucula, ae</b> <i>f</i> <i>PM</i>\n= navicula."],
  ["nauculor", "<big>nauculor</big>\n\n<b>nauculor, —, ari</b> [naucula]\nплыть на маленьком судне <i>M</i>."],
  ["naucum", "<big>naucum</big>\n\n<b>naucum, i</b> <i>n</i>\nореховая скорлупа, <i>перен.</i> безделица, мелочь: nauci non esse <i>погов. Pl</i> никуда не годиться; non nauci facere <i>Pl</i> (habere <i>C</i> <i>или</i> putare <i>Ap</i>) не ставить ни во что; homo non nauci <i>Pl</i> никчёмный человек, бездельник."],
  ["naufragator", "<big>naufragator</big>\n\n<b>naufragator, oris</b> <i>m</i>\nпотерпевший кораблекрушение <i>Aug</i>."],
  ["naufragiosus", "<big>naufragiosus</big>\n\n<b>naufragiosus, a, um</b>\nугрожающий кораблекрушением, опасный для судов (pelagus <i>Sid</i>; montes <i>Aug</i>)."],
  ["naufragium", "<big>naufragium</big>\n\n<b>naufragium, i</b> <i>n</i> [navis + frango]\n<b>1)</b> кораблекрушение: facere n. (naufragia) <i>C, Sen</i> потерпеть кораблекрушение; n. in portu facere <i>погов. Q</i> = потерпеть неудачу, когда цель уже совсем близка; tabula ex naufragio <i>C</i> уцелевшая от кораблекрушения доска, <i>перен.</i> соломинка, за которую хватается утопающий, единственное средство к спасению, последняя надежда;\n<b>2)</b> несчастье, поражение, гибель, катастрофа, разорение (n. patrimonii <i>C</i>): n. rei familiaris <i>C</i> совершенное расстройство (потеря) имущества, разорение, крах;\n<b>3)</b> <i>pl.</i> обломки, остатки (naufragia Caesaris amicorum <i>C</i>)."],
  ["naufrago", "<big>naufrago</big>\n\n<b>naufrago, avi, —, are</b> [naufragium]\nтерпеть кораблекрушение (omnes naves naufragaverunt <i>Pl</i>): n. circa fidem <i>Vlg</i> утратить веру."],
  ["naufragor", "<big>naufragor</big>\n\n<b>naufragor, —, ari</b> <i>Aug</i>\n= naufrago."],
  ["naufragosus", "<big>naufragosus</big>\n\n<b>naufragosus</b> <i>Aug</i>\n= naufragiosus."],
  ["naufragus", "<big>naufragus</big>\n\n<b>naufragus, a, um</b> [navis + frango]\n<b>1)</b> потерпевший кораблекрушение (homo <i>C etc.</i>; puppis <i>O</i>);\n<b>2)</b> несчастный, разорённый, обнищавший: n. patrimonio <i>C</i> лишившийся имущества, разорившийся;\n<b>3)</b> вызывающий (причиняющий) кораблекрушение, разбивающий (топящий) корабли (mare <i>H</i>; monstra <i>O</i>; tempestas <i>VF</i>)."],
  ["naulium", "<big>naulium</big>\n\n<b>naulium, i</b> <i>n</i> <i>O</i>\n(<i>v. l.</i>)= nablium."],
  ["Naulocha", "<big>Naulocha</big>\n\n<b>Naulocha, orum</b> <i>и</i> <b>Naulochum, i</b> <i>n</i>\nНавлох, <i>город на сев.-вост. побережье Сицилии</i> <i>Sil, Su</i>."],
  ["naulum", "<big>naulum</big>\n\n<b>naulum, i</b> <i>n</i> (<i>греч.</i>)\nплата за проезд <i>или</i> провоз (по морю) <i>J, Dig</i>."],
  ["naumachia", "<big>naumachia</big>\n\n<b>naumachia, ae</b> <i>f</i> (<i>греч.</i>; <i>лат.</i> navale proelium)\nнавмахия:\n<b>1)</b> представление морского боя (naumachiam committere, exhibere <i>Su</i>);\n<b>2)</b> место инсценировки морского сражения (искусственный водоём) <i>Su</i>."],
  ["naumachiarius 1", "<big>naumachiarius</big>\n\n<b>I naumachiarius, a, um</b> [naumachia]\nотносящийся к навмахии (pons <i>PM</i>)."],
  ["naumachiarius 2", "<big>naumachiarius</big>\n\n<b>II naumachiarius, i</b> <i>m</i>\nучастник навмахии <i>Su</i>."],
  ["Naupactous", "<big>Naupactous</big>\n\n<b>Naupactous, a, um</b> [Naupactus]\nнавпактский <i>O</i>."],
  ["Naupactus", "<big>Naupactus</big>\n\n<b>Naupactus, i</b> <i>f</i>\nНавпакт, <i>портовый город в Локриде Озольской, в Коринфском заливе</i> (<i>впоследствии</i> Lepanto) <i>Cs, C, PM etc.</i>"],
  ["naupegus", "<big>naupegus</big>\n\n<b>naupegus, i</b> <i>m</i> (<i>греч.</i>)\nсудостроитель <i>Dig</i>."],
  ["Naupliades", "<big>Naupliades</big>\n\n<b>Naupliades, ae</b> <i>m</i>\nсын Навплия, <i>т. е.</i> Palamedes <i>O</i>."],
  ["Nauplius 1", "<big>Nauplius</big>\n\n<b>I Nauplius, i</b> <i>m</i>\nНавплий, <i>царь Эвбеи, который, чтобы отомстить за казнённого под Троей своего сына Паламеда, зажёг над Кафарейскими скалами огни; плывшие мимо Эвбеи по пути домой греки были введены этими огнями в заблуждение и потерпели крушение</i> <i>Prp, Su</i>."],
  ["nauplius 2", "<big>nauplius</big>\n\n<b>II nauplius, i</b> <i>m</i> (<i>греч.</i>)\n<i>род моллюска</i> <i>PM</i>."],
  ["nausea", "<big>nausea</big>\n\n<b>nausea, ae</b> <i>f</i> (<i>греч.</i>)\n<b>1)</b> морская болезнь (sine nausea navigare <i>C</i>);\n<b>2)</b> тошнота, позыв на рвоту (nauseam fluentem coercere <i>H</i>);\n<b>3)</b> отвращение (coti diana <i>M</i>): ibat res ad summam nauseam <i>Pt</i> дело приняло крайне неприятный оборот."],
  ["nauseabilis", "<big>nauseabilis</big>\n\n<b>nauseabilis, e</b>\nтошнотворный, вызывающий позыв на рвоту <i>CA</i>."],
  ["nauseabundus", "<big>nauseabundus</big>\n\n<b>nauseabundus, a, um</b> [nauseo]\nподверженный морской болезни <i>или</i> страдающий морской болезнью <i>Sen</i>; чувствующий тошноту, позыв на рвоту <i>Sen</i>."],
  ["nauseator", "<big>nauseator</big>\n\n<b>nauseator, oris</b> <i>m</i> [nauseo]\nстрадающий морской болезнью <i>Sen</i>."],
  ["nauseo", "<big>nauseo</big>\n\n<b>nauseo, avi, atum, are</b> [nausea]\n<b>1)</b> страдать морской болезнью <i>или</i> чувствовать тошноту, позыв на рвоту <i>C, H, Sen</i>;\n<b>2)</b> испытывать отвращение <i>C, Aug</i>;\n<b>3)</b> делать недовольный вид, привередничать <i>Ph</i>."],
  ["nauseola", "<big>nauseola</big>\n\n<b>nauseola, ae</b> <i>f</i> [<i>demin. к</i> nausea]\nлёгкая тошнота <i>C</i>."],
  ["nauseosus", "<big>nauseosus</big>\n\n<b>nauseosus, a, um</b> [nauseo]\nвызывающий тошноту, тошнотворный, рвотный (radix <i>PM</i>)."],
  ["nausi-", "<big>nausi-</big>\n\n<b>nausi-</b> <i>PM etc.</i>\n= nause-."],
  ["Nausicaa", "<big>Nausicaa</big>\n\n<b>Nausicaa, ae</b> <i>и</i> <b>Nausicae, es</b> <i>f</i>\nНавсикая, <i>дочь Алкиноя, царя феаков, оказавшего гостеприимство Одиссею</i> <i>AG, M</i>."],
  ["nausum", "<big>nausum</big>\n\n<b>nausum, i</b> <i>n</i>\nнауз, <i>род галльского судна</i> <i>Aus</i>."],
  ["nauta", "<big>nauta</big>\n\n<b>nauta, ae</b> <i>m</i> [<i>из</i> navita <i>от</i> navis]\n<b>1)</b> лодочник <i>Ter, C etc.</i>;\n<b>2)</b> корабельщик, судовладелец <i>H, Sid</i>;\n<b>3)</b> мореплаватель, мореход, путешественник (по морю), <i>тж.</i> купец <i>H, Sid</i>;\n<b>4)</b> моряк, матрос <i>C, Cs etc.</i>"],
  ["nautalis", "<big>nautalis</big>\n\n<b>nautalis, e</b>\nкорабельный (formae <i>Aus</i>)."],
  ["nautea", "<big>nautea</big>\n\n<b>nautea, ae</b> <i>f</i> (<i>греч.</i>)\nтрюмная вода, корабельные нечистоты <i>Pl</i>."],
  ["nauticus 1", "<big>nauticus</big>\n\n<b>I nauticus, a, um</b> (<i>греч.</i>)\n<b>1)</b> корабельный: pinus nautica <i>V</i> = navis; exuviae nauticae <i>C</i> корабельные трофеи (<i>срезанные в качестве трофеев носовые части неприятельских судов</i>);\n<b>2)</b> морской: scientia rerum nauticarum <i>Cs, C</i> наука мореплавания; castra nautica <i>Nep</i> место стоянки судов, гавань."],
  ["nauticus 2", "<big>nauticus</big>\n\n<b>II nauticus, i</b> <i>m</i>\nморяк, матрос, мореход <i>L, PM</i>."],
  ["nautilus", "<big>nautilus</big>\n\n<b>nautilus, i</b> <i>m</i>\nкораблик, <i>род моллюска</i> <i>PM</i>."],
  ["navale", "<big>navale</big>\n\n<b>navale, is</b> <i>n</i> [navalis]\nместо стоянки судов, гавань <i>или</i> причал <i>O, Lcn</i>."],
  ["navalia", "<big>navalia</big>\n\n<b>navalia, ium</b> (<i>Vtr</i> <i>тж.</i> <b>iorum</b>) <i>n</i> [navalis]\n<b>1)</b> судостроительная верфь, док <i>C, Cs etc.</i>;\n<b>2)</b> корабельные снасти, такелаж <i>или</i> судостроительные материалы <i>L</i>."],
  ["navalis", "<big>navalis</big>\n\n<b>navalis, e</b> [navis]\n<b>1)</b> судовой, корабельный (forma <i>O</i>; materia <i>L</i>);\n<b>2)</b> морской (pugna, bellum <i>C</i>): stagnum navale <i>T</i> водоём для навмахии (<i>см.</i> naumachia); maritimus et n. hostis <i>C</i> враг, нападающий со стороны моря на кораблях; socii navales <i>L</i> моряки, матросы <i>или</i> морские войска; pedes navales <i>шутл. Pl</i> гребцы;\n<b>3)</b> мореходный (disciplina <i>C</i>);\n<b>4)</b> присуждаемый в награду за доблесть в морских сражениях (corona <i>V</i>; triumphus <i>L</i>)."],
  ["navarchus", "<big>navarchus</big>\n\n<b>navarchus, i</b> <i>m</i> <i>v. l.</i>\n= nauarchus."],
  ["nave 1", "<big>nave</big>\n\n<b>I nave</b> <i>Pl, Sl</i>\n= naviter."],
  ["nave 2", "<big>nave</big>\n\n<b>II nave</b> <i>V abl. sg. к</i>\nnavis."],
  ["navi 1", "<big>navi</big>\n\n<b>I navi</b> <i>pf. к</i>\nno."],
  ["navi 2", "<big>navi</big>\n\n<b>II navi</b> <i>abl. sg. к</i>\nnavis."],
  ["navia", "<big>navia</big>\n\n<b>navia, ae</b> <i>f</i> [navis]\nбарка, ладья <i>Mela, Macr</i>."],
  ["navicella", "<big>navicella</big>\n\n<b>navicella, ae</b> <i>f</i> <i>Dig, Eccl</i>\n= navicula."],
  ["navicula", "<big>navicula</big>\n\n<b>navicula, ae</b> <i>f</i> [<i>demin. к</i> navis]\nнебольшое судно, ладья, лодка, чёлн <i>C, Cs etc.</i>"],
  ["navicularia", "<big>navicularia</big>\n\n<b>navicularia, ae</b> <i>f</i> [navicularius I]\n(<i>sc.</i> res) морской транспорт, морские перевозки <i>C</i>."],
  ["navicularis", "<big>navicularis</big>\n\n<b>navicularis, e</b> <i>Dig</i>\n= navicularius I."],
  ["navicularius 1", "<big>navicularius</big>\n\n<b>I navicularius, a, um</b> [navicula]\nкорабельный, относящийся к судоходству (functio <i>CJ</i>; onus <i>CTh</i>)."],
  ["navicularius 2", "<big>navicularius</big>\n\n<b>II navicularius, i</b> <i>m</i>\nсудовладелец <i>C etc.</i>"],
  ["naviculor", "<big>naviculor</big>\n\n<b>naviculor</b> <i>v. l.</i>\n= nauculor."],
  ["navifragus", "<big>navifragus</big>\n\n<b>navifragus, a, um</b> [navis + frango]\nразбивающий суда (aequor <i>O</i>; fretum <i>O</i>; saxa <i>St</i>)."],
  ["navigabilis", "<big>navigabilis</big>\n\n<b>navigabilis, e</b> [navigo]\nсудоходный, удобный для судоходства (amnis <i>L</i>; litora <i>T</i>)."],
  ["navigatio", "<big>navigatio</big>\n\n<b>navigatio, onis</b> <i>f</i> [navigo]\nплавание (<i>на корабле</i>), судоходство, мореплавание (n. ignoti maris <i>Sen</i>; per maria <i>CA</i>): n. litorea <i>Amm</i> каботажное судоходство || морской рейс, возможность отправиться в плавание (primam navigationem non omittere <i>C</i>): patiens navigationis <i>Just</i> судоходный."],
  ["navigator", "<big>navigator</big>\n\n<b>navigator, oris</b> <i>m</i> [navigo]\nмореплаватель, мореход; моряк <i>Q, Eccl</i>."],
  ["naviger", "<big>naviger</big>\n\n<b>naviger, gera, gerum</b> [navis + gero]\n<b>1)</b> несущий на себе корабли, покрытый судами, <i>т. е.</i> судоходный (mare <i>Lcr</i>; iter <i>M</i>);\n<b>2)</b> плывущий: similitudo navigera <i>PM</i> сходство с кораблём (под парусами)."],
  ["navigiolum", "<big>navigiolum</big>\n\n<b>navigiolum, i</b> <i>n</i> [<i>demin. к</i> navigium]\nсудёнышко, чёлн <i>bAfr, C</i>."],
  ["navigium", "<big>navigium</big>\n\n<b>navigium, i</b> <i>n</i> [navigo]\n<b>1)</b> судно <i>C, L, Sen etc.</i>;\n<b>2)</b> плот <i>или</i> паром <i>Dig</i>;\n<b>3)</b> плавание (<i>на судне</i>) <i>Dig, CJ</i>."],
  ["navigo", "<big>navigo</big>\n\n<b>navigo, avi, atum, are</b> [navis + ago]\n<b>1)</b> (<i>тж.</i> n. navi <i>Dig</i>) плыть, отплывать (<i>на судне</i>) (n. in fero mari <i>C</i>; e portu <i>Q</i>): in portu n. <i>погов. Ter</i> = находиться в безопасности;\n<b>2)</b> проплывать, переплывать, объезжать (<i>на судне</i>) (n. Oceanum septentrionalem <i>Su</i>; per fines alicujus <i>Pt</i>; lacus Romanis classibus navigati <i>T</i>);\n<b>3)</b> (<i>о морской войне</i>) начинаться, предприниматься, разражаться (belli impetus navigavit <i>C</i>);\n<b>4)</b> плавать (<i>в воде</i>) (homo navigat <i>O</i>);\n<b>5)</b> добывать мореплаванием, наживать путём морской торговли: quae homines arant, navigant <i>Sl</i> (всё,) что люди добывают земледелием, мореплаванием."],
  ["navis", "<big>navis</big>\n\n<b>navis, is</b> <i>f</i> (<i>acc.</i> <i>иногда</i> im, <i>abl.</i> <i>чаще</i> i)\n<b>1)</b> корабль, судно: n. longa <i>L</i> военный корабль; n. oneraria <i>Pl, Cs etc.</i> грузовое (товарное) судно; n. tecta <i>L</i> (constrata <i>C</i>) палубное судно; n. aperta <i>C</i> беспалубное судно; navem ornare <i>L</i> (adornare <i>Cs</i>, instruere <i>L</i>, fabricari <i>T</i>, armare <i>Cs, L</i>) оснащать, снаряжать корабль; sub ducere navem <i>Cs</i> вытащить корабль на берег; deducere navem <i>Cs</i> спустить корабль на воду; navem solvere <i>Cs</i> отплыть; n. solvit <i>Cs</i> корабль отправляется; navem appellere <i>C</i> (applicare <i>Cs, L</i>) ad terram (terrae) пристать к берегу; navibus rem gerere <i>H</i> сражаться на море; navem frangere <i>Ter, rhH</i> потерпеть кораблекрушение; n. rei publicae <i>перен.</i> <i>C</i> государственный корабль; omnibus navibus et quadrigis <i>погов. H</i> = всеми силами и средствами; in eadem esse navi <i>погов. C</i> = находиться в одинаковом (с другими) положении; navem mortuo applicare <i>погов. Q</i> = вытаскивать из воды утопленника, <i>т. е.</i> оказывать запоздалую помощь;\n<b>2)</b> (<i>тж.</i> n. Argolica <i>C</i>) корабль Арго, <i>созвездие</i> <i>C</i>."],
  ["Navisalvia", "<big>Navisalvia</big>\n\n<b>Navisalvia, ae</b> <i>f</i> [navis + salvus]\n&laquo;Спасительница корабля&raquo;, <i>прозвище, данное весталке Клавдии Квинте, которая в 205 г. до н. э. дотащила до Рима по Тибру корабль с изображением Кибелы</i> <i>L, T, VM</i>."],
  ["navita", "<big>navita</big>\n\n<b>navita, ae</b> <i>m</i>\n<i>поэт.</i> <i>Lcr, V, Poeta ap. C</i> = nauta."],
  ["navitas", "<big>navitas</big>\n\n<b>navitas, atis</b> <i>f</i> [navus]\nревность, рвение, прилежание, усердие <i>C</i>."],
  ["naviter", "<big>naviter</big>\n\n<b>naviter</b> [navus]\n<b>1)</b> старательно, усердно, деятельно (pugnare <i>L</i>);\n<b>2)</b> вполне (plenus <i>Lcr</i>); весьма (n. impudens <i>C</i>)."],
  ["navo", "<big>navo</big>\n\n<b>navo, avi, atum, are</b> [navus]\nусердно выполнять (opus <i>VF</i>); деятельно заниматься, ревностно служить (n. rem publicam <i>C</i>): alicui bellum n. <i>T</i> оказывать кому-л. помощь в войне; operam n. alicui <i>C</i> деятельно помогать кому-л.; aliam operam n. <i>C</i> действовать в другой области; benevolentiam in aliquem n. <i>C</i> проявлять благожелательность к кому-л.; fortiter in acie operam n. <i>L</i> храбро сражаться."],
  ["navus", "<big>navus</big>\n\n<b>navus, a, um</b> [<i>одного корня с</i> ignavus]\n<b>1)</b> ревностный, старательный, . усердный, деятельный (homo n. et industrius <i>C</i>); прилежный, тщательный (rudimenta <i>Sil</i>; opera <i>VP</i>);\n<b>2)</b> придающий силы (timor <i>Sil</i>)."],
  ["naxa", "<big>naxa</big>\n\n<b>naxa, ae</b> <i>f</i> <i>v. l.</i>\n= nassa."],
  ["Naxius", "<big>Naxius</big>\n\n<b>Naxius, a, um</b> [Naxus]\nнаксосский <i>Prp, PM</i>: N. ardor <i>Col</i> = corona Ariadnes (<i>созвездие</i> Венец Ариадны)."],
  ["Naxus", "<big>Naxus</big>\n\n<b>Naxus (Naxos), i</b> <i>f</i>\nНаксос:\n<b>1)</b> <i>самый большой из Кикладских о-вов</i> (<i>посвященный Вакху</i>) <i>Prp, V, PM</i>;\n<b>2)</b> <i>город на вост. берегу Сицилии</i> <i>Prp, PM</i>."],
  ["Nazara", "<big>Nazara</big>\n\n<b>Nazara, ae</b> <i>f</i> <i>Jvc</i>\n= Nazareth."],
  ["Nazarenus", "<big>Nazarenus</big>\n\n<b>Nazarenus, a, um</b> [Nazareth]\nназарейский <i>Vlg</i>; <i>перен.</i> христианский <i>Eccl</i>."],
  ["Nazareth", "<big>Nazareth</big>\n\n<b>Nazareth</b> <i>f indecl.</i>\nНазарет, <i>город в древней Иудее</i> <i>Vlg</i>."],
  ["Nazareus", "<big>Nazareus</big>\n\n<b>Nazareus</b> <i>и</i> <b>Nazarus, a, um</b> <i>Eccl</i>\n= Nazarenus."],
  ["ne 1", "<big>ne</big>\n\n<b>I ne</b>\n<i>энклит. вопр. частица</i>\n<b>1)</b> разве, неужели, что ли: itane? <i>C</i> так ли?, в самом деле?, разве?; in nostrane potestate hoc est? <i>C</i> разве это в нашей власти?;\n<b>2)</b> ли: rectene interpretor sententiam tuam? <i>C</i> правильно ли я понимаю твою мысль?; Epaminondas quaesivit, salvusne esset clipeus <i>Nep</i> Эпаминонд спросил, цел ли щит;\n<b>3)</b>: -ne... an <i>или</i> -ne... -ne ли... или (feraene an homines <i>C</i>): audentes forsne deusne juvet <i>O</i> (посмотрим), кто помогает храбрым: случай или бог. — <i>В разговорной речи конечное</i> e <i>частицы</i> ne <i>и конечное</i> s <i>основного слова часто выпадают</i>: credon' = credo ne, satin' = satisne."],
  ["ne 2", "<big>ne</big>\n\n<b>II ne</b> <i>adv.</i>\n<b>1)</b> (<i>арх.</i> = non) не (crescere ne — <i>т. е.</i> non — possunt fruges <i>Lcr</i>);\n<b>2)</b> <i>при запрещении с</i> <i>imper.</i> (ne metue <i>Sen</i>) <i>или</i> <i>conjct.</i> (hoc ne feceris <i>C</i>; ne quaesieris <i>H</i>; senatus hoc ne concedat <i>C</i>);\n<b>3)</b> допустим (исходя из того, предположив), что не (ne sit sol, ne luna, ne stellae <i>C</i>);\n<b>4)</b> да (пусть) не (ne id Juppiter sirit = siverit <i>L</i>; ne vivam, si scio <i>C</i>);\n<b>5)</b>: ne... quidem: a) даже не (mendaci homini ne vera quidem dicenti credimus <i>C</i>); b) тем более не, тем менее (nullum est fatum: ita ne divinatio quidem <i>C</i>);\n<b>6)</b> нисколько не, не только не (ne connivente quidem, sed etiam hilarioribus oculis intuente <i>C</i>)."],
  ["ne 3", "<big>ne</big>\n\n<b>III ne</b> (<i>intens.</i> ut ne) <i>conj.</i>\n<b>1)</b> чтобы не (hoc te rogo, ne demittas animum <i>C</i>; pullos fovere, ne frigore laedantur <i>C</i>);\n<b>2)</b> как бы не, что: veritus, ne hostium impetum sustinere non posset <i>Cs</i> опасаясь, что ему не устоять против натиска неприятеля; ne non что не (timeo, ne non impetrem <i>C</i>)."],
  ["ne 4", "<big>ne</big>\n\n<b>IV ne</b> (<i>иногда</i> <b>nae</b>) <i>adv.</i>\nконечно, (вот) именно, право, ну да: egone? — Tu ne <i>Pl</i> я, что-ли? — Ну да, ты; edepol ne <i>Pl</i> клянусь Поллуксом, конечно; <i>у</i> <i>C</i> — <i>впереди местоимений личн. и указат.</i> (ne ego, ne tu, ne ille): ne illi vehementer errant, si illam meam pristinam lenitatem perpetuam sperant futuram <i>C</i> право же, они сильно ошибаются, если надеются, что моя прежняя снисходительность будет длиться вечно."],
  ["Neaera", "<big>Neaera</big>\n\n<b>Neaera, ae</b> <i>f</i> (<i>греч.</i>)\nНеэра, <i>женское имя</i> <i>V, Tib, H</i>."],
  ["Neapolis", "<big>Neapolis</big>\n\n<b>Neapolis, is</b> (<i>acc.</i> im <i>и</i> in, <i>abl.</i> i) <i>f</i> (<i>греч.</i> &laquo;новый город&raquo;)\nНеаполь:\n<b>1)</b> <i>портовый город Кампании на зап. склоне Везувия, на реке Sebethos</i> <i>C, L, H, M etc.</i>;\n<b>2)</b> <i>одна из частей Сиракуз</i> <i>C, L</i>."],
  ["Neapolitanum", "<big>Neapolitanum</big>\n\n<b>Neapolitanum, i</b> <i>n</i>\nимение под Неаполем <i>C</i>."],
  ["Neapolitanus 1", "<big>Neapolitanus</big>\n\n<b>I Neapolitanus, a, um</b> [Neapolis]\nнеаполитанский <i>C</i>."],
  ["Neapolitanus 2", "<big>Neapolitanus</big>\n\n<b>II Neapolitanus, i</b> <i>m</i>\nнеаполитанец <i>C etc.</i>"],
  ["Neapolites", "<big>Neapolites</big>\n\n<b>Neapolites, ae</b> <i>m</i> <i>Aug</i>\n= Neapolitanus II."],
  ["Nearchus", "<big>Nearchus</big>\n\n<b>Nearchus, i</b> <i>m</i>\nНеарх:\n<b>1)</b> <i>друг детства и начальник флота Александра Македонского</i> <i>QC, PM</i>;\n<b>2)</b> <i>пифагореец, учитель Катона Старшего</i> <i>C</i>."],
  ["nebris", "<big>nebris</big>\n\n<b>nebris, idis</b> (<i>acc.</i> ida; <i>acc. pl.</i> idas) <i>f</i> (<i>греч.</i>)\nшкура молодого оленя (<i>которую носили вакханки во время празднеств в честь Диониса и жрецы Цереры во время Элевсинских мистерий</i>) <i>St, Cld</i>."],
  ["nebula", "<big>nebula</big>\n\n<b>nebula, ae</b> <i>f</i>\n<b>1)</b> туман (n. matutina <i>L</i>; n. dispulsa sole <i>L</i>);\n<b>2)</b> <i>поэт.</i> чад, дым (lucernarum <i>Pers</i>);\n<b>3)</b> <i>поэт.</i> туча, облако (n. pulveris <i>Lcr</i>): cyathus nebulae <i>Pl</i> стакан облака, <i>т. е.</i> сущий пустяк, безделица;\n<b>4)</b> <i>перен.</i> туман, дымка (per nebulam scire aliquid <i>Pl</i>)."],
  ["nebulo", "<big>nebulo</big>\n\n<b>nebulo, onis</b> <i>m</i>\nбездельник, негодяй, мошенник <i>Ter, C, H etc.</i>"],
  ["nebulosus", "<big>nebulosus</big>\n\n<b>nebulosus, a, um</b> [nebula]\n<b>1)</b> туманный, пасмурный, мрачный (caelum <i>C</i>; dies <i>CC, Fl</i>);\n<b>2)</b> похожий на облако, <i>т. е.</i> из тончайших нитей (retia <i>Aus</i>);\n<b>3)</b> тёмный, малопонятный (nomen <i>AG</i>)."],
  ["nec", "<big>nec</big>\n\n<b>nec</b> <i>v. l.</i>\n= neque."],
  ["necator", "<big>necator</big>\n\n<b>necator, oris</b> <i>m</i> [neco]\nубийца <i>Macr, Lampr</i>."],
  ["necatrix", "<big>necatrix</big>\n\n<b>necatrix, icis</b> <i>f</i>\nубийца <i>Aug</i>."],
  ["necdum", "<big>necdum</big>\n\n<b>nec–dum (neque–dum)</b> <i>adv.</i>\n(и) ещё не (n. posita puerili praetexta <i>T, C etc.</i>)."],
  ["necessaria 1", "<big>necessaria</big>\n\n<b>I necessaria, orum</b> <i>n</i>\n<b>1)</b> нечто необходимое <i>C</i>;\n<b>2)</b> жизненные потребности <i>Sl, CC etc.</i>;\n<b>3)</b> половые органы <i>G</i>."],
  ["necessaria 2", "<big>necessaria</big>\n\n<b>II necessaria, ae</b> <i>f</i>\nродственница <i>или</i> близкий друг <i>C</i>."],
  ["necessarie", "<big>necessarie</big>\n\n<b>necessarie</b> [necessarius I]\n<b>1)</b> необходимо, по необходимости (comparatus cibus <i>VM</i>; impensae n. factae <i>Dig</i>);\n<b>2)</b> неопровержимо (n. demonstrari <i>C</i>)."],
  ["necessario", "<big>necessario</big>\n\n<b>necessario</b> <i>adv.</i> [necessarius I]\nпо необходимости, по принуждению, поневоле <i>Ter, rhH, C etc.</i>"],
  ["necessarius 1", "<big>necessarius</big>\n\n<b>I necessarius, a, um</b> [necesse]\n<b>1)</b> необходимый, потребный, нужный (omnia quae sunt ad vivendum necessaria <i>C</i>);\n<b>2)</b> обязательный, принудительный (lex <i>C</i>);\n<b>3)</b> неминуемый, неизбежный, неотвратимый, непреодолимый (mors <i>C</i>);\n<b>4)</b> настоятельный, повелительный, крайний (causa <i>Cs</i>): tempus necessarium <i>C, Cs</i> чрезвычайные обстоятельства, крайняя необходимость;\n<b>5)</b> неопровержимый, непреложный (mathematicorum ratio <i>C</i>; conclusio <i>C</i>);\n<b>6)</b> тесно связанный, близко стоящий, состоящий в родстве (homo <i>C, Nep</i>); <i>перен.</i> находящийся в тесной связи (alicui rei <i>C</i>);\n<b>7)</b> естественный, законный (heres <i>Dig</i>)."],
  ["necessarius 2", "<big>necessarius</big>\n\n<b>II necessarius, i</b> <i>m</i>\nблизкий человек, интимный друг (propinqui ac necessarii <i>C</i>); родственник (n. meus <i>C</i>): necessarii et consanguinei <i>Cs</i> друзья и близкие."],
  ["necesse", "<big>necesse</big>\n\n<b>necesse</b> [ne + cedo] <i>impers.</i>\n<i>тк. в сочетании с глаголами esse, habere, putare и т. п.</i>: n. est необходимо, должно (nomini n. est mori <i>C</i>; emere quod n. est <i>Cato ap. Sen</i>); n. habere считать нужным, долженствовать (non n. habeo dicere <i>L</i>) <i>или</i> (<i>редко</i>) нуждаться (non n. habent sani medico <i>Vlg</i>)."],
  ["necessitas", "<big>necessitas</big>\n\n<b>necessitas, atis</b> <i>f</i> [necesse]\n<b>1)</b> необходимость, неизбежность (n. fatalis <i>C</i>); принуждение (necessitatem alicui imponere <i>или</i> afferre <i>C</i>): ex necessitate, non ex voluntate <i>Dig</i> по необходимости, а не по доброй воле; parere (servire) necessitati <i>C</i> покориться необходимости; necessitate coactus <i>C</i> (permotus <i>Cs</i>) вынужденный необходимостью (под давлением необходимости); n. gaudendi <i>PJ</i> притворное веселье; facere de necessitate virtutem <i>погов. Hier</i> делать из необходимости добродетель; n. temporis <i>Cs</i> <i>или</i> necessitates majores <i>L</i> чрезвычайные обстоятельства, крайняя необходимость; ultima (suprema, extrema) n. <i>T, Sl, Sen, QC</i> последняя неизбежность, <i>т. е.</i> смерть;\n<b>2)</b> обязательная сила, нерушимость, незыблемость, неприкосновенность (n. ac religio <i>L</i>);\n<b>3)</b> непререкаемая власть (sanguinis paterni <i>C</i>);\n<b>4)</b> <i>pl.</i> потребности, нужды (suarum necessitatum causa <i>Cs</i>; indicare populo publicas necessitates <i>L</i>);\n<b>5)</b> нужда, бедность, скудость (fames et ceterae necessitates <i>Su</i>);\n<b>6)</b> <i>редко</i> тесная связь, близкие отношения, дружба <i>или</i> родство (necessitatem violare <i>C</i>)."],
  ["necessitudo", "<big>necessitudo</big>\n\n<b>necessitudo, inis</b> <i>f</i> [necesse]\n<b>1)</b> неизбежность, неотвратимость (n. alicui impendet <i>Sl</i>); необходимость (n. miserrima <i>VP</i>); принуждение (alicui necessitudinem imponere <i>C</i>);\n<b>2)</b> стеснённое положение, чрезвычайные обстоятельства (rerum necessitudine coactus <i>Sl</i>);\n<b>3)</b> неразрывная связь, тесная зависимость (rerum <i>C</i>);\n<b>4)</b> близкие отношения, тесная дружба: pro nostra necessitudine <i>C</i> из дружбы к нам || родство (n. quaestoris cum praetore <i>C</i>; n. familiaritasque <i>C</i>);\n<b>5)</b> <i>pl.</i> родственники, друзья <i>Su, PJ, Amm</i>."],
  ["necessum", "<big>necessum</big>\n\n<b>necessum (necessus) est</b> <i>Pl, Ter, Cato, Lcr etc.</i>\n= necesse est (<i>см.</i>)."],
  ["necleg-", "<big>necleg-</big>\n\n<b>necleg-</b> <i>v. l.</i>\n= negleg-."],
  ["necne", "<big>necne</big>\n\n<b>nec–ne</b> <i>conj.</i>\n<i>в косвенных, реже в прямых вопросах</i> или нет: sortibus declarare, utrum proelium committi ex usu sit, n. <i>Cs</i> объявлять на основании жеребьёвки, выгодно ли завязать сражение или нет; recte, n. <i>H</i> правильно ли, нет ли."],
  ["necnon", "<big>necnon</big>\n\n<b>nec–non (neque non)</b>\nа также, равным образом: tunc, nec non tamen ante <i>O</i> тогда, но также и раньше. — <i>См. тж.</i> neque 7."],
  ["neco", "<big>neco</big>\n\n<b>neco, avi</b> (<i>редко</i> <b>cui</b>), <b>atum, are</b> [nex]\n<b>1)</b> лишать жизни, убивать: n. aliquem excruciatum <i>Sl</i> замучить кого-л. насмерть; n. aliquem verberibus <i>C</i> засечь кого-л. до смерти; n. fame <i>C</i> уморить голодом; n. aliquem igni <i>Cs</i> сжечь кого-л.;\n<b>2)</b> губить, уничтожать (imbres necant frumenta <i>PM</i>)."],
  ["necopinans", "<big>necopinans</big>\n\n<b>necopinans, antis</b> <i>adj.</i> [nec + opinor]\nне ожидающий, не подозревающий: neque opinantibus omnibus <i>bAl</i> неожиданно для всех."],
  ["necopinato", "<big>necopinato</big>\n\n<b>nec–opinato</b> (<i>или в сочетании</i> ex n. <i>L</i>)\nнеожиданно, против ожидания, паче чаяния <i>C</i>."],
  ["necopinatus", "<big>necopinatus</big>\n\n<b>nec–opinatus, a, um</b>\nнеожиданный, нежданный, непредвиденный (bona <i>C</i>; adventus <i>L</i>)."],
  ["necopinus", "<big>necopinus</big>\n\n<b>nec–opinus, a, um</b>\n<b>1)</b> неожиданный, нежданный (mors <i>O</i>);\n<b>2)</b> не ожидающий, не ждавший (aliquem necopinum rapere <i>Ph</i>)."],
  ["necromantea", "<big>necromantea</big>\n\n<b>necromantea (-ia), ae</b> <i>f</i> (<i>греч.</i>)\nнекромантия, вызывание мёртвых и вопрошание их о будущем <i>Eccl</i>."],
  ["necrosis", "<big>necrosis</big>\n\n<b>necrosis, is</b> <i>f</i> (<i>греч.</i>)\nумерщвление <i>CA</i>."],
  ["Nectanabis", "<big>Nectanabis</big>\n\n<b>Nectanabis (Nectenebis), idis</b> <i>m</i>\nНектанабид, <i>имя двух египетских царей в первой половине IV в. до н. э.</i> <i>Nep</i>."],
  ["nectar", "<big>nectar</big>\n\n<b>nectar, aris</b> <i>n</i> (<i>греч.</i>)\n<b>1)</b> нектар, <i>напиток богов</i> <i>C, O etc.</i>;\n<b>2)</b> божественное притирание, бальзам <i>O</i>;\n<b>3)</b> приятный напиток <i>или</i> запах (Baccheum n. <i>St</i> = vinum; cellas distendere nectare <i>V</i> = melle; ferre in ubere n. <i>O</i> = lac);\n<b>4)</b> сладкие звуки, поэзия (Pegaseium n. <i>Pers</i>)."],
  ["nectarea", "<big>nectarea</big>\n\n<b>nectarea (nectaria), ae</b> <i>f</i> [nectareus]\n(<i>sc.</i> herba) <i>бот.</i> нектария, <i>предпол.</i> девясил (<i>Helenium</i>) <i>PM</i>."],
  ["nectareus", "<big>nectareus</big>\n\n<b>nectareus, a, um</b> [nectar]\n<b>1)</b> нектарный (aquae <i>O</i>);\n<b>2)</b> приятный как нектар (Falernum <i>M</i>)."],
  ["nectaria", "<big>nectaria</big>\n\n<b>nectaria</b> <i>v. l.</i>\n= nectarea."],
  ["nectarites", "<big>nectarites</big>\n\n<b>nectarites, ae</b> <i>m</i> [nectarea]\nвино, приправленное нектарией <i>PM</i>."],
  ["Nectenebis", "<big>Nectenebis</big>\n\n<b>Nectenebis</b> <i>v. l.</i>\n= Nactanabis."],
  ["necto", "<big>necto</big>\n\n<b>necto, nexui (nexi), nexum, ere</b>\n<b>1)</b> вязать, связывать, сплетать (coronam <i>H</i>; brachia <i>O</i>); <i>поэт.</i> обвязывать, обвивать (n. caput oliva <i>V</i>; comam myrto <i>O</i>); привязывать (pedibus talaria <i>V</i>); накидывать (laqueum alicui <i>H</i>); налагать, надевать (catenas alicui <i>H</i>; vincula <i>V</i>);\n<b>2)</b> соединять, связывать, сочетать (virtutes inter se nexae <i>C</i>): manibus nexis <i>Pt</i> взявши друг друга за руки; causa nexa (ex) aliqua re <i>C</i> причина, тесно связанная с чём-л. (зависящая от чего-л.); ex alio alia nectuntur <i>C</i> всё находится во взаимной связи;\n<b>3)</b> замышлять, задумывать (dolum <i>L</i>): n. jurgia cum aliquo <i>O, Pt</i> затевать ссору (начинать браниться) с кем-л.; n. moras <i>T, Ap</i> придумывать отсрочку за отсрочкой, откладывать, затягивать;\n<b>4)</b> связывать, обязывать, налагать обязательство (n. aliquem sacramento <i>Just</i>): n. aliquem <i>VM</i> отдать несамостоятельного должника в рабство заимодавцу (n. aliquem ob aes alienum <i>L</i>)."],
  ["necubi", "<big>necubi</big>\n\n<b>necubi</b> (<i>редко</i> <b>ut necubi</b> <i>Vr, Col</i>) <i>adv.</i> [ne + cubi]\nчтобы где-либо не: n. copias traducerent <i>Cs</i> (следить), чтобы (римляне) не переправили где-л. свои войска."],
  ["necui", "<big>necui</big>\n\n<b>necui</b> — <i>см.</i>\nпесо."],
  ["necunde", "<big>necunde</big>\n\n<b>necunde</b> <i>adv.</i> [ne + cunde]\nчтобы откуда-нибудь не: n. impetus fieret <i>L</i> (наблюдать), чтобы откуда-л. не последовало нападения."],
  ["necydalus", "<big>necydalus</big>\n\n<b>necydalus, i</b> <i>m</i> (<i>греч.</i>)\nкуколка шелкопряда <i>PM</i>."],
  ["necyomantia", "<big>necyomantia</big>\n\n<b>necyomantia (-ea), ae</b> <i>f</i> <i>PM</i>\n= necromantea."],
  ["nedum", "<big>nedum</big>\n\n<b>ne–dum</b>\n<b>1.</b> <i>adv.</i> (<i>или</i> n. ut) не говоря уже о; <i>после отрицания</i> тем менее; <i>после утверждения</i> тем более: ne voce quidem incommoda, n. ut ulla vis fieret <i>L</i> без единого протеста, не говоря уже о насилии; vel in pace, n. in bello <i>L</i> даже в мирное время, а (уж) тем более во время войны; n. quidquam probi moris reservaretur <i>T</i> а уж о сохранении сколько-нибудь чистых нравов и говорить нечего;\n<b>2.</b> <i>conj.</i> <i>в начале предложения</i> не говоря уже (о), не только (n. homines humiles, sed etiam amplissimi viri <i>C</i>); <i>арх.</i> чтобы (как бы) не: n. (<i>v. l.</i> ne tum) a me argentum petat <i>Pl</i> только бы он не потребовал у меня денег."],
  ["nefacio", "<big>nefacio</big>\n\n<b>ne–facio, —, —, ere</b>\nне делать, бездействовать <i>Pl</i> (<i>v. l.</i>)."],
  ["nefandus", "<big>nefandus</big>\n\n<b>nefandus, a, um</b> [ne + for]\nнечестивый, преступный, ужасный, гнусный (homo <i>Q</i>; scelus <i>C</i>; odium <i>V</i>)."],
  ["nefans", "<big>nefans</big>\n\n<b>nefans, antis</b> <i>adj.</i> <i>LM, Vr</i>\n= nefandus."],
  ["nefarie", "<big>nefarie</big>\n\n<b>nefarie</b> [nefarius I]\nнечестиво, преступно, ужасно: aliquid n. facere (committere) <i>C</i> совершить нечестивый Поступок."],
  ["nefarium", "<big>nefarium</big>\n\n<b>nefarium, i</b> <i>n</i> [nefarius]\nнечестивый поступок, преступление, злодеяние <i>C, L</i>."],
  ["nefarius 1", "<big>nefarius</big>\n\n<b>I nefarius, a, um</b> [nefas]\nнечестивый, преступный, беззаконный (homo, bellum <i>C</i>; facinus <i>C, Sl, Cs</i>)."],
  ["nefarius 2", "<big>nefarius</big>\n\n<b>II nefarius, i</b> <i>m</i>\nнечестивец, злодей, преступник <i>L, C</i>."],
  ["nefas", "<big>nefas</big>\n\n<b>ne–fas</b> <i>indecl. n</i>\n<b>1)</b> (<i>тж.</i> vetitum n. <i>H</i>) беззаконие, нечестие, злодеяние, грех: n. est <i>C etc.</i> нечестиво, грешно, непозволительно, нельзя; n. dictu <i>C etc.</i> и сказать нельзя, грешно говорить; quicquid corrigere est n. <i>H</i> всё, что непоправимо; n.! <i>или</i> (e)heu n.! <i>H, V, Ctl</i> о ужас!;\n<b>2)</b> чудовище, злодей(ка) <i>V, O</i>."],
  ["nefastus", "<big>nefastus</big>\n\n<b>ne–fastus, a, um</b>\n<b>1)</b> запретный, неприсутственный: dies nefasti <i>Vr, L, O, Su</i> запретные дни (<i>в которые, по религиозным соображениям, нельзя было созывать совещания, устраивать судебные заседания и выносить приговоры</i>);\n<b>2)</b> зловещий, роковой, приносящий несчастье, несчастный (dies <i>Su, T, H</i>; terra <i>L</i>);\n<b>3)</b> нечестивый, достойный проклятия, ужасный (crimen <i>PM</i>)."],
  ["nefrens", "<big>nefrens</big>\n\n<b>nefrens, frendis</b> <i>adj.</i> [ne + frendo]\nне умеющий ещё кусать, не имеющий ещё зубов (porcus <i>Vr</i>)."],
  ["negantia", "<big>negantia</big>\n\n<b>negantia, ae</b> <i>f</i> [nego]\nотрицание <i>C</i>."],
  ["negantinummius", "<big>negantinummius</big>\n\n<b>neganti–nummius, a, um</b>\nотказывающийся платить <i>Ap</i> (<i>v. l.</i>)."],
  ["negassim", "<big>negassim</big>\n\n<b>negassim</b> <i>Pl</i>\n(= negaverim) <i>арх. pf. conjct. к</i> nego."],
  ["negatio", "<big>negatio</big>\n\n<b>negatio, onis</b> <i>f</i> [nego]\nотрицание (n. infitiatioque facti <i>C</i>)."],
  ["negativus", "<big>negativus</big>\n\n<b>negativus, a, um</b> [nego]\n<b>1)</b> отрицающий, отклоняющий (verba, actio <i>G</i>);\n<b>2)</b> отрицательный (particula <i>Ap</i>)."],
  ["negator", "<big>negator</big>\n\n<b>negator, oris</b> <i>m</i>\nотрицатель <i>Eccl</i>."],
  ["negatorius", "<big>negatorius</big>\n\n<b>negatorius</b> <i>Dig</i>\n= negativus 1."],
  ["negatrix", "<big>negatrix</big>\n\n<b>negatrix, icis</b> <i>f</i>\nотрицательница <i>Eccl</i>."],
  ["negito", "<big>negito</big>\n\n<b>negito, avi, —, are</b> [<i>intens. к</i> nego]\nпостоянно отвергать, упорно отрицать <i>Pl, Lcr, C, Sl etc.</i>"],
  ["neglecte", "<big>neglecte</big>\n\n<b>neglecte</b> [neglectus I]\nнебрежно, нерадиво <i>Hier</i>."],
  ["neglectio", "<big>neglectio</big>\n\n<b>neglectio, onis</b> <i>f</i> [neglego]\nпренебрежение, равнодушие: n. alicujus <i>C</i> равнодушное отношение к кому-л."],
  ["neglector", "<big>neglector</big>\n\n<b>neglector, oris</b> <i>m</i>\nпренебрегающий, пренебрегший <i>Eccl</i>."],
  ["neglectus 1", "<big>neglectus</big>\n\n<b>I neglectus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> neglego;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> оставленный без внимания, заброшенный (ager <i>H</i>);\n<b>2)</b> простой, непритязательный: forma viros neglecta decet <i>O</i> мужчинам не пристало слишком заботиться о своей внешности."],
  ["neglectus 2", "<big>neglectus</big>\n\n<b>II neglectus, us</b> <i>m</i> [neglego]\nпренебрежение, равнодушие, безразличное отношение <i>Ter, PM</i>."],
  ["neglegens", "<big>neglegens</big>\n\n<b>neglegens, entis</b>\n<b>1.</b> <i>part. praes. к</i> neglego;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> небрежный, нерадивый, беспечный (in aliqua re <i>C, Sen</i> <i>и</i> circa aliquid <i>Su</i>); расточительный (modo parcior, modo neglegentior <i>Su</i>): n. rei alicujus <i>C etc.</i> равнодушный (безразличный) к чему-л., пренебрегающий чём-л.;\n<b>2)</b> неопрятный, нечёсаный <i>или</i> нестриженый (barba <i>Sen</i>)."],
  ["neglegenter", "<big>neglegenter</big>\n\n<b>neglegenter</b> [neglegens]\nбеспечно, нерадиво, небрежно, равнодушно, неаккуратно <i>C, L etc.</i>"],
  ["neglegentia", "<big>neglegentia</big>\n\n<b>neglegentia, ae</b> <i>f</i> [neglegens]\n<b>1)</b> нерадение, небрежность, беспечность (n. et pigritia <i>C</i>; n. in accusando <i>C</i>); неаккуратность (n. epistularum <i>C</i>);\n<b>2)</b> пренебрежение, неуважение: n. alicujus <i>Ter, C, T</i> равнодушие (неуважение) к кому-л."],
  ["neglego", "<big>neglego</big>\n\n<b>neglego, lexi, lectum, ere</b> [nec + lego]\n<b>1)</b> не заботиться, забрасывать, относиться беспечно (n. rem familiarem <i>Nep</i>); упускать <i>или</i> отказываться (n. aliquid facere <i>Pl etc.</i>): non neglexisse habeo gratiam <i>Ter</i> благодарю за аккуратность || быть равнодушным, допускать (Armeniam a Parthis occupari <i>Su</i>); смотреть сквозь пальцы, оставлять без внимания, не наказывать, спускать (n. facinus, pecuniam captam <i>C</i>; injurias <i>Cs</i>);\n<b>2)</b> пренебрегать, презирать (n. periculum <i>C</i>): metu mortis neglecto <i>Cs</i> презирая смерть; n. committere fraudem <i>H</i> не останавливаться перед совершением преступления || не считаться, презирать (deos <i>Sl</i>; leges <i>C</i>)."],
  ["neglig-", "<big>neglig-</big>\n\n<b>neglig-</b> <i>v. l.</i>\n= negleg-."],
  ["nego", "<big>nego</big>\n\n<b>nego, avi, atum, are</b> [nec]\n<b>1)</b> давать отрицательный ответ, отрицать (n. aliquam rem <i>C etc.</i>): negavit recte facere, quod seniori conviciarer <i>Pt</i> он сказал, что я нехорошо поступил, обругав старшего (годами); nunc aiunt, quod tunc negabant <i>C</i> теперь они утверждают то, что прежде отрицали; negat se meminisse <i>Tib</i> он заявляет, что не помнит; membra negant illi <i>St</i> его тело слабеет; poma negat regio <i>O</i> страна лишена плодов;\n<b>2)</b> отказывать (vilia alimenta miseris <i>O</i>): n. alicui aliquid <i>C, Cs, H etc.</i> отказывать кому-л. в чём-л.; n. se alicui <i>Ter</i> не отвечать кому-л. взаимностью; vela ventis n. <i>O</i> убрать паруса; se vinculis n. <i>Tib</i> освободиться от пут; comitem (se) n. <i>O</i> отказаться сопровождать; se alicui videndum n. <i>O</i> отворачиваться от кого-л., избегать кого-л.; vivis fama negatur <i>M</i> живым (обычно) отказывают в признании; vela negata pelago meo <i>O</i> паруса не показываются в моих водах; cupere negata <i>O</i> стремиться к запретному."],
  ["negotialis", "<big>negotialis</big>\n\n<b>negotialis, e</b> [negotium]\nкасающийся дела, деловой (pars <i>C</i>)."],
  ["negotians", "<big>negotians</big>\n\n<b>negotians, antis</b> <i>m</i>\nделец, <i>т. е.</i> оптовый торговец <i>или</i> крупный банкир <i>C, Su, Sen</i>."],
  ["negotiatio", "<big>negotiatio</big>\n\n<b>negotiatio, onis</b> <i>f</i> [negotior]\nоптовая торговля, <i>тж.</i> банковские операции <i>C, VM, Col etc.</i>; торговля (<i>вообще</i>) (sagaria et lintearia <i>Dig</i>); constat n. omnis ex empto et vendito <i>Sen</i> всякая торговля (торговая сделка) состоит из купли и продажи."],
  ["negotiator", "<big>negotiator</big>\n\n<b>negotiator, oris</b> <i>m</i> [negotior]\nоптовый торговец <i>или</i> банкир <i>C etc.</i>; торговец (<i>вообще</i>), купец, коммерсант <i>Q, VP etc.</i>"],
  ["negotiatorius", "<big>negotiatorius</big>\n\n<b>negotiatorius, a, um</b> [negotiator]\n<b>1)</b> купеческий (navis <i>Vop</i>);\n<b>2)</b> взимаемый с торгового оборота (aurum <i>Lampr</i>)."],
  ["negotiatrix", "<big>negotiatrix</big>\n\n<b>negotiatrix, icis</b> <i>f</i>\n<b>1)</b> купчиха, коммерсантка <i>Dig</i>;\n<b>2)</b> посредница <i>Tert</i>."],
  ["negotinummius", "<big>negotinummius</big>\n\n<b>negotinummius, a, um</b> [negotium + nummus]\nстоящий денег <i>Ap</i> (<i>v. l.</i>)."],
  ["negotiolum", "<big>negotiolum</big>\n\n<b>negotiolum, i</b> <i>n</i> [<i>demin. к</i> negotium]\nнебольшое дело, дельце <i>Pl, C</i>."],
  ["negotior", "<big>negotior</big>\n\n<b>negotior, atus sum, ari</b> <i>depon.</i> [negotium]\nзаниматься крупной торговлей, торговать оптом <i>или</i> заниматься финансовыми операциями <i>C, Sen, Dig</i>; торговать (<i>вообще</i>) <i>L, Vtr, Su</i>."],
  ["negotiositas", "<big>negotiositas</big>\n\n<b>negotiositas, atis</b> <i>f</i> [negotiosus]\nзанятость многими делами <i>AG</i>."],
  ["negotiosus", "<big>negotiosus</big>\n\n<b>negotiosus, a, um</b> [negotium]\nзанятый делами, деятельный, деловой (homo <i>Pl etc.</i>); полный дел, хлопотный (provincia <i>C</i>); посвященный делам (cogitatio <i>CC</i>); рабочий, будний (dies <i>T</i>): tergum negotiosum <i>шутл. Pl</i> не отдыхающая (от побоев) спина."],
  ["negotium", "<big>negotium</big>\n\n<b>negotium, i</b> <i>n</i> [nec + otium]\n<b>1)</b> занятие, дело, работа, деятельность (negotia domestica <i>C</i>): in negotio esse <i>Ter</i> быть занятым; in ipso negotio <i>C</i> в процессе (в разгаре) дела; n. publicum <i>Pl, C, Sl etc.</i> государственное дело, общественная работа, <i>тж.</i> должность, пост; n. alicui dare <i>C, Nep, T etc.</i> (mandare <i>C</i>) возложить на кого-л. (поручить кому-л.) дело; n. gerere (agere) <i>C, QC etc.</i> вести дело (дела); quid negotii est? <i>Pl</i> в чём дело?, что такое? (<i>ср.</i> 2); quid negotii mihi est? <i>Ter, Cs</i> какое мне дело?; in magno negotio habere aliquid facere <i>Su</i> ревностно делать что-л.;\n<b>2)</b> трудность, трудное дело, беспокойство (n. magnum <i>C</i>; satis habui negotii <i>C</i>): quid negotii est? <i>C</i> что (тут) трудного (особенного)? (<i>ср.</i> 1); nihil negotii est id facere <i>C</i> сделать это нетрудно; magno negotio <i>bAl, CC</i> с большим трудом; nullo (<i>или</i> sine) negotio <i>C, Nep</i> без всякого труда; facili (levi) negotio <i>AV, Amm</i> без особого труда, легко;\n<b>3)</b> неприятность, хлопоты (molestis negotiis implicari <i>C</i>): facessere <i>C</i> (facere <i>Q</i>, exhibere <i>C</i>) alicui negotia наделать кому-л. хлопот;\n<b>4)</b> обстоятельства, положение (in atroci negotio <i>Sl</i>);\n<b>5)</b> военное предприятие, сражение, бой: negotia bene gerere <i>Cs</i> успешно воевать;\n<b>6)</b> личность, создание, субъект (n. lentum, notum <i>C</i>)."],
  ["Neleius 1", "<big>Neleius</big>\n\n<b>I Neleius (Neleus), a, um</b> [Neleus]\nнелеев <i>O</i>."],
  ["Neleius 2", "<big>Neleius</big>\n\n<b>II Neleius, i</b> <i>m</i> <i>O</i>\n= Nelides, <i>т. е.</i> Nestor."],
  ["Neleus 1", "<big>Neleus</big>\n\n<b>I N&#275;le&#865;us, e&#299;</b> <i>m</i>\nНелей, <i>родом из Фессалии, сын Нептуна, брат Пелия, основатель Пилоса в Мессении, отец Нестора</i> <i>O</i>."],
  ["Neleus 2", "<big>Neleus</big>\n\n<b>II N&#275;l&#275;us, a, um</b> <i>v. l.</i>\n= Neleius I."],
  ["Nelides", "<big>Nelides</big>\n\n<b>Nelides, ae</b> <i>m</i>\nсын <i>или</i> потомок Нелея <i>O</i>."],
  ["nema", "<big>nema</big>\n\n<b>nema, atis</b> <i>n</i> (<i>греч.</i>)\nпряжа <i>Dig</i>."],
  ["Nemausum", "<big>Nemausum</big>\n\n<b>Nemausum, i</b> <i>n</i> <i>и</i> <b>Nemausus, i</b> <i>f</i>\nНемауз, <i>город в Нарбонской Галлии</i> (<i>ныне</i> Nimes) <i>PM, Mela</i>."],
  ["Nemea 1", "<big>Nemea</big>\n\n<b>I Nemea, ae</b> <i>и</i> <b>Nemee, es</b> <i>f</i>\nНемея:\n<b>1)</b> <i>лесистая долина в сев. Арголиде с рощей Юпитера, где раз в два года совершались Немейские игры; здесь Геркулес убил Немейского льва</i> <i>C, V, M etc.</i>;\n<b>2)</b> <i>река между Коринфом и Сикионом, впадающая в Лехейский валив</i> <i>L</i>."],
  ["Nemea 2", "<big>Nemea</big>\n\n<b>II Nemea, orum</b> <i>n</i>\nНемейские игры <i>L, Vtr</i>."],
  ["Nemeaeus", "<big>Nemeaeus</big>\n\n<b>Nemeaeus, a, um</b> [Nemea]\nнемейский (leo, <i>тж.</i> pestis <i>и</i> moles <i>O</i>)."],
  ["Nemee", "<big>Nemee</big>\n\n<b>Nemee, es</b> <i>f</i> <i>v. l.</i>\n= Nemea I."],
  ["Nemesa", "<big>Nemesa</big>\n\n<b>Nemesa, ae</b> <i>f</i>\nНемеза, <i>река в Gallia Belgica</i> (<i>ныне</i> Nims) <i>Aus</i>."],
  ["Nemesiaci", "<big>Nemesiaci</big>\n\n<b>Nemesiaci, orum</b> <i>m</i>\nпредсказатели-шарлатаны <i>CTh</i>."],
  ["Nemesianus", "<big>Nemesianus</big>\n\n<b>Nemesianus, i</b> <i>m</i>\n(M. Aurelius Olympius) <i>поэт III в. н. э.</i>"],
  ["Nemesis", "<big>Nemesis</big>\n\n<b>Nemesis, is</b> <i>и</i> <b>eos</b> <i>f</i>\nНемесида:\n<b>1)</b> <i>богиня возмездия</i> <i>Ctl, PM, Macr</i>;\n<b>2)</b> <i>женское имя</i> <i>Tib</i>."],
  ["Nemetes", "<big>Nemetes</big>\n\n<b>Nemetes, um</b> <i>m</i>\nнеметы, <i>племя в Gallia Belgica, с главн. городом Noviomagus</i> (<i>ныне</i> Шпейер) <i>Cs, T</i>."],
  ["Nemetocenna", "<big>Nemetocenna</big>\n\n<b>Nemetocenna, ae</b> <i>f</i>\nНеметоценна, <i>главн. город атребатов в Gallia Belgica</i> (<i>ныне</i> Arras) <i>Hirt</i>."],
  ["nemo", "<big>nemo</big>\n\n<b>nemo, inis</b> (<i>в класс. лат.</i> <i>gen.</i> nullius, <i>abl.</i> nullo) [<i>из</i> ne + hemo = homo]\n<b>1.</b> <i>subst. m, f</i> никто: n. amicorum <i>C</i> никто из друзей; n. unus <i>C, Cs</i> <i>или</i> unus n. <i>rhH</i> (n. omnium <i>C, Sl etc.</i>) ни один; non n. <i>C etc.</i> кое-кто, иной, один-другой; n. non <i>C etc.</i> любой, всякий: n. non nostrum peccat <i>Pt</i> всякий из нас ошибается; n. omnes, neminem omnes fefellerunt <i>PJ</i> (никогда) отдельный человек не мог обмануть всех, да и все не могли обмануть отдельного человека; nemini nihil satis est <i>Pt</i> никто ничем (никогда) не довольствуется;\n<b>2.</b> <i>adj.</i> ни один, никакой (n. hostis <i>L</i>; n. scriptor <i>C</i>)."],
  ["nemon'?", "<big>nemon'?</big>\n\n<b>nemo–n'?</b> <i>Pers</i>\n= nemone?"],
  ["nemoralis", "<big>nemoralis</big>\n\n<b>nemoralis, e</b> [nemus]\nотносящийся к роще, лесной (umbra <i>O</i>; templum Dianae <i>O</i>); находящийся в роще (antrum <i>O</i>)."],
  ["nemorensis 1", "<big>nemorensis</big>\n\n<b>I nemorensis, e</b> <i>Col</i>\n= nemoralis."],
  ["Nemorensis 2", "<big>Nemorensis</big>\n\n<b>II Nemorensis, e</b> [nemus]\nотносящийся к роще Дианы у Ариции (lacus <i>Prp</i>); rex N. <i>Su</i> = жрец Дианы Арицийской."],
  ["nemoricultrix", "<big>nemoricultrix</big>\n\n<b>nemori–cultrix, tricis</b> <i>f</i> [nemus + colo]\nобитательница леса, <i>т. е.</i> лесная, дикая (sus <i>Ph</i>)."],
  ["nemorivagus", "<big>nemorivagus</big>\n\n<b>nemorivagus, a, um</b> [nemus + vagor]\nбродящий по рощам, скитающийся по лесам, дикий (aper <i>Ctl</i>)."],
  ["nemorosus", "<big>nemorosus</big>\n\n<b>nemorosus, a, um</b> [nemus]\n<b>1)</b> лесистый, поросший лесом (vallis <i>Sl</i>);\n<b>2)</b> густой (silva <i>O</i>);\n<b>3)</b> густолиственный (platanus <i>PM</i>)."],
  ["Nemossus", "<big>Nemossus</big>\n\n<b>Nemossus, i</b> <i>f</i>\nНемосс, <i>главн. город арвернов</i> (<i>ныне</i> Clermont-Ferrand) <i>Lcn</i>."],
  ["nempe", "<big>nempe</big>\n\n<b>nempe</b> [nam + <i>указ. частица</i> pe]\n<b>1)</b> ибо ведь, очевидно, понятно, конечно, надо думать, само собой разумеется, без сомнения: n. eum dicis <i>Pl</i> ты, конечно, о нём говоришь; n. si te nihil impediret <i>C</i> если, понятно, ничто тебе не помешает;\n<b>2)</b> именно, действительно, в самом деле (n. negas? <i>C</i>)."],
  ["nemus", "<big>nemus</big>\n\n<b>nemus, oris</b> <i>n</i>\n<b>1)</b> роща, лес (nemora silvaeque <i>C</i>): N. (<i>sc.</i> Dianae) <i>C</i> роща Дианы у Ариции;\n<b>2)</b> дрова (n. congerere <i>SenT</i>)."],
  ["nenia 1", "<big>nenia</big>\n\n<b>I nenia (naenia), ae</b> <i>f</i>\n<b>1)</b> погребальная песнь (neniae, quo vocabulo etiam a Graecis cantus lugubres nominantur <i>C</i>): id fuit n. ludo <i>погов. Pl</i> песенка спета (пир кончился бедой);\n<b>2)</b> жалобная песня: Cea n. <i>H</i> грустная кеосская поэзия (<i>т. е.</i> Симонида) || грустная повесть (dicere alicui neniam de aliqua re <i>Pl</i>);\n<b>3)</b> заклинание, магическая формула, заговор <i>H, O</i>;\n<b>4)</b> песенка (n. puerorum <i>H</i>);\n<b>5)</b> пустяк, вздор (viles neniae <i>Ph</i>; alias nenias inveni <i>Pt</i>)."],
  ["Nenia 2", "<big>Nenia</big>\n\n<b>II Nenia, ae</b> <i>f</i>\nНения, <i>богиня погребальных песен</i> <i>Aug</i>."],
  ["neo", "<big>neo</big>\n\n<b>neo, nevi, netum, nere</b> (<i>греч.</i>)\n<b>1)</b> прясть (stamina, fila <i>O</i>);\n<b>2)</b> <i>поэт.</i> ткать, расшивать (tunicam auro <i>V</i>)."],
  ["Neobule", "<big>Neobule</big>\n\n<b>Neobule, es</b> <i>f</i>\nНеобула, <i>женское имя</i> <i>H</i>."],
  ["Neocles", "<big>Neocles</big>\n\n<b>Neocles, is</b> <i>и</i> <b>i</b> <i>m</i>\nНеокл:\n<b>1)</b> <i>отец Фемистокла</i> <i>Nep</i>;\n<b>2)</b> <i>отец Эпикура</i> <i>C</i>."],
  ["Neoclides", "<big>Neoclides</big>\n\n<b>Neoclides, ae</b> <i>m</i>\nсын Неокла, <i>т. е.</i> Фемистокл <i>O</i>."],
  ["neomenia", "<big>neomenia</big>\n\n<b>neomenia, ae</b> <i>f</i> (<i>греч.</i>)\nноволуние <i>Vlg, Tert</i>."],
  ["Neontichos", "<big>Neontichos</big>\n\n<b>Neontichos, us</b> <i>n</i> (<i>греч.</i> &laquo;Новая стена&raquo;)\nНеонтих, <i>укреплённое место на Фракийском побережье Пропонтиды</i> <i>Nep</i>."],
  ["neophytus", "<big>neophytus</big>\n\n<b>neophytus, i</b> <i>m</i> (<i>греч.</i>)\nнеофит, новообращённый <i>Tert</i>."],
  ["Neoptolemus", "<big>Neoptolemus</big>\n\n<b>Neoptolemus, i</b> <i>m</i>\nНеоптолем:\n<b>1)</b> <i>сын Ахилла и Деидамии</i> (<i>другое его имя</i> Pyrrhus), <i>муж Гермионы, основатель царства в Эпире</i> <i>C, V</i>;\n<b>2)</b> <i>мужское имя</i> <i>Nep, Just, Su</i>."],
  ["neopum", "<big>neopum</big>\n\n<b>neopum, i</b> <i>n</i>\nминдальное масло <i>PM</i>."],
  ["neotericus", "<big>neotericus</big>\n\n<b>neotericus, a, um</b> (<i>греч.</i>)\nновый, современный <i>AV</i>."],
  ["nepa", "<big>nepa</big>\n\n<b>nepa, ae</b> <i>f</i>\n<b>1)</b> скорпион <i>Col, C</i>; созвездие Скорпиона <i>C</i>;\n<b>2)</b> рак <i>Pl</i>; созвездие Рака <i>C</i>."],
  ["Nepe", "<big>Nepe</big>\n\n<b>Nepe</b> <i>и</i> <b>Nepete, is</b> <i>n</i>\nНепета, <i>город в южн. Этрурии</i> <i>VP, L, PM</i>."],
  ["Nepesinus", "<big>Nepesinus</big>\n\n<b>Nepesinus, a, um</b> [Nepe(te)]\nнепетинский <i>L</i>."],
  ["nepeta", "<big>nepeta</big>\n\n<b>nepeta, ae</b> <i>f</i> <i>бот.</i>\nкотовик, кошачья мята <i>CC, PM</i>."],
  ["Nepete", "<big>Nepete</big>\n\n<b>Nepete, is</b> <i>n</i> <i>L</i>\n= Nepe."],
  ["Nephelaeus", "<big>Nephelaeus</big>\n\n<b>Nephelaeus, a, um</b> [Nephele]\nнефелин: Nephelaeum pecus <i>VF</i> = aries a Nephele missus."],
  ["Nephele", "<big>Nephele</big>\n\n<b>Nephele, es</b> <i>f</i>\nНефела, <i>первая жена Атаманша, мать Фрикса и Геллы</i> <i>O</i>."],
  ["Nepheleias", "<big>Nepheleias</big>\n\n<b>Nepheleias, adis</b> <i>f</i> <i>Lcn</i>\n= Nepheleis."],
  ["Nepheleis", "<big>Nepheleis</big>\n\n<b>Nepheleis, idos</b> <i>и</i> <b>idis</b> <i>f</i>\nдочь Нефелы, <i>т. е.</i> Helle <i>O</i>."],
  ["nephritis", "<big>nephritis</big>\n\n<b>nephritis, idis</b> <i>f</i> (<i>греч.</i>; <i>лат.</i> renatis passio) <i>мед.</i>\nнефрит, болезнь почек <i>CA, Is</i>."],
  ["nepos 1", "<big>nepos</big>\n\n<b>I nepos, otis</b> <i>m</i>\n<b>1)</b> внук: Pompei n. ex filia <i>C</i> внук Помпея по женской линии; n. Atlantis <i>H</i> = Mercurius; n. Nereius <i>H</i> = Achilles; n. invisus <i>H</i> = Romulus;\n<b>2)</b> праправнук <i>C</i>; потомок <i>H, V, O etc.</i>;\n<b>3)</b> <i>поздн.</i> племянник <i>Eutr, Hier</i>\n<b>4)</b> росток, побег (<i>виноградной лозы</i>) <i>Col</i>;\n<b>5)</b> кутила, мот, расточитель (in patrimonio suo <i>C</i>)."],
  ["Nepos 2", "<big>Nepos</big>\n\n<b>II Nepos, otis</b> <i>m</i>\nНепот, <i>cognomen в роде Корнелиев; наиболее известен</i> Cornelius N. (<i>ок. 100-25 гг. до н. э.</i>), <i>римск. историк, автор &laquo;De viris illustribus&raquo;</i> <i>AG, PJ, PM</i>."],
  ["nepotalis", "<big>nepotalis</big>\n\n<b>nepotalis, e</b> [nepos 5]\nпышный, расточительный (luxus <i>Ap</i>; mensa <i>Amm</i>)."],
  ["nepotatus", "<big>nepotatus</big>\n\n<b>nepotatus, us</b> <i>m</i> [nepos 5]\nпышность, расточительность <i>PM, Su</i>."],
  ["nepotor", "<big>nepotor</big>\n\n<b>nepotor, —, ari</b> <i>depon.</i> [nepos 5]\n<b>1)</b> утопать в роскоши, быть расточительным <i>Tert</i>;\n<b>2)</b> расточать (liberalitatem <i>Sen</i>)."],
  ["nepotulus", "<big>nepotulus</big>\n\n<b>nepotulus, i</b> <i>m</i> [<i>demin. к</i> nepos I]\nвнучек <i>Pl</i>."],
  ["nepticula", "<big>nepticula</big>\n\n<b>nepticula, ae</b> <i>f</i> [<i>demin. к</i> neptis]\nвнученька <i>Symm</i>."],
  ["neptis", "<big>neptis</big>\n\n<b>neptis, is</b> (<i>иногда</i> <i>acc.</i> im, <i>abl.</i> i) <i>f</i> [nepos]\n<b>1)</b> внучка <i>C, Su, Dig etc.</i>: n. Veneris <i>O</i> = Ino; doctae neptes <i>O</i> = Музы;\n<b>2)</b> <i>поздн.</i> племянница <i>Spart</i>."],
  ["Neptunalia", "<big>Neptunalia</big>\n\n<b>Neptunalia, ium</b> <i>и</i> <b>orum</b> <i>n</i>\nНептуналии, празднества в честь Нептуна <i>Vr, Aus</i>."],
  ["Neptunalis", "<big>Neptunalis</big>\n\n<b>Neptunalis, e</b>\nнептунов (ludi <i>Tert</i>)."],
  ["Neptunicola", "<big>Neptunicola</big>\n\n<b>Neptuni–cola, ae</b> <i>m</i> [colo]\nпочитатель Нептуна <i>Sil</i>."],
  ["Neptunine", "<big>Neptunine</big>\n\n<b>Neptunine, es</b> <i>f</i>\nдочь Нептуна, <i>т. е.</i> Thetis <i>Ctl</i>."],
  ["Neptunius", "<big>Neptunius</big>\n\n<b>Neptunius, a, um</b> [Neptunus]\nнептунов: N. heros <i>O</i> = Theseus (<i>как потомок Нептуна</i>); N. dux <i>H</i> = Секст Помпеи (<i>выдававший себя за приёмного сына Нептуна</i>); Neptunia arva <i>V</i>, loca <i>Pl</i> <i>или</i> prata <i>C</i> = море; Neptunia Troja <i>V</i> — <i>которую Нептун (совместно с Аполлоном) обнёс стенами</i>; Neptuniae aquae <i>L</i> = источник близ Террацины."],
  ["Neptunus", "<big>Neptunus</big>\n\n<b>Neptunus, i</b> <i>m</i> (<i>у греков</i> Посейдон)\nНептун, <i>сын Сатурна и Реи, брат Юпитера и Плутона, муж Амфитриты, бог морей и всех водоёмов</i> <i>C, V, Ctl etc.</i>"],
  ["nequam", "<big>nequam</big>\n\n<b>nequam</b> <i>adj. indecl.</i> (<i>compar.</i> nequior, <i>superl.</i> nequissimus) [ne + quam]\nникуда не годный, ничего не стоящий, дрянной (libellus <i>M</i>): n. illud verbum est <i>Pl</i> никуда не годится это выражение || негодный, беспутный, озорной (homo <i>VP</i>): alicui n. dare (facere) <i>Pl</i> сыграть с кем-л. штуку; aleator quanto in arte est melior, tanto est nequior <i>PS</i> чем искуснее игрок, тем он негоднее."],
  ["nequaquam", "<big>nequaquam</big>\n\n<b>ne–quaquam</b> <i>adv.</i>\nникоим образом, никак, совсем (отнюдь) не, ни в какой мере <i>Pl, C etc.</i>: n. satis in re una consumere curam <i>H</i> отнюдь недостаточно сосредоточить все свои помыслы на чём-л. одном."],
  ["neque", "<big>neque</big>\n\n<b>ne–que</b> <i>или</i> <b>nec</b>\n<b>1)</b> <i>арх.</i> = non (senatori, qui nec aderit, culpa esto <i>lex ap. C</i>); <i>в классической латыни тк. в виде приставки</i>: necopinatus, negotium;\n<b>2)</b> и не, также не (non vidi n. scio <i>C</i>): nec in paupertatem cadere nec procul a paupertate discedere <i>Sen</i> не впадать в бедность, но и не стремиться далеко уйти от бедности; nec sine causa <i>C</i> да и не без причины; Druides a bello abesse consuerunt (= consueverunt) n. tributa pendunt <i>Cs</i> друиды обыкновенно не участвуют в походах и не платят податей;\n<b>3)</b> (<i>после предшествующего утверждения</i>) но не (prodesse qui vult, nec potest <i>PS</i>): Romani hostem in fugam dederunt n. persequi potuerunt <i>Cs</i> римляне обратили неприятеля в бегство, но не смогли его преследовать;\n<b>4)</b>: n. is (ea, id) и притом не (erant in eo plurimae litterae n. eae vulgares <i>C</i>): neque eo minus <i>L</i> (<i>или</i> secius <i>Nep</i>) и тем не менее; saxa, nec modica <i>L</i> камни и, притом, огромные;\n<b>5)</b>: n. vero <i>C etc.</i> но не; n. enim <i>C etc.</i> ибо не; n. quisquam <i>C etc.</i> и никто;\n<b>6)</b> <i>в косвенных и разделительных вопросах</i>: necne или нет (factum sit necne, quaeritur <i>C</i>);\n<b>7)</b>: n. non (necnon) и конечно, а также, да и: n. haec tu non intellegis <i>C</i> и ты, конечно, это понимаешь;\n<b>8)</b>: n. aut... aut (vel... vel) и ни... ни <i>C, Su etc.</i>;\n<b>9)</b>: n... n. ни... ни, как не... так и не (virtus n. naufragio n. incendio amittitur <i>C</i>; n. mora, n. requies <i>V</i>);\n10): n... et (que) с одной стороны не..., но с другой стороны: quod neque natum unquam sit et semper sit futurum <i>C</i> (не бывает такого живого существа), которое никогда бы не родилось и никогда не умирало;\n11): et... n. (хотя) с одной стороны..., но, с другой, не...: et horum utrumque neque praeterea quicquam potest accidere <i>C</i> из обоих этих случаев оба возможны, никакой же другой возможности нет. nequedum (<i>тж. раздельно</i>) <i>C etc.</i> = necdum."],
  ["nequeo", "<big>nequeo</big>\n\n<b>ne–queo, ivi (ii), itum, ire</b>\nне быть в состоянии, не мочь, не иметь возможности: nequeo, quin fleam <i>Pl</i> я не могу удержаться от слёз; commissa tacere qui nequit <i>H</i> тот, кто не умеет хранить доверенные ему тайны; fines, quos ultra citraque nequit consistere rectum <i>H</i> пределы, по обе стороны которых не может быть ничего правильного."],
  ["nequicquam", "<big>nequicquam</big>\n\n<b>nequicquam</b> <i>v. l.</i>\n= nequiquam."],
  ["nequidquam", "<big>nequidquam</big>\n\n<b>nequidquam</b> <i>v. l.</i>\n= nequiquam."],
  ["nequior", "<big>nequior</big>\n\n<b>nequior</b> <i>compar. к</i>\nnequam."],
  ["nequiquam", "<big>nequiquam</big>\n\n<b>ne–quiquam</b> <i>adv.</i>\n<b>1)</b> напрасно, тщетно, безуспешно, без пользы, зря (perire <i>C</i>; implorare auxilium <i>Cs</i>; occasionem omissam quaerere <i>L</i>);\n<b>2)</b> безнаказанно (dicere tam indignum dictum <i>Pl</i>);\n<b>3)</b> без оснований, без нужды (aliquem exterrere <i>L</i>)."],
  ["nequisse", "<big>nequisse</big>\n\n<b>nequisse</b> <i>inf. pf. к</i>\nnequeo."],
  ["nequissimus", "<big>nequissimus</big>\n\n<b>nequissimus</b> <i>superl. к</i>\nnequam."],
  ["nequiter", "<big>nequiter</big>\n\n<b>nequiter</b> [nequam]\n<b>1)</b> беспутно, расточительно (aliquid facere <i>Cato</i>; cenare <i>C</i>);\n<b>2)</b> плохо, легкомысленно, неумело (bellum gerere <i>L</i>)."],
  ["nequitia", "<big>nequitia</big>\n\n<b>nequitia, ae</b> <i>f</i> [nequam]\n<b>1)</b> негодность, испорченность (aceti <i>PM</i>);\n<b>2)</b> легкомыслие, небрежность (summa stultitia nequitiaque <i>C</i>); лень (inertia et n. <i>C</i>);\n<b>3)</b> расточительность (luxuria atque n. <i>C</i>);\n<b>4)</b> беспутство, развратность, бесстыдство (n. ac turpitudo <i>C</i>);\n<b>5)</b> низость, подлость (malitia atque n. <i>rhH</i>)."],
  ["nequities", "<big>nequities</big>\n\n<b>nequities, ei</b> <i>f</i> <i>Ter, H, PM</i>\n= nequitia."],
  ["Nereine", "<big>Nereine</big>\n\n<b>Nereine, es</b> <i>f</i> <i>Ctl</i>\n= Nereis."],
  ["Nereis", "<big>Nereis</big>\n\n<b>Nereis, idis</b> <i>f</i>\nНереида, дочь Нерея, <i>морская нимфа</i> <i>Ctl, Tib, O</i>."],
  ["Nereius", "<big>Nereius</big>\n\n<b>Nereius, a, um</b> <i>adj. к</i>\nNereus: N. nepos <i>H</i> = Achilles; N. juvenis <i>O</i> = Phocus; Nereia genetrix <i>O</i> = Thetis."],
  ["Neretum", "<big>Neretum</big>\n\n<b>Neretum, i</b> <i>n</i>\nНерет, <i>город саллентинов в юго-зап. Калабрии</i> <i>O</i>."],
  ["Nereus", "<big>Nereus</big>\n\n<b>N&#275;re&#865;us, e&#299;</b> <i>и</i> <b>eos</b> <i>m</i>\nНерей, <i>сын Океана и Геи, морское божество, муж Дориды, отец 50 Нереид</i> <i>V, O, St etc.</i>"],
  ["Neria", "<big>Neria</big>\n\n<b>Neria, ae, Neriene, es</b> <i>и</i> <b>Nerio, enis</b> <i>f</i>\nНериена, <i>богиня сабинян, жена Марса</i> <i>Pl, AG etc.</i>"],
  ["Nerine", "<big>Nerine</big>\n\n<b>Nerine, is</b> <i>f</i> <i>V</i>\n= Nereis."],
  ["Nerinus", "<big>Nerinus</big>\n\n<b>Nerinus, a, um</b> [Nereus]\nнереев, <i>т. е.</i> морской (aqua <i>Nem</i>; animantes <i>Aus</i>)."],
  ["Nerio", "<big>Nerio</big>\n\n<b>Nerio, enis</b> <i>f</i> <i>v. l.</i>\n= Neria."],
  ["Neris", "<big>Neris</big>\n\n<b>Neris, idis</b> <i>f</i>\nНерида, <i>гора в Арголиде</i> <i>St</i>."],
  ["Neritius", "<big>Neritius</big>\n\n<b>Neritius, a, um</b> [Neritos]\nнеритский, <i>поэт.</i> итакский (dux <i>O</i>); одиссеев (domus <i>O</i>)."],
  ["Neritos", "<big>Neritos</big>\n\n<b>Neritos (-us), i</b>\nНерит:\n<b>1.</b> <i>m</i> <i>гора в сев. Итаке</i> <i>PM, SenT</i>;\n<b>2.</b> <i>f</i> <i>островок близ Итаки</i> <i>V, Mela</i>."],
  ["Nero", "<big>Nero</big>\n\n<b>Nero, onis</b> <i>m</i>\nНерон, <i>cognomen в роде Клавдиев; наиболее известны</i>:\n<b>1)</b> C. Claudius N., <i>полководец во время II Пунической войны, победивший Гасдрубала при Сене</i> (<i>Sena Gallica, 207 г. до н. э.</i>) <i>L, VM, H, Fl</i>;\n<b>2)</b> Tib. Claudius N., <i>участвовал на стороне Цезаря в Александрийской войне, впоследствии сторонник Антония, Помпея Младшего и Октавиана; его сыновья</i> (<i>от Ливии</i>) — Тиберий Нерон и Друз Нерон <i>C, Su, T, VP</i>;\n<b>3)</b> Tib. Claudius N., <i>род. в 37 г. н. э., в 54-68 гг. н. э. римск. император</i> <i>T, Su</i>."],
  ["Neroneus", "<big>Neroneus</big>\n\n<b>Neroneus</b> — <i>см.</i>\nNeronianus."],
  ["Neronia", "<big>Neronia</big>\n\n<b>Neronia, orum</b> <i>n</i>\n<i>игры в честь императора Нерона</i> <i>Su</i>."],
  ["Neronianus", "<big>Neronianus</big>\n\n<b>Neronianus</b> <i>и</i> <b>Neronius (Neroneus), a, um</b> [Nero]\nнеронов <i>C, T, Su etc.</i>"],
  ["Neropolis", "<big>Neropolis</big>\n\n<b>Neropolis, is</b> <i>f</i>\nНерополь, <i>название, которое Нерон хотел дать Риму</i> <i>Su</i>."],
  ["Nersae", "<big>Nersae</big>\n\n<b>Nersae, arum</b> <i>f</i>\nНерсы, <i>город эквов в Латии</i> <i>V</i>."],
  ["Nerthus", "<big>Nerthus</big>\n\n<b>Nerthus, i</b> <i>f</i>\n<i>германская богиня земли</i> <i>T</i>."],
  ["Nerulonensis", "<big>Nerulonensis</big>\n\n<b>Nerulonensis, e</b> [Nerulum]\nнерульский <i>Su</i>."],
  ["Nerulum", "<big>Nerulum</big>\n\n<b>Nerulum, i</b> <i>n</i>\nНерул, <i>город в южн. Лукании</i> <i>L</i>."],
  ["nerunt", "<big>nerunt</big>\n\n<b>nerunt</b> <i>3 л. pl. pf. к</i>\nneo."],
  ["Nerva", "<big>Nerva</big>\n\n<b>Nerva, ae</b> <i>m</i>\nНерва, <i>cognomen в роде Кощеев и Силиев; наиболее известен</i> M. Cocceius N., <i>род. в 32 г. до н. э., римск. император в 96-98 гг. н. э.</i> (<i>преемник Домициана</i>) <i>T</i>."],
  ["nervalis", "<big>nervalis</big>\n\n<b>nervalis, e</b> [nervus]\nнервный <i>или</i> сухожильный: n. herba <i>Scr предпол.</i> подорожник."],
  ["nervia", "<big>nervia</big>\n\n<b>nervia, ae</b> <i>f</i>\nструна (кишечная) (nervias extendere <i>Vr</i>; nerviae in fidibus <i>AG</i>)."],
  ["nerviceus", "<big>nerviceus</big>\n\n<b>nerviceus, a, um</b> [nervus]\nсвитый из сухожилий (funus <i>Vlg</i>)."],
  ["nervicus 1", "<big>nervicus</big>\n\n<b>I nervicus, a, um</b> <i>Vtr</i>\n(<i>v. l.</i>) = neuricus."],
  ["Nervicus 2", "<big>Nervicus</big>\n\n<b>II Nervicus, a, um</b> [Nervii]\nнервийский <i>Cs</i>."],
  ["Nervii", "<big>Nervii</big>\n\n<b>Nervii, orum</b> <i>m</i>\nнервии, <i>племя в Gallia Belgica, с главн. городом Bagacum</i> (<i>ныне</i> Bavay) <i>Cs, T</i>."],
  ["nervinus", "<big>nervinus</big>\n\n<b>nervinus</b> <i>Veg</i>\n= nerviceus."],
  ["nervium", "<big>nervium</big>\n\n<b>nervium, i</b> <i>n</i>\nсухожилие (habere nervia praecisa <i>Pt</i>)."],
  ["Nervius", "<big>Nervius</big>\n\n<b>Nervius, a, um</b> [Nerva]\nимператора Нервы (miles <i>Cld</i>)."],
  ["nervose", "<big>nervose</big>\n\n<b>nervose</b> [nervosus]\nкрепко, сильно, энергично (dicere <i>C</i>)."],
  ["nervositas", "<big>nervositas</big>\n\n<b>nervositas, atis</b> <i>f</i> [nervosus]\nсила, крепость <i>PM, CA</i>."],
  ["nervosus", "<big>nervosus</big>\n\n<b>nervosus, a, um</b> [nervus]\n<b>1)</b> жилистый, мускулистый (dorcas <i>Lcr</i>; poples <i>O</i>); крепкий (cauliculi <i>PM</i>);\n<b>2)</b> сильный, энергичный (vivacitas <i>VM</i>); (<i>по стилю</i>) мощный, убедительный (Aristoteles <i>C</i>)."],
  ["nervulus", "<big>nervulus</big>\n\n<b>nervulus, i</b> <i>m</i> [<i>demin. к</i> nervus]\nнекоторое усилие <i>или</i> силёнка (nervulos suos adhibere <i>C</i>)."],
  ["nervus", "<big>nervus</big>\n\n<b>nervus, i</b> <i>m</i>\n<b>1)</b> жила, сухожилие (nervi a quibus artus continentur <i>C</i>);\n<b>2)</b> membrum virile <i>H, Pt</i>;\n<b>3)</b> струна (nervos pellere <i>C</i>); <i>pl.</i> струнный инструмент: cantus nervorum et tibiarum <i>C</i> струнная и духовая музыка;\n<b>4)</b> нить: nervis alienis mobile lignum <i>H</i> марионетка; n. umbilicaris <i>Tert</i> пуповина;\n<b>5)</b> тетива (nervo aptare sagittas <i>V</i>); <i>перен.</i> лук: erumpit nervo sagitta <i>V</i> прянула с лука стрела;\n<b>6)</b> шкура, кожа (<i>обтягивающая щит</i>) <i>T, Sil</i>;\n<b>7)</b> ремень, путы, оковы (nervo vinctus <i>Pl</i>; in nervo atque compedibus aetatem agere <i>Cato</i>); <i>перен.</i> темница, тюрьма (in nervis teneri <i>L</i>);\n<b>8)</b> сила, крепость, мощь (omnibus nervis contendendum est <i>C</i>; nervi oratorii <i>C</i>);\n<b>9)</b> движущая сила, ведущее начало (nervi conjurationis <i>L</i>; nervi belli — pecunia infinita <i>C</i>)."],
  ["Nesactium", "<big>Nesactium</big>\n\n<b>Nesactium, i</b> <i>n</i>\nНесактий, <i>город истриев на реке Арсия</i> <i>L, PM</i>."],
  ["nesapius", "<big>nesapius</big>\n\n<b>ne–sapius, i</b> <i>m</i> [sapio]\nневежда, неуч <i>Pt</i>."],
  ["nescienter", "<big>nescienter</big>\n\n<b>nescienter</b>\nне зная, по неведению <i>Aug</i>."],
  ["nescio", "<big>nescio</big>\n\n<b>ne–scio, ivi (ii), itum, ire</b>\n<b>1)</b> не знать, не быть знакомым (n. aliquem, aliquid <i>или</i> de aliqua re <i>Pl, Ter, C etc.</i>); не понимать (Graece n. <i>C</i>): de aliqua re opinari, sed certum n. <i>C</i> полагать что-л., но не знать наверное; nescio quid narres <i>Ter</i> не понимаю, о чём ты толкуешь; n. metuere aliquid <i>PS</i> не знать страха перед чём-л.;\n<b>2)</b>: nescio qui (quae, quod) какой (какая, какое)-то (<i>ср. франц.</i> je ne sais quoi, quel <i>etc.</i>): nescio qua permotus divinatione <i>C</i> движимый каким-то предчувствием; nescio quid litterularum <i>C</i> одно (какое-то) маленькое письмецо; nescio quo modo (quo pacto) <i>C etc.</i> как-то <i>или</i> в известной мере, так сказать; n. quid <i>Pl etc.</i> что-то, нечто: nescio quid majus Iliade <i>Prp</i> нечто побольше (самой) Илиады; n. quando <i>Pl etc.</i> когда-то <i>или</i> когда-л.; n. unde <i>Pl etc.</i> откуда-то;\n<b>3)</b> не уметь (n. equo haerere <i>H</i>);\n<b>4)</b> не быть в состоянии (et vivere nolle et mori n. <i>Sen</i>): nescit vox missa reverti <i>погов. H</i> раз произнесённому слову уже не вернуться;\n<b>5)</b> воздерживаться (n. vinum <i>J</i>);\n<b>6)</b> не замечать (n. sua pericula <i>Lcn</i>)."],
  ["nescitus", "<big>nescitus</big>\n\n<b>nescitus, a, um</b> [nescio]\nнеизвестный, неведомый <i>Sid</i>."],
  ["nescius", "<big>nescius</big>\n\n<b>nescius, a, um</b> [ne + scio]\n<b>1)</b> незнающий, незнакомый (n. alicujus rei <i>или</i> de aliqua re <i>V, O etc.</i>): non sum n. <i>C</i> я хорошо знаю; haud n. <i>L, V</i> намеренно, с умыслом, сознательно;\n<b>2)</b> не умеющий, неспособный (fallere <i>V</i>): n. cedere <i>H</i> неумолимый; vinci n. <i>O</i> не знающий поражений, непобедимый;\n<b>3)</b> неведомый, неизвестный (causa <i>O</i>; loca <i>Pl</i>): non nescium habere <i>T</i> хорошо знать."],
  ["Nesis", "<big>Nesis</big>\n\n<b>Nesis, idis</b> <i>f</i>\nНесида, <i>островок близ Путеол</i> <i>C, Sen</i>."],
  ["nesse", "<big>nesse</big>\n\n<b>nesse</b> <i>inf. pf. к</i>\nneo."],
  ["Nesseus", "<big>Nesseus</big>\n\n<b>Nesseus, a, um</b> [Nessus]\nнессов (venenum <i>O</i>)."],
  ["nessotrophium", "<big>nessotrophium</big>\n\n<b>nessotrophium, i</b> <i>n</i> (<i>греч.</i>)\nпомещение для уток <i>Col</i>."],
  ["Nessus", "<big>Nessus</big>\n\n<b>Nessus, i</b> <i>m</i>\nНесс:\n<b>1)</b> <i>кентавр, убитый Геркулесом; его отравленной кровью Деианира напоила праздничную одежду Геркулеса, что послужило причиной мучительной смерти героя</i> <i>O, SenT</i>;\n<b>2)</b> (<i>или</i> Nestus) <i>река в зап. Фракии</i> <i>L, Mela</i>."],
  ["nestis", "<big>nestis</big>\n\n<b>nestis, idis</b> <i>f</i> (<i>acc.</i> in) (<i>греч.</i>; <i>лат.</i> intestinum jejunum)\nтощая кишка <i>CA</i>."],
  ["Nestor", "<big>Nestor</big>\n\n<b>Nestor, oris</b> <i>m</i> (<i>acc. sg.</i> ora)\nНестор, <i>сын Нелея и Хлориды, царь Пилоса (в Мессении), муж Эвридики, старейший участник Троянской войны</i> <i>C, Tib, O etc.</i>"],
  ["Nestoreus", "<big>Nestoreus</big>\n\n<b>Nestoreus, a, um</b> [Nestor]\nнесторов (senecta <i>M etc.</i>)."],
  ["Nestus", "<big>Nestus</big>\n\n<b>Nestus</b> <i>Mela</i>\n= Nessus 2."],
  ["nete", "<big>nete</big>\n\n<b>nete, es</b> <i>f</i> (<i>греч.</i>)\n<b>1)</b> самая высокая струна лиры <i>Vtr</i>;\n<b>2)</b> мизинец <i>Ap</i>."],
  ["Netini", "<big>Netini</big>\n\n<b>Netini, orum</b> <i>и</i> <b>Netinenses, ium</b> <i>m</i>\nнетяне, жители города Netum <i>C, PM</i>."],
  ["Netum", "<big>Netum</big>\n\n<b>Netum, i</b> <i>n</i>\nНет, <i>городок к юго-зап. от Сиракуз</i> (<i>ныне</i> Noto) <i>C, Sil</i>."],
  ["netus", "<big>netus</big>\n\n<b>netus, a, um</b> <i>part. pf. к</i>\nneo."],
  ["neu", "<big>neu</big>\n\n<b>neu</b> <i>adv.</i> <i>v. l.</i>\n= neve."],
  ["neunt", "<big>neunt</big>\n\n<b>neunt</b> <i>Tib, Lcn 3 л. pl. praes. к</i>\nneo."],
  ["Neuri", "<big>Neuri</big>\n\n<b>Neuri, orum</b> <i>m</i>\nневры, <i>племя в европейской Скифии</i> <i>PM, Mela, VF</i>."],
  ["neuricus", "<big>neuricus</big>\n\n<b>neuricus, a, um</b> (<i>греч.</i>)\nстрадающий невралгией <i>или</i> нервнобольной <i>Vtr</i>."],
  ["neurobates", "<big>neurobates</big>\n\n<b>neurobates, ae</b> <i>m</i> (<i>греч.</i>)\nканатный плясун <i>Vop</i>."],
  ["neurospaston", "<big>neurospaston</big>\n\n<b>neurospaston, i</b> <i>n</i> (<i>греч.</i>)\nмарионетка <i>AG</i>."],
  ["neurospastos", "<big>neurospastos</big>\n\n<b>neurospastos, i</b> <i>f</i> (<i>греч.</i>)\nшиповник <i>PM</i>."],
  ["neuter", "<big>neuter</big>\n\n<b>ne–uter, tra, trum</b>\n(<i>в три слога</i>) (<i>gen.</i> neutrius, <i>но в грам. смысле</i> neutri)\n<b>1)</b> ни один из обоих, ни тот ни другой (n. consulum <i>L</i>; vincitur n. <i>Ap</i>): in neutram partem <i>C</i> ни в одну (ни в ту, ни в другую) сторону; neutrum faciendum est <i>Sen</i> не следует делать ни того, ни другого; neutro modo <i>Ap</i> ни так, ни этак;\n<b>2)</b> <i>грам.</i> средний (genus, nomen, verbum <i>Vr, C etc.</i>)."],
  ["neutiquam", "<big>neutiquam</big>\n\n<b>ne–utiquam</b>\nни в коем случае, никоим образом, отнюдь (совсем) не (id n. mihi placet <i>Pl, L</i>)."],
  ["neutique", "<big>neutique</big>\n\n<b>neutique</b> <i>CTh</i>\n= neutiquam."],
  ["neutralis", "<big>neutralis</big>\n\n<b>neutralis, e</b> [neuter] <i>грам.</i>\nсредний, среднего рода (nomen <i>Q</i>)."],
  ["neutro", "<big>neutro</big>\n\n<b>neutro</b> [neuter]\nни в ту, ни в другую сторону (n. inclinata res <i>L</i>)."],
  ["neutropassiva verba", "<big>neutropassiva verba</big>\n\n<b>neutro–passiva verba</b> <i>n</i> <i>грам.</i>\nнейтропассивные, <i>т. е.</i> полуотложительные глаголы (<i>pf.</i> <i>которых имеет, при пассивной форме, активное значение</i>)."],
  ["neutrubi", "<big>neutrubi</big>\n\n<b>neutr–ubi</b> [neuter + ubi]\n<b>1)</b> ни в том, ни в другом месте, ни здесь, ни там (n. habere stabile stabulum <i>Pl</i>);\n<b>2)</b> ни в ту, ни в другую сторону, ни туда, ни сюда (n. proelio inclinato <i>Amm</i>)."],
  ["neve", "<big>neve</big>\n\n<b>neve (neu)</b> (= et ne)\nи не (hostibus obviam eamus n. perturbemur <i>L</i>); и чтобы не (hortor, ut maneas in sententia n. pertimescas vim <i>C</i>): n... n. ни... ни (n. vivus n. mortuus <i>C</i>)."],
  ["nevi", "<big>nevi</big>\n\n<b>nevi</b> <i>pf. к</i>\nneo."],
  ["nevis", "<big>nevis</big>\n\n<b>nevis, nevult (nevolt)</b> <i>арх.</i> <i>Pl</i>\n= non vis, non vult (<i>к</i> nolo)."],
  ["nevolus", "<big>nevolus</big>\n\n<b>nevolus, i</b> <i>m</i> <i>Fronto</i>\n= naevulus."],
  ["nex", "<big>nex</big>\n\n<b>nex, necis</b> <i>f</i> [neco]\n<b>1)</b> насильственная смерть, убийство (alicui necem offerre <i>C</i>, parare <i>L</i>; neci dedere, demittere <i>V</i>; vitae necisque potestas <i>C, Cs</i>);\n<b>2)</b> казнь (alicui diem necis destinare <i>C</i>);\n<b>3)</b> кровь убитых (manus imbutae nece Phrygia <i>O</i>);\n<b>4)</b> <i>редко</i> (естественная) смерть, кончина (post necem Mithridatis <i>Just</i>)."],
  ["nexi", "<big>nexi</big>\n\n<b>nexi</b> <i>pf. к</i>\nnecto."],
  ["nexibilis", "<big>nexibilis</big>\n\n<b>nexibilis, e</b> [necto]\nлегко связуемый <i>или</i> связный (asseveratio <i>Amm</i>)."],
  ["nexilis", "<big>nexilis</big>\n\n<b>nexilis, e</b> [necto]\nсвязанный вместе, завязанный (vestis <i>Lcr</i>; plagae <i>O</i>); переплетённый (hedera <i>O</i>)."],
  ["nexo", "<big>nexo</big>\n\n<b>nexo, —, —, are</b> (<i>и</i> <b>nexo, nexui, —, ere</b>) [<i>intens. к</i> necto]\nкрепко связывать, сплетать <i>LA</i>."],
  ["nexui", "<big>nexui</big>\n\n<b>nexui</b> <i>pf. к</i>\n<b>1)</b> necto <i>и</i>\n<b>2)</b> nexo."],
  ["nexum", "<big>nexum</big>\n\n<b>nexum, i</b> <i>n</i> [necto] <i>юр.</i>\n<b>1)</b> купчая (se nexu = nexo Obligare <i>C</i>);\n<b>2)</b> долговое обязательство под гарантию личной свободы (<i>обязательство должника в том, что в случае неуплаты долга он будет работать на заимодавца или станет его рабом</i>): n. inire <i>L</i> стать рабом заимодавца."],
  ["nexus 1", "<big>nexus</big>\n\n<b>I nexus, a, um</b> <i>part. pf. к</i>\nnecto."],
  ["nexus 2", "<big>nexus</big>\n\n<b>II nexus, us</b> <i>m</i> [necto]\n<b>1)</b> связь, сцепление (atomorum <i>C</i>; causarum <i>T</i>): versuum n. <i>Ap</i> стихотворная связанность (речи) || сплетение, переплетение, соединение (brachiorum <i>Su</i>); обвивание: serpens baculum nexibus ambit <i>O</i> змея обвивается вокруг палки;\n<b>2)</b> <i>pl.</i> путы, препоны (omnes nexus abscindere <i>St</i>);\n<b>3)</b> <i>C etc.</i> = nexum."],
  ["ni", "<big>ni</big>\n\n<b>ni</b> <i>conj.</i>\n<b>1)</b> <i>арх.</i> (= ne) не: quid ego ni ita censeam? <i>Pl</i> почему бы мне так не думать?;\n<b>2)</b> чтобы не: monent, ni teneant cursus <i>V</i> (указания Гелена) предостерегают, чтобы не держать путь (между Скиллой и Харибдой);\n<b>3)</b> если бы не: plures cecidissent, ni nox proelio intervenisset <i>L</i> многие пали бы, если бы ночь не прервала сражения;\n<b>4)</b> если не (peream, ni ita est <i>Vr</i>): ni fallor <i>Sen</i> если я не ошибаюсь; capiunt vitium, ni moveantur, aquae <i>O</i> вода загнивает, если не находится в движении; at ni id fit <i>H</i> но если это и не случится."],
  ["Nicaea", "<big>Nicaea</big>\n\n<b>Nicaea, ae</b> <i>f</i>\nНикея:\n<b>1)</b> <i>город в Вифинии, на вост. берегу Асканийского озера</i> <i>C, Ctl, PJ</i>;\n<b>2)</b> <i>укреплённый город эпикнемидских локров, к вост. от Фермопил</i> <i>L</i>;\n<b>3)</b> <i>город в Индии на Гидаспе, основанный Александром Македонским</i> <i>QC, Just</i>:\n<b>4)</b> <i>город в Лигурии</i> (<i>ныне</i> Ницца) <i>PM</i>."],
  ["Nicaeensis", "<big>Nicaeensis</big>\n\n<b>Nicaeensis, e</b> [Nicaea 1]\nникейский <i>Vr, PM, AG etc.</i>"],
  ["Nicaenus", "<big>Nicaenus</big>\n\n<b>Nicaenus, a, um</b> [Nicaea 1]\nникейский (synodus <i>Eccl</i>): Nicaena fides <i>CJ</i> никейский символ веры."],
  ["Nicaeus", "<big>Nicaeus</big>\n\n<b>Nicaeus, a, um</b> (<i>греч.</i> &laquo;дающий победу&raquo;)\n<i>эпитет Юпитера</i> <i>L</i>."],
  ["Nicander", "<big>Nicander</big>\n\n<b>Nicander, dri</b> <i>m</i>\nНикандр, <i>поэт, грамматик и врач, родом из Колофона</i> (<i>середина II века до н. э.</i>) <i>C, Macr</i>."],
  ["Nicanor", "<big>Nicanor</big>\n\n<b>Nicanor, oris</b> <i>m</i>\nНиканор:\n<b>1)</b> <i>сын Пармениона, полководец Александра Македонского, умер в 330 г. до н. э.</i> <i>Q, C</i>;\n<b>2)</b> <i>полководец Александра Македонского, родом из Стагиры, в 318 г. до н. э. казнённый Кассандром</i> <i>QC</i>."],
  ["nicator 1", "<big>nicator</big>\n\n<b>I nicator, oris</b> (<i>acc. pl.</i> oras) <i>m</i> (<i>греч.</i> &laquo;победитель&raquo;)\n<i>прозвище телохранителей македонского царя Персея</i> <i>L</i>."],
  ["Nicator 2", "<big>Nicator</big>\n\n<b>II Nicator, oris</b> <i>m</i>\n<i>прозвище царя Селевка I</i> <i>Nep</i>."],
  ["Nicea", "<big>Nicea</big>\n\n<b>Nicea, ae</b> <i>f</i> <i>PJ</i>\n= Nicaea."],
  ["Nicephorion", "<big>Nicephorion</big>\n\n<b>Nicephorion (-um), i</b> <i>n</i>\nНикефорий:\n<b>1)</b> <i>город в Месопотамии на Евфрате</i> <i>PM, T</i>;\n<b>2)</b> <i>роща близ Пергама с храмом Венеры</i> <i>L</i>."],
  ["Nicer", "<big>Nicer</big>\n\n<b>Nicer, cri</b> <i>m</i>\nНикер, <i>правый приток Рейна</i> (<i>ныне</i> Неккар) <i>Amm, Aus</i>."],
  ["Niceros", "<big>Niceros</big>\n\n<b>Niceros, otis</b> <i>m</i>\nНикерот, <i>имя торговца благовониями</i> <i>M</i>."],
  ["Nicerotianus", "<big>Nicerotianus</big>\n\n<b>Nicerotianus, a, um</b> [Niceros]\nникеротов <i>M, Sid</i>."],
  ["niceterium", "<big>niceterium</big>\n\n<b>niceterium, i</b> <i>n</i> (<i>греч.</i>)\nнаграда победителю <i>J</i>."],
  ["Nicia", "<big>Nicia</big>\n\n<b>Nicia</b> <i>и</i> <b>Nicias, ae</b> <i>m</i>\nНикий, <i>афинский политик и полководец во время первой половины Пелопоннесской войны; казнён в Сиракузах в 413 г. до н. э.</i> <i>Nep, Just</i>."],
  ["Nicolaus", "<big>Nicolaus</big>\n\n<b>Nicolaus, i</b> <i>m</i>\nНиколай, <i>родом из Дамаска, историк и философ, друг Августа</i> <i>PM</i>."],
  ["Nicomedensis 1", "<big>Nicomedensis</big>\n\n<b>I Nicomedensis, e</b> [Nicomedia]\nникомедийский <i>PJ</i>."],
  ["Nicomedensis 2", "<big>Nicomedensis</big>\n\n<b>II Nicomedensis, is</b> <i>m</i>\nжитель города Nicomedia <i>PJ, Dig</i>."],
  ["Nicomedes", "<big>Nicomedes</big>\n\n<b>Nicomedes, is</b> <i>m</i>\nНикомед, <i>имя трёх вифинских царей, из которых первый царствовал в 281-246 гг. до н. э., второй в 149-91 гг. до н. э., а третий в 31-74 гг. до н. э.</i> (<i>завещал своё царство Риму</i>) <i>C, Su</i>."],
  ["Nicomedia", "<big>Nicomedia</big>\n\n<b>Nicomedia, ae</b> <i>f</i>\nНикомедия, <i>столица Вифинии</i> (<i>в Астакенском заливе</i>) <i>AV, Amm</i>."],
  ["Nicon", "<big>Nicon</big>\n\n<b>Nico(n), onis</b> <i>m</i>\nНикон, <i>мужское имя</i> <i>C, L</i>."],
  ["Nicon", "<big>Nicon</big>\n\n<b>Nicon</b> <i>v. l.</i>\n= Nico."],
  ["Nicopolis", "<big>Nicopolis</big>\n\n<b>Nicopolis, is</b> <i>f</i> (<i>греч.</i> &laquo;град победы&raquo;)\nНикополь:\n<b>1)</b> <i>город у входа в Амбракийский валив, основанный Августом в память победы при Актии</i> <i>Su, T</i>;\n<b>2)</b> <i>город в Малой Армении, основанный Помпеем в память победы над Митридатом</i> <i>bAl</i>."],
  ["nictatio", "<big>nictatio</big>\n\n<b>nictatio, onis</b> <i>f</i> [nicto]\nморгание, мигание, подмигивание <i>PM, Sol</i>."],
  ["nicto", "<big>nicto</big>\n\n<b>nicto, —, are</b>\n<b>1)</b> моргать, подмигивать (alicui <i>Pl</i>);\n<b>2)</b> мигать, вспыхивать (nictantia fulgura flammae <i>Lcr</i>)."],
  ["nictor", "<big>nictor</big>\n\n<b>nictor, —, ari</b> <i>depon.</i> <i>PM</i>\n= nicto."],
  ["nictus", "<big>nictus</big>\n\n<b>nictus, us</b> <i>m</i> [nicto]\nморгание, подмигивание <i>Caec</i>."],
  ["nidamentum", "<big>nidamentum</big>\n\n<b>nidamentum, i</b> <i>n</i> [nidus]\n<b>1)</b> материалы для постройки гнезда <i>Pl</i>;\n<b>2)</b> гнездо <i>Eccl</i>."],
  ["nidicus", "<big>nidicus</big>\n\n<b>nidicus, a, um</b> [nidus]\nотносящийся к гнезду <i>Vr</i> (<i>v. l.</i>)."],
  ["nidificium", "<big>nidificium</big>\n\n<b>nidificium, i</b> <i>n</i> [nidifico]\nгнездо <i>Ap</i>."],
  ["nidifico", "<big>nidifico</big>\n\n<b>nidifico, —, —, are</b> [nidificus]\nвить гнездо (ex aliqua re <i>и</i> aliqua re <i>PM</i>); гнездиться (loculamenta, quibus nidificent aves <i>Col</i>)."],
  ["nidificus", "<big>nidificus</big>\n\n<b>nidificus, a, um</b> [nidus + facio]\nвьющий гнёзда: ver nidificum <i>SenT</i> весна, пора гнездования."],
  ["nidor", "<big>nidor</big>\n\n<b>nidor, oris</b> <i>m</i>\n<b>1)</b> дым, чад, гарь (n. culinae <i>M, J</i>; n. ex adusta pluma <i>L</i>);\n<b>2)</b> испарение <i>или</i> запах (cadaveri ni nidores <i>Aug</i>)."],
  ["nidorosus", "<big>nidorosus</big>\n\n<b>nidorosus, a, um</b> [nidor]\nиздающий запах жареного мяса, чадящий <i>Tert, CA</i>."],
  ["nidulor", "<big>nidulor</big>\n\n<b>nidulor, —, ari</b> <i>depon.</i> [nidulus]\n<b>1)</b> вить гнездо, гнездиться (in segetibus <i>AG</i>);\n<b>2)</b> помещать (словно) в гнезде, укрывать (fetus contra rigorem hiemis <i>PM</i>)."],
  ["nidulus", "<big>nidulus</big>\n\n<b>nidulus, i</b> <i>m</i> [<i>demin. к</i> nidus]\nгнёздышко (n. aviculae <i>AG</i>); <i>перен.</i> укромный уголок, место отдохновения (n. senectutis <i>PJ</i>)."],
  ["nidus", "<big>nidus</big>\n\n<b>nidus, i</b> <i>m</i>\n<b>1)</b> гнездо (nidum facere <i>O</i>, effingere, constituere <i>C</i>);\n<b>2)</b> <i>pl.</i> птенцы (nidi loquaces <i>V</i>); детёныши одного помёта (suam quisque matrem n. exspectat <i>Col</i>);\n<b>3)</b> жилище, дом: nidum celsae Acherontiae tenere <i>H</i> обитать на высотах Ахеронтии || прибежище (senectae <i>Aus</i>);\n<b>4)</b> помещение, ящик <i>или</i> шкаф для книг <i>M</i>;\n<b>5)</b> кубок, чаша (n. potilis <i>Vr</i>)."],
  ["nigella", "<big>nigella</big>\n\n<b>nigella, ae</b> <i>f</i> <i>бот.</i>\nчернушка, нигелла <i>Eccl</i>."],
  ["nigellus", "<big>nigellus</big>\n\n<b>nigellus, a, um</b> [<i>demin. к</i> niger]\nчерноватый, тёмный (amiculum <i>Vr</i>, vina <i>Pall</i>): nigellae Cadmi filiae <i>Aus</i> = litterae."],
  ["niger 1", "<big>niger</big>\n\n<b>I niger, nigra, nigrum</b>\nчёрный (color <i>Lcr, Col</i>; crinis <i>H</i>); тёмный (nox, nubes <i>O</i>; silva <i>H</i>); тёмно-зелёный (hederae <i>V</i>);\n<b>2)</b> густой (umbra <i>Lcr</i>);\n<b>3)</b> загорелый, смуглый (n. ore <i>M</i>);\n<b>4)</b> омрачающий, покрывающий тьмой, заволакивающий тучами (auster, imber <i>V</i>; ventus <i>H</i>);\n<b>5)</b> мрачный, зловещий, страшный (hic n. est, hunc tu caveto <i>H</i>): n. deus <i>O</i> = Pluto || смертный (hora <i>Tib</i>; dies <i>Prp</i>);\n<b>6)</b> скорбный, погружённый в печаль (domus <i>St</i>);\n<b>7)</b> злой, злобный (homo <i>C</i>);\n<b>8)</b> острый, едкий, язвительный: sal n. <i>H</i> колкая острота. — <i>См. тж.</i> nigrum."],
  ["Niger 2", "<big>Niger</big>\n\n<b>II Niger (Nigris), ris</b> <i>m</i>\nНигер, <i>река в Африке</i> <i>PM, Sol</i>."],
  ["Nigidius", "<big>Nigidius</big>\n\n<b>Nigidius, i</b> <i>m</i>\nНигидий, <i>римск. nomen; наиболее известен</i> P. N. Figulus, <i>друг Цицерона, сторонник Помпея, грамматик и философ</i> (<i>умер в 44 г. до н. э.</i>) <i>C, AG</i>."],
  ["nigrans", "<big>nigrans</big>\n\n<b>nigrans, antis</b>\n<b>1.</b> <i>part. praes. к</i> nigro;\n<b>2.</b> <i>adj.</i> чёрный, тёмный (nimbus <i>V</i>; rosae <i>PM</i>); наводящий тьму (aegis <i>V</i>)."],
  ["nigredo", "<big>nigredo</big>\n\n<b>nigredo, inis</b> <i>f</i> [niger]\nчернота, чёрный цвет (n. corvina <i>Ap</i>)."],
  ["nigreo", "<big>nigreo</big>\n\n<b>nigreo, —, —, ere</b> [niger]\nбыть чёрным, чернеть <i>Pac, Acc</i>."],
  ["nigresco", "<big>nigresco</big>\n\n<b>nigresco, grui, —, ere</b> [<i>inchoat. к</i> nigreo]\nчернеть, темнеть, становиться чёрным, приобретать тёмный цвет (nigrescunt sanguine venae <i>O</i>): tenebris nigrescunt omnia circum <i>V</i> всё кругом потемнело в глазах."],
  ["nigrico", "<big>nigrico</big>\n\n<b>nigrico, —, —, are</b> [niger]\nбыть черноватым, иметь тёмную окраску <i>PM</i>."],
  ["nigricolor", "<big>nigricolor</big>\n\n<b>nigri–color, oris</b> <i>adj.</i>\nчёрный, тёмного цвета <i>Sol</i>."],
  ["nigriculus", "<big>nigriculus</big>\n\n<b>nigriculus, a, um</b> [<i>demin. к</i> niger]\nчерноватый, темноватый <i>Vr</i>."],
  ["Nigrina", "<big>Nigrina</big>\n\n<b>Nigrina, ae</b> <i>f</i>\nНигрина, <i>женское имя</i> <i>M</i>."],
  ["Nigrinus", "<big>Nigrinus</big>\n\n<b>Nigrinus, i</b> <i>m</i>\nНигрии, <i>римск. cognomen</i> <i>Su</i>."],
  ["Nigris", "<big>Nigris</big>\n\n<b>Nigris</b> <i>v. l.</i>\n= Niger."],
  ["nigritia", "<big>nigritia</big>\n\n<b>nigritia, ae</b> <i>f</i> [niger]\nчернота, чёрный цвет <i>PM</i>."],
  ["nigrities", "<big>nigrities</big>\n\n<b>nigrities, ei</b> <i>f</i>\n<b>1)</b> <i>CC</i> = nigritia;\n<b>2)</b> <i>мед.</i> омертвение тканей, некроз <i>CC</i>."],
  ["nigritudo", "<big>nigritudo</big>\n\n<b>nigritudo, inis</b> <i>f</i> <i>PM, Aug</i>\n= nigritia."],
  ["nigro", "<big>nigro</big>\n\n<b>nigro, avi, atum, are</b> [niger]\n<b>1)</b> быть чёрным (nigrant nigro de semine nata <i>Lcr</i>): nigrans <i>Lcr, Vr, V</i> чёрный, тёмный;\n<b>2)</b> делать чёрным, придавать тёмный цвет (n. atros lacertos <i>St</i>);\n<b>3)</b> погружать во тьму (nigrati ignorantiae tenebris <i>Tert</i>). — <i>См. тж.</i> nigrans."],
  ["nigror", "<big>nigror</big>\n\n<b>nigror, oris</b> <i>m</i> <i>LM, Lcr, CC etc.</i>\n= nigritia."],
  ["nigrum", "<big>nigrum</big>\n\n<b>nigrum, i</b> <i>n</i> [niger]\n<b>1)</b> чернота, чёрный цвет: facere candida de nigris, <i>тж.</i> n. in candida vertere <i>погов. O, J</i> делать белое чёрным, а чёрное белым (<i>т. е.</i> извращать факты);\n<b>2)</b> чёрное пятно <i>O</i>."],
  ["nihil 1", "<big>nihil</big>\n\n<b>I nihil</b> (<i>стяж.</i> nil) <i>n indecl.</i> [ne + hilum]\nничто, ничего (n. agere <i>C</i>; n. loqui <i>C</i>): n. quidquam <i>C</i> <i>и</i> n. omnino <i>C</i> решительно ничего <i>или</i> вообще не; n. novi <i>C</i> ничего нового; n. esse <i>C etc.</i> ничего не значить, не иметь никакого значения: n. est mittere <i>Pl</i> незачем посылать; n. hominis esse <i>C</i> не быть человеком; n. ad hoc tempus <i>C</i> в данный момент это неважно; n. cunctatus <i>T</i> нисколько не колеблясь; non n. кое-что: non n. temporis <i>Nep</i> некоторое время; n. non <i>C, Nep etc.</i> всё: n. mali non <i>C</i> всё дурное; Athenienses Alcibiadem n. non efficere posse putabant <i>Nep</i> афиняне полагали, что Алкивиад может сделать всё; n. nisi <i>Ter, C</i> ничего (ничто), кроме; n. aliud quam (nisi, praeterquam) <i>C, L etc.</i> только, исключительно; n. minus (sane) <i>C</i> нисколько, ничуть, отнюдь нет; n. mihi est cum aliquo <i>Ter, O</i> мне нет никакого дела до кого-л.; n. habere <i>или</i> putare aliquem <i>C etc.</i> ни во что не ставить кого-л.; hoc n. ad me (attinet) <i>Ter, C</i> это меня совершенно не касается; hoc n. ad rem <i>C</i> это не имеет отношения к делу; n. est quod (cur, quamobrem) <i>C</i> нет причины (оснований), чтобы."],
  ["nihil 2", "<big>nihil</big>\n\n<b>II nihil</b> (<i>у</i> <i>O</i> <i>тж.</i> <b>hi</b>) <i>adv.</i>\n<b>1)</b> ни в каком отношении, никоим образом, никак, нисколько (aliquem n. metuere <i>C</i>; n. te impedio <i>C</i>): n. te interpellabo <i>C</i> я перебивать тебя не стану; поп п. <i>C</i> в известной мере;\n<b>2)</b> без всякой причины, зря <i>Ter, C, L</i>."],
  ["nihildum", "<big>nihildum</big>\n\n<b>nihil–dum</b> <i>conj.</i>\nещё (пока) ничего <i>C, Su</i>. nihilo <i>adv.</i> ничем не (n. majus <i>C</i>): n. secius <i>Cs, Vr</i> (minus <i>Pl, C, Nep etc.</i>) тем не менее."],
  ["nihilominus", "<big>nihilominus</big>\n\n<b>nihilo–minus</b> <i>adv.</i>\nтем не менее, всё же <i>Pl, C etc.</i>"],
  ["nihilum 1", "<big>nihilum</big>\n\n<b>I nihilum</b> (<i>стяж.</i> nilum), <b>i</b> <i>n</i> [nihil]\nничто: gigni de nihilo nihil, in n. nil posse reverti <i>Pers</i> из ничего ничто возникнуть не может, и ничто не может превратиться в ничто; ad n. redigere <i>Lcr</i> свести на нет, уничтожить; pro nihilo esse <i>C</i> ничего не стоить (не значить); (homo) nihili <i>Vr</i> негодный человек; de nihilo <i>Pl, L</i> без причины (основания)."],
  ["nihilum 2", "<big>nihilum</big>\n\n<b>II nihilum</b> <i>adv.</i>\nникоим образом, ни в каком отношении (n. metuenda timere <i>H</i>)."],
  ["nil", "<big>nil</big>\n\n<b>nil</b> <i>стяж.</i>\n= nihil."],
  ["Nileus", "<big>Nileus</big>\n\n<b>N&#299;le&#865;us, e&#299;</b> <i>и</i> <b>eos</b> <i>m</i>\n(&laquo;сын Нила&raquo;, &laquo;египтянин&raquo;) Нилей, <i>один из противников Персея</i> <i>O</i>."],
  ["Niliacus", "<big>Niliacus</big>\n\n<b>Niliacus, a, um</b>\nнильский: Niliaca fera <i>M</i> = crocodilus || <i>поэт.</i> египетский: Niliaca juvenca <i>M</i> = Isis <i>или</i> Io; Niliacum pecus <i>St</i> = Apis."],
  ["Niligena", "<big>Niligena</big>\n\n<b>Niligena, ae</b> <i>m</i>\nрождённый Нилом, <i>т. е.</i> египтянин <i>Macr</i>."],
  ["Nilotes", "<big>Nilotes</big>\n\n<b>Nilotes, ae</b> <i>m</i>\nжитель берегов Нила <i>Prp</i>."],
  ["Niloticus", "<big>Niloticus</big>\n\n<b>Niloticus, a, um</b> <i>Sen, M</i>\n= Niliacus."],
  ["Nilotis", "<big>Nilotis</big>\n\n<b>Nilotis, idis</b> <i>M, Lcn adj. f к</i>\nNiloticus."],
  ["nilum", "<big>nilum</big>\n\n<b>nilum</b> <i>Lcr стяж.</i>\n= nihilum I."],
  ["Nilus 1", "<big>Nilus</big>\n\n<b>I Nilus, i</b> <i>m</i>\nНил:\n<b>1)</b> <i>река в Египте</i> <i>Lcr, C etc.</i>;\n<b>2)</b> <i>бог этой реки</i> <i>C</i>."],
  ["nilus 2", "<big>nilus</big>\n\n<b>II nilus, i</b> <i>m</i>\nводопровод, канал, акведук <i>C</i>."],
  ["nimbatus", "<big>nimbatus</big>\n\n<b>nimbatus, a, um</b> [nimbus]\nокутанный туманом, <i>т. е.</i> легкомысленный, пустой <i>Pl</i>."],
  ["nimbifer", "<big>nimbifer</big>\n\n<b>nimbifer, fera, ferum</b> [nimbus + fero]\nнесущий буря, грозовой (ignis <i>O</i>).:"],
  ["nimbosus", "<big>nimbosus</big>\n\n<b>nimbosus, a, um</b> [nimbus]\nоблачный, дождевой, бурный (ventus <i>O</i>); покрытый облаками (cacumina montis <i>V</i>)."],
  ["nimbus", "<big>nimbus</big>\n\n<b>nimbus, i</b> <i>m</i> [<i>одного корня с</i> nubes облако]\n<b>1)</b> облако, туман (Venus obscuro circumdata nimbo <i>V</i>); дождевая туча (involvere diem nimbi <i>V</i>);\n<b>2)</b> ветер, буря, ураган (nimborum patria Aeolia <i>V</i>); ненастье, ливень, дождь (funduntur nimbi <i>O</i>; <i>перен.</i> n. lapidum et saxorum <i>Fl</i>); дождевая вода: nimbi ligati <i>Pt</i> лёд;\n<b>3)</b> внезапное бедствие, несчастье, гроза (hunc nimbum cito transisse laetor <i>C</i>);\n<b>4)</b> нимб, <i>сосуд с множеством отверстий для опрыскивания благовониями</i> <i>M</i>;\n<b>5)</b> множество, масса, туча (n. peditum <i>V</i>; n. arenae <i>V</i>);\n<b>6)</b> нимб, ореол <i>Eccl</i>."],
  ["nimie", "<big>nimie</big>\n\n<b>nimie</b> [nimius]\n<b>1)</b> слишком (non n., sed pulcre dictum <i>Macr</i>);\n<b>2)</b> весьма (in locis n. frigidis <i>Pall</i>)."],
  ["nimietas", "<big>nimietas</big>\n\n<b>nimietas, atis</b> <i>f</i>\nизбыток, чрезмерность (gaudii <i>Ap</i>)."],
  ["nimio", "<big>nimio</big>\n\n<b>nimio</b> <i>adv.</i> [nimius]\n<b>1)</b> (<i>тж.</i> n. opere <i>C</i>) очень, весьма, чрезвычайно (n. magnus <i>Pl</i>): plus n. <i>H</i> больше, чем следует; n. malle <i>Pl</i> безусловно предпочитать;\n<b>2)</b> много, гораздо (n. melius) <i>C</i>."],
  ["nimiopere", "<big>nimiopere</big>\n\n<b>nimiopere</b> (<i>или</i> <b>nimio opere</b>) [nimius + opus]\nвесьма, чрезвычайно <i>Pl</i>."],
  ["nimirum", "<big>nimirum</big>\n\n<b>nimirum</b> <i>adv.</i> [ni = ne + mirum]\nбесспорно, конечно, разумеется, без сомнения (non parva res, sed n. omnium maxima <i>C</i>)."],
  ["nimis", "<big>nimis</big>\n\n<b>nimis</b> <i>adv.</i>\n<b>1)</b> слишком, чрезмерно (n. saepe <i>C</i>; n. gaudere <i>Sl</i>): ne quid n. <i>Ter</i> (не допускать) никаких излишеств;\n<b>2)</b> (<i>тж.</i> n. quam <i>Pl</i>) весьма, чрезвычайно: n. velim <i>Pl, Ter</i> я очень желал бы; non n. <i>C</i> не особенно."],
  ["nimium 1", "<big>nimium</big>\n\n<b>I nimium</b> <i>adv.</i>\n<b>1)</b> слишком, чрезмерно (paulo n. <i>C</i>; n. diu <i>C</i>): n. nimiumque <i>O</i> чересчур уж, безмерно; n. ne crede colori <i>V</i> не возлагай особых надежд на цвет кожи, <i>впоследствии погов.</i> не слишком доверяйся внешнему виду;\n<b>2)</b> весьма, чрезвычайно: n. vellem <i>Ter</i> я очень желал бы."],
  ["nimium 2", "<big>nimium</big>\n\n<b>II nimium, i</b> <i>n</i>\nчрезмерность, избыток (n. lucri <i>C</i>; omnia nimia <i>C</i>)."],
  ["nimius", "<big>nimius</big>\n\n<b>nimius, a, um</b> [nimis]\nслишком большой <i>или</i> сильный, чрезмерный (calor <i>C</i>); безмерный, не знающий меры (in aliqua re <i>C</i>, aliqua re <i>Sl, H, T</i> <i>или</i> alicujus rei <i>L, Sen</i>; nimia libertas in nimiam servitutem cadit <i>C</i>): nimia opinio alicujus rei <i>Nep</i> переоценка чего-л. || необычайный (homo nimia pulchritudine <i>Pl</i>): n. rebus secundis <i>T</i> превозносящийся (зазнавшийся) в счастье; n. mero <i>H</i> неумеренный в потреблении вина; n. sermonis <i>T</i> слишком болтливый; nimio opere — <i>см.</i> nimiopere."],
  ["Ninev-", "<big>Ninev-</big>\n\n<b>Ninev-</b> <i>v. l.</i>\n= Niniv-."],
  ["ningit", "<big>ningit</big>\n\n<b>ningit (ninguit), ninxit, —, ere</b> <i>impers.</i>\nидёт снег <i>Col, V</i>; <i>тж.</i> <i>pass.</i> ninguitur- <i>см.</i> pluo; <i>перен.</i> сыплет: ningunt floribus rosarum <i>Lcr</i> обильно осыпают розами."],
  ["ningor", "<big>ningor</big>\n\n<b>ningor, oris</b> <i>m</i> [ningit]\nснегопад, снежная погода <i>Ap</i>."],
  ["ninguidus", "<big>ninguidus</big>\n\n<b>ninguidus, a, um</b> [ninguis]\n<b>1)</b> снежный, покрытый снегом (juga Pyrenaei <i>Aus</i>);\n<b>2)</b> падающий как (<i>или</i> похожий на) снег (cibus <i>Eccl</i> — <i>о манне небесной</i>)."],
  ["ninguis", "<big>ninguis</big>\n\n<b>ninguis, is</b> <i>f</i> <i>арх. Lcr, Ap</i>\n= nix."],
  ["ninguit", "<big>ninguit</big>\n\n<b>ninguit</b> <i>v. l.</i>\n= ningit."],
  ["ningulus", "<big>ningulus</big>\n\n<b>ningulus, a, um</b> [<i>из</i> ne + singulus] <i>Enn</i>\n= nullus."],
  ["Ninive", "<big>Ninive</big>\n\n<b>Ninive, es</b> <i>и</i> <b>Niniva, ae</b> <i>f</i> (= Ninus)\nНиневия, <i>столица Ассирии</i> <i>Vlg, Eccl</i>."],
  ["Ninivitae", "<big>Ninivitae</big>\n\n<b>Ninivitae, arum</b> <i>m</i>\nжители Нинезии <i>Vlg, Eccl</i>."],
  ["Niniviticus", "<big>Niniviticus</big>\n\n<b>Niniviticus, a, um</b>\nниневийский <i>Hier</i>."],
  ["Ninnius", "<big>Ninnius</big>\n\n<b>Ninnius, a, um</b>\nНинний, <i>римск. nomen; наиболее известен</i> L. N. Quadratus, <i>народный трибун в 58 г. до н. э., противник Клодия, друг Цицерона</i> <i>C</i>."],
  ["Ninus", "<big>Ninus</big>\n\n<b>Ninus (-os), i</b>\n<b>1)</b> <i>f</i> Ниневия, <i>древняя столица Ассирии на вост. берегу Евфрата, разрушенная в 606 г. до н. э. Киаксаром</i> <i>PM, T, Lcn</i>;\n<b>2)</b> <i>m</i> Нин, <i>муж Семирамиды, основатель Ассирийского царства</i> <i>Just, QC, O</i>."],
  ["Ninya", "<big>Ninya</big>\n\n<b>Ninya</b> <i>и</i> <b>Ninyas, ae</b> <i>m</i>\nНиний, <i>сын Нина и Семирамиды</i> <i>Just, Aug</i>."],
  ["Nioba", "<big>Nioba</big>\n\n<b>Nioba, ae</b> <i>и</i> <b>Niobe, es</b> <i>f</i>\nНиоба, <i>дочь Тантала, жена фиванского царя Амфиона, оскорбившая Латону, лишившаяся за это всех своих детей и превратившаяся в камень</i> <i>Prp, O, C, Sen etc.</i>"],
  ["Niobeus", "<big>Niobeus</big>\n\n<b>Niobeus, a, um</b> [Nioba]\nниобин (proles <i>H</i>)."],
  ["Niphates", "<big>Niphates</big>\n\n<b>Niphates, ae</b> <i>m</i>\nНифат:\n<b>1)</b> <i>река в Большой Армении</i> <i>Lcn, J</i>;\n<b>2)</b> <i>горная цепь в вост. Армении, к югу от Арарата</i> <i>Mela, V, H</i>."],
  ["Niphe", "<big>Niphe</big>\n\n<b>Niphe, es</b> <i>f</i>\nНифа, <i>нимфа, спутница Дианы</i> <i>O</i> (<i>v. l.</i>)."],
  ["Niptra", "<big>Niptra</big>\n\n<b>Niptra, ae</b> <i>f</i> (<i>греч.</i> &laquo;вода для омовения&raquo;, &laquo;очищение&raquo;)\n<i>название трагедий Софокла и Пакувия</i> <i>C, AG</i>."],
  ["Nireus", "<big>Nireus</big>\n\n<b>N&#299;re&#865;us, e&#299;</b> <i>и</i> <b>eos</b> <i>m</i>\nНирей, <i>сын Харопа и Аглаи, самый красивый (после Ахилла) из греков под стенами Трои</i> <i>Prp, H</i>."],
  ["Nisa", "<big>Nisa</big>\n\n<b>Nisa, ae</b> <i>f</i>\nНиса, <i>женское имя</i> <i>V</i>."],
  ["Nisaeus", "<big>Nisaeus</big>\n\n<b>Nisaeus, a, um</b> <i>O</i>\n= Niseius."],
  ["nisei", "<big>nisei</big>\n\n<b>nisei</b> <i>арх.</i> <i>Pl</i>\n= nisi."],
  ["Niseis", "<big>Niseis</big>\n\n<b>Niseis, idis</b> <i>f</i>\nдочь Ниса, <i>т. е.</i> Scylla <i>O</i>."],
  ["Niseius", "<big>Niseius</big>\n\n<b>Niseius, a, um</b> [Nisus IV]\nнисов: Niseia virgo <i>O</i> — Scylla."],
  ["nisi", "<big>nisi</big>\n\n<b>nisi</b> <i>conj.</i> [ni + si]\n<b>1)</b> если не (memoria minuitur, n. eam exerceas <i>C</i>);\n<b>2)</b> кроме, разве лишь (quis istud credat, n. stultus? <i>C</i>): non... n. (n... non) только, лишь (n. victores in castra non revertemur <i>Cs</i>): n. quid necesse erit <i>C</i> разве уж в случае (крайней) необходимости; n. paret, imperat <i>H</i> если (чувство) не подчиняется, то оно повелевает; n. si (ut, quod, forte) <i>Pl, C etc.</i> разве только, за исключением, пожалуй; nihil aliud n. <i>C</i> не что иное, как; n. hoc mirum est <i>C</i> разве что считать это чудом, <i>т. е.</i> что же удивительного в том, что...?; n. vero si qui est, qui <i>C</i> кроме, пожалуй, тех, кто."],
  ["Nisias", "<big>Nisias</big>\n\n<b>Nisias, adis</b> <i>f</i> [Nisus IV] <i>поэт.</i>\nмегарянка <i>O</i>."],
  ["Nisibis", "<big>Nisibis</big>\n\n<b>Nisibis</b> (<i>acc.</i> bin, <i>abl.</i> bi) <i>f</i>\nНисибис, <i>главн. город области Мигдонии</i> (<i>Месопотамия</i>) <i>PM, T, Amm</i>."],
  ["nisus 1", "<big>nisus</big>\n\n<b>I nisus (nixus), a, um</b> <i>part. pf. к</i>\nnitor II."],
  ["nisus 2", "<big>nisus</big>\n\n<b>II nisus, us</b> <i>m</i> [nitor II]\n<b>1)</b> восхождение, влезание, подъём (n. per saxa <i>St</i>): adsummum pervenire non nisu, sed impetu <i>Q</i> добраться до вершины не (медленным) восхождением, а с разбега;\n<b>2)</b> полёт, взлёт (pinnarum <i>Lcr</i>);\n<b>3)</b> усилие, напряжение (n. vomentis <i>T</i>): majore nisu clamare <i>Pt</i> воскликнуть ещё громче || размах (tela nisu vibrare <i>QC</i>);\n<b>4)</b> <i>тж. pl.</i> потуги, родовые схватки (nisus laboriosi <i>AG</i>);\n<b>5)</b> упор, сила (stat nisu immotus eodem <i>V</i>);\n<b>6)</b> стремительное движение, тяготение (<i>sc.</i> astrorum <i>C</i>)."],
  ["nisus 3", "<big>nisus</big>\n\n<b>III nisus, i</b> <i>m</i>\nястреб-перепелятник <i>O</i>."],
  ["Nisus 4", "<big>Nisus</big>\n\n<b>IV Nisus, i</b> <i>m</i>\nНис:\n<b>1)</b> <i>сын Пандиона, царь Мегары, отец Скиллы; из любви к Миносу последняя отрезала у отца пурпурный волос, от которого зависела судьба царства; он и дочь были превращены в хищных птиц</i> <i>O, V</i>;\n<b>2)</b> <i>троянец, сын Гиртака, один из спутников Энея</i> <i>V</i>."],
  ["nitedula", "<big>nitedula</big>\n\n<b>nitedula, ae</b> <i>f</i> <i>предпол.</i>\nполевая мышь <i>C</i>."],
  ["nitefacio", "<big>nitefacio</big>\n\n<b>nitefacio, (feci), factum, ere</b> [niteo + facio]\nделать блестящим, сверкающим (ventus mare nitefacit <i>AG</i>)."],
  ["nitela 1", "<big>nitela</big>\n\n<b>I nitela, ae</b> <i>f</i> [niteo]\n<b>1)</b> блеск, блистание, сверкание <i>Sol</i>;\n<b>2)</b> блёстка (nitelae pulveris <i>Sol</i>);\n<b>3)</b> средство для наведения блеска: nitelae oris <i>Ap</i> зубной порошок."],
  ["nitela 2", "<big>nitela</big>\n\n<b>II nitela, ae</b> <i>f</i>\n<b>1)</b> <i>PM, M</i> = nitedula;\n<b>2)</b> <i>предпол.</i> белка <i>Sol</i>."],
  ["nitelinus", "<big>nitelinus</big>\n\n<b>nitelinus, a, um</b> [nitela II 1]\nцвета полевой мыши <i>PM</i>."],
  ["nitell-", "<big>nitell-</big>\n\n<b>nitell-</b> <i>v. l.</i>\n= nitel-."],
  ["nitens 1", "<big>nitens</big>\n\n<b>I nitens, entis</b> <i>part. praes. к</i>\nnitor II."],
  ["nitens 2", "<big>nitens</big>\n\n<b>II nitens, entis</b>\n<b>1.</b> <i>part. praes. к</i> niteo;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> блестящий, сверкающий (arma nitentia <i>L</i>);\n2)лоснящийся, тучный (taurus <i>V</i>);\n<b>3)</b> красивый, изящный (femina <i>O</i>); пышный (campi <i>O</i>; herba <i>V</i>);\n<b>4)</b> славный, блистательный (oratio <i>C</i>)."],
  ["niteo", "<big>niteo</big>\n\n<b>niteo, ui, —, ere</b>\n<b>1)</b> быть жирным, лосниться от жира (n. unguentis <i>C</i>); быть тучным, откормленным (oves nitent <i>Pl</i>);\n<b>2)</b> блестеть, сверкать (ebur nitet <i>Tib</i>; n. auro <i>St</i>; nitentes Cyclades <i>H</i>);\n<b>3)</b> блистать красотой, быть красивым (n. ore <i>M</i>); быть пышным, быть в цвету (tellus nitet <i>Pt</i>; arbores nitent <i>PM</i>);\n<b>4)</b> изобиловать (vectigal in pace nitet <i>C</i>);\n<b>5)</b> отличаться, быть выдающимся, славиться (res gestae alicujus nitent <i>C</i>). — <i>См. тж.</i> nitens II."],
  ["nitesco", "<big>nitesco</big>\n\n<b>nitesco, nitui, —, ere</b> [<i>inchoat. к</i> niteo]\n<b>1)</b> становиться жирным, жиреть, тучнеть (armenta nitescunt <i>PJ</i>);\n<b>2)</b> становиться блестящим, сверкающим, ясным (caelum nitescit <i>Poeta ap. C</i>);\n<b>3)</b> становиться славным, достигать успеха (ingenio <i>rhH</i>);\n<b>4)</b> расцветать (arbores nitescunt <i>PM</i>)."],
  ["nitibundus", "<big>nitibundus</big>\n\n<b>nitibundus, a, um</b> [nitor II]\n<b>1)</b> давящий, увесистый (pondus <i>Sol</i>);\n<b>2)</b> опирающийся: Achaei mentium animorumque conspiratu tacito nitibundi <i>AG</i> ахеяне, находившие опору (черпавшие силу) в безмолвном единстве мыслей и чувств."],
  ["nitide", "<big>nitide</big>\n\n<b>nitide</b> [nitidus]\nблестяще, блистательно, пышно (cenare <i>Pl</i>; accipere aliquem <i>Pl</i>)."],
  ["nitiditas", "<big>nitiditas</big>\n\n<b>nitiditas, atis</b> <i>f</i> [nitidus]\nблеск, блистание, красота (figurae <i>Acc</i>)."],
  ["nitidiuscule", "<big>nitidiuscule</big>\n\n<b>nitidiuscule</b> [nitidiusculus]\nдовольно пышно, блестяще (curari <i>Pl</i>)."],
  ["nitidiusculus", "<big>nitidiusculus</big>\n\n<b>nitidiusculus, a, um</b> [<i>demin. к</i> nitidus]\nнемного лоснящийся, блестящий (caput <i>Pl</i>)."],
  ["nitido", "<big>nitido</big>\n\n<b>nitido, —, —, are</b> [nitidus]\n<b>1)</b> чистить до блеска (ferramenta <i>Col</i>);\n<b>2)</b> мыть, купать (corpora <i>Enn</i>)."],
  ["nitidus", "<big>nitidus</big>\n\n<b>nitidus, a, um</b> [niteo]\n<b>1)</b> блестящий, блистающий, лоснящийся (capilli <i>H</i>; ebur <i>O</i>);\n<b>2)</b> чистый (vox <i>Q</i>); светлый, ясный (dies <i>O</i>);\n<b>3)</b> красивый, нарядный (villa <i>H</i>; femina <i>Pl</i>);\n<b>4)</b> изысканный, тонкий, учтивый, изящный (verba <i>C, Q</i>; homo <i>H</i>);\n<b>5)</b> цветущий, пышный (campi <i>C</i>; membra, <i>sc.</i> Apollinis <i>Ap</i>); тучный, откормленный (vacca <i>O</i>; jumenta <i>Nep</i>)."],
  ["Nitiobroges", "<big>Nitiobroges</big>\n\n<b>Nitiobroges, um</b> <i>m</i>\nнитиоброги, <i>кельт. племя в Аквитании, на берегах реки Гарумны с главн. городом Aginum</i> (<i>ныне</i> Agen) <i>Cs</i>."],
  ["nitor 1", "<big>nitor</big>\n\n<b>I nitor, oris</b> <i>m</i> [niteo]\n<b>1)</b> блеск, сверкание, лоск (argenti <i>O</i>; eboris <i>Lact</i>); сияние (aurorae <i>Lcr</i>): n. diurnus <i>O</i> дневной свет; n. generis <i>O</i> знатность рода, родовитость;\n<b>2)</b> красота (<i>sc.</i> virginis <i>H</i>);\n<b>3)</b> изысканность, изящество (orationis <i>C</i>)."],
  ["nitor 2", "<big>nitor</big>\n\n<b>II nitor, nixus (nisus) sum, niti</b> <i>depon.</i>\n<b>1)</b> опираться, упираться (aliqua re, in aliqua re <i>или</i> in aliquid <i>Pl, C, V etc.</i>): n. genibus <i>L</i> стоять на коленях; n. cubito <i>O</i> облокотиться; n. alis <i>V</i> парить на крыльях;\n<b>2)</b> опираться, основываться, покоиться, зиждиться, зависеть (salus civitatis nititur in civibus <i>C</i>): sua vi nixus <i>T</i> опираясь на собственную силу || полагаться (in consilio alicujus <i>C</i>);\n<b>3)</b> напрягаться, тужиться <i>Su</i>: n. corpore <i>Sl</i> делать резкие телодвижения; делать потуги, рожать <i>O, PM</i> (<i>v. l.</i>);\n<b>4)</b> с усилием подниматься, выпрямляться, вставать: niti modo ac, statim concidere (<i>inf.</i> <i>historicus</i>) <i>Sl</i> (нумидийцы) с трудом поднимаются, но тотчас же падают;\n<b>5)</b> взбираться, влезать (rupes in altas <i>Lcn</i>); восходить, подниматься (gradibus <i>V</i>); взлетать (pennis in aera <i>O</i>; per aspera ad laudem <i>Sil</i>);\n<b>6)</b> устремляться (ad sidera <i>V</i>; ad salutem <i>Ap</i>); стремиться (ad immortalitatem <i>C</i>): nitimur in vetitum semper <i>O</i> нас всегда тянет к запретному;\n<b>7)</b> силиться, стараться изо всех сил, прилагать все усилия (pro aliquo <i>L</i>; aliqua re magna vi n. <i>Cs</i>);\n<b>8)</b> бороться (n. contra aliquem <i>T</i>; pro Ubertate summa ope <i>Sl</i>);\n<b>9)</b> пытаться доказать: n. nihil posse percipi <i>C</i> пытаться показать, что познание невозможно."],
  ["nitraria", "<big>nitraria</big>\n\n<b>nitraria, ae</b> <i>f</i> [nitrum]\nместорождение соды <i>PM</i>."],
  ["nitratus", "<big>nitratus</big>\n\n<b>nitratus, a, um</b> [nitrum]\nщелочной, смешанный с содой (aqua <i>Col, M</i>)."],
  ["nitreus", "<big>nitreus</big>\n\n<b>nitreus, a, um</b>\nсодовый (pulvis <i>CA</i>)."],
  ["nitrosus", "<big>nitrosus</big>\n\n<b>nitrosus, a, um</b> [nitrum]\nизобилующий щёлочью, с примесью соды (fontes <i>PM</i>)."],
  ["nitrum", "<big>nitrum</big>\n\n<b>nitrum, i</b> <i>n</i>\nсамородная щелочная соль, натр, сода: flos nitri <i>Vtr</i> углекислая сода."],
  ["nivalis", "<big>nivalis</big>\n\n<b>nivalis, e</b> [nix]\n<b>1)</b> снежный (dies <i>L</i>; ventus <i>PM</i>);\n<b>2)</b> оснеженный, снеговой, покрытый снегом (mons <i>V</i>; juga <i>Sen</i>);\n<b>3)</b> холодный как снег, ледяной (osculum <i>M</i>): nivali compede vinctus Hebrus <i>H</i> окованный льдом Гебр (<i>река</i>);\n<b>4)</b> белоснежный, белый как снег (equi candore nivali <i>V</i>)."],
  ["nivarius", "<big>nivarius</big>\n\n<b>nivarius, a, um</b> [nix]\nснежный, снеговой: colum nivarium <i>M</i> снежный фильтр (<i>для вина</i>)."],
  ["nivatus", "<big>nivatus</big>\n\n<b>nivatus, a, um</b> [nix]\nохлаждённый снегом (potiones <i>Sen</i>; aqua <i>Pt</i>)."],
  ["nive 1", "<big>nive</big>\n\n<b>I nive</b> <i>conj.</i> [ni + ve] <i>Pl, C</i>\nили (и) если не."],
  ["nive 2", "<big>nive</big>\n\n<b>II nive</b> <i>арх. Lcr</i>\n(= neve) или (и) не."],
  ["nive 3", "<big>nive</big>\n\n<b>III nive</b> <i>abl. sg. к</i>\nnix."],
  ["niveo", "<big>niveo</big>\n\n<b>niveo, —, —, ere</b> [nix]\nбыть белым как снег <i>Eccl</i>."],
  ["nivesco", "<big>nivesco</big>\n\n<b>nivesco, —, —, ere</b> [<i>inchoat. к</i> niveo]\nстановиться белым как снег <i>Tert</i>."],
  ["niveus", "<big>niveus</big>\n\n<b>niveus, a, um</b> [nix]\n<b>1)</b> снежный, снеговой, из снега (aqua <i>M</i>; agger <i>V</i>); покрытый снегом, в снегу (mons <i>Ctl</i>);\n<b>2)</b> белоснежный, белый как снег (lac <i>V</i>; color <i>H</i>);\n<b>3)</b> одетый в белое (Quirites <i>J</i>);\n<b>4)</b> светлый, яркий, ослепительно белый (flumen <i>SenT</i>)."],
  ["nivifer", "<big>nivifer</big>\n\n<b>nivi–fer, fera, ferum</b> [nix + fero]\nпокрытый снегом (valles <i>Eccl</i>)."],
  ["nivis", "<big>nivis</big>\n\n<b>nivis</b> <i>gen. sg. к</i>\nnix."],
  ["nivit", "<big>nivit</big>\n\n<b>nivit, —, —, ere</b> <i>Pac</i>\n= ningit."],
  ["nivosus", "<big>nivosus</big>\n\n<b>nivosus, a, um</b> [nix]\nснежный, обильный снегом (hiems <i>L</i>); занесённый снегом, весь в снегу (Scythia <i>O</i>); предвещающий снег (sidus <i>St</i>)."],
  ["nix", "<big>nix</big>\n\n<b>nix, nivis</b> <i>f</i> (<i>gen. pl.</i> ium)\n<b>1)</b> (<i>тж.</i> <i>pl.</i>) снег: nivis casus <i>L</i> снегопад;\n<b>2)</b> <i>pl.</i> холодный климат <i>Prp</i>;\n<b>3)</b> белизна (eboris <i>Ap</i>);\n<b>4)</b> седина (nives capitis <i>H</i>)."],
  ["Nixi dii", "<big>Nixi dii</big>\n\n<b>Nixi dii</b> [nitor II, 3]\n<i>три божества деторождения</i> (<i>коленопреклонённые статуи которых хранились в Капитолии</i>) <i>O</i>."],
  ["nixor", "<big>nixor</big>\n\n<b>nixor, —, ari</b> <i>depon.</i> [<i>intens. к</i> nitor II]\n<b>1)</b> опираться (n. insistereque alis <i>Lcr</i>; serpens nixans nodis <i>V</i>);\n<b>2)</b> упираться, сопротивляться (adverso monte nixantem trudere saxum <i>Lcr</i>);\n<b>3)</b> покоиться, зиждиться (fundamenta, quibus nixatur vita salusque <i>Lcr</i>)."],
  ["nixus 1", "<big>nixus</big>\n\n<b>I nixus</b> (<i>или</i> <b>nisus</b>) <i>part. pf. к</i>\nnitor II."],
  ["nixus 2", "<big>nixus</big>\n\n<b>II nixus, us</b> <i>m</i> <i>V, O, AG</i>\n= nisus II."],
  ["no", "<big>no</big>\n\n<b>no, navi, —, nare</b>\n<b>1)</b> плавать (n. per undas <i>O</i>; n. discere <i>Pl</i>): n. sine cortice <i>погов. H</i> плавать без пробкового пояса, <i>т. е.</i> не нуждаться в опеке; nando <i>T</i> вплавь;\n<b>2)</b> <i>поэт.</i> плыть (classi <i>Ctl</i>);\n<b>3)</b> течь (undae nantes <i>Ctl</i>);\n<b>4)</b> летать, носиться, парить (apes nant <i>V</i>);\n<b>5)</b> быть осовелым <i>или</i> блуждающим (nant oculi <i>Lcr</i>)."],
  ["nobilis", "<big>nobilis</big>\n\n<b>nobilis, e</b> [nosco]\n<b>1)</b> известный (alicui <i>Pl</i>);\n<b>2)</b> заметный, видимый (gaudium <i>T</i>);\n<b>3)</b> славный, прославленный, знаменитый (n. et clarus <i>C</i>): n. (in, ex) aliqua re <i>C etc.</i> знаменитый чём-л.; n. disco <i>H</i> прославившийся в метании диска; n. in tibicinio <i>Ap</i> знаменитый флейтист; palma n. <i>H</i> почётная пальма, <i>т. е.</i> награда, приз;\n<b>4)</b> (<i>тж.</i> n. genere <i>Ap</i>) благородный, высокородный, родовитый, знатный, аристократический (nobili genere natus <i>C</i>);\n<b>5)</b> превосходный, отличный (fundus <i>C</i>; equus <i>QC, Sen</i>);\n<b>6)</b> пресловутый, покрытый дурной славой (Caudina pax <i>L</i>)."],
  ["nobilitas", "<big>nobilitas</big>\n\n<b>nobilitas, atis</b> <i>f</i> [nobilis]\n<b>1)</b> известность, слава (summorum virorum <i>Cs</i>);\n<b>2)</b> знатность, родовитость (vir summa nobilitate <i>C</i>);\n<b>3)</b> знать, аристократы (n. senatorum <i>Ap</i>): n. equestris <i>T</i> всадническая знать;\n<b>4)</b> благородство, достоинство (mentis <i>Fronto</i>; n. ingenita <i>T</i>);\n<b>5)</b> высокие качества, превосходные свойства (discipulorum <i>C</i>; rosae <i>PM</i>);\n<b>6)</b> представитель знати (nobilitates externae <i>T</i>)."],
  ["nobiliter", "<big>nobiliter</big>\n\n<b>nobiliter</b> [nobilis]\nотлично, превосходно, мастерски (caelare argentum <i>PM</i>)."],
  ["nobilito", "<big>nobilito</big>\n\n<b>nobilito, avi, atum, are</b> [nobilis]\n<b>1)</b> делать известным, распространять (famam <i>L</i>);\n<b>2)</b> прославлять, делать знаменитым (aliquem <i>C etc.</i>; post mortem nobilitari <i>C</i>);\n<b>3)</b> улучшать, облагораживать (vites <i>Pall</i>);\n<b>4)</b> ославить, опозорить (aliquem flagitiis <i>Ter</i>)."],
  ["nocens", "<big>nocens</big>\n\n<b>nocens, entis</b>\n<b>1.</b> <i>part. praes. к</i> noceo;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> вредный (pestifer et n. <i>C</i>);\n<b>2)</b> преступный (homo nocentissimus <i>C</i>);\n<b>3.</b> <i>subst. m</i> преступник, злодей <i>C, Sen etc.</i>: n. precatur, innocens irascitur <i>погов. PS</i> (выслушав обвинение), виновный просит (о снисхождении), а невиновный возмущается."],
  ["nocenter", "<big>nocenter</big>\n\n<b>nocenter</b> [nocens]\nвредно, во вред <i>Col, CC, Tert</i>."],
  ["noceo", "<big>noceo</big>\n\n<b>noceo, cui, citum, ere</b>\n<b>1)</b> вредить, причинять вред, наносить ущерб (alicui <i>Pl, Cs etc.</i>; aliquem <i>Veg, Vlg</i>): mihi nihil ab istis noceri potest <i>L</i> они (мои враги) не могут причинить мне никакого вреда;\n<b>2)</b> мешать, препятствовать: quid temptare nocebit? <i>O</i> чему помешает попытка?;\n<b>3)</b> повреждать, портить (larix a tinea non nocetur <i>Vtr</i>)."],
  ["nocibilis", "<big>nocibilis</big>\n\n<b>nocibilis, e</b> <i>Eccl</i>\n= nocivus."],
  ["nocivus", "<big>nocivus</big>\n\n<b>nocivus, a, um</b> [noceo]\nвредный, вредоносный (alicui <i>PM</i>); опасный, серьёзный (periculum <i>Ph</i>)."],
  ["nocte", "<big>nocte</big>\n\n<b>nocte</b> <i>adv.</i> <i>C, Nep etc.</i>\n= noctu 2."],
  ["noctesco", "<big>noctesco</big>\n\n<b>noctesco, —, —, ere</b> [nox I]\nвечереть, смеркаться, темнеть <i>AG</i>."],
  ["nocticolor", "<big>nocticolor</big>\n\n<b>nocti–color, oris</b> <i>adj.</i>\nтёмный как ночь, чёрный (Memnon <i>AG</i>; Styx <i>Aus</i>)."],
  ["Noctifer", "<big>Noctifer</big>\n\n<b>Noctifer, feri</b> <i>m</i>\nвечерняя звезда <i>Ctl, Calp</i>."],
  ["noctiluca", "<big>noctiluca</big>\n\n<b>noctiluca, ae</b> <i>f</i> [nox + luceo]\n<b>1)</b> ночное светило, <i>т. е.</i> луна <i>Vr, H</i>;\n<b>2)</b> фонарь <i>Vr</i>."],
  ["noctivagus", "<big>noctivagus</big>\n\n<b>nocti–vagus, a, um</b>\nбродящий по ночам (faces caeli <i>Lcr</i>; strepitus <i>Lcr</i>; currus, <i>sc.</i> Phoebes <i>V</i>)."],
  ["noctu", "<big>noctu</big>\n\n<b>noctu</b>\n<b>1.</b> <i>арх. abl. к</i> nox (hac noctu <i>Pl</i>; in sereno (так!) noctu <i>Cato</i>);\n<b>2.</b> <i>adv.</i> ночью (n. an interdiu <i>L etc.</i>)."],
  ["noctua", "<big>noctua</big>\n\n<b>noctua, ae</b> <i>f</i> [nox I]\nсова, сыч <i>Pl, V etc.</i>"],
  ["noctuabundus", "<big>noctuabundus</big>\n\n<b>noctuabundus, a, um</b> [nox I]\nпутешествующий (странствующий, идущий) поздно ночью <i>C</i>."],
  ["noctuinus", "<big>noctuinus</big>\n\n<b>noctuinus, a, um</b> [noctua]\nсовиный (oculi <i>Pl</i>)."],
  ["noctulucus", "<big>noctulucus</big>\n\n<b>noctulucus, i</b> <i>m</i> [nox + lux]\nбодрствующий ночью (<i>о собаке</i>) <i>Vr</i>."],
  ["nocturna", "<big>nocturna</big>\n\n<b>nocturna, ae</b> <i>f</i>\nноктурна, ночная ведьма <i>Pt</i>."],
  ["nocturnalis", "<big>nocturnalis</big>\n\n<b>nocturnalis, e</b>\nночной (cucullus <i>Sid</i>)."],
  ["nocturnus 1", "<big>nocturnus</big>\n\n<b>I nocturnus, a, um</b> [nox I]\nночной (hora, fur <i>C</i>; equi, <i>sc.</i> lunae <i>O</i>; cantus, <i>sc.</i> bubonum <i>Ap</i>): tempore nocturno <i>Lcr, Q</i> в ночное время, ночью; n. obambulat <i>V</i> — <i>см.</i> obambulo; manu nocturna versare (<i>sc.</i> librum) <i>H</i> изучать по ночам книгу; ora nocturna <i>Pl</i> безобразные лица."],
  ["nocturnus 2", "<big>nocturnus</big>\n\n<b>II nocturnus, i</b> <i>m</i> (<i>sc.</i> fur)\nночной вор <i>Pt</i>."],
  ["Nocturnus 3", "<big>Nocturnus</big>\n\n<b>III Nocturnus, i</b> <i>m</i>\nНоктурн, <i>бог ночи</i> <i>Pl</i>."],
  ["noctuvigilus", "<big>noctuvigilus</big>\n\n<b>noctu–vigilus, a, um</b>\nбодрствующий по ночам (Venus <i>Pl</i>)."],
  ["nocumentum", "<big>nocumentum</big>\n\n<b>nocumentum, i</b> <i>n</i> [noceo]\nвред <i>CA</i>."],
  ["nocuus", "<big>nocuus</big>\n\n<b>nocuus, a, um</b> [noceo]\nвредный <i>O, Scr</i>."],
  ["nodatio", "<big>nodatio</big>\n\n<b>nodatio, onis</b> <i>f</i> [nodo]\nузловатость, шишковатость (nodationis durities <i>Vtr</i>)."],
  ["nodatus", "<big>nodatus</big>\n\n<b>nodatus, a, um</b> [nodus]\nузловатый, суковатый (cornus <i>PM</i>)."],
  ["nodo", "<big>nodo</big>\n\n<b>nodo, (avi), atum, are</b> [nodus]\nзавязывать, скручивать узлом (crines in aurum <i>V</i>); связывать, перевязывать (collum laqueo <i>O</i>)."],
  ["nodose", "<big>nodose</big>\n\n<b>nodose</b>\nзапутанно, туманно (loqui <i>Tert</i>)."],
  ["nodositas", "<big>nodositas</big>\n\n<b>nodositas, atis</b> <i>f</i> [nodosus]\nузловатость, <i>перен.</i> запутанность (tortuosissima n. <i>Aug</i>)."],
  ["nodosus", "<big>nodosus</big>\n\n<b>nodosus, a, um</b> [nodus]\n<b>1)</b> узловатый, полный узлов (lina, plagae <i>O</i>); шишковатый (stipes <i>O, QC</i>); суковатый (rami <i>Sen</i>);\n<b>2)</b> вызывающий желваки (chiragra <i>H</i>);\n<b>3)</b> запутанный, сложный (quaestio <i>Aug, Macr</i>); обусловленный многими оговорками (stips <i>VM</i>); крючкотворный (<i>sc.</i> fenerator <i>H</i>)."],
  ["nodulus", "<big>nodulus</big>\n\n<b>nodulus, i</b> <i>m</i> [<i>demin. к</i> nodus]\nузелок <i>PM, Ap</i>."],
  ["nodus", "<big>nodus</big>\n\n<b>nodus, i</b> <i>m</i>\n<b>1)</b> узел (nodum conectere <i>C</i>, solvere <i>H</i>): n. Herculis <i>погов. PM</i> (Herculaneus <i>Sen</i>) геркулесов узел, <i>тж.</i> n. Gordius <i>Amm</i> Гордиев узел, <i>т. е.</i> неразрешимый вопрос; nodum in scirpo quaerere <i>погов. Pl, Ter</i> искать узлов в тростнике, <i>т. е.</i> трудностей там, где их нет;\n<b>2)</b> петля (cervices nodo condere <i>Pt</i>);\n<b>3)</b> объятие (aliquem in nodum complecti <i>V</i>);\n<b>4)</b> пояс (sinum nodo colligere <i>V, QC</i>): n. anni <i>Lcr</i> годовой узел, <i>т. е.</i> место пересечения эклиптики с экватором; nodi <i>Man</i> небесные узлы, <i>т. е.</i> четыре точки небосвода, отмечающие начала четырёх времён года;\n<b>5)</b> волосяной валик (<i>для женской причёски</i>): crinem nodo substringere <i>T</i> закручивать волосы валиком;\n<b>6)</b> узловатый сустав (crura sine nodis <i>Cs</i>);\n<b>7)</b> шишка, бугорок, узловатость (baculum sine nodo <i>L</i>); затвердение, желвак (articulorum nodi <i>PM</i>);\n<b>8)</b> <i>поэт.</i> суковатая палица (Herculis <i>SenT</i>);\n<b>9)</b> клуб(ок), извив (n. viperinus <i>H</i>);\n<b>10)</b> связь (continuationis <i>C</i>); узы (amicitiae <i>C</i>); оковы (leti <i>St</i>); <i>pl.</i> путы (nodi religionum <i>Lcr</i>); <i>pl.</i> рыболовная сеть (luctantur corpora- <i>sc.</i> piscium — nodis <i>Man</i>); скованность: nodum linguae rumpere <i>AG</i> <i>и</i> nodos linguae solvere <i>Just</i> нарушить молчание;\n<b>11)</b> обязанность, обязательство, клятва (nodos imponere <i>O</i>);\n<b>12)</b> трудность, осложнение, препятствие, помеха (in difficilem nodum incidere <i>C</i>): nodum alicujus rei expedire <i>C</i> (exsolvere <i>L</i>) устранить какое-л. препятствие;\n<b>13)</b> затяжка, промедление (pugnae nodusque moraque <i>V</i>);\n<b>14)</b> завязка (<i>в драматургическом произведении</i>) <i>H</i>."],
  ["noegeum", "<big>noegeum</big>\n\n<b>noegeum, i</b> <i>n</i> <i>предпол.</i>\nносовой платок <i>LA</i>."],
  ["Noemon", "<big>Noemon</big>\n\n<b>Noemon, onis</b> <i>m</i>\nНоэмон, <i>мужское имя</i> <i>V</i>."],
  ["noenu", "<big>noenu</big>\n\n<b>noenu (noenum)</b> [ne + oenum = unum] <i>арх.</i>\n(= non) не <i>Enn, Pl, LM, Lcr</i>."],
  ["noerus", "<big>noerus</big>\n\n<b>noerus, a, um</b> (<i>греч.</i>)\nрассудительный, разумный <i>Tert</i>."],
  ["nola 1", "<big>nola</big>\n\n<b>I nola, ae</b> <i>f</i> [nolo] <i>ирон.</i>\nнеприступная <i>Q</i>."],
  ["Nola 2", "<big>Nola</big>\n\n<b>II Nola, ae</b> <i>f</i>\nНола, <i>город в Кампании, к сев.-вост. от Везувия</i> <i>L, Sil, Aug</i>."],
  ["Nolanum", "<big>Nolanum</big>\n\n<b>Nolanum, i</b> <i>n</i>\nобласть Нолы <i>C</i>."],
  ["Nolanus 1", "<big>Nolanus</big>\n\n<b>I Nolanus, a, um</b> <i>и</i> <b>Nolensis, e</b> [Nola]\nноланский <i>C, L, Aug</i>."],
  ["Nolanus 2", "<big>Nolanus</big>\n\n<b>II Nolanus, i</b> <i>m</i>\nжитель города Nola <i>L</i>."],
  ["nolentia", "<big>nolentia</big>\n\n<b>nolentia, ae</b> <i>f</i> [nolo]\nнежелание <i>Tert</i>."],
  ["noli", "<big>noli</big>\n\n<b>noli, nolito, nolite</b> <i>imper. к</i>\nnolo."],
  ["nolle", "<big>nolle</big>\n\n<b>nolle</b> <i>inf. к</i>\nnolo."],
  ["nolo", "<big>nolo</big>\n\n<b>nolo, nolui, —, nolle</b> [ne + volo]\n<b>1)</b> не желать (aliquid <i>C, Nep etc.</i>); отказываться (n. parere <i>Nep</i>): me nolente <i>Q</i> против моей воли; ne homines, quos nolo, videar offendere <i>C</i> чтобы не подумали, будто я нападаю на людей, на которых я нападать не собираюсь; nolim statuas nos mente maligna id facere <i>Ctl</i> прошу тебя не думать, будто я делаю это по злому умыслу; quod nolunt, velint <i>SenT</i> пусть (бедняки) возымеют желания, которых у них нет; non n. <i>C</i> быть весьма склонным; <i>в отрицательно-повелительной форме</i>: noli (nolite) putare <i>C, Pt</i> не думай(те);\n<b>2)</b> сопротивляться: ducunt volentem fata, nolentem trahunt <i>погов. Sen</i> судьба ведёт того, кто ей повинуется, но тащит силой того, кто ей противится;\n<b>3)</b> недоброжелательно относиться, быть предубеждённым (n. alicui <i>C</i>)."],
  ["nomae", "<big>nomae</big>\n\n<b>nomae, arum</b> <i>PM f pl. к</i>\nnome."],
  ["nomas 1", "<big>nomas</big>\n\n<b>I nomas, adis</b> <i>m, f</i> (<i>греч.</i>)\nномад, кочевник, <i>преим.</i> нумидиец <i>PM, Sil, Prp, V</i>."],
  ["Nomas 2", "<big>Nomas</big>\n\n<b>II Nomas, adis</b> <i>f</i> (<i>sc.</i> terra)\nНумидия <i>M</i>."],
  ["nome", "<big>nome</big>\n\n<b>nome, es</b> <i>f</i> (<i>греч.</i>)\nнома, водяной рак (<i>болезнь</i>) <i>PM</i>."],
  ["nomen", "<big>nomen</big>\n\n<b>nomen, inis</b> <i>n</i>\n<b>1)</b> имя, название, наименование (n. paternum <i>Dig</i>; amicitiae sanctum et venerabile nomen <i>O</i>); aliquem nomine clamare <i>V</i> (increpare <i>L</i>) звать кого-л. по имени; urbs nomine Obba <i>L</i> город с названием Обба; n. mihi est Gajo (Gajus, Gai) <i>Pl, C etc.</i> меня зовут Гаем; n. dare <i>C, L</i> (edere, profiteri <i>L</i>) явиться (записаться) добровольцем; ad n. respondere <i>L</i> отзываться на перекличке; n. accipere <i>L</i> внести (чьё-л.) имя в избирательные списки; n. alicujus deferre de aliqua re <i>C</i> привлечь кого-л. к судебной ответственности по обвинению в чём-л.; n. recipere <i>C</i> принять жалобу, <i>т. е.</i> дать ход делу (<i>о преторе</i>); alicui nomine notum esse <i>C</i> быть известным кому-л. (лишь) по имени; nomine in Hectoreo pallidus <i>O</i> бледнеющий при (одном лишь) имени Гектора;\n<b>2)</b> (<i>тж.</i> n. gentilicium <i>или</i> gentile <i>Su</i>) родовое имя (<i>иногда</i> praenomen <i>или</i> cognomen <i>C etc.</i>): aliquem in familiam nomenque adoptare <i>Su</i> принять кого-л. в состав рода;\n<b>3)</b> звание, титул (n. regium <i>C</i>; n. imperatoris <i>Cs</i>); сословие (clarissimum n. <i>Dig</i> = senatores); достоинство (n. regale <i>C</i>);\n<b>4)</b> <i>грам.</i> имя, <i>преим.</i> существительное <i>Q</i>; слово, термин: n. carendi <i>C</i> слово (понятие) carere;\n<b>5)</b> род (n. Fabiorum <i>C</i>; ex nomine defuncti <i>Dig</i>);\n<b>6)</b> народ, нация <i>или</i> страна: n. populi Romani <i>и</i> n. Romanum <i>C etc.</i> всё римское, римский народ; n. Latinum <i>L</i> латиняне <i>или</i> страна латинян (Латий);\n<b>7)</b> человек, лицо: nomina tanta <i>O</i> такие мужи; vestrum n. <i>L</i> = vos;\n<b>8)</b> славное имя, слава, известность, знаменитость: n. habere <i>C</i> быть славным; sine nomine <i>V, O, Just</i> неизвестный, неведомый, безымянный; iisdemne ut finibus n. suum, quibus vita, terminaretur? <i>C</i> неужели его слава окончится там, где кончилась его жизнь?;\n<b>9)</b> предлог, повод, формальное основание: honestis nominibus <i>Sl</i> под благовидными предлогами; per n. militare <i>Sl</i> на основании состояния войны (под предлогом войны); eo nomine <i>C etc.</i> на этом основании, по этой причине; nullo nomine <i>Sen</i> ни в каком отношении; nullo nomine non <i>VP</i> во всех отношениях, всячески; nomine alicujus rei <i>C etc.</i> во имя (ради) чего-л.; meo nomine <i>C etc.</i> ради меня <i>или</i> с моей стороны <i>или</i> по моим соображениям <i>или</i> от моего имени; nomine obsidum <i>Cs</i> в качестве заложников; dotis nomine <i>Cs</i> в виде приданого; uno nomine <i>C</i> разом, сразу, в целом;\n<b>10)</b> видимость, форма, (одно лишь) имя (rex nomine magis, quam imperio <i>Nep</i>);\n<b>11)</b> лицевой счёт, долговая запись, долг: n. facere <i>C, O</i> (in tabulas <i>или</i> in codicem referre <i>C</i>) записать долг (за кем-л.); n. solvere (dissolvere, exsolvere, expedire) <i>C</i> оплачивать (погашать) долг; nomina exigere <i>C</i> взыскивать долги;\n<b>12)</b> долговая расписка, денежное обязательство: alicui certis nominibus grandem pecuniam debere <i>C</i> задолжать кому-л. крупную сумму под бесспорные долговые обязательства;\n<b>13)</b> должник (n. certum <i>или</i> bonum <i>C</i>, lentum <i>Sen</i>)."],
  ["nomenclatio", "<big>nomenclatio</big>\n\n<b>nomenclatio, onis</b> <i>f</i> [nomen + calo = voco]\n<b>1)</b> именование, называние по имени <i>Quintus C</i>;\n<b>2)</b> перечень, список <i>Col</i>."],
  ["nomenclator", "<big>nomenclator</big>\n\n<b>nomenclator, oris</b> <i>m</i> [nomenclatio]\nноменклатор (<i>раб, на обязанности которого лежало знать и называть своему господину имена граждан города и всех рабов в доме, а также провозглашать названия подаваемых кушаний</i>) <i>C, Sen etc.</i>"],
  ["nomenclatura", "<big>nomenclatura</big>\n\n<b>nomenclatura, ae</b> <i>f</i> [nomenclatio]\nперечень, список <i>PM</i>."],
  ["nomenculator", "<big>nomenculator</big>\n\n<b>nomenculator, oris</b> <i>m</i> <i>Su, Pt, Dig</i>\n= nomenclator."],
  ["Nomentanum", "<big>Nomentanum</big>\n\n<b>Nomentanum, i</b> <i>n</i>\nпоместье близ Номента <i>Nep</i>."],
  ["Nomentanus", "<big>Nomentanus</big>\n\n<b>Nomentanus, a, um</b> [Nomentum]\nноментанский <i>L</i>."],
  ["Nomentum", "<big>Nomentum</big>\n\n<b>Nomentum, i</b> <i>n</i>\nНомент, <i>городок в Латии, к сев.-вост. от Рима</i> (<i>ныне</i> Ментана) <i>L, V</i>."],
  ["nominalia", "<big>nominalia</big>\n\n<b>nominalia, ium</b> <i>n</i> [nominalis]\nноминалии, день наречения имени ребёнку <i>Tert</i>."],
  ["nominalis", "<big>nominalis</big>\n\n<b>nominalis, e</b> [nomen]\nкасающийся имени, именной (gentilitas <i>Vr</i>)."],
  ["nominatim", "<big>nominatim</big>\n\n<b>nominatim</b> [nomino]\nпо имени, поимённо (centuriones n. appellare <i>Cs</i>): quos ne n. tradam <i>T</i> имена которых я не хотел бы называть."],
  ["nominatio", "<big>nominatio</big>\n\n<b>nominatio, onis</b> <i>f</i> [nomino]\n<b>1)</b> называние, именование <i>Vr, rhH</i>;\n<b>2)</b> выдвижение кандидатуры, предложение на пост, называние кандидата (n. consulum <i>T</i>)."],
  ["nominativus", "<big>nominativus</big>\n\n<b>nominativus, a, um</b> [nomino] <i>грам.</i>\nименительный (casus <i>Vr, Q</i>)."],
  ["nominator", "<big>nominator</big>\n\n<b>nominator, oris</b> <i>m</i> [nomino]\n<b>1)</b> именующий, дающий имя <i>Aug</i>;\n<b>2)</b> назначающий <i>или</i> избирающий (n. magistratuum <i>Dig</i>)."],
  ["nominatus 1", "<big>nominatus</big>\n\n<b>I nominatus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> nomino;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> известный, славный, знаменитый: illa Attalica tota Sicilia nominata <i>C</i> атталики (златотканые ковры), известные во всей Сицилии;\n<b>2)</b> так называемый (philosophus n. <i>C</i>)."],
  ["nominatus 2", "<big>nominatus</big>\n\n<b>II nominatus, us</b> <i>m</i>\nнаименование, название, имя <i>Vr</i>."],
  ["nominito", "<big>nominito</big>\n\n<b>nominito, —, atum, are</b> [<i>frequ. к</i> nomino]\nименовать, называть (aliquid <i>Lcr</i>)."],
  ["nomino", "<big>nomino</big>\n\n<b>nomino, avi, atum, are</b> [nomen]\n<b>1)</b> называть, именовать (aliquid, aliquem <i>C etc.</i>): nominandi casus <i>Vr, AG</i> именительный падеж;\n<b>2)</b> называть по имени, упоминать, приводить (aliquem honoris causa <i>C</i>; omnes nominari volunt <i>C</i>): sempiterno nominabitur <i>Pt</i> его имя будет вечно на устах || обращаться по имени (quis me nominat? <i>Pl</i>);\n<b>3)</b> указывать, доносить (aliquem inter socios Catilinae <i>Su</i>);\n<b>4)</b> объявлять, провозглашать, назначать (interregem <i>L</i>; aliquem dictatorem <i>L</i>);\n<b>5)</b> выдвигать в кандидаты (n. augurem in contione <i>rhH</i>)."],
  ["Nomion", "<big>Nomion</big>\n\n<b>Nomio(n), onis</b> <i>m</i> [Nomius]\nгимн Аполлону <i>C</i> (<i>v. l.</i>)."],
  ["nomisma", "<big>nomisma</big>\n\n<b>nomisma, atis</b> <i>n</i> <i>H, M etc.</i>\n= numisma."],
  ["Nomius", "<big>Nomius</big>\n\n<b>Nomius (-os), i</b> <i>m</i> [<i>греч.</i> &laquo;пастырь&raquo;]\n<i>эпитет Аполлона</i> <i>C</i>."],
  ["nomos", "<big>nomos</big>\n\n<b>nomos, i</b> <i>m</i> (<i>греч.</i>)\n<b>1)</b> ном, область, округ <i>PM, PJ</i>;\n<b>2)</b> напев, песня <i>Su</i>."],
  ["nomus", "<big>nomus</big>\n\n<b>nomus</b> <i>Enn</i>\n= novimus (<i>pf.</i> <i>в знач.</i> <i>praes. к</i> nosco)."],
  ["non", "<big>non</big>\n\n<b>non</b> [<i>из</i> noenum = ne+ unum]\n<b>1)</b> не (n. feram, n. patiar, n. sinam! <i>C</i>): n. amicus <i>C</i> недруг, противник; n. ita <i>или</i> n. tam <i>C etc.</i> не так уж, не особенно; n. dubie <i>PJ</i> без сомнения; n. omnis <i>L</i> ни один, никто; n. vero <i>C</i> право не; n. invitus <i>C</i> охотно; n. sine <i>C etc.</i> = cum; n. ignorare <i>C</i> хорошо знать; n. homo <i>H</i> = nemo; n. minime <i>C</i> крайне, весьма; n. nemo <i>C</i> кое-кто, иной; nemo... n. <i>C</i> всякий; n. nihil <i>C</i> кое-что; nihil... n. <i>C</i> всё; n. nusquam <i>PM</i> кое-где; nusquam... n. <i>C</i> повсюду, везде; n... nisi (nisi... n.) <i>C</i> лишь, только; пес п. <i>C</i> а также; n. parere noluit <i>Nep</i> он не отказал в повиновении;\n<b>2)</b> <i>в вопросах</i> (= nonne) разве не: n. in medium proferenda res erit? <i>C</i> разве не следует дать делу законный ход?; n. taces? <i>Pt</i> замолчишь ты или нет?; <i>иногда</i> ne <i>перед imperat.</i> (n. petito <i>O</i>) <i>или</i> <i>conjct. hort.-optat.</i> (n. dubitaveris <i>Sen</i>);\n<b>3)</b> <i>в отриц. ответах</i> нет: aut &laquo;etiam&raquo;, aut &laquo;non&raquo; respondere <i>C</i> отвечать или &laquo;да&raquo;, или &laquo;нет&raquo;."],
  ["nona 1", "<big>nona</big>\n\n<b>I nona, ae</b> <i>f</i> [nonus]\n<b>1)</b> (<i>sc.</i> hora) девятый час дня, <i>т. е.</i> 3 часа пополудни (<i>обеденное время</i>) <i>H, M</i>;\n<b>2)</b> (<i>sc.</i> pars) девятая часть (nonas — <i>sc.</i> praedae — vovere <i>Just</i>)."],
  ["Nona 2", "<big>Nona</big>\n\n<b>II Nona, ae</b> <i>f</i> [nonus]\nНона, <i>первая из Парок</i> (<i>как богиня девятого месяца беременности</i>) <i>Vr ap. AG, Tert</i>."],
  ["Nonacria", "<big>Nonacria</big>\n\n<b>Nonacria, ae</b> <i>f</i>\n<b>1)</b> <i>Sen</i> = Nonacris;\n<b>2)</b> <i>O</i> = Atalanta."],
  ["Nonacrinus", "<big>Nonacrinus</big>\n\n<b>Nonacrinus, a, um</b> [Nonacris]\nнонакрийский, <i>поэт.</i> аркадский: Nonacrina virgo <i>O</i> = Callisto."],
  ["Nonacris", "<big>Nonacris</big>\n\n<b>Nonacris, is</b> <i>f</i>\n<i>городок в сев. Аркадии</i> <i>PM, Vtr</i>."],
  ["Nonacrius", "<big>Nonacrius</big>\n\n<b>Nonacrius, a, um</b> (= Nonacrinus)\nаркадский: N. heros <i>O</i> = Euander."],
  ["Nonae", "<big>Nonae</big>\n\n<b>Nonae, arum</b> <i>f</i> [nonus, <i>т. е.</i> девятый день до Ид]\nНоны, 5-й или (в марте, мае, июле, октябре) 1-й день месяца <i>Vr, C etc.</i>"],
  ["nonagenarius", "<big>nonagenarius</big>\n\n<b>nonagenarius, a, um</b> [nonageni]\nсодержащий девяносто (numerus <i>Sol</i>); шириной в девяносто дюймов (fistula <i>Frontin</i>); девяностолетний <i>Vlg, Eccl</i>."],
  ["nonageni", "<big>nonageni</big>\n\n<b>nonageni, ae, a</b> <i>adj. num. distr.</i> [nonaginta]\nпо девяносто <i>PM, Frontin</i>."],
  ["nonagesimus", "<big>nonagesimus</big>\n\n<b>nonagesimus, a, um</b> <i>adj. num. ord.</i> [nonaginta]\nдевяностый (annus <i>C, VM</i>)."],
  ["nonagies", "<big>nonagies</big>\n\n<b>nonagies (nonagiens)</b> <i>adv. num.</i> [nonaginta]\nдевяносто раз <i>C etc.</i>"],
  ["nonaginta", "<big>nonaginta</big>\n\n<b>nonaginta</b> [nonagies] <i>adj. num. card.</i>\nдевяносто <i>C etc.</i>"],
  ["nonalis", "<big>nonalis</big>\n\n<b>nonalis, e</b> [Nonae]\nнональный, относящийся к Нонам (Nonalia sacra <i>Vr</i>)."],
  ["nonanus", "<big>nonanus</big>\n\n<b>nonanus, a, um</b> [nonus]\nпринадлежащий к девятому легиону (miles <i>T</i>)."],
  ["nonaria", "<big>nonaria</big>\n\n<b>nonaria, ae</b> <i>f</i> [nonus]\n(<i>sc.</i> meretrix) публичная женщина (<i>женщины этой профессии могли показываться не ранее 3-го часа</i>) <i>Pers</i>."],
  ["nondum", "<big>nondum</big>\n\n<b>non–dum</b> <i>adv.</i>\nещё не <i>Ter, Cs, C etc.</i>: n. etiam <i>C</i> всё ещё не."],
  ["nongentesimus", "<big>nongentesimus</big>\n\n<b>nongentesimus, a, um</b> <i>adj. num. ord.</i> [nongenti]\nдевятисотый <i>AV, CA</i>."],
  ["nongenti", "<big>nongenti</big>\n\n<b>nongenti, ae, a</b> <i>adj. num. card.</i>\nдевятьсот <i>C, Col etc.</i>"],
  ["nongenties", "<big>nongenties</big>\n\n<b>nongenties (noningenties)</b> <i>adv. num.</i> [nongenti]\nдевятьсот раз <i>Vr, C etc.</i>"],
  ["nongentus", "<big>nongentus</big>\n\n<b>nongentus, i</b> <i>m</i>\nодин из девятисот <i>PM</i>."],
  ["noningenti", "<big>noningenti</big>\n\n<b>noningenti, ae, a</b> <i>Col</i>\n= nongenti."],
  ["noningenties", "<big>noningenties</big>\n\n<b>noningenties</b> <i>v. l.</i>\n= nongenties."],
  ["Nonius", "<big>Nonius</big>\n\n<b>Nonius, i</b> <i>m</i>\nНоний, <i>римск. nomen</i>: N. Marcellus, <i>римск. грамматик.</i>"],
  ["nonna", "<big>nonna</big>\n\n<b>nonna, ae</b> <i>f</i>\nмонахиня <i>Eccl</i>."],
  ["nonne", "<big>nonne</big>\n\n<b>non–ne</b>\n<b>1)</b> <i>в прямых вопросах</i> не правда ли?, разве не? (n. animadvertis? <i>C</i>);\n<b>2)</b> <i>в косвен. вопросах</i> не... ли: Croesus ex Solone quaesivit, n. se beatissimum putaret <i>C</i> Крез спросил Солона, не считает ли он его счастливейшим."],
  ["nonnemo", "<big>nonnemo</big>\n\n<b>non–nemo, inis</b> (<i>тж. раздельно</i>)\nкто-нибудь, кое-кто, иной, некто <i>C etc.</i>"],
  ["nonnihil", "<big>nonnihil</big>\n\n<b>non–nihil</b> <i>n</i> (<i>тж. раздельно</i>) <i>indecl.</i>\n<b>1)</b> нечто, кое-что <i>C etc.</i>;\n<b>2)</b> несколько <i>C etc.</i>"],
  ["nonnisi", "<big>nonnisi</big>\n\n<b>non–nisi</b> <i>conj.</i> (<i>тж. раздельно</i>)\nтолько, лишь <i>C, CC etc.</i>"],
  ["nonnullus", "<big>nonnullus</big>\n\n<b>non–nullus, a, um</b> (<i>тж. раздельно</i>)\nнекоторый (non nulla pars <i>Cs</i>); иной (non nulli <i>Cs, Nep etc.</i>)."],
  ["nonnumquam", "<big>nonnumquam</big>\n\n<b>non–numquam</b> <i>adv.</i> (<i>тж. раздельно</i>)\nиногда, подчас <i>C etc.</i>"],
  ["nonnus", "<big>nonnus</big>\n\n<b>nonnus, i</b> <i>m</i>\nмонах, инок <i>Eccl</i>."],
  ["nonnusquam", "<big>nonnusquam</big>\n\n<b>non–nusquam</b> <i>adv.</i> (<i>тж. раздельно</i>)\nкое-где, в некоторых местах, местами <i>Pl, AG</i>."],
  ["nono", "<big>nono</big>\n\n<b>nono</b> <i>adv.</i> [nonus]\nв-девятых <i>Eccl</i>."],
  ["nonus", "<big>nonus</big>\n\n<b>nonus, a, um</b> <i>num. ord.</i> [<i>из</i> *novenus <i>от</i> novem]\nдевятый (annus <i>H</i>; locus <i>C</i>). — <i>См. тж.</i> nona I."],
  ["nonusdecimus", "<big>nonusdecimus</big>\n\n<b>nonus–decimus (decumus), a, um</b> <i>adj.</i> (<i>тж. раздельно</i>) <i>num. ord.</i>\nдевятнадцатый <i>T etc.</i>"],
  ["nonussis", "<big>nonussis</big>\n\n<b>nonussis, is</b> <i>m</i> [novem + assis]\n(монета в) девять ассов <i>Vr</i>."],
  ["Nora 1", "<big>Nora</big>\n\n<b>I Nora, orum</b> <i>n</i>\nНоры, <i>горная крепость в Каппадокии, в сев. части Тавра</i> <i>Nep</i>."],
  ["Nora 2", "<big>Nora</big>\n\n<b>II Nora, ae</b> <i>f</i>\nНора, <i>город на южн. побережье Сардинии</i> <i>C, PM</i>."],
  ["noram", "<big>noram</big>\n\n<b>noram</b> <i>стяж.</i>\n= noveram (<i>ppf. к</i> nosco)."],
  ["Norba", "<big>Norba</big>\n\n<b>Norba, ae</b> <i>f</i>\nНорба, <i>город вольсков в Латии, к юго.-вост. от Рима</i> <i>L</i>."],
  ["Norbanus 1", "<big>Norbanus</big>\n\n<b>I Norbanus, a, um</b> [Norba]\nнорбанский <i>L</i>."],
  ["Norbanus 2", "<big>Norbanus</big>\n\n<b>II Norbanus, i</b> <i>m</i>\nжитель города Norba <i>L</i>."],
  ["Noreia", "<big>Noreia</big>\n\n<b>Noreia, ae</b> <i>f</i>\nНорейя, <i>главн. город таврисков в Норике, на реке Murius</i> (<i>в 113 г. до н. э. римск. войска Карбона потерпели здесь поражение от кимбров</i>) <i>Cs, L</i>."],
  ["Norenses", "<big>Norenses</big>\n\n<b>Norenses, ium</b> <i>m</i>\nжители города Nora <i>C, PM</i>."],
  ["Norica", "<big>Norica</big>\n\n<b>Norica, ae</b> <i>f</i>\nжительница области Noricum <i>Cs</i>."],
  ["Noricum", "<big>Noricum</big>\n\n<b>Noricum, i</b> <i>n</i>\nНорик, <i>область к югу от Дуная, между Ретией и Паннонией</i> <i>T</i>."],
  ["Noricus", "<big>Noricus</big>\n\n<b>Noricus, a, um</b> [Noricum]\nнорикский (ager <i>Cs</i>; ferrum <i>Pt</i>)."],
  ["norim", "<big>norim</big>\n\n<b>norim</b> <i>стяж.</i>\n= noverim (<i>pf. conjct. к</i> nosco)."],
  ["norma", "<big>norma</big>\n\n<b>norma, ae</b> <i>f</i> [nosco]\n<b>1)</b> наугольник (anguli ad normam respondentes <i>Vtr</i>);\n<b>2)</b> норма, руководящее начало, правило (n. rationis, juris <i>C</i>; n. loquendi <i>H</i>); образец (Demosthenes n. oratoris <i>PJ</i>)."],
  ["normalis", "<big>normalis</big>\n\n<b>normalis, e</b> [norma]\nпостроенный по наугольнику, прямой (angulus <i>Q</i>; virgula <i>Man</i>)."],
  ["normaliter", "<big>normaliter</big>\n\n<b>normaliter</b>\nпо наугольнику <i>Boet</i>; по прямой линии <i>Amm</i>."],
  ["normo", "<big>normo</big>\n\n<b>normo, (avi), atum, are</b> [norma]\nотмеривать по наугольнику <i>или</i> по отвесу (ad perpendiculum aliquid n. <i>Col</i>)."],
  ["Nortia", "<big>Nortia</big>\n\n<b>Nortia, ae</b> <i>f</i>\nНортия, <i>этрусская богиня судьбы</i> <i>L, J, Tert</i>."],
  ["norunt", "<big>norunt</big>\n\n<b>norunt</b> <i>стяж.</i>\n= noverunt (<i>pf. в знач. praes. к</i> nosco)."],
  ["nos", "<big>nos</big>\n\n<b>nos</b> (<i>gen.</i> nostri <i>и</i> nostrum, <i>редко</i> nostrorum, nostrarum) [<i>pl. к</i> ego]\nмы; <i>часто</i> = ego <i>C, V etc.</i>"],
  ["noscentia", "<big>noscentia</big>\n\n<b>noscentia, ae</b> <i>f</i> [nosco]\n(по)знание <i>Symm</i>."],
  ["noscibilis", "<big>noscibilis</big>\n\n<b>noscibilis, e</b> [nosco]\nпознаваемый, постижимый <i>Tert, Aug</i>."],
  ["noscitabundus", "<big>noscitabundus</big>\n\n<b>noscitabundus, a, um</b> [noscito]\nжелающий узнать, выведывающий <i>AG</i>."],
  ["noscito", "<big>noscito</big>\n\n<b>noscito, avi, atum, are</b> [<i>intens. к</i> nosco]\n<b>1)</b> узнавать (aliquem facie <i>L</i>; voce <i>PJ</i>);\n<b>2)</b> замечать (aliquem <i>L</i>);\n<b>3)</b> воспринимать (aliquid <i>L</i>);\n<b>4)</b> наблюдать, разведывать, высматривать, исследовать (aedes, vestigia <i>Pl</i>)."],
  ["nosco", "<big>nosco</big>\n\n<b>nosco, novi, notum, ere</b>\n<b>1)</b> знакомиться, познавать, изучать (provinciam <i>T</i>; linguam aliquam, se ipsum <i>C</i>);\n<b>2)</b> узнавать, опознавать (aliquem voce, de facie <i>C</i>);\n<b>3)</b> признавать, принимать (causam <i>Pl, C</i>; excusationem <i>C</i>); допускать (hanc voluptatem <i>C</i>);\n<b>4)</b> расследовать, исследовать, рассматривать, допрашивать (omnia quae a praetoribus noscebantur <i>T</i>);\n<b>5)</b> испытывать: quid opus est nota n.? <i>погов. Pl</i> зачем пытать испытанное?;\n<b>6)</b> <i>pf.</i> novi я узнал = знаю (Caesarem bene novi <i>C</i>) <i>или</i> умею (non novit virtus calamitati cedere <i>PS</i>): hunc servare modum nostri novere (= noverunt) libelli <i>M</i> в моих сочинениях соблюдён следующий принцип; nosse (— scire) Latine et Punice <i>Aug</i> знать латинский и пунический языки."],
  ["nosmet", "<big>nosmet</big>\n\n<b>nosmet</b> <i>C etc. intens. к</i>\nnos."],
  ["nosocomium", "<big>nosocomium</big>\n\n<b>nosocomium, i</b> <i>n</i> (<i>греч.</i>)\nбольница <i>CJ</i>."],
  ["nosocomus", "<big>nosocomus</big>\n\n<b>nosocomus, i</b> <i>m</i> (<i>греч.</i>)\nсанитар <i>CJ</i>."],
  ["nosse", "<big>nosse</big>\n\n<b>nosse</b> <i>стяж.</i>\n= novisse (<i>inf. pf. к</i> nosco)."],
  ["noster", "<big>noster</big>\n\n<b>noster, nostra, nostrum</b> [nos]\n<b>1)</b> наш (pater, provincia <i>Cs</i>; mare <i>Sl</i>);\n<b>2)</b> направленный на нас (amor <i>C</i>) <i>или</i> против нас (injuria <i>Sl</i>);\n<b>3)</b> современный нам, нынешний (tempora <i>C</i>);\n<b>4)</b> удобный для нас, благоприятствующий нам (loca <i>L</i>): nostro Marte pugnamus <i>V</i> мы сражаемся успешно;\n<b>5)</b> (<i>в обращении</i>) милый, любезный, друг мой (о п., salve! <i>Ter</i>);\n<b>6)</b> <i>шутл. Pl, H</i> = ego."],
  ["nosti", "<big>nosti</big>\n\n<b>nosti</b> <i>стяж.</i>\n= novisti (<i>pf. к</i> nosco)."],
  ["nostras", "<big>nostras</big>\n\n<b>nostras, atis</b> <i>adj.</i> [noster]\nнаш, местный, здешний, отечественный (philosophus, verba <i>C</i>; vox <i>Su</i>)."],
  ["nostratis", "<big>nostratis</big>\n\n<b>nostratis, e</b> <i>adj.</i> <i>арх. Cato</i>\n= nostras."],
  ["nostri", "<big>nostri</big>\n\n<b>nostri</b> <i>gen. к</i>\n<b>1)</b> nos <i>и</i>\n<b>2)</b> noster."],
  ["nostrorum", "<big>nostrorum</big>\n\n<b>nostrorum</b> <i>и</i> <b>nostrum</b> <i>gen. pl. к</i>\n<b>1)</b> nos <i>и</i>\n<b>2)</b> noster."],
  ["nota", "<big>nota</big>\n\n<b>nota, ae</b> <i>f</i>\n<b>1)</b> знак, (от)метка (aliquid nota distinguere <i>L</i>): n. pedum <i>O</i> след ног || признак (locorum <i>C</i>); пометка (notam apponere ad malum versum <i>C</i>); улика, опознавательное средство (notae scelerum <i>C</i>);\n<b>2)</b> письменный знак, буква (notae litterarum <i>C, Pt</i>): notae librariorum <i>C</i> знаки переписчиков, <i>т. е.</i> препинания || тайнопись, шифр (per notas scribere <i>Su</i>); стенографический знак (notis velocissime excipere <i>Su</i>); музыкальная нота (notis musicis cantica excipere <i>Q</i>);\n<b>3)</b> пятно, родимое пятнышко, отметина (genetivae notae per pectusdispersae <i>Su</i>); тавро (n. dorsualis <i>Ap</i>); татуировка (barbarus compunctus notis <i>C</i>);\n<b>4)</b> клеймо (deformare stigmatum notis <i>Su</i>); <i>перен.</i> позор, бесчестье (n. turpitudinis <i>C</i>): alicui notas adjicere <i>L</i> покрыть кого-л. бесчестьем;\n<b>5)</b> чекан, штамп (nummi omnis notae <i>Su</i>);\n<b>6)</b> замечание, порицание (cetisoriae severitatis n. <i>C</i>);\n<b>7)</b> характер (interior, animi nota <i>Su</i>);\n<b>8)</b> ярлык, этикетка, марка (n. Falerni <i>H</i>); сорт (mel secundae notae <i>Col</i>);\n<b>9)</b> вид, род, разряд (haec n. corporum <i>Sen</i>);\n<b>10)</b> отличительная нерта, свойство, качество (aliquem de meliore nota commendare <i>C</i>);\n<b>11)</b> почётное прозвище (notam trahere ab aliqua re <i>O</i>);\n<b>12)</b> поданный знак, кивок (notas accipere et reddere <i>O</i>);\n<b>13)</b> <i>pl.</i> записка, письмо <i>H, O</i>."],
  ["notabilis", "<big>notabilis</big>\n\n<b>notabilis, e</b> [noto]\n<b>1)</b> замечательный, достопамятный, достопримечательный (exitus <i>C</i>);\n<b>2)</b> поразительный, чрезвычайный (foeditas <i>PM</i>): notabilia in genere vitae <i>Sen</i> необычный образ жизни;\n<b>3)</b> ясно видный (candor, <i>sc.</i> cretae <i>Pt</i>); заметный, ощутительный (vix n. <i>Sen</i>)."],
  ["notabiliter", "<big>notabiliter</big>\n\n<b>notabiliter</b> [notabilis]\n<b>1)</b> поразительно, чрезвычайно (expallescere <i>PJ</i>);\n<b>2)</b> заметно (eminere <i>PJ</i>)."],
  ["notaculum", "<big>notaculum</big>\n\n<b>notaculum, i</b> <i>n</i>\nзнак, отметина (corporis <i>MF</i>)."],
  ["notarius", "<big>notarius</big>\n\n<b>notarius, i</b> <i>m</i> [nota]\n<b>1)</b> скорописец, стенограф <i>Q, PJ</i>;\n<b>2)</b> писец, секретарь <i>PJ, Treb</i>."],
  ["notatio", "<big>notatio</big>\n\n<b>notatio, onis</b> <i>f</i> [noto]\n<b>1)</b> обозначение, записывание: n. tabularum <i>C</i> заполнение избирательных дощечек (<i>воском различных цветов</i>);\n<b>2)</b> описывание, характеристика (vitae <i>C</i>);\n<b>3)</b> рассмотрение, наблюдение (n. naturae <i>C</i>);\n<b>4)</b> назначение, избрание (judicum <i>C</i>);\n<b>5)</b> замечание, порицание (n. censoria <i>C</i>)."],
  ["notatus", "<big>notatus</big>\n\n<b>notatus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> noto;\n<b>2.</b> <i>adj.</i> видимый, заметный (similitudines <i>rhH</i>; omnium scelerum maculis notatissimus <i>C</i>)."],
  ["notesco", "<big>notesco</big>\n\n<b>notesco, tui, —, ere</b> [notus]\nстановиться известным, доходить до сведения (alicui <i>T etc.</i>); приобретать известность (notescat magis atque magis <i>Ctl</i>)."],
  ["nothus", "<big>nothus</big>\n\n<b>nothus, a, um</b> (<i>греч.</i>)\n<b>1)</b> внебрачный: n. Sarpedonis <i>V</i> побочный сын Сарпедона;\n<b>2)</b> нечистокровный, смешанной породы (<i>о животных</i>) <i>Col, PM, Sol</i>;\n<b>3)</b> не свой, заимствованный (notho lumine lustrare <i>Lcr</i>); чужой (nomina <i>Vr</i>);\n<b>4)</b> мнимый (Attis notha mulier <i>Ctl</i>)."],
  ["noti", "<big>noti</big>\n\n<b>noti, orum</b> <i>m</i> [notus]\nзнакомые, друзья <i>H</i>."],
  ["notio", "<big>notio</big>\n\n<b>notio, onis</b> <i>f</i> [nosco]\n<b>1)</b> ознакомление, знакомство, узнавание <i>Pl</i>;\n<b>2)</b> представление, понятие (notiones insitae <i>C</i>);\n<b>3)</b> идея (alicujus rei <i>или</i> de aliqua re <i>C</i>);\n<b>4)</b> смысл, значение (verbi <i>C</i>);\n<b>5)</b> обследование (pontificum <i>C</i>);\n<b>6)</b> замечание, выговор (n. censoria <i>AG</i>)."],
  ["notitia", "<big>notitia</big>\n\n<b>notitia, ae</b> <i>f</i> [notus]\n<b>1)</b> знакомство (inter aliquos <i>Pl</i>): propter notitiam <i>Nep</i> по знакомству, в качестве знакомого; plus notitiae dare alicui <i>O</i> сделать кого-л. более известным; notitiam alicujus rei habere <i>Q</i> быть знакомым с чём-л. (<i>ср.</i> 2);\n<b>2)</b> известность, слава: notitiam consequi <i>Vtr</i> создать себе имя; notitiam posteritatis habere <i>O</i> пользоваться известностью у потомства (<i>ср.</i> 1); in notitiam alicujus pervenire <i>L</i> стать известным (знакомым) кому-л.;\n<b>3)</b> (по)знание (alicujus rei <i>C etc.</i>);\n<b>4)</b> представление, понятие: notitiam aperire <i>C</i> вскрыть (объяснить) понятие;\n<b>5)</b> признание, сознание (initium est salutis n. peccati <i>Epicurus ap. Sen</i>);\n<b>6)</b> ведомость, список, перечень <i>CJ</i>;\n<b>7)</b> половая связь (notitiam feminae habere <i>Cs</i>)."],
  ["notities", "<big>notities</big>\n\n<b>notities, ei</b> <i>f</i> <i>Lcr, Vtr</i>\n= notitia."],
  ["notius", "<big>notius</big>\n\n<b>notius, a, um</b> [Notus]\nюжный (sidera <i>Man</i>): Notium mare <i>PM</i> = Tyrrhenum mare."],
  ["noto", "<big>noto</big>\n\n<b>noto, avi, atum, are</b> [nota]\n<b>1)</b> обозначать, отмечать (aliquid aliqua re: siderum cursu tempora <i>Sen</i>): ab eventu facta notanda putare <i>O</i> полагать, что оценивать дела нужно по их исходу; creta an carbone n. <i>погов. H</i> помечать мелом или углём, <i>т. е.</i> оценивать положительно или отрицательно;\n<b>2)</b> исписывать, испещрять: digitis charta notata meis <i>O</i> моими пальцами (= руками) исписанный листок;\n<b>3)</b> царапать (genas ungue <i>O</i>);\n<b>4)</b> отличать, награждать (aliquem decore <i>C</i>);\n<b>5)</b> помечать, начертать, записывать, писать (nomina <i>O</i>; libellos <i>M</i>); записывать вкратце <i>или</i> отмечать (n., non perscribere <i>Su</i>);\n<b>6)</b> выражать (aliquid verbis Latinis <i>C</i>);\n<b>7)</b> указывать, намекать (n. aliquem <i>O</i>);\n<b>8)</b> объяснять, (этимологически) разъяснять (verbum <i>C</i>);\n<b>9)</b> воспринимать, замечать (fontem <i>QC</i>; cantus avium <i>C</i>); наблюдать (sidera <i>C</i>); подмечать (aliquid ex vultu <i>QC</i>); запоминать (dicta mente <i>O</i>; numerum <i>C</i>);\n<b>10)</b> делать выговор, клеймить (aliquem furti nomine <i>C</i>); бранить, порицать (aliquem verbis <i>C</i>): dignus notari <i>H</i> достойный порицания; n. aliquem alicujus rei <i>Aug</i> порицать кого-л. за что-л."],
  ["notor", "<big>notor</big>\n\n<b>notor, oris</b> <i>m</i> [nosco]\nлично знающий, свидетель <i>или</i> поручитель (notorem petere ab aliquo <i>Sen</i>; notorem dedisse <i>Pt</i>)."],
  ["notoria", "<big>notoria</big>\n\n<b>notoria, ae</b> <i>f</i> [noto]\nзаметка, записка <i>Treb</i>; сообщение, информация <i>Ap</i>."],
  ["notorium", "<big>notorium</big>\n\n<b>notorium, i</b> <i>n</i>\nобвинительное донесение, донос <i>Dig, Eccl</i>."],
  ["Notos", "<big>Notos</big>\n\n<b>Notos, i</b> <i>m</i> <i>v. l.</i>\n= Notus."],
  ["notrix", "<big>notrix</big>\n\n<b>notrix, tricis</b> <i>f</i> <i>арх. Q</i>\n= nutrix."],
  ["notus 1", "<big>notus</big>\n\n<b>I notus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> nosco;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> известный, знакомый (hoc mihi notum est <i>C, Sen</i>); aliquid notum habere <i>C</i> знать что-л.; aliquid notum facere alicui <i>C</i> ставить кого-л. в известность о чём-л.; n. animi in aliquem <i>H</i> известный своим отношением к кому-л.; nota mala res optuma est <i>погов. Pl</i> знакомое зло есть зло наименьшее; sic n. Ulixes? <i>V</i> так-то вы знаете Одиссея?;\n<b>2)</b> обычный, привычный (nota sedes columbarum <i>H</i>);\n<b>3)</b> известный, знаменитый (scriptor <i>H</i>);\n<b>4)</b> близкий, дружелюбный (voces <i>V</i>);\n<b>5)</b> общеизвестный, испытанный (virtus <i>Nep</i>);\n<b>6)</b> пресловутый, пользующийся дурной славой (mulier <i>C</i>; latrones <i>C</i>);\n<b>7)</b> <i>редко</i> знакомый (<i>с чём-л.</i>), знающий (n. alicujus rei <i>bH</i>)."],
  ["Notus 2", "<big>Notus</big>\n\n<b>II Notus, i</b> <i>m</i> (<i>греч.</i>; <i>лат.</i> Auster)\n<b>1)</b> сильный южный ветер (<i>с градом и ливнями</i>) <i>V, O etc.</i>;\n<b>2)</b> <i>поэт.</i> ветер (tendunt vela noti <i>V</i>);\n<b>3)</b> буря (n. procellosus <i>O</i>)."],
  ["novacula", "<big>novacula</big>\n\n<b>novacula, ae</b> <i>f</i>\nострый нож <i>C, L etc.</i>; бритва <i>Ph, Pt, M</i>."],
  ["novaculum", "<big>novaculum</big>\n\n<b>novac(u)lum, i</b> <i>n</i> <i>Lampr</i>\n= novacula."],
  ["Novaesium", "<big>Novaesium</big>\n\n<b>Novaesium</b> <i>v. l.</i>\n= Novesium."],
  ["novale", "<big>novale</big>\n\n<b>novale, is</b> <i>n</i>\n(<i>sc.</i> solum) <i>V, O etc.</i> = novalis II."],
  ["novalis 1", "<big>novalis</big>\n\n<b>I novalis, e</b> [novus]\nнаходящийся под паром, паровой, подлежащий вспашке (ager <i>Vr</i>)."],
  ["novalis 2", "<big>novalis</big>\n\n<b>II novalis, is</b> <i>f</i> (<i>sc.</i> terra)\n<b>1)</b> поле под паром (tonsae novales <i>V</i>);\n<b>2)</b> целина, новь (n. caesa vetere silva <i>PM</i>);\n<b>3)</b> поле, пашня <i>V, St</i>."],
  ["novamen", "<big>novamen</big>\n\n<b>novamen, inis</b> <i>n</i>\nнововведение, новшество, новость <i>Tert</i>."],
  ["novatio", "<big>novatio</big>\n\n<b>novatio, onis</b> <i>f</i> [novo]\n<b>1)</b> обновление, перемена <i>Eccl</i>;\n<b>2)</b> <i>юр.</i> замена старого обязательства новым (n. est prioris debiti in aliam obligationem transfusio atque translatio <i>Dig</i>)."],
  ["novator", "<big>novator</big>\n\n<b>novator, oris</b> <i>m</i> [novo]\nобновитель, реставратор (n. verborum Sallustius <i>AG</i>)."],
  ["novatrix", "<big>novatrix</big>\n\n<b>novatrix, tricis</b> <i>f</i> [novo]\nобновительница (rerum n. natura <i>O</i>)."],
  ["novatus", "<big>novatus</big>\n\n<b>novatus, us</b> <i>m</i> <i>Aug</i>\n= novatio 1."],
  ["nove", "<big>nove</big>\n\n<b>nove</b> [novus]\n<b>1)</b> ново, по-новому (verba n. dicta <i>AG</i>); модно, необыкновенно (ornatus n. <i>Pl</i>);\n<b>2)</b> <i>superl.</i> novissime недавно, только что (liber, quem novissime tibi misi <i>PJ</i>) <i>или</i> наконец (primum... deinde... novissime <i>C, Sen</i>)."],
  ["novellae", "<big>novellae</big>\n\n<b>novellae, arum</b> <i>f</i> [novellus]\n<b>1)</b> молодые деревья, молодые насаждения, молодняк <i>PM, Vlg</i>;\n<b>2)</b> <i>юр.</i> (<i>sc.</i> constitutiones) новеллы, <i>законодательные дополнения к</i> <i>CJ</i>."],
  ["novellatio", "<big>novellatio</big>\n\n<b>novellatio, onis</b> <i>f</i> [novello]\nновые насаждения (olivarum <i>Aug</i>)."],
  ["novelletum", "<big>novelletum</big>\n\n<b>novelletum, i</b> <i>n</i>\nместо с новыми насаждениями <i>Dig</i>."],
  ["novellitas", "<big>novellitas</big>\n\n<b>novellitas, atis</b> <i>f</i>\nновость <i>или</i> новизна <i>Tert</i>."],
  ["novello", "<big>novello</big>\n\n<b>novello, —, —, are</b> [novellus]\n<b>1)</b> (<i>тж.</i> n. vineas <i>Is</i>) делать новые насаждения, сажать молодые кусты (виноградника) <i>Su</i>;\n<b>2)</b> обновлять, начинать заново (n. vitam deo <i>Eccl</i>)."],
  ["novellus", "<big>novellus</big>\n\n<b>novellus, a, um</b> [<i>demin. к</i> novus]\n<b>1)</b> молоденький (arbor <i>C</i>; bos <i>Vr</i>; vitis <i>V</i>): turba novella <i>Tib</i> детвора;\n<b>2)</b> новый: oppidum novellum <i>L</i> вновь покорённый город; miles n. <i>Veg</i> новобранец."],
  ["novem", "<big>novem</big>\n\n<b>novem</b> <i>adj. num. card.</i>\nдевять (n. dierum iter <i>Cs</i>)."],
  ["November 1", "<big>November</big>\n\n<b>I November, bris</b> <i>m</i> (<i>sc.</i> mensis)\nноябрь, <i>девятый — впоследствии одиннадцатый — месяц года</i> <i>C, M etc.</i>"],
  ["November 2", "<big>November</big>\n\n<b>II November, bris, bre</b> [novem]\nноябрьский (Kalendae, Nonae, Idus <i>C etc.</i>)."],
  ["novemdecim", "<big>novemdecim</big>\n\n<b>novem–decim (noven–decim)</b> <i>adj. num. card.</i> [novem + decem]\nдевятнадцать <i>L</i> (<i>v. l.</i>)."],
  ["novemdialis", "<big>novemdialis</big>\n\n<b>novemdialis</b> <i>v. l.</i>\n= novendialis."],
  ["novenarius", "<big>novenarius</big>\n\n<b>novenarius, a, um</b> [novem]\n<b>1)</b> состоящий из девяти, девятеричный (numerus <i>Vr, Cens etc.</i>);\n<b>2)</b> имеющий 3 римских фута в ширину и 3 в глубину (sulcus <i>PM</i>)."],
  ["novendecim", "<big>novendecim</big>\n\n<b>noven–decim</b> <i>v. l.</i>\n= jiovemdecim."],
  ["novendiale", "<big>novendiale</big>\n\n<b>novendiale (novendial), is</b> <i>n</i> (<i>sc.</i> sacrum)\nжертвоприношение <i>или</i> поминальная трапеза, совершавшиеся на девятый день после похорон <i>Pt, Aug</i>."],
  ["novendialis", "<big>novendialis</big>\n\n<b>novendialis (novemdialis), e</b> [novem + dies]\n<b>1)</b> девятидневный (sacrum <i>или</i> sacrificium <i>L</i>): novendiales feriae <i>C</i> девятидневное празднество (<i>устраивавшееся для умилостивления богов в случае зловещих знамений</i>);\n<b>2)</b> происходящий на 3-й день: pulveres novendiales <i>H</i> (свежий ещё) пепел, погребавшийся на девятый день после кремации; cena n. <i>T</i> поминальный пир."],
  ["noveni", "<big>noveni</big>\n\n<b>noveni, ae, a</b> <i>num. distr.</i> (<i>sg.</i> <i>редко</i>)\nпо девяти.: novenis annis <i>O</i> раз в девять лет, каждое девятилетие; novena lampas <i>St</i> лампада, горящая девять дней."],
  ["novennis", "<big>novennis</big>\n\n<b>novennis, e</b> [novem+ annus]\nдевятилетний (filius <i>Lact</i>)."],
  ["Novensides", "<big>Novensides</big>\n\n<b>Novensides</b> <i>Vr</i>\n= Novensiles."],
  ["Novensiles", "<big>Novensiles</big>\n\n<b>Novensiles (dii)</b> <i>adj.</i> [novus]\nновые (заимствованные у других народов) боги: divi n., divi indigetes <i>L</i> новые и старые боги."],
  ["noverca", "<big>noverca</big>\n\n<b>noverca, ae</b> <i>f</i> [novus]\nмачеха <i>V, Q, Sen</i>: apud novercam queri <i>погов. Pl</i> жаловаться мачехе, <i>т. е.</i> напрасно."],
  ["novercalis", "<big>novercalis</big>\n\n<b>novercalis, e</b> [noverca]\n<b>1)</b> мачехин (agri <i>St</i>): novercales Mycenae <i>St</i> Микены, посвященные мачехе (Вакха, <i>т. е.</i> Юноне);\n<b>2)</b> <i>перен.</i> недружелюбный, враждебный (oculi <i>Sen</i>; odia <i>T</i>)."],
  ["novercor", "<big>novercor</big>\n\n<b>novercor, —, ari</b>\nотноситься как мачеха, <i>т. е.</i> недружелюбно (alicui <i>Sid</i>)."],
  ["novere", "<big>novere</big>\n\n<b>novere</b> (= noverunt) <i>3 л. pl. pf. к</i>\nnosco."],
  ["Novesium", "<big>Novesium</big>\n\n<b>Novesium, i</b> <i>n</i>\nНовезий, <i>укреплённое место убиев на Рейне</i> (<i>ныне</i> Нейс, <i>близ Дюссельдорфа</i>) <i>T, Amm</i>."],
  ["novi", "<big>novi</big>\n\n<b>novi, novisse</b> <i>pf. к</i>\nnosco."],
  ["noviciolus", "<big>noviciolus</big>\n\n<b>noviciolus, a, um</b> [<i>demin. к</i> novicius]\nновенький (paratura <i>Tert</i>)."],
  ["novicius 1", "<big>novicius</big>\n\n<b>I novicius, a, um</b> [novus]\nпринадлежащий к разряду новых, новый (nuperus et n. <i>Pl</i>); молодой (vinum <i>PM</i>); свежий (color <i>PM</i>)."],
  ["novicius 2", "<big>novicius</big>\n\n<b>II novicius, i</b> <i>m</i>\nновичок <i>J</i>; новый раб (grex noviciorum <i>C</i>)."],
  ["noviens", "<big>noviens</big>\n\n<b>novie(n)s</b> <i>adv. num.</i> [novem]\nдевять раз, девятикратно <i>Vr etc.</i>: n. Styx interfusa <i>V</i> девятикратно обтекающий (подземное царство) Стикс."],
  ["Noviodunum", "<big>Noviodunum</big>\n\n<b>Noviodunum, i</b> <i>n</i>\nНовиодун, <i>название нескольких кельт. городов, принадлежавших</i>:\n<b>1)</b> <i>племени Bituriges Cubi в Аквитании</i> (<i>ныне</i> Nouan, <i>близ Орлеана</i>) <i>Cs</i>;\n<b>2)</b> <i>племени Leuci e Belgica, между реками Матрона и Мозелла</i> (<i>ныне</i> Neufchateau) <i>Cs</i>;\n<b>3)</b> <i>племени Suessiones</i> (<i>ныне</i> Soissons, <i>на реке Aisne</i>) <i>Cs</i>;\n<b>4)</b> <i>племени Aedui на реке Лигер</i> (<i>ныне</i> Nevers) <i>Cs</i>."],
  ["novissime", "<big>novissime</big>\n\n<b>novissime</b> <i>superl. к</i>\nnove."],
  ["novitas", "<big>novitas</big>\n\n<b>novitas, atis</b> <i>f</i> [novus]\n<b>1)</b> новость, новизна: gratia novitatis <i>Q</i> прелесть новизны; n. anni <i>O</i> ранняя весна; aliqua novitate <i>O</i> с помощью какой-л. новой уловки || молодость, юность (mundi <i>Lcr</i>);\n<b>2)</b> <i>pl.</i> новые знакомства, недавно установленные связи <i>C</i>;\n<b>3)</b> недавно приобретённая знатность (vir novitatis nobilissimae <i>VP</i>; contemnere novitatem alicujus <i>Sl</i>);\n<b>4)</b> необычайность (sceleris <i>VP</i>)."],
  ["Novius", "<big>Novius</big>\n\n<b>Novius, a, um</b>\nНовий, <i>римск. nomen</i> <i>C, H etc.</i>"],
  ["novo", "<big>novo</big>\n\n<b>novo, avi, atum, are</b> [novus]\n<b>1)</b> обновлять (transtra <i>V</i>): momenta cuncta novantur <i>O</i> мгновения вечно сменяют друг друга || давать отдохнуть, освежать, подкреплять (fessa membra <i>O</i>; animus risu novatur <i>C</i>); возобновлять, воскрешать (amorem <i>O</i>): cura novata est <i>O</i> тревога вновь ожила || вторично вспахивать, двоить (agrum <i>C</i>); восстанавливать (agrum cultu <i>O</i>);\n<b>2)</b> разжигать (ardorem <i>V</i>); растравлять, бередить (vulnus <i>O</i>);\n<b>3)</b> (видо)изменять, переделывать, реформировать (naturam, formam <i>O</i>; pugnam equestrem <i>L</i>): res novandae <i>V</i> готовящиеся перемены || строить заново: n. tecta <i>V</i> строить новые дома;\n<b>4)</b> выдумывать, создавать, Изобретать (verba <i>C</i>; tela <i>V</i>);\n<b>5)</b> нарушать (fidem <i>V</i>): n. (res, omnia) <i>L</i> замышлять (совершать) переворот."],
  ["Novocomenses", "<big>Novocomenses</big>\n\n<b>Novocomenses, ium</b> <i>m</i>\nжители города Novum Comum (<i>см.</i> Comum) <i>C</i>."],
  ["novum", "<big>novum</big>\n\n<b>novum, i</b> <i>n</i>\nновое, новость, новшество, нововведение <i>Pl, Ter, C, L etc.</i>"],
  ["novus", "<big>novus</big>\n\n<b>novus, a, um</b>\n<b>1)</b> новый (navis <i>L</i>); ранний (aestas <i>Sl, V</i>); вновь сформированный, только что набранный (legio <i>C</i>); вновь избранный (consul <i>L</i>); молодой (arbor <i>Col</i>); юный (aetas <i>O</i>); недавно сжатый (frumentum <i>C</i>); свежий (lac <i>V</i>): n. maritus <i>Pl, Vr etc.</i> новобрачный; n. (miles) <i>Sl, L etc.</i> новобранец; nova res <i>C</i> новость; res novae <i>C etc.</i> нововведения, новшества, <i>тж.</i> перемены, переворот; rebus novis studere <i>C, Cs</i> (novas res moliri <i>VP</i>) замышлять переворот; nova imperia <i>C</i> смена власти; homo n. <i>C, Sl etc.</i> человек без роду и племени, незнатного происхождения, выскочка <i>или</i> первый в семье, занявший курульную должность;\n<b>2)</b> небывалый, неслыханный, невиданный, беспримерный (crimen <i>C, V</i>; monstra <i>H</i>); необычный (genus pugnae <i>Cs</i>); редкий, редкостный (immanitas, exemplum <i>C</i>): novum est (ut) <i>C</i> неслыханно (чтобы);\n<b>3)</b> неопытный; не привыкший, непривычный (aliqua re, alicui rei <i>или</i> ad aliquid): n. dolori <i>Sil</i> никогда не испытывавший боли; nova ferre jugum cervix <i>Sil</i> шея, непривычная к ярму || необъезженный (equus <i>C, O</i>);\n<b>4)</b> второй, другой: n. Decius <i>H</i> второй Деций (<i>т. е.</i> такой же самоотверженный патриот);\n<b>5)</b> <i>superl.</i> novissimus крайний, последний <i>или</i> тыловой, задний: primus ac novissimus <i>Su</i> первый и последний; novissimum agmen <i>или</i> novissimi <i>Cs etc.</i> арьергард; novissima acies <i>L</i> арьергардный бой; novissima cauda <i>O</i> кончик хвоста; novissima crura <i>O</i> нижняя часть ног; novissima verba <i>V</i> заключительные (последние) слова; etiam sapientibus cupido gloriae novissima exuitur <i>T</i> даже мудрецов жажда славы покидает в самую последнюю очередь; memorare novissima tua <i>Vlg</i> помни об исходе своих поступков; terrarum pars paene novissima <i>O</i> чуть ли не край света; novissimus casus <i>T</i> худший случай; novissima exspectare <i>T</i> ожидать наихудшего; novissima poena (exempla) <i>T</i> тягчайшее (высшее) наказание."],
  ["nox 1", "<big>nox</big>\n\n<b>I nox, noctis</b> <i>f</i>\n<b>1)</b> ночь (n. sideribus illustris <i>T</i>; n. gelida <i>V</i>): ante noctem <i>H</i> до наступления ночи, засветло; (de) nocte (<i>поэт. тж.</i> nocti) <i>C</i> ночной порой, ночью; (de) multa <i>или</i> concubia nocte <i>C</i> глубокой ночью; obducta <i>Nep</i> (obtenta <i>V</i>) nocte под покровом ночи; prima nocte <i>Cs, Nep</i> с наступлением ночи; priore nocte <i>C</i> в позапрошлую ночь; se conjicere in noctem <i>C</i> спешно отправиться под покровом ночи;\n<b>2)</b> <i>поэт.</i> ночной покой, ночной сон (oculis noctem accipere <i>V</i>);\n<b>3)</b> тьма, мрак (nocte opressa regio <i>Sen</i>); тень (veteris sub nocte cupressi <i>VF</i>); <i>перен.</i> неясность, непонятность, туманность (versus noctem habent <i>O</i>);\n<b>4)</b> тень смерти, смерть (omnes una manet n. <i>H</i>);\n<b>5)</b> слепота (perpetua sub nocte trahere senectam <i>O</i>);\n<b>6)</b> подземное царство (n. profunda <i>V</i>);\n<b>7)</b> ослепление, непонимание (n. animi <i>O</i>);\n<b>8)</b> <i>pl.</i> ночные труды: &laquo;Noctes Atticae&raquo;, &laquo;Аттические ночи&raquo;, <i>сборник филологических статей Авла Геллия</i>;\n<b>9)</b> тяжёлые обстоятельства (ingens scelerum n. <i>Lcn</i>); смута, разруха (rei publicae n. <i>C</i>). — <i>См. тж.</i> nocte <i>и</i> noctu."],
  ["nox 2", "<big>nox</big>\n\n<b>II nox</b> <i>арх. adv. LXIIT ap. Macr, Enn, LM, Pl</i>\n= noctu."],
  ["noxa", "<big>noxa</big>\n\n<b>noxa, ae</b> <i>f</i> [noceo]\n<b>1)</b> вред, ущерб: noxae (<i>dat.</i>) esse alicui <i>Sl</i> быть во вред кому-л.; sine noxa <i>Su, L</i> без вреда;\n<b>2)</b> вина, проступок, преступление: in noxa esse <i>Ter, L</i> (comprehendi <i>Cs</i>) быть виноватым; damnari noxae <i>L</i> быть осуждённым за преступление; noxam nocere <i>L</i> совершить преступление;\n<b>3)</b> наказание, кара: merere noxam <i>L</i> заслужить наказание, <i>т. е.</i> совершить преступление; eximere noxae <i>L</i> освободить от наказания; exsolvi (liberari) noxa <i>L</i> быть освобождённым от наказания."],
  ["noxale", "<big>noxale</big>\n\n<b>noxale, is</b> <i>n</i>\nиск о возмещении ущерба <i>Dig</i>."],
  ["noxalis", "<big>noxalis</big>\n\n<b>noxalis, e</b> [noxa]\nкасающийся нанесения ущерба (actio <i>Dig</i>)."],
  ["noxia", "<big>noxia</big>\n\n<b>noxia, ae</b> <i>f</i> [noxius I]\n<b>1)</b> вина, провинность, проступок (noxiae poena par esto <i>C</i>): alicui noxiae esse <i>L</i> вменяться в вину кому-л., <i>но тж.</i> причинять ущерб кому-л. <i>или</i> <i>Dig</i> быть кому-л. во вред;\n<b>2)</b> вред, ущерб (n. vini <i>PM</i>)."],
  ["noxietas", "<big>noxietas</big>\n\n<b>noxietas, atis</b> <i>f</i> <i>Tert</i>\n= noxitudo."],
  ["noxiosus", "<big>noxiosus</big>\n\n<b>noxiosus, a, um</b> [noxia]\n<b>1)</b> виновный, грешный, греховный (animus <i>Sen</i>; corpus <i>Pt</i>);\n<b>2)</b> вредный (animal <i>Sen</i>)."],
  ["noxitudo", "<big>noxitudo</big>\n\n<b>noxitudo, inis</b> <i>f</i> [noxa] <i>арх.</i>\nвина, преступление (Pelopidarum <i>Acc</i>)."],
  ["noxius 1", "<big>noxius</big>\n\n<b>I noxius, a, um</b> [noceo]\n<b>1)</b> вредный, вредоносный (animal <i>Col, Sen</i>; tela <i>O</i>);\n<b>2)</b> виновный, достойный наказания (homo <i>Sen</i>; corda <i>O</i>): n. conjurationis <i>T</i> виновный в заговоре."],
  ["noxius 2", "<big>noxius</big>\n\n<b>II noxius, i</b> <i>m</i>\nпреступник (cervicem noxio imperare praecidi <i>Sen</i>)."],
  ["Nubae", "<big>Nubae</big>\n\n<b>Nubae, arum</b> <i>m</i> <i>Sil</i>\n= Nubaei."],
  ["Nubaei", "<big>Nubaei</big>\n\n<b>Nubaei (Nubei), orum</b> <i>m</i>\nнубийцы <i>PM</i>."],
  ["nubecula", "<big>nubecula</big>\n\n<b>nubecula, ae</b> <i>f</i> [<i>demin. к</i> nubes]\n<b>1)</b> облачко <i>PM, Amm, Ap, Vlg</i>;\n<b>2)</b> пятнышко (nubeculae in facie <i>PM</i>): nubeculae oculorum <i>PM</i> чёрные точки в поле зрения (&laquo;mouches volantes&raquo;);\n<b>3)</b> мрачность, угрюмость (n. frontis <i>C</i>)."],
  ["Nubei", "<big>Nubei</big>\n\n<b>Nubei</b> <i>v. l.</i>\n= Nubaei."],
  ["nubes", "<big>nubes</big>\n\n<b>nubes, is</b> <i>f</i>\n<b>1)</b> облако, туча (atra <i>Pl, Lcr etc.</i>): n. pulveris <i>L, QC</i> туча (столб) пыли;\n<b>2)</b> множество, туча (telorum <i>L</i>); рой (locustarum <i>L</i>); масса, полчище, толпа (peditum <i>L</i>): nube facta <i>L</i> сгрудившись, плотной массой;\n<b>3)</b> дым курений (Sabaeis nubibus templa implere <i>St</i>);\n<b>4)</b> прозрачное одеяние, лёгкий покров <i>Q</i>; <i>перен.</i> покров (fraudibus nubem objicere <i>H</i>);\n<b>5)</b> скорбь, печаль (n. tristitiae <i>VM</i>); мрачность, угрюмость (supercilio nubem demere <i>H</i>);\n<b>6)</b> бедствие, несчастье, смута (rei publicae <i>C</i>); невзгода (n. belli <i>V, Just</i>);\n<b>7)</b> мутное пятно, помутнение (maculosa n. <i>PM</i>);\n<b>8)</b> помрачение: n. meri <i>VF</i> опьянение; n. frontis <i>St</i> слепота; n. soporis <i>VF</i> сонное состояние;\n<b>9)</b> пустота, ничто, призрачность (nubes et inania captare <i>H</i>)."],
  ["nubicula", "<big>nubicula</big>\n\n<b>nubicula</b> <i>Tert</i>\n= nubecula."],
  ["nubifer", "<big>nubifer</big>\n\n<b>nubifer, fera, ferum</b> [nubes + fero]\n<b>1)</b> несущий облака, нагоняющий тучи (noti <i>O</i>; ver <i>Lcn</i>);\n<b>2)</b> окутанный облаками (Appenninus <i>O</i>)."],
  ["nubifugus", "<big>nubifugus</big>\n\n<b>nubi–fugus, a, um</b> [fugo]\nразгоняющий тучи (Boreas <i>Col</i>)."],
  ["nubigena", "<big>nubigena</big>\n\n<b>nubigena, ae</b> <i>m, f</i> [nubes + gigno]\n<b>1)</b> рождённый облаками, дитя туч (amnes <i>St</i>; Centauri <i>V</i>); упавший с неба (clipeus <i>St</i>);\n<b>2)</b> <i>Col</i> = Phrixus (<i>как сын</i> Nephele, <i>т. е.</i> Тучи)."],
  ["nubilarium", "<big>nubilarium</big>\n\n<b>nubilarium, i</b> <i>n</i> [nubilum]\nсарай для хранения зерна (и защиты его от дождя) <i>Vr, Col, Pall</i>."],
  ["nubilis", "<big>nubilis</big>\n\n<b>nubilis, e</b> [nubo]\nдостигший брачного возраста, взрослый, зрелый (filia <i>C</i>; virgo <i>Nep</i>): nubiles anni <i>O</i> возраст, позволяющий вступление в брак."],
  ["nubilo", "<big>nubilo</big>\n\n<b>nubilo, —, —, are</b> [nubilum]\n<b>1)</b> быть облачным, пасмурным (n. coepit <i>Vr</i>);\n<b>2)</b> быть мутным (nubilantes carbunculi <i>PM</i>);\n<b>3)</b> затуманивать, помрачать (mentem <i>Aug</i>)."],
  ["nubilosus", "<big>nubilosus</big>\n\n<b>nubilosus, a, um</b> [nubilum]\nоблачный, пасмурный (aer <i>Sen</i>: caelum <i>Aug</i>)."],
  ["nubilum", "<big>nubilum</big>\n\n<b>nubilum, i</b> <i>n</i>\n<b>1)</b> облака, тучи (n. inducere <i>PJ</i>); пасмурная погода (soles et nubila <i>O</i>);\n<b>2)</b> угрюмость, мрачность (nubila humani animi <i>PM</i>);\n<b>3)</b> помрачение (mentis <i>Ap</i>)."],
  ["nubilus", "<big>nubilus</big>\n\n<b>nubilus, a, um</b> [nubes]\n<b>1)</b> облачный, покрытый облаками, пасмурный (caelum <i>CC, Aug</i>; annus <i>Tib</i>);\n<b>2)</b> наносящий тучи, заволакивающий тучами (ventus <i>PM</i>; auster <i>O</i>);\n<b>3)</b> тёмный, мрачный, хмурый (mens <i>Pl</i>; Styx <i>O</i>; color <i>PM</i>): vultus n. <i>O, Macr</i> угрюмое лицо;\n<b>4)</b> печальный, смутный (tempora <i>O</i>);\n<b>5)</b> неблагоприятный, недоброжелательный, недружелюбный (alicui <i>O</i>)."],
  ["nubis", "<big>nubis</big>\n\n<b>nubis, is</b> <i>f</i> <i>Pl</i>\n= nubes."],
  ["nubivagus", "<big>nubivagus</big>\n\n<b>nubi–vagus, a, um</b>\nпробегающий через облака (meatus <i>Sil</i>)."],
  ["nubo", "<big>nubo</big>\n\n<b>nubo, nupsi, nuptum, ere</b>\n<b>1)</b> (<i>редко</i>) покрывать, окутывать (aliquem <i>PV</i>; terram <i>Eccl</i>);\n<b>2)</b> закутываться в покрывало (<i>о невесте в день свадьбы</i>), <i>т. е.</i> выходить замуж (alicui <i>C, L, O</i>): n. in familiam clarissimam <i>C</i> через замужество войти в знатную семью; aliquam nuptum dare <i>Ter, Nep</i> (locare <i>Ter</i>, collocare <i>Cs</i>) выдать кого-л. замуж; uxori n. suae <i>шутл. M</i> быть женой у своей жены, <i>т. е.</i> находиться у жены под башмаком;\n<b>3)</b> быть подвязываемым к деревьям (<i>о винограде</i>) <i>Man, PM</i>."],
  ["nubs", "<big>nubs</big>\n\n<b>nubs, nubis</b> <i>f</i> <i>арх. LA, Aus</i>\n= nubes."],
  ["nubt-", "<big>nubt-</big>\n\n<b>nubt-</b> <i>Ap</i>\n(<i>v. l.</i>)= nupt-."],
  ["nucalis", "<big>nucalis</big>\n\n<b>nucalis, e</b> [nux]\nвеличиною с (<i>или</i> похожий на) орех (palmula <i>CA</i>)."],
  ["nucamentum", "<big>nucamentum</big>\n\n<b>nucamentum, i</b> <i>n</i> [nux]\nореховидный плод (<i>шишка и пр.</i>) <i>PM</i>."],
  ["Nuceria", "<big>Nuceria</big>\n\n<b>Nuceria, ae</b> <i>f</i>\nНуцерия (Нукерия), <i>город в южн. Кампании, на реке Sarnus</i> (<i>с портом Pompeii</i>) <i>C, L</i>."],
  ["Nucerinus 1", "<big>Nucerinus</big>\n\n<b>I Nucerinus, a, um</b> [Nuceria]\nнуцерийский <i>L, PM</i>."],
  ["Nucerinus 2", "<big>Nucerinus</big>\n\n<b>II Nucerinus, i</b> <i>m</i>\nжитель города Nuceria <i>L</i>."],
  ["nucetum", "<big>nucetum</big>\n\n<b>nucetum, i</b> <i>n</i> [nux]\nорешник, ореховая роща <i>St, Tert</i>."],
  ["nuceus", "<big>nuceus</big>\n\n<b>nuceus, a, um</b> [nux]\nореховый (materia <i>Cato</i>)."],
  ["nucicla", "<big>nucicla</big>\n\n<b>nucicla, ae</b> <i>f</i> [<i>demin. к</i> nux]\nорешек <i>Is</i>."],
  ["nucifragibulum", "<big>nucifragibulum</big>\n\n<b>nucifragibulum (nucifrangibulum), i</b> <i>n</i> [nux + frango]\nореховый щелкунчик, <i>шутл.</i> зуб <i>Pl</i>."],
  ["nucinus", "<big>nucinus</big>\n\n<b>nucinus, a, um</b> <i>Ambr</i>\n= nuceus."],
  ["nucipersicum", "<big>nucipersicum</big>\n\n<b>nucipersicum, i</b> <i>n</i> [nux + persicum]\nперсик, привитый к ореху <i>M</i>."],
  ["nuciprunum", "<big>nuciprunum</big>\n\n<b>nuciprunum, i</b> <i>n</i> [nux + prunum]\nслива, привитая к ореху <i>PM</i>."],
  ["nucis", "<big>nucis</big>\n\n<b>nucis</b> <i>gen. к</i>\nnux."],
  ["nucleatus", "<big>nucleatus</big>\n\n<b>nucleatus, a, um</b> [nucleus]\nкосточковый (uva <i>Scr</i>)."],
  ["nucleus", "<big>nucleus</big>\n\n<b>nucleus, &#299;</b> <i>m</i> [<i>demin. к</i> nux]\n<b>1)</b> ядро (amygdalae <i>PM</i>); qui e nuce nucleum esse (= edere) vult, frangit nucem <i>погов. Pl</i> кто хочет съесть ядро, должен разбить орех (скорлупу);\n<b>2)</b> косточка (mali Persici <i>CC</i>; cerasi <i>PM</i>);\n<b>3)</b> твёрдая сердцевина (pinguitudinis, <i>sc.</i> terrae <i>PM</i>): n. ferri <i>PM</i> сталь; n. conchae <i>PM</i> жемчужина."],
  ["nucula", "<big>nucula</big>\n\n<b>nucula, ae</b> <i>f</i> [<i>demin. к</i> nux]\nорешек <i>PM</i>."],
  ["nuculeus", "<big>nuculeus</big>\n\n<b>nuculeus, &#299;</b> <i>m</i> <i>Pl</i>\n(<i>v. l.</i>)= nucleus."],
  ["nudatio", "<big>nudatio</big>\n\n<b>nudatio, onis</b> <i>f</i> [nudo]\nобнажение <i>PM, Eccl</i>."],
  ["nude", "<big>nude</big>\n\n<b>nude</b> [nudus]\nбез прикрас, просто (tradere aliquid <i>Lact</i>)."],
  ["nudipedalia", "<big>nudipedalia</big>\n\n<b>nudipedalia, ium</b> <i>n</i> [nudipes]\n<b>1)</b> религиозное шествие босиком (<i>с молением о ниспослании дождя</i>) <i>Tert</i>;\n<b>2)</b> хождение босиком <i>Hier</i>."],
  ["nudipes", "<big>nudipes</big>\n\n<b>nudi–pes, pedis</b>\nбосой, босоногий <i>Tert</i>."],
  ["nuditas", "<big>nuditas</big>\n\n<b>nuditas, atis</b> <i>f</i> [nudus]\n<b>1)</b> обнажённость, нагота <i>Vlg, Eccl</i>;\n<b>2)</b> лишённость, недостаток <i>CTh</i>."],
  ["nudius", "<big>nudius</big>\n\n<b>nudius</b> <i>adv.</i> [<i>из</i> nunc + dies]\n<i>с порядковыми числ.</i> сегодня (нынче)... день: n. tertius <i>C</i> сегодня третий день, <i>т. е.</i> третьего дня, позавчера; n. tertius decimus fuit <i>C</i> 12 дней тому назад."],
  ["nudiustertianus", "<big>nudiustertianus</big>\n\n<b>nudius–tertianus, a, um</b> (<i>тж. раздельно</i>)\nпозавчерашний <i>Fronto, Eccl</i>."],
  ["nudo", "<big>nudo</big>\n\n<b>nudo, avi, atum, are</b> [nudus]\n<b>1)</b> обнажать (corpus <i>O, L</i>; caput crine <i>Pt</i>; gladium <i>L</i>); раздевать (aliquem <i>C</i>); вскрывать (viscera <i>V</i>); скалить (dentes <i>Lcr</i>);\n<b>2)</b> обмолачивать (messes <i>O</i>);\n<b>3)</b> опустошать (agros populando <i>L</i>); разграблять, расхищать (omnia <i>C</i>); грабить, обирать (provinciam <i>C</i>); разорять, доводить до нищеты (alea nudavit aliquem <i>H</i>);\n<b>4)</b> расчищать (spatium <i>Col</i>);\n<b>5)</b> вынимать (telum vagina <i>Nep</i>);\n<b>6)</b> обнаруживать, выявлять, открывать (ingenium alicujus <i>H</i>; consilia <i>QC</i>; voluntates hominum <i>L</i>);\n<b>7)</b> <i>воен.</i> оставлять незащищённым, оголять (litora, castra <i>Cs</i>); очищать (murum defensoribus <i>Cs</i>);\n<b>8)</b> лишать, отнимать (aliquem aliqua re <i>C, Su</i>); смещать с должности, лишать звания (aliquem <i>Eutr</i>)."],
  ["nudulus", "<big>nudulus</big>\n\n<b>nudulus, a, um</b> <i>adj.</i> [<i>demin. к</i> nudus]\nголенький <i>Spart</i>."],
  ["nudus", "<big>nudus</big>\n\n<b>nudus, a, um</b>\n<b>1)</b> (<i>тж.</i> n. membra <i>V</i> — <i>acc. gr.</i>) голый, нагой: nudo vestimenta detrahere <i>погов. Pl</i> снимать одежду с голого (<i>о невозможном</i>) || непокрытый (caput <i>Sl</i>); обнажённый (ensis <i>V</i>); босой: nudis pedibus <i>H, Sl, Pt</i> босиком || неосёдланный (equus <i>Sil</i>); легко одетый, полуодетый (inermis nudusque <i>L</i>); очищенный от сучьев (stipes <i>QC</i>); лишённый листьев, облетевший, голый (nemus <i>Sen</i>); лишённый растительности (colles <i>L</i>);\n<b>2)</b> непогребённый (ossa <i>Cld</i>);\n<b>3)</b> невооружённый (dextra <i>L</i>; certamen <i>St</i>);\n<b>4)</b> незанятый, пустой (domus, subsellia <i>C</i>);\n<b>5)</b> беззащитный (eques <i>C</i>); незащищённый, неприкрытый (nudo corpore pugnare <i>Cs</i>); беспомощный (senecta <i>O</i>);\n<b>6)</b> лишённый (aliqua re, ab aliqua re <i>или</i> alicujus rei <i>C etc.</i>): nudum remigio latus <i>H</i> борт без вёсел; mors famae nuda <i>Sil</i> бесславная смерть || лишённый украшений (capilli <i>O</i>); простой, написанный простым языком (commentarii Caesaris <i>C</i>); неприкрашенный, сущий (veritas <i>H</i>);\n<b>7)</b> бедный, неимущий (homo <i>Pl</i>);\n<b>8)</b> непристойный (verba <i>PJ</i>);\n<b>9)</b> чистый, подлинный, истинный (simplicitas <i>O</i>; virtus <i>Pt</i>); сплошной, один лишь (ira <i>O</i>): hoc nudum relinquitur <i>C</i> остаётся лишь (решить) следующий вопрос."],
  ["nugacissume", "<big>nugacissume</big>\n\n<b>nugacissume</b> [nugax]\nв высшей степени забавно, уморительно <i>Pl</i> (<i>v. l.</i>)."],
  ["nugacitas", "<big>nugacitas</big>\n\n<b>nugacitas, atis</b> <i>f</i> [nugax]\nвздорность, пустословие <i>Aug</i>."],
  ["nugae", "<big>nugae</big>\n\n<b>nugae, arum</b> <i>f</i>\n<b>1)</b> пустяки, вздор, шутки <i>Ctl, C, H</i>: aufer nugas! <i>Pl</i> довольно шуток!; nugas agere <i>Pl</i> говорить вздор, дурачиться; nugas (agis)! <i>Pl</i> (ты говоришь) вздор!, ерунда!; nugas postulare <i>Pl</i> требовать невозможного;\n<b>2)</b> ветрогон, пустомеля, болтун, несерьёзный человек (nugas amicos habere <i>C</i>)."],
  ["nugalis", "<big>nugalis</big>\n\n<b>nugalis, e</b> [nugae]\nвздорный, пустой (theoremata <i>AG</i>)."],
  ["nugamentum", "<big>nugamentum</big>\n\n<b>nugamentum, i</b> <i>n</i> [nugor]\nвздор, пустяки <i>Ap</i>."],
  ["nugas", "<big>nugas</big>\n\n<b>nugas</b> <i>n indecl.</i> <i>Vr</i>\n= nugax."],
  ["nugator", "<big>nugator</big>\n\n<b>nugator, oris</b> <i>f</i> [nugor]\nболтун, пустомеля <i>Pl, C etc.</i>"],
  ["nugatorie", "<big>nugatorie</big>\n\n<b>nugatorie</b> [nugatorius]\nвздорно, нелепо (respondere <i>rhH</i>)."],
  ["nugatorius", "<big>nugatorius</big>\n\n<b>nugatorius, a, um</b> [nugator]\n<b>1)</b> вздорный, нелепый, пустой (accusatio <i>C</i>; artes <i>Pl</i>);\n<b>2)</b> негодный (boves <i>Vr</i>)."],
  ["nugax", "<big>nugax</big>\n\n<b>nugax, acis</b> <i>adj.</i> [nugor]\nвздорный, пустой <i>C, Vlg, Eccl</i>."],
  ["nugigerulus", "<big>nugigerulus</big>\n\n<b>nugi–gerulus, i</b> <i>m</i>\nпустомеля, болтун, ветрогон <i>Pl, Is</i>."],
  ["Nugipolyloquides", "<big>Nugipolyloquides</big>\n\n<b>Nugipolyloquides, ae</b> <i>m</i> [nugae+ <i>греч.</i> poly много + loquor]\n&laquo;Невероятный пустомеля&raquo;, <i>шутливое имя</i> <i>Pl</i>."],
  ["nugivendus", "<big>nugivendus</big>\n\n<b>nugivendus, i</b> <i>m</i> [nugae + vendo]\nторговец женскими украшениями, безделушками <i>Pl</i> (<i>v. l.</i>)."],
  ["nugo", "<big>nugo</big>\n\n<b>nugo, onis</b> <i>m</i> <i>Ap</i>\n= nugator."],
  ["nugor", "<big>nugor</big>\n\n<b>nugor, atus sum, avi</b> <i>depon.</i> [nugae]\n<b>1)</b> говорить пустяки, молоть вздор (Democritus nugatur <i>C</i>); болтать пустое, празднословить (cum aliquo <i>H</i>);\n<b>2)</b> лгать, обманывать: non mihi n. potes <i>Pl</i> меня не проведёшь."],
  ["nullatenus", "<big>nullatenus</big>\n\n<b>nulla–tenus</b> <i>adv.</i>\n(<i>sc.</i> parte) никоим образом, никак <i>CJ, Eccl</i>."],
  ["nulli", "<big>nulli</big>\n\n<b>nulli</b>\n<i>Ter, Cato, C</i> = nullius (<i>gen. sg. к</i> nullus I)."],
  ["nullibi", "<big>nullibi</big>\n\n<b>nullibi</b> <i>adv.</i> [nullus + ibi]\nнигде <i>Vtr</i>."],
  ["nullificamen", "<big>nullificamen</big>\n\n<b>nullificamen, inis</b> <i>n</i> <i>и</i> <b>nullificatio, onis</b> <i>f</i> [nullifico]\nпренебрежение, презрение <i>Tert</i>."],
  ["nullifico", "<big>nullifico</big>\n\n<b>nulli–fico, —, —, are</b> [nullus + facio]\nнизко ставить, презирать (aliquem <i>Eccl</i>)."],
  ["nullius", "<big>nullius</big>\n\n<b>nullius</b> <i>gen. sg. к</i>\nnullus I."],
  ["nullo", "<big>nullo</big>\n\n<b>nullo</b>\n<i>rhH, Cs</i> = nulli (<i>dat. sg. к</i> nullus)."],
  ["nullum", "<big>nullum</big>\n\n<b>nullum, i</b> <i>n</i>\nничто <i>H, Fl</i>."],
  ["nullus 1", "<big>nullus</big>\n\n<b>I nullus, a, um</b> (<i>gen.</i> ius; <i>dat.</i> i, <i>редко</i> o) [ne + ullus]\n<b>1)</b> никакой, ни один (dies, res <i>Lcr</i>; nullum jus jurandum, nulla religio <i>L</i>): nullo adversante <i>T</i> без чьего бы то ни было сопротивления; nullos (deos) omnino putavit <i>C</i> (Диагор) полагал, что никаких богов вообще нет; n. unus <i>C</i> решительно никто; n. non <i>L, Q etc.</i> каждый: nulla non hora <i>Cld</i> всечасно, постоянно; non n. <i>C etc.</i> иной, некоторый; n. dum <i>L</i> ещё ни один; nullo periculo <i>Cs</i> без всякой опасности; ii nulli <i>C</i> никто из них; nullo (<i>sc.</i> loco) <i>L</i> нигде; non est spes mihi nulla <i>O</i> у меня (ещё) есть некоторая надежда;\n<b>2)</b> <i>как усиленное</i> non: Philotimus n. venit <i>C</i> Филотим вовсе не пришёл; n. metus <i>C</i> полное бесстрашие;\n<b>3)</b> несуществующий: de mortuis loquor, qui nulli sunt <i>C</i> я говорю о мёртвых, которых уже нет; illo tempore Hector n. erat <i>O</i> тогда Гектор (ещё) не существовал; n. sum <i>Pl, Ter, L</i> я пропал;\n<b>4)</b> ничтожный, пустой, незначительный, не имеющий значения, ничего не значащий: res nulla <i>Ter</i> пустяк, безделица; sine his studiis vita nulla est <i>C</i> без этих занятий жизнь пуста (лишена содержания); aliquid nullum putare <i>C</i> презирать (не ставить ни во что) что-л."],
  ["nullus 2", "<big>nullus</big>\n\n<b>II nullus, i</b> <i>m</i>\nникто <i>Pl, C etc.</i>"],
  ["nullusdum", "<big>nullusdum</big>\n\n<b>nullus–dum, nulladum, nullumdum</b> <i>adj.</i>\nникакой ещё, никто (ничто) пока <i>L etc.</i> num:\n<b>1)</b> <i>без вопроса</i> (ещё) теперь: etiam n. <i>C</i> ещё теперь;\n<b>2)</b> <i>с вопросом</i> ли: n. moror? <i>Pl</i> остаться (ли) мне?;\n<b>3)</b> разве, неужели: n. barbarorum Romulus rex fuit? <i>C</i> разве Ромул был царём варваров?; <i>с усилением</i> numnam, numne <i>и</i> numquid да разве, неужели же (numquid dubitas? <i>Ter</i>) <i>или</i> ли: rogo, numquid scis, ubi ego habitem? <i>Pt</i> послушай, ты знаешь ли, где я живу?"],
  ["Numa Pompilius", "<big>Numa Pompilius</big>\n\n<b>Numa Pompilius</b> <i>m</i>\nНума Помпилий, <i>сабинянин из города Cures, второй царь Рима</i> (<i>715-672 гг. до н. э.</i>) <i>L, C, O</i>."],
  ["numa-", "<big>numa-</big>\n\n<b>numa-</b> <i>v. l.</i>\n= numma-."],
  ["Numana", "<big>Numana</big>\n\n<b>Numana, ae</b> <i>f</i>\nНумана, <i>город в Пиценской области, на юго-вост. от Анконы</i> <i>Mela, PM, Sil</i>."],
  ["Numantia", "<big>Numantia</big>\n\n<b>Numantia, ae</b> <i>f</i>\nНуманция, <i>город в Hispania Tarraconensis, в верхнем течении реки Durius; после долгой осады разрушен Сципионом Африканским Младшим в 133 г. до н. э.</i> <i>C, L, Fl etc.</i>"],
  ["Numantinus", "<big>Numantinus</big>\n\n<b>Numantinus, a, um</b> [Numantia]\nнумантийский (bellum <i>C, Sl</i>; foedus <i>Q</i>)."],
  ["numcubi", "<big>numcubi</big>\n\n<b>numcubi</b> [<i>из</i> num + alicubi]\nразве где-л. (когда-л.)? <i>Ter, Vr</i>."],
  ["numella", "<big>numella</big>\n\n<b>numella, ae</b> <i>f</i>\nжелезный ошейник, шейная колодка <i>Pl, Col</i>."],
  ["numen", "<big>numen</big>\n\n<b>numen, inis</b> <i>n</i> [nuo]\n<b>1)</b> кивок, знак согласия (n. interdictumque deorum <i>C</i>);\n<b>2)</b> мановение, воля, повеление (n. dominae <i>O</i>; numini alicujus parere <i>C</i>): suum nutum numenque annuere <i>L</i> дать своё согласие, изъявить свою волю; numine sine meo <i>V</i> против моей воли; numine vestro <i>V</i> с вашего согласия;\n<b>3)</b> воля <i>или</i> могущество богов (nihil sine numine dei geritur <i>Nep</i>);\n<b>4)</b> божество, бог (simulacra numinum <i>PJ</i>): numina vagantia <i>Ap</i> блуждающие божества, <i>т. е.</i> планеты;\n<b>5)</b> изображение <i>или</i> статуя бога (numina divum <i>V</i>);\n<b>6)</b> величие (Augusti <i>T</i>): tua numina <i>O</i> твоя божественность (<i>обращение к Августу</i>);\n<b>7)</b> знак божественного могущества (magnum et memorabile n. <i>V</i>);\n<b>8)</b> духи дорогих покойников, маны <i>O, Q etc.</i>;\n<b>9)</b> <i>преим. pl.</i> изречение оракула <i>V</i>."],
  ["numerabilis", "<big>numerabilis</big>\n\n<b>numerabilis, e</b> [numero I]\nмогущий быть сосчитанным, (легко) исчислимый (calculus <i>O</i>): populus n., utpote parvus <i>H</i> народ, который легко можно сосчитать за его немногочисленностью."],
  ["numeralis", "<big>numeralis</big>\n\n<b>numeralis, e</b> <i>грам.</i>\nчислительный (nomen)."],
  ["numerarius", "<big>numerarius</big>\n\n<b>numerarius, i</b> <i>m</i>\nсчетовод <i>CTh, CJ, Sid, Amm</i>."],
  ["numeratio", "<big>numeratio</big>\n\n<b>numeratio, onis</b> <i>f</i> [numero I]\n<b>1)</b> выплата, уплата (numerationem exigere <i>Col</i>): de domo n. <i>Sen</i> уплата из собственных средств;\n<b>2)</b> исчисление, счёт <i>Boet</i>."],
  ["numerator", "<big>numerator</big>\n\n<b>numerator, oris</b> <i>m</i> <i>Aug</i>\n= numerarius."],
  ["numeratum", "<big>numeratum</big>\n\n<b>numeratum, i</b> <i>n</i> [numero I]\nналичные деньги (numerato solvere <i>C</i>): in numerato aliquid habere <i>Q, Sen</i> иметь что-л. (постоянно) в наличии (наготове)."],
  ["numeratus", "<big>numeratus</big>\n\n<b>numeratus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> numero I;\n<b>2.</b> <i>adj.</i> наличный (pecunia <i>C</i>): in numerato <i>PM</i> наличными деньгами."],
  ["Numerius", "<big>Numerius</big>\n\n<b>Numerius, i</b> <i>m</i>\nНумерий, <i>римск. praenomen</i> <i>C, L</i>."],
  ["numero 1", "<big>numero</big>\n\n<b>I numero, avi, atum, are</b> [numerus]\n<b>1)</b> считать, сосчитывать (pecus <i>V</i>): n. per digitos <i>O</i> считать по пальцам; n. senatum <i>C</i> считать присутствующих в сенате членов (<i>для установления кворума</i>);\n<b>2)</b> рассматривать, считать, относить: n. aliquem in primis <i>C</i> причислять кого-л. к первым (ставить на одно из первых мест); n. aliquid beneficii loco <i>C</i> считать что-л. благодеянием; n. aliquem post aliquem <i>T</i> ставить кого-л. ниже кого-л.; n. aliquid nullo loco <i>C</i> ни во что не ставить что-л.; quod habet, non numerat <i>погов. Man</i> (человек) не ценит того, что у него есть;\n<b>3)</b> насчитывать, иметь (multos n. amicos <i>O</i>): avi numerantur avorum <i>V</i> вереница предков уходит вглубь веков;\n<b>4)</b> перечислять (n. auctores <i>Ph</i>);\n<b>5)</b> отсчитывать, уплачивать (alicui pecuniam <i>C</i>; militibus stipendium <i>C</i>; vicesimam <i>Pt</i>);\n<b>6)</b> перебирать (pectine chordas <i>J</i>)."],
  ["numero 2", "<big>numero</big>\n\n<b>II numero</b> <i>adv.</i> [numerus]\n<b>1)</b> как раз, только что: n. mihi in mentem fuit <i>Pl</i> только что мне пришло в голову;\n<b>2)</b> быстро, скоро, тотчас же (aliquid facere <i>Vr</i>);\n<b>3)</b> слишком скоро, слишком рано (mori <i>Pl</i>)."],
  ["numerose", "<big>numerose</big>\n\n<b>numerose</b> [numerosus]\n<b>1)</b> в большом количестве (versare sententias <i>Q</i>);\n<b>2)</b> на много частей (dividi <i>PM</i>);\n<b>3)</b> в такт, ритмично, мерно (sonare <i>C</i>)."],
  ["numerositas", "<big>numerositas</big>\n\n<b>numerositas, atis</b> <i>f</i>\n<b>1)</b> большое количество, множество <i>Macr, Sid, CTh, Tert</i>;\n<b>2)</b> ритмичность, размеренность <i>Aug</i>."],
  ["numerosus", "<big>numerosus</big>\n\n<b>numerosus, a, um</b> [numerus]\n<b>1)</b> многочисленный (amici <i>Eutr</i>; folia <i>PM</i>); многолюдный (civitas <i>T</i>); массовый (caedes <i>Sil</i>);\n<b>2)</b> разнообразный, с разнообразным содержанием (opus <i>Q</i>; pictura <i>PM</i>); обширный, состоящий из многих частей (hortus <i>Col</i>);\n<b>3)</b> размеренный, мерный, ритмичный (oratio <i>C</i>): numerosos ponere gressus <i>O</i> ритмично танцевать;\n<b>4)</b> богатый ритмами <i>или</i> многообразный (Horatius <i>O</i>);\n<b>5)</b> плодовитый (pictor <i>PM</i>)."],
  ["numerus", "<big>numerus</big>\n\n<b>numerus, i</b> <i>m</i>\n<b>1)</b> составная часть, член, элемент: omnes numeros habere <i>C</i> быть полным (законченным); numeris suis carere (deesse) <i>O etc.</i> быть неполным; truncus suis numeris <i>O</i> увечный; omnibus numeris <i>C, PJ, Q</i> (omni numero <i>C</i>) во всех отношениях; omnium numer(or)um esse <i>Pt</i> быть безукоризненным;\n<b>2)</b> соразмерность, размеренность, ритмичность, ритм, такт: in (ad) numerum <i>C</i> в такт (<i>ср.</i> 7); extra numerum <i>C</i> не в такт; verborum ad numerum conclusio <i>C</i> ритмичное завершение периода;\n<b>3)</b> созвучие, благозвучие, гармония: etiam in verbis solutis inest n. <i>C</i> гармония свойственна и несвязанной речи (прозе); procedere in numerum <i>C</i> совершаться правильно (надлежащим образом); numeris vincire <i>C</i> делать стройным, подчинять гармонии;\n<b>4)</b> такт, тактичность: nihil extra numeros facere <i>H</i> не допускать ничего бестактного;\n<b>5)</b> музыка, мелодия, напев (numeros intendere nervis <i>V</i>);\n<b>6)</b> (<i>тж.</i> numeri poetici <i>Col</i>) стих, стихотворный размер, стопа: numeri graves <i>O</i> героические стихи (гексаметры); numeri impares <i>O</i> элегические двустишия;\n<b>7)</b> число (<i>мат. и грам.</i>), количество (n. innumeralis <i>Lcr</i>; n. militum <i>C, Nep, Dig</i>; n. frumenti <i>C</i>): numerum inire <i>C, L, QC, Sen</i> производить подсчёт, подсчитывать, считать; numero aliquid comprehendere <i>V</i> определять численность чего-л., подсчитывать что-л.; numerum deferre (exsequi) <i>L</i> указывать число; suum numerum habere <i>C</i> быть полным (укомплектованным), иметь надлежащее число; numero числом, всего (classis mille numero navium <i>C</i>); ad numerum <i>C, Cs, L etc.</i> в необходимом количестве, <i>но тж.</i> поровну (ad numerum aliquid dividere <i>Pt</i> — <i>ср. тж.</i> 2); extra numerum esse <i>Pl</i> быть излишним <i>или</i> не приниматься в расчёт; in numero esse <i>C, Cs etc.</i> принадлежать к (числу), <i>но тж.</i> <i>Dig</i> состоять на действительной военной службе; non fuisse in numero (<i>sc.</i> hominum) <i>Lcr</i> никогда не существовать среди людей (не родиться на свет); (in) numero amicorum ducere <i>Cs</i> (habere <i>C, Cs</i>, referre <i>Sl</i>, reponere <i>C</i>) считать другом; (in) numero sapientium haberi <i>Nep</i> быть причисляемым к мудрецам;\n<b>8)</b> толпа, множество (n. hominum <i>C etc.</i>); <i>презр.</i> ничтожества: nos n. sumus <i>H</i> мы (римская молодёжь) — ничтожные люди;\n<b>9)</b> <i>преим. pl.</i> арифметика, <i>тж.</i> астрономия (ab aliquo numeros et caelestia accipere <i>C</i>);\n<b>10)</b> <i>pl.</i> игральные кости (numeros manu jactare <i>O</i>);\n<b>11)</b> порядок (aliquid in numerum digerere <i>V</i>);\n<b>12)</b> промежуток, интервал (pares numeri <i>V</i>);\n<b>13)</b> воинская часть, войсковое подразделение (sparsi per provinciam numeri <i>T</i>); (<i>тж.</i> militaris n. <i>Amm</i>) когорта (numeri legionum <i>T</i>);\n<b>14)</b> <i>pl.</i> список, перечень (nomen in numeros referre <i>PJ</i>);\n<b>15)</b> положение, значение, достоинство, авторитет: (in) aliquo numero esse <i>C, Cs</i> (obtinere aliquem numerum <i>C</i>) иметь какое-л. значение (иметь влияние, пользоваться уважением); (in) numero obsidum <i>Cs</i> в числе <i>или</i> в качестве заложников; alicui parentis numero esse <i>C</i> быть кому-л. вместо отца;\n<b>16)</b> <i>pl.</i> должность, функция, роль (alicui numeros suos eripere <i>O</i>)."],
  ["Numicius 1", "<big>Numicius</big>\n\n<b>I Numicius, a, um</b>\nНумиций, <i>римск. nomen</i> <i>C, L, T</i>."],
  ["Numicius 2", "<big>Numicius</big>\n\n<b>II Numic(i)us, i</b> <i>m</i>\nНумиций, <i>река в Латии, впадающая в Тирренское море у Ардеи</i> <i>L, V, Tib etc.</i>"],
  ["Numida", "<big>Numida</big>\n\n<b>Numida, ae</b>\n<b>1.</b> <i>m</i> нумидиец <i>Sl, V etc.</i>;\n<b>2.</b> <i>adj.</i> нумидийский (leo <i>O</i>; eques <i>L</i>)."],
  ["Numidia", "<big>Numidia</big>\n\n<b>Numidia, ae</b> <i>f</i>\nНумидия, <i>страна в сев. Африке между Мавританией и областью Карфагена</i> (<i>прибл. и отчасти совпадает с территорией нын. Алжира</i>) <i>Sl, Mela, PM, Sen, T</i>."],
  ["Numidianus", "<big>Numidianus</big>\n\n<b>Numidianus</b> <i>PM</i>\n= Numidicus."],
  ["Numidica", "<big>Numidica</big>\n\n<b>Numidica, ae</b> <i>f</i> (<i>sc.</i> avis <i>или</i> gallina)\nцесарка (N. guttata <i>M</i>)."],
  ["Numidicus", "<big>Numidicus</big>\n\n<b>Numidicus, a, um</b>\n<b>1)</b> нумидийский <i>L, PM, Sen</i>;\n<b>2)</b> <i>agnomen Кв. Цецилия Метелла, победителя Югурты</i> <i>C</i>."],
  ["Numisianus", "<big>Numisianus</big>\n\n<b>Numisianus, a, um</b> [Numisius]\nнумизиев <i>C, Col</i>."],
  ["Numisius", "<big>Numisius</big>\n\n<b>Numisius, a, um</b>\nНумизий, <i>римск. nomen</i> <i>C</i>."],
  ["numisma", "<big>numisma</big>\n\n<b>numisma (nomisma), atis</b> <i>n</i> (<i>греч.</i>; <i>лат.</i> nummus)\n<b>1)</b> монета <i>H, M</i>;\n<b>2)</b> марка <i>или</i> жетон (<i>который вручался при входе в театр членам всаднического сословия и взамен которого они получали бесплатное угощение</i> <i>M</i>);\n<b>3)</b> изображение на монете <i>Eccl</i>."],
  ["Numistro", "<big>Numistro</big>\n\n<b>Numistro, onis</b> <i>f</i>\nНумистрон, <i>город в сев. Лукании</i> <i>L</i>."],
  ["Numitor", "<big>Numitor</big>\n\n<b>Numitor, oris</b> <i>m</i>\nНумитор, <i>царь Альбы Лонги, брат Амулия, отец Реи Сильвии, дед Ромула и Рема</i> <i>L, V, O, J</i>."],
  ["nummarius", "<big>nummarius</big>\n\n<b>nummarius, a, um</b> [nummus]\n<b>1)</b> денежный (theca, difficultas <i>C</i>); монетный (res <i>C</i>): lex nummaria <i>C</i> закон против фальшивомонетчиков;\n<b>2)</b> продажный, подкупленный (judex <i>C</i>)."],
  ["nummatus", "<big>nummatus</big>\n\n<b>nummatus, a, um</b> [nummus]\nденежный, с (большими) деньгами, богатый (bene n. <i>H, C, Amm</i>)."],
  ["Nummosexpalponides", "<big>Nummosexpalponides</big>\n\n<b>Nummosexpalponides, ae</b> <i>m</i> [nummus + expalpo]\n&laquo;Выманиватель денег&raquo;, <i>шутливое имя</i> <i>Pl</i>."],
  ["nummosus", "<big>nummosus</big>\n\n<b>nummosus, a, um</b> <i>Nigidius ap. AG</i>\n= nummatus."],
  ["nummularia", "<big>nummularia</big>\n\n<b>nummularia, ae</b> <i>f</i> (<i>sc.</i> ars <i>или</i> res)\nмонетное дело <i>Is</i>."],
  ["nummulariolus", "<big>nummulariolus</big>\n\n<b>nummulariolus, i</b> <i>m</i> [<i>demin. к</i> nummularius II]\nмелкий меняла <i>Sen</i>."],
  ["nummularius 1", "<big>nummularius</big>\n\n<b>I nummularius, a, um</b>\nменяльный (mensa <i>Dig</i>)."],
  ["nummularius 2", "<big>nummularius</big>\n\n<b>II nummularius, i</b> <i>m</i> [nummus]\n<b>1)</b> денежный маклер, меняла <i>Su, M, Pt, Dig etc.</i>;\n<b>2)</b> монетный контролёр (<i>чиновник монетного двора, проверявший качество поступавшего в чеканку серебра</i>) <i>Pt</i>."],
  ["nummulus", "<big>nummulus</big>\n\n<b>nummulus, i</b> <i>m</i> [<i>demin. к</i> nummus]\nнебольшая монета <i>Ap</i>: nummulorum aliquid <i>C</i> небольшое количество денег."],
  ["nummus", "<big>nummus</big>\n\n<b>nummus, i</b> (<i>gen. pl.</i> orum <i>и</i> um) <i>m</i>\n<b>1)</b> монета (aureus <i>C</i>);\n<b>2)</b> <i>pl.</i> деньги, наличные (omnia in nummis habere <i>Pt</i>; nummis vendere aliquid <i>Pt</i>): in suis nummis esse (versari) <i>C</i> обладать наличным капиталом; nummorum nummos habere <i>Pt</i> иметь страшно много денег;\n<b>3)</b> денежный курс: n. jactatur <i>C</i> курс колеблется;\n<b>4)</b> сестерций (quinque milia nummum <i>C</i>);\n<b>5)</b> (<i>тж.</i> n. sestertius <i>C, Sen</i>) мелочь, грош, копейка, безделица: nummo <i>H</i> за бесценок, <i>но тж.</i> <i>PJ</i> за деньги; ad nummum convenit <i>C</i> сошлось до копейки, точно;\n<b>6)</b> <i>греч. монета</i> драхма <i>или</i> дидрахма (две драхмы) (emere nummo <i>Pl</i>)."],
  ["numnam", "<big>numnam</big>\n\n<b>numnam, numne</b> — <i>см.</i>\nnum 3."],
  ["numquam", "<big>numquam</big>\n\n<b>numquam</b> [ne + unquam]\n<b>1)</b> никогда (n. adhuc <i>C</i>): n. non <i>C</i> всегда; non n. <i>C</i> иногда, подчас;\n<b>2)</b> ни за что, никак (n. hodie effugies <i>V</i>): hodie n. ad vesperam vivam <i>Pl</i> мне ни за что сегодня не дожить до вечера."],
  ["Numquameripides", "<big>Numquameripides</big>\n\n<b>Numquameripides, ae</b> <i>m</i> [eripio] <i>Pl</i>\n(<i>v. l.</i>) = Numquampostreddonides."],
  ["Numquampostreddonides", "<big>Numquampostreddonides</big>\n\n<b>Numquampostreddonides, ae</b> <i>m</i> [numquam + post + reddo]\n&laquo;Не отдающий назад&raquo;, <i>шутливое имя</i> <i>Pl</i> (<i>v. l.</i>)."],
  ["numquando", "<big>numquando</big>\n\n<b>num–quando</b> (<i>тж. раздельно</i>)\nразве когда-л. <i>C</i>."],
  ["numquis", "<big>numquis</big>\n\n<b>num–quis, num–quid</b> — <i>см.</i>\nnum 3."],
  ["numu-", "<big>numu-</big>\n\n<b>numu-</b> <i>v. l.</i>\n= nummu-."],
  ["nunc", "<big>nunc</big>\n\n<b>nunc</b> <i>adv.</i> [num + ce]\n<b>1)</b> теперь, в настоящее время, ныне (tunc inops erat, n. dives <i>Sen</i>).: n. quum maxime <i>L</i> именно (как раз, в особенности) теперь; etiam n. <i>Cs, Sl</i> ещё и теперь (всё ещё); n. olim <i>V</i> теперь или после, рано или поздно; ut n. est <i>C</i> при данных обстоятельствах; n. ipsum <i>C</i> именно теперь; n... n... (n... modo, n... jam) <i>O, QC, L etc.</i> то... то...;\n<b>2)</b> (<i>при логическом переходе</i>) так вот, поэтому, ввиду этого, же (abi n. <i>C</i>): n. autem (n. vero) <i>C etc.</i> теперь же, а вот теперь; si haec non ad homines, verum ad bestias conqueri vellem, tamen tanta rerum atrocitate commoverentur. Nunc vero, quum loquar apud senatores populi Romani... <i>C</i> если бы я жаловался на это не людям, а зверям, то и они были бы потрясены такой жестокостью деяний (Верреса). Но теперь, когда я говорю перед римскими сенаторами...;\n<b>3)</b> (<i>редко в знач.</i> <i>adj.</i>) теперешний, нынешний (n. homines <i>Pl</i>; n. mores <i>Pl</i>)."],
  ["nunciam", "<big>nunciam</big>\n\n<b>nunc–iam</b> <i>adv.</i>\nкак раз теперь, именно сейчас <i>Pl, Ter</i>."],
  ["nuncin'?", "<big>nuncin'?</big>\n\n<b>nuncin'?</b> <i>adv.</i> [nunc + ce + ne]\nразве теперь?, теперь, что-ли? <i>Ter</i>."],
  ["nuncine", "<big>nuncine</big>\n\n<b>nuncine</b> <i>adv.</i> <i>v. l.</i>\n= nuncin'."],
  ["nuncubi", "<big>nuncubi</big>\n\n<b>nuncubi</b> <i>adv.</i>\n= numcubi."],
  ["nuncupatim", "<big>nuncupatim</big>\n\n<b>nuncupatim</b>\nпо имени, поимённо <i>Sid</i>."],
  ["nuncupatio", "<big>nuncupatio</big>\n\n<b>nuncupatio, onis</b> <i>f</i> [nuncupo]\n<b>1)</b> именование, называние (imperatoribus Augusta n. amabilis et optata <i>Amm</i>); наименование, название: aliquid nuncupatione alicujus rei complecti <i>Ap</i> прилагать к чему-л. название чего-л.;\n<b>2)</b> торжественное обещание, обет (votorum <i>Su</i>); произнесение (sollemnium verborum <i>VM</i>);\n<b>3)</b> назначение наследника <i>Su, Dig</i>;\n<b>4)</b> <i>о книге</i> посвящение <i>PM</i>."],
  ["nuncupativus", "<big>nuncupativus</big>\n\n<b>nuncupativus, a, um</b>\nименуемый, (так) называемый <i>Ambr</i>."],
  ["nuncupator", "<big>nuncupator</big>\n\n<b>nuncupator, oris</b> <i>m</i> [nuncupo]\nименующий, дающий имя: Pythagoras, primus philosophiae n. <i>Ap</i> Пифагор, который первый дал философии её имя."],
  ["nuncupo", "<big>nuncupo</big>\n\n<b>nuncupo, avi, atum, are</b> [nomen + capio]\n<b>1)</b> именовать, называть, нарекать (mensem e nomine alicujus n. <i>Su</i>); поимённо перечислять (maria <i>PM</i>);\n<b>2)</b> торжественно произносить, объявлять (vota <i>C</i>; adoptionem <i>T</i>); провозглашать (aliquem Caesarem <i>AV</i>);\n<b>3)</b> назначать наследником (<i>устно при свидетелях</i>) (aliquem inter heredes <i>Su</i>): testamenta sua n. <i>PM</i> оглашать свои завещания;\n<b>4)</b> посвящать (aliquid alicui n. dedicareque <i>Ap</i>)."],
  ["nuncusque", "<big>nuncusque</big>\n\n<b>nunc–usque</b> (<i>тж. раздельно</i>)\nдо настоящего времени, до сих Пор <i>Amm</i>."],
  ["nundina 1", "<big>nundina</big>\n\n<b>I nundina, ae</b> <i>f</i> <i>Sid</i>\n= nundinae."],
  ["Nundina 2", "<big>Nundina</big>\n\n<b>II Nundina, ae</b> <i>f</i>\nНундина, <i>богиня очистительных обрядов, совершавшихся над мальчиками на девятый день их рождения</i> (<i>над девочками — на восьмой</i>) <i>Macr</i>."],
  ["nundinae", "<big>nundinae</big>\n\n<b>nundinae, arum</b> <i>f</i> [nundinus]\n(<i>sc.</i> feriae)\n<b>1)</b> нундины, рыночные дни (<i>нерабочие для крестьян последние дни римск. восьмидневной недели; они назывались девятыми, так как считался и предыдущий нерабочий день; от одного рыночного дня до следующего было семь рабочих дней</i>) <i>C, Macr, Dig</i>: tertiis nundinis <i>AG</i> на третий рыночный (<i>т. е.</i> на семнадцатый) день; nundinas obire <i>L</i> приходить на рынок (<i>для собирания голосов избирателей</i>);\n<b>2)</b> рынок (Capuae n. rusticorum sunt <i>C</i>);\n<b>3)</b> торг, торговля, продажа (alicujus rei <i>C</i>)."],
  ["nundinalis", "<big>nundinalis</big>\n\n<b>nundinalis, e</b> [nundinae]\nотносящийся к нундинам, бывающий раз в девять дней: n. cocus <i>Pl</i> плохой повар (<i>предпол. потому, что во время нундин находили работу и самые плохие повара</i>)."],
  ["nundinarius", "<big>nundinarius</big>\n\n<b>nundinarius, a, um</b> [nundinae]\nрыночный, торговый (forum, oppidum <i>PM</i>)."],
  ["nundinaticius", "<big>nundinaticius</big>\n\n<b>nundinaticius, a, um</b>\nвыставляемый (как бы) на продажу <i>или</i> напоказ <i>Tert</i>."],
  ["nundinatio", "<big>nundinatio</big>\n\n<b>nundinatio, onis</b> <i>f</i> [nundinor]\n<b>1)</b> торг, торговля <i>C</i>; спекуляция (juris ac fortunarum <i>C</i>);\n<b>2)</b> рыночная цена (rerum venalium <i>CTh</i>);\n<b>3)</b> подкуп <i>CJ</i>;\n<b>4)</b> растрата <i>или</i> хищение <i>CTh</i>."],
  ["nundinator", "<big>nundinator</big>\n\n<b>nundinator, oris</b> <i>m</i> [nundinor]\nторговец, продавец <i>Q, Ambr</i>."],
  ["nundinium", "<big>nundinium</big>\n\n<b>nundinium, i</b> <i>n</i>\nИундиний, <i>период консульских полномочий в позднюю императорскую эпоху</i> (<i>от 5 до 6 месяцев</i>) <i>Lampr, Vop</i>."],
  ["nundino", "<big>nundino</big>\n\n<b>nundino, avi, atum, are</b> <i>Tert etc.</i>\n= nundinor."],
  ["nundinor", "<big>nundinor</big>\n\n<b>nundinor, atus sum, ari</b> <i>depon.</i> [nundinae]\n<b>1)</b> вести торговлю, торговать <i>L, Su</i>;\n<b>2)</b> продавать, <i>тж.</i> спекулировать (aliquid pretio <i>Ap</i>);\n<b>3)</b> торговаться (in aliqua re <i>Su</i>); выторговывать, добиваться подкупом (jus ab aliquo <i>C</i>; senatorium nomen <i>C</i>);\n<b>4)</b> сходиться во множестве, стекаться массами (ad focum angues n. solent <i>C</i>)."],
  ["nundinum", "<big>nundinum</big>\n\n<b>nundinum, i</b> <i>n</i> [nundinus]\n(<i>sc.</i> tempus) рыночный день: inter n. <i>LM, Vr</i> восьмидневная неделя (<i>между двумя nundinae</i>); trinum n. (<i>т. е.</i> trinarum nundinarum tempus) <i>C, Q</i> промежуток, содержащий три рыночных дня, <i>т. е.</i> срок от 17 до 24 дней (comitia in trinum n. indicere <i>L</i>)."],
  ["nundinus", "<big>nundinus</big>\n\n<b>nundinus, a, um</b> [novem + dies]\nотносящийся к девятидневке, девятидневный (<i>см.</i> nundinae <i>и</i> nundinum)."],
  ["nunquam", "<big>nunquam</big>\n\n<b>nunquam</b> <i>adv.</i> <i>v. l.</i>\n= numquam."],
  ["nuntia", "<big>nuntia</big>\n\n<b>nuntia, ae</b> <i>f</i>\nвестница (historia n. vetustatis <i>C</i>): n. Jovis <i>Poeta ap. C</i> = aquila."],
  ["nuntiatio", "<big>nuntiatio</big>\n\n<b>nuntiatio, onis</b> <i>f</i> [nuntio]\n<b>1)</b> <i>культ.</i> сообщение, извещение (<i>авгура о результатах наблюдения знамений</i>) <i>C</i>;\n<b>2)</b> <i>юр.</i> уведомление, заявление (<i>преим. по хозяйственно-финансовым вопросам</i>) <i>Dig, CJ</i>."],
  ["nuntiator", "<big>nuntiator</big>\n\n<b>nuntiator, oris</b> <i>m</i> [nuntio]\n<b>1)</b> (провоз)вестник <i>Eccl</i>;\n<b>2)</b> доноситель, осведомитель <i>Dig, CJ</i>."],
  ["nuntio", "<big>nuntio</big>\n\n<b>nuntio, avi, atum, are</b> [nuntius]\nсообщать, объявлять, извещать, уведомлять, докладывать, передавать (alicui aliquid <i>или</i> de aliqua re <i>C</i>): n. horas <i>M</i> называть время (час); salutem n. alicui alicujus verbis <i>C</i> передавать привет (кланяться) кому-л. от кого-л.; qua re nuntiata <i>C</i> по получении этого известия; nuntiato <i>L</i> когда <i>или так как</i> поступило это сообщение; adesse nuntiatur <i>Cs</i> передают (докладывают), что он здесь; alicui bellum n. <i>L</i> объявить кому-л. войну; aliquid in urbem n. <i>L</i> сообщить (доставить Известие) о чём-л. в город; bene nuntias <i>Ter</i> ты приносишь хорошую весть (новость)."],
  ["nuntium", "<big>nuntium</big>\n\n<b>nuntium, i</b> <i>n</i>\n<b>1)</b> весть, известие (nova nuntia <i>Ctl</i>);\n<b>2)</b> вестник, признак (simulacra nuntia formae <i>Lcr</i>)."],
  ["nuntius 1", "<big>nuntius</big>\n\n<b>I nuntius, a, um</b> [nuntium]\nсообщающий, являющийся признаком, выражением (verba animi nuntia <i>O</i>); предвещающий (prodigia malorum nuntia <i>T</i>); вещий, пророческий (fibra, <i>sc.</i> extorum <i>Tib</i>)."],
  ["nuntius 2", "<big>nuntius</big>\n\n<b>II nuntius, i</b> <i>m</i>\n<b>1)</b> вестник, гонец (nuntios mittere ad aliquem <i>Cs</i>): Jovis et deorum n. <i>H</i> = Mercurius;\n<b>2)</b> весть, известие, сообщение (nuntium alicujus rei afferre <i>C</i>): hic nostri n. esto <i>V</i> иди и передай от меня эту весть; nuntium alicui mittere <i>Dig</i> (remittere <i>Pl, C</i>) послать кому-л. извещение о расторжении брака (дать развод) <i>или</i> о том, что предстоявший брак не состоится, <i>перен.</i> послать отказ, отказаться, порвать (nuntium remittere alicui rei <i>C</i>)."],
  ["nuo", "<big>nuo</big>\n\n<b>nuo, —, —, ere</b> [<i>одного корня с</i> numen]\nкивать, делать знак (<i>тк. в приставочных глаголах</i>: abnuo, adnuo <i>и т. д.</i>)."],
  ["nuper", "<big>nuper</big>\n\n<b>nuper</b> <i>adv.</i> [nuperus]\n(<i>superl.</i> nuperrime)\n<b>1)</b> недавно (qui n. Romae fuit <i>C</i>; n. compositum poema <i>H</i>);\n<b>2)</b> прежде, некогда (n., id est paucis ante saeculis <i>C</i>)."],
  ["nuperus", "<big>nuperus</big>\n\n<b>nuperus, a, um</b> [novus + paro]\nновый, вновь (недавно) приобретенный (n. et novicius <i>Pl</i>; experimenta nuperrima <i>CTh</i>)."],
  ["nupsi", "<big>nupsi</big>\n\n<b>nupsi</b> <i>pf. к</i>\nnubo."],
  ["nupta", "<big>nupta</big>\n\n<b>nupta, ae</b> <i>f</i> [nubo]\n<b>1)</b> жена, супруга (n. Jovis <i>O</i>); замужняя женщина (n. est an vidua? <i>Pl</i>);\n<b>2)</b> невеста (n. pudica <i>L, O</i>)."],
  ["nuptalicius", "<big>nuptalicius</big>\n\n<b>nuptalicius, a, um</b> [nupta]\nсвадебный, брачный (munus <i>Dig</i>)."],
  ["nuptiae", "<big>nuptiae</big>\n\n<b>nuptiae, arum</b> <i>f</i> [nubo]\n<b>1)</b> свадьба, бракосочетание, брак, женитьба <i>или</i> выход замуж: n. Liviae <i>Su</i> бракосочетание с Ливией; nuptiarum expers <i>H</i> неженатый, незамужняя; nuptias conciliare <i>Nep</i> устраивать свадьбу;\n<b>2)</b> сожительство, интимная (внебрачная) связь (nuptias facere cum aliqua <i>Pl</i>)."],
  ["nuptialis", "<big>nuptialis</big>\n\n<b>nuptialis, e</b>\nсвадебный, брачный (donum <i>C</i>; fax <i>H</i>; cena <i>Pl, L</i>; pactio <i>L</i>)."],
  ["nuptialiter", "<big>nuptialiter</big>\n\n<b>nuptialiter</b>\nпо-свадебному <i>Aug</i>."],
  ["nuptiator", "<big>nuptiator</big>\n\n<b>nuptiator, oris</b> <i>m</i>\nсправляющий свадьбу <i>Hier</i>."],
  ["nupto", "<big>nupto</big>\n\n<b>nupto, —, —, are</b> [<i>frequ. к</i> nubo]\nчасто вступать в брак <i>Tert</i>."],
  ["nuptula", "<big>nuptula</big>\n\n<b>nuptula, ae</b> <i>f</i> [<i>demin. к</i> nupta]\nмолодая жена <i>или</i> невеста <i>Vr</i>."],
  ["nuptuo", "<big>nuptuo</big>\n\n<b>nuptuo, —, —, ire</b> <i>M</i>\n= nupturio."],
  ["nupturio", "<big>nupturio</big>\n\n<b>nupturio, —, —, ire</b> [<i>desiderat. к</i> nubo]\nжелать вступить в брак <i>Ap</i>."],
  ["nuptus 1", "<big>nuptus</big>\n\n<b>I nuptus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> nubo;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> вступившая в брак (filia <i>C</i>; mulier <i>L</i>);\n<b>2)</b> женатый (viri nupti <i>Vr</i>)."],
  ["nuptus 2", "<big>nuptus</big>\n\n<b>II nuptus, us</b> <i>m</i> [nubo]\n<b>1)</b> закутывание в покрывало (<i>ритуал бракосочетания</i>) <i>Vr</i>;\n<b>2)</b> брак, свадьба: dies nuptus <i>AG</i> день свадьбы."],
  ["Nursia", "<big>Nursia</big>\n\n<b>Nursia, ae</b> <i>f</i>\nНурсия, <i>высокогорный город в области сабинян</i> (<i>в верхнем течении реки</i> Nar) <i>V, Sil, Su</i>."],
  ["Nursini", "<big>Nursini</big>\n\n<b>Nursini, orum</b> <i>m</i>\nжители города Nursia <i>PM</i>."],
  ["Nurtia", "<big>Nurtia</big>\n\n<b>Nurtia</b> <i>v. l.</i>\n= Nortia."],
  ["nuru", "<big>nuru</big>\n\n<b>nuru</b> <i>T dat. sg. к</i>\nnurus."],
  ["nurus", "<big>nurus</big>\n\n<b>nurus, us</b> <i>f</i>\n<b>1)</b> невестка, сноха <i>Ter, C etc.</i>;\n<b>2)</b> <i>поэт.</i> молодая женщина <i>O, M, Lcn</i>;\n<b>3)</b> невеста сына <i>Dig</i>;\n<b>4)</b> жена внука <i>Dig</i>."],
  ["nusquam", "<big>nusquam</big>\n\n<b>nusquam</b> [ne + usquam]\n<b>1)</b> нигде (n. est qui ubique est <i>Sen</i>): n. esse <i>H, Prp, Su</i> не быть, отсутствовать <i>или</i> не быть в живых; n. loci <i>AG</i> <i>и</i> n. gentium <i>Ter</i> нигде в мире; n. non <i>PM</i> всюду, везде;\n<b>2)</b> никуда (n. abire <i>Pl</i>);\n<b>3)</b> ниоткуда (n. nisi a Lacedaemoniis auxilium petere <i>Nep</i>);\n<b>4)</b> ни к чему, ни для чего: n. alio quam ad aliquid <i>L</i> только для чего-л.;\n<b>5)</b> никак, никоим образом (n. abero <i>V</i>): n. equidem quicquam deliqui <i>Pl</i> тут, право же, нет никакой моей вины."],
  ["nutabilis", "<big>nutabilis</big>\n\n<b>n&#363;t&#257;bilis, e</b> [nuto]\nшатающийся, шаткий (suggestus <i>Ap</i>)."],
  ["nutabundus", "<big>nutabundus</big>\n\n<b>n&#363;t&#257;bundus, a, um</b> [nuto]\nшатающийся, колеблющийся (miles <i>Ap</i>)."],
  ["nutamen", "<big>nutamen</big>\n\n<b>n&#363;t&#257;men, inis</b> <i>n</i> [nuto]\nшатание, качание (vibrare tremulo nutamine <i>Sil</i>)."],
  ["nutatio", "<big>nutatio</big>\n\n<b>n&#363;t&#257;tio, &#333;nis</b> <i>f</i> [nuto]\n<b>1)</b> колебание (in utramque partem <i>Q</i>); шатание, сотрясение (montium <i>Sen</i>);\n<b>2)</b> качание, кивание, наклонение (capitis <i>PM</i>);\n<b>3)</b> потрясение, смута (rei publicae <i>PJ</i>)."],
  ["nuto", "<big>nuto</big>\n\n<b>n&#363;to, &#257;v&#299;, &#257;tum, &#257;re</b> [<i>intens. к</i> nuo]\n<b>1)</b> качаться, раскачиваться (platanus nutans <i>Ctl</i>); шататься (n. vulnere <i>J</i>); ходить вверх и вниз, подниматься и опускаться (nutans machinamentum <i>T</i>);\n<b>2)</b> качать, мотать (caput utroque <i>Sil</i>); кивать (capite <i>Pl</i>); делать головой знак: nutat, ne loquar <i>Pl</i> он кивает мне, чтобы я молчал;\n<b>3)</b> колебаться, быть в нерешительности (animus nutat <i>O</i>; n. in aliqua re <i>C</i>); дрогнуть (nutans acies <i>T</i>); колебаться в верности (Galliae nutantes <i>T</i>): discrimine n. <i>T</i> подвергаться (находиться в) опасности;\n<b>4)</b> сотрясаться (quassae nutant turres <i>Lcn</i>; moenia nutantia <i>Sil</i>);\n<b>5)</b> мерцать (sidus nutat <i>Calp</i>)."],
  ["nutribam", "<big>nutribam</big>\n\n<b>nutribam</b> <i>V impf. к</i>\nnutrio."],
  ["nutribilis", "<big>nutribilis</big>\n\n<b>nutribilis, e</b> [nutrio]\nпитательный (cibus <i>CA</i>)."],
  ["nutricatio", "<big>nutricatio</big>\n\n<b>nutricatio, onis</b> <i>f</i> [nutrico]\n<b>1)</b> кормление грудью (puerorum nutricationes <i>Ap</i>);\n<b>2)</b> взращивание (herbarum <i>Vr</i>)."],
  ["nutricatus", "<big>nutricatus</big>\n\n<b>nutricatus, us</b> <i>m</i> <i>Pl, Vr</i>\n= nutricatio."],
  ["nutricium", "<big>nutricium</big>\n\n<b>nutricium, i</b> <i>n</i> [nutricius]\n<b>1)</b> кормление, питание <i>или</i> уход (maternum <i>Sen</i>; infantum <i>Man</i>);\n<b>2)</b> плата кормилице <i>Dig</i>."],
  ["nutricius 1", "<big>nutricius</big>\n\n<b>I nutricius, a, um</b> [nutrix]\nпитающий, вскармливающий (sinus <i>Col</i>)."],
  ["nutricius 2", "<big>nutricius</big>\n\n<b>II nutricius, i</b> <i>m</i>\nвоспитатель, наставник (pueri <i>Cs, Man, Vr etc.</i>)."],
  ["nutrico", "<big>nutrico</big>\n\n<b>nutrico, —, atum, are</b> [nutrix]\nкормить, выкармливать (mulum <i>Vr</i>); питать, вскармливать, воспитывать (pueros <i>Pl</i>): viperam sub ala n. <i>погов. Pt</i> вскормить змею на своей груди."],
  ["nutricor", "<big>nutricor</big>\n\n<b>nutricor, atus sum, ari</b> <i>depon.</i>\n<b>1)</b> вскармливать, откармливать (porcos <i>Vr</i>);\n<b>2)</b> вскармливать, лелеять (mundus omnia nutricatur <i>C</i>)."],
  ["nutricula", "<big>nutricula</big>\n\n<b>nutricula, ae</b> <i>f</i> [<i>demin. к</i> nutrix]\n<b>1)</b> кормилица <i>H, Su</i>;\n<b>2)</b> родина, источник, рассадник (Gallia n. seditiosorum <i>C</i>)."],
  ["nutrimen", "<big>nutrimen</big>\n\n<b>nutrimen, inis</b> <i>n</i> [nutrio]\nпища, пропитание <i>O</i>."],
  ["nutrimentum", "<big>nutrimentum</big>\n\n<b>nutrimentum, i</b> <i>n</i> [nutrio] (<i>чаще</i> <i>pl.</i>)\n<b>1)</b> питание, <i>перен.</i> пища (nutrimento alicujus rei esse <i>Ap</i>): nutrimenta ignis <i>Just, VM</i> горючий материал, топливо;\n<b>2)</b> <i>pl.</i> воспитание, раннее детство (consuetudo nutrimentorum <i>Su</i>); уход, ухаживание, лелеяние (educata Isocratis nutrimentis eloquentia <i>C</i>): favoris nutrimentis prosequi <i>VM</i> укреплять благожелательным отношением."],
  ["nutrio", "<big>nutrio</big>\n\n<b>nutrio, ivi (ii), itum, ire</b>\n<b>1)</b> кормить, питать (pullos aliqua re <i>J</i>; aliquem lacte <i>O</i>); вскармливать грудью (pueros <i>O</i>);\n<b>2)</b> взращивать, выращивать (silvam <i>H</i>); ухаживать (capillum <i>T</i>); лелеять (simultates <i>T</i>);\n<b>3)</b> воспитывать (nutriri in armis <i>Su</i>); культивировать (studia litterarum <i>AV</i>);\n<b>4)</b> поддерживать (ignem foliis <i>O</i>);\n<b>5)</b> охранять, оберегать (pacem <i>T</i>);\n<b>6)</b> лечить (ulcus <i>CC</i>); исправлять (damnum naturae <i>L</i>); укреплять (aegrum cibis <i>CC</i>)."],
  ["nutrior", "<big>nutrior</big>\n\n<b>nutrior, —, iri</b> <i>depon.</i> <i>V</i>\n= nutrio."],
  ["nutritius", "<big>nutritius</big>\n\n<b>nutritius</b> <i>v. l.</i>\n= nutricius."],
  ["nutritor", "<big>nutritor</big>\n\n<b>nutritor, oris</b> <i>m</i> [nutrio]\n<b>1)</b> воспитатель (Alexandri Severi <i>Lampr</i>);\n<b>2)</b> животновод: n. equorum <i>St</i> коневод."],
  ["nutritorius", "<big>nutritorius</big>\n\n<b>nutritorius, a, um</b>\n<b>1)</b> питающий, <i>т. е.</i> живительный (gratia <i>Aug</i>);\n<b>2)</b> питающий раннее детство, <i>т. е.</i> младенческий (cunae <i>Aug</i>)."],
  ["nutritura", "<big>nutritura</big>\n\n<b>nutritura, ae</b> <i>f</i>\nкормление, вскармливание <i>Eccl</i>."],
  ["nutrix", "<big>nutrix</big>\n\n<b>nutrix, icis</b> <i>f</i>\n<b>1)</b> кормилица (aliquid cum lacte nutricis sugere <i>C</i>; tellus n. <i>H</i>): nutrices <i>Ctl</i> груди; gallina n. <i>Col</i> наседка; curarum maxima n. nox <i>O</i> ночь больше всего усиливает наши заботы; virgo perpetui n. ignis <i>Eccl</i> = virgo Vestalis;\n<b>2)</b> <i>перен.</i> колыбель, родина <i>или</i> рассадник, питомник (Athenae n. tantorum philosophorum <i>Aug</i>)."],
  ["nutus", "<big>nutus</big>\n\n<b>nutus, us</b> <i>m</i> [nuto]\n<b>1)</b> наклон, кивок (capitis <i>Q</i>); мановение, знак: nutu clementi <i>Ap</i> лёгким (чуть заметным) движением; ad nutum alicujus <i>L</i> по чьему-л. знаку; nutum suum annuere alicui <i>L</i> кивнуть кому-л. в знак согласия;\n<b>2)</b> воля, повеление (nutu deorum <i>C</i>): alterius sub nutu <i>Lcr</i> по чужой прихоти;\n<b>3)</b> тяготение (suopte nutu ferri <i>C</i>)."],
  ["nux", "<big>nux</big>\n\n<b>nux, nucis</b> (<i>gen. pl.</i> um) <i>f</i>\n<b>1)</b> орех: n. cassa (quassa) <i>Pl, H</i> <i>или</i> vitiosa <i>Pl</i> пустой орех, <i>перен.</i> безделица, пустяк; nuces relinquere <i>погов. Pers</i> оставить детские игры, выйти из детского возраста;\n<b>2)</b> плод с твёрдой скорлупой: castanea n. <i>V</i> каштан; amara n. <i>CC</i> горький миндаль;\n<b>3)</b> ореховое дерево <i>V, L, J</i>;\n<b>4)</b> миндальное дерево <i>V</i>."],
  ["nyctalops", "<big>nyctalops</big>\n\n<b>nyctalops, opis</b> <i>adj.</i> (<i>греч.</i>)\nстрадающий никталопией, <i>т. е.</i> плохо видящий днём, но лучше в сумерки <i>PM, Dig</i>."],
  ["Nycteis", "<big>Nycteis</big>\n\n<b>Nycteis, idis</b> <i>и</i> <b>idos</b> <i>f</i>\nдочь Никтея, <i>т. е.</i> Antiope <i>Prp, O, St</i>."],
  ["Nyctelius", "<big>Nyctelius</big>\n\n<b>Nyctelius, a, um</b> (<i>греч.</i> &laquo;ночной&raquo;)\n<i>один из эпитетов Вакха</i> <i>O, SenT</i>."],
  ["Nycteus", "<big>Nycteus</big>\n\n<b>Nycte&#865;us, e&#299;</b> <i>и</i> <b>eos</b> <i>m</i>\nНиктей, <i>царь Беотии, отец Антиопы</i> <i>Prp</i>."],
  ["nycticorax", "<big>nycticorax</big>\n\n<b>nycticorax, acis</b> <i>m</i> (<i>греч.</i>)\nночной ворон, <i>т. е.</i> (<i>предпол.</i>) баклан <i>Vlg, Eccl</i>."],
  ["Nyctimene", "<big>Nyctimene</big>\n\n<b>Nyctimene, es</b> <i>f</i>\nНиктимена, <i>дочь лесбосского царя Эпопея, превращенная в сову</i> <i>O</i>."],
  ["nyctostrategus", "<big>nyctostrategus</big>\n\n<b>nyctostrategus, i</b> <i>m</i> (<i>греч.</i>; <i>лат.</i> praefectus vigilum)\nначальник ночной стражи <i>Dig</i>."],
  ["nymf-", "<big>nymf-</big>\n\n<b>nymf-</b> <i>v. l.</i>\n= nymph-."],
  ["nympha 1", "<big>nympha</big>\n\n<b>I nympha, ae</b> <i>f</i> (<i>греч.</i>; <i>лат.</i> nupta)\n<b>1)</b> невеста <i>O</i>; молодая женщина <i>O</i>;\n<b>2)</b> куколка (насекомого) <i>PM</i>."],
  ["nympha 2", "<big>nympha</big>\n\n<b>II nympha, ae</b> <i>или</i> <b>nymphe, es</b> <i>f</i>\n<b>1)</b> нимфа, <i>женское второстепенное божество, олицетворявшее, в зависимости от места, где оно обитало, различные силы природы</i>: n. Peneis <i>O</i> = Daphne; n. Maenalis <i>O</i> = Carmenta; n. vocalis <i>O</i> = Echo; nymphae Libethrides <i>V</i> = Musae; nymphae Avernales <i>O</i> нимфы подземного мира;\n<b>2)</b> <i>поэт.</i> озёрная вода <i>M, Prp</i>."],
  ["nymphaea 1", "<big>nymphaea</big>\n\n<b>I nymphaea, ae</b> <i>f</i> (<i>греч.</i>)\nводяная лилия, кувшинка <i>PM, Ap</i>."],
  ["Nymphaea 2", "<big>Nymphaea</big>\n\n<b>II Nymphaea, ae</b> <i>f</i>\nНимфея:\n<b>1)</b> <i>остров близ Самоса</i> <i>PM</i>;\n<b>2)</b> <i>прежнее название о-ва Кос</i> <i>PM</i>."],
  ["nymphaeum 1", "<big>nymphaeum</big>\n\n<b>I nymphaeum, i</b> <i>n</i>\nпосвященный нимфам источник <i>PM, CTh</i>."],
  ["Nymphaeum 2", "<big>Nymphaeum</big>\n\n<b>II Nymphaeum, i</b> <i>n</i>\nНимфей:\n<b>1)</b> <i>гора и город в Иллирике близ Аполлонии</i> <i>Cs</i>;\n<b>2)</b> <i>портовый город и мыс в Иллирике</i> <i>Cs, L, Lcn</i>."],
  ["nymphe", "<big>nymphe</big>\n\n<b>nymphe</b> <i>v. l.</i>\n= nympha II."],
  ["nympheum", "<big>nympheum</big>\n\n<b>nympheum</b> <i>v. l.</i>\n= nymphaeum."],
  ["nymphon", "<big>nymphon</big>\n\n<b>nymphon, onis</b> <i>m</i>\nбрачный покой <i>Tert</i>."],
  ["Nysa", "<big>Nysa</big>\n\n<b>Nysa, ae</b> <i>f</i>\nНиса:\n<b>1)</b> <i>город в сев. Карий</i> <i>PM</i>;\n<b>2)</b> <i>город и горная цепь, где Вакх был воспитан нимфами</i> (<i>в Индии, Эфиопии, Фракии или другой стране</i>) <i>QC, Mela</i>;\n<b>3)</b> <i>женское имя</i> <i>C, PM</i>."],
  ["Nysaeus", "<big>Nysaeus</big>\n\n<b>N&#563;saeus, a, um</b> [Nysa 2]\nнисский, <i>поэт.</i> вакхов, вакхический (thyrsus <i>SenT</i>; chori <i>Prp</i>)."],
  ["Nyseis", "<big>Nyseis</big>\n\n<b>Nyseis, idis</b> <i>и</i> <b>Nysias, adis</b> <i>O f к</i>\nNysaeus."],
  ["Nyseus", "<big>Nyseus</big>\n\n<b>N&#563;se&#865;us, e&#299; (eos)</b> <i>и</i> <b>N&#563;sius, i</b> <i>m</i> [Nysa 2]\nнисский, <i>эпитет Вакха</i> <i>O</i>."],
  ["Nysigena", "<big>Nysigena</big>\n\n<b>Nysigena, ae</b> <i>m, f</i> [Nysa + geno = gigno]\nрождённый в Нисе (Sileni <i>Ctl</i>)."],
  ["Nyssa", "<big>Nyssa</big>\n\n<b>Nyssa, ae</b> <i>f</i> <i>v. l.</i>\n= Nysa."]
]