export default [
  ["O", "<big>O</big>\n\n<b>O, o</b>\n<i>пятнадцатая буква латинского алфавита; в сокращениях</i>: O. = optimus, ossa, omnis: J. O. M. = Jovi Optimo Maximo; O. I. B. Q. = ossa illius bene quiescant; O. N. F. = omnium nomine faciunda; O. V. = optimo viro."],
  ["Oarion", "<big>Oarion</big>\n\n<b>Oarion, onis</b> <i>m</i> (<i>греч.</i>) <i>Ctl</i>\n= Orion."],
  ["Oaxes", "<big>Oaxes</big>\n\n<b>Oaxes, is</b> <i>m</i>\nОакс, <i>река на о-ве Крите</i> <i>V</i>."],
  ["ob", "<big>ob</big>\n\n<b>ob</b> (<i>как приставка, ассимилирует</i> b <i>перед</i> p — opprimo, f — offendo, c — occupo <i>и</i> g — ogganio) <i>praep. cum acc.</i>\n<b>1)</b> к (ob Romam legiones ducere <i>Enn</i>; ora obvertere ob os alicujus <i>Enn</i>);\n<b>2)</b> перед (ob oculos <i>Pl, C</i>); против (ob Italiam <i>L</i>);\n<b>3)</b> вследствие, из-за (invalidus ob morbos <i>Sl</i>): ob metum <i>T</i> из страха || ради, для (ob rem publicam mortem obire <i>T</i>): ob rem в интересах дела, с успехом (facere aliquid ob rem <i>Sl</i>); ob hanc rem, ob eam (illam) rem, ob eam (hanc) causam, quam ob causam (rem) <i>C, Cs</i> вследствие этого, по этой причине; non ob aliud <i>QC</i> ни по какой другой причине (именно поэтому);\n<b>4)</b> в обмен на, за (ob decem minas <i>Ter</i>; ob merita sua <i>Sl</i>)."],
  ["obaemulor", "<big>obaemulor</big>\n\n<b>ob–aemulor, —, ari</b>\nраздражать, сердить (aliquem <i>Tert</i>)."],
  ["obaerarius", "<big>obaerarius</big>\n\n<b>ob–aerarius, i</b> <i>m</i> [aes]\nдолжник, обязанный отработать свой долг <i>Vr</i>."],
  ["obaeratus 1", "<big>obaeratus</big>\n\n<b>I ob–aeratus, a, um</b> [aes]\nобременённый долгами <i>Vr, Su, T etc.</i>"],
  ["obaeratus 2", "<big>obaeratus</big>\n\n<b>II obaeratus, i</b> <i>m</i>\nдолжник <i>C, Cs, L</i>."],
  ["obagito", "<big>obagito</big>\n\n<b>obagito</b> <i>v. l.</i>\n= obigito."],
  ["obambulatio", "<big>obambulatio</big>\n\n<b>obambulatio, onis</b> <i>f</i> [obambulo]\nразгуливание, хождение взад и вперёд (frequentia et o. hominum <i>rhH</i>)."],
  ["obambulo", "<big>obambulo</big>\n\n<b>ob–ambulo, avi, atum, are</b>\nпрохаживаться, расхаживать, прогуливаться (in herbis <i>O</i>; Aetnam <i>O</i>; ante vallum <i>L</i>; muris <i>L dat.</i>): lupus gregibus nocturnus obambulat <i>V</i> волк ночью рыщет вокруг стад."],
  ["obardesco", "<big>obardesco</big>\n\n<b>ob–ardesco, arsi, —, ere</b>\nзагораться, воссиять впереди, <i>перен.</i> ослеплять (clipei torva luce <i>St</i>)."],
  ["obaresco", "<big>obaresco</big>\n\n<b>ob–aresco, arui, —, ere</b>\nзасыхать <i>Lact</i> (<i>v. l.</i>)."],
  ["obarmo", "<big>obarmo</big>\n\n<b>ob–armo, avi, atum, are</b>\nвооружать (dextras securi <i>H</i>; manus impias contra aliquem <i>Ap</i>): o. oculos ad vigilias <i>Ap</i> бороться со сном."],
  ["obaro", "<big>obaro</big>\n\n<b>ob–aro, avi, —, are</b>\nзапахать кругом, вспахать (herbidum terrenum <i>L</i>)."],
  ["obarsi", "<big>obarsi</big>\n\n<b>obarsi</b> <i>pf. к</i>\nobardesco."],
  ["obater", "<big>obater</big>\n\n<b>obater, tra, trum</b> <i>v. l.</i>\n= obatratus."],
  ["obatratus", "<big>obatratus</big>\n\n<b>obatratus, a, um</b> [ater]\nпочерневший, потемневший <i>PM</i>."],
  ["obaudiens", "<big>obaudiens</big>\n\n<b>ob–audiens, entis</b>\n<b>1.</b> <i>part. praes. к</i> obaudio;\n<b>2.</b> <i>adj.</i> повинующийся, послушный <i>Ambr</i>."],
  ["obaudientia", "<big>obaudientia</big>\n\n<b>ob–audientia, ae</b> <i>f</i>\nповиновение <i>Tert</i>."],
  ["obaudio", "<big>obaudio</big>\n\n<b>ob–audio, ii, —, ire</b>\nслушаться, повиноваться (alicui <i>Ap</i>)."],
  ["obauditus", "<big>obauditus</big>\n\n<b>ob–auditus, us</b> <i>m</i>\nслушание, внимание <i>Aug</i>."],
  ["obauratus", "<big>obauratus</big>\n\n<b>ob–auratus, a, um</b> [aurum]\nпозолоченный <i>Ap</i>."],
  ["obba 1", "<big>obba</big>\n\n<b>I obba, ae</b> <i>f</i>\n<b>1)</b> чаша (<i>большая глиняная братина с широким дном для приготовления винной смеси</i>) <i>Vr, Pers</i>;\n<b>2)</b> кубок (<i>с широким дном и узким горлышком</i>) <i>Tert</i>."],
  ["Obba 2", "<big>Obba</big>\n\n<b>II Obba, ae</b> <i>f</i>\nОбба, <i>пунический город близ Карфагена</i> <i>L</i>."],
  ["obbatus", "<big>obbatus</big>\n\n<b>obbatus, a, um</b> [obba]\nимеющий форму obba (cassides obbatae <i>Ap</i>). — <i>См. тж.</i> obba."],
  ["obblatero", "<big>obblatero</big>\n\n<b>ob–blatero, —, —, are</b>\nболтать, бормотать, лепетать (afannas <i>Ap</i> — <i>v. l.</i>)."],
  ["obbrutesco", "<big>obbrutesco</big>\n\n<b>ob–brutesco, tui, —, ere</b> [brutus]\nцепенеть, обмирать <i>Lcr</i>."],
  ["obc-", "<big>obc-</big>\n\n<b>ob–c-</b> <i>v. l.</i>\n= oc–c-."],
  ["obdensatio", "<big>obdensatio</big>\n\n<b>ob–densatio, onis</b> <i>f</i>\nуплотнение (cutis <i>CA</i>)."],
  ["obdo", "<big>obdo</big>\n\n<b>ob–do, didi, ditum, ere</b>\n<b>1)</b> выставлять, подставлять (latus apertum malo <i>H</i>);\n<b>2)</b> класть <i>или</i> ставить впереди, загромождать: obditis navibus fauces obserare <i>Fl</i> преградить кораблями (вход в) пролив;\n<b>3)</b> запирать (fores <i>O, Ter</i>; domum seris et catenis <i>Ap</i>): pessulum ostio o. <i>Ter</i> запирать ворота на засов; ceram auribus o. <i>Sen</i> затыкать уши воском;\n<b>4)</b> закутывать (amiculis obditus <i>Ap</i>); заплетать (capilli in mutuos nexus obditi <i>Ap</i>)."],
  ["obdormio", "<big>obdormio</big>\n\n<b>ob–dormio, —, —, ire</b> <i>CC, PM, Pt</i>\n= obdormisco."],
  ["obdormisco", "<big>obdormisco</big>\n\n<b>ob–dormisco, ivi (ii), —, ere</b>\nзасыпать (заснуть) (in mediis laboribus <i>C</i>; post cibum <i>Su</i>)."],
  ["obdormitio", "<big>obdormitio</big>\n\n<b>ob–dormitio, onis</b> <i>f</i>\nзасыпание, <i>перен.</i> оцепенение (duritia cordis o. est <i>Aug</i>)."],
  ["obdormito", "<big>obdormito</big>\n\n<b>obdormito, —, —, are</b> [<i>frequ. к</i> obdormio]\nчасто засыпать <i>Eccl</i>."],
  ["obduco", "<big>obduco</big>\n\n<b>ob–duco, duxi, ductum, ere</b>\n<b>1)</b> приводить, подводить (aliquem alicui <i>Pl</i>; exercitum ad oppidum <i>Pl</i>);\n<b>2)</b> прибавлять, добавлять (posterum diem <i>C</i>);\n<b>3)</b> проводить (fossam castris <i>Cs</i>);\n<b>4)</b> задвигать (seram <i>Prp</i>);\n<b>5)</b> покрывать (palus obducit pascua junco <i>V</i>); обтягивать, <i>pass.</i> обрастать (trunci obducuntur cortice <i>C</i>): obducta cicatrix <i>C</i> зарубцевавшаяся рана; obducta nocte <i>Nep</i> под покровом ночи; obductus dolor <i>V</i> (luctus <i>O</i>) затаённое горе; o. callum dolori <i>C</i> притупить боль; o. tenebras alicui rei <i>C</i> затемнять что-л.;\n<b>6)</b> закрывать, запирать (fores <i>SenT</i>); смежать (mors oculos coepit o. <i>Pt</i>);\n<b>7)</b> засорять (stomachum <i>CA</i>);\n<b>8)</b> морщить, хмурить (vultus <i>Sen</i>; frontem <i>H</i>);\n<b>9)</b> втягивать, вбирать, глотать, пить (venenum <i>C</i>; mulsi pultarium <i>Pt</i>)."],
  ["obductio", "<big>obductio</big>\n\n<b>obductio, onis</b> <i>f</i> [obduco]\nпокрывание, закрывание, закутывание (capitis <i>C, Amm</i>)."],
  ["obducto", "<big>obducto</big>\n\n<b>obducto, —, —, are</b> [<i>intens. к</i> obduco]\nприводить (aliquem in aedes <i>Pt</i>)."],
  ["obductus", "<big>obductus</big>\n\n<b>obductus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> obduco;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> затуманившийся, мрачный (frons <i>H</i>; vultus <i>O</i>); тёмный (specus <i>Pt</i>); закрытый (oculi albugine obducti <i>Ap</i>);\n<b>2)</b> зарубцевавшийся, заживший (cicatrix <i>C</i>); притупившийся (luctus o. annis <i>O</i>); тайный, скрытый (dolor <i>V</i>)."],
  ["obdulcesco", "<big>obdulcesco</big>\n\n<b>ob–dulcesco, —, —, ere</b>\nстановиться сладким, <i>перен.</i> приятным (alicui <i>Aug</i>)."],
  ["obdulco", "<big>obdulco</big>\n\n<b>ob–dulco, —, atum, are</b>\n<b>1)</b> подслащивать (aliquid aliqua re <i>CA</i>); услаждать (fauces <i>Ambr, Aug</i>);\n<b>2)</b> опреснять (fontes amari obdulcati <i>Vlg</i>)."],
  ["obduratio", "<big>obduratio</big>\n\n<b>obduratio, onis</b> <i>f</i> [obduro]\nзакоренелость, ожесточённость <i>Eccl</i>."],
  ["obduresco", "<big>obduresco</big>\n\n<b>ob–duresco, durui, —, ere</b>\n<b>1)</b> становиться твёрдым, твердеть (diuturnitate <i>Vr</i>);\n<b>2)</b> превращаться в камень, каменеть (Qorgonis vultu <i>Prp</i>);\n<b>3)</b> закаляться (contra fortunam <i>C</i>);\n<b>4)</b> становиться нечувствительным (ad dolorem <i>C</i>)."],
  ["obduro", "<big>obduro</big>\n\n<b>ob–duro, avi, atum, are</b>\n<b>1)</b> быть твёрдым, закалённым, обладать выдержкой, терпением (perfer et obdura <i>O</i>): obduretur hoc triduum <i>C</i> нужно потерпеть ещё три ближайших дня;\n<b>2)</b> делать нечувствительным (cor suum <i>Vlg</i>): obdurata verecundia <i>Capit</i> отбросив (всякое) стеснение."],
  ["obduxe", "<big>obduxe</big>\n\n<b>obduxe</b> <i>Pl</i>\n= obduxisse (<i>inf. pf. к</i> obduco)."],
  ["obedi-", "<big>obedi-</big>\n\n<b>obedi-</b> <i>v. l.</i>\n= oboedi-."],
  ["obedo", "<big>obedo</big>\n\n<b>ob–edo, —, esum, ere</b>\nразъедать, размывать (obesa cavamine terra <i>LJ</i>)."],
  ["obeliscus", "<big>obeliscus</big>\n\n<b>obeliscus, i</b> <i>m</i> (<i>греч.</i>)\n<b>1)</b> обелиск, остроконечная колонна <i>PM, Su etc.</i>;\n<b>2)</b> бутон розы <i>Aus</i>;\n<b>3)</b> отметка на полях книги <i>Aug</i>."],
  ["obelo", "<big>obelo</big>\n\n<b>obelo, —, atum, are</b> [obelus]\nставить пометки на полях книги <i>Is</i>."],
  ["obelus", "<big>obelus</big>\n\n<b>obelus, i</b> <i>m</i> (<i>греч.</i>)\nвертел, <i>перен.</i> пометка на полях против подозрительных <i>или</i> неясных мест текста <i>Aus, Eccl</i>."],
  ["obeo", "<big>obeo</big>\n\n<b>ob–eo, ii (ivi), itum, ire</b>\n<b>1)</b> входить, вступать, приходить (in infera loca <i>C</i>);\n<b>2)</b> заходить, закатываться (sol obiit <i>Lcr, C</i>);\n<b>3)</b> сопротивляться, давать отпор (ad omnes hostium conatus <i>L</i>);\n<b>4)</b> гибнуть (urbs obiit <i>PM</i>; morte voluntaria o. <i>Su, VP, Eutr</i>); умирать (morbo <i>PM</i>): o. diem <i>Su</i> (suum <i>C</i>, supremum <i>Nep</i>) <i>или</i> o. mortem <i>Ter, C</i> скончаться, умереть: obiti <i>Ap</i> умершие; morte obita <i>Lcr, C</i> после смерти;\n<b>5)</b> доходить, достигать (flamma obit aliquid <i>C</i>);\n<b>6)</b> обходить, объезжать, инспектировать (ordinas, vigilias <i>T</i>); посещать, путешествовать (o. provinciam <i>C</i>): o. aliquas regiones pedibus <i>C</i> обойти пешком какие-л. области;\n<b>7)</b> обводить, окидывать (omnia oculis <i>PJ</i>; visu <i>V</i>);\n<b>8)</b> являться, принимать участие, присутствовать (o. comitia <i>C</i>; nundinas <i>L</i>);\n<b>9)</b> обозревать, касаться (aliquid oratione sua <i>C</i>);\n<b>10)</b> окружать, окаймлять (limbus chlamydem obit <i>O</i>); омывать (mare terras obit <i>PM</i>); окутывать (pellis clipeum obit <i>V</i>); покрывать (ora pallor obit <i>O</i>);\n<b>11)</b> приниматься (за), приступать (к), предпринимать, начинать (negotium <i>C</i>; bellum <i>L</i>); совершать (facinus <i>C</i>); принимать на себя (curationes <i>PJ</i>); соблюдать (vadimonium, diem <i>C</i>); подвергаться, идти навстречу (o. pericula <i>L</i>);\n<b>12)</b> вступать во владение, принимать (hereditates <i>C</i>); осуществлять (imperia <i>T</i>);\n<b>13)</b> выжидать (alicujus rei locum tempusque <i>C</i>): annum petitionis suae o. <i>C</i> добиваться государственной должности по достижении установленного законом возраста."],
  ["obequito", "<big>obequito</big>\n\n<b>ob–equito, avi, atum, are</b>\n<b>1)</b> прискакать, примчаться, подъехать верхом (alicui rei, alicui <i>L, QC</i>; aliquid <i>Amm</i>; usque ad aliquid <i>L</i>);\n<b>2)</b> проехать верхом: o. agmini <i>QC</i> объехать верхом войско (фронт)."],
  ["oberro", "<big>oberro</big>\n\n<b>ob–erro, avi, atum, are</b>\n<b>1)</b> бродить, блуждать, скитаться (ignotis locis <i>QC</i>);\n<b>2)</b> ошибаться (eadem chorda <i>H</i>);\n<b>3)</b> стоять перед глазами, маячить, мерещиться (imago alicujus rei oculis oberrat <i>PJ, QC</i>);\n<b>4)</b> обходить, облетать: dives arat Curibus quantum non milvus oberret <i>Pers</i> богач (Вектидий) возделывает в Курах столько (земли), что и коршуну не облететь;\n<b>5)</b> преграждать путь (oberrantes rivi <i>QC</i>)."],
  ["obesitas", "<big>obesitas</big>\n\n<b>obesitas, atis</b> <i>f</i> [obesus]\nожирение (ventris <i>Su</i>); полнота, тучность (alicujus <i>Su etc.</i>); чрезмерная толщина (arboris <i>PM</i>)."],
  ["obeso", "<big>obeso</big>\n\n<b>obeso, —, —, are</b> [obesus]\nутучнять, откармливать (avem pane <i>Col</i>)."],
  ["obesse", "<big>obesse</big>\n\n<b>obesse</b> <i>inf. к</i>\nobsum."],
  ["obesus", "<big>obesus</big>\n\n<b>obesus, a, um</b> [obedo]\n<b>1)</b> жирный, тучный, упитанный, толстый (corpus <i>CC</i>; turdus <i>H</i>; venter <i>PM, Ap</i>);\n<b>2)</b> опухший, вздувшийся (fauces <i>V</i>);\n<b>3)</b> грубый, лишённый тонкости (aures <i>Calp</i>; mens <i>Aus</i>): homo naris obesae <i>H</i> человек, лишённый тонкого обоняния (чутья);\n<b>4)</b> вялый, тяжёлый (somnus <i>Sulp</i>);\n<b>5)</b> (<i>редко</i>) исхудалый, истощённый, тощий, худой (corpus <i>Naevius ap. AG</i> — <i>ср.</i> 1)."],
  ["obex", "<big>obex</big>\n\n<b>obex, icis</b> <i>m, f</i> [objicio]\n<b>1)</b> запор, засов, дверная перекладина (portarum <i>T</i>);\n<b>2)</b> плотина, дамба, вал (maria obices rumpunt<i>V</i>): o. saxi <i>T, V</i> скалистая стена;\n<b>3)</b> заграждение, преграда (per obices viarum <i>L</i>);\n<b>4)</b> помеха, препятствие (nullae obices <i>PJ</i>)."],
  ["obf-", "<big>obf-</big>\n\n<b>obf-</b> <i>v. l.</i>\n= off-."],
  ["obgannio", "<big>obgannio</big>\n\n<b>ob–gannio (oggannio), ivi (ii), itum, ire</b>\nтявкать, лаять, <i>перен.</i> назойливо повторять (centies eadem o. <i>Pl</i>); шептать, нашёптывать (aliquid in aurem alicujus <i>Ap</i>)."],
  ["obgero", "<big>obgero</big>\n\n<b>ob–gero (oggero), gessi, gestum, ere</b>\n<b>1)</b> подносить, преподносить, давать (alicui osculum <i>Pl</i>);\n<b>2)</b> навязывать (imperium alicui resistenti <i>AV</i>)."],
  ["obhaereo", "<big>obhaereo</big>\n\n<b>ob–haereo, —, —, ere</b>\nзасесть, застрять (в чём-л.) (navis obhaerens vado <i>Su</i>)."],
  ["obhaeresco", "<big>obhaeresco</big>\n\n<b>ob–haeresco, haesi, haesum, ere</b>\nзастревать (in medio flumine <i>Lcr</i>); <i>перен.</i> льнуть, крепко прирастать (pecunia alicui obhaesit <i>Sen</i>)."],
  ["obhumo", "<big>obhumo</big>\n\n<b>ob–humo, —, —, are</b> [humus]\nзаносить землёй или песком <i>Tert</i>."],
  ["obicio", "<big>obicio</big>\n\n<b>obicio</b> <i>v. l.</i>\n= objicio."],
  ["obicis", "<big>obicis</big>\n\n<b>obicis</b> <i>gen. к</i>\nobex."],
  ["obigito", "<big>obigito</big>\n\n<b>ob–igito, —, —, are</b> [agito]\nбеспокоить, тревожить <i>Enn</i>."],
  ["obii", "<big>obii</big>\n\n<b>obii</b> <i>pf. к</i>\nobeo."],
  ["obirascor", "<big>obirascor</big>\n\n<b>ob–irascor, iratus sum, irasci</b> <i>depon.</i>\nгневаться, сердиться (alicui <i>L, Sen, Ap</i>)."],
  ["obiratio", "<big>obiratio</big>\n\n<b>obiratio, onis</b> <i>f</i> [obirascor]\nгнев, злоба <i>C</i>."],
  ["obiratus", "<big>obiratus</big>\n\n<b>obiratus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> obirascor;\n<b>2.</b> <i>adj.</i> гневный, сердитый (alicui <i>L, Sen</i>)."],
  ["obiter", "<big>obiter</big>\n\n<b>obiter</b> <i>adv.</i> [ob + iter]\n<b>1)</b> мимоходом, вскользь, при случае (dicere <i>PM</i>); при этом, одновременно, заодно (cantare <i>Pt</i>);\n<b>2)</b> (<i>редко</i>) тотчас же (reverti <i>Q</i>)."],
  ["obitus 1", "<big>obitus</big>\n\n<b>I obitus, a, um</b> <i>part. pf. к</i>\nobeo."],
  ["obitus 2", "<big>obitus</big>\n\n<b>II obitus, us</b> <i>m</i> (<i>gen. Ap</i> <i>тж.</i> i) [obeo]\n<b>1)</b> Захождение, заход, закат (solis, stellae <i>C</i>);\n<b>2)</b> падение, гибель (rei publicae <i>C</i>); кончина, смерть (ante obitum nemo dici potest beatus <i>O</i>);\n<b>3)</b> посещение (civitatum multarum <i>Ap</i>);\n<b>4)</b> совершение: o. fugae <i>Tert</i> бегство."],
  ["obivi", "<big>obivi</big>\n\n<b>obivi</b> (= obii) <i>pf. к</i>\nobeo."],
  ["objaceo", "<big>objaceo</big>\n\n<b>ob–jaceo, jacui, —, ere</b>\nлежать (у), находиться впереди (saxa objacentia pedibus — <i>dat. L</i>; objacet alto Isthmos <i>St</i>)."],
  ["objectaculum", "<big>objectaculum</big>\n\n<b>objectaculum, i</b> <i>n</i> [objecto]\nнасыпь, плотина, дамба <i>Vr</i>."],
  ["objectamentum", "<big>objectamentum</big>\n\n<b>objectamentum, i</b> <i>n</i> [objecto]\nупрёк, укор <i>Ap</i>."],
  ["objectatio", "<big>objectatio</big>\n\n<b>objectatio, onis</b> <i>f</i> <i>Cs</i>\n= objectamentum."],
  ["objectio", "<big>objectio</big>\n\n<b>objectio, onis</b> <i>f</i> [objicio]\n<b>1)</b> выставление вперёд, противопоставление (saxorum objectione tutari <i>Eccl</i>);\n<b>2)</b> <i>Vlg, Eccl</i> = objectamentum;\n<b>3)</b> возражение <i>Aug, Macr</i>."],
  ["objecto", "<big>objecto</big>\n\n<b>objecto, avi, atum, are</b> [<i>intens. к</i> objicio]\n<b>1)</b> бросать навстречу, устремлять вперёд (caput periclis <i>V</i>; se hostium telis <i>L</i>); o. caput fretis <i>V</i> бросаться в море (нырять) вниз головой;\n<b>2)</b> подвергать (aliquem periculis <i>Sl</i>);\n<b>3)</b> причинять, вызывать (moras <i>O</i>);\n<b>4)</b> жертвовать, отдавать (animam pro cunctis <i>V</i>);\n<b>5)</b> упрекать, укорять, обвинять (o. alicui probrum <i>C</i>; o. alicui aliquid fecisse <i>L</i>)."],
  ["objectus 1", "<big>objectus</big>\n\n<b>I objectus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> objicio;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> лежащий (находящийся) впереди (silva <i>Cs</i>): Hadria divisus objecto <i>H</i> живущий по ту сторону Адриатического моря || противолежащий (insula objecta Alexandriae <i>Cs</i>);\n<b>2)</b> подверженный, оставленный на произвол, являющийся жертвой (fortunae <i>C</i>; ad omnes casus <i>C</i>; invidiae alicujus <i>PM</i>)."],
  ["objectus 2", "<big>objectus</big>\n\n<b>II objectus, us</b> <i>m</i> [objicio]\n<b>1)</b> противопоставление: dare objectum parmae <i>Lcr</i> выставлять вперёд (держать впереди себя) щит; terra objectu suo noctem efficit <i>C</i> (<i>v. l.</i>) вследствие поворота земли (в отношении солнца) наступает ночь;\n<b>2)</b> преграда (montis <i>T</i>); выступ, заслон (molium <i>T</i>): insula portum efficit objectu laterum <i>V</i> выступающие вперёд берега острова образуют порт;\n<b>3)</b> предмет, явление, зрелище <i>Nep</i>."],
  ["objex", "<big>objex</big>\n\n<b>objex, objicis</b> <i>v. l.</i>\n= obex."],
  ["objexim", "<big>objexim</big>\n\n<b>objexim</b> <i>Pl</i>\n= objecerim (<i>pf. conjct. к</i> objicio)."],
  ["objicio", "<big>objicio</big>\n\n<b>objicio, jeci, jectum, ere</b> [ob + jacio]\n<b>1)</b> бросать вперёд, устремлять (pedites hosti <i>QC</i>; se telis hostium <i>C</i>); выставлять вперёд, держать перед собой, протягивать (scutum <i>L</i>);\n<b>2)</b> бросать (cibum canibus <i>PM</i>); обрекать (captivos bestiis <i>Sl</i>; aliquem morti <i>C</i>);\n<b>3)</b> захлопывать (fores <i>L</i>; portam <i>V</i>);\n<b>4)</b> прокладывать впереди (fossam <i>L</i>);\n<b>5)</b> приставлять (manum ad os <i>C, QC</i>); подносить (vinum naribus <i>Pl</i>);\n<b>6)</b> противопоставлять, ставить <i>или</i> использовать в качестве преграды (Alpes Germanorum gentibus <i>L</i>): viatori moram o. <i>Ap</i> заставить путника остановиться; septentrio a Macedonia objicitur <i>L</i> северная часть обращена к Македонии;\n<b>7)</b> набрасывать, расстилать (в целях сокрытия) (nubem oculis <i>O</i>; noctem peccatis <i>H</i>; caliginem mentibus <i>Sen</i>);\n<b>8)</b> подвергать (se periculis <i>VF</i>);\n<b>9)</b> выдавать (praedam hosti <i>QC</i>);\n<b>10)</b> возражать, выдвигать (в качестве возражения) (aliquid adversario <i>C</i>);\n<b>11)</b> причинять, внушать, нагонять (animo terrorem <i>L</i>); вселять (alicui spem <i>L</i>): alicui errorem o. <i>C</i> обманывать (морочить) кого-л.;\n<b>12)</b> давать, доставлять (animo delenimentum <i>L</i>); ниспосылать (sortem alicui <i>H</i>);\n<b>13)</b> упрекать, укорять, винить (alicui aliquid <i>или</i> de aliqua re, de aliquo, <i>реже</i> aliquid in aliquem <i>C etc.</i>): o. ut probrum alicui, quod... <i>C</i> вменить кому-л. в позорную вину то, что... || objecta, orum <i>O</i> упрёки;\n<b>14)</b> <i>pass.</i> objici возникать (difficultates objectae <i>Sl</i>); появляться, предстать (oculis <i>V</i>)."],
  ["objurgatio", "<big>objurgatio</big>\n\n<b>objurgatio, onis</b> <i>f</i> [objurgo]\nбрань <i>C, Sen</i>; порицание, выговор (o. contumelia careat <i>C</i>; objurgatione dignus <i>Ap</i>)."],
  ["objurgator", "<big>objurgator</big>\n\n<b>objurgator, oris</b> <i>m</i> [objurgo]\nбранящий, порицающий, порицатель <i>C, Ap, Macr</i>."],
  ["objurgatorius", "<big>objurgatorius</big>\n\n<b>objurgatorius, a, um</b> [objurgo]\nбранный, ругательный, порицающий (epistula <i>C</i>; verba <i>AG</i>)."],
  ["objurgito", "<big>objurgito</big>\n\n<b>objurgito, —, —, are</b> [<i>intens. к</i> objurgo]\nсильно порицать (aliquem verbis <i>Pl</i>)."],
  ["objurgo", "<big>objurgo</big>\n\n<b>ob–jurgo, avi, atum, are</b>\n<b>1)</b> бранить, порицать, делать выговор (aliquem de <i>или</i> in aliqua re <i>C</i>, <i>редко</i> <i>MF</i> alicujus rei);\n<b>2)</b> отговаривать, отклонять (aliquem a peccatis <i>Pl</i>);\n<b>3)</b> наказывать, бить (aliquem verberibus <i>Sen</i>, colaphis <i>Pt</i>)."],
  ["objurgor", "<big>objurgor</big>\n\n<b>objurgor, —, ari</b> <i>depon.</i> (<i>редко</i>) <i>Caelius ap. C</i>\n= objurgo."],
  ["objurigo", "<big>objurigo</big>\n\n<b>objurigo, —, —, are</b> <i>Pl</i>\n= objurgo."],
  ["oblanguesco", "<big>oblanguesco</big>\n\n<b>ob–languesco, langui, —, ere</b>\nутомляться, уставать, слабеть <i>C</i>."],
  ["oblaqueatio", "<big>oblaqueatio</big>\n\n<b>oblaqueatio, onis</b> <i>f</i>\nокапывание (<i>sc.</i> arborum) <i>Is</i>."],
  ["oblaqueo", "<big>oblaqueo</big>\n\n<b>oblaqueo, —, —, are</b> [ob + laqueus]\n<b>1)</b> окапывать корни (arbores oblaqueatae <i>Col</i>);\n<b>2)</b> оправлять (gemmas argento <i>Tert</i>)."],
  ["oblata", "<big>oblata</big>\n\n<b>oblata, ae</b> <i>f</i> [offero]\n(<i>sc.</i> hostia) <i>культ.</i> просфора <i>или</i> облатка <i>Eccl</i>."],
  ["oblaticius", "<big>oblaticius</big>\n\n<b>oblaticius, a, um</b> [offero]\nприносимый в дар, жертвуемый (aurum <i>CTh</i>; impendium <i>Sid</i>)."],
  ["oblatio", "<big>oblatio</big>\n\n<b>oblatio, onis</b> <i>f</i> [offero]\n<b>1)</b> (добровольное) предложение <i>Dig, CTh</i>;\n<b>2)</b> преподнесение, приношение (victus vel potionis <i>CJ</i>);\n<b>3)</b> вручение (precum <i>CJ</i>);\n<b>4)</b> взнос, платёж (ab omnibus oblationibus liberatus <i>CTh</i>);\n<b>5)</b> дар, пожертвование <i>Dig, Vlg, Eccl</i>."],
  ["oblativus", "<big>oblativus</big>\n\n<b>oblativus, a, um</b> [offero]\nдобровольно приносимый <i>или</i> исполняемый (functiones <i>Symm</i>)."],
  ["oblator", "<big>oblator</big>\n\n<b>oblator, oris</b> <i>m</i> [offero]\nжертвователь, даритель <i>Eccl</i>."],
  ["oblatratio", "<big>oblatratio</big>\n\n<b>oblatratio, onis</b> <i>f</i> [oblatro]\nоблаивание, <i>перен.</i> брань, ругань <i>Hier</i>."],
  ["oblatrator", "<big>oblatrator</big>\n\n<b>oblatrator, oris</b> <i>m</i>\nругатель <i>Sid</i>."],
  ["oblatratrix", "<big>oblatratrix</big>\n\n<b>oblatratrix, icis</b> <i>f</i> [oblatro]\nругательница, сварливая женщина <i>Pl</i>."],
  ["oblatro", "<big>oblatro</big>\n\n<b>ob–latro, —, —, are</b>\nнабрасываться с лаем <i>или</i> с бранью, накидываться (alicui <i>Sen</i> <i>и</i> aliquem <i>Sil</i>); ругать, бранить: o. nescio quid <i>Su</i> ругать неизвестно за что (за что-то)."],
  ["oblatus", "<big>oblatus</big>\n\n<b>oblatus, a, um</b> <i>part. pf. к</i>\noffero."],
  ["oblectabilis", "<big>oblectabilis</big>\n\n<b>oblectabilis, e</b>\nзанимательный, приятный (negotium <i>Aus</i>)."],
  ["oblectamen", "<big>oblectamen</big>\n\n<b>oblectamen, inis</b> <i>n</i> <i>O, St</i>\n= oblectamentum."],
  ["oblectamentum", "<big>oblectamentum</big>\n\n<b>oblectamentum, i</b> <i>n</i> [oblecto]\n<b>1)</b> утеха, увеселение, развлечение, отрада, забава, наслаждение (oblectamenta vitae <i>Sen</i>);\n<b>2)</b> успокоительное средство, успокоение (requies obloctamentumque senectutis <i>C</i>)."],
  ["oblectatio", "<big>oblectatio</big>\n\n<b>oblectatio, onis</b> <i>f</i> [oblecto]\nувеселение, утеха, услаждение, радость (vitae <i>C</i>): res illa habet oblectationem <i>C</i> это доставляет удовольствие."],
  ["oblectator", "<big>oblectator</big>\n\n<b>oblectator, oris</b> <i>m</i> [oblecto]\n<b>1)</b> доставляющий наслаждение, радующий (o. hominis <i>Tert</i>);\n<b>2)</b> зачаровывающий, заклинатель (beluarum <i>Ap</i>)."],
  ["oblectatorius", "<big>oblectatorius</big>\n\n<b>oblectatorius, a, um</b> [oblecto]\nвеселящий, забавный, занимательный (aenigmata <i>AG</i>)."],
  ["oblecto", "<big>oblecto</big>\n\n<b>oblecto, avi, atum, are</b> [ob+ lacto II]\nзабавлять, увеселять (populum <i>H</i>); услаждать (aures alicujus <i>AG</i>; studia litterarum senectutem oblectant <i>C</i>): o. se (in) aliqua re <i>Su</i> находить в чём-л. удовольствие (наслаждаться чём-л.); o. se (cum) aliquo <i>C</i> приятно проводить с кем-л. время; o. otium temporis <i>PJ</i> (moras <i>O</i>) скрашивать досуг."],
  ["oblenimen", "<big>oblenimen</big>\n\n<b>oblenimen, inis</b> <i>n</i> [oblenio]\nуспокоительное средство <i>O</i> (<i>v. l.</i>)."],
  ["oblenio", "<big>oblenio</big>\n\n<b>ob–lenio, —, —, ire</b>\nуспокаивать, смягчать (aliquem iracundum <i>Sen</i>)."],
  ["oblevi", "<big>oblevi</big>\n\n<b>oblevi</b> <i>pf. к</i>\noblino."],
  ["oblicus", "<big>oblicus</big>\n\n<b>oblicus, a, um</b> <i>v. l.</i>\n= obliquus."],
  ["oblido", "<big>oblido</big>\n\n<b>oblido, lisi, lisum, ere</b> [ob + laedo]\n<b>1)</b> сдавливать, сжимать: o. gulam <i>C</i> (collum <i>C</i>) удавить, задушить;\n<b>2)</b> раздавливать (oblisus pondere <i>PJ</i>; aliquem toto corpore <i>Ap</i>)."],
  ["obligamentum", "<big>obligamentum</big>\n\n<b>obligamentum, i</b> <i>n</i>\n<b>1)</b> связь (obligamento obnoxius <i>Tert</i>);\n<b>2)</b> обязательность (legis <i>Tert</i>)."],
  ["obligatio", "<big>obligatio</big>\n\n<b>obligatio, onis</b> <i>f</i> [obligo]\n<b>1)</b> <i>юр.</i> обязательство (pecuniae <i>C</i>; o. ex contractu <i>Dig</i>); поручительство (pro aliquo <i>C</i>);\n<b>2)</b> связанность, скованность (linguae <i>Just</i>)."],
  ["obligatorius", "<big>obligatorius</big>\n\n<b>obligatorius, a, um</b>\nимеющий обязательную силу (mandatum <i>Dig</i>)."],
  ["obligatus", "<big>obligatus</big>\n\n<b>obligatus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> obligo;\n<b>2.</b> <i>adj.</i> обязанный, признательный <i>PJ</i>."],
  ["obligo", "<big>obligo</big>\n\n<b>ob–ligo, avi, atum, are</b>\n<b>1)</b> связывать (brachia <i>T</i>); перевязывать (venas <i>T</i>; vulnus <i>C</i>); завязывать (oculos <i>Sen</i>); обвязывать: age obliga, obsigna cito <i>Pl</i> обвяжи (письмо) и запечатай поскорей;\n<b>2)</b> обхватывать, обнимать (amatores obligati <i>Pt</i>);\n<b>3)</b> обязывать, связывать (milites sacramento <i>C</i>); привязывать (sibi aliquem beneficiis <i>C</i>): o. aliquem tribus milibus aeris <i>L</i> обязать кого-л. уплатить три тысячи денег; cives nova lege o. <i>C</i> издать для граждан новый закон;\n<b>4)</b> вменять в вину, винить, обвинять (aliquem scelere <i>C, Su</i>); <i>pass.</i> провиниться (obligari fraude <i>C</i>) <i>или</i> стать жертвой (obligari superstitione <i>C</i>);\n<b>5)</b> отдавать в залог, закладывать (fortunas suas <i>C</i>);\n<b>6)</b> торжественно обещать (dapem <i>H</i>): o. fidem suam <i>C</i> поручиться словом (честью); o. caput suum votis <i>H</i> прозакладывать свою голову;\n<b>7)</b> обрекать, отдавать: Proffletheus obligatus aliti <i>H</i> Прометей, отданный в жертву птице (коршуну)."],
  ["oblimo", "<big>oblimo</big>\n\n<b>oblimo, avi, atum, are</b> [ob + limus]\n<b>1)</b> покрывать илом, заносить тиной (fossas <i>Su</i>; agros <i>C</i>);\n<b>2)</b> расточать, проматывать (rem suam <i>H</i>);\n<b>3)</b> затемнять, помрачать (humanas mentes <i>Cld</i>)."],
  ["oblinio", "<big>oblinio</big>\n\n<b>ob–linio, —, —, ire</b>\n<b>1)</b> обмазывать (misto fimo cum cinere aliquid <i>Col</i>);\n<b>2)</b> замазывать (veritatem <i>Ambr</i>)."],
  ["oblino", "<big>oblino</big>\n\n<b>ob–lino, levi, litum, ere</b>\n<b>1)</b> намазывать, обмазывать (cerussa malas <i>Pl</i>); натирать (aliquem ungueritis <i>C</i>); загрязнять, пачкать (caeno <i>rhH</i>);\n<b>2)</b> конопатить (aliquid gypso <i>PM</i>): o. pice <i>PM</i> покрывать смолой, смолить;\n<b>3)</b> замазывать, затирать (написанное на воске), зачёркивать (verbum <i>AG</i>);\n<b>4)</b> заваливать, заполнять (villam tabulis pictis <i>Vr</i>); перегружать (orationem <i>rhH</i>); осыпать (divitiis <i>H</i>): aliquid Latio o. <i>C</i> придавать чему-л. латинский налёт, делать что-л. на латинский лад;\n<b>5)</b> пятнать, позорить, бесчестить (vitam turpitudine <i>C</i>); поносить (aliquem versibus atris <i>H</i>)."],
  ["obliquatio", "<big>obliquatio</big>\n\n<b>obliquatio, onis</b> <i>f</i> <i>Macr</i>\n= obliquitas."],
  ["oblique", "<big>oblique</big>\n\n<b>oblique</b> [obliquus]\n<b>1)</b> косо, вкось, наискось, вбок (ferri <i>C</i>);\n<b>2)</b> косвенно, скрыто (aliquem castigare <i>T</i>)."],
  ["obliquitas", "<big>obliquitas</big>\n\n<b>obliquitas, atis</b> <i>f</i> [obliquus]\n<b>1)</b> наклон, скошенность <i>PM, PJ</i>;\n<b>2)</b> двусмысленность <i>Aug</i>."],
  ["obliquo 1", "<big>obliquo</big>\n\n<b>I obliquo, avi, atum, are</b> [obliquus]\n<b>1)</b> поворачивать вбок, направлять в сторону (equos <i>St</i>; pedes <i>Sen</i>); скашивать, косить (oculos <i>O</i>; visus <i>St</i>); зачёсывать на сторону (crinem <i>T</i>): o. ensem <i>O</i> рубить мечом наискось; o. sinus (velorum) in ventum <i>V</i> ставить паруса наискось к направлению ветра, <i>т. е.</i> лавировать;\n<b>2)</b> произносить мягче, смягчать (litteram <i>Q</i>);\n<b>3)</b> косвенно (скрыто) выражать (preces <i>St</i>)."],
  ["obliquo 2", "<big>obliquo</big>\n\n<b>II obliquo</b> <i>Cato</i>\n= oblique."],
  ["obliquum", "<big>obliquum</big>\n\n<b>obliquum</b> <i>Amm</i>\n= oblique."],
  ["obliquus", "<big>obliquus</big>\n\n<b>obliquus, a, um</b>\n<b>1)</b> направленный в сторону, боковой (iter <i>Cs</i>; motus corporis <i>C</i>); косвенный, косой, наносимый сбоку (ictus <i>H</i>): imago obliqua <i>PM</i> профиль;\n<b>2)</b> наклонный (sublicae <i>Cs</i>); покатый (coilis <i>L</i>): ex (ab) obliquo <i>PM, O</i> сбоку, со стороны, косо; per (in) obliquum <i>H, L, PM</i> вкось, вбок, в сторону;\n<b>3)</b> косящийся, завистливый (invidia <i>V</i>): oculo obliquo aspicere <i>O</i> смотреть искоса <i>или</i> косо;\n<b>4)</b> кривой, изогнутый, извилистый (ripa, cursus <i>O</i>; fulmen <i>Sen</i>);\n<b>5)</b> побочный, неединоутробный (genus <i>St</i>; sanguis <i>Lcn</i>);\n<b>6)</b> иносказательный, двусмысленный (oratio <i>Su</i>; verba <i>Amm</i>);\n<b>7)</b> <i>грам.</i> относящийся к другому слову, косвенный (casus <i>Vr, Q</i>); непрямой, косвенный (oratio <i>Just</i>; allocutio <i>Q</i>)."],
  ["obliscor", "<big>obliscor</big>\n\n<b>obliscor, —, isci</b> <i>арх. Acc</i>\n= obliviscor."],
  ["oblisi", "<big>oblisi</big>\n\n<b>oblisi</b> <i>pf. к</i>\noblido."],
  ["obliter-", "<big>obliter-</big>\n\n<b>obliter-</b> <i>v. l.</i>\n= oblitter-."],
  ["oblitesco", "<big>oblitesco</big>\n\n<b>oblitesco, tui, —, ere</b> [ob + latesco]\nскрываться, прятаться (ab aspectu alicujus <i>C</i>; metu <i>Sen</i>)."],
  ["oblitteratio", "<big>oblitteratio</big>\n\n<b>oblitteratio, onis</b> <i>f</i> [oblittero]\nстирание, изглаживание, <i>перен.</i> забывание, забвение (alicujus rei <i>PM, Amm</i>)."],
  ["oblitterator", "<big>oblitterator</big>\n\n<b>oblitterator, oris</b> <i>m</i>\nизглаживающий из памяти, заставляющий забыть (alicujus rei <i>Tert</i>)."],
  ["oblittero", "<big>oblittero</big>\n\n<b>oblittero, avi, atum, are</b> [ob + littera]\n<b>1)</b> изглаживать, стирать (из памяти), предавать забвению (aliquid silentio <i>Su</i>); забывать (res jam oblitterata <i>L</i>);\n<b>2)</b> заглаживать (adversam — <i>sc.</i> pugnam — prospera pugna <i>L</i>)."],
  ["oblitterus", "<big>oblitterus</big>\n\n<b>oblitterus, a, um</b> [<i>из</i> oblitteratus]\nпришедший в забвение, забытый <i>AG</i>."],
  ["oblitui", "<big>oblitui</big>\n\n<b>oblitui</b> <i>pf. к</i>\noblitesco."],
  ["oblitus 1", "<big>oblitus</big>\n\n<b>I oblitus, a, um</b> <i>part. pf. к</i>\noblino."],
  ["oblitus 2", "<big>oblitus</big>\n\n<b>II oblitus, a, um</b> <i>part. pf. к</i>\nobliviscor (<i>act. и pass.</i>)."],
  ["oblivio", "<big>oblivio</big>\n\n<b>oblivio, onis</b> <i>f</i> [obliviscor]\n<b>1)</b> забвение: aliquid oblivioni dare (<i>или</i> tradere) <i>L</i>, <i>тж.</i> memoriam alicujus rei oblivione delere <i>C</i> предать что-л. забвению; venire (adduci) in oblivionem <i>C, L</i> <i>или</i> capere oblivionem rei alicujus <i>PM</i> забыть что-л. (о чём-л.); oblivio rei alicujus me capit <i>C</i> я забываю что-л.; in oblivionem ire <i>Sen</i> забываться;\n<b>2)</b> забывчивость (per oblivionem <i>Su</i>);\n<b>3)</b> амнистия (lex oblivionis <i>Nep</i>)."],
  ["obliviosus", "<big>obliviosus</big>\n\n<b>obliviosus, a, um</b> [oblivio]\n<b>1)</b> забывчивый (homo <i>C etc.</i>);\n<b>2)</b> дающий забвение (vinum <i>H</i>)."],
  ["obliviscor", "<big>obliviscor</big>\n\n<b>obliviscor, litus sum, livisci</b> <i>depon.</i> [oblino]\n<b>1)</b> забывать, не помнить (alicujus rei <i>или</i> aliquid <i>C etc.</i>); упускать из виду, не обращать внимания, не принимать во внимание (consuetudinis suae <i>C</i>); o. sui <i>C, V</i> изменить себе, поступать вопреки своему обыкновению, <i>тж.</i> <i>VF</i> забыть о своём достоинстве, <i>Ter</i> Не помнить себя <i>или</i> <i>T</i> не думать о реальных условиях; quid deceat, quid non, oblitus <i>H</i> забыв(ший), что подобает, а что нет; oblitus laborum <i>V</i> забыв о (своих) страданиях; oblitus nugarum <i>Pt</i> отбросив шутки; nomen suum o. <i>погов. Pt</i> забыть собственное имя, <i>т. е.</i> иметь очень плохую память; <i>иногда</i> <i>pass.</i>: oblita carmina <i>V</i> забытые стихотворения;\n<b>2)</b> покрывать забвением, изглаживать из памяти (saecla obliviscentia <i>Ctl</i>)."],
  ["oblivium", "<big>oblivium</big>\n\n<b>oblivium, i</b> <i>n</i> [obliviscor] <i>поэт.</i>\nзабвение: alicujus rei oblivia agere <i>O</i> (ducere <i>H</i>) забывать о чём-л.; oblivia rerum <i>Lcr</i> беспамятство."],
  ["oblivius", "<big>oblivius</big>\n\n<b>oblivius, a, um</b>\nзабытый: verba oblivia <i>Vr</i> устарелые слова."],
  ["obloco", "<big>obloco</big>\n\n<b>ob–loco, —, —, are</b>\nсдавать на откуп <i>или</i> с подряда (operam ad puteos exhauriendos <i>Just</i>)."],
  ["oblocutor", "<big>oblocutor</big>\n\n<b>oblocutor, oris</b> <i>m</i> [obloquor]\nпротивник в споре, оппонент, любитель прекословить <i>Pl</i>."],
  ["oblongulus", "<big>oblongulus</big>\n\n<b>oblongulus, a, um</b> [<i>demin. к</i> oblongus]\nдлинноватый, довольно длинный (surculi <i>AG</i>)."],
  ["oblongus", "<big>oblongus</big>\n\n<b>oblongus, a, um</b>\nдовольно длинный (hastile <i>L</i>); продолговатый (scutulum <i>T</i>)."],
  ["obloquium", "<big>obloquium</big>\n\n<b>obloquium, i</b> <i>n</i> [obloquor]\nпрекословие, противоречие <i>Sid, Eccl</i>."],
  ["obloquor", "<big>obloquor</big>\n\n<b>ob–loquor, locutus sum, loqui</b> <i>depon.</i>\n<b>1)</b> возражать, противоречить (alicui <i>Pl, C etc.</i>);\n<b>2)</b> бранить, порицать (gannire et o. <i>Ctl</i>);\n<b>3)</b> <i>поэт.</i> подпевать, вторить, сопровождать на музыкальном инструменте: non avis obloquitur <i>O</i> ни одна птица не поёт (в Понтийском краю); o. numeris septem <i>V</i> аккомпанировать на семиструнной лире."],
  ["obloquutor", "<big>obloquutor</big>\n\n<b>obloquutor</b> <i>v. l.</i>\n= oblocutor."],
  ["obluctatio", "<big>obluctatio</big>\n\n<b>obluctatio, onis</b> <i>f</i> [obluctor]\n(со)противление (adversus aliquid <i>Lact</i> <i>и</i> alicujus rei <i>Eccl</i>)."],
  ["obluctor", "<big>obluctor</big>\n\n<b>ob–luctor, atus sum, ari</b> <i>depon.</i>\nсопротивляться, бороться, противодействовать (alicui <i>Sil</i> <i>и</i> alicui rei <i>Col, QC</i>); упираться (genibus arenae <i>V</i>)."],
  ["obludo", "<big>obludo</big>\n\n<b>ob–ludo, lusi, lusum, ere</b>\n<b>1)</b> шутить, балагурить <i>Pl</i>;\n<b>2)</b> подтрунивать, насмехаться (alicui <i>Eccl</i>)."],
  ["obluridus", "<big>obluridus</big>\n\n<b>ob–luridus, a, um</b>\nземлистого цвета, мертвенно-бледный (spadones <i>Amm</i>)."],
  ["obluvium", "<big>obluvium</big>\n\n<b>obluvium, i</b> <i>n</i>\nсполаскивание, смывание <i>Acc</i> (<i>v. l.</i>)."],
  ["obmarcesco", "<big>obmarcesco</big>\n\n<b>ob–marcesco, —, —, ere</b>\nстановиться застарелым, укореняться (febris obmarcescebat <i>LM</i>)."],
  ["obmento", "<big>obmento</big>\n\n<b>obmento</b> <i>v. l.</i>\n= ommento."],
  ["obmolior", "<big>obmolior</big>\n\n<b>ob–molior, itus sum, iri</b> <i>depon.</i>\n<b>1)</b> выдвигать, выставлять вперёд, нагромождать впереди (truncos arborum et saxa <i>QC</i>);\n<b>2)</b> загромождать, затыкать, закрывать (aliquid <i>L</i>)."],
  ["obmordeo", "<big>obmordeo</big>\n\n<b>ob–mordeo, —, —, ere</b>\nобкусывать, обгрызать <i>Is</i>."],
  ["obmoveo", "<big>obmoveo</big>\n\n<b>ob–moveo, —, —, ere</b> <i>арх.</i>\nприносить, доставлять, предоставлять <i>Cato</i>."],
  ["obmurmuratio", "<big>obmurmuratio</big>\n\n<b>ob–murmuratio, onis</b> <i>f</i>\nсдержанный шум, ропот <i>Amm, Is</i>."],
  ["obmurmuro", "<big>obmurmuro</big>\n\n<b>ob–murmuro, avi, atum, are</b>\nворчать, бормотать <i>или</i> шуметь (в ответ) (alicui rei <i>O</i> <i>или</i> aliquid <i>Su</i>)."],
  ["obmussito", "<big>obmussito</big>\n\n<b>ob–mussito, —, —, are</b>\nсдержанно возражать, роптать про себя <i>Tert</i>."],
  ["obmusso", "<big>obmusso</big>\n\n<b>ob–musso, —, —, are</b> <i>Tert</i>\n= obmussito."],
  ["obmutesco", "<big>obmutesco</big>\n\n<b>ob–mutesco, mutui, —, ere</b>\nнеметь, умолкать (timore <i>rhH</i>): obmutuit amens <i>V</i> (Эней) онемел (словно) безумный || утихать, прекращаться (dolor obmutuit <i>C</i>)."],
  ["obnatus", "<big>obnatus</big>\n\n<b>ob–natus, a, um</b>\nприросший, растущий (на чём-л.) (obnata ripis salicta <i>L</i>)."],
  ["obnecto", "<big>obnecto</big>\n\n<b>ob–necto, —, nexum, ere</b>\nобвивать (pampino <i>Acc</i>)."],
  ["obnexus", "<big>obnexus</big>\n\n<b>ob–nexus, us</b> <i>n</i>\nсоединение, связь <i>Tert</i>."],
  ["obnisus", "<big>obnisus</big>\n\n<b>obnisus, a, um</b> <i>part. pf. к</i>\nobnitor."],
  ["obnitor", "<big>obnitor</big>\n\n<b>ob–nitor, nisus (nixus) sum, niti</b> <i>depon.</i>\n<b>1)</b> упираться (alicui rei <i>V, L, Nep</i>): o. umeris <i>V</i> напирать плечом;\n<b>2)</b> сопротивляться, бороться, противодействовать (alicui <i>T</i>, alicui rei <i>V</i> <i>и</i> adversus aliquid <i>Aug</i>, contra aliquid <i>T</i>); прилагать все усилия (aliquid impedire o. <i>VP</i>)."],
  ["obnixe", "<big>obnixe</big>\n\n<b>obnixe</b> [obnixus]\nстойко, усиленно, не щадя сил, всеми силами (omnia facere <i>Ter</i>, aliquid petere <i>Amm</i>)."],
  ["obnixum", "<big>obnixum</big>\n\n<b>obnixum</b> <i>adv.</i> <i>Aus</i>\n= obnixe."],
  ["obnixus", "<big>obnixus</big>\n\n<b>obnixus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> obnitor;\n<b>2.</b> <i>adj.</i> твёрдый, непоколебимый (ad aliquid <i>Sen</i>); твёрдо решивший(ся) (non cedere <i>V</i>)."],
  ["obnoxie", "<big>obnoxie</big>\n\n<b>obnoxie</b> [obnoxius]\n<b>1)</b> покорно, робко, смиренно, по-рабски (sententias dicere <i>L</i>);\n<b>2)</b> заслуженно, поделом: nihil o. <i>Pl</i> безвинно."],
  ["obnoxietas", "<big>obnoxietas</big>\n\n<b>obnoxietas, atis</b> <i>f</i>\nпокорность, смирение <i>Eccl</i>."],
  ["obnoxiose", "<big>obnoxiose</big>\n\n<b>obnoxiose</b> [obnoxiosus]\nпокорно, робко <i>Pl</i>."],
  ["obnoxiosus", "<big>obnoxiosus</big>\n\n<b>obnoxiosus, a, um</b> [ob + noxa]\n<b>1)</b> покорный, смиренный (alicui <i>Pl</i>);\n<b>2)</b> опасный, вредный (res <i>Enn</i>)."],
  ["obnoxius", "<big>obnoxius</big>\n\n<b>obnoxius, a, um</b> [ob + noxa]\n<b>1)</b> покорный, подвластный, зависимый (supplex et o. <i>Brutus ap. C</i>); раболепствующий (o. atque subjectus alicui <i>L</i>; jussis alienis <i>T</i>): pax obnoxia <i>L</i> позорный (кабальный) мир;\n<b>2)</b> слабый, хрупкий, жалкий (corpus, flos <i>PM</i>);\n<b>3)</b> обязанный (uxori <i>Ter</i>): luna radiis fratris (= solis) obnoxia est <i>V</i> луна обязана (своим светом) лучам солнца;\n<b>4)</b> подвластный, подверженный (periculis <i>PJ</i>; morti <i>O</i>; ad tales casus <i>PM</i>): omnibus o. causis <i>Sen</i> подверженный всяким случайностям; obnoxium est <i>T</i> представляет опасность, опасно;\n<b>5)</b> достойный наказания, подлежащий наказанию (tibi me obnoxium esse fateor <i>Pl</i>); виновный, повинный (alicui rei <i>Sl, Tib, O</i>): o. alicujus rei <i>L, CJ</i> заслуживающий наказания за что-л.;\n<b>6)</b> ответственный: pecuniae creditae (<i>gen.</i>) bona debitoris obnoxia sunt <i>L</i> за взятые в долг деньги должник отвечает своим имуществом."],
  ["obnubilatio", "<big>obnubilatio</big>\n\n<b>ob–nubilatio, onis</b> <i>f</i>\nзатуманивание, помрачение (o. tristitiae <i>Aug</i>)."],
  ["obnubilo", "<big>obnubilo</big>\n\n<b>ob–nubilo, avi, atum, are</b>\n<b>1)</b> закрывать облаками, затуманивать, омрачать (serenitatem vultus <i>AG</i>);\n<b>2)</b> приводить в бессознательное состояние, туманить, дурманить (animam <i>Ap</i>)."],
  ["obnubilus", "<big>obnubilus</big>\n\n<b>ob–nubilus, a, um</b>\nокутанный облаками, покрытый мраком (obnubila tenebris loca <i>Enn</i>)."],
  ["obnubo", "<big>obnubo</big>\n\n<b>ob–nubo, nupsi, nuptum, ere</b>\nокутывать, закрывать, покрывать (comas amictu <i>V</i>); заволакивать (terra obnupta nubibus <i>Macr</i>)."],
  ["obnuntiatio", "<big>obnuntiatio</big>\n\n<b>obnuntiatio, onis</b> <i>f</i> [obnuntio] <i>культ.</i>\nсообщение о дурных предзнаменованиях <i>C</i>."],
  ["obnuntio", "<big>obnuntio</big>\n\n<b>ob–nuntio, avi, atum, are</b>\n<b>1)</b> (<i>преим. о дурных вестях</i>) докладывать, извещать, сообщать <i>Ter</i>;\n<b>2)</b> <i>культ.</i> сообщать о дурных предзнаменованиях (alicui <i>C</i>)."],
  ["oboediens", "<big>oboediens</big>\n\n<b>oboediens, entis</b>\n<b>1.</b> <i>part. praes. к</i> oboedio;\n<b>2.</b> <i>adj.</i> послушный, покорный, уступчивый (alicui, alicui rei <i>Pl, L, C</i>, <i>реже</i> ad aliquid <i>L</i>); податливый (quocunque in opere <i>PM</i>)."],
  ["oboedienter", "<big>oboedienter</big>\n\n<b>oboedienter</b> [oboediens]\nпослушно, покорно, беспрекословно (imperata facere <i>L, QC</i>)."],
  ["oboedientia", "<big>oboedientia</big>\n\n<b>oboedientia, ae</b> <i>f</i> [oboediens]\nпослушание, повиновение, покорность (circum aliquem <i>PM</i>; in aliqua re <i>VM</i>)."],
  ["oboedio", "<big>oboedio</big>\n\n<b>oboedio, ivi, itum, ire</b> [ob + audio]\n<b>1)</b> слушать, следовать совету (o. alicui <i>Nep</i>);\n<b>2)</b> слушаться, повиноваться, подчиняться (magistratibus <i>C</i>; parentibus <i>Ap</i>; legibus <i>Nep</i>; <i>редко</i> aliquid <i>Ap</i>)."],
  ["oboeditio", "<big>oboeditio</big>\n\n<b>oboeditio, onis</b> <i>f</i> <i>Vlg, Eccl</i>\n= oboedientia."],
  ["oboeditor", "<big>oboeditor</big>\n\n<b>oboeditor, oris</b> <i>m</i>\nподчиняющийся (praeceptorum suorum <i>Aug</i>)."],
  ["oboleo", "<big>oboleo</big>\n\n<b>ob–oleo, lui, —, ere</b>\nиздавать запах, пахнуть: o. aliquid <i>Pl, Su, Ap</i> пахнуть чём-л.; o. alicui <i>Ap</i> быть доступным чьему-л. обонянию; aliquid obolet alicui кто-л. ощущает запах чего-л., <i>перен.</i> пронюхал, чует близость (oboluit marsupium huic istuc <i>Pl</i>)."],
  ["obolus", "<big>obolus</big>\n\n<b>obolus, i</b> <i>m</i> (<i>греч.</i>)\nобол, <i>одна шестая драхмы</i> (<i>мелкая греч. монета <i>Ter, Vtr, PM</i> или <i>CC, PM</i> мера веса = 0,728 г.</i>)."],
  ["obominor", "<big>obominor</big>\n\n<b>obominor</b> <i>Ap</i>\n(<i>v. l.</i>) = abominor."],
  ["oborior", "<big>oborior</big>\n\n<b>ob–orior, ortus sum, iri</b> <i>depon.</i>\nпоявляться, возникать: tenebrae oboriuntur <i>Nep</i> становится темно, темнеет; lacrimae obortae <i>V, QC</i> выступившие (на глазах) слёзы; sententia de repentino oborta <i>Ap</i> внезапно появившаяся мысль; bellum obortum est <i>L</i> вспыхнула (началась) война; verba quasi delirantibus oborta <i>Ap</i> речи как бы безумных людей."],
  ["obortus 1", "<big>obortus</big>\n\n<b>I obortus, a, um</b> <i>part. pf. к</i>\noborior."],
  ["obortus 2", "<big>obortus</big>\n\n<b>II obortus, us</b> <i>m</i>\nвозникновение, появление <i>Lcr</i> (<i>v. l.</i>)."],
  ["obp-", "<big>obp-</big>\n\n<b>obp-</b> <i>v. l.</i>\n= opp-."],
  ["obradio", "<big>obradio</big>\n\n<b>ob–radio, —, —, are</b>\nсиять (навстречу) <i>Is</i>."],
  ["obraucatus", "<big>obraucatus</big>\n\n<b>obraucatus, a, um</b> [ob + raucus]\nохрипший (vox <i>Sol</i>)."],
  ["obrepo", "<big>obrepo</big>\n\n<b>ob–repo, repsi, reptum, ere</b>\n<b>1)</b> подползать, тихо подбираться, подкрадываться (Galli in obsidione Capitolii obrepentes <i>AG</i>; feles obrepunt avibus — <i>dat. PM</i>);\n<b>2)</b> застигать врасплох, настигать (mors obrepit <i>Pl</i>); незаметно наступать (obrepsit dies <i>C</i>; obrepit non intellecta senectus <i>J</i>);\n<b>3)</b> прокрасться, достигнуть происками (o. ad honores <i>C</i>); перехитрить, обмануть (alicui <i>AG, Dig</i>)."],
  ["obrepticius", "<big>obrepticius</big>\n\n<b>obrepticius, a, um</b>\nдостигнутый хитростью <i>CJ</i>."],
  ["obreptio", "<big>obreptio</big>\n\n<b>obreptio, onis</b> <i>f</i> [obrepo]\n<b>1)</b> подкрадывание, застигание врасплох (aggredi per obreptionem <i>Frontin</i>);\n<b>2)</b> интриги, коварство, хитрость (per obreptionem petere aliquid <i>Dig</i>)."],
  ["obreptive", "<big>obreptive</big>\n\n<b>obreptive</b>\nисподтишка, втихомолку <i>CTh</i>."],
  ["obreptivus", "<big>obreptivus</big>\n\n<b>obreptivus, a, um</b>\n<b>1)</b> тайный, совершаемый втихомолку (supplicatio <i>Symm</i>);\n<b>2)</b> <i>CTh</i> = obrepticius."],
  ["obrepto", "<big>obrepto</big>\n\n<b>obrepto, avi, —, are</b> [<i>intens. к</i> obrepo]\nподбираться, подкрадываться <i>Pl, PM</i>."],
  ["obretio", "<big>obretio</big>\n\n<b>obretio, —, —, ire</b> [ob + rete]\nопутывать сетями, <i>pass.</i> попасться в сети, запутаться <i>Lcr</i>."],
  ["obrigesco", "<big>obrigesco</big>\n\n<b>ob–rigesco, rigui, —, ere</b>\n<b>1)</b> твердеть, костенеть, цепенеть (frigore <i>LM</i>; nive pruinaque <i>C</i>); замерзать (per pruinam <i>Pac</i> <i>и</i> pruina <i>Vr</i>); застывать, замирать (timore <i>Aug</i>); (<i>о мертвеце</i>) коченеть (commodum ille anima edita obriguerat <i>Ap</i>);\n<b>2)</b> <i>перен.</i> закаляться <i>Sen</i>."],
  ["Obrimas", "<big>Obrimas</big>\n\n<b>Obrimas, ae</b> <i>m</i>\nОбрим, <i>приток реки Меандр во Фригии</i> <i>L, PM</i>."],
  ["obroboratio", "<big>obroboratio</big>\n\n<b>ob–roboratio, onis</b> <i>f</i>\nзатвердение, ригидность (сухожилий) <i>Veg</i>."],
  ["obrodo", "<big>obrodo</big>\n\n<b>ob–rodo, —, —, ere</b>\nобгрызать, обгладывать (ossa <i>Tert</i>); жевать (<i>sc.</i> malum <i>Pl</i>)."],
  ["obrogatio", "<big>obrogatio</big>\n\n<b>obrogatio, onis</b> <i>f</i> [obrogo]\nвнесение законопроекта во изменение (<i>или</i> в отмену) действующего закона <i>rhH</i>."],
  ["obrogo", "<big>obrogo</big>\n\n<b>ob–rogo, avi, atum, are</b>\n<b>1)</b> изменять <i>или</i> заменять существующий закон новым (lex nova antiquae — <i>dat.</i> — obrogat <i>L</i>);\n<b>2)</b> возражать против законопроекта (o. legibus <i>Fl, AV</i>)."],
  ["obrubesco", "<big>obrubesco</big>\n\n<b>ob–rubesco, —, —, ere</b>\nрумяниться, краснеть (pudore <i>Aug</i>)."],
  ["obructo", "<big>obructo</big>\n\n<b>ob–ructo, —, —, are</b>\nрыгать, отрыгивать (alicui <i>Ap</i>)."],
  ["obruo", "<big>obruo</big>\n\n<b>ob–ruo, rui, rutum, ere</b>\n<b>1)</b> заваливать, закидывать, забрасывать, засыпать (aliquem telis <i>V</i>, lapidibus <i>C</i>; corpus terra <i>Just</i>); покрывать (nive perenni obruta juga <i>QC</i>; o. terram umbris <i>Lcr</i>); закапывать, зарывать (thesaurum <i>C</i>); погружать (puppes <i>V</i>): obrutus insanis aquis <i>O</i> потонувший в бурных водах; fluctibus obrui <i>Su</i> утонуть || окутывать, завёртывать (aliquem veste <i>PM</i>; obruto tunicula capite <i>Pt</i>);\n<b>2)</b> сеять (milium <i>Col</i>; semina sulcis <i>O</i>);\n<b>3)</b> затмевать (famam alicujus <i>T</i>); изглаживать, уничтожать (nomen alicujus <i>C</i>; aliquid perpetua oblivione <i>C</i>): spatio obruta memoria <i>SenT</i> воспоминание, изгладившееся от времени;\n<b>4)</b> перегружать, чрезмерно нагружать, обременять: o. vino mentis calorem <i>Pt</i> топить в вине жар души; vinoepulisque obrui <i>Nep</i> пить и есть свыше меры;\n<b>5)</b> подавлять (curae animum obruunt <i>Q</i>; obrui tot malis <i>CC</i>): o. ventos otio <i>H</i> унять ветры; obrui aere alieno <i>C</i> завязнуть в долгах; obrutus somno <i>St</i> погружённый в сон; o. vulneribus <i>Just</i> весь в ранах; o. aliquem criminibus <i>C</i> осыпать кого-л. обвинениями; obrui sceleribus <i>L</i> погрязнуть в преступлениях; obrui in augenda re <i>H</i> потерпеть крушение в погоне за наживой; risus aliquem obruit <i>C</i> кто-л.. покатывается со смеху;\n<b>6)</b> превосходить (aliquem dignatione <i>VP</i>): obrui numero <i>V</i> быть подавляемым численно."],
  ["obrussa", "<big>obrussa</big>\n\n<b>obrussa, ae</b> <i>f</i> (<i>греч.</i>)\n<b>1)</b> испытание золота на огне: aurum ad obrussam <i>Su</i> чистое золото; aliquid ad obrussam exigere <i>перен.</i> <i>Sen</i> подвергнуть что-л. испытанно (проверке);\n<b>2)</b> чистое (<i>или</i> высокопробное) золото (ex obrussa esse <i>Pt</i>)."],
  ["obryza", "<big>obryza</big>\n\n<b>obryza, ae</b> <i>f</i> <i>CTh</i>\n= obryzum."],
  ["obryzatus", "<big>obryzatus</big>\n\n<b>obryzatus, obryziacus, a, um</b>\nиз чистого золота <i>CJ</i>."],
  ["obryzum", "<big>obryzum</big>\n\n<b>obryzum, i</b> <i>n</i> (<i>греч.</i>) <i>Is</i>\n= obrussa 2."],
  ["obryzus", "<big>obryzus</big>\n\n<b>obryzus, a, um</b>\nочищенный на огне (aurum <i>Vlg</i>)."],
  ["obs-", "<big>obs-</big>\n\n<b>obs-</b> <i>v. l.</i>\n= ob-."],
  ["obsaepio", "<big>obsaepio</big>\n\n<b>ob–saepio, saepsi, saeptum, ire</b>\n<b>1)</b> загораживать, загромождать (agmina obsaepiunt iter <i>L</i>); преграждать (alicui iter ad curules magistratus <i>L</i>);\n<b>2)</b> затыкать (ora <i>PJ</i>)."],
  ["obsaeptio", "<big>obsaeptio</big>\n\n<b>obsaeptio, onis</b> <i>f</i>\nпреграждение <i>Aug</i>."],
  ["obsaturo", "<big>obsaturo</big>\n\n<b>ob–saturo, —, —, are</b>\nнасыщать, пресыщать: propediem istius obsaturabere <i>Ter</i> он скоро тебе надоест."],
  ["obscaen-", "<big>obscaen-</big>\n\n<b>obscaen-</b> <i>v. l.</i>\n= obscen-."],
  ["obscaevo", "<big>obscaevo</big>\n\n<b>obscaevo, avi, —, are</b> [ob + scaeva]\nподавать недобрый знак, быть дурным предзнаменованием (alicui rei <i>Pl</i>)."],
  ["obscena", "<big>obscena</big>\n\n<b>obscena, orum</b> <i>n</i>\n<b>1)</b> непристойные поступки <i>или</i> слова <i>O, Q, Sen</i>;\n<b>2)</b> половые органы <i>O, Mela, Su</i>;\n<b>3)</b> задняя часть тела <i>Sen</i>;\n<b>4)</b> извержения <i>O, Mela</i>."],
  ["obscene", "<big>obscene</big>\n\n<b>obscene</b> [obscenus]\nнепристойно, неприлично (dicere <i>C</i>); распутно, безнравственно (impudissime et obscenissime vivere <i>Eutr</i>)."],
  ["obscenitas", "<big>obscenitas</big>\n\n<b>obscenitas, atis</b> <i>f</i> [obscenus]\n<b>1)</b> непристойность, неприличие, зазорность (verborum, rerum <i>C</i>);\n<b>2)</b> <i>pl.</i> неприличные изображения <i>PM, Eccl</i>;\n<b>3)</b> неблагоприятность, тревожность (ominis <i>Eccl</i>)."],
  ["obscenum", "<big>obscenum</big>\n\n<b>obscenum, i</b> <i>n</i> [obscenus]\nполовой орган (o. virile <i>O, Lact</i>)."],
  ["obscenus", "<big>obscenus</big>\n\n<b>obscenus, a, um</b> [ob + caenum]\n<b>1)</b> отвратительный, противный, гадкий (avis <i>PM</i>): vas obscenum <i>Sen</i> = matella;\n<b>2)</b> непристойный, неприличный, зазорный (gestus <i>T</i>; sermo <i>H</i>; verbum <i>C</i>);\n<b>3)</b> безнравственный, развратный (vir <i>L</i>; puella <i>Sen</i>);\n<b>4)</b> злосчастный, страшный, зловещий, предвещающий беду (omen <i>C</i>; volucres <i>V</i>; cruor <i>V</i>)."],
  ["obscuratio", "<big>obscuratio</big>\n\n<b>obscuratio, onis</b> <i>f</i> [obscuro]\n<b>1)</b> затмение (solis <i>C, Vtr</i>; lunae <i>Sen</i>);\n<b>2)</b> тьма <i>bH</i>;\n<b>3)</b> помрачение, исчезновение, неощутимость (voluptatum <i>C</i>)."],
  ["obscure", "<big>obscure</big>\n\n<b>obscure</b> [obscurus]\n<b>1)</b> темно, втёмную (cernere <i>C</i>);\n<b>2)</b> неясно, туманно (dicere <i>C, Q</i>);\n<b>3)</b> тайно, скрыто, незаметно (tacite obscureque perire <i>C</i>);\n<b>4)</b> незнатно, в безвестности (o. natus <i>Eutr, Amm</i>)."],
  ["obscuriloquium", "<big>obscuriloquium</big>\n\n<b>obscuri–loquium, i</b> <i>n</i>\nзагадочное выражение <i>Is</i>."],
  ["obscuritas", "<big>obscuritas</big>\n\n<b>obscuritas, atis</b> <i>f</i> [obscurus]\n<b>1)</b> тьма, темнота, мрак (latebrarum <i>T</i>);\n<b>2)</b> помрачение, потемнение, затуманивание (visus, oculorum <i>PM</i>);\n<b>3)</b> туманность, неясность, непонятность (verborum, naturae <i>C</i>);\n<b>4)</b> неизвестность, безвестность, незнатность (generis <i>Fl</i>)."],
  ["obscuro", "<big>obscuro</big>\n\n<b>obscuro, avi, atum, are</b> [obscurus]\n<b>1)</b> затемнять, затмевать (sol obscuratur <i>VM</i>); ослаблять яркость (o. lumen lucernae <i>C</i>): o. caelum nocte <i>Sl</i> покрыть небо ночной тенью || <i>pass.</i> тускнеть, меркнуть (sidera obscurantur <i>PM</i>);\n<b>2)</b> скрывать, утаивать (magnitudinem periculi <i>C</i>);\n<b>3)</b> приглушать, проглатывать, неясно произносить (vocem <i>Q</i>; litteram <i>C</i>);\n<b>4)</b> туманно излагать, затуманивать (aliquid dicendo <i>C</i>);\n<b>5)</b> оставлять в тени, затмевать (veteres triumphus <i>Lcn</i>); затушёвывать (nomina <i>Enn ap. Macr</i>);\n<b>6)</b> изглаживать из памяти, приводить в забвение, делать неизвестным (obscurata vocabula <i>H</i>): memoria alicujus rei obscuratur <i>C</i> память о чём-л. исчезает;\n<b>7)</b> закутывать, прятать (caput lacerna <i>H</i>);\n<b>8)</b> помрачать, дурманить (amor pectus obscuravit alicui <i>Pt</i>)."],
  ["obscurum", "<big>obscurum</big>\n\n<b>obscurum, i</b> <i>n</i> [obscurus]\n<b>1)</b> тьма, мрак, тень (noctis <i>V, T etc.</i>);\n<b>2)</b> безвестность, тишь: vitam transmittere per o. <i>Sen</i> <i>или</i> in obscuro vitam agere <i>Sl</i> жить в безвестности."],
  ["obscurus", "<big>obscurus</big>\n\n<b>obscurus, a, um</b>\n<b>1)</b> тёмный (lucus <i>V</i>; nox <i>Sl</i>; umbra <i>Enn ap. C</i>): ibant obscuri <i>V</i> они шли во тьме || мрачный (caelum <i>H, V, Sil</i>); меркнущий, гаснущий (luna <i>V</i>); мутный (aqua <i>O</i>); сумеречный: lux obscura <i>L, QC</i> <i>и</i> lumen obscurum <i>Sl</i> сумрак, сумерки;\n<b>2)</b> приглушённый, глухой (vox <i>Q</i>);\n<b>3)</b> скрытый (odium <i>C</i>); укрытый, потаённый, невидимый, незаметный (locus <i>C</i>): obscuro gradu <i>Sen</i> неслышным шагом || замаскированный (forma <i>O</i>); тайный (taberna <i>H</i>);\n<b>4)</b> неясный, непонятный, загадочный, тёмный (oraculum <i>C</i>; carmen <i>O</i>);\n<b>5)</b> скрытный (adversus aliquem <i>T</i>);\n<b>6)</b> неизвестный, безвестный, незнатный (obscuris parentibus <i>и</i> obscuro loco natus <i>C</i>);\n<b>7)</b> смутный, неопределённый, неясный (spes <i>C</i>)."],
  ["obsecratio", "<big>obsecratio</big>\n\n<b>obsecratio, onis</b> <i>f</i> [obsecro]\n<b>1)</b> настоятельные просьбы, мольба, заклинание (obsecratione humili uti <i>C</i>; obsecrationem alicujus repudiare <i>C</i>);\n<b>2)</b> публичное молебствие (obsecrationem facere <i>L</i>);\n<b>3)</b> торжественная клятва (obsecrationibus se obligare <i>Just</i>)."],
  ["obsecro", "<big>obsecro</big>\n\n<b>obsecro, avi, atum, are</b> [ob + sacro]\nнастойчиво просить, молить, умолять, заклинать (aliquem cum multis lacrimis <i>C</i>; per amicitiam <i>Ter</i>); <i>вводно</i> obsecro будь добр, сделай милость, прошу (oro, obsecro, ignosco <i>C</i>) <i>или возглас удивления</i> скажи, пожалуйста! (obsecro, quem video <i>Ter</i>)."],
  ["obsecundatio", "<big>obsecundatio</big>\n\n<b>obsecundatio, onis</b> <i>f</i> [obsecundo]\nуступчивость, послушание <i>CJ, Eccl</i>."],
  ["obsecundator", "<big>obsecundator</big>\n\n<b>obsecundator, oris</b> <i>n</i>\nслуга, служитель (sacrorum scriniorum <i>CTh</i>)."],
  ["obsecundo", "<big>obsecundo</big>\n\n<b>ob–secundo, avi, atum, are</b>\nблагоприятствовать, соглашаться, уступать (in loco <i>Ter</i>; alicui <i>C</i>; voluntatibus alicujus <i>C</i>)."],
  ["obsecutor", "<big>obsecutor</big>\n\n<b>obsecutor, oris</b> <i>m</i>\nподчиняющийся (o. legis <i>Tert</i>)."],
  ["obsecutus", "<big>obsecutus</big>\n\n<b>obsecutus, a, um</b> <i>part. pf. к</i>\nobsequor."],
  ["obsedi", "<big>obsedi</big>\n\n<b>obsedi</b> <i>pf. к</i>\n<b>1)</b> obsideo <i>и</i>\n<b>2)</b> obsido."],
  ["obsepio", "<big>obsepio</big>\n\n<b>obsepio</b> <i>v. l.</i>\n= obsaepio."],
  ["obsequela", "<big>obsequela</big>\n\n<b>obsequela, ae</b> <i>f</i> [obsequor]\n<b>1)</b> уступчивость, услужливость <i>Eccl</i>;\n<b>2)</b> одолжение (obsequelam facere alicui <i>Pl</i> — <i>v. l.</i>)."],
  ["obsequens 1", "<big>obsequens</big>\n\n<b>I obsequens, entis</b>\n<b>1.</b> <i>part. praes. к</i> obsequor;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> уступчивый, послушный, услужливый (alicui <i>Ter, Sen</i>);\n<b>2)</b> снисходительный, милостивый (Fortuna <i>Pl</i>)."],
  ["Obsequens 2", "<big>Obsequens</big>\n\n<b>II Obsequens, entis</b> <i>m</i>\n(Julius) Обсеквент, <i>писатель IV в. н. э., автор &laquo;Liber prodigiorum&raquo;.</i>"],
  ["obsequenter", "<big>obsequenter</big>\n\n<b>obsequenter</b> [obsequens]\nуступчиво, услужливо, в угоду (aliquid facere <i>L</i>): alicui obsequentissime vivere <i>PJ</i> жить, самоотверженно ухаживая за кем-л."],
  ["obsequentia", "<big>obsequentia</big>\n\n<b>obsequentia, ae</b> <i>f</i> [obsequens]\nуступчивость, услужливость <i>Cs</i>."],
  ["obsequibilis", "<big>obsequibilis</big>\n\n<b>obsequibilis, e</b> [obsequor]\nуступчивый, услужливый <i>AG</i>."],
  ["obsequiosus", "<big>obsequiosus</big>\n\n<b>obsequiosus, a, um</b> <i>Pl</i>\n= obsequibilis."],
  ["obsequium", "<big>obsequium</big>\n\n<b>obsequium, i</b> <i>n</i> [obsequor]\n<b>1)</b> послушание, подчинение, покорность: obsequio tranantur aquae <i>O</i> — <i>см.</i> trano 1 || уступчивость, услужливость (in <i>или</i> erga aliquem <i>L, T, C</i>): jurare in alicujus o. <i>Just</i> присягать на верность кому-л.;\n<b>2)</b> (<i>тж.</i> o. deforme <i>T</i>) раболепие, угодливость (in regem <i>L</i>);\n<b>3)</b> потворство: o. ventris <i>H</i> страсть к пирам, чревоугодие; o. desiderii <i>Sen</i> исполнение желания; o. animo sumere <i>Pl</i> жить в своё удовольствие; obsequia fortunae <i>QC</i> благоприятная судьба, удачи, преуспеяние; o. corporis <i>C</i> физические наслаждения, <i>но тж.</i> <i>QC</i> половой разврат; o. aquarum <i>O</i> попутное течение;\n<b>4)</b> ласка, кротость (o. tigresque domat Numidasque leones <i>O</i>);\n<b>5)</b> податливость (flectitur obsequio ramus <i>O</i>);\n<b>6)</b> половая связь (o. feminae <i>Col</i>);\n<b>7)</b> служба вестового <i>или</i> ординарца <i>Capit, Veg</i>;\n<b>8)</b> <i>pl.</i> клиенты <i>или</i> свита <i>Ap, Eccl</i>."],
  ["obsequor", "<big>obsequor</big>\n\n<b>ob–sequor, quutus (cutus) sum, sequi</b> <i>depon.</i>\n<b>1)</b> уступать, оказывать услуги, быть услужливым (senibus <i>Ter</i>); слушаться, следовать (voluntati alicujus <i>C</i>; imperio <i>J</i>; naturae <i>C</i>);\n<b>2)</b> почитать, оказывать почести (regibus <i>Eutr</i>);\n<b>3)</b> отдаваться, предаваться (studiis suis <i>Nep</i>): animo o. <i>Ter</i> следовать своим склонностям;\n<b>4)</b> поддаваться, быть податливым (aes malleis obsequitur <i>PM</i>)."],
  ["obsequu-", "<big>obsequu-</big>\n\n<b>obsequu-</b> <i>v. l.</i>\n= obsecu-."],
  ["obsericatus", "<big>obsericatus</big>\n\n<b>ob–sericatus, a, um</b>\nвесь в шелку <i>Aug</i>."],
  ["obsero 1", "<big>obsero</big>\n\n<b>I ob–sero, avi, atum, are</b>\n<b>1)</b> запирать на засов (fores <i>Su</i>; ostium <i>Ter</i>; aedificium <i>L</i>);\n<b>2)</b> закрывать, смыкать: o. palatum <i>Ctl</i> смыкать уста, молчать; o. aures <i>H</i> затыкать уши, не слушать."],
  ["obsero 2", "<big>obsero</big>\n\n<b>II ob–sero, sevi, situm, ere</b>\n<b>1)</b> засевать, засаживать (terram frugibus <i>C</i>; arbusta nata, non obsita <i>Naev</i>);\n<b>2)</b> (на)делать, причинять (aerumnam gravem in aliquem <i>Pl</i>): o. pugnos (<i>sc.</i> in ore) <i>шутл. Pl</i> побить. — <i>См. тж.</i> obsitus."],
  ["observabilis", "<big>observabilis</big>\n\n<b>observabilis, e</b> [observo]\n<b>1)</b> заметный <i>Q, Sen</i>;\n<b>2)</b> замечательный (patientia <i>Ap</i>)."],
  ["observans", "<big>observans</big>\n\n<b>observans, antis</b>\n<b>1.</b> <i>part. praes. к</i> observo;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> в точности исполняющий, соблюдающий (o. officiorum <i>PJ</i>);\n<b>2)</b> относящийся с уважением, уважающий (alicujus <i>C, PJ</i>)."],
  ["observanter", "<big>observanter</big>\n\n<b>observanter</b>\n<b>1)</b> внимательно, старательно, тщательно (omnia facere <i>Ap</i>);\n<b>2)</b> почтительно, благоговейно (ad precandum accedere <i>Lact</i>)."],
  ["observantia", "<big>observantia</big>\n\n<b>observantia, ae</b> <i>f</i> [observans]\n<b>1)</b> наблюдение (temporum <i>VP</i>);\n<b>2)</b> соблюдение (morum <i>VM</i>; legum <i>Dig</i>); исполнение (catholicae observantiae fides <i>CTh</i>);\n<b>3)</b> почтительность, почтение, уважение (in aliquem <i>L</i> <i>или</i> alicujus <i>C</i>)."],
  ["observate", "<big>observate</big>\n\n<b>observate</b> [observo]\nпристально, внимательно наблюдая (o. curioseque <i>AG</i>)."],
  ["observatio", "<big>observatio</big>\n\n<b>observatio, onis</b> <i>f</i> [observo]\n<b>1)</b> наблюдение (siderum <i>C</i>): observationi operam dare <i>Pl</i> заняться наблюдением;\n<b>2)</b> наблюдательность <i>PM</i>;\n<b>3)</b> тщательность, добросовестность <i>или</i> осмотрительность (in bello movendo <i>C</i>); соблюдение: o. dierum <i>AG</i> соблюдение сроков;\n<b>4)</b> замечание, наставление, правило (sermonis antiqui <i>Su</i>);\n<b>5)</b> уважение, почитание (religionibus observationem reddere <i>VM</i>); благочестие (o. divina <i>CTh</i>)."],
  ["observator", "<big>observator</big>\n\n<b>observator, oris</b> <i>m</i> [observo]\nнаблюдатель (malorum bonorumque <i>Sen</i>); хранитель, блюститель (Latini sermonis <i>Sen</i>); соблюдающий, приверженец (catholicae legis <i>CJ</i>)."],
  ["observatrix", "<big>observatrix</big>\n\n<b>observatrix, icis</b> <i>f</i>\nсоблюдающая (fides o. <i>Tert</i>)."],
  ["observatus", "<big>observatus</big>\n\n<b>observatus, (us) </b> <i>m</i> [observo]\nнаблюдение (ex observatu dicere <i>Vr</i>)."],
  ["observito", "<big>observito</big>\n\n<b>observito, avi, —, are</b> [<i>intens. к</i> observo]\n<b>1)</b> пристально наблюдать (motus stellarum <i>C</i>);\n<b>2)</b> соблюдать (caerimonias <i>AG</i>)."],
  ["observo", "<big>observo</big>\n\n<b>ob–servo, avi, atum, are</b>\n<b>1)</b> внимательно следить, наблюдать (motus stellarum <i>C</i>); выслеживать, высматривать (vestigia oculis <i>Sl</i>); подстерегать, выжидать (aliquem <i>Pl, C</i>): o. et insidiari <i>C</i> быть настороже;\n<b>2)</b> улучить (tempus epistulae alicui reddendae <i>C</i>);\n<b>3)</b> сторожить, охранять, оберегать (januam <i>Pl</i>; greges <i>O</i>);\n<b>4)</b> соблюдать (leges <i>C</i>); выполнять (imperium <i>Sl</i>; foedus <i>Sil</i>);\n<b>5)</b> уважать, почитать, чтить (o. et colere aliquem <i>C</i>)."],
  ["obses", "<big>obses</big>\n\n<b>obses, idis</b> (<i>gen. pl.</i> um) <i>m</i> (<i>иногда</i> <i>f</i>) [ob + sedeo]\n<b>1)</b> заложник (obsides poscere, accipere <i>Cs</i>, retinere <i>Nep</i>);\n<b>2)</b> поручитель, гарант (sponsor et o. conjugii <i>O</i>): alicujus rei obsidem esse <i>Nep</i> ручаться за что-л.;\n<b>3)</b> залог, ручательство, порука: o. perpetuae in rem publicam voluntatis <i>C</i> залог неизменной преданности государству."],
  ["obsesse", "<big>obsesse</big>\n\n<b>obsesse</b> <i>Acc</i>\n= obsevisse (<i>inf. pf. к</i> obsero II)."],
  ["obsessio", "<big>obsessio</big>\n\n<b>obsessio, onis</b> <i>f</i> [obsideo]\n<b>1)</b> обложение, осада, блокада (castrorum <i>Su</i>);\n<b>2)</b> преграждение (viae <i>C</i>)."],
  ["obsessor", "<big>obsessor</big>\n\n<b>obsessor, oris</b> <i>m</i> [obsideo]\n<b>1)</b> осаждающий (obsessi et obsessores <i>T</i>);\n<b>2)</b> житель, обитатель (aquarum <i>O</i>)."],
  ["obsessus", "<big>obsessus</big>\n\n<b>obsessus, a, um</b> <i>part. pf. к</i>\n<b>1)</b> obsideo <i>и</i>\n<b>2)</b> obsido."],
  ["obsevi", "<big>obsevi</big>\n\n<b>obsevi</b> <i>pf. к</i>\nobsero II."],
  ["obsibilo", "<big>obsibilo</big>\n\n<b>ob–s&#299;bilo, —, —, &#257;re</b>\nсвистеть, шелестеть: arbores dulces strepitus obsibilabant <i>Ap</i> деревья нежно шелестели."],
  ["obsidatus", "<big>obsidatus</big>\n\n<b>obsid&#257;tus, &#363;s</b> <i>m</i> [obses]\nзаложничество <i>Amm</i>."],
  ["obsideo", "<big>obsideo</big>\n\n<b>obsideo, s&#275;d&#299;, sessum, &#275;re</b> [ob + sedeo]\n<b>1)</b> обитать, населять, жить (ranae stagna obsident <i>PM</i>);\n<b>2)</b> завладевать, владеть (Asiam <i>Sl</i>; <i>перен.</i> o. aures alicujus <i>L</i>): ira feras mentes obsidet, eruditas praelabitur <i>Pt</i> гнев прочно овладевает грубыми душами, но (быстро) соскальзывает с просвещённых || занимать, оккупировать (Italiam praesidiis <i>C</i>);\n<b>3)</b> наполнять, покрывать (palus salictis obsessa <i>O</i>);\n<b>4)</b> теснить, притеснять, стеснять (aliquem omnibus rebus <i>C</i>); ограничивать, суживать (tribunatum, tempus <i>C</i>);\n<b>5)</b> осадить, обложить, блокировать (oppidum <i>Sl, AV</i>; aliquem armis <i>C, Sl</i>; obsessa via <i>Sen</i>): faucibus obsessis <i>L</i> блокировав проходы;\n<b>6)</b> подстерегать, высматривать, выслеживать (rostra <i>C</i>): o. aliquid <i>C</i> выжидать удобный случай для чего-л.;\n<b>7)</b> задерживаться, оставаться (domi <i>Ter</i>; in limine <i>VF</i>)."],
  ["obsidialis", "<big>obsidialis</big>\n\n<b>obsidi&#257;lis, e</b> <i>L</i>\n= obsidionalis."],
  ["obsidio", "<big>obsidio</big>\n\n<b>obsidio, &#333;nis</b> <i>f</i> [obsideo]\n<b>1)</b> обложение, осада (urbis <i>L etc.</i>): urbem obsidione cingere <i>V, Just</i> (claudere <i>Nep</i>, premere <i>V, L</i>) осадить город, держать город в осаде; solvere <i>L</i> (omittere <i>T</i>) obsidionem снять осаду;\n<b>2)</b> бедственное положение, беда, опасность (rem publicam liberare obsidione <i>C</i>; ex obsidione aliquem eximere <i>C</i>);\n<b>3)</b> пленение, плен (filium obsidione liberare <i>Just</i>)."],
  ["obsidionalis", "<big>obsidionalis</big>\n\n<b>obsidi&#333;n&#257;lis, e</b> [obsidio]\nосадный, относящийся к осаде (machinae <i>Amm</i>): corona o. <i>L, AV, AG</i> венок за освобождение от осады."],
  ["obsidior", "<big>obsidior</big>\n\n<b>obsidior, —, &#257;r&#299;</b> <i>depon.</i>\nустраивать засаду (alicui <i>Col</i>)."],
  ["obsidium 1", "<big>obsidium</big>\n\n<b>I obsidium, &#299;</b> <i>n</i> [obsideo]\n<b>1)</b> обложение, осада (o. facere Ilio <i>Pl</i>);\n<b>2)</b> засада, козни (hominum et animalium <i>Col</i>);\n<b>3)</b> надзор, наблюдение (curatoris <i>Col</i>);\n<b>4)</b> <i>ирон.</i> опасность (tergo alicujus o. adest <i>Pl</i>)."],
  ["obsidium 2", "<big>obsidium</big>\n\n<b>II obsidium, &#299;</b> <i>n</i> [obses]\nположение заложника: obsidio alicui dari <i>T</i> быть выданным кому-л. в качестве заложника."],
  ["obsido", "<big>obsido</big>\n\n<b>ob–s&#299;do, s&#275;d&#299;, sessum, ere</b>\nзанимать, захватывать, завладевать (pontem <i>Sl</i>; fauces <i>V</i>; moenia Troica <i>Ctl</i>); оцеплять (portas <i>St</i>); обложить, осадить (milite campos <i>V</i>)."],
  ["obsignatio", "<big>obsignatio</big>\n\n<b>obs&#299;gn&#257;tio, &#333;nis</b> <i>f</i> [obsigno]\nзапечатывание, приложение <i>или</i> наложение печати (o. tabularum <i>AG</i>)."],
  ["obsignator", "<big>obsignator</big>\n\n<b>obs&#299;gn&#257;tor, &#333;ris</b> <i>m</i> [obsigno]\nприкладывающий печать (litterarum o. <i>C</i>): o. testamenti <i>C</i> свидетель, прикладывающий свою печать к завещанию."],
  ["obsigno", "<big>obsigno</big>\n\n<b>ob–s&#299;gno, &#257;v&#299;, &#257;tum, &#257;re</b>\n<b>1)</b> запечатывать (anulo litteras <i>QC</i>); скреплять приложением печати (testamentum <i>C, Cs, Su</i>): agere tabellis obsignatis cum aliquo <i>шутл. C</i> поступать с кем-л. согласно его же утверждениям, <i>т. е.</i> поймав его на слове;\n<b>2)</b> опечатывать (bona alicujus obsignari jubere <i>T</i>);\n<b>3)</b> подписать обвинение (contra aliquem <i>C</i>);\n<b>4)</b> заложить, формально передать (o. tria agri jugera ad aerarium <i>VM</i>);\n<b>5)</b> запечатлевать, придавать (formam verbis <i>Lcr</i>)."],
  ["obsipo", "<big>obsipo</big>\n\n<b>ob–sipo, —, —, &#257;re</b> [<i>одного корня с</i> supo <i>и</i> dissipo]\nобрызгивать, окроплять: o. aquulam <i>погов. Pl</i> освежать, подкреплять, придавать духу."],
  ["obsisto", "<big>obsisto</big>\n\n<b>ob–sisto, stit&#299;, stitum, ere</b>\n<b>1)</b> становиться поперёк пути, загораживать дорогу (alicui abeunti <i>L</i>);\n<b>2)</b> противостоять, противодействовать, сопротивляться (omnibus ejus consiliis <i>C</i>): peccaturo obsistat tibi filius infans <i>J</i> пусть мысль о (твоём) маленьком сыне удержит тебя от дурных поступков || возражать, бороться, не соглашаться (opinionibus <i>C</i>): alicujus famae o. <i>L</i> омрачать чью-л. славу. — <i>См. тж.</i> obstita <i>и</i> obstitus."],
  ["obsitus", "<big>obsitus</big>\n\n<b>obsitus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> obsero II;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> поросший (arbustis <i>Lcr</i>; spinis et pilis <i>Ap</i>);\n<b>2)</b> покрытый (montes nivibus obsiti <i>QC</i>): o. annis <i>Ter</i> <i>или</i> aevo <i>V</i> престарелый."],
  ["obsolefacio", "<big>obsolefacio</big>\n\n<b>obsolefacio, feci, factum, facere</b> [obsolesco + facio]\n<b>1)</b> приводить в ветхость, старить (toga obsolefacta <i>VM</i>);\n<b>2)</b> лишать значения, лишать блеска (auctoritas obsolefacta <i>Sen</i>)."],
  ["obsolefio", "<big>obsolefio</big>\n\n<b>obsolefio, factus sum, fieri</b> <i>pass. к</i>\nobsolefacio."],
  ["obsolesco", "<big>obsolesco</big>\n\n<b>obsolesco, levi, letum, ere</b> [ob + soleo]\n<b>1)</b> стареть, ветшать, выходить из употребления (vetustate <i>L</i>);\n<b>2)</b> устаревать, лишаться силы, терять значение (oratio obsolescit <i>C</i>; laus obsolevit <i>T</i>): claritudo arteriis obsolescit <i>Ap</i> ясность голоса утрачивается, <i>т. е.</i> появляется хрипота."],
  ["obsolete", "<big>obsolete</big>\n\n<b>obsolete</b> [obsoletus]\nв старом платье, бедно: o. vestitus <i>C</i> в поношенном платье."],
  ["obsoleto", "<big>obsoleto</big>\n\n<b>obsoleto, —, —, are</b>\nпачкать, марать (vestitum nuptialem <i>Tert</i>; <i>перен.</i> majestatis vestigia <i>Tert</i>)."],
  ["obsoletus", "<big>obsoletus</big>\n\n<b>obsoletus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> obsolesco;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> пришедший в ветхость, обветшалый (tectum <i>H</i>); старинный, устарелый (verba <i>C</i>); поношенный, потёртый, старый (vestis <i>L</i>);\n<b>2)</b> одетый в поношенное платье (homo <i>C</i>): obsoletissimus vestitu <i>Ap</i> в крайне ветхой одежде (<i>что было признаком траура</i>);\n<b>3)</b> запачканный (sanguine <i>SenT</i>; sordibus <i>H</i>);\n<b>4)</b> обыденный, повседневный, обычный, заурядный, простой (gaudium <i>L</i>; oratio <i>C</i>)."],
  ["obsolevi", "<big>obsolevi</big>\n\n<b>obsolevi</b> <i>pf. к</i>\nobsolesco."],
  ["obsolido", "<big>obsolido</big>\n\n<b>ob–solido, —, —, are</b>\nуплотнять, укреплять (rigide obsolidari <i>Vtr</i>)."],
  ["obsonator", "<big>obsonator</big>\n\n<b>obsonator, oris</b> <i>m</i> [obsono I]\n<b>1)</b> закупающий провизию, поставщик съестных припасов <i>Pl, Sen</i>;\n<b>2)</b> повар <i>Pt</i>."],
  ["obsonatus", "<big>obsonatus</big>\n\n<b>obsonatus, us</b> <i>m</i> [obsono I]\nзакупка съестных припасов <i>Pl</i>."],
  ["obsonito", "<big>obsonito</big>\n\n<b>obsonito, avi, —, are</b> [<i>frequ. к</i> obsono I]\nпировать <i>или</i> угощать <i>Cato</i>."],
  ["obsonium", "<big>obsonium</big>\n\n<b>obsonium, i</b> <i>n</i> [obsono I]\nзакуска (<i>преим.</i> рыба, овощи, <i>реже</i> мясо, плоды) <i>Pl, C etc.</i>; <i>pl.</i> рыбные продукты (obsonia captare <i>H</i>)."],
  ["obsono 1", "<big>obsono</big>\n\n<b>I obsono, avi, atum, are</b> (<i>греч.</i>)\n<b>1)</b> закупать продовольствие (obsonatum mittere <i>Pl</i>): o. ambulando famem <i>C</i> нагулять аппетит;\n<b>2)</b> задавать пир, угощать <i>Ter</i>."],
  ["obsono 2", "<big>obsono</big>\n\n<b>II ob–sono, —, —, are</b>\nвмешиваться, прерывать: o. alicui sermone, <i>v. l.</i> alicujus sermoni <i>Pl</i> перебивать чью-л. речь."],
  ["obsonor", "<big>obsonor</big>\n\n<b>obsonor, atus sum, ari</b> <i>depon.</i> <i>Pl</i>\n= obsono I."],
  ["obsopio", "<big>obsopio</big>\n\n<b>ob–sopio, ivi, itum, ire</b>\nусыплять, оглушать (aliquem <i>Scr, Sol</i>)."],
  ["obsorbeo", "<big>obsorbeo</big>\n\n<b>ob–sorbeo, sorbui, —, ere</b>\nприхлёбывать, потягивать, глотать (aquam <i>Pl</i>)."],
  ["obsordesco", "<big>obsordesco</big>\n\n<b>ob–sordesco, dui, —, ere</b>\n<b>1)</b> становиться грязным (fumo <i>Eccl</i>);\n<b>2)</b> утрачивать былой блеск, тускнеть (Libanus obsorduit <i>Vlg</i>)."],
  ["obstaculum", "<big>obstaculum</big>\n\n<b>obstaculum, i</b> <i>n</i> [obsto]\nпрепятствие, преграда <i>Sen, Ap, Amm, Eccl</i>."],
  ["obstantia 1", "<big>obstantia</big>\n\n<b>I obstantia, ae</b> <i>f</i> [obsto]\n<b>1)</b> <i>астр.</i> противостояние (terrae <i>Vtr</i>);\n<b>2)</b> препятствие (liberare ab obstantia <i>Vtr</i>);\n<b>3)</b> сопротивление (aeris <i>Vtr</i>)."],
  ["obstantia 2", "<big>obstantia</big>\n\n<b>II obstantia, ium</b> <i>n</i> [obsto]\nпрепятствия, помехи (silvarum <i>T</i>)."],
  ["obstaturus", "<big>obstaturus</big>\n\n<b>obstaturus, a, um</b> <i>Sen, Q, St part. fut. act. к</i>\nobsto."],
  ["obsterno", "<big>obsterno</big>\n\n<b>ob–sterno, —, —, ere</b>\nбросать (перед), подбрасывать (alicui aliquam <i>Ap</i>)."],
  ["obstetricia", "<big>obstetricia</big>\n\n<b>obstetricia, orum</b> <i>n</i> [obstetrix]\nродовспомогательное искусство, акушерская помощь <i>PM</i>."],
  ["obstetricius", "<big>obstetricius</big>\n\n<b>obstetricius, a, um</b> [obstetrix]\nродовспомогательный <i>Eccl, Fronto</i>."],
  ["obstetrico", "<big>obstetrico</big>\n\n<b>obstetrico, avi, —, are</b>\nзаниматься родовспоможением: obstetricandi scientia <i>Vlg</i> родовспомогательное искусство; o. aliquam <i>Vlg</i> принимать новорождённого у роженицы."],
  ["obstetrix", "<big>obstetrix</big>\n\n<b>obstetrix, icis</b> (<i>gen. pl.</i> um) <i>f</i> [obsto]\nповивальная бабка <i>Pl, Ter, H etc.</i>"],
  ["obstinate", "<big>obstinate</big>\n\n<b>obstinate</b> [obstinatus]\nупорно, упрямо (negare <i>Cs</i>); твёрдо (credere <i>L</i>)."],
  ["obstinatio", "<big>obstinatio</big>\n\n<b>obstinatio, onis</b> <i>f</i> [obstino]\nупорство, упрямство (animi <i>Sen</i>); твёрдость, непоколебимость (sententiae <i>C</i>): o. taciturna <i>Nep</i> упорное молчание."],
  ["obstinatus", "<big>obstinatus</big>\n\n<b>obstinatus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> obstino;\n<b>2.</b> <i>adj.</i> упорный, упрямый (silentium <i>L</i>); твёрдый, непоколебимый (animus <i>L</i>); упорствующий, упорно сопротивляющийся (contra <i>или</i> adversus aliquid <i>Q, L etc.</i>); твёрдо решившийся (o. mori <i>L</i>; o. ad resistendum <i>Su</i>): o. pro aliquo <i>L</i> преданный кому-л.; obstinatum est mihi <i>PJ</i> я твёрдо решился."],
  ["obstino", "<big>obstino</big>\n\n<b>obstino, avi, atum, are</b> [ob + sto]\nпринимать непреклонное решение, твёрдо решать(ся) (aliquid <i>Pl</i> <i>или</i> ad aliquid <i>T</i>; o. vincere aut mori <i>L</i>)."],
  ["obstipesco", "<big>obstipesco</big>\n\n<b>obstipesco</b> <i>Pl, Ter, C</i>\n= obstupesco."],
  ["obstipus", "<big>obstipus</big>\n\n<b>obstipus, a, um</b> [stipo]\n<b>1)</b> наклонённый, склонённый набок (caput <i>C, H, Pers</i>);\n<b>2)</b> закинутый назад (cervix <i>Su</i>)."],
  ["obstita", "<big>obstita</big>\n\n<b>obstita, orum</b> <i>n</i> [obsisto] <i>культ.</i>\nпредметы, поражённые молнией (obstita pianto <i>lex ap. C</i>)."],
  ["obstiti", "<big>obstiti</big>\n\n<b>obstiti</b> <i>pf. к</i>\n<b>1)</b> obsisto <i>и</i>\n<b>2)</b> obsto."],
  ["obstitrix", "<big>obstitrix</big>\n\n<b>obstitrix, icis</b> <i>f</i> <i>v. l.</i>\n= obstetrix."],
  ["obstitum", "<big>obstitum</big>\n\n<b>obstitum, i</b> <i>n</i> [obsisto]\nнаклон, косое направление <i>Ap</i>."],
  ["obstitus", "<big>obstitus</big>\n\n<b>obstitus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> obsisto;\n<b>2.</b> боковой:radii solis obstiti <i>Ap</i> косые лучи солнца."],
  ["obsto", "<big>obsto</big>\n\n<b>ob–sto, stiti, (statum), are</b>\n<b>1)</b> стоять перед, противостоять, стоять на пути (alicui <i>O, Q etc.</i>): quaestibus alicujus o. <i>Pt</i> быть чьим-л. конкурентом || противодействовать, мешать, препятствовать (rei bene gerendae <i>L</i>): principiis obsta <i>O</i> подавляй (зло) в самом начале; fata obstant <i>V</i> веления рока (тому) помеха; nunquam animo pretiis obstantibus <i>J</i> и никогда цены не являются препятствием, <i>т. е.</i> не останавливаясь ни перед какими затратами; si non obstatur <i>C</i> если нет препятствий;\n<b>2)</b> идти вразрез, противоречить (vita ejus obstat huic sceleri <i>Sl</i>);\n<b>3)</b> быть ненавистным (Ilium diis obstitit <i>V</i>);\n<b>4)</b> быть невнимательным <i>или</i> равнодушным (precibus <i>Cld</i>)."],
  ["obstragulum", "<big>obstragulum</big>\n\n<b>obstragulum, i</b> <i>n</i> [obsterno]\nремень для подвязывания обуви (o. crepidae <i>PM</i>)."],
  ["obstreperus", "<big>obstreperus</big>\n\n<b>obstreperus, a, um</b> [obstrepo]\nстрекочущий (carmen cicadarum <i>Ap</i>)."],
  ["obstrepitaculum", "<big>obstrepitaculum</big>\n\n<b>obstrepitaculum, i</b> <i>n</i>\nшум, гам <i>Tert</i>."],
  ["obstrepito", "<big>obstrepito</big>\n\n<b>obstrepito, —, —, are</b>\nшуметь, звучать <i>Lcn</i>."],
  ["obstrepo", "<big>obstrepo</big>\n\n<b>ob–strepo, strepui, strepitum, ere</b>\n<b>1)</b> шуметь (pluvia obstrepit <i>L</i>; milites obstrepunt portis <i>L</i>); гудеть, завывать (venti obstrepunt <i>L</i>); вторить пением (avis obstrepit <i>SenT</i>) <i>или</i> шелестом (arbor obstrepit aquis — <i>dat. Prp</i>) || <i>pass.</i> оглашаться (nemus obstrepitur aquis — <i>abl. O</i>);\n<b>2)</b> заглушать, перебивать, прерывать (alicui <i>L</i>): laetitia facundiae obstrepit <i>Ap</i> радость не даёт говорить;\n<b>3)</b> мешать, беспокоить, докучать, надоедать (alicui litteris <i>C</i>)."],
  ["obstricte", "<big>obstricte</big>\n\n<b>obstricte</b>\nтесно (aliquem alicui obstrictius subdere <i>Aug</i>)."],
  ["obstrictus", "<big>obstrictus</big>\n\n<b>obstrictus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> obstringo;\n<b>2.</b> <i>adj.</i> привязанный, преданный (toti Caesarum domui <i>T</i>)."],
  ["obstrigillator", "<big>obstrigillator</big>\n\n<b>obstrigillator, oris</b> <i>m</i> [obstrigillo]\nпорицатель, хулитель <i>Vr</i>."],
  ["obstrigillo", "<big>obstrigillo</big>\n\n<b>ob–strigillo, —, —, are</b>\n<b>1)</b> стоять поперёк пути, мешать (alicui <i>Vr</i>);\n<b>2)</b> порицать <i>Vr</i>."],
  ["obstrigillus", "<big>obstrigillus</big>\n\n<b>obstrigillus, i</b> <i>m</i> [obstringo]\n(подвязываемая спереди) сандалия <i>Is</i>."],
  ["obstringillo", "<big>obstringillo</big>\n\n<b>obstringillo</b> <i>v. l.</i>\n= obstrigillo."],
  ["obstringo", "<big>obstringo</big>\n\n<b>ob–stringo, strinxi, strictum, ere</b>\n<b>1)</b> стягивать, перетягивать (collum laqueo <i>Pl</i>); сдерживать, держать взаперти, связывать (ventos <i>H</i>);\n<b>2)</b> привязывать (aliquid ob gulam <i>Pl</i>); запрягать (tauros aratro <i>VF</i>);\n<b>3)</b> <i>перен.</i> связывать (aliquem legibus <i>C</i>); обязывать (aliquem jure jurando <i>Cs, T</i>, beneficiis <i>C</i>): fide obstrictum teneri <i>Cs</i> быть связанным словом; alicui omni gratia obstrictus <i>Ap</i> глубоко признательный кому-л.; alicui fidem suam o. <i>PJ</i> обязаться перед кем-л. честным словом;\n<b>4)</b> впутывать, ввязывать, вовлекать (aliquem scelere <i>Cs</i>): aere alieno obstringi <i>L</i> завязнуть в долгах. — <i>См. тж.</i> obstrictus."],
  ["obstructio", "<big>obstructio</big>\n\n<b>obstructio, onis</b> <i>f</i> [obstruo]\n<b>1)</b> закрывание, запирание, <i>перен.</i> маскировка, притворство <i>C</i>;\n<b>2)</b> преграда, препятствие <i>Eccl</i>."],
  ["obstrudo", "<big>obstrudo</big>\n\n<b>obstrudo</b> <i>Pl, O</i>\n= obtrudo."],
  ["obstruo", "<big>obstruo</big>\n\n<b>ob–struo, struxi, structum, ere</b>\n<b>1)</b> строить, воздвигать, возводить впереди (murum <i>L</i>; saxa <i>O, QC</i>): o. alicui rei <i>L</i> строить впереди чего-л.; luminibus alicujus o. <i>C</i> постройкой загородить кому-л. свет;\n<b>2)</b> загромождать, заваливать (portas <i>Cs, Sl</i>; vias <i>Just</i>; terras et undas <i>O</i>); перегораживать, запруживать (flumina <i>Cs</i>); преграждать (iter, aditum ad aliquid <i>C</i>; viam alicui rei <i>Tert</i>): o. alicui perfugia <i>C</i> закрыть кому-л. (все) лазейки; o. aures alicujus <i>V</i> затыкать кому-л. уши, <i>т. е.</i> делать неумолимым; sensus o. <i>PM</i> притупить чувства;\n<b>3)</b> мешать, препятствовать (cognitio multis obstructa difficultatibus <i>C</i>)."],
  ["obstrusio", "<big>obstrusio</big>\n\n<b>obstrusio, onis</b> <i>f</i> [obstrudo]\nзакупорка, затор <i>CA</i>."],
  ["obstupefacio", "<big>obstupefacio</big>\n\n<b>ob–stupefacio, feci, factum, ere</b>\n<b>1)</b> приводить в состояние окоченения (nimio frigore obstupefactus <i>VM</i>);\n<b>2)</b> оглушать, притуплять (obstupefactum voluptatibus cor <i>Treb</i>);\n<b>3)</b> приводить в изумление, поражать (aliquem aliqua re <i>C, L etc.</i>)."],
  ["obstupefio", "<big>obstupefio</big>\n\n<b>obstupefio, factus sum, fieri</b> <i>pass. к</i>\nobstupefacio."],
  ["obstupendus", "<big>obstupendus</big>\n\n<b>obstupendus, a, um</b> [ob + stupeo]\nизумительный, поразительный (morum constantia <i>Vr</i>)."],
  ["obstupescendus", "<big>obstupescendus</big>\n\n<b>obstupescendus, a, um</b> <i>v. l.</i>\n= obstupendus."],
  ["obstupesco", "<big>obstupesco</big>\n\n<b>obstupesco, stupui, —, ere</b> (<i>тж.</i> obstip-)\nприходить в изумление, быть поражённым (aliqua re <i>Ter, L</i> <i>или</i> ad aliquid <i>L</i>); приходить в ужас, (о)столбенеть (aspectu alicujus <i>C</i>; timore <i>Ter</i>); быть раздражённым: dentes obstupuerunt <i>Vlg</i> на зубах оскомина."],
  ["obstupidus", "<big>obstupidus</big>\n\n<b>obstupidus, a, um</b>\nизумлённый, поражённый (consternatus ac o. aliqua re <i>Ap</i>)."],
  ["obstupro", "<big>obstupro</big>\n\n<b>ob–stupro, —, atum, are</b>\nосквернять, позорить <i>Lampr</i>."],
  ["obsufflo", "<big>obsufflo</big>\n\n<b>ob–sufflo, —, —, are</b>\nдуть (навстречу <i>или</i> на что-л.) <i>Q</i>."],
  ["obsum", "<big>obsum</big>\n\n<b>ob–sum, obfui</b> <i>и</i> <b>offui, esse</b>\nпротиводействовать, быть против, мешать, вредить (alicui, alicui rei <i>Pl, C etc.</i>): nihil obest dicere <i>C</i> не мешает сказать."],
  ["obsuo", "<big>obsuo</big>\n\n<b>ob–suo, sui, sutum, ere</b>\nпришивать, зашивать <i>O, Sen, AG</i>: obsuta lectica <i>Su</i> носилки с зашитыми кругом занавесками."],
  ["obsurdatus", "<big>obsurdatus</big>\n\n<b>obsurdatus, a, um</b>\nоглушённый <i>Aug</i>."],
  ["obsurdefacio", "<big>obsurdefacio</big>\n\n<b>obsurde–facio, —, —, ere</b>\nоглушать, делать глухим <i>Aug</i>."],
  ["obsurdesco", "<big>obsurdesco</big>\n\n<b>ob–surdesco, dui, —, ere</b>\nглохнуть, становиться глухим (aures obsurduerunt <i>C</i>; o. stridore <i>Aug</i>); <i>перен.</i> не внимать, не (желать) слушать, быть глухим (ad aliquid <i>Ambr</i>; contra <i>и</i> adversus aliquid <i>Aug</i>; in aliqua re <i>Amm</i>)."],
  ["obsutus", "<big>obsutus</big>\n\n<b>obsutus, a, um</b> <i>part. pf. к</i>\nobsuo."],
  ["obtaedescit", "<big>obtaedescit</big>\n\n<b>ob–taedescit, —, —, ere</b> <i>impers.</i>\nстановится скучно, надоедает <i>Pl</i> (<i>v. l.</i>)."],
  ["obtego", "<big>obtego</big>\n\n<b>ob–tego, texi, tectum, ere</b>\n<b>1)</b> прикрывать, закрывать (aliquid aliqua re <i>Cato, Ph</i>); осенять (domus arboribus obtecta <i>V</i>);\n<b>2)</b> защищать (partem castrorum vineis <i>Cs</i>); ограждать, охранять (obtectus precibus alicujus <i>T</i>);\n<b>3)</b> скрывать, держать в тайне (errata <i>Pl</i>; turpitudinem <i>C</i>)."],
  ["obtemperanter", "<big>obtemperanter</big>\n\n<b>obtemperanter</b> [obtempero]\nпослушно, покорно <i>Eccl</i>."],
  ["obtemperatio", "<big>obtemperatio</big>\n\n<b>obtemperatio, onis</b> <i>f</i> [obtempero]\nповиновение, подчинение (legibus <i>C</i>)."],
  ["obtempero", "<big>obtempero</big>\n\n<b>ob–tempero, avi, atum, are</b>\nповиноваться, подчиняться (legibus <i>Nep</i>; alicui <i>Ter, C etc.</i>); приноровляться, угождать (cupiditati alicujus <i>C</i>)."],
  ["obtendo", "<big>obtendo</big>\n\n<b>ob–tendo, tendi, tentum, ere</b>\n<b>1)</b> растягивать перед, расстилать перед, держать (aliquid alicui rei <i>QC</i> <i>или</i> ante aliquid <i>Su</i>; velis obtendi <i>C</i>): o. nebulam pro aliquo <i>V</i> закрыть (окутать) кого-л. туманом (облаком) || застилать (diem nube atra <i>T</i>): terra illa obtenditur Hispaniae <i>T</i> эта страна простирается (находится) против Испании; obtenta nocte <i>V</i> под покровом ночи; o. aliquid curis <i>T</i> найти в чём-л. забвение от забот;\n<b>2)</b> скрывать (aliquid aliqua re <i>St, Sil</i>);\n<b>3)</b> приводить в оправдание, ссылаться в извинение (o. valetudinem corporis <i>T</i>)."],
  ["obtenebratio", "<big>obtenebratio</big>\n\n<b>obtenebratio, onis</b> <i>f</i>\nзатемнение, помрачение <i>Aug</i>."],
  ["obtenebresco", "<big>obtenebresco</big>\n\n<b>ob–tenebresco, —, —, ere</b>\nтемнеть, тускнеть, меркнуть (lux obtenebrescit <i>Vlg</i>)."],
  ["obtenebro", "<big>obtenebro</big>\n\n<b>ob–tenebro, —, atum, are</b>\nзатемнять, помрачать (stellas <i>Hier</i>)."],
  ["obtensus", "<big>obtensus</big>\n\n<b>obtensus, us</b> <i>m</i> <i>Fronto</i>\n= obtentus II, 2."],
  ["obtentus 1", "<big>obtentus</big>\n\n<b>I obtentus, a, um</b> <i>part. pf. к</i>\n<b>1)</b> obtendo <i>и</i>\n<b>2)</b> obtineo."],
  ["obtentus 2", "<big>obtentus</big>\n\n<b>II obtentus, us</b> <i>m</i> [obtendo]\n<b>1)</b> прикрытие, покров (frondis <i>V</i>; vestis <i>SenT</i>);\n<b>2)</b> благовидный предлог, оправдание (sub obtentu alicujus rei <i>L</i>): tempora rei publicae obtentui sumpta <i>T</i> политические обстоятельства были выдвинуты в качестве предлога."],
  ["obtero", "<big>obtero</big>\n\n<b>ob–tero, trivi, tritum, ere</b>\n<b>1)</b> давить, раздавливать (aliquem pondere alicujus rei <i>Lcr</i>); растаптывать (pedibus obtriti <i>QC</i>); (ис)толочь, (рас)крошить (ossa obtrita <i>Lcn</i>);\n<b>2)</b> подавлять, уничтожать (exercitum, calumniam <i>C</i>);\n<b>3)</b> попирать (omnia jura <i>L</i>); пренебрегать, презирать (voluptates <i>C</i>); умалять, унижать (laudem <i>QC</i>; majestatem alicujus <i>L</i>; aliquem verbis <i>L</i>);\n<b>4)</b> стирать (obtritae catenis manus <i>Sen</i>);\n<b>5)</b> натирать (aliquid carbone <i>Ap</i>)."],
  ["obtestatio", "<big>obtestatio</big>\n\n<b>obtestatio, onis</b> <i>f</i> [obtestor]\n<b>1)</b> торжественная клятва (o. et consecratio legis <i>C</i>);\n<b>2)</b> магическая формула, заклинание (obtestationem componere <i>L</i>);\n<b>3)</b> настоятельная просьба, мольба (preces obtestationesque <i>L</i>)."],
  ["obtestor", "<big>obtestor</big>\n\n<b>ob–testor, atus sum, ari</b> <i>depon.</i>\n<b>1)</b> призывать в свидетели (caelum ac deos <i>T</i>);\n<b>2)</b> клятвенно заверять, торжественно уверять (obtestatur se moriturum esse <i>T</i>);\n<b>3)</b> умолять, заклинать (aliquem per omnes deos <i>C, Cs</i>; o. aliquem, ut aliquid suscipiat <i>PJ</i>); <i>иногда <i>part. pf.</i> в пассивном значении</i>: obtestata fide <i>Ap</i> заставив поклясться."],
  ["obtexi", "<big>obtexi</big>\n\n<b>obtexi</b> <i>pf. к</i>\nobtego."],
  ["obtexo", "<big>obtexo</big>\n\n<b>ob–texo, texui, textum, ere</b>\n<b>1)</b> ткать поверх, прикрывать (fila alarum lanugine <i>PM</i>);\n<b>2)</b> окутывать, закрывать (caelum umbra obtexitur <i>V</i>);\n<b>3)</b> приводить в оправдание (excusationes alicui rei <i>Ambr</i>)."],
  ["obticentia", "<big>obticentia</big>\n\n<b>obticentia, ae</b> <i>f</i> [obticeo] <i>ритор.</i>\nумолчание <i>CC ap. Q</i>."],
  ["obticeo", "<big>obticeo</big>\n\n<b>obticeo, —, —, ere</b> [ob + taceo]\nхранить молчание, молчать <i>Ter</i>."],
  ["obticesco", "<big>obticesco</big>\n\n<b>obticesco, ticui, —, ere</b> [<i>inchoat. к</i> obticeo]\nумолкать, замолкать <i>Ter, H etc.</i>: cetera o. <i>Lact</i> умалчивать о прочем."],
  ["obtigi", "<big>obtigi</big>\n\n<b>obtigi</b> <i>pf. к</i>\nobtingo."],
  ["obtineo", "<big>obtineo</big>\n\n<b>obtineo, tinui, tentum, ere</b> [ob + teneo]\n<b>1)</b> владеть, обладать, населять (partem Galliae <i>Cs</i>); иметь, держать в своих руках (potestatem <i>Cs</i>; summam imperii <i>L</i>): medicamenti vim o. <i>PM</i> обладать целебной силой; noctem caligo obtinuit <i>L</i> ночь была объята туманом || занимать (secundum dignitatis locum <i>Cs</i>): o. proverbii locum <i>C</i> служить поговоркой; saepe error ingens sceleris obtinuit locum <i>SenT</i> огромная ошибка часто — то же преступление; ea fama plerosque obtinet <i>Sl</i> многие верят этому слуху;\n<b>2)</b> <i>воен.</i> оккупировать, занимать (oppidum <i>Cs</i>; vada custodiis <i>Cs</i>);\n<b>3)</b> иметь в управлении, управлять (provinciam <i>L</i>): o. magistratum <i>Nep</i> занимать государственный пост;\n<b>4)</b> получать, достигать, добывать (imperium <i>T</i>; principatum <i>Cs, T</i>): o. veniam ab aliquo <i>L</i> добиться прощения от кого-л.; veritatem o. <i>C</i> добиться истины; contra dicente et nihil obtinente Torquato <i>C</i> при безуспешном протесте Торквата;\n<b>5)</b> удерживать (за собой), сохранять (pontem <i>L</i>; provinciam <i>C</i>; libertatem <i>Cs</i>; proprium splendorem <i>PS</i>);\n<b>6)</b> хранить (silentium <i>C</i>); соблюдать, строго выполнять (legem <i>C</i>);\n<b>7)</b> отстоять (jus suum contra aliquem <i>C</i>); выиграть (litem, causam <i>C</i>);\n<b>8)</b> поддерживать, защищать, доказывать, утверждать (duas sententias contrarias <i>C</i>): verum enimvero celebrior fama obtinet... <i>Ap</i> однако более распространённое мнение утверждает (что);\n<b>9)</b> удерживаться, упорно держаться, оставаться, продолжаться (rumor obtinuit <i>L</i>);\n<b>10)</b> считаться (pro aliqua re <i>Sl</i>)."],
  ["obtingo", "<big>obtingo</big>\n\n<b>obtingo, tigi, —, ere</b> [ob + tango]\n<b>1)</b> (<i>крайне редко</i>) прикасаться, дотрагиваться (o. aliquid <i>Pl</i>);\n<b>2)</b> выпадать на долю, доставаться (provincia alicui sorte obtigit <i>C</i>); случаться (exoptata obtingent <i>Pl</i>); приключиться, стрястись: si quid mihi obtigerit <i>Sen</i> если со мной случится что-л."],
  ["obtinnio", "<big>obtinnio</big>\n\n<b>ob–tinnio, —, —, ire</b>\nзвенеть <i>Ap</i>."],
  ["obtorpesco", "<big>obtorpesco</big>\n\n<b>ob–torpesco, torpui, —, ere</b>\nцепенеть, лишаться чувств (manus prae metu obtorpuerunt <i>L</i>): oculi subita caligine obtorpuerunt <i>Sen</i> внезапно потемнело в глазах || неметь, затекать (crus obtorpuit <i>Sen</i>)."],
  ["obtorqueo", "<big>obtorqueo</big>\n\n<b>ob–torqueo, torsi, tortum, ere</b>\n<b>1)</b> поворачивать (proram <i>St</i>);\n<b>2)</b> вертеть, крутить, скручивать: aurum obtortum <i>V</i> витое золото; o. collum (gulam) alicujus <i>Pl, C</i> схватить кого-л. за шиворот; obtorta gula <i>или</i> collo obtorto <i>C</i> грубо, силой."],
  ["obtractatio", "<big>obtractatio</big>\n\n<b>ob–tractatio, onis</b> <i>f</i>\nприкосновение <i>CA</i>."],
  ["obtraho", "<big>obtraho</big>\n\n<b>ob–traho, —, —, ere</b>\nповорачивать обратно (gradum <i>Tert</i> — <i>v. l.</i>)."],
  ["obtrectatio", "<big>obtrectatio</big>\n\n<b>obtrectatio, onis</b> <i>f</i> [obtrecto]\nревнивое недоброжелательство, завистливая враждебность (o. atque invidia gloriae <i>или</i> adversus gloriam, alicujus <i>L etc.</i>)."],
  ["obtrectator", "<big>obtrectator</big>\n\n<b>obtrectator, oris</b> <i>m</i> [obtrecto]\nревнивый недоброжелатель, завистник, пристрастный соперник (o. laudum alicujus <i>C</i>)."],
  ["obtrectatus", "<big>obtrectatus</big>\n\n<b>obtrectatus, us</b> <i>m</i> <i>AG</i>\n= obtrectatio."],
  ["obtrecto", "<big>obtrecto</big>\n\n<b>obtrecto, avi, atum, are</b> [ob + tracto]\n<b>1)</b> быть противником, противодействовать, бороться (<i>преим.</i> из зависти), стараться умалить (alicui, alicui rei <i>C</i> <i>и</i> aliquem, aliquam rem <i>L, VM etc.</i>): o. laudibus <i>и</i> laudes alicujus <i>L</i> стараться умалить чьи-л. заслуги;\n<b>2)</b> чернить, клеветать (se invicem <i>T</i> <i>и</i> inter se <i>Nep</i>)."],
  ["obtritio", "<big>obtritio</big>\n\n<b>obtritio, onis</b> <i>f</i> [obtero]\n<b>1)</b> раздавливание, сокрушение <i>Aug</i>;\n<b>2)</b> душевное сокрушение, раскаяние (cordis <i>Aug</i>)."],
  ["obtritus 1", "<big>obtritus</big>\n\n<b>I obtritus, a, um</b> <i>part. pf. к</i>\nobtero."],
  ["obtritus 2", "<big>obtritus</big>\n\n<b>II obtritus, (us) </b> <i>m</i>\nраздавливание, растирание (herbae <i>PM</i>)."],
  ["obtrivi", "<big>obtrivi</big>\n\n<b>obtrivi</b> <i>pf. к</i>\nobtero."],
  ["obtrudo", "<big>obtrudo</big>\n\n<b>ob–trudo, trusi, trusum, ere</b>\n<b>1)</b> вталкивать, втыкать, вонзать (aliquid inguinibus <i>Ap</i>);\n<b>2)</b> быстро проглатывать (aliquid <i>Pl</i>);\n<b>3)</b> навязывать (alicui aliquid <i>и</i> aliquem <i>Ter</i>): palpum alicui o. <i>Pl</i> обмануть кого-л. ласковой лестью, подольститься к кому-л.;\n<b>4)</b> окаймлять: ob(s)trusa carbasa pullo <i>O</i> одежды с чёрной каймой;\n<b>5)</b> запрятывать, скрывать (condita et obtrusa <i>Sen</i>);\n<b>6)</b> затыкать (os <i>Eccl</i>)."],
  ["obtruncatio", "<big>obtruncatio</big>\n\n<b>obtruncatio, onis</b> <i>f</i> [obtrunco]\nобрезывание, обрубание (vitis <i>Col</i>)."],
  ["obtrunco", "<big>obtrunco</big>\n\n<b>ob–trunco, avi, atum, are</b>\n<b>1)</b> обрезывать, обрубать (vitem <i>Col</i>);\n<b>2)</b> рубить, резать на куски, убивать (cervos ferro <i>V</i>; graves mero, <i>sc.</i> Trojanos <i>Pt</i>)."],
  ["obtrusi", "<big>obtrusi</big>\n\n<b>obtrusi</b> <i>pf. к</i>\nobtrudo."],
  ["obtrusio", "<big>obtrusio</big>\n\n<b>obtrusio</b> <i>v. l.</i>\n= obstrusio."],
  ["obtueor", "<big>obtueor</big>\n\n<b>ob–tueor, —, eri</b> <i>depon.</i>\n<b>1)</b> глядеть, смотреть (aliquem <i>или</i> ad aliquem <i>Pl</i>);\n<b>2)</b> видеть, замечать <i>Pl</i>."],
  ["obtuli", "<big>obtuli</big>\n\n<b>obtuli</b> <i>pf. к</i>\noffero."],
  ["obtundo", "<big>obtundo</big>\n\n<b>ob–tundo, tudi, tusum (tunsum), ere</b>\n<b>1)</b> бить, ударять (os alicui <i>Pl</i>; obtundi pugnis <i>Pl</i>);\n<b>2)</b> делать тупым, притуплять (telum <i>Lcr</i>); ослаблять (aciem oculorum <i>PM</i>; auditum <i>PM</i>; vires <i>Lcr</i>; mentem <i>C</i>): o. aures alicujus <i>C</i> оглушить кого-л., прожужжать уши кому-л.;\n<b>3)</b> приглушать, делать хриплым: o. vocem (in dicendo) <i>C</i> говорить до хрипоты; o. aegritudinem <i>C</i> смягчать (облегчать, унимать) боль;\n<b>4)</b> надоедать, докучать, удручать (aliquem rogitando <i>Ter</i>, longis epistulis aliquem <i>C</i>; de aliqua re <i>Ter</i>)."],
  ["obtuns-", "<big>obtuns-</big>\n\n<b>obtuns-</b> <i>v. l.</i>\n= obtus-."],
  ["obtuor", "<big>obtuor</big>\n\n<b>obtuor, —, tui</b> <i>Acc, Pl</i>\n= obtueor."],
  ["obturamentum", "<big>obturamentum</big>\n\n<b>obturamentum, i</b> <i>n</i> [obturo]\nпробка, затычка, втулка (cadorum <i>PM</i>)."],
  ["obturatio", "<big>obturatio</big>\n\n<b>obturatio, enis</b> <i>f</i> [obturo]\nзатыкание (aurium <i>Vlg</i>)."],
  ["obturbo", "<big>obturbo</big>\n\n<b>ob–turbo, avi, atum, are</b>\n<b>1)</b> приводить в беспорядок, в смятение (hostes <i>T</i>); делать мутным, мутить (aquam <i>PM</i>); беспокоить, тревожить (non lenire, sed o. aliquem <i>C</i>);\n<b>2)</b> шуметь, кричать, заглушать, стараться перекричать (patres obturbabant <i>T</i>);\n<b>3)</b> мешать, перебивать (ne me obturba <i>Pl</i>);\n<b>4)</b> нарушать (solitudinem <i>C</i>)."],
  ["obturgesco", "<big>obturgesco</big>\n\n<b>ob–turgesco, tursi, —, ere</b>\nнабухать, распухать (pes obturgescit <i>Lcr</i>)."],
  ["obturo", "<big>obturo</big>\n\n<b>obturo, avi, atum, are</b>\nзатыкать (foramina <i>PM</i>); закупоривать (dolia operculis <i>Vtr</i>); зажимать (nares <i>PM, Eccl</i>): o. aures alicui <i>перен.</i> <i>H</i> не слушать кого-л.; o. amorem edendi <i>Lcr</i> утолять голод."],
  ["obtuse", "<big>obtuse</big>\n\n<b>obtuse</b> <i>adv.</i> [obtusus]\nпритуплённо, <i>т. е.</i> слабо, плохо (videre <i>Sol</i>)."],
  ["obtusiangulus", "<big>obtusiangulus</big>\n\n<b>obtusi–angulus, a, um</b>\nтупоугольный (triangulum <i>Boet</i>)."],
  ["obtusio", "<big>obtusio</big>\n\n<b>obtusio, onis</b> <i>f</i> [obtundo]\n<b>1)</b> удары, побои <i>Lampr, Aug</i>;\n<b>2)</b> приглушённость (vocis o. et raucedo <i>CA</i>);\n<b>3)</b> притуплённость (sensuum <i>CA, Tert</i>; visus <i>CA</i>; mentis <i>Aug</i>)."],
  ["obtusitas", "<big>obtusitas</big>\n\n<b>obtusitas, atis</b> <i>f</i>\nпритуплённость <i>Is</i>."],
  ["obtusus", "<big>obtusus</big>\n\n<b>obtusus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> obtundo;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> иступившийся (pugio <i>T</i>); тупой (angulus <i>Vtr</i>);\n<b>2)</b> притупившийся, оглушённый (aures <i>CC</i>); приглушённый, хриплый (vox <i>Q</i>); бесчувственный (pectora <i>V</i>);\n<b>3)</b> слабый, близорукий (sensus oculorum <i>L</i>);\n<b>4)</b> потускневший, померкший (acies stellarum <i>V</i>);\n<b>5)</b> тупоумный, глупый (homo <i>C</i>; ingenium <i>AG</i>);\n<b>6)</b> ослабленный, слабодействующий (venenum <i>Calp</i>); ослабевший (vires <i>Lcr</i>; vigor animi <i>L</i>)."],
  ["obtutus", "<big>obtutus</big>\n\n<b>obtutus, us</b> <i>m</i> [ob + tueor]\n<b>1)</b> взгляд, взор (oculorum <i>C</i>; o. terrenus et hebes <i>Ap</i>): obtutu in aliqua re figere <i>C</i> пристально глядеть на что-л.; semper in obtutu esse malorum <i>O</i> вечно созерцать свои беды, <i>т. е.</i> только о них и думать; animi obtutu <i>Ap</i> духовным взором;\n<b>2)</b> глаз (gemino obtutu aliquem aspicere <i>Ap</i>)."],
  ["obumbraculum", "<big>obumbraculum</big>\n\n<b>obumbraculum, i</b> <i>n</i> <i>Aug</i>\n= obumbratio."],
  ["obumbratio", "<big>obumbratio</big>\n\n<b>obumbratio, onis</b> <i>f</i>\nзатемнение <i>Eccl</i>; <i>перен.</i> помрачение <i>Eccl</i>."],
  ["obumbratrix", "<big>obumbratrix</big>\n\n<b>obumbratrix, icis</b> <i>f</i> [obumbro]\nпокрывающая своей тенью (arbores <i>Tert</i>)."],
  ["obumbro", "<big>obumbro</big>\n\n<b>ob–umbro, avi, atum, are</b>\n<b>1)</b> покрывать тенью, затенять, осенять (humum <i>O</i>; coma interlucentem cervicem obumbrat <i>Ap</i>); затемнять, помрачать (nubes solem obumbrant <i>PM</i>); слегка прикрывать (aliquid aliqua re <i>Ap</i>); затмевать (nomina numquam obscura <i>T</i>); помрачать, затуманивать (sensus <i>Q</i>; sapientia vino obumbratur <i>PM</i>);\n<b>2)</b> утаивать, скрывать, маскировать (crimina sub imagine erroris <i>O</i>);\n<b>3)</b> охранять, защищать (aliquem <i>V</i>)."],
  ["obuncatus", "<big>obuncatus</big>\n\n<b>ob–uncatus, a, um</b> [uncus]\nзагнутый, крючковатый (ungues <i>CA</i>)."],
  ["obunctus", "<big>obunctus</big>\n\n<b>obunctus, a, um</b> [ob + ungo]\nумащённый, смазанный (capillus guttis Arabicis o. <i>Ap</i>); подмазанный, подведённый (oculi <i>Ap</i>)."],
  ["obuncus", "<big>obuncus</big>\n\n<b>ob–uncus, a, um</b>\nзагнутый внутрь (rostrum <i>V</i>); кривой (pedes <i>O</i>)."],
  ["obundo", "<big>obundo</big>\n\n<b>ob–undo, —, —, are</b>\nвыступать из берегов, разливаться <i>Enn, St</i> (<i>v. l.</i>)."],
  ["obustus", "<big>obustus</big>\n\n<b>obustus, a, um</b> [ob + uro]\n<b>1)</b> обожжённый, обгоревший (torris, sudes <i>V</i>);\n<b>2)</b> повреждённый, обмороженный (gleba obusta gelu <i>O</i>)."],
  ["obvagio", "<big>obvagio</big>\n\n<b>ob–vagio, —, —, ire</b>\nскулить, визжать <i>Pl</i>."],
  ["obvagulo", "<big>obvagulo</big>\n\n<b>obvagulo, —, atum, are</b> [obvagio]\nгромогласно заявлять претензию, требовать себе (portus obvagulatus <i>LXIIT</i>)."],
  ["obvallo", "<big>obvallo</big>\n\n<b>ob–vallo, (avi), atum, are</b>\nокружать валом, укреплять <i>C</i>."],
  ["obvallus", "<big>obvallus</big>\n\n<b>obvallus, a, um</b>\nобнесённый валом, укреплённый <i>Acc</i> (<i>v. l.</i>)."],
  ["obvaro", "<big>obvaro</big>\n\n<b>obvaro, —, —, are</b> [ob + varus I]\nпротивиться, сопротивляться, противодействовать (alicui rei <i>Enn</i>)."],
  ["obvelo", "<big>obvelo</big>\n\n<b>ob–velo, —, —, are</b>\nзанавешивать, закрывать (aliquid aliqua re <i>Eccl</i>)."],
  ["obvenientia", "<big>obvenientia</big>\n\n<b>obvenientia, ae</b> <i>f</i> [obvenio]\nпроисшествие, событие, случай <i>Tert</i>."],
  ["obvenio", "<big>obvenio</big>\n\n<b>ob–venio, veni, ventum, ire</b>\n<b>1)</b> приходить: o. alicui somnio <i>AV</i> явиться во сне (присниться) кому-л. || подоспевать, являться (in tempore pugnae — <i>dat. L</i>);\n<b>2)</b> выпадать на долю, доставаться (alicui sorte o. <i>C</i>);\n<b>3)</b> случаться, приключаться (occasio obvenit <i>Pl, QC</i>): quaecum que obvenissent <i>Su</i> всё, что могло произойти (всякие случайности)."],
  ["obventicius", "<big>obventicius</big>\n\n<b>obventicius, a, um</b>\nслучайный <i>Tert</i>."],
  ["obventio", "<big>obventio</big>\n\n<b>obventio, onis</b> <i>f</i> [obvenio]\nпоступление, доход <i>Dig</i>."],
  ["obventus", "<big>obventus</big>\n\n<b>obventus, us</b> <i>m</i>\nвстреча <i>Tert</i>."],
  ["obverbero", "<big>obverbero</big>\n\n<b>ob–verbero, —, —, are</b>\nбить, избивать (asinum baculo <i>Ap</i>)."],
  ["obversio", "<big>obversio</big>\n\n<b>obversio, onis</b> <i>f</i> [obverto]\nповорачивание (alicujus rei <i>Hier</i>)."],
  ["obversor", "<big>obversor</big>\n\n<b>ob–versor, atus sum, ari</b> <i>depon.</i>\n<b>1)</b> расхаживать (перед чём-л.), бродить (castris <i>L</i>; in foro <i>L</i>); появляться, показываться: sedebant judices, obversabantur advocati <i>PJ</i> судьи занимали свои места, адвокаты расхаживали; magna pars perfugarum palam Carthagini obversabantur <i>L</i> значительная часть перебежчиков открыто появлялась в Карфагене;\n<b>2)</b> являться, возникать в представлении, мерещиться (obversabatur maris et litoris gravis aspectus <i>T</i>): in somnis alicui o. <i>Just</i> являться кому-л. во сне; ante oculos o. <i>Lcr, C etc.</i>, <i>тж.</i> oculis <i>L</i> <i>и</i> in oculis <i>Just</i> стоять перед глазами, представляться взору; ad aures o. <i>Lcr</i> слышаться, касаться слуха;\n<b>3)</b> противодействовать, противиться (malo obniti et o. <i>Tert</i>)."],
  ["obversus", "<big>obversus</big>\n\n<b>obversus, a, um</b> <i>part. pf. к</i>\nobverto."],
  ["obverto", "<big>obverto</big>\n\n<b>ob–verto, verti, versum, ere</b>\n<b>1)</b> обращать, поворачивать, направлять (arcus in aliquem <i>O</i>; proras pelago — <i>dat. V</i>): o. remos <i>O</i> повернуть вёсла, <i>т. е.</i> плыть назад кормой, табанить; o. remos lateri <i>O</i> положить вёсла поперёк (<i>т. е.</i> перестать грести);\n<b>2)</b> <i>med.-pass.</i> obverti поворачиваться (in hostem <i>L</i>); обращаться (ad aliquem <i>T</i>): obversus orienti <i>и</i> ad orientem <i>Col</i> <i>или</i> obversus orientem <i>Ap</i> обращенный на восток || обратиться, заняться, предаться (o. ad sanguinem et caedes <i>T</i>); склониться (на сторону) (alicui <i>T</i>);\n<b>3)</b> <i>med.-pass.</i> противостоять, быть противником: profligatis obversis <i>T</i> победив (своих) противников."],
  ["obviam", "<big>obviam</big>\n\n<b>ob–viam</b> <i>adv.</i>\nнавстречу (ire, prodire, se ferre <i>C etc.</i>): o. se dare alicui <i>L</i> идти кому-л. навстречу; o. alicui fieri <i>C</i> встретиться с кем-л.; aliquem forte habere o. <i>AG</i> случайно повстречаться с кем-л.; o. esse alicui <i>Pl</i> быть к чьим-л. услугам; effundi o. <i>L</i> ринуться навстречу; o. ire alicui rei противиться, сопротивляться чему-л. (cupiditati <i>C</i>) <i>или</i> противодействовать чему-л. (infecunditati terrae <i>T</i>): ni isset o. <i>T</i> если бы он не принял предупредительных мер; o. ire periculis <i>Sl</i> не бояться опасностей."],
  ["obvigilo", "<big>obvigilo</big>\n\n<b>ob–vigilo, (avi), atum, are</b>\nбыть бдительным: obvigilato est opus <i>Pl</i> нужно быть начеку."],
  ["obvio", "<big>obvio</big>\n\n<b>ob–vio, avi, atum, are</b>\n<b>1)</b> идти навстречу (alicui <i>Veg, Eccl</i>);\n<b>2)</b> противиться, противодействовать (alicui <i>Macr</i> <i>и</i> alicui rei <i>Dig, Sid</i>);\n<b>3)</b> бороться, преодолевать (vermibus <i>Pall</i>)."],
  ["obvius", "<big>obvius</big>\n\n<b>obvius, a, um</b> [ob + via]\n<b>1)</b> встречный, идущий навстречу: obvium ire <i>QC</i> (se dare <i>L</i>) alicui идти кому-л. навстречу; obvium fieri alicui <i>L</i> <i>или</i> alicui in obvio esse <i>L</i> встретить кого-л. (встретиться с кем-л.); obvios esse inter se <i>Sl</i> встретиться друг с другом; nullo obvio hoste <i>QC</i> не встретив врага; infestis signis obvia signa <i>Lcn</i> одни знамёна, устремляющиеся в бой против других (<i>о гражданской войне между Цезарем и Помпеем</i>); litteras obvias mittere alicui <i>C</i> послать кому-л. письмо навстречу; ventus o. <i>T</i> встречный (дующий в лицо) ветер;\n<b>2)</b> попадающийся на пути, находящийся напротив, противолежащий (montes obvii <i>Nep</i>): quidquid venerit obvium <i>M</i> всё, что ни попадётся (ни придёт в голову);\n<b>3)</b> находящийся вблизи (под рукой), доступный (testis <i>T</i>): opes obviae <i>T</i> легко изыскиваемые богатства; usu o. <i>AG</i> обычный, привычный, общеупотребительный;\n<b>4)</b> удобный (alicui <i>Q</i>);\n<b>5)</b> находящийся во власти, подверженный, открытый (o. furiis ventorum <i>V</i>);\n<b>6)</b> предупредительный, любезный, приветливый, дружелюбный (horno <i>PJ</i>; voluntas <i>Q</i>);\n<b>7)</b> явный, очевидный, напрашивающийся (rerum similitudo <i>T</i>)."],
  ["obvolutatus", "<big>obvolutatus</big>\n\n<b>ob–volutatus, a, um</b>\nзапутавшийся (obviis radicibus bos <i>Veg</i>)."],
  ["obvolutio", "<big>obvolutio</big>\n\n<b>obvolutio, onis</b> <i>f</i> [obvolvo]\nобволакивающий слой, оболочка (aetherea obvolutione vestiri <i>Macr</i>)."],
  ["obvolvo", "<big>obvolvo</big>\n\n<b>ob–volvo, volvi, volutum, ere</b>\nзакутывать, закрывать (caput toga <i>Pl, C etc.</i>); обёртывать (stuppa obvolutae sagittae <i>Veg</i>); скрывать, прикрывать (aliquid verbis decoris <i>H</i>)."],
  ["obvorto", "<big>obvorto</big>\n\n<b>obvorto</b> <i>арх.</i>\n= obverto."],
  ["occ-", "<big>occ-</big>\n\n<b>oc–c-</b> <i>v. l.</i>\n= ob–c-."],
  ["occa", "<big>occa</big>\n\n<b>occa, ae</b> <i>f</i>\nборона <i>Veg</i>."],
  ["occaeco", "<big>occaeco</big>\n\n<b>oc–caeco, avi, atum, are</b>\n<b>1)</b> лишать зрения (o. oculos <i>C</i>);<i>перен.</i> ослеплять (animus occaecatus cupiditate <i>C</i>): o. consilia alicujus <i>L</i> внушать кому-л. необдуманные поступки;\n<b>2)</b> затемнять, омрачать (caligo occaecat diem <i>L</i>); затуманивать, запутывать, делать неясным (orationem <i>C</i>);\n<b>3)</b> делать невидимым, закрывать: o. semen <i>C</i> засыпать семя землёй;\n<b>4)</b> лишать чувств, расслаблять, парализовать (timor occaecavit artus <i>V</i>)."],
  ["occallatus", "<big>occallatus</big>\n\n<b>occallatus, a, um</b> [ob + callus]\nмозолистый, огрубелый, <i>перен.</i> притупившийся, нечувствительный (fauces occallatae alicui rei <i>Sen</i>)."],
  ["occallesco", "<big>occallesco</big>\n\n<b>occallesco, callui, —, ere</b> [ob + calleo]\n<b>1)</b> становиться мозолистым, грубеть, затвердевать (occallescunt latera plagis <i>Pl</i>): osque meum sensi pando occallescere rostro <i>O</i> и я почувствовал, что моё лицо превращается в кривое мозолистое рыло (<i>о превращении Макарея в свинью</i>);\n<b>2)</b> притупляться, грубеть (mores occalluerunt <i>Col</i>);\n<b>3)</b> закаляться, приобретать выдержку (o. longa patientia <i>PJ</i>)."],
  ["occano", "<big>occano</big>\n\n<b>oc–cano, canui, —, ere</b>\nтрубить, заиграть на трубе (cornicines occanuerunt <i>Sl</i>)."],
  ["occanto", "<big>occanto</big>\n\n<b>oc–canto, —, (atum), are</b>\nзачаровывать, околдовывать (aliquem <i>Ap</i>)."],
  ["occasio", "<big>occasio</big>\n\n<b>occasio, onis</b> <i>f</i> [occido II]\n<b>1)</b> случай, повод (alicujus rei, <i>реже</i> ad aliquid <i>C, Cs etc.</i>; tempus actionis opportunum o. appellatur <i>C</i>): o. temporis <i>Cs</i> благоприятный момент; o. victoriae <i>Cs</i> <i>и</i> vincendi <i>QC</i> удобный случай для одержания победы; capere <i>Pl</i> (captare <i>C, L</i>) occasionem воспользоваться случаем (улучить время); per occasionem <i>Sl</i> при случае; ex occasione <i>L</i> в зависимости от случая (обстоятельств); ad occasionem aurae <i>Su</i> при благоприятном ветре; occasione data <i>C, Nep</i> (inventa, oblata <i>C, Su</i>) ввиду представившегося случая; non deesse occasioni <i>L</i> не упустить случая; non habeo ullam occasionem, ut apud te falsa fabuler <i>Pl</i> мне незачем говорить тебе неправду;\n<b>2)</b> <i>воен.</i> внезапное нападение, набег, быстрый манёвр, молниеносная операция, короткий удар: res occasionis est, non proelii <i>Cs</i> это — дело короткой (случайной) стычки, а не боя;\n<b>3)</b> наличие, запас (oleae <i>Col</i>)."],
  ["occasiuncula", "<big>occasiuncula</big>\n\n<b>occasiuncula, ae</b> <i>f</i> [<i>demin. к</i> occasio]\nнедурной случай, самый удобный момент <i>Pl</i>."],
  ["occasus 1", "<big>occasus</big>\n\n<b>I occasus, a, um</b> <i>part. pf. к</i>\noccido II."],
  ["occasus 2", "<big>occasus</big>\n\n<b>II occasus, us</b> <i>m</i> [occido II]\n<b>1)</b> заход, закат (lunae ortus occasusque <i>Mela</i>): o. (solis) <i>rhH, Cs etc.</i> заход солнца, вечер; praecipiti in occasum die <i>T</i> когда день клонился к вечеру;\n<b>2)</b> запад (ab occasu <i>V</i>; in occasum <i>V</i>): inter occasum solis et septentriones <i>Cs</i> на северо-запад(е);\n<b>3)</b> падение, гибель, конец (senectus o. vitae <i>Sen</i>; o. interitusque Trojae <i>V</i>); кончина, смерть (alicujus <i>C</i>);\n<b>4)</b> <i>арх.</i> (= occasio) случай, повод (o. tempusve <i>Enn</i>)."],
  ["occatio", "<big>occatio</big>\n\n<b>occatio, onis</b> <i>f</i> [occo]\nбороньба <i>C, Col, PM</i>."],
  ["occator", "<big>occator</big>\n\n<b>occator, oris</b> <i>m</i> [occo]\nразрыхляющий бороной, боронильщик <i>Pl, Col</i>."],
  ["occatorius", "<big>occatorius</big>\n\n<b>occatorius, a, um</b> [occator]\nотносящийся к бороньбе (opera <i>Col</i>)."],
  ["occedo", "<big>occedo</big>\n\n<b>oc–cedo, cessi, —, ere</b>\nвыступать, выходить, идти (in aspectum alicujus <i>Pl</i>; alicui obviam <i>Pl</i>)."],
  ["occensus", "<big>occensus</big>\n\n<b>occensus, a, um</b> <i>Enn</i>\n= accensus I."],
  ["occento", "<big>occento</big>\n\n<b>oc–cento, avi, —, are</b> [ob + canto]\n<b>1)</b> петь, распевать (перед кем-л., чём-л.): o. aliquem <i>Pl</i> напевать кому-л.;\n<b>2)</b> петь любовную песню (adire ad fores atque o. <i>Pl</i>);\n<b>3)</b> распевать насмешливую песню (перед чьим-л. домом) <i>LXIIT ap. C</i>;\n<b>4)</b> (<i>о зловещей птице</i>) возвещать, пророчить (o. funebria <i>Amm</i>)."],
  ["occentus", "<big>occentus</big>\n\n<b>occentus, us</b> <i>m</i> [occino]\nсвист (soricis <i>PM, VM</i>)."],
  ["occepi", "<big>occepi</big>\n\n<b>occepi</b> <i>pf. к</i>\noccipio."],
  ["occepso", "<big>occepso</big>\n\n<b>occepso</b> <i>арх.</i> <i>Pl</i>\n= occepero (<i>fut. II к</i> occipio)."],
  ["occepto", "<big>occepto</big>\n\n<b>occepto, avi, —, are</b> [<i>intens. к</i> occipio]\nначинать (o. insanire <i>Pl</i>): maxumo malo suo, si attigerit sive occeptassit (= occeptaverit) <i>Pl</i> ему же плохо будет, если он (только) тронет и начнёт (заденет)."],
  ["occidens", "<big>occidens</big>\n\n<b>occidens, entis</b>\n<b>1.</b> <i>part. praes. к</i> occido II;\n<b>2.</b> <i>m</i> запад (ab oriente ad occidentem <i>C</i>)."],
  ["occidentalis", "<big>occidentalis</big>\n\n<b>occidentalis, e</b> [occidens]\nзападный (ventus <i>AG, Vlg</i>; sidus <i>Amm</i>)."],
  ["occidi 1", "<big>occidi</big>\n\n<b>I occidi</b> <i>pf. к</i>\noccido I."],
  ["occidi 2", "<big>occidi</big>\n\n<b>II occidi</b> <i>pf. к</i>\noccido II."],
  ["occidio", "<big>occidio</big>\n\n<b>occidio, onis</b> <i>f</i> [occido I]\nизбиение, резня, истребление, уничтожение: occidione caedere <i>Just</i> (occidere <i>L, AG etc.</i>) перебить до одного, полностью уничтожить; occi dioni dari (occidione occumbere) <i>T</i> быть полностью уничтоженным."],
  ["occido 1", "<big>occido</big>\n\n<b>I occido, cidi, cisum, ere</b> [ob + caedo]\n<b>1)</b> валить наземь, повалить (aliquem pugnis <i>Ter</i>);\n<b>2)</b> убивать, умерщвлять, поражать насмерть (aliquem manu sua <i>C, Fl</i>; aliquem in proelio <i>L</i>; se o. <i>C, Su etc.</i>): non, si me occidisses <i>Pt</i> ни за что, хоть убей || избивать, уничтожать (copias hostium <i>C</i>): o. aliquem veneno S <i>и</i> отравить кого-л.; occisa est haec res <i>Pl</i> я погиб;\n<b>3)</b> мучить, терзать, донимать (aliquem rogando <i>H</i>); делать несчастным (aliquem fallaciis <i>Ter</i>)."],
  ["occido 2", "<big>occido</big>\n\n<b>II occido, cidi, casum, ere</b> [ob + cado]\n<b>1)</b> падать, валиться, рушиться, рухнуть (arbores occi derunt <i>L</i>);\n<b>2)</b> заходить: sol occidens <i>C</i> запад; occidente sole <i>C</i> на закате; vita occidens <i>C</i> преклонный возраст; ante solem occasum <i>Pl</i> перед заходом солнца; nondum omnium dierum sol occidit <i>погов. L</i> не в последний же раз зашло солнце;\n<b>3)</b> пасть, погибать, умирать (in bello pro patria <i>C</i>): o. ab aliquo <i>O</i> пасть от чьей-л. руки; postquam occi derant <i>T</i> после их смерти;\n<b>4)</b> пропадать, исчезать, изглаживаться (nomen alicujus occidit <i>C</i>; omnia orta occidunt <i>Sl</i>): occidi atque interii <i>Pl</i> я окончательно пропал;\n<b>5)</b> гаснуть, потухать (lucernae occi dentes <i>Pt</i>)."],
  ["occiduus", "<big>occiduus</big>\n\n<b>occiduus, a, um</b> [occido II]\n<b>1)</b> заходящий (Sol <i>O</i>); закатный, гаснущий (dies <i>O</i>); вечерний (hora <i>Calp</i>); западный (ab occiduo Sole <i>O</i>); occiduae aquae <i>O</i> море на западе;\n<b>2)</b> близкий к смерти, дряхлый: labitur occiduae per iter declive senectae <i>O</i> (прожив долгую жизнь, человек) скользит по наклонному пути дряхлой старости (<i>слова Пифагора</i>);\n<b>3)</b> преходящий, мимолётный <i>Eccl</i>."],
  ["occillo", "<big>occillo</big>\n\n<b>occillo, —, —, are</b> [occo]\nразбивать, избивать (alicui os <i>Pl</i>)."],
  ["occino", "<big>occino</big>\n\n<b>oc–cino, cinui (cecini), —, ere</b> [ob + cano]\nпеть, издавать звук, кричать (avis occinuit <i>L</i>); звучать, трубить (occinente liticine <i>Amm</i>); каркать (corvus voce clara occinuit <i>L</i>)."],
  ["occipio", "<big>occipio</big>\n\n<b>occipio, cepi (coepi), ceptum, ere</b> [ob + capio I]\n<b>1)</b> начинать (cantionem <i>Pl</i>; sermonem cum aliquo <i>Pl</i>; o. loqui <i>Pl</i>; tibias inflare <i>Ap</i>); приступать, вступать: o. magistratum <i>L</i> вступать в исполнение магистратских обязанностей || <i>вводно</i>: ita ut occepi <i>Pl</i> как я уже сказал;\n<b>2)</b> начинаться (hiemsoccipit <i>T</i>; doloresoccipiunt<i>Ter</i>)."],
  ["occipitium", "<big>occipitium</big>\n\n<b>occipitium, i</b> <i>n</i> [ob + caput]\nзатылок <i>Pl, CC etc.</i>: post o. <i>Vr</i> в тылу, сзади; frons domini plus potest, quam o. <i>погов. PM</i> = хозяйский глаз важнее всего."],
  ["occiput", "<big>occiput</big>\n\n<b>occiput, itis</b> (<i>abl.</i> i) <i>n</i> <i>Pers, Aus</i>\n= occipitium."],
  ["occisio", "<big>occisio</big>\n\n<b>occisio, onis</b> <i>f</i> [occido 1]\n<b>1)</b> убийство, умерщвление (alicujus <i>rhH, C etc.</i>);\n<b>2)</b> (= occidio) избиение, полное уничтожение (occisione occidere copias <i>C</i>)."],
  ["occisor", "<big>occisor</big>\n\n<b>occisor, oris</b> <i>m</i> [occido I]\nубийца <i>Pl, Eccl</i>."],
  ["occisorius", "<big>occisorius</big>\n\n<b>occisorius, a, um</b> [occido I]\nподлежащий закланию (animalia <i>Tert</i>)."],
  ["occisus", "<big>occisus</big>\n\n<b>occisus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> occido I;\n<b>2.</b> <i>adj.</i> погибший, пропащий (res <i>Pl</i>); несчастный (occisissimus omnium <i>Pl</i>)."],
  ["occlamito", "<big>occlamito</big>\n\n<b>oc–clamito, —, —, are</b>\nподнимать громкий крик, громко кричать <i>Pl</i>."],
  ["occlaresco", "<big>occlaresco</big>\n\n<b>oc–claresco, —, —, ere</b>\nприобретать славу, становиться знаменитым <i>Sol</i>."],
  ["occlaudo", "<big>occlaudo</big>\n\n<b>occlaudo, —, —, ere</b> <i>CTh</i>\n= occludo."],
  ["occludo", "<big>occludo</big>\n\n<b>occludo, clusi, clusum, ere</b> [ob + claudo]\n<b>1)</b> запирать, закрывать (tabernas <i>Vr, C</i>; armarium <i>Pl</i>; ostium <i>Pl, Vlg</i>): o. aliquem <i>Pl</i> запереть кого-л.; o. aures <i>Ap</i> заткнуть уши, не слушать;\n<b>2)</b> сдерживать: o. linguam <i>Pl</i> зажать рот (кому-л.)."],
  ["occlusti", "<big>occlusti</big>\n\n<b>occlusti</b> <i>Pl</i>\n= occlusisti (<i>2 л. sg. pf. к</i> occludo)."],
  ["occlusus", "<big>occlusus</big>\n\n<b>occlusus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> occludo;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> запертый (ostium occlusissimum <i>Pl</i>);\n<b>2)</b> припрятанный, скрытый: occlusiorem habere stultiloquentiam <i>Pl</i> поменьше болтать вздор."],
  ["occo", "<big>occo</big>\n\n<b>occo, avi, atum, are</b>\n<b>1)</b> боронить (agrum <i>Col</i>);\n<b>2)</b> возделывать (segetem <i>H</i>)."],
  ["occoepi", "<big>occoepi</big>\n\n<b>occoepi</b>\n= occepi (<i>pf. к</i> occipio)."],
  ["occresco", "<big>occresco</big>\n\n<b>oc–cresco, —, —, ere</b>\nвырастать, развиваться <i>Aug</i>."],
  ["occubitus", "<big>occubitus</big>\n\n<b>occubitus, us</b> <i>m</i> [occumbo]\nзаход, закат (solis <i>Vlg, Eccl</i>)."],
  ["occubo", "<big>occubo</big>\n\n<b>oc–cubo, —, —, are</b>\n<b>1)</b> лежать, покоиться (tumulo <i>V</i>): morte o. pro aliqua re <i>L</i> принять смерть (пасть) за что-л.;\n<b>2)</b> лежать у дверей (<i>на страже</i>) (custodes semper occubunt <i>Pl</i>)."],
  ["occubui", "<big>occubui</big>\n\n<b>occubui</b> <i>pf. к</i>\noccumbo."],
  ["occucurri", "<big>occucurri</big>\n\n<b>occucurri</b> <i>pf. к</i>\noccurro."],
  ["occulco", "<big>occulco</big>\n\n<b>occulco, avi, atum, are</b> [ob + calco]\nтоптать (aliquid <i>L</i>); утаптывать (aliquid pedibus <i>Vr</i>)."],
  ["occulo", "<big>occulo</big>\n\n<b>occulo, cului, cultum, ere</b> [ob + celo]\nприкрывать, покрывать (ruinosas occulit herba domos <i>O</i>); укрывать, скрывать, прятать (captivum <i>L</i>; classem sub rupe cavata <i>V</i>); таить, хранить в тайне (aliquid ab aliquo <i>T</i>)."],
  ["occultate", "<big>occultate</big>\n\n<b>occultate</b> <i>AV</i>\n= occulte."],
  ["occultatio", "<big>occultatio</big>\n\n<b>occultatio, onis</b> <i>f</i> [occulto]\n<b>1)</b> скрывание, сокрытие (se occultatione tutari <i>C</i>): cujus rei nulla est o. <i>Cs</i> что не поддаётся сокрытию;\n<b>2)</b> потускнение (stellarum <i>PM</i>);\n<b>3)</b> <i>ритор. rhH</i> = praeteritio."],
  ["occultator", "<big>occultator</big>\n\n<b>occultator, oris</b> <i>m</i> [occulto]\nскрывающий, укрыватель (latronum <i>C</i>)."],
  ["occulte", "<big>occulte</big>\n\n<b>occulte</b> [occultus]\n<b>1)</b> скрытно, тайно, тайком (perfugere <i>St</i>): o. ferre <i>Ter</i> скрывать, таить; o. dicere <i>C</i> выражаться туманно;\n<b>2)</b> незаметно, неслышно (o. labitur volubilis aetas <i>O</i>)."],
  ["occultim", "<big>occultim</big>\n\n<b>occultim</b> <i>Sol</i>\n= occulte."],
  ["occulto 1", "<big>occulto</big>\n\n<b>I occulto, avi, atum, are</b> [<i>intens. к</i> occulo]\nпрятать, укрывать (telum vestimentis <i>C</i>; se in hortis <i>C</i>); таить, держать в тайне, скрывать (consilium <i>Cs</i>; vitia sua <i>Sl</i>)."],
  ["occulto 2", "<big>occulto</big>\n\n<b>II occulto</b> <i>adv.</i> <i>Lcr</i>\n(<i>v. l.</i>) = occulte."],
  ["occultus", "<big>occultus</big>\n\n<b>occultus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> occulo;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> скрытый, сокровенный, тайный (res, malum <i>C</i>); укрытый, укромный, потаённый (locus <i>Pl, Cs, Sl</i>): aliquid occultum habere <i>или</i> ferre <i>Sl</i> держать что-л. в тайне; crescere occulto aevo <i>H</i> незаметно (исподволь) расти; ex (in) occulto <i>Pl, C, L</i> <i>или</i> per occultum <i>T</i> втайне;\n<b>2)</b> скрытный, неоткровенный (homo astutus et o. <i>C</i>); скрывающийся, действующий исподтишка: occulti illudunt <i>T</i> они втайне насмехаются; non occulti id ferunt <i>T</i> они не скрывают этого; o. odii <i>T</i> скрывающий (таящий в себе) ненависть."],
  ["occumbo", "<big>occumbo</big>\n\n<b>occumbo, cubui, cubitum, ere</b> [ob + cubo]\n<b>1)</b> падать, умирать (o. ante annos suos <i>O</i>; o. ob rem publicam <i>L</i>): o. mortem <i>C, L etc.</i>, <i>реже</i> morte <i>L</i> (<i>поэт.</i> o. morti <i>V</i>, neci <i>O</i>) пасть, погибнуть, умереть (pro patria <i>C</i>); o. alicui <i>Sil, Cld</i> пасть от чьей-л. руки;\n<b>2)</b> заходить, закатываться (cometes occumbit <i>Just</i>)."],
  ["occupatio", "<big>occupatio</big>\n\n<b>occupatio, onis</b> <i>f</i> [occupo I]\n<b>1)</b> оккупация, занятие, завладение (fori <i>C</i>): veteri occupatione <i>C</i> в силу давности (за)владения;\n<b>2)</b> дело, занятие (ab omni occupatione se expedire <i>C</i>): occupationes rei publicae <i>Cs</i> государственные дела; occupationes publicae <i>Cs</i> волнения в государстве; occupationes temporum <i>Cs</i> дела, связанные с обстоятельствами (<i>т. е.</i> текущие); occupationes urbis ac vitae <i>C</i> дела, связанные с общественной жизнью;\n<b>3)</b> <i>ритор.</i> предвосхищение (= prolepsis) <i>rhH</i>."],
  ["occupatus", "<big>occupatus</big>\n\n<b>occupatus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> occupo I;\n<b>2.</b> <i>adj.</i> занятый (in aliqua re <i>C, Cs etc.</i>; circa aliquid <i>VM, Sen</i> <i>или</i> aliqua re <i>Nep</i>)."],
  ["occupio", "<big>occupio</big>\n\n<b>occupio</b> <i>арх.</i> <i>Pl</i>\n= occipio."],
  ["occupo 1", "<big>occupo</big>\n\n<b>I occupo, avi, atum, are</b> [ob + capio]\n<b>1)</b> занимать, захватывать (collem, muros <i>O</i>; urbem viribus <i>V</i>); всходить, подниматься (o. currum <i>O</i>);\n<b>2)</b> заполнять, нагружать (navem frumento <i>bAfr</i>); покрывать, застраивать (urbem aedificiis <i>L</i>): iter sexaginta dierum o. <i>Mela</i> тянуться на расстоянии 60 дней пути (<i>о Герцинском лесе</i>);\n<b>3)</b> покрывать (pallor occupat ora, <i>sc.</i> Didonae <i>V</i>);\n<b>4)</b> захватывать, овладевать (agros <i>C</i>; fama occupat aurem humanam <i>H</i>); завладевать, схватывать, брать в плен (regem <i>QC</i>); покорять (aliquem <i>L</i>);\n<b>5)</b> приковывать, занимать, поглощать (animus occupatus aliqua re <i>Hirt</i>); сковывать (sopor occupat artus <i>V</i>): morbo occupatus <i>Dig</i> сражённый болезнью, больной || настигать (mors aliquem occupavit <i>Ter</i>); нападать (aliquem gladio <i>V</i>): aliquem amplexu o. <i>O</i> обнимать (обхватывать) кого-л.;\n<b>6)</b> достигать (portum <i>H</i>); стяжать, обретать (nomen beati <i>H</i>); доставать, (раз)добывать (cibum <i>QC</i>);\n<b>7)</b> спешить исполнить, быстро совершать (facinus <i>Just</i>; mortem manu <i>C, Fl</i>): pacem non dare solum, sed etiam o. <i>QC</i> не только согласиться на мир, но и ухватиться за его возможность;\n<b>8)</b> задерживать, сдерживать, останавливать (profluvium sanguinis <i>QC</i>): solitis remediis incipientem causam o. <i>Sen</i> — <i>см.</i> causa V);\n<b>9)</b> помещать, вкладывать, ссужать (pecuniam alicui <i>или</i> apud aliquem <i>C</i>): o. pecuniam fenore <i>C</i> отдать деньги в рост;\n<b>10)</b> опережать, (пред)упреждать, предвосхищать (ortum solis <i>QC</i>): diem fati o. <i>погов. QC</i> кончать самоубийством; o. bellum facere <i>L</i> первым начать войну; o. gratiam alicujus <i>QC</i> стараться заручиться чьей-л. благосклонностью; omnia, quae dicturus sum, occupabit <i>Sen</i> во всём, что бы я ни захотел сказать, он предупредит меня; omnes alias curas una occupavit <i>L</i> все прочие заботы отошли на второй план из-за одной (новой); aliquem occupatum interimere <i>C</i> убить кого-л. прежде, чем он успел осуществить свой замысел; num quid vis? occupo <i>H</i> что тебе?— спрашиваю я первый."],
  ["occupo 2", "<big>occupo</big>\n\n<b>II occupo, onis</b> <i>m</i> [occupo I]\nзахватчик, похититель, вор, <i>перен.</i> = Mercurius (<i>как бог воров</i>) <i>Pt</i>."],
  ["occurro", "<big>occurro</big>\n\n<b>oc–curro, curri</b> (<i>реже</i> <b>cucurri</b>), <b>cursum, ere</b>\n<b>1)</b> бежать, спешить навстречу (alicui <i>Cs, L etc.</i>; o. inter se <i>QC</i>); повстречаться, наткнуться (hostibus <i>L</i>): in ipso aditu occurrit mihi <i>Pt</i> я столкнулся с ним у самого входа; occurrentes <i>Pt</i> встречные (прохожие) || бросаться навстречу, устремляться, нападать (alicui cum classe <i>Cs</i>); оказывать сопротивление (duabus legionibus <i>Cs</i>);\n<b>2)</b> приходить, прибывать (alicui rei <i>или</i> ad aliquid <i>C, Cs etc.</i>; in aliam civitatem <i>C</i>; concilio <i>и</i> ad concilium <i>L</i>); попадать, оказываться: o. aliis rebus <i>Cs</i> очутиться в ином положении;\n<b>3)</b> явиться, представиться, предстать (oculis alicujus <i>O</i>; animo <i>C</i> <i>и</i> ad animum <i>Cs</i>); попадаться (nulla arbor occurrebat <i>L</i>); приходить в голову (aliud exemplum mihi occurrit <i>C</i>): nec tibi quid liceat, sed quid fecisse decebit, occurrat <i>Cld</i> помышляй не о том, что ты можешь сделать, а о том, что Должен; <i>impers.</i>: occurrit <i>C etc.</i> возникает мысль; nec mihi quicquam occurrit, cur... <i>C</i> да я и не вижу оснований, почему бы...; non satis occurrit, quid scribam <i>C</i> не совсем мне ясно, о чём писать;\n<b>4)</b> противодействовать (sceleri <i>C</i>); предупреждать, предотвращать, препятствовать (morbo <i>Pers</i>): o. discrepantiae <i>C</i> устранить недоразумение; occurrendum est satietati aurium vestrorum <i>C</i> (мне) не следует утомлять ваш слух;\n<b>5)</b> выполнять, справляться (publicis privatisque officiis o. <i>Sen</i>);\n<b>6)</b> возражать, давать отпор, выступать с отповедью (o. orationi alicujus <i>T</i>)."],
  ["occursaculum", "<big>occursaculum</big>\n\n<b>occursaculum, i</b> <i>n</i> [occurso]\nявление: noctium occursacula <i>Ap</i> ночные (при)видения."],
  ["occursatio", "<big>occursatio</big>\n\n<b>occursatio, onis</b> <i>f</i> [occurso]\nприветливая встреча <i>C</i>; сердечное приветствие <i>C</i>."],
  ["occursator", "<big>occursator</big>\n\n<b>occursator, oris</b> <i>m</i>\nустремляющийся навстречу <i>Aus</i>."],
  ["occursio", "<big>occursio</big>\n\n<b>occursio, onis</b> <i>f</i> [occurro]\n<b>1)</b> нападение, набег, <i>перен.</i> удар (fortunae occursiones <i>Sen</i>);\n<b>2)</b> посещение <i>Sid</i>;\n<b>3)</b> встреча <i>Sen</i>."],
  ["occursito", "<big>occursito</big>\n\n<b>occursito, —, —, are</b> [<i>intens. к</i> occurso]\nвстречаться (alicui <i>Sol</i>)."],
  ["occurso", "<big>occurso</big>\n\n<b>occurso, avi, atum, are</b> [<i>intens. к</i> occurro]\n<b>1)</b> бежать, идти навстречу (fugientibus <i>T</i>); встречаться, натыкаться (alicui <i>V, T</i>);\n<b>2)</b> совершать набег, нападать (gladio <i>Cs</i>);\n<b>3)</b> сопротивляться, противиться (fortunae <i>PJ</i>);\n<b>4)</b> сбегаться, приближаться (alicui rei <i>L, PJ</i>; <i>арх.</i> aliquem <i>Pl</i>): o. (animo) <i>PJ</i> приходить в голову."],
  ["occursor", "<big>occursor</big>\n\n<b>occursor, oris</b> <i>m</i> <i>Aug</i>\n= occursator."],
  ["occursorius", "<big>occursorius</big>\n\n<b>occursorius, a, um</b> [occurso]\nотносящийся к встрече: occursoria potio <i>Ap</i> выпивка перед едой."],
  ["occursus", "<big>occursus</big>\n\n<b>occursus, us</b> <i>m</i> [occurro]\n<b>1)</b> стечение, скопление (omnium hominum <i>VP</i>): itinerum occursus et recursus <i>PM</i> извилистые <i>или</i> запутанные ходы (лабиринта);\n<b>2)</b> встреча (alicujus occursum vitare <i>T</i>): occursu lenunculorum <i>T</i> повстречавшись с лодками;\n<b>3)</b> столкновение, удар (stipitis <i>O</i>)."],
  ["Oceanitis", "<big>Oceanitis</big>\n\n<b>&#332;cean&#299;tis, idis</b> <i>f</i>\nОкеанитида, <i>дочь Океана, морская нимфа</i> <i>V</i>."],
  ["Oceanus", "<big>Oceanus</big>\n\n<b>&#332;ceanus, &#299;</b> <i>m</i> (<i>редко</i> <b>&#332;ceanum, &#299;</b> <i>n</i>)\nОкеан:\n<b>1)</b> (<i>тж.</i> mare O. <i>Cs</i>) <i>обтекающее всю землю море</i> (<i>по верованиям древних</i>) <i>C etc.</i>;\n<b>2)</b> <i>сын Урана и Геи, внук Тефии</i> (Tethys) <i>C etc.</i>;\n<b>3)</b> огромная ванна <i>Lampr</i>."],
  ["ocellati", "<big>ocellati</big>\n\n<b>ocell&#257;t&#299;, &#333;rum</b> <i>n</i> [ocellus]\n(<i>sc.</i> lapilli)\n<b>1)</b> драгоценные камешки овальной формы <i>Vr</i>;\n<b>2)</b> игральные камешки с очками (ocellatis ludere <i>Su</i>)."],
  ["ocellus", "<big>ocellus</big>\n\n<b>ocellus, &#299;</b> <i>m</i> [<i>demin. к</i> oculus]\n<b>1)</b> глазок <i>Pl, O</i>;\n<b>2)</b> краса, перл (ocelli Italiae <i>C</i>); <i>ласк.</i> радость (ocelle mi! <i>Pl</i>);\n<b>3)</b> бугорок на корнях тростника <i>PM</i>."],
  ["Ocelum", "<big>Ocelum</big>\n\n<b>Ocelum, &#299;</b> <i>n</i>\nОкел, <i>город грайоцелов в <i>Gallia Cisalpina Cs</i>.</i>"],
  ["ochra", "<big>ochra</big>\n\n<b>ochra, ae</b> <i>f</i> (<i>греч.</i>)\nохра, <i>минеральная жёлтая краска</i> <i>Vtr, PM</i>."],
  ["Ochus", "<big>Ochus</big>\n\n<b>Ochus, i</b> <i>m</i>\nОх:\n<b>1)</b> <i>река в Средней Азии</i> (<i>ныне</i> Теджен) <i>PM</i>;\n<b>2)</b> <i>прозвище персидского царя Артаксеркса III</i> (<i>359-338 гг. до н. э.</i>) <i>QC, Just</i>;\n<b>3)</b> <i>сын пария Кодоманна</i> <i>QC</i>."],
  ["ocimum", "<big>ocimum</big>\n\n<b>ocimum, i</b> <i>n</i> (<i>греч.</i>) <i>бот.</i>\nбазилик (<i>Ocimum basilicum</i>, L.) <i>PM, Col, Pers</i>."],
  ["ocinum", "<big>ocinum</big>\n\n<b>ocinum, i</b> <i>n</i> (<i>греч.</i>)\n<i>род кормового растения (предпол. вид клевера) Caio</i>, <i>Vr, PM</i>."],
  ["ocior", "<big>ocior</big>\n\n<b>ocior, oris</b> <i>adj. compar.</i> (<i>без</i> <i>posit.</i>)\n<b>1)</b> более быстрый, более скорый (o. ventis <i>V</i>; o. cervis <i>H</i>); скорее действующий (venenum ocissimum <i>PM</i>); o. conscendere antennas <i>O</i> более быстро взбирающийся на мачты;\n<b>2)</b> более ранний, скороспелый (pira <i>PM</i>)."],
  ["ociter", "<big>ociter</big>\n\n<b>ociter</b> [ocius]\n(<i>compar.</i> ocius, <i>superl.</i> ocissime <i>или</i> ocissume) быстро, скоро, проворно (o. facere <i>C</i>; responde ocius <i>Pl</i>); немедленно (ocius ad navem! <i>Pers</i>)."],
  ["ocius", "<big>ocius</big>\n\n<b>ocius, ocissime</b> — <i>см.</i>\nociter."],
  ["ocliferius", "<big>ocliferius</big>\n\n<b>ocliferius, a, um</b> [oculus + ferio]\nбросающийся в глаза, очевидный <i>Sen</i>."],
  ["oclopeta", "<big>oclopeta</big>\n\n<b>oclopeta, ae</b> <i>m</i> [oculos + peto]\nцелящийся в глаз (sagittarius <i>Pt</i> — <i>v. l.</i>)."],
  ["Ocnus", "<big>Ocnus</big>\n\n<b>Ocnus, i</b> <i>m</i>\nОкн:\n<b>1)</b> <i>сын Тибра и Манто, миф. основатель города Мантуи</i> <i>V</i>;\n<b>2)</b> <i>лицо, в наказание за какую-то вину обречённое в подземном мире вечно вить верёвку, которую вечно обгрызает ослица</i> (<i>символ бесплодной работы</i>) <i>PM, Pers</i>."],
  ["ocrea", "<big>ocrea</big>\n\n<b>ocrea, ae</b> <i>f</i> (<i>преим.</i> <i>pl.</i>)\n<b>1)</b> поножи, наголенники (<i>часть доспехов, надевавшаяся на голень</i>) <i>V, L, Veg</i>: per scutum, per ocream <i>Pt</i> —<i>см.</i> scutum;\n<b>2)</b> <i>род охотничьих гетр</i> <i>V</i>."],
  ["ocreatus", "<big>ocreatus</big>\n\n<b>ocreatus, a, um</b> [ocrea]\nзащищенный поножами (crura <i>PM</i>); в болотных гетрах <i>или</i> сапогах (<i>sc.</i> venator <i>H</i>)."],
  ["Ocresia", "<big>Ocresia</big>\n\n<b>Ocresia (Ocrisia), ae</b> <i>f</i>\nОкрезия, <i>мать царя Сервия Туллия</i> <i>PM, O etc.</i>"],
  ["Ocriculanus", "<big>Ocriculanus</big>\n\n<b>Ocriculanus, a, um</b> [Ocriculum]\nокрикуланский <i>C, L</i>."],
  ["Ocriculum", "<big>Ocriculum</big>\n\n<b>Ocriculum, i</b> <i>n</i>\nОкрикул, <i>город в южн. Умбрии</i> (<i>ныне</i> Otricoli) <i>L</i>."],
  ["ocris", "<big>ocris</big>\n\n<b>ocris, is</b> (<i>acc.</i> im) <i>m</i> (<i>греч.</i>)\nкаменистая гора <i>или</i> обрывистая скала <i>LA, LM</i>."],
  ["Ocrisia", "<big>Ocrisia</big>\n\n<b>Ocrisia</b> <i>v. l.</i>\n= Ocresia."],
  ["octachordos", "<big>octachordos</big>\n\n<b>octachordos, on</b> <i>adj.</i> (<i>греч.</i>)\nвосьмиструнный, восьмиголосый <i>Vtr</i>."],
  ["octachorus", "<big>octachorus</big>\n\n<b>octachorus, a, um</b> (<i>греч.</i>)\nс восемью хорами (templum <i>Ambr</i>)."],
  ["octaeteris", "<big>octaeteris</big>\n\n<b>octaeteris, idis</b> <i>f</i>\nвосьмилетний период <i>Cens</i>."],
  ["octagonos", "<big>octagonos</big>\n\n<b>octagonos, on</b> <i>adj.</i> (<i>греч.</i>)\nвосьмиугольный <i>Vtr</i>."],
  ["octagonum", "<big>octagonum</big>\n\n<b>octagonum (on), i</b> <i>n</i> (<i>греч.</i>)\nвосьмиугольник <i>Vtr</i>."],
  ["octangulus", "<big>octangulus</big>\n\n<b>oct–angulus, a, um</b>\nвосьмиугольный <i>Ap</i>."],
  ["octans", "<big>octans</big>\n\n<b>octans, antis</b> <i>m</i> [octo]\nоктант, хорда в 45\" (<i>восьмая часть окружности</i>) <i>Vtr</i>."],
  ["octaphoron", "<big>octaphoron</big>\n\n<b>octaphoron, i</b> <i>n</i> <i>v. l.</i>\n= octophoron."],
  ["octastylos", "<big>octastylos</big>\n\n<b>octastylos, on</b> <i>adj.</i> (<i>греч.</i>)\nвосьмиколонный <i>Vtr</i>."],
  ["octateuchos", "<big>octateuchos</big>\n\n<b>octateuchos, on</b> (<i>греч.</i>)\nвосьмикнижный (codex <i>Eccl</i>)."],
  ["octava", "<big>octava</big>\n\n<b>octava, ae</b> <i>f</i> [octavus]\n<b>1)</b> (<i>sc.</i> hora) восьмой час <i>J</i>;\n<b>2)</b> (<i>sc.</i> pars) восьмая часть <i>bAfr, CJ</i>."],
  ["octavani", "<big>octavani</big>\n\n<b>octavani, orum</b> <i>m</i> [octavus]\nсолдаты восьмого легиона <i>Mela, PM</i>."],
  ["octavarium", "<big>octavarium</big>\n\n<b>octavarium, i</b> <i>n</i> [octava 2]\nуплата восьмой доли <i>CJ</i>."],
  ["Octavia", "<big>Octavia</big>\n\n<b>Octavia, ae</b> <i>f</i>\nОктавия:\n<b>1)</b> O. Minor, <i>сестра Августа, жена Т. Марцелла, а впоследствии М. Антония</i> <i>H, VP, Su</i>;\n<b>2)</b> O. Julia, <i>дочь Августа и Скрибонии</i> <i>T</i>;\n<b>3)</b> <i>дочь Клавдия и Мессалины, в 52-62 гг. н. э. жена Нерона</i> <i>T</i>."],
  ["Octavianus 1", "<big>Octavianus</big>\n\n<b>I Octavianus, a, um</b> [Octavius]\nоктавиев <i>C, Cs</i>."],
  ["Octavianus 2", "<big>Octavianus</big>\n\n<b>II Octavianus, i</b> <i>m</i>\nОктавиан, <i>cognomen Августа после его усыновления Ю.. Цезарем</i> <i>C, T</i>."],
  ["Octavius", "<big>Octavius</big>\n\n<b>Octavius, a, um</b>\nОктавий, <i>римск. nomen; наиболее известны</i>:\n<b>1)</b> Cn. O., <i>командующий флотом во время войны с Персеем (168 г. до н. э.), консул в 165 г., убит в Сирии в 162 г.</i> <i>C, L</i>;\n<b>2)</b> M. O., <i>коллега по народному трибунату и противник Тиберия Гракха</i> <i>L</i>;\n<b>3)</b> M. O., <i>внук предыдущего, приверженец Суллы, в 87 г. до н. э., будучи консулом, изгнал из Рима Цинну; по возвращении Цинны и Мария убит Цензорином</i> <i>C, VP</i>."],
  ["octavum", "<big>octavum</big>\n\n<b>octavum</b> <i>adv.</i>\n<b>1)</b> в восьмой раз <i>L</i>;\n<b>2)</b> в-восьмых <i>Eccl</i>."],
  ["octavus", "<big>octavus</big>\n\n<b>octavus, a, um</b> <i>adj. num. ord.</i>\nвосьмой (hora <i>Cs</i>; pars <i>C</i>): octavo (<i>sc.</i> die) <i>Col</i> на восьмой день; ager efficit cum octavo (<i>sc.</i> grano) <i>C</i> поле даёт урожай сам-восемь; octavum marmor ab urbe <i>M</i> восьмой мильный камень, <i>т. е.</i> в восьми милях от Рима."],
  ["octavusdecimus", "<big>octavusdecimus</big>\n\n<b>octavus–decimus</b> <i>adj. num. ord.</i>\nвосемнадцатый <i>T, Vtr</i>."],
  ["octennis", "<big>octennis</big>\n\n<b>octennis, e</b> [octo + annus]\nвосьмилетний (puer <i>Amm</i>)."],
  ["octennium", "<big>octennium</big>\n\n<b>octennium, i</b> <i>n</i>\nвосьмилетие <i>Macr</i>."],
  ["octies", "<big>octies</big>\n\n<b>octies (octiens)</b> <i>adv. num.</i> [octo]\n<b>1)</b> восемь раз <i>C etc.</i>;\n<b>2)</b> в восьмой раз <i>Amm</i>."],
  ["octingenarius", "<big>octingenarius</big>\n\n<b>octingenarius, a, um</b> [octingeni]\nсостоящий из восьмисот, содержащий восемьсот (<i>штук, голов</i>) (greges <i>Vr</i>)."],
  ["octingeni", "<big>octingeni</big>\n\n<b>octingeni, ae, a</b> <i>adj. num. distr.</i> <i>грам.</i>\nпо восьмисот."],
  ["octingenteni", "<big>octingenteni</big>\n\n<b>octingenteni, ae, a</b> <i>adj. num. distr.</i>\n= octingeni."],
  ["octingentesimus", "<big>octingentesimus</big>\n\n<b>octingentesimus, a, um</b> <i>adj. num. ord.</i>\nвосьмисотый: octingentesimo (<i>sc.</i> anno) <i>T</i> в 800-м году."],
  ["octingenti", "<big>octingenti</big>\n\n<b>octingenti, ae, a</b> <i>adj. num. card.</i> [octo + centum]\nвосемьсот <i>C etc.</i>"],
  ["octingentiens", "<big>octingentiens</big>\n\n<b>octingentie(n)s</b> <i>adv. num.</i> [octingenti]\nвосемьсот раз <i>Vop etc.</i>"],
  ["octipes", "<big>octipes</big>\n\n<b>octi–pes, pedis</b> <i>adj.</i>\nвосьминогий (cancer <i>Prp, O</i>)."],
  ["octo", "<big>octo</big>\n\n<b>octo</b> <i>adj. num. card. indecl.</i>\nвосемь <i>C etc.</i>"],
  ["October 1", "<big>October</big>\n\n<b>I October, bris, bre</b> [octo]\nоктябрьский (mensis, Kalendae, Idus <i>C etc.</i>)."],
  ["October 2", "<big>October</big>\n\n<b>II October, bris</b> <i>m</i>\n(<i>sc.</i> mensis) октябрь <i>Col etc.</i>"],
  ["octochordos", "<big>octochordos</big>\n\n<b>octochordos, on</b> <i>adj.</i> (<i>v. l.</i>)\n= octachordos."],
  ["octodecim", "<big>octodecim</big>\n\n<b>octodecim</b> <i>num. card.</i>\nвосемнадцать <i>L etc.</i>"],
  ["Octodurus", "<big>Octodurus</big>\n\n<b>Octodurus, i</b> <i>m</i>\nОктодур, <i>городок верагров в области гельветов</i> (<i>ныне</i> Martigny) <i>Cs</i>."],
  ["octogamus", "<big>octogamus</big>\n\n<b>octogamus, i</b> <i>m</i> (<i>греч.</i>)\nвосьмиженец <i>Hier</i>."],
  ["octogenarius", "<big>octogenarius</big>\n\n<b>octogenarius, a, um</b> [octogeni]\nсодержащий восемьдесят: восьмидесятилетний (homo <i>PJ, AV</i>); имеющий восемьдесят дюймов в ширину (fistula <i>Vtr, Frontin</i>)."],
  ["octogeni", "<big>octogeni</big>\n\n<b>octogeni, ae, a</b> <i>adj. num. distr.</i> [octoginta]\n<b>1)</b> по восьмидесяти <i>L</i>;\n<b>2)</b> восемьдесят <i>PM etc.</i>"],
  ["octogensimus", "<big>octogensimus</big>\n\n<b>octoge(n)simus, a, um</b> <i>adj. num. ord.</i> [octoginta]\nвосьмидесятый <i>C etc.</i>"],
  ["Octogesa", "<big>Octogesa</big>\n\n<b>Octogesa, ae</b> <i>f</i>\nОктогеза, <i>город илергетов в Hispania Tarraconensis</i> <i>Cs</i>."],
  ["octogies", "<big>octogies</big>\n\n<b>octogies (octegiens)</b> <i>adv. num.</i> [octoginta]\nвосемьдесят раз <i>C</i>."],
  ["octoginta", "<big>octoginta</big>\n\n<b>octoginta</b> <i>adj. num. card. indecl.</i>\nвосемьдесят <i>C etc.</i>"],
  ["octogon-", "<big>octogon-</big>\n\n<b>octogon-</b> <i>v. l.</i>\n= octagon-."],
  ["octojugis", "<big>octojugis</big>\n\n<b>octojugis, e</b> [octo + jugum]\nзапряжённый восьмёркой лошадей, <i>перен.</i> восьмеричный: octojuges tribuni militum <i>L</i> восемь человек военных трибунов."],
  ["octominutalis", "<big>octominutalis</big>\n\n<b>octominutalis, e</b> [octo + minutus]\nвосьмигрошовый <i>Lampr</i>."],
  ["octonarius", "<big>octonarius</big>\n\n<b>octonarius, a, um</b> [octo]\nсостоящий из восьми, восьмеричный (numerus <i>Vr etc.</i>); восьмидюймовый (fistula <i>PM etc.</i>): versus o. <i>Q</i> ямбический восьмистопный стих."],
  ["octoni", "<big>octoni</big>\n\n<b>octoni, ae, a</b> <i>adj. num. distr.</i> [octo]\nпо восьми: o. (<i>sc.</i> asses) aeris <i>H, PM</i> по восьми ассов; bis octonis integer annis, <i>тж.</i> octonis iterum natalibus actis <i>O</i> в расцвете (своих) шестнадцати лет."],
  ["octopeda", "<big>octopeda</big>\n\n<b>octopeda, ae</b> <i>m</i> (<i>греч.</i>) <i>предпол.</i>\nосьминог, кальмар <i>Pt</i> (<i>v. l.</i>)."],
  ["octophoron", "<big>octophoron</big>\n\n<b>octophoron, i</b> <i>n</i> (<i>греч.</i>)\nносилки, рассчитанные на восемь носильщиков (aliquem portare octophoro <i>C</i>)."],
  ["octophoros", "<big>octophoros</big>\n\n<b>octophoros, on</b> <i>adj.</i> (<i>греч.</i>)\nнесомый восемью носильщиками (lectica octophoro ferri <i>C</i>)."],
  ["octotopi", "<big>octotopi</big>\n\n<b>octotopi, orum</b> <i>m</i> (<i>греч.</i>) <i>астр.</i>\nоктотопы, <i>восемь секторов неба между четырьмя основными направлениями</i> (<i>странами света</i>) <i>Man</i>."],
  ["octuaginta", "<big>octuaginta</big>\n\n<b>octuaginta</b> <i>Col, Vtr, AG</i>\n= octoginta."],
  ["octuplicatus", "<big>octuplicatus</big>\n\n<b>octuplicatus, a, um</b> [octo + plico]\nумноженный на восемь, восьмикратный (census <i>L</i>)."],
  ["octuplum", "<big>octuplum</big>\n\n<b>octuplum, i</b> <i>n</i>\nв восемь раз большее: poena octupli <i>C</i> штраф в восьмикратном размере; judicium in o. <i>C</i> наложение восьмикратного штрафа."],
  ["octuplus", "<big>octuplus</big>\n\n<b>octuplus, a, um</b>\nвосьмикратный (pars <i>C</i>)."],
  ["octussis", "<big>octussis</big>\n\n<b>octussis, is</b> <i>m</i> [octo + as]\nвосемь ассов: emere octussibus <i>H</i> купить за восемь ассов."],
  ["ocularis", "<big>ocularis</big>\n\n<b>ocularis, e</b> <i>Veg, Amm</i>\n= ocularius I."],
  ["oculariter", "<big>oculariter</big>\n\n<b>oculariter</b>\nсобственными глазами (intueri <i>Sid</i>)."],
  ["ocularius 1", "<big>ocularius</big>\n\n<b>I ocularius, a, um</b> [oculus]\nглазной (medicus <i>CC, VM</i>; claritas <i>Sol</i>)."],
  ["ocularius 2", "<big>ocularius</big>\n\n<b>II ocularius, i</b> <i>m</i> (<i>sc.</i> medicus)\nглазной врач <i>Scr</i>."],
  ["oculata", "<big>oculata</big>\n\n<b>oculata, ae</b> <i>f</i> <i>предпол.</i>\nминога <i>CC, PM</i>."],
  ["oculatus", "<big>oculatus</big>\n\n<b>oculatus, a, um</b> [oculus]\n<b>1)</b> имеющий глаза, зрячий (o. duobus luminibus <i>Eccl</i>): male o. <i>Su</i> с плохим зрением; testis o. <i>Pl</i> свидетель-очевидец;\n<b>2)</b> явный, заметный, видимый (locus <i>PM</i>): oculata die vendere <i>Pl</i> продавать за наличные;\n<b>3)</b> имеющий форму глаза (circulus <i>Sol</i>)."],
  ["oculeus", "<big>oculeus</big>\n\n<b>oculeus, a, um</b> [oculus]\nмногоглазый (Argus <i>Pl</i>); ясновидящий, зоркий <i>Ap</i>."],
  ["oculicrepida", "<big>oculicrepida</big>\n\n<b>oculi–crepida, ae</b> <i>m</i> [crepo] <i>бран.</i>, <i>прибл.</i>\nсубъект с подбитыми глазами <i>Pl</i>."],
  ["oculiferius", "<big>oculiferius</big>\n\n<b>oculiferius</b> <i>Sen</i>\n(<i>v. l.</i>)= ocliferius."],
  ["oculissimus", "<big>oculissimus</big>\n\n<b>oculissimus, a, um</b> [<i>шутл. superl. к</i> oculus]\nмилейший, дражайший (oculissime homo! <i>Pl</i>)."],
  ["oculitus", "<big>oculitus</big>\n\n<b>oculitus</b> <i>adv.</i> [oculus]\nкак зеницу ока (amare <i>Pl</i>)."],
  ["oculo", "<big>oculo</big>\n\n<b>oculo, —, —, are</b> [oculus]\n<b>1)</b> одарять зрением (aliquem <i>Tert</i>);\n<b>2)</b> просвещать (caecos et ignaros <i>Eccl</i>);\n<b>3)</b> делать ярким (vestes purpura <i>Tert</i>)."],
  ["oculus", "<big>oculus</big>\n\n<b>oculus, i</b> <i>m</i>\n<b>1)</b> глаз, око, <i>тж.</i> взгляд, взор (oculi acres <i>C</i>, aquilini <i>Ap</i>, ardentes <i>V, Sen</i>): reducti introrsus oculi <i>Sen</i> впалые (глубоко сидящие) глаза; locus oculi <i>Pl</i> глазная орбита; capi altero oculo <i>L, Su</i> ослепнуть на один глаз; oculos conjicere (convertere, advertere) ad (in) aliquid <i>C etc.</i> устремить взоры (взглянуть) на что-л.; allevare oculos <i>QC</i> открыть глаза (<i>после обморока</i>); oculos dejicere de (ab) aliqua re <i>C</i> отвести глаза от чего-л.; oculos demittere <i>O</i> (in terram figere <i>T, Sen</i>) потупить глаза; oculos circumferre <i>L, O</i> оглядываться, озираться; pascere oculos aliqua re <i>C, O, Su</i> <i>или</i> capere fructum oculis ex aliqua re <i>Nep</i> наслаждаться видом (зрелищем) чего-л.; librum ab oculo legere <i>Pt</i> читать с листа (&laquo;a livre ouvert&raquo;); aliquem plus oculis amare <i>Cld</i> = любить кого-л. превыше всего; in oculis alicujus (alicui) esse <i>C</i> быть горячо любимым кем-л.; in oculis ferre (gestare) aliquem <i>C, Pt</i> = горячо любить кого-л.; aliquid alicui ante oculos ponere <i>C, Sen</i> (ясно) представить что-л. кому-л.; res ante oculos (in oculis) posita est <i>C etc.</i> это совершенно очевидно; ante oculos (in <i>или</i> sub oculis) <i>L, C, Cs etc.</i> на глазах, перед глазами, в присутствии, на виду; liberare oculos alicujus <i>QC</i> уйти прочь с чьих-л. глаз; obversari oculis <i>L, Sen</i> (ante oculos <i>C</i>) стоять перед глазами;\n<b>2)</b> зрение (oculos restituere alicui <i>Su</i>; oculos <i>Cs etc.</i> <i>или</i> lumina oculorum amittere <i>Nep</i>);\n<b>3)</b> светило, светоч (stellarum oculi <i>PM</i>): mundi o. <i>O</i> = sol; <i>ласк.</i> ocule mi! <i>Pl</i> свет моих очей!;\n<b>4)</b> круглое пятно, кружок, глазок (pavonum caudae oculi <i>PM</i>);\n<b>5)</b> <i>бот.</i> глазок, <i>тж.</i> почка, бугорок <i>или</i> росток: oculos imponere <i>V</i> производить прививку (окулировку);\n<b>6)</b> краса, украшение, перл: duo oculi orae maritimae <i>C</i> две жемчужины морского побережья (<i>о Коринфе и Карфагене</i>)."],
  ["ocymum", "<big>ocymum</big>\n\n<b>ocymum</b> <i>v. l.</i>\n= ocimum."],
  ["Ocyrhoe", "<big>Ocyrhoe</big>\n\n<b>Ocyrhoe, es</b> <i>f</i>\nОкироя, <i>дочь кентавра Хирона и Харикло, превращенная в коня</i> <i>O</i>."],
  ["odarium", "<big>odarium</big>\n\n<b>odarium, i</b> <i>n</i> (<i>греч.</i>; <i>лат.</i> carmen)\nпесня: odaria saltare <i>Pt</i> плясать под звуки песен."],
  ["ode", "<big>ode</big>\n\n<b>ode, es</b> <i>f</i> (<i>греч.</i>)\nпесня (<i>большей частью лирическая</i>) <i>Eccl</i>."],
  ["oderam", "<big>oderam</big>\n\n<b>oderam</b> <i>ppf. к</i>\nodi."],
  ["odeum", "<big>odeum</big>\n\n<b>odeum</b> (<i>Su</i> <b>odium</b>), <b>i</b> <i>n</i> (<i>греч.</i>)\nодеон, <i>здание, предназначенное для музыкальных и поэтических состязаний</i> <i>Vtr, Su etc.</i>"],
  ["odi", "<big>odi</big>\n\n<b>odi, —, odisse</b> (<i>part. fut.</i> osurus) <i>defect.</i>\nненавидеть, не выносить, не терпеть (aliquem, aliquid <i>Pl etc.</i>): quod ultra est o. curare <i>H</i> не заботиться о предстоящем; veritas odit moras <i>SenT</i> истина не терпит отсрочек (<i>т. е.</i> должна быть высказываема без промедлений); o. hospitis officium <i>Ctl</i> пренебрегать долгом гостеприимства; ruta odit hiemem <i>PM</i> рута боится холодов."],
  ["odibilis", "<big>odibilis</big>\n\n<b>odibilis, e</b> [odio]\nдостойный ненависти, ненавистный <i>Lampr, Eccl</i>."],
  ["odio", "<big>odio</big>\n\n<b>odio, odivi (odi), osum, ire</b> <i>арх.</i> <i>Pl, Sen, AG, Antonius ap. C, Vlg</i>\n= odi."],
  ["odiose", "<big>odiose</big>\n\n<b>odiose</b> [odiosus]\nненавистно, несносно, противно, отвратительно <i>Pl, Ter etc.</i>: o. sibi placere <i>AG</i> невыносимо хвастаться."],
  ["odiosicus", "<big>odiosicus</big>\n\n<b>odiosicus, a, um</b> <i>шутл. Pl</i>\n= odiosus."],
  ["odiosus", "<big>odiosus</big>\n\n<b>odiosus, a, um</b>\n<b>1)</b> достойный ненависти, ненавистный (res, genus hominum <i>C</i>);\n<b>2)</b> противный, досадный, неприятный (senectus <i>C</i>; ventus <i>O</i>); несносный, скучный (orator <i>C</i>): odiosum esse alicui <i>Pl etc.</i> быть в тягость (докучать, надоедать) кому-л."],
  ["odium 1", "<big>odium</big>\n\n<b>I odium, i</b> <i>n</i>\n<b>1)</b> ненависть, вражда (in aliquem <i>C, VP etc.</i>, erga aliquem <i>Nep</i> <i>и</i> adversus aliquem <i>L, VM etc.</i>, <i>тж.</i> o. alicujus <i>C etc.</i>): venire (incurrere) in o. alicujus <i>C</i> (odia alicujus in se convertere <i>C</i> <i>или</i> sibi conciliare <i>Q</i>) навлечь на себя чью-л. ненависть; (in) odio esse alicui (apud aliquem) <i>Pl, C</i> быть ненавистным кому-л.; habere o. <i>Sen, Pt</i> быть ненавидимым; habere <i>C etc.</i> (suscipere <i>C, Nep</i>) o. alicujus rei ненавидеть что-л.; magno odio in aliquem ferri <i>C</i> сильно ненавидеть кого-либо; o. est mihi cum illo <i>C</i> мы с ним во враждебных отношениях;\n<b>2)</b> неприязнь, нерасположение, <i>тж.</i> досада, неудовольствие, отвращение, антипатия (alicujus, alicujus rei <i>C, Sl etc.</i>): vitae o. lucisque videndae <i>Lcr</i> отвращение к жизни;\n<b>3)</b> невыносимость, несносность, несносное поведение, возмутительное обращение (quod erat o. I, quae superbia! <i>C</i>);\n<b>4)</b> предмет ненависти, ненавистный человек (o. omnium hominum vel deorum <i>C</i>): o. hominis <i>C</i> ужасный человек."],
  ["odium 2", "<big>odium</big>\n\n<b>II odium, i</b> <i>n</i> <i>Su</i>\n= odeum."],
  ["odo", "<big>odo</big>\n\n<b>odo, onis</b> <i>m</i> <i>Dig</i>\n= udo."],
  ["Odomanti", "<big>Odomanti</big>\n\n<b>Odomanti, orum</b> <i>m</i>\nодоманты, <i>племя во Фракии</i> <i>PM</i>."],
  ["Odomanticus", "<big>Odomanticus</big>\n\n<b>Odomanticus, a, um</b> [Odomanti]\nодомантский <i>L</i>."],
  ["Odones", "<big>Odones</big>\n\n<b>Odones, um</b> <i>m</i>\nодоны, <i>племя во Фракии</i> <i>Sil</i>."],
  ["Odonis", "<big>Odonis</big>\n\n<b>Odonis, idis</b> <i>f</i> [Odones] <i>поэт.</i>\nфракиянка <i>Sil</i>."],
  ["odontitis", "<big>odontitis</big>\n\n<b>odontitis, idis</b> <i>f</i> (<i>греч.</i>) <i>бот.</i>\nзубчатка (<i>Euphrasia odontites</i>, L.) <i>PM</i>."],
  ["odor", "<big>odor</big>\n\n<b>odor, oris</b> <i>m</i>\n<b>1)</b> запах (suavis, taeter <i>C</i>);\n<b>2)</b> дурной запах, зловоние (gravitatem odoris lenire <i>Su</i>);\n<b>3)</b> аромат, благоухание (florum <i>C</i>);\n<b>4)</b> <i>pl.</i> благовония (odores incendere <i>C</i>); волшебные мази (liquidi odores <i>H</i>);\n<b>5)</b> дым (ater <i>V</i>); чад, пар (culinarum fumantium <i>Sen</i>);\n<b>6)</b> предчувствие, предположение: o. alicujus rei est <i>C</i> чувствуется что-л. <i>или</i> поговаривают о чём-л.; quodam odore suspicionis sentire <i>C</i> иметь некоторое подозрение;\n<b>7)</b> слабый признак, налёт (urbanitatis <i>C</i>);\n<b>8)</b> <i>редко</i> (= odoratus II, 1) чутьё, обоняние <i>rhH</i>."],
  ["odorabilis", "<big>odorabilis</big>\n\n<b>odorabilis, e</b> <i>Is</i>\n= odoratus III, 1."],
  ["odoramen", "<big>odoramen</big>\n\n<b>odoramen, inis</b> <i>n</i> <i>Macr</i>\n= odoramentum."],
  ["odoramentum", "<big>odoramentum</big>\n\n<b>odoramentum, i</b> <i>n</i> [odoro]\nблаговонное вещество <i>PM, Col, CA etc.</i>"],
  ["odorarius", "<big>odorarius</big>\n\n<b>odorarius, a, um</b> [odor]\nкурительный (murra <i>PM</i>)."],
  ["odoratio", "<big>odoratio</big>\n\n<b>odoratio, onis</b> <i>f</i> <i>C, Lact</i>\n= odoratus II."],
  ["odorativus", "<big>odorativus</big>\n\n<b>odorativus, a, um</b> <i>Ap</i>\n= odoratus III, 1."],
  ["odoratus 1", "<big>odoratus</big>\n\n<b>I odoratus, a, um</b> <i>part. pf. к</i>\nodoror."],
  ["odoratus 2", "<big>odoratus</big>\n\n<b>II odoratus, us</b> <i>m</i> [odoror]\n<b>1)</b> обоняние, чутьё <i>C, PM</i>;\n<b>2)</b> вдыхание запаха, нюханье <i>C, Vlg</i>;\n<b>3)</b> запах (folia gravia odoratu <i>PM</i>)."],
  ["odoratus 3", "<big>odoratus</big>\n\n<b>III odoratus, a, um</b> [odoro]\n<b>1)</b> душистый, пахучий, благовонный (cedrus <i>V</i>; capilli <i>H</i>);\n<b>2)</b> обитающий в стране благовоний (Armenii <i>Tib</i>)."],
  ["odorifer", "<big>odorifer</big>\n\n<b>odorifer, fera, ferum</b> [odor + fero]\n<b>1)</b> производящий благовония (insula <i>PM</i>);\n<b>2)</b> пахучий, душистый (panacea <i>V</i>)."],
  ["odorificatus", "<big>odorificatus</big>\n\n<b>odori–ficatus, a, um</b> [facio]\nдушистый, пахучий <i>Ambr</i>."],
  ["odorisequus", "<big>odorisequus</big>\n\n<b>odorisequus, a, um</b> [odor + sequor]\nобнюхивающий следы, идущий по следам (canis <i>LA</i>)."],
  ["odoro", "<big>odoro</big>\n\n<b>odoro, avi, atum, are</b> [odor]\n<b>1)</b> делать душистым, надушить, напоить благоуханием (o. aera fumis <i>O</i>) <i>или</i> пропитать своим ароматом (mella <i>Col</i>);\n<b>2)</b> обонять, ощущать запах (nares habere et non o. <i>Vlg</i>)."],
  ["odoror", "<big>odoror</big>\n\n<b>odoror, atus sum, ari</b> <i>depon.</i>\n<b>1)</b> нюхать, обонять (odorem suavitatis <i>Vlg</i>; pallam <i>Pl</i>); чуять (cibum <i>H</i>);\n<b>2)</b> пронюхивать, выведывать (pecuniam <i>C</i>; quid futurum sit <i>C</i>; omnia <i>C</i>);\n<b>3)</b> домогаться (decemviratum <i>C</i>);\n<b>4)</b> <i>перен.</i> поверхностно узнавать, знакомиться вскользь (philosophiam o. <i>T</i>)."],
  ["odorus", "<big>odorus</big>\n\n<b>odorus, a, um</b> [odor]\n<b>1)</b> пахучий, душистый, благоуханный (flos <i>O</i>; pigmenta <i>Macr</i>);\n<b>2)</b> зловонный (sulphur <i>Cld</i>);\n<b>3)</b> тонко обоняющий, чуткий: odora vis canum <i>V</i> собачье чутьё (<i>по др.</i> множество чутких собак)."],
  ["odos", "<big>odos</big>\n\n<b>odos, oris</b> <i>m</i> <i>арх. Sl</i>\n= odor."],
  ["Odrusae", "<big>Odrusae</big>\n\n<b>Odrusae</b> <i>и</i> <b>Odrysae, arum</b> <i>m</i>\nодрисы, <i>фракийск. племя в верховьях реки Гебр</i> <i>L, QC</i>."],
  ["Odrysii", "<big>Odrysii</big>\n\n<b>Odrysii, orum</b> <i>m</i> <i>O</i>\n= Odrusae."],
  ["Odrysius", "<big>Odrysius</big>\n\n<b>Odrysius, a, um</b> [Odrysae]\nодрисийский, <i>поэт.</i> фракийский (tellus <i>Sil</i>)."],
  ["Odyssea", "<big>Odyssea</big>\n\n<b>Odyssea (Odyssia), ae</b> <i>f</i>\nОдиссея, <i>поэма Гомера, переведённая на латинский язык Ливием Андроником</i> <i>Vr, C, O etc.</i>"],
  ["Odysseae portus", "<big>Odysseae portus</big>\n\n<b>Odysseae portus</b> <i>m</i>\n<i>мыс на южн. оконечности Сицилии, близ Пахина</i> <i>C</i>."],
  ["Oea", "<big>Oea</big>\n\n<b>Oea, ae</b> <i>f</i>\nЭя, <i>город на сев. побережье Африки, между Большим и Малым Сиртами</i> (<i>ныне</i> Триполи) <i>Mela, Sil, Ap</i>."],
  ["Oeagrius", "<big>Oeagrius</big>\n\n<b>Oeagrius, a, um</b> [Oeagrus]\nэагров, <i>поэт.</i> фракийский (Hebrus <i>V</i>) <i>или</i> орфеев (Oeagrios pulsare nervos <i>Sil</i>)."],
  ["Oeagrus", "<big>Oeagrus</big>\n\n<b>Oeagrus, i</b> <i>m</i>\nЭагр, <i>миф. царь Фракии, отец Орфея</i> <i>O</i>."],
  ["Oebalia", "<big>Oebalia</big>\n\n<b>Oebalia, ae</b> <i>f</i>\nЭбалия, <i>поэт.</i> Тарент (<i>основанный якобы спартанцами</i>) <i>V, Cld</i>."],
  ["Oebalides", "<big>Oebalides</big>\n\n<b>Oebalides, ae</b> <i>m</i>\n<b>1)</b> потомок Эбала: puer Oe. <i>O</i> = Hyacinthus;\n<b>2)</b> <i>поэт.</i> лакедемонянин <i>VF</i>;\n<b>3)</b> <i>pl. O</i> = Castor et Pollux."],
  ["Oebalis", "<big>Oebalis</big>\n\n<b>Oebalis, idis</b> <i>f</i> [Oebalus]\nэбалова, <i>поэт.</i> спартанская <i>или</i> сабинская (<i>т. к. сабиняне считали себя потомками спартанцев</i>); Oe. nympha <i>O</i> = Helana."],
  ["Oebalius", "<big>Oebalius</big>\n\n<b>Oebalius, a, um</b> [Oebalus]\nэбалов, <i>поэт.</i> спартанский, сабинский: Oe. alumnus <i>VP</i> = Pollux; Oe. puer <i>M</i> = Hyacinthus; Oebalia pelex <i>O</i> = Helena; Oebalia arx <i>V</i> = Tarentum."],
  ["Oebalus", "<big>Oebalus</big>\n\n<b>Oebalus, i</b> <i>m</i>\nЭбал:\n<b>1)</b> <i>царь Спарты, отец Тиндарея, дед Елены и Диоскуров</i> <i>O etc.</i>;\n<b>2)</b> <i>италийский герой, сын царя телебоев Телона, завоеватель Кампании</i> <i>V</i>."],
  ["Oebasus", "<big>Oebasus</big>\n\n<b>Oebasus, i</b> <i>m</i>\nЭбас, <i>военачальник Колхиды</i> <i>VF</i>."],
  ["Oechalia", "<big>Oechalia</big>\n\n<b>Oechalia, ae</b> <i>f</i>\nЭхалия, <i>город в Эвбее, разрушенный Геркулесом</i> <i>V, PM</i>."],
  ["Oechalis", "<big>Oechalis</big>\n\n<b>Oechalis, idis</b> <i>f</i>\nуроженка Эхалии <i>O</i>."],
  ["Oecleus", "<big>Oecleus</big>\n\n<b>Oecle&#865;us, e&#299;</b> <i>m</i>\nЭклей, <i>аргивянин, отец Амфиарая</i> (<i>см.</i> Oeclides)."],
  ["Oeclides", "<big>Oeclides</big>\n\n<b>Oeclides, ae</b> <i>m</i>\nЭклид, сын Эклея, <i>т. е.</i> Амфиарай <i>O, St</i>."],
  ["oeconomia", "<big>oeconomia</big>\n\n<b>oeconomia, ae</b> <i>f</i> (<i>греч.</i>)\nправильное хозяйство, <i>перен.</i> надлежащее распределение частей, правильное размещение элементов (<i>театральной пьесы, речи</i>) <i>Q</i>."],
  ["oeconomicus", "<big>oeconomicus</big>\n\n<b>oeconomicus, a, um</b> (<i>греч.</i>)\nхозяйственный, <i>перен.</i> касающийся правильного распределения частей, гармоничный, стройный (dispositio causae <i>Q</i>)."],
  ["oeconomus", "<big>oeconomus</big>\n\n<b>oeconomus, i</b> <i>m</i> (<i>греч.</i>)\nзаведующий хозяйством, эконом <i>CJ, CTh, Eccl</i>."],
  ["oecus", "<big>oecus</big>\n\n<b>oecus (-os), i</b> <i>m</i> (<i>греч.</i>) <i>архит.</i>\nзала <i>Vtr</i>."],
  ["Oedipodionides", "<big>Oedipodionides</big>\n\n<b>Oedipodionides, ae</b> <i>m</i>\nсын Эдипа <i>St</i>; <i>pl. St, Aus</i> = Eteocles <i>и</i> Polynices."],
  ["Oedipodionius", "<big>Oedipodionius</big>\n\n<b>Oedipodionius, a, um</b> [Oedipus]\nэдипов: Oe. ales <i>St</i> = Sphinx."],
  ["Oedipus", "<big>Oedipus</big>\n\n<b>Oedipus, pi</b> <i>и</i> <b>podis</b> (<i>acc.</i> poda <i>и</i> pum, <i>abl.</i> pode <i>и</i> po, <i>acc. pl.</i> podas) <i>m</i>\nЭдип, <i>сын Лаия и Иокасты, царь Фив</i> <i>Pl, Vr, C etc.</i>"],
  ["Oeenses", "<big>Oeenses</big>\n\n<b>Oeenses, ium</b> <i>m</i>\nжители города Oea <i>T</i>."],
  ["oenanthe", "<big>oenanthe</big>\n\n<b>oenanthe, es</b> <i>f</i> (<i>греч.</i>)\nягоды дикого винограда <i>PM</i>."],
  ["oenanthium", "<big>oenanthium</big>\n\n<b>oenanthium, i</b> <i>n</i> [oenanthe]\nвытяжка из дикого винограда <i>Lampr</i>."],
  ["Oeneis", "<big>Oeneis</big>\n\n<b>Oeneis, idis</b> <i>f</i>\nдочь Ойнея, <i>т. е.</i> Deianira <i>SenT</i>."],
  ["Oeneius 1", "<big>Oeneius</big>\n\n<b>I Oene(i)us, a, um</b> [Oeneus]\nойнеев <i>O</i>."],
  ["Oeneus 2", "<big>Oeneus</big>\n\n<b>II Oene&#865;us, e&#299;</b> <i>и</i> <b>eos</b> <i>m</i>\nОйней, <i>сын Лорфея, миф. царь Калидона (в Этолии), отец Мелеагра, Тидея и Деианиры, устроитель &laquo;Калидонской охоты&raquo;</i> <i>O, Poeta ap. C</i>."],
  ["Oeniadae", "<big>Oeniadae</big>\n\n<b>Oeniadae, arum</b> <i>m</i>\nэниады, <i>город и племя в Акарнании</i> <i>L</i>."],
  ["Oenides", "<big>Oenides</big>\n\n<b>Oenides, ae</b> <i>m</i>\nпотомок Ойнея, <i>т. е.</i> Meleager (<i>сын</i>) <i>или</i> Diomedes (<i>внук</i>) <i>O</i>."],
  ["Oenomaus", "<big>Oenomaus</big>\n\n<b>Oenomaus, i</b> <i>m</i>\nЭномай, <i>миф. сын Марса, царь Писы (Элида), отец Гипподамии, родоначальник Атридов</i> <i>C, St</i>."],
  ["oenomeli", "<big>oenomeli</big>\n\n<b>oenomeli, litos</b> <i>n</i> (<i>греч.</i>)\nвино с мёдом <i>Dig</i>."],
  ["oenomelum", "<big>oenomelum</big>\n\n<b>oenomelum, i</b> <i>n</i> <i>Is</i>\n= oenomeli."],
  ["Oenone", "<big>Oenone</big>\n\n<b>Oenone, es</b> <i>f</i>\nЭнона, <i>фригийская нимфа, возлюбленная Париса</i> <i>O, Su</i>."],
  ["oenophorum", "<big>oenophorum</big>\n\n<b>oenophorum, i</b> <i>n</i> (<i>греч.</i>)\nэнофор, корзина для вина <i>LM, H, Pers, J</i>."],
  ["Oenopia", "<big>Oenopia</big>\n\n<b>Oenopia, ae</b> <i>f</i>\nЭнопия, <i>древнее название о-ва Эгина</i> <i>O</i>."],
  ["Oenopion", "<big>Oenopion</big>\n\n<b>Oenopion, onis</b> <i>m</i>\nЭнопион, <i>критянин, сын Вакха, отец Меропы, царь Хиоса</i> <i>C</i>."],
  ["Oenopius", "<big>Oenopius</big>\n\n<b>Oenopius, a, um</b> [Oenopia]\nэнопийский, <i>поэт.</i> эгинский <i>O</i>."],
  ["oenopolium", "<big>oenopolium</big>\n\n<b>oenopolium, i</b> <i>n</i> (<i>греч.</i>)\nвинный погребок, виноторговля <i>Pl</i>."],
  ["Oenotrius", "<big>Oenotrius</big>\n\n<b>Oenotrius, a, um</b> [Oenotrus II] <i>поэт.</i>\nиталийский, римский <i>V, Sil</i>."],
  ["Oenotrus 1", "<big>Oenotrus</big>\n\n<b>I Oenotrus, a, um</b>\nиталийский (viri <i>V</i>; terrae <i>Sil</i>)."],
  ["Oenotrus 2", "<big>Oenotrus</big>\n\n<b>II Oenotrus, i</b> <i>m</i>\nЭнотр, <i>сын Ликаона, переселившийся из Аркадии в Италию, юго-вост. часть которой (Лукания и Бруттий) получила название Oenotria (см.</i> Oenotrius)."],
  ["oenus 1", "<big>oenus</big>\n\n<b>I oenus</b> <i>арх.</i> <i>C</i>\n= unus."],
  ["Oenus 2", "<big>Oenus</big>\n\n<b>II Oenus, untis</b> <i>m</i>\nЭнунт, <i>приток Эврота в Лаконии</i> (<i>к югу от Спарты</i>) <i>L</i>."],
  ["oestrus", "<big>oestrus</big>\n\n<b>oestrus, i</b> <i>m</i> (<i>греч.</i>)\n<b>1)</b> овод <i>V, PM</i>;\n<b>2)</b> поэтический подъём, вдохновение (oe. Pierius <i>St</i>);\n<b>3)</b> возбуждение (libidinis <i>Hier</i>)."],
  ["oesus", "<big>oesus</big>\n\n<b>oesus, us</b> <i>m</i> <i>арх. C</i>\n= usus I."],
  ["oesypum", "<big>oesypum</big>\n\n<b>oesypum, i</b> <i>n</i> (<i>греч.</i>)\nовечий жиропот <i>PM</i>; (добываемый из жиропота) ланолин (<i>косметическое и целебное средство</i>) <i>PM, O</i>."],
  ["Oeta", "<big>Oeta</big>\n\n<b>Oeta, ae</b> <i>и</i> <b>Oete, es</b> <i>f</i>\nЭта, <i>горная цепь в южн. Фессалии от Малайского залива до Линда</i> <i>C, V, O, Lcn, SenT</i>."],
  ["Oetaeus 1", "<big>Oetaeus</big>\n\n<b>I Oetaeus, a, um</b> [Oeta]\nэтейский (mons <i>C</i>; Thermopylae <i>Ctl</i>); Oe. rex <i>O</i> = Ceyx."],
  ["Oetaeus 2", "<big>Oetaeus</big>\n\n<b>II Oetaeus, i</b> <i>m</i> <i>Prp, O</i>\n= Hercules (<i>сжёгший себя на горе Эта</i>)."],
  ["Oete", "<big>Oete</big>\n\n<b>Oete, es</b> <i>f</i> <i>v. l.</i>\n= Oeta."],
  ["ofella", "<big>ofella</big>\n\n<b>ofella, ae</b> <i>f</i> [<i>demin. к</i> offa]\nкусочек <i>J, M</i>."],
  ["Ofellus", "<big>Ofellus</big>\n\n<b>Ofellus, i</b> <i>m</i>\nОфелл, <i>мужское имя</i> <i>H</i>."],
  ["off-", "<big>off-</big>\n\n<b>of–f-</b> <i>v. l.</i>\n= ob–f-."],
  ["offa", "<big>offa</big>\n\n<b>offa, ae</b> <i>f</i>\n<b>1)</b> кусок, кусочек (panis <i>Col</i>): o. melle soporata <i>V</i> кусок, напоённый мёдом; inter os atque offam multa intervenire potest <i>погов. Cato ap. AG</i> между ртом и куском многое может произойти, <i>т. е.</i> об успехе дела нельзя судить до окончательного его завершения;\n<b>2)</b> комок (gummi in offas convolutum <i>PM</i>);\n<b>3)</b> опухоль, шишка (nigra in facie o. <i>J</i>);\n<b>4)</b> бесформенный кусок, обрывок (carminis <i>Pers</i>);\n<b>5)</b> выкидыш, недоносок <i>J, PM</i>."],
  ["offarcinatus", "<big>offarcinatus</big>\n\n<b>of–farcinatus, a, um</b> [farcino = farcio]\nтуго набитый <i>Tert</i>."],
  ["offarius", "<big>offarius</big>\n\n<b>offarius, i</b> <i>m</i> [offa]\nповар <i>Is</i>."],
  ["offatim", "<big>offatim</big>\n\n<b>offatim</b> [offa]\nкусочками, по кусочкам <i>Pl, Is</i>."],
  ["offendiculum", "<big>offendiculum</big>\n\n<b>offendiculum, i</b> <i>n</i> [offendo]\nпрепятствие, преткновение, помеха <i>PJ, Vlg, Eccl</i>."],
  ["offendo", "<big>offendo</big>\n\n<b>offendo, fendi, fensum, ere</b> [<i>одного корня с</i> defendo]\n<b>1)</b> ударять (caput ad fornicem <i>Q</i>; aliquem pede <i>Pl</i>); ушибать, повреждать (caput <i>L</i>; pes offensus <i>O</i>): o. solido <i>H</i> удариться о нечто твёрдое; scuta offensa strepunt <i>Sl</i> столкнувшись, звенят щиты; vocis offensa imago <i>V</i> отголосок;\n<b>2)</b> случайно встречать, неожиданно находить, заставать, наталкиваться (aliquid in aliqua re, in <i>или</i> ad aliquid, alicui rei <i>C, Cs, L etc.</i>): aliquem imparatum o. <i>C</i> застигать кого-л. врасплох; nondum perfectum templum offenderunt <i>C</i> они нашли храм ещё незаконченным; paululum si cessassem, domi non offendissem <i>Ter</i> если бы я немного помедлил, (то) не застал бы (его) дома; aliquid turbatum et inconditum o. <i>Ap</i> находить кое-какие нарушения и неправильности;\n<b>3)</b> пострадать, потерпеть крушение (аварию, урон, неудачу), попадать в беду (naves offenderunt <i>Cs</i>): apud judices o. <i>C</i> проиграть процесс; in exercitu offensum est <i>Cs</i> армия потерпела неудачу (поражение); quicquid offendit <i>Sen</i> любая неудача;\n<b>4)</b> возбуждать неудовольствие (apud aliquem de aliqua re <i>C</i>); быть неприятным, не нравиться, <i>тж.</i> задевать, оскорблять, обижать (aliquem <i>или</i> animum alicujus <i>C</i>): offensus convicio <i>Ap</i> задетый оскорбительными словами; quarum etiam recordatio me offendit <i>Ap</i> (лакомства), которые мне и вспомнить неприятно; alicujus existimationem o. <i>C</i> задеть чью-л. честь (повредить чьей-л. репутации); (animum) in aliquo o. <i>C, Cs</i> быть недовольным кем-л.; matutina vigilia offendebatur <i>Su</i> (Август) терпеть не мог рано вставать;\n<b>5)</b> поражать (corpus offensum est <i>CC</i>); вредить, дурно действовать (aliquid offendit stomachum <i>PM</i>); производить неприятное ощущение, неприятно поражать, раздражать (nares nidore <i>Lcr</i>); резать (colorum claritas aciem oculorum offendit <i>PM</i>);\n<b>6)</b> ошибаться, погрешать, допускать оплошность, совершать промах, провиниться (in aliqua re <i>или</i> aliquid <i>C, Cs etc.</i>): in quo ipsi offendissent, alios reprehendissent <i>C</i> в чём они (обвинители Попилия и Гутты) сами провинились, (в том) они обвинили других. — <i>См. тж.</i> offensus I."],
  ["offensa", "<big>offensa</big>\n\n<b>offensa, ae</b> <i>f</i> [offendo]\n<b>1)</b> удар, толчок (<i>sc.</i> confragosi itineris <i>Sen</i>); повреждение (dentium <i>PM</i>);\n<b>2)</b> неприязнь, немилость, напряжённые отношения, вражда, ненависть (sine offensa cum aliquo vivere <i>PJ</i>): offensam subire <i>PJ</i> <i>или</i> contrahere <i>Su</i> впасть в (навлечь на себя) немилость; minimum offensae <i>Sl</i> ничтожный повод к (взаимной) вражде;\n<b>3)</b> обида, оскорбление (offensas ense vindicare <i>O</i>): offensam habere <i>Q</i> вызывать неприязнь;\n<b>4)</b> ущерб (sine offensa corporis animique <i>Pt</i>); недомогание (aliquid offensae sentire <i>CC</i>);\n<b>5)</b> <i>pl.</i> препятствия, помехи <i>T, Sen</i>;\n<b>6)</b> <i>юр.</i> нарушение (sine offensa edicti <i>Dig</i>)."],
  ["offensaculum", "<big>offensaculum</big>\n\n<b>offensaculum, i</b> <i>n</i> [offenso]\n<b>1)</b> удар, толчок (offensaculis contusa crura <i>Ap</i>);\n<b>2)</b> препятствие (per offensacula multa ire <i>Eccl</i>)."],
  ["offensatio", "<big>offensatio</big>\n\n<b>offensatio, onis</b> <i>f</i> [offenso]\n<b>1)</b> удар (hastae <i>Q</i>);\n<b>2)</b> ошибка, погрешность, промах (memoriae <i>Sen</i>)."],
  ["offensator", "<big>offensator</big>\n\n<b>offensator, oris</b> <i>f, m</i> [offenso]\nспотыкающийся, оступающийся, <i>перен.</i> ошибающийся <i>Q</i> (<i>v. l.</i>)."],
  ["offensibilis", "<big>offensibilis</big>\n\n<b>offensibilis, e</b>\nспотыкающийся (offensibilibus gressibus per viam incedere <i>Lact</i>)."],
  ["offensio", "<big>offensio</big>\n\n<b>offensio, onis</b> <i>f</i> [offendo]\n<b>1)</b> удар, ушиб (pedis <i>C</i>);\n<b>2)</b> выступ, препятствие, неровность (nihil offensionis habere <i>C</i>);\n<b>3)</b> недомогание, недуг, болезнь (corporis <i>C</i>; o. gravis <i>C</i>);\n<b>4)</b> отвращение (offensionem habere ad aliquid <i>C</i>); неудовольствие, досада, негодование, обида (sine offensione accipere aliquid <i>C</i>): offensionem habere (movere) <i>C</i> причинять неприятности, возбуждать неудовольствие (негодование); in offensionem alicujus irruere <i>C</i> навлечь на себя чьё-л. негодование (впасть у кого-л. в немилость); o. dictorum <i>C</i> неудовольствие, вызванное словами;\n<b>5)</b> неудача, несчастье, провал (offensione permotus <i>Cs</i>; timere offensionem <i>C</i>): o. belli <i>C</i> военное поражение;\n<b>6)</b> погрешность, ошибка, промах <i>или</i> злоупотребление (judiciorum <i>C</i>): o. neglegentiae susceptis rebus <i>C</i> нерадивость в ведении принятых на себя дел;\n<b>7)</b> <i>юр.</i> нарушение (levis culpae o. <i>CJ</i>)."],
  ["offensiuncula", "<big>offensiuncula</big>\n\n<b>offensiuncula, ae</b> <i>f</i> [<i>demin. к</i> offensio]\nнебольшая обида (animi alicujus <i>C</i>); неприятность, неудовольствие (offensiunculam accipere in aliqua re <i>C</i>)."],
  ["offenso", "<big>offenso</big>\n\n<b>offenso, —, (atum), are</b> [<i>intens. к</i> offendo]\n<b>1)</b> ударить (o. pulsareque aliquid <i>Lcr</i>); ушибить: o. caput <i>L</i> удариться головою;\n<b>2)</b> (<i>в речи</i>) останавливаться, запинаться <i>Q</i>."],
  ["offensor", "<big>offensor</big>\n\n<b>offensor, oris</b> <i>m</i>\nоскорбитель, обидчик <i>Eccl</i>."],
  ["offensus 1", "<big>offensus</big>\n\n<b>I offensus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> offendo;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> раздражённый, сердитый, недоброжелательный (animus alienatus et o. <i>C</i>): o. alicui <i>или</i> in (erga) aliquem <i>Nep etc.</i> раздражённый против кого-л.;\n<b>2)</b> возбуждающий неудовольствие, вызывающий негодование, отталкивающий, противный, неприятный (invitus et o. alicui <i>Sl</i>)."],
  ["offensus 2", "<big>offensus</big>\n\n<b>II offensus, us</b> <i>m</i> [offendo]\n<b>1)</b> столкновение, встреча, стычка (armorum <i>St</i>);\n<b>2)</b> неудовольствие, отвращение: in offensu esse <i>Lcr</i> внушать отвращение, быть в тягость."],
  ["offerentia", "<big>offerentia</big>\n\n<b>offerentia, ae</b> <i>f</i> [offero]\nпоявление, возникновение (causarum <i>Tert</i>)."],
  ["offero", "<big>offero</big>\n\n<b>of–fero, obtuli, oblatum, offerre</b>\n<b>1)</b> представлять, предъявлять (aliquid non modo ostendere, sed etiam o. <i>C</i>): o. crimina <i>C</i> выдвигать обвинения;\n<b>2)</b> противопоставлять, выставлять навстречу (aciem strictam venientibus <i>V</i>); подставлять (telis corpus suum <i>C</i>) || o. se <i>или</i> <i>pass.</i> offerri представляться, показываться, являться (se o. in somnis alicui <i>V</i>): res oblata <i>V</i> явление; offertur occasio <i>Cs, PS</i> представляется случай; tempore oblato <i>C</i> когда (поскольку) представился случай; offertur religio <i>C</i> возникает суеверное колебание; offertur metus <i>C</i> находит страх; o. se (obviam) alicui <i>O</i> идти кому-л. навстречу; in via oblatus <i>Ap</i> встретившийся в пути; o. se противиться, бороться, восставать (hostibus <i>Cs</i>; sceleri alicujus <i>C</i>);\n<b>3)</b> предлагать (victoriam certam aut pacem <i>L</i>; alicui operam suam <i>L</i>): quae fors obtulerat <i>T</i> (всем,) что оказывалось под рукой, <i>т. е.</i> чем попало; o. se <i>C</i> предлагать свои услуги; o. se periculo <i>C</i> подвергать себя опасности; o. vitam in discrimen <i>C</i> подвергать жизнь (свою) опасности; o. se morti <i>C, Cs</i> <i>и</i> ad mortem <i>C</i> обрекать себя на смерть (жертвовать своей жизнью) (se o. pro patria ad mortem <i>C</i>);\n<b>4)</b> оказывать (alicui beneficium <i>Cs</i>; auxilium <i>LM</i>); доставлять (laetitiam alicui <i>Ter</i>); причинять (mortem alicui <i>C</i>); наносить (injuriam immerenti <i>Ter</i>);\n<b>5)</b> <i>культ.</i> приносить, жертвовать <i>Eccl</i>."],
  ["offertorium", "<big>offertorium</big>\n\n<b>offertorium, i</b> <i>n</i> [offero]\nместо жертвоприношений <i>Is</i>."],
  ["offerumentae", "<big>offerumentae</big>\n\n<b>offerumentae, arum</b> <i>f</i> [offero]\nподношения, подарки, <i>шутл.</i> побои <i>Pl</i>."],
  ["officialis 1", "<big>officialis</big>\n\n<b>I officialis, e</b> [officium]\n<b>1)</b> служебный, должностной (operae <i>Dig</i>);\n<b>2)</b> трактующий об обязанностях (libri <i>Lact</i>)."],
  ["officialis 2", "<big>officialis</big>\n\n<b>II officialis, is</b> <i>m</i>\nоффициал, мелкий чиновник <i>или</i> технический служащий <i>Dig, Ap, Spart</i>."],
  ["officina", "<big>officina</big>\n\n<b>officina, ae</b> <i>f</i> [<i>из</i> opificina <i>от</i> opifex]\n<b>1)</b> мастерская, место изготовления (armorum <i>Cs, Nep</i>; ferraria <i>bAfr</i>);\n<b>2)</b> рассадник, очаг (eloquentiae <i>C</i>); школа (sapientiae <i>C</i>); гнездо (nequitiae <i>C</i>; falsorum commentariorum <i>C</i>);\n<b>3)</b> птицеводческое хозяйство, птичник <i>Col</i>."],
  ["officinator", "<big>officinator</big>\n\n<b>officinator, oris</b> <i>m</i> [officina]\nмастер <i>Vtr, Ap</i>."],
  ["officio", "<big>officio</big>\n\n<b>officio, feci, fectum, ere</b> [ob + facio]\n<b>1)</b> становиться поперёк пути, преграждать, отрезать (hostium itineri <i>Sl</i> <i>или</i> iter <i>bAfr</i>); загораживать, застилать (o. soli <i>C</i>): luminibus alicui o. <i>C, G</i> заслонить кому-л. свет <i>или</i> вид (на что-л.); mentis luminibus alicujus o. <i>C</i> помрачить чьё-л. сознание; famae (<i>dat.</i>) alicujus o. <i>L</i> затмить чью-л. славу; offecti sensus <i>Lcr</i> усыплённые чувства;\n<b>2)</b> мешать, препятствовать, противодействовать (alicui rei <i>V, C, PM</i>); запутывать (o. sententiis alicujus <i>C</i>): o. auribus <i>Sl</i> мешать слушать, делать глухим (к доводам разума) || вредить (officiunt frugibus herbae <i>V</i>)."],
  ["officiose", "<big>officiose</big>\n\n<b>officiose</b> [officiosus I]\nуслужливо, предупредительно, любезно (scribere <i>C</i>; reverenter et o. <i>PJ</i>)."],
  ["officiositas", "<big>officiositas</big>\n\n<b>officiositas, atis</b> <i>f</i>\nлюбезность, предупредительность <i>Sid</i>."],
  ["officiosus 1", "<big>officiosus</big>\n\n<b>I officiosus, a, um</b> [officium]\n<b>1)</b> услужливый, готовый к услугам, предупредительный, любезный (homo <i>C</i>; voluntas <i>O</i>; o. in aliquem <i>C</i>);\n<b>2)</b> ревностный (labor <i>C</i>); сообразный с долгом, законный, должный (dolor <i>C</i>; pietas <i>Sen</i>)."],
  ["officiosus 2", "<big>officiosus</big>\n\n<b>II officiosus, i</b> <i>m</i>\n<b>1)</b> банщик, банный служитель <i>Pt</i>;\n<b>2)</b> воздыхатель, поклонник <i>Sen</i>."],
  ["officiperda", "<big>officiperda</big>\n\n<b>officiperda, ae</b> <i>m</i> [officium + perdo]\nплохо использующий чужие услуги <i>DC</i>."],
  ["officium", "<big>officium</big>\n\n<b>officium, i</b> <i>n</i> [<i>из</i> opificium]\n<b>1)</b> услуга, любезность, одолжение: praestare o. <i>Cs, Hirt, Nep etc.</i> оказать услугу; multa officia in aliquem conferre <i>C</i> оказать кому-л. много услуг;\n<b>2)</b> готовность к услугам, внимательность, услужливость, предупредительность, любезность: homo summo officio praeditus <i>C</i> весьма услужливый (предупредительный) человек; litterae plenae officii <i>C</i> весьма любезное (полное внимания) письмо;\n<b>3)</b> рвение, усердие (officia intendere <i>Sl</i>);\n<b>4)</b> засвидетельствование почтения, поклон, визит, поздравление (officia urbana <i>Nep</i>): officii causa <i>Pt</i> из уважения; alicui o. facere <i>Pl, Ter</i> (praestare <i>O</i>) засвидетельствовать кому-л. своё почтение (нанести визит); suprema officia <i>T</i> (o. triste <i>O</i>) последний долг (умершему);\n<b>5)</b> долг, обязанность (tria oratorum officia <i>C</i>); чувство долга (pudor atque o. <i>Cs</i>): o. facere <i>Ter</i> (exsequi, perficere <i>C</i>), satisfacere officio <i>C</i> <i>или</i> o. implere <i>C etc.</i> исполнять обязанность; deserere o. (discedere ab officio <i>или</i> officio suo deesse) <i>C</i> не исполнять обязанности; esse in officio <i>или</i> officio fungi <i>C</i> исполнять (свои) обязанности; in officio (per)manere <i>Nep etc.</i> оставаться верным (своему) долгу; istuc viri est o. <i>Ter</i> вот так должен поступать (истинный) муж; tenere <i>Nep, Cs</i> (continere <i>Cs</i>) in officio держать в повиновении;\n<b>6)</b> должность, служба, служебные занятия (o. scribae <i>Nep</i>; o. legationis <i>Cs</i>); o. itineris <i>Cs</i> служебные обязанности во время похода; o. maritimum <i>Cs</i> командование морскими силами, военно-морская служба;\n<b>7)</b> обряд, церемония (o. nuptiarum <i>Su</i> <i>и</i> nuptiale <i>Pt</i>; exsequiarum <i>Just</i>);\n<b>8)</b> служебный персонал <i>или</i> прислугами, <i>Dig, Tert</i>;\n<b>9)</b> функции, свойства (corporis <i>Lcr</i>);\n<b>10)</b> половой акт <i>Prp, O, Pt</i>."],
  ["offigo", "<big>offigo</big>\n\n<b>of–figo, fixi, fixum, ere</b>\nвбивать, вколачивать (furcas circum <i>Cato</i>); прибивать, приколачивать (aliquid clavo grandi <i>Ap</i>)."],
  ["offirmate", "<big>offirmate</big>\n\n<b>offirmate</b> [offirmatus]\nупорно, твёрдо, стойко, непоколебимо (resistere <i>Su</i>)."],
  ["offirmatio", "<big>offirmatio</big>\n\n<b>offirmatio, onis</b> <i>f</i>\nупорство, стойкость <i>Hier</i>."],
  ["offirmatus", "<big>offirmatus</big>\n\n<b>offirmatus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> offirmo;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> твёрдый, упорный, стойкий (animus <i>Pl</i>);\n<b>2)</b> своенравный (voluntas <i>C</i>)."],
  ["offirmo", "<big>offirmo</big>\n\n<b>of–firmo, avi, atum, are</b>\nделать крепким, твёрдым, укреплять (stabuli fores pertica <i>Ap</i>): o. animum <i>Pl, Ctl, PJ</i> ободряться, мужаться; o. <i>Pl</i> <i>и</i> o. se <i>Acc, Ter</i> твёрдо настаивать, упорствовать, упрямиться; offirmatum est mihi <i>Pl</i> для меня это решённый вопрос; ne tam offirma te <i>Ter</i> не упрямься так; o. facere aliquid <i>Pl, Ter</i> непременно хотеть сделать что-л."],
  ["offla", "<big>offla</big>\n\n<b>offla, ae</b> <i>f</i> <i>Pt</i>\n= offula."],
  ["offlecto", "<big>offlecto</big>\n\n<b>of–flecto, —, —, ere</b>\nповорачивать (navem <i>Pl</i>)."],
  ["offoco", "<big>offoco</big>\n\n<b>offoco, —, —, are</b> [ob + fauces]\n(за)душить, удавить (aliquem <i>Fl, Sen, Eccl</i>)."],
  ["offrenatus", "<big>offrenatus</big>\n\n<b>offrenatus, a, um</b> [ob + freno]\n<b>1)</b> взнузданный, <i>перен.</i> укрощенный, присмиревший (Cerberus <i>Ap</i>);\n<b>2)</b> <i>перен.</i> обманутый (illecebris alicujus <i>Ap</i>)."],
  ["offringo", "<big>offringo</big>\n\n<b>offringo, fregi, fractum, ere</b> [ob + frango]\nвторично разрыхлять, перепахивать (glebas <i>Col</i>; terram <i>Vr</i>)."],
  ["offucia", "<big>offucia</big>\n\n<b>offucia, ae</b> <i>f</i> [ob + fucus]\n<b>1)</b> косметическое средство, притирание <i>Pl</i>;\n<b>2)</b> обман, заблуждение <i>Pl, AG</i>."],
  ["offui", "<big>offui</big>\n\n<b>offui</b> (= obfui) <i>pf. к</i>\nobsum."],
  ["offula", "<big>offula</big>\n\n<b>offula</b> (<i>Pt</i> <b>offla</b>), <b>ae</b> <i>f</i> [<i>demin. к</i> offa]\nкусочек (carnis <i>Vr, Col</i>; panis <i>Veg</i>): crucis o. <i>бран. Pt</i> висельник, негодяй."],
  ["offulcio", "<big>offulcio</big>\n\n<b>of–fulcio, (fulsi), fultum, ire</b>\nзатыкать, закрывать (vulnus spongia <i>Ap</i>)."],
  ["offulgeo", "<big>offulgeo</big>\n\n<b>of–fulgeo, fulsi, —, ere</b>\nсиять, светить навстречу (alicui rei <i>Sil</i>): nova lux oculis offulsit <i>V</i> новый свет блеснул в глаза."],
  ["offundo", "<big>offundo</big>\n\n<b>of–fundo, fudi, fusum, ere</b>\n<b>1)</b> лить, сыпать (перед кем-л. <i>или</i> чём-л.) (alicui, alicui rei): o. cibum avibus <i>Pl</i> засыпать корму птицам;\n<b>2)</b> <i>med.-pass.</i> offundi падать, растянуться (asinus offunditur <i>Ap</i>);\n<b>3)</b> разливать, распространять: aer offunditur nobis <i>C</i> воздух окружает нас; multa simul offundebantur <i>T</i> многие мысли одновременно пришли в голову (Корбулону); o. ruborem alicui <i>Ap</i> (<i>v. l.</i>) вогнать кого-л. в краску, заставить кого-л. покраснеть; o. terrorem <i>L</i> нагнать страх; o. errorem <i>C</i> ввести в заблуждение;\n<b>4)</b> застилать, расстилать (velut nebula nobis ob oculos offusa <i>Ap</i>): o. noctem alicui rei <i>C</i> затемнять (затуманивать) что-л.;\n<b>5)</b> покрывать: offusus pavore <i>T</i> объятый страхом; lucernae lumen luce solis offunditur <i>C</i> свет фонаря затмевается солнечным светом."],
  ["offuscatio", "<big>offuscatio</big>\n\n<b>offuscatio, onis</b> <i>f</i> [offusco]\nзатемнение, помрачение, <i>перен.</i> умаление <i>Vlg</i>: o. deorum <i>Tert</i> сумерки богов."],
  ["offusco", "<big>offusco</big>\n\n<b>of–fusco, —, atum, are</b>\nзатемнять, помрачать (lumen <i>Eccl</i>); умалять, унижать (justitiam <i>Tert</i>)."],
  ["offusio", "<big>offusio</big>\n\n<b>offusio, onis</b> <i>f</i> [offundo]\n<b>1)</b> распространение (tenebrarum <i>Ambr</i>);\n<b>2)</b> помрачение, ослепление (o. mentem occupat <i>Ambr</i>)."],
  ["ogdoas", "<big>ogdoas</big>\n\n<b>ogdoas, adis</b> <i>f</i> (<i>греч.</i>)\nогдоада, восьмерица <i>Hier, Tert</i>."],
  ["ogg-", "<big>ogg-</big>\n\n<b>og–g-</b>\n= ob–g-."],
  ["Ogyges", "<big>Ogyges</big>\n\n<b>Ogyges, is</b> <i>и</i> <b>i</b> <i>и</i> <b>Ogygus, i</b> <i>m</i>\nОгиг, <i>древнейший царь Фив</i> <i>Vr, Aug</i>."],
  ["Ogygius", "<big>Ogygius</big>\n\n<b>Ogygius, a, um</b> [Ogyges]\nогигов, <i>поэт.</i> фиванский (populus <i>Sen</i>): O. deus <i>O</i> = Bacchus."],
  ["oh", "<big>oh</big>\n\n<b>&#333;h!</b> <i>interj.</i>\nо!, ах! <i>Pl, Ter etc.</i>"],
  ["Oh", "<big>Oh</big>\n\n<b>&#332; (&#333;h)!</b> <i>interj.</i>\nо!, ах! (<i>возглас радости, горя, удивления</i>)<i>: c vocat.</i> (o mi Furni! <i>C</i>), <i>c</i> <i>acc.</i> (o me miserum! <i>C</i>) <i>или с</i> <i>nom.</i> (o vir fortis! <i>Ter</i>); <i>иногда поэт. с</i> <i>gen.</i> (o. mihi nuntii, beati! <i>Ctl</i>); (<i>тж. при пожеланиях</i>) (o si adesset! <i>V</i>)."],
  ["ohe", "<big>ohe</big>\n\n<b>&#333;h&#275;!</b> <i>interj.</i>\nэй!, слушай! (o.! desine <i>Ter</i>)."],
  ["oho", "<big>oho</big>\n\n<b>oho!</b> <i>interj.</i>\nого! (<i>возглас удивления</i>) <i>Pl, Ter</i>."],
  ["oiei", "<big>oiei</big>\n\n<b>oiei!</b> <i>interj.</i>\nох! (<i>возглас скорби</i>) <i>Ter</i>."],
  ["Oileus 1", "<big>Oileus</big>\n\n<b>I O&#299;le&#865;us, e&#299;</b> <i>и</i> <b>eos</b> <i>m</i>\nОилей, <i>миф. царь локров, отец Эанта</i> <i>C</i>."],
  ["Oileus 2", "<big>Oileus</big>\n\n<b>II O&#299;l&#275;us, a, um</b> [Oileus I]\nоилеев (Ajax <i>C</i>)."],
  ["Oiliades", "<big>Oiliades</big>\n\n<b>Oiliades (Oilides), ae</b> <i>m</i>\nсын Оилея, <i>т. е.</i> Эант (Аякс) <i>Prp, Sil</i>."],
  ["oinos", "<big>oinos</big>\n\n<b>oinos, a, um</b> <i>арх.</i> <i>C</i>\n= unus."],
  ["Olbia", "<big>Olbia</big>\n\n<b>Olbia, ae</b> <i>f</i>\nОльбия:\n<b>1)</b> <i>город в Памфилии</i> <i>Mela</i>;\n<b>2)</b> <i>город в Вифинии</i> (<i>впоследствии</i> Nicea) <i>PM</i>;\n<b>3)</b> <i>город в сев.-вост. Сардинии</i> <i>C, Fl, VM</i>."],
  ["Olbiensis", "<big>Olbiensis</big>\n\n<b>Olbiensis, e</b> [Olbia]\nольбийский <i>C, L</i>."],
  ["Olcades", "<big>Olcades</big>\n\n<b>Olcades, um</b> <i>m</i>\nолькады, <i>племя в Hispania Tarraconensis, к сев. от Нового Карфагена</i> <i>L</i>."],
  ["Olcinium", "<big>Olcinium</big>\n\n<b>Olcinium, i</b> <i>n</i>\n<i>приморский город в Иллирии</i> <i>L, PM</i>."],
  ["olea", "<big>olea</big>\n\n<b>olea, ae</b> <i>f</i> <i>Cato, Col, C etc.</i>\n= oliva."],
  ["oleagineus", "<big>oleagineus</big>\n\n<b>oleagineus, a, um</b> [olea]\n<b>1)</b> оливковый, масличный (semen <i>Vr</i>; virgula <i>Nep</i>);\n<b>2)</b> похожий на маслину (uva <i>PM</i>);\n<b>3)</b> оливковый, цвета маслины (color <i>Veg</i>)."],
  ["oleaginus", "<big>oleaginus</big>\n\n<b>oleaginus, a, um</b> <i>V, Nep etc.</i>\n= oleagineus."],
  ["oleamen", "<big>oleamen</big>\n\n<b>oleamen, inis</b> <i>и</i> <b>oleamentum, i</b> <i>n</i> [oleum]\nмазь на оливковом масле <i>Scr</i>."],
  ["olearius 1", "<big>olearius</big>\n\n<b>I olearius, a, um</b> [olea]\nмасляный, предназначенный для (оливкового) масла (cella <i>Cato, C</i>; ampulla <i>Ap</i>; vasa <i>Col</i>)."],
  ["olearius 2", "<big>olearius</big>\n\n<b>II olearius, i</b> <i>m</i>\nмаслобойщик, торговец маслом <i>Pl, Col</i>."],
  ["Olearos", "<big>Olearos</big>\n\n<b>Olearos, i</b> <i>f</i>\nОлеар, <i>о-в из группы Киклад, к юго-зап. от Пароса</i> (<i>ныне</i> Антипаро) <i>V, Mela</i>."],
  ["oleastellus", "<big>oleastellus</big>\n\n<b>oleastellus, i</b> <i>m</i> [<i>demin. к</i> oleaster]\n<i>вид масличного дерева</i> <i>Col</i>."],
  ["oleaster", "<big>oleaster</big>\n\n<b>oleaster, stri</b> <i>m</i> [olea]\nдикая маслина <i>C, V, PM</i>."],
  ["oleastrum", "<big>oleastrum</big>\n\n<b>oleastrum, i</b> <i>n</i> <i>Calp</i>\n= oleaster."],
  ["oleatus", "<big>oleatus</big>\n\n<b>oleatus, a, um</b> [oleum]\nприготовленный на оливковом масле (panis <i>Vlg</i>; puls <i>CA</i>)."],
  ["oleitas", "<big>oleitas</big>\n\n<b>oleitas, atis</b> <i>f</i> [olea]\n<b>1)</b> время сбора маслин, <i>тж.</i> сбор маслин <i>Cato, MF</i>;\n<b>2)</b> добывание оливкового масла <i>Cato</i>."],
  ["Olenides", "<big>Olenides</big>\n\n<b>Olenides, ae</b> <i>m</i>\nсыи Олена <i>VF, O</i> (<i>см.</i> Olenus II)."],
  ["Olenie", "<big>Olenie</big>\n\n<b>Olenie, es</b> <i>f</i> [Olenius]\nКоза Амалтея (<i>созвездие</i>) <i>Man</i>."],
  ["Olenius", "<big>Olenius</big>\n\n<b>Olenius, a, um</b> [Olenus II]\nоленский, <i>поэт.</i> этолийский: capella Olenia <i>O</i> = Amalthea."],
  ["olens", "<big>olens</big>\n\n<b>olens, entis</b>\n<b>1.</b> <i>part. praes. к</i> oleo;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> пахучий, душистый (olentia pascua <i>O</i>);\n<b>2)</b> неприятно пахнущий, зловонный (stagna olentia sulpure <i>O</i>)."],
  ["olentia", "<big>olentia</big>\n\n<b>olentia, ae</b> <i>f</i> [oleo]\nзапах (suavis <i>Tert</i>)."],
  ["olenticetum", "<big>olenticetum</big>\n\n<b>olenticetum, i</b> <i>n</i> [oleo]\nзловонная лужа <i>Ap</i>."],
  ["Olenus 1", "<big>Olenus</big>\n\n<b>I Olenus, i</b> <i>f</i>\nОлен, <i>древний город Этолии, близ Плеврона</i> <i>SenT</i>."],
  ["Olenus 2", "<big>Olenus</big>\n\n<b>II Olenus, i</b> <i>m</i>\nОлен, <i>сын Юпитера, муж Летеи, превращенный в камень</i> <i>O</i>."],
  ["oleo", "<big>oleo</big>\n\n<b>oleo, lui, —, ere</b> [<i>одного корня с</i> odor]\n<b>1)</b> пахнуть, издавать запах (bene <i>C</i>; suave <i>Ctl</i>; male <i>Pl</i>): o. aliquid <i>C, H etc.</i> <i>или</i> aliqua re <i>Prp, O</i> пахнуть чём-л.; nihil o. <i>C</i> не иметь запаха, ничем не пахнуть;\n<b>2)</b> <i>перен.</i> отдавать (чём-л.), обнаруживать, выказывать (malitiam <i>C</i>): aurum huic olet <i>Pl</i> он чует, что (у меня) есть золото; nihil olet ex Academia <i>C</i> (у Эпикура) нет и намёка на учение Академии."],
  ["oleosus", "<big>oleosus</big>\n\n<b>oleosus, a, um</b> [oleum]\nмаслянистый (semen <i>PM</i>)."],
  ["olera", "<big>olera</big>\n\n<b>olera</b> <i>pl. к</i>\nolus."],
  ["oleraceus", "<big>oleraceus</big>\n\n<b>oleraceus, a, um</b> [olus]\nтравянистый (frutex <i>PM</i>)."],
  ["oleris", "<big>oleris</big>\n\n<b>oleris</b> <i>и</i> <b>olerorum</b> — <i>см.</i>\nolus."],
  ["oleto", "<big>oleto</big>\n\n<b>oleto, avi, —, are</b> [oletum II]\nмарать, пачкать <i>Frontin, Ambr</i>."],
  ["oletum 1", "<big>oletum</big>\n\n<b>I oletum, i</b> <i>n</i> [olea] <i>Cato</i>\n= olivetum."],
  ["oletum 2", "<big>oletum</big>\n\n<b>II oletum, i</b> <i>n</i> [oleo]\nнавоз, экскременты <i>Pers</i>."],
  ["oleum", "<big>oleum</big>\n\n<b>oleum, i</b> <i>n</i> [olea]\n<b>1)</b> растительное масло (amygdalinum, laurinum <i>PM</i>), <i>но преим.</i> оливковое (деревянное) масло: addere o. camino <i>погов. H</i> подливать масла в огонь; o. et operam perdere <i>погов. Pl, C</i> потерять масло (в светильнике) и труд, <i>т. е.</i> напрасно потрудиться; oleo tranquillior <i>погов. Pl</i> тише масла (<i>ср. русск.</i> тише воды);\n<b>2)</b> гимнастические упражнения, <i>тж.</i> гимнастический зал, палестра (<i>так как при гимнастических упражнениях натирались маслом</i>): decus olei <i>Ctl</i> краса палестры || ученичество, школа: verba palaestrae et olei <i>C</i> речь, уместная в ученических упражнениях, <i>т. е.</i> школьная."],
  ["olfacio", "<big>olfacio</big>\n\n<b>olfacio, feci, factum, ere</b> [oleo + facio]\n<b>1)</b> обонять, чувствовать запах (o. unguentum <i>Ctl</i>): sensus olfaciendi <i>Aug</i> обоняние; sagacissime o. <i>PM</i> иметь весьма тонкое обоняние;\n<b>2)</b> <i>перен.</i> чуять, разнюхать, проведать (nummum <i>C</i>; nomen alicujus <i>Pt</i>);\n<b>3)</b> придавать запах: o. labra agni lacte <i>Vr</i> приучить ягнёнка к запаху молока."],
  ["olfactatrix", "<big>olfactatrix</big>\n\n<b>olfactatrix, icis</b> <i>f</i> [olfacto]\nимеющая чутьё <i>PM</i>."],
  ["olfacto", "<big>olfacto</big>\n\n<b>olfacto, avi, atum, are</b> [<i>intens. к</i> olfacio] <i>Pt, PM</i>\n= olfacio."],
  ["olfactoriolum", "<big>olfactoriolum</big>\n\n<b>olfactoriolum, i</b> <i>n</i> [<i>demin. к</i> olfactorium]\nфлакончик духов <i>Vlg, Eccl, Sid</i>."],
  ["olfactorium", "<big>olfactorium</big>\n\n<b>olfactorium, i</b> <i>n</i> [olfacio]\nбанка с благовониями <i>PM</i>."],
  ["olfactorius", "<big>olfactorius</big>\n\n<b>olfactorius, a, um</b> [olfacio]\nблаговонный, душистый <i>Fronto</i>."],
  ["olfactus", "<big>olfactus</big>\n\n<b>olfactus, us</b> <i>m</i> [olfacio]\n<b>1)</b> обоняние <i>PM, Aug</i>;\n<b>2)</b> запах <i>PM</i>."],
  ["olfeci", "<big>olfeci</big>\n\n<b>olfeci</b> <i>pf. к</i>\nolfacio."],
  ["olidus", "<big>olidus</big>\n\n<b>olidus, a, um</b> [oleo]\n<b>1)</b> пахучий, пахнущий (bene o. <i>Col</i>);\n<b>2)</b> зловонный (capra <i>H</i>; basia <i>Pt</i>)."],
  ["olim", "<big>olim</big>\n\n<b>olim</b> [<i>одного корня с</i> ollus]\n<b>1)</b> когда-то, некогда, встарь, прежде (sic o. loquebantur <i>C</i>); однажды (fuit o. senex <i>Pl, Ter</i>);\n<b>2)</b> давно: o. nescio, quid sit otium <i>PJ</i> я давно уже не знаю, что такое досуг; o. oliorum <i>шутл. Pt</i> давным-давно <i>или</i> с давних пор;\n<b>3)</b> иногда, временами, подчас, изредка: ut o. carmina moriens canit cygnus <i>O</i> как иногда поёт (свою) песнь умирающий лебедь;\n<b>4)</b> когда-либо: vestra meos o. si fistula dicat amores <i>V</i> если когда-либо ваша свирель воспоёт мои любовные томления;\n<b>5)</b> когда-нибудь, в будущем (dolor hic tibi proderit o. <i>O</i>); впредь: nunc, o., quocumque dabunt se tempore vires <i>V</i> ныне, впредь, когда только будут силы."],
  ["oliorum", "<big>oliorum</big>\n\n<b>oliorum</b> <i>предпол.</i> <i>gen. pl. к</i>\nolle (= ille) (<i>тк. в выраж.</i> olim o. — <i>см.</i> olim 2)."],
  ["olitor", "<big>olitor</big>\n\n<b>olitor, oris</b> <i>m</i> [olus]\nзеленщик, огородник <i>Naev, Pl, C etc.</i>"],
  ["olitorius", "<big>olitorius</big>\n\n<b>olitorius, a, um</b> [olitor]\nогородный, овощной, зеленной (forum <i>Vr, L, T</i>): hortus o. <i>Dig</i> огород."],
  ["oliva", "<big>oliva</big>\n\n<b>oliva, ae</b> <i>f</i>\n<b>1)</b> оливка, маслина <i>Col, H etc.</i>: mons <i>или</i> clivus Olivarum <i>Vlg</i> Масличная гора;\n<b>2)</b> (<i>тж.</i> arbor olivae <i>Col</i>) масличное (оливковое) дерево, олива <i>C, H</i>;\n<b>3)</b> (<i>тж.</i> ramus olivae <i>V</i>) масличная ветвь <i>H</i>;\n<b>4)</b> масличный венок (fronti praeponere olivam <i>H</i>);\n<b>5)</b> посох из масличного дерева (incumbere olivae <i>V</i>)."],
  ["olivarius", "<big>olivarius</big>\n\n<b>olivarius, a, um</b> [oliva]\nмасличный, оливковый <i>или</i> масляный (metretae <i>Col</i>)."],
  ["olivetum", "<big>olivetum</big>\n\n<b>olivetum, i</b> <i>n</i> [oliva]\nместо, засаженное оливковыми деревьями, масличная роща <i>V, C, Col</i>."],
  ["olivifer", "<big>olivifer</big>\n\n<b>olivifer, fera, ferum</b> [oliva + fero]\nприносящий оливы, покрытый масличными рощами (arva <i>O</i>): corona olivifera <i>M</i> оливковый венок."],
  ["olivitas", "<big>olivitas</big>\n\n<b>olivitas, atis</b> <i>f</i> <i>Vr, Col</i>\n= oleitas."],
  ["olivitor", "<big>olivitor</big>\n\n<b>olivitor, oris</b> <i>m</i>\nвзращивающий оливковые деревья <i>Sid</i>."],
  ["olivo", "<big>olivo</big>\n\n<b>olivo, —, —, are</b>\nсобирать маслины, производить сбор урожая оливок <i>PM</i>."],
  ["olivum", "<big>olivum</big>\n\n<b>olivum, i</b> <i>n</i> <i>поэт.</i>\n<b>1)</b> <i>Pl, H, V etc.</i> = oleum;\n<b>2)</b> душистая мазь <i>Ctl, Prp</i>."],
  ["olla 1", "<big>olla</big>\n\n<b>I olla, ae</b> <i>f</i>\nгоршок (o. fictilis <i>Col</i>; o. aenea <i>Hier</i>): o. male fervet <i>погов. Pt</i> = дела неважны; ipsa olera o. legit <i>погов. Ctl</i> горшок сам выбирает (себе) овощи, <i>т. е.</i> всякий следует своей склонности."],
  ["olla 2", "<big>olla</big>\n\n<b>II olla</b> <i>арх. Enn etc.</i>\n= illa II."],
  ["ollaris", "<big>ollaris</big>\n\n<b>ollaris, e</b> [olla I]\nгоршечный, содержащийся в горшках (uvae <i>M</i>)."],
  ["ollarius", "<big>ollarius</big>\n\n<b>ollarius, a, um</b> <i>PM</i>\n= ollaris."],
  ["olle", "<big>olle</big>\n\n<b>olle</b> <i>арх. V etc.</i>\n= ille."],
  ["ollicoquus", "<big>ollicoquus</big>\n\n<b>olli–coquus, a, um</b>\nсваренный в горшке, тушёный (exta <i>Vr</i> — <i>v. l.</i>)."],
  ["ollula", "<big>ollula</big>\n\n<b>ollula, ae</b> <i>f</i> [<i>demin. к</i> olla I]\nгоршочек <i>Vr, Ap</i>."],
  ["ollus", "<big>ollus</big>\n\n<b>ollus, a, um</b> <i>арх. Enn, C etc.</i>\n= ille."],
  ["olo", "<big>olo</big>\n\n<b>olo, —, —, ere</b> <i>Pl</i>\n= oleo."],
  ["olor 1", "<big>olor</big>\n\n<b>I olor, oris</b> <i>m</i>\nлебедь <i>PM, Su, Ap etc.</i>"],
  ["olor 2", "<big>olor</big>\n\n<b>II olor, oris</b> <i>m</i> [oleo]\nзапах <i>Vr</i>."],
  ["olorifer", "<big>olorifer</big>\n\n<b>olori–fer, fera, ferum</b> [olor I]\nизобилующий лебедями (Padus <i>Cld</i>)."],
  ["olorinus", "<big>olorinus</big>\n\n<b>olorinus, a, um</b> [olor I]\nлебединый (alae <i>O</i>; pennae <i>V</i>; color <i>PM</i>; cantus <i>Sid</i>)."],
  ["olosericus", "<big>olosericus</big>\n\n<b>olosericus, a, um</b> <i>v. l.</i>\n= holosericus."],
  ["olus", "<big>olus</big>\n\n<b>olus (holus), eris</b> <i>n</i> (<i>gen. pl. LM</i> olerorum; <i>abl. pl. Cato</i> oleris)\nзелень, овощи <i>Col, V, H etc.</i>: o. atrum <i>Pl, Col</i> разновидность петрушки (<i>Smyrnium olusatrtim</i>, L.)."],
  ["olusatrum", "<big>olusatrum</big>\n\n<b>olusatrum, i</b> <i>n</i> <i>Col, PM etc.</i>\n= olus atrum (<i>см.</i> olus)."],
  ["olusculum", "<big>olusculum</big>\n\n<b>olusculum, i</b> <i>n</i> [<i>demin. к</i> olus]\nмелкое огородное растение, овощ <i>C, H, J etc.</i>"],
  ["Olympeni", "<big>Olympeni</big>\n\n<b>Olympeni, orum</b> <i>m</i>\nжители города Олимпа (<i>в Дикий</i>) <i>C</i>."],
  ["Olympia 1", "<big>Olympia</big>\n\n<b>I Olympia, ae</b> <i>f</i>\nОлимпия, <i>посвященная Юпитеру местность в Elis Pisatis, на сев. берегу Алфея, в которой каждые четыре года устраивались Олимпийские игры</i> <i>Vr, rhH, C, L</i>."],
  ["Olympia 2", "<big>Olympia</big>\n\n<b>II Olympia, orum</b> <i>n</i>\nОлимпийские игры <i>C</i>."],
  ["Olympiacus", "<big>Olympiacus</big>\n\n<b>Olympiacus, a, um</b>\nолимпийский <i>V, Su</i>."],
  ["Olympias", "<big>Olympias</big>\n\n<b>Olympias, adis</b> <i>f</i>\nОлимпиада:\n<b>1)</b> четырёхлетие (<i>у древних греков летосчисление по Олимпиадам велось с 776 г. до н. э.</i>) <i><i>C etc.</i>; поэт.</i> пятилетие (= lustrum) <i>O, M</i>;\n<b>2)</b> <i>жена Филиппа Македонского, мать Александра</i> <i>C, QC</i>."],
  ["Olympicus", "<big>Olympicus</big>\n\n<b>Olympicus, a, um</b> <i>H</i>\n= Olympiacus."],
  ["Olympieum", "<big>Olympieum</big>\n\n<b>Olympieum, i</b> <i>n</i>\nОлимпий, <i>храм Юпитера Олимпийского</i> <i>VP</i>."],
  ["Olympionices", "<big>Olympionices</big>\n\n<b>Olympionices, ae</b> <i>m</i> (<i>греч.</i>)\nпобедитель на Олимпийских играх <i>C</i>."],
  ["Olympium", "<big>Olympium</big>\n\n<b>Olympium, i</b> <i>n</i>\nОлимпий, <i>храм Юпитера и городок близ Сиракуз</i> (<i>Сицилия</i>) <i>Vtr, L</i>."],
  ["Olympius", "<big>Olympius</big>\n\n<b>Olympius, a, um</b> <i>Pl, C etc.</i>\n= Olympiacus."],
  ["Olympus 1", "<big>Olympus</big>\n\n<b>I Olympus, i</b> <i>m</i>\nОлимп;\n<b>1)</b> <i>горная гряда на границе Македонии и Фессалии, местопребывание богов</i> <i>Vr, Mela, V etc.</i>;\n<b>2)</b> <i>поэт.</i> небо, небесный свод (stelliger O. <i>SenT</i>);\n<b>3)</b> <i>флейтист, ученик Марсия</i> <i>O, PM</i>."],
  ["Olympus 2", "<big>Olympus</big>\n\n<b>II Olympus, i</b> <i>f</i>\nОлимп, <i>приморский город в вост. Ликии</i> <i>C, Fl, Eutr</i>."],
  ["Olynthius 1", "<big>Olynthius</big>\n\n<b>I Olynthius, a, um</b> [Olynthus]\nолинфский <i>Vr</i>."],
  ["Olynthius 2", "<big>Olynthius</big>\n\n<b>II Olynthius, i</b> <i>m</i>\nжитель города Олинфа <i>Nep, Sen</i>."],
  ["Olynthus", "<big>Olynthus</big>\n\n<b>Olynthus, i</b> <i>f</i>\nОлинф, <i>город в Халкидике, на берегу Торонейского залива</i> (<i>в 347 г. до н. э. разрушен Филиппом Македонским</i>) <i>Nep, Sen etc.</i>"],
  ["omasum", "<big>omasum</big>\n\n<b>omasum, i</b> <i>n</i> (<i>галльск.</i>)\nбычачьи внутренности, потроха (o. pingue <i>H</i>)."],
  ["omen 1", "<big>omen</big>\n\n<b>I omen, inis</b> <i>n</i>\n<b>1)</b> знак, знамение, примета, предзнаменование (tristia omina dare <i>O</i>): omine fausto (dextro, secundo) <i>C, L, Su</i> со счастливым предзнаменованием, счастливо, в добрый час; nomen atque o. <i>Pl</i> имя, в котором содержится предзнаменование; o. capere <i>C</i> выжидать (высматривать) примету; o. accipere <i>C, L</i> признать (что-л.) счастливым предзнаменованием; o. abnuere <i>V</i> не усмотреть (в чём-л.) предзнаменования; quod o. Juppiter avertat <i>C</i> да сохранит (нас) Юпитер (от этого); o. res consecuta est <i>C</i> предзнаменование осуществилось; i secundo omine <i>H</i> иди с богом, счастливого пути;\n<b>2)</b> желание, пожелание: fausta <i>Su</i> (optima <i>C</i>) omina наилучшие пожелания;\n<b>3)</b> торжественное заверение, условие: ea lege et omine <i>Ter</i> под этой торжественной клятвой;\n<b>4)</b> <i>поэт.</i> торжественный обычай, обряд (<i>сопряжённый с наблюдением примет</i>); hic sceptra accipere regibus o. erat <i>V</i> здесь, по обычаю, (римские) цари принимали скипетры;\n<b>5)</b> бракосочетание, брак (prima omina <i>V</i>)."],
  ["omen 2", "<big>omen</big>\n\n<b>II omen, inis</b> <i>n</i> <i>Eccl</i>\n= omentum."],
  ["omentum", "<big>omentum</big>\n\n<b>omentum, i</b> <i>n</i>\n<b>1)</b> жировая оболочка, сальник, <i>тж.</i> брюшина <i>CC, PM</i>;\n<b>2)</b> внутренности, потроха <i>J, Pers</i>;\n<b>3)</b> <i>анат.</i> перепонка, оболочка (<i>вообще</i>) <i>Macr, Su</i>;\n<b>4)</b> жир <i>Pers</i>."],
  ["ominalis", "<big>ominalis</big>\n\n<b>ominalis, e</b> <i>AG</i>\n(<i>v. l.</i>)= ominosus."],
  ["ominator", "<big>ominator</big>\n\n<b>ominator, oris</b> <i>m</i> [ominor]\nпрорицатель <i>Pl</i>."],
  ["ominor", "<big>ominor</big>\n\n<b>ominor, atus sum, ari</b> <i>depon.</i> [omen I]\n<b>1)</b> знаменовать, предзнаменовывать, предвещать, предсказывать, пророчить (alicui aliquid <i>C, L etc.</i>): male ominata verba <i>H</i> слова, предвещающие недоброе;\n<b>2)</b> желать (alicui prospera <i>Su</i>);\n<b>3)</b> предчувствовать, чуять: velut ominatus sese venisse <i>L</i> словно предчувствуя, что ему придётся вернуться."],
  ["ominosus", "<big>ominosus</big>\n\n<b>ominosus, a, um</b> [omen I]\nпредвещающий дурное, зловещий, роковой <i>PJ, Lampr, AG</i>."],
  ["omisi", "<big>omisi</big>\n\n<b>omisi</b> <i>pf. к</i>\nomitto."],
  ["omissio", "<big>omissio</big>\n\n<b>omissio, onis</b> <i>f</i> [omitto]\n<b>1)</b> опущение, пропуск <i>Symm</i>;\n<b>2)</b> отнятие, лишение (alicujus rei <i>AV</i>)."],
  ["omissus", "<big>omissus</big>\n\n<b>omissus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> omitto;\n<b>2.</b> <i>adj.</i> невнимательный, беззаботный, беспечный (animo esse omisso <i>Ter</i>): o. ab re <i>Ter</i> легко относящийся к своим материальным интересам."],
  ["omitto", "<big>omitto</big>\n\n<b>omitto, misi, missum, ere</b> [ob + mitto]\n<b>1)</b> выпускать из рук (habenas <i>T</i>); ронять, отбрасывать, бросать (arma <i>L</i>);\n<b>2)</b> упускать, не воспользоваться (o. occasionem, tempus, navigationem <i>C</i>); оставлять без внимания, оставлять в покое, упускать из виду, пренебрегать (hostem, Galliam <i>L</i>; consilium <i>L</i>): omitte me <i>Ter</i> оставь меня в покое: o. scelus impunitum <i>Sl</i> оставить преступление безнаказанным;\n<b>3)</b> обходить молчанием, умалчивать, не упоминать (aliquid <i>или</i> de aliqua re, aliquem <i>Ter, C etc.</i>): at id omitto <i>Ter</i> но об этом я говорить не стану;\n<b>4)</b> отказываться, покидать (voluptates <i>C</i>); переставать (lugere <i>C</i>; iratum esse <i>Pl</i>; mirari <i>H</i>): omnibus rebus omissis <i>Cs</i> оставляя всё (это) в стороне; o. opus <i>H</i> — <i>см.</i> repeto 4; o. animam <i>Pl</i> умереть."],
  ["ommento", "<big>ommento</big>\n\n<b>ommento, —, —, are</b> [ob + manto]\nждать, выжидать <i>LA</i>."],
  ["omnicanus", "<big>omnicanus</big>\n\n<b>omnicanus, a, um</b> [omnis + cano]\nпоющий на все лады <i>или</i> везде раздающийся (oratio <i>Ap</i>)."],
  ["omnicarpus", "<big>omnicarpus</big>\n\n<b>omni–carpus, a, um</b> [carpo]\nвсё обгладывающий (capra <i>Vf</i>)."],
  ["omnicreans", "<big>omnicreans</big>\n\n<b>omni–creans, antis</b>\nвсесозидающий <i>Aug</i>."],
  ["omnifariam", "<big>omnifariam</big>\n\n<b>omnifariam</b> <i>adv.</i>\nвезде, повсюду (terras o. circumfluere <i>AG</i>); всячески, всесторонне (sapientiae studium con temptare <i>Ap</i>)."],
  ["omnifarius", "<big>omnifarius</big>\n\n<b>omnifarius, a, um</b>\nвсесторонний, всяческий (mitigatio <i>CA</i>)."],
  ["omnifer", "<big>omnifer</big>\n\n<b>omnifer, fera, ferum</b> [omnis + fero]\nвсё приносящий, всё поддерживающий, всё (на себе) несущий <i>O</i> (<i>v. l.</i>)."],
  ["omnifluentia", "<big>omnifluentia</big>\n\n<b>omni–fluentia, ae</b> <i>f</i>\nчрезвычайное обилие <i>Lact</i>."],
  ["omniformis", "<big>omniformis</big>\n\n<b>omni–formis, e</b> [forma]\nвсевозможных образов (форм) (species <i>Ap</i>)."],
  ["omnigena", "<big>omnigena</big>\n\n<b>omnigena, ae</b> (<i>gen. pl.</i> um) <i>m, f</i> [omnis + geno = gigno]\nразнородный, разный, всякий, всяческий <i>V, Cld</i>."],
  ["omnigenus", "<big>omnigenus</big>\n\n<b>omnigenus, a, um</b> <i>V</i>\n= omnigena."],
  ["omnimodis", "<big>omnimodis</big>\n\n<b>omnimodis</b> [omnis + modus] <i>adv.</i>\nвсеми способами, всеми средствами, всячески <i>или</i> во всех отношениях, вполне <i>Lcr, Ap etc.</i>"],
  ["omnimodo", "<big>omnimodo</big>\n\n<b>omnimodo</b> <i>AG, Dig</i>\n= omni modis."],
  ["omnimodus", "<big>omnimodus</big>\n\n<b>omni–modus, a, um</b>\nвсяческий, всевозможный <i>Ap</i>."],
  ["omnino", "<big>omnino</big>\n\n<b>omnino</b> <i>adv.</i> [omnis]\n<b>1)</b> полностью, вполне, целиком, совершенно, во всех отношениях (vir o. egregius <i>C</i>; o. nescire <i>C</i>; aut o. aut magna ex parte <i>C</i>): non o. <i>C</i> не совсем; o. non <i>C</i> совсем (ничуть) не; o. nemo <i>C</i> решительно никто;\n<b>2)</b> <i>в уступ. предлож.</i> конечно, бесспорно: pugnas o., sed cum adversario facili <i>C</i> (бороться-то) ты, конечно, борешься, но с лёгким (слабым) противником;\n<b>3)</b> в целом, в общем, вообще (hominum genus aut o. animalia <i>C</i>): si quid o. fuisset <i>Su</i> если бы нечто подобное вообще произошло;\n<b>4)</b> всего, в общей сложности (octo o. legiones <i>L</i>); всего лишь (duo o. itinera erant <i>Cs</i>)."],
  ["omninominis", "<big>omninominis</big>\n\n<b>omni–nominis, e</b>\nносящий все имена (тот, к которому применимо любое имя) <i>Ap</i>."],
  ["omniparens", "<big>omniparens</big>\n\n<b>omni–parens, entis</b>\nвсепроизводящий, всерождающий (terra <i>Lcr, V</i>)."],
  ["omnipavus", "<big>omnipavus</big>\n\n<b>omni–pavus, a, um</b> [paveo]\nбоящийся всего <i>CA</i>."],
  ["omnipotens", "<big>omnipotens</big>\n\n<b>omni–potens, entis</b>\nвсемогущий <i>Enn, V, Ctl etc.</i>"],
  ["omnipotenter", "<big>omnipotenter</big>\n\n<b>omnipotenter</b>\nсилой своего всемогущества <i>Aug</i>."],
  ["omnipotentia", "<big>omnipotentia</big>\n\n<b>omni–potentia, ae</b> <i>f</i>\nвсемогущество <i>Macr, Eccl</i>."],
  ["omnis", "<big>omnis</big>\n\n<b>omnis, e</b>\n<b>1)</b> каждый, всякий (per omnia maria currere <i>H</i>): omnibus horis <i>PM</i> в любой час, постоянно; omnibus locis <i>V</i> всюду, везде; omnibus annis <i>Lcn</i> из года в год, ежегодно; in omni re <i>C</i> во всём; ad unum omnes <i>C etc.</i> все до одного (без исключения); constat inter omnes <i>C</i> всем известно;\n<b>2)</b> всевозможный, всякого рода, всяческий (honores <i>C</i>; pollicitationes, preces <i>Cs</i>);\n<b>3)</b> один лишь, сплошной (per omnia deserta proficisci <i>L</i>);\n<b>4)</b> весь, целый, совокупный, полный (natio, opus <i>C</i>): non omnis moriar <i>H</i> не весь я умру; omnis feret omnia tellus <i>V</i> (в золотой век Сатурна) вся земля будет давать всё (необходимое); in eo sunt omnia <i>C</i> в этом (от этого зависит) всё; cum eo mihi omnia sunt <i>C</i> я с ним в прекрасных отношениях; imperaturus omnibus eligi debet ex omnibus <i>PJ</i> тот, кому предстоит повелевать всеми, должен избираться из числа всех; esse omnia alicui <i>L</i> быть кому-л. дороже всего; alia omnia <i>C, Sl</i> всё прочее, <i>но тж.</i> <i>C, PJ</i> всё, что угодно, кроме этого, <i>т. е.</i> совсем кет (как раз наоборот); in alia omnia ire (discedere) <i>C</i> <i>или</i> transire <i>Hirt</i> быть противоположного мнения; ad omnia <i>L</i>, per omnia <i>L, Q</i> <i>и</i> omnia <i>V</i> во всех отношениях, всецело;\n<b>5)</b> (= ullus) никакой (sine omni periclo <i>Ter</i>);\n<b>6)</b> <i>для усиления</i> <i>superl.</i>: omnium callidissimus <i>Nep</i> самый хитрый."],
  ["omniscius", "<big>omniscius</big>\n\n<b>omni–scius, a, um</b> [scio]\nвсеведущий <i>Aug</i>."],
  ["omnitenens", "<big>omnitenens</big>\n\n<b>omni–tenens, entis</b>\nвсеохватывающий, владеющий всем <i>Aug, Tert</i>."],
  ["omnituens", "<big>omnituens</big>\n\n<b>omni–tuens, entis</b> [tueor]\nвсевидящий (sensus <i>Lcr</i>; sol <i>Ap</i>)."],
  ["omnivagus", "<big>omnivagus</big>\n\n<b>omni–vagus, a, um</b>\nповсюду блуждающий, всюду бродящий (Diana <i>C</i>)."],
  ["omnivolus", "<big>omnivolus</big>\n\n<b>omni–volus, a, um</b> [volo II]\nжелающий всего (Juppiter <i>Ctl</i>)."],
  ["omnivorus", "<big>omnivorus</big>\n\n<b>omni–vorus, a, um</b> [voro]\nвсепожирающий, всеядный (boves <i>PM</i>)."],
  ["Omphale", "<big>Omphale</big>\n\n<b>Omphale, es</b> <i>f</i>\nОмфала, <i>царица Лидии, у которой Геркулес был три года рабом</i> <i>Ter, Prp, O</i>."],
  ["omphalos", "<big>omphalos</big>\n\n<b>omphalos, i</b> <i>m</i> (<i>греч.</i>; <i>лат.</i> umbilicus)\nпуп, <i>перен.</i> центр <i>Aus</i>."],
  ["onager", "<big>onager</big>\n\n<b>onager, gri</b> <i>m</i> (<i>греч.</i>)\nонагр :\n<b>1)</b> <i>дикий осёл</i> <i>CC, V, M etc.</i>;\n<b>2)</b> <i>воен. камнемёт</i> <i>Veg, Amm</i>."],
  ["onagos", "<big>onagos</big>\n\n<b>onagos, i</b> <i>m</i> (<i>греч.</i>)\nпогонщик ослов <i>Pl</i>."],
  ["onagrus", "<big>onagrus</big>\n\n<b>onagrus, i</b> <i>m</i> <i>Vr etc.</i>\n= onager."],
  ["Onchesmites", "<big>Onchesmites</big>\n\n<b>Onchesmites, ae</b> <i>m</i> (<i>sc.</i> ventus)\n<i>ветер, дующий в Италии со стороны Онхесма</i> (<i>портовый город в Эпире</i>) <i>C</i>."],
  ["Onchestius", "<big>Onchestius</big>\n\n<b>Onchestius, a, um</b> [Onchestus]\nонхестийский, <i>поэт.</i> беотийский <i>O</i>."],
  ["Onchestus 1", "<big>Onchestus</big>\n\n<b>I Onchestus, i</b> <i>f</i>\nОнхест, <i>город на Копаидском озере (Беотия) с храмом Нептуна</i> <i>PM</i>."],
  ["Onchestus 2", "<big>Onchestus</big>\n\n<b>II Onchestus, i</b> <i>m</i>\nОнхест, <i>река в Фессалии</i> <i>L</i>."],
  ["onco", "<big>onco</big>\n\n<b>onco, —, —, are</b>\nкричать (<i>об ослах</i>) <i>Su, Ap</i>."],
  ["oneraria", "<big>oneraria</big>\n\n<b>oneraria, ae</b> <i>f</i>\nгрузовое (транспортное) судно <i>Naev, Su, C</i>."],
  ["onerarius", "<big>onerarius</big>\n\n<b>onerarius, a, um</b> [onus]\nгрузовой (navis <i>Pl, Cs etc.</i>); вьючный (jumenta <i>L</i>)."],
  ["onero", "<big>onero</big>\n\n<b>onero, avi, atum, are</b> [onus]\n<b>1)</b> нагружать (navem <i>Cs</i>); грузить (на корабль), погружать (vinum <i>Pt</i>); навьючивать (jumenta <i>Sl</i>); переполнять, класть (лить) в изобилии (dona canistris — <i>dat. V</i>; vina cadis <i>V</i>): o. sinum suum saxis <i>Pt</i> набрать камней за пазуху; o. mensas dapibus <i>V</i> уставить столы множеством яств;\n<b>2)</b> обременять, перегружать, отягощать (ventrem <i>Sl</i>): o. aliquem malis <i>V</i> причинить кому-л. много зла; o. se <i>или</i> <i>pass.</i> onerari cibo (vino) <i>QC, Sl</i> слишком много есть (пить); o. membra sepulcro <i>V</i> хоронить мертвеца; o. manus jaculis <i>V</i> вооружиться метательными копьями; umerum pallio o. <i>Ter</i> накинуть (надеть) плащ; o. provinciam tributo <i>L</i> обложить провинцию податью; spe oneratus <i>L</i> полный надежд;\n<b>3)</b> обматывать, окутывать (oneratae veste cervices <i>Pt</i>);\n<b>4)</b> осыпать упрёками, обвинять (aliquem <i>T</i>); осыпать (aliquem honoribus <i>Just</i>; laudibus <i>L</i>); o. promissis <i>Sl, L</i> много обещать (сулить золотые горы);\n<b>5)</b> утомлять, докучать (aliquem votis <i>V</i>): o. contumeliis <i>C</i> позорить, бесчестить; o. injuria <i>Ter</i> сильно обидеть; o. pudore <i>O</i> покрывать позором; o. pudorem <i>или</i> verecundiam <i>Dig, CJ</i> оскорблять чувство стыда;\n<b>6)</b> отягощать, усиливать, усугублять, увеличивать (periculum <i>T</i>; inopiam <i>L</i>; curas <i>T</i>; injuriam invidia <i>L</i>);\n<b>7)</b> <i>с.-х.</i> случать (vaccas <i>Pall</i>)."],
  ["onerositas", "<big>onerositas</big>\n\n<b>onerositas, atis</b> <i>f</i>\nобременительность, тяжесть (ornandi capitis <i>Tert</i>)."],
  ["onerosus", "<big>onerosus</big>\n\n<b>onerosus, a, um</b> [onus]\n<b>1)</b> тяжёлый, увесистый, тяжеловесный (hasta <i>O</i>; praeda <i>V</i>);\n<b>2)</b> тягостный, обременительный (sors <i>O</i>; collatio <i>PJ</i>)."],
  ["Onesicritus", "<big>Onesicritus</big>\n\n<b>Onesicritus, i</b> <i>m</i>\nОнесикрит, <i>греч. историк, сопровождавший Александра Македонского в его походах</i> <i>QC, PM</i>."],
  ["oniros", "<big>oniros</big>\n\n<b>oniros, i</b> <i>m</i> (<i>греч.</i>)\nдикий мак <i>Ap</i>."],
  ["oniscos", "<big>oniscos</big>\n\n<b>oniscos (-us), i</b> <i>m</i> (<i>греч.</i>)\nмокрица <i>PM</i>."],
  ["onocentaurus", "<big>onocentaurus</big>\n\n<b>onocentaurus, i</b> <i>m</i> (<i>греч.</i>)\nонокентавр, <i>т. е.</i> ослокентавр (<i>баснословное животное</i>) <i>Vlg</i>."],
  ["onocrotalus", "<big>onocrotalus</big>\n\n<b>onocrotalus, i</b> <i>m</i> (<i>греч.</i>)\nпеликан (<i>Pelecanus O.</i> L.) <i>PM, M, Vlg</i>."],
  ["onus", "<big>onus</big>\n\n<b>onus, eris</b> <i>n</i>\n<b>1)</b> бремя, тяжесть, вес (turris tanti oneris <i>Cs</i>); груз (onera transportare <i>Cs</i>): o. navis <i>C, L</i> корабельный груз; onera venalia <i>Pt</i> коммерческий груз, товар(ы); o. gravidi ventris <i>O</i> утробный плод, зародыш, <i>но</i>: o. ventris <i>M, Lampr</i> (<i>тж.</i> ciborum onera <i>PM</i>) = excrementa alvi;\n<b>2)</b> бремя, тягостная обязанность, тягость (allevare o. <i>C</i>): esse alicui oneri <i>Sl, L</i> быть кому-л. в тягость; o. rejicere <i>C</i> сбросить обузу;\n<b>3)</b> <i>pl.</i> поборы, налоги: graviora onera injungere <i>Cs</i> усилить налоговое бремя;\n<b>4)</b> <i>pl.</i> долги: oneribus premi <i>C</i> быть обременённым долгами;\n<b>5)</b> <i>pl.</i> расходы (bellorum civilium <i>Su</i>);\n<b>6)</b> обязанность, долг (o. probandi <i>или</i> probationis <i>Dig, CJ</i>)."],
  ["onusto", "<big>onusto</big>\n\n<b>onusto, —, atum, are</b> [onus]\nобременять, отягощать (aliquem <i>Vlg, Eccl</i>)."],
  ["onustus", "<big>onustus</big>\n\n<b>onustus, a, um</b> [onus]\n(на)груженный (frumento <i>C</i> <i>и</i> frumenti <i>T</i>); навьюченный (asellus o. auro <i>C</i>); наполненный (aula onusta auro <i>и</i> auri <i>Pl</i>); преисполненный (pectus laetitia onustum <i>Pl</i>); отягощенный, обременённый (cibo et vino <i>C</i>): onustum gerere corpus <i>Pl</i> быть очень старым; o. vulneribus <i>T</i> весь в ранах; o. fustibus <i>Pl</i> весь избитый палками."],
  ["onychina", "<big>onychina</big>\n\n<b>onychina, orum</b> <i>n</i> [onychinus]\n(<i>sc.</i> vasa) вазы из оникса <i>Lampr</i>."],
  ["onychintinus", "<big>onychintinus</big>\n\n<b>onychi(n)tinus, a, um</b> <i>Sid</i>\n= onychinus."],
  ["onychinus", "<big>onychinus</big>\n\n<b>onychinus, a, um</b> [onyx]\n<b>1)</b> ониксовый (lapis <i>Vlg</i>);\n<b>2)</b> цвета оникса, похожий на оникс (tegimen <i>AG</i>; pruna <i>Col</i>)."],
  ["onychus", "<big>onychus</big>\n\n<b>onychus, i</b> <i>m</i>\nсосуд из оникса <i>Lampr</i>."],
  ["Onytes", "<big>Onytes</big>\n\n<b>Onytes, ae</b> <i>m</i>\nОнит, <i>имя воина</i> <i>V</i>."],
  ["onyx", "<big>onyx</big>\n\n<b>onyx, ychis</b> (<i>acc. sg.</i> a, <i>acc. pl.</i> as) <i>m, f</i> (<i>греч.</i>)\n<b>1)</b> оникс <i>PM, Lcn, M etc.</i>;\n<b>2)</b> сосуд <i>или</i> баночка из оникса (o. murreus <i>Prp</i>)."],
  ["opacitas", "<big>opacitas</big>\n\n<b>op&#257;cit&#257;s, &#257;tis</b> <i>f</i> [opacus]\nтенистость, тень (arborum <i>T</i>); тьма, мрак (noctium <i>PM</i>)."],
  ["opaco", "<big>opaco</big>\n\n<b>op&#257;co, &#257;v&#299;, &#257;tum, &#257;re</b> [opacus]\n<b>1)</b> покрывать тенью (locum, terras <i>C</i>); покрывать, обвивать (opacat tempora pinus <i>Sil</i>);\n<b>2)</b> помрачать (humanitatis opacata auctoritas <i>Aug</i>)."],
  ["opacum", "<big>opacum</big>\n\n<b>op&#257;cum, &#299;</b> <i>n</i> [opacus]\nтенистое место, тень (in opaco <i>PM</i>; opaca locorum <i>V</i>)."],
  ["opacus", "<big>opacus</big>\n\n<b>op&#257;cus, a, um</b>\n<b>1)</b> тенистый (platanon <i>PJ</i>); покрытый тенью (ripa <i>C</i>): frigus opacum <i>V</i> прохладная тень;\n<b>2)</b> тёмный (nox <i>V</i>): opaca mater <i>O</i> = terra;\n<b>3)</b> густой (barba <i>Ctl</i>)."],
  ["Opalia", "<big>Opalia</big>\n\n<b>Op&#257;lia, ium</b> <i>n</i>\nопалии, <i>августовские празднества в честь богини Ops</i> <i>Vr</i>."],
  ["opalus", "<big>opalus</big>\n\n<b>opalus, &#299;</b> <i>m</i>\nопал <i>PM</i>."],
  ["Opeconsiva", "<big>Opeconsiva</big>\n\n<b>Ope–consiva (Opiconsiva), orum</b> <i>n</i>\nпразднества в честь богини Ops Consiva <i>Vr</i>."],
  ["opella", "<big>opella</big>\n\n<b>opella, ae</b> <i>f</i> [<i>demin. к</i> opera]\nнебольшой труд, работа, дельце (forensis <i>H</i>): parva opella <i>Lcr</i> без большого труда."],
  ["opera", "<big>opera</big>\n\n<b>opera, ae</b> [opus I]\n<b>1.</b> <i>f</i>\n<b>1)</b> работа, труд, дело, деяние, усилие, старание, деятельность: res est multae operae <i>Cs</i> это требует большого труда; operam in aliqua re consumere (ponere, locare, collocare) <i>C etc.</i> <i>или</i> operam alicui rei tribuere (navare, impendere, <i>тж.</i> in <i>или</i> ad aliquid conferre <i>C</i>) трудиться над чём-л., прилагать к чему-л. старания; homines in operas mittere <i>C</i> давать людям работу; operae pretium facere <i>L</i> делать нечто, стоящее труда, совершать нечто ценное; operae pretium est <i>Enn, C, Sl etc.</i> дело стоит труда; operae pretium habere <i>L</i> быть вознаграждённым за свой труд; non operae est <i>L</i> не стоит труда, незачем; operam dare alicui rei <i>Ter, C etc.</i> заниматься чём-л.; dare operam, ne... <i>C, Cs, Pt</i> прилагать усилия (стараться), чтобы не...; dare operam funeri <i>C</i> присутствовать на похоронах; operam dare liberis <i>C</i> обзаводиться детьми; operam dare tonsori <i>Su</i> бриться (<i>у цирюльника</i>); aurium operam alicui dicere <i>Pl</i> <i>или</i> operam dare alicui <i>Pl</i> (sermoni alicujus <i>C</i>) внимательно слушать кого-л.; data <i>C</i> (dedita <i>C</i>, consulta <i>AG</i>) opera намеренно, умышленно; opera mea <i>Ter, C etc.</i> благодаря мне, из-за меня; una et eadem opera <i>Pl</i> так же точно <i>или</i> в одно и то же время, зараз; operam fortium virorum edere <i>L</i> проявить геройство;\n<b>2)</b> услуга, помощь (operam suam alicui polliceri <i>Sl</i>; opera alicujus uti <i>Cs</i>): operam alicui praestare (dare) <i>Cs</i> оказывать кому-л. услугу; o. forensis <i>C, Nep</i> судебная защита;\n<b>3)</b> время, досуг (deest mihi o. <i>C</i>); operae est mihi <i>L</i> у меня есть время (<i>или</i> желание), <i>тж.</i> мне удобно, <i>но</i>: operae non est <i>Pl</i> нельзя, не полагается; operae ubi mihi erit, ad te venero <i>Pl</i> как только (если) у меня будет время, я приду к тебе;\n<b>4)</b> <i>преим. pl.</i> рабочий день, дневной труд (quaternis operis aliquid facere <i>Vr</i>);\n<b>5)</b> произведение, изделие: operae aranearum <i>Pl</i> паутина;\n<b>2.</b> <i>m</i> <i>и</i> <i>f</i>\n<b>1)</b> рабочий, подёнщик <i>или</i> подмастерье (plures operas conducere <i>Col</i>);\n<b>2)</b> наёмник, наймит: operae theatrales <i>T</i> клакёры."],
  ["operans", "<big>operans</big>\n\n<b>operans, antis</b>\n<b>1.</b> <i>part. praes. к</i> operor;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> деятельный, трудолюбивый (apes <i>PM</i>);\n<b>2)</b> действенный (clysteres <i>CA</i>);\n<b>3.</b> <i>subst. m</i> землекоп <i>T</i>."],
  ["operantius", "<big>operantius</big>\n\n<b>operantius</b> <i>adv. compar.</i>\nболее действительно, действеннее (mederi <i>CA</i>)."],
  ["operaria", "<big>operaria</big>\n\n<b>operaria, ae</b> <i>f</i>\nработница <i>Pl</i>."],
  ["operarius 1", "<big>operarius</big>\n\n<b>I operarius, a, um</b> [opera]\nрабочий (pecus <i>Col</i>): usus o. <i>PM</i> полезная работа; homo o. <i>C</i> чернорабочий."],
  ["operarius 2", "<big>operarius</big>\n\n<b>II operarius, i</b> <i>m</i>\nработник, рабочий, чернорабочий <i>или</i> подёнщик <i>Pl, Cato etc.</i>; <i>презр.</i> (<i>о плохом ораторе</i>) ремесленник <i>C</i>."],
  ["operatio", "<big>operatio</big>\n\n<b>operatio, onis</b> <i>f</i> [operor]\n<b>1)</b> дело, действие <i>Vtr, PM</i>;\n<b>2)</b> воздействие, влияние: o. umidae potestatis <i>Vtr</i> влияние влажности;\n<b>3)</b> деятельная любовь, милосердие <i>Eccl</i>."],
  ["operator", "<big>operator</big>\n\n<b>operator, oris</b> <i>m</i>\nработник, производитель, творец <i>Eccl</i>."],
  ["operatorius", "<big>operatorius</big>\n\n<b>operatorius, a, um</b>\nпроизводящий, созидающий <i>Ambr</i>."],
  ["operatrix", "<big>operatrix</big>\n\n<b>operatrix, icis</b> <i>f</i>\nпроизводительница, созидательница <i>Eccl</i>."],
  ["operatus 1", "<big>operatus</big>\n\n<b>I operatus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> operor;\n<b>2.</b> <i>adj.</i> действенный <i>Tert</i>."],
  ["operatus 2", "<big>operatus</big>\n\n<b>II operatus, us</b> <i>m</i>\nработа, труд <i>Tert</i>."],
  ["operatus 3", "<big>operatus</big>\n\n<b>III operatus, i</b> <i>m</i>\nгорнорабочий, рудокоп <i>PM</i>."],
  ["operculo", "<big>operculo</big>\n\n<b>operculo, avi, atum, are</b> [operculum]\nснабжать крышкой, закрывать (dolia <i>Col</i>)."],
  ["operculum", "<big>operculum</big>\n\n<b>operculum, i</b> <i>n</i> [operio]\n(по)крышка (dolii <i>Cato</i>; arcae <i>L</i>)."],
  ["operimentum", "<big>operimentum</big>\n\n<b>operimentum, i</b> <i>n</i> [operio]\n<b>1)</b> крышка (dolii <i>PM</i>);\n<b>2)</b> покров (terrae <i>C</i>); покрывало (operimenta equorum <i>Sl</i>);\n<b>3)</b> оболочка, скорлупа (nucum <i>PM</i>)."],
  ["operio", "<big>operio</big>\n\n<b>operio, perui, pertum, ire</b> [<i>одного корня с</i> aperio]\n<b>1)</b> покрывать (amphoras auro <i>Nep</i>): aliquem loris usque ad necem o. <i>Ter</i> засечь кого-л. до смерти; opertus infamia <i>T</i> покрытый позором;\n<b>2)</b> окутывать (caput pallio <i>Pt</i>; mons opertus nubibus <i>O</i>; umenti bus umbris nox operit terras <i>V, Pt</i>): operto capite <i>Pt</i> с закутанной головой;\n<b>3)</b> хоронить (reliquias pugnae <i>T</i>);\n<b>4)</b> закрывать (oculos alicujus <i>Sen</i>; ostium <i>Ter</i>): opertis oculis <i>погов. Pt</i> с закрытыми глазами, <i>т. е.</i> не взирая ни на что;\n<b>5)</b> запирать (domum <i>Ctl</i>);\n<b>6)</b> скрывать, таить в себе (luctum <i>PJ</i>)."],
  ["operior", "<big>operior</big>\n\n<b>operior</b> <i>v. l.</i>\n= opperior."],
  ["opero", "<big>opero</big>\n\n<b>opero, avi, atum, are</b> <i>Eccl</i>\n= operor."],
  ["operor", "<big>operor</big>\n\n<b>operor, atus sum, ari</b> <i>depon.</i> [opus I]\n<b>1)</b> работать, трудиться, быть занятым: o. alicui rei (<i>редко</i> in aliqua re) заниматься чём-л. (rebus domesticis <i>Col</i>; studiis litterarum <i>VM</i>; capillis ornandis <i>O</i>): o. rei publicae <i>L</i> работать для государства; o. materiis caedendis <i>T</i> рубить лес; o. in cute curanda <i>H</i> ухаживать за своей кожей (<i>т. е.</i> заботиться о своей наружности);\n<b>2)</b> <i>культ.</i> служить, священнодействовать, совершать жертвоприношения (deo <i>Tib</i>): o. sacris <i>H</i> свершать священные обряды; Vesta, fave, tibi nunc operata resolvimus ora <i>O</i> будь благосклонна, о Веста, к тебе обращаюсь я с благоговением на лице;\n<b>3)</b> действовать, иметь действие (venenum operatur <i>Lampr</i>);\n<b>4)</b> обрабатывать (terram <i>Hier</i>);\n<b>5)</b> совершать (scelus <i>Vlg</i>); творить (miracula <i>Ambr</i>);\n<b>6)</b> вершить, осуществлять (justitiam <i>Lact</i>)."],
  ["operose", "<big>operose</big>\n\n<b>operose</b> [operosus]\n<b>1)</b> с большим трудом, с усилием (aliquid agere <i>Sen</i>);\n<b>2)</b> тщательно, точно (dicemus operosius <i>PM</i>)."],
  ["operositas", "<big>operositas</big>\n\n<b>operositas, atis</b> <i>f</i> [operosus]\nповышенная деятельность, хлопотливость <i>Q, Vop, Tert</i>."],
  ["operosus", "<big>operosus</big>\n\n<b>operosus, a, um</b> [opera]\n<b>1)</b> весьма трудный, сопряжённый с большим трудом, требующий большого усилия, тяжёлый (opus, artes <i>C</i>; carmen <i>H</i>); с трудом достающийся <i>или</i> добытый (gloria rerum <i>O</i>): o. cibo <i>PM</i> неудобоваримый;\n<b>2)</b> искусный, художественный (templa <i>O</i>); величественный (mundi moles <i>O</i>);\n<b>3)</b> деятельный, трудолюбивый, хлопотливый (colonus <i>O</i>; senectus <i>C</i>);\n<b>4)</b> сильно действующий, действенный, оказывающий (полезное) влияние (herba <i>O</i>)."],
  ["opertaneus", "<big>opertaneus</big>\n\n<b>opertaneus, a, um</b> [opertum]\nсокрытый, сокровенный, тайный (sacra <i>PM</i>)."],
  ["operte", "<big>operte</big>\n\n<b>operte</b> [opertus]\nскрыто, иносказательно (o. et symbolice <i>AG</i>)."],
  ["opertio", "<big>opertio</big>\n\n<b>opertio, onis</b> <i>f</i> [operio]\nпокрытие <i>Vr</i>."],
  ["operto", "<big>operto</big>\n\n<b>operto, —, —, are</b> [<i>intens. к</i> operio]\nзакрывать, прикрывать <i>Enn</i>."],
  ["opertorium", "<big>opertorium</big>\n\n<b>opertorium, i</b> <i>n</i> [operio]\n<b>1)</b> покрывало, одеяло <i>Sen</i>; покров <i>Vlg</i>;\n<b>2)</b> могила, гробница (senis nostri <i>Sid</i>)."],
  ["opertum", "<big>opertum</big>\n\n<b>opertum, i</b> <i>n</i> [operio]\n<b>1)</b> потаённое место, тайник: telluris operta subire <i>V</i> спуститься в недра земли (в преисподнюю);\n<b>2)</b> тайна, секрет (litterarum <i>AG, Aug</i>); таинственное изречение (operta Apollinis <i>C</i>)."],
  ["opertus 1", "<big>opertus</big>\n\n<b>I opertus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> operio;\n<b>2.</b> <i>adj.</i> скрытый, тайный, потаённый: operta quae fuere aperta sunt <i>Pl</i> скрытое стало явным."],
  ["opertus 2", "<big>opertus</big>\n\n<b>II opertus, us</b> <i>m</i> [opertus I]\nокутывание, закрывание <i>или</i> покров <i>Ap, Macr</i>."],
  ["operui", "<big>operui</big>\n\n<b>operui</b> <i>pf. к</i>\noperio."],
  ["operula", "<big>operula</big>\n\n<b>operula, ae</b> <i>f</i> [<i>demin. к</i> opera]\n<b>1)</b> маленькое дельце, небольшой труд <i>Dig, Eccl</i>;\n<b>2)</b> небольшой заработок, маленькая прибыль (operulas merere <i>Ap</i>)."],
  ["opes", "<big>opes</big>\n\n<b>opes, um</b> <i>f pl. к</i>\nops."],
  ["opficium", "<big>opficium</big>\n\n<b>opficium</b> <i>Pl</i>\n(<i>v. l.</i>)= officium."],
  ["Opheltes", "<big>Opheltes</big>\n\n<b>Opheltes, ae</b> <i>m</i>\nОфельт, <i>мужское имя</i> <i>V, O, St etc.</i>"],
  ["Ophias", "<big>Ophias</big>\n\n<b>Ophias, adis</b> <i>f</i>\nженщина из племени офиев (<i>в Этолии</i>) <i>O</i>."],
  ["ophiomachus", "<big>ophiomachus</big>\n\n<b>ophiomachus, i</b> <i>m</i> (<i>греч.</i>)\nофиомах, <i>род саранчи</i> <i>Vlg</i>."],
  ["Ophion", "<big>Ophion</big>\n\n<b>Ophion, onis</b> <i>m</i>\nОфион:\n<b>1)</b> <i>отец кентавра Амика</i> <i>O</i>;\n<b>2)</b> <i>спутник Кадма</i> (<i>см.</i> Ophionius)."],
  ["Ophionides", "<big>Ophionides</big>\n\n<b>Ophionides, ae</b> <i>m</i>\nсын Офиона, <i>т. е.</i> Amycus <i>O</i>."],
  ["Ophionius", "<big>Ophionius</big>\n\n<b>Ophionius, a, um</b> [Ophion 2]\nофионов <i>SenT</i>."],
  ["Ophiuchus", "<big>Ophiuchus</big>\n\n<b>Ophiuchus, i</b> <i>m</i> (<i>греч.</i>; <i>лат.</i> Anguitenens)\nЗмеедержец (<i>созвездие</i>) <i>C, Man</i>."],
  ["Ophiusa", "<big>Ophiusa</big>\n\n<b>Ophiusa, ae</b> <i>f</i>\nОфиуса, <i>древнее название Кипра</i> <i>PM</i>."],
  ["Ophiusius", "<big>Ophiusius</big>\n\n<b>Ophiusius, a, um</b> <i>поэт.</i>\nкипрский (arva <i>O</i>)."],
  ["ophthalmias", "<big>ophthalmias</big>\n\n<b>ophthalmias, ae</b> <i>m</i> (<i>греч.</i>; <i>лат.</i> oculata) <i>предпол.</i>\nминога <i>Pl</i>."],
  ["ophthalmicus", "<big>ophthalmicus</big>\n\n<b>ophthalmicus, i</b> <i>m</i> (<i>греч.</i>)\nглазной врач, окулист <i>M</i>."],
  ["Opiconsiva", "<big>Opiconsiva</big>\n\n<b>Opiconsiva</b> <i>v. l.</i>\n= Opeconsiva."],
  ["Opicus", "<big>Opicus</big>\n\n<b>Opicus, a, um</b>\n= Oscus, <i>перен.</i> неотёсанный, грубый <i>Cato, AG, J, Aus</i>."],
  ["opifer", "<big>opifer</big>\n\n<b>opifer, fera, ferum</b> [ops + fero]\nподающий помощь: deus o. <i>O</i> = Aesculapius."],
  ["opifex", "<big>opifex</big>\n\n<b>opifex, icis</b> <i>m</i> [opus + facio]\nтворец, создатель (mundi, verborum <i>C</i>); мастер, художник <i>C, Sl</i>; мастеровой, ремесленник (opifices atque servitia <i>Sl</i>; o. ferrarius <i>Treb</i>): apes opifices <i>Vr</i> трудолюбивые пчёлы; stilus est dicendi o. <i>C</i> письмо развивает искусство владения словом."],
  ["opificina", "<big>opificina</big>\n\n<b>opificina, ae</b> <i>f</i> [opifex]\nмастерская <i>Pl</i>."],
  ["opificium", "<big>opificium</big>\n\n<b>opificium, i</b> <i>n</i> [opifex]\nизготовление, работа <i>Vr, Ap, Eccl</i>."],
  ["opificus", "<big>opificus</big>\n\n<b>opificus, a, um</b> [opus + facio]\nсамостоятельно изготовленный (opera <i>Ap</i> —<i>v. l.</i>)."],
  ["opilio", "<big>opilio</big>\n\n<b>opilio (upilio), onis</b> <i>m</i> [ovis + pello]\nовчар, пастух (<i>пасущий мелкий рогатый скот</i>) <i>Pl, Cato, V, Ap</i>."],
  ["opima", "<big>opima</big>\n\n<b>opima, orum</b> <i>n</i> [opimus]\nдобыча полководца, почётные трофеи (= spolia opima- <i>см.</i> Opimus 3) <i>PJ, Fl</i>."],
  ["opimatus", "<big>opimatus</big>\n\n<b>opimatus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> opimo;\n<b>2.</b> <i>adj.</i> откормленный, тучный (abdomen <i>Aus</i>)."],
  ["opime", "<big>opime</big>\n\n<b>opime</b> [opimus]\nбогато, пышно, роскошно (domus instructa <i>Pl</i>)."],
  ["Opimianum", "<big>Opimianum</big>\n\n<b>Opimianum, i</b> <i>n</i>\n(<i>sc.</i> vinum) вино, приготовленное в консульство Опимия (<i>см.</i> Opimius 1), <i>т. е. старое, выдержанное</i> <i>Pt</i>."],
  ["opimitas", "<big>opimitas</big>\n\n<b>opimitas, atis</b> <i>f</i> [opimus]\n<b>1)</b> дородность, тучность (o. sapientiam impedit, exilitas expedit <i>Tert</i>);\n<b>2)</b> обилие (praedarum <i>Amm</i>);\n<b>3)</b> <i>pl.</i> богатства (maximae opimitates <i>Pl</i>)."],
  ["Opimius", "<big>Opimius</big>\n\n<b>Opimius, a, um</b>\n<i>римск. nomen; наиболее известны</i>:\n<b>1)</b> L. O., <i>претор в 125 г. до н. э.; в 121 г., будучи консулом, возглавил борьбу аристократов против Тиберия Гракха; в 114 г., уличённый в подкупе Югуртой, подвергся изгнанию</i> <i>C, Sl</i>;\n<b>2)</b> Q. O., <i>противник закона Суллы о недопущении народных трибунов к высшим должностям.; был обложен за это огромным штрафом</i> (<i>74 г. до н. э.</i>) <i>C</i>."],
  ["opimo", "<big>opimo</big>\n\n<b>opimo, avi, atum, are</b> [opimus]\n<b>1)</b> делать жирным, тучным, откармливать (turtures <i>Col</i>);\n<b>2)</b> утучнять, удобрять (terram <i>Ap</i>);\n<b>3)</b> обогащать, снабжать (mensam agrestis, <i>sc.</i> cibis <i>Vop</i>)."],
  ["opimus", "<big>opimus</big>\n\n<b>opimus, a, um</b> [ops]\n<b>1)</b> плодородный, тучный (ager, regio <i>C</i>);\n<b>2)</b> жирный, упитанный, откормленный, дородный (bos <i>C, O</i>; membra <i>AG</i>; habitus corporis <i>C</i>);\n<b>3)</b> богатый, изобилующий (aliqua re <i>C, L</i>); пышный, обильный (dapes <i>C</i>; messis <i>PJ</i>; praeda <i>C</i>; triumphus <i>H</i>): spolia opima <i>L</i> (<i>тж.</i> opimum belli decus <i>QC</i>) доспехи и вооружение, снятые с неприятельского полководца;\n<b>4)</b> напыщенный, высокопарный (genus dictionis <i>C</i>);\n<b>5)</b> чреватый (opus opimum casibus <i>T</i>)."],
  ["opinabilis", "<big>opinabilis</big>\n\n<b>opinabilis, e</b> [opinor]\n<b>1)</b> основанный на предположении, построенный на догадках (artes <i>C</i>);\n<b>2)</b> воображаемый, мнимый (<i>sc.</i> dolor <i>C</i>; amor <i>AG</i>)."],
  ["opinabiliter", "<big>opinabiliter</big>\n\n<b>opinabiliter</b>\nпредположительно <i>Boet</i>."],
  ["opinatio", "<big>opinatio</big>\n\n<b>opinatio, onis</b> <i>f</i> [opinor]\nпредположение, представление, воображение <i>C, VM</i>."],
  ["opinator", "<big>opinator</big>\n\n<b>opinator, oris</b> <i>m</i> [opinor]\n<b>1)</b> выражающий предположение, предполагающий, склонный строить догадки <i>C</i>;\n<b>2)</b> (<i>в императ. Риме</i>) заготовитель продовольствия для армии <i>CTh, CJ</i>."],
  ["opinatus 1", "<big>opinatus</big>\n\n<b>I opinatus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> opinor;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> предполагаемый, воображаемый (bona, mila <i>C</i>);\n<b>2)</b> славный, знаменитый. (certamen <i>Amm</i>)."],
  ["opinatus 2", "<big>opinatus</big>\n\n<b>II opinatus, us</b> <i>m</i> [opinor]\nпредположение (animi <i>Lcr</i>)."],
  ["opinio", "<big>opinio</big>\n\n<b>opinio, onis</b> <i>f</i>\n<b>1)</b> мнение, взгляд, представление, предположение: opinionibus, non scientia contineri <i>C</i> основываться на предположениях, а не на (достоверном) знании; o. communis (hominum, popularis, vulgaris <i>или</i> vulgi) <i>C</i> общественное мнение || ожидание (alicujus rei <i>или</i> de aliqua re); o. hujus diei <i>Cs</i> предположение насчёт результатов нынешнего дня; o. trium legionum <i>Cs</i> надежда (расчёты) на получение трёх легионов; opinionem alicujus rei praebere <i>Cs</i> производить впечатление чего-л.; opinionem habere (in opinione esse) <i>C</i> придерживаться мнения; ut opinionem habeas rerum <i>C</i> если ты желаешь знать (моё) мнение о положении вещей; res erat in ea opinione, ut putarent... <i>C</i> по общему мнению дело обстояло так, что...; in opinionem incidere (adduci) <i>C</i> прийти к мнению; venit mihi in opinionem <i>Nep</i> мне пришло в голову; contra (praeter) opinionem <i>Cs, PJ, C</i> сверх (против) ожидания (паче чаяния); mea opinione (ut mea o. fert <i>или</i> est) <i>C</i> по моему мнению; opinione celerius <i>bAl</i> быстрее, чем можно было ожидать; vincere opinionem alicujus <i>C</i> превзойти чьи-л. ожидания; pro opinione alicujus <i>Nep</i> как кто-л. ожидал; deorum o. <i>C</i> вера в богов <i>или</i> представление о богах;\n<b>2)</b> предубеждённость, предрассудок <i>или</i> воображение, фантазия (ex opinione aliquid aestimare <i>C</i>): in opinione versari (esse) <i>C</i> покоиться на воображении;\n<b>3)</b> репутация (malignitatis <i>T</i>); благоприятное, высокое мнение (opinionem alicujus fallere <i>Cs, C</i>); хорошая репутация, слава: opinionem capere <i>Cs</i> снискать (себе) славу; summam justitiae opinionem habere <i>Cs</i> слыть поборником величайшей справедливости;\n<b>4)</b> слух (alicujus rei <i>C, Just etc.</i>): o. sine auctore exiit <i>L</i> распространился неизвестно кем пущенный слух; opinionem in vulgus edere <i>Cs</i> (opinionem serere <i>Just, L</i>) распускать (распространять) слух."],
  ["opiniosus", "<big>opiniosus</big>\n\n<b>opiniosus, a, um</b> [opinio]\nполный предположений, догадок (homo <i>Tert</i>)."],
  ["opino", "<big>opino</big>\n\n<b>opino, —, —, are</b> <i>арх. Enn, Pac, Pl</i>\n= opinor."],
  ["opinor", "<big>opinor</big>\n\n<b>opinor, atus sum, ari</b> <i>depon.</i>\nполагать, предполагать, считать (aliquid <i>C</i> <i>или</i> de aliqua re <i>C, Just etc.</i>); думать (de aliquo male <i>Su</i>): loquor, ut opinor <i>C</i> я говорю, как (= то, что) думаю; <i>вводно</i> (ut) opinor <i>Pl, Ter, C</i> полагаю, надо думать, по-моему, <i>в вопр. предлож.</i> не так ли? <i>Pl, Ter, C</i>."],
  ["opinosus", "<big>opinosus</big>\n\n<b>opinosus, a, um</b> <i>v. l.</i>\n= opiniosus."],
  ["opipare", "<big>opipare</big>\n\n<b>opipare</b> [opiparus]\nобильно, прекрасно, великолепно (edere et bibere <i>C</i>; opime atque o. <i>Pl</i>)."],
  ["opiparis", "<big>opiparis</big>\n\n<b>opiparis, e</b> <i>Ap</i>\n= opiparus."],
  ["opiparus", "<big>opiparus</big>\n\n<b>opi–parus, a, um</b> [ops + paro]\nобильный, прекрасный, великолепный (obsonia <i>Pl</i>; munera <i>Ap</i>)."],
  ["Opis", "<big>Opis</big>\n\n<b>Opis, is</b> (<i>acc.</i> im) <i>f</i>\nОпис, <i>имя нимфы</i> <i>V</i>."],
  ["opisthographum", "<big>opisthographum</big>\n\n<b>opisthographum, i</b> <i>n</i> (<i>греч.</i>)\nнаписанное на обороте листа <i>Dig</i>."],
  ["opisthographus", "<big>opisthographus</big>\n\n<b>opisthographus, a, um</b> (<i>греч.</i>)\nнаписанный на обороте листа (liber <i>PJ</i>)."],
  ["opisthotonia", "<big>opisthotonia</big>\n\n<b>opisthotonia, ae</b> <i>f</i> <i>CA</i>\n= opisthotonos."],
  ["opisthotonos", "<big>opisthotonos</big>\n\n<b>opisthotonos, i</b> <i>m</i> (<i>греч.</i>; <i>лат.</i> supinus raptus)\nопистотонус, судорожное сведение мышц назад <i>или</i> затылочная судорога <i>PM, CC</i>."],
  ["opitulatio", "<big>opitulatio</big>\n\n<b>opitulatio, onis</b> <i>f</i>\nоказание помощи, поддержка <i>Dig, Vlg, Eccl</i>."],
  ["opitulator", "<big>opitulator</big>\n\n<b>opitulator, oris</b> <i>m</i> [opitulor]\nпомощник (sodalis <i>Ap</i>)."],
  ["opitulo", "<big>opitulo</big>\n\n<b>opitulo, —, —, are</b> <i>арх. LA</i>\n= opitulor."],
  ["opitulor", "<big>opitulor</big>\n\n<b>opitulor, atus sum, ari</b> <i>depon.</i> [ops + tulo = fero]\nпомогать, оказывать поддержку (alicui <i>Pl etc.</i>): o. aliqua re aegris <i>Ap</i> облегчать чём-л. страдания больных; inopiae plebis Romanae o. <i>Sl</i> помочь нужде (устранить нужду) римского плебса || (<i>о лекарствах и т. п.</i>) помогать, быть действительным (contra aliquid <i>PM</i>)."],
  ["opitulus", "<big>opitulus</big>\n\n<b>opitulus, i</b> <i>m</i> <i>Aug</i>\n= opitulator."],
  ["opium", "<big>opium</big>\n\n<b>opium, i</b> <i>n</i> (<i>греч.</i>)\nопиум <i>PM, Scr</i>."],
  ["opobalsametum", "<big>opobalsametum</big>\n\n<b>opobalsametum, i</b> <i>n</i> [opobalsamum]\nместо, засаженное бальзамными деревьями <i>Just</i>."],
  ["opobalsamum", "<big>opobalsamum</big>\n\n<b>opobalsamum, i</b> <i>n</i> (<i>греч.</i>)\nсок бальзамного дерева, бальзам <i>CC, PM, M, J, St etc.</i>"],
  ["oporinos", "<big>oporinos</big>\n\n<b>oporinos, on</b> (<i>греч.</i>) <i>M</i>\n= autumnalis."],
  ["oporotheca", "<big>oporotheca</big>\n\n<b>oporotheca, ae (e, es)</b> <i>f</i> (<i>греч.</i>)\nплодохранилище <i>Vr</i>."],
  ["oportet", "<big>oportet</big>\n\n<b>oportet, uit, —, ere</b> <i>impers.</i>\n<b>1)</b> нужно, следует, надлежит, целесообразно: esse (= edere) oportet ut vivas, non vivere ut edas <i>rhH</i> нужно есть, чтобы жить, а не жить, чтобы есть; ex malis eligere minima oportet <i>C</i> из (разных) зол нужно выбирать наименьшие; hoc fieri et oportet et opus est <i>C</i> это совершенно необходимо сделать; non solum oportet, sed etiam necesse est <i>C</i> не только целесообразно, но и необходимо; alio tempore atque oportuerit <i>Cs</i> в другое, Чем следовало, (в ненадлежащее) время; haec facta ab illo oportebat (<i>иногда лично</i>: oportebant) <i>Ter</i> вот как он должен был поступить; mendacem memorem esse oportet <i>погов. Q, Ap</i> лжец Должен обладать хорошей памятью;\n<b>2)</b> должно быть, по-видимому, видно: servum te esse oportet nequam <i>Pl</i> ты, видно, негодный раб; incendium oportet fiat <i>Pt</i> (где-то,) надо думать, пожар."],
  ["oportun-", "<big>oportun-</big>\n\n<b>oportun-</b> <i>v. l.</i>\n= opportun-."],
  ["oppando", "<big>oppando</big>\n\n<b>op–pando, pand&#299;, p&#257;nsum (passum), ere</b>\n(перед чём-л.) развешивать (<i>для просушки</i>) (<i>sc.</i> retia <i>GF</i>); растягивать (velum <i>Vlg</i>)."],
  ["oppansum", "<big>oppansum</big>\n\n<b>opp&#257;nsum (oppassum), &#299;</b> <i>n</i> [oppando]\nпокров, оболочка (corporis <i>Tert</i>)."],
  ["oppecto", "<big>oppecto</big>\n\n<b>op–pecto, —, —, ere</b>\nпричёсывать, <i>перен.</i>, <i>шутл.</i> обгладывать, есть (murenam <i>Pl</i>)."],
  ["oppedo", "<big>oppedo</big>\n\n<b>oppedo, —, —, ere</b> [ob + pedo II] <i>вульг.</i>\nнасмехаться, издеваться, глумиться (alicui <i>H</i>)."],
  ["oppegi", "<big>oppegi</big>\n\n<b>oppegi</b> <i>pf. к</i>\noppingo."],
  ["opperior", "<big>opperior</big>\n\n<b>op–perior, pertus</b> (<i>арх. Pl</i> <b>peritus</b>) <b>sum, iri</b> (<i>иногда</i> <i>fut.</i> -ibor, <i>imper.</i> -imino) <i>depon.</i>\nждать (unum diem <i>Nep</i>); ожидать (aliquem <i>Ter, L etc.</i>); выжидать (tempora sua <i>L</i>)."],
  ["oppessulatus", "<big>oppessulatus</big>\n\n<b>oppessulatus, a, um</b> [ob + pessulus]\nзапертый на засов (janua <i>Ap</i>; fores <i>Pt</i>)."],
  ["oppeto", "<big>oppeto</big>\n\n<b>op–peto, ivi (ii), itum, ere</b>\nидти навстречу, принимать, подвергаться: o. poenas alicujus rei <i>Ph</i> подвергнуться наказанию за что-л.; o. (malam) pestem <i>Enn, Pl</i> погибнуть злой смертью; o. mortem <i>C, VM, AG</i> <i>или</i> o. letum <i>L</i> (<i>тж. просто</i> o. <i>V, T etc.</i>) идти на смерть, умирать."],
  ["oppexus", "<big>oppexus</big>\n\n<b>oppexus, us</b> <i>m</i> [oppecto]\nпричёсывание, причёска (crinium <i>Ap</i>)."],
  ["oppico", "<big>oppico</big>\n\n<b>op–pico, —, —, are</b>\nпокрывать смолой, (о)смолить (corticem <i>Cato</i>)."],
  ["oppidanus 1", "<big>oppidanus</big>\n\n<b>I oppidanus, i</b> <i>m</i> [oppidum]\nгорожанин (<i>преим.</i> провинциал) <i>Cs, bAl, T</i>."],
  ["oppidanus 2", "<big>oppidanus</big>\n\n<b>II oppidanus, a, um</b> [oppidum]\n<b>1)</b> городской, из небольшого города (senex <i>C</i>);\n<b>2)</b> <i>презр.</i> местечковый, провинциальный (genus dicendi <i>C</i>; lascivia <i>T</i>)."],
  ["oppidatim", "<big>oppidatim</big>\n\n<b>oppidatim</b> [oppidum]\nпо городам, в городах, в каждом городе (ludos o. constituere <i>Su</i>)."],
  ["oppido", "<big>oppido</big>\n\n<b>oppido</b>\n<b>1)</b> весьма, очень, чрезвычайно, крайне (arduum et o. difficile est <i>Ap</i>): o. pauci <i>C</i> весьма немногие; quamquam o. festinem <i>Ap</i> хотя я и чрезвычайно спешу;\n<b>2)</b> вполне: o. adulescens <i>L</i> совсем (ещё) Юноша; aurum omne o. <i>Pl</i> до последней копейки; perii ego o. <i>Pl</i> я пропал окончательно; o. quam <i>L</i> чрезвычайно, необыкновенно."],
  ["oppidulum", "<big>oppidulum</big>\n\n<b>oppidulum, i</b> <i>n</i> [<i>demin. к</i> oppidum]\nгородок, местечко <i>C, H, Hier</i>."],
  ["oppidum", "<big>oppidum</big>\n\n<b>oppidum, i</b> (<i>gen. pl.</i> <i>иногда</i> um) <i>n</i>\n<b>1)</b> <i>воен.</i> укрепление, укреплённое место: &laquo;oppidum&raquo; Britanni vocant, quum silvas impeditas vallo atque fossa munierunt <i>Cs</i> британцы называют &laquo;oppidum&raquo; укреплённые валом и рвом малодоступные леса;\n<b>2)</b> город (<i>преим. провинциальный; изредка о Риме</i>): urbe oppidove ullo <i>Su</i> из Рима или из какого-л. провинциального города;\n<b>3)</b> <i>архит.</i> барьеры в цирке (<i>впоследствии</i> carceres) <i>Vr</i>."],
  ["oppignerator", "<big>oppignerator</big>\n\n<b>oppignerator, oris</b> <i>m</i> [oppignero]\nдающий ссуды под залог <i>Aug</i>."],
  ["oppignero", "<big>oppignero</big>\n\n<b>op–pignero, avi, atum, are</b>\n<b>1)</b> закладывать, отдавать в (под) залог (aliquid pro aliqua re <i>C</i> <i>или</i> aliqua re <i>M</i>);\n<b>2)</b> гарантировать, обеспечить: se verbo o. <i>Sen</i> обязаться словом."],
  ["oppilatio", "<big>oppilatio</big>\n\n<b>oppilatio, onis</b> <i>f</i> [oppilo]\nзакупорка (narium <i>Scr</i>)."],
  ["oppilo", "<big>oppilo</big>\n\n<b>oppilo, avi, atum, are</b>\nзаваливать, загораживать (ostia <i>Lcr</i>; scalas tabernae <i>C</i>); затыкать, зажимать (os loquentium iniqua <i>Aug</i>)."],
  ["oppingo", "<big>oppingo</big>\n\n<b>oppingo, pegi, —, ere</b> [ob + pango]\nзапечатлевать: o. savium <i>Pl</i> поцеловать."],
  ["Oppius", "<big>Oppius</big>\n\n<b>Oppius, a, um</b>\nОппий, <i>римск. nomen</i>: O. mons <i>Vr</i> Оппиева вершина (<i>одна из двух вершин Эсквилинского холма в Риме</i>)."],
  ["oppleo", "<big>oppleo</big>\n\n<b>op–pleo, plevi, pletum, ere</b> [ob + *pleo]\n<b>1)</b> наполнять, переполнять (nives omnia oppleverunt <i>L</i>); nares gravedine oppletae <i>Ap</i> насморк;\n<b>2)</b> охватывать, окутывать (luctus totam urbem opplevit <i>L</i>; mentes oppletae tenebris <i>C</i>)."],
  ["opploro", "<big>opploro</big>\n\n<b>op–ploro, —, —, are</b>\nплакать: auribus alicujus o. <i>rhH</i> надоедать кому-л. своим плачем."],
  ["oppono", "<big>oppono</big>\n\n<b>op–pono, posui, positum</b> (<b>postum</b> <i>Lcr</i>), <b>ere</b>\n<b>1)</b> ставить против, выставлять навстречу, противопоставлять: armatos homines ad omnes introitus o. <i>Cs</i> занять все входы вооружённой охраной; se o. alicui in itinere <i>Cs</i> преградить кому-л. путь; manus ante oculos (<i>или</i> oculis) o. <i>O</i> закрыть глаза руками; foramini alternos oculos O. <i>Pt</i> смотреть в отверстие то одним, то другим глазом; gallinae se opponunt pullis <i>C</i> куры закрывают собой цыплят (чтобы защитить от холода); oppositas habere fores <i>O</i> держать двери запертыми; o. auriculam <i>H</i> дать согласие, согласиться (<i>на какую-л. просьбу</i>); ad omne periculum opponi <i>C</i> идти навстречу любым опасностям;\n<b>2)</b> закладывать, давать в залог (suum anulum <i>Pl</i>);\n<b>3)</b> противополагать (clementiae crudelitas opponitur <i>Sen</i>); противопоставлять (в виде возражения <i>или</i> довода), ссылаться (o. auctoritatem suam <i>C</i>): formidines o. <i>C</i> стращать, запугивать; o. Stoicis <i>C</i> возражать (противоречить) стоикам."],
  ["opportune", "<big>opportune</big>\n\n<b>opportune</b> [opportunus]\nудачно, уместно, вовремя, кстати (advenire <i>Ter</i>, adesse <i>C</i>); улучив подходящий момент (se sub ducere <i>Pt</i>)."],
  ["opportunitas", "<big>opportunitas</big>\n\n<b>opportunitas, atis</b> <i>f</i> [opportunus]\n<b>1)</b> выгодность, благоприятное положение, удобство (viae <i>PJ</i>; loci <i>Cs, Sl</i>); o. temporis <i>C, Cs</i> удобное время;\n<b>2)</b> удобный случай, благоприятный повод: opportunitate quadam data <i>Cs</i> когда представится удобный случай (в благоприятном случае); o. aetatis <i>Sl</i> подходящий возраст;\n<b>3)</b> преимущество, выгода (amicitia tantas opportunitates habet <i>C</i>);\n<b>4)</b> целесообразное устройство, соразмерность, складность (corporis, membrorum <i>C</i>)."],
  ["opportunus", "<big>opportunus</big>\n\n<b>opportunus, a, um</b> [<i>предпол. из</i> ob portum, <i>sc.</i> situs]\n<b>1)</b> удобный, благоприятный, выгодный, подходящий (tempus <i>Cs</i>; aetas <i>C</i>; locus <i>Cs, Sl</i>; nox opportuna erat eruptioni <i>L</i>): opportuni silentii laus <i>Ap</i> похвальное умение вовремя хранить молчание;\n<b>2)</b> годный, пригодный, способный, полезный (ad aliquam rem <i>Ter, L etc.</i> <i>или</i> alicui rei <i>Sl, Q etc.</i>);\n<b>3)</b> легко подвергающийся, подверженный (o. injuriae <i>Sl etc.</i>): o. morbis <i>CC, PM</i> склонный к заболеваниям;\n<b>4)</b> открытый для нападения, незащищённый, уязвимый (locus hostibus o. <i>Nep</i>; opportuna moenium <i>L</i>, locorum <i>T</i>)."],
  ["oppositio", "<big>oppositio</big>\n\n<b>oppositio, onis</b> <i>f</i> [oppono]\nпротивопоставление, возражение <i>Vlg</i>."],
  ["oppositus 1", "<big>oppositus</big>\n\n<b>I oppositus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> oppono;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> находящийся перед <i>или</i> против, противолежащий, противостоящий (mons o. <i>Cs</i>; o. contra aliquid <i>O</i> <i>или</i> alicui rei <i>Cs</i>);\n<b>2)</b> противоположный, противоречащий <i>AG</i>."],
  ["oppositus 2", "<big>oppositus</big>\n\n<b>II oppositus</b>, (<b>us</b>) (<i>в sg.</i> <i>встреч. тк.</i> <i>abl.</i>) <i>m</i>\n<b>1)</b> противоположение, противопоставление (laterum et corporum <i>C</i>);\n<b>2)</b> противостояние (lunae <i>C</i>; globi <i>PM</i>)."],
  ["oppostum", "<big>oppostum</big>\n\n<b>oppostum</b> — <i>см.</i>\noppono."],
  ["oppostus", "<big>oppostus</big>\n\n<b>oppostus</b> <i>Lcr</i>\n= oppositus (<i>part. pf. к</i> oppono)."],
  ["opposui", "<big>opposui</big>\n\n<b>opposui</b> <i>pf. к</i>\noppono."],
  ["oppressi", "<big>oppressi</big>\n\n<b>oppressi</b> <i>pf. к</i>\nopprimo."],
  ["oppressio", "<big>oppressio</big>\n\n<b>oppressio, onis</b> <i>f</i> [opprimo]\n<b>1)</b> давление <i>Vtr</i>;\n<b>2)</b> притеснение, угнетение (innocentium <i>Aug</i>); подавление (legum et libertatis <i>C</i>);\n<b>3)</b> насилие (per oppressionem aliquam eripere <i>Ter</i>); насильственный захват (curiae <i>C</i>)."],
  ["oppressiuncula", "<big>oppressiuncula</big>\n\n<b>oppressiuncula, ae</b> <i>f</i> [<i>demin. к</i> oppressio]\nлёгкое пожимание, тискание, ощупывание (papillarum horridularum <i>Pl</i>)."],
  ["oppressor", "<big>oppressor</big>\n\n<b>oppressor, oris</b> <i>m</i> [opprimo]\n<b>1)</b> сокрушитель (dominationis <i>C</i>);\n<b>2)</b> притеснитель, угнетатель (libertatis <i>AV</i>; pauperum <i>Aug</i>)."],
  ["oppressus 1", "<big>oppressus</big>\n\n<b>I oppressus, a, um</b> <i>part. pf. к</i>\nopprimo."],
  ["oppressus 2", "<big>oppressus</big>\n\n<b>II oppressus, (us) </b> <i>m</i> [opprimo]\nдавление <i>Aug, Sid</i>: in oppressu valido durare <i>Lcr</i> устоять против сильного давления."],
  ["opprimo", "<big>opprimo</big>\n\n<b>opprimo, pressi, pressum, ere</b> [ob + premo]\n<b>1)</b> сжимать, тискать (aliquid manu <i>L</i>); придавить, задавить (opprimi terra <i>C</i>); топтать, попирать (aliquid pede <i>Cato</i>);\n<b>2)</b> пригибать, сгибать (opprimi onere <i>C, Sl</i>);\n<b>3)</b> зажимать, смыкать, закрывать (os <i>Ter</i>; ora loquentis <i>O</i>; fauces alicujus <i>Sen</i>);\n<b>4)</b> сбивать с ног, валить на землю (pedes ab equite oppressus <i>Su</i>); уложить, сразить (jaculo leonem <i>VF</i>);\n<b>5)</b> прекращать, класть конец (o. tumultum <i>L</i>); подавлять, гасить (flammam <i>Enn</i>; orientem ignem <i>L</i>); топить, уничтожать (classem <i>C</i>): apud Siciliam oppressus <i>T</i> потерпев(ший) поражение в Сицилии;\n<b>6)</b> превозмогать, сдерживать, подавлять (iram <i>Sl</i>; desiderium <i>Sen</i>; dolorem <i>C</i>); скрывать (aliquid o. atque abscondere <i>C</i>);\n<b>7)</b> теснить, угнетать (libertatem <i>Nep</i>; opprimi aere alieno <i>C</i>); расстраивать, разрушать, срывать, обезвреживать (fraudem <i>L</i>); поражать, одолевать, сломить (adversarios <i>Nep</i>);\n<b>8)</b> схватить, поймать, задержать, арестовать (hominem suspectissimum <i>Su</i>); застигать врасплох (improvidos incautosque hostes <i>L</i>; aliquem nox oppressit <i>L</i>); постигать (mors oppressit aliquem <i>C</i>); охватывать, сковывать (somnus aliquem opprimit <i>Cs</i>);\n<b>9)</b> озадачивать, ставить в тупик (aliquem aliqua re <i>C</i>);\n<b>10)</b> стоять на своём, настаивать, не отставать: institit, oppressit, non remisit <i>C</i> (Скандилий) упорствовал, стоял на своём, не уступал;\n<b>11)</b> неясно произносить, проглатывать (litteras <i>C</i>)."],
  ["opprobramentum", "<big>opprobramentum</big>\n\n<b>opprobramentum, i</b> <i>n</i>\nпозор, срам (o. aut flagitium <i>Pl</i>)."],
  ["opprobratio", "<big>opprobratio</big>\n\n<b>opprobratio, onis</b> <i>f</i>\nупрёк, порицание: o. acerba reprehensionis <i>AG</i> жестокий упрёк."],
  ["opprobriosus", "<big>opprobriosus</big>\n\n<b>opprobriosus, a, um</b> [opprobrium]\nпозорящий, порочащий <i>CJ, Ambr</i>."],
  ["opprobrium", "<big>opprobrium</big>\n\n<b>opprobrium, i</b> <i>n</i> [opprobro]\n<b>1)</b> поношение (o. majorum <i>T</i>); позор, бесчестье: alicui opprobrio esse <i>C, Nep</i> лечь позорным пятном на кого-л.;\n<b>2)</b> попрёк, брань (opprobria dicere <i>O</i> <i>и</i> fundere <i>H</i>)."],
  ["opprobro", "<big>opprobro</big>\n\n<b>opprobro, —, —, are</b> [ob + probrum]\nупрекать, попрекать, бранить (alicui aliquid <i>Pl, AG</i>)."],
  ["oppugnatio", "<big>oppugnatio</big>\n\n<b>oppugnatio, onis</b> <i>f</i> [oppugno I]\n<b>1)</b> осада <i>или</i> штурм, приступ (castrorum, oppidi <i>Cs</i>): terra mari que oppugnationem instruere <i>L</i> подготовлять штурм с суши и с моря; oppugnationem ostendere <i>L</i> производить миимый (диверсионный) штурм; scientia oppugnationis <i>Cs</i> осадное искусство;\n<b>2)</b> противоречие, оппозиция, возражение (judicium sine oppugnatione <i>C</i>)."],
  ["oppugnator", "<big>oppugnator</big>\n\n<b>oppugnator, oris</b> <i>m</i> [oppugno I]\nнападающий, наступающий, идущий приступом (hostis et o. patriae Antonius <i>C</i>)."],
  ["oppugnatorius", "<big>oppugnatorius</big>\n\n<b>oppugnatorius, a, um</b>\nосадный <i>Vtr</i>."],
  ["oppugno 1", "<big>oppugno</big>\n\n<b>I op–pugno, avi, atum, are</b>\n<b>1)</b> нападать (o. aliquem <i>C</i>; Aegyptum <i>Nep</i>); атаковать, штурмовать, брать приступом (o. castra <i>Cs</i>; urbem operibus <i>L</i>);\n<b>2)</b> покушаться (o. alicujus salutem <i>C</i>): o. aliquem pecunia <i>C</i> пытаться подкупить кого-л.;\n<b>3)</b> выступать против, обвинять (oppugnatus in judicio pecuniarum repetundarum <i>Sl</i>)."],
  ["oppugno 2", "<big>oppugno</big>\n\n<b>II oppugno, (avi), atum, are</b> [ob + pugnus]\nбить кулаками, колотить (o. os <i>Pl</i>)."],
  ["opputo", "<big>opputo</big>\n\n<b>op–puto, —, —, are</b>\nподрезать, подстригать (opputatur quidquid tulit fructum <i>PM</i>)."],
  ["ops 1", "<big>ops</big>\n\n<b>I ops, opis</b> (<i>nom. и dat. sg.</i> <i>не встреч.</i>) <i>f</i>\n<b>1)</b> сила, мощь, могущество, возможность, власть: non opis est nostrae <i>V</i> не в нашей власти; gemina ope <i>O</i> с удвоенной силой; omnibus (summis) opibus <i>Pl etc.</i> всеми силами (изо всех сил); nihil est opis in hac voce <i>C</i> эти слова лишены всякого значения;\n<b>2)</b> помощь, поддержка, защита: ad medicam confugere opem <i>O</i> прибегнуть к помощи врача; ope alicujus <i>C</i> с чьей-л. помощью; opem alicui ferre <i>C</i> (afferre, dare <i>O</i>) оказывать кому-л. помощь;\n<b>3)</b> <i>pl.</i> средства (omnibus viribus atque opibus resistere <i>C</i>); имущество, состояние, богатство (opes modicae <i>O</i>): homo magnis opibus <i>Sl</i> очень состоятельный человек; opibus florere <i>C</i> (valere <i>Sl, C</i>) быть богатым; urbium opes exhaurire <i>L</i> грабить богатые города; pro opibus <i>Sl</i> в зависимости от имущественного положения; attritis opibus <i>T</i> ввиду расстроенных финансовых дел; opes ruris <i>O</i> сельскохозяйственные продукты; opes addunt fluvii mari <i>VF</i> реки несут свои воды в море;\n<b>4)</b> <i>pl.</i> военная мощь, вооружённые силы, войска (opibus praeesse <i>Nep</i>; Italiam suis opibus occupare <i>C</i>);\n<b>5)</b> политическая власть, господство, влиятельность (alicujus opes frangere <i>L</i>): opes et dignitatem tenere <i>C</i> пользоваться большим политическим весом."],
  ["Ops 2", "<big>Ops</big>\n\n<b>II Ops, Opis</b> <i>f</i>\nОпа, <i>римск. богиня плодородия и урожая; впоследствии отождествлялась с Реей Сильвией и с Кибелой и считалась женой Сатурна</i> <i>Vr, C, O</i>."],
  ["ops- 3", "<big>ops-</big>\n\n<b>III op–s-</b> <i>v. l.</i>\n= ob–s-."],
  ["opt-", "<big>opt-</big>\n\n<b>op–t-</b> <i>v. l.</i>\n= ob–t-."],
  ["optabilis", "<big>optabilis</big>\n\n<b>optabilis, e</b> [opto]\nжелательный, желаемый, желанный (pax <i>C</i>); вожделенный (venit ecce optabile tempus <i>O</i>)."],
  ["optabiliter", "<big>optabiliter</big>\n\n<b>optabiliter</b> [optabilis]\nжелательно <i>VM</i>."],
  ["optandus", "<big>optandus</big>\n\n<b>optandus, a, um</b>\n<b>1.</b> <i>part. fut. к</i> opto;\n<b>2.</b> <i>adj.</i> желательный, желанный (alia optanda sunt, alia aversanda <i>Sen</i>)."],
  ["optassis", "<big>optassis</big>\n\n<b>optassis</b> <i>Pl</i>\n= optaveris (<i>fut. II к</i> opto)."],
  ["optate", "<big>optate</big>\n\n<b>optate</b> [optatus]\nпо желанию <i>Lampr</i>."],
  ["optatio", "<big>optatio</big>\n\n<b>optatio, onis</b> <i>f</i> [opto]\n<b>1)</b> желание, пожелание <i>C</i>;\n<b>2)</b> <i>Symm</i> = optio I."],
  ["optativus", "<big>optativus</big>\n\n<b>optativus, a, um</b> [opto] <i>грам.</i>\nжелательный (modus)."],
  ["optato", "<big>optato</big>\n\n<b>optato</b> <i>adv.</i> [optatus]\nв соответствии с желанием, как хотелось <i>Pl, Ter, V</i>: mi o. veneris <i>C</i> твоё посещение доставит мне удовольствие."],
  ["optatum", "<big>optatum</big>\n\n<b>optatum, i</b> <i>n</i> [optatus]\nжелаемое, желание: praeter o. meum <i>C</i> против моего желания; mihi in optatis est <i>C</i> я желаю (мне хочется); optata furiosorum <i>C</i> несбыточные мечты, бредни."],
  ["optatus", "<big>optatus</big>\n\n<b>optatus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> opto;\n<b>2.</b> <i>adj.</i> желанный, с нетерпением ожидаемый, приятный (rumor <i>C</i>); дорогой (optatissime frater <i>C</i>)."],
  ["optice", "<big>optice</big>\n\n<b>optic&#275;, &#275;s</b> <i>f</i> (<i>греч.</i>)\nоптика <i>Vtr</i>."],
  ["optigo", "<big>optigo</big>\n\n<b>optigo</b> <i>Pl</i>\n= obtego."],
  ["optimas", "<big>optimas</big>\n\n<b>optim&#257;s, &#257;tis</b> [optimus]\n<b>1.</b> <i>adj.</i> знатнейший (genus <i>C</i>; matrona <i>Enn</i>); благороднейший (amor <i>Ap</i>);\n<b>2.</b> <i>subst. m</i> <i>преим.</i> <i>pl.</i> optimates, um <i>и</i> ium оптиматы, знать, аристократы, аристократическая партия (plebis et optimatium certamina <i>T</i>)."],
  ["optime", "<big>optime</big>\n\n<b>optim&#275;</b> <i>adv. superl. к</i>\nbene."],
  ["optimus", "<big>optimus</big>\n\n<b>optimus, a, um</b> <i>adj. superl. к</i>\nbonus."],
  ["optineo", "<big>optineo</big>\n\n<b>optineo</b> <i>Ap</i>\n= obtineo."],
  ["optio 1", "<big>optio</big>\n\n<b>I optio, &#333;nis</b> <i>f</i> [opto] (<i>тж.</i> o. eligendi <i>C</i>)\nсвободный выбор, неограниченная свобода, желание, воля, усмотрение: si o. sit <i>C</i> если угодно; o. tua est <i>Pl</i> <i>и</i> o. sit tua <i>C</i> пусть будет по твоему усмотрению (выбору); potestatem optionemque facere alicui, ut eligat <i>C</i> предоставить кому-л. полную свободу выбора."],
  ["optio 2", "<big>optio</big>\n\n<b>II optio, &#333;nis</b> <i>m</i>\n<b>1)</b> помощник (выбираемый самим начальником) <i>Pl, Dig</i>;\n<b>2)</b> <i>воен.</i> помощник центуриона <i>Vr, T, Veg</i>."],
  ["optionatus", "<big>optionatus</big>\n\n<b>opti&#333;n&#257;tus, &#363;s</b> <i>m</i> [optio II]\nдолжность помощника центуриона <i>Cato</i>."],
  ["optivus", "<big>optivus</big>\n\n<b>opt&#299;vus, a, um</b> [opto]\nсвободно избранный (cognomen <i>H</i>; tutor <i>G</i>)."],
  ["opto", "<big>opto</big>\n\n<b>opto, avi, atum, are</b>\n<b>1)</b> выбирать, избирать (duces <i>V</i>; aliquem patronum <i>PJ</i>; locum tecto <i>V</i>);\n<b>2)</b> желать, стремиться (o. libertatem <i>Sl</i>; sese laudari <i>Enn</i>; mortem <i>C, Sen, Lact</i>): votis o. <i>V</i> страстно желать; careat successibus opto <i>O</i> да не будет ему (ни в чём) удачи;\n<b>3)</b> просить (ab aliquo, ut... <i>C</i>)."],
  ["optum-", "<big>optum-</big>\n\n<b>optum-</b> <i>арх. v. l.</i>\n= optim-."],
  ["opulens", "<big>opulens</big>\n\n<b>opulens, entis</b> [ops]\n<b>1.</b> <i>adj.</i>\n<b>1)</b> богатый, состоятельный (matrona <i>Ap</i>);\n<b>2)</b> сильный, мощный (castellum <i>Ap</i>); могущественный (civitas <i>Sl</i>);\n<b>2.</b> <i>subst. m pl.</i> богачи <i>Nep</i>."],
  ["opulente", "<big>opulente</big>\n\n<b>opulente</b> <i>Ap</i>\n= opulenter."],
  ["opulenter", "<big>opulenter</big>\n\n<b>opulenter</b> [opulens]\nбогато, пышно (ludos facere <i>L</i>)."],
  ["opulentia", "<big>opulentia</big>\n\n<b>opulentia, ae</b> <i>f</i> [opulens]\n<b>1)</b> состоятельность, довольство, богатство (copiae atque opulentiae <i>AG</i>; o. pacis <i>Sl</i>);\n<b>2)</b> политическая сила, мощь, влияние (Lydorum <i>T</i>)."],
  ["opulentitas", "<big>opulentitas</big>\n\n<b>opulentitas, atis</b> <i>f</i> <i>Pl</i>\n= opulentia."],
  ["opulento", "<big>opulento</big>\n\n<b>opulento, —, —, are</b> [opulens]\nобогащать (aliquem aliqua re <i>H</i>); в изобилии снабжать (mensam dapibus pretiosis <i>Col</i>)."],
  ["opulentus", "<big>opulentus</big>\n\n<b>opulentus, a, um</b> [ops]\n<b>1.</b> <i>adj.</i>\n<b>1)</b> (<i>тж.</i> fortuna o. <i>Ap</i>) состоятельный, имущий, богатый (o. et copiosus <i>C</i>; Croesus inter reges opulentissimus <i>Sen</i>; o. auro <i>Pl</i>);\n<b>2)</b> обильный, пышный, щедрый (obsonium <i>Pl</i>; dona <i>Su</i>);\n<b>3)</b> сильный, могущественный (rex <i>Sl</i>); влиятельный (factio <i>L</i>);\n<b>4)</b> содержательный (liber <i>AG</i>);\n<b>5)</b> (драго)ценный (opulentissima metalla <i>Sen</i>);\n<b>2.</b> <i>subst. n pl.</i> богатства (opulenta regionis <i>QC</i>)."],
  ["opulesco", "<big>opulesco</big>\n\n<b>opulesco, —, —, ere</b> [ops]\nбогатеть <i>AG</i>."],
  ["opulus", "<big>opulus</big>\n\n<b>opulus, i</b> <i>f</i>\nклён (<i>Acer campestre</i>, L.) <i>PM</i>."],
  ["Opuntius 1", "<big>Opuntius</big>\n\n<b>I Opuntius, a, um</b> [Opus]\nопунтский <i>C</i>."],
  ["Opuntius 2", "<big>Opuntius</big>\n\n<b>II Opuntius, i</b> <i>m</i>\nжитель Опунта <i>L</i>."],
  ["opus 1", "<big>opus</big>\n\n<b>I opus, eris</b> <i>n</i>\n<b>1)</b> работа, труд, занятие, деятельность, профессия, функция, задача (rusticum <i>Pl, Ter, Col</i>; militare <i>Su</i>; servile <i>L</i>): in o. se collocare <i>Pl</i> наняться на работу; res est magni operis <i>QC</i> это требует большого труда; o. censorium <i>C</i> функция цензора; res mei operis est <i>L</i> это лежит на моей обязанности; o. facere <i>Pl, Col, C etc.</i> работать, строить, заниматься полевыми работами <i>и т. д.;</i>\n<b>2)</b> строительство, постройка, военное сооружение: urbem operibus oppugnare <i>L</i> штурмовать город с помощью осадных машин; lex operi (de opere) faciundo <i>C</i> строительный контракт;\n<b>3)</b> (<i>тж.</i> o. metallicum <i>или</i> metalli <i>Dig</i>) горнопромышленный труд, рудничная работа (in o. damnari <i>PJ</i>);\n<b>4)</b> фортификация, сооружение укреплений (miles in opere occupatus <i>Cs</i>);\n<b>5)</b> действие, результат действия (hastae <i>O</i>): prompsit e pharetra duo tela diversorum operum <i>O</i> (Купидон) вынул из колчана две стрелы различного действия;\n<b>6)</b> усилие, напряжение: magno opere <i>C etc.</i> весьма; nimio opere <i>C etc.</i> слишком;\n<b>7)</b> ручной труд, человеческое искусство (locus natura et opere munitus <i>Cs</i>);\n<b>8)</b> стиль, образец, <i>тж.</i> замысел: opere magnifico <i>C</i> в великолепном стиле; opere Palladio <i>Ph</i> по образцу Паллады;\n<b>9)</b> произведение, творение, соз(и)дание, работа, труд (opera magnifica atque praeclara <i>C</i>): flumen operibus obstruere <i>Cs</i> запрудить реку плотиной; o. marmoreum <i>O</i> мраморная статуя; o. pistorium <i>CC, PM, Pt</i> хлебопекарное изделие, печенье;\n<b>10)</b> литературное произведение (o. Sophocigum <i>C</i>): o. exigere <i>O</i> закончить поэму (книгу)."],
  ["opus 2", "<big>opus</big>\n\n<b>II opus</b> <i>n indecl.</i>\nпотребность, надобность: o. est нужно, следует (aliqua re, <i>реже</i> alicujus rei, aliquam rem <i>или</i> aliquid): emas non quod o. est, sed quod necesse est <i>Cato ap. Sen</i> покупай не то, что (просто) нужно, а то, что необходимо; quid tibi pecunia o. est? <i>PS</i> зачем тебе деньги?; quanti argenti o. est? <i>L</i> сколько денег нужно?; properato o. est <i>C</i> нужно поторопиться; auxilio mihi o. est <i>C</i> я нуждаюсь в помощи; quid verbis o. est? <i>Pl, Ter</i> к чему (лишние) слова?; temporis o. est ad aliquid <i>L</i> необходимо время для чего-л.; si quid o. est <i>C</i> в случае надобности; quae o. sunt <i>Cs</i> необходимые (нужные) мероприятия; omnia enumerare o. non est <i>C</i> незачем перечислять всё."],
  ["Opus 3", "<big>Opus</big>\n\n<b>III Opus, untis</b> <i>f</i>\nОпунт, <i>главн. город опунтских локров</i> (<i>на берегу Эвбейского залива</i>) <i>L, O</i>."],
  ["opusculum", "<big>opusculum</big>\n\n<b>opusculum, i</b> <i>n</i> [<i>demin. к</i> opus I]\nнебольшое литературное произведение, сочиненьице, небольшой труд (opuscula minuta <i>C</i>)."],
  ["ora 1", "<big>ora</big>\n\n<b>I ora, ae</b> <i>f</i> [os 1] (<i>тж.</i> <i>pl.</i>)\n<b>1)</b> граница, предел (regiones, quarum nulla esset o. <i>C</i>); край (poculi <i>Lcr</i>); кайма (clipei <i>V</i>); опушка (silvae <i>L</i>);\n<b>2)</b> берег, побережье, прибрежная область (Italiae <i>C, L</i>): o. maritima <i>Cs</i> приморье, <i>но тж.</i> <i>C</i> жители приморья;\n<b>3)</b> (далёкий) край (orae caelestes <i>O</i>): luminis orae <i>Enn, Lcr, V</i> край света, <i>т. е.</i> мир, земля, жизнь || <i>поэт.</i> область (maris terrarumque orae <i>L</i>): orae belli <i>V</i> театр войны; Acheruntis orae <i>Lcr</i> край Ахеронта, <i>т. е.</i> подземное царство;\n<b>4)</b> земной пояс, климатическая зона (o. gelida <i>H</i>; o. australis <i>C</i>)."],
  ["ora 2", "<big>ora</big>\n\n<b>II ora, ae</b> <i>f</i>\nкорабельный канат, причал (oras praecidere <i>L</i>, solvere <i>Q</i> <i>и</i> re sol vere <i>L</i>)."],
  ["Ora 3", "<big>Ora</big>\n\n<b>III Ora, ae</b> <i>f</i> <i>O</i>\n= Hersilia."],
  ["ora 4", "<big>ora</big>\n\n<b>IV ora</b> <i>pl. к</i>\nos I."],
  ["oraclum", "<big>oraclum</big>\n\n<b>oraclum, i</b> <i>n</i> <i>Acc, Vr, C</i>\n= oraculum."],
  ["oracularius", "<big>oracularius</big>\n\n<b>oracularius, a, um</b> [oraculum] <i>ирон.</i>\nзанимающийся прорицаниями, изрекающий оракулы (servi <i>Pt</i>)."],
  ["oraculum", "<big>oraculum</big>\n\n<b>oraculum, i</b> <i>n</i> [oro]\n<b>1)</b> оракул, храм, в который верующие обращались за прорицаниями (o. adire <i>O</i>);\n<b>2)</b> пророческое изречение оракула, прорицание, предсказание (o. edere <i>C</i>; oracula fundere <i>C</i>);\n<b>3)</b> высказывание, положение (physicorum <i>C</i>)."],
  ["orarium", "<big>orarium</big>\n\n<b>orarium, i</b> <i>n</i> [os I]\nплаток для вытирания лица, носовой платок <i>Vop, Treb, Eccl</i>."],
  ["orarius", "<big>orarius</big>\n\n<b>orarius, a, um</b> [ora I]\nприбрежный, береговой, каботажный (navis <i>PJ</i>)."],
  ["oratim", "<big>oratim</big>\n\n<b>oratim</b> [ora I]\nот берега к берегу <i>Sol</i> (<i>v. l.</i>)."],
  ["oratio", "<big>oratio</big>\n\n<b>oratio, onis</b> <i>f</i> [oro]\n<b>1)</b> способность (дар) речи: ferae rationis et orationis expertes sunt <i>C</i> животные лишены разума и речи;\n<b>2)</b> речь, язык (o. Latina <i>AG</i>); (<i>тж.</i> genus orationis <i>C, Q</i>) манера изъясняться, стиль, слог (qualis homo, talis ejus est o. <i>Q</i>); специальный язык (o. philosophorum <i>C</i>): utriusque orationis facultas <i>C</i> умение пользоваться обоими (основными) стилями (философским и риторическим);\n<b>3)</b> (<i>тж.</i> o. soluta <i>Vr</i>) прозаическая речь, проза (et in poematis et in oratione <i>C</i>; seu oratione, seu versibus <i>Ap</i>);\n<b>4)</b> материал речи, тема, предмет (o. deest <i>C</i>);\n<b>5)</b> высказывание, выражение, утверждение, слова: o. secunda <i>Sl</i> лестные слова; hac oratione habita <i>L</i> после этих слов (сказав это); tecum mihi instituenda est o. <i>C</i> мне нужно с тобой переговорить;\n<b>6)</b> доклад, обращение, выступление, речь: orationem habere (dicere) <i>C etc.</i> произносить речь; orationem facere (conficere) <i>C etc.</i> подготовлять речь; o. in aliquem <i>C, L</i> речь против кого-л.; o. pro aliquo <i>C, L</i> речь в защиту кого-л.;\n<b>7)</b> красноречие, дар слова (satis in eo est orationis <i>C</i>);\n<b>8)</b> императорский указ, рескрипт <i>T, Su</i>;\n<b>9)</b> молитва <i>Eccl</i>."],
  ["oratiuncula", "<big>oratiuncula</big>\n\n<b>oratiuncula, ae</b> <i>f</i> [<i>demin. к</i> oratio]\nнебольшая <i>или</i> изящная речь <i>C, Q</i>."],
  ["orator", "<big>orator</big>\n\n<b>orator, oris</b> <i>m</i> [oro]\n<b>1)</b> оратор (magnus, mediocris <i>C etc.</i>);\n<b>2)</b> делегат, посол с устным поручением (ad Pyrrhum de captivis recuperandis missus o. <i>C</i>);\n<b>3)</b> проситель, посредник (oratores mittere ad aliquem <i>Pl</i>);\n<b>4)</b> профессия оратора (oratorem complexi sumus <i>C</i>)."],
  ["oratoria", "<big>oratoria</big>\n\n<b>oratoria, ae</b> <i>f</i> (<i>sc.</i> ars)\nораторское искусство, риторика, красноречие <i>Q, Aug</i>."],
  ["oratorie", "<big>oratorie</big>\n\n<b>oratorie</b> [oratorius]\nпо-ораторски, красноречиво <i>C, Q</i>."],
  ["oratorium", "<big>oratorium</big>\n\n<b>oratorium, i</b> <i>n</i>\nмолитвенный дом, молельня <i>Aug</i>."],
  ["oratorius", "<big>oratorius</big>\n\n<b>oratorius, a, um</b> [orator]\nораторский (ingenium <i>C</i>; ars <i>Q</i>): oratio oratoria <i>C</i> речь, соответствующая правилам риторического искусства."],
  ["oratrix", "<big>oratrix</big>\n\n<b>oratrix, icis</b> <i>f</i> [orator]\n<b>1)</b> просящая, просительница (o. pacis <i>C</i>);\n<b>2)</b> (<i>sc.</i> ars) риторика, ораторское искусство <i>Q</i>."],
  ["oratus", "<big>oratus</big>\n\n<b>oratus, (us) </b> <i>m</i>\nпросьба: oratu tuo <i>C</i> по твоей просьбе."],
  ["orba", "<big>orba</big>\n\n<b>orba, ae</b> <i>f</i> [orbus]\n<b>1)</b> сирота <i>Ter, L, Q</i>;\n<b>2)</b> вдова <i>L</i>."],
  ["orbatio", "<big>orbatio</big>\n\n<b>orbatio, onis</b> <i>f</i> [orbo]\nлишение, отнятие <i>Sen</i>."],
  ["orbator", "<big>orbator</big>\n\n<b>orbator, oris</b> <i>m</i> [orbo]\nлишающий родителей <i>или</i> детей: nostri o. Achilles <i>O</i> Ахилл, убийца моих (Гекубы) детей."],
  ["orbiculatim", "<big>orbiculatim</big>\n\n<b>orbiculatim</b> [orbiculatus]\nокругло, в виде кругов (vertebrata ossa <i>PM</i>; o. rotari <i>Macr</i>)."],
  ["orbiculatus", "<big>orbiculatus</big>\n\n<b>orbiculatus, a, um</b> [orbiculus]\nкругообразный (ambitus foliorum <i>PM</i>); круглый, шаровидный (mala <i>Vr etc.</i>)."],
  ["orbiculus", "<big>orbiculus</big>\n\n<b>orbiculus, i</b> <i>m</i> [<i>demin. к</i> orbis]\n<b>1)</b> кружок, круглый ломтик (radix concisa in orbiculos <i>PM</i>);\n<b>2)</b> шарик: orbiculi lapillorum <i>Sol</i> жемчужины;\n<b>3)</b> ролик, каток <i>Cato, Vtr</i>;\n<b>4)</b> гнездо в перстне (<i>для камня или печатки</i>) (o. anuli <i>Ap</i>)."],
  ["orbifico", "<big>orbifico</big>\n\n<b>orbifico, —, —, are</b> [orbus + facio]\nлишать детей (orbificari liberorum leto <i>Acc</i>)."],
  ["orbile", "<big>orbile</big>\n\n<b>orbile, is</b> <i>n</i> [orbis]\nобод (<i>колеса</i>) <i>Vr</i>."],
  ["Orbilius Pupillus", "<big>Orbilius Pupillus</big>\n\n<b>Orbilius Pupillus</b> <i>m</i>\nОрбилий Пупилл, <i>из Беневента, римск. грамматик времён Цицерона</i> <i>H, Su</i>."],
  ["orbis", "<big>orbis</big>\n\n<b>orbis, is</b> (<i>abl.</i> <i>изредка</i> i) <i>m</i>\n<b>1)</b> окружность, круг: o. rotae <i>O, PM</i> обод колеса; ferratus o. <i>V</i> обитое железом колесо; dubius Fortunae o. <i>O</i> ненадёжное колесо Счастья; morae orbium <i>Ap</i> задержка из-за (поломки) колёс; o. saltatorius <i>C</i> хоровод; o. laneus <i>Prp</i> шерстяная повязка; o. muri <i>L</i> кольцевая стена; o. signifer <i>C</i> зодиак; o. lacteus <i>C</i> Млечный путь; orbes finientes <i>C</i> небосклон, горизонт; digitum justo orbe terit anulus <i>O</i> кольцо как раз по пальцу; immensis orbibus angues incumbunt pelago <i>V</i> исполинскими кольцами змеи простёрлись по морю; in orbem consistere <i>Cs</i> стать в (образовать) круг; in (per) orbem <i>L, V etc.</i> по порядку, по очереди <i>или</i> из рук в руки;\n<b>2)</b> <i>воен.</i> &laquo;кольцо&raquo;, <i>боевой порядок, со всех сторон прикрытый щитами</i> (<i>род каре</i>): orbem facere <i>Cs, Sl</i> образовать кольцевой строй; in orbem pugnare <i>L</i> Сражаться кольцом;\n<b>3)</b> диск, круг (solis, lunae <i>O etc.</i>): o. mensae <i>O</i> круглый стол; o. genuum <i>O</i> коленная чашка; o. luminis (oculorum) <i>O</i> глазная впадина (орбита), <i>но тж.</i> <i>O, St etc.</i> глаз || клубок (lanam glomerare in orbes <i>O</i>);\n<b>4)</b> <i>поэт.</i> щит (hasta per orbem transiit <i>V</i>; commovere orbem manu <i>Pt</i>);\n<b>5)</b> метательный диск (ictus ab orbe <i>O</i>);\n<b>6)</b> чашка весов (alternus o. <i>Tib</i>);\n<b>7)</b> круглое зеркало (o, nitidus <i>M</i>);\n<b>8)</b> кимвал, тимпан (orbem digito temperare <i>Su</i>);\n<b>9)</b> круговое движение, оборот, круговорот (temporum <i>QC</i>; annuus <i>V</i>); <i>поэт.</i> годичный круговорот, год (triginta orbes explere <i>V</i>); sidera orbes suos conficiunt <i>C</i> небесные светила совершают свои обороты;\n<b>10)</b> небесный свод, небо (o. Phoebi surgentis <i>VF</i>);\n<b>11)</b> переворот, смена (o. rei publicae <i>C</i>);\n<b>12)</b> <i>ритор.</i> закругление, период (orationis, verborum <i>C</i>; conficere orbem suum <i>C</i>);\n<b>13)</b> (<i>тж.</i> o.. terrarum <i>или</i> terrae <i>C, Just etc.</i>) земной круг, земля, мир (ager Campanus o, terrae pulcherrimus <i>C</i>): orbis caput <i>O</i> = мировая Столица;\n<b>14)</b> человеческий род, человечество: toti salutifer orbi cresce, puer <i>O</i> расти, дитя, на благо всему человечеству;\n<b>15)</b> область, страна (o. Assyrius <i>J</i>): Eous o. <i>O</i> восток, восточные страны; in quo lateas orbe <i>O</i> (не знаю), в каком краю ты скрываешься;\n<b>16)</b> царство, владение (Crete, quae meus est o. <i>O</i>);\n<b>17)</b> совокупность, система: o. doctrinae <i>Q</i> круг знаний, система наук, энциклопедия."],
  ["orbita", "<big>orbita</big>\n\n<b>orbita, ae</b> <i>f</i> [orbis]\n<b>1)</b> след от колеса, колея (impressa o. <i>C</i>);\n<b>2)</b> глубокий след, отпечаток (o. vinculi <i>PM</i>);\n<b>3)</b> круговой путь, орбита (lunae <i>LJ</i> <i>и</i> lunaris <i>Sen</i>);\n<b>4)</b> навык, старый пример (veteris culpae <i>J</i>)."],
  ["orbitas", "<big>orbitas</big>\n\n<b>orbitas, atis</b> <i>f</i> [orbus II]\n<b>1)</b> утрата, потеря (oculi <i>Ap</i>; luminis <i>PM</i>);\n<b>2)</b> осиротелость <i>C etc.</i>;\n<b>3)</b> бездетность, утрата детей (orbitates liberum = liberorum <i>C, Eccl</i>);\n<b>4)</b> неимение, отсутствие (o. rei publicae virorum talium <i>C</i>);\n<b>5)</b> вдовство <i>Just</i>."],
  ["orbitosus", "<big>orbitosus</big>\n\n<b>orbitosus, a, um</b> [orbita]\nизборождённый колеями, изъезженный <i>V</i>."],
  ["orbitudo", "<big>orbitudo</big>\n\n<b>orbitudo, inis</b> <i>f</i> <i>Acc, Pac</i>\n= orbitas."],
  ["orbitus", "<big>orbitus</big>\n\n<b>orbitus, a, um</b> [orbis]\nкругообразный, круговой (motus siderum <i>Vr</i>)."],
  ["Orbius", "<big>Orbius</big>\n\n<b>Orbius, a, um</b>\nОрбий, <i>римск. nomen</i>: P. O., <i>известный правовед времён Цицерона</i> <i>C</i>."],
  ["orbo", "<big>orbo</big>\n\n<b>orbo, avi, atum, are</b> [orbus II]\nлишать, отнимать (aliquem aliqua re <i>C</i>; orbatus vitae commodis <i>C</i>); отнимать родителей, делать сиротой <i>C, Sen</i>; отнимать детей (filio orbatus <i>C</i>; catulo orbata leaena <i>O</i>)."],
  ["Orbona", "<big>Orbona</big>\n\n<b>Orbona, ae</b> <i>f</i>\nОрбона, <i>римск. богиня, к которой обращались с мольбами лишившиеся детей родители</i> <i>C, PM, Eccl</i>."],
  ["orbus 1", "<big>orbus</big>\n\n<b>I orbus, &#299;</b> <i>m</i>\nсирота: orbi orbaeque <i>L</i> сироты и вдовы."],
  ["orbus 2", "<big>orbus</big>\n\n<b>II orbus, a, um</b>\n<b>1)</b> лишённый (aliqua re <i>или</i> alicujus rei): o. pedum <i>Lcr</i> безногий; o. consilio auxilioque <i>L</i> беспомощный; o. viribus <i>O</i> бессильный; lintea orba ventis <i>O</i> паруса, не надутые (попутными) ветрами;\n<b>2)</b> потерявший родителей, осиротевший (filius <i>C</i>); лишившийся детей, бездетный (senex <i>Sen</i>); вдовый, овдовевший: cubile orbum <i>Ctl</i> опустелое ложе;\n<b>3)</b> (<i>тж.</i> o. luminibus <i>PM</i> <i>или</i> luminis <i>O</i>) слепой (orba et iniqua fortuna <i>Ap</i>);\n<b>4)</b> не имеющий плодовых почек, бесплодный (palmites <i>Col</i>)."],
  ["orca", "<big>orca</big>\n\n<b>orca, ae</b> <i>f</i>\n<b>1)</b> животное из семейства китов, <i>предпол.</i> касатка (<i>Delphinus Orca</i>, L.) <i>PM</i>;\n<b>2)</b> бочка (<i>для солёной рыбы и пр.</i>) <i>Pers, H, Col etc.</i>;\n<b>3)</b> коробка для игральных костей <i>Pers</i>."],
  ["Orcades", "<big>Orcades</big>\n\n<b>Orcades, um</b> <i>f</i>\nОркады, <i>группа о-вов у сев. берегов Шотландии</i> (<i>ныне</i> Оркнейские <i>и</i> Шетландские) <i>Mela, PM, J</i>."],
  ["Orchamus", "<big>Orchamus</big>\n\n<b>Orchamus, i</b> <i>m</i>\nОрхам, <i>миф. царь Вавилона, муж Эвриномы, отец Левкотои</i> <i>O</i>."],
  ["orchas", "<big>orchas</big>\n\n<b>orchas, adis</b> <i>f</i> (<i>греч.</i>)\nорхада, <i>сорт маслин</i> <i>V</i>."],
  ["orchestra", "<big>orchestra</big>\n\n<b>orchestra, ae</b> <i>f</i> (<i>греч.</i>)\n<b>1)</b> орхестра, <i>у римлян — передние места в театре для сенаторов</i> <i>Vtr, Su, J</i>;\n<b>2)</b> <i>перен.</i> сенат <i>J</i>."],
  ["orchis", "<big>orchis</big>\n\n<b>orchis, is</b> <i>f Col</i>, <b>orchita (-es), ae</b> <i>m</i> <i>и</i> <b>orchitis, is</b> <i>f</i> <i>Cato, Col</i>\n= orchas."],
  ["Orchomenius 1", "<big>Orchomenius</big>\n\n<b>I Orchomenius, a, um</b> [Orchomenus]\nорхоменский <i>PM</i>."],
  ["Orchomenius 2", "<big>Orchomenius</big>\n\n<b>II Orchomenius, i</b> <i>m</i>\nжитель Орхомена <i>Nep, Just</i>."],
  ["Orchomenos", "<big>Orchomenos</big>\n\n<b>Orchomenos (-us), i</b> <i>f</i> <i>и</i> <b>Orchomenum, i</b> <i>n</i>\nОрхомеи:\n<b>1)</b> <i>город в Беотии, к сев.-зап. от озера Копаида</i> <i>Mela, Cs, PM</i>;\n<b>2)</b> <i>город в Аркадии, к сев.-зап. от Мантинеи</i> <i>L, O, PM</i>."],
  ["Orchus", "<big>Orchus</big>\n\n<b>Orchus</b> <i>Naev</i>\n= Orcus."],
  ["orcinianus", "<big>orcinianus</big>\n\n<b>orcinianus, a, um</b> [Orcus]\nпогребальный: sponda orciniana <i>M</i> похоронные носилки."],
  ["orcinus", "<big>orcinus</big>\n\n<b>orcinus, a, um</b> [Orcus]\nотносящийся к подземному царству, связанный со смертью, посмертный: senatores orcini <i>Su</i> получившие сенаторское звание по смерти Цезаря и в силу его завещания || отпущенный на волю по завещанию (liberti <i>Dig</i>)."],
  ["orcivus", "<big>orcivus</big>\n\n<b>orcivus, a, um</b> <i>v. l.</i>\n= orcinus."],
  ["orcula", "<big>orcula</big>\n\n<b>orcula, ae</b> <i>f</i> [<i>demin. к</i> orca 2]\nбочонок, ведро <i>Cato</i>."],
  ["Orcus", "<big>Orcus</big>\n\n<b>Orcus, i</b> <i>m</i>\nОрк:\n<b>1)</b> <i>подземный мир, царство теней</i> <i>V etc.</i>;\n<b>2)</b> (= Pluton) <i>бог подземного царства</i> <i>C, V</i>;\n<b>3)</b> смерть (fortis tanquam O. <i>Pt</i>): Orcum morari <i>H</i> медлить со смертью, <i>т. е.</i> продолжать жить; cum Orco rationem habere <i>Vr</i> (ponere <i>Col</i>) сводить счёты с Орком, <i>т. е.</i> готовиться к смерти."],
  ["Orcynia silva", "<big>Orcynia silva</big>\n\n<b>Orcynia silva</b> (<i>у греков</i>) <i>Cs</i>\n= Hercynia silva."],
  ["orde-", "<big>orde-</big>\n\n<b>orde-</b> <i>v. l.</i>\n= hordeordia prima <i>Lcr</i> = primordia (<i>см.</i> primordium)."],
  ["ordibor", "<big>ordibor</big>\n\n<b>ordibor</b> <i>Acc fut. к</i>\nordior."],
  ["ordinabilis", "<big>ordinabilis</big>\n\n<b>ordinabilis, e</b>\nмогущий быть упорядоченным <i>Boet</i>."],
  ["ordinalis", "<big>ordinalis</big>\n\n<b>ordinalis, e</b> [ordo] <i>грам.</i>\nпорядковый (nomen)."],
  ["ordinarie", "<big>ordinarie</big>\n\n<b>ordinarie</b>\nв порядке (disponere <i>Frontin</i>)."],
  ["ordinarius", "<big>ordinarius</big>\n\n<b>ordinarius, a, um</b> [ordo]\n<b>1)</b> расположенный (находящийся) в порядке (vites <i>Col</i>): lapis o. <i>Vtr</i> ординарная каменная кладка, <i>при которой верхний слой закрывает пазы нижнего</i>;\n<b>2)</b> соответствующий обычным правилам, обычный, надлежащий, нормальный: oleum ordinarium <i>Col</i> высококачественное оливковое масло (<i>из лучших маслин</i>); consul o. <i>L</i> консул, избранный в обычном порядке (<i>т. е.</i> <i>до наступления года</i>); oratio ordinaria <i>Sen</i> правильно построенная речь; non est res subsiciva philosophia, ordinaria est <i>Sen</i> философий есть нечто не побочное, а основное."],
  ["ordinate", "<big>ordinate</big>\n\n<b>ordinate</b> [ordinatus]\nв надлежащем порядке, правильно (disponere <i>rhH</i>)."],
  ["ordinatim", "<big>ordinatim</big>\n\n<b>ordinatim</b> [ordo]\nв должном порядке, по порядку, последовательно, правильно, как следует <i>Cs etc.</i>"],
  ["ordinatio", "<big>ordinatio</big>\n\n<b>ordinatio, onis</b> <i>f</i> [ordino]\n<b>1)</b> распределение, приведение в порядок, упорядочение, устройство, организация (vitae <i>PJ</i>): o. orientis <i>Su</i> управление восточными странами;\n<b>2)</b> определение, распоряжение, назначение: ordinatione praeficere aliquem Aegypto <i>Su</i> возложить на кого-л. управление Египтом;\n<b>3)</b> порядок, упорядоченность (mundus est ornata o. <i>Ap</i>)."],
  ["ordinativus", "<big>ordinativus</big>\n\n<b>ordinativus, a, um</b> [ordino]\nзнаменующий порядок (principatus <i>Tert</i>)."],
  ["ordinator", "<big>ordinator</big>\n\n<b>ordinator, oris</b> <i>m</i> [ordino]\nприводящий в Порядок, устроитель (rerum <i>Ap</i>); организатор (litis <i>Sen</i>)."],
  ["ordinatrix", "<big>ordinatrix</big>\n\n<b>ordinatrix, icis</b> <i>f</i>\nустроительница (mens o. rerum omnium <i>Aug</i>)."],
  ["ordinatus", "<big>ordinatus</big>\n\n<b>ordinatus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> ordino;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> правильный, упорядоченный (vita <i>Sen</i>);\n<b>2)</b> намеченный, определённый (cursus <i>C</i>);\n<b>3)</b> уравновешенный, аккуратный, солидный (compositus ordinatusque vir <i>Sen</i>)."],
  ["ordino", "<big>ordino</big>\n\n<b>ordino, avi, atum, are</b> [ordo]\n<b>1)</b> располагать в порядке, приводить в порядок (arbusta sulcis <i>H</i>; affluentes annos <i>H</i>); правильно распределять (partes orationis <i>C</i>); выстраивать (agmina <i>H</i>);\n<b>2)</b> устраивать, возбуждать (litem <i>Dig</i>);\n<b>3)</b> рассматривать <i>или</i> рассказывать по порядку (res publicas <i>H</i>);\n<b>4)</b> управлять (statum libererum civitatum <i>PJ</i>); руководить (o. studia alicujus <i>Pt</i>); назначать (magistratus <i>Su</i>); распределять (praefecturas <i>Just</i>);\n<b>5)</b> составлять (testamentum <i>Sen, Dig</i>)."],
  ["ordior", "<big>ordior</big>\n\n<b>ordior, orsus sum, iri</b> <i>depon.</i>\n<b>1)</b> <i>ткацк.</i> навивать основу (Parca, quae hominis vitam ordiatur <i>Lact</i>) <i>или</i> ткать (araneus orditur telas <i>PM</i>);\n<b>2)</b> начинать (opus, bellum <i>L</i>; sermonem <i>C</i>): initium alicujus rei o. <i>C</i> положить начало чему-л.; o. aliquem <i>Nep</i> начать говорить о ком-л. (дать чьё-л. жизнеописание); commodum dominari orso <i>Ap</i> как только (Поликрат) воцарился || начинать говорить, приступать к рассказу: o. ab aliqua re <i>C, L etc.</i> исходить из (начинать с) чего-л.; unde igitur ordiar? <i>C</i> с чего же мне начать?; tum Aeneas sic orsus est <i>V</i> тогда Эней начал так;\n<b>3)</b> говорить, обращаться (alicui <i>O</i>);\n<b>4)</b> начинаться (oratio orsa est <i>C</i>)."],
  ["ordo", "<big>ordo</big>\n\n<b>ordo, inis</b> <i>m</i> [ordior]\n<b>1)</b> ряд, вереница (arborum <i>Vr, C</i>; dentium <i>VM, PM</i>): directo ordine <i>C</i> по прямой линии; per bis quinque dies et junctas ordine noctes <i>O</i> в течение десяти дней и ночей подряд; ordine ponere <i>V</i> располагать в ряд;\n<b>2)</b> ряд мест в театре, скамьи: in quattuordecim ordinibus sedere <i>погов. C</i> принадлежать к сословию всадников (<i>которым отводилось в зрительном зале 14 первых рядов</i>);\n<b>3)</b> слой, пласт (caespitum <i>Cs</i>);\n<b>4)</b> <i>воен.</i> строй, шеренга, фронт: ordines explicare <i>L</i> развернуть (расширить) фронт; ordines habere (servare, observare) <i>Cs, Sl, L etc.</i> держать равнение; in suis ordinibus <i>Cs</i> в строгом равнении; nullo ordine <i>Cs</i> в беспорядке; aliquem in ordinem cogere (redigere) <i>погов. L, PJ, Su</i> поставить кого-л. в строй, <i>т. е.</i> разжаловать, понизить в должности, <i>перен.</i> . ограничить, смирить, унизить;\n<b>5)</b> группа, толпа, вереница (comitum <i>J</i>); <i>воен.</i> отряд; центурия (cohortes ordinesque <i>Cs</i>): primorum ordinum centuriones <i>Cs</i> командиры первых шести центурий, <i>т. е.</i> первой когорты (<i>это были старшие центурионы легиона</i>); ordinem ducere <i>Cs etc.</i> командовать центурией;\n<b>6)</b> должность центуриона, командование центурией <i>или</i> манипулой (decimum ordinem alicui assignare <i>L</i>); центурион: primi ordines <i>Cs</i> = centuriones primorum ordinum;\n<b>7)</b> сословие, звание, общественный слой (o. mercatorum <i>C</i>; homines omnium ordinum <i>Sl</i>): o. senatorius <i>или</i> amplissimus <i>C</i> сенаторское сословие, сенат; o. equestris <i>C</i> <i>или</i> secundus <i>Lcn</i> всадническое сословие || корпорация (scribarum <i>C</i>);\n<b>8)</b> порядок, расположение (agminis <i>L</i>); план (dictionis <i>Pt</i>); ход, течение (rerum, fatorum <i>V</i>): euntibus ordine fatis <i>O</i> в силу установленного порядка вещей || следование, движение, развитие, сцепление: o. seriesque causarum <i>C</i> цепь причинных связей || канон: o. a grammaticis datus <i>Q</i> перечень писателей-классиков (<i>установленный александрийскими филологами-критиками</i>); o. sanguinis <i>O</i> родословие; in ordinem adducere (referre) <i>C</i> приводить в порядок, упорядочить; (in) ordine <i>Pl, Ter, C etc.</i> <i>или</i> in ordinem <i>C</i> по порядку, в порядке, как следует, надлежащим образом; ex ordine <i>Ter, C</i> по порядку, <i>но тж.</i> <i>V</i> тотчас же, беспрерывно; extra ordinem <i>C</i> в беспорядке <i>или</i> <i>C, Q etc.</i> необыкновенно, чрезвычайно, вопреки обыкновению, в чрезвычайном порядке (alicui extra ordinem decernere provinciam <i>C</i>)."],
  ["Ordovices", "<big>Ordovices</big>\n\n<b>Ordovices, um</b> <i>m</i>\nордовики, <i>племя в сев. части нын. Уэлса, против о-ва Мона</i> (<i>ныне</i> Anglesey) <i>T</i>."],
  ["Oreades", "<big>Oreades</big>\n\n<b>Oreades, um</b> <i>f</i> (<i>греч.</i>)\nореады, <i>горные нимфы <i>V, O</i>. etc.</i>"],
  ["oreae", "<big>oreae</big>\n\n<b>oreae, arum</b> <i>f</i> [os I]\nудила <i>Naev</i>."],
  ["Oreas", "<big>Oreas</big>\n\n<b>Oreas, adis</b> (<i>acc. sg.</i> ada, <i>acc. pl.</i> adas) <i>f sg. к</i>\nOreades."],
  ["Orestae", "<big>Orestae</big>\n\n<b>Orestae, arum</b> <i>m</i>\nоресты, <i>ветвь племени молоссов в Эпире</i> <i>QC, L, PM</i>."],
  ["Orestes", "<big>Orestes</big>\n\n<b>Orestes, ae</b> <i>и</i> <b>is</b> <i>m</i>\nОрест, <i>сын Агамемнона и Клитемнестры, друг Пилада, убийца своей матери и Эгиста</i> <i>rhH, C, V, H, O etc.</i>"],
  ["Oresteus", "<big>Oresteus</big>\n\n<b>Oresteus, a, um</b> [Orestes]\nорестов: Orestea dea <i>O</i> = Diana."],
  ["Orestis", "<big>Orestis</big>\n\n<b>Orestis, idis</b> <i>f</i>\nОрестида, <i>область между Эпиром и Македонией</i> <i>C, L</i>."],
  ["Oretani", "<big>Oretani</big>\n\n<b>Oretani, orum</b> <i>m</i>\nоретаны, <i>племя на сев.-зап. Hispania Tarraconensis</i> (<i>с главн. городом Castulo</i>) <i>L</i>."],
  ["orexis", "<big>orexis</big>\n\n<b>orexis, is</b> (<i>acc.</i> im <i>или</i> in) <i>f</i> (<i>греч.</i>)\nаппетит <i>J, Lampr</i>."],
  ["organarius", "<big>organarius</big>\n\n<b>organarius, i</b> <i>m</i>\nмузыкант-инструменталист <i>Amm</i>."],
  ["organicus 1", "<big>organicus</big>\n\n<b>I organicus, a, um</b> [organum]\n<b>1)</b> механический, машинный (telarum administratio <i>Vtr</i>);\n<b>2)</b> музыкальный, певучий, поющий (saltus Heliconis <i>Lcr</i>)."],
  ["organicus 2", "<big>organicus</big>\n\n<b>II organicus, i</b> <i>m</i>\nмузыкант (<i>играющий на струнном инструменте</i>) <i>Lcr</i>."],
  ["organum", "<big>organum</big>\n\n<b>organum, i</b> <i>n</i> (<i>греч.</i>)\n<b>1)</b> орудие, инструмент <i>Col, PM, Vtr</i>;\n<b>2)</b> музыкальный инструмент (<i>преим.</i> орган) <i>Vtr, Su, Lampr, Eccl</i>."],
  ["Orgetorix", "<big>Orgetorix</big>\n\n<b>Orgetorix, igis</b> <i>m</i>\nОргеториг, <i>знатный гельвет</i> <i>Cs</i>."],
  ["orgia", "<big>orgia</big>\n\n<b>orgia, orum</b> <i>n</i> (<i>греч.</i>)\nоргии:\n<b>1)</b> ночные разгульные празднества в честь Вакха <i>Pl, Ctl, V, O etc.</i>;\n<b>2)</b> священная утварь, употреблявшаяся на этих празднествах (o. ferre condita cistis <i>SenT</i>);\n<b>3)</b> прочие разгульные таинства и празднества <i>J, St</i>;\n<b>4)</b> тайны (sacra o. naturae <i>Col</i>)."],
  ["oria", "<big>oria</big>\n\n<b>oria, ae</b> <i>f</i> <i>Pl</i>\n= horia."],
  ["oric-", "<big>oric-</big>\n\n<b>oric-</b> <i>v. l.</i> <i>Ctl</i>\n= auric-."],
  ["orichalcum", "<big>orichalcum</big>\n\n<b>orichalcum, i</b> <i>n</i> (<i>греч.</i>)\n<b>1)</b> медь (<i>преим.</i> желтая) <i>C, H, V</i>;\n<b>2)</b> медная труба (orichalca movere <i>VF</i>);\n<b>3)</b> медное оружие (orichalca renident <i>St</i>)."],
  ["Oricini", "<big>Oricini</big>\n\n<b>Oricini, orum</b> <i>m</i>\nжители города Орик <i>L</i>."],
  ["Oricius", "<big>Oricius</big>\n\n<b>Oricius, a, um</b> [Oricum]\nорикский <i>V</i>."],
  ["oricul-", "<big>oricul-</big>\n\n<b>oricul-</b> <i>v. l.</i>\n= auricul-."],
  ["Oricum", "<big>Oricum</big>\n\n<b>Oricum, i</b> <i>n</i> <i>и</i> <b>Oricus (-os), i</b> <i>f</i>\nОрик, <i>приморский город в Эпире</i> <i>Mela, L, H etc.</i>"],
  ["oriens", "<big>oriens</big>\n\n<b>oriens, entis</b>\n<b>1.</b> <i>part. praes. к</i> orior;\n<b>2.</b> <i>subst. m</i> (<i>sc.</i> sol) восходящее солнце <i>V, O</i>; восток <i>C, L, T etc.</i>"],
  ["orientalis", "<big>orientalis</big>\n\n<b>orientalis, e</b> [oriens]\nвосточный (ventus <i>AG</i>; populus <i>Treb</i>); orientales (<i>sc.</i> homines <i>или</i> incolae) <i>Just</i> жители стран Востока."],
  ["orificium", "<big>orificium</big>\n\n<b>orificium, i</b> <i>n</i> [os I]\n<b>1)</b> отверстие <i>Ap, Macr</i>;\n<b>2)</b> лицо (labra et ipsa orificia <i>Veg</i>)."],
  ["origa", "<big>origa</big>\n\n<b>origa, ae</b> <i>m</i> <i>Vr</i>\n= auriga."],
  ["origanites", "<big>origanites</big>\n\n<b>origanites</b> <i>или</i> <b>origanitus, a, um</b> [origanum]\nсдобренный ориганом (vinum <i>Cato</i>)."],
  ["origanum", "<big>origanum</big>\n\n<b>origanum (-on), i</b> <i>n</i> (<i>греч.</i>) <i>бот.</i>\nориган, душица <i>Col, PM, Pall</i>."],
  ["originalis", "<big>originalis</big>\n\n<b>originalis, e</b> [origo]\nпервоначальный, первичный (Ceres frugum parens o. <i>Ap</i>; rerum species <i>Macr</i>)."],
  ["originaliter", "<big>originaliter</big>\n\n<b>originaliter</b>\nпервоначально <i>Aug</i>."],
  ["originarius", "<big>originarius</big>\n\n<b>originarius, a, um</b>\n<b>1)</b> первоначальный (solum <i>CTh</i>);\n<b>2)</b> по происхождению, <i>т. е.</i> природный (coloni <i>CJ</i>);\n<b>3)</b> туземный, местный, коренной (<i>sc.</i> incolae <i>CJ</i>)."],
  ["originatio", "<big>originatio</big>\n\n<b>originatio, onis</b> <i>f</i> [origo]\nсловопроизводство, этимология <i>Q</i>."],
  ["originitus", "<big>originitus</big>\n\n<b>originitus</b> <i>adv.</i>\nпо происхождению (Persae, qui sunt o. Scythae <i>Amm</i>)."],
  ["origo", "<big>origo</big>\n\n<b>origo, inis</b> <i>f</i> [orior]\n<b>1)</b> происхождение, начало (omnium rerum <i>C</i>): originem trahere <i>L</i> (ducere <i>H</i>, habere <i>PM</i>) ab aliquo вести свой род (происходить) от кого-л.; prima ab origine mundi <i>O</i> с самого возникновения мира;\n<b>2)</b> род: ab origine ultima stirpis Romanae generatus <i>Nep</i> происходящий из весьма древнего римского рода;\n<b>3)</b> родоначальник, предок (Aeneas Romanae stirpis o. <i>V</i>);\n<b>4)</b> метрополия (urbium <i>Sl</i>);\n<b>5)</b> родина, первоисточник (summi boni <i>C</i>; verborum <i>Q</i>; juris <i>Dig</i>);\n<b>6)</b> <i>pl.</i> Origines &laquo;Древнейшая история&raquo; (<i>сочинение М. Порция Катона об истории италийских городов-государств</i>) <i>C, AG</i>."],
  ["oriola", "<big>oriola</big>\n\n<b>oriola, ae</b> <i>f</i> <i>v. l.</i>\n= horiola."],
  ["Orion", "<big>Orion</big>\n\n<b>Orion, onis</b> <i>m</i> (<i>gen.</i> <i>тж.</i> Orionis <i>и</i> Orionis)\nОрион, <i>великан-охотник из Беотии, возлюбленный Авроры, убитый Дианой и превращенный со своей собакой в созвездие</i> (<i>его заход в начале ноября считался предвестником гроз</i>) <i>V, H, O etc.</i>"],
  ["orior", "<big>orior</big>\n\n<b>orior, ortus sum, iri</b> (<i>part. fut.</i> oriturus, oriundus) <i>depon.</i>\n<b>1)</b> вставать (de nocte <i>L</i>); восходить, появляться (sol oritur <i>O</i>); orta luce <i>Cs</i> на рассвете; sol oriens <i>C etc.</i> восток;\n<b>2)</b> начинаться (Belgae ab extremis Galliae finibus oriuntur <i>Cs</i>): oratio oriens <i>C</i> начало речи;\n<b>3)</b> возникать (bellum oritur <i>C</i>): levi contentione atrox caedes orta (est) <i>T</i> лёгкий спор перерос в жестокую резню || вырастать (e gemma oriens uva <i>C</i>); подниматься (ventus oriens <i>Nep</i>; tempestas, clamor oritur <i>Nep, Cs</i>); вспыхивать (seditio orta est <i>L</i>); исходить (bellum ab Hannibale ortum est <i>L</i>); брать начало, зарождаться (fons in monte oritur <i>PJ</i>); происходить, рождаться (ab <i>или</i> ex aliquo <i>C, Sl etc.</i>): a Catone ortus <i>C</i> потомок Катона; homo a se ortus <i>C</i> человек, добившийся почестей личными заслугами; Corintho ortus <i>O</i> родом из Коринфа; tibi a me nulla orta est injuria <i>Ter</i> я не причинил тебе никакой обиды."],
  ["oris", "<big>oris</big>\n\n<b>oris</b> <i>gen. sg. к</i>\nos I."],
  ["Oritae", "<big>Oritae</big>\n\n<b>Oritae, arum</b> <i>m</i>\nориты, <i>индийское племя в Гедросии</i> <i>PM, QC</i>."],
  ["Orithyia", "<big>Orithyia</big>\n\n<b>&#332;r&#299;thy&#299;a, ae</b> <i>f</i>\nОрифия:\n<b>1)</b> <i>дочь афинского царя Эрехтея, жена Борея, мать Зетеса и Калаида</i> <i>C, O</i>;\n<b>2)</b> <i>царица амазонок</i> <i>Just</i>."],
  ["oriundus", "<big>oriundus</big>\n\n<b>oriundus, a, um</b> [orior]\nпроисходящий (ab aliquo <i>Pl etc.</i>); уроженец (Thebis <i>Ap</i>; ex insula <i>Ap</i>; ex Achaia <i>L</i>; Alba <i>L</i>)."],
  ["Ormenis", "<big>Ormenis</big>\n\n<b>Ormenis, idis</b> <i>f</i>\nвнучка Ормена, <i>основателя города Ормениона в Магнесии</i> (<i>Фессалия</i>) <i>O</i>."],
  ["ornamentum", "<big>ornamentum</big>\n\n<b>ornamentum, i</b> <i>n</i> [orno]\n<i>преим. pl.</i>\n<b>1)</b> снаряжение, вооружение, оружие (per ornamenta percuti <i>Sen</i>);\n<b>2)</b> сбруя (ornamenta asinorum <i>Cato</i>);\n<b>3)</b> одежда, наряд (feminarum <i>Q</i>); убранство, драгоценности (ornamenta ex fano Herculis in oppidum conferre <i>Cs</i>);\n<b>4)</b> знаки отличия, награды (omnia ornamenta congerere ad aliquem <i>C</i>);\n<b>5)</b> звание, титул (o. consulare <i>Su</i>);\n<b>6)</b> орнамент (forum et basilicas ornamentis ornare <i>C</i>);\n<b>7)</b> украшение, честь, слава (senectutis <i>C</i>);\n<b>8)</b> прикрасы (sine ullis verborum ornamentis <i>C</i>)."],
  ["ornate", "<big>ornate</big>\n\n<b>ornate</b> [ornatus]\nнарядно, красиво, изящно, со вкусом (facere, loqui <i>C</i>)."],
  ["ornatio", "<big>ornatio</big>\n\n<b>ornatio, onis</b> <i>f</i> [orno]\nукрашение, отделка <i>Vtr</i>."],
  ["ornatrix", "<big>ornatrix</big>\n\n<b>ornatrix, icis</b> <i>f</i> [orno]\nрабыня, занятая туалетом своей госпожи <i>O, Su, Dig</i>."],
  ["ornatulus", "<big>ornatulus</big>\n\n<b>ornatulus, a, um</b> [<i>demin. к</i> ornatus I]\nразодетый, щегольской (muliercula <i>Pl</i>)."],
  ["ornatus 1", "<big>ornatus</big>\n\n<b>I ornatus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> orno;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> снаряжённый, снабжённый (ornatissimus et paratissimus omnibus rebus <i>C</i>); оснащённый (navis <i>Cs</i>);\n<b>2)</b> взнузданный и осёдланный (equus <i>L</i>);\n<b>3)</b> отмеченный, награждённый (honoribus <i>C</i>);\n<b>4)</b> почётный (locus ad dicendum <i>C</i>);\n<b>5)</b> красивый, изящный (oratio, versus <i>C</i>): agro bene culto nihil specie ornatius <i>C</i> нет ничего красивее хорошо возделанного поля;\n<b>6)</b> прекрасный, превосходный, выдающийся (homo o. in dicendo <i>C</i>; adulescens ornatissimus <i>C</i>)."],
  ["ornatus 2", "<big>ornatus</big>\n\n<b>II ornatus, us</b> <i>m</i> (<i>gen. sg.</i> i <i>Ter</i>) [orno]\n<b>1)</b> снаряжение, вооружение (o. militaris <i>C</i>);\n<b>2)</b> облачение, одежда (o. regalis <i>C</i>);\n<b>3)</b> наряд (o. muliebris <i>C</i>);\n<b>4)</b> сбруя (equus regio ornatu instructus <i>PM</i>);\n<b>5)</b> разукрашивание, украшение (portarum <i>Hirt</i>; urbis <i>C</i>; verborum <i>C</i>); краса (aedilitatis <i>C</i>): afferre ornatum rei alicui <i>C</i> украшать что-л."],
  ["orneoscopus", "<big>orneoscopus</big>\n\n<b>orneoscopus, i</b> <i>m</i> (<i>греч.</i>; <i>лат.</i> auspex)\nпрорицатель по полёту птиц, птицегадатель <i>Lampr</i>."],
  ["orneus", "<big>orneus</big>\n\n<b>orneus, a, um</b> [ornus]\nясеневый (frons <i>Col</i>)."],
  ["Orni", "<big>Orni</big>\n\n<b>Orni, orum</b> <i>m</i>\nОрны, <i>укреплённое место на Пропонтиде</i> (<i>Фракия</i>) <i>Nep</i>."],
  ["ornithias", "<big>ornithias</big>\n\n<b>ornithias, ae</b> (<i>acc.</i> an) <i>m</i> (<i>греч.</i>)\nптичий ветер (<i>дующий ранней весной, когда возвращаются перелётные птицы</i>) <i>Col, Vtr, Ap, PM</i>."],
  ["ornithon", "<big>ornithon</big>\n\n<b>ornithon, onis</b> (<i>acc.</i> ona) <i>m</i> (<i>греч.</i>; <i>лат.</i> aviarium)\nптичий двор, птичник <i>Vr, Col</i>."],
  ["orno", "<big>orno</big>\n\n<b>orno, avi, atum, are</b> [<i>из</i> ordino]\n<b>1)</b> снабжать (aliquem aliqua re <i>Enn, C, Nep etc.</i>);\n<b>2)</b> одевать, наряжать <i>Ter</i>: o. se <i>и</i> ornari <i>Pl, Ter</i> одеваться, наряжаться;\n<b>3)</b> снаряжать (exercitum <i>L</i>); оснащать (classem <i>C</i>; navem <i>L</i>); устраивать, подготовлять (convivium <i>C</i>): o. armis <i>V</i> вооружать; o. provinciam <i>C</i> послать в провинцию всё необходимое для управления ею (<i>служебный персонал, войска, деньги</i>);\n<b>4)</b> украшать (domum suam <i>C</i>; orationem <i>C</i>); убирать (sepulcrum floribus <i>C</i>); причёсывать, завивать (capillos <i>O</i>);\n<b>5)</b> хвалить, превозносить, прославлять (aliquid verbis, laudibus <i>C</i>); чтить (egressum alicujus frequentia <i>C</i>; aliquem beneficiis <i>C</i>): aliquis imperator a senatu ornatus est <i>C</i> кто-л. пожалован сенатом званием императора."],
  ["ornus", "<big>ornus</big>\n\n<b>ornus, i</b> <i>f</i>\nясень (<i>Fraxinus Ornus</i>, L.) <i>Col, V, H, O etc.</i>"],
  ["oro", "<big>oro</big>\n\n<b>oro, avi, atum, are</b> [os 1]\n<b>1)</b> говорить (talibus dictis <i>V</i>): orandi nescius <i>T</i> не имеющий ораторского дарования;\n<b>2)</b> <i>юр.</i> защищать (causam <i>C, L, T</i>); вести (litem <i>C</i>);\n<b>3)</b> просить, молить (aliquem aliquid <i>T, V etc.</i>; aliquid alicui <i>Ter etc.</i>): o. mortem <i>V</i> призывать смерть; per omnes deos aliquem o. <i>H</i> заклинать кого-л. всеми богами; o. veniam alicui rei <i>O</i> извиняться за что-л.; <i>вводно</i> oro (te, vos) <i>C, L, Sen etc.</i> прошу, пожалуйста, послушай(те)."],
  ["Oroanda", "<big>Oroanda</big>\n\n<b>Oroanda, orum</b> <i>n</i>\nОроанды, <i>город на вост. границе Писидии</i> <i>L</i>."],
  ["Oroandensis", "<big>Oroandensis</big>\n\n<b>Oroandensis, is</b> <i>m</i>\nжитель города Oroanda <i>L</i>."],
  ["Oroandicus", "<big>Oroandicus</big>\n\n<b>Oroandicus, a, um</b> [Oroanda]\nороандский <i>C, PM</i>."],
  ["Orodes", "<big>Orodes</big>\n\n<b>Orodes, is</b> (<i>редко</i> <b>i</b>) <i>m</i>\nОрод:\n<b>1)</b> <i>царь парфян, взявший в плен и казнивший Красса</i> <i>C, VP, Just</i>;\n<b>2)</b> <i>царь Альбании</i> <i>Eutr</i>;\n<b>3)</b> <i>имя воина</i> <i>V</i>."],
  ["Oromedon", "<big>Oromedon</big>\n\n<b>Oromedon, ontis</b> <i>m</i>\nОромедонт, <i>один из гигантов</i> <i>Prp</i>."],
  ["Orontes", "<big>Orontes</big>\n\n<b>Orontes, ae, is</b> <i>и</i> <b>i</b> <i>m</i>\nОронт:\n<b>1)</b> <i>главн. река Сирии</i> <i>Mela, Prp, V</i>;\n<b>2)</b> <i>предводитель ликийцев, спутник Энея</i> <i>V</i>."],
  ["Oronteus", "<big>Oronteus</big>\n\n<b>Oronteus, a, um</b> [Orontes 1]\nоронтский, <i>поэт.</i> сирийский (murra <i>Prp</i>)."],
  ["Oropos", "<big>Oropos</big>\n\n<b>Oropos (-us), i</b> <i>f</i>\nОроп, <i>город в Беотии, близ Аттики</i> <i>C, L, PM</i>."],
  ["Orosius", "<big>Orosius</big>\n\n<b>Orosius, i</b> <i>m</i>\n(Paulus) Орозий, <i>христианский писатель <i>V</i> в. н. э., уроженец Таррагоны, автор &laquo;Historiarum adversus paganos libri VII&raquo;, &laquo;De arbitrii Ubertate&raquo; и др.</i>"],
  ["orphaici", "<big>orphaici</big>\n\n<b>orphaici, orum</b> <i>m</i>\nорфики, последователи Орфея <i>Macr</i>."],
  ["orphanotrophium", "<big>orphanotrophium</big>\n\n<b>orphanotrophium (-eum), i</b> <i>n</i> (<i>греч.</i>)\nсиротский приют <i>CJ</i>."],
  ["Orpheicus", "<big>Orpheicus</big>\n\n<b>Orpheicus (Orphicus), a, um</b> [Orpheus I]\nорфеев, орфический (versus <i>Macr</i>; carmen <i>C</i>)."],
  ["Orpheus 1", "<big>Orpheus</big>\n\n<b>I Orphe&#865;us, e&#299;</b> <i>и</i> <b>eos</b> <i>m</i>\nОрфей:\n<b>1)</b> <i>сын Аполлона (по др., фракийского царя Эагра) и Каллиопы, муж Эвридики, миф. поэт и певец, растерзанный вакханками</i> <i>C, V, O etc.</i>;\n<b>2)</b> <i>раб Цицерона</i> <i>C</i>."],
  ["Orpheus 2", "<big>Orpheus</big>\n\n<b>II Orph&#275;us</b> <i>и</i> <b>Orphicus, a, um</b> <i>Prp etc.</i>\n= Orpheicus."],
  ["orphus", "<big>orphus</big>\n\n<b>orphus, i</b> <i>m</i> (<i>греч.</i>)\nорф (<i>морская рыба</i>) <i>PM</i>."],
  ["orropygium", "<big>orropygium</big>\n\n<b>orropygium, i</b> <i>n</i> <i>v. l.</i>\n= orthopygium."],
  ["orsa", "<big>orsa</big>\n\n<b>orsa, orum</b> <i>n</i> [ordior]\n<b>1)</b> начинание, начало (operis <i>L</i>);\n<b>2)</b> речи, слова (o. referre <i>V</i>)."],
  ["orsus 1", "<big>orsus</big>\n\n<b>I orsus, a, um</b> <i>part. pf. к</i>\nordior."],
  ["orsus 2", "<big>orsus</big>\n\n<b>II orsus, us</b> <i>m</i> [ordior]\nначинание, начало (tenuem formare orsum <i>V</i>)."],
  ["orthius", "<big>orthius</big>\n\n<b>orthius, a, um</b> (<i>греч.</i>)\nвозвышенный, высокий (carmen <i>AG</i>)."],
  ["orthodoxus", "<big>orthodoxus</big>\n\n<b>orthodoxus, a, um</b> (<i>греч.</i>)\nправоверный <i>CJ, Eccl</i>."],
  ["orthogonius", "<big>orthogonius</big>\n\n<b>orthogonius, a, um</b> (<i>греч.</i>)\nпрямоугольный <i>Vtr</i>."],
  ["orthographia", "<big>orthographia</big>\n\n<b>orthographia, ae</b> <i>f</i> (<i>греч.</i>)\n<b>1)</b> орфография, правописание <i>Su</i>;\n<b>2)</b> <i>архит.</i> чертёж здания с фасада <i>Vtr</i>."],
  ["orthopnoea", "<big>orthopnoea</big>\n\n<b>orthopnoea, ae</b> <i>f</i> (<i>греч.</i>)\nтяжёлая одышка (<i>заставляющая больного находиться всегда в стоячем положении</i>) <i>PM</i>."],
  ["orthopsalticus", "<big>orthopsalticus</big>\n\n<b>orthopsalticus, a, um</b> (<i>греч.</i>)\nисполняемый в высокой тонации (psalterium <i>Vr</i>)."],
  ["orthopygium", "<big>orthopygium</big>\n\n<b>orthopygium, i</b> <i>n</i> (<i>греч.</i>)\nторчащий вверх хвост, гузка (o. macrae anatis <i>M</i>)."],
  ["orthostata", "<big>orthostata</big>\n\n<b>orthostata, ae</b> <i>m</i> (<i>греч.</i>; <i>лат.</i> frons) <i>архит.</i>\nфасад стены <i>Vtr</i>."],
  ["ortivus", "<big>ortivus</big>\n\n<b>ortivus, a, um</b> [ortus]\nвосходящий (sol <i>Ap</i>): cardo o. <i>Man</i> восточная часть неба."],
  ["Ortona", "<big>Ortona</big>\n\n<b>Ortona, ae</b> <i>f</i>\nОртона, <i>город эквов в Латии</i> <i>L</i>."],
  ["ortus 1", "<big>ortus</big>\n\n<b>I ortus, a, um</b> <i>part. pf. к</i>\norior."],
  ["ortus 2", "<big>ortus</big>\n\n<b>II ortus, us</b> <i>m</i> [orior]\n<b>1)</b> восход (solis et lunae reliquarumque siderum <i>C</i>): o. brumalis <i>Ap</i> сторона, где солнце восходит зимой; primi ortus <i>V</i> начало восхода, <i>т. е.</i> утренняя заря;\n<b>2)</b> восток (ab ortu ad occasum commeare <i>C</i>): ad finem solis ab ortu <i>O</i> от востока до запада;\n<b>3)</b> возникновение, начало, зарождение, происхождение (animantium <i>C</i>; tribuniciae potestatis <i>C</i>): primo ortu <i>C</i> тотчас же после рождения; ortum ducere ab aliquo <i>O</i> происходить от кого-л."],
  ["Ortygia", "<big>Ortygia</big>\n\n<b>Ortygia, ae</b> <i>и</i> <b>Ortygie, es</b> <i>f</i>\nОртигия:\n<b>1)</b> <i>древнее название о-ва Делоса</i> <i>V, O</i>;\n<b>2)</b> <i>островная и полуостровная часть Сиракуз с цитаделью</i> <i>V, O</i>;\n<b>3)</b> <i>роща близ Эфеса</i> <i>T</i>."],
  ["Ortygius", "<big>Ortygius</big>\n\n<b>Ortygius, a, um</b> [Ortygia 1]\nортигийский, <i>поэт.</i> делосский <i>O</i>."],
  ["oryx", "<big>oryx</big>\n\n<b>oryx, ygis</b> <i>m</i> (<i>греч.</i>)\nгазель <i>Col, PM, M, J etc.</i>"],
  ["oryza", "<big>oryza</big>\n\n<b>oryza, ae</b> <i>f</i> (<i>греч.</i>)\nрис <i>CC, H etc.</i>"],
  ["os 1", "<big>os</big>\n\n<b>I os, oris</b> <i>n</i> (<i>тж.</i> <i>pl.</i>)\n<b>1)</b> рот, уста (os aqua implere <i>Sen</i>); пасть (Cerberi <i>H</i>; <i>перен.</i> belli <i>C</i>); клюв (pulli <i>C, PM</i>); орган речи: libero ore loqui <i>Sl</i> говорить откровенно; alicui (alicujus) in ore esse <i>rhH, C</i> быть частым предметом чьего-л. разговора; aliquem in ore habere <i>C</i> часто говорить о ком-л.; in hominum ora abire <i>L</i> (venire <i>Prp, H</i>) стать предметом общих разговоров (притчей во языцех); uno ore <i>Ter, C, V</i> единогласно; respondere ore alicujus <i>C</i> дать ответ через кого-л.; aliquem ex ore alicujus admirari <i>C</i> восторгаться кем-л. в чьём-л. изображении; pleno ore <i>C</i> во весь голос;\n<b>2)</b> <i>поэт.</i> речь, разговор (o. atque oratio <i>Nep</i>): ora sono discordia <i>V</i> чужой говор, иностранная речь; o. Latinum <i>PJ</i> латинская речь; o. promptum <i>Q</i> бойкий язык; tali ore locutus <i>V</i> произнося эти слова;\n<b>3)</b> красноречие, дар слова (ardor oris <i>VP</i>);\n<b>4)</b> лицо, обличье: o. vultusque <i>PJ</i> черты лица; coram in o. <i>Ter</i> открыто, в лицо; os ducere <i>C</i> гримасничать; o. praebere ad aliquid <i>L</i> подвергать себя чему-л.; ora tenere <i>V</i> глядеть; omnium ora in se convertere <i>C, Sl</i> обратить на себя всеобщее внимание; tria virginis ora Dianae <i>V</i> трёхликая дева Диана (<i>т. е.</i> &laquo;Super terras Luna, iri terris Diana, sub terris Proserpina&raquo; <i>Servius</i>);\n<b>5)</b> наличие, присутствие: in ore <i>C, Sen</i> (ante ora <i>L, V</i>; per ora <i>St, H</i>) alicujus на глазах у кого-л., в чьём-л. присутствии; concedere ab ore alicujus <i>Ter</i> оставить (уйти от) кого-л.; in ore omnium versari <i>C</i> показываться публично;\n<b>6)</b> отверстие (dolii <i>L</i>; ulceris <i>V</i>; fenestrae <i>V</i>); вход, выход (portus <i>C</i>; specus <i>L</i>); расселина, щель <i>или</i> кратер (montis <i>Sen</i>); устье (fluminis <i>O, VF</i>); источник (ora fontium <i>O</i>);\n<b>7)</b> нос корабля (ora navium rostrata <i>H</i>);\n<b>8)</b> голова (Gorgonis o. cinctum anguibus <i>C</i>);\n<b>9)</b>: o. <i>или</i> o. durum <i>C</i> (impudens <i>Ter</i>, ferreum <i>Su</i>) наглый вид, нахальство, бесстыдство (Appii os habere <i>C</i>)."],
  ["os 2", "<big>os</big>\n\n<b>II os, ossis</b> <i>n</i>\n<b>1)</b> кость; <i>pl.</i> кости <i>или</i> костяк, скелет <i>C etc.</i>: (in) ossibus <i>V, O</i> <i>или</i> per ossa <i>V</i> внутри, в глубине души; ossa legere <i>V, Sen</i> собирать кости после кремации <i>или</i> выбирать из раны осколки кости;\n<b>2)</b> косточка (olearum <i>Su</i>; prunorum <i>Pall</i>);\n<b>3)</b> сердцевина (arborum <i>PM</i>)."],
  ["Osca", "<big>Osca</big>\n\n<b>Osca, ae</b> <i>f</i>\nОска, <i>город илергетов в сев.-вост. Испании</i> (<i>ныне</i> Huesca) <i>VP, Fl</i>."],
  ["oscedo", "<big>oscedo</big>\n\n<b>oscedo, inis</b> <i>f</i> [oscito]\n<b>1)</b> позыв к зевоте, желание зевать <i>AG</i>;\n<b>2)</b> (<i>у детей</i>) язвочки на слизистой оболочке рта (<i>предпол.</i> афты) <i>Is</i>."],
  ["oscen", "<big>oscen</big>\n\n<b>oscen, inis</b> <i>m</i> (<i>редко</i> <i>f</i>) [obs + cano] <i>культ.</i>\nвещая птица (<i>ворон, ворона, сова</i>), <i>по крику которой авгуры делали предсказания</i> <i>Vr, C, H, Sid etc.</i>"],
  ["Oscensis 1", "<big>Oscensis</big>\n\n<b>I Oscensis, ium</b> <i>m</i> [Osca]\nоскский <i>L</i>."],
  ["Oscensis 2", "<big>Oscensis</big>\n\n<b>II Oscensis, e</b>\nжитель города Osca <i>Cs</i>."],
  ["Osci", "<big>Osci</big>\n\n<b>Osci, orum</b> <i>m</i>\nоски, <i>южн. ветвь умбрского племени в Кампании, на реке Лирис</i> <i>Vr, V</i>."],
  ["oscillatio", "<big>oscillatio</big>\n\n<b>oscillatio, onis</b> <i>f</i>\nкачание, раскачивание (<i>на качелях</i>) (oscillatione ludere <i>Pt</i>)."],
  ["oscillum 1", "<big>oscillum</big>\n\n<b>I oscillum, i</b> <i>n</i> [<i>demin. к</i> os I]\n<b>1)</b> маленькое восковое изображение Вакха (<i>вешалось на ветви и раскачивалось ветром</i>) <i>V, Macr</i>;\n<b>2)</b> впадина в стручковых плодах, из которой появлялся росток <i>Col</i>."],
  ["oscillum 2", "<big>oscillum</big>\n\n<b>II oscillum, i</b> <i>n</i> [obs + cilio]\nкачели <i>Tert</i>."],
  ["oscinis", "<big>oscinis</big>\n\n<b>oscinis</b> <i>gen. sg. к</i>\noscen."],
  ["oscitabundus", "<big>oscitabundus</big>\n\n<b>oscitabundus, a, um</b> [oscito]\nзевающий, одолеваемый зевотой <i>AG, Sid</i>."],
  ["oscitanter", "<big>oscitanter</big>\n\n<b>oscitanter</b> [oscito]\nзевая, сонно, сонливо, <i>перен.</i> вяло, безучастно, равнодушно (agere <i>C</i>)."],
  ["oscitatio", "<big>oscitatio</big>\n\n<b>oscitatio, onis</b> <i>f</i> [oscito]\n<b>1)</b> раскрытие, разевание (conchae pandentes sese quadam oscitatione <i>PM</i>);\n<b>2)</b> зевота, зевание <i>CC, PM etc.</i>: longas trahere oscitatiohes <i>M</i> протяжно зевать;\n<b>3)</b> сонливость, вялость: oscitatione alicujus solvi <i>Q</i> заразиться чьей-л. сонливостью;\n<b>4)</b> скучное сочинение (oscitationes alicujus <i>St</i>)."],
  ["oscito", "<big>oscito</big>\n\n<b>oscito, —, —, are</b> [os I]\n<b>1)</b> разевать рот, <i>перен.</i> кричать (alites oscitantes <i>Ctl</i> — <i>v. l.</i>);\n<b>2)</b> раскрываться (folia arborum ad solem oscitant <i>PM</i>);\n<b>3)</b> зевать <i>Lcr, C, AG</i>;\n<b>4)</b> быть вялым, равнодушным, невнимательным, безучастным (oscitans sapientia <i>C</i>)."],
  ["oscitor", "<big>oscitor</big>\n\n<b>oscitor, —, ari</b> <i>depon.</i> <i>Pl, rhH</i>\n= oscito."],
  ["osculabundus", "<big>osculabundus</big>\n\n<b>osculabundus, a, um</b> [osculor]\nцелующий <i>Su, Ap</i>."],
  ["osculatio 1", "<big>osculatio</big>\n\n<b>I osculatio, onis</b> <i>f</i> [osculor]\nцелование, поцелуи <i>Ctl, C etc.</i>"],
  ["osculatio 2", "<big>osculatio</big>\n\n<b>II osculatio, onis</b> <i>f</i> [osculo II]\nраскрытие <i>или</i> (хирургическое) расширение (venarum <i>CA</i>)."],
  ["osculo 1", "<big>osculo</big>\n\n<b>I osculo, —, —, are</b> <i>арх. Ap, Spart, Capit</i>\n= osculor."],
  ["osculo 2", "<big>osculo</big>\n\n<b>II osculo, —, —, are</b> [os I]\nвскрывать <i>или</i> расширять (venas <i>CA</i>)."],
  ["osculor", "<big>osculor</big>\n\n<b>osculor, atus sum, ari</b> <i>depon.</i> [osculum]\n<b>1)</b> целовать (aliquem <i>Pl, C</i>; manum <i>Sen</i>): o. inter se <i>Pl</i> (cum aliquo <i>Pl</i>) целовать друг друга, целоваться;\n<b>2)</b> нежно любить (filiolam suam <i>C</i>);\n<b>3)</b> высоко ценить (scientiam juris <i>C</i>)."],
  ["osculum", "<big>osculum</big>\n\n<b>osculum, i</b> <i>n</i> [<i>demin. к</i> os I]\n<b>1)</b> <i>преим. pl.</i> ротик <i>O, Pt</i>: oscula delibare <i>V</i> целовать в губы;\n<b>2)</b> поцелуй: o. figere <i>Lcr, V</i> (jungere <i>O</i>, dare <i>Cato, Pt etc.</i>, ferre <i>Pl, AG</i>) поцеловать; suprema oscula <i>T</i> прощальные поцелуи."],
  ["Oscus", "<big>Oscus</big>\n\n<b>Oscus, a, um</b> [Osci]\nоскский (lingua <i>Vr</i>)."],
  ["Osi", "<big>Osi</big>\n\n<b>Osi, orum</b> <i>m</i>\nосы, <i>племя в области Карпат, между верхним течением рек Одры и Вислы</i> <i>T</i>."],
  ["Osiris", "<big>Osiris</big>\n\n<b>Osiris, idis</b> <i>и</i> <b>is</b> <i>m</i>\nОзирис, <i>египетский бог, муж Исиды, покровитель Египта, убитый и разрезанный на части своим братом Тифоном</i> (<i>у греков отождествлялся с Вакхом</i>) <i>Tib, H, O, J etc.</i>"],
  ["Osismi", "<big>Osismi</big>\n\n<b>Osismi, orum</b> <i>m</i>\nосисмы, <i>племя в сев.-зап. части нын. Бретани</i> <i>Cs, PM</i>."],
  ["osmen", "<big>osmen</big>\n\n<b>osmen</b> <i>арх. Vr</i>\n= omen I."],
  ["osnamentum", "<big>osnamentum</big>\n\n<b>osnamentum</b> <i>арх. Vr</i>\n= ornamentum."],
  ["osor", "<big>osor</big>\n\n<b>osor, oris</b> <i>m</i> [odi]\nненавидящий, ненавистник <i>Pl, Ap, Aus, Eccl</i>."],
  ["ospratura", "<big>ospratura</big>\n\n<b>ospratura, ae</b> <i>f</i> [<i>греч.</i> osprion]\nзакупка бобовых растений <i>Dig</i>."],
  ["ossa 1", "<big>ossa</big>\n\n<b>I ossa</b> <i>nom. pl. к</i>\nos II."],
  ["Ossa 2", "<big>Ossa</big>\n\n<b>II Ossa, ae</b> <i>f, m</i>\nOcca, <i>гора в сев.-вост. Фессалии, отделённая от Олимпа рекой Пенеем</i> (<i>долиной Темпе</i>) <i>Mela, Prp, V, O etc.</i>"],
  ["Ossaeus", "<big>Ossaeus</big>\n\n<b>Ossaeus, a, um</b> [Ossa]\nосский (ursa <i>O</i>): Ossaei bimembres <i>St</i> = Centauri."],
  ["ossarium", "<big>ossarium</big>\n\n<b>oss&#257;rium</b> <i>v. l.</i>\n= ossuarium."],
  ["osseus", "<big>osseus</big>\n\n<b>osseus, a, um</b> [os II]\n<b>1)</b> костяной (culter <i>Col</i>);\n<b>2)</b> костный: larva ossea <i>O</i> скелет;\n<b>3)</b> костлявый (manus <i>J</i>);\n<b>4)</b> твёрдый как кость (ilex <i>PM</i>)."],
  ["ossiclum", "<big>ossiclum</big>\n\n<b>ossiclum</b> <i>Aus</i>\n= ossiculum."],
  ["ossiculum", "<big>ossiculum</big>\n\n<b>ossiculum, &#299;</b> <i>n</i> [<i>demin. к</i> os II]\nкосточка <i>PM, AG</i>."],
  ["ossifraga", "<big>ossifraga</big>\n\n<b>ossifraga, ae</b> <i>f</i> <i>Lcr</i>\n= ossifragus 2."],
  ["ossifragus", "<big>ossifragus</big>\n\n<b>ossifragus, &#299;</b> <i>m</i> [os + frango]\n<b>1)</b> ломающий кости, костолом <i>Sen</i>;\n<b>2)</b> <i>разновидность кречета</i> (<i>Falco Ossifragus</i>, L.) <i>PM</i>."],
  ["ossilago", "<big>ossilago</big>\n\n<b>ossil&#257;go, inis</b> <i>f</i> [os II]\nкостная опухоль <i>Veg</i>."],
  ["ossor", "<big>ossor</big>\n\n<b>ossor, &#333;ris</b> <i>m</i> <i>Pl v. l.</i>\n= osor."],
  ["ossosus", "<big>ossosus</big>\n\n<b>oss&#333;sus</b> <i>CA</i>\n= ossuosus."],
  ["ossuarium", "<big>ossuarium</big>\n\n<b>ossu&#257;rium, &#299;</b> <i>n</i> [os II]\nоссуарий, погребальная урна <i>Dig</i>."],
  ["ossuculum", "<big>ossuculum</big>\n\n<b>ossuculum</b> <i>Pt</i>\n= ossiculum."],
  ["ossum", "<big>ossum</big>\n\n<b>ossum, &#299;</b> <i>n</i> (<i>gen. Pac</i> ossuum) <i>AG, Ap</i>\n= os II."],
  ["ossuosus", "<big>ossuosus</big>\n\n<b>ossu&#333;sus, a, um</b>\nполный костей <i>Veg</i>."],
  ["ostendo", "<big>ostendo</big>\n\n<b>ostendo, tendi, tentum</b> (<i>поздн.</i> <b>tensum</b>), <b>ere</b> [obs + tendo]\n<b>1)</b> протягивать (manus <i>Pl</i>);\n<b>2)</b> выставлять, подставлять, открывать (ager soli ostentus <i>Cato</i>); показывать (se alicui <i>C</i>; dentem medico <i>Su</i>; errantibus viam <i>Pt</i>); выводить (boves caelo <i>V</i>); вводить (iambos Latio <i>H</i>); демонстрировать (aciem ad terrorem hostium <i>L</i>): oppugnationem o. <i>L</i> производить мнимое наступление (<i>военная диверсия</i>); lacrimas fundunt, ut ostendant <i>Sen</i> (многие) льгот слёзы лишь для вида;\n<b>3)</b> издавать, подавать (Vocem <i>Ph</i>); внушать (tum spem, tum metum <i>C</i>): ostenditur spes alicujus rei <i>Ter</i> есть надежда на что-л.; ostenditur victoria <i>L</i> есть виды на победу; quaedam mihi ejus defensio ostenditur <i>C</i> мне угрожают каким-то защитительным аргументом в его (Верреса) пользу;\n<b>4)</b> проявлять, обнаруживать (virtutem <i>Sl</i>; se alicui inimicum <i>Nep</i>); выражать, высказывать (alicui sententiam suam <i>Ter</i>);\n<b>5)</b> обещать, сулить (alicui munus <i>C</i>)."],
  ["ostensio", "<big>ostensio</big>\n\n<b>ostensio, onis</b> <i>f</i> [ostendo]\nпоказывание <i>Ap, Tert, Hier</i>."],
  ["ostensionalis", "<big>ostensionalis</big>\n\n<b>ostensionalis, e</b> [ostensio]\nпредназначенный для парадов, показной (milites <i>Lampr</i>)."],
  ["ostensivus", "<big>ostensivus</big>\n\n<b>ostensivus, a, um</b>\nнаглядный (demonstratio <i>Boet</i>)."],
  ["ostensor", "<big>ostensor</big>\n\n<b>ostensor, oris</b> <i>m</i> [ostendo]\nуказчик, показывающий <i>Tert, Is</i>."],
  ["ostentaneus", "<big>ostentaneus</big>\n\n<b>ostentaneus, a, um</b> [ostento]\nпоказывающий: fulmen ostentaneum <i>Sen</i> угрожающая опасностью молния."],
  ["ostentarius", "<big>ostentarius</big>\n\n<b>ostentarius, i</b> <i>m</i>\n(<i>sc.</i> liber) книга знамений <i>или</i> примет <i>Macr</i>."],
  ["ostentaticius", "<big>ostentaticius</big>\n\n<b>ostentaticius, a, um</b>\nпоказной <i>Tert</i>."],
  ["ostentatio", "<big>ostentatio</big>\n\n<b>ostentatio, onis</b> <i>f</i> [ostento]\n<b>1)</b> показывание (aliquid armorum ostentatione compescere <i>PJ</i>); демонстрация (virium <i>T</i>): ostentationis causa <i>Cs</i> в целях демонстрации, для вида;\n<b>2)</b> похвальба, важничание (o. scientiae <i>Cs</i>);\n<b>3)</b> выказывание, проявление (saevitiae <i>L</i>);\n<b>4)</b> притворство, видимость (doloris <i>Sen</i>); veritate, non ostentatione <i>C</i> на самом деле, а не притворно;\n<b>5)</b> заверение, обещание, посул (ostentationes nunc in discrimen adductae <i>C</i>)."],
  ["ostentator", "<big>ostentator</big>\n\n<b>ostentator, oris</b> <i>m</i> [ostento]\n<b>1)</b> делающий указания, обращающий внимание (periculorum praemiorumque o. <i>T</i>);\n<b>2)</b> хвастун: o. factorum <i>L</i> хвастающийся своими подвигами."],
  ["ostentatrix", "<big>ostentatrix</big>\n\n<b>ostentatrix, icis</b> <i>f</i> [ostentator]\nхвастунья (o. alicujus rei <i>Ap</i>)."],
  ["ostento", "<big>ostento</big>\n\n<b>ostento, avi, atum, are</b> [<i>intens. к</i> ostendo]\n<b>1)</b> протягивать (manu panem <i>Pl</i>); подставлять (alicui jugula sua <i>C</i>);\n<b>2)</b> показывать (liberos suos <i>Su</i>); указывать в качестве примера (aliquem <i>Cs</i>);\n<b>3)</b> (хвастливо) выставлять напоказ, хвастаться (o. prudentiam <i>C</i>; o. cicatrices suas <i>Ter</i>): quid me ostentem? <i>C</i> к чему мне хвалиться?; ostentanda pietas <i>T</i> показное благочестие;\n<b>4)</b> предлагать, сулить, обещать (praemia <i>Sl</i>; agrum <i>C</i>): nova jura Cappadociae ostentata magis, quam mansura <i>T</i> новые права были даны Каппадокии более для вида, чем всерьёз;\n<b>5)</b> угрожать, выдвигать в виде угрозы (o. bellum <i>PJ</i>; caedem <i>C</i>);\n<b>6)</b> обнаруживать, проявлять (aequitatem <i>Su</i>; omnem disciplinam <i>Ap</i>); свидетельствовать, служить доказательством: magnitudo moenium vires veteres earum urbium ostentat <i>VP</i> величина стен указывает на древнее могущество этих городов."],
  ["ostentum", "<big>ostentum</big>\n\n<b>ostentum, i</b> <i>n</i> [ostendo]\n<b>1)</b> знамение, чудесное явление, чудо (ostenta et praedictiones <i>Su</i>): ostenta facere <i>Su</i> творить чудеса;\n<b>2)</b> чудовище (monstrum ostentumque <i>Ap</i>); изверг: Vatinius inter foedissima ostenta fuit <i>T</i> Ватиний был одним из самых гнусных чудовищ."],
  ["ostentus 1", "<big>ostentus</big>\n\n<b>I ostentus, a, um</b> <i>part. pf. к</i>\nostendo."],
  ["ostentus 2", "<big>ostentus</big>\n\n<b>II ostentus</b>, (<b>us</b>) (<i>встреч. тк.</i> <i>dat. и abl. sg.</i>) <i>m</i> [ostendo]\n<b>1)</b> показывание, выставление напоказ, демонстрирование: ostentui <i>T</i> напоказ, для вида; aliquid ostentui (<i>sc.</i> esse) credere <i>Sl</i> считать что-л. обманом;\n<b>2)</b> знак, признак, доказательство: ostentui esse alicujus rei <i>T</i> служить доказательством чего-л., обнаруживать что-л."],
  ["ostes", "<big>ostes</big>\n\n<b>ostes, ae</b> <i>m</i> (<i>греч.</i>)\n<i>вид землетрясения</i> <i>Ap</i>."],
  ["Ostia", "<big>Ostia</big>\n\n<b>Ostia, ae</b> <i>f</i> <i>и</i> <b>Ostia, orum</b> <i>n</i>\nОстия, <i>портовый город Рима в устье Тибра</i> <i>C, L, J etc.</i>"],
  ["ostiaria", "<big>ostiaria</big>\n\n<b>ostiaria, ae</b> <i>f</i> (<i>тж.</i> o. ancilla)\nпривратница <i>Vlg, Eccl</i>."],
  ["ostiarium", "<big>ostiarium</big>\n\n<b>ostiarium, i</b> <i>n</i> (<i>у</i> <i>C</i> exactio ostiorum)\nподать с дверей <i>Cs</i>."],
  ["ostiarius", "<big>ostiarius</big>\n\n<b>ostiarius, i</b> <i>m</i>\n<b>1)</b> привратник <i>Vr, Sen, Pt etc.</i>;\n<b>2)</b> ризничий <i>или</i> ключарь <i>Eccl</i>."],
  ["ostiatim", "<big>ostiatim</big>\n\n<b>ostiatim</b> [ostium]\n<b>1)</b> от дверей до дверей, из дома в дом, дом за домом (o. totum oppidum compilare <i>C</i>);\n<b>2)</b> поодиночке, порознь (singula scrutari <i>Q</i>)."],
  ["osticium", "<big>osticium</big>\n\n<b>osticium</b> <i>Is v. l.</i>\n= usticium."],
  ["Ostiensis", "<big>Ostiensis</big>\n\n<b>Ostiensis, e</b> [Ostia]\nостийский: O. provincia <i>C, Su</i> <i>обязанность одного из квесторов наблюдать за водоснабжением и доставкой хлеба в Рим</i> (<i>из Остии</i>)."],
  ["ostigo", "<big>ostigo</big>\n\n<b>ostigo, inis</b> <i>f</i> <i>Col</i>\n= mentigo."],
  ["ostiolum", "<big>ostiolum</big>\n\n<b>ostiolum, i</b> <i>n</i> [<i>demin. к</i> ostium]\nдверца, калитка <i>Col, PM, Pt, Vlg</i>."],
  ["ostium", "<big>ostium</big>\n\n<b>ostium, i</b> <i>n</i> [os I]\nвход (portus <i>C</i>); устье (fluminis <i>C</i>); дверь, ворота (o. aperire <i>Ter</i>): aperto ostio <i>C</i> при открытых дверях; o. rectum <i>Pl</i> передняя дверь; o. Oceani <i>C</i> = fretum Oceani (<i>ныне</i> Гибралтарский пролив)."],
  ["ostrea", "<big>ostrea</big>\n\n<b>ostrea, ae</b> <i>f</i> (<i>греч.</i>)\nустрица <i>LM, C etc.</i>: ostrearum vivaria <i>PM</i> = ostrearium."],
  ["ostrearium", "<big>ostrearium</big>\n\n<b>ostrearium, i</b> <i>n</i> [ostrea]\nустричная отмель (&laquo;банка&raquo;) <i>PM, Macr</i>."],
  ["ostrearius", "<big>ostrearius</big>\n\n<b>ostrearius, a, um</b> [ostrea]\nустричный: panis o. <i>PM</i> особый хлеб, с которым ели устриц."],
  ["ostreatus", "<big>ostreatus</big>\n\n<b>ostreatus, a, um</b> [ostrea]\nпокрытый раковинами устриц, <i>ирон.</i> покрытый струпьями (quasi ostreatum tergum ulceribus <i>Pl</i>)."],
  ["ostreosus", "<big>ostreosus</big>\n\n<b>ostreosus (ostriosus), a, um</b> <i>Priap, Ctl</i>\n= ostrifer."],
  ["ostreum", "<big>ostreum</big>\n\n<b>ostreum, i</b> <i>n</i> <i>LM, Vr, Sen, Pt</i>\n= ostrea."],
  ["ostria", "<big>ostria</big>\n\n<b>ostria</b> <i>Pl</i>\n= ostrea."],
  ["ostricolor", "<big>ostricolor</big>\n\n<b>ostri–color, oris</b> [ostrum]\nпурпурного цвета (pepli textus <i>Sid</i>)."],
  ["ostrifer", "<big>ostrifer</big>\n\n<b>ostrifer, fera, ferum</b> [ostrea + fero]\nобильный устрицами (Abydus <i>V</i>)."],
  ["ostrinus", "<big>ostrinus</big>\n\n<b>ostrinus, a, um</b> [ostrum]\nпурпуровый (tunica <i>Prp</i>)."],
  ["ostriosus", "<big>ostriosus</big>\n\n<b>ostriosus</b> <i>v. l.</i>\n= ostreosus."],
  ["Ostrogothi", "<big>Ostrogothi</big>\n\n<b>Ostrogothi, orum</b> <i>m</i>\nостготы <i>Cld, Sid</i>."],
  ["ostrum", "<big>ostrum</big>\n\n<b>ostrum, i</b> <i>n</i> (<i>греч.</i>)\n<b>1)</b> пурпур, добывавшийся из пурпурной улитки (багрянки) <i>Vtr, V</i>;\n<b>2)</b> пурпурная ткань (одежда, покрывало) (ostro velare umeros <i>V</i>); пурпурная попона (ostro insignis sonipes <i>V</i>)."],
  ["osurus", "<big>osurus</big>\n\n<b>osurus, a, um</b> <i>part. fut. к</i>\nodi."],
  ["osus", "<big>osus</big>\n\n<b>osus, a, um</b> <i>part. pf. к</i>\nodi."],
  ["otacustes", "<big>otacustes</big>\n\n<b>otacustes, ae</b> <i>m</i> (<i>греч.</i>)\nподслушиватель, шпион <i>Ap</i>."],
  ["Otho", "<big>Otho</big>\n\n<b>Otho, onis</b> <i>m</i>\nОтон, <i>римск. cognomen; наиболее известны</i>:\n<b>1)</b> L. Roscius O., <i>народный трибун в 67 г. до н. э.</i> <i>C</i>;\n<b>2)</b> M. Salvius O., <i>род. в 32 г. н. э., римск. император с 15 января до 16 апреля 69 г. н. э.</i> (<i>свергнутый Вителлием</i>) <i>Su</i>."],
  ["Othonianus 1", "<big>Othonianus</big>\n\n<b>I Othonianus, a, um</b> [Otho]\nотонов (partes <i>T</i>)."],
  ["Othonianus 2", "<big>Othonianus</big>\n\n<b>II Othonianus, i</b> <i>m</i>\nсолдат императора Отона <i>T</i>."],
  ["Othryades", "<big>Othryades</big>\n\n<b>Othryades, ae</b> <i>m</i>\nОтриад:\n<b>1)</b> сын Отрия, <i>т. е.</i> Panthus <i>V</i>;\n<b>2)</b> <i>спартанский военачальник, единственный уцелевший в бою с аргивянами</i> (<i>середина VI века до н. э.</i>) <i>O, VM</i>."],
  ["Othrys", "<big>Othrys</big>\n\n<b>Othrys, yos</b> <i>m</i>\nОтрий, <i>горная цепь в южн. Фессалии, местопребывание лапифов</i> <i>V, O, VF etc.</i>"],
  ["otiabundus", "<big>otiabundus</big>\n\n<b>otiabundus, a, um</b> [otior]\nнаслаждающийся досугом, свободный от дел и забот <i>Sid</i>."],
  ["oticus", "<big>oticus</big>\n\n<b>oticus</b> (<i>греч.</i>) <i>CA</i>\n= oricularius."],
  ["otiolum", "<big>otiolum</big>\n\n<b>otiolum, i</b> <i>n</i> [<i>demin. к</i> otium]\nнебольшой досуг <i>C</i>."],
  ["otior", "<big>otior</big>\n\n<b>otior, atus sum, ari</b> <i>depon.</i> [otium]\nбыть свободным от дел, отдыхать: se Syracusas otiandi causa conferre <i>C</i> отправиться в Сиракузы для отдыха; domesticus otior <i>H</i> я наслаждаюсь досугом дома."],
  ["otiose", "<big>otiose</big>\n\n<b>otiose</b> [otiosus]\n<b>1)</b> праздно, без дела (vivere <i>C</i>);\n<b>2)</b> неторопливо, медленно (inambulare in foro <i>L</i>);\n<b>3)</b> спокойно, без боязни (dormire <i>Ter</i>)."],
  ["otiositas", "<big>otiositas</big>\n\n<b>otiositas, atis</b> <i>f</i> [otiosus]\n<b>1)</b> безделье, праздность <i>Vlg, Eccl</i>;\n<b>2)</b> плод досуга (otiositates suas edere <i>Sid</i>)."],
  ["otiosse", "<big>otiosse</big>\n\n<b>otiosse</b> <i>Pl v. l.</i>\n= otiose."],
  ["otiosus", "<big>otiosus</big>\n\n<b>otiosus, a, um</b> [otium]\n<b>1)</b> свободный от дел, незанятый (tempus <i>C</i>); располагающий досугом, досужий (o. eram domi <i>C</i>); незамещённый (honor <i>PJ</i>);\n<b>2)</b> находящийся не у дел, не занимающий государственного поста: P. Scipio numquam minus o. erat, quam quum o. <i>C</i> у П. Сципиона никогда не было так мало досуга, как в то время, когда он был не у дел;\n<b>3)</b> не принимающий участия, равнодушный, безучастный, нейтральный (spectator pugnae <i>C</i>);\n<b>4)</b> спокойный, бесстрастный (dignitas <i>C</i>; aliquem otiosum reddere <i>C</i>);\n<b>5)</b> лежащий без дела, не приносящий дохода (pecuniae <i>PJ</i>);\n<b>6)</b> излишний, бесполезный, пустой, праздный (sermo <i>Q</i>; occupatio <i>PJ</i>);\n<b>7)</b> Праздный, бездеятельный: mihi fuit ne otium quidem umquam otiosum <i>C</i> у меня и самый досуг никогда не был праздным;\n<b>8)</b> мирный, безмятежный (cursus vitae <i>C</i>);\n<b>9)</b> свободный (от), лишённый (o. a metu <i>AG</i>): o. ab animo <i>Ter</i> свободный от умственных дел (забот); spatium ab hoste otiosum <i>Cs</i> время, в течение которого неприятель не тревожил;\n<b>10)</b> неторопливый, медлительный (rivus <i>Pall</i>): Cicero est o. circa excessus <i>T</i> Цицерон задерживается (затягивает свои речи) отступлениями."],
  ["otis", "<big>otis</big>\n\n<b>otis, idis</b> <i>f</i>\nдрофа (<i>Otis arabs</i>, L.) <i>PM</i>."],
  ["otium", "<big>otium</big>\n\n<b>otium, i</b> <i>n</i> (<i>тж.</i> <i>pl.</i>)\n<b>1)</b> свободное время, досуг (deus nobis haec otia fecit <i>V</i>); in otio de negotiis cogitare <i>C</i> в свободное время думать о делах; otio <i>Ph</i> <i>и</i> in otio <i>C etc.</i> на досуге, <i>тж.</i> неторопливо, спокойно; o. litteratum <i>C</i> досуг, посвященный литературным занятиям;\n<b>2)</b> бездеятельность, праздность, безделье (languescere in otio <i>C</i>; o. beatas perdidit urbes <i>Ctl</i>);\n<b>3)</b> отдых, покой (se ad o. pacemque convertere <i>C</i>);\n<b>4)</b> мир: per o. <i>L</i> в спокойный момент, <i>но тж.</i> <i>Sl</i> из-за бездеятельности <i>и</i> <i>Sl</i> во время перемирия; res ad o. deducere <i>Cs</i> мирно уладить дело;\n<b>5)</b> учёные занятия на досуге (requies atque o. <i>C</i>);\n<b>6)</b> плод досуга, написанные на досуге произведения (otia mea <i>O</i>)."],
  ["otopeta", "<big>otopeta</big>\n\n<b>otopeta, ae</b> <i>m</i> (<i>греч.</i>)\nдлинноухий (<i>о зайце</i>) <i>Pt</i> (<i>v. l.</i>)."],
  ["ovalis", "<big>ovalis</big>\n\n<b>ovalis, e</b> [ovo]\nовационный, относящийся к овации: corona o. <i>AG</i> (миртовый) венок, возлагавшийся на голову полководца при овации."],
  ["ovanter", "<big>ovanter</big>\n\n<b>ovanter</b> [ovo]\nликующе (accurrere <i>Tert</i>)."],
  ["ovatio", "<big>ovatio</big>\n\n<b>ovatio, onis</b> <i>f</i> [ovo]\nовация, малый триумф (<i>торжественное вступление полководца-победителя в Рим верхом или пешком — не на колеснице, в отличие от триумфатора</i>) <i>PM, AG, Fl</i>."],
  ["ovatus 1", "<big>ovatus</big>\n\n<b>I ovatus, a, um</b> [ovum]\n<b>1)</b> овальный, яйцевидный (species <i>PM</i>);\n<b>2)</b> покрытый овальными пятнышками (lapis <i>PM</i>)."],
  ["ovatus 2", "<big>ovatus</big>\n\n<b>II ovatus, us</b> <i>m</i> [ovo]\nликование, торжество <i>VF</i>."],
  ["ovatus 3", "<big>ovatus</big>\n\n<b>III ovatus, a, um</b> <i>part. pf. к</i>\novo."],
  ["oviaria", "<big>oviaria</big>\n\n<b>oviaria, ae</b> <i>f</i> [ovis I]\nстадо овец, отара <i>Vr</i>."],
  ["oviarius", "<big>oviarius</big>\n\n<b>oviarius, a, um</b> [ovis I]\nовечий (pecus <i>Col</i>)."],
  ["ovicula", "<big>ovicula</big>\n\n<b>ovicula, ae</b> <i>f</i> [<i>demin. к</i> ovis I]\nовечка <i>AV, Eccl</i>."],
  ["Ovidianus", "<big>Ovidianus</big>\n\n<b>Ovidianus, a, um</b> [Ovidius]\nовидиев <i>Sen</i>."],
  ["Ovidius", "<big>Ovidius</big>\n\n<b>Ovidius, a, um</b>\n<i>римск. nomen; наиболее известен</i> P. O. Naso, <i>поэт, род. в 43 г. до н. э. в Сульмоне, сосланный Августом в 8 г. н. э. в город Томы на Чёрном море, где он умер в 17 г. н. э.; автор &laquo;Metamorphoses&raquo;, &laquo;Heroides&raquo;, &laquo;Tristia&raquo;, &laquo;Amores&raquo;, &laquo;Ars amatoria&raquo;, &laquo;Fasti&raquo, &laquo;Pontica&raquo; etc.</i>"],
  ["ovile", "<big>ovile</big>\n\n<b>ovile, is</b> <i>n</i> [ovilis]\n<b>1)</b> (<i>sc.</i> stabulum) овечий хлев, овчарня <i>Vr, V, Col etc.</i>; <i>тж.</i> хлев для коз;\n<b>2)</b> огороженное место на Марсовом поле, где происходило голосование комициев <i>L</i>."],
  ["ovilio", "<big>ovilio</big>\n\n<b>ovilio, onis</b> <i>m</i>\nовчар <i>Dig</i>."],
  ["ovilis", "<big>ovilis</big>\n\n<b>ovilis, e</b> [ovis I]\nовечий (stabulatio <i>Ap</i>; grex <i>Dig</i>)."],
  ["ovilla", "<big>ovilla</big>\n\n<b>ovilla, ae</b> <i>f</i> (<i>sc.</i> caro)\nбаранина <i>Pt</i>."],
  ["ovillus", "<big>ovillus</big>\n\n<b>ovillus, a, um</b> [<i>demin. к</i> ovis I]\nовечий (pecus <i>Vr</i>, lac <i>PM</i>)."],
  ["ovinus", "<big>ovinus</big>\n\n<b>ovinus, a, um</b> <i>Aug</i>\n= ovillus."],
  ["oviparus", "<big>oviparus</big>\n\n<b>oviparus, a, um</b> [ovis + pario I]\nяйцекладущий: vivipari et ovipari <i>Ap</i> живородящие и яйцекладущие (животные)."],
  ["ovis 1", "<big>ovis</big>\n\n<b>I ovis, is</b> <i>f</i>\n<b>1)</b> овца: o. mas <i>Vr</i> баран; o. semimas <i>O</i> холощёный баран; ovem lupo committere <i>погов. Ter</i> поручить (доверить) волку овцу;\n<b>2)</b> <i>бран.</i> баран, дурень <i>Pl</i>;\n<b>3)</b> <i>поэт.</i> шерсть: murice tingere ovem <i>Tib, O</i> красить шерсть в пурпурный цвет."],
  ["ovis 2", "<big>ovis</big>\n\n<b>II ovis</b> <i>dat. abl. pl. к</i>\novum."],
  ["ovo", "<big>ovo</big>\n\n<b>ovo, —, atum, are</b> [<i>одного корня с греч.</i> euoe]\n<b>1)</b> торжествовать, ликовать (cum ovante gaudio <i>V</i>): o. victoria <i>L</i> торжествовать по случаю победы;\n<b>2)</b> быть встречаемым овацией, совершать триумфальное вступление в столицу пешком или верхом (ovans urbem ingressus est <i>PM</i>): ovantes currus <i>Prp</i> триумфальные колесницы; ovatum aurum <i>Pers</i> добытое на войне и показываемое во время овации золото;\n<b>3)</b> кричать, галдеть (ovantes corvi <i>V</i>); шуметь, выть (Africus ovat <i>VF</i>)."],
  ["ovum", "<big>ovum</big>\n\n<b>ovum, i</b> <i>n</i>\n<b>1)</b> яйцо: o. parere <i>C</i> (gignere <i>C</i>, ponere <i>Col</i>, facere <i>Vr</i>, edere <i>PM</i>) снести яйцо; ab ovo usque ad mala <i>погов. H</i> от яйца до фруктов (<i>обед начинался обыкновенно с яиц, а заканчивался фруктами</i>), <i>т. е.</i> от начала до конца; integram famem ad o. afferre <i>C</i> не испортить аппетита к обеду; geminum o. <i>H</i> оба яйца (<i>из которых родились дети Юпитера и Леды: из одного — Кастор и Поллукс, из другого — Клитемнестра и Елена</i>);\n<b>2)</b> каждая из семи яйцеобразных фигур, поочерёдным сниманием которых отмечался семикратный объезд круга на цирковых состязаниях (ova ad notas curriculis numerandis <i>L</i>);\n<b>3)</b> ёмкость яичной скорлупы <i>PM</i>;\n<b>4)</b> овальная форма (<i>sc.</i> arenae <i>Calp</i>)."],
  ["Oxus", "<big>Oxus</big>\n\n<b>Oxus (-os), i</b> <i>m</i>\nОке, <i>река в Средней Азии</i> (<i>ныне</i> Аму-Дарья) <i>Mela, PM, QC</i>."],
  ["oxycominum", "<big>oxycominum</big>\n\n<b>oxycominum, i</b> <i>n</i> (<i>греч.</i>)\nмаринованная маслина с тмином <i>Pt</i>."],
  ["oxygala", "<big>oxygala</big>\n\n<b>oxygala, ae</b> <i>f</i> (<i>или</i> <i>n indecl.</i>)\nкислое молоко, простокваша, <i>тж.</i> творог <i>Col, PM</i>."],
  ["oxygarum", "<big>oxygarum</big>\n\n<b>oxygarum, i</b> <i>n</i> (<i>греч.</i>)\nрыбный соус с уксусом <i>M</i>."],
  ["oxymoron", "<big>oxymoron</big>\n\n<b>oxymoron, i</b> <i>n</i> <i>ритор.</i>\nоксиморон, бессмысленное (на первый взгляд!) сочетание несовместимых понятий, <i>напр.</i>: insaniens sapientia <i>H</i>; festina lente <i>Su</i>; mitis austeritas <i>Ap</i>; mortalium rerum misera beatitudo <i>Boet</i>; otiosissimae occupationes <i>PJ</i>."],
  ["oxypaederotinus", "<big>oxypaederotinus</big>\n\n<b>oxypaederotinus, a, um</b> (<i>греч.</i>)\nопаловый, цвета опала <i>Vop</i>."],
  ["oxyporus", "<big>oxyporus</big>\n\n<b>oxyporus, a, um</b> (<i>греч.</i>)\n<b>1)</b> весьма удобоваримый, <i>по др.</i> острый на вкус (<i>о пище</i>) <i>Col</i>;\n<b>2)</b> быстро действующий (<i>о лекарствах</i>) <i>PM</i>."]
]