export default [
  ["P", "<big>P</big>\n\n<b>P, p</b>\n<i>шестнадцатая буква латинского алфавита; в сокращениях</i>: P. = Publius (<i>praenomen</i>); P. C. = patres conscripti; P. M. = pontifex maximus; P. Vili = pedum octo; P. R. = populus Romanus; P. S. = plurimam salutem <i>или</i> pecunia sua."],
  ["pabillus", "<big>pabillus</big>\n\n<b>pabillus, i</b> <i>m</i>\nтачка <i>Lampr</i>."],
  ["pabularis", "<big>pabularis</big>\n\n<b>pabularis, e</b> [pabulum]\nкормовой (vicia <i>Col, Pall</i>)."],
  ["pabulatio", "<big>pabulatio</big>\n\n<b>pabulatio, onis</b> <i>f</i> [pabulor]\n<b>1)</b> пастьба <i>Vr, Col</i>;\n<b>2)</b> <i>воен.</i> заготовка кормов, фуражировка <i>Cs</i>."],
  ["pabulator", "<big>pabulator</big>\n\n<b>pabulator, oris</b> <i>m</i> [pabulor]\nфуражир <i>Cs, L</i>."],
  ["pabulatorius", "<big>pabulatorius</big>\n\n<b>pabulatorius, a, um</b> [pabulor]\nпредназначенный для фуража, фуражный (corbis <i>Col</i>)."],
  ["pabulor", "<big>pabulor</big>\n\n<b>pabulor, atus sum, ari</b> <i>depon.</i> [pabulum]\n<b>1)</b> искать себе пропитания (ad mare prodire pabulatum <i>Pl</i>);\n<b>2)</b> пастись на подножном корму <i>или</i> поедать фураж (capella pabulatur <i>Col</i>);\n<b>3)</b> заготовлять фураж (pabulandi causa tres legiones mittere <i>Cs</i>);\n<b>4)</b> подкармливать, унавоживать (fimo pabulandae sunt oleae <i>Col</i>)."],
  ["pabulosus", "<big>pabulosus</big>\n\n<b>pabulosus, a, um</b> [pabulum]\nбогатый пастбищами (Hibernia <i>Sol</i>)."],
  ["pabulum", "<big>pabulum</big>\n\n<b>pabulum, i</b> <i>n</i> [pasco]\n<b>1)</b> пища, питание, корм (pabula carpit ovis <i>O</i>): pabula caelestia <i>O</i> = ambrosia;\n<b>2)</b> пастьба (cervi noctu procedunt ad pabula <i>PM</i>);\n<b>3)</b> <i>воен.</i> фураж (convehere p. <i>Cs</i>);\n<b>4)</b> <i>перен.</i> пища, материал, способствующий развитию <i>или</i> укреплению (p. morbi <i>O</i>; amoris <i>Lcr</i>; doctrinae <i>C</i>): p. Acheruntis <i>Pl бран.</i> пища ада, <i>т. е.</i> человек, заслуживающий казни."],
  ["pacalis", "<big>pacalis</big>\n\n<b>pacalis, e</b> [pax]\nкасающийся мира, мирный (oliva <i>O</i>): flammae pacales <i>O</i> огни на алтаре богини мира."],
  ["pacate", "<big>pacate</big>\n\n<b>pacate</b> [pacatus]\nмирно, тихо, спокойно (pacatius ad reliqua secedere <i>Pt</i>)."],
  ["pacatio", "<big>pacatio</big>\n\n<b>pacatio, onis</b> <i>f</i> <i>Frontin</i>\n= pacificatio."],
  ["pacator", "<big>pacator</big>\n\n<b>pacator, oris</b> <i>m</i> [paco] <i>Sen, Sil, Cld</i>\n= pacificator."],
  ["pacatorius", "<big>pacatorius</big>\n\n<b>pacatorius, a, um</b> <i>Ter</i>\n= pacificatorius."],
  ["pacatum", "<big>pacatum</big>\n\n<b>pacatum, i</b> <i>n</i> [pacatus] <i>преим. pl.</i>\nмирная страна (inter pacata et hostilia <i>Sen</i>)."],
  ["pacatus", "<big>pacatus</big>\n\n<b>pacatus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> paco;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> мирный, миролюбивый (gentes <i>C</i>); живущий в мире (civitas <i>C</i>; orbis <i>V</i>); спокойный (mare <i>H, O</i>); дружественный (domus <i>L</i>);\n<b>2)</b> уравновешенный, безмятежный (animus compositus et p. <i>Sen</i>)."],
  ["Pachynos", "<big>Pachynos</big>\n\n<b>Pachynos (-us), i</b> <i>f</i> <i>и</i> <b>Pachynum, i</b> <i>n</i>\nПахин, <i>мыс в юго-вост. Сицилии</i> (<i>ныне</i> Capo di Passaro) <i>C, L, PM, O</i>."],
  ["Pacidejanus", "<big>Pacidejanus</big>\n\n<b>Pacidejanus, i</b> <i>m</i>\nПацидейан, <i>известный римск. гладиатор эпохи Гракхов</i> <i>LM, C, H</i>."],
  ["pacifer", "<big>pacifer</big>\n\n<b>paci–fer, fera, ferum</b> [pax + fero]\nнесущий мир (oliva <i>V</i>; virga Mercurii <i>VF</i>; sermo <i>Lcn</i>)."],
  ["pacificatio", "<big>pacificatio</big>\n\n<b>pacificatio, onis</b> <i>f</i> [pacifico]\nумиротворение, заключение мира, установление мирных отношений <i>C, AG</i>."],
  ["pacificator", "<big>pacificator</big>\n\n<b>pacificator, oris</b> <i>m</i> [pacifico]\n<b>1)</b> умиротворитель, примиритель, миротворец <i>C</i>;\n<b>2)</b> усмиритель (Allobrogum <i>C</i>)."],
  ["pacificatorius", "<big>pacificatorius</big>\n\n<b>pacificatorius, a, um</b> [pacificator]\nсвязанный с заключением мира, ведущий мирные переговоры, мирный (legatio <i>C</i>)."],
  ["pacifice", "<big>pacifice</big>\n\n<b>pacifice</b>\nмирно, с миром <i>Vlg, Eccl</i>."],
  ["pacifico", "<big>pacifico</big>\n\n<b>pacifico, avi, —, are</b> [pax + facio]\n<b>1)</b> мириться, заключать мир (cum aliquo <i>Pl</i>): ad pacificandum <i>или</i> pacificatum <i>L</i> для заключения мира;\n<b>2)</b> успокаивать, смягчать (mentem suam <i>SenT</i>); примирять, умилостивлять (caelestes eros <i>Ctl</i>)."],
  ["pacificor", "<big>pacificor</big>\n\n<b>pacificor, atus sum, ari</b> <i>depon.</i> <i>Pl, Just</i>\n= pacifico."],
  ["pacificus", "<big>pacificus</big>\n\n<b>pacificus, a, um</b> [pax + facio]\nмиротворческий, водворяющий мир, примиряющий (persona <i>C</i>; Janus <i>M</i>); мирный (mores <i>Cld</i>)."],
  ["pacis", "<big>pacis</big>\n\n<b>pacis</b> <i>gen. к</i>\npax."],
  ["pacisco", "<big>pacisco</big>\n\n<b>pacisco, —, —, ere</b> <i>Naev, Pl</i>\n= paciscor."],
  ["paciscor", "<big>paciscor</big>\n\n<b>paciscor, pactus sum, pacisci</b> <i>depon.</i> [<i>inchoat. к</i> pango]\n<b>1)</b> договариваться, уславливаться, приходить к соглашению, заключать договор (cum aliquo <i>Pl, C etc.</i>; inter se <i>Just</i>);\n<b>2)</b> обусловливать, предусматривать договором (aliquid cum aliquo <i>bAl</i>; provinciam sibi <i>C</i>); ut pactus <i>Ap</i> согласно условиям договора || выговаривать себе (aliquid ab aliquo <i>Sl</i>); заключать (foedus <i>C</i>; pacem <i>L</i>);\n<b>3)</b> обручаться (p. aliquam <i>L</i>);\n<b>4)</b> отдавать, жертвовать (vitam pro laude <i>V</i>); принимать в обмен (letum pro laude <i>V</i>)."],
  ["paco", "<big>paco</big>\n\n<b>I paco, avi, atum, are</b> [pax]\n<b>1)</b> делать мирным, успокаивать (Galliam <i>Cs</i>); усмирять (Hispanias <i>Cs</i>; Erymanthi nemora <i>V</i>);\n<b>2)</b> укрощать (feras <i>Cld</i>);\n<b>3)</b> успокаивать, унимать (animi aestus <i>Cld</i>);\n<b>4)</b> делать пригодным для земледелия; p. silvas vomere <i>H</i> распахивать леса.\n\n<b>II paco, —, —, ere</b> <i>арх. LXIIT, AG</i>\n= pango."],
  ["Paconius", "<big>Paconius</big>\n\n<b>Paconius, a, um</b>\nПаконий, <i>римск. nomen</i> <i>C, Su, T</i>."],
  ["Pacorus", "<big>Pacorus</big>\n\n<b>Pacorus, i</b> <i>m</i>\nПакор, <i>имя нескольких парфянских царей; наиболее известны</i>:\n<b>1)</b> P. I, <i>сын Орода I, завоевавший всю Сирию (в 40 г. до н. э.), но потерпевший в 39-38 гг. до н. э. ряд поражений от Вентидия Васса и погибший в этой борьбе</i> <i>C, Just</i>;\n<b>2)</b> P. II, <i>современник Домициана</i> <i>PJ, M</i>."],
  ["pacta", "<big>pacta</big>\n\n<b>pacta, ae</b> <i>f</i> [paciscor]\nобручённая, невеста <i>V, VP, J</i>."],
  ["pacticius", "<big>pacticius</big>\n\n<b>pacticius, a, um</b> [paciscor]\nпредусмотренный договором, обусловленный (cessatio pugnae <i>AG</i>)."],
  ["pactilis", "<big>pactilis</big>\n\n<b>pactilis, e</b> [pango]\nсплетённый, свитый (corona <i>PM</i>)."],
  ["pactio", "<big>pactio</big>\n\n<b>pactio, onis</b> <i>f</i> [paciscor]\n<b>1)</b> договор, соглашение, условие: victoria pax, non pactione parienda est <i>C</i> мир должен быть добыт победой, а не соглашением; pactionem facere <i>C</i> (inire <i>Just</i>, adire <i>Nep</i>) заключить договор, войти в соглашение; nulla pactione <i>Sen</i> никоим образом, никак; arma per pactionem tradere <i>L</i> сдаться на капитуляцию;\n<b>2)</b> <i>pl.</i> условия соглашения (talibus pactionibus pacem facere <i>Nep</i>);\n<b>3)</b> сделка, тайный сговор (pactionis suspicio <i>C</i>);\n<b>4)</b> контракт между генеральным откупщиком и населением провинции (pactiones conficere <i>C</i>);\n<b>5)</b> перемирие (aut pax aut p. <i>Fl</i>);\n<b>6)</b> условная формула: p. verborum, quibus jus jurandum comprehenditur <i>C</i> формула присяги."],
  ["Pactolis", "<big>Pactolis</big>\n\n<b>Pactolis, idis</b> <i>adj. f</i> [Pactolus]\nпактольская (nympha <i>O</i>)."],
  ["Pactolus", "<big>Pactolus</big>\n\n<b>Pactolus (-os), i</b> <i>m</i>\nПактол, <i>золотоносная река</i> (aurifer amnis <i>Tib</i>) <i>в Лидии, близ Сард, приток Герма</i> (<i>ныне</i> Сарабат) <i>V, Prp, O, H etc.</i>"],
  ["pactor", "<big>pactor</big>\n\n<b>pactor, oris</b> <i>m</i> [paciscor]\nпосредник, организатор (societatis <i>C</i>)."],
  ["pactum", "<big>pactum</big>\n\n<b>pactum, i</b> <i>n</i> [paciscor]\nсоглашение, договор, условие (pacta observanda sunt <i>rhH</i>; pacta conventaque <i>Sen</i>; tacita p. <i>Dig</i>); manere in pacto <i>C</i> соблюдать условия договора; sub pacto abolitionis <i>Q</i> под условием забвения (прошлого); nullo pacto <i>C</i> никак, никоим образом; aliquo pacto <i>Ter</i> тем или иным способом, так или иначе; alio pacto <i>C</i> иначе; hoc pacto <i>V</i> следующим (этим) образом; vivitur hoc pacto <i>Pers</i> такова жизнь; nescio quo pacto <i>C</i> каким-то образом."],
  ["pactus", "<big>pactus</big>\n\n<b>I pactus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> paciscor;\n<b>2.</b> <i>adj.</i> обусловленный соглашением (pretium <i>C</i>); обещанный (praemium <i>C</i>): pacto inter se (<i>abl. abs.</i>) <i>L</i> взаимно условившись.\n\n<b>II pactus, a, um</b> <i>part. pf. к</i>\npango.\n\n<b>III pactus, i</b> <i>m</i> [paciscor]\nобручённый, жених <i>St</i>."],
  ["Pactye", "<big>Pactye</big>\n\n<b>Pactye, es</b> <i>и</i> <b>Pactya, ae</b> <i>f</i>\nПактия, <i>город на Пропонтиде, в сев.-вост. части Херсонеса Фракийского</i> <i>Nep, PM</i>."],
  ["Pacuvianus", "<big>Pacuvianus</big>\n\n<b>Pacuvianus, a, um</b> [Pacuvius]\nпакувиев <i>Ter, C, AG</i>."],
  ["Pacuvius", "<big>Pacuvius</big>\n\n<b>Pacuvius, i</b> <i>m</i>\nПакувий, <i>оскск. имя; наиболее известны</i>:\n<b>1)</b> P. Calavius, <i>знатный капуанец времён II Пунической войны</i> <i>L</i>;\n<b>2)</b> M. P., <i>родом из Брундизия, племянник Энния, автор трагедий</i> (<i>ок. 219-132 гг. до н. э.</i>) <i>C, Q, H</i>."],
  ["Padaei", "<big>Padaei</big>\n\n<b>Padaei, orum</b> <i>m</i>\nпадей, <i>кочевое племя в сев -зап. Индии</i> <i>Tib</i>."],
  ["Padaneus", "<big>Padaneus</big>\n\n<b>Padaneus</b> <i>и</i> <b>Padanus, a, um</b> [Padus]\nпаданский <i>Sol, Sid</i>."],
  ["Padua", "<big>Padua</big>\n\n<b>Padua, ae</b> <i>f</i>\nПадуя, <i>один из рукавов в устье реки Padus</i> <i>Ctl</i>."],
  ["Padus", "<big>Padus</big>\n\n<b>Padus, i</b> <i>m</i>\nПад, <i>главн. река в сев. Италии</i> (<i>ныне</i> По) <i>L, V, O</i>."],
  ["Padusa", "<big>Padusa</big>\n\n<b>Padusa, ae</b> <i>f</i>\nПадуза, <i>самый южный рукав реки Пад, недалеко от Равенны</i> (<i>ныне канал</i> St. Alberti) <i>V, PM</i>."],
  ["paean", "<big>paean</big>\n\n<b>I paeān, ānis</b> (<i>acc.</i> ana) <i>m</i> (<i>греч.</i>)\n<b>1)</b> пеан, хвалебный гимн божеству (<i>преим.</i> Аполлону) <i>C, V, Prp</i>;\n<b>2)</b> торжествующая победная песнь, ликующий напев (dicite «io paean» <i>O</i>);\n<b>3)</b> пеон <i>или</i> пеан, <i>стихотворная стопа из трех кратких слогов и одного долгого в разных сочетаниях</i>: –UUU; U–UU; UU–U; UUU– <i>C</i>.\n\n<b>II Paean, anis</b> <i>m</i> (<i>греч.</i> «врачеватель», «целитель»)\nПэан, <i>первоначально — бог-целитель, впоследствии — эпитет Аполлона и Эскулапия как богов врачевания</i> <i>C, O</i>."],
  ["Paeant-", "<big>Paeant-</big>\n\n<b>Paeant-</b> <i>v. l.</i>\n= Poeant-."],
  ["Paeas", "<big>Paeas</big>\n\n<b>Paeas</b> <i>v. l.</i>\n= Poeas."],
  ["paedagoga", "<big>paedagoga</big>\n\n<b>paedagoga, ae</b> <i>f</i> (<i>греч.</i>)\nнаставница <i>Hier</i>."],
  ["paedagogatus", "<big>paedagogatus</big>\n\n<b>paedagogatus, us</b> <i>m</i> (<i>греч.</i>)\nвоспитание, обучение <i>Tert</i>."],
  ["paedagogianus", "<big>paedagogianus</big>\n\n<b>paedagogianus, a, um</b> [paedagogium]\nполучающий <i>или</i> получивший образование в педагогии <i>Amm, CTh</i>."],
  ["paedagogium", "<big>paedagogium</big>\n\n<b>paedagogium, i</b> <i>n</i> (<i>греч.</i>)\n<b>1)</b> педагогии, училище, школа (<i>для сыновей некоторых рабов</i>) <i>PJ</i>;\n<b>2)</b> воспитанники педагогия <i>PM, Sen</i>."],
  ["paedagogo", "<big>paedagogo</big>\n\n<b>paedagogo, —, —, are</b>\nбыть наставником, воспитывать <i>Pac</i>."],
  ["paedagogus", "<big>paedagogus</big>\n\n<b>paedagogus, i</b> <i>m</i> (<i>греч.</i>)\n<b>1)</b> слуга, приставленный к ребёнку для надзора и сопровождения («дядька») <i>Pl, C, Sen</i>;\n<b>2)</b> руководитель, учитель, наставник, воспитатель <i>Pl, Sen</i>: paedagogi vultus <i>Su</i> (недовольная) мина наставника;\n<b>3)</b> <i>шутл.</i> ухаживатель, провожающий свою возлюбленную в школу и обратно <i>Ter</i>."],
  ["paederos", "<big>paederos</big>\n\n<b>paederos, otis</b> (<i>acc.</i> ota; <i>acc. pl.</i> otas) <i>m</i> (<i>греч.</i>)\n<b>1)</b> опал <i>PM</i>;\n<b>2)</b> разновидность аметиста <i>PM</i>;\n<b>3)</b> <i>бот.</i> акант <i>PM</i>."],
  ["paedicator", "<big>paedicator</big>\n\n<b>paedicator, oris</b> <i>m</i>\n= <i>nomen agentis к</i> paedico I <i>Su</i>."],
  ["paedico", "<big>paedico</big>\n\n<b>I paedico, —, —, are</b> (<i>греч.</i>)\npuerum inire (p. aliquem <i>Ctl, M</i>).\n\n<b>II paedico, onis</b> <i>m</i> <i>M</i>\n= paedicator."],
  ["paedidus", "<big>paedidus</big>\n\n<b>paedidus, a, um</b> [paedor]\nгрязный, неопрятный <i>Pt</i> (<i>v. l.</i>)."],
  ["paedor", "<big>paedor</big>\n\n<b>paedor (pedor), oris</b> <i>m</i>\n<b>1)</b> грязь (barba paedore horrida <i>Poeta ap. C</i>);\n<b>2)</b> дурной запах, зловоние <i>Aug, CA</i>."],
  ["paegniarius", "<big>paegniarius</big>\n\n<b>paegniarius, i</b> <i>m</i> (<i>греч.</i>)\nучастник гладиаторской игры, исполнитель театрализованной вооружённой борьбы <i>Su</i>."],
  ["paelex", "<big>paelex</big>\n\n<b>paelex</b> <i>v. l.</i>\n= pelex."],
  ["Paeligni", "<big>Paeligni</big>\n\n<b>Paeligni, orum</b> <i>m</i>\nпелигны, <i>горное сабинское племя в Апеннинах</i> (<i>с городами Корфиний и Сульмон</i>) <i>Cato, C, Cs, L</i>."],
  ["Paelignus", "<big>Paelignus</big>\n\n<b>Paelignus, a, um</b> [Paeligni]\nпелигнский (miles <i>O</i>); <i>перен.</i> чародейный (anus <i>H</i>)."],
  ["Paemani", "<big>Paemani</big>\n\n<b>Paemani, orum</b> <i>m</i>\nпеманы, <i>бельгийское племя близ нын. Льежа</i> <i>Cs</i>."],
  ["paemin-", "<big>paemin-</big>\n\n<b>paemin-</b> <i>v. l.</i>\n= pemin-."],
  ["paene", "<big>paene</big>\n\n<b>paene</b> <i>adv.</i>\n<b>1)</b> (<i>intens.</i> p. vixdum) почти, чуть (p. manu tangere aliquid <i>O</i>): quem poenitet pecasse, p. est innocens <i>SenT</i> кто раскаивается в (своём) прегрешении, тот почти (уж) невиновен; p. dicam <i>C</i> я сказал бы (даже) || чуть (ли) не (p. crura mea fregi <i>Pt</i>): p. in singulis domibus factiones sunt <i>Cs</i> (у галлов) политические группировки существуют чуть ли не в отдельных домах (родах);\n<b>2)</b> (<i>тж.</i> <i>superl.</i> paenissime) чуть ли не вполне, совершенно (non p. sum deceptus <i>C</i>): Philemonis exemplo paenissime <i>Pt</i> почти совсем так, как это случилось с (поэтом) Филемоном."],
  ["paeneinsula", "<big>paeneinsula</big>\n\n<b>paeneinsula, ae</b> <i>f</i> <i>Ctl, L</i>\n(<i>v. l.</i>)= paeninsula."],
  ["paeninsula", "<big>paeninsula</big>\n\n<b>paen–insula, ae</b> <i>f</i>\nполуостров <i>Ctl, L, PM</i>."],
  ["paenissime", "<big>paenissime</big>\n\n<b>paenissime</b> <i>superl. к</i>\npaene."],
  ["paenit-", "<big>paenit-</big>\n\n<b>paenit-</b> <i>v. l.</i>\n= poenit-."],
  ["paenula", "<big>paenula</big>\n\n<b>paenula, ae</b> <i>f</i> (<i>греч.</i>)\n<b>1)</b> пенула, верхнее дорожное платье, тёплый плащ с капюшоном: paenulam scindere alicui <i>погов. C</i> — упрашивать гостя остаться;\n<b>2)</b> покров, покрывало (ne p. desit olivis <i>M</i>);\n<b>3)</b> навес (super catinum <i>Vtr</i>)."],
  ["paenulatus", "<big>paenulatus</big>\n\n<b>paenulatus, a, um</b> [paenula]\nодетый в дорожный плащ (пенулу) <i>C, Sen, M etc.</i>"],
  ["paenultima", "<big>paenultima</big>\n\n<b>paen–ultima, ae</b> <i>f</i> (<i>sc.</i> syllaba)\nпредпоследний слог <i>AG, Aug</i>."],
  ["paenuria", "<big>paenuria</big>\n\n<b>paenuria, ae</b> <i>f</i> <i>v. l.</i>\n= penuria."],
  ["paeon", "<big>paeon</big>\n\n<b>I paeon, onis</b> <i>m</i> <i>C, Q</i>\n= paean I, 3.\n\n<b>II Paeon, onis</b> <i>m</i> <i>v. l.</i>\n= Paean.\n\n<b>III Paeon, onis</b> <i>m</i>\nжитель области Paeonia <i>L, O</i>."],
  ["paeonia", "<big>paeonia</big>\n\n<b>I paeonia, ae</b> <i>f</i> (<i>греч.</i>) <i>бот.</i>\nпион <i>PM, Is</i>.\n\n<b>II Paeonia, ae</b> <i>f</i>\nПеония, <i>область в сев. Македонии вокруг Аксия</i> <i>L, PM</i>."],
  ["Paeonis", "<big>Paeonis</big>\n\n<b>Paeonis, idis</b> <i>f</i> [Paeon III]\nжительница Пеонии <i>O</i>."],
  ["Paeonius", "<big>Paeonius</big>\n\n<b>I Paeonius, a, um</b> [Paeon II]\n<b>1)</b> аполлоновский (ope Paeonia <i>O</i>);\n<b>2)</b> врачебный (mos <i>V</i>); целительный (herba <i>V</i>).\n\n<b>II Paeonius, a, um</b> [Paeonia II]\nпеонийский (gentes <i>PM</i>)."],
  ["Paestanus", "<big>Paestanus</big>\n\n<b>I Paestanus, a, um</b> [Paestum]\nпест(ум)ский (sinus <i>C</i>; rosae <i>O</i>).\n\n<b>II Paestanus, i</b> <i>m</i>\nжитель города Paestum <i>L</i>."],
  ["Paestum", "<big>Paestum</big>\n\n<b>Paestum, i</b> <i>n</i>\nПест(ум), <i>город на зап. побережье Лукании</i> (<i>прежде Posidonia, ныне</i> Pesti) <i>C, Prp, V, O etc.</i>"],
  ["paetulus", "<big>paetulus</big>\n\n<b>paetulus, a, um</b> [<i>demin. к</i> paetus]\nслегка косящий <i>C</i>."],
  ["paetus", "<big>paetus</big>\n\n<b>paetus, a, um</b>\n<b>1)</b> подмигивающий, томно щурящийся, искоса бросающий лукавый взгляд, строящий глазки (Venus <i>Vr, O etc.</i>);\n<b>2)</b> косоглазый, слегка косящий <i>Pl, H etc.</i>"],
  ["paganalia", "<big>paganalia</big>\n\n<b>paganalia, ium</b> <i>и</i> <b>orum</b> <i>n</i> [pagus]\nсельский праздник <i>Vr, Macr</i>."],
  ["paganica", "<big>paganica</big>\n\n<b>paganica, ae</b> <i>f</i> [paganicus]\n(<i>sc.</i> pila) мяч, набитый пухом <i>M</i>."],
  ["paganicum", "<big>paganicum</big>\n\n<b>paganicum, i</b> <i>n</i> (<i>sc.</i> patrimonium)\nсельское поместье, усадьба <i>CTh</i>."],
  ["paganicus", "<big>paganicus</big>\n\n<b>paganicus, a, um</b>\n<b>1)</b> сельский, деревенский (feriae <i>Vr</i>);\n<b>2)</b> языческий <i>Eccl</i>."],
  ["paganismus", "<big>paganismus</big>\n\n<b>paganismus, i</b> <i>m</i> [paganus]\nязычество <i>Aug</i>."],
  ["paganitas", "<big>paganitas</big>\n\n<b>paganitas, atis</b> <i>f</i> <i>CTh</i>\n= paganismus."],
  ["paganus", "<big>paganus</big>\n\n<b>I paganus, a, um</b> [pagus]\n<b>1)</b> сельский, деревенский (foci <i>O</i>);\n<b>2)</b> крестьянский, простой, неучёный (cultus <i>PJ</i>);\n<b>3)</b> языческий (superstitio <i>CTh</i>).\n\n<b>II paganus, i</b> <i>m</i>\n<b>1)</b> сельский житель, крестьянин, поселянин (pagani et oppidani <i>bAl</i>; pagani aut montani <i>C</i>);\n<b>2)</b> невоенный, штатский <i>PJ, Dig</i>; обыватель <i>T, Su</i>;\n<b>3)</b> язычник <i>CJ, Eccl</i>."],
  ["Pagasae", "<big>Pagasae</big>\n\n<b>Pagasae, arum, Pagasa, ae</b> <i>и</i> <b>Pagase, es</b> <i>f</i>\nПагасы, <i>фессалийский портовый город в сев. части Пагасейского залива (еде был сооружён корабль Арго), прежде Demetrias</i> <i>Prp, Mela, PM, VF</i>."],
  ["Pagasaeus", "<big>Pagasaeus</big>\n\n<b>I Pagasaeus, a, um</b> [Pagasae]\nпагасейский: Pagasaea puppis <i>или</i> carina <i>O</i> = корабль Argo; Pagasaea conjunx <i>O</i> = Alcestis (<i>дочь фессалийского царя Пелия</i>).\n\n<b>II Pagasaeus, i</b> <i>m</i>\nуроженец города Pagasae, <i>т. е.</i> Ясон <i>O</i>."],
  ["Pagaseius", "<big>Pagaseius</big>\n\n<b>Pagaseius</b> <i>VF</i> <i>и</i> <b>Pagasicus, a, um</b> <i>PM</i>\n= Pagasaeus I."],
  ["pagatim", "<big>pagatim</big>\n\n<b>pagatim</b> [pagus]\nпо сёлам, по деревням, по округам (templa p. sacrata <i>L</i>)."],
  ["pagella", "<big>pagella</big>\n\n<b>pagella, ae</b> <i>f</i> [<i>demin. к</i> pagina]\nстраничка, листок <i>C, Vlg</i>."],
  ["pager", "<big>pager</big>\n\n<b>pager, gri</b> <i>m</i> <i>v. l.</i>\n= phager."],
  ["Pagidas", "<big>Pagidas</big>\n\n<b>Pagida(s), ae</b> <i>m</i>\nПагид:\n<b>1)</b> <i>река в Африке</i> (<i>предпол. в Нумидии</i>) <i>T</i>;\n<b>2)</b> (<i>или</i> Belus) <i>река в Палестине близ горы Кармел</i> <i>PM</i>."],
  ["pagina", "<big>pagina</big>\n\n<b>pagina, ae</b> <i>f</i>\n<b>1)</b> лист, страница: in extrema pagina <i>C</i> в конце страницы; paginam utramque facere <i>погов. PM</i> заполнять обе страницы, заносить в приход и в расход (<i>т. е.</i> приписывать и хорошее и дурное);\n<b>2)</b> рукопись, сочинение, произведение (lasciva p. <i>M</i>): p. sancta <i>Hier</i> = Biblia sacra;\n<b>3)</b> список, перечень (paginae consulares <i>Amm</i>; longa honorum p. <i>J</i>);\n<b>4)</b> плита (tabellae marmoreae aut paginae <i>Pall</i>);\n<b>5)</b> квартал, прямоугольник из четырёх рядов виноградных лоз <i>PM</i>."],
  ["pagino", "<big>pagino</big>\n\n<b>pagino, —, atum, are</b> [pagina]\n<b>1)</b> скреплять, соединять (navis solido robore paginata <i>Eccl</i>);\n<b>2)</b> писать, сочинять (aliquid de aliqua re <i>Ambr</i>)."],
  ["paginula", "<big>paginula</big>\n\n<b>paginula, ae</b> <i>f</i> [<i>demin. к</i> pagina]\nстраничка, листочек, <i>pl.</i> списки, перечень (consulum <i>C</i>)."],
  ["pagmentum", "<big>pagmentum</big>\n\n<b>pagmentum, i</b> <i>n</i> [pango]\nобивка, обшивка, покров <i>Vtr</i> (<i>v. l.</i>)."],
  ["pago", "<big>pago</big>\n\n<b>pago, —, —, ere</b> <i>арх. LXIIT</i>\n= pango."],
  ["pagrus", "<big>pagrus</big>\n\n<b>pagrus</b> <i>и</i> <b>pagur</b> <i>v. l.</i>\n= phager."],
  ["pagurus", "<big>pagurus</big>\n\n<b>pagūrus, ī</b> <i>m</i> (<i>греч.</i>)\nразновидность морских раков (<i>предпол.</i> краб <i>Cancer pagurus</i>, L.) <i>PM</i>."],
  ["pagus", "<big>pagus</big>\n\n<b>pāgus, ī</b> <i>m</i> [pango]\n<b>1)</b> сельская община, село, деревня: pagi foraque <i>L</i> деревни и ярмарочные центры;\n<b>2)</b> сельское население: festus in pratis vacat otioso cum bove p. <i>H</i> предаются отдыху на лугах празднующие сельские жители, (отдыхает) и вол;\n<b>3)</b> nar, область, район, округ (omnis Helvetia in quattuor pagos divisa est <i>Cs</i>)."],
  ["pala", "<big>pala</big>\n\n<b>pala, ae</b> <i>f</i> [<i>из</i> *pagela <i>от</i> pango]\n<b>1)</b> лопата, заступ <i>Cato, Vr, L etc.</i>;\n<b>2)</b> ручная веялка <i>Tert</i>;\n<b>3)</b> оправа камня в перстне, чашечка, гнездо (p. anuli <i>C</i>);\n<b>4)</b> <i>анат.</i> лопатка <i>CA</i>;\n<b>5)</b> <i>бот.</i> пизанг, райский банан (<i>Musa paradisiaca</i>, L.) <i>PM</i>."],
  ["palabundus", "<big>palabundus</big>\n\n<b>palabundus, a, um</b> [palor]\nстранствующий, бродячий, скитальческий <i>Tert</i>."],
  ["palacra", "<big>palacra</big>\n\n<b>palacra (palacrana, palacurna), ae</b> <i>f</i> <i>v. l.</i>\n= palaga."],
  ["Palaemon", "<big>Palaemon</big>\n\n<b>Palaemon, onis</b> <i>m</i>\nПалемон:\n<b>1)</b> <i>сын Атаманта и Ино, бог моря</i> (= Melicertes) <i>C, V</i>;\n<b>2)</b> Remmius P., <i>римск. грамматик времён Тиберия и Клавдия</i> <i>Q, Su, J</i>;\n<b>3)</b> <i>имя пастуха</i> <i>V</i>."],
  ["Palaemonius", "<big>Palaemonius</big>\n\n<b>Palaemonius, a, um</b> [Palaemon 1]\nпалемонов, <i>поэт.</i> коринфский <i>или</i> истмийский <i>St, Cld</i>."],
  ["Palaepharsalus", "<big>Palaepharsalus</big>\n\n<b>Palaepharsalus, i</b> <i>f</i>\nПалефарсал, Старый Фарсал, <i>город в Фессалии на правом берегу реки Апидан</i> <i>L, bAl</i>."],
  ["Palaephatius", "<big>Palaephatius</big>\n\n<b>Palaephatius, a, um</b> [Palaephatus]\nпалефатов <i>V</i>."],
  ["Palaephatus", "<big>Palaephatus</big>\n\n<b>Palaephatus, i</b> <i>m</i>\nПалефат, <i>греч. мифограф догомеровских времён</i> <i>PM</i>."],
  ["Palaepolis", "<big>Palaepolis</big>\n\n<b>Palaepolis, is</b> <i>f</i>\nПалеполь, часть Неаполя <i>L</i>."],
  ["Palaepolitani", "<big>Palaepolitani</big>\n\n<b>Palaepolitani, orum</b> <i>m</i>\nжители Палеполя <i>L</i>."],
  ["Palaeste", "<big>Palaeste</big>\n\n<b>Palaeste, es</b> <i>f</i>\nПалеста, <i>морской порт в Эпире; место, еде, по преданию, начинался спуск в подземное царство</i> <i>Cs</i>."],
  ["palaestes", "<big>palaestes</big>\n\n<b>palaestes, ae</b> <i>m</i> (<i>греч.</i>)\nборец, атлет <i>Lampr</i>."],
  ["Palaestina", "<big>Palaestina</big>\n\n<b>Palaestina, ae</b>\n(-e, -es) <i>f</i> Палестина, <i>страна в бассейне реки Иордана</i> <i>PM, Mela</i>."],
  ["Palaestinensis", "<big>Palaestinensis</big>\n\n<b>Palaestinensis, e</b> [Palaestina]\nпалестинский: Neapolitanus P. <i>Spart</i> житель Неаполя Палестинского, <i>т. е.</i> Наблуса (<i>библейского</i> Сихема)."],
  ["Palaestinus", "<big>Palaestinus</big>\n\n<b>I Palaestinus, a, um</b>\n<b>1)</b> [Palaeste] палестский: Palaestinae deae <i>C</i> = Фурии;\n<b>2)</b> [Palaestina] палестинский <i>Tib, O, Sl etc.</i>\n\n<b>II Palaestinus, i</b> <i>m</i>\n<b>1)</b> житель Палестины;\n<b>2)</b> сириец."],
  ["palaestra", "<big>palaestra</big>\n\n<b>palaestra, ae</b> <i>f</i> (<i>греч.</i>)\n<b>1)</b> палестра, гимнастическая школа <i>Pl, C, V</i>;\n<b>2)</b> гимнастические упражнения, борьба (discere palaestram <i>C</i>): p. nitida (uncta) <i>O</i> упражнения умащённых оливковым маслом борцов;\n<b>3)</b> школа красноречия <i>C</i>;\n<b>4)</b> риторические упражнения (palaestra institutus <i>C</i>);\n<b>5)</b> искусство, умение: uti palaestra alicujus <i>C</i> использовать чьё-л. искусство;\n<b>6)</b> <i>шутл.</i> дом разврата <i>Pl, Ter</i>."],
  ["palaestrica", "<big>palaestrica</big>\n\n<b>palaestrica, ae</b> <i>f</i> (<i>sc.</i> ars)\nгимнастика, искусство атлетических состязаний <i>Q</i>."],
  ["palaestrice", "<big>palaestrice</big>\n\n<b>palaestrice</b> [palaestricus I]\nкак в палестре, по-школьному <i>C</i>."],
  ["palaestricus", "<big>palaestricus</big>\n\n<b>I palaestricus, a, um</b> [palaestra]\nизучаемый (преподаваемый) в палестре (motus <i>C</i>); преподающий в палестре (magister <i>Q</i>): sucus p. <i>Ap</i> физическое здоровье, свежесть; p. praetor <i>ирон. C</i> пристрастный к гимнастам претор (<i>о Верресе, который несправедливо решил процесс в пользу гимнастической организации</i>).\n\n<b>II palaestricus, i</b> <i>m</i>\nучитель гимнастики <i>Q</i>."],
  ["palaestrita", "<big>palaestrita</big>\n\n<b>palaestrita, ae</b> <i>m</i> [palaestra]\nборец, атлет <i>C, Pt, Amm</i>."],
  ["palaestro", "<big>palaestro</big>\n\n<b>palaestro, —, —, are</b> [palaestra]\nупражнять(ся) <i>Fronto</i>."],
  ["Palaetyros", "<big>Palaetyros</big>\n\n<b>Palaetyros, i</b> <i>f</i>\nПалетир, Старый Тир, <i>город на финикийском побережье</i> (<i>к югу от Сидона</i>) <i>PM</i>."],
  ["palaga", "<big>palaga</big>\n\n<b>palaga (palacra), ae</b> <i>f</i> (<i>исп.</i>)\nзолотой слиток <i>PM</i>."],
  ["palam", "<big>palam</big>\n\n<b>I palam</b> <i>adv.</i>\n<b>1)</b> открыто, явно (p. et aperte <i>Pl</i>; haec p. et alia secreto <i>T</i>; p. ante oculos omnium <i>C</i>): luce p. <i>C, L</i> среди бела дня; p. hortatu <i>T</i> открытой агитацией;\n<b>2)</b> напрямик (pronuntiare, proferre <i>C</i>);\n<b>3)</b> общеизвестно (p. est <i>Pl, Ter</i>): p. facere aliquid <i>и</i> de aliqua re <i>C, Nep etc.</i> разглашать что-л., объявлять о чём-л.; p. fieri <i>C</i> становиться известным; cunctis gentibus p. esse <i>Cld</i> быть на виду у всех народов; p. facere de morte alicujus <i>Nep</i> оповещать о чьей-л. смерти.\n\n<b>II palam</b> <i>praep. cum abl.</i>\nв присутствии, при (p. populo <i>L</i>; me p. <i>O</i>; p. nobis <i>Pt</i>)."],
  ["Palamedes", "<big>Palamedes</big>\n\n<b>Palamēdēs, is</b> <i>m</i>\nПаламед, <i>сын эвбейского царя Навплия, убитый перед стенами Трои по наущению его врага Одиссея; Паламеду приписывалось введение в греческий алфавит</i> букв, Θ, Ξ, Φ, Χ, а также Υ и Δ <i>C, PM</i>: Palamedis aves <i>M</i> = журавли (<i>так как силуэт летящего журавля внушил, якобы, Паламеду форму буквы</i> <i>Υ</i>)."],
  ["palanga", "<big>palanga</big>\n\n<b>palanga, ae</b> <i>f</i> <i>Vr</i>\n= phalanga."],
  ["palaria", "<big>palaria</big>\n\n<b>palaria, ium</b> <i>n</i> [palaris] <i>воен.</i>\nупражнения с шестами <i>Veg</i>."],
  ["palaris", "<big>palaris</big>\n\n<b>palaris, e</b> [palus I]\nпригодный для изготовления жердей (silva <i>Dig</i>)."],
  ["palathium", "<big>palathium</big>\n\n<b>palathium, i</b> <i>n</i> (<i>греч.</i>)\nфруктовая масса (<i>род мармелада</i>) (p. caryotarum <i>M</i>)."],
  ["palatinus", "<big>palatinus</big>\n\n<b>I palatinus, i</b> <i>m</i> [palatium]\nслужащий императорского двора, царедворец <i>M, Lampr</i>.\n\n<b>II Palatinus, a, um</b> [Palatium]\n<b>1)</b> Палатинский (collis <i>O</i>; tribus <i>Vr, C</i>): Apollo P. <i>H</i> храм Аполлона на Палатинском холме (<i>с библиотекой, основанной при Августе</i>);\n<b>2)</b> императорский (domus <i>Su</i>; laurus <i>O</i>)."],
  ["palatio", "<big>palatio</big>\n\n<b>palatio, onis</b> <i>f</i> [palo I]\nвбивание кольев, обнесение частоколом <i>Vtr</i>."],
  ["palatium", "<big>palatium</big>\n\n<b>I palatium, i</b> <i>n</i>\nдворец, чертог (palatia caeli <i>O</i>).\n\n<b>II Palatium, i</b> <i>n</i>\n<b>1)</b> Палатинский холм (<i>в Риме, между Авентином и Капитолием, южнее Форума</i>) <i>C, L, Tib</i>;\n<b>2)</b> императорский дворец (<i>так как резиденция Августа была на Палатинском холме</i>) <i>O, Amm</i>."],
  ["Palatua", "<big>Palatua</big>\n\n<b>Palatua, ae</b> <i>f</i>\nПалатуя, <i>богиня-хранительница Палатинского холма</i> <i>Vr</i>."],
  ["palatum", "<big>palatum</big>\n\n<b>palatum, i</b> <i>n</i>\n<b>1)</b> нёбо <i>Vr, C, V</i>;\n<b>2)</b> вкусовые ощущения, вкус (p. subtile <i>H</i>, torpens <i>O</i>): placere palato <i>Pt</i> (<i>о кушаньях</i>) приходиться по вкусу, быть вкусным;\n<b>3)</b> вкусовые наслаждения, обжорство <i>J</i>;\n<b>4)</b> уста: obserare p. <i>Ctl</i> молчать;\n<b>5)</b> свод (caeli <i>Enn ap. C</i>)."],
  ["palatus", "<big>palatus</big>\n\n<b>I palatus, i</b> <i>m</i> <i>C</i>\n= palatum.\n\n<b>II palatus, a, um</b> <i>part. pf. к</i>\npalor."],
  ["pale", "<big>pale</big>\n\n<b>pale, es</b> <i>f</i> (<i>греч.</i>)\nгимнастическая борьба <i>St, Sid</i>."],
  ["palea", "<big>palea</big>\n\n<b>palea, ae</b> <i>f</i>\n<b>1)</b> мякина, высевки <i>Col, C</i>;\n<b>2)</b> солома <i>Pt</i>;\n<b>3)</b> шлак (aeris <i>PM</i>);\n<b>4)</b> подзобок, <i>мясистый нарост под петушиным клювом</i> <i>Vr, Col</i>."],
  ["palealis", "<big>palealis</big>\n\n<b>palealis, e</b> [palea]\nсохраняемый в соломе (uva <i>CA</i>)."],
  ["palear", "<big>palear</big>\n\n<b>palear, aris</b> <i>n</i>\n<b>1)</b> подгрудок у быка (palearia pendula <i>O</i>);\n<b>2)</b> <i>Calp</i> = ruma 2."],
  ["palearis", "<big>palearis</big>\n\n<b>palearis, e</b> [palea]\nмякинный: tegmen paleare <i>Aug</i> шелуха."],
  ["palearium", "<big>palearium</big>\n\n<b>palearium, i</b> <i>n</i> [palea]\nсарай для мякины <i>или</i> соломы <i>Col</i>."],
  ["paleatus", "<big>paleatus</big>\n\n<b>paleatus, a, um</b> [palea]\nсмешанный с микиной <i>или</i> с соломой (lutum <i>Col, PM</i>)."],
  ["Pales", "<big>Pales</big>\n\n<b>Pales, is</b> <i>f</i>\nПалее, <i>древнеиталийская богиня, покровительница пастухов и скота</i> <i>Tib, V, O etc.</i>"],
  ["palest-", "<big>palest-</big>\n\n<b>palest-</b> <i>v. l.</i>\n= palaest-."],
  ["Palici", "<big>Palici</big>\n\n<b>Palici, orum</b> <i>m</i>\nПалики:\n<b>1)</b> <i>сыновья-близнецы Юпитера и нимфы Талии или Этны, хтонические божества, чтившиеся в Сицилии, между Генной и Сиракузами, близ города Палики</i> <i>V, O, St, Macr</i>;\n<b>2)</b> жители города Palica <i>Sil</i>."],
  ["Palicus", "<big>Palicus</big>\n\n<b>Palicus, i</b> <i>m</i> <i>V, O sg. к</i>\nPalici 1."],
  ["Palilia", "<big>Palilia</big>\n\n<b>Palilia, ium</b> <i>и</i> <b>orum</b> <i>n</i> [Pales]\nПалилии, <i>сельский пастушеский праздник, справлявшийся 21 апреля, в годовщину основания Рима</i> <i>Vr, C, O</i>."],
  ["Palilicius", "<big>Palilicius</big>\n\n<b>Palilicius, a, um</b> <i>adj. к</i>\nPalilia: Palilicium sidus <i>PM</i> = Гиады (<i>которые исчезали в канун праздника Palilia</i>)."],
  ["Palilis", "<big>Palilis</big>\n\n<b>Palilis, e</b> [Pales]\nпосвященный богине Pales: flamma P. <i>O</i> костёр из соломы, через который прыгали во время Palilia (<i>см.</i>)."],
  ["palimbacchius", "<big>palimbacchius</big>\n\n<b>palimbacchīus, ī</b> <i>m</i> (<i>греч.</i>)\nпалимвакхий (<i>стихотворная стопа</i> – –U) <i>Q</i>."],
  ["palimpsestus", "<big>palimpsestus</big>\n\n<b>palimpsestus, i</b> <i>m</i> (<i>греч.</i>)\nпалимпсест, <i>пергамент, с которого стёрт первоначальный текст, чтобы можно было на нём писать заново</i> <i>Ctl, C</i>."],
  ["palinodia", "<big>palinodia</big>\n\n<b>palinodia, ae</b> <i>f</i> (<i>греч.</i>)\nпалинодия:\n<b>1)</b> <i>повторение старой песни</i> (palinodiam concinere <i>Amm</i>);\n<b>2)</b> <i>отказ от прежних слов, песнь отречения</i> (palinodiam canere <i>Macr</i>)."],
  ["Palinurus", "<big>Palinurus</big>\n\n<b>Palinurus, i</b> <i>m</i>\nПалинур, <i>рулевой Энея, который, заснув, упад в море и утонул у берегов Лукании; по его имени якобы назван мыс на зап. побережье Лукании</i> (<i>ныне</i> Capo di Palinuro) <i>V, L</i>."],
  ["palitor", "<big>palitor</big>\n\n<b>palitor, —, ari</b> <i>depon.</i> [<i>intens. к</i> palor]\nбродить, блуждать: oves a pecu palitantes <i>Pl</i> отбившиеся от стада овцы."],
  ["paliurus", "<big>paliurus</big>\n\n<b>paliurus, i</b> <i>f</i> (<i>греч.</i>) <i>бот.</i>\nдержидерево, <i>колючий кустарник</i> (<i>Rhamnus Paliurus</i>, L.) <i>V, Col, PM</i>."],
  ["palla", "<big>palla</big>\n\n<b>palla, ae</b> <i>f</i>\n1), палла, <i>верхнее парадное женское платье до пят, надевавшееся поверх столы</i> <i>Pl, H, V, O</i>;\n<b>2)</b> мантия, плащ <i>rhH, Tib, H, O</i>;\n<b>3)</b> завеса, занавес (inter dicentes et audientem p. intererat <i>Sen</i>)."],
  ["pallaca", "<big>pallaca</big>\n\n<b>pallaca, ae</b> <i>f</i> (<i>греч.</i>)\nналожница <i>PM, Su</i>."],
  ["Pallacinus", "<big>Pallacinus</big>\n\n<b>Pallacinus, a, um</b>\nпаллацинский, <i>т. е.</i> находящийся в римск. квартале Pallacine (<i>близ Circus Flaminius</i>) (balneae <i>C</i>)."],
  ["Palladium", "<big>Palladium</big>\n\n<b>Palladium, i</b> <i>n</i> [Pallas]\nПалладий, <i>упавшее с неба в Трою изображение Паллады, считавшееся залогом целости этого города; Троя пала, когда Палладий был похищен Одиссеем и Диомедом</i> (<i>после чего он якобы попал в Афины или в Аргос, а оттуда в Рим</i>) <i>V, O, Fl</i>."],
  ["Palladius", "<big>Palladius</big>\n\n<b>I Palladius, a, um</b> [Pallas]\nпалладии (manus <i>St</i>; aegis <i>Lcn</i>): numen Palladium <i>O</i> = Паллада; P. ramus <i>V</i>= оливковая ветвь; Palladia silva <i>V</i> = оливковые рощи; Palladi! latices <i>O</i> = оливковое масло; Palladia corona <i>O</i> = оливковый венок; Palladia arx <i>Prp</i> = Троя; Palladiae arces <i>O</i> = Афины; Palladia pinus <i>VF</i> = корабль Арго.\n\n<b>II Palladius, i</b> <i>m</i>\nПалладий: P. Rutilius Taurus Aemilianus, <i>автор сочинения «De re rustica libri XIV»</i> (<i>IV в. н. э.</i>)."],
  ["Pallanteum", "<big>Pallanteum</big>\n\n<b>Pallanteum, i</b> <i>n</i>\nПаллантей:\n<b>1)</b> <i>город в Аркадии, к вост. от Тегеи, откуда Эвандр прибыл в Италию</i> <i>L, Just</i>;\n<b>2)</b> <i>город, основанный Эвандром на Авентинском, а затем и на Палатинском холме</i> <i>V</i>."],
  ["Pallanteus", "<big>Pallanteus</big>\n\n<b>Pallanteus, a, um</b> [Pallas II]\nпаллантов: moenia Pallantea <i>V</i> = Pallanteum 2; P. apex <i>Cld</i> = collis Palatinus."],
  ["Pallantias", "<big>Pallantias</big>\n\n<b>Pallantias, adis</b> <i>и</i> <b>Pallantis, idis (idos)</b> <i>f</i>\n<b>1)</b> Аврора (<i>названная так по имени своего предка, гиганта Палланта</i>) <i>O</i>;\n<b>2)</b> <i>поэт.</i> день <i>O</i>."],
  ["Pallantius", "<big>Pallantius</big>\n\n<b>Pallantius, a, um</b> [Pallas II]\nпаллантов: P. heros <i>O</i> = Эвандр."],
  ["Pallas", "<big>Pallas</big>\n\n<b>I Pallas, adis</b> <i>и</i> <b>ados</b> <i>f</i>\n<b>1)</b> Паллада, <i>греч. эпитет Минервы</i>: Irata Pallade <i>O</i> = invita Minerva; Palladis ars <i>O</i> = ткацкое искусство; Palladis arbor <i>O</i> = олива; Palladis ales <i>O</i> — сова; Palladis ignes <i>Prp</i> огни в храме Весты (<i>куда, по преданию, был помещён троянский Палладий</i>);\n<b>2)</b> <i>O</i> = Palladium;\n<b>3)</b> <i>Prp</i> = храм Весты (<i>см.</i> Pallas I, 1);\n<b>4)</b> <i>O</i> = оливковое масло;\n<b>5)</b> <i>O</i> = масличное дерево;\n<b>6)</b> <i>Macr</i> =* число семь (<i>которое считалось символом непорочности</i>).\n\n<b>II Pallas, antis</b> <i>m</i>\nПаллант:\n<b>1)</b> <i>аркадец, предок Эвандра, основатель города Pallanteum в Аркадии</i> <i>V</i>;\n<b>2)</b> <i>сын Эвандра, союзник Энея, убитый Турном</i> <i>V</i>;\n<b>3)</b> <i>один из гигантов</i> (<i>или титанов</i>) <i>Cld</i>."],
  ["Pallat-", "<big>Pallat-</big>\n\n<b>Pallat-</b> <i>v. l.</i>\n= » Palat-."],
  ["Pallenaeus", "<big>Pallenaeus</big>\n\n<b>Pallenaeus, a, um</b> <i>Lcn</i>\n= Pallenensis."],
  ["Pallene", "<big>Pallene</big>\n\n<b>Pallene, es</b> <i>f</i>\nПаллена, <i>зап. полуостров Халкидики, на котором, по преданию, происходило сражение между богами и гигантами</i> <i>O, PM, VF</i>."],
  ["Pallenensis", "<big>Pallenensis</big>\n\n<b>I Pallenensis, e</b> [Pallene]\nпалленский (ager <i>L</i>).\n\n<b>II Pallenensis, is</b> <i>m</i>\nжитель п-ова Pallene <i>L</i>."],
  ["pallens", "<big>pallens</big>\n\n<b>pallens, entis</b>\n<b>1.</b> <i>part. praes. к</i> palleo;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> бледный, <i>поэт.</i> бледно-зелёный, светло-жёлтый, блёклый, тусклый (oliva, herbae <i>V</i>): pallentes umbrae (<i>или</i> animae) <i>V</i> тени подземного царства (усопших); p. fama <i>T</i> призрак славы;\n<b>2)</b> наводящий бледность, делающий бледным (morbus <i>V</i>; philtra <i>O</i>; curae <i>M</i>);\n<b>3)</b> порочный (mores <i>Pers</i>)."],
  ["palleo", "<big>palleo</big>\n\n<b>palleo, pallui, —, ere</b>\n<b>1)</b> становиться бледным, покрываться бледностью, бледнеть (amisso sanguine <i>O</i>; morbo <i>J</i>; metu <i>O</i>); быть бледным, изнурённым (aliqua re <i>H, Sen</i>): p. aliquid <i>Pers</i> <i>или</i> alicui rei <i>J</i> работать над чём-л. до изнеможения;\n<b>2)</b> бледнеть от страха, бояться, страшиться, тревожиться: p. ad aliquid <i>J</i> <i>и</i> aliquid <i>H</i> бояться чего-л.; p. alicui <i>H</i> бояться за кого-л.;\n<b>3)</b> выцветать, линять (seges pallet <i>O</i>); p. multos colores <i>Prp</i> часто менять цвет;\n<b>4)</b> быть бледно-зелёным, желтоватым: saxum palluit auro <i>O</i> камень (в руках Мидаса) становится золотым. — <i>См. тж.</i> pallens."],
  ["pallesco", "<big>pallesco</big>\n\n<b>pallesco, pallui, —, ere</b> [<i>inchoat. к</i> palleo]\n<b>1)</b> становиться бледным, бледнеть (aliqua re <i>Prp, Q</i>);\n<b>2)</b> желтеть, блёкнуть (frondes pallescunt <i>O</i>);\n<b>3)</b> замирать от восторга (super aliqua re <i>H</i>); быть без памяти влюблённым (in aliqua <i>Prp</i>);\n<b>4)</b> страшно беспокоиться, бояться (nulla culpa p. <i>H</i>)."],
  ["palliastrum", "<big>palliastrum</big>\n\n<b>palliastrum, i</b> <i>n</i> [pallium]\nдрянной плащ <i>Ap</i>."],
  ["palliatus", "<big>palliatus</big>\n\n<b>palliatus, a, um</b> [pallium]\n<b>1)</b> одетый в паллиум (плащ): Graecus p. <i>Pl etc.</i> грек в плаще (<i>о греч. философах</i>); palliata mendicabula <i>ирон. Ap</i> попрошайки в плащах философов;\n<b>2)</b> <i>перен.</i> греческий: fabula palliata <i>Vr</i> комедия из греческого быта и с греческими костюмами (<i>в отличие от</i> fabula togata, <i>см.</i>)."],
  ["pallidulus", "<big>pallidulus</big>\n\n<b>pallidulus, a, um</b> [<i>demin. к</i> pallidus]\nбледноватый, побледневший (pes <i>Ctl</i>): abire in loca pallidula <i>Hadrianus ap. Spart</i> = mori."],
  ["pallidus", "<big>pallidus</big>\n\n<b>pallidus, a, um</b> [palleo]\n<b>1)</b> бледный: p. in aliqua <i>Prp, O</i> побледневший от любви к кому-л., <i>т. е.</i> безумно влюблённый в кого-л.; pallida turba <i>Tib</i> души умерших в подземном царстве;\n<b>2)</b> покрытый плесенью, заплесневелый (ficus <i>Vr</i>);\n<b>3)</b> бледно-зелёный, блёклый <i>или</i> выцветший, выгоревший (от солнца) (arva <i>Sil</i>);\n<b>4)</b> больной, измождённый (pauper ac p. <i>Sen</i>);\n<b>5)</b> покрывающий бледностью, делающий бледным (mors <i>H</i>)."],
  ["pallio", "<big>pallio</big>\n\n<b>pallio, —, —, are</b>\nпрятать, <i>перен.</i> сглаживать, скрашивать (aliquid <i>Eccl</i>)."],
  ["palliolatim", "<big>palliolatim</big>\n\n<b>palliolatim</b>\nв плаще: p. amictus <i>Pl</i> окутанный плащом."],
  ["palliolatus", "<big>palliolatus</big>\n\n<b>palliolatus, a, um</b> [palliolum]\nснабжённый капюшоном (tunica <i>Vop</i>; p. vagatur <i>M</i>)."],
  ["palliolum", "<big>palliolum</big>\n\n<b>palliolum, i</b> <i>n</i> [<i>demin. к</i> pallium]\n<b>1)</b> небольшой греческий плащ <i>Pl, C, Ap etc.</i>;\n<b>2)</b> капюшон <i>O, Sen etc.</i>"],
  ["pallium", "<big>pallium</big>\n\n<b>pallium, i</b> <i>n</i>\n<b>1)</b> покрывало, покров (lecti <i>O</i>);\n<b>2)</b> завеса, полог (lecticulae <i>Eutr</i>);\n<b>3)</b> паллий (паллиум), просторный греческий плащ (<i>один из внешних признаков древних философов</i>): p. accipere <i>Ap</i> стать (<i>или</i> объявить себя) философом; manum intra p. continere <i>погов. O</i> = говорить спокойно, сдержанно; tunica propior pallio <i>погов. Pl</i> туника ближе (к телу), чем плащ, <i>т. е.</i> что ближе, то дороже;\n<b>4)</b> верхнее платье (<i>вообще</i>) <i>M</i>."],
  ["pallo", "<big>pallo</big>\n\n<b>pallo, avi, —, are</b>\nгубить (tempestas omnia pallavit <i>Ap</i> — <i>v. l.</i>)."],
  ["pallor", "<big>pallor</big>\n\n<b>pallor, oris</b> <i>m</i> [palleo]\n<b>1)</b> бледность, бледный цвет (terrorem p. consequitur <i>C</i>); (<i>тж.</i> albus p. <i>H</i>) мертвенная бледность (p. hiemsque <i>O</i>; infernae p. domus <i>Lcn</i>);\n<b>2)</b> плесень (pallore volumina corrumpere <i>Vtr</i>);\n<b>3)</b> тусклость, блёклый цвет (omnia palloribus pingere <i>Lcr</i>);\n<b>4)</b> тревога, беспокойство, страх (pallorem incutere <i>Pl</i>); p. pro aliquo <i>St</i> страх за кого-л."],
  ["pallula", "<big>pallula</big>\n\n<b>pallula, ae</b> <i>f</i> [<i>demin. к</i> palla]\nкороткий плащ <i>PM</i>."],
  ["palma", "<big>palma</big>\n\n<b>palma, ae</b> <i>f</i> (<i>греч.</i>)\n<b>1)</b> ладонь, кисть руки (compressa palma ferire <i>Pl</i>; cavis palmis undam de flumine tollere <i>V</i>);\n<b>2)</b> рука: passis palmis salutem petere <i>Cs</i> с протянутыми руками молить о пощаде;\n<b>3)</b> лопасть весла (verrentes aequora palmis <i>Ctl</i>);\n<b>4)</b> (<i>тж.</i> arbor palmae <i>Su</i>) пальма (<i>преим.</i> финиковая) <i>Cs, PM, AG etc.</i>;\n<b>5)</b> пальмовая ветвь <i>Cato, Col</i>;\n<b>6)</b> финик <i>O, Pers, PM</i>;\n<b>7)</b> веник <i>или</i> метла из пальмовых ветвей <i>H, M</i>;\n<b>8)</b> пальмовая ветвь (<i>как награда победителю</i>) (palmae victoribus datae <i>L</i>); palmam dare (deferre) alicui <i>C</i> признать кого-л. победителем; palmam accipere (ferre) <i>C</i> одержать победу;\n<b>9)</b> преимущество, превосходство (Siculum mel fert palmam <i>Vr</i>);\n<b>10)</b> слава (bellorum palmae <i>Just</i>);\n<b>11)</b> победитель на состязаниях: post Helymus subit et nunc tertia palma Diores <i>V</i> после (Эвриала) идёт Гелим, а затем третий победитель, Диорес;\n<b>12)</b> участник состязания, претендент на приз (ultima p. Virbius <i>Sil</i>);\n<b>13)</b> (= parma) щит <i>Tib</i>;\n<b>14)</b> побег, крайняя ветка (palmae arborum <i>QC</i>)."],
  ["palmaris", "<big>palmaris</big>\n\n<b>I palmāris, e</b> [palma]\n<b>1)</b> пальмовый (lucus <i>Amm</i>);\n<b>2)</b> достойный пальмовой ветви, отличный, превосходный (sententia, statua <i>C</i>);\n<b>3)</b> победный: dea p. <i>Ap</i> богиня победы.\n\n<b>II palmāris, e</b> [palmus]\nвеличиной в один пальм (spatium <i>Col</i>)."],
  ["palmarium", "<big>palmarium</big>\n\n<b>palmārium, ī</b> <i>n</i> [palmarius]\n<b>1)</b> заслуга, достойный награды поступок <i>Ter</i>;\n<b>2)</b> гонорар защитнику, выигравшему судебный процесс <i>Dig</i>."],
  ["palmarius", "<big>palmarius</big>\n\n<b>palmārius, a, um</b> [palma]\nпальмовый, поросший пальмами (insula <i>Vr</i>)."],
  ["palmata", "<big>palmata</big>\n\n<b>palmāta, ae</b> <i>f</i> <i>Vop, Aus, Sid</i>\n= palmata toga (<i>см.</i> palmatus)."],
  ["palmatias", "<big>palmatias</big>\n\n<b>palmatiās, ae</b> <i>m</i> (<i>греч.</i>)\nземлетрясение, подземные толчки <i>Ap</i>."],
  ["palmatus", "<big>palmatus</big>\n\n<b>palmātus, a, um</b> [palma]\n<b>1)</b> украшенный изображениями пальм, расшитый пальмовидными узорами (tunica <i>L</i>; vestis <i>VM</i>; toga <i>M</i>);\n<b>2)</b> одетый (<i>за одержанную победу</i>) в украшенную пальмами тогу (consul <i>Hier</i>);\n<b>3)</b> с пальмовидно ветвящимися рогами (cervus <i>Capit</i>);\n<b>4)</b> имеющий (кровавый) отпечаток руки (paries <i>Q</i>)."],
  ["palmes", "<big>palmes</big>\n\n<b>palmes, itis</b> <i>m</i>\n<b>1)</b> отпрыск, побег виноградной лозы <i>V, Col, PM, J</i>;\n<b>2)</b> виноградник (palmitis custos <i>M</i>);\n<b>3)</b> отросток, ветвь (palmites arborum <i>QC</i>)."],
  ["palmetum", "<big>palmetum</big>\n\n<b>palmetum, i</b> <i>n</i> [palma]\nпальмовая роща, финиковое насаждение <i>H, PM, Just</i>."],
  ["palmeus", "<big>palmeus</big>\n\n<b>I palmeus, a, um</b> [palma]\nпальмовый (vinum <i>PM</i>; tabulae <i>Vtr</i>).\n\n<b>II palmeus, a, um</b> [palmus] <i>PM</i>\n= palmaris II."],
  ["palmifer", "<big>palmifer</big>\n\n<b>palmifer, fera, ferum</b> [palma + fero]\nпроизводящий пальмы, богатый пальмами (Arabes <i>O</i>; Idume <i>Sil</i>)."],
  ["palmiger", "<big>palmiger</big>\n\n<b>palmiger, gera, gerum</b> [palma + gero] <i>PM</i>\n= palmifer."],
  ["palmipedalis", "<big>palmipedalis</big>\n\n<b>palmipedalis, e</b> <i>Vr, Vtr, Col</i>\n= palmipes 2."],
  ["palmipes", "<big>palmipes</big>\n\n<b>palmipes, pedis</b> <i>adj.</i> [palma + pes]\n<b>1)</b> перепончатолапый (anser <i>PM</i>);\n<b>2)</b> величиной в один фут и один пальм (= 36,97 <i>см</i>) <i>PM</i>."],
  ["palmo", "<big>palmo</big>\n\n<b>I palmo, —, —, are</b> [palma]\nсделать отпечаток руки (ладони) (p. vestigium <i>Q</i>).\n\n<b>II palmo, —, —, are</b> [palmes]\nподвязывать виноградные лозы <i>Col</i>."],
  ["palmop-", "<big>palmop-</big>\n\n<b>palmop-</b> <i>v. l.</i>\n= palmip-."],
  ["palmosus", "<big>palmosus</big>\n\n<b>palmosus, a, um</b> [palma]\nизобилующий пальмовыми деревьями (Setinus <i>V</i>)."],
  ["palmula", "<big>palmula</big>\n\n<b>palmula, ae</b> <i>f</i> [<i>demin. к</i> palma]\n<b>1)</b> кисть руки (aliquid palmulis converberare <i>Ap</i>);\n<b>2)</b> лопасть весла, <i>тж.</i> весло <i>V</i>;\n<b>3)</b> пальма (<i>преим.</i> финиковая) <i>Vr</i>;\n<b>4)</b> финик <i>Vr, CC, Su</i>;\n<b>5)</b> пальмовый лист (sportellae palmulis textae <i>Pt</i>);\n<b>6)</b> крыло (птицы): extimae palmulae <i>Ap</i> наружные кончики крыльев."],
  ["palmus", "<big>palmus</big>\n\n<b>palmus, i</b> <i>m</i> [palma]\n<b>1)</b> <i>Vtr, PM</i> = palma I;\n<b>2)</b> пальм, <i>мера длины</i> = 7,39 <i>см</i> (<i>1/4 римск. фута</i>): p. major = 3 пальма = 22,18 <i>см</i> <i>Cato, Vr, PM</i>."],
  ["Palmyra", "<big>Palmyra</big>\n\n<b>Palmyra, ae</b> <i>f</i>\nПальмира, <i>город в Сирии, центр империи царя Одената и царицы Зенобии (Зиновии) (впоследствии</i> Тадмор) <i>PM</i>."],
  ["Palmyrenus", "<big>Palmyrenus</big>\n\n<b>Palmyrenus, a, um</b> [Palmyra]\nпальмирский (solitudines <i>PM</i>)."],
  ["palo", "<big>palo</big>\n\n<b>I palo, avi, atum, are</b> [palus I]\nделать подпорки, привязывать к тычинам (vitem <i>Col, Pall</i>).\n\n<b>II palo, —, —, are</b> <i>Sulp</i>\n= palor."],
  ["palor", "<big>palor</big>\n\n<b>palor, atus sum, ari</b> <i>depon.</i>\n<b>1)</b> рассеиваться, разбрестись (per agros <i>L</i>); бродить (vagi palantesque <i>или</i> palatique <i>Sl, L</i>); блуждать (palantia sidera <i>Lcr</i>; palantes flammarum ardores <i>Amm</i>);\n<b>2)</b> выступать из берегов, разливаться (palans amnis <i>PJ</i>)."],
  ["palpabilis", "<big>palpabilis</big>\n\n<b>palpabilis, e</b> [palpo]\nосязаемый, ощутимый <i>Eccl</i>."],
  ["palpamentum", "<big>palpamentum</big>\n\n<b>palpamentum, i</b> <i>n</i> <i>Amm</i>\n= palpatio."],
  ["palpatio", "<big>palpatio</big>\n\n<b>palpatio, onis</b> <i>f</i> [palpo]\nпоглаживание, <i>перен.</i> лесть (verborum <i>Eccl</i>); <i>pl.</i> ласки, любезничание <i>Pl</i>."],
  ["palpator", "<big>palpator</big>\n\n<b>palpator, oris</b> <i>m</i> [palpo]\nподлиза, льстец <i>Pl, Eccl</i>."],
  ["palpatus", "<big>palpatus</big>\n\n<b>palpatus, us</b> <i>m</i>\nощупывание <i>Eccl</i>."],
  ["palpebra", "<big>palpebra</big>\n\n<b>palpebra, ae</b> <i>f</i> [palpo]\n<b>1)</b> веко <i>CC</i>; <i>преим.</i> <i>pl.</i> веки, вежды <i>C etc.</i>;\n<b>2)</b> <i>pl.</i> ресницы <i>PM</i>."],
  ["palpebraris", "<big>palpebraris</big>\n\n<b>palpebraris, e</b> [palpebra]\nпальпебральный, применяемый для век (collyria <i>CA</i>)."],
  ["palpebratio", "<big>palpebratio</big>\n\n<b>palpebratio, onis</b> <i>f</i>\nмигание веками, моргание <i>CA</i>."],
  ["palpebro", "<big>palpebro</big>\n\n<b>palpebro, —, —, are</b>\nмигать, моргать <i>CA</i>."],
  ["palpebrum", "<big>palpebrum</big>\n\n<b>palpebrum, i</b> <i>n</i> <i>CA</i>\n= palpebra 1."],
  ["palpitatio", "<big>palpitatio</big>\n\n<b>palpitatio, onis</b> <i>f</i> [palpito]\n<b>1)</b> мигание, моргание (oculorum <i>PM</i>);\n<b>2)</b> подёргивание (partium singularum, <i>sc.</i> insecti <i>PM</i>);\n<b>3)</b> биение, пульсирование (cordis <i>PM</i>)."],
  ["palpitatus", "<big>palpitatus</big>\n\n<b>palpitatus, us</b> <i>m</i> <i>PM</i>\n= palpitatio 3."],
  ["palpito", "<big>palpito</big>\n\n<b>palpito, avi, atum, are</b> [<i>intens. к</i> palpo]\nтрепетать, биться, пульсировать (cor palpitat <i>C</i>); подёргиваться (lingua palpitat <i>O, Lcn</i>); корчиться, извиваться (<i>в предсмертных муках и т. п.</i>) <i>O, J</i>; дрожать: arduus ignis palpitat <i>St</i> пламя дрожит и взвивается вверх || волноваться, быть потрясённым (animus palpitans <i>Pt</i>)."],
  ["palpo", "<big>palpo</big>\n\n<b>I palpo, avi, atum, are</b>\n<b>1)</b> гладить, поглаживать, похлопывать (aliquid manu <i>O</i>);\n<b>2)</b> ласкать (animalia <i>Man, Dig etc.</i>);\n<b>3)</b> подлаживаться, льстить (alicui <i>Acc, C</i> <i>или</i> aliquem <i>Ap</i>); aliquem munere p. <i>J</i> задабривать кого-л. подарками;\n<b>4)</b> успокаивать, унимать (iram alicujus <i>Ap</i>);\n<b>5)</b> нащупывать дорогу, идти ощупью (sicut caecus in tenebris <i>Vlg</i>).\n\n<b>II palpo, onis</b> <i>m</i> <i>Pers</i>\n= palpator."],
  ["palpor", "<big>palpor</big>\n\n<b>palpor, atus sum, ari</b> <i>depon.</i> <i>Pl, H, Dig</i>\n= palpo I."],
  ["palpum", "<big>palpum</big>\n\n<b>palpum, i</b> <i>n</i> <i>или</i> <b>palpus, i</b> <i>m</i> (<i>nom.</i> <i>не встреч.</i>)\nпохлопывание, поглаживание, <i>перен.</i> лесть (aliquem palpo percutere <i>Pl</i>): mihi obtrudere non potes palpum <i>Pl</i> тебе ко мне не подольститься."],
  ["paludamentum", "<big>paludamentum</big>\n\n<b>paludamentum, i</b> <i>m</i>\nвоенный плащ (<i>преим. полководца</i>) <i>Vr, L, PM, Ap</i>."],
  ["paludatus", "<big>paludatus</big>\n\n<b>paludatus, a, um</b> [paludamentum]\nодетый в военный плащ (duces <i>J</i>; lictores <i>L</i>)."],
  ["paludicola", "<big>paludicola</big>\n\n<b>paludi–cola, ae</b> <i>m, f</i> [colo II]\nжитель(ница) болот (Sicambri <i>Sid</i>)."],
  ["paludigena", "<big>paludigena</big>\n\n<b>paludigena, ae</b> <i>m, f</i> [palus II + geno = gigno]\nрастущий на болотах, болотный (papyrus <i>Eccl</i>)."],
  ["paludosus", "<big>paludosus</big>\n\n<b>paludosus, a, um</b> [palus II]\nболотистый (humus <i>O</i>; Nilus <i>St</i>)."],
  ["palum", "<big>palum</big>\n\n<b>palum, i</b> <i>n</i> <i>Vr</i>\n= palus I."],
  ["palumba", "<big>palumba</big>\n\n<b>palumba, ae</b> <i>f</i> <i>CC</i>\n= palumbes."],
  ["palumbes", "<big>palumbes</big>\n\n<b>palumbes (palumbis), is</b> <i>m, f</i>\nдикий (сизый) голубь, вяхирь <i>C, V etc.</i>: palumbem alicui ad aream adducere <i>погов. Pl</i> привести в чьи-л. сети голубя, <i>т. е.</i> оказать кому-л. помощь."],
  ["Palumbinum", "<big>Palumbinum</big>\n\n<b>Palumbinum, i</b> <i>n</i>\nПалумбин, <i>городок в Самнии</i> <i>L</i>."],
  ["palumbinus", "<big>palumbinus</big>\n\n<b>palumbinus, a, um</b> [palumbes]\nголубиный (caro <i>PM</i>; ovum <i>Lampr</i>)."],
  ["palumbis", "<big>palumbis</big>\n\n<b>palumbis</b> <i>v. l.</i>\n= palumbes."],
  ["palumbulus", "<big>palumbulus</big>\n\n<b>palumbulus, i</b> <i>m</i> [<i>demin. к</i> palumbes] <i>ласкат.</i>\nголубок <i>Ap</i>."],
  ["palumbus", "<big>palumbus</big>\n\n<b>palumbus, i</b> <i>m</i> <i>Col, CC, Pt etc.</i>\n= palumbes."],
  ["palus", "<big>palus</big>\n\n<b>I palus, i</b> <i>m</i> [pango]\n<b>1)</b> кол, столб (aliquem ad palum alligare <i>C</i>);\n<b>2)</b> <i>воен.</i> столб, фигура, чучело, изображающее неприятеля и служащее для военных упражнений: exerceri ad palum <i>Veg, Sen</i> упражняться в борьбе, <i>перен.</i> закаляться для борьбы; p. primus <i>Lampr</i> предводитель гладиаторов-secutores (<i>см.</i>);\n<b>3)</b> membrum virile (porrectus ab inguine p. <i>H</i>).\n\n<b>II palus, udis</b> (<i>gen. pl.</i> <i>иногда</i> ium) <i>f</i>\n<b>1)</b> болото <i>C, Cs, H, O</i>: tarda <i>V</i> (dis juranda <i>O</i>) p. = Stygia p.;\n<b>2)</b> болотный тростник, камыш <i>M</i>."],
  ["paluster", "<big>paluster</big>\n\n<b>paluster, stris, stre</b> (<i>тж.</i> <b>palustris, e</b> <i>Col</i>) [palus II]\n<b>1)</b> болотный (rana <i>H</i>; avis <i>Mela</i>);\n<b>2)</b> болотистый (loca <i>C etc.</i>)."],
  ["palustria", "<big>palustria</big>\n\n<b>palustria, ium</b> <i>n</i> [paluster]\n<b>1)</b> болотистые места <i>PM, Vlg</i>;\n<b>2)</b> болотные растения <i>PM</i>."],
  ["pammacharius", "<big>pammacharius</big>\n\n<b>pammacharius, i</b> <i>m</i> (<i>греч.</i>)\nпобедитель во всеборье <i>Ambr</i>."],
  ["Pammenes", "<big>Pammenes</big>\n\n<b>Pammenes, is</b> <i>m</i>\nПаммен, <i>греч. ритор, учитель М. Брута</i> <i>C</i>."],
  ["Pamphilus", "<big>Pamphilus</big>\n\n<b>Pamphilus, i</b> <i>m</i>\nПамфил:\n<b>1)</b> <i>ученик Платона, которого слушал Эпикур</i> <i>C</i>;\n<b>2)</b> <i>живописец, учитель Апеллеса</i> <i>PM</i>."],
  ["Pamphylia", "<big>Pamphylia</big>\n\n<b>Pamphylia, ae</b> <i>f</i>\nПамфилия, <i>страна на юге Малой Азии, между Ликией и Киликией</i> <i>C etc.</i>"],
  ["Pamphylius", "<big>Pamphylius</big>\n\n<b>I Pamphylius, a, um</b> [Pamphylia]\nпамфилийский (tellus <i>Lcn</i>).\n\n<b>II Pamphylius, i</b> <i>m</i>\nжитель страны Pamphylia <i>C</i>."],
  ["pampinaceus", "<big>pampinaceus</big>\n\n<b>pampinaceus, a, um</b> <i>Col</i>\n= pampineus."],
  ["pampinarium", "<big>pampinarium</big>\n\n<b>pampinarium, i</b> <i>n</i> [pampinarius]\nлоза с усиками и листьями, но без ягод <i>PM</i>."],
  ["pampinarius", "<big>pampinarius</big>\n\n<b>pampinarius, a, um</b> [pampinus]\nусиковый, имеющий лишь усики и листья (но не ягоды) (palmes, sarmentum <i>Col</i>)."],
  ["pampinatio", "<big>pampinatio</big>\n\n<b>pampinatio, onis</b> <i>f</i> [pampino]\nобламывание лишних побегов с листьями на виноградных лозах <i>Col, PM</i>."],
  ["pampinator", "<big>pampinator</big>\n\n<b>pampinator, oris</b> <i>m</i> [pampino]\nобламывающий лишние побеги с листьями на виноградных лозах <i>Col</i>."],
  ["pampinatus", "<big>pampinatus</big>\n\n<b>pampinatus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> pampino;\n<b>2.</b> <i>adj.</i> имеющий вид виноградной ветви, <i>т. е.</i> вьющийся (orbis <i>PM</i>); покрытый изображениями виноградных лоз (lanx <i>Treb</i>)."],
  ["pampineus", "<big>pampineus</big>\n\n<b>pampineus, a, um</b> [pampinus]\n<b>1)</b> сплетённый из виноградных листьев (corona <i>T</i>); украшенный виноградными листьями (hasta <i>O</i> = thyrsus); покрытый виноградными листьями (uva <i>O</i>);\n<b>2)</b> богатый виноградом (autumnus <i>V</i>);\n<b>3)</b> обвитый виноградной лозой (ulmus <i>Calp</i>);\n<b>4)</b> винный (odor <i>Prp</i>)."],
  ["pampino", "<big>pampino</big>\n\n<b>pampino, avi, atum, are</b> [pampinus]\nобламывать лишние побеги и листья на виноградных лозах (p. vites <i>Vr</i>); подчищать, подрезывать (salicem <i>Col</i>)."],
  ["pampinosus", "<big>pampinosus</big>\n\n<b>pampinosus, a, um</b> [pampinus]\nвесь обросший виноградными листьями (vitis <i>Col</i>)."],
  ["pampinus", "<big>pampinus</big>\n\n<b>pampinus, i</b> <i>m</i> <i>и</i> <i>f</i>\n1) новый виноградный побег <i>Col, PM</i>;\n<b>2)</b> виноградные листья (p. defendit uvas <i>V</i>);\n<b>3)</b> усик винограда и других ползучих растений <i>PM</i>;\n<b>4)</b> щупальце (полипа) <i>PM</i>."],
  ["Pan", "<big>Pan</big>\n\n<b>Pan, is</b> <i>и</i> <b>os</b> (<i>acc.</i> Pana) <i>m</i>\n<b>1)</b> Пан, <i>бог лесов, пастухов и скота</i> (armenti custos <i>O</i>), <i>сын Меркурия или Юпитера, изображавшийся с козлиными копытами, рогами и хвостом</i> (semicaper <i>O</i>); <i>изобретатель пастушеской семиствольной свирели</i> (syrinx <i>V, C etc.</i>);\n<b>2)</b> <i>pl.</i> <i>лесные и полевые божества</i> (<i>сходные с Паном, т. е. фавны и сильваны</i>) <i>Col, O, Mela</i>."],
  ["panaca", "<big>panaca</big>\n\n<b>panaca, ae</b> <i>f</i>\nкубок, чаша <i>M</i>."],
  ["panacea", "<big>panacea</big>\n\n<b>panacea, ae</b> <i>f</i> (<i>греч.</i>)\nпанацея, <i>баснословное всеисцеляющее растение</i> <i>V</i>."],
  ["panaces", "<big>panaces</big>\n\n<b>panaces, is</b> <i>n</i> <i>CC, PM</i>\n= panacea."],
  ["panacinus", "<big>panacinus</big>\n\n<b>panacinus, a, um</b> [panacea]\nприготовленный из панацеи (oleum <i>CA</i>)."],
  ["Panaetius", "<big>Panaetius</big>\n\n<b>Panaetius, i</b> <i>m</i>\nПанетий, <i>философ-стоик из Родоса, друг Сципиона Младшего и Лелия</i> <i>C, H, VP</i>."],
  ["Panaetolicus", "<big>Panaetolicus</big>\n\n<b>Panaetoli(c)us, a, um</b> (<i>греч.</i>)\nвсеэтолийский (concilium <i>L</i>)."],
  ["panaricium", "<big>panaricium</big>\n\n<b>panaricium, i</b> <i>n</i> (<i>испорч. греч.</i>) <i>мед.</i>\nногтоеда <i>Ap</i>."],
  ["panariolum", "<big>panariolum</big>\n\n<b>panariolum, i</b> <i>n</i> [<i>demin. к</i> panarium]\nмаленькая корзинка для хлеба <i>M</i>."],
  ["panarium", "<big>panarium</big>\n\n<b>panarium, i</b> <i>n</i> [panis]\nхлебная корзина <i>Vr, PJ, Su, St</i>."],
  ["Panathenaicus", "<big>Panathenaicus</big>\n\n<b>Panathenaicus, i</b> <i>m</i> (<i>sc.</i> logos)\n<i>похвальная речь, произнесённая Исократом на празднике Панафиней в 339 г. до н. э.</i> <i>C</i>."],
  ["panax", "<big>panax</big>\n\n<b>panax, acis</b> <i>m</i> <i>Col</i>\n= panacea."],
  ["pancarpineus", "<big>pancarpineus</big>\n\n<b>pancarpineus, a, um</b> (<i>греч.</i>)\nприготовленный из всевозможных плодов (cibus <i>Vr</i>)."],
  ["pancarpius", "<big>pancarpius</big>\n\n<b>pancarpius</b> <i>Tert</i> <i>и</i> <b>pancarpus, a, um</b> <i>Aug</i>\n= pancarpineus."],
  ["Panchaeus", "<big>Panchaeus</big>\n\n<b>Panchaeus, a, um</b> <i>Lcn</i>\n= Panchaius."],
  ["Panchaia", "<big>Panchaia</big>\n\n<b>Panchaia, ae</b> <i>f</i>\nПанхайя <i>или</i> Панхея, <i>легендарный о-в у берегов Arabia Felix</i> <i>Tib, V, VF, PM</i>."],
  ["Panchaius", "<big>Panchaius</big>\n\n<b>Panchaius, a, um</b> [Panchaia]\nпанхейский (tellus <i>O</i>; tura <i>V</i>)."],
  ["panchrestus", "<big>panchrestus</big>\n\n<b>panchrestus, a, um</b> (<i>греч.</i>)\nпригодный для всего, универсальный (medicamentum <i>C, PM</i>)."],
  ["panchromos", "<big>panchromos</big>\n\n<b>panchromos, i</b> <i>m</i> (<i>греч.</i>; <i>лат.</i> Verbenaca)\nвербена обыкновенная, железняк (<i>Verbena officinalis</i>, L.) <i>Ap</i>."],
  ["pancratiastes", "<big>pancratiastes</big>\n\n<b>pancratiastes, ae</b> <i>m</i> [pancration]\nпанкратиаст, универсальный борец, всеборец <i>PM, Q, AG</i>."],
  ["pancratice", "<big>pancratice</big>\n\n<b>pancratice</b> [pancration]\nкак панкратиаст: p. valere <i>Pl</i> быть атлетически здоровым."],
  ["pancration", "<big>pancration</big>\n\n<b>pancration (pancratium), i</b> <i>n</i> (<i>греч.</i>)\n<b>1)</b> панкратий, универсальная борьба, всеборье, <i>т. е.</i> борьба (lucta), комбинированная с кулачным боем (pugilatus) <i>Prp, Sen, PM</i>;\n<b>2)</b> <i>бот.</i> цикорий <i>PM</i>."],
  ["Panda", "<big>Panda</big>\n\n<b>I Panda, ae</b> <i>f</i>\nПанда, <i>римск. богиня, иногда отождествлявшаяся с Церерой</i> <i>Vr ap. AG</i>.\n\n<b>II Panda, ae</b> <i>m</i>\nПанда, <i>река в Скифии</i> <i>T</i>."],
  ["Pandataria", "<big>Pandataria</big>\n\n<b>Pandataria (Pandateria), ae</b> <i>f</i>\nПандатария, <i>островок у берегов Кампании; в императорскую эпоху — место ссылки</i> <i>Vr, PM, T, Su</i>."],
  ["pandatio", "<big>pandatio</big>\n\n<b>pandatio, onis</b> <i>f</i> [pando I]\nкоробление (<i>о дереве</i>) <i>Vtr</i>."],
  ["pandectae", "<big>pandectae</big>\n\n<b>pandectae, arum</b> <i>f</i> (<i>греч.</i>)\nпандекты, <i>составленные по указу Юстиниана сборники законоположений из сочинений виднейших римск. юристов</i> (<i>533 г. н. э.</i>) <i>CJ</i>."],
  ["pandemus", "<big>pandemus</big>\n\n<b>pandemus, a, um</b> (<i>греч.</i>)\nвсеобщий, эпидемический (lues <i>Amm</i>)."],
  ["pandiculor", "<big>pandiculor</big>\n\n<b>pandiculor, —, ari</b> <i>depon.</i> [pando]\nпотягиваться (pandiculans oscitatur <i>Pl</i>)."],
  ["Pandion", "<big>Pandion</big>\n\n<b>Pandion, onis</b> <i>m</i>\nПандион, <i>миф. царь Афин, сын Эрихтония, отец Эрехтея, Бута, Прокны и Филомелы</i>: Pandionis populus <i>Lcr</i> = афиняне; Pandione nata <i>O</i> = Прокна <i>и</i> Филомела."],
  ["Pandionius", "<big>Pandionius</big>\n\n<b>Pandionius, a, um</b> [Pandion]\nпандионов: Pandioniae volucres <i>SenT</i> = соловей (Philomela) и ласточка (Progne); || <i>поэт.</i> афинский <i>O, Cld, St</i>."],
  ["pando", "<big>pando</big>\n\n<b>I pando, avi, atum, are</b> [pandus]\n<b>1)</b> сгибать (manus leviter pandata <i>Q</i>; pandari onere fructuum <i>Col</i>);\n<b>2)</b> сгибаться, гнуться (ulnus celeriter pandat <i>Vtr</i>).\n\n<b>II pando, pandi, passum (pansam), ere</b>\n<b>1)</b> распускать (vela <i>C, O</i>; crines <i>Cs</i>): capillus passus <i>Ter</i> <i>и</i> passi capilli <i>Pt</i> распущенные волосы; conscribere passis verbis <i>Ap</i> писать прозой || расправлять, раскрывать (pennas ad solem <i>V</i>); расширять, развёртывать (aciem <i>L</i>): aliquid in orbem p. <i>Pt</i> дугообразно изгибать что-л. || распростирать, раздвигать: passis manibus (<i>или</i> palmis) <i>Cs</i> с распростёртыми руками;\n<b>2)</b> распространять (longe lateque se p. <i>C</i>);\n<b>3)</b> открывать, распахивать (januam <i>Pl</i>); широко раскрывать, разевать (guttura <i>V</i>);\n<b>4)</b> пробивать, прорезывать (rupem ferro <i>L</i>); вспахивать (agros <i>Lcr</i>);\n<b>5)</b> раскладывать для просушки, сушить (uvam in sole <i>Col</i>): racemi passi <i>V</i>, <i>тж.</i> uva passa <i>Pl, Pt</i> изюм; lac passum <i>O</i> сыр; senex passus <i>шутл. LM</i> высохший старик;\n<b>6)</b> простирать, расстилать (mare, planities panditur <i>L</i>); пролагать, прокладывать (viam ad dominationem <i>L</i>);\n<b>7)</b> возвещать, объявлять (oraculum <i>Ctl</i>; nomen <i>O</i>; opus versibus p. <i>Pt</i>)."],
  ["Pandora", "<big>Pandora</big>\n\n<b>Pandora, ae</b> (<i>gen.</i> <i>тж.</i> as) <i>f</i> (<i>греч.</i>)\nПандора, <i>первая женщина, созданная, по указанию Юпитера, Вулканом и одарённая всеми богами так, чтобы навлечь несчастья на человеческий род за похищенный для него Прометеем огонь</i> <i>PM, Tert</i>."],
  ["pandorium", "<big>pandorium</big>\n\n<b>pandorium, i</b> <i>n</i> <i>и</i> <b>pandorius, i</b> <i>m</i> <i>Is</i>\n= pandura."],
  ["Pandosia", "<big>Pandosia</big>\n\n<b>Pandosia, ae</b> <i>f</i>\nПандосия :\n<b>1)</b> <i>город в Феспротии</i> (<i>Эпир</i>) <i>PM</i>;\n<b>2)</b> <i>город в Бруттии, на границе с Луканией</i> <i>L, PM, Just</i>."],
  ["Pandrosos", "<big>Pandrosos</big>\n\n<b>Pandrosos, i</b> <i>f</i>\nПандроса, <i>одна из трёх дочерей афинского царя Кекропа</i> <i>O</i>."],
  ["pandura", "<big>pandura</big>\n\n<b>pandura, ae</b> <i>f</i> (<i>греч.</i>)\nпандура, <i>трёхструнный музыкальный инструмент</i> <i>Vr</i>."],
  ["pandurizo", "<big>pandurizo</big>\n\n<b>pandurizo, avi, —, are</b> [pandura]\nиграть па пандуре <i>Lampr</i>."],
  ["pandus", "<big>pandus</big>\n\n<b>pandus, a, um</b>\nсогнутый, изогнутый (carina <i>V</i>; rami, cornua <i>O</i>); искривлённый, кривой (cupressus <i>Vtr</i>); согбенный, сгорбленный, сутулый (homo <i>Q</i>)."],
  ["pane", "<big>pane</big>\n\n<b>pane, is</b> <i>n</i> <i>арх.</i> <i>Pl</i>\n= panis."],
  ["panegyricus", "<big>panegyricus</big>\n\n<b>I panegyricus, a, um</b> (<i>греч.</i>)\nсвязанный с торжественным всенародным собранием, <i>перен.</i> хвалебный (libri <i>Aus</i>).\n\n<b>II panegyricus, i</b> <i>m</i>\n<b>1)</b> (<i>sc.</i> sermo) панегирик, <i>речь Исократа, в которой он призывал греков к единству и к борьбе с персами и восхвалял афинян</i> <i>C, Q</i>;\n<b>2)</b> панегирик, восхваление, похвальное слово <i>Q, Spart</i>."],
  ["panegyrista", "<big>panegyrista</big>\n\n<b>panegyrista, ae</b> <i>m</i> (<i>греч.</i>)\nпанегирист, славословящий <i>Sid</i>."],
  ["Pangaea", "<big>Pangaea</big>\n\n<b>I Pangaea, orum</b> <i>n</i> <i>и</i> <b>Pangaeus mons</b>\nПангей, <i>горная цепь в Македонии на границе с Фракией, к вост. от устья Стримона</i> <i>V, PM, Lcn</i>."],
  ["Pangaeus", "<big>Pangaeus</big>\n\n<b>II Pangaeus, a, um</b> [Pangaeus mons]\nпангейский, <i>поэт.</i> фракийский (juga <i>VF</i>; nemora <i>Sil</i>)."],
  ["pango", "<big>pango</big>\n\n<b>pango, pepigi</b> (<i>тж.</i> <b>panxi, pegi</b>), <b>panctum</b> (<i>тж.</i> <b>pactum</b>), <b>ere</b>\n<b>1)</b> вбивать, вколачивать (clavum <i>L</i>); вонзать: litoribus nostris ancora pacta <i>O</i> якорь, брошенный у наших берегов || вдавливать, вырезывать (litteram in cera <i>Col</i>); сажать (ramulos <i>Su</i>);\n<b>2)</b> засаживать, покрывать насаждениями (colles <i>Prp</i>);\n<b>3)</b> замышлять, затевать: temptamenta alicujus (<i>sc.</i> animum alicujus) pepigisse <i>V</i> попытаться проникнуть в чьи-л. намерения;\n<b>4)</b> слагать, сочинять, творить (carmina <i>Lcr</i>; poemata <i>H</i>): pangendi facultas <i>T</i> умение писать стихи || рождать (filios <i>Tert</i>);\n<b>5)</b> воспевать (maxima facta patrum <i>Enn ap. C</i>);\n<b>6)</b> заключать (pacem <i>L</i>; foedera <i>V</i>; amicitiam cum aliquo <i>L</i>);\n<b>7)</b> обусловливать, устанавливать, определять (pretium alicui rei <i>T</i>; fines <i>C</i>): sibi letum pepigisse <i>Cld</i> обречь себя на смерть; mercedem pepigisse <i>Ap</i> выговорить (себе определённое) вознаграждение || обещать (aliquid dare <i>L</i>);\n<b>8)</b> давать слово, обещать: alicui se p. <i>O</i> дать кому-л. слово (стать его женой)."],
  ["Panhorm-", "<big>Panhorm-</big>\n\n<b>Panhorm-</b> <i>v. l.</i>\n= Panorm-."],
  ["paniceus", "<big>paniceus</big>\n\n<b>paniceus, a, um</b> [panis]\nхлебный: milites panicei <i>шутл. Pl</i> солдаты из Хлеб-города, <i>т. е.</i> поставщики хлеба."],
  ["panicula", "<big>panicula</big>\n\n<b>panicula, ae</b> <i>f</i> [panus]\n<b>1)</b> кисть, метёлка, пучок (<i>у растений</i>) <i>PM</i>;\n<b>2)</b> род опухоли <i>или</i> нарыва <i>Ap, Scr</i>."],
  ["paniculus", "<big>paniculus</big>\n\n<b>paniculus, i</b> <i>m</i> [<i>demin. к</i> panus]\nпучок камыша: p. tectorius <i>Pl</i> кровельный камыш."],
  ["panicum", "<big>panicum</big>\n\n<b>panicum, i</b> <i>n</i>\nитальянское просо (<i>Panicum italicum</i>, L.) <i>Cato, Cs, PM</i>."],
  ["panifica", "<big>panifica</big>\n\n<b>pani–fica, ae</b> <i>f</i> [panis + facio]\nбулочница <i>Vlg</i>."],
  ["panificium", "<big>panificium</big>\n\n<b>panificium, i</b> <i>n</i> [panis + facio]\n<b>1)</b> хлебопечение <i>Vr</i>;\n<b>2)</b> хлебное изделие (<i>хлеб, пирожное</i>) <i>CC</i>;\n<b>3)</b> <i>культ.</i> жертвенный пирог <i>Su, Just</i>."],
  ["Panionium", "<big>Panionium</big>\n\n<b>Panionium, i</b> <i>n</i>\nПанионий, <i>священная местность на северном склоне мыса Микале, где происходили собрания представителей всех 12 городов Ионийского союза</i> <i>Mela</i>."],
  ["Panionius", "<big>Panionius</big>\n\n<b>Panionius, a, um</b>\nвсеионийский (Apollo <i>Vtr</i>)."],
  ["panis", "<big>panis</big>\n\n<b>panis, is</b> (<i>gen. pl.</i> um <i>и</i> ium) <i>m</i> [pasco]\n<b>1)</b> хлеб: p. cibarius <i>C</i> (secundus <i>H</i>, ater <i>Ter</i>) простой хлеб (<i>из темной, грубого помола муки</i>); habet haec res panem <i>Pt</i> это — доходное («хлебное») занятие; p. azymus <i>Scr</i> опреснок;\n<b>2)</b> масса в форме хлебного каравая, слиток, кусок (p. aeris <i>St</i>)."],
  ["Paniscus", "<big>Paniscus</big>\n\n<b>Paniscus, i</b> <i>m</i> [<i>demin. к</i> Pan]\nПаниск, <i>сельское божество</i> (Nymphae, Panisci et Satyri <i>C</i>)."],
  ["pannaria", "<big>pannaria</big>\n\n<b>pannaria, orum</b> <i>n</i> [pannus]\nпреподносимые в подарок ткани <i>St</i>."],
  ["panneus", "<big>panneus</big>\n\n<b>panneus, a, um</b> [pannus]\nизношенный, рваный (vestis <i>PM</i>)."],
  ["pannicularia", "<big>pannicularia</big>\n\n<b>pannicularia, ium</b> <i>n</i>\nветхая одежда, лохмотья <i>Dig</i>."],
  ["panniculus", "<big>panniculus</big>\n\n<b>panniculus, i</b> <i>m</i> [<i>demin. к</i> pannus]\n<b>1)</b> кусок ткани, лоскут (pila ex panniculis facta <i>CC</i>);\n<b>2)</b> <i>презр.</i> платье, тряпьё (bombycinus <i>J</i>)."],
  ["Pannonia", "<big>Pannonia</big>\n\n<b>Pannonia, ae</b> <i>f</i>\nПаннония, <i>область между Дакией, Нориком и Иллирией</i> (<i>прибл. совпадает с нын. Венгрией</i>) <i>O, PM, PJ</i>."],
  ["Pannoniacus", "<big>Pannoniacus</big>\n\n<b>Pannoni(a)cus, a, um</b> [Pannonia]\nпаннонийский <i>PM, M, Spart</i>."],
  ["Pannonis", "<big>Pannonis</big>\n\n<b>Pannonis, idis</b> <i>f</i> [Pannonia]\nпаннонийская (ursa <i>Lcn</i>)."],
  ["Pannonius", "<big>Pannonius</big>\n\n<b>I Pannonius, a, um</b> <i>Tib</i>\n= Pannoni(a)cus.\n\n<b>II Pannonius, i</b> <i>m</i>\nжитель области Pannonia, <i>VP, Su, T etc.</i>"],
  ["pannositas", "<big>pannositas</big>\n\n<b>pannositas, atis</b> <i>f</i>\nизорванность, взлохмаченность (cutis <i>CA</i>)."],
  ["pannosus", "<big>pannosus</big>\n\n<b>pannosus, a, um</b> [pannus]\n<b>1)</b> в лохмотьях, оборванный (homo <i>C etc.</i>);\n<b>2)</b> бедный, жалкий, ветхий (resculae <i>Ap</i>);\n<b>3)</b> увядший, морщинистый (mammae <i>M</i>);\n<b>4)</b> сгустившийся (faex aceti <i>Pers</i>)."],
  ["pannucea", "<big>pannucea</big>\n\n<b>pannucea (-ia), ae</b> <i>f</i>\nлохмотья, тряпьё <i>Ap, Pt, Is</i>."],
  ["pannuceus", "<big>pannuceus</big>\n\n<b>pannuceus (-ius), a, um</b> <i>Pers, Pt, PM</i>\n= pannosus."],
  ["pannuli", "<big>pannuli</big>\n\n<b>pannuli, orum</b> <i>m</i> [pannus]\nлохмотья <i>Ap, Amm</i>."],
  ["pannus", "<big>pannus</big>\n\n<b>pannus, i</b> <i>m</i>\n<b>1)</b> кусок ткани, лоскут (lineus <i>PM</i>; purpureus <i>H</i>);\n<b>2)</b> лохмотья, тряпьё, ветошь <i>Pt</i>; pannis annisque obsitus <i>Ter</i> оборванный и дряхлый;\n<b>3)</b> мешок (p. in quo faba erat reposita <i>Pt</i>)."],
  ["pannuvellium", "<big>pannuvellium</big>\n\n<b>pannuvellium, i</b> <i>n</i> [pannus + volvo]\nверетено с намотанной пряжей <i>Vr</i>."],
  ["pannychismus", "<big>pannychismus</big>\n\n<b>pannychismus, i</b> <i>m</i> (<i>греч.</i>)\nвсенощное бдение <i>Eccl</i>."],
  ["pannychius", "<big>pannychius</big>\n\n<b>pannychius, a, um</b> (<i>греч.</i>)\nдлящийся всю ночь (negotium <i>Fronto</i>)."],
  ["Panomphaeus", "<big>Panomphaeus</big>\n\n<b>Panomphaeus, i</b> <i>m</i> (<i>греч.</i> «всевозвещающий»)\n<i>эпитет Юпитера как вдохновителя всех оракулов</i> <i>O</i>."],
  ["Panope", "<big>Panope</big>\n\n<b>I Panope, es</b> <i>и</i> <b>Panopea, ae</b> <i>f</i>\nПанопа, <i>одна из Нереид</i> <i>V</i>.\n\n<b>II Panope, es</b> <i>f</i>\nПанопа, <i>город в южн. Фокиде на реке Кефис</i> <i>O, St</i>."],
  ["Panormitanus", "<big>Panormitanus</big>\n\n<b>Panormitanus, a, um</b> [Panormus]\nпанормитанский <i>C</i>."],
  ["Panormum", "<big>Panormum</big>\n\n<b>Panormum, i</b> <i>n</i> <i>и</i> <b>Panormus, i</b> <i>f</i>\nПанорм:\n<b>1)</b> <i>город на сев.-зап. побережье Сицилии</i> (<i>ныне</i> Palermo) <i>C, PM, Mela etc.</i>;\n<b>2)</b> <i>город на о-ве Самосе</i> <i>L</i>."],
  ["panosus", "<big>panosus</big>\n\n<b>panosus, a, um</b> [panis]\nхлебообразный (cibus <i>CA</i>)."],
  ["pansa", "<big>pansa</big>\n\n<b>I pansa, ae</b> <i>m</i> [pando II]\nчеловек с широкими <i>или</i> плоскими ступнями <i>Pl</i>.\n\n<b>II Pansa, ae</b> <i>m</i>\nПанса, <i>cognomen в роде Вибиев</i> <i>C, PM</i>."],
  ["pansus", "<big>pansus</big>\n\n<b>pansus, a, um</b> <i>part. pf. к</i>\npando II."],
  ["pantagathus", "<big>pantagathus</big>\n\n<b>pantagathus, i</b> <i>m</i> (<i>греч.</i>)\n<b>1)</b> птица, предвещающая счастье <i>Lampr</i>;\n<b>2)</b> <i>бот. Ap</i> = pulejum."],
  ["Pantagias", "<big>Pantagias</big>\n\n<b>Pantagias</b> <i>и</i> <b>Pantagies, ae</b> <i>m</i>\nПантагий, <i>речка в Сицилии между Сиракузами и Катаной</i> (<i>ныне</i> Fiume di Porcari) <i>V, O</i>."],
  ["pantex", "<big>pantex</big>\n\n<b>pantex, icis</b> <i>m</i> <i>Priap</i>\n= pantices 1."],
  ["Pantheon", "<big>Pantheon</big>\n\n<b>Pantheon (-um), i</b> <i>n</i>\nПантеон:\n<b>1)</b> <i>храм Юпитера, построенный Агриппой и восстановленный при Адриане, М. Аврелии, Септимии Севере и Каракалле</i> (<i>ныне</i> Tempio di S. Maria Rotonda) <i>PM, Spart, Macr, Amm</i>;\n<b>2)</b> <i>статуя, в которой воплощены многие боги</i> <i>Aus</i>."],
  ["panther", "<big>panther</big>\n\n<b>panther, eris</b> <i>m</i> (<i>греч.</i>)\nбольшая звероловная сеть <i>Vr</i>."],
  ["panthera", "<big>panthera</big>\n\n<b>panthera, ae</b> <i>f</i> (<i>греч.</i>)\n<b>1)</b> охотничья добыча, весь улов <i>Dig</i>;\n<b>2)</b> пантера <i>или</i> барс <i>O, C, L</i>: confusa p. camelo <i>H</i> = pantherocamelus."],
  ["pantherinus", "<big>pantherinus</big>\n\n<b>pantherinus, a, um</b> [panthera]\n<b>1)</b> барсовый (pellis <i>PM</i>);\n<b>2)</b> пятнистый как пантера (mensae <i>PM</i>);\n<b>3)</b> <i>шутл.</i> избитый, пятнистый от побоев, <i>т. е.</i> ловкий, хитрый (genus nominum <i>Pl</i>)."],
  ["pantheris", "<big>pantheris</big>\n\n<b>pantheris, is</b> <i>f</i>\nпантера-самка <i>Vr</i>."],
  ["pantherocamelus", "<big>pantherocamelus</big>\n\n<b>pantherocamelus, i</b> <i>m</i> (<i>греч.</i>)\nжираф <i>LM</i>."],
  ["Pantheum", "<big>Pantheum</big>\n\n<b>Pantheum</b> <i>v. l.</i>\n= Pantheon."],
  ["Panthoides", "<big>Panthoides</big>\n\n<b>Panthoides, ae</b> <i>m</i>\n<b>1)</b> сын Панфоя, <i>т. е.</i> Euphorbus <i>O</i>;\n<b>2)</b> <i>H</i> = Пифагор (<i>утверждавший, что в него переселилась душа Эвфорба</i>)."],
  ["Panthous", "<big>Panthous</big>\n\n<b>Panthous</b> <i>и</i> <b>Panthus, i</b> (<i>voc.</i> u) <i>m</i>\nПанфой, <i>сын Отриса, племянник Гекубы, отец Эвфорба и Полидаманта, жрец Аполлона в Дельфах, а впоследствии в Трое</i> <i>V</i>."],
  ["Panticapaeon", "<big>Panticapaeon</big>\n\n<b>Panticapaeon (-um), i</b> <i>n</i>\nПантикапей, <i>город в Херсонесе Таврическом, прибл. на месте нын. Керчи</i> <i>Mela, PM</i>."],
  ["pantices", "<big>pantices</big>\n\n<b>pantices, um</b> <i>m</i>\n<b>1)</b> живот, брюхо <i>или</i> кишки <i>Pl, M, Priap</i>;\n<b>2)</b> колбаса <i>V</i>."],
  ["Pantilius", "<big>Pantilius</big>\n\n<b>Pantilius, i</b> <i>m</i> (<i>греч.</i> «всё растерзывающий»)\n<i>вымышленное имя плохого поэта и злостного клеветника</i> <i>H</i>."],
  ["Pantolabus", "<big>Pantolabus</big>\n\n<b>Pantolabus, i</b> <i>m</i> (<i>греч.</i> «всё хватающий»)\n<i>имя парасита</i> <i>Pl</i>."],
  ["pantomima", "<big>pantomima</big>\n\n<b>pantomima, ae</b> <i>f</i> (<i>греч.</i>)\nпантомимическая актриса, танцовщица <i>Sen</i>."],
  ["pantomimicus", "<big>pantomimicus</big>\n\n<b>pantomimicus, a, um</b> (<i>греч.</i>)\nпантомимический (ornamenta <i>Sen</i>)."],
  ["pantomimus", "<big>pantomimus</big>\n\n<b>pantomimus, i</b> <i>m</i> (<i>греч.</i>)\n<b>1)</b> пантомимический актёр, танцор <i>Sen, Su, PJ, Vop etc.</i>;\n<b>2)</b> пантомима <i>PM</i>."],
  ["pantophobos", "<big>pantophobos</big>\n\n<b>pantophobos, on</b> (<i>греч.</i>) <i>CA</i>\n= omnipavus."],
  ["panus", "<big>panus</big>\n\n<b>panus, i</b> <i>m</i> (<i>греч.</i>)\n<b>1)</b> пряжа, намотанная на веретено <i>LM</i>;\n<b>2)</b> род опухоли <i>CC, PM</i>;\n<b>3)</b> метёлка проса <i>PM</i>."],
  ["panxi", "<big>panxi</big>\n\n<b>panxi</b> <i>pf. к</i>\npango."],
  ["papa", "<big>papa</big>\n\n<b>I papa (pappa), ae</b> <i>f</i>\n<i>детское слово, обозначающее еду</i> <i>Vr</i>.\n\n<b>II papa, ae</b> <i>m</i>\n<b>1)</b> <i>Tert</i> = papas;\n<b>2)</b> отец, <i>почётное звание епископов</i> <i>Eccl</i>."],
  ["papae", "<big>papae</big>\n\n<b>papae!</b> <i>interj.</i> (<i>греч.</i>)\n<i>возглас удивления</i> ба!, да ну!, вот как! <i>Pl, Ter, Pers</i>."],
  ["paparium", "<big>paparium</big>\n\n<b>paparium, i</b> <i>n</i>\nдетская каша <i>Sen</i>."],
  ["papas", "<big>papas</big>\n\n<b>papas, ae</b> <i>m</i> (<i>греч.</i>)\nнаставник, воспитатель <i>J</i>."],
  ["papaver", "<big>papaver</big>\n\n<b>papaver, eris</b> <i>n</i>\n<b>1)</b> (<i>арх. <i>Cato, Pl, Vr</i> m</i>) мак <i>PM, L, Pt etc.</i>: papaverum capita <i>L, PM, V, O</i> маковые головки, цветы <i>или</i> семена;\n<b>2)</b> мелкие зёрнышки, семечки (p. fici <i>Tert</i>)."],
  ["papaveratus", "<big>papaveratus</big>\n\n<b>papaveratus, a, um</b> [papaver]\nбелёный маком (toga <i>PM</i>)."],
  ["papavereus", "<big>papavereus</big>\n\n<b>papavereus, a, um</b> [papaver]\nмаковый: papavereae comae <i>O</i> венок из маков."],
  ["Paphie", "<big>Paphie</big>\n\n<b>Paphie, es</b> <i>f</i>\nжительница Пафоса, <i>т. е.</i> Венера <i>M</i>."],
  ["Paphius", "<big>Paphius</big>\n\n<b>Paphius, a, um</b> [Paphus II]\n<b>1)</b> пафийский, <i>поэт.</i> кипрский (Venus <i>T</i>): P. heros <i>O</i> = Pygmalion; Paphii thyrsi <i>Col</i> кипрский латук;\n<b>2)</b> посвященный Венере (lampades <i>St</i>; columbae <i>M</i>)."],
  ["Paphlagon", "<big>Paphlagon</big>\n\n<b>Paphlago(n), onis</b> <i>m</i>\nпафлагонец <i>Pl, Mela, Nep etc.</i>"],
  ["Paphlagonia", "<big>Paphlagonia</big>\n\n<b>Paphlagonia, ae</b> <i>f</i>\nПафлагония, <i>область на сев. Малой Азии между Вифинией и Понтом</i> <i>C, Mela, L etc.</i>"],
  ["Paphus", "<big>Paphus</big>\n\n<b>I Paphus (-os), i</b> <i>m</i>\nПаф, <i>сын (по др., дочь) Пигмалиона, основатель города Paphus</i> <i>O</i>.\n\n<b>II Paphus (-os), i</b> <i>f</i>\nПаф, <i>приморский город на юго.-зап. Кипра со знаменитым храмом Венеры</i> <i>C, H, V</i>."],
  ["papilio", "<big>papilio</big>\n\n<b>papilio, onis</b> <i>m</i>\n<b>1)</b> бабочка, мотылёк <i>Col, PM, O, M</i>;\n<b>2)</b> палатка, шатёр (apertis papiliortibus cenare <i>Lampr</i>)."],
  ["papiliunculus", "<big>papiliunculus</big>\n\n<b>papiliunculus, i</b> <i>m</i> [<i>demin. к</i> papilio]\nмотылечек <i>Tert</i>."],
  ["papilla", "<big>papilla</big>\n\n<b>papilla, ae</b> <i>f</i>\n<b>1)</b> грудной сосок <i>PM, PJ</i>;\n<b>2)</b> грудь <i>Pl, Ctl, V, O, J</i>;\n<b>3)</b> бутон розы <i>PV</i>."],
  ["papillatus", "<big>papillatus</big>\n\n<b>papillatus, a, um</b> [papilla]\nимеющий форму бутона (corymbus <i>Hier</i>)."],
  ["Papinianus", "<big>Papinianus</big>\n\n<b>Papinianus, i</b> <i>m</i>\n(Aemilius) Папиниан, <i>один из крупнейших римских юристов, друг Септимия Севера, казнённый Каракаллой</i> <i>Spart</i>."],
  ["Papirianus", "<big>Papirianus</big>\n\n<b>Papirianus, a, um</b> [Papirius]\nпапириев <i>C, L, PM</i>."],
  ["Papirius", "<big>Papirius</big>\n\n<b>I Papirius</b> (<i>арх.</i> <b>Papisius</b>), <b>a, um</b>\nПапирий, <i>римск. nomen; наиболее известны</i>:\n<b>1)</b> L. P. Cursor, <i>герой Самнитской войны 325 г. до н. э.; был пять раз консулом и два раза диктатором</i> <i>L, AV</i>;\n<b>2)</b> C. P. Carbo, <i>народный трибун в 131 г. до н. э., сторонник Т. Гракха, в 120 г., будучи консулом, перешёл на сторону оптиматов</i> <i>C, VM, VP</i>;\n<b>3)</b> Gn. P. Carbo, <i>потерпевший поражение от кимбров и тевтонов при Порее</i> (<i>113 г. до н. э.</i>) <i>T, Fl</i>.\n\n<b>II Papirius, a, um</b> <i>PM, L, VM</i>\n= Papirianus."],
  ["Papius", "<big>Papius</big>\n\n<b>Papius, a, um</b>\nПапий, <i>римск. nomen; наиболее известны</i>:\n<b>1)</b> C. P. Celsus, <i>из Ланувия, отец Милона</i><i>C</i>;\n<b>2)</b> C. P., <i>народный трибун в 65 г. до н. э., автор lex Papia de peregrinis exterminandis</i> <i>C</i>;\n<b>3)</b> M. P. Mutilus, <i>консул в 9 г. до н. э., провёл со своим коллегой Поппеем Сабином lex Julia et Papia Poppaea de maritandis ordinibus</i> <i>T</i>."],
  ["papo", "<big>papo</big>\n\n<b>papo</b> <i>v. l.</i>\n= pappo."],
  ["pappa", "<big>pappa</big>\n\n<b>pappa</b> <i>v. l.</i>\n= papa I."],
  ["pappa-", "<big>pappa-</big>\n\n<b>pappa-</b> <i>v. l.</i>\n= papa-."],
  ["pappo", "<big>pappo</big>\n\n<b>pappo, —, —, are</b> (<i>на детском языке</i>)\nесть, кушать <i>Pl, Pers</i>."],
  ["pappus", "<big>pappus</big>\n\n<b>pappus (-os), i</b> <i>m</i> (<i>греч.</i>)\n<b>1)</b> старик <i>Vr</i>;\n<b>2)</b> дед (pappi aviaeque <i>Aus</i>);\n<b>3)</b> пушистое семя, летучка <i>Lcr, PM</i>."],
  ["papula", "<big>papula</big>\n\n<b>papula, ae</b> <i>f</i>\nпрыщ, волдырь <i>V, CC, PM</i>: papulas observatis alienas obsiti plurimis ulceribus <i>погов. Sen</i> вы, (сами) покрытые множеством язв, высматриваете чужие волдыри (<i>т. е.</i> видите сучок в чужом глазу, а в своём не видите бревна)."],
  ["papulo", "<big>papulo</big>\n\n<b>papulo, —, —, are</b> [papula]\nвызывать сыпь <i>CA</i>."],
  ["papyraceus", "<big>papyraceus</big>\n\n<b>papyraceus, a, um</b> [papyrus]\nприготовленный из папируса (navis <i>PM</i>)."],
  ["papyrifer", "<big>papyrifer</big>\n\n<b>papyrifer, fera, ferum</b> [papyrus + fero]\nпроизводящий (дающий) папирус (Nilus <i>O</i>)."],
  ["papyrinus", "<big>papyrinus</big>\n\n<b>papyrinus, a, um</b> [papyrus]\nпапирусовый (stilus <i>Vr</i>)."],
  ["papyrio", "<big>papyrio</big>\n\n<b>papyrio, onis</b> <i>m</i>\nзаросли папируса <i>Vlg</i>."],
  ["papyrius", "<big>papyrius</big>\n\n<b>papyrius, a, um</b> <i>Aus</i>\n= papyraceus."],
  ["papyrum", "<big>papyrum</big>\n\n<b>papyrum, i</b> <i>n</i> <i>CC, PM</i>\n= papyrus."],
  ["papyrus", "<big>papyrus</big>\n\n<b>papyrus, i</b> <i>m, f</i> (<i>греч.</i>)\n<b>1)</b> папирус (<i>Cyperus Papyrus</i>, L.) <i>Lcn, J, Sen, M</i>;\n<b>2)</b> одежда из папируса <i>J</i>;\n<b>3)</b> бумага из папируса <i>PM, Ctl, M</i>."],
  ["par", "<big>par</big>\n\n<b>I par, paris</b> <i>adj.</i>\n<b>1)</b> равный, одинаковый (virtutes inter se pares <i>C</i>): pari modo (ratione) <i>C etc.</i> одинаково, точно так же; p. et aequalis (aequus) <i>C</i> совершенно одинаковый; pari intervallo <i>Cs</i> на равном расстоянии; p. (in) aliqua re <i>O, V</i> <i>или</i> ad aliquid <i>L</i> равный в каком-л. отношении; p. in amore <i>Prp</i> пользующийся взаимностью в любви; paribus alis nitens <i>V</i> опираясь на равно распростёртые крылья, <i>т. е.</i> паря в воздухе; p. annis <i>O</i> (aetate <i>V</i>) ровесник; viri doctrina et eloquentia paribus <i>Ap</i> люди равные по учёности и красноречию; p. alicui <i>Cs</i> (adversus aliquem <i>Just</i>) могущий противостоять (не уступающий) кому-л.;\n<b>2)</b> соответствующий, подходящий, подобающий, приличествующий (oratio rebus p. <i>C</i>; p. delicto poena <i>O</i>): ut p. est <i>C</i> как подобает; alicui parem gratiam referre <i>Pt</i> должным образом отблагодарить кого-л.; p. negotiis <i>T</i> деловой человек; p. deo dignum (<i>sc.</i> spectaculum) <i>Sen</i> зрелище, достойное божества; male merenti p. erit <i>Pl</i> дурно поступающему воздастся по заслугам; paria facere <i>Sen etc.</i> расплачиваться;\n<b>3)</b> чётный: p. impar ludere <i>H, Su</i> играть в чёт и нечет;\n<b>4)</b> одинаково сильный (animo ac viribus <i>L</i>); одинаково искусный (in utriusque orationis facultate <i>C</i>): paria componere <i>Pt</i> группировать (противников) парами; pares cantare <i>V</i> равные в искусстве петь.\n\n<b>II par, paris</b>\n<b>1.</b> <i>m, f</i>\n<b>1)</b> сверстник, товарищ, ровесник <i>Pl, Pt</i>;\n<b>2)</b> собутыльник <i>Pl</i>;\n<b>3)</b> супруг (cum pare suo <i>O</i>);\n<b>4)</b> противник, соперник (parem Hannibalem habere <i>L</i>);\n<b>2.</b> <i>n</i>\n<b>1)</b> равное: p. pari (paria paribus) respondere <i>Pl, C</i> <i>или</i> p. pari referre <i>Ter</i> воздавать тем же; ex pari <i>Ap, Sen</i> равным образом;\n<b>2)</b> пара, двое (tria paria amicorum <i>C</i>; p. columbarum <i>O</i>)."],
  ["parabilis", "<big>parabilis</big>\n\n<b>parabilis, e</b> [paro I]\nлегко приобретаемый, достающийся без большого труда, вполне доступный (divitiae naturae <i>C</i>; parabile est quod natura desiderat <i>Sen</i>)."],
  ["parabola", "<big>parabola</big>\n\n<b>parabola, ae</b> <i>f</i> <i>Vlg</i>\n= parabole."],
  ["parabolani", "<big>parabolani</big>\n\n<b>parabolani, orum</b> <i>m</i> [parabolus]\nрискующие собой, <i>т. е.</i> ухаживающие за заразными больными <i>CTh, CJ</i>."],
  ["parabole", "<big>parabole</big>\n\n<b>parabole, es</b> <i>f</i> (<i>греч.</i>)\n<b>1)</b> сравнение, сопоставление <i>Sen, Q</i>;\n<b>2)</b> назидательное иносказание, притча <i>Vlg, Eccl</i>."],
  ["parabolice", "<big>parabolice</big>\n\n<b>parabolice</b> [parabole]\nиносказательно <i>Hier, Sid</i>."],
  ["parabolus", "<big>parabolus</big>\n\n<b>parabolus, i</b> <i>m</i>\nрискующий своей жизнью; смельчак <i>Eccl</i>."],
  ["paracenterium", "<big>paracenterium</big>\n\n<b>paracenterium, i</b> <i>n</i> (<i>греч.</i>)\nланцет для хирургических проколов <i>или</i> надрезов <i>Veg</i>."],
  ["paracentesis", "<big>paracentesis</big>\n\n<b>paracentesis</b> (<i>acc.</i> im, <i>abl.</i> i) <i>f</i> (<i>греч.</i>) <i>мед.</i>\nпрокол, надрез <i>PM, CA, Veg</i>."],
  ["paracharactes", "<big>paracharactes</big>\n\n<b>paracharactes, ae</b> <i>m</i> (<i>греч.</i>)\nфальшивомонетчик <i>CTh</i>."],
  ["Parachelois", "<big>Parachelois</big>\n\n<b>Parachelois, idis</b> <i>и</i> <b>idos</b> <i>f</i>\nПарахелоида, <i>город в Фессалии на реке Ахелой</i> <i>L</i>."],
  ["paracletus", "<big>paracletus</big>\n\n<b>paracletus, i</b> <i>m</i> (<i>греч.</i>)\nзаступник, утешитель <i>Vlg, Eccl</i>."],
  ["parada", "<big>parada</big>\n\n<b>parada, ae</b> <i>f</i>\nнавес, тент (<i>на судне</i>) <i>Aus, Sid</i>."],
  ["paradigma", "<big>paradigma</big>\n\n<b>paradigma, atis</b> <i>n</i> (<i>греч.</i>)\n(поучительный) пример <i>Tert</i>."],
  ["paradisus", "<big>paradisus</big>\n\n<b>paradisus, i</b> <i>m</i> (<i>перс.</i>)\n<b>1)</b> большой сад, парк <i>Aug</i>;\n<b>2)</b> райский сад, рай, эдем <i>Vlg, Eccl</i>."],
  ["paradoxa", "<big>paradoxa</big>\n\n<b>paradoxa, orum</b> <i>n</i> (<i>греч.</i>; <i>лат.</i> admirabilia)\nпарадоксы, странные мнения, противоречащие общепринятым (p. Stoicorum <i>C</i>)."],
  ["paraenesis", "<big>paraenesis</big>\n\n<b>paraenesis, is</b> <i>f</i> (<i>греч.</i>)\nувещевание, напоминание <i>или</i> указание <i>Vulc</i>."],
  ["Paraetacae", "<big>Paraetacae</big>\n\n<b>Paraetacae, arum</b> <i>m</i>\nжители области Paraetacene <i>Nep</i>."],
  ["Paraetacene", "<big>Paraetacene</big>\n\n<b>Paraetacene, es</b> <i>f</i>\nПаретакена, <i>горная область между Персией и Мидией</i> <i>Pl, QC</i>."],
  ["Paraetaceni", "<big>Paraetaceni</big>\n\n<b>Paraetaceni, orum</b> <i>m</i> <i>PM</i>\n= Paraetacae."],
  ["Paraetonium", "<big>Paraetonium</big>\n\n<b>Paraetonium, i</b> <i>n</i>\nПаретоний, <i>портовый город в сев. Африке, между Египтом и Сиртами</i> <i>bAl, O, Fl</i>."],
  ["Paraetonius", "<big>Paraetonius</big>\n\n<b>Paraetonius, a, um</b> [Paraetonium]\nпаретонийский, <i>поэт.</i> египетский (Nilus <i>Sl</i>; litus <i>Cld</i>) <i>или</i> африканский (serpens <i>Sil</i>): Paraetonia urbs <i>Lcn</i> = Александрия."],
  ["paragauda", "<big>paragauda</big>\n\n<b>paragauda, ae</b> <i>и</i> <b>paragaudis, is</b> <i>f</i> (<i>перс.</i>)\n<b>1)</b> кайма, обшивка <i>CTh, CJ</i>;\n<b>2)</b> платье с обшивкой <i>Treb, Vop</i>."],
  ["paragaudius", "<big>paragaudius</big>\n\n<b>paragaudius, a, um</b>\nокаймлённый, обшитый (interula <i>Vop</i>)."],
  ["paragoge", "<big>paragoge</big>\n\n<b>paragoge, es</b> <i>f</i> (<i>греч.</i>) <i>грам.</i>\nпарагога, <i>удлинение слова путём прибавления к нему буквы или слога</i> (<i>напр.</i> facio — facesso)."],
  ["paragogia", "<big>paragogia</big>\n\n<b>paragogia, orum</b> <i>n</i>\nводопроводная система <i>CJ</i>."],
  ["paragramma", "<big>paragramma</big>\n\n<b>paragramma, atis</b> <i>n</i> (<i>греч.</i>)\nописка <i>Hier</i>."],
  ["paralios", "<big>paralios</big>\n\n<b>paralios, on</b> <i>adj.</i> (<i>греч.</i>)\nрастущий у моря, приморский <i>PM</i>."],
  ["paralipomena", "<big>paralipomena</big>\n\n<b>paralipomena, orum</b> <i>n</i> (<i>греч.</i> «пропуски», «пробелы»)\nпаралипомены, <i>название двух книг Библии</i> <i>Vlg, Eccl</i>."],
  ["parallelos", "<big>parallelos</big>\n\n<b>parallelos, on</b> <i>и</i> <b>parallelus, a, um</b> <i>adj.</i> (<i>греч.</i>)\nпараллельный (linea <i>Vtr</i>; circuli <i>Macr</i>)."],
  ["Paralus", "<big>Paralus</big>\n\n<b>Paralus, i</b> <i>m</i>\nПарал, <i>афинский герой, портрет которого был написан Протогеном</i> <i>C, PM</i>."],
  ["paralysis", "<big>paralysis</big>\n\n<b>paralysis, is</b> <i>и</i> <b>eos</b> (<i>acc.</i> in, <i>abl.</i> i) <i>f</i> (<i>греч.</i>; <i>лат.</i> nervorum resolutio)\nрасслабление, паралич <i>PM, Pt</i>."],
  ["paralyticus", "<big>paralyticus</big>\n\n<b>paralyticus, i</b> <i>m</i> [paralysis]\nпаралитик <i>PM, Pt</i>."],
  ["paramese", "<big>paramese</big>\n\n<b>paramese, es</b> <i>f</i> (<i>греч.</i>)\nвторая из пяти струн <i>Vtr</i>."],
  ["parandrus", "<big>parandrus</big>\n\n<b>parandrus, i</b> <i>m</i> <i>v. l.</i>\n= tarandrus."],
  ["parangariae", "<big>parangariae</big>\n\n<b>parangariae, arum</b> <i>f</i>\nдополнительная гужевая повинность <i>CTh</i>."],
  ["paranymphus", "<big>paranymphus</big>\n\n<b>paranymphus, i</b> <i>m</i> (<i>греч.</i>)\nдружка (шафер) невесты <i>Aug</i>."],
  ["Parapam-", "<big>Parapam-</big>\n\n<b>Parapam-</b> <i>v. l.</i>\n= Paropam-."],
  ["parapegma", "<big>parapegma</big>\n\n<b>parapegma, atis</b> (<i>gen. pl.</i> um <i>и</i> orum) <i>n</i> (<i>греч.</i>)\nтаблица с астрономическими вычислениями <i>Vtr</i>."],
  ["parapetasius", "<big>parapetasius</big>\n\n<b>parapetasius, a, um</b> (<i>греч.</i>)\nслужащий для укрытия (<i>от солнца, дождя и т. п.</i>) (aedificia <i>CTh</i>)."],
  ["parapeteuma", "<big>parapeteuma</big>\n\n<b>parapeteuma, atis</b> <i>n</i> (<i>греч.</i>)\nмарка (талон) на право получения хлебного пайка <i>CJ</i>."],
  ["paraphoros", "<big>paraphoros</big>\n\n<b>paraphoros, on</b> <i>adj.</i> (<i>греч.</i>)\nплохой, низкого качества (alumen <i>PM</i>)."],
  ["paraphrasis", "<big>paraphrasis</big>\n\n<b>paraphrasis, is</b> (<i>acc.</i> im, <i>abl.</i> i) <i>f</i> (<i>греч.</i>)\nпарафраза, описание <i>Q</i>."],
  ["paraphrastes", "<big>paraphrastes</big>\n\n<b>paraphrastes, ae</b> <i>m</i>\nпарафраст, автор описательного перевода (пересказа) <i>Hier</i>."],
  ["paraplectus", "<big>paraplectus</big>\n\n<b>paraplectus, a, um</b> (<i>греч.</i>)\nпоражённый частичным параличом <i>CA</i>."],
  ["paraplexia", "<big>paraplexia</big>\n\n<b>paraplexia, ae</b> <i>f</i>\nпараплексия, частичный паралич <i>CA</i>."],
  ["parapsis", "<big>parapsis</big>\n\n<b>parapsis, idis</b> <i>f</i> <i>v. l.</i>\n= paropsis."],
  ["parapycnos", "<big>parapycnos</big>\n\n<b>parapycnos, ī</b> <i>m</i> (<i>греч.</i>)\n<i>стих. стопа</i> U–UUU."],
  ["pararius", "<big>pararius</big>\n\n<b>pararius, i</b> <i>m</i> [paro I]\nпосредник, агент, маклер <i>Sen</i>."],
  ["parasanga", "<big>parasanga</big>\n\n<b>parasanga, ae</b> <i>m</i> (<i>греко-перс.</i>)\nпарасанг, <i>персидская мера длины = ок. 5 км.</i> <i>PM, Hier</i>."],
  ["parasceue", "<big>parasceue</big>\n\n<b>parasceue, es</b> <i>f</i> (<i>греч.</i> «приготовление»)\nканун субботы <i>Vlg, Eccl</i>."],
  ["parasita", "<big>parasita</big>\n\n<b>parasita, ae</b> <i>f</i> [parasitus]\nприхлебательница, приживалка <i>H, PM</i>."],
  ["parasitaster", "<big>parasitaster</big>\n\n<b>parasitaster, tri</b> <i>m</i> [parasitus]\nжалкий прихлебатель, подхалим <i>Ter</i>."],
  ["parasitatio", "<big>parasitatio</big>\n\n<b>parasitatio, onis</b> <i>f</i> [parasitor]\nприхлебательство <i>Pl</i>."],
  ["parasiticus", "<big>parasiticus</big>\n\n<b>parasiticus, a, um</b> [parasitus]\nприхлебательский (ars <i>Pl</i>)."],
  ["parasitor", "<big>parasitor</big>\n\n<b>parasitor, —, ari</b> <i>depon.</i> [parasitus]\nбыть прихлебателем, приживальщиком <i>Pl</i>."],
  ["parasitus", "<big>parasitus</big>\n\n<b>parasitus, i</b> <i>m</i> (<i>греч.</i>)\n<b>1)</b> (<i>лат.</i> conviva) сотрапезник, гость: p. Phoebi <i>M</i> = поклонник поэзии;\n<b>2)</b> прихлебатель, угодливый приживальщик <i>Pl, Ter, C, H</i>: ulmos parasitos facere <i>ирон. Pl</i> дать в прихлебатели вязовые розги."],
  ["parastas", "<big>parastas</big>\n\n<b>parastas, ados</b> <i>f</i> (<i>греч.</i>)\n<b>1)</b> четырёхугольная, вделанная в стену колонна, пилястра <i>Vtr</i>;\n<b>2)</b> вертикальный столб рамы в катапульте и баллисте <i>Vtr</i>."],
  ["parastates", "<big>parastates</big>\n\n<b>parastates, ae</b> <i>и</i> <b>parastatica, ae</b> <i>f</i> <i>Vtr</i>\n= parastas."],
  ["parastichis", "<big>parastichis</big>\n\n<b>parastichis, idis</b> <i>f</i> (<i>греч.</i>)\nакростих <i>Su</i>."],
  ["parasynaxis", "<big>parasynaxis</big>\n\n<b>parasynaxis, is</b> <i>f</i> (<i>греч.</i>)\nтайное (запрещённое) собрание <i>CJ</i>."],
  ["parate", "<big>parate</big>\n\n<b>parate</b> [paratus]\n<b>1)</b> подготовленно, с предварительной подготовкой (venire ad dicendum <i>C</i>);\n<b>2)</b> тщательно (curare <i>Pl</i>);\n<b>3)</b> быстро (respondere <i>PJ</i>)."],
  ["paratio", "<big>paratio</big>\n\n<b>paratio, onis</b> <i>f</i> [paro]\n<b>1)</b> заготовка, приобретение (difficilis alicujus rei p. <i>Dig</i>);\n<b>2)</b> старание приобрести, домогательство (regni <i>Sl</i>)."],
  ["paratragoedo", "<big>paratragoedo</big>\n\n<b>paratragoedo, —, —, are</b> (<i>греч.</i>)\nвыражаться в стиле трагедии, говорить высокопарно <i>Pl</i>."],
  ["paratura", "<big>paratura</big>\n\n<b>paratura, ae</b> <i>f</i> [paro I]\n<b>1)</b> снаряжение (omnis p. tabernaculi <i>Vlg</i>);\n<b>2)</b> приобретение, достояние (nostra noviciola p. <i>Tert</i>)."],
  ["paratus", "<big>paratus</big>\n\n<b>I paratus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> paro;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> подготовленный, приготовившийся, готовый, находящийся в готовности (ad <i>или</i> in aliquid <i>C, Cs etc.</i>, aliquid facere <i>Cs</i> <i>или</i> alicui rei <i>V, L, T</i>); решившийся (ad pericula subeunda <i>Cs</i>); склонный (ad omne facinus <i>C</i>; in res novas <i>T</i>);\n<b>2)</b> готовый, уже прежде созданный (paratas artes cognoscere <i>Pt</i>);\n<b>3)</b> услужливый (paratissimus puer <i>Pt</i>);\n<b>4)</b> привычный, свойственный (humanis ingeniis parata simulatio <i>QC</i>);\n<b>5)</b> хорошо обученный, опытный (ab exercitu <i>C</i>; in rebus maritimis <i>C</i>);\n<b>6)</b> легко достающийся, не стоивший большого труда (victoria <i>L</i>);\n<b>7)</b> хорошо снабжённый, боеспособный (exercitus instructus paratusque <i>L</i>);\n<b>8)</b> укреплённый, защищенный (contra fortunam <i>C</i>; ab omni re <i>C</i>).\n\n<b>II paratus, us</b> <i>m</i> [paro I]\n<b>1)</b> приготовление, подготовка (p. invadendae Syriae <i>T</i>); устройство, организация (vitae <i>C</i>; triumphi <i>O</i>);\n<b>2)</b> <i>pl.</i> одежда, наряд (largis paratibus uti <i>O</i>);\n<b>3)</b> снаряжение (militum <i>Sl</i>)."],
  ["paraveredus", "<big>paraveredus</big>\n\n<b>paraveredus, i</b> <i>m</i> [<i>греч.</i> para + veredus, <i>по др., евр.</i> pered «мул»]\nпочтовая лошадь <i>CJ, Eccl</i>."],
  ["parazonium", "<big>parazonium</big>\n\n<b>parazonium, i</b> <i>n</i> (<i>греч.</i>)\nкороткий меч, кинжал <i>M</i>."],
  ["Parca", "<big>Parca</big>\n\n<b>Parca, ae</b> <i>f</i>\nПарка, <i>богиня судьбы</i> (<i>их было три</i>: Clotho, <i>прявшая нить жизни</i>, Lachesis, <i>тянувшая её, и</i> Atropos, <i>обрезавшая её</i>) <i>C, V, H, O etc.</i>"],
  ["parce", "<big>parce</big>\n\n<b>I parce</b> [parcus]\n<b>1)</b> скупо, экономно, расчётливо, бережливо (vitam agere <i>Ter</i>; vivere <i>Lcr</i>; frumentum metiri <i>Cs</i>): p. parcus <i>Pl</i> крайне бережливый;\n<b>2)</b> мало, редко (scribere <i>C</i>);\n<b>3)</b> слегка (laedere aliquem <i>C</i>).\n\n<b>II parce</b> <i>imper. к</i>\nparco."],
  ["parcepromus", "<big>parcepromus</big>\n\n<b>parcepromus, i</b> <i>m</i> [parcus + promo]\nскряга <i>Pl</i>."],
  ["parciloquium", "<big>parciloquium</big>\n\n<b>parciloquium, i</b> <i>n</i> [parce + loquor]\nсдержанность в словах, неразговорчивость <i>Ap</i>."],
  ["parcimonia", "<big>parcimonia</big>\n\n<b>parcimonia, ae</b> <i>f</i> <i>v. l.</i>\n= parsimonia."],
  ["parcitas", "<big>parcitas</big>\n\n<b>parcitas, atis</b> <i>f</i>\n<b>1)</b> бережливость, расчётливость, умеренность (alicujus rei <i>Sen, CA</i>);\n<b>2)</b> пощада (sine ulla parcitate verberare aliquem <i>Eccl</i>)."],
  ["parco", "<big>parco</big>\n\n<b>parco, peperci</b> (<i>реже</i> <b>parsi</b>), <b>—, ere</b> (<i>part. fut.</i> parsurus) [parcus]\n<b>1)</b> беречь, жалеть, скупиться (alicui rei <i>Pl, C etc.</i> <i>или</i> aliquid <i>Pl, Lcr etc.</i>); экономить (p. impensae <i>L</i>; pecuniam <i>Pl</i>);\n<b>2)</b> беречь (p. valetudini <i>C</i>); сохранять (p. vitae <i>Nep</i>); щадить (hostibus <i>V</i>; <i>редко</i> in aliquem <i>Ap</i>; p. personis, dicere de vitiis <i>M</i>); оберегать (p. aedificiis <i>C</i>);\n<b>3)</b> копить (talenta gnatis suis <i>V</i>);\n<b>4)</b> уклоняться, избегать (p. labori <i>C</i>); воздерживаться (contumeliis dicendis <i>L</i>; <i>редко</i> ab aliqua re <i>L</i>); не пользоваться, отказываться (p. auxilio <i>C</i>); переставать (parce fidem jactare <i>L</i>): parcite nimium procedere <i>V</i> берегитесь заходить слишком далеко; parce metu (= metui) <i>V</i> не бойся; p. oculis <i>Prp</i> (luminibus <i>O</i>) отвернуться, не смотреть."],
  ["parcus", "<big>parcus</big>\n\n<b>parcus, a, um</b>\n<b>1)</b> бережливый, экономный, расчётливый (pater, colonus <i>C</i>); скупой (senex <i>Pl, H</i>): p. pecuniae suae (<i>gen.</i>) <i>Su, T</i> скупо расходующий свои деньги, <i>но тж.</i> <i>Just</i> пренебрегающий деньгами; parca manu <i>H</i> нещедро, скупо; opera haud fui p. mea <i>Pl</i> я не щадил труда || сдержанный (in laudando <i>PJ</i>); умеренный (in cibum <i>Just</i> <i>и</i> cibi <i>Spart</i>; vini <i>Su</i>);\n<b>2)</b> скудный (victus <i>T</i>); немногочисленный <i>или</i> мягкий (verba <i>O</i>); короткий (somnus <i>PJ</i>); parca die <i>Prp</i> в короткое время, быстро || слабый, тусклый (lucerna <i>Prp</i>): lintea parca vento dare <i>O</i> распускать мало парусов по ветру, <i>т. е.</i> слишком медленно плыть."],
  ["parda", "<big>parda</big>\n\n<b>parda, ae</b> <i>f</i>\nсамка леопарда <i>Is</i>."],
  ["pardalis", "<big>pardalis</big>\n\n<b>pardalis, is</b> <i>f</i> (<i>греч.</i>)\nбарс (<i>самка</i>) <i>QC</i>."],
  ["pardus", "<big>pardus</big>\n\n<b>pardus, i</b> <i>m</i> (<i>греч.</i>)\nбарс <i>или</i> леопард (<i>самец</i>) <i>PM, J</i>."],
  ["pareas", "<big>pareas</big>\n\n<b>pareas (parias), ae</b> <i>m</i> (<i>греч.</i>)\nпарей, <i>змея, посвященная Эскулапию</i> <i>Lcn, Is</i>."],
  ["parectatos", "<big>parectatos</big>\n\n<b>parectatos, on</b> (<i>греч.</i>)\nвзрослый <i>LM, Vr</i>."],
  ["paredros", "<big>paredros</big>\n\n<b>paredros, i</b> <i>m</i> (<i>греч.</i>)\nпомощник <i>или</i> спутник (p. spiritus <i>Tert</i>)."],
  ["paregoria", "<big>paregoria</big>\n\n<b>paregoria, ae</b> <i>f</i> (<i>греч.</i>)\nуспокоение, утешение, облегчение <i>Ap</i>."],
  ["parelion", "<big>parelion</big>\n\n<b>parelion, i</b> <i>n</i> (<i>греч.</i>)\nпаргелий, боковое (мнимое) изображение солнца <i>Sen</i>."],
  ["paremphatus", "<big>paremphatus</big>\n\n<b>paremphatus, a, um</b> (<i>греч.</i>) <i>грам.</i>\nопределённый <i>Macr</i>."],
  ["parens", "<big>parens</big>\n\n<b>I parens, entis</b>\n<b>1.</b> <i>part. praes. к</i> pareo;\n<b>2.</b> <i>adj.</i> послушный, покорный (asellus <i>H</i>); дисциплинированный (exercitus <i>C</i>).\n\n<b>II parens, entis</b> <i>m</i> [pareo] (<i>преим.</i> <i>pl.</i>)\nподданный <i>Sl</i>: male parentes <i>Sen</i> непокорные подданные.\n\n<b>III parens, entis</b> (<i>gen. pl.</i> um <i>или</i> ium) <i>m, f</i> [pario]\n<b>1)</b> родитель(ница) <i>Enn, C etc.</i>: terra p. <i>V</i> мать-земля; p. Idaea <i>V, O</i> = Cybele; p. Tegeaea <i>или</i> p. Euandri <i>O</i> = Carmenta || <i>pl.</i> родители (parentes cum liberis <i>Cs</i>) <i>или</i> деды, предки (Sicilia parentibus nostris erepta <i>L</i>);\n<b>2)</b> родоначальник, основатель (philosophiae <i>C</i>);\n<b>3)</b> метрополия (Sidon Thebarum Boeotiarum p. <i>PM</i>);\n<b>4)</b> изобретатель (operis <i>C</i>): p. lyrae <i>H</i> = Mercurius;\n<b>5)</b> родственник: p. Tarquinii <i>Eutr</i> = Brutus."],
  ["parentalia", "<big>parentalia</big>\n\n<b>parentalia, ium</b> <i>n</i> [parentalis]\nпаренталии, <i>поминальное празднество в честь покойных родственников</i> <i>C</i>."],
  ["parentalis", "<big>parentalis</big>\n\n<b>parentalis, e</b> [parens III]\nродительский: dies p. <i>O</i> поминальный день (<i>по родителям и родственникам</i>)."],
  ["parentatio", "<big>parentatio</big>\n\n<b>parentatio, onis</b> <i>f</i>\nпочитание усопших предков <i>Ter</i>."],
  ["parentela", "<big>parentela</big>\n\n<b>parentela, ae</b> <i>f</i> [parens III]\nродственная связь, родство <i>Capit, Is</i>."],
  ["parenthesis", "<big>parenthesis</big>\n\n<b>parenthesis, is</b> <i>f</i> (<i>греч.</i>)\n<b>1)</b> <i>грам.</i> вставка (<i>буквы или слога</i>);\n<b>2)</b> вводное предложение <i>или</i> слово."],
  ["parentia", "<big>parentia</big>\n\n<b>parentia, ae</b> <i>f</i> [pareo]\nпослушание, повиновение <i>Eccl</i>."],
  ["parenticida", "<big>parenticida</big>\n\n<b>parenticida, ae</b> <i>m</i> <i>Pl</i>\n(<i>v. l.</i>) = parricida."],
  ["Parentini", "<big>Parentini</big>\n\n<b>Parentini, orum</b> <i>m</i>\nжители города Parentium <i>Pt</i>."],
  ["Parentium", "<big>Parentium</big>\n\n<b>Parentium, i</b> <i>n</i>\nПарентий, <i>город в Истрии</i> <i>PM</i>."],
  ["parento", "<big>parento</big>\n\n<b>parento, avi, atum, are</b> [parens III]\n<b>1)</b> приносить жертву умершему (<i>преим.</i> отцу, матери, родственнику) (p. mortuis <i>C, Sen</i>);\n<b>2)</b> приносить искупительную жертву (alicui sanguine alicujus <i>L</i>); мстить: noxio sanguine p. injuriae suae <i>Pt</i> кровью преступника смыть свою обиду; p. majoribus excidio urbis <i>QC</i> мстить за предков разрушением города;\n<b>3)</b> удовлетворять, утолять: internecione hostium justae irae p. <i>QC</i> утолить справедливый гнев истреблением врагов."],
  ["pareo", "<big>pareo</big>\n\n<b>pareo, ui, itum, ere</b>\n<b>1)</b> являться, появляться (alicui <i>Su</i>; ad portum <i>M</i>);\n<b>2)</b> быть очевидным: <i>impers.</i> paret <i>C, Pt, Dig</i> очевидно, ясно: si paret <i>C</i> если окажется;\n<b>3)</b> быть в помощь (ad memonam <i>Spart</i>);\n<b>4)</b> находиться в подчинении, состоять (при) (p. magistratibus in provincias euntibus <i>AG</i>);\n<b>5)</b> подчиняться, повиноваться, слушаться (praecepto alicujus <i>C</i>; ducibus <i>L</i>; patris imperio <i>V</i>; necessitati est parendum <i>C</i>; freta parentia ventis <i>Tib</i>): parendo imperare <i>PS</i> подчиняясь, повелевать (<i>об умной и добродетельной жене</i>); ille, cui paret orbis <i>O</i> тот, кому повинуется мир, <i>т. е.</i> владыка вселенной; <i>impers.</i> legato a centurionibus parebatur <i>T</i> центурионы повиновались легату;\n<b>6)</b> сообразоваться, следовать (naturae <i>Nep</i>); исполнять (promissis <i>O</i>)."],
  ["parere", "<big>parere</big>\n\n<b>I parere</b> <i>inf. к</i>\npareo.\n\n<b>II parere</b> <i>inf. к</i>\npario I."],
  ["parergon", "<big>parergon</big>\n\n<b>parergon, i</b> (<i>pl.</i> parerga, on) <i>n</i> (<i>греч.</i>)\n<b>1)</b> второстепенное украшение, добавление, деталь (<i>к главному предмету картины</i>) <i>PM, Vtr</i>;\n<b>2)</b> второстепенное произведение, <i>pl.</i> разные мелкие статьи <i>Acc</i>."],
  ["parhedrus", "<big>parhedrus</big>\n\n<b>parhedrus</b> <i>v. l.</i>\n= paredros."],
  ["parhelion", "<big>parhelion</big>\n\n<b>parhelion, i</b> <i>n</i> <i>v. l.</i>\n= parelion."],
  ["parhippus", "<big>parhippus</big>\n\n<b>parhippus, i</b> <i>m</i>\nдополнительная (почтовая) лошадь <i>CTh, CJ</i>."],
  ["pariambodes", "<big>pariambodes</big>\n\n<b>pariambōdes, is</b> <i>n</i> (<i>греч.</i>)\nпариамбод, <i>стихотворная стопа</i> U–U– –."],
  ["pariambus", "<big>pariambus</big>\n\n<b>pariambus, ī</b> <i>m</i> (<i>греч.</i>)\nпариамб, <i>стихотворные стопы</i> U– –, –UUUU <i>или</i> (= pyrrhichius) UU <i>Q</i>."],
  ["Parianus", "<big>Parianus</big>\n\n<b>Parianus, a, um</b> [Parium]\nпарийский <i>C, PM</i>."],
  ["parias", "<big>parias</big>\n\n<b>parias, ae</b> <i>m</i> <i>v. l.</i>\n= pareas."],
  ["pariatio", "<big>pariatio</big>\n\n<b>pariatio, onis</b> <i>f</i> [pario II]\nурегулирование взаимных расчётов <i>Dig</i>."],
  ["pariator", "<big>pariator</big>\n\n<b>pariator, oris</b> <i>m</i>\nплательщик долга <i>Dig</i>."],
  ["pariatoria", "<big>pariatoria</big>\n\n<b>pariatoria, ae</b> <i>f</i>\nрасплата <i>Aug</i>."],
  ["paricid-", "<big>paricid-</big>\n\n<b>paricid-</b> <i>v. l.</i>\n= parricid-."],
  ["paries", "<big>paries</big>\n\n<b>paries, etis</b> <i>m</i>\nстена (parietem ducere <i>C</i> <i>или</i> exstruere <i>Cs</i>; canis in pariete pictus <i>Pt</i>): intra parietes <i>C</i> келейно, в семейном порядке; in caducum parietem inclinare <i>погов. Spart</i> иметь ненадёжную опору; utrosque parietes linere <i>погов. Pt</i> = служить и нашим и вашим; duo parietes de eadem fidelia dealbare <i>C погов.</i> — <i>см.</i> fidelia."],
  ["parietarius", "<big>parietarius</big>\n\n<b>parietarius, a, um</b> [paries]\nстенной <i>или</i> растущий на стенах (herba <i>Ap</i>)."],
  ["parietinae", "<big>parietinae</big>\n\n<b>parietinae, arum</b> <i>f</i> [paries]\nстарые стены, развалины <i>C</i>."],
  ["parietinus", "<big>parietinus</big>\n\n<b>parietinus, a, um</b> <i>Tert, Amm</i>\n= parietarius."],
  ["Parilia", "<big>Parilia</big>\n\n<b>Parilia, ium</b> <i>n</i> <i>v. l.</i>\n= Palilia."],
  ["parilis", "<big>parilis</big>\n\n<b>parilis, e</b> [par]\nравный, одинаковый (noctes pariles diebus <i>Lcr</i>): parili aetate <i>O</i> одинакового возраста; parili separatu <i>Ap</i> ровным разделением (волос), <i>т. е.</i> ровным пробором."],
  ["parilitas", "<big>parilitas</big>\n\n<b>parilitas, atis</b> <i>f</i> [parilis]\nравенство <i>AG, Ap, Eccl</i>."],
  ["pario", "<big>pario</big>\n\n<b>I pario, peperi, par(a)tum, ere</b> (<i>редко</i> <i>Enn</i> <b>ire</b>) (<i>part. fut.</i> pariturus)\n<b>1)</b> рожать (sine doloribus <i>Pl</i>): p. quintum <i>C</i> родить в пятый раз || <i>о животных</i> рожать, метать, высиживать <i>Cato, C etc.</i>: p. ova <i>C</i> класть яйца;\n<b>2)</b> производить (fruges <i>C</i>); создавать, основывать, строить (urbes <i>C</i>); выдумывать (verba <i>C</i>);\n<b>3)</b> добывать, достигать (consulatum <i>C</i>; paritur pax bello <i>Nep</i>); снискать, стяжать, заслужить (sibi laudem <i>C</i>; gratiam apud aliquem <i>L</i>); одерживать (victoriam ex hoste <i>L</i>); наживать (parta bona <i>C</i>): bene partus <i>Lcr</i> честно нажитой; non minor est virtus, quam quaerere, parta tueri <i>O</i> в умении сберегать приобретённое не меньшая заслуга, чем в самом приобретении || завоёвывать (aliquid armis <i>Sl</i>);\n<b>4)</b> причинять (alicui dolorem <i>C</i>); навлекать, порождать (veritas odium parit <i>Ter</i>): letum sibi manu p. <i>V</i> покончить с собой.\n\n<b>II pario, avi, atum, are</b> [par]\n<b>1)</b> делать равным, <i>med.-pass.</i> быть равным (pariari deo <i>Tert</i>);\n<b>2)</b> производить взаимные расчёты (p. alicui <i>Dig</i>);\n<b>3)</b> совершать куплю-продажу, торговать <i>Lampr</i>."],
  ["Parion", "<big>Parion</big>\n\n<b>Parion, i</b> <i>m</i>\n= Parium."],
  ["paris", "<big>paris</big>\n\n<b>I paris</b> <i>gen. к</i>\npar.\n\n<b>II Paris, idis</b> <i>m</i>\nПарис (<i>лучше</i> Парид), <i>он же Александр, сын Приама и Гекубы, судья в споре трёх богинь о яблоке, похититель Елены, жены Менелая, непосредственный виновник Троянской войны; убит стрелой Филоктета</i> <i>C, V, Prp, O etc.</i>"],
  ["Parisii", "<big>Parisii</big>\n\n<b>Parisii, orum</b> <i>m</i>\nпаризии, <i>галльское племя по среднему течению Секваны с главным городом Lutetia</i> (<i>Parisiorum</i>) <i>Cs, PM, Amm</i>."],
  ["paritas", "<big>paritas</big>\n\n<b>paritas, atis</b> <i>f</i> [par]\nравенство, одинаковость <i>Boet, Eccl</i>."],
  ["pariter", "<big>pariter</big>\n\n<b>pariter</b> [par]\n<b>1)</b> равно, равным образом, одинаково, так же (cum aliquo <i>C etc.</i> <i>или</i> alicui <i>St</i>): p. ultimi propinquis <i>L</i> самые отдалённые, как и ближайшие; p. ac (atque, et, ut) <i>Ter, C etc.</i> равно (так же) как;\n<b>2)</b> вместе, в одно и то же время (p. cum occasu solis <i>Sl</i>): quomodo ista tam diversa p. sunt? <i>Sen</i> как совместить столь различные вещи?; p. cum aetate crescere <i>C</i> расти с годами; p.... p. как только... так (hanc p. vidit, p. optavit <i>O</i>);\n<b>3)</b> равномерно: referre p. ad pectora remos <i>O</i> равномерно (дружно) приближать вёсла к груди, <i>т. е.</i> грести в такт."],
  ["parito", "<big>parito</big>\n\n<b>parito, —, —, are</b> [<i>intens. к</i> paro I]\n<b>1)</b> готовить, затевать (insidias alicui <i>Pl</i>);\n<b>2)</b> собираться, готовиться: quo nunc ire paritas? <i>Pl</i> куда ты собираешься идти?"],
  ["paritor", "<big>paritor</big>\n\n<b>paritor, oris</b> <i>m</i> [pareo]\nслужитель <i>или</i> телохранитель <i>AV</i>."],
  ["Parium", "<big>Parium</big>\n\n<b>Parium, i</b> <i>n</i>\nПарий, <i>город при Геллеспонте в Мисии, к сев.-вост. от Лампсака</i> <i>Sl, Mela, VF</i>."],
  ["Parius", "<big>Parius</big>\n\n<b>I Parius, a, um</b> [Paros]\nпаросский (marmor <i>H</i>): P. iapis <i>V</i> = мрамор; Parii iambi <i>H</i> паросские ямбы (<i>т. е.</i> ямбы Архилоха); crimen Parium <i>Nep</i> вина (Мильтиада), состоявшая в том, что он не завоевал Парос.\n\n<b>II Parius, i</b> <i>m</i>\nпаросец, житель острова Paros <i>Nep, L, PM</i>."],
  ["parma", "<big>parma</big>\n\n<b>I parma, ae</b> <i>f</i> (<i>греч.</i>)\n<b>1)</b> парма, небольшой круглый щит <i>Enn, Sl, L etc.</i>; <i>тж.</i> щит <i>вообще</i> <i>V, M</i>;\n<b>2)</b> вооружённый щитом гладиатор <i>M</i>;\n<b>3)</b> клапан кузнечного меха <i>Aus</i>.\n\n<b>II Parma, ae</b> <i>f</i>\nПарма, <i>город в Gallia Cispadana между Кремоной и Мутиной</i> (<i>ныне тж.</i> Parma) <i>C, L etc.</i>"],
  ["parmatus", "<big>parmatus</big>\n\n<b>parmatus, a, um</b>\nвооружённый пармой (щитом) <i>L</i>."],
  ["Parmenides", "<big>Parmenides</big>\n\n<b>Parmenides, is</b> <i>m</i>\nПарменид, <i>родом из Элей (Велии), один из основателей элейской философской школы (VI-V вв. до н. э.</i>) <i>C</i>."],
  ["Parmenion", "<big>Parmenion</big>\n\n<b>Parmenio(n), onis</b> <i>m</i>\nПарменион, <i>полководец Филиппа и Александра Македонского, умерщвлённый по приказанию последнего</i> <i>Just, QC, VM</i>."],
  ["Parmensis", "<big>Parmensis</big>\n\n<b>I Parmensis, e</b> [Parma II]\nпармский <i>H</i>.\n\n<b>II Parmensis, is</b> <i>m</i>\nжитель Пармы <i>C</i>."],
  ["parmula", "<big>parmula</big>\n\n<b>parmula, ae</b> <i>f</i> [<i>demin. к</i> parma]\nнебольшой щит (парма) <i>H etc.</i>"],
  ["parmularius", "<big>parmularius</big>\n\n<b>parmularius, i</b> <i>m</i> [parmula]\nсторонник фракийских борцов (вооружённых щитами) <i>Q, Su</i>."],
  ["Parnassia", "<big>Parnassia</big>\n\n<b>Parnas(s)ia, ae</b> <i>f</i> <i>Cld</i>\n= Musa."],
  ["Parnassis", "<big>Parnassis</big>\n\n<b>Parnas(s)is, idis</b> <i>f</i> [Parnas(s)us]\nпарнасская (laurus <i>O</i>)."],
  ["Parnassius", "<big>Parnassius</big>\n\n<b>Parnas(s)ius, a, um</b> [Parnassus]\n<b>1)</b> парнасский (rupes <i>V</i>): Parnas(s)ia Themis <i>O</i> Фемида Парнасская (<i>так как до Аполлона Дельфийский оракул принадлежал ей</i>);\n<b>2)</b> аполлонов, дельфийский (templa <i>O</i>): vox Parnas(s)ia <i>VF</i> Дельфийский оракул."],
  ["Parnassus", "<big>Parnassus</big>\n\n<b>Parnas(s)us (-os), i</b> <i>m</i>\nПарнасе, <i>двувершинная</i> (biceps <i>Pers, O, PM</i>; verticibus duobus <i>O</i>; gemino colle <i>Lcn</i>) <i>гора в Фокиде; у её южной подошвы находились посвященные Аполлону и Музам Дельфы и Кастальский источник</i> <i>Mela, PM etc.</i>"],
  ["Parnes", "<big>Parnes</big>\n\n<b>Parnes, ethis</b> <i>m</i>\nПарнет, <i>горная цепь в Аттике на границе Беотии, славившаяся виноградом и дичью</i> <i>St, SenT</i>."],
  ["paro", "<big>paro</big>\n\n<b>I paro, avi, atum, are</b> [<i>одного корня с</i> pario I]\n<b>1)</b> готовить, подготовлять, устраивать, организовывать (convivium <i>C</i>; insidias alicui <i>Sl</i>; bellum <i>Cs</i>; fugam <i>C</i>; aliquid alicui in prandium <i>Pt</i>; se ad iter <i>L</i>; alicui necem <i>L</i>); развивать, создавать (exercitatio artem parat <i>T</i>); собираться, намереваться (litteras alicui mittere <i>C</i>): jussa p. <i>V</i> готовиться исполнить приказания;\n<b>2)</b> наживать, накапливать (divitias <i>O</i>; lamellulas <i>Pt</i>); набирать, собирать (copias, exercitum <i>Sl</i>); заготовлять (commeatum <i>Sl</i>); скупать, приобретать (praedia, hortos <i>C</i>): magno parari <i>Lcn</i> приобретаться дорогой ценой;\n<b>3)</b> заключать (pacem, societatem <i>L</i>).\n\n<b>II paro, (avi), atum, are</b> [par]\n<b>1)</b> приравнивать, равнять (eodem vos pono et paro <i>Pl</i>);\n<b>2)</b> улаживать: p. se cum aliquo <i>C</i> договориться с кем-л.\n\n<b>III paro, onis</b> <i>m</i> (<i>греч.</i>)\nлёгкое судно, барка <i>C, AG</i>."],
  ["parobsis", "<big>parobsis</big>\n\n<b>parobsis, idis</b> <i>f</i> <i>v. l.</i>\n= paropsis."],
  ["parochia", "<big>parochia</big>\n\n<b>parochia, ae</b> <i>f</i> <i>Sid</i>\n= paroecia."],
  ["parochus", "<big>parochus</big>\n\n<b>parochus, i</b> <i>m</i> (<i>греч.</i>)\n<b>1)</b> трактирщик, который за счёт государства предоставлял проезжающим должностным лицам помещение и питание <i>C, H</i>;\n<b>2)</b> хозяин, принимающий у себя гостей <i>H</i>."],
  ["paroecia", "<big>paroecia</big>\n\n<b>paroecia, ae</b> <i>f</i> (<i>греч.</i>)\nцерковный приход <i>или</i> епархия <i>Aug</i>."],
  ["paronomasia", "<big>paronomasia</big>\n\n<b>paronomasia, ae</b> <i>f</i> (<i>греч.</i>; <i>лат.</i> agnominatio) <i>ритор.</i>\nпарономасия, игра близкими по звучанию, но разными по смыслу словами, <i>напр.</i>: meditari <i>condiscere</i>, loquitari dediscere <i>Ap</i> <i>или</i> neminem proconsulum provincia Africa magis <i>reverita</i> est, minus <i>verita</i> <i>Ap</i>."],
  ["paronychia", "<big>paronychia</big>\n\n<b>paronychia, ae</b> <i>f</i>, <i>реже</i> <b>paronychium, i</b> <i>n</i> (<i>греч.</i>; <i>лат.</i> reduvia)\nзаусеница <i>PM, Pt</i>."],
  ["Paropamisadae", "<big>Paropamisadae</big>\n\n<b>Paropamisadae, arum</b> <i>и</i> <b>Paropamisii, orum</b> <i>m</i>\nобитатели Паропамиса <i>QC, Mela</i>."],
  ["Paropamisus", "<big>Paropamisus</big>\n\n<b>Paropamisus, i</b> <i>m</i>\nПаропамис, <i>горная цепь к югу от Бактрии</i> (<i>ныне</i> Гиндукуш) <i>Mela, QC, PM</i>."],
  ["Paropan-", "<big>Paropan-</big>\n\n<b>Paropan-</b> <i>Amm etc.</i>\n= Paropam-."],
  ["paropsis", "<big>paropsis</big>\n\n<b>paropsis, idis</b> (<i>acc. pl.</i> ides <i>и</i> idas) <i>f</i> (<i>греч.</i>)\nнебольшое блюдо, миска для десерта <i>Pt, Su etc.</i>"],
  ["Paros", "<big>Paros</big>\n\n<b>Paros (-us), i</b> <i>f</i>\nПарос, <i>один из Кикладских островов, славившийся белым мрамором; родина поэта Архилоха</i> <i>Mela, Nep, O, L, V</i>."],
  ["parotis", "<big>parotis</big>\n\n<b>parotis, idis</b> (<i>acc. pl.</i> idas) <i>f</i> (<i>греч.</i>)\n<b>1)</b> паротит, воспаление околоушной железы <i>PM</i>;\n<b>2)</b> <i>архит.</i> консоль <i>Vtr</i>."],
  ["parra", "<big>parra</big>\n\n<b>parra, ae</b> <i>f</i>\nпарра, <i>неизвестная нам птица, крик которой предвещал беду</i> (parrae recinentis omen <i>H</i>): malam parram pilare <i>Pt</i> — <i>см.</i> pilo I."],
  ["Parrhasia", "<big>Parrhasia</big>\n\n<b>Parrhasia, ae</b> <i>f</i>\nПаррасия, <i>город и область в юго-зап. Аркадии</i> <i>PM</i>."],
  ["Parrhasis", "<big>Parrhasis</big>\n\n<b>I Parrhasis, idis</b> <i>f</i> [Parrhasia]\nпаррасийская: P. ursa <i>и</i> Parrhasides stellae <i>O</i> = Большая Медведица.\n\n<b>II Parrhasis, idis</b> <i>f</i>\n<i>O</i> = Каллисто."],
  ["Parrhasius", "<big>Parrhasius</big>\n\n<b>I Parrhasius, a, um</b> [Parrhasia]\n<b>1)</b> <i>поэт.</i> аркадский (nives, Euander <i>O</i>): Parrhasia dea <i>O</i> = Кармента (мать Эвандра); Parrhasia virgo <i>O</i> = Каллисто; Parrhasia ursa <i>O</i>, Parrhasii triones <i>или</i> Parrhasium jugum <i>M</i> = созвездие Большой Медведицы; Parrhasiae pennae <i>Lcn</i> = крылья Меркурия (<i>уроженца Аркадии</i>);\n<b>2)</b> палатинский, императорский (domus <i>или</i> aula <i>M</i>) (<i>поскольку аркадский царь Эвандр жил якобы на Палатинском холме в Риме</i>).\n\n<b>II Parrhasius, i</b> <i>m</i>\nПаррасий, <i>афинский живописец родом из Эфеса, сын Эвенора</i> (<i>1-я половина IV в. до н. э.</i>) <i>C, PM, Sen, H, Prp</i>."],
  ["parricida", "<big>parricida</big>\n\n<b>parricida, ae</b> <i>m, f</i>\n<b>1)</b> убийца близкого родственника (отцеубийца, братоубийца <i>и т. д.</i>) <i>C, L, Sen etc.</i>;\n<b>2)</b> убийца лиц, пользующихся неприкосновенностью (p. civium <i>C</i>);\n<b>3)</b> государственный изменник (p. patriae <i>VM</i>, rei publicae <i>Sl</i>);\n<b>4)</b> злодей, преступник <i>C, Sl etc.</i>"],
  ["parricidalis", "<big>parricidalis</big>\n\n<b>parricidalis, e</b> [parricida]\nбратоубийственный (manus <i>Pt</i>; bellum <i>Fl</i>); злодейский, нечестивый (scelus <i>Just</i>)."],
  ["parricidaliter", "<big>parricidaliter</big>\n\n<b>parricidaliter</b> [parricidalis]\nзлодейски, нечестиво <i>Aug</i>."],
  ["parricidatus", "<big>parricidatus</big>\n\n<b>parricidatus, us</b> <i>m</i> <i>Q</i>\n= parricidium."],
  ["parricidial-", "<big>parricidial-</big>\n\n<b>parricidial-</b> <i>Lampr, Ambr</i>\n= parricidal-."],
  ["parricidium", "<big>parricidium</big>\n\n<b>parricidium, i</b> <i>n</i> [parricida]\n<b>1)</b> убийство ближайших родственников (p. fraternum <i>C</i> <i>или</i> fratris <i>L</i>; p. patris <i>C</i>; p. matris <i>Su</i>);\n<b>2)</b> злодеяние, государственная измена, предательство (p. patriae <i>C</i>; p. publicum <i>L</i>)."],
  ["pars", "<big>pars</big>\n\n<b>pars, partis</b> (<i>изредка</i> <i>acc.</i> im, <i>abl.</i> i) <i>f</i>\n<b>1)</b> часть (corporis, urbis <i>C</i>; civium <i>Cs</i>; diei <i>Nep</i>; de nobis, ex illis <i>C</i>): parte <i>PM etc.</i> <i>и</i> in parte <i>Q</i> частью, отчасти; major p. <i>C</i> большая часть, большинство; dimidia p. <i>C, Sl etc.</i> половина; tertia p. <i>Cs</i> одна треть; duae partes <i>C</i> две трети; tres partes <i>Cs</i> три четверти; novem partes <i>Sl, Nep</i> девять десятых; duabus partibus plus (<i>или</i> amplius) <i>C</i> вдвое больше; multis partibus <i>C</i> во много раз, гораздо; omnibus partibus major <i>C</i> несравненно больший; partes facere <i>C</i> делить; partem facere <i>Dig</i> составлять <i>или</i> получать часть; partes rei <i>C</i> подробности, детали; magnam (bonam) partem <i>или</i> magna (ex) parte <i>Lcr, C, L, O etc.</i> в значительной части; ex (in) parte <i>C, Cs etc.</i> частью, частично; ex aliqua (quadam) parte <i>C</i> до некоторой степени; aliqua (ex) parte <i>C, PJ</i> в некоторой части <i>или</i> в известном отношении; nulla (ex) parte <i>O, Q</i> ни в какой степени, нисколько; ne minima quidem ex parte <i>C</i> ни в малейшей степени; non minimam partem <i>Lcr</i> не в малой степени <i>или</i> совсем нередко; decem partes dicere <i>Pt</i> уметь делить (числа) на десять, <i>т. е.</i> усвоить начатки арифметики;\n<b>2)</b> участие, доля: in parte alicujus rei esse <i>L</i> принимать участие в чём-л.; partem habere in (ex) aliqua re <i>C</i> быть участником чего-л.; ego pro mea parte <i>C</i> я со своей стороны (в меру моей возможности); pro parte conferre <i>C</i> внести свою долю; agrum partibus locare <i>PJ</i> сдавать поле в аренду на началах издольщины; plus quam pro parte laborare <i>O</i> работать больше положенного (<i>т. е.</i> изо всех сил);\n<b>3)</b> область, край (pagi partesque <i>Cs</i>; partes orientis <i>C</i>);\n<b>4)</b> сторона (dextra, sinistra <i>Cs</i>; ex omnibus partibus <i>C</i>): in utramque partem <i>или</i> ab (ex) utraque parte <i>Cs etc.</i> справа и слева; omnibus (in) partibus <i>C, Cs etc.</i> везде, повсюду; qua ex parte? <i>C</i> с какой стороны?, где?; in ea parte <i>C</i> там; quam in partem <i>C</i> куда; in eam partem <i>C</i> туда, <i>перен.</i> в том смысле (что) <i>или</i> с тем (чтобы);\n<b>5)</b> отношение: omni (ex) parte, in omnes partes <i>C</i>, omnibus partibus <i>C, Cs</i> <i>или</i> in (ab) omni parte <i>C, O, L, H</i> во всех отношениях, совершенно, всецело; in utramque partem <i>C etc.</i> в обоих случаях; nullam in partem <i>C etc.</i> ни в каком отношении; in utramque partem (<i>или</i> in contrarias partes) disputare <i>C</i> спорить за и против; accipere aliquid in bonam partem <i>C</i> принимать что-л. с хорошей стороны;\n<b>6)</b> вид, род, раздел: partes animi <i>C</i> душевные способности; ea parte belli <i>L</i> этого рода войны;\n<b>7)</b> <i>преим. pl.</i> партия (partes optimatium et popularium <i>C</i>; partes Sullanae <i>Nep</i>): partes optimae <i>C</i> партия оптиматов; a parte alicujus esse <i>C, Sl</i> быть на чьей-л. стороне; nullius (neutrius) partis esse (<i>или</i> se in nullam partem movere) <i>C</i> быть нейтральным;\n<b>8)</b> <i>pl. театр.</i> роль: primae partes <i>Ter, C</i> главная роль; alicujus partes agere <i>C</i> играть чью-л. роль; vocare ad partes aliquem <i>J</i> выводить (изображать) кого-л. в литературном произведении;\n<b>9)</b> <i>pl.</i> должность, обязанность, задача, долг (partes imperatoriae <i>C</i>): defensionis partes suscipere (sibi sumere, obtinere) <i>C</i> взять на себя обязанности защитника; res est mearum partium <i>или</i> meae sunt partes <i>C</i> это относится к моим обязанностям; partes scelus expiandi <i>H</i> обязанность искупить вину; partes implere <i>O</i> исполнять свой долг; ad partes paratus <i>Vr, O</i> готовый выполнять свою задачу;\n<b>10)</b> участник (Consus, p. ingens belli <i>V</i>);\n<b>11)</b> член (partes corporis <i>Sl</i>): pars obscena (partes obscenae) <i>O</i> <i>и</i> naturae partes <i>Ph</i> = genitalia."],
  ["Parsagada", "<big>Parsagada</big>\n\n<b>Parsagada, orum</b> <i>n</i> <i>QC</i>\n= Pasargadae."],
  ["parsi", "<big>parsi</big>\n\n<b>parsi</b> (= peperci) <i>Pl, Ter, Nep pf. к</i>\nparco."],
  ["parsimonia", "<big>parsimonia</big>\n\n<b>parsimonia, ae</b> <i>f</i> [parco]\n<b>1)</b> бережливость <i>C</i>: sera p. in fundo est <i>погов. Sen</i> поздно быть бережливым, когда всё растрачено (<i>о запоздалых мероприятиях</i>);\n<b>2)</b> <i>pl.</i> сбережения <i>Pl</i>;\n<b>3)</b> сдержанность, трезвость (orationis <i>C</i>);\n<b>4)</b> воздержание, пост <i>Eccl</i>."],
  ["parsis", "<big>parsis</big>\n\n<b>parsis</b> <i>Pl</i>\n(= peperceris) <i>2 л. sg. pf. conjct. к</i> parco."],
  ["Parstrymonia", "<big>Parstrymonia</big>\n\n<b>Parstrymonia, ae</b> <i>f</i>\nПарстримония, <i>полоса земли вдоль реки Стримон</i> (<i>Фракия</i>) <i>L</i>."],
  ["parsurus", "<big>parsurus</big>\n\n<b>parsurus, a, um</b> <i>part. fut. к</i>\nparco."],
  ["parta", "<big>parta</big>\n\n<b>parta, ae</b> <i>f</i> [pario]\nродильница <i>Col</i>."],
  ["Parthaon", "<big>Parthaon</big>\n\n<b>Parthaon, onis</b> <i>m</i>\nПартаон, <i>сын Агенора, отец Энея</i> (Oeneus) <i>и Деианиры, царь Калидона</i> <i>Pl, O</i>."],
  ["Parthaonides", "<big>Parthaonides</big>\n\n<b>Parthaonides, ae</b> <i>m</i>\nсын <i>или</i> потомок Партаона. <i>т. е.</i> Meleager <i>VF</i>."],
  ["Parthaonius", "<big>Parthaonius</big>\n\n<b>Parthaonius, a, um</b> [Parthaon]\nпартаонов <i>O, St</i>."],
  ["Partheni", "<big>Partheni</big>\n\n<b>Partheni, orum</b> <i>m</i> <i>PM</i>\n= Parthini."],
  ["Parthenia", "<big>Parthenia</big>\n\n<b>Parthenia, ae</b> <i>f</i> (<i>греч.</i> «девственная»)\nостров Самос (<i>так как здесь жила до своего замужества Юнона</i>) <i>PM</i>."],
  ["Partheniae", "<big>Partheniae</big>\n\n<b>Partheniae, arum</b> <i>m</i> (<i>греч.</i> «дети девушек»)\nпарфенин, <i>спартанцы внебрачного происхождения, выселившиеся под руководством Фаланта в южн. Италию и основавшие там Тарент</i> <i>Just</i>."],
  ["Parthenianus", "<big>Parthenianus</big>\n\n<b>Parthenianus, a, um</b> [Parthenius II]\nпарфениев <i>M</i>."],
  ["parthenice", "<big>parthenice</big>\n\n<b>parthenice, es</b> <i>f</i> (<i>греч.</i>) <i>предпол.</i>\nромашка <i>Ctl</i>."],
  ["parthenicon", "<big>parthenicon</big>\n\n<b>parthenicon, i</b> <i>n Ap</i> <i>и</i> <b>parthenion (-um), i</b> <i>n</i>\n<i>PM</i> = parthenice."],
  ["Parthenius", "<big>Parthenius</big>\n\n<b>I Parthenius, i</b> <i>m</i>\nПарфений:\n<b>1)</b> <i>гора на границе Арголиды и Аркадии, на которой Геркулес поймал лань пианы</i> <i>L, PM</i>;\n<b>2)</b> <i>река на границе Пафлагонии и Вифинии</i> <i>PM, Mela</i>.\n\n<b>II Parthenius, i</b> <i>m</i>\nПарфений, <i>греч. поэт и грамматик, учитель Вергилия</i> <i>AG, Su, Macr</i>.\n\n<b>III Parthenius, a, um</b> [Parthenius I, 1]\nпарфенийский (nemus, valles <i>O</i>; antra <i>Prp</i>): Parthenium mare (pelagus) часть Mare internum — восточная <i>Amm</i> <i>или</i> западная <i>Macr</i>."],
  ["Parthenon", "<big>Parthenon</big>\n\n<b>Parthenon, onis</b> <i>m</i>\nПарфенон:\n<b>1)</b> <i>храм Афины в афинском Акрополе</i> <i>PM</i>;\n<b>2)</b> <i>портик в вилле Помпония Аттика</i> <i>C</i>."],
  ["Parthenopaeus", "<big>Parthenopaeus</big>\n\n<b>Parthenopaeus, i</b> <i>m</i>\nПартенопей, <i>сын Мелеагра и Аталанты, брат Адраста, один из «семерых против Фив»</i> <i>V</i>."],
  ["Parthenope", "<big>Parthenope</big>\n\n<b>Parthenope, es</b> <i>f</i>\nПартенопа:\n<b>1)</b> <i>одна из сирен, которая после отъезда Одиссея бросилась в море там, где впоследствии был построен город Партенопея, нын. Неаполь</i> <i>V, O</i>;\n<b>2)</b> <i>древнее название Неаполя</i> <i>PM</i>."],
  ["Parthenopeius", "<big>Parthenopeius</big>\n\n<b>Parthenopeius, a, um</b> [Parthenope 2]\nпартенопейский, <i>поэт.</i> неаполитанский (moenia <i>O</i>)."],
  ["Parthenopolis", "<big>Parthenopolis</big>\n\n<b>Parthenopolis, is</b> <i>f</i>\nПартенополь, <i>город в Нижней Мезии</i> (<i>на Чёрном море</i>) <i>PM, Eutr</i>."],
  ["Parthi", "<big>Parthi</big>\n\n<b>Parthi, orum</b> <i>и</i> <b>um</b> <i>m</i>\nпарфяне, <i>степное племя к югу от Каспийского моря; славились как меткие стрелки и превосходные наездники</i> <i>C, H, V, O etc.</i>"],
  ["Parthia", "<big>Parthia</big>\n\n<b>Parthia, ae</b> <i>f</i>\nПарфия, <i>страна парфян</i> <i>PM, Just, Lcn</i>."],
  ["Parthicarius", "<big>Parthicarius</big>\n\n<b>Parthicarius, a, um</b>\nзанимающийся торговлей парфянскими шкурами <i>CTh</i>."],
  ["Parthicus", "<big>Parthicus</big>\n\n<b>Parthicus, a, um</b> [Parthi]\nпарфянский (bellum <i>C</i>; equitatus <i>Fl</i>; pellis <i>Dig</i>)."],
  ["Parthieni", "<big>Parthieni</big>\n\n<b>Parthieni, orum</b> <i>m</i> <i>QC</i>\n= Parthi."],
  ["Parthini", "<big>Parthini</big>\n\n<b>Parthini, orum</b> <i>m</i> [Parthum]\nпартины, <i>иллирийское племя в окрестностях Диррахия</i> <i>Cs, C</i>."],
  ["Parthum", "<big>Parthum</big>\n\n<b>Parthum, i</b> <i>n</i>\nПарт(ум), <i>город в Иллирии, близ Диррахия</i> <i>Su</i>."],
  ["Parthus", "<big>Parthus</big>\n\n<b>I Parthus, a, um</b>\nпарфянский <i>C, O, Sen</i>.\n\n<b>II Parthus, i</b> <i>m</i>\nпарфянин <i>C, H etc.</i>"],
  ["Parthyaeus", "<big>Parthyaeus</big>\n\n<b>Parthyaeus, i</b> <i>m</i> <i>QC</i>\n= Parthus II."],
  ["Parthyene", "<big>Parthyene</big>\n\n<b>Parthyene, es</b> <i>f</i> <i>QC</i>\n= Parthia."],
  ["parti", "<big>parti</big>\n\n<b>parti</b>\n<i>Pl, Cato, Lcr</i> = parte (<i>abl. sg. к</i> pars)."],
  ["partialis", "<big>partialis</big>\n\n<b>partialis, e</b> [pars]\nчастичный <i>Eccl</i>."],
  ["partialiter", "<big>partialiter</big>\n\n<b>partialiter</b> <i>CA</i>\n= partiario."],
  ["partiario", "<big>partiario</big>\n\n<b>partiario</b> <i>adv.</i> [pars]\nчастично, наполовину <i>Cato, Ap</i>."],
  ["partiarius", "<big>partiarius</big>\n\n<b>I partiarius, a, um</b> [pars]\nучаствующий (в чём-л.), имеющий долю (legatarius <i>G</i>): p. colonus <i>G</i> издольщик.\n\n<b>II partiarius, i</b> <i>m</i>\nучастник (alicujus rei <i>Tert</i>)."],
  ["particeps", "<big>particeps</big>\n\n<b>I particeps, cipis</b> <i>adj.</i> [pars + capio]\nпринимающий участие, участвующий, причастный (alicujus rei <i>Pl, Ter, C etc.</i>); имеющий долю (praedae ac praemiorum <i>Cs</i>): participem esse alicui alicujus rei <i>QC</i> быть чьим-л. соучастником в чём-л.; p. ad omne secretum alicui <i>T</i> посвященный во все тайны кого-л.; p. leti <i>Lcr</i> смертный; p. tori <i>O</i> супруг.\n\n<b>II particeps, cipis</b> <i>m, f</i>\nучастник, соучастник, товарищ <i>Pl, Ter, Su</i>."],
  ["participalis", "<big>participalis</big>\n\n<b>participalis, e</b> [particeps I] <i>грам.</i>\nпричастный, имеющий форму причастия (verba <i>Vr, Q</i>)."],
  ["participatio", "<big>participatio</big>\n\n<b>participatio, onis</b> <i>f</i> [participo]\nпривлечение к участию, <i>т. е.</i> раздел(ение) (imperii <i>Spart, Aug</i>)."],
  ["participatus", "<big>participatus</big>\n\n<b>participatus, us</b> <i>m</i> <i>Spart</i>\n= participatio."],
  ["participialis", "<big>participialis</big>\n\n<b>participialis, e</b> <i>Q</i>\n= participalis."],
  ["participium", "<big>participium</big>\n\n<b>participium, i</b> <i>n</i> [particeps I]\n<b>1)</b> причастность, участие <i>CJ</i>;\n<b>2)</b> <i>грам.</i> причастие <i>Vr, Q</i>."],
  ["participo", "<big>participo</big>\n\n<b>participo, avi, atum, are</b> [particeps I]\n<b>1)</b> привлекать к участию, приобщать: p. aliquem alicujus rei <i>или</i> aliqua re <i>Pl etc.</i> сделать кого-л. соучастником в чём-л. <i>или</i> посвящать кого-л. во что-л. (aliquem consilii p. <i>Pl</i>; participari honore <i>Eccl</i>);\n<b>2)</b> участвовать: p. aliquid <i>Enn, AG, Dig</i> <i>и</i> de aliqua re <i>Vlg</i> участвовать в чём-л., иметь долю в чём-л.;\n<b>3)</b> делить, разделять (laudes suas cum aliquo <i>L</i>)."],
  ["particula", "<big>particula</big>\n\n<b>particula, ae</b> <i>f</i> [<i>demin. к</i> pars]\nчастица, частичка (arenae <i>H</i>); кусочек (caeli <i>C</i>); <i>грам.</i> частица <i>AG</i>."],
  ["particularis", "<big>particularis</big>\n\n<b>particularis, e</b> [particula]\nчастичный, частный (propositio <i>Ap</i>; solutio <i>CJ</i>)."],
  ["particulariter", "<big>particulariter</big>\n\n<b>particulariter</b> [particularis]\nв частности <i>Ap, CA, Aug</i>."],
  ["particulatim", "<big>particulatim</big>\n\n<b>particulatim</b> [particula]\n<b>1)</b> по частям (summatim, non p. narrare <i>rhH</i>); по кускам (aliquem excarnificare <i>Sen</i>);\n<b>2)</b> порознь, в отдельности (loca p. vocare <i>Vr</i>)."],
  ["partilis", "<big>partilis</big>\n\n<b>partilis, e</b> [pars]\n<b>1)</b> делимый (corporeum omne <i>Aug</i>);\n<b>2)</b> отдельный, частный (fata <i>Amm</i>)."],
  ["partim", "<big>partim</big>\n\n<b>partim</b> [pars]\n<b>1)</b> частью, отчасти (p. cupiditate, p. ambitione <i>Q</i>);\n<b>2)</b> (<i>с gen.</i> <i>или предлогом</i> ex) некоторая часть, некоторые (p. e nobis <i>C</i>; bonorum p. necessaria, p. non necessaria sunt <i>C</i>)."],
  ["partio", "<big>partio</big>\n\n<b>I partio, onis</b> <i>f</i> [pario I]\nроды (mulieris <i>AG</i>); кладка яиц (gallina ad partionem apta <i>Vr</i>).\n\n<b>II partio, ivi (ii), itum, ire</b> <i>и</i> <b>partior, itus sum, iri</b> <i>depon.</i> [pars]\n<b>1)</b> делить, разделять (aliquid in partes aequas <i>Lcr</i>); разбивать (genus in species <i>C</i>); логически расчленять (de partiendo docere <i>C</i>; partiri in tria <i>C</i>);\n<b>2)</b> распределять, раздавать (praedam in omnes <i>V</i>; officia inter se <i>Cs</i>); p. aliquid cum aliquo <i>C, Cs</i> делиться чём-л. с кем-л.; p. aliquem in suspicionem <i>Enn</i> распространить своё подозрение также на кого-л.;\n<b>3)</b> быть причастным, получать свою долю (p. invidiam <i>C</i>);\n<b>4)</b> столковываться: inter se p. <i>Pl, C</i> договориться между собой (насчёт чего-л.)."],
  ["partipes", "<big>partipes</big>\n\n<b>parti–pes, pedis</b> <i>стих.</i>\nразделённый по стопам: p. versus стих, в котором каждая стопа совпадает с отдельным словом."],
  ["partite", "<big>partite</big>\n\n<b>partite</b> [partitus]\nс должным расчленением, раздельно (dicere <i>C</i>)."],
  ["partitio", "<big>partitio</big>\n\n<b>partitio, onis</b> <i>f</i> [partio]\nделение, разделение, расчленение, распределение <i>C, Q etc.</i>"],
  ["partitudo", "<big>partitudo</big>\n\n<b>partitudo, inis</b> <i>f</i> [pario I]\nрождение, роды <i>Pl, CTh</i>."],
  ["partitus", "<big>partitus</big>\n\n<b>partitus, a, um</b> <i>part. pf. к</i>\npartio <i>и</i> partior, <i>преим.</i> <i>pass.</i> разделённый."],
  ["partualis", "<big>partualis</big>\n\n<b>partualis, e</b> [partus II]\nвызванный родами, родовой (sanguinis fluxus <i>Tert</i>)."],
  ["partuis", "<big>partuis</big>\n\n<b>partuis</b> <i>арх. Vr gen. sg. к</i>\npartus II."],
  ["partura", "<big>partura</big>\n\n<b>partura, ae</b> <i>f</i> <i>Vr</i>\n= partitudo."],
  ["parturio", "<big>parturio</big>\n\n<b>parturio, ivi, —, ire</b> [<i>desiderat. к</i> pario I]\n<b>1)</b> мучиться родами, рожать (canis parturiens <i>Ph</i>): parturiunt montes, nascetur ridiculus mus <i>впоследствии погов.</i> <i>M</i> рожают горы, а родится смешная мышь (<i>ср. русск.</i> гора родила мышь);\n<b>2)</b> быть чреватым, таить в себе (periculum <i>C</i>);\n<b>3)</b> (= pario) рождать, производить (Notus parturit imbres <i>H</i>; parturit ira minas <i>O</i>); давать побеги, распускаться (arbor parturit <i>V</i>); давать всходы, зеленеть (ager parturit <i>V</i>);\n<b>4)</b> иметь на уме, замышлять, затевать (aliquid <i>L</i>);\n<b>5)</b> тревожиться, беспокоиться, терзаться (pro aliquo <i>C</i> <i>или</i> aliquem <i>Ap</i>)."],
  ["partus", "<big>partus</big>\n\n<b>I partus, a, um</b> <i>part. pf. к</i>\npario.\n\n<b>II partus, us</b> <i>m</i> [pario]\n<b>1)</b> рождение: aliquem partu edere (eniti, dare, reddere) <i>O, L etc.</i> произвести кого-л. на свет;\n<b>2)</b> происхождение, возникновение, начало (Graeciae oratorum partus atque fontes <i>C</i>);\n<b>3)</b> роды, время родов (p. appropinquat <i>C</i>);\n<b>4)</b> утробный плод, зародыш (Latona partu gravida <i>T</i>): partum gerere (ferre) <i>PM</i> быть беременной; p. abjectus <i>PM</i> выкидыш;\n<b>5)</b> дитя: partus Ledae <i>Prp</i> = Елена и Клитемнестра; partus terrae <i>H</i> = Гиганты;\n<b>6)</b> детёныш (ferae partus suos diligunt <i>C</i>)."],
  ["parum", "<big>parum</big>\n\n<b>parum</b> <i>adv.</i> (<i>compar.</i> minus, <i>superl.</i> minime)\n<b>1)</b> мало, немного, недостаточно (satis eloquentiae, sapientiae p. <i>Sl</i>): aliquid p. facere <i>Sl</i> считать что-л. неважным; p. habere <i>Sl, L</i> не довольствоваться, считать недостаточным; p. splendoris habere <i>H</i> быть недостаточно ярким; non (haud) p. saepe <i>C</i> довольно часто; p.! <i>Pl</i> мало!, еще!; p. multi <i>C</i> слишком немногие; p. confidere alicui <i>bAfr</i> не особенно доверять кому-л.;\n<b>2)</b> <i>иногда</i> = non: p. castus <i>H</i> осквернённый, запятнанный."],
  ["parumper", "<big>parumper</big>\n\n<b>parum–per</b> <i>adv.</i>\n<b>1)</b> на короткое время, (на) минутку (tace p. <i>Pl</i>);\n<b>2)</b> в короткое время, быстро <i>Enn</i>."],
  ["parunculus", "<big>parunculus</big>\n\n<b>parunculus, i</b> <i>m</i> [<i>demin. к</i> paro III]\nнебольшое судно, маленькая барка <i>C</i>."],
  ["Parus", "<big>Parus</big>\n\n<b>Parus, i</b> <i>f</i>\n= Paros."],
  ["parva", "<big>parva</big>\n\n<b>parva, ae</b> <i>f</i> [parvus]\nдевочка <i>Ter</i>."],
  ["parve", "<big>parve</big>\n\n<b>parve</b> [parvus]\nнемного, слегка (flecti <i>Vtr</i>); чуть-чуть (parvissime aliquid memorare <i>CA</i>)."],
  ["parvibibulus", "<big>parvibibulus</big>\n\n<b>parvi–bibulus, a, um</b> [bibo]\nмало пьющий <i>CA</i>."],
  ["parvicollis", "<big>parvicollis</big>\n\n<b>parvi–collis, e</b> [collum]\nс короткой шеей <i>CA</i>."],
  ["parvior", "<big>parvior</big>\n\n<b>parvior</b> <i>CA</i>\n= minor."],
  ["parvipendentia", "<big>parvipendentia</big>\n\n<b>parvi–pendentia, ae</b> <i>f</i>\nпренебрежение, презрение <i>Boet</i>."],
  ["parvipendo", "<big>parvipendo</big>\n\n<b>parvipendo, —, —, ere</b> (<i>чаще раздельно</i> parvi pendo)\nне очень уважать, считать маловажным <i>Ter etc.</i>"],
  ["parvipensio", "<big>parvipensio</big>\n\n<b>parvipensio, onis</b> <i>f</i> <i>v. l.</i>\n= parvipendentia."],
  ["parvissimus", "<big>parvissimus</big>\n\n<b>parvissimus</b> <i>Vr, Lcr</i>\n= minimus."],
  ["parvitas", "<big>parvitas</big>\n\n<b>parvitas, atis</b> <i>f</i> [parvus]\nнебольшой объём, ничтожные размеры (propter parvitatem cerni non posse <i>C</i>); незначительность (quaestioriis <i>AG</i>): mea p. <i>VM</i> моя скромная особа, я."],
  ["parvolus", "<big>parvolus</big>\n\n<b>parvolus, a, um</b> <i>арх.</i>\n= parvulus I."],
  ["parvulum", "<big>parvulum</big>\n\n<b>I parvulum, i</b> <i>n</i> [parvulus I]\nмелочь, безделица, пустяк <i>H</i>.\n\n<b>II parvulum</b> <i>adv.</i> [parvulus I]\nвесьма мало: p. differre <i>PJ</i> мало различаться."],
  ["parvulus", "<big>parvulus</big>\n\n<b>I parvulus, a, um</b> [<i>demin. к</i> parvus]\n<b>1)</b> маленький, крохотный (navicula <i>Cs</i>; oppidum <i>QC</i>); мелкий, незначительный, пустячный (proelium <i>Cs</i>);\n<b>2)</b> маленький, молоденький (ursus <i>Cs</i>; filia <i>Nep</i>).\n\n<b>II parvulus, i</b> <i>m</i>\nдитя <i>Just</i>: a parvulo <i>Ter</i> (a parvulis <i>Cs</i>) с молодых лет, с детства, сызмальства."],
  ["parvum", "<big>parvum</big>\n\n<b>parvum, i</b> <i>n</i> [parvus]\nнебольшое количество, мелочь: parvo contentus <i>C etc.</i> довольствующийся малым; parvi (<i>или</i> parvo) facere (aestimare) <i>Pl, Ter, C etc.</i> мало уважать, не придавать большого значения; parvi refert <i>Ter, C etc.</i> неважно; parvi esse <i>C</i> не иметь большого значения; parvo emere <i>H</i> дёшево купить; parva loquor <i>Cld</i> я мало сказал, <i>т. е.</i> мало того <i>или</i> скажу больше; si parva licet componere magnis <i>V</i> если позволено (будет) сопоставить малое с великим."],
  ["parvus", "<big>parvus</big>\n\n<b>I parvus, a, um</b> (<i>compar.</i> minor, us, <i>superl.</i> minimus)\n<b>1)</b> малый, маленький, небольшой, мелкий (insula <i>L</i>; navis <i>C</i>; avis <i>PM</i>); незначительный (numerus navium <i>Nep</i>; pondus <i>PM</i>); краткий, кратковременный, короткий (tempus <i>Lcr</i>; vita <i>Lcn</i>; nox <i>VF</i>); маленький, молодой (filius, frater <i>C</i>): minor natu <i>C</i> младший || скудный (cibus <i>O</i>; herba <i>PM</i>);\n<b>2)</b> неважный, жалкий (carmen <i>H</i>);\n<b>3)</b> слабый, тихий (vox, murmur <i>O</i>); робкий, малодушный (animus <i>C</i>);\n<b>4)</b> скромный, смиренный (verbis minoribus uti <i>O</i>);\n<b>5)</b> второстепенный, незаметный (senator <i>bAfr</i>). — <i>См. тж.</i> parvum.\n\n<b>II parvus, i</b> <i>m</i>\nмальчик, ребёнок <i>V, H, pl.</i> дети <i>Ter, C, H</i>: a parvo <i>L</i> (a parvis <i>Ter, C</i>) с детства, смолоду."],
  ["Pasargadae", "<big>Pasargadae</big>\n\n<b>Pasargadae, arum</b> <i>и</i> <b>Pasargada, orum</b> <i>n</i>\nПасаргады, <i>древнейшая столица Персии близ Персеполя</i> (<i>развалины к юго-вост. от нын. Исфагана</i>) <i>PM, QC</i>."],
  ["pascalis", "<big>pascalis</big>\n\n<b>pascalis, e</b> [<i>из</i> pascualis <i>от</i> pascuum]\nпасущийся (pecus <i>LM</i>; oves <i>Cato</i>)."],
  ["pasceolus", "<big>pasceolus</big>\n\n<b>pasceolus, i</b> <i>m</i> (<i>греч.</i>)\nкошелёк <i>Cato, Pl, LM</i>."],
  ["pascha", "<big>pascha</big>\n\n<b>pascha, atis</b> <i>n</i> <i>и</i> <b>pascha, ae</b> <i>f</i> (<i>евр.</i>)\n<b>1)</b> праздник Пасхи <i>Eccl</i>;\n<b>2)</b> пасхальный агнец <i>Vlg</i>."],
  ["paschalis", "<big>paschalis</big>\n\n<b>paschalis, e</b> [pascha]\nпасхальный (tempus <i>CTh</i>; laetitiae dies <i>CJ</i>): victima p. <i>Eccl</i> = pascha 2."],
  ["pascito", "<big>pascito</big>\n\n<b>pascito, —, —, are</b> [<i>intens. к</i> pasco]\nпастись, есть (<i>о животных</i>) <i>Vr</i>."],
  ["pasco", "<big>pasco</big>\n\n<b>pasco, pavi, pastum, ere</b>\n<b>1.</b> <i>act.</i>\n<b>1)</b> пасти (greges armentaque <i>O</i>); разводить (equos <i>V</i>); заниматься скотоводством (bene p. <i>C</i>);\n<b>2)</b> кормить, питать, содержать (aliquem <i>C etc.</i>; viginti ventres et canem <i>Pt</i>);\n<b>3)</b> ухаживать, холить, отращивать (barbam <i>H</i>; crinem <i>V</i>);\n<b>4)</b> поддерживать (flammam <i>O</i>); лелеять (spes inanes <i>V</i>);\n<b>5)</b> утолять (jejunia <i>O</i>);\n<b>6)</b> обрабатывать, возделывать (agros <i>M</i>);\n<b>7)</b> увеличивать, нагромождать (nummos alienos <i>H</i>); обогащать (aliquem aliqua re <i>C</i>);\n<b>8)</b> тешить, услаждать, радовать, веселить (oculos animumque aliqua re <i>или</i> in aliqua re <i>C</i>);\n<b>9)</b> скармливать, стравливать, опустошать (collium asperrima <i>V</i>); объедать (silvas, gramina <i>V</i>); съедать, пожирать (cibum <i>O</i>);\n<b>2.</b> <i>pass.</i>\n<b>1)</b> пастись (pecora pastum propellere <i>L</i>; capellae pascentes <i>V</i>); есть (pulli pascebantur <i>C</i>); питаться (boves frondibus pascuntur <i>V</i>; mendicato cibo pasci <i>O</i>): pasto cibo pasci <i>O</i> пережёвывать съеденное; eodem cibo pastus <i>Pt</i> вскормленный той же пищей, <i>т. е.</i> воспитанный в том же духе;\n<b>2)</b> <i>перен.</i> жить (scelere p. <i>C</i>);\n<b>3)</b> (<i>тж.</i> animum pascere <i>V</i>) наслаждаться (animus pascitur otiis <i>O</i>)."],
  ["pascor", "<big>pascor</big>\n\n<b>pascor, pastus sum, pasci</b> — <i>см.</i>\npasco 2."],
  ["pascua", "<big>pascua</big>\n\n<b>pascua, ae</b> <i>f</i>\nпастбище, выгон <i>Tert</i>."],
  ["pascualis", "<big>pascualis</big>\n\n<b>pascualis, e</b> <i>Vlg</i>\n= pascalis."],
  ["pascuosus", "<big>pascuosus</big>\n\n<b>pascuosus, a, um</b> [pascuum]\nпригодный для пастьбы (herba <i>Ap</i>)."],
  ["pascuum", "<big>pascuum</big>\n\n<b>pascuum, i</b> <i>n</i> [pascuus]\n<b>1)</b> <i>преим. pl.</i> пастбище, выгон (gregem in pascua mittere <i>Col</i>);\n<b>2)</b> корм, пища (pascuis pecudum destinata silva <i>Dig</i>)."],
  ["pascuus", "<big>pascuus</big>\n\n<b>pascuus, a, um</b> [pasco]\nпастбищный (ager <i>Pl, C</i>)."],
  ["Pasicompsa", "<big>Pasicompsa</big>\n\n<b>Pasicompsa, ae</b> <i>f</i> (<i>греч.</i> «вся украшенная»)\nПасикомпса, <i>женское имя</i> <i>Pl</i>."],
  ["Pasiphae", "<big>Pasiphae</big>\n\n<b>Pasiphae, es</b> <i>и</i> <b>Pasiphaa, ae</b> <i>f</i> (<i>греч.</i> «вся светящаяся»)\nПасифая, <i>дочь Солнца, сестра Кирки, жена Миноса, мать Андрогея, Федры и Ариадны; Венера из ненависти внушила ей любовь к быку, от которого она родила Минотавра</i> <i>C, V, O etc.</i>"],
  ["Pasiphaeia", "<big>Pasiphaeia</big>\n\n<b>Pasiphaeia, ae</b> <i>f</i>\nдочь Пасифаи, <i>т. е.</i> Федра <i>O</i>."],
  ["Pasithea", "<big>Pasithea</big>\n\n<b>Pasithea, ae</b> <i>и</i> <b>Pasithee, as</b> <i>f</i>\nПасифея, <i>одна из трёх Граций</i> (= Aglaie) <i>Ctl, Sen, St</i>."],
  ["Pasitigris", "<big>Pasitigris</big>\n\n<b>Pasitigris, idis</b> <i>и</i> <b>is</b> <i>m</i>\nПаситигрида, <i>название реки Eulaeus</i> (<i>ныне</i> Карун) <i>при впадении её в Персидский валив</i> <i>QC</i>."],
  ["passarius", "<big>passarius</big>\n\n<b>passarius, a, um</b> [passus <i>к</i> pando II]\nвыставленный на солнце, <i>т. е.</i> сушёный (ficus <i>Capit</i> —<i>v. l.</i>)."],
  ["Passaron", "<big>Passaron</big>\n\n<b>Passaro(n), onis</b> <i>f</i>\nПассарон, <i>город в области Молоссида</i> (<i>Эпир</i>) <i>L</i>."],
  ["passeolus", "<big>passeolus</big>\n\n<b>passeolus, i</b> <i>m, f</i> <i>PM v. l.</i>\n= phaselus."],
  ["passer", "<big>passer</big>\n\n<b>passer, eris</b> <i>m</i>\n<b>1)</b> воробей <i>Ctl, C etc.</i>: p. marinus («заморский воробей») <i>Pl, Aus</i> страус || <i>ласк.</i> воробушек (meus pullus p. <i>Pl</i>);\n<b>2)</b> вид камбалы, палтус <i>H, PM, O</i>."],
  ["passerarius", "<big>passerarius</big>\n\n<b>passerarius, a, um</b> [passer]\nворобьиный (ficus <i>Capit</i> — <i>v. l.</i>)."],
  ["passerculus", "<big>passerculus</big>\n\n<b>passerculus, i</b> <i>m</i> [<i>demin. к</i> passer]\nворобушек <i>Pl, C, PJ</i>."],
  ["passibilis", "<big>passibilis</big>\n\n<b>passibilis, e</b> [patior]\nподверженный страданию <i>Tert</i>."],
  ["passibiliter", "<big>passibiliter</big>\n\n<b>passibiliter</b>\nстрадая, со страданием <i>Tert</i>."],
  ["passim", "<big>passim</big>\n\n<b>passim</b> [pando II]\n<b>1)</b> рассеянно, там и сям, в разных местах, всюду (vagari <i>C, Just</i>; palari per agros <i>L</i>; diffugere <i>V</i>);\n<b>2)</b> без разбора, вперемешку, вместе (indocti doctique p. <i>H</i>): p. nomina data (sunt) <i>T</i> записывались все подряд."],
  ["passio", "<big>passio</big>\n\n<b>passio, onis</b> <i>f</i> [patior]\n<b>1)</b> претерпевание (poenarum <i>Ap</i>);\n<b>2)</b> страдание (corporum passiones <i>Eccl</i>): p. epileptica (sacra <i>или</i> puerilis) <i>CA</i> эпилепсия; p. dominica <i>Eccl</i> страсти господни;\n<b>3)</b> страсть, аффект (animi passiones <i>Aug</i>);\n<b>4)</b> явление природы (passiones, quum impulsu frigidioris aeris venti generantur <i>Ap</i>);\n<b>5)</b> <i>грам.</i> страдательность."],
  ["passionalis", "<big>passionalis</big>\n\n<b>passionalis, e</b> <i>CA, Tert</i>\n= passibilis."],
  ["passito", "<big>passito</big>\n\n<b>passito, —, —, are</b>\nчирикать (<i>о скворцах</i>) <i>Su</i>."],
  ["passive", "<big>passive</big>\n\n<b>I passive</b> [passivus I]\nповсюду, рассеянно (dispersi per colla crines <i>Ap</i>).\n\n<b>II passive</b> [passivus II] <i>грам.</i>\nпассивно, в страдательной форме (dicere)."],
  ["passivitas", "<big>passivitas</big>\n\n<b>passivitas, atis</b> <i>f</i> [passivus I]\nперепутанность, беспорядочность <i>Tert</i>."],
  ["passivitus", "<big>passivitus</big>\n\n<b>passivitus</b> <i>adv.</i> <i>Tert</i>\n= passim."],
  ["passivus", "<big>passivus</big>\n\n<b>I passivus, a, um</b> [pando II]\n<b>1)</b> наносимый без разбора (morsus, <i>sc.</i> canum <i>Ap</i>);\n<b>2)</b> рассеянный, находящийся повсюду, повсеместный (nomen dei <i>Tert</i>);\n<b>3)</b> перемешанный (seminum congeries <i>Ap</i>).\n\n<b>II passivus, a, um</b> [patior]\n<b>1)</b> восприимчивый, ощущающий (animus <i>Ap</i>);\n<b>2)</b> <i>грам.</i> пассивный, страдательный (verbum)."],
  ["passum", "<big>passum</big>\n\n<b>passum, i</b> <i>n</i> [<i>из</i> passus <i>от</i> pando II,5]\n(<i>sc.</i> vinum) вино из сушёного винограда <i>V, Col</i>."],
  ["passus", "<big>passus</big>\n\n<b>I passus, a, um</b> <i>part. pf. к</i>\n<b>1)</b> patior <i>и</i>\n<b>2)</b> pando II.\n\n<b>II passus, us</b> <i>m</i> [pando II]\n<b>1)</b> шаг (lentus <i>O</i>; rapidis passibus ferri <i>V</i>);\n<b>2)</b> поступь, движение: strepitum passu non faciente <i>O</i> бесшумной поступью;\n<b>3)</b> стопа: passu tenaci stare <i>O</i> стоять твёрдой ногой (стопой);\n<b>4)</b> след ноги (passus in litore <i>V</i>);\n<b>5)</b> пасс(ус) (двойной шаг) <i>PM</i> = 1,48 <i>м</i>: mille passus <i>C etc.</i> римская миля = 1478,7 <i>м.</i>"],
  ["pastillico", "<big>pastillico</big>\n\n<b>pastillico, —, —, are</b> [pastillus]\nиметь форму шарика <i>PM</i>."],
  ["pastillum", "<big>pastillum</big>\n\n<b>pastillum, i</b> <i>n</i> [<i>demin. к</i> panis]\nхлебец <i>Vr</i>."],
  ["pastillus", "<big>pastillus</big>\n\n<b>pastillus, i</b> <i>m</i> [<i>demin. к</i> panis]\nшарик <i>PM</i>; пилюля <i>CC, PM</i>; ароматическая лепёшка (<i>для жевания</i>) <i>H, M, PM</i>."],
  ["pastinaca", "<big>pastinaca</big>\n\n<b>pastinaca, ae</b> <i>f</i>\nпастернак <i>PM</i>."],
  ["pastinatio", "<big>pastinatio</big>\n\n<b>pastinatio, onis</b> <i>f</i> [pastino]\n<b>1)</b> вскапывание почвы для виноградника <i>Col</i>;\n<b>2)</b> вскопанная почва <i>Col</i>."],
  ["pastinator", "<big>pastinator</big>\n\n<b>pastinator, oris</b> <i>m</i> [pastino]\nвскапывающий почву для виноградника <i>Col, Aug</i>."],
  ["pastinatum", "<big>pastinatum</big>\n\n<b>pastinatum, i</b> <i>n</i> (<i>sc.</i> solum) <i>Col, PM</i>\n= pastinatio 2."],
  ["pastino", "<big>pastino</big>\n\n<b>pastino, avi, atum, are</b> [pastinum]\nвскапывать, разрыхлять (vineas <i>PM</i>; agrum <i>Col</i>)."],
  ["pastinum", "<big>pastinum</big>\n\n<b>pastinum, i</b> <i>n</i>\n<b>1)</b> мотыга для вскапывания почвы <i>Col</i>;\n<b>2)</b> вскапывание, мотыжение <i>Pall</i>;\n<b>3)</b> вскопанный участок <i>Pall, Dig</i>."],
  ["pastio", "<big>pastio</big>\n\n<b>pastio, onis</b> <i>f</i> [pasco]\n<b>1)</b> пастьба, разведение животных <i>Vr, Col</i>;\n<b>2)</b> пастбище, выгон <i>Vr, C</i>."],
  ["pastophori", "<big>pastophori</big>\n\n<b>pastophori, orum</b> <i>и</i> <b>um</b> <i>m</i> (<i>греч.</i>)\nпастофоры, <i>жрецы, совершавшие обряд обхода часовни и нёсшие изображение божества</i> <i>Ap</i>."],
  ["pastophorium", "<big>pastophorium</big>\n\n<b>pastophorium, i</b> <i>n</i> (<i>греч.</i>)\nпастофорий, <i>место в храме для хранения идолов</i> <i>Vlg, Hier</i>."],
  ["pastor", "<big>pastor</big>\n\n<b>pastor, oris</b> <i>m</i> [pasco]\n<b>1)</b> пастух (ovium <i>Eccl</i>): p. Aetnaeus <i>O</i> = Polyphemus; pastorum domina <i>O</i> = Pales;\n<b>2)</b> <i>перен.</i> пастырь (populi <i>Q</i>);\n<b>3)</b> ухаживающий за домашней птицей, птичник (p. gallinarum <i>Col</i>; p. columbarius <i>Vr</i>)."],
  ["pastoralis", "<big>pastoralis</big>\n\n<b>pastoralis, e</b> [pastor]\nпастушеский, пастуший (vita <i>Vr</i>; canis <i>Col</i>; myrtus <i>V</i>)."],
  ["pastoricius", "<big>pastoricius</big>\n\n<b>pastoricius (pastorius), a, um</b> <i>Vr, C, Ap</i>\n= pastoralis."],
  ["pastura", "<big>pastura</big>\n\n<b>pastura, ae</b> <i>f</i> [pasco]\nпастьба <i>Pall</i>."],
  ["pastus", "<big>pastus</big>\n\n<b>I pastus</b> <i>part. pf. к</i>\npasco.\n\n<b>II pastus, us</b> <i>m</i> [pasco]\n<b>1)</b> кормление (anquirere pastum et latibula <i>C</i>);\n<b>2)</b> корм (terra fundit ex se pastus varios <i>C</i>); <i>перен.</i> пища, питание (suavissimus p. animorum <i>C</i>);\n<b>3)</b> пастбище (e pastu decedere <i>V</i>)."],
  ["patagiarius", "<big>patagiarius</big>\n\n<b>patagiarius, i</b> <i>m</i> [patagium]\nмастер, изготовляющий обшивку (тесьму) для платьев <i>Pl</i>."],
  ["patagiatus", "<big>patagiatus</big>\n\n<b>patagiatus, a, um</b> [patagium]\nобшитый тесьмой, окаймлённый, отороченный (tunica <i>Pl</i>)."],
  ["patagium", "<big>patagium</big>\n\n<b>patagium, i</b> <i>n</i> (<i>греч.</i>)\nобшивка, кайма, тесьма, бордюр <i>Ap, Tert</i>."],
  ["patagus", "<big>patagus</big>\n\n<b>patagus, i</b> <i>m</i> (<i>греч.</i>)\nшум, грохот <i>Pl ap. Macr</i>."],
  ["Patara", "<big>Patara</big>\n\n<b>Patara, orum</b> <i>n</i>\nПатары, <i>приморский город в юго-зап. Ликии с оракулом Аполлона</i> <i>L</i>."],
  ["pataracinum", "<big>pataracinum</big>\n\n<b>pataracinum, i</b> <i>n</i> [patera]\nширокая чаша <i>Pt</i>."],
  ["Pataraeus", "<big>Pataraeus</big>\n\n<b>Pataraeus (-ēus), a, um</b> [Patara]\nпатарейский (regia <i>O</i>)."],
  ["Patarani", "<big>Patarani</big>\n\n<b>Patarani, orum</b> <i>m</i>\nжители города Patara <i>C</i>."],
  ["Patareus", "<big>Patareus</big>\n\n<b>Patare͡us, eī</b> <i>и</i> <b>eos</b> <i>m</i> [Patara]\nПатарейский, <i>эпитет Аполлона</i> <i>H</i>."],
  ["Patavinitas", "<big>Patavinitas</big>\n\n<b>Patavinitas, atis</b> <i>f</i> [Patavinus]\nпатавинизм, <i>диалектные особенности языка населения города Патавия</i> (<i>обнаруживаемые Азинием Поллионом у Ливия</i>) <i>Q</i>."],
  ["Patavinus", "<big>Patavinus</big>\n\n<b>I Patavinus, a, um</b> [Patavium]\nпатавийский: Patavinum municipium <i>C</i> = Patavium; Patavina volumina <i>Sid</i> = история Рима Тита Ливия.\n\n<b>II Patavinus, i</b> <i>m</i>\nпатавинец, житель города Патавия <i>C</i>."],
  ["Patavium", "<big>Patavium</big>\n\n<b>Patavium, i</b> <i>n</i>\nПатавий, <i>город в Gallia Cisalpina, на территории венетов, между Мутиной и Альтином</i> (<i>ныне</i> Padua); <i>место рождения Тита Ливия</i> <i>L, Su, PM</i>."],
  ["patefacio", "<big>patefacio</big>\n\n<b>patefacio, feci, factum, ere</b> [pateo+ facio]\n<b>1)</b> широко раскрывать, распахивать (portas <i>Cs, L</i>; <i>перен.</i> aures assentatoribus <i>C</i>); разевать, разверзать (os <i>PM</i>); раздвигать, расширять (vulnus latius <i>CC</i>);\n<b>2)</b> взрывать, рыть (sulcum aratro <i>O</i>);\n<b>3)</b> растягивать, удлинять (aciem longius <i>L</i>);\n<b>4)</b> открывать путь (к), делать доступным (loca <i>Nep</i>); прокладывать, пролагать (iter per Alpes <i>Cs</i>);\n<b>5)</b> делать видимым (lux orbem patefacit <i>O</i>);\n<b>6)</b> раскрывать, вскрывать (conjurationem <i>C, Sl</i>; veritatem <i>C</i>; cogitata <i>Nep</i>); открывать (alicui consilia alicujus <i>Cs, VP</i>); обнаруживать, проявлять (odium suum in aliquem <i>C</i>)."],
  ["patefactio", "<big>patefactio</big>\n\n<b>patefactio, onis</b> <i>f</i> [patefacio]\nоткрытие, раскрытие, обнаружение (rerum opertarum <i>C</i>)."],
  ["patefio", "<big>patefio</big>\n\n<b>patefio, factus sum, fieri</b> <i>pass. к</i>\npatefacio."],
  ["patella", "<big>patella</big>\n\n<b>patella, ae</b> <i>f</i> [<i>demin. к</i> patera]\n<b>1)</b> мелкая кастрюля, чашка <i>или</i> сковорода <i>Vr, H, PM</i>: invenit p. operculum <i>погов. Hier</i> нашла (себе) кастрюля крышку (<i>ср.</i> «два сапога — пара»);\n<b>2)</b> жертвенная чаша: edere de patella <i>погов. C</i> = презрительно относиться к религиозным обрядам;\n<b>3)</b> <i>анат.</i> коленная чашка <i>CC</i>."],
  ["patellarius", "<big>patellarius</big>\n\n<b>patellarius, a, um</b> [patella]\nвкушающий из жертвенной чаши: dii patellarii <i>Pl</i> = лары и пенаты (<i>для которых на свадьбах выставлялась тарелка с первыми плодами и солонка</i>)."],
  ["patena", "<big>patena</big>\n\n<b>patena, ae</b> <i>f</i> <i>Ph, Col etc.</i>\n= patina."],
  ["patens", "<big>patens</big>\n\n<b>I patens, entis</b>\n<b>1.</b> <i>part. praes. к</i> pateo;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> открытый (locus <i>L</i>; caelum <i>C</i>; campi <i>V, Sl</i>);\n<b>2)</b> явный, очевидный (causa <i>O</i>).\n\n<b>II patens, entis</b> <i>n</i>\nоткрытое место (ex patenti in angustias <i>L</i>); брешь, пролом (per patentia in urbem vadere <i>L</i>)."],
  ["patenter", "<big>patenter</big>\n\n<b>patenter</b> [patens 1]\nоткрыто, явно <i>C, Boet</i>."],
  ["pateo", "<big>pateo</big>\n\n<b>pateo, ui, —, ere</b>\n<b>1)</b> быть открытым (domus patet <i>Cs</i>; valvae patent <i>C</i>; mille ad mortem aditus patent <i>SenT</i>); быть раскрытым, зиять (vulnera patent <i>VF</i>);\n<b>2)</b> быть доступным (caelum patet <i>O</i>; honores alicui patent <i>C</i>); быть удобопроходимым (via patet <i>C</i>);\n<b>3)</b> находиться в распоряжении, быть к услугам (cuncta maria terraeque alicui patent <i>Sl</i>);\n<b>4)</b> находиться под угрозой (p. ictui <i>T</i>; p. insidiis alicujus <i>QC</i>); быть подверженным (longis morbis <i>CC</i>);\n<b>5)</b> быть заметным, видимым (nervi patent <i>CC</i>);\n<b>6)</b> быть очевидным, ясным, явным (causa patet <i>O</i>); обнаруживаться (conjuratio patuit <i>C</i>): p. nulli <i>H</i> никому не быть известным; vera incessu patuit dea <i>V</i> в (одной уж) поступи видна была истинная богиня;\n<b>7)</b> простираться (campus late patet <i>L</i>; vallis usque ad mare patet <i>Cs</i>): in latitudinem milia passuum centum octoginta p. <i>Cs</i> простираться на 180 римских миль в ширину; gallina patentibus in orbem alis <i>Pt</i> курица с кругообразно распростёртыми крыльями; tum denique quam hoc late pateat intelleges <i>C</i> лишь тогда ты поймёшь эти (традиции) во всей их широте, <i>т. е.</i> сможешь сделать из них дальнейшие выводы;\n<b>8)</b> быть распространённым, иметь обширное применение (haec ars late patet <i>C</i>)."],
  ["pater", "<big>pater</big>\n\n<b>pater, tris</b> <i>m</i>\n<b>1)</b> отец: p. familiae (<i>арх.</i> familias) <i>C, Cs etc.</i> отец (глава) семейства; patre Sejo Strabone <i>T</i> сын Сея Страбона || <i>pl.</i> родители <i>O</i>;\n<b>2)</b> тесть, свёкор <i>T etc.</i>; <i>pl.</i> предки (patrum more <i>C</i>);\n<b>3)</b> <i>pl.</i> сенаторы (<i>из патрициев</i>), сенат <i>C etc.</i>: patres (et) conscripti (<i>формула обращения</i>) <i>C</i> сенаторы-патриции и сенаторы выборные, господа сенаторы; res ad patres relata <i>T</i> вопрос, переданный на рассмотрение сената;\n<b>4)</b> <i>pl.</i> патриции (<i>как сословие</i>) <i>C</i>;\n<b>5)</b> основатель, родоначальник, отец (Herodotus p. historiae <i>C</i>): p. esuritionum <i>Ctl</i> бедняк на бедняков, горемыка;\n<b>6)</b> устроитель (cenae <i>H</i>);\n<b>7)</b> отец (<i>как почётное прозвище</i>) (p. Juppiter <i>C</i>; p. Aeneas <i>V</i>); p. Lemnius <i>V</i> = Вулкан; p. patratus <i>C</i> главный из жрецов-фециалов; p. patriae <i>C, PJ</i> отец отечества (<i>почётнейшее звание для особенно заслуженных государственных деятелей</i>)."],
  ["patera", "<big>patera</big>\n\n<b>patera, ae</b> <i>f</i> [pateo]\nчаша (<i>преим.</i> для возлияний), жертвенный сосуд <i>Pl, C, V etc.</i>"],
  ["Paterculus", "<big>Paterculus</big>\n\n<b>Paterculus, i</b> <i>m</i>\nПатеркул, <i>римск. cognomen — см.</i> Vellejus."],
  ["paterfamilias", "<big>paterfamilias</big>\n\n<b>pater–familias</b> (<i>чаще раздельно</i>) — <i>см.</i>\npater 1."],
  ["paterne", "<big>paterne</big>\n\n<b>paterne</b>\nпо-отечески <i>Aug</i>."],
  ["paternitas", "<big>paternitas</big>\n\n<b>paternitas, atis</b> <i>f</i>\n<b>1)</b> отцовство <i>Vlg</i>;\n<b>2)</b> отеческие чувства <i>Aug</i>."],
  ["paternus", "<big>paternus</big>\n\n<b>paternus, a, um</b> [pater]\n<b>1)</b> отцовский, доставшийся по наследству от отца (possessiones <i>C</i>; ager <i>Sen</i>): p. avus <i>H, PJ</i> дед с отцовской стороны;\n<b>2)</b> направленный против отца (odium <i>Nep</i>; injuria <i>Ter</i>);\n<b>3)</b> отечестаенный, родной (terra <i>O</i>; flumen <i>H</i>)."],
  ["patesco", "<big>patesco</big>\n\n<b>patesco, patui, —, ere</b> [<i>inchoat. к</i> pateo]\n<b>1)</b> открываться (portus patescit <i>V</i>);\n<b>2)</b> становиться явным, выходить наружу, обнаруживаться (insidiae patescunt <i>V</i>);\n<b>3)</b> простираться, тянуться (campus late patescit <i>L</i>)."],
  ["patetae", "<big>patetae</big>\n\n<b>patetae, arum</b> <i>f</i>\nсушёные финики <i>PM</i>."],
  ["pateti", "<big>pateti</big>\n\n<b>pateti, orum</b> <i>m</i> <i>CA</i>\n= patetae."],
  ["pathetice", "<big>pathetice</big>\n\n<b>pathetice</b>\nстрастно (tractare aliquid <i>Macr</i>)."],
  ["patheticus", "<big>patheticus</big>\n\n<b>patheticus, a, um</b> (<i>греч.</i>)\nстрастный (oratio <i>Macr</i>)."],
  ["pathicus", "<big>pathicus</big>\n\n<b>pathicus, a, um</b> (<i>греч.</i>)\nраспутный, развратный <i>Ctl, J</i>; непристойный (libelli <i>M</i>)."],
  ["pathos", "<big>pathos</big>\n\n<b>pathos</b> <i>n</i> (<i>греч.</i>)\nпафос, страсть <i>Macr</i>."],
  ["patibilis", "<big>patibilis</big>\n\n<b>patibilis, e</b> [patior]\n<b>1)</b> терпимый (dolor <i>C</i>);\n<b>2)</b> чувствительный, восприимчивый (natura <i>C</i>);\n<b>3)</b> страдательный, пассивный (elementum femininum <i>Lact</i>)."],
  ["patibulatus", "<big>patibulatus</big>\n\n<b>patibulatus, a, um</b> [patibulum]\nс колодкой на шее <i>Pl</i>."],
  ["patibulum", "<big>patibulum</big>\n\n<b>patibulum, i</b> <i>n</i> [pateo]\n<b>1)</b> шейная колодка, <i>надевавшаяся на приговорённых к распятию</i> <i>Pl, C etc.</i>;\n<b>2)</b> поперечина креста <i>T</i>;\n<b>3)</b> виселица <i>CTh</i>;\n<b>4)</b> вилообразная тычина (<i>для виноградных лоз</i>) <i>Cato, PM</i>."],
  ["patibulus", "<big>patibulus</big>\n\n<b>patibulus, i</b> <i>m</i> <i>Pl</i>\n= patibulum 1."],
  ["patiens", "<big>patiens</big>\n\n<b>patiens, entis</b>\n<b>1.</b> <i>part. praes. к</i> patior;\n<b>2.</b> <i>adj.</i> (хорошо) переносящий, способный переносить, выносливый (frigoris <i>Col</i>; laboris <i>Su</i> <i>и</i> in laboribus <i>C</i>, pulveris atque solis <i>H</i>); терпеливый, стойкий (miser et p. <i>Cs</i>); непреклонный (Lacedaemon <i>H</i>); <i>поэт.</i> выдерживающий давление, негнущийся, крепкий (aratrum <i>O</i>; saxum <i>Prp</i>): amnis p. navium <i>L, T</i> судоходная река."],
  ["patienter", "<big>patienter</big>\n\n<b>patienter</b> [patiens]\nтерпеливо (ferre aliquid <i>C</i>)."],
  ["patientia", "<big>patientia</big>\n\n<b>patientia, ae</b> <i>f</i> [patiens]\n<b>1)</b> перенесение (famis, frigoris <i>C</i>); терпеливость, терпение, выносливость, выдержка, стойкость (laboris <i>L</i>; in carendo <i>Sen</i>);\n<b>2)</b> уступчивость, снисходительность (abuti patientia alicujus <i>C</i>);\n<b>3)</b> равнодушие, вялость, мягкотелость (in patientia firmitudinem simulare <i>T</i>);\n<b>4)</b> разврат, распутство (multiplex <i>T</i>): muliebris patientiae legem accipere <i>Pt</i> = devirginari;\n<b>5)</b> покорность, подчинение (servilis <i>T</i>)."],
  ["patina", "<big>patina</big>\n\n<b>patina, ae</b> <i>f</i> (<i>греч.</i>)\n<b>1)</b> миска, <i>тж.</i> мелкая кастрюля <i>или</i> сковородка <i>Pl, Ter, C etc.</i>;\n<b>2)</b> кормушка, ясли <i>Veg</i>."],
  ["patinarius", "<big>patinarius</big>\n\n<b>I patinarius, a, um</b> [patina]\nотносящийся к миске: struices patinariae <i>Pl</i> горы блюд; piscis p. <i>Pl</i> рыба в соусе.\n\n<b>II patinarius, i</b> <i>m</i>\nобжора <i>Su</i>."],
  ["patio", "<big>patio</big>\n\n<b>patio, —, —, ere</b> <i>арх. Naev, C</i>\n= patior."],
  ["patior", "<big>patior</big>\n\n<b>patior, passus sum, pati</b> <i>depon.</i>\n<b>1)</b> терпеть, претерпевать, переносить (dolores, servitutem <i>C</i>; naufragium <i>SenT, Eutr</i>; sitim <i>L</i>): supplicium patitur non nova culpa novum <i>O</i> не я первый совершил этот проступок, но первый несу за него кару || терпеливо сносить, переносить (omnia <i>C</i>; pauperiem <i>H</i>): quidvis et facere et p. <i>H</i> (нужда заставляет) делать и выносить всё, что придётся; p. muliebria <i>Sl, Pt</i> <i>или</i> venerem <i>O</i>, <i>тж.</i> p. virum <i>Sen, Pt, Tert</i> = viro subire;\n<b>2)</b> страдать, мучиться: nihil gravius audenti, quam ignavo patiendum (est) <i>T</i> храбрецу приходится страдать ничуть не больше, чем трусу;\n<b>3)</b> подвергаться, испытывать (p. injuriam <i>Sen</i>): p. scelus <i>SenT</i> быть жертвой преступления || понести (poenam <i>PJ</i>): cogitationis poenam nemo patitur <i>Dig</i> никто не может быть наказан за свои мысли;\n<b>4)</b> разрешать, допускать, позволять (aliquid <i>C, Sl etc.</i>): ut tempus locusque patitur <i>L</i> насколько позволяют время и место; patiens Vocari Caesaris ultor <i>H</i> разрешающий именовать себя мстителем за Цезаря; qui oculos nostros patiuntur <i>Sen</i> то, что доступно нашему глазу; p. non potest divitias <i>Sen</i> богатство кружит ему голову; non possum p., quin tibi caput demulceam <i>Ter</i> не могу удержаться, чтобы не обнять тебя;\n<b>5)</b> <i>грам.</i> иметь страдательное значение: patiendi modus <i>Q</i> страдательный залог;\n<b>6)</b> продолжаться, длиться, существовать (novem saecula passa cornix <i>O</i>);\n<b>7)</b> получать, обретать (bonum <i>Pl</i>; quietem <i>QC</i>). — <i>См. тж.</i> patiens."],
  ["patisco", "<big>patisco</big>\n\n<b>patisco, —, —, ere</b> <i>арх.</i>\n= patesco."],
  ["Patmos", "<big>Patmos</big>\n\n<b>Patmos (-us), i</b> <i>f</i>\nПатмос, <i>один из Спорадских о-вов, место ссылки у римлян</i> <i>PM</i>."],
  ["pator", "<big>pator</big>\n\n<b>pator, oris</b> <i>m</i> [pateo]\nотверстие, дыра (profundus <i>Ap</i>)."],
  ["Patrae", "<big>Patrae</big>\n\n<b>Patrae, arum</b> <i>f</i>\nПатры, <i>приморский город в сев.-зап. Ахайе.</i> <i>C, L</i>."],
  ["patratio", "<big>patratio</big>\n\n<b>patratio, onis</b> <i>f</i> [patro]\nокончание, исполнение, совершение <i>VP</i>."],
  ["patrator", "<big>patrator</big>\n\n<b>patrator, oris</b> <i>m</i> [patro]\nисполнитель, виновник (necis <i>T</i>); создатель (illustrium operum <i>Hier</i>)."],
  ["patratus", "<big>patratus</big>\n\n<b>patratus</b> — <i>см.</i>\npatro <i>и</i> pater 7."],
  ["Patrensis", "<big>Patrensis</big>\n\n<b>I Patrensis, e</b> [Patrae]\nпатрийский <i>C</i>.\n\n<b>II Patrensis, is</b> <i>m</i>\nжитель города Patrae <i>C</i>."],
  ["patria", "<big>patria</big>\n\n<b>patria, ae</b> <i>f</i> [patrius]\n(<i>sc.</i> terra) отечество, родина <i>C, L etc.</i>: p. major <i>QC</i> метрополия, старая родина <i>или</i> земля отцов."],
  ["patriarcha", "<big>patriarcha</big>\n\n<b>patriarcha (-es), ae</b> <i>m</i> (<i>греч.</i>)\nглавный епископ, патриарх <i>Vop, Eccl</i>."],
  ["patrice", "<big>patrice</big>\n\n<b>patrice</b> [patricus]\nкак хозяин (дома), по-барски <i>Pl</i>."],
  ["patricia", "<big>patricia</big>\n\n<b>patricia, ae</b> <i>f</i> [patricius]\nпатрицианка <i>L</i>."],
  ["patriciatus", "<big>patriciatus</big>\n\n<b>patriciatus, us</b> <i>m</i> [patricius]\nпатрициат, звание (достоинство) патриция <i>Su</i>."],
  ["patricida", "<big>patricida</big>\n\n<b>patricida, ae</b> <i>m, f</i> [pater + caedo]\nотцеубийца <i>C, Eccl</i>."],
  ["patricius", "<big>patricius</big>\n\n<b>I patricius, a, um</b> [pater]\nпринадлежащий к родовой римской знати, патрицианский (puer <i>Pl</i>; familia <i>C etc.</i>; gens <i>J</i>).\n\n<b>II patricius, i</b> <i>m</i>\nпатриций; <i>pl.</i> патриции, сословие патрициев <i>C, L etc.</i>"],
  ["patricus", "<big>patricus</big>\n\n<b>patricus, a, um</b> [pater]\nотцовский; <i>грам.</i> (= genetivus) родительный (casus <i>Vr</i>)."],
  ["patrie", "<big>patrie</big>\n\n<b>patrie</b> [patrius]\nпо-отечески (monere <i>Q</i>)."],
  ["patrimonialis", "<big>patrimonialis</big>\n\n<b>patrimonialis, e</b> [patrimonium]\nнаследственный (munus <i>Dig</i>)."],
  ["patrimoniolum", "<big>patrimoniolum</big>\n\n<b>patrimoniolum, i</b> <i>n</i> [<i>demin. к</i> patrimonium]\nнебольшое наследство <i>Hier</i>."],
  ["patrimonium", "<big>patrimonium</big>\n\n<b>patrimonium, i</b> <i>n</i> [pater]\n<b>1)</b> наследственное имение, вотчина (filio suo p. relinquere <i>Col, C</i>); наследство, наследие (p. paterni nominis <i>C</i>);\n<b>2)</b> имущество, состояние (dissipare p. <i>C</i>)."],
  ["patrimus", "<big>patrimus</big>\n\n<b>patrimus, a, um</b> [pater]\nимеющий отца <i>или</i> родителей в живых (puer <i>C, L, T etc.</i>)."],
  ["patriota", "<big>patriota</big>\n\n<b>patriota, ae</b> <i>m</i> (<i>греч.</i>)\nсоотечественник, земляк <i>Eccl</i>."],
  ["patrioticus", "<big>patrioticus</big>\n\n<b>patrioticus, a, um</b>\nотечественный, национальный <i>Eccl</i>."],
  ["patrisso", "<big>patrisso</big>\n\n<b>patrisso, —, —, are</b> (<i>греч.</i>)\nподражать отцу (filius patrissat <i>Pl</i>); идти по стопам отца (in artificio tibi cinii <i>Ap</i>)."],
  ["patritus", "<big>patritus</big>\n\n<b>patritus, a, um</b> [pater]\nотцовский, унаследованный от отца (philosophia patrita atque avita <i>C</i>; avito ac patrito more <i>Vr</i>)."],
  ["patrium", "<big>patrium</big>\n\n<b>patrium, i</b> <i>n</i> [patrius]\n(<i>sc.</i> nomen) имя по отцу, отчество (<i>напр.</i> Anchisides сын Анхиза) <i>Q</i>."],
  ["patrius", "<big>patrius</big>\n\n<b>patrius, a, um</b> [pater]\n<b>1)</b> отцовский, отеческий (potestas <i>C</i>; amor <i>O</i>); принадлежащий (принадлежавший) отцу, доставшийся по наследству от отца (regnum <i>Sl</i>; animus <i>C</i>): bona patria <i>Ter</i> (res patriae <i>C</i>) отцовское (родовое) имение;\n<b>2)</b> дедовский, наследственный, древний (mos <i>C, O</i>; sacra <i>C</i>);\n<b>3)</b> родной, отечественный (sermo <i>C</i>): sepulcrum patrium <i>H, C etc.</i> могила в родной земле <i>или</i> семейная;\n<b>4)</b> <i>грам.</i> родительный (casus <i>AG</i>)."],
  ["patrizo", "<big>patrizo</big>\n\n<b>patrizo</b> <i>v. l.</i>\n= patrisso."],
  ["patro", "<big>patro</big>\n\n<b>patro, avi, atum, are</b> [<i>одного корня с</i> impetro]\nприводить в исполнение, совершать (facinus — <i>acc.</i> — patratus <i>и</i> patraturus <i>Sl, T</i>); выполнять (promissa <i>C</i>); заключать (pacem <i>L</i>); заканчивать (bellum <i>Sl</i>); завершать (victoria patrata <i>T</i>): patrans ocellus <i>Pers</i> истомлённый (усталый) взор."],
  ["patrocinium", "<big>patrocinium</big>\n\n<b>patrocinium, i</b> <i>n</i> [patronus]\n<b>1)</b> покровительство, защита, охрана (patrocinio alicujus uti <i>Sl</i>); судебная защита (p. alicujus adversus aliquem <i>L</i>);\n<b>2)</b> <i>преим. pl.</i> подзащитный, клиент <i>Vatinius ap. C</i>."],
  ["patrocinor", "<big>patrocinor</big>\n\n<b>patrocinor, atus sum, ari</b> <i>depon.</i> [patrocinium]\nзащищать, оказывать покровительство, покровительствовать (alicui <i>Ter, Q, T etc.</i>)."],
  ["Patrocles", "<big>Patrocles</big>\n\n<b>Patrocles, is</b> <i>и</i> <b>Patroclus (-os), i</b> <i>m</i>\nПатрокл:\n<b>1)</b> <i>сын Менетия и Сфенелы, родом из Опунта, ближайший друг Ахилла, убитый в единоборстве с Гектором</i> <i>O, Prp</i>;\n<b>2)</b> <i>один из военачальников царя Персея</i> <i>L</i>."],
  ["Patroclianus", "<big>Patroclianus</big>\n\n<b>Patroclianus, a, um</b> [Patrocles]\nпатроклов (<i>вероятно, по имени какого-л. откупщика городских уборных</i>): Patroclianae sellae <i>M</i> = latrinae."],
  ["patrona", "<big>patrona</big>\n\n<b>patrona, ae</b> <i>f</i> [patronus]\nпокровительница, защитница <i>Pl, Ter, C etc.</i>; госпожа (вольноотпущенника) <i>Pl, Ter, C, PJ</i>."],
  ["patronalis", "<big>patronalis</big>\n\n<b>patronalis, e</b>\nотносящийся к патрону (reverentia <i>CJ</i>)."],
  ["patronatus", "<big>patronatus</big>\n\n<b>patronatus, us</b> <i>m</i>\nпатронат, положение <i>или</i> права патрона (jus patronatus <i>Dig</i>)."],
  ["patronus", "<big>patronus</big>\n\n<b>patronus, i</b> <i>m</i> [pater]\nзащитник, поборник (justitiae <i>C</i>; plebis <i>L</i>; foederis <i>C</i>); правозаступник, судебный защитник, адвокат (causae <i>C</i>); патрон, покровитель, заступник (<i>патриций в отношении своего клиента, бывший хозяин в отношении своего вольноотпущенника и т. п.</i>) <i>C, T</i>."],
  ["patruelis", "<big>patruelis</big>\n\n<b>I patruelis, e</b> [patruus]\n<b>1)</b> двоюродный (<i>преим.</i> по брату отца) (frater <i>C, Su etc.</i>);\n<b>2)</b> принадлежащий двоюродному брату: dona patruelia <i>O</i> дары двоюродного брата (<i>об оружии, подаренном Эанту Теламониду Ахиллом, так как их отцы были братьями</i>).\n\n<b>II patruelis, is</b> <i>m</i>\nдвоюродный брат <i>Su, Dig</i>."],
  ["patruus", "<big>patruus</big>\n\n<b>I patruus, a, um</b> [pater]\nпринадлежащий <i>или</i> свойственный брату отца, дядин (lingua <i>H</i>; ensis <i>O</i>); patruus patruissimus <i>шутл. Pl</i> вседядейший дядя, <i>т. е.</i> лучший из дядей.\n\n<b>II patruus, i</b> <i>m</i>\n<b>1)</b> дядя по отцу <i>C etc.</i>: p. magnus <i>Dig</i> брат деда; p. major <i>T</i> брат прадеда;\n<b>2)</b> <i>перен.</i> строгий блюститель нравов (alicui p. esse <i>H</i>; p. objurgator <i>Ap</i>)."],
  ["Patulcianus", "<big>Patulcianus</big>\n\n<b>Patulcianus, a, um</b> — <i>см.</i>\nPatulcius 2."],
  ["Patulcius", "<big>Patulcius</big>\n\n<b>Patulcius, i</b> <i>m</i> [pateo]\nПатульций:\n<b>1)</b> <i>эпитет Юпитера</i> («разверзающий небеса»); <i>по др., эпитет Януса</i> («открывающий ворота храма»), <i>так как во время войны ворота храма Януса держались открытыми</i> <i>O, Macr</i>;\n<b>2)</b> <i>имя римск. квестора, должника Цицерона</i>: nomen Patulcianum <i>C</i> (денежный) долг Патульция."],
  ["patulus", "<big>patulus</big>\n\n<b>patulus, a, um</b> [pateo]\n<b>1)</b> открытый, раскрытый (os <i>Q</i>; fenestra <i>O</i>; aures <i>H</i>); открытый для всех, доступный всем (locus <i>Vr</i>; orbis <i>H</i>);\n<b>2)</b> широкий, обширный (lacus <i>O</i>; mundus <i>Lcr</i>; navis <i>PM</i>): latitudo patula <i>T</i> широкая дорога;\n<b>3)</b> раскидистый (fagus <i>V</i>; rami <i>C</i>)."],
  ["pauciloquium", "<big>pauciloquium</big>\n\n<b>pauciloquium, i</b> <i>n</i> [paucus + loquor]\nнемногословие, скупость на слова <i>Pl</i>."],
  ["paucitas", "<big>paucitas</big>\n\n<b>paucitas, atis</b> <i>f</i> [paucus]\nнебольшое количество, незначительное число (militum <i>Cs</i>)."],
  ["pauculus", "<big>pauculus</big>\n\n<b>pauculus, a, um</b> [<i>demin. к</i> paucus] (<i>преим.</i> <i>pl.</i>)\nсовсем немногие (anni <i>PJ</i>): post pauculum tempus <i>Ap</i> немного спустя; volo te verbis pauculis <i>Pl</i> хочу тебе сказать несколько слов."],
  ["paucus", "<big>paucus</big>\n\n<b>paucus, a, um</b>\n<b>1)</b> малый, небольшой (sermo <i>rhH</i>; numerus <i>bAfr</i>);\n<b>2)</b> <i>преим. pl.</i> немногие, немногочисленные (milites <i>Sl</i>; dies <i>C etc.</i>); некоторые, кое-какие, кое-кто, несколько (pauci ex <i>или</i> de nostris <i>C, Sl etc.</i>): inter paucos disertus <i>Q</i> красноречивый каких мало; factio paucorum <i>Cs</i> <i>или</i> pauci <i>Sl</i> партия немногих, олигархия (<i>об оптиматах</i>); pauca (<i>sc.</i> verba) dicere <i>C</i> говорить кратко; ut paucis dicam (<i>или</i> in pauca conferam) <i>C</i> коротко говоря; pauca possidere <i>Sen</i> владеть немногим; de aliqua re paucis cognoscere <i>Ap</i> вкратце разузнать о чём-л."],
  ["paulatim", "<big>paulatim</big>\n\n<b>paulatim</b> [paulum]\n<b>1)</b> понемногу, мало-помалу, постепенно (crescere <i>Sl</i>): p. acclivis <i>Cs</i> медленно (отлого) возвышающийся;\n<b>2)</b> поодиночке (discedere ex castris <i>Cs</i>)."],
  ["Paulian-", "<big>Paulian-</big>\n\n<b>Paulian-</b> <i>v. l.</i>\n= Paul(l)ian-."],
  ["paulisper", "<big>paulisper</big>\n\n<b>paulisper</b> <i>adv.</i> [paulum]\nнедолго, немного, (на) короткое время, (на) минуту (manere <i>Pl</i>; stetisse <i>Ap</i>)."],
  ["Paull-", "<big>Paull-</big>\n\n<b>Paull-</b> <i>v. l.</i>\n= Paul-."],
  ["Paullianus", "<big>Paullianus</big>\n\n<b>Paul(l)ianus, a, um</b> [Paul(l)us]\nпавлов: Paul(l)iana victoria <i>VM</i> победа Л. Эмилия Павла (над Персеем)."],
  ["Paullinus", "<big>Paullinus</big>\n\n<b>Paul(l)inus, a, um</b> <i>adj. к</i>\nPaul(l)us: C. Suetonius P. — <i>см.</i> Suetonius; Lollia Paul(l)ina, <i>жена Калигулы</i> <i>T</i>."],
  ["Paullus", "<big>Paullus</big>\n\n<b>Paul(l)us, i</b> <i>m</i>\nПавл, <i>cognomen в роде Эмилиев</i>:\n<b>1)</b> <i>см.</i> Aemilius;\n<b>2)</b> Julius P., <i>известный юрист, современник Александра Севера</i> <i>Lampr</i>."],
  ["paulo", "<big>paulo</big>\n\n<b>paulo</b> — <i>см.</i>\npaulum I."],
  ["paululatim", "<big>paululatim</big>\n\n<b>paululatim</b> <i>Ap</i>\n= paulatim."],
  ["paululum", "<big>paululum</big>\n\n<b>paululum, i</b> <i>n</i> [<i>demin. к</i> paulum]\nнемножечко, чуть-чуть <i>Ter, Cato, C etc.</i>"],
  ["paululus", "<big>paululus</big>\n\n<b>paululus, a, um</b> [<i>demin. к</i> paulus]\nмаленький, крохотный <i>Pl, Ter, L etc.</i>"],
  ["paulum", "<big>paulum</big>\n\n<b>I paulum, (i)</b> (<i>gen. и dat.</i> <i>не встреч.</i>) <i>n</i> [paulus]\nнемногое, немного (p. deest ad aliquid <i>Cs</i>; p. novi <i>C</i>): paulo ненамного, немногим, несколько (paulo antecedere aliquem <i>Cs</i>): paulo ante <i>C, Pt</i> незадолго до этого.\n\n<b>II paulum</b> <i>adv.</i>\nнемного, несколько, чуть-чуть (commorari <i>C</i>)."],
  ["paulus", "<big>paulus</big>\n\n<b>paulus, a, um</b> [<i>одного корня с</i> paucus <i>и</i> pauper]\nмалый, небольшой, незначительный (momentum, sumptus <i>Ter</i>, diverticulum <i>CTh</i>)."],
  ["pauper", "<big>pauper</big>\n\n<b>pauper, peris</b> [paucus + per]\n<b>1.</b> <i>adj.</i> бедный, неимущий (p. argenti <i>H</i>): horum optarim pauperrimus esse bonorum <i>H</i> подобных благ я желал бы вовсе не иметь || скудный (ager <i>Tib</i>); p. aquae <i>H</i> бедный водой, маловодный;\n<b>2.</b> <i>subst. m</i> бедняк (pauperes locupletesque <i>Sen</i>)."],
  ["pauperculus", "<big>pauperculus</big>\n\n<b>pauperculus, a, um</b> [<i>demin. к</i> pauper]\nбедненький, убогий (senex <i>Pl</i>; anus <i>Ter</i>)."],
  ["pauperies", "<big>pauperies</big>\n\n<b>pauperies, ei</b> <i>f</i>\n<b>1)</b> <i>Enn, Pl, Ter, V etc.</i> = paupertas;\n<b>2)</b> ущерб (pauperiem facere <i>Dig</i>)."],
  ["pauperius", "<big>pauperius</big>\n\n<b>pauperius</b> <i>adv. compar.</i>\nбеднее <i>Tert</i>."],
  ["paupero", "<big>paupero</big>\n\n<b>paupero, avi, atum, are</b> [pauper]\nделать бедным (aliquem <i>Pl etc.</i>): p. aliquem aliqua re <i>Pl, H</i> лишать кого-л. чего-л., отнимать что-л. у кого-л."],
  ["paupertas", "<big>paupertas</big>\n\n<b>paupertas, atis</b> <i>f</i> [pauper]\nбедность (in paupertate vivere <i>C</i>): p. vel potius egestas ac mendicitas <i>C</i> бедность или, лучше сказать, нужда и нищета."],
  ["paupertatula", "<big>paupertatula</big>\n\n<b>paupertatula, ae</b> <i>f</i> <i>Hier</i>\n= paupertas."],
  ["paupertinus", "<big>paupertinus</big>\n\n<b>paupertinus, a, um</b> <i>Vr, AG, Amm, Ap etc.</i>\n= pauper."],
  ["paupulo", "<big>paupulo</big>\n\n<b>paupulo, —, —, are</b>\nкричать (<i>о павлинах</i>) <i>Su</i>."],
  ["pausa", "<big>pausa</big>\n\n<b>pausa, ae</b> <i>f</i> (<i>греч.</i>)\nостановка, задержка (ardoris violenti <i>Lcr</i>); прекращение, конец (vitae <i>Lcr</i>): pausam facere (dare) <i>Pl, Lcr, Ap etc.</i> остановиться <i>или</i> кончить, прекратить; pausas edere (explere) <i>культ. Spart</i> делать положенные остановки (для пения гимнов) во время процессий в честь Исиды."],
  ["pausabilis", "<big>pausabilis</big>\n\n<b>pausabilis, e</b>\nпрерывающийся (inspiratio <i>CA</i>)."],
  ["Pausanias", "<big>Pausanias</big>\n\n<b>Pausanias, ae</b> <i>m</i>\nПавсаний:\n<b>1)</b> <i>сын Клеомброта, спартанский полководец, победитель при Платеях (479 г. до н. э.); умер, обвинённый в измене, в 467 г. до н. э.</i> <i>C, Nep</i>;\n<b>2)</b> <i>внук предыдущего, сын Плистоанакта, царь Спарты в 408-394 гг. до н. э.; умер в 385 г. до н. э. в изгнании</i> <i>Just</i>;\n<b>3)</b> <i>македонец, убийца Филиппа Македонского</i> <i>Just</i>."],
  ["pausarius", "<big>pausarius</big>\n\n<b>pausarius, i</b> <i>m</i> [pausa]\nначальник гребцов (<i>ударом молотка дававший знак остановиться</i>) <i>Sen</i>."],
  ["pausatio", "<big>pausatio</big>\n\n<b>pausatio, onis</b> <i>f</i> <i>Hier, Aug</i>\n= pausa."],
  ["pausea", "<big>pausea</big>\n\n<b>pausea, ae</b> <i>f</i> <i>Vr, Col</i>\n= pausia."],
  ["pausia", "<big>pausia</big>\n\n<b>pausia, ae</b> <i>f</i>\nкрупный и мясистый сорт маслин <i>V, Col, Pall</i>."],
  ["Pausiacus", "<big>Pausiacus</big>\n\n<b>Pausiacus, a, um</b> [Pausias]\nпавсиев (tabella <i>H</i>)."],
  ["Pausias", "<big>Pausias</big>\n\n<b>Pausias, ae</b> <i>m</i>\nПавсий, <i>живописец родом из Сикиона</i> (<i>середина IV в. до н. э.</i>) <i>PM</i>."],
  ["pausill-", "<big>pausill-</big>\n\n<b>pausill-</b> <i>и</i> <b>pauxill-</b> <i>Pl</i>\n= paul-."],
  ["Pausistratus", "<big>Pausistratus</big>\n\n<b>Pausistratus, i</b> <i>m</i>\nПавсистрат, <i>родосский полководец</i> <i>L</i>."],
  ["pauso", "<big>pauso</big>\n\n<b>pauso, avi, —, are</b> [pausa]\nприостанавливаться, утихать (dolor pausavit <i>CA</i>); переставать (pausa et quiesce <i>Vlg</i>)."],
  ["paussa", "<big>paussa</big>\n\n<b>paussa</b> <i>арх. v. l.</i>\n= pausa."],
  ["pauxillatim", "<big>pauxillatim</big>\n\n<b>pauxillatim</b> <i>Pl</i>\n= paulatim."],
  ["pauxillisper", "<big>pauxillisper</big>\n\n<b>pauxillisper</b> <i>Pl</i>\n= paulisper."],
  ["pauxillitas", "<big>pauxillitas</big>\n\n<b>pauxillitas, atis</b> <i>f</i>\nмалость, скудость, незначительность <i>Ambr</i>."],
  ["pauxillulum", "<big>pauxillulum</big>\n\n<b>pauxillulum</b> <i>Pl, Ter, Sid</i>\n= paululum."],
  ["pauxillum", "<big>pauxillum</big>\n\n<b>pauxillum</b> <i>Pl</i>\n= paululum."],
  ["pauxillus", "<big>pauxillus</big>\n\n<b>pauxillus, a, um</b> <i>Pl, Lcr</i>\n= paululus."],
  ["pava", "<big>pava</big>\n\n<b>pava, ae</b> <i>f</i>\nпава <i>Aus</i>."],
  ["pavefactus", "<big>pavefactus</big>\n\n<b>pavefactus, a, um</b> [paveo + facio]\nиспуганный, объятый страхом <i>O, SenT, Su, Ambr</i>."],
  ["paveo", "<big>paveo</big>\n\n<b>paveo, pavi, —, ere</b>\nбыть объятым страхом, дрожать от страха, быть в ужасе (intus paveo et foris formido <i>Pl</i>); бояться, робеть (aliqua re <i>O, L</i>, ad aliquid <i>L</i> <i>и</i> aliquid <i>Sl</i>, <i>H, L</i>): p. aliquem <i>H, L</i> бояться кого-л.; pavendus alicui rei <i>PM</i> опасный для чего-л.; p. alicui <i>Ter, T etc.</i> бояться за кого-л.; venae pavent <i>T</i> сосуды (бальзамового дерева) сжимаются (<i>если прикоснуться к ним железом</i>)."],
  ["pavesco", "<big>pavesco</big>\n\n<b>pavesco, —, —, ere</b> [<i>inchoat. к</i> paveo]\nзадрожать от страха, испугаться, оробеть (ad aliquid <i>Col</i> <i>или</i> aliqua re <i>Sl</i>); страшиться, бояться (aliquid <i>T, Sil</i>)."],
  ["pavi", "<big>pavi</big>\n\n<b>I pavi</b> <i>pf. к</i>\npasco.\n\n<b>II pavi</b> <i>pf. к</i>\npaveo."],
  ["pavibundus", "<big>pavibundus</big>\n\n<b>pavibundus, a, um</b> [paveo]\nобъятый страхом, полный боязни <i>Aug</i>."],
  ["pavicula", "<big>pavicula</big>\n\n<b>pavicula, ae</b> <i>f</i> [pavio]\nколотушка, трамбовка <i>Cato, Col</i>."],
  ["pavide", "<big>pavide</big>\n\n<b>pavide</b> [pavidus]\nбоязливо, робко <i>Lcr, L, Q</i>."],
  ["pavidum", "<big>pavidum</big>\n\n<b>pavidum</b> <i>adv.</i> <i>O</i>\n= pavide."],
  ["pavidus", "<big>pavidus</big>\n\n<b>pavidus, a, um</b> [paveo]\n<b>1)</b> боязливый, пугливый, робкий, боящийся (lepus, dama <i>H</i>; p. alicujus rei <i>T, Lcn</i> <i>или</i> ad aliquid <i>T</i>); исполненный страха (ex somno <i>L</i>);\n<b>2)</b> <i>поэт.</i> причиняющий беспокойство, приводящий в трепет, гнетущий (religio <i>Lcr</i>; metus <i>O</i>; lucus <i>St</i>)."],
  ["pavimentatus", "<big>pavimentatus</big>\n\n<b>pavimentatus, a, um</b> [pavimentum]\nпокрытый каменным полом, выложенный плитами, мощёный (porticus <i>C</i>)."],
  ["pavimento", "<big>pavimento</big>\n\n<b>pavimento, —, —, are</b> [pavimentum]\nутаптывать, утрамбовывать <i>или</i> покрывать настилом <i>PM</i>; <i>перен.</i> закреплять (peccata <i>Aug</i>)."],
  ["pavimentum", "<big>pavimentum</big>\n\n<b>pavimentum, i</b> <i>n</i> [pavio]\n<b>1)</b> утрамбованная со щебнем и извёсткой земля <i>Vr, C etc.</i>;\n<b>2)</b> каменный мозаичный пол <i>H, Pt</i>;\n<b>3)</b> каменная кровля (aedificia tecta pavimentis <i>bAl</i>)."],
  ["pavio", "<big>pavio</big>\n\n<b>pavio, ivi, itum, ire</b>\nвыравнивать, утаптывать, утрамбовывать (terram <i>C, PM</i>; arenam <i>Lcr</i>)."],
  ["pavitatio", "<big>pavitatio</big>\n\n<b>pavitatio, onis</b> <i>f</i> [pavito]\nдрожание, трепет <i>Ap</i>."],
  ["pavitensis", "<big>pavitensis</big>\n\n<b>pavitensis, e</b>\nиз плотной ткани, тяжёлый (vestis <i>Is</i>)."],
  ["pavito", "<big>pavito</big>\n\n<b>pavito, avi, —, are</b> [<i>intens. к</i> paveo]\n<b>1)</b> дрожать, трепетать (pavitante gressu <i>SenT</i>); бояться (quae pueri in tenebris pavitant <i>Lcr</i>);\n<b>2)</b> чувствовать озноб, лихорадить <i>Ter, CA</i>."],
  ["pavo", "<big>pavo</big>\n\n<b>pavo, onis</b> <i>m</i>\nпавлин (<i>был посвящен Юноне</i> — Junonia avis <i>O</i>) <i>V, C etc.</i>: p. femina <i>AG</i> = pava."],
  ["pavonaceus", "<big>pavonaceus</big>\n\n<b>pāvōnāceus, a, um</b> [pavo]\nпавлиний, <i>перен.</i> разноцветный, пёстрый (tegendi genera <i>PM</i>)."],
  ["pavoninus", "<big>pavoninus</big>\n\n<b>pavoninus, a, um</b> [pavo]\n<b>1)</b> павлиний (ovum <i>Vr, Col etc.</i>); сделанный из павлиньих перьев (muscarium <i>M</i>);\n<b>2)</b> разноцветный, пёстрый (lectus <i>M</i>; ornatus <i>Tert</i>)."],
  ["pavor", "<big>pavor</big>\n\n<b>pavor, oris</b> <i>m</i> [paveo]\n<b>1)</b> страх, ужас (p. ac metus <i>Lcr</i>): p. aquae <i>PM</i> водобоязнь;\n<b>2)</b> трепет ожидания (p. corda pulsat <i>V</i>); радостный трепет (laetus p. <i>Sil</i>)."],
  ["pavos", "<big>pavos</big>\n\n<b>pavos, oris</b> <i>m</i> <i>арх. Naev, Pac</i>\n= pavor."],
  ["pavus", "<big>pavus</big>\n\n<b>pavus, i</b> <i>m</i> <i>Enn, Vr</i>\n= pavo."],
  ["pax", "<big>pax</big>\n\n<b>I pax, pacis</b> <i>f</i>, <i>иногда</i> <i>pl.</i> [<i>одного корня с</i> pango <i>и</i> paciscor]\n<b>1)</b> мир (civilis <i>Su</i>): pacem facere <i>Nep</i> (componere <i>AV</i>, conciliare <i>C</i>, pangere <i>L</i>) заключать мир; pace uti <i>C</i> <i>или</i> pacem agitare (gerere) <i>Sl</i> жить в мире; (in) pace <i>C, Sl, L etc.</i> в мирное время; cum bona pace <i>L</i> миролюбиво, в мире и согласии; bonae paces <i>H</i> благодеяния мира; agere pacem perpetuam cum aliquo <i>J</i> жить в вечном мире с кем-л.; p, Ariovisti <i>Cs</i> мир с Ариовистом;\n<b>2)</b> мирный договор (belli atque paces <i>Sl</i>): p. Romana <i>Sen, PM</i> «замирённая» римскими завоеваниями часть мира, <i>т. е.</i> римская империя;\n<b>3)</b> покой, спокойствие (animi, vultus <i>O</i>): ventorum paces <i>Lcr</i> безветрие; pace tua dixerim <i>C</i> (dicere liceat <i>Pt</i>) не в обиду тебе будь сказано; tua pace <i>Pl</i> <i>и</i> pace quod fiat tua <i>Ter</i> с твоего позволения, если разрешишь;\n<b>4)</b> содействие, благоволение, милость (a Jove pacem petere <i>C</i>).\n\n<b>II pax!</b> (<i>греч.</i>) <i>interj.</i>\n(<i>вследствие звуковой и смысловой близости часто смешивается с</i> pax I) довольно!, будет!: p., abi <i>Pl</i> ладно, уходи!; p., nihil amplius! <i>Ter</i> тише, ни слова больше!"],
  ["paxillus", "<big>paxillus</big>\n\n<b>paxillus, i</b> <i>m</i> [<i>demin. к</i> palus I]\nколышек <i>Vr, Col, PM</i>."],
  ["peccamen", "<big>peccamen</big>\n\n<b>peccamen, inis</b> <i>n</i> <i>Eccl</i>\n= peccatum."],
  ["peccans", "<big>peccans</big>\n\n<b>peccans, antis</b> <i>m adj.</i> [pecco]\nвиновный <i>Nep, Sen</i>."],
  ["peccanter", "<big>peccanter</big>\n\n<b>peccanter</b>\nошибочно, неправильно <i>CA</i>."],
  ["peccantia", "<big>peccantia</big>\n\n<b>peccantia, ae</b> <i>f</i> <i>Tert</i>\n= peccatum."],
  ["peccatela", "<big>peccatela</big>\n\n<b>peccatela, ae</b> <i>f</i> <i>Tert</i>\n= peccatum."],
  ["peccatio", "<big>peccatio</big>\n\n<b>peccatio, onis</b> <i>f</i> [pecco]\nпрегрешение, грех <i>AG</i>."],
  ["peccator", "<big>peccator</big>\n\n<b>peccator, oris</b> <i>m</i>\nгрешник <i>Eccl</i>."],
  ["peccatorius", "<big>peccatorius</big>\n\n<b>peccatorius, a, um</b>\nгреховный <i>Tert</i>."],
  ["peccatrix", "<big>peccatrix</big>\n\n<b>peccatrix, icis</b> <i>f</i>\nгрешница <i>Eccl</i>."],
  ["peccatum", "<big>peccatum</big>\n\n<b>peccatum, i</b> <i>n</i> [pecco]\nпрегрешение, провинность, грех (in p. incidere <i>C</i>); заблуждение, промах (p. suum confiteri <i>C</i>): p. originale <i>Eccl</i> первородный грех; p. mortale <i>Eccl</i> смертный грех."],
  ["pecco", "<big>pecco</big>\n\n<b>pecco, avi, atum, are</b> (<i>арх. fut. Pl</i> peccasso)\n<b>1)</b> совершать проступок, погрешать, провиниться, <i>тж.</i> совершать оплошность, ошибаться (in <i>или</i> erga aliquem <i>C, Pl</i> <i>и</i> in aliquo <i>Cs, H</i>): p. (in) aliqua re <i>или</i> aliquid поступить неправильно (ошибиться) в чём-л. (verbo uno <i>O</i>; in una syllaba <i>или</i> unam syllabam <i>Pl</i>); multa peccantur <i>C</i> совершается много ошибок; sibi quisque peccat <i>погов. Pt</i> всякому приходится расплачиваться за свои грехи; quid peccavit? <i>Pt</i> в чём он провинился?; p. in turpem rem (<i>v. l.</i> in turpi re) <i>PS</i> совершить позорный поступок; vina peccatura <i>Pall</i> скоропортящиеся вина;\n<b>2)</b> спотыкаться (equus peccat <i>H</i>)."],
  ["pecorosus", "<big>pecorosus</big>\n\n<b>pecorosus, a, um</b> [pecus I]\nбогатый скотом (Palatia <i>Prp</i>)."],
  ["pecten", "<big>pecten</big>\n\n<b>pecten, inis</b> <i>m</i> [pecto]\n<b>1)</b> гребень, расчёска: deducere pectine crines <i>O</i> расчёсывать волосы гребнем; pectine <i>Lcn</i> наподобие гребня; p. dentium <i>Eccl</i> сплошной ряд зубов; digiti inter se pectine juncti <i>O</i> сплетённые (заломленные) пальцы;\n<b>2)</b> ткацкое бёрдо <i>V, O</i>; <i>перен.</i> ткацкое искусство, ткачество (p. Nili acus <i>M</i>);\n<b>3)</b> чесалка (<i>для льна, шерсти</i>) <i>PM</i>;\n<b>4)</b> грабли <i>или</i> борона <i>O, Col</i>;\n<b>5)</b> прожилки древесины <i>PM</i>;\n<b>6)</b> род танца <i>St</i>;\n<b>7)</b> плектр <i>V, J</i>; струнный инструмент <i>VF</i>; <i>перен.</i> песня, напев: alterno pectine <i>O</i> чередующимися стихами, <i>т. е.</i> элегическими двустишиями;\n<b>8)</b> <i>анат.</i> лобок <i>CC</i>; растительность на лобке <i>PM, J</i>;\n<b>9)</b> гребенчатая раковина <i>H, PM</i>."],
  ["pectinatim", "<big>pectinatim</big>\n\n<b>pectinatim</b> [pecten]\nнаподобие гребня (digiti inter se implexi <i>PM</i>)."],
  ["pectinatus", "<big>pectinatus</big>\n\n<b>pectinatus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> pectino;\n<b>2.</b> <i>adj.</i> гребнеобразный (fastigiorum dispositio <i>Vtr</i>)."],
  ["pectino", "<big>pectino</big>\n\n<b>pectino, avi, atum, are</b> [pecten]\n<b>1)</b> расчёсывать (jubam <i>Ap</i>);\n<b>2)</b> бороновать (segetem <i>PM</i>)."],
  ["pectio", "<big>pectio</big>\n\n<b>pectio, onis</b> <i>f</i> [pecto]\nпричёсывание <i>CA</i>."],
  ["pecto", "<big>pecto</big>\n\n<b>pecto, pexi, pexum</b> (<i>редко</i> <b>pectitum</b>), <b>ere</b>\n<b>1)</b> расчёсывать, причёсывать (capillos <i>O</i>); чесать (lanam <i>Col</i>);\n<b>2)</b> <i>шутл.</i> бить (aliquem pugnis <i>Pl</i>);\n<b>3)</b> разрыхлять, вскапывать (terram ferro <i>Col</i>)."],
  ["pectoralia", "<big>pectoralia</big>\n\n<b>pectoralia, ium</b> <i>n</i> [pectoralis]\nнагрудные доспехи, панцирь <i>Vr, PM</i>."],
  ["pectoralis", "<big>pectoralis</big>\n\n<b>pectoralis, e</b> [pectus]\nгрудной (os <i>CC</i>)."],
  ["pectorosus", "<big>pectorosus</big>\n\n<b>pectorosus, a, um</b> [pectus]\nс мощной грудью, крепкогрудый <i>Col, PM</i>."],
  ["pectunculus", "<big>pectunculus</big>\n\n<b>pectunculus, i</b> <i>m</i> [<i>demin. к</i> pecten] <i>зоол.</i>\nморской гребешок <i>Vr, Col, PM</i>."],
  ["pectus", "<big>pectus</big>\n\n<b>pectus, oris</b> <i>n</i>\n<b>1)</b> <i>часто</i> <i>pl.</i> грудь (nudum <i>CC</i>): antrum pectoris <i>Aug</i> грудная клетка; cicatrices adverso pectore <i>Sl</i> рубцы на груди;\n<b>2)</b> мужество, смелость: forti pectore <i>H</i> храбро;\n<b>3)</b> душа, сердце (toto pectore amare <i>C</i>; p. est, quod disertos facit <i>O</i>; artibus ingenuis pectora mollescunt <i>O</i>): pectoris ingenii que bona <i>O</i> сокровища сердца и ума; pectore puro placere alicui <i>H</i> понравиться кому-л. чистотой (своего) сердца;\n<b>4)</b> вдохновение (aspicies, quantum dederis mihi pectoris <i>O</i>);\n<b>5)</b> ум, рассудок (toto pectore cogitare <i>C</i>): de summo pectore dicere <i>AG</i> <i>и</i> hiscere <i>Ap</i> говорить необдуманно; excidere pectore <i>O</i> изглаживаться из памяти, забываться;\n<b>6)</b> утроба, желудок (pectore egerere dapes <i>O</i>);\n<b>7)</b> личность, человек: mortalia pectora <i>O</i> смертные; sororum pectora <i>O</i> сестры; pondus lapsi pectoris <i>Lcn</i> тяжесть рухнувшего тела."],
  ["pectusculum", "<big>pectusculum</big>\n\n<b>pectusculum, i</b> <i>n</i> [<i>demin. к</i> pectus]\nмаленькая <i>или</i> слабая грудь <i>Hier</i>."],
  ["pecu", "<big>pecu</big>\n\n<b>pecu, (us) </b> <i>n</i>\nскот, домашние животные (homines et pecua <i>Vr</i>): squamosum p. <i>Pl</i> = рыбы."],
  ["pecuaria", "<big>pecuaria</big>\n\n<b>I pecuaria, ae</b> <i>f</i>\nскотоводство (pecuariam facere <i>Su</i>); скотоводческое хозяйство (pecuariae grandes <i>Vr</i>).\n\n<b>II pecuaria, orum</b> <i>n</i> [pecuarius]\nстада <i>V, PM</i>."],
  ["pecuarius", "<big>pecuarius</big>\n\n<b>I pecuarius, a, um</b> [pecu]\nотносящийся к скоту, животноводческий: res pecuaria <i>C</i> скотоводство.\n\n<b>II pecuarius, i</b> <i>m</i> [pecu]\n<b>1)</b> скотовод <i>C</i>;\n<b>2)</b> арендатор общественных выгонов (<i>в провинции</i>) <i>C, L</i>."],
  ["pecuda", "<big>pecuda</big>\n\n<b>pecuda</b> <i>n pl.</i> <i>Acc, C</i>\n= pecudes."],
  ["pecudes", "<big>pecudes</big>\n\n<b>pecudes</b> <i>pl. к</i>\npecus II."],
  ["pecuinus", "<big>pecuinus</big>\n\n<b>pecuinus, a, um</b> [pecu]\n<b>1)</b> относящийся к скоту, принадлежащий животным (ossa <i>Ap</i>);\n<b>2)</b> <i>перен.</i> скотский, грубый (animus <i>Ap</i>)."],
  ["peculator", "<big>peculator</big>\n\n<b>peculator, oris</b> <i>m</i> [peculor]\nрасхититель государственных денег, казнокрад, растратчик <i>C, T, VM</i>."],
  ["peculatus", "<big>peculatus</big>\n\n<b>peculatus, us</b> <i>m</i> [peculor]\nрасхищение государственных денег, растрата, казнокрадство (peculatum facere <i>C</i>): peculatus damnari <i>C</i> быть осуждённым за растрату; in aliquem peculatum facere <i>Pl</i> обмануть чьё-л. доверие."],
  ["peculiaris", "<big>peculiaris</big>\n\n<b>peculiaris, e</b> [peculium]\n<b>1)</b> составляющий личную собственность, собственный (servus <i>Su, Dig</i>; oves <i>Pl</i>);\n<b>2)</b> свойственный, присущий, отличительный (hoc mihi peculiare est <i>C</i>);\n<b>3)</b> особый, чрезвычайный (edictum <i>C</i>)."],
  ["peculiariter", "<big>peculiariter</big>\n\n<b>peculiariter</b> [peculiaris]\n<b>1)</b> особенно, в особенности (medicinae p. studiosus <i>PM</i>);\n<b>2)</b> в качестве личной собственности (aliquid nancisci <i>Dig</i>)."],
  ["peculiarius", "<big>peculiarius</big>\n\n<b>peculiarius, a, um</b> <i>Dig</i>\n= peculiaris."],
  ["peculiatus", "<big>peculiatus</big>\n\n<b>peculiatus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> peculio;\n<b>2.</b> <i>adj.</i> имеющий состояние (servus <i>Dig</i>; libertus <i>Ap</i>); bene p. <i>C</i> зажиточный, состоятельный."],
  ["peculio", "<big>peculio</big>\n\n<b>peculio, avi, atum, are</b> [peculium]\nдавать в собственность, одарять (aliquem <i>Pl</i>)."],
  ["peculiolum", "<big>peculiolum</big>\n\n<b>peculiolum, i</b> <i>n</i> [<i>demin. к</i> peculium]\nнебольшая собственность, состояньице <i>Q</i>."],
  ["peculiosus", "<big>peculiosus</big>\n\n<b>peculiosus, a, um</b> [peculium]\nзажиточный, состоятельный <i>Pl, Aug</i>."],
  ["peculium", "<big>peculium</big>\n\n<b>peculium, i</b> <i>n</i> [pecus]\n<b>1)</b> собственность, имущество (cura peculi <i>V</i>);\n<b>2)</b> сбережения <i>Pl, H, L, Dig</i>;\n<b>3)</b> личная собственность (filii <i>Dig</i>);\n<b>4)</b> <i>шутл.</i> добавление в письме для чьего-л. личного сведения (epistula sine ullo ad me peculio <i>Sen</i>);\n<b>5)</b> membrum virile <i>Pt, Lampr</i>."],
  ["peculor", "<big>peculor</big>\n\n<b>peculor, —, ari</b> <i>depon.</i> [peculium]\nпохищать государственные деньги, обкрадывать (rem publicam <i>Fl</i>)."],
  ["pecunia", "<big>pecunia</big>\n\n<b>pecunia, ae</b> <i>f</i> [pecu]\n<b>1)</b> имущество, состояние, собственность: pecuniam facere <i>C</i> наживать состояние;\n<b>2)</b> деньги (magna p. <i>Nep</i>; redigere omnia in pecuniam <i>Q</i>): p. praesens <i>Pl, C</i> (numerata <i>C</i>) наличные деньги; p. publica <i>C</i> государственные финансы; dies pecuniae <i>C</i> день платежа; p. alicujus rei <i>Cato, Dig</i> выручка от продажи чего-л.; pecunias capere (accipere) <i>C etc.</i> принимать деньги, <i>но тж.</i> брать взятку; pedibus p. compensatur <i>погов. Cato ap. C</i> деньги окупаются хождением, <i>т. е.</i> низкая цена на отдалённые земельные участки уравновешивается их дальностью;\n<b>3)</b> <i>в императ. эпоху</i> монета (vitiare pecunias <i>Eutr</i>); медная монета (numquam aurum, numquam argentum, vix pecuniam donare <i>Lampr</i>)."],
  ["pecunialis", "<big>pecunialis</big>\n\n<b>pecunialis, e</b> <i>CA</i>\n= pecuniarius."],
  ["pecuniarie", "<big>pecuniarie</big>\n\n<b>pecuniarie</b> <i>Dig</i>\n= pecuniariter."],
  ["pecuniaris", "<big>pecuniaris</big>\n\n<b>pecuniaris, e</b> <i>Dig</i>\n= pecuniarius."],
  ["pecuniariter", "<big>pecuniariter</big>\n\n<b>pecuniariter</b>\nв денежном отношении: p. agere <i>Dig</i> требовать денежного возмещения."],
  ["pecuniarius", "<big>pecuniarius</big>\n\n<b>pecuniarius, a, um</b> [pecunia]\nденежный, касающийся денег (lis <i>Q</i>): res pecuniaria <i>C, T</i> денежное дело <i>или</i> деньги (inopia rei pecuniariae <i>C</i>): praemia rei pecuniariae <i>Cs</i> денежные награды."],
  ["pecuniosus", "<big>pecuniosus</big>\n\n<b>pecuniosus, a, um</b> [pecunia]\n<b>1)</b> имеющий много денег, состоятельный, богатый (homo <i>C</i>);\n<b>2)</b> обогащающий, доходный (ars <i>M</i>)."],
  ["pecus", "<big>pecus</big>\n\n<b>I pecus, oris</b> <i>n</i>\n<b>1)</b> <i>собир.</i> скот, домашние животные (p. equinum <i>Vr</i>; p. ovillum <i>Col</i>); мелкий скот (p. et armenta <i>QC</i>); <i>преим.</i> овцы (p. capraeque <i>PM</i>): p. lanigerum <i>O</i> = овцы; p. setigerum <i>O</i> <i>и</i> setosum <i>Col</i> = свиньи; p. aquatile <i>Col</i> = рыбы; p. volatile <i>Col</i> = домашняя птица;\n<b>2)</b> пчёлы <i>Col</i> <i>или</i> шершни <i>V</i>;\n<b>3)</b> <i>поэт.</i> (отдельное) животное: p. Magnae Parentis <i>O</i> зверь Великой Матери (Кибелы), <i>т. е.</i> лев;\n<b>4)</b> утробный плод <i>Eccl</i>;\n<b>5)</b> <i>бран.</i> скот, скотина, стадо (imitatorum servum p. <i>H</i>).\n\n<b>II pecus, udis</b> <i>f</i> (<i>редко</i> <i>Enn m</i>)\n<b>1)</b> домашнее животное (pecudes et bestiae <i>C</i>): p. Indica <i>M</i> = слон; pecudes natantes <i>Lcr</i> <i>или</i> Neptuni pecudes <i>PJ</i> = рыбы;\n<b>2)</b> мелкое домашнее животное, <i>преим.</i> овца (armenta et pecudes <i>Lcr</i>): p. Helles <i>O</i> животное Геллы, <i>т. е.</i> златорунный баран;\n<b>3)</b> <i>pl.</i> наземные животные (pecudes et volucres <i>V</i>);\n<b>4)</b> <i>бран.</i> животное, скотина <i>C, T etc.</i>"],
  ["pecusculum", "<big>pecusculum</big>\n\n<b>pecusculum, i</b> <i>n</i> [<i>demin. к</i> pecus]\nскотинка, овечка <i>Jvc</i>."],
  ["pedale", "<big>pedale</big>\n\n<b>pedale, is</b> <i>n</i> [pes]\n<b>1)</b> обувь, туфля <i>Pt</i>;\n<b>2)</b> мера, предел (alicujus rei <i>Vlg</i>)."],
  ["pedalis", "<big>pedalis</big>\n\n<b>pedalis, e</b> [pes]\nфутовый, размером в римский фут (<i>29,57 см</i>) (trabes <i>Cs</i>; altitudo <i>PM</i>)."],
  ["pedamen", "<big>pedamen</big>\n\n<b>pedamen, inis</b> <i>n</i> <i>Col</i>\n= pedamentum."],
  ["pedamentum", "<big>pedamentum</big>\n\n<b>pedamentum, i</b> <i>n</i> [pedo I]\nтычина, подпорка <i>Vr, Col</i>."],
  ["pedaneus", "<big>pedaneus</big>\n\n<b>pedaneus, a, um</b> [pes]\n<b>1)</b> <i>Pall, Sol</i> = pedalis;\n<b>2)</b> <i>AG</i> = pedarius;\n<b>3)</b> вспомогательный, подсобный (judex <i>Dig</i>)."],
  ["Pedanum", "<big>Pedanum</big>\n\n<b>Pedanum, i</b> <i>n</i> [Pedanus I]\n(<i>sc.</i> praedium) поместье близ города Pedum <i>C</i>."],
  ["Pedanus", "<big>Pedanus</big>\n\n<b>I Pedanus, a, um</b> [Pedum]\nпеданский (regio <i>H</i>).\n\n<b>II Pedanus, i</b> <i>m</i>\nжитель города Pedum <i>L, PM</i>."],
  ["pedarius", "<big>pedarius</big>\n\n<b>pedarius, a, um</b> [pes]\nпеший: (senatores) pedarii <i>C, T</i> сенаторы второго ранга (<i>они не занимали ещё курульных должностей и не имели права самостоятельного голоса, но могли присоединяться к голосам других сенаторов</i>: pedibus in alienam sententiam ire <i>AG</i>)."],
  ["Pedasa", "<big>Pedasa</big>\n\n<b>Pedasa, orum</b> <i>и</i> <b>Pedasum, i</b> <i>n</i>\nПедас, <i>город в Карий</i> <i>L, PM</i>."],
  ["pedatim", "<big>pedatim</big>\n\n<b>pedatim</b> [pes]\nнога к ноге, <i>т. е.</i> не выставляя левую ногу вперёд, а приставляя её сзади к правой (gradi <i>PM</i>)."],
  ["pedatura", "<big>pedatura</big>\n\n<b>pedatura, ae</b> <i>f</i>\nизмеренная шагами (<i>или</i> футами) площадь <i>Veg</i>."],
  ["pedatus", "<big>pedatus</big>\n\n<b>I pedatus, a, um</b> [pes]\nимеющий ноги: male p. <i>Su</i> с ногами (ступнями) неправильной формы (уродливыми).\n\n<b>II pedatus, (us) </b> <i>m</i>\nнападение, натиск, набег, атака <i>Cato, Pl</i>."],
  ["pedeplana", "<big>pedeplana</big>\n\n<b>pede–plana, orum</b> <i>n</i> [pes + planus]\nпервый (нижний) этаж дома <i>CTh, CJ</i>."],
  ["pedes", "<big>pedes</big>\n\n<b>I pedes, itis</b> <i>m</i> [pes]\n<b>1)</b> пешеход, пеший (aliquis p. it <i>V etc.</i>);\n<b>2)</b> пехотинец <i>Cs etc.</i> <i>или</i> <i>pl.</i> пехота <i>C, L, T etc.</i>;\n<b>3)</b> плебей (<i>так как военную службу он отбывал в пехоте</i>): equites peditesque <i>C, L, H</i> патриции и плебеи, <i>т. е.</i> римский народ;\n<b>4)</b> <i>pl.</i> сухопутная армия (classicae peditumque excursiones <i>VP</i>).\n\n<b>II pedes, um</b> <i>pl. к</i>\npes.\n\n<b>III pedes, um</b> <i>pl. к</i>\npedis I."],
  ["pedester", "<big>pedester</big>\n\n<b>pedester, tris, tre</b> [pedes]\n<b>1)</b> пеший (statua pedestris, <i>в противопол. к</i> statua equestris <i>C</i>); пехотный (copiae <i>Cs</i>; scutum <i>L</i>): pugna pedestris <i>L</i> бой в пешем строю (<i>ср.</i> 2); ordo p. <i>L</i> сословие, отбывавшее военную службу в пехоте;\n<b>2)</b> сухопутный (pugna <i>C</i> — <i>ср.</i> 1): iter pedestre <i>Cs</i> сухой путь;\n<b>3)</b> простой, обычный, серый, посредственный (sermo <i>H, Vop</i>; Musa <i>H</i>);\n<b>4)</b> написанный в прозе, прозаический (historiae <i>H</i>)."],
  ["pedetemptim", "<big>pedetemptim</big>\n\n<b>pedetemptim (pedetentim)</b> [pes + tempto <i>или</i> tendo]\nшаг за шагом <i>Pac</i>; постепенно, осторожно (p. et gradatim <i>C</i>)."],
  ["pedic-", "<big>pedic-</big>\n\n<b>pedic-</b> <i>v. l.</i>\n= paedic-."],
  ["pedica", "<big>pedica</big>\n\n<b>pedica, ae</b> <i>f</i> [pes]\n<b>1)</b> петля, силок, западня (gruibus pedicas ponere <i>V</i>);\n<b>2)</b> <i>перен.</i> путы, узы (amoris <i>Ap</i>; pedicis coartari <i>Ap</i>)."],
  ["pedicinus", "<big>pedicinus</big>\n\n<b>pedicinus, i</b> <i>m</i> [pes]\nножка виноградного пресса <i>Cato</i>."],
  ["pedicularis", "<big>pedicularis</big>\n\n<b>pedicularis, e</b> [pediculus]\n<b>1)</b> вшивый: morbus p. <i>CA</i> вшивость;\n<b>2)</b> истребляющий вшей (herba <i>Col</i>)."],
  ["pedicularius", "<big>pedicularius</big>\n\n<b>pedicularius, a, um</b> <i>Scr</i>\n= pedicularis."],
  ["pediculosus", "<big>pediculosus</big>\n\n<b>pediculosus, a, um</b> [pediculus]\nвшивый, изъеденный вшами <i>M</i>."],
  ["pediculus", "<big>pediculus</big>\n\n<b>I pediculus, i</b> <i>m</i> [<i>demin. к</i> pes]\n<b>1)</b> ножка (argenteus <i>Dig</i>; fungorum <i>PM</i>);\n<b>2)</b> стебелёк (foliorum <i>PM</i>); черенок (uvarum <i>Col</i>).\n\n<b>II pediculus</b> (<i>стяж.</i> pediclus <i>и</i> peduclus), <b>i</b> <i>m</i> [<i>demin. к</i> pedis]\nвошь <i>CC, Col, PM</i>: in alio peduclum vides, in te ricinum non vides погов. <i>Pt</i> у другого ты видишь вошь, а у себя и клеща не замечаешь."],
  ["pedis", "<big>pedis</big>\n\n<b>I pedis, is</b> <i>m, f</i>\nвошь <i>Pl, Vr</i>.\n\n<b>II pedis</b> <i>gen. к</i>\npes."],
  ["pedisec-", "<big>pedisec-</big>\n\n<b>pedisec-</b> <i>v. l.</i>\n= pedisequ-."],
  ["pedisequa", "<big>pedisequa</big>\n\n<b>pedisequa, ae</b> <i>f</i> [pedisequus]\nслужанка, <i>следовавшая за своей госпожой <i>Pl, Ter</i>; перен.</i> компаньонка, спутница (juris scientiam eloquentiae tamquam ancillulam pedisequamque adjunxisti <i>C</i>)."],
  ["pedisequus", "<big>pedisequus</big>\n\n<b>pedisequus, i</b> <i>m</i> [pes + sequor]\nслуга, <i>следовавший за своим господином</i> <i>C</i>: p. tibi sum <i>Pl</i> я следую за тобой."],
  ["peditastellus", "<big>peditastellus</big>\n\n<b>peditastellus, i</b> <i>m</i> [pedes]\nжалкий пехотинец, пехтура <i>Pl</i>."],
  ["peditatus", "<big>peditatus</big>\n\n<b>peditatus, us</b> <i>m</i> [pedes]\nпехота <i>C, Cs etc.</i>"],
  ["peditum", "<big>peditum</big>\n\n<b>I peditum, i</b> <i>n</i> [pedo II] <i>мед.</i>\nветры <i>Ctl</i>.\n\n<b>II peditum</b> <i>gen. pl. к</i>\npedes I."],
  ["Pedius", "<big>Pedius</big>\n\n<b>Pedius, a, um</b>\nПедий, <i>римск. nomen; наиболее известен</i> Q. P., <i>сонаследник (вместе с Августом) имения Юлия Цезаря</i> <i>C</i>."],
  ["pedo", "<big>pedo</big>\n\n<b>I pedo, avi, atum, are</b> [pes]\nподпирать тычинами (vineam <i>Col</i>).\n\n<b>II pedo, pepedi, peditum, ere</b> <i>мед.</i>\nиспускать ветры <i>H, M</i>."],
  ["pedor", "<big>pedor</big>\n\n<b>pedor, oris</b> <i>m</i>\n= paedor."],
  ["peduc-", "<big>peduc-</big>\n\n<b>peduc-</b> <i>v. l.</i>\n= pedic-."],
  ["Peducaeanus", "<big>Peducaeanus</big>\n\n<b>Peducaeanus, a, um</b> [Peducaeus 1]\nпедуцеев (census <i>C</i>)."],
  ["Peducaeus", "<big>Peducaeus</big>\n\n<b>Peducaeus, a, um</b>\nПедуцей, <i>римск. nomen; наиболее известны</i>:\n<b>1)</b> S. P., <i>народный трибун в 113 г. до н. э., внёсший rogatio Peducaea de incestu</i> (<i>относительно весталок</i>) <i>C</i>;\n<b>2)</b> S. P., <i>претор в Сицилии в 76-75 гг. до н. э.</i> (<i>когда Цицерон был квестором</i>) <i>C</i>;\n<b>3)</b> S. P., <i>друг Аттика и Цицерона, сторонник Цезаря, а затем Октавиана</i> <i>C</i>."],
  ["peduculus", "<big>peduculus</big>\n\n<b>peduc(u)lus</b> <i>PM</i>\n= pediculus II."],
  ["pedule", "<big>pedule</big>\n\n<b>pedule, is</b> <i>n</i> [pes]\nтуфля <i>Pt, Fronto</i>."],
  ["pedulis", "<big>pedulis</big>\n\n<b>pedulis, e</b> [pes]\nножной (fascia <i>Dig</i>)."],
  ["pedum", "<big>pedum</big>\n\n<b>I pedum, i</b> <i>n</i>\nпастушеский посох <i>V</i>.\n\n<b>II pedum</b> <i>gen. pl. к</i>\npes.\n\n<b>III Pedum, i</b> <i>n</i>\nПед, <i>древний город в Латии к вост. от Рима, на via Lavicana</i> <i>L</i>."],
  ["Pegae", "<big>Pegae</big>\n\n<b>Pegae, arum</b> <i>или</i> <b>Pege, es</b> <i>f</i> (<i>греч.</i> «источники»)\nПеги, <i>горный источник в Вифинии</i> <i>Prp</i>."],
  ["peganon", "<big>peganon</big>\n\n<b>peganon, i</b> <i>n</i> (<i>греч.</i>) <i>бот.</i>\nрута (<i>Rutagraveolens</i>, L.)<i>Ap.</i>"],
  ["Pegaseius", "<big>Pegaseius</big>\n\n<b>Pegaseius, a, um</b> [Pegasus 1]\nпегасов, <i>перен.</i> поэтический (melos <i>Pers</i>)."],
  ["Pegaseus", "<big>Pegaseus</big>\n\n<b>Pegaseus, a, um</b> [Pegasus I]\nпегасов (volatus <i>Ctl</i>): Pegaseo gradu <i>Sen</i> со стремительностью Пегаса."],
  ["Pegasis", "<big>Pegasis</big>\n\n<b>I Pegasis, idis</b> <i>f adj.</i> [Pegasus I]\nпегасова: Pegasides undae <i>O</i> = Иппокрена.\n\n<b>II Pegasis, idis</b> <i>f</i>\nПегасида:\n<b>1)</b> <i>Prp, O</i> = Муза;\n<b>2)</b> <i>нимфа источника Иппокрены</i> (= Oenone) <i>O</i>."],
  ["Pegasus", "<big>Pegasus</big>\n\n<b>I Pegasus (-os), i</b> <i>m</i> (<i>греч.</i> «ключевой», «родниковый»)\n<b>1)</b> Пегас, <i>крылатый конь, родившийся из крови Медузы-Горгоны, которой Персей отрубил голову; от удара его копыта на горе Геликон образовался источник Иппокрены</i>—fons caballinus <i>Pers</i>; <i>впоследствии Пегас служил Беллерофонту, убившему Химеру, но когда Беллерофонт хотел подняться на нём на небо, Пегас сбросил его и взлетел один, после чего был превращен в созвездие; впоследствии</i>—конь Муз <i>H, O, PM etc.</i>;\n<b>2)</b> крылатый вестник, быстрый гонец <i>C</i>.\n\n<b>II Pegasus, i</b> <i>m</i>\nПегас, <i>известный правовед времён Веспасиана</i> <i>J, Dig</i>."],
  ["Pege", "<big>Pege</big>\n\n<b>Pege, es</b> <i>f</i>\n= Pegae."],
  ["pegi", "<big>pegi</big>\n\n<b>pegi</b> <i>pf. к</i>\npango."],
  ["pegma", "<big>pegma</big>\n\n<b>pegma, atis</b> <i>n</i> (<i>греч.</i>)\n<b>1)</b> полки для книг <i>C</i>;\n<b>2)</b> театральные подмостки, которые могли быстро подниматься и опускаться <i>J, Sen, Ph, Vop</i>."],
  ["pegmaris", "<big>pegmaris</big>\n\n<b>pegmaris, e</b> [pegma]\nсражающийся на сцене <i>Su</i> (<i>v. l.</i>)."],
  ["pegniarius", "<big>pegniarius</big>\n\n<b>pegniarius, i</b> <i>m</i> <i>v. l.</i>\n= paegniarius."],
  ["pejero", "<big>pejero</big>\n\n<b>pejero (perjuro), avi, atum, are</b>\n<b>1)</b> ложно клясться, давать ложную присягу: conceptis verbis p. <i>Pl, C etc.</i> формально дать ложную присягу; p. aliquid <i>PM, Lcn</i>, per aliquid <i>Ctl</i> <i>и</i> de aliqua re <i>M</i> ложно клясться чём-л.;\n<b>2)</b> нарушить клятву: jus pejeratum <i>J</i> клятвопреступление; bellum pejerans <i>St</i> вероломная война;\n<b>3)</b> лгать: p. optime <i>Pl</i> лгать без зазрения совести."],
  ["pejor", "<big>pejor</big>\n\n<b>pejor, pejus</b> <i>compar. к</i>\nmalus."],
  ["pejoro", "<big>pejoro</big>\n\n<b>pejoro, —, —, are</b> [pejor]\n<b>1)</b> ухудшать (statum suam <i>Dig</i>);\n<b>2)</b> ухудшаться, обостряться (pejorans morbus <i>CA</i>)."],
  ["pejuro", "<big>pejuro</big>\n\n<b>pejuro</b> <i>H</i>\n= pejero."],
  ["pejurus", "<big>pejurus</big>\n\n<b>pejurus, a, um</b> <i>H</i>\n= perjurus."],
  ["pejus", "<big>pejus</big>\n\n<b>pejus</b> <i>compar. к</i>\nmale."],
  ["pelage", "<big>pelage</big>\n\n<b>pelage</b> <i>n</i> (<i>греч.</i>) <i>pl. к</i>\npelagus."],
  ["pelagia", "<big>pelagia</big>\n\n<b>pelagia, ae</b> <i>f</i> [pelagius]\nпелагия, <i>вид раковин-жемчужниц</i> <i>PM</i>."],
  ["pelagicus", "<big>pelagicus</big>\n\n<b>pelagicus, a, um</b> [pelagus]\nморской (pisces <i>Col</i>)."],
  ["pelagium", "<big>pelagium</big>\n\n<b>pelagium, i</b> <i>n</i> [pelagius]\nпурпурная краска <i>PM</i>."],
  ["pelagius", "<big>pelagius</big>\n\n<b>pelagius, a, um</b> [pelagus]\nморской (pisces <i>Col</i>; cursus <i>Ph</i>): phalerae pelagiae <i>PS</i> жемчужины и кораллы."],
  ["Pelagones", "<big>Pelagones</big>\n\n<b>Pelagones, um</b> <i>m</i>\nжители Пел агонии (<i>Македония</i>) <i>L</i>."],
  ["Pelagonia", "<big>Pelagonia</big>\n\n<b>Pelagonia, ae</b> <i>f</i>\nПелагония, <i>область и город в сев.-зап. Македонии</i> <i>L</i>."],
  ["pelagum", "<big>pelagum</big>\n\n<b>pelagum, i</b> <i>n</i> <i>Tert</i>\n= pelagus."],
  ["pelagus", "<big>pelagus</big>\n\n<b>pelagus, i</b> (<i>pl.</i> pelage) <i>n</i> (<i>греч.</i>; <i>лат.</i> mare)\nморе <i>Lcr, V, bH, VM etc.</i>; <i>поэт.</i> стремительный поток: it mare proruptum et pelago premit arva sonanti <i>V</i> (река Tumas) течёт как прорвавшееся море и бурным потоком затопляет поля."],
  ["pelamis", "<big>pelamis</big>\n\n<b>pelamis, dis</b> <i>или</i> <b>pelamys, ydis</b> <i>f</i> (<i>греч.</i>)\n<i>рыба</i> тунец (<i>до годовалого возраста</i>) <i>PM, J</i>."],
  ["Pelasgi", "<big>Pelasgi</big>\n\n<b>Pelasgi, orum</b> <i>и</i> <b>um</b> <i>m</i>\nпеласги:\n<b>1)</b> <i>доисторические (догреческие) обитатели Греции, а также части Малой Азии, Крита, Латия и Этрурии</i> <i>Mela, V, PM</i>;\n<b>2)</b> <i>поэт.</i> греки <i>V, O</i>."],
  ["Pelasgia", "<big>Pelasgia</big>\n\n<b>Pelasgia, ae</b> <i>f</i>\n<b>1)</b> <i>древнее название Пелопоннеса</i> <i>PM</i>\n<b>2)</b> <i>область в Фессалии</i> <i>PM</i>;\n<b>3)</b> <i>PM</i> = о-в Лесбос."],
  ["Pelasgias", "<big>Pelasgias</big>\n\n<b>Pelasgias, adis</b> <i>и</i> <b>Pelasgis, idis</b> [Pelasgi]\nпеласгическая, <i>поэт.</i> греческая <i>O</i>."],
  ["Pelasgicus", "<big>Pelasgicus</big>\n\n<b>Pelasgicus, a, um</b> <i>PM</i>\n= Pelasgus."],
  ["Pelasgus", "<big>Pelasgus</big>\n\n<b>Pelasgus, a, um</b> [Pelasgi]\nпеласгический, <i>поэт.</i> греческий (pubes <i>Prp</i>; ars <i>V</i>); додонский (quercus <i>O</i>)."],
  ["pelecanus", "<big>pelecanus</big>\n\n<b>pelecanus (pelicanus), i</b> <i>m</i> (<i>греч.</i>)\nпеликан <i>Vlg, Hier</i>."],
  ["pelecinon", "<big>pelecinon</big>\n\n<b>pelecinon, i</b> <i>n</i> (<i>греч.</i>)\nпелекин, <i>род солнечных часов</i> <i>Vtr</i>."],
  ["Peleius", "<big>Peleius</big>\n\n<b>Peleius, a, um</b> [Peleus]\nПелеев, <i>поэт.</i> Ахиллесов (facta <i>Sil</i>): Peleia virgo <i>St</i> = Брисеида."],
  ["Pelethronius", "<big>Pelethronius</big>\n\n<b>Pelethronius, a, um</b> <i>adj. к</i>\nPelethronium (<i>лесистая долина на горе Пелион (в Фессалии), в которой жили кентавры и лапифы</i>) <i>V, O, Lcn, St</i>."],
  ["Peleus", "<big>Peleus</big>\n\n<b>Pēle͡us, eī</b> <i>и</i> <b>eos</b> <i>m</i>\nПелей, <i>сын эгинского царя Эака, брат Теламона, царь мирмидонян во Фтии, один из Аргонавтов, муж нереиды Фетиды, отец Ахилла</i> <i>O, H, Ctl etc.</i>"],
  ["pelex", "<big>pelex</big>\n\n<b>pelex</b> (<b>pellex</b> <i>и</i> <b>paelex</b>), <b>icis</b> <i>f</i> (<i>греч.</i>)\n<b>1)</b> наложница, любовница <i>C, L, Just etc.</i>: p. Oebalia <i>O</i> = Елена; p. barbara <i>O</i> = Медея; p. Tyria <i>O</i> = Европа;\n<b>2)</b> соперница законной жены, разлучница (p. matris <i>O</i>)."],
  ["Pelia", "<big>Pelia</big>\n\n<b>Pelia, ae</b> <i>m</i> <i>SenT</i>\n= Pelias II."],
  ["Peliacus", "<big>Peliacus</big>\n\n<b>Peliacus, a, um</b> [Pelion]\nпелионский (vertex <i>Ctl</i>; apex <i>O</i>): Peliaca trabs <i>Prp</i> (carina <i>VF</i>) = Арго (<i>построенный из пелионского леса</i>); Peliaca cuspis <i>O</i> пелионское копьё (Ахилла)."],
  ["Pelias", "<big>Pelias</big>\n\n<b>I Pelias, adis</b> <i>f</i> [Pelias II]\nПелиева (<i>sc.</i> filia <i>Ph</i>).\n\n<b>II Pelias, ae</b> <i>m</i>\nПелий, <i>сын Нептуна и нимфы Тиро, брат Нелея и Эсона, дядя Ясона, царь Иолка, по наущению Медеи разрубленный на куски собственными дочерьми, надеявшимися вернуть этим отцу молодость</i> <i>Pl, C, O, VF</i>.\n\n<b>III Pelias, adis</b> <i>adj. f</i> [Pelion]\nпелионская: P. hasta <i>O</i> копьё Ахилла (<i>из пелионского леса</i>); P. pinus <i>St</i> = Арго."],
  ["pelicanus", "<big>pelicanus</big>\n\n<b>pelicanus</b> <i>v. l.</i>\n= pelecanus."],
  ["pelicatus", "<big>pelicatus</big>\n\n<b>pelicatus, us</b> <i>m</i> [pelex]\nвнебрачная связь, сожительство <i>C, Just</i>."],
  ["pelicula", "<big>pelicula</big>\n\n<b>pelicula, ae</b> <i>f</i> <i>v. l.</i>\n= pellicula."],
  ["Pelides", "<big>Pelides</big>\n\n<b>Pelides, ae</b> <i>m</i> [Peleus]\nПелид:\n<b>1)</b> сын Пелея, <i>т. е.</i> Ахилл <i>V, H etc.</i>;\n<b>2)</b> внук Пелея, <i>т. е.</i> Неоптолем <i>V</i>."],
  ["Pelign-", "<big>Pelign-</big>\n\n<b>Pelign-</b> <i>v. l.</i>\n= Paelign-."],
  ["Pelio", "<big>Pelio</big>\n\n<b>Pelio, onis</b> <i>m</i> <i>Sil</i>\n= Pelion."],
  ["Pelion", "<big>Pelion</big>\n\n<b>Pelion, i</b> <i>n</i>\nПелион, <i>гора на п-ове Магнесия (Фессалия), южное продолжение Оссы (ныне</i> Петрас) <i>Mela, O</i>."],
  ["Pelius", "<big>Pelius</big>\n\n<b>I Pelius (-os), i</b> <i>m</i> <i>PM, C</i>\n= Pelion.\n\n<b>II Pelius, a, um</b> [Pelion]\nпелионский (nemus <i>Ph</i>)."],
  ["Pella", "<big>Pella</big>\n\n<b>Pella, ae</b> <i>f</i>\nПелла, <i>столица Македонии к зап. от Аксия, место рождения Александра Македонского</i> <i>C, L</i>."],
  ["pellacia", "<big>pellacia</big>\n\n<b>pellacia, ae</b> <i>f</i> [pellax]\n<b>1)</b> заманивание, манящая сила, соблазн (placidi ponti <i>Lcr</i>);\n<b>2)</b> совращение, растление <i>Eccl</i>."],
  ["Pellaeus", "<big>Pellaeus</big>\n\n<b>Pellaeus, a, um</b> [Pella]\n<b>1)</b> пеллейский, <i>поэт.</i> македонский: P. juvenis <i>M</i> (ductor, tyrannus <i>Lcn</i>)= Александр Македонский;\n<b>2)</b> <i>поэт.</i> египетский (<i>так как Александр Македонский владел одно время Египтом</i>) (Canopus <i>V</i>) <i>или</i> александрийский (arces <i>Lcn</i>)."],
  ["pellax", "<big>pellax</big>\n\n<b>pellax, acis</b> <i>adj.</i> [pellicio]\nобманчивый, коварный (Ulixes <i>V</i>)."],
  ["Pelle", "<big>Pelle</big>\n\n<b>Pelle, es</b> <i>f</i> <i>PM</i>\n= Pella."],
  ["pellecebrae", "<big>pellecebrae</big>\n\n<b>pellecebrae, arum</b> <i>f</i> [pellicio]\nзаманивание, обольщение, соблазн <i>Pl</i>."],
  ["pellectio", "<big>pellectio</big>\n\n<b>pellectio, onis</b> <i>f</i> [pellego]\nпрочитывание <i>C</i>."],
  ["pellectus", "<big>pellectus</big>\n\n<b>I pellectus, a, um</b> <i>part. pf. к</i>\npellego (perlego).\n\n<b>II pellectus, a, um</b> <i>part. pf. к</i>\npellicio."],
  ["pellego", "<big>pellego</big>\n\n<b>pellego</b> <i>v. l.</i>\n= perlego."],
  ["Pellenaeus", "<big>Pellenaeus</big>\n\n<b>I Pellenaeus, a, um</b> [Pellene]\nпелленский <i>PM</i>.\n\n<b>II Pellenaeus, i</b> <i>m</i>\nжитель города Pellene <i>PM</i>."],
  ["Pellene", "<big>Pellene</big>\n\n<b>Pellene, es</b> <i>f</i>\nПеллена, <i>город в вост. Ахайе, между Сикионом и Эгирой</i> <i>L</i>."],
  ["Pellenensis", "<big>Pellenensis</big>\n\n<b>Pellenensis, e</b> <i>L</i>\n= Pellenaeus I, II."],
  ["pellesuina", "<big>pellesuina</big>\n\n<b>pellesuina, ae</b> <i>f</i> [pellis + suo]\nскорняжная лавка, мастерская меховщика <i>Vr</i>."],
  ["pellex", "<big>pellex</big>\n\n<b>pellex, icis</b> <i>f</i> <i>v. l.</i>\n= pelex."],
  ["pellexi", "<big>pellexi</big>\n\n<b>pellexi</b> <i>pf. к</i>\npellicio."],
  ["pelli", "<big>pelli</big>\n\n<b>I pelli</b> <i>inf. pass. к</i>\npello.\n\n<b>II pelli</b> (<i>редкий</i>) <i>abl. sg. к</i>\npellis."],
  ["pelliarius", "<big>pelliarius</big>\n\n<b>pelliarius, a, um</b> [pellis]\nскорняжный (taberna <i>Vr</i>)."],
  ["pellicatus", "<big>pellicatus</big>\n\n<b>pellicatus, us</b> <i>m</i> <i>v. l.</i>\n= pelicatus."],
  ["pelliceus", "<big>pelliceus</big>\n\n<b>pelliceus, a, um</b> <i>v. l.</i>\n= pellicius."],
  ["pellicio", "<big>pellicio</big>\n\n<b>pellicio, lexi, lectum, ere</b> [per + lacio]\nпривлекать, заманивать, соблазнять, склонять (aliquem ad se <i>C</i>; aliquem donis <i>T</i>); притягивать (vim ferri <i>Lcr</i>); чарами переносить на своё поле (fruges alienas <i>PM</i>)."],
  ["pellicis", "<big>pellicis</big>\n\n<b>pellicis</b> <i>gen. к</i>\npellex."],
  ["pellicius", "<big>pellicius</big>\n\n<b>pellicius, a, um</b> [pellis]\nприготовленный из шкур (sella <i>Lampr</i>; tunica <i>Pall</i>)."],
  ["pellicula", "<big>pellicula</big>\n\n<b>pellicula, ae</b> <i>f</i> [<i>demin. к</i> pellis]\n<b>1)</b> шкурка (haedina <i>C</i>); овчинка: pelliculam curare <i>H ирон.</i> заботиться о себе, всячески беречь себя; pelliculam veterem retinere <i>Pers</i> = держаться своих прежних обычаев;\n<b>2)</b> <i>анат.</i> крайняя плоть <i>J</i>."],
  ["pelliculatio", "<big>pelliculatio</big>\n\n<b>pelliculatio, onis</b> <i>f</i> [pellicio]\nсманивание, искушение <i>Cato</i>."],
  ["pelliculo", "<big>pelliculo</big>\n\n<b>pelliculo, —, —, are</b> [pellicula]\nпокрывать кожей, обтягивать шкурами (opercula vasorum <i>Col</i>)."],
  ["pellio", "<big>pellio</big>\n\n<b>pellio, onis</b> <i>m</i> [pellis]\nскорняк, меховщик <i>Pl, Lampr, Dig</i>."],
  ["pellis", "<big>pellis</big>\n\n<b>pellis, is</b> (<i>abl.</i> <i>иногда</i> i) <i>f</i>\n<b>1)</b> шкура, мех (caprina <i>C</i>; leonina <i>PM</i>): pellis aurea <i>Mela</i> золотое руно || кожа (p. rugosa <i>Ph</i>): detrahere alicui pellem <i>погов. H</i> = разоблачить кого-л., обнаружить чьи-л. недостатки; quiescere in propria pelle <i>погов. H</i> = довольствоваться своим; pellem rodere caninam <i>погов. M</i> = поносить, хулить;\n<b>2)</b> зимняя палатка (sub pellibus hiemare <i>Cs</i>);\n<b>3)</b> пергамент (pellibus exiguis artatur Livius ingens <i>M</i>);\n<b>4)</b> меховая шапка (pellibus tecta tempora <i>O</i>);\n<b>5)</b> кожаная обувь (pes in pelle natat <i>O</i>);\n<b>6)</b> ремень у обуви (pelles nigrae <i>H</i>)."],
  ["pellitus", "<big>pellitus</big>\n\n<b>pellitus, a, um</b> [pellis]\n<b>1)</b> покрытый шкурой, одетый в мех (Sardi <i>L</i>): oves pellitae <i>H</i> тонкорунные овцы (<i>тарентинской, аттической и др. пород</i>), на которых для защиты руна надевались овчины;\n<b>2)</b> меховой <i>или</i> кожаный (habitus <i>Eccl</i>);\n<b>3)</b> кожистый (vespertilio volat pellitis alis <i>Macr</i>)."],
  ["pello", "<big>pello</big>\n\n<b>pello, pepuli, pulsum, ere</b>\n<b>1)</b> бить, толкать (aliquem <i>C</i>); потрясать, сотрясать, колебать (ventis pulsa arbor <i>Lcr</i>); поражать (vulnere pelli <i>V</i>): p. fores <i>Ter, C</i> стучать в дверь; p. terram pede <i>Lcr, H</i> топать ногой о землю; p. mare remis <i>C</i> плыть на вёслах по морю; p. lyram <i>O</i> бряцать (играть) на лире; p. classica <i>Tib</i> трубить в рожок;\n<b>2)</b> приводить в движение (navigium contis <i>Su</i>): pulsae rates <i>Pt</i> отплывшие (снявшиеся с якоря) корабли; p. sagittam <i>V</i> пустить стрелу; p. initium sermonis <i>C</i> начинать (затевать) разговор;\n<b>3)</b> поражать, производить впечатление, возбуждать, затрагивать (aliquem <i>Pl</i>; animum alicujus <i>L</i>); задевать, касаться (injuria pellit aliquem <i>C</i>);\n<b>4)</b> изгонять, прогонять (aliquem patria <i>Nep</i>; praesidium ex arce <i>Nep</i>; aliquem de moenibus <i>L</i>, ab urbe <i>O</i>, in exsilium <i>C</i>; inimicos tectis <i>O</i>); отгонять, удалять, откинуть (maestitiam ex animis <i>C</i>; curas vino <i>H</i>); подавлять, удерживать (lacrimas <i>V</i>); утолять (famem <i>O</i>; sitim <i>H</i>);\n<b>5)</b> наносить поражение, разбивать, обращать в бегство (hostium exercitum <i>Cs</i>)."],
  ["pelluc-", "<big>pelluc-</big>\n\n<b>pelluc-</b> <i>v. l.</i>\n= perlue-."],
  ["pelluo", "<big>pelluo</big>\n\n<b>pelluo</b> <i>v. l.</i>\n= perluo."],
  ["Pelopea", "<big>Pelopea</big>\n\n<b>Pelopea, ae</b> <i>f</i> [Pelops]\nПелопея, <i>внучка Пелопа, дочь Тиеста</i> <i>O, M, Cld, J</i>."],
  ["Pelopeia", "<big>Pelopeia</big>\n\n<b>Pelopeia, ae</b> <i>f</i> <i>v. l.</i>\n= Pelopea."],
  ["Pelopeias", "<big>Pelopeias</big>\n\n<b>Pelopeias, adis</b> <i>adj. f</i> [Pelops]\nпелопова, <i>поэт.</i> пелопоннесская (Mycenae <i>O</i>)."],
  ["Pelopeis", "<big>Pelopeis</big>\n\n<b>Pelopeis, idis</b> <i>f</i>\n<b>1)</b> <i>O</i> = Pelopeias;\n<b>2)</b> жительница Пелопоннеса, аргивянка <i>St</i>."],
  ["Pelopeius", "<big>Pelopeius</big>\n\n<b>Pelopeius, a, um</b> [Pelops]\n<b>1)</b> пелопов (Atreus <i>O</i>): Pelopeia virgo <i>O</i> = Ифигения; Pelopeia arva <i>O</i> = Фригия (<i>где родился Пелоп</i>);\n<b>2)</b> пелопоннесский: Pelopeia sedes <i>SenT</i> престол Креонта, царя Коринфа."],
  ["Pelopeus", "<big>Pelopeus</big>\n\n<b>Pelopeus, a, um</b> <i>Prp, V, Lcn, St</i>\n= Pelopeius."],
  ["Pelopidae", "<big>Pelopidae</big>\n\n<b>Pelopidae, arum</b> <i>m</i>\nПелопиды, потомки Пелопа <i>C</i>."],
  ["Pelopidas", "<big>Pelopidas</big>\n\n<b>Pelopidas, ae</b> <i>m</i>\nПелопид, <i>фиванский полководец и политик, друг Эпаминонда</i> <i>Nep</i>."],
  ["Pelopius", "<big>Pelopius</big>\n\n<b>Pelopius, a, um</b> [Pelops]\nпелопов (domus <i>SenT</i>)."],
  ["Peloponnenses", "<big>Peloponnenses</big>\n\n<b>Peloponnenses, ium</b> <i>m</i>\nжители Пелопоннеса <i>QC etc.</i>"],
  ["Peloponnensis", "<big>Peloponnensis</big>\n\n<b>Peloponnensis, e</b>, <i>чаще</i> <b>Peloponnesiacus, a, um</b>\nпелопоннесский (bellum <i>Sen</i>)."],
  ["Peloponnesius", "<big>Peloponnesius</big>\n\n<b>I Peloponnesius, a, um</b>\nпелопоннесский (bellum <i>Nep</i>; civitates <i>C</i>): Peloponnesia tempora <i>Q</i> время (эпоха) Пелопоннесской войны (<i>431-404 гг. до н. э.</i>).\n\n<b>II Peloponnesius, i</b> <i>m</i>\nжитель <i>или</i> уроженец Пелопоннеса <i>Vr, QC</i>."],
  ["Peloponnesus", "<big>Peloponnesus</big>\n\n<b>Peloponnesus (-os), i</b> <i>f</i>\nПелопоннес, <i>названный так по имени Пелопа, который, по преданию, переселился сюда</i> (<i>ныне</i> Морея) <i>Vr, C, L etc.</i> ."],
  ["Pelops", "<big>Pelops</big>\n\n<b>Pelops, opis</b> <i>m</i>\nПелоп, <i>сын фригийского царя Тантала, брат Ниобы, муж Гипподамии, царь Элиды и Аргоса, отец Атрея и Тиеста, дед Агамемнона и Менелая; в детстве был заколот отцом и подан в качестве угощения богам, но злодеяние Тантала было тотчас же раскрыто, и Юпитер воскресил мальчика, а съеденное Церерой плечо заменил плечом из слоновой кости</i> (umero P. insignis eburno <i>V</i>); <i>будучи впоследствии изгнан из Фригии, Пелоп отправился в Элиду, где женился на Гипподамии, дочери царя Эномая, по смерти которого стал царём Элиды; затем он распространил свою власть на весь полуостров, который стал называться «островом Пелопа»</i> (<i>Peloponnesus</i>) <i>Enn, C, H, St</i>."],
  ["Pelorias", "<big>Pelorias</big>\n\n<b>Pelorias, adis</b> <i>f</i>\nПелориада, <i>мыс на сев.-вост. оконечности Сицилии</i> (<i>ныне</i> Capo di Faro) <i>O, Sol</i>."],
  ["peloris", "<big>peloris</big>\n\n<b>I peloris, idis</b> (<i>acc. sg.</i> idem <i>или</i> ida; <i>acc. pl.</i> idas) <i>f</i> (<i>греч.</i>)\nпелорида, <i>гигантская раковина</i> <i>Vr, H, CC, PM, M</i>.\n\n<b>II Peloris, idis</b> <i>f C, Mela</i>, <b>Peloros (-us), i</b> <i>m O, Sil</i> <i>и</i> <b>Pelorum, i</b> <i>n PM</i>\n= Pelorias."],
  ["pelta", "<big>pelta</big>\n\n<b>pelta, ae</b> <i>f</i> (<i>греч.</i>)\nпельта, небольшой серповидный щит <i>Nep, L, V</i>."],
  ["peltastae", "<big>peltastae</big>\n\n<b>peltastae, arum</b> <i>m</i> [pelta]\nпельтасты, воины, вооружённые пельтами <i>Nep, L</i>."],
  ["peltatus", "<big>peltatus</big>\n\n<b>peltatus, a, um</b> [pelta]\nвооружённый пельтой <i>O, M, Cld</i>."],
  ["peltifer", "<big>peltifer</big>\n\n<b>peltifer, fera, ferum</b> [pelta + fero] <i>St</i>\n= peltatus."],
  ["peluis", "<big>peluis</big>\n\n<b>peluis, is</b> <i>f</i> <i>v. l.</i>\n= pelvis."],
  ["Pelusiacus", "<big>Pelusiacus</big>\n\n<b>Pelusiacus</b> <i>и</i> <b>Pelusianus, a, um</b> [Pelusium]\nпелузийский <i>V, PM, Col</i>."],
  ["Pelusiota", "<big>Pelusiota</big>\n\n<b>Pelusiota (-es), ae</b> <i>m</i>\nжитель города Pelusium <i>AG, Hier</i>."],
  ["Pelusium", "<big>Pelusium</big>\n\n<b>Pelusium, i</b> <i>n</i>\nПелузий, <i>укреплённый город в Нижнем Египте, близ устья самого вост. рукава нильской дельты</i> (<i>ныне</i> Tineh) <i>Cs, L, PM</i>."],
  ["Pelusius", "<big>Pelusius</big>\n\n<b>Pelusius, a, um</b> <i>M, Ph</i>\n= Pelusiacus."],
  ["pelvis", "<big>pelvis</big>\n\n<b>pelvis, is</b> (<i>acc.</i> em <i>и</i> im, <i>abl.</i> e <i>и</i> i) <i>f</i>\nтаз <i>Vr, CC, Cato, PM etc.</i>"],
  ["peminosus", "<big>peminosus</big>\n\n<b>peminosus, a, um</b> (<i>греч.</i>)\nвесь в трещинах <i>или</i> трескающийся (area <i>Vr</i>)."],
  ["pemma", "<big>pemma</big>\n\n<b>pemma, atis</b> <i>n</i> (<i>греч.</i>)\nсладкое печенье, пирожное <i>Vr, Ap</i>."],
  ["penarius", "<big>penarius</big>\n\n<b>penārius, a, um</b> [penus]\nотносящийся к съестным припасам, продовольственный: cella penaria <i>C</i> кладовая, амбар, <i>перен.</i> житница (cella penaria rei publicae nostrae, <i>sc.</i> Sicilia <i>C</i>)."],
  ["penates", "<big>penates</big>\n\n<b>penātēs, ium</b> <i>m</i>\n<b>1)</b> пенаты, боги-хранители: p. minores (familiares, privati) <i>C etc.</i> боги домашнего очага; p. majores (publici) <i>C etc.</i> боги-хранители общества и государства;\n<b>2)</b> жилище, дом: p. conducti <i>M</i> наёмная квартира; angusti penates <i>Lcn</i> бедное жильё."],
  ["penatiger", "<big>penatiger</big>\n\n<b>penātiger, gera, ger</b> [penates + gero]\nнесущий <i>или</i> уносящий (своих) пенатов (Aeneas <i>O</i>)."],
  ["penator", "<big>penator</big>\n\n<b>penātor, ōris</b> <i>m</i> [penus]\nнесущий продовольствие <i>Cato</i>."],
  ["pendeo", "<big>pendeo</big>\n\n<b>pendeo, pependi, —, ere</b>\n<b>1)</b> висеть, свешиваться, свисать, повиснуть (in <i>и</i> ex arbore <i>C</i>; ab <i>или</i> ex umero <i>C, O</i>; de collo alicujus <i>O</i>): p. tenui filo <i>погов. O</i> висеть на волоске;\n<b>2)</b> быть вывешенным, объявленным (<i>ко всеобщему сведению</i>): Claudius pependit venalis <i>Su</i> имущество Клавдия было объявлено к продаже с торгов;\n<b>3)</b> быть подвешенным (<i>для избиения</i>) (pendentem aliquem ferire <i>Pl</i>);\n<b>4)</b> (<i>тж.</i> laqueo p. <i>Sen</i>) быть повешенным, повеситься (pendentem aliquem videre <i>M</i>): p. in cruce <i>Sen</i> быть распятым на кресте;\n<b>5)</b> ниспадать (apte pendet chlamys <i>O</i>); быть опущенным (lacerti pendent <i>PJ</i>);\n<b>6)</b> парить, порхать, носиться (per aerias auras <i>O</i>);\n<b>7)</b> нависать (pendentia nubila <i>O</i>; scopulus pendet <i>O</i>): p. in latus <i>Col</i> валиться (крениться) на бок || быть близким к падению (homo pendens <i>C</i>);\n<b>8)</b> медлить, задерживаться, оставаться (in limine alicujus <i>V</i>); льнуть: ex (ab) alicujus ore p. <i>Lcr, V, O</i> жадно слушать кого-л.; p. circum oscula <i>V</i> тянуться с поцелуями;\n<b>9)</b> останавливаться, быть прерванным, прекращаться (pendent opera interrupta <i>V</i>);\n<b>10)</b> (<i>тж.</i> animo p. <i>C, L</i>) быть в нерешительности, колебаться, сомневаться (animi pendetites <i>L, Sen, QC</i>): in pendenti <i>Dig</i> в состоянии неопределённости;\n<b>11)</b> быть неясным, неопределённым, недостоверным (pendet belli fortuna <i>O</i>);\n<b>12)</b> зависеть (ex vultu et nutu alicujus <i>C</i>; in sententiis civium <i>C</i>; levi momento <i>C</i>): casu p. ab uno <i>Lcn</i> быть связанным одной и той же судьбой; finis ab origine pendet <i>Man</i> конец зависит от начала;\n<b>13)</b> происходить (ex una origine <i>O</i>);\n<b>14)</b> быть преданным (ex <i>или</i> de aliquo <i>C, H etc.</i>);\n<b>15)</b> точно следовать, рабски подражать: hinc omnis pendet Lucilius <i>H</i> этому (манере древней комедии) следует Луцилий;\n<b>16)</b> весить (cyathus pendet drachmas decem <i>PM</i>)."],
  ["pendigo", "<big>pendigo</big>\n\n<b>pendigo, inis</b> <i>f</i>\n<b>1)</b> внутренний нарыв, гнойник <i>Veg</i>;\n<b>2)</b> пустота, полость (simulacri <i>Eccl</i>)."],
  ["pendo", "<big>pendo</big>\n\n<b>pendo, pependi, pensum, ere</b>\n<b>1)</b> вешать, отвешивать (herbas <i>O</i>; aliquid aequa lance <i>Eccl</i>);\n<b>2)</b> взвешивать, обсуждать, обдумывать (res, non verba <i>C</i>; aliquem ex virtute <i>C</i>; aliquid suo pondere <i>C</i>);\n<b>3)</b> ценить, уважать, ставить (aliquid magni <i>H</i>, parvi <i>Sl</i>): p. nihili <i>Pl, Ter</i> (flocci <i>Ter</i>) ни во что не ставить;\n<b>4)</b> платить, выплачивать, вносить (pecuniam alicui <i>C</i>; vectigal <i>Cs, C, Sl</i>; stipendium alicui <i>Cs, Sl, L</i>);\n<b>5)</b> нести, терпеть (poenam pro scelere <i>Lcr</i> <i>и</i> poenas temeritatis <i>C</i>); искупать (crimen, culpam <i>VF</i>; aliquid sanguine <i>Just</i>);\n<b>6)</b> весить (minus pondo octoginta <i>L</i>);\n<b>7)</b> <i>перен.</i> иметь вес, значение (bona vera idem pendunt <i>Sen</i> — <i>v. l.</i> pendent)."],
  ["pendulus", "<big>pendulus</big>\n\n<b>pendulus, a, um</b> [pendeo]\n<b>1)</b> висящий, висячий (libra <i>O</i>); свисающий, ниспадающий (tela <i>O</i>); обвислый (genae <i>PM</i>; vela <i>Ap</i>); нависший (loca <i>Col</i>);\n<b>2)</b> парящий (eodem loco pendula, <i>sc.</i> aquila <i>Ap</i>);\n<b>3)</b> колеблющийся, нерешительный (spe p. <i>H</i>)."],
  ["pene", "<big>pene</big>\n\n<b>pene</b> <i>v. l.</i>\n= paene."],
  ["Peneis", "<big>Peneis</big>\n\n<b>Peneis, idis</b> <i>f</i> [Peneus I]\nпенейская (undae <i>O</i>); P. nympha <i>O</i> = Дафна."],
  ["Peneius", "<big>Peneius</big>\n\n<b>Peneius, a, um</b> [Peneus I]\nпенейский (arva <i>O</i>; Tempe <i>V</i>): P. amnis <i>Lcn</i> река Пеней."],
  ["Penelopa", "<big>Penelopa</big>\n\n<b>Penelopa, ae</b> <i>и</i> <b>Penelope, es</b> <i>f</i>\nПенелопа, <i>дочь Икария и Перибеи, жена Одиссея, мать Телемаха</i> <i>C, H, Sen etc.</i>"],
  ["Penelopeus", "<big>Penelopeus</big>\n\n<b>Penelopeus, a, um</b> [Penelopa]\nпенелопин (fides <i>O</i>)."],
  ["Peneos", "<big>Peneos</big>\n\n<b>Peneos</b> <i>v. l.</i>\n= Peneus."],
  ["penes", "<big>penes</big>\n\n<b>penes</b> <i>praep. cum acc.</i>\n1) у, в обладании, в руках (potestas est p. aliquem <i>C</i>): eloquentia est p. eum <i>C</i> он обладает красноречием; usus, p. quem arbitrium est <i>H</i> обычай, который имеет решающее значение; p. te es? <i>H</i> в уме ли ты?; causam p. judices vicisse <i>Ap</i> выиграть дело в суде;\n<b>2)</b> на стороне (p. rem publicam esse <i>T</i>): p. te culpa est <i>Ter</i> ты виноват."],
  ["Penestae", "<big>Penestae</big>\n\n<b>Penestae, arum</b> <i>m</i>\nжители Пенестии (<i>Иллирия</i>) <i>L</i>."],
  ["Penestia", "<big>Penestia</big>\n\n<b>Penestia, ae</b> <i>f</i>\nПенестия, <i>область в сев.-вост. Иллирии</i> <i>L</i>."],
  ["Penestianus", "<big>Penestianus</big>\n\n<b>Penestianus, a, um</b> [Penestae <i>и</i> Penestia]\nпенестийский <i>L</i>."],
  ["penetica", "<big>penetica</big>\n\n<b>penetica, ae</b> <i>f</i> (<i>греч.</i>)\nголодный режим: peneticam facere <i>Caelius ap. C</i> быть посаженным на голодную диету."],
  ["penetrabilis", "<big>penetrabilis</big>\n\n<b>penetrabilis, e</b> [penetro]\n<b>1)</b> проницаемый (corpus nullo penetrabile telo <i>O</i>);\n<b>2)</b> проникающий, пробивающий насквозь (telum <i>V</i>); пронизывающий (frigus <i>V</i>);\n<b>3)</b> трогательный (misericordia <i>AG</i>)."],
  ["penetrabiliter", "<big>penetrabiliter</big>\n\n<b>penetrabiliter</b>\nнасквозь (videre <i>Eccl</i>)."],
  ["penetrale", "<big>penetrale</big>\n\n<b>penetrale (penetral), is</b> <i>n</i> [penetralis] (<i>преим.</i> <i>pl.</i>)\n<b>1)</b> внутренность, внутренняя часть, глубина (urbis <i>L</i>);\n<b>2)</b> <i>перен.</i> недра, тайники (penetralia sapientiae <i>Q</i>; cordis et animae <i>AG</i>); святилище (deorum <i>T</i>)."],
  ["penetralis", "<big>penetralis</big>\n\n<b>penetralis, e</b> [penetro]\n<b>1)</b> проникающий насквозь, прожигающий (ignis <i>Lcr</i>); пронизывающий (frigus <i>Lcr</i>);\n<b>2)</b> внутренний, находящийся внутри (tecta <i>V</i>): dii penetrales <i>Sen</i> = пенаты."],
  ["penetratio", "<big>penetratio</big>\n\n<b>penetratio, onis</b> <i>f</i>\nпроникновение <i>Ap, Aug</i>."],
  ["penetrator", "<big>penetrator</big>\n\n<b>penetrator, oris</b> <i>m</i>\nпроникающий (domus alienae <i>Aug</i>)."],
  ["penetratus", "<big>penetratus</big>\n\n<b>penetratus, a, um</b> [penetro]\n<b>1.</b> <i>part. pf. к</i> penetro;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> пройденный, проложенный (iter penetratum <i>T</i>);\n<b>2)</b> <i>иногда</i> <i>act.</i> проникший: quae penetrata queunt sensum progignere acerbum <i>Lcr</i> которые (частицы), проникнув (в тело), могут вызвать неприятное ощущение."],
  ["penetro", "<big>penetro</big>\n\n<b>penetro, avi, atum, are</b> [penitus]\n<b>1)</b> вводить, вставлять, ставить: p. pedem intra aedes <i>или</i> intra portam <i>Pl</i> входить в дом; se p. отправляться (foras <i>Pl</i>): se p. in fugam <i>Pl</i> обращаться в бегство;\n<b>2)</b> проникать (in castra hostium <i>L</i>; in animos <i>C</i>); достигать (aures <i>Lcr</i> <i>и</i> ad aures <i>O</i>); доходить, вступать, вторгаться (Illyricos sinus <i>V</i>; regna Persidis <i>Amm</i>);\n<b>3)</b> производить впечатление, потрясать (animum alicujus <i>T</i>): tum penetrabat eos, posse haec... <i>Lcr</i> тогда им пришло в голову, что эти (металлы) могут."],
  ["Peneus", "<big>Peneus</big>\n\n<b>I Peneus (-os), i</b> <i>m</i>\nПеней:\n<b>1)</b> <i>главн. река Фессалии, берущая начало в Эпире (гора Пинд), текущая по долине Темпе и впадающая в Термейский залив</i> <i>O, L</i>;\n<b>2)</b> <i>бог этой реки, отец Мирены и Дафны</i> <i>V, O</i>.\n\n<b>II Peneus, a, um</b> [Peneus I]\nпенейский (undae, arva <i>O</i>)."],
  ["penicillum", "<big>penicillum</big>\n\n<b>penicillum, i</b> <i>n</i> <i>и</i> <b>penicillus, i</b> <i>m</i> [<i>demin. к</i> peniculus]\n<b>1)</b> кисть <i>C, PM, Q</i>;\n<b>2)</b> корпия <i>CC, PM</i>;\n<b>3)</b> губка (<i>для стирания</i>) (penicillo detergere <i>PM</i>);\n<b>4)</b> живопись (Zeuxis penicillum ad magnam gloriam perduxit <i>PM</i>);\n<b>5)</b> стиль, слог (pingere Britanniam penicillo suo <i>C</i>)."],
  ["peniculamentum", "<big>peniculamentum</big>\n\n<b>peniculamentum, i</b> <i>n</i> [peniculus]\n<b>1)</b> хвост <i>Eccl</i>;\n<b>2)</b> хвост платья, шлейф <i>Enn, LM</i>."],
  ["peniculus", "<big>peniculus</big>\n\n<b>peniculus, i</b> <i>m</i> [<i>demin. к</i> penis]\n<b>1)</b> метёлка (peniculo mensam detergere <i>Pl</i>);\n<b>2)</b> губка (<i>удлинённой формы</i>) <i>Pl, Ter</i>;\n<b>3)</b> кисточка <i>Dig</i>."],
  ["peninsula", "<big>peninsula</big>\n\n<b>peninsula, ae</b> <i>f</i> <i>v. l.</i>\n= paeninsula."],
  ["Peninus", "<big>Peninus</big>\n\n<b>Peninus, a, um</b> <i>v. l.</i>\n= Penninus."],
  ["penis", "<big>penis</big>\n\n<b>penis, is</b> (<i>abl.</i> e <i>и</i> i) <i>m</i>\n<b>1)</b> хвост (caudam antiqui penem vocabant <i>C</i>);\n<b>2)</b> мужской член <i>C, H, J</i>;\n<b>3)</b> разврат (pene bona patria lacerare <i>Sl</i>)."],
  ["penissime", "<big>penissime</big>\n\n<b>penissime</b> <i>v. l.</i>\n= paenissime."],
  ["penite", "<big>penite</big>\n\n<b>penite</b> [penitus I]\nвнутри, внутренне, глубоко <i>Ctl, Sid</i>."],
  ["penitus", "<big>penitus</big>\n\n<b>I penitus, a, um</b>\nвнутренний, глубокий (ignis <i>Vtr</i>; pars domus <i>Ap</i>): ex penitis faucibus <i>Pl</i> из глубины горла.\n\n<b>II penitus</b> <i>adv.</i>\n<b>1)</b> внутри (p., non fronte notare <i>Man</i>); внутрь (extra penitusque <i>LM</i>); глубоко, далеко (p. in Thraciam se abdere <i>Nep</i>; argentum p. abditum <i>C</i>; p. discindere ferro <i>Lcn</i>): p. mandare animis <i>C</i> глубоко запечатлеть в умах;\n<b>2)</b> от всего сердца, из глубины души (rogare <i>C</i>);\n<b>3)</b> совершенно, вполне (p. Dalmatia subacta <i>Eutr</i>);\n<b>4)</b> точно, ясно, в совершенстве (cognoscere, nosse, videre <i>C</i>);\n<b>5)</b> целиком, всецело (huic uni studio deditus <i>C</i>);\n<b>6)</b> <i>при</i> <i>compar.</i> гораздо (p. crudelior <i>Prp</i>)."],
  ["Penius", "<big>Penius</big>\n\n<b>Penius, i</b> <i>m</i>\nПений, <i>река в Колхиде, впадающая в Чёрное море</i> <i>O</i>."],
  ["penna", "<big>penna</big>\n\n<b>penna (pinna), ae</b> <i>f</i>\n<b>1)</b> перо (alae pennas habent <i>Pl</i>);\n<b>2)</b> крыло, крылья (pennas explicare <i>O</i>): vertere pennas <i>Prp</i> улететь; incidere pennas alicui <i>погов. C</i> подрезать кому-л. крылья;\n<b>3)</b> оперение стрелы (pennis acta sagitta <i>O</i>);\n<b>4)</b> стрела (olor trajectus penna <i>O</i>);\n<b>5)</b> полёт (вещей птицы) (felicibus edita pennis <i>Prp</i>);\n<b>6)</b> остроконечная башенка <i>или</i> зубец стены (pinnae loricaeque ex cratibus <i>Cs</i>);\n<b>7)</b> ковш водяного колеса <i>Vtr</i>;\n<b>8)</b> рыбий плавник <i>PM</i>;\n<b>9)</b> клавиш водяного органа <i>Vtr</i>;\n<b>10)</b> зубочистка (pinna dentes perfodere <i>Pt</i>);\n<b>11)</b> писчее перо (instrumenta scribae: calamus et p. <i>Is</i>)."],
  ["pennatulus", "<big>pennatulus</big>\n\n<b>pennatulus, a, um</b> <i>Tert</i>\n= pennatus."],
  ["pennatus", "<big>pennatus</big>\n\n<b>pennatus, a, um</b> [penna]\nоперённый, окрылённый, крылатый (Zephyrus <i>Lcr</i>; apes <i>PM</i>)."],
  ["pennifer", "<big>pennifer</big>\n\n<b>pennifer, fera, ferum</b> <i>Sid</i>\n= penniger."],
  ["penniger", "<big>penniger</big>\n\n<b>penniger, gera, gerum</b> [penna + gero] <i>C, PM, Sil</i>\n= pennatus."],
  ["Penninus", "<big>Penninus</big>\n\n<b>Penninus, a, um</b>\nпеннинский: Alpes Penninae <i>T</i> Пеннинские Альпы (<i>между нын. Saint-Bernard <i>и</i> Saint-Gothard</i>); P. (mons) <i>Sen, L</i> <i>ныне</i> Большой Сен-Бернард; Penninum iter <i>T</i> путь через нынешний Большой Сен-Бернард."],
  ["pennipes", "<big>pennipes</big>\n\n<b>pennipes, pedis</b> [penna + pes]\nс крыльями на ногах (Perseus <i>Ctl</i>)."],
  ["pennipotens", "<big>pennipotens</big>\n\n<b>penni–potens, entis</b> <i>adj.</i>\nпернатый, крылатый (ferae <i>Lcr</i>)."],
  ["pennula", "<big>pennula</big>\n\n<b>pennula, ae</b> <i>f</i> [<i>demin. к</i> penna]\nкрылышко <i>C, Hier</i>."],
  ["pensabilis", "<big>pensabilis</big>\n\n<b>pensabilis, e</b> [penso]\nвозместимый (damna <i>Amm</i>)."],
  ["pensatio", "<big>pensatio</big>\n\n<b>pensatio, onis</b> <i>f</i> [penso]\n<b>1)</b> возмещение, компенсация (bonorum <i>Pt</i>);\n<b>2)</b> рассмотрение, исследование <i>Amm</i>."],
  ["pense", "<big>pense</big>\n\n<b>pense</b> <i>Symm</i>\n= pensiculate."],
  ["pensiculate", "<big>pensiculate</big>\n\n<b>pensiculate</b> [pensiculo]\nточно, тщательно (scribere <i>AG</i>)."],
  ["pensiculo", "<big>pensiculo</big>\n\n<b>pensiculo, (avi), atum, are</b> [penso]\nтщательно взвешивать, обдумывать (unumquodque dictum <i>Ap</i>; p. et examinare aliquid <i>Ap</i>)."],
  ["pensilia", "<big>pensilia</big>\n\n<b>pensilia, ium</b> <i>n</i> [pensilis]\n<b>1)</b> (<i>sc.</i> poma) подвешенные (для просушки <i>или</i> для сохранности) плоды <i>Vr</i>;\n<b>2)</b> (<i>sc.</i> membra) <i>Priap</i> = membrum virile."],
  ["pensilis", "<big>pensilis</big>\n\n<b>pensilis, e</b> [pendeo]\nвисящий, подвешенный (uva <i>H</i>): se facere pensilem <i>Pl</i> повеситься || <i>архит.</i> подпираемый столбами, висячий (horti <i>QC, PM, Lad</i>): pensilibus plumis vehi <i>J</i> передвигаться на носилках, устланных пуховиками; balneae pensiles <i>VM, PM, Macr</i> бани с душем (<i>по др.</i>, паровые)."],
  ["pensio", "<big>pensio</big>\n\n<b>pensio, onis</b> <i>f</i> [pendo]\n<b>1)</b> платёж, уплата: p. praesens <i>L</i> платёж наличными;\n<b>2)</b> взнос: pecuniam tribus pensioni bus solvere <i>L</i> уплатить деньги в три срока (тремя взносами);\n<b>3)</b> налог, подать (p. vectigalium <i>AV</i>);\n<b>4)</b> арендная <i>или</i> квартирная плата (aedium p. annua <i>J</i>);\n<b>5)</b> проценты на капитал <i>Lampr, Dig</i>;\n<b>6)</b> возмещение (jacturae <i>Pt</i>);\n<b>7)</b> взвешивание <i>Vtr</i>;\n<b>8)</b> вес, тяжесть <i>Vtr</i>."],
  ["pensitatio", "<big>pensitatio</big>\n\n<b>pensitatio, onis</b> <i>f</i> <i>PM, Eccl</i>\n= pensatio."],
  ["pensitator", "<big>pensitator</big>\n\n<b>pensitator, oris</b> <i>m</i> [pensito]\nвзвешивающий: p. verborum <i>AG</i> блюститель точного словоупотребления."],
  ["pensito", "<big>pensito</big>\n\n<b>pensito, avi, atum, are</b> [<i>intens. к</i> penso]\n<b>1)</b> взвешивать: aliquid aequa lance p. <i>погов. PM</i> = обсудить что-л. по справедливости;\n<b>2)</b> обдумывать, размышлять (aliquid <i>L, PJ, Su</i>);\n<b>3)</b> уплачивать, вносить (vectigalia alicui <i>C</i>)."],
  ["pensiuncula", "<big>pensiuncula</big>\n\n<b>pensiuncula, ae</b> <i>f</i> [<i>demin. к</i> pensio]\nплатёж, взнос (fenoris <i>Col</i>)."],
  ["penso", "<big>penso</big>\n\n<b>penso, avi, atum, are</b> [<i>intens. к</i> pendo]\n<b>1)</b> взвешивать, отвешивать (aurum <i>L</i>);\n<b>2)</b> сопоставлять, сравнивать (aliquid alicui rei <i>L</i>); уравновешивать: res pensatae <i>L</i> вещи, находящиеся в равновесии (приведённые в равновесие); auro pensandus <i>Sen</i> ценящийся на вес золота; p. iter <i>Lcn</i> сокращать путь;\n<b>3)</b> возмещать, воздавать, вознаграждать (munus munere <i>O</i>); навёрстывать (moram velocitate <i>Sen</i>);\n<b>4)</b> поплатиться, искупать (laudem cum damno <i>O</i>; aliquid morte <i>VM</i>; nece matura pudorem <i>O</i>);\n<b>5)</b> взвешивать, обсуждать, обдумывать (consilium <i>L</i>); оценивать, определять (vires hostium oculis <i>L</i>; aliquem ex factis <i>L</i>); p. aliquem eadem trutina <i>погов. H</i> мерить кого-л. на общий аршин;\n<b>6)</b> утолять (sitis est pensanda gregum <i>Calp</i>)."],
  ["pensor", "<big>pensor</big>\n\n<b>pensor, oris</b> <i>m</i> [pendo]\nвзвешиватель (mensores et pensores elementorum <i>Aug</i>)."],
  ["pensum", "<big>pensum</big>\n\n<b>pensum, i</b> <i>n</i> [pendo]\n<b>1)</b> «урок», определённое по весу количество шерсти, которое надлежало спрясть за один день (p. facere <i>Pl</i>);\n<b>2)</b> пряжа сестёр-Парок (durae peragunt pensa sorores <i>SenT</i>; tres Parcae aurea pensa torquentes <i>Pt</i>);\n<b>3)</b> задание, обязанность (p. suum conficere <i>Pl</i>)."],
  ["pensura", "<big>pensura</big>\n\n<b>pensura, ae</b> <i>f</i> [pendo]\nвзвешивание, отвешивание <i>Vr</i>."],
  ["pensus", "<big>pensus</big>\n\n<b>pensus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> pendo;\n<b>2.</b> <i>adj.</i> важный: nihil pensi habeo <i>Sl, Su</i> <i>или</i> duco <i>VM</i>, <i>тж.</i> mihi non pensi est <i>Sl</i> мне это безразлично."],
  ["pentacontarchus", "<big>pentacontarchus</big>\n\n<b>pentacontarchus, i</b> <i>m</i> (<i>греч.</i>)\nпентаконтарх, командир отряда в 500 человек <i>Vlg</i>."],
  ["pentadactylos", "<big>pentadactylos</big>\n\n<b>pentadactylos, i</b> <i>f</i> (<i>греч.</i>) <i>PM, Ap</i>\n= pentapetes."],
  ["pentadoros", "<big>pentadoros</big>\n\n<b>pentadoros, on</b> <i>adj.</i> (<i>греч.</i>; <i>лат.</i> palmipes)\nпротяжением в 5 пальмов, <i>т. е.</i> 36,95 <i>см</i> <i>Vtr, PM</i>."],
  ["pentagonon", "<big>pentagonon</big>\n\n<b>pentagonon (-um), i</b> <i>n</i> (<i>греч.</i>)\nпятиугольник <i>Boet</i>."],
  ["pentameter", "<big>pentameter</big>\n\n<b>pentameter, tri</b> <i>m</i> (<i>греч.</i>)\nпентаметр, пятистопный стих <i>Q</i>."],
  ["pentapetes", "<big>pentapetes</big>\n\n<b>pentapetes, is</b> <i>m</i> (<i>греч.</i>) <i>бот.</i>\nлапчатка (<i>Potentilla</i>, L.) <i>PM</i>."],
  ["pentapharmacum", "<big>pentapharmacum</big>\n\n<b>pentapharmacum, i</b> <i>n</i> (<i>греч.</i>)\nблюдо из пяти составных частей <i>Spart</i>."],
  ["pentaphyllon", "<big>pentaphyllon</big>\n\n<b>pentaphyllon, i</b> <i>n</i> <i>PM, Ap</i>\n= pentapetes."],
  ["Pentapolis", "<big>Pentapolis</big>\n\n<b>Pentapolis, is</b> <i>f</i> (<i>греч.</i> «Пятиградье»)\nПентаполис, <i>область в Кирене с городами: Аполлония, Птолемаида, Арсиноя, Береника и Кирена</i> <i>Sol, Vlg, CJ</i>."],
  ["Pentapolitanus", "<big>Pentapolitanus</big>\n\n<b>Pentapolitanus, a, um</b> [Pentapolis]\nпентаполитанский, <i>тж.</i> киренейский (regio <i>PM</i>)."],
  ["pentaprotia", "<big>pentaprotia</big>\n\n<b>pentaprotia, ae</b> <i>f</i> (<i>греч.</i>)\nпентапротия, коллегия из пятерых высших чиновников городского управления (curia) <i>CJ</i>."],
  ["pentaspaston", "<big>pentaspaston</big>\n\n<b>pentaspaston, i</b> <i>n</i> (<i>греч.</i>)\nпентаспаст, полиспаст из пяти блоков <i>Vtr</i>."],
  ["pentastichos", "<big>pentastichos</big>\n\n<b>pentastichos, on</b> (<i>греч.</i>)\nимеющий пять рядов колонн (porticus <i>Treb</i>)."],
  ["pentateuchum", "<big>pentateuchum</big>\n\n<b>pentateuchum, i</b> <i>n</i> <i>и</i> <b>-us, i</b> <i>m</i> (<i>греч.</i>)\nпятикнижие (Моисея) <i>Eccl</i>."],
  ["pentathlus", "<big>pentathlus</big>\n\n<b>pentathlus, i</b> <i>m</i> (<i>греч.</i>) <i>PM</i>\n= quinquertio."],
  ["pentecostalis", "<big>pentecostalis</big>\n\n<b>pentecostalis, e</b> [pentecoste]\nсвязанный с пятидесятницей (festum <i>Tert</i>)."],
  ["pentecoste", "<big>pentecoste</big>\n\n<b>pentecoste, es</b> <i>f</i> (<i>греч.</i>)\nпятидесятница <i>Eccl</i>."],
  ["Pentelensis", "<big>Pentelensis</big>\n\n<b>Pentelensis, e</b> <i>Vtr</i>\n= Pentelicus."],
  ["Pentelicus", "<big>Pentelicus</big>\n\n<b>Pentelicus, a, um</b>\nпентельский (<i>неправильно</i> пентеликонский): P. mons, <i>славившаяся своим мрамором гора в Аттике, к сев.-зап. от Афин</i> <i>C</i> || сделанный (высеченный) из пентельского мрамора (Hermae <i>C</i>)."],
  ["penteloris", "<big>penteloris</big>\n\n<b>penteloris, e</b> [<i>греч.</i> pente + lorum]\nимеющий пять полос (vestis <i>Vop</i>)."],
  ["penteremis", "<big>penteremis</big>\n\n<b>pente–remis, e</b> <i>f</i> [<i>греч.</i> pente + remus] <i>Is</i>\n= penteris."],
  ["penteris", "<big>penteris</big>\n\n<b>penteris, is</b> <i>и</i> <b>idis</b> <i>f</i> (<i>греч.</i>)\nпентера, <i>судно с пятью рядами вёсел</i> <i>bAfr, bAl</i>."],
  ["pentethronicus", "<big>pentethronicus</big>\n\n<b>pentethronicus, a, um</b>\n<i>ничего не значащее слово у Плавта, выдуманное хвастуном-солдатом, чтобы ошеломить своего собеседника</i> (pentethronica pugna <i>Poen. 471</i>)."],
  ["Pentheius", "<big>Pentheius</big>\n\n<b>Pentheius, a, um</b> <i>Sid</i>\n= Pentheus II."],
  ["penthemimeres", "<big>penthemimeres</big>\n\n<b>penthemimeres, is</b> (<i>acc.</i> en) <i>f</i> (<i>греч.</i> «состоящий из 2 1/2 частей»)\nпентемимерес, <i>цезура после первой половины третьей стопы</i> (<i>в гексаметре и в ямбическом триметре</i>) <i>Aus</i>."],
  ["Penthesilea", "<big>Penthesilea</big>\n\n<b>Penthesilea, ae</b> <i>f</i>\nПентесилея, <i>дочь Марса, царица амазонок, союзница троянцев, побеждённая и убитая Ахиллом</i> <i>Prp, V, Just</i>."],
  ["Pentheus", "<big>Pentheus</big>\n\n<b>I Penthe͡us, eī</b> <i>и</i> <b>eos</b> <i>m</i>\nПентей, <i>сын Эхиона и Агавы, внук Кадма, царь Фив, растерзанный своей матерью и сестрами за то, что он противился поклонению Вакху</i> <i>O, H, Prp</i>.\n\n<b>II Penthēus, a, um</b> [Pentheus I]\nпентеев <i>O, St</i>."],
  ["penthiacum", "<big>penthiacum</big>\n\n<b>penthiacum, i</b> <i>n</i> [Pentheus]\nблюдо из мелко нарезанного мяса, род фрикассе <i>или</i> рагу <i>Pt</i>."],
  ["Penthides", "<big>Penthides</big>\n\n<b>Penthides, ae</b> <i>m</i>\nПентид, потомок Пентея, <i>т. е.</i> Ликург <i>O</i>."],
  ["Pentri", "<big>Pentri</big>\n\n<b>Pentri, orum</b> <i>m</i>\nпентры, <i>самнитское племя</i> (<i>с главн. городом Bovianum</i>) <i>L</i>."],
  ["penuarius", "<big>penuarius</big>\n\n<b>penuārius, a, um</b> <i>Dig</i>\n= penarius."],
  ["penul-", "<big>penul-</big>\n\n<b>pēnul-</b> <i>v. l.</i>\n= paenul-."],
  ["penum", "<big>penum</big>\n\n<b>penum, ī</b> <i>n</i> <i>Lampr</i>\n= penus II."],
  ["penuria", "<big>penuria</big>\n\n<b>pēnūria, ae</b> <i>f</i>\nнедостаток, нехватка (aquarum <i>Sl</i>; sapientium civium <i>C</i>): p. edendi <i>V</i> голодание; non est p. parvi <i>Lcr</i> в малом, <i>т. е.</i> в самом необходимом, недостатка нет."],
  ["penus", "<big>penus</big>\n\n<b>I penus, oris</b> <i>n</i> <i>Dig</i>\n= penus II.\n\n<b>II penus, ūs</b> <i>и</i> <b>ī</b> <i>m, f</i>\n<b>1)</b> продовольственные запасы, съестные припасы <i>Pl, Ter, C etc.</i>;\n<b>2)</b> кладовая (in locuplete penu <i>Pers</i>);\n<b>3)</b> внутреннее святилище храма <i>Lampr</i>."],
  ["Peparethius", "<big>Peparethius</big>\n\n<b>Peparethius, a, um</b> [Peparethus]\nпепаретский (vinum <i>PM</i>)."],
  ["Peparethus", "<big>Peparethus</big>\n\n<b>Peparethus (-os), i</b> <i>f</i>\nПепаретос, <i>островок к востоку от полуострова Магнесии</i> (ferax olivae <i>O</i>)."],
  ["pepedi", "<big>pepedi</big>\n\n<b>pepedi</b> <i>pf. к</i>\npedo II."],
  ["pependi", "<big>pependi</big>\n\n<b>pependi</b> <i>pf. к</i>\n<b>1)</b> pendeo <i>и</i>\n<b>2)</b> pendo."],
  ["peperci", "<big>peperci</big>\n\n<b>peperci</b> <i>pf. к</i>\nparco."],
  ["peperi", "<big>peperi</big>\n\n<b>peperi</b> <i>pf. к</i>\npario I."],
  ["pepigi", "<big>pepigi</big>\n\n<b>pepigi</b> <i>pf. к</i>\npango."],
  ["peplis", "<big>peplis</big>\n\n<b>peplis, idis</b> (<i>acc.</i> in) <i>f</i> (<i>греч.</i>)\n<i>разновидность молочая</i> (<i>Euphorbia peplis</i>, L.) <i>PM</i>."],
  ["peplum", "<big>peplum</big>\n\n<b>peplum, i</b> <i>n</i> <i>и</i> <b>peplus, i</b> <i>m</i> (<i>греч.</i>)\nпеплум:\n<b>1)</b> <i>роскошное просторное женское платье из тончайшей ткани; одеяние Афины, которое в праздник Панафиней в Афинах выставлялось для всеобщего обозрения</i> <i>Pl, V, St</i>;\n<b>2)</b> <i>роскошная парадная мужская одежда, мантия</i> (p. imperatorium <i>Treb</i>);\n<b>3)</b> <i>плащ</i> (pepla lanea <i>Sol</i>)."],
  ["pepo", "<big>pepo</big>\n\n<b>pepo, onis</b> <i>m</i> (<i>греч.</i>) <i>предпол.</i>\nтыква <i>PM, Tert</i>."],
  ["peposci", "<big>peposci</big>\n\n<b>peposci</b> <i>арх. AG</i>\n= poposci."],
  ["pepticus", "<big>pepticus</big>\n\n<b>pepticus, a, um</b> (<i>греч.</i>)\nспособствующий пищеварению (medicina <i>PM</i>)."],
  ["pepugi", "<big>pepugi</big>\n\n<b>pepugi</b> (= pupugi) <i>арх. pf. к</i>\npungo."],
  ["pepuli", "<big>pepuli</big>\n\n<b>pepuli</b> <i>pf. к</i>\npello."],
  ["per", "<big>per</big>\n\n<b>I per</b> <i>praep. cum acc.</i>\n<b>1)</b> <i>место</i>: через (p. urbem <i>Su</i>; p. Aeduorum fines <i>Cs</i>); сквозь, по, в (p. venas diffundi <i>C</i>; p. totam provinciam <i>Cs</i>; p. orbem terrarum <i>Sl</i> <i>и</i> p. terras <i>V</i>); вдоль (p. flumina <i>PM</i>); вниз по течению (p. amnem <i>QC</i>); среди, посреди (ire p. feras <i>O</i>); перед (p. ora vestra <i>Sl</i>): p. gradus <i>L</i> со ступени на ступень, по ступеням; p. domos <i>L</i> (familias <i>C</i>) от дома к дому, по домам; p. manus tradere <i>Cs</i> передавать из рук в руки; p. omnia <i>L</i> во всех частях, всюду;\n<b>2)</b> <i>время</i>: в течение, в продолжение, на протяжении (p. multos annos <i>C</i>; p. duas noctes <i>C</i>; p. otium <i>C, Just</i>); во время (p. somnium <i>C</i>; p. eos dies <i>C</i>; p. indutias <i>L</i>): p. tempus advenire <i>Ter</i> приходить вовремя (кстати); p. omne tempus <i>PJ</i> в течение всего времени;\n<b>3)</b> через посредство, с помощью (cognoscere aliquid p. exploratores <i>Cs</i>; occidi p. aliquem <i>C</i>); посредством (aliquem certiorem facere p. litteras <i>C</i>; decipere aliquem p. aliquid <i>C</i>): p. se <i>C</i> самостоятельно, по своей воле, сам по себе (nihil p. se audere <i>Cs</i>) <i>или</i> ради самого себя (virtus p. se expetenda est <i>C</i>) <i>или</i> лично (aliquem p. se aut p. alios sollicitare <i>Sl</i>); p. quos et a quibus? <i>C</i> по чьему наущению и кем (именно);\n<b>4)</b> <i>при клятвах и просьбах</i>: ради, именем (p. deos aliquem orare <i>Ter</i>): p. deos jurare <i>C</i> клясться богами; p. aliquid orare aliquem <i>V</i> заклинать кого-л. чём-л.; p. deos! <i>C</i> ради (самих) богов!;\n<b>5)</b> из-за, по причине, вследствие (p. metum <i>L</i>; p. ambitionem <i>Sl</i>; p. vinum <i>C</i>), благодаря: p. haec благодаря этому <i>Eutr</i>, <i>но тж.</i> тем временем <i>Su</i>; p. causam alicujus rei <i>C</i> (якобы) по причине чего-л. || ввиду, в силу (hoc p. leges non licet <i>C</i>; aliquid p. valetudinem facere non posse <i>C</i>): p. senatum <i>C</i> ввиду (препятствий со стороны) сената; p. me <i>C etc.</i> по мне (пусть); non stat p. me (quominus) <i>Ter</i> не моя вина (что не);\n<b>6)</b> под предлогом, под видом, прикрываясь (p. fidem fallere aliquem <i>Pl, C</i>; p. hospitium exhaurire domum <i>C</i>);\n<b>7)</b> путём, в порядке, в виде (p. fraudem <i>Su</i>): p. jocum <i>L</i> в шутку; p. ludibrium <i>L</i> издевательски; p. litteras <i>C</i> письменно; p. vim et metum <i>C</i> насилием и угрозами; p. manus <i>Cs</i> на руках, вручную, <i>но тж.</i> <i>Sl</i> силой, насильно; p. ludum et neglegentiam <i>C</i> играючи и без забот; p. speciem <i>L</i> (p. causam <i>Cs</i>) alicujus rei под видом <i>или</i> под предлогом чего-л.; p. iram <i>C</i> в гневе; p. artem <i>V</i> искусно; p. errorem <i>Su</i> по ошибке; p. lacrimas <i>VF</i> со слезами; p. amicitiam <i>Sl</i> во имя дружбы; p. virtutem <i>Sl</i> доблестно, мужественно; p. occasionem <i>L</i> при случае; p. nostram ignominiam <i>L</i> к нашему стыду; p. summum dedecus <i>C</i> позорнейшим образом; p. commodum <i>L</i> с удобством, для удобства.\n\n<b>II per-</b>\n<i>приставка, означающая</i>:\n<b>1)</b> <i>усиление</i> (per–altus);\n<b>2)</b> <i>завершение</i> (per–ficio);\n<b>3)</b> <i>действие, направленное через (сквозь) что-л., по чему-л. и т. д.</i> (per–egrinus)."],
  ["pera", "<big>pera</big>\n\n<b>pera, ae</b> <i>f</i> (<i>греч.</i>)\nранец, сума (p. et baculum <i>Ap, Aus</i>)."],
  ["perabsurdus", "<big>perabsurdus</big>\n\n<b>per–absurdus, a, um</b>\nкрайне нелепый, в высшей степени глупый <i>C</i>."],
  ["peraccomodatus", "<big>peraccomodatus</big>\n\n<b>per–accomodatus, a, um</b>\nвесьма удобный, вполне подходящий <i>C</i> (<i>in tmesi</i>)."],
  ["peracer", "<big>peracer</big>\n\n<b>per–acer, acris, acre</b>\nочень острый, едкий (acetum <i>Pl</i> — <i>v. l.</i>); весьма резкий (judicium <i>C</i>)."],
  ["peracerbus", "<big>peracerbus</big>\n\n<b>per–acerbus, a, um</b>\n<b>1)</b> очень терпкий (uva <i>C</i>);\n<b>2)</b> крайне неприятный <i>PJ</i>."],
  ["peracesco", "<big>peracesco</big>\n\n<b>per–acesco, acui, —, ere</b>\nсовершенно прокисать, <i>перен.</i> огорчаться, досадовать (pectus peracescit <i>Pl</i>)."],
  ["peractio", "<big>peractio</big>\n\n<b>peractio, onis</b> <i>f</i> [perago]\nзавершение, окончание (fabulae <i>C</i>)."],
  ["peractus", "<big>peractus</big>\n\n<b>peractus, a, um</b> <i>part. pf. к</i>\nperago."],
  ["peracute", "<big>peracute</big>\n\n<b>peracute</b> [peracutus]\nочень остро, <i>перен.</i> весьма остроумно <i>C, Ap</i>."],
  ["peracutus", "<big>peracutus</big>\n\n<b>per–acutus, a, um</b>\n<b>1)</b> очень острый, <i>перен.</i> весьма резкий, пронзительный (vox <i>C</i>);\n<b>2)</b> <i>перен.</i> очень остроумный (oratio <i>C</i>)."],
  ["peradolescens", "<big>peradolescens</big>\n\n<b>per–adolescens, entis</b> <i>adj.</i>\nочень молодой, молоденький <i>C</i>."],
  ["peradolescentulus", "<big>peradolescentulus</big>\n\n<b>per–adolescentulus, i</b> <i>m</i>\nсовсем молодой человек <i>Nep</i>."],
  ["peradpositus", "<big>peradpositus</big>\n\n<b>peradpositus, a, um</b> <i>v. l.</i>\n= perappositus."],
  ["peradul-", "<big>peradul-</big>\n\n<b>peradul-</b> <i>v. l.</i>\n= peradol-."],
  ["Peraea", "<big>Peraea</big>\n\n<b>Peraea, ae</b> <i>f</i>\nПерея :\n<b>1)</b> <i>область Палестины по левому берегу Иордана с главн. городом Радара</i> <i>PM</i>;\n<b>2)</b> (P. Rhodiorum) <i>южное побережье Карий против о-ва Родос</i> <i>L</i>."],
  ["peraedifico", "<big>peraedifico</big>\n\n<b>per–aedifico, —, atum, are</b>\nзавершать постройку, достраивать (domum <i>Col</i>; templum <i>Vlg</i>)."],
  ["peraequatio", "<big>peraequatio</big>\n\n<b>peraequatio, onis</b> <i>f</i> [peraequo]\n<b>1)</b> полное уравнение, выравнивание <i>Sol</i>;\n<b>2)</b> развёрстка налогов <i>CTh, CJ</i>."],
  ["peraequator", "<big>peraequator</big>\n\n<b>peraequator, oris</b> <i>m</i> [peraequo]\nраспределитель налогов <i>CJ</i>."],
  ["peraeque", "<big>peraeque</big>\n\n<b>per–aeque</b> <i>adv.</i>\nсовершенно равно, вполне одинаково, так же точно (aliquid in aliqua re reperire <i>C</i>); совершенно, равномерно (p. in singulos menses <i>Nep</i>)."],
  ["peraequo", "<big>peraequo</big>\n\n<b>per–aequo, avi, atum, are</b>\nсовершенно уравнивать (partes <i>Vtr</i>): p. singulos culleos <i>Col</i> давать по одному culleus (вина)."],
  ["peraestimo", "<big>peraestimo</big>\n\n<b>per–aestimo, —, —, are</b>\nвысоко ценить (aliquid <i>CTh</i>)."],
  ["peragito", "<big>peragito</big>\n\n<b>per–agito, āvī, ātum, āre</b>\n<b>1)</b> гнать, теснить, сильно тревожить (peragitari ab equitatu <i>Cs</i>);\n<b>2)</b> побуждать, возбуждать (audaces <i>Sen</i>);\n<b>3)</b> взбалтывать, перемешивать (mustum <i>Col</i>);\n<b>4)</b> проделывать, заканчивать (peragitatur enim messis mense Maio <i>PM</i>)."],
  ["perago", "<big>perago</big>\n\n<b>per–ago, ēgī, āctum, ere</b>\n<b>1)</b> гнать (asilus pecora peragens <i>Sen</i>);\n<b>2)</b> проделывать, совершать (navigationem <i>Cs</i>); проходить (iter <i>VF</i>): quem dederat cursum fortuna, peregi <i>V</i> какой путь указала судьба, такой я и прошла (<i>слова Дидоны</i>) || проплывать (freta <i>O</i>); проводить (comitia <i>C</i>; conventus <i>Cs</i>); преодолевать, выдерживать, переносить (fortunam <i>V</i>); исполнять, завершать, заканчивать (inceptum consulatum <i>C</i>); прожить (aetatem <i>O</i>; vitam <i>O</i>): quum peracta fata sint <i>Aus</i> когда жизнь окончилась || провести (noctem <i>QC</i>): p. otia <i>O</i> мирно проводить время; diebus tribus peractis <i>L</i> по прошествии трёх дней; mors peracta <i>PJ, Sil</i> наступившая смерть; multum egerunt, qui ante nos fuerunt, sed non peregerunt <i>Sen</i> те, кто жили до нас, много свершили, но (ничего) не завершили; hibernis peractis <i>Cs</i> по окончании зимнего периода;\n<b>3)</b> сыграть (fabulam <i>C</i>; partes suas <i>PJ</i>);\n<b>4)</b> довести до конца (inceptum <i>V, L</i>; accusationem <i>PJ</i>);\n<b>5)</b> подвести под обвинительный приговор, осудить (reum <i>L, O</i>; aliquis peractus non est ob mortem <i>T</i>);\n<b>6)</b> разрыхлять, обрабатывать (humum <i>O</i>);\n<b>7)</b> пронзать (latus ense <i>O</i>); умерщвлять (jam peractus est <i>M</i>);\n<b>8)</b> раздавать, распределять (dona <i>V</i>);\n<b>9)</b> излагать, выражать, формулировать (sententiam <i>L</i>; jus jurandum <i>L</i>); описывать (res gestas <i>L</i>);\n<b>10)</b> переваривать (cibum <i>PM</i>)."],
  ["peragranter", "<big>peragranter</big>\n\n<b>peragranter</b>\nпроездом, во время странствий <i>Amm</i>."],
  ["peragratio", "<big>peragratio</big>\n\n<b>peragrātio, ōnis</b> <i>f</i>\nпрохождение (itinerum <i>C</i>)."],
  ["peragratus", "<big>peragratus</big>\n\n<b>peragrātus, a, um</b> <i>part. pf. к</i>\nperagro; (<i>иногда</i> <i>act.</i>) прошедший, объехавший (omnes partes Germaniae <i>VP</i>)."],
  ["peragro", "<big>peragro</big>\n\n<b>peragro, āvī, ātum, āre</b> [per + ager]\n<b>1)</b> проходить, обходить, объезжать (omnes provincias <i>C</i>);\n<b>2)</b> <i>перен.</i> рассматривать, исследовать (omnes latebras <i>C</i>; rerum naturam <i>Sen</i>);\n<b>3)</b> глубоко проникать (per animos hominum <i>C</i>): fama peragravit <i>C</i> распространился (разнёсся) слух."],
  ["peralbus", "<big>peralbus</big>\n\n<b>per–albus, a, um</b>\nбелоснежный (equus <i>Ap</i>)."],
  ["peraltus", "<big>peraltus</big>\n\n<b>per–altus, a, um</b>\nочень высокий (ripae <i>L</i> —<i>v. l.</i>)."],
  ["peramans", "<big>peramans</big>\n\n<b>per–amans, antis</b> <i>adj.</i>\nнежно любящий: homo p. semper nostri fuit <i>C</i> он всегда нежно любил меня (был очень предан мне)."],
  ["peramanter", "<big>peramanter</big>\n\n<b>per–amanter</b>\nс большой любовью <i>или</i> весьма любезно <i>C</i>."],
  ["peramarus", "<big>peramarus</big>\n\n<b>per–amarus, a, um</b>\nчрезвычайно горький, полный горечи, ужасный (mors <i>Aug</i>)."],
  ["perambulo", "<big>perambulo</big>\n\n<b>per–ambulo, avi, atum, are</b>\n<b>1)</b> проходить, проезжать, объезжать (rura <i>H</i>): fuga p. <i>V</i> пробегать, проноситься; crocum floresque p. <i>погов. H</i> проходить по дороге, устланной шафраном и цветами, <i>т. е.</i> иметь шумный успех || <i>о врачебных визитах</i> обходить, посещать (<i>sc.</i> aegros <i>Sen</i>);\n<b>2)</b> проникать, пронизывать (frigus perambulat artus <i>O</i>)."],
  ["peramicus", "<big>peramicus</big>\n\n<b>per–amicus, a, um</b>\nнаходящийся в весьма дружественных отношениях <i>C</i> (<i>v. l.</i>)."],
  ["peramoenus", "<big>peramoenus</big>\n\n<b>per–amoenus, a, um</b>\nвесьма приятный, прелестный, чудесный (aestas <i>T</i>)."],
  ["peramplus", "<big>peramplus</big>\n\n<b>per–amplus, a, um</b>\nвесьма большой (simulacrum <i>C</i>); очень обширный, огромный (regnum <i>VM</i>)."],
  ["peranceps", "<big>peranceps</big>\n\n<b>per–anceps, cipitis</b>\nкрайне сомнительный, весьма недостоверный (labor <i>Amm</i>)."],
  ["peranguste", "<big>peranguste</big>\n\n<b>peranguste</b> [perangustus]\nочень тесно <i>C</i>."],
  ["perangustus", "<big>perangustus</big>\n\n<b>per–angustus, a, um</b>\nочень узкий, очень тесный (fretum <i>L</i>; via <i>L</i>)."],
  ["peranno", "<big>peranno</big>\n\n<b>peranno, avi, atum, are</b> [per + annus]\nпрожить год: non p. <i>Su</i> не прожить и года."],
  ["perantiquus", "<big>perantiquus</big>\n\n<b>per–antiquus, a, um</b>\nочень старый, древний (simulacrum, sacrarium <i>C</i>)."],
  ["perappositus", "<big>perappositus</big>\n\n<b>per–appositus (peradpositus), a, um</b>\nочень удобный, весьма подходящий (alicui <i>C</i>)."],
  ["perarduus", "<big>perarduus</big>\n\n<b>per–arduus, a, um</b>\nочень трудный <i>C</i>."],
  ["peraresco", "<big>peraresco</big>\n\n<b>per–aresco, arui, —, ere</b>\nсовершенно высыхать <i>Vr, Col</i>."],
  ["perargutus", "<big>perargutus</big>\n\n<b>per–argutus, a, um</b>\n<b>1)</b> весьма пронзительный (tintinnabulum <i>Ap</i>; carmen hirundinum <i>Ap</i>);\n<b>2)</b> очень остроумный, проницательный (homo <i>C</i>)."],
  ["peraridus", "<big>peraridus</big>\n\n<b>per–aridus, a, um</b>\nсильно высохший, очень сухой (frons populnea <i>Cato</i>; solum <i>Col</i>)."],
  ["perarmatus", "<big>perarmatus</big>\n\n<b>per–armatus, a, um</b>\nхорошо вооружённый (exercitus <i>QC</i>)."],
  ["peraro", "<big>peraro</big>\n\n<b>per–aro, avi, atum, are</b>\n<b>1)</b> вспахивать, <i>перен.</i> бороздить (pontum <i>SenT</i>; ora rugis <i>O</i>);\n<b>2)</b> чертить, писать (subito perarata littera <i>O</i>);\n<b>3)</b> исписывать, покрывать письменами (tabellas <i>O</i>)."],
  ["perasper", "<big>perasper</big>\n\n<b>per–asper, era, erum</b>\nочень грубый, жёсткий <i>или</i> шероховатый (corpus <i>CC</i>)."],
  ["perastutulus", "<big>perastutulus</big>\n\n<b>per–astutulus, a, um</b>\nс большой хитрецой, очень хитрый (ad aliquid <i>Ap</i>)."],
  ["peratim", "<big>peratim</big>\n\n<b>peratim</b> [pera]\n(целыми) сумками: p. ductare <i>Pl</i> (<i>v. l.</i>) красть деньги сумками."],
  ["perattente", "<big>perattente</big>\n\n<b>perattente</b> [perattentus]\nвесьма внимательно (ab aliquo audiri <i>C</i>)."],
  ["perattentus", "<big>perattentus</big>\n\n<b>per–attentus, a, um</b>\nвесьма внимательный (animus <i>C</i>)."],
  ["peratus", "<big>peratus</big>\n\n<b>peratus, a, um</b> [pera]\nснабжённый сумой <i>Pl</i>."],
  ["perbacchor", "<big>perbacchor</big>\n\n<b>per–bacchor, atus sum, ari</b> <i>depon.</i>\nпогулять, пропировать (multos dies <i>C</i>): perbacchata domos incendia <i>Cld</i> пробушевавшие по домам пожары."],
  ["perbasio", "<big>perbasio</big>\n\n<b>per–basio, —, —, are</b>\nрасцеловать (aliquem <i>Pt</i>)."],
  ["perbeatus", "<big>perbeatus</big>\n\n<b>per–beatus, a, um</b>\nвесьма счастливый <i>C</i>."],
  ["perbelle", "<big>perbelle</big>\n\n<b>per–belle</b>\nпрекрасно, отлично <i>C</i>."],
  ["perbene", "<big>perbene</big>\n\n<b>per–bene</b>\nочень хорошо, превосходно (Latine loqui <i>C</i>; prandere <i>Pl</i>)."],
  ["perbenevolus", "<big>perbenevolus</big>\n\n<b>per–benevolus, a, um</b>\nвесьма благожелательный (alicui <i>C</i>)."],
  ["perbenigne", "<big>perbenigne</big>\n\n<b>per–benigne</b>\nочень милостиво, благосклонно <i>Ter, C</i> — <i>in tmesi</i>."],
  ["Perbibesia", "<big>Perbibesia</big>\n\n<b>Perbibesia, ae</b> <i>f</i> [perbibo] <i>шутл.</i>\nстрана «Выпивохия» <i>Pl</i>."],
  ["perbibo", "<big>perbibo</big>\n\n<b>per–bibo, bibi, —, ere</b>\n<b>1)</b> выпивать, высасывать (medullam <i>Pl</i>); впитывать, вбирать в себя (lacrimas <i>O</i>; colores <i>Sen</i>);\n<b>2)</b> <i>перен.</i> впивать, усваивать (Latinum sermonem <i>Q</i>)."],
  ["perbito", "<big>perbito</big>\n\n<b>per–bito, —, —, ere</b>\n<b>1)</b> уходить, убираться (in Siciliam <i>Pl</i>);\n<b>2)</b> погибать, пропадать (fame <i>LA</i>; cruciatu <i>Pl</i>)."],
  ["perblande", "<big>perblande</big>\n\n<b>per–blande</b>\nвесьма ласково (aliquem salutare <i>Macr</i>)."],
  ["perblandus", "<big>perblandus</big>\n\n<b>per–blandus, a, um</b>\nочень ласковый, обходительный (successor <i>C</i>; oratio <i>L</i>)."],
  ["perbonus", "<big>perbonus</big>\n\n<b>per–bonus, a, um</b>\nочень хороший (ager <i>C</i>; prandium <i>Pl</i>; cella <i>Pt</i>)."],
  ["perbrevis", "<big>perbrevis</big>\n\n<b>per–brevis, e</b>\nочень короткий (aevum <i>L</i>; perbrevi tempore <i>C</i>)."],
  ["perbreviter", "<big>perbreviter</big>\n\n<b>per–breviter</b>\nочень кратко (aliquid exponere <i>C</i>)."],
  ["perca", "<big>perca</big>\n\n<b>perca, ae</b> <i>f</i>\nокунь <i>O, PM, Aus</i>."],
  ["percaedo", "<big>percaedo</big>\n\n<b>per–caedo, cidi, —, ere</b>\nперебить, уничтожить (exercitum <i>Fl</i> — <i>v. l.</i>)."],
  ["percalefacio", "<big>percalefacio</big>\n\n<b>per–calefacio, feci, factum, ere</b>\nсильно нагревать, прогревать (aliquid <i>Vtr</i>; motu percalefactus <i>Lcr</i>)."],
  ["percalefio", "<big>percalefio</big>\n\n<b>per–calefio, factus sum, fieri</b> <i>pass. к</i>\npercalefacio <i>Vr, VM, Vtr</i>."],
  ["percalesco", "<big>percalesco</big>\n\n<b>per–calesco, lui, —, ere</b>\nразогреваться, сильно нагреваться (ab igne solis <i>O</i>)."],
  ["percallesco", "<big>percallesco</big>\n\n<b>per–callesco, callui, —, ere</b>\n<b>1)</b> затвердевать, грубеть, <i>перен.</i> закаляться, привыкать (patientia percalluit <i>C</i>);\n<b>2)</b> приобретать опыт, знания, навыки (usu rerum <i>C</i>); хорошо усваивать, основательно изучать (quinque linguas <i>AG</i>)."],
  ["percandefacio", "<big>percandefacio</big>\n\n<b>per–candefacio, —, —, ere</b>\nсильно нагревать, накаливать (terram <i>Vtr</i>)."],
  ["percandidus", "<big>percandidus</big>\n\n<b>per–candidus, a, um</b>\nсовершенно белый (compositio <i>CC</i>; gemma <i>Sol</i>)."],
  ["percarus", "<big>percarus</big>\n\n<b>per–carus, a, um</b>\n<b>1)</b> очень дорогой, дорогостоящий (puella <i>Ter</i>);\n<b>2)</b> дорогой, милый, любезный (alicui <i>C, T, Just</i>)."],
  ["percautus", "<big>percautus</big>\n\n<b>per–cautus, a, um</b>\nвесьма осторожный <i>C</i>."],
  ["perceleber", "<big>perceleber</big>\n\n<b>per–celeber, bris, bre</b>\nвесьма известный, прославленный (gloria alicujus <i>PM</i>; templum <i>Mela</i>)."],
  ["percelebro", "<big>percelebro</big>\n\n<b>per–celebro, avi, atum, are</b>\nделать во множестве, нагромождать (mala <i>Eccl</i>); часто упоминать, постоянно говорить (rem, versum <i>C</i>): percelebrata sermonibus res est <i>C</i> об этом постоянно говорят."],
  ["perceler", "<big>perceler</big>\n\n<b>per–celer, is, e</b>\nочень быстрый, весьма скорый, стремительный <i>C, Amm</i>."],
  ["perceleriter", "<big>perceleriter</big>\n\n<b>perceleriter</b> [perceler]\nочень быстро, весьма скоро <i>C</i>."],
  ["percello", "<big>percello</big>\n\n<b>percello, culi, culsum, ere</b> [<i>одного корня с</i> procello]\n<b>1)</b> повергнуть наземь, опрокинуть (plaustrum oneratum <i>Cato</i>; aliquem <i>Ter, V, O</i>): plaustrum perculi <i>погов. Pl</i> мой воз свалился, <i>т. е.</i> плохо мне приходится;\n<b>2)</b> сильно ударить, толкнуть (alicui femur <i>L</i>; aliquem cuspide <i>O</i>);\n<b>3)</b> поразить (hostem <i>L</i>): perculsi tam inopinatae rei miraculo <i>Ap</i> поражённые чудом столь неожиданного события;\n<b>4)</b> разогнать, заставить разбежаться (quos pavor perculerat in silvas <i>L</i>);\n<b>5)</b> потрясти до основания, расшатать, погубить (imperium, potentiam <i>Nep</i>; rem publicam <i>T</i>);\n<b>6)</b> смутить, лишить бодрости, привести в замешательство (aliquem <i>C, L</i>; perculsus timore <i>C</i>);\n<b>7)</b> побуждать, подстрекать (aliquem ad turpitudinem <i>Ap</i>)."],
  ["percenseo", "<big>percenseo</big>\n\n<b>per–censeo, censui, —, ere</b>\n<b>1)</b> осматривать (manipulus <i>Vr</i>); обозревать, рассматривать, разбирать (aliquid subtili ingenio <i>AG</i>);\n<b>2)</b> подсчитывать (numerum legionum <i>T</i>);\n<b>3)</b> проходить, объезжать (Thessaliam <i>L</i>);\n<b>4)</b> перебирать, пересказывать (omnia maledicta <i>Ap</i>)."],
  ["percensio", "<big>percensio</big>\n\n<b>percensio, onis</b> <i>f</i>\n(о)смотр <i>Fronto</i>."],
  ["percepi", "<big>percepi</big>\n\n<b>percepi</b> <i>pf. к</i>\npercipio."],
  ["percepset", "<big>percepset</big>\n\n<b>percepset</b> <i>арх.</i> <i>Poeta ap. C</i>\n= percepisset (<i>ppf. conjct.</i>)."],
  ["perceptibilis", "<big>perceptibilis</big>\n\n<b>perceptibilis, e</b> [percipio]\nощутимый, познаваемый (species corporum <i>Aug</i>)."],
  ["perceptio", "<big>perceptio</big>\n\n<b>perceptio, onis</b> <i>f</i> [percipio]\n<b>1)</b> получение, собирание, сбор (frugum <i>C</i>; fructuum <i>Col</i>);\n<b>2)</b> понимание, постижение (veritatis <i>Aug</i>); познавание (cognitio aut p. <i>C</i>); восприятие, представление, понятие (animi perceptiones <i>C</i>)."],
  ["perceptor", "<big>perceptor</big>\n\n<b>perceptor, oris</b> <i>m</i> [perceptio]\nвоспринимающий, усваивающий (p. sapientiae <i>Aug</i>)."],
  ["perceptum", "<big>perceptum</big>\n\n<b>perceptum, i</b> <i>n</i> [percipio]\nосновное правило, основоположение (artis percepta <i>C</i>): pro percepto liquere <i>AG</i> приниматься за основу, считаться бесспорным."],
  ["perceptus", "<big>perceptus</big>\n\n<b>perceptus, a, um</b> <i>part. pf. к</i>\npercipio."],
  ["percido", "<big>percido</big>\n\n<b>per–cido, cidi, cisum, ere</b> [caedo]\n<b>1)</b> разбивать (os alicui <i>Pl</i>);\n<b>2)</b> <i>M</i> = paedico I."],
  ["percieo", "<big>percieo</big>\n\n<b>per–cieo, —, —, ere</b> <i>и</i> <b>per–cio, civi, citum, ire</b>\n<b>1)</b> приводить в движение, возбуждать (ira percitus <i>Pl</i>); раздражать (ingenium percitum ac ferox <i>L</i>);\n<b>2)</b> погонять, торопить (aliquem impudicum <i>Pl</i>). — <i>См. тж.</i> percitus."],
  ["percingo", "<big>percingo</big>\n\n<b>percingo, —, —, ere</b> <i>Col, Man</i>\n= praecingo."],
  ["percipio", "<big>percipio</big>\n\n<b>per–cipio, cepi, ceptum, ere</b> [capio]\n<b>1)</b> принимать в себя (semen <i>PM</i>; cibum <i>V</i>); приобретать (colorem <i>PM</i>);\n<b>2)</b> охватывать, овладевать (horror membra percipit <i>Pl</i>; saepe percipit humanos odium vitae <i>Lcr</i>);\n<b>3)</b> захватывать, забирать (auras <i>O</i>); вбирать (sucum <i>Col</i>);\n<b>4)</b> получать (praemia <i>Cs</i>; hereditatem <i>Su, Pt</i>); собирать (fructus <i>C</i>); взыскивать, взимать (vectigal <i>PM</i>);\n<b>5)</b> воспринимать, замечать: p. auribus <i>C</i> слышать; p. oculis <i>C</i> видеть; percipite, quae dicam <i>C</i> слушайте то, что я скажу;\n<b>6)</b> ощущать, чувствовать (dolorem, voluptatem <i>C</i>); понимать, усваивать, узнавать, познавать, постигать (aliquid animo <i>C</i>; nulla ars sine exercitatione percipi potest <i>C</i>); habeo aliquid perceptum <i>Nep</i> я понял (<i>или</i> знаю) что-л. — <i>См. тж.</i> perceptum."],
  ["percisus", "<big>percisus</big>\n\n<b>percisus, a, um</b> <i>part. pf. к</i>\npercido."],
  ["percitus", "<big>percitus</big>\n\n<b>percitus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> percio;\n<b>2.</b> <i>adj.</i> возбуждённый (urigine <i>Ap</i>); легко возбуждающийся, вспыльчивый, пылкий (ingenium <i>L</i>)."],
  ["percivilis", "<big>percivilis</big>\n\n<b>per–civilis, e</b>\nвесьма ласковый, любезный (sermo <i>Su</i>)."],
  ["perclamo", "<big>perclamo</big>\n\n<b>per–clamo, —, —, are</b>\nгромко кричать <i>Pl</i> (<i>v. l.</i>)."],
  ["perclaresco", "<big>perclaresco</big>\n\n<b>per–claresco, rui, —, ere</b>\nстановиться широко известным <i>Symm</i>."],
  ["percoctus", "<big>percoctus</big>\n\n<b>percoctus, a, um</b> <i>part. pf. к</i>\npercoquo."],
  ["percognosco", "<big>percognosco</big>\n\n<b>per–cognosco, (novi), nitum, ere</b>\nточно узнавать, подробно знакомиться (lex naturae non ignota, etsi nondum percognita <i>PM</i>)."],
  ["percolapo", "<big>percolapo</big>\n\n<b>percolapo</b> <i>v. l.</i>\n= percolopo."],
  ["percolatio", "<big>percolatio</big>\n\n<b>percolatio, onis</b> <i>f</i> (<i>тк.</i> <i>pl.</i>) [percolo I]\nпроцеживание (aquae <i>Vtr</i>)."],
  ["percolo", "<big>percolo</big>\n\n<b>I per–colo, avi, atum, are</b>\n<b>1)</b> процеживать, фильтровать (vinum <i>Cato</i>); <i>pass.</i> проходить, просачиваться (umor per terras percolatur <i>Lcr</i>);\n<b>2)</b> перерабатывать, переваривать (cibos et potiones <i>Sen</i>).\n\n<b>II per–colo, colui, cultum, ere</b>\n<b>1)</b> населять, обитать (Eleusiniam glebam <i>Ap</i>);\n<b>2)</b> тщательно чистить (os <i>Ap</i>);\n<b>3)</b> наряжать, разодевать (femina perculta <i>Pl</i>);\n<b>4)</b> завершать, оканчивать (inchoata <i>PJ</i>);\n<b>5)</b> заниматься, предаваться, развивать, культивировать, ревностно изучать (disciplinas studiosius <i>Ap</i>; p. sapientiam <i>Ap</i>);\n<b>6)</b> разукрашивать, украшать .(aliquid eloquentia <i>T</i>);\n<b>7)</b> чтить (deos <i>Sol</i>);\n<b>8)</b> почитать, уважать (patrem <i>Pl</i>);\n<b>9)</b> справлять, праздновать (initia Cereris <i>AV</i>);\n<b>10)</b> отличать, награждать, жаловать (aliquem aliquo honore <i>T</i>)."],
  ["percolopo", "<big>percolopo</big>\n\n<b>percolopo, —, —, are</b> [per + <i>греч.</i> colaphos]\nздорово отшлёпать (aliquem <i>Pt</i>)."],
  ["percomis", "<big>percomis</big>\n\n<b>per–comis, e</b>\nвесьма учтивый, дружелюбный, ласковый (homo <i>C</i>)."],
  ["percommode", "<big>percommode</big>\n\n<b>percommode</b> [percommodus]\nвесьма удобно (tumulus p. situs <i>L</i>); весьма удачно (dicere <i>PJ</i>): p. cadit (accidit <i>или</i> factum est), quod... <i>C</i> весьма кстати, что."],
  ["percommodus", "<big>percommodus</big>\n\n<b>per–commodus, a, um</b>\nвесьма удобный, очень подходящий (alicui rei <i>L</i>)."],
  ["perconor", "<big>perconor</big>\n\n<b>per–conor, —, ari</b> <i>depon.</i>\nосуществлять, завершать начатое <i>Sen</i>."],
  ["percontatio", "<big>percontatio</big>\n\n<b>percontatio, onis</b> <i>f</i> [percontor]\nспрашивание, расспрашивание <i>C etc.</i>; допрос (percontationem facere <i>L</i>)."],
  ["percontativus", "<big>percontativus</big>\n\n<b>percontativus, a, um</b>\nобщеизвестный <i>CA</i>."],
  ["percontator", "<big>percontator</big>\n\n<b>percontator, oris</b> <i>m</i> [percontor]\nдопрашивающий, любопытный, забрасывающий вопросами (percontatorem fugito <i>H</i>)."],
  ["perconto", "<big>perconto</big>\n\n<b>perconto, avi, atum, are</b> <i>Naev, Ap, AG</i>\n= percontor."],
  ["percontor", "<big>percontor</big>\n\n<b>percontor, atus sum, ari</b> <i>depon.</i> [per + contus]\nспрашивать, расспрашивать, осведомляться: p. aliquem (aliquid) <i>Ter, C etc.</i> расспрашивать о ком-л. (о чём-л.); p. ab (ex) aliquo de aliqua re <i>C</i> осведомляться у кого-л. о чём-л.; p. absurde quaedam <i>L</i> задавать глупые вопросы; quoniam percontanti nemo responderat <i>Ap</i> так как на его вопросы никто не отвечал."],
  ["percontumax", "<big>percontumax</big>\n\n<b>per–contumax, acis</b>\nочень упрямый <i>Ter</i>."],
  ["percopiosus", "<big>percopiosus</big>\n\n<b>per–copiosus, a, um</b>\nвесьма обильный, пространно говорящий, многословный (homo <i>PJ, Sid</i>)."],
  ["percoquo", "<big>percoquo</big>\n\n<b>per–coquo, coxi, coctum, ere</b>\n<b>1)</b> разваривать, варить до мягкости (carnem <i>PM</i>); <i>pass.</i> percoqui свариться <i>Pt</i>;\n<b>2)</b> печь, выпекать (placentam <i>Cato</i>; panem <i>Sen</i>);\n<b>3)</b> делать спелым, способствовать созреванию (sol percoquit fructus <i>Sen</i>);\n<b>4)</b> согревать, нагревать (umorem <i>Lcr</i>);\n<b>5)</b> обжигать, опалять, делать смуглым: viri percocto colore <i>Lcr</i> смуглые люди (мавры)."],
  ["Percosius", "<big>Percosius</big>\n\n<b>Percosius, a, um</b> [Percote]\nперкотийский: Percosia conjux <i>VF</i> = Clyte (<i>жена основателя и первого царя Кизика</i>)."],
  ["Percote", "<big>Percote</big>\n\n<b>Percote, es</b> <i>f</i>\nПеркота, <i>город в Троаде на побережье Мисии</i> <i>VF, PM</i>."],
  ["percrassus", "<big>percrassus</big>\n\n<b>per–crassus, a, um</b>\nочень густой, чрезвычайно вязкий (sanies <i>CC</i>)."],
  ["percrebesco", "<big>percrebesco</big>\n\n<b>per–crebesco</b> <i>v. l.</i>\n= percrebresco."],
  ["percrebresco", "<big>percrebresco</big>\n\n<b>per–crebresco, crebrui, —, ere</b>\n<b>1)</b> становиться всё более частым, учащаться (percrebruerunt conjugia, <i>sc.</i> lege prohibita <i>T</i>); распространяться (fama de aliqua re percrebruit <i>C</i>);\n<b>2)</b> становиться общеизвестным (scelus percrebruit <i>C</i>)."],
  ["percrepo", "<big>percrepo</big>\n\n<b>per–crepo, crepui, crepitum, are</b>\n<b>1)</b> громко звучать, оглашаться (locus percrepat mulierum vocibus <i>C</i>);\n<b>2)</b> громогласно возвещать (pugnam <i>LM</i>)."],
  ["percribro", "<big>percribro</big>\n\n<b>per–cribro, —, atum, are</b>\nпросеивать (contusus atque percribratus <i>Scr</i>)."],
  ["percrucio", "<big>percrucio</big>\n\n<b>per–crucio, —, —, are</b>\nмучить, терзать: hoc est, quod percrucior <i>Pl</i> это-то мне и больно."],
  ["percrudus", "<big>percrudus</big>\n\n<b>per–crudus, a, um</b>\n<b>1)</b> совершенно сырой (corium <i>Vtr</i>);\n<b>2)</b> совсем ещё незрелый (pruna <i>Col</i>)."],
  ["percudo", "<big>percudo</big>\n\n<b>per–cudo, cudi, —, ere</b>\nпробивать (pulli rostellis ova percudunt <i>Col</i>)."],
  ["perculi", "<big>perculi</big>\n\n<b>perculi</b> <i>pf. к</i>\npercello."],
  ["perculsus", "<big>perculsus</big>\n\n<b>I perculsus, a, um</b> <i>part. pf. к</i>\npercello.\n\n<b>II perculsus, (us) </b> <i>m</i>\nтолчок, сотрясение (p. intestinus <i>Tert</i>)."],
  ["percultor", "<big>percultor</big>\n\n<b>percultor, oris</b> <i>m</i> [percolo II]\nревностный почитатель, поклонник (doctorum <i>AV</i>)."],
  ["percunct-", "<big>percunct-</big>\n\n<b>percunct-</b> <i>v. l.</i>\n= percont-."],
  ["percunctatum", "<big>percunctatum</big>\n\n<b>percunctatum, i</b> <i>n</i>\nобщее положение, основная истина <i>CA</i>."],
  ["percupidus", "<big>percupidus</big>\n\n<b>per–cupidus, a, um</b>\nвесьма расположенный, очень любящий (alicujus <i>C</i>)."],
  ["percupio", "<big>percupio</big>\n\n<b>per–cupio, —, —, ere</b>\nочень хотеть, сильно желать (<i>sc.</i> domi opperiri <i>Ter</i>, aliquid sinceriter p. <i>AG</i>)."],
  ["percuriosus", "<big>percuriosus</big>\n\n<b>per–curiosus, a, um</b>\nвесьма любознательный, очень любопытный <i>C</i>."],
  ["percuro", "<big>percuro</big>\n\n<b>per–curo, avi, atum, are</b>\nсовершенно вылечить (vulnus percuratum <i>L</i>; mentem aegram <i>Sen</i>)."],
  ["percurro", "<big>percurro</big>\n\n<b>per–curro, cucurri (curri), cursum, ere</b>\n<b>1)</b> бежать, мчаться, спешить (rapido turbine campos <i>Lcr</i>; ad forum <i>Ter</i>; per omnes civitates <i>C</i>); <i>перен.</i> проходить, отслужить (quaesturam, praeturam, consulatum <i>Su</i>); пробегать, поспешно проходить, быстро проезжать (agrum <i>Cs</i>; percursum iter <i>Amm</i>);\n<b>2)</b> пронизывать (lumine nimbos <i>V</i>);\n<b>3)</b> облетать (animo polum <i>H</i>); бегло просматривать, пробегать (paginas <i>L</i>); бегло коснуться (multas res oratione <i>C</i>): metus pectora percurrit <i>QC</i> страх охватывает людей; p. suum ordinem <i>QC</i> пройти свой жизненный путь; p. oculo <i>H</i> бегло обозреть, окинуть глазом."],
  ["percursatio", "<big>percursatio</big>\n\n<b>percursatio, onis</b> <i>f</i> [percurso]\nобъезд, поездка: p. Italiae <i>C</i> поездка по Италии."],
  ["percursio", "<big>percursio</big>\n\n<b>percursio, onis</b> <i>f</i> [percurro]\nпробегание, <i>перен.</i> беглый обзор, упоминание вскользь <i>C</i>; быстрое обдумывание (multarum rerum <i>C</i>)."],
  ["percurso", "<big>percurso</big>\n\n<b>percurso, —, —, are</b> [<i>intens. к</i> percurro]\nбродить, рыскать (p. ripas <i>PJ</i>; p. finibus nostris <i>L</i>)."],
  ["percursus", "<big>percursus</big>\n\n<b>percursus, (us) </b> <i>m</i> [percurro]\nбыстрое прохождение, протекание (aquae <i>Vtr</i>)."],
  ["percussi", "<big>percussi</big>\n\n<b>percussi</b> <i>pf. к</i>\npercutio."],
  ["percussibilis", "<big>percussibilis</big>\n\n<b>percussibilis, e</b> [percutio]\nпронизывающий, <i>перен.</i> резкий, сильный (odor <i>CA</i>)."],
  ["percussio", "<big>percussio</big>\n\n<b>percussio, onis</b> <i>f</i> [percutio]\n<b>1)</b> нанесение ударов, удар(ы): p. capitis <i>C</i> удар в голову; p. digitorum <i>C</i> щёлканье пальцами;\n<b>2)</b> <i>перен.</i> отбивание такта (p. intervallum <i>C</i>); мерность, размеренность (sermonis <i>Q</i>)."],
  ["percussionalis", "<big>percussionalis</big>\n\n<b>percussionalis, e</b> [percutio]\nударный, перкуссионный (instrumentum musicum <i>Eccl</i>)."],
  ["percussor", "<big>percussor</big>\n\n<b>percussor, oris</b> <i>m</i> [percutio]\n<b>1)</b> нанёсший рану (vulneratus percussorem novit <i>PM</i>);\n<b>2)</b> убийца, разбойник <i>C, T, Pt</i>."],
  ["percussura", "<big>percussura</big>\n\n<b>percussura, ae</b> <i>f</i> [percutio]\nудар (ferro facta <i>Ap</i>)."],
  ["percussus", "<big>percussus</big>\n\n<b>I percussus, a, um</b> <i>part. pf. к</i>\npercutio.\n\n<b>II percussus, us</b> <i>m</i> <i>Sen, O</i>\n= percussio."],
  ["percusti", "<big>percusti</big>\n\n<b>percusti</b>\n<i>H</i> = percussisti (<i>pf. к</i> percutio)."],
  ["percutio", "<big>percutio</big>\n\n<b>percutio, cussi, cussum, ere</b> [per + quatio]\n<b>1)</b> пробивать (navem rostro <i>L</i>); пронзать, прокалывать (pectus gladio <i>L</i>; latus apri cultro <i>Pt</i>);\n<b>2)</b> прокапывать, прорывать (fossam <i>PJ</i>);\n<b>3)</b> перерезать, вскрывать (venam <i>Sen</i>);\n<b>4)</b> бить, ударять, поражать (aliquem lapide <i>C</i>): locum non p. <i>C</i> не попасть в точку, промахнуться; canere aliquid pede ter percusso <i>H</i> воспевать что-л. трёхударными стопами, <i>т. е.</i> триметрами;\n<b>5)</b> стучать (p. valvas triclinii <i>Pt</i>);\n<b>6)</b> чеканить (nummum <i>Su</i>; monetam <i>Vop</i>; <i>перен.</i> dicta una forma percussa <i>Sen</i>);\n<b>7)</b> жалить <i>или</i> кусать (a serpente ac scorpione percuti <i>PM</i>);\n<b>8)</b> опьянять (se meraco flore Liberi p. <i>Pl</i>);\n<b>9)</b> продевать через бёрдо, <i>т. е.</i> ткать (lacernae male percussae <i>J</i>);\n<b>10)</b> бряцать (p. nervos <i>Q</i>): p. lyram <i>O</i> играть на лире;\n<b>11)</b> взмахивать (pennas <i>O</i>); потрясать, колебать (muros arietum pulsu <i>C</i>);\n<b>12)</b> ранить (aliquem sagitta <i>QC</i>); уязвлять, болезненно задевать (percuti calamitate <i>C</i>); поражать, потрясать (aliquem metu <i>L</i>, dolore <i>VF</i>); (неприятно) резать (aures alicujus <i>Pt</i>);\n<b>13)</b> закалывать, убивать, умерщвлять (aliquem sica <i>C</i>); казнить, обезглавливать (aliquem securi <i>C</i>);\n<b>14)</b> торжественно заключать (<i>с закланием жертвенного животного</i>) (foedus <i>C, L, bAl</i>);\n<b>15)</b> обманывать, вводить в заблуждение (aliquem strategemate <i>C</i>)."],
  ["perdecorus", "<big>perdecorus</big>\n\n<b>per–decorus, a, um</b>\nвесьма приличный, прекрасный <i>PJ</i>."],
  ["perdeleo", "<big>perdeleo</big>\n\n<b>per–deleo, —, —, ere</b>\nсовершенно уничтожать (aliquid <i>Veg, Tert</i>)."],
  ["perdelirus", "<big>perdelirus</big>\n\n<b>per–delirus, a, um</b>\nсовершенно нелепый, бессмысленный <i>Lcr</i>."],
  ["perdensus", "<big>perdensus</big>\n\n<b>per–densus, a, um</b>\nочень плотный (humus <i>Col</i>)."],
  ["perdepso", "<big>perdepso</big>\n\n<b>per–depso, depsui, —, ere</b>\nосновательно мять (aliquam <i>Ctl</i>)."],
  ["perdicalis", "<big>perdicalis</big>\n\n<b>perdicalis, e</b> [perdix]\nкуропатковый (herba <i>Ap</i>)."],
  ["Perdiccas", "<big>Perdiccas</big>\n\n<b>Perdicca(s), ae</b> <i>m</i>\nПердикка, <i>имя ряда македонских царей</i> <i>QC, C</i>."],
  ["perdicis", "<big>perdicis</big>\n\n<b>perdicis</b> <i>gen. sg. к</i>\nperdix."],
  ["perdicium", "<big>perdicium</big>\n\n<b>perdicium, i</b> <i>n</i> (<i>греч.</i>) <i>бот.</i>\nстенница (<i>Parietaria officinalis</i>, L.) <i>PM</i>."],
  ["perdidi", "<big>perdidi</big>\n\n<b>perdidi</b> <i>pf. к</i>\nperdo."],
  ["perdidici", "<big>perdidici</big>\n\n<b>perdidici</b> <i>pf. к</i>\nperdisce."],
  ["perdifficilis", "<big>perdifficilis</big>\n\n<b>per–difficilis, e</b>\nочень трудный (navigatio, quaestio <i>C</i>; amnis transitu p. <i>L</i>)."],
  ["perdifficiliter", "<big>perdifficiliter</big>\n\n<b>perdifficiliter</b> [perdifficilis]\nс большим трудом (internoscere <i>C</i>)."],
  ["perdignus", "<big>perdignus</big>\n\n<b>per–dignus, a, um</b>\nвесьма достойный (aliqua re <i>C</i>)."],
  ["perdiligens", "<big>perdiligens</big>\n\n<b>per–diligens, entis</b> <i>adj.</i>\nвесьма тщательный, старательный: res est hominis perdiligentis <i>C</i> это требует большой аккуратности."],
  ["perdiligenter", "<big>perdiligenter</big>\n\n<b>perdiligenter</b> [perdiligens]\nвесьма тщательно, аккуратно, старательно (scribere <i>C</i>)."],
  ["perdisco", "<big>perdisco</big>\n\n<b>per–disco, didici, —, ere</b>\nосновательно изучить (ortus obitusque signorum <i>C</i>; disciplinam ruris <i>Col</i>); хорошо понять (perdidici istaec esse vera <i>Pl</i>)."],
  ["perdiserte", "<big>perdiserte</big>\n\n<b>per–diserte</b>\nвесьма красноречиво (rationem reddere <i>C</i>)."],
  ["perdite", "<big>perdite</big>\n\n<b>perdite</b> [perditus]\n<b>1)</b> крайне дурно, бесчестно (se gerere <i>C</i>; vivere <i>Aug</i>);\n<b>2)</b> свыше всякой меры, отчаянно (conari <i>Q</i>): amare aliquem p. <i>Ter</i> быть без ума от кого-л."],
  ["perditio", "<big>perditio</big>\n\n<b>perditio, onis</b> <i>f</i> [perdo]\n(по)гибель (via, quae ducit ad perditionem <i>Vlg</i>)."],
  ["perditor", "<big>perditor</big>\n\n<b>perditor, oris</b> <i>m</i> [perdo]\nгубитель, разоритель (rei publicae <i>C</i>)."],
  ["perditrix", "<big>perditrix</big>\n\n<b>perditrix, icis</b> <i>f</i>\nгубительница <i>Tert, Hier</i>."],
  ["perditus", "<big>perditus</big>\n\n<b>I perditus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> perdo;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> безнадёжный (aeger <i>O</i>); совершенно подорванный, расстроенный (valetudo <i>C</i>); погибший, несчастный (sum p. <i>Pl</i>);\n<b>2)</b> безмерный (amor <i>Ctl</i>): p. luctu <i>C</i> погружённый в глубокую скорбь; perditi animi esse <i>Pl</i> быть вне себя;\n<b>3)</b> пропащий, распутный, развращённый, бесчестный (homo <i>Ter, QC</i>; mores <i>QC</i>).\n\n<b>II perditus, us</b> <i>m</i>\nпотеря, утрата <i>Pl, Eccl</i>."],
  ["perdiu", "<big>perdiu</big>\n\n<b>per–diu</b>\nочень долго (exsistere <i>C</i>; stare <i>Ap</i>)."],
  ["perdius", "<big>perdius</big>\n\n<b>perdius, a, um</b> [per + dius = dies]\nдлящийся, стоящий, работающий и т. д. целый день <i>Ap, Amm etc.</i>: stare solitus Socrates dicitur pertinaci statu p. atque pernox <i>AG</i> говорят, что Сократ часто простаивал в одном и том же положении весь день и всю ночь (круглые сутки)."],
  ["perdiuturnus", "<big>perdiuturnus</big>\n\n<b>per–diuturnus, a, um</b>\nвесьма долгий, продолжительный, длительный, затяжной (bellum <i>C</i>)."],
  ["perdives", "<big>perdives</big>\n\n<b>per–dives, itis</b> <i>adj.</i>\nочень богатый <i>C</i>."],
  ["perdix", "<big>perdix</big>\n\n<b>perdix, icis</b> <i>m, f</i> (<i>греч.</i>)\nкуропатка <i>PM, M</i>."],
  ["perdo", "<big>perdo</big>\n\n<b>per–do, didi, ditum, ere</b>\n<b>1)</b> губить: illum di omnes perduint! <i>Ter</i> да погубят его все боги! || уничтожать, убивать (serpentem <i>O</i>);\n<b>2)</b> расстраивать, разрушать (civitatem, valetudinem, fruges <i>C</i>); портить (oculos assiduis lucubrationibus <i>C</i>); обездоливать, разорять (cives <i>C</i>); помрачать (aethera umbra <i>VF</i>);\n<b>3)</b> тратить, мотать (fortunas suas <i>Ter</i>);\n<b>4)</b> проигрывать (litem <i>C</i>; causam <i>C</i>; multum in alea <i>C</i>): ne perdi derit, non cessat p. lusor <i>O</i> (зарвавшийся) игрок не перестаёт проигрывать в надежде, что проигрывать не будет;\n<b>5)</b> терять (tempus <i>C</i>; spem <i>Pl, L</i>; liberos <i>C</i>); напрасно расточать (blanditias <i>O</i>): scapulas perdidisse <i>ирон. Ter</i> поплатиться спиной, <i>т. е.</i> быть избитым; p. oleum et operam <i>погов. C</i> = трудиться без пользы; Decius vitam non perdidit <i>rhH</i> Деций не без пользы пожертвовал жизнью; p. mortem <i>Sen, Sil, St</i> <i>или</i> letum <i>Lcn</i> погибнуть бесполезной смертью; lucrandi perdendive temeritas <i>T</i> азарт в игре;\n<b>6)</b> забывать (nomen <i>Ter</i>)."],
  ["perdoceo", "<big>perdoceo</big>\n\n<b>per–doceo, cui, ctum, ere</b>\nосновательно преподавать, хорошо учить (artes omnes <i>Pl</i>; res difficilis ad perdocendum <i>C</i>); ясно показывать, обучать (aliquem aliquid fieri <i>Lcr</i>)."],
  ["perdocilis", "<big>perdocilis</big>\n\n<b>per–docilis, e</b>\nвесьма восприимчивый (sensus <i>Jvc</i>)."],
  ["perdocte", "<big>perdocte</big>\n\n<b>perdocte</b> [perdoctus]\nвесьма учёно (искусно) (callere cuncta <i>Pl</i>)."],
  ["perdoctus", "<big>perdoctus</big>\n\n<b>per–doctus, a, um</b>\nвесьма учёный, искуснейший <i>Pl, Ter, Lcr, C</i>."],
  ["perdoleo", "<big>perdoleo</big>\n\n<b>per–doleo, lui, litum, ere</b>\nсильно болеть, <i>impers.</i>: tandem perdoluit <i>Ter</i> наконец задело за живое; perdolitum est Manlio <i>AG</i> Манлию было весьма больно (тяжело видеть)."],
  ["perdolesco", "<big>perdolesco</big>\n\n<b>perdolesco, dolui, —, ere</b> [perdoleo]\nболезненно воспринимать (aliquid irrisui fore <i>Cs</i>)."],
  ["perdolo", "<big>perdolo</big>\n\n<b>per–dolo, (avi), atum, are</b>\nтщательно обтёсывать, обрубать (arboris partem <i>Vtr</i>)."],
  ["perdominor", "<big>perdominor</big>\n\n<b>per–dominor, —, ari</b>\nпровластвовать (annum <i>Cld</i>)."],
  ["perdomo", "<big>perdomo</big>\n\n<b>per–domo, domui, domitum, are</b>\n<b>1)</b> совершенно приручать (tauros feroces <i>O</i>); укрощать (serpentes <i>O</i>); усмирять, подчинять, покорять, порабощать (Latium <i>L</i>; Judaeam <i>Su</i>);\n<b>2)</b> подавлять (malitiam <i>Ap</i>);\n<b>3)</b> возделывать, обрабатывать (solum <i>PJ</i>);\n<b>4)</b> замешивать (farinam aqua <i>Sen</i>);\n<b>5)</b> молоть (Ceres saxo perdomita <i>St</i>)."],
  ["perdormisco", "<big>perdormisco</big>\n\n<b>perdormisco, —, —, ere</b> [per + dormio]\nпроспать (usque ad lucem <i>Pl</i>)."],
  ["perduco", "<big>perduco</big>\n\n<b>per–duco, duxi, ductum, ere</b>\n<b>1)</b> приводить (aliquem in <i>или</i> ad senatum <i>Cs etc.</i>; legiones in Galliam <i>Cs</i>; in hunc locum <i>Pt</i>); отводить (bovem ad stabula <i>V</i>);\n<b>2)</b> доводить (aliquem ad furorem <i>C</i>; aliquid ad exitum <i>C</i>; aliquem ad honores <i>C</i>; ad ultimam perductus tristitiam <i>Pt</i>): en, quo discordia cives perduxit! <i>V</i> вот до чего довели граждан раздоры!;\n<b>3)</b> проводить, строить (fossam, murum <i>Cs</i>; viam <i>L</i>);\n<b>4)</b> продолжать, затягивать (orationes in noctem <i>L</i>);\n<b>5)</b> склонять (aliquem ad suam sententiam <i>C</i>; animum ad contemptionem animae <i>Sen</i>); побуждать (aliquem ad mendacium <i>C</i>); привлекать, переманивать, располагать (aliquem ad se pollicitationibus <i>Cs</i>);\n<b>6)</b> заманивать, завлекать (filia perducta per dolum atque insidias <i>C</i>);\n<b>7)</b> натирать, обмазывать (corpus aliqua re <i>V</i>);\n<b>8)</b> втягивать, пить (poculum continuo haustu <i>Ap</i>);\n<b>9)</b> вычёркивать (nomen <i>Dig</i>)."],
  ["perductio", "<big>perductio</big>\n\n<b>perductio, onis</b> <i>f</i> [perduco]\nпроведение (aquarum <i>Vtr</i>)."],
  ["perducto", "<big>perducto</big>\n\n<b>perducto, —, —, are</b> [<i>intens. к</i> perduco]\nводить <i>Pl</i>."],
  ["perductor", "<big>perductor</big>\n\n<b>perductor, oris</b> <i>m</i> [perduco]\n<b>1)</b> проводник <i>C</i>;\n<b>2)</b> сводник <i>C</i>."],
  ["perdudum", "<big>perdudum</big>\n\n<b>per–dudum</b> <i>adv.</i>\nочень давно: haud p. <i>Pl</i> не так уж давно."],
  ["perduellio", "<big>perduellio</big>\n\n<b>perduellio, ōnis</b> <i>f</i> [perduellis]\n<b>1)</b> враждебные (военные) действия: perduelliones <i>rhH</i> враги, неприятель;\n<b>2)</b> государственная измена, государственное преступление <i>C, L</i>."],
  ["perduellis", "<big>perduellis</big>\n\n<b>perduellis, is</b> <i>m</i> [per + duellum = bellum]\n<b>1)</b> неприятель, противник (<i>на войне</i>) <i>Pl, C, L, Vr, Dig</i>;\n<b>2)</b> личный враг <i>Pl</i>."],
  ["perduim", "<big>perduim</big>\n\n<b>perduim</b> <i>арх. Pl, Ter etc. praes. conjct. к</i>\nperdo."],
  ["perdulcis", "<big>perdulcis</big>\n\n<b>per–dulcis, e</b>\nочень сладкий <i>Lcr</i>."],
  ["perduro", "<big>perduro</big>\n\n<b>per–duro, avi, atum, are</b>\n<b>1)</b> выносить, переносить, выдерживать, (вы)терпеть (p. non posse <i>Su</i>);\n<b>2)</b> длиться, продолжаться (longum in aevum <i>O</i>; ad hoc genus hominum <i>Pl</i>);\n<b>3)</b> пребывать, находиться, оставаться (apud aliquem <i>Ter</i>; cotidie in templo <i>Vlg</i>)."],
  ["perdurus", "<big>perdurus</big>\n\n<b>per–durus, a, um</b>\nочень жёсткий, крайне суровый (interpretatio <i>Dig</i>)."],
  ["perduxi", "<big>perduxi</big>\n\n<b>perduxi</b> <i>pf. к</i>\nperduco."],
  ["Peredia", "<big>Peredia</big>\n\n<b>Peredia, ae</b> <i>f</i> [peredo I] <i>шутл.</i>\nстрана «Обжория» <i>Pl</i>."],
  ["peredo", "<big>peredo</big>\n\n<b>I per–edo, edi, esum, ere</b>\n<b>1)</b> съедать, пожирать (cibum <i>Pl</i>);\n<b>2)</b> разъедать, размывать (saxa <i>Tib</i>);\n<b>3)</b> иссушать (aliquem tabe <i>V</i>).\n\n<b>II per–edo, —, —, ere</b>\nиздать в свет (melos <i>Sid</i>)."],
  ["perefflo", "<big>perefflo</big>\n\n<b>per–efflo, —, —, are</b>\nцеликом выдохнуть, испустить (animam <i>Ap</i>)."],
  ["pereffluo", "<big>pereffluo</big>\n\n<b>per–effluo, —, —, ere</b>\nпротечь, <i>перен.</i> утратить воспоминание <i>Vlg</i>."],
  ["pereger", "<big>pereger</big>\n\n<b>per–eger, egris</b> <i>adj. m</i> [ager]\nуехавший, путешествующий <i>Aus</i>: p. factus <i>Dig</i> находящийся в отъезде."],
  ["peregi", "<big>peregi</big>\n\n<b>peregi</b> <i>pf. к</i>\nperago."],
  ["peregre", "<big>peregre</big>\n\n<b>peregre</b> [per + ager]\n<b>1)</b> вне дома, за границей, вне отечества, в чужих краях, на чужбине (domi et p. <i>Ter</i>; p. esse <i>Pl</i>, habitare <i>L</i>, depugnare <i>C</i>): p. esse быть чуждым миру (оторванным от практических интересов) (animus est p. <i>H</i>);\n<b>2)</b> за границу, в чужие края (proficisci <i>Pl</i>; exire <i>H</i>);\n<b>3)</b> из-за границы, из чужих краёв (nuntiare <i>L</i>; redire <i>Ter</i>)."],
  ["peregregius", "<big>peregregius</big>\n\n<b>per–egregius, a, um</b>\nпревосходный, замечательный (tragoedia <i>Ap</i>)."],
  ["peregri", "<big>peregri</big>\n\n<b>peregri</b> <i>Naev, Pl</i>\n= peregre 1."],
  ["peregrina", "<big>peregrina</big>\n\n<b>peregrina, ae</b> <i>f</i> [peregrinus]\nиностранка, чужеземка <i>Ter, C</i>."],
  ["peregrinabundus", "<big>peregrinabundus</big>\n\n<b>peregrinabundus, a, um</b> [peregrinor]\nпутешествующий за границей <i>L</i>."],
  ["peregrinatio", "<big>peregrinatio</big>\n\n<b>peregrinatio, onis</b> <i>f</i> [peregrinor]\nстранствование, путешествие <i>или</i> пребывание за границей (p. perpetua <i>C</i>; peregrinationes jucundae <i>Sen</i>)."],
  ["peregrinator", "<big>peregrinator</big>\n\n<b>peregrinator, oris</b> <i>m</i> [peregrinor]\nпутешественник, любитель путешествий <i>C, Ap</i>."],
  ["peregrinitas", "<big>peregrinitas</big>\n\n<b>peregrinitas, atis</b> <i>f</i> [peregrinus]\n<b>1)</b> чужеземные нравы и обычаи <i>C</i>; иностранщина, иностранное произношение <i>Q</i>;\n<b>2)</b> положение иностранца в Риме (aliquem in peregrinitatem redigere <i>Su</i>): peregrinitatis reus <i>Su</i> иностранец, обвиняемый в незаконном присвоении прав римского гражданина."],
  ["peregrinor", "<big>peregrinor</big>\n\n<b>peregrinor, atus sum, ari</b> <i>depon.</i> [peregrinus]\n<b>1)</b> жить на чужбине (p. in aliena civitate <i>C</i>); странствовать, путешествовать (longe lateque <i>C</i>);\n<b>2)</b> быть чужим, считаться иноземным (philosophia Romae peregrinatur <i>C</i>);\n<b>3)</b> <i>перен.</i> быть чуждым, незнакомым: peregrinatus est hujus animus in nequitia, non habitavit <i>VM</i> его (Полемона) характер (лишь) соприкоснулся с пороком, но не свыкся с ним;\n<b>4)</b> <i>перен.</i> отсутствовать, быть далеко, быть невнимательным (vestrae peregrinantur aures <i>C</i>)."],
  ["peregrinus", "<big>peregrinus</big>\n\n<b>I peregrinus, a, um</b> [peregre]\n<b>1)</b> иноземный, чужой (navis <i>Pl</i>; mores <i>L, J</i>): in loco peregrino <i>Pt</i> на чужбине; peregrina ratione <i>Pt</i> на иностранный манер || привозной, иностранной породы (bos <i>Col</i>); перелётный (volucris <i>Ph</i>): timor p. <i>L</i> страх перед внешним врагом; condicio peregrina <i>PM</i> правовое положение иностранца (<i>в Риме</i>); peregrina mors <i>C</i> смерть на чужбине; provincia peregrina (sors inter peregrinus) <i>L</i> должность претора, разбиравшего споры между иностранцами (praetor p. <i>Dig</i>);\n<b>2)</b> дорожный, путевой: peregrino labore fessus <i>Ctl</i> усталый от странствий;\n<b>3)</b> несведущий, неопытный (p. atque hospes in aliqua re <i>C</i>).\n\n<b>II peregrinus, a, um</b>\nчужеземец, иностранец (peregrini atque advenae <i>C</i>); иностранный резидент (<i>в Риме</i>) <i>C</i>."],
  ["perelegans", "<big>perelegans</big>\n\n<b>per–elegans, antis</b> <i>adj.</i>\nвесьма изящный, изысканный (oratio <i>C</i>; ingenium <i>VP</i>)."],
  ["pereleganter", "<big>pereleganter</big>\n\n<b>pereleganter</b> [perelegans]\nвесьма изящно, изысканно (dicere <i>C</i>)."],
  ["pereloquens", "<big>pereloquens</big>\n\n<b>per–eloquens, entis</b> <i>adj.</i>\nвесьма красноречивый <i>C</i>."],
  ["peremi", "<big>peremi</big>\n\n<b>peremi</b> <i>pf. к</i>\nperimo."],
  ["peremnis", "<big>peremnis</big>\n\n<b>peremnis, e</b> [per + amnis]\nкасающийся перехода через реку, относящийся к переправе: auspicia peremnia <i>C</i> ауспиции, устраивавшиеся перед переправой через реку (<i>источник которой считался священным</i>)."],
  ["peremo", "<big>peremo</big>\n\n<b>peremo</b> <i>Cato, PM, Ap</i>\n= perimo."],
  ["peremptalis", "<big>peremptalis</big>\n\n<b>peremptalis, e</b> [peremptus]\nотменяющий: fulmen peremptale <i>Sen</i> молния, отменяющая то, что было возвещено предшествующей молнией."],
  ["peremptio", "<big>peremptio</big>\n\n<b>peremptio, onis</b> <i>f</i> [perimo]\nуничтожение, умерщвление <i>Aug</i>."],
  ["peremptor", "<big>peremptor</big>\n\n<b>peremptor, oris</b> <i>m</i> [perimo]\nубийца <i>Sen, T, Ap</i>; <i>перен.</i> мучитель <i>Vlg, Eccl</i>."],
  ["peremptorius", "<big>peremptorius</big>\n\n<b>peremptorius, a, um</b> [peremptor]\n<b>1)</b> губительный, смертельный (venenum <i>Ap</i>; spiculum <i>Tert</i>);\n<b>2)</b> пресекающий все споры, <i>т. е.</i> решительный, окончательный (edictum <i>Dig</i>)."],
  ["peremptrix", "<big>peremptrix</big>\n\n<b>peremptrix, icis</b> <i>f</i>\nгубительница, кладущая конец (mors p. sensus <i>Tert</i>)."],
  ["peremptus", "<big>peremptus</big>\n\n<b>peremptus, a, um</b> <i>part. pf. к</i>\nperimo."],
  ["perendie", "<big>perendie</big>\n\n<b>perendie</b> <i>adv.</i>\nпослезавтра <i>Pl, C</i>."],
  ["perendinus", "<big>perendinus</big>\n\n<b>perendinus, a, um</b> [perendie]\nпослезавтрашний (dies <i>C, Cs etc.</i>)."],
  ["Perenna", "<big>Perenna</big>\n\n<b>Perenna</b> — <i>см.</i>\nAnna 2."],
  ["perenne", "<big>perenne</big>\n\n<b>perenne</b> [perennis]\nв течение всего года, круглый год <i>Col etc.</i>"],
  ["perenne", "<big>perenne</big>\n\n<b>perenne, avi, atum, are</b> [perennis]\n<b>1)</b> долго длиться, долго существовать (amor perennat <i>O</i>);\n<b>2)</b> долго сохранять (vini soporem <i>Col</i>)."],
  ["perennis", "<big>perennis</big>\n\n<b>perennis, e</b> (<i>abl.</i> <i>иногда</i> e) [per + annus]\n<b>1)</b> длящийся круглый год (militia <i>L</i>); остающийся на весь год, неперелётный (avis <i>PM</i>); постоянно текущий, неиссякаемый (aqua <i>C</i>); постоянный, неизменный (cursus stellarum <i>C</i>);\n<b>2)</b> долговечный, прочный (monumentum aere perennius <i>H</i>); стойкий, непоколебимый (virtus <i>C</i>);\n<b>3)</b> старинный (fama <i>C</i>);\n<b>4)</b> неугомонный (loquacitas <i>C</i>);\n<b>5)</b> вечный (inimicus <i>C</i>)."],
  ["perenniservus", "<big>perenniservus</big>\n\n<b>perenni–servus, i</b> <i>m</i>\nвечный раб <i>Pl</i>."],
  ["perennitas", "<big>perennitas</big>\n\n<b>perennitas, atis</b> <i>f</i> [perennis]\nдлительность, постоянство, неиссякаемость: fontium gelidae perennitates <i>C</i> неиссякаемость прохладных источников; perennitatem afferre alicui rei <i>Col</i> сохранить (предохранить от порчи) что-л. || (<i>в обращении к императорам</i>) долголетие (p. vestra! <i>Symm</i>)."],
  ["perenniter", "<big>perenniter</big>\n\n<b>perenniter</b>\nпостоянно, вечно <i>CTh, Aug, Sol, Sid</i>."],
  ["perenticida", "<big>perenticida</big>\n\n<b>perenticida, ae</b> <i>m</i> [<i>из</i> pera + caedo] <i>шутл.</i>\n(<i>по созвучию с</i> parenticida = parricida) режущий кошельки, карманный вор <i>Pl</i>."],
  ["pereo", "<big>pereo</big>\n\n<b>per–eo, ii (ivi), itum, ire</b>\nгибнуть (naves perierunt <i>C</i>); исчезать, приходить в упадок (civitates pereunt <i>C</i>); таять (pereunt nives <i>O</i>); умирать (morbo <i>bAl, Su</i> <i>или</i> a morbo <i>Nep</i>; ex vulneribus <i>L</i>): inter duos periturum esse secretum <i>Pt</i> (мы оба поклялись,) что тайна останется между нами || томиться, чахнуть (amore <i>V</i>); быть без ума влюблённым (p. aliquem <i>Pl</i> <i>или</i> aliquo, aliqua <i>Prp</i>); пропадать, проходить без пользы (labor periit <i>O, Q</i>; tempus periit <i>O</i>); растрачиваться бесцельно (tanta pecunia periit <i>C</i>); терять силу: actiones et res alicui perierunt <i>L</i> чьё-л. право на иск потеряло силу (за давностью); deliberando saepe perit occasio <i>PS</i> из-за колебаний часто упускается удобный случай; perii, interii, occidi <i>Pl</i> я погиб, пропал; <i>в клятвах</i>: peream, si (nisi, ni)... <i>O etc.</i> пусть я погибну, если (не), <i>т. е.</i> клянусь жизнью, что."],
  ["perequito", "<big>perequito</big>\n\n<b>per–equito, avi, atum, are</b>\nобъезжать, проезжать верхом (aciem <i>L</i> <i>и</i> per aciem <i>Cs</i>); промчаться верхом (inter duas acies <i>Cs</i>)."],
  ["pererro", "<big>pererro</big>\n\n<b>per–erro, avi, atum, are</b>\n<b>1)</b> странствовать, обходить, объезжать, исходить (orbem <i>Col</i>; freta <i>O</i>): omnes aditus p. <i>V</i> обойти все входы (осмотреть все подступы); pererratis amborum finibus <i>V</i> обменявшись местами своего жительства; p. aliquem tacitis luminibus <i>V</i> молча окинуть кого-л. взглядом;\n<b>2)</b> обвивать (hedera pererrat truncum <i>PJ</i>);\n<b>3)</b> проникать насквозь (venenum pererrat aliquem <i>PM</i>)."],
  ["pereruditus", "<big>pereruditus</big>\n\n<b>per–eruditus, a, um</b>\nвесьма учёный, просвещённейший <i>C</i>."],
  ["peresus", "<big>peresus</big>\n\n<b>peresus, a, um</b> <i>part. pf. к</i>\nperedo I."],
  ["pereuntis", "<big>pereuntis</big>\n\n<b>pereuntis</b> <i>gen. sg. к</i>\nperiens."],
  ["perexcelsus", "<big>perexcelsus</big>\n\n<b>per–excelsus, a, um</b>\nочень высокий (locus <i>C</i>)."],
  ["perexigue", "<big>perexigue</big>\n\n<b>perexigue</b> [perexiguus]\nочень мало, весьма скудно (praebere <i>C</i>)."],
  ["perexiguus", "<big>perexiguus</big>\n\n<b>per–exiguus, a, um</b>\nочень маленький, крайне скудный, весьма незначительный (spatium <i>Cs</i>; dies <i>C</i>; argentum <i>L</i>)."],
  ["perexilis", "<big>perexilis</big>\n\n<b>per–exilis, e</b>\nочень тонкий <i>или</i> тощий (vitis <i>Col</i>)."],
  ["perexoptatus", "<big>perexoptatus</big>\n\n<b>per–exoptatus, a, um</b>\nвесьма желанный (p. venis <i>AG</i> — <i>in tmesi</i>)."],
  ["perexpeditus", "<big>perexpeditus</big>\n\n<b>per–expeditus, a, um</b>\nочень лёгкий, не представляющий никаких трудностей (defensio <i>C</i>)."],
  ["perexplicatus", "<big>perexplicatus</big>\n\n<b>per–explicatus, a, um</b>\nвполне завершённый, законченный (reditus <i>Sid</i>)."],
  ["perfabrico", "<big>perfabrico</big>\n\n<b>per–fabrico, avi, —, are</b>\nсделать, изготовить, <i>шутл.</i> перехитрить, обмануть (aliquem <i>Pl</i>)."],
  ["perfacete", "<big>perfacete</big>\n\n<b>perfacete</b> [perfacetus]\nочень остроумно (dicta <i>C</i>)."],
  ["perfacetus", "<big>perfacetus</big>\n\n<b>per–facetus, a, um</b>\nвесьма остроумный <i>C</i>."],
  ["perfacile", "<big>perfacile</big>\n\n<b>perfacile</b> [perfacilis]\n<b>1)</b> очень легко <i>C</i>;\n<b>2)</b> весьма охотно <i>Acc, Pl</i>."],
  ["perfacilis", "<big>perfacilis</big>\n\n<b>per–facilis, e</b>\n<b>1)</b> очень лёгкий, совсем нетрудный <i>C, Cs</i>;\n<b>2)</b> очень любезный <i>C</i>."],
  ["perfacundus", "<big>perfacundus</big>\n\n<b>per–facundus, a, um</b>\nвесьма красноречивый <i>Just, Ap</i>."],
  ["perfamiliaris", "<big>perfamiliaris</big>\n\n<b>I per–familiaris, e</b>\nнаходящийся в тесной дружбе, близкий (alicui <i>C</i>).\n\n<b>II per–familiaris, is</b> <i>m</i>\nблизкий товарищ, задушевный друг (p. meus <i>C</i>)."],
  ["perfeci", "<big>perfeci</big>\n\n<b>perfeci</b> <i>pf. к</i>\nperficio."],
  ["perfecte", "<big>perfecte</big>\n\n<b>perfecte</b> [perfectus]\nсовершенно, вполне <i>C, Ap etc.</i>"],
  ["perfectio", "<big>perfectio</big>\n\n<b>perfectio, onis</b> <i>f</i> [perficio]\n<b>1)</b> завершение, совершение, исполнение (maximorum operum <i>C</i>);\n<b>2)</b> совершенство: p. atque absolutio (optimi) <i>C</i> высшее совершенство, идеал."],
  ["perfectissimatus", "<big>perfectissimatus</big>\n\n<b>perfectissimatus, us</b> <i>m</i>\nперфектиссимат, звание перфектиссима (<i>см.</i> perfectus) <i>CTh</i>."],
  ["perfector", "<big>perfector</big>\n\n<b>perfector, oris</b> <i>m</i> [perficio]\nсвершитель, творец (alicujus rei <i>Ter</i>); усовершенствователь, доведший до совершенства (dicendi <i>C</i>)."],
  ["perfectrix", "<big>perfectrix</big>\n\n<b>perfectrix, icis</b> <i>f</i>\nусовершенствовательница, завершительница (philosophia beatae vitae p. <i>Nep ap. Lad</i>)."],
  ["perfectum", "<big>perfectum</big>\n\n<b>perfectum, i</b> <i>n</i> [perfectus]\n<b>1)</b> совершенство (ad p. venire <i>Vr</i>);\n<b>2)</b> <i>грам.</i> (<i>sc.</i> tempus) прошедшее совершенное время: plus quam p. преждепрошедшее время; minus quam p. (= imperfectum) прошедшее несовершенное время."],
  ["perfectus", "<big>perfectus</big>\n\n<b>I perfectus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> perficio;\n<b>2.</b> <i>adj.</i> совершенный, отличный, превосходный (opus, homo <i>C</i>): nihil est simul et inventum et perfectum <i>C</i> ни одно изобретение не может стать сразу же совершенным; perfecta aetas <i>Dig</i> совершеннолетие (в 25 <i>лет</i>) || полный (defectio, <i>sc.</i> solis <i>Sen</i>); perfectissimus <i>CJ, CTh</i> перфектиссим, совершеннейший (<i>почётное звание, присваивавшееся высшим чиновникам при императорах, начиная с Константина; ему предшествовало звание</i> clarissimus).\n\n<b>II perfectus, us</b> <i>m</i>\nзавершённость, законченность <i>Vtr, Tert</i>."],
  ["perferentia", "<big>perferentia</big>\n\n<b>perferentia, ae</b> <i>f</i>\nвыносливость <i>Lact</i>."],
  ["perfero", "<big>perfero</big>\n\n<b>per–fero, tuli, latum, ferre</b>\n<b>1)</b> приносить (a duobus lecticariis perferri <i>Pt</i>); привозить, доставлять (corpus alicujus in patriam <i>Nep</i>; praedam in portum <i>L</i>): epistulas ad aliquem perferendas tradere alicui <i>Sen</i> передать через кого-л. письма для кого-л.; tulisse et pertulisse <i>Sen</i> нести и донести || приводить (aliquem ad aliquem <i>L</i>);\n<b>2)</b> доносить, распространять (famam ad Graeciam <i>L</i>): lucernas in lucem p. <i>H</i> жечь светильники до рассвета; vetustatem pertulisse <i>Q</i> сохраниться от старых времён; hasta vires non perfert <i>V</i> копьё теряет стремительность (полёта); lapis ictum non pertulit <i>V</i> камень не долетел до цели; se p. отправляться (ad limina alicujus <i>V</i>): clamor circa collem perfertur <i>L</i> вокруг холма раздаётся крик;\n<b>3)</b> передавать (epistulam alicui <i>или</i> ad aliquem <i>C</i>); сообщать, возвещать, уведомлять, доносить (aliquid <i>или</i> de aliqua re ad aliquem <i>C</i>; fama Romam perlata est <i>L</i>);\n<b>4)</b> исполнять (mandata <i>T</i>; jussa <i>Prp</i>); заканчивать, совершать, доводить до конца (id, quod suscepi <i>C</i>); сохранять до конца (intrepidos vultus <i>O</i>); проводить, добиться утверждения (p. legem <i>C</i>);\n<b>5)</b> переносить (до конца), претерпевать, выдерживать (calamitates <i>Cs</i>; poenas <i>C, Nep</i>; dolorem <i>V</i>): leve est miseriam ferre, p. est grave <i>SenT</i> тяжело не перенести горе, а переносить его всё время || терпеливо выносить (aliquem <i>Ter, C</i>)."],
  ["perferus", "<big>perferus</big>\n\n<b>per–ferus, a, um</b>\nочень дикий (bos <i>Vr</i>)."],
  ["perfervefio", "<big>perfervefio</big>\n\n<b>per–fervefio, —, fieri</b>\nсильно нагреваться <i>Vr</i> (<i>v. l.</i>)."],
  ["perfica", "<big>perfica</big>\n\n<b>perfica, ae</b> <i>f</i> [perficio]\nусовершенствовательница (p. natura <i>Lcr</i>)."],
  ["perficio", "<big>perficio</big>\n\n<b>perficio, feci, fectum, ere</b> [per + facio]\n<b>1)</b> готовить, изготовлять, делать (signum ex ebore <i>C</i>; poculum argento <i>V</i>); выделывать (coria <i>PM</i>); обрабатывать (lanam <i>PM</i>);\n<b>2)</b> перерабатывать, переваривать (cibos <i>PM</i>);\n<b>3)</b> строить, сооружать (naves, pontem <i>Cs</i>); сочинять (commentarius <i>AG</i>); формулировать, составлять (testamentum jure <i>Dig</i>);\n<b>4)</b> совершенствовать, обучать (aliquem cithara <i>O</i>);\n<b>5)</b> выручать, зарабатывать (sexagena, <i>sc.</i> milia HS <i>Vr</i>);\n<b>6)</b> устраивать, проводить (censum, comitia <i>L</i>); совершать (scelus, facinus <i>C</i>); осуществлять (conata <i>Cs</i>);\n<b>7)</b> завершать, доводить до конца (inceptum <i>Sl</i>); aut non tentaris, aut perfice <i>O</i> или не пытайся, или (уж) доводи до конца; in primordio sui perfici <i>Ap</i> при самом своём возникновении приобрести вполне законченный вид; centum p. annos <i>H</i> просуществовать сто лет; perfecto sole <i>St</i> после заката || выполнять (mandata <i>L</i>);\n<b>8)</b> добиваться, достигать (aliquid <i>или</i> de aliqua re <i>C</i>): perfice, si potes <i>C</i> сделай (устрой), если можешь. — <i>См. тж.</i> perfectum <i>и</i> perfectus."],
  ["perfide", "<big>perfide</big>\n\n<b>perfide</b> [perfidus]\nвероломно, коварно <i>Sen, AG</i>."],
  ["perfidelis", "<big>perfidelis</big>\n\n<b>per–fidelis, e</b>\nвполне надёжный, верный (homo <i>C</i>)."],
  ["perfidens", "<big>perfidens</big>\n\n<b>perfidens, entis</b>\nвполне доверяющийся, полагающийся (aliqua re <i>AV</i>)."],
  ["perfidia", "<big>perfidia</big>\n\n<b>perfidia, ae</b> <i>f</i> [per + fides]\nвероломство, неверность, нечестность <i>Pl, C, T etc.</i>"],
  ["perfidiose", "<big>perfidiose</big>\n\n<b>perfidiose</b> [perfidiosus]\nвероломно, коварно <i>Pl, C, Su</i>."],
  ["perfidiosus", "<big>perfidiosus</big>\n\n<b>perfidiosus, a, um</b> [perfidus]\nвероломный, коварный <i>Pl, C etc.</i>"],
  ["perfidum", "<big>perfidum</big>\n\n<b>perfidum</b> <i>adv.</i> <i>H</i>\n= perfide."],
  ["perfidus", "<big>perfidus</big>\n\n<b>perfidus, a, um</b> [perfidia]\nвероломный, неверный (amicus <i>C</i>): pastor p. <i>O</i> = Paris || коварный (nex <i>Sen</i>); ненадёжный, опасный (via <i>Prp</i>); лишь с виду хороший, <i>т. е.</i> дрянной (vappa <i>M</i>)."],
  ["perfixus", "<big>perfixus</big>\n\n<b>per–fixus, a, um</b>\nпронзённый, <i>перен.</i> охваченный, обуреваемый (desiderio <i>Lcr</i>)."],
  ["perflabilis", "<big>perflabilis</big>\n\n<b>per–flabilis, e</b>\nобвеваемый, продуваемый ветром, находящийся на открытом воздухе (locus <i>Ap, Pall</i>)."],
  ["perflagitiosus", "<big>perflagitiosus</big>\n\n<b>per–flagitiosus, a, um</b>\nвесьма бесчестный, крайне порочный <i>C</i>."],
  ["perflatilis", "<big>perflatilis</big>\n\n<b>perflatilis, e</b> <i>v. l.</i>\n= perflabilis."],
  ["perflatus", "<big>perflatus</big>\n\n<b>perflatus, us</b> <i>m</i> [perflo]\nпродувание, веяние (venti <i>Vtr</i>); ветер <i>CC, Col</i>."],
  ["perfletus", "<big>perfletus</big>\n\n<b>perfletus, a, um</b> [per + fleo]\nвесь в слезах, заплаканный <i>Ap</i>."],
  ["perflo", "<big>perflo</big>\n\n<b>per–flo, avi, atum, are</b>\n<b>1)</b> продувать, обдувать, обвевать (terras turbine <i>V</i>); дуть (Favonius perflat <i>Col</i>);\n<b>2)</b> трубить, издавать (звук) (p. murmura concha <i>Lcn</i>)."],
  ["perfluctuo", "<big>perfluctuo</big>\n\n<b>per–fluctuo, —, —, are</b>\nпроникать кишащей массой (tumidos artus <i>Lcr</i>)."],
  ["perfluo", "<big>perfluo</big>\n\n<b>per–fluo, fluxi, fluxum, ere</b>\n<b>1)</b> протекать, течь (per colum <i>Lcr</i>): hac et illac p. <i>погов. Ter</i> течь со всех сторон, <i>т. е.</i> не уметь хранить тайн;\n<b>2)</b> втекать, вливаться (in mare <i>PM</i>; in vas <i>Lcr</i>);\n<b>3)</b> обливаться (sudore <i>Ap</i>);\n<b>4)</b> ниспадать, спускаться до низу (ambitus pallae perfluit <i>Ap</i>);\n<b>5)</b> изобиловать (pomis <i>Tib</i>)."],
  ["perfluus", "<big>perfluus</big>\n\n<b>perfluus, a, um</b> [perfluo]\nрастекающийся, <i>перен.</i> расслабленный, изнеженный (incessus <i>Ap</i>)."],
  ["perfodio", "<big>perfodio</big>\n\n<b>per–fodio, fodi</b> (<i>арх. Pl</i> <b>fodivi</b>), <b>fossum, ere</b>\n<b>1)</b> прокапывать (Isthmum <i>Col</i>); прорывать (montem <i>Vr, QC</i>); пробивать, проламывать (parietem <i>Pl, C</i>);\n<b>2)</b> прокалывать, пронзать (thoraca <i>V</i>; pectus <i>PJ</i>);\n<b>3)</b> ковырять (dentes spina, <i>v. l.</i> pinna <i>Pt</i>)."],
  ["perforatio", "<big>perforatio</big>\n\n<b>perforatio, onis</b> <i>f</i>\nпробуравливание: p. testae <i>CA</i> трепанация черепа."],
  ["perforator", "<big>perforator</big>\n\n<b>perforator, oris</b> <i>m</i> [perforo]\nпроламывающий: p. parietum <i>Pl</i> взломщик (<i>v. l.</i>)."],
  ["performidolosus", "<big>performidolosus</big>\n\n<b>per–formidolosus, a, um</b>\nочень боязливый <i>AV</i>."],
  ["performo", "<big>performo</big>\n\n<b>per–formo, —, —, are</b>\nобразовывать, создавать (aliquem ad malum <i>Tert</i> — <i>v. l.</i>)."],
  ["perforo", "<big>perforo</big>\n\n<b>per–foro, avi, atum, are</b>\n<b>1)</b> пробуравливать, просверливать, провертеть (operculum <i>L</i>); пробивать, проламывать (navem rostro <i>bAl</i>);\n<b>2)</b> прокалывать, пронзать (aliquem hasta <i>O</i>);\n<b>3)</b> проникать (sol perforat culmina radiis <i>St</i>);\n<b>4)</b> прокладывать (vias <i>C</i>)."],
  ["perfortiter", "<big>perfortiter</big>\n\n<b>per–fortiter</b>\nочень храбро <i>Ter</i>."],
  ["perfossor", "<big>perfossor</big>\n\n<b>perfossor, oris</b> <i>m</i> [perfodio] <i>v. l.</i>\n= perforator."],
  ["perfossus", "<big>perfossus</big>\n\n<b>perfossus, a, um</b> <i>part. pf. к</i>\nperfodio."],
  ["perfractus", "<big>perfractus</big>\n\n<b>perfractus, a, um</b> <i>part. pf. к</i>\nperfringo."],
  ["perfregi", "<big>perfregi</big>\n\n<b>perfregi</b> <i>pf. к</i>\nperfringo."],
  ["perfremo", "<big>perfremo</big>\n\n<b>per–fremo, —, —, ere</b>\nфыркать, наполнять шумом (perfrimunt delphini <i>Acc</i>)."],
  ["perfrequens", "<big>perfrequens</big>\n\n<b>per–frequens, entis</b> <i>adj.</i>\nпосещаемый многими, многолюдный (emporium <i>L</i>)."],
  ["perfreto", "<big>perfreto</big>\n\n<b>perfreto, —, —, are</b> [per + fretum]\nпереплывать <i>Sol</i>."],
  ["perfrico", "<big>perfrico</big>\n\n<b>per–frico, fricui, fricatum (frictum), are</b>\n<b>1)</b> натирать (aliquem in unctione <i>CC</i>; oleo <i>или</i> ex oleo <i>CC</i>); смазывать (faciem alicujus fuligine <i>Pt</i>);\n<b>2)</b> стирать: p. os <i>C</i> (frontem <i>M</i> <i>или</i> faciem <i>PM, Q</i>) стирать краску стыда с лица, <i>т. е.</i> отбросить стыд;\n<b>3)</b> почёсывать (caput sinistra manu <i>C</i>)."],
  ["perfrictio", "<big>perfrictio</big>\n\n<b>I perfrictio, onis</b> <i>f</i> [perfrigesco]\nпростуда <i>PM, Scr, CA</i>.\n\n<b>II perfrictio, onis</b> <i>f</i> [perfrico]\nнатирание, втирание <i>PM, Veg</i>."],
  ["perfrictiuncula", "<big>perfrictiuncula</big>\n\n<b>perfrictiuncula, ae</b> <i>f</i> [<i>demin. к</i> perfrictio I]\nлёгкая простуда <i>Tert</i>."],
  ["perfrigefacio", "<big>perfrigefacio</big>\n\n<b>per–frigefacio, (feci, factum), ere</b> [frigefacio <i>от</i> frigeo <i>и</i> facio]\nнагонять холод, леденить: p. cor alicui <i>Pl</i> сильно напугать кого-л."],
  ["perfrigero", "<big>perfrigero</big>\n\n<b>per–frigero, —, —, are</b>\nохлаждать (aliquid <i>PM, Scr</i>)."],
  ["perfrigesco", "<big>perfrigesco</big>\n\n<b>per–frigesco, frixi, —, ere</b>\n<b>1)</b> охлаждаться <i>PM</i>;\n<b>2)</b> простуживаться <i>Vr, CC, J</i>."],
  ["perfrigidus", "<big>perfrigidus</big>\n\n<b>per–frigidus, a, um</b>\nочень холодный (hiems <i>C</i>)."],
  ["perfringo", "<big>perfringo</big>\n\n<b>perfringo, fregi, fractum, ere</b> [per + frango]\n<b>1)</b> разламывать, разбивать (saxum <i>C</i>): p. cervicem <i>T</i> сломать шею; naves perfregerunt proras <i>L</i> корабли разбились в носовой части;\n<b>2)</b> ниспровергать, нарушать, попирать, уничтожать (leges, decreta senatus <i>C</i>);\n<b>3)</b> проламывать (muros <i>T</i>); прорвать (phalangem hostium <i>Cs</i>); силой прокладывать себе путь (сквозь, через что-л.), преодолевать (p. angustias, altitudines <i>C</i>): p. domos <i>T</i> ворваться (вломиться) в дома;\n<b>4)</b> <i>перен.</i> захватывать, потрясать (animos <i>C</i>)."],
  ["perfrio", "<big>perfrio</big>\n\n<b>per–frio, —, —, are</b>\nрастирать, крошить (bacas myrti <i>Col</i>)."],
  ["perfrivolus", "<big>perfrivolus</big>\n\n<b>per–frivolus, a, um</b>\nсовершенно ничтожный, никуда не годный (haec video esse perfrivola <i>Vop</i>)."],
  ["perfrixi", "<big>perfrixi</big>\n\n<b>perfrixi</b> <i>pf. к</i>\nperfrigesco."],
  ["perfructio", "<big>perfructio</big>\n\n<b>perfructio</b> <i>и</i> <b>perfruitio, onis</b> <i>f</i> [perfruor]\nнаслаждение, пользование (boni <i>Aug</i>)."],
  ["perfruor", "<big>perfruor</big>\n\n<b>per–fruor, fructus sum, frui</b> <i>depon.</i>\n<b>1)</b> (всецело) наслаждаться (leni otio <i>SenT</i>; pacis libertatisque bonis <i>L</i>; <i>реже</i> aliquid <i>C</i>);\n<b>2)</b> выполнять, исполнять (mandatis <i>O</i>)."],
  ["perfudi", "<big>perfudi</big>\n\n<b>perfudi</b> <i>pf. к</i>\nperfundo."],
  ["perfuga", "<big>perfuga</big>\n\n<b>perfuga, ae</b> <i>m</i> [perfugio]\nперебежчик, <i>перен.</i> отступник <i>C, Cs, Nep, L</i>."],
  ["perfugio", "<big>perfugio</big>\n\n<b>per–fugio, fugi, fugitum, ere</b>\n<b>1)</b> прибегать, искать убежища (ad aliquem <i>L</i>; Corinthum <i>Nep</i>; ad otium <i>C</i>);\n<b>2)</b> перебегать (ad Caesarem <i>Cs</i>)."],
  ["perfugium", "<big>perfugium</big>\n\n<b>perfugium, i</b> <i>n</i> [perfugio]\nубежище (p, ac praesidium salutis <i>C</i>)."],
  ["perfulguro", "<big>perfulguro</big>\n\n<b>per–fulguro, —, —, are</b>\nблистать, сверкать (ense <i>St</i>)."],
  ["perfunctio", "<big>perfunctio</big>\n\n<b>per–functio, onis</b> <i>f</i> [perfungor]\nвыполнение, исполнение (laborum, honorum <i>C</i>)."],
  ["perfunctorie", "<big>perfunctorie</big>\n\n<b>perfunctorie</b> [perfungor]\nслегка (aliquem verberare <i>Pt</i>); поверхностно (aliquid facere <i>Dig</i>; rem agere <i>Aug</i>)."],
  ["perfunctorius", "<big>perfunctorius</big>\n\n<b>perfunctorius, a, um</b>\nповерхностный (sermo <i>Ambr</i>)."],
  ["perfunctus", "<big>perfunctus</big>\n\n<b>perfunctus, a, um</b> <i>part. pf. к</i>\nperfungor; <i>иногда</i> <i>pass.</i>: memoria perfuncti periculi <i>C</i> память (воспоминание) о пережитой опасности."],
  ["perfundo", "<big>perfundo</big>\n\n<b>per–fundo, fudi, fusum, ere</b>\n<b>1)</b> обливать, поливать (aliquem aqua <i>C</i>): perfusus liquidis odoribus <i>H</i> облитый духами || вливать (aliquid in vas <i>Col</i>); орошать, мочить (artus rore <i>O</i>); натирать (umeros — <i>acc. gr.</i> — oleo perfusus <i>V</i>); заливать: perfusus sanguine <i>O</i> залитый (обагрённый) кровью; perfundi fletu <i>L</i> (lacrimis <i>O</i>) заливаться слезами; perfundi flumine <i>V</i> купаться в реке;\n<b>2)</b> красить, окрашивать (ostro <i>V</i>; coloribus <i>Lcr</i>);\n<b>3)</b> посыпать (aliquid pulvere <i>V</i>); покрывать (tecta auro <i>Sen</i>); закрывать (pedes amictu <i>M</i>);\n<b>4)</b> <i>поэт.</i> наполнять, преисполнять (animum alicujus voluptate <i>C</i>; aliquem gaudio <i>L</i>): perfusus timore <i>L</i> объятый страхом;\n<b>5)</b> тревожить, смущать (aliquem judicio <i>C</i>; litora bello <i>Sil</i>)."],
  ["perfungor", "<big>perfungor</big>\n\n<b>per–fungor, functus sum, fungi</b> <i>depon.</i>\n<b>1)</b> исполнять, управлять, ведать, заниматься (aliqua re <i>C</i>); проделывать, осуществлять (p. laboribus <i>C</i>); заканчивать: perfunctus sum <i>C</i> я кончил (готов);\n<b>2)</b> переносить, претерпевать, переживать (memoria perfuncti periculi <i>C</i>): p. fato <i>L, T</i> (vita <i>Lcr</i>) умереть; perfunctus a febri <i>Vr</i> оправившись от лихорадки;\n<b>3)</b> вкушать, наслаждаться (epulis <i>O</i>; omnibus bonis <i>Sulpicius ap. C</i>; редко с <i>acc.</i>: p. omnia vitae praemia <i>Lcr</i>)."],
  ["perfuro", "<big>perfuro</big>\n\n<b>per–furo, —, —, ere</b>\n<b>1)</b> непрестанно бесноваться, бушевать (pontus — <i>v. l.</i> ventus — perfurit <i>Lcr</i>);\n<b>2)</b> бешено мчаться, пролетать, проноситься (Eoas domos <i>St</i>)."],
  ["perfusio", "<big>perfusio</big>\n\n<b>perfusio, onis</b> <i>f</i> [perfundo]\n<b>1)</b> обливание (corporis ex aqua calida <i>CC</i>); омовение (malarum <i>PM</i>);\n<b>2)</b> обряд крещения <i>Lact</i>."],
  ["perfusorie", "<big>perfusorie</big>\n\n<b>perfusorie</b>\nповерхностно, вскользь (dicere <i>Dig</i>)."],
  ["perfusorius", "<big>perfusorius</big>\n\n<b>perfusorius, a, um</b> [perfundo]\n<b>1)</b> поверхностный, неглубокий (voluptas <i>Sen</i>);\n<b>2)</b> искажающий, ложный (assertio <i>Su</i>)."],
  ["perfusus", "<big>perfusus</big>\n\n<b>perfusus, a, um</b> <i>part. pf. к</i>\nperfundo."],
  ["Pergama", "<big>Pergama</big>\n\n<b>Pergama, orum</b> <i>n</i> <i>Lcr, V, O</i>\n= Pergamum 1."],
  ["pergamena", "<big>pergamena</big>\n\n<b>pergamena, ae</b> <i>f</i> (<i>sc.</i> charta)\nпергамен(т) <i>Is</i>."],
  ["Pergamenus", "<big>Pergamenus</big>\n\n<b>I Pergamenus, a, um</b> [Pergamum 2]\nпергамский (navis <i>Nep</i>; civitas <i>C</i>): Pergamena charta <i>PM</i> = pergamena.\n\n<b>II Pergamenus, i</b> <i>m</i>\nжитель Пергама <i>C, Nep</i>."],
  ["Pergameus", "<big>Pergameus</big>\n\n<b>Pergameus, a, um</b>\n<b>1)</b> [Pergamum 1] <i>поэт.</i> троянский (gens, arces <i>V</i>): vates Pergamea <i>Prp</i> = Кассандра || римский (<i>т. к. по официальной версии римляне считались потомками троянцев</i>) (sanguis <i>Sil</i>);\n<b>2)</b> [Pergamum 2] пергамский: deus P. <i>M</i> = Эскулапий."],
  ["Pergamis", "<big>Pergamis</big>\n\n<b>Pergamis, idis</b> <i>f</i>\nПергамида, <i>город в Эпире</i> <i>Vr</i>."],
  ["Pergamum", "<big>Pergamum</big>\n\n<b>Pergamum, i</b> <i>n</i> <i>и</i> <b>Pergamus (-os), i</b> <i>f</i>\nПергам:\n<b>1)</b> (<i>обычно</i> Pergama, orum <i>n</i>) троянский кремль <i>SenT, St</i>; <i>поэт.</i> Троя <i>Lcr, V, O</i>;\n<b>2)</b> <i>город в Большой Мисии, на реке Селин, столица Пергамского царства</i> (<i>ныне</i> Бергама) <i>L, PM</i>;\n<b>3)</b> <i>город на Крите</i> <i>V, VP</i>."],
  ["pergaudeo", "<big>pergaudeo</big>\n\n<b>per–gaudeo, —, —, ere</b>\nвесьма радоваться, ликовать <i>C</i>."],
  ["pergin'", "<big>pergin'</big>\n\n<b>pergin'</b> <i>Pl, Ter</i>\n= pergisne (<i>к</i> pergo)."],
  ["pergitin'", "<big>pergitin'</big>\n\n<b>pergitin'</b> <i>Pl</i>\n= pergitisne (<i>к</i> pergo)."],
  ["perglisco", "<big>perglisco</big>\n\n<b>per–glisco, —, —, ere</b>\nтолстеть, жиреть (gallina pergliscit <i>Col</i>)."],
  ["pergnarus", "<big>pergnarus</big>\n\n<b>per–gnarus, a, um</b>\nхорошо знакомый (alicujus rei <i>Sl, Ap</i>)."],
  ["pergnosco", "<big>pergnosco</big>\n\n<b>pergnosco</b> <i>арх.</i> <i>Pl</i>\n= pernosco."],
  ["pergo", "<big>pergo</big>\n\n<b>pergo, perrexi, perrectum, ere</b> [per + rego]\n<b>1)</b> идти дальше, продвигаться (eadem via <i>C</i>): video, quo pergat oratio <i>C</i> вижу, к чему клонится (твоя) речь || собираться, выступать, отправляться (ad aliquem <i>C</i>; in agros <i>L</i>; ad castra <i>Cs</i>; adversus aliquem <i>Sl</i>): domum ire pergam <i>Ter</i> пойду-ка домой;\n<b>2)</b> начинать, приступать: perge! <i>V</i> начинай!, давай!, ну же!; perge audacter <i>Su</i> смело вперёд!; perge linquere <i>Ctl</i> оставь, брось;\n<b>3)</b> продолжать, не прекращать (perge, ut coeperas <i>C</i>): p. iter <i>St, L</i> продолжать путь; pergamus ad reliqua <i>C</i> перейдём к остальному; non pergam ad cetera <i>C</i> об остальном я умолчу."],
  ["pergracilis", "<big>pergracilis</big>\n\n<b>per–gracilis, e</b>\nстройный, тонкий (caulis <i>PM</i>)."],
  ["pergraecor", "<big>pergraecor</big>\n\n<b>per–graecor, —, ari</b> <i>depon.</i>\nжить на греческий лад, <i>т. е.</i> шумно пировать, кутить <i>Pl</i>."],
  ["pergrandesco", "<big>pergrandesco</big>\n\n<b>per–grandesco, —, —, ere</b>\nстановиться очень большим <i>Acc</i>."],
  ["pergrandis", "<big>pergrandis</big>\n\n<b>per–grandis, e</b>\n<b>1)</b> очень большой, крупный (gemma <i>C</i>; lucrum <i>Pl</i>);\n<b>2)</b> очень старый, престарелый (natu <i>L</i>)."],
  ["pergraphicus", "<big>pergraphicus</big>\n\n<b>per–graphicus, a, um</b> (<i>греч.</i>)\nзаконченный, самый настоящий (sycophanta <i>Pl</i>)."],
  ["pergratus", "<big>pergratus</big>\n\n<b>per–gratus, a, um</b>\nочень приятный (p. et jucundus <i>Cs</i>); pergratum alicui facere <i>C</i> доставлять кому-л. удовольствие."],
  ["pergravis", "<big>pergravis</big>\n\n<b>per–gravis, e</b>\nочень веский, очень важный, весьма серьёзный (testis <i>C</i>): levia sunt, quae tu pergravia esse in animum induxti tuum <i>Ter</i> то, что тебе кажется столь серьёзным, не более, чем пустяки."],
  ["pergraviter", "<big>pergraviter</big>\n\n<b>pergraviter</b> [pergravis]\nвесьма серьёзно, очень сильно (aliquem reprehendere <i>C</i>)."],
  ["pergula", "<big>pergula</big>\n\n<b>pergula, ae</b> <i>f</i> [pergo]\n<b>1)</b> выступ дома, пристройка, навес <i>PM</i>;\n<b>2)</b> лачуга, хижина <i>Pt, Aus</i>;\n<b>3)</b> лавка <i>PM, Dig</i>;\n<b>4)</b> длинный трельяж для виноградных лоз <i>Col, PM</i>;\n<b>5)</b> школа (in pergula docere <i>Su</i>);\n<b>6)</b> астрономическая вышка <i>Su</i>;\n<b>7)</b> публичный дом <i>Pl, Prp</i>."],
  ["pergulanus", "<big>pergulanus</big>\n\n<b>pergulanus, a, um</b> [pergula 4]\nподвязанный к трельяжу (vitis <i>Col</i>)."],
  ["Pergus", "<big>Pergus</big>\n\n<b>Pergus, i</b> <i>m</i>\nПерг, <i>озеро в Сицилии, близ города</i> Henna, <i>где Плутон, по преданию, похитил Прозерпину</i> <i>O</i>."],
  ["perhaurio", "<big>perhaurio</big>\n\n<b>per–haurio, hausi, —, ire</b>\nполностью опорожнять, осушать (calicem uno haustu <i>Ap</i>); <i>перен.</i> впивать (aliquid auribus <i>Pl</i>)."],
  ["perhibeo", "<big>perhibeo</big>\n\n<b>perhibeo, hibui, hibitum, ere</b> [per + habeo]\n<b>1)</b> пред(о)ставлять, предлагать: p. aliquem <i>C</i> предлагать кого-л. в защитники;\n<b>2)</b> оказывать (alicui honorem <i>PM</i>); выражать (alicui gratias <i>Ap</i>);\n<b>3)</b> придавать, приписывать (vim herbae <i>PM</i>); признавать: alicui rei palmam p. <i>PM</i> признавать за чем-л. превосходство;\n<b>4)</b> высказывать, произносить (testimonium <i>Vr, PJ</i>); приводить (exemplum <i>PM</i>); утверждать, говорить: Romulus perhibetur omnibus rebus ceteris praestitisse <i>C</i> говорят, что Ромул превосходил всех прочих во всех отношениях; iit perbibent <i>V</i> как рассказывают; nuntii fuisse perhibentur <i>C</i> (Диоскуры) были, говорят, вестниками (<i>победы Эмилия Павла над Персеем Македонским при Пидне в 168 г. до н. э.</i>);\n<b>5)</b> упоминать (non minus est Agesilaus ille perhibendus <i>C</i>);\n<b>6)</b> считать <i>или</i> называть (aliquem optimum vatem <i>C</i>)."],
  ["perhiemo", "<big>perhiemo</big>\n\n<b>per–hiemo, —, —, are</b>\nперезимовать <i>Col</i>."],
  ["perhilum", "<big>perhilum</big>\n\n<b>per–hilum</b> <i>adv.</i>\nочень мало, чуть-чуть (vacillare <i>Lcr</i>)."],
  ["perhonorifice", "<big>perhonorifice</big>\n\n<b>perhonorifice</b> [perhonorificus]\nс честью, весьма почтительно <i>C</i>."],
  ["perhonorificus", "<big>perhonorificus</big>\n\n<b>per–honorificus, a, um</b>\n<b>1)</b> весьма почётный (discessus <i>C</i>); сопряжённый с почестями (consalutatio <i>C</i>);\n<b>2)</b> весьма почтительный (in aliquem <i>C</i>)."],
  ["perhorreo", "<big>perhorreo</big>\n\n<b>per–horreo, —, —, ere</b>\nужасаться, приходить в ужас (p. aliquid <i>Amm, Ambr</i>)."],
  ["perhorresco", "<big>perhorresco</big>\n\n<b>per–horresco, horrui, —, ere</b>\n<b>1)</b> сильно дрожать, содрогаться (toto corpore <i>C</i>); сотрясаться (clamore perhorruit Aetne <i>O</i>);\n<b>2)</b> сильно волноваться, высоко вздыматься (aequor perhorruit <i>O</i>);\n<b>3)</b> ужасаться, трепетать, сильно бояться (aliquem, aliquid <i>C, H, O</i>)."],
  ["perhorridus", "<big>perhorridus</big>\n\n<b>per–horridus, a, um</b>\nстрашный, ужасный, жуткий (silvae <i>L</i>)."],
  ["perhumaniter", "<big>perhumaniter</big>\n\n<b>perhumaniter</b> [perhumanus]\nвесьма ласково, приветливо, учтиво (scribere <i>C</i>)."],
  ["perhumanus", "<big>perhumanus</big>\n\n<b>per–humanus, a, um</b>\nочень приветливый, весьма ласковый, вежливый (epistula, sermo <i>C</i>; vultus <i>Ap</i>)."],
  ["perhumilis", "<big>perhumilis</big>\n\n<b>per–humilis, e</b>\nчрезвычайно малорослый (corpus <i>Amm</i>)."],
  ["periambus", "<big>periambus</big>\n\n<b>per–iambus, i</b> <i>m</i> <i>Q</i>\n= pyrrhichius."],
  ["Periander", "<big>Periander</big>\n\n<b>Periander</b> <i>и</i> <b>Periandrus, dri</b> <i>m</i>\nПериандр, <i>сын Клипсела, коринфский царь, причисляемый к «семи мудрецам»</i> (<i>668-585 гг. до н. э.</i>) <i>AG, Sid</i>."],
  ["periboetos", "<big>periboetos</big>\n\n<b>periboetos, on</b> <i>adj.</i> (<i>греч.</i>)\nславный, знаменитый <i>PM</i>."],
  ["peribolus", "<big>peribolus</big>\n\n<b>peribolus, i</b> <i>m</i> (<i>греч.</i>)\nкольцевая стена <i>или</i> круговой ход <i>Vlg</i>."],
  ["perichyte", "<big>perichyte</big>\n\n<b>perichyte, es</b> <i>f</i> (<i>греч.</i>)\nрукопашная борьба (<i>род гладиаторского состязания</i>) <i>CJ</i>."],
  ["Pericles", "<big>Pericles</big>\n\n<b>Pericles, is</b> <i>и</i> <b>i</b> <i>m</i>\nПерикл, <i>афинский политик, оратор и полководец</i> (<i>493-429 гг. до н. э.</i>) <i>C, VM, Q etc.</i>"],
  ["periclitabundus", "<big>periclitabundus</big>\n\n<b>periclitabundus, a, um</b> [periclitor]\nпробующий, испытывающий (aliquid aliqua re <i>Ap</i>): p. sui <i>Ap</i> пробующий свои силы."],
  ["periclitans", "<big>periclitans</big>\n\n<b>periclitans, antis</b>\n<b>1.</b> <i>part. praes. к</i> periclitor;\n<b>2.</b> <i>subst. m</i>\n<b>1)</b> опасно больной (ex aliqua re <i>PM</i>);\n<b>2)</b> состоящий под судом, обвиняемый <i>Q, Su, T</i>."],
  ["periclitatio", "<big>periclitatio</big>\n\n<b>periclitatio, onis</b> <i>f</i> [periclitor]\nопыт, испытание <i>C</i>."],
  ["periclitatus", "<big>periclitatus</big>\n\n<b>I periclitatus, a, um</b> <i>part. pf. к</i>\npericlitor; <i>иногда</i> <i>pass.</i> испытанный (mores <i>C</i>).\n\n<b>II periclitatus, i</b> <i>m</i> <i>Q etc.</i>\n= periclitans 2, 2."],
  ["periclitor", "<big>periclitor</big>\n\n<b>periclitor, atus sum, ari</b> <i>depon.</i> [periculum]\n<b>1)</b> пробовать, испытывать, подвергать испытанию (fortunam <i>C</i>; animum alicujus <i>Pl</i>; vires <i>C</i>);\n<b>2)</b> рисковать, подвергать опасности (non est salus rei publicae periclitanda <i>C</i>);\n<b>3)</b> делать попытку (in aliqua re <i>C</i>);\n<b>4)</b> подвергаться (находиться в) опасности (vita alicujus periclitatur <i>Cs</i>; succurrere periclitanti <i>Pt</i>; p. aliqua re <i>L, Q</i>, pro aliqua re <i>Q</i> <i>или</i> in <i>и</i> de aliqua re <i>C etc.</i>): p. capitis <i>Ap</i> находиться в смертельной опасности."],
  ["periclum", "<big>periclum</big>\n\n<b>periclum, i</b> <i>n</i> <i>Pl, Ter, Lcr etc.</i>\n= periculum."],
  ["Periclymenus", "<big>Periclymenus</big>\n\n<b>Periclymenus, i</b> <i>m</i>\nПериклимен, <i>один из Аргонавтов, сын Нелея, брат Нестора, получивший от Нептуна дар преображения</i> <i>O</i>."],
  ["periculor", "<big>periculor</big>\n\n<b>periculor, —, ari</b> <i>depon.</i> <i>Cato</i>\n= periclitor."],
  ["periculose", "<big>periculose</big>\n\n<b>periculose</b> [periculosus]\n<b>1)</b> опасно (aegrotare <i>C</i>); с опасностью (navigari <i>C</i>);\n<b>2)</b> с опаской, со страхом (dicere <i>C</i>)."],
  ["periculosus", "<big>periculosus</big>\n\n<b>periculosus, a, um</b> [periculum]\nопасный (locus, vulnus <i>C</i>); угрожающий опасностями (bellum <i>C</i>): p. in aliquem <i>C</i> подвергающий опасности кого-л."],
  ["periculum", "<big>periculum</big>\n\n<b>periculum, i</b> <i>n</i> [<i>одного корня с</i> experimentum]\n<b>1)</b> проба, попытка, опыт: p. facere alicujus rei <i>C</i> испытать что-л., познакомиться с чём-л. (на опыте); facere p. in litteris <i>Ter</i> проверить (чью-л.) образованность; p. facere alicujus <i>C</i> померяться с кем-л. (силами);\n<b>2)</b> опасность, риск: numquam p. sine periclo vincitur <i>PS</i> ни одной опасности нельзя преодолеть, не подвергаясь (при этом) опасности; negotium sine periculo <i>C</i> спокойные занятия; p. Persarum <i>Nep</i> опасность со стороны персов; p. facere alicujus rei <i>L</i> рисковать сделать что-л. (осмеливаться на что-л.); suo periculo <i>C, Pt etc.</i> на свой риск (страх); periculo alicujus <i>Ter, C, Cs</i> с опасностью для кого-л.; in periculo versari (<i>или</i> esse) <i>C etc.</i> находиться в опасности; sibi causam periculi facere <i>T</i> поставить себя в опасное положение; bono periculo p. facere <i>Ap</i> ничем, в сущности, не рисковать; p. adire <i>C etc.</i> (obire, subire, suscipere <i>C</i>) <i>или</i> in p. vocari (venire) <i>C</i> подвергаться опасности; p. conservandae existimationis adire <i>Ap</i> рисковать своей репутацией; p. capitis <i>Ter, C</i> <i>и</i> p. vitae <i>Cs</i> опасность для жизни; p. est (ne) <i>C, Nep</i> приходится опасаться (как бы не);\n<b>3)</b> судебный процесс (aliquem in periculis defendere <i>Nep</i>): p. alicui facessere <i>T</i> вчинить кому-л. иск;\n<b>4)</b> протокол судебного заседания (in periculo inscribere <i>Nep</i>);\n<b>5)</b> <i>ритор.</i> рискованный оборот, вычурность (p. in enuntiatione <i>Q</i>);\n<b>6)</b> гибель, разрушение (tremefacta usque ad periculum civitas <i>Eccl</i>);\n<b>7)</b> обострение, пароксизм (<i>sc.</i> morbi <i>PM</i>)."],
  ["peridoneus", "<big>peridoneus</big>\n\n<b>per–idoneus, a, um</b>\nвесьма удобный, пригодный, подходящий (alicui rei <i>Cs, T</i> <i>или</i> ad aliquid <i>Sl, Su</i>)."],
  ["periegeticus", "<big>periegeticus</big>\n\n<b>periegeticus, i</b> <i>m</i> (<i>греч.</i>)\nавтор путевых заметок <i>Lact</i>."],
  ["periens", "<big>periens</big>\n\n<b>periens, pereuntis</b> <i>part. praes. к</i>\npereo."],
  ["Perilleus", "<big>Perilleus</big>\n\n<b>Perilleus, a, um</b> [Perillus]\nпериллов (aes <i>O</i>)."],
  ["Perillus", "<big>Perillus</big>\n\n<b>Perillus, i</b> <i>m</i>\nПерилл, <i>агригентский медник, изготовивший для тирана Фаларида медного быка, в котором должны были сжигаться приговорённые к смерти; первым был подвергнут этой казни сам Перилл</i> <i>Prp, PM, O, Sil</i>."],
  ["perillustris", "<big>perillustris</big>\n\n<b>per–illustris, e</b>\n<b>1)</b> очень ясный, вполне очевидный <i>Nep</i>;\n<b>2)</b> окружённый большим почётом, пользующийся большим уважением, овеянный славой <i>C</i>."],
  ["perimachia", "<big>perimachia</big>\n\n<b>perimachia, ae</b> <i>f</i> (<i>греч.</i>)\nнападение, набег <i>Sid</i>."],
  ["perimbecillus", "<big>perimbecillus</big>\n\n<b>per–imbecillus, a, um</b>\nочень слабый <i>Vr, C</i>."],
  ["Perimede", "<big>Perimede</big>\n\n<b>Perimede, es</b> <i>f</i>\nПеримеда, <i>имя волшебницы у Феокрита</i> <i>Prp</i>."],
  ["Perimedeus", "<big>Perimedeus</big>\n\n<b>Perimedeus, a, um</b> [Perimede]\nволшебный, колдовской (gramina <i>Prp</i>)."],
  ["Perimele", "<big>Perimele</big>\n\n<b>Perimele, es</b> <i>f</i>\nПеримела, <i>дочь Гипподама, возлюбленная речного бога Ахелоя, превращенная Нептуном в о-в того же имени</i> <i>O</i>."],
  ["perimetros", "<big>perimetros</big>\n\n<b>perimetros, i</b> <i>f</i> (<i>греч.</i>)\nокружность, периметр <i>Vtr, Frontin</i>."],
  ["perimo", "<big>perimo</big>\n\n<b>perimo, emi, emptum (emtum), ere</b> [per + emo]\n<b>1)</b> отнимать, лишать (sensum <i>C</i>);\n<b>2)</b> разрушать, уничтожать (simulacra <i>C</i>; Trojam <i>V</i>); изнурять, истощать (corpus macie peremptum <i>L</i>); убивать, умерщвлять (aliquem <i>C, O, T</i>; se <i>VM, M etc.</i>): morte peremptus <i>V</i> похищенный смертью; luna perempta est <i>Poeta ap. C</i> луна скрылась (затмилась);\n<b>3)</b> расстраивать, препятствовать, мешать, срывать (consilium <i>C</i>; causam publicam <i>C</i>): si vis aliqua major reditum peremisset <i>C</i> если бы по какой-л. независящей причине возвращение (моё) оказалось невозможным."],
  ["perimpeditus", "<big>perimpeditus</big>\n\n<b>per–impeditus, a, um</b>\nочень трудный: locus p. <i>bAfr</i> непроходимое место."],
  ["perinaeon", "<big>perinaeon</big>\n\n<b>perinaeon (-eon)</b> <i>n</i> <i>и</i> <b>perineos, i</b> <i>m</i> (<i>греч.</i>) <i>анат.</i>\nпромежность <i>CA</i>."],
  ["perinanis", "<big>perinanis</big>\n\n<b>per–inanis, e</b>\nсовершенно пустой, бесплодный, ненужный: magnum, sed perinane «sophos» <i>M</i> шумные, но бесполезные одобрения (зрителей)."],
  ["perincertus", "<big>perincertus</big>\n\n<b>per–incertus, a, um</b>\nвесьма недостоверный, очень сомнительный <i>Sl</i>."],
  ["perincommode", "<big>perincommode</big>\n\n<b>perincommode</b> [perincommodus]\nвесьма неудобно, крайне некстати (accidit <i>C</i>)."],
  ["perincommodus", "<big>perincommodus</big>\n\n<b>per–incommodus, a, um</b>\nочень неудобный, крайне неподходящий <i>L</i>."],
  ["perinconsequens", "<big>perinconsequens</big>\n\n<b>per–inconsequens, entis</b> <i>adj.</i>\nвесьма непоследовательный, совершенно нелепый <i>AG</i> (<i>in tmesi</i>)."],
  ["perinde", "<big>perinde</big>\n\n<b>per–inde</b> <i>adv.</i>\nточно так же, равным образом, в равной степени (p. laudare castigareque <i>L</i>); а также, соответственно (corpore ingens, p. armatus <i>Sl</i>): p... ac (atque) <i>C</i> в той мере..., в какой; non (haud) p. <i>T, Su</i> не особенно, не слишком; p. ac si (quasi, tamquam <i>или</i> quam si) <i>C, L etc.</i> так, как будто (словно); ne mortuo quidem p. affectus est <i>Su</i> даже смерть его не особенно огорчила (Тиберия)."],
  ["perindigeo", "<big>perindigeo</big>\n\n<b>per–indigeo, —, —, ere</b>\nкрайне нуждаться <i>Tert</i>."],
  ["perindigne", "<big>perindigne</big>\n\n<b>per–indigne</b>\nв крайнем негодовании, негодующе (ferre aliquid <i>Su</i>)."],
  ["perindignus", "<big>perindignus</big>\n\n<b>per–indignus, a, um</b>\nкрайне недостойный <i>Sid</i>."],
  ["perindulgens", "<big>perindulgens</big>\n\n<b>per–indulgens, entis</b> <i>adj.</i>\nвесьма снисходительный (in aliquem <i>C</i>)."],
  ["perineos", "<big>perineos</big>\n\n<b>perineos</b> — <i>см.</i>\nperinaeon."],
  ["perinfamis", "<big>perinfamis</big>\n\n<b>per–infamis, e</b>\nобесславленный, опозоренный (vir <i>Su</i>; alicujus rei <i>Ap</i>)."],
  ["perinfirmus", "<big>perinfirmus</big>\n\n<b>per–infirmus, a, um</b>\nочень слабый <i>C, CC</i>."],
  ["peringeniosus", "<big>peringeniosus</big>\n\n<b>per–ingeniosus, a, um</b>\nвесьма одарённый <i>C</i>."],
  ["peringratus", "<big>peringratus</big>\n\n<b>per–ingratus, a, um</b>\nкрайне неблагодарный <i>Sen</i>."],
  ["periniquus", "<big>periniquus</big>\n\n<b>per–iniquus, a, um</b>\n<b>1)</b> весьма несправедливый <i>C</i>;\n<b>2)</b> крайне недовольный, негодующий (periniquo animo pati <i>C</i>)."],
  ["perinjurius", "<big>perinjurius</big>\n\n<b>per–injurius, a, um</b>\nкрайне несправедливый <i>Cato, MF</i>."],
  ["perinsignis", "<big>perinsignis</big>\n\n<b>per–insignis, e</b>\nсразу бросающийся в глаза, весьма заметный (corporis pravitates <i>C</i>)."],
  ["perinteger", "<big>perinteger</big>\n\n<b>per–integer, gra, grum</b>\nбезукоризненно честный, весьма добродетельный <i>AG</i>."],
  ["Perinthia", "<big>Perinthia</big>\n\n<b>Perinthia, ae</b> <i>f</i>\n«Перинфянка», <i>название одной из комедий Менандра</i> <i>Ter</i>."],
  ["Perinthius", "<big>Perinthius</big>\n\n<b>Perinthius, a, um</b> [Perinthus]\nперинфский <i>Ter</i>."],
  ["Perinthus", "<big>Perinthus</big>\n\n<b>Perinthus, i</b> <i>f</i>\nПеринф, <i>фракийский город на Пропонтиде, к зап. от Византия</i> (<i>впоследствии Heraclea, ныне</i> Eradi) <i>L, Mela</i>."],
  ["perinungo", "<big>perinungo</big>\n\n<b>per–inungo, —, —, ere</b>\nцеликом натереть, смазать (oleo <i>Vr</i>)."],
  ["perinvalidus", "<big>perinvalidus</big>\n\n<b>per–invalidus, a, um</b>\nочень слабый <i>QC</i>."],
  ["perinvisus", "<big>perinvisus</big>\n\n<b>per–invisus, a, um</b>\nкрайне ненавистный (alicui <i>C</i>)."],
  ["perinvitus", "<big>perinvitus</big>\n\n<b>per–invitus, a, um</b>\nвесьма неохотно делающий, крайне нерасположенный (legere aliquid <i>C</i> — <i>v. l.</i>)."],
  ["periocha", "<big>periocha</big>\n\n<b>periocha, ae</b> <i>f</i> (<i>греч.</i>)\nпериоха, сжатое изложение <i>Aus</i>."],
  ["periodeuta", "<big>periodeuta</big>\n\n<b>periodeuta, ae</b> <i>m</i> (<i>греч.</i>)\nинспектор, визитатор <i>CJ</i>."],
  ["periodicus", "<big>periodicus</big>\n\n<b>periodicus, a, um</b>\nпериодический, перемежающийся (febris <i>PM</i>)."],
  ["periodus", "<big>periodus</big>\n\n<b>periodus, i</b> <i>f</i> (<i>греч.</i>; <i>лат.</i> ambitus <i>или</i> comprehensio verborum <i>C</i>) <i>грам.</i>\nпериод <i>Q, PJ</i>."],
  ["perior", "<big>perior</big>\n\n<b>(perior), peritus sum, iri</b> <i>depon.</i> (<i>встреч. тк.</i> <i>pf.</i>)\nиспытать, изведать: nil mihi novi offerri potest, quin sim peritus <i>Pl</i> не может быть такой новости, которая не была бы мне известна."],
  ["peripateticus", "<big>peripateticus</big>\n\n<b>I peripateticus, a, um</b> (<i>греч.</i>)\nперипатетический, аристотелевский (philosophi <i>C</i>; secta <i>Col</i>).\n\n<b>II peripateticus, i</b> <i>m</i>\nперипатетик, последователь Аристотеля <i>C, AG</i>."],
  ["peripetasma", "<big>peripetasma</big>\n\n<b>peripetasma, atis</b> <i>n</i> (<i>abl. pl.</i> atis) (<i>греч.</i>)\nковёр, ковровая завеса <i>или</i> скатерть <i>C</i>."],
  ["Periphas", "<big>Periphas</big>\n\n<b>Periphas, antis</b> <i>m</i>\nПерифант:\n<b>1)</b> <i>древний царь Аттики (до Кекропа), жрец Аполлона, превращенный Юпитером в орла</i> <i>O</i>;\n<b>2)</b> <i>один из спутников Пирра</i> <i>V</i>;\n<b>3)</b> <i>один из лапифов</i> <i>O</i>."],
  ["periphrasis", "<big>periphrasis</big>\n\n<b>periphrasis, is</b> (<i>acc.</i> in, <i>abl.</i> i) <i>f</i> (<i>греч.</i>; <i>лат.</i> circumlocutio <i>и</i> circuitus eloquendi)\nперифраза, описание <i>Q</i>."],
  ["peripleumonia", "<big>peripleumonia</big>\n\n<b>peripleumonia, ae</b> <i>f</i> (<i>греч.</i>)\nвоспаление лёгких <i>PM, Veg</i>."],
  ["periplus", "<big>periplus</big>\n\n<b>periplus, i</b> <i>m</i> (<i>греч.</i>)\nплавание вдоль берегов, <i>тж.</i> описание такого путешествия <i>PM</i>."],
  ["peripneumonia", "<big>peripneumonia</big>\n\n<b>peripneumonia</b> <i>CA</i>\n= peripleumonia."],
  ["peripsema", "<big>peripsema</big>\n\n<b>peripsema, atis</b> <i>n</i> (<i>греч.</i>)\nсор, грязь <i>Vlg, Tert</i>."],
  ["peripteros", "<big>peripteros</big>\n\n<b>peripteros, on</b> <i>adj.</i> (<i>греч.</i>)\nокружённый одним рядом колонн (aedes; <i>pl.</i> aedes peripteroe <i>Vtr</i>)."],
  ["periratus", "<big>periratus</big>\n\n<b>per–iratus, a, um</b>\nочень сердитый, сильно разгневанный (alicui <i>Pl, C</i>)."],
  ["periscelis", "<big>periscelis</big>\n\n<b>periscelis, idis</b> <i>f</i> (<i>греч.</i>)\nножное кольцо (<i>женское украшение</i>) <i>H, Pt</i>."],
  ["periscellum", "<big>periscellum</big>\n\n<b>periscellum, i</b> <i>n</i> <i>Tert</i>\n= periscelis."],
  ["perisseuma", "<big>perisseuma</big>\n\n<b>perisseuma, atis</b> <i>n</i> <i>CJ</i>\n= perissochoregia."],
  ["perissochoregia", "<big>perissochoregia</big>\n\n<b>perissochoregia, ae</b> <i>f</i> (<i>греч.</i>)\nдополнительный дар <i>CTh</i>."],
  ["peristasis", "<big>peristasis</big>\n\n<b>peristasis</b> (<i>acc.</i> im) <i>f</i> (<i>греч.</i>)\nпредмет речи, тема (p. declamationis <i>Pt</i>)."],
  ["peristereon", "<big>peristereon</big>\n\n<b>peristereon, onis</b> <i>m</i> <i>и</i> <b>peristereos, i</b> <i>f</i> <i>бот.</i>\nвербена <i>PM, CA</i>."],
  ["peristroma", "<big>peristroma</big>\n\n<b>peristroma, atis</b> (<i>abl. pl.</i> <i>иногда</i> atis) <i>n</i> (<i>греч.</i>)\nпокрывало, ковёр <i>Pl, C</i>."],
  ["peristylium", "<big>peristylium</big>\n\n<b>peristyl(i)um, i</b> <i>n</i> (<i>греч.</i>)\nперистиль, <i>место, окружённое колоннами, двор с колоннадой</i> <i>Vtr, PJ, Su, C, Aus</i>."],
  ["perite", "<big>perite</big>\n\n<b>perite</b> [peritus]\nумело, со знанием дела, искусно <i>C, PJ, Sen etc.</i>"],
  ["Perithous", "<big>Perithous</big>\n\n<b>Perithous, i</b> <i>m</i>\n= Pirithous."],
  ["peritia", "<big>peritia</big>\n\n<b>peritia, ae</b> <i>f</i> [peritus]\nопытность, опыт, знание (ars et p. <i>T</i>): p. futurorum <i>Su</i> дар прозорливости."],
  ["peritonaeum", "<big>peritonaeum</big>\n\n<b>peritonaeum, i</b> <i>n</i> (<i>тж.</i> peritonaeos membrana <i>f</i>) <i>анат.</i>\nбрюшина <i>CA</i>."],
  ["peritus", "<big>peritus</big>\n\n<b>I peritus, a, um</b> [perior]\n<b>1)</b> опытный, умелый, сведущий, знающий (alicujus rei <i>C etc.</i>, aliqua re <i>VP</i>, de <i>Vr</i> <i>или</i> in aliqua re <i>Prp</i>, ad aliquid <i>C</i>): p. cantare <i>V</i> умеющий петь, искусный певец;\n<b>2)</b> искусно сделанный (fabula <i>Aus</i>).\n\n<b>II peritus, a, um</b> <i>part. pf. к</i>\npereo."],
  ["perizoma", "<big>perizoma</big>\n\n<b>perizoma, atis</b> <i>n</i> (<i>греч.</i>)\nпояс, повязка на чресла <i>Vlg, Eccl</i>."],
  ["perjeratiuncula", "<big>perjeratiuncula</big>\n\n<b>perjeratiuncula, ae</b> <i>f</i> [perjero]\nложная присяга, клятвопреступление <i>Pl</i>."],
  ["perjero", "<big>perjero</big>\n\n<b>perjero (perjuro)</b> <i>Pl, MF v. l.</i>\n= pejero."],
  ["perjucunde", "<big>perjucunde</big>\n\n<b>perjucunde</b> [perjucundus]\n<b>1)</b> с большим удовольствием (versari in aliqua re <i>C</i>);\n<b>2)</b> в прекрасном настроении (p. esse <i>C</i>)."],
  ["perjucundus", "<big>perjucundus</big>\n\n<b>per–jucundus, a, um</b>\nвесьма приятный, отрадный <i>C</i>."],
  ["perjuratiuncula", "<big>perjuratiuncula</big>\n\n<b>perjuratiuncula, ae</b> <i>f</i> <i>v. l.</i>\n= perjeratiuncula."],
  ["perjuriosus", "<big>perjuriosus</big>\n\n<b>perjuriosus, a, um</b> <i>Pl</i>\n= perjurus."],
  ["perjurium", "<big>perjurium</big>\n\n<b>perjurium, i</b> <i>n</i> [perjuro]\nклятвопреступление, нарушение клятвы <i>Ctl, C, L, V etc.</i>"],
  ["perjuro", "<big>perjuro</big>\n\n<b>per–juro (perjero)</b> <i>C</i>\n= pejero."],
  ["perjurus", "<big>perjurus</big>\n\n<b>perjurus, a, um</b> [perjurium]\nклятвопреступный, вероломный, лживый <i>Pl, C, H</i>."],
  ["perlabor", "<big>perlabor</big>\n\n<b>per–labor, lapsus sum, labi</b> <i>depon.</i>\n<b>1)</b> скользить (по, внутрь), проскальзывать, проникать (aer perlabitur per aliquid <i>Lcr</i>): atque rotis summas levibus perlabitur undas <i>V</i> и (Нептун) на лёгкой колеснице скользит по поверхности волн;\n<b>2)</b> проходить, пробегать (campos collesque <i>LM ap. Ap</i>);\n<b>3)</b> пробираться, прокрадываться (in aedem Jovis <i>L</i>; in insulam nando <i>T</i>)."],
  ["perlaetus", "<big>perlaetus</big>\n\n<b>per–laetus, a, um</b>\nвесьма радостный, весёлый <i>L</i>."],
  ["perlapsus", "<big>perlapsus</big>\n\n<b>perlapsus, a, um</b> <i>part. pf. к</i>\nperlabor."],
  ["perlate", "<big>perlate</big>\n\n<b>per–late</b>\nвесьма широко, очень обширно: p. patere <i>C</i> иметь очень большое распространение."],
  ["perlateo", "<big>perlateo</big>\n\n<b>per–lateo, latui, —, ere</b>\nвсегда оставаться скрытым (in sua turri <i>O</i>)."],
  ["perlatio", "<big>perlatio</big>\n\n<b>perlatio, onis</b> <i>f</i> [perfero]\nперенесение, претерпевание (malorum <i>Lact</i>)."],
  ["perlator", "<big>perlator</big>\n\n<b>perlator, oris</b> <i>m</i> [perfero]\nпереносчик, передатчик (epistularum <i>Amm</i>; litterarum <i>Aug</i>)."],
  ["perlatus", "<big>perlatus</big>\n\n<b>per–latus, a, um</b> <i>part. pf. к</i>\nperfero."],
  ["perlavo", "<big>perlavo</big>\n\n<b>per–lavo, lavi, —, are</b>\n<b>1)</b> тщательно промывать, <i>перен.</i> омывать (peccatorem fluctibus mersum <i>Tert</i>);\n<b>2)</b> промочить насквозь (parietes <i>Pl</i> — <i>v. l.</i>)."],
  ["perlec-", "<big>perlec-</big>\n\n<b>perlec-</b> <i>v. l.</i>\n= pellec-."],
  ["perlego", "<big>perlego</big>\n\n<b>per–lego, legi, lectum, ere</b>\n<b>1)</b> прочитывать (до конца) (librum <i>C</i>; reliquum, <i>sc.</i> audilionis <i>Ap</i>); зачитывать, оглашать (leges <i>Pl</i>): p. senatum <i>L</i> оглашать список сенаторов;\n<b>2)</b> осматривать, рассматривать (aliquid oculis <i>V, Sl</i>)."],
  ["perlepide", "<big>perlepide</big>\n\n<b>per–lepide</b>\nочень мило (narrare <i>Pl</i>)."],
  ["perlevi", "<big>perlevi</big>\n\n<b>perlevi</b> <i>pf. к</i>\nperlino."],
  ["perlevis", "<big>perlevis</big>\n\n<b>per–levis, e</b>\nочень лёгкий, <i>перен.</i> совсем неважный, незначительный (momentum <i>C, L</i>)."],
  ["perleviter", "<big>perleviter</big>\n\n<b>perleviter</b> [perlevis]\nслегка, чуть-чуть <i>C</i>."],
  ["perlibens", "<big>perlibens</big>\n\n<b>per–libens</b> (<i>арх.</i> <b>perlubens</b>), <b>entis</b> <i>adj.</i>\n<b>1)</b> охотно соглашающийся, относящийся благосклонно: ausculto p. <i>Pl</i> я слушаю с удовольствием;\n<b>2)</b> испытывающий удовольствие: me perlubente (= perlibente) <i>C</i> к моему удовольствию (что было мне очень приятно)."],
  ["perlibenter", "<big>perlibenter</big>\n\n<b>perlibenter</b> [perlibens]\nвесьма охотно, с большим удовольствием (loqui cum aliquo <i>C</i>)."],
  ["perliberalis", "<big>perliberalis</big>\n\n<b>per–liberalis, e</b>\nблаговоспитанный <i>Ter</i>."],
  ["perliberaliter", "<big>perliberaliter</big>\n\n<b>per–liberaliter</b>\n<b>1)</b> весьма щедро: ut p. ageretur <i>C</i> если уж совсем расщедриться;\n<b>2)</b> весьма милостиво, весьма благосклонно <i>C</i>."],
  ["perlibet", "<big>perlibet</big>\n\n<b>per–libet, libuit, —, ere</b> <i>impers.</i>\nвесьма приятно, крайне желательно: p. scire <i>Pl</i> очень хотелось бы узнать."],
  ["perlibratio", "<big>perlibratio</big>\n\n<b>perlibratio, onis</b> <i>f</i> [perlibro]\nприведение к одному уровню, выравнивание, нивелировка <i>Vtr</i>."],
  ["perlibro", "<big>perlibro</big>\n\n<b>per–libro, avi, atum, are</b>\n<b>1)</b> выравнивать (solum <i>Col</i>): planities perlibrata <i>Col</i> горизонтальная плоскость;\n<b>2)</b> метать, бросать (hastam, jaculum <i>Sil</i>)."],
  ["perlicio", "<big>perlicio</big>\n\n<b>perlicio</b> <i>v. l.</i>\n= pellicio."],
  ["perligo", "<big>perligo</big>\n\n<b>perligo</b> <i>v. l.</i>\n= perlego."],
  ["perlimo", "<big>perlimo</big>\n\n<b>per–limo, —, —, are</b>\nопиливать, шлифовать, <i>перен.</i> делать острее, прояснять (oculorum speciem <i>Vtr</i>)."],
  ["perlinio", "<big>perlinio</big>\n\n<b>perlinio, —, —, ire</b> <i>и</i> <b>per–lino, (levi), litum, ere</b>\n<b>1)</b> вымазывать, намазывать (aliquid melle <i>Ap</i>);\n<b>2)</b> <i>перен.</i> натирать, приукрашивать (aliquo fuco <i>Amm</i>)."],
  ["perliquidus", "<big>perliquidus</big>\n\n<b>per–liquidus, a, um</b>\nочень жидкий (<i>sc.</i> alvus <i>CC</i>)."],
  ["perlito", "<big>perlito</big>\n\n<b>per–lito, avi, atum, are</b>\nсовершать жертвоприношение при благоприятных предзнаменованиях: res divinae perlitatae <i>AG</i> не отвергнутые богами жертвы; non perlitatum <i>L</i> жертва, неугодная богам."],
  ["perlitteratus", "<big>perlitteratus</big>\n\n<b>per–litteratus, a, um</b>\nвесьма учёный <i>Hier</i>."],
  ["perlitus", "<big>perlitus</big>\n\n<b>perlitus, a, um</b> <i>part. pf. к</i>\nperlino."],
  ["perlonge", "<big>perlonge</big>\n\n<b>perlonge</b> [perlongus]\nочень далеко <i>Ter</i>."],
  ["perlonginquus", "<big>perlonginquus</big>\n\n<b>per–longinquus, a, um</b>\nочень продолжительный, весьма длительный <i>Pl</i>."],
  ["perlongum", "<big>perlongum</big>\n\n<b>perlongum</b> <i>adv.</i>\nочень долго <i>Aus</i>."],
  ["perlongus", "<big>perlongus</big>\n\n<b>per–longus, a, um</b>\nочень длинный, дальний (via <i>C</i>); очень долгий, затяжной <i>Pl</i>."],
  ["perlub-", "<big>perlub-</big>\n\n<b>perlub-</b> <i>арх.</i>\n= perlib-."],
  ["perlucens", "<big>perlucens</big>\n\n<b>perlūcēns, entis</b>\n<b>1.</b> <i>part. praes. к</i> perluceo;\n<b>2.</b> <i>adj.</i>\n&nbsp;&nbsp;<b>1)</b> прозрачный (aether tenuis et p. <i>C</i>); ажурный, сквозной (amictus <i>O</i>); просвечивающий, <i>т. е.</i> весь в трещинах <i>J</i>;\n&nbsp;&nbsp;<b>2)</b> просверлённый, трубчатый (avena <i>Tib</i>);\n&nbsp;&nbsp;<b>3)</b> ясный (oratio <i>C</i>)."],
  ["perluceo", "<big>perluceo</big>\n\n<b>per–lūceo (pellūceo), lūxī, —, ēre</b>\n<b>1)</b> просвечивать, светиться, виднеться (lux perlucens <i>L</i>; saxum a vado perlucens <i>PM</i>);\n<b>2)</b> быть прозрачным (vitrum potest p. <i>Sen</i>);\n<b>3)</b> отливать (violaceo colore <i>PM</i>);\n<b>4)</b> явствовать, быть очевидным, заметным (aliquid perlucet ex voce <i>Q</i>). — <i>См. тж.</i> perlucens."],
  ["perluciditas", "<big>perluciditas</big>\n\n<b>perlūciditās, ātis</b> <i>f</i> [perlucidus]\nпрозрачность (vitri <i>Vtr</i>)."],
  ["perlucidulus", "<big>perlucidulus</big>\n\n<b>perlūcidulus, a, um</b> [<i>demin. к</i> perlucidus]\nдовольно прозрачный: lapis p. <i>Ctl</i> жемчужина."],
  ["perlucidus", "<big>perlucidus</big>\n\n<b>per–lūcidus, a, um</b>\n<b>1)</b> прозрачный (membrana <i>C</i>; vitrum <i>Vlg</i>);\n<b>2)</b> одетый в ажурные ткани (homo <i>Sen</i>);\n<b>3)</b> ясный, яркий (stella <i>C</i>)."],
  ["perluctuosus", "<big>perluctuosus</big>\n\n<b>per–lūctuōsus, a, um</b>\nвесьма печальный, горестный (funus <i>C</i>)."],
  ["perluo", "<big>perluo</big>\n\n<b>per–luo, luī, lūtum, ere</b>\n<b>1)</b> вымывать, ополаскивать (vasa aqua <i>Col</i>); умывать (manus unda <i>O</i>);\n<b>2)</b> <i>pass.</i> perlui купаться (in flumine <i>Cs</i>); sudore perlutus <i>Ap</i> весь в поту, обливаясь потом."],
  ["perlusorius", "<big>perlusorius</big>\n\n<b>per–lūsōrius, a, um</b>\nпостроенный на тайном соглашении (judicium <i>Dig</i> — <i>v. l.</i>)."],
  ["perlustro", "<big>perlustro</big>\n\n<b>per–lūstro, āvī, ātum, āre</b>\n<b>1)</b> проходить, обходить (agros <i>L</i>);\n<b>2)</b> обозревать, окидывать (aliquid oculis <i>L, Sil</i> <i>или</i> animo <i>C</i>);\n<b>3)</b> <i>культ.</i> очищать (aliquid sulphure <i>Col</i>);\n<b>4)</b> внимать, благоговейно слушать (p. solenni prece dicta, <i>sc.</i> Sibyllae <i>St</i>)."],
  ["perlutus", "<big>perlutus</big>\n\n<b>perlūtus, a, um</b> <i>part. pf. к</i>\nperluo."],
  ["permaceo", "<big>permaceo</big>\n\n<b>per–maceo, —, —, ere</b>\nбыть очень тонким, слабым <i>Enn</i>."],
  ["permacer", "<big>permacer</big>\n\n<b>per–macer, era, erum</b>\nвесьма тощий (caro <i>CC</i>)."],
  ["permacero", "<big>permacero</big>\n\n<b>per–macero, —, —, are</b>\nсовершенно размягчать, размачивать (calculos <i>Vtr</i>)."],
  ["permadefacio", "<big>permadefacio</big>\n\n<b>per–madefacio, feci, —, ere</b>\nсмачивать насквозь, <i>перен.</i> совершенно смягчать (cor alicujus <i>Pl</i>)."],
  ["permadesco", "<big>permadesco</big>\n\n<b>per–madesco, madui, —, ere</b>\n<b>1)</b> сильно промокать, размокнуть (terra pluviis permaduit <i>Col</i>);\n<b>2)</b> расслабляться, изнеживаться (animi permadescunt felicitate <i>Sen</i>)."],
  ["permagnificus", "<big>permagnificus</big>\n\n<b>per–magnificus, a, um</b>\nвеликолепный, пышный (convivium <i>Vlg</i>)."],
  ["permagnus", "<big>permagnus</big>\n\n<b>per–magnus, a, um</b>\nочень большой, огромный (hereditas <i>C</i>; numerus <i>Cs</i>): permagno <i>C</i> за очень высокую цену, очень дорого; permagni interest <i>C</i> весьма важно."],
  ["permale", "<big>permale</big>\n\n<b>per–male</b>\nвесьма несчастливо (pugnare <i>C</i> — <i>v. l.</i>)."],
  ["permananter", "<big>permananter</big>\n\n<b>permananter</b> [permano]\nдалеко вглубь <i>или</i> беспрерывно (pervalere <i>Lcr</i>)."],
  ["permanasco", "<big>permanasco</big>\n\n<b>permanasco, —, —, ere</b> [permano]\nпритекать, <i>перен.</i> достигать, доходить (ad aliquem <i>Pl</i>)."],
  ["permanens", "<big>permanens</big>\n\n<b>permanens, entis</b>\n<b>1.</b> <i>part. praes. к</i> permaneo;\n<b>2.</b> <i>adj.</i> постоянный, неослабевающий (vox <i>C</i>)."],
  ["permaneo", "<big>permaneo</big>\n\n<b>per–maneo, mansi, (mansum), ere</b>\nоставаться, пребывать (complures dies <i>Cs</i>; eodem loco <i>bAfr</i>; in armis <i>Cs</i>; in sua pristina sententia <i>C</i>); сохраняться, удерживаться (mos permanet <i>C</i>; p. ad extremos rogos <i>O</i>); длиться, продолжаться (ad immensum tempus <i>C</i>)."],
  ["permano", "<big>permano</big>\n\n<b>per–mano, avi, atum, are</b>\n<b>1)</b> протекать (permanat aquarum umor <i>Lcr</i>); проникать (permanat frigus ad ossa <i>Lcr</i>);\n<b>2)</b> достигать, доходить (ad aures alicujus <i>C</i>): ad sensum non permanans <i>C</i> непонятный, с неуловимым смыслом."],
  ["permansio", "<big>permansio</big>\n\n<b>permansio, onis</b> <i>f</i> [permaneo]\n<b>1)</b> постоянное нахождение (в одном месте), пребывание (diuturna <i>Amm</i>);\n<b>2)</b> непоколебимость: p. in una sententia <i>C</i> постоянство во мнении, устойчивость взгляда."],
  ["permarinus", "<big>permarinus</big>\n\n<b>per–marinus, a, um</b>\nсопровождающий в морских путешествиях (lares <i>C</i>)."],
  ["permaturesco", "<big>permaturesco</big>\n\n<b>per–maturesco, maturui, —, ere</b>\nокончательно созревать, поспевать <i>CC, O, Col</i>."],
  ["permaturus", "<big>permaturus</big>\n\n<b>per–maturus, a, um</b>\nсовершенно спелый (oleae <i>CC</i>; bacae <i>Col</i>)."],
  ["permaxime", "<big>permaxime</big>\n\n<b>per–maxime</b>\nв высшей степени, больше всего (cavere <i>Cato</i> — <i>v. l.</i>)."],
  ["permeabilis", "<big>permeabilis</big>\n\n<b>per–meabilis, e</b>\nпроходимый, проезжий (plaustro <i>Sol</i>)."],
  ["permeator", "<big>permeator</big>\n\n<b>permeator, oris</b> <i>m</i> [permeo]\nпроникающий (spiritus p. universitatis <i>Cleanthes ap. Tert</i>)."],
  ["permeatus", "<big>permeatus</big>\n\n<b>permeatus, us</b> <i>m</i> [permeo]\nпрохождение <i>PM</i>."],
  ["permediocris", "<big>permediocris</big>\n\n<b>per–mediocris, e</b>\nвесьма умеренный (motus <i>C</i>)."],
  ["permeditatus", "<big>permeditatus</big>\n\n<b>per–meditatus, a, um</b>\nхорошо подготовленный (fidicina <i>Pl</i>)."],
  ["permensus", "<big>permensus</big>\n\n<b>permensus, a, um</b> <i>part. pf. к</i>\npermetior; <i>иногда</i> <i>pass.</i> измеренный (opus <i>Col</i>); пройденный (spatium <i>Ap</i>)."],
  ["permeo", "<big>permeo</big>\n\n<b>per–meo, avi, atum, are</b>\n<b>1)</b> проходить, пробегать (circulus, quem permeat sol <i>Ap</i>); проезжать, проходить <i>или</i> проплывать (maria ac terras <i>O</i>); протекать, пересекать (Euphrates mediam Babylonem permeat <i>PM</i>);\n<b>2)</b> достигать, долетать (sagittae in hostes permeabant <i>T</i>);\n<b>3)</b> проникать (intellegentia per omnia permeat <i>C</i> — <i>v. l.</i>); пронзать (sime in terris, quod non permeat Tyrrhena hasta? <i>Sil</i>)."],
  ["permereo", "<big>permereo</big>\n\n<b>per–mereo, ui, —, ere</b>\nпроходить военную службу (sole sub omni <i>St</i>)."],
  ["Permessis", "<big>Permessis</big>\n\n<b>Permessis, idis</b> <i>и</i> <b>idos</b> <i>f</i> [Permessus]\nпермесская (lympha <i>M</i>)."],
  ["Permessius", "<big>Permessius</big>\n\n<b>Permessius, a, um</b> [Permessus]\nпермесский (fons <i>Cld</i>)."],
  ["Permessus", "<big>Permessus</big>\n\n<b>Permessus, i</b> <i>m</i>\nПермесс, <i>река в Беотии, посвященная Аполлону и Музам, берущая начало на горе Геликон и впадающая в Копаидское озеро</i> <i>V</i>."],
  ["permetior", "<big>permetior</big>\n\n<b>per–metior, mensus sum, iri</b> <i>depon.</i>\n<b>1)</b> мерить, измерять (magnitudinem alicujus rei <i>C</i>);\n<b>2)</b> проходить (viam ad aliquem <i>Pl</i>); переплывать (classibus aequor <i>V</i>);\n<b>3)</b> прожить, просуществовать (saecula <i>M</i>). — <i>См. тж.</i> permensus."],
  ["permicies", "<big>permicies</big>\n\n<b>permicies (-ties), ei</b> <i>f</i> <i>Enn, Pl, LM</i>\n= pernicies."],
  ["permilito", "<big>permilito</big>\n\n<b>per–milito, —, —, are</b>\nотбывать срок военной службы <i>Dig</i>."],
  ["permingo", "<big>permingo</big>\n\n<b>per–mingo, minxi, —, ere</b>\n<b>1)</b> орошать мочой (lectum <i>LM</i>);\n<b>2)</b> <i>H</i> = stupro."],
  ["perminimus", "<big>perminimus</big>\n\n<b>per–minimus, a, um</b>\nсовсем маленький (pars <i>Jvc</i>)."],
  ["permirabilis", "<big>permirabilis</big>\n\n<b>permirabilis, e</b> <i>Aug</i>\n= permirus."],
  ["permirandus", "<big>permirandus</big>\n\n<b>per–mirandus, a, um</b> <i>AG</i>\n(<i>in tmesi</i>)= permirus."],
  ["permirus", "<big>permirus</big>\n\n<b>per–mirus, a, um</b>\nвесьма удивительный, весьма странный <i>C</i>."],
  ["permisceo", "<big>permisceo</big>\n\n<b>per–misceo, miscui, mistum (mixtum), ere</b>\n<b>1)</b> смешивать, перемешивать (aliquid aliqua re, cum aliqua re <i>или</i> rei alicui <i>C, Cs, Col etc.</i>): alicujus consiliis permixtus <i>T</i> замешанный в чьи-л. планы; lituo tubae permixtus sonitus <i>H</i> звуки трубы, смешанные со звуками рожка; fructus acerbitate permixti <i>C</i> плоды с горечью;\n<b>2)</b> расстраивать (permixtus dolore <i>Ap</i>);\n<b>3)</b> путать (jura divina et humana <i>Cs</i>);\n<b>4)</b> поражать (omnia timore <i>Fl</i>); приводить в замешательство (Graeciam <i>C</i>);\n<b>5)</b> вонзать: alicui totum ensem p. <i>Sil</i> вонзать в кого-л. меч по рукоятку."],
  ["permisi", "<big>permisi</big>\n\n<b>permisi</b> <i>pf. к</i>\npermitto."],
  ["permissio", "<big>permissio</big>\n\n<b>permissio, onis</b> <i>f</i> [permitto]\n<b>1)</b> <i>воен.</i> безусловная сдача (капитуляция) <i>L</i>;\n<b>2)</b> <i>ритор.</i> передача на чьё-л. решение, предоставление чьему-л. усмотрению <i>rhH, C, Q</i>;\n<b>3)</b> разрешение, позволение (alicujus rei <i>C</i>)."],
  ["permissor", "<big>permissor</big>\n\n<b>permissor, oris</b> <i>m</i> [permitto]\nразрешающий, допускающий (p. mali <i>Tert</i>)."],
  ["permissum", "<big>permissum</big>\n\n<b>permissum, i</b> <i>n</i> [permitto]\nразрешение <i>H</i>."],
  ["permissus", "<big>permissus</big>\n\n<b>permissus, us</b> (<i>встреч. преим.</i> <i>abl.</i>) <i>m</i> [permitto]\nпозволение: permissu tuo <i>C</i> с твоего разрешения; permissu legis <i>C</i> на основании закона; citra permissum praetoris <i>Dig</i> без разрешения претора."],
  ["permitialis", "<big>permitialis</big>\n\n<b>permitialis, e</b> <i>Lcr</i>\n= pernicialis."],
  ["permities", "<big>permities</big>\n\n<b>permities, ei</b> <i>f</i> <i>Pl, Enn, LM</i>\n= pernicies."],
  ["permitis", "<big>permitis</big>\n\n<b>per–mitis, e</b>\nочень нежный (sorbum <i>Col</i>)."],
  ["permitto", "<big>permitto</big>\n\n<b>per–mitto, misi, missum, ere</b>\n<b>1)</b> пускать, метать, бросать (tela <i>Hirt</i>; saxum in hostes <i>O</i>);\n<b>2)</b> посылать, отправлять (aliquid trans maria <i>Col</i>): rei judicium senatui p. <i>T</i> передать вопрос на рассмотрение сената;\n<b>3)</b> отпускать: p. habenas equo <i>Tib, SenT</i> давать повод коню; p. vela ventis <i>Q</i> распускать паруса по ветру;\n<b>4)</b> устремлять, гнать (equum in hostem <i>L</i>): se p. in hostem <i>Hirt</i> броситься на врага; se p. стремиться (ad famam <i>AG</i>);\n<b>5)</b> оказывать, проявлять (bonitatem in aliquem <i>Sen</i>);\n<b>6)</b> оставлять: p. aliquem in suam quietem <i>Ap</i> оставить кого-л. в покое;\n<b>7)</b> предоставлять, поручать, доверять, вверять (p. se potestati alicujus <i>Cs</i>; cetera divis p. <i>H</i>; p. se totum coelestium arbitrio <i>VM</i>): p. tribunatum <i>L</i> предоставить трибунату свободу действий; p. consulibus rem publicam <i>C</i> дать консулам чрезвычайные полномочия; p. se in deditionem <i>или</i> in dicionem alicujus <i>L</i> сдаться на чью-л. волю; aliquid alicui dotale p. <i>V</i> принести что-л. кому-л. в приданое; p. aliquid flammae <i>V</i> предать что-л. огню;\n<b>8)</b> допускать, разрешать (lex jubet aut permittit aut vetat <i>C</i>): permittente senatu <i>Eutr</i> с разрешения сената;\n<b>9)</b> отказываться: inimicitias patribus conscriptis p. <i>C</i> во внимание к сенаторам отказаться от вражды; p. aliquid iracundiae alicujus <i>C</i> снисходительно отнестись к чему-л. ввиду чьего-л. гнева."],
  ["permixte", "<big>permixte</big>\n\n<b>permixte</b> [permixtus]\nсмешанно, вперемешку <i>C</i>."],
  ["permixtim", "<big>permixtim</big>\n\n<b>permixtim</b> <i>C, Eccl</i>\n= permixte."],
  ["permixtio", "<big>permixtio</big>\n\n<b>permixtio, onis</b> <i>f</i> [permisceo]\n<b>1)</b> смешение, беспорядок <i>C etc.</i>;\n<b>2)</b> смесь, микстура <i>Pall</i>;\n<b>3)</b> смятение (terrae <i>Sl</i>; rei publicae <i>AV</i>)."],
  ["permixtus", "<big>permixtus</big>\n\n<b>permixtus, a, um</b> <i>part. pf. к</i>\npermisceo."],
  ["permodestus", "<big>permodestus</big>\n\n<b>per–modestus, a, um</b>\nочень сдержанный, весьма скромный (homo <i>C</i>; sensus verborum <i>T</i>)."],
  ["permodice", "<big>permodice</big>\n\n<b>permodice</b> [permodicus]\nвесьма умеренно <i>Col</i>."],
  ["permodicus", "<big>permodicus</big>\n\n<b>per–modicus, a, um</b>\nочень умеренный, маленький (locus <i>Su</i>); крайне скудный (res familiaris <i>C</i>)."],
  ["permoleste", "<big>permoleste</big>\n\n<b>permoleste</b> [permolestus]\nс большим неудовольствием, очень тяжело (p. ferre aliquid <i>C</i>)."],
  ["permolestus", "<big>permolestus</big>\n\n<b>per–molestus, a, um</b>\nочень тягостный <i>C</i>."],
  ["permollis", "<big>permollis</big>\n\n<b>per–mollis, e</b>\nочень мягкий, очень нежный <i>Q</i>."],
  ["permolo", "<big>permolo</big>\n\n<b>per–molo, —, —, ere</b>\nмолоть, перемалывать, толочь, <i>перен.</i> бесчестить (uxores alienas <i>H</i>)."],
  ["permonstro", "<big>permonstro</big>\n\n<b>per–monstro, —, —, are</b>\nясно (воочию) показывать (aliquid <i>Amm</i>)."],
  ["permotio", "<big>permotio</big>\n\n<b>permotio, onis</b> <i>f</i> [permoveo]\nдвижение, волнение (animi <i>C</i>); душевное движение, аффект <i>C, Aug</i>: permotionis causa <i>C</i> чтобы подействовать на (растрогать) кого-л.; mentis p. <i>C</i> вдохновение."],
  ["permoveo", "<big>permoveo</big>\n\n<b>per–moveo, movi, motum, ere</b>\n<b>1)</b> приводить в сильное движение, волновать (mare permotum <i>Lcr</i>);\n<b>2)</b> глубоко вскапывать (terram <i>Col</i>);\n<b>3)</b> побуждать, склонять (aliquem pollicitationibus <i>Cs</i>): permotus studio dominandi <i>Sl</i> движимый желанием властвовать; permotus amore <i>C</i> под влиянием (из) любви;\n<b>4)</b> возбуждать, вызывать (metum, iras <i>T</i>): p. animum alicujus miseratione <i>Just</i> разжалобить (растрогать) кого-л.; permotus ad miserationem <i>T</i> <i>и</i> misericordia <i>Pt</i> движимый состраданием;\n<b>5)</b> тревожить, расстраивать, выводить из равновесия (aliquem <i>C etc.</i>): permoveri animo <i>Cs</i> пасть духом; permoveri labore itineris <i>Cs</i> досадовать на трудность перехода."],
  ["permulceo", "<big>permulceo</big>\n\n<b>per–mulceo, mulsi, mulsum (mulctum), ere</b>\n<b>1)</b> гладить, поглаживать (comas <i>O</i>);\n<b>2)</b> слегка касаться (aliquem manu <i>O</i>); овевать (aram flatu <i>Poeta ap. C</i>);\n<b>3)</b> ласкать, тешить, услаждать (aliquem <i>C</i>; aures <i>C</i>);\n<b>4)</b> смягчать, успокаивать (animum alicujus <i>Cs</i>; iram alicujus <i>L</i>; aliquem mitibus verbis <i>T</i>)."],
  ["permulto", "<big>permulto</big>\n\n<b>per–multo</b>\nгораздо, (на)много (clarior <i>C</i>)."],
  ["permultum", "<big>permultum</big>\n\n<b>I per–multum</b> <i>adv.</i>\nмного, значительно (p. ante <i>C</i>).\n\n<b>II permultum, i</b> <i>n</i> [permultus] (<i>преим.</i> <i>pl.</i>)\nочень многое, множество <i>Pl, C, H</i>."],
  ["permultus", "<big>permultus</big>\n\n<b>per–multus, a, um</b> (<i>преим.</i> <i>pl.</i>)\nвесьма многие (viri <i>C</i>; partes <i>V</i>); et alia permulta <i>C</i> и многое-многое другое."],
  ["permundo", "<big>permundo</big>\n\n<b>per–mundo, —, —, are</b>\nсовершенно очищать (aream suam <i>Vlg</i>)."],
  ["permundus", "<big>permundus</big>\n\n<b>per–mundus, a, um</b>\nочень чистоплотный, опрятный (volucres <i>Vr</i>)."],
  ["permunio", "<big>permunio</big>\n\n<b>per–munio, ivi, itum, ire</b>\n<b>1)</b> закончить фортификационные сооружения, завершить работы по укреплению: quae munimenta inchoaverat, permunit <i>L</i> (Сципион) заканчивает начатые им прежде укрепления;\n<b>2)</b> сильно укреплять (locorum opportuna <i>T</i>)."],
  ["permutabilis", "<big>permutabilis</big>\n\n<b>per–mutabilis, e</b>\nизменчивый <i>Amm</i>."],
  ["permutatim", "<big>permutatim</big>\n\n<b>per–mutatim</b>\nнаоборот, в обратном отношении <i>Boet</i>."],
  ["permutatio", "<big>permutatio</big>\n\n<b>permutatio, onis</b> <i>f</i> [permuto]\n<b>1)</b> перемена, изменение (defensionis <i>Q</i>); смена (coloris <i>C</i>): p. jumentorum <i>Amm</i> смена (перекладных) лошадей || обмен (captivorum <i>L, Eutr</i>): p. mercium <i>T</i> меновая торговля || товарообмен (partim emptiones, partim permutationes <i>C</i>);\n<b>2)</b> денежный перевод посредством векселя (p. publica <i>C</i>)."],
  ["permuto", "<big>permuto</big>\n\n<b>per–muto, avi, atum, are</b>\n<b>1)</b> совершенно изменять (vultum <i>Pt</i>; sententiam <i>C</i>; statum rei publicae <i>C</i>): cum argento domum p. <i>погов. Pl</i> сбежать, захватив (чужие) деньги;\n<b>2)</b> менять, обменивать (aliquid inter se <i>Pl</i>); производить товарообмен: p. aliquid aliqua re <i>H, PM, M</i> обменять что-л. на что-л.;\n<b>3)</b> выкупать (aliquem triginta milibus <i>QC</i>);\n<b>4)</b> производить размен, разменивать (denariorum sedecim assibus <i>PM</i>);\n<b>5)</b> делать денежный перевод: p. Athenas <i>C</i> послать переводную расписку (вексель) в Афины; aliquid cum aliquo p. <i>C</i> получить что-л. от кого-л. в порядке денежного перевода;\n<b>6)</b> покупать (equos talentis auri <i>PM</i>);\n<b>7)</b> поворачивать (aliquid in contrarium <i>PM</i>): permutata ratione <i>PM</i> в обратном порядке, наоборот."],
  ["perna", "<big>perna</big>\n\n<b>perna, ae</b> <i>f</i> (<i>греч.</i>)\n<b>1)</b> задняя часть бедра (pernas alicui succidere <i>Enn</i>);\n<b>2)</b> <i>у животных</i> задняя нога, ляжка <i>Cato etc.</i>; окорок (fumosa <i>H</i>);\n<b>3)</b> разновидность морского моллюска <i>PM</i>;\n<b>4)</b> утолщённая часть древесного побега у самого ствола, комель <i>PM</i>."],
  ["pernavigo", "<big>pernavigo</big>\n\n<b>per–navigo, —, —, are</b>\nпроплывать (<i>на судне</i>) (pars vergens in Caspium mare pernavigata <i>PM</i>)."],
  ["pernecessarius", "<big>pernecessarius</big>\n\n<b>per–necessarius, a, um</b>\n<b>1)</b> весьма нужный, крайне необходимый (res <i>Dig</i>): tempus pernecessarium <i>C</i> обстоятельства, не терпящие отлагательства;\n<b>2)</b> весьма близкий, находящийся в близких отношениях (homo <i>C</i>); близкий друг (pernecessarii mei <i>C</i>)."],
  ["pernecesse", "<big>pernecesse</big>\n\n<b>per–necesse</b>\nвесьма нужно, совершенно необходимо <i>C</i>."],
  ["perneco", "<big>perneco</big>\n\n<b>per–neco, avi, —, are</b>\nубивать, умерщвлять (aliquem <i>Aug</i>)."],
  ["pernego", "<big>pernego</big>\n\n<b>per–nego, avi, atum, are</b>\n<b>1)</b> упорно отрицать <i>Pl, Ter, C, Su etc.</i>;\n<b>2)</b> наотрез отказывать, решительно отклонять <i>M</i>: Catoni praeturam negavit, consulatum pernegavit <i>Sen</i> Катону (народ) отказал в претуре, в консульстве же отказал наотрез."],
  ["perneo", "<big>perneo</big>\n\n<b>per–neo, evi, etum, ere</b>\nспрясть до конца (vita nondum perneta <i>Sid</i>): quum mihi supremos Lachesis perneverit annos <i>M</i> когда Лахесис спрядёт последние нити моей жизни."],
  ["perniciabilis", "<big>perniciabilis</big>\n\n<b>perniciabilis, e</b> <i>L, QC, T</i>\n= perniciosus."],
  ["pernicialis", "<big>pernicialis</big>\n\n<b>pernicialis, e</b> <i>Lcr, PM</i>\n= perniciosus."],
  ["pernicies", "<big>pernicies</big>\n\n<b>pernicies, ei</b> (<i>арх. gen.</i> i <i>и</i> <i>dat.</i> e <i>и</i> i) <i>f</i> [per+ nex]\nуничтожение, гибель (alicui perniciei esse <i>Nep</i>); <i>перен.</i> чума, порча (p. adulescentum <i>Ter</i>; Verres p. Siciliae <i>C</i>): p. macelli <i>H</i> ненасытная утроба, обжора."],
  ["perniciose", "<big>perniciose</big>\n\n<b>perniciose</b> [perniciosus]\nгибельно, пагубно, роковым образом <i>C, PM, Aug</i>."],
  ["perniciosus", "<big>perniciosus</big>\n\n<b>perniciosus, a, um</b> [pernicies]\nгибельный, губительный, пагубный, опасный (morbus <i>C</i>; hostis <i>Sl, Sen, Q</i>; natura serpentium <i>Sl</i>; numina <i>O</i>)."],
  ["pernicitas", "<big>pernicitas</big>\n\n<b>pernicitas, atis</b> <i>f</i> [pernix]\nпроворство, ловкость, быстрота (pedum <i>Pl, L</i>; equorum <i>QC</i>)."],
  ["perniciter", "<big>perniciter</big>\n\n<b>perniciter</b> [pernix]\nпроворно, ловко, быстро <i>Pl, Ctl, L etc.</i>"],
  ["perniger", "<big>perniger</big>\n\n<b>per–niger, gra, grum</b>\nсовершенно чёрный (oculi <i>Pl</i>)."],
  ["pernimium", "<big>pernimium</big>\n\n<b>per–nimium</b> <i>adv.</i>\nчрезвычайно (severus <i>Dig</i>): p. inter vos interest <i>Ter</i> между вами громадная разница."],
  ["pernio", "<big>pernio</big>\n\n<b>pernio, onis</b> <i>m</i> [perna]\nотмороженное место на ноге <i>PM</i>."],
  ["perniteo", "<big>perniteo</big>\n\n<b>per–niteo, —, —, ere</b>\nярко блестеть <i>Scr, Mela</i>."],
  ["pernities", "<big>pernities</big>\n\n<b>pernities, ei</b> <i>f</i> <i>v. l.</i>\n= pernicies."],
  ["perniunculus", "<big>perniunculus</big>\n\n<b>perniunculus, i</b> <i>m</i> <i>PM demin. к</i>\npernio."],
  ["pernix", "<big>pernix</big>\n\n<b>pernix, icis</b> <i>adj.</i>\n<b>1)</b> проворный, ловкий, скорый, быстрый (p. pedibus <i>Pl</i> <i>или</i> plantis <i>V</i>): pernicibus alis <i>V</i> быстрокрылый; amata relinquere p. <i>H</i> с лёгкостью покидающий то, что (недавно) любил || быстротечный (tempus <i>Sen</i>);\n<b>2)</b> выносливый (taurus <i>V</i> — <i>v. l.</i> pernox)."],
  ["pernobilis", "<big>pernobilis</big>\n\n<b>per–nobilis, e</b>\n<b>1)</b> весьма известный, знаменитый (epigramma <i>C</i>); славный (Abydos Leandri amore p. <i>Mela</i>);\n<b>2)</b> знатный (matrona <i>Lampr</i>)."],
  ["pernoctatio", "<big>pernoctatio</big>\n\n<b>pernoctatio, onis</b> <i>f</i> [pernocto]\nпроведение ночей <i>Ambr</i>."],
  ["pernocto", "<big>pernocto</big>\n\n<b>per–nocto, avi, (atum), are</b> [nox]\n<b>1)</b> ночевать, проводить ночь (foris <i>Ter</i>; extra moenia <i>L</i>);\n<b>2)</b> <i>перен.</i> не расставаться даже ночью, никогда не покидать (haec studia pernoctant nobiscum <i>C</i>)."],
  ["Pernonides", "<big>Pernonides</big>\n\n<b>Pernonides, ae</b> <i>m</i> [perna] <i>шутл.</i>\n«сын окорока»: laridum P. <i>Pl</i> кусок ветчины."],
  ["pernosco", "<big>pernosco</big>\n\n<b>per–nosco, novi, notum, ere</b>\nосновательно знакомиться (p. aliquid <i>Pl, C</i>): pernovi <i>Pl</i> я хорошо знаю."],
  ["pernosti", "<big>pernosti</big>\n\n<b>pernosti</b> <i>Ter</i>\n= pernovisti (<i>к</i> pernosco)."],
  ["pernotesco", "<big>pernotesco</big>\n\n<b>per–notesco, notui, —, ere</b>\nстановиться общеизвестным <i>T, Q</i>: <i>impers.</i> pernotuit <i>T</i> всем стало известно."],
  ["pernoto", "<big>pernoto</big>\n\n<b>per–noto, —, —, are</b>\nтщательно отмечать <i>Boet</i>."],
  ["pernotus", "<big>pernotus</big>\n\n<b>per–notus, a, um</b>\nвесьма известный, хорошо знакомый (alicui <i>QC</i>; aliqua re <i>Mela</i>)."],
  ["pernox", "<big>pernox</big>\n\n<b>per–nox, noctis</b> <i>adj.</i> (<i>встреч. только</i> <i>nom. и abl.</i>)\nпродолжающийся всю ночь (perdius et p. <i>Ap, Amm</i>): luna p. erat <i>L</i> луна светила всю ночь; luna pernocte <i>O</i> при свете луны."],
  ["pernoxius", "<big>pernoxius</big>\n\n<b>per–noxius, a, um</b>\nкрайне опасный (alicui <i>Mela</i>)."],
  ["pernumero", "<big>pernumero</big>\n\n<b>per–numero, avi, atum, are</b>\n<b>1)</b> отсчитывать, выплачивать (pecuniam <i>L</i>; argentum <i>Pl</i>);\n<b>2)</b> пересчитывать (opes <i>M</i>; res hereditarias <i>Dig</i>)."],
  ["pero", "<big>pero</big>\n\n<b>I pero, onis</b> <i>m</i>\nперон, <i>сапожок из сыромятной кожи.</i> (<i>солдатский и крестьянский</i>) <i>Cato, V, J, Sid</i>.\n\n<b>II Pero, us</b> <i>f</i>\nПеро, <i>дочь Нелея, сестра Нестора и Периклимена, вышедшая замуж за Бианта, брат которого, Мелампод, добыл для Нелея скот Ификла</i> <i>Prp</i>."],
  ["perobscurus", "<big>perobscurus</big>\n\n<b>per–obscurus, a, um</b>\nочень тёмный (quaestio <i>C</i>); смутный, неясный (fama <i>L</i>)."],
  ["perodi", "<big>perodi</big>\n\n<b>per–odi, —, odisse</b>\nсильно ненавидеть (ali quid <i>Man</i>). — <i>См. тж.</i> perosus."],
  ["perodiosus", "<big>perodiosus</big>\n\n<b>per–odiosus, a, um</b>\nкрайне ненавистный, отвратительный <i>C</i>."],
  ["perofficiose", "<big>perofficiose</big>\n\n<b>per–officiose</b>\nвесьма любезно (aliquem observare <i>C</i>)."],
  ["peroleo", "<big>peroleo</big>\n\n<b>per–oleo, olevi, —, ere</b>\nиздавать резкий неприятный запах (cadavera perolent <i>Lcr</i>)."],
  ["perolesco", "<big>perolesco</big>\n\n<b>perolesco, levi, —, ere</b>\nсильно расти <i>LM</i>."],
  ["peronatus", "<big>peronatus</big>\n\n<b>peronatus, a, um</b> [pero I]\nобутый в (простые) сапоги <i>Pers</i>."],
  ["peropacus", "<big>peropacus</big>\n\n<b>per–opacus, a, um</b>\nочень тёмный (spelunca <i>Lact</i>)."],
  ["peropportune", "<big>peropportune</big>\n\n<b>per–opportune</b>\nвесьма удачно, очень кстати (venire <i>C</i>)."],
  ["peropportunus", "<big>peropportunus</big>\n\n<b>per–opportunus, a, um</b>\nвесьма удачный, очень кстати случившийся (пришедшийся) (deversorium <i>C</i>; victoria <i>C, L</i>)."],
  ["peroptato", "<big>peroptato</big>\n\n<b>per–optato</b> <i>adv.</i>\nв полном соответствии с пожеланиями, весьма кстати <i>C</i>."],
  ["peropus", "<big>peropus</big>\n\n<b>per–opus</b> <i>adv.</i>\nкрайне нужно <i>Ter</i>."],
  ["peroratio", "<big>peroratio</big>\n\n<b>peroratio, onis</b> <i>f</i> [peroro]\n<b>1)</b> пространная речь <i>PM</i>;\n<b>2)</b> заключительная часть речи, заключение, эпилог <i>C</i>;\n<b>3)</b> заключительное слово <i>C</i>."],
  ["peroriga", "<big>peroriga</big>\n\n<b>per–origa, ae</b> <i>m</i> [auriga]\nтабунщик <i>или</i> конюх <i>Vr</i> (<i>v. l.</i>)."],
  ["perornatus", "<big>perornatus</big>\n\n<b>per–ornatus, a, um</b>\nзамечательно изящный, изысканный (in dicendo <i>C</i>)."],
  ["perorno", "<big>perorno</big>\n\n<b>per–orno, avi, atum, are</b>\nбыть постоянным <i>или</i> лучшим украшением, украшать собой (senatum <i>T</i>)."],
  ["peroro", "<big>peroro</big>\n\n<b>per–oro, avi, atum, are</b>\n<b>1)</b> заканчивать речь, говорить в заключение (nunc est mihi perorandum <i>C</i>): totum crimen p. <i>C</i> закончить обвинительную речь;\n<b>2)</b> произносить заключительную речь (accusatores et testes perorabant <i>T</i>); говорить последним (perorandi locum alicui relinquere <i>C</i>);\n<b>3)</b> обстоятельно говорить, подробно высказываться (aliquid <i>C</i> <i>или</i> de aliqua re <i>Nep</i>)."],
  ["perosculor", "<big>perosculor</big>\n\n<b>per–osculor, —, ari</b> <i>depon.</i>\nперецеловать (aliquos <i>M</i>)."],
  ["perose", "<big>perose</big>\n\n<b>perose</b>\nк великому неудовольствию (слушателей) <i>Ap</i> (<i>v. l.</i>)."],
  ["perosus", "<big>perosus</big>\n\n<b>perosus, a, um</b> [per + odi]\n<b>1)</b> страстно ненавидящий, возненавидевший (longum exsilium <i>O</i>): p. lucem <i>O</i> боящийся света <i>или</i> <i>V</i> возненавидевший жизнь;\n<b>2)</b> крайне ненавистный <i>Tert, Jvc</i>."],
  ["perpaco", "<big>perpaco</big>\n\n<b>per–paco, avi, atum, are</b>\nводворять мир, умиротворять (Brennos <i>Fl</i>; omnibus perpacatis <i>L</i>)."],
  ["perpallidus", "<big>perpallidus</big>\n\n<b>per–pallidus, a, um</b>\nсовершенно бледный (color, <i>sc.</i> monentis <i>CC</i>)."],
  ["perparce", "<big>perparce</big>\n\n<b>per–parce</b>\nочень экономно, скупо (facere sumptum <i>Ter</i>)."],
  ["perparum", "<big>perparum</big>\n\n<b>per–parum</b> <i>adv.</i>\nкрайне мало <i>Veg, Aug</i>."],
  ["perparvulus", "<big>perparvulus</big>\n\n<b>perparvulus, a, um</b> [<i>demin. к</i> perparvus]\nсовсем маленький (sigilla <i>C</i>)."],
  ["perparvum", "<big>perparvum</big>\n\n<b>perparvum</b> <i>C</i>\n= perparum."],
  ["perparvus", "<big>perparvus</big>\n\n<b>per–parvus, a, um</b>\nочень маленький (pars loci <i>Pl</i>); мелкий (semen <i>Lcr</i>; civitas, controversia <i>C</i>)."],
  ["perpasco", "<big>perpasco</big>\n\n<b>per–pasco, —, —, ere</b>\nпастись <i>Vr</i> (<i>v. l.</i>)."],
  ["perpascor", "<big>perpascor</big>\n\n<b>per–pascor, —, pasci</b>\nпожирать, <i>перен.</i> опустошать (fluvius perpascitur agros <i>LJ</i> — <i>v. l.</i>)."],
  ["perpastus", "<big>perpastus</big>\n\n<b>perpastus, a, um</b> [per + pasco]\nоткормленный, отъевшийся (canis <i>Ph</i>)."],
  ["perpateo", "<big>perpateo</big>\n\n<b>per–pateo, —, —, ere</b>\nбыть широко открытым (alicui <i>Aug</i>)."],
  ["perpauculus", "<big>perpauculus</big>\n\n<b>per–pauculus, a, um</b> (<i>тк.</i> <i>pl.</i>)\nвесьма немногие (passus <i>C</i>)."],
  ["perpaucus", "<big>perpaucus</big>\n\n<b>per–paucus, a, um</b> (<i>тк.</i> <i>pl.</i>)\nочень немногие <i>Ter, C, L etc.</i>"],
  ["perpaululum", "<big>perpaululum</big>\n\n<b>perpaululum</b> <i>C</i>\n= perpaulum (<i>v. l.</i>)."],
  ["perpaulum", "<big>perpaulum</big>\n\n<b>I per–paulum, i</b> <i>n</i>\nмалость, кусочек, очень немного (loci <i>C</i>).\n\n<b>II per–paulum</b> <i>adv.</i>\nчуть-чуть, очень немного <i>C</i>."],
  ["perpauper", "<big>perpauper</big>\n\n<b>per–pauper, eris</b>\nвесьма бедный <i>C</i>."],
  ["perpauxillum", "<big>perpauxillum</big>\n\n<b>per–pauxillum, i</b> <i>n</i> <i>Pl</i>\n= perpaulum I."],
  ["perpavefacio", "<big>perpavefacio</big>\n\n<b>per–pavefacio, —, —, ere</b>\nсильно пугать (pectora <i>Pl</i>)."],
  ["perpedio", "<big>perpedio</big>\n\n<b>per–pedio, —, —, ire</b>\nсильно мешать (aliquem <i>Acc</i>)."],
  ["perpello", "<big>perpello</big>\n\n<b>per–pello, pali, pulsum, ere</b>\n<b>1)</b> побуждать, убеждать, склонять (aliquem ut aliquid faciat <i>Sl, L etc.</i>; aliquem ad aliquid <i>L</i>);\n<b>2)</b> достигать, добиться (ut aliquid fiat <i>L</i>)."],
  ["perpendiculator", "<big>perpendiculator</big>\n\n<b>perpendiculator, oris</b> <i>m</i>\nпользующийся отвесом, <i>т. е.</i> строитель <i>AV</i>."],
  ["perpendiculum", "<big>perpendiculum</big>\n\n<b>perpendiculum, i</b> <i>n</i> [perpendo]\nотвес <i>Ap</i>: ad p. <i>C, Cs</i> отвесно, вертикально, <i>перен.</i> <i>Aus</i> по строгим правилам, сурово."],
  ["perpendo", "<big>perpendo</big>\n\n<b>per–pendo, pendi, pensum, ere</b>\n<b>1)</b> тщательно отвешивать (aliquid in librili <i>AG</i>; aliquid aequa lance <i>Aug</i>);\n<b>2)</b> взвешивать (vitia virtutesque <i>Su</i>);\n<b>3)</b> определять (p. quantum quisque possit <i>C</i>); исследовать (aliquid acri judicio <i>Lcr</i>)."],
  ["Perpenna", "<big>Perpenna</big>\n\n<b>Perpenna, ae</b> <i>m</i> <i>Nep, T</i>\n= Perperna."],
  ["perpensatio", "<big>perpensatio</big>\n\n<b>per–pensatio, onis</b> <i>f</i>\nтщательное взвешивание, рассмотрение (officiorum <i>AG</i>)."],
  ["perpense", "<big>perpense</big>\n\n<b>perpense</b>\nтщательно, внимательно (cogitare <i>Amm</i>)."],
  ["perpensio", "<big>perpensio</big>\n\n<b>perpensio, onis</b> <i>f</i> <i>Aug</i>\n= perpensatio."],
  ["perpenso", "<big>perpenso</big>\n\n<b>perpenso, —, —, are</b> [<i>intens. к</i> perpendo]\nтщательно взвешивать, точно определять (aliquid <i>GF, Amm</i>)."],
  ["perpensus", "<big>perpensus</big>\n\n<b>perpensus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> perpendo;\n<b>2.</b> <i>adj.</i> тщательный, точный (deliberatio <i>Amm</i>; judicium <i>Eccl</i>)."],
  ["perperam", "<big>perperam</big>\n\n<b>perperam</b> <i>adv.</i> [perperus]\n<b>1)</b> неправильно, превратно (judicare <i>C</i>; suadere aliquid alicui <i>Pl</i>): si quid p. feci <i>Pt</i> если я допустил какую-л. оплошность;\n<b>2)</b> ошибочно, по недосмотру, по ошибке (aliquem imperatorem pronuntiare <i>Su</i>)."],
  ["perperitudo", "<big>perperitudo</big>\n\n<b>perperitudo, inis</b> <i>f</i> [perperus]\nопрометчивость, ошибочность <i>Acc</i>."],
  ["Perperna", "<big>Perperna</big>\n\n<b>Perperna (Perpenna), ae</b> <i>m</i>\nПерперна, <i>римск. nomen; наиболее известны</i>:\n<b>1)</b> M. P., <i>отправленный в 168 г. до н. э. с посольством к иллирийскому царю Гентию, был заключён им в темницу</i> <i>L</i>;\n<b>2)</b> M. P., <i>консул в 92 г., цензор в 86 г., умер в 49 г. до н. э.</i> <i>C</i>;\n<b>3)</b> M. P. Vento, <i>приверженец Мария, соратник и преемник Сертория в Испании, казнённый в 71 г. до н. э. Помпеем</i> <i>L</i>."],
  ["perperus", "<big>perperus</big>\n\n<b>perperus, a, um</b> (<i>греч.</i>)\nнеправильный, опрометчивый <i>Acc</i>."],
  ["perpes", "<big>perpes</big>\n\n<b>perpes, petis</b> <i>adj.</i> (= perpetuus)\nпостоянный, сплошной, непрерывный (silentium <i>Ap</i>); noctem perpetem <i>Pl</i> <i>или</i> nocte perpeti <i>Just, Capit</i> всю ночь напролёт."],
  ["perpessicius", "<big>perpessicius</big>\n\n<b>perpessicius (perpessitius), a, um</b> [perpetior]\nвесьма терпеливый, выносливый (senex <i>Sen</i>)."],
  ["perpessio", "<big>perpessio</big>\n\n<b>perpessio, onis</b> <i>f</i> [perpetior]\nпретерпевание, перенесение (laborum <i>C</i>; dolorum <i>Sen</i>)."],
  ["perpessitius", "<big>perpessitius</big>\n\n<b>perpessitius, a, um</b> <i>v. l.</i>\n= perpessicius."],
  ["perpetim", "<big>perpetim</big>\n\n<b>perpetim</b>\nнапролёт (noctem p. pernoctare <i>Pl</i>); сплошь (anno p. assiduo <i>Ap</i>)."],
  ["perpetior", "<big>perpetior</big>\n\n<b>per–petior, itus sum, iri</b>\nполностью овладеть, добиться (p. privilegiis, beneficiis <i>CJ</i>)."],
  ["perpetior", "<big>perpetior</big>\n\n<b>per–petior, pessus sum, peti</b> <i>depon.</i> [patior]\n<b>1)</b> твёрдо сносить, переносить, терпеть (paupertatem facile <i>Nep</i>): levia perpessus est <i>SenT</i> его горе не так уж велико;\n<b>2)</b> найти в себе мужество (memorare aliquid <i>O</i>);\n<b>3)</b> позволять, допускать (aliquem requiescere somno <i>Ctl</i>)."],
  ["perpetitus", "<big>perpetitus</big>\n\n<b>per–petitus, a, um</b>\nдостигший, вознёсшийся (in secundam numinum formam <i>Sen</i>)."],
  ["perpetrabilis", "<big>perpetrabilis</big>\n\n<b>perpetrabilis, e</b> [perpetro]\nвозможный, допустимый <i>или</i> позволительный <i>Tert</i>."],
  ["perpetratio", "<big>perpetratio</big>\n\n<b>perpetratio, onis</b> <i>f</i>\nсовершение, осуществление <i>Tert, Aug</i>."],
  ["perpetrator", "<big>perpetrator</big>\n\n<b>perpetrator, oris</b> <i>m</i>\nсвершитель (peccati magni <i>Aug</i>; mali <i>Sid</i>)."],
  ["perpetro", "<big>perpetro</big>\n\n<b>per–petro, avi, itum, are</b> [patro]\n<b>1)</b> совершать (caedem <i>L</i>): quanto suo labore perpetratum, ne... <i>T</i> чего он только не делал, чтобы не...; audax omnia p. <i>H</i> готовый на всё, не останавливающийся ни перед чем; interfici se p. <i>Ter</i> решиться на самоубийство || исполнять (promissa <i>T</i>);\n<b>2)</b> заключать (pacem <i>L</i>); заканчивать, доводить до конца (bellum <i>L, T</i>)."],
  ["perpetualis", "<big>perpetualis</big>\n\n<b>perpetualis, e</b> [perpetuus]\nвсеобщий (universalis vel p. <i>Q</i>)."],
  ["perpetualiter", "<big>perpetualiter</big>\n\n<b>perpetualiter</b>\nпостоянно, вечно <i>Eccl</i>."],
  ["perpetuarius", "<big>perpetuarius</big>\n\n<b>I perpetuarius, a, um</b> [perpetuus]\nпостоянный, бессменный, <i>т. е.</i> всегда находящийся в пути (mulio <i>Sen</i>).\n\n<b>II perpetuarius, i</b> <i>m</i>\nнаследственный арендатор <i>CJ</i>."],
  ["perpetue", "<big>perpetue</big>\n\n<b>perpetue</b> <i>Pl</i>\n= perpetuo II."],
  ["perpetuitas", "<big>perpetuitas</big>\n\n<b>perpetuitas, atis</b> <i>f</i> [perpetuus]\n<b>1)</b> непрерывность (temporis <i>C</i>); p. vitae <i>C</i> вся жизнь в целом; p. verborum <i>C</i> связная (последовательная) речь; non ex singulis vocibus spectare, sed ex perpetuitate <i>C</i> судить не по отдельным словам, а по их общей связи; ad perpetuitatem <i>C</i> навсегда;\n<b>2)</b> <i>pl.</i> сплошная полоса (umbrosae perpetuitates <i>Vtr</i>)."],
  ["perpetuo", "<big>perpetuo</big>\n\n<b>I perpetuo, avi, atum, are</b> [perpetuus]\nделать непрерывным, беспрерывно продолжать, сохранять на всё время (potestatem judicum <i>C</i>): p. verba <i>C</i> говорить не останавливаясь (без умолку); dii te perpetuent <i>Lampr</i> да ниспошлют тебе боги долголетие.\n\n<b>II perpetuo</b> <i>adv.</i> [perpetuus]\n<b>1)</b> беспрерывно, постоянно, всегда (sub imperio alicujus esse <i>Cs</i>);\n<b>2)</b> навсегда, навечно (perire <i>Ter</i>)."],
  ["perpetuum", "<big>perpetuum</big>\n\n<b>perpetuum</b> <i>adv.</i> (<i>тж.</i> in p. <i>C etc.</i>)\nнавсегда <i>St</i>."],
  ["perpetuus", "<big>perpetuus</big>\n\n<b>perpetuus, a, um</b> [per + peto]\n<b>1)</b> непрерывный, сплошной (montes <i>PM</i>; paludes <i>Cs</i>; agmen <i>C</i>): trabes perpetuae <i>Cs</i> цельные брусья (из одного куска) || цельный, неразрубленный (bovis tergum <i>V</i>); целостный, связный, охватывающий целый круг сказаний (carmen <i>H, O</i>); целый (dies, triduum <i>Ter</i>): vita perpetua <i>Nep, Cs</i> в течение всей жизни; quaestio perpetua <i>юр. C</i> постоянный уголовно-следственный суд, <i>но</i> <i>ритор. C</i> вопрос общего характера;\n<b>2)</b> постоянный, вечный (ver <i>O</i>; pax <i>C, Sl</i>; sopor <i>H</i>); неизменный (cursus stellarum <i>C</i>): in perpetuum (modum) <i>Pl, C, L, Ap</i> навеки, навсегда;\n<b>3)</b> непоколебимый (voluntas <i>C</i>); неугасимый (ignis Vestae <i>C</i>);\n<b>4)</b> пожизненный (imperium <i>Nep</i>; tribunus <i>L</i>);\n<b>5)</b> всеобщий, общеобязательный (jus <i>C</i>);\n<b>6)</b>: modus p. (= infinitivus) <i>грам.</i> инфинитив."],
  ["perplaceo", "<big>perplaceo</big>\n\n<b>per–placeo, —, —, ere</b>\nочень нравиться (alicui <i>Pl, Ter, C</i>)."],
  ["perplexabilis", "<big>perplexabilis</big>\n\n<b>perplexabilis, e</b> [perplexor]\nпутаный, непонятный, двусмысленный (verbum <i>Pl</i>)."],
  ["perplexabiliter", "<big>perplexabiliter</big>\n\n<b>perplexabiliter</b> [perplexabilis]\nдвусмысленными (запутанными) речами (perpavefacere pectora alicujus <i>Pl</i>)."],
  ["perplexe", "<big>perplexe</big>\n\n<b>perplexe</b> [perplexus]\nзапутанно, неясно, двусмысленно (indicare <i>L</i>; respondere <i>Ter</i>)."],
  ["perplexim", "<big>perplexim</big>\n\n<b>perplexim</b> <i>Pl</i>\n= perplexe."],
  ["perplexitas", "<big>perplexitas</big>\n\n<b>perplexitas, atis</b> <i>f</i>\n<b>1)</b> перепутанность, сложное сплетение (nervorum <i>Eccl</i>);\n<b>2)</b> запутанность, двусмысленность (condicionum, <i>sc.</i> pacis <i>Amm</i>)."],
  ["perplexor", "<big>perplexor</big>\n\n<b>perplexor, —, ari</b> <i>depon.</i> [perplexus]\nпутать, запутывать <i>Pl</i>."],
  ["perplexus", "<big>perplexus</big>\n\n<b>perplexus, a, um</b> [per + plecto]\n<b>1)</b> запутанный (iter silvae <i>V</i>);\n<b>2)</b> переплетённый, смешанный (servili cognomine et regia appellatione p., <i>sc.</i> Servius Tullius <i>VM</i>);\n<b>3)</b> неясный, туманный, двусмысленный (responsum, sermones <i>L</i>; quaestio <i>Aug</i>)."],
  ["perplicatus", "<big>perplicatus</big>\n\n<b>per–plicatus, a, um</b> [plico]\nпереплетённый, перепутанный (elementa <i>Lcr</i> —<i>in tmesi</i>)."],
  ["perpluo", "<big>perpluo</big>\n\n<b>per–pluo, —, —, ere</b>\n<b>1)</b> протекать внутрь (ex imbribus aqua perpluit <i>Vtr</i>); <i>impers.</i> perpluit <i>Cato</i> дождь проникает внутрь (крыша протекает);\n<b>2)</b> протекать насквозь, пропускать дождь (tigna perpluunt <i>Pl</i>);\n<b>3)</b> смачивать, обрызгивать, орошать (crocus capellas odoro perpluit imbre <i>Ap</i>);\n<b>4)</b> <i>перен.</i> вливать, вносить (tempestatem in pectus alicujus <i>Pl</i>)."],
  ["perpluvium", "<big>perpluvium</big>\n\n<b>per–pluvium, i</b> <i>n</i>\nсильный ливень <i>Pall</i>."],
  ["perpolio", "<big>perpolio</big>\n\n<b>per–polio, ivi, itum, ire</b>\n<b>1)</b> тщательно разглаживать, полировать (aurum attritu <i>PM</i>);\n<b>2)</b> штукатурить (locum calce arenaque <i>VP</i>);\n<b>3)</b> совершенствовать, улучшать, отделывать (omnes partes orationis <i>rhH</i>; vita perpolita humanitate <i>C</i>)."],
  ["perpolite", "<big>perpolite</big>\n\n<b>per–polite</b>\nвесьма тонко, тщательно <i>rhH</i>."],
  ["perpolitio", "<big>perpolitio</big>\n\n<b>perpolitio, onis</b> <i>f</i> [perpolio]\nотделка (sermonis <i>rhH</i>)."],
  ["perpopulor", "<big>perpopulor</big>\n\n<b>per–populor, atus sum, ari</b> <i>depon.</i>\nсовершенно опустошать, разорять, разграблять (Italiam <i>L</i>; homines <i>T</i>)."],
  ["perporto", "<big>perporto</big>\n\n<b>per–porto, —, —, are</b>\nперевозить, доставлять (praedam Carthaginem <i>L</i>)."],
  ["perpotatio", "<big>perpotatio</big>\n\n<b>perpotatio, onis</b> <i>f</i> [perpoto]\nдолгая попойка <i>C, PM</i>."],
  ["perpoto", "<big>perpoto</big>\n\n<b>per–poto, avi, atum, are</b>\n<b>1)</b> много пить, пьянствовать (totos dies <i>C</i>);\n<b>2)</b> выпивать до дна (amarum absinthi laticem <i>Lcr</i>)."],
  ["perpremo", "<big>perpremo</big>\n\n<b>perpremo</b> <i>Sen</i>\n= perprimo."],
  ["perpressa", "<big>perpressa</big>\n\n<b>perpressa, ae</b> <i>f</i> <i>бот.</i>\nколокольчик <i>PM</i>."],
  ["perprimo", "<big>perprimo</big>\n\n<b>per–primo, pressi, pressum, ere</b> [premo]\n<b>1)</b> сильно жать, беспрерывно давить: p. cubilia <i>H</i> подолгу <i>или</i> постоянно лежать в постели;\n<b>2)</b> выжимать, выдавливать (oculis adjacentem humorem <i>Sen</i>);\n<b>3)</b> приставать (с любовными излияниями) (p. aliquam <i>O</i>)."],
  ["perprobabilis", "<big>perprobabilis</big>\n\n<b>per–probabilis, e</b>\nвесьма правдоподобный <i>Aug</i>."],
  ["perpropinquus", "<big>perpropinquus</big>\n\n<b>I per–propinquus, a, um</b>\nочень близкий (commutatio rerum <i>Acc ap. C</i>).\n\n<b>II per–propinquus, i</b> <i>m</i>\nблизкий родственник <i>C</i>."],
  ["perprosper", "<big>perprosper</big>\n\n<b>per–prosper, pera, perum</b>\nвесьма благоприятный, цветущий (valetudo <i>Su</i>)."],
  ["perprurisco", "<big>perprurisco</big>\n\n<b>per–prurisco, —, —, ere</b> [prurio]\nраззудеться, <i>перен.</i> воспламениться страстью <i>Pl, Ap</i>."],
  ["perpudesco", "<big>perpudesco</big>\n\n<b>per–pudesco, —, —, ere</b>\nглубоко стыдиться <i>Nep</i>."],
  ["perpugnax", "<big>perpugnax</big>\n\n<b>per–pugnax, acis</b> <i>adj.</i>\nлюбящий споры, задорный (in disputando <i>C</i>)."],
  ["perpulcher", "<big>perpulcher</big>\n\n<b>per–pulcher, chra, chrum</b>\nочень красивый, прекрасный (dona <i>Ter</i>, tabulina <i>Ap</i>)."],
  ["perpuli", "<big>perpuli</big>\n\n<b>perpuli</b> <i>pf. к</i>\nperpello."],
  ["perpungo", "<big>perpungo</big>\n\n<b>per–pungo, punxi, punctum, ere</b>\nпрокалывать насквозь (aliquid <i>CA</i>)."],
  ["perpurgo", "<big>perpurgo</big>\n\n<b>per–purgo, avi, atum, are</b>\n<b>1)</b> полностью очищать (alvum <i>Cato</i>; aliquem quadam herbula <i>C</i>): perpurgatis auribus dare operam alicui <i>Pl</i> с величайшим вниманием слушать кого-л.;\n<b>2)</b> приводить в ясность (rationes <i>C</i>);\n<b>3)</b> полностью опровергать (crimina <i>C</i>)."],
  ["perpurigo", "<big>perpurigo</big>\n\n<b>perpurigo</b> <i>Pl</i>\n(<i>v. l.</i>) = perpurgo."],
  ["perpurus", "<big>perpurus</big>\n\n<b>per–purus, a, um</b>\nсовершенно чистый (lana <i>Vr</i>)."],
  ["perpusillus", "<big>perpusillus</big>\n\n<b>per–pusillus, a, um</b>\nочень маленький, крошечный <i>C</i>."],
  ["perputo", "<big>perputo</big>\n\n<b>per–puto, —, —, are</b>\nполностью изложить, развить (alicui argumentum <i>Pl</i>)."],
  ["perquadratus", "<big>perquadratus</big>\n\n<b>per–quadratus, a, um</b>\nсовершенно квадратный <i>Vtr</i>."],
  ["perquam", "<big>perquam</big>\n\n<b>per–quam</b> <i>adv.</i>\nчрезвычайно, весьма, крайне (p. laboriosum iter <i>VP</i>; donarium p. opulentum <i>Ap</i>): p. breviter <i>C</i> совсем коротко; p. velim scire <i>PJ</i> я очень желал бы знать."],
  ["perquiesco", "<big>perquiesco</big>\n\n<b>per–quiesco, —, —, ere</b>\nпроводить в отдыхе, отдыхать (totam noctem <i>Ap</i>)."],
  ["perquiro", "<big>perquiro</big>\n\n<b>perquiro, quisivi, quisitum, ere</b> [per + quaero]\n<b>1)</b> тщательно разыскивать (vias <i>Cs</i>);\n<b>2)</b> осведомляться, опрашивать (nomines <i>C</i>);\n<b>3)</b> разведывать, разузнавать (aliquid <i>Pl, C etc.</i>);\n<b>4)</b> исследовать (indicia alicujus rei <i>Lcr</i>)."],
  ["perquisite", "<big>perquisite</big>\n\n<b>perquisite</b> [perquiro]\nточно, тщательно, строго (conscribere <i>C</i>)."],
  ["perquisitor", "<big>perquisitor</big>\n\n<b>perquisitor, oris</b> <i>m</i>\nвыслеживатель, <i>ирон.</i> большой охотник, любитель (p. auctionum <i>Pl</i>)."],
  ["Perranthes", "<big>Perranthes</big>\n\n<b>Perranthēs, is</b> <i>m</i>\nПерранф, <i>крутая возвышенность в Эпире, на склоне которой находился город Амбракия</i> <i>L</i>."],
  ["perraro", "<big>perraro</big>\n\n<b>per–rārō</b> <i>adv.</i>\nочень редко <i>rhH, C etc.</i>"],
  ["perrarus", "<big>perrarus</big>\n\n<b>per–rārus, a, um</b>\nочень редкий <i>L, PM, Lampr</i>."],
  ["perreconditus", "<big>perreconditus</big>\n\n<b>per–reconditus, a, um</b>\nсовершенно скрытый, тайный, сокровенный (ratio <i>C</i>)."],
  ["perrectus", "<big>perrectus</big>\n\n<b>perrēctus, a, um</b> <i>part. pf. к</i>\npergo."],
  ["perrepo", "<big>perrepo</big>\n\n<b>per–rēpo, rēpsī, rēptum, ere</b>\nпроползать (ad <i>и</i> in aliquid <i>Col</i>; tellurem genibus <i>Tib</i>)."],
  ["perrepto", "<big>perrepto</big>\n\n<b>perrēpto, āvī, ātum, āre</b> [<i>intens. к</i> perrepo]\nпроползать, пролезать, пробираться (usque omne oppidum <i>Ter</i>); обегать (omnes latebras <i>Pl</i>)."],
  ["Perrhaebi", "<big>Perrhaebi</big>\n\n<b>Perrhaebī, ōrum</b> <i>m</i>\nперребы, <i>пеласгическое племя, населявшее область Perrhaebia</i> (<i>Фессалия</i>) <i>L</i>."],
  ["Perrhaebia", "<big>Perrhaebia</big>\n\n<b>Perrhaebia, ae</b> <i>f</i>\nПерребия, <i>область пеласгического племени перребов в сев. Фессалии по реке Титаресий</i> <i>C, L</i>."],
  ["Perrhaebus", "<big>Perrhaebus</big>\n\n<b>Perrhaebus, a, um</b> [Perrhaebia]\nперребийский, <i>поэт.</i> фессалийский (cacumina Pindi <i>Prp</i>)."],
  ["perridicule", "<big>perridicule</big>\n\n<b>perrīdiculē</b> [perridiculus]\nочень смешно <i>C</i>."],
  ["perridiculus", "<big>perridiculus</big>\n\n<b>per–rīdiculus, a, um</b>\nочень смешной, уморительный <i>C, VF, M</i>."],
  ["perrimosus", "<big>perrimosus</big>\n\n<b>per–rīmōsus, a, um</b>\nвесь в расщелинах, растрескавшийся <i>Vr</i> (<i>v. l.</i>)."],
  ["perrodo", "<big>perrodo</big>\n\n<b>per–rōdo, rōsī, —, ere</b>\nразъедать (materiam <i>PM</i>)."],
  ["perrogatio", "<big>perrogatio</big>\n\n<b>perrogātio, ōnis</b> <i>f</i> [perrogo]\nпроведение (через законодательный орган) (p. legis Maniliae <i>C</i> — <i>v. l.</i>)."],
  ["perrogito", "<big>perrogito</big>\n\n<b>perrogito, —, —, āre</b> [<i>intens. к</i> perrogo]\nпоочерёдно расспрашивать (aliquos de aliquibus <i>Pac</i>)."],
  ["perrogo", "<big>perrogo</big>\n\n<b>per–rogo, āvī, ātum, āre</b>\n<b>1)</b> расспрашивать, опрашивать (одного за другим) (p. sententias <i>L, T, Su etc.</i>);\n<b>2)</b> проводить (legem <i>VM</i>)."],
  ["perrumpo", "<big>perrumpo</big>\n\n<b>per–rumpo, rūpī, ruptum, ere</b>\n<b>1)</b> пробивать дорогу, прорываться (per hostes <i>Cs</i>); <i>воен.</i> форсировать (paludem <i>Cs</i>; Acheronta <i>H</i>); штурмовать, брать приступом (caeli munimenta <i>Sen</i>); врываться, вторгаться (in urbem <i>L</i>); протискиваться, силой входить (castra perrupta <i>T</i>); взламывать (limina bipenni <i>V</i>); ломать, проломить (rates <i>Cs</i>);\n<b>2)</b> вскапывать, взрывать (terram aratro <i>V</i>);\n<b>3)</b> преодолевать (periculum <i>C</i>); устранять (difficultates <i>PM</i>; quaestiones <i>C</i>);\n<b>4)</b> попирать (leges <i>C</i>); срывать, расстраивать (alicujus consilia <i>C</i>)."],
  ["Persa", "<big>Persa</big>\n\n<b>I Persa, ae</b> <i>m sg. к</i>\nPersae.\n\n<b>II Persa, ae</b> <i>f</i>\nПерса:\n<b>1)</b> <i>дочь Океана, жена Солнца, мать Кирки, Гекаты, Пасифаи, Ээта и Алоэя</i> (<i>см.</i> Perseius);\n<b>2)</b> <i>кличка собачки</i> <i>C</i>."],
  ["Persae", "<big>Persae</big>\n\n<b>Persae, arum</b> <i>m</i>\nперсы:\n<b>1)</b> <i>жители Персиды</i> (<i>нын. Фарсистана</i>) <i>C etc.</i>;\n<b>2)</b> <i>жители Персидской империи</i> <i>Nep etc.</i>;\n<b>3)</b> <i>поэт.</i> парфяне <i>H</i>;\n<b>4)</b> Персия (in Persas proficisci <i>Nep</i>)."],
  ["Persaea", "<big>Persaea</big>\n\n<b>Persaea, ae</b> <i>f</i> <i>V</i>\n= Hecate."],
  ["persaepe", "<big>persaepe</big>\n\n<b>per–saepe</b>\nочень часто <i>C, H etc.</i>"],
  ["Persaepolis", "<big>Persaepolis</big>\n\n<b>Persaepolis, is</b> <i>f</i> <i>v. l.</i>\n= Persepolis."],
  ["Persaeus", "<big>Persaeus</big>\n\n<b>Persaeus, a, um</b> <i>v. l.</i>\n= Perseius 1."],
  ["persaevus", "<big>persaevus</big>\n\n<b>per–saevus, a, um</b>\nбешеный, неистовый (flumen <i>Mela</i>)."],
  ["Persagadae", "<big>Persagadae</big>\n\n<b>Persagadae, arum</b> <i>f</i> <i>PM</i>\n= Pasargadae."],
  ["persalse", "<big>persalse</big>\n\n<b>persalse</b> [persalsus]\nвесьма остроумно <i>C</i>."],
  ["persalsus", "<big>persalsus</big>\n\n<b>per–salsus, a, um</b>\nвесьма остроумный <i>C</i>."],
  ["persalutatio", "<big>persalutatio</big>\n\n<b>persalutatio, onis</b> <i>f</i> [persaluto]\nприветствование (одного за другим) <i>M</i>."],
  ["persaluto", "<big>persaluto</big>\n\n<b>per–saluto, avi, atum, are</b>\nприветствовать (всех — одного за другим) (nos omnes <i>C</i>; Troianos et Achivos <i>Sen</i>; a toto exercitu persalutari <i>QC</i>)."],
  ["persancte", "<big>persancte</big>\n\n<b>per–sancte</b>\nименем величайших святынь, <i>т. е.</i> весьма торжественно (dejerare <i>Ter</i>; jurare <i>Su</i>)."],
  ["persano", "<big>persano</big>\n\n<b>per–sano, avi, atum, are</b>\nсовершенно вылечивать (ulcera <i>Sen</i>; carcinomata <i>PM</i>; a medicamento persanari <i>Scr</i>)."],
  ["persanus", "<big>persanus</big>\n\n<b>per–sanus, a, um</b>\nсовершенно здоровый <i>Cato</i>."],
  ["persapiens", "<big>persapiens</big>\n\n<b>per–sapiens, entis</b>\nвесьма мудрый (homo <i>C</i>)."],
  ["persapienter", "<big>persapienter</big>\n\n<b>per–sapienter</b>\nвесьма мудро <i>C</i>."],
  ["persciens", "<big>persciens</big>\n\n<b>per–sciens, entis</b> <i>adj.</i>\nхорошо знающий (animum alicujus <i>Lampr</i>)."],
  ["perscienter", "<big>perscienter</big>\n\n<b>per–scienter</b>\nвесьма умело, искуснейшим образом <i>C</i>."],
  ["perscindo", "<big>perscindo</big>\n\n<b>per–scindo, scidi, scissum, ere</b>\nразрывать (сверху донизу <i>или</i> совершенно) (nubem <i>Lcr</i>; ventus perscindens omnia <i>L</i>; carbasus perscissa <i>Lcr</i>)."],
  ["perscitus", "<big>perscitus</big>\n\n<b>per–scitus, a, um</b>\nочень умный, весьма тонкий <i>Ter</i>, (<i>in tmesi</i>) <i>C</i>."],
  ["perscribo", "<big>perscribo</big>\n\n<b>per–scribo, scripsi, scriptum, ere</b>\n<b>1)</b> тщательно записывать, обстоятельно описывать (res populi Romani gestas <i>L</i>); письменно излагать, сообщать, доносить (alicui aliquid <i>и</i> ad aliquem de aliqua re <i>C</i>); протоколировать (senatus consulta <i>C</i>);\n<b>2)</b> проводить по счетам, вносить в счётные книги (rationes <i>C</i>); заносить в списки (nomen <i>C</i>); регистрировать, вносить (in tabulis senatus consultum <i>C</i>);\n<b>3)</b> выплачивать по приказу (argentum alicui <i>Ter</i>; pecuniam in aedem sacram reficiendam <i>C</i>)."],
  ["perscriptio", "<big>perscriptio</big>\n\n<b>per–scriptio, onis</b> <i>f</i>\n<b>1)</b> записывание, протоколирование <i>C</i>;\n<b>2)</b> бухгалтерская запись, внесение в счётные книги <i>C</i>;\n<b>3)</b> <i>pl.</i> счётные статьи <i>C</i>;\n<b>4)</b> ассигновка, переводная расписка <i>C</i>."],
  ["perscriptor", "<big>perscriptor</big>\n\n<b>per–scriptor, oris</b> <i>m</i>\nзаписывающий в счётные книги, счетовод <i>C</i>."],
  ["perscriptus", "<big>perscriptus</big>\n\n<b>perscriptus, a, um</b> <i>part. pf. к</i>\nperscribo."],
  ["perscrutatio", "<big>perscrutatio</big>\n\n<b>perscrutatio, onis</b> <i>f</i> [perscrutor]\nразыскивание, исследование (profundi <i>Sen</i>)."],
  ["perscrutator", "<big>perscrutator</big>\n\n<b>perscrutator, oris</b> <i>m</i>\nизыскатель, исследователь <i>Eccl</i>: p. maris <i>Eccl</i> ловец (искатель) жемчуга."],
  ["perscrutatrix", "<big>perscrutatrix</big>\n\n<b>perscrutatrix, tricis</b> <i>f</i>\nисследовательница, испытательница (animi alicujus <i>Julianus ap. Aug</i>)."],
  ["perscruto", "<big>perscruto</big>\n\n<b>perscruto, avi, atum, are</b> <i>Pl</i>\n= perscrutor."],
  ["perscrutor", "<big>perscrutor</big>\n\n<b>per–scrutor, atus sum, ari</b> <i>depon.</i>\n<b>1)</b> обыскивать, осматривать (omnia, arculas muliebres <i>C</i>);\n<b>2)</b> рассматривать, исследовать (sententiam alicujus <i>C</i>; naturam rationemque criminum <i>C</i>)."],
  ["perseco", "<big>perseco</big>\n\n<b>per–seco, secui, sectum, are</b>\n<b>1)</b> разрезать, рассекать (vomicas <i>Q</i>); <i>перен.</i> вскрывать (rerum naturas <i>C</i>);\n<b>2)</b> вырезывать, <i>перен.</i> искоренять (vitium <i>L</i>);\n<b>3)</b> прорезать, проникать (p. aeris crebritatem <i>Vtr</i>)."],
  ["persector", "<big>persector</big>\n\n<b>per–sector, atus sum, ari</b> <i>depon.</i>\n<b>1)</b> преследовать (<i>sc.</i> aliquem <i>Lcr</i>);;\n<b>2)</b> пристально исследовать (primordia <i>Lcr</i>)."],
  ["persecutio", "<big>persecutio</big>\n\n<b>persecutio, onis</b> <i>f</i> [persequor]\n<b>1)</b> погоня, преследование (latronum <i>Ap</i>; bestiae <i>Dig</i>);\n<b>2)</b> преследование, гонения (Judaeorum <i>CTh</i>; Christi anorum <i>Tert</i>);\n<b>3)</b> судебное преследование <i>rhH, C, Dig</i>; иск (p. hereditatis <i>Dig</i>);\n<b>4)</b> продолжение (negotii <i>Ap</i>)."],
  ["persecutor", "<big>persecutor</big>\n\n<b>persecutor, oris</b> <i>m</i>\n<b>1)</b> жалобщик, истец <i>Dig</i>;\n<b>2)</b> преследователь, гонитель (ecclesiae <i>Hier</i>; flagitiorum <i>Capit</i>)."],
  ["persecutrix", "<big>persecutrix</big>\n\n<b>persecutrix, icis</b> <i>f</i>\nпреследовательница, гонительница <i>Aug</i>."],
  ["persedeo", "<big>persedeo</big>\n\n<b>per–sedeo, sedi, sessum, ere</b>\nдолго сидеть, оставаться подолгу (in auctione per diem totum <i>Sen</i>): p. in equo dies noctesque <i>L</i> сутками не сходить с коня."],
  ["persegnis", "<big>persegnis</big>\n\n<b>per–segnis, e</b>\nочень вялый (proelium <i>L</i>)."],
  ["Perseis", "<big>Perseis</big>\n\n<b>I Perseis, idis</b> <i>и</i> <b>idos</b> [Persa II, 1]\n<b>1.</b> <i>f</i> Персеида, дочь Персы, <i>т. е.</i> Кирка <i>VF</i> <i>или</i> Геката <i>St</i>;\n<b>2.</b> <i>f adj.</i> волшебная (herbae <i>O</i>).\n\n<b>II Perseis, idis</b> <i>f</i> [Persa II, 1]\nПерсеида, <i>латинская поэма о Персее, упоминаемая Овидием.</i>\n\n<b>III Perseis, idis</b> (<i>acc.</i> ida) <i>f</i>\nПерсеида, <i>город в Македонии, названный по имени Персея</i> (<i>см.</i> Perseus I, 2) <i>L</i>."],
  ["Perseius", "<big>Perseius</big>\n\n<b>Perseius, a, um</b>\n<b>1)</b> [Persa II, 1] Персы (<i>gen.</i>), Персии (Hecate <i>C</i>): Perseia proles <i>VF</i> = Aeetes;\n<b>2)</b> [Perseus I] персеев <i>O</i>."],
  ["persenesco", "<big>persenesco</big>\n\n<b>per–senesco, senui, —, ere</b>\nсильно стариться <i>Eutr</i>."],
  ["persenex", "<big>persenex</big>\n\n<b>per–senex, senis</b> <i>adj.</i>\nпрестарелый <i>Su</i>."],
  ["persenilis", "<big>persenilis</big>\n\n<b>per–senilis, e</b>\nпрестарелый, преклонный (longaevus et aetatis p. <i>Vlg</i>)."],
  ["persentio", "<big>persentio</big>\n\n<b>per–sentio, sensi, sensum, ire</b>\n<b>1)</b> глубоко чувствовать (magno pectore curas <i>V</i>);\n<b>2)</b> ясно видеть, примечать <i>V, Ap</i>."],
  ["persentisco", "<big>persentisco</big>\n\n<b>per–sentisco, —, —, ere</b> <i>Pl, Ter etc.</i>\n= persentio."],
  ["Persephone", "<big>Persephone</big>\n\n<b>Persephone, es</b> <i>f</i> (<i>греч.</i>)\n<b>1)</b> <i>Prp, O, Lcn</i> = <i>римск.</i> Proserpina;\n<b>2)</b> <i>поэт.</i> смерть <i>Tib, O</i>."],
  ["persephonium", "<big>persephonium</big>\n\n<b>persephonium, i</b> <i>n</i> (<i>греч.</i>)\nдикий мак <i>Ap</i>."],
  ["Persepolis", "<big>Persepolis</big>\n\n<b>Persepolis, is</b> <i>f</i>\nПерсеполь, <i>столица Персиды, основанная Дарием I, разрушенная Александром Македонским</i> <i>PM, QC, Amm</i>."],
  ["persequax", "<big>persequax</big>\n\n<b>per–sequax, acis</b> <i>adj.</i>\nусиленно преследующий (in reatu pervestigando <i>Sid</i>)."],
  ["persequens", "<big>persequens</big>\n\n<b>persequens, entis</b>\n<b>1.</b> <i>part. praes. к</i> persequor;\n<b>2.</b> <i>adj.</i> настойчивый в преследовании (inimitiarum persequentissimus <i>rhH</i>): flagitii p. <i>Pl</i> преданный пороку."],
  ["persequor", "<big>persequor</big>\n\n<b>per–sequor, secutus (sequutus) sum, sequi</b> <i>depon.</i>\n<b>1)</b> неотступно следовать (за кем-л.), сопровождать (aliquem in Asiam <i>Ter</i>); следовать (по чему-л.) (p. viam <i>Ter</i>): p. vestigia alicujus <i>или</i> aliquem ipsius vestigiis <i>C</i> идти по чьим-л. следам; p. omnes vias <i>C</i> использовать все средства;\n<b>2)</b> преследовать, гнаться (за кем-л.) (p. hostes equitatu <i>L</i>; aliquem lapidibus <i>Pt</i>); охотиться (на кого-л.) (p. feras <i>O</i>);\n<b>3)</b> воевать: p. aliquem bello <i>C</i> <i>или</i> armis <i>Nep</i> воевать против кого-л.;\n<b>4)</b> наказывать, карать (aliquem debita poena <i>C</i>): poenam octupli p. <i>C</i> наложить штраф в восьмикратном размере || мстить: mortem alicujus <i>C, Cs</i> мстить за чью-л. смерть; <i>но</i>: mortem inedia p. <i>Pt</i> стремиться уморить себя голодом;\n<b>5)</b> преследовать в судебном порядке (aliquem judicio <i>или</i> apud judicium <i>C</i>);\n<b>6)</b> отстаивать (rem suam, jus suum <i>C</i>);\n<b>7)</b> ревностно заниматься, усердно изучать (artes <i>C</i>); стремиться приобрести (virtutes oratoris <i>C</i>; otium <i>C, H</i>); настойчиво добиваться, домогаться (aliquid ab aliquo <i>C</i>);\n<b>8)</b> следовать, подражать, придерживаться (p. mores patris <i>Pl</i>); воспроизводить (aliquem <i>Cs, L etc.</i>; exempla majorum <i>C</i>); быть приверженцем, быть последователем, примыкать (p. Academiam veterem <i>C</i>);\n<b>9)</b> прослеживать, исследовать (numeris siderum motus <i>C</i>); испытующе осматривать (totum parietem <i>Pt</i>); ощупывать (aliquid manu <i>Pt</i>); обходить (omnes solitudines <i>C</i>);\n<b>10)</b> продолжать, не прекращать (incepta <i>L</i>; studia usque ad senectutem <i>C</i>): p. quaerendo <i>L</i> продолжать спрашивать; ne pluris persequar <i>C</i> (<i>вводно</i>) чтобы не продолжать мне этого перечисления;\n<b>11)</b> выполнять, осуществлять (promissa <i>Pl</i>; mandata <i>C</i>);\n<b>12)</b> нагонять, догонять (aliquem triginta diebus <i>C</i>); настигать (mors et fugacem persequitur virum <i>H</i>);\n<b>13)</b> получать, взыскивать (pecuniam ab aliquo <i>C</i>; syngraphas <i>C</i>);\n<b>14)</b> записывать (за говорящим), протоколировать (p. celeritate scribendi aliquid <i>C</i>);\n<b>15)</b> описывать, излагать, рассказывать (alicujus vitam <i>и</i> de vita alicujus <i>Nep</i>; aliquid versibus <i>C</i>);\n<b>16)</b> вести, влачить (vitam inopem et vagam <i>C</i>)."],
  ["persero", "<big>persero</big>\n\n<b>I per–sero, —, —, ere</b>\nвтыкать, продевать (resticulas per aliquid <i>Vr</i>).\n\n<b>II per–sero, sevi, —, ere</b>\nсеять (semen <i>Sid</i>)."],
  ["perserpo", "<big>perserpo</big>\n\n<b>per–serpo, —, —, ere</b>\nрасползаться <i>LM</i> (<i>v. l.</i>)."],
  ["perservo", "<big>perservo</big>\n\n<b>per–servo, —, —, are</b>\nхранить до конца (patientiam <i>Tert</i>)."],
  ["Perses", "<big>Perses</big>\n\n<b>Perses, ae</b> <i>m</i>\n<b>1)</b> <i>C</i> = Persa I;\n<b>2)</b> <i>C, Sl etc.</i> = Perseus 2;\n<b>3)</b> <i>сын Персея и Андромеды</i> <i>PM</i>."],
  ["Perseus", "<big>Perseus</big>\n\n<b>I Perse͡us, eī</b> <i>и</i> <b>eos</b> <i>m</i>\nПерсей:\n<b>1)</b> <i>сын Юпитера и Данаи, убивший Медузу и освободивший Андромеду, на которой он женился; стал царём Тиринфа; после смерти превращен в созвездие</i> <i>O</i>;\n<b>2)</b> <i>последний царь Македонии, побочный сын Филиппа <i>V</i>; род. в 212 г. до н. э.; в 168 г. был разбит Эмилием Павлом Македонским, умер пленником в Альбе в 166 г. до н. э.</i> <i>C, Sl, L etc.</i>\n\n<b>II Persēus, a, um</b> [Perseus I, 1]\nПерсеев <i>Prp, St</i>."],
  ["perseverans", "<big>perseverans</big>\n\n<b>perseverans, antis</b>\n<b>1.</b> <i>part. praes. к</i> persevero;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> упорный, настойчивый (colendorum agrorum studium <i>Col</i>);\n<b>2)</b> стойкий, упорный, затяжной (valetudo <i>PJ</i>); непоколебимый (pietas <i>Aug</i>)."],
  ["perseveranter", "<big>perseveranter</big>\n\n<b>perseveranter</b> [perseverans]\nнастойчиво, твёрдо, упорно (tacere <i>VM</i>; aliquid tueri <i>L</i>)."],
  ["perseverantia", "<big>perseverantia</big>\n\n<b>perseverantia, ae</b> <i>f</i> [perseverans]\n<b>1)</b> настойчивость, твёрдость, стойкость (sententiae <i>C</i>); выдержка (nautarum <i>Cs</i>);\n<b>2)</b> затяжной характер, длительность (belli <i>Just</i>)."],
  ["persevero", "<big>persevero</big>\n\n<b>per–severo, avi, atum, are</b>\n<b>1)</b> быть настойчивым, постоянным, упорствовать (in aliqua re <i>C, L, Su</i>, aliqua re <i>QC</i> <i>и</i> aliquid <i>L, bAfr etc.</i>): p. bellis continuis <i>Just</i> беспрестанно воевать; perseverabat se esse Orestem <i>C</i> (Пилад) настаивал на том (упорно утверждал), что он Орест;\n<b>2)</b> продолжать (fugere <i>L</i>): persevera, ut coepisti <i>Sen</i> продолжай, как начал;\n<b>3)</b> (<i>sc.</i> ire) продолжать путь (navis perseverat <i>Cs</i>): p. Aquilejam usque <i>Su</i> продолжать идти до самой Аквилеи;\n<b>4)</b> продолжаться, длиться (dignitas tribunorum non diu perseveravit <i>Eutr</i>);\n<b>5)</b> упорно держаться, не прекращаться (siccitas perseverat <i>Pt</i>)."],
  ["perseverus", "<big>perseverus</big>\n\n<b>per–severus, a, um</b>\nочень строгий (imperium <i>T</i>)."],
  ["persevi", "<big>persevi</big>\n\n<b>persevi</b> <i>pf. к</i>\npersero II."],
  ["Persia", "<big>Persia</big>\n\n<b>Persia, ae</b> <i>f</i> <i>Pl</i>\n= Persis I."],
  ["Persianus", "<big>Persianus</big>\n\n<b>Persianus, a, um</b> [Persius]\nперсиев <i>Lact, Ap</i>."],
  ["persibus", "<big>persibus</big>\n\n<b>persibus, a, um</b>\nвесьма остроумный, очень хитрый <i>Naev, Pl, Vr</i>."],
  ["Persica", "<big>Persica</big>\n\n<b>Persica, orum</b> <i>n</i>\nистория Персии <i>C</i>."],
  ["persiccus", "<big>persiccus</big>\n\n<b>per–siccus, a, um</b>\nсовершенно сухой (oculi <i>CC</i>)."],
  ["Persice", "<big>Persice</big>\n\n<b>I Persice</b> <i>adj. f</i> (<i>греч. форма</i>) [Persicus]\nперсидская (porticus <i>C</i>).\n\n<b>II Persice</b> <i>adv.</i> [Persicus]\nпо-персидски (loqui <i>Q</i>)."],
  ["Persicum", "<big>Persicum</big>\n\n<b>Persicum, i</b> <i>n</i> [Persicus]\nперсик <i>PM, Pt etc.</i>"],
  ["Persicus", "<big>Persicus</big>\n\n<b>I Persicus, a, um</b>\n<b>1)</b> персидский: P. portus <i>Pl</i> <i>порт в Эвбейском море, где была стоянка персидского флота</i>; Persica (arbor) <i>PM</i> персиковое дерево;\n<b>2)</b> [Perseus I, 2] персеев <i>C, PM</i>.\n\n<b>II Persicus, i</b> <i>f</i> (<i>sc.</i> arbor)\nперсиковое дерево <i>Col</i>."],
  ["Persidae", "<big>Persidae</big>\n\n<b>Persidae, arum</b> <i>m</i>\nпотомки Персея (<i>заглавие трагедии Акция</i>)."],
  ["persideo", "<big>persideo</big>\n\n<b>per–sideo, sedi, sessum, ere</b> <i>QC, PM</i>\n= persedeo."],
  ["persido", "<big>persido</big>\n\n<b>per–sido, sedi, sessum, ere</b>\n<b>1)</b> оседать (in aliquid <i>Lcr</i>);\n<b>2)</b> проникать: frigidus imber persidit ad vivum <i>V</i> холодный дождь пронизывает до живого мяса («до костей»)."],
  ["persigno", "<big>persigno</big>\n\n<b>per–signo, —, —, are</b>\n<b>1)</b> точно отмечать, записывать (dona <i>L</i>);\n<b>2)</b> расписывать <i>или</i> татуировать (corpus notis <i>Mela</i>)."],
  ["persimilis", "<big>persimilis</big>\n\n<b>per–similis, e</b>\nочень похожий (alicujus <i>C</i> <i>или</i> alicui rei <i>H</i>)."],
  ["persimplex", "<big>persimplex</big>\n\n<b>per–simplex, simplicis</b> <i>adj.</i>\nвесьма простой (victus <i>T</i>)."],
  ["Persis", "<big>Persis</big>\n\n<b>I Persis, idis</b> <i>f</i>\nПерсида, <i>область у Персидского залива (ныне Фарсистан) с главн. городом Персеполь</i> <i>V</i>.\n\n<b>II Persis, idis</b> <i>f adj.</i>\nперсидская (rates <i>O</i>)."],
  ["persisto", "<big>persisto</big>\n\n<b>per–sisto, stiti, —, ere</b>\n<b>1)</b> пребывать, оставаться (ante signa <i>QC</i>);\n<b>2)</b> упорствовать (in aliqua re <i>L, Treb</i>)."],
  ["Persius", "<big>Persius</big>\n\n<b>Persius, i</b> <i>m</i>\nПерсии, <i>римск. nomen; наиболее известны</i>:\n<b>1)</b> C. P., <i>оратор, современник Гракхов</i> <i>C</i>;\n<b>2)</b> A. P. Flaccus, <i>родом из Волатерры, поэт-сатирик</i> (<i>34-62 гг. н. э.</i>) <i>Q, M</i>."],
  ["persolata", "<big>persolata</big>\n\n<b>persolata, ae</b> <i>f</i> <i>бот.</i>\nкоровяк (<i>Verbascum</i>, L.), <i>по др.</i> репейник <i>PM</i>."],
  ["persolido", "<big>persolido</big>\n\n<b>per–solido, —, —, are</b>\nуплотнять, <i>перен.</i> замораживать (imbres <i>St</i>)."],
  ["persolla", "<big>persolla</big>\n\n<b>persolla, ae</b> <i>f</i> [<i>demin. к</i> persona]\nмаленькая маска, <i>бран.</i> рожа, харя <i>Pl</i>."],
  ["persollata", "<big>persollata</big>\n\n<b>persollata, ae</b> <i>f</i> <i>v. l.</i>\n= persolata."],
  ["persolus", "<big>persolus</big>\n\n<b>per–solus, a, um</b>\nсовершенно один <i>Pl</i>."],
  ["persolvo", "<big>persolvo</big>\n\n<b>per–solvo, solvi, solutum, ere</b>\n<b>1)</b> полностью разрешать, разъяснять (aliquid alicui <i>C</i>);\n<b>2)</b> выплачивать (stipendium mili tibiis <i>C</i>); погашать, оплачивать (aes alienum <i>PJ</i>): pecuniam ab aliquo p. <i>C</i> уплатить деньги через посредство кого-л. (с переводом на кого-л.) || выдавать (praemia <i>Su</i>);\n<b>3)</b> выражать, воздавать (gratiam alicui <i>C</i>); исполнять (vota <i>C</i>; promissum <i>VP</i>); оказывать (alicui honorem <i>V</i>); justa alicui p. <i>SenT</i> отдать кому-л. последний долг; p. epistulae <i>C</i> отвечать на письмо; poenas p. <i>C, Cs</i> нести наказание, <i>но</i>: poenae alicui ab aliquo persolutae <i>C</i> наказание, наложенное на кого-л. кем-л.; p. poenas capite <i>QC</i> поплатиться головой; alicui mortem p. <i>Su</i> умертвить кого-л."],
  ["persona", "<big>persona</big>\n\n<b>I persona, ae</b> <i>f</i>\n<b>1)</b> маска, личина (<i>преим.</i> театральная) (tragica <i>Ph</i>; comica <i>Q</i>): risus sub persona <i>PS</i> затаённый смех; personam vitae deponere <i>Ap</i> снять маску жизни, <i>т. е.</i> умереть;\n<b>2)</b> театральная роль, характер (alicujus personam capere <i>C</i>; p. alicujus <i>Ter</i> <i>и</i> de aliquo <i>C</i>);\n<b>3)</b> житейская роль, положение, функции (accusatoris, philosophi <i>C</i>): alicujus personam ferre <i>L</i> (tenere, sustinere, gerere, tueri <i>C</i>) представлять кого-л., исполнять чью-л. обязанность;\n<b>4)</b> личность, лицо (de alicujus persona aliquid dicere <i>C</i>): alicujus (in, ex) persona <i>или</i> per personam alicujus <i>C, VP etc.</i> в чьём-л. лице <i>или</i> в качестве кого-л.;\n<b>5)</b> <i>грам.</i> лицо <i>V, Q</i>.\n\n<b>II persona</b> <i>f к</i>\npersonus."],
  ["personalis", "<big>personalis</big>\n\n<b>personalis, e</b> [persona I]\nличный (usus alicujus rei <i>Dig</i>); <i>грам.</i> личный (verbum)."],
  ["personaliter", "<big>personaliter</big>\n\n<b>personaliter</b> [persona I]\n<b>1)</b> лично, в личном плане <i>Eccl</i>;\n<b>2)</b> <i>грам.</i> в личной форме (dicere <i>AG</i>)."],
  ["personata", "<big>personata</big>\n\n<b>personata, ae</b> <i>f</i>\nрепейник крупный <i>PM, Col, Veg</i>."],
  ["personatus", "<big>personatus</big>\n\n<b>personatus, a, um</b> [persona]\n<b>1)</b> надевший личину, замаскированный (Roscius <i>C</i>): pater p. <i>H</i> актёр в роли отца;\n<b>2)</b> мнимый, ложный (felicitas <i>Sen</i>); деланный, притворный (fastus <i>M</i>)."],
  ["persono", "<big>persono</big>\n\n<b>per–sono, ui, itum, are</b>\n<b>1)</b> громко звучать, раздаваться (aliquid totis castris personabat <i>L</i>); оглашаться (domus personat cantu <i>C</i>; convivium risu personat <i>Ap</i>): aures personant hujusmodi vocibus <i>C</i> эти звуки раздаются в ушах;\n<b>2)</b> играть (tibia <i>Ap</i>): p. cithara <i>V</i> играть на кифаре;\n<b>3)</b> оглашать (silvas, litora <i>T</i>): plausibus p. <i>T</i> оглушительно рукоплескать; p. classicum <i>Ap</i> давать сигнал к бою;\n<b>4)</b> громко говорить, шумно обсуждать (aliquid <i>C</i>): p. aurem <i>H</i> кричать на ухо;\n<b>5)</b> громко прославлять (aliquem in psalmo <i>Aug</i>)."],
  ["personus", "<big>personus</big>\n\n<b>per–sonus, a, um</b>\n<b>1)</b> звучащий, играющий (Io persona sistro <i>VF</i>);\n<b>2)</b> оглашаемый (verno cantu persona virgulta <i>Pt</i>)."],
  ["persorbeo", "<big>persorbeo</big>\n\n<b>per–sorbeo, —, —, ere</b>\nвсасывать, впитывать (spongia persorbens <i>PM</i>)."],
  ["perspargo", "<big>perspargo</big>\n\n<b>perspargo</b> <i>v. l.</i>\n= perspergo."],
  ["perspecte", "<big>perspecte</big>\n\n<b>perspecte</b> [perspectus]\nумно, разумно, рассудительно (docte et p. <i>Pl</i>)."],
  ["perspectio", "<big>perspectio</big>\n\n<b>perspectio, onis</b> <i>f</i>\nпостижение (veritatis <i>Aug</i>; rerum celestium <i>Lact</i>)."],
  ["perspectiva", "<big>perspectiva</big>\n\n<b>perspectiva, ae</b> <i>f</i> [perspecto] (<i>sc.</i> ars)\nоптика <i>Boet</i>."],
  ["perspecto", "<big>perspecto</big>\n\n<b>perspecto, avi, —, are</b> [<i>intens. к</i> perspicio]\n<b>1)</b> смотреть до конца, досмотреть (certamen <i>Su</i>);\n<b>2)</b> осматривать, внимательно вглядываться (p. aliquid <i>Pl</i>)."],
  ["perspector", "<big>perspector</big>\n\n<b>perspector, oris</b> <i>m</i>\nпостигающий: p. mentis <i>Jvc</i> сердцевед."],
  ["perspectus", "<big>perspectus</big>\n\n<b>perspectus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> perspicio;\n<b>2.</b> <i>adj.</i> хорошо известный (alicui <i>C</i>); испытанный (virtus <i>C</i>): aliquid perspectum habere <i>C</i> хорошо знать что-л."],
  ["perspeculor", "<big>perspeculor</big>\n\n<b>per–speculor, atus sum, ari</b> <i>depon.</i>\nвнимательно рассматривать, исследовать, разведывать (locorum situs <i>Su</i>)."],
  ["perspergo", "<big>perspergo</big>\n\n<b>per–spergo, spersi, spersum, ere</b> [spargo]\nобрызгивать, окроплять (templum aqua <i>T</i>; membra aromatis perspersa <i>Ap</i>; <i>перен.</i> lepore, tamquam sale, perspergatur omnis oratio <i>C</i>)."],
  ["perspicabilis", "<big>perspicabilis</big>\n\n<b>perspicabilis, e</b>\nдостойный обозрения, достопримечательный (urbs <i>Aug, Amm</i>)."],
  ["perspicacitas", "<big>perspicacitas</big>\n\n<b>perspicacitas, atis</b> <i>f</i> [perspicax]\nпроницательность, прозорливость <i>Amm</i>."],
  ["perspicaciter", "<big>perspicaciter</big>\n\n<b>perspicaciter</b>\nпроницательно, прозорливо <i>Amm</i>."],
  ["perspicax", "<big>perspicax</big>\n\n<b>perspicax, acis</b> <i>adj.</i> [perspicio]\nзоркий (ales <i>Ap</i>); проницательный, прозорливый (judex <i>C, Ap</i>)."],
  ["perspicibilis", "<big>perspicibilis</big>\n\n<b>perspicibilis, e</b> [perspicio]\nясный, светлый, яркий (splendor solis <i>Vtr</i>)."],
  ["perspicientia", "<big>perspicientia</big>\n\n<b>perspicientia, ae</b> <i>f</i> [perspicio]\nпознание, постижение, уразумение (veri <i>C</i>)."],
  ["perspicio", "<big>perspicio</big>\n\n<b>per–spicio, spexi, spectum, ere</b> [specio]\n<b>1)</b> смотреть (видеть) сквозь, проникать взором (introrsus <i>C</i>; quid intus agatur <i>Pl</i>);\n<b>2)</b> проникать, постигать (alicujus innocentiam <i>C</i>; se ipsum <i>C</i>): fidem alicujus in pecunia p. <i>Ter</i> убедиться в чьей-л. денежной честности; p. aliquid coniectura <i>C</i> догадываться о чём-л.; aliquid perspectum habere <i>C</i> быть убеждённым в чём-л.;\n<b>3)</b> ясно видеть (caelum <i>L</i>; cuncta <i>QC</i>): spero vel potius perspicio <i>C</i> я надеюсь или, вернее, предвижу;\n<b>4)</b> пристально рассматривать, внимательно разглядывать (opus <i>Cs, C, O</i>; domum <i>C</i>): p. litteras <i>C</i> внимательно читать письмо."],
  ["perspicue", "<big>perspicue</big>\n\n<b>perspicue</b> [perspicuus]\nясно, очевидным образом (plene et p. <i>C</i>; dilucide atque p. <i>PM</i>)."],
  ["perspicuitas", "<big>perspicuitas</big>\n\n<b>perspicuitas, atis</b> <i>f</i> [perspicuus]\n<b>1)</b> прозрачность (vitrea <i>PM</i>);\n<b>2)</b> ясность, очевидность (in verbis <i>Q</i>; p. atque evidentia <i>C</i>)."],
  ["perspicuus", "<big>perspicuus</big>\n\n<b>perspicuus, a, um</b> [perspicio]\n<b>1)</b> светлый, прозрачный (aqua <i>O</i>; amnis <i>St</i>);\n<b>2)</b> ясный, очевидный (veritas <i>C</i>)."],
  ["perspiro", "<big>perspiro</big>\n\n<b>per–spiro, —, —, are</b>\n<b>1)</b> дышать (ubi venae non possunt p. in toto corpore <i>Cato</i>);\n<b>2)</b> постоянно дуть (venti perspirantes <i>PM</i>)."],
  ["perspisso", "<big>perspisso</big>\n\n<b>per–spisso</b> <i>adv.</i> [spissus]\nчрезвычайно медленно (evenire <i>Pl</i>)."],
  ["persterno", "<big>persterno</big>\n\n<b>per–sterno, stravi, stratum, ere</b>\nделать совершенно ровным <i>Vtr</i>; мостить (viam <i>L</i>)."],
  ["perstillo", "<big>perstillo</big>\n\n<b>per–stillo, —, —, are</b>\nпропускать влагу, протекать (tecta perstillantia <i>Vlg</i>)."],
  ["perstimulo", "<big>perstimulo</big>\n\n<b>per–stimulo, —, —, are</b>\nпостоянно возбуждать, подстрекать (spiritus tumidos <i>T</i>)."],
  ["perstipo", "<big>perstipo</big>\n\n<b>per–stipo, —, —, are</b>\nсильно уплотнять (aliquid <i>CA</i>)."],
  ["perstiti", "<big>perstiti</big>\n\n<b>perstiti</b> <i>pf. к</i>\n<b>1)</b> persisto <i>и</i>\n<b>2)</b> persto."],
  ["persto", "<big>persto</big>\n\n<b>per–sto, stiti, (statum), are</b>\n<b>1)</b> твёрдо стоять, оставаться на месте, не сходить с места (p. ad vallum <i>L</i>; p. totum diem <i>L</i>); продолжать (facere aliquid <i>O</i>);\n<b>2)</b> твёрдо пребывать, упорно держаться, упорствовать (in sententia <i>Cs, L</i>; in incepto <i>L, O</i>; in impudentia <i>C</i>; amore <i>SenT</i>): hiems perstat <i>O</i> зима упорно держится (продолжается); eadem mens perstat mihi <i>V</i> я остаюсь при своём решении (моё решение неизменно)."],
  ["perstratus", "<big>perstratus</big>\n\n<b>perstratus, a, um</b> <i>part. pf. к</i>\npersterno."],
  ["perstrepo", "<big>perstrepo</big>\n\n<b>per–strepo, ui, itum, ere</b>\n<b>1)</b> сильно шуметь (perstrepit rumor in aura <i>St</i>); оглушительно гудеть, грохотать (tellus perstrepit <i>Sil</i>);\n<b>2)</b> оглашать (latas aedes <i>Cld</i>; clamore perstrepi <i>Ap</i>): p. maledictis <i>Ap</i> изрыгать проклятия, громко браниться."],
  ["perstricte", "<big>perstricte</big>\n\n<b>perstricte</b>\nсжато, кратко <i>Aug</i>."],
  ["perstrictim", "<big>perstrictim</big>\n\n<b>perstrictim</b> <i>Aug</i>\n= praestrictim."],
  ["perstrictio", "<big>perstrictio</big>\n\n<b>perstrictio, onis</b> <i>f</i> [perstringo]\nстяжение <i>Veg</i>."],
  ["perstringo", "<big>perstringo</big>\n\n<b>per–stringo, strinxi, strictum, ere</b>\n<b>1)</b> перевязывать, подвязывать (vites <i>Cato</i>); стягивать (rigore perstrictus <i>Veg</i>);\n<b>2)</b> бороздить (solum aratro <i>C</i>);\n<b>3)</b> легко ранить, оцарапать (femur mucrone <i>QC</i>);\n<b>4)</b> задевать (за живое), неприятно поражать (aliquem <i>C</i> <i>или</i> animum alicujus <i>QC</i>);\n<b>5)</b> овладевать, охватывать (horror aliquem perstringit <i>L</i>); оглушать (aures <i>H</i>); ослеплять (oculos <i>L</i>);\n<b>6)</b> порицать, пробирать (habitum alicujus <i>T</i>): modice perstringi <i>T</i> отделаться лёгким выговором;\n<b>7)</b> слегка касаться, вскользь упоминать (breviter transire ac p. unamquamque rem <i>C</i>)."],
  ["perstruo", "<big>perstruo</big>\n\n<b>per–struo, —, structum, ere</b>\nвыстраивать (paries in altitudinem perstructus <i>Vtr</i>)."],
  ["perstudiose", "<big>perstudiose</big>\n\n<b>perstudiose</b> [perstudiosus]\nвесьма усердно, жадно (audire aliquem <i>C</i>)."],
  ["perstudiosus", "<big>perstudiosus</big>\n\n<b>per–studiosus, a, um</b>\nвесьма прилежный, усердно занимающийся: p. litterarum <i>C</i> целиком отдавшийся наукам."],
  ["persuadeo", "<big>persuadeo</big>\n\n<b>per–suadeo, suasi, suasum, ere</b>\n<b>1)</b> убеждать, уверять, внушать (alicui de aliqua re <i>или</i> aliquid <i>C, Cs etc.</i>): persuadentia verba <i>O</i> убедительные речи; mihi persuadetur <i>C etc.</i> я убеждаюсь; persuasus sum, mihi persuasum (persuasissimum) est, mihi persuasi <i>C etc.</i>, <i>реже</i> (mihi) persuasum habeo <i>Cs, PJ etc.</i> я убеждён; persuade tibi hoc <i>Sen</i> будь в этом уверен;\n<b>2)</b> побуждать, склонять, уговаривать (aliquem <i>Pt</i>): mihi persuasum est <i>C</i> <i>или</i> persuasus sum <i>Just</i> я решил(ся) (aliquid facere)."],
  ["persuasibilis", "<big>persuasibilis</big>\n\n<b>persuasibilis, e</b> [persuadeo]\nубедительный <i>Q, Vlg, Eccl</i>."],
  ["persuasibiliter", "<big>persuasibiliter</big>\n\n<b>persuasibiliter</b>\nубедительно (dicere <i>Q</i>)."],
  ["persuasio", "<big>persuasio</big>\n\n<b>persuasio, onis</b> <i>f</i> [persuadeo]\n<b>1)</b> уговаривание, уверение (dicere ad persuasionem <i>C</i>);\n<b>2)</b> убеждение, мнение, уверенность: persuasionis plenus cuncta fato agi <i>Su</i> (Тиберий) был твёрдо убеждён, что всё происходит по предначертаниям рока; superstitionum persuasione <i>T</i> из суеверного мнения, из суеверия."],
  ["persuasor", "<big>persuasor</big>\n\n<b>persuasor, oris</b> <i>m</i>\nуговаривающий, соблазнитель <i>Eccl</i>."],
  ["persuastrix", "<big>persuastrix</big>\n\n<b>persuastrix, icis</b> <i>f</i> [persuadeo]\nсоблазнительница (pellecebrae et persuastrices <i>Pl</i>)."],
  ["persuasus", "<big>persuasus</big>\n\n<b>I persuasus, us</b> <i>m</i> [persuadeo]\nуговаривание, уговоры (persuasu alicujus <i>Pl, C etc.</i>).\n\n<b>II persuasus, a, um</b> <i>part. pf. к</i>\npersuadeo."],
  ["persuaviter", "<big>persuaviter</big>\n\n<b>persuaviter</b>\nчрезвычайно сладостно (<i>для слуха</i>) <i>Aug</i>."],
  ["persubtilis", "<big>persubtilis</big>\n\n<b>per–subtilis, e</b>\n<b>1)</b> очень тонкий, тончайший (animus <i>Lcr</i>);\n<b>2)</b> глубоко обдуманный (oratio <i>C</i>)."],
  ["persulcatus", "<big>persulcatus</big>\n\n<b>per–sulcatus, a, um</b>\nизборождённый (rugis p. genas — <i>acc. gr. Cld</i>)."],
  ["persultator", "<big>persultator</big>\n\n<b>persultator, oris</b> <i>m</i> [persulto]\nперепрыгивающий, проносящийся (p. litoris <i>Symm</i>)."],
  ["persulto", "<big>persulto</big>\n\n<b>per–sulto, avi, atum, are</b> [salto]\n<b>1)</b> скакать, рыскать, носиться (in agro <i>L</i>);\n<b>2)</b> объезжать (Italiam <i>T</i>; maria <i>Ap</i>)."],
  ["pertabesco", "<big>pertabesco</big>\n\n<b>(per–tabesco), tabui, —, ere</b>\nсовершенно уничтожаться, разрушаться (summo igni <i>LJ</i>)."],
  ["pertaedesco", "<big>pertaedesco</big>\n\n<b>per–taedesco, taedui, —, ere</b>\n<b>1)</b> испытывать отвращение (quum omnes pertaeduissent <i>AG</i>);\n<b>2)</b> становиться невыносимым <i>Cato</i>."],
  ["pertaedet", "<big>pertaedet</big>\n\n<b>per–taedet, pertaesum</b> (<i>арх.</i> <b>pertisum</b>) <b>est, ere</b> <i>impers.</i>\nвесьма скучно, противно: me pertaesum est hujus rei <i>C, V, Nep etc.</i> мне это надоело (опротивело)."],
  ["pertaesus", "<big>pertaesus</big>\n\n<b>pertaesus, a, um</b> [pertaedet]\nиспытывающий скуку, питающий отвращение (alicujus rei <i>T</i> <i>и</i> aliquid <i>Su</i>)."],
  ["pertego", "<big>pertego</big>\n\n<b>per–tego, texi, tectum, ere</b>\nсовершенно покрывать, перекрывать (aliquid aliqua re <i>Pl, Vr, Vtr etc.</i>)."],
  ["pertemerarius", "<big>pertemerarius</big>\n\n<b>per–temerarius, a, um</b>\nкрайне безрассудный <i>CJ</i> (<i>in tmesi</i>)."],
  ["pertempto", "<big>pertempto</big>\n\n<b>pertempto</b> <i>v. l.</i>\n= pertento."],
  ["pertendo", "<big>pertendo</big>\n\n<b>per–tendo, tendi, tentum (tensum), ere</b>\n<b>1)</b> делать, совершать (aliquid <i>Ter</i>);\n<b>2)</b> отправляться, идти, двигаться (Romam <i>L</i>; ad alteram ripam <i>Su</i>);\n<b>3)</b> стремиться осуществить, проявлять настойчивость, упорствовать (p. naviter <i>Ter</i>): pertendens animo <i>Prp</i> упорный (упорно)."],
  ["pertento", "<big>pertento</big>\n\n<b>per–tento (pertempto), avi, atum, are</b>\n<b>1)</b> пробовать (pugionem <i>T</i>); испытывать, исследовать (animos militum <i>L</i>; aliquem aliqua re <i>Ter</i>);\n<b>2)</b> обдумывать, взвешивать (omnia <i>PJ</i>);\n<b>3)</b> проникать, преисполнять, охватывать (gaudium pertentat pectus <i>V</i>; tremor pertentat corpora <i>V</i>);\n<b>4)</b> пытаться пробить (bipenni aliquid <i>Pt</i>)."],
  ["pertenuis", "<big>pertenuis</big>\n\n<b>per–tenuis, e</b>\n<b>1)</b> очень тонкий, мельчайший (sabulum <i>Cato</i>);\n<b>2)</b> весьма слабый, ничтожный (spes, suspicio <i>C</i>);\n<b>3)</b> совсем незначительный (patrimonium <i>Capit</i>)."],
  ["pertepidus", "<big>pertepidus</big>\n\n<b>per–tepidus, a, um</b>\nочень тёплый (aqua <i>Vop</i>)."],
  ["perterebro", "<big>perterebro</big>\n\n<b>per–terebro, avi, atum, are</b>\nпробуравливать, просверливать (aliquid <i>C, Vtr etc.</i>)."],
  ["pertergeo", "<big>pertergeo</big>\n\n<b>per–tergeo, tersi, tersum, ere</b>\n<b>1)</b> вытирать (mensam aliqua re <i>H</i>);\n<b>2)</b> (при)касаться (lux oculos perterget <i>Lcr</i>)."],
  ["pertero", "<big>pertero</big>\n\n<b>per–tero, trivi, tritum, ere</b>\nрастирать, раздавливать (bacam <i>Col</i>)."],
  ["perterrefacio", "<big>perterrefacio</big>\n\n<b>per–terrefacio, (feci), factum, ere</b>\nсильно пугать, ужасать (aliquem <i>Ter</i>)."],
  ["perterreo", "<big>perterreo</big>\n\n<b>per–terreo, terrui, territum, ere</b> (<i>тж.</i> metu p. <i>C</i>)\nустрашать, приводить в ужас (aliquem magnitudine poenae <i>Cs</i>): p. ab aliqua re <i>C</i> отпугнуть от чего-л., страхом держать вдали от чего-л."],
  ["perterricrepus", "<big>perterricrepus</big>\n\n<b>perterricrepus, a, um</b> [perterreo + crepo]\nстрашно шумный, ужасно грохочущий (sonitus <i>Lcr</i>)."],
  ["pertersi", "<big>pertersi</big>\n\n<b>pertersi</b> <i>pf. к</i>\npertergeo."],
  ["pertexo", "<big>pertexo</big>\n\n<b>per–texo, texui, textum, ere</b>\n<b>1)</b> выткать <i>или</i> проткать (vestis bysso pertexta <i>Ap</i>);\n<b>2)</b> украшать (Odeum navium malis <i>Vtr</i>);\n<b>3)</b> завершать, заканчивать (coeptum <i>Lcr</i>): pertexe, quod exorsus es <i>C</i> кончай, что ты начал (говорить)."],
  ["pertica", "<big>pertica</big>\n\n<b>pertica, ae</b> <i>f</i>\n<b>1)</b> шест, жердь <i>Pl, Vr, Col etc.</i>; палка <i>Pl, Ap</i>; землемерный шест <i>Prp</i>;\n<b>2)</b> мера, масштаб: una pertica tractare <i>погов. PJ</i> = мерить на один аршин;\n<b>3)</b> саженец <i>PM</i>."],
  ["perticalis", "<big>perticalis</big>\n\n<b>perticalis, e</b> [pertica]\nгодный для изготовления жердей (salix <i>Col, PM</i>)."],
  ["perticatus", "<big>perticatus</big>\n\n<b>perticatus, a, um</b> [pertica]\nукреплённый на жерди (pondus <i>M</i>)."],
  ["pertimefacio", "<big>pertimefacio</big>\n\n<b>per–timefacio, feci, factum, ere</b>\nсильно пугать, приводить в ужас (aliquem <i>Pac, Brutus ap. C</i>)."],
  ["pertimeo", "<big>pertimeo</big>\n\n<b>per–timeo, —, —, ere</b>\nсильно бояться <i>Vlg</i>."],
  ["pertimesco", "<big>pertimesco</big>\n\n<b>per–timesco, timui, —, ere</b>\nпугаться, страшиться, бояться (de aliqua re <i>C, Nep</i>, ob aliquid <i>AG</i> <i>или</i> aliquid <i>C etc.</i>; horribilis aut pertimescendus <i>C</i>): longa obsidio pertimescenda est <i>Cs</i> следует опасаться длительной осады."],
  ["pertinacia", "<big>pertinacia</big>\n\n<b>pertinacia, ae</b> <i>f</i> [pertinax]\nупрямство, упорство, неуступчивость <i>Acc, Ter, Cs, L etc.</i>"],
  ["pertinaciter", "<big>pertinaciter</big>\n\n<b>pertinaciter</b> [pertinax]\nкрепко (haerere <i>Q</i>); <i>перен.</i> упорно, упрямо (insequi <i>L</i>; studere <i>Sen</i>)."],
  ["pertinax", "<big>pertinax</big>\n\n<b>I per–tinax, acis</b> <i>adj.</i> [tenax]\n<b>1)</b> цепкий (digitus <i>H</i>);\n<b>2)</b> скупой (homo <i>Pl</i>);\n<b>3)</b> долго сохраняющийся (siligo <i>PM</i>);\n<b>4)</b> непрерывный (lacrimae <i>Q</i>);\n<b>5)</b> настойчивый (spiritus <i>Q</i>); стойкий (miles <i>L</i>); непоколебимый (virtus <i>L</i>); твёрдый, неуступчивый, непримиримый (sermo <i>C</i>); упрямый, упорный (in aliqua re <i>L, Fl etc.</i>; alicujus rei <i>VM, Ap</i>; ad, in <i>или</i> adversus aliquid <i>L, Sen</i>); упорный, ожесточённый (certamen <i>L</i>);\n<b>6)</b> неумолимый (fortuna <i>H</i>).\n\n<b>II Pertinax, acis</b> <i>m</i>\n(Helvius) Пертинак(с), <i>римск. император с 1.I по 28.III 193 г. н. э.</i> <i>Capit</i>."],
  ["pertinenter", "<big>pertinenter</big>\n\n<b>pertinenter</b> [pertineo]\nв связи (с), применительно (к) (p. ad causam <i>Tert</i>)."],
  ["pertineo", "<big>pertineo</big>\n\n<b>per–tineo, tinui, —, ere</b> [teneo]\n<b>1)</b> простираться, тянуться, достигать (ab <i>или</i> ex aliqua re, in <i>или</i> ad aliquid <i>C, Cs etc.</i>): pons ex oppido ad Helvetios pertinet <i>Cs</i> мост ведёт из города в область гельветов; rivi ad mare pertinent <i>Cs</i> потоки вливаются в море; quingenta milia passuum p. <i>Cs</i> тянуться (простираться) на 500 (римских) миль; caritas patriae per omnes pertinebat <i>L</i> любовь к родине охватила всех (граждан);\n<b>2)</b> касаться, относиться (suspicio pertinet ad aliquem <i>C</i>): quod ad indutias pertinet <i>Cs</i> что касается перемирия; nec ad caelum nec ad terram p. <i>погов. Pt</i> не иметь отношения ни к небу, ни к земле (<i>ср.</i> «ни к селу ни к городу»); ad rectam vivendi viam p. <i>C</i> учить как правильно жить; ad sitim p. <i>Ap</i> (хорошо) утолять жажду; culpa pertinet ad aliquem <i>C</i> вина падает на кого-л.; illud, quo pertineat, videte <i>C</i> обратите внимание на то, к чему ведёт следующее;\n<b>3)</b> принадлежать (praeda pertinet ad milites <i>L</i>);\n<b>4)</b> служить, способствовать (ad usum alicujus <i>Cs, O</i>; ad effeminandos animos <i>Cs</i>): quo (quorsum) pertinet? <i>T, H</i>. к чему (это)?; quae ad victum pertinent <i>Nep</i> съестные припасы; res ad luxuriam pertinentes <i>Cs</i> предметы роскоши; ad spem p. <i>C</i> подавать (внушать) надежду; ad felicitatem alicujus p. <i>Sen</i> иметь целью чьё-л. счастье; ad aliquam urbanitatem p. <i>Pt</i> содержать какой-то хитрый замысел;\n<b>5)</b> иметь значение, быть важным: ad me maxime pertinet, neminem esse... <i>C</i> для меня чрезвычайно важно, чтобы никто."],
  ["pertingo", "<big>pertingo</big>\n\n<b>per–tingo, —, —, ere</b> [tango]\nкасаться, достигать, простираться (in immensum <i>Sl</i>)."],
  ["pertisum", "<big>pertisum</big>\n\n<b>pertisum</b> <i>арх. LM</i>\n= pertaesum."],
  ["pertolero", "<big>pertolero</big>\n\n<b>per–tolero, avi, atum, are</b>\nпретерпеть до конца, вынести (omnia tormenta <i>Lcr</i>; vitam <i>Acc</i>)."],
  ["pertono", "<big>pertono</big>\n\n<b>per–tono, tonui, —, are</b>\nгреметь, возвещать громовым голосом (gloriam alicujus <i>Ambr</i>; contra aliquem <i>Hier</i>)."],
  ["pertorqueo", "<big>pertorqueo</big>\n\n<b>per–torqueo, —, —, ere</b>\nискривлять, морщить (ora foedo sapore <i>Lcr</i>)."],
  ["pertractate", "<big>pertractate</big>\n\n<b>pertractātē</b> [pertracto]\nобычным образом, как принято (fabulam facere <i>Pl</i>)."],
  ["pertractatio", "<big>pertractatio</big>\n\n<b>pertractātio, ōnis</b> <i>f</i> [pertracto]\nзанятие, работа (над чём-л.) (rerum publicarum <i>C</i>); прикосновение <i>или</i> исследование (partium sincerarum aut aegrarum <i>AG</i>)."],
  ["pertracto", "<big>pertracto</big>\n\n<b>per–tracto, āvī, ātum, āre</b>\n<b>1)</b> щупать, ощупывать (aliquid manu <i>bAfr</i>); исследовать (vulnera visu <i>Sil</i>);\n<b>2)</b> разрабатывать, изучать (philosophiam <i>C</i>): aliquid pertractatum habere <i>C</i> изучить (обдумать, разработать) что-л.; p. aliquid cogitatione <i>C</i> обдумывать что-л.;\n<b>3)</b> воздействовать, настраивать (sensus mentesque hominum <i>C</i>)."],
  ["pertractus", "<big>pertractus</big>\n\n<b>I pertractus, a, um</b> <i>part. pf. к</i>\npertraho.\n\n<b>II pertractus, ūs</b> <i>m</i>\nзатяжка (во времени) <i>Tert</i>."],
  ["pertraho", "<big>pertraho</big>\n\n<b>per–traho, trāxī, tractum, ere</b>\n<b>1)</b> тащить (ratem ad ripam <i>L</i>); приводить силой (aliquem Romam <i>Ph</i>; aliquem in jus <i>VM</i>);\n<b>2)</b> завлекать, заманивать (hostem ad insidiarum locum <i>L</i>);\n<b>3)</b> извлекать, удалять (virus <i>Scr</i>)."],
  ["pertralucidus", "<big>pertralucidus</big>\n\n<b>pertrālūcidus</b> <i>v. l.</i>\n= pertranslucidus."],
  ["pertranseo", "<big>pertranseo</big>\n\n<b>per–trānseo, īvī (iī), —, īre</b>\n<b>1)</b> проходить насквозь (fines Assyriorum <i>Vlg</i>); переходить (a vertice montis usque ad fontem aquae <i>Vlg</i>);\n<b>2)</b> обходить (omnes beneficiendo et sanando <i>Vlg</i>);\n<b>3)</b> проходить, миновать (quum pertransissent decem dies <i>Vlg</i>; donec pertranseat furor tuus <i>Vlg</i>)."],
  ["pertranslucidus", "<big>pertranslucidus</big>\n\n<b>per–trānslūcidus, a, um</b>\nсовершенно прозрачный (charta <i>PM</i>)."],
  ["pertrect-", "<big>pertrect-</big>\n\n<b>pertrect-</b> <i>Pt, Ap</i>\n(<i>v. l.</i>) = pertract-."],
  ["pertremisco", "<big>pertremisco</big>\n\n<b>per–tremisco, —, —, ere</b>\nсильно содрогаться: p. aliquid <i>Hier</i> быть в ужасе от чего-л."],
  ["pertrepidus", "<big>pertrepidus</big>\n\n<b>pertrepidus</b> <i>Capit</i>\n(<i>v. l.</i>) = praetrepidus."],
  ["pertribuo", "<big>pertribuo</big>\n\n<b>per–tribuo, bui, —, ere</b>\nраздавать, выдавать (alicui testimonia <i>PJ</i>)."],
  ["pertricosus", "<big>pertricosus</big>\n\n<b>per–tricosus, a, um</b>\nзапутанный, сложный (res <i>M</i>)."],
  ["pertristis", "<big>pertristis</big>\n\n<b>per–tristis, e</b>\n<b>1)</b> очень печальный, грустный (carmen <i>C</i>);\n<b>2)</b> очень угрюмый, мрачный (homo, censor <i>C</i>)."],
  ["pertritus", "<big>pertritus</big>\n\n<b>pertritus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> pertero;\n<b>2.</b> <i>adj.</i> обыденный, избитый, заурядный (quaestio <i>Sen</i>)."],
  ["pertrux", "<big>pertrux</big>\n\n<b>pertrux</b> <i>Ap</i>\n(<i>v. l.</i>) = trux."],
  ["pertudi", "<big>pertudi</big>\n\n<b>pertudi</b> <i>pf. к</i>\npertundo."],
  ["pertuli", "<big>pertuli</big>\n\n<b>pertuli</b> <i>pf. к</i>\npertero."],
  ["pertumultuose", "<big>pertumultuose</big>\n\n<b>per–tumultuose</b>\nвесьма возбуждённо, очень шумно (aliquid nuntiare <i>C</i>)."],
  ["pertundo", "<big>pertundo</big>\n\n<b>per–tundo, tudi, tusum (tunsum), ere</b>\nпробивать, проламывать (saxa <i>Lcr</i>); разбивать, раскалывать (ova <i>Pt</i>); прокалывать (aures <i>Pt</i>); продырявливать (tunicam <i>Ctl</i>); просверливать (dolium <i>Pl, L</i>); (<i>о моли</i>) прогрызать (libellos <i>J</i>)."],
  ["perturbate", "<big>perturbate</big>\n\n<b>perturbate</b> [perturbatus]\nпутано, беспорядочно (dicere <i>C</i>; moveri <i>Sen</i>)."],
  ["perturbatio", "<big>perturbatio</big>\n\n<b>perturbatio, onis</b> <i>f</i> [perturbo]\n<b>1)</b> расстройство, замешательство, потрясение, смятение (rerum <i>C</i>; totius exercitus <i>Cs</i>): p. valetudinis <i>C</i> расстроенное здоровье; p. caeli <i>C</i> бурная погода;\n<b>2)</b> бурное собрание (comitiorum <i>C</i>);\n<b>3)</b> смущение, возбуждение, волнение (animi <i>C</i>); душевное волнение, сильная страсть (perturbationes concitare <i>C</i>)."],
  ["perturbator", "<big>perturbator</big>\n\n<b>perturbator, oris</b> <i>m</i>\nвозмутитель <i>Eccl</i>."],
  ["perturbatrix", "<big>perturbatrix</big>\n\n<b>perturbatrix, icis</b> <i>f</i> [perturbo]\nвозмутительница, нарушительница (harum omnium rerum <i>C</i>)."],
  ["perturbatus", "<big>perturbatus</big>\n\n<b>perturbatus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> perturbo;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> неспокойный, бурный (genus tempestatis <i>Sen</i>; tempora <i>C</i>);\n<b>2)</b> расстроенный, взволнованный (vultus <i>L</i>);\n<b>3)</b> беспорядочный, путаный (oratio <i>C</i>)."],
  ["perturbidus", "<big>perturbidus</big>\n\n<b>per–turbidus, a, um</b>\nкрайне неспокойный (civitas <i>Vop</i>)."],
  ["perturbo", "<big>perturbo</big>\n\n<b>per–turbo, avi, atum, are</b>\n<b>1)</b> приводить в беспорядок, в расстройство, в замешательство (hostes, aciem <i>Sl</i>; provinciam <i>C</i>);\n<b>2)</b> приводить в смущение, смущать, ошеломлять (animum, aliquem <i>C etc.</i>): nolite perturbari <i>Pt</i> не волнуйтесь || расстраивать, нарушать (otium, condiciones <i>C</i>; ordinem <i>C, Cs</i>)."],
  ["perturpis", "<big>perturpis</big>\n\n<b>per–turpis, e</b>\nкрайне позорный, гнусный <i>C</i>."],
  ["pertussus", "<big>pertussus</big>\n\n<b>pertussus, a, um</b> <i>арх.</i>\n= pertusus."],
  ["pertusura", "<big>pertusura</big>\n\n<b>pertusura, ae</b> <i>f</i> [pertundo]\nпросверливание, прокол <i>CA</i>."],
  ["pertusus", "<big>pertusus</big>\n\n<b>pertusus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> pertundo;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> дырявый (vas <i>Lcr</i>; dolium <i>Pl, L</i>);\n<b>2)</b> проходимый (compita <i>Pers</i>)."],
  ["perubique", "<big>perubique</big>\n\n<b>per–ubique</b>\nповсюду <i>Tert</i>."],
  ["perula", "<big>perula</big>\n\n<b>perula, ae</b> <i>f</i> [<i>demin. к</i> pera]\nсумочка <i>Sen</i>; <i>шутл.</i> материнская утроба <i>Ap</i>."],
  ["perunctio", "<big>perunctio</big>\n\n<b>perunctio, onis</b> <i>f</i> [perungo]\nнатирание мазью <i>PM, CA</i>."],
  ["perungo", "<big>perungo</big>\n\n<b>per–ungo, unxi, unctum, ere</b>\nсмазывать, натирать (corpora oleo <i>C</i>); вымазать (ora manu <i>O</i>)."],
  ["perurbane", "<big>perurbane</big>\n\n<b>perurbane</b>\nвесьма тонко, изысканно, учтиво (respondere <i>Sid</i>)."],
  ["perurbanus", "<big>perurbanus</big>\n\n<b>per–urbanus, a, um</b>\nвесьма учтивый, чрезвычайно тонкий, изысканный <i>C</i>."],
  ["perurgeo", "<big>perurgeo</big>\n\n<b>per–urgeo (-urgueo), ursi, —, ere</b>\n<b>1)</b> прижимать, теснить (aliquem <i>Amm, Treb etc.</i>): perurgeri sub hora mortis <i>Spart</i> чувствовать приближение смерти;\n<b>2)</b> вынуждать (aliquem ad aliquid <i>Su</i>);\n<b>3)</b> усердно заниматься : p. festinationem <i>Treb</i> усиленно торопить; sacra p. <i>LJ</i> посвятить себя исследованию сокровенных тайн."],
  ["peruro", "<big>peruro</big>\n\n<b>per–uro, ussi, ustum, ere</b>\n<b>1)</b> сжигать (agrum <i>PM</i>; ossa <i>O</i>); обжигать, опалять (perustus sole <i>H</i>): zona perusta <i>Lcn, Macr</i> жаркий пояс; gelu perustus <i>O</i> промёрзший;\n<b>2)</b> жечь, томить (perustus febre <i>PJ</i>; sitis perurit saucios <i>QC</i>); растравлять (vulnera <i>Col</i>); мучить, терзать (curis <i>M</i>): perustus gloria <i>C</i> томимый жаждой славы;\n<b>3)</b> приводить в гнев, выводить из себя (aliquem <i>Ctl, Sen</i>);\n<b>4)</b> повреждать (uvae peruruntur <i>Col</i>);\n<b>5)</b> натирать до ран, (из)ранить (colla perusta <i>O</i>)."],
  ["Perusia", "<big>Perusia</big>\n\n<b>Perusia, ae</b> <i>f</i>\nПерузия, <i>один из 12 союзных городов Этрурии к вост. от Тразименского озера</i> (<i>ныне</i> Perugia) <i>L, VP</i>."],
  ["Perusinum", "<big>Perusinum</big>\n\n<b>Perusinum, i</b> <i>m</i>\nПерузин, <i>дачное место в окрестностях Перузии</i> <i>PJ</i>."],
  ["Perusinus", "<big>Perusinus</big>\n\n<b>I Perusinus, a, um</b> [Perusia]\nперузийский <i>L, Prp, VP, Lcn</i>.\n\n<b>II Perusinus, i</b> <i>m</i>\nжитель города Perusia <i>L, VP</i>."],
  ["perustus", "<big>perustus</big>\n\n<b>perustus, a, um</b> <i>part. pf. к</i>\nperuro."],
  ["perutilis", "<big>perutilis</big>\n\n<b>per–utilis, e</b>\nвесьма полезный <i>C</i>."],
  ["pervacuus", "<big>pervacuus</big>\n\n<b>per–vacuus, a, um</b>\nсовершенно пустой <i>Boet</i>."],
  ["pervado", "<big>pervado</big>\n\n<b>per–vado, vasi, vasum, ere</b>\n<b>1)</b> проходить, проникать (per illa loca <i>L</i>; Thessaliam <i>L</i>; cunctos artus <i>T</i>): fama pervadit urbem <i>L</i> молва распространяется по городу;\n<b>2)</b> доходить, достигать (ad castra <i>L</i>; ad senatum et populum <i>T</i>)."],
  ["pervagabilis", "<big>pervagabilis</big>\n\n<b>per–vagabilis, e</b>\nстранствующий, блуждающий (cymbulae <i>Sid</i>)."],
  ["pervagatus", "<big>pervagatus</big>\n\n<b>per–vagatus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> pervagor;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> широко распространённый, повсеместный, общеизвестный (fama, sermo <i>C</i>);\n<b>2)</b> общий (pars <i>C</i>)."],
  ["pervago", "<big>pervago</big>\n\n<b>pervago, —, —, are</b> <i>rhH</i>\n= pervagor."],
  ["pervagor", "<big>pervagor</big>\n\n<b>per–vagor, atus sum, ari</b> <i>depon.</i>\n<b>1)</b> бродить, блуждать, странствовать (omnibus in locis <i>Cs</i>); проходить, обходить (domos suas <i>L</i>; orbem terrarum <i>L</i>);\n<b>2)</b> (широко) распространяться, доходить (usque ad ultimas terras <i>C</i>);\n<b>3)</b> проникать, овладевать (cupiditates hominum mentes pervagantur <i>C</i>)."],
  ["pervagus", "<big>pervagus</big>\n\n<b>per–vagus, a, um</b>\nскитающийся, странствующий (vasto orbe <i>O</i>)."],
  ["pervaleo", "<big>pervaleo</big>\n\n<b>per–valeo, valui, —, ere</b>\nбыть очень сильным <i>Lcr</i>."],
  ["pervalidus", "<big>pervalidus</big>\n\n<b>per–validus, a, um</b>\nвесьма сильный, могучий (agmina <i>Amm</i>; contritio <i>Vlg</i>)."],
  ["pervaporo", "<big>pervaporo</big>\n\n<b>per–vaporo, —, —, are</b>\nнаполнять паром, <i>т. е.</i> нагревать (solium <i>Pt</i> — <i>v. l.</i>)."],
  ["pervarie", "<big>pervarie</big>\n\n<b>pervarie</b> [pervarius]\nвесьма разнообразно (narrare <i>C</i>)."],
  ["pervarius", "<big>pervarius</big>\n\n<b>per–varius, a, um</b>\nвесьма разнообразный, различный (utilitates <i>Ap</i>)."],
  ["pervasi", "<big>pervasi</big>\n\n<b>pervasi</b> <i>pf. к</i>\npervado."],
  ["pervasio", "<big>pervasio</big>\n\n<b>pervasio, onis</b> <i>f</i> [pervado]\nвторжение, захват (чужой собственности) <i>CTh, Eccl</i>."],
  ["pervasor", "<big>pervasor</big>\n\n<b>pervasor, oris</b> <i>m</i> [pervado]\nзахватчик <i>CTh, Eccl</i>."],
  ["pervasto", "<big>pervasto</big>\n\n<b>per–vasto, avi, atum, are</b>\nсовершенно опустошать, разорять (omnia ferro flammaque <i>L</i>)."],
  ["pervasus", "<big>pervasus</big>\n\n<b>pervasus, a, um</b> <i>part. pf. к</i>\npervado."],
  ["pervectio", "<big>pervectio</big>\n\n<b>pervectio, onis</b> <i>f</i> [perveho]\nотплытие, переезд <i>CTh</i>."],
  ["pervector", "<big>pervector</big>\n\n<b>pervector, oris</b> <i>m</i> [perveho]\nпередатчик: p. apicum <i>Symm</i> письмоносец, гонец."],
  ["perveho", "<big>perveho</big>\n\n<b>per–veho, vexi, vectum, ere</b>\n<b>1)</b> провозить, доставлять (commeatum <i>L</i>); приводить (aliquem Romam <i>Su</i>): aliquem in caelum p. <i>QC</i> превозносить кого-л. до небес;\n<b>2)</b> <i>pass.-med.</i> pervehi ездить, плыть (pervehi Oceanum <i>T</i>); прибывать, приезжать (pervehi in portum <i>C</i>; Chalcidem <i>L</i>); доходить (ad exitus optatos <i>C</i>)."],
  ["pervelim", "<big>pervelim</big>\n\n<b>pervelim</b> <i>praes. conjct. к</i>\npervolo II."],
  ["pervelle", "<big>pervelle</big>\n\n<b>pervelle</b> <i>inf. к</i>\npervolo II."],
  ["pervello", "<big>pervello</big>\n\n<b>per–vello, velli, —, ere</b>\n<b>1)</b> щипать (nates <i>Pl</i>): p. aurem <i>Ph</i> драть за ухо, <i>перен.</i> <i>Sen, VM</i> делать напоминание;\n<b>2)</b> возбуждать, раздражать (stomachum <i>H</i>);\n<b>3)</b> пробуждать, воскрешать (fidem <i>Sen</i>);\n<b>4)</b> терзать, мучить, донимать, удручать (dolor aliquem pervellit <i>C</i>);\n<b>5)</b> бранить, ругать, пробирать <i>или</i> осмеивать (jus civile <i>C</i>)."],
  ["pervenat", "<big>pervenat</big>\n\n<b>pervenat</b> <i>Pl praes. conjct. к</i>\npervenio."],
  ["pervenio", "<big>pervenio</big>\n\n<b>per–venio, veni, ventum, ire</b>\n<b>1)</b> приходить, прибывать (ad aliquem <i>C</i>; in castra <i>Cs</i>; in domicilium suum <i>C</i>): p. in urbem <i>L</i> прибывать в город; p. ad urbem <i>Sl</i> прибыть к городу (в окрестности);\n<b>2)</b> доходить (epistula ad aliquem pervenit <i>C</i>); достигать (ad hunc locum <i>Cs</i>; ad nonagesimum annum <i>C</i>): verba aures non pervenientia <i>O</i> речи, которых не слышно; ad manus pervenitur <i>C</i> дело доходит до драки; ad oculos p. alicujus <i>C</i> предстать перед чьими-л. глазами; p. in senatum <i>C</i> войти в сенат (стать сенатором); p. ad nummos <i>C</i> разбогатеть; p. in odium alicujus <i>Nep</i> стать предметом чьей-л. ненависти; ira ad rabiem pervenit <i>Su</i> гнев перешёл в бешенство; eo perventum est, ut... <i>Pt</i> (<i>безл.</i>) дело дошло до того, что...; quod si contigerit, satis vivus pervenero <i>Pt</i> если это удастся, (значит) я в жизни многого добился;\n<b>3)</b> доставаться (divitiae ad paucos perveniunt <i>Sl</i>)."],
  ["pervenor", "<big>pervenor</big>\n\n<b>per–venor, —, ari</b> <i>depon.</i>\nпромчаться, обегать (totam urbem <i>Pl</i>)."],
  ["perventio", "<big>perventio</big>\n\n<b>perventio, onis</b> <i>f</i> [pervenio]\nприбытие (p. cum salute <i>Aug</i>)."],
  ["perventor", "<big>perventor</big>\n\n<b>perventor, oris</b> <i>m</i>\nприбывающий, пришелец <i>Sid</i>; <i>перен.</i> достигший <i>или</i> постигший <i>Aug</i>."],
  ["pervenustus", "<big>pervenustus</big>\n\n<b>per–venustus, a, um</b>\nпрелестнейший (homo <i>Sid</i>)."],
  ["perverro", "<big>perverro</big>\n\n<b>perverro</b> <i>V</i>\n(<i>v. l.</i>)= praeverro."],
  ["perverse", "<big>perverse</big>\n\n<b>perverse</b> [perversus]\n<b>1)</b> превратно, дурно (uti aliqua re <i>C</i>): p. errare <i>Pl</i> жестоко заблуждаться;\n<b>2)</b> неправильно, наоборот (sella curulis p. collocata <i>Su</i>)."],
  ["perversio", "<big>perversio</big>\n\n<b>perversio, onis</b> <i>f</i> [perverto]\nпереворачивание, <i>перен. грам.</i> перестановка слов, инверсия <i>rhH, Tert</i>."],
  ["perversitas", "<big>perversitas</big>\n\n<b>perversitas, atis</b> <i>f</i> [perversus]\n<b>1)</b> превратность (opinionum <i>C</i>);\n<b>2)</b> извращённость, испорченность (hominum <i>C</i>; morum <i>Su</i>)."],
  ["perversor", "<big>perversor</big>\n\n<b>perversor, oris</b> <i>m</i>\nразвратитель <i>Eccl</i>."],
  ["perversus", "<big>perversus</big>\n\n<b>perversus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> perverto;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> косой, косящий (oculi <i>C</i>); неправильный (partus <i>Vr ap. AG</i>): togam perversam induere <i>Sen</i> надеть (судейскую) тогу наискось (<i>или</i> наизнанку) (<i>что было символом сурового приговора</i>); <i>поэтому</i>: toga perversa неумолимо, беспощадно (persequi aliquem <i>Pt</i>);\n<b>2)</b> плохой, нехороший, неудачный (dies p. atque adversus <i>Pl</i>);\n<b>3)</b> чудаковатый, эксцентричный (homo praeposterus atque p. <i>C</i>);\n<b>4)</b> дурной, злой, испорченный (mos <i>Su</i>; perversa via sequi <i>Sen</i>); превратный, извращённый, нелепый (subtilitas <i>PM</i>)."],
  ["perverto", "<big>perverto</big>\n\n<b>per–verto, verti, versum, ere</b>\n<b>1)</b> опрокидывать, переворачивать, обрушивать, валить, низвергать (arbusta, tecta <i>C</i>): perversae rupes <i>L</i> обрушившиеся скалы;\n<b>2)</b> <i>перен.</i> ниспровергать, разрушать (regnum, jura <i>C</i>); расстраивать (amicitiam <i>C</i>); портить, губить (mores <i>Nep</i>; liberos alicujus <i>T</i>): perverso numine <i>V</i> против воли богов;\n<b>3)</b> совершенно изменять (figuram <i>Pt</i>). — <i>См. тж.</i> perversus."],
  ["pervesperi", "<big>pervesperi</big>\n\n<b>per–vesperi</b> <i>adv.</i>\nочень поздно вечером (venire <i>C</i>)."],
  ["pervestigatio", "<big>pervestigatio</big>\n\n<b>pervestigatio, onis</b> <i>f</i> [pervestigo]\n<b>1)</b> отыскивание по следам, выслеживание <i>CTh</i>;\n<b>2)</b> тщательное исследование <i>C, Aug</i>."],
  ["pervestigator", "<big>pervestigator</big>\n\n<b>pervestigator, oris</b> <i>m</i>\nисследователь, изыскатель (bibliothecae <i>Hier</i>)."],
  ["pervestigo", "<big>pervestigo</big>\n\n<b>per–vestigo, avi, atum, are</b>\n<b>1)</b> отыскивать по следам, выслеживать (omnia <i>C</i>);\n<b>2)</b> тщательно исследовать (p. et cognoscere <i>C</i>); расследовать (sacrilegium pervestigatum <i>L</i>)."],
  ["pervetus", "<big>pervetus</big>\n\n<b>per–vetus, eris</b> <i>adj.</i>\nочень старый (rex, oppidum <i>C</i>; vinum <i>CC</i>)."],
  ["pervetustus", "<big>pervetustus</big>\n\n<b>per–vetustus, a, um</b>\nочень старый, архаический (vocabulum <i>Vr</i>; verbum <i>C</i>)."],
  ["perviam", "<big>perviam</big>\n\n<b>perviam</b> <i>adv.</i> [per + via]\nдоступно: p. facere aliquid <i>Pl</i> делать что-л. доступным (<i>v. l.</i>)."],
  ["pervicacia", "<big>pervicacia</big>\n\n<b>pervicacia, ae</b> <i>f</i> [pervicax]\n<b>1)</b> настойчивость, упорство, непреклонность (in hostem <i>T</i>): perdurandi p. <i>PM</i> живучесть, долговечность;\n<b>2)</b> своенравие, упрямство <i>C, L etc.</i>"],
  ["pervicaciter", "<big>pervicaciter</big>\n\n<b>pervicaciter</b> [pervicax]\nнастойчиво, упорно <i>L, T, Dig</i>."],
  ["pervicax", "<big>pervicax</big>\n\n<b>pervicax, acis</b> <i>adj.</i> [pervinco]\n<b>1)</b> настойчивый, упорный (animus <i>Ter, QC</i>); непреклонный, непоколебимый, стойкий (virtus <i>L</i>); упрямый (p. irae <i>T</i>);\n<b>2)</b> устойчивый (libra <i>PM</i>)."],
  ["pervictus", "<big>pervictus</big>\n\n<b>pervictus, a, um</b> <i>part. pf. к</i>\npervinco."],
  ["pervicus", "<big>pervicus</big>\n\n<b>pervicus, a, um</b> <i>Acc, Pl</i>\n= pervicax."],
  ["pervideo", "<big>pervideo</big>\n\n<b>per–video, vidi, visum, ere</b>\n<b>1)</b> обозревать, тщательно рассматривать (omnia <i>O</i>);\n<b>2)</b> основательно исследовать, ясно видеть, хорошо понимать, постигать (aliquid <i>Lcr, C etc.</i>): p. sua mala oculis inunctis <i>ирон. H</i> видеть свои недостатки замазанными глазами, <i>т. е.</i> быть слепым к ним; videre, sed non p. <i>Col</i> видеть, но не понимать сущности."],
  ["pervigeo", "<big>pervigeo</big>\n\n<b>per–vigeo, gui, —, ere</b>\nпродолжать процветать, неизменно пользоваться (opibus atque honoribus <i>T</i>)."],
  ["pervigil", "<big>pervigil</big>\n\n<b>per–vigil, is</b> <i>adj.</i>\nвсегда бодрствующий, неусыпный (insomnis magis quam p. <i>T</i>; p. gubernator <i>Pt</i>): nox p. <i>Just</i> ночь, проведённая без сна."],
  ["pervigilatio", "<big>pervigilatio</big>\n\n<b>pervigilatio, onis</b> <i>f</i> [pervigil] <i>культ.</i>\nночное богослужение <i>C</i>."],
  ["pervigilia", "<big>pervigilia</big>\n\n<b>pervigilia, ae</b> <i>f</i> [pervigil]\nночное бдение, бессонная ночь <i>Just</i>."],
  ["pervigilis", "<big>pervigilis</big>\n\n<b>pervigilis, e</b> <i>Ap</i>\n= pervigil."],
  ["pervigilium", "<big>pervigilium</big>\n\n<b>pervigilium, i</b> <i>n</i> [pervigil]\n<b>1)</b> ночное бдение, бессонная ночь (pervigilio fatigatus <i>Just</i>);\n<b>2)</b> <i>культ.</i> ночное богослужение (p. indicere <i>Su</i>; p. celebrare <i>T</i>; p. Priapi genio debetur <i>Pt</i>)."],
  ["pervigilo", "<big>pervigilo</big>\n\n<b>per–vigilo, avi, atum, are</b>\nпроводить без сна, не спать (in armis <i>L</i>; totam noctem <i>Pl, C</i>; longos dies <i>Tib</i>)."],
  ["pervilis", "<big>pervilis</big>\n\n<b>per–vilis, e</b>\nисключительно дешёвый (annona <i>L</i>)."],
  ["pervinca", "<big>pervinca</big>\n\n<b>pervinca</b> — <i>см.</i>\nvincapervinca."],
  ["pervinco", "<big>pervinco</big>\n\n<b>per–vinco, vici, victum, ere</b>\n<b>1)</b> окончательно победить, разгромить, одолеть (p. et subigere nationes <i>T</i>): utraque p. <i>T</i> одержать победу в обоих состязаниях;\n<b>2)</b> <i>перен.</i> одержать победу, восторжествовать, поставить на своём (<i>Cato</i> pervicit <i>C</i>); добиться, достигнуть (consules p. non potuerunt <i>L</i>): non pervicerunt, quin objecta denegaret <i>T</i> (палачи) не смогли добиться, чтобы (Эпихарис) перестала отрицать обвинения;\n<b>3)</b> склонить, побудить: (Hegesilochus) Rhodios pervicerat, ut Romanam societatem retinerent <i>L</i> (Гегесилох) убедил родосцев сохранить союз с Римом;\n<b>4)</b> заглушать, подавлять (sonum <i>H</i>); преодолевать, превозмогать (pavorem <i>Sen</i>);\n<b>5)</b> неопровержимо доказывать (aliquid dictis <i>Lcr</i>)."],
  ["perviridis", "<big>perviridis</big>\n\n<b>per–viridis, e</b>\nярко-зелёный <i>PM, Mela</i>."],
  ["perviso", "<big>perviso</big>\n\n<b>per–viso, —, —, ere</b>\nобозревать, созерцать (caelum <i>Man</i>)."],
  ["pervium", "<big>pervium</big>\n\n<b>pervium, i</b> <i>n</i> [pervius]\nпроход <i>T</i>."],
  ["pervius", "<big>pervius</big>\n\n<b>pervius, a, um</b> [per II + via]\n<b>1)</b> (удобо)проходимый (saltus <i>L</i>): transitiones perviae <i>C</i> открытые проходы || проходной (aedes <i>Ter</i>); доступный (loca pervia equo <i>O</i>; p. ambitioni <i>T</i>); переходимый вброд (amnis <i>T</i>); проницаемый (hastis <i>Lcn</i>): vulneribus p. <i>SenT</i> уязвимый;\n<b>2)</b> просверлённый, проколотый (anulus <i>AG</i>): cor mihi nunc pervium est <i>Pl</i> теперь мне легче стало на сердце;\n<b>3)</b> проникающий внутрь, <i>т. е.</i> острый (ensis <i>Sil</i>)."],
  ["pervivo", "<big>pervivo</big>\n\n<b>per–vivo, vixi, —, ere</b>\nпрожить (usque ad summam aetatem <i>Pl</i>)."],
  ["pervolaticus", "<big>pervolaticus</big>\n\n<b>per–volaticus, a, um</b>\nлетучий, чрезвычайно подвижный (vis vaga et pervolatica <i>Tert</i>)."],
  ["pervolgo", "<big>pervolgo</big>\n\n<b>pervolgo</b> <i>арх.</i>\n= pervulgo 1."],
  ["pervolitantia", "<big>pervolitantia</big>\n\n<b>pervolitantia, ae</b> <i>f</i> [pervolito]\nкруговой полёт, вращательное движение (mundi circa terram <i>Vtr</i>)."],
  ["pervolito", "<big>pervolito</big>\n\n<b>pervolito, avi, —, are</b> [<i>intens. к</i> pervolo 1]\n<b>1)</b> пролетать, проноситься (voces pervolitant per dissaepta domorum <i>Lcr</i>); облетать (lumen pervolitat omnia loca <i>V</i>);\n<b>2)</b> снова и снова пробегать (iter <i>Vtr</i>)."],
  ["pervolo", "<big>pervolo</big>\n\n<b>I per–volo, avi, atum, are</b>\n<b>1)</b> пролетать (iter aerium <i>O</i>); проноситься (rumor agitatis pervolat alis <i>O</i>);\n<b>2)</b> поспешно проезжать <i>или</i> проходить (per viam <i>Ap</i>); промчаться (sex milia passuum <i>C</i>);\n<b>3)</b> лететь, спешить (in hanc sedem <i>C</i>; in hospitium alicujus <i>Pt</i>).\n\n<b>II per–volo, volui, —, velle</b>\nсильно желать <i>L</i>: pervelim scire <i>C</i> я весьма желал бы знать."],
  ["pervoluto", "<big>pervoluto</big>\n\n<b>pervoluto, —, —, are</b> [<i>intens. к</i> pervolvo]\nчасто развёртывать, <i>т. е.</i> постоянно читать, перечитывать, тщательно изучать (libros alicujus <i>C</i>; scriptores legendi et pervolutandi <i>C</i>)."],
  ["pervolvo", "<big>pervolvo</big>\n\n<b>per–volvo, volvi, volutum, ere</b>\n<b>1.</b> <i>act.</i>\n<b>1)</b> катать, валять (aliquem in luto <i>Ter</i>);\n<b>2)</b> развёртывать, <i>т. е.</i> читать (<i>sc.</i> librum <i>Ctl</i>);\n<b>2.</b> <i>pass.</i> pervolvi:\n<b>1)</b> валять я (in suo sanguine <i>Ap</i>);\n<b>2)</b> прилежно заниматься (animus pervolvitur in aliqua re <i>C</i>)."],
  ["pervor-", "<big>pervor-</big>\n\n<b>pervor-</b> <i>арх.</i>\n= perver-."],
  ["pervulgate", "<big>pervulgate</big>\n\n<b>pervulgate</b> [pervulgatus]\nобщеупотребительно, в обычном смысле (loqui <i>AG</i>)."],
  ["pervulgatus", "<big>pervulgatus</big>\n\n<b>pervulgatus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> pervulgo;\n<b>2.</b> <i>adj.</i> самый обыкновенный, общепринятый, общеизвестный <i>rhH, C, AG, Ap</i>."],
  ["pervulgo", "<big>pervulgo</big>\n\n<b>per–vulgo, avi, atum, are</b>\n<b>1)</b> делать общим, общедоступным (praemia virtutis <i>C</i>): se p. отдаваться (omnibus <i>C</i>);\n<b>2)</b> издавать, публиковать (tabulas <i>C</i>); разглашать, доводить до всеобщего сведения (res pervulgata <i>C</i>); обнародовать (edictum <i>Cs</i>);\n<b>3)</b> часто посещать, пролетать (volucres pervulgant nemora <i>Lcr</i>); проноситься (solis lumina pervulgant caelum <i>Lcr</i>)."],
  ["pes", "<big>pes</big>\n\n<b>pes, pedis</b> <i>m</i>\n<b>1)</b> нога, ступня (hominis <i>PM</i>); копыто, нога (equi <i>PM</i>): duos pedes habens <i>Aug</i> двуногий; pedes bifidi <i>O</i> раздвоенные копыта; pedes adunci <i>O</i> лапы с кривыми когтями; pedem ferre <i>V</i> идти, (при)ходить; pedem inferre <i>C</i> входить, вступать; pedem porta (limine) efferre <i>C</i> выходить из дому; pedibus (pede) <i>Cs, C etc.</i> вброд, пешком <i>или</i> сухим путём (ire, iter facere <i>C etc.</i>); pedem trahere <i>O</i> ковылять, хромать; excipere se in pedes <i>L</i> соскочить; in pedes se conjicere <i>Pl, Ter</i> броситься бежать; ad pedes stare <i>Pt</i> стоять в ногах (<i>у чьего-л. ложа</i>); servus a pedibus <i>C</i> (verna ad pedes <i>M</i>) пеший гонец, рассыльный; ante pedes (positum) esse <i>C</i> находиться перед самым носом, быть в наличии <i>или</i> быть очевидным; esse sub pedibus alicujus <i>L</i> находиться под чьей-л. властью; esse (jacere) sub pedibus <i>O</i> быть презираемым; sub pede ponere <i>H</i> презирать; manibus pedibusque <i>погов. Ter</i> руками и ногами, <i>т. е.</i> всеми средствами; per me ista trahantur pedibus <i>погов. C</i> = по мне, пропади оно пропадом; pede secundo <i>V</i> (felici <i>O</i>) милостиво, благосклонно <i>или</i> в добрый час; pedibus stipendia facere <i>или</i> pedibus merere <i>L</i> служить в пехоте; pedibus proeliari <i>Cs</i> сражаться в пешем строю; pede aequo congredi <i>V</i> стремительно атаковать; ad pedes descendere <i>L</i> (desilire <i>Cs</i>, degredi <i>L</i>) слезать с лошади, спешиваться; deducere equites ad pedes <i>L</i> спешивать конницу; pugna ad pedes it (<i>или</i> venit) <i>L</i> завязывается бой в пешем строю; pedem conferre <i>C etc.</i> вступить в рукопашный бой; pedem referre <i>L, O</i> (revocare, retrahere <i>V</i>) отступить; (<i>в сенате</i>) pedibus ire in sententiam alicujus <i>Sl, L</i> голосовать путём перехода на ту или другую сторону, <i>т. е.</i> присоединиться к чьему-л. мнению; pedem alicui opponere <i>погов. O</i> противодействовать <i>или</i> <i>Pt</i> подставить кому-л. ножку; omni pede stare <i>погов. Q</i> твёрдо стоять на ногах, быть во всеоружии; imi pedes montis <i>Amm</i> подошва горы;\n<b>2)</b> ножка (mensae <i>O</i>);\n<b>3)</b> стебель, черешок (oleae <i>PM</i>);\n<b>4)</b> ручка (палка) носилок <i>M</i>;\n<b>5)</b> площадь, территория, местность (p. Zeugitanus <i>Sol</i>): planus p. <i>Vtr</i> ровное место;\n<b>6)</b> ход, движение (crepante pede <i>H</i>; cito pede labitur aetas <i>O</i>);\n<b>7)</b> (<i>тж.</i> p. veli <i>C</i>) шкот, канат, которым парус поворачивают по ветру: pede aequo <i>O</i> (pedibus aequis <i>C</i>) на всех парусах; pedem facere <i>V</i> управлять шкотом, <i>т. е.</i> лавировать; navales pedes <i>шутл. Pl</i> = remiges;\n<b>8)</b> римский фут = 29,57 см = 1/6 passus (murus viginti pedes altus <i>Cs</i>): non pedem discedere <i>C</i> не отступать ни на пядь;\n<b>9)</b> мера: pede suo se metiri <i>H</i> сообразоваться со своими силами;\n<b>10)</b> стихотворная стопа (p. Lesbius <i>H</i>): verba pedibus claudere <i>H</i> (in suos pedes cogere <i>O</i>) писать стихи."],
  ["Pescennius", "<big>Pescennius</big>\n\n<b>Pescennius, i</b> <i>m</i>\n(Niger) Песценний, <i>римск. император в 193-4 гг. н. э.</i> <i>Spart</i>."],
  ["pessarium", "<big>pessarium</big>\n\n<b>pessarium, i</b> <i>n</i> <i>CA</i>\n= pessum II."],
  ["pessime", "<big>pessime</big>\n\n<b>pessime</b> <i>superl. к</i>\nmale."],
  ["pessimo", "<big>pessimo</big>\n\n<b>pessimo, —, —, are</b> [pessimus]\nпричинять вред, мучить (plebem, se ipsum <i>Vlg</i>); <i>pass.</i> pessimari терпеть ущерб (ex aliqua re <i>Vlg</i>)."],
  ["pessimus", "<big>pessimus</big>\n\n<b>pessimus</b> <i>superl. к</i>\nmalus."],
  ["Pessinuntica", "<big>Pessinuntica</big>\n\n<b>Pes(s)inuntica, ae</b> <i>f</i> (<i>sc.</i> dea) <i>Ap</i>\n= Cybele."],
  ["Pessinunticus", "<big>Pessinunticus</big>\n\n<b>Pes(s)inunticus, a, um</b> <i>Ap</i>\n= Pes(s)inuntius."],
  ["Pessinuntius", "<big>Pessinuntius</big>\n\n<b>Pes(s)inuntius, a, um</b> [Pes(s)inus]\nпессинунтский (sacerdos magnae Matris <i>C</i>)."],
  ["Pessinus", "<big>Pessinus</big>\n\n<b>Pes(s)inus, untis</b> <i>m</i>, <i>чаще</i> <i>f</i>\nПессинунт, <i>город в Галатии, на южном склоне горы Dindymus; центр культа Кибелы</i> <i>C, L</i>."],
  ["pessulum", "<big>pessulum</big>\n\n<b>pessulum, i</b> <i>n</i> <i>CA</i>\n= pessarium."],
  ["pessulus", "<big>pessulus</big>\n\n<b>pessulus, i</b> <i>m</i>\nзадвижка, засов: pessulum obdere ostio <i>или</i> foribus <i>Ter</i> (<i>тж.</i> pessulos injicere <i>Ap</i>) запереть Дверь на засов."],
  ["pessum", "<big>pessum</big>\n\n<b>I pessum</b> <i>adv.</i> [pes + versum]\nназемь, вниз: p. ire <i>Col</i> опускаться (оседать) на дно, <i>перен.</i> <i>Pl, T</i> стать несчастным, погибнуть; p. subsidere per mare <i>Lcr</i> погрузиться на дно моря; p. aetas acta est <i>Enn</i> годы прошли; p. dare <i>Lcn</i> (dejicere <i>Ap</i>, premere <i>Pl</i>, agere <i>Enn</i>) уничтожать (hostium copias <i>VM</i>), разрушать (civitates <i>Sl</i>), губить (aliquem <i>Pt</i>); ad inertiam p. dari <i>Sl</i> опуститься, утратить мужество.\n\n<b>II pessum, i</b> <i>n</i> (<i>греч.</i>) <i>мед.</i>\nматочное кольцо, пессарий <i>Ap</i>."],
  ["pessundo", "<big>pessundo</big>\n\n<b>pessundo (pessumdo)</b>\n= pessum do (<i>см.</i> pessum 1)."],
  ["pessus", "<big>pessus</big>\n\n<b>pessus, i</b> <i>m</i> <i>v. l.</i>\n= pessum II."],
  ["pestibilis", "<big>pestibilis</big>\n\n<b>pestibilis, e</b>\nзачумлённый, вредный для здоровья (fundus <i>CJ</i>)."],
  ["pestifer", "<big>pestifer</big>\n\n<b>pestifer (pestiferus), fera, ferum</b> [pestis + fero]\nгибельный, пагубный, тлетворный (odor <i>L</i>; aer <i>Lcn</i>); опасный, вредный (civis <i>C</i>); смертоносный (vipera <i>C</i>; fames <i>O</i>)."],
  ["pestifere", "<big>pestifere</big>\n\n<b>pestifere</b> [pestifer]\nгибельно, пагубно <i>C</i>."],
  ["pestilens", "<big>pestilens</big>\n\n<b>pestilens, entis</b> [pestis]\n<b>1)</b> заражённый, чумной (aer <i>Vtr</i>; locus <i>C</i>; aestas <i>L</i>); нездоровый (locus, annus, aspiratio <i>C</i>);\n<b>2)</b> гибельный, пагубный (sidus <i>Pt</i>; munus <i>L</i>); вредный, опасный (homo <i>C</i>)."],
  ["pestilentia", "<big>pestilentia</big>\n\n<b>pestilentia, ae</b> <i>f</i> [pestilens]\n<b>1)</b> чума, зараза, повальная болезнь (p. ingens orta est <i>L</i>; p. pecudum <i>V</i>);\n<b>2)</b> нездоровый климат, вредность (autumni, loci <i>Cs</i>);\n<b>3)</b> нездоровая местность (pestilentiae possessor <i>C</i>);\n<b>4)</b> отрава (oratio plena pestilentiae <i>Ctl</i>)."],
  ["pestilentiosus", "<big>pestilentiosus</big>\n\n<b>pestilentiosus, a, um</b> <i>Dig, Aug</i>\n= pestifer."],
  ["pestilentus", "<big>pestilentus</big>\n\n<b>pestilentus, a, um</b> <i>AG</i>\n= pestilens."],
  ["pestilitas", "<big>pestilitas</big>\n\n<b>pestilitas, atis</b> <i>f</i> <i>Lcr</i>\n= pestilentia."],
  ["pestis", "<big>pestis</big>\n\n<b>pestis, is</b> <i>f</i>\n<b>1)</b> зараза, чума, повальная болезнь (morbi ac pestes <i>Col</i>);\n<b>2)</b> дурной запах (nasorum <i>Ctl</i>);\n<b>3)</b> гибель, разрушение (pestem in aliquem machinari <i>C</i>); несчастье, катастрофа (pestem ab aliquo avertere <i>C</i>; servatae a peste carinae <i>V</i>); бич, язва (patriae <i>C</i>; omnes pestes exigere <i>Ap</i>): p. textilis <i>Poeta ap. C</i> отравленная одежда (<i>кентавра <i>H</i> ecca</i>)."],
  ["petalum", "<big>petalum</big>\n\n<b>petalum, i</b> <i>n</i> (<i>греч.</i>)\nметаллический лист <i>Is</i>."],
  ["petasatus", "<big>petasatus</big>\n\n<b>petasatus, a, um</b> [petasus]\nв дорожной шляпе, <i>перен.</i> готовый к отъезду <i>Vr, C, Su</i>."],
  ["petasio", "<big>petasio</big>\n\n<b>petasio, onis</b> <i>m</i>\nпередняя лопатка, окорок <i>Vr</i>."],
  ["petaso", "<big>petaso</big>\n\n<b>petaso, onis</b> <i>m</i> <i>M</i>\n= petasio."],
  ["petasunculus", "<big>petasunculus</big>\n\n<b>I petasunculus, i</b> <i>m</i> [<i>demin. к</i> petasio]\nмаленький окорок <i>J</i>.\n\n<b>II petasunculus, i</b> <i>m</i> [<i>demin. к</i> petasus]\nмаленькая дорожная шляпа <i>Eccl</i>."],
  ["petasus", "<big>petasus</big>\n\n<b>petasus, i</b> <i>m</i> (<i>греч.</i>)\n<b>1)</b> дорожная шляпа с широкими полями <i>Pl, Eccl</i>;\n<b>2)</b> род купола, перекрытия <i>или</i> навеса <i>Vr ap. PM</i>."],
  ["petauristarius", "<big>petauristarius</big>\n\n<b>petauristarius, i</b> <i>m</i> <i>Pt</i>\n= petauristes."],
  ["petauristes", "<big>petauristes</big>\n\n<b>petauristes, ae</b> <i>m</i> (<i>греч.</i>)\nканатный плясун, эквилибрист <i>Vr</i>."],
  ["petaurum", "<big>petaurum</big>\n\n<b>petaurum, i</b> <i>n</i> (<i>греч.</i>)\nпетавр, <i>снаряд для гимнастических трюков</i> <i>LM, M, J, Man, Pt</i>."],
  ["Petelia", "<big>Petelia</big>\n\n<b>Petelia, ae</b> <i>f</i>\nПетелия:\n<b>1)</b> <i>древний город на вост. побережье Бруттия, к сев. от Кротона</i> (<i>ныне</i> Strongoli) <i>V, L, Sil</i>;\n<b>2)</b> <i>город на зап. побережье Лукании, между Paestum <i>и</i> Velia</i> <i>VM</i>."],
  ["Petelinus", "<big>Petelinus</big>\n\n<b>I Petelinus, a, um</b> [Petelia]\nпетелийский: P. lucus <i>L</i> <i>название рощи близ Рима.</i>\n\n<b>II Petelinus, i</b> <i>m</i>\nжитель города Petelia <i>VM</i>."],
  ["Peteon", "<big>Peteon</big>\n\n<b>Peteon, onis</b> <i>f</i>\nПетеон, <i>город в Беотии</i> <i>PM</i>."],
  ["petesso", "<big>petesso</big>\n\n<b>petesso, —, —, ere</b> [<i>intens. к</i> peto]\nстремиться, домогаться, рваться (p. pugnam caedesque <i>Lcr</i>; p. laudem <i>C</i>)."],
  ["petigo", "<big>petigo</big>\n\n<b>petigo, inis</b> <i>f</i>\nпарша <i>LM</i> (<i>v. l.</i>)."],
  ["Petillius", "<big>Petillius</big>\n\n<b>Petil(l)ius, a, um</b>\nПетилий, <i>римск. nomen</i>: Q. P. Cerealis, <i>римск. полководец и наместник в Британии и в области батавов</i> (<i>умер в 72 г. н. э.</i>) <i>L</i>."],
  ["petilus", "<big>petilus</big>\n\n<b>petilus, a, um</b>\n<b>1)</b> слабый, хилый (crura <i>LM</i>);\n<b>2)</b> тонкий (labra <i>Pl</i>);\n<b>3)</b> скудный, ничтожный (donum <i>Pl</i>)."],
  ["petimen", "<big>petimen</big>\n\n<b>petimen, inis</b> <i>n</i>\nнарыв на плече у рабочего скота <i>LM</i>."],
  ["Petina", "<big>Petina</big>\n\n<b>Petina, ae</b> <i>f</i>\n(Aelia) Петина (Элия), <i>жена императора Клавдия, с которой он развёлся, чтобы жениться на Мессалине</i> <i>Su</i>."],
  ["petiolus", "<big>petiolus</big>\n\n<b>petiolus, i</b> <i>m</i> [<i>demin. к</i> pes]\n<b>1)</b> ножка (haedi <i>CC</i>);\n<b>2)</b> черешок (mali <i>Col</i>)."],
  ["petisti", "<big>petisti</big>\n\n<b>petisti</b> <i>V</i>\n= petivisti (<i>к</i> peto)."],
  ["petitio", "<big>petitio</big>\n\n<b>petitio, onis</b> <i>f</i> [peto]\n<b>1)</b> старание достать <i>или</i> добиться, домогательство, просьба, хлопоты, стремление (indutiarum <i>L</i>; consulatus <i>Cs</i>; honorum <i>T</i>): dare se petitioni <i>C</i>, <i>тж.</i> ad petitionem descendere <i>Q</i> добиваться должности; dare alicui petitionem alicujus rei <i>Su, VM</i> позволить кому-л. выставить свою кандидатуру на какой-л. пост; p. nostra <i>C</i> наша (= моя) кандидатура;\n<b>2)</b> <i>юр.</i> требование, притязание, предъявление претензии, гражданский иск (p. hereditatis <i>Dig</i>; p. pecuniae <i>Q</i>);\n<b>3)</b> нападение, нанесение удара, удар (alicujus petitiones effugere <i>C</i>): conjicere petitiones <i>C</i> наносить удары; p. apta <i>C</i> меткий словесный выпад;\n<b>4)</b> право на иск, законность претензии (petitionem habere <i>Dig</i>)."],
  ["petitor", "<big>petitor</big>\n\n<b>petitor, oris</b> <i>m</i> [peto]\n<b>1)</b> проситель, искатель: p. aquae <i>Frontin</i> предъявляющий спрос на воду; p. famae <i>Lcn</i> искатель славы, честолюбец;\n<b>2)</b> жалобщик, истец <i>C, Dig</i>;\n<b>3)</b> соискатель служебного поста, кандидат <i>H, Su</i>;\n<b>4)</b> претендент на руку девушки <i>Sen, Ap etc.</i>"],
  ["petitorium", "<big>petitorium</big>\n\n<b>petitorium, i</b> <i>n</i>\nписьменное (исковое) заявление, прошение (по имущественным вопросам) <i>CTh</i>."],
  ["petitorius", "<big>petitorius</big>\n\n<b>petitorius, a, um</b>\nкасающийся имущественного иска, Исковой (formula <i>G</i>)."],
  ["petitrix", "<big>petitrix</big>\n\n<b>petitrix, icis</b> <i>f</i> [petitor]\n<b>1)</b> соискательница <i>Q</i>;\n<b>2)</b> истица <i>Dig</i>."],
  ["petitum", "<big>petitum</big>\n\n<b>petitum, i</b> <i>n</i> [peto]\nтребование, просьба <i>Ctl</i>."],
  ["petiturio", "<big>petiturio</big>\n\n<b>petiturio, —, —, ire</b> [<i>desiderat. к</i> peto]\nсобираться хлопотать, просить, домогаться (<i>преим.</i> должности) <i>C</i>."],
  ["petitus", "<big>petitus</big>\n\n<b>petitus, us</b> <i>m</i> [peto]\n<b>1)</b> желание, требование (consensu petituque omnium <i>AG</i>);\n<b>2)</b> наклон: terrae p. suavis <i>Lcr</i> лёгкое пошатывание (<i>при головокружении</i>)."],
  ["peto", "<big>peto</big>\n\n<b>peto, ivi (ii), itum, ere</b> [<i>одного корня с</i> praepes, impetus]\n<b>1)</b> стараться, стремиться, добиваться, искать, домогаться (victoriam ex aliquo <i>L</i>; consulatum <i>C</i>; virginem <i>L</i>): certa amittimus, dum incerta petimus <i>погов. Pl</i> гонясь за сомнительным, мы упускаем верное; salutem fuga p. <i>Cs</i> искать спасения в бегстве; decus ferro petendum <i>Lcn</i> слава, которую приходится добывать мечом (с бою); certum voto pete finem <i>H</i> ставь себе (лишь) ясные (достижимые) цели || собирать, добывать (cochleas <i>Sl</i>);\n<b>2)</b> просить, требовать (aliquid ab aliquo <i>C, Sl etc.</i>; multa petentibus desunt multa <i>H</i>); p. poenas ab aliquo <i>C</i> взыскать с (наказать) кого-л.; quantum res petet <i>C</i> сколько потребуют обстоятельства; p. aliquem ad supplicium <i>Q</i> требовать чьей-л. казни;\n<b>3)</b> <i>юр.</i> иметь <i>или</i> предъявлять претензию (p. hereditatis possessionem <i>C</i>): is qui petit <i>C etc.</i> истец; is unde (<i>или</i> a quo) petitur <i>C etc.</i> ответчик;\n<b>4)</b> доставать, получать, брать (lignum, pabulum, commeatum <i>Cs</i>); черпать (doloris oblivionem a litteris <i>C</i>): p. aliquid a Graecis <i>C</i> заимствовать что-л. у греков; p. initium <i>C</i> начинать, приступать; prooemium alte petitum <i>C</i> издалека начатое вступление; p. fugam <i>Cs</i> обратиться в бегство; p. iter (viam) <i>L</i> избрать путь; <i>поэт.</i>: p. spiritum <i>H</i> дышать; suspirium alte p. <i>Pl</i> <i>или</i> p. spiritus (gemitus) alto de corde <i>O</i> тяжело вздыхать (стонать);\n<b>5)</b> обращаться (aliquem precibus <i>C</i>): te supplex peto <i>V</i> обращаюсь к тебе с мольбой;\n<b>6)</b> бросаться, устремляться, нападать: p. aliquem aliqua re <i>L, H, O etc.</i> стараться ударить кого-л., запустить в кого-л. чём-л. (p. caput alicujus <i>C, O</i>; p. aliquem armis <i>VP</i>): bos cornibus petit <i>Dig</i> бык бодается; p. aliquem bello <i>V</i> идти войной на кого-л. (начать с кем-л. войну);\n<b>7)</b> угрожать (aliquem ferro <i>H</i>): serpentium morsus non petit serpentes <i>погов. PJ</i> змеи змей не кусают; lege aliquis petitur <i>C</i> закон направлен против кого-л.; se peti putabant <i>L</i> они думали, что враждебные действия направлены против них; p. aliquem fraude <i>V, L</i> стараться обмануть кого-л.;\n<b>8)</b> направляться, отправляться (castra <i>Nep</i>; Dyrrhachium <i>C</i>; Lacedaemona classe <i>O</i>): p. aliquem <i>Lcn</i> следовать за кем-л.;\n<b>9)</b> лететь, взлетать (caelum pennis <i>O</i>); изливаться, хлынуть (campum petit amnis <i>V</i>); возвышаться, вздыматься, устремляться (mons petit astra <i>O</i>; flamma petit altum <i>O</i> <i>или</i> supera <i>C</i>);\n<b>10)</b> подходить, приближаться (aliquem <i>Pl, C etc.</i>): aliquem p. dextra <i>V</i> протягивать кому-л. правую руку; aliquem amplexu p. <i>O</i> обнимать кого-л."],
  ["petorritum", "<big>petorritum</big>\n\n<b>petor(r)itum, i</b> <i>n</i> (<i>кельт.</i>)\nчетырёхколёсная повозка <i>PM, H, AG, Aus</i>."],
  ["Petosiris", "<big>Petosiris</big>\n\n<b>Petosiris, idis</b> <i>m</i>\nПетосирид, <i>египетский математик и астролог</i> <i>PM, J</i>."],
  ["petra", "<big>petra</big>\n\n<b>I petra, ae</b> <i>f</i> (<i>греч.</i>; <i>лат.</i> saxum)\nскала, камень <i>QC, AG, PM</i>.\n\n<b>II Petra, ae</b> <i>f</i>\nПетра:\n<b>1)</b> <i>холм на побережье Иллирии, к югу от Диррахия</i> <i>Cs</i>;\n<b>2)</b> <i>город в области медов</i> (<i>Фракия</i>) <i>L</i>;\n<b>3)</b> <i>главн. город идумеев, к югу от Мёртвого моря</i> <i>PM</i>;\n<b>4)</b> P. (Petrusa), <i>город в Умбрии</i> <i>AV</i>;\n<b>5)</b> <i>город в Сицилии, между городами Himera и Henna</i> <i>C, PM</i>."],
  ["Petraea", "<big>Petraea</big>\n\n<b>Petraea, ae</b> <i>f</i>\n(Arabia) «Каменистая» Аравия (<i>в действительности названа так по главн. её городу Petra</i>), <i>простиравшаяся на Синайский п-ов и непосредственно прилегавшие к нему с севера и северо-востока части Палестины</i> <i>PM</i>."],
  ["Petraeus", "<big>Petraeus</big>\n\n<b>I Petraeus, a, um</b> <i>PM adj. к</i>\nPetra 3.\n\n<b>II petraeus, a, um</b> [petra]\nскалистый, <i>перен.</i> растущий на скалах (brassica <i>PM</i>)."],
  ["Petrejanus", "<big>Petrejanus</big>\n\n<b>Petrejanus, a, um</b> [Petrejus]\nпетреев (auxilium <i>bAfr</i>)."],
  ["Petrejus", "<big>Petrejus</big>\n\n<b>Petrejus, a, um</b>\nПетрей, <i>римск. nomen; наиболее известен</i> M. P., <i>который, в качестве легата проконсула Т. Антония, в 62 г. до н. э. победил Катилину при Пистории; с 54 г. был легатом Помпея в Испании; потерпев поражение от Цезаря при Тапсе, покончил с собой</i> (<i>46 г. до н. э.</i>) <i>C, Sl</i>."],
  ["petrensis", "<big>petrensis</big>\n\n<b>petrensis, e</b>\nживущий среди скал <i>или</i> камней (pisces <i>CA</i>)."],
  ["Petreus", "<big>Petreus</big>\n\n<b>Petreus, a, um</b> [petra]\nкаменный <i>или</i> [Petrus] (апостола) Петра (pedes <i>Aug</i>)."],
  ["Petrinus", "<big>Petrinus</big>\n\n<b>I Petrinus, a, um</b> <i>C, PM adj. к</i>\nPetra 5.\n\n<b>II Petrinus, i</b> <i>m</i>\nжитель города Petra (<i>см.</i> Petra 5) <i>C, PM</i>.\n\n<b>III petrinus, a, um</b> [petra]\nкаменный (cultellus <i>Lact</i>): petrina acies <i>Tert</i> острый камень."],
  ["Petrites", "<big>Petrites</big>\n\n<b>Petrites, ae</b> <i>m</i>\n<i>сорт вина из города Petra</i> (<i>см.</i> Petra 3) <i>PM</i>."],
  ["petro", "<big>petro</big>\n\n<b>petro, onis</b> <i>m</i> [petra]\nстарый баран (<i>чьё мясо жёстко как камень</i>) <i>Pl</i>."],
  ["Petrocorii", "<big>Petrocorii</big>\n\n<b>Petrocorii, orum</b> <i>m</i>\nпетрокории, <i>кельт. племя в Аквитании, на левом берегу Гарумны, с главн. городом Vesunna</i> <i>Cs, PM</i>."],
  ["Petronia", "<big>Petronia</big>\n\n<b>Petronia, ae</b> <i>f</i>\nПетрония, <i>первая жена императора Вителлин</i> <i>T</i>."],
  ["Petronius", "<big>Petronius</big>\n\n<b>Petronius, a, um</b>\nПетроний, <i>римск. nomen</i>: T. (<i>или</i> C.) P. Arbiter, <i>приближённый Нерона, умер в 66 г. н. э.; автор бытового романа</i> «Satiricon»."],
  ["petrosa", "<big>petrosa</big>\n\n<b>petrosa, orum</b> <i>n</i> (<i>sc.</i> loca)\nскалистые, каменистые места <i>PM, Vlg</i>."],
  ["petroselinon", "<big>petroselinon</big>\n\n<b>petroselinon, i</b> <i>n</i> (<i>греч.</i>) <i>бот.</i>\nпетрушка <i>PM</i>."],
  ["petrosus", "<big>petrosus</big>\n\n<b>petrosus, a, um</b> [petra]\nкаменистый, скалистый (loca <i>PM</i>)."],
  ["petrotos", "<big>petrotos</big>\n\n<b>petrotos, on</b> <i>adj.</i> (<i>греч.</i>)\nокаменелый <i>PM</i>."],
  ["Petrus", "<big>Petrus</big>\n\n<b>Petrus, i</b> <i>m</i>\nПётр, <i>первый из апостолов</i> (sprinceps apostolorum) <i>Vlg, Eccl</i>."],
  ["petulans", "<big>petulans</big>\n\n<b>petulans, antis</b> <i>adj.</i> [peto]\n<b>1)</b> резвый, шаловливый (animalia <i>AG</i>); развязный (genus dicendi <i>C</i>): adjectis petulantibus dictis <i>Pt</i> сопровождая (удары) насмешками;\n<b>2)</b> дерзкий (lingua <i>Sen</i>)."],
  ["petulanter", "<big>petulanter</big>\n\n<b>petulanter</b> [petulans]\n<b>1)</b> резво, шаловливо (fieri <i>C</i>); развязно (alicui rei sine ulla suspicione maledicere <i>C</i>); распущенно (vivere <i>C</i>);\n<b>2)</b> дерзко, нагло (invehi in aliquem <i>C</i>)."],
  ["petulantia", "<big>petulantia</big>\n\n<b>petulantia, ae</b> <i>f</i> [petulans]\n<b>1)</b> резвость, шаловливость (mariti gregum perniciosi propter petulantiam <i>Col</i>);\n<b>2)</b> своеволие, разнузданность (furor et p. <i>C</i>); дерзость, наглость: p. linguae <i>Prp, Su</i> (dictorum <i>AG</i>) невоздержность на язык, брань <i>или</i> рискованное (смелое) высказывание;\n<b>3)</b> буйное разрастание (ramorum <i>PM</i>);\n<b>4)</b> острота, сила (morbi <i>AG</i>)."],
  ["petulcus", "<big>petulcus</big>\n\n<b>petulcus, a, um</b> [peto]\nбодливый (agnus <i>Lcr</i>; haedus <i>V</i>)."],
  ["petulus", "<big>petulus</big>\n\n<b>petulus, a, um</b> <i>v. l.</i>\n= petilus."],
  ["peuce", "<big>peuce</big>\n\n<b>I peuce, es</b> <i>f</i> (<i>греч.</i>) <i>PM</i>\n= larix.\n\n<b>II Peuce, es</b> <i>f</i>\nПевка:\n<b>1)</b> <i>островок в устье Дуная Meta</i>, <i>PM, Lcn, Cld</i>;\n<b>2)</b> <i>жена Истра</i> (<i>бога реки Hister</i>) <i>St, VF</i>."],
  ["peucedanum", "<big>peucedanum</big>\n\n<b>peucedanum (-on), i</b> <i>n</i> (<i>греч.</i>) <i>бот.</i>\nгоричник (<i>Peucedanum officinale</i>, L.) <i>PM, CC, Lcn</i>."],
  ["Peucetia", "<big>Peucetia</big>\n\n<b>Peucetia, ae</b> <i>f</i>\nПевкетия, <i>средняя часть вост. побережья Апулии</i> (<i>от Бария до Брундизия</i>) <i>PM</i>."],
  ["Peucetius", "<big>Peucetius</big>\n\n<b>Peucetius, a, um</b> [Peucetia]\nпевкетийский, <i>поэт.</i> апулийский <i>или</i> тарентинский (sinus <i>O</i>)."],
  ["pexatus", "<big>pexatus</big>\n\n<b>pexatus, a, um</b> [pexus]\nодетый в ворсистое, <i>т. е.</i> новое платье <i>Sen, M</i>."],
  ["pexi", "<big>pexi</big>\n\n<b>pexi</b> <i>pf. к</i>\npecto."],
  ["pexitas", "<big>pexitas</big>\n\n<b>pexitas, atis</b> <i>f</i>\nмохнатость, ворсистость (telae <i>PM</i>)."],
  ["pexus", "<big>pexus</big>\n\n<b>pexus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> pecto;\n<b>2.</b> <i>adj.</i> пушистый, ворсистый, <i>т. е.</i> новый (tunica <i>H</i>); покрытый пушком (folium <i>Col</i>)."],
  ["Phacelina", "<big>Phacelina</big>\n\n<b>Phacelina, ae</b> <i>f</i> (<i>греч.</i> «принесённая в вязанке хвороста»)\n<i>эпитет Дианы Сицилийской</i> (<i>статуя которой была, по преданию, доставлена Орестом из Скифии именно таким образом</i>) <i>Sil</i>."],
  ["Phacus", "<big>Phacus</big>\n\n<b>Phacus, i</b> <i>m</i>\nФак, <i>укрепление в Македонии близ Пеллы</i> <i>L</i>."],
  ["Phaeaces", "<big>Phaeaces</big>\n\n<b>Phaeāces, um</b> <i>m</i>\nфеаки, <i>миф. племя мореплавателей на о-ве Схерия (позднее предпол. Коркира), у царя которых, Алкиноя, Одиссей встретил радушный приём</i> <i>C, V, O etc.</i>"],
  ["Phaeacia", "<big>Phaeacia</big>\n\n<b>Phaeācia, ae</b> <i>f</i>\nФеакия, страна феаков, <i>т. е.</i> Коркира <i>Tib</i>."],
  ["Phaeacis", "<big>Phaeacis</big>\n\n<b>Phaeācis, idis</b> <i>f</i>\n<b>1)</b> феакиянка;\n<b>2)</b> «Феакида», <i>поэма Тутикана о пребывании Одиссея у феаков</i> <i>O</i>."],
  ["Phaeacius", "<big>Phaeacius</big>\n\n<b>Phaeācius, a, um</b>\nфеакийский (tellus <i>Tib, O</i>)."],
  ["Phaeacus", "<big>Phaeacus</big>\n\n<b>Phaeācus, a, um</b> <i>Prp</i>\n= Phaeacius."],
  ["Phaeax", "<big>Phaeax</big>\n\n<b>Phaeāx, ācis</b> <i>m sg. к</i>\nPhaeaces <i>J</i>; <i>перен.</i> лакомка, кутила <i>H</i>."],
  ["phaecasia", "<big>phaecasia</big>\n\n<b>phaecasia, ae</b> <i>f</i> <i>Pt</i>\n= phaecasium."],
  ["phaecasiatus", "<big>phaecasiatus</big>\n\n<b>phaecasiātus, a, um</b> [phaecasium]\nобутый в белую обувь (фекасий) (milites <i>Pt</i>): ph. palliatusque <i>Sen</i> в фекасиях и в греческом плаще, <i>т. е.</i> греческий философ."],
  ["phaecasium", "<big>phaecasium</big>\n\n<b>phaecasium, ī</b> <i>n</i> (<i>греч.</i>)\nфекасий, <i>род белой обуви</i> (<i>преим. афинских жрецов</i>) <i>Sen</i>."],
  ["Phaedimus", "<big>Phaedimus</big>\n\n<b>Phaedimus, ī</b> <i>m</i>\nФедим, <i>один из сыновей Амфиона и Ниобы</i> <i>O</i>."],
  ["Phaedon", "<big>Phaedon</big>\n\n<b>Phaedō(n), ōnis</b> <i>m</i>\nФедон, <i>ученик Сократа и друг Платона, именем которого назван диалог Платона о бессмертии души</i> <i>C, AG</i>."],
  ["Phaedra", "<big>Phaedra</big>\n\n<b>Phaedra, ae</b> <i>f</i>\nФедра, <i>дочь Миноса и Пасифаи, жена Тезея, мачеха Ипполита</i> <i>V, O, SenT</i>."],
  ["Phaedrus", "<big>Phaedrus</big>\n\n<b>Phaedrus, ī</b> <i>m</i>\nФедр:\n<b>1)</b> <i>ученик Сократа, именем которого назван один из диалогов Платона о любви</i> <i>C</i>;\n<b>2)</b> <i>философ-эпикуреец в Афинах, учитель Цицерона</i> <i>C</i>;\n<b>3)</b> <i>римск. баснописец, родом фракиец, вольноотпущенник Августа.</i>"],
  ["phaenomenon", "<big>phaenomenon</big>\n\n<b>phaenomenon, ī</b> <i>n</i> (<i>греч.</i>)\nявление, <i>преим.</i> небесное <i>Lact</i>."],
  ["Phaenon", "<big>Phaenon</big>\n\n<b>Phaenōn, ōnis</b> <i>m</i> (<i>греч.</i> «сияющий»)\n<i>название планеты Сатурна</i> <i>Ap, Aus</i>."],
  ["Phaestias", "<big>Phaestias</big>\n\n<b>Phaestias, adis</b> <i>f</i>\nжительница города Phaestum <i>O</i>."],
  ["Phaestius", "<big>Phaestius</big>\n\n<b>Phaestius, a, um</b> [Phaestum]\nфестийский (tellus <i>O</i>)."],
  ["Phaestum", "<big>Phaestum</big>\n\n<b>Phaestum, ī</b> <i>n</i>\n<b>1)</b> <i>город на южн. побережье Крита</i> <i>PM</i>;\n<b>2)</b> <i>город в области Thessaliotis</i> (<i>Фессалия</i>) <i>L</i>."],
  ["Phaethon", "<big>Phaethon</big>\n\n<b>Phaëthō(n), ontis</b> <i>m</i> (<i>греч.</i> «сияющий»)\nФаэтонт:\n<b>1)</b> <i>эпитет бога солнца</i> <i>V, Sil</i>;\n<b>2)</b> <i>сын бога солнца и Климены, взявшийся управлять солнечной колесницей отца, но чуть не сжёгший землю и убитый молнией Юпитера</i> <i>C, O</i>."],
  ["Phaethonteus", "<big>Phaethonteus</big>\n\n<b>Phaëthontēus, a, um</b> [Phaethon]\nфаэтонтов (ignes <i>O</i>): Ph. Padus <i>M</i> Фаэтонтова река Пад (<i>так как в неё упал пораженный Юпитером Фаэтонт</i>); Phaethontea umbra <i>M</i> тень Фаэтонтова тополя (<i>так как скорбевшие о брате сестры Фаэтонта были превращены в тополя</i>)."],
  ["Phaethontiades", "<big>Phaethontiades</big>\n\n<b>Phaëthontiades, um</b> <i>f</i>\nФаэтонтиады, <i>т. е.</i> сестры Фаэтонта (= Heliades) <i>O, V, SenT, Sid</i>."],
  ["Phaethontis", "<big>Phaethontis</big>\n\n<b>Phaëthontis, idis</b> <i>f</i> [Phaethon]\nФаэтонтова: Ph. gutta <i>M</i> слеза Фаэтонтиад (<i>сестёр Фаэтонта</i>), <i>т. е.</i> янтарь."],
  ["Phaethontius", "<big>Phaethontius</big>\n\n<b>Phaëthontius, a, um</b>\n<b>1)</b> <i>adj. к</i> Phaethon 2: Ph. amnis <i>Sil</i> = Padus;\n<b>2)</b> <i>adj. к</i> Phaethon I: Phaethontia ora <i>Sil</i> Сияющий лик (солнца)."],
  ["Phaethusa", "<big>Phaethusa</big>\n\n<b>Phaëthūsa, ae</b> <i>f</i>\nФаэтуса, <i>сестра Фаэтонта, одна из Гелиад</i> <i>O</i>."],
  ["phagedaena", "<big>phagedaena</big>\n\n<b>phagedaena, ae</b> <i>f</i> (<i>греч.</i>)\n<b>1)</b> болезненно-неутолимый голод <i>PM, CA</i>;\n<b>2)</b> разъедающая язва <i>PM</i>."],
  ["phagedaenicus", "<big>phagedaenicus</big>\n\n<b>phagedaenicus, a, um</b> [phagedaena]\n<b>1)</b> разъедающий, разрушающий ткани (vulnus <i>PM</i>);\n<b>2)</b> страдающий неутолимым голодом <i>CA</i>."],
  ["phager", "<big>phager</big>\n\n<b>phager, gri</b> <i>m</i> (<i>греч.</i>)\nфагр (<i>род рыбы</i>) <i>PM, O</i>."],
  ["phago", "<big>phago</big>\n\n<b>phago, onis</b> <i>m</i> (<i>греч.</i>)\nобжора <i>Vr, Vop</i>."],
  ["Phalacrina", "<big>Phalacrina</big>\n\n<b>Phalacrina (Falacrina), ae</b> <i>f</i>\nФалакрина, <i>город в сабинской области, место рождения императора Веспасиана</i> <i>Su</i>."],
  ["phalacrocorax", "<big>phalacrocorax</big>\n\n<b>phalacro–corax, acis</b> <i>m</i> (<i>греч.</i>; <i>лат.</i> corvus aquaticus)\nбаклан <i>PM</i>."],
  ["Phalaeceus", "<big>Phalaeceus</big>\n\n<b>Phalaeceus, a, um</b> [Phalaecus 2]\nфалеков (nex <i>O</i>)."],
  ["phalaecium", "<big>phalaecium</big>\n\n<b>phalaecium (-on), phalaecum</b> <i>и</i> <b>phaleucium, ī</b> <i>n</i> <i>стих.</i>\nфалеков размер, <i>т. е.</i> дактилическо-трохаический одиннадцатисложник: – – –UU–U–U–U <i>Aus, Sulp</i>."],
  ["Phalaecus", "<big>Phalaecus</big>\n\n<b>Phalaecus, i</b> <i>m</i>\nФалек:\n<b>1)</b> <i>греч. поэт. создатель размера</i> phalaecium (еж.) <i>Aus</i>;\n<b>2)</b> <i>тиран фокейцев</i> <i>O</i>."],
  ["phalanga", "<big>phalanga</big>\n\n<b>phalanga, ae</b> <i>f</i> (<i>греч.</i>)\n<b>1)</b> шест для переноски тяжестей <i>Vtr, PM</i>;\n<b>2)</b> каток для передвижения тяжестей <i>Cs</i>."],
  ["phalangarius", "<big>phalangarius</big>\n\n<b>phalangarius, i</b> <i>m</i>\n<b>1)</b> носильщик, пользующийся phalanga (<i>см.</i>) <i>Vtr</i>;\n<b>2)</b> солдат фаланги (<i>см.</i> phalanx) <i>Lampr</i>."],
  ["phalangitae", "<big>phalangitae</big>\n\n<b>phalangitae, arum</b> <i>m</i> [phalanx]\nсолдаты фаланги <i>L</i>."],
  ["phalangites", "<big>phalangites</big>\n\n<b>phalangites, ae</b> <i>m</i> <i>или</i> <b>phalangitis, idis</b> <i>f</i> (<i>греч.</i>) <i>бот.</i>\nфалангит, паучья трава (<i>Anthericum</i>, L.) <i>PM</i>."],
  ["phalangium", "<big>phalangium</big>\n\n<b>phalangium (-ion)</b> <i>n</i> <i>зоол.</i>\nфаланга <i>или</i> тарантул <i>CC, PM</i>."],
  ["Phalanna", "<big>Phalanna</big>\n\n<b>Phalanna, ae</b> <i>f</i>\nФаланна, <i>город в Фессалии</i> <i>L</i>."],
  ["Phalannaeus", "<big>Phalannaeus</big>\n\n<b>Phalannaeus, a, um</b> <i>L adj. к</i>\nPhalanna."],
  ["Phalantheus", "<big>Phalantheus</big>\n\n<b>Phalant(h)eus</b> <i>Sil</i> <i>и</i> <b>Phalant(h)inus, a, um</b> <i>M adj. к</i>\nPhalanthus <i>и</i> Phalanthum."],
  ["Phalanthum", "<big>Phalanthum</big>\n\n<b>Phalanthum, i</b> <i>n</i>\nФалант, <i>поэт. название города Тарента</i> <i>M</i>."],
  ["Phalanthus", "<big>Phalanthus</big>\n\n<b>Phalant(h)us, i</b> <i>m</i>\nФалант, <i>спартанец, предводитель парфениев</i> (<i>см.</i> Partheniae), <i>переселившихся в южн. Италию и основавших там город Тарент</i> <i>H, Just</i>."],
  ["phalanx", "<big>phalanx</big>\n\n<b>phalanx, langis</b> <i>f</i>\n<b>1)</b> фаланга, <i>отряд войска в сомкнутом строю</i> (<i>у македонян — 50 человек по фронту и 12-16 в глубину</i> <i>Cs, V, Nep, QC</i>);\n<b>2)</b> полчище, множество (culparum <i>Eccl</i>)."],
  ["Phalara", "<big>Phalara</big>\n\n<b>Phalara, orum</b> <i>n</i>\nФалары, <i>портовый город в Фессалии, в сев. части Малийского залива</i> <i>L, PM</i>."],
  ["phalarica", "<big>phalarica</big>\n\n<b>phalarica, ae</b> <i>f</i> <i>v. l.</i>\n= falarica."],
  ["Phalaris", "<big>Phalaris</big>\n\n<b>I Phalaris, idis</b> (<i>acc.</i> im, <i>abl.</i> i <i>и</i> ide) <i>m</i>\nФаларид, <i>тиран Агригента (Сицилия), для которого Перилл сделал новое орудие казни — «медного быка»</i> (<i>см.</i> Perillus) <i>C, O, Cld</i>.\n\n<b>II phalaris, idis</b> <i>f</i> <i>PM</i>\n= phaleris."],
  ["Phalasarna", "<big>Phalasarna</big>\n\n<b>Phalasarna, ae</b> <i>и</i> <b>Phalasarne, es</b> <i>f</i>\nФаласарна, <i>город на зап. побережье Крита</i> <i>PM</i>."],
  ["Phalasarneus", "<big>Phalasarneus</big>\n\n<b>Phalasarneus, a, um</b> <i>L adj. к</i>\nPhalasarne."],
  ["phalerae", "<big>phalerae</big>\n\n<b>phalerae, arum</b> <i>f</i> (<i>греч.</i>)\nфалеры:\n<b>1)</b> <i>металлические украшения, служившие воинскими знаками отличия</i> (equites donati phaleris <i>L</i>);\n<b>2)</b> <i>металлические бляхи на груди и на лбу у лошадей и слонов</i> (equus phaleris insignis <i>V</i>);\n<b>3)</b> <i>женские украшения, носившиеся на груди</i> (matrona ornata phaleris <i>PS</i>);\n<b>4)</b> <i>перен.</i> цветы красноречия, риторические прикрасы (loquendi ph. <i>Symm</i>); ad populum phaleras! <i>Pers</i> брось пустые слова!"],
  ["phaleratus", "<big>phaleratus</big>\n\n<b>phaleratus, a, um</b> [phalerae]\n<b>1)</b> с металлическими украшениями (equi <i>L</i>; cursores <i>M, Pt</i>);\n<b>2)</b> красиво звучащий (dicta <i>Ter</i>)."],
  ["Phalereus", "<big>Phalereus</big>\n\n<b>Phalēre͡us</b> (<i>и</i> <b>-ēus</b>), <b>eī</b> <i>m</i>\nжитель Фалерона (<i>прозвище Деметрия; см.</i> Demetrius) <i>C, Nep etc.</i>"],
  ["Phalericus", "<big>Phalericus</big>\n\n<b>I Phalericus, a, um</b> [Phalerum]\nфалерский (portus <i>Nep</i>).\n\n<b>II Phalericus, i</b> <i>m</i> (<i>sc.</i> portus) <i>C</i>\n= Phalerum 1."],
  ["phaleris", "<big>phaleris</big>\n\n<b>phaleris, idis</b> <i>f</i> (<i>греч.</i>)\n<b>1)</b> <i>зоол.</i> лысуха (<i>Fulica</i>, L.) <i>Vr, Col, PM</i>;\n<b>2)</b> <i>бот.</i> канареечник (<i>Phalaris Canariensis</i>, L.) <i>PM</i>."],
  ["phalero", "<big>phalero</big>\n\n<b>phalero, —, —, are</b> [phalerae]\nразукрашивать (eloquentiam <i>Ambr</i>). — <i>См. тж.</i> phaleratus."],
  ["Phalerum", "<big>Phalerum</big>\n\n<b>Phalerum, i</b> <i>n</i> <i>и</i> <b>Phalera, orum</b> <i>n</i>\nФалер(ы):\n<b>1)</b> <i>древнейшая гавань Афин</i> <i>PM</i>;\n<b>2)</b> <i>аттический дем, прилегающий к этой гавани</i> <i>PM</i>."],
  ["phaleucium", "<big>phaleucium</big>\n\n<b>phaleucium</b> <i>v. l.</i>\n= phalaecium."],
  ["phallovitrobulum", "<big>phallovitrobulum</big>\n\n<b>phallo–vitrobulum, i</b> <i>n</i> [vitrum I]\nчаша или бокал в форме фалла <i>Capit</i> (<i>v. l.</i>)."],
  ["phallus", "<big>phallus</big>\n\n<b>phallus, i</b> <i>m</i> (<i>греч.</i>)\nфалл, membrum virile (<i>как культовый символ плодородия</i>) <i>Eccl</i>."],
  ["Phanae", "<big>Phanae</big>\n\n<b>Phanae, arum</b> <i>f</i>\nФаны, <i>гавань и мыс на южн. побережье о-ва Хиос</i> <i>L</i>."],
  ["Phanaeus", "<big>Phanaeus</big>\n\n<b>Phanaeus, a, um</b> [Phanae]\nфанейский: rex Ph. <i>V</i> царь-вино (превосходное хиосское вино)."],
  ["Phanote", "<big>Phanote</big>\n\n<b>Phanote, es (Phanope, es)</b> <i>f</i>\nФанота, <i>город в Эпире</i> <i>L</i>."],
  ["phantasia", "<big>phantasia</big>\n\n<b>phantasia, ae</b> <i>f</i> (<i>греч.</i>)\n<b>1)</b> представление, идея, мысль <i>Sen</i>;\n<b>2)</b> <i>ирон.</i> иллюзия, видимость: ph., non homo <i>Pt</i> не человек, а сказка."],
  ["phantasma", "<big>phantasma</big>\n\n<b>phantasma, atis</b> <i>n</i> (<i>греч.</i>)\n<b>1)</b> мысленный образ, представление <i>Vlg, Aug</i>;\n<b>2)</b> призрак, видение <i>PJ, Tert</i>."],
  ["phantasmaticus", "<big>phantasmaticus</big>\n\n<b>phantasmaticus</b> <i>Is</i>\n= phantasticus."],
  ["phantasticus", "<big>phantasticus</big>\n\n<b>phantasticus, a, um</b>\nвоображаемый, призрачный <i>Eccl</i>."],
  ["Phantasus", "<big>Phantasus</big>\n\n<b>Phantasus (-os), i</b> <i>m</i> (<i>греч.</i>)\nФантас, <i>бог сновидений, сын бога сна</i> (<i>Somnus</i>) <i>O</i>."],
  ["Phaon", "<big>Phaon</big>\n\n<b>Phaon, onis</b> <i>m</i>\nФаон:\n<b>1)</b> <i>лесбосский юноша, которого без взаимности любила поэтесса Сапфо</i> <i>O, PM</i>;\n<b>2)</b> (<i>gen.</i> ontis) <i>вольноотпущенник Нерона</i> <i>Su</i>."],
  ["pharetra", "<big>pharetra</big>\n\n<b>pharetra, ae</b> <i>f</i> (<i>греч.</i>)\n<b>1)</b> колчан <i>V, H etc.</i>;\n<b>2)</b> солнечные часы (<i>в форме колчана</i>) <i>Vtr</i>."],
  ["pharetratus", "<big>pharetratus</big>\n\n<b>pharetratus, a, um</b> [pharetra]\nносящий колчан, с колчаном (Persis <i>V</i>): puer ph. <i>O</i> = Amor; virgo pharetrata <i>O</i> = Diana <i>или</i> <i>J</i> = Semiramis."],
  ["pharetriger", "<big>pharetriger</big>\n\n<b>pharetri–ger, gera, gerum</b> <i>Sil</i>\n= pharetratus."],
  ["Phariacus", "<big>Phariacus</big>\n\n<b>Phariacus, a, um</b> <i>Ap</i>\n= Pharius."],
  ["Pharis", "<big>Pharis</big>\n\n<b>Pharis, is</b> <i>f</i> <i>St</i>\n= Pherae 1."],
  ["Pharitae", "<big>Pharitae</big>\n\n<b>Pharitae, arum</b> <i>m</i>\nжители острова Pharus <i>bAl</i>."],
  ["Pharius", "<big>Pharius</big>\n\n<b>Pharius, a, um</b> [<i>adj. к</i> Pharus]\n1) фаросский: Phariae flammae <i>Lcn</i> Фаросский маяк;\n<b>2)</b> <i>поэт.</i> египетский: Pharia juvenca <i>O</i> = Ио <i>или</i> <i>M</i> = Исида; Pharia conjunx <i>M</i> = Клеопатра; Ph. piscis <i>O</i> = крокодил; Pharia turba <i>Tib</i> = сонм жрецов Исиды; Pharii dolores <i>St</i> египетские вопли (<i>участниц печальных празднеств в честь Исиды, оплакивающей смерть Осириса</i>)."],
  ["pharmaceuticus", "<big>pharmaceuticus</big>\n\n<b>pharmaceuticus, a, um</b> (<i>греч.</i>)\nлекарственный <i>CA</i>."],
  ["pharmaceutria", "<big>pharmaceutria</big>\n\n<b>pharmaceutria, ae</b> <i>f</i> (<i>греч.</i>)\nколдунья <i>V</i>."],
  ["pharmacopola", "<big>pharmacopola</big>\n\n<b>pharmacopola, ae</b> <i>m</i> (<i>греч.</i>)\n<b>1)</b> торгующий лекарствами, волшебными снадобьями <i>Cato ap. AG, C</i>;\n<b>2)</b> шарлатан <i>H</i>."],
  ["pharmacus", "<big>pharmacus</big>\n\n<b>pharmacus, i</b> <i>m</i> (<i>греч.</i>)\nприготовляющий яды, отравитель <i>Pt</i>."],
  ["Pharmacussa", "<big>Pharmacussa</big>\n\n<b>Pharmacu(s)sa, ae</b> <i>f</i>\nФармакусса, <i>островок близ Крита, у которого Цезарь был захвачен пиратами</i> <i>PM, Su</i>."],
  ["Pharnabazus", "<big>Pharnabazus</big>\n\n<b>Pharnabazus, i</b> <i>m</i>\nФарнабаз, <i>сын Фарнака, сатрап при Дарий Ноте и Артаксерксе, сторонник спартанцев во время Пелопоннесской войны</i> <i>Nep, Just</i>."],
  ["Pharnaces", "<big>Pharnaces</big>\n\n<b>Pharnaces, is</b> <i>m</i>\nФарнак:\n<b>1)</b> <i>первый царь Понта</i> (<i>184-156 гг. до н. э.</i>) <i>PM</i>;\n<b>2)</b> <i>сын Митридата, занимавший престол Боспорского царства</i> (<i>63-47 гг. до н. э.), но побеждённый при Зеле (Zela) Цезарем и низложенный им</i> <i>C</i>;\n<b>3)</b> <i>один из рабов Цицерона</i> <i>C</i>."],
  ["pharnuprium", "<big>pharnuprium</big>\n\n<b>pharnuprium, i</b> <i>n</i>\nфиговое вино <i>PM</i>."],
  ["Pharos", "<big>Pharos</big>\n\n<b>Pharos, i</b> <i>f</i> <i>v. l.</i>\n= Pharus."],
  ["Pharsalia", "<big>Pharsalia</big>\n\n<b>Pharsalia, ae</b> <i>f</i>\n<b>1)</b> <i>область Фарсала</i> <i>Ctl, T, O, Lcn</i>;\n<b>2)</b> <i>название поэмы Лукана.</i>"],
  ["Pharsalicus", "<big>Pharsalicus</big>\n\n<b>Pharsalicus</b> <i>и</i> <b>Pharsalius, a, um</b> [Pharsalus]\nфарсальский (terra <i>L</i>; castra <i>Lcn</i>)."],
  ["Pharsalus", "<big>Pharsalus</big>\n\n<b>Pharsalus (-os), i</b> <i>f</i>\nФарсал, <i>город в центре Фессалии, на реке Энипей, место победы Цезаря над Помпеем в 48 г. до н. э.</i> <i>L, Lcn</i>."],
  ["Pharus", "<big>Pharus</big>\n\n<b>Pharus (-os), i</b> <i>f</i>, <i>редко</i> (<i>Su</i>) <i>m</i>\n<b>1)</b> Фарос, <i>островок близ Александрии Египетской с городом того же названия и с гигантским маяком, сооружённым при Птолемее Филадельфе</i> <i>Mela, Cs, bAl etc.</i>;\n<b>2)</b> <i>поэт. Lcn, St</i> = Египет;\n<b>3)</b> маяк (<i>в других странах</i>) <i>VF, Su, St</i>."],
  ["phaselaria", "<big>phaselaria</big>\n\n<b>phaselaria, ium</b> <i>n</i> [phaselus]\nблюдо маринованной фасоли <i>Lampr</i>."],
  ["Phaselinus", "<big>Phaselinus</big>\n\n<b>Phaselinus, a, um</b> [Phaselis]\nфаселидский <i>PM</i>."],
  ["Phaselis", "<big>Phaselis</big>\n\n<b>Phaselis, idis</b> <i>f</i>\nФаселида:\n<b>1)</b> <i>дорический город в Ликии</i> <i>C, L</i>;\n<b>2)</b> <i>город в Иудее</i> <i>PM, Lcn</i>."],
  ["Phaselitae", "<big>Phaselitae</big>\n\n<b>Phaselitae, arum</b> <i>и</i> <b>um</b> <i>m</i>\nжители города Phaselis I <i>C</i>."],
  ["phaselus", "<big>phaselus</big>\n\n<b>phaselus, i</b> <i>m, f</i> (<i>греч.</i>)\n<b>1)</b> длинный турецкий боб, фасоль <i>V, Col</i>;\n<b>2)</b> лёгкое быстроходное судно <i>C, Ctl, V etc.</i>"],
  ["phaseolus", "<big>phaseolus</big>\n\n<b>phaseolus, i</b> <i>m</i> <i>Col etc.</i>\n= phaselus 1."],
  ["phasganion", "<big>phasganion</big>\n\n<b>phasganion, i</b> <i>n</i> (<i>греч.</i>) <i>PM</i>\n= gladiolus 3."],
  ["Phasiacus", "<big>Phasiacus</big>\n\n<b>Phasiacus</b> <i>и</i> <b>Phasianus, a, um</b> [Phasis]\n<b>1)</b> фасидский (Colchi <i>Pt</i>): Phasiana avis <i>PM</i> фазан;\n<b>2)</b> <i>поэт.</i> колхидский: Phasiaca conjux <i>SenT</i> = Medea."],
  ["phasiana", "<big>phasiana</big>\n\n<b>phāsiāna, ae</b> <i>f</i> (<i>sc.</i> avis) <i>PM</i>\n= phasianus."],
  ["phasianus", "<big>phasianus</big>\n\n<b>I phāsiānus, ī</b> <i>m</i> (<i>sc.</i> gallus)\nфазан <i>Su</i>.\n\n<b>II Phāsiānus</b> <i>v. l.</i>\n= Phasiacus."],
  ["Phasias", "<big>Phasias</big>\n\n<b>I Phasias, adis</b> <i>f</i> [Phasis]\nфасидская, <i>поэт.</i> колхидская: Ph. puella <i>O</i> = Medea.\n\n<b>II Phasias, adis</b> <i>f</i>\nколхидянка, <i>т. е.</i> Медея <i>O</i>."],
  ["Phasis", "<big>Phasis</big>\n\n<b>I Phasis, is, idis</b> <i>и</i> <b>idos</b> (<i>acc.</i> im <i>и</i> in; <i>abl.</i> i <i>и</i> ide) <i>m</i>\nФасида:\n<b>1)</b> <i>река в Колхиде, на границе с Малой Азией, впадающая в Чёрное море</i> (<i>ныне</i> Риони) <i>V, O, Prp etc.</i>: Phasidis ales <i>St</i> фазан;\n<b>2)</b> <i>милетская колония в устье реки Фасида</i> <i>Mela, PM</i>.\n\n<b>II Phasis, idis</b> <i>f</i> <i>O, M</i>\n= Phasias I <i>и</i> II."],
  ["phasma", "<big>phasma</big>\n\n<b>phasma, atis</b> <i>n</i> (<i>греч.</i>)\nвидение, привидение, призрак <i>Ter, J</i>."],
  ["Phegeius", "<big>Phegeius</big>\n\n<b>Phegeius, a, um</b> [Phegeus]\nфегеев (ensis <i>O</i>)."],
  ["Phegeus", "<big>Phegeus</big>\n\n<b>Phēge͡us, eī</b> <i>m</i>\nФегей, <i>царь Псофиды (Аркадия), отец Алфесибеи, тесть Алкмеона</i> <i>O</i>."],
  ["Phegia", "<big>Phegia</big>\n\n<b>Phegia, ae</b> <i>f</i>\nФегия, <i>город в сев.-зап. Аркадии</i> (<i>впоследствии</i> Psophis) <i>O</i>."],
  ["Phegiacus", "<big>Phegiacus</big>\n\n<b>Phegiacus, a, um</b> [Phegia]\nфегийский <i>O</i>."],
  ["Phegis", "<big>Phegis</big>\n\n<b>Phegis, idis</b> <i>f</i>\nдочь Фегея, <i>т. е.</i> Алфесибея <i>O</i>."],
  ["pheleta", "<big>pheleta</big>\n\n<b>pheleta, ae</b> <i>m</i> (<i>егип.</i>)\nвор <i>Sen</i>."],
  ["phellos", "<big>phellos</big>\n\n<b>phellos, i</b> <i>m</i> (<i>греч.</i>)\nпробковый дуб, пробка, <i>перен.</i> пробковый вращающийся диск <i>или</i> барабан в водяных часах <i>Vtr</i>."],
  ["Phemius", "<big>Phemius</big>\n\n<b>Phemius, i</b> <i>m</i>\nФемий, <i>знаменитый кифаред на острове Итака</i> <i>O</i>."],
  ["Phemonoe", "<big>Phemonoe</big>\n\n<b>Phemonoe, es</b> <i>f</i>\n<b>1)</b> Фемоноя, <i>дочь Аполлона, первая прорицательница в Дельфах</i> <i>PM</i>;\n<b>2)</b> <i>поэт.</i> пророчица <i>Lcn</i>."],
  ["Pheneatae", "<big>Pheneatae</big>\n\n<b>Pheneatae, arum</b> <i>m</i>\nжители города Pheneus <i>C</i>."],
  ["Pheneus", "<big>Pheneus</big>\n\n<b>Pheneus (-os), i</b> <i>f</i>, <i>реже</i> <b>Pheneum, i</b> <i>n</i>\nФеней, <i>город в сев.-вост. Аркадии с ядовитым озером в окрестностях</i> <i>L, V, O</i>."],
  ["phengites", "<big>phengites</big>\n\n<b>phengites, ae</b> <i>m</i> (<i>греч.</i>)\nфенгит (<i>род слюды</i>) <i>PM, Su</i>."],
  ["Pherae", "<big>Pherae</big>\n\n<b>Pherae, arum</b> <i>f</i>\nФеры:\n<b>1)</b> <i>приморский город в Мессении, в Мессенском заливе</i> <i>C, Nep</i>;\n<b>2)</b> <i>город в вост. Фессалии, по преданию, резиденция Адмета</i> <i>C, L</i>."],
  ["Pheraeus", "<big>Pheraeus</big>\n\n<b>I Pheraeus, a, um</b> [Pherae]\nферейский (Alexander <i>C, Nep</i>; gens <i>O</i>); <i>поэт.</i> фессалийский (vaccae, <i>sc.</i> Admeti <i>O</i>).\n\n<b>II Pheraeus, i</b> <i>m</i>\nжитель города Pherae <i>C</i>."],
  ["Pherecleus", "<big>Pherecleus</big>\n\n<b>Pherecleus, a, um</b> [Phereclus]\nфереклеев (puppis <i>O</i>)."],
  ["Phereclus", "<big>Phereclus</big>\n\n<b>Phereclus, i</b> <i>m</i>\nФерекл, <i>строитель корабля Париса, на котором последний увёз Елену из Спарты</i> <i>O</i>."],
  ["Pherecrates", "<big>Pherecrates</big>\n\n<b>Pherecrates, is</b> <i>m</i>\nФерекрат, <i>автор аттических комедий, предшественник Аристофана</i> <i>Sid</i>."],
  ["Pherecratius", "<big>Pherecratius</big>\n\n<b>Pherecratīus, a, um</b> [Pherecrates]\nферекратов (metrum <i>Sid</i>): Ph. versus ферекратов стих (– – –UU–U)."],
  ["Pherecydes", "<big>Pherecydes</big>\n\n<b>Pherecydes, is</b> <i>m</i>\nФерекид:\n<b>1)</b> <i>философ, родом с о-ва Сирое, учитель Пифагора</i> (<i>серед. VI в. до н. э.</i>) <i>C, PM, Ap</i>;\n<b>2)</b> <i>греч. историк, афинянин</i> (<i>серед. <i>V</i> в. до н. э.</i>) <i>C</i>."],
  ["Pherecydeus", "<big>Pherecydeus</big>\n\n<b>Pherecydeus, a, um</b> [Pherecydes]\nферекидов (illud, <i>sc.</i> dictum <i>C</i>)."],
  ["Pheres", "<big>Pheres</big>\n\n<b>Pheres, etis</b> <i>m</i>\nФерет, <i>царь города Фер (Фессалия), отец Адмета</i> <i>O</i>."],
  ["Pheretiades", "<big>Pheretiades</big>\n\n<b>Pheretiades, ae</b> <i>m</i>\nсын Ферета, <i>т. е.</i> Адмет <i>O</i>."],
  ["pheugydros", "<big>pheugydros</big>\n\n<b>pheugydros, on</b> (<i>греч.</i>)\nстрадающий водобоязнью <i>CA</i>."],
  ["phiala", "<big>phiala</big>\n\n<b>phiala, ae</b> <i>f</i> (<i>греч.</i>)\nфиал, плоская чаша <i>Pt, PM, M, J, Vlg</i>."],
  ["Phidiacus", "<big>Phidiacus</big>\n\n<b>Phidiacus, a, um</b> [Phidias]\nфидиев (Juppiter <i>Prp</i>; manus <i>O</i>)."],
  ["Phidias", "<big>Phidias</big>\n\n<b>Phidias, ae</b> <i>m</i>\nФидий, <i>афинский ваятель эпохи Перикла</i> (<i>умер в 431 г. до н. э.</i>) <i>C, VM, PM etc.</i>"],
  ["Phidippides", "<big>Phidippides</big>\n\n<b>Phidippides, is</b> <i>m</i>\nФидиппид, <i>афинский гонец-скороход</i> <i>PM</i>."],
  ["phiditia", "<big>phiditia</big>\n\n<b>phiditia, orum</b> <i>n</i> <i>v. l.</i>\n= philitia."],
  ["Philadelpheni", "<big>Philadelpheni</big>\n\n<b>Philadelpheni, orum</b> <i>m</i>\nжители города Philadelphia <i>T, PM</i>."],
  ["Philadelphia", "<big>Philadelphia</big>\n\n<b>Philadelphia, ae</b> <i>f</i>\nФиладельфия, <i>город в вост. Лидии, на берегу Тмола</i> <i>Vlg</i>."],
  ["Philadelphus", "<big>Philadelphus</big>\n\n<b>Philadelphus, i</b> <i>m</i> (<i>греч.</i> «братолюбивый»)\nФиладельф, <i>прозвище Птолемея II Египетского</i> <i>PM</i>."],
  ["Philae", "<big>Philae</big>\n\n<b>Philae, arum</b> <i>f</i>\nФилы, <i>островок на реке Нил, к югу от Элефантины</i> (<i>ныне</i> Джезирет-эль-Бирбэ) <i>PM, Sen, Lcn</i>."],
  ["Philaeni", "<big>Philaeni</big>\n\n<b>Philaeni, orum</b> <i>m</i>\nФилены, <i>два брата-карфагенянина, добровольно принесшие свою жизнь в жертву государству</i> <i>Sl</i>: Philaenon (<i>греч. формам</i> Philaenorum) arae <i>Sl, PM</i> филеновы жертвенники (<i>порт на границе между областями Карфагена и Кирены</i>)."],
  ["Philammon", "<big>Philammon</big>\n\n<b>Philammon, onis</b> <i>m</i>\nФиламмон, <i>сын Аполлона и Хионы, фракийский певец</i> <i>O</i>."],
  ["philanthropion", "<big>philanthropion</big>\n\n<b>philanthropion (-ium), i</b> <i>n</i> (<i>греч.</i>)\nдружеский дар <i>Dig</i>."],
  ["philanthropos", "<big>philanthropos</big>\n\n<b>philanthropos, i</b> <i>f</i> (<i>греч.</i>) <i>бот.</i>\nподмаренник цепкий (<i>Galium spurium</i>, L.) <i>PM</i>."],
  ["philargyria", "<big>philargyria</big>\n\n<b>philargyria, ae</b> <i>f</i> (<i>греч.</i>)\nсребролюбие <i>Eccl</i>."],
  ["philema", "<big>philema</big>\n\n<b>philema, atis</b> <i>n</i> (<i>греч.</i>)\nпоцелуй <i>Lcr</i>."],
  ["Philemon", "<big>Philemon</big>\n\n<b>Philemo(n), onis</b> <i>m</i>\nФилемон:\n<b>1)</b> <i>представитель новоаттической комедии, родом из Киликии, современник Менандра</i> <i>Pl, Q</i>;\n<b>2)</b> <i>благочестивый фригийский старик-крестьянин, муж Бавкиды</i> <i>O</i>."],
  ["Philetas", "<big>Philetas</big>\n\n<b>Philetas, ae</b> <i>m</i>\nФилет, <i>родом из Коса, греч. грамматик и элегический поэт середины <i>JV</i> в. до н. э., учитель Птолемея Филадельфа</i> <i>Prp, Q</i>."],
  ["Phileteus", "<big>Phileteus</big>\n\n<b>Phileteus (-aeus), a, um</b> [Philetas]\nфилетов (ora, corymbi <i>Prp</i>)."],
  ["Philippensis", "<big>Philippensis</big>\n\n<b>Philippensis, e</b> [Philippi]\n(происходивший) при Филиппах (proelium <i>PM</i>; bellum <i>Su</i>)."],
  ["Philippeus", "<big>Philippeus</big>\n\n<b>Philippeus, a, um</b>\n<b>1)</b> [Philippi] (находящийся) у Филипп (campi <i>VP</i>);\n<b>2)</b> [Philippus] Филиппов (<i>gen. pl.</i> Philippeum) (nummus <i>Pl, L etc.</i>): Ph. sanguis <i>Prp</i> = Клеопатра (<i>которая считалась потомком Филиппа Македонского</i>); Philippeum aurum <i>Pl</i> золото, из которого чеканилась монета Филиппа (<i>см.</i> Philippus II)."],
  ["Philippi", "<big>Philippi</big>\n\n<b>Philippi, orum</b> <i>m</i>\nФилиппы, <i>город в Македонии на реке Пангей, к сев.-зап. от Амфиполя, бывш. Crenides, переименованный в честь Филиппа Македонского</i> (<i>здесь в 42 г. до н. э. Октавиан и Антоний разбили войска Брута и Кассия</i>) <i>L, VP, Mela</i>."],
  ["Philippica", "<big>Philippica</big>\n\n<b>Philippica, orum</b> <i>n</i>\nфилиппики:\n<b>1)</b> <i>пламенные речи Демосфена против Филиппа Македонского</i> <i>Hier</i>;\n<b>2)</b> <i>речи Цицерона против Антония</i> <i>J</i>."],
  ["Philippicus", "<big>Philippicus</big>\n\n<b>Philippicus, a, um</b>\n= Philippeus: Philippicae orationes <i>C, J, Hier</i> = Philippica."],
  ["Philippius", "<big>Philippius</big>\n\n<b>Philippius, a, um</b> <i>VP adj. к</i>\nPhilippi."],
  ["Philippopolis", "<big>Philippopolis</big>\n\n<b>Philippopolis, is</b> <i>f</i>\nФилиппополь, <i>укреплённый город во Фракии, на реке Гебр</i> (<i>ныне</i> Пловдив) <i>L, T, PM etc.</i>"],
  ["Philippus", "<big>Philippus</big>\n\n<b>I Philippus, i</b> <i>m</i>\nФилипп:\n<b>1.</b> <i>имя македонских царей, из которых наиболее известны</i>:\n<b>1)</b> Ph., <i>сын Аминта, отец Александра</i> (<i>царствовал в 359-336 гг. до н. э.</i>) <i>Pl, C, Nep etc.</i>;\n<b>2)</b> Ph., <i>потерпевший поражение при Киноскефалах в 197 г. до н. э. от Т. Квинкция Фламиния</i> (<i>царствовал в 220-178 гг. до н. э.</i>) <i>Nep, L</i>;\n<b>2.</b> <i>cognomen в роде Марциев</i> (L. Martius Ph.) <i>C</i>.\n\n<b>II Philippus, i</b> <i>m</i>\n<i>золотая монета с изображением Филиппа II Македонского</i> <i>Pl, H</i>."],
  ["Philistaea", "<big>Philistaea</big>\n\n<b>Philistaea, ae</b> <i>f</i>\nФилистея, <i>страна филистимлян</i> <i>Hier</i>."],
  ["Philistini", "<big>Philistini</big>\n\n<b>Philistini, orum</b> <i>m</i>\nфилистимляне <i>Vlg, Eccl</i>."],
  ["Philistion", "<big>Philistion</big>\n\n<b>Philistion, onis</b> <i>m</i>\nФилистион:\n<b>1)</b> <i>известный греческий врач</i> <i>PM</i>;\n<b>2)</b> <i>популярный танцовщик («пантомим») в Риме</i> <i>M</i>."],
  ["Philistus", "<big>Philistus</big>\n\n<b>Philistus, i</b> <i>m</i>\nФилист, <i>греч. историк, подражатель Фукидида, сиракузец, современник обоих Дионисиев</i> <i>C, Nep</i>."],
  ["philitia", "<big>philitia</big>\n\n<b>philitia, orum</b> <i>n</i> (<i>греч.</i>)\nфилитии, <i>общие трапезы у лакедемонян</i> <i>C</i>."],
  ["Phillyrides", "<big>Phillyrides</big>\n\n<b>Phillyrides</b> <i>v. l.</i>\n= Philyrides."],
  ["Philo", "<big>Philo</big>\n\n<b>Philo (Philon), onis</b> <i>m</i>\nФилон:\n<b>1)</b> <i>афинский зодчий и писатель IV-III вв. до н. э.</i> <i>C, Vtr</i>;\n<b>2)</b> <i>родом из Лариссы, греч. философ, бежавший в начале I Митридатовой войны (88 г. до н. э.) в Рим, где подружился с Цицероном</i> <i>C</i>;\n<b>3)</b> <i>родом из Тарсоса, греч. врач</i> <i>CC</i>."],
  ["Philocles", "<big>Philocles</big>\n\n<b>Philocles, is</b> <i>m</i>\nФилокл, <i>афинский военачальник в Пелопоннесской войне</i> <i>Nep</i>."],
  ["Philocteta", "<big>Philocteta</big>\n\n<b>Philocteta</b> <i>и</i> <b>Philoctetes, ae</b> <i>m</i>\nФилоктет, <i>сын Пеанта из Мелибеи (Фессалия); отправился в поход против Трои, но в пути, укушенный змеёй, вынужден был остаться на о-ве Лемносе, и лишь на десятом году Троянской войны Одиссей и Неоптолем доставали его к стенам Трои, где он был исцелён Махаоном; убийством Париса содействовал падению Трои</i> <i>C, O etc.</i>"],
  ["Philoctetaeus", "<big>Philoctetaeus</big>\n\n<b>Philoctetaeus, a, um</b> [Philocteta]\nфилоктетов (clamor <i>C</i>)."],
  ["Philodemus", "<big>Philodemus</big>\n\n<b>Philodemus, i</b> <i>m</i>\nФилодем, <i>философ-эпикуреец и поэт-эпиграмматист (I в. до н. э.), родом из Гадары (Палестина</i>) <i>C</i>."],
  ["philograecus", "<big>philograecus</big>\n\n<b>philograecus, i</b> <i>m</i>\nгрекофил <i>Vr</i>."],
  ["Philolaus", "<big>Philolaus</big>\n\n<b>Philolaus, i</b> <i>m</i>\nФилолай, <i>философ-пифагореец серед. <i>V</i> в. до н. э., родом из Кротона, ученик Архита</i> <i>C</i>."],
  ["philologia", "<big>philologia</big>\n\n<b>philologia, ae</b> <i>f</i> (<i>греч.</i>)\n<b>1)</b> любовь к наукам и литературе, <i>тж.</i> литературно-научные занятия <i>C, Pt, Vtr</i>;\n<b>2)</b> толкование литературных произведений <i>Sen</i>."],
  ["philologus", "<big>philologus</big>\n\n<b>I philologus, a, um</b>\nучёный (homo <i>Sen</i>); научный (res <i>Vtr</i>).\n\n<b>II philologus, i</b> <i>m</i>\n<b>1)</b> учёный <i>C, Su</i>;\n<b>2)</b> исследователь текстов, толкователь авторов <i>Sen</i>."],
  ["Philomela", "<big>Philomela</big>\n\n<b>Philomela, ae</b> <i>f</i>\n<b>1)</b> Филомела, <i>дочь афинского царя Пандиона, сестра Прокны, обесчещенная мужем Прокны, Тереем, и превращенная в соловья</i> <i>O, M</i>;\n<b>2)</b> <i>поэт.</i> соловей <i>V, Sid, Eccl</i>."],
  ["Philomelienses", "<big>Philomelienses</big>\n\n<b>Philomelienses, ium</b> <i>m</i>\nжители города Philomelium <i>C, PM</i>."],
  ["Philomelium", "<big>Philomelium</big>\n\n<b>Philomelium, i</b> <i>n</i>\nФиломелий, <i>город в юго-вост. Фригии</i> <i>C</i>."],
  ["Philometor", "<big>Philometor</big>\n\n<b>Philometor, oris</b> <i>m</i> (<i>греч.</i> «любящий мать»)\n<i>прозвище египетского царя Птолемея VI</i> <i>Just</i>."],
  ["Philon", "<big>Philon</big>\n\n<b>Philon, onis</b> <i>m</i> — <i>см.</i>\nPhilo."],
  ["Philopator", "<big>Philopator</big>\n\n<b>Philopator, oris</b> <i>m</i> (<i>греч.</i> «любящий отца»)\n<i>насмешливое прозвище египетского царя-отцеубийцы Птолемея IV</i> <i>PM, J</i>."],
  ["Philopoemen", "<big>Philopoemen</big>\n\n<b>Philopoemen, enis</b> <i>m</i>\nФилопэмен, <i>род. в Мегалополе (Аркадия) в 253 г. до н. э., командовавший войсками Ахейского союза против римлян, этолян и спартанцев; одержал победы при Мессене и Мантинее и завоевал Спарту, но был захвачен мессенцами и в 183 г. до н. э. отравлен</i> <i>L, Just, Aus</i>."],
  ["Philoromaeus", "<big>Philoromaeus</big>\n\n<b>Philoromaeus, i</b> <i>m</i> (<i>греч.</i> «друг римлян»)\n<i>прозвище каппадокийского царя Ариобарзана</i> <i>C</i>."],
  ["philosopha", "<big>philosopha</big>\n\n<b>philosopha, ae</b> <i>f</i>\nженщина-философ <i>C, Vulc</i>."],
  ["philosophaster", "<big>philosophaster</big>\n\n<b>philosophaster, stri</b> <i>m</i>\nлжефилософ <i>Aug</i>."],
  ["philosophe", "<big>philosophe</big>\n\n<b>philosophe</b> [philosophus]\nпо-философски <i>C</i>."],
  ["philosophia", "<big>philosophia</big>\n\n<b>philosophia, ae</b> <i>f</i> (<i>греч.</i>)\nфилософия (studium sapientiae, quae ph. dicitur <i>C</i>); философский вопрос (de philosophia sermonem habere <i>Nep</i>); <i>pl.</i> философские системы <i>C, AG</i>."],
  ["philosophice", "<big>philosophice</big>\n\n<b>philosophice</b> <i>C</i>\n(<i>v. l.</i>), <i>Lact</i> = philosophe."],
  ["philosophicus", "<big>philosophicus</big>\n\n<b>philosophicus, a, um</b>\nфилософский <i>Sid, Ambr</i>."],
  ["philosophor", "<big>philosophor</big>\n\n<b>philosophor, atus sum, ari</b> <i>depon.</i>\nзаниматься философией, философствовать <i>Pl, C etc.</i>; <i>иногда</i> <i>pass.</i> (sed jam satis est philosophatum <i>Pl</i>)."],
  ["philosophumenos", "<big>philosophumenos</big>\n\n<b>philosophumenos, on</b> <i>Sen</i>\n= philosophicus."],
  ["philosophus", "<big>philosophus</big>\n\n<b>I philosophus, a, um</b>\nфилософский <i>Pac, C etc.</i>\n\n<b>II philosophus, i</b> <i>m</i>\nфилософ <i>C, Sen etc.</i>"],
  ["philostorgus", "<big>philostorgus</big>\n\n<b>philostorgus, a, um</b> (<i>греч.</i>)\nнежно любящий <i>Fronto</i>."],
  ["Philotas", "<big>Philotas</big>\n\n<b>Philotas, ae</b> <i>m</i>\nФилот, <i>сын Пармениона, начальник конницы Александра Македонского</i> <i>QC</i>."],
  ["philotechnus", "<big>philotechnus</big>\n\n<b>philotechnus, a, um</b> (<i>греч.</i>)\nпреданный <i>или</i> посвященный искусствам, художественный (res <i>Vtr</i>)."],
  ["Philoxenus", "<big>Philoxenus</big>\n\n<b>Philoxenus, i</b> <i>m</i> (<i>греч.</i> «гостеприимный»)\nФилоксен, <i>римск. cognomen</i> (C. Avianus Ph.) <i>C</i>."],
  ["philtrum", "<big>philtrum</big>\n\n<b>philtrum, i</b> <i>n</i> (<i>греч.</i>)\nлюбовный напиток <i>O, J</i>."],
  ["philura", "<big>philura</big>\n\n<b>philura</b> <i>v. l.</i>\n= philyra."],
  ["Philus", "<big>Philus</big>\n\n<b>Philus, i</b> <i>m</i> (<i>греч.</i>)\nФил, <i>cognomen в роде Фуриев; наиболее известен</i> L. Furius Ph., <i>консул в 136 г. до н. э., друг Сципиона и Лелия</i> <i>C</i>."],
  ["philyra", "<big>philyra</big>\n\n<b>I philyra (philura), ae</b> <i>f</i> (<i>греч.</i>; <i>лат.</i> tilia)\n<b>1)</b> липа, <i>перен.</i> липовый луб <i>или</i> лыко (<i>которым скрепляли гирлянды и венки</i>) <i>H, PM etc.</i>;\n<b>2)</b> лубяная дощечка для письма <i>Dig, PM</i>.\n\n<b>II Philyra, ae</b> <i>f</i>\nФилира, <i>нимфа, дочь Океана, родившая от Сатурна кентавра Хирона и превращенная в липу</i> <i>VF</i>."],
  ["Philyreius", "<big>Philyreius</big>\n\n<b>Philyreius, a, um</b> [Philyra]\n<b>1)</b> филирин: Ph. heros <i>O</i> = Хирон;\n<b>2)</b> <i>поэт.</i> хиронов (tecta <i>O</i>)."],
  ["Philyrides", "<big>Philyrides</big>\n\n<b>Philyrides, ae</b> <i>m</i>\nсын Филиры, <i>т. е.</i> Хирон <i>V, O, Prp, M</i>."],
  ["phimus", "<big>phimus</big>\n\n<b>phimus, i</b> <i>m</i> (<i>греч.</i>; <i>лат.</i> fritillus)\nстакан для игральных костей <i>H</i>."],
  ["Phineius", "<big>Phineius</big>\n\n<b>Phineius, a, um</b> <i>V, Ap</i>\n= Phineus II."],
  ["Phineus", "<big>Phineus</big>\n\n<b>I Phīne͡us, eī</b> <i>и</i> <b>eos</b> <i>m</i>\nФиней:\n<b>1)</b> <i>царь Салмидесса (Фракия); по внушению своей второй жены Идеи ослепил своих сыновей от первого брака, за что боги лишили его зрения и наслали на него Гарпий, которые мучили его до тех пор, пока аргонавты Калаис и Зет не убили этих чудовищ</i> <i>O, M</i>;\n<b>2)</b> <i>сын Бела, брат Эгипта, Даная и Кефея; показав ему голову Медузы, Персей превратил его в камень</i> <i>O</i>.\n\n<b>II Phīnēus, a, um</b> [Phineus I]\nфинеев: Phineae aves <i>Sen</i> = Гарпии."],
  ["Phinides", "<big>Phinides</big>\n\n<b>Phinides, ae</b> <i>m</i>\nФинид, потомок Финея <i>O</i>."],
  ["Phintia", "<big>Phintia</big>\n\n<b>Phintia, ae</b> <i>f</i>\nФинтия, <i>город в Сицилии</i> <i>C</i>."],
  ["Phintias", "<big>Phintias</big>\n\n<b>Phintias, ae</b> <i>m</i>\nФинтий, <i>сиракузский пифагореец, друг Дамона</i> <i>C, VM</i>."],
  ["Phintienses", "<big>Phintienses</big>\n\n<b>Phintienses, ium</b> <i>m</i>\nжители города Phintia <i>PM</i>."],
  ["phlasca", "<big>phlasca</big>\n\n<b>phlasca (flasca), ae</b> <i>f</i> (<i>греч.</i>)\nсосуд для вина <i>Is</i>."],
  ["phlebotomia", "<big>phlebotomia</big>\n\n<b>phlebotomia, ae</b> <i>и</i> <b>phlebotomice, es</b> <i>f</i> (<i>греч.</i>)\nжилосечение, <i>т. е.</i> кровопускание <i>CA, Veg</i>."],
  ["phlebotomo", "<big>phlebotomo</big>\n\n<b>phlebotomo, —, —, are</b>\nпускать («отворять») кровь <i>Aug, CA</i>."],
  ["phlebotomus", "<big>phlebotomus</big>\n\n<b>phlebotomus, i</b> <i>m</i> (<i>греч.</i>)\nланцет для кровопускания <i>CA, Veg</i>."],
  ["Phlegethon", "<big>Phlegethon</big>\n\n<b>Phlegethon, ontis</b> <i>m</i> (<i>греч.</i> «горящий»)\nФлегетонт, <i>огненная река в подземном царстве</i> <i>V, St</i>."],
  ["Phlegethonteus", "<big>Phlegethonteus</big>\n\n<b>Phlegethonteus, a, um</b>\nфлегетонтовский (ripa <i>Cld</i>; undae <i>Aug</i>)."],
  ["Phlegethontis", "<big>Phlegethontis</big>\n\n<b>Phlegethontis, idis</b> <i>f</i> <i>O</i>\n= Phlegethontea."],
  ["phlegma", "<big>phlegma</big>\n\n<b>phlegma, atis</b> <i>n</i> (<i>греч.</i>)\nфлегма, слизистое выделение <i>Pall, Veg, Hier</i>."],
  ["phlegmaticus", "<big>phlegmaticus</big>\n\n<b>phlegmaticus, a, um</b> [phlegma]\nслизистый (materia <i>Is</i>)."],
  ["phlegmone", "<big>phlegmone</big>\n\n<b>phlegmone, es</b> <i>f</i> (<i>греч.</i>)\nфлегмона, <i>гнойное подкожное воспаление</i> <i>PM</i>."],
  ["Phlegon", "<big>Phlegon</big>\n\n<b>Phlegon, ontis</b> <i>m</i> (<i>греч.</i> «пылающий»)\nФлегонт, <i>один из четырёх коней в колеснице солнца</i> <i>O</i>."],
  ["Phlegra", "<big>Phlegra</big>\n\n<b>Phlegra, ae</b> <i>f</i>\nФлегра, <i>древнее название п-ова Pallene на Халкидике</i> (<i>место, где были сражены небесными молниями восставшие гиганты</i>) <i>PM, SenT</i>."],
  ["Phlegraeus", "<big>Phlegraeus</big>\n\n<b>Phlegraeus, a, um</b> [Phlegra]\n<b>1)</b> флегрейский (campi <i>O</i>; victoria <i>M</i>);\n<b>2)</b> горящий, пылающий: Ph. vertex <i>Sil</i> = Везувии."],
  ["Phlegyae", "<big>Phlegyae</big>\n\n<b>Phlegyae, arum</b> <i>m</i>\nфлегии, <i>разбойничье племя в сев. Беотии и южн. Фессалии</i> <i>O</i>."],
  ["Phlegyas", "<big>Phlegyas</big>\n\n<b>Phlegyas, ae</b> <i>m</i>\nФлегий, <i>сын Марса и Хрисы, отец Иксиона и Коронисы, родоначальник племени флегиев, царь лапифов; убит Аполлоном</i> <i>V, VF, St</i>."],
  ["Phliasius", "<big>Phliasius</big>\n\n<b>I Phliasius, a, um</b> [Phlius]\nфлиунтский (sermo <i>C</i>; regna <i>O</i>; ager <i>L</i>).\n\n<b>II Phliasius, i</b> <i>m</i>\nжитель города Phlius <i>C</i>."],
  ["Phlius", "<big>Phlius</big>\n\n<b>Phlius, antis</b> <i>f</i>\nФлиунт, <i>вольный город в сев.-вост. Пелопоннесе, к юго-зап. от Коринфа</i> <i>L, PM</i>."],
  ["Phobetor", "<big>Phobetor</big>\n\n<b>Phobetor, oris</b> <i>m</i> (<i>греч.</i> «устрашающий»)\n<i>бог сновидений, сын Морфея</i> <i>O</i>."],
  ["phobodipsos", "<big>phobodipsos</big>\n\n<b>phobodipsos, i</b> <i>f</i> (<i>греч.</i>)\nводобоязнь <i>CA</i>."],
  ["phoca", "<big>phoca</big>\n\n<b>phoca, ae</b> <i>f</i> (<i>греч.</i>; <i>лат.</i> vitulus marinus)\nтюлень <i>V, PM, O</i>."],
  ["Phocaea", "<big>Phocaea</big>\n\n<b>Phocaea, ae</b> <i>f</i>\nФокея, <i>приморский город в Ионии, к сев.-зап. от Смирны, против города Лесбос, метрополия Массилии</i> <i>L, Mela, PM</i>."],
  ["Phocaeensis", "<big>Phocaeensis</big>\n\n<b>I Phocaeensis, e</b> [Phocaea]\nфокейский (Graeci <i>PM</i>).\n\n<b>II Phocaeensis, is</b> <i>m</i>\nжитель города Phocaea <i>L, PM, Just</i>."],
  ["Phocaei", "<big>Phocaei</big>\n\n<b>Phocaei, orum</b> <i>m</i>\nфокеяне, жители города Phocaea <i>H, Mela, Amm</i>."],
  ["Phocaicae emporiae", "<big>Phocaicae emporiae</big>\n\n<b>Phocaicae emporiae, arum</b> <i>f</i>\n<i>основанный массилийцами город в Испании</i> <i>Sil</i>."],
  ["Phocaicus", "<big>Phocaicus</big>\n\n<b>Phocaicus, a, um</b>\n<b>1)</b> фокейский (murex <i>O</i>);\n<b>2)</b> <i>поэт.</i> массилийский (ora <i>Sil</i>);\n<b>3)</b> фокидский (tellus <i>O</i>);\n<b>4)</b> <i>поэт.</i> парнасский (laurus <i>Lcn</i>)."],
  ["Phocais", "<big>Phocais</big>\n\n<b>Phocais, idis</b> <i>f</i> [Phocaea]\nфокейская, <i>поэт.</i> массилийская (juventus <i>Lcn</i>; ballista <i>Sil</i>)."],
  ["phoce", "<big>phoce</big>\n\n<b>phoce, es</b> <i>f</i> <i>O</i>\n= phoca."],
  ["Phocenses", "<big>Phocenses</big>\n\n<b>Phocenses, ium</b> <i>m</i>\nфокидяне, жители области Phocis <i>L, PM</i>."],
  ["Phoceus", "<big>Phoceus</big>\n\n<b>I Phōcēus, a, um</b> [Phocis]\nфокидский (rura <i>O</i>): juvenis <i>Ph, O</i> = Pylades.\n\n<b>II Phōce͡us, eī</b> <i>и</i> <b>eos</b> <i>m</i>\nжитель области Phocis <i>O</i>."],
  ["Phocii", "<big>Phocii</big>\n\n<b>Phocii, orum</b> <i>m</i>\nфокидяне, жители области Phocis <i>C</i>."],
  ["Phocion", "<big>Phocion</big>\n\n<b>Phocion, onis</b> <i>m</i>\nФокион, <i>афинский военачальник и политик, противник Демосфена, сторонник Македонии; казнён в 318 г. до н. э.</i> <i>Nep</i>."],
  ["Phocis", "<big>Phocis</big>\n\n<b>Phocis, idis</b> <i>и</i> <b>idos</b> <i>f</i>\n<b>1)</b> Фокида, <i>область в средней Греции, между Беотией и Этолией; на её территории находились горы Парнасе и Геликон, Кастальский источник и река Кефис</i> <i>L, O, Lcn etc.</i>;\n<b>2)</b> <i>поэт. Sid</i> = Massilia (<i>как колония Фокиды</i>)."],
  ["Phocus", "<big>Phocus</big>\n\n<b>Phocus, i</b> <i>m</i>\nФок, <i>сын эгинского царя Эака, убитый своими братьями Теламоном и Пелеем</i> <i>O</i>."],
  ["Phoebas", "<big>Phoebas</big>\n\n<b>Phoebas, adis</b> <i>f</i>\nФебада, <i>жрица Аполлона, прорицательница</i> <i>O, Lcn</i>."],
  ["Phoebe", "<big>Phoebe</big>\n\n<b>Phoebe, es</b> <i>f</i> (<i>греч.</i> «лучезарная»)\n<b>1)</b> Феба, <i>сестра Феба, богиня луны</i> (= <i>Diana</i>) <i>V, O, VF</i>;\n<b>2)</b> <i>поэт.</i> (лунная) ночь (tertia <i>Ph, O</i>);\n<b>3)</b> Феба, <i>дочь Леды, сестра Елены</i> <i>O</i>;\n<b>4)</b> <i>дочь мессенского царя Левкиппа, ставшая женой Поллукса</i> <i>Prp, O</i>."],
  ["Phoebeius", "<big>Phoebeius</big>\n\n<b>Phoebeius, a, um</b> [Phoebus]\nфебов: Ph. juvenis <i>O</i> = Эскулапий; Ph. anguis <i>O</i> = anguis Aesculapii; Phoebeia ales <i>O</i> = ворон."],
  ["Phoebeum", "<big>Phoebeum</big>\n\n<b>Phoebeum, i</b> <i>n</i>\n<i>храм Феба близ Спарты</i> <i>L</i>."],
  ["Phoebeus", "<big>Phoebeus</big>\n\n<b>Phoebeus, a, um</b>\n= Phoebeius: Phoebea lampas <i>V</i> = солнце; Phoebea virgo <i>O</i> = Дафна; Phoebea Rhodos <i>O</i> Родос, посвященный Фебу-Аполлону; Phoebeae sortes <i>O</i> = оракул Феба-Аполлона; Phoebea ars <i>O</i> = medicina."],
  ["Phoebidas", "<big>Phoebidas</big>\n\n<b>Phoebidas, ae</b> <i>m</i>\nФебид, <i>спартанский военачальник, занявший в 383 г. до н. э. Кадмею в Фивах</i> <i>Nep</i>."],
  ["Phoebigena", "<big>Phoebigena</big>\n\n<b>Phoebigena, ae</b> <i>m</i> [Phoebus + gigno]\n«рождённый Фебом», <i>т. е.</i> Эскулапий <i>V</i>."],
  ["Phoebus", "<big>Phoebus</big>\n\n<b>Phoebus, i</b> <i>m</i> (<i>греч.</i> «лучезарный»)\n<b>1)</b> Феб, <i>поэт. эпитет Аполлона</i> <i>V, H, Prp</i>;\n<b>2)</b> <i>поэт.</i> солнце (rediens fugit astra <i>Ph, H</i>): sub utroque Phoebo <i>O</i> на востоке и на западе; Phoebi orbis <i>O</i> солнечный диск."],
  ["Phoenica", "<big>Phoenica</big>\n\n<b>Phoenica, ae</b> <i>и</i> <b>Phoenice, es</b> <i>f</i>\nФиникия:\n<b>1)</b> <i>страна на вост. побережье Средиземного моря, от горы Кармел до Арада</i> (<i>с городами Тир и Сидон</i>) <i>Vr, C etc.</i>;\n<b>2)</b> <i>город в Эпире</i> <i>L</i>."],
  ["Phoenicatus", "<big>Phoenicatus</big>\n\n<b>Phoenicatus, a, um</b>\nгнедой (equus <i>Is</i>)."],
  ["Phoenices", "<big>Phoenices</big>\n\n<b>Phoenices, um</b> <i>m</i>\n<b>1)</b> финикияне <i>C, Sl, Lcn etc.</i>;\n<b>2)</b> пунийцы, карфагеняне <i>Sil</i>."],
  ["Phoenicia", "<big>Phoenicia</big>\n\n<b>Phoenicia, ae</b> <i>f</i> <i>C</i>\n= Phoenica 1."],
  ["Phoenicias", "<big>Phoenicias</big>\n\n<b>Phoenicias, ae</b> <i>m</i>\nфиникий, юго-юго-вост. ветер <i>PM</i>."],
  ["phoenicium", "<big>phoenicium</big>\n\n<b>phoenicium, i</b> <i>m</i>\nпурпур <i>Aug</i>."],
  ["Phoenicius", "<big>Phoenicius</big>\n\n<b>Phoenicius, a, um</b> [Phoenica]\n<b>1)</b> финикийский (mare <i>PM</i>);\n<b>2)</b> пурпурный (flos <i>PM</i>)."],
  ["phoenicobalanus", "<big>phoenicobalanus</big>\n\n<b>phoenicobalanus, i</b> <i>m</i> (<i>греч.</i>)\nфиник <i>PM</i>."],
  ["phoenicopterus", "<big>phoenicopterus</big>\n\n<b>phoenicopterus, i</b> <i>m</i> (<i>греч.</i>) <i>зоол.</i>\nфламинго <i>Sen, Su, PM etc.</i>"],
  ["phoenicurus", "<big>phoenicurus</big>\n\n<b>phoenicurus, i</b> <i>m</i>\n<i>предпол. птица</i> горихвостка (<i>Ruticilla phoenicurus</i>) <i>PM</i>."],
  ["Phoenicus", "<big>Phoenicus</big>\n\n<b>Phoenicus, untis</b> <i>f</i>\nФеникунт:\n<b>1)</b> <i>портовый город в Ликии</i> <i>L</i>;\n<b>2)</b> <i>город в Ионии</i> <i>L</i>."],
  ["Phoenicussa", "<big>Phoenicussa</big>\n\n<b>Phoenicus(s)a, ae</b> <i>f</i>\nФеникусса, <i>один из Эолийских о-вов</i> <i>PM</i>."],
  ["Phoenissa", "<big>Phoenissa</big>\n\n<b>I Phoenissa, ae</b> <i>f</i> [Phoenica]\n<b>1)</b> финикийская (Dido <i>V</i>): Ph. exsul <i>O</i> — Анна (<i>сестра Дидоны</i>);\n<b>2)</b> фиванская (<i>так как Фивы Беотийские основаны финикиянином Кадмом</i>) (cohors <i>St</i>);\n<b>3)</b> карфагенская (juventa, classis <i>Sil</i>).\n\n<b>II Phoenissa, ae</b> <i>f</i>\n<b>1)</b> (<i>sc.</i> mulier) финикиянка <i>V, SenT</i>;\n<b>2)</b> (<i>sc.</i> urbs) <i>Sil</i> Карфаген."],
  ["Phoenissus", "<big>Phoenissus</big>\n\n<b>Phoenissus, a, um</b>\nфиникийский, <i>поэт.</i> карфагенский (agmina <i>Sil</i>)."],
  ["Phoenix", "<big>Phoenix</big>\n\n<b>I Phoenix, icis</b> <i>m</i>\nФеникс:\n<b>1)</b> <i>сын Аминтора, предводитель долопов, воспитатель Ахилла и спутник его в Троянской войне</i> <i>C, V, Prp, O</i>;\n<b>2)</b> <i>сын Агенора, брат Кадма и Европы</i> <i>PM</i>;\n<b>3)</b> <i>река в Фессалии</i> <i>PM, Lcn</i>.\n\n<b>II Phoenix, icis</b> <i>m</i> [<i>sg. к</i> Phoenices]\nфиникиянин <i>PM</i>.\n\n<b>III phoenix, icis</b> <i>f</i> (<i>греч.</i>)\nпальма <i>PM</i>.\n\n<b>IV phoenix, icis</b> <i>m</i>\nфеникс, <i>сказочная птица, которая, прожив 500 лет, сама сжигала себя в своём гнезде, и из пепла которой возникал новый феникс</i> <i>Sen, T, O</i>."],
  ["Pholoe", "<big>Pholoe</big>\n\n<b>Pholoe, es</b> <i>f</i>\nФолоя :\n<b>1)</b> <i>лесистая горная цепь на границе Аркадии и Элиды</i> <i>O, PM, Lcn etc.</i>;\n<b>2)</b> <i>гора в Фессалии, где, по преданию, обитали кентавры</i> <i>Lcn, St</i>;\n<b>3)</b> <i>женское имя</i> <i>V, H, Tib</i>."],
  ["Pholus", "<big>Pholus</big>\n\n<b>Pholus, i</b> <i>m</i>\nФол, <i>кентавр, сын Иксиона</i> <i>V, O</i>."],
  ["phonascus", "<big>phonascus</big>\n\n<b>phonascus, i</b> <i>m</i> (<i>греч.</i>)\n<b>1)</b> учитель пения и декламации <i>Su, Q</i>;\n<b>2)</b> (pho-) запевала <i>Sid</i>."],
  ["Phorcis", "<big>Phorcis</big>\n\n<b>Phorcis, idis</b> <i>и</i> <b>idos</b> <i>f</i>\n<b>1.</b> <i>adj. к</i> Phorcus;\n<b>2.</b> <i>subst.</i> Форкида, <i>дочь Форка</i> <i>Prp</i>: sorores Phorcides <i>O</i> = Graeae, <i>три сестры с единственным глазом и единственным зубом</i> <i>O</i>."],
  ["Phorcus", "<big>Phorcus</big>\n\n<b>Phorcus, i</b> <i>m</i>\nФорк, <i>сын Понта (Моря) и Геи (Земли), отец Грей (Graeae) и Горгон, превращенный после смерти в морское божество</i> <i>V, PM etc.</i>"],
  ["Phorcynis", "<big>Phorcynis</big>\n\n<b>Phorcynis, idos</b> <i>f</i>\n(= Phorcis) <i>O, Lcn</i> = Medusa."],
  ["Phorcys", "<big>Phorcys</big>\n\n<b>Phorcys, ydis</b> <i>и</i> <b>ydos</b> <i>f</i> <i>v. l.</i>\n= Phorcis."],
  ["Phormio", "<big>Phormio</big>\n\n<b>I Phormio, onis</b> <i>m</i>\nФормион:\n<b>1)</b> <i>философ-перипатетик из Эфеса, вздумавший прочесть Ганнибалу лекцию о военном искусстве; перен. — любитель поучать в делах, в которых ничего не смыслит</i> <i>C</i>;\n<b>2)</b> <i>заглавие комедии Теренция</i> (<i>по имени её героя, парасита</i>).\n\n<b>II phormio, onis</b> <i>m</i> (<i>греч.</i>)\nплетёнка, циновка <i>Dig</i>."],
  ["Phoroneus", "<big>Phoroneus</big>\n\n<b>I Phorōne͡us, eī</b> <i>и</i> <b>eos</b> <i>m</i>\nФороней, <i>сын Инаха, брат Ио, царь Аргоса</i> <i>Pl, St</i>.\n\n<b>II Phorōnēus, a, um</b> [Phoroneus I]\nфоронеев, <i>поэт.</i> аргосский (antra <i>St</i>)."],
  ["Phoronis", "<big>Phoronis</big>\n\n<b>Phoronis, idis</b> <i>и</i> <b>idos</b> <i>f</i>\nФоронида, <i>т. е.</i> Ио: Phoronides venae <i>SenT</i> = река Inachus."],
  ["Phosphorus", "<big>Phosphorus</big>\n\n<b>Phosphorus, i</b> <i>m</i> (<i>греч.</i> «светоносец»)\nутренняя звезда <i>M</i>."],
  ["Phrahates", "<big>Phrahates</big>\n\n<b>Phra(h)ates, ae</b> <i>и</i> <b>is</b> <i>m</i>\nФраат, <i>имя ряда парфянских царей из дома Арсакидов; наиболее известен</i> Ph. IV, <i>одержавший в 36 г. до н. э. победу над Антонием, но в 20 г. до н. э. вернувший Августу военнопленных и военные знамёна, которые были захвачены им у Антония и Красса</i> <i>H, T, PM</i>."],
  ["phrasis", "<big>phrasis</big>\n\n<b>phrasis, is</b> (<i>acc.</i> im <i>и</i> in, <i>abl.</i> i) <i>f</i> (<i>греч.</i>; <i>лат.</i> elocutio)\nораторский слог, стиль <i>Q, Sen</i>."],
  ["Phrates", "<big>Phrates</big>\n\n<b>Phrates</b> <i>v. l.</i>\n= Phra(h)ates."],
  ["phrenesis", "<big>phrenesis</big>\n\n<b>phrenesis, is</b> <i>f</i> (<i>греч.</i>)\nумопомешательство, безумие <i>Sen, J</i>."],
  ["phreneticus", "<big>phreneticus</big>\n\n<b>phreneticus (freneticus)</b> <i>и</i> <b>phreniticus</b> (<i>CC</i>), <b>a, um</b> (<i>греч.</i>)\nсумасшедший, безумный <i>C</i>: f. periit <i>Pt</i> он умер в состоянии буйного помешательства."],
  ["phrenitis", "<big>phrenitis</big>\n\n<b>phrenitis, idis</b> (<i>acc.</i> im) <i>f</i> <i>CC, Sen, J</i>\n= phrenesis."],
  ["phrenitizo", "<big>phrenitizo</big>\n\n<b>phrenitizo, —, —, are</b>\nнаходиться в состоянии (буйного) помешательства <i>CC</i>."],
  ["Phrixeus", "<big>Phrixeus</big>\n\n<b>Phrixeus, a, um</b> <i>adj. к</i>\nPhrixus: Phrixea vellera <i>O</i> = золотое руно; stagna Phrixeae sororis <i>O</i> = Геллеспонт; Ph. agnus <i>M</i> = созвездие Овна; Ph. maritus <i>M</i> = златорунный баран (<i>на котором Фрикс переплыл Геллеспонт</i>); Phrixeum mare <i>SenT</i> <i>или</i> aequor <i>St</i>, <i>тж.</i> Ph. pontus <i>Lcn</i> = Геллеспонт, <i>реже</i> Эгейское море."],
  ["Phrixus", "<big>Phrixus</big>\n\n<b>Phrixus (-os), i</b> <i>m</i>\nФрикс, <i>сын Атаманта и Нефелы; спасаясь со своей сестрой Геллой от преследований мачехи Ино, на златорунном баране прибыл в Колхиду к царю Ээту, принёс в жертву барана, а его золотое руно повесил в пещере Марса</i> <i>O</i>: Phrixi litora <i>St</i> = берега Геллеспонта; Phrixi semita <i>St</i> = Геллеспонт; portitor Phrixi <i>Col</i> = созвездие Овна."],
  ["Phryges", "<big>Phryges</big>\n\n<b>Phryges, um</b> <i>m</i>\nфригийцы, жители Фригии <i>C etc.</i>; <i>перен.</i> троянцы <i>C, V</i> <i>или</i> римляне (<i>как потомки Энея</i>) <i>Sil</i>."],
  ["Phrygia", "<big>Phrygia</big>\n\n<b>Phrygia, ae</b> <i>f</i>\n<b>1)</b> Фригия, <i>страна в зап. части Малой Азии, разделённая Мисией на Большую Фригию и Малую Фригию</i> <i>Vr, L, V, H, O etc.</i>;\n<b>2)</b> фригиянка, <i>поэт.</i> троянка <i>V</i>."],
  ["Phrygianus", "<big>Phrygianus</big>\n\n<b>Phrygianus, a, um</b> <i>Sen</i>\n= Phrygionius."],
  ["Phrygio", "<big>Phrygio</big>\n\n<b>Phrygio, onis</b> <i>m</i>\nфригиец, <i>перен.</i> вышивальщик <i>Pl, PM</i>."],
  ["Phrygionius", "<big>Phrygionius</big>\n\n<b>Phrygionius, a, um</b> [Phrygio]\nвышитый (vestes <i>PM</i>)."],
  ["Phrygiscus", "<big>Phrygiscus</big>\n\n<b>Phrygiscus, a, um</b> <i>Veg</i>\n= Phrygius I."],
  ["Phrygius", "<big>Phrygius</big>\n\n<b>I Phrygius, a, um</b>\nфригийский (modus <i>Ap</i>); <i>поэт.</i> троянский (<i>так как Троя относилась к Фригии</i>): Phrygii hymenaei <i>V</i> = брак Энея и Лавинии; Ph. maritus <i>V</i> = Эней, <i>но тж.</i> <i>Prp</i> = Пелоп (<i>как сын фригийского царя Тантала</i>); Ph. senex <i>O</i> = Антенор; Ph. vates <i>O</i> = Гелен; Ph. pastor <i>V</i> = Парис; Ph. tyrannus <i>V</i> = Эней <i>и</i> <i>O</i> = Лаомедонт; Ph. minister <i>VF</i> <i>или</i> venator <i>St</i> = Ганимед; Phrygia mater <i>V</i> = Кибела; Phrygii modi <i>O</i> = песни в честь Кибелы; Phrygiae vestes <i>V</i> фригийские (расписные) одежды; Phrygium aes <i>Lcn</i> = кимвалы; Ph. lapis <i>H</i> фригийский мрамор, <i>но тж.</i> <i>PM</i> охра; Phrygiae columna <i>Tib</i> колонны из фригийского мрамора.\n\n<b>II Phrygius, i</b> <i>m</i>\nФригии, <i>река в Ионии, приток Герма</i> <i>L</i>."],
  ["Phryne", "<big>Phryne</big>\n\n<b>Phryne, es</b> <i>f</i>\nФрина:\n<b>1)</b> (<i>собств.</i> Mnesarete), <i>афинская красавица-гетера, родом из Теспий, подруга Гиперида, отстроившая на свой счёт разрушенные Александром Македонским Фивы</i> <i>Prp, Q, VM</i>;\n<b>2)</b> <i>имя римск. куртизанки</i> <i>H</i>;\n<b>3)</b> <i>имя сводни</i> <i>Tib</i>."],
  ["Phryx", "<big>Phryx</big>\n\n<b>I Phryx, ygis</b> <i>m</i> [<i>sg. к</i> Phryges]\nфригиец: Phrygem plagis fieri solere meliorem <i>C</i> (как говорит пословица,) фригиец становится лучше от побоев, <i>т. е.</i> крепок задним умом || <i>поэт.</i> Эней <i>Prp, O</i>, Марсий <i>St</i> <i>или</i> жрец Кибелы <i>Prp</i>.\n\n<b>II Phryx, ygis</b> <i>m</i> <i>J, Cld</i>\n= Phrygius II.\n\n<b>III Phryx, ygis</b> <i>m</i>\nФриг, <i>река в Лидии</i> <i>PM</i>."],
  ["Phthas", "<big>Phthas</big>\n\n<b>Phthas</b> <i>m indecl.</i>\nФта, <i>египетский бог, соответствующий римск. Вулкану</i> <i>C</i>."],
  ["Phthia", "<big>Phthia</big>\n\n<b>Phthia, ae</b> <i>f</i>\nФтия, <i>город в южн. Фессалии, центр племени мирмидонян с их царём Пелеем, отцом Ахилла</i> <i>V, Mela</i>."],
  ["Phthias", "<big>Phthias</big>\n\n<b>Phthias, adis</b> <i>f</i>\nфтиянка <i>O</i>."],
  ["Phthiota", "<big>Phthiota</big>\n\n<b>Phthiota</b> <i>и</i> <b>Phthiotes, ae</b> <i>m</i>\nфтиот, житель Фтии <i>C, L, PM, O</i>."],
  ["Phthioticus", "<big>Phthioticus</big>\n\n<b>Phthioticus, a, um</b> [Phthia]\nфтийский <i>или</i> фтиотидский (ager <i>L</i>), <i>поэт.</i> фессалийский (Tempe <i>Ctl</i>)."],
  ["Phthiotis", "<big>Phthiotis</big>\n\n<b>Phthiotis, idis</b> <i>f</i>\nФтиотида, <i>область Фтии</i> <i>L</i>."],
  ["phthiriasis", "<big>phthiriasis</big>\n\n<b>phthiriasis, is</b> <i>f</i> (<i>греч.</i>) <i>мед.</i>\nфтириаз, вшивая болезнь <i>PM</i>."],
  ["phthisicus", "<big>phthisicus</big>\n\n<b>phthisicus, a, um</b> [phthisis]\nчахоточный <i>Vtr, PM, M, Scr</i>."],
  ["phthisis", "<big>phthisis</big>\n\n<b>phthisis, is</b> <i>f</i> (<i>греч.</i>)\nчахотка <i>Sen, Col, PM, J</i>."],
  ["phthisiscens", "<big>phthisiscens</big>\n\n<b>phthisiscens, entis</b> <i>Sid</i>\n= phthisicus."],
  ["Phthius", "<big>Phthius</big>\n\n<b>Phthius, a, um</b> [Phthia]\n<b>1)</b> фтийский: Ph. vir <i>Prp</i> = Ахилл; Ph. rex <i>O</i> = Пелей;\n<b>2)</b> <i>перен.</i> фессалийский (Thebae <i>L</i>)."],
  ["phthoe", "<big>phthoe</big>\n\n<b>phthoe, es</b> <i>f</i> (<i>греч.</i>) <i>CA</i>\n= phthisis."],
  ["phthorius", "<big>phthorius</big>\n\n<b>phthorius, a, um</b> (<i>греч.</i>)\nразрушающий, уничтожающий, <i>тж.</i> абортивный (vinum <i>PM</i>)."],
  ["phy", "<big>phy</big>\n\n<b>phy!</b> <i>interj.</i>\nда ну!, полно! <i>Ter</i>."],
  ["phycos", "<big>phycos</big>\n\n<b>phycos, i</b> <i>n</i> (<i>греч.</i>)\nлакмусовый лишайник <i>PM</i>."],
  ["Phycus", "<big>Phycus</big>\n\n<b>Phycus, antis</b> <i>m</i>\nФикунт, <i>мыс в Кирене</i> <i>PM, Lcn</i>."],
  ["phylaca", "<big>phylaca</big>\n\n<b>phylaca, ae</b> <i>f</i> (<i>греч.</i>; <i>лат.</i> custodia)\nтюрьма, темница <i>Pl</i>."],
  ["Phylace", "<big>Phylace</big>\n\n<b>Phylace, es</b> <i>f</i>\nФилака:\n<b>1)</b> <i>город в южн. Фессалии</i> (<i>где царствовал Протесилай</i>) <i>PM</i>;\n<b>2)</b> <i>город молоссян в Эпире <i>L</i>.</i>"],
  ["Phylaceis", "<big>Phylaceis</big>\n\n<b>Phylaceis, idis</b> <i>f</i> [Phylace 1]\n<b>1)</b> жительница города Phylace, <i>т. е.</i> Лаодамия (<i>жена Протесилая</i>) <i>St</i>;\n<b>2)</b> фессалиянка (matres Phylaceides <i>O</i>)."],
  ["Phylaceius", "<big>Phylaceius</big>\n\n<b>Phylaceius, a, um</b> [Phylace]\nфилакский: Phylaceia conjunx <i>O</i> = Лаодамия."],
  ["Phylacides", "<big>Phylacides</big>\n\n<b>Phylacides, ae</b> <i>m</i> [Phylacus]\nпотомок Филака, <i>т. е.</i> Протесилай <i>Prp, O</i>."],
  ["phylacista", "<big>phylacista</big>\n\n<b>phylacista, ae</b> <i>m</i> [phylaca]\nтюремщик, <i>перен.</i> кредитор, подстерегающий должника у его дома <i>Pl</i>."],
  ["phylacterium", "<big>phylacterium</big>\n\n<b>phylacterium, i</b> <i>n</i> (<i>греч.</i>; <i>евр.</i> th'philin) <i>преим. pl.</i>\nфилактерии, <i>записанные на пергаменте отрывки из библии</i> (<i>главным образом, из 10 заповедей</i>) <i>Vlg, Hier</i>."],
  ["Phylacus", "<big>Phylacus</big>\n\n<b>Phylacus, i</b> <i>m</i>\n<i>основатель города Phylace (Фессалия), отец Ификла</i> <i>O etc.</i>"],
  ["phylarchus", "<big>phylarchus</big>\n\n<b>phylarchus, i</b> <i>m</i> (<i>греч.</i>)\nфиларх, глава племени (ph. Arabum <i>C</i>; ph. Saracenorum <i>Amm</i>)."],
  ["Phyle", "<big>Phyle</big>\n\n<b>Phyle, es</b> <i>f</i>\nФила, <i>крепостное сооружение к сев. от Афин, на границе с Беотией</i> <i>Nep, Just</i>."],
  ["Phyleus", "<big>Phyleus</big>\n\n<b>Phȳle͡us, eī</b> <i>m</i>\nФилей, <i>сын Авгия, царя Элиды</i> <i>O</i>."],
  ["Phyllac-", "<big>Phyllac-</big>\n\n<b>Phyllac-</b> <i>O</i>\n= Phylac-."],
  ["Phylleis", "<big>Phylleis</big>\n\n<b>Phylleis, idis</b> <i>f</i> [Phyllos]\nфиллосская, <i>поэт.</i> фессалийская (mater <i>O предпол.</i> = Laodamia)."],
  ["Phylleius", "<big>Phylleius</big>\n\n<b>Phylle(i)us, a, um</b> [Phyllos]\nфиллосский, <i>поэт.</i> фессалийский: Ph. juvenis <i>O</i> = Кеней."],
  ["Phyllis", "<big>Phyllis</big>\n\n<b>Phyllis, idis</b> <i>и</i> <b>idos</b> <i>f</i> (<i>греч.</i> «молодой листок»)\nФиллида:\n<b>1)</b> <i>дочь фракийск. царя Ситона (Sithon), превращенная в миндальное дерево</i> <i>O, PM</i>;\n<b>2)</b> <i>имя девушки</i> <i>V</i>;\n<b>3)</b> миндальное дерево <i>Pall</i>."],
  ["Phyllos", "<big>Phyllos</big>\n\n<b>Phyllos, i</b> <i>f</i>\nФиллос, <i>город в зап. Фессалии, к сев. от Метрополя</i> <i>St</i>."],
  ["phyma", "<big>phyma</big>\n\n<b>phyma, atis</b> <i>n</i> (<i>греч.</i>)\nгнойный нарыв <i>CC</i>."],
  ["Physcon", "<big>Physcon</big>\n\n<b>Physcon, onis</b> <i>m</i> (<i>греч.</i> «толстобрюхий»)\n<i>прозвище египетского царя Птолемея II Эвергета</i> <i>Just, Tert</i>."],
  ["physeter", "<big>physeter</big>\n\n<b>physeter, eris</b> <i>m</i> <i>предпол.</i>\nкашалот <i>PM, Sen</i>."],
  ["physica", "<big>physica</big>\n\n<b>I physica, ae</b> <i>f</i>\nестествознание, наука о природе <i>C</i>.\n\n<b>II physica, orum</b> <i>n</i> <i>C, Aus</i>\n= physica I."],
  ["physice", "<big>physice</big>\n\n<b>I physice</b> [physicus]\nфизически, <i>т. е.</i> как принято у естествоведов (dicere <i>C</i>).\n\n<b>II physice, es</b> <i>f</i> <i>C</i>\n= physica."],
  ["physicus", "<big>physicus</big>\n\n<b>I physicus, a, um</b> (<i>греч.</i>)\n<b>1)</b> естественный, физический, естественнонаучный (ratio <i>C</i>);\n<b>2)</b> подходящий по своей природе (remedium <i>Veg</i>).\n\n<b>II physicus, i</b> <i>m</i>\nестествоиспытатель, натуралист <i>Vr, C, Mela</i>."],
  ["physiognomon", "<big>physiognomon</big>\n\n<b>physiognomon, onis</b> <i>m</i>\nзнаток природы, <i>преим.</i> человеческих свойств (по чертам лица), физиогномист <i>C</i>."],
  ["physiologia", "<big>physiologia</big>\n\n<b>physiologia, ae</b> <i>f</i>\nестествоведение, исследование природы <i>C, Aug</i>."],
  ["physiologice", "<big>physiologice</big>\n\n<b>physiologice</b>\nестественнонаучно (interpretari <i>Tert</i>)."],
  ["physiologicus", "<big>physiologicus</big>\n\n<b>physiologicus, a, um</b> (<i>греч.</i>)\nестественнонаучный <i>Tert, Aug</i>."],
  ["piabilis", "<big>piabilis</big>\n\n<b>piabilis, e</b> [pio]\nкоторого можно умилостивить: p. fulmen <i>O</i> молния, которую можно отвратить умилостивительной жертвой."],
  ["piacularia", "<big>piacularia</big>\n\n<b>piacularia, ium</b> <i>n</i> (<i>sc.</i> sacrificia)\nискупительные (умилостивительные) жертвы <i>L</i>."],
  ["piacularis", "<big>piacularis</big>\n\n<b>piacularis, e</b> [piaculum]\nочистительный, умилостивительный, искупительный (sacrificia <i>L</i>): men piacularem oportet fieri ob stultitiam tuam? <i>Pl</i> стало быть я должен искупить твоё безумие?"],
  ["piaculariter", "<big>piaculariter</big>\n\n<b>piaculariter</b> [piaculum]\nгреховно, неправедно (negare <i>Tert</i>)."],
  ["piaculo", "<big>piaculo</big>\n\n<b>piaculo, —, —, are</b>\nумилостивлять, искупать (Martem suovetaurilibus <i>Cato</i>)."],
  ["piaculum", "<big>piaculum</big>\n\n<b>piaculum, i</b> <i>n</i> [pio]\n<b>1)</b> умилостивительная жертва (pecudes prima piacula sunto <i>V</i>); искупление (te piacula nulla resolvent <i>H</i>; p. rupti foederis <i>L</i>);\n<b>2)</b> кара, наказание: exigere p. ab aliquo <i>L</i> наказать кого-л.;\n<b>3)</b> достойный кары поступок, грех, преступление: p. committere (mereri) <i>L</i> <i>или</i> p. sibi contrahere <i>L</i> совершить преступление, согрешить;\n<b>4)</b> бедствие, несчастье (tutus a piaculis omnibus <i>PM</i>)."],
  ["piamen", "<big>piamen</big>\n\n<b>piamen, inis</b> <i>n</i> [pio]\nумилостивительная (очистительная) жертва, искупление <i>O</i>."],
  ["piamentum", "<big>piamentum</big>\n\n<b>piamentum, i</b> <i>n</i> <i>PM, Sen</i>\n= piamen."],
  ["piatio", "<big>piatio</big>\n\n<b>piatio, onis</b> <i>f</i> <i>PM, Lad</i>\n= piamen."],
  ["piator", "<big>piator</big>\n\n<b>piator, oris</b> <i>m</i>\nприносящий умилостивительную жертву <i>Ap</i> (<i>v. l.</i>)."],
  ["pica", "<big>pica</big>\n\n<b>pica, ae</b> <i>f</i>\nсорока <i>O, PM</i>: p. varia <i>Pt</i> пёстрая (<i>т. е.</i> длиннохвостая) сорока || <i>перен.</i> болтунья <i>Pt</i>."],
  ["Picanus", "<big>Picanus</big>\n\n<b>Picanus, i</b> <i>m</i>\nПикан, <i>гора в области Пицена</i> <i>Sil</i>."],
  ["picaria", "<big>picaria</big>\n\n<b>picaria, ae</b> <i>f</i> [pix]\nдегтярня <i>или</i> смоловарня <i>C, Dig</i>."],
  ["picea", "<big>picea</big>\n\n<b>picea, ae</b> <i>f</i> [pix]\nсосна <i>V, PM, O</i>."],
  ["piceatus", "<big>piceatus</big>\n\n<b>piceatus, a, um</b> [pix]\nсмолистый, липкий, <i>перен.</i> вороватый (manus <i>M</i> — <i>v. l.</i>)."],
  ["Picens", "<big>Picens</big>\n\n<b>I Picens, entis</b> [Picenum]\nпиценский (ager <i>C</i>; populus <i>L</i>).\n\n<b>II Picens, entis</b> <i>m</i>\nжитель Пицена <i>Vr, C, PM etc.</i>"],
  ["Picentia", "<big>Picentia</big>\n\n<b>Picentia, ae</b> <i>f</i>\nПиценция, <i>город в Кампании, к юго-вост. от Салерна</i> <i>Mela, Sil</i>."],
  ["Picentinus", "<big>Picentinus</big>\n\n<b>Picentinus, a, um</b>\n<b>1)</b> [Picentia] пицентийский (ager <i>PM</i>);\n<b>2)</b> [Picenus] пиценский (Ceres <i>M</i>)."],
  ["Picenum", "<big>Picenum</big>\n\n<b>Picenum, i</b> <i>n</i>\nПицен, <i>область в средней Италии, к югу от Анконы, примыкающая к Адриатическому морю</i> <i>Vr, C, Cs</i>. <i>L etc.</i>"],
  ["Picenus", "<big>Picenus</big>\n\n<b>Picenus, a, um</b> [Picenum]\nпиценский (ager <i>H</i>; vitis, olivae <i>PM</i>)."],
  ["piceus", "<big>piceus</big>\n\n<b>piceus, a, um</b> [pix]\n<b>1)</b> смоляной (ignes <i>Lcn</i>);\n<b>2)</b> чёрный как смола (nubes <i>O</i>; caligo <i>V</i>; oves <i>VF</i>)."],
  ["picinus", "<big>picinus</big>\n\n<b>picinus, a, um</b> [pix]\nчёрный как смола (uva <i>PM</i>)."],
  ["picis", "<big>picis</big>\n\n<b>picis</b> <i>gen. к</i>\npix."],
  ["pico", "<big>pico</big>\n\n<b>pico, avi, atum, are</b> [pix]\n<b>1)</b> осмаливать, засмаливать (dolia <i>Cato, Col, Su</i>);\n<b>2)</b> приправлять смолой: vinum picatum <i>Col, M</i> вино со смолистым запахом."],
  ["picridiae", "<big>picridiae</big>\n\n<b>picridiae, arum</b> <i>f</i> <i>Aug</i>\n= picris."],
  ["picris", "<big>picris</big>\n\n<b>picris, idis</b> <i>f</i> (<i>греч.</i>)\nгорький латук <i>PM</i>."],
  ["pictac-", "<big>pictac-</big>\n\n<b>pictac-</b> <i>v. l.</i>\n= pittac-."],
  ["Pictava", "<big>Pictava</big>\n\n<b>Pictāva, ae</b> <i>f</i>\nПиктава, <i>главн. город пиктавов</i> (<i>ныне</i> Poitiers) <i>Eccl</i>."],
  ["Pictavi", "<big>Pictavi</big>\n\n<b>Pictāvī, ōrum</b> <i>m</i>\nпиктавы, <i>народность в Аквитанской Галлии</i> (<i>впоследствии провинция</i> Poitou) <i>Amm</i>."],
  ["Pictavicus", "<big>Pictavicus</big>\n\n<b>Pictāvicus, a, um</b> [Pictavi]\nпиктавский <i>Aus</i>."],
  ["Picti", "<big>Picti</big>\n\n<b>Picti, orum</b> <i>m</i>\n(<i>т. е.</i> крашенные, татуированные) пикты, <i>народность в Каледонии</i> <i>Amm, Cld</i>."],
  ["pictilis", "<big>pictilis</big>\n\n<b>pictilis, e</b> [pictus]\nвышитый, расшитый (balteus <i>Ap</i>)."],
  ["Pictones", "<big>Pictones</big>\n\n<b>Pictones, um</b> <i>m</i> <i>Cs, Lcn</i>\n= Pictavi."],
  ["Pictonicus", "<big>Pictonicus</big>\n\n<b>Pictonicus</b> <i>Aus</i>\n= Pictavicus."],
  ["pictor", "<big>pictor</big>\n\n<b>I pictor, oris</b> <i>m</i> [pingo]\nживописец <i>C, PM</i>.\n\n<b>II Pictor, oris</b> <i>m</i>\nПиктор, <i>cognomen в gens Fabia</i> <i>C</i>."],
  ["pictorius", "<big>pictorius</big>\n\n<b>pictorius, a, um</b> [pictor I]\nпринадлежащий <i>или</i> свойственный живописцам (operae <i>Dig</i>; licentia <i>Dig</i>)."],
  ["pictura", "<big>pictura</big>\n\n<b>pictura, ae</b> <i>f</i> [pingo]\n<b>1)</b> живопись (ars picturae <i>C</i>); рисование (p. linearis <i>PM</i>); раскрашивание (parietum <i>PM</i>);\n<b>2)</b> картина (oculi pictura tenentur <i>C</i>); мозаичная картина (picturae decus <i>V</i>): p. textilis <i>Lcr, C</i> шитьё, вышивка;\n<b>3)</b> описание яркими красками, художественное изображение (p. Homeri <i>C</i>)."],
  ["picturatus", "<big>picturatus</big>\n\n<b>picturatus, a, um</b> [pictura]\n<b>1)</b> расписной, пёстрый, разноцветный (p. floribus agger <i>St</i>; volucres <i>Cld</i>);\n<b>2)</b> расшитый, вышитый (vestes <i>V</i>)."],
  ["pictus", "<big>pictus</big>\n\n<b>pictus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> pingo;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> разукрашенный, красивый, изящный (genus orationis <i>C</i>); пёстро расшитый, вышитый <i>или</i> расписной (toga <i>L</i>); раскрашенный <i>или</i> татуированный (Agathyrsi <i>V</i>); одетый в пёструю одежду (reges <i>M</i>); пёстрый, разноцветный (volucres <i>V</i>);\n<b>2)</b> пятнистый (pelies <i>V</i>; panthera <i>O</i>);\n<b>3)</b> беспредметный, пустой (metus <i>Prp</i>)."],
  ["picula", "<big>picula</big>\n\n<b>picula, ae</b> <i>f</i> [<i>demin. к</i> pix]\nнемножко смолы <i>Ap</i>."],
  ["Picumnus", "<big>Picumnus</big>\n\n<b>Picumnus</b> — <i>см.</i>\nPilumnus."],
  ["picus", "<big>picus</big>\n\n<b>I picus, i</b> <i>m</i>\n<b>1)</b> дятел <i>Pl, O, PM</i>;\n<b>2)</b> <i>миф.</i> гриф (<i>птица</i>) <i>Pl</i>.\n\n<b>II Picus, i</b> <i>m</i>\nПик, <i>сын Сатурна, отец Фавна, дед Латина, бог полей и лесов, первый царь Латия; был превращен Киркой в дятла за то, что отверг её любовь</i> <i>V, O</i>."],
  ["pie", "<big>pie</big>\n\n<b>pie</b> [pius]\n<b>1)</b> набожно, благочестиво, благоговейно (deos colere <i>C</i>);\n<b>2)</b> по чистой совести, как требует долг (vixisse <i>Ap</i>; ferre aliquid <i>Sen</i>);\n<b>3)</b> чистосердечно, искренно, глубоко (lugere aliquem <i>C</i>)."],
  ["Pieria", "<big>Pieria</big>\n\n<b>I Pieria, ae</b> <i>f</i>\nПиэрия :\n<b>1)</b> <i>область в юго-зап. Македонии, к сев. от Олимпа, излюбленное местопребывание Муз</i> <i>L, Mela, PM</i>;\n<b>2)</b> <i>область в Сирии, между Киликией и Финикией, с горой того же названия и главн. городом Seleucia Pieria</i> <i>C, PM</i>.\n\n<b>II Pieria, ae</b> <i>f</i> <i>C</i>\n= Pieris I."],
  ["Piericus", "<big>Piericus</big>\n\n<b>Piericus, a, um</b>\nпиэрийский (pix <i>PM</i>)."],
  ["Pieris", "<big>Pieris</big>\n\n<b>I Pieris, idis</b> <i>f</i>\nПиэрида, <i>т. е.</i> Муза <i>Lcr, C, H</i>.\n\n<b>II Pieris, idis</b> <i>f</i>\nПиэрида, <i>т. е.</i> дочь царя Пиэра <i>H, O</i>."],
  ["Pierius", "<big>Pierius</big>\n\n<b>Pierius, a, um</b> [Pieria I, 1]\nпиэрийский, <i>поэт.</i> посвященный Музам (quercus <i>Prp</i>); мусический, поэтический (via <i>O</i>); Pieria tuba <i>M</i> = героическая поэма; Pieria corona <i>M</i> = лавр; P. grex <i>M</i> = Музы и их поклонники (поэты); Pierium pectus <i>Pt</i> = поэтическое вдохновение; P. dies <i>St</i> день, посвященный искусствам."],
  ["Pierus", "<big>Pierus</big>\n\n<b>Pierus (-os), i</b> <i>m</i>\nПиэр:\n<b>1)</b> <i>царь Пеллы (Македония), девять дочерей которого, побеждённые в состязании с Музами, были превращены в сорок</i> <i>O</i>;\n<b>2)</b> <i>македонец, отец девяти Муз</i> (<i>или положивший начало их почитанию</i>) <i>C</i>."],
  ["pietas", "<big>pietas</big>\n\n<b>pietas, atis</b> <i>f</i> [pius]\n<b>1)</b> набожность, благочестие (adversus deos <i>C</i>);\n<b>2)</b> любовь, нежность, преданность (erga patriam aut parentes <i>C</i>; in parentes <i>C</i>; in matrem <i>C</i>);\n<b>3)</b> справедливость, правосудие (caeli <i>V</i>; si qua deis p. <i>Sil</i>);\n<b>4)</b> добросердечие, доброта, милосердие, сострадание (p., non atrocitas <i>Dig</i>)."],
  ["pietaticultrix", "<big>pietaticultrix</big>\n\n<b>pietaticultrix, icis</b> <i>f</i> [pietas + colo II]\nполная материнской любви (ciconia <i>Pt</i>)."],
  ["piger", "<big>piger</big>\n\n<b>piger, gra, grum</b> [<i>одного корня с</i> piget]\n<b>1)</b> медленный, медлительный (elephanti pigra moles <i>Cld</i>); вялый, ленивый (in aliqua re <i>C</i>, alicujus rei <i>H</i> <i>или</i> ad aliquid <i>C, L etc.</i>); затяжной (bellum <i>H</i>); долго тянущийся (annus <i>H</i>); медленно действующий (remedium <i>Col</i>); медленно текущий (mare <i>T</i>); стоячий (palus <i>O</i>);\n<b>2)</b> (<i>тж.</i> frumento p. <i>Ap</i>) малохлебородный, бесплодный (campus <i>H</i>; ager <i>Ap</i>);\n<b>3)</b> бесчувственный (pectora <i>O</i>);\n<b>4)</b> притупляющий, приводящий в оцепенение (frigus <i>Tib</i>; sopor <i>Ctl</i>);\n<b>5)</b> мрачный, угрюмый, печальный (vultus <i>M</i>)."],
  ["piget", "<big>piget</big>\n\n<b>piget, guit (gitum est), ere</b> <i>преим.</i> <i>impers.</i>\n<b>1)</b> досадно, неприятно, стыдно: verba pigenda <i>Prp</i> речи, вызывающие неудовольствие; me (<i>acc.</i>) piget stultitiae meae (<i>gen.</i>) <i>C</i> мне досадно (стыдно за) моё неразумие; fateri pigebat <i>L</i> стыдно было признаться; nec me meminisse pigebit Elissae <i>V</i> и мне будет отрадно вспомнить Элиссу; non pigebit (<i>sc.</i> me) aliquid fabulari <i>Ap</i> я не прочь кое-что порассказать;\n<b>2)</b> овладевает чувство раскаяния: pigere eum facti coepit <i>Just</i> он стал раскаиваться в том, что сделал."],
  ["pigmentarius", "<big>pigmentarius</big>\n\n<b>pigmentarius, i</b> <i>m</i> [pigmentum] (<i>тж.</i> p. institor <i>Scr</i>)\nторговец красками и мазями <i>C, Dig</i>."],
  ["pigmentatus", "<big>pigmentatus</big>\n\n<b>pigmentatus, a, um</b>\nкрашеный (crinis <i>Eccl</i>)."],
  ["pigmentum", "<big>pigmentum</big>\n\n<b>pigmentum, i</b> <i>n</i> [pingo]\n<b>1)</b> краска, красящее вещество (pigmenta in tabula <i>C</i>): pingere aliquem pigmentis ulmeis <i>ирон. Pl</i> размалевать (<i>т. е.</i> высечь) кого-л. вязовыми прутьями;\n<b>2)</b> снадобье <i>Ap, CA, Vlg</i>: odora pigmenta <i>Macr</i> благовония;\n<b>3)</b> <i>pl.</i> риторические прикрасы (sententiae verae sine pigmentis <i>C</i>)."],
  ["pigneraticius", "<big>pigneraticius</big>\n\n<b>pigneraticius, a, um</b> [pignero]\n<b>1)</b> отдаваемый в залог (fundus <i>Dig</i>);\n<b>2)</b> принимающий в залог (creditor <i>Dig</i>);\n<b>3)</b> касающийся залога (actio <i>Dig, CJ</i>)."],
  ["pigneratio", "<big>pigneratio</big>\n\n<b>pigneratio, onis</b> <i>f</i>\nотдача в залог, ипотечная операция <i>CJ</i>."],
  ["pignerator", "<big>pignerator</big>\n\n<b>pignerator, oris</b> <i>m</i> [pigneror]\nберущий в залог, залогодержатель <i>C</i>."],
  ["pignero", "<big>pignero</big>\n\n<b>pignero, avi, atum, are</b> [pignus]\n<b>1)</b> отдавать в залог, закладывать (laenam <i>J</i>; praedium pigneratum <i>Dig</i>): bona pigneranda poenae praebere <i>L</i> оставить своё имущество в виде замены наказания;\n<b>2)</b> брать в виде залога, принимать в залог (pigneratos habere animos <i>L</i>): p. se cenae alicujus <i>Ap</i> обещать отобедать у кого-л."],
  ["pigneror", "<big>pigneror</big>\n\n<b>pigneror, atus sum, ari</b> <i>depon.</i> [pignus]\n<b>1)</b> брать в залог, <i>перен.</i> принимать: p. aliquid omen <i>O</i> принять что-л. в качестве предзнаменования;\n<b>2)</b> присваивать, обращать в свою собственность (Mars fortissimum quemque p. solet <i>C</i>): fidem alicujus praemio p. <i>Su</i> приобрести чью-л. верность за деньги. — <i>См. тж.</i> pignero."],
  ["pignor-", "<big>pignor-</big>\n\n<b>pignor-</b> <i>v. l.</i>\n= pigner-."],
  ["pignoriscapio", "<big>pignoriscapio</big>\n\n<b>pignoriscapio, onis</b> <i>f</i> [pignus + capio]\nвзятие в залог <i>Cato ap. AG</i>."],
  ["pignus", "<big>pignus</big>\n\n<b>pignus, oris</b> <i>и</i> <b>eris</b> <i>n</i>\n<b>1)</b> залог: pignori dare <i>Dig</i> (opponere <i>Pl, Ter</i>) отдать в залог; pignori accipere <i>T</i> взять в залог; pignora capere <i>L</i> (auferre <i>C</i>) <i>или</i> pignoribus cogere senatores <i>C</i> путём взимания залогов обеспечить явку сенаторов на заседание;\n<b>2)</b> залог, порука, гарантия, ручательство, несомненный признак (gratiae <i>QC</i>; voluntatis, injuriae <i>C</i>): p. sanguinis <i>Pt</i> кровная близость;\n<b>3)</b> заложник (pacis <i>L</i>);\n<b>4)</b> <i>pl.</i> (<i>тж.</i> pignera conjugum ac liberorum <i>L</i>) залог(и) любви, привязанности, <i>т. е.</i> дети, родители, близкие <i>Q, PJ etc.</i>: gemina nati pignora <i>Pt</i> оба сына (Лаокоонта) || <i>перен.</i> прививок, привой (pignora quae morus alit <i>Pall</i>);\n<b>5)</b> пари, заклад: p. ponere <i>O</i> <i>или</i> pignore contendere <i>Ctl</i> (certare <i>V</i>) биться об заклад, держать пари; da p. <i>Ter</i> побьёмся об заклад, <i>но</i>: pignora da <i>O</i> докажи на деле."],
  ["pigre", "<big>pigre</big>\n\n<b>pigre</b> [piger]\nвяло, неохотно, лениво, медлительно (cunctanter ac p. <i>PJ</i>; p. ac segniter agere <i>Col</i>)."],
  ["pigredo", "<big>pigredo</big>\n\n<b>pigredo, inis</b> <i>f</i> [piger]\nлень, бездеятельность (p. immittit soporem <i>Vlg</i>)."],
  ["pigreo", "<big>pigreo</big>\n\n<b>pigreo, —, —, ere</b>\nбыть вялым, медлительным <i>Acc, Enn</i>."],
  ["pigresco", "<big>pigresco</big>\n\n<b>pigresco, —, —, ere</b>\nстановиться медлительным, замедлять течение (Nilus pigrescit <i>PM</i>); застаиваться (aera non sinere p. <i>Sen</i>)."],
  ["pigritia", "<big>pigritia</big>\n\n<b>pigritia, ae</b> <i>f</i> [piger]\n<b>1)</b> вялость, неохота, лень (alicujus rei <i>C, L, Pt</i>; p. aut ignavia <i>C</i>);\n<b>2)</b> слабость (stomachi <i>Sen</i>);\n<b>3)</b> досуг (jus ingenuae pigritiae dare alicui <i>M</i>)."],
  ["pigrities", "<big>pigrities</big>\n\n<b>pigrities, ei</b> <i>f</i> <i>L</i>\n= pigritia."],
  ["pigritor", "<big>pigritor</big>\n\n<b>pigritor, atus sum, ari</b> [<i>intens. к</i> pigror]\nлениться (ne pigriteris venire usque ad nos <i>Vlg</i>)."],
  ["pigro", "<big>pigro</big>\n\n<b>pigro, avi, —, are</b> <i>Acc</i>\n= pigreo."],
  ["pigror", "<big>pigror</big>\n\n<b>I pigror, —, ari</b> <i>depon.</i> [piger]\nбыть вялым, медлить, лениться <i>Lcr, C</i>.\n\n<b>II pigror, oris</b> <i>m</i> <i>LM</i>\n= pigritia."],
  ["pila", "<big>pila</big>\n\n<b>I pila, ae</b> <i>f</i> [<i>из</i> *pisula <i>от</i> piso]\n<b>1)</b> ступка (p., ubi triticum pinsant <i>Cato</i>);\n<b>2)</b> корыто (p. fullonica <i>Cato</i>).\n\n<b>II pila, ae</b> <i>f</i> [<i>предпол. из</i> pigula <i>от</i> pango]\n<b>1)</b> столб, свая (locare pilas pontis in Tiberim <i>L</i>); столб у книжных лавок (для объявлений о поступивших в продажу изданиях) (nulla meos habeat p. libellos <i>H</i>);\n<b>2)</b> каменная плотина, мол (p. saxea <i>V</i>; p. impingitur ponto <i>Sil</i>).\n\n<b>III pila, ae</b> <i>f</i>\n<b>1)</b> мяч (ludere pila <i>C</i>): claudus pilam (<i>sc.</i> ludebat) <i>погов. C</i> хромой с мячом (<i>о попытке, заранее обречённой на неудачу</i>); mea p. est <i>погов. Pl</i> я выиграл (победа за мной); terrae p. <i>Vr</i> земной шар || шарообразный плод репы (pilae Nursinae <i>M</i>) <i>или</i> кусок мыла (pilae Mattiacae <i>M</i>);\n<b>2)</b> клубок (lanuginis <i>PM</i>);\n<b>3)</b> шар для судейского голосования <i>Prp</i>;\n<b>4)</b> человеческое чучело, которым дразнили быков <i>M</i>.\n\n<b>IV pila</b> <i>pl. к</i>\npilum."],
  ["pilanus", "<big>pilanus</big>\n\n<b>pilanus, i</b> <i>m</i> [pilum II] <i>Vr, O</i>\n= triarius."],
  ["pilaris", "<big>pilaris</big>\n\n<b>pilaris, e</b> [pila III]\nотносящийся к мячу: lusio p. <i>St</i> игра в мяч."],
  ["pilarius", "<big>pilarius</big>\n\n<b>pilarius, i</b> <i>m</i> [pila III]\nжонглёр <i>Q</i>."],
  ["pilatim", "<big>pilatim</big>\n\n<b>pilatim</b> [pila II]\nс колоннами (aedificia agere <i>Vtr</i>)."],
  ["pilatus", "<big>pilatus</big>\n\n<b>pilatus, a, um</b> [pilum II]\nвооружённый дротиками (agmina <i>V</i>; cohors <i>M</i>)."],
  ["pileatus", "<big>pileatus</big>\n\n<b>pileatus, a, um</b> [pileus 1]\nносящий головной убор pileus (Parthi <i>M</i>): pileati fratres <i>Ctl</i> = Castor et Pollux; coloni pileati <i>L</i> вольноотпущенные крестьяне (<i>при даровании рабу воли ему давался</i> pileus)."],
  ["pilentum", "<big>pilentum</big>\n\n<b>pilentum, i</b> <i>n</i>\nпарадный экипаж (<i>преим. знатных римск. женщин</i>) <i>L, V, H</i>."],
  ["pileo", "<big>pileo</big>\n\n<b>pileo, —, —, are</b> [pileus 1]\nнадевать pileus (<i>символ дарования свободы рабу</i>) (p. aliquem <i>Dig</i>)."],
  ["pileolum", "<big>pileolum</big>\n\n<b>pileolum, i</b> <i>n</i> <i>Hier</i>\n= pileolus."],
  ["pileolus", "<big>pileolus</big>\n\n<b>pileolus, i</b> <i>m</i> [<i>demin. к</i> pileus 1]\nкруглая шапочка <i>H, Col</i>."],
  ["pileus", "<big>pileus</big>\n\n<b>pileus, i</b> <i>m</i> <i>и</i> <b>pileum, i</b> <i>n</i>\n<b>1)</b> войлочная круглая шапка (<i>рабы могли надевать её только при их продаже и при даровании им воли</i>): pileum redimere <i>M</i> получить свободу; servos ad pileum vocare <i>L</i> призывать рабов к восстанию, обещая им свободу;\n<b>2)</b> защитник, оплот (pileum meum <i>Pl</i>);\n<b>3)</b> <i>анат.</i> сорочка (<i>оболочка на голове новорождённого</i>) <i>Lampr</i>."],
  ["pilicrepus", "<big>pilicrepus</big>\n\n<b>pilicrepus, a, um</b> [pila III + crepo]\nиграющий в мяч <i>Sen</i>."],
  ["pille-", "<big>pille-</big>\n\n<b>pille-</b> <i>v. l.</i>\n= pile-."],
  ["pilo", "<big>pilo</big>\n\n<b>I pilo, avi, —, are</b> [pilus I]\n<b>1)</b> удалять волосы (p. aliquem, aliquid <i>M</i>; resina, qua pilantur uxores <i>M</i>);\n<b>2)</b> ощипывать: malam parram p. <i>погов. Pt</i> жить в крайней нужде;\n<b>3)</b> <i>перен.</i> разносить, пробирать (singulos in curia <i>Pt</i>).\n\n<b>II pilo, —, atum, are</b> [<i>одного корня с</i> expilo]\nограблять (villam <i>Amm</i>)."],
  ["pilosus", "<big>pilosus</big>\n\n<b>I pilosus, a, um</b> [pilus I]\n<b>1)</b> покрытый волосами, волосистый (ovis venter <i>Vr</i>; genae <i>C</i>); покрытый пушком (folia <i>PM</i>).\n\n<b>II pilosus, i</b> <i>m</i> <i>предпол.</i>\nкосматое чудовище <i>Vlg</i>."],
  ["pilpito", "<big>pilpito</big>\n\n<b>pilpito, —, —, are</b>\nпищать (<i>о мышах</i>) <i>Su</i>."],
  ["pilula", "<big>pilula</big>\n\n<b>pilula, ae</b> <i>f</i> [<i>demin. к</i> pila III]\nшарик (p. nuci non absimilis <i>PM</i>); детский мячик <i>Aug</i>; <i>мед.</i> пилюля <i>PM</i>."],
  ["pilum", "<big>pilum</big>\n\n<b>I pilum, i</b> <i>n</i> [<i>одного корня с</i> pinso]\nпестик <i>Pl, Cato, PM</i>.\n\n<b>II pilum, i</b> <i>n</i>\nметательное копьё, дротик <i>C, L, Sen etc.</i>: p. murale <i>Cs</i> тяжёлое копьё (<i>бросавшееся с помощью метательных машин при осаде</i>); circumstantia pila <i>Cld</i> вооружённая охрана; p. injicere alicui <i>погов. Pl</i> напасть на кого-л."],
  ["Pilumnus", "<big>Pilumnus</big>\n\n<b>Pilumnus</b> <i>и</i> <b>Picumnus, i</b> <i>m</i>\nПилумн <i>и</i> Пикумн:\n<b>1)</b> <i>древнеримск. божества, покровители новорождённых детей</i> <i>Vr ap. Aug</i>;\n<b>2)</b> (Pilumnus) <i>предок царя Турна</i> <i>V</i>."],
  ["pilus", "<big>pilus</big>\n\n<b>I pilus, i</b> <i>m</i>\n<b>1)</b> волос (pili caudae equinae <i>H</i>): ne pilo minus <i>C</i> ни на волос не меньше; ne pilum quidem accipere <i>C</i> ничего не получить; pili non facere aliquid <i>Ctl, Pt</i> ни во что не ставить что-л.;\n<b>2)</b> волосяной покров, волосы (p. cadit alicui <i>PM</i>): crebro pilo <i>Vr</i> с густыми волосами.\n\n<b>II pilus, i</b> <i>m</i> [pilum II]\n<b>1)</b> манипул триариев в римском легионе (<i>см.</i> triarius): primum pilum ducere <i>Cs</i> быть первым центурионом первого манипула триариев;\n<b>2)</b> центурион триариев: primus p. <i>L</i> <i>или</i> primipilus <i>Cs, VM</i> (= centurio primi pili) первый центурион первого манипула триариев (<i>старший из всех центурионов легиона</i>)."],
  ["Pimpla", "<big>Pimpla</big>\n\n<b>Pimpla, ae</b> <i>f</i>\nПимпла, <i>город и источник на Олимпе (южн. Македония), где, по преданию, жили Музы и Орфей (см.</i> Pimplea <i>и</i> Pimpleus)."],
  ["Pimplea", "<big>Pimplea</big>\n\n<b>Pimplea, ae</b> <i>f</i> <i>и</i> <b>Pimpleis, idis</b> <i>и</i> <b>idos</b> <i>f</i>\n<b>1)</b> Муза <i>H, Vr, M</i>;\n<b>2)</b> источник Pimpla <i>St, Aus</i>."],
  ["Pimpleus", "<big>Pimpleus</big>\n\n<b>Pimpleus, a, um</b> [Pimpla]\nпимплейский, <i>поэт.</i> посвященный Музам (mons <i>Ctl</i>; antrum <i>M</i>)."],
  ["Pimplias", "<big>Pimplias</big>\n\n<b>Pimplias, adis</b> <i>f</i> [Pimplea] <i>Sid</i>\n= Musa."],
  ["pina", "<big>pina</big>\n\n<b>pina, ae</b> <i>f</i> (<i>греч.</i>)\nморское перо, <i>род морского моллюска</i> <i>C, PM</i>."],
  ["pinacotheca", "<big>pinacotheca</big>\n\n<b>pinacotheca, ae</b> <i>f</i> (<i>греч.</i>)\nсобрание картин, картинная галерея <i>Vr, PM, Vtr, Pt</i>."],
  ["pinacothece", "<big>pinacothece</big>\n\n<b>pinacothece, es</b> <i>f</i> <i>Vr</i>\n= pinacotheca."],
  ["pinalis", "<big>pinalis</big>\n\n<b>pinalis, e</b> [pinus]\nсосновый (resina <i>Is</i>)."],
  ["Pinarius", "<big>Pinarius</big>\n\n<b>Pinarius, a, um</b>\nПинарий, <i>римск. nomen</i> (<i>на родах Пинариев и Потитиев лежали обязанности жрецов Геркулеса у Ara maxima</i>) <i>L, V, Macr</i>."],
  ["Pinarus", "<big>Pinarus</big>\n\n<b>Pinarus, i</b> <i>m</i>\nПинар, <i>река в Киликии</i> <i>PM</i>."],
  ["pinastellus", "<big>pinastellus</big>\n\n<b>pinastellus, i</b> <i>m</i> <i>Ap</i>\n= peucedanum."],
  ["pinaster", "<big>pinaster</big>\n\n<b>pinaster, stri</b> <i>m</i> (= pinus silvestris)\nлесная сосна <i>PM</i>."],
  ["pincerna", "<big>pincerna</big>\n\n<b>pincerna, ae</b> <i>m</i>\nвиночерпий <i>Lampr, Hier</i>."],
  ["Pindaricus", "<big>Pindaricus</big>\n\n<b>Pindaricus, a, um</b> [Pindarus]\nпиндаров (Camenae <i>H</i>; lyra <i>M</i>): metrum Pindaricum = versus trimeter brachycatalectus <i>или</i> пятистопный анапестический стих."],
  ["Pindarus", "<big>Pindarus</big>\n\n<b>Pindarus, i</b> <i>m</i>\nПиндар, <i>родом из Фив, знаменитый лирический поэт древней Греции</i> (<i>522-442 гг. до н. э.</i>) <i>C, H, Sen, Q</i>."],
  ["Pindenissitae", "<big>Pindenissitae</big>\n\n<b>Pindenissitae, arum</b> <i>m</i>\nжители города Pindenissus <i>C</i>."],
  ["Pindenissum", "<big>Pindenissum</big>\n\n<b>Pindenissum, i</b> <i>n</i> <i>C</i>\n= Pindenissus."],
  ["Pindenissus", "<big>Pindenissus</big>\n\n<b>Pindenissus, i</b> <i>f</i>\nПинденисс, <i>город в Киликии на горе Аман</i> <i>C</i>."],
  ["Pindus", "<big>Pindus</big>\n\n<b>Pindus (-os), i</b> <i>m</i>\nПинд, <i>горная цепь между Фессалией и Эпиром, считавшаяся местопребыванием Муз</i> <i>V, O, PM</i>."],
  ["pinea", "<big>pinea</big>\n\n<b>pinea, ae</b> <i>f</i>\nсосновая шишка <i>Col, Lampr</i>."],
  ["pinetum", "<big>pinetum</big>\n\n<b>pinetum, i</b> <i>n</i> [pinus]\nсосновый лес, бор <i>O, PM</i>."],
  ["pineum", "<big>pineum</big>\n\n<b>pineum, i</b> <i>n</i> <i>Vlg</i>\n= pinus."],
  ["pineus", "<big>pineus</big>\n\n<b>pineus, a, um</b> [pinus]\nсосновый (silva <i>V, Sil</i>): pinea nux <i>Cato, PM</i> = pinea; p. ardor <i>V</i> жар соснового костра; pinea moles <i>Prp</i>, texta <i>O</i> <i>или</i> compages <i>M</i> = navis."],
  ["pingo", "<big>pingo</big>\n\n<b>pingo, pinxi, pictum, ere</b>\n<b>1)</b> писать красками, рисовать (tabulam <i>C</i>; Alexandrum <i>C</i>): tabula picta <i>C</i> картина;\n<b>2)</b> раскрашивать, расписывать, натирать (frontem moris <i>V</i>);\n<b>3)</b> расшивать, вышивать (togam acu <i>O</i>); picti tori <i>T</i> ложа с вышитыми покрывалами;\n<b>4)</b> украшать, приукрашивать (stellae quibus pingitur aether <i>SenT</i>; aliquem omnibus artis coloribus <i>Ap</i>);\n<b>5)</b> изображать: p. aliquid secundum pedes statuae alicujus <i>Pt</i> изобразить что-л. у подножия чьей-л. статуи || описывать (Britanniam <i>C</i>; fera proelia <i>O</i>). — <i>См. тж.</i> pictus."],
  ["pinguamen", "<big>pinguamen</big>\n\n<b>pinguāmen, inis</b> <i>n</i>\nжир, тучность <i>Eccl</i>."],
  ["pingue", "<big>pingue</big>\n\n<b>pingue, is</b> <i>n</i>\nжир, нутряное сало <i>V, PM</i>."],
  ["pinguedo", "<big>pinguedo</big>\n\n<b>pinguēdo, inis</b> <i>f</i>\n<b>1)</b> жирность, тучность <i>PM, Pall, Aug</i>;\n<b>2)</b> жир, сало <i>Col</i>."],
  ["pinguefacio", "<big>pinguefacio</big>\n\n<b>pinguefacio, fēcī, factum, ere</b> [pinguis + facio]\nделать жирным, утучнять <i>PM, Tert</i>."],
  ["pinguesco", "<big>pinguesco</big>\n\n<b>pinguēsco, —, —, ere</b> [pinguis]\n<b>1)</b> жиреть (pinguescit armentum <i>Col</i>); наливаться (frumentum pinguescit <i>PM</i>); утучняться (Aegyptus pinguescit, <i>sc.</i> aquis <i>PJ</i>); становиться маслянистым <i>или</i> густеть (vinum vetustate pinguescit <i>PM</i>);\n<b>2)</b> усиливаться, разгораться (flammae pinguescunt <i>Sil</i>)."],
  ["pinguetudo", "<big>pinguetudo</big>\n\n<b>pinguetūdo, inis</b> <i>f</i> <i>v. l.</i>\n= pinguitudo."],
  ["pinguiarius", "<big>pinguiarius</big>\n\n<b>pinguiārius, a, um</b> [pinguis] <i>шутл.</i>\nлюбящий жирное <i>M</i>."],
  ["pinguiculus", "<big>pinguiculus</big>\n\n<b>pinguiculus, a, um</b> [<i>demin. к</i> pinguis]\nдовольно крупный (plantae <i>Fronto</i>)."],
  ["pinguis", "<big>pinguis</big>\n\n<b>pinguis, e</b>\n<b>1)</b> жирный, толстый, упитанный (homo <i>C, H</i>; ovis <i>H</i>); откормленный, нагулявший жир (aper ulvis et arundine p. <i>H</i>); тучный (campus <i>H</i>; arva <i>Sen</i>); маслянистый, жирный (olivum <i>O</i>); густой (vinum <i>Col</i>); сочный, мясистый (ficus <i>H</i>);\n<b>2)</b> пышный (hortus <i>V</i>);\n<b>3)</b> плотный (toga <i>Su</i>); толстый (folium <i>PM</i>); массивный (aurum <i>Pers</i>);\n<b>4)</b> туманный, влажный, сырой (caelum <i>C</i>);\n<b>5)</b> утучняющий (flumen <i>V</i>);\n<b>6)</b> обильно политый, напоённый (pecudum cruore pingue solum <i>V</i>);\n<b>7)</b> изобилующий мёдом (stabula apum <i>V</i>); медоносный (tilia <i>V</i>); залитый жиром жертвенных животных (ara <i>V</i>); обильный, богато уставленный яствами (mensa <i>Ctl</i>);\n<b>8)</b> напомаженный (pexus pinguisque <i>Q</i>): coma p. <i>M</i> смазанные жиром <i>или</i> <i>Su</i> густые (пышные) волосы;\n<b>9)</b> слабый, неострый, пресный (sapor <i>PM</i>);\n<b>10)</b> насыщенный, яркий (color <i>PM</i>);\n<b>11)</b> измазанный (luto <i>J</i>): p. virga <i>M</i> — птицеловный прут;\n<b>12)</b> полногласный, полнозвучный (sonus <i>Q</i>);\n<b>13)</b> вялый, грубый, неповоротливый (ingenium <i>O</i>): pingui Minerva aliquid facere <i>C</i> делать что-л. неумело, неловко;\n<b>14)</b> витиеватый, велеречивый (facundia, <i>sc.</i> Pindari <i>AG</i>);\n<b>15)</b> спокойный, безмятежный (somnus <i>O</i>; vita <i>PJ</i>)."],
  ["pinguiter", "<big>pinguiter</big>\n\n<b>pinguiter</b> [pinguis]\n<b>1)</b> жирно, тучно: solum p. densum <i>Col</i> почва тучная и плотная;\n<b>2)</b> обильно, щедро (succurrere alicui <i>Dig</i>);\n<b>3)</b> грубо, неточно (aliquid accipere <i>Dig</i>)."],
  ["pinguitia", "<big>pinguitia</big>\n\n<b>pinguitia, ae</b> <i>f</i> <i>и</i> <b>pinguitiēs, ēī</b> <i>f</i> [pinguis]\nжирность <i>Ap, Eccl</i>."],
  ["pinguitudo", "<big>pinguitudo</big>\n\n<b>pinguitūdo, inis</b> <i>f</i> [pinguis]\n<b>1)</b> жирность (olei <i>Col</i>); тучность (suis <i>Cato</i>);\n<b>2)</b> яркость (<i>sc.</i> coloris <i>PM</i>);\n<b>3)</b> грубость, тяжеловесность (<i>sc.</i> sonorum <i>Q</i>)."],
  ["pinguiusculus", "<big>pinguiusculus</big>\n\n<b>pinguiusculus, a, um</b> [<i>demin. к</i> pinguis]\nдовольно жирный (corpus <i>Sol</i>)."],
  ["pinifer", "<big>pinifer</big>\n\n<b>pinifer, fera, ferum</b> [pinus + fero]\nпоросший соснами (caput Atlantis <i>V</i>; Ida <i>St</i>)."],
  ["piniger", "<big>piniger</big>\n\n<b>piniger, gera, gerum</b> <i>O, VF, St</i>\n= pinifer."],
  ["pinna", "<big>pinna</big>\n\n<b>I pinna, ae</b> <i>f</i> <i>v. l.</i>\n= penna.\n\n<b>II pinna, ae</b> <i>f</i> <i>v. l.</i>\n= pina."],
  ["pinnaculum", "<big>pinnaculum</big>\n\n<b>pinnaculum, i</b> <i>n</i> [<i>demin. к</i> pinna I]\n<b>1)</b> крылышко <i>Vlg</i>;\n<b>2)</b> конёк кровли, щипец <i>Vlg, Tert</i>."],
  ["pinnatus", "<big>pinnatus</big>\n\n<b>pinnatus, a, um</b> [pinna I]\n<b>1)</b> оперённый, покрытый перьями (cauda, <i>sc.</i> aquilae <i>Poeta ap. C</i>);\n<b>2)</b> окрылённый, крылатый (Cupido <i>C</i>);\n<b>3)</b> перистый (folia abietis <i>PM</i>)."],
  ["pinniger", "<big>pinniger</big>\n\n<b>pinniger, gera, gerum</b> [pinna I + gero]\nоперённый, крылатый (Amor <i>Lcr</i>); имеющий плавники (piscis <i>O</i>)."],
  ["pinnipes", "<big>pinnipes</big>\n\n<b>pinnipes, pedis</b> <i>adj.</i> [pinna I + pes]\nс окрылёнными ногами (Perseus <i>Ctl</i>)."],
  ["pinnirapus", "<big>pinnirapus</big>\n\n<b>pinnirapus, i</b> <i>m</i> [pinna I + rapio]\nстарающийся схватить остриё (<i>или</i> перья) шлема и сорвать его, <i>т. е.</i> борющийся с самнитским гладиатором, носящим шлем с остриём <i>J</i>."],
  ["pinnula", "<big>pinnula</big>\n\n<b>pinnula, ae</b> <i>f</i> [<i>demin. к</i> pinna I]\n<b>1)</b> перышко <i>Col</i>;\n<b>2)</b> крылышко <i>Pl, C, Ap</i>;\n<b>3)</b> рыбий плавник <i>PM, Vlg</i>."],
  ["pinophylax", "<big>pinophylax</big>\n\n<b>pinophylax, acis</b> <i>m</i> (<i>греч.</i>) <i>PM</i>\n= pinoteres."],
  ["pinoteres", "<big>pinoteres</big>\n\n<b>pinoteres, ae</b> <i>m</i> (<i>греч.</i>)\nрод мелкого краба <i>C, PM</i>."],
  ["pinsatio", "<big>pinsatio</big>\n\n<b>pinsatio, onis</b> <i>f</i> [pinso]\nтолчение <i>Vtr</i>."],
  ["pinsito", "<big>pinsito</big>\n\n<b>pinsito, —, —, are</b> [<i>frequ. к</i> pinso II]\nтолочь (polentam <i>Pl</i>)."],
  ["pinso", "<big>pinso</big>\n\n<b>I pinso, pinsui (pinsi), pinsitum (pinsum, pistum), ere</b>\n<b>1)</b> толочь, растирать, молоть (triticum <i>Cato</i>);\n<b>2)</b> бичевать (aliquem flagro <i>Pl</i>): quem nulla ciconia pinsit <i>погов. Pers</i> которого ни одна насмешка не коснётся.\n\n<b>II pinso, —, (atum), are</b>\nтолочь, растирать (far in pistrino <i>Vr</i>)."],
  ["pinula", "<big>pinula</big>\n\n<b>pinula, ae</b> <i>f</i> <i>v. l.</i>\n= pinnula."],
  ["pinus", "<big>pinus</big>\n\n<b>pinus, us</b> <i>и</i> <b>i</b> <i>f</i>\n<b>1)</b> сосна <i>или</i> пихта <i>V, H, Prp, O etc.</i>;\n<b>2)</b> <i>изделие из соснового дерева</i>: корабль (p. <i>и</i> p. nautica <i>V</i>); факел (p. flagrans <i>V</i>); весло (mergere aequore pinus <i>Lcn</i>); копьё (pinu aliquem ferire <i>St</i>);\n<b>3)</b> венок из хвои (pinu praecinctus <i>O</i>; cingit comas p. <i>Sil</i>);\n<b>4)</b> сосновый лес: p. Gallinaria <i>J</i> Галлинарийский бор (<i>близ города Cumae</i>);\n<b>5)</b> <i>бот.</i> пиния <i>PM</i>."],
  ["pinxi", "<big>pinxi</big>\n\n<b>pinxi</b> <i>pf. к</i>\npingo."],
  ["pio", "<big>pio</big>\n\n<b>pio, avi, atum, are</b> [pius]\n<b>1)</b> умилостивлять (Silvanum lacte <i>H</i>); умиротворять (ossa <i>V</i>; busta <i>O</i>);\n<b>2)</b> чтить, воздавать почести (p. sacra <i>Prp</i>); соблюдать, свято хранить (pietatem <i>Pt</i>);\n<b>3)</b> искупать, заглаживать (damna <i>O</i>); карать, мстить (culpam morte <i>V</i>; nefas morte piandum <i>J</i>);\n<b>4)</b> отвращать (nefas triste <i>V</i>)."],
  ["pipatus", "<big>pipatus</big>\n\n<b>pipatus, us</b> <i>m</i> [pipo]\nписк (pullorum <i>Vr</i>)."],
  ["piper", "<big>piper</big>\n\n<b>piper, eris</b> <i>n</i>\nперец <i>CC, H, Ap, Pt</i>; <i>перен.</i> кипучая натура (p., non homo <i>Pt</i>)."],
  ["piperatum", "<big>piperatum</big>\n\n<b>piperatum, i</b> <i>n</i> [piperatus]\nперечный соус <i>CC</i>."],
  ["piperatus", "<big>piperatus</big>\n\n<b>piperatus, a, um</b> [piper]\n<b>1)</b> наперченный (garum <i>Pt</i>; vitellus <i>Pt</i>);\n<b>2)</b> едкий, язвительный (facundia <i>Sid</i>);\n<b>3)</b> вороватый (manus <i>M</i>)."],
  ["piperitis", "<big>piperitis</big>\n\n<b>piperitis, is</b> <i>и</i> <b>idis</b> <i>PM</i>\n= siliquastrum."],
  ["pipilo", "<big>pipilo</big>\n\n<b>pipilo, —, —, are</b>\nчирикать, щебетать <i>Ctl</i> (<i>v. l.</i>)."],
  ["pipinna", "<big>pipinna</big>\n\n<b>pipinna, ae</b> <i>f</i> <i>M</i>\n= exigua mentula."],
  ["pipio", "<big>pipio</big>\n\n<b>I pipio, —, —, are</b>\n<b>1)</b> хныкать (infantes pipiantes <i>Tert</i>): imbres p. <i>Tert</i> плакать навзрыд;\n<b>2)</b> <i>Ctl</i> (<i>v. l.</i>) = pipilo.\n\n<b>II pipio, onis</b> <i>m</i> [pipo]\nголубёнок, птенец, птенчик <i>Lampr</i>."],
  ["Pipl-", "<big>Pipl-</big>\n\n<b>Pipl-</b> <i>v. l.</i>\nPimpl-."],
  ["pipo", "<big>pipo</big>\n\n<b>pipo, —, —, are</b>\nиздавать писк <i>Vr</i>."],
  ["pipulum", "<big>pipulum</big>\n\n<b>pipulum, i</b> <i>n</i> <i>v. l.</i>\n= pipulus."],
  ["pipulus", "<big>pipulus</big>\n\n<b>pipulus, i</b> <i>m</i>\nшум, гам, брань <i>Pl, Fronto</i>."],
  ["piracium", "<big>piracium</big>\n\n<b>piracium, i</b> <i>n</i> [pirum]\nгрушевый сидр <i>Hier</i>."],
  ["Piraea", "<big>Piraea</big>\n\n<b>Piraea, orum</b> <i>n</i> <i>O</i>\n= Piraeus I (<i>так как Пирей состоял из трёх пристаней</i> — triplex Piraei portus <i>Nep</i>)."],
  ["Piraeus", "<big>Piraeus</big>\n\n<b>I Piraeus, i</b> <i>m</i> <i>и</i> <b>Piraeeus, ei</b> <i>m</i>\nПирей, <i>порт и город в Аттике, в устье реки Кефисса, на расстоянии ок. 7 км к юго-зап. от Афин</i> <i>Pl, Ter, C, Nep etc.</i>\n\n<b>II Piraeus, a, um</b> [Pireus I]\nпирейский (portus <i>Prp</i>; litora <i>O</i>)."],
  ["pirata", "<big>pirata</big>\n\n<b>pirata, ae</b> <i>m</i> (<i>греч.</i>; <i>лат.</i> praedo maritimus)\nморской разбойник, пират <i>V, C, Sen</i>."],
  ["piratica", "<big>piratica</big>\n\n<b>piratica, ae</b> <i>f</i> [pirata]\nпиратство, морской разбой (piraticam facere <i>C, Dig</i> <i>или</i> exercere <i>VM, Just</i>)."],
  ["piraticum", "<big>piraticum</big>\n\n<b>piraticum, i</b> <i>n</i> <i>Hier</i>\n= piracium."],
  ["piraticus", "<big>piraticus</big>\n\n<b>piraticus, a, um</b> [pirata]\nпиратский, разбойничий (navis <i>L</i>): bellum piraticum <i>Vr, C</i> война с пиратами."],
  ["Pirena", "<big>Pirena</big>\n\n<b>Pirena, ae</b> <i>и</i> <b>Pirene, es</b> <i>f</i>\nПирена, <i>источник на Коринфском акрополе, образовавшийся, по преданию, от удара копыта Пегаса и посвященный Музам</i> <i>Pl, Pers, PM, St</i>."],
  ["Pirenis", "<big>Pirenis</big>\n\n<b>Pirenis, idis</b> <i>f</i> [Pirene]\nпиренский (undae <i>O, SenT</i>): P. Ephyre <i>O</i> = Коринф."],
  ["piretrum", "<big>piretrum</big>\n\n<b>piretrum, i</b> <i>n</i> <i>v. l.</i>\n= pyrethrum (-on)."],
  ["Pirithous", "<big>Pirithous</big>\n\n<b>Pirithous, i</b> <i>m</i>\nПирифой, <i>сын Иксиона, царь лапифов, друг Тезея; на его свадьбе с Гипподамией произошло сражение между кентаврами и лапифами; когда умерла Гипподамия, Пирифой и Тезей спустились в подземное царство, чтобы похитить Прозерпину, но были схвачены а закованы в цепи</i> <i>O, H</i>."],
  ["pirum", "<big>pirum</big>\n\n<b>pirum, i</b> <i>n</i>\nгруша (<i>плод</i>) <i>Cato, Vr, Pl, V etc.</i>"],
  ["pirus", "<big>pirus</big>\n\n<b>pirus, i</b> <i>f</i> (<i>тж.</i> piri arbor <i>Pall</i>)\nгрушевое дерево <i>Cato, V, PM</i>."],
  ["Pirustae", "<big>Pirustae</big>\n\n<b>Pirustae, arum</b> <i>m</i>\nпирусты, <i>разбойничье племя в Иллирии</i> <i>Cs, L</i>."],
  ["pisa", "<big>pisa</big>\n\n<b>I pisa, ae</b> <i>f</i> <i>Pall</i>\n= pisum.\n\n<b>II pisa, ae</b> <i>f</i> [pinso]\nпест(ик) <i>Is</i>.\n\n<b>III Pisa, ae</b> <i>f</i>\nПиза, <i>город в Элиде на реке Алфей, близ которого происходили Олимпийские игры</i> <i>V, O, St</i>."],
  ["Pisae", "<big>Pisae</big>\n\n<b>Pisae, arum</b> <i>f</i>\nПизы, <i>город в сев.-зап. Этрурии на реке Арн, по преданию — колония Пизы Элидской</i> (<i>ныне</i> Pisa) <i>V, L</i>."],
  ["Pisaea", "<big>Pisaea</big>\n\n<b>Pisaea, ae</b> <i>f</i>\nуроженка города Pisa, <i>т. е.</i> Гипподамия <i>O</i>."],
  ["Pisaeus", "<big>Pisaeus</big>\n\n<b>Pisaeus, a, um</b> [Pisa]\nпизанский, <i>поэт.</i> элидский (Arethusa <i>O</i>): Pisaea hasta <i>O</i> = копьё Эномая (царя Элиды); P. annus <i>St</i> = год Олимпийских игр (<i>см.</i> Pisa); P. pollex <i>St</i> = рука Фидия (<i>изваявшая статую Юпитера Олимпийского</i>)."],
  ["Pisander", "<big>Pisander</big>\n\n<b>Pisander</b> <i>и</i> <b>Pisandrus (-os), dri</b> <i>m</i>\nПисандр:\n<b>1)</b> <i>сын Поликтора, один из претендентов на руку Пенелопы</i> <i>O</i>;\n<b>2)</b> <i>родом из Ахарн, один из 400 афинских олигархов во время Пелопоннесской войны</i> <i>Just</i>;\n<b>3)</b> <i>спартанский наварх (капитан корабля), павший при Книде (394 г. до н. э.</i>) <i>Nep</i>."],
  ["Pisanus", "<big>Pisanus</big>\n\n<b>I Pisanus, a, um</b> [Pisae]\nпизанский <i>L, Rut</i>.\n\n<b>II Pisanus, i</b> <i>m</i>\nжитель города Pisae <i>L</i>."],
  ["pisatio", "<big>pisatio</big>\n\n<b>pisatio, onis</b> <i>f</i> [piso II]\nутаптывание (<i>sc.</i> terrae <i>Sen</i> — <i>v. l.</i>)."],
  ["Pisaurensis", "<big>Pisaurensis</big>\n\n<b>Pisaurensis, e</b> [Pisaurum]\nпизаврский <i>C</i>."],
  ["Pisaurum", "<big>Pisaurum</big>\n\n<b>Pisaurum, i</b> <i>n</i>\nПизавр, <i>приморский город в Умбрии, в устье реки Pisaurus</i> (<i>ныне</i> Pesaro) <i>C, Cs</i>."],
  ["Pisaurus", "<big>Pisaurus</big>\n\n<b>Pisaurus, i</b> <i>m</i>\nПизавр, <i>река в Умбрии</i> <i>Ctl, M</i>."],
  ["piscaria", "<big>piscaria</big>\n\n<b>piscaria, ae</b> <i>f</i> [piscarius]\nрыбный рынок <i>Vr</i>."],
  ["piscarius", "<big>piscarius</big>\n\n<b>I piscarius, a, um</b> [piscis]\nрыбный (forum <i>Pl</i>); рыболовный (hamulus <i>Pl</i>).\n\n<b>II piscarius, i</b> <i>m</i>\nрыботорговец, рыбник <i>Vr</i>."],
  ["piscatio", "<big>piscatio</big>\n\n<b>piscatio, onis</b> <i>f</i> [piscor]\nрыбная ловля <i>Dig</i>."],
  ["piscator", "<big>piscator</big>\n\n<b>piscator, oris</b> <i>m</i> [piscor]\n<b>1)</b> рыбак, рыболов <i>C, VM, Sen, Pt, Ap</i>;\n<b>2)</b> рыботорговец <i>Pl, Ter</i>."],
  ["piscatorius", "<big>piscatorius</big>\n\n<b>piscatorius, a, um</b> [piscator]\nрыбачий, рыбацкий (navis <i>Cs</i>); рыбный (forum <i>L, Col</i>); рыболовный (arundo <i>PM</i>)."],
  ["piscatrix", "<big>piscatrix</big>\n\n<b>piscatrix, icis</b> <i>f</i>\nрыбачка <i>PM</i>."],
  ["piscatura", "<big>piscatura</big>\n\n<b>piscatura, ae</b> <i>f</i> <i>Tert</i>\n= piscatus."],
  ["piscatus", "<big>piscatus</big>\n\n<b>piscatus, us</b> <i>m</i> [piscor]\n<b>1)</b> рыболовство, рыбная ловля <i>Pl, C</i>;\n<b>2)</b> лов (testudinum <i>PM</i>);\n<b>3)</b> улов, барыш (p. bonus <i>Pl</i>);\n<b>4)</b> рыба, запас рыбы (parare piscatum sibi <i>Pl</i>)."],
  ["pisciculus", "<big>pisciculus</big>\n\n<b>pisciculus, i</b> <i>m</i> [<i>demin. к</i> piscis]\nрыбка <i>Ter, C, Pt, PM</i>."],
  ["piscina", "<big>piscina</big>\n\n<b>piscina, ae</b> <i>f</i> [piscis]\n<b>1)</b> пруд для рыбы, садок <i>Vr, C, Sen etc.</i>;\n<b>2)</b> бассейн для купанья (calida <i>PJ, Sen, Su</i>);\n<b>3)</b> водохранилище, цистерна (p. lignea <i>PM</i>);\n<b>4)</b> водопой <i>Col</i>."],
  ["piscinalis", "<big>piscinalis</big>\n\n<b>piscinalis, e</b> [piscina]\nкупальный <i>или</i> плавательный (cellae <i>Pall</i>)."],
  ["piscinarius", "<big>piscinarius</big>\n\n<b>piscinarius, i</b> <i>m</i> [piscina]\nлюбитель рыб, рыбовод-любитель <i>C</i>."],
  ["piscinensis", "<big>piscinensis</big>\n\n<b>piscinensis, is</b> <i>m</i> [piscina]\nкупальщик <i>LM</i>."],
  ["piscinilla", "<big>piscinilla</big>\n\n<b>piscinilla (piscinula), ae</b> <i>f</i> [<i>demin. к</i> piscina]\nмаленький рыбный пруд, садок <i>Vr</i>."],
  ["piscis", "<big>piscis</big>\n\n<b>piscis, is</b> <i>m</i>\nрыба <i>Pl, C etc.</i>: p. curvus <i>Sil</i> = delphinus; p. aquosus <i>V</i>, <i>тж.</i> <i>pl.</i> pisces <i>Col, O</i> <i>и</i> pisces gemini (gemelli) <i>O</i> созвездие Рыб."],
  ["piscor", "<big>piscor</big>\n\n<b>piscor, atus sum, ari</b> <i>depon.</i> [piscis]\nловить рыбу (hamo <i>Su</i>): p. in aere <i>погов. Pl</i> ловить рыбу в воздухе (= носить воду решетом)."],
  ["piscosus", "<big>piscosus</big>\n\n<b>piscosus, a, um</b> [piscis]\nизобилующий рыбой, рыбный (amnis <i>O</i>; scopuli <i>V</i>)."],
  ["pisculentum", "<big>pisculentum</big>\n\n<b>pisculentum, i</b> <i>n</i>\nлекарственное снадобье из рыб <i>Ap</i>."],
  ["pisculentus", "<big>pisculentus</big>\n\n<b>pisculentus, a, um</b> <i>Pl, Cato, Sol</i>\n= piscosus."],
  ["Pisidae", "<big>Pisidae</big>\n\n<b>Pisidae, arum</b> <i>m</i>\nписиды, <i>племя в Писидии</i> <i>C, Cld</i>."],
  ["Pisidia", "<big>Pisidia</big>\n\n<b>Pisidia, ae</b> <i>f</i>\nПисидия, <i>гористая область в юго-зап. части Малой Азии, к сев. от Памфилии</i> <i>L</i>."],
  ["Pisidicus", "<big>Pisidicus</big>\n\n<b>Pisidicus, a, um</b> [Pisidia]\nписидийский <i>PM</i>."],
  ["Pisistratidae", "<big>Pisistratidae</big>\n\n<b>Pisistratidae, arum</b> <i>m</i>\nПисистратиды, <i>сыновья Писистрата</i> (<i>см.</i> Pisistratus 1), <i>т. е.</i> Гиппий и Гиппарх <i>L</i>."],
  ["Pisistratus", "<big>Pisistratus</big>\n\n<b>Pisistratus, i</b> <i>m</i>\nПисистрат:\n<b>1)</b> <i>тиран афинский</i> (<i>560-527 гг. до н. э.</i>) <i>C, AG</i>;\n<b>2)</b> <i>беотийский предводитель, друг римлян</i> <i>L</i>."],
  ["Piso", "<big>Piso</big>\n\n<b>I Piso, onis</b> <i>m</i>\nПизон, <i>cognomen в роде Кальпурниев</i> <i>C, H</i>.\n\n<b>II piso, pisi, —, ere</b> <i>Cato, PM</i>\n= pinso I.\n\n<b>III piso, —, (atum), are</b> <i>v. l.</i>\n= pinso II."],
  ["Pisonianus", "<big>Pisonianus</big>\n\n<b>Pisonianus, a, um</b> [Piso]\nпизонов (coniuratio <i>Su</i>)."],
  ["pissinus", "<big>pissinus</big>\n\n<b>pissinus, a, um</b> (<i>греч.</i>)\nсмоляной, приготовленный из смолы (oleum <i>PM</i>)."],
  ["pissoceros", "<big>pissoceros</big>\n\n<b>pissoceros, i</b> <i>m</i> (<i>греч.</i>)\nсмесь смолы с воском <i>PM</i>."],
  ["pistacia", "<big>pistacia</big>\n\n<b>pistacia, ae</b> <i>f</i>\nфисташковое дерево <i>Pall</i>."],
  ["pistacium", "<big>pistacium</big>\n\n<b>pistacium, i</b> <i>n</i> (<i>греч.</i>)\nфисташка <i>PM, Pall</i>."],
  ["pistana", "<big>pistana</big>\n\n<b>pistana, ae</b> <i>f</i> <i>бот. предпол.</i>\nстрелолист (<i>Sagittaria sagittifolia</i>, L.) <i>PM</i>."],
  ["pistatio", "<big>pistatio</big>\n\n<b>pistatio, onis</b> <i>f</i> [pisto]\nтолчение, растирание <i>Vtr</i> (<i>v. l.</i>)."],
  ["pisticus", "<big>pisticus</big>\n\n<b>pisticus, a, um</b> (<i>греч.</i>)\nнастоящий, неподдельный (unguentum nardi <i>Vlg</i>)."],
  ["pistillum", "<big>pistillum</big>\n\n<b>pistillum, i</b> <i>n</i> [pinso]\nпестик <i>Pl, Col, PM</i>."],
  ["pistillus", "<big>pistillus</big>\n\n<b>pistillus, i</b> <i>m</i> <i>v. l.</i>\n= pistillum."],
  ["pisto", "<big>pisto</big>\n\n<b>pisto, —, atum, are</b> [<i>frequ. к</i> pinso]\nтолочь, растирать (aliquid <i>Veg, Ap</i>)."],
  ["pistor", "<big>pistor</big>\n\n<b>pistor, oris</b> <i>m</i> [pinso]\n<b>1)</b> мукомол (растирающий зёрна вручную), мельник <i>Pl, Vr, AG</i>;\n<b>2)</b> пекарь, хлебопёк, хлебник, <i>тж.</i> пирожник, кондитер <i>Vr, C, Sen M, Pt</i>."],
  ["pistoriensis", "<big>pistoriensis</big>\n\n<b>I pistor(i)ensis, e</b> [pistor]\nхлебопекарный <i>Pl</i> (<i>см.</i> Pistoriensis <i>H</i>).\n\n<b>II Pistor(i)ensis, e</b>\n<b>1)</b> [Pistorium] писторийский (ager <i>Sl</i>);\n<b>2)</b> [pistor] <i>шутл.</i> падкий до хлеба: milites pistor(i)enses <i>Pl</i> (<i>игра слов</i>) солдаты из Пистория, <i>а тж.</i> хлебопёки, булочники."],
  ["Pistorium", "<big>Pistorium</big>\n\n<b>Pistorium, i</b> <i>n</i>\nПисторий, <i>город в сев. Этрурии, у которого в 62 г. до н. э. Катилина потерпел поражение и погиб</i> (<i>ныне</i> Pistoia) <i>PM</i>."],
  ["pistorius", "<big>pistorius</big>\n\n<b>pistorius, a, um</b> [pistor]\nхлебопекарный, хлебный (opus <i>CC, PM</i> <i>и</i> opera <i>Pt, Dig</i>)."],
  ["pistriger", "<big>pistriger</big>\n\n<b>pistri–ger, gera, gerum</b> [pistrix I + gero]\nнесомый китом, <i>по др.</i>, с рыбьим хвостом (Triton <i>Sid</i>)."],
  ["pistrilla", "<big>pistrilla</big>\n\n<b>pistrilla, ae</b> <i>f</i> [<i>demin. к</i> pistrina]\nмаленькая ручная мельница <i>Ter</i>."],
  ["pistrina", "<big>pistrina</big>\n\n<b>pistrina, ae</b> <i>f</i> [pinso]\nмукомольня-хлебопекарня <i>LM, Vr, PM</i>."],
  ["pistrinalis", "<big>pistrinalis</big>\n\n<b>pistrinalis, e</b>\nмельничный: pecus p. <i>Col</i> свинья, откормленная отходами с мельницы."],
  ["pistrinarius", "<big>pistrinarius</big>\n\n<b>pistrinarius, i</b> <i>m</i>\nмельник-булочник <i>Dig</i>."],
  ["pistrinensis", "<big>pistrinensis</big>\n\n<b>pistrinensis, e</b> [pistrinum]\nмельничный, вращающий жернова (jumenta <i>Su</i>)."],
  ["pistrinum", "<big>pistrinum</big>\n\n<b>pistrinum, i</b> <i>n</i> [pinso]\n<b>1)</b> толчея, ручная мельница (in pistrino molere <i>Ter</i>): in p. dare <i>Ter</i> (tradere <i>Pl</i>, dedere <i>Ter</i>, submittere <i>Sen</i>) послать (в виде наказания) молоть зерно; in eodem pistrino vivere <i>погов. C</i> = тянуть одну и ту же лямку;\n<b>2)</b> пекарня <i>Su, Ap, Pt</i>: p. exercere <i>Su, Ap</i> заниматься мукомольем и хлебопечением."],
  ["pistris", "<big>pistris</big>\n\n<b>pistris, is</b> <i>f</i> <i>VF</i>\n= pistrix 1."],
  ["pistrix", "<big>pistrix</big>\n\n<b>I pistrix, icis</b> <i>f</i>\n<b>1)</b> морское чудовище (<i>кит, акула, пила-рыба</i>) <i>PM, VF, Fl</i>;\n<b>2)</b> небольшое быстроходное военное судно <i>L</i>;\n<b>3)</b> судно Энея с эмблемой кита <i>V</i>;\n<b>4)</b> созвездие Кита <i>C</i>.\n\n<b>II pistrix, icis</b> <i>f</i> [pistor]\nмельничиха-булочница <i>LM</i>."],
  ["pistura", "<big>pistura</big>\n\n<b>pistura, ae</b> <i>f</i> [pinso]\nтолчение, растирание, размол <i>PM</i>."],
  ["pistus", "<big>pistus</big>\n\n<b>pistus, a, um</b> <i>part. pf. к</i>\npinso I."],
  ["pisum", "<big>pisum</big>\n\n<b>pisum, i</b> <i>n</i> (<i>греч.</i>)\nгорох <i>Col, PM, Pt etc.</i>"],
  ["Pitane", "<big>Pitane</big>\n\n<b>Pitane, es</b> <i>f</i>\nПитана:\n<b>1)</b> <i>приморский город в Мисии, против о-ва Лемноса</i> <i>O, Mela</i>;\n<b>2)</b> <i>город в Лаконии, на реке Эврот</i> <i>PM, Aus</i>."],
  ["pithecium", "<big>pithecium</big>\n\n<b>pithecium, i</b> <i>n</i> (<i>греч.</i>)\nобезьянка <i>Pl</i>."],
  ["Pithecusa", "<big>Pithecusa</big>\n\n<b>Pithecusa, ae</b> <i>f</i> <i>Mela</i>\n= Pithecusae."],
  ["Pithecusae", "<big>Pithecusae</big>\n\n<b>Pithecusae, arum</b> <i>f</i> (<i>греч.</i> «обезьяньи острова»)\nПитекузы, <i>два о-ва в Тирренском море, против Неаполя</i> (<i>ныне</i> Ischia <i>и</i> Procida) <i>L, O</i>."],
  ["pitheus", "<big>pitheus</big>\n\n<b>pithe͡us, eī</b> <i>и</i> <b>eos</b> <i>m</i> (<i>греч.</i>)\nкомета в виде бочки <i>PM</i>."],
  ["pithias", "<big>pithias</big>\n\n<b>pithias, ae</b> <i>Sen</i> <i>и</i> <b>pithus, i</b> <i>Ap m</i>\n= pitheus."],
  ["Pitholeon", "<big>Pitholeon</big>\n\n<b>Pitholeon, ontis</b> <i>m</i>\nПифолеонт, <i>поэт из Родоса</i> <i>H</i>."],
  ["pittaciolum", "<big>pittaciolum</big>\n\n<b>pittaciolum, i</b> <i>n</i> [<i>demin. к</i> pittacium]\nлисточек, кусочек ткани <i>или</i> пергамента <i>Hier</i>."],
  ["pittacium", "<big>pittacium</big>\n\n<b>pittacium, i</b> <i>n</i> (<i>греч.</i>)\n<b>1)</b> кусок пергамента <i>или</i> ткани <i>AG</i>;\n<b>2)</b> пластырь, компресс <i>CC</i>;\n<b>3)</b> ярлык, этикетка (pittacia in cervicibus amphorarum affixa <i>Pt</i>);\n<b>4)</b> заплата (calceamenta pittaciis consuta <i>Vlg</i>);\n<b>5)</b> лотерейный билет (pittacia in scypho circumferri coepisse <i>Pt</i>);\n<b>6)</b> объявление (proponere p. publice <i>Aug</i>);\n<b>7)</b> список, перечень <i>Lampr, CTh, CJ</i>;\n<b>8)</b> расписка <i>Eccl</i>."],
  ["Pittacus", "<big>Pittacus</big>\n\n<b>Pittacus (-os), i</b> <i>m</i>\nПиттак, <i>родом из Митилены, один из «семи мудрецов», политический противник поэта Алкея</i> (<i>VII-VII вв. до н. э.</i>) <i>C, Nep, J</i>."],
  ["Pittheis", "<big>Pittheis</big>\n\n<b>Pittheis, idis</b> <i>и</i> <b>idos</b> <i>f</i>\nПитфеида, дочь Питфея, <i>т. е.</i> Aethra <i>O</i>."],
  ["Pittheius", "<big>Pittheius</big>\n\n<b>Pittheius, a, um</b> [Pittheus]\nпитфеев (Troezen <i>O</i>)."],
  ["Pittheus", "<big>Pittheus</big>\n\n<b>I Pitthe͡us, eī</b> <i>и</i> <b>eos</b> <i>m</i>\nПитфей, <i>сын Пелопа, царь Трезена, отец Этры, матери Тезея</i> <i>O</i>.\n\n<b>II Pitthēus, a, um</b> <i>O</i>\n= Pittheius."],
  ["pituinus", "<big>pituinus</big>\n\n<b>pituinus (pityinus), a, um</b> (<i>греч.</i>)\nсосновый (resina <i>Scr</i>)."],
  ["pituita", "<big>pituita</big>\n\n<b>pituita, ae</b> <i>f</i>\n<b>1)</b> слизь, мокрота <i>Cato, CC</i>; гнойная жидкость <i>CC</i>;\n<b>2)</b> (<i>тж.</i> p. nasi <i>Ctl</i> <i>и</i> p. capitis <i>PM</i>) насморк (molesta <i>H</i>);\n<b>3)</b> типун (<i>у домашней птицы</i>) <i>Col, PM</i>;\n<b>4)</b> сок, вытекающий из просверлённого ствола дерева (defluens p. <i>PM</i>)."],
  ["pituitaria", "<big>pituitaria</big>\n\n<b>pituitaria, ae</b> <i>f</i> (<i>sc.</i> herba)\nудаляющее слизь, <i>т. е.</i> отхаркивающее средство <i>PM</i>."],
  ["pituitosus", "<big>pituitosus</big>\n\n<b>pituitosus, a, um</b> [pituita]\nстрадающий скоплением слизи (homo <i>C</i>)."],
  ["pitvita", "<big>pitvita</big>\n\n<b>pitvita</b> <i>H</i>\n= pituita."],
  ["pitydion", "<big>pitydion</big>\n\n<b>pitydion, i</b> <i>n</i> <i>или</i> <b>pityte, idos</b> <i>f</i> (<i>греч.</i>)\nсосновая шишка <i>PM</i>."],
  ["pityinus", "<big>pityinus</big>\n\n<b>pityinus, a, um</b> <i>v. l.</i>\n= pituinus."],
  ["pityocampe", "<big>pityocampe</big>\n\n<b>pityocampe, es</b> <i>f</i> (<i>греч.</i>)\nгусеница соснового шелкопряда <i>PM</i>."],
  ["Pityus", "<big>Pityus</big>\n\n<b>Pityus, untis</b> <i>f</i> (<i>acc.</i> unta)\nПитиунт, <i>основанный греками город в азиатской Сарматии</i> (<i>на Колхидском побережье Чёрного моря</i>) <i>PM, Amm</i>."],
  ["pius", "<big>pius</big>\n\n<b>pius, a, um</b>\n<b>1)</b> набожный, благочестивый <i>или</i> добродетельный (vates <i>V</i>; Aeneas <i>V</i>; piissimi civium <i>QC</i>): manibus piis <i>V</i> с молитвенно воздетыми (<i>или</i> сложенными) руками; piorum sedes <i>C</i> обиталище праведных (= Элизиум);\n<b>2)</b> посвященный богам, священный (lucus <i>H</i>; vitta <i>V</i>); жертвенный (far <i>V, H, O</i>); угодный богам, справедливый, законный (bellum <i>L</i>; pax <i>C</i>);\n<b>3)</b> милостивый, милосердный (numina <i>V</i>); любящий, добрый, преданный, нежный (in parentes <i>C</i>; adversus fratrem <i>L</i>); полный любви (dolor <i>C</i>; metus <i>O</i>);\n<b>4)</b> милый, дорогой: pia sarcina <i>O</i> дорогая (сердцу) ноша (Энея), <i>т. е.</i> престарелый Анхиз."],
  ["pix", "<big>pix</big>\n\n<b>pix, picis</b> <i>f</i>\nсмола <i>или</i> дёготь <i>Pl, Lcr, Cs, V etc.</i>"],
  ["placabilis", "<big>placabilis</big>\n\n<b>placabilis, e</b> [placo]\n<b>1)</b> быстро успокаиваемый, легко успокаивающийся, смягчающийся, отходчивый (animus <i>C</i>): quo quisque est major, magis est p. irae <i>O</i> чем выше человек, тем менее он неумолим в гневе;\n<b>2)</b> (<i>тж.</i> p. ad preces <i>L</i>) движимый мольбами, милосердный (ara Dianae <i>V</i>);\n<b>3)</b> успокаивающий, успокоительный, примиряющий (sacrificium <i>Lact</i>): placabilius est <i>Ter</i> это лучшее средство успокоить (их)."],
  ["placabilitas", "<big>placabilitas</big>\n\n<b>placabilitas, atis</b> <i>f</i> [placabilis]\nсклонность к примирению, легко смягчающийся характер, отходчивость (p. atque clementia <i>C</i>)."],
  ["placabiliter", "<big>placabiliter</big>\n\n<b>placabiliter</b> [placabilis]\nуспокаивающе, смягчающе (propitiare aliquem <i>AG</i>)."],
  ["placamen", "<big>placamen</big>\n\n<b>placamen, inis</b> <i>n</i> <i>L, Sil</i>\n= placamentum."],
  ["placamentum", "<big>placamentum</big>\n\n<b>placamentum, i</b> <i>n</i> [placo]\nспособ умиротворения, средство к смягчению (animarum placamenta vel irritamenta <i>Ap</i>; placamenta deorum <i>PM, T</i>)."],
  ["placate", "<big>placate</big>\n\n<b>placate</b> [placatus]\nспокойно, невозмутимо (omnia humana ferre <i>C</i>)."],
  ["placatio", "<big>placatio</big>\n\n<b>placatio, onis</b> <i>f</i> [placo]\nуспокоение (perturbati animi <i>C</i>); умилостивление (deorum <i>C</i>)."],
  ["placatorius", "<big>placatorius</big>\n\n<b>placatorius, a, um</b>\nумилостивительный (hostia <i>Tert</i>)."],
  ["placatus", "<big>placatus</big>\n\n<b>placatus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> placo;\n<b>2.</b> <i>adj.</i> мирный, спокойный (vita <i>C</i>); тихий (mare <i>V</i>); кроткий, благосклонный (Venus <i>V</i>); расположенный (в пользу) (alicui <i>L</i>)."],
  ["placens", "<big>placens</big>\n\n<b>placens, entis</b>\n<b>1.</b> <i>part. praes. к</i> placeo;\n<b>2.</b> <i>adj.</i> милый, любимый (uxor <i>H</i>)."],
  ["placenta", "<big>placenta</big>\n\n<b>placenta, ae</b> <i>f</i>\nпирог <i>Cato, H, M, J</i>."],
  ["placentia", "<big>placentia</big>\n\n<b>I placentia, ae</b> <i>f</i> [placeo]\nпривлекательность, прелесть (ad placentiam libido flectitur <i>Ap</i>).\n\n<b>II Placentia, ae</b> <i>f</i>\nПлаценция, <i>город в Gallia Cisatpina, на правом берегу реки Пад</i> (<i>ныне</i> Piacenza) <i>C, L, VP</i>."],
  ["Placentinus", "<big>Placentinus</big>\n\n<b>I Placentinus, a, um</b> [Placentia]\nплацентинский (municipium <i>C</i>): Placentini milites <i>Pl шутл.</i> (<i>по созвучию</i> Placentia <i>и</i> placenta) воины-пирожники.\n\n<b>II Placentinus, i</b> <i>m</i>\nжитель Плаценции <i>L</i>."],
  ["placeo", "<big>placeo</big>\n\n<b>placeo, cui, citum, ere</b>\n<b>1)</b> нравиться (alicui <i>C etc.</i>); иметь успех, цениться (p. in tragoediis <i>C</i>; fabula placet <i>Ter</i>): placuit sollertia <i>T</i> (эта) выдумка пришлась по вкусу; p. sibi <i>C, O, J, Sen, Pt</i> быть довольным собой, много мнить о себе, но <i>тж.</i> <i>Pt</i> много позволять себе; victrix causa diis placuit, sed victa Catoni <i>Lcn</i> боги были на стороне победителей, Катон же — на стороне побеждённых; placitus amor <i>V</i> сердечное влечение, пламенная любовь; videte, numquid hoc placeat <i>Pt</i> смотрите (<i>т. е.</i> скажите), согласны ли вы на это?;\n<b>2)</b> <i>impers.</i> placet (<i>реже</i> placitum est) <i>C, Cs etc.</i> угодно, хочется, желательно: placet, bene facitis <i>Pt</i> согласен, вы правы; si placet <i>C</i> если (вам, тебе) угодно; placuit mihi (ut) <i>C</i> я решил; senatui placet <i>C etc.</i> сенату угодно (сенат требует); placitum est, ut reverteretur Pompejus <i>C</i> постановили, чтобы Помпеи вернулся; minoris pallium addicere placuit <i>Pt</i> было решено продать плащ подешевле;\n<b>3)</b> <i>impers.</i> placet кажется, представляется, существует мнение: non placet <i>C</i> (моё) мнение не таково; placet Carneadi, duo esse genera visorum <i>C</i> Карнеад полагает (того мнения), что существует два рода явлений; ut Stoicis placet <i>C</i> как полагают стоики."],
  ["placibilis", "<big>placibilis</big>\n\n<b>placibilis, e</b> [placeo]\nпривлекательный, приятный <i>Tert</i>."],
  ["placidae", "<big>placidae</big>\n\n<b>placidae, arum</b> <i>f</i>\nплоскодонные суда <i>AG</i>."],
  ["placide", "<big>placide</big>\n\n<b>placide</b> [placidus]\n<b>1)</b> тихо (pultare <i>Pl</i>); неслышно, бесшумно (ire <i>Ter</i>; progredi <i>Cs</i>);\n<b>2)</b> спокойно, кротко, безропотно (ferre dolorem <i>C</i>); мягко (aliquem tractare <i>Sl</i>);\n<b>3)</b> постепенно, отлого (colles p. acclives <i>L</i>)."],
  ["Placidejanus", "<big>Placidejanus</big>\n\n<b>Placidejanus, a, um</b>\n= Pacidejanus."],
  ["placiditas", "<big>placiditas</big>\n\n<b>placiditas, atis</b> <i>f</i> [placidus]\nкротость, миролюбие <i>Vr, AG, Eccl</i>."],
  ["placido", "<big>placido</big>\n\n<b>placido, avi, —, are</b>\nумерять, замедлять (cursus suos <i>Ambr</i>)."],
  ["placidulus", "<big>placidulus</big>\n\n<b>placidulus, a, um</b> [<i>demin. к</i> placidus]\nспокойный, мирный (cinis <i>Aus</i>)."],
  ["placidus", "<big>placidus</big>\n\n<b>placidus, a, um</b> [placeo]\n<b>1)</b> спокойный, миролюбивый (homo, urbs <i>C</i>);\n<b>2)</b> смирный, ручной, кроткий (animal <i>PM</i>); послушный (senatus <i>C</i>); тихий (aqua <i>Pl etc.</i>); безмятежный (quies <i>V</i>);\n<b>3)</b> ясный, погожий (dies <i>PJ</i>);\n<b>4)</b> облагороженный, культурный (arbores <i>PM</i>);\n<b>5)</b> лёгкий, безболезненный (mors <i>V</i>)."],
  ["placito", "<big>placito</big>\n\n<b>placito, —, —, are</b> [<i>intens. к</i> placeo]\nчрезвычайно нравиться <i>Pl</i>."],
  ["placitum", "<big>placitum</big>\n\n<b>placitum, i</b> <i>n</i> [placeo]\n<b>1)</b> приятное, удовольствие: ultra p. <i>V</i> чересчур, сверх меры;\n<b>2)</b> мнение, взгляд (placita philosophorum <i>T</i>);\n<b>3)</b> предписание, указание (placita majorum <i>T</i>)."],
  ["placitus", "<big>placitus</big>\n\n<b>placitus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> placeo;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> любезный, дорогой (placita paci oliva <i>V</i>);\n<b>2)</b> условленный (locus ambobus p. <i>Sl</i>)."],
  ["placo", "<big>placo</big>\n\n<b>placo, avi, atum, are</b> [<i>одного корня с</i> placeo. <i>и</i> planus]\n<b>1)</b> успокаивать (aequora <i>O</i>; genus irritabile vatum <i>H</i>); унимать (iram deorum donis <i>C</i>); улаживать (discordias <i>C</i>); мирить, примирять (aliquem alicui <i>Ter, C</i> <i>или</i> in aliquem <i>Nep, L</i>): placari in aliquem <i>Nep</i> помириться с кем-л. (простить кого-л.); placari alicui <i>C</i> стать благосклонным к кому-л.;\n<b>2)</b> задабривать (inimicum beneficiis <i>L</i>); умилостивлять (deos <i>H</i>; carmine di placantur <i>H</i>);\n<b>3)</b> утолять (sitim <i>M</i>; ventrem iratum <i>H</i>)."],
  ["placor", "<big>placor</big>\n\n<b>placor, oris</b> <i>m</i> <i>Vlg</i>\n= placentia."],
  ["placusa", "<big>placusa</big>\n\n<b>placusa (placusia), ae</b> <i>f</i> — <i>см.</i>\nplagusia."],
  ["Plaetorianus", "<big>Plaetorianus</big>\n\n<b>Plaetorianus, a, um</b> [Plaetorius]\nплеториев <i>C</i>."],
  ["Plaetorius", "<big>Plaetorius</big>\n\n<b>Plaetorius, a, um</b>\nПлеторий, <i>римск. nomen; наиболее известен</i> M. P., <i>обвинитель Фонтея в 69 г. до н. э.</i> <i>C</i>."],
  ["plaga", "<big>plaga</big>\n\n<b>I plaga, ae</b> <i>f</i> [plango]\n<b>1)</b> удар, ушиб (plagis vulnerari <i>Nep</i>): verbera et plagae <i>Sl</i> удары плетьми и розгами (палками); plagam infligere <i>C</i> (ferre <i>V</i>) нанести удар, ранить; p. caeli <i>Lcr</i> удар молнии;\n<b>2)</b> укус (scorpionum et canum <i>PM</i>); рана (plagas hiantes vincire <i>Ap</i>);\n<b>3)</b> ущерб, урон, <i>тж.</i> бедствие (levior est p. ab amico <i>C</i>);\n<b>4)</b> сильное впечатление, сенсация (oratio gravem plagam facit <i>C</i>);\n<b>5)</b> страдание: articulum ingenti plaga reconciliare <i>Ap</i> вправить сустав с мучительной болью.\n\n<b>II plaga, ae</b> <i>f</i>\n<b>1)</b> страна, область: p. Materina <i>L</i> <i>одна из областей Умбрии</i>; p. aetheria <i>V</i> (caeli <i>C</i>) небесное (воздушное) пространство;\n<b>2)</b> сторона, страна (света) (quattuor plagae <i>V</i>);\n<b>3)</b> пояс, зона (frigida <i>L</i>; fervida <i>Sen</i>): p. solis iniqui <i>V</i> жаркий пояс.\n\n<b>III plaga, ae</b> <i>f</i> <i>преим.</i> <i>pl.</i>\n<b>1)</b> сеть, охотничьи тенёта (plagas tendere <i>C</i>);\n<b>2)</b> <i>перен.</i> сети, интриги (in plagas incidere <i>C, Pt</i>): cum auritis plagis <i>Pl</i> = arrectis attentisque auribus;\n<b>3)</b> покрывало <i>или</i> завеса (полог) <i>Pac, Vr</i>."],
  ["plagella", "<big>plagella</big>\n\n<b>plagella, ae</b> <i>f</i> [<i>demin. к</i> plaga III]\nвдвое сложенный кусок ткани (для примочки) <i>CA</i>."],
  ["plagiarius", "<big>plagiarius</big>\n\n<b>plagiarius, i</b> <i>m</i> [plagium]\n<b>1)</b> похититель людей <i>C, Sen, Dig, Vlg</i>;\n<b>2)</b> литературный вор, плагиатор <i>M</i>."],
  ["plagiator", "<big>plagiator</big>\n\n<b>plagiator, oris</b> <i>m</i> <i>Tert, Hier</i>\n= plagiarius 1."],
  ["plagiger", "<big>plagiger</big>\n\n<b>plagiger, a, um</b> <i>Pl</i>\n= plagigerulus."],
  ["plagigerulus", "<big>plagigerulus</big>\n\n<b>plagigerulus, a, um</b> [plaga I + gerulus]\nтерпящий побои <i>Pl</i>."],
  ["Plagiosippus", "<big>Plagiosippus</big>\n\n<b>Plagiosippus</b> <i>или</i> <b>Plagioxyppus, i</b> <i>m</i> [plaga I]\n«Избивающий» (<i>вымышленное имя у</i> <i>rhH</i>)."],
  ["plagipatida", "<big>plagipatida</big>\n\n<b>plagipatida, ae</b> <i>m</i> [plaga 1 + patior] <i>Pl</i>\n= plagigerulus."],
  ["plagium", "<big>plagium</big>\n\n<b>plagium, i</b> <i>n</i> (<i>греч.</i>)\nпохищение, (коварный) увод <i>Dig</i>."],
  ["plago", "<big>plago</big>\n\n<b>plago, avi, atum, are</b> [plaga I]\nбить, избивать (aliquem <i>Vlg, Aug</i>)."],
  ["plagosus", "<big>plagosus</big>\n\n<b>plagosus, a, um</b> [plaga I]\n<b>1)</b> избитый (dorsum <i>Ap</i>);\n<b>2)</b> драчливый (homo <i>H</i>)."],
  ["plagula", "<big>plagula</big>\n\n<b>plagula, ae</b> <i>f</i> [<i>demin. к</i> plaga III]\n<b>1)</b> полотнище тоги (<i>тога сшивалась из двух plagulae</i>) <i>Vr</i>;\n<b>2)</b> лист бумаги (<i>около 1/20 scapus</i>) <i>PM</i>;\n<b>3)</b> ковёр, покрывало <i>Vr</i>;\n<b>4)</b> полог, занавеска (dimovere plagulas <i>Su</i>)."],
  ["plagusia", "<big>plagusia</big>\n\n<b>plagusia (placusa, placusia), ae</b> <i>f</i>\n<i>род рыбы или моллюска</i> <i>Pl</i>."],
  ["planarius", "<big>planarius</big>\n\n<b>planarius, a, um</b> [planum]\nравнинный, происходящий на ровном месте (conflictus <i>Amm</i>): planaria interpellatio <i>Dig, CJ</i> заявление судьи до занятия им судейской трибуны (<i>т. е.</i> не pro tribunali)."],
  ["Planasia", "<big>Planasia</big>\n\n<b>Planasia</b> <i>или</i> <b>Planaria, ae</b> <i>f</i>\nПланасия, <i>о-в между овами Корсика и Ильва (Эльба); место ссылки (ныне</i> Pianosa) <i>PM, T</i>."],
  ["planca", "<big>planca</big>\n\n<b>planca, ae</b> <i>f</i>\nдоска (roborea <i>Pall</i>)."],
  ["Plancina", "<big>Plancina</big>\n\n<b>Plancina, ae</b> <i>f</i>\nПланцина, <i>жена Гнея Кальпурния Пизона</i> <i>T</i>."],
  ["Plancius", "<big>Plancius</big>\n\n<b>Plancius, a, um</b>\nПланций, <i>римск. nomen; наиболее известны</i>:\n<b>1)</b> Cn. P., <i>римск. всадник и откупщик времён Цицерона</i> <i>C</i>;\n<b>2)</b> Cn. P., <i>сын предыдущего, в 58 г. до н. э. квестор в Македонии, друг Цицерона и его подзащитный в 54 г. до н. э.</i> <i>C</i>."],
  ["planctus", "<big>planctus</big>\n\n<b>planctus, us</b> <i>m</i> [plango]\n<b>1)</b> хлопанье крыльями (Harpyarum <i>VF</i>);\n<b>2)</b> плеск, гул (undae <i>Lcn</i>);\n<b>3)</b> горестное биение в грудь, плач, рыдание, вопли (gemitus ac p. <i>T</i>)."],
  ["plancus", "<big>plancus</big>\n\n<b>I plancus, i</b> <i>m</i> <i>PM v. l.</i>\n= plangus.\n\n<b>II Plancus, i</b> <i>m</i>\nПланк, <i>cognomen в роде Мунациев</i> <i>PM</i>."],
  ["plane", "<big>plane</big>\n\n<b>plane</b> [planus]\n<b>1)</b> ровно (collocare aliquid <i>Pl</i>);\n<b>2)</b> ясно (loqui <i>C</i>); открыто, прямо (jure civili dimicare <i>Pt</i>); поистине: p. non mentiar <i>Pt</i> право же, я лгать не стану || напрямик, без обиняков (dicere <i>C</i>);\n<b>3)</b> совершенно, вполне (carere sensu communi <i>H</i>): p. quam ex incendio fugere <i>Pt</i> бежать словно от настоящего пожара; non p. <i>C</i> не совсем || безусловно (optimus <i>Ap</i>); весьма (bene <i>C</i>): p. iratus <i>Pt</i> сильно рассерженный || очень хорошо (scire <i>Ter</i>);\n<b>4)</b> (<i>преим. в ответах</i>) конечно, разумеется <i>Pl, Ter</i>: ita p. <i>C</i> именно так; p. is ipse erat <i>Pt</i> конечно, это был он."],
  ["planetae", "<big>planetae</big>\n\n<b>planetae, arum</b> <i>f</i> <i>и</i> <b>planetes, um</b> <i>m</i> (<i>греч.</i>; <i>лат.</i> stellae errantes <i>или</i> erraticae)\nпланеты <i>Aus, Is, AG</i>."],
  ["planetarius", "<big>planetarius</big>\n\n<b>planetarius, i</b> <i>m</i> [planetae]\nзвездочёт, астролог <i>Aug</i>."],
  ["planeticus", "<big>planeticus</big>\n\n<b>planeticus, a, um</b> (<i>греч.</i>)\nблуждающий: planetica sidera <i>Sid</i> = planetae."],
  ["plango", "<big>plango</big>\n\n<b>plango, planxi, planctum, ere</b>\n<b>1)</b> с шумом ударять, бить (tympana palmis <i>Ctl</i>): litora planguntur fluctu <i>O</i> волны с шумом бьются (плещутся) о берег; volucris plangitur <i>O</i> птица бьёт себя (хлопает) крыльями; p. (pectora) <i>C, O</i> <i>или</i> <i>med.-pass.</i> plangi <i>O</i> бить себя в грудь (<i>от отчаяния, в скорби</i>); p. aliquem <i>Tib</i> предаваться скорби по ком-л., горестно оплакивать кого-л.; planctus est optime <i>Pt</i> его горько оплакивали;\n<b>2)</b> шуметь (nemora plangunt <i>V</i>)."],
  ["plangor", "<big>plangor</big>\n\n<b>plangor, oris</b> <i>m</i> [plango]\n<b>1)</b> громкое хлопанье: p. alicujus rei <i>rhH</i> хлопанье (удар) по чему-л. || шум, плеск (resonant plangore cachinni, <i>sc.</i> undarum <i>Ctl</i>);\n<b>2)</b> биение себя в грудь, <i>перен.</i> скорбное отчаяние, горестные вопли: plangorem dare <i>O</i> громко рыдать; plangore forum implere <i>C</i> оглашать площадь рыданиями."],
  ["planguncula", "<big>planguncula</big>\n\n<b>planguncula, ae</b> <i>f</i>\nвосковая кукла <i>C</i> (<i>v. l.</i>)."],
  ["plangus", "<big>plangus</big>\n\n<b>plangus, i</b> <i>m</i>\n<i>разновидность орла</i> <i>PM</i>."],
  ["planicies", "<big>planicies</big>\n\n<b>planicies, ei</b> <i>f</i> <i>v. l.</i>\n= planities."],
  ["planiloquus", "<big>planiloquus</big>\n\n<b>planiloquus, a, um</b> [planus + loquor]\nговорящий напрямик, откровенный <i>Pl</i>."],
  ["planipes", "<big>planipes</big>\n\n<b>planipes, pedis</b> <i>m</i> [planus + pes]\nтанцор-босоножка (<i>без комических</i> socci <i>и трагических</i> cothurni) <i>AG, J, Aus</i>."],
  ["planitas", "<big>planitas</big>\n\n<b>planitas, atis</b> <i>f</i> [planus]\nясность, чёткость (sententiarum <i>T</i>)."],
  ["planitia", "<big>planitia</big>\n\n<b>planitia, ae</b> <i>f</i> <i>C, Cs, L</i>\n= planities."],
  ["planities", "<big>planities</big>\n\n<b>planities, ei</b> <i>f</i> [planus]\nплоскость, ровное место, равнина <i>C, Cs etc.</i>"],
  ["planitudo", "<big>planitudo</big>\n\n<b>planitudo, inis</b> <i>f</i> <i>Boet</i>\n= planities."],
  ["planta", "<big>planta</big>\n\n<b>I planta, ae</b> <i>f</i>\n<b>1)</b> саженец (plantas ex seminario transferre <i>PM</i>);\n<b>2)</b> прививок, привой (plantas abscindere de corpore matrum <i>V</i>);\n<b>3)</b> рассада (plantam deponere in hortis <i>O</i>);\n<b>4)</b> (взрослое) растение (tenues plantae <i>J</i>).\n\n<b>II planta, ae</b> <i>f</i>\nстопа, подошва (pedis <i>Ctl, V, O etc.</i>): intenta planta <i>VF</i> быстрым шагом; certamina plantae <i>Sil</i> состязания в беге; summa sidera plantis contingere <i>погов. Prp</i> = вкусить высшее блаженство; supra plantam (<i>или</i> crepidam) ascendere <i>погов. VM</i> судить выше сапога."],
  ["plantago", "<big>plantago</big>\n\n<b>plantago, inis</b> <i>f</i> <i>бот.</i>\nподорожник <i>PM</i>."],
  ["plantaria", "<big>plantaria</big>\n\n<b>I plantaria, ium</b> <i>n</i> [planta I]\n<b>1)</b> молодые деревья, саженцы (p. transferre <i>PM</i>); рассадник, питомник (p. instituere <i>PM</i>);\n<b>2)</b> растительность, зелень, овощи (p. horti <i>J</i>);\n<b>3)</b> <i>поэт.</i> волосы (plantaria vellere <i>Pers</i>).\n\n<b>II plantaria, ium</b> <i>n</i> [planta II]\nокрылённая обувь (Perseos <i>VF</i>)."],
  ["plantaris", "<big>plantaris</big>\n\n<b>plantaris, e</b> [planta II]\nнаходящийся на ногах (alae Mercurii <i>St</i>)."],
  ["plantarium", "<big>plantarium</big>\n\n<b>plantarium, i</b> <i>n</i> [planta I]\nрассадник, питомник <i>PM, Vlg, Eccl</i>."],
  ["plantatio", "<big>plantatio</big>\n\n<b>plantatio, onis</b> <i>f</i> [planto]\n<b>1)</b> сажание, пересадка (<i>sc.</i> violarum rosarumque <i>PM</i>; vineae <i>Aug</i>); ;\n<b>2)</b> насаждения, плантация <i>Vlg</i>."],
  ["plantator", "<big>plantator</big>\n\n<b>plantator, oris</b> <i>m</i> [planto]\nнасадитель <i>Aug</i>."],
  ["plantiger", "<big>plantiger</big>\n\n<b>plantiger, gera, gerum</b> [planta + gero]\nпустивший молодые побеги, с молодыми ветками (siliqua <i>PM</i>)."],
  ["planto", "<big>planto</big>\n\n<b>planto, avi, atum, are</b> [planta]\n<b>1)</b> сажать, пересаживать (seri vel plantari <i>Pall</i>; nemus <i>Vlg</i>);\n<b>2)</b> засаживать (inculta <i>Vlg</i>)."],
  ["planum", "<big>planum</big>\n\n<b>planum, i</b> <i>n</i> [planus I]\nровное место, равнина, плоскость (in p. deducere <i>Sl</i>): de (e) <i>или</i> in piatio <i>Dig, Aus</i> внизу, на ровном месте, <i>перен.</i> <i>Dig</i> не перед судом, вне суда <i>или</i> <i>Sen</i> в низах, в обычной (частной) обстановке (aute plano, aute tribunali <i>Su</i>); in p. deferre понизить, сделать скромным (fortunam <i>Sen</i>); de plano легко, без труда (aliquid promittere <i>Lcr</i>); ex proclivi p. facere <i>погов. Pl</i> разъяснить неясное; p. fac <i>C</i> докажи."],
  ["planus", "<big>planus</big>\n\n<b>I planus, a, um</b>\n<b>1)</b> плоский (corpus <i>C</i>); ровный (locus <i>C</i>; campus <i>L</i>); имеющий сплющенную форму (piscis <i>PM</i>); находящийся вровень с землёй, внизу: pede plano aedificia <i>Vtr</i> одноэтажные дома; manus plana <i>Sen</i> открытая рука, ладонь;\n<b>2)</b> ясный, понятный (narratio <i>C</i>);\n<b>3)</b> легко произносимый (littera <i>Q</i>). — <i>См. тж.</i> planum.\n\n<b>II planus, i</b> <i>m</i> (<i>греч.</i>)\nавантюрист, шарлатан <i>C, H</i>; праздношатающийся <i>Pt</i>."],
  ["planxi", "<big>planxi</big>\n\n<b>planxi</b> <i>pf. к</i>\nplango."],
  ["plasma", "<big>plasma</big>\n\n<b>plasma, atis</b> <i>n</i> (<i>греч.</i>)\n<b>1)</b> творение, тварь <i>Eccl</i>;\n<b>2)</b> (поэтический) вымысел, фантазия <i>Aus</i>;\n<b>3)</b> манерные модуляции голоса, жеманный тон <i>Pers, Q</i>."],
  ["plasmatio", "<big>plasmatio</big>\n\n<b>plasmatio, onis</b> <i>f</i>\nобразование, созидание, творение <i>Hier, Ambr</i>."],
  ["plasmator", "<big>plasmator</big>\n\n<b>plasmator, oris</b> <i>m</i>\nсоздатель, творец (hominis <i>Tert</i>)."],
  ["plasmo", "<big>plasmo</big>\n\n<b>plasmo, avi, atum, are</b> (<i>греч.</i>)\nобразовывать, созидать (corporis effigiem <i>Eccl</i>)."],
  ["plastes", "<big>plastes</big>\n\n<b>plastes, ae</b> <i>m</i> (<i>греч.</i>)\n<b>1)</b> ваятель <i>Vtr, PM, VP, Pt</i>;\n<b>2)</b> гончар <i>Vlg</i>."],
  ["plastica", "<big>plastica</big>\n\n<b>plastica, ae</b> <i>и</i> <b>plastice, es</b> <i>f</i> (<i>греч.</i>)\nпластика <i>Tert, PM</i>."],
  ["plasticus", "<big>plasticus</big>\n\n<b>plasticus, a, um</b> [plastice]\n<b>1)</b> пластический, скульптурный: ratio plastica <i>Vtr</i> основы ваяния;\n<b>2)</b> лепящий, формирующий (manus <i>Tert</i>)."],
  ["Plataeae", "<big>Plataeae</big>\n\n<b>Plataeae, arum</b> <i>f</i>\nПлатеи, <i>город в южн. Беотии, у сев. подошвы Киферона</i> (<i>ныне</i> Paleo Castro); <i>место победы греков над персами в 479 г. до н. э.</i> <i>C, Nep, Just</i>."],
  ["Plataeenses", "<big>Plataeenses</big>\n\n<b>Plataeenses, ium</b> <i>m</i>\nжители города Платеи <i>Nep, Just</i>."],
  ["Plataicus", "<big>Plataicus</big>\n\n<b>Plataicus, a, um</b> [Plataeae]\nплатейский, при Платеях (proelium <i>Vtr</i>)."],
  ["platalea", "<big>platalea</big>\n\n<b>platalea, ae</b> <i>f</i>\nколпица (<i>птица из семейства ибисовых</i>), <i>по др.</i>, пеликан <i>C</i>."],
  ["plataninus", "<big>plataninus</big>\n\n<b>plataninus, a, um</b> [platanus]\nплатановый (folia <i>Col</i>)."],
  ["platanon", "<big>platanon</big>\n\n<b>platanon, onis</b> (<i>acc.</i> ona) <i>m</i> (<i>греч.</i>)\nплатановая роща <i>Vtr, PJ, Sen, Pt, M</i>."],
  ["platanus", "<big>platanus</big>\n\n<b>platanus, i</b> <i>и</i> <b>us</b> <i>f</i> (<i>греч.</i>)\nплатан <i>C, PM, H, V</i>."],
  ["platea", "<big>platea</big>\n\n<b>I platea, ae</b> <i>f</i> (<i>греч.</i>)\n<b>1)</b> улица <i>Ter, Cs, H, PJ</i>;\n<b>2)</b> внутренний двор <i>Lampr</i>.\n\n<b>II platea, ae</b> <i>f</i>\nводоплавающая птица, <i>предпол.</i> пеликан <i>PM</i>."],
  ["platessa", "<big>platessa</big>\n\n<b>platessa, ae</b> <i>f</i> <i>зоол.</i>\nкамбала <i>Aus</i>."],
  ["Platon", "<big>Platon</big>\n\n<b>Plato(n), onis</b> <i>m</i>\nПлатон:\n<b>1)</b> <i>родом из Афин, сын Аристона, ученик Сократа и учитель Аристотеля, основатель Академической философской школы</i> (<i>427-347 гг. до н. э.</i>) <i>C, Sen, H, AG etc.</i>;\n<b>2)</b> <i>родом из города Сарды, философ-эпикуреец, современник Цицерона</i> <i>C</i>."],
  ["Platonicus", "<big>Platonicus</big>\n\n<b>I Platonicus, a, um</b>\nплатоновский (subtilitas <i>PJ</i>; philosophus <i>AG</i>).\n\n<b>II Platonicus, i</b> <i>m</i>\nпоследователь Платона, платоник <i>C etc.</i>"],
  ["Platonitas", "<big>Platonitas</big>\n\n<b>Platonitas, atis</b> <i>f</i> <i>филос.</i>\nплатонизм <i>Boet</i>."],
  ["platyceros", "<big>platyceros</big>\n\n<b>platyceros, otis</b> <i>f</i> (<i>греч.</i>)\nлань <i>PM</i>."],
  ["platycoriasis", "<big>platycoriasis</big>\n\n<b>platycoriasis, is</b> <i>f</i> (<i>греч.</i>)\nрасширение зрачков <i>Veg</i>."],
  ["platyophthalmus", "<big>platyophthalmus</big>\n\n<b>platyophthalmus (-os), i</b> <i>m</i> (<i>греч.</i>) <i>PM</i>\n= stibi."],
  ["plaudo", "<big>plaudo</big>\n\n<b>plaudo (plodo), plausi (plosi), plausum (plosum), ere</b>\n<b>1)</b> бить (pennis <i>O</i>; alis <i>V</i>); хлопать (pectus manu <i>O</i>); похлопывать, трепать (colla equorum <i>V, O</i>); стучать (rostro <i>O</i>): p. choreas pedibus <i>V</i> носиться в пляске, танцевать;\n<b>2)</b> (<i>sc.</i> manibus) рукоплескать, аплодировать <i>Pl, H, C etc.</i>; одобрять, хвалить (alicui <i>C, H etc.</i>; versibus alicujus <i>O</i>): p. sibi <i>H, PJ</i> быть довольным собой; plaudente aliquo <i>C, Pt</i> при чьём-л. одобрении; plauditur <i>O</i> раздаются рукоплескания;\n<b>3)</b> (<i>в знак неодобрения</i>) топать (in aliquem <i>MF</i>)."],
  ["plaumoratum", "<big>plaumoratum</big>\n\n<b>plaumoratum, i</b> <i>n</i> (<i>ретийское слово</i>)\nколёсный плуг <i>PM</i>."],
  ["plausi", "<big>plausi</big>\n\n<b>plausi</b> <i>pf. к</i>\nplaudo."],
  ["plausibilis", "<big>plausibilis</big>\n\n<b>plausibilis, e</b> [plaudo]\nзаслуживающий одобрения, достойный похвалы (nomen <i>C</i>; oratio <i>Sen</i>) ."],
  ["plausibiliter", "<big>plausibiliter</big>\n\n<b>plausibiliter</b>\nвызывая одобрение (aliquid manifestare <i>Sid</i>)."],
  ["plausor", "<big>plausor</big>\n\n<b>plausor, oris</b> <i>m</i> [plaudo]\nаплодирующий, рукоплещущий <i>H, PJ, Su</i>: p. redemptus <i>Pt</i> платный клакёр."],
  ["plaustellum", "<big>plaustellum</big>\n\n<b>plaustellum, i</b> <i>n</i> [<i>demin. к</i> plaustrum]\nтележка <i>Vr</i>."],
  ["plaustra", "<big>plaustra</big>\n\n<b>plaustra, ae</b> <i>f</i> <i>Sid</i>\n= plaustrum."],
  ["plaustrarius", "<big>plaustrarius</big>\n\n<b>I plaustrarius, a, um</b> [plaustrum]\nупряжной (asinus <i>Cato</i>).\n\n<b>II plaustrarius, i</b> <i>m</i>\n<b>1)</b> каретник, тележник <i>Lampr</i>;\n<b>2)</b> возница <i>Dig</i>."],
  ["plaustrum", "<big>plaustrum</big>\n\n<b>I plaustrum, i</b> <i>n</i>\nтелега, воз, повозка <i>Cato, Lcr, C etc.</i>: p. perculi <i>погов. Pl</i> у меня всё пропало (пошло прахом).\n\n<b>II Plaustrum, i</b> <i>n</i>\nВоз, <i>созвездие Большой Медведицы</i> <i>O</i>."],
  ["plausus", "<big>plausus</big>\n\n<b>plausus, us</b> (<b>i</b> <i>CTh</i>) <i>m</i> [plaudo]\n<b>1)</b> хлопанье: plausum dare <i>V, Pt</i> хлопать;\n<b>2)</b> рукоплескание, одобрение (plausum quaerere <i>и</i> captare <i>C</i>; p. alicui in theatro datus <i>H</i>): mortuo plausu — <i>см.</i> morior 2."],
  ["Plautianus", "<big>Plautianus</big>\n\n<b>Plautianus, a, um</b> [Plauttus]\nплавтиев <i>C, AG</i>."],
  ["Plautinotatus", "<big>Plautinotatus</big>\n\n<b>Plautinotatus, a, um</b> [Plautinus <i>в форме греч.</i> <i>superl.</i>]\nсамый что ни на есть плавтовский (verbum <i>Fronto</i>)."],
  ["Plautinus", "<big>Plautinus</big>\n\n<b>Plautinus, a, um</b> [Plautus]\nплавтов (numeri, sales <i>H</i>; Plautinissimi versus <i>AG</i>)."],
  ["Plautius", "<big>Plautius</big>\n\n<b>Plautius, a, um</b>\nПлавтий, <i>римск. nomen</i> <i>C, AG</i>."],
  ["plautus", "<big>plautus</big>\n\n<b>I plautus, a, um</b>\nширокий, (<i>об ушах</i>) оттопыренный <i>Tert</i>.\n\n<b>II Plautus, i</b> <i>m</i>\n(T. Maccius) Плавт, <i>родом из Сарсины</i> (<i>Умбрия</i>), <i>римск. комедиограф</i> (<i>254-184 гг. до н. э.</i>)."],
  ["plebecula", "<big>plebecula</big>\n\n<b>plebecula, ae</b> <i>f</i> [<i>demin. к</i> plebs] <i>презр.</i>\nчернь, простонародье <i>C, H, Hier</i>."],
  ["plebeja", "<big>plebeja</big>\n\n<b>plebeja, ae</b> <i>f</i>\nплебейка <i>L</i>."],
  ["plebejus", "<big>plebejus</big>\n\n<b>I plebejus, a, um</b> [plebs]\n<b>1)</b> простонародный, плебейский: plebejum sapere <i>Pt</i> иметь плебейский привкус || вышедший из простого народа, принадлежащий к плебсу (homo <i>C</i>; consul <i>L</i>); народный (ludi <i>C</i>);\n<b>2)</b> простой (sermo <i>C</i>; vinum <i>PM</i>); беспородный (plebeji sanguinis pulli <i>Col</i>).\n\n<b>II plebejus, i</b> <i>m</i>\nплебей <i>C, L etc.</i>"],
  ["plebes", "<big>plebes</big>\n\n<b>plebes, ei</b> <i>или</i> <b>i</b> <i>f</i> <i>арх. C, Sl, L</i>\n= plebs."],
  ["plebicola", "<big>plebicola</big>\n\n<b>plebicola, ae</b> <i>m</i> [plebs+ colo II]\nдруг плебса, защитник простого народа <i>C, L</i>."],
  ["plebis", "<big>plebis</big>\n\n<b>I plebis, is</b> <i>f</i> <i>L</i>\n= plebs.\n\n<b>II plebis</b> <i>gen. к</i>\nplebs."],
  ["plebiscitum", "<big>plebiscitum</big>\n\n<b>plebiscitum, i</b> <i>n</i> — <i>см.</i>\nscitum."],
  ["plebitas", "<big>plebitas</big>\n\n<b>plebitas, atis</b> <i>f</i> [plebs]\nплебейство <i>Cato</i>."],
  ["plebs", "<big>plebs</big>\n\n<b>plebs</b> (<b>plebis</b>, <i>арх.</i> <b>plebes</b>), <i>gen.</i> <b>plebis</b> <i>f</i>\n<b>1)</b> плебс, народная масса, простой народ: patres et p. <i>C, Sl etc.</i> патриции и плебеи;\n<b>2)</b> толпа, рядовой состав: legentium p. <i>Macr</i> читательская масса; p. deorum <i>O, M</i> низшие боги; p. superum (= superorum) <i>O</i> полубоги;\n<b>3)</b> <i>презр.</i> чернь (ventosa p. <i>H</i>);\n<b>4)</b> рой пчёл (tres alveorum plebes <i>Col</i>)."],
  ["plecta", "<big>plecta</big>\n\n<b>plecta, ae</b> <i>f</i> [plecto I] <i>архит.</i>\nплетёная кайма, гирлянда <i>Vlg</i>."],
  ["plectibilis", "<big>plectibilis</big>\n\n<b>plectibilis, e</b> [plecto II]\n<b>1)</b> подлежащий наказанию, наказуемый (usurpatio <i>CTh</i>; invidia <i>Sid</i>);\n<b>2)</b> карающий (severitas <i>CTh</i>)."],
  ["plectilis", "<big>plectilis</big>\n\n<b>plectilis, e</b> [plecto I]\n<b>1)</b> плетёный, свитый (corona <i>Pl</i>);\n<b>2)</b> запутанный, трудный (syllogismus <i>Eccl</i>)."],
  ["plecto", "<big>plecto</big>\n\n<b>I plecto, plexi (plexui), plexum, ere</b>\nплести, сплетать, свивать (flores plexi <i>Ctl</i>); заплетать (crines <i>Vlg</i>).\n\n<b>II plecto, —, —, ere</b> (<i>встреч. преим. в</i> <i>pass.</i>)\n<b>1)</b> наказывать, карать (aliquem poena pecuniaria <i>Dig</i>): plecti neglegentia <i>C</i> быть наказанным за нерадивость; quidquid delirant reges, plectuntur Achivi <i>H</i> <i>впоследствии погов.</i> за всякое сумасбродство царей платятся (простые) ахейцы;\n<b>2)</b> порицать, хулить: cavit, ne qua in re jure plecteretur <i>Nep</i> (Аттик) остерегался того, чтобы не услышать за что-л. заслуженного порицания."],
  ["plectripotens", "<big>plectripotens</big>\n\n<b>plectri–potens, entis</b>\nвеликий в области лирической поэзии <i>Sid</i>."],
  ["plectrum", "<big>plectrum</big>\n\n<b>plectrum, i</b> <i>n</i> (<i>греч.</i>)\n<b>1)</b> плектр (<i>палочка, которой ударяли по струнам кифары</i>) (plectra movere <i>O</i>); <i>перен.</i> язык (попугая) (verba hominibus articulare plectro et palato <i>Ap</i>);\n<b>2)</b> <i>поэт.</i> кифара (plectro modulari <i>Tib</i>);\n<b>3)</b> лирическая поэма, песня (Lesbium <i>H</i>);\n<b>4)</b> тон (levius, gravius <i>H</i>);\n<b>5)</b> кормило, руль <i>Sil</i>."],
  ["Pleiades", "<big>Pleiades</big>\n\n<b>Pleiades, um</b> <i>f</i>\n<b>1)</b> Плеяды, <i>семь звёзд в созвездии Тельца</i> (<i>видимые от середины мая до конца октября</i>) <i>V, O</i>;\n<b>2)</b> <i>миф.</i> Плеяды, <i>семь дочерей Атланта и Плейоны</i> (<i>Electra, Halcyone, Celaeno, Maja, S tempe, Taygete <i>и</i> Merope</i>) <i>O, St</i>;\n<b>3)</b> <i>поэт.</i> гроза, ливень <i>VF</i>."],
  ["Pleias", "<big>Pleias</big>\n\n<b>Pleias</b> <i>и</i> <b>Plejas, adis</b> <i>f sg. к</i>\nPleiades (<i>преим.</i> = Maja) <i>O, St</i>."],
  ["Pleione", "<big>Pleione</big>\n\n<b>Pleione, es</b> <i>f</i>\nПлейона :\n<b>1)</b> <i>дочь Океана и Тефии, жена Атланта, мать Плеяд</i> (<i>см.</i> Pleiades) <i>O</i>: Pleiones nepos <i>O</i> = Mercurius;\n<b>2)</b> <i>VF</i> = Pleiades 1."],
  ["Plemmyrium", "<big>Plemmyrium</big>\n\n<b>Plem(m)yrium (Plimyrium), i</b> <i>n</i>\nПлеммирий, <i>мыс на вост. побережье Сицилии, к югу от Сиракуз</i> (<i>ныне</i> Punto di Gigante) <i>V</i>."],
  ["plenarius", "<big>plenarius</big>\n\n<b>plenarius, a, um</b>\nполный, законченный <i>Eccl</i>."],
  ["plene", "<big>plene</big>\n\n<b>plene</b> [plenus]\n<b>1)</b> полно, до краёв (infundere vasa <i>PM</i>): plenius aequo <i>H</i> сверх меры;\n<b>2)</b> вполне, совершенно (p. perfectae munitiones <i>Cs</i>; homo p. sapiens <i>C</i>); полностью (ulcisci <i>Fl</i>)."],
  ["plenilunium", "<big>plenilunium</big>\n\n<b>plenilunium, i</b> <i>n</i> [plenus + luna]\nполнолуние <i>Col, PM, Amm</i>."],
  ["plenitas", "<big>plenitas</big>\n\n<b>plenitas, atis</b> <i>f</i>\n<b>1)</b> наполненность, переполненность (nubium <i>Vtr</i>);\n<b>2)</b> обилие (umoris <i>Vtr</i>)."],
  ["plenitudo", "<big>plenitudo</big>\n\n<b>plenitudo, inis</b> <i>f</i> [plenus]\nполнота (syllabarum <i>rhH</i>); толщина (crescere in plenitudinem <i>PM</i>); обилие (aquarum <i>Sol</i>)."],
  ["plenus", "<big>plenus</big>\n\n<b>plenus, a, um</b> [pleo]\n<b>1)</b> полный, наполненный (poculum <i>Pt</i>): manibus dare lilia plenis <i>V</i> дать полные пригоршни лилий; p. alicujus rei <i>и</i> aliqui re <i>C etc.</i> полный чего-л. || вздувшийся (amnis <i>QC</i>); заполненный, исписанный (cera <i>O</i>): plena luna <i>V, Cs etc.</i> полнолуние, полная луна;\n<b>2)</b> надутый: plenis velis navigare <i>C</i> <i>или</i> ire <i>Pt</i> плыть (идти) на всех парусах;\n<b>3)</b> преисполненный (laetitia, spei <i>Cs</i>);\n<b>4)</b> толстый, полный, плотный (homo <i>C, CC</i>; corpus <i>H</i>);\n<b>5)</b> сильный, мощный, полнозвучный (vox <i>C</i>): manu plena verberare <i>Pt</i> ударять изо всей силы; pleno gradu <i>Sl, L</i> ускоренным шагом; pleno ore <i>C</i> во весь голос;\n<b>6)</b> беременная (venter, femina <i>O</i>; sus <i>C</i>);\n<b>7)</b> удовлетворённый (minimo <i>O</i>); пресытившийся (amator <i>H</i>);\n<b>8)</b> обременённый, обильно нагружённый (praeda <i>L</i>); отягощенный (annis <i>PJ</i>): p. inimicorum <i>C</i> имеющий много врагов;\n<b>9)</b> состоятельный, богатый (homo <i>C</i>); хорошо снабжённый, имеющий большие продовольственные запасы (urbs <i>C</i>); богато уставленный (mensa <i>V</i>);\n<b>10)</b> содержательный, обстоятельный (oratio <i>C</i>);\n<b>11)</b> многолюдный (convivium <i>Su</i>); посещаемый многими (domus alicujus <i>T</i>);\n<b>12)</b> законченный, полный (annus <i>C</i>);\n<b>13)</b> загружённый, целиком занятый, поглощённый (negotiis <i>C</i>);\n<b>14)</b> крепкий (vinum <i>CC</i>); подкрепляющий, питательный (cibus <i>CC</i>);\n<b>15)</b> большой, значительный (pecunia <i>C</i>);\n<b>16)</b> несокращённый, полногласный (verbum <i>C</i>): verba plena <i>O etc.</i> многословие; in plenum <i>Sen, PM</i> вообще;\n<b>17)</b> полный, совершенный (concordia <i>O</i>): ad plenum <i>V, PM</i> до конца, вполне, совершенно."],
  ["pleo", "<big>pleo</big>\n\n<b>pleo, —, —, ere</b>\nнаполнять (<i>тк. в приставочных глаголах</i> compleo, expleo <i>etc.</i>)."],
  ["pleonasmus", "<big>pleonasmus</big>\n\n<b>pleonasmus, i</b> <i>m</i> (<i>греч.</i>) <i>грам.</i>\nплеоназм, употребление излишних с точки зрения смысла слов, <i>напр.: inter se mutuo</i> amicissimi <i>Ap</i>; <i>reddere rursus</i> <i>H</i>; sic <i>ore locuta est</i> (Dido) <i>V</i>; <i>сюда же следует отнести и некоторые случаи усиливающего hendiadys, вроде</i>: cupere atque exoptare <i>C</i>; multum decipiturque falliturque <i>M</i>."],
  ["pleraque", "<big>pleraque</big>\n\n<b>pleraque</b> <i>adv.</i> <i>AG</i>\n= plerumque I."],
  ["pleroma", "<big>pleroma</big>\n\n<b>pleroma, atis</b> <i>n</i> (<i>греч.</i>)\nполнота, преизбыток <i>Tert</i>."],
  ["plerumque", "<big>plerumque</big>\n\n<b>I plerumque</b> <i>adv.</i> [plerusque]\nбольшей частью, в большинстве случаев: p. casu, saepe natura <i>C</i> большей частью (это происходит) случайно, но часто — по естественному побуждению || обычно <i>или</i> часто (p., non semper <i>C</i>).\n\n<b>II plerumque</b> <i>n</i> [plerusque]\nбольшая часть (noctis <i>Sl</i>; pleraque ejus insulae <i>QC</i>)."],
  ["plerus", "<big>plerus</big>\n\n<b>plerus, a, um</b> <i>Pac, Cato, C</i>\n= plerusque."],
  ["plerusque", "<big>plerusque</big>\n\n<b>plerusque, pleraque, plerumque</b> (<i>преим.</i> <i>pl.</i> plerique, pleraeque, pleraque — <i>gen.</i> plurimorum, -arum)\n<b>1)</b> большая часть, большинство: pleraque oratione <i>C</i> в большей части речи; plerique omnes <i>Pl, Ter, AG</i> почти все; plerique putant <i>C</i> большинство думает;\n<b>2)</b> большая часть, многие (plerique milites <i>Sl</i> <i>и</i> militum <i>T</i>; pleraeque urbium <i>L</i>; plerique Poenorum <i>C</i>; plerique e Graecis <i>PM</i>): ut plerique meministis <i>C</i> как многие из вас помнят; in plerisque <i>C</i> в большинстве случаев."],
  ["Plestini", "<big>Plestini</big>\n\n<b>Plestini, orum</b> <i>m</i>\nплестины, <i>племя в Умбрии</i> <i>PM</i>."],
  ["pletura", "<big>pletura</big>\n\n<b>pletura, ae</b> <i>f</i>\nпереполнение (кровью), (чрезмерное) полнокровие <i>Veg</i>."],
  ["Pleumoxii", "<big>Pleumoxii</big>\n\n<b>Pleumoxii</b> <i>или</i> <b>Pleumosii, orum</b> <i>m</i>\nплевмоксии, <i>племя в Gallia Belgica</i> (<i>подвластное нервиям</i>) <i>Cs</i>."],
  ["pleuriticus", "<big>pleuriticus</big>\n\n<b>pleuriticus, a, um</b>\nстрадающий колотьём в боку <i>PM</i>: pleuritica passio <i>Is</i> = pleuritis."],
  ["pleuritis", "<big>pleuritis</big>\n\n<b>pleuritis, is</b> <i>f</i> (<i>греч.</i>)\nколотьё в боку <i>Vtr, CA</i>."],
  ["Pleuron", "<big>Pleuron</big>\n\n<b>Pleuron, onis</b> <i>f</i>\nПлеврон, <i>город в Этолии на реке Эвен</i> <i>PM</i>."],
  ["Pleuronius", "<big>Pleuronius</big>\n\n<b>Pleuronius, a, um</b> [Pleuron]\nплевронский <i>O</i>."],
  ["plexi", "<big>plexi</big>\n\n<b>plexi</b> <i>и</i> <b>plexui</b> <i>pf. к</i>\nplecto I."],
  ["Plexippus", "<big>Plexippus</big>\n\n<b>Plexippus, i</b> <i>m</i>\nПлексипп, <i>сын Фестия, брат Алфеи, дядя Мелеагра</i> <i>O</i>."],
  ["plexus", "<big>plexus</big>\n\n<b>I plexus, a, um</b> <i>part. pf. к</i>\nplecto I.\n\n<b>II plexus, us</b> <i>m</i> [plecto I]\nсплетение <i>Man</i> (<i>v. l.</i>)."],
  ["Plias", "<big>Plias</big>\n\n<b>Plias, adis</b> <i>f</i> <i>V, O</i>\n= Pleias."],
  ["plica", "<big>plica</big>\n\n<b>plica, ae</b> <i>f</i> [plico]\nскладка <i>Cato</i>."],
  ["plicatilis", "<big>plicatilis</big>\n\n<b>plicatilis, e</b> [plico]\nскладывающийся, складной (naves <i>PM</i>)."],
  ["plicatrix", "<big>plicatrix</big>\n\n<b>plicatrix, icis</b> <i>f</i>\nплиссировщица <i>Pl</i>."],
  ["plicatura", "<big>plicatura</big>\n\n<b>plicatura, ae</b> <i>f</i> [plico]\nскладывание, свёртывание (vestis <i>PM</i>)."],
  ["plico", "<big>plico</big>\n\n<b>plico, cui, (atum), are</b>\nскладывать, свёртывать (chartam <i>M</i>); свивать (anguis se in sua membra plicans <i>V</i>)."],
  ["Plimyrium", "<big>Plimyrium</big>\n\n<b>Plimyrium, i</b> <i>n</i> <i>v. l.</i>\n= Plem(m)yrium."],
  ["Plinianus", "<big>Plinianus</big>\n\n<b>Plinianus, a, um</b> [Plinius]\nплиниев <i>PM</i>."],
  ["Plinius", "<big>Plinius</big>\n\n<b>Plinius, a, um</b>\nПлиний, <i>римск. nomen; наиболее известны</i>:\n<b>1)</b> C. P. Secundus (Major), <i>римск. естествоиспытатель, автор «Naturalis Historia» в 37 книгах</i> (<i>23-79 гг. н. э.</i>);\n<b>2)</b> C. P. Caecilius (Junior), <i>племянник предыдущего, автор «Epistulae» и «Panegyricum Trajani»</i> (<i>62-113 гг. н. э.</i>);\n<b>3)</b> P. Valerianus, <i>врач времён императора Константина.</i>"],
  ["plinthis", "<big>plinthis</big>\n\n<b>plinthis, idis</b> <i>f</i> (<i>греч.</i>)\nплинтус, нижняя часть пьедестала колонны <i>Vtr</i>."],
  ["plinthium", "<big>plinthium</big>\n\n<b>plinthium, i</b> <i>n</i> (<i>греч.</i>)\nплинтий, циферблат солнечных часов <i>Vtr</i>."],
  ["plinthus", "<big>plinthus</big>\n\n<b>plinthus, i</b> <i>m</i> <i>Vtr</i>\n= plinthis."],
  ["plipio", "<big>plipio</big>\n\n<b>plipio, —, —, are</b>\nкричать (<i>о ястребе</i>) <i>Sl</i>."],
  ["plisimus", "<big>plisimus</big>\n\n<b>plisimus</b> <i>v. l.</i>\n= plusimus."],
  ["Plisthenes", "<big>Plisthenes</big>\n\n<b>Plisthenes, is</b> <i>m</i>\nПлисфен:\n<b>1)</b> <i>сын Пелопа, брат Атрея и Фиеста, отец (по др., дядя) Агамемнона и Менелая</i> <i>O</i>;\n<b>2)</b> <i>сын Фиеста, убитый Атреем</i> <i>SenT</i>."],
  ["Plisthenius", "<big>Plisthenius</big>\n\n<b>Plisthenius (-icus), a, um</b> [Plisthenes]\nплисфенов, <i>поэт.</i> агамемнонов (torus <i>O</i>)."],
  ["Plistonices", "<big>Plistonices</big>\n\n<b>Plistonices, ae</b> <i>m</i>\nПлистоник («побеждающий весьма многих»), <i>прозвище грамматика Апиона</i> <i>PM, AG</i>."],
  ["plodo", "<big>plodo</big>\n\n<b>plodo, —, —, ere</b> <i>Vr, Q</i>\n= plaudo."],
  ["ploeres", "<big>ploeres</big>\n\n<b>ploeres</b> <i>арх.</i>\n= plures."],
  ["ploerus", "<big>ploerus</big>\n\n<b>ploerus, a, um</b> <i>арх.</i>\n= plerus."],
  ["plorabilis", "<big>plorabilis</big>\n\n<b>plorabilis, e</b> [ploro]\nплачевный, жалкий <i>Pers, Cld</i>."],
  ["plorabundus", "<big>plorabundus</big>\n\n<b>plorabundus, a, um</b> [ploro]\nплачущий, рыдающий <i>Pl</i>."],
  ["ploratio", "<big>ploratio</big>\n\n<b>ploratio, onis</b> <i>f</i>\nплач, рыдание: convallis plorationis <i>Aug</i> юдоль слёз."],
  ["plorator", "<big>plorator</big>\n\n<b>plorator, oris</b> <i>m</i> <i>M</i>\n= plorabundus."],
  ["ploratus", "<big>ploratus</big>\n\n<b>ploratus, us</b> <i>m</i> [ploro]\n<b>1)</b> плач, рыдание, вопль <i>Lcr, C, L, QC etc.</i>;\n<b>2)</b> стекание капель <i>или</i> камеди (<i>из дерева</i>) <i>PM</i>."],
  ["ploro", "<big>ploro</big>\n\n<b>ploro, avi, atum, are</b>\n<b>1)</b> горько плакать, отчаянно рыдать: lacrimandum est, non plorandum <i>Sen</i> (по поводу смерти друга) нужно лить слёзы, но нельзя предаваться отчаянию;\n<b>2)</b> оплакивать (juvenem <i>H</i>; mortuum <i>Pt</i>): plorari lacrimis veris <i>J</i> быть искренно оплакиваемым; p. alicui <i>Tib</i> излить своё горе на чьей-л. груди."],
  ["plosor", "<big>plosor</big>\n\n<b>plosor, oris</b> <i>m</i> <i>Sid</i>\n= plausor."],
  ["plost-", "<big>plost-</big>\n\n<b>plost-</b> <i>Cato, H, Vr</i>\n= plaust-."],
  ["plosus", "<big>plosus</big>\n\n<b>plosus, us</b> <i>m</i> <i>Macr</i>\n= plausus."],
  ["Ploti-", "<big>Ploti-</big>\n\n<b>Ploti-</b> <i>v. l.</i>\n= Plauti-."],
  ["plovebat", "<big>plovebat</big>\n\n<b>plovebat</b> (= pluebat) <i>Pt 3 л. sg. impf. к</i>\npluo."],
  ["ploxemum", "<big>ploxemum</big>\n\n<b>ploxemum (-num), i</b> <i>n</i> (<i>галльск.</i>)\nкузов повозки <i>Ctl, Q</i>."],
  ["pluit", "<big>pluit</big>\n\n<b>pluit</b>\n<i>3 л. sg. praes. и pf. к</i> pluo."],
  ["pluma", "<big>pluma</big>\n\n<b>pluma, ae</b> <i>f</i>\n<b>1)</b> перышко, пух, <i>pl.</i> оперение (avium plumae <i>Sen, Ap etc.</i>): dormire in pluma <i>J</i> спать на пуховиках; pluma haud interest <i>погов. Pl</i> (это) решительно всё равно;\n<b>2)</b> первый пушок на подбородке <i>H</i>;\n<b>3)</b> чешуя панциря <i>V, Sl, St</i>."],
  ["plumacium", "<big>plumacium</big>\n\n<b>plumacium (-tium), i</b> <i>n</i> [pluma]\nпостель из перьев, пуховик <i>Eccl</i>."],
  ["plumalis", "<big>plumalis</big>\n\n<b>plumalis, e</b> [pluma]\nпокрытый перьями, оперённый (avis <i>Eccl</i>)."],
  ["plumarius", "<big>plumarius</big>\n\n<b>I plumarius, a, um</b> [pluma]\nвышивальный (ars <i>Vlg, Hier</i>); шитый, златотканый (opera <i>Vlg</i>).\n\n<b>II plumarius, i</b> <i>m</i> [pluma]\nвышивающий золотом перистые узоры <i>Vr Vtr, Vlg</i>."],
  ["plumatile", "<big>plumatile</big>\n\n<b>plumatile, is</b> <i>n</i> [pluma]\n(<i>sc.</i> vestimentum) платье, расшитое перистыми узорами <i>Pl</i>."],
  ["plumatus", "<big>plumatus</big>\n\n<b>plumatus, a, um</b> [plumo]\n<b>1)</b> оперённый, покрытый перышками (corpus <i>Poeta ap. C</i>); пушистый (molli p. lanugine <i>PM</i>);\n<b>2)</b> чешуйчатый (lorica <i>Just</i>);\n<b>3)</b> расшитый перистыми узорами (vestis <i>Vop</i>)."],
  ["plumbago", "<big>plumbago</big>\n\n<b>plumbago, inis</b> <i>f</i>\n<b>1)</b> свинцовый блеск (<i>руда</i>) <i>PM</i>;\n<b>2)</b> <i>бот.</i> свинцовка (<i>Ptumbago Europaea</i>, L.) <i>PM</i>."],
  ["plumbarius", "<big>plumbarius</big>\n\n<b>plumbarius, a, um</b> [plumbum]\n<b>1)</b> свинцовый (metalla <i>PM</i>);\n<b>2)</b> обрабатывающий свинец (artifex <i>Vtr</i>)."],
  ["plumbatae", "<big>plumbatae</big>\n\n<b>plumbatae, arum</b> <i>f</i>\n<b>1)</b> свинцовые шарики (пули) <i>Veg</i>;\n<b>2)</b> бичи с вплетённым свинцом <i>CJ, Eccl</i>."],
  ["plumbatura", "<big>plumbatura</big>\n\n<b>plumbatura, ae</b> <i>f</i> [plumbo]\nзапаивание свинцом <i>Dig</i>."],
  ["plumbatus", "<big>plumbatus</big>\n\n<b>plumbatus, a, um</b> [plumbum]\nсвинцовый, сделанный из свинца (tabulae <i>VM</i>; sagittae <i>PM</i>)."],
  ["plumbea", "<big>plumbea</big>\n\n<b>plumbea, ae</b> <i>f</i> [plumbeus]\nсвинцовое ядро, пуля (ictu plumbeae mortuus <i>Spart</i>)."],
  ["plumbei", "<big>plumbei</big>\n\n<b>plumbei, orum</b> <i>m</i> (<i>sc.</i> nummi)\nсвинцовые, <i>т. е.</i> ничего не стоящие монетки <i>M</i>."],
  ["plumbeum", "<big>plumbeum</big>\n\n<b>plumbeum, i</b> <i>n</i> [plumbeus]\nсвинцовый сосуд <i>Cato, Col, M</i>."],
  ["plumbeus", "<big>plumbeus</big>\n\n<b>plumbeus, a, um</b> [plumbum]\n<b>1)</b> свинцовый (glans <i>Lcr</i>; color <i>PM</i>): plumbei ictus <i>Eccl</i> удары, наносимые свинцовыми бичами;\n<b>2)</b> дрянной (nummus <i>Pl</i>; vinum <i>M</i>; carmen <i>Aus</i>);\n<b>3)</b> тяжёлый, невыносимый (ira <i>Pl</i>; auster <i>H</i>);\n<b>4)</b> тупой: pugio (gladius) p. <i>C</i> свинцовый кинжал (меч), <i>перен.</i> слабый довод;\n<b>5)</b> глупый, тупоумный (asinus <i>Ter</i>); невежественный (in physicis <i>C</i>);\n<b>6)</b> бесчувственный, чёрствый (cor <i>Su</i>)."],
  ["plumbo", "<big>plumbo</big>\n\n<b>plumbo, avi, atum, are</b>\nпокрывать <i>или</i> запаивать свинцом <i>Cato, PM, Dig</i>."],
  ["plumbosus", "<big>plumbosus</big>\n\n<b>plumbosus, a, um</b> [plumbum]\nимеющий сильную примесь (с большим содержанием) свинца <i>PM</i>."],
  ["plumbum", "<big>plumbum</big>\n\n<b>plumbum, i</b> <i>n</i>\n<b>1)</b> (<i>тж.</i> p. nigrum <i>PM</i>) свинец: p. album <i>Cs, PM</i> <i>или</i> candidum <i>PM</i> олово; in manu illius p. aurum fiebat <i>Pt</i> в его руке свинец превращался в золото (<i>о баловне счастья</i>);\n<b>2)</b> свинцовая пуля <i>V, O</i>; свинцовая труба <i>H</i>; свинцовая палочка (<i>род карандаша</i>) <i>Ctl</i>; плеть с вделанным в неё свинцом <i>Eccl</i>;\n<b>3)</b> пятно на глазу <i>PM</i>."],
  ["plumesco", "<big>plumesco</big>\n\n<b>plumesco, —, —, ere</b> [pluma]\nпокрываться перьями, оперяться <i>PM, Vlg, Hier</i>."],
  ["plumeus", "<big>plumeus</big>\n\n<b>plumeus, a, um</b> [pluma]\n<b>1)</b> набитый перьями (culcita <i>Vr, C</i>); пуховый (torus <i>O</i>);\n<b>2)</b> покрытый пушком (cutis <i>Ap</i>);\n<b>3)</b> лёгкий как пух (pondus <i>M</i>);\n<b>4)</b> расшитый (indumenta <i>Eccl</i>)."],
  ["plumiger", "<big>plumiger</big>\n\n<b>plumiger, gera, gerum</b> [pluma + gero]\nпокрытый перьями <i>или</i> пухом, пернатый (anser <i>PM</i>): plumigera series <i>Eccl</i> оперение."],
  ["plumipes", "<big>plumipes</big>\n\n<b>plumipes, pedis</b> <i>adj.</i> [pluma + pes]\nс перьями на ногах, <i>т. е.</i> с окрылёнными ногами, быстрый как птица <i>Ctl</i>."],
  ["plumo", "<big>plumo</big>\n\n<b>plumo, avi, atum, are</b> [pluma]\n<b>1)</b> покрывать перьями: se in avem p. <i>Ap</i> превращаться в птицу;\n<b>2)</b> вышивать перистые узоры <i>PS, Lcn etc.</i>;\n<b>3)</b> оперяться (pulli plumantes <i>AG</i>). — <i>См. тж.</i> plumatus."],
  ["plumosus", "<big>plumosus</big>\n\n<b>plumosus, a, um</b> [pluma]\n<b>1)</b> покрытый перьями <i>или</i> пухом (aucupium <i>Prp</i>);\n<b>2)</b> пушистый (folia <i>PM</i>)."],
  ["plumula", "<big>plumula</big>\n\n<b>plumula, ae</b> <i>f</i> [<i>demin. к</i> pluma]\nперышко <i>или</i> пушинка <i>Col, Ap</i>."],
  ["pluo", "<big>pluo</big>\n\n<b>pluo, plui</b> (<i>арх.</i> <b>pluvi</b>), <b>—, ere</b>\n<b>1)</b> идти (<i>о дожде</i>), <i>преим.</i> <i>impers.</i>: pluet hodie <i>Pl</i> сегодня будет дождь; spatium, qua pluitur et ning(u)itur <i>Ap</i> место, где происходит образование дождя и снега; lapides <i>или</i> lapidibus pluebat <i>L</i> камни сыпались дождём;\n<b>2)</b> ниспосылать дождь (non pluit caelum <i>Eccl</i>; nubes pluunt <i>Aug</i>); <i>перен.</i> обрушивать (p. sulphur et ignem super Sodomam et Gomorrham <i>Vlg</i>)."],
  ["pluralia", "<big>pluralia</big>\n\n<b>pluralia, ium</b> <i>n</i> (<i>sc.</i> nomina)\nслова, стоящие во множественном числе <i>Q</i>: p. tantum слова, употребляющиеся только во множественном числе."],
  ["pluralis", "<big>pluralis</big>\n\n<b>I pluralis, e</b>\nмножественный, многообразный (sermo <i>Dig</i>); <i>грам.</i> множественный (numerus <i>Q</i>).\n\n<b>II pluralis, is</b> <i>m</i> (<i>sc.</i> numerus) <i>грам.</i>\nмножественное число <i>Q</i>."],
  ["pluralitas", "<big>pluralitas</big>\n\n<b>pluralitas, atis</b> <i>f</i>\n<b>1)</b> множественность (librorum <i>Boet</i>);\n<b>2)</b> <i>грам.</i> множественное число."],
  ["pluraliter", "<big>pluraliter</big>\n\n<b>pluraliter</b> [pluralis I] <i>грам.</i>\nво множественном числе (aliquid dicere <i>Sen</i>)."],
  ["plurativum", "<big>plurativum</big>\n\n<b>plurativum, i</b> <i>n</i> <i>AG</i>\n= pluralis II."],
  ["plurativus", "<big>plurativus</big>\n\n<b>plurativus, a, um</b> [plures] <i>грам.</i>\nмножественный (numerus <i>AG</i>)."],
  ["plures", "<big>plures</big>\n\n<b>plures, a</b> (<i>редко</i> <b>ia</b>) (<i>gen.</i> ium) <i>adj.</i> [<i>compar. к</i> multus]\n<b>1)</b> более многочисленные, больше (oratores duo aut plures <i>C</i>): pluribus verbis <i>C</i> более пространно; quid plura (<i>sc.</i> dicam) <i>C</i> к чему ещё говорить, <i>т. е.</i> короче говоря;\n<b>2)</b> (= complures) многие, несколько: pluribus praesentibus <i>Cs</i> в присутствии многих; ad p. se penetrare <i>Pl</i> <i>или</i> abire <i>погов. Pt</i> отправиться к праотцам, умереть."],
  ["pluries", "<big>pluries</big>\n\n<b>pluries (pluriens)</b> <i>adv.</i> [plus]\nмного раз, часто <i>Cs etc.</i> (<i>v. l.</i>)."],
  ["plurifariam", "<big>plurifariam</big>\n\n<b>plurifariam</b> <i>adv.</i> [plus]\n<b>1)</b> во многих местах (aliquid exhibere <i>Su</i>);\n<b>2)</b> разнообразно, различно, на разные лады <i>Dig</i>: p. superari <i>Ap</i> быть превзойдённым многими, уступать многим."],
  ["plurifarius", "<big>plurifarius</big>\n\n<b>plurifarius, a, um</b>\nмногообразный, различный (sermo <i>Sid</i>)."],
  ["pluriformis", "<big>pluriformis</big>\n\n<b>pluriformis, e</b> [plures + forma]\nмногообразный (modus <i>Ap</i>)."],
  ["plurimum", "<big>plurimum</big>\n\n<b>I plurimum, i</b> <i>n</i> [<i>superl. к</i> multum I]\nочень много, больше всего (alicujus rei <i>C etc.</i>): quam p. <i>C</i> как можно больше; plurimi esse <i>C</i> быть в высшей степени ценным; plurimi facere <i>Nep</i> ценить превыше всего; quam plurimo vendere <i>C</i> продать подороже; p., <i>тж.</i> quum p. <i>и</i> ut p. <i>L, PM etc.</i> самое большее.\n\n<b>II plurimum</b> <i>adv.</i> [<i>superl. к</i> multum II]\n<b>1)</b> очень много, больше всего (p. interest <i>C</i>);\n<b>2)</b> большею частью: ibi p. est <i>Ter</i> там он чаще всего бывает."],
  ["plurimus", "<big>plurimus</big>\n\n<b>plurimus, a, um</b> [<i>superl. к</i> multus]\n<b>1)</b> чрезвычайно большой, величайший (labor <i>H</i>): plurimo sudore <i>C</i> с величайшим трудом; plurimum aurum <i>T</i> очень много золота;\n<b>2)</b> очень густой (silva <i>O</i>); роскошнейший (coma <i>O</i>);\n<b>3)</b> сильно вздувшийся (amnis <i>O</i>);\n<b>4)</b> самый искренний, сердечнейший (alicui salutem plurimam dicere <i>Pl, C</i>);\n<b>5)</b> многочисленный (plurimis verbis dicere <i>C</i>);\n<b>6)</b> чрезвычайно жаркий, палящий (sol <i>O</i>);\n<b>7)</b> <i>преим. pl.</i> очень многие, большинство (plurimi hostes <i>или</i> hostium <i>L</i>);\n<b>8)</b> многообразный, различный (plurima mortis imago <i>V</i>)."],
  ["plus", "<big>plus</big>\n\n<b>I plus, pluris</b> <i>n</i> [<i>compar. к</i> multum I]\nбольшая часть, большее количество, больше (p. reddere quam accepisse <i>C</i>): quod p. est <i>L</i> больше того; pluris esse <i>C</i> быть дороже; pluris (<i>арх. abl.</i> plure <i>LM</i>) vendere <i>C</i> дороже продать.\n\n<b>II plus</b> <i>adv.</i> [<i>compar. к</i> multum II]\n<b>1)</b> больше, в большей степени, сильнее (amare <i>C</i>): p. plusque <i>C</i> всё больше и больше; p. (aut) minus <i>Enn, Hirt, Pt etc.</i> более или менее, приблизительно, около; uno (<i>abl.</i>) p. <i>C, L</i> одним больше; p. aequo <i>C</i> больше, чем следует;\n<b>2)</b> чаще (non p. quam semel <i>C</i>);\n<b>3)</b> дольше (p. quam decem dies abesse <i>C</i>)."],
  ["plusculum", "<big>plusculum</big>\n\n<b>I plusculum, i</b> <i>n</i> [plusculus]\nнесколько больше: p. negotii <i>C</i> немного больше труда.\n\n<b>II plusculum</b> <i>adv.</i> [plusculus]\nнесколько больше: p. annum <i>Pl</i> немного более года."],
  ["plusculus", "<big>plusculus</big>\n\n<b>plusculus, a, um</b> [<i>demin. к</i> plus I]\nнесколько больший, в несколько большем количестве (plusculae noctes <i>Ap</i>; p. sal <i>Col</i>)."],
  ["plusimus", "<big>plusimus</big>\n\n<b>plusimus</b> <i>арх. Vr</i>\n= plurimus."],
  ["plusquamperfectum", "<big>plusquamperfectum</big>\n\n<b>plusquamperfectum, i</b> <i>n</i> (<i>sc.</i> tempus)\nпреждепрошедшее время."],
  ["plusscius", "<big>plusscius</big>\n\n<b>plus–scius, a, um</b>\nбольше знающий <i>или</i> более умелый (чем принято думать), <i>т. е.</i> весьма ловкий, себе на уме (mulieres <i>Pt</i>)."],
  ["pluteum", "<big>pluteum</big>\n\n<b>pluteum, i</b> <i>n</i>\n= pluteus."],
  ["pluteus", "<big>pluteus</big>\n\n<b>pluteus, i</b> <i>m</i>\n<b>1)</b> осадный щит (<i>плетёный и обтянутый кожей щит на колёсах для защиты при осадных работах</i>) <i>Pt, Cs, L etc.</i>;\n<b>2)</b> постоянный бруствер на башнях (pluteos vallo addere <i>Cs</i>);\n<b>3)</b> спинка обеденного ложа (ad pluteum stare <i>Su</i>); <i>перен.</i> обеденное ложе (plutei fulcra <i>Prp</i>);\n<b>4)</b> доска, на которую клали покойника, катафалк (supra pluteum jacere <i>M</i>);\n<b>5)</b> пюпитр <i>Pers</i>;\n<b>6)</b> стенная полка <i>J, Dig</i>;\n<b>7)</b> <i>архит.</i> парапет между колоннами <i>Vtr</i>."],
  ["Pluton", "<big>Pluton</big>\n\n<b>Pluto(n), onis</b> <i>m</i> (<i>acc.</i> ona <i>H</i>)\nПлутон, <i>бог подземного царства, сын Сатурна и Реи, брат Юпитера и Нептуна, муж Прозерпины</i> <i>C, V etc.</i>"],
  ["Plutonia", "<big>Plutonia</big>\n\n<b>Plutonia, orum</b> <i>n</i> (<i>sc.</i> loca)\nПлутонии, <i>нездоровая местность в Малой Азии, где, по преданию, находился храм Плутона</i> <i>C</i>."],
  ["Plutonius", "<big>Plutonius</big>\n\n<b>Plutonius, a, um</b> [Pluto(n)]\nплутонов: domus Plutonia <i>H</i> подземный мир, царство теней."],
  ["plutor", "<big>plutor</big>\n\n<b>plutor, oris</b> <i>m</i> [pluo]\nподатель дождя <i>Aug</i>."],
  ["Plutus", "<big>Plutus</big>\n\n<b>Plutus, i</b> <i>m</i> (<i>греч.</i>)\nПлутус, <i>бог богатства</i> <i>Ph</i>."],
  ["pluvi", "<big>pluvi</big>\n\n<b>pluvi</b> <i>арх. pf. к</i>\npluo."],
  ["pluvia", "<big>pluvia</big>\n\n<b>pluvia, ae</b> <i>f</i>\n<b>1)</b> дождь <i>C, V, Col, L</i>;\n<b>2)</b> дождевая вода (admixta p. <i>PM</i>)."],
  ["pluvialis", "<big>pluvialis</big>\n\n<b>pluvialis, e</b> [pluvia]\nдождевой (aqua <i>O</i>); дождливый (dies <i>Sen</i>); нагоняющий дождевые тучи, приносящий дождь (auster <i>V</i>); вырастающий после дождя (fungi <i>O</i>); проливной (imbres <i>T</i>)."],
  ["pluvialiter", "<big>pluvialiter</big>\n\n<b>pluvialiter</b>\nдождём, в виде дождя <i>Is</i>."],
  ["pluviatilis", "<big>pluviatilis</big>\n\n<b>pluviatilis, e</b> [pluvia]\nдождевой (aqua <i>CC, Col</i>)."],
  ["pluviosus", "<big>pluviosus</big>\n\n<b>pluviosus, a, um</b> [pluvia]\nдождливый (hiems <i>PM</i>)."],
  ["pluvium", "<big>pluvium</big>\n\n<b>pluvium, i</b> <i>n</i> <i>Ter</i>\n= impluvium 2."],
  ["pluvius", "<big>pluvius</big>\n\n<b>pluvius, a, um</b>\n<b>1)</b> дождевой (aqua <i>Cato, C, Sl</i>); дождливый (dies <i>Col</i>; caelum <i>Vr</i>): arcus p. <i>H</i> радуга;\n<b>2)</b> ниспосылающий дождь (Juppiter <i>Tib</i>); приносящий дождь (venti <i>H</i>)."],
  ["pneumaticus", "<big>pneumaticus</big>\n\n<b>pneumaticus, a, um</b> (<i>греч.</i>)\nпневматический, действующий сжатым воздухом (organa <i>PM</i>)."],
  ["pnigeus", "<big>pnigeus</big>\n\n<b>pnīge͡us, eos</b> <i>m</i> (<i>греч.</i>)\nглушитель, закрывающий выход сжатому Воздуху в водяном органе <i>Vtr</i>."],
  ["Pnytagoras", "<big>Pnytagoras</big>\n\n<b>Pnytagoras, ae</b> <i>m</i>\nПнитагор, <i>царь Кипра, союзник Александра Македонского</i> <i>QC</i>."],
  ["po'", "<big>po'</big>\n\n<b>po'</b> <i>Q</i>\n= post (po' meridiem)."],
  ["poblicus", "<big>poblicus</big>\n\n<b>poblicus, a, um</b> <i>арх.</i>\n= publicus."],
  ["pocillator", "<big>pocillator</big>\n\n<b>pocillator, oris</b> <i>m</i> [pocillum]\nвиночерпий: p. Phrygius <i>Ap</i> = Ганимед."],
  ["pocillum", "<big>pocillum</big>\n\n<b>pocillum, i</b> <i>n</i> [<i>demin. к</i> poculum]\nбокальчик <i>Cato, L, Su</i>."],
  ["poclum", "<big>poclum</big>\n\n<b>poclum, i</b> <i>n</i> <i>Pl, Eccl</i>\n= poculum."],
  ["poculum", "<big>poculum</big>\n\n<b>poculum, i</b> <i>n</i>\n<b>1)</b> бокал, чаша: eodem poculo bibere <i>погов. Pl</i> пить из той же чаши, <i>т. е.</i> хлебнуть того же горя;\n<b>2)</b> <i>поэт.</i> питьё: pocula vitea <i>V</i> виноградная влага, вино; p. amoris <i>или</i> desiderii <i>H</i> любовный напиток <i>или</i> любовь;\n<b>3)</b> попойка (ad pocula venire <i>V</i>): in poculis <i>C</i> за вином; prae poculis <i>Fl</i> из-за опьянения, (будучи) в хмелю;\n<b>4)</b> (<i>тж.</i> p. mortis <i>C</i>) чаша с ядом (alicui p. dare <i>C</i>);\n<b>5)</b> водопой (venient ad pocula dammae <i>V</i>)."],
  ["podager", "<big>podager</big>\n\n<b>podager, gri</b> <i>m</i> (<i>греч.</i>)\nподагрик <i>Enn, Cld, Aug</i>."],
  ["podagra", "<big>podagra</big>\n\n<b>podagra, ae</b> <i>f</i> (<i>греч.</i>) (<i>тж.</i> podagrae morbus <i>Su</i>)\nподагра (<i>тк. в ногах</i>) <i>C, Sen, H</i>; <i>pl.</i> подагрические боли, приступы подагры (podagrae chiragraeque <i>CC</i>)."],
  ["podagricus", "<big>podagricus</big>\n\n<b>podagricus, a, um</b> [podagra]\nстрадающий подагрой, подагрический <i>CC, Vtr, Sen, Pt</i>."],
  ["podagrosus", "<big>podagrosus</big>\n\n<b>podagrosus, a, um</b> <i>LM, Pl, Lampr, Vop</i>\n= podagricus."],
  ["Podalirius", "<big>Podalirius</big>\n\n<b>Podalirius, i</b> <i>m</i>\nПодалирий:\n<b>1)</b> <i>фессалиец, сын Эскулапия, брат Махаона, врачевавший греков под Троей</i> <i>O</i>;\n<b>2)</b> <i>троянец, один из спутников Энея</i> <i>V</i>."],
  ["poderes", "<big>poderes</big>\n\n<b>poderes (poderis), is</b> <i>m</i> (<i>греч.</i>)\nподир, <i>долгополая одежда священников</i> <i>Vlg, Eccl</i>."],
  ["podex", "<big>podex</big>\n\n<b>podex, dicis</b> <i>m</i>\n<b>1)</b> задний проход <i>H</i>;\n<b>2)</b> зад <i>J</i>."],
  ["podismo", "<big>podismo</big>\n\n<b>podismo, avi, atum, are</b> [podismus]\nизмерять футами <i>Boet</i>."],
  ["podismus", "<big>podismus</big>\n\n<b>podismus, i</b> <i>m</i> (<i>греч.</i>)\nобмер в футах <i>Veg, Frontin, Boet</i>."],
  ["podium", "<big>podium</big>\n\n<b>podium, i</b> <i>n</i> (<i>греч.</i>)\n<b>1)</b> общий постамент для колонн <i>Vtr</i>;\n<b>2)</b> цоколь, консоль (<i>лат.</i> suggestus) <i>Pall</i>;\n<b>3)</b> филёнка вдоль нижней части стены <i>Vtr, PJ, Dig</i>;\n<b>4)</b> подиум, <i>выступ (балкон) в амфитеатре или цирке для самых знатных зрителей</i> <i>PM, Su, J, Spart</i>."],
  ["Poeantiades", "<big>Poeantiades</big>\n\n<b>Poeantiades, ae</b> <i>m</i>\nсын Пеанта, <i>т. е.</i> Филоктет <i>O</i>."],
  ["Poeantius", "<big>Poeantius</big>\n\n<b>I Poeantius, a, um</b> [Poeas]\nпеантов: Poeantia proles <i>или</i> heros <i>O</i> = Филоктет.\n\n<b>II Poeantius, i</b> <i>m</i> <i>O</i>\n= Philoctetes."],
  ["Poeas", "<big>Poeas</big>\n\n<b>Poeas, antis</b> <i>m</i>\nПеант, <i>фессалиец, отец Филоктета</i> <i>O</i>."],
  ["Poecile", "<big>Poecile</big>\n\n<b>Poecile, es</b> <i>f</i> (<i>греч.</i> «пёстрый», «расписной»)\nПойкиле:\n<b>1)</b> <i>галерея в Афинах, известная своей росписью</i> <i>Nep, PM</i>;\n<b>2)</b> <i>такая же галерея на Тибуртинской даче императора Адриана</i> <i>Spart</i>."],
  ["Poediculi", "<big>Poediculi</big>\n\n<b>Poediculi, orum</b> <i>m</i>\n<i>племя в южн. Италии</i> <i>Just, PM</i>."],
  ["poema", "<big>poema</big>\n\n<b>poema, atis</b> (<i>gen. pl.</i> um <i>и</i> orum, <i>dat. abl.</i> ibus <i>и</i> is) <i>n</i> (<i>греч.</i>)\nпоэма, стихотворение (p. facere, componere, condere <i>C</i>, scribere, pangere <i>H</i>); (<i>иногда</i> <i>pl.</i>) поэзия: p. loquens pictura est <i>rhH</i> поэзия есть говорящая живопись."],
  ["poematium", "<big>poematium</big>\n\n<b>poematium, i</b> <i>n</i> (<i>греч.</i>)\nстихотвореньице <i>PJ, Aus</i>."],
  ["poena", "<big>poena</big>\n\n<b>poena, ae</b> <i>f</i>\n<b>1)</b> наказание, кара: p. legum <i>Nep</i> наказание, установленное законами; viginti quinque aeris p. <i>LXIIT</i> штраф (пеня) в 25 ассов; p. mortis <i>C, Su</i>, <i>тж.</i> p. capitis <i>Cs, C</i> (vitae <i>C</i>) смертная казнь; poenam (poenas) dare (solvere, persolvere, reddere, pendere, expendere) <i>C, L etc.</i> уплатить пеню, <i>т. е.</i> понести наказание, быть наказанным <i>или</i> поплатиться; nisi valentior fuissem, dedissem poenas <i>Pt</i> если бы я не был сильнее (своего противника), пришлось бы мне плохо; poenam (poenas) petere (repetere, capere, sumere) ab aliquo alicujus rei <i>C etc.</i> наказать кого-л. за что-л.; poenas dare alicui <i>C, Sl</i> быть наказанным кем-л.; teneri poena <i>C</i> подлежать наказанию <i>или</i> <i>PS</i> быть наказуемым; in poenam cecinit <i>Ap</i> (Марсий) доигрался до того, что был наказан; poenam habere <i>L</i> подвергнуться наказанию, <i>но</i>: poenas habere ab aliquo <i>L</i> отомстить кому-л.; poenas capere de aliquo <i>L</i> мстить кому-л.; extra poenam esse <i>L</i> оставаться безнаказанным; poenam pati alicujus rei <i>Dig</i> нести наказание за что-л.; innocentium poenae <i>C</i> отмщение за (казнь) невинных; poenas alicujus persequi <i>C</i> <i>или</i> poenas capere pro aliquo <i>Sl</i> отомстить за кого-л.;\n<b>2)</b> страдание, мучение, мука (finem imponere poenis <i>VF</i>): pariendi poenae <i>PM</i> родовые муки."],
  ["poenalis", "<big>poenalis</big>\n\n<b>poenalis, e</b> [poena]\n<b>1)</b> служащий наказанием, несущий с собой наказание, карающий (gladius <i>Amm</i>); claustra poenalia <i>Sol</i> тюрьма || карательный (lex <i>G</i>);\n<b>2)</b> подлежащий наказанию <i>или</i> осуждённый (homo <i>CJ</i>)."],
  ["poenaliter", "<big>poenaliter</big>\n\n<b>poenaliter</b>\n<b>1)</b> в порядке наказания (interire <i>Amm</i>);\n<b>2)</b> достойным наказания образом (peccare <i>Aug</i>)."],
  ["poenarius", "<big>poenarius</big>\n\n<b>poenarius, a, um</b> [poena]\nкарательный (actio <i>Q</i>)."],
  ["Poeni", "<big>Poeni</big>\n\n<b>Poeni, orum</b> (<i>реже</i> <b>um</b>) <i>m</i>\n<b>1)</b> пунийцы, карфагеняне <i>C, L, V etc.</i>;\n<b>2)</b> финикияне (<i>как предки пунийцев</i>) <i>C</i>."],
  ["Poenic-", "<big>Poenic-</big>\n\n<b>Poenic-</b> <i>Vr, O etc.</i>\n= Punic-."],
  ["Poeninus", "<big>Poeninus</big>\n\n<b>Poeninus, a, um</b> <i>L, T, PM</i>\n= Penninus."],
  ["poenio", "<big>poenio</big>\n\n<b>poenio</b> <i>и</i> <b>poenior</b> <i>арх. C, AG, Ap</i>\n= punio <i>и</i> punior."],
  ["poenitentia", "<big>poenitentia</big>\n\n<b>poenitentia, ae</b> (<i>тж.</i> <b>pae-</b>) <i>f</i> [poeniteo]\nраскаяние (alicujus rei <i>Q, PJ</i>): alicujus rei poenitentiam agere <i>VM, Sen etc.</i> (gerere <i>Eccl</i>) раскаиваться (каяться) в чём-л.; bona fide poenitentiam emendare <i>Pt</i> чистосердечным раскаянием загладить свою вину."],
  ["poeniteo", "<big>poeniteo</big>\n\n<b>poeniteo, tui, —, ere</b>\n(тж. рае-)\n<b>1)</b> раскаиваться, сожалеть (alicujus rei <i>C, Sl</i> <i>и</i> de aliqua re <i>Su</i>): nobis poenitendum est alicujus rei <i>C</i> нам следует (по)жалеть о чём-л.; sapientis est nihil, quod p. possit, facere <i>C</i> мудрецу несвойственно делать то, о чём ему пришлось бы (потом) жалеть; haud poenitendus <i>L</i> отнюдь не вызывающий сожаления, <i>т. е.</i> вполне достойный || <i>преим.</i> <i>impers.</i>: poenitet eum alicujus rei <i>C</i> он раскаивается в чём-л.; poenitet me hoc fecisse <i>C, Sen</i> (quod hoc feci <i>C, L</i>) я жалею о том, что сделал это; non paenitebit <i>Pt</i> раскаиваться не придётся (не пожалеете);\n<b>2)</b> <i>impers.</i> быть недовольным, досадовать (me poenitet alicujus rei <i>C etc.</i>): haud me poenitet sententiae eorum esse <i>L</i> я ничего не имею против их мнения (согласен с их мнением); an vos poenitet, quod salvum exercitum traduxerim? <i>Cs</i> или вы недовольны тем, что я переправил армию в полной сохранности?; memet mei poenitet <i>C</i> я сам на себя негодую."],
  ["poeniteor", "<big>poeniteor</big>\n\n<b>poeniteor, —, eri</b> <i>Vlg, Eccl</i>\n= poeniteo."],
  ["poenitio", "<big>poenitio</big>\n\n<b>poenitio, onis</b> <i>f</i> <i>арх. v. l.</i>\n= punitio."],
  ["poenitudo", "<big>poenitudo</big>\n\n<b>poenitudo, inis</b> <i>f</i> <i>Pac, Aus, Sid, CA, Eccl</i>\n= poenitentia."],
  ["Poenulus", "<big>Poenulus</big>\n\n<b>Poenulus, i</b> <i>m</i> [<i>demin. к</i> Poenus]\n«Молодой пуниец», <i>название комедии Плавта.</i>"],
  ["Poenus", "<big>Poenus</big>\n\n<b>I Poenus, i</b> <i>m</i> [<i>sg. к</i> Poeni]\n<b>1)</b> пуниец, карфагенянин <i>Pl, L</i>: uterque P. <i>H</i> карфагеняне в Африке и в Испании;\n<b>2)</b> <i>C</i> = Hannibal.\n\n<b>II Poenus, a, um</b>\nпунический, карфагенский (navita <i>H</i>; sermo <i>St</i>): nullus me P. Poenior <i>Pl</i> никто лучше меня не знает пунического языка."],
  ["poesis", "<big>poesis</big>\n\n<b>poesis, is</b> (<i>acc.</i> in) <i>f</i> (<i>греч.</i>)\nпоэзия, поэтическое искусство <i>C, H, Q</i>."],
  ["poeta", "<big>poeta</big>\n\n<b>poeta, ae</b> <i>m</i> (<i>греч.</i>)\n<b>1)</b> изобретатель, мастер (tu p. es ad eam rem <i>Pl</i>);\n<b>2)</b> поэт, стихотворец <i>Pl, Vr, C etc.</i>"],
  ["Poetelius", "<big>Poetelius</big>\n\n<b>Poetelius, a, um</b>\nПетелий, <i>римск. nomen</i> <i>L</i>."],
  ["poetica", "<big>poetica</big>\n\n<b>I poetica, ae</b> <i>f</i> (<i>греч.</i>)\nпоэтика, поэтическое искусство <i>C, Tert</i>.\n\n<b>II poetica, orum</b> <i>n</i> [poeticus]\nпоэтические произведения, стихи, поэзия <i>Sen</i>."],
  ["poetice", "<big>poetice</big>\n\n<b>I poetice, es</b> <i>f</i> <i>Vr, Nep, PJ</i>\n= poetica I.\n\n<b>II poetice</b> [poeticus]\nпоэтически (loqui <i>C, PJ</i>, <i>Pt</i>)."],
  ["poeticus", "<big>poeticus</big>\n\n<b>poeticus, a, um</b>\nпоэтический (verbum <i>Vr, C</i>; licentia <i>Q, Lact</i>): poeticis numeris <i>Col</i> в стихах."],
  ["poeto", "<big>poeto</big>\n\n<b>poeto, —, —, are</b> <i>Fronto, Aus</i>\n= poetor."],
  ["poetor", "<big>poetor</big>\n\n<b>poetor, —, ari</b> <i>depon.</i>\nзаниматься поэзией, сочинять стихи <i>Enn</i>."],
  ["Poetovio", "<big>Poetovio</big>\n\n<b>Poetovio, onis</b> <i>f</i>\nПетовион, <i>город в Паннонии</i> <i>T</i>."],
  ["poetria", "<big>poetria</big>\n\n<b>poetria, ae</b> <i>C, O</i> <i>и</i> <b>poetris, idis</b> <i>f</i>\nпоэтесса <i>Pers</i>."],
  ["pogonias", "<big>pogonias</big>\n\n<b>pogonias, ae</b> <i>m</i> (<i>греч.</i>)\n«бородатая» комета <i>Sen</i>."],
  ["pol", "<big>pol</big>\n\n<b>I pol!</b> <i>interj.</i> [Pollux]\nклянусь Поллуксом!, право!, подлинно! <i>Pl, Ter, C</i>.\n\n<b>II pol-</b> [<i>из</i> por- = pro-]\n<i>глагольная приставка</i> (pol–liceor, pol–luo)."],
  ["polea", "<big>polea</big>\n\n<b>polea, ae</b> <i>f</i> (<i>сир.</i>)\nпомёт новорождённого ослёнка <i>PM</i>."],
  ["Polemon", "<big>Polemon</big>\n\n<b>Polemo(n), onis</b> <i>m</i>\nПолемон:\n<b>1)</b> <i>афинский философ IV-III вв. до н. э., ученик Ксенократа, учитель Зенона и Аркесилая</i> <i>C, H, VM</i>;\n<b>2)</b> <i>имя двух понтийских царей, из которых старший царствовал с 34 г. до н. э. до 1 г. н. э.</i> <i>Su, Eutr</i>;\n<b>3)</b> командующий флотом Александра Македонского <i>QC</i>."],
  ["Polemoneus", "<big>Polemoneus</big>\n\n<b>Polemoneus, a, um</b> [Polemo(n) 1]\nполемонов (Stoicus <i>C</i>)."],
  ["Polemoniacus", "<big>Polemoniacus</big>\n\n<b>Polemoniacus, a, um</b> <i>adj. к</i>\nPolemo(n) 2 (Pontus <i>AV, Eutr, Vop</i>)."],
  ["polenta", "<big>polenta</big>\n\n<b>I polenta, ae</b> <i>f</i>\nячменная крупа <i>Cato, Col, O, PM etc.</i>\n\n<b>II polenta, orum</b> <i>n</i> <i>Macr</i>\n= polenta I."],
  ["polentarius", "<big>polentarius</big>\n\n<b>polentarius, a, um</b> [polenta]\nячменный <i>Pl, Ap</i>."],
  ["polia", "<big>polia</big>\n\n<b>polia, ae</b> <i>f</i> (<i>греч.</i>)\nтабун лошадей <i>Dig</i>."],
  ["polimen", "<big>polimen</big>\n\n<b>polimen, inis</b> <i>n</i> [polio I]\n<b>1)</b> украшение (baltei <i>Ap</i>);\n<b>2)</b> <i>Eccl</i> = testiculus."],
  ["polimenta", "<big>polimenta</big>\n\n<b>polimenta, orum</b> <i>n</i> <i>Pl</i>\n(<i>v. l.</i>) = testiculi."],
  ["polio", "<big>polio</big>\n\n<b>I polio, ivi, itum, ire</b>\n<b>1)</b> делать гладким, шлифовать, полировать (marmora <i>PM</i>); <i>перен.</i> придавать лоск, отделывать (orationem <i>C</i>): polivisse materiam versibus senariis <i>Ph</i> изложить содержание (басен) в шестистопных стихах;\n<b>2)</b> штукатурить, белить (columnas albo <i>L</i>);\n<b>3)</b> тщательно обрабатывать (agros <i>Enn</i>);\n<b>4)</b> лощить (vestes <i>PM</i>).\n\n<b>II polio, onis</b> <i>m</i>\nваляльщик, сукновал <i>Dig</i>."],
  ["polion", "<big>polion</big>\n\n<b>polion (-ium), i</b> <i>n</i> <i>бот.</i>\nполиум (<i>Teucrium polium</i>, L.) <i>PM</i>."],
  ["Poliorcetes", "<big>Poliorcetes</big>\n\n<b>Poliorcetes, ae</b> <i>m</i>\nПолиоркет («Осаждающий города»), <i>прозвище Деметрия Македонского</i> (<i>337-283 гг. до н. э.</i>) <i>Vtr, Sen</i>."],
  ["polite", "<big>polite</big>\n\n<b>polite</b> [politus]\nтщательно, художественно, тонко, красиво, изящно (eloqui aliquid <i>C</i>; politius de aliqua re proferri <i>C</i>)."],
  ["Polites", "<big>Polites</big>\n\n<b>Polites, ae</b> <i>m</i>\nПолит, <i>сын Приама, убитый Пирром</i> <i>V</i>."],
  ["politia", "<big>politia</big>\n\n<b>politia, ae</b> <i>f</i> (<i>греч.</i>)\n<b>1)</b> государственное устройство <i>Eccl</i>;\n<b>2)</b> «Государство», <i>заглавие сочинения Платона</i> <i>C</i>."],
  ["politicus", "<big>politicus</big>\n\n<b>politicus, a, um</b> (<i>греч.</i>)\n<b>1)</b> касающийся государственных дел, политический (libri <i>Caelius ap. C</i>);\n<b>2)</b> рассматривающий политические вопросы (philosophus <i>C</i>)."],
  ["politio", "<big>politio</big>\n\n<b>politio, onis</b> <i>f</i> [polio]\n<b>1)</b> разглаживание, шлифовка, полировка (speculi <i>Vtr</i>);\n<b>2)</b> покрывание штукатуркой, побелка <i>Vtr</i>;\n<b>3)</b> обработка, возделывание (agrorum <i>Cato</i>)."],
  ["politor", "<big>politor</big>\n\n<b>politor, oris</b> <i>m</i> [polio]\n<b>1)</b> полировщик, шлифовальщик (gemmarum <i>Eccl</i>);\n<b>2)</b> обрабатывающий землю, земледелец-издольщик <i>Cato, Dig</i>."],
  ["Politorium", "<big>Politorium</big>\n\n<b>Politorium, i</b> <i>n</i>\nПолиторий, <i>город в Латии, к югу от Тибра</i> <i>L, PM</i>."],
  ["politura", "<big>politura</big>\n\n<b>politura, ae</b> <i>f</i> [polio]\nполировка, шлифовка (marmoris <i>PM</i>); лощение (chartae <i>PM</i>); отделка (orationis <i>Sen</i>)."],
  ["politus", "<big>politus</big>\n\n<b>politus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> polio;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> художественно обработанный, со вкусом отделанный, изящный (oratio <i>C</i>); тонкий (judicium <i>C</i>);\n<b>2)</b> образованный (homo omni doctrina politissimus <i>C</i>)."],
  ["polium", "<big>polium</big>\n\n<b>polium, i</b> <i>n</i> <i>CC</i>\n= polion."],
  ["Polla", "<big>Polla</big>\n\n<b>Polla, ae</b> <i>f</i>\n(= Paul(l)a)\n<b>1)</b> Valeria P., <i>жена Д. Брута</i> <i>C</i>;\n<b>2)</b> Argentaria P., <i>жена поэта Лукана, а впоследствии поэта Стация</i> <i>M, St</i>."],
  ["pollen", "<big>pollen</big>\n\n<b>pollen, inis</b> <i>n</i>\n<b>1)</b> мука самого мелкого помола <i>Ter, CC, PM, CA</i>;\n<b>2)</b> пыль, порошок (turis <i>Col</i>): p. piperis <i>CC</i> молотый перец."],
  ["pollens", "<big>pollens</big>\n\n<b>pollens, entis</b>\n<b>1.</b> <i>part. praes. к</i> polleo;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> могущественный (dea <i>Naev</i>); богатый, знатный (matrona <i>Ap</i>): p. alicujus rei <i>Pl</i>, aliqua re <i>Sl, O, VF</i> <i>или</i> aliquid <i>SenT</i> сильный в чём-л.;\n<b>2)</b> сильнодействующий (herba <i>O</i>)."],
  ["pollenter", "<big>pollenter</big>\n\n<b>pollenter</b>\nмогущественно (caesa redit pollentius Hydra <i>Cld</i>)."],
  ["pollentia", "<big>pollentia</big>\n\n<b>I pollentia, ae</b> <i>f</i> [polleo]\nсила, могущество <i>L</i>.\n\n<b>II Pollentia, ae</b> <i>f</i>\nПолленция, <i>богиня силы и мощи</i> <i>L</i>.\n\n<b>III Pollentia, ae</b> <i>f</i>\nПолленция:\n<b>1)</b> <i>город в Пицене</i> (<i>ныне</i> Urbisaglia) <i>PM</i>;\n<b>2)</b> <i>город в Лигурии у слияния рек Стура и Танар</i> <i>C, Col, Sil</i>."],
  ["Pollentinus", "<big>Pollentinus</big>\n\n<b>I Pollentinus, a, um</b> [Pollentia III, 2]\nполлентийский (lanae <i>M</i>; plebs <i>Su</i>).\n\n<b>II Pollentinus, i</b> <i>m</i>\nжитель города Pollentia (III, 1) <i>PM</i>."],
  ["polleo", "<big>polleo</big>\n\n<b>polleo, ui, —, ere</b> [potis + valeo]\n<b>1)</b> иметь силу, быть в силе (posse pollereque <i>L</i>);\n<b>2)</b> иметь силу, влияние, значение (armis, gratia, nobilitate <i>T</i>): magis p. <i>Ap</i> быть сильнее (значительнее); videtin'viginti minae quid pollent quidve possunt? <i>Pl</i> видите, как много значат 20 мин?;\n<b>3)</b> быть действительным (действенным), целебным (adversus scorpiones <i>PM</i>; contra morbum <i>PM</i>);\n<b>4)</b> расцениваться, цениться (eadem vitis aliud in aliis terris pollet <i>PM</i>);\n<b>5)</b> быть богатым, владеть (aliqua re <i>Su, Ap</i>)."],
  ["pollex", "<big>pollex</big>\n\n<b>pollex, icis</b> <i>m</i> [polleo]\n<b>1)</b> (<i>тж.</i> digitus p. <i>Cs</i>) большой палец: pollicem premere <i>PM</i> пригнуть большой палец (<i>к указа тельному — знак одобрения</i>); pollicem vertere <i>J etc.</i> поворачивать большой палец к груди (<i>в знак того, чтобы победитель добил раненого гладиатора</i>); pollice utroque laudare <i>H</i> сильно расхваливать;\n<b>2)</b> шишкообразный выступ на древесном стволе <i>Col, PM</i>;\n<b>3)</b> римск. дюйм = 1/12 pes = 24,6 мм <i>Cato, Cs</i>."],
  ["pollicaris", "<big>pollicaris</big>\n\n<b>pollicaris, e</b> [pollex 3]\nдюймовый (latitudo <i>PM</i>)."],
  ["polliceo", "<big>polliceo</big>\n\n<b>polliceo, —, —, ere</b> <i>V, O, Dig</i>\n= polliceor."],
  ["polliceor", "<big>polliceor</big>\n\n<b>pol–liceor, licitus sum, eri</b> <i>depon.</i>\nобещать, сулить (alicui aliquid <i>и</i> aliquid facere <i>C, Cs etc.</i>): p. montes auri <i>Ter</i> <i>и</i> maria montesque <i>погов. Sl</i> = сулить золотые горы; bene <i>Sl</i> (benigne <i>C, L</i>) p. давать хорошие обещания."],
  ["pollicitatio", "<big>pollicitatio</big>\n\n<b>pollicitatio, onis</b> <i>f</i> [polliceor]\nобещание <i>Pl, rhH, C, Cs etc.</i>"],
  ["pollicitator", "<big>pollicitator</big>\n\n<b>pollicitator, oris</b> <i>m</i>\nдающий обещания <i>Tert</i>."],
  ["pollicitor", "<big>pollicitor</big>\n\n<b>pollicitor, atus sum, ari</b> <i>depon.</i> [<i>intens. к</i> polliceor]\nобещать, сулить <i>Pl, Ter, Sl etc.</i>"],
  ["pollicitum", "<big>pollicitum</big>\n\n<b>pollicitum, i</b> <i>n</i> [polliceor]\nобещанное, обещание, посул <i>Col</i>: poli ici tis dives quilibet esse potest <i>O</i> посулами всякий может быть богат."],
  ["pollicitus", "<big>pollicitus</big>\n\n<b>pollicitus, a, um</b> <i>part. pf. к</i>\npolliceor; <i>иногда</i> <i>pass.</i> обещанный (fides <i>O</i>)."],
  ["pollictor", "<big>pollictor</big>\n\n<b>pollictor, oris</b> <i>m</i> [pollingo]\nсовершающий омовение трупов <i>Pl, Vr</i> (<i>v. l.</i>)."],
  ["pollinarius", "<big>pollinarius</big>\n\n<b>pollinarius, a, um</b> [pollen]\nмучной (cribrum <i>Pl, PM</i>)."],
  ["pollinatus", "<big>pollinatus</big>\n\n<b>pollinatus, a, um</b> [pollen]\nтонко просеянный (farina <i>PM</i>)."],
  ["pollinctor", "<big>pollinctor</big>\n\n<b>pollinctor, oris</b> <i>m</i>\n<b>1)</b> <i>Pl, M, Dig, Eccl</i> = pollictor;\n<b>2)</b> совершающий погребение <i>Vlg</i>."],
  ["pollinctus", "<big>pollinctus</big>\n\n<b>pollinctus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> pollingo;\n<b>2.</b> <i>adj.</i> <i>перен.</i>\n<b>1)</b> близкий к могиле (corpus <i>VM</i>);\n<b>2)</b> испорченный (voluptas <i>Sen</i>)."],
  ["pollingo", "<big>pollingo</big>\n\n<b>pollingo, linxi, linctum, ere</b>\nомывать и умащивать (мертвецов) (p. aliquem <i>Pl, Ap</i>). — <i>См. тж.</i> pollinctus."],
  ["Pollio", "<big>Pollio</big>\n\n<b>Pollio, onis</b> <i>m</i>\nПоллион, <i>cognomen в родах Азиниев и Требеллиев</i> <i>V, H</i>."],
  ["pollis", "<big>pollis</big>\n\n<b>pollis, inis</b> <i>m, f</i> <i>v. l.</i>\n= pollen."],
  ["Pollius", "<big>Pollius</big>\n\n<b>Pollius, a, um</b>\nполлийский: Pollia tribus <i>L</i> Поллийская триба, <i>одна из римск. триб.</i>"],
  ["pollubrum", "<big>pollubrum</big>\n\n<b>pollubrum, i</b> <i>n</i> <i>v. l.</i>\n= polubrum."],
  ["polluceo", "<big>polluceo</big>\n\n<b>polluceo, luxi, luctum, ere</b>\n<b>1)</b> ставить на стол, предлагать, угощать (p. pisces <i>PM</i>): virgis polluctus <i>ирон. Pl</i> получивший побои;\n<b>2)</b> <i>культ.</i> приносить в жертву (Herculi decimam partem <i>Pl</i>)."],
  ["Polluces", "<big>Polluces</big>\n\n<b>Polluces, is</b> <i>m</i> <i>Pt</i>\n= Pollux."],
  ["pollucibilis", "<big>pollucibilis</big>\n\n<b>pollucibilis, e</b> [polluceo]\nдостойный богов, <i>т. е.</i> великолепный (cena <i>Macr</i>; victus <i>Symm</i>)."],
  ["pollucibiliter", "<big>pollucibiliter</big>\n\n<b>pollucibiliter</b> [polluceo]\nобильно, великолепно (obsonare <i>Pl</i>)."],
  ["polluctum", "<big>polluctum</big>\n\n<b>polluctum, i</b> <i>n</i> [polluceo]\nжертвенная трапеза <i>Pl, Vr, PM, Macr</i>."],
  ["polluctura", "<big>polluctura</big>\n\n<b>polluctura, ae</b> <i>f</i> [polluceo]\nвеликолепный пир, пышная трапеза <i>Pl</i>."],
  ["polluo", "<big>polluo</big>\n\n<b>polluo, ui, utum, ere</b> [por + lutum]\n<b>1)</b> марать, пачкать (ora cruore <i>O</i>);\n<b>2)</b> бесчестить, осквернять (jura scelere <i>C</i>); оскорблять (Jovem <i>Prp</i>);\n<b>3)</b> нарушать, ломать (polluta pax <i>V</i>)."],
  ["pollutio", "<big>pollutio</big>\n\n<b>pollutio, onis</b> <i>f</i> [polluo]\nосквернение <i>Pall, Vlg, Eccl</i>."],
  ["pollutrix", "<big>pollutrix</big>\n\n<b>pollutrix, icis</b> <i>f</i>\nосквернительница (sacrorum <i>Aug</i>)."],
  ["pollutus", "<big>pollutus</big>\n\n<b>pollutus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> polluo;\n<b>2.</b> <i>adj.</i> греховный, порочный (scelestus pollutusque <i>Ap</i>); преступный (licentia <i>Sl</i>)."],
  ["Pollux", "<big>Pollux</big>\n\n<b>Pollux, ucis</b> <i>m</i>\nПоллукс, <i>сын Тиндарея и Леды, брат Кастора</i>: geminus <i>или</i> uterque P. <i>H</i> = Castor et P.; Castora de Polluce facere aliquem <i>погов. M</i> превратить кого-л. из Поллукса в Кастора (<i>Кастору приписывалось искусство кулачного боя, а Поллуксу — искусство верховой езды).</i> — <i>См.</i> Tyndaridae."],
  ["polluxi", "<big>polluxi</big>\n\n<b>polluxi</b> <i>pf. к</i>\npolluceo."],
  ["polubrum", "<big>polubrum</big>\n\n<b>polubrum, i</b> <i>n</i>\nтаз (<i>для омовения рук</i>) <i>LA, Vr</i>."],
  ["polulus", "<big>polulus</big>\n\n<b>polulus, a, um</b> <i>арх. Cato</i>\n= paululus."],
  ["polus", "<big>polus</big>\n\n<b>polus, i</b> <i>m</i> (<i>греч.</i>; <i>лат.</i> vertex)\n<b>1)</b> полюс: p. (<i>тж.</i> p. glacialis <i>или</i> gelidus) <i>O</i> северный полюс; p. australis <i>O</i> южный полюс;\n<b>2)</b> небесный свод <i>Acc, H, O</i>; <i>поэт.</i> небо (polo dimovere umbram <i>V</i>);\n<b>3)</b> полярная звезда <i>Vtr</i>."],
  ["Polyaenus", "<big>Polyaenus</big>\n\n<b>Polyaenus, ī</b> <i>m</i>\nПолиен, <i>математик, друг и последователь Эпикура</i> <i>C</i>."],
  ["polyandrion", "<big>polyandrion</big>\n\n<b>polyandrion, ī</b> <i>n</i> (<i>греч.</i>)\nкладбище <i>Eccl</i>."],
  ["Polybius", "<big>Polybius</big>\n\n<b>Polybius, ī</b> <i>m</i>\nПолибий, <i>греч. историк (204-122 гг. до н. э.), родом из Мегалополя (Аркадия), гиппарх (начальник конницы) Ахейского союза; в 166 г. до н. э. попал в качестве заложника в Рим, где подружился со Сципионами; умер в Аркадии; из 40 книг его «Истории» дошло полностью лишь 5 (преим. история Рима с 220 по 168 г. до н. э.</i>) <i>C, L, Amm</i>."],
  ["polybrum", "<big>polybrum</big>\n\n<b>polybrum</b> <i>v. l.</i>\n= polubrum."],
  ["Polybus", "<big>Polybus</big>\n\n<b>Polybus, ī</b> <i>m</i>\nПолиб:\n<b>1)</b> <i>царь Коринфа, при дворе которого воспитывался Эдип <i>St</i>;</i>\n<b>2)</b> <i>один из претендентов на руку Пенелопы</i> <i>O</i>."],
  ["Polycletus", "<big>Polycletus</big>\n\n<b>Polyclētus</b> (<i>чаще</i> <b>Polyclītus</b>), <b>ī</b> <i>m</i>\nПоликлет, <i>родом из Сикиона, ваятель, зодчий и писатель по вопросам искусства (середина <i>V</i> в. до н. э.), глава «Аргивской школы»</i> <i>C, M</i>."],
  ["Polycrates", "<big>Polycrates</big>\n\n<b>Polycratēs, is</b> <i>m</i>\nПоликрат, <i>богатый и щедрый тиран о-ва Самос (середина VI в. до н. э.), друг египетского царя Амасиса; в 522 г. был распят в Магнесии на кресте персидским сатрапом Оретом</i> <i>C, Q, Ap</i>."],
  ["Polydamas", "<big>Polydamas</big>\n\n<b>Polydamās, antis</b> <i>m</i>\nПолидамант:\n<b>1)</b> <i>троянец, сын Пантоя и Фронтиды, храбрый воин; друг Гектора</i> <i>Prp, O, Pers</i>;\n<b>2)</b> <i>фракиец, известный атлет времён Сократа</i> <i>VM</i>."],
  ["Polydectes", "<big>Polydectes</big>\n\n<b>Polydectēs, ae</b> <i>m</i>\nПолидект, <i>царь о-ва Серифос, воспитатель Персея, превращенный последним в камень за попытку принудить Данаю к замужеству</i> <i>O</i>."],
  ["Polydoreus", "<big>Polydoreus</big>\n\n<b>Polydōrēus, a, um</b> [Polydorus]\nполидоров <i>O</i>."],
  ["Polydorus", "<big>Polydorus</big>\n\n<b>Polydōrus, ī</b> <i>m</i>\nПолидор, <i>сын Приама и Гекубы, отправленный родителями к фракийскому царю Полиместору, который, узнав о гибели Трои, убил своего питомца, чтобы овладеть его сокровищами</i> <i>C, V, O</i>."],
  ["Polygnotus", "<big>Polygnotus</big>\n\n<b>Polygnōtus, ī</b> <i>m</i>\nПолигнот, <i>греческий живописец с о-ва Фасос, с 463 г. до н. э. работавший в Афинах, автор картины «Марафонская битва»</i> (<i>в афинском Poecile</i>) <i>C, PM</i>."],
  ["polygonius", "<big>polygonius</big>\n\n<b>polygōnius, a, um</b> (<i>греч.</i>)\nмногоугольный (turris <i>Vtr</i>)."],
  ["polygonum", "<big>polygonum</big>\n\n<b>polygōnum, ī</b> <i>n</i> (<i>греч.</i>)\n<b>1)</b> многоугольник <i>Cens</i>;\n<b>2)</b> <i>бот.</i> (<i>тж.</i> polygonus, i <i>f</i>) гречиха <i>PM</i>."],
  ["polyhistor", "<big>polyhistor</big>\n\n<b>polyhistōr, oris</b> <i>m</i> (<i>греч.</i>)\nполигистор, многосторонний учёный <i>Su</i>."],
  ["Polyhymnia", "<big>Polyhymnia</big>\n\n<b>Polyhymnia, ae</b> <i>f</i>\nПолигимния, <i>муза лирики и музыки</i> <i>H, O, M, Aus</i>."],
  ["Polymachaeroplagides", "<big>Polymachaeroplagides</big>\n\n<b>Polymachaeroplāgidēs, ae</b> <i>m</i> (<i>греч.</i> «наносящий множество ударов мечом»)\n<i>шутл. имя солдата</i> <i>Pl</i>."],
  ["Polymestor", "<big>Polymestor</big>\n\n<b>Polymēstōr (Polymnēstōr), oris</b> <i>m</i>\nПолим(н)естор, <i>царь Фракии, муж Илионы, дочери Приама</i> (<i>см.</i> Polydorus) <i>O</i>."],
  ["polymitarius", "<big>polymitarius</big>\n\n<b>I polymitārius</b> <i>Vlg</i>\n= polymitus.\n\n<b>II polymitārius, ī</b> <i>m</i>\nткач-камчатник <i>Vlg</i>."],
  ["polymitum", "<big>polymitum</big>\n\n<b>polymitum, ī</b> <i>n</i> (<i>sc.</i> opus)\nузорчатая (камчатная) ткань <i>PM, Vlg</i>."],
  ["polymitus", "<big>polymitus</big>\n\n<b>polymitus, a, um</b> (<i>греч.</i>)\nпестротканый, камчатный (alicula <i>Pt</i>; tunica <i>Vlg</i>)."],
  ["Polymnestor", "<big>Polymnestor</big>\n\n<b>Polymnēstor</b> <i>v. l.</i>\n= Polymestor."],
  ["Polymnia", "<big>Polymnia</big>\n\n<b>Polymnia, ae</b> <i>f</i> <i>v. l.</i>\n= Polyhymnia."],
  ["polymyxos", "<big>polymyxos</big>\n\n<b>polymyxos, on</b> <i>adj.</i> (<i>греч.</i>)\nмногофитильный (lucerna <i>M</i>)."],
  ["Polynices", "<big>Polynices</big>\n\n<b>Polynīcēs, is</b> <i>m</i>\nПолиник, <i>сын Эдипа и Иокасты, брат Этеокла</i> <i>PM, St</i>."],
  ["Polyperchon", "<big>Polyperchon</big>\n\n<b>Polyperc(h)ōn, ontis</b> <i>m</i>\nПолиперхонт, <i>полководец Филиппа и Александра Македонских, впоследствии правитель Македонии, а после смерти Антипатра</i> (<i>318 г. до н. э.</i>) — <i>опекун царской семьи</i> <i>Nep</i>."],
  ["polyphagus", "<big>polyphagus</big>\n\n<b>polyphagus, ī</b> <i>m</i> (<i>греч.</i>)\nобжора <i>Su</i>."],
  ["Polyphemus", "<big>Polyphemus</big>\n\n<b>Polyphēmus (-os), ī</b> <i>m</i>\nПолифем, <i>сын Нептуна и нимфы Фоосы, одноглазый Киклоп, ослеплённый Одиссеем</i> <i>C, O, M</i>."],
  ["polyplusius", "<big>polyplusius</big>\n\n<b>polyplūsius, a, um</b> (<i>греч.</i>)\nбогатейший (genus <i>Pl</i>)."],
  ["polyposus", "<big>polyposus</big>\n\n<b>polypōsus, a, um</b> [polypus]\nстрадающий полипами в носу <i>M</i>."],
  ["polyptycha", "<big>polyptycha</big>\n\n<b>polyptyc(h)a, ōrum</b> <i>n</i> (<i>греч.</i>)\nсчетоводные документы, (податные) списки <i>Veg, CTh</i>."],
  ["polypus", "<big>polypus</big>\n\n<b>polypus, ī</b> (<i>тж.</i> <b>pō-</b>) <i>m</i> (<i>греч.</i>)\n<b>1)</b> <i>зоол.</i> полип <i>Pl, PM</i>;\n<b>2)</b> <i>мед.</i> полип <i>CC, H</i>;\n<b>3)</b> хищник <i>Pl</i>."],
  ["polyrrhizos", "<big>polyrrhizos</big>\n\n<b>polyrr(h)izos, on</b> (<i>греч.</i>)\nимеющий много корней <i>PM</i>."],
  ["polysarcia", "<big>polysarcia</big>\n\n<b>polysarcia, ae</b> <i>f</i> (<i>греч.</i>)\nчрезмерная мясистость, дородность <i>CA</i>."],
  ["polyspaston", "<big>polyspaston</big>\n\n<b>polyspaston, ī</b> <i>n</i> (<i>греч.</i>)\nполиспаст, составной блок <i>Vtr</i>."],
  ["polysyllabus", "<big>polysyllabus</big>\n\n<b>polysyllabus, a, um</b> (<i>греч.</i>) <i>грам.</i>\nмногосложный."],
  ["polysyntheton", "<big>polysyntheton</big>\n\n<b>polysyntheton, ī</b> <i>n</i> (<i>греч.</i>) <i>грам.</i>, <i>ритор.</i>\nмногосоюзность <i>Is</i>."],
  ["Polyxena", "<big>Polyxena</big>\n\n<b>Polyxena, ae</b> (<b>Polyxenē, ēs</b> <i>SenT</i>) <i>f</i>\n Поликсена, <i>дочь Приама, возлюбленная Ахилла, которую Неоптолем принёс в жертву тени Ахилла</i> <i>O, J</i>."],
  ["Polyxenius", "<big>Polyxenius</big>\n\n<b>Polyxenius, a, um</b> <i>adj. к</i>\nPolyxena (caedes <i>Ctl</i>)."],
  ["pomarium", "<big>pomarium</big>\n\n<b>pomarium, i</b> <i>n</i> [pomarius]\n<b>1)</b> фруктовый сад <i>C, Col etc.</i>;\n<b>2)</b> кладовая для хранения плодов <i>Vr, PM</i>."],
  ["pomarius", "<big>pomarius</big>\n\n<b>I pomarius, a, um</b> [pomum]\nплодовый, фруктовый (seminarium <i>Cato</i>).\n\n<b>II pomarius, i</b> <i>m</i>\nторгующий плодами, фруктовщик <i>H, Lampr</i>."],
  ["pomerid-", "<big>pomerid-</big>\n\n<b>pomerid-</b> <i>v. l.</i>\n= postmerid-."],
  ["pomerium", "<big>pomerium</big>\n\n<b>pomerium (pomoerium), i</b> <i>n</i> [post + moerus = murus] <i>тж.</i> <i>pl.</i>\n<b>1)</b> померий, <i>незастроенная полоса земли по обе стороны городской стены</i> <i>Vr, C, L, T, AG</i>;\n<b>2)</b> граница, рубеж <i>Vr, Macr</i>."],
  ["Pometia", "<big>Pometia</big>\n\n<b>Pometia, ae</b> <i>f</i> <i>и</i> <b>Pometii, orum</b> <i>m</i>\nПомеция, <i>город вольсков в Латии</i> <i>C, V, PM</i>."],
  ["Pometinus", "<big>Pometinus</big>\n\n<b>Pometinus, a, um</b> [Pometia]\nпометийский (manubiae <i>L</i>)."],
  ["pometum", "<big>pometum</big>\n\n<b>pometum, i</b> <i>n</i> [pomus]\nфруктовый сад <i>Pall</i>."],
  ["pomifer", "<big>pomifer</big>\n\n<b>pomifer, fera, ferum</b> [pomum + fero]\nплодоносный (nemora <i>Sol</i>); богатый плодами (autumnus <i>H</i>); урожайный (annus <i>H</i>)."],
  ["pomoerium", "<big>pomoerium</big>\n\n<b>pomoerium, i</b> <i>n</i> <i>v. l.</i>\n= pomerium."],
  ["Pomona", "<big>Pomona</big>\n\n<b>Pomona, ae</b> <i>f</i> [pomum]\nПомона, <i>богиня плодов и плодовых деревьев, жена Вертумна <i>Vr, O, PM</i>; перен.</i> плоды, фрукты <i>PM, Sol</i>."],
  ["Pomonalis", "<big>Pomonalis</big>\n\n<b>Pomonalis, e</b> [Pomona]\nпомонин (flamen <i>Vr</i>)."],
  ["pomosus", "<big>pomosus</big>\n\n<b>pomosus, a, um</b> [pomum]\nобильный плодами (hortus <i>Tib</i>); фруктовый (corona <i>Prp</i>)."],
  ["pompa", "<big>pompa</big>\n\n<b>pompa, ae</b> <i>f</i> (<i>греч.</i>)\n<b>1)</b> торжественное шествие, процессия (p. funeris <i>Nep, O</i>);\n<b>2)</b> великолепие, пышность, блеск (ad pompam manus porrigere <i>Pt</i>; in dicendo pompam adhibere <i>C</i>; pompam parare triumphis <i>O</i>);\n<b>3)</b> блистательная речь <i>C</i>;\n<b>4)</b> вереница, свита (lictorum <i>C</i>);\n<b>5)</b> множество, масса (pecuniae <i>Sen</i>)."],
  ["pompabilis", "<big>pompabilis</big>\n\n<b>pompabilis, e</b> [pompa]\nпышный, роскошный, великолепный <i>Treb</i>."],
  ["pompabiliter", "<big>pompabiliter</big>\n\n<b>pompabiliter</b> [pompabilis]\nвеликолепно, пышно (ornatus <i>Treb</i>)."],
  ["pompalis", "<big>pompalis</big>\n\n<b>pompalis, e</b> [pompa]\nторжественный, величественный (vultus <i>Capit</i>)."],
  ["pompatice", "<big>pompatice</big>\n\n<b>pompatice</b>\nпышно, торжественно (ingredientes <i>Vlg</i>)."],
  ["pompaticus", "<big>pompaticus</big>\n\n<b>pompaticus, a, um</b> <i>Ap, Eccl</i>\n= pompabilis."],
  ["Pompei", "<big>Pompei</big>\n\n<b>Pompei, orum</b> <i>n</i>\n= Pompeji."],
  ["Pompeia", "<big>Pompeia</big>\n\n<b>Pompeia</b> <i>v. l.</i>\n= Pompeja."],
  ["Pompeius", "<big>Pompeius</big>\n\n<b>Pompeius, a, um</b>\n= Pompejus."],
  ["Pompeja", "<big>Pompeja</big>\n\n<b>Pompeja (Pompeia), ae</b> <i>f</i>\nПомпея:\n<b>1)</b> <i>дочь Помпея Магна, жена Суллы Фауста</i> <i>bAfr</i>;\n<b>2)</b> <i>жена (впоследствии разведённая) Ю. Цезаря</i> <i>Su</i>;\n<b>3)</b> P. Paulina, <i>жена философа Сенеки</i> <i>T</i>."],
  ["Pompejani", "<big>Pompejani</big>\n\n<b>Pompejani, orum</b> <i>m</i>\n<b>1)</b> жители Помпеи <i>C</i>;\n<b>2)</b> помпеянцы, сторонники Помпея <i>Cs, Sen, T</i>."],
  ["Pompejanum", "<big>Pompejanum</big>\n\n<b>Pompejanum, i</b> <i>n</i>\n<i>поместье Цицерона близ Помпеи</i> <i>C</i>."],
  ["Pompejanus", "<big>Pompejanus</big>\n\n<b>Pompejanus, a, um</b> <i>C, Cs etc. adj. к</i>\nPompeji <i>и</i> Pompejus."],
  ["Pompeji", "<big>Pompeji</big>\n\n<b>Pompeji, orum</b> <i>n</i>\nПомпеи, <i>городу Кампании, к юго.-вост. от Неаполя, в 79 г. н. э. засыпанный лавой в результате извержения Везувия</i> (<i>вместе с Геркуланумом и Стабиями</i>) <i>Mela, L, Sen</i>."],
  ["Pompejopolis", "<big>Pompejopolis</big>\n\n<b>Pompejopolis, is</b> <i>f</i>\nПомпейополь:\n<b>1)</b> <i>город в Киликии</i> (<i>прежде</i> Soli) <i>T</i>;\n<b>2)</b> <i>город в Пафлагонии</i> <i>PM</i>."],
  ["Pompejus", "<big>Pompejus</big>\n\n<b>Pompejus, a, um</b>\nПомпеи, <i>римск. nomen; наиболее известны</i>:\n<b>1)</b> Q. P., <i>в качестве консула в 141 г. и проконсула в 140 г. до н. э. безуспешно вёл войну против Вириата в Нуманции</i> <i>C</i>;\n<b>2)</b> Q. P. Rufus, <i>народный трибун в 100 г. до н. э.; в 88 г. (вместе с Суллой) — консул; по приказанию Помпея Страбона убит солдатами</i> <i>C</i>;\n<b>3)</b> Q. P. Bithynicus, <i>друг Цицерона; в 75 г. до н. э. сделал Вифинию римск. провинцией; в 48 г. до н. э. погиб в Египте вместе с Помпеем Великим</i> <i>C</i>;\n<b>4)</b> Cn. P. Strabo, <i>претор в 94 г. до н. э., консул в 89 г., военачальник в Союзнической войне</i> <i>C, VP, Fl</i>;\n<b>5)</b> Cn. P. Magnus, <i>сын предыдущего, род. в 106 г. до н. э., победитель пиратов и Митридата; в 60 г. до н. э. заключил первый триумвират с Цезарем и Крассом, но в 49 г. разошёлся с Цезарем, а в 48 г. был разбит им в битве при Фарсале и убит во время бегства в Египет</i> (<i>его сыновья Гней и Секст продолжали борьбу против Цезаря; Гней потерпел в 45 г. до н. э. поражение при Мунде и был убит во время бегства, а Секст погиб в 35 г. до н. э. в Милете</i>) <i>C, Cs etc.</i>"],
  ["Pompilianus", "<big>Pompilianus</big>\n\n<b>Pompilianus, a, um</b> [Pompilius]\nпомпилиев (tempus <i>Amm</i>; indigetamenta <i>Eccl</i>)."],
  ["Pompilius", "<big>Pompilius</big>\n\n<b>Pompilius, a, um</b>\nПомпилий, <i>римск. nomen; наиболее известен</i> Numa P., <i>второй римск. царь</i> (<i>715-672 гг. до н. э.</i>) <i>L, Eutr, Vr, H etc.</i>"],
  ["pompilus", "<big>pompilus</big>\n\n<b>pompilus, i</b> <i>m</i> (<i>греч.</i>; <i>лат.</i> nautilus)\n<i>рыба</i> лоцман (<i>Naucrates ductor</i>, L.) <i>O, PM</i>."],
  ["Pomponia", "<big>Pomponia</big>\n\n<b>Pomponia, ae</b> <i>f</i>\nПомпония:\n<b>1)</b> <i>мать Сципиона Африканского <i>Su</i>;</i>\n<b>2)</b> <i>сестра Аттика, жена Кв. Цицерона</i> <i>C</i>."],
  ["Pomponianus", "<big>Pomponianus</big>\n\n<b>Pomponianus, a, um</b> [Pomponius]\nпомпониев <i>C, PM</i>."],
  ["Pomponius", "<big>Pomponius</big>\n\n<b>Pomponius, a, um</b>\nПомпоний, <i>римск. nomen; наиболее известны</i>:\n<b>1)</b> L. P., <i>родом из Бононии, современник Лукреция, автор ателлан</i> <i>AG</i>;\n<b>2)</b> T. P. Atticus — <i>см.</i> Atticus;\n<b>3)</b> Publius P. Secundus, <i>автор трагедий времён Калигулы и Клавдия, сторонник Сеяна, друг Плиния Старшего</i> <i>Q</i>;\n<b>4)</b> P. Mela — <i>см.</i> Mela."],
  ["Pomptinus", "<big>Pomptinus</big>\n\n<b>Pomptinus, a, um</b>\nПомптин, <i>римск. nomen; наиболее известен</i> C. P., <i>претор в 63 г., пропретор в Gallia Narbonensis в 62-59 гг., легат Цицерона в Киликии в 51 г. до н. э.</i> <i>Sl, C</i>: paludes Pomptinae Помптинские болота (<i>на побережье Латия, между Цирцеями и Таррациной, наводняемые реками Амасея, Астура и Уфента</i>) <i>Cs, PM, M, Lcn</i>; Pomptina summa <i>C</i> верхний край Помптинских болот."],
  ["Pomtinus", "<big>Pomtinus</big>\n\n<b>Pomtinus, a, um</b> <i>v. l.</i>\n= Pomptinus."],
  ["pomum", "<big>pomum</big>\n\n<b>pomum, i</b> <i>n</i>\n<b>1)</b> плод (<i>преим. древесный: яблоко, вишня, финик, орех и проч.</i>) <i>Vr, V, H etc.</i>: bellum p.! <i>Pl ирон.</i> хорош фрукт!;\n<b>2)</b> (= pomus) фруктовое дерево <i>Cato, V, PJ</i>."],
  ["pomus", "<big>pomus</big>\n\n<b>pomus, i</b> <i>f</i>\n<b>1)</b> фруктовое дерево <i>Cato, PM, Tib</i>;\n<b>2)</b> древесный плод <i>Cato ap. PM</i>."],
  ["ponderatio", "<big>ponderatio</big>\n\n<b>ponderatio, onis</b> <i>f</i> [pondero]\nвзвешивание, отвешивание <i>Vtr, Vlg</i>."],
  ["ponderator", "<big>ponderator</big>\n\n<b>ponderator, oris</b> <i>m</i> [pondero]\nвесовщик, <i>т. е.</i> приёмщик (по весу) денег от налогоплательщиков <i>CTh, CJ</i>."],
  ["ponderatus", "<big>ponderatus</big>\n\n<b>ponderatus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> pondero;\n<b>2.</b> <i>adj.</i> важный, значительный <i>Nep, Sid</i>."],
  ["pondero", "<big>pondero</big>\n\n<b>pondero, avi, atum, are</b> [pondus]\n<b>1)</b> взвешивать (granum <i>PM</i>);\n<b>2)</b> подвергать рассмотрению, оценивать, судить, определять (aliquid ex aliqua re <i>C</i>; numerantur sententiae, non ponderantur <i>PJ</i>): consilia eventis p. <i>C</i> судить о замыслах по их результатам."],
  ["ponderosus", "<big>ponderosus</big>\n\n<b>ponderosus, a, um</b> [pondus]\n<b>1)</b> тяжеловесный, увесистый, тяжёлый (lapis <i>PM</i>; verbera <i>VM</i>); массивный (pocula <i>Pt</i>);\n<b>2)</b> важный, серьёзный (epistula <i>C</i>); веский, авторитетный (vox <i>VM</i>)."],
  ["pondo", "<big>pondo</big>\n\n<b>pondo</b> <i>adv.</i> [pondus]\nпо весу, весом (в фунтах) (corona libra p. <i>L</i>): libra p. <i>L</i> весом в фунт; auri quinque p. <i>C</i> пять фунтов золота; sex p. et selibram habere <i>Pt</i> весить 6,5 фунтов; uncia p. <i>Pl, L</i> = 1/12 фунта."],
  ["pondus", "<big>pondus</big>\n\n<b>pondus, eris</b> <i>n</i> [pendo]\n<b>1)</b> вес, тяжесть (onus maximi ponderis <i>Vtr</i>): talea ad certum p. examinata <i>Cs</i> брусок определённого веса; emere aliquid pondere <i>PM</i> покупать что-л. на вес;\n<b>2)</b> гиря, груз (tela suspensis ponderibus extendere <i>Sen</i>);\n<b>3)</b> весомое тело, масса (omnia pondera in terram feruntur <i>C</i>);\n<b>4)</b> весовой римский фунт (as erat libra p. <i>Vr</i>; argenti pondera quinque <i>M</i>);\n<b>5)</b> тяготение (moveri gravitate et pondere <i>C</i>);\n<b>6)</b> равновесие: tellus ponderibus librata suis <i>O</i> земля, пришедшая в равновесие;\n<b>7)</b> бремя, обуза (pondera amara senectae <i>Sen</i>): p. uteri <i>Prp</i> утробный плод;\n<b>8)</b> количество, множество (magnum p. argenti <i>Cs</i>);\n<b>9)</b> значение, вес, важность, роль (litterae maximi ponderis <i>C</i>): pondera rerum <i>C</i> важнейшие основы вещей; verba sine pondere <i>H</i> невыразительные (пустые) слова; interdum lacrimae pondera vocis habent <i>O</i> слезам присуща подчас сила слова; p. majestatis <i>Pt</i> величавая серьёзность (стиля);\n<b>10)</b> твёрдость; стойкость, постоянство (p. habere <i>Prp</i>);\n<b>11)</b> серьёзность, важность (vultus <i>VM</i>);\n<b>12)</b> прочное основание, фундамент (interjecto intrinsecus pondere <i>Ap</i>);\n<b>13)</b> <i>Ctl, St pl.</i> = testiculi."],
  ["pondusculum", "<big>pondusculum</big>\n\n<b>pondusculum, i</b> <i>n</i> [<i>demin. к</i> pondus]\nнебольшой вес, маленькая тяжесть <i>Col, PM</i>."],
  ["pone", "<big>pone</big>\n\n<b>I pone</b> <i>adv.</i>\nназади, позади, сзади (et ante et p. moveri <i>C</i>); назад (p. jacere <i>VF</i>).\n\n<b>II pone</b> <i>praep. cum acc.</i>\nпозади, за (p. aliquem <i>Pl etc.</i>; manus p. tergum vincire <i>T</i>).\n\n<b>III pone</b> <i>imper. к</i>\npono."],
  ["pono", "<big>pono</big>\n\n<b>pono, posui (posivi), positum, ere</b>\n<b>1)</b> класть (aliquid in mensa <i>Naev</i>; finem vitae <i>T</i>): p. aliquid in manu <i>Pt</i> вручать что-л. кому-л.; somno positus <i>V</i> улёгшийся ко сну; se p. toro <i>O</i> возлечь на ложе || ставить, размещать (sellam in foro <i>C</i>); выстраивать (pedites post equites <i>L</i>); разбивать, располагать (castra in colle <i>Cs</i>; Roma in montibus posita <i>C</i>): p. aliquid in conspectu <i>и</i> ante oculos <i>C</i> показать что-л. воочию (наглядно); in Alpibus positi <i>Vop</i> живущие в Альпах; posita nix <i>H</i> выпавший снег || ставить на стол, подавать (pocula <i>V, QC</i>); запечатлевать (oscula in labellis <i>Prp</i>);\n<b>2)</b> бросать, кидать (aliquid in flammam <i>O</i>); бросать, опускать (ancoras <i>L</i>);\n<b>3)</b> сажать (arborem <i>H</i>); сеять (semina <i>V</i>);\n<b>4)</b> хоронить (aliquem tumulo <i>O</i>);\n<b>5)</b> вкладывать, помещать (pecuniam in praedio <i>C</i>);\n<b>6)</b> употреблять, использовать, распоряжаться (p. otia recte <i>H</i>); обращать, прилагать (omnem curam in salute patriae <i>C</i>);\n<b>7)</b> складывать, сдавать на хранение (testamentum in aerario <i>Cs</i>);\n<b>8)</b> делать ставку, ставить (<i>в игре или пари</i>) (aliquid <i>Pl</i>);\n<b>9)</b> назначать, устанавливать, обещать (praemium <i>Sl, L</i>);\n<b>10)</b> издавать (leges <i>C</i>); придавать, давать (nomen alicui <i>C etc.</i>); определять (alicui poenam <i>C</i>): p. de quo quis audire vellet <i>C</i> предложить тему, которую желательно было бы сделать предметом лекции;\n<b>11)</b> укладывать, приводить в порядок, причёсывать (comas, capillos <i>O</i>);\n<b>12)</b> приставлять (scalas <i>Cs</i>);\n<b>13)</b> опускать (caput <i>H</i>); преклонять (genua <i>O, QC etc.</i>);\n<b>14)</b> возлагать, надевать (coronam in caput <i>AG</i> —<i>ср.</i> 19);\n<b>15)</b> жертвовать, посвящать богам (ex praeda tripodem Delphis <i>Nep</i>); se p. totum in aliqua re <i>C</i> целиком отдаться чему-л. || приносить, давать (vota <i>Prp</i>);\n<b>16)</b> расставлять, располагать, дислоцировать (duas legiones in aliqua regione <i>Cs</i>); выставлять (vigilias <i>Sl</i>); приставлять (alicui custodem <i>C</i>); помещать, переносить (aliquem in caelo <i>Just</i>); переводить (aliquem Thebis <i>H</i>); назначать (Numidis imperatorem <i>Sl</i>);\n<b>17)</b> воздвигать (alicujus statuam <i>Nep</i>; templa Jovi <i>V</i>); строить, устраивать (insidias contra aliquem <i>C, Cs</i>); основывать, сооружать, строить (aram <i>L</i>; urbem <i>V</i>): Tibur Argeo positura colono <i>H</i> Тибур, заложенный аргосскими поселенцами || вить (nidum <i>H</i>);\n<b>18)</b> изображать (aliquem coloribus <i>H</i>); изваять (Venerem marmoream <i>H</i>);\n<b>19)</b> сбрасывать с себя, скидывать, снимать (vestem <i>C</i>; coronam luctus gratia <i>C</i> — <i>ср.</i> 14; praetextam puerilem <i>T</i>); откладывать (librum de manibus <i>C</i>);\n<b>20)</b> обрезывать (ungues <i>H</i>); сбривать (barbam <i>Su</i>); терять (vires <i>V</i>); ронять (frondes <i>V</i>); складывать (arma <i>C etc.</i>); откладывать в сторону (iracunda fulmina <i>H</i>); отбрасывать прочь (curas <i>L etc.</i>; metum <i>O</i>): positis ambagibus <i>O</i> без обиняков; nomine triumviri posito <i>T</i> сложив с себя звание триумвира || утрачивать, отдавать (vitam <i>C etc.</i>; spem <i>Pl</i>); заканчивать, прекращать (bellum <i>Sl</i>);\n<b>21)</b> производить на свет, рожать (uteri onus <i>O</i>);\n<b>22)</b> успокаивать, унимать (magnos motus <i>Prp</i>; freta <i>H</i>); униматься, утихать (venti ponunt <i>V, O, AG</i>);\n<b>23)</b> возлагать (omnem spem in aliqua re <i>Cs</i>); полагать, считать, рассматривать (aliquem primum <i>Nep</i>; aliquid in malis <i>C</i>): p. aliquid inter munera naturae <i>J</i> относить что-л. к числу даров природы || (пред)полагать (hoc posito atque concesso <i>C</i>): aliquod pretium alicui rei p. <i>Sen</i> придавать чему-л. какую-то цену, в какой-то мере дорожить чём-л. || подвергать (aliquid in dubio <i>L</i>; caput periculo <i>Pl</i>); вменять (aliquid in laude alicujus <i>C</i>); включать (aliquem inter vatum choros <i>H</i>); утверждать, высказывать (aliquid pro certo <i>Cs</i>): ut paulo ante posui <i>C</i> как я сказал несколько выше || выдвигать (quaestionem <i>L, Ph, Sen</i>): aliquem in stispicione p. <i>C</i> навлечь подозрение на кого-л."],
  ["pons", "<big>pons</big>\n\n<b>pons, pontis</b> <i>m</i>\n<b>1)</b> мост; <i>pl.</i> мост из нескольких пролётов: p. sublicius <i>L, T, Sen, PM</i> свайный мост (<i>соединявший Яникульский холм с Римом</i>); pontem facere in flumine <i>Cs</i> (per flumen <i>L</i>), flumini pontem imponere <i>QC</i> (injicere <i>L</i>, indere <i>T</i>) <i>или</i> flumen jungere ponte <i>L</i> построить (навести) мост через реку;\n<b>2)</b> гать, бревенчатый настил (<i>через болото</i>) (consternere paludem pontibus <i>Cs</i>);\n<b>3)</b> сходни, мостки (aliquos de puppibus pontibus exponere <i>V</i>);\n<b>4)</b> подъёмный мостик (<i>преим. для соединения осадной башни с городской стеной</i>) (turres pontibus transjectis conjungere <i>Cs</i>);\n<b>5)</b> корабельная палуба (<i>для установки военных орудий</i>) (naves pontibus stratae, super quas tormenta veherentur <i>T</i>);\n<b>6)</b> ярус (помост) башни (insternere pontes altos <i>V</i>);\n<b>7)</b> <i>pl.</i> мосты, по которым во время центуриатских комициев римляне шли в Saepta для подачи голосов (<i>см.</i> sexagenarius 2) <i>rhH, C, O</i>."],
  ["Pontia", "<big>Pontia</big>\n\n<b>Pontia, ae</b>, <i>тж.</i> <i>pl. f</i>\nПонтия, <i>скалистый о-в (и группа о-вов) у побережья Латия, против города Formiae (ныне</i> Ponza) <i>Vr, L, Su etc.</i>"],
  ["Pontiani", "<big>Pontiani</big>\n\n<b>Pontiani, orum</b> <i>m</i>\nжители о-ва Pontia <i>L</i>."],
  ["ponticulus", "<big>ponticulus</big>\n\n<b>ponticulus, i</b> <i>m</i> [<i>demin. к</i> pons]\nМостик <i>Ctl, C, Su etc.</i>"],
  ["Ponticus", "<big>Ponticus</big>\n\n<b>I Ponticus, a, um</b> [Pontus]\n<b>1)</b> понтийский: mare Ponticum <i>L, T</i> = Pontus I, 1; mus P. <i>PM</i> горностай; P. serpens <i>J</i> = дракон, стерёгший золотое руно в Колхиде; fauces freti Pontici <i>SenT</i> = Боспор Фракийский;\n<b>2)</b> <i>перен.</i> Митридатов (triumphus <i>VP</i>): raedicamina Pontica <i>J</i> = яд, которым отравился Митридат.\n\n<b>II Ponticus, i</b> <i>m</i>\nПонтик, <i>римск. поэт, друг Овидия и Проперция</i> <i>Prp, O</i>."],
  ["pontifex", "<big>pontifex</big>\n\n<b>pontifex, ficis</b> <i>m</i>\n<b>1)</b> понтифик, жрец (<i>их коллегия</i> — collegium pontificum <i>L</i> — <i>состояла сначала из 4, впоследствии из 8 и, наконец, из 15 человек, с</i> p. maximus <i>во главе, и ведала всеми вопросами культа</i>): pontifices minores <i>C, L</i> помощники (секретари) понтификов;\n<b>2)</b> (<i>в христианской церкви</i>) епископ, прелат <i>Sid, Eccl</i>."],
  ["pontificalis", "<big>pontificalis</big>\n\n<b>pontificalis, e</b> [pontifex]\nпонтификальный, жреческий (auctoritas <i>C</i>; insignia <i>L</i>)."],
  ["pontificatus", "<big>pontificatus</big>\n\n<b>pontificatus, us</b> <i>m</i> [pontifex]\nпонтификат, сан и должность (верховного) жреца <i>C, Sen, Su, T</i>."],
  ["pontificium", "<big>pontificium</big>\n\n<b>pontificium, i</b> <i>n</i>\n<b>1)</b> сан епископа <i>CTh</i>;\n<b>2)</b> власть, право (negotium pontificiumque alicujus <i>AG</i>; p. adipiscendae hereditatis <i>CTh</i>)."],
  ["pontificius", "<big>pontificius</big>\n\n<b>pontificius, a, um</b> <i>C etc.</i>\n= pontificalis."],
  ["pontilis", "<big>pontilis</big>\n\n<b>pontilis, e</b> [pons]\nмостовой (stratus <i>Veg</i>)."],
  ["Pontinus", "<big>Pontinus</big>\n\n<b>Pontinus, a, um</b> <i>v. l.</i>\n= Pomptinus."],
  ["Pontius", "<big>Pontius</big>\n\n<b>Pontius, a, um</b>\nПонтий, <i>римск. nomen; наиболее известны</i>:\n<b>1)</b> C. P., <i>полководец самнитов, победитель римлян при Клавдии в 321 г. до н. э.; в 292 г. захвачен в плен и казнён римлянами</i> <i>C, L, Fl</i>;\n<b>2)</b> P. Aquila, <i>народный трибун в 45 г. до н. э., один из убийц Цезаря, пал при Мутине в 43 г. до н. э.</i> <i>C, Su</i>;\n<b>3)</b> P. Pilatus, <i>императорский прокуратор Иудеи, отозванный в 36 г. н. э.</i> <i>T, Vlg</i>."],
  ["ponto", "<big>ponto</big>\n\n<b>ponto, onis</b> <i>m</i> [pons]\nплоскодонное судно, понтон <i>Cs, AG, Dig, Aus</i>."],
  ["pontonium", "<big>pontonium</big>\n\n<b>pontonium, i</b> <i>n</i> [<i>demin. к</i> ponto]\nплоскодонное судёнышко <i>Is</i>."],
  ["Pontus", "<big>Pontus</big>\n\n<b>I Pontus, i</b> <i>m</i>\nПонт:\n<b>1)</b> (<i>sc.</i> Euxinus) <i>C, VF</i> = Чёрное море;\n<b>2)</b> побережье Чёрного моря <i>C, bAl, O</i>;\n<b>3)</b> <i>преим.</i> Понт, <i>страна в Малой Азии между Арменией и Вифинией, впоследствии римск. провинция</i> <i>C, V, O etc.</i>\n\n<b>II pontus, i</b> <i>m</i> (<i>греч.</i>) <i>поэт.</i>\n<b>1)</b> глубина, пучина (maris <i>V</i>);\n<b>2)</b> море (aequora <i>и</i> freta ponti <i>V</i>; transcurrere pontum <i>St</i>);\n<b>3)</b> морской вал (p. ingens in puppim ferit <i>V</i>)."],
  ["popa", "<big>popa</big>\n\n<b>popa, ae</b> <i>m</i>\nпомощник жреца при жертвоприношении <i>C, Prp, Su</i>: p. venter <i>Pers</i> толстобрюхий, пузан."],
  ["popanum", "<big>popanum</big>\n\n<b>popanum, i</b> <i>n</i> (<i>греч.</i>)\nжертвенный пирог <i>J</i>."],
  ["popellus", "<big>popellus</big>\n\n<b>popellus, i</b> <i>m</i> [<i>demin. к</i> populus] <i>презр.</i>\nнародишко, простонародье, чернь <i>H, Pers</i>."],
  ["Popilia", "<big>Popilia</big>\n\n<b>Popilia, ae</b> <i>f</i>\nПопилия, <i>мать Кв. Лутация Катула</i> <i>C</i>."],
  ["Popilius", "<big>Popilius</big>\n\n<b>Popilius, a, um</b>\nПопилий, <i>римск. nomen; наиболее известен</i> C. P. Laenas, <i>убийца Цицерона</i> <i>L, Sen</i>."],
  ["popina", "<big>popina</big>\n\n<b>popina, ae</b> <i>f</i>\n<b>1)</b> трактир, харчевня <i>Pl, C, H, M, Su etc.</i>;\n<b>2)</b> трактирные кушанья <i>C</i>."],
  ["popinalis", "<big>popinalis</big>\n\n<b>popinalis, e</b> [popina]\nтрактирный (deliciae <i>Col</i>; luxuria <i>Ap</i>)."],
  ["popinarius", "<big>popinarius</big>\n\n<b>popinarius, i</b> <i>m</i>\nтрактирщик, кухмистер <i>Lampr</i>."],
  ["popinator", "<big>popinator</big>\n\n<b>popinator, oris</b> <i>m</i> <i>Macr</i>\n= popino."],
  ["popino", "<big>popino</big>\n\n<b>popino, onis</b> <i>m</i> [popina]\nзавсегдатай трактиров кутила <i>LM, Vr, H, Su</i>."],
  ["popinor", "<big>popinor</big>\n\n<b>popinor, —, ari</b> <i>depon.</i> [popino]\nбражничать, кутить <i>Treb</i>."],
  ["poples", "<big>poples</big>\n\n<b>poples, itis</b> <i>m</i>\n<b>1)</b> подколенная впадина (genua poplitesque <i>Col</i>);\n<b>2)</b> поджилки (succidere poplitem <i>V</i> <i>и</i> poplites <i>Sen</i>);\n<b>3)</b> колено: duplicato poplite <i>V</i> преклонив колени; poplitibus semet excipere <i>QC</i> опуститься на колени; contento poplite <i>H</i> выпрямившись во весь рост, <i>т. е.</i> напряжённо."],
  ["poplicola", "<big>poplicola</big>\n\n<b>I poplicola, ae</b> <i>m</i> [populus + colo II]\nдруг (покровитель) народа <i>Sid</i>.\n\n<b>II Poplicola, ae</b> <i>m</i> <i>арх.</i>\n= Publicola."],
  ["poplicus", "<big>poplicus</big>\n\n<b>poplicus, a, um</b> <i>арх.</i>\n= publicus."],
  ["Poplifugia", "<big>Poplifugia</big>\n\n<b>Poplifugia, orum</b> <i>n</i> [populus + fugio]\n<i>праздник, справлявшийся в июльские ноны в память спасения римлян от латинян</i> <i>Vr</i>."],
  ["poplus", "<big>poplus</big>\n\n<b>poplus, i</b> <i>m</i> <i>Pl</i>\n= populus I."],
  ["poposci", "<big>poposci</big>\n\n<b>poposci</b> <i>pf. к</i>\nposco."],
  ["Poppaea", "<big>Poppaea</big>\n\n<b>Poppaea, ae</b> <i>f</i>\n(Sabina) Поппея, <i>вторая жена Нерона</i> (<i>после Октавии</i>) <i>T, Su</i>."],
  ["Poppaeanus", "<big>Poppaeanus</big>\n\n<b>Poppaeanus, a, um</b> [Poppaea]\nПоппеин: Poppaeane pinguia <i>J</i> косметические средства Поппеи."],
  ["Poppaeus", "<big>Poppaeus</big>\n\n<b>Poppaeus, a, um</b>\nПоппей, <i>римск. nomen; наиболее известен</i> Q. P. Secundus, <i>проконсул Малой Азии в 19 г. н. э.; вместе с М. Папием Мутилом издал lex Julia et Papia Poppaea против безбрачия</i> (<i>9 г. н. э.</i>) <i>T</i>."],
  ["poppysma", "<big>poppysma</big>\n\n<b>poppysma, atis</b> <i>n</i> (<i>греч.</i>)\n<b>1)</b> щёлканье языком (<i>в знак одобрения <i>J</i> или для предохранения себя от удара молнии</i> <i>PM</i>);\n<b>2)</b> щёлкающий звук <i>M</i>."],
  ["poppysmus", "<big>poppysmus</big>\n\n<b>poppysmus, i</b> <i>m</i> <i>PM</i>\n= poppysma."],
  ["poppyzon", "<big>poppyzon</big>\n\n<b>poppyzon, ontis</b> (<i>acc.</i> onta) (<i>греч.</i>)\nщёлкающий <i>PM</i>."],
  ["populabilis", "<big>populabilis</big>\n\n<b>populabilis, e</b> [populor]\nподверженный разрушению: quodcumque fuit populabile flammae <i>O</i> всё, что могло быть уничтожено огнём."],
  ["populabundus", "<big>populabundus</big>\n\n<b>populabundus, a, um</b> [populor]\nопустошительный (bellum <i>L</i>); разоряющий (hostis <i>L</i>)."],
  ["popularia", "<big>popularia</big>\n\n<b>popularia, ium</b> <i>n</i> (<i>sc.</i> subsellia)\nместа для простого народа в театре <i>Su</i>."],
  ["popularis", "<big>popularis</big>\n\n<b>I popularis, e</b>\n<b>1)</b> отечественный, местный, туземный (flumina, oliva <i>O</i>); принадлежащий к тому же народу (puella <i>C</i>);\n<b>2)</b> народный (carmen <i>SenT</i>; verba <i>C</i>): civitas (res publica) p. <i>C etc.</i> демократия; aura <i>C</i> (<i>тж.</i> ventus <i>C</i> <i>и</i> plausus <i>C, CJ</i>) p. народное расположение; oratio p. <i>C</i> речь к народу; munus populare <i>C</i> дар народу;\n<b>3)</b> популярный, пользующийся народной любовью (consul, pax <i>C</i>; ministerium <i>L</i>); преданный народу (vir <i>L</i>; civis <i>C</i>);\n<b>4)</b> простонародный, из простого народа (homo <i>Vlg</i>); простой (sal <i>Cato</i>).\n\n<b>II popularis, is</b> <i>m, f</i>\n<b>1)</b> соотечественник, земляк (meus <i>и</i> mea <i>Pt, C etc.</i>); туземец, житель (alicujus loci <i>C</i>);\n<b>2)</b> товарищ, спутник (amicus et p. <i>Ter</i>);\n<b>3)</b> соучастник, сообщник (p. conjurationis <i>Sl</i>);\n<b>4)</b> <i>pl.</i> популяры, демократы, народная партия (populares et optimates <i>C, Nep</i>);\n<b>5)</b> <i>pl.</i> гражданское население (populares et milites <i>Capit</i>)."],
  ["popularitas", "<big>popularitas</big>\n\n<b>popularitas, atis</b> <i>f</i>\n<b>1)</b> общность родины, происхождение из одного города: popularitatis causa <i>Pl</i> на правах согражданина, как соотечественник, как подобает земляку;\n<b>2)</b> стремление угодить народу, желание заслужить популярность <i>Su, T, St</i>;\n<b>3)</b> народонаселение <i>Tert</i>."],
  ["populariter", "<big>populariter</big>\n\n<b>populariter</b> [popularis]\n<b>1)</b> популярно, общедоступно, в народном духе, просто (loqui <i>C</i>);\n<b>2)</b> на благо народа (agere multa <i>C</i>);\n<b>3)</b> <i>иногда</i> демагогически (contiones concitare <i>C</i>)."],
  ["populatim", "<big>populatim</big>\n\n<b>populatim</b> [populus]\nот народа к народу, <i>т. е.</i> повсюду <i>Caec</i>."],
  ["populatio", "<big>populatio</big>\n\n<b>populatio, onis</b> <i>f</i> [populor]\n<b>1)</b> опустошение, разорение, разрушение (agrorum <i>L, Just</i>; villarum vicorumque <i>QC</i>);\n<b>2)</b> ограбление (viatorum <i>Col</i>);\n<b>3)</b> награбленная добыча (pleni populationum <i>L</i>);\n<b>4)</b> разложение, распад (morum <i>PM</i>)."],
  ["populator", "<big>populator</big>\n\n<b>populator, oris</b> <i>m</i> [populor]\nразрушитель, разоритель, опустошитель (agrorum <i>L</i>; Trojae <i>O</i>); истребитель, пожиратель (glandis p. aper <i>M</i>)."],
  ["populatrix", "<big>populatrix</big>\n\n<b>populatrix, icis</b> <i>f</i> [populator]\nразрушительница, истребительница: p. virgo profundi <i>St</i> = Scylla || опустошительница (p. Hymetti apis <i>M</i>)."],
  ["populatus", "<big>populatus</big>\n\n<b>populatus, (us) </b> <i>m</i> [populor]\nопустошение, разорение <i>Lcn, Sid</i>."],
  ["populetum", "<big>populetum</big>\n\n<b>populetum, i</b> <i>n</i> [populus II]\nтополевая роща <i>PM</i>."],
  ["populeus", "<big>populeus</big>\n\n<b>populeus, a, um</b> [populus II]\nтополевый (frondes <i>V</i>; corona <i>H</i>)."],
  ["populifer", "<big>populifer</big>\n\n<b>populifer, fera, ferum</b> [populus + fero]\nпоросший тополями (Padus <i>O</i>)."],
  ["Populifugia", "<big>Populifugia</big>\n\n<b>Populifugia, orum</b> <i>n</i> <i>Macr</i>\n= Poplifugia."],
  ["populiscitum", "<big>populiscitum</big>\n\n<b>populi–scitum, i</b> <i>n</i> (<i>тж. раздельно</i>)\nвсенародное определение, решение народа, плебисцит <i>C</i>."],
  ["populito", "<big>populito</big>\n\n<b>populito, —, —, are</b> <i>Dig</i>\n= populor."],
  ["populneus", "<big>populneus</big>\n\n<b>populneus, a, um</b> <i>Cato, Col</i>\n= populeus."],
  ["populnus", "<big>populnus</big>\n\n<b>populnus, a, um</b> <i>Pt</i>\n= populeus."],
  ["populo", "<big>populo</big>\n\n<b>populo, avi, —, are</b> (= populor)\n<b>1)</b> расхищать, растаскивать (formicae farris acervum populant <i>V</i>);\n<b>2)</b> ограблять (populata provincia <i>C</i>);\n<b>3)</b> уничтожать, истреблять (Achivos <i>Prp</i>)."],
  ["Populonia", "<big>Populonia</big>\n\n<b>I Populonia, ae</b> <i>f</i>\nПопулония, <i>приморский город в Этрурии</i> (<i>близ нын.</i> Piombino) <i>V, Mela</i>.\n\n<b>II Populonia, ae</b> <i>f</i> [populo]\nПопулония («Охраняющая от грабежей»), <i>эпитет Юноны</i> <i>Sen ap. Aug, Macr</i>."],
  ["Populonienses", "<big>Populonienses</big>\n\n<b>Populonienses, ium</b> <i>m</i>\nжители города Populonia <i>L</i>."],
  ["Populonium", "<big>Populonium</big>\n\n<b>Populonium, i</b> <i>n PM</i> <i>и</i> <b>Populonii, orum</b> <i>m</i> <i>L</i>\n= Populonia."],
  ["populor", "<big>populor</big>\n\n<b>populor, atus sum, ari</b> <i>depon.</i>\n<b>1)</b> опустошать, разорять (agros <i>C, L</i>); разрушать (formam populabitur aetas <i>O</i>);\n<b>2)</b> отнимать, лишать (aliqua re <i>Sen</i>);\n<b>3)</b> увечить: populata tempora raptis auribus <i>V</i> виски, обезображенные из-за отрезанных ушей."],
  ["populositas", "<big>populositas</big>\n\n<b>populositas, atis</b> <i>f</i>\nмножество (deorum <i>Eccl</i>)."],
  ["populosus", "<big>populosus</big>\n\n<b>populosus, a, um</b> [populus I]\nмноголюдный, сильно населённый (civitas <i>Ap</i>); многочисленный (gens <i>Ap, Amm</i>)."],
  ["populus", "<big>populus</big>\n\n<b>I populus, i</b> <i>m</i>\n<b>1)</b> народ, народность (p. Romanus <i>C etc.</i>; p. Atheniensium <i>C</i>);\n<b>2)</b> гражданское общество, демократическое государство (populi potentia <i>Nep</i>): ad retinendam populi fidem <i>T</i> для поддержания государственного кредита;\n<b>3)</b> (<i>в Риме до С. Туллия</i>) патриции, зиать (p. plebesque Romana <i>C</i>); <i>впоследствии</i> весь народ (senatus populusque Romanus, <i>в сокр.</i> S. P. Q. R. <i>C etc.</i>): aliquis ex (<i>или</i> de) populo <i>C</i> первый встречный, любой (человек);\n<b>4)</b> простой народ, плебс (p. patresque <i>M</i>): ad populum phaleras — <i>см.</i> phalerae; unus e populo <i>Sen</i> один из многих, заурядный человек:\n<b>5)</b> <i>тж. pl.</i> народная масса, толпа (concursus populi <i>L</i>): omnes in me populi fabulabantur <i>Ap</i> все заговорили обо мне;\n<b>6)</b> население (p. urbanus <i>Nep</i>);\n<b>7)</b> улица; populum spectare <i>O</i> быть обращенным в сторону улицы (<i>о наружной стороне входной двери</i>);\n<b>8)</b> общественная казна <i>T, J, G</i>;\n<b>9)</b> множество, рой (apum <i>Col</i>); масса (imaginum <i>PM</i>; scelerum <i>Sid</i>);\n<b>10)</b> область, местность (frequens cultoribus p. <i>L</i>).\n\n<b>II populus, i</b> <i>f</i>, <i>редко</i> (<i>Veg</i>) <i>m</i>\nтополь <i>V, O, PM etc.</i>: p. alba <i>H</i> серебристый тополь."],
  ["por-", "<big>por-</big>\n\n<b>por-</b> [<i>из</i> pro-]\n<i>глагольная приставка</i> (por–rigo, por–tento <i>и т. п.</i>)."],
  ["porca", "<big>porca</big>\n\n<b>porca, ae</b> <i>f</i>\n<b>1)</b> свинья (<i>самка</i>) <i>Cato, C etc.</i>;\n<b>2)</b> гряда <i>Vr, Col</i>;\n<b>3)</b> испанская поземельная мера (<i>30</i> X <i>180 римск. футов</i>) <i>Col</i>."],
  ["porcarius", "<big>porcarius</big>\n\n<b>porcarius, a, um</b>\nсвиной (vulva <i>PM</i>)."],
  ["porcastrum", "<big>porcastrum</big>\n\n<b>porcastrum, i</b> <i>n</i> <i>бот.</i>\nпортулак <i>Ap</i>."],
  ["porcella", "<big>porcella</big>\n\n<b>porcella, ae</b> <i>f</i> [<i>demin. к</i> porca]\nсвинка <i>Pl</i>."],
  ["porcellio", "<big>porcellio</big>\n\n<b>porcellio, onis</b> <i>m</i>\nмокрица <i>CA</i>."],
  ["porcellulus", "<big>porcellulus</big>\n\n<b>porcellulus, i</b> <i>m</i> [<i>demin. к</i> porcellus]\nсовсем маленький поросёнок <i>Lampr</i>."],
  ["porcellus", "<big>porcellus</big>\n\n<b>porcellus, i</b> <i>m</i> [<i>demin. к</i> porcus]\nпоросёнок <i>Vr, Ph, Pt</i>."],
  ["porceo", "<big>porceo</big>\n\n<b>porceo, —, —, ere</b> [pro + arceo]\nудерживать, задерживать (aliquem aliqua re <i>Vr</i> <i>и</i> ab aliqua re <i>Acc</i>): ab armis anni porcent <i>Acc</i> годы не позволяют взяться за оружие."],
  ["porcetra", "<big>porcetra</big>\n\n<b>porcetra, ae</b> <i>f</i> [porca]\nсвинья, опоросившаяся только один раз <i>AG</i>."],
  ["Porcia", "<big>Porcia</big>\n\n<b>Porcia, ae</b> <i>f</i>\nПорция, <i>сестра Катона Младшего, жена Домиция Аэнобарба</i> <i>C</i>."],
  ["porcillaca", "<big>porcillaca</big>\n\n<b>porcillaca, ae</b> <i>f</i> <i>бот.</i>\nпортулак <i>PM</i>."],
  ["porcina", "<big>porcina</big>\n\n<b>porcina, ae</b> <i>f</i> (<i>sc.</i> caro)\nсвинина <i>Pl, Vop, CA</i>."],
  ["porcinarius", "<big>porcinarius</big>\n\n<b>porcinarius, i</b> <i>m</i> [porcus]\nсвиноторговец <i>или</i> колбасник <i>Pl</i>."],
  ["porcinus", "<big>porcinus</big>\n\n<b>porcinus, a, um</b> [porcus]\nсвиной (caro <i>Lampr, CA</i>; vox <i>Sen</i>): caput porcinum <i>Veg</i> «свинья», клинообразный боевой порядок."],
  ["Porcius", "<big>Porcius</big>\n\n<b>Porcius, a, um</b>\nПорций, <i>римск. nomen; наиболее известны</i>:\n<b>1)</b> M. P. Cato — <i>см.</i> Cato;\n<b>2)</b> M. P. Laeca, <i>сообщник Катилины</i> <i>C, Sl</i>;\n<b>3)</b> L. P. Licinus, <i>претор в 207 г. до н. э.</i> <i>L</i>."],
  ["porcula", "<big>porcula</big>\n\n<b>porcula, ae</b> <i>f</i> <i>Pl</i>\n= porcella."],
  ["porculatio", "<big>porculatio</big>\n\n<b>porculatio, onis</b> <i>f</i> [porculus]\nразведение свиней, свиноводство <i>Vr</i>."],
  ["porculator", "<big>porculator</big>\n\n<b>porculator, oris</b> <i>m</i>\nсвиновод <i>Vr, Col</i>."],
  ["porculetum", "<big>porculetum</big>\n\n<b>porculetum, i</b> <i>n</i> [porca 2]\nполе, разделённое на гряды <i>PM</i>."],
  ["porculus", "<big>porculus</big>\n\n<b>porculus, i</b> <i>m</i>\n<b>1)</b> <i>Pl, PM, Eccl</i> = porcellus;\n<b>2)</b> крюк в виноградном прессе <i>Cato</i>."],
  ["porcus", "<big>porcus</big>\n\n<b>porcus, i</b> <i>m</i>\n<b>1)</b> свинья <i>C, Col etc.</i>: p. femina <i>C</i> свиная матка, свиноматка; caput porci <i>Amm</i> = caput porcinum (<i>см.</i>);\n<b>2)</b> поросёнок (p. lactens <i>Vr</i>): p. Trojanus <i>Macr</i> (<i>по шутливому намёку на Троянского коня</i>) свинья, начинённая разной дичью и вместе с ней зажаренная;\n<b>3)</b> (<i>тж.</i> p. marinus) <i>предпол.</i> бурый дельфин <i>PM</i>;\n<b>4)</b> <i>Vr</i> = virginis pudenda."],
  ["porgo", "<big>porgo</big>\n\n<b>porgo, porxi, —, ere</b> <i>C, V, L etc.</i>\n= porrigo."],
  ["poricinum", "<big>poricinum</big>\n\n<b>poricinum, i</b> <i>n</i> (<i>или</i> <b>-us, i</b> <i>m</i>) <i>Pt</i>\n(<i>v. l.</i>)= forceps."],
  ["porphyreticus", "<big>porphyreticus</big>\n\n<b>porphyreticus, a, um</b> (<i>греч.</i>)\nпурпурно-красный (marmor <i>Su</i>); из пурпурного мрамора (porticus <i>Vop</i>)."],
  ["porphyrio", "<big>porphyrio</big>\n\n<b>I porphyrio, onis</b> <i>m</i> (<i>греч.</i>) <i>зоол.</i>\nлысуха красная (<i>Fulica porphyrio</i>, L.) <i>PM, Vlg</i>.\n\n<b>II Porphyrio, onis</b> <i>m</i>\n(Pomponius) Порфирион, <i>комментатор Горация (III в. н. э.)</i>."],
  ["Porphyrion", "<big>Porphyrion</big>\n\n<b>Porphyrion, onis</b> <i>m</i>\nПорфирион, <i>один из Гигантов</i> <i>H</i>."],
  ["porraceus", "<big>porraceus</big>\n\n<b>porraceus, a, um</b> [porrum]\nпорейный (folia <i>PM</i>); похожий на порей <i>или</i> цвета порея (gemma <i>PM</i>)."],
  ["porrecte", "<big>porrecte</big>\n\n<b>porrecte</b> [porrectus II]\nдалеко (porrectius progressus <i>Amm</i>); широко (sagum porrectius pansum <i>Amm</i>)."],
  ["porrectio", "<big>porrectio</big>\n\n<b>porrectio, onis</b> <i>f</i> [porrigo I]\n<b>1)</b> протягивание, простирание (brachii <i>rhH</i>);\n<b>2)</b> прямая линия <i>Vtr</i>."],
  ["porrectum", "<big>porrectum</big>\n\n<b>porrectum, i</b> <i>n</i> [porrigo I]\n<b>1)</b> протяжение: in p. longitudo <i>PM</i> протяжение в длину;\n<b>2)</b> прямая линия <i>Vtr</i>."],
  ["porrectus", "<big>porrectus</big>\n\n<b>I porrectus, a, um</b> <i>part. pf. к</i>\nporricio.\n\n<b>II porrectus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> porrigo I;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> протяжённый, обширный (loca <i>Cs</i>); растянутый, длинный (acies <i>T</i>);\n<b>2)</b> прояснившийся, ясный: porrectiore fronte loqui <i>Pl</i> говорить в более спокойном тоне;\n<b>3)</b> продолжительный (mora <i>O</i>);\n<b>4)</b> <i>грам.</i> долгий (syllaba <i>Q</i>)."],
  ["porrexi", "<big>porrexi</big>\n\n<b>porrexi</b> <i>pf. к</i>\nporrigo I."],
  ["porricio", "<big>porricio</big>\n\n<b>porricio, —, rectum, ere</b> [<i>из</i> *porjicio <i>от</i> projicio]\n<b>1)</b> бросать в жертву, посвящать богам (exta in fluctus p. <i>V</i>): inter caesa et porrecta <i>погов. C</i> между закланием и возложением на алтарь, <i>т. е.</i> накануне решительного момента, в самую последнюю минуту, <i>по друг.</i> — не вовремя, некстати;\n<b>2)</b> производить на свет (seges frumentum porricit <i>Vr</i>)."],
  ["porrigo", "<big>porrigo</big>\n\n<b>I por–rigo, rexi, rectum, ere</b> [por = pro + rego]\n<b>1)</b> вытягивать, протягивать, простирать (manus ad <i>или</i> in caelum <i>L</i>; bracchia caelo <i>O</i>): qua primas porrigit aequor aquas <i>O</i> куда море дохлёстывает первые волны, <i>т. е.</i> куда доходит морской прибой || поднимать (pocula <i>H</i>; manum <i>C</i>); расширять, развёртывать (aciem latius <i>Sl</i>); растягивать (syllabam <i>O</i>); увеличивать, повышать (vectigalia <i>H</i>);\n<b>2)</b> давать, протягивать (alicui dextram <i>C</i>; puero poma <i>H</i>): mihi forsan, tibi quod negarit, porriget hora <i>H</i> время, быть может, уделит мне то, в чём отказало тебе || оказывать (amicis praesidium <i>C</i>);\n<b>3)</b>: se p. <i>или</i> <i>pass.</i> простираться, тянуться (ab ortu solis ad occidentem <i>PM</i>): novem jugera porrigi <i>V</i> растянуться на (занять собою) девять югеров || <i>перен.</i> распространяться, достигать (quo se tua porrigat ira? <i>O</i>); затягиваться (morbus porrigitur <i>CC</i>);\n<b>4)</b> уложить, свалить, повергнуть (aliquem in herbis <i>O</i>). — <i>См. тж.</i> porrectus II.\n\n<b>II porrigo, inis</b> <i>f</i>\nпарша <i>CC, H, PM</i>."],
  ["Porrima", "<big>Porrima</big>\n\n<b>Porrima, ae</b> <i>f</i>\nПоррима, <i>римск. богиня, чтившаяся женщинами</i> <i>O</i>."],
  ["porrina", "<big>porrina</big>\n\n<b>porrina, ae</b> <i>f</i> [porrum]\nгрядка порея <i>Cato, Dig</i>."],
  ["porrixo", "<big>porrixo</big>\n\n<b>porrixo, —, —, are</b> [<i>intens. к</i> porrigo I]\nсильно вытягивать (aliquid <i>Ap</i>)."],
  ["porro", "<big>porro</big>\n\n<b>porro</b> <i>adv.</i> (<i>греч.</i>)\n<b>1)</b> дальше вперёд (ire <i>L</i>; agere armentum <i>L</i>): age p. <i>C</i> ну дальше; perge p. <i>C</i> продолжай || дальше впереди, вдали: inscius, quae sint ea flumina p. <i>V</i> не зная, что там впереди за реки;\n<b>2)</b> в дальнейшем, в будущем, впоследствии, впредь (amare p. omnes annos <i>Ctl</i>): fac, eadem ut sis p. <i>Ter</i> будь и впредь такой же;\n<b>3)</b> в свою очередь, со своей стороны: p. autem <i>St</i> с другой же стороны; saepe audivi a majoribus natu, qui se p. pueros a senibus audivisse dicebant <i>C</i> я часто слышал от старших, которые, по их словам, в свою очередь, будучи мальчиками, слышали (это) от стариков;\n<b>4)</b> даже, более того (exiguus et p. minimus <i>C</i>)."],
  ["porrum", "<big>porrum</big>\n\n<b>porrum, i</b> <i>n</i> <i>бот.</i>\nпорей <i>Col, Pt etc.</i>"],
  ["porrus", "<big>porrus</big>\n\n<b>porrus, i</b> <i>m</i> <i>Pall, M etc.</i>\n= porrum."],
  ["Porsena", "<big>Porsena</big>\n\n<b>Porsena</b>, <i>тж.</i> <b>Porsenna, Porsinna, Porsina, ae</b> <i>m</i>\nПорсена, <i>царь Клузия (Этрурия), союзник изгнанных из Рима Тарквиниев, в 509 г. до н. э. одержавший победу над Римом</i> <i>C, L, T, V etc.</i>"],
  ["porta", "<big>porta</big>\n\n<b>porta, ae</b> <i>f</i>\n<b>1)</b> <i>тж. pl.</i> ворота (urbis <i>C</i>; castrorum <i>Cs etc.</i>): p. praetoria (decumana) <i>Cs</i> передние (задние) ворота (<i>в лагере</i>); p. principalis (<i>sc.</i> castrorum) <i>L</i> боковые ворота; portae belli <i>Enn</i> врата войны, <i>т. е.</i> храм Януса; ante leti portas <i>Lcr</i> у врат (в преддверии) смерти; portae solis <i>Macr</i> тропики Рака и Козерога; p. itineri longissima <i>погов. Vr</i> = труден лишь первый шаг;\n<b>2)</b> <i>pl.</i> ущелье, теснина (portae Ciliciae <i>Nep</i>);\n<b>3)</b> вход, выход, проход: portae jecoris <i>C</i> воротная вена; ventriculi p. <i>CC</i> привратник желудка; quibus e portis <i>перен.</i> <i>Lcr</i> какими путями."],
  ["portabilis", "<big>portabilis</big>\n\n<b>portabilis, e</b> [porto]\nудобоносимый (onus <i>Aug</i>)."],
  ["portarius", "<big>portarius</big>\n\n<b>portarius, i</b> <i>m</i> [porta]\nпривратник <i>Vlg</i>."],
  ["portatio", "<big>portatio</big>\n\n<b>portatio, onis</b> <i>f</i> [porto]\nпереноска, перевозка, доставка (armorum atque telorum <i>Sl</i>)."],
  ["portator", "<big>portator</big>\n\n<b>portator, oris</b> <i>m</i> [porto]\nноситель, переносчик <i>Aug</i>: p. tabellarum <i>Is</i> письмоносец."],
  ["portatoria", "<big>portatoria</big>\n\n<b>portatoria, ae</b> <i>f</i>\nносилки, паланкин <i>CA</i>."],
  ["portatorius", "<big>portatorius</big>\n\n<b>portatorius, a, um</b> [porto]\nносящий на себе (portatoriis nubibus ascendere <i>Aug</i>); носимый: sella portatoria <i>CA</i> носилки."],
  ["portendo", "<big>portendo</big>\n\n<b>portendo, tendi, tentum, ere</b> [por = pro + tendo] <i>культ.</i>\nвозвещать, предвещать, пророчить (dii periculum portendunt <i>C</i>); <i>pass.</i> предвидеться, предстоять (pericula portenduntur <i>L</i>)."],
  ["portentifer", "<big>portentifer</big>\n\n<b>portentifer, fera, ferum</b> [portentum + fero] <i>v. l.</i>\n= portentificus."],
  ["portentificus", "<big>portentificus</big>\n\n<b>portentificus, a, um</b>\n<b>1)</b> чудодейственный (venena <i>O</i>);\n<b>2)</b> диковинный, чудовищный (animalium figurae <i>Lact</i>);\n<b>3)</b> дивный (opera <i>Lact</i>)."],
  ["portentosus", "<big>portentosus</big>\n\n<b>portentosus, a, um</b> [portentum]\nнеобыкновенный, странный, сверхъестественный (oratio <i>Sen</i>); чудовищный (serpens portentosae magnitudinis <i>L</i>); уродливый (puer portentoso capite <i>Su</i>)."],
  ["portentum", "<big>portentum</big>\n\n<b>portentum, i</b> <i>n</i> [portendo]\n<b>1)</b> чудо, чудесное явление, знамение (portenta atque prodigia <i>Sl</i>);\n<b>2)</b> чудовище (portenta subigere <i>H</i>); урод (hominum pecudumque portenta <i>C</i>); выродок (rei publicae <i>C</i>);\n<b>3)</b> вымысел, фантазия (poetarum et pictorum <i>C</i>);\n<b>4)</b> фокус (portenta aliqua facere <i>Pt</i>)."],
  ["portentuosus", "<big>portentuosus</big>\n\n<b>portentuosus, a, um</b> <i>Sen</i>\n= portentosus."],
  ["Porthaon", "<big>Porthaon</big>\n\n<b>Porthaon, onis</b> <i>m</i>\nПортаон, <i>сын Агенора, отец Энея (Oeneus) и Деианиры, царь Калидона</i> <i>Pl, O, St</i>."],
  ["Porthaonius", "<big>Porthaonius</big>\n\n<b>Porthaonius, a, um</b> [Porthaon]\nпортаонов <i>O, St</i>."],
  ["Porthmeus", "<big>Porthmeus</big>\n\n<b>Porthme͡us, eī</b> <i>и</i> <b>eos</b> (<i>acc.</i> ea) <i>m</i> (<i>греч.</i> «Перевозчик»)\n<i>прозвище Харона</i> <i>J, Pt</i>."],
  ["porthmos", "<big>porthmos</big>\n\n<b>porthmos, i</b> <i>m</i> (<i>греч.</i>; <i>лат.</i> fretum)\nпролив <i>PM</i>."],
  ["porticatio", "<big>porticatio</big>\n\n<b>porticatio, onis</b> <i>f</i> [porticus]\nколоннада <i>Dig</i>."],
  ["porticula", "<big>porticula</big>\n\n<b>porticula, ae</b> <i>f</i> [<i>demin. к</i> porticus]\nгалерейка <i>C</i>."],
  ["porticus", "<big>porticus</big>\n\n<b>porticus, us</b> <i>f</i>\n<b>1)</b> портик, колоннада, крытая галерея <i>C, V etc.</i>: p. Stoicorum <i>C</i> <i>портик в Афинах, где Зенон собирал своих слушателей и учеников</i>;\n<b>2)</b> <i>перен.</i> стоическая школа, учение стоиков (Zeno et tota p. <i>C</i>);\n<b>3)</b> зал судебных заседаний <i>C</i>;\n<b>4)</b> <i>воен.</i> крытый ход, навес из vineae <i>Cs</i>;\n<b>5)</b> вход в палатку <i>Enn</i>;\n<b>6)</b> верхний ряд мест в амфитеатре, «галёрка» <i>Calp</i>."],
  ["portio", "<big>portio</big>\n\n<b>portio, onis</b> <i>f</i>\n<b>1)</b> часть, доля <i>PM, Just</i>: pari portione <i>CC</i> равными частями; pro rata (sua <i>Q</i>, virili <i>T, Hier</i>) portione <i>PM</i> со своей стороны <i>или</i> в меру своих сил;\n<b>2)</b> отношение, пропорция: (pro) portione <i>C, PM, Q etc.</i> <i>и</i> ad portionem <i>PM</i> в правильном соотношении, пропорционально, соразмерно."],
  ["portionalis", "<big>portionalis</big>\n\n<b>portionalis, e</b> [portio]\nчастичный <i>Tert</i>."],
  ["portisculus", "<big>portisculus</big>\n\n<b>portisculus, i</b> <i>m</i>\n<b>1)</b> молоток, которым начальник гребцов отбивал такт <i>Enn, Cato, Fronto</i>;\n<b>2)</b> <i>перен.</i> управление (habere portisculum ad aliquid <i>Pl</i>)."],
  ["portitor", "<big>portitor</big>\n\n<b>I portitor, oris</b> <i>m</i> [<i>одного корня с</i> porto]\nперевозчик, Лодочник: p. Orci <i>V</i> <i>и</i> Lethaei p. amnis <i>St</i> = Charon; p. Heiles <i>Lcn</i> <i>или</i> Phrixi <i>Col</i> = aries; p. litterarum (epistularum) <i>Hier</i> <i>и</i> apicum <i>Sid</i> письмоносец; p. Ursae <i>Sl</i> = созвездие Волопаса.\n\n<b>II portitor, oris</b> <i>m</i> [portus]\nсборщик таможенных пошлин <i>Pl, Ter, C</i>; <i>шутл.</i> (<i>о жене</i>) всюду сующий свой нос, любопытный <i>Pl</i>."],
  ["portiuncula", "<big>portiuncula</big>\n\n<b>portiuncula, ae</b> <i>f</i> [<i>demin. к</i> portio]\nчастица, кусочек (vestis <i>PM</i>; aedium <i>Dig</i>)."],
  ["porto", "<big>porto</big>\n\n<b>porto, avi, atum, are</b>\n<b>1)</b> носить, переносить (omnia mecum porto mea <i>C</i>); перевозить (statuas in Italiam <i>VP</i>);\n<b>2)</b> ввозить, привозить (commeatum in castra <i>L</i>); иметь на борту (navis milites portat <i>Cs</i>); доставлять (nuntium ad aliquem <i>L</i>); <i>pass.</i> portari передвигаться (lectica <i>C</i>) <i>или</i> ехать (vehiculo <i>Nep</i>);\n<b>3)</b> передавать (preces alicujus alicui <i>VF</i>);\n<b>4)</b> наносить (vulnus cuspide <i>Sil</i>);\n<b>5)</b> направлять (ad moenia gressum <i>Sil</i>)."],
  ["portorium", "<big>portorium</big>\n\n<b>portorium, i</b> <i>n</i> [portus]\nпошлина, сбор (portoria et vectigalia <i>L</i>)."],
  ["portuensis", "<big>portuensis</big>\n\n<b>portuēnsis, e</b> [portus]\nпортовый (horrea <i>CTh</i>)."],
  ["portula", "<big>portula</big>\n\n<b>portula, ae</b> <i>f</i> [<i>demin. к</i> porta]\nнебольшие ворота, воротца <i>L</i>."],
  ["portulaca", "<big>portulaca</big>\n\n<b>portulāca, ae</b> <i>f</i> <i>бот.</i>\nпортулак <i>Vr, CC, Col</i>."],
  ["Portunalia", "<big>Portunalia</big>\n\n<b>Portūnālia, ium</b> <i>n</i> [Portunus]\nпортуналии, <i>празднества в честь Портуна</i> <i>Vr</i>."],
  ["Portunus", "<big>Portunus</big>\n\n<b>Portūnus, ī</b> <i>m</i>\nПортун, <i>римск. бог портов и пристаней</i> (<i>впоследствии отождествлённый с греч. Палемоном</i>) <i>C, V, O etc.</i>"],
  ["portuosus", "<big>portuosus</big>\n\n<b>portuōsus, a, um</b> [portus]\nимеющий много гаваней, изобилующий портами (mare <i>C</i>; pars Numidiae <i>Sl</i>); встречающий много портов (navigatio minime portuosa <i>C</i>)."],
  ["portus", "<big>portus</big>\n\n<b>portus, ūs</b> <i>m</i> (<i>dat. abl. pl.</i> <i>тж.</i> ubus)\n<b>1)</b> порт, пристань, гавань: portum tenere <i>C</i> стоять в порту; portum capere <i>Cs</i> прибыть в порт; in portu operam dare <i>C</i> служить в портовой таможне; in portu esse <i>C</i> (navigare <i>Ter</i>) <i>погов.</i> = находиться в безопасности;\n<b>2)</b> убежище, защита (perfugium portusque <i>C</i>);\n<b>3)</b> устье (septem portus, <i>sc.</i> Nili <i>O</i>);\n<b>4)</b> товарный склад, складское помещение <i>CTh, Dig</i>;\n<b>5)</b> пристанище, дом <i>LXIIT</i>."],
  ["Porus", "<big>Porus</big>\n\n<b>I Porus, i</b> <i>m</i>\nПор, <i>царь Индии, побеждённый Александром Македонским</i> <i>QC</i>.\n\n<b>II porus, i</b> <i>m</i> (<i>греч.</i>)\nбелый туф <i>PM</i>.\n\n<b>III porus, i</b> <i>m</i> (<i>греч.</i>) <i>анат.</i>\nпроход, канал <i>Ambr, Is</i>."],
  ["porxi", "<big>porxi</big>\n\n<b>porxi</b> <i>St стяж. pf. к</i>\nporrigo I."],
  ["pos", "<big>pos</big>\n\n<b>pos</b> <i>арх.</i>\n= post."],
  ["posca", "<big>posca</big>\n\n<b>posca, ae</b> <i>f</i> [poto]\nнапиток из воды, уксуса и яиц <i>Pl, CC</i>."],
  ["poscaenium", "<big>poscaenium</big>\n\n<b>poscaenium, i</b> <i>n</i> [post + scaena]\nтеатральные кулисы, постсцениум: poscaenia vitae <i>Lcr</i> житейские тайны."],
  ["poscinummius", "<big>poscinummius</big>\n\n<b>poscinummius, a, um</b> [posco + nummus]\nтребующий денег, сребролюбивый, продажный, корыстный (oscula <i>Ap</i>)."],
  ["posco", "<big>posco</big>\n\n<b>posco, poposci, —, ere</b>\n<b>1)</b> требовать (aliquid sibi <i>C</i>): p. aquam ad manus <i>Pt</i> потребовать воды для (омовения) рук; p. aliquem aquam terramque <i>QC</i> требовать у кого-л. воды и земли, <i>т. е.</i> изъявления покорности; p. aliquem (<i>или</i> ab aliquo) aliquid <i>C etc.</i> требовать у кого-л. чего-л.; p. aliquem imperatorem <i>Sl</i> требовать назначения кого-л. полководцем; poscor aliquid <i>O</i> от меня требуют чего-л.; poscimur <i>O</i> нас ждут;\n<b>2)</b> просить, выпрашивать (p. deos veniam <i>V</i>; p. veniam peccatis <i>H</i>): poscunt fidem secunda, at adversa exigunt <i>SenT</i> счастье нуждается в верности, несчастье же не может обойтись без неё; desunt manus poscentibus arvis <i>Lcn</i> нет рук для нив, которые в них (так) нуждаются;\n<b>3)</b> спрашивать, осведомляться (p. causas <i>V</i>);\n<b>4)</b> звать (clamore aliquem <i>Pl</i>); вызывать (aliquem in proelia <i>V</i>);\n<b>5)</b> звать на суд, привлекать к суду (aliquem <i>C</i>);\n<b>6)</b> взывать, призывать (ventos, numina <i>V</i>)."],
  ["posculentus", "<big>posculentus</big>\n\n<b>posculentus, a, um</b> <i>AG</i>\n= potulentus."],
  ["posea", "<big>posea</big>\n\n<b>posea (posia), ae</b> <i>f</i> <i>v. l.</i>\n= pausea."],
  ["Posides", "<big>Posides</big>\n\n<b>Posides, ae</b> <i>m</i>\nПосид, <i>вольноотпущенник императора Клавдия</i> <i>Su</i>."],
  ["Posideum", "<big>Posideum</big>\n\n<b>Posideum, i</b> <i>n</i>\nПосидей:\n<b>1)</b> <i>мыс в Ионии</i> <i>PM</i>;\n<b>2)</b> <i>мыс в Паллене</i> (<i>Македония</i>) <i>L</i>."],
  ["Posidonia", "<big>Posidonia</big>\n\n<b>Posidonia, ae</b> <i>f</i>\nПосидония, <i>греч. название города</i> Paestum <i>PM</i>."],
  ["Posidonius", "<big>Posidonius</big>\n\n<b>Posidonius, i</b> <i>m</i>\nПосидоний, <i>философ-стоик из Апамеи (Сирия) (II-I вв. до н. э.), ученик Панэтия в Афинах, учивший на о-ве Родос</i> <i>C, PM</i>."],
  ["positio", "<big>positio</big>\n\n<b>positio, onis</b> <i>f</i> [pono]\n<b>1)</b> установка, посадка (surculi <i>Col</i>);\n<b>2)</b> постановка, употребление (nominis pro nomine <i>Q</i>);\n<b>3)</b> положение (loci <i>Sen, Q</i>): p. <i>и</i> p. caeli <i>Col, T</i> климатическая область, климат; positiones <i>Q</i> обстоятельства || позиция (corporis <i>Sen</i>); состояние (mentis <i>Sen</i>);\n<b>4)</b> установление, утверждение: paupertas non per positionem, sed per detractionem dicitur <i>Sen</i> «бедность» понимается в смысле не утверждения (чего-л.), а лишения (<i>т. е.</i> она есть понятие не положительное, а отрицательное);\n<b>5)</b> тезис, положение, тема <i>Q</i>;\n<b>6)</b> <i>грам.</i> окончание (p. feminina <i>Q</i>): prima p. <i>грам.</i> исходная форма;\n<b>7)</b> нисходящее ударение (a sublatione ad positionem <i>Q</i>);\n<b>8)</b> положение гласной перед двумя согласными, <i>т. е.</i> долгота по положению (p. syllabae <i>Q</i>)."],
  ["positivum", "<big>positivum</big>\n\n<b>positivum, i</b> <i>n</i> (<i>sc.</i> nomen)\nимя существительное <i>Macr</i>."],
  ["positivus", "<big>positivus</big>\n\n<b>positivus, a, um</b> [pono]\n<b>1)</b> условный, произвольный (nomen non positivum, sed naturale <i>Nigidius ap. AG</i>);\n<b>2)</b> <i>грам.</i> положительный (gradus)."],
  ["positor", "<big>positor</big>\n\n<b>positor, oris</b> <i>m</i> [pono]\nоснователь, строитель (moenium, templorum <i>O</i>)."],
  ["positura", "<big>positura</big>\n\n<b>positura, ae</b> <i>f</i> [pono]\n<b>1)</b> положение (stellarum <i>AG</i>); постановка, расположение (verborum <i>AG</i>);\n<b>2)</b> создание, творение (<i>sc.</i> mundorum <i>Prp</i>);];\n<b>3)</b> <i>грам.</i> пунктуация."],
  ["positus", "<big>positus</big>\n\n<b>I positus, a, um</b> <i>part. pf. к</i>\npono.\n\n<b>II positus, us</b> <i>m</i> [pono]\n<b>1)</b> <i>Sl, O, T etc.</i> = positura;\n<b>2)</b> <i>AG</i> = positio 8;\n<b>3)</b> укладывание, причёсывание (positu variare capillos <i>O</i>)."],
  ["posivi", "<big>posivi</big>\n\n<b>posivi</b> <i>арх. Pl, Cato, C pf. к</i>\npono."],
  ["posmeridianus", "<big>posmeridianus</big>\n\n<b>posmeridianus</b> <i>v. l.</i>\n= postmeridianus."],
  ["posquam", "<big>posquam</big>\n\n<b>posquam</b> <i>Pl</i>\n= postquam."],
  ["posse", "<big>posse</big>\n\n<b>posse</b> <i>inf. к</i>\npossum."],
  ["possedi", "<big>possedi</big>\n\n<b>possedi</b> <i>pf. к</i>\n<b>1)</b> possideo <i>и</i>\n<b>2)</b> possido."],
  ["possessio", "<big>possessio</big>\n\n<b>I possessio, onis</b> <i>f</i> [possideo]\n<b>1)</b> владение, обладание: esse in possessione alicujus rei <i>C</i> владеть чём-л., иметь что-л.; p. rei publicae <i>C</i> управление государством;\n<b>2)</b> собственность, владение, имение (habere magnas possessiones <i>C</i>).\n\n<b>II possessio, onis</b> <i>f</i> [possido]\nзавладение, присвоение, захват (bonorum <i>C</i>; regni <i>L</i>): in possessionera mittere <i>C</i> послать для захвата (чьего-л. владения)."],
  ["possessiuncula", "<big>possessiuncula</big>\n\n<b>possessiuncula, ae</b> <i>f</i> [<i>demin. к</i> possessio I]\nнебольшое имение, именьице <i>C</i>."],
  ["possessivus", "<big>possessivus</big>\n\n<b>possessivus, a, um</b> [possideo] <i>грам.</i>\nобозначающий принадлежность, притяжательный (nomen <i>Q</i>): casus p. родительный падеж."],
  ["possessor", "<big>possessor</big>\n\n<b>possessor, oris</b> <i>m</i> [possideo]\n<b>1)</b> владелец, обладатель (agrorum <i>L, Col</i>);\n<b>2)</b> повелитель (Italici litoris aquila p. <i>Pt</i>): p. regni inertis <i>Lcn</i> = Pluto;\n<b>3)</b> <i>юр.</i> владеющий предметом спора, <i>т. е.</i> ответчик <i>PM, Q, Dig</i>."],
  ["possessorius", "<big>possessorius</big>\n\n<b>possessorius, a, um</b>\nкасающийся владения (actio <i>Dig</i>)."],
  ["possessus", "<big>possessus</big>\n\n<b>I possessus, a, um</b> <i>part. pf. к</i>\n<b>1)</b> possideo <i>и</i>\n<b>2)</b> possido.\n\n<b>II possessus, (us) </b> <i>m</i> [possideo]\nобладание, владение (possessu carere, usu frui <i>Ap</i>)."],
  ["possibile", "<big>possibile</big>\n\n<b>possibile, is</b> <i>n</i> [possum]\nвозможное <i>Q</i>."],
  ["possibilis", "<big>possibilis</big>\n\n<b>possibilis, e</b> [possum]\nвозможный <i>Dig, Vlg, Eccl</i>."],
  ["possibilitas", "<big>possibilitas</big>\n\n<b>possibilitas, atis</b> <i>f</i>\nвозможность <i>Eccl, CA</i>: pro possibilitate <i>Pall, Amm</i> в меру возможности."],
  ["possideo", "<big>possideo</big>\n\n<b>possideo, sedi, sessum, ere</b> [potis + sedeo]\n<b>1)</b> обладать, владеть, иметь (bona, agrum, ingenium <i>C</i>; solum bello captum <i>L</i>);\n<b>2)</b> занимать (forum armatis catervis <i>C</i>)."],
  ["possido", "<big>possido</big>\n\n<b>pos–sido, sedi, sessum, ere</b>\nовладевать, завладевать, захватывать (aliquid <i>Lcr, Cs, C, O etc.</i>)."],
  ["possiem", "<big>possiem</big>\n\n<b>possiem, possiet</b> <i>Pl</i>\n(<i>арх.</i>) <i>praes. conjct. к</i> possum."],
  ["possum", "<big>possum</big>\n\n<b>possum, potui, —, posse</b>\n<b>1)</b> мочь, быть в состоянии (facere aliquid <i>Pl, C etc.</i>); si posset (<i>sc.</i> fieri) <i>C</i> если бы это было возможно; quantum potest <i>Ter</i> <i>и</i> ut potest <i>C</i> насколько возможно; qui potest? <i>C</i> как (это) возможно?; (fieri) potest (ut) <i>C etc.</i> возможно (что); fieri non potest (ut non <i>или</i> quin) <i>C etc.</i> невозможно (чтобы не); possunt, quia posse videntur (<i>sc.</i> sibi) <i>V</i> <i>впосл.</i> <i>погов.</i> они могут (добиться победы), ибо они в этом убеждены;\n<b>2)</b> быть в состоянии сделать (multum <i>C</i>) <i>или</i> обладать (p. aliquam <i>M</i>); omnia posse <i>C</i> быть всемогущим, обладать абсолютной властью; optime p. <i>C</i> иметь прекрасные способности;\n<b>3)</b> иметь значение, пользоваться влиянием (apud aliquem <i>Cs, Hirt</i>);\n<b>4)</b> уметь (non omnia possumus omnes <i>V</i>)."],
  ["post", "<big>post</big>\n\n<b>I post</b> <i>adv.</i>\n<b>1)</b> сзади, позади (ante aut p. <i>C</i>): p. esse <i>перен.</i> <i>C, Sl etc.</i> быть позади, ниже <i>или</i> на втором плане, уступать; fronte capillata, p. est occasio calva <i>погов. DC</i> случай спереди волосат, а сзади лыс (<i>т. е.</i> упустишь случай, не поймаешь);\n<b>2)</b> затем, потом, после, позже, впоследствии (ali quanto p. <i>C</i>): anno p. <i>C, Cs</i> год спустя; die p. <i>Cs</i> на следующий день.\n\n<b>II post</b> <i>praep. cum acc.</i>\n1) за, позади (p. me erat Aegina, ante Megara <i>C</i>);\n<b>2)</b> после, спустя, по истечении (p. longum tempus <i>C</i>); diebus decem post eum diem <i>C</i> спустя десять дней с того времени; p. homines natos <i>C</i> с тех пор, как появились люди (с незапамятных времён); p. urbem conditam <i>C, L</i> после основания города (Рима); habere (ducere, ponere) aliquid p. aliquid <i>C, Sl etc.</i> ставить что-л. ниже чего-л."],
  ["postautumnalis", "<big>postautumnalis</big>\n\n<b>post–autumnalis, e</b>\nпослеосенний (pira <i>PM</i>)."],
  ["postdelictum", "<big>postdelictum</big>\n\n<b>post–delictum, i</b> <i>n</i>\nпоследующее преступление, <i>т. е.</i> рецидив <i>Dig</i>."],
  ["poste", "<big>poste</big>\n\n<b>poste</b> <i>Pl</i>\n(<i>v. l.</i>) = post <i>или</i> postea."],
  ["postea", "<big>postea</big>\n\n<b>post–ea</b> <i>adv.</i> [post + id]\n<b>1)</b> (<i>тж.</i> p. loci <i>Sl</i>) после того, потом, позже, впоследствии: brevi p. <i>C</i> немного спустя; p. quam equitatus in conspectum venit <i>Cs</i> после того как (как только) показалась конница;\n<b>2)</b> сверх того, далее: quid p.? <i>Ter, C</i> что (же) дальше? <i>или</i> что из этого?"],
  ["posteaquam", "<big>posteaquam</big>\n\n<b>postea–quam</b> (<i>тж. раздельно</i>) <i>C, Cs etc.</i>\n= postquam."],
  ["postella", "<big>postella</big>\n\n<b>postella, ae</b> <i>f</i> <i>Is</i>\n= postilena."],
  ["posteo", "<big>posteo</big>\n\n<b>post–eo, —, —, ire</b>\nидти позади, <i>т. е.</i> отставать (aliquem honore p. <i>Sid</i>)."],
  ["poster", "<big>poster</big>\n\n<b>poster</b> <i>v. l.</i>\n= posterus."],
  ["posterganeus", "<big>posterganeus</big>\n\n<b>pos–terganeus, a, um</b> [tergum]\nнаходящийся за спиной (occasus <i>Eccl</i>)."],
  ["posteri", "<big>posteri</big>\n\n<b>posteri, orum</b> <i>m</i> [posterus]\nпотомки <i>C etc.</i>"],
  ["posterior", "<big>posterior</big>\n\n<b>posterior, ius</b> [<i>compar. к</i> posterus]\n<b>1)</b> задний (pedes priores et posteriores <i>PM</i>);\n<b>2)</b> (по)следующий, ближайший (pars voluminis <i>VP</i>): paria esse debent posteriora superioribus <i>C</i> последующие элементы (фразы) должны находиться в соответствии с предшествующими; discipulus est prioris p. dies <i>погов. PS</i> (всякий) последующий день есть ученик предыдущего; aetate posterior <i>C</i> младший; tempore p. <i>Sl</i> более поздний; verbum facere posterius <i>H</i> поставить слово в конце;\n<b>3)</b> последний (из упомянутых) (oratores <i>C</i>);\n<b>4)</b> менее важный, менее значительный: suam salutem posteriorem communi salute ducere <i>C</i> своё благо ставить на втором плане после блага общественного;\n<b>5)</b> <i>поздн.</i> (<i>филос.</i>) более поздний, апостериорный: cognitio a posteriori познание «ab effectibus ad causas», <i>впосл.</i> «ex phaenomenis» из явлений, <i>т. е.</i> на основании опыта."],
  ["posteriora", "<big>posteriora</big>\n\n<b>posteriora, um</b> <i>n</i>\nседалище, зад <i>Lampr</i>."],
  ["posteriores", "<big>posteriores</big>\n\n<b>posteriores, um</b> <i>m</i> <i>Dig</i>\n= posteri."],
  ["posteritas", "<big>posteritas</big>\n\n<b>posteritas, atis</b> <i>f</i> [posterus]\n<b>1)</b> будущность, будущее время: in posteritatem <i>C, Just</i> на будущее время, в будущем, впредь;\n<b>2)</b> потомство, потомки (posteritati servire <i>C</i>);\n<b>3)</b> низшее <i>или</i> последнее место (posteritatem alicui rei deputare <i>Tert</i>)."],
  ["posterius", "<big>posterius</big>\n\n<b>I posterius</b> <i>adv.</i>\nпосле, позднее: p. istuc dicis, quam credo tibi <i>Pl</i> ты говоришь это (уже) после того, как я тебе поверил, <i>т. е.</i> не уверяй, и так верю; sed haec p. <i>C</i> но об этом после (скажу).\n\n<b>II posterius</b> <i>n</i> <i>к</i>\nposterior."],
  ["postero", "<big>postero</big>\n\n<b>I postero</b> (<i>sc.</i> die)\nна следующий день <i>T</i>.\n\n<b>II postero, avi, —, are</b> [posterus]\nотставать, запаздывать <i>Pall</i>."],
  ["posterula", "<big>posterula</big>\n\n<b>posterula, ae</b> <i>f</i> [posterus]\nзадний выход, лазейка <i>Amm, Eccl</i>."],
  ["posterus", "<big>posterus</big>\n\n<b>posterus, era, erum</b> [post]\n(<i>compar.</i> posterior, <i>superl.</i> postremus <i>и</i> postumus) следующий (dies, annus <i>C etc.</i>): postero <i>или</i> postero die (quam) <i>C, QC, Pt</i> на следующий день (после того, как); postera Aurora <i>V</i> заря нового дня; in posterum <i>C etc.</i> на будущее <i>или</i> на следующий день; postera aetas <i>H</i> потомство, потомки; postera laus <i>H</i> посмертная слава (у потомков); postera et consequentia <i>C</i> последствия и результаты."],
  ["postfactus", "<big>postfactus</big>\n\n<b>post–factus, a, um</b> (<i>тж. раздельно</i>)\nсовершённый (случившийся) после (furta <i>AG</i>): ex postfacto <i>Dig</i> после уже свершившегося."],
  ["postfero", "<big>postfero</big>\n\n<b>post–fero, —, —, ferre</b>\nменее ценить, ставить ниже (alicui aliquem <i>и</i> alicui rei aliquid <i>L, VP, QC</i>)."],
  ["postfuturum", "<big>postfuturum</big>\n\n<b>post–futurum, i</b> <i>n</i>\nбудущее <i>PM, AG</i>."],
  ["postfuturus", "<big>postfuturus</big>\n\n<b>post–futurus, a, um</b> [postsum]\nбудущий, предстоящий <i>Sl</i>."],
  ["postgenitus", "<big>postgenitus</big>\n\n<b>post–genitus, a, um</b>\nродившийся после <i>Aug</i>: postgeniti <i>H</i> потомки."],
  ["posthabeo", "<big>posthabeo</big>\n\n<b>post–habeo, bui, bitum, ere</b>\nсчитать менее важным, менее ценить, ставить ниже (alicui aliquid <i>Ter, V</i>); omnibus rebus posthabitis <i>C</i> пренебрегая всем (остальным)."],
  ["posthac", "<big>posthac</big>\n\n<b>post–hac</b> <i>adv.</i>\n<b>1)</b> после того, затем <i>Pl, Ter, Su</i>;\n<b>2)</b> отныне, впредь <i>C, H</i>."],
  ["posthaec", "<big>posthaec</big>\n\n<b>post–haec</b> <i>adv.</i> (<i>тж. раздельно</i>)\nзатем, потом <i>C, Col, T</i>."],
  ["posthiemat", "<big>posthiemat</big>\n\n<b>post–hiemat, —, —, are</b> <i>impers.</i>\nзима запоздала <i>PM</i> (<i>v. l.</i>)."],
  ["posthinc", "<big>posthinc</big>\n\n<b>post–hinc</b> <i>adv.</i> (<i>тж. раздельно</i>)\nпосле того, с (э)того времени <i>V</i>."],
  ["posthumus", "<big>posthumus</big>\n\n<b>posthumus, a, um</b> <i>v. l.</i>\n= postumus."],
  ["postibi", "<big>postibi</big>\n\n<b>post–ibi</b> <i>adv.</i>\nпосле этого, затем <i>Pl</i>."],
  ["postica", "<big>postica</big>\n\n<b>postica, ae</b> <i>f</i> [posticus]\n(<i>sc.</i> porta) задняя дверь <i>Ap, Amm, Dig</i>."],
  ["posticula", "<big>posticula</big>\n\n<b>posticula, ae</b> <i>f</i> [<i>demin. к</i> postica]\nзадняя дверца <i>Ap</i>."],
  ["posticulum", "<big>posticulum</big>\n\n<b>posticulum, i</b> <i>n</i> [<i>demin. к</i> posticum]\nзадняя часть дома <i>Pl</i>."],
  ["posticum", "<big>posticum</big>\n\n<b>posticum, i</b> <i>n</i> (<i>sc.</i> ostium)\n<b>1)</b> задняя дверь <i>Pl, H, Su</i>;\n<b>2)</b> задняя сторона дома, задворки <i>Vtr</i>;\n<b>3)</b> <i>Vr, Eccl</i> = podex."],
  ["posticus", "<big>posticus</big>\n\n<b>posticus, a, um</b> [post]\nнаходящийся позади (partes aedium <i>L</i>); задний (pedes <i>Sol</i>)."],
  ["postid", "<big>postid</big>\n\n<b>postid</b> <i>adv.</i> <i>Pl</i>\n= postidea."],
  ["postidea", "<big>postidea</big>\n\n<b>post–id–ea</b> <i>adv.</i> <i>арх.</i> <i>Pl</i>\n= postea."],
  ["postilena", "<big>postilena</big>\n\n<b>postilena, ae</b> <i>f</i>\nподхвостник (<i>часть сбруи</i>) <i>Pl</i>."],
  ["postilio", "<big>postilio</big>\n\n<b>postilio, onis</b> <i>f</i> [postulo] <i>культ.</i>\nтребование (божеством) жертвы, <i>т. е.</i> искупление <i>Vr, C</i>."],
  ["postilla", "<big>postilla</big>\n\n<b>postilla</b> <i>и</i> <b>postillae</b> <i>adv.</i> <i>Ter, Pl</i>\n= postea."],
  ["postis", "<big>postis</big>\n\n<b>postis, is</b> (<i>abl.</i> <i>иногда</i> i) <i>m</i>\n<b>1)</b> столб, устой (p. lapideus <i>VP</i>); дверной косяк: tenere postem <i>C</i> держать косяк (<i>при освящении храма</i>);\n<b>2)</b> <i>преим. <i>pl.</i> поэт.</i> дверь (postes a cardine vellere <i>V</i>)."],
  ["postliminium", "<big>postliminium</big>\n\n<b>postliminium, i</b> <i>n</i> [post + limen]\nправо возвращения на родину и восстановление в правах (<i>для римлянина, освободившегося из неприятельского плена</i>) <i><i>C, Dig</i>; тж. черен.</i> возвращение: postliminio redire <i>C, Ap</i> вернуться на основании соответствующего разрешения; postliminio domum rettulisse aliquem <i>Ap</i> отнести кого-л. домой; corpus postliminio mortis animare <i>Ap</i> оживить тело мертвеца."],
  ["postmeridianus", "<big>postmeridianus</big>\n\n<b>post–meridianus, a, um</b>\nпополуденный (tempus <i>C</i>)."],
  ["postmodo", "<big>postmodo</big>\n\n<b>postmodo (postmodum)</b> <i>adv.</i>\nпотом, впоследствии, в дальнейшем <i>Ter, L, H, O, Su etc.</i>"],
  ["postmoerium", "<big>postmoerium</big>\n\n<b>postmoerium, i</b> <i>n</i> <i>L v. l.</i>\n= pomerium."],
  ["postpartor", "<big>postpartor</big>\n\n<b>post–partor, oris</b> <i>m</i>\nпоследующий владелец, наследник <i>Pl</i>."],
  ["postpono", "<big>postpono</big>\n\n<b>post–pono, posui, positum, ere</b> (= posthabeo)\nставить ниже (aliquid alicui rei <i>H</i>): omnibus rebus postpositis <i>Cs</i> поставив всё (остальное) на второй план."],
  ["postpositivus", "<big>postpositivus</big>\n\n<b>post–positivus, a, um</b> <i>грам.</i>\nпостпозитивный, ставящийся в конце <i>или</i> после слова (conjunctiones)."],
  ["postprincipia", "<big>postprincipia</big>\n\n<b>post–principia, orum</b> <i>n</i>\nто, что непосредственно следует за началом, <i>т. е.</i> дальнейший ход (событий) (p. atque exitus <i>Poeta ap. C</i>)."],
  ["postputo", "<big>postputo</big>\n\n<b>postputo, avi, —, are</b> <i>Ter</i>\n= posthabeo."],
  ["postquam", "<big>postquam</big>\n\n<b>postquam</b> <i>conj.</i>\n<b>1)</b> после того как, с тех пор как (undecimo die p. a te discesseram <i>C</i>);\n<b>2)</b> поскольку, так как: p. ab Romanis nulla est spes <i>L</i> так как на римлян рассчитывать нечего."],
  ["postremi", "<big>postremi</big>\n\n<b>postremi, orum</b> <i>m</i>\nарьергард <i>Sl</i>."],
  ["postremissimus", "<big>postremissimus</big>\n\n<b>postremissimus, a, um</b> <i>AG</i>\n= <i>усил.</i> postremus."],
  ["postremius", "<big>postremius</big>\n\n<b>postremius</b> <i>Ap compar. к</i>\npostremus."],
  ["postremo", "<big>postremo</big>\n\n<b>postremo</b>\nнаконец, в конце концов (primum..., deinde..., p. <i>C</i>); короче говоря, вообще: p. omnibus modis pecuniam trahunt <i>Sl</i> (друзья Каталины покупают предметы роскоши, строят дома), словом — всячески швыряют деньги."],
  ["postremum", "<big>postremum</big>\n\n<b>postremum</b> <i>adv.</i>\n<b>1)</b> (<i>тж.</i> ad p.) в последний раз <i>Pl, Ter, C etc.</i>;\n<b>2)</b> в конце концов, наконец <i>Just, Pall</i>."],
  ["postremus", "<big>postremus</big>\n\n<b>postremus, a, um</b> [<i>superl. к</i> posterus]\n<b>1)</b> (самый) последний (pagina <i>C</i>): in postremo libro <i>Just</i> в конце книги; postremum munus <i>Ctl</i> последний долг (<i>т. е.</i> погребение);\n<b>2)</b> худший, тягчайший (postremum malorum omnium <i>C</i>);\n<b>3)</b> наименее важный (cura <i>V</i>): hoc non in postremis <i>C</i> (и) это не последней важности вопрос;\n<b>4)</b> самый задний, тыловой, арьергардный (acies <i>Sl</i>)."],
  ["postridie", "<big>postridie</big>\n\n<b>postridie</b> [из postero die]\nна следующий день (prima luce p. <i>Cs</i>): p. ejus diei <i>Cs</i> на другой день после этого; p. quam discessi <i>C</i> на следующий день после моего отъезда; p. aliquid на другой день после чего-л. (p. ludos <i>C</i>)."],
  ["postriduanus", "<big>postriduanus</big>\n\n<b>postriduanus, a, um</b>\nзавтрашний (dies <i>Macr</i>)."],
  ["postriduo", "<big>postriduo</big>\n\n<b>postriduo</b> <i>Pl</i>\n= postridie."],
  ["postscaenium", "<big>postscaenium</big>\n\n<b>post–scaenium, i</b> <i>n</i>\nпостсцений, кулисы <i>Lcr</i> (<i>v. l.</i>)."],
  ["postscribo", "<big>postscribo</big>\n\n<b>post–scribo, scripsi, scriptum, ere</b>\nприписать после, дописать (nomen alicujus suo p. <i>T</i>)."],
  ["postsecus", "<big>postsecus</big>\n\n<b>post–secus</b> <i>adv.</i>\nназад (rejici <i>Is</i>)."],
  ["postsignani", "<big>postsignani</big>\n\n<b>post–signani, orum</b> <i>m</i> [signum]\nпостсигнаны, <i>т. е.</i> солдаты, которые во время боевых действий находились позади знамён <i>Frontin, Amm</i>."],
  ["postsum", "<big>postsum</big>\n\n<b>post–sum, fui, esse</b> (<i>тж. раздельно</i>)\n<b>1)</b> отходить на второй план <i>Sl</i>;\n<b>2)</b> относиться к будущему: in postfuturum loqui <i>AG</i> говорить о том, что последует. — <i>См. тж.</i> postfuturus."],
  ["postulaticius", "<big>postulaticius</big>\n\n<b>postulaticius, a, um</b> [postulo]\nвызываемый, усиленно приглашаемый (gladiatores <i>Sen</i>)."],
  ["postulatio", "<big>postulatio</big>\n\n<b>postulatio, onis</b> <i>f</i>\n<b>1)</b> требование, просьба (concedere postulationi alicujus <i>C</i>);\n<b>2)</b> жалоба <i>Pl, Ter</i>; обращение к претору о разрешении вчинить иск <i>C</i>; <i>юр.</i> иск <i>PJ, Su</i>."],
  ["postulator", "<big>postulator</big>\n\n<b>postulator, oris</b> <i>m</i> [postulo]\nистец, жалобщик <i>Su</i>."],
  ["postulatorius", "<big>postulatorius</big>\n\n<b>postulatorius, a, um</b>\nнапоминающий, <i>т. е.</i> требующий исполнения обета (fulmen <i>Sen</i>)."],
  ["postulatrix", "<big>postulatrix</big>\n\n<b>postulatrix, icis</b> <i>f</i>\nпросительница: p. solemnitas <i>Tert</i> = supplicatio."],
  ["postulatum", "<big>postulatum</big>\n\n<b>postulatum, i</b> <i>n</i> [postulo]\n<b>1)</b> требование <i>C, Cs</i>;\n<b>2)</b> иск <i>CJ</i>."],
  ["postulatus", "<big>postulatus</big>\n\n<b>postulatus, (us) </b> <i>m</i> [postulo] <i>юр.</i>\nжалоба, иск <i>L</i>."],
  ["postulo", "<big>postulo</big>\n\n<b>postulo, avi, atum, are</b>\n<b>1)</b> требовать, испрашивать, просить (aliquid ab aliquo <i>C</i>); выдвигать требование (de aliqua re <i>C</i>): variis postulantibus <i>Ap</i> по требованию многих; aliquid ab aliquo postulatur <i>C</i> кто-л. требует от кого-л. чего-л.; a senatu de aliqua re p. <i>C</i> делать запрос в сенате о чём-л.; si res postulat <i>C</i> <i>и</i> ubi res postularet <i>Sl</i> в случае необходимости; deliberandi (<i>или</i> ad deliberandum) sibi unum diem p. <i>C</i> просить себе один день на размышление;\n<b>2)</b> стремиться, желать (dicendo vincere <i>C</i>): me ducere istis dictis postulas? <i>Ter</i> ты хочешь ввести меня в заблуждение этими словами?;\n<b>3)</b> привлекать к судебной ответственности, обвинять (aliquem de ambitu <i>C</i>, ob contumelias <i>T</i>, proditionis <i>Cs</i>)."],
  ["postumatus", "<big>postumatus</big>\n\n<b>postumatus, us</b> <i>m</i> [postumus]\nпоследнее место, низшая ступень <i>Tert</i>."],
  ["Postumianus", "<big>Postumianus</big>\n\n<b>Postumianus, a, um</b> [Postumius]\nпостумиев <i>L</i>."],
  ["Postumius", "<big>Postumius</big>\n\n<b>Postumius, a, um</b>\nПостумий, <i>римск. nomen; наиболее известны</i>:\n<b>1)</b> Publius P. Tubertus, <i>консул 505 и 503 гг. до н. э.; успешно воевал с сабинянами</i> <i>L, PM</i>;\n<b>2)</b> A. P. Tubertus, <i>бывший в 431 г. до н. э. диктатором; победоносно сражался против эквов и вольсков</i> <i>L, VM</i>;\n<b>3)</b> Sp. P. Albinus, <i>консул 321 г. до н. э., потерпевший сильное поражение при Кавдии</i> <i>C, L</i>."],
  ["postumo", "<big>postumo</big>\n\n<b>postumo, —, —, are</b> [postumus]\nстоять ниже, отставать, уступать (alicui <i>Tert</i>)."],
  ["postumus", "<big>postumus</big>\n\n<b>I postumus, a, um</b> [<i>superl. к</i> posterus]\n<b>1)</b> последний (spes <i>Ap</i>);\n<b>2)</b> родившийся последним, самый младший (proles <i>V</i>);\n<b>3)</b> посмертный (is, qui post patris mortem natus est, dicitur p. <i>Vr</i>).\n\n<b>II Postumus, i</b> <i>m</i>\n<b>1)</b> <i>O</i> = Postumius 2;\n<b>2)</b> <i>друг Горация</i> <i>H</i>."],
  ["postus", "<big>postus</big>\n\n<b>postus</b> <i>Lcr</i>\n= positus."],
  ["postvenio", "<big>postvenio</big>\n\n<b>post–venio, —, —, ire</b>\nприходить после <i>PM</i>."],
  ["Postverta", "<big>Postverta</big>\n\n<b>Post–verta (Post–vorta), ae</b> <i>f</i>\n<i>римск. богиня, которую призывали при неправильных родах; по др. — богиня предсказаний</i> <i>Vr, O, Macr</i>."],
  ["posui", "<big>posui</big>\n\n<b>posui</b> <i>pf. к</i>\npono."],
  ["potabilis", "<big>potabilis</big>\n\n<b>potabilis, e</b> [poto]\nгодный для питья (medicamina <i>CA</i>); содержащий питьевую воду (fons <i>Amm</i>)."],
  ["potaculum", "<big>potaculum</big>\n\n<b>potaculum, i</b> <i>n</i> <i>Tert</i>\n= potatio."],
  ["potamentum", "<big>potamentum</big>\n\n<b>potamentum, i</b> <i>n</i> [poto]\nнапиток <i>Is</i>."],
  ["potatio", "<big>potatio</big>\n\n<b>potatio, onis</b> <i>f</i>\nпопойка, кутёж <i>Pl, C etc.</i>"],
  ["potator", "<big>potator</big>\n\n<b>potator, oris</b> <i>m</i> (<i>тж.</i> p. vini <i>Hier</i>)\nбражник, кутила, пьяница <i>Pl, Vop, Vlg, Eccl</i>: p. aquae <i>Sil</i> житель берегов (этой) реки."],
  ["potatus", "<big>potatus</big>\n\n<b>potatus, us</b> <i>m</i>\nпитьё, выпивка (vinum potatui sufficiens <i>Ap</i>)."],
  ["pote", "<big>pote</big>\n\n<b>pote</b>\n= potest — <i>см.</i> potis."],
  ["potens", "<big>potens</big>\n\n<b>potens, entis</b>\n<b>1.</b> <i>part. praes. к</i> possum;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> сильный, могучий, могущественный (urbs, rex, civitas <i>C</i>): diva p. Cypri <i>H</i> = Venus; multa ignoscendo fit p. potentior <i>PS</i> многое прощая, сильный становится ещё сильнее || мощный (arma <i>VP, L</i>); владеющий (rerum omnium p. Juppiter <i>T</i>): p. sui <i>H</i> владеющий собой, помнящий себя <i>или</i> <i>L</i> независимый; non p. sui <i>QC</i> вне себя; p. irae <i>QC</i> умеющий подавить свой гнев; maxime in res bellicas (<i>v. l.</i> in re bellica) p. <i>L</i> имеющий решающее значение в военном деле;\n<b>2)</b> способный (alicujus rei <i>или</i> ad aliquid): p. regni (imperii) <i>L</i> способный управлять (умеющий повелевать);\n<b>3)</b> достигший, овладевший, имеющий (p. ingenti praeda <i>L</i>); преуспевающий (in aliqua re <i>Ctl</i>): p. voti <i>O</i> достигший желаемого; p. jussi <i>O</i> исполнивший приказание; parvo potens <i>V</i> довольствующийся малым; promissi p. factus <i>V</i> исполнивший обещание, сдержавший слово; animal p. leti <i>Lcn</i> хищный зверь; incepti p. <i>VF</i> положивший успешное начало;\n<b>4)</b> благотворно действующий, действительный (herbae <i>V, O</i>; adversus <i>и</i> contra aliquid <i>PM</i>)."],
  ["potentator", "<big>potentator</big>\n\n<b>potentator, oris</b> <i>m</i>\nвластелин (p. et rex regnantium <i>Tert</i>)."],
  ["potentatus", "<big>potentatus</big>\n\n<b>potentatus, us</b> <i>m</i>\nвысшая власть, господство <i>C, Cs, L</i>."],
  ["potenter", "<big>potenter</big>\n\n<b>potenter</b> [potens]\n<b>1)</b> сильно, мощно (se defendere <i>VM</i>);\n<b>2)</b> по мере сил (lecta p. res <i>H</i>)."],
  ["potentia", "<big>potentia</big>\n\n<b>I potentia, ae</b> <i>f</i>\n<b>1)</b> сила, мощь (solis <i>V</i>; morbi <i>O</i>; supra humanam potentiam <i>Just</i>);\n<b>2)</b> политическая власть, влияние (in magna potentia esse <i>C</i>): p. rerum <i>O</i> власть над миром; p. singularis <i>Nep</i> единодержавие, монархия;\n<b>3)</b> <i>поздн. филос.</i> (<i>перевод греч.</i> dynamis) возможность, потенция: in potentia <i>Eccl</i> потенциально.\n\n<b>II Potentia, ae</b> <i>f</i>\nПотенция:\n<b>1)</b> <i>город в сев.-вост. Пицене, к юго-вост. от Анконы</i> <i>C, L</i>;\n<b>2)</b> <i>город в сев. Лукании</i> <i>PM</i>."],
  ["poterion", "<big>poterion</big>\n\n<b>poterion, i</b> <i>n</i> (<i>греч.</i>)\nчаша, бокал <i>Pl</i>."],
  ["potesse", "<big>potesse</big>\n\n<b>potesse</b> <i>арх. Pl, Ter, Lcr</i>\n= posse (<i>inf. к</i> possum)."],
  ["potessim", "<big>potessim</big>\n\n<b>potessim</b> <i>Pl</i>\n= possim."],
  ["potessunt", "<big>potessunt</big>\n\n<b>potessunt</b> <i>Pl</i>\n= possunt."],
  ["potestas", "<big>potestas</big>\n\n<b>potestas, atis</b> <i>f</i> [possum]\n<b>1)</b> сила, мощь (fatorum <i>PJ</i>); ценность (pecuniarum <i>Dig</i>); действие, действенность (herbarum <i>V</i>);\n<b>2)</b> распорядительные права, господство, власть: p. vitae necisque <i>C</i> право казнить и миловать; alicui p. est alicujus <i>C</i> кто-л. находится в чьём-л. распоряжении; patris in filium p. <i>Dig</i> власть отца над сыном; esse in potestate alicujus <i>C</i> быть подвластным кому-л.; in (sub) potestatem redigere <i>Nep, L</i> подчинить (чьей-л.) власти, покорить; mihi est p. (est in potestate mea) <i>C etc.</i> в моей власти (я могу); in potestate mentis esse <i>C</i> владеть собой, быть в здравом уме; exire de (ex) potestate <i>C</i> выйти из себя, не помнить себя;\n<b>3)</b> государственная должность, пост, власть (consularis, praetoria <i>C</i>): potestatem gerere <i>C</i> <i>или</i> potestati praeesse <i>Nep</i> исполнять (занимать) должность; cum potestate in provinciam proficisci <i>C</i> отправиться в провинцию с официальными полномочиями; imperia et potestates <i>C</i> военные и гражданские власти;\n<b>4)</b> возможность, случай, разрешение (data est p. alicujus rei <i>C, L</i>): facere alicui potestatem alicujus rei <i>C etc.</i> дать кому-л. возможность чего-л. (разрешить что-л.); populi (<i>v. l.</i> populo) potestatem facere <i>L</i> предоставить решение народу; lacere alicui potestatem sui (conveniendi) <i>C</i> позволить кому-л. подойти к себе, <i>т. е.</i> принять кого-л.; facere potestatem sui <i>воен. Cs, Nep</i> принять сражение; p. est <i>L, V etc.</i> можно: p. certorum hominum mihi est <i>C</i> я могу найти надёжных людей;\n<b>5)</b> владыка, властелин, господин (hominum divumque, <i>v. l.</i> rerumque <i>V</i>);\n<b>6)</b> значение, смысл (verborum ancipites aut multiplices potestates <i>rhH</i>)."],
  ["potestativus", "<big>potestativus</big>\n\n<b>potestativus, a, um</b> [potestas]\nоблечённый властью (principatus <i>Tert</i>)."],
  ["potestur", "<big>potestur</big>\n\n<b>potestur</b> <i>арх. Lcr</i>\n= potest (<i>3 л. sg. praes. к</i> possum)."],
  ["Pothinus", "<big>Pothinus</big>\n\n<b>Pothinus, i</b> <i>m</i>\nПотин, <i>евнух, убивший Помпея</i> <i>Cs, Lcn</i>."],
  ["poti", "<big>poti</big>\n\n<b>poti</b> <i>Enn inf. к</i>\npotior I."],
  ["potilis", "<big>potilis</big>\n\n<b>potilis, e</b>\nслужащий для питья (nidus <i>Vr</i>): haustu potili <i>CA</i> большими глотками."],
  ["potin'?", "<big>potin'?</big>\n\n<b>potin'?</b> <i>Ter</i>\n= potisne?"],
  ["potio", "<big>potio</big>\n\n<b>I potio, ivi, itum, ire</b> [potis]\nотдавать во власть: p. aliquem servitutis <i>Pl</i> сделать кого-л. рабом; potiri hostium <i>Pl</i> попасть в руки врагов.\n\n<b>II potio, onis</b> <i>f</i>\n<b>1)</b> питьё, выпивание <i>C, CC, Sen</i>;\n<b>2)</b> напиток (cibus et p. <i>C</i>): potione honoratus est <i>Pt</i> в награду ему была преподнесена чаша вина || лекарство, микстура (p. ad aliquid efficax <i>CC</i>); любовный напиток <i>H</i>; яд <i>C, Q</i>; бульон (p. ferventissima <i>Pt</i>)."],
  ["potiono", "<big>potiono</big>\n\n<b>potiono, —, atum, are</b> [potio II]\nпоить (aliquem aliqua potione <i>Veg</i>; amatorio medicamento potionatus <i>Su</i>)."],
  ["potior", "<big>potior</big>\n\n<b>I potior, titus sum, iri</b> (<i>иногда по 3-му спряж.</i>: potitur <i>V, inf.</i> poti <i>Enn etc.</i>) <i>depon.</i>\n<b>1)</b> завладевать, овладевать, захватывать (aliqua re <i>C, L</i>, alicujus rei <i>C, Sl</i> <i>или</i> aliquid <i>Pl, Ter, C etc.</i>): rapto p. <i>V</i> захватить добычу; p. victoria <i>Cs</i> одержать победу; p. rerum <i>C</i> овладеть верховной властью, <i>тж.</i> <i>C</i> стать господином положения; dum Nero rerum potiebatur <i>T</i> пока Нерон находился у власти; libido ad potiundum (<i>v. l.</i> potiendum) <i>C</i> страсть к захвату, хищничество, стяжательство; p. monte <i>O</i> достигнуть (дойти до) горы; p. campo <i>V</i> пройти поле;\n<b>2)</b> владеть, иметь власть, господствовать (urbe <i>C</i>): p. mari <i>L</i> господствовать на море; p. summam imperii (rerum) <i>Nep</i> иметь высшую власть; p. voluptatibus <i>C</i> предаваться наслаждениям; p. morte <i>Lcr, Aus</i> умереть; p. caede <i>St</i> совершить убийство.\n\n<b>II potior, ius</b> <i>adj.</i> [<i>compar. к</i> potis]\nпредпочтительный, лучший (mors servitute p. <i>C</i>; cives potiores, quam peregrini <i>C</i>; potioribus deteriora submittere <i>Sen</i>): potiores credere saevos labores cenis <i>J</i> ставить тяжкие труды выше пиров."],
  ["potis", "<big>potis</big>\n\n<b>potis, pote</b> <i>adj.</i> (<i>compar.</i> potior, <i>superl.</i> potissimus)\n<b>1)</b> могущественный (divi <i>Vr</i>);\n<b>2)</b> могущий: p. est <i>Enn, Lcr, Ctl, V etc.</i> он может (в состоянии); qui p. est? <i>Ctl</i> как это возможно?; si p. est <i>Ter etc.</i> если можно; postin' (= potisne es) ut desinas? <i>Ter</i> не можешь ли перестать?; pote <i>Vr, Prp</i> может быть; nihil pote supra <i>Ter</i> ничего сверх этого быть не может; quantum pote <i>C</i> насколько возможно; non pote minoris <i>C</i> дешевле невозможно; non pote valdius, quam expavit <i>Pt</i> он чрезвычайно испугался."],
  ["potisset", "<big>potisset</big>\n\n<b>potisset</b> <i>Pl, LM</i>\n= posset."],
  ["potissimum", "<big>potissimum</big>\n\n<b>potissimum</b> <i>adv.</i>\n<b>1)</b> главным образом, преимущественно (otium ad scribendum p. conferre <i>C</i>); лучше, предпочтительнее всего (eo p. tempore <i>C</i>);\n<b>2)</b> с величайшей готовностью (mors pro patria p. reddita <i>C</i>)."],
  ["potissimus", "<big>potissimus</big>\n\n<b>potissimus, a, um</b> [<i>superl. к</i> potis]\nважнейший, лучший (opusculum <i>PJ</i>; hoc potissimum est <i>L</i>)."],
  ["potissunt", "<big>potissunt</big>\n\n<b>potissunt</b> <i>LM</i>\n= possunt."],
  ["Potitius", "<big>Potitius</big>\n\n<b>Potitius, a, um</b>\nПотитий, <i>nomen древнеримск. жреческого рода, рано пресекшегося</i> (<i>см.</i> Pinarius) <i>L, V</i>."],
  ["potito", "<big>potito</big>\n\n<b>potito, —, —, are</b> [<i>intens. к</i> poto]\nмного пить (patera aurea <i>Pl</i>; poscam <i>Pl</i>)."],
  ["potitor", "<big>potitor</big>\n\n<b>potitor, oris</b> <i>m</i> [potior I]\nзахватчик (Capuae <i>VM</i>)."],
  ["potitus", "<big>potitus</big>\n\n<b>potitus, a, um</b> <i>part. pf. к</i>\npotio I <i>и</i> potior I."],
  ["potiuncula", "<big>potiuncula</big>\n\n<b>potiuncula, ae</b> <i>f</i> [<i>demin. к</i> potio II]\nглоток (p. modica <i>Su</i>; crebrae potiunculae <i>Pt</i>)."],
  ["potius", "<big>potius</big>\n\n<b>potius</b> <i>adv.</i> [potis]\n<b>1)</b> скорее, лучше сказать, вернее (non laudatio, sed p. irrisio <i>C</i>);\n<b>2)</b> предпочтительнее, лучше (emori p., quam servire <i>C</i>): pecuniam ob delicta p. dedit, quam ne delinquerent <i>T</i> (Нерон) предпочитал платить за преступления, чем предупреждать их."],
  ["potivi", "<big>potivi</big>\n\n<b>potivi</b> <i>pf. к</i>\npotio I."],
  ["Potniae", "<big>Potniae</big>\n\n<b>Potniae, arum</b> <i>f</i>\nПотнии, <i>местечко в Беотии, на реке Asopus</i> <i>PM</i>."],
  ["Potnias", "<big>Potnias</big>\n\n<b>Potnias, adis</b> <i>f</i> [Potniae]\nпотнийская: Potniades quadrigae <i>V</i> (equae <i>O</i>)= кобылицы Главка."],
  ["poto", "<big>poto</big>\n\n<b>poto, avi, atum</b> (<i>или</i> <b>potum</b>), <b>are</b>\n<b>1)</b> много <i>или</i> часто пить, напиваться (p. aquas <i>O</i>; p. vinum <i>Pl, PM</i>); пьянствовать, бражничать (totos dies <i>C</i>): bibere, immo p. <i>Sen</i> пить и даже напиваться; p. crapulam <i>Pl</i> напиваться допьяна, (о)хмелеть;\n<b>2)</b> <i>поэт.</i> жить (на <i>берегу реки</i>), обитать, населять (fera, quae potat Araxen <i>SenT</i>);\n<b>3)</b> впитывать, всасывать (sudorem <i>Lcr</i>);\n<b>4)</b> поить (felle et aceto potatus <i>Tert</i>)."],
  ["potor", "<big>potor</big>\n\n<b>potor, oris</b> <i>m</i> [poto]\n<b>1)</b> пьющий (p. aquae <i>H</i>);\n<b>2)</b> <i>поэт.</i> житель, обитатель: p. Rhodani <i>H</i> житель берегов Родана;\n<b>3)</b> кутила, гуляка <i>Prp, H, M, PM</i>."],
  ["potorium", "<big>potorium</big>\n\n<b>potorium, i</b> <i>n</i>\nчаша для питья, бокал <i>PM</i>."],
  ["potorius", "<big>potorius</big>\n\n<b>potorius, a, um</b> [potor]\nслужащий для питья (vas <i>PM</i>; argentum <i>Dig</i>)."],
  ["potrix", "<big>potrix</big>\n\n<b>potrix, icis</b> <i>f</i> [poto]\nпьющая, пьяница <i>Ph</i>."],
  ["potsum", "<big>potsum</big>\n\n<b>potsum</b> <i>Pl</i>\n(<i>v. l.</i>) = possum."],
  ["potui", "<big>potui</big>\n\n<b>I potui</b> <i>pf. к</i>\nposse.\n\n<b>II potui</b> <i>dat. sg. к</i>\npotus II."],
  ["potulenta", "<big>potulenta</big>\n\n<b>potulenta, orum</b> <i>n</i>\nнапитки (esculenta et p. <i>C, Ap</i>)."],
  ["potulentus", "<big>potulentus</big>\n\n<b>potulentus, a, um</b> [potus]\n<b>1)</b> годный для питья <i>C, AG</i>;\n<b>2)</b> пьяный <i>Su, Ap</i>."],
  ["potus", "<big>potus</big>\n\n<b>I potus</b> <i>part. pf. к</i>\npoto: <i>pass.</i> выпитый (veneno poto mori <i>Sen</i>); <i>act.</i> выпивший (p. sum <i>Vr ap. AG</i>); пьяный (bene p. <i>C</i>).\n\n<b>II potus, us</b> <i>m</i> [poto]\n<b>1)</b> питьё (immoderatus <i>C</i>);\n<b>2)</b> напиток (cibi potusque <i>T</i>);\n<b>3)</b> моча (p. hominum <i>PM</i>)."],
  ["prae", "<big>prae</big>\n\n<b>I prae</b> <i>adv.</i>\n<b>1)</b> впереди, вперёд (ire p. <i>Pl, Ter</i>): abi p., jam ego sequar <i>Pl</i> иди вперёд, а я последую (за тобой);\n<b>2)</b> в сравнении, против: p. quam <i>и</i> p. ut (<i>тж. слитно</i>: praequam <i>и</i> praeut) <i>Pl, Ter</i> в сравнении с чём-л.; p. ut dudum fuit <i>Pl</i> в сравнении с тем, каким он был раньше.\n\n<b>II prae</b> <i>praep. cum abl.</i> (<i>редко</i> <i>с acc.</i>)\n<b>1)</b> впереди, перед (p. se mittere <i>Sl</i>; pugionem p. se ferre <i>C</i>): p. se ferre (gerere) <i>C etc.</i> выставлять напоказ, открыто выражать, обнаруживать (gaudia vultu p. se declarare <i>Ctl</i>): fateor et prae me fero <i>Pt</i> сознаюсь и нимало не скрываю; aliquid paulum p. manu dare <i>Ter</i> дать кое-что на руки (наличными); si liber p. manibus est <i>AG</i> если книга (эта у тебя) под рукой;\n<b>2)</b> по сравнению с: Gallis, p. magnitudine corporum suorum, brevitas nostra contemptui est <i>Cs</i> наш рост, небольшой по сравнению с большим ростом галлов, является для них предметом презрения;\n<b>3)</b> <i>преим. в отриц. предлож.</i> из-за, от (p. lacrimis scribere non posse <i>C</i>).\n\n<b>III prae-</b>\n<i>приставка со значением</i>:\n<b>1)</b> <i>нахождения впереди</i> (prae–cedo);\n<b>2)</b> <i>упреждения</i> (prae–cipio);\n<b>3)</b> <i>преждевременности</i> (prae–canus);\n<b>4)</b> <i>высокой или высшей степени</i> (prae–calidus)."],
  ["praeacuo", "<big>praeacuo</big>\n\n<b>prae–acuo, (ui), utum, ere</b>\nзаострять спереди (surculum <i>Cato</i>)."],
  ["praeacutus", "<big>praeacutus</big>\n\n<b>prae–acutus, a, um</b> [praeacuo]\nзаострённый, остроконечный, острый (stipites <i>Cs</i>; cuspis <i>O</i>)."],
  ["praealtus", "<big>praealtus</big>\n\n<b>prae–altus, a, um</b>\n<b>1)</b> очень высокий (ripae <i>L</i>);\n<b>2)</b> очень глубокий (flumen <i>L</i>; mare <i>QC, Pl</i>)."],
  ["praeargumentor", "<big>praeargumentor</big>\n\n<b>prae–argumentor, —, ari</b> <i>depon.</i>\nпредварительно устанавливать принципы <i>Boet</i>."],
  ["praeaudio", "<big>praeaudio</big>\n\n<b>prae–audio, ivi, itum, ire</b>\nзаранее <i>или</i> предварительно выслушивать (aliquem <i>Dig</i>)."],
  ["praebenda", "<big>praebenda</big>\n\n<b>praebenda, ae</b> <i>f</i> [praebeo] <i>Eccl</i>\n= praebita."],
  ["praebeo", "<big>praebeo</big>\n\n<b>praebeo, bui, bitum, ere</b> [prae + habeo]\n<b>1)</b> протягивать, подставлять (aurem dictis <i>O</i>; se telis hostium <i>L</i>; manum verberibus <i>O</i>; crura sagittis <i>Pers</i>);\n<b>2)</b> давать, подавать (alicui panem <i>Nep</i>; exempla nequitiae <i>C</i>): p. causam alicujus rei <i>O</i> послужить поводом к чему-л.; p. manum alicujus rei <i>O</i> привести в исполнение что-л. || поставлять (equites regi <i>L</i>); доставлять (spectaculum <i>L</i>; perfugium ac solatium <i>C</i>); ниспосылать (noctem sideribus illustrem <i>T</i>);\n<b>3)</b> вызывать, возбуждать (gaudium <i>L</i>); внушать (suspicionem <i>Nep</i>); допускать, позволять (p. rapi <i>O</i>);\n<b>4)</b> оказывать, делать (honorem alicui <i>PM</i>; operam rei publicae <i>L</i>): alicui vires p. <i>T</i> укреплять (<i>или</i> поощрять) кого-л.; p. speciem alicujus <i>Cs</i> казаться кем-либо; se p. attentum auditorem <i>C</i> оказаться (показать себя) внимательным слушателем; p. se voluntati alicujus <i>Ap</i> пойти навстречу чьим-л. желаниям."],
  ["praebeo", "<big>praebeo</big>\n\n<b>prae–beo, avi, atum, are</b>\nставить впереди, на первом месте (aliquid in facie p. <i>Aug</i>)."],
  ["praebia", "<big>praebia</big>\n\n<b>praebia, orum</b> <i>n</i> [praebeo]\nамулеты <i>Vr</i>."],
  ["praebibo", "<big>praebibo</big>\n\n<b>prae–bibo, bibi, —, ere</b>\n<b>1)</b> пить первым за здравие, провозгласить первый тост (p. alicui <i>C, Ap</i>);\n<b>2)</b> пить ранее (aquam <i>CA</i>; antidotum <i>Hier</i>)."],
  ["praebita", "<big>praebita</big>\n\n<b>praebita, orum</b> <i>n</i> [praebeo]\nжалованье, содержание (p. annua <i>Su</i>)."],
  ["praebitio", "<big>praebitio</big>\n\n<b>praebitio, onis</b> <i>f</i> [praebeo]\n<b>1)</b> поставка, доставка (copiarum <i>Just</i>; frumenti <i>AV</i>);\n<b>2)</b> угощение (mea erat p. <i>Vr</i>)."],
  ["praebitor", "<big>praebitor</big>\n\n<b>praebitor, oris</b> <i>m</i> [praebeo]\nдоставщик, поставщик <i>C</i>."],
  ["praeblandus", "<big>praeblandus</big>\n\n<b>prae–blandus, a, um</b>\nвесьма ласковый <i>Jvc</i>."],
  ["praebui", "<big>praebui</big>\n\n<b>praebui</b> <i>pf. к</i>\npraebeo."],
  ["praecadens", "<big>praecadens</big>\n\n<b>prae–cadens, entis</b> [cado]\nвыступающий (выдающийся) вперёд (podex <i>CA</i>)."],
  ["praecalefactus", "<big>praecalefactus</big>\n\n<b>prae–calefactus (-calfactus), a, um</b> <i>Scr, CA</i>\n= praecalidus."],
  ["praecalidus", "<big>praecalidus</big>\n\n<b>prae–calidus, a, um</b>\nвесьма тёплый, горячий (potio <i>T</i>)."],
  ["praecalvus", "<big>praecalvus</big>\n\n<b>prae–calvus, a, um</b>\nсовершенно <i>или</i> спереди лысый (caput <i>Su</i>)."],
  ["praecano", "<big>praecano</big>\n\n<b>prae–cano, —, —, ere</b>\n<b>1)</b> предупредить чары, заранее обезвредить колдовство <i>PM</i>;\n<b>2)</b> предсказывать, пророчить <i>Tert</i>."],
  ["praecantatio", "<big>praecantatio</big>\n\n<b>praecantatio, onis</b> <i>f</i> [praecanto]\nоколдовывание, чары, заговор <i>Q, Sol, Aug</i>."],
  ["praecantator", "<big>praecantator</big>\n\n<b>praecantator, oris</b> <i>m</i>\nчародей <i>Aug</i>."],
  ["praecantatrix", "<big>praecantatrix</big>\n\n<b>prae–cantatrix, icis</b> <i>f</i>\nчародейка <i>Pl, Aug</i>."],
  ["praecanto", "<big>praecanto</big>\n\n<b>prae–canto, (avi), atum, are</b>\n<b>1)</b> предсказывать, пророчить <i>LM</i> (<i>v. l.</i>);\n<b>2)</b> заколдовывать <i>Pt</i>."],
  ["praecantrix", "<big>praecantrix</big>\n\n<b>praecantrix, icis</b> <i>f</i> <i>Vr</i>\n= praecantatrix."],
  ["praecanus", "<big>praecanus</big>\n\n<b>prae–canus, a, um</b>\nпреждевременно поседевший <i>H</i>."],
  ["praecarpo", "<big>praecarpo</big>\n\n<b>praecarpo</b> <i>Macr</i>\n= praecerpo."],
  ["praecautio", "<big>praecautio</big>\n\n<b>praecautio, onis</b> <i>f</i> [praecaveo]\nпредосторожность <i>CA</i>."],
  ["praecautus", "<big>praecautus</big>\n\n<b>prae–cautus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> praecaveo;\n<b>2.</b> <i>adj.</i> предусмотренный, обставленный всеми мерами предосторожности (res mihi tota praecauta est <i>C</i>)."],
  ["praecaveo", "<big>praecaveo</big>\n\n<b>prae–caveo, cavi, cautum, ere</b>\n<b>1)</b> беречься, остерегаться (ne quid accidat <i>Cs</i>; p. ab aliqua re <i>L</i>); принимать меры предосторожности (providens et praecavens <i>C</i>); оберегать (alicui ab aliqua re <i>Ter</i>);\n<b>2)</b> предотвращать, предупреждать, отводить (aliquid <i>C, AG etc.</i>): peccata, quae difficillime praecaventur <i>C</i> проступки, которые особенно трудно предупредить; ad praecavenda venena <i>Su</i> чтобы предохранить себя от ядов."],
  ["praececini", "<big>praececini</big>\n\n<b>praececini</b> <i>pf. к</i>\npraecino."],
  ["praecedentia", "<big>praecedentia</big>\n\n<b>praecedentia, ae</b> <i>f</i> [praecedo] <i>астр.</i>\nпредварение, прецессия <i>Is</i>."],
  ["praecedo", "<big>praecedo</big>\n\n<b>prae–cedo, cessi, cessum, ere</b>\n<b>1)</b> идти впереди, предшествовать (aliquem <i>PJ etc.</i>): praecedentibus facibus <i>Sen</i> с факелами впереди; p. nuntios <i>VP</i> предупредить (опередить) гонцов; aliquem aetate p. <i>Q</i> быть чьим-л. предшественником (быть старше кого-л.);\n<b>2)</b> иметь преимущество, быть выше (aliquem dignitate <i>Q</i>)."],
  ["praeceler", "<big>praeceler</big>\n\n<b>prae–celer, celeris, celere</b>\nчрезвычайно быстрый (cursu <i>St</i>; fuga <i>PM</i>)."],
  ["praecelero", "<big>praecelero</big>\n\n<b>prae–celero, —, —, are</b>\n<b>1)</b> опережать (aliquem <i>St</i>);\n<b>2)</b> сильно спешить (calle latenti <i>St</i>)."],
  ["praecellens", "<big>praecellens</big>\n\n<b>prae–cellens, entis</b>\n<b>1.</b> <i>part. praes. к</i> praecello;\n<b>2.</b> <i>adj.</i> замечательный, превосходный, отличный (omnibus rebus praecellentissimus <i>C</i>; ingenium <i>AG</i>)."],
  ["praecellentia", "<big>praecellentia</big>\n\n<b>praecellentia, ae</b> <i>f</i>\nпревосходство (divinitatis <i>Tert</i>)."],
  ["praecelleo", "<big>praecelleo</big>\n\n<b>praecelleo, —, —, ere</b> <i>Pl</i>\n= praecello."],
  ["praecello", "<big>praecello</big>\n\n<b>prae–cello, —, —, ere</b>\n<b>1)</b> превосходить (aliquem aliqua re <i>T, Dig</i>); выделяться, отличаться (inter reliquos <i>Dig</i>; mobilitate <i>Lcr</i>; per eloquentiam <i>T</i>);\n<b>2)</b> господствовать, предводительствовать (genti <i>T</i>; mortalibus caelicolae, animalibus isti praecellunt <i>Sil</i>)."],
  ["praecelsus", "<big>praecelsus</big>\n\n<b>prae–celsus, a, um</b>\nочень высокий, высоко вздымающийся (rupes <i>V</i>; moles <i>St</i>; <i>перен.</i> fortuna <i>St</i>)."],
  ["praecentio", "<big>praecentio</big>\n\n<b>praecentio, onis</b> <i>f</i> [praecino]\nмузыкальное вступление, песнь (<i>перед жертвоприношением или боем</i>) <i>C, AG</i>."],
  ["praecento", "<big>praecento</big>\n\n<b>praecento, —, —, are</b> [prae + canto]\nпроизносить заклинания (alicui <i>C</i>)."],
  ["praecentor", "<big>praecentor</big>\n\n<b>praecentor, oris</b> <i>m</i> [praecino]\nзапевала <i>или</i> дирижёр (p. in choris <i>Ap</i>)."],
  ["praecentorius", "<big>praecentorius</big>\n\n<b>prae–centorius, a, um</b>\nотносящийся к музыкальному вступлению (tibiae <i>Sol</i>)."],
  ["praecepi", "<big>praecepi</big>\n\n<b>praecepi</b> <i>pf. к</i>\npraecipio."],
  ["praeceps", "<big>praeceps</big>\n\n<b>I praeceps, cipitis</b> <i>adj.</i> [prae + caput]\n<b>1)</b> вперёд <i>или</i> вниз головой, стремглав (aliquem praecipitem dejicere <i>C</i> <i>или</i> dare <i>Ter</i>);\n<b>2)</b> стремительный, быстрый (columba <i>V</i>; amnis <i>H</i>; Africus <i>H</i>); быстротечный (nox <i>O</i>; annus <i>SenT</i>); головокружительный (celeritas <i>C</i>): p. fertur <i>C</i> он бежит во всю прыть; praecipiti festinatione <i>Pt</i> с величайшей поспешностью; praecipites abimus <i>Pt</i> мы опрометью бросились бежать; agere aliquem praecipitem <i>C, Cs</i> гнаться за кем-л. сломя голову;\n<b>3)</b> склоняющийся (in occasum sol <i>L</i>); склоняющийся к закату (dies <i>L, Eutr</i>); уходящий, кончающийся (aestas <i>Sl</i>); неудержимо мчащийся (ad exitium <i>Su</i>);\n<b>4)</b> необдуманный, опрометчивый (in omnibus consiliis <i>C</i>); неистовый, безудержный (furor <i>C</i>); склонный, легко увлекаемый (in avaritiam et crudelitatem <i>L</i>); рискованный, опасный, критический (tempus <i>O</i>);\n<b>5)</b> крутой, обрывистый (locus <i>Cs</i>; saxa <i>L</i>); покатый, стремительно спускающийся (iter <i>O</i>);\n<b>6)</b> быстро действующий (remedium <i>QC</i>).\n\n<b>II praeceps, cipitis</b> <i>n</i>\n<b>1)</b> (= praecipitium) круча, крутизна, обрыв, пропасть, бездна (in p. deferri <i>L</i> <i>и</i> vadere <i>SenT</i>);\n<b>2)</b> крайняя опасность, критическое положение (se in p. dare <i>L</i>; levare aliquem ex praecipiti <i>H</i>): in praecipiti esse <i>Pt</i> быть крайне неустойчивым, висеть на волоске;\n<b>3)</b> высшая точка, вершина (accedere ad p. <i>PJ</i>); <i>перен.</i> высшая степень, предел (in praecipiti stare <i>J</i>).\n\n<b>III praeceps</b> <i>adv.</i>\nбыстро, стремительно, стремглав, сломя голову (aliquem p. trahere <i>T</i>): aliquid p. dare <i>T</i> подвергнуть что-л. опасности."],
  ["praeceptio", "<big>praeceptio</big>\n\n<b>praeceptio, onis</b> <i>f</i> [praecipio]\n<b>1)</b> <i>юр.</i> получение доли имущества до его раздела, прецепция (per praeceptionem legare <i>G, Dig</i>);\n<b>2)</b> предписание, приказание <i>CJ</i>;\n<b>3)</b> наставление, указание (p. recti <i>C</i>);\n<b>4)</b> учение (Stoicorum <i>C</i>);\n<b>5)</b> заранее сложившееся мнение, предвзятая мысль, предубеждение (praeceptionem inchoatam habere in animis <i>C</i>)."],
  ["praeceptive", "<big>praeceptive</big>\n\n<b>praeceptive</b>\nв виде предписания <i>Tert</i>."],
  ["praeceptivus", "<big>praeceptivus</big>\n\n<b>praeceptivus, a, um</b> [praecipio]\nдающий указания, поучающий, дидактический (pars philosophiae <i>Sen</i>)."],
  ["praeceptor", "<big>praeceptor</big>\n\n<b>praeceptor, oris</b> <i>m</i> [praecipio]\n<b>1)</b> наставник, учитель (philosophiae <i>Nep</i>; recti boni que <i>Pt</i>);\n<b>2)</b> повелитель, правитель <i>AG</i>."],
  ["praeceptrix", "<big>praeceptrix</big>\n\n<b>praeceptrix, icis</b> <i>f</i>\nнаставница <i>C, Vtr</i>."],
  ["praeceptum", "<big>praeceptum</big>\n\n<b>praeceptum, i</b> <i>n</i> [praecipio]\nпредписание, наставление, указание (praecepta medicorum <i>C</i>); правило (praecepta Latine loquendi <i>C</i>); учение (philosophorum <i>C</i>)."],
  ["praecerpo", "<big>praecerpo</big>\n\n<b>praecerpo, cerpsi, cerptum, ere</b> [prae + carpo]\n<b>1)</b> преждевременно собирать (messes <i>O</i>);\n<b>2)</b> отнимать (gratiam novitatis <i>PJ</i>); присваивать себе, <i>т. е.</i> уменьшать, умалять (fructum officii alicujus <i>C</i>);\n<b>3)</b> обрывать спереди (jubam leonis <i>St</i>);\n<b>4)</b> брать выдержки, цитировать (Aristotelis libros p. <i>AG</i>)."],
  ["praecessi", "<big>praecessi</big>\n\n<b>praecessi</b> <i>pf. к</i>\npraecedo."],
  ["praecessio", "<big>praecessio</big>\n\n<b>praecessio, onis</b> <i>f</i> [praecedo]\nпредварение <i>Aug</i>."],
  ["praecessor", "<big>praecessor</big>\n\n<b>praecessor, oris</b> <i>m</i>\n<b>1)</b> предшественник <i>Tert, Hier</i>;\n<b>2)</b> начальник, старший <i>Vlg</i>."],
  ["praecia", "<big>praecia</big>\n\n<b>prae–cia, ae</b> <i>m</i> [cieo]\nглашатай (<i>который призывал ремесленников прервать работу на время богослужебных обрядов фламинов</i>) <i>Ap</i> (<i>v. l.</i>)."],
  ["praecidaneus", "<big>praecidaneus</big>\n\n<b>praecidaneus, a, um</b> [prae + caedo]\n<b>1)</b> предварительно закалываемый, служащий вступительной жертвой (hostia <i>AG</i>): porca praecidanea <i>Cato</i> свинья, которую приносили в жертву перед жатвой;\n<b>2)</b> предварительный, празднуемый накануне (feriae <i>AG</i>)."],
  ["praecido", "<big>praecido</big>\n\n<b>praecido, cidi, cisum, ere</b> [prae + caedo]\n<b>1)</b> отрезывать, обрезывать, обрубать (alicui nasum <i>Just</i>, manus <i>C, Hirt</i>); отрубать, отсекать (caput alicujus <i>C, Pt</i>): p. ancoram <i>C</i> рубить якорный канат || обстригать (barbam <i>PM</i>); перерезать, преграждать (iter <i>PM</i>); срезать, <i>т. е.</i> объезжать, миновать (omnes sinus maris <i>Sen</i>);\n<b>2)</b> кастрировать (praecisi fanatici <i>Lampr</i>);\n<b>3)</b> ограничивать (libertatem vivendi <i>C</i>); лишать, отнимать (p. alicui spem, sibi reditum <i>C</i>); исключать: praecisa ultione <i>Pt</i> так как месть оказалась невозможной;\n<b>4)</b> устранять (causam belli <i>H</i>);\n<b>5)</b> отказывать, отклонять (plane aliquid <i>C</i>);\n<b>6)</b> вкратце излагать, быть кратким: brevi praecidam <i>C</i> скажу вкратце; praecide <i>C</i> скажи в немногих словах;\n<b>7)</b> разрезать, разрубать (cotem novacula <i>C</i>);\n<b>8)</b> прерывать (incipere aliquid dicere, deinde p. <i>rhH</i>); рвать, порывать (amicitias <i>C</i>);\n<b>9)</b> очерчивать: ad similitudinem praecisus <i>Pt</i> нарисованный с большим сходством;\n<b>10)</b> портить, приводить в негодность (naves <i>C</i>). — <i>См. тж.</i> praecisus."],
  ["praecinctio", "<big>praecinctio</big>\n\n<b>praecinctio, onis</b> <i>f</i> [praecingo]\nогибающий кругом (кольцевой) проход <i>или</i> кольцевая ограда <i>Vtr</i>."],
  ["praecinctorium", "<big>praecinctorium</big>\n\n<b>praecinctorium, i</b> <i>n</i>\nПоясная повязка, передник <i>Aug</i>."],
  ["praecinctura", "<big>praecinctura</big>\n\n<b>praecinctura, ae</b> <i>f</i>\n<b>1)</b> <i>Vtr</i> = praecinctio;\n<b>2)</b> опоясывание, т. е. способ ношения тоги <i>Macr</i>."],
  ["praecinctus", "<big>praecinctus</big>\n\n<b>praecinctus, us</b> <i>m</i>\n<b>1)</b> <i>Vtr</i> = praecinctio;\n<b>2)</b> одежда, платье <i>Macr</i>."],
  ["praecingo", "<big>praecingo</big>\n\n<b>prae–cingo, cinxi, cinctum, ere</b>\nопоясывать, препоясывать, подпоясывать (praecingi ense <i>O</i>); одевать (puer praecinctus <i>H etc.</i>): altius p. <i>H</i> выше подпоясанный, <i>т. е.</i> лучше подготовленный к путешествию <i>или</i> <i>Pt</i> к бою; praecincta membra <i>PM</i> тела с перетяжками (<i>sc.</i> insectorum) || обносить (fontem vallo <i>Prp</i>); окружать (terra praecincta mari <i>PM</i>); покрывать (parietes aliqua re <i>PJ</i>)."],
  ["praecino", "<big>praecino</big>\n\n<b>praecino, cinui (cecini), centum, ere</b> [prae + cano]\n<b>1)</b> петь <i>или</i> играть перед (sacrificiis <i>L</i>; alicui <i>Fl</i>);\n<b>2)</b> произносить заклинание (p. carmine magico <i>Tib</i>);\n<b>3)</b> запевать: p. gemitus <i>Sl</i> затянуть скорбную песнь;\n<b>4)</b> предвещать, возвещать (magnum aliquid alicui <i>C</i>; futura <i>PM</i>)."],
  ["praecinxi", "<big>praecinxi</big>\n\n<b>praecinxi</b> <i>pf. к</i>\npraecingo."],
  ["praecipes", "<big>praecipes</big>\n\n<b>praecipes, is</b> <i>Enn, Pl</i>\n= praeceps II."],
  ["praecipio", "<big>praecipio</big>\n\n<b>praecipio, cepi, ceptum, ere</b> [prae + capio]\n<b>1)</b> брать вперёд, наперёд получать (pecuniam mutuam <i>Cs</i>);\n<b>2)</b> ранее занимать, предупреждать в захвате (montem p. <i>Sl</i>); ранее начинать (bellum <i>T</i>): p. aliquantum viae (p. iter) <i>L</i> отправляться (несколько) раньше <i>или</i> опережать; tempore praecepto <i>L</i> ввиду преимущества (выигрыша) во времени;\n<b>3)</b> безвременно похищать (fatum aliquem praecipit <i>QC</i>);\n<b>4)</b> ускорять (mortem <i>Fl</i>): p. Parcas <i>St</i> ниспосылать преждевременную смерть;\n<b>5)</b> ранее высушивать (aestus lac praecipit <i>V</i>);\n<b>6)</b> предвосхищать (cogitatione futur? <i>C</i>); <i>med.-pass.</i> преждевременно созревать (seges precipitur <i>O</i>); предчувствовать, предвидеть (animo victoriam <i>Cs</i>); заранее ощущать, предвкушать (gaudia <i>C</i>): non praecipiam <i>C</i> не буду забегать вперёд;\n<b>7)</b> заранее узнавать, заблаговременно знакомиться (p. consilia hostium <i>C</i>): aliquid opinione p. <i>Cs</i> подозревать (предполагать) что-л.;\n<b>8)</b> предписывать, предлагать, приказывать, велеть, отдавать распоряжение (aliquid facere <i>C, Cs, QC etc.</i>, alicui aliquid <i>Pl, C etc.</i> <i>или</i> de aliqua re <i>PM</i>);\n<b>9)</b> учить, наставлять (p. artem nandi <i>O</i>; p. de eloquentia <i>C</i>; p. lugubres cantus <i>H</i>): praecipientes <i>Q, T</i> учителя;\n<b>10)</b> рекомендовать, советовать (aliquid facere <i>C etc.</i>): bene proverbio praecipitur <i>C</i> хороший совет даётся пословицей."],
  ["praecipitanter", "<big>praecipitanter</big>\n\n<b>praecipitanter</b>\nстремительно, сломя голову (currere <i>Lcr</i>)."],
  ["praecipitantia", "<big>praecipitantia</big>\n\n<b>praecipitantia, ae</b> <i>f</i> <i>AG</i>\n= praecipitatio."],
  ["praecipitatim", "<big>praecipitatim</big>\n\n<b>praecipitatim</b> <i>Vr</i>\n(<i>v. l.</i>) = praecipitanter."],
  ["praecipitatio", "<big>praecipitatio</big>\n\n<b>praecipitatio, onis</b> <i>f</i> [praecipito]\nстремительное падение, низвержение <i>Vtr, Sen, Ap</i>."],
  ["praecipitator", "<big>praecipitator</big>\n\n<b>praecipitator, oris</b> <i>m</i> [praecipito]\nниспровергатель, разрушитель <i>Aug</i>."],
  ["praecipitatus", "<big>praecipitatus</big>\n\n<b>praecipitatus, us</b> <i>m</i> <i>Vr</i>\n(<i>v. l.</i>)= praecipitatio."],
  ["praecipitis", "<big>praecipitis</big>\n\n<b>praecipitis</b> <i>gen. к</i>\npraeceps."],
  ["praecipitium", "<big>praecipitium</big>\n\n<b>praecipitium, i</b> <i>n</i>\n<b>1)</b> стремнина, пропасть, обрыв <i>Sen, Su, Q, VP</i>;\n<b>2)</b> <i>PM</i> = praecipitatio."],
  ["praecipito", "<big>praecipito</big>\n\n<b>praecipito, avi, atum, are</b> [praeceps]\n<b>1)</b> низвергать, сбрасывать, сталкивать (aliquem ex equo <i>L</i>, muro <i>Cs</i> <i>и</i> de muro <i>Hirt</i>, in fossam <i>Cs</i>; ex altissimo dignitatis gradu <i>C</i>); опрокидывать (aliquem super lectum <i>Pt</i>): p. se (<i>или</i> praecipitari) de turri <i>L</i> броситься с башни; lux (sol) praecipitatur aquis (in aquas) <i>O</i> солнце быстро заходит за море;\n<b>2)</b> ввергать в бедствия (rem publicam <i>L</i>); <i>перен.</i> разрушать, подрывать (spem <i>O</i>);\n<b>3)</b> устранять, прекращать (moras <i>V</i>);\n<b>4)</b> торопить, ускорять (curae praecipitant <i>V</i>; p. cursum <i>J</i>): consilia praecipitata <i>Q</i> наспех составленные планы, необдуманные решения;\n<b>5)</b> низвергаться, падать (praecipitantem impellere <i>C</i>; aqua praecipitat ex montibus <i>C</i>); бросаться (in fossam <i>L</i>);\n<b>6)</b> склоняться к закату (sol praecipitans <i>C</i>); <i>тж.</i> <i>med.-pass.</i> близиться к концу, быть на исходе (hiems praecipitat <i>Cs</i>; praecipitata nox <i>O</i>): aetate praecipitata <i>Matius ap. C</i> на склоне лет;\n<b>7)</b> быстро спускаться (nox caelo praecipitat <i>V</i>);\n<b>8)</b> гибнуть, рушиться (res publica praecipitans <i>C</i>);\n<b>9)</b> быстро устремляться (ad exitium <i>C</i>): dum p. potestas (<i>sc.</i> est) <i>V</i> пока ещё есть возможность спешить || попадать (in insidias <i>L</i>);\n<b>10)</b> выводить из равновесия, увлекать (furor mentem praecipitat <i>V</i>);\n<b>11)</b> низко нагибать (palmitem <i>Col</i>)."],
  ["praecipue", "<big>praecipue</big>\n\n<b>praecipue</b> [praecipuus]\nпреимущественно, главным образом, особенно (haec p. colenda est nobis <i>C</i>)."],
  ["praecipuum", "<big>praecipuum</big>\n\n<b>praecipuum, i</b> <i>n</i>\n<b>1)</b> преимущественное право, преимущество (praecipui nihil alicui dare <i>C</i>);\n<b>2)</b> важнейший момент, сущность (praecipua rerum <i>T</i>);\n<b>3)</b> <i>юр.</i> завещанное до общего распределения наследства <i>Su</i>;\n<b>4)</b> преимущество, достоинство (praecipua vel delicta <i>Amm</i>);\n<b>5)</b> <i>C</i> = producta."],
  ["praecipuus", "<big>praecipuus</big>\n\n<b>praecipuus, a, um</b> [praecipio]\n<b>1)</b> исключительный, чрезвычайный, особенный (dolor <i>C</i>; honor <i>Cs, T</i>); преимущественный (jus <i>C, O</i>);\n<b>2)</b> выдающийся, замечательный (p. in eloquentia vir <i>Q</i> — <i>о Цицероне</i>); главнейший, первейший, лучший (p. amicorum <i>T</i>);\n<b>3)</b> особенно пригодный, весьма полезный (remedium contra aliquid <i>PM</i>);\n<b>4)</b> особенно склонный (ad scelera <i>T</i>)."],
  ["praecisamentum", "<big>praecisamentum</big>\n\n<b>praecisamentum, i</b> <i>n</i> [praecido]\nкайма <i>или</i> обшивка <i>Is</i>."],
  ["praecise", "<big>praecise</big>\n\n<b>praecise</b> [praecisus]\n<b>1)</b> решительно, прямо (negare <i>C</i>);\n<b>2)</b> вкратце, сокращённо (dicere aliquid <i>C</i>)."],
  ["praecisio", "<big>praecisio</big>\n\n<b>praecisio, onis</b> <i>f</i> [praecido]\n<b>1)</b> обрезывание, вырезывание (genitalium <i>Ap</i>);\n<b>2)</b> обрезок, отрезок (praecisiones tignorum <i>Vtr</i>);\n<b>3)</b> <i>ритор.</i> внезапная остановка, умолчание <i>rhH, C</i>;\n<b>4)</b> жадность, любостяжание <i>Vlg</i>."],
  ["praecisor", "<big>praecisor</big>\n\n<b>praecisor, oris</b> <i>m</i>\nрежущий; praecisores dentes <i>Is</i> резцы."],
  ["praecisum", "<big>praecisum</big>\n\n<b>praecisum, i</b> <i>n</i> [praecido]\nотрезанный кусок мясной туши <i>Naev, LM</i>."],
  ["praecisura", "<big>praecisura</big>\n\n<b>praecisura, ae</b> <i>f</i> [praecido]\n<b>1)</b> отрезок <i>или</i> участок (agrorum <i>Frontin</i>);\n<b>2)</b> раздел <i>Boet</i>."],
  ["praecisus", "<big>praecisus</big>\n\n<b>praecisus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> praecido;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> крутой, обрывистый (saxum <i>V</i>);\n<b>2)</b> сокращённый, краткий, сжатый (expositio <i>Q</i>);\n<b>3)</b> кастрированный <i>Sen</i>;\n<b>4)</b> отрезанный (проливом) (Trinacria Italia praecisa <i>Man</i>)."],
  ["praeclamo", "<big>praeclamo</big>\n\n<b>prae–clamo, avi, —, are</b>\nпредупреждать криком <i>Dig</i>."],
  ["praeclare", "<big>praeclare</big>\n\n<b>praeclare</b> [praeclarus]\n<b>1)</b> весьма ясно (explicare <i>C</i>); отчётливо (meminisse <i>C</i>);\n<b>2)</b> прекрасно (facere aliquid <i>C etc.</i>; se habere <i>C</i>)."],
  ["praeclaritas", "<big>praeclaritas</big>\n\n<b>praeclaritas, atis</b> <i>f</i>\nблистательность (in communicatione sermonum <i>Vlg</i>)."],
  ["praeclarus", "<big>praeclarus</big>\n\n<b>praeclarus, a, um</b>\n<b>1)</b> очень светлый, ясный, яркий (lux <i>Lcr</i>);\n<b>2)</b> отчётливый, ясно слышимый (pulsus venarum <i>Ap</i>);\n<b>3)</b> прекрасный, замечательный (ad aspectum <i>C</i>); блистательный (genus dicendi <i>C</i>); славный, отличный (vir <i>C</i>); знаменитый (homo in philosophia p. <i>C</i>);\n<b>4)</b> известный (sceleribus suis praeclarus <i>Sl</i>)."],
  ["praecludo", "<big>praecludo</big>\n\n<b>praecludo, clusi, clusum, ere</b> [prae + claudo]\n<b>1)</b> запирать (portas <i>Cs</i>): praeclusis foribus <i>Pt</i> заперев двери;\n<b>2)</b> преграждать, загромождать (viam <i>Su</i>; aditum <i>Pt</i>): p. alicui aliquid <i>C etc.</i> преградить кому-л. путь (доступ) к чему-л.; p. alicui vocem <i>погов. L</i> зажать кому-л. рот (заставить молчать); p. negotiatorem <i>Su</i> запретить купцу торговать."],
  ["praeclusio", "<big>praeclusio</big>\n\n<b>praeclusio, onis</b> <i>f</i> [praecludo]\n<b>1)</b> закрывание (vulneris <i>Veg</i>);\n<b>2)</b> хранилище, резервуар: p. aquarum <i>Vtr</i> водоём."],
  ["praeclusor", "<big>praeclusor</big>\n\n<b>praeclusor, oris</b> <i>m</i>\nпреграждающий, закрывающий доступ (p. legis <i>Tert</i>)."],
  ["praeco", "<big>praeco</big>\n\n<b>praeco, onis</b> <i>m</i> [<i>из</i> praedico]\n<b>1)</b> глашатай, вестник, герольд <i>C, H etc.</i>;\n<b>2)</b> лицо, производящее продажу с торгов, аукционист (per praeconem vendere <i>C</i>);\n<b>3)</b> провозвестник (virtutis <i>C</i>)."],
  ["praecocis", "<big>praecocis</big>\n\n<b>praecocis</b> <i>gen. к</i>\npraecox."],
  ["praecogitatio", "<big>praecogitatio</big>\n\n<b>praecogitatio, onis</b> <i>f</i>\nпредварительное обдумывание <i>Eccl</i>."],
  ["praecogito", "<big>praecogito</big>\n\n<b>prae–cogito, avi, atum, are</b>\nнаперёд обдумывать (aliquid <i>Sen, Q</i>): praecogitatum facinus <i>L</i> заранее обдуманное преступление."],
  ["praecognitio", "<big>praecognitio</big>\n\n<b>prae–cognitio, onis</b> <i>f</i>\nпредшествующее (врождённое) знание <i>Boet, Ambr</i>."],
  ["praecognosco", "<big>praecognosco</big>\n\n<b>prae–cognosco, cognovi, cognitum, ere</b>\nзаранее узнавать, предвидеть (aliquid <i>C, Su, Vlg</i>)."],
  ["praecolligo", "<big>praecolligo</big>\n\n<b>prae–colligo, —, —, ere</b>\nзаранее собирать (aliquid <i>Boet</i>)."],
  ["praecolo", "<big>praecolo</big>\n\n<b>prae–colo, colui, cultum, ere</b>\n<b>1)</b> заранее обрабатывать, подготовлять (aliquid ad aliquid <i>C</i>);\n<b>2)</b> окружать преждевременным уважением, раньше времени восторгаться (p. nova et ancipitia <i>T</i>). — <i>См. тж.</i> praecultus."],
  ["praecommodo", "<big>praecommodo</big>\n\n<b>prae–commodo, —, —, are</b>\nуделять вперёд, давать взаймы (quingentos solidos <i>CTh</i>)."],
  ["praecommoveo", "<big>praecommoveo</big>\n\n<b>prae–commoveo, —, —, ere</b>\nглубоко волновать (aliquem <i>SenT</i>)."],
  ["praecompositus", "<big>praecompositus</big>\n\n<b>prae–compositus, a, um</b>\nзаранее подготовленный, заученный (os <i>O</i>)."],
  ["praeconcinnatus", "<big>praeconcinnatus</big>\n\n<b>prae–concinnatus, a, um</b>\nзаранее обдуманный, подготовленный (mendacium <i>Ap</i>)."],
  ["praeconfessio", "<big>praeconfessio</big>\n\n<b>prae–confessio, onis</b> <i>f</i>\nзаранее сделанное признание <i>Boet</i>."],
  ["praeconium", "<big>praeconium</big>\n\n<b>praeconium, i</b> <i>n</i> [praeconius]\n<b>1)</b> должность вестника, глашатая: facere p. <i>C</i> быть вестником;\n<b>2)</b> провозглашение, обнародование, объявление: peragere p. alicujus rei <i>O</i> опубликовать что-л., объявить о чём-л.;\n<b>3)</b> прославление, похвальное слово (quid addi potest ad hoc p.? <i>Ap</i>): alicui p. tribuere <i>C</i> <i>и</i> p. facere de aliquo <i>Lact</i> прославлять кого-л."],
  ["praeconius", "<big>praeconius</big>\n\n<b>praeconius, a, um</b> [praeco]\nотносящийся к вестнику, касающийся глашатая (quaestus <i>C</i>)."],
  ["praecono", "<big>praecono</big>\n\n<b>praecono, avi, —, are</b> [praeco]\nпровозглашать, возвещать (aliquid <i>Aug</i>)."],
  ["praeconor", "<big>praeconor</big>\n\n<b>praeconor, —, ari</b> <i>Eccl</i>\n= praecono."],
  ["praeconsumo", "<big>praeconsumo</big>\n\n<b>prae–consumo, sumpsi, sumptum, ere</b>\nзаранее израсходовать, растратить (suas vires <i>O</i>)."],
  ["praecontrecto", "<big>praecontrecto</big>\n\n<b>prae–contrecto, —, —, are</b>\nзаранее касаться, мысленно осязать (videndo aliquem <i>O</i>)."],
  ["praecoquis", "<big>praecoquis</big>\n\n<b>praecoquis, e</b> <i>Col, SenT</i>\n= praecox."],
  ["praecoquo", "<big>praecoquo</big>\n\n<b>prae–coquo, coxi, coctum, ere</b>\n<b>1)</b> заблаговременно сварить (aliquid in aliqua re <i>CA</i>);\n<b>2)</b> доводить до полной зрелости (uva praecocta sole <i>PM</i>)."],
  ["praecoquus", "<big>praecoquus</big>\n\n<b>praecoquus, a, um</b> <i>Col</i>\n= praecox."],
  ["praecordia", "<big>praecordia</big>\n\n<b>praecordia, orum</b> <i>n</i> [praebeor]\n<b>1)</b> грудобрюшная преграда <i>C, PM</i>;\n<b>2)</b> внутренности <i>C, V, H, PM</i>;\n<b>3)</b> грудная клетка (spiritus remanet in praecordiis <i>L</i>);\n<b>4)</b> грудь, <i>перен.</i> душа (redit in p. virtus <i>V</i>); желания, помыслы (stultae p. mentis <i>O</i>): Liber aperit p. <i>H</i> вино раскрывает душу; p. flagrantia <i>J</i> гнев, ярость."],
  ["praecordium", "<big>praecordium</big>\n\n<b>praecordium, i</b> <i>n</i> <i>Is</i>\n= praecordia 1."],
  ["praecorrumpo", "<big>praecorrumpo</big>\n\n<b>prae–corrumpo, corrupi, corruptum, ere</b>\nзаранее подкупать (aliquem donis <i>O</i>)."],
  ["praecox", "<big>praecox</big>\n\n<b>praecox, ocis</b> <i>adj.</i> [praecoquo]\n<b>1)</b> скороспелый, ранний (uva, pira <i>Col</i>);\n<b>2)</b> преждевременный, несвоевременный (gaudium <i>QC</i>); ранний, безвременный (fatum <i>SenT</i>)."],
  ["praecoxi", "<big>praecoxi</big>\n\n<b>praecoxi</b> <i>pf. к</i>\npraecoquo."],
  ["praecrassus", "<big>praecrassus</big>\n\n<b>prae–crassus, a, um</b>\nчрезвычайно толстый (cortex <i>PM</i>)."],
  ["praecrepo", "<big>praecrepo</big>\n\n<b>prae–crepo, crepui, —, are</b>\nранее раздаваться <i>Ambr</i>."],
  ["praeculco", "<big>praeculco</big>\n\n<b>prae–culco, —, —, are</b> [calco]\nзаранее <i>или</i> сильно запечатлевать, внушать (aliquid <i>Tert</i>)."],
  ["praecultus", "<big>praecultus</big>\n\n<b>prae–cultus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> praecolo;\n<b>2.</b> <i>adj.</i> пышно украшенный, разукрашенный (auro <i>St</i>); цветистый (genus eloquentiae <i>Q</i>)."],
  ["praecupidus", "<big>praecupidus</big>\n\n<b>prae–cupidus, a, um</b>\nсильно жаждущий, обожающий (alicujus rei <i>Su</i>)."],
  ["praecuro", "<big>praecuro</big>\n\n<b>prae–curo, —, —, are</b>\nухаживать, заботиться (p. corpus unctione <i>CA</i>; p. res suas <i>Tert</i>)."],
  ["praecurro", "<big>praecurro</big>\n\n<b>prae–curro, (cu)curri, cursum, ere</b>\n<b>1)</b> ехать (ездить) впереди, выезжать вперёд (equites praecurrunt <i>Cs</i>); предшествовать (certis rebus certa signa praecurrunt <i>C</i>); оказываться впереди, забегать вперёд (fama praecucurrit alicui <i>Cs</i>);\n<b>2)</b> быть предшественником, быть старше (aliquem aetate <i>C</i>); опережать, перегонять (aliquem <i>H, Sen</i>); упреждать (alicujus adventum <i>PM</i>); превосходить (alicui studio <i>C</i>; aliquem celeritate <i>Cs</i>): reges p. vita <i>H</i> жить счастливее царей."],
  ["praecursator", "<big>praecursator</big>\n\n<b>praecursator, oris</b> <i>m</i>\nразведчик, дозорный <i>Amm</i>."],
  ["praecursio", "<big>praecursio</big>\n\n<b>praecursio, onis</b> <i>f</i> [praecurro]\n<b>1)</b> предшествие, предварение <i>C, Aug</i>;\n<b>2)</b> <i>ритор.</i> подготовка, вступление <i>C</i>;\n<b>3)</b> <i>воен.</i> авангардная стычка, завязывание боя <i>PJ</i>."],
  ["praecursor", "<big>praecursor</big>\n\n<b>praecursor, oris</b> <i>m</i> [praecurro]\n<b>1)</b> <i>воен.</i> передовой боец, <i>pl.</i> головной отряд, авангард <i>L</i>;\n<b>2)</b> слуга, шедший впереди своего господина <i>PJ</i>;\n<b>3)</b> предшественник, предтеча <i>C, Eccl</i>."],
  ["praecursorius", "<big>praecursorius</big>\n\n<b>praecursorius, a, um</b> [praecursus]\nпредшествующий, служащий предвестником, предварительный (epistula <i>PJ</i>; index <i>Amm</i>)."],
  ["praecursus", "<big>praecursus</big>\n\n<b>praecursus, us</b> <i>m</i> [praecurro]\nпредшествование, более раннее появление (наступление) (etesiarum <i>PM</i>): mittere aliquem in praecursum alicujus <i>Vlg</i> выслать кого-л. в виде чьего-л. авангарда."],
  ["praecutio", "<big>praecutio</big>\n\n<b>praecutio, cussi, cussum, ere</b> [prae + quatio]\nпотрясать перед, размахивать впереди (taedas <i>O</i>; faces <i>Prp</i> — <i>v. l.</i>)."],
  ["praeda", "<big>praeda</big>\n\n<b>praeda, ae</b> <i>f</i> [<i>одного корня с</i> prehendo]\n<b>1)</b> добыча (bellica <i>Sl</i>; venaticia <i>Amm</i>); улов <i>O</i>;\n<b>2)</b> разграбление (agros praeda vastare <i>L</i>);\n<b>3)</b> награбленное (praedam e <i>и</i> de manibus amittere <i>Pl, C</i>);\n<b>4)</b> прибыль, барыш (ab aliquo magnas praedas facere <i>Nep</i>)."],
  ["praedabundus", "<big>praedabundus</big>\n\n<b>praedabundus, a, um</b> [praedor]\nрасхищающий, грабящий (exercitus <i>Sl, L, T</i>)."],
  ["praedamnatio", "<big>praedamnatio</big>\n\n<b>prae–damnatio, onis</b> <i>f</i>\nзаранее вынесенное осуждение <i>Tert</i>."],
  ["praedamno", "<big>praedamno</big>\n\n<b>prae–damno, avi, atum, are</b>\nзаранее осуждать (aliquem <i>L, Su</i>): p. spem <i>L</i> заранее отказаться от надежды; p. se perpetuae infelicitatis <i>VM</i> считать себя человеком, навеки обречённым на несчастье."],
  ["praedaticius", "<big>praedaticius</big>\n\n<b>praedaticius, a, um</b> [praedor]\nпохищенный, награбленный (pecunia <i>AG</i>)."],
  ["praedatio", "<big>praedatio</big>\n\n<b>praedatio, onis</b> <i>f</i> [praedor]\nграбительство, грабёж, расхищение <i>VP, T etc.</i>"],
  ["praedator", "<big>praedator</big>\n\n<b>praedator, oris</b> <i>m</i> [praedor]\n<b>1)</b> грабитель, хищник (praedatores direptoresque <i>C</i>);\n<b>2)</b> охотник (p. aprorum <i>O</i>);\n<b>3)</b> похититель, соблазнитель, совратитель (corporis <i>Pt</i>);\n<b>4)</b> любитель наживы, жадный человек <i>Tib</i>."],
  ["praedatorius", "<big>praedatorius</big>\n\n<b>praedatorius, a, um</b> [praedator]\nграбительский, разбойничий (manus <i>Sl</i>; navis <i>Pl</i>; classis <i>L</i>)."],
  ["praedatrix", "<big>praedatrix</big>\n\n<b>praedatrix, icis</b> <i>f</i>\nпохитительница (alicujus <i>St</i>); хищница: p. fera (bestia) <i>Amm</i> хищный зверь."],
  ["praedatum", "<big>praedatum</big>\n\n<b>praedatum, i</b> <i>n</i>\nдобыча <i>Vop</i>."],
  ["praedatus", "<big>praedatus</big>\n\n<b>I praedatus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> praedor <i>и</i> praedo II;\n<b>2.</b> <i>adj.</i> обременённый добычей (bene p. <i>Pl</i>).\n\n<b>II prae–datus, a, um</b> [do]\nданный ранее (<i>sc.</i> remedium) <i>CA</i>.\n\n<b>III praedatus, us</b> <i>m</i> [praedor]\nграбёж, расхищение <i>Pl</i>."],
  ["praedecessor", "<big>praedecessor</big>\n\n<b>prae–decessor, oris</b> <i>m</i>\nпредшественник <i>Eccl</i>."],
  ["praedelasso", "<big>praedelasso</big>\n\n<b>praedelasso, —, —, are</b>\nзаранее утомлять, <i>перен.</i> ослаблять (incursus aquarum <i>O</i>)."],
  ["praedelegatio", "<big>praedelegatio</big>\n\n<b>prae–delegatio, onis</b> <i>f</i>\nзаранее данное предписание <i>CTh</i>."],
  ["praedemno", "<big>praedemno</big>\n\n<b>praedemno</b> <i>v. l.</i>\n= praedamno."],
  ["praedemo", "<big>praedemo</big>\n\n<b>prae–demo, —, demptum, ere</b>\nпредварительно снимать (praedempto tegmine <i>CA</i> — <i>v. l.</i>)."],
  ["praedensus", "<big>praedensus</big>\n\n<b>praedensus, a, um</b>\nочень плотный (terra <i>PM</i>)."],
  ["praedes", "<big>praedes</big>\n\n<b>praedes</b> <i>pl. к</i>\npraes I."],
  ["praedesignatus", "<big>praedesignatus</big>\n\n<b>prae–designatus, a, um</b>\nранее возвещённый <i>Tert</i>."],
  ["praedestinatio", "<big>praedestinatio</big>\n\n<b>prae–destinatio, onis</b> <i>f</i>\nпредопределение, предназначение <i>Boet, Eccl</i>."],
  ["praedestino", "<big>praedestino</big>\n\n<b>prae–destino, avi, atum, are</b>\n<b>1)</b> заранее намечать, наперёд ставить целью (sibi aliquid <i>L</i>);\n<b>2)</b> заранее приобретать (aliquid modico pretio <i>Ap</i>);\n<b>3)</b> предопределять (aliquem <i>Vlg, Eccl</i>)."],
  ["praediator", "<big>praediator</big>\n\n<b>praediator, oris</b> <i>m</i> [praedium]\n<b>1)</b> скупщик заложенных и назначенных к продаже с торгов имений <i>C, G</i>;\n<b>2)</b> специалист по вопросам земельной собственности, оценщик земельных участков <i>C</i>."],
  ["praediatorius", "<big>praediatorius</big>\n\n<b>praediatorius, a, um</b> [praediator]\nкасающийся залога земельной собственности (lex <i>Su</i>): jus praediatorium <i>C</i> ипотечное право."],
  ["praediatura", "<big>praediatura</big>\n\n<b>praediatura, ae</b> <i>f</i>\nскупка заложенных государству и продаваемых с торгов земельных участков <i>G</i>."],
  ["praediatus", "<big>praediatus</big>\n\n<b>praediatus, a, um</b> [praedium]\nвладеющий земельными угодьями (bene p. <i>Ap</i>)."],
  ["praedicabilis", "<big>praedicabilis</big>\n\n<b>praedicabilis, e</b> [praedico I]\nдостойный похвалы, похвальный, ценный (p. aut gloriandus <i>C</i>; testimonium <i>Ap</i>)."],
  ["praedicamentum", "<big>praedicamentum</big>\n\n<b>praedicamentum, i</b> <i>n</i> [praedico I]\n<b>1)</b> высказывание (judicii <i>Aug</i>);\n<b>2)</b> <i>филос.</i> категория, наиболее общее свойство <i>Aug</i>."],
  ["praedicatio", "<big>praedicatio</big>\n\n<b>praedicatio, onis</b> <i>f</i> [praedico I]\n<b>1)</b> провозглашение, объявление, оповещение <i>C, Ap</i>;\n<b>2)</b> высказывание, утверждение <i>C, Lact</i>;\n<b>3)</b> похвальный отзыв, похвала, прославление <i>Pl, C, L</i>;\n<b>4)</b> возвещение, проповедь <i>Vlg, Lact</i>."],
  ["praedicativus", "<big>praedicativus</big>\n\n<b>praedicativus, a, um</b> [praedico]\nутвердительный, категорический (propositio <i>Ap</i>)."],
  ["praedicator", "<big>praedicator</big>\n\n<b>praedicator, oris</b> <i>m</i> [praedico]\n<b>1)</b> объявляющий, возвещающий (Mercurius <i>Ap</i>);\n<b>2)</b> отзывающийся с похвалой <i>C</i>;\n<b>3)</b> проповедник <i>Eccl</i>."],
  ["praedico", "<big>praedico</big>\n\n<b>I prae–dico, avi, atum, are</b>\n<b>1)</b> объявлять во всеуслышание, провозглашать, извещать (praeco praedicat aliquid <i>C</i>; p. aliquem repertorem ejus inventi <i>Ap</i>);\n<b>2)</b> объявлять, заявлять, говорить (de aliqua re <i>Cs</i>);\n<b>3)</b> упоминать (отзываться) с похвалой, хвалить (p. de meritis alicujus <i>Cs</i>; aliquem <i>и</i> de aliquo <i>C</i>; benignitatem alicujus <i>Nep</i>): praedicanda <i>Ap</i> похвальные дела;\n<b>4)</b> именовать, называть (aliquem liberatorem patriae <i>Nep</i>); обзывать (purgamentum dedecusque <i>Pt</i>);\n<b>5)</b> проповедовать (Christum <i>Vlg</i>; apostolus mittere ad praedicandum <i>Tert</i>);\n<b>6)</b> предвещать, предсказывать (persecutiones <i>Tert</i>).\n\n<b>II prae–dico, dixi, dictum, ere</b>\n<b>1)</b> говорить наперёд, предупреждать: praediceres <i>Ter</i> тебе бы следовало (об этом) раньше сказать;\n<b>2)</b> заранее назначать (praedicta die <i>T</i>);\n<b>3)</b> предсказывать (futura <i>C</i>);\n<b>4)</b> ранее упоминать: tria, quae praediximus <i>Q</i> три (момента), упомянутые нами выше; praedicta ratio <i>Q</i> вышеуказанное основание;\n<b>5)</b> велеть, предписывать, указывать (alicui, ut aliquid faciat <i>L</i>)."],
  ["praedictio", "<big>praedictio</big>\n\n<b>praedictio, onis</b> <i>f</i> [praedico II]\n<b>1)</b> <i>ритор.</i> предисловие, вступление <i>Q</i>;\n<b>2)</b> предсказывание (rerum futurarum <i>C</i>)."],
  ["praedictivus", "<big>praedictivus</big>\n\n<b>praedictivus, a, um</b> [praedico II] <i>мед.</i>\nпрогностический (liber <i>CA</i>)."],
  ["praedictum", "<big>praedictum</big>\n\n<b>praedictum, i</b> <i>n</i> [praedico II]\n<b>1)</b> предсказание (Chaldaeorum <i>C</i>; vatum priorum, <i>v. l.</i> piorum <i>V</i>);\n<b>2)</b> приказ, предписание (dictatoris <i>L</i>);\n<b>3)</b> соглашение, уговор: ex praedicto <i>L</i> по уговору."],
  ["praedifficilis", "<big>praedifficilis</big>\n\n<b>prae–difficilis, e</b>\nкрайне трудный <i>Tert</i>."],
  ["praedigestus", "<big>praedigestus</big>\n\n<b>prae–digestus, a, um</b>\nхорошо переваривший (corpus <i>CA</i>)."],
  ["praediolum", "<big>praediolum</big>\n\n<b>praediolum, i</b> <i>n</i> [<i>demin. к</i> praedium]\nнебольшое поместье, именьице <i>C, AG, PJ</i>."],
  ["praedirus", "<big>praedirus</big>\n\n<b>prae–dirus, a, um</b>\nужасающий, ужасный (visu dictuque <i>Amm</i>)."],
  ["praedis", "<big>praedis</big>\n\n<b>praedis</b> <i>gen. к</i>\npraes I."],
  ["praedisco", "<big>praedisco</big>\n\n<b>prae–disco, didici, —, ere</b>\nнаперёд изучать, узнавать, знакомиться (p. ventos <i>V</i>; p. ea, quae agenda sunt <i>C</i>)."],
  ["praedispositus", "<big>praedispositus</big>\n\n<b>praedispositus, a, um</b> [prae + dispono]\nзаранее выставленный, заблаговременно подготовленный (nuntii <i>L</i> — <i>v. l.</i>)."],
  ["praeditus", "<big>praeditus</big>\n\n<b>praeditus, a, um</b> [prae + do]\n<b>1)</b> одарённый, наделённый (sensibus <i>C</i>; summis <i>или</i> maximis ingeniis <i>C</i>); снабжённый (pulchris armis <i>Pl</i>): tanta stat praedita culpa (<i>sc.</i> natura rerum)! <i>Lcr</i> вот как порочна природа!;\n<b>2)</b> проникнутый (spe <i>C</i>); охваченный (metu <i>C</i>); отличающийся (immani crudelitate <i>C</i>);\n<b>3)</b> возглавляющий, руководящий ((alicui <i>и</i> alicui rei <i>Ap</i>)."],
  ["praedium", "<big>praedium</big>\n\n<b>praedium, i</b> <i>n</i> [praes I]\nнедвижимое имущество (p. urbanum <i>C</i>); земельная собственность, поместье (p. suburbanum <i>Col</i>; p. Cumanum <i>Pt</i>)."],
  ["praedives", "<big>praedives</big>\n\n<b>prae–dives, vitis</b> <i>adj.</i>\nчрезвычайно богатый <i>L, T, O, J</i>."],
  ["praedivinatio", "<big>praedivinatio</big>\n\n<b>praedivinatio, anis</b> <i>f</i> [praedivino]\nпредчувствие, предвидение, предсказание <i>PM</i>."],
  ["praedivino", "<big>praedivino</big>\n\n<b>prae–divino, avi, —, are</b>\nпредугадывать, предчувствовать (aliquid <i>Vr, PM</i>)."],
  ["praedivinus", "<big>praedivinus</big>\n\n<b>prae–divinus, a, um</b>\nпророческий, вещий (somnium <i>PM, Sol</i>)."],
  ["praedixi", "<big>praedixi</big>\n\n<b>praedixi</b> <i>pf. к</i>\npraedico II."],
  ["praedo", "<big>praedo</big>\n\n<b>I praedo, onis</b> <i>m</i> [praeda]\nграбитель, разбойник <i>C, L, Nep etc.</i>; хищник: p. volucrum <i>M</i> = accipiter || похититель: p. mantus <i>O</i> = Pluto.\n\n<b>II praedo, avi, atum, are</b> <i>Pl, AG etc.</i>\n= praedor."],
  ["praedoceo", "<big>praedoceo</big>\n\n<b>prae–doceo, (docui), doctum, ere</b>\nнаперёд учить, заранее наставлять, предварительно указывать (praedoctus ab aliquo <i>Sl</i>): praedoctus esto <i>PM</i> знай, имей в виду."],
  ["praedomo", "<big>praedomo</big>\n\n<b>prae–domo, domui, —, are</b>\nзаранее укрощать, заблаговременно подавлять, наперёд преодолевать (aliquid meditando <i>Sen</i>)."],
  ["praedonius", "<big>praedonius</big>\n\n<b>praedonius, a, um</b> [praedo I]\nразбойничий, грабительский (mos <i>Dig</i>)."],
  ["praedonulus", "<big>praedonulus</big>\n\n<b>praedonulus, i</b> <i>m</i> [<i>demin. к</i> praedo I]\nмелкий грабитель <i>Cato</i>."],
  ["praedor", "<big>praedor</big>\n\n<b>praedor, atus sum, ari</b> <i>depon.</i> [praeda]\n<b>1)</b> грабить, разбойничать (milites praedantes <i>Cs</i>): p. maria <i>Lact</i> пиратствовать || ограблять, обирать (aliquem <i>T</i>); похищать, уносить (ovem <i>O</i>): singula de nobis anni praedantur euntes <i>H</i> уходящие годы мало-помалу уносят и нас || хватать добычу (dentibus, unguibus <i>PM</i>);\n<b>2)</b> обогащаться, извлекать пользу (in <i>и</i> de bonis alienis <i>C</i>; ex alieno <i>L</i>);\n<b>3)</b> ловить (pisces hamo <i>Prp</i>)."],
  ["praeduco", "<big>praeduco</big>\n\n<b>prae–duco, duxi, ductum, ere</b>\nпроводить перед (p. fossas viis — <i>dat. Cs</i>); строить впереди (murum <i>Cs</i>): p. lineas itineri <i>PM</i> обозначать направление пути, проводить трассу дороги."],
  ["praeductorius", "<big>praeductorius</big>\n\n<b>prae–ductorius, a, um</b>\nслужащий для ведения вперёд (lorum <i>Cato</i>)."],
  ["praedulce", "<big>praedulce</big>\n\n<b>praedulce</b> <i>adv.</i>\nласково, чарующе <i>St</i>."],
  ["praedulcis", "<big>praedulcis</big>\n\n<b>prae–dulcis, e</b>\n<b>1)</b> очень сладкий (mel, sapor <i>PM</i>); <i>перен.</i> приторный (sententia <i>Sen</i>);\n<b>2)</b> весьма приятный, милый (decus <i>V</i>; vox <i>Su</i>; nomen <i>VF</i>; luxuries praedulce malum <i>Cld</i>)."],
  ["praeduro", "<big>praeduro</big>\n\n<b>prae–duro, avi, atum, are</b>\n<b>1)</b> делать очень твёрдым, сильно уплотнять (sucum <i>PM</i>; caseum <i>Col</i>);\n<b>2)</b> делать чёрствым, нечувствительным (callum pectoris <i>Eccl</i>)."],
  ["praedurus", "<big>praedurus</big>\n\n<b>prae–durus, a, um</b>\n<b>1)</b> крайне твёрдый (faba <i>PM</i>; corium <i>T</i>);\n<b>2)</b> очень крепкий, выносливый (corpora <i>V</i>);\n<b>3)</b> весьма тяжёлый, труднейший (labor <i>VF</i>);\n<b>4)</b> слишком бесстыдный, крайне наглый (os, verba <i>Q</i>)."],
  ["praeeligo", "<big>praeeligo</big>\n\n<b>prae–eligo, —, —, ere</b>\nпредпочитать <i>Sid</i>."],
  ["praeemineo", "<big>praeemineo</big>\n\n<b>prae–emineo, —, —, ere</b>\n<b>1)</b> выдаваться вперёд (oculi praeeminentes <i>Dig</i>);\n<b>2)</b> быть выше, превосходить (alicui <i>Sen, Aus</i> <i>или</i> aliquem <i>T</i>)."],
  ["praeeo", "<big>praeeo</big>\n\n<b>prae–eo, ivi (ii), itum, ire</b>\n<b>1)</b> идти впереди, предшествовать (lictores consulibus praeeunt <i>C</i>); обгонять, опережать (famam sui <i>T</i>);\n<b>2)</b> предводительствовать, руководить, указывать путь (alicui <i>C, Q, AG</i> <i>или</i> aliquem <i>T</i>): natura praeeurtte <i>C</i> по указаниям (велениям) природы;\n<b>3)</b> (<i>тж.</i> p. verbis <i>Pl</i>) диктовать для повторения, подсказывать (alicui sacramentum <i>T</i>);\n<b>4)</b> велеть, предписывать (omnia, ut decemviri praeierunt, facta sunt <i>L</i>)."],
  ["praeesse", "<big>praeesse</big>\n\n<b>praeesse</b> <i>inf. к</i>\npraesum."],
  ["praeeuntis", "<big>praeeuntis</big>\n\n<b>praeeuntis</b> <i>gen. к</i>\npraeiens."],
  ["praeexsisto", "<big>praeexsisto</big>\n\n<b>prae–ex(s)isto, —, —, ere</b>\nпредсуществовать <i>Boet</i>."],
  ["praefactus", "<big>praefactus</big>\n\n<b>prae–factus, a, um</b>\nранее сделанный <i>CA</i>."],
  ["praefamen", "<big>praefamen</big>\n\n<b>prae–famen, inis</b> <i>n</i> [for]\nпредисловие <i>Symm</i>."],
  ["praefamino", "<big>praefamino</big>\n\n<b>praefamino</b> <i>Cato imper. fut. к</i>\npraefor."],
  ["praefandus", "<big>praefandus</big>\n\n<b>praefandus</b> — <i>см.</i>\npraefor."],
  ["praefari", "<big>praefari</big>\n\n<b>praefari</b> <i>inf. к</i>\npraefor."],
  ["praefatio", "<big>praefatio</big>\n\n<b>prae–fatio, onis</b> <i>f</i> [praefor]\n<b>1)</b> вступительное слово, вступление (p. sacrorum <i>L</i>);\n<b>2)</b> введение, предисловие <i>Q, PJ, M, Dig</i>;\n<b>3)</b> предварительное замечание: cum honoris praefatione <i>PM</i> предварительно извинившись, с позволения сказать;\n<b>4)</b> титул, звание, обращение (jucundissime imperator — sit enim haec tui p. verissima <i>PM</i>)."],
  ["praefatiuncula", "<big>praefatiuncula</big>\n\n<b>praefatiuncula, ae</b> <i>f</i> [<i>demin. к</i> praefatio]\nмаленькое предисловие <i>Hier</i>."],
  ["praefato", "<big>praefato</big>\n\n<b>praefato</b> <i>imper. fut. к</i>\npraefor."],
  ["praefator", "<big>praefator</big>\n\n<b>praefator, oris</b> <i>m</i> [praefor] <i>Is</i>\n= propheta."],
  ["praefatum", "<big>praefatum</big>\n\n<b>praefatum, i</b> <i>n</i> <i>Symm</i>\n= praefatio 1."],
  ["praefatus", "<big>praefatus</big>\n\n<b>I prae–fatus, a, um</b> <i>part. pf. к</i>\npraefor.\n\n<b>II praefatus, (us) </b> <i>m</i>\nпредупреждение <i>Symm</i>."],
  ["praefectianus", "<big>praefectianus</big>\n\n<b>praefectiānus, ī</b> <i>m</i> (<i>тж.</i> p. apparitor <i>Amm</i>)\nслужащий при praefectus praetorio <i>CJ</i>."],
  ["praefectio", "<big>praefectio</big>\n\n<b>praefectio, ōnis</b> <i>f</i> [praeficio]\nвыдвигание вперёд, постановка впереди <i>Vr</i>."],
  ["praefector", "<big>praefector</big>\n\n<b>praefector, ōris</b> <i>m</i> <i>Dig</i>\n= praefectus II, 1."],
  ["praefectorius", "<big>praefectorius</big>\n\n<b>praefectōrius, ī</b> <i>m</i> (<i>тж.</i> p. vir)\nбывший префект <i>Dig, Sid</i>."],
  ["praefectura", "<big>praefectura</big>\n\n<b>praefectūra, ae</b> <i>f</i> [praefectus II]\n<b>1)</b> должность смотрителя, надзирателя, начальника (p. villae <i>Vr</i>): p. morum <i>Su</i> надзор за нравственностью граждан (<i>в республиканскую эпоху лежавший на цензорах</i>); p. annonae <i>T</i> руководство продовольственным снабжением;\n<b>2)</b> начальствование, командование (equitum <i>Hirt</i>);\n<b>3)</b> управление провинцией (p. Aegypti <i>Su</i>);\n<b>4)</b> наместничество, административный округ (Aegyptus dividitur in praefecturas <i>PM</i>);\n<b>5)</b> префектура, <i>областной город в Италии, управлявшийся римск. префектом</i> <i>C</i>."],
  ["praefectus", "<big>praefectus</big>\n\n<b>I praefectus, a, um</b> <i>part. pf. к</i>\npraeficio.\n\n<b>II praefectus, ī</b> <i>m</i>\n<b>1)</b> префект, начальник, глава (alicujus rei <i>или</i> alicui rei); p. cohortibus praetoriis <i>T</i>, <i>тж.</i> p. praetorii <i>Su, T</i> <i>и</i> p. praetorio <i>Veg, CJ</i> префект преторских когорт, <i>т. е.</i> начальник императорской охраны; p. morum (moribus) <i>Pl, C</i> блюститель нравов; p. annonae (rei frumentariae) <i>L, T etc.</i> заведующий продовольственным снабжением (Рима); p. vigilum <i>T, Dig</i> (vigilibus <i>Dig</i>) начальник ночной стражи (<i>семи римск. когорт с полицейскими функциями</i>); p. urbis <i>L</i> (urbi <i>Vr, AG</i>; urbicus <i>Lampr</i>) начальник города Рима (<i>в республик. эпоху замещавший отсутствовавшего консула</i>); p. classis (classi) <i>L</i> командующий флотом; p. navis <i>L, Fl</i> командир судна; p. Lydiae <i>Nep</i> (<i>у персов</i>) префект (наместник) Лидии; p. equitum <i>Hirt</i> командир конного отряда (<i>преим.</i> союзников);\n<b>2)</b> (<i>в странах Азии</i>) полководец (p. regis <i>или</i> regius <i>Nep, Fl</i>)."],
  ["praefecundus", "<big>praefecundus</big>\n\n<b>prae–fēcundus, a, um</b>\nчрезвычайно плодородный <i>PM</i>."],
  ["praefero", "<big>praefero</big>\n\n<b>prae–fero, tuli, latum, ferre</b>\n<b>1)</b> носить впереди (aliquid alicui): p. fasces praetoribus <i>C</i> нести фасции впереди преторов;\n<b>2)</b> выставлять вперёд, держать впереди (manus cautas <i>O</i>);\n<b>3)</b> показывать как пример (suam vitam civibus <i>C</i>);\n<b>4)</b> отговариваться: alicujus rei speciem <i>или</i> titulum alicui rei p. <i>QC</i> скрывать что-л. подвидом чего-л.;\n<b>5)</b> предпочитать (aliquid alicui rei <i>J</i>; p. antiquos novis <i>M</i>): p. aliquem alicui <i>O etc.</i> отдать кому-л. предпочтение перед кем-л.;\n<b>6)</b> проявлять, выказывать (avaritiam <i>C</i>); явно показывать, выражать, обнаруживать (dolorem animi vultu <i>QC</i>; motum animi oculis <i>QC</i>); высказывать (judicium <i>L</i>): praefertur opinio <i>Cs</i> высказывается мнение; se p. alicui <i>Cs</i> проявить себя (отличиться) перед кем-л.; virtute belli omnibus praeferri <i>Cs</i> превзойти всех воинской доблестью;\n<b>7)</b> оказывать (nullam opem <i>St</i>);\n<b>8)</b> упреждать, преждевременно пользоваться (diem triumphi p. <i>L</i>);\n<b>9)</b> <i>pass.</i> praeferri ехать мимо, проезжать (praelatus equo <i>T</i>; praeter castra praelati <i>L</i>)."],
  ["praefero", "<big>praefero</big>\n\n<b>prae–fero, trivi, tritum, ere</b>\nстирать спереди (aliquid Urna <i>Pl</i>; primores dentes <i>PM</i>); предварительно протирать (amygdalum praetritum <i>CA</i>)."],
  ["praeferox", "<big>praeferox</big>\n\n<b>prae–ferox, ocis</b> <i>adj.</i>\nнеобузданный, неистовый, буйный <i>L, T</i>; заносчивый <i>Su</i>."],
  ["praeferratus", "<big>praeferratus</big>\n\n<b>prae–ferratus, a, um</b>\n<b>1)</b> окованный (обитый) спереди железом (modium <i>Cato</i>); снабжённый железным наконечником (pilum <i>PM</i>);\n<b>2)</b> закованный в цепи (tribunus <i>Pl</i>)."],
  ["praefertilis", "<big>praefertilis</big>\n\n<b>prae–fertilis, e</b> <i>Eccl</i>\n= praefecundus."],
  ["praefervidus", "<big>praefervidus</big>\n\n<b>prae–fervidus, a, um</b>\n<b>1)</b> очень горячий, кипящий (balneum <i>T</i>); жаркий, знойный (aestas <i>Col</i>; regio <i>Col</i>);\n<b>2)</b> пламенный, неукротимый (ira <i>L</i>)."],
  ["praefestino", "<big>praefestino</big>\n\n<b>prae–festino, —, —, are</b>\n<b>1)</b> слишком торопиться, спешить (aliquid facere <i>Pl, L</i>); слишком поспешно делать (praefestinatum opus <i>Col</i>);\n<b>2)</b> торопливо проходить мимо, быстро миновать, промчаться мимо (Thermaeum sinum p. <i>T</i>)."],
  ["praefica", "<big>praefica</big>\n\n<b>praefica, ae</b> <i>f</i> [praeficio] (<i>тж.</i> mulier p. <i>AG</i>)\nплакальщица (<i>на похоронах</i>) <i>Pl, Vr</i>."],
  ["praeficio", "<big>praeficio</big>\n\n<b>praeficio, feci, fectum, ere</b> [prae + facio]\nставить над, во главе: p. aliquem alicui rei <i>C, Cs, QC</i> сделать кого-л. начальником над чём-л. (поставить кого-л. во главе чего-л.); p. aliquem classi <i>Cs, Nep</i> возложить на кого-л. командование флотом. — <i>См. тж.</i> praefectus."],
  ["praefidens", "<big>praefidens</big>\n\n<b>prae–fidens, entis</b> <i>adj.</i>\nслишком доверяющий: p. sibi <i>C, Aug</i> слишком самонадеянный, самоуверенный."],
  ["praefidenter", "<big>praefidenter</big>\n\n<b>praefidenter</b>\nсамоуверенно <i>Aug</i>."],
  ["praefigo", "<big>praefigo</big>\n\n<b>prae–figo, fixi, fixum, ere</b>\n<b>1)</b> спереди прибивать, надевать, прикреплять (aliquid alicui rei: p. ora capistris <i>V</i>); втыкать, накалывать (capita in hastis <i>V</i> <i>или</i> hastae — — <i>dat. Su</i>): nigrum theta p. vitio <i>Pers</i> отметить порок чёрной тэтой (<i>начальной буквой греч. слова</i> thanatos <i>смерть</i>), <i>т. е.</i> осудить на смерть;\n<b>2)</b> спереди вбивать (sudes praefixae <i>Cs</i>);\n<b>3)</b> спереди обивать, оковывать (aliquid ferro <i>V, O, L</i>);\n<b>4)</b> пронзать, прокалывать (latus veru <i>Tib</i>);\n<b>5)</b> околдовывать (aliquid aliqua re <i>Q</i>);\n<b>6)</b> загораживать (prospectus <i>PM</i>; fenestras praefixas habere <i>Dig</i>)."],
  ["praefiguratio", "<big>praefiguratio</big>\n\n<b>praefiguratio, onis</b> <i>f</i>\nпредварительное образование, предначертанность (futurorum <i>Aug</i>)."],
  ["praefiguro", "<big>praefiguro</big>\n\n<b>praefiguro, avi, atum, are</b>\nпредобразовывать (aliquid <i>Eccl</i>)."],
  ["praefinio", "<big>praefinio</big>\n\n<b>prae–finio, ivi (ii), itum, ire</b>\nзаранее определять, наперёд устанавливать, предопределять, предписывать (diem alicui <i>C</i>; ad certum praefinitumque tempus <i>Su</i>): neque de illo tibi quicquam praefinio, quominus ingenio mecum contendas <i>C</i> я ничего не предписываю тебе относительно того, какими логическими приёмами пользоваться в споре со мной."],
  ["praefinitio", "<big>praefinitio</big>\n\n<b>praefinitio, onis</b> <i>f</i>\nпредустановление, предписание (sine praefinitione temporis <i>Dig</i>); предопределение, предначертание <i>Vlg, Eccl</i>."],
  ["praefinito", "<big>praefinito</big>\n\n<b>prae–finito</b>\nсогласно предписанию, как приказано (loqui <i>Ter</i>)."],
  ["praefiscine", "<big>praefiscine</big>\n\n<b>praefiscine (praefiscini)</b> <i>adv.</i> [prae + fascinum]\nне сглазить бы, <i>перен.</i> можно (смело) сказать, без преувеличения: homo p. frugi <i>Pt</i> человек, прямо скажем, честный; p. hoc dixerim <i>Ap</i> я не побоюсь сказать это."],
  ["praefixus", "<big>praefixus</big>\n\n<b>praefixus, a, um</b> <i>part. pf. к</i>\npraefigo."],
  ["praefletus", "<big>praefletus</big>\n\n<b>praefletus, a, um</b> <i>v. l.</i>\n= perfletus."],
  ["praefloreo", "<big>praefloreo</big>\n\n<b>prae–floreo, florui, —, ere</b>\nпреждевременно цвести, раньше времени зацветать <i>PM, Hier</i>."],
  ["praefloro", "<big>praefloro</big>\n\n<b>prae–floro, avi, atum, are</b>\n<b>1)</b> преждевременно лишать цвета, <i>перен.</i> блеска (fructum gaudii <i>AG</i>); омрачать, умалять (gloriam victoriae <i>L</i>);\n<b>2)</b> <i>Ap</i> = defloro."],
  ["praefluo", "<big>praefluo</big>\n\n<b>prae–fluo, —, —, ere</b>\nпротекать перед, впереди, мимо (infima valle praefluit Tiberis <i>L</i>); обтекать (Noricam provinciam <i>T</i>)."],
  ["praefluus", "<big>praefluus</big>\n\n<b>prae–fluus, a, um</b>\nпротекающий мимо (amnis <i>PM</i>)."],
  ["praefocabilis", "<big>praefocabilis</big>\n\n<b>praefocabilis, e</b>\n<b>1)</b> задыхающийся (spiratio <i>CA</i>);\n<b>2)</b> удушливый (loca <i>CA</i>): difficultas p. <i>CA</i> затруднённое дыхание, одышка."],
  ["praefocatio", "<big>praefocatio</big>\n\n<b>praefocatio, onis</b> <i>f</i>\nодышка, удушье <i>Scr, CA</i>."],
  ["praefoco", "<big>praefoco</big>\n\n<b>prae–foco, avi, atum, are</b> [fauces]\nдушить, удавить (aliquem <i>O, Dig, CA, Eccl</i>); (<i>о сорняках</i>) глушить (praefocata messis habet lolium <i>Calp</i>)."],
  ["praefodio", "<big>praefodio</big>\n\n<b>prae–fodio, fodi, fossum, ere</b>\n<b>1)</b> окапывать впереди: p. portas <i>V</i> вырыть ров перед воротами;\n<b>2)</b> заблаговременно рыть (scrobes <i>PM</i>);\n<b>3)</b> заранее закапывать, зарывать (aurum <i>O</i>)."],
  ["praefor", "<big>praefor</big>\n\n<b>prae–for, fatus sum, fari</b> <i>depon.</i>\n<b>1)</b> произносить вступительное слово, делать вступление, предварять, предпосылать: majores nostri omnibus rebus agendis «quod bonum, faustum, felix esset» praefabantur <i>C</i> всем делам наши предки предпосылали слова «да будет к добру, на благо, на счастье»; ture, vino Jovi p. <i>Cato</i> предварительно помолиться Юпитеру, куря ладан и совершая возлияние; quae de ea re praefatus est <i>C</i> то, что он скат зал выше об этом; praefanda venia est <i>Ap</i> я должен начать с просьбы о (вашей) благосклонности; sed jam p. desinamus <i>Col</i> но довольно (этих) вступительных слов; p. honorem <i>C</i> (veniam <i>Ap</i>) начать с извинений, заранее извиниться; praefanda (<i>sc.</i> verba, dicta) <i>Q</i> слова, которые принято произносить лишь предварительно извинившись, <i>т. е.</i> непристойные; umor praefandus <i>PM</i> = excrementa;\n<b>2)</b> предсказывать <i>Ctl, L, Just</i>."],
  ["praeformido", "<big>praeformido</big>\n\n<b>prae–formido, (avi), atum, are</b>\nзаранее бояться (aliquem <i>Q, Sil</i>)."],
  ["praeformo", "<big>praeformo</big>\n\n<b>prae–formo, avi, atum, are</b>\n<b>1)</b> заранее образовывать, преформировать (velut praeformata materia <i>Q</i>);\n<b>2)</b> (пред)назначать (aliquem ad malum <i>Tert</i>);\n<b>3)</b> заранее писать (в виде образца) (p. litteras infantibus <i>Q</i>);\n<b>4)</b> поучать (aliquem his dictis <i>Sil</i>)."],
  ["praefortis", "<big>praefortis</big>\n\n<b>prae–fortis, e</b>\nмогучий (homo <i>Tert</i>)."],
  ["praefossus", "<big>praefossus</big>\n\n<b>praefossus, a, um</b> <i>part. pf. к</i>\npraefodio."],
  ["praefoveo", "<big>praefoveo</big>\n\n<b>prae–foveo, —, fotum, ere</b>\nраспаривать, вымачивать (aliquid aqua marina <i>CA</i>)."],
  ["praefracte", "<big>praefracte</big>\n\n<b>praefracte</b> [praefractus]\nнепреклонно, строжайше, упорно (aliquid defendere <i>C</i>; astringere <i>VM</i>)."],
  ["praefractus", "<big>praefractus</big>\n\n<b>praefractus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> praefringo;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> непреклонный, неумолимый, беспощадно строгий <i>C, VM</i>;\n<b>2)</b> отрывистый, резкий по стилю (Thucydides <i>C</i>)."],
  ["praefrico", "<big>praefrico</big>\n\n<b>prae–frico, —, atum, are</b>\nпредварительно растирать (aliquid <i>CA</i>)."],
  ["praefrigidus", "<big>praefrigidus</big>\n\n<b>prae–frigidus, a, um</b>\nочень холодный (Auster <i>O</i>; unda <i>Just</i>)."],
  ["praefringo", "<big>praefringo</big>\n\n<b>prae–fringo, fregi, fractum, ere</b> [prae + frango]\nспереди отламывать (praefracto rostro <i>Cs</i>); раскалывать, разрубать (ligna praefracta <i>Lcr</i>); сверху обломить (hastas <i>L</i>). — <i>См. тж.</i> praefractus."],
  ["praefugio", "<big>praefugio</big>\n\n<b>prae–fugio, fugi, —, ere</b>\nранее убегать <i>Tert</i>."],
  ["praefui", "<big>praefui</big>\n\n<b>praefui</b> <i>pf. к</i>\npraesum."],
  ["praefulcio", "<big>praefulcio</big>\n\n<b>prae–fulcio, fulsi, fultum, ire</b>\n<b>1)</b> использовать в качестве подпоры: aliquem suis negotiis p. <i>Pl</i> опираться на кого-л. в своих делах; omnibus miseriis praefulciri <i>погов. Pl</i> испытать все невзгоды;\n<b>2)</b> подпирать, поддерживать, укреплять (torum <i>Eccl</i>);\n<b>3)</b> подкреплять (aliquid multis modis <i>AG</i>)."],
  ["praefulgeo", "<big>praefulgeo</big>\n\n<b>prae–fulgeo, fulsi, —, ere</b>\n<b>1)</b> ярко блистать, сиять, сверкать (nitor smaragdi praefulget <i>Ph</i>; praefulget dies <i>SenT</i>);\n<b>2)</b> выделяться, отличаться (nomine <i>VP</i>; praefulsit honesti dignitas <i>AG</i>)."],
  ["praefulgidus", "<big>praefulgidus</big>\n\n<b>prae–fulgidus, a, um</b>\nблистающий, сверкающий (nubes caeli <i>Jvc</i>)."],
  ["praefulguro", "<big>praefulguro</big>\n\n<b>prae–fulguro, —, —, are</b>\n<b>1)</b> сверкать, блистать (latus praefulgurat ense <i>St</i>);\n<b>2)</b> наполнять сиянием, освещать (vias stricto ense <i>VF</i>)."],
  ["praefulsi", "<big>praefulsi</big>\n\n<b>praefulsi</b> <i>pf. к</i>\n<b>1)</b> praefulcio <i>и</i>\n<b>2)</b> praefulgeo."],
  ["praefultus", "<big>praefultus</big>\n\n<b>praefultus, a, um</b> <i>part. pf. к</i>\npraefulcio."],
  ["praefundo", "<big>praefundo</big>\n\n<b>prae–fundo, —, fusum, ere</b>\nпредварительно смачивать (aliquid vino <i>CA</i>)."],
  ["praefurnium", "<big>praefurnium</big>\n\n<b>praefurnium, i</b> <i>n</i> [prae + furnus]\n<b>1)</b> топка (<i>печи</i>) <i>Cato</i>;\n<b>2)</b> отопительное отделение, котельная (<i>в банях</i>) <i>Vtr</i>."],
  ["praefuro", "<big>praefuro</big>\n\n<b>prae–furo, —, —, ere</b>\nбушевать, неистовствовать <i>St</i>."],
  ["praefuscus", "<big>praefuscus</big>\n\n<b>prae–fuscus, a, um</b>\nвесьма тёмного цвета (terra <i>Man</i> — <i>v. l.</i>)."],
  ["praegaudeo", "<big>praegaudeo</big>\n\n<b>prae–gaudeo, —, —, ere</b> <i>Sil</i>\n= praegestio."],
  ["praegelidus", "<big>praegelidus</big>\n\n<b>prae–gelidus, a, um</b>\nвесьма холодный (caeli status <i>Col</i>; hiems <i>PM</i>); крайне суровый (frigora <i>Col</i>)."],
  ["praegermino", "<big>praegermino</big>\n\n<b>prae–germino, —, —, are</b>\nпреждевременно прорастать, рано давать побеги <i>PM</i>."],
  ["praegero", "<big>praegero</big>\n\n<b>prae–gero, gessi, gestum, ere</b>\n<b>1)</b> нести впереди, подносить (speculum sub oculis alicujus <i>Ap</i>);\n<b>2)</b> ранее делать: oblivio praegestorum <i>CA</i> забвение прошлых дел."],
  ["praegestio", "<big>praegestio</big>\n\n<b>prae–gestio, —, —, ire</b>\nбурно радоваться, ликовать (praegestit animus aliquem videre <i>C</i>): ludere praegestiens <i>H</i> предающийся весёлым играм."],
  ["praegigno", "<big>praegigno</big>\n\n<b>praegigno, —, —, ere</b> <i>Pl v. l.</i>\n= progigno."],
  ["praegnans", "<big>praegnans</big>\n\n<b>praegnans, antis</b> <i>adj.</i> [prae + gnascor]\n<b>1)</b> беременная (uxor <i>C</i>): bos p. <i>Vr</i> стельная корова; plagae praegnantes <i>шутл. Pl</i> здоровенные удары;\n<b>2)</b> полный (stamine fusus <i>J</i>); переполненный (veneno vipera <i>PM</i>);\n<b>3)</b> набухший (oculi arborum <i>PM</i>); пузатый (cucurbita <i>Col</i>)."],
  ["praegnas", "<big>praegnas</big>\n\n<b>praegnas, atis</b> <i>adj.</i> <i>Pl, Ter etc.</i>\n= praegnans."],
  ["praegnatio", "<big>praegnatio</big>\n\n<b>praegnātio, ōnis</b> <i>f</i>\n<b>1)</b> беременность <i>Ap, Vr</i>;\n<b>2)</b> оплодотворённость (<i>sc.</i> arborum ac frugum <i>Vr</i>);\n<b>3)</b> плодовитость (naturarum omnium <i>Ap</i>)."],
  ["praegnatus", "<big>praegnatus</big>\n\n<b>praegnatus, us</b> <i>m</i> <i>Tert</i>\n= praegnatio."],
  ["praegracilis", "<big>praegracilis</big>\n\n<b>prae–gracilis, e</b>\nвесьма тонкий, стройный (proceritas <i>T</i>)."],
  ["praegrado", "<big>praegrado</big>\n\n<b>prae–grado, —, —, are</b>\nидти впереди <i>Pac</i>."],
  ["praegrandis", "<big>praegrandis</big>\n\n<b>prae–grandis, e</b>\nогромный, громадный (arbores <i>PM</i>; flumen <i>Mela</i>); великий (senex, <i>sc.</i> Aristophanes <i>Pers</i>)."],
  ["praegravidus", "<big>praegravidus</big>\n\n<b>prae–gravidus, a, um</b>\nочень тяжёлый (moles <i>St</i>)."],
  ["praegravis", "<big>praegravis</big>\n\n<b>prae–gravis, e</b>\n<b>1)</b> весьма тяжёлый, увесистый (onus <i>O</i>); перегружённый, отягощенный (cibo vinoque <i>T</i>); тяжеловесный, неуклюжий (p. corpore <i>L</i>);\n<b>2)</b> весьма тягостный, невыносимый (servitium <i>PM</i>);\n<b>3)</b> крайне опасный: praegravem ratus, interficere constituit <i>T</i> считая (Агриппину) весьма опасной, (Нерон) решил умертвить (её)."],
  ["praegravo", "<big>praegravo</big>\n\n<b>prae–gravo, avi, atum, are</b>\n<b>1)</b> сильно обременять, отягощать (aliquem sarcina praegravat <i>Amm</i>; telis praegravari <i>L</i>);\n<b>2)</b> перетягивать, перевешивать, <i>перен.</i> затмевать (artes infra se positas <i>H</i>); свешиваться: praegravantes aures <i>Col</i> свисающие уши;\n<b>3)</b> тяготить, давить, угнетать (animum <i>H</i>);\n<b>4)</b> иметь перевес, превосходить (facta praegravant <i>Su</i>)."],
  ["praegredior", "<big>praegredior</big>\n\n<b>praegredior, gressus sum, gredi</b> [prae + gradior]\n<b>1)</b> идти впереди, предшествовать (sequuntur alii, alii praegrediuntur <i>C</i>): p. aliquem <i>L, Su, J</i> (<i>реже</i> alicui <i>Vr</i>) идти впереди кого-л.;\n<b>2)</b> опережать, обгонять (nuntios <i>L</i>);\n<b>3)</b> превзойти (aliquem <i>Sl</i>);\n<b>4)</b> идти мимо, миновать (castra <i>L</i>)."],
  ["praegressio", "<big>praegressio</big>\n\n<b>praegressio, onis</b> <i>f</i> [praegredior]\n(про)движение вперёд (errantium stellarum <i>C</i>)."],
  ["praegressus", "<big>praegressus</big>\n\n<b>praegressus, us</b> <i>m</i> [praegredior]\nдвижение вперёд, развитие <i>Amm</i>; <i>pl.</i> ступени развития (causae rerum earumque praegressus <i>C</i>)."],
  ["praegubernans", "<big>praegubernans</big>\n\n<b>prae–gubernans, antis</b>\nнаправляющий (ведущий) вперёд <i>Sid</i>."],
  ["praegustator", "<big>praegustator</big>\n\n<b>praegustator, oris</b> <i>m</i> [praegusto]\n<b>1)</b> прегустатор (<i>слуга, пробовавший блюда перед подачей их на стол</i>) <i>Su</i>;\n<b>2)</b> организатор, устроитель (libidinum alicujus <i>C</i>)."],
  ["praegusto", "<big>praegusto</big>\n\n<b>prae–gusto, avi, atum, are</b>\n<b>1)</b> предварительно пробовать, отведывать (cibos <i>O, Sen</i>);\n<b>2)</b> предвкушать (voluptates <i>Sen</i>);\n<b>3)</b> заранее принимать (medicamina <i>J</i>)."],
  ["praegypso", "<big>praegypso</big>\n\n<b>prae–gypso, —, —, are</b>\nспереди обмазывать гипсом (os vasculi <i>CA</i>)."],
  ["praehendo", "<big>praehendo</big>\n\n<b>praehendo</b> <i>v. l.</i>\n= prehendo."],
  ["praehibeo", "<big>praehibeo</big>\n\n<b>praehibeo, hibui, hibitum, ere</b> <i>Pl, Pt</i>\n= praebeo."],
  ["praeiens", "<big>praeiens</big>\n\n<b>praeiens, praeeuntis</b> <i>part. praes. к</i>\npraeeo."],
  ["praeinfundo", "<big>praeinfundo</big>\n\n<b>prae–infundo, —, infusum, ere</b>\nпредварительно вливать (aliquid <i>CA</i>)."],
  ["praeinnuo", "<big>praeinnuo</big>\n\n<b>prae–innuo, —, —, ere</b>\nпредвещать (nimbos <i>Vr</i> — <i>v. l.</i>)."],
  ["praeisti", "<big>praeisti</big>\n\n<b>praeisti</b> <i>2 л. sg. pf. к</i>\npraeeo."],
  ["praejaceo", "<big>praejaceo</big>\n\n<b>prae–jaceo, —, —, ere</b>\nлежать впереди, находиться перед (mare praejacens Asiae — <i>dat. PM</i>): castra (<i>acc.</i>) p. <i>T</i> простираться впереди лагеря."],
  ["praejacio", "<big>praejacio</big>\n\n<b>prae–jacio, (jeci), jactum, ere</b>\nбросать вперёд (moles in gyrum <i>Col</i>)."],
  ["praejudicatum", "<big>praejudicatum</big>\n\n<b>prae–judicatum, i</b> <i>n</i>\n<b>1)</b> <i>L</i> = praejudicium;\n<b>2)</b> предубеждение: postulo, ne quid praejudicati huc afferatis <i>C</i> я прошу вас отнестись к этому без всякого предубеждения."],
  ["praejudicatus", "<big>praejudicatus</big>\n\n<b>praejudicatus, a, um</b>\n<b>1)</b> предрешённый (eventus belli <i>L</i>);\n<b>2)</b> предвзятый (opinio <i>C</i>);\n<b>3)</b> не внушающий никаких сомнений, надёжный (vir praejudicatissimus. <i>Sid</i>)."],
  ["praejudicialis", "<big>praejudicialis</big>\n\n<b>praejudicialis, e</b> [praejudicium]\n<b>1)</b> относящийся к предыдущему судебному решению (actiones <i>L</i>);\n<b>2)</b> налагаемый на основании предыдущего решения (multa <i>CTh</i>);\n<b>3)</b> наносящий ущерб <i>Eccl</i>."],
  ["praejudicium", "<big>praejudicium</big>\n\n<b>prae–judicium, i</b> <i>n</i>\n<b>1)</b> предрешение вопроса, заранее принятое решение (senatus <i>L</i>): sine praejudicio dicere causam <i>QC</i> вести дело, не будучи связанным прежними решениями || предварительный приговор (de aliqua re <i>C</i>): p. afferre tantae rei <i>L</i> наперёд (не вникнув в суть) решить столь важное дело;\n<b>2)</b> обстоятельство, позволяющее судить о последствиях (aliquid pro praejudicio accipere <i>T</i>); повод, предостережение <i>или</i> прецедент (Africi belli praejudicia <i>Cs</i>): aliquem praejudicio juvare <i>PJ</i> создать прецедент в чью-л. пользу;\n<b>3)</b> ущерб (alicui facere <i>Dig</i>; p. in aliquem <i>Sen</i>)."],
  ["praejudico", "<big>praejudico</big>\n\n<b>prae–judico, avi, atum, are</b>\n<b>1)</b> предварительно судить, заранее решать, предрешать (aliquid de aliqua re <i>C</i>): pro praejudicato ferre <i>L</i> считать предрешённым; opinio praejudicata <i>C</i> заранее усвоенное мнение, предубеждение, <i>тж.</i> предрассудок;\n<b>2)</b> причинять ущерб (alicui <i>Dig</i>)."],
  ["praejuvo", "<big>praejuvo</big>\n\n<b>prae–juvo, juvi, —, are</b>\nзаблаговременно помогать, заранее оказывать поддержку (affectam alicujus fidem p. <i>T</i>)."],
  ["praelabor", "<big>praelabor</big>\n\n<b>prae–labor, lapsus sum, labi</b>\nскользить мимо, проплывать (piscis praelabitur <i>C</i>): ira eruditas mentes praelabitur <i>Pt</i> — <i>см.</i> obsideo 2 || протекать мимо (amnis praelabitur <i>Lcn</i>); проходить (tempus praelabens <i>Col</i>); пролетать, мелькать (praelabitur ales <i>Ap</i>)."],
  ["praelambo", "<big>praelambo</big>\n\n<b>prae–lambo, lambi, —, ere</b>\nзаранее облизывать, первым пробовать (aliquid <i>H, Aug</i>): p. arenas <i>Eccl</i> (<i>о реке</i>) плескаться о песчаный берег."],
  ["praelargus", "<big>praelargus</big>\n\n<b>prae–largus, a, um</b>\nвесьма обильный (dapes <i>Jvc</i>); обширный: pulmo animae p. <i>Pers</i> мощные лёгкие."],
  ["praelassatus", "<big>praelassatus</big>\n\n<b>prae–lassatus, a, um</b>\nранее уставший <i>Frontin</i>."],
  ["praelatio", "<big>praelatio</big>\n\n<b>praelatio, onis</b> <i>f</i> [praefero]\nпредпочитание, предпочтение <i>VM, Tert</i>."],
  ["praelator", "<big>praelator</big>\n\n<b>praelator, oris</b> <i>m</i>\nоказывающий предпочтение (p. misericordiae <i>Tert</i>)."],
  ["praelatus", "<big>praelatus</big>\n\n<b>I praelatus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> praefero;\n<b>2.</b> <i>adj.</i> предпочтительный, лучший <i>CA, Tert</i>.\n\n<b>II praelatus, i</b> <i>m</i> [praefero]\nвысший церковнослужитель, прелат <i>Eccl</i>."],
  ["praelautus", "<big>praelautus</big>\n\n<b>prae–lautus, a, um</b>\nлюбящий пышность, изысканный (homines <i>Su</i>)."],
  ["praelavo", "<big>praelavo</big>\n\n<b>prae–lavo, —, —, ere</b>\nпредварительно мыть (os <i>Ap</i>)."],
  ["praelaxatus", "<big>praelaxatus</big>\n\n<b>prae–laxatus, a, um</b>\nс предварительно очищенным желудком (aegrotans <i>CA</i>)."],
  ["praelectio", "<big>praelectio</big>\n\n<b>prae–lectio, onis</b> <i>f</i> [praelego II]\nобъяснительное чтение, чтение вслух (кому-л.), лекция <i>Q</i>."],
  ["praelector", "<big>praelector</big>\n\n<b>praelector, oris</b> <i>m</i> [praelego II]\nчтец, лектор, преподаватель <i>AG</i>."],
  ["praelectus", "<big>praelectus</big>\n\n<b>praelectus, a, um</b> <i>part. pf. к</i>\npraelego II."],
  ["praelego", "<big>praelego</big>\n\n<b>I prae–lego, avi, atum, are</b>\nзаранее и сверх законной доли завещать (alicui aliquid <i>PM, VM, Dig</i>).\n\n<b>II prae–lego, legi, lectum, ere</b>\n<b>1)</b> выбирать, отбирать (hircus praelectus <i>Ap</i>);\n<b>2)</b> ехать мимо, проезжать, проплывать (Campaniam <i>T</i>);\n<b>3)</b> читать (преподавать), сопровождая комментариями (auctores <i>Q</i>)."],
  ["praeliator", "<big>praeliator</big>\n\n<b>praeliator, oris</b> <i>m</i> <i>v. l.</i>\n= proeliator."],
  ["praelibatio", "<big>praelibatio</big>\n\n<b>prae–libatio, onis</b> <i>f</i>\n<b>1)</b> отведывание: p. labiorum <i>Ambr</i> лёгкое прикосновение губами;\n<b>2)</b> умаление, ослабление (sine ulla praelibatione alicujus rei <i>Tert</i>)."],
  ["praelibo", "<big>praelibo</big>\n\n<b>prae–libo, —, —, are</b>\n<b>1)</b> заранее пробовать, отведывать (aliquid <i>St</i>);\n<b>2)</b> рассматривать, наблюдать, испытывать (vultus visu <i>St</i>);\n<b>3)</b> сжато излагать, бегло обозревать (multa de aliqua re <i>Treb</i>)."],
  ["praelicenter", "<big>praelicenter</big>\n\n<b>praelicenter</b>\nслишком свободно, чересчур смело (verba fingere <i>AG</i>)."],
  ["praeliganeus", "<big>praeliganeus</big>\n\n<b>praeliganeus, a, um</b> [praelego II]\nрано собранный: vinum praeliganeum <i>Cato</i> вино из самого незрелого винограда."],
  ["praeligo", "<big>praeligo</big>\n\n<b>prae–ligo, avi, atum, are</b>\n<b>1)</b> спереди привязывать (aliquid alicui rei <i>L</i>);\n<b>2)</b> перевязывать (vulnera <i>PM</i>); повязывать, завязывать (caput aliqua re <i>PM</i>);\n<b>3)</b> закутывать, закрывать (capita vestibus <i>Pt</i>): praeligatum pectus <i>Pl</i> наглухо закрытое, <i>т. е.</i> жестокое сердце."],
  ["praelinitus", "<big>praelinitus</big>\n\n<b>prae–linitus, a, um</b> [linio I]\nпредварительно смазанный (oleo <i>CA</i>)."],
  ["praelino", "<big>praelino</big>\n\n<b>prae–lino, (levi), litum, ere</b>\nмазать спереди (villas tectorio <i>Cato ap. AG</i>)."],
  ["praelior", "<big>praelior</big>\n\n<b>praelior</b> <i>depon.</i> <i>v. l.</i>\n= proelior."],
  ["praeliquatus", "<big>praeliquatus</big>\n\n<b>prae–liquatus, a, um</b> [liquo]\nпредварительно процеженный (liquor <i>CA</i>)."],
  ["praelium", "<big>praelium</big>\n\n<b>praelium, i</b> <i>n</i> <i>v. l.</i>\n= proelium."],
  ["praelocutio", "<big>praelocutio</big>\n\n<b>praelocutio, onis</b> <i>f</i> [praeloquor]\nпредисловие, введение, вступление <i>Sen, Frontin, Aug</i>."],
  ["praelongo", "<big>praelongo</big>\n\n<b>praelongo, avi, —, are</b> [praelongus]\nсильно удлинять, вытягивать (pedum crura <i>PM</i>)."],
  ["praelongus", "<big>praelongus</big>\n\n<b>prae–longus, a, um</b>\nочень длинный (gladius <i>L</i>; sermo <i>Q</i>; homo <i>Q</i>)."],
  ["praeloquor", "<big>praeloquor</big>\n\n<b>prae–loquor, locutus (loquutus) sum, loqui</b>\n<b>1)</b> делать предисловие, выступать со вступительным словом (de aliqua re <i>PJ</i>); предпосылать (aliquid operibus suis <i>Sen</i>);\n<b>2)</b> говорить первым: occupas p., quae mea oratio est <i>Pl</i> ты стараешься раньше сказать то, что сказать должен я."],
  ["praeluceo", "<big>praeluceo</big>\n\n<b>prae–luceo, luxi, —, ere</b>\n<b>1)</b> освещать впереди, идти впереди с огнём (servus praelucens <i>Su</i>); светить впереди (faces praeluxerunt <i>M</i>): bonam spem p. <i>C</i> подавать радостную надежду;\n<b>2)</b> ярко сиять <i>PM</i>;\n<b>3)</b> превосходить, затмевать (p. alicui rei <i>H</i>)."],
  ["praelucidus", "<big>praelucidus</big>\n\n<b>prae–lucidus, a, um</b>\nярко светящий(ся), очень яркий (candor <i>PM</i>)."],
  ["praeludo", "<big>praeludo</big>\n\n<b>prae–ludo, lusi, —, ere</b>\n<b>1)</b> играть предварительно, на пробу (p. tragoediis <i>AG</i>);\n<b>2)</b> делать вступление, предпосылать (aliquid alicui rei <i>St</i>);\n<b>3)</b> готовиться (ad imperium <i>Capit</i>): pugnam p. <i>Rut</i> подготовляться к бою; magnis p. fatis (<i>dat.</i>) <i>Cld</i> класть начало великим делам."],
  ["praelum", "<big>praelum</big>\n\n<b>praelum, i</b> <i>n</i> <i>v. l.</i>\n= prelum."],
  ["praeluminatus", "<big>praeluminatus</big>\n\n<b>prae–luminatus, a, um</b>\nранее объяснённый (parabola <i>Tert</i>)."],
  ["praelusi", "<big>praelusi</big>\n\n<b>praelusi</b> <i>pf. к</i>\npraeludo."],
  ["praelusio", "<big>praelusio</big>\n\n<b>praelusio, onis</b> <i>f</i> [praeludo]\nвступительная часть, прелюдия (p. atque praecursio <i>PJ</i>)."],
  ["praelusorius", "<big>praelusorius</big>\n\n<b>praelusorius, a, um</b> [praeludo]\nслужащий вступлением <i>или</i> началом (pocula <i>Ambr</i>)."],
  ["praelustris", "<big>praelustris</big>\n\n<b>praelustris, e</b> [prae + lustro]\nвесьма видный, блистательный (arx <i>O</i>): praelustria vitare <i>O</i> сторониться всего, что знатно и могущественно."],
  ["praeluxi", "<big>praeluxi</big>\n\n<b>praeluxi</b> <i>pf. к</i>\npraeluceo."],
  ["praemacero", "<big>praemacero</big>\n\n<b>prae–macero, —, —, are</b>\nпредварительно вымачивать (mala <i>Scr</i>)."],
  ["praemaledico", "<big>praemaledico</big>\n\n<b>prae–maledico, —, —, ere</b>\nранее проклясть (aliquem <i>Tert</i>)."],
  ["praemandatum", "<big>praemandatum</big>\n\n<b>praemandatum, i</b> <i>n</i> [praemando I]\nприказ об аресте (aliquem praemandatis requirere <i>C</i>)."],
  ["praemando", "<big>praemando</big>\n\n<b>I prae–mando, avi, atum, are</b>\n<b>1)</b> заранее поручать, наперёд заказывать (aliquem <i>Pl</i>);\n<b>2)</b> отдавать приказ об аресте <i>C</i>;\n<b>3)</b> рекомендовать, выдвигать (hominem <i>Pl</i>).\n\n<b>II prae–mando, —, —, ere</b>\nразжёвывать, <i>перен.</i> подробно объяснять (alicui aliquid <i>AG</i>)."],
  ["praemature", "<big>praemature</big>\n\n<b>praemature</b>\nпреждевременно <i>Pl, AG, Dig</i>."],
  ["praematurus", "<big>praematurus</big>\n\n<b>prae–maturus, a, um</b>\nочень ранний, скороспелый (fructus <i>Col</i>); преждевременный (hiems <i>T</i>); несвоевременный (denuntiatio <i>C</i>): praematurae segetum rapinae <i>Lcn</i> снятие несозревшего урожая || безвременный (mors <i>PM</i>)."],
  ["praemedicatus", "<big>praemedicatus</big>\n\n<b>prae–medicatus, a, um</b>\nзаблаговременно снабжённый (обезопасивший себя) снадобьями, волшебными зельями <i>O, Tert</i>."],
  ["praemeditatio", "<big>praemeditatio</big>\n\n<b>praemeditatio, onis</b> <i>f</i> [praemeditor]\nпредварительное размышление, заблаговременное обдумывание (rerum futurarum <i>C</i>)."],
  ["praemeditatorium", "<big>praemeditatorium</big>\n\n<b>praemeditatorium, i</b> <i>n</i>\nместо подготовки (alicujus rei <i>Tert</i>)."],
  ["praemeditor", "<big>praemeditor</big>\n\n<b>prae–meditor, atus sum, ari</b> <i>depon.</i>\n<b>1)</b> (<i>тж.</i> prius <i>и</i> ante p. <i>L</i>) заранее размышлять, наперёд обдумывать (quo animo accedam ad urbem <i>C</i>); <i>part. pf.</i> praemeditatus <i>act.</i> обдумавший <i>C etc.</i> <i>или</i> <i>pass.</i> обдуманный, предумышленный (praemeditata mala <i>C</i>);\n<b>2)</b> <i>перен.</i> пробовать, репетировать (tentans citharam et praemeditans <i>T</i>)."],
  ["praemercor", "<big>praemercor</big>\n\n<b>prae–mercor, atus sum, ari</b> <i>depon.</i>\nзаранее покупать (aliquam <i>Pl</i>)."],
  ["praemetuenter", "<big>praemetuenter</big>\n\n<b>praemetuenter</b> [praemetuens]\nс большой опаской, <i>т. е.</i> весьма заботливо, крайне тщательно (errorem vitare <i>Lcr</i>)."],
  ["praemetuo", "<big>praemetuo</big>\n\n<b>prae–metuo, —, —, ere</b>\nзаранее бояться, опасаться (iras alicujus <i>V</i>): praemetuens doli (<i>v. l.</i> dolum) <i>Ph</i> опасаясь обмана; p. alicui <i>Cs</i> бояться за кого-л."],
  ["praemialis", "<big>praemialis</big>\n\n<b>praemialis, e</b> [praemium]\nвознаграждающий <i>Aug</i>."],
  ["praemiator", "<big>praemiator</big>\n\n<b>praemiator, oris</b> <i>m</i> [praemium]\n<b>1)</b> грабитель (p. nocturnus <i>Naev</i>);\n<b>2)</b> вознаграждающий <i>Ambr</i>."],
  ["praemiatrix", "<big>praemiatrix</big>\n\n<b>praemiatrix, icis</b> <i>f</i>\nвознаграждающая <i>Amm</i>."],
  ["praemico", "<big>praemico</big>\n\n<b>prae–mico, —, —, are</b>\nярко светить, сверкать (claro lumine <i>Ap</i>)."],
  ["praemigro", "<big>praemigro</big>\n\n<b>prae–migro, —, —, are</b>\nзаранее уходить, заблаговременно выселяться (ruinis imminentibus <i>PM</i>)."],
  ["praemineo", "<big>praemineo</big>\n\n<b>praemineo</b> <i>v. l.</i>\n= praeemineo."],
  ["praeminister", "<big>praeminister</big>\n\n<b>prae–minister, tri</b> <i>m</i>\nслужитель, прислужник <i>Macr, Tert</i>."],
  ["praeministra", "<big>praeministra</big>\n\n<b>prae–ministra, ae</b> <i>f</i>\nприслужница, служанка <i>Ap, Macr</i>."],
  ["praeministro", "<big>praeministro</big>\n\n<b>prae–ministro, —, —, are</b>\n<b>1)</b> прислуживать (alicui <i>AG</i>);\n<b>2)</b> подавать (tibia praeministrat modulos <i>AG</i>); преподносить (fabulas ad destructionem veritatis <i>Tert</i>)."],
  ["praeminor", "<big>praeminor</big>\n\n<b>prae–minor, atus sum, ari</b> <i>depon.</i>\nнаперёд угрожать, сильно грозить (p. malum grande <i>Ap</i>; p. aliquid alicui <i>Tert</i>)."],
  ["praemio", "<big>praemio</big>\n\n<b>praemio, —, —, are</b>\nвознаграждать (aliquem <i>Aug</i>)."],
  ["praemior", "<big>praemior</big>\n\n<b>praemior, —, ari</b> <i>depon.</i>\nуславливаться о вознаграждении (nundinari praemiarique <i>Su</i>)."],
  ["praemiosus", "<big>praemiosus</big>\n\n<b>praemiosus, a, um</b>\nимущий, богатый (dives ac p. <i>Acc</i>)."],
  ["praemisi", "<big>praemisi</big>\n\n<b>praemisi</b> <i>pf. к</i>\npraemitto."],
  ["praemissa", "<big>praemissa</big>\n\n<b>praemissa, orum</b> <i>n</i> [praemitto]\nпервые плоды нового урожая, первинки <i>PM</i>."],
  ["praemissio", "<big>praemissio</big>\n\n<b>praemissio, onis</b> <i>f</i> [praemitto]\nпостановка (помещение) впереди (p. manuum ante oculos <i>CA</i>)."],
  ["praemistus", "<big>praemistus</big>\n\n<b>praemistus</b> <i>v. l.</i>\n= praemixtus."],
  ["praemitis", "<big>praemitis</big>\n\n<b>prae–mitis, e</b>\nчрезвычайно кроткий (asina <i>Jvc</i>)."],
  ["praemitto", "<big>praemitto</big>\n\n<b>prae–mitto, misi, missum, ere</b>\n<b>1)</b> высылать вперёд (aliquem cum equitatu <i>C</i>); заранее (заблаговременно) посылать (legiones in Hispaniam <i>Cs</i>): cogitationes in longinqua p. <i>Sen</i> думать о далёком будущем;\n<b>2)</b> предпосылать, предварять (aliquid oratione <i>T</i>)."],
  ["praemium", "<big>praemium</big>\n\n<b>praemium, i</b> <i>n</i> [prae + emo]\n<b>1)</b> награда, вознаграждение, знак отличия (honores et praemia <i>C</i>; hederae — praemia doctarum frontium <i>H</i>): ad praemia peccare <i>Pt</i> получать награду за свои грехи; praemio aliquem afficere (donare <i>C</i>) <i>или</i> p. alicui tribuere <i>C</i> (dare <i>C</i>, constituere <i>Cs</i>) наградить кого-л.;\n<b>2)</b> добыча (pugnae <i>V</i>; silvestria praemia venatu quaesita <i>VF</i>);\n<b>3)</b> преимущество, отличие <i>C, H</i>;\n<b>4)</b> благосклонность, дар (fortunae <i>C</i>; vitae <i>Lcr</i>; legis <i>C</i>);\n<b>5)</b> доблестный поступок (p. magnum <i>V</i>)."],
  ["praemixtus", "<big>praemixtus</big>\n\n<b>prae–mixtus, a, um</b>\nпредварительно перемешанный <i>CA</i>."],
  ["praemoderor", "<big>praemoderor</big>\n\n<b>prae–moderor, —, ari</b> <i>depon.</i>\nустанавливать меру, отбивать такт: proelia ingredi praemoderante cithara gressibus <i>AG</i> идти на бой в ногу под звуки кифары."],
  ["praemodulor", "<big>praemodulor</big>\n\n<b>prae–modulor, —, ari</b>\nзаранее соразмерять (gestum cogitatione <i>Q</i>)."],
  ["praemodum", "<big>praemodum</big>\n\n<b>prae–modum</b> <i>adv.</i>\nсверх меры <i>LA ap. AG</i>."],
  ["praemoenio", "<big>praemoenio</big>\n\n<b>praemoenio</b> <i>v. l.</i>\n= praemunio."],
  ["praemolestia", "<big>praemolestia</big>\n\n<b>prae–molestia, ae</b> <i>f</i>\nтяжёлое предчувствие, преждевременное опасение <i>C</i>."],
  ["praemolior", "<big>praemolior</big>\n\n<b>prae–molior, —, iri</b> <i>depon.</i>\nзаранее подготовлять, приводить в порядок (praemoliendae res <i>L</i>)."],
  ["praemollio", "<big>praemollio</big>\n\n<b>prae–mollio, (ivi), itum, ire</b>\n<b>1)</b> предварительно размягчать (sulcum <i>Q</i>; aliquid vino <i>CA</i>);\n<b>2)</b> предварительно смягчать (mentes judicum <i>Q</i>)."],
  ["praemollis", "<big>praemollis</big>\n\n<b>prae–mollis, e</b>\n<b>1)</b> очень мягкий (ova <i>PM</i>);\n<b>2)</b> вялый, немощный (in carminibus permolle <i>Q</i>)."],
  ["praemoneo", "<big>praemoneo</big>\n\n<b>prae–moneo, monui, monitum, ere</b>\n<b>1)</b> заранее напоминать, обращать внимание, указывать (p. aliquem de aliqua re <i>C etc.</i> <i>или</i> aliquid <i>Col, L etc.</i>);\n<b>2)</b> предостерегать, увещевать (aliquem ne aliquid faciat <i>Col, Just</i>): praemonenda p. <i>Ap</i> сделать необходимые предостережения;\n<b>3)</b> предсказывать, предвещать (futura <i>Just</i>; nefas <i>O</i>)."],
  ["praemonitio", "<big>praemonitio</big>\n\n<b>praemonitio, onis</b> <i>f</i> <i>Tert</i>\n= praemonitum."],
  ["praemonitor", "<big>praemonitor</big>\n\n<b>praemonitor, oris</b> <i>m</i> [praemoneo]\nнапоминающий, делающий предостережение <i>Ap</i>."],
  ["praemonitorius", "<big>praemonitorius</big>\n\n<b>praemonitorius, a, um</b>\nпредостерегающий (edictum <i>Tert</i>)."],
  ["praemonitum", "<big>praemonitum</big>\n\n<b>praemonitum, i</b> <i>n</i> [praemoneo]\nнапоминание, предостережение <i>AG</i>."],
  ["praemonitus", "<big>praemonitus</big>\n\n<b>praemonitus, us</b> <i>m</i> [praemoneo]\nнапоминание, предостережение (deorum <i>O, VM</i>)."],
  ["praemonstratio", "<big>praemonstratio</big>\n\n<b>praemonstratio, onis</b> <i>f</i>\nзаблаговременное указание, предостережение <i>Lact</i>."],
  ["praemonstrator", "<big>praemonstrator</big>\n\n<b>praemonstrator, oris</b> <i>m</i> [praemonstro]\nуказывающий путь, советчик (monitor et p. <i>Ter</i>)."],
  ["praemonstro", "<big>praemonstro</big>\n\n<b>prae–monstro, avi, atum, are</b>\n<b>1)</b> (<i>тж.</i> p. viam <i>Lcr</i>) показывать дорогу, быть проводником (currenti spatium p. <i>Lcr</i>);\n<b>2)</b> руководить: te praemonstrante <i>St</i> под твоим руководством;\n<b>3)</b> предупреждать, предостерегать (alicui <i>C</i>);\n<b>4)</b> предсказывать, пророчить (aliquid <i>C, PM</i>)."],
  ["praemordeo", "<big>praemordeo</big>\n\n<b>prae–mordeo, mordī (morsī), morsum, ēre</b>\n<b>1)</b> кусать спереди (linguam <i>Lcn</i>);\n<b>2)</b> откусывать, <i>перен.</i> урывать, присваивать себе (aliquid ex aliqua re <i>J</i>)."],
  ["praemordicus", "<big>praemordicus</big>\n\n<b>prae–mordicus, a, um</b>\nсо съеденным (откушенным) кончиком (olera <i>Hier</i>)."],
  ["praemorior", "<big>praemorior</big>\n\n<b>prae–morior, mortuus sum, morī</b> <i>depon.</i>\n<b>1)</b> преждевременно умирать (ego praemoriar primoque exstinguar in aevo <i>O</i>); отмирать (membra praemortua <i>O</i>);\n<b>2)</b> <i>перен.</i> исчезать, пропадать: praemortui pudoris esse <i>L</i> утратить всякий стыд."],
  ["praemorsi", "<big>praemorsi</big>\n\n<b>praemorsī</b> <i>pf. к</i>\npraemordeo."],
  ["praemostro", "<big>praemostro</big>\n\n<b>praemostro</b> <i>Pl</i>\n= praemonstro."],
  ["praemoveo", "<big>praemoveo</big>\n\n<b>prae–moveo, —, motum, ere</b>\nдвигаться вперёд <i>CA</i>."],
  ["praemulceo", "<big>praemulceo</big>\n\n<b>prae–mulceo, —, mulsum, ere</b>\nзачёсывать вперёд (praemulsae antiae <i>Ap</i>)."],
  ["praemunio", "<big>praemunio</big>\n\n<b>prae–munio, ivi, itum, ire</b>\n<b>1)</b> укреплять спереди, защищать с передней стороны (aditus magnis operibus <i>Cs</i>);\n<b>2)</b> предохранять, обеспечивать (medicamentis metu venenorum praemuniri <i>Su</i>);\n<b>3)</b> предпосылать, заранее приводить в доказательство (aliquid reliquo sermoni <i>C</i>)."],
  ["praemunitio", "<big>praemunitio</big>\n\n<b>praemunitio, onis</b> <i>f</i> [praemunio]\n<b>1)</b> оплот, защита <i>Ambr</i>;\n<b>2)</b> <i>ритор.</i> подготовка слушателей, предисловие, обеспечивающее благосклонность слушателей (p. orationis <i>C, Q</i>)."],
  ["praenarro", "<big>praenarro</big>\n\n<b>prae–narro, avi, —, are</b>\nзаранее рассказывать (aliquid <i>Ter</i>)."],
  ["praenascor", "<big>praenascor</big>\n\n<b>prae–nascor, —, nasci</b>\nранее родиться <i>Aug</i> <i>или</i> возникнуть (in mente <i>Aug</i>)."],
  ["praenato", "<big>praenato</big>\n\n<b>prae–nato, —, —, are</b>\nплыть вперёд <i>PM, St</i>; плавать, проплывать перед <i>или</i> мимо (praenatans pisciculus <i>PM</i>); протекать мимо (amnis domos praenatat <i>V</i>)."],
  ["praenavigatio", "<big>praenavigatio</big>\n\n<b>prae–navigatio, onis</b> <i>f</i>\nпроплывание мимо <i>PM</i>."],
  ["praenavigo", "<big>praenavigo</big>\n\n<b>prae–navigo, avi, atum, are</b>\nплыть вдоль, проплывать мимо (litus <i>VF</i>): p. vitam <i>Sen</i> плыть по реке жизни; praenavigantes <i>Sen</i> плывущие мимо мореплаватели."],
  ["praendo", "<big>praendo</big>\n\n<b>praendo</b> <i>v. l.</i>\n= prehendo."],
  ["praenego", "<big>praenego</big>\n\n<b>prae–nego, —, —, are</b>\nзаранее отрицать (aliquid <i>Boet</i>)."],
  ["Praeneste", "<big>Praeneste</big>\n\n<b>Praeneste, is</b> <i>n</i> <i>и</i> <i>f</i>\nПренеста, <i>древний город в Латии, к вост. от Рима, с храмами Фортуны и Юноны</i> (<i>ныне</i> Palestrina) <i>Vr, C, Prp, V, H</i>."],
  ["Praenestinus", "<big>Praenestinus</big>\n\n<b>I Praenestinus, a, um</b> [Praeneste]\nпренестинский (rosae <i>PM</i>; nuces <i>Cato</i>): Praenestinae sortes <i>C</i> оракулы Пренестинского святилища.\n\n<b>II Praenestinus, i</b> <i>m</i>\nжитель города Praeneste <i>Pl, C etc.</i>"],
  ["praenexus", "<big>praenexus</big>\n\n<b>prae–nexus, a, um</b>\nзавязанный спереди (os <i>Sol</i>)."],
  ["praenimis", "<big>praenimis</big>\n\n<b>prae–nimis</b>\nслишком, чересчур <i>AG</i>."],
  ["praeniteo", "<big>praeniteo</big>\n\n<b>prae–niteo, nitui, —, ere</b>\n<b>1)</b> ярко сиять (luna praenitet <i>PM</i>); ослепительно сверкать (nivea veste <i>Ap</i>);\n<b>2)</b> выделяться, блистать (virtus Catonis praenitens <i>VP</i>);\n<b>3)</b> затмевать, превосходить (alicui <i>H, SenT</i>)."],
  ["praenobilis", "<big>praenobilis</big>\n\n<b>prae–nobilis, e</b>\nвесьма славный, прославленный (potio <i>Ap</i>): praenobilior proprio ingenio, quam patricio consulatu <i>Ap</i> (Эмилиан Страбон), более славный своими личными дарованиями, чем званиями патриция и консула."],
  ["praenomen", "<big>praenomen</big>\n\n<b>prae–nomen, inis</b> <i>n</i>\n<b>1)</b> личное имя, <i>ставящееся впереди nomen и обозначающееся обыкновенно инициалом</i>: M. (Marcus), P. (Publius) <i>и т. д.</i> <i>C, L etc.</i>;\n<b>2)</b> звание, титул (p. imperatoris <i>Su</i>)."],
  ["praenomino", "<big>praenomino</big>\n\n<b>praenomino, —, atum, are</b> [praenomen]\nдавать личное имя <i>или</i> титул <i>Vr</i>."],
  ["praenosco", "<big>praenosco</big>\n\n<b>prae–nosco, novi, notum, ere</b>\nнаперёд узнавать (futura <i>C</i>); предугадывать, предчувствовать (ver praenosci potest <i>PM</i>)."],
  ["praenosse", "<big>praenosse</big>\n\n<b>praenosse</b> <i>St inf. pf. к</i>\npraenosco."],
  ["praenotio", "<big>praenotio</big>\n\n<b>praenotio, onis</b> <i>f</i> [praenosco]\nзаранее сложившееся <i>или</i> (<i>греч.</i> prolepsis) «врождённое» понятие <i>C, Boet</i>."],
  ["praenoto", "<big>praenoto</big>\n\n<b>prae–noto, avi, atum, are</b>\n<b>1)</b> отмечать, помечать спереди (librum litteris <i>Ap</i>);\n<b>2)</b> озаглавливать (librum <i>C</i>);\n<b>3)</b> запечатывать (aliquid anulo <i>Ap</i>);\n<b>4)</b> предсказывать, предвещать (adventum alicujus <i>Tert</i>);\n<b>5)</b> записывать, набрасывать (fabellam <i>Ap</i>)."],
  ["praenubilus", "<big>praenubilus</big>\n\n<b>prae–nubilus, a, um</b>\nочень пасмурный, мрачный (p. densa arbore lucus <i>O</i>)."],
  ["praenum", "<big>praenum</big>\n\n<b>praenum, i</b> <i>n</i>\nчесальный гребень, <i>тж.</i> орудие пытки <i>Tert</i>."],
  ["praenuntia", "<big>praenuntia</big>\n\n<b>prae–nuntia, ae</b> <i>f</i>\nпредвестница (stella p. calamitatum <i>C</i>; praenuntiae flagitii blanditiae <i>T</i>)."],
  ["praenuntiatio", "<big>praenuntiatio</big>\n\n<b>praenuntiatio, onis</b> <i>f</i>\nпредвещание, предсказание <i>Eccl</i>."],
  ["praenuntiativus", "<big>praenuntiativus</big>\n\n<b>praenuntiativus, a, um</b>\nпредвещающий, пророческий <i>Aug, Is</i>."],
  ["praenuntiator", "<big>praenuntiator</big>\n\n<b>praenuntiator, oris</b>\nот провозвестник <i>Aug</i>."],
  ["praenuntiatrix", "<big>praenuntiatrix</big>\n\n<b>praenuntiatrix, icis</b> <i>f</i>\nпровозвестница <i>Eccl</i>."],
  ["praenuntio", "<big>praenuntio</big>\n\n<b>prae–nuntio, avi, atum, are</b>\nзаранее извещать (adventum <i>bAfr</i> <i>или</i> de adventu <i>Nep</i>); предсказывать, предвещать (tempestates <i>PM</i>; futura <i>C</i>)."],
  ["praenuntium", "<big>praenuntium</big>\n\n<b>praenuntium, i</b> <i>n</i> <i>PM, Sen</i>\n= praenuntius II."],
  ["praenuntius", "<big>praenuntius</big>\n\n<b>I prae–nuntius, a, um</b>\nвозвещающий, сигнализирующий, сигнальный (ignes <i>PM</i>).\n\n<b>II prae–nuntius, i</b> <i>m</i>\nпредвестник (ales p. lucis <i>O</i>)."],
  ["praeobturo", "<big>praeobturo</big>\n\n<b>prae–obturo, —, —, are</b>\nзатыкать спереди <i>Vtr</i>."],
  ["praeoccido", "<big>praeoccido</big>\n\n<b>prae–occido, —, —, ere</b>\nранее заходить (alicui sideri <i>PM</i>)."],
  ["praeoccupatio", "<big>praeoccupatio</big>\n\n<b>praeoccupatio, onis</b> <i>f</i> [praeoccupo]\n<b>1)</b> заблаговременное занятие (locorum <i>Nep</i>);\n<b>2)</b> болезненное вздутие желудка (<i>у животных</i>) <i>Veg</i>;\n<b>3)</b> <i>ритор. Eccl</i> = prolepsis."],
  ["praeoccupo", "<big>praeoccupo</big>\n\n<b>prae–occupo, avi, atum, are</b>\n<b>1)</b> заранее занимать, первым овладевать, наперёд захватывать (loca opportuna <i>L</i>);\n<b>2)</b> охватывать (animos timor praeoccupavit <i>Cs</i>);\n<b>3)</b> заранее заручиться (gratiam apud — <i>v. l.</i> ad — plebem <i>L</i>);\n<b>4)</b> заранее (ещё раньше) связывать (aliquem legatione <i>Cs</i>);\n<b>5)</b> предвосхищать, предупреждать, опережать (aliquem <i>Nep etc.</i>): p. legem ferre <i>L</i> опередить кого-л. с внесением законопроекта."],
  ["praeoleo", "<big>praeoleo</big>\n\n<b>praeoleo, —, —, ere</b> <i>Fronto</i>\n= praeolo."],
  ["praeolo", "<big>praeolo</big>\n\n<b>prae–olo, —, —, ere</b>\nиздали пахнуть, <i>перен.</i> внушать предчувствие: praeolit mihi <i>Pl</i> я предчувствую."],
  ["praeopimus", "<big>praeopimus</big>\n\n<b>prae–opimus, a, um</b>\nочень жирный (vitulus <i>Tert</i>)."],
  ["praeopinor", "<big>praeopinor</big>\n\n<b>prae–opinor, —, ari</b>\nзаранее предполагать <i>Boet</i>."],
  ["praeopto", "<big>praeopto</big>\n\n<b>prae–opto, avi, atum, are</b>\nбольше желать (aliquid quam aliquid <i>L</i>); предпочитать (aliquid alicui rei <i>Nep, L</i> <i>и</i> aliquid pro aliqua re <i>Ap</i>)."],
  ["praeordino", "<big>praeordino</big>\n\n<b>prae–ordino, avi, atum, are</b>\nпредопределять, предназначать (aliquem ut faciat aliquid <i>Vlg</i>; praeordinatae causae <i>CA</i>)."],
  ["praeostendo", "<big>praeostendo</big>\n\n<b>prae–ostendo, (ostendi), ostensum, ere</b>\nпредуказывать, указывать заранее (ostendere et p. <i>Aug</i>)."],
  ["praepando", "<big>praepando</big>\n\n<b>prae–pando, —, —, ere</b>\n<b>1)</b> распростирать, раскрывать, располагать спереди (vestibula <i>PM</i>): p. clara lumina menti alicujus <i>Lcr</i> озарить чей-л. ум ярким светом;\n<b>2)</b> возвещать (hiberni temporis ortus <i>C</i>)."],
  ["praeparatio", "<big>praeparatio</big>\n\n<b>praeparatio, onis</b> <i>f</i> [praeparo]\nприготовление, подготовка (alicujus rei <i>VP</i> <i>или</i> ad aliquid <i>C</i>)."],
  ["praeparato", "<big>praeparato</big>\n\n<b>prae–parato</b> (<i>тж.</i> ex p. <i>Sen</i>)\nс должной подготовкой, приготовившись <i>Q</i>."],
  ["praeparator", "<big>praeparator</big>\n\n<b>praeparator, oris</b> <i>m</i>\nподготовитель (viarum domini <i>Tert</i>)."],
  ["praeparatorius", "<big>praeparatorius</big>\n\n<b>praeparatorius, a, um</b>\nподготовительный (interdictum <i>Dig</i>)."],
  ["praeparatura", "<big>praeparatura</big>\n\n<b>praeparatura, ae</b> <i>f</i> <i>Tert</i>\n= praeparatio."],
  ["praeparatus", "<big>praeparatus</big>\n\n<b>praeparatus, us</b> <i>m</i> <i>VP, AG</i>\n= praeparatio."],
  ["praeparcus", "<big>praeparcus</big>\n\n<b>prae–parcus, a, um</b>\nкрайне скупой <i>PM, Is</i>."],
  ["praeparo", "<big>praeparo</big>\n\n<b>prae–paro, avi, atum, are</b>\nготовить, снаряжать (naves <i>L</i>); подготовлять (bellum <i>Veg</i>): p. profectionem <i>Su</i> готовиться к отъезду; bene praeparatum pectus <i>H</i> ко всему готовый дух; praeparato <i>Q</i> <i>и</i> ex praeparato <i>Sen</i> подготовившись; ex ante praeparato <i>L</i> из резерва."],
  ["praeparvus", "<big>praeparvus</big>\n\n<b>prae–parvus, a, um</b>\nвесьма малый, крошечный <i>Jvc</i>."],
  ["praepatior", "<big>praepatior</big>\n\n<b>prae–patior, passus sum, pati</b>\nсильно страдать <i>CA</i>."],
  ["praepedimentum", "<big>praepedimentum</big>\n\n<b>praepedimentum, i</b> <i>n</i> [praepedio]\nпрепятствие, помеха <i>Pl, Sid</i>."],
  ["praepedio", "<big>praepedio</big>\n\n<b>prae–pedio, ivi (ii), itum, ire</b> [prae + pes]\n<b>1)</b> связывать (aliquem <i>Pl</i>); спутывать, стреноживать (equum <i>T</i>);\n<b>2)</b> задерживать, мешать, препятствовать (fugam hostium <i>L</i>): praepeditus morbo <i>C</i> скованный болезнью; verba sua praepediens <i>T</i> заикаясь, запинаясь."],
  ["praependeo", "<big>praependeo</big>\n\n<b>prae–pendeo, pendi, —, ere</b>\nвисеть впереди, свешиваться вперёд, свисать (tegimenta praependent ad defendendos ictus <i>Cs</i>; praependent demissae sertae <i>Prp</i>)."],
  ["praepes", "<big>praepes</big>\n\n<b>I praepes, petis</b> (<i>abl.</i> e <i>или</i> i, <i>gen. pl.</i> um) <i>adj.</i> [prae + peto]\n<b>1)</b> стремительно летящий (cursus <i>St, Cld</i>); быстрый (volatus <i>PM</i>); крылатый (deus <i>O</i>);\n<b>2)</b> <i>культ.</i> сулящий счастье, благоприятный, счастливый (penna <i>V</i>; avis <i>C, L</i>).\n\n<b>II praepes, petis</b> <i>m</i>\nптица: p. Jovis <i>V</i> = aquila; p. Medusaeus <i>O</i> = Pegasus."],
  ["praepigneratus", "<big>praepigneratus</big>\n\n<b>prae–pigneratus, a, um</b>\nзаранее обязавшийся (homo <i>Amm</i>)."],
  ["praepilatus", "<big>praepilatus</big>\n\n<b>I praepilatus, a, um</b> [prae + pila III]\nс набалдашником впереди (на конце) (hasta <i>PM</i>; pilum <i>bAfr</i>); спереди закруглённый, с вздутием на конце (locustarum cornua <i>PM</i>).\n\n<b>II prae–pilatus, a, um</b> [pilum]\nзаострённый спереди, остроконечный (missilia <i>Amm</i>)."],
  ["praepinguis", "<big>praepinguis</big>\n\n<b>prae–pinguis, e</b>\n<b>1)</b> очень жирный, весьма тучный (solum <i>V</i>; sues <i>PM</i>);\n<b>2)</b> слишком грубый <i>или</i> низкий (vox <i>Q</i>)."],
  ["praepollens", "<big>praepollens</big>\n\n<b>praepollens, entis</b>\n<b>1.</b> <i>part. praes. к</i> praepolleo;\n<b>2.</b> <i>adj.</i> могучий, могущественный <i>Aug</i>."],
  ["praepolleo", "<big>praepolleo</big>\n\n<b>prae–polleo, pollui, —, ere</b>\nбыть сильнее других, быть могущественным, преобладать (Phoenices mari praepollebant <i>T</i>): p. aliqua re <i>Ap, Macr</i> отличаться в чём-л. <i>или</i> чём-л.; praepollens virtute <i>L</i> весьма доблестный."],
  ["praepondero", "<big>praepondero</big>\n\n<b>prae–pondero, avi, atum, are</b>\n<b>1)</b> быть тяжелее, перевешивать, перетягивать (in alterum latus <i>Ap</i>);\n<b>2)</b> иметь перевес, брать верх (honestas praeponderabit <i>AG</i>);\n<b>3)</b> склоняться (in neutram partem <i>Vr</i>)."],
  ["praepono", "<big>praepono</big>\n\n<b>prae–pono, posui, positum, ere</b>\n<b>1)</b> класть <i>или</i> расстилать впереди (toralia toris <i>Pt</i>); ставить впереди (aliquid alicui rei <i>T</i>): pauca p. <i>C</i> сделать несколько предварительных замечаний || предпосылать (causis principia <i>C</i>): ultima primis p. <i>H</i> поставить последнее впереди первого; olivam fronti p. <i>H</i> увенчать чело оливой || <i>pass.</i> praeponi предшествовать: praepositus <i>C etc.</i> вышеупомянутый, предшествующий;\n<b>2)</b> ставить во главе, назначать начальником (p. aliquem navibus <i>C</i>; p. aliquem media acie <i>Cs</i>): p. aliquem alicui rei custodiae causa <i>Pt</i> поручить кому-л. охрану чего-л.; praepositum esse <i>C</i> быть начальником, командовать;\n<b>3)</b> ставить выше, предпочитать (aliquem alicui <i>Ter</i>; aliquid alicui rei <i>Pl</i>): p. utilibus rectum <i>Cld</i> предпочитать праведное полезному."],
  ["praeporto", "<big>praeporto</big>\n\n<b>prae–porto, —, —, are</b>\n<b>1)</b> нести перед собой, носить впереди (tela <i>Lcr</i>);\n<b>2)</b> явно выражать, не скрывать (pectoris iras <i>Ctl</i>)."],
  ["praeposita", "<big>praeposita</big>\n\n<b>praeposita, ae</b> <i>f</i> [praepositus II]\nнастоятельница монастыря, аббатиса <i>Aug</i>."],
  ["praepositio", "<big>praepositio</big>\n\n<b>praepositio, onis</b> <i>f</i> [praepono]\n<b>1)</b> постановка впереди (negationis <i>C</i>): p. diei et consulis <i>Dig</i> указание в начале (документа) дня и консула, <i>т. е.</i> числа и года;\n<b>2)</b> назначение начальником <i>Dig</i>;\n<b>3)</b> предпочтительность, преимущество (tali jacti <i>C</i>);\n<b>4)</b> <i>грам.</i> предлог <i>Vr, C, Su</i>;\n<b>5)</b> <i>грам.</i> префиксация <i>C, Q</i>."],
  ["praepositivus", "<big>praepositivus</big>\n\n<b>praepositivus, a, um</b> [praepono] <i>грам.</i>\nпрепозитивный, ставящийся впереди (coniunctio)."],
  ["praepositum", "<big>praepositum</big>\n\n<b>praepositum, i</b> <i>n</i> [praepono] (<i>греч.</i> proegmenon)\nпредпочтительное, <i>т. е.</i> относительное благо (<i>в стоической философии</i>) <i>C</i>."],
  ["praepositura", "<big>praepositura</big>\n\n<b>praepositura, ae</b> <i>f</i> [praepositus II]\nкомандная должность, руководящий пост <i>Lampr, Capit, CJ</i>."],
  ["praepositus", "<big>praepositus</big>\n\n<b>I praepositus, a, um</b> <i>part. pf. к</i>\npraepono.\n\n<b>II praepositus, i</b> <i>m</i>\n<b>1)</b> начальник, заведующий, глава (alicujus rei <i>PJ etc.</i>);\n<b>2)</b> командир <i>T</i>;\n<b>3)</b> наместник (в провинции) <i>Su</i>;\n<b>4)</b> настоятель монастыря <i>Eccl</i>."],
  ["praeposivi", "<big>praeposivi</big>\n\n<b>praeposivi</b>\n<i>Pl</i> = praeposui (<i>pf. к</i> praepono)."],
  ["praepossum", "<big>praepossum</big>\n\n<b>prae–possum, potui, posse</b>\nбыть более могущественным, получать перевес, одерживать верх (Macedones praepotuerunt <i>T</i>)."],
  ["praeposteratio", "<big>praeposteratio</big>\n\n<b>praeposteratio, onis</b> <i>f</i>\nобратный порядок <i>Aug</i>."],
  ["praepostere", "<big>praepostere</big>\n\n<b>praepostere</b> <i>adv.</i> [praeposterus]\n<b>1)</b> наоборот, навыворот: laevus calceus p. inductus <i>PM</i> левый сапог, надетый на правую ногу;\n<b>2)</b> превратно (p. atque inscite syllogismo uti <i>AG</i>);\n<b>3)</b> неправильно (agere cum aliquo <i>C</i>)."],
  ["praepostero", "<big>praepostero</big>\n\n<b>I praepostero</b> <i>adv.</i> <i>Sen</i>\n= praepostere.\n\n<b>II prae–postero, —, —, are</b>\nставить вверх дном, опрокидывать (ordinem <i>Q</i>)."],
  ["praeposterus", "<big>praeposterus</big>\n\n<b>prae–posterus, a, um</b>\n<b>1)</b> превратный, неправильный (ordo <i>Lcr</i>): naturae p. legibus (<i>dat.</i>) <i>O</i> идущий наперекор законам природы;\n<b>2)</b> несвоевременный (frigus <i>Sen</i>): praeposteris consiliis uti <i>C</i> быть крепким задним умом;\n<b>3)</b> нелепо поступающий, бестолковый (homo <i>C</i>)."],
  ["praepostus", "<big>praepostus</big>\n\n<b>praepostus</b> <i>Lcr</i>\n= praepositus (<i>part. pf. к</i> praepono)."],
  ["praeposui", "<big>praeposui</big>\n\n<b>praeposui</b> <i>pf. к</i>\npraepono."],
  ["praepotens", "<big>praepotens</big>\n\n<b>prae–potens, entis</b> (<i>abl.</i> e, <i>gen. pl.</i> ium, <i>реже</i> um)\n<b>1.</b> <i>part. praes. к</i> praepossum;\n<b>2.</b> <i>adj.</i> могучий, могущественный (Carthago p. terra marique <i>C</i>; legibus praepotentes non alligantur <i>VM</i>)."],
  ["praepotentia", "<big>praepotentia</big>\n\n<b>prae–potentia, ae</b> <i>f</i>\nмогущество, великая мощь <i>Ap, Tert</i>."],
  ["praepoto", "<big>praepoto</big>\n\n<b>prae–poto, —, —, are</b>\n<b>1)</b> сначала пить (aliquid <i>CA</i>);\n<b>2)</b> сначала поить (aegrotantes aliqua re <i>CA</i>)."],
  ["praeproperanter", "<big>praeproperanter</big>\n\n<b>praeproperanter</b> <i>Lcr</i>\n= praepropere."],
  ["praepropere", "<big>praepropere</big>\n\n<b>praepropere</b> [praeproperus]\nкрайне поспешно, с чрезмерной торопливостью <i>L, Q</i>."],
  ["praeproperus", "<big>praeproperus</big>\n\n<b>prae–properus, a, um</b>\nчрезвычайно поспешный, слишком торопливый, стремительный (festinatio <i>C</i>); опрометчивый (ingenium <i>L</i>)."],
  ["praepulcher", "<big>praepulcher</big>\n\n<b>prae–pulcher, chra, chrum</b>\nпрекраснейший <i>Jvc</i>."],
  ["praepurgo", "<big>praepurgo</big>\n\n<b>prae–purgo, —, —, are</b>\nпредварительно прочищать (aegros <i>CA</i>)."],
  ["praeputiatio", "<big>praeputiatio</big>\n\n<b>praeputiatio, onis</b> <i>f</i> [praeputium]\nнеобрезанность <i>Tert</i>."],
  ["praeputiatus", "<big>praeputiatus</big>\n\n<b>praeputiatus, a, um</b>\nнеобрезанный <i>Tert</i>."],
  ["praeputium", "<big>praeputium</big>\n\n<b>praeputium, i</b> <i>n</i> <i>анат.</i>\nкрайняя плоть <i>Sen, J, Vlg, Eccl</i>."],
  ["praequam", "<big>praequam</big>\n\n<b>prae–quam</b> (<i>тж. раздельно</i>)\n— <i>см.</i> prae I."],
  ["praequeror", "<big>praequeror</big>\n\n<b>prae–queror, questus sum, queri</b>\nгорько жаловаться <i>O</i>."],
  ["praeradio", "<big>praeradio</big>\n\n<b>prae–radio, —, —, are</b>\n<b>1)</b> ярко блистать (pilenta praeradiant ductura nurum <i>Cld</i>);\n<b>2)</b> превосходить блеском, затмевать (stellis suis signa minora <i>O</i>)."],
  ["praerancidus", "<big>praerancidus</big>\n\n<b>prae–rancidus, a, um</b>\nвесьма зловонный, <i>перен.</i> крайне неприятный <i>AG</i>."],
  ["praerapidus", "<big>praerapidus</big>\n\n<b>prae–rapidus, a, um</b>\nочень быстрый, стремительный (gurges <i>L</i>; celeritas <i>Sen</i>); весьма пылкий, порывистый (ira <i>Sen</i>; juvenis <i>Sil</i>)."],
  ["praerasus", "<big>praerasus</big>\n\n<b>prae–rasus, a, um</b> [rado]\nпредварительно сбритый (capilli <i>CA</i>)."],
  ["praereptor", "<big>praereptor</big>\n\n<b>praereptor, oris</b> <i>m</i> [praeripio]\nзахватчик, похититель <i>Hier</i>."],
  ["praerigesco", "<big>praerigesco</big>\n\n<b>(prae–rigesco), rigui, —, ere</b>\nсовершенно закоченеть (manus praeriguerunt <i>T</i>)."],
  ["praerigidus", "<big>praerigidus</big>\n\n<b>prae–rigidus, a, um</b>\nокоченевший, застывший, неподвижный (mens <i>Q</i>)."],
  ["praeripia", "<big>praeripia</big>\n\n<b>praeripia, orum</b> <i>n</i> [ripa]\nберега (fluminis <i>Ap</i>)."],
  ["praeripio", "<big>praeripio</big>\n\n<b>praeripio, ripui, reptum, ere</b> [prae + rapio]\n<b>1)</b> перехватывать, урывать, похищать, отнимать <i>Pl, V, Cs etc.</i>: p. aliquid alicui <i>C etc.</i> отнять у кого-л. что-л.; p. aliquid festinatione <i>C</i> преждевременно присвоить себе; p. oscula <i>Lcr</i> наперебой целовать (кого-л.); brevibus praereptus in annis <i>VM</i> безвременно умерший;\n<b>2)</b> предвосхищать, упреждать, <i>т. е.</i> расстраивать, срывать (hostium consilia <i>C</i>); забегать вперёд (non praeripiam <i>C</i>): se p. <i>Dig</i> заблаговременно ускользнуть."],
  ["praeroboratus", "<big>praeroboratus</big>\n\n<b>prae–roboratus, a, um</b>\nпредварительно укреплённый (corpus <i>CA</i>)."],
  ["praerodo", "<big>praerodo</big>\n\n<b>prae–rodo, rosi, rosum, ere</b>\nобгрызать спереди (digitos <i>Pl</i>); отгрызать, откусывать (caudam <i>PM</i>): p. hamum <i>H</i> объедать приманку с (рыболовного) крючка."],
  ["praerogantia", "<big>praerogantia</big>\n\n<b>praerogantia, ae</b> <i>f</i>\nплатёж вперёд <i>Aug</i>."],
  ["praerogatio", "<big>praerogatio</big>\n\n<b>praerogatio, onis</b> <i>f</i>\n<b>1)</b> преимущественное право <i>Sen</i>;\n<b>2)</b> заблаговременное распределение (annonae <i>Eccl</i>)."],
  ["praerogativa", "<big>praerogativa</big>\n\n<b>praerogativa, ae</b> <i>f</i> [praerogativus]\n<b>1)</b> результаты голосования первой центурии: referre praerogativam <i>C</i> объявить о результатах голосования первой центурии;\n<b>2)</b> (<i>sc.</i> centuria) прерогативная центурия (голосующая первой) <i>C, L</i>;\n<b>3)</b> предварительные выборы (comitiorum militarium <i>L</i>);\n<b>4)</b> преимущественное право, прерогатива (p. militaris <i>Lampr</i>; p. minoris aetatis <i>CJ</i>);\n<b>5)</b> добрый знак, предзнаменование, предвестник (supplicatio est p. triumphi <i>C</i>)."],
  ["praerogativarius", "<big>praerogativarius</big>\n\n<b>praerogativarius, a, um</b>\nпользующийся известными преимуществами <i>Eccl</i>."],
  ["praerogativus", "<big>praerogativus</big>\n\n<b>praerogativus, a, um</b> [praerogo]\nзапрошенный первым, первым подающий голос (centuria <i>C</i>): omen praerogativum <i>C</i> прерогативная центурия (<i>как предзнаменование окончательного результата выборов</i>— <i>см.</i> praerogativa 2)."],
  ["praerogator", "<big>praerogator</big>\n\n<b>praerogator, oris</b> <i>m</i> [praerogo 2]\nраспределитель, раздатчик <i>Eccl</i>."],
  ["praerogo", "<big>praerogo</big>\n\n<b>prae–rogo, avi, atum, are</b>\n<b>1)</b> запрашивать первым (p. sententias <i>Su</i>);\n<b>2)</b> выплачивать вперёд <i>CJ</i>;\n<b>3)</b> ранее вносить: lex praerogata <i>CTh</i> ранее поступивший законопроект."],
  ["praerumpo", "<big>praerumpo</big>\n\n<b>prae–rumpo, rupi, ruptum, ere</b>\nспереди обрывать, отрывать (funes praerumpebantur <i>Cs</i>; p. retinacula puppis <i>O</i>). — <i>См. тж.</i> praeruptus I."],
  ["praerupium", "<big>praerupium</big>\n\n<b>prae–rupium, i</b> <i>n</i> [rupes]\nкрутизна, обрыв <i>Tert</i>."],
  ["praerupta", "<big>praerupta</big>\n\n<b>praerupta, orum</b> <i>n</i>\nкрутые места, обрывы, кручи <i>L, PM, QC, Just</i>."],
  ["praerupte", "<big>praerupte</big>\n\n<b>praerupte</b> [praeruptus I]\nкруто, обрывисто: mons p. altus <i>PM</i> гора, круто поднимающаяся вверх."],
  ["praeruptio", "<big>praeruptio</big>\n\n<b>praeruptio, onis</b> <i>f</i> <i>Is</i>\n= praerupium."],
  ["praeruptus", "<big>praeruptus</big>\n\n<b>I praeruptus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> praerumpo;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> крутой, отвесный, обрывистый (mons <i>Ctl, QC</i>; vallis <i>bAl</i>);\n<b>2)</b> пылкий, опрометчивый, безумный (audacia <i>C</i>);\n<b>3)</b> резкий (animo p. <i>T</i>); строгий, суровый (dominatio <i>T</i>);\n<b>4)</b> крайний, смертельный (periculum <i>VP</i>); опасный (seditio <i>Dig</i>);\n<b>5)</b> обрывающийся, отрывистый (sermo <i>Sen</i>).\n\n<b>II praeruptus, (us) </b> <i>m</i> <i>Eccl</i>\n= praeruptio."],
  ["praes", "<big>praes</big>\n\n<b>I praes, praedis</b> <i>m</i> [prae + vas]\n<b>1)</b> поручитель, гарант (praedem esse pro aliquo <i>C</i>): praedem dare <i>C</i> выставить поручителя;\n<b>2)</b> имущество поручителя (praedes alicujus vendere <i>C</i>).\n\n<b>II praes</b> <i>adv.</i> (= praesto)\nвот уж, под рукой, в полной готовности (ibi tibi parata p. est <i>Pl</i> — <i>v. l.</i>)."],
  ["praesaepe", "<big>praesaepe</big>\n\n<b>praesaepe, is</b> <i>n</i> <i>v. l.</i>\n= praesaepis."],
  ["praesaepes", "<big>praesaepes</big>\n\n<b>praesaepes, is</b> <i>f</i> <i>v. l.</i>\n= praesaepis."],
  ["praesaepio", "<big>praesaepio</big>\n\n<b>prae–saepio, saepsi, saeptum, ire</b>\nобносить забором, огораживать, загораживать (omnem aditum <i>Cs</i>)."],
  ["praesaepis", "<big>praesaepis</big>\n\n<b>praesaepis, is</b> <i>f</i> [praesepio] (<i>тж.</i> <i>pl.</i>)\n<b>1)</b> стойло, хлев, загон <i>Cato, V etc.</i>; логово (ursi <i>V</i>); улей <i>V</i>;\n<b>2)</b> ясли, кормушка <i>Vr</i>;\n<b>3)</b> кабак, притон <i>C</i>;\n<b>4)</b> жилище <i>Pl</i>."],
  ["praesaepium", "<big>praesaepium</big>\n\n<b>praesaepium, i</b> <i>n</i> <i>V, PM, Ap, Eccl</i>\n= praesaepis."],
  ["praesagio", "<big>praesagio</big>\n\n<b>prae–sagio, ivi (ii), —, ire</b>\n<b>1)</b> предчувствовать, предугадывать (aliquid animo <i>L</i>);\n<b>2)</b> предзнаменовать, предвещать, предсказывать (tempestatem <i>PM</i>)."],
  ["praesagior", "<big>praesagior</big>\n\n<b>praesagior, —, iri</b> <i>depon.</i> <i>Pl</i>\n= praesagio."],
  ["praesagitio", "<big>praesagitio</big>\n\n<b>praesagitio, onis</b> <i>f</i> [praesagio]\nпредчувствование, предугадывание, дар прозорливости <i>C</i>."],
  ["praesagium", "<big>praesagium</big>\n\n<b>praesagium, i</b> <i>n</i> [praesagio]\n<b>1)</b> предчувствие (malorum <i>T</i>); предсказание (de aliqua re <i>T</i>);\n<b>2)</b> предзнаменование (praesagia atque indicia futuri periculi <i>VP</i>): praesagia somni <i>St</i> вещий сон."],
  ["praesago", "<big>praesago</big>\n\n<b>prae–sago, avi, atum, are</b> [sagus]\nпредчувствовать (futura rerum <i>Ap</i>)."],
  ["praesagus", "<big>praesagus</big>\n\n<b>prae–sagus, a, um</b>\n<b>1)</b> (<i>тж.</i> p. futuri <i>O</i>) предчувствующий (pectora <i>O</i>);\n<b>2)</b> предвещающий, вещий (verba <i>O</i>; mens praesaga mali <i>V</i>)."],
  ["praesanesco", "<big>praesanesco</big>\n\n<b>prae–sanesco, sanui, —, ere</b>\nзаживать ранее обычного (cicatrices praesanuerunt <i>PM</i>)."],
  ["praesano", "<big>praesano</big>\n\n<b>prae–sano, (avi), atum, are</b>\nзаживлять, исцелять раньше обыкновенного (vulnera praesanata <i>PM</i>)."],
  ["praescateo", "<big>praescateo</big>\n\n<b>prae–scateo, —, —, ere</b>\nбыть переполненным, изобиловать (aliqua re <i>AG</i>)."],
  ["praescientia", "<big>praescientia</big>\n\n<b>prae–scientia, ae</b> <i>f</i>\nзнание будущего, предвидение, прозорливость <i>Aug, Tert, Boet</i>."],
  ["praescindo", "<big>praescindo</big>\n\n<b>prae–scindo, scidi, —, ere</b>\nразрывать (vestes medias <i>Vlg</i>)."],
  ["praescio", "<big>praescio</big>\n\n<b>I prae–scio, ivi, itum, ire</b>\nзнать заранее <i>Ter, Su, Vlg, Eccl</i>.\n\n<b>II praescio, onis</b> <i>m</i>\nпрозорливец <i>Amm</i> (<i>v. l.</i>)."],
  ["praescisco", "<big>praescisco</big>\n\n<b>prae–scisco, ivi (ii), —, ere</b>\nзаранее знакомиться, наперёд узнавать (aliquid <i>V, Col, L etc.</i>)."],
  ["praescitio", "<big>praescitio</big>\n\n<b>praescitio, onis</b> <i>f</i> <i>Amm</i>\n= praescitum."],
  ["praescitum", "<big>praescitum</big>\n\n<b>praescitum, i</b> <i>n</i> [praescio]\nпредчувствие (animi <i>PM</i>); предвещание (picorum <i>PM</i>)."],
  ["praescius", "<big>praescius</big>\n\n<b>prae–scius, a, um</b>\nзнающий наперёд, предчувствующий (cor <i>V</i>); вещий (lingua <i>O</i>; numen <i>T</i>); прозорливый, предвидящий (futuri <i>V</i>)."],
  ["praescribo", "<big>praescribo</big>\n\n<b>prae–scribo, scripsi, scriptum, ere</b>\n<b>1)</b> писать впереди, надписывать (nomen libro <i>AG</i>): p. titulum alicui rei <i>AG</i> озаглавить что-л.; auctoritates praescriptae <i>C</i> указанные впереди (сенатского постановления) имена присутствующих на заседании сенаторов;\n<b>2)</b> чертить, вычерчивать (praescripta lineamenta <i>PM</i>);\n<b>3)</b> предписывать, определять, устанавливать (jura civibus <i>C</i>; finem rebus <i>Ter</i>);\n<b>4)</b> намечать (formam futuri principatus <i>T</i>);\n<b>5)</b> <i>юр.</i> представлять возражения, возражать (alicui <i>C</i>);\n<b>6)</b> ссылаться в оправдание, использовать в виде предлога (aliquem <i>T</i>);\n<b>7)</b> диктовать, внушать (carmina alicui <i>Tib</i>)."],
  ["praescripsti", "<big>praescripsti</big>\n\n<b>praescripsti</b> <i>Ter</i>\n= praescripsisti (<i>pf. к</i> praescribo)."],
  ["praescriptio", "<big>praescriptio</big>\n\n<b>praescriptio, onis</b> <i>f</i> [prae + scribo]\n<b>1)</b> надпись, заголовок, заглавие (legis <i>C</i>; senatus consulti <i>C</i>);\n<b>2)</b> предписание, установление, определение, веление (rationis <i>C</i>; naturae <i>C</i>): sine praescriptione numeri <i>T</i> не предрешая вопроса о количестве;\n<b>3)</b> возражение, отвод (excludere aliquem praescriptione <i>Dig</i>);\n<b>4)</b> оговорка (praescriptiones cautionesque <i>C</i>);\n<b>5)</b> предлог: p. honesta <i>Cs</i> благовидный предлог."],
  ["praescriptive", "<big>praescriptive</big>\n\n<b>praescriptive</b>\nв порядке отвода (occurrere <i>Tert</i>)."],
  ["praescriptum", "<big>praescriptum</big>\n\n<b>praescriptum, i</b> <i>n</i> [praescribo]\n<b>1)</b> установленные пределы (intra p. equitare <i>H</i>);\n<b>2)</b> предписание, правило (agere ad p. <i>Cs</i>);\n<b>3)</b> образец для списывания, прописи (p. puerile <i>Sen</i>)."],
  ["praescriptus", "<big>praescriptus</big>\n\n<b>praescriptus, us</b> <i>m</i> [praescribo]\nпредписание, наставление <i>Tert</i>."],
  ["praeseco", "<big>praeseco</big>\n\n<b>prae–seco, secui, secatum (sectum), are</b>\nотрезывать, отрубать (aures <i>L</i>); обстригать спереди (crines <i>Cs</i>): ad unguem praesectum <i>погов. H</i> = тщательно, с величайшей точностью."],
  ["praesedi", "<big>praesedi</big>\n\n<b>praesedi</b> <i>pf. к</i>\npraesideo."],
  ["praesegmen", "<big>praesegmen</big>\n\n<b>praesegmen, inis</b> <i>n</i> [praeseco]\nобрезок, клочок <i>Pl, Ap</i>."],
  ["praeseminatio", "<big>praeseminatio</big>\n\n<b>prae–seminatio, onis</b> <i>f</i>\nзародыш, эмбрион <i>Vr</i>."],
  ["praesemino", "<big>praesemino</big>\n\n<b>prae–semino, —, atum, are</b>\n<b>1)</b> ранее сеять (homines ex draconis dentibus praeseminati <i>Lact</i>);\n<b>2)</b> закладывать основы, подготовлять (majora sibi <i>Amm</i>)."],
  ["praesens", "<big>praesens</big>\n\n<b>I praesens, entis</b>\n<b>1.</b> <i>part. praes. к</i> praesum;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> лично присутствующий, сам (aliquis p. adest <i>Pl, Ter, Cs etc.</i>): aliquem praesentem laudare <i>C</i> хвалить кого-л. в его присутствии (в глаза); aliquo praesente <i>C</i> в чьём-л. присутствии;\n<b>2)</b> устный (sermo <i>C</i>);\n<b>3)</b> близкий, непосредственный (periculum <i>QC</i>): praesentiores fructus <i>C</i> более непосредственные выгоды;\n<b>4)</b> настоящий, теперешний, нынешний, текущий (tempus <i>Cs</i>; bellum <i>Nep</i>); годный на настоящее время (perfugium <i>C</i>);\n<b>5)</b> подлинный, самый: in rem praesentem venire <i>C</i> прибыть на место (<i>происшествия, события или для осмотра предмета судебного разбирательства</i>); in re praesenti <i>L</i> на месте (происшествия);\n<b>6)</b> немедленный (poena <i>C</i>); срочный, спешный (decretum <i>L</i>); скоропостижный (mors <i>V etc.</i>);\n<b>7)</b> наличный (pecunia <i>C</i>);\n<b>8)</b> быстродействующий, радикальный (medicina <i>Col</i>; venenum <i>Mela, PM</i>; remedium <i>PM</i>); действенный (auxilium <i>C</i>); сильный (amor <i>O</i>);\n<b>9)</b> явный, очевидный, заметный (insidiae <i>C</i>);\n<b>10)</b> несущий помощь, милостивый (numina <i>V, H</i>; divi <i>V</i>);\n<b>11)</b> настойчивый, настоятельный (preces <i>C, Prp</i>);\n<b>12)</b> решительный, неустрашимый, <i>тж.</i> невозмутимый (animus <i>Ter, C, Sen etc.</i>);\n<b>13)</b> всегда готовый <i>или</i> способный (aliquid facere <i>H</i>);\n<b>14)</b> (ныне) употребительный (verba <i>AG</i>).\n\n<b>II praesens, entis</b> <i>n</i> (<i>преим.</i> <i>pl.</i>)\nнастоящее время, текущие дела, нынешние обстоятельства, создавшееся положение <i>C, QC, Q</i>: in praesenti <i>C, Nep, L</i> <i>и</i> e praesentibus <i>L</i> в настоящее время, теперь, при нынешних обстоятельствах; in p. <i>L, T, H</i> <i>и</i> ad p. <i>T, PM, Su</i> сейчас, для настоящего момента, на данное время."],
  ["praesensio", "<big>praesensio</big>\n\n<b>praesensio, onis</b> <i>f</i> [praesentio]\n<b>1)</b> предчувствие (futurarum rerum <i>C</i>);\n<b>2)</b> изначальное (врождённое) представление <i>C</i>."],
  ["praesentalis", "<big>praesentalis</big>\n\n<b>praesentalis, e</b> [praesens]\nлично присутствующий <i>CJ</i>."],
  ["praesentaneus", "<big>praesentaneus</big>\n\n<b>praesentaneus, a, um</b> [praesens I]\n<b>1)</b> присутствующий, наличный <i>Lact</i>;\n<b>2)</b> быстродействующий (venenum <i>PM, Su</i>; remedium <i>PM</i>)."],
  ["praesentarius", "<big>praesentarius</big>\n\n<b>praesentarius, a, um</b> [praesens II]\n<b>1)</b> настоящий (malum <i>Pl</i>);\n<b>2)</b> наличный (aurum <i>Pl</i>);\n<b>3)</b> <i>Ap</i> = praesentaneus 2."],
  ["praesentia", "<big>praesentia</big>\n\n<b>praesentia, ae</b> <i>f</i> [praesens I]\n<b>1)</b> присутствие, наличие (alicujus praesentiam vitare <i>C</i>): minuit p. famam <i>Cld</i> <i>впоследствии погов.</i> = вблизи враг не так уж страшен; p. animi <i>C, Cs</i> присутствие духа, решимость; in praesentia <i>C, Cs etc.</i> в настоящее время, в данный момент <i>или</i> в то время;\n<b>2)</b> непосредственное воздействие, сила (veri <i>O</i>)."],
  ["praesentio", "<big>praesentio</big>\n\n<b>prae–sentio, sensi, sensum, ire</b>\nпредчувствовать, предвидеть, предугадывать (futura <i>C</i>; dolos <i>V</i>): certa notione animi p. <i>C</i> иметь верное предчувствие."],
  ["praesento", "<big>praesento</big>\n\n<b>praesento, —, —, are</b> [praesens I]\n<b>1)</b> представлять (sese alicui <i>Ap</i>);\n<b>2)</b> передавать, вручать (aliquid alicui <i>AV</i>)."],
  ["praesep-", "<big>praesep-</big>\n\n<b>praesep-</b> <i>v. l.</i>\n= praesaep-."],
  ["praesepultus", "<big>praesepultus</big>\n\n<b>prae–sepultus, a, um</b>\nранее погребённый, уже умерший <i>Q, Amm</i>."],
  ["praesertim", "<big>praesertim</big>\n\n<b>praesertim</b>\nособенно, в особенности (cito exarescit lacrima, p. in alienis malis <i>C</i>): vir p. ingenio ingenti <i>Ap</i> человек исключительно высокой одарённости; qui p. quaestor in ea provincia fuisset <i>C</i> тем более что он был квестором в этой провинции."],
  ["praeservio", "<big>praeservio</big>\n\n<b>prae–servio, —, —, ire</b>\n<b>1)</b> служить рабом (alicui <i>Pl</i>);\n<b>2)</b> <i>грам.</i> подчиняться: p. numeris <i>AG</i> изменяться по числам."],
  ["praeservo", "<big>praeservo</big>\n\n<b>prae–servo, —, —, are</b>\nранее соблюдать (vitae consuetudinem <i>CA</i>)."],
  ["praeses", "<big>praeses</big>\n\n<b>praeses, sidis</b> [praesideo]\n<b>1.</b> <i>adj.</i> защищающий, охраняющий (dextra <i>SenT</i>): ars salutis humanae p. <i>CC</i> искусство, стоящее на страже человеческого здоровья, <i>т. е.</i> медицина; locus p. <i>Pl</i> убежище;\n<b>2.</b> <i>subst. m, f</i> хранитель, покровитель, защитник: p. belli <i>V, Q</i> богиня (бог) войны; senatus rei publicae custos, p., propugnator <i>C</i> сенат — страж, хранитель (и) защитник государства; p. provinciae <i>Su</i> наместник (в) провинции, <i>т. е.</i> проконсул; praesidum curricula <i>Ap</i> сроки полномочий проконсулов."],
  ["praeseverus", "<big>praeseverus</big>\n\n<b>prae–severus, a, um</b>\nкрайне строгий <i>T</i>."],
  ["praesica", "<big>praesica</big>\n\n<b>praesica, ae</b> <i>f</i> <i>Vr предпол.</i>\n= brassica."],
  ["praesiccatus", "<big>praesiccatus</big>\n\n<b>prae–siccatus, a, um</b>\nзаранее (раньше) высушенный (succus <i>Ap</i>)."],
  ["praesico", "<big>praesico</big>\n\n<b>praesico</b> <i>v. l.</i>\n= praeseco."],
  ["praesidalis", "<big>praesidalis</big>\n\n<b>praesidalis, e</b> [praeses]\nнаместнический (jus <i>Treb</i>; provincia <i>Lampr</i>); vir p. <i>Symm</i> бывший наместник."],
  ["praesidarius", "<big>praesidarius</big>\n\n<b>praesidarius</b> <i>v. l.</i>\n= praesidiarius."],
  ["praesidatus", "<big>praesidatus</big>\n\n<b>praesidatus, us</b> <i>m</i> [praeses]\nнаместничество, звание наместника <i>Vop, Treb etc.</i>"],
  ["praesidens", "<big>praesidens</big>\n\n<b>praesidens, entis</b> <i>m</i> [praesideo]\nпредседательствующий, глава <i>T etc.</i>"],
  ["praesideo", "<big>praesideo</big>\n\n<b>praesideo, sedi, sessum, ere</b> [prae + sedeo]\n<b>1)</b> восседать впереди, председательствовать, <i>перен.</i> стоять во главе, руководить, управлять (alicui rei, <i>реже</i> aliquid): p. rebus urbanis <i>Cs</i> ведать городскими делами; p. exercitum <i>T</i> командовать армией; p. Pannoniam <i>T</i> командовать войсками Паннонии; p. ad judicandum <i>Tert</i> заседать в суде || <i>в императ. эпоху</i> (<i>тж.</i> p. in senatu <i>PJ</i>) председательствовать в сенате <i>Su</i>;\n<b>2)</b> защищать, охранять (Juno, quae toris praesidet maritis <i>O</i>); оборонять (urbi <i>C</i>; agros suos <i>St</i>)."],
  ["praesidialis", "<big>praesidialis</big>\n\n<b>praesidialis, e</b> <i>v. l.</i>\n= praesidalis."],
  ["praesidiarius", "<big>praesidiarius</big>\n\n<b>praesidiarius, a, um</b> [praesidium]\n<b>1)</b> обороняющий, <i>т. е.</i> входящий в состав гарнизона (milites <i>L</i>);\n<b>2)</b> наместнический, присвоенный наместнику (toga <i>Spart</i>);\n<b>3)</b> <i>с.-х.</i> запасный (malleolus <i>Col</i>)."],
  ["praesidiatus", "<big>praesidiatus</big>\n\n<b>praesidiatus, us</b> <i>m</i> <i>v. l.</i>\n= praesidatus."],
  ["praesidium", "<big>praesidium</big>\n\n<b>praesidium, i</b> <i>n</i> [praesideo]\n<b>1)</b> защита (p. domus familiaeque <i>Pt</i> — <i>о собаке</i>); оплот, помощь, опора, поддержка (et p. et decus meum <i>n</i>): esse (adesse) praesidio <i>C etc.</i> защищать; ferre alicui p. <i>C</i> оказывать кому-л. помощь; praesidio alicujus rei <i>C</i> с помощью чего-л.;\n<b>2)</b> способ, средство (quaerere sibi praesidia periculis <i>C</i>; sibi p. ad beatam vitam comparare <i>C</i>): esse in praesidio <i>Pt</i> служить средством:\n<b>3)</b> <i>воен.</i> прикрытие, охрана, оборона, гарнизон, стражи, караул (praesidia ad ripas disponere <i>Cs</i>): p. imponere urbi <i>L</i> разместить в городе гарнизон || личная охрана, эскорт (ad judicium cum praesidio venire <i>C</i>);\n<b>4)</b> вооружённые силы, войско (Italia tenetur praesidiis <i>C</i>);\n<b>5)</b> военный пост, укреплённый участок, укрепление, крепость (legio p. occupavit <i>Cs</i>): p. deserere <i>C</i>, (de) praesidio decedere <i>L, C</i> (p. relinquere <i>C</i>) покинуть свой пост, дезертировать; in praesidiis alicujus esse <i>C etc.</i> находиться в чьём-л. стане, быть на чьей-л. стороне;\n<b>6)</b> средство, лекарство (aurium morbis p. esse <i>PM</i>)."],
  ["praesignatio", "<big>praesignatio</big>\n\n<b>prae–signatio, onis</b> <i>f</i>\nпредуведомление <i>Aug</i>."],
  ["praesignificatio", "<big>praesignificatio</big>\n\n<b>praesignificatio, onis</b> <i>f</i> <i>Lact</i>\n= praemonstratio."],
  ["praesignifico", "<big>praesignifico</big>\n\n<b>prae–significo, —, —, are</b>\nпредуказывать, предзнаменовать (futura <i>C</i>)."],
  ["praesignis", "<big>praesignis</big>\n\n<b>prae–signis, e</b> [prae + signum]\nвыдающийся, замечательный (aliqua re <i>O, Sil, St</i>)."],
  ["praesigno", "<big>praesigno</big>\n\n<b>prae–signo, avi, atum, are</b>\nзаранее отмечать (aliquid <i>Cato, PM, Eccl</i>)."],
  ["praesolidus", "<big>praesolidus</big>\n\n<b>prae–solidus, a, um</b>\nвесьма крепкий <i>или</i> стойкий (homo <i>Jvc</i>)."],
  ["praesono", "<big>praesono</big>\n\n<b>prae–sono, sonui, —, are</b>\n<b>1)</b> звучать раньше: ubi praesonuit solemni tibia cantu <i>O</i> лишь только зазвучит торжественный напев флейт;\n<b>2)</b> заглушать <i>или</i> превосходить своим звучанием (avena p. chelyn <i>Calp</i>)."],
  ["praespargo", "<big>praespargo</big>\n\n<b>prae–spargo, —, —, ere</b>\nрассыпать заранее, рассеивать впереди (<i>sc.</i> flores <i>Lcr</i>)."],
  ["praespeculatus", "<big>praespeculatus</big>\n\n<b>prae–speculatus, a, um</b>\nпредварительно проверенный (utilitas <i>Amm</i>)."],
  ["praespero", "<big>praespero</big>\n\n<b>prae–spero, —, —, are</b>\nпитать надежду (p. aliquid <i>Tert</i>)."],
  ["praespicio", "<big>praespicio</big>\n\n<b>prae–spicio, —, —, ere</b> [specio]\nпредвидеть (aliquid <i>CA</i>)."],
  ["praestabilis", "<big>praestabilis</big>\n\n<b>praestabilis, e</b> <i>C, Sl etc.</i>\n= praestans."],
  ["praestans", "<big>praestans</big>\n\n<b>praestans, antis</b>\n<b>1.</b> <i>part. praes. к</i> praesto I;\n<b>2.</b> <i>adj.</i> выдающийся, превосходный, отличный (p. ingenio et doctrina <i>C</i>; numen praestantissimae mentis <i>C</i>): victoria p. <i>C</i> победоносный; labor p. <i>Lcr</i> неутомимый труд; auxilium p. <i>PM</i> действенная помощь; armorum p. <i>St</i> славный боевыми подвигами."],
  ["praestanter", "<big>praestanter</big>\n\n<b>praestanter</b>\nпревосходно, отлично <i>PM, CA</i>."],
  ["praestantia", "<big>praestantia</big>\n\n<b>praestantia, ae</b> <i>f</i> [praestans]\n<b>1)</b> превосходство: p. alicujus rei <i>C</i> превосходство в чём-л. <i>или</i> над чём-л.;\n<b>2)</b> действенность, эффективность (remedii <i>PM</i>)."],
  ["praestatio", "<big>praestatio</big>\n\n<b>praestatio, onis</b> <i>f</i> [praesto I]\n<b>1)</b> поручительство, гарантия: ad praestationem scribere <i>Sen</i> ручаться за истинность написанного;\n<b>2)</b> внесение, уплата (vectigalis <i>Dig</i>)."],
  ["praestaturus", "<big>praestaturus</big>\n\n<b>praestaturus, a, um</b> <i>C part. fut. к</i>\npraesto I."],
  ["praestatus", "<big>praestatus</big>\n\n<b>praestatus, a, um</b> <i>PM part. pf. к</i>\npraesto I."],
  ["praestavi", "<big>praestavi</big>\n\n<b>praestavi</b> <i>Dig pf. к</i>\npraesto I."],
  ["praesterno", "<big>praesterno</big>\n\n<b>prae–sterno, —, —, ere</b>\n<b>1)</b> заранее рассеивать (folia <i>Pl</i>);\n<b>2)</b> раньше приготовлять (altaria sibi <i>St</i>): materiam campumque laudibus p. <i>PJ</i> открыть поле для славных деяний."],
  ["praestes", "<big>praestes</big>\n\n<b>praestes, stitis</b> <i>m, f</i> [praesto I]\nхранитель, защитник (lares <i>O</i>; Juppiter <i>Capit</i>)."],
  ["praestigiae", "<big>praestigiae</big>\n\n<b>praestigiae, arum</b> <i>f</i> [<i>из</i> *praestrigiae <i>от</i> praestringo] (<i>редко</i> <i>sg.</i>)\nпризраки, обман: p. verborum <i>C</i> словесный туман; p. nubium <i>Ap</i> обманчивые образы облаков."],
  ["praestigiator", "<big>praestigiator</big>\n\n<b>praestigiator, oris</b> <i>m</i> [praestigiae]\nфокусник <i>Sen</i>; обманщик <i>Pl, Ap, Eccl</i>."],
  ["praestigiatrix", "<big>praestigiatrix</big>\n\n<b>praestigiatrix, tricis</b> <i>f</i> [praestigiator]\nобманщица <i>Pl</i>."],
  ["praestigio", "<big>praestigio</big>\n\n<b>praestigio, —, —, are</b>\nобманывать: p. aliquid alicui <i>JV</i> дурачить кого-л. чём-л."],
  ["praestigiosus", "<big>praestigiosus</big>\n\n<b>praestigiosus, a, um</b>\nобманчивый, обманный <i>AG</i>."],
  ["praestigium", "<big>praestigium</big>\n\n<b>praestigium, i</b> <i>n</i>\nобман, надувательство <i>Hier</i>."],
  ["praestino", "<big>praestino</big>\n\n<b>praestino, avi, atum, are</b> [praes]\nпокупать, приобретать (pisces <i>Pl</i>; aliquid viginti denariis <i>Ap</i>; baxeas de sutrina <i>Ap</i>)."],
  ["praestiti", "<big>praestiti</big>\n\n<b>praestiti</b> <i>pf. к</i>\npraesto I."],
  ["praestitor", "<big>praestitor</big>\n\n<b>praestitor, oris</b> <i>m</i> [praesto I]\nподатель (omnium bonorum <i>Ap</i>)."],
  ["praestituo", "<big>praestituo</big>\n\n<b>prae–stituo, stitui, stitutum, ere</b> [statuo]\nназначать (tempus alicui <i>C</i>); предназначать (diem operi <i>C</i>): nulla praestituta die <i>C</i> без указания (определённого) срока."],
  ["praestiturus", "<big>praestiturus</big>\n\n<b>praestiturus, a, um</b> <i>Dig part. fut. к</i>\npraesto I."],
  ["praestitus", "<big>praestitus</big>\n\n<b>praestitus, a, um</b> <i>part. pf. к</i>\npraesto."],
  ["praesto", "<big>praesto</big>\n\n<b>I prae–sto, stiti, stitum, are</b>\n<b>1)</b> стоять впереди <i>или</i> выше, быть лучше, выделяться, превосходить (alicui <i>Ter, C</i>, inter aliquos <i>C</i> <i>или</i> aliquem aliqua re <i>Nep, L</i>; praestat honesta mors turpi vitae <i>Nep</i>);\n<b>2)</b> доставлять, предоставлять, давать (alicui voluptatem <i>C</i>); поставлять (annonam <i>C</i>; milites <i>Eutr</i>); даровать (pacem, honores <i>Eutr</i>); выплачивать (stipendium exercitui <i>L, T</i>; dimidium tributorum <i>Eutr</i>): p. caput fulminibus <i>Lcn</i> подставлять свою голову под молнии, <i>т. е.</i> рисковать своей жизнью;\n<b>3)</b> проявлять (benevolentiam <i>C</i>; magnam virtutem <i>Cs</i>; amorem <i>H</i>); оказывать (alicui debitum honorem <i>C</i>); выказывать, обнаруживать (mobilitatem equitum <i>Cs</i>);\n<b>4)</b> соблюдать (fidem <i>C</i>); сохранять (aliquem incolumem <i>H</i>; consuetudinem <i>C</i>); хранить (silentium <i>L</i>); исполнять (promissum <i>QC, L</i>); выполнять, осуществлять (rem maximam <i>PJ</i>); удерживать (aliquem finibus certis <i>C</i>): sententiam p. <i>C</i> подавать голос; alicui mare tutum p. <i>C</i> сделать море безопасным для кого-л.; terga p. <i>T</i> обратить тыл, бежать; alicui justa p. <i>QC</i> воздать кому-л. последние почести; aliquem salvum p. <i>C</i> спасти кого-л.; vicem alicujus p. <i>Sl</i> замещать кого-л.; se p. оказываться, показывать себя (se p. dignum alicui rei <i>C</i>);\n<b>5)</b> ручаться, брать на себя ответственность, отвечать, гарантировать (nihil in vita praestandum <i>C</i>): p. aliquid <i>или</i> de aliqua re <i>C etc.</i> отвечать за что-л.; p. alicui aliquid <i>C</i> принимать на себя ответственность перед кем-л. за что-л.;\n<b>6)</b> <i>impers.</i> praestat предпочтительнее, лучше (praestat nobis — <i>или</i> nos — mori, quam servire <i>C</i>): est ubi damnum praestet facere, quam lucrum <i>Pl</i> бывает, что убыток приходится предпочесть (нечестной) прибыли.\n\n<b>II praesto</b> <i>adv.</i> [prae + situs]\nтут, под рукой, наготове, в состоянии готовности (ubique et omnibus p. esse <i>Sen</i>): p. est <i>и</i> p. adest <i>Ter</i> вот он; nulla ei fuit clementia p. <i>Ctl</i> он не имел никакой жалости; p. esse alicui <i>C etc.</i> находиться у кого-л. <i>или</i> быть в чьём-л. распоряжении, служить (помогать) кому-л.; hirundines aestivo tempore p. sunt <i>rhH</i> с наступлением весны ласточки прилетают."],
  ["praestolatio", "<big>praestolatio</big>\n\n<b>praestolatio, onis</b> <i>f</i>\nожидание, надежда (ubi est nunc p. mea? <i>Vlg</i>)."],
  ["praestolo", "<big>praestolo</big>\n\n<b>praestolo, —, —, are</b> <i>LA, Ap</i>\n= praestolor."],
  ["praestolor", "<big>praestolor</big>\n\n<b>praestolor, atus sum, ari</b> <i>depon.</i> [praesto II]\nбыть готовым, ожидать, поджидать (alicui <i>C</i> <i>или</i> aliquem <i>Pl, Ter</i> <i>и</i> aliquid <i>Cs</i>)."],
  ["praestrangulo", "<big>praestrangulo</big>\n\n<b>prae–strangulo, —, —, are</b>\nудушить, <i>перен.</i> зажать рот (p. oratorem <i>Q</i>)."],
  ["praestrictim", "<big>praestrictim</big>\n\n<b>prae–strictim</b> <i>adv.</i>\nсжато, вкратце <i>Eccl</i>."],
  ["praestrictio", "<big>praestrictio</big>\n\n<b>prae–strictio, onis</b> <i>f</i>\nсжатие, сужение <i>Tert</i>."],
  ["praestrictus", "<big>praestrictus</big>\n\n<b>praestrictus, a, um</b> <i>part. pf. к</i>\npraestringo."],
  ["praestrig-", "<big>praestrig-</big>\n\n<b>praestrig-</b> <i>v. l.</i>\n= praestig-."],
  ["praestringo", "<big>praestringo</big>\n\n<b>prae–stringo, strinxi, strictum, ere</b>\n<b>1)</b> стягивать, связывать (aliquid aliqua re <i>O, T</i>);\n<b>2)</b> замораживать (umor praestrictus gelu <i>PM</i>);\n<b>3)</b> сдерживать, ограничивать (aliquid <i>PM</i>);\n<b>4)</b> задевать (fulgur aliquid praestrinxit <i>Su</i>); затрагивать (в разговоре), упоминать (nomen alicujus praestringebatur <i>Amm</i>);\n<b>5)</b> проходить мимо (p. palatii januas <i>Amm</i>);\n<b>6)</b> притуплять (aciem ferri <i>PM</i>, <i>перен.</i> aciem mentis <i>C</i>);\n<b>7)</b> ослеплять (aciem oculorum <i>Pl, L</i>; oculos <i>C</i>; lumina flamma <i>Lcn</i>): p, vites <i>PM</i> лишать лозы почек;\n<b>8)</b> ослаблять (nitorem eboris <i>PM</i>)."],
  ["praestructim", "<big>praestructim</big>\n\n<b>prae–structim</b> <i>adv.</i>\nпредварительно подготовившись (aliquo pervenire <i>Tert</i>)."],
  ["praestructio", "<big>praestructio</big>\n\n<b>prae–structio, onis</b> <i>f</i>\nпредуготовление, подготовка <i>Tert, Aug</i>."],
  ["praestructura", "<big>praestructura</big>\n\n<b>praestructura, ae</b> <i>f</i> <i>Tert</i>\n= praestructio."],
  ["praestruo", "<big>praestruo</big>\n\n<b>praestruo, struxi, structum, ere</b>\n<b>1)</b> наперёд приготовлять, заранее приобретать (aliquid <i>Su, Amm</i>): tacitas vindictae p. iras <i>Cld</i> затаить в душе злобу я месть; p. fidem sibi <i>L</i> заранее заручиться доверием;\n<b>2)</b> застраивать, заграждать (aditum <i>O</i>)."],
  ["praestupesco", "<big>praestupesco</big>\n\n<b>prae–stupesco, —, —, ere</b>\nчрезвычайно изумляться <i>JV</i>."],
  ["praestupidus", "<big>praestupidus</big>\n\n<b>prae–stupidus, a, um</b>\nкрайне тупой (cor <i>Jvc</i>)."],
  ["praesudo", "<big>praesudo</big>\n\n<b>prae–sudo, —, —, are</b>\n<b>1)</b> заранее потеть, <i>перен.</i> быть ещё влажным: dum matutinis praesudat solibus aer <i>Cld</i> пока ещё воздух влажен до наступления утреннего зноя;\n<b>2)</b> потрудиться, поработать прежде (Martio bello <i>St</i>)."],
  ["praesul", "<big>praesul</big>\n\n<b>praesul, ulis</b> <i>m, f</i> [prae+ salio II]\n<b>1)</b> передний танцор, пляшущий впереди <i>C</i>;\n<b>2)</b> главный из 12 жрецов Марса (Salii) (<i>плясавший впереди ежегодной процессии со священными щитами ancilia</i>) <i>AV, Capit</i>;\n<b>3)</b> глава, покровитель (alicujus rei <i>Sol, Aus, Sid</i>)."],
  ["praesulatus", "<big>praesulatus</big>\n\n<b>praesulatus, us</b> <i>m</i> [praesul]\nруководящий пост, главенство <i>Eccl</i>."],
  ["praesulsus", "<big>praesulsus</big>\n\n<b>prae–sulsus, a, um</b> [prae + salsus]\nочень солёный (adeps <i>Col</i>)."],
  ["praesultator", "<big>praesultator</big>\n\n<b>praesultator (praesultor), oris</b> <i>m</i> <i>L, VM</i>\n= praesul 1."],
  ["praesulto", "<big>praesulto</big>\n\n<b>prae–sulto, —, —, are</b> [prae + salto]\nпрыгать, танцевать, плясать впереди (alicui rei <i>L</i>)."],
  ["praesultor", "<big>praesultor</big>\n\n<b>praesultor</b> <i>v. l.</i>\n= praesultator."],
  ["praesum", "<big>praesum</big>\n\n<b>prae–sum, fui, esse</b>\n<b>1)</b> быть впереди, быть первым, главным, руководить (p. alicui negotio <i>Cs</i>); командовать, предводительствовать (exercitui <i>Cs, Nep</i>): p. rebus divinis <i>Cs</i> <i>или</i> sacris <i>C</i> совершать богослужения, исполнять священные обряды || играть главную роль (p. illi crudelitati <i>C</i>): p. alicui rei <i>C, Cs, Nep etc.</i> стоять во главе чего-л., начальствовать над чём-л.;\n<b>2)</b> оборонять, защищать (moenibus urbis <i>O</i>)."],
  ["praesumenter", "<big>praesumenter</big>\n\n<b>praesumenter</b> <i>Eccl</i>\n= praesumpte."],
  ["praesumo", "<big>praesumo</big>\n\n<b>prae–sumo, sumpsi, sumptum, ere</b>\n<b>1)</b> брать вперёд (dapes <i>O</i>); принимать наперёд <i>или</i> заранее (remedia <i>T</i>): p. patientiam <i>Q</i> заранее запастись терпением;\n<b>2)</b> заранее предвкушать (gaudium <i>PJ</i>; fortunam principatus <i>T</i>); наперёд представлять себе, заранее воображать, предвосхищать (futura <i>Sen</i>; aliquid cogitatione <i>PJ</i>); предугадывать, предвидеть, предчувствовать, ожидать (aliquid animo <i>VM</i>): opinio praesumpta <i>Q</i> предвзятое мнение, предубеждение; aliquid praesumptum habere <i>T</i> предполагать что-л.;\n<b>3)</b> быть самонадеянным, зазнаваться (praesumentes de se et de sua virtute gloriantes <i>Vlg</i>)."],
  ["praesumpte", "<big>praesumpte</big>\n\n<b>praesumpte</b>\nуверенно, решительно (veritatem dicere <i>Vop</i>)."],
  ["praesumptio", "<big>praesumptio</big>\n\n<b>praesumptio, onis</b> <i>f</i> [praesumo]\n<b>1)</b> преждевременное пользование, заблаговременное использование (alicujus rei <i>PJ</i>);\n<b>2)</b> предположение, ожидание, надежда, чаяние (contra praesumptionem suam <i>Just</i>);\n<b>3)</b> <i>ритор.</i> (<i>греч.</i> prolepsis) предвосхищение возражений, заблаговременное опровержение их, упреждение <i>Q</i>;\n<b>4)</b> (<i>тж.</i> p. opinionis <i>Aug</i>) предубеждение (alicujus benigna p. de aliquo <i>Ap</i>);\n<b>5)</b> упорство (contra aliquid <i>Ap</i>)."],
  ["praesumptiose", "<big>praesumptiose</big>\n\n<b>praesumptiose</b>\nсамонадеянно (loqui <i>Sid</i>)."],
  ["praesumptiosus", "<big>praesumptiosus</big>\n\n<b>praesumptiosus, a, um</b>\nсамонадеянный (juvenis <i>Sid</i>)."],
  ["praesumptive", "<big>praesumptive</big>\n\n<b>praesumptive</b> <i>Eccl</i>\n= praesumptiose."],
  ["praesumptivus", "<big>praesumptivus</big>\n\n<b>praesumptivus, a, um</b>\n<b>1)</b> <i>грам.</i> выражающий предположительность (conjunctio);\n<b>2)</b> <i>Eccl</i> = praesumptiosus."],
  ["praesumptor", "<big>praesumptor</big>\n\n<b>praesumptor, oris</b> <i>m</i> [praesumo]\n<b>1)</b> захватчик, похититель <i>Eccl</i>;\n<b>2)</b> самонадеянный человек <i>Hier, Aug</i>."],
  ["praesumptorie", "<big>praesumptorie</big>\n\n<b>praesumptorie</b> <i>Tert</i>\n= praesumptiose."],
  ["praesumptuos-", "<big>praesumptuos-</big>\n\n<b>praesumptuos-</b> <i>v. l.</i>\n= praesumptios-."],
  ["praesuo", "<big>praesuo</big>\n\n<b>prae–suo, —, sutum, ere</b>\nобшивать спереди, <i>перен.</i> покрывать (hasta foliis praesuta <i>O</i>; surculos coriis p. <i>PM</i>)."],
  ["praesuspecto", "<big>praesuspecto</big>\n\n<b>prae–suspecto, —, —, are</b>\nпредвидеть, ожидать (aliquid <i>JV</i>)."],
  ["praetango", "<big>praetango</big>\n\n<b>prae–tango, —, tactum, ere</b>\n<b>1)</b> предварительно прикасаться (oleo praetactus <i>CA</i>);\n<b>2)</b> <i>мед.</i> поражать (praetacta membra <i>CA</i>)."],
  ["praetectio", "<big>praetectio</big>\n\n<b>prae–tectio, onis</b> <i>f</i>\nприкрытие спереди, защита (p. oculorum <i>CA</i>)."],
  ["praetego", "<big>praetego</big>\n\n<b>prae–tego, texi, tectum, ere</b>\n<b>1)</b> покрывать спереди (praetectum caput <i>CA</i>);\n<b>2)</b> защищать, охранять (somnum alicujus <i>PJ</i>);\n<b>3)</b> прикрывать, скрывать (vitia divitiis <i>Lact</i>)."],
  ["praetempt-", "<big>praetempt-</big>\n\n<b>praetempt-</b> <i>v. l.</i>\n= praetent-."],
  ["praetendo", "<big>praetendo</big>\n\n<b>prae–tendo, tendi, tentum, ere</b>\n<b>1)</b> вытягивать (propagines e vite praetentae <i>Fabius Pictor ap. AG</i>); протягивать, держать перед собой (ramum olivae manu <i>V</i>; hastas dextris <i>V</i>): conjugis taedas p. <i>V</i> держать впереди брачные факелы, <i>перен.</i> объявлять себя супругом, вступать в законный брак; aliquid oculis p. <i>O</i> держать что-л. перед глазами; p. saepem segeti <i>V</i> огораживать посевы; p. decreto sermonem <i>L</i> предпослать постановлению речь;\n<b>2)</b> приводить в качестве основания, ссылаться в извинение, оправдываться (aliquid alicui rei): culpae splendida verba p. <i>O</i> приводить в своё оправдание пышные слова || <i>перен.</i> прикрывать (nomen hominis immanibus moribus <i>C</i>): p. ignorantiam <i>Q</i> отговариваться незнанием; humanitatem p. <i>Ap</i> притворяться любезным;\n<b>3)</b> противопоставлять (morti p. muros <i>V</i>);\n<b>4)</b> <i>pass.</i> praetendi <i>воен.</i> быть расположенным впереди (legio praetenta Britannis <i>Cld</i>);          \n<b>5)</b> <i>pass.</i> praetendi простираться (ad Pontum <i>Amm</i>); лежать, находиться перед (Baeticae praetenditur Lusitania <i>PM</i>).<p></p>"],
  ["praetener", "<big>praetener</big>\n\n<b>prae–tener, era, erum</b>\nчрезвычайно нежный (vitis <i>PM</i>)."],
  ["praetentatus", "<big>praetentatus</big>\n\n<b>praetentatus, (us) </b> <i>m</i> [praetento]\nощупывание, пробование <i>PM</i>."],
  ["praetento", "<big>praetento</big>\n\n<b>prae–tento, (tempto), avi, atum, are</b>\n<b>1)</b> предварительно ощупывать (iter baculo <i>O</i>); обыскивать (silvas <i>O, T</i>);\n<b>2)</b> испытывать, пробовать (vires <i>O</i>; pollice chordas <i>O</i>);\n<b>3)</b> держать перед собой (pallia <i>Cld</i>)."],
  ["praetentura", "<big>praetentura</big>\n\n<b>praetentura, ae</b> <i>f</i> [praetendo]\nпограничная стража, защитный кордон <i>Amm</i>."],
  ["praetenuis", "<big>praetenuis</big>\n\n<b>prae–tenuis, e</b>\n<b>1)</b> очень тонкий, узкий (folium <i>PM</i>);\n<b>2)</b> очень высокий, тонкий (sonus <i>Q</i>)."],
  ["praetepesco", "<big>praetepesco</big>\n\n<b>prae–tepesco, tepui, —, ere</b>\nпрежде быть горячим: mollis in obsequium facilisque rogantibus esses, si tuus in quavis praetepuisset amor <i>O</i> ты был бы уступчив и снисходителен к мольбам, если бы прежде пылал любовью к кому-л."],
  ["praeter", "<big>praeter</big>\n\n<b>I praeter</b> <i>adv.</i>\n<b>1)</b> мимо (<i>тк. как приставка</i>: praeter–ago <i>etc.</i>);\n<b>2)</b> кроме, исключая: nihil p. <i>O</i> ничего кроме, только; praeterque <i>PM</i> а (и) кроме того; p. quam <i>Pl etc.</i> = praeterquam; p. propter <i>арх. AG</i> более или менее, приблизительно.\n\n<b>II praeter</b> <i>praep. cum acc.</i>\n1) мимо (copias p. castra traducere <i>Cs</i>); вдоль (p. moenia fluere <i>L</i>); перед (p. oculos alicujus <i>C, O</i>);\n<b>2)</b> исключая, за исключением, кроме (omnes p. aliquem invitare <i>C</i>): p. haec <i>Ter, CC, QC</i> кроме того; nemo p. Cajum <i>C</i> никто, кроме Гая, <i>т. е.</i> только Гай; nihil habeo p. auditum <i>C</i> я знаю только понаслышке;\n<b>3)</b> наряду, помимо (p. aliquem decem alios adducere <i>Cs</i>);\n<b>4)</b> сверх: p. modum <i>C</i> чрезмерно; p. omnes <i>Pl, H</i> превыше всех;\n<b>5)</b> против, вопреки (p. exspectationem <i>C</i>; p. spem omnium <i>L</i>); наперекор (p. justum <i>Lcr</i>);\n<b>6)</b> в большей степени чем (p. ceteros beatus <i>C</i>): quae me res p. ceteros impulit? <i>C</i> что (же) побудило меня больше, чем остальных?"],
  ["praeterago", "<big>praeterago</big>\n\n<b>praeter–ago, —, actum, ere</b>\nгнать мимо (equum <i>H</i>)."],
  ["praeterbito", "<big>praeterbito</big>\n\n<b>praeter–bito, —, —, ere</b>\nидти мимо, проходить (inter vias <i>Pl</i>; nullas aedes <i>Pl</i>)."],
  ["praetercurro", "<big>praetercurro</big>\n\n<b>praeter–curro, —, cursum, ere</b>\nпробегать (промчаться) мимо (equi praetercurrentes <i>Veg</i>; urbe praetercursa <i>Amm</i>)."],
  ["praeterduco", "<big>praeterduco</big>\n\n<b>praeter–duco, duxi, ductum, ere</b>\nвести мимо (aliquem quasi pompam <i>Pl</i>)."],
  ["praeterea", "<big>praeterea</big>\n\n<b>praeter–ea</b> <i>adv.</i>\n<b>1)</b> кроме (сверх) того <i>C etc.</i>;\n<b>2)</b> потом, затем, далее <i>Ter, V etc.</i>"],
  ["praetereo", "<big>praetereo</big>\n\n<b>praeter–eo, ivi (ii), itum, ire</b>\n<b>1)</b> проходить мимо, миновать (aliquem, aliquid <i>C etc.</i>); протекать мимо (flumina ripas praetereunt <i>H</i>); тянуться вдоль (mons Ciliciam praeterit <i>QC</i>): praetereuntes <i>H</i> прохожие; praeteriens dixit <i>Ter</i> он сказал мимоходом;\n<b>2)</b> (<i>о времени</i>) проходить, истекать: tempus praeterit <i>Ter</i> время проходит; praeterita die <i>Cs</i> по истечении срока; tempus praeteritum <i>rhH, C etc.</i> прошедшее время; viri praeteriti <i>Prp</i> жившие прежде люди, угасшие поколения;\n<b>3)</b> опережать, обгонять (aliquem cursu <i>V</i>); превзойти (aliquem <i>O</i>; aliquem vincere et p. <i>Sen</i>); переступить (modum <i>O</i>);\n<b>4)</b> <i>перен.</i> ускользать, <i>т. е.</i> оставаться неизвестным: hoc me praeterit <i>Ter etc.</i> это мне неизвестно; non me praeterit <i>C</i> мне не безызвестно, я (очень) хорошо знаю; neminem praeterit <i>C etc.</i> всем известно;\n<b>5)</b> не упоминать, опускать, оставлять без внимания (p. peccatum, <i>v. l.</i> delictum <i>PS</i>): p. aliquid silentio <i>C etc.</i> обойти что-л. молчанием (умолчать о чём-л.); praeterita, orum <i>Hier</i> = paralipomena; non praetereundus <i>VP</i> о котором нельзя умолчать, <i>т. е.</i> заслуживающий упоминания;\n<b>6)</b> пропускать (syllabas <i>Su</i>);\n<b>7)</b> обходить, обделять (dignos <i>C</i>): aliquis praeter itur <i>Cs</i> кто-л. ничего не получает (уходит ни с чем);\n<b>8)</b> упускать: piscem p. <i>Pt</i> забыть выпотрошить рыбу;\n<b>9)</b> ускользать, избегнуть (aliquid mali p. <i>Ter</i>; poenam <i>PS</i>): erubuimus p. <i>Pt</i> мы постеснялись уклониться (<i>от выполнения общего обряда</i>)."],
  ["praeterequito", "<big>praeterequito</big>\n\n<b>praeter–equito, —, —, are</b>\nпроезжать мимо (промчаться) верхом <i>L</i>."],
  ["praetereunter", "<big>praetereunter</big>\n\n<b>praetereunter</b> [praetereo]\nмимоходом, <i>т. е.</i> вскользь, поверхностно (loqui <i>Aug</i>)."],
  ["praeterfero", "<big>praeterfero</big>\n\n<b>praeter–fero, tuli, latum, ferre</b>\nнести мимо, <i>встреч. тк.</i> <i>pass.</i> praeterferri проноситься, проходить, проезжать мимо (latebras <i>L</i>): vox praeterlata perit <i>Lcr</i> пронёсшийся звук замирает."],
  ["praeterfluo", "<big>praeterfluo</big>\n\n<b>praeter–fluo, —, —, ere</b>\n<b>1)</b> течь вдоль, протекать мимо (p. moenia <i>L</i>);\n<b>2)</b> изглаживаться, исчезать из памяти: aliquid p. sinere <i>Cato, C</i> предавать забвению (забывать) что-л."],
  ["praeterfugio", "<big>praeterfugio</big>\n\n<b>praeter–fugio, —, —, ere</b>\nпробегать мимо, <i>перен.</i> пропускать мимо ушей (aliquid <i>Ambr</i>)."],
  ["praetergredior", "<big>praetergredior</big>\n\n<b>praeter–gredior, gressus sum, gredi</b> <i>depon.</i> [gradior]\nпроходить мимо, миновать (castra <i>C, bAfr</i> <i>и</i> propter castra <i>bAfr</i>; aliquem <i>Sl</i>)."],
  ["praeterhac", "<big>praeterhac</big>\n\n<b>praeter–hac</b>\nсверх этого, кроме того, ещё: si p. unum verbum faxis <i>Pl</i> если ты скажешь ещё одно слово (<i>угроза</i>)."],
  ["praeteriens", "<big>praeteriens</big>\n\n<b>praeter–iens, euntis</b>\n<b>1.</b> <i>part. praes. к</i> praetereo;\n<b>2.</b> <i>subst. m</i> проходящий мимо, прохожий <i>H</i>."],
  ["praeteriet", "<big>praeteriet</big>\n\n<b>praeteriet</b> <i>Vlg, Jvc fut. к</i>\npraetereo."],
  ["praeterinquiro", "<big>praeterinquiro</big>\n\n<b>praeter–inquiro, —, —, ere</b>\nпродолжать исследовать (p. in negotium <i>Amm</i>)."],
  ["praeterita", "<big>praeterita</big>\n\n<b>praeterita, orum</b> <i>n</i> [praetereo]\nпрошлое, былое <i>C, VP</i>."],
  ["praeteritio", "<big>praeteritio</big>\n\n<b>praeteritio, onis</b> <i>f</i> [praetereo]\n<b>1)</b> прохождение, протекание (temporis <i>Aug</i>);\n<b>2)</b> неупоминание в завещании, <i>т. е.</i> лишение наследства (poena praeteritionis <i>CJ</i>);\n<b>3)</b> <i>ритор.</i> фигура умолчания."],
  ["praeteritus", "<big>praeteritus</big>\n\n<b>praeteritus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> praetereo;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> прошедший, прошлый, истекший (tempus <i>C etc.</i>): diebus decem praeteritis <i>Vr</i> по прошествии 10 дней;\n<b>2)</b> минувший (dolor <i>C</i>; annus <i>V etc.</i>); прежний, прежде живший, покойный (vir <i>Prp</i>);\n<b>3)</b> не уплаченный в срок (stipendium <i>L</i>)."],
  ["praeterlabor", "<big>praeterlabor</big>\n\n<b>praeter–labor, lapsus sum, labi</b> <i>depon.</i>\n<b>1)</b> мимо скользить, протекать (flumina praeterlabentia <i>Q</i>); проплывать, миновать (tellurem pelago <i>V</i>);\n<b>2)</b> <i>перен.</i> ускользать (definitio ante praeterlabitur, quam percepta est <i>C</i>)."],
  ["praeterlambo", "<big>praeterlambo</big>\n\n<b>praeter–lambo, —, —, ere</b>\n(<i>о реках</i>) омывать (oppidum <i>Amm</i>)."],
  ["praeterluo", "<big>praeterluo</big>\n\n<b>praeter–luo, —, —, ere</b>\nпротекать, омывать (fluvius praeterluens <i>Ap</i>)."],
  ["praetermeo", "<big>praetermeo</big>\n\n<b>praeter–meo, —, —, are</b>\nпроходить мимо, миновать (<i>Lcr</i> —<i>in tmesi</i>, <i>Ambr</i>); протекать мимо, омывать (tacitas ripas <i>Cld</i>); проноситься мимо (cometae stellas praetenneant <i>Sen</i>)."],
  ["praetermissio", "<big>praetermissio</big>\n\n<b>praetermissio, onis</b> <i>f</i> [praetermitto]\n<b>1)</b> пропуск, опущение <i>C</i>;\n<b>2)</b> отказ: p. aedilitatis <i>C</i> отказ от участия в соискании поста эдила."],
  ["praetermitto", "<big>praetermitto</big>\n\n<b>praeter–mitto, misi, missum, ere</b>\n<b>1)</b> пропускать (neminem <i>C</i>; nullum diem <i>C</i>); упускать (nullam occasionem <i>bAfr</i>; praetermissa frustra revocantur <i>PM</i>); пренебрегать (temperantia in praetermittendis voluptatibus cernatur <i>C</i>); не совершать, не исполнять (officium <i>C</i>); (<i>тж.</i> silentio p. <i>C</i>) <i>Just</i> оставлять без внимания, обходить молчанием, не упоминать (quod dignum memoria <i>Cs</i>); смотреть сквозь пальцы, закрывать глаза (на что-л.), попустительствовать (p. omnia <i>Lcr</i>);\n<b>2)</b> переносить, переправлять (aliquem facili unda <i>St</i>)."],
  ["praetermonstro", "<big>praetermonstro</big>\n\n<b>praeter–monstro, —, —, are</b>\nуказывать на (рекомендовать) другого, отсылать к другому <i>AG</i>."],
  ["praeternavigatio", "<big>praeternavigatio</big>\n\n<b>praeter–navigatio, onis</b> <i>f</i>\nплавание мимо, проплывание <i>PM</i> (<i>v. l.</i>)."],
  ["praeternavigo", "<big>praeternavigo</big>\n\n<b>praeter–navigo, —, —, are</b>\nплыть мимо, проплывать (sinum <i>Su</i>); огибать на корабле, миновать (Scyllam <i>Ap</i>)."],
  ["praeterpropter", "<big>praeterpropter</big>\n\n<b>praeter–propter</b>\nприблизительно <i>Cato, Vr, AG</i>; так себе, кое-как (p. vitam vivitur <i>Enn</i>)."],
  ["praeterquam", "<big>praeterquam</big>\n\n<b>praeter–quam</b> <i>adv.</i>\nкроме, за исключением, помимо: cuncta potest vetustas, p. curas attenuare meas <i>O</i> всё может ослабить время, но не мою печаль; p. si <i>PM</i> если (только) не; nihil p. <i>L</i> ничего кроме, только."],
  ["praetersum", "<big>praetersum</big>\n\n<b>praeter–sum, fui, esse</b>\nбыть вне, не участвовать (p. ipsis rebus <i>Tert</i>)."],
  ["praetervectio", "<big>praetervectio</big>\n\n<b>praetervectio, onis</b> <i>f</i> [praetervehor]\nпроезд (мимо): p. omnium <i>C</i> место проезда всех."],
  ["praetervehor", "<big>praetervehor</big>\n\n<b>praeter–vehor, vectus sum, vehi</b> <i>depon.</i>\n<b>1)</b> ехать мимо, проезжать (equo <i>L</i>; urbem <i>Cs etc.</i>); плыть мимо, проплывать (navibus Galliae oram <i>L</i>); проходить мимо (cito agmine forum <i>T</i>);\n<b>2)</b> <i>перен.</i> проходить мимо, миновать (oratio aures vestras praetervecta est <i>C</i>); p. aliquid silentio <i>C</i> обходить что-л. молчанием (умолчать о чём-л.); p. scopulos <i>C</i> обходить <i>или</i> преодолевать трудности."],
  ["praeterverto", "<big>praeterverto</big>\n\n<b>praeter–verto, —, —, ere</b>\nпроходить мимо, миновать (aliquid <i>PM</i>)."],
  ["praetervolo", "<big>praetervolo</big>\n\n<b>praeter–volo, —, —, are</b>\n<b>1)</b> летать мимо, пролетать (aquila praetervolans <i>Su</i>; p. aliquem <i>C</i>): puppe p. lacum <i>Cld</i> пронестись на судне по озеру;\n<b>2)</b> улетать, ускользать (occasionis opportunitas praetervolat <i>L</i>);\n<b>3)</b> поверхностно рассматривать, скользить (p. proposita <i>C</i>)."],
  ["praetestor", "<big>praetestor</big>\n\n<b>prae–testor, atus sum, ari</b>\nранее засвидетельствовать (aliquid <i>Tert</i>)."],
  ["praetexo", "<big>praetexo</big>\n\n<b>prae–texo, texui, textum, ere</b>\n<b>1)</b> приткать (спереди), оторочить, окаймлять, обрамлять (tunicam purpura <i>O, L</i>): toga praetexta — <i>см.</i> praetexta; fabula praetexta <i>H, C</i> трагедия, действующие лица которой одеты в toga praetexta, <i>т. е.</i> с сюжетом из римской жизни; carmen primis litteris sententiae alicujus praetexitur <i>C</i> начальные буквы (строк) стихотворения образуют чью-то фразу (<i>об акростихе</i>); aliquid lenioribus principiis p. <i>C</i> начать что-л. с небольшого; domum quercu p. <i>O</i> обсадить дом дубами; nationes Rheno praetexuntur <i>T</i> народы живут вдоль Рейна; p. hymno dialogum <i>Ap</i> предпослать гимну диалог;\n<b>2)</b> украшать спереди, <i>т. е.</i> помещать на фронтоне (Augusto praetextum nomine templum <i>O</i>);\n<b>3)</b> закрывать, прикрывать (culpam aliqua re <i>V</i>): p. funera sacris <i>V</i> под видом священных обрядов совершать погребение;\n<b>4)</b> приводить в виде предлога, ссылаться в оправдание (p. aliquid <i>C, T etc.</i>)."],
  ["praetexta", "<big>praetexta</big>\n\n<b>praetexta, ae</b> <i>f</i> (<i>sc.</i> toga)\nпретекста:\n<b>1)</b> <i>окаймлённая пурпуром тога, которую носили магистраты и жрецы, а также мальчики свободных сословий</i> (p. puerilis <i>T</i>) <i>до 11-летнего возраста, т. е. до</i> toga virilis <i>C etc.</i>;\n<b>2)</b> (<i>sc.</i> fabula) — <i>см.</i> praetexo 1."],
  ["praetextatus", "<big>praetextatus</big>\n\n<b>I praetextatus, a, um</b>\n<b>1)</b> носящий претексту (<i>см.</i> praetexta) <i>C etc.</i>;\n<b>2)</b> отроческий, относящийся к детству и отрочеству (amicitia <i>M</i>): aetas praetextata <i>AG</i> отроческий возраст (<i>до 17 лет</i>);\n<b>3)</b> неприличный, непристойный (verba <i>Su, AG, Macr</i>; mores <i>J</i>).\n\n<b>II praetextatus, i</b> <i>m</i>\nюноша, отрок <i>L, J</i>."],
  ["praetextum", "<big>praetextum</big>\n\n<b>praetextum, i</b> <i>n</i> [praetexo]\n<b>1)</b> украшение, краса (rei publicae <i>Sen</i>); блеск, пышность (triumphi <i>VM</i>);\n<b>2)</b> предлог (sub praetextu alicujus rei <i>L, Pt</i>)."],
  ["praetextus", "<big>praetextus</big>\n\n<b>I praetextus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> praetexo;\n<b>2.</b> <i>adj.</i> одетый в toga praetexta (<i>см.</i> praetexta 1) (senatus <i>Prp</i>).\n\n<b>II praetextus, us</b> <i>m</i> <i>L, Just, Pt</i>\n= praetextum."],
  ["praetimeo", "<big>praetimeo</big>\n\n<b>prae–timeo, —, —, ere</b>\nзаранее бояться <i>Pl, Sen, Eccl</i>."],
  ["praetinctus", "<big>praetinctus</big>\n\n<b>praetinctus, a, um</b> [prae + tingo]\nзаранее (предварительно) намоченный (veneno <i>O</i>; in aqua <i>CA</i>)."],
  ["praetondeo", "<big>praetondeo</big>\n\n<b>prae–tondeo, totondi, —, ere</b>\nостригать спереди (pinnas <i>Ap</i>)."],
  ["praetor", "<big>praetor</big>\n\n<b>praetor, oris</b> <i>m</i> [<i>из</i> praeitor <i>от</i> prae + eo]\n<b>1)</b> претор (<i>первоначальный титул римск. консулов и диктаторов</i>), предводитель, начальник (p. maximus <i>L</i>);\n<b>2)</b> градоправитель (<i>в Капуе</i>) <i>C</i>;\n<b>3)</b> карфагенский суффет <i>Nep</i>;\n<b>4)</b> <i>с 366 г. до н. э.</i> претор (<i>второй после консула сановник, осуществлявший верховную судебную власть</i>); <i>с 247 г. до н. э. их двое</i>: p. urbanus <i>или</i> urbis претор по делам римских граждан <i>и</i> p. peregrinus претор по делам иностранцев и по их взаимоотношениям с римскими гражданами; <i>впоследствии число преторов возросло до 16, при Нероне до 18</i> <i>C, L etc.</i>: p. primus <i>C</i> первый из избранных преторов;\n<b>5)</b> <i>иногда</i> = пропретор (pro praetore <i>или</i> propraetor), <i>т. е.</i> наместник в одной из спокойных провинций (<i>из бывших преторов <i>C</i>; как proconsul — наместник из бывших консулов в мятежной провинции</i>); <i>иногда тж.</i> = proconsul <i>C</i>;\n<b>6)</b> <i>при Августе</i> главный казначей (p. aerarii <i>T</i>);\n<b>7)</b> <i>у неримлян</i> полководец, главнокомандующий (decem praetores Atheniensium <i>Nep</i>): p. navalis <i>VP</i> командующий флотом;\n<b>8)</b> наместник (p. Darei <i>C</i>; p. Syriae <i>QC</i>)."],
  ["praetoria", "<big>praetoria</big>\n\n<b>praetoria, ae</b> <i>f</i> (<i>sc.</i> cohors)\nпреторская когорта <i>AV</i>."],
  ["praetorianus", "<big>praetorianus</big>\n\n<b>I praetorianus, a, um</b> [praetorium]\n<b>1)</b> преторианский, относящийся к преторской гвардии (miles <i>T</i>; cohors <i>PM, Su</i>): praetoriani <i>T, AV, Eutr</i> телохранители, преторианцы;\n<b>2)</b> относящийся к начальнику преторианцев-телохранителей (praefectura <i>AV</i>).\n\n<b>II praetorianus, a, um</b> [praetor]\nпреторский (tutor <i>Dig</i>; pretia <i>CJ</i>)."],
  ["praetoricius", "<big>praetoricius</big>\n\n<b>praetoricius, a, um</b> [praetor]\nпреторский: corona praetoricia <i>M</i> венок, полученный в награду от претора."],
  ["praetoriolum", "<big>praetoriolum</big>\n\n<b>praetoriolum, i</b> <i>n</i> [<i>demin. к</i> praetorium]\nзагородный домик, <i>по др.</i>— капитанская рубка <i>Vlg</i>."],
  ["praetorium", "<big>praetorium</big>\n\n<b>praetorium, i</b> <i>n</i> [praetor]\nпреторий:\n<b>1)</b> главная площадка в римском лагере вокруг палатки полководца (fit concursus in p. <i>Cs</i>);\n<b>2)</b> палатка полководца, ставка, главная квартира <i>Cs, L</i>;\n<b>3)</b> военный совет при полководце (p. dimittere <i>L</i>; in praetoriis leones, in castris lepores <i>погов. Sid</i>);\n<b>4)</b> резиденция наместника в провинции <i>C</i>;\n<b>5)</b> императорская гвардия, преторианцы (praefectus praetorii <i>или</i> praetorio <i>T, Su</i>);\n<b>6)</b> ячейка пчелиной матки <i>V</i>;\n<b>7)</b> дворец (praetoria regis <i>J</i>);\n<b>8)</b> загородный дом (praetoria atque villae <i>Su</i>);\n<b>9)</b> обиталище: Diogenis p. <i>Hier</i> = бочка Диогена."],
  ["praetorius", "<big>praetorius</big>\n\n<b>I praetorius, a, um</b> [praetor]\n<b>1)</b> предводительский, относящийся к командующему, принадлежащий полководцу: cohors praetoria <i>Cs</i> сопровождавшая полководца когорта, телохранители; navis praetoria <i>L</i> флагманское судно; porta praetoria <i>Cs</i> ворота лагеря, ближайшие к палатке полководца (и обращенные к неприятелю); imperium praetorium <i>C</i> главное начальство, главное командование;\n<b>2)</b> преторский (potestas <i>C</i>): comitia praetoria <i>L</i> преторские комиции (<i>в которых производились выборы претора</i>); vir p. <i>Su</i>, <i>тж.</i> praetoria potestate usus <i>T</i> бывший претор;\n<b>3)</b> пропреторский, наместнический (domus <i>C</i>).\n\n<b>II praetorius, i</b> <i>m</i>\n<b>1)</b> бывший претор <i>C</i>;\n<b>2)</b> имеющий звание претора <i>PJ</i>."],
  ["praetorqueo", "<big>praetorqueo</big>\n\n<b>prae–torqueo, torsi, tortum, ere</b>\nвыворачивать вперёд, <i>перен.</i> свернуть (collum injuriae <i>Pl</i>; caput <i>Col</i>)."],
  ["praetorridus", "<big>praetorridus</big>\n\n<b>prae–torridus, a, um</b>\nзнойный, палящий (aestas <i>Calp</i>)."],
  ["praetortus", "<big>praetortus</big>\n\n<b>praetortus, a, um</b> <i>part. pf. к</i>\npraetorqueo."],
  ["praetractatus", "<big>praetractatus</big>\n\n<b>prae–tractatus, us</b> <i>m</i>\nпрежнее <i>или</i> предварительное рассмотрение <i>Tert</i>."],
  ["praetracto", "<big>praetracto</big>\n\n<b>prae–tracto, —, —, are</b>\nзаранее рассматривать, наперёд обдумывать (aliquid <i>T</i> —<i>v. l.</i>)."],
  ["praetrepidans", "<big>praetrepidans</big>\n\n<b>prae–trepidans, antis</b> <i>adj.</i>\nдрожащий от нетерпения, взволнованный (mens <i>Ctl</i>)."],
  ["praetrepidus", "<big>praetrepidus</big>\n\n<b>prae–trepidus, a, um</b>\n<b>1)</b> весь трепещущий, сильно бьющийся (cor <i>Pers</i>);\n<b>2)</b> крайне робкий, боязливый (homo <i>Su</i>)."],
  ["praetritus", "<big>praetritus</big>\n\n<b>praetritus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> praefero;\n<b>2.</b> <i>adj.</i> избитый, протоптанный (vestigia <i>Hier</i>)."],
  ["praetrunco", "<big>praetrunco</big>\n\n<b>prae–trunco, —, —, are</b>\nспереди отсекать, отрубать (collos = colla <i>Pl</i>; linguam <i>Pl</i>)."],
  ["praetuli", "<big>praetuli</big>\n\n<b>praetuli</b> <i>pf. к</i>\npraefero."],
  ["praetumidus", "<big>praetumidus</big>\n\n<b>prae–tumidus, a, um</b>\n<b>1)</b> раздувшийся от чванства (potestas <i>Amm</i>);\n<b>2)</b> крайне возбуждённый, неистовый (furor <i>Cld</i>)."],
  ["praetura", "<big>praetura</big>\n\n<b>praetura, ae</b> <i>f</i> [praetor]\n<b>1)</b> претура, звание, достоинство <i>или</i> должность претора <i>C, L etc.</i>;\n<b>2)</b> (<i>в Греции</i>) звание полководца <i>C</i>."],
  ["Praetutianus", "<big>Praetutianus</big>\n\n<b>Praetutianus, a, um</b> [Praetutii]\nпретутианский (ager <i>L, PM</i>)."],
  ["Praetutii", "<big>Praetutii</big>\n\n<b>Praetutii, orum</b> <i>m</i>\nпретутии, <i>племя в южн. Пицене</i> <i>PM</i>."],
  ["Praetutius", "<big>Praetutius</big>\n\n<b>Praetutius, a, um</b> <i>PM, Sil</i>\n= Praetutianus."],
  ["praeulceratus", "<big>praeulceratus</big>\n\n<b>prae–ulceratus, a, um</b>\nуже покрытый язвами (loca <i>CA</i>)."],
  ["praeumbro", "<big>praeumbro</big>\n\n<b>prae–umbro, —, —, are</b>\nзатенять, помрачать, <i>перен.</i> затмевать (praeumbrans imperatoris fastigium <i>T</i>)."],
  ["praeunctus", "<big>praeunctus</big>\n\n<b>prae–unctus, a, um</b>\nпредварительно смазанный (adipe anserino <i>CA</i>)."],
  ["praeuro", "<big>praeuro</big>\n\n<b>prae–uro, —, ustum, ere</b>\n<b>1)</b> обжигать спереди (hasta praeusta <i>L</i>);\n<b>2)</b> отмораживать (praeusta nive membra <i>PM</i>)."],
  ["praeut", "<big>praeut</big>\n\n<b>prae–ut</b> <i>adv.</i> (<i>тж. раздельно</i>)\nв сравнении <i>Ter</i>: p. dudum fuit <i>Pl</i> в сравнении с тем, чем он раньше был; p. alia dicam <i>Pl</i> по сравнению с тем, что я ещё скажу."],
  ["praevado", "<big>praevado</big>\n\n<b>prae–vado, —, —, ere</b>\nизбегать, освобождаться (p. dictaturam <i>Sen</i> — <i>v. l.</i>)."],
  ["praevalentia", "<big>praevalentia</big>\n\n<b>prae–valentia, ae</b> <i>f</i>\nпревосходство, перевес <i>Dig</i>."],
  ["praevaleo", "<big>praevaleo</big>\n\n<b>prae–valeo, valuī, —, ēre</b>\n<b>1)</b> быть весьма сильным, крепким (praevalens homo <i>CC</i> <i>и</i> equus <i>QC</i>);\n<b>2)</b> быть действительным, целительным (contra serpentium ictus <i>PM</i>);\n<b>3)</b> быть сильнее, иметь перевес, преобладать (pugna equestri <i>T</i>; magna est veritas et praevalet <i>Vlg</i>); превосходить (mons altitudine praevalens <i>PM</i>; p. corpore <i>VP</i>): virtute praevalet sapientia <i>Ph</i> ум выше храбрости."],
  ["praevalesco", "<big>praevalesco</big>\n\n<b>prae–valēsco, —, —, ere</b> [<i>inchoat. к</i> praevaleo]\nстановиться очень крепким, крепнуть <i>Col, Eccl</i>."],
  ["praevalide", "<big>praevalide</big>\n\n<b>prae–validē</b>\nчрезвычайно сильно <i>PM</i>."],
  ["praevalidus", "<big>praevalidus</big>\n\n<b>prae–validus, a, um</b>\n<b>1)</b> очень сильный (juvenis <i>L</i>); весьма крепкий (ramus <i>Su</i>; moles <i>St</i>); весьма мощный (vires <i>Man</i>); могущественный (urbs <i>L</i>; homo <i>T</i>);\n<b>2)</b> славный, крупный, громкий (nomina equitum <i>T</i>);\n<b>3)</b> слишком плодородный (terra <i>V</i>);\n<b>4)</b> укрепившийся, укоренившийся (vitia <i>T</i>)."],
  ["praevallo", "<big>praevallo</big>\n\n<b>prae–vallo, —, —, āre</b>\nспереди укреплять, обносить валом (pontem <i>bAl</i>; amnem <i>Cld</i>)."],
  ["praevaporo", "<big>praevaporo</big>\n\n<b>prae–vaporo, —, —, are</b>\nпредварительно окуривать (loca <i>CA</i>)."],
  ["praevaricatio", "<big>praevaricatio</big>\n\n<b>praevaricatio, onis</b> <i>f</i> [praevaricor]\nдвуличие, лицемерие, вероломство (crimen praevaricationis <i>PJ</i>); (<i>в судебном деле</i>) двурушничество, тайное содействие противной стороне <i>C, PJ, Q etc.</i>"],
  ["praevaricator", "<big>praevaricator</big>\n\n<b>praevaricator, oris</b> <i>m</i> [praevaricor]\nдвурушник, лицемер, притворный поборник (causae publicae <i>C</i>); (<i>в судебном деле</i>) втайне содействующий противной стороне, тайный пособник <i>C, PJ, Q, Dig</i>."],
  ["praevaricatrix", "<big>praevaricatrix</big>\n\n<b>praevaricatrix, icis</b> <i>f</i>\nнарушительница, грешница <i>Vlg, Eccl</i>."],
  ["praevarico", "<big>praevarico</big>\n\n<b>praevarico, —, —, are</b> <i>Aug</i>\n= praevaricor."],
  ["praevaricor", "<big>praevaricor</big>\n\n<b>praevaricor, atus sum, ari</b> <i>depon.</i> [prae + varico]\n<b>1)</b> идти по непрямой линии, отклоняться в сторону (arator praevaricatur <i>PM</i>);\n<b>2)</b> действовать не прямо, лицемерить, кривить душой, поступать нечестно <i>PJ, Dig etc.</i>; (<i>в судебном деле</i>) втайне содействовать противной стороне (p. accusationi <i>C</i>)."],
  ["praevarus", "<big>praevarus</big>\n\n<b>prae–varus, a, um</b>\n<b>1)</b> весьма неправильный, с большими перебоями (venarum pulsus <i>Ap</i>);\n<b>2)</b> превратный, извращённый (quid tam praevarum? <i>C</i>)."],
  ["praevehor", "<big>praevehor</big>\n\n<b>prae–vehor, vectus sum, vehi</b> <i>depon.</i>\n<b>1)</b> выезжать вперёд (p. equo <i>V, L, Sil</i>); вылетать вперёд, носиться впереди (missilia praevehuntur <i>T</i>);\n<b>2)</b> проезжать, проноситься (cum impetu quodam <i>PJ</i>); проплывать, плыть мимо: Rhenus Germaniam praevehitur <i>T</i> Рейн протекает вдоль границы Германии."],
  ["praevello", "<big>praevello</big>\n\n<b>prae–vello, velii (vulsi), —, ere</b>\nобрывать спереди <i>или</i> заранее, <i>перен.</i> уничтожать (martyria <i>Tert</i>)."],
  ["praevelo", "<big>praevelo</big>\n\n<b>prae–velo, —, —, are</b>\nзакрывать спереди (aliquid aliqua re <i>Cld</i>)."],
  ["praevelox", "<big>praevelox</big>\n\n<b>prae–velox, ocis</b> <i>adj.</i>\nвесьма скорый, чрезвычайно быстрый (camelus <i>PM</i>); быстро схватывающий (memoria <i>Q</i>)."],
  ["praevenio", "<big>praevenio</big>\n\n<b>prae–venio, veni, ventum, ire</b>\n<b>1)</b> упреждать, опережать (hostem <i>L</i>, ales bestiam praevenit <i>Ap</i>): balneo frigus p. <i>CC</i> купаться до наступления холодов || предупреждать (desiderium alicujus <i>L</i>): morte praeventus <i>O, Just</i> настигнутый смертью;\n<b>2)</b> превосходить (vites fecunditate <i>Col</i>)."],
  ["praeventores", "<big>praeventores</big>\n\n<b>praeventores, um</b> <i>m</i> [praevenio]\nбойцы авангарда <i>Amm</i>."],
  ["praeverbium", "<big>praeverbium</big>\n\n<b>praeverbium, i</b> <i>n</i> [prae + verbum] <i>грам.</i>\nпредлог <i>или</i> приставка <i>Vr, AG</i>."],
  ["praevernat", "<big>praevernat</big>\n\n<b>prae–vernat, —, —, are</b> <i>impers.</i>\nрано наступать (<i>о весне</i>) <i>PM</i>."],
  ["praeverro", "<big>praeverro</big>\n\n<b>prae–verro, —, —, ere</b>\nнаперёд подметать, расчищать (veste vias <i>O</i>)."],
  ["praeverto", "<big>praeverto</big>\n\n<b>prae–verto</b> (<i>арх.</i> <b>-vorto</b>), <b>verti, versum, ere</b> <i>тж.</i> <i>depon.</i>\n<b>1)</b> отдавать преимущество, предпочитать (aliquid alicui rei <i>C</i> <i>и</i> prae aliqua re <i>Pl etc.</i>); уделять преимущественное внимание, прежде всего обращаться (ad aliquid <i>Col etc.</i>): illuc praevertamur <i>H</i> обратим внимание вот на что, вспомним; alia sibi praevertenda esse dixit <i>L</i> он сказал, что у него есть другие и более важные Дела; non potest bello praevertisse quidquam <i>L</i> (когда враг у ворот), нельзя ни о чём думать, кроме войны || предаваться (pigritiae <i>Pl</i>);\n<b>2)</b> опережать, обгонять (ventos equo <i>V</i>); превосходить, иметь большее значение, быть важнее (alicui rei <i>L etc.</i>);\n<b>3)</b> предотвращать, предупреждать, расстраивать, препятствовать (aliquid <i>Pl, L, O</i> <i>или</i> alicui rei <i>Cs</i>): p. usum alicujus rei <i>L</i> сделать бесполезным что-л.;\n<b>4)</b> заранее завладевать, стремиться пленить (aliquem amore <i>V</i>)."],
  ["praevertor", "<big>praevertor</big>\n\n<b>praevertor, —, verti</b> <i>depon.</i> <i>Pl, L, V etc.</i>\n= praeverto."],
  ["praevetitus", "<big>praevetitus</big>\n\n<b>prae–vetitus, a, um</b>\nстрого запрещённый (praevetitum et capital est <i>Sil</i>)."],
  ["praevexatus", "<big>praevexatus</big>\n\n<b>prae–vexatus, a, um</b>\nранее ослабленный (p. aeger viribus <i>CA</i>); подорванный (vires <i>CA</i>)."],
  ["praevians", "<big>praevians</big>\n\n<b>prae–vians, antis</b> <i>m</i> [vio]\nидущий впереди и показывающий дорогу, проводник <i>Ambr</i>."],
  ["praevideo", "<big>praevideo</big>\n\n<b>prae–video, vidi, visum, ere</b>\n<b>1)</b> заранее увидеть, издали завидеть (ictum venientem <i>V</i>);\n<b>2)</b> предвидеть, наперёд видеть (aliquem in summis periculis <i>C</i>)."],
  ["praevidus", "<big>praevidus</big>\n\n<b>prae–vidus, a, um</b>\nпредвидящий, прозорливый (pectora <i>Jvc</i>)."],
  ["praevigilo", "<big>praevigilo</big>\n\n<b>prae–vigilo, —, —, are</b>\nбыть чрезвычайно бдительным <i>Aug</i>."],
  ["praevincio", "<big>praevincio</big>\n\n<b>prae–vincio, vinxi, vinctum, ire</b>\nзаранее связывать, сковывать (laqueo <i>AG</i>; <i>перен.</i> voluptatibus praevinctus <i>AG</i>)."],
  ["praevitio", "<big>praevitio</big>\n\n<b>prae–vitio, (avi), atum, are</b>\nзаранее портить, заражать (gurgitem <i>O</i>): ex alio morbo praevitiatus <i>CA</i> уже поражённый другой болезнью."],
  ["praevius", "<big>praevius</big>\n\n<b>praevius, a, um</b> [prae + via]\nвпереди идущий, предшествующий (turba <i>O</i>; agmina <i>St</i>)."],
  ["praevolo", "<big>praevolo</big>\n\n<b>prae–volo, avi, —, are</b>\nлететь впереди (praevolantes grues <i>C</i>); обгонять на лету (aliquem <i>Aug</i>)."],
  ["praevorto", "<big>praevorto</big>\n\n<b>prae–vorto</b> <i>арх.</i>\n= praeverto."],
  ["praevulsi", "<big>praevulsi</big>\n\n<b>praevulsi</b> <i>pf. к</i>\npraevello."],
  ["pragmaticarius", "<big>pragmaticarius</big>\n\n<b>pragmaticarius, i</b> <i>m</i> (<i>греч.</i>)\nсекретарь, ведавший составлением прагматических санкций (<i>см.</i> pragmaticus 1) <i>CJ</i>."],
  ["pragmaticum", "<big>pragmaticum</big>\n\n<b>pragmaticum, i</b> <i>n</i> (<i>sc.</i> rescriptum) <i>CTh</i>\n= pragmatica sanctio."],
  ["pragmaticus", "<big>pragmaticus</big>\n\n<b>I pragmaticus, a, um</b> (<i>греч.</i>)\n<b>1)</b> деловой, сведущий, практический (homines <i>C</i>);\n<b>2)</b> <i>юр.</i> прагматический: pragmatica sanctio (jussio <i>или</i> lex) <i>CJ</i>, <i>тж.</i> pragmaticum rescriptum <i>Aug</i> прагматическая санкция, <i>т. е.</i> чрезвычайный императорский указ по особо важному публично-правовому вопросу.\n\n<b>II pragmaticus, i</b> <i>m</i>\nсудебный практик, правовед, судебный консультант <i>C, Q, J</i>."],
  ["prandeo", "<big>prandeo</big>\n\n<b>prandeo, prandi, pransum, ere</b> [prandium]\nзавтракать (ad satietatem <i>Su</i>; perbene <i>Pl</i>); есть за завтраком, закусывать (p. prandium calidum <i>Pt</i>; p. olus <i>H</i>)."],
  ["prandium", "<big>prandium</big>\n\n<b>prandium, i</b> <i>n</i>\n<b>1)</b> завтрак (adducere ali quem ad se ad p. <i>Pl</i>); закуска, трапеза (<i>вообще</i>) (abstemium p. <i>AG</i>);\n<b>2)</b> еда, корм (bubus glandem prandio depromere <i>Pl</i>)."],
  ["pransito", "<big>pransito</big>\n\n<b>pransito, avi, atum, are</b> [<i>intens. к</i> prandeo]\n<b>1)</b> часто <i>или</i> обильно завтракать (p. et cenitare <i>Lampr</i>);\n<b>2)</b> есть за завтраком (polentam <i>Pl</i>)."],
  ["pransor", "<big>pransor</big>\n\n<b>pransor, oris</b> <i>m</i> [prandeo]\nучастник завтрака, гость (к завтраку) <i>Pl, Macr, Aug</i>."],
  ["pransorius", "<big>pransorius</big>\n\n<b>pransorius, a, um</b> [pransor]\nупотребляемый за завтраком: candelabrum pransorium <i>Q</i> подсвечник, зажигавшийся во время завтрака."],
  ["pransus", "<big>pransus</big>\n\n<b>pransus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> prandeo;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> позавтракавший, поевший: curatus et p. <i>L</i> <i>или</i> p. et paratus <i>Cato, Vr</i> готовый к выступлению (к походу);\n<b>2)</b> объевшийся (p., potus <i>C</i>)."],
  ["Prasiae", "<big>Prasiae</big>\n\n<b>Prasiae, arum</b> <i>f</i>\nПрасии, <i>дем в Аттике</i> <i>L</i>."],
  ["Prasianus", "<big>Prasianus</big>\n\n<b>Prasianus, a, um</b> [Prasii]\nпрасийский (gens <i>PM</i>)."],
  ["Prasii", "<big>Prasii</big>\n\n<b>Prasii, orum</b> <i>m</i>\nпрасии, <i>племя в Индии по нижнему течению Ганга</i> <i>PM, QC</i>."],
  ["prasinatus", "<big>prasinatus</big>\n\n<b>prasinatus, a, um</b> [prasinus]\nодетый в светло-зелёное платье (ostiarius <i>Pt</i>)."],
  ["prasinianus", "<big>prasinianus</big>\n\n<b>prasinianus, i</b> <i>m</i> [prasinus]\nприверженец партии «зелёных» <i>Pt, Capit</i> (<i>эта цирковая партия была ведущей в первые годы империи и её особенно поддерживал Нерон</i>)."],
  ["prasinus", "<big>prasinus</big>\n\n<b>I prasinus, a, um</b> (<i>греч.</i>)\n<b>1)</b> светло-зелёный (color <i>PM</i>; pila <i>Pt</i>);\n<b>2)</b> относящийся к «зелёным» (<i>т. е.</i> <i>к гонщикам или наездникам на состязаниях, одевавшимся в зелёное</i>) (factio <i>Su</i>; equus <i>Capit</i>).\n\n<b>II prasinus, i</b> <i>m</i>\n<b>1)</b> гонщик <i>или</i> наездник из партии «зелёных» <i>M</i>;\n<b>2)</b> (<i>sc.</i> lapis) изумруд <i>Capit, Eccl</i>."],
  ["pratens", "<big>pratens</big>\n\n<b>pratens, entis</b> <i>adj.</i> [pratum]\nтравянисто-зелёный (viretum <i>Ap</i>)."],
  ["pratensis", "<big>pratensis</big>\n\n<b>pratensis, e</b> [pratum]\nлуговой (fungi <i>H</i>; flores <i>PM</i>)."],
  ["pratulum", "<big>pratulum</big>\n\n<b>pratulum, i</b> <i>n</i> [<i>demin. к</i> pratum]\nлужок <i>C, PJ</i>."],
  ["pratum", "<big>pratum</big>\n\n<b>pratum, i</b> <i>n</i>\n<b>1)</b> луг: prata caedere (secare) <i>PM</i> косить луга; prata Neptunia <i>C</i> просторы Нептуна, <i>т. е.</i> море;\n<b>2)</b> луговая трава <i>Pl, O</i>."],
  ["pratura", "<big>pratura</big>\n\n<b>pratura, ae</b> <i>f</i> (<i>греч.</i>)\nпродажа <i>или</i> закупка <i>Dig</i>."],
  ["prave", "<big>prave</big>\n\n<b>prave</b> [pravus]\n<b>1)</b> криво, косо (p. sectus unguis <i>H</i>);\n<b>2)</b> дурно, плохо (p. factum <i>Ter, C, H</i>); неправильно: pudens p. <i>H</i> из ложного стыда; p. facundus <i>T</i> злоупотребляя (своим) красноречием."],
  ["pravicordius", "<big>pravicordius</big>\n\n<b>pravi–cordius, a, um</b> [cor]\nнравственно порочный <i>Vlg, Aug</i>."],
  ["pravitas", "<big>pravitas</big>\n\n<b>pravitas, atis</b> <i>f</i> [pravus]\n<b>1)</b> искривлённость (membrorum <i>C</i>); кривобокость (corporis <i>C</i>); неправильность, уродливость (oris <i>C</i>);\n<b>2)</b> превратность, негодность, порочность, испорченность (morum <i>T</i>; consulum <i>L</i>): p. ominis <i>AG</i> дурное предзнаменование;\n<b>3)</b> <i>pl.</i> пороки (pravitates animi <i>C</i>)."],
  ["pravum", "<big>pravum</big>\n\n<b>pravum, i</b> <i>n</i> [pravus]\n<b>1)</b> искривлённость (elapsi in p. artus <i>T</i>);\n<b>2)</b> извращённость, порочность, дурные наклонности <i>Q</i>."],
  ["pravus", "<big>pravus</big>\n\n<b>pravus, a, um</b>\n<b>1)</b> кривой, неровный (regula <i>Lcr</i>); искривлённый, неправильный, уродливый (membra <i>C</i>);\n<b>2)</b> неправильный, превратный (sententia <i>C</i>): prava sentire de aliqua re <i>C</i> иметь превратные представления о чём-л.;\n<b>3)</b> злой, низкий (mens <i>C</i>; homo <i>VP</i>): p. fidei , <i>Sil</i> вероломный || неправый, порочный (certamen <i>L</i>; amor <i>Sen</i>); дурной, испорченный, извращённый (ingenium <i>Sl</i>): recta prava facere <i>Ter</i> представлять дурное хорошим;\n<b>4)</b> плохой, зловещий (omen <i>AG</i>). — <i>См. тж.</i> pravum."],
  ["praxis", "<big>praxis</big>\n\n<b>praxis</b> (<i>acc.</i> im) <i>f</i> (<i>греч.</i>)\nдело, способ, употребление: habere praxim <i>Pt</i> иметь особое применение."],
  ["Praxiteles", "<big>Praxiteles</big>\n\n<b>Praxiteles, is</b> <i>m</i>\nПракситель, <i>греч. ваятель первой полов. IV в. до н. э. в Афинах, известный преим. своими статуями Венеры Книдской и Амура Феспийского</i> <i>C etc.</i>"],
  ["Praxitelius", "<big>Praxitelius</big>\n\n<b>Praxitelius, a, um</b> [Praxiteles]\nпраксителев (capita <i>C</i>; Venus <i>PM</i>)."],
  ["precaria", "<big>precaria</big>\n\n<b>precaria, orum</b> <i>n</i> [precarius]\nвыпрошенное, <i>т. е.</i> данное во временное пользование (corpus suum inter p. numerare <i>Sen</i>)."],
  ["precario", "<big>precario</big>\n\n<b>precario</b> <i>adv.</i> [precarius]\n<b>1)</b> просьбами, ценою просьб (p. possidere aliquid <i>C</i>); убедительно, настоятельно (petere aliquid ab aliquo <i>Pl</i>): etiamsi p. essent rogandi <i>C</i> если бы даже пришлось их умолять;\n<b>2)</b> по чьей-л. милости, <i>т. е.</i> непрочно, ненадёжно, случайным образом (praeesse <i>T</i>; studere <i>PJ</i>)."],
  ["precarium", "<big>precarium</big>\n\n<b>precarium, i</b> <i>n</i>\nданное во временное пользование (p. est, quod precibus petenti utendum conceditur <i>Dig</i>)."],
  ["precarius", "<big>precarius</big>\n\n<b>precarius, a, um</b> [preces]\n<b>1)</b> выпрошенный, данный из милости (victus <i>QC</i>; libertas <i>L</i>);\n<b>2)</b> временный, неверный, ненадёжный, преходящий (forma <i>O</i>; imperium <i>T</i>)."],
  ["precatio", "<big>precatio</big>\n\n<b>precatio, onis</b> <i>f</i> [precor]\nпросьба, мольба <i>C etc.</i>; молитва (sollemne carmen precationis <i>L</i>)."],
  ["precativus", "<big>precativus</big>\n\n<b>precativus, a, um</b> [precor]\n<b>1)</b> просительный (pretivo modo <i>Dig</i>);\n<b>2)</b> выпрошенный (pax <i>Amm</i>)."],
  ["precator", "<big>precator</big>\n\n<b>precator, oris</b> <i>m</i> [precor]\nпроситель, ходатай, заступник <i>Pl, Ter, Macr, Amm</i>."],
  ["precatus", "<big>precatus</big>\n\n<b>precatus, us</b> <i>m</i> [precor]\nмольба, моление, молитва <i>St, Amm, Sid</i>."],
  ["preces", "<big>preces</big>\n\n<b>preces, um</b> (<i>sg.</i> *prex <i>встреч. тк. в</i> <i>dat.</i> preci, <i>acc.</i> precem <i>и</i> <i>abl.</i> prece) <i>f</i>\n<b>1)</b> просьба, мольба: omnibus precibus orare <i>C</i> <i>и</i> petere <i>Cs</i> всячески умолять; nullas preces dicere de aliqua re <i>Ap</i> не просить о чём-л.;\n<b>2)</b> молитвы, моление (p. et vota <i>C</i>);\n<b>3)</b> <i>поэт.</i> проклятия (diras p. fundere <i>T</i>);\n<b>4)</b> пожелание: dare et accipere alternas p. <i>O</i> обмениваться добрыми пожеланиями."],
  ["preciae", "<big>preciae</big>\n\n<b>preciae, arum</b> <i>f</i>\n<i>сорт скороспелого винограда</i> <i>V, Col, PM</i>."],
  ["precium", "<big>precium</big>\n\n<b>precium, i</b> <i>n</i> <i>v. l.</i>\n= pretium."],
  ["precor", "<big>precor</big>\n\n<b>precor, atus sum, ari</b> <i>depon.</i> [prex]\n<b>1)</b> настоятельно просить, молить, умолять (verba precantia <i>O</i>); взывать, воссылать моления, молиться (p. deos <i>C</i>); выпрашивать, вымаливать (veniam, salutem <i>C</i>): alicui p. <i>V</i> обращаться к кому-л. со слёзными просьбами || заступаться (nil precor <i>Ter</i>);\n<b>2)</b> призывать, желать (omnia bona <i>или</i> mala alicui <i>C</i>);\n<b>3)</b> проклинать (male alicui p. <i>C</i>)."],
  ["prehendo", "<big>prehendo</big>\n\n<b>prehendo (prendo), prehendi (prendi), prehensum (prensum), ere</b>\n<b>1)</b> хватать, схватывать (aliquem manu <i>C, QC</i>; dextram <i>Pl</i> <i>и</i> dextra <i>V</i>); ловить, поймать (servum fugitivum <i>C</i>; aliquem in furto <i>Pl</i>, furto <i>AG</i> <i>и</i> furti <i>Pl, AG</i>): aliquem cursu p. <i>V</i> догнать кого-л.; p. aliquem <i>Ter, C</i> останавливать кого-л. (для разговора), обращаться к кому-л., заговаривать с кем-л.; p. aliquem mendacii <i>Pl</i> уличить кого-л. во лжи;\n<b>2)</b> захватывать, завладевать (p. arcem <i>V</i>);\n<b>3)</b> достигать (oras Italiae <i>V</i>);\n<b>4)</b> ясно воспринимать, понимать (rerum omnium naturam <i>C</i>);\n<b>5)</b> получать, <i>т. е.</i> пускать корни (plantae prehendunt <i>Pall</i>)."],
  ["prehensio", "<big>prehensio</big>\n\n<b>prehensio (prensio), onis</b> <i>f</i> [prehendo]\n<b>1)</b> (<i>тж.</i> potestas prensionis <i>Vr</i>) взятие, схватывание, право захвата <i>Capit</i>;\n<b>2)</b> подъёмная машина, ворот (aliquid prehensionibus tollere <i>Cs</i> — <i>v. l.</i>)."],
  ["prehenso", "<big>prehenso</big>\n\n<b>prehenso, avi, atum, are</b> [<i>intens. к</i> prehendo]\n<b>1)</b> схватывать (ferrum forcipe <i>V</i>; manu brachia <i>H</i>);\n<b>2)</b> обращаться с просьбой, просить (<i>преим. о должности</i>) (p. aliquem <i>L</i>)."],
  ["Prelius lacus", "<big>Prelius lacus</big>\n\n<b>Prelius lacus</b> <i>v. l.</i>\n= Prilius lacus."],
  ["prelum", "<big>prelum</big>\n\n<b>prelum, i</b> <i>n</i> [premo]\n<b>1)</b> давильня, виноградный пресс (prelo domita uva <i>H</i>);\n<b>2)</b> каток (<i>для разглаживания платья</i>) <i>M, Cld</i>."],
  ["premo", "<big>premo</big>\n\n<b>premo, pressi, pressum, ere</b>\n<b>1)</b> давить, придавливать, топтать, попирать (aliquem pede <i>H, V</i>); жать, прижимать (aliquem ad pectora <i>V</i>); стискивать, сжимать (ferrum dextera <i>Sil</i>): aliquem rotis p. <i>O</i> переехать кого-л.; pressus et exanimatus <i>T</i> задавленный насмерть; oscula per longas pressa moras <i>O</i> долгие поцелуи; aliquid pressis manibus tenere <i>QC</i> держать что-л. в крепко сжатых руках; ora ore p. <i>O</i> целовать в губы; aliquid morsu <i>Lcr</i> <i>или</i> ore <i>O</i> p. кусать, жевать, <i>но тж.</i>: p. aliquid ore <i>V</i> умолчать о чём-л.; frena dente p. <i>O</i> кусать удила; frena manu p. <i>O</i> крепко натягивать поводья: frigore premi <i>O</i> застывать от холода, замерзать; p. terga equi <i>O</i> ехать <i>или</i> сидеть верхом на коне; p. torum <i>O</i> лежать на ложе; facie p. torum <i>Pt</i> уткнуться лицом в кровать; p. sedilia <i>O</i> сидеть на стульях; p. solum <i>St</i> ступать (ходить) по земле; p. cubitum <i>H</i> облокотиться, опираться; classes premunt mare <i>Pt</i> корабли бороздят море; p. humum <i>O</i> лечь <i>или</i> рухнуть на землю; p. columnas <i>H</i> покоиться на колоннах; p. portum <i>O</i> войти в порт; vestigia alicujus p. <i>T</i> идти по чьим-л. следам (<i>ср.</i> 12 <i>и</i> 20); forum p. <i>C</i> часто бывать (постоянно находиться) на форуме <i>или</i> не покидать форума;\n<b>2)</b> занимать, захватывать (saltus montium praesidiis p. <i>L</i>);\n<b>3)</b> нагружать (naves magno onere <i>T</i>): pressus mero <i>Prp</i> опьянённый || обременять, отягощать (juvencos jugo <i>O</i>);\n<b>4)</b> запрягать (equos curru <i>O</i>);\n<b>5)</b> покрывать, окутывать, охватывать (nix terram premit <i>O</i>); обвивать, увенчивать (crinem fronde p. <i>O</i>);\n<b>6)</b> засыпать, зарывать, хоронить (p. ossa <i>O</i>; p. aliquid terra <i>H</i>; condi tellure premi que <i>VF</i>); закрывать, затмевать (luna sole premitur <i>QC</i>): lumen p. <i>V</i> меркнуть, тускнеть || скрывать, таить (interius omne secretum <i>Sen</i>): nonum prematur in annum <i>H</i> (готовая рукопись) пусть хранится девять лет, <i>т. е.</i> не следует торопиться с её опубликованием || подавлять (gemitum sub imo corde p. <i>V</i>; curam sub corde <i>V</i>): aliquid ore p. <i>V</i> умолчать о чём-л. || превышать, превосходить: si titulos annosque tuos numerare velimus, facta premant annos <i>O</i> если бы мы захотели перечислить твои подвиги и годы, то деяний окажется больше, чем лет;\n<b>7)</b> теснить, напирать, оказывать давление (p. hostem obsidione <i>Cs</i>); донимать, беспокоить, изводить (aliquem criminibus <i>O</i>); принуждать (aliquem ad exeundum <i>Nep</i>); вынуждать (confessionem <i>Q</i>): aliquo premente <i>C</i> по чьему-л. настоянию || притеснять, угнетать (aere alieno premi <i>C, Cs</i>); nox premit aliquem <i>H</i> ночь нависла над кем-л. (<i>или</i> окутала кого-л.); aliquem verbo p. <i>C</i> поймать кого-л. на слове, придраться к чьим-л. словам;\n<b>8)</b> <i>pass.</i> premi испытывать нужду, ощущать недостаток (premi re frumentaria <i>Cs</i>); терпеть, страдать (premi ab aliquo <i>C, Nep</i>; premi aliqua valetudine <i>Nep</i>);\n<b>9)</b> преследовать, гнать (cedentem <i>L</i>; bestias <i>Is</i>); загонять (cervum ad retia <i>V</i>); непосредственно (по пятам) следовать (poena culpam premit <i>H</i>);\n<b>10)</b> прижиматься: p. latus <i>O</i> держаться в стороне; p. litus <i>H</i> держаться берега; p. aera <i>Lcr</i> летать; presso gradu <i>или</i> pede <i>L</i> нога к ноге (<i>т. е.</i> сомкнутым строем); <i>но</i>: presso gressu <i>O</i> медленным шагом; amnis insulam premit <i>O</i> река окружает остров;\n<b>11)</b> настаивать, напирать, подчёркивать (argumentum p. <i>C</i>); propositum p. <i>O</i> настаивать на своём;\n<b>12)</b> вдавливать (vestigium <i>C</i>): auro p. aliquid <i>St</i> отделывать что-л. золотом; p. vestigia <i>C</i> ступать, идти (<i>ср.</i> 20); quum decimum premeretur sidere (= sole) signum <i>O</i> когда солнце вошло в десятый знак зодиака, <i>т. е.</i> пошёл десятый месяц || вонзать, вгонять, втыкать (ensem <i>Lcn</i>; dentes in aliqua re <i>O</i>); погружать, врезывать (vomerem <i>V</i>); поражать: nube pharetrae p. <i>VF</i> осыпать тучей стрел; пронзать, прокалывать (aliquem hasta <i>V</i>);\n<b>13)</b> сажать (virgulta per agros <i>V</i>);\n<b>14)</b> обозначать, отмечать (aliquid aeterna nota <i>O</i>): vocem alicujus p. <i>V</i> крепко запомнить <i>или</i> обдумывать чьё-л. слово; <i>но тж.</i>: p. vocem <i>V, Ph</i> замолчать;\n<b>15)</b> выжимать, выдавливать (mella <i>H</i>; oleum <i>H</i>): p. ubera <i>O</i> доить; p. lac <i>или</i> caseum <i>V</i> приготовлять сыр; p. vina <i>H</i> давить виноград;\n<b>16)</b> снижать, опускать (aulaeum premitur <i>H</i>): pressa dextra <i>SenT</i> опустив правую руку; p. cursum <i>O</i> направить бег (колесницы) вниз;\n<b>17)</b> сваливать, валить, бросать на землю, уложить, сразить (aliquem <i>V, T</i>);\n<b>18)</b> рыть, выкапывать (sulcum <i>V</i>; cavernas in altitudinem <i>QC</i>);\n<b>19)</b> умалять, принижать (famam alicujus <i>T</i>); презирать (humana omnia <i>C</i>); недооценивать (arma alicujus <i>V</i>);\n<b>20)</b> тормозить, задерживать, унимать (sanguinem <i>T</i>); не пропускать (lucem <i>L</i>); натягивать (habenas <i>V</i>); прекращать, останавливать (cursum <i>C</i> — <i>ср.</i> 16); сдерживать (equos currentes <i>V</i>); ограничивать, обуздывать (cupiditates <i>Aug</i>; sermones alicujus <i>T</i>): p. vestigia <i>V</i> <i>или</i> gradum <i>VF</i> задерживать (замедлять) шаги <i>или</i> останавливаться (<i>ср.</i> 12);\n<b>21)</b> обрезывать, обстригать (vitem falce <i>H</i>);\n<b>22)</b> подчинять себе, порабощать, покорять (populos dicione <i>V</i>; ventos imperio <i>V</i>; arva jugo <i>V</i>);\n<b>23)</b> смыкать, закрывать (oculos, os <i>V</i>);\n<b>24)</b> сжимать, душить (monstra manu <i>V</i>); стягивать (collum laqueo <i>H</i>; alicui fauces <i>O</i>); сдавливать (guttur <i>V</i>);\n<b>25)</b> сокращать, излагать вкратце (quae dilatantur a nobis, Zeno sic premebat <i>C</i>);\n<b>26)</b> <i>M, Su, VF</i> = futuo (aliquam). — <i>См. тж.</i> pressus."],
  ["prendidi", "<big>prendidi</big>\n\n<b>prendidi</b> <i>Vlg pf. к</i>\nprehendo."],
  ["prendo", "<big>prendo</big>\n\n<b>prendo, prendi, prensum, ere</b>\n<i>стяж.</i>= prehendo."],
  ["prensatio", "<big>prensatio</big>\n\n<b>prensatio, onis</b> <i>f</i> [prenso]\nдомогательство, хлопоты (<i>насчёт должности; см.</i> prehenso 2) <i>C</i>."],
  ["prensio", "<big>prensio</big>\n\n<b>prensio, onis</b> <i>f</i> [prendo]\nсхватывание, арест: habere prensionem <i>Capit, Vr</i> <i>или</i> habere potestatein prensionis <i>Vr</i> иметь право арестовать."],
  ["prensito", "<big>prensito</big>\n\n<b>prensito, —, —, are</b> [<i>intens. к</i> prenso]\nсхватывать (Libitinam ipsam <i>Sid</i>)."],
  ["prenso", "<big>prenso</big>\n\n<b>prenso, avi, atum, are</b>\n<i>стяж.</i>= prehenso."],
  ["presbyter", "<big>presbyter</big>\n\n<b>presbyter, eri</b> <i>m</i> (<i>греч.</i>)\n<b>1)</b> старец, старейшина <i>Tert</i>;\n<b>2)</b> пресвитер (<i>в христ. церкви — священнический сан между епископом и диаконом</i>) <i>Vop, Amm, Eccl</i>."],
  ["presbyteratus", "<big>presbyteratus</big>\n\n<b>presbyteratus, us</b> <i>m</i>\nсан пресвитера <i>Hier</i>."],
  ["presbyterium", "<big>presbyterium</big>\n\n<b>presbyterium, i</b> <i>n</i>\nсвященство <i>или</i> духовенство <i>Vlg, Eccl</i>."],
  ["presse", "<big>presse</big>\n\n<b>presse</b> [pressus I]\n<b>1)</b> коротко (vites putare <i>Pall</i>);\n<b>2)</b> сжато, кратко (dicere <i>C</i>); коротко, не растягивая звуков (loqui <i>C</i>);\n<b>3)</b> точно, определённо (agere <i>C</i>);\n<b>4)</b> сильно, тяжело (conflictari <i>Ap</i>);\n<b>5)</b> с напряжённым вниманием (causas audire <i>C</i>)."],
  ["pressi", "<big>pressi</big>\n\n<b>pressi</b> <i>pf. к</i>\npremo."],
  ["pressim", "<big>pressim</big>\n\n<b>pressim</b> [pressus II]\n<b>1)</b> сжимая в объятиях, прижимая к себе (p. deosculari <i>Ap</i>);\n<b>2)</b> плотно, тесно (linteolo agglutinato <i>Ap</i>)."],
  ["pressio", "<big>pressio</big>\n\n<b>pressio, onis</b> <i>f</i> [premo]\n<b>1)</b> давление <i>Vtr</i>;\n<b>2)</b> подпорка, точка опоры (<i>рычага</i>) <i>Vtr, Cs</i>."],
  ["presso", "<big>presso</big>\n\n<b>presso, —, —, are</b> [<i>intens. к</i> premo]\nдавить, жать (aliquid aliqua re <i>Lcr, Macr</i>); прижимать (aliquid ad pectora <i>O</i>): p. ubera manibus <i>O</i> <i>или</i> palmis <i>V</i> доить."],
  ["pressor", "<big>pressor</big>\n\n<b>pressor, oris</b> <i>m</i>\nохотник, загоняющий дичь в сети, загонщик <i>Is</i>."],
  ["pressorium", "<big>pressorium</big>\n\n<b>pressorium, i</b> <i>n</i>\nпресс, каток (<i>для разглаживания одежды</i>) <i>Amm</i>."],
  ["pressorius", "<big>pressorius</big>\n\n<b>pressorius, a, um</b>\nдавильный (vasa <i>Col</i>)."],
  ["pressule", "<big>pressule</big>\n\n<b>pressule</b> [<i>demin. к</i> presse]\n<b>1)</b> прижимая к себе, сжимая в объятиях (exosculari <i>Ap</i>);\n<b>2)</b> тесно, плотно (adhaerere <i>Ap</i>)."],
  ["pressulus", "<big>pressulus</big>\n\n<b>pressulus, a, um</b> [<i>demin. к</i> pressus I]\nнесколько сжатый, сплюснутый, сдавленный (rotunditas <i>Ap</i>)."],
  ["pressura", "<big>pressura</big>\n\n<b>pressura, ae</b> <i>f</i> [premo]\n<b>1)</b> давление <i>Ap, Frontin</i>;\n<b>2)</b> выжимание (<i>виноградного сока, растительных масел</i>) <i>Col, PM</i>;\n<b>3)</b> выжатый сок <i>Lcn</i>;\n<b>4)</b> <i>перен.</i> давка, теснота <i>Ap</i>;\n<b>5)</b> тягота (pressurae et miseriae <i>Lact</i>);\n<b>6)</b> летаргический сон <i>CA</i>."],
  ["pressus", "<big>pressus</big>\n\n<b>I pressus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> premo;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> сгущённый: lac pressum <i>V</i> творог, сыр || сжатый, краткий (oratio <i>C</i>); немногословный (orator <i>C</i>);\n<b>2)</b> точный, определённый (Thucydides <i>C</i>);\n<b>3)</b> мерный, размеренный, (за)медленный (presso gradu incedere <i>L</i>);\n<b>4)</b> нерешительный, колеблющийся (cogitationes <i>Ap</i>); сдержанный (cunctatio <i>PJ</i>);\n<b>5)</b> насыщенный, тёмный: color viridi pressior <i>PJ</i> тёмно-зелёный цвет.\n\n<b>II pressus, us</b> <i>m</i> [premo]\n<b>1)</b> давление (ponderum <i>C</i>);\n<b>2)</b> сжатие (oris <i>C</i>)."],
  ["prester", "<big>prester</big>\n\n<b>prester, teris</b> (<i>acc. pl.</i> teras) <i>m</i> (<i>греч.</i>)\n<b>1)</b> знойный (огненный) смерч <i>Lcr, PM, Sen, Amm</i>;\n<b>2)</b> ядовитая змея (<i>укус которой вызывал жгучую жажду</i>) <i>PM, Lcn, Sol</i>."],
  ["pretiae", "<big>pretiae</big>\n\n<b>pretiae, arum</b> <i>f</i> <i>v. l.</i>\n= preciae."],
  ["pretiose", "<big>pretiose</big>\n\n<b>pretiose</b> [pretiosus]\nвеликолепно, роскошно (vasa p. caelata <i>C</i>)."],
  ["pretiositas", "<big>pretiositas</big>\n\n<b>pretiositas, atis</b> <i>f</i>\n(драго)ценность (calicis <i>Ap</i>; anuli <i>Macr</i>)."],
  ["pretiosus", "<big>pretiosus</big>\n\n<b>pretiosus, a, um</b> [pretium]\n<b>1)</b> дорогой, ценный, драгоценный (equus, res <i>C</i>; homo <i>Pt</i>);\n<b>2)</b> дорогостоящий, вводящий в большие расходы (Thais, nox <i>Prp</i>; odores <i>Col</i>);\n<b>3)</b> не стесняющийся расходами, не жалеющий денег (emptor <i>H</i>);\n<b>4)</b> богатый, изобилующий (Hispania pretiosa metallis <i>Cld</i>)."],
  ["pretium", "<big>pretium</big>\n\n<b>pretium, i</b> <i>n</i>\n<b>1)</b> цена, стоимость: p. habere <i>C</i> <i>или</i> in pretio esse <i>L, O</i> быть в цене; magni (parvi) pretii esse <i>Pl, Ter, C etc.</i> дорого (дешево) стоить; pretia jacent <i>C</i> цены низки (упали); non parvum p. accipere <i>Ap</i> продать недёшево; in pretio p. nunc est <i>O</i> ныне в цене лишь цена, <i>т. е.</i> одно лишь богатство и ценится; erat operae p. <i>Pt</i> дело стоило того;\n<b>2)</b> ценность (в деньгах): pretio <i>C</i> за деньги; magno pretio <i>C</i> дорого; parvo pretio <i>C</i> дёшево;\n<b>3)</b> выкуп (captivos sine pretio reddere <i>QC</i>): p. ob stultitiam ferre <i>Ter</i> поплатиться за свою глупость;\n<b>4)</b> плата, вознаграждение (p. laboris <i>C</i>); награда (p. certaminis <i>O</i>); воздаяние, возмездие, наказание (p. ignaviae <i>Pl</i>): pretio afficere <i>V</i> наградить; facere operae p. <i>L</i> делать нечто стоящее труда <i>или</i> не напрасно (по)трудиться; p. est (operis) <i>T, Sil etc.</i> (это) стоит сделать;\n<b>5)</b> подкуп (pretio judicem corrumpere <i>C</i>)."],
  ["prex", "<big>prex</big>\n\n<b>prex, precis</b> <i>f</i> — <i>см.</i>\npreces."],
  ["Priameis", "<big>Priameis</big>\n\n<b>Priameis, idis</b> <i>f</i>\nПриамеида, дочь Приама, <i>т. е.</i> Кассандра <i>O</i>."],
  ["Priameius", "<big>Priameius</big>\n\n<b>Priameius, a, um</b> [Priamus]\nприамов: Priameia conjunx <i>O</i> = Hecuba; Priameia virgo <i>V</i> = Cassandra."],
  ["Priamides", "<big>Priamides</big>\n\n<b>Priamides, ae</b> <i>m</i>\n<b>1)</b> Приамид, сын <i>или</i> потомок Приама <i>V, O</i>;\n<b>2)</b> <i>поэт.</i> троянец <i>Sil</i>."],
  ["Priamus", "<big>Priamus</big>\n\n<b>Priamus, i</b> <i>m</i>\nПриам, <i>сын Лаомедонта, муж Гекубы (Гекабы), от которой он имел 19 (из 50) сыновей (в том числе Гектора, Гелена, Париса, Деифоба); царь Трои; убит сыном Ахилла, Пирром</i> <i>Pl, C, O, V etc.</i>"],
  ["Priantae", "<big>Priantae</big>\n\n<b>Priantae, arum</b> <i>m</i>\nприанты, <i>народность во Фракии</i> <i>PM, Sol</i>."],
  ["Priapeia", "<big>Priapeia</big>\n\n<b>Priapeia, orum</b> <i>n</i>\nприапеи, <i>сборник стихотворений разных авторов о Приапе.</i>"],
  ["priapismus", "<big>priapismus</big>\n\n<b>priapismus, i</b> <i>m</i>\nприапизм, <i>болезненное возбуждение у мужчин</i> <i>CA</i>."],
  ["Priapus", "<big>Priapus</big>\n\n<b>Priapus, i</b> <i>m</i>\n<b>1)</b> Приап, <i>сын Вакха и Венеры, бог садов, полей, плодородия и деторождения; его статуи, выкрашенные красной охрой, выставлялись в садах для охраны их от воров и птиц</i> <i>V, H, O etc.</i>;\n<b>2)</b> <i>перен.</i> мужской член <i>J, M, Pt</i>;\n<b>3)</b> похотливый человек, сладострастник <i>Ctl, O</i>."],
  ["pridem", "<big>pridem</big>\n\n<b>pridem</b> <i>adv.</i> [<i>одного корня с</i> primus <i>и</i> priscus]\n<b>1)</b> давно (jam p. <i>C</i>; non p. <i>Ter</i>): non (haud) ita p. <i>C, H</i> не так уж давно;\n<b>2)</b> некогда, прежде, когда-то <i>Pl, C etc.</i>; недавно, незадолго перед тем <i>Just</i>."],
  ["pridianus", "<big>pridianus</big>\n\n<b>pridianus, a, um</b> [pridie]\nвчерашний, относящийся к предыдущему дню <i>Ctl, PM, Su etc.</i>"],
  ["pridie", "<big>pridie</big>\n\n<b>pri–die</b>\n<b>1)</b> днём раньше, за день до чего-л., накануне (p. ejus diei <i>или</i> p. eum diem <i>C</i>; p. incipere, postero die finire <i>Pt</i>);\n<b>2)</b> прежде, раньше (non ita, ut p. <i>Ap</i>)."],
  ["Priene", "<big>Priene</big>\n\n<b>Priene, es</b> <i>f</i>\nПриена, <i>ионийский город в Карий, к сев. от Милета, на крутом склоне горы Микале; родина Бианта</i> (<i>см.</i> Bias) <i>C</i>."],
  ["Prienenses", "<big>Prienenses</big>\n\n<b>Prienenses, ium</b> <i>m</i>\nжители города Priene <i>VM</i>."],
  ["Prilius lacus", "<big>Prilius lacus</big>\n\n<b>Prilius lacus</b>\n<i>озеро у побережья Этрурии</i> (<i>ныне</i> Lago di Castiglione) <i>C</i>."],
  ["primae", "<big>primae</big>\n\n<b>primae, arum</b> <i>f</i> (<i>sc.</i> partes)\n<b>1)</b> главная роль: primas agere <i>или</i> tenere <i>C</i> играть главную роль;\n<b>2)</b> первое место (primas alicui concedere <i>или</i> dare <i>C</i>);\n<b>3)</b> высшая награда, главный приз (primas ferre <i>или</i> tenere <i>C</i>): primas auferre <i>C</i> одержать верх, восторжествовать."],
  ["primaevitas", "<big>primaevitas</big>\n\n<b>primaevitas, atis</b> <i>f</i>\nранний возраст, детство <i>или</i> младенчество <i>Aug</i>."],
  ["primaevus", "<big>primaevus</big>\n\n<b>primaevus, a, um</b> [primus + aevum]\nочень молодой, юный: primaevo flore <i>V</i> на заре юности."],
  ["primanus", "<big>primanus</big>\n\n<b>primanus, i</b> <i>m</i> [primus]\nсолдат первого легиона <i>T, Capit</i>."],
  ["primarius", "<big>primarius</big>\n\n<b>primarius, a, um</b> [primus]\nодин из первых (locus <i>C</i>); знатный, влиятельный (vir <i>C</i>)."],
  ["primas", "<big>primas</big>\n\n<b>primas, atis</b> <i>m, f</i> <i>Ap, Amm, CTh, Eccl</i>\n= primarius."],
  ["primatus", "<big>primatus</big>\n\n<b>primatus, us</b> <i>m</i> [primas]\nпервое место, старшинство, примат <i>Vr, PM</i>."],
  ["prime", "<big>prime</big>\n\n<b>prime</b> [primus]\nособенно, исключительно <i>Naev, Pl</i>."],
  ["primiceriatus", "<big>primiceriatus</big>\n\n<b>primiceriatus, us</b> [primicerius]\nпримицериат, руководящий пост <i>CJ, Eccl</i>."],
  ["primicerius", "<big>primicerius</big>\n\n<b>primi–cerius, i</b> <i>m</i> [cera]\nчьё имя стоит первым на восковых табличках (tabulae ceratae), <i>т. е.</i> начальник, глава, управляющий <i>CTh, CJ, Veg, Amm</i>."],
  ["primigenes", "<big>primigenes</big>\n\n<b>primi–genes</b> <i>m</i>\nпервородный, первенец <i>Ambr</i>."],
  ["Primigenia", "<big>Primigenia</big>\n\n<b>Primigenia, ae</b> <i>f</i>\n«первородная», «прирождённая», <i>эпитет Фортуны, сопровождающей своих избранников с самого их рождения</i> <i>C</i>."],
  ["primigenius", "<big>primigenius</big>\n\n<b>primigenius, a, um</b> [primus + gigno]\nперворождённый, первичный (semina <i>Vr</i>); первоначальный, исходный (nomina, pronomina <i>Is</i>)."],
  ["primigenus", "<big>primigenus</big>\n\n<b>primigenus, a, um</b> <i>Lcr</i>\n= primigenius."],
  ["primipara", "<big>primipara</big>\n\n<b>primipara, ae</b> <i>f</i> [primus + pario]\nвпервые рожающая <i>или</i> родившая (<i>о самках животных</i>) <i>PM</i>."],
  ["primipilaris", "<big>primipilaris</big>\n\n<b>primipilaris, is</b> <i>m</i> [primipilus]\n<b>1)</b> центурион примипилов (<i>первой центурии первого манипула первой когорты</i>) <i>Sen, T, Su</i>;\n<b>2)</b> бывший центурион примипилов <i>Q</i>;\n<b>3)</b> (<i>в императ. Риме</i>) военный интендант <i>Dig, CJ</i>;\n<b>4)</b> епископ <i>Sid</i>."],
  ["primipilarius", "<big>primipilarius</big>\n\n<b>primipilarius, i</b> <i>m</i> <i>CTh</i>\n= primipilaris."],
  ["primipilatus", "<big>primipilatus</big>\n\n<b>primipilatus, us</b> <i>m</i>\nдолжность военного интенданта <i>CJ</i>."],
  ["primipilus", "<big>primipilus</big>\n\n<b>primipilus, i</b> <i>m</i> <i>Cs</i>\n= primipilaris."],
  ["primipotens", "<big>primipotens</big>\n\n<b>primipotens, entis</b> <i>adj.</i> [primus + potens]\nпервый по могуществу, самый могущественный (deus <i>Ap</i>)."],
  ["primiscriniatus", "<big>primiscriniatus</big>\n\n<b>primiscriniatus, us</b> <i>m</i>\nпост примискриния <i>Eccl</i>."],
  ["primiscrinius", "<big>primiscrinius</big>\n\n<b>primiscrinius, i</b> <i>m</i> (= primicerius scrinii)\nпримискриний, <i>т. е.</i> начальник канцелярии <i>или</i> секретариата <i>CJ</i>."],
  ["primitiae", "<big>primitiae</big>\n\n<b>primitiae, arum</b> <i>f</i> [primus]\nпервые плоды, первинки (p. frugum <i>O</i>); первые начатки, начало: p. metallorum <i>T</i> металлическая руда; miserae p. <i>V</i> несчастливое начало; p. lacrimarum <i>St</i> первые слёзы; simus a primitiis hilares <i>Pt</i> будем по-прежнему веселы."],
  ["primitivus", "<big>primitivus</big>\n\n<b>primitivus, a, um</b>\nпервый, самый ранний (flores <i>Col</i>)."],
  ["primitus", "<big>primitus</big>\n\n<b>primitus</b> <i>adv.</i> [primus]\n<b>1)</b> впервые: fulmen detulit mortalibus ignem p. <i>Lcr</i> молния впервые принесла людям огонь;\n<b>2)</b> вначале, сначала (ego p. nesciebam, ubi essem <i>Pt</i>)."],
  ["primo", "<big>primo</big>\n\n<b>primo</b>\n<b>1)</b> впервые <i>Pl, Ter, C etc.</i>;\n<b>2)</b> во-первых (p.... denique <i>St</i>); прежде всего, сперва, вначале: quum p. <i>L</i> как только."],
  ["primocreatus", "<big>primocreatus</big>\n\n<b>primo–creatus, a, um</b>\nпервозданный <i>Ambr</i>."],
  ["primogenita", "<big>primogenita</big>\n\n<b>primogenita, orum</b> <i>n</i>\nправо первородства <i>Aug</i>."],
  ["primogenitalis", "<big>primogenitalis</big>\n\n<b>primogenitalis, e</b> <i>Tert</i>\n= primogenitus."],
  ["primogenitus", "<big>primogenitus</big>\n\n<b>primogenitus, a, um</b>\nпервородный (vitulus <i>Pall</i>; filius <i>Lact</i>)."],
  ["primogenius", "<big>primogenius</big>\n\n<b>primogenius, a, um</b> <i>v. l.</i>\n= primigenius."],
  ["primopilaris", "<big>primopilaris</big>\n\n<b>primopilaris, is</b> <i>m</i> <i>v. l.</i>\n= primipilaris."],
  ["primoplastus", "<big>primoplastus</big>\n\n<b>primo–plastus, a, um</b> (<i>лат.-греч.</i>) <i>Eccl</i>\n= primocreatus."],
  ["primor", "<big>primor</big>\n\n<b>prīmor</b> <i>v. l.</i>\n= primoris."],
  ["primordialis", "<big>primordialis</big>\n\n<b>prīmōrdiālis, e</b> [primordium]\nпервоначальный (causa <i>Amm</i>; lex <i>Tert</i>)."],
  ["primordialiter", "<big>primordialiter</big>\n\n<b>prīmōrdiāliter</b>\nпервоначально <i>Aug</i>."],
  ["primordium", "<big>primordium</big>\n\n<b>prīmōrdium, ī</b> <i>n</i> [primus + ordior] <i>преим. pl.</i>\n<b>1)</b> начало, первоначало, основа (primordia rerum <i>C</i>): primordia dicendi <i>Q</i> вступление;\n<b>2)</b> возникновение, зарождение, происхождение (gentis <i>O</i>; mundi <i>O</i>);\n<b>3)</b> начало царствования <i>T</i>."],
  ["primoris", "<big>primoris</big>\n\n<b>prīmōris, e</b> [primus]\n<b>1)</b> первый, самый ранний: primores dentes <i>PM</i> самые ранние зубы, <i>но тж.</i> передние зубы, <i>т. е.</i> резцы;\n<b>2)</b> передний (pars <i>AG</i>): in primore libro <i>AG</i> в начале книги; digitulis primoribus <i>Pl</i> кончиками пальцев; primoribus labris attingere (gustare) aliquid <i>погов. C</i> чуть хлебнуть чего-л., поверхностно ознакомиться с чём-л.;\n<b>3)</b> первый, знатнейший (feminae <i>T</i>; civitatis <i>L</i>); важнейший, главный (venti <i>AG</i>);\n<b>4)</b> передовой (dimicare inter primores <i>QC</i>)."],
  ["primulum", "<big>primulum</big>\n\n<b>primulum</b> <i>adv.</i> [primulus]\nв самом начале, с самого начала <i>Pl, Ter</i>."],
  ["primulus", "<big>primulus</big>\n\n<b>primulus, a, um</b> [<i>demin. к</i> primus]\nпервый: primulo diluculo <i>Pl</i> чуть свет."],
  ["primum", "<big>primum</big>\n\n<b>I primum, i</b> <i>n</i> [primus]\n<i>преим. pl.</i>\n<b>1)</b> первое, находящееся впереди (prima postrema palam recitare <i>L</i>); авангард (in primo ire <i>Sl</i>); <i>тж.</i> <i>pl.</i> prima <i>L, QC</i> авангардная стычка; <i>pl.</i> передовые отряды: prima tenere <i>V</i> (habere <i>Sl</i>) <i>перен.</i> занимать первые места; in (cum) primis <i>C etc.</i> <i>и</i> ad prima <i>V</i> преимущественно, особенно, <i>тж.</i> весьма;\n<b>2)</b> начало (a primo ad extremum <i>C</i> <i>и</i> ad ultimum <i>Pt</i>): in primo <i>C, L</i> вначале <i>и</i> <i>Pt</i> прежде всего; prima belli <i>L</i> начало войны;\n<b>3)</b> <i>pl.</i> предпосылки (extrema primis respondent <i>C</i>); первоначала, принципы <i>Lcr, C</i>.\n\n<b>II primum</b> <i>adv.</i> [primus]\n<b>1)</b> сначала, сперва: p. omnium <i>Pl, Ter, C etc.</i> прежде всего <i>и</i> с самого начала;\n<b>2)</b> впервые (tum p. <i>C etc.</i>; p. consul <i>C</i>);\n<b>3)</b> во-первых (p... deinde... tum... denique <i>C, Cs, VP etc.</i>);\n<b>4)</b>: ubi (quum, ut, simul, simulac) p. <i>C, L etc.</i> как (лишь) только; quam p. <i>C etc.</i> как можно скорее (раньше)."],
  ["primumdum", "<big>primumdum</big>\n\n<b>primum–dum</b> <i>adv.</i>\nпрежде, сначала: p. omnium <i>Pl</i> прежде всего."],
  ["primus", "<big>primus</big>\n\n<b>primus, a, um</b> [<i>superl. к</i> prior]\n<b>1)</b> передний (dentes <i>PM</i>; pedes <i>O</i>); ближайший (dies <i>VF</i>); первый (liber <i>C</i>; initium <i>L</i>); передовой, головной, начальный, образующий начало: via prima <i>O</i> начало пути; prima impedimenta <i>Cs</i> голова обоза; primum agmen <i>Cs etc.</i> авангард; primi hostium <i>VP</i> передовые посты неприятеля; prima sapientia <i>H</i> начало мудрости; primum saxum <i>O</i> край скалы; in prima epistula <i>C</i> в начале письма; prima luce <i>QC</i> на рассвете; prima nocte <i>Cs, Nep</i> с наступлением ночи; primo adventu <i>Cs</i> тотчас же по приходе; prima juventa <i>T</i> в ранней юности; primo anno <i>Col</i> в начале года; prima urbs <i>Sil</i> окраина города, предместье; primis labris gustare aliquid <i>погов. C</i> чуть хлебнуть чего-л., <i>т. е.</i> иметь очень поверхностное представление о чём-л.;\n<b>2)</b> восходящий (sol <i>V</i>): prima luna <i>PM</i> новолуние;\n<b>3)</b> свежий, молодой (terra <i>VF</i>; tellus <i>H</i>);\n<b>4)</b> главный, лучший, виднейший, важнейший, высший (homines <i>C, Cs</i>; opera <i>C</i>): aliquid primum putare <i>Ter, St</i> считать что-л. самым важным; primae partes <i>Ter, C</i> главная роль; in primis stare <i>Nep</i> находиться в первых рядах; primae magnitudinis <i>Pt</i> огромной величины, крупнейший; rostri prima duritia <i>Ap</i> клюв (попугая) отличается замечательной твёрдостью;\n<b>5)</b>: p. quisque каждый следующий, каждый по очереди, один за другим (primum quidque explicemus <i>C</i>); первый попавшийся, любой: primo quoque tempore <i>C</i> при первой возможности, по какому-л. первому же поводу. — <i>См. тж.</i> primae <i>и</i> primum."],
  ["princeps", "<big>princeps</big>\n\n<b>I princeps, cipis</b> <i>adj.</i> [primus + capio]\n<b>1)</b> первый (Hannibal p. in proelium ibat <i>L</i>): exordium p. omnium esse debet <i>C</i> вступление должно быть на первом месте;\n<b>2)</b> главный, лучший (p. amicorum <i>C</i>); крупнейший, значительнейший (principes philosophi <i>Nep</i>); основной (principem locum tenere <i>C</i>); виднейший (civis <i>C</i>; Juno dearum p. <i>O</i>): p. ad aliquid <i>C, L</i> особенно пригодный для чего-л.;\n<b>3)</b> знатный (Sophocles principe loco genitus <i>PM</i>).\n\n<b>II princeps, cipis</b> <i>m, f</i> [primus + capio]\n<b>1)</b> глава (Stoicorum <i>C</i>); руководитель (legationis <i>L</i>); предводитель (equitatus <i>QC</i>); вожак, главарь (conjurationis <i>C</i>): p. senatus <i>L</i> старший из сенаторов (<i>имя которого ставилось первым в списке и который при голосовании первым приглашался высказаться</i>); p. juventutis <i>C, T</i> первый в списке всадников, <i>в императорск. эпоху</i> сын императора, наследник престола;\n<b>2)</b> вдохновитель (ad suscipiendum aliquid <i>C</i>); творец (consilii <i>Cs, C</i>); подстрекатель, виновник (belli inferendi <i>Cs</i>); зачинщик (sceleris <i>C, Cs, Nep</i>): p. nobilitatis alicujus <i>L</i> чей-л. родоначальник; p. inveniendi <i>C</i> первый изобретатель;\n<b>3)</b> государственный (политический) деятель (duces et principes <i>C</i>);\n<b>4)</b> повелитель, властелин, владыка (Roma p. urbium <i>H</i>); принцепс, государь, император: (Augustus) cuncta nomine principis sub imperium accepit <i>T</i> Август принял верховную власть со званием принцепса;\n<b>5)</b> <i>pl.</i> принципы, <i>вначале</i> солдаты первого, <i>впоследствии</i> тяжеловооружённые бойцы второго боевого строя (<i>между</i> hastati <i>и</i> triarii <i>L</i>);\n<b>6)</b> манипул принципов (signum primi principis <i>L</i>; octavum principem ducere <i>C</i>);\n<b>7)</b> центурион (командир) принципов (primus p. prioris centuriae <i>L</i>)."],
  ["principalis", "<big>principalis</big>\n\n<b>I principalis, e</b> [princeps]\n<b>1)</b> первый, первоначальный (significatio <i>Q</i>; verba <i>AG</i>); главный, важнейший (causa <i>C</i>; quaestio <i>Q</i>);\n<b>2)</b> относящийся к главной площадке (преторию) в римском лагере (<i>см.</i> principium 5): porta p. (dextra <i>и</i> sinistra) <i>L</i> главные ворота (<i>ближайшие к преторию боковые ворота лагеря</i>); via p. <i>L</i> главная дорога (<i>посредине лагеря, впереди претория</i>);\n<b>3)</b> относящийся к принцепсу, императорский (apparatus <i>PJ</i>; majestas <i>Su</i>).\n\n<b>II principalis, is</b> <i>m</i>\nпринципал, <i>старшее должностное лицо в муниципальном городе</i> <i>Dig</i>."],
  ["principalitas", "<big>principalitas</big>\n\n<b>principalitas, atis</b> <i>f</i>\nпреимущество, превосходство <i>Macr, Tert</i>."],
  ["principaliter", "<big>principaliter</big>\n\n<b>principaliter</b> [principalis II]\n<b>1)</b> особенно, преимущественно <i>Dig, Sol</i>;\n<b>2)</b> по-императорски, как принцепс (gaudere <i>Sen</i>)."],
  ["principatus", "<big>principatus</big>\n\n<b>principatus, us</b> <i>m</i> [princeps]\n<b>1)</b> первое место, руководящая роль, первенство, верховенство, господство, гегемония (p. totius Galliae <i>Cs</i>; de principatu contendere <i>Nep</i>): principatum sententiae tenere <i>C</i> иметь право подавать голос первым; animi principatum tenere <i>C</i> управлять душевными явлениями;\n<b>2)</b> принципат, власть цезарей, императорская власть (<i>sc.</i> Neronis <i>PM</i>); <i>перен.</i> цезарь, принцепс <i>T</i>;\n<b>3)</b> <i>филос.</i> руководящее начало, принцип, основная сила <i>C</i>;\n<b>4)</b> начало, возникновение (p. temporis <i>C</i>)."],
  ["principialis", "<big>principialis</big>\n\n<b>principialis, e</b> [principium]\nизначальный, первоначальный, первозданный <i>Lcr</i>."],
  ["principio", "<big>principio</big>\n\n<b>principio, —, —, are</b>\nприступать, делать вступление <i>Aug</i>."],
  ["principium", "<big>principium</big>\n\n<b>principium, i</b> <i>n</i> [princeps]\n<b>1)</b> начало (veris <i>Sl, L</i>; anni <i>L</i>): principio <i>V</i> сначала, прежде всего; a principio <i>T</i> вначале || происхождение (p. ducere ab aliquo <i>C</i>); принцип, первопричина, первоисточник (movendi <i>C</i>; gaudia p. nostri sunt saepe doloris <i>O</i>); основа (principia cognoscere <i>C</i>); основоположение (principia philosophiae <i>C</i>); элемент, стихия (quattuor genera principiorum <i>C</i>); вступление (orationis <i>L</i>; cenae <i>Pt</i>);\n<b>2)</b> основоположник, создатель (moris <i>O</i>): avorum p. <i>Sil</i> родоначальник;\n<b>3)</b> курия, подававшая голос первой (= praerogativa) <i>L</i>;\n<b>4)</b> <i>воен. pl.</i> первые ряды, передовая линия, фронт (equites post principia coilocare <i>L</i>);\n<b>5)</b> <i>pl.</i> главная площадка в лагере <i>C, Nep, L</i>;\n<b>6)</b> <i>pl.</i> главная ставка (= praetorium) <i>Frontin</i>."],
  ["principor", "<big>principor</big>\n\n<b>principor, —, ari</b>\nгосподствовать (in nationes <i>Lact</i>; alicui <i>Sid</i>; <i>перен.</i> in nostro corpore <i>Aug</i>)."],
  ["prinus", "<big>prinus</big>\n\n<b>prinus, i</b> <i>f</i> (<i>греч.</i>) <i>Vlg</i>\n= ilex."],
  ["prior", "<big>prior</big>\n\n<b>prior, ius</b> (<i>gen.</i> prioris) <i>adj.</i> [<i>compar.</i> <i>к</i> <i>арх.</i> pri- = prae]\n<b>1)</b> передний (pes <i>Nep</i>; pars <i>PM</i>); ближайший (fossa <i>Cs</i>); прежний, предыдущий (consul anni prioris <i>L</i>); cunctis jam prioribus gnaris <i>Ap</i> так как все уже были знакомы с ранее изложенным || прошлый, прошедший, истекший (nox <i>C</i>): anno priore <i>Pt</i> в прошлом году || первый (Caesar p. locum occupavit <i>Cs</i>);\n<b>2)</b> старший (Dionysius <i>Nep</i>);\n<b>3)</b> более значительный <i>или</i> лучший (aliquis p. habetur <i>L</i>; nil prius neque fortius <i>Ter</i>); более важный (cura <i>VF</i>): partes priores <i>C</i> преимущество; p. sapientia <i>Sl</i> более мудрый; p. numero <i>Sl</i> более многочисленный; p. omnibus <i>Ap</i> первейший из всех, самый главный;\n<b>4)</b> ранний (pueritia <i>AG</i>);\n<b>5)</b> умерший, покойный (priores fratres, <i>sc.</i> Constantii <i>Amm</i>): priores <i>V, O, PM etc.</i> прежние поколения, предшественники, предки;\n<b>6)</b> <i>филос.</i> (<i>поздн.</i>) более ранний, предшествующий: a priori — (познание) «ex causis ad effectum», <i>впоследствии</i> «ex notionibus», <i>т. е.</i> из «чистых» понятий, <i>следовательно</i> независимо от опыта."],
  ["prioratus", "<big>prioratus</big>\n\n<b>prioratus, us</b> <i>m</i> [prior]\nпервенство, старшинство <i>Tert</i>."],
  ["priorsum", "<big>priorsum</big>\n\n<b>prior–sum (-sus)</b>\nвперёд (accedere <i>Macr</i>)."],
  ["prisce", "<big>prisce</big>\n\n<b>prisce</b> [priscus]\nпо-старинному, по-старому, <i>перен.</i> незатейливо, просто (agere <i>C</i>)."],
  ["priscus", "<big>priscus</big>\n\n<b>priscus, a, um</b> [<i>одного корня с</i> prior]\nстаринный, древний (vir, lex, littera <i>C</i>; gens mortalium <i>H</i>): priscis temporibus <i>Pt</i> встарь || патриархальный, прадедовский (mores <i>L</i>); старомодный, старого закала (homo <i>O</i>); старый, прежний (nomen <i>O</i>)."],
  ["prista", "<big>prista</big>\n\n<b>prista, ae</b> <i>m</i> (<i>греч.</i>)\nпильщик <i>PM</i>."],
  ["pristinus", "<big>pristinus</big>\n\n<b>I pristinus, a, um</b> [prior]\n<b>1)</b> прежний, старый, старинный (mos <i>Pl</i>; tempus <i>Nep</i>): in pristinum (<i>sc.</i> statum) restituere <i>Nep</i> вернуть в прежнее состояние;\n<b>2)</b> истекший, прошлый, вчерашний (dies <i>Cs</i>).\n\n<b>II pristinus, a, um</b> [pristis]\nкитовый: pristinum sidus <i>Col</i> созвездие Кита."],
  ["pristis", "<big>pristis</big>\n\n<b>pristis, is</b> <i>V, L</i>\n<i>и</i> pistrix, tricis <i>f C, V</i> = pistris."],
  ["prius", "<big>prius</big>\n\n<b>I prius</b> <i>n</i> <i>к</i>\nprior.\n\n<b>II prius</b> <i>adv.</i>\n<b>1)</b> раньше, прежде, сначала (p.... deinde <i>C</i>): respondere priori (<i>sc.</i> epistulae) prius <i>C</i> ответить сначала на более раннее письмо; p. quam = priusquam;\n<b>2)</b> скорее, лучше (p. moriemur, quam serviemus <i>C</i>);\n<b>3)</b> встарь, давно (sed haec p. fuere <i>Ctl</i>). priusquam <i>conj.</i> прежде чем (p. promittas, deliberes <i>Sen</i>)."],
  ["privantia", "<big>privantia</big>\n\n<b>privantia, ium</b> <i>n</i> [privo] <i>грам.</i>\nотрицания <i>C</i>."],
  ["privatim", "<big>privatim</big>\n\n<b>privatim</b> [privatus]\n<b>1)</b> частным образом, от своего имени, для себя (aliquid negotii gerere <i>C</i>): maximo p. periculo <i>C</i> с величайшей для себя опасностью;\n<b>2)</b> у себя: p. se tenere <i>L</i> оставаться дома;\n<b>3)</b> на свой счёт (ditare aliquem <i>Eutr</i>)."],
  ["privatio", "<big>privatio</big>\n\n<b>privatio, onis</b> <i>f</i> [privo]\nлишение, отнятие (lucis <i>Aug</i>); освобождение (culpae <i>AG</i>); избавление (doloris <i>C, AG</i>); отрицание <i>или</i> отсутствие (malum non est nisi p. boni <i>Aug</i>)."],
  ["privativus", "<big>privativus</big>\n\n<b>privativus, a, um</b> [privo] <i>грам.</i>\nпривативный, отрицательный (particula <i>AG</i>)."],
  ["privatus", "<big>privatus</big>\n\n<b>I privatus, a, um</b> [privo]\n<b>1)</b> частный, находящийся в личной собственности (domus, navis <i>C</i>); личный (calamitas <i>C</i>; dolor <i>L</i>): ex privato <i>L</i> на личные средства <i>или</i> из своего дома; in privato <i>L, O</i> у себя дома; judicium privatum <i>C</i> частный процесс (<i>т. е.</i> по поводу спора между частными лицами); privatis quaestibus <i>Pt</i> каждый своим личным трудом;\n<b>2)</b> не занимающий государственных постов, не имеющий общественного положения (vir <i>C etc.</i>);\n<b>3)</b> обыкновенный, простой (homo <i>T, PJ</i>; carmen <i>H</i>);\n<b>4)</b> <i>воен.</i> рядовой: p. numero militis <i>Nep</i> простой солдат;\n<b>5)</b> находящийся в отставке, вне службы (imperator <i>Pl</i>).\n\n<b>II privatus, i</b> <i>m</i>\nчастный человек <i>Vr, C, Q</i>."],
  ["Privernas", "<big>Privernas</big>\n\n<b>I Privernas, atis</b> [Privernum]\nпривернский (ager <i>C</i>).\n\n<b>II Privernas, itis</b> <i>n</i>\n<b>1)</b> <i>область Приверна</i> <i>L</i>;\n<b>2)</b> <i>поместье близ Приверна</i> <i>C</i>."],
  ["Privernates", "<big>Privernates</big>\n\n<b>Privernates, ium</b> <i>m</i>\nжители Приверна <i>L</i>."],
  ["Privernum", "<big>Privernum</big>\n\n<b>Privernum, i</b> <i>n</i>\nПриверн, <i>город вольсков в Латии</i> (<i>ныне</i> Piperno) <i>V, L, VM</i>."],
  ["privigna", "<big>privigna</big>\n\n<b>privigna, ae</b> <i>f</i>\nпадчерица <i>C, Just etc.</i>"],
  ["privignus", "<big>privignus</big>\n\n<b>privignus, i</b> <i>m</i> [<i>из</i> *privigenus <i>от</i> privus + gigno]\nпасынок <i>C, H, Dig etc.</i>"],
  ["privilegiarius", "<big>privilegiarius</big>\n\n<b>privi–legiarius, i</b> <i>m</i> (<i>sc.</i> creditor)\nимеющий преимущественные права <i>Dig</i>."],
  ["privilegium", "<big>privilegium</big>\n\n<b>privilegium, i</b> <i>n</i> [privus + lex]\n<b>1)</b> закон <i>или</i> постановление, изданные в пользу <i>или</i> против одного лица, <i>т. е.</i> исключительный закон (p. ferre de aliquo <i>C</i>);\n<b>2)</b> привилегия, преимущественное право (alicui omnia privilegia dare <i>Sen, PJ</i>)."],
  ["privo", "<big>privo</big>\n\n<b>privo, avi, atum, are</b> [privus]\n<b>1)</b> отнимать, лишать (aliquem vita, somno <i>C</i>);\n<b>2)</b> избавлять, освобождать (aliquem exsilio, dolore <i>C</i>; dominis superbis <i>Lcr</i>). — <i>См. тж.</i> privatus."],
  ["privus", "<big>privus</big>\n\n<b>privus, a, um</b>\n<b>1)</b> отдельный, отдельно взятый, каждый порознь: in dies privos <i>Lcr</i> изо дня в день;\n<b>2)</b> каждый: lapides privos ferre <i>L</i> нести каждому по камню;\n<b>3)</b> особый, собственный (priva triremis <i>L</i>);\n<b>4)</b> редкостный (aliquid privum dare alicui <i>H</i>);\n<b>5)</b> своеобразный (aliquid privis vocibus ferre <i>AG</i>);\n<b>6)</b> лишённый, не имеющий (p. verae rationis <i>Ap</i>);\n<b>7)</b> освобождённый, свободный (p. militiae — <i>gen. Sl</i>)."],
  ["pro", "<big>pro</big>\n\n<b>I pro</b> <i>praep. cum abl.</i>\n1) перед, против (aciem instruere p. castris <i>Cs</i>; praesidia collocare p. templis <i>C</i>): p. consilio <i>Sl</i> перед военным советом;\n<b>2)</b> впереди, спереди: saxa mittere p. tectis <i>Sl</i> бросать камни, стоя на переднем краю крыши;\n<b>3)</b> в пользу, в защиту, за (p. patria pugnare <i>L</i> <i>и</i> mori <i>C, H</i>; oratio p. Milone <i>C</i>): p. aliquo esse <i>C</i> стоять за кого-л.;\n<b>4)</b> вместо (taleis p. nummo uti <i>Cs</i>): veniam p. laude peto <i>O</i> я прошу не похвалы, а снисхождения; pro medicina esse <i>PS</i> служить как бы лекарством; p. vili re <i>O</i> словно нечто ненужное;\n<b>5)</b> за (incerta p. certis captare <i>Sl</i>): p. consule <i>C</i> проконсул, наместник, замещающий консула || от имени, именем (loqui p. aliquo <i>Cs</i>);\n<b>6)</b> как, в качестве (aliquem p. deo colere <i>QC, Just</i>): p. amico <i>C, L</i> как друг, по-дружески; p. certo <i>C, L etc.</i> наверное, с уверенностью; p. viso <i>Cs</i> как лично виденное, как факт;\n<b>7)</b> в уплату за (pecuniam p. frumento accipere <i>L</i>); в воздаяние за (p. meritis <i>Cs</i>); в возмездие за (p. scelere <i>Cs</i>);\n<b>8)</b> по сравнению (с чём-л.), соответственно, по отношению (к чему-л.): pro multitudine hominum Helvetii angustos se fines habere arbitrabantur <i>Cs</i> гельветы считали, что по отношению к (с точки зрения) своей численности они обладали (слишком) маленькой территорией;\n<b>9)</b> сообразно, в зависимости (от чего-л.): p. tempore et p. re <i>Cs</i> в зависимости от обстоятельств; p. portione <i>Cato</i> <i>и</i> p. rata parte <i>Cs</i> в определённом соотношении, сообразно; p. viribus <i>C</i> насколько позволяют силы; p. mea (<i>или</i> virili) parte <i>C etc.</i> по мере моих (человеческих) сил; p. se quisque <i>C etc.</i> каждый в меру своей возможности;\n<b>10)</b> относительно, про (p. re pauca loquar <i>V</i>; pauca p. aliqua re verba facere <i>Sl</i>).\n\n<b>II pro!</b> (<b>proh!</b>) <i>interj.</i>\nо!, ах! (p. dii immortales! <i>C</i>; p. deorum atque hominum fidem! <i>C</i>); увы!: at mea, pro, nullo pondere verba cadunt! <i>O</i> мои же слова (Брисеиды) не имеют, увы, никакого веса!\n\n<b>III pro-</b> (<i>и</i> <b>prod-</b>)\n<i>приставка со значением</i>:\n<b>1)</b> вперёд (pro–fero, prod–eo);\n<b>2)</b> пра- (pro–avus);\n<b>3)</b> для, в пользу (pro–sum);\n<b>4)</b> вместо (pro–praetor);\n<b>5)</b> сообразно (pro–quam, pro–ut)."],
  ["proagorus", "<big>proagorus</big>\n\n<b>proagorus, i</b> <i>m</i> (<i>греч.</i>)\nпроагор, <i>первое должностное лицо в некоторых городах Сицилии</i> <i>C</i>."],
  ["proamita", "<big>proamita</big>\n\n<b>pro–amita, ae</b> <i>f</i>\nсестра деда <i>Dig</i>."],
  ["proauctor", "<big>proauctor</big>\n\n<b>pro–auctor, oris</b> <i>m</i>\nродоначальник (generis <i>Su</i>)."],
  ["proavia", "<big>proavia</big>\n\n<b>pro–avia, ae</b> <i>f</i>\nпрабабка <i>Su</i>."],
  ["proavitus", "<big>proavitus</big>\n\n<b>pro–avitus, a, um</b>\nпрадедовский, доставшийся от предков (regna <i>O</i>; rura <i>St</i>)."],
  ["proavunculus", "<big>proavunculus</big>\n\n<b>pro–avunculus, i</b> <i>m</i>\nбрат бабки <i>Dig</i>."],
  ["proavus", "<big>proavus</big>\n\n<b>pro–avus, i</b> <i>m</i> (<i>gen. pl.</i> <i>тж.</i> um)\n<b>1)</b> прадед <i>C</i>;\n<b>2)</b> предок, пращур <i>C, J, St</i>."],
  ["proba", "<big>proba</big>\n\n<b>proba, ae</b> <i>f</i> [probo]\nиспытание, проба <i>Amm, CJ</i>."],
  ["probabilis", "<big>probabilis</big>\n\n<b>probabilis, e</b> [probo]\n<b>1)</b> достойный одобрения, порядочный, изрядный, неплохой (orator, ingenium <i>C</i>);\n<b>2)</b> приемлемый, внушающий доверие, возможный, вероятный, правдоподобный (res <i>C</i>): probabilia conjectura sequi <i>C</i> путём предположения устанавливать вероятности."],
  ["probabilitas", "<big>probabilitas</big>\n\n<b>probabilitas, atis</b> <i>f</i> [probabilis]\nправдоподобие, вероятность <i>C</i>."],
  ["probabiliter", "<big>probabiliter</big>\n\n<b>probabiliter</b> [probabilis]\n<b>1)</b> достойным образом, похвально, хорошо (consulatum gerere <i>VP</i>);\n<b>2)</b> правдоподобно, с большой степенью вероятия (rem exponere, dicere <i>C</i>)."],
  ["probamentum", "<big>probamentum</big>\n\n<b>probamentum, i</b> <i>n</i> [probo]\nиспытание, проба <i>CTh</i>."],
  ["probata", "<big>probata</big>\n\n<b>probata, orum</b> <i>n</i> (<i>греч.</i>; <i>лат.</i> oves)\nовцы <i>PM</i>."],
  ["probate", "<big>probate</big>\n\n<b>probate</b>\nхорошо, отлично (nosse aliquid <i>Eccl</i>)."],
  ["probaticus", "<big>probaticus</big>\n\n<b>probaticus, a, um</b> [probata]\nовечий (porta <i>Hier</i>)."],
  ["probatio", "<big>probatio</big>\n\n<b>probatio, onis</b> <i>f</i> [probo]\n<b>1)</b> одобрение (p. multa <i>C</i>);\n<b>2)</b> видимость истины, правдоподобие <i>C</i>;\n<b>3)</b> проба, испытание, осмотр (gemmarum <i>PM</i>; athletarum <i>C</i>); смотр (equitum <i>VM</i>);\n<b>4)</b> доказательство (evidens <i>Q</i>; criminis <i>T</i>)."],
  ["probativus", "<big>probativus</big>\n\n<b>probativus, a, um</b> [probo]\nкасающийся доказательства <i>Q</i>."],
  ["probator", "<big>probator</big>\n\n<b>probator, oris</b> <i>m</i> [probo]\nвыражающий одобрение, одобряющий (p. alicujus rei <i>C, O, Ap</i>)."],
  ["probatoria", "<big>probatoria</big>\n\n<b>probatoria, ae</b> <i>f</i> [probo]\nимператорское рекомендательное письмо <i>CJ</i>."],
  ["probatus", "<big>probatus</big>\n\n<b>probatus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> probo;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> хороший, отличный (argentum <i>Pl</i>); испытанный, дельный, честный (homo, femina <i>C</i>);\n<b>2)</b> милый, дорогой (probatissimus alicui <i>C</i>): minus probatum esse alicui <i>Nep</i> не особенно нравиться кому-л.;\n<b>3)</b> приемлемый (sententia <i>AG</i>)."],
  ["probe", "<big>probe</big>\n\n<b>probe</b> [probus]\n<b>1)</b> правильно (facere <i>C</i>); хорошо (scire <i>C</i>); дельно (loqui <i>AG</i>): p. narras <i>Ter</i> ты приносишь хорошие вести; probissime! <i>Ter</i> прекрасно!;\n<b>2)</b> изрядно, сильно (errare <i>Pl</i>)."],
  ["probeat", "<big>probeat</big>\n\n<b>probeat</b> <i>Lcr</i>\n= prohibeat."],
  ["prober", "<big>prober</big>\n\n<b>prober, bra, brum</b> <i>арх. Pl, AG</i>\n= probrosus."],
  ["probet", "<big>probet</big>\n\n<b>probet</b> <i>Lcr</i>\n= prohibet."],
  ["probitas", "<big>probitas</big>\n\n<b>probitas, atis</b> <i>f</i> [probus]\nчестность, порядочность, скромность <i>C, Sl, QC, J</i>."],
  ["probiter", "<big>probiter</big>\n\n<b>probiter</b> <i>Vr</i>\n= probe."],
  ["probito", "<big>probito</big>\n\n<b>pro–bito, —, —, ere</b>\nпродвигаться вперёд (p. gradum <i>Pac</i>)."],
  ["problema", "<big>problema</big>\n\n<b>problema, atis</b> <i>n</i> (<i>gen. pl.</i> orum; <i>dat. abl. pl.</i> is) (<i>греч.</i>)\nзадание, задача, вопрос, проблема <i>Sen, Su, AG, Ap</i>."],
  ["probo", "<big>probo</big>\n\n<b>probo, avi, atum, are</b> [probus]\n<b>1)</b> испытывать, пробовать, проверять (munera <i>T</i>; mucronem ad buccam <i>Pt</i>; ignis aurum probat, miseria — fortes viros <i>Sen</i>); <i>о цензорах</i> осматривать (opera <i>L</i>); <i>воен.</i> производить (о)смотр, инспектировать (p. milites <i>Veg</i>); судить, оценивать (aliquid aliqua re <i>O</i> <i>или</i> ex aliqua re <i>C</i>);\n<b>2)</b> одобрять, удовлетворяться, благоприятно отзываться, хвалить (aliquem, aliquid <i>Pl, C etc.</i>): exitus acta probat <i>погов. O</i> исход венчает дело;\n<b>3)</b> признавать, принимать (aliquem imperatorem p. <i>Cs</i>): probata re <i>Cs</i> когда это (предложение) было принято; aliquo non probante <i>H</i> против чьей-л. воли;\n<b>4)</b> рекомендовать (libros oratorios alicui <i>C</i>); делать приятным, внушать симпатию (suam operam alicui p. <i>Cs</i>): se p. <i>или</i> <i>pass.</i> probari нравиться (Epicurus multis se probavit <i>C</i>);\n<b>5)</b> ясно представлять, с очевидностью показывать, доказывать (crimen <i>C</i>; res difficilis probatu <i>C</i>): alicui se memorem p. <i>C</i> оказаться благодарным в отношении кого-л.; aliquid pro vero p. <i>C</i> доказать истинность чего-л.; omne vitae suae tempus alicui p. <i>Ap</i> отдать кому-л. отчёт во всяком мгновении своей жизни;\n<b>6)</b> выдавать (aliquem pro aliquo <i>Ter, C</i>). — <i>См. тж.</i> probatus."],
  ["proboscis", "<big>proboscis</big>\n\n<b>proboscis, idis</b> <i>f</i> (<i>греч.</i>; <i>лат.</i> manus)\nхобот (слона) <i>Vr, bAfr, PM etc.</i>"],
  ["probrachys", "<big>probrachys</big>\n\n<b>pro–brachys</b> (<i>acc.</i> yn) <i>m</i> (<i>греч.</i>)\nпробрахий, стихотворная стопа U– – – –"],
  ["probrose", "<big>probrose</big>\n\n<b>probrose</b> [probrosus]\nпостыдно, позорно <i>AG, Amm</i>."],
  ["probrositas", "<big>probrositas</big>\n\n<b>probrositas, atis</b> <i>f</i> [probrosus]\nпостыдность, позор (morum <i>Eccl</i>)."],
  ["probrosus", "<big>probrosus</big>\n\n<b>probrosus, a, um</b>\n<b>1)</b> позорный, постыдный, мерзкий (crimen <i>C</i>);\n<b>2)</b> порочный (natura <i>Su</i>);\n<b>3)</b> позорящий, оскорбительный (carmina <i>T</i>)."],
  ["probrum", "<big>probrum</big>\n\n<b>probrum, i</b> <i>n</i> [prober]\n<b>1)</b> постыдный поступок, позорное дело: insimulare aliquem probri <i>Pl</i> обвинять кого-л. в позорном поступке;\n<b>2)</b> позор, срам: inferre alicui probrum <i>C</i> опозорить кого-л.;\n<b>3)</b> поношение, ругательство (litterae plenae probrorum <i>C</i>; dicere alicui probra <i>O</i>)."],
  ["probus", "<big>probus</big>\n\n<b>probus, a, um</b>\n<b>1)</b> хороший, добротный (merx <i>Pl</i>); доброкачественный (argentum <i>Pl, L</i>);\n<b>2)</b> испытанный, порядочный, честный (mores <i>H</i>; mulier <i>Ter</i>); дельный (faber <i>Pl</i>); скромный (vir <i>PJ</i>)."],
  ["Proca", "<big>Proca</big>\n\n<b>Proca, ae</b> <i>m</i> <i>L, O</i>\n= Procas."],
  ["procacia", "<big>procacia</big>\n\n<b>procacia, ae</b> <i>f</i> <i>Aus</i>\n= procacitas."],
  ["procacitas", "<big>procacitas</big>\n\n<b>procacitas, atis</b> <i>f</i> [procax]\nназойливость, необузданность, дерзость, бесстыдство <i>C, Nep, M, Col, T</i>."],
  ["procaciter", "<big>procaciter</big>\n\n<b>procaciter</b> [procax]\nдерзко, нагло <i>QC, T, L</i>."],
  ["Procas", "<big>Procas</big>\n\n<b>Procas, ae</b> <i>m</i>\nПрок, <i>двенадцатый царь Альбы Лонги, отец Нумитора и Амулия</i> <i>V</i>."],
  ["procatio", "<big>procatio</big>\n\n<b>procatio, onis</b> <i>f</i> [proco]\nбрачное предложение, сватовство <i>Ap</i>."],
  ["procax", "<big>procax</big>\n\n<b>procax, acis</b> <i>adj.</i> [proco]\n<b>1)</b> назойливый, дерзкий, необузданный (leno <i>Pl</i>; meretrix <i>C</i>; auster <i>V</i>);\n<b>2)</b> похотливый (feminae <i>Capit</i>; aries <i>Col</i>; manus <i>Pt</i>);\n<b>3)</b> развязный, распущенный, наглый (sermo <i>Sl</i>; scripta <i>T</i>)."],
  ["procedo", "<big>procedo</big>\n\n<b>pro–cedo, cessi, cessum, ere</b>\n<b>1)</b> выходить (e tabernaculo in solem <i>C</i>); выступать (castris <i>V</i>, e <i>или</i> de castris <i>Cs, Sl</i>); выступать, выдаваться вперёд (promunturium contra Peloponnesum procedit <i>PM</i>); проходить (aliquantum viae <i>Pl</i>); двигаться, продвигаться (a castris ad mare <i>Cs</i>): p. in numerum alicui <i>C</i> <i>перен.</i> доставлять удовлетворение кому-л.;\n<b>2)</b> приходить, появляться (in contionem <i>L</i>); возникать, зарождаться (postquam philosophia processit <i>C</i>); давать всходы, прорастать (germen procedit <i>Col</i>); восходить (astrum procedit <i>V</i>); выходить из уст, раздаваться (voces procedebant <i>T</i>);\n<b>3)</b> проходить, протекать: procedente tempore <i>PM</i>, <i>тж.</i> procedentibus annis <i>Pt</i> с течением времени; procedente aliqua re <i>Q</i> в течение (во время) чего-л.; incipient magni p. menses <i>V</i> начнётся вереница великих месяцев; in processa aetate <i>Scr</i> в преклонные годы;\n<b>4)</b> продолжаться, (успешно) продвигаться (opus procedit <i>Cs, Q</i>): stipendia <i>или</i> aera procedunt alicui <i>L</i> жалование идёт (начисляется) кому-л.; stationes procedunt <i>L</i> сторожевая служба продолжается;\n<b>5)</b> нарастать, прибавляться (luna procedit <i>Pall</i>);\n<b>6)</b> преуспевать (in philosophia <i>C</i>; studiis <i>PJ</i>): aetate p. <i>C</i> подрастать <i>или</i> стариться; morbus contactu p. <i>CC</i> болезнь распространяется через соприкосновение; nihil ei procedit <i>Sl</i> у него ничего не выходит; si (bene) processit <i>C, L</i> если посчастливится (удастся);\n<b>7)</b> доходить, достигать (quem ad finem res processit? <i>C</i>; p. Hyperidis ad famam <i>Pt</i>);\n<b>8)</b> оказывать (надлежащее) действие, действовать (venenum non processit <i>T</i>)."],
  ["proceleusmaticus", "<big>proceleusmaticus</big>\n\n<b>proceleu(s)maticus, ī</b> <i>m</i> (<i>греч.</i>)\nпрокелевматик, стихотворная стопа UUUU."],
  ["procella", "<big>procella</big>\n\n<b>procella, ae</b> <i>f</i> [procello]\n<b>1)</b> сильная буря <i>Lcr, C etc.</i>: procellae civiles <i>Nep</i> <i>или</i> temporis <i>C</i> смуты, междоусобия;\n<b>2)</b> напор, натиск, порыв (eloquentiae <i>Q</i>); стремительная атака (equestris <i>L, T</i>)."],
  ["procello", "<big>procello</big>\n\n<b>pro–cello, —, —, ere</b>\nбросать вперёд: se p. <i>Pl</i> стремительно падать, валиться, рухнуть."],
  ["procellose", "<big>procellose</big>\n\n<b>procellose</b>\nбурно, стремительно <i>Aug</i>."],
  ["procellosus", "<big>procellosus</big>\n\n<b>procellosus, a, um</b> [procella]\nбурный (шаге <i>Sen</i>; dies <i>QC</i>); порывистый, стремительный (ventus <i>L, Sen</i>)."],
  ["procer", "<big>procer</big>\n\n<b>procer, eris</b> <i>m</i> <i>J sg. к</i>\nproceres."],
  ["procere", "<big>procere</big>\n\n<b>procere</b> <i>adv.</i> (<i>встреч. тк.</i> <i>compar.</i>) [procerus]\nв длину (procerius projectum brachium <i>C</i>)."],
  ["proceres", "<big>proceres</big>\n\n<b>proceres, erum</b> <i>m</i>\nстоящие во главе, предводители (civitatis <i>T</i>); знатнейшие, знать (Latinorum <i>L</i>); <i>перен.</i> мастера, «цвет» (artis, <i>sc.</i> medicinae <i>PM</i>; sapientiae <i>PM</i>)."],
  ["proceritas", "<big>proceritas</big>\n\n<b>proceritas, atis</b> <i>f</i> [procerus]\n<b>1)</b> длина (collorum <i>C</i>); протяжённость (spatii <i>Ap</i>); высота (corporis <i>PJ</i>); высокий рост, вышина (arborum <i>C</i>);\n<b>2)</b> долгота: p. pedum <i>C</i> долгота стихотворных стоп."],
  ["proceritudo", "<big>proceritudo</big>\n\n<b>proceritudo, inis</b> <i>f</i>\nвысокий рост <i>или</i> стройность (decus proceritudinis <i>Sol</i>)."],
  ["procerulus", "<big>procerulus</big>\n\n<b>procerulus, a, um</b> [<i>demin. к</i> procerus]\nудлинённый (manus <i>Ap</i>)."],
  ["procerus", "<big>procerus</big>\n\n<b>procerus, a, um</b>\n<b>1)</b> длинный (cauda <i>Col</i>; corpus serpentis <i>Lcr</i>; collum <i>C</i>; cornua <i>Col</i>);\n<b>2)</b> высокий, стройный (statura <i>Su, Vop, Treb</i>; arundo <i>O</i>); рослый (corpus <i>PJ</i>); высокоствольный (arbores <i>PM</i>);\n<b>3)</b> протяжённый (spatium <i>AG</i>);\n<b>4)</b> долгий (syllabae <i>Vr</i>; anapestus — procerior numerus <i>C</i>)."],
  ["processio", "<big>processio</big>\n\n<b>processio, onis</b> <i>f</i> [procedo]\n<b>1)</b> движение вперёд, продвижение <i>C, Veg</i>;\n<b>2)</b> торжественный выход (<i>нового консула</i>) <i>Capit, CJ</i>;\n<b>3)</b> <i>культ.</i> процессия, крестный ход <i>Sid</i>."],
  ["processus", "<big>processus</big>\n\n<b>I processus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> procedo;\n<b>2.</b> <i>adj.</i> продвинувшийся: processior in diebus suis <i>Aug</i> состарившийся.\n\n<b>II processus, us</b> <i>m</i> [procedo]\n<b>1)</b> торжественный выход, шествие <i>Treb, Dig</i>;\n<b>2)</b> движение вперёд, продвижение (amnis <i>Sen</i>);\n<b>3)</b> <i>анат.</i> отросток, выступ <i>CC</i>;\n<b>4)</b> процесс (p. dicendi <i>C</i>); течение, ход (p. temporis <i>CTh, Amm etc.</i>; morbi <i>V</i>);\n<b>5)</b> успех, удача (in aliqua re <i>Su</i>); преуспеяние (via processus <i>J</i>);\n<b>6)</b> натиск, напор (rapidus turmarum p. <i>Amm</i>)."],
  ["Prochyta", "<big>Prochyta</big>\n\n<b>Prochyta, ae</b> <i>и</i> <b>Prochyte, es</b> <i>f</i>\nПрохита, <i>островок у побережья Кампании, близ мыса Misenum</i> (<i>ныне</i> Procida) <i>PM, V</i>."],
  ["procidentia", "<big>procidentia</big>\n\n<b>procidentia, ae</b> <i>f</i> [procido]\n<b>1)</b> выпадение, <i>т. е.</i> сильное выпячивание (oculorum, vulvarum <i>PM</i>);\n<b>2)</b> <i>грам.</i> антиптосис, замена одного падежа другим."],
  ["procido", "<big>procido</big>\n\n<b>procido, cidi, —, ere</b> [pro + cado]\n<b>1)</b> падать (pars muri prociderat <i>L</i>): ad pedes alicujus p. <i>H</i> упасть к чьим-либо ногам;\n<b>2)</b> сильно выпячиваться, выдаваться наружу (oculi procidunt <i>CC</i>)."],
  ["prociduus", "<big>prociduus</big>\n\n<b>prociduus, a, um</b> [procido]\n<b>1)</b> упавший, рухнувший (salix <i>PM</i>);\n<b>2)</b> выступающий наружу (umbilicus <i>PM</i>)."],
  ["procinctus", "<big>procinctus</big>\n\n<b>I procinctus, (us) </b> <i>m</i> [procingo]\n<b>1)</b> опоясывание, <i>перен.</i> готовность: in procinctu препоясавшийся к бою, готовый к сражению <i>PM, T, AG</i> <i>или</i> перед сражением, на поле сражения <i>C, Dig</i>, <i>перен.</i> наготове, в полной готовности (habere aliquid in procinctu <i>Q, Sen</i>);\n<b>2)</b> военный поход <i>Amm, CTh</i>;\n<b>3)</b> сражение, бой (procinctus ancipites <i>Amm</i>).\n\n<b>II procinctus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> procingo;\n<b>2.</b> <i>adj.</i> готовый к бою (classis <i>AG</i>; legiones <i>Amm</i>)."],
  ["procingo", "<big>procingo</big>\n\n<b>pro–cingo, cinxi, cinctum, ere</b>\nпрепоясываться, <i>перен.</i> готовиться, снаряжаться (<i>встреч. тк.</i> <i>part. pf.</i> procinctus, <i>см.</i>)."],
  ["proclamatio", "<big>proclamatio</big>\n\n<b>proclamatio, onis</b> <i>f</i> [proclamo]\nкрик, зов (p. gemitusque <i>Q</i>); призыв (ad libertatem <i>Dig</i>)."],
  ["proclamator", "<big>proclamator</big>\n\n<b>proclamator, oris</b> <i>m</i> [proclamo]\nкрикун, горлан (<i>о плохом адвокате</i>) <i>C</i>."],
  ["proclamo", "<big>proclamo</big>\n\n<b>pro–clamo, avi, atum, are</b>\nпровозглашать, кричать (praeco proclamavit <i>Pt</i>); распинаться (pro aliquo <i>L</i>); взывать, громко требовать (in <i>и</i> ad libertatem <i>Dig</i>); читать вслух, декламировать (cottidie <i>Ap</i>)."],
  ["Procles", "<big>Procles</big>\n\n<b>Procles, is</b> <i>и</i> <b>i</b> <i>m</i>\nПрокл, <i>миф. сын Аристодема, брат-близнец Эврисфена, царь Спарты, родоначальник Проклидов</i> <i>C, Nep</i>."],
  ["proclinatio", "<big>proclinatio</big>\n\n<b>proclinatio, onis</b> <i>f</i> [proclino]\nнаклон, покатость <i>Vtr</i>."],
  ["proclino", "<big>proclino</big>\n\n<b>pro–clino, avi, atum, are</b>\nнаклонять вперёд: p. mare in haec litora <i>O</i> благоприятствовать возвращению к этим берегам; partes proclinatae <i>O</i> наклон; res proclinata <i>Cs ap. C</i> дело, приближающееся к концу, находящееся на исходе, <i>но тж.</i> трудно поправимое (adjuvare rem proclinatam <i>Cs</i>)."],
  ["proclive", "<big>proclive</big>\n\n<b>I proclive (proclivi)</b> <i>adv.</i> [proclivis]\n<b>1)</b> под гору, вниз с горы (currere <i>C</i>);\n<b>2)</b> стремительно, стремглав (prodivius decurrere ad iras <i>Lcr</i>);\n<b>3)</b> легко (multo proclivius exoriri de aliqua re <i>Lcr</i>).\n\n<b>II proclive, is</b> <i>n</i>\nнаклон, скат, спуск <i>bAl, Col, Sen</i>."],
  ["proclivis", "<big>proclivis</big>\n\n<b>proclivis, e</b> [pro + clivus]\n<b>1)</b> покатый, отлогий, наклонный (via <i>L</i>; solum <i>Vr</i>): per (in) proclive <i>L etc.</i> с горы, под гору, вниз;\n<b>2)</b> склонный, расположенный (ad morbum <i>C</i>; sceleri <i>Sil</i>);\n<b>3)</b> лёгкий (proclive fuit tranare flumen <i>Cs</i>; proclive dictu <i>C</i>): alicui ex proclivo planum facere <i>погов. Pl</i> = разъяснить кому-л. что-л. неясное;\n<b>4)</b> преклонный, глубокий (senectus <i>Ap</i>);\n<b>5)</b> склоняющийся, близкий (Junius mensis jam p. in Julium <i>Sen</i>)."],
  ["proclivitas", "<big>proclivitas</big>\n\n<b>proclivitas, atis</b> <i>f</i> [proclivis]\n<b>1)</b> покатость, скат, спуск <i>bAfr</i>;\n<b>2)</b> склонность, расположенность (ad morbos <i>C</i>)."],
  ["procliviter", "<big>procliviter</big>\n\n<b>procliviter</b> [proclivis]\n<b>1)</b> быстро, стремительно (labi <i>C</i>);\n<b>2)</b> легко, без труда (facile et p. <i>AG</i>)."],
  ["proclivium", "<big>proclivium</big>\n\n<b>proclivium, i</b> <i>n</i> <i>Frontin</i>\n= proclive II."],
  ["proclivo", "<big>proclivo</big>\n\n<b>proclivo, —, —, are</b>\nнаклоняться, склоняться <i>Tert</i>."],
  ["proclivus", "<big>proclivus</big>\n\n<b>proclivus, a, um</b> <i>Pl, Vr, Ctl</i>\n= proclivis."],
  ["procludo", "<big>procludo</big>\n\n<b>pro–cludo, —, —, ere</b> [claudo]\nзапирать (aliquid <i>Pall</i>)."],
  ["Procne", "<big>Procne</big>\n\n<b>Procne, es</b> <i>f</i>\n<b>1)</b> Прокна, <i>сестра Филомелы</i> (<i>см.</i> Philomela) <i>O</i>;\n<b>2)</b> <i>поэт.</i> ласточка <i>V, O</i>."],
  ["Procnesus", "<big>Procnesus</big>\n\n<b>Procnesus</b> <i>VF</i>\n= Proconnesus."],
  ["proco", "<big>proco</big>\n\n<b>proco, —, —, are</b>\nтребовать, добиваться <i>LA, Vr</i>."],
  ["procoeton", "<big>procoeton</big>\n\n<b>procoeton, onis</b> <i>m</i> (<i>греч.</i>)\nпрокойтон, передняя <i>PJ</i>."],
  ["procomium", "<big>procomium</big>\n\n<b>procomium (-on), i</b> <i>n</i> (<i>греч.</i>; <i>лат.</i> antiae)\nчёлка (<i>у лошади</i>) <i>Veg</i>."],
  ["Proconnesius", "<big>Proconnesius</big>\n\n<b>Proconnesius, a, um</b> [Proconnesus]\nпроконнесский (marmor <i>Sol</i>)."],
  ["Proconnesus", "<big>Proconnesus</big>\n\n<b>Proconnesus, i</b> <i>f</i>\nПроконнес, <i>о-в в Пропонтиде, знаменитый белым мрамором</i> <i>Vtr, Mela, PM</i>."],
  ["proconsul", "<big>proconsul</big>\n\n<b>pro–consul, ulis</b> <i>m</i>\nпроконсул, наместник в провинции (<i>в эпоху республики — преим. из бывших консулов</i>) <i>C, Cs, Su</i>."],
  ["proconsularis", "<big>proconsularis</big>\n\n<b>pro–consularis, e</b>\nпроконсульский (potestas <i>Amm</i>): vir P. <i>T</i> = proconsul; p. imago <i>L</i> призрак консульства (<i>о военном трибунате</i>)."],
  ["proconsulatus", "<big>proconsulatus</big>\n\n<b>pro–consulatus, us</b> <i>m</i>\n<b>1)</b> должность <i>или</i> звание проконсула <i>PM, T, Eutr</i>;\n<b>2)</b> наместничество <i>Su</i>."],
  ["procor", "<big>procor</big>\n\n<b>procor, —, ari</b> <i>depon.</i> <i>C, Sen</i>\n= proco."],
  ["procrastinatio", "<big>procrastinatio</big>\n\n<b>procrastinatio, onis</b> <i>f</i> [procrastino]\nоткладывание, отсрочивание, затягивание <i>C, CJ</i>."],
  ["procrastino", "<big>procrastino</big>\n\n<b>procrastino, avi, atum, are</b> [pro + crastinus]\nоткладывать, отсрочивать, затягивать (rem <i>C, AG</i>)."],
  ["procreatio", "<big>procreatio</big>\n\n<b>procreatio, onis</b> <i>f</i> [procreo]\n<b>1)</b> рождение, произведение на свет (liberorum <i>C, Eccl</i>);\n<b>2)</b> произведение, плод (procreationes terrae <i>Aug</i>);\n<b>3)</b> утробный плод <i>Vtr</i>."],
  ["procreator", "<big>procreator</big>\n\n<b>procreator, oris</b> <i>m</i> [procreo]\nродитель (a procreatoribus amari <i>C</i>); создатель, творец (mundi <i>C</i>)."],
  ["procreatrix", "<big>procreatrix</big>\n\n<b>procreatrix, icis</b> <i>f</i> [procreator]\nродительница, мать <i>Eccl, PV</i>; <i>перен.</i> создательница (artium omnium <i>C</i>)."],
  ["procreo", "<big>procreo</big>\n\n<b>pro–creo, avi, atum, are</b>\n<b>1)</b> рождать, производить на свет (duos filios <i>C</i>); прижить (liberos ex tribus uxoribus <i>Nep</i>);\n<b>2)</b> вызывать, причинять, порождать (leges bonae ex malis moribus procreantur <i>Macr</i>);\n<b>3)</b> взращивать (truncos ramosque ex seminibus <i>C</i>)."],
  ["procresco", "<big>procresco</big>\n\n<b>pro–cresco, —, —, ere</b>\n<b>1)</b> вырастать, возникать <i>Lcr</i>;\n<b>2)</b> расти, увеличиваться <i>Lcr</i>."],
  ["Procris", "<big>Procris</big>\n\n<b>Procris, idis (is)</b> <i>f</i>\nПрокрида, <i>дочь аттического царя Эрехтея, сестра Орифии (Orithyia), жена Кефала, нечаянно убитая им на охоте</i> <i>V, O</i>."],
  ["Procrustes", "<big>Procrustes</big>\n\n<b>Procrustes, ae</b> <i>m</i> <i>миф.</i>\nПрокруст, <i>прозвище аттического разбойника Полипемона, который ловил путников и отрубал им ноги, если они были слишком длинны для его постели, или растягивал их, если они оказывались слишком короткими; был убит Тесеем</i> <i>O, Sen</i>."],
  ["procubitor", "<big>procubitor</big>\n\n<b>procubitor, oris</b> <i>m</i> [procumbo]\nкараульный, часовой <i>Cato</i>."],
  ["procubo", "<big>procubo</big>\n\n<b>pro–cubo, —, —, are</b>\nлежать, быть простёртым (umbra procubat <i>V</i>; Gradivus procubat in Haemo <i>Cld</i>)."],
  ["procubui", "<big>procubui</big>\n\n<b>procubui</b> <i>pf. к</i>\nprocumbo."],
  ["procubus", "<big>procubus</big>\n\n<b>procubus, a, um</b> [procumbo]\nлежащий ниц <i>Jvc</i>."],
  ["procucurri", "<big>procucurri</big>\n\n<b>procucurri</b> <i>pf. к</i>\nprocurro."],
  ["procudo", "<big>procudo</big>\n\n<b>pro–cudo, cudi, cusum, ere</b>\n<b>1)</b> выковывать, ковать (enses <i>H</i>);\n<b>2)</b> острить, точить (dentem vomeris obtusi <i>V</i>);\n<b>3)</b> образовывать, формировать (vitam legendo et scribendo <i>Vr</i>; linguam <i>C</i>);\n<b>4)</b> производить, порождать (prolem <i>Lcr</i>; ignem ignes procudunt <i>Lcr</i>);\n<b>5)</b> строить, готовить (dolos <i>Pl</i>)."],
  ["procul", "<big>procul</big>\n\n<b>I procul</b> <i>adv.</i>\n<b>1)</b> вдаль (abire <i>PM</i>); вдали, поодаль, далеко (p. a Castris <i>Cs</i>); издали (p. tela conjicere <i>Cs</i>); ввысь (p. exstruere aliquid <i>H</i>): p. errare <i>Sl</i> сильно ошибаться; aliquid p. habere <i>T</i> испытывать отвращение к чему-л., презирать что-л.;\n<b>2)</b> прочь (p. este, profani! <i>V</i>); долой (p. hinc jam foedera sunto! <i>Lcn</i>);\n<b>3)</b> долго (durare <i>St</i>).\n\n<b>II procul</b> <i>praep. cum abl.</i>\n1) вдали (далеко) от (p. vero <i>C</i>; haud p. seditione res erat <i>L</i>);\n<b>2)</b> вне (p. dubio <i>L</i>; p. periculo <i>L</i>);\n<b>3)</b> задолго до (haud p. occasu solis <i>L</i>)."],
  ["proculcatio", "<big>proculcatio</big>\n\n<b>proculcatio, onis</b> <i>f</i> [proculco]\n<b>1)</b> топтание, размешивание (ногами) (obturbata proculcatione aqua <i>PM</i>);\n<b>2)</b> разрушение (ruina et p. regni <i>Sen</i>)."],
  ["proculcator", "<big>proculcator</big>\n\n<b>proculcator, oris</b> <i>m</i>\nсолдат авангарда <i>Amm</i>."],
  ["proculcatus", "<big>proculcatus</big>\n\n<b>proculcatus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> proculco;\n<b>2.</b> <i>adj.</i> избитый, надоевший, обыденный (verba <i>AG</i>)."],
  ["proculco", "<big>proculco</big>\n\n<b>proculco, avi, atum, are</b> [pro + calco]\n<b>1)</b> топтать (uvas <i>Col</i>; nives <i>QC</i>); вытаптывать (crescentes segetes <i>O</i>);\n<b>2)</b> давить, попирать (aliquem pedibus <i>O</i>)."],
  ["Proculejus", "<big>Proculejus</big>\n\n<b>Proculejus, i</b> <i>m</i>\n(Caius) Прокулей, <i>римск. всадник, шурин Мецената, друг Октавиана, поделившийся своим состоянием с двумя братьями, которые были разорены из-за проскрипционного террора</i> <i>H</i>."],
  ["Proculus", "<big>Proculus</big>\n\n<b>Proculus, i</b> <i>m</i>\n(Julius) Прокул, <i>римск. сенатор, которому, по преданию, Ромул объявил, что он желает быть почитаемым под именем Квирина</i> <i>C</i>."],
  ["procumbo", "<big>procumbo</big>\n\n<b>pro–cumbo, cubui, cubitum, ere</b>\n<b>1)</b> нагибаться, наваливаться: certamine summo procumbunt <i>V</i> (гребцы) изо всех сил наваливаются на вёсла;\n<b>2)</b> набрасываться (in armos juvencorum <i>M</i>);\n<b>3)</b> гнуться, наклоняться: tigna secundum naturam fluminis procumbunt <i>Cs</i> брёвна имеют наклон в сторону течения реки (<i>при постройке моста</i>);\n<b>4)</b> отлого спускаться (mons in pontum procumbit <i>PM</i>);\n<b>5)</b> склоняться, погружаться, предаваться (in voluptates <i>Sen</i>);\n<b>6)</b> припадать (alicui ad pedes <i>Cs, Pt</i> <i>и</i> ad genua alicujus <i>Pt</i>); рухнуть, обрушиваться (domus procumbit in domini caput <i>O</i>); ложиться (bos procumbit humi <i>V</i>); пасть, погибать (dextra alicujus <i>V</i>): p. alicui <i>T</i> быть сражённым кем-л. (в бою);\n<b>7)</b> полегать (frumenta imbribus procubuerant <i>Cs</i>);\n<b>8)</b> рушиться, приходить в упадок (res alicujus procubuerunt <i>O</i>)."],
  ["procupido", "<big>procupido</big>\n\n<b>pro–cupido, inis</b> <i>f</i>\nпредрасположенность, предрасположение (p. amoris <i>MF</i>)."],
  ["procuratio", "<big>procuratio</big>\n\n<b>procuratio, onis</b> <i>f</i> [procuro]\n<b>1)</b> попечение, заведывание, управление (rei publicae <i>C</i>; regni <i>Sl</i>);\n<b>2)</b> должность прокуратора, прокуратура (p. Narbonensis provinciae <i>PJ</i>);\n<b>3)</b> <i>культ.</i> искупительный обряд для предотвращения бедствия, умилостивление (expiatio et p. <i>C</i>);\n<b>4)</b> усилие, старание (recipiendae gratiae <i>AG</i>)."],
  ["procuratiuncula", "<big>procuratiuncula</big>\n\n<b>procuratiuncula, ae</b> <i>f</i> [<i>demin. к</i> procuratio]\nнебольшое поручение <i>или</i> маленькая должность по заведованию (чём-л.) <i>Sen</i>."],
  ["procurator", "<big>procurator</big>\n\n<b>procurator, oris</b> <i>m</i> [procuro]\n<b>1)</b> заведующий, управляющий, распорядитель (p. urbis <i>Su</i>; p. ludi <i>T</i>); представитель, поверенный, уполномоченный (agere aliquid per procuratorem <i>C</i>): p. regni <i>Cs</i> правитель государства;\n<b>2)</b> прокуратор, заведующий доходами императора в провинции <i>PJ, T etc.</i>"],
  ["procuratorius", "<big>procuratorius</big>\n\n<b>procuratorius, a, um</b>\nпрокураторский (nomen <i>Dig</i>)."],
  ["procuratrix", "<big>procuratrix</big>\n\n<b>procuratrix, icis</b> <i>f</i> [procuro]\nзаведующая, управительница (sapientia totius hominis custos et p. <i>C</i>)."],
  ["procuro", "<big>procuro</big>\n\n<b>pro–curo, avi, atum, are</b>\n<b>1)</b> заботиться, ухаживать, холить (corpus <i>V</i>; pueros <i>Pl</i>); обеспечивать, совершать (sacrificia <i>Cs</i>): p. sacra <i>Nep</i> ведать делами культа;\n<b>2)</b> управлять, заведовать, вести (negotia alicujus <i>C</i>); ведать императорскими доходами в провинции, быть прокуратором (in Hispania <i>PJ</i>);\n<b>3)</b> <i>культ.</i> приносить умилостивительную жертву, искупать, предотвращать (monstra <i>C</i>): p. Jovi hostiis majoribus <i>AG</i> принести Юпитеру великие умилостивительные жертвы; procuratum est <i>L</i> совершены искупительные жертвоприношения."],
  ["procurro", "<big>procurro</big>\n\n<b>pro–curro, (cu)curri, cursum, ere</b>\n<b>1)</b> выбегать вперёд (ex castris <i>Cs</i>); устремляться, спешно выступать, спешить (in vias <i>L</i>; contra aliquem <i>V</i>);\n<b>2)</b> выдаваться, выступать вперёд (saxa procurrentia <i>V</i>; terra procurrit in aequor <i>O</i>);\n<b>3)</b> увеличиваться, возрастать (pecunia procurrens <i>Sen</i>)."],
  ["procursatio", "<big>procursatio</big>\n\n<b>procursatio, onis</b> <i>f</i> [procurso]\nнабег, авангардная стычка <i>L, Amm</i>."],
  ["procursatores", "<big>procursatores</big>\n\n<b>procursatores, um</b> <i>m</i> [procurso] <i>воен.</i>\nлёгкие передовые отряды (<i>завязывавшие авангардные бои</i>) <i>L, Amm</i>."],
  ["procursio", "<big>procursio</big>\n\n<b>procursio, onis</b> <i>f</i> [procurro]\n<b>1)</b> <i>воен.</i> стремительное нападение, выпад <i>Frontin, Amm</i>;\n<b>2)</b> быстрое движение вперёд (<i>оратора в сторону слушателей</i>) <i>Q</i>;\n<b>3)</b> отступление в речи <i>Q</i>."],
  ["procurso", "<big>procurso</big>\n\n<b>procurso, —, —, are</b> [<i>intens. к</i> procurro]\nвыбегать вперёд <i>PM</i>; <i>воен.</i> производить набеги <i>L, Amm</i>."],
  ["procursus", "<big>procursus</big>\n\n<b>procursus, us</b> <i>m</i> [procurro]\n<b>1)</b> движение вперёд, быстрое продвижение (militum <i>L</i>);\n<b>2)</b> приступ, вспышка (irae <i>VM</i>); проявление (virtutis <i>VM</i>);\n<b>3)</b> выступ (angulosus <i>PM</i>);\n<b>4)</b> течение, развитие (civitatum exortus et p. <i>Aug</i>)."],
  ["procurvo", "<big>procurvo</big>\n\n<b>pro–curvo, —, —, are</b>\nнагибать вперёд (aliquem <i>St</i>)."],
  ["procurvus", "<big>procurvus</big>\n\n<b>pro–curvus, a, um</b>\nвыгнутый вперёд, изогнутый, кривой (falx, litus <i>V</i>)."],
  ["procus", "<big>procus</big>\n\n<b>I procus, i</b> <i>m</i> [proco]\nсватающийся, претендент на руку, жених <i>Pl, C, V, Ap</i>.\n\n<b>II procus, i</b> <i>m</i> (<i>gen. pl.</i> procum) <i>арх. C</i>\n= procer."],
  ["Procyon", "<big>Procyon</big>\n\n<b>Procyon, onis</b> <i>m</i> (<i>греч.</i>; <i>лат.</i> Antecanis)\nПрокион, <i>созвездие Малого Пса</i> <i>C</i>."],
  ["prod-", "<big>prod-</big>\n\n<b>prod-</b>\n<i>приставка</i> = pro- III (<i>перед начальным гласным основного слова</i>)."],
  ["prodactus", "<big>prodactus</big>\n\n<b>prodactus, a, um</b> <i>part. pf. к</i>\nprodigo."],
  ["prodambulo", "<big>prodambulo</big>\n\n<b>prod–ambulo, —, —, are</b>\nгулять, прогуливаться <i>Ter</i>."],
  ["prodeambulo", "<big>prodeambulo</big>\n\n<b>pro–de–ambulo</b> <i>v. l.</i>\n= prodambulo."],
  ["prodecessor", "<big>prodecessor</big>\n\n<b>pro–decessor, oris</b> <i>m</i>\nпредшественник <i>Symm</i>."],
  ["prodegi", "<big>prodegi</big>\n\n<b>prodegi</b> <i>pf. к</i>\nprodigo."],
  ["prodeo", "<big>prodeo</big>\n\n<b>prod–eo, ii, itum, ire</b>\n<b>1)</b> выходить, идти вперёд (alicui obviam <i>C</i>; ex portu <i>Cs</i>); продвигаться (longius <i>Cs</i>): p. (in publicum) <i>Ter, C, L</i> показываться на людях; p. in funus <i>Ter</i> провожать покойника;\n<b>2)</b> выступать (lacrimae prodeunt <i>Ap</i>; p. in scaenam <i>C, Su</i>): p. in tragoedia <i>Pl</i> играть (роль) в трагедии;\n<b>3)</b> обнаруживаться, проявляться (consuetudo p. coepit <i>C</i>);\n<b>4)</b> выступать, выдаваться (rupes prodit in aequor <i>V</i>): p. aliqua re extra modum <i>C</i> переступать в чём-л. меру;\n<b>5)</b> всходить, произрастать (prodeunt semina <i>Col</i>)."],
  ["prodesse", "<big>prodesse</big>\n\n<b>prodesse</b> <i>inf. к</i>\nprosum."],
  ["prodest", "<big>prodest</big>\n\n<b>prodest</b> <i>3 л. sg. praes. к</i>\nprosum."],
  ["Prodicius", "<big>Prodicius</big>\n\n<b>Prodicius, a, um</b> [Prodicus]\nпродиков (Hercules <i>C</i>)."],
  ["prodico", "<big>prodico</big>\n\n<b>pro–dico, dixi, dictum, ere</b>\n<b>1)</b> предсказывать (<i>sc.</i> futura <i>C</i>);\n<b>2)</b> откладывать, отсрочивать (diem in Quirinalia <i>C</i>);\n<b>3)</b> заранее назначать (alicui Kalendas <i>AG</i>)."],
  ["prodictator", "<big>prodictator</big>\n\n<b>pro–dictator, oris</b> <i>m</i>\nзаместитель диктатора <i>L</i> (<i>v. l.</i>)."],
  ["Prodicus", "<big>Prodicus</big>\n\n<b>Prodicus, i</b> <i>m</i>\nПродик, <i>греч. софист, родом из Кеоса, современник Сократа, учитель красноречия в Афинах, автор аллегорической повести «Геркулес на распутье-»</i> <i>C, Q, AG</i>."],
  ["prodidi", "<big>prodidi</big>\n\n<b>prodidi</b> <i>pf. к</i>\nprodo."],
  ["prodiet", "<big>prodiet</big>\n\n<b>prodiet</b> <i>Lad fut. к</i>\nprodeo."],
  ["prodigalitas", "<big>prodigalitas</big>\n\n<b>prodigalitas, atis</b> <i>f</i> <i>Boet</i>\n= prodigentia."],
  ["prodigaliter", "<big>prodigaliter</big>\n\n<b>prodigaliter</b> <i>Ambr</i>\n= prodige."],
  ["prodige", "<big>prodige</big>\n\n<b>prodige</b> [prodigus]\nрасточительно (vivere <i>C</i>; uti aliqua re <i>Sen</i>)."],
  ["prodigentia", "<big>prodigentia</big>\n\n<b>prodigentia, ae</b> <i>f</i> [prodigo]\nрасточительство, расточительность (opum <i>T</i>)."],
  ["prodigiale", "<big>prodigiale</big>\n\n<b>prodigiale</b> <i>St, Cld</i>\n= prodigialiter."],
  ["prodigialis", "<big>prodigialis</big>\n\n<b>prodigialis, e</b> [prodigium]\n<b>1)</b> странный, необыкновенный, удивительный (ves <i>Amm</i>; signa <i>Eccl</i>);\n<b>2)</b> хранящий от дурных знамений (Juppiter <i>Pl</i>)."],
  ["prodigialiter", "<big>prodigialiter</big>\n\n<b>prodigialiter</b> [prodigialis]\nстранно, необыкновенно, неестественно <i>H, Col, Eccl</i>."],
  ["prodigiose", "<big>prodigiose</big>\n\n<b>prodigiose</b> <i>PM</i>\n= prodigialiter."],
  ["prodigiosus", "<big>prodigiosus</big>\n\n<b>prodigiosus, a, um</b> [prodigium]\nстранный, необыкновенный, неестественный <i>O, Q etc.</i>"],
  ["prodigitas", "<big>prodigitas</big>\n\n<b>prodigitas, utis</b> <i>f</i> <i>LM</i>\n= prodigentia."],
  ["prodigium", "<big>prodigium</big>\n\n<b>prodigium, i</b> <i>n</i> [pro + aio]\n<b>1)</b> чудо, чудесное явление <i>C, L, T etc.</i>;\n<b>2)</b> чудовищный поступок <i>C</i>;\n<b>3)</b> чудовище (monstrum atque p. <i>C</i> — <i>о Каталине</i>): p. triplex <i>O</i> = Qeryones."],
  ["prodigo", "<big>prodigo</big>\n\n<b>prodigo, egi, actum, ere</b> [pro + ago]\n<b>1)</b> гнать перед собой <i>или</i> вперёд (pullos in pabulum <i>Vr</i>);\n<b>2)</b> растрачивать, расточать (aliena bene parta <i>Sl</i>; opes <i>Su</i>);\n<b>3)</b> потреблять (esculentum potulentumve <i>Dig</i>);\n<b>4)</b> рассеивать, прогонять (soporem <i>Tert</i>)."],
  ["prodigus", "<big>prodigus</big>\n\n<b>prodigus, a, um</b> [prodigo]\n<b>1)</b> расточительный (alicujus rei <i>Pl, H</i> <i>и</i> in aliquid <i>AG</i>): p. arcani <i>H</i> разглашающий тайну; p. animae <i>H</i> не щадящий (своей) жизни;\n<b>2)</b> болтливый (lingua prodiga inirenisque <i>AG</i>);\n<b>3)</b> богатый, щедрый (tellus <i>O</i>); изобилующий (locus p. herbae <i>H</i>);\n<b>4)</b> огромный, толстый (alvus <i>Aus</i>);\n<b>5)</b> сильный (odor <i>PM</i>);\n<b>6)</b> дорогостоящий (res <i>PM</i>)."],
  ["prodii", "<big>prodii</big>\n\n<b>prodii</b> <i>pf. к</i>\nprodeo."],
  ["prodinunt", "<big>prodinunt</big>\n\n<b>prodinunt</b> <i>Enn</i>\n= prodeunt (<i>к</i> prodeo)."],
  ["proditio", "<big>proditio</big>\n\n<b>I prōditio, ōnis</b> <i>f</i> [prodo]\n<b>1)</b> выдача, разглашение (arcanorum <i>PM</i>);\n<b>2)</b> предательство, измена (patriae <i>C</i>);\n<b>3)</b> отсрочка <i>Cato</i>.\n\n<b>II prōditio, ōnis</b> <i>f</i> [prodeo]\nвыступление, появление <i>Macr, Sid</i>."],
  ["proditius", "<big>proditius</big>\n\n<b>prōditius, a, um</b> [prodo]\nобнаруживающий <i>Tert</i>."],
  ["proditor", "<big>proditor</big>\n\n<b>prōditor, ōris</b> <i>m</i> [prodo]\nпредатель, изменник <i>C, L, T, H</i>."],
  ["proditorius", "<big>proditorius</big>\n\n<b>prōditōrius, a, um</b> [proditor]\nпредательский (gladius <i>Aug</i>)."],
  ["proditrix", "<big>proditrix</big>\n\n<b>prōditrīx, īcis</b> <i>f</i>\nпредательница <i>Lact</i>."],
  ["proditus", "<big>proditus</big>\n\n<b>prōditus, a, um</b> <i>part. pf. к</i>\n<b>1)</b> prodeo <i>и</i>\n<b>2)</b> prodo."],
  ["prodius", "<big>prodius</big>\n\n<b>prōdius</b> <i>adv.</i> [prodeo]\n(по)ближе (accedere <i>Vr</i> — <i>v. l.</i>)."],
  ["prodo", "<big>prodo</big>\n\n<b>pro–do, didi, ditum, ere</b>\n<b>1)</b> вести дальше, продолжать (genus a sanguine Teucri <i>V</i>);\n<b>2)</b> передавать, выдавать (classem praedonibus <i>C</i>);\n<b>3)</b> выпускать (fumum <i>L</i>); испускать (suspiria pectore <i>O</i>); издавать (vocem <i>VF</i>); наливать (vina cado <i>Q</i>);\n<b>4)</b> вынимать (для показа) (Medusae ora <i>O</i>);\n<b>5)</b> показывать, подавать (exemplum <i>C, L, VP</i>);\n<b>6)</b> устанавливать, вводить (bellicas caerimonias <i>L</i>);\n<b>7)</b> провозглашать, именовать, назначать (inter regem <i>C</i>);\n<b>8)</b> порождать, производить (fetum olivae <i>O</i>);\n<b>9)</b> доводить до всеобщего сведения, обнародовать (decretum <i>C</i>);\n<b>10)</b> разглашать (arcanum <i>J</i>);\n<b>11)</b> сообщать, передавать, рассказывать (aliquid ad memoriam posteritatis <i>C</i>): quod est proditum memoria (<i>или</i> memoriae) ac litteris <i>C</i> что закреплено как в устных преданиях, так и в письменности; in proverbium proditus <i>L</i> вошедший в пословицу;\n<b>12)</b> выдавать (conscios <i>C</i>; aliquem ad mortem <i>V</i>; in omnes cruciatus <i>L</i>);\n<b>13)</b> выдавать, показывать, обнаруживать (gaudia vultu <i>H</i>); изобличать, раскрывать (voluntatem et consilium alicujus <i>QC</i>);\n<b>14)</b> передавать (по наследству) (aliquid suis posteris <i>C</i>);\n<b>15)</b> предавать (patriam <i>C</i>): p. officium <i>C</i> изменять долгу; p. fidem <i>Sl</i> нарушать слово; somnus me prodidit <i>O</i> сон покинул меня;\n<b>16)</b> расстраивать (alvum <i>Pl</i>);\n<b>17)</b> продлевать (alicui vitam ad miseriam <i>Pl</i>); откладывать, отсрочить (aliquot dies nuptiis p. <i>Ter</i>);\n<b>18)</b> приносить в жертву, отдавать (suam vitam et pecuniam omnem <i>Ter</i>)."],
  ["prodoceo", "<big>prodoceo</big>\n\n<b>pro–doceo, (cui, ctum), ere</b>\nучить, поучать, проповедовать <i>H</i>."],
  ["prodormio", "<big>prodormio</big>\n\n<b>pro–dormio, —, —, ire</b>\nдолго спать <i>или</i> продолжать спать <i>Fronto</i>."],
  ["prodromus", "<big>prodromus</big>\n\n<b>prodromus, i</b> <i>m</i> (<i>греч.</i>)\n<b>1)</b> предвестник, посланец, агент (prodromi Pompejani <i>C</i>);\n<b>2)</b> продром, северо-северо-восточный ветер (<i>дующий за восемь дней до восхода Сириуса</i>) <i>C, Col, PM etc.</i>;\n<b>3)</b> <i>сорт скороспелых фиг</i> <i>PM</i>."],
  ["produco", "<big>produco</big>\n\n<b>pro–duco, duxi, ductum, ere</b>\n<b>1)</b> выводить (copias pro castris <i>Cs</i>); уводить (exereitum in locum alienum <i>L</i>); приводить, представлять (aliquem testem <i>C</i>; aliquem in scaenam <i>Su</i>); выставлять на продажу (servos <i>Ter</i>); выводить для сдачи, выдавать (captivos, equos <i>Cs</i>); провожать, сопровождать (virum <i>C, O</i>; aliquem funere <i>V</i>); выносить, придвигать (scamnum lecto <i>O</i>); спускать (navem ex navalibus <i>L</i>); распростирать, расстилать, окутывать (p. nubila menti <i>St</i>);\n<b>2)</b> увлекать (aliquem longius <i>Hirt</i>); завлекать (aliquem dolo in proelium <i>Nep</i>); доводить, приводить (quo discordia., Romanos produxit! <i>V</i>; ad faciendum aliquid <i>PM</i>); вынуждать (producti sumus, ut loqueremur <i>rhH</i>);\n<b>3)</b> вести дальше (navem longius <i>Cs</i>); проводить (noctes <i>Prp</i>); делать долгим, растягивать (syllabam <i>Q</i>); удлинять (supercilium, <i>sc.</i> fuligine <i>J</i>); продлевать (vitam alicui <i>Pl</i>); вытягивать (ferrum incude <i>J</i>; aliquid dentibus <i>M</i>); протягивать, проводить (lineas <i>PM</i>);\n<b>4)</b> производить, создавать (nova vocabula <i>H</i>; magnani mos nos natura produxit <i>Sen</i>); рождать, выращивать (liberos <i>LM</i>; filiam <i>Pl</i>; arborem <i>H</i>): p. folia <i>Pall</i> покрываться листвой;\n<b>5)</b> затягивать (convivium ad multam noctem <i>C</i>); тянуть, задерживать, вводить в заблуждение (aliquem falsa spe <i>Ter</i>); vitam ad centesimum annum p. <i>C</i> дожить до ста лет; p. moram alicui rei <i>Ter</i> задержать наступление чего-л.; paulatim p. diem <i>Ter</i> коротать день; p. rem in hiemem <i>Cs</i> затянуть дело до зимы; res producitur <i>Cs</i> дело затягивается;\n<b>6)</b> возвышать, поднимать (aliquem ad honores <i>C</i>; aliquem pro ejus dignitate <i>C</i>; aliquem omni genere honoris <i>L</i>): ignes ad flammas p. <i>O</i> раздуть яркое пламя;\n<b>7)</b> увеличивать, повышать (legata ad vicies sestertium <i>Su</i>);\n<b>8)</b> издавать (vocem <i>SenT</i>)."],
  ["producta", "<big>producta</big>\n\n<b>producta, orum</b> <i>n</i> [produco](<i>греч.</i> proegmena — <i>см. тж.</i> productus)\n(<i>в учении стоиков</i>) относительные, <i>т. е.</i> внешние блага (<i>напр.</i>, <i>здоровье, красота и т. п.</i>) <i>C, Sen</i>."],
  ["producte", "<big>producte</big>\n\n<b>producte</b> [productus]\n<b>1)</b> протяжно, долго (dicere syllabam <i>AG</i>);\n<b>2)</b> выносливо, терпеливо (parcius vivere, productius jejunare <i>Aug</i>)."],
  ["productilis", "<big>productilis</big>\n\n<b>productilis, e</b> [produco]\nчеканной работы <i>Vlg</i>."],
  ["productio", "<big>productio</big>\n\n<b>productio, onis</b> <i>f</i> [produco]\nудлинение, растяжение (voces productione aut correptione mutatae <i>Q</i>); продление (temporis <i>C</i>)."],
  ["productivus", "<big>productivus</big>\n\n<b>productivus, a, um</b>\n<b>1)</b> поддающийся удлинению, растяжимый (nomina <i>Eccl</i>);\n<b>2)</b> <i>грам.</i> производный (nomen)."],
  ["producto", "<big>producto</big>\n\n<b>producto, —, —, ere</b> [<i>intens. к</i> produco]\nзатягивать (moram <i>Ter</i> — <i>v. l.</i>)."],
  ["productus", "<big>productus</big>\n\n<b>productus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> produco;\n<b>2.</b> <i>adj.</i> растянутый (cornu sinistrum <i>T</i>); протяжный, долгий (syllaba <i>C</i>); длительный, продолжительный (dolor <i>C</i>); затяжной (exitus orationis <i>C</i>); удлинённый (nomen <i>C</i>)."],
  ["produx", "<big>produx</big>\n\n<b>produx, ucis</b> <i>m</i> [produco]\nотпрыск <i>или</i> отводок <i>Tert</i>."],
  ["produxe", "<big>produxe</big>\n\n<b>produxe</b> <i>Ter inf. pf. к</i>\nproduco."],
  ["proebia", "<big>proebia</big>\n\n<b>proebia</b> <i>v. l.</i>\n= praebia."],
  ["proegmena", "<big>proegmena</big>\n\n<b>proegmena, orum</b> <i>n</i> (<i>греч.</i>) <i>C</i>\n= producta."],
  ["proeliaris", "<big>proeliaris</big>\n\n<b>proeliaris, e</b> [proelium]\nимеющий характер сражения: pugna p. <i>Pl</i> настоящее (форменное) сражение; dea p. <i>Ap</i> богиня сражений (Minerva <i>или</i> Bellona)."],
  ["proeliator", "<big>proeliator</big>\n\n<b>proeliator, oris</b> <i>m</i> [proelior] (<i>тж.</i> p. miles <i>Ap</i>)\nвоин, боец <i>VM, T, Just</i>."],
  ["proelio", "<big>proelio</big>\n\n<b>proelio, —, —, are</b> <i>арх. Enn</i>\n= proelior."],
  ["proelior", "<big>proelior</big>\n\n<b>proelior, atus sum, ari</b> <i>depon.</i> [proelium]\n<b>1)</b> сражаться <i>C, Cs etc.</i>;\n<b>2)</b> бороться <i>C, Pt</i>;\n<b>3)</b> спорить <i>C</i>."],
  ["proelium", "<big>proelium</big>\n\n<b>proelium, i</b> <i>n</i>\n<b>1)</b> сражение, битва, бой: p. facere <i>Sl</i> (committere <i>Pl, C, Cs</i>, inire <i>L</i>) завязать бой; p. singulare <i>Aus</i> единоборство;\n<b>2)</b> спор (proelia committere voce <i>O</i>);\n<b>3)</b> борьба <i>C, O etc.</i>;\n<b>4)</b> нападение, натиск, атака (proelio hostem lacessere <i>Cs</i>);\n<b>5)</b> война (p. Marcomanicum <i>Vop</i>);\n<b>6)</b> <i>pl.</i> воин, боец (armigera proelia <i>Prp</i>)."],
  ["Proetides", "<big>Proetides</big>\n\n<b>Proetides, um</b> <i>f</i>\nПрэтиды, дочери Прэта, <i>т. е.</i> Лисиппа, Ифиноя и Ифианасса, <i>которые за пренебрежительное отношение к культу Вакха были поражены безумием и воображали себя коровами</i> <i>V, O</i>."],
  ["Proetus", "<big>Proetus</big>\n\n<b>Proetus, i</b> <i>m</i>\nПрэт, <i>сын Абанта (Abas), брат-близнец Акрисия, мифический царь Тиринфа; головой Медузы Персей превратил его в камень за то, что он изгнал своего брата из Аргоса</i> <i>O</i>."],
  ["profanatio", "<big>profanatio</big>\n\n<b>profanatio, onis</b> <i>f</i>\nпрофанация, лишение святости <i>Eccl</i>."],
  ["profanator", "<big>profanator</big>\n\n<b>profanator, oris</b> <i>m</i>\nпрофанатор, осквернитель <i>CTh, Eccl</i>."],
  ["profane", "<big>profane</big>\n\n<b>profane</b>\nнечестиво, кощунственно (de divinitate disputare <i>MF</i>)."],
  ["profanitas", "<big>profanitas</big>\n\n<b>profanitas, atis</b> <i>f</i>\nнечестие, <i>перен.</i> язычество <i>Eccl</i>."],
  ["profano", "<big>profano</big>\n\n<b>I profano, avi, atum, are</b> [profanus]\n<b>1)</b> лишать святости (dies festos <i>L</i>);\n<b>2)</b> осквернять, позорить (nomen alicujus <i>Q</i>);\n<b>3)</b> раскрывать, разоблачать (secreta <i>Ap</i>).\n\n<b>II profano, avi, atum, are</b> [pro + fanum]\n(перед храмом) посвящать, приносить в жертву (daps profanata <i>Cato</i>)."],
  ["profanus", "<big>profanus</big>\n\n<b>profanus, a, um</b> [pro + fanum]\n<b>1)</b> лишённый святости, неосвящённый (locus <i>C</i>; secernere sacra profanis <i>H</i>); нечистый (animalia <i>T</i>);\n<b>2)</b> непосвящённый (procul este profani! <i>V</i>); непросвещённый, тёмный (vulgus <i>H</i>);\n<b>3)</b> нечестивый, кощунственный (verba <i>O</i>);\n<b>4)</b> зловещий (avis <i>O</i>)."],
  ["profatum", "<big>profatum</big>\n\n<b>profatum, i</b> <i>n</i> [profor]\nизречение, положение <i>AG</i>."],
  ["profatus", "<big>profatus</big>\n\n<b>I profatus, a, um</b> <i>part. pf. к</i>\nprofor.\n\n<b>II profatus, (us) </b> <i>m</i> [profor]\n<b>1)</b> речь, язык (dictu profatuque insuavis <i>AG</i>);\n<b>2)</b> произношение (vocum <i>Sen</i>)."],
  ["profeci", "<big>profeci</big>\n\n<b>profeci</b> <i>pf. к</i>\nproficio."],
  ["profecticius", "<big>profecticius</big>\n\n<b>profecticius, a, um</b> [proficiscor]\nдоставшийся от отца <i>или</i> другого родственника (dos <i>Dig</i>)."],
  ["profectio", "<big>profectio</big>\n\n<b>profectio, onis</b> <i>f</i> [proficiscor]\n<b>1)</b> уход, отъезд, отбытие (p. et reversio alicujus <i>C</i>);\n<b>2)</b> происхождение, источник (pecuniae <i>C</i>)."],
  ["profecto", "<big>profecto</big>\n\n<b>profecto</b> [pro + factum]\n<b>1)</b> в самом деле, действительно: si modo sunt, ut p. sunt <i>C</i> если только они существуют,— а они действительно существуют;\n<b>2)</b> наверно, конечно: p. hercle <i>Pl</i> право же; ut meministis p. <i>Ap</i> как вы, конечно, помните."],
  ["profectus", "<big>profectus</big>\n\n<b>I profectus, a, um</b> <i>part. pf. к</i>\nproficio.\n\n<b>II profectus, a, um</b> <i>part. pf. к</i>\nproficiscor.\n\n<b>III profectus, us</b> <i>m</i> [proficio]\nпреуспеяние, благополучие (agrorum <i>Vr</i>); успех: sine profectu <i>O</i> безуспешно, напрасно; profectu carere <i>O</i> быть бесцельным."],
  ["profero", "<big>profero</big>\n\n<b>pro–fero, tuli, latum, ferre</b>\n<b>1)</b> выносить (aliquid ex aliqua re <i>C, Su etc.</i>); выводить (liberos in conspectu <i>Cs</i>); вывозить (commeatus ex agris in viam <i>L</i>); вынимать, доставать (nummos ex arca <i>C</i>; aliquid de sinu <i>Pt</i>); выдавать (arma ex oppido <i>Cs</i>); высовывать, выставлять (digitum <i>C</i>; caput <i>O, Ph</i>); представлять (testem <i>C</i>); показывать (tabulas <i>Cs</i>);\n<b>2)</b> опубликовывать, издавать (orationem <i>C</i>); разоблачать, открывать (alicujus flagitia <i>C</i>); обнаруживать (ingenium <i>T</i>); p. aliquid foras <i>C</i> (in medium <i>C</i> <i>и</i> in populum <i>Pt</i>) разглашать, делать известным (предавать гласности) что-л.;\n<b>3)</b> приводить, упоминать, называть (exempla nota <i>C</i>; multos nominatim <i>C</i>); произносить, высказывать (ineptias <i>Sen</i>): libera verba animi p. <i>J</i> свободно выражать свои чувства;\n<b>4)</b> продвигать, выдвигать вперёд (munitiones <i>Cs</i>); выкатывать (vineas <i>Cs</i>): p. gradum <i>Pl</i> (pedem <i>H</i>) идти вперёд, продолжать путь; p. signa (castra) <i>воен. L</i> выступить, отправиться в поход; p. aliquem ad famam <i>T</i> прославить кого-л.; prolatis pedibus <i>PM</i> (prolato pede <i>SenT</i>) (<i>см.</i> pes 7) на всех парусах;\n<b>5)</b> расширять, раздвигать (fines agri publici <i>L</i>; imperium ad mare <i>L</i>): cupidine proferendi imperii <i>T</i> из желания раздвинуть пределы (своей) империи || удлинять, продлить (vitam <i>C</i>);\n<b>6)</b> откладывать, отсрочивать (diem <i>H</i>; aliquid in propinquum diem <i>Ap</i>): res prolatae <i>Pl, C</i> перерыв в общественных и судебных делах;\n<b>7)</b> производить, давать (laurum profert terra <i>PJ</i>; frondes et umbras p. <i>VF</i>); изобретать, делать (enses <i>Tib</i>)."],
  ["professe", "<big>professe</big>\n\n<b>professe</b> [profiteor]\nнапрямик, без обиняков (palam ac p. <i>Fl</i>)."],
  ["professio", "<big>professio</big>\n\n<b>professio, onis</b> <i>f</i> [profiteor]\n<b>1)</b> изъявление, выражение (bonae voluntatis <i>C</i>; opinionis <i>AG</i>; pietatis <i>T</i>);\n<b>2)</b> официальное объявление, заявление, указание (<i>своего звания, состояния, имени</i>) (p. jugerum <i>C</i>);\n<b>3)</b> официально указанное занятие, профессия (bene dicendi <i>C</i>); специальность (p. grammaticae <i>Su</i>): salutans p. <i>CC</i> <i>или</i> p. medendi <i>Amm</i> = ars medicinae; p. rhetorica <i>Aug</i> преподавание риторики;\n<b>4)</b> очевидный признак (vitiorum, <i>sc.</i> vitis <i>Pall</i>)."],
  ["professor", "<big>professor</big>\n\n<b>professor, oris</b> <i>m</i> [profiteor]\nпреподаватель, учитель: p. sapientiae <i>CC</i> = philosophus; p. eloquentiae <i>Su</i> = rhetor."],
  ["professorius", "<big>professorius</big>\n\n<b>professorius, a, um</b> [professor]\nсвойственный преподавателю риторики, ораторский, школьный (lingua <i>T</i>)."],
  ["professus", "<big>professus</big>\n\n<b>professus, a, um</b> <i>part. pf. к</i>\nprofiteor."],
  ["profestus", "<big>profestus</big>\n\n<b>pro–festus, a, um</b>\n<b>1)</b> непраздничный, будничный: dies profesti <i>L</i> <i>или</i> profestae luces <i>H</i> будни;\n<b>2)</b> непосвящённый, тёмный, невежественный (vulgus <i>AG</i>)."],
  ["proficienter", "<big>proficienter</big>\n\n<b>proficienter</b> [proficio]\nс успехом, успешно <i>Aug</i>."],
  ["proficio", "<big>proficio</big>\n\n<b>proficio, feci, fectum, ere</b> [pro + facio]\n<b>1)</b> идти вперёд, продвигаться, проходить (tridui viam <i>Cs</i>): severa lege p. <i>Pt</i> воспитываться в строгости;\n<b>2)</b> (пре)успевать, достигать, добиваться (multum, nihil <i>C, Cs etc.</i>): p. ad (in) aliquid <i>C, Cs etc.</i> сделать успехи в чём-л.; proficiens aegrotus <i>CA</i> выздоравливающий больной;\n<b>3)</b> помогать, приносить пользу, быть полезным (aliquid, adversus aliquid, in aliquid, in aliqua re <i>или</i> ad aliquid <i>C, CC etc.</i>); оказывать действие, действовать (herba proficit nihil <i>H</i>);\n<b>4)</b> расти (vitis proficit <i>PM</i>); повышаться (pretium proficit <i>PM</i>)."],
  ["proficisco", "<big>proficisco</big>\n\n<b>proficisco, —, —, ire</b> <i>Pl</i>\n= proficiscor."],
  ["proficiscor", "<big>proficiscor</big>\n\n<b>proficiscor, fectus sum, ficisci</b> <i>depon.</i> [<i>inchoat. к</i> proficio]\n<b>1)</b> отправляться, уезжать, уходить (ex <i>или</i> ab urbe <i>Cs etc.</i>; ad bellum <i>Cs, Nep</i> <i>и</i> in bellum <i>Just, AG</i>; Corinthum <i>Ter</i>); отплывать (ex portu <i>Cs</i>): Troja profecti <i>V</i> выходцы из Трои || <i>перен.</i> выступать (ad <i>и</i> contra hostem <i>Cs</i>);\n<b>2)</b> (<i>в речи</i>) переходить (ad reliqua <i>C</i>);\n<b>3)</b> исходить, начинать (ab hoc initio <i>Cs</i>); происходить, вести свой род (ab aliquo <i>V</i>); возникать (a natura <i>C</i>); брать начало (ex aliqua re <i>C</i>): profecti ab Aristotele <i>C</i> ученики Аристотеля;\n<b>4)</b> проистекать, зависеть (a nostra voluntate <i>C</i>): prima commendatio proficiscitur a modestia <i>C</i> главное достоинство (юноши) состоит в скромности."],
  ["proficuus", "<big>proficuus</big>\n\n<b>proficuus, a, um</b> [proficio]\nполезный (alicui <i>Eccl</i>)."],
  ["profiteor", "<big>profiteor</big>\n\n<b>profiteor, fessus sum, eri</b> <i>depon.</i> [pro + fateor]\n<b>1)</b> открыто заявлять, прямо говорить, объявлять, признавать (verum <i>O</i>): de se p. <i>C</i> выступить с публичным заявлением о себе; ex professo <i>Sen, Q etc.</i> <i>и</i> de professo <i>Ap</i> открыто, напрямик || выражать, обнаруживать (dolorem <i>Just</i>);\n<b>2)</b> объявлять своим делом, называть своей специальностью, признавать своим занятием (p. philosophiam <i>C</i>): se p. выдавать себя, объявлять себя (qui se sapientes esse profitentur <i>C</i>; se p. adversarium alicui <i>C</i>); professae (<i>sc.</i> lenocinium) <i>O euph.</i> публичные женщины;\n<b>3)</b> преподавать, читать лекции: qui profitentur <i>PJ</i> преподаватели;\n<b>4)</b> обещать, сулить, предлагать (operam suam ad aliquid p. <i>C</i>): p. grandia <i>H</i> <i>или</i> magna <i>H, O</i> заявлять великие претензии; se adjutorem ad aliquid p. <i>Cs</i> обещать свою помощь в чём-л.; incepta magna professa <i>H</i> многообещающее начало; p. se ad colloquium venturum <i>Cs</i> обещать прибыть для совещания;\n<b>5)</b> <i>юр.</i> делать заявление, давать показание: indicium p. <i>Sl</i> просить слова для заявления <i>или</i> показания; accusationem p. alicujus rei <i>Q</i> выступать с обвинением (против) чего-л.;\n<b>6)</b> давать сведения, указывать: p. jugera sationum suarum <i>C</i> объявить (дать сведения) о числе своих засеваемых югеров; p. (nomen) <i>C, L</i> являться, записываться (<i>на военную службу, в качестве соискателя должности и т. п.</i>) (p. apud praetorem <i>C</i>; p. intra legitimos dies <i>Sil</i>; indignos p. vetare <i>VP</i>); in his nomen suum p. <i>Ter</i> причислять себя к этим (людям)."],
  ["proflatus", "<big>proflatus</big>\n\n<b>proflatus, (us) </b> <i>m</i>\n<b>1)</b> веяние, дуновение (aestivus <i>Col</i> — <i>v. l.</i>);\n<b>2)</b> храпение <i>St</i>."],
  ["proflictus", "<big>proflictus</big>\n\n<b>proflictus, a, um</b> <i>part. pf. к</i>\nprofligo II."],
  ["profligatio", "<big>profligatio</big>\n\n<b>profligatio, onis</b> <i>f</i> [profligo I]\n<b>1)</b> регулирование, взыскивание (debitorum <i>CTh</i>);\n<b>2)</b> расточение (bonorum <i>Aus</i>)."],
  ["profligator", "<big>profligator</big>\n\n<b>profligator, oris</b> <i>m</i> [profligo]\nрасточитель, мот <i>T</i>."],
  ["profligatus", "<big>profligatus</big>\n\n<b>profligatus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> profligo I;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> погибший, пропащий, испорченный (homo <i>C</i>);\n<b>2)</b> бессильный, жалкий (judicia <i>C</i>);\n<b>3)</b> преклонный (homo profligatae aetatis <i>Sen</i>): in profligato esse <i>AG</i> быть близким к окончанию."],
  ["profligo", "<big>profligo</big>\n\n<b>I pro–fligo, avi, atum, are</b>\n<b>1)</b> поражать, сокрушать, опрокидывать (copias hostium <i>C</i>); подрывать, разрушать (rem publicam <i>C</i>); низвергать, повергать в прах, обездоливать (aliquem <i>L</i>); угнетать (maerore afflictus et profligatus <i>C</i>);\n<b>2)</b> доводить (почти) до конца, в основном решать (quaestionem <i>C</i>; bellum profligatum conficere <i>L</i>): proelia p. <i>T</i> выигрывать сражения.\n\n<b>II profligo, —, filetum, ere</b>\nсокрушать, уничтожать (res proflictae <i>AG</i>)."],
  ["proflo", "<big>proflo</big>\n\n<b>pro–flo, avi, atum, are</b>\n<b>1)</b> выдыхать, извергать, изрыгать (flammas <i>O</i>): p. iras <i>PM</i> быть в ярости; toto pectore p. somnum <i>V</i> громко храпеть во сне;\n<b>2)</b> раздувать (nares <i>Ap</i>);\n<b>3)</b> расплавлять, плавить (<i>дутьём</i>) <i>PM</i>."],
  ["profluens", "<big>profluens</big>\n\n<b>I profluens, entis</b>\n<b>1.</b> <i>part. praes. к</i> profluo;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> текущий, текучий, проточный (aqua <i>C, L</i>);\n<b>2)</b> плавный, непрерывный (loquacitas <i>C</i>; eloquentia <i>T</i>).\n\n<b>II profluens, entis</b>\n<b>1.</b> <i>f</i> (<i>sc.</i> aqua) проточная вода <i>rhH, C</i>;\n<b>2.</b> <i>n</i> поток красноречия <i>C, T</i>."],
  ["profluenter", "<big>profluenter</big>\n\n<b>profluenter</b> [profluens I]\n<b>1)</b> плавно (exsequi <i>AG</i>);\n<b>2)</b> обильно, в изобилии (<i>sc.</i> adesse <i>C</i>)."],
  ["profluentia", "<big>profluentia</big>\n\n<b>profluentia, ae</b> <i>f</i> [profluens I]\nтечение, поток: p. loquendi <i>C</i> многословие."],
  ["profluo", "<big>profluo</big>\n\n<b>pro–fluo, fluxi, fluxum, ere</b>\nвытекать, литься (ex monte <i>Cs</i>); течь (in mare <i>C</i>); p. ad aliquid <i>перен.</i> незаметно достигнуть (чего-л.) (ad famam <i>C</i>) <i>или</i> предаться (чему-л.) (ad libidines <i>T</i>)."],
  ["profluus", "<big>profluus</big>\n\n<b>profluus, a, um</b> [profluo]\nобильно текущий (recessus, <i>sc.</i> maris <i>Col</i>; lacrimae <i>Sid</i>)."],
  ["profluvium", "<big>profluvium</big>\n\n<b>profluvium, i</b> <i>n</i> [profluo]\n<b>1)</b> истечение (sanguinis <i>Lcr, Col etc.</i>): p. mulierum <i>или</i> genitale <i>PM</i> менструации; profluvia alba <i>PM</i> бели;\n<b>2)</b> (<i>тж.</i> p. alvi <i>Col</i> <i>и</i> p. ventris <i>Eutr</i>) понос <i>CC</i>."],
  ["profluvius", "<big>profluvius</big>\n\n<b>I profluvius, a, um</b>\nтекучий, <i>перен.</i> непостоянный, шаткий (fides <i>Caec</i>).\n\n<b>II profluvius, i</b> <i>m</i> <i>Veg</i>\n= profluvium 2."],
  ["profluxi", "<big>profluxi</big>\n\n<b>profluxi</b> <i>pf. к</i>\nprofluo."],
  ["profluxio", "<big>profluxio</big>\n\n<b>profluxio, onis</b> <i>f</i>\nистечение, поток <i>Aug</i>."],
  ["profor", "<big>profor</big>\n\n<b>pro–for, fatus sum, fari</b> <i>depon.</i>\n<b>1)</b> говорить, рассказывать, поведать (cascam rem <i>Acc</i>; pudor prohibet plura p. <i>H</i>): magno clamore profatur <i>Sil</i> он громогласно объявляет;\n<b>2)</b> предсказывать (diem <i>LA</i>); пророчить (Pythia a tripodi profatur <i>Lcr</i>)."],
  ["profore", "<big>profore</big>\n\n<b>profore</b> <i>inf. fut. к</i>\nprosum."],
  ["profringo", "<big>profringo</big>\n\n<b>profringo, —, —, ere</b> [pro + frango]\nразбивать: p. inarata <i>St</i> поднимать целину."],
  ["profuga", "<big>profuga</big>\n\n<b>profuga, ae</b> <i>m</i> <i>Ap</i>\n= profugus II."],
  ["profugio", "<big>profugio</big>\n\n<b>pro–fugio, fugi, fugitum, ere</b>\n<b>1)</b> бежать, убегать (ex oppido <i>Cs</i>; in exsilium <i>C</i>; ad aliquem <i>C, Cs</i>);\n<b>2)</b> бежать, избегать (aliquem, aliquid <i>Col, Sen, QC etc.</i>)."],
  ["profugus", "<big>profugus</big>\n\n<b>I profugus, a, um</b> [profugio]\n1). бегущий, убегающий (p. vinculorum <i>PM</i>; taurus p. altaribus <i>T</i>); бежавший (proelio, a <i>или</i> e proelio <i>Fl, T</i>); беглый (miles <i>Sl</i>): castra profuga <i>Cld</i> = castra profugorum;\n<b>2)</b> изгнанный (domo, patria <i>L</i>);\n<b>3)</b> странствующий, скитающийся, кочевой (Scythes <i>H</i>).\n\n<b>II profugus, i</b> <i>m</i>\n<b>1)</b> беглец (Trojani Aenea duce profugi <i>Sl</i>);\n<b>2)</b> перебежчик (p. ad rebelles <i>T</i>);\n<b>3)</b> изгнанник <i>O</i>;\n<b>4)</b> отступник (suae religionis <i>MF</i>)."],
  ["profui", "<big>profui</big>\n\n<b>profui</b> <i>pf. к</i>\nprosum."],
  ["profunde", "<big>profunde</big>\n\n<b>profunde</b> [profundus]\nглубоко <i>PM, Vlg, Eccl</i>."],
  ["profunditas", "<big>profunditas</big>\n\n<b>profunditas, atis</b> <i>f</i>\nглубина (corporum sunt tres dimensiones: longitudo, latitudo, p. <i>Macr</i>; <i>перен.</i> Platonici dogmatis <i>Macr</i>)."],
  ["profundo", "<big>profundo</big>\n\n<b>pro–fundo, fudi, fusum, ere</b>\n<b>1)</b> проливать (sanguinem <i>C, PM etc.</i>; lacrimas <i>C etc.</i>); выливать (vinum <i>Pl</i>); щедро расходовать, не жалеть (pecuniam <i>C, PM</i>): p. omnes vires <i>C</i> употребить все свои силы; p. vitam pro patria <i>C</i> отдать жизнь за родину; totum se in aliquem p. <i>C</i> жертвовать всем для кого-л., делиться последним с кем-л.;\n<b>2)</b> напрасно тратить, расточать зря (verba ventis <i>Lcr</i>; divitias <i>Sl</i>; patrimonium <i>C</i>);\n<b>3)</b> изливать (odium in aliquem <i>C</i>): p. clamorem <i>C</i> издавать крик, кричать || произносить (has maesto pectore voces <i>Ctl</i>): p. se <i>или</i> <i>pass.</i> profundi изливаться (in questus flebiles se p. <i>L</i>) || показываться во множестве, хлынуть (lacrimae se profuderunt <i>C</i>); обрушиваться, ринуться (multitudo sagittariorum se profudit <i>Cs</i>);\n<b>4)</b> <i>культ.</i> возливать (deo vina <i>Lact</i>);\n<b>5)</b> делать вялым, распростирать (somnus membra profudit <i>Lcr</i>): cadunt profusae, <i>sc.</i> aves <i>Lcr</i> птицы замертво падают;\n<b>6)</b> испускать (animam <i>C</i>); извергать (ignes <i>Lcr</i>); выталкивать (infantem ex alvo matris natura profudit <i>Lcr</i>);\n<b>7)</b> опускать, <i>med.</i> profundi спускаться, свисать (infula ex utraque parte profusa <i>Lcr</i>);\n<b>8)</b> рождать, производить (в изобилии); se p. <i>или</i> <i>med.</i> родиться, разрастаться (nimium <i>C</i>) <i>или</i> выделяться (humores qui ex renibus profunduntur <i>C</i>)."],
  ["profundum", "<big>profundum</big>\n\n<b>profundum, i</b> <i>n</i> [profundus]\n<b>1)</b> глубина, глубь, бездна (maris <i>Su</i>); p. camporum <i>Just</i> низко лежащие поля;\n<b>2)</b> море: penitus profundo vela dare <i>V</i> пуститься в открытое море;\n<b>3)</b> высь, вышина (p. caeli <i>Man</i>);\n<b>4)</b> <i>pl.</i> дебри (profunda silvarum et montium <i>T</i>);\n<b>5)</b> <i>перен.</i> бездна, безмерность (miseriarum <i>VM</i>);\n<b>6)</b> <i>pl.</i> подземный мир, преисподняя <i>Cld</i>;\n<b>7)</b> безвестность (res quae in profundo esse dicuntur <i>Dig</i>)."],
  ["profundus", "<big>profundus</big>\n\n<b>profundus, a, um</b>\n<b>1)</b> глубокий (mare <i>C, QC</i>; tenebrae <i>AG</i>; nox Erebi <i>V</i>);\n<b>2)</b> подземный (manes <i>V</i>): p. Juppiter <i>St</i> = Pluto; profunda Juno <i>Cld</i> = Proserpina;\n<b>3)</b> высокий, бездонный (caelum <i>V, VF</i>; altitudo <i>L, T</i>);\n<b>4)</b> глубокий, крепкий (somnus <i>Ap</i>);\n<b>5)</b> безмерный (libido <i>Sl, C</i>; avaritia <i>Sl</i>);\n<b>6)</b> ненасытный (gula <i>Su</i>);\n<b>7)</b> густой, дремучий <i>или</i> огромный (silvae <i>Lcr, QC</i>);\n<b>8)</b> густой, плотный (grando <i>Aus</i>);\n<b>9)</b> обильный (merum <i>St</i>);\n<b>10)</b> неиссякаемый, неистощимый (os Pindari <i>H</i>)."],
  ["profuse", "<big>profuse</big>\n\n<b>profuse</b> [profusus]\n<b>1)</b> беспорядочно (tendere in castra <i>L</i>);\n<b>2)</b> неумеренно, чрезмерно (laudare <i>AG</i>); расточительно, не жалея средств (festos dies celebrare <i>Su</i>)."],
  ["profusio", "<big>profusio</big>\n\n<b>profusio, onis</b> <i>f</i> [profundo]\n<b>1)</b> проливание, пролитие (sanguinis <i>Cs</i>); истечение: p. alvi <i>CC</i> = profluvium 2;\n<b>2)</b> <i>культ.</i> возлияние (vini <i>Lact</i>);\n<b>3)</b> расточительность (nimia <i>PJ</i>);\n<b>4)</b> расточение (divitiarum <i>Su</i>; clementiae <i>Tert</i>)."],
  ["profusor", "<big>profusor</big>\n\n<b>profusor, oris</b> <i>m</i>\nрасточитель <i>Tert</i>."],
  ["profusus", "<big>profusus</big>\n\n<b>profusus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> profundo;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> длинный (cauda <i>Vr, Pall</i>);\n<b>2)</b> неумеренный, чрезмерный, разнузданный (hilaritas <i>C</i>; libido <i>Su</i>); слишком щедрый, расточительный (homo <i>M</i>; sumptus <i>C</i>; convivium <i>Su</i>)."],
  ["progemmo", "<big>progemmo</big>\n\n<b>pro–gemmo, —, —, are</b>\nдавать (пускать) почки (<i>о растениях</i>) <i>Col</i>."],
  ["progener", "<big>progener</big>\n\n<b>pro–gener, eri</b> <i>m</i>\nмуж внучки <i>Sen, T, Su, Dig</i>."],
  ["progeneratio", "<big>progeneratio</big>\n\n<b>pro–generatio, onis</b> <i>f</i>\nразмножение, разведение (mularum <i>PM</i>)."],
  ["progenero", "<big>progenero</big>\n\n<b>pro–genero, —, —, are</b>\nрождать, производить на свет (fetum <i>PM</i>; non progenerant aquilae columbam <i>H</i>)."],
  ["progenies", "<big>progenies</big>\n\n<b>progenies, ei</b> <i>f</i> (<i>арх. gen.</i>, <i>Pac</i> <b>ii</b>) [progigno]\n<b>1)</b> поколение, род (progeniem Trojano a sanguine duci <i>V</i>);\n<b>2)</b> потомство (se progeniem deorum dicere <i>Lcr</i>); потомок, сын (Miltiadis p. <i>Nep</i>);\n<b>3)</b> Детище, творение, (литературное) произведение (mea p. <i>O</i>)."],
  ["progenitivus", "<big>progenitivus</big>\n\n<b>pro–genitivus, a, um</b>\nпорождающий (genus multarum specierum progenitivum <i>Boet</i>)."],
  ["progenitor", "<big>progenitor</big>\n\n<b>progenitor, oris</b> <i>m</i> [progigno]\nродоначальник <i>Nep, O etc.</i>"],
  ["progenitrix", "<big>progenitrix</big>\n\n<b>pro–genitrix, icis</b> <i>f</i>\nродоначальница, прародительница <i>Is</i>."],
  ["progeno", "<big>progeno</big>\n\n<b>progeno, —, —, ere</b> <i>Dig</i>\n= progigno."],
  ["progenui", "<big>progenui</big>\n\n<b>progenui</b> <i>pf. к</i>\nprogigno."],
  ["progermino", "<big>progermino</big>\n\n<b>pro–germino, —, —, are</b>\nпоказываться, прорастать (<i>о молодых побегах</i>) <i>Col</i>."],
  ["progero", "<big>progero</big>\n\n<b>pro–gero, gessi, gestum, ere</b>\n<b>1)</b> выносить (fimum stabulis <i>Col</i>);\n<b>2)</b> нести впереди (effigies divinas <i>Ap</i>)."],
  ["progesto", "<big>progesto</big>\n\n<b>progesto, —, —, ire</b> [<i>intens. к</i> progero]\nнести вперёд, <i>т. е.</i> (пре)подносить (edulia mitiora <i>Ap</i>)."],
  ["progigno", "<big>progigno</big>\n\n<b>pro–gigno, genui, genitum, ere</b>\nрождать, порождать, производить на свет (aliquem <i>и</i> aliquid <i>Pl, Lcr, V etc.</i>)."],
  ["prognariter", "<big>prognariter</big>\n\n<b>prognariter</b> [pro + gnarus]\n<b>1)</b> умело, искусно (certare <i>Enn</i>);\n<b>2)</b> определённо, окончательно (indicare, <i>sc.</i> pretium <i>Pl</i>)."],
  ["prognati", "<big>prognati</big>\n\n<b>prognati, orum</b> <i>m</i> [prognatus]\nпотомки <i>Pl</i>."],
  ["prognatus", "<big>prognatus</big>\n\n<b>prognatus, a, um</b> [pro + gnascor]\nвыросший (arundines in palude prognatae <i>PM</i>); родившийся, происходящий (ab aliquo <i>Pl</i>; ex Cimbris <i>Cs</i>); рождённый (Tantalo <i>Poeta ap. C</i>)."],
  ["Progne", "<big>Progne</big>\n\n<b>Progne, es</b> <i>f</i> <i>v. l.</i>\n= Procne."],
  ["Prognis", "<big>Prognis</big>\n\n<b>Prognis, idis</b> <i>f</i> <i>O</i>\n= Procris."],
  ["prognosis", "<big>prognosis</big>\n\n<b>prognosis, is</b> <i>f</i> (<i>греч.</i>)\nпрогноз, предсказание <i>CA</i>."],
  ["prognostica", "<big>prognostica</big>\n\n<b>prognostica, orum</b> <i>n</i> (<i>греч.</i>)\nметеорологические признаки для предсказания погоды <i>C, Q</i>."],
  ["prognosticus", "<big>prognosticus</big>\n\n<b>prognosticus, a, um</b>\nпрогностический (praevisio <i>Is</i>)."],
  ["programma", "<big>programma</big>\n\n<b>programma, atis</b> <i>n</i> (<i>греч.</i>)\nписьменное извещение <i>Vulc, CJ</i>."],
  ["progredior", "<big>progredior</big>\n\n<b>pro–gredior, gressus sum, gredi</b> <i>depon.</i>\n<b>1)</b> выступать, выходить (ex domo <i>C</i>; extra vallum <i>bAfr</i>); идя вперёд, продвигаться дальше (p. et regredi <i>C</i>); ultra quo progrediar, quam ut verisimilia videam, non babeo <i>C</i> выйти за пределы вероятного я не в состоянии;\n<b>2)</b> переходить, приступать (ad reliqua <i>C</i>);\n<b>3)</b> доходить (ad adulationem <i>T</i>); усиливаться (amentia alicujus progreditur <i>C</i>); заходить слишком далеко: p. aetate <i>C</i> стариться, стареть;\n<b>4)</b> преуспевать, делать успехи (in aliqua re <i>C</i>): incipientes aut paulum progressi <i>Q</i> начинающие или немного продвинувшиеся."],
  ["progrediri", "<big>progrediri</big>\n\n<b>progrediri</b> <i>Pl inf. к</i>\nprogredior."],
  ["progressio", "<big>progressio</big>\n\n<b>progressio, onis</b> <i>f</i> [progredior]\n<b>1)</b> движение вперёд (progressionem facere ad virtutem <i>C</i>); преуспеяние, успех, рост (rei militaris <i>C</i>); продвижение (ad omnem excellentiam <i>C</i>); <i>pl.</i> развитие <i>C</i>;\n<b>2)</b> <i>ритор.</i> прогрессия, постепенное усиление выражений <i>C, Q</i>."],
  ["progressor", "<big>progressor</big>\n\n<b>progressor, oris</b> <i>m</i>\nувеличивающийся (<i>о числе</i>) <i>Aug</i>."],
  ["progressus", "<big>progressus</big>\n\n<b>I progressus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> progredior;\n<b>2.</b> <i>adj.</i> достигший успеха, продвинувшийся <i>Tert</i>.\n\n<b>II progressus, us</b> <i>m</i> [progredior]\n<b>1)</b> движение вперёд (p. et regressus, <i>sc.</i> stellarum <i>C</i>); <i>воен.</i> продвижение (aliquem progressu arcere <i>C</i>);\n<b>2)</b> ход, развитие (rerum <i>C</i>); течение (aetatis <i>C</i>);\n<b>3)</b> успех (progressus facere in studiis <i>C</i>)."],
  ["progubernator", "<big>progubernator</big>\n\n<b>pro–gubernator, oris</b> <i>m</i>\nпомощник рулевого <i>Caec</i>."],
  ["progymnastes", "<big>progymnastes</big>\n\n<b>progymnastes, ae</b> <i>m</i> (<i>греч.</i>)\nпрогимнаст, <i>раб, нёсший обязанности гимнастического инструктора</i> <i>Sen</i>."],
  ["proh", "<big>proh</big>\n\n<b>proh</b> <i>interj.</i>\n= pro II."],
  ["prohibeo", "<big>prohibeo</big>\n\n<b>prohibeo, buī, bitum, ēre</b> [pro + habeo]\n<b>1)</b> удерживать, удалять (aliquem aliqua re <i>или</i> ab aliqua re <i>Cs etc.</i>); держать на расстоянии, отбивать нападение, отражать (praedones ab insula <i>C</i>);\n<b>2)</b> не давать (p. hostem rapinis <i>C</i>); защищать, ограждать, предохранять (rem publicam a periculo <i>C</i>; cives calamitate <i>C</i>): id te Juppiter prohibessit (<i>арх. pf. conjct.</i>)! <i>Pl</i> да сохранит тебя Юпитер от этого!;\n<b>3)</b> препятствовать, мешать, запрещать (aliquem abire <i>C</i>; lex prohibet aliquid <i>C</i>) || prohibiti in decem annos ejusmodi coetu (sunt) <i>T</i> им на 10 лет запрещены подобные собрания; nullis prohibentibus <i>O</i> не встречая никаких препятствий || отрезывать (oppidanos aqua <i>Hirt</i>);\n<b>4)</b> избегать, не допускать (prohibenda est ira in puniendo <i>C</i>); предупреждать, предотвращать (seditiones <i>Sl</i>);\n<b>5)</b> отказывать (aliquem sepultura <i>rhH, QC</i>): p. aliquem aditu <i>O</i> <i>и</i> alicui aditum <i>bAfr</i> закрывать кому-л. доступ; prohiberi jure suffragii <i>C</i> быть лишённым избирательного права;\n<b>6)</b> отделять (Caspium mare a Rubro <i>Amm</i>)."],
  ["prohibessit", "<big>prohibessit</big>\n\n<b>prohibessit</b> <i>арх. Enn, Cato, Pl, C pf. conjct. к</i>\nprohibeo."],
  ["prohibitio", "<big>prohibitio</big>\n\n<b>prohibitio, ōnis</b> <i>f</i> [prohibeo]\nнедопущение, запрет, запрещение (alicujus rei <i>C, Dig etc.</i>)."],
  ["prohibitor", "<big>prohibitor</big>\n\n<b>prohibitor, ōris</b> <i>m</i> [prohibeo]\nпрепятствующий, удерживающий <i>Ap, Amm etc.</i>"],
  ["prohibitorius", "<big>prohibitorius</big>\n\n<b>prohibitōrius, a, um</b> [prohibitor]\nзапретительный (interdictum <i>Dig</i>); удерживающий: avis prohibitoria <i>PM</i> птица, предостерегающая от чего-л. (зловещими приметами)."],
  ["prohinc", "<big>prohinc</big>\n\n<b>pro–hinc</b>\nпоэтому, ввиду этого <i>Ap</i>."],
  ["prohoemium", "<big>prohoemium</big>\n\n<b>prohoemium</b> <i>v. l.</i>\n= prooemium."],
  ["proicio", "<big>proicio</big>\n\n<b>proicio</b> <i>v. l.</i>\n= projicio."],
  ["proin", "<big>proin</big>\n\n<b>proin</b> (<i>преим. односложно</i>) <i>Pl, Ter, Ctl</i>\n= proinde."],
  ["proinde", "<big>proinde</big>\n\n<b>pro–inde</b>\n<b>1)</b> поэтому, потому, ввиду этого: p. agmine impeditos adorirentur <i>Cs</i> поэтому (на врагов) следует напасть, когда они на марше (<i>т. е.</i> не развернулись в боевой порядок) || итак: p. sciant... <i>QC</i> так пусть же они знают...;\n<b>2)</b> таким же образом, (точно) так же: p... ac (atque, ut, quam, quasi, tamquam) так же... как (если бы): p. ac famul infimus <i>Lcr</i> словно последний слуга;\n<b>3)</b> соответственно: cultu non p. speciosus <i>Pt</i> наряд (старца с глубокомысленным видом) не был соответственно блистателен."],
  ["projeci", "<big>projeci</big>\n\n<b>projeci</b> <i>pf. к</i>\nprojicio."],
  ["projecte", "<big>projecte</big>\n\n<b>projecte</b>\nпренебрежительно (ignoscere <i>Tert</i>)."],
  ["projecticius", "<big>projecticius</big>\n\n<b>projecticius, a, um</b> [projicio]\n<b>1)</b> подкинутый, брошенный (puella <i>Pl</i>);\n<b>2)</b> отвергнутый (Palladius <i>Amm</i>)."],
  ["projectio", "<big>projectio</big>\n\n<b>projectio, onis</b> <i>f</i> [projicio]\n<b>1)</b> выбрасывание, протягивание, вытягивание (brachii <i>C</i>);\n<b>2)</b> <i>архит.</i> выступ <i>или</i> пристройка здания <i>Dig</i>."],
  ["projecto", "<big>projecto</big>\n\n<b>pro–jecto, —, —, are</b>\nгнать вперёд <i>Is</i>."],
  ["projecto", "<big>projecto</big>\n\n<b>projecto, —, —, are</b> [<i>intens. к</i> projicio]\n<b>1)</b> порицать, обвинять (aliquid <i>Enn</i>);\n<b>2)</b> подвергать, выставлять (aliquem in pericula <i>Amm</i>)."],
  ["projectum", "<big>projectum</big>\n\n<b>projectum, i</b> <i>n</i> <i>Dig</i>\n= projectura."],
  ["projectura", "<big>projectura</big>\n\n<b>projectura, ae</b> <i>f</i> [projicio] <i>архит.</i>\nвыступ <i>Vtr</i>."],
  ["projectus", "<big>projectus</big>\n\n<b>I projectus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> projicio;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> выступающий, выдающийся вперёд, торчащий (saxa <i>V</i>; venter <i>Su</i>);\n<b>2)</b> бросающийся в глаза, чрезвычайный, чрезмерный (audacia, cupiditas <i>C</i>);\n<b>3)</b> склонный (ad aliquid <i>C, T</i> <i>и</i> in aliquid <i>AG etc.</i>);\n<b>4)</b> лежащий, покоящийся (viridi in antro <i>V</i>);\n<b>5)</b> поверженный (ad terram <i>Cs</i>);\n<b>6)</b> презренный, низкий (servientium patientia <i>T</i>);\n<b>7)</b> печальный, унылый (vultus <i>T</i>).\n\n<b>II projectus, (us) </b> <i>m</i> [projicio]\n<b>1)</b> выступание, торчание (frondis <i>PM</i>);\n<b>2)</b> вытянутое положение (corporis <i>Lcr</i>)."],
  ["projicio", "<big>projicio</big>\n\n<b>projicio, jeci, jectum, ere</b> [pro + jacio]\n<b>1)</b> бросать (cibum alicui <i>H</i>; praedam fluvio <i>V</i>); швырять (non donare, sed p. <i>Sen</i>); (нечаянно) ронять (phialam in pavimentum <i>Pt</i>); se p. бросаться (ad pedes alicujus <i>Cs</i>; ex navi <i>Cs</i>): in miserias projici <i>Sl</i> впасть в нищету;\n<b>2)</b> выбрасывать вперёд, <i>т. е.</i> держать впереди, протягивать, вытягивать вперёд (clipeum prae se p. <i>L</i>; brachium projectum <i>C</i>); брать наперевес <i>или</i> выставлять вперёд (hastam <i>Nep</i>); <i>pass.-med.</i> projici выдаваться вперёд, выступать, торчать (tectum projicitur <i>C</i>): jus <i>или</i> servitus projiciendi <i>Dig</i> право сделать пристройку к своему дому над чужим участком; in altum projici <i>C</i> вдаваться в море;\n<b>3)</b> выбрасывать, прогонять (aliquem foras <i>C</i>); изгонять, ссылать (aliquem in insulam <i>T</i>): extra leges moresque projectus <i>Sen</i> беззаконный и безнравственный;\n<b>4)</b> отбрасывать, бросать на землю (sarcinas <i>Cs</i>); бросать прочь, складывать (arma <i>Cs</i>);\n<b>5)</b> бросать, оставлять (aliquem insepultum <i>L</i>); ввергать (aliquem in pericula <i>V</i>); подкидывать (projecti fratres, <i>sc.</i> Romulus et Remus <i>Man</i>); сбрасывать, скидывать (projectis vestimentis <i>Pt</i>);\n<b>6)</b> отказываться, отрекаться, откинуть (pudorem <i>O</i>; spem salutis <i>PJ</i>): p. animam <i>V</i> лишить себя жизни;\n<b>7)</b> пренебрегать, оставлять без внимания (omnia <i>Cs</i>): p. auctoritatem alicujus <i>T</i> ни во что не ставить кого-л.; se in fletus p. <i>L</i> унизиться до рыданий, зарыдать;\n<b>8)</b> проливать (lacrimas <i>bAl</i>);\n<b>9)</b> произносить (verba <i>Sen</i>);\n<b>10)</b> откладывать, отсрочивать: p. aliquem <i>T</i> отложить чьё-л. дело. — <i>См. тж.</i> projectus."],
  ["prolabor", "<big>prolabor</big>\n\n<b>pro–labor, lapsus sum, labi</b> <i>depon.</i>\n<b>1)</b> мало-помалу продвигаться, скользить вперёд: serpens prolabitur Argo <i>C</i> медленно подползает (созвездие) Арго;\n<b>2)</b> проскальзывать, мало-помалу (незаметно) дойти, неожиданно прийти (ad aliquid <i>C, T etc.</i>): p. in misericordiam <i>L</i> разжалобиться; p. ad superbiam <i>T</i> проникнуться гордостью;\n<b>3)</b> скользить вниз, падать (ex equo <i>L</i>);\n<b>4)</b> ошибаться, допускать погрешности, делать промахи (in multis rebus <i>C</i>): ab aliqua cupiditate prolapsum verbum <i>C</i> слово, вырвавшееся в минуту некоего увлечения;\n<b>5)</b> приходить в упадок, портиться (mores prolabuntur <i>C</i>); гибнуть, рушиться (prolapsum clade Romana imperium <i>L</i>; Pergama prolapsa <i>V</i>)."],
  ["prolapsio", "<big>prolapsio</big>\n\n<b>prolapsio, onis</b> <i>f</i> [prolabor]\n<b>1)</b> падение (ingredi sine casu et prolapsione <i>C</i>);\n<b>2)</b> разрушение, обвал (aedifici! <i>Su</i>);\n<b>3)</b> промах, оплошность (temeritas et p. <i>Amm</i>)."],
  ["prolatio", "<big>prolatio</big>\n\n<b>prolatio, onis</b> <i>f</i> [profero]\n<b>1)</b> представление, упоминание, приведение (exemplorum <i>C</i>);\n<b>2)</b> расширение (aedificiorum <i>Su</i>); раздвигание (finium <i>L</i>);\n<b>3)</b> откладывание, отсрочка (judicii <i>C</i>; diei <i>Cs</i>): p. rerum (= res prolatae) <i>C</i> перерыв в общественных делах;\n<b>4)</b> произнесение (verborum <i>Eccl</i>); произношение (Latinorum nominum <i>L</i>)."],
  ["prolativus", "<big>prolativus</big>\n\n<b>prolativus, a, um</b> [profero]\nпроизнесённый (verbum <i>Eccl</i>)."],
  ["prolato", "<big>prolato</big>\n\n<b>pro–lato, avi, atum, are</b> [<i>intens. к</i> profero]\n<b>1)</b> расширять, увеличивать (agros <i>T</i>; imperium <i>Q</i>);\n<b>2)</b> длить, <i>перен.</i> проводить (vitam in hortis <i>T</i>);\n<b>3)</b> сохранять, удерживать (spem <i>T</i>);\n<b>4)</b> откладывать, отсрочивать (diem ex die <i>T</i>); затягивать (bellum <i>T</i>)."],
  ["prolatus", "<big>prolatus</big>\n\n<b>I prolatus, a, um</b> <i>part. pf. к</i>\nprofero.\n\n<b>II prolatus, (us) </b> <i>m</i>\nпроизведение, производство <i>Tert</i>."],
  ["prolectibilis", "<big>prolectibilis</big>\n\n<b>prolectibilis, e</b> [prolicio]\nпривлекательный, соблазнительный <i>Sid</i> (<i>v. l.</i>)."],
  ["prolecto", "<big>prolecto</big>\n\n<b>prolecto, avi, atum, are</b> [<i>intens. к</i> prolicio]\nвызывать, привлекать (aviculae prolectatae aliqua re <i>Ap</i>); манить, заманивать, прельщать, соблазнять (aliquem spe alicujus rei <i>C</i>; aliquam ad nuptias <i>Ap</i>)."],
  ["prolepsis", "<big>prolepsis</big>\n\n<b>prolepsis, is</b> <i>f</i> (<i>греч.</i>)\nпролепсис, упреждение:\n<b>1)</b> <i>ритор. предвосхищение возможного возражения</i> <i>Q</i>;\n<b>2)</b> <i>приписывание свойства, которого не могло быть до указанного действия</i> (<i>напр.: submersas</i> obruere puppes <i>V</i>; astringere <i>glacialem</i> frigore pontum <i>Lcn</i>; amor <i>deceptam</i> fefellit <i>V</i>)."],
  ["proles", "<big>proles</big>\n\n<b>proles, is</b> <i>f</i> [pro + alo]\n<b>1)</b> отпрыск, потомок, детище, дитя (p. postuma <i>V</i>): Bacchi rustica p. <i>Tib</i> = Priapus; p. fulminis <i>SenT</i> = Bacchus;\n<b>2)</b> род, потомство (p. futurorum hominum <i>C</i>);\n<b>3)</b> племя, раса (p. ferrea <i>O</i>); <i>поэт.</i> население, животный <i>или</i> растительный мир (p. maris immensi <i>V</i>);\n<b>4)</b> молодёжь (p. equitum peditumque <i>C</i>);\n<b>5)</b> <i>pl. Eccl</i> = testiculi."],
  ["proletarius", "<big>proletarius</big>\n\n<b>I proletarius, a, um</b> [proles]\n<b>1)</b> производящий потомство <i>AG</i>;\n<b>2)</b> простонародный (sermo <i>Pl</i>).\n\n<b>II proletarius, i</b> <i>m</i> (<i>тж.</i> p. civis <i>AG</i>)\n(<i>по системе Сервия Туллия</i>) пролетарий, <i>римск. гражданин, принадлежавший к неимущему и неподатному сословию, но юридически свободный</i> <i>C, L</i>."],
  ["prolevo", "<big>prolevo</big>\n\n<b>pro–levo, —, —, are</b>\nвынимать, извлекать (aliquem peccatorum flucti bus mersum <i>Tert</i>)."],
  ["prolibo", "<big>prolibo</big>\n\n<b>pro–libo, —, —, are</b>\nприносить в жертву, совершать возлияния (vina diis <i>PM</i>)."],
  ["prolicio", "<big>prolicio</big>\n\n<b>prolicio, —, —, ere</b> [pro + lacio]\nвызывать (voluptatem <i>O</i>); заманивать (aliquem huc <i>Pl</i>): p. aliquem ad spem <i>T</i> подавать (внушать) кому-л. надежду."],
  ["prolimen", "<big>prolimen</big>\n\n<b>pro–limen, inis</b> <i>n</i>\nплощадка перед порогом, преддверие <i>Hier</i>."],
  ["proliquatus", "<big>proliquatus</big>\n\n<b>pro–liquatus, a, um</b>\nжидкий, текучий <i>Ap</i>."],
  ["prolixe", "<big>prolixe</big>\n\n<b>prolixe</b> [prolixus]\n<b>1)</b> обильно, щедро, пышно (arbor p. foliata <i>Ap</i>);\n<b>2)</b> любезно, охотно (respondere <i>C</i>; consentire de aliqua re <i>Ap</i>)."],
  ["prolixitas", "<big>prolixitas</big>\n\n<b>prolixitas, atis</b> <i>f</i> [prolixus]\n<b>1)</b> большая величина, огромное протяжение (terrae <i>Ap</i>);\n<b>2)</b> большая продолжительность (temporis <i>Dig</i>);\n<b>3)</b> многословность, пространность <i>Eccl</i>."],
  ["prolixitudo", "<big>prolixitudo</big>\n\n<b>prolixitudo, inis</b> <i>f</i> <i>Pac</i>\n= prolixitas 3."],
  ["prolixo", "<big>prolixo</big>\n\n<b>pro–lixo, —, —, are</b>\nрасширять, удлинять (aciem ferramenti <i>Col</i>)."],
  ["prolixus", "<big>prolixus</big>\n\n<b>prolixus, a, um</b> [pro + liqueo]\n<b>1)</b> широкий, просторный (tunica <i>AG</i>); длинный (barba <i>Hier</i>; coma prolixior <i>Ap</i>);\n<b>2)</b> большой, крупный (corpus <i>Su</i>); коренастый, крепкий, сильный (arator <i>Col</i>);\n<b>3)</b> далёкий, дальний (ictus <i>Lcr</i>);\n<b>4)</b> благоприятный, счастливый (res secundae atque prolixae <i>Cato</i>); благожелательный, любезный (natura <i>C</i>): venia prolixior <i>Ap</i> большая снисходительность;\n<b>5)</b> обильный, обстоятельный, пространный (oratio <i>Ap</i>);\n<b>6)</b> долгий, продолжительный (tempus <i>Dig</i>);\n<b>7)</b> преклонный (aetas <i>Dig</i>);\n<b>8)</b> растянутый, многословный: ne sim p. <i>Macr</i> чтобы мне не быть многословным, <i>т. е.</i> для краткости."],
  ["prolocutor", "<big>prolocutor</big>\n\n<b>prolocutor, oris</b> <i>m</i> [proloquor]\nправозаступник, защитник <i>Q</i>."],
  ["prologium", "<big>prologium</big>\n\n<b>prologium, i</b> <i>n</i> (<i>греч.</i>)\nпредисловие <i>Pac</i>."],
  ["prologumene lex", "<big>prologumene lex</big>\n\n<b>prologumene lex</b> <i>f</i> (<i>греч.</i> nomos ho prologumenos)\nзакон с разъяснительным предисловием <i>Sen</i>."],
  ["prologus", "<big>prologus</big>\n\n<b>prologus, i</b> <i>m</i> (<i>греч.</i>)\n<b>1)</b> пролог <i>Ter, Q</i>;\n<b>2)</b> актёр, читающий пролог <i>Ter</i>."],
  ["prolongo", "<big>prolongo</big>\n\n<b>pro–longo, avi, atum, are</b> [longus]\nпродлевать (dies alicujus <i>Vlg</i>); продолжать (iniquitatem <i>Vlg</i>)."],
  ["proloquium", "<big>proloquium</big>\n\n<b>proloquium, i</b> <i>n</i> [proloquor]\nвысказывание, утверждение, положение <i>Vr, Ap, AG etc.</i>"],
  ["proloquor", "<big>proloquor</big>\n\n<b>pro–loquor, locutus (loquutus) sum, loqui</b>\n<b>1)</b> высказывать, выражать (cogitata <i>Ter</i>): mendacium p. alicui <i>Pl</i> солгать кому-л.; omitto proloqui <i>Ter</i> об этом я умалчиваю;\n<b>2)</b> предсказывать, пророчить <i>Prp</i>."],
  ["proloquutor", "<big>proloquutor</big>\n\n<b>proloquutor</b> <i>v. l.</i>\n= prolocutor."],
  ["prolubido", "<big>prolubido</big>\n\n<b>prolubido, inis</b> <i>f</i> <i>Vr</i>\n= prolubium."],
  ["prolubium", "<big>prolubium</big>\n\n<b>prolubium, i</b> <i>n</i> [pro + lubet]\n<b>1)</b> желание, влечение <i>Ter, AG</i>;\n<b>2)</b> удовольствие <i>AG</i>."],
  ["proludium", "<big>proludium</big>\n\n<b>pro–ludium, i</b> <i>n</i>\nвступительное упражнение, проба <i>Amm</i>; <i>воен.</i> учение, упражнение (exercitus <i>Amm</i>)."],
  ["proludo", "<big>proludo</big>\n\n<b>pro–ludo, lusi, lusum, ere</b>\n<b>1)</b> играть в виде вступления <i>SenT</i>;\n<b>2)</b> совершать предварительное упражнение, подготовляться (ad pugnam <i>V</i>); <i>перен.</i> делать введение (sententiis <i>C</i>)."],
  ["proluo", "<big>proluo</big>\n\n<b>pro–luo, lui, lutum, ere</b>\n<b>1)</b> промывать, споласкивать (vino os <i>Col</i>); умывать (vivo in rore maniis <i>O</i>); омочить (labra fonte <i>Pers</i>): se p. <i>или</i> <i>pass.</i> prolui пить, выпивать; multa prolutus vappa <i>H</i> сильно напившийся дешёвым вином;\n<b>2)</b> уносить течением, смывать (silvas Eri danus proluit <i>V</i>);\n<b>3)</b> выносить течением, выбрасывать на сушу (omne genus natantum in litore proluit fluctus <i>V</i>);\n<b>4)</b> растрачивать, прокучивать (pecuniam suam <i>AG</i>)."],
  ["prolusio", "<big>prolusio</big>\n\n<b>prolusio, onis</b> <i>f</i> [proludo]\nвступление, введение, подготовительное упражнение <i>C</i>."],
  ["proluvies", "<big>proluvies</big>\n\n<b>proluvies, ei</b> <i>f</i> [proluo]\n<b>1)</b> паводок, разлив <i>Lcr</i>;\n<b>2)</b> истечение, выделение, извержение (alvi <i>Lcr, Col</i>; ventris <i>V, Col</i>)."],
  ["proluvio", "<big>proluvio</big>\n\n<b>proluvio, onis</b> <i>f</i> [proluo]\nнаводнение, разлив <i>Ap</i>."],
  ["proluvium", "<big>proluvium</big>\n\n<b>proluvium, i</b> <i>n</i> [proluo]\n<b>1)</b> разлив <i>Eccl</i>;\n<b>2)</b> истечение, нечистоты (rei venereae <i>AG</i>)."],
  ["prolytae", "<big>prolytae</big>\n\n<b>prolytae, arum</b> <i>m</i> (<i>греч.</i>)\nизучающие правоведение (<i>на 5-ом году обучения</i>) <i>Dig</i>."],
  ["proma", "<big>proma</big>\n\n<b>proma, ae</b> <i>f</i> [promus I]\nкладовая <i>Tert</i>."],
  ["promarinus", "<big>promarinus</big>\n\n<b>promarinus</b> <i>Fronto</i>\n= permarinus."],
  ["promatertera", "<big>promatertera</big>\n\n<b>pro–matertera, ae</b> <i>f</i>\nсестра бабки <i>Dig</i>."],
  ["promercalis", "<big>promercalis</big>\n\n<b>promercalis, e</b> [pro + merx]\nпродающийся, продажный <i>Col, Su, AG</i>."],
  ["promercium", "<big>promercium</big>\n\n<b>pro–mercium, i</b> <i>n</i> [merx]\nкупля-продажа, торговля <i>Dig</i>."],
  ["promereo", "<big>promereo</big>\n\n<b>pro–mereo, ui, itum, ere</b>\nзаслуживать, оказываться достойным (p. deorum indulgentiam <i>PJ</i>); снискивать (voluntatem omnium <i>Su</i>; poenam <i>O</i>): p. de aliquo (in aliquem) <i>C</i> оказать кому-л. большие услуги; p. socios <i>Su</i> снискать признательность союзников."],
  ["promereor", "<big>promereor</big>\n\n<b>pro–mereor, itus sum, eri</b> <i>depon.</i> <i>Pl</i>\n= promereo."],
  ["promeritum", "<big>promeritum</big>\n\n<b>promeritum, i</b> <i>n</i> [promereo]\n<b>1)</b> (<i>тж.</i> bene p. <i>Lcr</i>) услуга, благодеяние (in aliquem <i>C</i>);\n<b>2)</b> (<i>тж.</i> male p. <i>Pl</i>) обида <i>bAfr</i>; вина <i>C</i>."],
  ["Prometheus", "<big>Prometheus</big>\n\n<b>I Promēthe͡us, eī</b> <i>и</i> <b>eos</b> <i>m</i>\nПрометей, <i>сын титана Япета и Климены, брат Эпиметея, отец Девкалиона; вылепив из глины человека, он оживил его посредством огня, который похитил у Юпитера; за это был прикован к скале Кавказа, где коршун (или орёл) терзал его внутренности, пока Геркулес не освободил его</i> <i>Vr, rhH, C etc.</i>\n\n<b>II Promēthēus, a, um</b>\nпрометеев: Promethea juga <i>Prp</i> (rupes <i>M</i>) = Caucasus."],
  ["Promethiades", "<big>Promethiades</big>\n\n<b>Promethiades</b> <i>и</i> <b>Promethides, ae</b> <i>m</i>\nПромети(а)д, сын Прометея, <i>т. е.</i> Девкалион <i>O</i>."],
  ["promico", "<big>promico</big>\n\n<b>promico, —, —, are</b>\n<b>1)</b> пробиваться, выступать наружу (lacrimae promicant <i>Ap</i>); прорастать, вырастать (promicant molles plumulae <i>Ap</i>);\n<b>2)</b> произносить (promicanda oratio <i>Naev</i>)."],
  ["prominens", "<big>prominens</big>\n\n<b>prominens, entis</b>\n<b>1.</b> <i>part. praes. к</i> promineo;\n<b>2.</b> <i>subst. n</i> выступ, мыс: prominentia montium <i>T</i> горные отроги."],
  ["prominenter", "<big>prominenter</big>\n\n<b>prominenter</b> (<i>тк. в</i> <i>compar.</i>)\nвыпукло <i>CA</i>."],
  ["prominentia", "<big>prominentia</big>\n\n<b>I prominentia, ae</b> <i>f</i> [prominens]\n<b>1)</b> выступ, мыс <i>Sol, Vtr</i>;\n<b>2)</b> выступание вперёд, выпяченность: p. oculorum <i>CA</i> пучеглазие.\n\n<b>II prominentia, ium</b> <i>n</i>\nпредметы переднего плана (abscedentia et p. <i>Vtr</i>)."],
  ["promineo", "<big>promineo</big>\n\n<b>pro–mineo, minui, —, ere</b>\n<b>1)</b> выдаваться вперёд, выступать (in mare <i>L</i>); возвышаться (collis prominens <i>L</i>); торчать (barba prominens <i>Sen</i>): oculi prominentes <i>PM</i> глаза навыкате; p. pectore <i>Cs</i> свешиваться вниз (с крепостной стены), <i>т. е.</i> перегнуться через стену;\n<b>2)</b> простираться, достигать (gloria alicujus in memoriam et posteritatem prominet <i>L</i>)."],
  ["promino", "<big>promino</big>\n\n<b>pro–mino, —, —, are</b>\nгнать вперёд (jumenta ad lacum <i>Ap</i>)."],
  ["prominulus", "<big>prominulus</big>\n\n<b>prominulus, a, um</b>\nнесколько выдающийся вперёд (venter <i>Capit</i>; mammae <i>Sol</i>)."],
  ["promiscam", "<big>promiscam</big>\n\n<b>promiscam</b> <i>adv.</i> [promiscus]\nсообща, вместе <i>Pl</i>."],
  ["promisce", "<big>promisce</big>\n\n<b>promisce</b> <i>C, L, AG</i>\n= promiscue."],
  ["promiscua", "<big>promiscua</big>\n\n<b>promiscua, orum</b> <i>n</i> [promiscuus] <i>грам.</i>\n(<i>sc.</i> nomina) слова общего рода <i>Q</i>."],
  ["promiscue", "<big>promiscue</big>\n\n<b>promiscue</b> [promiscuus]\nсмешанно, без разбору (suffragium p. — <i>v. l.</i> promisce — omnibus datum <i>L</i>); вместе, сообща (in fluminibus perlui <i>Cs</i>)."],
  ["promiscus", "<big>promiscus</big>\n\n<b>promiscus, a, um</b> <i>V, L, T etc.</i>\n= promiscuus."],
  ["promiscuus", "<big>promiscuus</big>\n\n<b>promiscuus, a, um</b> [pro + misceo]\n<b>1)</b> смешанный, общий (comitia plebi et patribus promiscua <i>L</i>): conubia promiscua <i>L</i> смешанные браки (<i>между патрициями и плебеями</i>); in promiscuo <i>L, T</i> сообща, вместе;\n<b>2)</b> безразличный: divina atque humana promiscua habere <i>Sl</i> ставить божеское на один уровень с человеческим, <i>т. е.</i> презрительно относиться к богам; injurias et honores vulgi in promiscuo habere <i>PS ap. Sen</i> быть равнодушным как к оскорблениям, так и к похвалам черни;\n<b>3)</b> обыкновенный, простой (cibus <i>PJ</i>);\n<b>4)</b> общедоступный (spectaculum <i>T</i>);\n<b>5)</b> общественный (privata atque promiscua <i>T</i>)."],
  ["promisi", "<big>promisi</big>\n\n<b>promisi</b> <i>pf. к</i>\npromitto."],
  ["promisse", "<big>promisse</big>\n\n<b>promisse</b> <i>Ctl</i>\n= promisisse (<i>к</i> promitto)."],
  ["promissio", "<big>promissio</big>\n\n<b>promissio, onis</b> <i>f</i> [promitto]\nобещание <i>C, Q etc.</i>"],
  ["promissive", "<big>promissive</big>\n\n<b>promissive</b>\nв форме обещания <i>Tert</i>."],
  ["promissivus", "<big>promissivus</big>\n\n<b>promissivus, a, um</b>\nсулящий, предвещающий (sententiae <i>Is</i>): tempus promissivum <i>грам.</i> = futurum."],
  ["promissor", "<big>promissor</big>\n\n<b>promissor, oris</b> <i>m</i> [promitto]\n<b>1)</b> (много)обещающий <i>Aug</i>; <i>перен.</i> хвастун <i>H, Q</i>;\n<b>2)</b> <i>юр.</i> дающий формальное обязательство <i>Dig</i>."],
  ["promissum", "<big>promissum</big>\n\n<b>promissum, i</b> <i>n</i> [promitto]\nобещанное, обещание: facere <i>C</i> (implere <i>PJ</i>) p. <i>или</i> promisso stare <i>C</i> (manere <i>V</i>) исполнить обещание, сдержать слово; promissa dare <i>Ctl</i> давать <i>или</i> <i>O</i> исполнять обещания; exigere p. <i>C</i> требовать исполнения обещания."],
  ["promissus", "<big>promissus</big>\n\n<b>I promissus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> promitto;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> длинный (capillus <i>Cs</i>; barba <i>L, PJ</i>);\n<b>2)</b> обвислый (venter <i>Col</i>).\n\n<b>II promissus, (us) </b> <i>m</i> <i>Man</i>\n= promissum."],
  ["promisti", "<big>promisti</big>\n\n<b>promisti</b> <i>Ter, Ctl</i>\n= promisisti (<i>pf. к</i> promitto)."],
  ["promitto", "<big>promitto</big>\n\n<b>pro–mitto, misi, missum, ere</b>\n<b>1)</b> отпускать, отращивать (ramos <i>Col</i>; capillum, barbam <i>L</i>); se p. <i>или</i> <i>med.-pass.</i> вырастать, расти (arbor se promittit <i>PM</i>; sonus promittitur <i>PM</i>);\n<b>2)</b> обещать, сулить (alicui aliquid <i>или</i> de aliqua re <i>C etc.</i>): quum promiseris, facias <i>Sen</i> если обещал, исполни || давать обет (Jovi templum p. <i>C etc.</i>): alicui bene (large) p. <i>O etc.</i> давать кому-л. щедрые обещания; sibi aliquid p. <i>O</i> льстить себя надеждой на что-л.; se alicui p. <i>O</i> обещать кому-л. вернуться; se ultorem p. <i>V</i> грозить местью; p. damni infecti <i>C</i> обещать возмещение ущерба (принять на себя ответственность за ущерб); p. ad aliquem <i>C</i> обещать прийти к кому-л.; p. ad cenam <i>Pl, Ph, Pt</i> принять приглашение к ужину; si quid p. de me possum aliud vere, promitto <i>H</i> если я в чём-л. могу поручиться за себя, то именно в этом;\n<b>3)</b> предлагать: ducem se itineris p. <i>Pt</i> предложить свои услуги в качестве проводника;\n<b>4)</b> предсказывать, предвещать (alicui aliquid <i>C, Fl</i>)."],
  ["promo", "<big>promo</big>\n\n<b>promo, prompsi, promptum, ere</b> [pro + emo]\n<b>1)</b> вынимать, доставать, вытаскивать (pecuniam ex aerario <i>C</i>; tela e pharetra <i>O</i>); брать (vina dolio <i>H</i>): se p. выходить наружу (cavo robore <i>V</i>);\n<b>2)</b> извлекать, заимствовать (argumenta ex aliqua re <i>C</i>);\n<b>3)</b> прилагать (vires <i>V</i>); проявлять (justitiam <i>PM</i>); выражать, обнаруживать (animi voluntatem <i>Q</i>);\n<b>4)</b> открывать, раскрывать (obscura <i>H</i>); высказывать (sententiam suam <i>Pt</i>); рассказывать, говорить (aliquid adversus aliquem <i>T</i>); произносить (syllabam circumflexe <i>AG</i>);\n<b>5)</b> истолковывать, разъяснять (jura alicui <i>H</i>);\n<b>6)</b> придумывать, изобретать (nova crustula <i>H</i>);\n<b>7)</b> издавать, публиковать (carmen <i>Ap</i>); se p. давать побеги (vites promunt se <i>Col</i>)."],
  ["promoneo", "<big>promoneo</big>\n\n<b>pro–moneo, —, —, ere</b>\nзаранее обращать внимание, предупреждать (aliquem de aliqua re <i>C</i> — <i>v. l.</i>)."],
  ["promontorium", "<big>promontorium</big>\n\n<b>promontorium (promonturium), i</b> <i>n</i> <i>v. l.</i>\n= promunturium."],
  ["promoram", "<big>promoram</big>\n\n<b>promoram</b> <i>H, Ph</i>\n= promoveram (<i>ppf. к</i> promoveo)."],
  ["promoscida", "<big>promoscida</big>\n\n<b>promoscida, ae</b> <i>f Is</i> <i>и</i> <b>promoscis, idis</b> <i>f Sol</i>\n= proboscis."],
  ["promossem", "<big>promossem</big>\n\n<b>promossem</b> <i>O</i>\n= promovissem (<i>ppf. conjct. к</i> promoveo)."],
  ["promota", "<big>promota</big>\n\n<b>promota, orum</b> <i>n</i> <i>C</i>\n= producta."],
  ["promotio", "<big>promotio</big>\n\n<b>promotio, onis</b> <i>f</i> [promoveo]\nповышение (<i>в должности</i>) <i>или</i> возведение, производство (<i>в звание</i>) <i>Lampr</i>."],
  ["promotus", "<big>promotus</big>\n\n<b>I promotus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> promoveo;\n<b>2.</b> <i>adj.</i> поздний (promota nocte <i>Ap</i>).\n\n<b>II promotus, us</b> <i>m</i> <i>Tert</i>\n= promotio."],
  ["promoveo", "<big>promoveo</big>\n\n<b>pro–moveo, movi, motum, ere</b>\n<b>1)</b> двигать вперёд, продвигать (saxa vectibus <i>Cs</i>; castra ad Carthaginem <i>L</i>; acies <i>Lcn</i>); продвигаться вперёд (nihil p. <i>Ter</i>): in arte promovisse <i>Ap</i> сделать успехи в искусствен продвигать, приближать (aggerem ad urbem <i>L</i>); расширять, раздвигать (moenia <i>Su</i>; terminos imperii <i>PM</i>);\n<b>2)</b> откладывать (nuptias <i>Ter</i>);\n<b>3)</b> раскрывать, разоблачать (arcana loco <i>H</i>);\n<b>4)</b> смещать, вывихнуть (femur <i>CC</i>; ossa suis sedibus promoventur <i>CC</i>);\n<b>5)</b> развивать, укреплять (vim insitam <i>H</i>);\n<b>6)</b> повышать, возводить (aliquem in amplissimum ordinem <i>PJ</i>); назначать (judices <i>Lampr</i>)."],
  ["prompsi", "<big>prompsi</big>\n\n<b>prompsi</b> <i>pf. к</i>\npromo."],
  ["promptarium", "<big>promptarium</big>\n\n<b>promptarium</b> <i>Aus</i>\n= promptuarium."],
  ["promptarius", "<big>promptarius</big>\n\n<b>promptarius</b> <i>Pl</i>\n= promptuarius."],
  ["prompte", "<big>prompte</big>\n\n<b>prompte</b> [promptus]\n<b>1)</b> легко, без затруднений (aliquid facere <i>J, VM</i>);\n<b>2)</b> быстро, проворно (operam dare <i>T</i>);\n<b>3)</b> без колебаний, смело (necem subire <i>T</i>);\n<b>4)</b> охотно, с готовностью (veniam alicujus rei impertire <i>Ap</i>);\n<b>5)</b> прямо, напрямик (dicere <i>C</i>)."],
  ["promptim", "<big>promptim</big>\n\n<b>promptim</b> <i>Eccl</i>\n= prompte."],
  ["prompto", "<big>prompto</big>\n\n<b>prompto, —, —, are</b> [<i>frequ. к</i> promo]\nчасто <i>или</i> щедро раздавать (aliquid <i>Pl</i>): Jovis thesauros p. <i>Pl</i> заведовать казной Юпитера."],
  ["promptuarium", "<big>promptuarium</big>\n\n<b>promptuarium, i</b> <i>n</i>\nбуфет <i>или</i> кладовая <i>Ap</i>; <i>перен.</i> вместилище: p. rationis et orationis <i>Ap</i> = os."],
  ["promptuarius", "<big>promptuarius</big>\n\n<b>promptuarius, a, um</b> [promo]\nслужащий для хранения припасов (armarium <i>Cato</i>): promptuaria cella <i>Pl ирон.</i> место заключения."],
  ["promptulus", "<big>promptulus</big>\n\n<b>promptulus, a, um</b> [promptus]\nдовольно способный, искусный (ad loquendum <i>Hier</i>)."],
  ["promptus", "<big>promptus</big>\n\n<b>I promptus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> promo;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> явный, очевидный (p. et apertus <i>C</i>; p. et obvius <i>T</i>);\n<b>2)</b> находящийся в полной готовности, имеющийся наготове (promptas habere sagittas <i>O</i>); скорый, немедленный (p. in auxilium <i>CC</i>): p. in rem subitam consensus <i>T</i> быстрое согласование действий в неожиданных обстоятельствах || готовый, решительный, ревностный, склонный (p. ad aliquid <i>C</i>, in aliquid <i>T</i> <i>и</i> alicui rei <i>L, T</i>): homo p. <i>или</i> p. manu <i>Sl, VP, L</i> деятельный <i>или</i> храбрый человек; industria p. <i>Ap</i> неутомимый, деятельный; quod illi promptissimum erat <i>Su</i> что было ему весьма свойственно; servitio p. <i>T</i> угодливый; p. metuenda pati <i>Lcn</i> не боящийся опасностей; promptissimus ad jussa <i>T</i> готовый беспрекословно исполнить любое приказание;\n<b>3)</b> лёгкий, удобный (defensio <i>C</i>; aditus <i>T</i>; moenia prompta oppugnanti <i>L</i>).\n\n<b>II promptus, (us)</b> (<i>встреч. тк.</i> <i>abl. sg.</i>) <i>m</i> [promo]:\nesse in promptu <i>C etc.</i> быть на виду, быть очевидным <i>или</i> быть лёгким, нетрудным; ponere (habere) in promptu <i>C, Sl</i> показывать, не скрывать; in promptu esse <i>C</i> быть наготове, быть готовым; in promptu habere <i>C etc.</i> иметь наготове, в готовности, под рукой."],
  ["promt-", "<big>promt-</big>\n\n<b>promt-</b> <i>v. l.</i>\n= prompt-."],
  ["promulceo", "<big>promulceo</big>\n\n<b>pro–mulceo, —, mulsum, ere</b>\nприглаживать <i>или</i> зачёсывать вперед (promulsae caproneae <i>Ap</i>)."],
  ["promulgatio", "<big>promulgatio</big>\n\n<b>promulgatio, onis</b> <i>f</i> [promulgo]\nобъявление, обнародование (законопроекта) (leges nulla promulgatione latae <i>C</i>)."],
  ["promulgator", "<big>promulgator</big>\n\n<b>promulgator, oris</b> <i>m</i>\nвозвещающий (p. feriarum <i>Fronto</i>; p. legis <i>Aug</i>)."],
  ["promulgo", "<big>promulgo</big>\n\n<b>promulgo, avi, atum, are</b>\n<b>1)</b> делать известным, объявлять (dies festos <i>PM</i>);\n<b>2)</b> (<i>о законопроектах</i>) обнародовать (legem <i>C, L</i>; rogationem <i>Cs</i>)."],
  ["promulsidare", "<big>promulsidare</big>\n\n<b>promulsidare, is</b> <i>n</i> [promulsis]\nподнос для первого блюда <i>Pt, Dig</i>."],
  ["promulsis", "<big>promulsis</big>\n\n<b>promulsis, idis</b> <i>f</i> [pro + mulsum]\n<b>1)</b> первое кушанье, первое блюдо <i>или</i> закуска <i>C</i>; <i>перен.</i> p. libidinis <i>Pt</i>;\n<b>2)</b> <i>Tert</i> = promulsidare."],
  ["promum", "<big>promum</big>\n\n<b>promum, i</b> <i>n</i>\nкладовая <i>Tert</i>."],
  ["promunturium", "<big>promunturium</big>\n\n<b>promunturium, i</b> <i>n</i> [promineo]\n<b>1)</b> горный выступ, отрог <i>L</i>;\n<b>2)</b> мыс <i>C, Cs etc.</i>"],
  ["promurale", "<big>promurale</big>\n\n<b>pro–murale, is</b> <i>n</i>\nпередовое укрепление городской стены <i>Is</i>."],
  ["promus", "<big>promus</big>\n\n<b>I promus, a, um</b> [promo]\nслужащий для хранения припасов (cella <i>Tert</i>).\n\n<b>II promus, i</b> <i>m</i> [promo]\n<b>1)</b> ключник, эконом <i>Pl, Vr, C, H etc.</i>;\n<b>2)</b> хранитель, раздатчик (librorum <i>Ap</i>)."],
  ["promuscis", "<big>promuscis</big>\n\n<b>promuscis, idis</b> <i>f</i> <i>Veg, Eccl</i>\n= proboscis."],
  ["promutuum", "<big>promutuum</big>\n\n<b>promutuum, i</b> <i>n</i>\nденежный аванс <i>Dig</i>."],
  ["promutuus", "<big>promutuus</big>\n\n<b>pro–mutuus, a, um</b>\nвыданный вперёд, данный в виде ссуды <i>или</i> займа (vectigal <i>Cs</i>)."],
  ["pronaon", "<big>pronaon</big>\n\n<b>pronaon (-um), i</b> <i>n</i> <i>или</i> <b>pronaos (-us), i</b> <i>m</i> (<i>греч.</i>)\nпаперть <i>или</i> притвор храма <i>Vtr</i>."],
  ["pronatus", "<big>pronatus</big>\n\n<b>pronatus</b> <i>Tert</i>\n= prognatus."],
  ["prone", "<big>prone</big>\n\n<b>prone</b> [pronus]\n<b>1)</b> наклонно, покато <i>Cs</i>;\n<b>2)</b> охотно <i>Naev, Amm</i>."],
  ["pronecto", "<big>pronecto</big>\n\n<b>pro–necto, —, —, ere</b>\nвязать, прясть (seriem aevi <i>Sl</i>)."],
  ["pronepos", "<big>pronepos</big>\n\n<b>pro–nepos, otis</b> <i>m</i>\nправнук <i>C etc.</i>"],
  ["proneptis", "<big>proneptis</big>\n\n<b>pro–neptis, is</b> <i>f</i>\nправнучка <i>Pers, Su etc.</i>"],
  ["pronis", "<big>pronis</big>\n\n<b>pronis, e</b> <i>Vr</i>\n= pronus."],
  ["prono", "<big>prono</big>\n\n<b>prono, avi, atum, are</b> [pronus]\nнаклонять вперёд (aliquid <i>Sid</i>)."],
  ["pronoea", "<big>pronoea</big>\n\n<b>pronoea, ae</b> <i>f</i> (<i>греч.</i>; <i>лат.</i> providentia)\nпровидение <i>C</i>."],
  ["pronomen", "<big>pronomen</big>\n\n<b>pro–nomen, inis</b> <i>n</i>\nместоимение <i>Vr, Q</i>."],
  ["pronominalis", "<big>pronominalis</big>\n\n<b>pronominalis, e</b> [pronomen] <i>грам.</i>\nместоименный."],
  ["pronominatio", "<big>pronominatio</big>\n\n<b>pro–nominatio, onis</b> <i>f</i> <i>ритор.</i>\nантономасия, <i>замена нарицательного имени собственным</i> (<i>напр.</i>, Cicero <i>вместо</i> orator magnus) <i>rhH</i>."],
  ["pronuba", "<big>pronuba</big>\n\n<b>pronuba, ae</b> <i>f</i> [pro + nubo]\nдружка невесты <i>Ctl, Vr, Tert</i>; распорядительница на свадебном торжестве (p. Tisiphone <i>O</i>): Juno P. <i>V, O</i> Юнона-покровительница брака."],
  ["pronubo", "<big>pronubo</big>\n\n<b>pronubo, —, —, are</b> [pronubus]\nустраивать браки <i>Hier</i>."],
  ["pronubus", "<big>pronubus</big>\n\n<b>I pronubus, i</b> <i>m</i> [pro + nubo]\nдружка жениха <i>Lampr, Vlg</i>.\n\n<b>II pronubus, a, um</b>\nбрачный, свадебный (flamma, nox <i>Cld</i>); венчальный (anulus <i>Tert</i>)."],
  ["pronum", "<big>pronum</big>\n\n<b>pronum, i</b> <i>n</i> [pronus]\n<b>1)</b> спуск, скат, покатость (prona montium <i>QC</i>);\n<b>2)</b> тяготение вниз (nihil proni habere <i>C</i>)."],
  ["pronumero", "<big>pronumero</big>\n\n<b>pro–numero, —, —, are</b>\nоплачивать вперёд (aliquid <i>Sid</i>)."],
  ["pronuntiabilis", "<big>pronuntiabilis</big>\n\n<b>pronuntiabilis, e</b>\n«произносимый», <i>т. е.</i> содержащий законченную мысль (oratio <i>Ap</i>)."],
  ["pronuntiatio", "<big>pronuntiatio</big>\n\n<b>pronuntiatio, onis</b> <i>f</i> [pronuntio]\n<b>1)</b> объявление, уведомление <i>Cs</i>;\n<b>2)</b> заключение суда, судебный приговор <i>C, Pt, Dig</i>;\n<b>3)</b> устное изложение, речь <i>VM</i>;\n<b>4)</b> чтение, декламация <i>rhH, VM, Q</i>;\n<b>5)</b> <i>филос.</i> положение, посылка <i>C</i>."],
  ["pronuntiativus", "<big>pronuntiativus</big>\n\n<b>pronuntiativus</b>\n<i>грам.</i>= indicativus."],
  ["pronuntiator", "<big>pronuntiator</big>\n\n<b>pronuntiator, oris</b> <i>m</i> [pronuntio]\nповествователь, рассказчик (rerum gestarum <i>C</i>)."],
  ["pronuntiatum", "<big>pronuntiatum</big>\n\n<b>pronuntiatum, i</b> <i>n</i> [pronuntio]\n<b>1)</b> суждение, положение, посылка <i>C</i>;\n<b>2)</b> судебный приговор <i>Dig</i>."],
  ["pronuntiatus", "<big>pronuntiatus</big>\n\n<b>pronuntiatus, (us) </b> <i>m</i>\nпроизношение <i>AG</i>."],
  ["pronuntio", "<big>pronuntio</big>\n\n<b>pro–nuntio, avi, atum, are</b>\n<b>1)</b> объявлять, уведомлять, обнародовать (aliquid per praeconem <i>Su</i>); оглашать, зачитывать (sententiam <i>C, Cs</i>; magna Voce nomina <i>C</i>); провозглашать (aliquem praetorem <i>L</i>); <i>воен.</i> приказывать, отдавать распоряжение, назначать (proelium in posterum diem <i>L</i>);\n<b>2)</b> формально обещать (alicui aliquid. <i>L, Sen, Su</i>);\n<b>3)</b> повествовать, рассказывать (quae gesta sunt <i>Cs</i>);\n<b>4)</b> выносить приговор (de aliquo <i>Sen</i>; contra aliquem <i>и</i> pro aliquo <i>AG</i>): pronuntjari ad bestias <i>Tert</i> быть приговорённым к растерзанию дикими зверями;\n<b>5)</b> формулировать (con dicionem <i>Dig</i>); (<i>J</i>) читать, декламировать (aliquid memoriter <i>C</i>); произносить (litteram tractim <i>AG</i>): sententiam alicujus p. <i>Cs</i> ставить чьё-л. предложение на голосование."],
  ["pronuper", "<big>pronuper</big>\n\n<b>pro–nuper</b>\nсовсем недавно <i>Pl</i> (<i>v. l.</i>)."],
  ["pronurus", "<big>pronurus</big>\n\n<b>pro–nurus, us</b> <i>f</i>\nжена внука <i>O, Dig</i>."],
  ["pronus", "<big>pronus</big>\n\n<b>pronus, a, um</b> [pro I]\n<b>1)</b> склонившийся (in baculum <i>Ap</i>); наклонённый, нагнувшийся (auriga <i>V</i>); наклонённый вперёд (motus corporis <i>C</i>); обращенный (orienti <i>Col</i>; ad solem <i>Col</i>); наклонный, круто спускающийся, покатый (via <i>O</i>);\n<b>2)</b> низвергающийся, быстротекущий, стремительный (amnis <i>V</i>);\n<b>3)</b> склоняющийся к закату, заходящий (sidus <i>Prp</i>);\n<b>4)</b> скоропреходящий, быстротечный (anni <i>H</i>);\n<b>5)</b> склонный, расположенный (in <i>или</i> ad aliquid <i>T, Su</i> <i>или</i> alicui rei <i>T</i>): offensioni pronior <i>T</i> обидчивый || благосклонный, благоприятно настроенный (alicui <i>Su</i> <i>или</i> in aliquem <i>T</i>): pronis auribus accipi <i>T</i> охотно выслушиваться;\n<b>6)</b> лёгкий, легко доступный (iter pronum ad honores <i>PJ</i>): p. ad credendum <i>AV</i> легковерный; p. ad fidem <i>L</i> вероятный. — <i>См. тж.</i>, pronum."],
  ["prooemior", "<big>prooemior</big>\n\n<b>prooemior, —, ari</b> <i>depon.</i> (<i>греч.</i>)\nделать вступление <i>PJ, Sid</i>."],
  ["prooemium", "<big>prooemium</big>\n\n<b>prooemium, i</b> <i>n</i> (<i>греч.</i>)\nвведение, вступление, предисловие <i>C, Q</i>; начало, источник (miserae rixae <i>J</i>)."],
  ["propagatio", "<big>propagatio</big>\n\n<b>propagatio, onis</b> <i>f</i> [propago I]\n<b>1)</b> рассаживание, разведение (vitis <i>C</i>; pomorum <i>Col</i>);\n<b>2)</b> распространение, расширение (finium imperii <i>C</i>); удлинение, продолжение, продление (temporis, vitae <i>C</i>); сохранение, передача из рода в род (nominis <i>C</i>)."],
  ["propagator", "<big>propagator</big>\n\n<b>propagator, oris</b> <i>m</i> [propago I]\nрасширяющий пределы (страны) (<i>sc.</i> Juppiter <i>Ap</i>); удлиняющий срок: p. provinciae <i>C</i> продливший срок управления провинцией."],
  ["propages", "<big>propages</big>\n\n<b>propages, is</b> <i>f</i> [propago]\nотпрыск (sanguinis <i>Pac</i>; stirpis <i>Aus</i>)."],
  ["propaginatio", "<big>propaginatio</big>\n\n<b>propaginatio, onis</b> <i>f</i>\nразведение (отводками) <i>Is</i>."],
  ["propagino", "<big>propagino</big>\n\n<b>propagino, —, atum, are</b>\nразводить (отводками) <i>Is</i>; <i>перен.</i> распространять <i>Tert</i>."],
  ["propagmen", "<big>propagmen</big>\n\n<b>propagmen, inis</b> <i>n</i> [propago I]\nудлинение, продление (vitae <i>Enn</i>)."],
  ["propago", "<big>propago</big>\n\n<b>I propago, avi, atum, are</b> [pango]\n<b>1)</b> рассаживать, разводить (vitem <i>Col</i>);\n<b>2)</b> расширять, раздвигать, распространять (fines imperii <i>C, Nep</i>); увеличивать, повышать (sumptum alicujus rei <i>AG</i>);\n<b>3)</b> продолжать (prolem <i>Lcr</i>; familiam <i>Ap</i>); удлинять, продлевать (vitam <i>C etc.</i>); длить; затягивать (bellum <i>C</i>); сохранять, увековечивать (memoriam <i>PM</i>): p. imperium alicui in annum <i>L</i> оставить за кем-л. командование ещё на один год.\n\n<b>II propago, inis</b> <i>f</i> [propago I]\n<b>1)</b> отводок, отросток (vitium <i>H</i>);\n<b>2)</b> отпрыск, потомок, дитя (alicujus <i>Lcr, O</i>);\n<b>3)</b> потомство, поколение, род (p. Romana <i>V</i>)."],
  ["propalam", "<big>propalam</big>\n\n<b>pro–palam</b>\nявно, открыто, всенародно, публично (collocare signa <i>C</i>; aliquid dicere <i>L</i>): p. fieri <i>Pl</i> обнаружиться."],
  ["propalo", "<big>propalo</big>\n\n<b>propalo, avi, atum, are</b> [propalam]\nоткрывать, обнаруживать (aliquid <i>C, L etc.</i>)."],
  ["propando", "<big>propando</big>\n\n<b>pro–pando, (pandi), pansum (passum), ere</b>\nрасширять, расправлять: propassis <i>и</i> propansis pennis <i>Ap</i> на распростёртых крыльях."],
  ["propassio", "<big>propassio</big>\n\n<b>pro–passio, onis</b> <i>f</i>\nпрежние страдания <i>Hier</i>."],
  ["propator", "<big>propator</big>\n\n<b>propatōr, oris</b> <i>m</i> (<i>греч.</i>)\nпраотец, предок <i>Tert</i>."],
  ["propatruus", "<big>propatruus</big>\n\n<b>prō–patruus, ī</b> <i>m</i>\nбрат деда <i>Dig</i>."],
  ["propatula", "<big>propatula</big>\n\n<b>prōpatula, ōrum</b> <i>n</i> [propatulus]\nоткрытые вещи, явное (praebentur p. pudendis et secreta laudandis <i>Aug</i>)."],
  ["propatulus", "<big>propatulus</big>\n\n<b>prō–patulus, a, um</b>\nоткрытый (locus <i>Vr, C</i>): (in) propatulo на площади, под открытым небом (in propatulo aedium <i>L</i>); <i>перен.</i> открыто, публично: aliquid habere in propatulo <i>Sl</i> выставлять на продажу что-л., торговать чем-л."],
  ["prope", "<big>prope</big>\n\n<b>I prope</b> <i>adv.</i> (<i>compar.</i> propius, <i>superl.</i> proxime)\n<b>1)</b> близко, вблизи, поблизости, недалеко (p. ad portas esse <i>L</i>; p. a Sicilia <i>C</i>): p. adest (ut) <i>Pl</i> близко время (когда);\n<b>2)</b> недавно, только что (legio proxime conscripta <i>Cs</i>): longius aut propius <i>Prp</i> позднее или ранее;\n<b>3)</b> почти, чуть ли не (p. biennium <i>C</i>; his p. verbis <i>C</i>): tempestatis p. ritu <i>H</i> наподобие, можно сказать, бури; p. dicam <i>C</i> я сказал бы (можно сказать); p. erat (ut) <i>L</i> чуть было не;\n<b>4)</b> быть может, пожалуй: p. res est una solaque, quae possit facere beatum <i>H</i> это, пожалуй, единственная вещь, способная дать блаженство;\n<b>5)</b> внимательно, <i>т. е.</i> благосклонно (propius aliquid aspicere <i>V</i>).\n\n<b>II prope</b> <i>praep. cum acc.</i> <i>иногда</i> (<i>при</i> <i>compar. и superl.</i>) <i>cum dat.</i>\n<b>1)</b> близ, вблизи, поблизости (p. oppidum <i>Cs</i>; p. urbem <i>C</i>; propius castris <i>Cs</i>);\n<b>2)</b> почти до: p. seditionem ventum est <i>T</i> дело чуть было не дошло до мятежа;\n<b>3)</b> около: p. lucem <i>Su</i> около рассвета;\n<b>4)</b> подобно (proxime morem Romanum <i>L</i>)."],
  ["propediem", "<big>propediem</big>\n\n<b>prope–diem</b> (<i>тж. раздельно</i>) [prope + dies]\nв скором времени, вскоре <i>Ter, C, Sl, L, Pt</i>."],
  ["propello", "<big>propello</big>\n\n<b>pro–pello, puli, pulsum, ere</b>\n<b>1)</b> гнать (перед собой), погонять, выгонять (pecus pastum <i>L</i>; oves in pabulum <i>Vr</i>); толкать вперёд, подталкивать, подгонять (navem remis <i>C</i>); сбрасывать, сваливать (saxa in aliquem <i>QC</i>); отгонять, отбивать, отражать (hostem a castris <i>L</i>); изгонять (alitem nido <i>Col</i>);\n<b>2)</b> предупреждать, предотвращать (frigus duramque famem <i>H</i>): periculum vitae ab aliquo p. <i>L</i> избавить кого-л. от смертельной опасности;\n<b>3)</b> побуждать, вынуждать (aliquem ad aliquid <i>T</i>): nullo propellente <i>Su</i> без всякой внешней причины;\n<b>4)</b> опрокидывать, рушить (crates pro munitione objectas <i>Cs</i>; urbem <i>VF</i>)."],
  ["propemodo", "<big>propemodo</big>\n\n<b>prope–modo</b> <i>Pl</i>\n= propemodum."],
  ["propemodum", "<big>propemodum</big>\n\n<b>prope–modum</b>\nпочти, чуть ли не <i>Pac, Ter, C etc.</i>"],
  ["propempticon", "<big>propempticon</big>\n\n<b>propempticon, i</b> <i>n</i> (<i>греч.</i>)\nпрощальные стихи <i>St, Sid</i>."],
  ["propendeo", "<big>propendeo</big>\n\n<b>pro–pendeo, pendi, pensum, ere</b>\n<b>1)</b> висеть, наклоняться вниз, свешиваться (ex ramis <i>PM</i>);\n<b>2)</b> перевешивать, иметь, перевес (bona propendent <i>C</i>): nec dependere nec p. <i>Pl</i> — <i>см.</i> dependeo;\n<b>3)</b> склоняться, быть расположенным (alicui <i>или</i> in aliquem <i>C</i>). — <i>См. тж.</i> propensus."],
  ["propendulus", "<big>propendulus</big>\n\n<b>pro–pendulus, a, um</b>\nсвешивающийся спереди, ниспадающий (crines <i>Ap</i>)."],
  ["propense", "<big>propense</big>\n\n<b>propense</b> [propensus]\nс удовольствием, охотно <i>C, L</i>."],
  ["propensio", "<big>propensio</big>\n\n<b>propensio, onis</b> <i>f</i> [propendeo]\nсклонность, расположенность, охота <i>C</i>."],
  ["propensus", "<big>propensus</big>\n\n<b>propensus, a, um</b> [propendeo]\n<b>1.</b> <i>part. pf. к</i> propendeo;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> отвислый (labrum <i>Sol</i>);\n<b>2)</b> склоняющийся, склонный, расположенный (ad aliquid <i>C</i>; in alteram partem <i>C</i>): propenso animo aliquid facere <i>C</i> делать что-л. охотно (с удовольствием);\n<b>3)</b> расположенный, благосклонно настроенный (alicui <i>VM</i> <i>и</i> pro aliquo <i>Just</i>): venia propensiore <i>Ap</i> с большей снисходительностью;\n<b>4)</b> приближающийся, близкий (ad veritatem propensior <i>C</i>);\n<b>5)</b> имеющий перевес, важный: id fit propensius <i>C</i> это становится особенно важным."],
  ["properabilis", "<big>properabilis</big>\n\n<b>properabilis, e</b> [propero]\nускоряющий (calor <i>Tert</i>)."],
  ["properans", "<big>properans</big>\n\n<b>properans, antis</b>\n<b>1.</b> <i>part. praes. к</i> propero;\n<b>2.</b> <i>adj.</i> торопливый <i>C, Cld</i>."],
  ["properanter", "<big>properanter</big>\n\n<b>properanter</b> [propero]\nпоспешно <i>Lcr, Sl etc.</i>"],
  ["properantia", "<big>properantia</big>\n\n<b>properantia, ae</b> <i>f</i> [propero]\nпоспешность, торопливость <i>Sl, T</i>."],
  ["properatim", "<big>properatim</big>\n\n<b>properatim</b> <i>Caec, AG</i>\n= properato."],
  ["properatio", "<big>properatio</big>\n\n<b>properatio, onis</b> <i>f</i> <i>C, Ap</i>\n= properantia."],
  ["properato", "<big>properato</big>\n\n<b>properato</b> [propero]\nпоспешно, скоро <i>T</i>."],
  ["propere", "<big>propere</big>\n\n<b>propere</b> [properus]\nпоспешно, быстро (sequi <i>Sl</i>): p. esse <i>Pl</i> спешить."],
  ["properipes", "<big>properipes</big>\n\n<b>properipes, pedis</b> <i>adj.</i> [properus + pes]\nпоспешающий, быстро идущий <i>Ctl</i>."],
  ["properiter", "<big>properiter</big>\n\n<b>properiter</b> <i>Pac, Acc, Pl, Ap, Aus</i>\n= properato."],
  ["propero", "<big>propero</big>\n\n<b>propero, avi, atum, are</b> [properus]\n<b>1)</b> спешить (p. in patriam redire <i>C</i>; p. Romam <i>C</i>): properato opus est <i>Pl, C etc.</i> нужно спешить; p. ad epistulas <i>C</i> торопиться прочесть письма;\n<b>2)</b> ускорять (iter <i>Sl</i>; mortem <i>V</i>); hoc opus properemus <i>H</i> поторопимся с этим делом; vascula pure p. <i>Pl</i> поскорее почистить посуду || быстро стяжать (gloriam <i>O</i> <i>и</i> ad gloriam <i>Cs</i>)-, быстро приносить (arma alicui <i>L</i>): p. pecuniam heredi <i>H</i> усердно копить деньги для наследника; adjutum (<i>supin.</i>) p. <i>Sl</i> спешить на помощь; naves properatae <i>T</i> спешно построенные корабли."],
  ["Propertius", "<big>Propertius</big>\n\n<b>Propertius, i</b> <i>m</i>\n(Sextus Aurelius) Проперций, <i>римск. элегический поэт, родом из Умбрии, принадлежавший к кругу Мецената</i> (<i>приблиз. 49-15 гг. до н. э.</i>) <i>O, Q, PM</i>."],
  ["properus", "<big>properus</big>\n\n<b>properus, a, um</b>\nпоспешный, спешащий, торопливый, проворный: venit p. <i>O</i> он быстро пришёл; p. irae (<i>gen.</i>) <i>T</i> вспыльчивый; p. vindictae <i>T</i> горящий жаждой мести; mors propera fato <i>T</i> безвременная смерть; p. occasionis <i>T</i> быстро пользующийся случаем."],
  ["propetro", "<big>propetro</big>\n\n<b>pro–petro, —, —, are</b> [patro]\nприводить в исполнение, осуществлять (pacem <i>LM</i> — <i>v. l.</i>)."],
  ["propexus", "<big>propexus</big>\n\n<b>pro–pexus, a, um</b> [pro + pecto]\nнаперёд зачёсанный, свесившийся (propexa in pectore — ad pectora <i>O</i> — barba <i>V</i>)."],
  ["prophanus", "<big>prophanus</big>\n\n<b>prophanus</b> <i>Sid</i>\n= profanus."],
  ["propheta", "<big>propheta</big>\n\n<b>propheta (prophetes), ae</b> <i>m</i> (<i>греч.</i>)\n<b>1)</b> прорицатель, жрец, маг <i>Ap, Macr</i>;\n<b>2)</b> пророк <i>Eccl</i>."],
  ["prophetalis", "<big>prophetalis</big>\n\n<b>prophetalis, e</b>\nпророческий (vaticinium, os <i>Hier</i>)."],
  ["prophetia", "<big>prophetia</big>\n\n<b>prophetia, ae</b> <i>f</i> [propheta]\nпророчество <i>Eccl</i>."],
  ["prophetialis", "<big>prophetialis</big>\n\n<b>prophetialis, e</b> <i>Tert</i>\n= prophetalis."],
  ["prophetice", "<big>prophetice</big>\n\n<b>prophetice</b>\nпророчески (dictum est <i>Tert</i>)."],
  ["propheticus", "<big>propheticus</big>\n\n<b>propheticus, a, um</b> <i>Eccl</i>\n= prophetalis."],
  ["prophetis", "<big>prophetis</big>\n\n<b>prophetis, idis</b> <i>и</i> <b>prophetissa, ae</b> <i>f</i>\nпророчица <i>Vlg, Eccl</i>."],
  ["prophetizo", "<big>prophetizo</big>\n\n<b>prophetizo, —, —, are</b>\nпророчествовать <i>Vlg</i>."],
  ["propheto", "<big>propheto</big>\n\n<b>propheto, avi, atum, are</b> <i>Eccl</i>\n= prophetizo."],
  ["propilo", "<big>propilo</big>\n\n<b>pro–pilo, —, —, are</b> [pilum]\nметать (missilia <i>Amm</i>)."],
  ["propina", "<big>propina</big>\n\n<b>propina, ae</b> <i>f</i> <i>Is</i>\n= popina 1."],
  ["propinatio", "<big>propinatio</big>\n\n<b>propinatio, onis</b> <i>f</i> [propino]\nпредложение выпить за чьё-л. здоровье, здравица <i>Pt, Sen</i>."],
  ["propincus", "<big>propincus</big>\n\n<b>propincus, a, um</b> <i>L</i>\n= propinquus I."],
  ["propino", "<big>propino</big>\n\n<b>propino, avi, atum, are</b> (<i>греч.</i>)\n<b>1)</b> (<i>тж.</i> p. salutem <i>Pt</i>) пить за здоровье, поднимать заздравную чашу (alicui <i>C, Sen etc.</i>; poculum magnum <i>Pl</i>);\n<b>2)</b> давать пить (aquam e fonte <i>PM</i>);\n<b>3)</b> давать, передавать, доставлять (aliquid alicui <i>Ter, Ap</i>)."],
  ["propinqua", "<big>propinqua</big>\n\n<b>propinqua, ae</b> <i>f</i> [propinquus II]\nродственница <i>C, T</i>."],
  ["propinque", "<big>propinque</big>\n\n<b>propinque</b> [propinquus I]\nблизко, вблизи, поблизости (adesse <i>Pl</i>)."],
  ["propinquitas", "<big>propinquitas</big>\n\n<b>propinquitas, atis</b> <i>f</i> [propinquus I]\n<b>1)</b> близость (loci <i>C</i>; hostium <i>Cs</i>): ex propinquitate <i>Cs</i> на близком расстоянии, вблизи;\n<b>2)</b> родство (p. et affinitas <i>C</i>);\n<b>3)</b> родня <i>Amm</i>."],
  ["propinquo", "<big>propinquo</big>\n\n<b>propinquo, avi, atum, are</b> [propinquus I]\n<b>1)</b> приближаться (alicui rei <i>V, T</i> <i>или</i> aliquid <i>St, T</i>);\n<b>2)</b> приближать, ускорять (augurium <i>V</i>; mortem <i>Sil</i>);\n<b>3)</b> быть близким <i>или</i> в родстве (alicui <i>Lact</i>; propinquantes vel affines <i>Amm</i>)."],
  ["propinquus", "<big>propinquus</big>\n\n<b>I propinquus, a, um</b> [prope]\n<b>1)</b> близкий, близлежащий, соседний (p. cubiculo hortus <i>L</i>; colles inter se propinqui <i>Sl</i>): in propinquo <i>L</i> поблизости, вблизи; ex propinquo <i>St, L</i> из близлежащего места, на близком расстоянии, вблизи (aspicere <i>L</i>);\n<b>2)</b> близкий (<i>по времени</i>), приближающийся (mors <i>C</i>);\n<b>3)</b> сходный, подобный, похожий (alicui rei <i>C</i>);\n<b>4)</b> родственный, родной: cognatio propinqua <i>C, Nep</i> близкое родство.\n\n<b>II propinquus, i</b> <i>m</i>\nродственник <i>C etc.</i>"],
  ["propior", "<big>propior</big>\n\n<b>propior, ius</b> (<i>gen.</i> propioris) <i>adj. compar.</i> [prope]\n<b>1)</b> более близкий, ближе расположенный (locus <i>bAfr</i>): p. alicui rei <i>V, O, QC etc.</i> (<i>реже</i> aliquid <i>Hirt, Sl</i> <i>и</i> alicujus rei <i>Lcr</i>) более близкий к чему-л.; tunica p. pallio est <i>Pl</i> — <i>см.</i> tunica;\n<b>2)</b> более близкий (<i>по времени</i>) (p. funeri <i>H</i>): septimus octavo p. annus <i>H</i> седьмой год на исходе;\n<b>3)</b> более поздний, недавний, последний (tempora <i>Dig etc.</i>; epistula <i>C</i>);\n<b>4)</b> более похожий, имеющий большее сходство: p. vero <i>L, O</i> более правдоподобный;\n<b>5)</b> находящийся в ближайшем родстве, более родственный (homo <i>C</i>);\n<b>6)</b> более интимный, задушевный, близкий (amicus <i>H</i>); более важный, близкий к сердцу (cura <i>O</i>);\n<b>7)</b> более склонный (p. irae quam timori <i>T</i>);\n<b>8)</b> больше соответствующий, лучший (alicui rei <i>C, L</i>);\n<b>9)</b> более расположенный, благосклонный (alicui <i>O</i>)."],
  ["propitiabilis", "<big>propitiabilis</big>\n\n<b>propitiabilis, e</b>\nмогущий быть умилостивленным <i>Enn, Eccl</i>."],
  ["propitiatio", "<big>propitiatio</big>\n\n<b>propitiatio, onis</b> <i>f</i>\nумилостивление <i>Ambr</i>; умилостивительная жертва <i>Macr</i>."],
  ["propitiator", "<big>propitiator</big>\n\n<b>propitiator, oris</b> <i>m</i>\nпримиритель <i>Eccl</i>."],
  ["propitiatus", "<big>propitiatus</big>\n\n<b>propitiatus, us</b> <i>m</i> <i>Vlg</i>\n= propitiatio."],
  ["propitio", "<big>propitio</big>\n\n<b>propitio, avi, atum, are</b> [propitius]\nвнушать благоволение, умилостивлять (p. Jovem <i>QC</i>; propitiata Juno <i>T</i>)."],
  ["propitius", "<big>propitius</big>\n\n<b>propitius, a, um</b> [pro + peto]\nблагосклонный, милостивый (alicui <i>Pl, C etc.</i>): satin' illi dii propitii sunt? <i>погов. Ter</i> = в уме ли он?"],
  ["propius", "<big>propius</big>\n\n<b>propius</b>\n<b>1.</b> <i>adv. compar.</i> [prope] ближе (p. accedere <i>Ter</i>);\n<b>2.</b> <i>praep. cum acc.</i> поближе к (p. mare Africum <i>Sl</i>) <i>и</i> <i>cum dat.</i> (p. mari <i>PM</i>);\n<b>3.</b> <i>n</i> <i>к</i> propior."],
  ["proplasma", "<big>proplasma</big>\n\n<b>proplasma, alis</b> <i>n</i> (<i>греч.</i>)\nобразец, модель <i>PM</i>."],
  ["propnigeon", "<big>propnigeon</big>\n\n<b>propnigeon (-um), i</b> <i>n</i> (<i>греч.</i>; <i>лат.</i> praefurnium)\nместо впереди печи, парильня, топочное отделение (<i>в бане</i>) <i>Vtr, PJ</i>."],
  ["Propoetides", "<big>Propoetides</big>\n\n<b>Propoetides, um</b> <i>f</i>\nПропетиды, <i>девушки из города Аматунт (Кипр), превращенные в камни за отрицание божественности Венеры</i> <i>O</i>."],
  ["propola", "<big>propola</big>\n\n<b>I propola, ae</b> <i>m</i> (<i>греч.</i>)\nмелкий торговец, лавочник <i>Pl, Vr, C etc.</i>\n\n<b>II propola, ae</b> <i>f</i>\nмелочная лавка <i>CTh, Eccl</i>."],
  ["propolis", "<big>propolis</big>\n\n<b>propolis, is</b> <i>и</i> <b>eos</b> (<i>acc.</i> im <i>или</i> in, <i>abl.</i> i) <i>f</i> (<i>греч.</i>)\nпчелиный клей <i>Vr, PM, CC, AG</i>."],
  ["propolluo", "<big>propolluo</big>\n\n<b>pro–polluo, —, —, ere</b>\nещё больше (сильнее) осквернять, <i>т. е.</i> усугублять (obscura initia impudentibus ausis <i>T</i>)."],
  ["propoma", "<big>propoma</big>\n\n<b>propoma, atis</b> <i>n</i> (<i>греч.</i>)\nлёгкая выпивка перед трапезой, аперитив <i>Pall</i>."],
  ["propono", "<big>propono</big>\n\n<b>pro–pono, posui, positum, ere</b>\n<b>1)</b> ставить впереди, выставлять (vexillum <i>Cs</i>; signum pugnae <i>L</i>); вывешивать (edictum <i>Su</i>): p. aliquid oculis (ante oculos <i>или</i> in conspectum) <i>C</i> показывать что-л.; p. aliquid <i>Su</i> (venale <i>C</i>) выставить что-л. на продажу (объявить о продаже чего-л.); animo aliquid p. <i>L</i> вообразить (представить себе) что-л.;\n<b>2)</b> объявлять: p. aliquid populo (in publicum <i>или</i> in publico) <i>C</i> объявить о чём-л. публично, обнародовать что-л.;\n<b>3)</b> предлагать (remedia morbo <i>Nep</i>; p. alicui quaestionem <i>Nep</i>); обещать (p. militibus praemia <i>C</i>);\n<b>4)</b> <i>pass.</i> proponi предвидеться (summa difficultas proponitur <i>C</i>);\n<b>5)</b> подвергать (telis fortunae vita proposita <i>C</i>);\n<b>6)</b> грозить, угрожать (improbis poenam p. <i>C</i>): morte proposita <i>C</i> в виду неминуемой смерти;\n<b>7)</b> представлять, воображать (animo vim fortunae <i>L</i>): p. sibi aliquem ad imitandum <i>C</i> поставить кого-л. себе в пример; sibi spem p. alicujus rei <i>bAl</i> льстить себя надеждой на что-л.;\n<b>8)</b> задумывать, затевать, предпринимать (iter <i>Cs</i>): p. aliquid animo <i>Cs</i> задумать что-л. (решиться на что-л.); mihi propositum est hoc facere <i>C</i> я решил это сделать; nihil erat propositum ad scribendum <i>C</i> писать было не о чём; omni huic sermoni propositum est, ut... <i>C</i> смысл всей этой речи состоит в том, чтобы...;\n<b>9)</b> излагать, рассказывать (p. rem gestam <i>Cs</i>; de aliqua re <i>Cs</i>): aliquid pro certo p. <i>Cs</i> утверждать что-л.;\n<b>10)</b> устанавливать, постановлять, принимать (propositum consilium <i>C</i>);\n<b>11)</b> называть, упоминать (viros notissimos <i>C</i>);\n<b>12)</b> <i>филос.</i> выставлять первую посылку силлогизма <i>C</i>. — <i>См. тж.</i> propositum <i>и</i> propositus."],
  ["Propontiacus", "<big>Propontiacus</big>\n\n<b>Propontiacus, a, um</b> [Propontis]\nпропонтидский (aqua <i>Prp</i>; ora <i>O</i>)."],
  ["Propontis", "<big>Propontis</big>\n\n<b>Propontis, idis</b> <i>f</i> (<i>греч.</i> «Предморье»)\nПропонтида, <i>море между Геллеспонтом и Боспором Фракийским</i> (<i>ныне</i> Мраморное море) <i>L, Ctl, O etc.</i>"],
  ["proporro", "<big>proporro</big>\n\n<b>pro–porro</b>\nдальше, далее, сверх того <i>Lcr</i>."],
  ["proportio", "<big>proportio</big>\n\n<b>pro–portio, onis</b> <i>f</i>\nпропорция, соотношение, соразмерность <i>C, Vr, Q</i>."],
  ["proportionalis", "<big>proportionalis</big>\n\n<b>proportionalis, e</b>\nпропорциональный, соразмерный <i>Aug, Boet</i>."],
  ["propositio", "<big>propositio</big>\n\n<b>propositio, onis</b> <i>f</i> [propono]\n<b>1)</b> представление (animi <i>C</i>);\n<b>2)</b> основное положение, предмет, тема (p., quid sis dicturus <i>C</i>);\n<b>3)</b> краткое изложение (p. est, per quam ostendimus summatim, quid sit, quod probare volumus <i>rhH</i>);\n<b>4)</b> предложение, фраза <i>Q, AG</i>;\n<b>5)</b> намерение, цель <i>Dig</i>;\n<b>6)</b> предпосылка <i>или</i> большая посылка силлогизма <i>C etc.</i>;\n<b>7)</b> юридический случай, казус <i>Dig</i>."],
  ["propositum", "<big>propositum</big>\n\n<b>propositum, i</b> <i>n</i> [propono]\n<b>1)</b> намерение, замысел, план (p. peragere <i>Nep</i>; a proposito deterreri <i>Cs</i>): quo proposito <i>VP</i> с этой целью;\n<b>2)</b> основное положение, главный предмет, тема (ad p. suum redire <i>CC</i>);\n<b>3)</b> общий обзор, рассмотрение вопроса в общем виде <i>C</i>;\n<b>4)</b> предпосылка <i>C, Sen</i>."],
  ["propositus", "<big>propositus</big>\n\n<b>propositus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> propono;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> подверженный, доступный, открытый (alicui ad praedam <i>Cs</i>; telis <i>C</i>);\n<b>2)</b> предстоящий, угрожающий (propositum vitae periculum <i>C</i>);\n<b>3)</b> служащий предметом <i>или</i> целью (чего-л.) (aliud ars ipsa, aliud quod propositum est arti <i>C</i>)."],
  ["proposui", "<big>proposui</big>\n\n<b>proposui</b> <i>pf. к</i>\npropono."],
  ["propraetor", "<big>propraetor</big>\n\n<b>propraetor, oris</b> <i>m</i> [<i>из</i> pro praetore]\nпропретор, <i>бывший претор, назначенный по окончании своей претуры наместником в провинцию</i> <i>C, Sl etc.</i>"],
  ["propriatim", "<big>propriatim</big>\n\n<b>propriatim</b> <i>Eccl</i>\n= proprie 3."],
  ["proprie", "<big>proprie</big>\n\n<b>proprie</b> [proprius]\n<b>1)</b> индивидуально, отдельно, каждый для себя лично (id est p. tuum <i>C</i>): difficile est p. communia dicere <i>H</i> трудно выразить общие истины по-своему (своеобразно);\n<b>2)</b> особенно (p. rei militaris peritus <i>L</i>);\n<b>3)</b> в собственном смысле, в подлинном значении (p. vereque dici <i>C</i>): p. magis <i>C</i> точнее говоря."],
  ["proprietarius", "<big>proprietarius</big>\n\n<b>proprietarius, i</b> <i>m</i>\nвладелец, собственник <i>Dig</i>."],
  ["proprietas", "<big>proprietas</big>\n\n<b>proprietas, atis</b> <i>f</i> [proprius]\n<b>1)</b> особенность, своеобразие (rerum <i>C</i>); особое значение (verborum <i>Q, Dig</i>); свойство, черта, признак (alicujus rei <i>C, L etc.</i>);\n<b>2)</b> право собственности, владение, обладание (litem proprietatis movere <i>Dig</i>): nuda p. <i>Dig</i> голая собственность, <i>т. е.</i> без права пользования ею (<i>поскольку это право принадлежит другому</i>)."],
  ["proprio", "<big>proprio</big>\n\n<b>proprio, avi, —, are</b> [proprius]\n(физиологически) усваивать (digestione propriari <i>CA</i>)."],
  ["propritim", "<big>propritim</big>\n\n<b>propritim</b> [proprius]\nсобственно говоря <i>Lcr</i>."],
  ["proprium", "<big>proprium</big>\n\n<b>proprium, i</b> <i>n</i>\n<b>1)</b> (личная) собственность (vivere de proprio <i>M</i>): amittit p. qui alienum appetit <i>Ph</i> теряет своё тот, кто желает чужого;\n<b>2)</b> <i>pl.</i> свой дом <i>или</i> своя страна (ad propria recedere <i>Treb</i> <i>или</i> remeare <i>Amm</i>)."],
  ["proprius", "<big>proprius</big>\n\n<b>proprius, a, um</b>\n<b>1)</b> своеобразный, особенный, неотъемлемый, отличительный, характерный (facultas <i>C</i>; p. et peculiaris <i>PM</i>);\n<b>2)</b> собственный, лично принадлежащий, частный (praedia <i>C</i>; aedes <i>Dig</i>): propria impensa <i>Just</i> на собственный счёт; proprium fecisse aliquid <i>H</i> присвоить себе что-л.;\n<b>3)</b> подлинный (res propriis vocabulis nominare <i>C</i>): proprio nomine <i>C, Dig</i> в собственном смысле слова;\n<b>4)</b> длительный, постоянный, надёжный, прочный (victoria <i>L</i>);\n<b>5)</b> исключительный, особый (periculum <i>C</i>);\n<b>6)</b> подходящий, пригодный (tempus agendi <i>C</i>)."],
  ["propter", "<big>propter</big>\n\n<b>I propter</b> <i>adv.</i> [<i>из</i> *propiter <i>от</i> prope]\nвблизи (aliquid intueri <i>C</i>); поблизости, рядом (p. stetisse <i>C</i>).\n\n<b>II propter</b> <i>praep. cum acc.</i>\n1) близ, рядом, у (p. viam <i>T</i>);\n<b>2)</b> вследствие, ввиду, из-за (p. frigora <i>Cs</i>; p. metum <i>C</i>): p. eam ipsam ceusam <i>C</i> <i>и</i> p. hoc ipsum <i>Pt</i> по этой самой причине; te p. <i>V</i> по твоей вине, из-за тебя: p. vitam vivendi perdere causam <i>J</i> <i>впоследствии погов.</i> ради сохранения жизни утратить её смысл || благодаря (p. vos <i>C</i>);\n<b>3)</b> посредством, путём (p. dolum alienum lucrum facere <i>Dig</i>)."],
  ["propterea", "<big>propterea</big>\n\n<b>propter–ea</b>\nпо этой причине, потому <i>Enn, Ter etc.</i>: p. quod <i>Lcr, Cs, C etc.</i> <i>и</i> p. quia <i>Lcr, C</i> потому что."],
  ["proptervus", "<big>proptervus</big>\n\n<b>proptervus</b> <i>Pl</i>\n= protervus."],
  ["propudiosus", "<big>propudiosus</big>\n\n<b>propudiosus, a, um</b> [propudium I]\nкрайне бесстыдный, бесчестный (<i>sc.</i> homo <i>Pl</i>; mulier <i>Ap</i>; facinus <i>Eccl</i>)."],
  ["propudium", "<big>propudium</big>\n\n<b>I propudium, i</b> <i>n</i>\n<b>1)</b> позорный поступок, бесстыдство <i>Pl, PM</i>;\n<b>2)</b> <i>бран.</i> бесстыдный, низкий человек (quid ais, p.! <i>Pl</i>).\n\n<b>II propudium</b> <i>adv.</i> <i>прост. Pt</i>\n(<i>v. l.</i>)= propediem."],
  ["propugnaculum", "<big>propugnaculum</big>\n\n<b>propugnaculum, i</b> <i>n</i> [propugno]\nукрепление (p. moenium <i>T</i>); оборона, защита (adversus Gallos <i>T</i>); оплот (p. imperii <i>C</i>)."],
  ["propugnatio", "<big>propugnatio</big>\n\n<b>propugnatio, onis</b> <i>f</i> [propugno]\nзащита <i>C, VP etc.</i>"],
  ["propugnator", "<big>propugnator</big>\n\n<b>propugnator, oris</b> <i>m</i> [propugno]\nратник, боец <i>Cs, Nep, T etc.</i>; защитник (libertatis <i>C</i>; sceleris <i>Just</i>)."],
  ["propugno", "<big>propugno</big>\n\n<b>pro–pugno, avi, atum, are</b>\n<b>1)</b> оказывать сопротивление, обороняться, защищаться (ex turribus <i>Cs</i>);\n<b>2)</b> сражаться, биться (pro aliqua re <i>C</i>, <i>реже</i> alicui rei <i>H, Ap, Amm</i> <i>и</i> aliquid <i>PM, T, Cld</i>): p. absentiam alicujus <i>Su</i> защищать кого-л. в его отсутствие."],
  ["propuli", "<big>propuli</big>\n\n<b>propuli</b> <i>pf. к</i>\npropello."],
  ["propulsatio", "<big>propulsatio</big>\n\n<b>propulsatio, onis</b> <i>f</i> [propulso]\nотведение, отклонение (periculi <i>C</i>)."],
  ["propulsator", "<big>propulsator</big>\n\n<b>propulsator, oris</b> <i>m</i> [propulso]\nотражающий, отвращающий, отводящий (alicujus rei <i>VM, Eccl</i>)."],
  ["propulso", "<big>propulso</big>\n\n<b>propulso, avi, atum, are</b> [<i>intens. к</i> propello]\nотражать, отбивать (hostem <i>Cs</i>; injurias illatas <i>Cs</i>); отвращать (periculum <i>C</i>): p. injurias ab aliquo <i>C</i> защищать кого-л. от обид; p. famem <i>C</i> бороться с голодом; caloris incommoda lucorum umbra p. <i>AG</i> укрыться в тень рощ от палящего зноя || отводить, отклонять (suspicionem a se <i>C</i>); устранять, отстранять, отбрасывать (metum <i>C</i>); прогонять (aliquem <i>Ter</i>)."],
  ["propulsus", "<big>propulsus</big>\n\n<b>I propulsus, a, um</b> <i>part. pf. к</i>\npropello.\n\n<b>II propulsus, (us) </b> <i>m</i> [propello]\nтолчок, напор, давление <i>Sen</i>."],
  ["propungo", "<big>propungo</big>\n\n<b>pro–pungo, —, —, ere</b>\nколоть спереди <i>CA</i> (<i>v. l.</i>)."],
  ["propurgo", "<big>propurgo</big>\n\n<b>pro–purgo, —, —, are</b>\nпредварительно очищать (se <i>Vlg</i>)."],
  ["propylaea", "<big>propylaea</big>\n\n<b>propylaea, orum</b> <i>n</i> (<i>греч.</i>)\nпропилеи, <i>портик, ведущий в Парфенон</i> (<i>в афинском Акрополе</i>) <i>C</i>."],
  ["proquaestore", "<big>proquaestore</big>\n\n<b>pro–quaestore</b>\n(<i>т. е.</i> pro quaestore) <i>m</i> проквестор, квестор в провинции <i>C</i>."],
  ["proquam", "<big>proquam</big>\n\n<b>pro–quam</b> <i>conj.</i> (<i>тж. раздельно</i>)\nсмотря по, в зависимости от: p. posset <i>Lcr</i> насколько он смог, в меру его сил."],
  ["proquirito", "<big>proquirito</big>\n\n<b>pro–quirito, (avi), atum, are</b>\nпублично объявлять, возглашать (epistulam <i>Ap</i>; lex proquiritata <i>Sid</i>)."],
  ["prora", "<big>prora</big>\n\n<b>prora, ae</b> <i>f</i>\n<b>1)</b> передняя часть (нос) корабля <i>Pl, Lcr, Cs etc.</i>: prorae tutela <i>O</i> <i>или</i> custos <i>Rut</i> = proreta; p. et puppis mihi fuit (ut) <i>C греч. погов.</i> = весь мой план состоял в том (чтобы);\n<b>2)</b> <i>перен.</i> корабль, судно <i>V, O</i>."],
  ["prorepo", "<big>prorepo</big>\n\n<b>pro–repo, repsi, raptum, ere</b>\n<b>1)</b> выползать (e cavis terrae <i>PM</i>);\n<b>2)</b> показываться, прорастать (pampinus prorepsit e duro <i>Col</i>);\n<b>3)</b> пробираться, прокрадываться (ad aliquid <i>PJ, Sen</i>)."],
  ["proreptus", "<big>proreptus</big>\n\n<b>I proreptus, a, um</b> <i>part. pf. к</i>\nprorepo.\n\n<b>II proreptus, a, um</b> <i>part. pf. к</i>\nproripio."],
  ["proreta", "<big>proreta</big>\n\n<b>proreta, ae</b> <i>m</i> (<i>греч.</i>)\nнаблюдатель, вахтенный на носу корабля (<i>сигнализировавший кормчему направление</i>), <i>он же</i> помощник рулевого (<i>который находился на корме</i>) <i>Pl</i>."],
  ["proreus", "<big>proreus</big>\n\n<b>prōre͡us, eī</b> <i>и</i> <b>eos</b> <i>m</i> <i>O</i>\n= proreta."],
  ["proriga", "<big>proriga</big>\n\n<b>proriga (peroriga), ae</b> <i>m</i>\nконюх <i>Vr, PM</i>."],
  ["proripio", "<big>proripio</big>\n\n<b>proripio, ripui, reptum, ere</b> [pro + rapio]\n<b>1)</b> похищать, хватать (aliquem <i>C</i>); уносить, увлекать (catervas in caedem <i>H</i>);\n<b>2)</b> (<i>тж.</i> se p.) прорываться наружу (libido se proripit <i>C</i>);\n<b>3)</b> спешить, бежать (quo proripis? <i>V</i>): p. nudos pedes <i>O</i> убежать (умчаться) босиком; p. se вырваться, устремиться прочь, броситься (se porta foras <i>Cs</i>; de cella <i>Pt</i>; in silvam <i>Su</i>)."],
  ["proris", "<big>proris</big>\n\n<b>proris, is</b> (<i>acc.</i> im) <i>f</i> <i>арх. Acc</i>\n= prora."],
  ["prorito", "<big>prorito</big>\n\n<b>prorito, avi, —, are</b> [<i>одного корня с</i> irrito]\n<b>1)</b> вызывать, причинять (procidentiam <i>PM</i>);\n<b>2)</b> возбуждать, раздражать (stomachum varietate ciborum <i>Scr</i>);\n<b>3)</b> соблазнять, манить (spes proritat aliquem <i>Sen</i>)."],
  ["prorogatio", "<big>prorogatio</big>\n\n<b>prorogatio, onis</b> <i>f</i> [prorogo]\n<b>1)</b> продление (imperii <i>L</i>);\n<b>2)</b> отсрочка (diei <i>C</i>)."],
  ["prorogativus", "<big>prorogativus</big>\n\n<b>prorogativus, a, um</b>\nдопускающий отсрочку (своих предвещаний) (fulmina <i>Sen</i>)."],
  ["prorogo", "<big>prorogo</big>\n\n<b>pro–rogo, avi, atum, are</b>\n<b>1)</b> продлевать (alicui imperium <i>C</i>; vitae spatium <i>T</i>); p. annum <i>C</i> продлить (срок занятия должности) на один год; p. alicui provinciam <i>C</i> продлить кому-л. срок управления провинцией;\n<b>2)</b> откладывать, отсрочивать (in alium diem aliquid <i>Pl</i>; dies ad solvendum <i>C</i>);\n<b>3)</b> продолжать (subolem <i>CJ</i>);\n<b>4)</b> оглашать (prorogata sententia <i>CJ</i>);\n<b>5)</b> уплачивать вперёд <i>Dig, CJ</i>."],
  ["prorostra", "<big>prorostra</big>\n\n<b>prorostra, orum</b> <i>n</i> <i>Is</i>\n= rostra."],
  ["Prorsa", "<big>Prorsa</big>\n\n<b>Prorsa (Prosa), ae</b> <i>f</i> [prorsus]\n<i>богиня правильных родов</i> <i>Vr ap. AG</i>."],
  ["prorsum", "<big>prorsum</big>\n\n<b>prorsum</b> <i>adv.</i> [pro + versum]\n<b>1)</b> вперёд (cursari rursum p. <i>Ter</i>);\n<b>2)</b> прямо, прямым путём (p. Athenas abire <i>Pl</i>);\n<b>3)</b> вполне, совсем, совершенно (p. nihil intellego <i>Ter</i>);\n<b>4)</b> вообще говоря, одним словом: p. non eo infitias non id genus ferramentis uti nosse <i>Ap</i> короче говоря, я отнюдь не отрицаю, что этого рода инструментами пользоваться не умею."],
  ["prorsus", "<big>prorsus</big>\n\n<b>I prorsus</b> <i>adv.</i> [pro + versus]\n<b>1)</b> вперёд (cedere <i>Pl</i>);\n<b>2)</b> прямо (irruere <i>Ter</i>);\n<b>3)</b> совершенно, решительно, вполне, всецело, безусловно: ita p. existimo <i>C</i> именно так я думаю; p. nemo <i>C</i> решительно никто; p. nihil <i>C</i> ровно ничего; nunc p. hoc statui, ut... <i>C</i> теперь я твёрдо решил, что...;\n<b>4)</b> вообще, короче говоря, одним словом: p. in facie vultuque vecordia inerat <i>Sl</i> словом, вся наружность (Каталины) выражала безумие.\n\n<b>II prorsus, a, um</b> [pro + versus]\nпрямой, простой, несвязанный (oratio <i>Q</i>); prorsa et vorsa facundia <i>Ap</i> в прозе и в стихах."],
  ["prorumpo", "<big>prorumpo</big>\n\n<b>pro–rumpo, rupi, ruptum, ere</b>\n<b>1)</b> бросаться, устремляться, хлынуть, кинуться (medios in hostes <i>V</i>); <i>тж.</i> se p. <i>или</i> <i>med.-pass.</i> прорываться, пробиваться (in mare <i>V</i>; prorumpitur in mare venti vis <i>Lcr</i>);\n<b>2)</b> внезапно появляться, внезапно вспыхивать (pestis prorumpit <i>C</i>; incendium proruperat <i>T</i>); обнаруживаться (nihil prorupit <i>T</i>); разражаться: p. ad minas <i>T</i> разразиться угрозами;\n<b>3)</b> выбрасывать, выталкивать, извергать (nubem atram ad aethera <i>V</i>); <i>part. pass.</i> proruptus прорвавшийся, хлынувший (mare, sudor <i>V</i>): prorupta audacia <i>C</i> отчаянная дерзость."],
  ["proruo", "<big>proruo</big>\n\n<b>pro–ruo, rui, rutum, ere</b>\n<b>1)</b> (<i>тж.</i> se p. <i>Ap</i>) бросаться, устремляться (in hostem <i>QC</i>; se foras <i>Ter</i>);\n<b>2)</b> разрушаться, обрушиваться (oppidum proruit <i>T</i>);\n<b>3)</b> разрушать, срыть (vallum in fossas <i>L</i>); валить (prorutae arbores <i>T</i>); разбивать (hostes <i>T</i>)."],
  ["prorupi", "<big>prorupi</big>\n\n<b>prorupi</b> <i>pf. к</i>\nprorumpo."],
  ["proruptio", "<big>proruptio</big>\n\n<b>proruptio, onis</b> <i>f</i> [prorumpo]\nвылазка, набег, нападение (hostium <i>AV</i>)."],
  ["proruptor", "<big>proruptor</big>\n\n<b>proruptor, oris</b> <i>m</i> [prorumpo]\nучастник вылазки (из осаждённого места) <i>Amm</i>."],
  ["proruptus", "<big>proruptus</big>\n\n<b>proruptus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> prorumpo;\n<b>2.</b> <i>adj.</i> необузданный, неукротимый (audacia <i>Sl</i>)."],
  ["prorutus", "<big>prorutus</big>\n\n<b>prorutus, a, um</b> <i>part. pf. к</i>\nproruo."],
  ["prosa", "<big>prosa</big>\n\n<b>I prosa, ae</b> <i>f</i> [prosus]\n(<i>sc.</i> oratio) проза <i>Col, Sen etc.</i>\n\n<b>II Prosa, ae</b> <i>f</i> <i>v. l.</i>\n= Prorsa."],
  ["prosaeptum", "<big>prosaeptum</big>\n\n<b>prosaeptum, i</b> <i>m</i> [saepio]\nогороженное место, <i>перен.</i> оболочка (intra p. carnis inclusus <i>Ambr</i>)."],
  ["prosaicus", "<big>prosaicus</big>\n\n<b>prosaicus, a, um</b> <i>Eccl</i>\n= prosalis."],
  ["prosalis", "<big>prosalis</big>\n\n<b>prosalis, e</b> [prosa]\nпрозаический (dictio <i>Eccl</i>)."],
  ["prosapia", "<big>prosapia</big>\n\n<b>prosapia, ae</b> <i>f</i> <i>арх.</i>\nпоколение, род (homo veteris prosapiae <i>Sl</i>)."],
  ["prosapies", "<big>prosapies</big>\n\n<b>prosapies, ei</b> <i>f</i> <i>Eccl</i>\n= prosapia."],
  ["prosarius", "<big>prosarius</big>\n\n<b>prosarius, a, um</b> <i>Sid</i>\n= prosalis."],
  ["prosator", "<big>prosator</big>\n\n<b>prosator, oris</b> <i>m</i> [prosero II]\nтворец, создатель <i>Aug</i>."],
  ["prosatus", "<big>prosatus</big>\n\n<b>prosatus, a, um</b> <i>part. pf. к</i>\nprosero II."],
  ["proscaenium", "<big>proscaenium</big>\n\n<b>pro–scaenium, i</b> <i>n</i> (<i>греч.</i>)\nпросцений, передняя часть сцены <i>Pl, V, Ap</i>; <i>перен.</i> театр <i>Cld</i>."],
  ["proscenium", "<big>proscenium</big>\n\n<b>proscenium, i</b> <i>n</i> <i>v. l.</i>\n= proscaenium."],
  ["proscholus", "<big>proscholus</big>\n\n<b>pro–scholus (-os), i</b> <i>m</i> (<i>греч.</i>)\nпомощник учителя <i>Aug, Aus</i>."],
  ["proscindo", "<big>proscindo</big>\n\n<b>pro–scindo, scidi, scissum, ere</b>\n<b>1)</b> раскалывать (quercum <i>Lcn</i>); разрезать (piscem <i>Ap</i>); бороздить, вспахивать (terram pressis aratris <i>Lcr</i>): peritus proscindendi soli <i>Ap</i> опытный землепашец; p. aequor <i>Ctl</i> бороздить море, плыть, <i>но тж.</i> <i>V</i> поднимать целину;\n<b>2)</b> бранить, порицать, поносить (aliquem <i>O, Su</i>)."],
  ["proscissio", "<big>proscissio</big>\n\n<b>proscissio, onis</b> <i>f</i> [proscindo]\nпервая вспашка, распашка пара <i>Col</i>."],
  ["proscissum", "<big>proscissum</big>\n\n<b>proscissum, i</b> <i>n</i>\nпервая борозда (на целине) <i>Col</i>."],
  ["proscribo", "<big>proscribo</big>\n\n<b>pro–scribo, scripsi, scriptum, ere</b>\n<b>1)</b> публично объявлять (auctionem <i>C, Pt</i>; diem <i>C</i>); опубликовать, обнародовать (legem <i>C</i>);\n<b>2)</b> назначать к продаже, объявлять о продаже (fundum <i>C</i>): p. aliquem <i>C</i> объявлять о продаже с торгов чьего-л. имущества; cum hoc titulo p. <i>Pt</i> вывесить объявление (о продаже) следующего содержания;\n<b>3)</b> конфисковать (alicujus possessiones <i>C</i>);\n<b>4)</b> объявлять вне закона (aliquem <i>C, Sl etc.</i>): p. dignitatem alicujus <i>Pt</i> создать кому-л. дурную славу."],
  ["proscriptio", "<big>proscriptio</big>\n\n<b>proscriptio, onis</b> <i>f</i> [proscribo]\n<b>1)</b> объявление, <i>преим.</i> о продаже (bonorum <i>C</i>);\n<b>2)</b> объявление вне закона, проскрипция (proscriptiones Sullanae <i>Sen</i>): proscriptione cecidisse <i>T</i> пасть жертвой проскрипции."],
  ["proscriptor", "<big>proscriptor</big>\n\n<b>proscriptor, oris</b> <i>m</i> [proscribo]\nсклонный объявлять вне закона, сторонник проскрипционной системы (p. animus <i>PM</i>)."],
  ["proscripturio", "<big>proscripturio</big>\n\n<b>proscripturio, —, —, ire</b> [<i>desiderat. к</i> proscribo]\nжелать объявить вне закона <i>C</i>."],
  ["proscriptus", "<big>proscriptus</big>\n\n<b>proscriptus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> proscribo;\n<b>2.</b> <i>subst. m</i> проскрипт, внесённый в проскрипционные списки, объявленный вне закона <i>C, Sl, Sen, Sid</i>."],
  ["prosecia", "<big>prosecia</big>\n\n<b>prosecia, ae</b> <i>f</i> <i>LM</i>\n= prosecta."],
  ["proseco", "<big>proseco</big>\n\n<b>pro–seco, secui, sectum, are</b>\n<b>1)</b> отрезывать, отсекать (aures <i>Ap</i>): p. exta <i>Cato, L, Su etc.</i> отрезывать у жертвенного животного части, подлежавшие принесению в жертву;\n<b>2)</b> приносить в жертву (Aesculapio gallinaceum <i>Tert</i>);\n<b>3)</b> вспахивать: p. solum <i>PJ</i> поднимать пар."],
  ["prosecro", "<big>prosecro</big>\n\n<b>pro–secro, —, —, are</b> [sacro]\nсовершать жертвоприношения <i>Lact</i>."],
  ["prosecta", "<big>prosecta</big>\n\n<b>prosecta, orum</b> <i>n</i> [proseco]\nжертвенные части животного, внутренности <i>O, St</i>."],
  ["prosectum", "<big>prosectum</big>\n\n<b>prosectum, i</b> <i>n</i> <i>Vr</i>\n= prosecta."],
  ["prosectus", "<big>prosectus</big>\n\n<b>I prosectus, a, um</b> <i>part. pf. к</i>\nproseco.\n\n<b>II prosectus, (us) </b> <i>m</i> [proseco]\nразрезание, разрубание (gladiorum <i>Ap</i>): p. dentium <i>Ap</i> укус."],
  ["prosecutio", "<big>prosecutio</big>\n\n<b>prosecutio, onis</b> <i>f</i> [prosequor]\n<b>1)</b> сопровождение <i>CTh, Symm</i>;\n<b>2)</b> прощальный привет <i>Ambr</i>;\n<b>3)</b> продолжение <i>Ambr</i>."],
  ["prosecutor", "<big>prosecutor</big>\n\n<b>prosecutor, oris</b> <i>m</i>\n<b>1)</b> проводник-охранник (auri <i>CTh</i>);\n<b>2)</b> член свиты <i>или</i> эскорта <i>Veg, Eccl</i>."],
  ["prosecutoria", "<big>prosecutoria</big>\n\n<b>prosecutoria, ae</b> <i>f</i> (<i>sc.</i> epistula)\nсопроводительное послание <i>CTh, CJ</i>."],
  ["proseda", "<big>proseda</big>\n\n<b>proseda, ae</b> <i>f</i> [pro + sedeo]\nпроститутка <i>Pt</i>."],
  ["prosedamum", "<big>prosedamum</big>\n\n<b>prosedamum, i</b> <i>n</i> (<i>сарматское</i>)\npigritia equorum in coitu <i>PM</i>."],
  ["prosedeo", "<big>prosedeo</big>\n\n<b>pro–sedeo, —, —, ere</b>\nсидеть перед домом <i>Is</i>."],
  ["proselenos", "<big>proselenos</big>\n\n<b>proselenos, i</b> <i>m</i> (<i>греч.</i>)\nпредлунный, <i>т. е.</i> древнее луны (<i>sc.</i> Arcades <i>Cens</i>)."],
  ["proselytus", "<big>proselytus</big>\n\n<b>proselytus, i</b> <i>m</i> (<i>греч.</i>)\nпрозелит, новообращённый <i>Vlg, Eccl</i>."],
  ["proseminator", "<big>proseminator</big>\n\n<b>proseminator, oris</b> <i>m</i> [prosemino]\nпродолжатель рода <i>Tert</i>."],
  ["prosemino", "<big>prosemino</big>\n\n<b>pro–semino, avi, atum, are</b>\n<b>1)</b> рассевать, сеять (aliquid <i>C, Vtr, Lact</i>);\n<b>2)</b> продолжать рождать (familias <i>C</i>)."],
  ["prosentio", "<big>prosentio</big>\n\n<b>pro–sentio, sensi, —, ire</b>\nпредчувствовать <i>Pl</i>."],
  ["prosequor", "<big>prosequor</big>\n\n<b>pro–sequor, secutus sum, sequi</b> <i>depon.</i>\n<b>1)</b> провожать, сопровождать (aliquem usque ad portam <i>C</i>; abeuntem <i>T</i>): p. exsequiae <i>C</i> (defunctum <i>Pt</i>) провожать покойника;\n<b>2)</b> снабжать, одарять (aliquem magnis donis <i>L</i>); удостаивать (aliquem aliqua re <i>C etc.</i>): plausu Bruti memoriam p. <i>C</i> встречать упоминание имени Брута рукоплесканиями; p. aliquem laudibus <i>L, bAl, Col</i> <i>и</i> laudationibus <i>Pt</i> хвалить кого-л.; p. aliquem benevolentia <i>C</i> быть благосклонным к кому-л.; p. honorificis verbis <i>C</i> обращаться к кому-л. с любезными речами; p. aliquem beneficiis <i>C</i> делать добро, оказывать услуги кому-л.;\n<b>3)</b> следовать (aliquem p. et imitari <i>C</i>);\n<b>4)</b> преследовать (fugientes, hostem <i>Cs</i>);\n<b>5)</b> продолжать (non longius prosequar <i>C</i>);\n<b>6)</b> торжественно справлять (diem <i>Nep</i>);\n<b>7)</b> не переставать описывать (pascua versu <i>V</i>)."],
  ["prosero", "<big>prosero</big>\n\n<b>I pro–sero, ui, —, ere</b>\nвыдвигать, высовывать (caput <i>Sil, Veg</i>): se p. обращаться, направляться (favor publicus se proseruit in aliquem <i>Capit</i>).\n\n<b>II pro–sero, sevi, satum, ere</b>\n<b>1)</b> сеять, в результате посева производить (segetem <i>Lcn</i>);\n<b>2)</b> <i>перен.</i> порождать, вызывать (venti ex aere prosati <i>Ap</i>): augusto conceptu prosatus <i>Ap</i> высокого происхождения."],
  ["Proserpina", "<big>Proserpina</big>\n\n<b>Proserpina, ae</b> <i>f</i>\n<b>1)</b> Прозерпина, <i>дочь Цереры и Юпитера, жена Плутона, богиня подземного мира</i> <i>Vr, C, O etc.</i>;\n<b>2)</b> <i>поэт.</i> подземный мир, царство теней (me imperiosa trahit P. <i>H</i>)."],
  ["proserpinaca", "<big>proserpinaca</big>\n\n<b>proserpinaca, ae</b> <i>f</i> <i>бот.</i>\nптичья гречиха (спорыш) (<i>Polygonum aviculare</i>) <i>PM</i>."],
  ["proserpo", "<big>proserpo</big>\n\n<b>pro–serpo, —, —, ere</b>\nвыползать, вылезать (in lucem <i>Ap</i>); ползти вперёд (bestia proserpens <i>Pl</i>); расползаться, распространяться (malum proserpens <i>Amm</i>)."],
  ["proseucha", "<big>proseucha</big>\n\n<b>proseucha, ae</b> <i>f</i> (<i>греч.</i>)\nместо для молений (<i>у евреев</i>) <i>J</i>."],
  ["prosiciae", "<big>prosiciae</big>\n\n<b>prosiciae, arum</b> <i>f</i> <i>Sol</i>\n<i>и</i> prosicies, ei <i>f Vr</i> = prosecta."],
  ["prosico", "<big>prosico</big>\n\n<b>prosico</b> <i>Pl</i>\n= proseco."],
  ["prosilio", "<big>prosilio</big>\n\n<b>prosilio, ui</b> (<i>реже</i> <b>ivi</b> <i>и</i> <b>ii</b>), <b>—, ire</b> [pro + salio]\n<b>1)</b> вскакивать, спрыгивать (ab sede sua <i>L</i>; e lecto <i>QC, Su</i>); бросаться (ad flumen <i>Su</i>; in publicum <i>Pt</i>); выскакивать (finibus suis <i>V</i>);\n<b>2)</b> брызнуть (sanguis prosilit <i>O</i>; lacrimae prosiliunt <i>M</i>); пробиваться, прорастать (in altitudinem <i>Col</i>); вырываться, прорываться (frenis remotis <i>H</i>);\n<b>3)</b> спешно приниматься, спешить (p. aliquem castigatum <i>Pl</i>; ad accusationem <i>Dig</i>)."],
  ["prosisto", "<big>prosisto</big>\n\n<b>pro–sisto, —, —, ere</b>\nвыдаваться, выступать наружу (folia in modum radiorum prosistentia <i>Ap</i>)."],
  ["prosocer", "<big>prosocer</big>\n\n<b>pro–socer, soceri</b> <i>m</i>\nдед жены <i>O, PM, Dig</i>."],
  ["prosocrus", "<big>prosocrus</big>\n\n<b>pro–socrus, us</b> <i>f</i>\nбабка жены <i>Dig</i>."],
  ["prosodia", "<big>prosodia</big>\n\n<b>prosodia, ae</b> <i>f</i> (<i>греч.</i>)\nпросодия, тонический акцент, количество слога <i>Vr ap. AG</i>."],
  ["prosopopoeia", "<big>prosopopoeia</big>\n\n<b>prosopopoeia, ae</b> <i>f</i> (<i>греч.</i>; <i>лат.</i> ficta personarum inductio <i>или</i> personificatio)\n<b>1)</b> <i>ритор.</i> олицетворение <i>Q</i>;\n<b>2)</b> представление речи в форме диалога, драматизация <i>Q</i>."],
  ["prospecte", "<big>prospecte</big>\n\n<b>prospecte</b> [prospicio]\nпо зрелом размышлении, обдуманно (decernere <i>Tert</i>)."],
  ["prospectio", "<big>prospectio</big>\n\n<b>prospectio, onis</b> <i>f</i>\nпредусмотрительность, забота <i>CTh</i>."],
  ["prospectivus", "<big>prospectivus</big>\n\n<b>prospectivus, a, um</b> [prospicio]\nоткрывающий вид (на что-л.) (fenestrae <i>CJ</i>)."],
  ["prospecto", "<big>prospecto</big>\n\n<b>prospecto, avi, atum, are</b> [<i>intens. к</i> prospicio]\n<b>1)</b> смотреть вдаль, глядеть (ex tectis fenestrisque <i>L</i>); созерцать (pontum e puppi <i>O</i>): jam prospectantibus astris <i>St</i> уже при свете звёзд;\n<b>2)</b> быть обращенным, открывать вид (villa prospectat Siculum mare <i>Ph</i>): septentrionem p. <i>T</i> быть обращенным к северу; prospectantia vestigia pedum <i>AG</i> следы ног, обращенные вперёд; locus late prospectans <i>T</i> место, открывающее широкий вид;\n<b>3)</b> ждать, ожидать (aliquid <i>C etc.</i>): te quoque fata prospectant parta <i>V</i> и тебя ждёт такая же судьба."],
  ["prospector", "<big>prospector</big>\n\n<b>prospector, oris</b> <i>m</i> [prospicio]\nзаботящийся, промыслитель (in rebus incertis <i>Ap</i>; humanorum necessitatum <i>Tert</i>)."],
  ["prospectus", "<big>prospectus</big>\n\n<b>I prospectus, a, um</b> <i>part. pf. к</i>\nprospicio.\n\n<b>II prospectus, us</b> <i>m</i> [prospicio]\n<b>1)</b> вид (maris <i>C</i>): in prospectu esse <i>Cs</i> быть видным, заметным; in prospectu habere aliquid <i>Pt</i> иметь что-л. в перспективе;\n<b>2)</b> взор, взгляд (prospectu suo aequora metiri <i>O</i>);\n<b>3)</b> внимание: prospectum alicujus rei habere <i>AG</i> принимать во внимание что-л.;\n<b>4)</b> предусмотрительность, осторожность <i>Tert</i>."],
  ["prospeculor", "<big>prospeculor</big>\n\n<b>pro–speculor, atus sum, ari</b> <i>depon.</i>\n<b>1)</b> смотреть, глядеть (de vallo <i>bAfr</i>);\n<b>2)</b> высматривать, выжидать (adventum alicujus <i>L</i>);\n<b>3)</b> производить разведку (aliquem prospeculatum mittere <i>L</i>)."],
  ["prosper", "<big>prosper</big>\n\n<b>prosper (prosperus), spera, sperum</b> [pro + spero]\n<b>1)</b> благоприятный, благополучный, удачный, счастливый (exitus <i>C</i>): in prosperis rebus <i>QC</i> когда счастье благоприятствует;\n<b>2)</b> благоприятствующий (Luna prospera frugum <i>H</i>)."],
  ["prospera", "<big>prospera</big>\n\n<b>prospera, orum</b> <i>n</i> [prosper] (<i>тж.</i> p. rerum <i>Lcn, Sil</i>)\nблагоприятные обстоятельства <i>O, T etc.</i>; успехи (p. vel adversa <i>T</i>)."],
  ["prospere", "<big>prospere</big>\n\n<b>prospere</b> [prosper]\nблагоприятно, благополучно, счастливо (procedere, evenire <i>C</i>): p. cadere <i>T</i> принимать благоприятный оборот, удаваться."],
  ["prospergo", "<big>prospergo</big>\n\n<b>prospergo, —, rsum, ere</b> [pro + spargo]\nокроплять, обрызгивать (aliquid aqua prospersum <i>T</i>)."],
  ["prosperitas", "<big>prosperitas</big>\n\n<b>prosperitas, atis</b> <i>f</i> [prosper]\nблагополучие, процветание, счастье (vitae <i>C</i>); цветущее состояние (valetudinis <i>Nep</i>)."],
  ["prosperiter", "<big>prosperiter</big>\n\n<b>prosperiter</b> <i>Enn</i>\n= prospere."],
  ["prospero", "<big>prospero</big>\n\n<b>prospero, avi, atum, are</b> [prosper]\nблагоприятствовать, осенять счастьем (coepta <i>T</i>): p. alicui victoriam <i>L</i> даровать кому-л. победу; prosperantibus avibus <i>VM</i> при благоприятных ауспициях."],
  ["prosperus", "<big>prosperus</big>\n\n<b>prosperus, a, um</b>\n= prosper."],
  ["prospex", "<big>prospex</big>\n\n<b>prospex, icis</b> <i>m</i> [prospicio]\nпровидец, прозорливец <i>Tert</i>."],
  ["prospexi", "<big>prospexi</big>\n\n<b>prospexi</b> <i>pf. к</i>\nprospicio."],
  ["prospica", "<big>prospica</big>\n\n<b>prospica, ae</b> <i>f</i> [prospicio]\nзаботящаяся (p. sibi <i>Naev</i>)."],
  ["prospicienter", "<big>prospicienter</big>\n\n<b>prospicienter</b> [prospicio]\nосмотрительно, предусмотрительно (animadvertere aliquid <i>AG</i>)."],
  ["prospicientia", "<big>prospicientia</big>\n\n<b>prospicientia, ae</b> <i>f</i> [prospicio]\n<b>1)</b> осмотрительность, предусмотрительность (vigilia et p. <i>C</i>);\n<b>2)</b> внешний вид, образ (prospicientiam alicujus rei induisse <i>Tert</i>)."],
  ["prospicio", "<big>prospicio</big>\n\n<b>prospicio, spexi, spectum, ere</b> [pro + specio]\n<b>1)</b> глядеть вдаль, смотреть вперёд (respicere et p. <i>Vr</i>): p. longe (multum) <i>C</i> далеко видеть, иметь перед собой далёкую перспективу; parum prospiciunt oculi <i>Ter</i> глаза плохо видят;\n<b>2)</b> издали завидеть, увидеть вдали (aliquem procul <i>V</i>; hostium classem <i>L</i>): mortem p. <i>Sen</i> считать смерть чем-то далёким; <i>но</i>: p. senectutem <i>Sen</i> чувствовать, что надвигается старость || обозревать издали (p. incendium <i>Su</i>): domus agros prospicit <i>H</i> из дома открывается вид на поля;\n<b>3)</b> стоять на страже, сторожить (p. a janua <i>Nep</i>);\n<b>4)</b> заботиться, иметь попечение (alicui <i>C</i>; patriae <i>C</i>); предусматривать, предвидеть (illa, quae futura sunt <i>Ter</i>; casus futuros <i>C</i>; animo aliquid p. <i>C, L</i>): p. malo <i>C</i> предупреждать беду; plagae crescunt, nisi prospicis <i>Ter</i> бед будет всё больше, если заранее не принять мер;\n<b>5)</b> заранее готовить, заготовлять (commeatus in hiemem <i>L</i>); подготовлять, готовить (habitationem sibi <i>Pt</i>); подыскивать (maritum filiae <i>PJ</i>);\n<b>6)</b> внимательно <i>или</i> с удивлением смотреть (p. aliquem propter aliquid <i>Nep</i>)."],
  ["prospicue", "<big>prospicue</big>\n\n<b>prospicue</b> [prospicuus]\nосмотрительно, осторожно <i>Ap</i>."],
  ["prospicuus", "<big>prospicuus</big>\n\n<b>prospicuus, a, um</b> [prospicio]\nдалеко видный, издали заметный (turris <i>St</i>), <i>но</i>: turris prospicua <i>Ap</i> пророческая (волшебная) башня."],
  ["prospiro", "<big>prospiro</big>\n\n<b>pro–spiro, avi, —, are</b>\nвыдыхать (aliquid foras corporis <i>Ap</i>)."],
  ["prossus", "<big>prossus</big>\n\n<b>prossus</b> <i>арх.</i>\n= prorsus I."],
  ["prostas", "<big>prostas</big>\n\n<b>prostas, adis</b> <i>f</i> (<i>греч.</i>)\nпередняя, вестибюль <i>Vtr</i>."],
  ["prosterno", "<big>prosterno</big>\n\n<b>pro–sterno, stravi, stratum, ere</b>\n<b>1)</b> валить, срубать (arborem <i>Su</i>); повергать, бросать (corpus per herbas <i>QC</i>; se humi <i>C</i>); сбивать с ног (hostem <i>C</i>); опрокидывать (aliquem ingenti clade <i>QC</i>);\n<b>2)</b> расстраивать, разрушать, уничтожать (omnia <i>C</i>; mores <i>PM</i>): in totum mundi aevum prosterni <i>Lcn</i> погибнуть навеки;\n<b>3)</b> приводить в уныние (se abjicere et p. <i>C</i>);\n<b>4)</b> бросать в объятия порока, отдавать на позор (aliquam alicui <i>Su</i>; pudicitiam <i>Just, Su</i>). — <i>См. тж.</i> prostratus."],
  ["prosthesis", "<big>prosthesis</big>\n\n<b>prosthesis</b> (<i>acc.</i> in) <i>f</i> (<i>греч.</i>) <i>грам.</i>\nпро(с)тез, буквенная <i>или</i> слоговая приставка (<i>напр.</i> g- в слове gnatus)."],
  ["prostibilis", "<big>prostibilis</big>\n\n<b>prostibilis, is</b> <i>f</i> <i>Pl</i>\n= prostibulum 1."],
  ["prostibula", "<big>prostibula</big>\n\n<b>prostibula, ae</b> <i>f</i> <i>Pl, Tert</i>\n= prostibulum."],
  ["prostibulum", "<big>prostibulum</big>\n\n<b>prostibulum, i</b> <i>n</i> [prosto]\n<b>1)</b> публичная женщина, проститутка <i>Pl, Amm etc.</i>;\n<b>2)</b> дом разврата <i>Vlg, Is</i>."],
  ["prostituo", "<big>prostituo</big>\n\n<b>prostituo, stitui, stitutum, ere</b> [pro + statuo]\n<b>1)</b> ставить впереди (aliquem in limine <i>Eccl</i>);\n<b>2)</b> выставлять для разврата, выводить на позор, проституировать (aliquam <i>Sen</i>; se <i>Ctl</i>; faciem suam lucro <i>O</i>);\n<b>3)</b> позорить, бесчестить (famam <i>Cato</i>; pudicitiam suam <i>Su</i>)."],
  ["prostituta", "<big>prostituta</big>\n\n<b>prostituta, ae</b> <i>f</i>\nпубличная женщина <i>PM, Sen etc.</i>"],
  ["prostitutio", "<big>prostitutio</big>\n\n<b>prostitutio, onis</b> <i>f</i> [prostituo]\n<b>1)</b> предание разврату, совращение, растление <i>Eccl</i>;\n<b>2)</b> обесчещение, осквернение (imaginum <i>Tert</i>)."],
  ["prostitutor", "<big>prostitutor</big>\n\n<b>prostitutor, oris</b> <i>m</i>\n<b>1)</b> развратитель, растлитель <i>Tert</i>;\n<b>2)</b> осквернитель (sacramenti <i>Tert</i>)."],
  ["prostitutus", "<big>prostitutus</big>\n\n<b>prostitutus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> prostituo;\n<b>2.</b> <i>adj.</i> развратный, бесстыдный (sermones <i>Sid</i>)."],
  ["prosto", "<big>prosto</big>\n\n<b>pro–sto, stiti, —, are</b>\n<b>1)</b> выступать, торчать (angelli prostantes <i>Lcr</i>);\n<b>2)</b> стоять с товаром (in foro ipso <i>Pl</i>);\n<b>3)</b> быть выставленным на продажу (liber prostat <i>H</i>);\n<b>4)</b> предаваться публичному разврату <i>O, Sen, J etc.</i>"],
  ["prostomis", "<big>prostomis</big>\n\n<b>prostomis, idis</b> <i>f</i> (<i>греч.</i>)\nузда <i>LM</i> (<i>v. l.</i>)."],
  ["prostrasse", "<big>prostrasse</big>\n\n<b>prostrasse</b> <i>O</i>\n= prostravisse (<i>inf. pf. к</i> prosterno)."],
  ["prostratio", "<big>prostratio</big>\n\n<b>prostratio, onis</b> <i>f</i> [prosterno]\n<b>1)</b> угнетение, подавление <i>Eccl</i>;\n<b>2)</b> разорение, опустошение <i>Amm</i>;\n<b>3)</b> упадок (disciplinae <i>Tert</i>)."],
  ["prostrator", "<big>prostrator</big>\n\n<b>prostrator, oris</b> <i>m</i> [prosterno]\nпобедитель (hostium <i>Eccl</i>)."],
  ["prostratus", "<big>prostratus</big>\n\n<b>prostratus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> prosterno;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> лежащий (humi <i>QC</i>);\n<b>2)</b> плоский (pisces <i>Col</i>)."],
  ["prostravi", "<big>prostravi</big>\n\n<b>prostravi</b> <i>pf. к</i>\nprosterno."],
  ["prostylos", "<big>prostylos</big>\n\n<b>prostylos, on</b> <i>adj.</i> (<i>греч.</i>)\nимеющий впереди колонны (aedes <i>Vtr</i>)."],
  ["prostypa", "<big>prostypa</big>\n\n<b>prostypa, orum</b> <i>n</i> (<i>греч.</i>) <i>архит.</i>\nрельефные орнаменты <i>PM</i>."],
  ["prosubigo", "<big>prosubigo</big>\n\n<b>pro–subigo, —, —, ere</b>\n<b>1)</b> рыть, взрывать (terram <i>V</i>);\n<b>2)</b> заранее подготовить, хорошо обработать (fulmina <i>VF</i>);\n<b>3)</b> попирать, топтать (aliquid pede <i>Eccl</i>)."],
  ["prosulio", "<big>prosulio</big>\n\n<b>prosulio</b> <i>арх.</i>\n= prosilio."],
  ["prosum", "<big>prosum</big>\n\n<b>I pro–sum, profui, (profuturus), prodesse</b>\n<b>1)</b> быть полезным, приносить пользу (aut p., aut delectare <i>H</i>); служить, способствовать (alicui, in <i>или</i> ad aliquid, in aliqua re <i>C, Q etc.</i>): ut, si occupati profulmus aliquid ; civibus nostris, prosimus etiam otiosi <i>C</i> с тем, чтобы мы, своими делами приносившие какую-то пользу нашим согражданам, были им полезны и своим досугом;\n<b>2)</b> помогать (contra anginam <i>PM</i>; omnibus exemplo <i>Ap</i>): lacrimae nihil profuturae <i>Sen</i> совершенно бесполезные слёзы;\n<b>3)</b> быть выгодным (quum vitia prosunt, peccat qui recte facit <i>PS</i>): cui prodest scelus, is fecit <i>SenT</i> <i>впоследствии погов.</i> кому на руку было преступление, тот его и совершил.\n\n<b>II prosum</b> <i>adv.</i> <i>арх. Pl</i>\n= prorsum."],
  ["prosumia", "<big>prosumia</big>\n\n<b>prosumia, ae</b> <i>f</i>\nлёгкое разведывательное судно <i>Caec ap. AG</i>."],
  ["prosus", "<big>prosus</big>\n\n<b>I prosus, a, um</b> [<i>из</i> prorsus]\nнесвязанный, свободный, прозаический (oratio <i>Sen, Q, Col</i>).\n\n<b>II prosus</b> <i>adv.</i> <i>арх.</i>\n= prorsus I."],
  ["Prosymna", "<big>Prosymna</big>\n\n<b>Prosymna, ae</b> <i>и</i> <b>Prosymne, es</b> <i>f</i>\nПросимна, <i>город в Арголиде</i> (<i>с культом Геры-Юноны</i>) <i>St</i>."],
  ["Protagoras", "<big>Protagoras</big>\n\n<b>Protagoras, ae</b> <i>m</i>\nПротагор, <i>греч. софист из Абдеры (середина <i>V</i> в. до н. э.), друг Перикла; был изгнан из Афин по обвинению в безбожии и утонул (в 412 г. до н. э.) на пути в Сицилию</i> <i>C, Q, AG</i>."],
  ["Protagorion", "<big>Protagorion</big>\n\n<b>Protagorion, i</b> <i>n</i>\nпротагоровское изречение <i>AG</i>."],
  ["protasis", "<big>protasis</big>\n\n<b>protasis, is</b> <i>f</i> (<i>греч.</i>; <i>лат.</i> effatum) <i>лог.</i>, <i>грам.</i>\nутверждение, положение <i>Ap</i>."],
  ["protectio", "<big>protectio</big>\n\n<b>protectio, onis</b> <i>f</i> [protego]\nприкрытие, покровительство, защита <i>Q Dig, Eccl</i>."],
  ["protector", "<big>protector</big>\n\n<b>protector, oris</b> <i>m</i> [protego]\n<b>1)</b> страж, телохранитель <i>Spart, Capit, Amm</i>;\n<b>2)</b> охранитель, защитник (legum et paternorum institutorum <i>Tert</i>)."],
  ["protectum", "<big>protectum</big>\n\n<b>protectum, i</b> <i>n</i> [protego]\nприкрытие, навес (protecta vinearum <i>PM</i>)."],
  ["protectus", "<big>protectus</big>\n\n<b>I protectus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> protego;\n<b>2.</b> <i>adj.</i> безопасный <i>C</i> (<i>v. l.</i>), <i>CJ</i>.\n\n<b>II protectus, us</b> <i>m</i> <i>Dig</i>\n= protectum."],
  ["protego", "<big>protego</big>\n\n<b>pro–tego, texi, tectum, ere</b>\n<b>1)</b> прикрывать (aliquem scuto <i>Cs</i>); покрывать (tabernaculum hedera <i>Cs</i>); укрывать (naves a ventis <i>L</i>); защищать (aliquem precibus <i>T</i>; aliquem ab impetu alicujus <i>Sen</i>): terra protectura hiemes <i>St</i> земляной слой, долженствующий защищать от холодов;\n<b>2)</b> скрывать (nequitiam aliqua re <i>VP</i>)."],
  ["protelo", "<big>protelo</big>\n\n<b>protelo, avi, atum, are</b> [<i>одного корня с</i> telum]\n<b>1)</b> прогонять (aliquem saevidicis dictis <i>Ter</i>);\n<b>2)</b> пригонять, доставлять (aliquem in portum <i>Tert</i>);\n<b>3)</b> откладывать, отсрочивать (diem <i>Dig</i>; litem <i>CJ</i>);\n<b>4)</b> продлевать жизнь, <i>т. е.</i> спасать (aliquos <i>Tert</i>);\n<b>5)</b> протягивать, <i>pass.</i> быть протяжённым, длинным (protelata moles <i>Eccl</i>)."],
  ["protelum", "<big>protelum</big>\n\n<b>protelum, i</b> <i>n</i> (<i>встреч. только в</i> <i>abl.</i>) [protendo]\n<b>1)</b> постромка для запряжки цугом (protelo ducere aratrum <i>Cato</i>);\n<b>2)</b> упряжка (boum <i>PM</i>; protelis binis arare <i>PM</i>);\n<b>3)</b> ряд, вереница: protelo continuato <i>Lcr</i> в Виде непрерывного ряда, сплошной вереницей."],
  ["protenam", "<big>protenam</big>\n\n<b>protenam</b> <i>v. l.</i>\n= protinam."],
  ["protendo", "<big>protendo</big>\n\n<b>pro–tendo, tendi, tentum (tensum), ere</b>\n<b>1)</b> протягивать (dextram <i>V</i>; supinas manus ad genua alicujus <i>Pt</i>); вытягивать (cervicem <i>T</i>); выставлять вперёд (hastas <i>V</i>);\n<b>2)</b> растягивать, произносить протяжно (praepositiones <i>AG</i>);\n<b>3)</b> <i>med.-pass.</i> protendi простираться (ad Bactros <i>PM</i>); выдаваться вперёд (digitus medius longissime protenditur <i>PM</i>)."],
  ["protensio", "<big>protensio</big>\n\n<b>protensio, onis</b> <i>f</i>\n<b>1)</b> протягивание (manus <i>Hier etc.</i>);\n<b>2)</b> <i>Ap</i> = protasis."],
  ["protentus", "<big>protentus</big>\n\n<b>protentus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> protendo;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> имеющий в длину, протяжением (p. in octo pedes <i>Vtr</i>);\n<b>2)</b> долгий, продолжительный (protentior quam nostra vita est <i>Sol</i>)."],
  ["protenus", "<big>protenus</big>\n\n<b>protenus</b> <i>adv.</i> <i>v. l.</i>\n= protinus."],
  ["protermino", "<big>protermino</big>\n\n<b>pro–termino, —, —, are</b>\nраздвигать, расширять (fines <i>Ap</i>; possessionis metas <i>Sid</i>)."],
  ["protero", "<big>protero</big>\n\n<b>pro–tero, trivi, tritum, ere</b>\n<b>1)</b> давить, раздавливать (aliquem pedibus <i>Pl</i>); топтать (arva florentia <i>O</i>);\n<b>2)</b> сокрушать, смять, опрокидывать (aciem hostium <i>T</i>; agmina curru <i>V</i>);\n<b>3)</b> попирать, жестоко обращаться, угнетать (aliquem <i>C, H</i>; urbem <i>O</i>);\n<b>4)</b> прогонять, вытеснять (ver — <i>acc.</i> — proterit aestas <i>H</i>). — <i>См. тж.</i> protritus."],
  ["proterreo", "<big>proterreo</big>\n\n<b>pro–terreo, terrui, territum, ere</b>\n<b>1)</b> отпугивать, прогонять (aliquem ferro <i>C</i>; ardentibus facibus feras <i>Ap</i>);\n<b>2)</b> изгонять (aliquem patria <i>C</i>)."],
  ["proterve", "<big>proterve</big>\n\n<b>proterve</b> [protervus]\nсмело, дерзко (loqui <i>Pt</i>); нагло, бесстыдно (dicere, facere <i>O</i>)."],
  ["protervia", "<big>protervia</big>\n\n<b>protervia, ae</b> <i>f</i> <i>Aus, Ambr</i>\n= protervitas."],
  ["protervio", "<big>protervio</big>\n\n<b>protervio, —, —, ire</b>\nбыть дерзким, наглым <i>Tert</i>."],
  ["protervitas", "<big>protervitas</big>\n\n<b>protervitas, atis</b> <i>f</i> [protervus]\nсвоеволие, своенравие (p. grata <i>H</i>); дерзость, наглость, бесстыдство <i>Ter, C etc.</i>"],
  ["proterviter", "<big>proterviter</big>\n\n<b>proterviter</b> <i>Enn</i>\n= proterve."],
  ["protervus", "<big>protervus</big>\n\n<b>protervus, a, um</b>\n<b>1)</b> дерзкий, своевольный, наглый, бесстыдный (juvenis <i>H</i>; frons <i>H</i>; dicta <i>O</i>);\n<b>2)</b> буйный, резкий (ventus <i>H</i>);\n<b>3)</b> беспощадный, знойный (stella canis <i>O</i>)."],
  ["Protesilaeus", "<big>Protesilaeus</big>\n\n<b>Protesilaeus, a, um</b> [Protesilaus]\nпротесилаев (domus <i>Ctl</i>)."],
  ["Protesilaodamia", "<big>Protesilaodamia</big>\n\n<b>Protesilaodamia, ae</b> <i>f</i>\n<i>название трагедии Ливия Андроника</i> <i>AG</i>."],
  ["Protesilaus", "<big>Protesilaus</big>\n\n<b>Protesilaus, i</b> <i>m</i>\nПротесилай, <i>сын Ификла из Филаки, муж Лаодамии, предводитель фессалийцев в Троянской войне и первая её жертва со стороны греков</i> <i>Prp, O</i>."],
  ["protestatio", "<big>protestatio</big>\n\n<b>protestatio, onis</b> <i>f</i> [protestor]\nторжественное заявление, провозглашение, заверение <i>Vlg, Eccl</i>."],
  ["protestor", "<big>protestor</big>\n\n<b>pro–testor, atus sum, ari</b> <i>depon.</i>\nторжественно заявлять, утверждать, свидетельствовать <i>Q, Macr, Amm etc.</i>"],
  ["Proteus", "<big>Proteus</big>\n\n<b>Prōte͡us, eī</b> <i>и</i> <b>eos</b> <i>m</i>\n<b>1)</b> Протей, <i>вещий морской бог на о-ве Фаросе (Египет), обладавший даром преображения</i> <i>V, O, Lcn etc.</i>: Protei columnae <i>V</i> Протеевы столпы, <i>т. е.</i> границы Египта;\n<b>2)</b> изменчивая натура, непостоянный человек <i>H</i>;\n<b>3)</b> хитрец, лукавый человек <i>H</i>."],
  ["protexi", "<big>protexi</big>\n\n<b>protexi</b> <i>pf. к</i>\nprotego."],
  ["prothesis", "<big>prothesis</big>\n\n<b>prothesis, is</b> <i>f</i> <i>v. l.</i>\n= prosthesis."],
  ["prothyme", "<big>prothyme</big>\n\n<b>prothyme (prothume)</b> <i>adv.</i> (<i>греч.</i>)\nприятно, с удовольствием (diem sumere <i>Pl</i>)."],
  ["prothymia", "<big>prothymia</big>\n\n<b>prothymia (prothumia), ae</b> <i>f</i> (<i>греч.</i>)\nблагосклонность, радушие, любезность (benignitates et prothymiae hominum <i>Pl</i>)."],
  ["prothyra", "<big>prothyra</big>\n\n<b>prothyra, orum</b> <i>n</i> (<i>греч.</i>)\n<i>у греков</i>— передняя, вестибюль <i>Vtr</i>, <i>у римлян</i> — навес на колоннах впереди входа в дом <i>Vtr</i>."],
  ["protinam", "<big>protinam</big>\n\n<b>protinam (protenam)</b> <i>adv.</i> <i>арх. Pl, Ter</i>\n= protinus."],
  ["protinus", "<big>protinus</big>\n\n<b>protinus</b> <i>и</i> <b>protenus</b> <i>adv.</i> [tenus]\n<b>1)</b> дальше, вперед (pergere <i>C</i>); безостановочно, прямо (Laodiceam p. ire <i>C</i>); всё дальше и дальше (capellas agere <i>V</i>; vivere <i>H</i>);\n<b>2)</b> вдоль и поперёк (p. omne contremuit nemus <i>V</i>);\n<b>3)</b> сплошь, непрерывно (quum p. utraque tellus una foret <i>V</i>); постоянно, неизменно (morem sacrum colere <i>V</i>);\n<b>4)</b> немедленно, сразу же, тотчас (p. a <i>или</i> de aliqua re <i>CC, PM etc.</i>): p. virili toga <i>Su</i> лишь только (была надета) мужская тога; p. ut (quum, quam, atque) <i>PM etc.</i> как только; p. ut venias <i>O</i> если ты даже сейчас вернёшься;\n<b>5)</b> (<i>тж.</i> p. initio <i>CC</i>) в самом же начале (oratio p. perficiens auditorem benevolum <i>C</i>)."],
  ["Protogenes", "<big>Protogenes</big>\n\n<b>Protogenes, is</b> <i>m</i>\nПротоген, <i>живописец из Кавна (Кария) IV-III вв. до н. э.</i> <i>C, Vr, PM etc.</i>"],
  ["protolapsus", "<big>protolapsus</big>\n\n<b>proto–lapsus, a, um</b> (<i>греко-лат.</i>)\nвпервые согрешивший (Adam <i>Eccl</i>)."],
  ["protollo", "<big>protollo</big>\n\n<b>pro–tollo, —, —, ere</b>\n<b>1)</b> вытягивать, протягивать (manum <i>Pl</i>);\n<b>2)</b> оттягивать, откладывать (aliquid in crastinum <i>Pl</i>);\n<b>3)</b> повышать (vox a minore solita ad majorem protolli <i>Amm</i>)."],
  ["protomysta", "<big>protomysta</big>\n\n<b>protomysta, ae</b> <i>m</i>\nпротомист, верховный жрец <i>Sid</i>."],
  ["protono", "<big>protono</big>\n\n<b>pro–tono, —, —, are</b>\nвозгреметь (tali ira <i>VF</i>)."],
  ["protoplastus", "<big>protoplastus</big>\n\n<b>protoplastus, i</b> <i>m</i> (<i>греч.</i>)\nпервозданный (человек) <i>Eccl</i>."],
  ["protopraxia", "<big>protopraxia</big>\n\n<b>protopraxia, ae</b> (<i>acc.</i> an) <i>f</i> (<i>греч.</i>)\nпреимущество перед остальными кредиторами (<i>при уплате долгов</i>), привилегированный долг <i>PJ</i>."],
  ["protosedeo", "<big>protosedeo</big>\n\n<b>proto–sedeo, —, —, ere</b> (<i>греко-лат.</i>)\nвосседать впереди <i>Tert</i>."],
  ["protostasia", "<big>protostasia</big>\n\n<b>protostasia, ae</b> <i>f</i> (<i>греч.</i>)\nдолжность главы податного ведомства <i>CTh, CJ</i>."],
  ["prototomi", "<big>prototomi</big>\n\n<b>prototomi, orum</b> <i>m</i> [prototomus]\nранние всходы капусты (<i>срезанные для стола</i>) <i>M</i>."],
  ["prototomus", "<big>prototomus</big>\n\n<b>prototomus, a, um</b> (<i>греч.</i> «отрезанный первым»)\nтолько что впервые собранный (caulis <i>Col</i>, coliculi <i>M</i>)."],
  ["prototypia", "<big>prototypia</big>\n\n<b>prototypia, ae</b> <i>f</i>\nдолжность начальника рекрутского набора <i>CTh, CJ</i>."],
  ["protractio", "<big>protractio</big>\n\n<b>protractio, onis</b> <i>f</i> [protraho]\n<b>1)</b> продление (черты) <i>Macr</i>;\n<b>2)</b> отсрочка <i>Eccl, CJ</i>."],
  ["protraho", "<big>protraho</big>\n\n<b>pro–traho, traxi, tractum, ere</b>\n<b>1)</b> вытаскивать, притаскивать, выволакивать (aliquem e tentorio <i>T</i>); извлекать (aliquid in lucem <i>Lcr</i>);\n<b>2)</b> обнаруживать, открывать, разоблачать (facinus <i>L</i>);\n<b>3)</b> тянуть, затягивать (epulas ad mediam noctem <i>Su</i>);\n<b>4)</b> доводить (aliquem ad paupertatem <i>Pl</i>);\n<b>5)</b> распространять (aliquid usque ad Graecum sermonem <i>Dig</i>)."],
  ["protraxe", "<big>protraxe</big>\n\n<b>protraxe</b> <i>Lcr</i>\n= protraxisse (<i>inf. pf. к</i> protraho)."],
  ["protrepticon", "<big>protrepticon</big>\n\n<b>protrepticon (-um), i</b> <i>n</i> (<i>греч.</i>)\nувещевание <i>St, Treb, Aus, Sid</i>."],
  ["protrimenta", "<big>protrimenta</big>\n\n<b>protrimenta, orum</b> <i>n</i> [protero]\nгустая похлёбка <i>Ap</i>."],
  ["protritus", "<big>protritus</big>\n\n<b>protritus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> protero;\n<b>2.</b> <i>adj.</i> обыденный, избитый (verba <i>AG</i>); банальный (oratio <i>AG</i>)."],
  ["protropon", "<big>protropon</big>\n\n<b>protropon (-um), i</b> <i>n</i> (<i>греч.</i>)\nмолодое вино, сок, выделяемый виноградом до выдавливания <i>PM, Vtr</i>."],
  ["protrudo", "<big>protrudo</big>\n\n<b>pro–trudo, trusi, trusum, ere</b>\n<b>1)</b> толкать вперёд, выталкивать (aliquem foras <i>Ph</i>);\n<b>2)</b> отсрочивать, откладывать (comitia in mensem Januarium <i>C</i>)."],
  ["protubero", "<big>protubero</big>\n\n<b>pro–tubero, —, —, are</b>\nнабухать (poma protuberant <i>Sol</i>); вырастать (supra modum <i>Aus</i>)."],
  ["protuli", "<big>protuli</big>\n\n<b>protuli</b> <i>pf. к</i>\nprofero."],
  ["protumidus", "<big>protumidus</big>\n\n<b>pro–tumidus, a, um</b>\nвздутый спереди, выпуклый, выгнутый (luna <i>Ap</i>)."],
  ["proturbo", "<big>proturbo</big>\n\n<b>pro–turbo, avi, atum, are</b>\n<b>1)</b> гнать, прогонять (hostes telis <i>L</i>);\n<b>2)</b> валить (silvas <i>O</i>);\n<b>3)</b> изрыгать, испускать (anhelatum pectore murmur <i>Sil</i>)."],
  ["protutela", "<big>protutela</big>\n\n<b>pro–tutela, ae</b> <i>f</i>\nобязанности заместителя опекуна <i>Dig</i>."],
  ["prout", "<big>prout</big>\n\n<b>pro–ut</b> <i>conj.</i>\nсмотря по, в зависимости от (p. res postulat <i>C</i>): p. mea opinio est <i>Ap</i> по моему (личному) мнению."],
  ["provectio", "<big>provectio</big>\n\n<b>provectio, onis</b> <i>f</i> <i>Lact</i>\n= provectus II."],
  ["provectus", "<big>provectus</big>\n\n<b>I provectus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> proveho;\n<b>2.</b> <i>adj.</i> пожилой: provectior <i>Aus</i> старший годами.\n\n<b>II provectus, us</b> <i>m</i> [proveho]\nповышение, продвижение (honestiorum <i>AV</i>; animorum <i>Aug</i>); рост, преуспеяние (praesentia domini p. est agri <i>Pall</i>); увеличение, нарастание: provectu aetatis <i>Sid</i> с наступлением старости."],
  ["proveho", "<big>proveho</big>\n\n<b>pro–veho, vexi, vectum, ere</b>\n<b>1)</b> подвигать, вести дальше, увозить вперёд (ratem per freta <i>VF</i>); <i>pass.</i> уезжать, выезжать (equo provectus <i>L</i>); отплывать (provehi pelago <i>V</i>; naves in altum provectae <i>Cs</i>): provectus aetate <i>или</i> provecta aetate <i>C</i> старый, престарелый; provecta senectus <i>T</i> глубокая старость; nox provecta <i>T</i> поздняя (глубокая) ночь; bellum longius provehitur <i>T</i> война затягивается;\n<b>2)</b> выдвигать, возвышать, возвеличивать (aliquem ad amplissimos honores <i>Su</i>); <i>pass.</i> подниматься, возвышаться, доходить (e gregariis ad summa militiae <i>T</i>);\n<b>3)</b> увлекать, завлекать (spes provehit aliquem <i>L etc.</i>): gaudio provehente <i>L</i> в порыве радости, на радостях || <i>pass.</i> быть увлечённым, увлекаться, заходить далеко (provehi amore <i>C</i>; provectus eo audaciae <i>T</i>; provehi in maledicta <i>L</i>): eo usque provectus, ut... <i>T</i> дошедший до того, что...;\n<b>4)</b> затягивать (orationem <i>C</i>); <i>pass.</i> распространяться: quid ultra provehor? <i>V</i> к чему мне ещё продолжать?"],
  ["provenio", "<big>provenio</big>\n\n<b>pro–venio, veni, ventum, ire</b>\n<b>1)</b> выходить, выступать, появляться (in scaenam <i>Pl</i>);\n<b>2)</b> всходить, рождаться (frumentum angustius provenerat <i>Cs</i>); расти, вырастать (arbor provenit <i>PM</i>);\n<b>3)</b> возникать (gaudium ex studiis provenit <i>PJ</i>); оказываться: mira ubertate provenit terra <i>T</i> земля удивительно плодородна;\n<b>4)</b> случаться, происходить (plerumque <i>Col</i>); складываться (ut initia belli provenissent <i>T</i>);\n<b>5)</b> обнаруживаться, находиться, встречаться (insula, in qua plumbum provenit <i>PM</i>);\n<b>6)</b> удаваться: carmina proveniunt animo deducta sereno <i>O</i> стихи удаются (лишь), если созданы при душевной ясности; proveni nequiter <i>Pl</i> не повезло мне."],
  ["proventus", "<big>proventus</big>\n\n<b>proventus, us</b> <i>m</i> [provenio]\n<b>1)</b> появление, рост (rosarum <i>PM</i>);\n<b>2)</b> урожай, сбор, выход (lini <i>Col</i>; frumentorum <i>Aug</i>);\n<b>3)</b> множество, изобилие (poetarum <i>PJ</i>);\n<b>4)</b> развитие, ход (pugnae <i>Cs</i>);\n<b>5)</b> благоприятный исход, успех (temporis superioris <i>Cs</i>); процветание (artium <i>Ap</i>)."],
  ["proverbialis", "<big>proverbialis</big>\n\n<b>proverbialis, e</b> [proverbium]\nвошедший в пословицу (versus <i>AG</i>)."],
  ["proverbialiter", "<big>proverbialiter</big>\n\n<b>proverbialiter</b>\nв виде пословицы <i>Amm, Sid</i>."],
  ["proverbium", "<big>proverbium</big>\n\n<b>proverbium, i</b> <i>n</i> [pro + verbum]\nпословица: in p. venire <i>L, PM</i> <i>и</i> in proverbii consuetudinem venire <i>C</i> войти в пословицу."],
  ["proversus", "<big>proversus</big>\n\n<b>proversus, a, um</b>\nобращенный (направленный) вперёд <i>Pl ap. Vr</i>."],
  ["provexi", "<big>provexi</big>\n\n<b>provexi</b> <i>pf. к</i>\nproveho."],
  ["provide", "<big>provide</big>\n\n<b>provide</b> <i>PM</i>\n= providenter."],
  ["providens", "<big>providens</big>\n\n<b>providens, entis</b>\n<b>1.</b> <i>part. praes. к</i> provideo;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> предусмотрительный, осторожный (dux cautus et p. <i>L</i>);\n<b>2)</b> верный, надёжный (id est providentius <i>C</i>)."],
  ["providenter", "<big>providenter</big>\n\n<b>providenter</b>\nпредусмотрительно, осторожно <i>C, Sl, Q, PM etc.</i>"],
  ["providentia", "<big>providentia</big>\n\n<b>providentia, ae</b> <i>f</i> [providens]\n<b>1)</b> предвидение (p., per quam futurum aliquid videtur <i>C</i>);\n<b>2)</b> предусмотрительность (<i>sc.</i> Jugurthae <i>Sl</i>);/;\n<b>3)</b> заботливость (filiorum suorum <i>Dig</i>);\n<b>4)</b> провидение <i>Sen, Q, Ap, Eccl</i>."],
  ["provideo", "<big>provideo</big>\n\n<b>pro–video, vidi, visum, ere</b>\n<b>1)</b> раньше увидеть, издали завидеть (aliquem <i>Pl</i>; navem <i>Su</i>); видеть вдали, замечать впереди (procul provideri <i>L</i>);\n<b>2)</b> предвидеть (tempestatem <i>C</i>; aliquid ante multos annos <i>Just</i>); наперёд взвешивать, заранее обдумывать, предусмотреть (omnia <i>C</i>): p. ratione <i>C</i> заранее высчитывать;\n<b>3)</b> заранее заботиться, иметь попечение (de aliqua re <i>C, Cs</i> <i>или</i> alicui rei <i>Pl, rhH etc.</i>; p. sibi liberisque <i>Sl</i>);\n<b>4)</b> заранее готовить, заготовлять (frumentum exercitui <i>Cs</i>): consilia in posterum p. <i>C</i> принимать меры на будущее."],
  ["providus", "<big>providus</big>\n\n<b>providus, a, um</b> [provideo]\n<b>1)</b> предвидящий (rerum futurarum <i>C</i>);\n<b>2)</b> предусмотрительный, осторожный (prudens et p. <i>C</i>);\n<b>3)</b> заботливый, заботящийся (utilitatum opportunitatumque omnium <i>C</i>)."],
  ["provincia", "<big>provincia</big>\n\n<b>provincia, ae</b> <i>f</i>\n<b>1)</b> должность, поле деятельности, должностные обязанности (provincias partiri <i>L</i>); верховное командование (p. classis <i>или</i> maritima <i>L</i>): Apuliam provinciam obtinere <i>L</i> получить руководство военными действиями против Апулии;\n<b>2)</b> подсудность, юрисдикция, сфера компетенции: p. urbana <i>L</i> круг полномочий у praetor urbanus;\n<b>3)</b> поручение, дело (alicui provinciam dare <i>C</i>);\n<b>4)</b> провинция (<i>внеиталийская завоёванная область с римск. наместником во главе</i>) (civitatem in provinciae formam redigere <i>L, Su</i>); <i>обыкновенно</i> Малая Азия, Африка (<i>область бывшего Карфагена</i>), <i>преим. же</i> юго-вост. Галлия (Gallia p. <i>или</i> Gallia ulterior, <i>тж.</i> Narbonensis), <i>впоследствии франц.</i> la Provence <i>Cs, PM</i>; <i>у неримск. народов</i> наместничество, сатрапия <i>Hirt, L, Nep</i>;\n<b>5)</b> управление провинцией, должность наместника в провинции (primus annus provinciae <i>C</i>);\n<b>6)</b> <i>перен.</i> обязанность, задача (provinciam cepisse duram <i>Ter</i>)."],
  ["provincialis", "<big>provincialis</big>\n\n<b>I provincialis, e</b> [provincia]\nпровинциальный (bellum <i>T</i>); относящийся к управлению провинцией (negotia <i>C</i>); находящийся в провинции (ornamenta et commoda <i>C</i>); внеиталийский (non p., sed Italicus ager <i>Col</i>).\n\n<b>II provincialis, is</b> <i>m</i>\nжитель провинции, провинциал <i>C, PJ, Su</i>."],
  ["provinciatim", "<big>provinciatim</big>\n\n<b>provinciatim</b> [provincia]\nпо провинциям (legiones et auxilia distribuere <i>Su</i>)."],
  ["provindemiator", "<big>provindemiator</big>\n\n<b>pro–vindemiator, oris</b> <i>m</i>\nпредуборочная звезда (<i>справа от созвездия Девы; её восход предшествовал сбору винограда</i>) <i>Vtr</i>."],
  ["provisio", "<big>provisio</big>\n\n<b>provisio, onis</b> <i>f</i> [provideo]\n<b>1)</b> предвидение (posteri temporis <i>C</i>); прозорливость (p. animi et praeparatio <i>C</i>);\n<b>2)</b> предусмотрительность, осторожность (longa animi p. <i>C</i>);\n<b>3)</b> забота, попечение (alicujus rei <i>C</i>): p. annonaria <i>Treb</i> продовольственное снабжение; p. vitiorum <i>C</i> средство предохранить (себя) от пороков."],
  ["proviso", "<big>proviso</big>\n\n<b>I pro–viso, —, —, ere</b>\nсмотреть, глядеть (proviso, quid agat <i>Ter</i>).\n\n<b>II proviso</b> <i>adv.</i>\nосмотрительно, с оглядкой (temere et p. <i>T</i>)."],
  ["provisor", "<big>provisor</big>\n\n<b>provisor, oris</b> <i>m</i> [provideo]\nпредвидящий: utilium tardus p. <i>H</i> не очень-то помышляющий о будущей пользе || заранее заботящийся (alicujus rei <i>T</i>)."],
  ["provisum", "<big>provisum</big>\n\n<b>provisum, i</b> <i>n</i>\nпредусмотрительность, забота (provisa majorum <i>Eccl</i>); domi provisa <i>Ap</i> внутренние (домашние) дела."],
  ["provisus", "<big>provisus</big>\n\n<b>I provisus, a, um</b> <i>part. pf. к</i>\nprovideo.\n\n<b>II provisus, (us) </b> <i>m</i> [provideo]\n<b>1)</b> смотрение вдаль: ne oculi quidem provisu juvabant <i>T</i> (во время ночного боя) ничего нельзя было разглядеть;\n<b>2)</b> предвидение (periculi <i>T</i>);\n<b>3)</b> предосторожность, забота, попечение (deorum <i>T</i>);\n<b>4)</b> обеспечение (rei frumentariae <i>T</i>)."],
  ["provivo", "<big>provivo</big>\n\n<b>pro–vivo, —, —, ere</b>\nпродолжать жить, оставаться в жнвых <i>T</i>."],
  ["provocabulum", "<big>provocabulum</big>\n\n<b>prō–vocābulum, ī</b> <i>n</i>\nзаменяющее слово, <i>т. е.</i> местоимение <i>Vr</i>."],
  ["provocaticius", "<big>provocaticius</big>\n\n<b>prōvocātīcius, a, um</b> [provoco]\n<b>1)</b> вызванный, возбуждённый (animatio <i>Tert</i>);\n<b>2)</b> возбуждающий (unguentum <i>CA</i> — <i>v. l.</i>)."],
  ["provocatio", "<big>provocatio</big>\n\n<b>prōvocātio, ōnis</b> <i>f</i> [provoco]\n<b>1)</b> вызов (ex provocatione dimicare <i>PM</i>);\n<b>2)</b> апелляция, обжалование (ad populum <i>C</i>); право апелляции: est p. <i>L</i> есть возможность апелляции; poena sine provocatione <i>C</i> наказание, не подлежащее обжалованию."],
  ["provocativus", "<big>provocativus</big>\n\n<b>prōvocātīvus, a, um</b> <i>Tert, CA</i>\n= provocaticius 1 <i>и</i> 2."],
  ["provocator", "<big>provocator</big>\n\n<b>prōvocātor, ōris</b> <i>m</i> [provoco]\n<b>1)</b> бросающий вызов <i>L, Just, AG</i>;\n<b>2)</b> гладиатор, приёмы которого заключались исключительно в ловкости и быстроте приёмов <i>C</i>."],
  ["provocatorius", "<big>provocatorius</big>\n\n<b>prōvocātōrius, a, um</b> [provocator]\nотносящийся к тому, кто бросает вызов: dona provocatoria <i>AG</i> дары преподносившиеся тем, кто бросал врагу вызов и побеждал его."],
  ["provocatrix", "<big>provocatrix</big>\n\n<b>prōvocātrīx, īcis</b> <i>f</i>\nсоблазнительница (p. improbitas <i>Lact</i>)."],
  ["provoco", "<big>provoco</big>\n\n<b>prō–voco, āvī, ātum, āre</b>\n<b>1)</b> вызывать, бросать вызов (aliquem ad pugnam <i>C</i>): p. aliquem ad judices <i>Ap</i> привлекать кого-л. к судебной ответственности; p. aliquem virtute <i>PJ</i> соперничать с кем-л. в добродетели; elegia Graecos p. <i>Q</i> оспаривать у греков первенство в области элегии;\n<b>2)</b> пробуждать (roseo ore diem <i>O</i>; animam quibusdam medicamentis <i>Ap</i>);\n<b>3)</b> вызывать, причинять (mortem <i>PM</i>);\n<b>4)</b> возбуждать, подстрекать (aliquos sermonibus <i>Cs</i>); раздражать (aliquem injuria <i>VP, T</i>);\n<b>5)</b> приглашать (aliquem ad bibendum <i>Vop</i>; in aleam <i>Pl</i>);\n<b>6)</b> обжаловать, апеллировать (ab omni judicio provocari licet <i>C</i>; p. ad populum <i>C</i>);\n<b>7)</b> ссылаться (p. ad Catonem <i>C</i>)."],
  ["provolgo", "<big>provolgo</big>\n\n<b>prōvolgo</b> <i>арх.</i>\n= provulgo."],
  ["provolo", "<big>provolo</big>\n\n<b>prō–volo, āvī, ātum, āre</b>\n<b>1)</b> вылетать (pulli provolant, <i>sc.</i> ex ovis <i>PM</i>);\n<b>2)</b> устремляться, бросаться вперёд, ринуться (in primum <i>L</i>)."],
  ["provolvo", "<big>provolvo</big>\n\n<b>prō–volvo, volvī, volūtum, ere</b>\n<b>1)</b> катить вперёд, скатывать (saxa in aliquos <i>QC</i>); сбрасывать, свергать (aliquem in mediam viam <i>T</i>);\n<b>2)</b>: p. se <i>или</i> <i>pass.</i> provolvi ad pedes <i>L</i> (ad genua <i>L</i> <i>или</i> genibus alicujus <i>T</i>) броситься к чьим-л. ногам; provolvi ad libita alicujus <i>T</i> совершенно подчиниться чьим-л. прихотям; provolvi aliqua re <i>T</i> лишиться чего-л."],
  ["provomo", "<big>provomo</big>\n\n<b>prō–vomo, —, —, ere</b>\nизрыгать, извергать (turbinis immanem vim <i>Lcr</i>)."],
  ["provulgo", "<big>provulgo</big>\n\n<b>pro–vulgo, avi, atum, are</b>\nоглашать, предавать гласности (conjurationes <i>Su</i>); опубликовывать, обнародовать (litteras <i>Ap</i>; operam <i>Sid</i>)."],
  ["prox", "<big>prox</big>\n\n<b>prox</b> <i>interj.</i> [<i>из</i> proba vox]\nне взыщите на слове, с позволения сказать <i>Pl</i>."],
  ["proxeneta", "<big>proxeneta</big>\n\n<b>proxeneta (-es), ae</b> <i>m</i> (<i>греч.</i>)\nпосредник, маклер <i>Sen, M, Dig</i>."],
  ["proxeneticum", "<big>proxeneticum</big>\n\n<b>proxeneticum, i</b> <i>n</i>\nмаклерское вознаграждение, куртаж <i>Dig</i>."],
  ["proximatus", "<big>proximatus</big>\n\n<b>proximatus, us</b> <i>m</i> [proximus II]\nпроксимат, должность проксима <i>CJ, CTh</i>."],
  ["proxime", "<big>proxime</big>\n\n<b>proxime</b> <i>adv. и praep. cum acc.</i> <i>реже</i> <i>cum dat.</i> [<i>superl. к</i> prope]\n<b>1)</b> весьма близко (p. ab illa urbe <i>L</i>; p. hostium castris <i>Cs</i>): p. hostem <i>C</i> возле самого неприятеля;\n<b>2)</b> совсем недавно, только что (quem p. nominavi <i>C</i>);\n<b>3)</b> непосредственно за, тотчас же после (p. deos <i>C</i>; p. ab aliquo <i>C</i>; p. solis occasum <i>Pall</i>);\n<b>4)</b> весьма сходно, очень похоже (p. morem Romanum <i>L</i>): p. atque ille <i>C</i> почти так же, Как и он; quam p. transferre aliquid in Latinum <i>Q</i> как можно точнее перевести что-л. на латинский язык;\n<b>5)</b> прежде всего, во-первых (p..., mox..., tertio loco <i>PM</i>)."],
  ["proximi", "<big>proximi</big>\n\n<b>proximi, orum</b> <i>m</i> [proximus]\n<b>1)</b> ближайшие соседи <i>или</i> друзья <i>C</i>;\n<b>2)</b> ближайшие родственники, близкие <i>C</i>."],
  ["proximior", "<big>proximior</big>\n\n<b>proximior, ius</b> <i>Sen, MF compar. к</i>\nproximus."],
  ["proximitas", "<big>proximitas</big>\n\n<b>proximitas, atis</b> <i>f</i> [proximus]\n<b>1)</b> близость, близкое соседство <i>O, Vtr</i>;\n<b>2)</b> близкое родство <i>O, Q</i>;\n<b>3)</b> тесная связь <i>Ap</i>;\n<b>4)</b> сходство <i>O, Ap</i>."],
  ["proximo", "<big>proximo</big>\n\n<b>I proximo</b> [proximus I]\nсовсем недавно <i>C</i>.\n\n<b>II proximo, avi, atum, are</b> [proximus I]\nприближаться, близко подходить (alicui rei <i>Sol</i> <i>или</i> aliquid <i>Ap</i>): luce proximante <i>Ap</i> на рассвете."],
  ["proximum", "<big>proximum</big>\n\n<b>proximum, i</b> <i>n</i>\nнепосредственная близость, близкое соседство (in proximo urbis <i>L</i>; aquam de proximo rogare <i>Pl</i>)."],
  ["proximus", "<big>proximus</big>\n\n<b>I proximus, a, um</b> <i>adj. superl.</i> [prope]\n<b>1)</b> весьма близкий, ближайший (oppidum, via <i>Cs</i>): in proximo litore <i>Nep</i> у самого берега; p. alicui rei <i>C, L etc.</i> (<i>реже</i> aliquid <i>C, L etc.</i>) наиболее близкий к чему-л.; culpae p. <i>Ph</i> причастный к преступлению; p. cognatione <i>C</i> находящийся в теснейшем родстве; proximos honores occupare <i>H</i> занимать следующее по рангу место; p. a postremo <i>C</i> предпоследний;\n<b>2)</b> только что истекший, последний, предшествующий (nox <i>C</i> — <i>ср.</i> 3; bellum <i>L</i>); предыдущий, прежний (censor <i>C</i>; rex <i>L</i>);\n<b>3)</b> (непосредственно) следующий, ближайший (nox <i>Cs</i> — <i>ср.</i> 2; annus <i>C</i>): triduo proximo <i>L</i> в ближайшие три дня; die proximi <i>Cato ap. AG</i> (<i>арх.</i>) = die proximo;\n<b>4)</b> наиболее сходный, наиболее близкий (alicui <i>C, O, H etc.</i>): p. vero (veris) <i>O, L, H</i> самый правдоподобный;\n<b>5)</b> самый преданный, вернейший (religloni suae <i>C</i>; amore alicui p. <i>C</i>);\n<b>6)</b> совершенно ясный, очевидный, явный (signum <i>Ap</i>; argumentum <i>Q, Ap</i>).\n\n<b>II proximus, i</b> <i>m</i>\nпроксим, чиновник императорской канцелярии (<i>следующий по рангу за magister scrinii</i>) <i>CJ</i>."],
  ["proxum-", "<big>proxum-</big>\n\n<b>proxum-</b> <i>арх. v. l.</i>\n= proxim-."],
  ["prudens", "<big>prudens</big>\n\n<b>prudens, entis</b> <i>adj.</i> [<i>из</i> providens]\n<b>1)</b> сознательный: p. (et) sciens hoc feci <i>Ter, C etc.</i> я сделал это сознательно (умышленно);\n<b>2)</b> сведущий, знающий, опытный (p. rei militaris <i>Nep</i>): homo prudentissimus <i>Pt</i> тонкий знаток; juris p. <i>Dig</i> законовед;\n<b>3)</b> умный, разумный, благоразумный, рассудительный (consilium <i>C</i>; p. et acutus <i>C</i>);\n<b>4)</b> хитрый, ловкий, искусный (gens prudentissima adulandi <i>J</i>)."],
  ["prudenter", "<big>prudenter</big>\n\n<b>prudenter</b> [prudens]\nблагоразумно, рассудительно <i>C, Sl etc.</i>"],
  ["prudentia", "<big>prudentia</big>\n\n<b>prudentia, ae</b> <i>f</i> [prudens]\n<b>1)</b> знание, предвидение (futurorum <i>C</i>);\n<b>2)</b> наука (juris <i>C</i>; rei militaris <i>Nep</i>);\n<b>3)</b> благоразумие, рассудительность (p. cernitur in delectu bonorum et malorum <i>C</i>)."],
  ["Prudentius", "<big>Prudentius</big>\n\n<b>Prudentius, i</b> <i>m</i>\n(Aurelius P. Clemens) Пруденций, <i>христианско-латинский поэт IV в. н. э.</i>"],
  ["pruditas", "<big>pruditas</big>\n\n<b>prūditās, ātis</b> <i>f</i> <i>Vr</i>\n= prudentia."],
  ["pruina", "<big>pruina</big>\n\n<b>pruina, ae</b> <i>f</i>\n<b>1)</b> иней, изморозь (p. gignitur ex rore gelido <i>PM</i>);\n<b>2)</b> <i>поэт.</i> снег (glacies et cana p. <i>Pt</i>); зима (ad medias sementem extendere pruinas <i>V</i>)."],
  ["pruinosus", "<big>pruinosus</big>\n\n<b>pruinosus, a, um</b> [pruina]\n<b>1)</b> покрытый инеем (herbae <i>O</i>); полный инея (locus <i>Vtr</i>);\n<b>2)</b> негреющий, холодный (horrere pruinosis pannis <i>Pt</i>)."],
  ["pruna", "<big>pruna</big>\n\n<b>pruna, ae</b> <i>f</i>\nгорящий уголь, жар <i>V, H etc.</i>"],
  ["pruniceus", "<big>pruniceus</big>\n\n<b>pruniceus (-ius), a, um</b> [prunus]\nиз сливы (<i>дерева</i>) (torris <i>O</i>)."],
  ["prunulum", "<big>prunulum</big>\n\n<b>prunulum, i</b> <i>n</i> [<i>demin. к</i> prunum]\nмелкая слива <i>Fronto</i>."],
  ["prunum", "<big>prunum</big>\n\n<b>prunum, i</b> <i>n</i>\nслива (<i>плод</i>) <i>Cato, V, PM etc.</i>"],
  ["prunus", "<big>prunus</big>\n\n<b>prunus, i</b> <i>f</i>\nслива (<i>дерево</i>) <i>V, PM, Col</i>: p. silvestris <i>Col, PM</i> тёрн (<i>Prunus spinosa</i>)."],
  ["pruriginosus", "<big>pruriginosus</big>\n\n<b>pruriginosus, a, um</b> [prurigo]\nпораженный чесоткой (зудом) <i>Dig</i>."],
  ["prurigo", "<big>prurigo</big>\n\n<b>prurigo, inis</b> <i>f</i> [prurio]\n<b>1)</b> зуд <i>CC</i>; кожная болезнь, вызывающая зуд <i>CC, PM</i>;\n<b>2)</b> похоть <i>M, Aus</i>."],
  ["prurio", "<big>prurio</big>\n\n<b>prurio, —, —, ire</b>\n<b>1)</b> чесаться, зудеть (angulus ocelli prurit <i>J</i>); <i>перен.</i> ждать побоев (malae an dentes tibi pruriunt? <i>Pl</i>); бояться побоев (dorsus totus prurit <i>Pl</i>);\n<b>2)</b> быть похотливым <i>Pl, Ctl, J, M</i>;\n<b>3)</b> испытывать непреодолимое желание, рваться (in pugnam <i>M</i>)."],
  ["pruriosus", "<big>pruriosus</big>\n\n<b>pruriosus, a, um</b> [prurio]\n<b>1)</b> вызывающий зуд (bulbus <i>CA</i>);\n<b>2)</b> похотливый (puella <i>Priap</i>)."],
  ["pruritivus", "<big>pruritivus</big>\n\n<b>pruritivus, a, um</b> [prurio]\nпричиняющий зуд (ulcera <i>PM</i>)."],
  ["pruritus", "<big>pruritus</big>\n\n<b>pruritus, us</b> <i>m</i> [prurio]\nзуд <i>PM</i>."],
  ["Prusa", "<big>Prusa</big>\n\n<b>Prusa, ae</b> <i>f</i>\nПруса, <i>город в зап. Вифинии, на сев. склоне Олимпа Мисийского</i> (<i>ныне</i> Брусса) <i>PM, PJ</i>."],
  ["Prusia", "<big>Prusia</big>\n\n<b>Prusia, ae</b> <i>m</i> <i>L</i>\n=» Prusias."],
  ["Prusiacus", "<big>Prusiacus</big>\n\n<b>Prusiacus, a, um</b> [Prusias]\nпрусиев (ora <i>Sil</i>)."],
  ["Prusias", "<big>Prusias</big>\n\n<b>Prusias, ae</b> <i>m</i>\nПрусий, <i>имя двух царей Вифинии, из которых один царствовал в 236-186 гг. до н. э., а другой (отец Никомеда) в 186-149 гг. до н. э.</i> <i>C, Nep, L</i>."],
  ["prymnesius", "<big>prymnesius</big>\n\n<b>prymnesius, a, um</b> (<i>греч.</i>)\nкормовой, находящийся в кормовой части судна, <i>т. е.</i> причальный (palus <i>LM</i>; funis <i>Is</i>)."],
  ["prytaneum", "<big>prytaneum</big>\n\n<b>prytaneum (prytaneium), i</b> <i>n</i> (<i>греч.</i>)\nпританей, <i>городское управление в греч. городах, преим. в Афинах, где заседали притоны и где особо заслуженные граждане получали питание на государственный счёт</i> <i>C, L, PM</i>."],
  ["prytanis", "<big>prytanis</big>\n\n<b>prytanis, is</b> (<i>acc.</i> in) <i>m</i> (<i>греч.</i>)\nпритан, <i>один из носителей высшей государственной власти в некоторых греч. городах</i> <i>Sen, L</i>."],
  ["psallo", "<big>psallo</big>\n\n<b>psallo, psalli, —, ere</b> (<i>греч.</i>)\n<b>1)</b> играть на струнном инструменте (<i>преим.</i> на кифаре) <i>Sl, H, Su etc.</i>: psallentis gestu <i>Ap</i> (привычным) жестом музыканта; psallendi doctissimus <i>Ap</i> замечательный кифарист;\n<b>2)</b> петь <i>или</i> читать нараспев псалмы <i>Hier</i>."],
  ["psalma", "<big>psalma</big>\n\n<b>psalma, atis</b> <i>n</i> (<i>греч.</i>)\nдуховная песнь, псалом <i>Eccl</i>."],
  ["psalmicen", "<big>psalmicen</big>\n\n<b>psalmi–cen, cinis</b> <i>m</i> [cano]\nпсалмопевец <i>Sid</i>."],
  ["psalmista", "<big>psalmista</big>\n\n<b>psalmista, ae</b> <i>m</i>\nпсалмопевец, сочинитель псалмов <i>Eccl</i>."],
  ["psalmodia", "<big>psalmodia</big>\n\n<b>psalmodia, ae</b> <i>f</i> (<i>греч.</i>)\nпсалмодия, чтение псалмов <i>Eccl</i>."],
  ["psalmographus", "<big>psalmographus</big>\n\n<b>psalmographus, i</b> <i>m</i> <i>Tert</i>\n= psalmista."],
  ["psalmus", "<big>psalmus</big>\n\n<b>psalmus, i</b> <i>m</i> (<i>греч.</i>)\nпсалом <i>Lact</i>."],
  ["psalterium", "<big>psalterium</big>\n\n<b>psalterium, i</b> <i>n</i> (<i>греч.</i>)\n<b>1)</b> псалтерий, <i>струнный инструмент</i> <i>Vr, C, V etc.</i>;\n<b>2)</b> псалтырь (Давида) <i>Hier etc.</i>"],
  ["psaltes", "<big>psaltes</big>\n\n<b>psaltes, ae</b> <i>m</i> (<i>греч.</i>)\nиграющий на струнном инструменте (<i>преим.</i> на кифаре) <i>Q, Sid</i>."],
  ["psaltria", "<big>psaltria</big>\n\n<b>psaltria, ae</b> <i>f</i> (<i>греч.</i>)\nмузыкантша <i>Ter, C, J, Macr</i>."],
  ["Psamathe", "<big>Psamathe</big>\n\n<b>Psamathe, es</b> <i>f</i>\nПсамата:\n<b>1)</b> <i>дочь Нерея и Дориды, мать Фока</i> <i>O</i>;\n<b>2)</b> <i>дочь аргивского царя Кротопа, мать Лина</i> <i>O</i>."],
  ["Psammathus", "<big>Psammathus</big>\n\n<b>Psammathus, untis</b> <i>f</i>\nПсамматунт, <i>портовый город в Лаконии</i> <i>PM</i>."],
  ["Psecas", "<big>Psecas</big>\n\n<b>I Psecas, adis</b> <i>f</i> (<i>греч.</i> «кропительница», <i>т. е.</i> «окропляющая благовониями»)\nПсекада, <i>имя одной из спутниц Дианы</i> <i>O</i>.\n\n<b>II psecas, adis</b> <i>f</i> (<i>греч.</i>)\nпсекада, <i>рабыня, кропившая благовониями волосы своей госпожи, т. е.</i> парикмахерша <i>C, J</i>."],
  ["psephisma", "<big>psephisma</big>\n\n<b>psephisma, atis</b> <i>n</i> (<i>греч.</i>; <i>лат.</i> plebiscitum)\n<b>1)</b> постановление народного собрания (<i>у греков</i>)<i><i>C</i>;</i>\n2)(<i>в импират. Риме</i>) благодарственный адрес греч. общины императору <i>PJ</i>."],
  ["pseudo-", "<big>pseudo-</big>\n\n<b>pseudo-</b> (<i>греч.</i>)\n<i>приставка со значением</i> лже-, мнимый кажущийся."],
  ["Pseudocato", "<big>Pseudocato</big>\n\n<b>Pseudocato, onis</b> <i>m</i>\nЛжекатон <i>C</i>."],
  ["Pseudolus", "<big>Pseudolus</big>\n\n<b>Pseudolus, i</b> <i>m</i> (<i>греч.</i> «лжец», «обманщик»)\nПсевдол, <i>название одной из комедий Плавта.</i>"],
  ["pseudomenos", "<big>pseudomenos</big>\n\n<b>pseudomenos, i</b> <i>m</i> (<i>греч.</i>; <i>лат.</i> mentiens)\nсофизм «лжец» (<i>критянин говорит, что все критяне лжецы, следовательно, и он лжец; если же он лжец, то критяне не лжецы; следовательно, и он, как критянин, не лжец, а потому говорит правду и т. д.</i>) <i>C</i>."],
  ["Pseudophilippus", "<big>Pseudophilippus</big>\n\n<b>Pseudophilippus, i</b> <i>m</i>\nЛжефилипп, <i>т. е.</i> Андриск (<i>выдававший себя за Филиппа, сына Персея Македонского</i>) <i>C</i>."],
  ["pseudopropheta", "<big>pseudopropheta</big>\n\n<b>pseudo–propheta, ae</b> <i>m</i> (<i>греч.</i>)\nлжепророк <i>Tert</i>."],
  ["pseudothyrum", "<big>pseudothyrum</big>\n\n<b>pseudothyrum, i</b> <i>n</i> (<i>греч.</i>)\nпотайная дверь <i>Amm</i>: per p. <i>погов. C</i> тайно, тайком."],
  ["psila", "<big>psila</big>\n\n<b>psila, ae</b> <i>f</i> (<i>греч.</i>)\nпокрывало, покрытое ворсом лишь с одной стороны <i>LM</i>."],
  ["psilocitharistes", "<big>psilocitharistes</big>\n\n<b>psilocitharistes, ae</b> <i>m</i> (<i>греч.</i>)\nпсилокифарист, <i>тк. играющий на кифаре, но не поющий</i> <i>Su</i>."],
  ["psilothrum", "<big>psilothrum</big>\n\n<b>psilothrum, i</b> <i>n</i> (<i>греч.</i>)\nмазь для удаления волос <i>Sen, M, Lampr</i>."],
  ["psithius", "<big>psithius</big>\n\n<b>psithius, a, um</b> (<i>греч.</i>)\nпситийский: psithia (vitis) <i>V, Col, St</i> пситийский виноград (<i>сорт греч. винограда</i>)."],
  ["psittacinus", "<big>psittacinus</big>\n\n<b>psittacinus, a, um</b> [psittacus]\nяркий <i>или</i> пёстрый как попугай (collyrium <i>Scr</i>)."],
  ["psittacus", "<big>psittacus</big>\n\n<b>psittacus, i</b> <i>m</i> (<i>греч.</i>)\nпопугай <i>PM, PJ, Ap etc.</i>"],
  ["Psophis", "<big>Psophis</big>\n\n<b>Psophis, idis</b> <i>f</i>\nПсофида, <i>город в сев.-зап. Аркадии, к зап. от Эриманта</i> <i>PM, O etc.</i>"],
  ["Psophodees", "<big>Psophodees</big>\n\n<b>Psophodees, is</b> <i>m</i> (<i>греч.</i> «застенчивый», «робкий»)\n<i>название одной из комедий Менандра</i> <i>Q</i>."],
  ["Psyche", "<big>Psyche</big>\n\n<b>Psyche, es</b> <i>f</i>\nПсихея, <i>возлюбленная Купидона, которой Юпитер даровал бессмертие</i> <i>Ap</i>."],
  ["psychomantium", "<big>psychomantium</big>\n\n<b>psychomantium, i</b> <i>n</i> (<i>греч.</i>)\nместо, где вызывали души умерших <i>C</i>; <i>тж.</i> вызывание душ из царства теней <i>C</i>."],
  ["psychrolusia", "<big>psychrolusia</big>\n\n<b>psychrolusia, ae</b> <i>f</i> (<i>греч.</i>)\nкупание в холодной воде <i>CA</i>."],
  ["psychrolutes", "<big>psychrolutes</big>\n\n<b>psychrolutes, ae</b> (<i>acc.</i> em) <i>m</i> (<i>греч.</i>)\nкупающийся в холодной воде <i>Sen</i>."],
  ["Psylli", "<big>Psylli</big>\n\n<b>Psylli, orum</b> <i>m</i>\nпсиллы, <i>африканское племя к юго-зап. от Большого Сирта, славившееся искусством заклинания змей и лечения от их укусов</i> <i>PM, CC, Su</i>."],
  ["psythia", "<big>psythia</big>\n\n<b>psythia, ae</b> <i>f</i> <i>v. l.</i>\n= psithia (<i>см.</i> psithius)."],
  ["ptarmicum", "<big>ptarmicum</big>\n\n<b>ptarmicum, i</b> <i>n</i> (<i>греч.</i>)\nчихательное средство <i>CA</i>."],
  ["Pteleum", "<big>Pteleum</big>\n\n<b>Pteleum</b> <i>и</i> <b>Pteleon, i</b> <i>n</i>\nПтелей, <i>портовый город в Фессалии, на юго-зап. берегу Пегасейского залива</i> <i>L, Mela</i>."],
  ["Pterelas", "<big>Pterelas</big>\n\n<b>Pterelas, ae</b> <i>m</i>\nПтерел:\n<b>1)</b> <i>миф. царь о-ва Taphos</i> <i>O</i>;\n<b>2)</b> <i>кличка одной из собак Актеона</i> <i>O</i>."],
  ["pteroma", "<big>pteroma</big>\n\n<b>pteroma, atis</b> <i>n</i> (<i>греч.</i>) <i>архит.</i>\nкрыло строения <i>Vtr</i>."],
  ["ptisana", "<big>ptisana</big>\n\n<b>ptisana, ae</b> <i>f</i> (<i>греч.</i>)\n<b>1)</b> ячменная каша <i>CC, M</i>;\n<b>2)</b> ячменный отвар <i>Vr, PM</i>."],
  ["ptisanarium", "<big>ptisanarium</big>\n\n<b>ptisanarium, i</b> <i>n</i> [ptisana]\nячменный отвар: p. oryzae <i>H</i> рисовый отвар."],
  ["ptocheum", "<big>ptocheum</big>\n\n<b>ptocheum (-ium), i</b> <i>n</i> <i>и</i> <b>ptochotrophium, i</b> <i>n</i> (<i>греч.</i>)\nприют для бедных <i>CJ</i>."],
  ["Ptolemaeum", "<big>Ptolemaeum</big>\n\n<b>Ptolemaeum, ī</b> <i>n</i>\nгробница Птолемеев <i>Su</i>."],
  ["Ptolemaeus", "<big>Ptolemaeus</big>\n\n<b>I Ptolemaeus, ī</b> <i>m</i>\nПтолемей:\n<b>1)</b> <i>имя македонско-греч. царей Египта, царствовавших в 323-30 гг. до н. э.</i> (<i>с Птолемея Сотера</i>) <i>QC, C etc.</i>;\n<b>2)</b> <i>звездочёт времён императора Отона</i> <i>T</i>.\n\n<b>II Ptolemaeus, a, um</b> [Ptolemaeus]\nптолемеев, <i>перен.</i> египетский (gymnasium <i>C</i>; Pharos <i>Prp</i>)."],
  ["Ptolemais", "<big>Ptolemais</big>\n\n<b>I Ptolemāis, idis</b> <i>f</i>\nПтолемаида:\n<b>1)</b> <i>название ряда городов в Египте и Финикии</i> <i>C, PM, Mela etc.</i>;\n<b>2)</b> <i>поэт. Lcn</i> = Cleopatra (<i>как дочь Птолемея Авлета</i>).\n\n<b>II Ptolemāis, idis (idos)</b> <i>adj. f</i>\nптолемеева, <i>перен.</i> египетская (aula <i>Aus</i>; gaza <i>Sid</i>)."],
  ["Ptolemocratia", "<big>Ptolemocratia</big>\n\n<b>Ptolemocratia, ae</b> <i>f</i>\nПтолемократия, <i>греч. женское имя</i> <i>Pl</i>."],
  ["Ptolom-", "<big>Ptolom-</big>\n\n<b>Ptolom-</b> <i>v. l.</i>\n= Ptolem-."],
  ["pubens", "<big>pubens</big>\n\n<b>pubens, entis</b> <i>adj.</i> [pubesco]\nздоровый, крепкий, цветущий (herbae <i>V</i>; fratres <i>Cld</i>; gramina, anni <i>Aus</i>; rosae <i>St</i>)."],
  ["puber", "<big>puber</big>\n\n<b>puber, eris</b> <i>v. l.</i>\n= pubes I <i>и</i> II."],
  ["pubertas", "<big>pubertas</big>\n\n<b>pubertas, itis</b> <i>f</i> [pubes I]\n<b>1)</b> возмужалость, половая зрелость, зрелый возраст (pubertatis tempus <i>Su</i> <i>и</i> anni <i>Just</i>);\n<b>2)</b> мужская сила (p. inexhausta <i>T</i>);\n<b>3)</b> взрослая молодёжь <i>VM</i>;\n<b>4)</b> первая растительность на лице, юношеский пушок <i>C, PM</i>;\n<b>5)</b> пушок на растениях (incipientis uvae <i>PM</i>)."],
  ["pubes", "<big>pubes</big>\n\n<b>I pubes, eris</b>\n<b>1.</b> <i>adj.</i>\n<b>1)</b> возмужалый, зрелый, взрослый (homo <i>C, Nep</i>; aetas <i>L</i>);\n<b>2)</b> разросшийся, сочный, пышный (folia, herba <i>L</i>);\n<b>2.</b> <i>subst. m</i> юноша (достигший зрелости) <i>Cs, Sl, Ap</i>."],
  ["pubesco", "<big>pubesco</big>\n\n<b>pubesco, pubui, —, ere</b> [puber]\n<b>1)</b> (воз)мужать, созревать, достигать совершеннолетия, становиться мужчиной <i>Lcr, C etc.</i>: pubescentibus annis <i>Pt</i> во цвете лет;\n<b>2)</b> подрастать, поспевать, созревать (omnia florent et pubescunt <i>C</i>);\n<b>3)</b> обрастать волосами (malae pubescunt <i>VF</i>); покрываться растительностью (prata pubescunt flore <i>O</i>);\n<b>4)</b> набирать силу, крепнуть: pubescente vere <i>Amm</i> с наступлением весны;\n<b>5)</b> оживать, воскресать: proprio p. leto <i>Cld</i> возрождаться силой самой смерти (<i>о Фениксе</i>)."],
  ["pubis", "<big>pubis</big>\n\n<b>pubis, is</b> <i>f</i> <i>Eccl</i>\n= pubes II."],
  ["pubis", "<big>pubis</big>\n\n<b>II pubis, is</b> <i>f</i>\n<b>1)</b> возмужалость <i>CC, PM</i>; зрелость, спелость (seminum <i>Amm</i>);\n<b>2)</b> растительность на лице, борода (capillus et p. <i>PM</i>);\n<b>3)</b> растительность на лобке, <i>перен.</i> поясничная область <i>O, Ap</i>: pube tenus <i>V</i> до пояса;\n<b>4)</b> взрослые люди, мужчины, народ (p. Dardana <i>V</i>; p. rustica <i>Tib</i>)."],
  ["Publianus", "<big>Publianus</big>\n\n<b>Publianus, a, um</b> [Publius]\nпублиев <i>Sen</i> (<i>v. l.</i>)."],
  ["publica", "<big>publica</big>\n\n<b>publica, ae</b> <i>f</i>\nпубличная женщина <i>Sen</i>."],
  ["publicanus", "<big>publicanus</big>\n\n<b>I publicanus, a, um</b> [publicum]\n<b>1)</b> относящийся к государственным доходам, финансовый (servi <i>C</i> — <i>v. l.</i>);\n<b>2)</b> принадлежащий откупщику: muliercula publicana <i>C</i> жена откупщика.\n\n<b>II publicanus, i</b> <i>m</i>\nоткупщик государственных доходов (<i>преим.</i> налогов) <i>C, L, Dig etc.</i>"],
  ["publicatio", "<big>publicatio</big>\n\n<b>publicatio, onis</b> <i>f</i> [publico]\nобращение в доход казны, конфискация (bonorum <i>C</i>)."],
  ["publicator", "<big>publicator</big>\n\n<b>publicator, oris</b> <i>m</i>\nразгласитель (occulti <i>Sid</i>)."],
  ["publice", "<big>publice</big>\n\n<b>publice</b> [publicus]\n<b>1)</b> официально (litteras mittere <i>Nep</i>); от имени (по поручению <i>или</i> приказу) государства (templum p. dedicare <i>L</i>): p. privatimque petere aliquid <i>Cs</i> просить о чём-л. от имени государства и от себя лично;\n<b>2)</b> в интересах государства, для общественной пользы, с национальной точки зрения: p. maximam putant esse laudem, quam latissime a suis finibus vacare agros <i>Cs</i> (свевы) видят величайшую национальную пользу в том, чтобы на возможно большем от их границ расстоянии поли оставались незаселёнными;\n<b>3)</b> на государственный счёт (alere aliquem <i>Nep</i>);\n<b>4)</b> сообща, вместе, всенародно (exsulatum ire <i>L</i>);\n<b>5)</b> публично, на виду у всех (disserere <i>AG, Ap</i>)."],
  ["Publicianus", "<big>Publicianus</big>\n\n<b>Publicianus, a, um</b> [Publicius]\nпублициев <i>C</i>."],
  ["publicitus", "<big>publicitus</big>\n\n<b>publicitus</b> <i>adv.</i>\n<b>1)</b> в интересах государства <i>или</i> общества, с общественной точки зрения (peccata, quae prohiberi p. interest <i>AG</i>);\n<b>2)</b> на государственный (общественный) счёт (p. ornari scutisque feroque ferro <i>Enn ap. AG</i>);\n<b>3)</b> публично, всенародно, открыто (auctio fit p. <i>Pl</i>);\n<b>4)</b> в общественном порядке (non privatim, sed p. <i>Ap</i>);\n<b>5)</b> в глазах общественного мнения (p. perinfamis est <i>Ap</i>)."],
  ["Publicius", "<big>Publicius</big>\n\n<b>Publicius, a, um</b>\nПублиций, <i>римск. nomen</i>: clivus P. <i>L</i> Публициев склон (<i>улица в Риме, которая вела на Авентинский холм и названная так по двум эдилам, братьям Публициям</i>)."],
  ["publico", "<big>publico</big>\n\n<b>publico, avi, atum, are</b> [publicus]\n<b>1)</b> сделать общим достоянием, предоставить всем, открыть для всех (Aventinum <i>L</i>; bibliothecas <i>Su</i>): sermonibus publicatis operam dare <i>Pt</i> начать общую беседу; p. corpus suum vulgo <i>Pl</i> <i>и</i> p. pudicitiam <i>T</i> предаваться разврату; se p. <i>Su</i> выступать публично;\n<b>2)</b> объявить всенародно, обнародовать, опубликовать <i>PJ</i>; выпустить в свет, издать (orationem, libellos <i>Su</i>); разглашать (reticenda <i>Just</i>);\n<b>3)</b> отбирать в казну, обращать в доход государства, конфисковать (pecuniam <i>L</i>; bona <i>Cs, L</i>): p. aliquem <i>C</i> конфисковать чьё-л. имущество."],
  ["Publicola", "<big>Publicola</big>\n\n<b>Publicola, ae</b> <i>m</i> [populus + colo]\n(«друг народа») Публикола, <i>cognomen в роде Валериев</i> <i>C, L</i>."],
  ["publicum", "<big>publicum</big>\n\n<b>publicum, i</b> <i>n</i>\n<b>1)</b> государственное имущество, государственные угодья (земли) (p. Campanum <i>C</i>);\n<b>2)</b> государственные финансы, казна (pecuniam ex publico tradere <i>Cs</i>): in p. emere <i>L</i> покупать на государственный счёт; aliquid in p. addicere <i>Cs</i> <i>или</i> redigere <i>L</i> отобрать что-л. в казну; de publico <i>C</i> на государственный счёт;\n<b>3)</b> государственные доходы: conducere publica <i>H</i> брать на откуп государственные доходы; p. habere <i>Pl</i> быть откупщиком; societates publicorum <i>C</i> общества по откупу государственных доходов;\n<b>4)</b> государственный склад, общественный магазин (frumentum conferre in p. <i>Cs</i>);\n<b>5)</b> государство, общество: aquae in publico manant <i>Pt</i> вода является общественной собственностью; consulere in p. <i>PJ</i> заботиться об общественном благе || общественность, общественная жизнь, общественное место: in publico <i>PJ, C</i>, <i>тж.</i> in publicum <i>Pt</i> в общественном месте, <i>т. е.</i> вне дома; in p. prosilire <i>Pt</i> броситься на улицу; fugere in p. <i>Pt</i> удирать, давать тягу; publico carere <i>C</i> (abstinere <i>T, Su</i>) жить замкнуто, вести уединённую жизнь;\n<b>6)</b> народные массы, народ (nummos in publico effundere <i>Pt</i>);\n<b>7)</b> государственный архив (ut scriptum in publico exstat <i>Vr</i>)."],
  ["publicus", "<big>publicus</big>\n\n<b>publicus, a, um</b> [<i>одного корня с</i> populus I]\n<b>1)</b> общественный, государственный (agri <i>C etc.</i>; pecunia <i>Nep</i>); всенародный, национальный (dii <i>Nep</i>): mensa publica <i>C</i> государственный банк; sumptu publico <i>C</i> на государственный счёт; funus publicum <i>PJ, T</i> похороны на счёт государства, <i>но тж.</i> <i>Su</i> всенародные похороны с объявлением государственного траура; judicium publicum <i>C</i> государственный процесс (<i>т. е.</i> в связи с нарушением государственных законов);\n<b>2)</b> официальный (litterae, tabulae <i>C</i>);\n<b>3)</b> общеупотребительный, народный (verba <i>C</i>; mores <i>QC</i>); обычный, заурядный (structura carminum <i>O</i>);\n<b>4)</b>: res publica (respublica) государственные дела, политика (in re publica versari <i>C</i>; aetatem procul a re publica agere <i>Sl</i>); государственное имущество, казна (rem publicam conficere <i>C</i>); государственное благо, интересы общества (rei publicae causa <i>C</i>); государство (rem publicam defendere <i>C</i> <i>и</i> administrare <i>Sl</i>); политическое положение (in optima re publica <i>C</i>). — <i>См. тж.</i> publicum <i>и</i> publica."],
  ["Publilius", "<big>Publilius</big>\n\n<b>Publilius, a, um</b>\nПублилий, <i>римск. nomen</i>; P. Syrus — <i>см.</i> Syrus II, 3."],
  ["Publius", "<big>Publius</big>\n\n<b>Publius, i</b> <i>m</i>\nПублий, <i>римск. praenomen</i> (<i>в сокр.</i>— P.)."],
  ["pubui", "<big>pubui</big>\n\n<b>pubui</b> <i>pf. к</i>\npubesco."],
  ["pudefactus", "<big>pudefactus</big>\n\n<b>pudefactus, a, um</b> [pudeo + facio]\nустыдившийся, стыдящийся (aliqua re <i>AG</i>)."],
  ["pudenda", "<big>pudenda</big>\n\n<b>pudenda, orum</b> <i>n</i> [pudendus]\n<b>1)</b> непристойности <i>O, Q</i>;\n<b>2)</b> половые органы <i>Sen, Lampr, Aug</i>;\n<b>3)</b> <i>MF</i> = clunes."],
  ["pudendus", "<big>pudendus</big>\n\n<b>pudendus, a, um</b> [pudeo]\n<b>1)</b> постыдный, позорный (vita <i>O</i>);\n<b>2)</b> половой (pars <i>O</i>)."],
  ["pudens", "<big>pudens</big>\n\n<b>pudens, entis</b> <i>adj.</i> [pudeo]\n<b>1)</b> стыдливый, застенчивый, скромный (femina, vir <i>C</i>); благопристойный, благовидный (impudentiae suae exitum pudentem quaerere <i>C</i>): veniam petere pudentem <i>Ctl</i> смиренно просить снисхождения;\n<b>2)</b> почтенный, честный (viri <i>C</i>)."],
  ["pudenter", "<big>pudenter</big>\n\n<b>pudenter</b> [pudens]\nстыдливо, скромно, смиренно <i>C, H etc.</i>"],
  ["pudeo", "<big>pudeo</big>\n\n<b>pudeo, dui, ditum, ere</b>\n<b>1)</b> стыдиться <i>Pl, C</i>;\n<b>2)</b> (при)стыдить, преисполнить стыдом (non te haec pudent? <i>Ter</i>);\n<b>3)</b> <i>преим. impers.</i> pudet, puduit <i>или</i> puditum est, pudere: aliquem alicujus rei pudet <i>Pl, C etc.</i> кто-л. стыдится чего-л.; Apollinem tam humilis victoriae puditum est <i>Ap</i> Аполлону было стыдно за столь жалкую победу (над Марсием); aliquem alicujus pudet <i>Ter, L etc.</i> кому-л. стыдно перед кем-л.; pudet dicere <i>Ap</i> (<i>вводно</i>) совестно сказать. — <i>См. тж.</i> pudendus <i>и</i> pudens."],
  ["pudescit", "<big>pudescit</big>\n\n<b>pudescit, —, —, ere</b> <i>MF</i>\n= pudet."],
  ["pudet", "<big>pudet</big>\n\n<b>pudet</b> — <i>см.</i>\npudeo 3."],
  ["pudibilis", "<big>pudibilis</big>\n\n<b>pudibilis, e</b> <i>Lampr</i>\n= pudendus."],
  ["pudibundus", "<big>pudibundus</big>\n\n<b>pudibundus, a, um</b> [pudet]\n<b>1)</b> стыдящийся, застенчивый (matrona <i>H</i>; vir <i>Just</i>): pudibunda dies <i>St</i> устыдившийся (при виде позорного зрелища) день;\n<b>2)</b> постыдный, позорный (exitia <i>VF</i>; sordidus atque p. <i>Just</i>)."],
  ["pudice", "<big>pudice</big>\n\n<b>pudice</b> [pudicus]\nстыдливо, целомудренно, скромно <i>Pl, Ter, Ctl etc.</i>"],
  ["pudicitia", "<big>pudicitia</big>\n\n<b>pudicitia, ae</b> <i>f</i> [pudicus]\nстыдливость, целомудренность, скромность <i>Pl, V, C, Sl etc.</i>"],
  ["pudicus", "<big>pudicus</big>\n\n<b>pudicus, a, um</b> [pudet]\nстыдливый, целомудренный, скромный <i>Pl, C, O etc.</i>"],
  ["pudor", "<big>pudor</big>\n\n<b>pudor, oris</b> <i>m</i> [pudeo]\n<b>1)</b> стыд, стыдливость: p. alicujus rei <i>C, H, J</i> стыд за что-л.; p. alicujus <i>Enn</i> стыд перед кем-л.; pudori est narrare <i>O</i> стыдно рассказывать; ecqui p. est? <i>C</i> есть ли у тебя чувство стыда?;\n<b>2)</b> почтительность, уважение: p. patris <i>Ter</i> почтение к отцу;\n<b>3)</b> совестливость, чувство чести (homo summo pudore <i>C</i>): veterem pudorem sibi imponere <i>Pt</i> исполнять свои обязанности с прежней добросовестностью; ad coercenda peccata plus p., quam timor, valuit <i>Ap</i> от правонарушений удерживала скорее совесть, чем страх;\n<b>4)</b> благопристойность (pudor retinetur alicui rei <i>T</i>); целомудрие (pudorem alicui auferre <i>O</i>);\n<b>5)</b> честь (p. defuncti <i>PJ</i>);\n<b>6)</b> причина стыда, <i>т. е.</i> позорное пятно, позор (pudore non caret culpa <i>O</i>; p. evulgatus <i>T</i>);\n<b>7)</b> румянец, краска стыда <i>O, Cld</i>;\n<b>8)</b> <i>Eccl</i> = pudenda 2 и 3."],
  ["pudoratus", "<big>pudoratus</big>\n\n<b>pudoratus, a, um</b> <i>Vlg</i>\n= pudicus."],
  ["pudoricolor", "<big>pudoricolor</big>\n\n<b>pudoricolor, oris</b> <i>adj.</i> [pudor + color]\nпокрытый румянцем, румяный (Aurora <i>AG</i>)."],
  ["puduit", "<big>puduit</big>\n\n<b>puduit</b> <i>pf. к</i>\npudet."],
  ["puella", "<big>puella</big>\n\n<b>puella, ae</b> <i>f</i> [puellus]\n<b>1)</b> девушка <i>Pl, Ter, C etc.</i>;\n<b>2)</b> дочь (Danai puellae <i>H</i>);\n<b>3)</b> молодая женщина <i>O</i>; молодая жена (laborantes utero puellae <i>H</i>)."],
  ["puellaris", "<big>puellaris</big>\n\n<b>puellaris, e</b> [puella]\nдевический, девичий <i>O, Sen, T etc.</i>"],
  ["puellariter", "<big>puellariter</big>\n\n<b>puellariter</b> [puellaris]\nпо-девичьи <i>или</i> по-детски <i>PJ</i>."],
  ["puellarius", "<big>puellarius</big>\n\n<b>puellarius, i</b> <i>m</i> [puellus <i>или</i> puella]\n(<i>sensu obsceno</i>) любитель маленьких детей <i>Pt</i>."],
  ["puellasco", "<big>puellasco</big>\n\n<b>puellasco, —, —, ere</b> [puella]\nпревращаться в девушку <i>Vr</i>."],
  ["puellatorius", "<big>puellatorius</big>\n\n<b>puellatorius, a, um</b> [puella]\nдетский, с детским голоском, <i>т. е.</i> очень высокого регистра (tibiae <i>Sol</i>)."],
  ["puellula", "<big>puellula</big>\n\n<b>puellula, ae</b> <i>f</i> [<i>demin. к</i> puella]\nдевчоночка <i>Ter, Ctl</i>."],
  ["puellus", "<big>puellus</big>\n\n<b>puellus, i</b> <i>m</i> [<i>demin. к</i> puer]\nмальчишечка <i>или</i> ребёночек <i>LM, Enn, Lcr</i>."],
  ["puelus", "<big>puelus</big>\n\n<b>puelus, i</b> <i>f</i> (<i>греч.</i>)\nванна <i>или</i> бассейн для купания <i>Pl</i>."],
  ["puer", "<big>puer</big>\n\n<b>puer, erī</b> (<i>у</i> <i>Pl gen. pl.</i> um, <i>voc. sg.</i> puere) <i>m</i>, <i>редко</i> <i>f</i>\n<b>1)</b> ребёнок, дитя (vir puerique <i>H</i>); <i>pl.</i> дети (cum pueris et conjuge <i>H</i>): a puero (a pueris) <i>C etc.</i> с детства; nobis pueris <i>C</i> во время нашего детства; se puero <i>H</i> когда он был ребёнком; ex pueris excedere <i>C</i> выйти из детского возраста;\n<b>2)</b> мальчик (<i>преим. от 7 до 16 л</i>) (pueri atque puellae <i>H</i>);\n<b>3)</b> девочка (pueri appellatione etiam puella significatur <i>Dig</i>; Proserpina p. <i>Naev</i>);\n<b>4)</b> юноша (p. egregius <i>C</i>): p. discens <i>Pt</i> ученик, подмастерье;\n<b>5)</b> сын (Latonae p. <i>H</i>);\n<b>6)</b> молодой слуга, раб (da, p., pocula! <i>H</i>);\n<b>7)</b> холостяк (semper fac p. esse velis <i>O</i>)."],
  ["puera", "<big>puera</big>\n\n<b>puera, ae</b> <i>f</i>\nдевочка <i>LA, Vr, Su</i>."],
  ["puerasco", "<big>puerasco</big>\n\n<b>puerāsco, —, —, ere</b>\n<b>1)</b> вступать в детский <i>или</i> отроческий возраст <i>Su, Aug</i>;\n<b>2)</b> молодеть <i>Aus</i>."],
  ["puerculus", "<big>puerculus</big>\n\n<b>puerculus, ī</b> <i>m</i> [puer]\nребёночек, младенец <i>Eccl, Ap</i>."],
  ["puerilis", "<big>puerilis</big>\n\n<b>puerīlis, e</b> [puer]\n<b>1)</b> детский, отроческий (aetas <i>C</i>; anni <i>O, PM</i>);\n<b>2)</b> детский, ребяческий (consilium <i>C</i>; vota <i>O</i>)."],
  ["puerilitas", "<big>puerilitas</big>\n\n<b>puerīlitās, ātis</b> <i>f</i> [puerilis]\n<b>1)</b> детство, младенчество, отрочество <i>Vr, VM</i>;\n<b>2)</b> ребяческое поведение, ребячество (non pueritia, sed, quod gravius est, p. <i>Sen</i>)."],
  ["pueriliter", "<big>pueriliter</big>\n\n<b>puerīliter</b> [puerilis]\nпо-детски, ребячески <i>C, L, T</i>."],
  ["pueritas", "<big>pueritas</big>\n\n<b>pueritās, ātis</b> <i>f</i> <i>Tert</i> <i>и</i> <b>pueritiēs, ēī</b>  <i>f Aus</i>\n= pueritia."],
  ["pueritia", "<big>pueritia</big>\n\n<b>pueritia, ae</b> <i>f</i> [puer]\nдетство <i>или</i> отрочество (<i>преим. до 16-17 л.</i>) <i>Ter, C etc.</i>"],
  ["puerities", "<big>puerities</big>\n\n<b>pueritiēs</b> <i>v. l.</i>\n= pueritia."],
  ["puerpera", "<big>puerpera</big>\n\n<b>puerpera, ae</b> <i>f</i> [puer + pario]\nроженица, родильница <i>Pl, Ter, H etc.</i>"],
  ["puerperium", "<big>puerperium</big>\n\n<b>puerperium, ī</b> <i>n</i> [puer + pario]\n<b>1)</b> роды, рождение <i>Pl, T, Su etc.</i>;\n<b>2)</b> новорождённый, ребёнок, плод <i>PM, Sen, T etc.</i>"],
  ["puerperus", "<big>puerperus</big>\n\n<b>puerperus, a, um</b> [puerpera]\nродильный: puerpera uxor <i>Sen</i> жена-роженица; puerpera verba <i>O</i> заклинания для облегчения родов."],
  ["puertia", "<big>puertia</big>\n\n<b>puertia, ae</b> <i>f</i> <i>H</i>\n= pueritia."],
  ["puerulus", "<big>puerulus</big>\n\n<b>puerulus, ī</b> <i>m</i> [<i>demin. к</i> puer]\nмладенец <i>C, VM</i>."],
  ["puerus", "<big>puerus</big>\n\n<b>I puerus, ī</b> <i>m</i> (<i>редко</i>) <i>Caec, Pl</i>\n= puer.\n\n<b>II puerus, a, um</b> <i>Eccl</i>\n= puerilis."],
  ["puga", "<big>puga</big>\n\n<b>puga, ae</b> <i>f</i> <i>H</i>\n= pyga."],
  ["pugil", "<big>pugil</big>\n\n<b>pugil, ilis</b> (<i>abl.</i> i) <i>m</i>\nКулачный боец <i>C, H, O, Su</i>: os pugilis <i>Su</i> наглое лицо."],
  ["pugilatio", "<big>pugilatio</big>\n\n<b>pugilatio, onis</b> <i>f</i> <i>и</i> <b>pugilatus, us</b> <i>m</i> [pugil]\nкулачный бой <i>C etc.</i>"],
  ["pugilator", "<big>pugilator</big>\n\n<b>pugilator, oris</b> <i>m</i> <i>Eccl</i>\n= pugil."],
  ["pugilatorius", "<big>pugilatorius</big>\n\n<b>pugilatorius, a, um</b> [pugil]\nподбрасываемый ударом кулака (follis <i>Pl</i>)."],
  ["pugilatus", "<big>pugilatus</big>\n\n<b>pugilatus, us</b> <i>m</i> <i>Pl, PM</i>\n= pugilatio."],
  ["pugilice", "<big>pugilice</big>\n\n<b>pugilice</b> [pugil]\nкак кулачный боец (valere <i>Pl</i>)."],
  ["pugilis", "<big>pugilis</big>\n\n<b>pugilis, is</b> <i>m</i> <i>Vr</i>\n= pugil."],
  ["pugillar", "<big>pugillar</big>\n\n<b>pugillar, aris</b> <i>n</i> <i>Aus</i>\n= pugillares."],
  ["pugillares", "<big>pugillares</big>\n\n<b>pugillares, ium</b> <i>m</i> [pugillaris]\n(<i>sc.</i> libelli <i>или</i> codicilli) таблички для заметок, записная книжка <i>PM, PJ, Sen, M etc.</i>"],
  ["pugillaria", "<big>pugillaria</big>\n\n<b>pugillaria, ium</b> <i>n</i> <i>Ctl, AG</i>\n= pugillares."],
  ["pugillaris", "<big>pugillaris</big>\n\n<b>pugillaris, e</b> [pugillus]\nвеличиной с кулак <i>J</i>."],
  ["pugillat-", "<big>pugillat-</big>\n\n<b>pugillat-</b> <i>v. l.</i>\n= pugilat-."],
  ["pugillator", "<big>pugillator</big>\n\n<b>pugillator, oris</b> <i>m</i>\n<b>1)</b> письмоносец, гонец <i>Sid</i>;\n<b>2)</b> жулик, вор <i>Is</i>."],
  ["pugillor", "<big>pugillor</big>\n\n<b>pugillor</b> <i>v. l.</i>\n= pugilor."],
  ["pugillus", "<big>pugillus</big>\n\n<b>pugillus, i</b> <i>m</i> [<i>demin. к</i> pugnus]\nгорсть <i>Cato, PM</i>."],
  ["pugilor", "<big>pugilor</big>\n\n<b>pugilor, —, ari</b> <i>depon.</i> [pugil]\n<b>1)</b> биться на кулаках, вести кулачный бой <i>Ap, Treb</i>;\n<b>2)</b> брыкаться, лягаться (equus pugilatur unguibus in aliquem <i>Ap</i>)."],
  ["pugio", "<big>pugio</big>\n\n<b>pugio, onis</b> <i>m</i> [pungo]\nкинжал <i>C, T, Su etc.</i>: plumbeus p. <i>C</i> свинцовый кинжал, <i>т. е.</i> слабый довод."],
  ["pugiunculus", "<big>pugiunculus</big>\n\n<b>pugiunculus, i</b> <i>m</i> [<i>demin. к</i> pugio]\nмаленький кинжал <i>C</i>."],
  ["pugna", "<big>pugna</big>\n\n<b>pugna, ae</b> <i>f</i>\n<b>1)</b> бой, битва (pugnam committere <i>C</i>; p. Cannensis <i>C</i> <i>или</i> p. Cannarum <i>L</i>);\n<b>2)</b> состязание (p. quinquennis <i>O</i>);\n<b>3)</b> боевой порядок (pugnam mutare <i>QC</i>);\n<b>4)</b> война (Siciliae adversa p. <i>Just</i>);\n<b>5)</b> борьба, спор (doctissimorum hominum <i>C</i>);\n<b>6)</b> проделка: aliquid pugnae edere <i>Pl</i> <i>или</i> pugnam aliquam dare <i>Ter</i> выкинуть какую-л. штуку."],
  ["pugnacitas", "<big>pugnacitas</big>\n\n<b>pugnacitas, atis</b> <i>f</i> [pugnax]\nдрачливость, воинственность (<i>sc.</i> perdicum <i>PM</i>); пыл, задор (p. accusatoria <i>Sen</i>)."],
  ["pugnaciter", "<big>pugnaciter</big>\n\n<b>pugnaciter</b> [pugnax]\nупорно, своенравно (sententiam defendere <i>C</i>); всеми силами (certare de aliqua re <i>C</i>)."],
  ["pugnaculum", "<big>pugnaculum</big>\n\n<b>pugnaculum, i</b> <i>n</i> [pugno]\nбастион, вал <i>Pl, Amm</i>."],
  ["pugnator", "<big>pugnator</big>\n\n<b>pugnator, oris</b> <i>m</i> [pugno]\nбоец, ратник, воин <i>V, L, Su etc.</i>: p. gallus <i>PM</i> бойцовый петух."],
  ["pugnatorius", "<big>pugnatorius</big>\n\n<b>pugnatorius, a, um</b> [pugnator]\nбоевой, воинский (arma <i>Su</i>)."],
  ["pugnatrix", "<big>pugnatrix</big>\n\n<b>pugnatrix, icis</b> <i>f</i>\n<b>1.</b> <i>adj.</i> воинственная (natio <i>Amm</i>);\n<b>2.</b> <i>subst.</i> воительница <i>Eccl</i>."],
  ["pugnax", "<big>pugnax</big>\n\n<b>pugnax, acis</b> <i>adj.</i> [pugno]\n<b>1)</b> жаждущий войны, воинственный (gens <i>QC</i>; ensis <i>O</i>); полемический, задорный (oratio <i>C</i>);\n<b>2)</b> строптивый, упрямый, упорный (contra aliquem <i>C</i>; in aliqua re <i>C</i>); непримиримый, враждебный (ignis aquae p. <i>O</i>);\n<b>3)</b> терпкий (musta pugnacia <i>PM</i>)."],
  ["pugneus", "<big>pugneus</big>\n\n<b>pugneus, a, um</b> [pugnus]\nкулачный: mergae pugneae <i>ирон. Pl</i> удары кулаком."],
  ["pugnitus", "<big>pugnitus</big>\n\n<b>pugnitus</b> <i>adv.</i> [pugnus]\nударами кулака (pessum dari <i>Caec</i>)."],
  ["pugno", "<big>pugno</big>\n\n<b>pugno, avi, atum, are</b> [pugna]\n<b>1)</b> (<i>тж.</i> proelium p. <i>H, Sl</i> <i>и</i> pugnam p. <i>LM, C</i>) биться, драться, сражаться (comminus <i>Cs, QC</i>; ex equo <i>C etc.</i>; cum aliquo <i>C, Cs etc.</i> <i>или</i> contra aliquem <i>C</i>, in aliquem <i>L, T</i> <i>и</i> adversus aliquem <i>Sl, Nep</i>);\n<b>2)</b> спорить (cum aliquo <i>C</i>; de aliqua re <i>C</i>);\n<b>3)</b> противоречить, находиться в противоречии (metus cum cupiditate pugnat <i>C</i>): secum p. <i>или</i> pugnantia loqui <i>C</i> противоречить самому себе; pugnantia et contraria studia <i>C</i> противоречивые и исключающие друг друга влечения;\n<b>4)</b> бороться, стараться (p. molles evincere somnos <i>O</i>): equus habenis pugnat <i>V</i> лошадь не слушается поводьев; placito amori p. <i>V</i> стараться побороть влечение сердца;\n<b>5)</b> находиться в любовной связи (cum aliqua <i>Pt</i>)."],
  ["pugnus", "<big>pugnus</big>\n\n<b>pugnus, i</b> <i>m</i>\n<b>1)</b> кулак (pugnis certare <i>C</i>): pugnum facere <i>C</i> сжать руку в кулак;\n<b>2)</b> удар кулака (dentes uno pugno excutere <i>Treb</i>);\n<b>3)</b> кулачный бой (pugno victus <i>H</i>);\n<b>4)</b> горсть (aeris <i>Sen</i>) <i>или</i> размер кулака <i>Cato</i>."],
  ["pulc-", "<big>pulc-</big>\n\n<b>pulc-</b> <i>v. l.</i>\n= pulch-."],
  ["pulchellus", "<big>pulchellus</big>\n\n<b>pulchellus, a, um</b> [<i>demin. к</i> pulcher]\nхорошенький (Bacchae <i>C</i>): p. puer <i>C ирон.-презр.</i> красавчик, молодчик."],
  ["pulcher", "<big>pulcher</big>\n\n<b>I pulcher, chra, chrum</b>\n<b>1)</b> красивый (pulchrae turpesque puellae <i>O</i>; urbs pulcherrima <i>Cs</i>);\n<b>2)</b> прекрасный, славный, благородный (facinus <i>Sl</i>; mors <i>V</i>): quid habet pulchri? <i>H</i> что в нём хорошего?\n\n<b>II Pulcher, chri</b> <i>m</i>\nПульхр, <i>cognomen в роде Клавдиев</i> <i>L, VM</i>."],
  ["pulchralia", "<big>pulchralia</big>\n\n<b>pulchralia, ae</b> <i>f</i> [pulcher]\nдесерт, фрукты <i>Cato</i>."],
  ["pulchre", "<big>pulchre</big>\n\n<b>pulchre</b> [pulcher]\nкрасиво, прекрасно, отлично <i>Pl, C, H etc.</i>"],
  ["pulchresco", "<big>pulchresco</big>\n\n<b>pulchresco, —, —, ere</b>\nстановиться краше, хорошеть <i>Aug</i>."],
  ["Pulchri promunturium", "<big>Pulchri promunturium</big>\n\n<b>Pulchri promunturium</b> <i>n</i>\n<i>мыс в сев. Африке, к сев.-вост. от Карфагена</i> <i>L</i>."],
  ["pulchritas", "<big>pulchritas</big>\n\n<b>pulchritas, atis</b> <i>f</i> <i>Caec</i>\n= pulchritudo."],
  ["pulchritudo", "<big>pulchritudo</big>\n\n<b>pulchritudo, inis</b> <i>f</i> [pulcher]\nкрасота (corporis, verborum <i>C</i>; urbis <i>Fl</i>; equi <i>AG</i>)."],
  ["pulegium", "<big>pulegium</big>\n\n<b>pulegium, i</b> <i>n</i> <i>CC</i>\n= pulejum."],
  ["pulejatum", "<big>pulejatum</big>\n\n<b>pulejatum, i</b> <i>n</i> [pulejum]\n(<i>sc.</i> vinum) настоенное на полее вино <i>Lampr</i>."],
  ["pulejatus", "<big>pulejatus</big>\n\n<b>pulejatus, a, um</b>\nсдобренный полеем (porca <i>Veg</i>)."],
  ["pulejum", "<big>pulejum</big>\n\n<b>pulejum, i</b> <i>n</i>\nполей, <i>разновидность мяты</i> (<i>Mentha pulegium</i>, L.) <i>C, Col, Sen etc.</i>"],
  ["pulex", "<big>pulex</big>\n\n<b>pulex, icis</b> <i>m</i>\nблоха <i>Pl, CC etc.</i>; земляная блоха <i>Col</i>."],
  ["pulicarius", "<big>pulicarius</big>\n\n<b>pulicarius, a, um</b> [pulex]\nблошиный (herba <i>CA</i>)."],
  ["pulicosus", "<big>pulicosus</big>\n\n<b>pulicosus, a, um</b> [pulex]\nполный блох (canis <i>Col</i>)."],
  ["pullarius", "<big>pullarius</big>\n\n<b>I pullarius, a, um</b>\nотносящийся к молодым животным <i>Veg</i>: p. feles <i>ирон. Aus</i> ловец маленьких детей.\n\n<b>II pullarius, i</b> <i>m</i> [pullus I]\nцыплятник, кормящий священных цыплят (<i>по клёву которых предсказывали будущее</i>) <i>C, L</i>."],
  ["pullastra", "<big>pullastra</big>\n\n<b>pullastra, ae</b> <i>f</i> [pullus I]\nмолодая Курица, курочка <i>Vr</i>."],
  ["pullati", "<big>pullati</big>\n\n<b>pullati, orum</b> <i>m</i> [pullatus]\nпростой народ <i>Su</i>."],
  ["pullatio", "<big>pullatio</big>\n\n<b>pullatio, onis</b> <i>f</i> [pullo]\nвысиживание (цыплят) <i>Col</i>."],
  ["pullatus", "<big>pullatus</big>\n\n<b>pullatus, a, um</b> [pullus II]\n<b>1)</b> одетый в платье тёмного (чёрного) цвета, <i>т. е.</i> в траур <i>J, Amm</i>: incedunt albati ad exsequiae, pullati ad nuptias <i>погов. Sid</i> в белом они идут на похороны, а в чёрном — на свадьбу (<i>о людях, которые всё делают невпопад</i>);\n<b>2)</b> нечисто одетый, <i>т. е.</i> простой: p. circulus <i>или</i> pullata turba <i>Q</i> = pullati."],
  ["pullejaceus", "<big>pullejaceus</big>\n\n<b>pullejaceus, a, um</b> [pullus II]\nтёмный, чёрный <i>Augustus ap. Su</i>."],
  ["pullicenus", "<big>pullicenus</big>\n\n<b>pullicenus, i</b> <i>m</i> [pullus I]\nптенец, цыплёнок <i>Lampr</i>."],
  ["pulligo", "<big>pulligo</big>\n\n<b>pulligo, inis</b> <i>f</i> [pullus II]\nтёмный цвет (ovium <i>PM</i>)."],
  ["pullinus", "<big>pullinus</big>\n\n<b>pullinus, a, um</b> [pullus I]\n<b>1)</b> принадлежащий молодым животным (dentes <i>PM</i>);\n<b>2)</b> куриный (ova <i>Lampr</i>)."],
  ["pulliprema", "<big>pulliprema</big>\n\n<b>pulli–prema, ae</b> <i>m</i> [pullus + premo] <i>Aus</i>\n= paedico II."],
  ["pullities", "<big>pullities</big>\n\n<b>pullities, ei</b> <i>f</i> [pullus I]\nвыводок, молодняк <i>Vr, Col</i>."],
  ["pullo", "<big>pullo</big>\n\n<b>pullo, avi, —, are</b> <i>Pt</i>\n(<i>v. l.</i>), <i>Calp</i> = pullulo 3."],
  ["pullulasco", "<big>pullulasco</big>\n\n<b>pullulasco, —, —, ere</b> [pullulo]\nдавать побеги, прорастать (vitis pullulascit <i>Col</i>)."],
  ["pullulo", "<big>pullulo</big>\n\n<b>pullulo, avi, atum, are</b> [pullulus I]\n<b>1)</b> давать побеги, пускать ростки (pullulat ab radice silva <i>V</i>);\n<b>2)</b> кишеть (colubris <i>V</i>);\n<b>3)</b> развиваться, разрастаться, распространяться (p. incipiebat luxuria <i>Nep</i>);\n<b>4)</b> производить, порождать (venerem aliam <i>Ap</i>)."],
  ["pullulus", "<big>pullulus</big>\n\n<b>I pullulus, i</b> <i>m</i> [<i>demin. к</i> pullus I]\n<b>1)</b> цыплёночек, птенчик <i>Ap</i>;\n<b>2)</b> молодой побег, веточка <i>PM</i>.\n\n<b>II pullulus, a, um</b> [<i>demin. к</i> pullus II]\nчерноватый, тёмно-серый (terra <i>Col</i>)."],
  ["pullum", "<big>pullum</big>\n\n<b>pullum, i</b> <i>n</i>\nтёмный цвет <i>O</i>."],
  ["pullus", "<big>pullus</big>\n\n<b>I pullus, i</b> <i>m</i> [<i>одного корня с</i> puellus]\n<b>1)</b> детёныш, молодое животное: p. equinus <i>PM</i> жеребёнок; p. columbinus <i>C</i> молодой голубь; p. milvinus <i>C</i> птенец коршуна, <i>перен.</i> жадный человек;\n<b>2)</b> цыплёнок <i>C, L</i>;\n<b>3)</b> птенец <i>Ap</i>;\n<b>4)</b> крошка, карапуз <i>H</i>;\n<b>5)</b> молодой побег, росток <i>Cato, Pall</i>.\n\n<b>II pullus, a, um</b>\n<b>1)</b> тёмно-серый, тёмный (color <i>Col</i>); тёмно-зелёный (myrtus <i>H</i>): terra pulla <i>Col</i> чернозём; pulla vestis <i>O</i> траурное платье <i>или</i> простое платье;\n<b>2)</b> простонародный (sermo <i>Vr</i>).\n\n<b>III pullus, a, um</b> [<i>demin. к</i> purus]\nчистенький (vestis <i>Vr</i> — <i>ср.</i> pullus II)."],
  ["pulmentaris", "<big>pulmentaris</big>\n\n<b>pulmentaris, e</b> [pulmentum]\nотносящийся к закуске (cibus <i>PM</i>)."],
  ["pulmentarium", "<big>pulmentarium</big>\n\n<b>pulmentarium, i</b> <i>n</i> [pulmentum]\n<b>1)</b> закуска (<i>преим. рыбное или мясное блюдо</i>) <i>или</i> приправа <i>Cato, Vr, Ph, Sen etc.</i>: pulmentaria quaerere sudando <i>H</i> приобретать аппетит усиленной работой;\n<b>2)</b> корм (<i>для домашней птицы</i>) <i>Cato</i>."],
  ["pulmentum", "<big>pulmentum</big>\n\n<b>pulmentum, i</b> <i>n</i> [<i>из</i> pulpamentum <i>от</i> pulpa]\n<b>1)</b> лакомое мясное <i>или</i> рыбное блюдо <i>Pl, CA</i>;\n<b>2)</b> приправа, гарнир (pulmenta laboribus empta <i>H</i>);\n<b>3)</b> кусок, порция (singula pulmenta <i>H</i>)."],
  ["pulmo", "<big>pulmo</big>\n\n<b>pulmo, onis</b> <i>m</i>\n<b>1)</b> <i>анат.</i> лёгкое <i>C, CC, O etc.</i>;\n<b>2)</b> (<i>тж.</i> p. marinus <i>PM</i>) <i>род моллюска</i> <i>PM</i>."],
  ["pulmonaceus", "<big>pulmonaceus</big>\n\n<b>pulmonaceus, a, um</b> [pulmo]\nцелительный для лёгких (radicula <i>Veg</i>)."],
  ["pulmonarius", "<big>pulmonarius</big>\n\n<b>pulmonarius, a, um</b> [pulmo]\n<b>1)</b> больной лёгкими (ovis <i>Col</i>);\n<b>2)</b> <i>Veg</i> = pulmonaceus."],
  ["pulmoneus", "<big>pulmoneus</big>\n\n<b>pulmoneus, a, um</b> [pulmo]\n<b>1)</b> лёгочный: pulmoneum vomitum vomas! <i>бран. Pl</i> чтоб тебя вырвало лёгкими!;\n<b>2)</b> рыхлый, мягкий (mala <i>PM</i>)."],
  ["pulmunculus", "<big>pulmunculus</big>\n\n<b>pulmunculus, i</b> <i>m</i> [<i>demin. к</i> pulmo]\nгубчатый мясистый нарост (<i>на ногах или на спине некоторых животных</i>) <i>Sol, Veg</i>."],
  ["pulmunes", "<big>pulmunes</big>\n\n<b>pulmunes</b> <i>Pl</i>\n= pulmones (<i>pl. к</i> pulmo)."],
  ["pulpa", "<big>pulpa</big>\n\n<b>pulpa, ae</b> <i>f</i>\n<b>1)</b> мясистая часть, мясо (ossa viduata pulpis <i>Ap</i>);\n<b>2)</b> плоть, <i>перен.</i> род человеческий (p. scelerata <i>Pers</i>); p. plebeja <i>Aus</i> = plebs;\n<b>3)</b> микоть плодов <i>Scr</i>; мягкие части древесины <i>PM</i>."],
  ["pulpamen", "<big>pulpamen</big>\n\n<b>pulpamen, inis</b> <i>n</i> [pulpa] <i>L</i>\n= pulmentum."],
  ["pulpamentum", "<big>pulpamentum</big>\n\n<b>pulpamentum, i</b> <i>n</i> (= pulmentum)\n<b>1)</b> мясное <i>или</i> рыбное блюдо <i>Pl, PM, Ap</i>;\n<b>2)</b> приправа (mihi est p. fames <i>Anacharsis ap. C</i>)."],
  ["pulpito", "<big>pulpito</big>\n\n<b>pulpito, —, —, are</b> [pulpitum]\nобшивать досками (cari narum ventres <i>Sid</i>)."],
  ["pulpitum", "<big>pulpitum</big>\n\n<b>pulpitum, i</b> <i>n</i>\n<b>1)</b> дощатый помост, подмостки (insternere pulpita tignis <i>H</i>);\n<b>2)</b> трибуна, кафедра (<i>sc.</i> grammaticorum <i>H</i>);\n<b>3)</b> сцена <i>H, O, Prp, J etc.</i>"],
  ["pulpitus", "<big>pulpitus</big>\n\n<b>pulpitus, i</b> <i>m</i> <i>Is</i>\n= pulpitum 3."],
  ["pulpo", "<big>pulpo</big>\n\n<b>pulpo, —, —, are</b>\nкричать (<i>о коршуне</i>) <i>Su</i>."],
  ["pulposus", "<big>pulposus</big>\n\n<b>pulposus, a, um</b> [pulpa]\nмясистый (toruli <i>Ap</i>)."],
  ["puls", "<big>puls</big>\n\n<b>puls, pultis</b> <i>f</i>\nкрутая похлёбка <i>или</i> каша из полбы <i>Vr, C VM, PM etc.</i>"],
  ["pulsabulum", "<big>pulsabulum</big>\n\n<b>pulsabulum, i</b> <i>n</i> [pulso]\nплектр (p. citharae admovere <i>Ap</i>)."],
  ["pulsatio", "<big>pulsatio</big>\n\n<b>pulsatio, onis</b> <i>f</i> [pulso]\n<b>1)</b> стук, удары (ostii <i>Ph</i>, scutorum <i>L</i>);\n<b>2)</b> избиение (alicujus <i>C</i>);\n<b>3)</b> насилие (p. pudoris <i>Dig</i>);\n<b>4)</b> судебный иск, жалоба (illata sollemniter p. <i>CJ</i>)."],
  ["pulsator", "<big>pulsator</big>\n\n<b>pulsator, oris</b> <i>m</i> [pulso]\nударяющий: p. citharae <i>VF</i> играющий на кифаре; p. aurium alicujus <i>Aug</i> = чей-л. неутомимый наставник."],
  ["pulsio", "<big>pulsio</big>\n\n<b>pulsio, onis</b> <i>f</i> [pello]\nудар <i>Eccl</i>."],
  ["pulso", "<big>pulso</big>\n\n<b>pulso, avi, atum, are</b> [<i>intens. к</i> pello]\n<b>1)</b> сильно стучать (p. fores <i>O</i>): p. ad aliquem <i>Aug</i> стучаться к кому-л. || ударять (montis caput vento pulsatur <i>V</i>); бить, ломать (muros ariete <i>V</i>); разбивать, толочь (semen in pila <i>PM</i>); погонять (equum <i>Cld</i>); отгонять, отвращать (pericula <i>Cld</i>); топать (humum pede <i>O</i>); p. curru <i>V</i> проезжать; p. pedibus <i>O</i> пробегать; p. sagittam <i>V</i> пускать стрелу; sidera p. <i>V</i> касаться звёзд; p. lyram <i>V etc.</i> бряцать (играть) на лире;\n<b>2)</b> бить, избивать (p. et verberare aliquem <i>C</i>); хлестать, сечь (terras grandine <i>O</i>);\n<b>3)</b> вскапывать, обрабатывать (arva <i>O</i>);\n<b>4)</b> потрясать, поражать (corda <i>V</i>); тревожить, угнетать (pectus alicui <i>Ter</i>); оскорблять (aliquem aliqua re <i>Amm</i>); волновать (urbem rumoribus <i>Pt</i>); толкать, увлекать, побуждать (quae te vecordia pulsat? <i>O</i>); докучать, донимать (aliquem querelis <i>St, Cld</i>);\n<b>5)</b> жаловаться (на кого-л.), обвинять (aliquem judicio <i>CTh</i>; injusta Tartara <i>St</i>; crimine falso pulsari <i>Cld</i>; pulsantibus respondere <i>Dig</i>)."],
  ["pulsuosus", "<big>pulsuosus</big>\n\n<b>pulsuosus, a, um</b> [pulsus]\nпульсирующий (dolor <i>CA</i>)."],
  ["pulsus", "<big>pulsus</big>\n\n<b>I pulsus, a, um</b> <i>part. pf. к</i>\npello.\n\n<b>II pulsus, us</b> <i>m</i> [pello]\n<b>1)</b> удар, толчок: p. equorum <i>V</i> конский топот; p. venarum <i>PM, T</i> (arteriarum <i>PM</i>) биение пульса, пульс; p. remorum <i>C</i> гребля; p. lyrae <i>O</i> игра на лире;\n<b>2)</b> побуждение (externus p. <i>C</i>); впечатление (animus afficitur pulsibus <i>AG</i>)."],
  ["pultarius", "<big>pultarius</big>\n\n<b>pultarius, i</b> <i>m</i> [puls]\nпосуда, сосуд, горшок <i>PM, Col, Pt etc.</i>; медицинская банка <i>CC</i>."],
  ["pultatio", "<big>pultatio</big>\n\n<b>pultatio, onis</b> <i>f</i> [pulto]\nстучание, стук <i>Pl</i>."],
  ["pulticula", "<big>pulticula</big>\n\n<b>pulticula, ae</b> <i>f</i> [<i>demin. к</i> puls]\nпохлёбка <i>CC, PM, Col etc.</i>"],
  ["pultifagus", "<big>pultifagus</big>\n\n<b>pultifagus, i</b> <i>m</i> <i>Pl</i>\n= pultiphagus."],
  ["pultificus", "<big>pultificus</big>\n\n<b>pulti–ficus, a, um</b> [puls + facio]\nслужащий для приготовления похлёбки (far <i>Aus</i>)."],
  ["pultiphagonides", "<big>pultiphagonides</big>\n\n<b>pultiphagonides, ae</b> <i>m</i> <i>Pl</i>\n= pultiphagus."],
  ["pultiphagus", "<big>pultiphagus</big>\n\n<b>pultiphagus, i</b> <i>m</i>\nпоедатель полбы, полбоед (<i>шутл. о римлянине</i>) <i>Pl</i>."],
  ["pultis", "<big>pultis</big>\n\n<b>pultis</b> <i>gen. к</i>\npuls."],
  ["pulto", "<big>pulto</big>\n\n<b>pulto, avi, —, are</b> [pulso]\nстучать, ударять, бить (januam, pectus <i>Pl</i>)."],
  ["pulver", "<big>pulver</big>\n\n<b>pulver, eris</b> <i>m</i> <i>Ap</i>\n= pulvis."],
  ["pulveratica", "<big>pulveratica</big>\n\n<b>pulveratica, ae</b> <i>f</i> <i>и</i> <b>pulveraticum, i</b> <i>n</i> [pulvis 7]\nплата за труд <i>CTh</i>."],
  ["pulveratio", "<big>pulveratio</big>\n\n<b>pulveratio, onis</b> <i>f</i> [pulvero]\nразмельчение, разбивание комьев земли <i>Col</i>."],
  ["pulvereus", "<big>pulvereus</big>\n\n<b>pulvereus, a, um</b> [pulvis]\n<b>1)</b> пыльный, покрытый пылью (solum <i>O</i>); из пыли: nubes pulverea <i>V</i> облако пыли;\n<b>2)</b> поднимающий пыль (equi <i>VF</i>);\n<b>3)</b> мелкий как пыль, тончайшего помола (farina <i>O</i>)."],
  ["pulveris", "<big>pulveris</big>\n\n<b>pulveris</b> <i>gen. к</i>\npulvis."],
  ["pulvero", "<big>pulvero</big>\n\n<b>pulvero, —, —, are</b> [pulvis]\n<b>1)</b> покрывать пылью, запылить (sese <i>PM</i>);\n<b>2)</b> покрываться пылью <i>Pl ap. AG</i>;\n<b>3)</b> превращать в пыль (fragiles herbas <i>Calp</i>)."],
  ["pulverulentus", "<big>pulverulentus</big>\n\n<b>pulverulentus, a, um</b> [pulvis]\n<b>1)</b> пыльный, в пыли (via <i>C</i>; aestas <i>V</i>);\n<b>2)</b> поднимающий пыль (agmina <i>V</i>);\n<b>3)</b> <i>перен.</i> трудный, тяжёлый (praemia militiae <i>O</i>)."],
  ["pulvesco", "<big>pulvesco</big>\n\n<b>pulvesco, —, —, ere</b>\nпревращаться в прах <i>Aug</i>."],
  ["pulviculus", "<big>pulviculus</big>\n\n<b>pulviculus, i</b> <i>m</i> [<i>demin. к</i> pulvis]\nпыльца <i>Sol</i> (<i>v. l.</i>)."],
  ["pulvillus", "<big>pulvillus</big>\n\n<b>pulvillus, i</b> <i>m</i> [<i>demin. к</i> pulvinus]\nподушечка <i>H, Ap</i>."],
  ["pulvinar", "<big>pulvinar</big>\n\n<b>pulvinar, aris</b> <i>n</i> [pulvinus]\n<b>1)</b> (<i>тж.</i> p. deorum <i>H</i>) подушка для изображений богов (<i>при</i> lectisternium) (p. deae dedicare <i>C</i>);\n<b>2)</b> храм: ad (apud, circa) omnia pulvinaria <i>C, L, T</i> во всех храмах;\n<b>3)</b> <i>преим. pl.</i> pulvinaria <i>T</i> = lectisternium;\n<b>4)</b> подушка, пуховик (caput inclinare super p. <i>Pt</i>);\n<b>5)</b> мягкое ложе (pulvinaria servare <i>O</i>);\n<b>6)</b> ложа в цирке (<i>для римск. цезарей</i>) (spectare e pulvinari <i>Su</i>);\n<b>7)</b> <i>pl.</i> якорная стоянка (subducere navim in pulvinaria <i>Pl</i>);\n<b>8)</b> культовое пиршество, <i>т. е.</i> lectisternium (deorum pulvinaribus fides praecinunt <i>C</i>)."],
  ["pulvinaris", "<big>pulvinaris</big>\n\n<b>pulvinaris, e</b> [pulvinus]\nсидящий на подушке, <i>т. е.</i> Комнатный, ручной (pica <i>Pt</i>)."],
  ["pulvinarium", "<big>pulvinarium</big>\n\n<b>pulvinarium, i</b> <i>n</i> [pulvinus]\n<b>1)</b> <i>L</i> = pulvinar;\n<b>2)</b> причал, стоянка судна <i>Pl</i> (<i>ср.</i> pulvinar 7)."],
  ["pulvinatus", "<big>pulvinatus</big>\n\n<b>pulvinatus, a, um</b> [pulvinus]\nимеющий форму подушки, <i>т. е.</i> выпуклый (calyx juglandis <i>PM</i>; capitula columnarum <i>Vtr</i>)."],
  ["pulvinulus", "<big>pulvinulus</big>\n\n<b>pulvinulus, i</b> <i>m</i> [<i>demin. к</i> pulvinus]\nкочка <i>Col</i>."],
  ["pulvinus", "<big>pulvinus</big>\n\n<b>pulvinus, i</b> <i>m</i>\n<b>1)</b> подушка (effultus sericis pulvinis, <i>v. l.</i> pulvillis <i>M</i>); изголовье (epistulam pulvino subjicere <i>Nep</i>); мягкое сиденье (<i>sc.</i> subsellii <i>Pl</i>);\n<b>2)</b> грядка, клумба <i>Vr, Col, PM, PJ</i>;\n<b>3)</b> <i>архит.</i>, <i>тех.</i> круглое возвышение, каменное <i>или</i> земляное основание <i>Cato, Col, Vtr</i>."],
  ["pulvis", "<big>pulvis</big>\n\n<b>pulvis, eris</b> <i>m</i> (<i>редко</i> <i>Prp f</i>)\n<b>1)</b> пыль, мелкий песок (nubes pulveris <i>Lcr, L, QC, V etc.</i>): p. hibernus <i>V</i> пыльная, <i>т. е.</i> сухая зима; p. (<i>тж.</i> p. eruditus) <i>C, L, Pers</i> песок, на котором чертили математические фигуры || <i>перен.</i> математика (numquam eruditum pulverem attingere <i>C</i>): sulcos in pulvere ducere <i>погов. J</i> пахать песок, <i>т. е.</i> заниматься бесполезным делом; pulverem ob oculos aspergere <i>погов. AG</i> пускать пыль в глаза;\n<b>2)</b> порошок (ex lapide speculari p. tritus <i>Pt</i>);\n<b>3)</b> пепел (покойника), прах (p. et umbra sumus <i>H</i>): novendiales pulveres <i>H</i> прах девятидневной давности, т. е. свежий;\n<b>4)</b> глина (p. Etrusca <i>Prp</i>): p. coctus <i>St</i> обожжённые кирпичи;\n<b>5)</b> арена, ристалище (domitare in pulvere currus <i>V</i>);\n<b>6)</b> поприще, поле деятельности (in suo pulvere <i>O</i>): in solem et (atque) pulverem публично (procedere, producere <i>C</i>);\n<b>7)</b> борьба, усилие, напряжение: palma sine pulvere <i>H</i> награда, полученная без напряжения; p. belli <i>M</i> <i>и</i> p. bellicus (Martius) <i>Amm</i> боевая страда, сеча."],
  ["pulvisculum", "<big>pulvisculum</big>\n\n<b>pulvisculum, i</b> <i>n</i> <i>Hier</i>\n= pulvisculus."],
  ["pulvisculus", "<big>pulvisculus</big>\n\n<b>pulvisculus, i</b> <i>m</i> [<i>demin. к</i> pulvis]\nмелкая пыль: p. ex Arabicis frugibus <i>Ap</i> = зубной порошок || порошок (песок) для математических занятий (<i>см.</i> pulvis 1): abaco et pulvisculo se dedisse <i>Ap</i> заняться математикой; cum pulvisculo <i>погов. Pl</i> до последней пылинки, <i>т. е.</i> целиком."],
  ["Pulydamas", "<big>Pulydamas</big>\n\n<b>Pulydamas</b> <i>Prp</i>\n(<i>ионич. форма</i>) = Polydamas."],
  ["pumex", "<big>pumex</big>\n\n<b>pumex, icis</b> <i>m</i>\n<b>1)</b> пемза <i>PM, O, Ctl, Sen</i>: aquam a pumice postulare <i>погов. Pl</i> = требовать от кого-л. невозможного;\n<b>2)</b> скважистый (пористый) камень <i>PM, O, V etc.</i>"],
  ["pumicatus", "<big>pumicatus</big>\n\n<b>pumicatus, a, um</b> [pumex]\n<b>1)</b> шлифованный <i>или</i> выглаженный пемзой, <i>т. е.</i> безволосый (manus <i>M</i>);\n<b>2)</b> <i>перен.</i> вылощенный (homo comptus et p. <i>PJ</i>). — <i>См. тж.</i> pumico."],
  ["pumiceus", "<big>pumiceus</big>\n\n<b>pumiceus, a, um</b> [pumex]\nпемзовый, сделанный из пемзы (molae <i>O</i>; antra <i>St</i>): oculi pumicei <i>Pl</i> сухие глаза (<i>т. е.</i> никогда не плачущие); fontes pumicei <i>M</i> источники, вытекающие из пемзовых скал."],
  ["pumico", "<big>pumico</big>\n\n<b>pumico, avi, atum, are</b> [pumex]\n<b>1)</b> чистить, тереть (dentem atque gingivam <i>Ctl</i>);\n<b>2)</b> <i>перен.</i> наводить лоск <i>PM, Sid</i>. — <i>См. тж.</i> pumicatus."],
  ["pumicosus", "<big>pumicosus</big>\n\n<b>pumicosus, a, um</b> [pumex]\nпохожий на пемзу, ноздреватый, пористый (lapis <i>PM</i>)."],
  ["pumilio", "<big>pumilio</big>\n\n<b>pumilio, onis</b> <i>m, f</i> [pumilus]\nкарлик, карлица <i>Lcr, Sen, M, Col etc.</i>"],
  ["pumilo", "<big>pumilo</big>\n\n<b>pumilo, onis</b> <i>m</i> <i>St</i>\n= pumilio."],
  ["pumilus", "<big>pumilus</big>\n\n<b>I pumilus, i</b> <i>m</i>\nкарлик <i>Su, St</i>.\n\n<b>II pumilus, a, um</b>\nкарликовый (maritus quovis puero pumilior <i>Ap</i>)."],
  ["pumula", "<big>pumula</big>\n\n<b>pumula, ae</b> <i>f</i>\n<i>разновидность винограда</i> <i>PM</i>."],
  ["puncta", "<big>puncta</big>\n\n<b>puncta, ae</b> <i>f</i> [pungo]\nудар колющим орудием, укол <i>Veg</i>."],
  ["punctariola", "<big>punctariola</big>\n\n<b>punctariola</b>, <i>v. l.</i> <b>punctatoriola, ae</b> <i>f</i> [pungo]\nлёгкая стычка <i>Cato</i>."],
  ["punctillum", "<big>punctillum</big>\n\n<b>punctillum, i</b> <i>n</i> [<i>demin. к</i> punctum]\nмаленькая точка <i>Sol</i>."],
  ["punctim", "<big>punctim</big>\n\n<b>punctim</b> [pungo]\n<b>1)</b> нанося удары колющим орудием, коля (p. et caesim petere hostem <i>L</i>);\n<b>2)</b> от удара колющим орудием (quid interest, caesim moriar an p.? <i>Sen</i>)."],
  ["punctio", "<big>punctio</big>\n\n<b>punctio, onis</b> <i>f</i> [pungo]\nукол, колотьё, Колющая боль (laterum punctiones <i>PM</i>)."],
  ["punctiuncula", "<big>punctiuncula</big>\n\n<b>punctiuncula, ae</b> <i>f</i> [<i>demin. к</i> punctio]\n<b>1)</b> лёгкий укол (punctiunculas sentire <i>Sen</i>);\n<b>2)</b> лёгкое воздействие, малейшее ощущение (punctiunculae voluptatum doloruraque <i>Sen</i>)."],
  ["punctulum", "<big>punctulum</big>\n\n<b>punctulum, i</b> <i>n</i> [<i>demin. к</i> punctum]\nлёгкий укол <i>Ap</i>."],
  ["punctum", "<big>punctum</big>\n\n<b>punctum, i</b> <i>n</i> [pungo]\n<b>1)</b> укол (acus aereae <i>PM</i>); ужаление (parvulae volucris <i>Ph</i>);\n<b>2)</b> колющая боль (quasi puncta sentiuntur <i>CC</i>);\n<b>3)</b> маленькое отверстие (puncta, quae terebrantur acu <i>M</i>);\n<b>4)</b> точка <i>C, Aus</i>;\n<b>5)</b> пятнышко (ova punctis distincta <i>PM</i>);\n<b>6)</b> очко: quadringenis in punctum sestertiis aleam ludere <i>Su</i> ставить по 400 тысяч сестерциев на каждое очко;\n<b>7)</b> (<i>на выборах</i>) голос, подаваемый за кого-л. (<i>отмечавшийся точкой против имени кандидата</i>): multa puncta ferre <i>C</i> получить много голосов; omne p. ferre <i>погов. H</i> снискать всеобщее одобрение; puncto illius <i>H</i> согласно его суждению;\n<b>8)</b> маленькая часть, местечко (terrae <i>C</i>); кусочек, крошка (lapidis <i>Man</i>);\n<b>9)</b> мгновение, момент (p. est, quod vivimus <i>Sen</i>): puncto temporis <i>C, Cs</i> <i>и</i> ad p. temporis <i>C</i>, <i>тж.</i> puncto <i>Ap</i> в один миг; nullo puncto temporis intermisso <i>C</i> непрерывно; puncto mobilis horae <i>H</i> быстрым темпом;\n<b>10)</b> небольшой раздел (речи), пункт (puncta argumentorum <i>C</i>)."],
  ["punctus", "<big>punctus</big>\n\n<b>I punctus, a, um</b> <i>part. pf. к</i>\npungo.\n\n<b>II punctus, i</b> <i>m</i> <i>Boet, Is</i>\n= punctum.\n\n<b>III punctus, us</b> <i>m</i>\nукол <i>PM, Ap</i>."],
  ["pungo", "<big>pungo</big>\n\n<b>pungo, pupugi</b> (<i>арх.</i> <b>pepugi</b>), <b>punctum, ere</b>\n<b>1)</b> колоть (acu <i>Pt</i>); жалить (apes pungunt <i>Pt</i>); наносить уколом (vulnus acu punctum <i>C</i>);\n<b>2)</b> быть острым на вкус, жечь (nitrum pungit <i>PM</i>);\n<b>3)</b> поражать (sensum <i>Lcr</i>);\n<b>4)</b> проникать (p. corpus <i>Lcr</i>);\n<b>5)</b> щипать (aliquem manu <i>Pt</i>);\n<b>6)</b> беспокоить, огорчать (scrupulus aliquem stimulat ac pungit <i>C</i>); тревожить (suspiria pungunt somnos <i>Prp</i>)."],
  ["punicans", "<big>punicans</big>\n\n<b>punicans, antis</b> <i>adj.</i> [Punicus]\nкрасный, румяный (prae pudore <i>Ap</i>)."],
  ["Punicanus", "<big>Punicanus</big>\n\n<b>Punicanus, a, um</b> [Punus]\nпунический, карфагенский (lectuli <i>C, VM</i>)."],
  ["Punice", "<big>Punice</big>\n\n<b>Punice</b>\nпо-пунически, по-карфагенски <i>Pl</i>."],
  ["Puniceus", "<big>Puniceus</big>\n\n<b>Puniceus</b> <i>и</i> <b>Punicius, a, um</b> <i>Pl, V, O etc.</i>\n= Punicus."],
  ["Punicum", "<big>Punicum</big>\n\n<b>Punicum, i</b> <i>n</i> (<i>sc.</i> malum)\nгранат <i>PM</i>."],
  ["Punicus", "<big>Punicus</big>\n\n<b>Punicus, a, um</b>\n<b>1)</b> пунический, карфагенский (bellum <i>C</i>; ars <i>L</i>): Punica arbos <i>Col</i> гранатовое дерево; malum Punicum <i>PM</i> гранат;\n<b>2)</b> пурпурный (sagum <i>H</i>; tunica <i>VM</i>)."],
  ["punio", "<big>punio</big>\n\n<b>punio, ivi, itum, ire</b> [poena]\n<b>1)</b> наказывать, карать (aliquem supplicio <i>C</i> <i>и</i> morte <i>Su</i>; peccata <i>C</i>; lex omnis aut punit aut jubet <i>Q</i>): punienda <i>Ap</i> наказуемые дела, <i>т. е.</i> преступления;\n<b>2)</b> мстить: p. aliquid <i>C</i> мстить за что-л."],
  ["punior", "<big>punior</big>\n\n<b>punior, itus sum, iri</b> <i>depon.</i> <i>C</i>\n= punio."],
  ["punitio", "<big>punitio</big>\n\n<b>punitio, onis</b> <i>f</i> <i>etc.</i> [punio]\nнаказание, кара <i>VM, AG, etc.</i>"],
  ["punitor", "<big>punitor</big>\n\n<b>punitor, oris</b> <i>m</i> [punio]\n<b>1)</b> каратель (alicujus <i>Su, VM</i>);\n<b>2)</b> мститель (doloris sui <i>C</i>)."],
  ["Punus", "<big>Punus</big>\n\n<b>Punus, i</b> <i>m</i> <i>v. l.</i>\n= Poenus."],
  ["pupa", "<big>pupa</big>\n\n<b>pupa, ae</b> <i>f</i>\n<b>1)</b> девочка <i>M, Aus</i>;\n<b>2)</b> Кукла <i>Vr, Pers, Eccl</i>."],
  ["pupilla", "<big>pupilla</big>\n\n<b>pupilla, ae</b> <i>f</i> [<i>demin. к</i> pupa]\n<b>1)</b> девочка-сирота <i>C, PJ</i>;\n<b>2)</b> зрачок, зеница <i>Lcr, CC, PM, Ap</i>."],
  ["pupillaris", "<big>pupillaris</big>\n\n<b>I pupillaris, e</b> [pupillus]\nсиротский (pecuniae <i>L</i>): p. aetas <i>Su</i> несовершеннолетие (сирот).\n\n<b>II pupillaris, is</b> <i>m</i>\nнаследник умершего сироты <i>Dig</i>."],
  ["pupillariter", "<big>pupillariter</big>\n\n<b>pupillariter</b>\nвместо сироты (substituere aliquem <i>CJ</i>)."],
  ["pupillus", "<big>pupillus</big>\n\n<b>pupillus, i</b> <i>m</i> [<i>demin. к</i> pupus]\nмальчик-сирота <i>C, H, Su, J, Dig</i>."],
  ["Pupinia", "<big>Pupinia</big>\n\n<b>Pupinia, ae</b> <i>f</i> <i>или</i> <b>Pupiniensis</b> <i>и</i> <b>Pupinius ager</b>\nПупиния, <i>неплодородная область в Латии</i> <i>Vr, L, Col, VM, C</i>."],
  ["Pupius", "<big>Pupius</big>\n\n<b>Pupius, a, um</b>\nПупий, <i>римск. nomen <i>C, Cs</i>; наиболее известен</i> P., <i>римск. трагический поэт, современник Горация</i> <i>H</i>."],
  ["puplicitus", "<big>puplicitus</big>\n\n<b>puplicitus</b> <i>v. l.</i>\n= publicitus."],
  ["puppis", "<big>puppis</big>\n\n<b>puppis, is</b> (<i>acc.</i> im, <i>abl.</i> e <i>и</i> i) <i>f</i>\n<b>1)</b> корма: puppes vertere <i>погов. Nep</i> = бежать; advertere puppim <i>O</i> приставать к берегу;\n<b>2)</b> <i>поэт.</i> корабль <i>H, V, O</i>; созвездие Арго <i>C</i>;\n<b>3)</b> <i>шутл.</i> спина (p. pereunda est <i>Pl</i>)."],
  ["pupugi", "<big>pupugi</big>\n\n<b>pupugi</b> <i>pf. к</i>\npungo."],
  ["pupula", "<big>pupula</big>\n\n<b>pupula, ae</b> <i>f</i> [<i>demin. к</i> pupa]\n<b>1)</b> девчурка <i>Ap</i>;\n<b>2)</b> зрачок <i>Vr, C, Ctl, H, O etc.</i>;\n<b>3)</b> глаз: defrictis pupulis <i>Ap</i> протирая глаза."],
  ["pupulus", "<big>pupulus</big>\n\n<b>pupulus, i</b> <i>m</i> [<i>demin. к</i> pupus]\n<b>1)</b> мальчонка <i>Ctl, Sen</i>;\n<b>2)</b> куколка, статуэтка <i>Eccl</i>."],
  ["pupus", "<big>pupus</big>\n\n<b>pupus, i</b> <i>m</i>\n<b>1)</b> мальчик <i>V, Su</i>;\n<b>2)</b> <i>Eccl</i> = pupula 2."],
  ["pura", "<big>pura</big>\n\n<b>I pura</b> <i>f к</i>\npurus.\n\n<b>II pura</b> <i>pl. к</i>\npus."],
  ["pure", "<big>pure</big>\n\n<b>pure</b> [purus]\n<b>1)</b> начисто (lauta corpora <i>L</i>); чисто, светло (Pario marmore purius <i>H</i>); в чистом виде, ясно (apparere <i>H</i>); безукоризненно, безошибочно, чисто (loqui <i>AG</i>); вполне, совершенно (tranquillare <i>H</i>);\n<b>2)</b> просто, естественно (describere <i>AG</i>);\n<b>3)</b> непорочно, с чистой совестью (p. et caste deos venerari <i>C</i>);\n<b>4)</b> прямо, безоговорочно (suum heredem scribere <i>Dig</i>)."],
  ["purgabilis", "<big>purgabilis</big>\n\n<b>purgabilis, e</b> [purgo]\nлегко очищающийся (castanea <i>PM</i>)."],
  ["purgamen", "<big>purgamen</big>\n\n<b>purgamen, inis</b> <i>n</i> [purgo]\n<b>1)</b> нечистота, отбросы, грязь <i>O</i>;\n<b>2)</b> очищение, очистительное средство (p. mali, caedis <i>O</i>);\n<b>3)</b> чистота, блеск (praenitere purgamine <i>Eccl</i>)."],
  ["purgamentum", "<big>purgamentum</big>\n\n<b>purgamentum, i</b> <i>n</i> (= purgamen) <i>тж.</i> <i>pl.</i>\nсор, мусор, грязь (urbis <i>L</i>; cenae <i>PM</i>; purgamenta scopis everrere <i>Pt</i>); сорная трава, сорняк (curare purgamenta pro frugibus <i>Sen</i>); <i>бран.</i> (<i>преим.</i> <i>pl.</i>) подонки, отбросы (purgamenta urbium suarum <i>QC</i>; p. dedecusque <i>Pt</i>)."],
  ["purgatio", "<big>purgatio</big>\n\n<b>purgatio, onis</b> <i>f</i> [purgo]\n<b>1)</b> чистка, очистка (cloacarum <i>PJ</i>); очищение (menstrua <i>PM</i>): p. (alvi) <i>Cato, C, CC</i> очистка кишечника;\n<b>2)</b> <i>pl.</i> слабительные средства <i>C</i>;\n<b>3)</b> искупление, снятие вины (peccati <i>Ter</i>; caedis <i>PM</i>);\n<b>4)</b> оправдание, извинение (p. et deprecatio <i>C</i>)."],
  ["purgativus", "<big>purgativus</big>\n\n<b>purgativus, a, um</b> [purgo]\nочистительный, <i>т. е.</i> слабительный (medicamina <i>CA</i>)."],
  ["purgator", "<big>purgator</big>\n\n<b>purgator, oris</b> <i>m</i>\nчистильщик, производящий очистку (cloacarum <i>Eccl</i>): p. ferarum <i>Ap</i> очищающий (край) от диких зверей, <i>т. е.</i> Геркулес; p. animae <i>Aug</i> очищающий душу."],
  ["purgatorium", "<big>purgatorium</big>\n\n<b>purgatorium, i</b> <i>n</i> <i>Eccl</i>\n= purgatorius ignis (<i>см.</i>)."],
  ["purgatorius", "<big>purgatorius</big>\n\n<b>purgatorius, a, um</b>\n<b>1)</b> очистительный, слабительный (medicamentum <i>Symm</i>);\n<b>2)</b> очищающий (virtutes <i>Macr</i>; poenae <i>Aug</i>); p. ignis <i>Aug</i> чистилище."],
  ["purgatrix", "<big>purgatrix</big>\n\n<b>purgatrix, icis</b> <i>f</i>\nочищающая (aqua <i>Tert</i>)."],
  ["purgatus", "<big>purgatus</big>\n\n<b>purgatus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> purgo;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> чистый, непорочный (purgatiora vota <i>Aug</i>);\n<b>2)</b> оправданный (purgatiores dimittuntur <i>Sl</i>)."],
  ["purgito", "<big>purgito</big>\n\n<b>purgito, —, —, are</b> [<i>intens. к</i> purgo]\nчистить, очищать (angiporta <i>Pl</i> — <i>v. l.</i>)."],
  ["purgo", "<big>purgo</big>\n\n<b>purgo, avi, atum, are</b> [<i>из</i> *purigo <i>от</i> purus + ago]\n<b>1)</b> чистить, очищать (cloacas <i>Dig etc.</i>; urbem <i>C</i>); перебирать (pisum in lance <i>Pt</i>); расчищать (viam <i>Dig</i>); прочищать, слабить (radix multum valet ad purgandum <i>C</i>);\n<b>2)</b> искупать (nefas <i>O</i>); заглаживать (malum facinus forti facinore <i>L</i>);\n<b>3)</b> оправдывать <i>или</i> оправдываться (в чём-л.) (p. facinus <i>QC</i>; dedecora alicujus <i>Eutr</i>); опровергать (crimen <i>C</i>): aliquem de aliqua re <i>или</i> alicujus rei p. <i>C etc.</i> оправдывать кого-л. в чём-л.; p. se alicui <i>C</i> оправдываться (защищать себя) перед кем-л.; innocentiam suam p. <i>L</i> доказать свою невиновность;\n<b>4)</b> очищать, освобождать: p. ravim <i>Ap</i> излечиваться от хрипоты; purgari morbi <i>H</i> излечиться от болезни; auris purgata <i>H</i> готовность слушать; p. rationes <i>Su</i> регулировать счёты, учинять расчёт, расплачиваться сполна; in aethera se purgat nubes <i>V</i> (разорвавшееся) облако показало ясность неба;\n<b>5)</b> убирать (rudera <i>Su</i>; sordes <i>Cld</i>); удалять, устранять (metum doloris <i>Q</i>): purgata suspicione <i>Ap</i> по устранении (взаимных) подозрений."],
  ["purificatio", "<big>purificatio</big>\n\n<b>purificatio, onis</b> <i>f</i> [purifico]\nочищение <i>PM, M</i>."],
  ["purifico", "<big>purifico</big>\n\n<b>purifico, avi, atum, are</b> [purus + facio]\n<b>1)</b> очищать (aliquid aqua <i>PM</i>; se p. ab aliqua re <i>Su</i> <i>или</i> aliqua re <i>PM</i>);\n<b>2)</b> искупать, очищать (se ab aliqua re <i>Su</i>)."],
  ["purigo", "<big>purigo</big>\n\n<b>purigo</b> <i>Pl, Vr</i>\n= purgo."],
  ["puris", "<big>puris</big>\n\n<b>I puris</b> <i>gen. к</i>\npus.\n\n<b>II puris</b> <i>dat. pl. к</i>\npurus."],
  ["puritas", "<big>puritas</big>\n\n<b>I puritas, atis</b> <i>f</i> [purus] <i>тж.</i> <i>pl.</i>\nчистота (vini <i>Pall</i>; aquarum <i>Eccl</i>; <i>перен.</i> sermonis <i>Hier</i>; vivendi <i>Capit</i>).\n\n<b>II puritas, atis</b> <i>f</i> [pus]\nнагноение <i>CA</i>."],
  ["puriter", "<big>puriter</big>\n\n<b>puriter</b> <i>Cato, Ctl</i>\n= pure."],
  ["purporiss-", "<big>purporiss-</big>\n\n<b>purporiss-</b> <i>v. l.</i>\n= purpuriss-."],
  ["purpura", "<big>purpura</big>\n\n<b>purpura, ae</b> <i>f</i>\n<b>1)</b> пурпурная улитка, багрянка <i>PM</i>;\n<b>2)</b> пурпуровый цвет <i>V, H, Sen etc.</i>;\n<b>3)</b> пурпуровая ткань <i>Col, Q, Su etc.</i>; пурпуровая одежда, багряница, порфира (cum purpura et sceptro <i>C</i>): septima p. <i>Fl</i> седьмое консульство (<i>см.</i> purpurissatus 2)."],
  ["purpuraria", "<big>purpuraria</big>\n\n<b>purpuraria, ae</b> <i>f</i>\nпродавщица багряниц <i>Vlg</i>."],
  ["Purpurariae insulae", "<big>Purpurariae insulae</big>\n\n<b>Purpurariae insulae</b> <i>f</i>\nПурпурные острова (<i>ныне</i> Madeira <i>и</i> Porto Santo) <i>PM</i>."],
  ["purpurarius", "<big>purpurarius</big>\n\n<b>purpurarius, a, um</b> [purpura]\nприготовляющий пурпур (officina <i>PM</i>)."],
  ["purpurasco", "<big>purpurasco</big>\n\n<b>purpurasco, —, —, ere</b> [purpuro]\nбагроветь, покрываться пурпуром, румяниться <i>C, Vop</i>."],
  ["purpuratus", "<big>purpuratus</big>\n\n<b>I purpuratus, a, um</b> [purpura]\nодетый в пурпурную ткань (mulier <i>Pl</i>).\n\n<b>II purpuratus, i</b> <i>m</i>\nпридворный, царедворец <i>C, L, QC</i>."],
  ["purpureus", "<big>purpureus</big>\n\n<b>purpureus, a, um</b> [purpura]\n<b>1)</b> пурпуровый, багряный, алый, пурпурно-фиолетовый (vestitus <i>C</i>; sanguis <i>Sil</i>; flos <i>H, PM</i>); легко краснеющий (pudor <i>O</i>): purpurea anima <i>V</i> алая кровь || тёмный (fluctus <i>Prp</i>; mare <i>C, V</i>);\n<b>2)</b> сияющий, блестящий, прекрасный (olor <i>H</i>; ver <i>V</i>);\n<b>3)</b> одетый в пурпур, облачённый в багряницу (reges <i>Cld</i>); устланный, покрытый пурпуром (torus <i>O</i>)."],
  ["purpurissatus", "<big>purpurissatus</big>\n\n<b>purpurissatus, a, um</b> [purpurissum]\n<b>1)</b> нарумяненный (buccae <i>Pl</i>; genae <i>Ap</i>);\n<b>2)</b> консульский (<i>так как консулы были одеты в пурпур</i>) (fasti <i>Sid</i>)."],
  ["purpurissum", "<big>purpurissum</big>\n\n<b>purpurissum, i</b> <i>n</i>\n<b>1)</b> тёмная пурпурная краска <i>PM</i>;\n<b>2)</b> румяна <i>Pl, Eccl</i>."],
  ["purpurissus", "<big>purpurissus</big>\n\n<b>purpurissus, i</b> <i>m</i> <i>Hier</i>\n= purpurissum."],
  ["purpuro", "<big>purpuro</big>\n\n<b>purpuro, avi, atum, are</b> [purpura]\n<b>1)</b> окрашивать в пурпур (aliquid <i>AG</i>); обагрять (terra purpurata, <i>sc.</i> sanguine <i>Aug</i>);\n<b>2)</b> украшать, расцвечивать (omnia rosis <i>Ap</i>);\n<b>3)</b> быть пурпурным, багряным (maculae purpurantes <i>Vop</i>)."],
  ["purulenta", "<big>purulenta</big>\n\n<b>purulenta, orum</b> <i>n</i> [purulentus]\n<b>1)</b> гной <i>PM, Sen</i>;\n<b>2)</b> гноящиеся места, нагноения <i>CC, PM</i>."],
  ["purulentatio", "<big>purulentatio</big>\n\n<b>purulentatio, onis</b> <i>f</i>\nнагноение <i>CA</i>."],
  ["purulente", "<big>purulente</big>\n\n<b>purulente</b> <i>adv.</i>\nв виде гноя (ossa p. limosa <i>PM</i>)."],
  ["purulentia", "<big>purulentia</big>\n\n<b>purulentia, ae</b> <i>f</i>\nгной <i>Hier, Tert</i>."],
  ["purulentus", "<big>purulentus</big>\n\n<b>purulentus, a, um</b> [pus]\nгноящийся (aures <i>PM</i>; vulnus <i>Sen</i>); гнойный (sputum <i>CC</i>)."],
  ["purum", "<big>purum</big>\n\n<b>purum, i</b> <i>n</i> [purus]\n<b>1)</b> чистое, ясное небо <i>V, H</i>;\n<b>2)</b> чистая прибыль <i>C</i>."],
  ["purus", "<big>purus</big>\n\n<b>purus, a, um</b>\n<b>1)</b> чистый (manus <i>Tib</i>; aqua <i>H</i>); чистый, беспримесный (aurum <i>PM</i>), <i>но</i>: argentum purum <i>C</i> (<i>ср.</i> 6) столовая посуда без украшений, <i>т. е.</i> гладкая; p. (ab) aliqua re <i>или</i> alicujus rei <i>C, H etc.</i> чистый (свободный) от чего-л.;\n<b>2)</b> пустой, незастроенный, открытый (ager <i>O</i>); безволосый (genae <i>SenT</i>); неисписанный (charta <i>Dig</i>); незаросший, голый: p. ab arboribus безлесный (campus <i>O</i>);\n<b>3)</b> ясный, светлый (sol <i>H</i>; dies <i>Cld</i>; aether <i>C</i>);\n<b>4)</b> отдавший последний долг, <i>т. е.</i> снявший с себя траур (familia <i>C</i>);\n<b>5)</b> нетронутый, девственный (locus <i>L</i>);\n<b>6)</b> простой, без украшений (parma <i>V</i>): hasta pura <i>V</i> копьё без железного наконечника (<i>знак боевого отличия и знак достоинства повелителей и жрецов</i>); vestis pura <i>Ctl</i> = toga virilis;\n<b>7)</b> безупречный, душевно чистый, незапятнанный, невинный (animus <i>C</i>): sceleris p. <i>H</i> незапятнанный (никаким) преступлением;\n<b>8)</b> естественный, бесхитростный, незатейливый (oratio <i>C</i>);\n<b>9)</b> безоговорочный, безусловный (judicium <i>C</i>);\n<b>10)</b> безошибочный, безукоризненный (sermo <i>Cs, Q, PJ</i>);\n<b>11)</b> очищающий (sulphur <i>Tib</i>)."],
  ["pus", "<big>pus</big>\n\n<b>pus, puris</b> <i>n</i>\n<b>1)</b> гной, сукровица <i>CC, PM</i>;\n<b>2)</b> язвительная речь (p. atque venenum <i>H</i>)."],
  ["pusa", "<big>pusa</big>\n\n<b>pusa, ae</b> <i>f</i> [pusus]\nмаленькая девочка <i>Vr</i>."],
  ["pusca", "<big>pusca</big>\n\n<b>pusca, ae</b> <i>f</i> <i>Veg</i>\n= posca."],
  ["pusillanimis", "<big>pusillanimis</big>\n\n<b>pusill–animis, e</b>\nмалодушный <i>Tert, Sid</i>."],
  ["pusillanimitas", "<big>pusillanimitas</big>\n\n<b>pusillanimitas, atis</b> <i>f</i>\nмалодушие <i>Lact</i>."],
  ["pusillanimus", "<big>pusillanimus</big>\n\n<b>pusillanimus, a, um</b> <i>Vlg, Eccl</i>\n= pusillanimis."],
  ["pusillitas", "<big>pusillitas</big>\n\n<b>pusillitas, atis</b> <i>f</i>\nмалость, ничтожность (intelligentiae <i>Lact</i>; mea p. <i>Hier</i>)."],
  ["pusillulus", "<big>pusillulus</big>\n\n<b>pusillulus, a, um</b> [<i>demin. к</i> pusillus]\nсовсем маленький, крохотный (vinea <i>Ambr</i>)."],
  ["pusillum", "<big>pusillum</big>\n\n<b>pusillum, i</b> <i>n</i>\nнемножко, чуточку (p. laxamenti <i>C</i>; p. insaniae <i>Sen</i>; p. requiescere <i>Vlg</i>)."],
  ["pusillus", "<big>pusillus</big>\n\n<b>pusillus, a, um</b> [<i>demin. к</i> pusus]\n<b>1)</b> крохотный, крошечный (puer <i>Cato</i>; epistula <i>C</i>; pyxis non pusilla <i>Pt</i>);\n<b>2)</b> слабый, тихий (vox <i>Q</i>);\n<b>3)</b> мелкий, незначительный (animus <i>C</i> — <i>ср.</i> 4; ingenium <i>M</i>); ничтожный, малодушный (homo <i>C etc.</i>);\n<b>4)</b> слабый, бесплодный (animus <i>H</i> — <i>ср.</i> 3)."],
  ["pusio", "<big>pusio</big>\n\n<b>pusio, onis</b> <i>m</i> [pusus]\nмаленький мальчик, карапуз <i>C, Eccl</i>; <i>ирон.</i> молодчик (bellissimus ille p. <i>Ap</i>)."],
  ["pusiola", "<big>pusiola</big>\n\n<b>pusiola, ae</b> <i>f</i> [<i>demin. к</i> pusa]\nдевчоночка <i>Eccl</i>."],
  ["pussula", "<big>pussula</big>\n\n<b>pussula, ae</b> <i>f</i> <i>Tib</i>\n(<i>v. l.</i>) = pustula."],
  ["pustula", "<big>pustula</big>\n\n<b>pustula, ae</b> <i>f</i>\n<b>1)</b> пузырь (calx pustulas emittit <i>Vtr</i>);\n<b>2)</b> <i>мед.</i> волдырь, гнойник, прыщ (eruptio pustularum <i>Sen</i> — <i>v. l.</i>);\n<b>3)</b> пузырёк из чистого серебра, <i>перен.</i> чистое серебро <i>M</i>."],
  ["pustulatio", "<big>pustulatio</big>\n\n<b>pustulatio, onis</b> <i>f</i> [pustula]\nпоявление волдырей <i>CA</i>."],
  ["pustulatus", "<big>pustulatus</big>\n\n<b>pustulatus, a, um</b> [pustula 3]\nпузырчатый, <i>т. е.</i> очищенный на огне: argentum pustulatum <i>Su, M, Dig</i> чистое серебро."],
  ["pustulesco", "<big>pustulesco</big>\n\n<b>pustulesco, —, —, ere</b>\nпокрываться волдырями <i>CA</i>."],
  ["pustulo", "<big>pustulo</big>\n\n<b>pustulo, —, —, are</b> [pustula]\n<i>тж.</i> <i>med.</i> покрываться пузырями, образовывать волдыри (concha pustulat <i>Tert</i>; usta pustulantur <i>CA</i>)."],
  ["pustulosus", "<big>pustulosus</big>\n\n<b>pustulosus, a, um</b> <i>CC</i>\n= pusulosus."],
  ["pusula", "<big>pusula</big>\n\n<b>pusula, ae</b> <i>f</i> <i>CC, Col, PM, Sen</i>\n= pustula."],
  ["pusulatus", "<big>pusulatus</big>\n\n<b>pusulatus, a, um</b> <i>Dig</i>\n= pustulatus."],
  ["pusulosus", "<big>pusulosus</big>\n\n<b>pusulosus, a, um</b> [pusula]\nпокрытый волдырями, в гнойниках (pecus <i>Col</i>)."],
  ["pusus", "<big>pusus</big>\n\n<b>pusus, i</b> <i>m</i> [puer]\nмаленький мальчик <i>Vr</i>."],
  ["puta", "<big>puta</big>\n\n<b>puta</b> <i>adv.</i> [<i>imper. к</i> puto II] (<i>тж.</i> ut p.)\nнапример <i>H, CC, Sen etc.</i>"],
  ["putamen", "<big>putamen</big>\n\n<b>putamen, inis</b> <i>n</i> [puto I]\nскорлупа (ovi <i>PM, Pt</i>); шелуха, кожица (fabae <i>PM, Pt</i>; juglandium <i>C</i>)."],
  ["putatio", "<big>putatio</big>\n\n<b>I putatio, onis</b> <i>f</i> [puto I]\nобрезывание, обстригание, подчистка (arborum ac vitium <i>C</i>).\n\n<b>II putatio, onis</b> <i>f</i> [puto II]\n<b>1)</b> подсчёт, расчёт <i>Macr</i>;\n<b>2)</b> мнение <i>Eccl</i>: p. personae <i>Dig</i> принятие одного человека за другого."],
  ["putative", "<big>putative</big>\n\n<b>putative</b>\nошибочно, мнимо <i>Eccl</i>."],
  ["putativus", "<big>putativus</big>\n\n<b>putativus, a, um</b>\nвоображаемый, мнимый <i>Eccl</i>."],
  ["putator", "<big>putator</big>\n\n<b>putator, oris</b> <i>m</i> [puto I]\nобрезывающий, подчищающий деревья <i>Vr, Col, PM</i>."],
  ["putatorius", "<big>putatorius</big>\n\n<b>putatorius, a, um</b> [puto I]\nслужащий для подстригания, <i>т. е.</i> садовый (falces <i>Pall</i>)."],
  ["puteal", "<big>puteal</big>\n\n<b>puteal, alis</b> <i>n</i> [puteus]\n<b>1)</b> каменная ограда вокруг колодца <i>C, Dig</i>;\n<b>2)</b> путеал, <i>поражённое молнией, а потому освящённое и огороженное место</i>: p. (Libonis <i>или</i> Scribonianum) <i>C, H, O, Pers</i> путеал на римском форуме (<i>вблизи преторского судилища и римской биржи</i>)."],
  ["putealis", "<big>putealis</big>\n\n<b>putealis, e</b> [puteus]\nколодезный (fons <i>Col</i>; unda <i>O, Col</i>)."],
  ["puteanus", "<big>puteanus</big>\n\n<b>puteanus, a, um</b> <i>Col, PM</i>\n= putealis."],
  ["putearius", "<big>putearius</big>\n\n<b>putearius, i</b> <i>m</i> [puteus]\nколодезный мастер <i>PM</i>."],
  ["putefac-", "<big>putefac-</big>\n\n<b>putefac-</b> <i>Pl</i>\n= putrefac-."],
  ["puteo", "<big>puteo</big>\n\n<b>puteo, ui, —, ere</b> [pus]\nпахнуть гнилью (tigna putent <i>Pl</i>); издавать зловоние (aper putet <i>H</i>): pisces putentes <i>Scr</i> тухлая рыба; p. vino <i>H</i> издавать запах вина."],
  ["Puteolanum", "<big>Puteolanum</big>\n\n<b>Puteolanum, i</b> <i>n</i>\nПутеоланы, <i>имение Цицерона близ города Puteoli</i> <i>C</i>."],
  ["Puteolanus", "<big>Puteolanus</big>\n\n<b>I Puteolanus, a, um</b> [Puteoli]\nпутеоланский <i>C</i>: pulvis P. <i>Sen</i> пуццолан (<i>вулканическая порода</i>).\n\n<b>II Puteolanus, i</b> <i>m</i>\nжитель города Puteoli <i>C</i>."],
  ["Puteoli", "<big>Puteoli</big>\n\n<b>Puteoli, orum</b> <i>m</i>\nПутеолы, <i>приморский город в Кампании, между Неаполем и Кумами</i> (<i>ныне</i> Pozzuoli) <i>Vr, C, L etc.</i>"],
  ["puter", "<big>puter</big>\n\n<b>puter (putris), tris, tre</b> [pus]\n<b>1)</b> гниющий, гнилой, прогнивший (fanum <i>H</i>; poma <i>O</i>); гноящийся (ulcus <i>CC</i>);\n<b>2)</b> трухлявый, хрупкий (saxa <i>Sen</i>); рыхлый (solum <i>V</i>); сыпучий (arena <i>Col</i>);\n<b>3)</b> увядший, дряблый, вялый (mammae <i>H</i>); дряхлый (anima <i>Prp</i>);\n<b>4)</b> тусклый, мутный (oculi <i>H</i>): in Venerem p. <i>Pers</i> утопающий в разврате."],
  ["putesco", "<big>putesco</big>\n\n<b>putesco, tui, —, ere</b> [<i>inchoat. к</i> puteo]\nзагнивать, гнить, разлагаться <i>Cato, Lcr, C etc.</i>"],
  ["puteum", "<big>puteum</big>\n\n<b>puteum, i</b> <i>n</i> <i>Vr, Dig</i>\n= puteus."],
  ["puteus", "<big>puteus</big>\n\n<b>puteus, i</b> <i>m</i>\n<b>1)</b> яма (puteum demittere <i>V</i>);\n<b>2)</b> копь, рудник, шахта <i>PM</i>;\n<b>3)</b> колодец (puteum fodere <i>Pl</i>): in puteum conjicere <i>погов. Pt</i> бросать на ветер, тратить попусту; ad summum puteum geri <i>погов. Pl</i> быть на краю пропасти;\n<b>4)</b> подземелье, подземная тюрьма <i>Pl</i>."],
  ["puticuli", "<big>puticuli</big>\n\n<b>puticuli, orum</b> <i>m</i> <i>и</i> <b>puticulae, arum</b> <i>f</i> [puteus]\nмогилы бедняков и рабов (<i>на Эскеилинском холме</i>) <i>Vr</i>."],
  ["putide", "<big>putide</big>\n\n<b>putide</b> [putidus]\nнудно, до тошноты, надоедливо (loqui <i>Sen</i>; litteras exprimere <i>C</i>)."],
  ["putidiusculus", "<big>putidiusculus</big>\n\n<b>putidiusculus, a, um</b> [<i>demin. к</i> putidus]\nдовольно нудный, скучный <i>C</i>."],
  ["putidulus", "<big>putidulus</big>\n\n<b>putidulus, a, um</b> [<i>demin. к</i> putidus]\nпротивный, тошнотворный, надоедливый <i>M, Capit</i>."],
  ["putidus", "<big>putidus</big>\n\n<b>putidus, a, um</b> [puteo]\n<b>1)</b> гнилой, тухлый (caro <i>C</i>); прогнивший (navis <i>Caec</i>); прокисший (vinum <i>Pl</i>); зловонный (aper <i>M</i>);\n<b>2)</b> дряблый, одряхлевший (homo <i>Pl</i>);\n<b>3)</b> расстроенный, помешанный (cerebrum <i>H</i>);\n<b>4)</b> нудный, тошный, докучный, надоедливый (jactatio <i>Pt</i>);\n<b>5)</b> вычурный, неестественный (orator <i>C</i>)."],
  ["putillus", "<big>putillus</big>\n\n<b>putillus, i</b> <i>m</i> [<i>demin. к</i> putus II]\n<b>1)</b> маленький мальчик, крошка <i>Pl</i>;\n<b>2)</b> птенчик <i>Vr</i>."],
  ["putisco", "<big>putisco</big>\n\n<b>putisco</b> <i>v. l.</i>\n= putesce."],
  ["puto", "<big>puto</big>\n\n<b>I puto, avi, atum, are</b> [<i>одного корня с</i> putus I]\n<b>1)</b> чистить, очищать (vellus <i>Vr</i>);\n<b>2)</b> подрезывать, подстригать, подчищать (vites <i>V</i>; arbores <i>Cato</i>).\n\n<b>II puto, avi, atum, are</b> [putus I]\n<b>1)</b> размышлять, обдумывать, взвешивать (aliquid <i>C etc.</i>): rem ipsam putemus <i>Ter</i> обсудим дело по существу;\n<b>2)</b> сводить счёты, производить расчёты, подсчитывать, рассчитываться (p. rationes cum aliquo <i>Cato, C etc.</i>);\n<b>3)</b> оценивать (statuam denariis quadringentis <i>C</i>);\n<b>4)</b> считать, рассматривать, ставить (aliquid magni, nihili <i>или</i> pro nihilo <i>C etc.</i>; aliquem hostium numero p. <i>C</i>): nullius momenti putari <i>C</i> не ставиться ни во что;\n<b>5)</b> предполагать, полагать, думать: quid illum putas? <i>Pl</i> что ты о нём думаешь?; putes, putasses <i>или</i> putares <i>Pt</i> можно было бы подумать; stare putes, adeo procedunt tempora tarde <i>O</i> время тянется так медленно, что кажется, будто оно остановилось; puta <i>Pt</i> (= finge) вообрази, представь себе, допустим; nec est quod putes illum quicquam emere <i>Pt</i> и не думай, будто ему приходится что-л. покупать; recte (bene) putas <i>Ter</i> ты прав."],
  ["putor", "<big>putor</big>\n\n<b>putor, oris</b> <i>m</i> [puteo]\nгниль, тлен, зловоние <i>Cato, Vr, Lcr etc.</i>"],
  ["putramen", "<big>putramen</big>\n\n<b>putrāmen, inis</b> <i>n</i> [puter]\nгниль <i>Eccl</i>."],
  ["putredo", "<big>putredo</big>\n\n<b>putrēdo, inis</b> <i>f</i> [putreo]\nгниение, нагноение (vulnerum <i>Ap</i>; gingivarum <i>CA</i>)."],
  ["putredulus", "<big>putredulus</big>\n\n<b>putrēdulus, a, um</b> <i>предпол.</i>\nхриплый (voces <i>Amm</i>)."],
  ["putrefacio", "<big>putrefacio</big>\n\n<b>putre–facio, fēcī, factum, ere</b> [putreo + facio]\nделать гнилым, гноить (putrefactus per imbrem <i>Lcr</i>); делать рыхлым, разрыхлять (saxa aceto <i>L</i>)."],
  ["putrefactio", "<big>putrefactio</big>\n\n<b>putrefactio, ōnis</b> <i>f</i> <i>Aug, CA</i>\n= putredo."],
  ["putrefio", "<big>putrefio</big>\n\n<b>putre–fīo, factus sum, fierī</b> [<i>pass. к</i> putrefacio]\nтлеть, гнить <i>Lcr, Vr, L etc.</i>"],
  ["putreo", "<big>putreo</big>\n\n<b>putreo, truī, —, ēre</b> [puter]\nстановиться хрупким, разрушаться, дряхлеть (annis corpus putret <i>Acc, Pac</i>)."],
  ["putrescibilis", "<big>putrescibilis</big>\n\n<b>putrēscibilis, e</b> [putresco]\nподверженный гниению, тленный <i>Hier</i>."],
  ["putresco", "<big>putresco</big>\n\n<b>putrēsco, truī, —, ere</b> [<i>inchoat. к</i> putreo]\nтлеть, истлевать (vestis putrescit <i>H</i>); гнить (dentes putrescunt <i>PM</i>); гноиться (ulcus putrescit <i>CC</i>); становиться рыхлым (solum putrescit <i>Col</i>)."],
  ["putribilis", "<big>putribilis</big>\n\n<b>putribilis, e</b> <i>Aug</i>\n= putrescibilis."],
  ["putridus", "<big>putridus</big>\n\n<b>putridus, a, um</b> [puter]\nгнилой (dens <i>C</i>); увядший, дряблый (pectora <i>Ctl</i>)."],
  ["putris", "<big>putris</big>\n\n<b>putris, e</b> <i>PJ, Pers</i>\n= puter."],
  ["putus", "<big>putus</big>\n\n<b>I putus, a, um</b> [<i>одного корня с</i> purus]\nчистый, беспримесный, подлинный: purus p. настоящий, чистокровный, самый что ни на есть (sycophanta <i>Pl</i>; pondus argenti <i>AG</i>): meae putissimae orationes <i>C</i> лучшие мои речи.\n\n<b>II putus, i</b> <i>m</i> <i>V</i>\n= pusus."],
  ["puxis", "<big>puxis</big>\n\n<b>puxis, idis</b> <i>f</i>\n<i>Scr</i>= pyxis."],
  ["pycnostylos", "<big>pycnostylos</big>\n\n<b>pycnostylos, on</b> (<i>греч.</i>)\nс частыми колоннами (<i>т. е. когда расстояние между двумя смежными колоннами не меньше полуторного сечения каждой</i>) <i>Vtr</i>."],
  ["pycta", "<big>pycta</big>\n\n<b>pycta (pyctes), ae</b> <i>m</i> (<i>греч.</i>; <i>лат.</i> pugil)\n<b>1)</b> кулачный боец <i>PM, Sen</i>;\n<b>2)</b> бойцовый петух <i>Col</i>."],
  ["Pydna", "<big>Pydna</big>\n\n<b>Pydna, ae</b> <i>f</i>\nПидна, <i>город в южн. Македонии близ Термейского залива; место битвы. в 168 г. до н. э., в которой Эмилий Павл нанёс поражение Персею Македонскому</i> <i>Nep, L</i>."],
  ["Pydnaei", "<big>Pydnaei</big>\n\n<b>Pydnaei, orum</b> <i>m</i>\nжители города Pydna <i>L</i>."],
  ["pyelus", "<big>pyelus</big>\n\n<b>pyelus</b> <i>v. l.</i>\n= puelus."],
  ["pyga", "<big>pyga</big>\n\n<b>pyga, ae</b> <i>f</i> (<i>греч.</i>) <i>Naev, H</i>\n= nates."],
  ["pygargus", "<big>pygargus</big>\n\n<b>pygargus, i</b> <i>m</i> (<i>греч.</i>)\n<b>1)</b> орлан-белохвост <i>PM</i>;\n<b>2)</b> вид антилопы <i>PM, J, Vlg</i>."],
  ["pygesiacus", "<big>pygesiacus</big>\n\n<b>pygesiacus, a, um</b> (<i>греч.</i>)\nседалищный, анальный <i>Pt</i>."],
  ["Pygmaei", "<big>Pygmaei</big>\n\n<b>Pygmaei, orum</b> <i>m</i>\nпигмеи, <i>миф. карликовое племя где-то на юге, с которым воевали журавли, перелетавшие туда осенью и постоянно их побеждавшие</i> <i>PM, Mela, AG</i>."],
  ["Pygmaeus", "<big>Pygmaeus</big>\n\n<b>Pygmaeus, a, um</b> [Pygmaei]\n<b>1)</b> Пигмейский (sanguis <i>O</i>);\n<b>2)</b> карликовый (virgo <i>J</i>)."],
  ["Pygmalion", "<big>Pygmalion</big>\n\n<b>Pygmalion, onis</b> <i>m</i>\nПигмалион:\n<b>1)</b> <i>внук кипрского царя Агенора, ваятель, влюбившийся в созданную им самим статую девушки и упросивший Венеру вдохнуть в неё жизнь</i> <i>O</i>;\n<b>2)</b> <i>миф. царь Тира, убийца Сихея, мужа его сестры Дидоны</i> <i>V, Just</i>."],
  ["Pygmalioneus", "<big>Pygmalioneus</big>\n\n<b>Pygmalioneus, a, um</b> [Pygmalion 2]\nПигмалионов, <i>поэт.</i> финикийский, карфагенский <i>Sil</i>."],
  ["Pylades", "<big>Pylades</big>\n\n<b>Pylades, ae</b> <i>и</i> <b>is</b> <i>m</i>\nПилад:\n<b>1)</b> <i>сын фокидского царя Строфия, племянник Агамемнона, верный друг Ореста</i> <i>C, O, M</i>;\n<b>2)</b> <i>танцовщик («пантомим») из Киликии времён Августа</i> <i>Su, Sen</i>."],
  ["Pyladeus", "<big>Pyladeus</big>\n\n<b>Pyladeus, a, um</b> [Pylades 1]\nпиладов (amicitia <i>C</i>)."],
  ["Pylae", "<big>Pylae</big>\n\n<b>Pylae, arum</b> <i>f</i> (<i>греч.</i>)\nворота, ущелье, теснины: P. Tauri <i>C</i> <i>или</i> Amanicae <i>QC</i> Таврские ворота (<i>горный проход между Каппадокией и Киликией</i>); P. Susides <i>QC</i> Сузские ворота (<i>в Персии</i>); P. Caspiae <i>Mela</i> Каспийские ворота (<i>горный перевал между Мидией и Гирканией</i>); <i>иногда</i> Pylae <i>L</i> = Thermopylae."],
  ["Pylaemenes", "<big>Pylaemenes</big>\n\n<b>Pylaemenes, is</b> <i>m</i>\nПилемен, <i>миф. царь Пафлагонии, союзник Приама, убитый Менелаем</i> <i>L, Nep</i>."],
  ["Pylaemenius", "<big>Pylaemenius</big>\n\n<b>Pylaemenius, a, um</b> [Pylaemenes]\nпилеменов, <i>перен.</i> пафлагонский (gens <i>PM</i>)."],
  ["Pylaicus", "<big>Pylaicus</big>\n\n<b>Pylaicus, a, um</b> [Pylae = Thermopylae]\nпилейский, <i>т. е.</i> фермопильский (concilium, conventus <i>L</i>)."],
  ["Pylene", "<big>Pylene</big>\n\n<b>Pylene, es</b> <i>f</i>\nПилена, <i>город в Этолии</i> <i>St</i>."],
  ["Pylius", "<big>Pylius</big>\n\n<b>I Pylius, a, um</b> [Pylus]\n<b>1)</b> пилосский (Nestor <i>H</i>);\n<b>2)</b> <i>поэт.</i> чрезвычайно старый, древний (senecta <i>M</i>);\n<b>3)</b> мессенский (agri <i>O</i>).\n\n<b>II Pylius, i</b> <i>m</i>\nпилосец, <i>т. е.</i> Нестор <i>O</i>."],
  ["pylorus", "<big>pylorus</big>\n\n<b>pylorus, i</b> <i>m</i> (<i>греч.</i>) <i>анат.</i>\nпривратник <i>CA</i>."],
  ["Pylus", "<big>Pylus</big>\n\n<b>Pylus (-os), i</b> <i>f</i>\nПилос, <i>название трёх городов в Мессении, из них столицей Нестора был, вероятно, Пилос на юго-зап. побережье Мессении против о-ва Сфактерия</i> <i>L, O</i>."],
  ["pyra", "<big>pyra</big>\n\n<b>pyra, ae</b> <i>f</i> (<i>греч.</i>; <i>лат.</i> rogus)\nкостёр <i>V, O, bAfr</i>."],
  ["Pyracmon", "<big>Pyracmon</big>\n\n<b>Pyracmon, onis</b> <i>m</i>\nПиракмон, <i>один из циклопов, работавших у Вулкана</i> <i>V, Cld</i>."],
  ["pyramidalis", "<big>pyramidalis</big>\n\n<b>pyramidalis, e</b>\nпирамидальный <i>Boet</i>."],
  ["pyramidatus", "<big>pyramidatus</big>\n\n<b>pyramidatus, a, um</b> [pyramis]\nпирамидальный (corpuscula <i>C</i> — <i>v. l.</i>)."],
  ["pyramis", "<big>pyramis</big>\n\n<b>pyramis, idis</b> (<i>acc. pl.</i> idas) <i>f</i> (<i>греч.</i>)\nпирамида <i>C, PM, T, Prp etc.</i>"],
  ["Pyramus", "<big>Pyramus</big>\n\n<b>Pyramus, i</b> <i>m</i>\nПирам:\n<b>1)</b> <i>вавилонский юноша, возлюбленный Тисбы</i> <i>O</i>;\n<b>2)</b> <i>река в вост. Киликии</i> <i>C, QC</i>."],
  ["Pyrenaeus", "<big>Pyrenaeus</big>\n\n<b>I Pyrenaeus, a, um</b> [Pyrene]\nпиренейский: P. saltus <i>Cs</i> (mons <i>Sil, Mela</i>) <i>или</i> Pyrenaei saltus <i>Cs</i> (montes <i>Cs, L</i>) Пиренейские горы.\n\n<b>II Pyrenaeus, i</b> <i>m</i> (<i>sc.</i> saltus)\nПиренейские горы <i>L, Sen, PM etc.</i>"],
  ["Pyrenaicus", "<big>Pyrenaicus</big>\n\n<b>Pyrenaicus</b> <i>Aus</i>\n= Pyrenaeus I."],
  ["Pyrene", "<big>Pyrene</big>\n\n<b>Pyrene, es</b> <i>f</i>\n<b>1)</b> Пирена, <i>дочь царя Бебрика</i> (<i>см.</i> Bebryx), <i>возлюбленная Геркулеса, похороненная под горами, которые получили от неё своё название</i> <i>Sil</i>;\n<b>2)</b> Пиренейские горы <i>Tib, Lcn</i>;\n<b>3)</b> <i>Sil</i> = Hispania."],
  ["Pyreneus", "<big>Pyreneus</big>\n\n<b>Pyrēne͡us, eī</b> <i>и</i> <b>eos</b> <i>m</i>\nПиреней, <i>миф. царь Фракии, завоевавший Давлиду (Daulis) в Фокиде, но погибший при попытке преследования Муз</i> <i>O</i>."],
  ["pyrethrum", "<big>pyrethrum</big>\n\n<b>pyrethrum (pyrethron), i</b> <i>n</i> (<i>греч.</i>) <i>бот.</i>\nпиретрум, жигунец <i>PM, O, CC etc.</i>"],
  ["Pyrgensis", "<big>Pyrgensis</big>\n\n<b>Pyrgensis, e</b> [Pyrgi]\nпиргейский <i>C, L</i>."],
  ["Pyrgi", "<big>Pyrgi</big>\n\n<b>Pyrgi, orum</b> <i>m</i> (<i>греч.</i> «башни»)\nПирги, <i>приморский город в южн. Этрурии</i> <i>V, L etc.</i>"],
  ["Pyrgo", "<big>Pyrgo</big>\n\n<b>Pyrgo, us</b> <i>f</i>\nПирго, <i>кормилица детей Приама</i> <i>V</i>."],
  ["Pyrgopolinices", "<big>Pyrgopolinices</big>\n\n<b>Pyrgopolinices, is</b> <i>m</i> (<i>греч.</i> «побеждающий башни и города»)\nПиргополиник, <i>комич. имя воина</i> <i>Pl</i>."],
  ["pyrgus", "<big>pyrgus</big>\n\n<b>pyrgus, i</b> <i>m</i> (<i>греч.</i>)\nдеревянная башенка со ступеньками, сквозь которую бросались на доску игральные кости <i>Sid</i>."],
  ["Pyriphlegethon", "<big>Pyriphlegethon</big>\n\n<b>Pyriphlegethon, ontis</b> <i>m</i> <i>C</i>\n= Phlegeton."],
  ["Pyrois", "<big>Pyrois</big>\n\n<b>Pyrois (Pyroeis), roentis</b> <i>m</i> (<i>греч.</i> «огненный»)\nПироэнт:\n<b>1)</b> планета Марс <i>Col, Aus, Ap</i>;\n<b>2)</b> <i>один из коней солнечной колесницы</i> <i>O</i>."],
  ["pyropus", "<big>pyropus</big>\n\n<b>pyropus, i</b> <i>m</i> (<i>греч.</i>)\nзолотая бронза (<i>три — по друг., четыре — части меди и одна часть золота</i>) <i>Lcr, Prp, O, PM</i>."],
  ["Pyrrha", "<big>Pyrrha</big>\n\n<b>Pyrrha, ae</b> <i>f</i>\nПирра:\n<b>1)</b> <i>дочь Эпиметея, жена Девкалиона</i> <i>H, O</i>;\n<b>2)</b> <i>имя Ахилла, переодетого девушкой</i> <i>Sid</i>;\n<b>3)</b> <i>город в зап. части о-ва Лесбос</i> <i>PM</i>."],
  ["Pyrrhaeus", "<big>Pyrrhaeus</big>\n\n<b>Pyrrhaeus, a, um</b> [Pyrrha 1]\nпиррин: Pyrrhaea saxa <i>St</i> камни, которые бросали за спину Пирра и Девкалион (и из которых родилось новое поколение людей)."],
  ["Pyrrhe", "<big>Pyrrhe</big>\n\n<b>Pyrrhe, es</b> <i>f</i> <i>SenT</i>\n= Pyrrha."],
  ["Pyrrhias", "<big>Pyrrhias</big>\n\n<b>I Pyrrhias, adis</b> <i>f</i> [Pyrrha 3]\nпиррейский (puellae <i>O</i>).\n\n<b>II Pyrrhias, ae</b> <i>m</i>\nПиррий, <i>военачальник этолян</i> <i>L</i>."],
  ["pyrrhicha", "<big>pyrrhicha</big>\n\n<b>pyrrhicha, ae</b> <i>и</i> <b>pyrrhiche, es</b> <i>f</i> (<i>греч.</i>)\nпирриха, военная пляска лакедемонян (<i>в вооружении</i>) <i>PM, Su, Spart etc.</i>"],
  ["pyrrhichius", "<big>pyrrhichius</big>\n\n<b>pyrrhichius, ī</b> <i>m</i> (<i>греч.</i>)\nпиррихий, стихотворная стопа U U <i>Q</i>."],
  ["pyrrhichus", "<big>pyrrhichus</big>\n\n<b>pyrrhic(h)us, a, um</b> [pyrrhicha]\nпиррихийский (ars <i>Sid</i>)."],
  ["Pyrrhidae", "<big>Pyrrhidae</big>\n\n<b>Pyrrhidae, arum</b> <i>m</i>\n<i>жители царства, основанного Пирром</i> (<i>см.</i> Pyrrhus 1), <i>т. е.</i> эпироты <i>Just</i>."],
  ["Pyrrho", "<big>Pyrrho</big>\n\n<b>Pyrrho, onis</b> <i>m</i>\nПиррон, <i>родом из Элиды, современник Аристотеля, основатель скептической философской школы</i> <i>C</i>."],
  ["Pyrrhoneus", "<big>Pyrrhoneus</big>\n\n<b>I Pyrrhoneus (Pyrrhonius), a, um</b> [Pyrrho]\nпирронов <i>AG</i>.\n\n<b>II Pyrrhoneus (Pyrrhonius), i</b> <i>m</i>\nпирроновец, <i>философ направления Пиррона</i> <i>C, AG</i>."],
  ["Pyrrhus", "<big>Pyrrhus</big>\n\n<b>Pyrrhus, i</b> <i>m</i>\nПирр:\n<b>1)</b> <i>V, O, Just</i> = Neoptolemus;\n<b>2)</b> <i>царь эпирский; победил римлян в 280 г. до н. э. при Гераклее и в 279 при Аскуле (Апулия), но потерпел от них поражение при Беневенте в 275 г. и погиб при осаде Аргоса в 272 г. до н. э.</i> <i>C, L, Just</i>."],
  ["pyrrich-", "<big>pyrrich-</big>\n\n<b>pyrrich-</b> <i>v. l.</i>\n= pyrrhich-."],
  ["Pythagoras", "<big>Pythagoras</big>\n\n<b>Pythagoras, ae</b> <i>m</i>\nПифагор, <i>греч. философ середины VI в. до н. э., родом из Самоса, основатель пифагорейской школы в Кротоне</i> <i>C, L, Ap etc.</i>: littera <i>или</i> bivium Pythagorae <i>Aus, Lact</i> = <i>T</i> (<i>как символ распутья двух дорог: добродетели и порока</i>)."],
  ["Pythagoreus", "<big>Pythagoreus</big>\n\n<b>I Pythagoreus (-ius), a, um</b> [Pythagoras]\nпифагоров, пифагорейский <i>C etc.</i>: P. pavo <i>Pers</i> павлин, <i>в Которого, по утверждению Пифагора, перенеслась душа Эвфорба, прежде чем переселиться в Пифагора.</i>\n\n<b>II Pythagoreus (-ius), i</b> <i>m</i>\nпифагореец <i>Vr, C</i>."],
  ["Pythagoricus", "<big>Pythagoricus</big>\n\n<b>Pythagoricus, a, um</b> <i>L, Sen, PM</i>\n= Pythagore(i)us."],
  ["pythagorisso", "<big>pythagorisso</big>\n\n<b>pythagorisso, —, —, are</b>\nследовать Пифагору (Plato pythagorissat in plurimis <i>Ap</i>)."],
  ["Pythagorius", "<big>Pythagorius</big>\n\n<b>Pythagorius</b>\n= Pythagoreus I <i>и</i> II."],
  ["pythaules", "<big>pythaules</big>\n\n<b>pythaules, ae</b> <i>m</i> (<i>греч.</i>)\nфлейтист <i>Sen</i>."],
  ["Pythia", "<big>Pythia</big>\n\n<b>I Pythia, ae</b> <i>f</i> [Pytho]\nПифия, <i>жрица Аполлона, изрекавшая предсказания в Дельфийском храме</i> <i>C, Nep etc.</i>\n\n<b>II Pythia, orum</b> <i>n</i>\nПифийские игры, <i>которые с 586 г. до н. э. раз в четыре года (в третьем году каждой Олимпиады) устраивались на Крисейской равнине близ Дельф в честь Аполлона</i> <i>H, O, PM etc.</i>"],
  ["Pythias", "<big>Pythias</big>\n\n<b>Pythias, adis</b> <i>f</i>\nПифиада, <i>имя лукавой служанки</i> <i>Ter, H</i>."],
  ["Pythicus", "<big>Pythicus</big>\n\n<b>Pythicus, a, um</b> <i>L</i>\n= Pythius."],
  ["Pythius", "<big>Pythius</big>\n\n<b>Pythius, a, um</b> [Pytho]\n«пифийский», <i>т. е.</i> дельфийский (Apollo <i>C</i>): Pythia regna <i>Prp</i> = Delphi."],
  ["Pytho", "<big>Pytho</big>\n\n<b>Pytho, us</b> <i>f</i>\nПифо, <i>древнее название области Парнаса в Фокиде, где находился храм и оракул Аполлона Дельфийского</i> (Delphica P. <i>Tib</i>)."],
  ["Python", "<big>Python</big>\n\n<b>I Python, onis (onos)</b> <i>m</i>\nПифон, <i>свирепый дракон, обитавший на Парнасе, убитый Аполлоном</i> <i>O, Prp, Lcn etc.</i>\n\n<b>II python, onis</b> <i>m</i>\n<b>1)</b> (<i>тж.</i> spiritus p. <i>Vlg</i>) пророческий дар (mulier habens pythonem <i>Vlg</i>);\n<b>2)</b> прорицатель (pythones consulere <i>Vlg</i>)."],
  ["pythonicus", "<big>pythonicus</big>\n\n<b>pythonicus, a, um</b> [python II]\nпророческий (spiritus <i>Vlg, Tert</i>)."],
  ["pythonissa", "<big>pythonissa</big>\n\n<b>pythonissa, ae</b> <i>f</i> [python II]\nпрорицательница <i>Vlg</i>."],
  ["pytisma", "<big>pytisma</big>\n\n<b>pytisma, atis</b> <i>n</i> (<i>греч.</i>)\nвыплюнутая жидкость, мокрота <i>Vtr, J</i>."],
  ["pytisso", "<big>pytisso</big>\n\n<b>pytisso, —, —, are</b> (<i>греч.</i>)\nвыплёвывать обратно <i>Ter</i>."],
  ["pyxagathos", "<big>pyxagathos</big>\n\n<b>pyxagathos, i</b> <i>m</i> (<i>греч.</i>)\nопытный кулачный боец <i>M</i>."],
  ["pyxidatus", "<big>pyxidatus</big>\n\n<b>pyxidatus, a, um</b> [pyxis]\nимеющий форму Коробочки <i>PM</i>."],
  ["pyxidicula", "<big>pyxidicula</big>\n\n<b>pyxidicula, ae</b> <i>f</i> [<i>demin. к</i> pyxis]\nмаленькая коробочка <i>CC</i>."],
  ["pyxinum", "<big>pyxinum</big>\n\n<b>pyxinum, i</b> <i>n</i> (<i>греч.</i>)\nмазь (<i>содержащаяся в коробочке</i>) <i>CC</i>."],
  ["pyxis", "<big>pyxis</big>\n\n<b>pyxis, idis</b> (<i>acc. pl.</i> idas) <i>f</i> (<i>греч.</i>)\n<b>1)</b> коробочка, баночка (p. aurea <i>Su</i>, <i>Pt</i>);\n<b>2)</b> обушок пестика (p. ferrea <i>PM</i>)."]
]