export default [
  ["R", "<big>R</big>\n\n<b>R, r</b>\n<i>восемнадцатая буква латинского алфавита; в сокращениях</i>: R. = Romanus, Rufus, regnum; R. P. = res publica <i>или</i> <i>Eccl</i> reverendissimus pater."],
  ["rabide", "<big>rabide</big>\n\n<b>rabide</b> [rabidus]\nнеистово, яростно (aliquid appetere <i>C</i>)."],
  ["rabidus", "<big>rabidus</big>\n\n<b>rabidus, a, um</b> [rabies]\nбешеный (canis <i>Lcr, PM, Sen</i>); яростный (leones <i>H</i>); неистовый, буйный, бурный (flatus ventorum <i>Amm</i>; lingua <i>Prp</i>); неутолимый (sitis <i>SenT</i>)."],
  ["rabies", "<big>rabies</big>\n\n<b>rabies, ei</b> <i>f</i> (<i>gen.</i> <b>es</b> <i>Lcr</i>)\nбешенство (canum <i>O, Col, PM etc.</i>), <i>но</i>: r. Canis <i>H</i> палящий зной (<i>см.</i> Canis II) || бушевание, бурность (maris <i>V</i>; ventorum <i>O</i>); неистовство, исступление (rabie corda tument <i>V</i>): linguae r. <i>Ap</i> словесное буйство || ярость (hostilis <i>L</i>): velut injecta rabie <i>L</i> точно взбесившись; r. ventris <i>и</i> r. edendi <i>V</i> мучительный голод."],
  ["rabio", "<big>rabio</big>\n\n<b>rabio, —, —, ere</b>\nбеситься, бесноваться, неистовствовать, быть в исступлении <i>Caec, Vr, C, Sen, Man</i>."],
  ["rabiose", "<big>rabiose</big>\n\n<b>rabiose</b> [rabiosus]\nнеистово, исступлённо, с яростью <i>C</i>."],
  ["rabiosulus", "<big>rabiosulus</big>\n\n<b>rabiosulus, a, um</b> [<i>demin. к</i> rabiosus]\nполубезумный, сумасбродный <i>C</i>."],
  ["rabiosus", "<big>rabiosus</big>\n\n<b>rabiosus, a, um</b> <i>Pl, C, CC etc.</i>\n= rabidus."],
  ["Rabirianus", "<big>Rabirianus</big>\n\n<b>Rabirianus, a, um</b> [Rabirius]\nрабириев (domus <i>C</i>)."],
  ["Rabirius", "<big>Rabirius</big>\n\n<b>Rabirius, a, um</b>\nРабирий, <i>римск. nomen; наиболее известны</i>:\n<b>1)</b> C. R., <i>народный трибун в 63 г. до н. э., которого Цицерон успешно защищал на суде от обвинения в соучастии в убийстве народного трибуна Сатурнина</i> <i>C</i>;\n<b>2)</b> C. R. Postumus, <i>приёмный сын предыдущего, обвинённый в 54 г. до н. э. в государственной измене и также оправданный благодаря защите Цицерона</i> <i>C</i>;\n<b>3)</b> C. R., <i>эпический поэт, современник Вергилия</i> <i>Sen, Q, O</i>."],
  ["rabo", "<big>rabo</big>\n\n<b>I rabo, onis</b> <i>m</i>\n<b>1)</b> <i>Pl</i> = arrhabo;\n<b>2)</b> рабон, <i>мера ёмкости</i> (modii et rabones <i>Aug</i>).\n\n<b>II rabo, —, —, ere</b> <i>v. l.</i>\n= rabio."],
  ["rabula", "<big>rabula</big>\n\n<b>rabula, ae</b> <i>m</i> [rabio]\n<b>1)</b> адвокат-крючкотвор (r. de loro <i>C</i>);\n<b>2)</b> пустой крикун, пустозвон (r. latratorque <i>Q</i>)."],
  ["rabulanus", "<big>rabulanus</big>\n\n<b>rabulanus, a, um</b> [ravus I]\nтёмно-жёлтый, изжелта-чёрный <i>или</i> бурый (pix <i>PM</i>)."],
  ["rabusculus", "<big>rabusculus</big>\n\n<b>rabusculus, a, um</b> <i>предпол.</i>\nжелтоватый (vitis <i>PM</i>)."],
  ["raca", "<big>raca</big>\n\n<b>I raca, ae</b> <i>f</i> <i>CTh</i>\n= racana.\n\n<b>II raca</b> (<i>евр.</i>) <i>бран.</i>\nпустой человек, ничтожество <i>Vlg, Aug</i>."],
  ["racana", "<big>racana</big>\n\n<b>racana, ae</b> <i>f</i>\nгрубый монашеский плащ <i>Eccl</i>."],
  ["racemarius", "<big>racemarius</big>\n\n<b>racemarius, a, um</b> [racemus]\nвиноградный (pampini <i>Col</i>)."],
  ["racematio", "<big>racematio</big>\n\n<b>racematio, onis</b> <i>f</i> [racemor]\nподбирание оставшегося на поле винограда (post vindemiam r. superstes <i>Tert</i>)."],
  ["racematus", "<big>racematus</big>\n\n<b>racematus, a, um</b> [racemus]\nпокрытый ягодами <i>PM</i>."],
  ["racemifer", "<big>racemifer</big>\n\n<b>racemifer, fera, ferum</b> [racemus + fero]\n<b>1)</b> приносящий ягоды, покрытый ягодами (uva <i>O</i>);\n<b>2)</b> украшенный <i>или</i> увенчанный виноградом (Bacchus <i>O</i>)."],
  ["racemor", "<big>racemor</big>\n\n<b>racemor, atus sum, ari</b> <i>depon.</i> [racemus]\n<b>1)</b> собирать оставшийся после уборки виноград <i>Aug</i>;\n<b>2)</b> <i>перен.</i> дополнительно обсуждать (de aliqua re <i>Vr</i>)."],
  ["racemosus", "<big>racemosus</big>\n\n<b>racemosus, a, um</b> [racemus]\n<b>1)</b> обильно покрытый ягодами (uva <i>PM</i>);\n<b>2)</b> гроздевидный (flos <i>PM</i>)."],
  ["racemus", "<big>racemus</big>\n\n<b>racemus, i</b> <i>m</i>\n<b>1)</b> виноградная кисть, гроздь, ветвь, лоза (poma racemis dependent <i>PM</i>); <i>перен.</i> виноград <i>V, H, Prp etc.</i>;\n<b>2)</b> ягода (<i>преим.</i> виноградная) (uva fert racemos <i>V</i>);\n<b>3)</b> виноградное сусло <i>или</i> вино <i>O</i>."],
  ["Racilius", "<big>Racilius</big>\n\n<b>Racilius, a, um</b>\nРацилий, <i>римск. nomen; наиболее известен</i> L. R., <i>народный трибун с 57 г. до н. э., защищавший Цицерона от нападок Клодия</i> <i>C</i>."],
  ["radians", "<big>radians</big>\n\n<b>radians, antis</b>\n<b>1.</b> <i>part. praes. к</i> radio;\n<b>2.</b> <i>m</i> солнце <i>Poeta ap. C</i>."],
  ["radiatio", "<big>radiatio</big>\n\n<b>radiatio, onis</b> <i>f</i>\nсияние, блеск (marmoris <i>PM</i>; lucis radiationes <i>Eccl</i>)."],
  ["radiatus", "<big>radiatus</big>\n\n<b>radiatus, a, um</b> [radius]\n<b>1)</b> снабжённый спицами (rota <i>Vr</i>);\n<b>2)</b> лучезарный, сияющий (sol <i>C</i>); окружённый сиянием (caput <i>PJ</i>); осиянный, озарённый (adverso Phoebi ab ictu <i>Lcn</i>)."],
  ["radicaliter", "<big>radicaliter</big>\n\n<b>radicaliter</b> <i>Aug</i>\n= radicitus."],
  ["radicesco", "<big>radicesco</big>\n\n<b>radicesco, —, —, ere</b> [radix]\nпускать корни <i>Sen</i>."],
  ["radicitus", "<big>radicitus</big>\n\n<b>radicitus</b> <i>adv.</i> [radix]\n<b>1)</b> с корнем (evellere arborem <i>Sen, Su</i>);\n<b>2)</b> в зародыше, совершенно, до конца (omnia male facta reperire <i>Pl</i>; excutere opinionem sibi <i>C</i>; aliquid tollere et extrahere <i>C</i>)."],
  ["radico", "<big>radico</big>\n\n<b>radico, avi, —, are</b> <i>Vlg, Eccl</i>\n= radicor."],
  ["radicor", "<big>radicor</big>\n\n<b>radicor, atus sum, ari</b> <i>depon.</i> <i>Col, PM, Sid</i>\n= radicesco."],
  ["radicosus", "<big>radicosus</big>\n\n<b>radicosus, a, um</b>\nимеющий много корней, с разветвлёнными корнями (brachia hederarum <i>PM</i>)."],
  ["radicula", "<big>radicula</big>\n\n<b>radicula, ae</b> <i>f</i> [<i>demin. к</i> radix]\n<b>1)</b> маленький корень, корешок <i>C, CC, Col</i>;\n<b>2)</b> редька <i>или</i> редиска <i>CC, Col</i>;\n<b>3)</b> <i>бот. предпол.</i> мыльнянка <i>PM</i>."],
  ["radio", "<big>radio</big>\n\n<b>radio, avi, atum, are</b> [radius]\nиспускать лучи, сиять, сверкать, блистать <i>Lcr, V, Prp, O etc.</i>"],
  ["radiolus", "<big>radiolus</big>\n\n<b>radiolus, i</b> <i>m</i> [<i>demin. к</i> radius]\n<b>1)</b> слабый луч (solis <i>Amm</i>);\n<b>2)</b> «лучик», <i>разновидность маслины с продолговатыми ягодами</i> <i>Col</i>."],
  ["radior", "<big>radior</big>\n\n<b>radior, —, ari</b> <i>depon.</i> <i>O, T</i>\n= radio."],
  ["radiosus", "<big>radiosus</big>\n\n<b>radiosus, a, um</b>\nлучистый, лучезарный (sol <i>Pl</i>)."],
  ["radius", "<big>radius</big>\n\n<b>radius, i</b> <i>m</i>\n<b>1)</b> палочка (r. acutus <i>L</i>); чертёжная палочка (<i>для черчения математических фигур</i>) (pulvis et r. <i>C</i>): radio uti nosse <i>Ap</i> знать толк в геометрии;\n<b>2)</b> спица (radii rotarum <i>V</i>);\n<b>3)</b> ткацкий челнок <i>Lcr, V, O etc.</i>;\n<b>4)</b> лучевая кость <i>CC</i>;\n<b>5)</b> шип, шпора (<i>преим. у петуха</i>) <i>PM</i>;\n<b>6)</b> (= radiolus 2) сорт продолговатых маслин <i>V, Col etc.</i>;\n<b>7)</b> полудиаметр, радиус <i>C</i>;\n<b>8)</b> луч (solis <i>Pl, C etc.</i>; lunae <i>O</i>): radii aurati <i>V</i> золотое сияние, нимб;\n<b>9)</b> (<i>тж.</i> r. virilis) <i>CA</i> = membrum virile."],
  ["radix", "<big>radix</big>\n\n<b>radix, icis</b> <i>f</i>\n<b>1)</b> корень (radices arborum <i>Vr</i>): radices agere <i>C etc.</i> <i>или</i> capere <i>Cato</i> пускать корни;\n<b>2)</b> корнеплод, клубень (<i>преим.</i> редька, редиска) <i>Cs, CC etc.</i>: r. <i>или</i> r. Syriaca <i>Col</i> хрен;\n<b>3)</b> нижняя часть, низ, основание (linguae, plumae <i>O</i>); подошва (montis <i>C, Cs etc.</i>);\n<b>4)</b> <i>перен. pl.</i> твёрдая почва, прочная база: virtus altissimis defixa radicibus <i>C</i> добродетель, имеющая твёрдое основание; vir iis radicibus <i>C</i> человек столь солидный;\n<b>5)</b> <i>pl.</i> начало, источник, корень (miseriarum <i>C</i>); происхождение (homo ex iisdem radicibus natus <i>C</i>)."],
  ["rado", "<big>rado</big>\n\n<b>rado, rasi, rasum, ere</b>\n<b>1)</b> скоблить, скрести (terram pedibus <i>Pl</i>): vomere r. <i>Sil</i> пахать || соскабливать, строгать, спиливать, чистить (aliquid lima <i>M</i>); сцарапывать, выскабливать, стирать (litteram <i>O</i>; tabellae rasae <i>O</i>);\n<b>2)</b> царапать (genas <i>LXIIT</i>);\n<b>3)</b> брить, сбривать (caput <i>C</i>; barbam <i>Su</i>);\n<b>4)</b> смывать (arva imbribus <i>H</i>);\n<b>5)</b> слегка касаться, задевать, <i>перен.</i> неприятно поражать (teneras auriculas mordaci vero <i>Pers</i>);\n<b>6)</b> омывать (ripas <i>Lcr</i>);\n<b>7)</b> огибать (litora <i>V</i>);\n<b>8)</b> пробегать (freta sicco passu <i>O</i>); проплывать, пролетать (iter liquidum <i>V</i>);\n<b>9)</b> подойти вплотную, достигнуть: raditur ultima meta <i>O</i> конечная цель почти достигнута."],
  ["radula", "<big>radula</big>\n\n<b>radula, ae</b> <i>f</i> [rado]\nскребок, скребница <i>Col</i>."],
  ["raeda", "<big>raeda</big>\n\n<b>raeda, ae</b> <i>f</i> (<i>галльск.</i>)\nчетырёхколёсный экипаж, повозка <i>C, Cs, H, Q</i>."],
  ["raedarius", "<big>raedarius</big>\n\n<b>I raedarius, i</b> <i>m</i> [raeda]\n<b>1)</b> возница <i>C</i>;\n<b>2)</b> каретник <i>Capit</i>.\n\n<b>II raedarius, a, um</b>\nупряжной (mulae <i>Vr</i>)."],
  ["Raeti", "<big>Raeti</big>\n\n<b>Raeti, orum</b> <i>m</i>\nжители области Raetia <i>L, T</i>."],
  ["Raetia", "<big>Raetia</big>\n\n<b>Raetia, ae</b> <i>и</i> <b>Raetiae, arum</b> <i>f</i>\nРетия, <i>область между реками Пад, Дунай, Рейн и Лех</i> (<i>населённая кельт. племенами и включённая в качестве провинции в Римскую империю ок. 15 г. н. э. Друзом и Тиберием</i>) <i>T, Vop, Amm</i>."],
  ["Raeticus", "<big>Raeticus</big>\n\n<b>Raeti(c)us, a, um</b> [Raeti]\nретийский (arma <i>O</i>; bellum <i>Su</i>; vinum <i>PM</i>)."],
  ["Raetus", "<big>Raetus</big>\n\n<b>Raetus, a, um</b> <i>H</i>\n= Raeti(c)us."],
  ["raga", "<big>raga</big>\n\n<b>raga, ae</b> <i>f</i> <i>CTh</i>\n= racana."],
  ["ragad-", "<big>ragad-</big>\n\n<b>ragad-</b> <i>v. l.</i>\n= rhagad-."],
  ["raja", "<big>raja</big>\n\n<b>raja, ae</b> <i>f</i> <i>зоол.</i>\nскат <i>PM</i>."],
  ["rallum", "<big>rallum</big>\n\n<b>rallum, i</b> <i>n</i> [rado]\nплужный скребок, сошник <i>PM</i>."],
  ["rallus", "<big>rallus</big>\n\n<b>rallus, a, um</b> [rado]\nгладко выстриженный, <i>т. е.</i> тонкий, лёгкий (tunica <i>Pl</i>)."],
  ["ramale", "<big>ramale</big>\n\n<b>ramale, is</b> <i>n</i> [ramus] <i>преим. pl.</i>\nсухие ветви, сучья, хворост <i>Pers, Sen, T, O</i>."],
  ["ramalis", "<big>ramalis</big>\n\n<b>ramalis, e</b> [ramale]\nиз сучьев (pix <i>Is</i>)."],
  ["ramenta", "<big>ramenta</big>\n\n<b>ramenta, ae</b> <i>f</i> <i>Pl</i>\n= ramentum."],
  ["ramentosus", "<big>ramentosus</big>\n\n<b>ramentosus, a, um</b> [ramentum]\nполный измельчённых кусков <i>CA</i>."],
  ["ramentum", "<big>ramentum</big>\n\n<b>ramentum, i</b> <i>n</i> [<i>из</i> *radmentum <i>от</i> rado]\n<i>преим. pl.</i>\n<b>1)</b> оскрёбки, осколки <i>или</i> опилки (ferri <i>Lcr</i>; ligni <i>PM</i>): r. fluminum <i>PM</i> речной песок;\n<b>2)</b> последние остатки, крохи: aurum omne cum ramento <i>Pl</i> все деньги до последнего гроша."],
  ["rames", "<big>rames</big>\n\n<b>rames, itis</b> <i>m</i>\nгрыжа <i>PM</i>."],
  ["rameus", "<big>rameus</big>\n\n<b>rameus, a, um</b> [ramus]\nсостоящий из сучьев <i>или</i> ветвей: fragmenta ramea <i>V</i> хворост."],
  ["ramex", "<big>ramex</big>\n\n<b>ramex, icis</b> <i>m</i> [ramus]\n<b>1)</b> прут, палка <i>Col</i>;\n<b>2)</b> <i>pl.</i> лёгочные (кровеносные) сосуды, <i>перен.</i> лёгкие: ramices (<i>v. l.</i> ramites) rumpere <i>Pl, Vr</i> надорвать лёгкие;\n<b>3)</b> грыжа <i>CC, PM</i>."],
  ["ramites", "<big>ramites</big>\n\n<b>ramites, um</b> <i>m</i> <i>анат.</i>\nлёгкие <i>Pl</i> (<i>см.</i> ramex 2)."],
  ["ramitosus", "<big>ramitosus</big>\n\n<b>ramitosus, a, um</b> [rames]\nстрадающий грыжей <i>PM</i>."],
  ["Ramnes", "<big>Ramnes</big>\n\n<b>Ramnes</b> <i>и</i> <b>Ramnenses, ium</b> <i>m</i>\n<b>1)</b> <i>одна из трёх древних патрицианских триб, состоявшая из латинян</i> (<i>вместе с сабинской трибой Tities и этрусской Luceres она образовала древнейшее население Рима</i>) <i>Vr, L, Prp, O</i>;\n<b>2)</b> <i>всадническая центурия (до реформы Сервия)</i> <i>C, L, AV</i>;\n<b>3)</b> <i>поэт.</i> старинная римская знать <i>H</i>."],
  ["ramosus", "<big>ramosus</big>\n\n<b>ramosus, a, um</b> [ramus]\nветвистый, сильно разветвлённый (arbor <i>Lcr, L</i>; cornu <i>V, Ph</i>; compita <i>Pers</i>)."],
  ["ramulosus", "<big>ramulosus</big>\n\n<b>ramulosus, a, um</b> [ramulus]\nс густыми прожилками (folium <i>PM</i>)."],
  ["ramulus", "<big>ramulus</big>\n\n<b>ramulus, i</b> <i>m</i> [<i>demin. к</i> ramus]\nсучок, ветка <i>Cato, C etc.</i>; отпрыск, побег <i>Col</i>."],
  ["ramus", "<big>ramus</big>\n\n<b>ramus, i</b> <i>m</i>\n<b>1)</b> ветвь, ветка, сук (r. frondens <i>V</i>);\n<b>2)</b> дубинка, палица (r. Herculis <i>Prp</i>);\n<b>3)</b> <i>поэт.</i> дерево <i>или</i> куст (bacas dant rami <i>V</i>); <i>pl.</i> древесные плоды (rami atque venatus alebat <i>V</i>); листва (tempora ramis cingere <i>V</i>);\n<b>4)</b> отрасль, ветвь (<i>по родству</i>) <i>Pers</i>;\n<b>5)</b> ответвление, отрог (Caucasi <i>PM</i>); боковая линия: Samii rami <i>Pers</i> самосские ветви, <i>т. е.</i> рога греческой буквы <i>T</i> (<i>служившие у самосца Пифагора символом расходящихся путей добродетели и порока</i>);\n<b>6)</b> рукав реки (<i>sc.</i> Nili <i>Sen</i>)."],
  ["ramusculus", "<big>ramusculus</big>\n\n<b>ramusculus, i</b> <i>m</i> [<i>demin. к</i> ramus]\nветочка, сучок <i>Vlg, Eccl</i>."],
  ["rana", "<big>rana</big>\n\n<b>rana, ae</b> <i>f</i>\n<b>1)</b> лягушка: r. turpis <i>H</i> (rubeta <i>Prp, PM</i>) жаба; qui fuit r., nunc est rex <i>погов. Pt</i> был лягушкой, а стал царём;\n<b>2)</b> (<i>тж.</i> r. marina <i>C</i> <i>и</i> r. piscatrix <i>PM</i>) лягва, морской чёрт (<i>рыба Lophius piscatorius</i>, L.) <i>PM</i>;\n<b>3)</b> опухоль на языке (<i>у животных</i>) <i>Veg</i>."],
  ["ranceo", "<big>ranceo</big>\n\n<b>ranceo, —, —, ere</b>\nбыть тухлым, гнилым, зловонным (rancens viscus <i>Lcr</i>)."],
  ["rancide", "<big>rancide</big>\n\n<b>rancide</b>\nзловонно, <i>перен.</i> противно, отвратительно (r. ficta verba <i>AG</i>)."],
  ["rancidulus", "<big>rancidulus</big>\n\n<b>rancidulus, a, um</b> [<i>demin. к</i> rancidus]\n<b>1)</b> несколько протухший, с запашком (obsonia <i>J</i>);\n<b>2)</b> противный, неприятный (rancidulo loqui ore <i>M</i>)."],
  ["rancidus", "<big>rancidus</big>\n\n<b>rancidus, a, um</b>\n<b>1)</b> тухлый, разложившийся, зловонный (cadaver <i>Lcr</i>; aper <i>H</i>);\n<b>2)</b> противный, отвратительный (aspectus <i>PM</i>)."],
  ["ranco", "<big>ranco</big>\n\n<b>ranco, —, —, are</b>\nрычать (<i>о тигре</i>) <i>Su</i>."],
  ["rancor", "<big>rancor</big>\n\n<b>rancor, oris</b> <i>m</i> [ranceo]\n<b>1)</b> протухлость, гниль <i>PM</i>;\n<b>2)</b> <i>перен.</i> застарелая неприязнь, злопамятность <i>Eccl</i>."],
  ["ranula", "<big>ranula</big>\n\n<b>ranula, ae</b> <i>f</i> [<i>demin. к</i> rana]\n<b>1)</b> маленькая лягушка, лягушонок <i>Ap</i>;\n<b>2)</b> <i>Veg</i> = rana 3."],
  ["ranunculus", "<big>ranunculus</big>\n\n<b>ranunculus, i</b> <i>m</i> [<i>demin. к</i> rana]\n<b>1)</b> лягушонок <i>C</i>;\n<b>2)</b> <i>бот.</i> лютик <i>PM</i>."],
  ["rapa", "<big>rapa</big>\n\n<b>rapa, ae</b> <i>f</i>\nрепа <i>Col, Pt</i>."],
  ["rapacida", "<big>rapacida</big>\n\n<b>rapacida (-des), ae</b> <i>m</i> [rapax + <i>греч. патронимическое окончание</i>]\n(«сын грабителя») <i>шутл.</i> грабитель, разбойник <i>Pl</i>."],
  ["rapacitas", "<big>rapacitas</big>\n\n<b>rapacitas, atis</b> <i>f</i> [rapax]\nхищность, алчность, жадность <i>C, Su, M etc.</i>"],
  ["rapax", "<big>rapax</big>\n\n<b>rapax, acis</b> <i>adj.</i> [rapio]\n<b>1)</b> порывистый, стремительный, неудержимый, увлекающий с собой (ventus <i>VF</i>; fluvius <i>Lcr, V</i>): r. ignium <i>PM</i> легко воспламеняющийся, горючий;\n<b>2)</b> жадно схватывающий, быстро усваивающий (ingenium r. virtutis <i>Sen</i>);\n<b>3)</b> алчный, жадный (domina <i>Tib</i>); хищный (lupus <i>H</i>); грабительский (olim furunculus, nunc etiam r. <i>C</i>)."],
  ["raphaninus", "<big>raphaninus</big>\n\n<b>raphaninus, a, um</b> [raphanus]\nприготовленный из редьки (oleum <i>PM</i>)."],
  ["raphanitis", "<big>raphanitis</big>\n\n<b>raphanitis, idis</b> <i>f</i> (<i>греч.</i>) <i>бот.</i>\nирис <i>или</i> шпажник <i>PM</i>."],
  ["raphanus", "<big>raphanus</big>\n\n<b>raphanus, i</b> <i>m</i> (<i>греч.</i>)\nредька <i>или</i> редиска <i>Cato, Ctl, PM etc.</i>"],
  ["rapicii", "<big>rapicii</big>\n\n<b>rapicii, orum</b> <i>m</i> [rapicius]\n(<i>sc.</i> caules) молодые побеги (всходы) репы <i>PM</i>."],
  ["rapicius", "<big>rapicius</big>\n\n<b>rapicius, a, um</b> [rapum]\nрепный (semen <i>Cato</i>)."],
  ["rapide", "<big>rapide</big>\n\n<b>rapide</b>\nбыстро, стремительно (venire <i>T</i>; iter conficere <i>Su</i>)."],
  ["rapiditas", "<big>rapiditas</big>\n\n<b>rapiditas, atis</b> <i>f</i> [rapidus]\nстремительность, быстрота (fluminis <i>Cs</i>)."],
  ["rapidus", "<big>rapidus</big>\n\n<b>rapidus, a, um</b> [rapio]\n<b>1)</b> стремительный, быстрый, скорый (fluvius <i>Pl</i>, equus <i>O</i>): r. ire <i>Ap</i> быстрый на ходу, быстроходный || быстро действующий (venenum <i>T</i>); торопливый (oratio <i>C</i>); поспешный, опрометчивый (rapidus in consiliis <i>L</i>);\n<b>2)</b> <i>поэт.</i> хищный, дикий (agmen canum <i>O</i>; leo <i>Lcr</i>; ferae <i>O</i>);\n<b>3)</b> палящий, знойный (aestus <i>V</i>; flamma <i>O</i>)."],
  ["rapina", "<big>rapina</big>\n\n<b>I rapina, ae</b> <i>f</i> [rapio]\n<b>1)</b> грабёж, ограбление, захват (rapinae populationesque <i>Cs</i>): facere rapinam <i>bAfr</i> грабить;\n<b>2)</b> поглощение, всасывание (alimenti <i>PM</i>);\n<b>3)</b> награбленное, добыча <i>V, O</i>: aequorum rapinae <i>M</i> морской улов (рыбы);\n<b>4)</b> быстрый вынос (из огня) (rapina tutari opes <i>LJ</i>).\n\n<b>II rapina, ae</b> <i>f</i> [rapa]\n<b>1)</b> поле, засаженное репой <i>Col</i>;\n<b>2)</b> pena <i>Cato</i>."],
  ["rapinator", "<big>rapinator</big>\n\n<b>rapinator, oris</b> <i>m</i> [rapio]\nвор, грабитель <i>Vr</i>."],
  ["rapio", "<big>rapio</big>\n\n<b>rapio, rapui, raptum, ere</b>\n<b>1)</b> хватать (arma manu <i>V</i>); вырывать (telum de vulnere <i>V</i>); выхватывать (securim ab aliquo <i>L</i>): r. nigrum colorem <i>O</i> (моментально) принимать чёрный цвет; membra toro r. <i>VF</i> быстро подняться с ложа; ipsae res verba rapiunt <i>C</i> самый предмет сразу подсказывает слова (слова сами приходят); r. animum in partes varias <i>V</i> обдумывать (вопрос) с разных сторон;\n<b>2)</b> обрывать, срывать (frondes arbore <i>O</i>); сносить, отрубать (caput <i>Sil</i>);\n<b>3)</b> быстро доставать, выносить (aliquid tectis <i>V</i>);\n<b>4)</b> ускорять (gressus <i>Lcn</i>); спешно справлять (nuptias <i>L</i>); быстро проходить (viam <i>O</i>; silvas <i>V</i>; campum <i>St</i>); быстро гнать (navem per aequora <i>V</i>): se r. спешить, торопиться, быстро отправляться (ad urbem <i>C</i>; praecipiti fuga <i>VP</i>);\n<b>5)</b> быстро совершать (fugam <i>VF</i>): rapiuntque ruuntque <i>V</i> (все) торопятся, спешат || быстро убирать (scalas <i>bAl</i>; corpus alicujus <i>L</i>); ловить на лету (voluptates <i>T</i>; oscula <i>H</i>; occasionem <i>H</i>): condicionem rapuisse <i>Pt</i> ухватиться за (сделанное) предложение || бегло описывать (immensos orbes <i>V</i>); быстро раздувать (ventus rapiens incendia <i>Lcn</i>): r. flammam <i>O</i> быстро воспламеняться;\n<b>6)</b> быстро уносить (flumen rapit pecudes <i>O</i>); спешно увозить (frumentum ex agris <i>L</i>);\n<b>7)</b> уводить (aliquem e carcere ad necem <i>C</i>); отнимать силой, похищать (alicui aliquid <i>L</i>); умыкать (Sabinas virgines <i>C</i>); угонять (armenta stabulis <i>O</i>); грабить, опустошать (castra urbesque <i>L</i>); насильно приводить (aliquem in jus <i>Pl</i>);\n<b>8)</b> завладевать, захватывать (dominationem <i>T</i>); присваивать (gloriam victoriae in se r. <i>L</i>);\n<b>9)</b> увлекать (cupiditas rapit aliquem <i>C</i>): rapit hora diem <i>H</i> час увлекает за собой день; rapiente fato <i>SenT</i> стремительной силой судьбы || устремлять (totam aciem in Teucros <i>V</i>); вовлекать (in errorem rapi <i>C</i>); быстро вводить (auditorem in medias res r. <i>H</i>);\n<b>10)</b> подвергать (чему-л.), делать жертвой (r. aliquem in adversum <i>V</i>; r. in invidiam <i>C</i>): r. in pejorem partem <i>Ter</i> дурно истолковывать, осуждать; amentia rapi <i>C</i> быть охваченным безумием."],
  ["rapister", "<big>rapister</big>\n\n<b>rapister, tri</b> <i>m</i> <i>LM</i>\n= rapinator."],
  ["rapistrum", "<big>rapistrum</big>\n\n<b>rapistrum, i</b> <i>n</i>\nдикая репа <i>Col</i>."],
  ["rapo", "<big>rapo</big>\n\n<b>rapo, onis</b> <i>m</i> <i>Vr</i>\n= rapinator."],
  ["rapsit", "<big>rapsit</big>\n\n<b>rapsit</b> <i>арх. C</i> (= rapuerit) <i>fut. II к</i>\nrapio."],
  ["rapso", "<big>rapso</big>\n\n<b>rapso, —, atum, are</b> <i>bAfr, AG</i>\n= rapto."],
  ["raptim", "<big>raptim</big>\n\n<b>raptim</b> <i>adv.</i> [rapio]\n<b>1)</b> сразу же, поспешно (scripsisse aliquid <i>C</i>; agmen ducere <i>QC</i>; petere aequora campi <i>Lcr</i>);\n<b>2)</b> опрометью (fugere <i>Pt</i>)."],
  ["raptio", "<big>raptio</big>\n\n<b>raptio, onis</b> <i>f</i> [rapio]\nпохищение (<i>sc.</i> citharistriae <i>Ter</i>; Proserpinae <i>Eccl</i>)."],
  ["raptito", "<big>raptito</big>\n\n<b>raptito, —, —, are</b> [<i>frequ. к</i> rapto]\nхватать, похищать <i>AG</i>."],
  ["rapto", "<big>rapto</big>\n\n<b>rapto, avi, atum, are</b> [<i>intens. к</i> rapio]\n<b>1)</b> насильно хватать, похищать (conjugem <i>C</i>); влечь, влачить, тащить (aliquem comis <i>O</i>);\n<b>2)</b> подхватывать, увлекать (amor aliquem raptat <i>Pl, Sil</i>); вести за собой (aeratas in agmina turmas <i>Sil</i>);\n<b>3)</b> грабить (arcem <i>St</i>; Africam <i>T</i>)."],
  ["raptor", "<big>raptor</big>\n\n<b>raptor, oris</b> <i>m</i> [rapio]\nпохититель (filiae <i>T</i>); грабитель, вор (panis et peni <i>Pl</i>; orbis <i>T</i>); хищник (lupi raptores <i>V</i>): r. spiritus <i>VM</i> убийца; r. ferri <i>Aug</i> = магнит."],
  ["raptorius", "<big>raptorius</big>\n\n<b>raptorius, a, um</b>\nзахватывающий, служащий для извлечения (machinamentum <i>CA</i>)."],
  ["raptum", "<big>raptum</big>\n\n<b>raptum, i</b> <i>n</i>\n<b>1)</b> грабёж (vivere rapto <i>L</i> <i>и</i> ex rapto <i>O</i>);\n<b>2)</b> награбленное, добыча (rapto potiri <i>V</i>)."],
  ["raptus", "<big>raptus</big>\n\n<b>I raptus, a, um</b> <i>part. pf. к</i>\nrapio.\n\n<b>II raptus, us</b> <i>m</i> [rapio]\n<b>1)</b> похищение (virginis <i>C</i>); грабёж (r. et latrocinia <i>T</i>);\n<b>2)</b> захватывание, резкое движение (runcinae <i>PM</i>)."],
  ["rapulum", "<big>rapulum</big>\n\n<b>rapulum, i</b> <i>n</i> [<i>demin. к</i> rapum]\nмелкая репа, репка <i>H</i>."],
  ["rapum", "<big>rapum</big>\n\n<b>rapum, i</b> <i>n</i>\n<b>1)</b> репа <i>Vr, Col, PM, Pall</i>;\n<b>2)</b> клубень <i>Sen</i>."],
  ["rare", "<big>rare</big>\n\n<b>rare</b> <i>Pl, Col</i>\n= raro."],
  ["rarefacio", "<big>rarefacio</big>\n\n<b>rare–facio, feci, factum, ere</b>\nразрежать, разрыхлять (terram <i>Lcr</i>)."],
  ["rarefio", "<big>rarefio</big>\n\n<b>rare–fio, factus sum, fieri</b> [<i>pass. к</i> rarefacio]\nразрежаться, распадаться, разлагаться <i>Lcr</i>."],
  ["rarenter", "<big>rarenter</big>\n\n<b>rarenter</b> <i>арх.</i>\n(= raro) изредка, с большими перерывами (loqui <i>Ap</i>)."],
  ["raresco", "<big>raresco</big>\n\n<b>raresco, —, —, ere</b> [rarus]\n<b>1)</b> редеть, терять плотность, становиться прозрачным (nubila rarescunt <i>Lcr</i>); разжижаться (in liquidas aquas <i>O</i>); разрежаться (calore <i>Lcr</i>);\n<b>2)</b> <i>поэт.</i> разверзаться, открываться (claustra rarescunt <i>V</i>);\n<b>3)</b> слабеть, угасать, замирать (sonitus rarescit <i>Prp</i>);\n<b>4)</b> становиться реже, убывать (montes rarescunt <i>T</i>; tenebrae profundae rarescunt <i>St</i>)."],
  ["raripilus", "<big>raripilus</big>\n\n<b>raripilus, a, um</b> [rarus + pilus I]\nредкошёрстный (caprinum pecus <i>Col</i>)."],
  ["raritas", "<big>raritas</big>\n\n<b>raritas, atis</b> <i>f</i>\n<b>1)</b> разрежённость, рыхлость (terrae <i>Sen</i>); неплотность (dentium <i>Q</i>); взаимная отдалённость, разбросанность (arborum <i>PM</i>); пустота, пустой промежуток (per has raritates spiritus fertur <i>Sen</i>);\n<b>2)</b> редкость, малочисленность: r. capillorum <i>Su</i> редкие волосы;\n<b>3)</b> редкость, редкий предмет (r. monstruosa <i>AG</i>)."],
  ["raritudo", "<big>raritudo</big>\n\n<b>raritudo, inis</b> <i>f</i> <i>Vr, Col</i>\n= raritas."],
  ["raro", "<big>raro</big>\n\n<b>raro</b> <i>adv.</i>\nредко, изредка <i>Pl, C, O etc.</i>"],
  ["rarus", "<big>rarus</big>\n\n<b>rarus, a, um</b>\n<b>1)</b> редкий, негустой (silva <i>T</i>); разрежённый, рассыпной (agmen <i>QC</i>; ordo <i>L</i>); редкий, неплотный, из неплотной ткани, лёгкий (tunica <i>O</i>); нечастый, с широкими петлями (cribrum <i>O</i>; rete <i>H, V</i>); рыхлый, пористый (terra <i>V, Col</i>); разбросанный, не сплошной, одиночный (arbores <i>Nep</i>; voces <i>V</i>): rara manus <i>Q</i> рука с широко раздвинутыми пальцами;\n<b>2)</b> редко попадающийся, редкий (litterae <i>C</i>; portus <i>Cs</i>): r. egressu <i>T</i> редко выходящий из дому; rarum habere locum <i>PS</i> редко случаться, нечасто бывать;\n<b>3)</b> редкостный (avis <i>H</i>); необыкновенный, незаурядный (facies <i>O</i>); замечательный (vestis <i>Ctl</i>)."],
  ["rasi", "<big>rasi</big>\n\n<b>rasi</b> <i>pf. к</i>\nrado."],
  ["rasilis", "<big>rasilis</big>\n\n<b>rasilis, e</b> [rado]\n<b>1)</b> гладкий (calathus <i>O</i>);\n<b>2)</b> поддающийся обтачиванию (buxum <i>V</i>);\n<b>3)</b> голый, лишённый растительности (scopuli <i>Eccl</i>)."],
  ["rasio", "<big>rasio</big>\n\n<b>rasio, onis</b> <i>f</i> [rado]\nсоскрёбывание, скобление (strigilis <i>CA</i>)."],
  ["rasis", "<big>rasis</big>\n\n<b>rasis, is</b> <i>f</i>\nсырая смола <i>Col</i>."],
  ["rasito", "<big>rasito</big>\n\n<b>rasito, avi, —, are</b> [<i>intens. к</i> rado]\nсбривать, часто брить (barbam <i>AG</i>; faciem cotidie <i>Su</i>)."],
  ["rasta", "<big>rasta</big>\n\n<b>rasta, ae</b> <i>f</i>\nгерманская миля <i>Hier</i>."],
  ["rastellus", "<big>rastellus</big>\n\n<b>rastellus, i</b> <i>m</i> [<i>demin. к</i> rastrum]\nмаленькая мотыга <i>Vr, Col, Su</i>."],
  ["raster", "<big>raster</big>\n\n<b>raster, tri</b> <i>m</i> [rado] (<i>преим.</i> <i>pl.</i>)\nмотыга (rastris glaebas frangere <i>V</i>): ei res ad rastros redit <i>Ter</i> ему приходится взяться за мотыгу, <i>т. е.</i> он обеднел."],
  ["rastraria", "<big>rastraria</big>\n\n<b>rastraria, ae</b> <i>f</i> [raster]\nобрабатывающая землю, крестьянка <i>Caec</i>."],
  ["rastrum", "<big>rastrum</big>\n\n<b>rastrum, i</b> <i>n</i> <i>O</i>\n= raster."],
  ["rasum", "<big>rasum</big>\n\n<b>rasum</b> <i>supin. к</i>\nrado."],
  ["rasura", "<big>rasura</big>\n\n<b>rasura, ae</b> <i>f</i> [rado]\n<b>1)</b> выглаживание (calami <i>Col</i>);\n<b>2)</b> бритьё (capitis <i>CA, Hier</i>);\n<b>3)</b> оскрёбки, стружки (eboris <i>Veg</i>);\n<b>4)</b> скобление, царапание: r. gulae <i>Hier</i> гортанное произношение."],
  ["rasus", "<big>rasus</big>\n\n<b>I rasus, us</b> <i>m</i> <i>Vr</i>\n= rasura 4.\n\n<b>II rasus, a, um</b> <i>part. pf. к</i>\nrado."],
  ["ratariae", "<big>ratariae</big>\n\n<b>ratariae (ratiariae), arum</b> <i>f</i> [ratis]\nплот <i>AG</i>."],
  ["ratiarius", "<big>ratiarius</big>\n\n<b>ratiarius, i</b> <i>m</i> [ratis]\nплотовщик <i>Dig</i>."],
  ["ratihabitio", "<big>ratihabitio</big>\n\n<b>rati–habitio, onis</b> <i>f</i> [ratus + habito]\nутверждение, ратификация <i>Dig</i>."],
  ["ratio", "<big>ratio</big>\n\n<b>ratio, onis</b> <i>f</i> [reor]\n<b>1)</b> счёт, подсчёт (r. accepti atque expensi <i>Pl</i>, <i>тж.</i> r. acceptorum et datorum <i>C</i>): rationem inire <i>Cs</i> (putare <i>Pl</i>, subducere <i>C</i>, conficere <i>Cs</i>) считать, подсчитывать; rationes accipere <i>C, Sen, Pt</i> принимать счета (<i>в порядке отчётности</i>); rationem alicujus rei habere (inire) <i>C etc.</i> высчитывать что-л.; facilem rationem habere <i>C</i> легко поддаваться подсчёту; r. constat <i>C</i> счёт верен; mihi constat impensae r. <i>Sen</i> я веду счёт расходам; a rationibus (<i>sc.</i> servus) <i>Su</i> счетовод; libri rationum <i>AV</i> счетоводные книги; quantum in ratione est <i>Hirt</i> насколько можно рассчитать;\n<b>2)</b> отчёт: rationem repetere (reposcere) <i>C, Cs etc.</i> требовать отчёта; rationem reddere (persolvere) <i>C etc.</i> давать отчёт;\n<b>3)</b> список, перечень, ведомость: rationem alicujus rei conficere <i>C</i> вести список чего-л.; in rationem venire, <i>тж.</i> in rationes inferri <i>Pt</i> быть внесённым в список, <i>т. е.</i> быть взятым на учёт; r. carceris <i>C</i> тюремный журнал;\n<b>4)</b> сумма, итог, число (pecuniae <i>C</i>): plurima auri et argenti r. <i>Pt</i> множество золота и серебра, <i>но</i>: r. pecuniarum <i>C</i> денежные операции;\n<b>5)</b> деловые связи, денежные взаимоотношения, взаимные расчёты: re ac ratione conjunctum esse cum aliquo <i>C</i> находиться в деловых отношениях с Кем-л.;\n<b>6)</b> дела, вопросы (r. popularis <i>C</i>): e ratione domestica esse <i>C</i> относиться к вопросам внутренней политики; fori judiciique r. <i>C</i> политические и судебные дела; temporis r. <i>C</i> время, условия, обстоятельства; r. saporum <i>H</i> вопросы вкусовых ощущений (<i>как основа поваренного искусства</i>); quae r. tibi cum eo intercesserat? <i>C</i> что за дело было у тебя с ним?;\n<b>7)</b> отношение, взаимоотношения: artes et rationes <i>C</i> искусства и отношения между ними; pacis r. <i>C</i> мирные взаимоотношения; veterem cum aliquo rationem reducere <i>Pt</i> восстановить прежние отношения с кем-л.; pro ratione alicujus rei <i>C, Cs</i> сообразно с чём-л., по отношению к чему-л.; habere rationem cum terra <i>C</i> быть связанным с землёй, <i>т. е.</i> заниматься сельским хозяйством;\n<b>8)</b> область, категория, разряд (in rationem utilitatis cadere <i>C</i>; studia in dissimili ratione <i>C</i>);\n<b>9)</b> учёт, соображение, принятие во внимание: rationem habere (ducere) alicujus rei <i>C</i> принимать что-л. во внимание; ex utilitatis ratione <i>C</i> сообразуясь с пользой; ad meam rationem <i>C</i> с точки зрения моих личных взглядов; r. pudoris <i>C</i> чувство щепетильности;\n<b>10)</b> выгода, интерес: r. rei publicae <i>C</i> учёт государственных интересов, политические соображения; non alienus rationi nostrae <i>C</i> не противоречащий нашим интересам; suam rationem ducere <i>C</i> думать о своей выгоде; rationes suas alicui rei anteponere <i>C</i> ставить свои личные интересы выше чего-л.; propter rationem brevitatis ac temporis <i>C</i> в целях сбережения времени; salutis alicujus rationem habere <i>Cs</i> заботиться о чьей-л. безопасности;\n<b>11)</b> мышление, размышление, обдумывание, рассмотрение (omnia ratione lustrare <i>C</i>): non sine summa ratione <i>C</i> вполне обдуманно; in ratione versari <i>C</i> быть предметом размышления;\n<b>12)</b> предмет размышления, проблема (rationes agitandae exquirendaeque <i>C</i>);\n<b>13)</b> рассудок, разум (bestiae rationis expertes sunt <i>C</i>); разумность, смысл (nulla hujusce rei r. est <i>C</i>): r. est aliquid facere <i>C</i> разумно (<i>т. е.</i> следует) что-л. сделать; rationis esse <i>C</i> быть делом (творением) разума;\n<b>14)</b> образ, способ, приём, метод, план (r. dicendi <i>C</i>; r. argumentandi <i>C</i>; r. vivendi <i>rhH</i> <i>или</i> vitae <i>C</i>; Socratica r. disserendi <i>C</i>): castrensis r. <i>C</i> лагерный режим; ratione <i>C</i> методически, планомерно; ratione acutissima <i>Pt</i> с помощью весьма остроумного приёма; quacumque ratione possunt <i>Pt</i> любыми доступными им способами; r. et disciplina <i>C</i> систематическое изучение; instituta r. <i>C</i> установленный план; ratione et numero moveri <i>C</i> двигаться по плану и ритмично; r. et consilium <i>C</i> методическое размышление; r. et distributio <i>C</i> плановое распределение; r. et doctrina <i>C</i> систематическое образование; r. et via <i>C</i> систематичность, планомерность; r. belli <i>C, Cs, QC</i> военное искусство; rationes belli gerendi <i>Cs</i> военно-оперативные планы; r. in provincialium negotiis <i>Ap</i> система руководства делами провинции; qua ratione? <i>C, Cs etc.</i> каким образом?;\n<b>15)</b> возможность, путь (nulla ad aliquid r. erat <i>C</i>);\n<b>16)</b> образ мыслей, взгляд, точка зрения, принцип (homo alterius rationis <i>C</i>); направление, смысл (epistulae in eandem rationem scriptae <i>C</i>); форма, порядок (duplex est r. orationis <i>C</i>): r. comitiorum <i>C</i> порядок работы комиций, <i>т. е.</i> организация выборов; florens homo in populari ratione <i>C</i> главный представитель демократического направления;\n<b>17)</b> основание, мотив (alicujus rei causam rationemque cognoscere <i>C</i>): nulla ratione <i>Cs</i> никоим образом, <i>но</i>: <i>C</i> без всякого основания; confirmare aliquid rationibus <i>C</i> подкрепить что-л. основательными доводами;\n<b>18)</b> обоснование, доказательство (quid opus est ratione? <i>C</i>);\n<b>19)</b> (<i>тж.</i> r. conclusa <i>Ap</i>) умозаключение, вывод: rationem concludere <i>C</i> сделать вывод;\n<b>20)</b> учение, система, теория, наука, школа (r. Epicuri <i>C</i>): r. atque usus belli <i>Cs</i> теория и практика войны; ars et r. <i>C</i> практика и теория;\n<b>21)</b> положение, правило, мнение (mea sic est r. <i>Ter</i>);\n<b>22)</b> положение, состояние, устройство, система: r. rerum civilium <i>C</i> политическая система; r. annonae <i>C</i> состояние хлебных цен; r. atque ordo <i>Cs</i> упорядоченность, распорядок; r. ordoque agminis <i>Cs</i> походный порядок, строй; r. Galliae <i>Cs</i> положение Галлии; convenientium r. <i>Ap</i> характер собравшихся (аудитории); r. atque inclinatio temporis <i>C</i> настоящие обстоятельства и их направленность."],
  ["ratiocinabiliter", "<big>ratiocinabiliter</big>\n\n<b>ratiocinabiliter</b>\nсогласно правильному расчёту <i>Macr</i>."],
  ["ratiocinatio", "<big>ratiocinatio</big>\n\n<b>ratiocinatio, onis</b> <i>f</i> [ratiocinor]\n<b>1)</b> размышление, рассуждение, взвешивание <i>rhH, C, CC</i>;\n<b>2)</b> умозаключение <i>C, Q</i>;\n<b>3)</b> теория <i>Vtr</i>."],
  ["ratiocinativus", "<big>ratiocinativus</big>\n\n<b>ratiocinativus, a, um</b> [ratiocinor]\nпостроенный на рассуждении, основанный на умозрении (genus dicendi <i>C</i>; quaestio <i>Q</i>)."],
  ["ratiocinator", "<big>ratiocinator</big>\n\n<b>ratiocinator, oris</b> <i>m</i> [ratiocinor]\nсчётчик, счетовод <i>C, Col</i>; <i>перен.</i> учитывающий (bonus r. officiorum <i>C</i>)."],
  ["ratiocinium", "<big>ratiocinium</big>\n\n<b>ratiocinium, i</b> <i>n</i>\nучёт, исчисление, <i>тж.</i> счетоводство <i>Col, CTh, CJ</i>."],
  ["ratiocinor", "<big>ratiocinor</big>\n\n<b>ratiocinor, atus sum, ari</b> <i>depon.</i> [ratio]\n<b>1)</b> вычислять, исчислять, считать (metiendi ratiocinandique utilitas <i>C</i>; r. didicisse <i>Pt</i>); рассчитывать (r., quid in similibus rebus fieri soleat <i>C</i>);\n<b>2)</b> рассуждать, заключать, делать вывод (si recte ratiocinabimur <i>C</i>)."],
  ["rationabilis", "<big>rationabilis</big>\n\n<b>rationabilis, e</b>\n<b>1)</b> одарённый разумом, разумный (natura <i>Sen</i>; anima <i>Ap</i>);\n<b>2)</b> разумный, основанный на разуме (sententia <i>Dig</i>)."],
  ["rationabilitas", "<big>rationabilitas</big>\n\n<b>rationabilitas, atis</b> <i>f</i>\nразумение, разумность <i>Eccl</i>."],
  ["rationabiliter", "<big>rationabiliter</big>\n\n<b>rationabiliter</b> <i>Ap, Macr, CTh</i>\n= rationaliter."],
  ["rationale", "<big>rationale</big>\n\n<b>rationale, is</b> <i>n</i> (<i>sc.</i> vestimentum)\nнаперсник (<i>часть облачения древнееврейских первосвященников</i>) <i>Vlg</i>."],
  ["rationalis", "<big>rationalis</big>\n\n<b>I rationalis, e</b> [ratio]\n<b>1)</b> счётный, учётный (officium <i>CJ</i>);\n<b>2)</b> основанный на умозрении, умозрительный, теоретический (disciplina <i>CC</i>); построенный на умозаключении, силлогистический (philosophia <i>Sen</i>);\n<b>3)</b> разумный, одарённый разумом (animal <i>Sen, Q</i>).\n\n<b>II rationalis, is</b> <i>m</i>\nсчетовод, <i>тж.</i> казначей <i>Lampr, Capit, CTh</i>."],
  ["rationalitas", "<big>rationalitas</big>\n\n<b>rationalitas, atis</b> <i>f</i>\nразумность <i>Tert</i>."],
  ["rationaliter", "<big>rationaliter</big>\n\n<b>rationaliter</b> [rationalis I]\nразумно <i>Sen, Eccl</i>."],
  ["rationarium", "<big>rationarium</big>\n\n<b>rationarium, i</b> <i>m</i> [ratio]\nсчёт, опись, статистический обзор (imperii <i>Su</i>)."],
  ["rationarius", "<big>rationarius</big>\n\n<b>rationarius, i</b> <i>m</i>\nсчетовод <i>Dig</i>."],
  ["ratis", "<big>ratis</big>\n\n<b>ratis, is</b> <i>f</i>\nплот (transire Rhenum navibus ratibusque <i>Cs</i>); <i>поэт.</i> судно (<i>преим.</i> утлое) (cava r. <i>O</i>); паром, барка (ratem conto subigere <i>V</i>)."],
  ["ratitus", "<big>ratitus</big>\n\n<b>ratitus, a, um</b> [ratis]\nс изображением судна (quadrans <i>LM ap. Vr</i>)."],
  ["ratiuncula", "<big>ratiuncula</big>\n\n<b>ratiuncula, ae</b> <i>f</i> [<i>demin. к</i> ratio]\n<b>1)</b> небольшой счёт, счётец <i>Pl, Ter</i>;\n<b>2)</b> маловажная причина, основание (levis <i>C</i>);\n<b>3)</b> небольшое умозаключение, рассужденьице <i>C, Aug</i>."],
  ["rato", "<big>rato</big>\n\n<b>rato</b>\nнесомненно, бесспорно <i>Eccl</i>."],
  ["ratus", "<big>ratus</big>\n\n<b>ratus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> reor;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> рассчитанный, определённый: rato tempore <i>C</i> в определённое (назначенное) время; pro rata (parte) <i>C, Cs etc.</i> <i>или</i> pro rata portione <i>PM</i>, <i>тж.</i> secundum ratam partem <i>Vtr</i> соразмерно, в определённом отношении, пропорционально;\n<b>2)</b> незыблемый, неизменный, постоянный (motus stellarum <i>C</i>; astrorum ordines <i>C</i>; fides <i>O</i>);\n<b>3)</b> решённый, юридически действительный, имеющий законную силу (lex, testamentum <i>C</i>): ratum habere (ducere, facere) aliquid <i>C etc.</i> утвердить, одобрить, ратифицировать что-л.; abunde r., si praesentibus frueretur <i>T</i> вполне довольствуясь настоящим; rata facere visa <i>O</i> исполнять желания; spes jubere esse ratas <i>H</i> объявить надежды осуществлёнными;\n<b>4)</b> решившийся (potitur r. Romulus praedam <i>Enn</i>)."],
  ["rauca", "<big>rauca</big>\n\n<b>rauca, ae</b> <i>f</i>\nземляной червь (<i>разновидность</i>) <i>PM, Dig</i>."],
  ["raucedo", "<big>raucedo</big>\n\n<b>raucedo, inis</b> <i>f</i> <i>Is</i>\n= raucitas."],
  ["raucesco", "<big>raucesco</big>\n\n<b>raucesco, —, —, ere</b>\nхрипнуть <i>Is</i>."],
  ["raucidulus", "<big>raucidulus</big>\n\n<b>raucidulus, a, um</b> [raucus]\nхрипливый (cornix <i>Hier</i>)."],
  ["raucio", "<big>raucio</big>\n\n<b>raucio, rausi, rausum, ire</b> <i>LM</i>\n= raucesco."],
  ["raucisonus", "<big>raucisonus</big>\n\n<b>rauci–sonus, a, um</b>\n<b>1)</b> хриплый (cantus cornicum <i>Lcr</i>);\n<b>2)</b> (<i>о звуке</i>) глухой (bombi <i>Ctl</i>)."],
  ["raucitas", "<big>raucitas</big>\n\n<b>raucitas, atis</b> <i>f</i>\n<b>1)</b> охриплость, хрипота <i>CC, PM, Scr</i>;\n<b>2)</b> глухой <i>или</i> сиплый звук (tubarum <i>PM</i>)."],
  ["raucus", "<big>raucus</big>\n\n<b>raucus, a, um</b>\n<b>1)</b> осипший, сиплый, хриплый (tussis <i>Lcr</i>): r. factus sum rogitando <i>Pl</i> я охрип от расспросов;\n<b>2)</b> глухой, негромкий, рокочущий (murmur <i>V</i>; tonitrua <i>St</i>); гудящий, глухо звучащий (tympana <i>O</i>; aes <i>V</i>); воркующий (palumbes <i>V</i>);\n<b>3)</b> пронзительный (cicada <i>V</i>)."],
  ["Raudii campi", "<big>Raudii campi</big>\n\n<b>Raudii campi</b> <i>или</i> <b>Raudius campus</b> <i>m</i>\n<i>равнина близ Вероны, на которой Марий в 101 г. до н. э. нанёс поражение кимбрам</i> <i>VP, Fl, AV</i>."],
  ["raudus", "<big>raudus</big>\n\n<b>raudus, eris</b> (<i>pl.</i> rudera, um) <i>n</i>\nкусочек меди, медяк (<i>употр. в качестве монеты</i>) <i>Vr, VM, L</i>."],
  ["Rauduscula", "<big>Rauduscula</big>\n\n<b>Rauduscula</b> <i>и</i> <b>Raudusculana porta</b> <i>f</i>\n<i>ворота в Риме между Porta Naevia <i>и</i> Porta Lavernalis</i> (<i>по делению Сервия Туллия</i>) <i>Vr, VM</i>."],
  ["raudusculum", "<big>raudusculum</big>\n\n<b>raudusculum, i</b> <i>m</i> [<i>demin. к</i> raudus]\nмелкие деньги, мелочь <i>C</i>."],
  ["Rauraci", "<big>Rauraci</big>\n\n<b>Rauraci</b> <i>и</i> <b>Raurici, orum</b> <i>m</i>\nраурики, <i>кельт. племя на Рейне с главн. городом Augusta Rauricorum</i> <i>Cs</i>."],
  ["Rauracum", "<big>Rauracum</big>\n\n<b>Rauracum (Rauricum), i</b> <i>m</i>\nРаурик, <i>главный город рауриков</i> (<i>ныне</i> Augst, <i>близ Базеля</i>) <i>PM, Amm</i>."],
  ["ravastellus", "<big>ravastellus</big>\n\n<b>ravastellus, i</b> <i>m</i> <i>Pl</i>\n(<i>v. l.</i>) = gravastellus."],
  ["Ravenna", "<big>Ravenna</big>\n\n<b>Ravenna, ae</b> <i>f</i>\nРавенна, <i>город в Gallia Cispadana, к югу от впадения реки Пад в Адриатическое море</i> (aequorea <i>M</i>; paludosa <i>Sil</i>)."],
  ["Ravennas", "<big>Ravennas</big>\n\n<b>Ravennas, atis</b> <i>adj.</i> [Ravenna]\nравеннский (vir <i>C</i>; ranae <i>M</i>)."],
  ["ravicelus", "<big>ravicelus</big>\n\n<b>ravicelus, i</b> <i>m</i>\nшишки пинии, варёные в меду <i>PM</i>."],
  ["ravidus", "<big>ravidus</big>\n\n<b>ravidus, a, um</b> [ravus I]\nсероватый, серо-жёлтый (oculi gallinaceorum <i>Col</i>)."],
  ["ravio", "<big>ravio</big>\n\n<b>ravio, —, —, ire</b> [ravis]\nговорить до хрипоты <i>Pl</i>."],
  ["ravis", "<big>ravis</big>\n\n<b>ravis</b> (<i>acc.</i> im) <i>f</i>\nохриплость, хрипота <i>Pl</i>."],
  ["ravistellus", "<big>ravistellus</big>\n\n<b>ravistellus, i</b> <i>m</i> <i>v. l.</i>\n= gravastellus."],
  ["ravulus", "<big>ravulus</big>\n\n<b>ravulus, a, um</b> [ravus II]\nхрипливый <i>Sid</i>."],
  ["ravus", "<big>ravus</big>\n\n<b>I ravus, a, um</b>\nсерый, серо-жёлтый (lupus, leo <i>H</i>).\n\n<b>II ravus, a, um</b> <i>Sid</i>\n= raucus."],
  ["re", "<big>re</big>\n\n<b>I re– (red-)</b>\n<i>приставка, выражающая</i>:\n<b>1)</b> <i>обратное действие</i> (red–ire);\n<b>2)</b> <i>возобновление, повторность</i> (re–ponere, re–cognoscere);\n<b>3)</b> <i>противодействие</i> (re–sistere);\n<b>4)</b> <i>противоположное действие</i> (re–probare).\n\n<b>II re</b> <i>abl. к</i>\nres."],
  ["rea", "<big>rea</big>\n\n<b>I rea, ae</b> <i>f</i> [reus]\nобвиняемая <i>C</i>.\n\n<b>II Rea, ae</b> <i>f</i> <i>v. l.</i>\n= Rhea I <i>и</i> II."],
  ["reaccendo", "<big>reaccendo</big>\n\n<b>re–accendo, —, accensum, ere</b>\nвновь воспламенять <i>Eccl</i>."],
  ["readunatio", "<big>readunatio</big>\n\n<b>re–adunatio, onis</b> <i>f</i>\nвоссоединение <i>Tert</i>."],
  ["reaedifico", "<big>reaedifico</big>\n\n<b>re–aedifico, —, —, are</b>\nотстраивать, восстанавливать (aliquid <i>Eccl</i>)."],
  ["reapse", "<big>reapse</big>\n\n<b>reapse</b> [re + eapse = ea ipsa]\nв самом деле, действительно <i>Pac, Pl, C</i>."],
  ["Reate", "<big>Reate</big>\n\n<b>Reate, is</b> (<i>abl.</i> e) <i>n</i>\nРеата, <i>древняя столица сабинян</i> (<i>ныне</i> Rieti) <i>L, PM</i>."],
  ["Reatinus", "<big>Reatinus</big>\n\n<b>I Reatinus, a, um</b> [Reate]\nреатинский (ager <i>Vr</i>; praefectura <i>C</i>).\n\n<b>II Reatinus, i</b> <i>m</i>\nжитель города Reate <i>Vr, C, L, PM</i>."],
  ["reatus", "<big>reatus</big>\n\n<b>reatus, us</b> <i>m</i> [reus]\n<b>1)</b> состояние под судом <i>Just, Q, M, Dig</i>;\n<b>2)</b> обвинение, вина <i>Ap, Eccl</i>;\n<b>3)</b> (= sordes) одежда обвиняемого (mulier reatu deformis <i>Ap</i>)."],
  ["rebaptizo", "<big>rebaptizo</big>\n\n<b>re–baptizo, —, —, are</b>\nвновь крестить, перекрещивать <i>CJ, Eccl</i>."],
  ["rebar", "<big>rebar</big>\n\n<b>rebar</b> <i>impf. к</i>\nreor."],
  ["rebellatio", "<big>rebellatio</big>\n\n<b>rebellatio, onis</b> <i>f</i> <i>T, VM</i>\n= rebellio 1."],
  ["rebellatrix", "<big>rebellatrix</big>\n\n<b>rebellatrix, icis</b> <i>f</i> [rebello]\nвозобновляющая войну, бунтующая, мятежница (provincia <i>L</i>)."],
  ["rebellio", "<big>rebellio</big>\n\n<b>I rebellio, onis</b> <i>f</i> [rebellis]\nвозобновление войны (r. facta post deditionem <i>Cs</i>); мятеж, восстание <i>L, Su, T etc.</i>\n\n<b>II rebellio, onis</b> <i>m</i>\nповстанец <i>Treb, Vop</i>."],
  ["rebellis", "<big>rebellis</big>\n\n<b>I rebellis, e</b> [re + bellum]\n<b>1)</b> возобновляющий войну, мятежный (Numidae <i>O</i>);\n<b>2)</b> непокорный, строптивый (animus <i>Treb, Spart</i>; amor <i>O</i>).\n\n<b>II rebellis, is</b> <i>m</i>\nмятежник <i>T</i>."],
  ["rebello", "<big>rebello</big>\n\n<b>re–bello, avi, atum, are</b>\n<b>1)</b> опять начинать войну, восставать (aliquem ad rebellandum excitare <i>L</i>); возобновлять сражение <i>O</i>;\n<b>2)</b> противиться, сопротивляться (pudor rebellat <i>SenT</i>);\n<b>3)</b> вновь прорываться, снова обнаруживаться (vitia rebellant <i>PM</i>)."],
  ["Rebilus", "<big>Rebilus</big>\n\n<b>Rebilus, i</b> <i>m</i>\nРебил, <i>cognomen в роде Каниниев</i> <i>Sen</i>."],
  ["rebito", "<big>rebito</big>\n\n<b>re–bito, —, —, ere</b> <i>Pl</i>\n= redeo."],
  ["reboo", "<big>reboo</big>\n\n<b>re–boo, —, —, are</b>\nоткликаться, отзываться, звучать в ответ (reboat ursa lupis <i>VF</i>); давать отголосок (reboant silvae <i>V</i>)."],
  ["rebullio", "<big>rebullio</big>\n\n<b>re–bullio, ivi (ii), itum, ire</b>\n<b>1)</b> бить ключом, вскипать (vinum rebullit <i>Ap</i>);\n<b>2)</b> заставлять клокотать, доводить до кипения (oleum <i>Ap</i>)."],
  ["reburrus", "<big>reburrus</big>\n\n<b>reburrus, a, um</b>\nс взъерошенными волосами (homo <i>Aug</i>)."],
  ["recalcitro", "<big>recalcitro</big>\n\n<b>re–calcitro, avi, —, are</b>\n<b>1)</b> бить задними ногами, брыкаться (jumenta recalcitrantia <i>Aug</i>);\n<b>2)</b> не допускать до себя, противиться <i>H, Amm, Vlg, Eccl</i>."],
  ["recalco", "<big>recalco</big>\n\n<b>re–calco, —, atum, are</b>\nснова попирать (humum <i>Col</i>); вновь вступать, идти (vestigia priora <i>Ap</i>); <i>перен.</i> повторять (mysteriis recalcatis <i>CTh</i>)."],
  ["recalefacio", "<big>recalefacio</big>\n\n<b>re–calefacio (recalfacio), feci, factum, ere</b>\nснова согревать, опять отогревать (aliquid <i>O, Scr, CA</i>)."],
  ["recaleo", "<big>recaleo</big>\n\n<b>re–caleo, —, —, ere</b>\nвновь и вновь нагреваться, быть горячим, оставаться тёплым <i>V, Amm, Aus</i>."],
  ["recalesco", "<big>recalesco</big>\n\n<b>recalesco, calui, —, ere</b> [<i>inchoat. к</i> recaleo]\nвновь нагреваться, опять согреваться (motu atque exercitatione <i>C</i>); разгорячаться (mens recalescit <i>O</i>)."],
  ["recalfacio", "<big>recalfacio</big>\n\n<b>re–calfacio</b> <i>v. l.</i>\n= recalefacio."],
  ["recalvaster", "<big>recalvaster</big>\n\n<b>recalvaster, stri</b> <i>Vlg</i>\n= recalvus."],
  ["recalvatio", "<big>recalvatio</big>\n\n<b>recalvatio, onis</b> <i>f</i>\nплешь на передней части головы <i>Vlg</i>."],
  ["recalvities", "<big>recalvities</big>\n\n<b>recalvities, ei</b> <i>f</i> <i>Eccl</i>\n= recalvatio."],
  ["recalvus", "<big>recalvus</big>\n\n<b>re–calvus, a, um</b>\nс залысиной на лбу (senex <i>Pl</i>); плешивый (frons <i>Sen</i>)."],
  ["recandesco", "<big>recandesco</big>\n\n<b>re–candesco, dui, —, ere</b>\n<b>1)</b> снова становиться белым (от пены), опять вспениваться (unda recanduit <i>O</i>);\n<b>2)</b> опять раскаляться, накаляться (terra recanduit aestu <i>O</i>);\n<b>3)</b> разгорячаться, вскипать (recanduit ira <i>O</i>)."],
  ["recano", "<big>recano</big>\n\n<b>re–cano, —, —, ere</b>\n<b>1)</b> звать назад, откликаться пением (perdix recanit revocatque marem <i>PM</i>);\n<b>2)</b> снимать чары, расколдовывать <i>PM</i>."],
  ["recanto", "<big>recanto</big>\n\n<b>re–canto, —, —, are</b>\n<b>1)</b> давать отголосок, откликаться, отдаваться (recantat echo <i>M</i>);\n<b>2)</b> снимать чары, расколдовывать, <i>т. е.</i> отгонять прочь (curas <i>O</i>);\n<b>3)</b> снова напевать (versiculum <i>Ambr</i>);\n<b>4)</b> брать назад, отрекаться (r. opprobria <i>H</i>)."],
  ["recapitulatio", "<big>recapitulatio</big>\n\n<b>recapitulatio, onis</b> <i>f</i>\nсжатое повторение, рекапитуляция <i>Aug</i>."],
  ["recasum", "<big>recasum</big>\n\n<b>recasum</b> <i>supin. к</i>\nrecido I."],
  ["recaveo", "<big>recaveo</big>\n\n<b>re–caveo, cavi, cautum, ere</b>\nдавать со своей стороны гарантии <i>CTh</i>."],
  ["recavus", "<big>recavus</big>\n\n<b>re–cavus, a, um</b>\nвогнутый (speculum <i>Eccl</i>)."],
  ["reccido", "<big>reccido</big>\n\n<b>reccido</b> <i>поэт. Lcr, O, J</i>\n= recido I."],
  ["recedo", "<big>recedo</big>\n\n<b>re–cedo, cessi, cessum, ere</b>\n<b>1)</b> уходить назад (e Gallia <i>C</i>); подаваться назад, отступать (in castra <i>Cs</i>); уходить, миновать (anni recedentes <i>H</i>): r. in ventos <i>V</i> превращаться в дым, исчезать, улетучиваться || уклоняться, увёртываться (a telo <i>O</i>);\n<b>2)</b> быть удалённым, находиться в отдалении, теряться вдали <i>или</i> уходить вдаль, скрываться из виду (terraeque urbesque recedunt <i>V</i>); удаляться (a conspectu suorum <i>Nep</i>): in otia tuta r. <i>H</i> уходить на покой; pigritiam recedendi imposuerat hilaritas <i>Pt</i> весёлое настроение отняло желание спать (разогнало сон);\n<b>3)</b> уклоняться (a verbo, ab officio <i>C</i>; ab usu cotidiano <i>Q</i>); отказываться (ab oppugnatione <i>Hirt</i>): r. ab armis <i>C</i> сложить оружие; r. a vita <i>C</i> лишить себя жизни;\n<b>4)</b> отделяться (caput cervice recedit <i>O</i>);\n<b>5)</b> отклоняться: nomen hostis a peregrino recessit <i>C</i> слово «hostis» отклонилось от (первоначального) значения (утратило значение) «иноземец»."],
  ["recello", "<big>recello</big>\n\n<b>recello, —, —, ere</b> [re + celio поражать; <i>ср.</i> procella]\n<b>1)</b> отскакивать, отлетать назад (inclinari retroque r. <i>Lcr</i>);\n<b>2)</b> отгибать назад (totum corporis pondus <i>Ap</i>)."],
  ["recens", "<big>recens</big>\n\n<b>I recens, entis</b> <i>adj.</i>\n<b>1)</b> свежий (aqua <i>O</i>); парной (lac <i>O</i>); молодой (vinum <i>Sen</i>); младший (<i>Cato Sen</i>);\n<b>2)</b> недавний (injuriae <i>Cs</i>); последний, недавно полученный (epistula <i>C</i>); новый, современный (Graeci <i>C</i>): animae recentes <i>O, SenT</i> души недавно почивших (<i>ср.</i> 4); laude r. <i>H</i> покрытый неувядающей славой; recenti re (<i>или</i> negotio) <i>C</i> тут же, тотчас; vox, a qua r. sum <i>Sen</i> слово, недавно услышанное мною; r. memoria <i>C</i> недавнее (новое) время;\n<b>3)</b> только что (недавно) пришедший (r. Roma <i>C</i>; r. e provincia <i>C</i>): r. a partu <i>Vr</i> только что родившийся; r. (in) dolore <i>T</i> ещё не оправившийся от скорби; r. victoria <i>T</i> недавно одержавший победу; prata recentia rivis <i>V</i> только что политые луга; homines recentes <i>C</i> люди недавнего прошлого, <i>но</i>: <i>H</i> первобытные люди;\n<b>4)</b> свежий, бодрый, неизнурённый (recentes atque integri <i>Cs</i>): recentis animi <i>L</i> со свежими душевными силами (<i>ср.</i> 2).\n\n<b>II recens</b> <i>в знач. adv.</i>\nнедавно <i>или</i> только что (sol r. ortus <i>V</i>; clades r. accepta <i>L</i>): fabula, quam r. fecerat <i>Ap</i> пьеса, которую он недавно сочинил."],
  ["recenseo", "<big>recenseo</big>\n\n<b>re–censeo, censui, censum (censitum), ere</b>\n<b>1)</b> пересчитывать, проверять (numerum suorum <i>V</i>); производить подсчёт, осматривать (captivos praedamque <i>L</i>); делать смотр, обходить, инспектировать (exercitum <i>L</i>);\n<b>2)</b> размышлять, обдумывать (aliquid <i>Q</i>);\n<b>3)</b> перечислять, приводить, рассказывать (fortia facta <i>O</i>);\n<b>4)</b> критически разбирать, рецензировать (poemata <i>AG</i>);\n<b>5)</b> <i>поэт.</i> пробегать, проходить: signa sua sol recenset <i>O</i> солнце проходит свой путь."],
  ["recensio", "<big>recensio</big>\n\n<b>recensio, onis</b> <i>f</i> [recenseo]\nосмотр, цензорское обследование <i>C, Su</i>."],
  ["recensitio", "<big>recensitio</big>\n\n<b>recensitio, onis</b> <i>f</i> <i>Dig</i>\n= recensio."],
  ["recensitus", "<big>recensitus</big>\n\n<b>recensitus</b> <i>и</i> <b>recensus</b> <i>part. pf. к</i>\nrecenseo."],
  ["recensus", "<big>recensus</big>\n\n<b>recensus, us</b> <i>m</i> <i>L, Su, Tert</i>\n= recensio."],
  ["recenter", "<big>recenter</big>\n\n<b>recenter</b> <i>Pall, PM, Just</i>\n= recens II."],
  ["recentor", "<big>recentor</big>\n\n<b>recentor, —, ari</b> [recens]\nобновляться (recentatur commune lumen hominibus voluptatis <i>Matius ap. AG</i>)."],
  ["Recentoricus ager", "<big>Recentoricus ager</big>\n\n<b>Recentoricus ager</b> <i>m</i>\n<i>государственный земельный фонд в Сицилии</i> <i>C</i>."],
  ["recepi", "<big>recepi</big>\n\n<b>recepi</b> <i>pf. к</i>\nrecipio."],
  ["recepso", "<big>recepso</big>\n\n<b>recepso</b>\n<i>Ctl</i> = recepero (<i>fut. II к</i> recipio)."],
  ["receptabilis", "<big>receptabilis</big>\n\n<b>receptabilis, e</b>\nвосприимчивый, доступный (passionis <i>Ambr</i>)."],
  ["receptaculum", "<big>receptaculum</big>\n\n<b>receptaculum, i</b> <i>n</i> [recepto]\n<b>1)</b> вместилище (corpus animi r. <i>C</i>); хранилище, кладовая (frugibus <i>T</i>; olei <i>Pall</i>): r. aquae <i>Dig</i> водохранилище || водоотводный канал (Nili <i>T</i>): r. mercibus <i>L</i> товарный склад;\n<b>2)</b> пристанище, приют, убежище (hostibus <i>L</i>; refugium atque r. <i>Dig</i>); сборный пункт: r. adversae pugnae <i>L</i> место сбора после неудачного сражения."],
  ["receptatio", "<big>receptatio</big>\n\n<b>receptatio, onis</b> <i>f</i> [recipio]\nвбирание в себя, вдыхание (spiritus <i>Amm</i>)."],
  ["receptator", "<big>receptator</big>\n\n<b>receptator, oris</b> <i>m</i> [recepto] <i>Fl, Dig</i>\n= receptor."],
  ["receptibilis", "<big>receptibilis</big>\n\n<b>receptibilis, e</b> [recipio]\n<b>1)</b> могущий быть вновь обретённым (amissibilis et r. <i>Aug</i>);\n<b>2)</b> постижимый (verbum incarnatum <i>Aug</i>)."],
  ["recepticius", "<big>recepticius</big>\n\n<b>recepticius, a, um</b> [recipio] <i>юр.</i>\nрецептивный:\n<b>1)</b> обусловленный в качестве исключительной собственности жены (dos <i>Dig</i>);\n<b>2)</b> касающийся взятого кем-л. на себя поручительства (actio <i>CJ</i>)."],
  ["receptio", "<big>receptio</big>\n\n<b>receptio, onis</b> <i>f</i> [recipio]\n<b>1)</b> приём, принятие (aliquem ad se r. <i>Pl</i>);\n<b>2)</b> укрывательство (per receptionem subtrahere aliquem <i>CJ</i>);\n<b>3)</b> оговорка <i>Dig</i>."],
  ["recepto", "<big>recepto</big>\n\n<b>recepto, avi, atum, are</b> [<i>intens. к</i> recipio]\n<b>1)</b> брать назад, обратно вынимать (hastam ossibus haerentem <i>V</i>): se r. отступать назад, ретироваться, удирать (in tectum <i>Ter</i>): litus se receptat <i>Pers</i> берег вдаётся в глубь (образует излучину);\n<b>2)</b> назад <i>или</i> вновь принимать (cuncta sinu natura receptat <i>Lcn</i>); часто принимать (aliquem ad se <i>Ter</i>);\n<b>3)</b> давать убежище, укрывать (aliquem adversus aliquem <i>T</i>)."],
  ["receptor", "<big>receptor</big>\n\n<b>receptor, oris</b> <i>m</i> [recipio]\n<b>1)</b> вновь добывший, снова завоевавший (r. Orientis Occidentisque <i>Eutr</i>);\n<b>2)</b> укрыватель (praedarum <i>T</i>; furum <i>Dig</i>);\n<b>3)</b> убежище, вертеп (latronum occultator et r. locus <i>C</i>);\n<b>4)</b> освободитель (civium <i>Eccl</i>)."],
  ["receptorium", "<big>receptorium</big>\n\n<b>receptorium, i</b> <i>n</i>\nубежище, приют <i>Sid</i>."],
  ["receptorius", "<big>receptorius</big>\n\n<b>receptorius, a, um</b>\nслужащий убежищем (locus <i>Tert</i>)."],
  ["receptrix", "<big>receptrix</big>\n\n<b>receptrix, icis</b> <i>f</i>\n<b>1)</b> принимающая у себя, дающая убежище (terra omnium r. <i>Ap</i>);\n<b>2)</b> укрывательница (praedarum ac furtorum <i>C</i>)."],
  ["receptum", "<big>receptum</big>\n\n<b>receptum, i</b> <i>n</i>\nпринятое обязательство, обязанность, гарантия (promissum ac r. <i>C</i>)."],
  ["receptus", "<big>receptus</big>\n\n<b>I receptus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> recipio;\n<b>2.</b> <i>adj.</i> общепринятый, общераспространённый, общепризнанный (persuasio <i>Q</i>; scriptor <i>Sol</i>).\n\n<b>II receptus, us</b> <i>m</i> [recipio]\n<b>1)</b> взятие назад, отказ (r. sententiae <i>L</i>);\n<b>2)</b> <i>воен.</i> отступление, отход (r. ad suos <i>Cs</i>): receptui canere <i>Cs</i> (signum dare <i>L, QC, bAl</i>) трубить отступление, <i>перен.</i> переставать, прекращать, давать покой (сапе, Musa, receptus <i>O</i>); canere receptui ab aliqua re <i>C</i> отвлечься от чего-л.;\n<b>3)</b> возможность отступления (dare alicui receptum <i>Cs</i>);\n<b>4)</b> убежище, прибежище (habere receptum ad aliquem <i>Cs</i>);\n<b>5)</b> получение обратно, восстановление: occasio receptus difficiles habet <i>PS</i> (упущенный) случай редко повторяется; spiritus in receptu difficilis <i>Q</i> затруднённость дыхания, одышка."],
  ["recessa", "<big>recessa</big>\n\n<b>recessa, ae</b> <i>f</i> [recedo]\nморской отлив <i>Is</i>."],
  ["recessi", "<big>recessi</big>\n\n<b>recessi</b> <i>pf. к</i>\nrecedo."],
  ["recessim", "<big>recessim</big>\n\n<b>recessim</b> [recedo]\nназад, вспять (cedere <i>Pt</i>)."],
  ["recessio", "<big>recessio</big>\n\n<b>recessio, onis</b> <i>f</i> <i>Vtr, Eccl</i>\n= recessus II."],
  ["recessus", "<big>recessus</big>\n\n<b>I recessus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> recedo;\n<b>2.</b> <i>adj.</i> глубокий (scaena recessior <i>Vtr</i>).\n\n<b>II recessus, us</b> <i>m</i> [recedo]\n<b>1)</b> отступление, удаление (lunae accessus et r. <i>C</i>);\n<b>2)</b> возвращение (avium <i>L</i>): accessus et r. maris <i>C</i> морской прилив и отлив; r. ab aliqua re <i>C</i> отвращение к чему-л.; r. animi <i>C</i> робость, застенчивость <i>или</i> замкнутость;\n<b>3)</b> углубление, впадина (r. speluncae similis <i>O</i>); полость (<i>sc.</i> equi Trojani <i>Pt</i>);/; уединённое, скрытое место, уголок, убежище (mihi provincia est solitudo et r. <i>C</i>);\n<b>4)</b> внутренний покой, потайная комната (domus in multos diducta recessus <i>Q</i>); <i>перен.</i> тайник (in animis hominum tanti sunt recessus <i>C</i>);\n<b>5)</b> фон картины, задний план, перспектива (umbra et r. <i>C</i>)."],
  ["rechamus", "<big>rechamus</big>\n\n<b>rechamus, i</b> <i>m</i> <i>тех.</i>\n(— trochlea) блок <i>Vtr</i>."],
  ["recharmido", "<big>recharmido</big>\n\n<b>recharmido, —, —, are</b> [re + Charmides] <i>шутл.</i>\nперестать быть Хармидом, расхармидиться <i>Pl</i>."],
  ["recidivatus", "<big>recidivatus</big>\n\n<b>recidivatus, us</b> <i>m</i> [recidivus]\nвосстановление, возобновление (carnis <i>Tert</i>)."],
  ["recidivus", "<big>recidivus</big>\n\n<b>recidivus, a, um</b> [recedo]\nвозобновляющийся (febris <i>CC, PM</i>); восстанавливающийся, вновь отстроенный: recidiva posuisse Pergama <i>V</i> заново отстроить Пергам; de recidivo <i>Tert</i> сызнова."],
  ["recido", "<big>recido</big>\n\n<b>I re–cido, cidi, casum, ere</b> [cado]\n<b>1)</b> падать назад (in terras <i>C</i>); отскакивать (ramulus adductus in oculum recidit <i>C</i>);\n<b>2)</b> снова впадать (in imbecillitatem <i>CC</i>); снова попадать, вновь оказываться (r. in antiquam servitutem <i>L</i>);\n<b>3)</b> вновь переходить, возвращаться (potentatus recidit ad aliquem <i>C</i>);\n<b>4)</b> падать, обрушиваться (poena recidit in <i>или</i> ad aliquem <i>C</i>);\n<b>5)</b> переходить, превращаться (ad aliquid <i>Ph</i>): in morbum r. <i>L</i> вновь заболеть; ad nihil(um) r. <i>C etc.</i> свестись к нулю, исчезнуть; id ego puto ad nihil recasurum <i>C</i> думаю, что из этого ничего не выйдет; r. in periculum <i>QC</i> подвергнуться опасности; r. ad ludibrium <i>QC</i> стать посмешищем; quorsum recidat responsum tuum, non magno opere laboro <i>C</i> как сложится (каков будет) твой ответ, мне довольно безразлично; ne delusa spes ad querelam recidat <i>Ph</i> как бы обманутая надежда не превратилась в жалобу (не кончилась бы разочарованием);\n<b>6)</b> приходиться, случаться (in nostrum annum <i>C</i>); выпадать на долю, быть уделом (r. in paucos <i>Ter</i>).\n\n<b>II re–cido, cidi, cisum, ere</b> [caedo]\n<b>1)</b> отрубать, отрез(ыв)ать, отсекать (sceptrum de stirpe <i>V</i>): cervice recisa <i>Lcn</i> отрубив голову; Deliaci manu recisi <i>Pt</i> делосские кастраты (о-в <i>Делос был центром торговли рабами и, в частности, евнухами</i>) || вырубать (hastile e silva <i>O</i>); вырезывать (vulnus ense <i>O</i>); высекать (columnas <i>H</i>); обстригать (capillos <i>PJ</i>; ungues <i>PM</i>);\n<b>2)</b> истреблять, вырез(ыв)ать (nationes <i>C</i>);\n<b>3)</b> вычёркивать (aliquid ex oratione <i>Q</i>); устранять, удалять (quidquid obstat alicui rei <i>QC</i>);\n<b>4)</b> заглаживать (culpam supplicio <i>H</i>);\n<b>5)</b> урезывать, сокращать (ambitiosa ornamenta <i>H</i>); умерять, ограничивать (nimiam loquacitatem <i>Q</i>): opus recisum <i>VP</i> сжатый очерк."],
  ["recingo", "<big>recingo</big>\n\n<b>re–cingo, cinxi, cinctum, ere</b>\nраспоясывать, распускать (tunicam <i>O</i>; in veste recincta <i>V</i>); снимать (casta zona recincta <i>O</i>); <i>med.-pass.</i> recingi распоясываться, раздеваться, снимать с себя: recingi ferrum <i>Sl</i> снять с себя меч, <i>но тж.</i> <i>Amm</i> вновь опоясываться (мечом), <i>т. е.</i> снова быть призванным на военную службу."],
  ["recino", "<big>recino</big>\n\n<b>re–cino, —, —, ere</b> [cano]\n<b>1)</b> петь в ответ, откликаться, давать отзвук, повторять (r. nomen alicujus <i>H</i>; r. dictata <i>H</i>);\n<b>2)</b> петь, воспевать (lyra Latonam <i>H</i>);\n<b>3)</b> раздаваться, звучать, слышаться (in vocibus nostrorum oratorum recinit quiddam urbanius <i>C</i>);\n<b>4)</b> отрекаться (от прежнего), отказываться от своих слов (recinendo placare aliquem indignatum <i>Ap</i>)."],
  ["reciper-", "<big>reciper-</big>\n\n<b>reciper-</b> <i>Ap</i>\n= recuper-."],
  ["recipio", "<big>recipio</big>\n\n<b>recipio, cepi, ceptum, ere</b> [re + capio]\n<b>1)</b> вынимать обратно (ensem <i>V</i>); <i>но</i>: ense recepto (<i>к</i> 8) <i>Sil</i> пронзив себя мечом || выводить (aliquem medio ex hoste <i>V</i>);\n<b>2)</b> обращать назад (gressum ad limina <i>V</i>); уводить (milites <i>Cs</i>); отводить, обратно стягивать (copias in oppidum <i>L</i>); отпускать, освобождать (aliquem ex servitute <i>L</i>);\n<b>3)</b>: se r. возвращаться (e Sicilia <i>C</i>; in civitatem <i>Ap</i> — <i>ср.</i> 8): recipe te ad nos <i>C</i> вернись к нам; signo recipiendi dato <i>Cs</i> по сигналу, данному к отступлению || вновь обращаться (ad bonam frugem <i>C</i>; a voluptatibus in otium <i>PJ</i>);\n<b>4)</b>: se r. (<i>реже</i> r.) отступать (se r. in silvas <i>Cs</i>);\n<b>5)</b> оставить за собой, выговорить себе, обусловить (aliquid sibi <i>C</i>);\n<b>6)</b> вновь надевать (recepta tunica <i>Pt</i>);\n<b>7)</b> вновь обретать (rem amissam <i>L</i>); вновь овладевать (r. urbem <i>L</i>); завладевать, завоёвывать, захватывать (rem publicam armis <i>Sl</i>): r. arma <i>L</i> вновь взяться за оружие; spiritum r. <i>Q</i> перевести дыхание; se r. in principem <i>PJ</i> вновь войти в роль принцепса; se r. <i>или</i> animum r. <i>L</i> вновь прийти в себя, овладеть собой, воспрянуть духом; se r. ex terrore <i>Cs</i> (ex <i>и</i> a pavore <i>L</i>) оправиться от страха;\n<b>8)</b> принимать (aliquem in ordinem senatorium <i>C</i>; aliquem domum suam <i>C</i>; aliquem in amicitiam <i>Sen</i>); впускать (aliquem finibus <i>или</i> intra fines <i>Cs</i>); принимать внутрь (<i>sc.</i> medicamenta <i>Scr etc.</i>); получать (pecunias ex vectigalibus <i>C</i>); допускать, терпеть (res non recipit cunctationem <i>L</i>): r. telum (ferrum) <i>C</i> получить смертельный удар; poenas ab aliquo r. <i>V</i> наказывать кого-л. <i>или</i> мстить кому-л.; sinu gremioque, <i>тж.</i> sinu complexuque r. <i>C</i> принять с распростёртыми объятиями; r. aliquem sessum <i>C</i> усадить кого-л. (рядом с собой); r. in fidem <i>C</i> принять под покровительство; r. in parem juris libertatisque condicionem <i>Cs</i> уравнять в правах; r. aliquem civitate <i>и</i> in civitatem <i>C</i> присвоить кому-л. права гражданства, <i>но</i>: civitatem r. aliquos <i>Cs</i> принять кого-л. в подданство (<i>ср. тж.</i> 3); receptum est <i>PM, Dig</i> (обще)принято; scriptores receptissimi <i>Sol</i> наиболее общепризнанные писатели, <i>т. е.</i> классики; cicatricem r. <i>CC</i> (<i>о ране</i>) зарубцовываться, заживать; nomen alicujus r. <i>C</i> принять жалобу на кого-л.; aliquem reum (<i>или</i> inter reos) r. <i>T</i> посадить кого-л. на скамью подсудимых; causam alicujus r. <i>C</i> принять на себя чьё-л. дело;\n<b>9)</b> обещать, сулить, ручаться, гарантировать, обязываться (alicui aliquid <i>или</i> de aliqua re <i>C</i>): aliquid pro aliquo r. <i>C</i> поручиться в чём-л. за кого-л.; fidem alicui r. <i>C</i> дать кому-л. клятвенное заверение."],
  ["reciprocatio", "<big>reciprocatio</big>\n\n<b>reciprocatio, onis</b> <i>f</i> [reciproco]\n<b>1)</b> возвращение, возврат, попятное движение (siderum <i>AG</i>): r. aestus <i>PM</i> морской отлив; r. animorum <i>Tert</i> = metempsychosis;\n<b>2)</b> взаимность: r. talionum <i>AG</i> взаимное возмездие."],
  ["reciprocatus", "<big>reciprocatus</big>\n\n<b>reciprocatus, us</b> <i>m</i> <i>Eccl</i>\n= reciprocatio."],
  ["reciproce", "<big>reciproce</big>\n\n<b>reciproce</b> [reciprocus]\nвзад и вперёд, туда и обратно (fluere <i>Vr</i>)."],
  ["reciprocicornis", "<big>reciprocicornis</big>\n\n<b>reciproci–cornis, e</b>\nс загнутыми внутрь, <i>т. е.</i> спиральными рогами (aries <i>Tert</i>)."],
  ["reciproco", "<big>reciproco</big>\n\n<b>reciproco, avi, atum, are</b> [reciprocus]\nвозвращать (in adversum reciprocari <i>L</i>); двигать взад и вперёд, потрясать (telum manu <i>AG</i>): r. animam <i>L</i> вдыхать и выдыхать, дышать; in motu reciprocando <i>C</i> в смене приливов и отливов; mare reciprocat <i>PM etc.</i> (<i>или</i> reciprocatur <i>QC</i>) на море происходят приливы и отливы."],
  ["reciprocus", "<big>reciprocus</big>\n\n<b>reciprocus, a, um</b>\n<b>1)</b> возвращающийся, обратный, движущийся взад и вперёд (telum <i>Vr</i>): mare reciprocum <i>PM</i> морской отлив; reciprocae vices pugnandi <i>AG</i> превратности сражения;\n<b>2)</b> отражающийся, отдающийся (vox <i>PM</i>): argumenta reciproca <i>AG</i> доводы, обращающиеся против их автора;\n<b>3)</b> взаимный (taliones <i>AG</i>);\n<b>4)</b> <i>грам.</i> возвратный (pronomen)."],
  ["recisamentum", "<big>recisamentum</big>\n\n<b>recisamentum, i</b> <i>n</i> [recido]\nобрезки, опилки <i>PM</i>."],
  ["recisio", "<big>recisio</big>\n\n<b>recisio, onis</b> <i>f</i> [recido]\nобрезывание (rosae <i>PM</i>); урезывание, уменьшение (legatorum <i>Dig</i>)."],
  ["recisus", "<big>recisus</big>\n\n<b>recisus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> recido;\n<b>2.</b> <i>adj.</i> сжатый, краткий (opus <i>VP</i>); ограниченный (tempus <i>Dig</i>)."],
  ["recitatio", "<big>recitatio</big>\n\n<b>recitatio, onis</b> <i>f</i> [recito]\nчтение вслух <i>PJ, T, Ap</i>; <i>юр.</i> оглашение, зачитывание (legis <i>rhH</i>; litterarum <i>C</i>)."],
  ["recitator", "<big>recitator</big>\n\n<b>recitator, oris</b> <i>m</i> [recito]\nчитающий вслух, чтец <i>L, Sen, PJ</i>."],
  ["recito", "<big>recito</big>\n\n<b>re–cito, avi, atum, are</b>\n<b>1)</b> читать вслух (quod erat cerae creditum <i>Pl</i>); зачитывать, оглашать (testimonium <i>C</i>): r. senatum <i>C</i> огласить список сенаторов;\n<b>2)</b> произносить вслух (sacramentum <i>T</i>);\n<b>3)</b> читать наизусть: qui recitare solent <i>M</i> = актёры."],
  ["reclamatio", "<big>reclamatio</big>\n\n<b>reclamatio, onis</b> <i>f</i> [reclamo]\nгромкое возражение, неодобрение, возгласы протеста <i>C, Ap, Aug</i>."],
  ["reclamito", "<big>reclamito</big>\n\n<b>reclamito, —, —, are</b> [<i>intens. к</i> reclamo]\nгромко возражать, протестовать <i>C</i>."],
  ["reclamo", "<big>reclamo</big>\n\n<b>re–clamo, avi, atum, are</b>\n<b>1)</b> громко возражать, шумно протестовать, криком выражать неудовольствие (alicui <i>Q, PJ</i>; orationi alicujus <i>C</i>): vehementer ab omnibus reclamatur <i>C</i> все резко высказываются против;\n<b>2)</b> <i>поэт.</i> давать отзвук, отзываться, оглашаться (scopulis aequora redamant <i>V</i>);\n<b>3)</b> громко выкрикивать, звать (aliquem nomine <i>VF</i>)."],
  ["reclango", "<big>reclango</big>\n\n<b>re–clango, —, —, ere</b>\nоткликаться, отдаваться (sonitu immenso <i>Amm</i>)."],
  ["reclinatio", "<big>reclinatio</big>\n\n<b>reclinatio, onis</b> <i>f</i>\n<b>1)</b> наклон назад, откидывание (capitis <i>Aug</i>);\n<b>2)</b> отдохновение (a laboribus <i>Ambr</i>)."],
  ["reclinatorium", "<big>reclinatorium</big>\n\n<b>re–clinatorium, i</b> <i>n</i>\n<b>1)</b> спинка кресла <i>Vlg</i>;\n<b>2)</b> изголовье <i>Is</i>."],
  ["reclinis", "<big>reclinis</big>\n\n<b>reclinis, e</b> [reclino]\nоткинувшийся назад, опёршийся (in aliqua re <i>M</i>, alicui rei <i>Sen</i>, <i>St</i>, super aliquid <i>T</i> <i>или</i> aliqua re <i>St</i>)."],
  ["reclino", "<big>reclino</big>\n\n<b>reclino, avi, atum, are</b>\n<b>1)</b> склонять назад, прислонять (se <i>Cs</i>; scuta <i>V</i>): reclinari in cubitum <i>Pt</i> облокотиться; reclinari in gramine <i>H</i> лежать на траве;\n<b>2)</b> взваливать, возлагать (onus imperii in aliquem <i>Sen</i>);\n<b>3)</b> освобождать (aliquem a labore <i>H</i>)."],
  ["reclivis", "<big>reclivis</big>\n\n<b>re–clivis, e</b> [clivus]\nнаклонённый (campus ad solem r. <i>Pall</i>)."],
  ["recludo", "<big>recludo</big>\n\n<b>re–cludo, clusi, clusum, ere</b> [claudo]\n<b>1)</b> отпирать, отворять (portas alicui <i>V, O</i>; reclusae subito fores <i>Pt</i>); открывать, обнаруживать, показывать (viam <i>O</i>);\n<b>2)</b> разрыхлять, вскапывать (tellurem <i>V</i>); вскрывать, откапывать (thesauros tellure <i>V</i>; fontes <i>V</i>); вскрывать, разрезать (reclusa pectora pecudum <i>V</i>);\n<b>3)</b> обнажать (vulnera <i>T</i>; ensem <i>V</i>);\n<b>4)</b> разглашать, выдавать (operta <i>H</i>): r. iram <i>Enn</i> дать волю гневу;\n<b>5)</b> пронзать, разрубать (pectus mucrone <i>V</i>);\n<b>6)</b> отменять, отвращать (fata precibus <i>H</i>);\n<b>7)</b> запирать, заключать (aliquem in carcerem <i>Just</i>); запирать, затворять (Jani templum <i>Amm</i>)."],
  ["reclusio", "<big>reclusio</big>\n\n<b>reclusio, onis</b> <i>f</i> [recludo]\nраскрытие (perfectam reclusionem facere, <i>sc.</i> oculorum <i>CA</i>)."],
  ["recoctus", "<big>recoctus</big>\n\n<b>recoctus, a, um</b> <i>part. pf. к</i>\nrecoquo."],
  ["recogitatio", "<big>recogitatio</big>\n\n<b>recogitatio, onis</b> <i>f</i>\nобдумывание, размышление <i>Aug</i>."],
  ["recogitatus", "<big>recogitatus</big>\n\n<b>recogitatus, us</b> <i>m</i> <i>Tert</i>\n= recogitatio."],
  ["recogito", "<big>recogito</big>\n\n<b>re–cogito, avi, —, are</b>\nдумать, обдумывать, размышлять, вспоминать (secum <i>Col</i>; in otio de aliqua re <i>C</i>)."],
  ["recognitio", "<big>recognitio</big>\n\n<b>recognitio, onis</b> <i>f</i> [recognosco]\n<b>1)</b> смотр, инспектирование (equitum <i>Su</i>); осмотр, обозревание, обследование (agri <i>L</i>); разбор, рассмотрение, анализ (r. sui <i>Sen</i>);\n<b>2)</b> узнавание (inter se mutua <i>AG</i>)."],
  ["recognosco", "<big>recognosco</big>\n\n<b>re–cognosco, gnovi, gnitum, ere</b>\n<b>1)</b> опознавать, узнавать (aliquid reminiscendo <i>C</i>);\n<b>2)</b> вызывать в памяти, вспоминать (noctem illam <i>C</i>);\n<b>3)</b> осматривать, обследовать (agros <i>L</i>); инспектировать, производить смотр (r. equites <i>Su</i>);\n<b>4)</b> пересматривать, подвергать пересмотру (legem <i>C</i>; libellos suos <i>PJ</i>)."],
  ["recolligo", "<big>recolligo</big>\n\n<b>re–colligo, legi, lectum, ere</b>\n<b>1)</b> вновь собирать (multitudinem, quae passim vagabatur <i>Just</i>); подбирать, поднимать (stolam <i>PJ</i>); вновь брать (gladium <i>Just</i>);\n<b>2)</b> вспоминать, писать по памяти (actionem suam <i>PJ</i>): se r. прийти в себя, оправиться <i>O</i> <i>или</i> поправиться (a longa valetudine <i>PM</i>); <i>но тж.</i> укрываться, прятаться (sub antrum <i>Eccl</i>); r. primos annos <i>O</i> вновь помолодеть; r. animum alicujus <i>C</i> вновь приобрести чьё-л. расположение (примириться с кем-л.)."],
  ["recolloco", "<big>recolloco</big>\n\n<b>re–colloco, —, —, are</b>\nвновь укладывать (aegrum lecto <i>CA</i>)."],
  ["recolo", "<big>recolo</big>\n\n<b>I re–colo, —, —, are</b>\nвновь процеживать (oleum per linteum duplex <i>Scr</i>).\n\n<b>II re–colo, colui, cultum, ere</b>\n<b>1)</b> вновь обрабатывать (metalla <i>L</i>); снова возделывать (terram <i>L</i>);\n<b>2)</b> вновь посещать (locum <i>Ph</i>);\n<b>3)</b> снова заниматься, изучать (r. haec studia <i>C</i>; artes aliquas <i>C</i>);\n<b>4)</b> восстанавливать (imagines subversas <i>T</i>; dignitatem <i>C</i>): aliquem sacerdotiis r. <i>T</i> вновь удостаивать кого-л. правом жертвоприношений;\n<b>5)</b> вновь вспоминать, опять обдумывать, размышлять (sua facta pectore <i>Ctl</i>)."],
  ["recommentor", "<big>recommentor</big>\n\n<b>re–commentor, atus sum, ari</b> <i>depon.</i>\nвспоминать (nomen <i>Pl</i>)."],
  ["recomminiscor", "<big>recomminiscor</big>\n\n<b>re–comminiscor, —, isci</b> <i>depon.</i>\nприпоминать (aliqua re <i>Pl</i>)."],
  ["recommoneo", "<big>recommoneo</big>\n\n<b>re–commoneo, —, —, ere</b>\nвновь напоминать <i>или</i> увещевать (aliquem <i>Eccl</i>)."],
  ["recompensatio", "<big>recompensatio</big>\n\n<b>re–compensatio, onis</b> <i>f</i>\nвозмещение <i>Eccl</i>."],
  ["recompingo", "<big>recompingo</big>\n\n<b>re–compingo, —, —, ere</b>\nвновь соединять (os ad os <i>Tert</i>)."],
  ["recompono", "<big>recompono</big>\n\n<b>re–compono, posui, positum, ere</b>\n<b>1)</b> снова приводить в порядок (comas <i>O</i>);\n<b>2)</b> воссоединять (fracturam <i>Veg</i>);\n<b>3)</b> вновь успокаивать (aliquem <i>PJ</i>). reconciliasso <i>Pl</i> (= reconciliavero) <i>fut. II к</i> reconcilio."],
  ["reconciliatio", "<big>reconciliatio</big>\n\n<b>reconciliatio, onis</b> <i>f</i> [reconcilio]\n<b>1)</b> восстановление, возобновление (concordiae, gratiae <i>C</i>);\n<b>2)</b> примирение (inter fratres <i>Just</i>)."],
  ["reconciliator", "<big>reconciliator</big>\n\n<b>reconciliator, oris</b> <i>m</i> [reconcilio]\nвосстановитель (r. pacis <i>L</i>); примиритель, умиротворитель (r. populi <i>Ap</i>)."],
  ["reconcilio", "<big>reconcilio</big>\n\n<b>re–concilio, avi, atum, are</b>\n<b>1)</b> снова соединять, сближать, мирить, примирять: r. aliquem cum aliquo <i>или</i> aliquem alicui <i>C</i> примирять кого-л. с кем-л.; r. inimicos in gratiam <i>C</i> примирить врагов;\n<b>2)</b> восстанавливать, возобновлять (concordiam <i>L</i>; pacem <i>Nep</i>; existimationem <i>C</i>);\n<b>3)</b> ставить на место, вправлять (articulum ingenti plaga <i>Ap</i>);\n<b>4)</b> снова приобретать (insulam <i>Nep</i>): r. studia alicujus <i>T</i> вновь снискать чьё-л. расположение;\n<b>5)</b> возвращать (aliquem domum <i>Pl</i>);\n<b>6)</b> восполнять, навёрстывать (laboris detrimentum virtute <i>Cs</i>)."],
  ["reconcinno", "<big>reconcinno</big>\n\n<b>re–concinno, —, —, are</b>\nисправлять, чинить (pallam <i>Pl</i>); восстанавливать, ремонтировать (<i>sc.</i> aedificia <i>C</i>)."],
  ["reconcludo", "<big>reconcludo</big>\n\n<b>re–concludo, —, —, ere</b>\nзапирать, заключать (aliquem in aliqua re <i>Tert</i>)."],
  ["reconditor", "<big>reconditor</big>\n\n<b>re–conditor, oris</b> <i>m</i>\nукрывающий, прячущий (r. suorum <i>Aug</i>)."],
  ["reconditorium", "<big>reconditorium</big>\n\n<b>reconditorium, i</b> <i>m</i>\nхранилище <i>Is</i>."],
  ["reconditus", "<big>reconditus</big>\n\n<b>re–conditus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> recondo;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> отдалённый, скрытый (locus <i>C</i>): recondita templi <i>Cs</i> отдалённые (внутренние) части храма;\n<b>2)</b> глубоко лежащий (venae auri argenti que <i>C</i>; fontes <i>C</i>);\n<b>3)</b> потаённый, тайный (res <i>C</i>);\n<b>4)</b> замкнутый, скрытый (homo <i>C</i>);\n<b>5)</b> глубокий, малопонятный, труднодоступный (sententiae <i>C</i>; litterae <i>C</i>; artes <i>C</i>)."],
  ["recondo", "<big>recondo</big>\n\n<b>re–condo, didi, ditum, ere</b>\n<b>1)</b> убирать, укладывать, прятать, класть (uvas in amphoras <i>Col</i>); сливать, наливать (oleum vasis <i>Col</i>);\n<b>2)</b> вкладывать (gladium in vaginam <i>C</i>);\n<b>3)</b> <i>поэт.</i> вновь закрывать, смыкать (oculos <i>O</i>);\n<b>4)</b> поглощать (avida alvo aliquid <i>O</i>);\n<b>5)</b> глубоко вонзать (gladium lateri <i>O</i>; ensem in pulmone <i>V</i>);\n<b>6)</b> затаивать (odia <i>T</i>); утаивать, скрывать (multi, quos fama obscura recondit <i>V</i>): r. se укрываться, скрываться, удаляться (in aliquem locum <i>Q</i>);\n<b>7)</b> сохранять в памяти (verba, vultus, <i>sc.</i> alicujus <i>T</i>)."],
  ["reconduco", "<big>reconduco</big>\n\n<b>re–conduco, duxi, ductum, ere</b>\n<b>1)</b> вести обратно (copias <i>Q</i>);\n<b>2)</b> вновь нанимать <i>или</i> арендовать (aliquid <i>Dig</i>);\n<b>3)</b> принимать на себя (для лечения за плату) (aegrum <i>PM</i>)."],
  ["reconflo", "<big>reconflo</big>\n\n<b>re–conflo, —, —, are</b>\nвновь раздувать, <i>перен.</i> восстанавливать, вновь будить (sensum per membra <i>Lcr</i>)."],
  ["reconsigno", "<big>reconsigno</big>\n\n<b>re–consigno, —, —, are</b>\nвновь отмечать <i>Tert</i>."],
  ["reconstruo", "<big>reconstruo</big>\n\n<b>re–construo, —, —, ere</b>\nотстраивать, восстанавливать (aliquid <i>Eccl</i>)."],
  ["recontrans", "<big>recontrans</big>\n\n<b>re–contrans, antis</b> [contra]\nмятежный, непокорный (gens r. regibus suis <i>Tert</i>)."],
  ["reconvinco", "<big>reconvinco</big>\n\n<b>re–convinco, —, —, ere</b>\nокончательно убедить <i>Ambr</i>."],
  ["recoquo", "<big>recoquo</big>\n\n<b>re–coquo, coxi, coctum, ere</b>\n<b>1)</b> опять кипятить, снова варить (lanam <i>Sen</i>);\n<b>2)</b> переплавлять, перековывать (aurum, enses <i>V</i>);\n<b>3)</b> переделывать (recoctus scriba ex quinqueviro <i>H</i>); возрождать, омолаживать (Peliam <i>C</i>): senex recoctus <i>Ctl</i> старый волокита."],
  ["recordabilis", "<big>recordabilis</big>\n\n<b>recordabilis, e</b>\nвспоминаемый <i>Eccl</i>."],
  ["recordantia", "<big>recordantia</big>\n\n<b>recordantia, ae</b> <i>f</i> <i>Ambr</i>\n= recordatio."],
  ["recordatio", "<big>recordatio</big>\n\n<b>recordatio, onis</b> <i>f</i> [recordor]\nвоспоминание (ultimi temporis <i>C</i>); r. et memoria <i>C</i> яркое воспоминание."],
  ["recordatus", "<big>recordatus</big>\n\n<b>recordatus, us</b> <i>m</i> <i>Tert</i>\n= recordatio."],
  ["recordo", "<big>recordo</big>\n\n<b>recordo, avi, atum, are</b> <i>Vr, Sid</i>\n= recordor."],
  ["recordor", "<big>recordor</big>\n\n<b>recordor, atus sum, ari</b> <i>depon.</i> [re + cor]\n<b>1)</b> вспоминать, восстанавливать в памяти, припоминать, помнить (aliquid, de aliqua re, <i>реже</i> alicujus rei <i>C etc.</i>): si recte recordor <i>Ap</i> если память мне не изменяет; pueritiae memoriam r. ultimam <i>C</i> вспоминать раннюю юность;\n<b>2)</b> принимать во внимание, учитывать, взвешивать, представлять себе (quae sum passurus recordor <i>O</i>)."],
  ["recorporatio", "<big>recorporatio</big>\n\n<b>recorporatio, onis</b> <i>f</i>\nвосстановление плоти <i>Tert, CA</i>."],
  ["recorporativus", "<big>recorporativus</big>\n\n<b>re–corporativus, a, um</b>\nвосстанавливающий мышечную ткань (virtus <i>CA</i>)."],
  ["recorporo", "<big>recorporo</big>\n\n<b>re–corporo, —, —, are</b> [corpus]\nвновь облекать плотью (animam <i>Tert</i>)."],
  ["recorrigo", "<big>recorrigo</big>\n\n<b>re–corrigo, rexi, rectum, ere</b>\nисправлять, улучшать (animum nostrum <i>Sen</i>): r. costas alicujus <i>погов. Pt</i> = вновь поставить кого-л. на ноги."],
  ["recoxi", "<big>recoxi</big>\n\n<b>recoxi</b> <i>pf. к</i>\nrecoquo."],
  ["recrastinatio", "<big>recrastinatio</big>\n\n<b>recrastinatio, onis</b> <i>f</i>\nоткладывание до завтра <i>Hier</i>."],
  ["recrastino", "<big>recrastino</big>\n\n<b>recrastino, —, —, are</b> [re + crastinus]\nоткладывать до завтра, со дня на день (aliquid <i>Col, PM</i>)."],
  ["recreatio", "<big>recreatio</big>\n\n<b>recreatio, onis</b> <i>f</i> [recreo]\nвосстановление (сил), выздоровление (r. ab aegritudine <i>PM</i>)."],
  ["recreator", "<big>recreator</big>\n\n<b>recreator, oris</b> <i>m</i>\nвосстановитель (corporis r. somnus <i>Tert</i>)."],
  ["recrementum", "<big>recrementum</big>\n\n<b>recrementum, i</b> <i>n</i> [re + cerno]\nотходы, отбросы (farris <i>PM</i>); нечистоты <i>AG</i>."],
  ["recreo", "<big>recreo</big>\n\n<b>re–creo, avi, atum, are</b>\n<b>1)</b> воссоздавать, воспроизводить, обновлять (aliquid <i>Lcr</i>);\n<b>2)</b> переделывать, преобразовывать (vitam <i>Lcr</i>);\n<b>3)</b> восстанавливать, подкреплять, освежать (vires <i>Lcr</i>): spiritum r. <i>Ap</i> восстановить дыхание (<i>у мнимоумершего</i>) || укреплять, ободрять (afflictum animum <i>C</i>; mentem <i>C</i>): recreatus hoc sermone <i>Pt</i> успокоенный этими словами;\n<b>4)</b>: se r. <i>или</i> <i>pass.</i> recreari оправляться (ex <i>или</i> a timore <i>C, bAfr</i>); выздоравливать (e morbo <i>C</i>); возрождаться, вновь крепнуть (res publica recreatur <i>C</i>); отдыхать, приходить в себя (litteris sustentari et recreari <i>C</i>)."],
  ["recrepo", "<big>recrepo</big>\n\n<b>re–crepo, —, —, are</b>\n<b>1)</b> давать отзвук, отзываться, раздаваться (cymbala recrepant <i>Ctl</i>);\n<b>2)</b> (<i>о звуках</i>) издавать (Cyllenia murmura <i>V</i>)."],
  ["recresco", "<big>recresco</big>\n\n<b>re–cresco, crevi, cretum, ere</b>\nснова расти, вновь вырастать (recisa ossa non recrescunt <i>PM</i>): luna toto quater orbe recrevit <i>O</i> луна четырежды вырастала в полный диск, <i>т. е.</i> прошло четыре полнолуния (месяца)."],
  ["recrucifigo", "<big>recrucifigo</big>\n\n<b>re–crucifigo, —, —, ere</b>\nвновь распинать на кресте (aliquem <i>Hier</i>)."],
  ["recrudesco", "<big>recrudesco</big>\n\n<b>re–crudesco, crudui, —, ere</b>\nстановиться свежим, вскрываться (<i>о ране</i>) (quae consanuisse videbantur, recrudescunt <i>C</i>); возобновляться, усиливаться (seditio recrudescens <i>L</i>; amor recrudescit <i>Sen</i>)."],
  ["recta", "<big>recta</big>\n\n<b>recta</b> <i>adv.</i> (<i>sc.</i> via)\nпрямо, напрямик (pergere ad aliquem <i>Ter</i>; de cubiculo in triclinium ire <i>Pt</i>)."],
  ["recte", "<big>recte</big>\n\n<b>recte</b>\n<b>1)</b> прямо, по прямой линии (ferri <i>C</i>); отвесно вверх (stare <i>PM</i>);\n<b>2)</b> правильно (respondere <i>C</i>); надлежащим образом, хорошо (vivere <i>Pl</i>); здраво (judicare <i>C</i>); хорошо, близко (aliquem novisse <i>C</i>);\n<b>3)</b> честно, справедливо (r. facere <i>C</i>);\n<b>4)</b> счастливо, успешно (bella gerere <i>L</i>);\n<b>5)</b> искусно (scribere <i>H</i>); уверенно, надёжным образом, без риска (alicui ad aliquem litteras dare <i>C</i>); выгодно (vendere <i>Pl</i>);\n<b>6)</b> порядочно, изрядно (locus r. ferax <i>Cato</i>);\n<b>7)</b> (<i>при уклончивом ответе</i>) да так, ладно уж, ничего (quid es tam tristis? — R., mater <i>Ter</i>);\n<b>8)</b> (<i>при вежливом отказе</i>) благодарю покорно: rogo, numquid velit. — R., inquit <i>Ter</i> я спрашиваю, не желает ли он чего-либо. — Да нет, говорит, спасибо;\n<b>9)</b> (<i>угрожающе</i>) погоди ты у меня, ладно: r., videbo te in publicum! <i>Pt</i> ладно уж, вот попадёшься ты мне на улице!"],
  ["rectiangulum", "<big>rectiangulum</big>\n\n<b>recti–angulum, i</b> <i>n</i>\nпрямоугольный треугольник <i>Is</i>."],
  ["rectio", "<big>rectio</big>\n\n<b>rectio, onis</b> <i>f</i> [rego]\nправление, управление, руководство (rerum publicarum <i>C</i>)."],
  ["rectitudo", "<big>rectitudo</big>\n\n<b>rectitudo, inis</b> <i>f</i>\nпрямизна, <i>перен.</i> прямота, <i>тж.</i> справедливость <i>Hier, Aug</i>."],
  ["recto", "<big>recto</big>\n\n<b>recto</b> <i>adv.</i> <i>Dig</i>\n= recta."],
  ["rector", "<big>rector</big>\n\n<b>rector, oris</b> <i>m</i> [rego]\nправитель, управитель, руководитель (civitatis <i>C</i>): r. navis <i>C</i> кормчий; r. equi <i>T</i> всадник; r. elephanti <i>L</i> вожатый слона; r. deum (= deorum) <i>O, V</i> = Juppiter."],
  ["rectrix", "<big>rectrix</big>\n\n<b>rectrix, icis</b> <i>f</i>\nправительница, руководительница (sapientia domina rectrixque <i>Sen</i>)."],
  ["rectum", "<big>rectum</big>\n\n<b>rectum, i</b> <i>n</i>\nправильное, разумное, справедливое (fidem rectumque colere <i>O</i>)."],
  ["rectus", "<big>rectus</big>\n\n<b>rectus, a, um</b> [rego]\n<b>1)</b> прямой (linea <i>Cs</i>; via <i>Ter, C</i>): r. stabat <i>Pt</i> он стоял, выпрямившись во весь рост; intestinum rectum <i>CC</i> прямая кишка || ровный (acies <i>L</i>); отвесный (saxa <i>L</i>): recta regione fluminis <i>Cs</i> параллельно течению реки; recto litore <i>L</i> прямо по берегу;\n<b>2)</b> стройный, прямо держащийся (puella <i>H</i>);\n<b>3)</b> пристальный (oculi <i>C</i>; acies <i>O</i>);\n<b>4)</b> спокойный, непреклонный, непоколебимый (animus <i>H</i>);\n<b>5)</b> правильный, разумный, здравый (sententia <i>C</i>); надлежащий, хороший (cena <i>Su, M</i>; exemplum <i>L</i>): recta nomina <i>H</i> надёжные должники;\n<b>6)</b> простой, естественный, безыскусственный (commentarii Caesaris <i>C</i>; orator <i>PJ</i>); прямой, прямодушный (homo <i>Ctl</i>; ingenium <i>PJ</i>); праведный, справедливый (judex <i>PJ, Lad</i>); честный (ratio, facinus <i>C</i>): recta via <i>или</i> de recto <i>Pl etc.</i> напрямик, прямо;\n<b>7)</b> <i>грам.</i> прямой, независимый: casus r. <i>Vr, Q</i> = nominativus."],
  ["recubitus", "<big>recubitus</big>\n\n<b>recubitus, (us) </b> <i>m</i> [recumbo]\n<b>1)</b> опускание, падение <i>PM</i>;\n<b>2)</b> место за столом (primi recubitus in cenis <i>Vlg</i>)."],
  ["recubo", "<big>recubo</big>\n\n<b>re–cubo, avi, —, are</b>\nлежать на спине, покоиться, отдыхать (sub arbore <i>O</i>; sub tegmine fagi <i>V</i>; Tyrio toro <i>Tib</i>)."],
  ["recula", "<big>recula</big>\n\n<b>recula (rescula), ae</b> <i>f</i> [<i>demin. к</i> res]\n<b>1)</b> вещица, пустячок, мелочь <i>Ap</i>;\n<b>2)</b> состояньице <i>Pl</i>."],
  ["recultus", "<big>recultus</big>\n\n<b>recultus</b> <i>part. pf. к</i>\nrecolo."],
  ["recumbo", "<big>recumbo</big>\n\n<b>re–cumbo, cubui, —, ere</b>\n<b>1)</b> ложиться, опускаться (in herba <i>C</i>; in cubiculo <i>C</i>); возлежать (in triclinio <i>C</i>): recumbentes <i>Pt</i> возлежащие (за столом), <i>т. е.</i> обедающие; quare non recumbit? <i>Pt</i> почему его нет за столом?;\n<b>2)</b> оседать, опускаться, падать (pons recumbit in palude <i>Ctl</i>); спускаться, стлаться (nebulae campo recumbunt <i>V</i>); улечься, успокаиваться (minax unda ponto recumbit <i>H</i>);\n<b>3)</b> лежать, тянуться (longo Janiculi jugo <i>M</i>);\n<b>4)</b> <i>перен.</i> покоиться, опираться: in te omnis domus inclinata recumbit <i>V</i> тобой держится весь (наш) дом (<i>слова Аматы её сыну Турну</i>)."],
  ["recuperatio", "<big>recuperatio</big>\n\n<b>recuperatio, onis</b> <i>f</i> [recupero]\nобратное получение, возвращение (libertatis <i>C</i>; sumptuum <i>CJ</i>)."],
  ["recuperator", "<big>recuperator</big>\n\n<b>recuperator, oris</b> <i>m</i> [recupero]\n<b>1)</b> снова приобретающий, отвоёвывающий, снова овладевающий (r. urbis <i>T</i>);\n<b>2)</b> <i>pl.</i> рекуператоры, члены суда по имущественным вопросам (<i>особая судебная коллегия по спешным имущественным делам</i>) <i>Pl, C, L, T etc.</i>"],
  ["recuperatorius", "<big>recuperatorius</big>\n\n<b>recuperatorius, a, um</b> [recuperator 2]\nотносящийся к рекуператорам, рекуператорский (judicium <i>C, PJ, Su</i>)."],
  ["recupero", "<big>recupero</big>\n\n<b>recupero (recipero), avi, atum, are</b> [recipio]\n<b>1)</b> вновь обретать, снова получать (amissa <i>Cs</i>; jus suum <i>C</i>; libertatem <i>Cs</i>; recuperata alicujus gratia <i>T</i>); вновь завоёвывать, отвоёвывать (urbem <i>L</i>);\n<b>2)</b> восстанавливать (vires cibo <i>T</i>): r. gressum <i>Ap</i> вновь получить возможность ходить; se quiete r. <i>Vr</i> отдохнуть;\n<b>3)</b> освобождать из плена (aliquem <i>C, Cs, Nep</i>);\n<b>4)</b> вновь снискать расположение: r. aliquem <i>Nep</i> вновь добиться чьего-л. расположения; r. rem publicam <i>C</i> снова достичь высшей государственной власти."],
  ["recuro", "<big>recuro</big>\n\n<b>re–curo, (avi), atum, are</b>\n<b>1)</b> тщательно обрабатывать, старательно изготовлять (chartam <i>PM</i>);\n<b>2)</b> снова лечить (se aliqua re <i>Ctl</i>)."],
  ["recurro", "<big>recurro</big>\n\n<b>re–curro, (cu)curri, cursum, ere</b>\n<b>1)</b> бежать назад, спешить обратно, возвращаться (ad aliquem <i>C etc.</i>; in arcem <i>L</i>; hiems recurrit <i>O</i>); <i>перен.</i> опять приходить, <i>т. е.</i> вновь предлагать (r. ad easdem condiciones <i>Cs</i>): memoriae r. <i>PJ</i> вспомниться;\n<b>2)</b> совершать круговорот (sol recurrens <i>V</i>): tempore certo r. <i>Su</i> периодически возвращаться;\n<b>3)</b> прибегать, обращаться, искать убежища (ad aliquem <i>H</i>)."],
  ["recursio", "<big>recursio</big>\n\n<b>recursio, onis</b> <i>f</i>\n<b>1)</b> круговорот (annua <i>Aug</i>);\n<b>2)</b> рвота <i>CA</i>."],
  ["recurso", "<big>recurso</big>\n\n<b>recurso, avi, atum, are</b> [<i>intens. к</i> recurro]\n<b>1)</b> бежать назад, спешить обратно, возвращаться (corpora longe recursant <i>Lcr</i>);\n<b>2)</b> <i>перен.</i> опять приходить, вновь пробуждаться (cura recursat <i>V</i>); снова представляться, вспоминаться, всплывать (aliquid recursat animo <i>V</i>)."],
  ["recursus", "<big>recursus</big>\n\n<b>recursus, us</b> <i>m</i> [recurro]\n<b>1)</b> обратная поездка, обратный путь (in Hesperiam <i>Lcn</i>); попятное движение, возвращение: alios inire cursus aliosque recursus <i>V</i> бегать то туда, то обратно;\n<b>2)</b> возврат (ad pristinum ordinem <i>VM</i>): maris r. <i>Sen</i> морской отлив;\n<b>3)</b> <i>юр.</i> обжалование <i>CJ</i>."],
  ["recurvo", "<big>recurvo</big>\n\n<b>re–curvo, (avi), atum, are</b>\nзагибать назад (brachium recurvatur <i>CC</i>); гнуть в обратную сторону, оттягивать (colla equi <i>O</i>): aquas in caput r. <i>O</i> повернуть течение вод вспять; undae recurvatae <i>O</i> извивающееся течение (Meandri)."],
  ["recurvus", "<big>recurvus</big>\n\n<b>re–curvus, a, um</b>\nзагнутый назад (cornu <i>V</i>); извилистый, извивающийся (nexus hederae <i>O</i>): tectum recurvum <i>O</i> лабиринт; recurva aera <i>O</i> рыболовные крючки."],
  ["recusabilis", "<big>recusabilis</big>\n\n<b>recusabilis, e</b>\nмогущий быть отвергнутым (revelatio <i>Tert</i>)."],
  ["recusatio", "<big>recusatio</big>\n\n<b>recusatio, onis</b> <i>f</i> [recuso]\n<b>1)</b> отклонение, отказ (sine recusatione ac sine ulla mora <i>C</i>);\n<b>2)</b> <i>юр.</i> возражение, протест <i>C, Q</i>;\n<b>3)</b> отвращение: r. stomachi <i>Pt</i> тошнота."],
  ["recuso", "<big>recuso</big>\n\n<b>re–cuso, avi, atum, are</b> [causa]\n<b>1)</b> отказывать, отклонять (condiciones pacis <i>L</i>); отвергать, отказываться (r. aliquam uxorem <i>Ter</i>; r. aliquid <i>или</i> de aliqua re <i>C, Cs etc.</i>; ignis non unquam alimentare cusat <i>O</i>): r. imperium <i>C</i> отказывать в повиновении; r. lyram <i>C</i> заявить о своём неумении играть на лире; nullum r. periculum <i>Cs, Pt</i> не отступать ни перед какой опасностью; specie recusantis <i>T</i> притворно отказываясь;\n<b>2)</b> возражать, заявлять протест, протестовать, защищаться <i>Pl, C, Q, Dig</i>;\n<b>3)</b> опровергать (mendacium <i>Tert</i>)."],
  ["recussabilis", "<big>recussabilis</big>\n\n<b>recussabilis, e</b>\nотскакивающий (sphaera <i>CA</i>)."],
  ["recussus", "<big>recussus</big>\n\n<b>I recussus, a, um</b> <i>part. pf. к</i>\nrecutio.\n\n<b>II recussus, (us) </b> <i>m</i>\nотскакивание, обратный толчок, отдача <i>VM, PM</i>."],
  ["recutio", "<big>recutio</big>\n\n<b>re–cutio, cussi, cussum, ere</b> [quatio]\n<b>1)</b> отражать (aliquid retro <i>Aug</i>);\n<b>2)</b> поражать, потрясать (uterum, <i>sc.</i> equi Trojani <i>V</i>); встряхивать, расталкивать (recussus somno <i>Ap</i>)."],
  ["recutitus", "<big>recutitus</big>\n\n<b>recutitus, a, um</b> [re + cutis]\n<b>1)</b> обрезанный <i>M, Pt</i>; <i>перен.</i> иудейский (sabbata <i>Pers</i>);\n<b>2)</b> покрытый ссадинами, израненный (colla mulae <i>M</i>)."],
  ["red-", "<big>red-</big>\n\n<b>red-</b>\n<i>приставка</i> = re- <i>перед начальным гласным</i>, d <i>или</i> h <i>основного слова.</i>"],
  ["reda", "<big>reda</big>\n\n<b>reda, ae</b> <i>f</i> <i>v. l.</i>\n= raeda."],
  ["redaccendo", "<big>redaccendo</big>\n\n<b>redaccendo</b> <i>Tert</i>\n= reaccendo."],
  ["redactus", "<big>redactus</big>\n\n<b>I redactus</b> <i>part. pf. к</i>\nredigo.\n\n<b>II redactus, us</b> <i>m</i>\n<b>1)</b> выручка (venditionis <i>Dig</i>);\n<b>2)</b> сбор, урожай (fructuum <i>Dig</i>)."],
  ["redadopto", "<big>redadopto</big>\n\n<b>red–adopto, —, —, are</b>\nвновь усыновлять <i>Dig</i>."],
  ["redambulo", "<big>redambulo</big>\n\n<b>red–ambulo, —, —, are</b>\nвозвращаться (с прогулки): bene ambula et redambula <i>Pl</i> счастливого пути и благополучного возвращения."],
  ["redamo", "<big>redamo</big>\n\n<b>red–amo, —, —, are</b>\nотвечать (на любовь) любовью, любить взаимно <i>C, Macr, Eccl</i>."],
  ["redamptruo", "<big>redamptruo</big>\n\n<b>red–am(p)truo, —, —, are</b>\nучаствовать в пляске салиев (<i>см.</i> Salii) после praesul'а (<i>см.</i>) <i>Pac, LM</i>."],
  ["redanimatio", "<big>redanimatio</big>\n\n<b>red–animatio, onis</b> <i>f</i>\nоживление, воскрешение <i>Tert</i>."],
  ["redanimo", "<big>redanimo</big>\n\n<b>red–animo, —, —, are</b>\nвновь оживлять, воскрешать (aliquem <i>Tert</i>)."],
  ["redardesco", "<big>redardesco</big>\n\n<b>red–ardesco, —, —, ere</b>\nопять загораться, снова воспламеняться (flamma redardescit <i>O</i>)."],
  ["redarguo", "<big>redarguo</big>\n\n<b>red–arguo, gui, gutum, ere</b>\n<b>1)</b> опровергать (aliquem <i>и</i> aliquid <i>C</i>);\n<b>2)</b> изобличать, уличать во лжи (verba <i>V</i>; aliquem alicujus rei <i>Lact</i>; libellum <i>Su</i>); неопровержимо доказывать (r. rationem falsam esse <i>AG</i>)."],
  ["redargutio", "<big>redargutio</big>\n\n<b>redargutio, onis</b> <i>f</i>\nопровержение <i>Vlg</i>."],
  ["redarius", "<big>redarius</big>\n\n<b>redarius, i</b> <i>m</i> <i>v. l.</i>\n= raedarius I."],
  ["redauspico", "<big>redauspico</big>\n\n<b>red–auspico, —, —, are</b>\nвновь обращаться к ауспициям, <i>шутл.</i> возвращаться (in catenas <i>Pl</i>)."],
  ["reddibo", "<big>reddibo</big>\n\n<b>reddibo</b> <i>Pl fut. к</i>\nreddo."],
  ["reddidi", "<big>reddidi</big>\n\n<b>reddidi</b> <i>pf. к</i>\nreddo."],
  ["redditio", "<big>redditio</big>\n\n<b>redditio, onis</b> <i>f</i> [reddo]\n<b>1)</b> приведение, указание (rationis <i>Aug</i>);\n<b>2)</b> воздаяние (dies redditionis <i>Eccl</i>);\n<b>3)</b> <i>ритор.</i> последующее предложение, аподосис (<i>нисходящая, заключительная часть периода</i>) <i>Q</i>."],
  ["redditor", "<big>redditor</big>\n\n<b>redditor, oris</b> <i>m</i>\nвоздаятель <i>Vlg, Eccl</i>."],
  ["redditus", "<big>redditus</big>\n\n<b>I redditus, a, um</b> <i>part. pf. к</i>\nreddo.\n\n<b>II redditus, us</b> <i>m</i> <i>v. l.</i>\n= reditus."],
  ["reddo", "<big>reddo</big>\n\n<b>red–do, reddidi, redditum, ere</b>\n<b>1)</b> отдавать назад, выдавать обратно, возвращать (aliquid alicui <i>Pl, C etc.</i>; redde, quod debes <i>Pt</i>): r. aliquid naturae suae <i>QC</i> возвращать чему-л. естественный вид; me nox diei reddidit <i>Ap</i> = проспав всю ночь, я утром проснулся; se r. вернуться (convivio <i>L</i>), представиться (oculis <i>V</i>), вновь присоединиться (alicui <i>PJ</i>): se in arma Teucrorum r. <i>V</i> вновь вступить в бой с тевкрами;\n<b>2)</b> давать отклик, откликаться, повторять (sonum <i>VF</i>; clamorem <i>QC</i>); говорить в ответ (alicui talia <i>V</i>): r. mutua dicta <i>O</i> обмениваться словами; r. responsum <i>V, L</i> давать ответ, отвечать;\n<b>3)</b> давать, производить (ager reddit Cererem <i>H</i>);\n<b>4)</b> открывать, показывать (marc terras reddit <i>PM</i>);\n<b>5)</b> излагать, рассказывать, передавать (omnia sine scripto <i>C</i>); произносить (verba male <i>O</i>): nomina omnium r. <i>Pt</i> называть всех по имени;\n<b>6)</b> переводить (aliquid Latine <i>C</i>; verbum pro verbo <i>C</i> <i>и</i> verbum verbo <i>H</i>);\n<b>7)</b> воспроизводить, отражать (omnia diligenter pictor reddiderat <i>Pt</i>): r. vultum alicujus <i>SenT</i> походить лицом на кого-л.; r. aliquem nomine <i>V</i> называться как Кто-л.; fulgorem auri r. <i>PM</i> блестеть как Золото;\n<b>8)</b> делать (aliquem meliorem <i>C</i>; aliquem inimicum <i>SenT</i>; homines ex feris mites <i>C</i>; mare tutum <i>Nep</i>; ferrum acutum <i>H</i>): r. in antiquum statum <i>Vop</i> вернуть в прежнее состояние || превращать (aliquem avem r. <i>O</i>);\n<b>9)</b> (<i>тж.</i> r. rursus <i>H</i>) воздавать, отплачивать (r. beneficium <i>Ter, C, Cs</i>; r. odium pro gratia <i>T</i>; r. hostibus cladem <i>L</i>); отдавать (pro vita vitam <i>Cs</i>);\n<b>10)</b> приносить (victimas <i>H</i>);\n<b>11)</b> Отдавать, жертвовать (vitam pro patria <i>C</i>); уплачивать (debitum <i>Tib</i>): morbo naturae debitum r. <i>Nep</i> отдать долг природе, <i>т. е.</i> умереть от болезни;\n<b>12)</b> исполнять (promissa, vota alicui <i>V, C etc.</i>): Veniebam redditum, quod pepigeram <i>Ap</i> я пришёл исполнить то, что обещал;\n<b>13)</b> терпеть, претерпевать: poenas alicui rei r. <i>Sl</i> нести наказание за что-л.;\n<b>14)</b> доставлять, вручать, передавать (epistulam alicui ab aliquo <i>C</i>);\n<b>15)</b> оказывать, воздавать (suum cuique <i>C</i>; honorem <i>C</i>); давать (veniam peccatis <i>H</i>): jus (jura) r. <i>T etc.</i> выносить суждение, судить; judicium r. <i>Ter, Cs, Q etc.</i> производить <i>или</i> назначать судебное следствие; rationem r. <i>C etc.</i> отдавать отчёт;\n<b>16)</b> оставлять (populo leges suas <i>L</i>);\n<b>17)</b> издавать (vocem <i>V</i>; sonum <i>Q</i>); испускать (animam <i>V</i>); извергать (flammam <i>PM</i>; sanguinem <i>PJ</i>): partu r. рожать (catulum <i>O</i>); r. lucem <i>O</i> <i>или</i> vitam <i>Lcr</i> умереть; bene reddita vita <i>C</i> хорошо прожитая жизнь;\n<b>18)</b> выделять (urinam, alvum <i>CC</i>)."],
  ["redduco", "<big>redduco</big>\n\n<b>redduco</b> <i>v. l.</i>\n= reduco."],
  ["redegi", "<big>redegi</big>\n\n<b>redegi</b> <i>pf. к</i>\nredigo."],
  ["redemi", "<big>redemi</big>\n\n<b>redemi</b> <i>pf. к</i>\nredimo."],
  ["redemptio", "<big>redemptio</big>\n\n<b>redemptio, onis</b> <i>f</i> [redimo]\n<b>1)</b> выкуп (captivorum <i>L</i>): r. sacramenti <i>bAl</i> освобождение от боннской присяги, <i>т. е.</i> от военной службы;\n<b>2)</b> откуп, подряд <i>C</i>;\n<b>3)</b> подкуй (judicii <i>C</i>);\n<b>4)</b> искупление (per sanguinem alicujus <i>Vlg</i>)."],
  ["redemptito", "<big>redemptito</big>\n\n<b>redemptito, avi, —, are</b> [<i>intens. к</i> redempto]\nискупать, заглаживать (malefacta benefactis <i>Cato</i>)."],
  ["redempto", "<big>redempto</big>\n\n<b>redempto, —, —, are</b> [<i>frequ. к</i> redimo]\nвыкупать (captivos <i>T</i>)."],
  ["redemptor", "<big>redemptor</big>\n\n<b>redemptor, oris</b> <i>m</i> [redimo]\n<b>1)</b> подрядчик, поставщик (frumenti <i>L</i>); предприниматель (operum <i>Dig</i>); откупщик (vectigalium <i>Dig</i>);\n<b>2)</b> вносящий выкуп (<i>за пленного</i>) <i>Sen</i>;\n<b>3)</b> искупитель <i>Eccl</i>."],
  ["redemptrix", "<big>redemptrix</big>\n\n<b>redemptrix, icis</b> <i>f</i>\nискупительница <i>Eccl</i>."],
  ["redemptura", "<big>redemptura</big>\n\n<b>redemptura, ae</b> <i>f</i> [redimo]\nоткуп, подряд <i>L, Dig</i>."],
  ["redemptus", "<big>redemptus</big>\n\n<b>redemptus (redemtus), a, um</b>\n<b>1.</b> <i>part. pf. к</i> redimo;\n<b>2.</b> <i>adj.</i> оплачиваемый, платный (plausor <i>Pt</i>)."],
  ["redeo", "<big>redeo</big>\n\n<b>red–eo, ii (ivi), itum, ire</b>\n<b>1)</b> (<i>тж.</i> r. viam <i>C, V</i>) идти назад, ехать обратно, возвращаться (ex provincia in Italiam <i>C</i>; a cena <i>Ter, Ph</i>; domum <i>L</i>); ad vestitum suum r. <i>C</i> вернуться к своей (обычной) одежде, <i>т. е.</i> снять траур;\n<b>2)</b> восстанавливаться, возобновляться (vigor membris redit <i>QC</i>; redeunt frondes arboribus <i>O</i>): anno redeunte <i>H</i> в новом году; res redierunt <i>C</i> дела возобновились (<i>об общественной жизни</i>);\n<b>3)</b> вновь появляться <i>или</i> показываться (vera redit facies, assimilata perit <i>Pt</i>); вновь приходить (in pristinum statum <i>Cs</i>): ad se r. <i>Ter, L etc.</i> вновь приобретать прежние свойства, принимать прежний вид <i>или</i> <i>C</i> приходить в себя, оправляться; in gratiam cum aliquo r. <i>C etc.</i> помириться с кем-л.; in memoriam alicujus rei r. <i>C</i> вспоминать о чём-л.; in juvenem r. <i>O</i> вновь стать юношей;\n<b>4)</b> вновь переходить (collis ad planitiem redit <i>Cs</i>); (<i>о теме, вопросе</i>) возвращаться, вновь заговаривать (redeamus illuc, unde divertimus <i>C</i>);\n<b>5)</b> происходить, проистекать, возникать (ex otio bellum redit <i>L</i>);\n<b>6)</b> (<i>о деньгах</i>) поступать (quinquaginta talenta redibant <i>Nep</i>);\n<b>7)</b> доходить: ad manus reditur <i>bAfr</i> дело доходит до рукопашной; ad gladios r. <i>Cs</i> взяться за мечи; omnia haec verba huc redeunt <i>Ter</i> все эти слова сводятся к этому;\n<b>8)</b> доставаться (hereditas ad filium redit <i>Dig</i>): summa imperii rediit ad aliquem <i>Cs</i> высшая власть перешла к кому-л."],
  ["redhalo", "<big>redhalo</big>\n\n<b>red–halo, —, —, are</b>\nвыдыхать, возвращать в виде испарений (umorem <i>Lcr</i>).:"],
  ["redhibeo", "<big>redhibeo</big>\n\n<b>redhibeo, buī, bitum, ēre</b> [red + habeo]\nвозвращать (aliquid salvum alicui <i>Pl</i>); отдавать назад, возвращать (<i>купленное</i>) <i>Pl, C, Dig</i>; принимать обратно (<i>проданное</i>) <i>Pl</i>."],
  ["redhibitio", "<big>redhibitio</big>\n\n<b>redhibitio, ōnis</b> <i>f</i> [redhibeo]\nвозвращение, возврат (<i>Купленного</i>) <i>Q, AG</i>; принятие обратно (<i>проданного</i>) <i>Dig</i>."],
  ["redhibitor", "<big>redhibitor</big>\n\n<b>redhibitor, ōris</b> <i>m</i>\nпринимающий обратно (<i>забракованную вещь</i>) <i>Aug</i>."],
  ["redhibitorius", "<big>redhibitorius</big>\n\n<b>redhibitōrius, a, um</b>\nкасающийся возврата забракованного предмета (actio <i>Dig</i>)."],
  ["redhostio", "<big>redhostio</big>\n\n<b>red–hostio, —, —, ire</b>\nвознаграждать, отплачивать, возмещать (aliquid <i>Acc, Naev</i>)."],
  ["redi", "<big>redi</big>\n\n<b>redi</b> <i>imper. к</i>\nredeo."],
  ["redico", "<big>redico</big>\n\n<b>re–dico, —, —, ere</b>\nповторять (aliquid <i>Sid</i>)."],
  ["Rediculus", "<big>Rediculus</big>\n\n<b>Rediculus, i</b> <i>m</i> [redeo]\nРедикул, <i>римск. божество, якобы вынудившее Ганнибала к отступлению</i> <i>PM</i>."],
  ["redies", "<big>redies</big>\n\n<b>redies, rediet</b> <i>Ap, Sen fut. к</i>\nredeo."],
  ["redigo", "<big>redigo</big>\n\n<b>redigo, egi, actum, ere</b> [red + ago]\n<b>1)</b> гнать назад, отгонять (capellas <i>O</i>); прогонять (hostem in castra <i>L</i>; aliquem in exsilium <i>Just</i>);\n<b>2)</b> вызывать (aliquid in memoriam <i>C</i>);\n<b>3)</b> взыскивать, собирать (pecuniam ex vectigalibus <i>C</i>); выручать (multum auri <i>Eutr</i>);\n<b>4)</b> обращать, направлять (praedam in fiscum <i>T</i>);\n<b>5)</b> отдавать, передавать (aliquid penes aliquem <i>C</i>); обращать (aliquem in servitutem <i>Cs</i>); приводить (Galliam in potestatem alicujus <i>Cs</i>); доводить (eo, ut... <i>Ter</i>); вынуждать (aliquem ad aliquid <i>Eutr etc.</i>); делать (aliquem infirmiorem <i>Cs</i>); сводить, низводить (ad vilem assem <i>H</i>; ad nihilum redigi <i>Lcr</i>, r. ad minimum onus <i>O</i>): r. in ordinem <i>rhH, Q</i> упорядочивать; r. in membra <i>O</i> расчленять; ad inopiam redactus <i>Sen</i> доведённый до нищеты; r. in vanum <i>L</i> сводить на нет, расстраивать; r. ad certum <i>L</i> удостоверять, делать достоверным; r. ad internecionem <i>Cs</i> истребить; r. aliquid ad limam et lineam certam <i>Ap</i> проверять что-л. с помощью напильника и отвеса, <i>т. е.</i> весьма тщательно; aliquos in gratiam r. <i>Ter</i> мирить кого-л."],
  ["redii", "<big>redii</big>\n\n<b>redii</b> <i>pf. к</i>\nredeo."],
  ["redimiculum", "<big>redimiculum</big>\n\n<b>redimiculum, i</b> <i>n</i> [redimio]\n<b>1)</b> головная повязка <i>или</i> лента (habent redimicula mitrae <i>V</i>); ожерелье (detrahere redimicula collo <i>O</i>);\n<b>2)</b> <i>перен.</i> связь, узы <i>Pl</i>."],
  ["redimio", "<big>redimio</big>\n\n<b>redimio, ii, itum, ire</b>\nобвязывать, повязывать, обвивать (frontem corona <i>M</i>; ara floribus redimita <i>Ap</i>); окружать, окаймлять (silvis redimita loca <i>Ctl</i>)."],
  ["redimitus", "<big>redimitus</big>\n\n<b>redimitus, (us) </b> <i>m</i>\nобвязывание, обвивание <i>Sol</i>."],
  ["redimo", "<big>redimo</big>\n\n<b>redimo, emi, emptum (emtum), ere</b> [red + emo]\n<b>1)</b> выкупать (domum <i>C</i>; captivos ab hostibus <i>rhH</i>); вносить выкуп, освобождать (captos e servitute <i>C</i>): r. se ab aliquo <i>Pt</i> откупиться от кого-л.;\n<b>2)</b> предотвратить, избежать (mortem morte alterius <i>O</i>); полюбовно улаживать (litem <i>C</i>);\n<b>3)</b> искупать, заглаживать (culpam <i>C</i>); redemptus a vitiis <i>J</i> загладивший свои пороки;\n<b>4)</b> спасать, выручать (aliquem sanguine suo <i>Nep</i>);\n<b>5)</b> покупать (necessaria ad aliquid <i>L</i>): plausor redemptus — <i>см.</i> plausor || обретать, приобретать, добывать, добиваться (pacem obsidibus datis <i>Cs</i>); снискать (largitione voluntatem alicujus <i>Cs</i>);\n<b>6)</b> брать на откуп (vectigalia <i>Cs</i>);\n<b>7)</b> брать с подряда, получать подряд (r. opus <i>C</i>)."],
  ["redindutus", "<big>redindutus</big>\n\n<b>red–indutus, a, um</b>\nвновь одевшийся (r. carnem <i>Tert</i>)."],
  ["redintegratio", "<big>redintegratio</big>\n\n<b>redintegratio, onis</b> <i>f</i> [redintegro]\nвозобновление, восстановление (corporis et virium <i>Eccl</i>); повторение (ejusdem verbi <i>rhH</i>)."],
  ["redintegrator", "<big>redintegrator</big>\n\n<b>redintegrator, oris</b> <i>m</i>\nвосстановитель (r. virium, <i>sc.</i> somnus <i>Tert</i>)."],
  ["redintegro", "<big>redintegro</big>\n\n<b>red–integro, avi, atum, are</b>\n<b>1)</b> снова пополнять, освежать (deminutas copias <i>Cs</i>); возобновлять (proelium <i>Cs</i>): seditio redintegratur <i>T</i> бунт вспыхивает с новой силой || восстанавливать (pacem <i>L</i>; vires <i>Cs</i>); вновь оживлять, воскрешать (spem victoriae <i>Cs</i>; memoriam alicujus rei <i>L</i>);\n<b>2)</b> повторять (verbum <i>rhH</i>)."],
  ["redinvenio", "<big>redinvenio</big>\n\n<b>red–invenio, —, —, ire</b>\nвновь обретать (coronam auream <i>Tert</i>)."],
  ["redipiscor", "<big>redipiscor</big>\n\n<b>redipiscor, —, pisci</b> <i>depon.</i> [re + apiscor]\nвновь обретать, снова получать (aliquid <i>Pl</i>)."],
  ["reditio", "<big>reditio</big>\n\n<b>reditio, onis</b> <i>f</i> [redeo]\nвозвращение <i>Pl, Ter, C, Cs etc.</i>"],
  ["reditus", "<big>reditus</big>\n\n<b>reditus, us</b> <i>m</i> [redeo]\n<b>1)</b> возвращение (ad aliquem <i>C</i>; Roma <i>C</i>): r. in amicitiam <i>Cs</i> возобновление дружбы; r. in gratiam <i>C</i> <i>и</i> gratiae <i>C</i> примирение;\n<b>2)</b> возможность (право) возвращения, возврат (nullum reditum in patriam habere <i>C</i>);\n<b>3)</b> круговорот (solis <i>C</i>);\n<b>4)</b> доход, доходы: r. metallorum <i>L</i> поступления от рудников."],
  ["redivi", "<big>redivi</big>\n\n<b>redivi</b> <i>Ap, Amm pf. к</i>\nredeo."],
  ["redivia", "<big>redivia</big>\n\n<b>redivia, ae</b> <i>f</i> <i>v. l.</i>\n= reduvia."],
  ["rediviva", "<big>rediviva</big>\n\n<b>rediviva, orum</b> <i>m</i> [redivivus]\nбывшие в употреблении строительные материалы <i>C</i>."],
  ["redivivum", "<big>redivivum</big>\n\n<b>redivivum, i</b> <i>m</i> <i>C</i>\n= rediviva."],
  ["redivivus", "<big>redivivus</big>\n\n<b>redivivus, a, um</b>\n<b>1)</b> бывший в употреблении, подержанный, старый (lapis <i>C</i>; assulae <i>Ctl</i>);\n<b>2)</b> воскресший, обновлённый (senex <i>Sen</i>)."],
  ["redo", "<big>redo</big>\n\n<b>rēdo, ōnis</b> <i>m</i>\nредон, <i>род бескостной рыбы</i> <i>Aus</i>."],
  ["redolentior", "<big>redolentior</big>\n\n<b>redolentior, ius</b> <i>adj. compar.</i> [redoleo]\nболее благовонный <i>Aug</i>."],
  ["redoleo", "<big>redoleo</big>\n\n<b>red–oleo, uī, —, ēre</b>\n<b>1)</b> пахнуть, распространять запах: r. <i>или</i> r. aliquid <i>C</i> <i>или</i> aliqua re <i>V</i> пахнуть чём-л.; anima redolens <i>Sen</i> зловонное дыхание;\n<b>2)</b> <i>перен.</i> отзываться, отдавать, иметь привкус (r. antiquitatem <i>C</i>)."],
  ["redomitus", "<big>redomitus</big>\n\n<b>re–domitus, a, um</b>\nвновь обузданный, снова приведённый в состояние покорности (cives <i>C</i>)."],
  ["Redones", "<big>Redones</big>\n\n<b>Rēdonēs, um</b> <i>m</i>\nредоны, <i>кельт. племя в Gallia Lugdunensis, в районе нын. Rennes</i> (<i>Bretagne</i>) <i>Cs</i>."],
  ["redono", "<big>redono</big>\n\n<b>re–dōno, āvī, ātum, āre</b>\n<b>1)</b> обратно приносить в дар, возвращать (aliquem diis patriis <i>H</i>);\n<b>2)</b> уступать: aliquem alicui r. <i>H</i> простить кого-л. в угоду кому-л.; r. iras <i>H</i> унять свой гнев, смягчиться."],
  ["redoperio", "<big>redoperio</big>\n\n<b>red–operio, ruī, —, īre</b>\nвновь открывать (aliquid <i>Ambr</i>)."],
  ["redopto", "<big>redopto</big>\n\n<b>red–opto, —, —, āre</b>\nвновь желать (mortem <i>Tert</i>)."],
  ["redordior", "<big>redordior</big>\n\n<b>red–ōrdior, —, īrī</b> <i>depon.</i>\nраспускать (telas <i>PM</i>)."],
  ["redormio", "<big>redormio</big>\n\n<b>re–dormio, —, —, īre</b>\nещё раз засыпать, вновь заснуть <i>CC, PJ</i>."],
  ["redormitio", "<big>redormitio</big>\n\n<b>redormītio, ōnis</b> <i>f</i>\nвторичное засыпание <i>PM</i>."],
  ["redorno", "<big>redorno</big>\n\n<b>red–ōrno, —, —, āre</b>\nвновь украшать <i>Tert</i>."],
  ["reducis", "<big>reducis</big>\n\n<b>reducis</b> <i>gen. к</i>\nredux."],
  ["reduco", "<big>reduco</big>\n\n<b>re–duco, duxi, ductum, ere</b>\n<b>1)</b> отодвигать назад (turres <i>Cs</i>); отводить назад, заносить (для удара) (hastam <i>V</i>): reducta cuspide <i>Pl</i> размахнувшись копьём || отводить, оттягивать (remos ad pectora <i>O</i>; copias a munitionibus in castra <i>Cs</i>): aliquem a morte reduxisse <i>V</i> спасти кого-л. от смерти || вынимать (gladium e vagina <i>C</i>); переносить назад (munitiones a fossa centum pedes <i>Cs</i>); вновь выводить (aliquem in scaenam <i>PM</i>);\n<b>2)</b> провожать (aliquem domum <i>Pl, C etc.</i>);\n<b>3)</b> возвращать (de <i>и</i> ab exsilio <i>C, Q</i>); возвращать к себе, приводить обратно (uxorem <i>Ter, Nep</i>); отзывать обратно (legatos <i>L</i>);\n<b>4)</b> вновь вводить, восстанавливать (legem <i>T</i>; aliquid in memoriam <i>C</i>); возобновлять, воскрешать (spem <i>H</i>; timoris tormentum <i>Sen</i>): aliquem in gratiam cum aliquo r. <i>C</i> примирить кого-л. с кем-л.; aestas reducitur <i>V</i> лето возвращается; in formam r. <i>O</i> придавать образ, формировать; aliquem ex errore r. in viam <i>Pl</i> указать кому-л. правильный путь;\n<b>5)</b> задерживать: reducto spiritu <i>Pt</i> затаив дыхание."],
  ["reducta", "<big>reducta</big>\n\n<b>reducta, orum</b> <i>n</i> (<i>греч.</i> apoproegmena)\n<i>в философии стоиков</i> вещи, которых не стоит желать (<i>ср.</i> producta) <i>C</i>."],
  ["reductio", "<big>reductio</big>\n\n<b>reductio, onis</b> <i>f</i> [reduco]\n<b>1)</b> возвращение, восстановление (regis <i>C</i>);\n<b>2)</b> отодвигание назад (rudentium ductiones et reductiones <i>Vtr</i>)."],
  ["reducto", "<big>reducto</big>\n\n<b>reducto, —, —, are</b> [<i>intens. к</i> reduco]\nотводить назад, обратно приводить (exercitum <i>AV</i>)."],
  ["reductor", "<big>reductor</big>\n\n<b>reductor, oris</b> <i>m</i> [reduco]\n<b>1)</b> возвращающий, приведший назад (r. plebis Romanae in urbem <i>L</i>);\n<b>2)</b> восстановитель (litterarum <i>PJ</i>)."],
  ["reductus", "<big>reductus</big>\n\n<b>reductus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> reduco;\n<b>2.</b> <i>adj.</i> находящийся в стороне, удалённый, отдалённый, уединённый (vallis <i>V, H</i>): utrimque r. <i>H</i> равно далёкий от обеих (крайностей); reducti introrsus oculi <i>Sen</i> глубоко сидящие (впалые) глаза."],
  ["redulcero", "<big>redulcero</big>\n\n<b>red–ulcero, avi, atum, are</b>\nбередить, растравлять, раздражать (aliquid pumice <i>Col</i>); <i>перен.</i> возобновлять, воскрешать (dolorem <i>Ap</i>)."],
  ["reduncus", "<big>reduncus</big>\n\n<b>red–uncus, a, um</b>\nзагнутый назад <i>или</i> вниз, изогнутый, искривлённый (cornua <i>PM</i>; rostrum aquilae <i>O</i>)."],
  ["redundans", "<big>redundans</big>\n\n<b>redundans, antis</b>\n<b>1.</b> <i>part. praes. к</i> redundo;\n<b>2.</b> <i>adj.</i> избыточный, излишний (amputatio redundantioris nitoris <i>Tert</i>)."],
  ["redundanter", "<big>redundanter</big>\n\n<b>redundanter</b> [redundo]\nслишком обильно, в преизбытке (immodice et r. <i>PJ</i>)."],
  ["redundantia", "<big>redundantia</big>\n\n<b>redundantia, ae</b> <i>f</i> [redundo]\n<b>1)</b> разлитие вод, разлив (habere crebras redundantias <i>Vtr</i>);\n<b>2)</b> преизбыток (carere redundantia et egestate <i>Ap</i>);\n<b>3)</b> <i>перен.</i> велеречивость, многословие <i>C</i>."],
  ["redundatio", "<big>redundatio</big>\n\n<b>redundatio, onis</b> <i>f</i>\n<b>1)</b> разлитие, <i>перен.</i> переполнение (stomachi <i>PM</i>);\n<b>2)</b> множество, изобилие (cicatricum <i>Q</i>);\n<b>3)</b> обратное движение (astrorum <i>Vtr</i>)."],
  ["redundo", "<big>redundo</big>\n\n<b>red–undo, avi, atum, are</b> [unda]\n<b>1)</b> разливаться, растекаться, выступать из берегов (lacus redundant <i>C</i>); обливаться, истекать (sanguine <i>C</i>);\n<b>2)</b> быть велеречивым, многословным (orator redundans <i>C</i>): nihil inane aut redundans ferre <i>Q</i> не допускать ни пустословия, ни многословия || обильно течь (ex rerum cognitione oratio redundat <i>C</i>);\n<b>3)</b> быть перегруженным (hesterna cena <i>PJ</i>); обрушиваться в изобилии (dolores in aliquem redundant <i>C</i>): redundante multitudine <i>T</i> в огромном множестве;\n<b>4)</b> вливаться, притекать, стекаться (in provincias <i>C</i>);\n<b>5)</b> происходить, получаться <i>или</i> поступать во множестве (pecuniae hinc redundant <i>C</i>); вытекать, обнаруживаться, оказываться в полной мере: non reus ex ea causa Postumus redundat <i>C</i> Постум не оказался целиком виновным в этом деле;\n<b>6)</b> изобиловать, иметь в избытке (r. aliqua re <i>C etc.</i>); находиться в избытке (aliquid alicui redundat <i>C</i>); быть чрезмерным (ornatus orationis in eo redundat <i>C</i>): redundatus <i>поэт.</i> = redundans (redundatae aquae <i>O</i>);\n<b>7)</b> наплывать, обрушиваться (mala exempla in eos redundant, qui faciunt <i>Sen</i>): sera redundavit veteris vindicta libelli <i>O</i> запоздалая кара обрушилась (на меня) за давно опубликованную книжку."],
  ["reduplicatio", "<big>reduplicatio</big>\n\n<b>re–duplicatio, onis</b> <i>f</i> <i>ритор.</i>, <i>грам.</i>\nудвоение."],
  ["reduplicatus", "<big>reduplicatus</big>\n\n<b>re–duplicatus, a, um</b>\nдвойной, удвоенный (quae amiserat, reduplicata possedit <i>Tert</i>)."],
  ["reduresco", "<big>reduresco</big>\n\n<b>re–duresco, —, —, ere</b>\nснова отвердевать (ferrum redurescit <i>Vtr</i>)."],
  ["reduvia", "<big>reduvia</big>\n\n<b>reduvia, ae</b> <i>f</i>\n<b>1)</b> заусеница: curare reduviam <i>погов. C</i> = заниматься мелочами;\n<b>2)</b> остатки (escarum <i>Sol</i>)."],
  ["redux", "<big>redux</big>\n\n<b>redux, ucis</b> <i>adj.</i> [reduco]\n<b>1)</b> приводящий назад, ведущий обратно (ad penatos iter <i>M</i>); возвращающий (Juppiter <i>O</i>; Fortuna <i>M</i>);\n<b>2)</b> вернувшийся, возвращающийся, возвратившийся (exercitus r. <i>L</i>; r. domum <i>L</i>; navis r. <i>L</i>): aliquem reducem facere in patriam <i>Pl, Ter</i> вернуть кого-л. на родину."],
  ["reduxi", "<big>reduxi</big>\n\n<b>reduxi</b> <i>pf. к</i>\nreduco."],
  ["reexpecto", "<big>reexpecto</big>\n\n<b>re–expecto, —, —, are</b>\nвновь ожидать <i>Vlg</i>."],
  ["refabrico", "<big>refabrico</big>\n\n<b>re–fabrico, —, —, are</b>\nвосстанавливать (collapsum balneum <i>CJ</i>)."],
  ["refeci", "<big>refeci</big>\n\n<b>refeci</b> <i>pf. к</i>\nreficio."],
  ["refectio", "<big>refectio</big>\n\n<b>refectio, onis</b> <i>f</i> [reficio]\n<b>1)</b> возобновление, обновление, ремонт (Capitolii <i>Su</i>; itinerum <i>Dig</i>);\n<b>2)</b> подкрепление, освежение (somnus refectioni necessarius est <i>Sen</i>); отдохновение (laborum <i>Amm</i>)."],
  ["refector", "<big>refector</big>\n\n<b>refector, oris</b> <i>m</i> [reficio]\nвосстановитель, возобновитель, реставратор (<i>sc.</i> statuarum <i>Su</i>)."],
  ["refectorius", "<big>refectorius</big>\n\n<b>refectorius, a, um</b>\nвосстанавливающий (силы), освежающий (quies <i>Ambr</i>)."],
  ["refectus", "<big>refectus</big>\n\n<b>I refectus, a, um</b> <i>part. pf. к</i>\nreficio.\n\n<b>II refectus, us</b> <i>m</i>\n<b>1)</b> отдых <i>Ap</i>;\n<b>2)</b> доход <i>Dig</i>."],
  ["refello", "<big>refello</big>\n\n<b>refello, felli, —, ere</b> [re + fallo]\n<b>1)</b> опровергать, изобличать во лжи (orationem alicujus <i>C</i>; aliquem <i>C, Q</i>; dicta <i>V</i>): nil r. alicujus rei <i>Ter</i> не возражать против чего-л.;\n<b>2)</b> <i>поэт.</i> отражать, устранять, удалять (crimen ferro <i>V</i>)."],
  ["refercio", "<big>refercio</big>\n\n<b>re–fercio, fersi, fertum, ire</b> [farcio]\nнабивать, наполнять (aliquid aliqua re <i>C, PJ, Sil etc.</i>)."],
  ["referendarius", "<big>referendarius</big>\n\n<b>referendarius, i</b> <i>m</i>\nреферендарий, <i>личный секретарь и докладчик императора</i> <i>CJ</i>."],
  ["referio", "<big>referio</big>\n\n<b>re–ferio, —, —, ire</b>\n<b>1)</b> отвечать ударом на удар, отбивать (r. aliquem <i>Pl</i>);\n<b>2)</b> отражать (referiri speculi imagine <i>O</i>; referitur vocibus echo <i>Aus</i>)."],
  ["referivus", "<big>referivus</big>\n\n<b>referivus, a, um</b> <i>v. l.</i>\n= refrivus."],
  ["refero", "<big>refero</big>\n\n<b>re–fero, rettuli (retuli), relatum (rellatum), referre</b>\n<b>1)</b> нести назад, уносить обратно (aliquem domum <i>Su</i>); относить, гнать назад (navem in mare <i>H</i>): me referunt pedes in Tusculanum <i>C</i> ноги сами несут меня (<i>т. е.</i> меня тянет) в Тускулан; a mari relatus <i>PM</i> удалённый от моря || вынимать, извлекать (telum e corpore <i>Sil</i>);\n<b>2)</b> относить назад, развевать (aura refert talaria <i>O</i>); отводить, передвигать назад (castra <i>L</i>); поворачивать назад (caput <i>O</i>); подносить (maniis ad ora <i>Pt</i>): r. pedem <i>O, QC, Pt etc.</i> (gradum <i>L</i>, vestigia <i>V</i>) <i>или</i> se r. (<i>реже</i> referri) отступать, идти обратно, возвращаться (se in castra r. <i>Cs</i>);\n<b>3)</b> переводить, направлять (oculos in aliquem <i>O</i>): retulisse se ad aliquid <i>C</i> предаться чему-л. || обратно вкладывать (rursus enses vaginae <i>Sil</i>);\n<b>4)</b> приставлять (digitos ad frontem <i>O</i>);\n<b>5)</b> приносить (с собой), доставлять (aliquid ad Caesarem <i>Cs</i>; opima spolia <i>L</i>; egregiam laudem <i>V</i>); приносить в дар <i>или</i> в жертву (lauream Jovi <i>Su</i>); вносить (mille talenta in publicum <i>Nep</i>);\n<b>6)</b> относить, связывать, приурочивать: se de Trajani genere r. <i>Vop</i> объявить себя потомком Траяна || причислять, относить (aliquem in oratorum numerum <i>C</i>); перекладывать, возлагать (culpam in aliquem <i>QC</i>); сводить (omnia ad voluptatem <i>C</i>): ad oculos omnia r. <i>C</i> сводить всё к зрительным восприятиям || приписывать (aliquid ad fortunam <i>T</i>): referri ad casum non potest <i>T</i> (это) не может быть приписано случайности || ставить в зависимость (omnia ad suum arbitrium r. <i>C</i>);\n<b>7)</b> одерживать (r. victoriam de <i>или</i> ex aliquo <i>Just, L, VM</i>);\n<b>8)</b> обращать (r. animum ad studia <i>C</i>; se r. ad philosophiam <i>C</i>);\n<b>9)</b> сопоставлять, сравнивать, судить (aliquid ad se ipsum <i>C</i>): aliquid ad animum r. <i>Ph</i> принимать во внимание что-л.; majores ab aliquo r. <i>Just</i> вести родословную от кого-л.;\n<b>10)</b> отдавать назад, возвращать (pateram surreptam <i>C</i>); обратно извергать (cum sanguine mixta vina <i>V</i>); восстанавливать, возвращать (judicia ad equestrem ordinem <i>C</i>; alicui praeteritos annos <i>V</i>); воздавать, отплачивать, возмещать (par pari r. <i>Ter</i>): alicui salutem r. <i>C</i> ответить кому-л. на приветствие; alicui gratiam r. <i>C, Cs etc.</i> отблагодарить кого-л.;\n<b>11)</b> возражать, отвечать (aliquid alicui r. <i>C etc.</i>): tandem pauca refert <i>V</i> наконец (Эней) кратко отвечает;\n<b>12)</b> произносить (litteram expressius <i>VM</i>); передавать, повторять (responsum <i>L</i>): r. verba geminata <i>C</i> повторять за кем-л. слова; r. aliquem <i>C</i> ещё раз (дважды) назвать кого-л.; r. vocem <i>C, O, QC etc.</i> дать отзвук, отозваться эхом на голос (clamor refertur totis castris <i>L</i>);\n<b>13)</b> восстанавливать, возобновлять, опять вводить (antiquum morem <i>Su</i>); вновь приобретать (amissos colores <i>H</i>); подавать на новое рассмотрение (rem judicatam <i>C</i>); вновь вносить на утверждение (legem <i>L</i>); восстанавливать в памяти, вспоминать (magna facta <i>V</i>);\n<b>14)</b> пересказывать (sermonem <i>C, H</i>); цитировать (exemplum a Cicerone relatum <i>VP</i>); приводить (versum <i>Nep</i>);\n<b>15)</b> воспроизводить, быть похожим (r. patrem sermone vultu que <i>T</i>): r. nomine avum <i>V</i> носить имя деда;\n<b>16)</b> передавать, вверять (consulatum ad aliquem <i>C</i>);\n<b>17)</b> сообщать, передавать, докладывать, доносить (aliquid alicui <i>или</i> ad aliquem <i>C, Cs, L etc.</i>): digna relatu <i>O</i> достойное быть рассказанным, заслуживающее упоминания; quid referam Cannas? <i>Man</i> стоит ли мне рассказать о (катастрофе при) Каннах?;\n<b>18)</b> называть (aliquem parentem <i>V</i>);\n<b>19)</b> обращаться (ad aliquem aliquid <i>или</i> de aliqua re <i>C, L etc.</i>);\n<b>20)</b> записывать, вносить, регистрировать (nomen alicujus in tabulas r. <i>C</i>; r. pecuniam alicui datam <i>C</i>; r. aliquem in <i>или</i> inter proscriptos <i>C, Su</i>);\n<b>21)</b> включать, вводить (epistulas in volumina <i>C</i>); соотносить, сопоставлять: terram hanc puncti loco ponimus ad universa referentes <i>Sen</i> сравнивая нашу землю с вселенной, мы находим, что она есть как бы точка."],
  ["refert", "<big>refert</big>\n\n<b>refert, retulit, referre</b> <i>impers.</i> [res + ferre, <i>ср.</i> ex re est; <i>не смешивать с</i> refert <i>от</i> refero]\nважно, имеет значение, небезразлично, полезно (ad me aut ad meam rem <i>Pl</i>): mea refert <i>Pl, Ter, C</i> для меня важно; magnopere alicujus (<i>или</i> alicui) refert <i>C etc.</i> весьма важно для кого-л.; nihil refert <i>Pl etc.</i> безразлично, не имеет значения, неважно, всё равно; quid refert? <i>C etc.</i> для чего?; к чему?, что толку?, не всё ли равно?; tamquam referret <i>T</i> словно это имело какое-л. значение."],
  ["refertus", "<big>refertus</big>\n\n<b>refertus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> refercio;\n<b>2.</b> <i>adj.</i> наполненный, переполненный, битком набитый (aliqua re <i>или</i> alicujus rei <i>C</i>)."],
  ["referveo", "<big>referveo</big>\n\n<b>re–ferveo, —, —, ere</b>\nсильно кипеть, вскипать: refervens falsum crimen <i>C</i> жгучая клевета."],
  ["refervesco", "<big>refervesco</big>\n\n<b>refervesco, —, —, ere</b> [referveo]\n<b>1)</b> вскипать, клокотать, разгорячаться, приходить в волнение (sanguis refervescit <i>C</i>);\n<b>2)</b> перестать кипеть, остывать, утихать (oratio refervescit <i>C</i> — <i>v. l.</i>)."],
  ["refibulo", "<big>refibulo</big>\n\n<b>re–fibulo, —, —, are</b>\nрасстёгивать, освобождать (aliquid <i>M</i>)."],
  ["reficio", "<big>reficio</big>\n\n<b>re–ficio, feci, tectum, ere</b> [facio]\n<b>1)</b> снова делать, заново изготовлять (arma, tela <i>Sl</i>);\n<b>2)</b> переизбирать, вновь назначать (aliquem consulem <i>Sen</i>);\n<b>3)</b> восстанавливать (rex rursus refectus <i>T</i>); отстраивать, исправлять, ремонтировать, чинить (muros dirutos <i>Nep</i>; rates quassas <i>H</i>; laceras puppes <i>O</i>); вновь раздувать (flammam <i>O</i>); возобновлять (refectum est convivium <i>Pt</i>);\n<b>4)</b> восполнять, пополнять, комплектовать (copias <i>Cs</i>);\n<b>5)</b> восстанавливать силы, давать отдых, освежать (r. aliquem ex labore <i>Cs</i>; ingenium studio fatigatum <i>Sen</i>); ободрять, давать оправиться (r. animos militum a terrore <i>L</i>); укреплять, подкреплять (vires cibo <i>L</i>); ставить на ноги, излечивать (saucios <i>Sl</i>): se r. <i>или</i> <i>pass.</i> refici оправиться, прийти в себя, отдохнуть (a jactatione maritima <i>L</i>); res refectae sunt <i>Nep</i> дела поправились; vix unquam refectus sum <i>Pt</i> я едва пришёл в себя;\n<b>6)</b> извлекать, добывать, выручать (pecuniam ex fundo <i>C</i>);\n<b>7)</b> возмещать, покрывать (impensas belli <i>Just</i>);\n<b>8)</b> слагать, сочинять (poemata <i>Ap</i>)."],
  ["refigo", "<big>refigo</big>\n\n<b>re–figo, fixi, fixum, ere</b>\n<b>1)</b> снимать (clipeum de poste <i>V</i>); удалять (dentes <i>CC</i>); срывать (signa templis <i>H</i>; tabulas <i>C</i>);\n<b>2)</b> отменять, упразднять, уничтожать (legem <i>C</i>);\n<b>3)</b> упаковывать, укладывать (nostra <i>C</i>)."],
  ["refiguro", "<big>refiguro</big>\n\n<b>re–figuro, —, —, are</b>\nвновь создавать (creaturam ad suum genus <i>Vlg</i>)."],
  ["refingo", "<big>refingo</big>\n\n<b>re–fingo, —, —, ere</b>\nопять образовывать, снова создавать (aliquid <i>V</i>): laetiorem se r. <i>Ap</i> принять весёлый вид."],
  ["refixus", "<big>refixus</big>\n\n<b>refixus, a, um</b> <i>part. pf. к</i>\nrefigo."],
  ["reflabilis", "<big>reflabilis</big>\n\n<b>reflābilis, e</b> [reflo]\nулетучивающийся (cibi <i>CA</i>)."],
  ["reflabri venti", "<big>reflabri venti</big>\n\n<b>reflābrī ventī</b> <i>m</i>\nветры, дующие в обоих противоположных направлениях <i>Ap</i>."],
  ["reflagito", "<big>reflagito</big>\n\n<b>re–flāgito, —, —, āre</b>\nтребовать обратно <i>Ctl</i>."],
  ["reflatio", "<big>reflatio</big>\n\n<b>reflātio, ōnis</b> <i>f</i> [reflo]\nиспарение <i>CA</i>."],
  ["reflatus", "<big>reflatus</big>\n\n<b>reflātus, ūs </b> <i>m</i> [reflo]\n<b>1)</b> встречный ветер <i>C</i>;\n<b>2)</b> выдох (<i>у кита</i>) (r. beluae <i>PM</i>)."],
  ["reflecto", "<big>reflecto</big>\n\n<b>re–flecto, flexi, flexum, ere</b>\n<b>1)</b> загибать, запрокидывать назад (cervicem <i>V, O, Macr</i>); поворачивать, обращать назад (caput <i>Ctl</i>; oculos <i>O</i>); r. pedem <i>Ctl</i>, gressum <i>SenT</i> <i>и</i> cursum <i>VM</i> возвратиться; longos ungues (<i>acc. gr.</i>) reflecti <i>O</i> загибаться наподобие длинных когтей;\n<b>2)</b> обращать (animum ad aliquid <i>C</i>): animum r. <i>V</i> вспоминать, думать (о чём-л.);\n<b>3)</b> склонять, смягчать (animum, mentes <i>C</i>): aliquid in melius r. <i>V</i> улучшить, изменить к лучшему что-л.;\n<b>4)</b> отступать, уходить (causa morbi reflexit <i>Lcr</i>)."],
  ["reflexio", "<big>reflexio</big>\n\n<b>reflexio, onis</b> <i>f</i> [reflecto]\n<b>1)</b> загибание, закидывание <i>или</i> поворачивание (cervicis <i>Macr</i>);\n<b>2)</b> <i>грам.</i> перестановка, конверсия <i>Ap</i>."],
  ["reflexus", "<big>reflexus</big>\n\n<b>reflexus, us</b> <i>m</i> [reflecto]\n<b>1)</b> изгиб: oceani r. <i>Ap</i> морской залив;\n<b>2)</b> обратное движение, поворот (solis <i>Eccl</i>)."],
  ["reflo", "<big>reflo</big>\n\n<b>re–flo, avi, atum, are</b>\n<b>1)</b> дуть навстречу (venti reflantes <i>C</i>);\n<b>2)</b> вновь выдыхать (aer ducitur atque reflatur <i>Lcr</i>); выпускать воздух (r. folles <i>Lampr</i>);\n<b>3)</b> надувать, раздувать (laciniam <i>Ap</i>);\n<b>4)</b> испарять (reflata sucositas <i>CA</i>)."],
  ["refloresco", "<big>refloresco</big>\n\n<b>re–floresco, rui, —, ere</b>\nвновь зацветать, снова расцветать <i>PM, Sil, Vlg</i>."],
  ["refluctuo", "<big>refluctuo</big>\n\n<b>re–fluctuo, —, —, are</b> <i>Is</i>\n= refluo 1."],
  ["refluo", "<big>refluo</big>\n\n<b>re–fluo, fluxi, fluxum, ere</b>\n<b>1)</b> течь назад (Meandros refluit fluitque <i>O</i>);\n<b>2)</b> разливаться, заливать (Nilus campis refluit <i>V</i>)."],
  ["refluus", "<big>refluus</big>\n\n<b>refluus, a, um</b> [refluo]\n<b>1)</b> текущий назад, находящийся в состоянии отлива (mare <i>O, Lcn</i>);\n<b>2)</b> омываемый морем (litus <i>Ap</i>)."],
  ["refocilo", "<big>refocilo</big>\n\n<b>re–focilo (refocillo), avi, atum, are</b>\nвновь согревать, <i>перен.</i> оживлять, приводить в себя (lugentem <i>Sen</i> — <i>v. l.</i>; spiritum <i>Vlg</i>)."],
  ["refodio", "<big>refodio</big>\n\n<b>re–fodio, fodi, fossum, ere</b>\n<b>1)</b> вскапывать, перекапывать (solum quam altissime <i>PM</i>);\n<b>2)</b> выкапывать (alicujus corpus <i>PM</i>; latices refossae <i>Lcn</i>)."],
  ["reformatio", "<big>reformatio</big>\n\n<b>reformatio, onis</b> <i>f</i> [reformo]\n<b>1)</b> преображение, превращение <i>Ap</i>;\n<b>2)</b> преобразование, исправление (morum <i>Sen</i>);\n<b>3)</b> возвращение, восстановление (possessionis <i>CJ</i>)."],
  ["reformator", "<big>reformator</big>\n\n<b>reformator, oris</b> <i>m</i> [reformo]\nсовершающий превращение, преобразователь, <i>перен.</i> исправитель (litterarum <i>PJ</i>)."],
  ["reformatus", "<big>reformatus</big>\n\n<b>reformatus, us</b> <i>m</i>\nпреобразование <i>Tert</i>."],
  ["reformidatio", "<big>reformidatio</big>\n\n<b>reformidatio, onis</b> <i>f</i> [reformido]\nсильный страх, боязнь <i>C</i>."],
  ["reformido", "<big>reformido</big>\n\n<b>re–formido, avi, atum, are</b>\n<b>1)</b> страшиться, бояться (aliquem, aliquid <i>C, V etc.</i>; dicere r. <i>C</i>);\n<b>2)</b> отставать в росте, испытывать задержку (oculus vitis reformidat <i>Col</i>)."],
  ["reformo", "<big>reformo</big>\n\n<b>re–formo, avi, atum, are</b>\n<b>1)</b> преображать, превращать (ad <i>или</i> in aliquid <i>O, Ap</i>): reformari in primos annos <i>O</i> быть омоложённым;\n<b>2)</b> преобразовывать, улучшать (mores depravatos <i>PJ</i>);\n<b>3)</b> восстанавливать (pacem <i>Eutr</i>); отстраивать (templum <i>Sol</i>);\n<b>4)</b> изменять, переделывать (aliquid in pejus <i>Dig</i>)."],
  ["refossus", "<big>refossus</big>\n\n<b>refossus, a, um</b> <i>part. pf. к</i>\nrefodio."],
  ["refotus", "<big>refotus</big>\n\n<b>refotus, a, um</b> <i>part. pf. к</i>\nrefoveo."],
  ["refoveo", "<big>refoveo</big>\n\n<b>re–foveo, fovi, fotum, ere</b>\n<b>1)</b> опять, снова согревать (artus admoto igne <i>QC</i>);\n<b>2)</b> оживлять, вызывать подъём, воскрешать (studia exstincta <i>PJ</i>); подкреплять, укреплять (corpus <i>O</i>; vires <i>VP, T</i>); refotum (eum) nuntiare <i>T</i> сообщить, что ему стало лучше || обнадёживать, ободрять, успокаивать (animum <i>AG</i>)."],
  ["refractariolus", "<big>refractariolus</big>\n\n<b>refractariolus, a, um</b> [<i>demin. к</i> refractarius]\nнесколько строптивый, довольно упрямый <i>C</i>."],
  ["refractarius", "<big>refractarius</big>\n\n<b>refractarius, a, um</b> [refringo]\nстроптивый, упрямый, несговорчивый <i>Sen</i>."],
  ["refraen-", "<big>refraen-</big>\n\n<b>refraen-</b> <i>v. l.</i>\n= refren-."],
  ["refragatio", "<big>refragatio</big>\n\n<b>refragatio, onis</b> <i>f</i>\nпротиводействие, сопротивление <i>Aug</i>."],
  ["refragator", "<big>refragator</big>\n\n<b>refragator, oris</b> <i>m</i>\nпротивник <i>Tert</i>."],
  ["refragium", "<big>refragium</big>\n\n<b>refragium, i</b> <i>n</i>\n<b>1)</b> противодействие <i>Ambr</i>;\n<b>2)</b> препятствие <i>Ambr</i>."],
  ["refragor", "<big>refragor</big>\n\n<b>re–fragor, atus sum, ari</b> <i>depon.</i>\nподавать голос против, голосовать (быть) против, возражать (alicui <i>C, L etc.</i>); противиться, сопротивляться, противодействовать, мешать (alicui rei <i>QC, PM</i>)."],
  ["refregi", "<big>refregi</big>\n\n<b>refregi</b> <i>pf. к</i>\nrefringo."],
  ["refrenatio", "<big>refrenatio</big>\n\n<b>refrenatio, onis</b> <i>f</i> [refreno]\nобуздывание, укрощение, сдерживание (doloris <i>Sen</i>; libidinum <i>Aug</i>)."],
  ["refreno", "<big>refreno</big>\n\n<b>re–freno, avi, atum, are</b>\nобуздывать, укрощать, сдерживать (equos <i>QC</i>; aquas <i>O</i>; animum <i>C</i>): ipsis stimulis refrenor <i>Ap</i> то, что. (казалось бы) должно поощрять меня, (напротив) стесняет || удерживать (aliquem ab aliqua re <i>C</i>)."],
  ["refrico", "<big>refrico</big>\n\n<b>re–frico, fricui, fricatum, are</b>\n<b>1)</b> вновь растравлять, бередить (vulnus <i>C</i>; obductam cicatricem <i>C</i>);\n<b>2)</b> возобновлять, воскрешать, вновь вызывать, снова возбуждать (desiderium, dolorem, memoriam <i>C</i>);\n<b>3)</b> вновь проявляться, обнаруживаться (lippitudo refricat <i>C</i>)."],
  ["refrigeratio", "<big>refrigeratio</big>\n\n<b>refrigeratio, onis</b> <i>f</i> [refrigero]\n<b>1)</b> охлаждение, освежение, прохлада <i>Col, C, Vtr</i>;\n<b>2)</b> ослабление болезни, ремиссия <i>Veg</i>."],
  ["refrigeratorius", "<big>refrigeratorius</big>\n\n<b>refrigeratorius, a, um</b>\nохлаждающий, освежающий (vis <i>PM</i>)."],
  ["refrigeratrix", "<big>refrigeratrix</big>\n\n<b>refrigeratrix, icis</b> <i>f</i>\nохлаждающая, освежающая (natura <i>PM</i>)."],
  ["refrigerium", "<big>refrigerium</big>\n\n<b>refrigerium, i</b> <i>n</i>\n<b>1)</b> прохлада, свежесть <i>Vlg</i>;\n<b>2)</b> облегчение, утешение <i>Eccl</i>."],
  ["refrigero", "<big>refrigero</big>\n\n<b>re–frigero, avi, atum, are</b>\n<b>1)</b> охлаждать, остужать (aquam decoctam <i>PM</i>);\n<b>2)</b> освежать (membra unda <i>O</i>);\n<b>3)</b> умерять, облегчать (podagras <i>PM</i>);\n<b>4)</b> лишать подъёма, рвения, расхолаживать (sermo refrigeratus <i>C</i>); <i>med.-pass.</i> охладевать, ослабевать <i>Su</i>."],
  ["refrigerosus", "<big>refrigerosus</big>\n\n<b>refrigerosus, a, um</b> <i>Aug</i>\n= refrigeratorius."],
  ["refrigescentia", "<big>refrigescentia</big>\n\n<b>refrigescentia, ae</b> <i>f</i>\nосвежение <i>Tert</i>."],
  ["refrigesco", "<big>refrigesco</big>\n\n<b>re–frigesco, frixi, —, ere</b>\n<b>1)</b> остывать (vinum refrixit <i>Cato</i>; sanguis vel calescit, vel refrigescit <i>CC</i>);\n<b>2)</b> охладевать, ослабевать (oratio refrigescit <i>Q</i>);\n<b>3)</b> терять виды на избрание (Scaurus refrixit <i>C</i>);\n<b>4)</b> утихать (forum refrixit <i>C</i>)."],
  ["refringo", "<big>refringo</big>\n\n<b>re–fringo, fregi, fractum, ere</b> [frango]\n<b>1)</b> взламывать, выламывать, ломать (portas <i>Cs, O</i>; carcerem <i>L</i>): refracta janua <i>T</i> взломав ворота || отламывать (ramum <i>V</i>);\n<b>2)</b> сокрушить, одолеть, сломить (vim fluminis <i>Cs</i>; dominationem <i>L</i>; Achivos <i>H</i>); уничтожать, разрушать (claustra <i>C</i>); коверкать (verba <i>St</i>);\n<b>3)</b> преломлять (radium solis <i>PM</i>);\n<b>4)</b> разрывать, раздирать (vestes <i>O</i>)."],
  ["refrivus", "<big>refrivus</big>\n\n<b>refrivus, a, um</b> [refero]\nприносимый домой (<i>для умилостивительного жертвоприношения</i>) (faba <i>PM</i>)."],
  ["refrixi", "<big>refrixi</big>\n\n<b>refrixi</b> <i>pf. к</i>\nrefrigesco."],
  ["refrondesco", "<big>refrondesco</big>\n\n<b>re–frondesco, —, —, ere</b>\nпокрываться свежей листвой, вновь зеленеть <i>Sid</i>."],
  ["refudi", "<big>refudi</big>\n\n<b>refudi</b> <i>pf. к</i>\nrefundo."],
  ["refuga", "<big>refuga</big>\n\n<b>re–fuga, ae</b> <i>m</i>\n<b>1)</b> дезертир <i>Dig</i>;\n<b>2)</b> перебежчик <i>CJ</i>;\n<b>3)</b> отступник <i>Eccl</i>."],
  ["refugio", "<big>refugio</big>\n\n<b>re–fugio, fugi, fugitum, ere</b>\n<b>1)</b> бежать (назад), убегать (ex castris in montem <i>Cs</i>): r. anguem <i>V</i> отскочить от змеи;\n<b>2)</b> отступать, удаляться (ad suos <i>C</i>; domum <i>Su</i>); находиться вдали (templum refugit a litore <i>V</i>); скрываться, исчезать (ex oculis <i>O</i>): r. memoriam <i>Col</i> ускользнуть из памяти;\n<b>3)</b> искать прибежища, прибегать (r. ad carminis tranquillitatem <i>Pt</i>);\n<b>4)</b> избегать, уклоняться (r. pericula <i>C</i>): a salibus suffusis felle r. <i>O</i> воздерживаться от жёлчных насмешек; r. tendere barbiton <i>H</i> отказываться настроить барбитон;\n<b>5)</b> противиться (refugit animus eaque dicere reformidat <i>C</i>)."],
  ["refugium", "<big>refugium</big>\n\n<b>refugium, i</b> <i>n</i> [refugio]\n<b>1)</b> поиски убежища (r. ad naves <i>Frontin</i>): portas refugiis alicujus aperire <i>Just</i> дать убежище кому-л.;\n<b>2)</b> убежище (silvae dederunt r. <i>L</i>)."],
  ["refugus", "<big>refugus</big>\n\n<b>refugus, a, um</b> [refugio]\nбегущий назад (in castra <i>T</i>); отступающий, отхлынувший (unda <i>O</i>); спадающий (Nilus <i>PJ</i>); закинутый назад (capilli <i>Lcn</i>)."],
  ["refulgentia", "<big>refulgentia</big>\n\n<b>refulgentia, ae</b> <i>f</i>\nблеск, отблеск, отсвет, сверкание (lacunarium <i>Ap</i>)."],
  ["refulgeo", "<big>refulgeo</big>\n\n<b>re–fulgeo, fulsi, —, ere</b>\nотражать лучи, сверкать, сиять, блистать (arma refulgentia <i>L</i>; stella refulsit <i>H</i>)."],
  ["refundo", "<big>refundo</big>\n\n<b>re–fundo, fudi, fusum, ere</b>\n<b>1)</b> лить обратно, переливать (aequor in aequor <i>O</i>): refusus oceanus <i>V</i> океан, обратно вливающийся в самого себя, <i>т. е.</i> опоясывающий землю;\n<b>2)</b> отбрасывать, уносить назад: lateri illisa refunditur alga <i>V</i> нанесённая на склон (скалы) морская трава уносится обратно;\n<b>3)</b> отдавать назад, возвращать (aliquid alicui <i>PJ etc.</i>);\n<b>4)</b> <i>перен.</i> перекладывать, сваливать (necem in alicujus consilia <i>Spart</i>);\n<b>5)</b> растоплять, плавить (glaciem <i>PM</i>);\n<b>6)</b> <i>med.-pass.</i> refundi разливаться, хлынуть: palus Acheronte refuso <i>V</i> болото, образованное разлитием Ахеронта; fletu super ora refuso <i>O</i> с лицом, мокрым от слёз;\n<b>7)</b> <i>med.-pass.</i> распростираться, лежать (in spatium immensum <i>Sil</i>)."],
  ["refuscatus", "<big>refuscatus</big>\n\n<b>re–fuscatus, a, um</b>\nзатемнённый, помрачённый, потускневший (luna <i>Eccl</i>)."],
  ["refuse", "<big>refuse</big>\n\n<b>refuse</b> [refundo]\nв рыхлом виде (humus refusius egesta <i>Col</i>)."],
  ["refusio", "<big>refusio</big>\n\n<b>refusio, onis</b> <i>f</i> [refundo]\n<b>1)</b> пролитие (humoris <i>Macr</i>);\n<b>2)</b> возвращение (possessionis <i>Ambr</i>)."],
  ["refusorius", "<big>refusorius</big>\n\n<b>refusorius, a, um</b>\nпредоставляющий льготу (litterae <i>Sid</i>)."],
  ["refusus", "<big>refusus</big>\n\n<b>refusus, a, um</b> <i>part. pf. к</i>\nrefundo."],
  ["refutabilis", "<big>refutabilis</big>\n\n<b>refutabilis, e</b>\n(легко) опровержимый, <i>т. е.</i> неприемлемый <i>Ambr</i>."],
  ["refutatio", "<big>refutatio</big>\n\n<b>refutatio, onis</b> <i>f</i> [refuto]\nопровержение <i>C, Q</i>."],
  ["refutator", "<big>refutator</big>\n\n<b>refutator, oris</b> <i>m</i>\nопровергатель <i>Eccl</i>."],
  ["refutatorius", "<big>refutatorius</big>\n\n<b>refutatorius, a, um</b>\nсодержащий опровержение (preces <i>CJ</i>)."],
  ["refutatus", "<big>refutatus</big>\n\n<b>refutatus, (us) </b> <i>m</i>\nопровержение <i>Lcr</i>."],
  ["refuto", "<big>refuto</big>\n\n<b>refuto, avi, atum, are</b>\n<b>1)</b> отражать, оттеснять (nationes bello <i>C</i>);\n<b>2)</b> опровергать (testem <i>C</i>; aliquid argumentis ac rationibus <i>C</i>): fors dicta refutet! <i>V</i> да не сбудутся эти слова!;\n<b>3)</b> отвергать, презирать (vitam <i>Sl</i>);\n<b>4)</b> подавлять, обуздывать (cupiditatem <i>C</i>)."],
  ["regales", "<big>regales</big>\n\n<b>regales, ium</b> <i>m</i>\nчлены царствующего дома <i>Amm, CTh</i>."],
  ["regalia", "<big>regalia</big>\n\n<b>regalia, ium</b> <i>n</i>\nцарская резиденция <i>Eccl</i>, <i>перен.</i> (<i>о голове, как средоточии души</i>) <i>CA</i>."],
  ["regaliolus", "<big>regaliolus</big>\n\n<b>regaliolus, i</b> <i>m</i> [regalis] <i>предпол.</i>\nкоролёк <i>или</i> крапивник <i>Su</i>."],
  ["regalis", "<big>regalis</big>\n\n<b>regalis, e</b> [rex]\n<b>1)</b> царский, царственный (nomen, potestas, genus <i>C</i>); касающийся царя (scriptum <i>O</i>);\n<b>2)</b> подобающий царю, великолепный, пышный (ornatus <i>C</i>)."],
  ["regaliter", "<big>regaliter</big>\n\n<b>regaliter</b>\nцарственно, по-царски <i>L, O etc.</i>"],
  ["regelo", "<big>regelo</big>\n\n<b>re–gelo, avi, atum, are</b>\nвновь оттаивать, согревать (aliquid manu <i>M</i>); <i>med.-pass.</i> (<i>перен.</i>) оживать (aetas mea media regelatur aestate <i>Sen</i>)."],
  ["regemo", "<big>regemo</big>\n\n<b>re–gemo, —, —, ere</b>\nиздавать вздохи <i>или</i> стоны, вздыхать, стонать в ответ <i>St, Sid</i>."],
  ["regendarius", "<big>regendarius</big>\n\n<b>regendarius, i</b> <i>m</i> [rego]\nрегендарий, государственный секретарь <i>Eccl</i>."],
  ["regeneratio", "<big>regeneratio</big>\n\n<b>regeneratio, onis</b> <i>f</i>\nвозрождение <i>Vlg, Eccl</i>."],
  ["regenero", "<big>regenero</big>\n\n<b>re–genero, avi, atum, are</b>\n<b>1)</b> вновь производить, возрождать, воспроизводить (cicatrices <i>PM</i>);\n<b>2)</b> быть похожим (r. avum <i>PM</i>)."],
  ["regens", "<big>regens</big>\n\n<b>regens, entis</b>\n<b>1.</b> <i>part. praes. к</i> rego;\n<b>2.</b> <i>subst. m</i> правитель, властелин <i>Sen, T, Cld</i>."],
  ["regerminatio", "<big>regerminatio</big>\n\n<b>regerminatio, onis</b> <i>f</i> [regermino]\nпускание новых ростков <i>PM</i>."],
  ["regermino", "<big>regermino</big>\n\n<b>re–germino, —, —, are</b>\nпускать новые ростки, вновь прорастать <i>PM, Calp</i>."],
  ["regero", "<big>regero</big>\n\n<b>re–gero, gessi, gestum, ere</b>\n<b>1)</b> носить назад, доставлять обратно (onera <i>PM</i>); сваливать, сбрасывать назад (tellurem <i>O</i>); выкапывать (regesta e fossa terra <i>L</i>); бросать назад (faces <i>T</i>); отбрасывать, отражать (solis radios <i>PM</i>); наносить, наваливать, насыпать (humum <i>Col</i>; culmos in acervum <i>Col</i>);\n<b>2)</b> давать отпор, опровергать (Stoicos <i>Cassius ap. C</i>); противопоставлять (r. convicia alicui <i>H</i>);\n<b>3)</b> перекладывать, сваливать, переносить (culpam in aliquem <i>PJ</i>);\n<b>4)</b> вносить, регистрировать, записывать (aliquid in commentarios <i>Q</i>)."],
  ["regesta", "<big>regesta</big>\n\n<b>regesta, orum</b> <i>n</i> [regero 4]\nсписки, перечень, реестр <i>Vop, CTh, Eccl</i>."],
  ["regestum", "<big>regestum</big>\n\n<b>regestum, i</b> <i>n</i> [regero]\nземляная насыпь <i>Col</i>."],
  ["regia", "<big>regia</big>\n\n<b>regia, ae</b> <i>f</i> [regius]\n<b>1)</b> (<i>sc.</i> domus) царский дворец, замок (r. Priami senis <i>O</i>); чертог (r. solis <i>O</i>); (<i>в Риме</i>) дворец Нумы (<i>на Via Sacra близ храма Весты</i>) <i>C, O</i>;\n<b>2)</b> (<i>sc.</i> Urbs) столица, царская резиденция (Croesi r. Sardes <i>H</i>);\n<b>3)</b> царская палатка (<i>в лагере</i>) <i>L, QC</i>;\n<b>4)</b> царская фамилия <i>или</i> царский двор <i>L, T, Pt</i>;\n<b>5)</b> царская власть (Persica r. <i>QC</i>);\n<b>6)</b> базилика <i>Su, T</i>;\n<b>7)</b> скотный двор (gregis r. <i>VF</i>)."],
  ["regibilis", "<big>regibilis</big>\n\n<b>regibilis, e</b> [rego]\nпослушный, дисциплинированный (juventus <i>Amm</i>)."],
  ["regie", "<big>regie</big>\n\n<b>regie</b> [regius]\n<b>1)</b> по-царски (accubare <i>Pl</i>); великолепно, пышно (r. polita aedificia <i>Vr</i>);\n<b>2)</b> деспотически (crudeliter et r. <i>C</i>)."],
  ["Regienses", "<big>Regienses</big>\n\n<b>Regienses, ium</b> <i>m</i>\nжители города Regium I <i>C</i>."],
  ["regifice", "<big>regifice</big>\n\n<b>regifice</b> <i>Enn, Sil</i>\n= regie."],
  ["regificus", "<big>regificus</big>\n\n<b>regificus, a, um</b> <i>V, VF, Ambr</i>\n= regalis."],
  ["regifugium", "<big>regifugium</big>\n\n<b>regi–fugium, i</b> <i>n</i>\nбегство царей (<i>праздник изгнания царей, справлявшийся в Риме ежегодно 20 февраля</i>) <i>Aus</i>."],
  ["regigno", "<big>regigno</big>\n\n<b>re–gigno, —, —, ere</b>\nснова рождать, возрождать, воспроизводить, <i>med.-pass.</i> восстанавливаться, отрастать <i>Lcr</i>."],
  ["regii", "<big>regii</big>\n\n<b>regii, orum</b> <i>m</i>\n<b>1)</b> царские войска <i>Nep, L</i>;\n<b>2)</b> придворные, двор <i>Nep</i>."],
  ["Regillanus", "<big>Regillanus</big>\n\n<b>Regillanus, a, um</b> <i>Su</i>\n= Regillensis I, 1."],
  ["Regillensis", "<big>Regillensis</big>\n\n<b>I Regillensis, e</b> [Regillus]\n<b>1)</b> регилланский <i>L</i>;\n<b>2)</b> Региллензий, <i>cognomen в родах Посту миге и Эмилиев</i> <i>C, L</i>.\n\n<b>II Regillensis, is</b> <i>m</i>\nжитель города Regillus <i>L</i>."],
  ["Regillus", "<big>Regillus</big>\n\n<b>I Regillus, i</b> <i>m</i>\nРегилл:\n<b>1)</b> <i>город в области сабинян</i> (<i>откуда Аппий Клавдий переселился в Рим</i>) <i>L</i>;\n<b>2)</b> <i>озеро в Латии, у которого римляне под начальством диктатора Постумия нанесли поражение латинянам</i> (<i>496 г. до н. э.</i>) <i>C, L, PM etc.</i>\n\n<b>II regillus, a, um</b> [rectus]\nс вертикальной основой, <i>по шутл. созвучию</i> царский, пышный (inducula <i>Pl</i>; tunica <i>Vr</i>)."],
  ["regimen", "<big>regimen</big>\n\n<b>regimen, inis</b> <i>n</i> [rego]\n<b>1)</b> управление (navium, equorum <i>T</i>); командование, начальствование (cohortium <i>T</i>); заведование, руководство, управление (omnium rerum <i>L</i>);\n<b>2)</b> кормило, руль (carinae <i>O</i>; navis <i>SenT</i>): r. flectere <i>O</i> управлять рулём;\n<b>3)</b> руководитель, правитель (pecunia una r. est rerum omnium <i>PS</i>): r. rerum <i>L</i> глава государства."],
  ["regimentum", "<big>regimentum</big>\n\n<b>regimentum, i</b> <i>n</i> <i>Dig, Amm</i>\n= regimen."],
  ["regina", "<big>regina</big>\n\n<b>regina, ae</b> <i>f</i> [rex]\n<b>1)</b> царица (r. Dido <i>V</i>; <i>ирон.</i> r. Pecunia <i>H</i>); повелительница: siderum r. bicornis <i>H</i> = Luna;\n<b>2)</b> царевна (virgines reginae <i>QC</i>)."],
  ["Reginus", "<big>Reginus</big>\n\n<b>I Reginus, a, um</b> [Regium]\nрегийский (ager <i>C</i>; litora <i>Sil</i>).\n\n<b>II Reginus, i</b> <i>m</i>\nжитель города Regium <i>C</i>."],
  ["regio", "<big>regio</big>\n\n<b>regio, onis</b> <i>f</i> [rego]\n<b>1)</b> направление, линия (eandem petere regionem <i>Cs</i>): recta regione <i>L</i> по прямой линии; regione occidentis <i>L</i> в западном направлении; e regione прямо, напрямик (moveri <i>C</i>) <i>или</i> напротив, на противоположной стороне (e regione alicujus rei <i>или</i> alicui rei <i>C</i>, <i>Cs</i>);\n<b>2)</b> положение, состояние (natura et r. provinciae <i>C</i>);\n<b>3)</b> пограничная линия, граница, предел, черта, рубеж (regionibus spatium circumscribere <i>C</i>): sese regionibus alicujus rei continere <i>C</i> держаться в рамках чего-л.;\n<b>4)</b> <i>культ.</i> небесная линия (<i>мысленно проводимая авгурами при ауспициях</i>): lituo regiones terminare <i>C</i> проводить (небесную) линию авгурским жезлом;\n<b>5)</b> страна света: r. vespertina <i>H, Vtr</i> запад;\n<b>6)</b> страна, край (r. fertilissima <i>Cs</i>);\n<b>7)</b> область, округ, район (r. Padana <i>H</i>); <i>перен.</i> сфера, круг, участок (definitam habere regionem <i>C</i>);\n<b>8)</b> (<i>в Риме</i>) городской район, квартал (quattuordecim urbis regiones <i>T</i>)."],
  ["Region", "<big>Region</big>\n\n<b>Region, i</b> <i>v. l.</i>\n= Regium."],
  ["regionalis", "<big>regionalis</big>\n\n<b>regionalis, e</b> [regio]\nобластной, поместный (concilium <i>Aug</i>)."],
  ["regionaliter", "<big>regionaliter</big>\n\n<b>regionaliter</b> <i>Ap</i>\n= regionatim."],
  ["regionatim", "<big>regionatim</big>\n\n<b>regionatim</b> [regio]\nпо областям, по округам <i>L, Su</i>."],
  ["regis", "<big>regis</big>\n\n<b>I regis</b> <i>2 л. sg. praes. к</i>\nrego.\n\n<b>II regis</b> <i>gen. sg. к</i>\nrex."],
  ["Regium", "<big>Regium</big>\n\n<b>Regium</b> <i>и</i> <b>Region, i</b> <i>n</i>\nРегий:\n<b>1)</b> <i>город в Gallia Cispadana, между Моденой и Пармой, на Via Aemila</i> (<i>ныне</i> Reggio d'Emilia) <i>C, T</i>;\n<b>2)</b> <i>город в Бруттии, против Мессаны</i> (<i>ныне</i> Reggio) <i>C, Sl, L, T etc.</i>"],
  ["regius", "<big>regius</big>\n\n<b>regius, a, um</b> [rex]\n<b>1)</b> царский (potestas <i>C</i>): civitas regia <i>C</i> монархия; bellum regium <i>C</i> война с царём (<i>Митридатом или Тиграном</i>); morbus r. <i>H, CC</i> желтуха; r. metus <i>L</i> страх перед царской властью; anni regii <i>C</i> период царей (<i>в Риме</i>);\n<b>2)</b> царственный (Juno <i>V</i>); роскошный, пышный (more regio vivere <i>T</i>);\n<b>3)</b> деспотический, тиранический (voluntates <i>Sl</i>)."],
  ["reglesco", "<big>reglesco</big>\n\n<b>re–glesco, —, —, ere</b> [glisco]\nвоспламеняться, разжигаться <i>Pl</i>."],
  ["reglutino", "<big>reglutino</big>\n\n<b>re–glutino, (avi), atum, are</b>\n<b>1)</b> освобождать, выпускать (ab unguibus <i>Ctl</i>);\n<b>2)</b> вновь приращивать (amputatum collum <i>Eccl</i>)."],
  ["regnator", "<big>regnator</big>\n\n<b>regnator, oris</b> <i>m</i> [regno]\nцарь, повелитель, властелин: r. deum (= deorum) <i>V</i> <i>и</i> r. Olympi = Juppiter; eois r. aquis <i>Ap</i> = Ganges."],
  ["regnatrix", "<big>regnatrix</big>\n\n<b>regnatrix, icis</b> <i>f</i>\nправящая (domus <i>T</i>)."],
  ["regnicola", "<big>regnicola</big>\n\n<b>regni–cola, ae</b> <i>m</i> [colo II]\nобитатель царства <i>Aug</i>."],
  ["regno", "<big>regno</big>\n\n<b>regno, avi, atum, are</b>\n<b>1)</b> царствовать, быть царём: Tarquinio regnante <i>T</i> в царствование Тарквиния; r. in aliquem <i>T</i> (<i>реже</i> alicujus <i>H</i>) царствовать над кем-л.; terra regnata Lycurgo <i>V</i> край, где правил Ликург; Gotones regnantur <i>T</i> готоны подвластны царям; regnatum (est) Romae annos ducentos quadraginta quattuor <i>L</i> в Риме царская власть длилась 244 года;\n<b>2)</b> царить, повелевать, господствовать, властвовать, <i>перен.</i> иметь решительное влияние (in equitum centuriis <i>C</i>): vivere et r. <i>погов. H</i> быть полновластным хозяином, пользоваться беспредельной свободой, <i>перен.</i> поступать самовластно, своевольничать (in omnibus oppidis <i>C</i>);\n<b>3)</b> свирепствовать (ignis regnat <i>V</i>; ardor edendi per viscera regnat <i>O</i>)."],
  ["regnum", "<big>regnum</big>\n\n<b>regnum, i</b> <i>n</i> [rex] <i>тж.</i> <i>pl.</i>\n<b>1)</b> царское достоинство, царская власть (regno <i>и</i> regni potiri <i>Just, VP</i>): r. agere <i>L</i> царствовать;\n<b>2)</b> самовластие, тирания (crimen regni <i>O</i>): r. in aliquo exercere <i>L</i> владычествовать над кем-л.;\n<b>3)</b> власть, господство: r. forense <i>или</i> judiciorum <i>C</i> судебная власть; regna vini <i>H</i> председательствование на пирушке;\n<b>4)</b> царство, государство (r. Masinissae <i>Sl</i>): regna tristia <i>O</i>, invia vivis <i>V</i> <i>или</i> viduata lumine <i>Sil</i> царство мёртвых, подземное царство; r. caelorum <i>Eccl</i> царствие небесное;\n<b>5)</b> царствующий дом (Numidiae <i>Sl</i>);\n<b>6)</b> владение, область (uberrima regna <i>V</i>);\n<b>7)</b> (= rex) (regna vetant <i>St</i>)."],
  ["rego", "<big>rego</big>\n\n<b>rego, rexi, rectum, ere</b>\n<b>1)</b> править, управлять (equum <i>L</i>; navem <i>Lcr</i>; rem publicam <i>C</i>): Tiberio regente <i>T</i> в правление Тиберия || руководить (r. juvenem <i>C</i>; disciplinam <i>Su</i>): valetudines alicujus r. <i>T</i> быть чьим-л. врачом;\n<b>2)</b> направлять, бросать в цель (tela <i>V</i>);\n<b>3)</b> определять, устанавливать (fines <i>C, CJ</i>);\n<b>4)</b> давать наставления, указания (r. errantem <i>Cs</i>);\n<b>5)</b> исправлять, воспитывать (mores alicujus <i>O</i>)."],
  ["regradatio", "<big>regradatio</big>\n\n<b>re–gradatio, onis</b> <i>f</i>\n<b>1)</b> понижение в должности <i>или</i> в звании <i>Dig</i>;\n<b>2)</b> <i>астр.</i> обратное движение <i>Aug</i>."],
  ["regrado", "<big>regrado</big>\n\n<b>regrado, —, atum, are</b> [gradus]\nпонижать в должности <i>или</i> в звании (aliquem <i>CJ, Eccl</i>)."],
  ["regredior", "<big>regredior</big>\n\n<b>re–gredior, gressus sum, gredi</b> <i>depon.</i> [gradior]\n<b>1)</b> идти назад, возвращаться (in urbem <i>Su</i>; longinquo ab exsilio <i>T</i>);\n<b>2)</b> отходить назад, отступать <i>Cs, L etc.</i>: in memoriam r. <i>Pl</i> вспоминать;\n<b>3)</b> <i>юр.</i> предъявлять иск по праву регресса (r. ad venditorem <i>Dig</i>)."],
  ["regredo", "<big>regredo</big>\n\n<b>re–gredo, —, —, ere</b> [gradus]\nотводить назад: gradum r. <i>Enn</i> идти назад, отступать."],
  ["regressio", "<big>regressio</big>\n\n<b>regressio, onis</b> <i>f</i> [regredior]\n<b>1)</b> обратное движение, возвращение <i>Ap, Fronto</i>; отход, переход (a temporalibus ad aeterna <i>Aug</i>);\n<b>2)</b> <i>ритор.</i> повторение <i>Q</i>."],
  ["regressus", "<big>regressus</big>\n\n<b>I regressus, a, um</b> <i>part. pf. к</i>\nregredior.\n\n<b>II regressus, us</b> <i>m</i> [regredior]\n<b>1)</b> обратное движение (progressus et r. siderum <i>C</i>); возвращение: dare alicui regressum <i>O</i> позволить кому-л. отступить (возвратиться); regressum non habere <i>V</i> не иметь возврата; r. ab ira <i>T</i> смягчение гнева;\n<b>2)</b> <i>воен.</i> отступление, отход <i>L, T</i>;\n<b>3)</b> обращение за помощью, возможность найти поддержку (est r. ad aliquem <i>T</i>);\n<b>4)</b> <i>юр.</i> регрессное право, регресс (regressum habere in <i>или</i> adversus aliquem <i>Dig</i>)."],
  ["regula", "<big>regula</big>\n\n<b>regula, ae</b> <i>f</i> [rego]\n<b>1)</b> брусок, планка (r. quadrata <i>Cs</i>);\n<b>2)</b> линейка (materia ad regulam et libellam exigitur <i>PM</i>);\n<b>3)</b> норма, критерий, правило, принцип (r. juris <i>C</i>): sub regulam cadere <i>Sen</i> подчиняться правилу."],
  ["regularis", "<big>regularis</big>\n\n<b>regularis, e</b> [regula]\n<b>1)</b> могущий быть отлитым в бруски, брусковый (aes <i>PM</i>);\n<b>2)</b> содержащий наставления (libri <i>CA</i>);\n<b>3)</b> имеющий силу правила, обязательный (sententia <i>Eccl</i>)."],
  ["regulariter", "<big>regulariter</big>\n\n<b>regulariter</b>\nсогласно правилам <i>Dig, Macr, Aug</i>."],
  ["regulatim", "<big>regulatim</big>\n\n<b>regulatim</b> <i>Veg</i>\n= regulariter."],
  ["regulo", "<big>regulo</big>\n\n<b>regulo, —, —, are</b>\nнаправлять, упорядочивать (r. sive dirigere aliquid <i>CA</i>)."],
  ["regulus", "<big>regulus</big>\n\n<b>I regulus, i</b> <i>m</i> [<i>demin. к</i> rex]\n<b>1)</b> царь небольшого государства, царёк <i>L</i>;\n<b>2)</b> сын царя, царевич, принц <i>Sl, L</i>;\n<b>3)</b> пчелиная матка <i>Vr</i>;\n<b>4)</b> <i>птица</i> королёк <i>Eccl</i>;\n<b>5)</b> <i>змея</i> василиск <i>Vlg, Hier</i>.\n\n<b>II Regulus, i</b> <i>m</i>\nРегул, <i>римск. cognomen; наиболее известны</i>:\n<b>1)</b> M. Atilius R., <i>консул в 269 и 256 гг. до н. э.; в 255 г. потерпел поражение от карфагенян и был взят ими в плен</i> <i>C, Sen</i>;\n<b>2)</b> L. Livinejus R., <i>легат Цезаря во время Африканской войны</i> <i>C</i>."],
  ["regusto", "<big>regusto</big>\n\n<b>re–gusto, avi, atum, are</b>\n<b>1)</b> снова отведывать (aliquid <i>Sen, Pers</i>);\n<b>2)</b> <i>перен.</i> снова вкушать: r. litteras <i>C</i> снова читать (перечитывать) письмо."],
  ["regyro", "<big>regyro</big>\n\n<b>regyro, avi, —, are</b> [re + gyrus]\n(описав круг) снова возвращаться: bellum in Hispaniam regyravit <i>Fl</i> война вернулась в Испанию."],
  ["rei", "<big>rei</big>\n\n<b>rei</b> <i>gen., dat. sg. к</i>\nres."],
  ["reicio", "<big>reicio</big>\n\n<b>reicio</b> <i>v. l.</i>\n= rejicio."],
  ["reiculus", "<big>reiculus</big>\n\n<b>reiculus, a, um</b> <i>v. l.</i>\n= rejiculus."],
  ["reintegro", "<big>reintegro</big>\n\n<b>reintegro</b> <i>v. l.</i>\n= redintegro."],
  ["reinvito", "<big>reinvito</big>\n\n<b>re–invito, —, —, are</b>\nпригласить в свою очередь (aliquem <i>Vlg</i>)."],
  ["reiteratio", "<big>reiteratio</big>\n\n<b>reiteratio, onis</b> <i>f</i> [reitero]\nповторение <i>Q</i> (<i>v. l.</i>)."],
  ["reitero", "<big>reitero</big>\n\n<b>re–itero, —, —, are</b>\nповторять (aliquid <i>Ap</i>)."],
  ["rejecta", "<big>rejecta</big>\n\n<b>rejecta, orum</b> <i>n</i> <i>C</i>\n= rejectanea."],
  ["rejectanea", "<big>rejectanea</big>\n\n<b>rejectanea, orum</b> <i>n</i> [rejicio] (<i>греч.</i> apoproegmana) (<i>тж.</i> rejecta <i>и</i> rejicienda)\n<i>в философии стоиков</i> вещи, не являющиеся злом, но лишённые значения и не достойные внимания <i>C</i>."],
  ["rejectio", "<big>rejectio</big>\n\n<b>rejectio, onis</b> <i>f</i> [rejicio]\n<b>1)</b> выбрасывание, извержение: r. sanguinis <i>PM</i> кровохарканье;\n<b>2)</b> непринятие, отклонение, отвержение: r. ci vitatis <i>C</i> отказ от гражданства;\n<b>3)</b> <i>юр.</i> отвод (judicum <i>C</i>)."],
  ["rejecto", "<big>rejecto</big>\n\n<b>rejecto, —, —, are</b> [<i>intens. к</i> rejicio]\n<b>1)</b> отбрасывать, отражать (montes rejectant voces ad sidera <i>Lcr</i>);\n<b>2)</b> отдавать, выпускать (praedam <i>Sil</i>);\n<b>3)</b> извергать (в виде рвоты) (aliquid <i>Spart</i>)."],
  ["rejectus", "<big>rejectus</big>\n\n<b>rejectus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> rejicio;\n<b>2.</b> <i>adj.</i> негодный, неприемлемый <i>C</i>."],
  ["rejiciendus", "<big>rejiciendus</big>\n\n<b>rejiciendus, a, um</b>\n<b>1.</b> <i>part. fut. к</i> rejicio;\n<b>2.</b> <i>adj.</i> негодный, неприемлемый <i>C</i>."],
  ["rejicio", "<big>rejicio</big>\n\n<b>re–jicio, jeci, jectum, ere</b> [re + jacio]\n<b>1)</b> бросать назад, обратно (lapides in hostem <i>Cs</i>); закидывать назад (vestem de corpore <i>O</i>; r. manus ad <i>или</i> post tergum <i>C, M</i>); сбрасывать (amictum ex umeris <i>V</i>): fatigata membra r. <i>QC</i> опуститься в изнеможении || <i>med.-pass.</i> rejici <i>или</i> se r. бросаться (se in alicujus gremium <i>Lcr</i>; in grabatum <i>Pt</i>);\n<b>2)</b> извергать (sanguinem ore <i>PM</i>; vinum <i>Su</i>);\n<b>3)</b> гнать назад, отгонять (capellas a flumine <i>V</i>);\n<b>4)</b> отбрасывать, отражать (copias in oppidum <i>Cs</i>; hostem ab Antiochia <i>C</i>); давать отпор (r. audaciam alicujus <i>C</i>);\n<b>5)</b> относить (naves tempestate rejectae <i>Cs</i>);\n<b>6)</b> отвращать, отворачивать (oculos <i>V</i>);\n<b>7)</b> отклонять, отвлекать (aliquem a libris <i>C</i>);\n<b>8)</b> отказываться, отклонять от себя, не принимать, отвергать (donum, munus, disputationem <i>C</i>); <i>юр.</i> делать отвод, отводить (judices <i>C</i>);\n<b>9)</b> отправлять, отсылать (aliquem in illum locum <i>C</i>; r. militem in postremam aciem <i>L</i>; aliquem ad ipsam epistulam <i>C</i>);\n<b>10)</b> возлагать, поручать, переносить (rem ad novos consules <i>L</i>);\n<b>11)</b> направлять: legatos ad senatum r. <i>L</i> предложить послам обратиться к сенату; huc se r. <i>Balbus ap. C</i> вплотную заняться этим вопросом;\n<b>12)</b> откладывать, отсрочивать (aliquid in aliud tempus <i>C</i>)."],
  ["rejiculus", "<big>rejiculus</big>\n\n<b>rejiculus, a, um</b> [rejicio]\nбесполезный, негодный (oves <i>Vr</i>); проведённый без пользы, потерянный (dies <i>Sen</i>)."],
  ["relabor", "<big>relabor</big>\n\n<b>re–labor, lapsus sum, labi</b> <i>depon.</i>\n<b>1)</b> падать назад, скользить, скатываться (prenso rudente <i>O</i>); плыть, течь обратно (unda relabens <i>V</i>; r. verso vento <i>O</i>);\n<b>2)</b> мало-помалу возвращаться (mens relabitur <i>H</i>);\n<b>3)</b> исчезать (Eurydice relapsa est <i>O</i>)."],
  ["relanguesco", "<big>relanguesco</big>\n\n<b>re–languesco, gui, —, ere</b>\n<b>1)</b> ослабевать, изнемогать (moribunda relanguit <i>O</i>); утихать (venti relanguescunt <i>Sen</i>); тускнеть (stella relanguescit <i>PM</i>); становиться слабее, уменьшаться (relanguit ardor <i>O</i>);\n<b>2)</b> падать, развращаться (animi relanguescunt <i>Cs</i>)."],
  ["relatio", "<big>relatio</big>\n\n<b>relatio, onis</b> <i>f</i> [refero]\n<b>1)</b> несение назад, отнесение: crebra relatione morari manum <i>Q</i> частым движением (пера к чернильнице) замедлять руку (<i>т. е.</i> процесс письма);\n<b>2)</b> перекладывание, перенесение (criminis <i>C</i>);\n<b>3)</b> возмещение, вознаграждение, воздаяние (r. gratiae <i>Sen</i>);\n<b>4)</b> <i>ритор.</i> частое повторение, анафора <i>C, Q</i>;\n<b>5)</b> сообщение, доклад, предложение (в сенате), предмет доклада (approbare relationem alicujus <i>L</i>): relationem egredi <i>T</i> отклоняться от темы доклада;\n<b>6)</b> донесение, передача, рассказ (r. rerum gestarum <i>Just</i>);\n<b>7)</b> <i>филос.</i> отношение (ad aliquid <i>Q</i>)."],
  ["relative", "<big>relative</big>\n\n<b>relative</b>\nсоотносительно, соответственно (vicinus et amicus r. dicuntur <i>Aug</i>)."],
  ["relativus", "<big>relativus</big>\n\n<b>relativus, a, um</b> [refero]\n(со)относительный (appellatio, <i>sc.</i> domini et servi <i>Aug</i>)."],
  ["relator", "<big>relator</big>\n\n<b>relator, oris</b> <i>m</i> [refero]\n<b>1)</b> вносящий предложение, делающий доклад, докладчик <i>Balbus ap. C</i>;\n<b>2)</b> рассказчик <i>Sid</i>."],
  ["relatoria", "<big>relatoria</big>\n\n<b>relatoria, ae</b> <i>f</i> [refero]\nрасписка в получении <i>CTh</i>."],
  ["relatus", "<big>relatus</big>\n\n<b>I relatus, a, um</b> <i>part. pf. к</i>\nrefero.\n\n<b>II relatus, us</b> <i>m</i> [refero]\n<b>1)</b> сообщение, донесение, доклад (de aliqua re <i>или</i> alicujus rei <i>T</i>);\n<b>2)</b> рассказ <i>Sen, T</i>."],
  ["relaxatio", "<big>relaxatio</big>\n\n<b>relaxatio, onis</b> <i>f</i> [relaxo]\nослабление, уменьшение (doloris <i>C</i>); разрядка, отдых (non contentio animi, sed r. <i>C</i>)."],
  ["relaxator", "<big>relaxator</big>\n\n<b>relaxator, oris</b> <i>m</i>\nдающий выход, высвобождающий (r. fluentium <i>CA</i>)."],
  ["relaxo", "<big>relaxo</big>\n\n<b>re–laxo, avi, atum, are</b>\n<b>1)</b> ослаблять, распускать, развязывать (vincula <i>O</i>; nodos <i>Lcr</i>); отпускать (arcum <i>SenT</i>); снимать <i>или</i> отпирать (claustra <i>O, Pt</i>);\n<b>2)</b> разбивать, разрыхлять (glebas <i>Vr</i>); разрежать (densa <i>V</i>); <i>med.-pass.</i> становиться дряблым (cutis relaxatur <i>CC</i>) <i>или</i> расслабляться (alvus relaxatur <i>C</i>);\n<b>3)</b> расширять (ora fontibus <i>O</i>); открывать, прокладывать (vias <i>V</i>);\n<b>4)</b> смягчать, умерять, уменьшать (tristitiam ac severitatem <i>C</i>): relaxato in hilaritatem vultu <i>Pt</i> с повеселевшим лицом;\n<b>5)</b> уменьшаться, униматься, утихать, убывать (dolor relaxat <i>C</i>): r. se (<i>или</i> animum) <i>и</i> relaxari animo перевести дух, отдохнуть, освежиться (somno <i>C</i>; ex sermone <i>C</i>; occupationibus <i>C</i>);\n<b>6)</b> расшатывать, портить (mores <i>Cld</i>)."],
  ["relectus", "<big>relectus</big>\n\n<b>relectus, a, um</b> <i>part. pf. к</i>\nrelego II."],
  ["relegatio", "<big>relegatio</big>\n\n<b>relegatio, onis</b> <i>f</i> [relego I]\n<b>1)</b> удаление, ссылка, изгнание (<i>с сохранением прав римского гражданина</i>) (r. atque amandatio <i>C</i>);\n<b>2)</b> <i>юр.</i> завещание (r. dotis <i>Dig</i>)."],
  ["relego", "<big>relego</big>\n\n<b>I re–lego, avi, atum, are</b>\n<b>1)</b> отправлять, удалять (filium rus <i>C</i>; aliquem in vilicationem <i>Pt</i>): terris (<i>dat.</i>) gens relegata ultimis <i>C</i> племя, обитающее в отдаленнейшем краю;\n<b>2)</b> ссылать, изгонять (aliquem in exsilium <i>L</i>): relegatus, non exsul <i>O</i> изгнанник, но не лишённый гражданских прав;\n<b>3)</b> отклонять, отвергать (dona <i>C</i>; verba alicujus <i>O</i>): ambitione relegata <i>H</i> — <i>см.</i> ambitio 4;\n<b>4)</b> отсылать, ссылаться (ad auctores <i>PM</i>);\n<b>5)</b> относить, приписывать (rationem rectae viae ad philosophus <i>Q</i>; omnia maia ad crimen fortunae <i>Q</i>);\n<b>6)</b> переносить, перекладывать, сваливать (culpam in aliquem <i>Q</i>);\n<b>7)</b> <i>юр.</i> завещать (dotem <i>Dig</i>).\n\n<b>II re–lego, legi, lectum, ere</b>\n<b>1)</b> вновь собирать, обратно наматывать (filum <i>O</i>): abies docilis relegi <i>VF</i> копьё, которое легко выдёргивается обратно;\n<b>2)</b> вновь посещать, снова проезжать (aquas Hellespontiacas <i>O</i>): retrorsus litora r. <i>V</i> проплывать берега в обратном направлении;\n<b>3)</b> вновь читать, перечитывать (scripta <i>O</i>; scriptorem <i>H</i>);\n<b>4)</b> снова обсуждать, опять обдумывать (aliquid sermone r. <i>O</i>)."],
  ["relentesco", "<big>relentesco</big>\n\n<b>re–lentesco, —, —, ere</b>\nвновь убывать, опять ослабевать, уменьшаться (amor relentescit <i>O</i>)."],
  ["relevatio", "<big>relevatio</big>\n\n<b>relevatio, onis</b> <i>f</i> [relevo]\n<b>1)</b> облегчение (animi atque corporis <i>CA</i>);\n<b>2)</b> накипь, помутнение <i>или</i> пена (urina nullam facit relevationem <i>CA</i>)."],
  ["relevi", "<big>relevi</big>\n\n<b>relevi</b> <i>pf. к</i>\nrelino."],
  ["relevo", "<big>relevo</big>\n\n<b>re–levo, avi, atum, are</b>\n<b>1)</b> поднимать, приподнимать: r. corpus e terra <i>O</i> подняться с земли; r. membra in cubitum <i>O</i> облокотиться;\n<b>2)</b> облегчать, разгружать: r. vimina curva <i>O</i> облегчить (от мёда) ульи;\n<b>3)</b> успокаивать, умерять, уменьшать (morbum <i>C</i>; laborem <i>PJ</i>): r. gradum <i>Ap</i> замедлять ход || утолять (famem, sitim <i>O</i>);\n<b>4)</b> утешать, ободрять (dolentem <i>C</i>); давать отдохнуть: r. membra sedili <i>O</i> сесть и дать отдых телу || помочь оправиться, освежить (pectora mero <i>O</i>); <i>med.-pass.</i> relevari оправиться (gravi morbo <i>C</i>; metu <i>C</i>); отдохнуть (cura <i>C</i>);\n<b>5)</b> освобождать (aliquem aliqua re <i>Su</i>)."],
  ["relicinus", "<big>relicinus</big>\n\n<b>re–licinus, a, um</b> [licium]\n<b>1)</b> с локонами, ниспадающими назад (Marsyas coma r. <i>Ap</i>);\n<b>2)</b> с закинутыми назад волосами, <i>т. е.</i> открытый (frons <i>Ap</i>)."],
  ["relictio", "<big>relictio</big>\n\n<b>relictio, onis</b> <i>f</i> [relinquo]\n<b>1)</b> оставление (без помощи) (r. proditioque <i>C</i>);\n<b>2)</b> отделение, обособление (argenti vivi <i>Vtr</i>)."],
  ["relictor", "<big>relictor</big>\n\n<b>relictor, oris</b> <i>m</i> [relinquo]\nоставляющий без оказания помощи (voluntatum malarum r. et damnator <i>Aug</i>)."],
  ["relictum", "<big>relictum</big>\n\n<b>relictum, i</b> <i>n</i> [relinquo]\nостаток <i>O</i>."],
  ["relictus", "<big>relictus</big>\n\n<b>I relictus</b> <i>part. pf. к</i>\nrelinquo.\n\n<b>II relictus, us</b> <i>m</i> <i>AG</i>\n= relictio 1."],
  ["relicuus", "<big>relicuus</big>\n\n<b>relicuus (relicus), a, um</b> <i>Pl, Lcr, C</i>\n= reliquus."],
  ["relido", "<big>relido</big>\n\n<b>re–līdo, līsī, līsum, ere</b> [laedo]\n<b>1)</b> отбрасывать, отталкивать (aestum in italiam <i>Sl</i>);\n<b>2)</b> разбивать (frontem <i>Eccl</i>);\n<b>3)</b> ударять (r. alapas <i>Eccl</i>);\n<b>4)</b> отвергать (aliquid <i>Aus</i>)."],
  ["religamen", "<big>religamen</big>\n\n<b>religāmen, inis</b> <i>n</i> [religo]\nсвязь <i>Eccl</i>."],
  ["religatio", "<big>religatio</big>\n\n<b>religātio, ōnis</b> <i>f</i> [religo]\nпривязывание, подвязывание (vitium <i>C</i>)."],
  ["religens", "<big>religens</big>\n\n<b>religēns, entis</b> <i>adj.</i> [religio]\nбогобоязненный, набожный <i>AG</i>."],
  ["religio", "<big>religio</big>\n\n<b>religio, ōnis</b> <i>f</i> [religo]\n<b>1)</b> совестливость, добросовестность, благочестие (antiqua <i>C</i>): r, vitae <i>C</i> благочестивая жизнь;\n<b>2)</b> благоговение, благоговейное отношение, религиозное чувство, набожность: r. deorum <i>C</i> благоговение перед богами; summa religione caerimoniaque <i>C</i> с величайшей и благоговейной торжественностью;\n<b>3)</b> религия (r. id est cultus deorum <i>C</i>);\n<b>4)</b> <i>тж. pl.</i> богослужение, богослужебные обряды (religiones publicae <i>C</i>; religiones nocturnae <i>Pt</i>);\n<b>5)</b> святость (templi <i>T</i>; juris jurandi, societatis <i>C</i>): obstringere aliquem religione <i>C</i> торжественно (клятвенно) обязать кого-л.;\n<b>6)</b> святыня, священный предмет, предмет культа (violare religiones <i>C</i>);\n<b>7)</b> сомнение, недоумение, (моральное) стеснение <i>или</i> опасение, неуверенность: oblata est ei r. <i>C</i> его взяло раздумье (он стал терзаться сомнениями); habere aliquid religioni (in religione) <i>C</i> <i>или</i> trahere aliquid in religionem <i>L</i> питать сомнения относительно чего-л.; afferre (offerre, incutere, injicere) religionem alicui <i>C etc.</i> поселить в ком-л. недоумение, внушить кому-л. сомнение; r. mihi est dicere <i>C</i> мне совестно сказать; nulla mihi r. est <i>H</i> мне (до этого) дела нет;\n<b>8)</b> преступление против совести, прегрешение, грех, вина (r. inexpiabilis <i>C</i>; liberare <i>или</i> solvere aliquem religione <i>L</i>);\n<b>9)</b> (<i>тж.</i> r. perversa, prava <i>C</i> <i>или</i> impia <i>L</i>) суеверие (novas sibi religiones fingere <i>Cs</i>; tantum r. (rē-!) potuit suadere malorum! <i>Lcr</i>);\n<b>10)</b> добросовестность, тщательность, точность (r. testimoniorum <i>C</i>; vir summa religione et fide <i>C</i>): sine ulla religione <i>C</i> без зазрения совести; r. dicendi <i>Ap</i> осмотрительность в словесной формулировке;\n<b>11)</b> вещий знак, знамение (auspiciis et religionibus inductus <i>bAl</i>)."],
  ["religiose", "<big>religiose</big>\n\n<b>religiōsē</b> [religiosus]\n<b>1)</b> добросовестно, по совести (testimonium dicere <i>C</i>; aliquid administrare <i>Col</i>); тщательно (rem rusticam colere <i>Col</i>);\n<b>2)</b> набожно, благочестиво, благоговейно (deos colere <i>L</i> <i>и</i> venerari <i>Ap</i>; aliquem humare <i>Ap</i>)."],
  ["religiositas", "<big>religiositas</big>\n\n<b>religiōsitās, ātis</b> <i>f</i>\nнабожность, благочестие, религиозность <i>Ap, Eccl</i>."],
  ["religiosulus", "<big>religiosulus</big>\n\n<b>religiōsulus, a, um</b> [<i>demin. к</i> religiosus]\nНемного благочестивый <i>Hier</i>."],
  ["religiosus", "<big>religiosus</big>\n\n<b>religiōsus, a, um</b>\n<b>1)</b> внушающий сомнения <i>или</i> тревогу : dies r. <i>LM, C, L, Su etc.</i> день дурных предзнаменований;\n<b>2)</b> испытывающий опасения, подверженный религиозному страху (agricola <i>Col</i>);\n<b>3)</b> тщательный, осмотрительный, добросовестный (testis, judex <i>C</i>);\n<b>4)</b> благочестивый, набожный, богобоязненный (homo, mores <i>C</i>): religiosum erat <i>L</i> (это) было религиозным обрядом; religiosissimis jurare verbis <i>Pt</i> клясться именем всего святого;\n<b>5)</b> религиозный, благоговейный (modus Phrygius <i>Ap</i>);\n<b>6)</b> святой, священный (templum, loca, Ceres <i>C</i>; est jus jurandum affirmatio religiosa <i>C</i>);\n<b>7)</b> суеверный (religentem esse oportet, religiosum est nefas <i>Poëta ap. AG</i>)."],
  ["religo", "<big>religo</big>\n\n<b>re–ligo, āvī, ātum, āre</b>\n<b>1)</b> связывать сзади (manus post tergum <i>VP, QC</i>); привязывать сзади (Hector religatus ad currum Achillis <i>C</i>);\n<b>2)</b> заплетать, убирать (flavam comam <i>H</i>): comam nodo r. <i>H</i> завязать волосы узлом;\n<b>3)</b> обвязывать, обвивать (hedera crines <i>H</i>);\n<b>4)</b> запрягать, впрягать (equos <i>V</i>);\n<b>5)</b> привязывать (retinacula mulae saxo <i>H</i>); захватывать (naves ferreis manibus injectis <i>Cs</i>);\n<b>6)</b> отвязывать, развязывать, снимать (juga manu <i>Ctl</i>)."],
  ["relino", "<big>relino</big>\n\n<b>re–lino, levi, litum, ere</b>\nснимать осмолку, вскрывать, распечатывать (dolia <i>Ter</i>); вынимать, извлекать (melia thesauris <i>V</i>)."],
  ["relinquo", "<big>relinquo</big>\n\n<b>re–linquo, liqui, lictum, ere</b>\n<b>1)</b> оставлять (aliquem in Gallia <i>Cs</i>; f. aliquem heredem <i>C</i>; milites praesidio castris <i>Cs</i>; sibi Spatium ad cognoscendum <i>Cs</i>; acrem sui memoriam <i>T</i>): r. equos <i>Cs</i> сойти с коней; r. arma <i>L</i> сложить оружие; r. signa <i>L</i> дезертировать; f. aliquem poenae <i>O, PJ</i> обрекать кого-л. на кару; r. urbeiti direptioni <i>C</i> отдать город на разграбление; r. aliquid in medie <i>C, Q</i> оставить что-л. нерешённым; aliquem in aliqua re procul se r. <i>Q</i> оставить кого-л. далеко Позади себя (опередить) в чём-л.; nihil inexpertum r. <i>V, QC</i> испробовать всё; <i>med.-pass.</i> оставаться (in Graecia; una spes relinquitur <i>C</i>);\n<b>2)</b> Предоставлять, разрешать (aliquem aliquid facere <i>H</i>);\n<b>3)</b> бросать (rem inchoatam <i>C</i>);» покидать (aliquem aegrum <i>Cs</i>; domum propinquosque <i>Cs</i>; miseros prudentia prima relinquit <i>O</i>): r. totam spem alicujus rei <i>C</i> оставить всякую надежду на что-л.; relinquit animus Sextium <i>Cs</i> Секстйй лишается чувств; vitam <i>V</i> (lucem <i>V</i>, animam <i>Ter</i>) r. испустить дух, умереть; r. obsidionem <i>L</i> снять осаду; aliquid pro relicto habere <i>C</i> считать что-л. делом прошлым (конченным); relictis rebus omnibus <i>Pl, C, Cs</i> отбросив (оставив без внимания) все остальные дела; quod fuit ante, relictum est <i>O</i> что было прежде, того уж нет; jus suum r. <i>C</i> пренебречь своим правом; agrum r. <i>Vr</i> оставлять поле под паром;\n<b>4)</b> обходить молчанием, не упоминать (crimen <i>C</i>)."],
  ["reliquatio", "<big>reliquatio</big>\n\n<b>reliquatio, onis</b> <i>f</i> [reliquor]\nнедоплата, Недоимка, остаток задолженности <i>Dig</i>: r. aetatis <i>Tert</i> недостающее число Лет."],
  ["reliquator", "<big>reliquator</big>\n\n<b>reliquator, oris</b> <i>m</i>\nнедоимщик <i>или</i> неплательщик <i>Dig, Eccl</i>."],
  ["reliquatrix", "<big>reliquatrix</big>\n\n<b>reliquatrix, icis</b> <i>f</i>\nнедоимщица: r. peccatorum <i>Tert</i> не искупившая еще свои грехи."],
  ["reliqui", "<big>reliqui</big>\n\n<b>I reliqui</b> <i>gen. sg. и nom. pl. к</i>\nreliquus.\n\n<b>II reliqui</b> <i>pf. к</i>\nrelinquo."],
  ["reliquia", "<big>reliquia</big>\n\n<b>reliquia, ae</b> <i>f</i> <i>Ap</i>\n= reliquiae."],
  ["reliquiae", "<big>reliquiae</big>\n\n<b>reliquiae, arum</b> <i>f</i> [relinquo]\n<b>1)</b> остатки (cenarum <i>Ap</i>); оставшаяся часть, уцелевший остаток (copiarum <i>Nep</i>; exercitus <i>bAfr</i>); развалины (r. oppidorum <i>V</i>); обломки (navigii <i>Sen</i>); останки, прах (alicujus <i>C etc.</i>): r. cibi <i>C</i> <i>и</i> r. <i>Sen</i> экскременты;\n<b>2)</b> наследие: reliquias avi persequi <i>C</i> продолжать то, что дед оставил неоконченным;\n<b>3)</b> <i>культ.</i> реликвии, мощи <i>Eccl</i>."],
  ["reliquo", "<big>reliquo</big>\n\n<b>reliquo, —, —, are</b> <i>Dig</i>\n= reliquor."],
  ["reliquor", "<big>reliquor</big>\n\n<b>reliquor, atus sum, ari</b>\nиметь задолженность (r. amplam pecuniam <i>Dig</i>); быть в долгу (r. alicui <i>Dig</i>)."],
  ["reliquum", "<big>reliquum</big>\n\n<b>reliquum, i</b> <i>n</i>\n<b>1)</b> остальное, остальная часть, остаток (vitae <i>C</i>; temporis <i>Su</i>): reliquo noctis <i>T</i> в остальную часть ночи; nihil ad celeritatem sibi reliqui fecisse <i>Cs</i> приложить все усилия для достижения (необходимой) быстроты; in r. <i>Sl</i> впредь;\n<b>2)</b> неуплаченный остаток, Недоимка (r. accipere <i>C</i>; persolvere <i>Pl</i>)."],
  ["reliquus", "<big>reliquus</big>\n\n<b>reliquus, a, um</b> [relinquo]\n<b>1)</b> остальной, оставшийся, остающийся (spes <i>C</i>): reliquum est (ut) <i>C, Sl</i> Остаётся ещё; nihil reliqui facere <i>C etc.</i> ничего не оставить <i>или</i> сделать всё, ничего не упустить;\n<b>2)</b> неуплаченный, недоимочный (pecunia <i>C</i>);\n<b>3)</b> прочий, остальной (Alexandrea reliquaque Aegyptus <i>C</i>);\n<b>4)</b> будущий, последующий (tempus <i>Nep</i>);\n<b>5)</b> прежний <i>или</i> обычный (insolentia <i>Pt</i>). — <i>См. тж.</i> reliquum."],
  ["relisus", "<big>relisus</big>\n\n<b>relisus, a, um</b> <i>part. pf. к</i>\nrel ido."],
  ["relitus", "<big>relitus</big>\n\n<b>relitus, a, um</b> <i>part. pf. к</i>\nrelino."],
  ["rellatus", "<big>rellatus</big>\n\n<b>rellatus</b> <i>Ter, Lcr</i>\n= relatus I."],
  ["rellig-", "<big>rellig-</big>\n\n<b>rellig-</b> <i>v. l.</i>\n= relig-."],
  ["relliq-", "<big>relliq-</big>\n\n<b>relliq-</b> <i>v. l.</i>\n= reliq-."],
  ["reloco", "<big>reloco</big>\n\n<b>re–loco, —, —, are</b>\n<b>1)</b> ставить на место, вправлять (articulum <i>CA</i>);\n<b>2)</b> вновь арендовать (aliquid <i>Dig</i>)."],
  ["reloquus", "<big>reloquus</big>\n\n<b>reloquus, a, um</b> [re + loquor]\nговорящий в ответ, отвечающий <i>Vr</i>."],
  ["reluceo", "<big>reluceo</big>\n\n<b>re–luceo, luxi, —, ere</b>\n<b>1)</b> отражать свет, давать отблеск, отсвечивать, сиять, светить (freta igni relucent <i>V</i>; incendia relucebant <i>PM</i>);\n<b>2)</b> вспыхивать, загораться (barba reluxit alicui <i>V</i>)."],
  ["relucesco", "<big>relucesco</big>\n\n<b>relucesco, luxi, —, ere</b> [<i>inchoat. к</i> reluceo]\nначинать сиять, становиться светлым, заблистать (solis imago reluxit <i>O</i>): caeco reluxit dies <i>T</i> слепец прозрел."],
  ["reluctanter", "<big>reluctanter</big>\n\n<b>reluctanter</b>\nпротиводействуя, сопротивляясь <i>Aug</i>."],
  ["reluctatio", "<big>reluctatio</big>\n\n<b>reluctatio, onis</b> <i>f</i>\nсопротивление, противодействие <i>CTh, Eccl</i>."],
  ["relucto", "<big>relucto</big>\n\n<b>relucto, —, —, are</b> <i>Ap, Cld</i>\n= reluctor."],
  ["reluctor", "<big>reluctor</big>\n\n<b>re–luctor, itus sum, ari</b> <i>depon.</i>\nпротивоборствовать, сопротивляться, противодействовать, противиться (alicui <i>QC</i>): reluctante natura <i>Sen</i> вопреки природе; reluctatus invitusque revertens <i>VP</i> крайне неохотно вернувшись."],
  ["reludo", "<big>reludo</big>\n\n<b>re–ludo, lusi, —, ere</b>\n<b>1)</b> отбрасывать (брошенный) мяч (Sibi r., <i>sc.</i> pilas <i>Man</i>);\n<b>2)</b> шутить в ответ, отшучиваться (jocos alicujus r. <i>Sen</i>)."],
  ["relumino", "<big>relumino</big>\n\n<b>re–lumino, —, —, are</b>\nвновь озарять, <i>перен.</i> вновь одарять зрением (caecos <i>Tert</i>)."],
  ["reluo", "<big>reluo</big>\n\n<b>re–luo, —, —, ere</b>\nобратно выкупать (aliquid <i>Caec</i>)."],
  ["remacresco", "<big>remacresco</big>\n\n<b>re–macresco, macrui, —, ere</b>\nснова худеть <i>Su</i>."],
  ["remaledico", "<big>remaledico</big>\n\n<b>re–maledico, —, —, ere</b>\nотвечать на брань бранью <i>Su, Eccl</i>."],
  ["remancipo", "<big>remancipo</big>\n\n<b>re–mancipo, —, atum, are</b>\nвновь передавать в собственность (aliquid <i>G</i>)."],
  ["remando", "<big>remando</big>\n\n<b>I re–mando, (avi), atum, are</b>\nотсылать ответ, сообщать в ответ, отвечать (alicui <i>Eutr, Eccl</i>).\n\n<b>II re–mando, —, —, ere</b>\nпережёвывать (eundem cibum <i>Q</i>)."],
  ["remaneo", "<big>remaneo</big>\n\n<b>re–maneo, mansi, mansum, ere</b>\nоставаться (domi <i>C</i>; apud aliquem <i>Cs</i>; in vita <i>C</i>); сохраняться, пребывать (post mortem <i>C</i>)."],
  ["remano", "<big>remano</big>\n\n<b>re–mano, —, —, are</b>\nтечь назад (retro <i>Lcr</i>)."],
  ["remansi", "<big>remansi</big>\n\n<b>remansi</b> <i>pf. к</i>\nremaneo."],
  ["remansio", "<big>remansio</big>\n\n<b>re–mansio, onis</b> <i>f</i>\nпребывание на одном и том же (прежнем) месте <i>C</i>."],
  ["remansor", "<big>remansor</big>\n\n<b>remansor, oris</b> <i>m</i> [remaneo]\nсолдат, не вернувшийся в срок из отпуска <i>Dig</i>."],
  ["remeabilis", "<big>remeabilis</big>\n\n<b>remeabilis, e</b> [remeo]\nвозвращающийся (скатывающийся) назад (saxum Sisyphi <i>St</i>; anima in corpora r. <i>Tert</i>)."],
  ["remeaculum", "<big>remeaculum</big>\n\n<b>remeaculum, i</b> <i>n</i> [remeo]\nобратный путь, возвращение: demeacula et remeacula <i>Ap</i> спуск и возвращение (Proserpinae)."],
  ["remeatus", "<big>remeatus</big>\n\n<b>remeatus, us</b> <i>m</i> [remeo]\nвозвращение (remeatum dare Exsuli <i>Dig</i>)."],
  ["remediabilis", "<big>remediabilis</big>\n\n<b>remediabilis, e</b> [remedium]\n<b>1)</b> излечимый, исцелимый (livi cura <i>Sen</i>);\n<b>2)</b> целебный, спасительный (bonum <i>Eccl</i>)."],
  ["remedialis", "<big>remedialis</big>\n\n<b>remedialis, e</b> <i>Macr, Eccl</i>\n= remediabilis 2."],
  ["remediatio", "<big>remediatio</big>\n\n<b>remediatio, onis</b> <i>f</i>\n(из)лечение <i>Scr</i>."],
  ["remediator", "<big>remediator</big>\n\n<b>remediator, oris</b> <i>m</i>\nцелитель <i>Tert</i>."],
  ["remedio", "<big>remedio</big>\n\n<b>remedio, avi, atum, are</b>\nлечить, исцелять (aliquem <i>и</i> aliquid <i>Scr, Tert</i>)."],
  ["remedior", "<big>remedior</big>\n\n<b>remedior, —, ari</b> <i>depon.</i> [remedium]\nлечить, излечивать (alicui <i>Hier</i>)."],
  ["remedium", "<big>remedium</big>\n\n<b>remedium, i</b> <i>n</i> [re + medeor]\nлекарство, средство: r. alicujus rei, alicui rei, ad, contra <i>или</i> adversus aliquid <i>Ter, C, L, PM etc.</i> лекарство против (от) чего-л.; esse remedio alicui rei <i>rhH, L</i> служить лекарством (средством), помогать от (против) чего-л.; facile r. est ubertatis <i>погов. Q</i> с избытком справиться нетрудно; solum insidiarum r. <i>T</i> единственный способ избежать козней."],
  ["remeligo", "<big>remeligo</big>\n\n<b>remeligo, inis</b> <i>f</i>\nмедлительница, «копунья» <i>Pl</i>."],
  ["rememini", "<big>rememini</big>\n\n<b>re–memini, —, isse</b>\nвновь вспоминать (priorum <i>Tert</i>)."],
  ["rememoratio", "<big>rememoratio</big>\n\n<b>re–memoratio, onis</b> <i>f</i>\nвоспоминание <i>Vlg, Eccl</i>."],
  ["rememoror", "<big>rememoror</big>\n\n<b>re–memoror, atus sum, ari</b>\nснова вспоминать, перебирать в памяти (aliquid <i>Vlg, Is</i>)."],
  ["remensus", "<big>remensus</big>\n\n<b>remensus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> remetior;\n<b>2.</b> <i>adj.</i> пройденный (mare <i>V</i>)."],
  ["remeo", "<big>remeo</big>\n\n<b>re–meo, avi, atum, are</b>\nприходить назад, возвращаться (navis remeat <i>T</i>; r. urbes <i>V</i> = r. in urbes): r. aevum peractum <i>H</i> вновь пройти (пережить) прожитую жизнь."],
  ["remergo", "<big>remergo</big>\n\n<b>re–mergo, —, —, ere</b>\nвновь Погружать (soporis altitudine remergi <i>Aug</i>)."],
  ["remetior", "<big>remetior</big>\n\n<b>re–metior, mensus sum, iri</b> <i>depon.</i>\n<b>1)</b> снова измерять <i>или</i> отмерять, <i>перен.</i> оплачивать (frumentum pecunia <i>Q</i>): in qua mensura mensi fueritis, remetietur <i>Vlg</i> какой мерой будете мерить, (такая и вам) отмерится;\n<b>2)</b> вновь наблюдать (servata astra <i>V</i>);\n<b>3)</b> снова проходить <i>или</i> Проезжать (iter <i>Lcr</i>): bellum remenso transferre Pado <i>Cld</i> перенести военные действия на ту сторону реки Пад;\n<b>4)</b> снова обдумывать (dicta et facta <i>Sen</i>);\n<b>5)</b> повторять, пересказывать (fabulam <i>Ap</i>);\n<b>6)</b> <i>шутл.</i> извергать, выбрасывать (aliquid vomitu <i>Sen</i>)."],
  ["remex", "<big>remex</big>\n\n<b>remex, igis</b> (<i>gen. pl.</i> um) <i>m</i> [remus + ago]\nгребец; <i>собир.</i> гребцы (triremes cum suo remige <i>QC</i>)."],
  ["Remi", "<big>Remi</big>\n\n<b>Remi, orum</b> <i>m</i>\nремы, <i>племя вельгов между реками Матрона и Аксона с главн. городом Durocortorum</i> (<i>впоследствии Remi, ныне</i> Reims) <i>Cs, T, Amm</i>."],
  ["remigatio", "<big>remigatio</big>\n\n<b>remigatio, onis</b> <i>f</i> [remigo]\nгребля <i>C</i>."],
  ["remigium", "<big>remigium</big>\n\n<b>remigium, i</b> <i>n</i> [remigo]\n<b>1)</b> вёсла <i>Pl, H etc.</i>: remigio veloque <i>погов. Pl</i> = изо всех сил, во всю мочь (festinare); suo remigio rem gerere <i>погов. Pl</i> = действовать по своему усмотрению; r. alarum <i>поэт. V, Ap</i> крылья;\n<b>2)</b> гребцы (navis remigio instructa <i>L</i>; r. classiariorum <i>T</i>);\n<b>3)</b> (= remigatio) гребля (remigio noctemque diemque fatigare <i>V</i>)."],
  ["remigo", "<big>remigo</big>\n\n<b>remigo, avi, atum, are</b> [remex + ago]\nгрести (contra aquam <i>Sen</i>; remigandi labor <i>Cs</i>): missam Pado r. alnum <i>Cld</i> плыть на вёслах по реке Пад."],
  ["remigro", "<big>remigro</big>\n\n<b>re–migro, avi, atum, are</b>\n<b>1)</b> переселяться назад, переезжать обратно (in domum Veterem e nova <i>C</i>);\n2) возвращаться (ad argumentum <i>Pl</i>)."],
  ["reminiscentia", "<big>reminiscentia</big>\n\n<b>reminiscentia, ae</b> <i>f</i>\nвоспоминание <i>Boet, Eccl</i>."],
  ["reminiscor", "<big>reminiscor</big>\n\n<b>reminiscor, —, minisci</b> <i>depon.</i> [re + memini]\n<b>1)</b> припоминать, вспоминать (de aliqua re, alicujus rei <i>или</i> aliquid <i>C, Cs etc.</i>);\n<b>2)</b> обдумывать, взвешивать (aliquid <i>C etc.</i>);\n<b>3)</b> придумывать (plura bona <i>Nep</i>)."],
  ["remipes", "<big>remipes</big>\n\n<b>remipes, pedis</b> <i>adj.</i> [remus + pes]\n<b>1)</b> «веслоногий», гребущий лапами (anates <i>Vr, Aus</i>);\n<b>2)</b> движимый вёслами (lembi <i>Aus</i>)."],
  ["remisceo", "<big>remisceo</big>\n\n<b>re–misceo, miscui, mistum (mixtum), ere</b>\nперемешивать, смешивать (vera falsis <i>H</i>); примешивать (venenum cibo remixtum <i>Sen</i>)."],
  ["remisi", "<big>remisi</big>\n\n<b>remisi</b> <i>pf. к</i>\nremitto."],
  ["remissa", "<big>remissa</big>\n\n<b>remissa, ae</b> <i>f</i> [remitto]\nотпущение (peccatorum <i>Tert</i>)."],
  ["remissarius", "<big>remissarius</big>\n\n<b>remissarius, a, um</b> [remitto]\nпередвигающийся, задвигающийся (vectis <i>Cato</i>)."],
  ["remisse", "<big>remisse</big>\n\n<b>remisse</b> [remissus]\n<b>1)</b> свободно (non astricte, sed r. <i>C</i>);\n<b>2)</b> тихо, спокойно, мягко (disputare <i>C</i>);\n<b>3)</b> в шутку, для отдыха (r. vel serio <i>Su</i>)."],
  ["remissibilis", "<big>remissibilis</big>\n\n<b>remissibilis, e</b> [remitto]\n<b>1)</b> простительный (delicta <i>Tert</i>);\n<b>2)</b> лёгкий (digestio <i>CA</i>)."],
  ["remissio", "<big>remissio</big>\n\n<b>remissio, onis</b> <i>f</i> [remitto]\n<b>1)</b> обратная отправка, освобождение (captivorum <i>L</i>);\n<b>2)</b> отбрасывание, отражение (splendoris <i>Vtr</i>);\n<b>3)</b> опускание (superciliorum <i>C</i>);\n<b>4)</b> понижение (vocis <i>C</i>);\n<b>5)</b> прекращение, перерыв (laboris <i>Vr</i>); ослабление, ремиссия (morbi <i>C</i>; febris <i>CC</i>): r. usis <i>C</i> (постепенное) прекращение сношений;\n<b>6)</b> сложение, освобождение, отмена, аннулирование (tributi <i>T</i>);\n<b>7)</b> отпущение, прощение (delicti <i>Tert</i>; peccatorum <i>Ambr</i>);\n<b>8)</b> (<i>тж.</i> r. animi <i>C, Sen</i>) отдых (danda est r. animis <i>Sen</i>; tempora curarum remissionumque divisa <i>T</i>);\n<b>9)</b> спокойствие, невозмутимость (animi <i>C</i>);\n<b>10)</b> возобновление, повторение (nova ludorum r. <i>Pt</i> — <i>v. l.</i> missio)."],
  ["remissivus", "<big>remissivus</big>\n\n<b>remissivus, a, um</b>\n<b>1)</b> мягчительный (<i>sc.</i> remedium <i>CA</i>);\n<b>2)</b> <i>грам.</i> выражающий замедленность действия (adverbia)."],
  ["remissor", "<big>remissor</big>\n\n<b>remissor, oris</b> <i>m</i>\nотпускающий, прощающий (r. peccatorum <i>Ambr</i>)."],
  ["remissus", "<big>remissus</big>\n\n<b>remissus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> remitto;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> ненатянутый (arcus <i>H</i>); расслабленный, вялый (corpora <i>L</i>; membra <i>Lcr</i>); ленивый, сонный (animus <i>Cs</i>);\n<b>2)</b> рыхлый (ager <i>O</i>);\n<b>3)</b> тусклый, матовый, неяркий (color <i>Sen</i>);\n<b>4)</b> свободный, непринуждённый (amicitia <i>C</i>);\n<b>5)</b> спокойный, мягкий (sermo <i>C, Su</i>); мягкий, благосклонный (judex <i>Sen</i>); умеренный (ventus, frigora <i>Cs</i>);\n<b>6)</b> весёлый, радостный <i>или</i> развязный (homo, jocus <i>C</i>);\n<b>7)</b> распущенный, (слишком) вольный (opus <i>O</i>);\n<b>8)</b> пониженный (vox <i>Q</i>; aestimatio <i>C</i>);\n<b>9)</b> доступный, открытый (mons <i>Prp</i>)."],
  ["remitto", "<big>remitto</big>\n\n<b>re–mitto, misi, missum, ere</b>\n<b>1)</b> отсылать, посылать обратно, отпускать (captivos <i>Nep</i>; legatos Romam <i>Nep</i>); распускать (contionem <i>L</i>); направлять (causam ad senatum <i>T</i>): nuntium uxori r. <i>C</i> послать жене письмо о разводе; alicui litteras r. <i>Cs</i> отвечать кому-л. на письма; fama remissa Pylo <i>O</i> слух, дошедший из Пилоса;\n<b>2)</b> бросать обратно (pila in hostes <i>Cs</i>): r. calces <i>Nep</i> брыкаться, лягаться || возвращать (Gallis imperium <i>Cs</i>);\n<b>3)</b> возмещать, вознаграждать (veniam alicui latam <i>V</i>): r. beneficium <i>Cs</i> отблагодарить за услугу;\n<b>4)</b> издавать (sonum <i>H</i>): r. vocem <i>V</i> давать отголосок, откликаться эхом;\n<b>5)</b> извергать (sanguinem e pulmone <i>O</i>); испускать, выделять, испарять (nebulas <i>V</i>); оставлять (labem <i>H</i>);\n<b>6)</b> отказываться, отбрасывать, оставлять (inimicitias <i>L</i>; opinionem <i>C</i>): remittas quaerere <i>H</i> не пытайся дознаться || прощать, отпускать (poenam <i>L</i>; pecuniam <i>Cs</i>; injurias <i>Sl</i>): r. legem <i>QC</i> приостановить <i>или</i> ослабить действие закона;\n<b>7)</b> ослаблять, отпускать (arcum <i>H</i>; habenas <i>C</i>); ослаблять, замедлять (studia remissa <i>C</i>); опускать (brachia <i>V</i>); спускать (tunicam <i>O</i>); развязывать (vincula <i>O</i>); разглаживать (frontem <i>PJ</i>); распускать, расплавлять, растоплять (mella <i>V</i>): ager remissus <i>O</i> оттаявшее (рыхлое) поле || распускать, давать волю (r. appetitus <i>C</i>);\n<b>8)</b> ослаблять, умерять (iracundiam <i>C</i>): bruma nescia vere remitti <i>Lcn</i> зима, которая никогда не сменяется весной (<i>на дальнем севере</i>) || убавлять, уменьшать (curam <i>C</i>); кончать (vita remissa <i>Poeta ap. C</i>): r. de celeritate <i>C</i> убавить скорость; nihil r. ex pristina virtute <i>Cs</i> ничего не утратить из старой доблести;\n<b>9)</b> уступать, переставать сопротивляться (de aliqua re <i>T</i>); предоставлять, разрешать (alicui istam voluptatem <i>C</i>); давать отдых, освежать, позволить оправиться (r. animos a certamine <i>L</i>): se r. <i>или</i> <i>med.</i> remitti <i>Nep, PJ</i> отдохнуть, оправиться;\n<b>10)</b> утихать, успокаиваться (ventus remittit <i>Cs</i>); проходить, прекращаться (dolores remittunt <i>Ter</i>). — <i>См. тж.</i> remissus."],
  ["remivagus", "<big>remivagus</big>\n\n<b>remivagus, a, um</b> [remus+ vagor I]\nприводимый в движение вёслами (celox <i>Vr</i>)."],
  ["Remmius", "<big>Remmius</big>\n\n<b>Remmius, a, um</b>\nРеммий, <i>римск. nomen</i>: lex Remmia de calumniatoribus <i>C, Dig</i> закон Реммия о клеймении за клевету в уголовных делах."],
  ["remolior", "<big>remolior</big>\n\n<b>re–molior, itus sum, iri</b> <i>depon.</i>\n<b>1)</b> отодвигать, отталкивать, сбрасывать (pondera terrae <i>O</i>); взламывать (ferrea claustra <i>St</i>): <i>pass.</i> remolitus <i>SenT</i> опрокинутый, разрушенный;\n<b>2)</b> вновь пускать в ход (arma <i>Sil</i>)."],
  ["remollesco", "<big>remollesco</big>\n\n<b>re–mollesco, —, —, ere</b>\n<b>1)</b> делаться мягким, размягчаться (cera remollescit sole <i>O</i>);\n<b>2)</b> смягчаться, быть тронутым (precibus <i>O</i>);\n<b>3)</b> изнеживаться, становиться слабым (ad laborem ferendum <i>Cs</i>)."],
  ["remollio", "<big>remollio</big>\n\n<b>re–mollio, ivi, itum, ire</b>\n<b>1)</b> делать мягким, размягчать, разрыхлять (terram <i>Col</i>);\n<b>2)</b> смягчать (aliquem <i>Su</i>);\n<b>3)</b> расслаблять, лишать силы, изнеживать (artus <i>O</i>)."],
  ["remoneo", "<big>remoneo</big>\n\n<b>re–moneo, —, —, ere</b>\nвновь напоминать, опять указывать <i>Ap</i>."],
  ["Remora", "<big>Remora</big>\n\n<b>I Remora, ae</b> <i>f</i>\n<i>название, которое, по преданию, хотел дать Риму Рем</i> <i>Enn ap. C</i>.\n\n<b>II re–mora, ae</b> <i>f</i>\nзадержка, промедление <i>CC, Amm</i>: remoram facere <i>Pl</i> задерживать, затягивать.\n\n<b>III remora, ae</b> <i>f</i>\n<i>рыба</i> прилипало <i>PM</i> (<i>v. l.</i>)."],
  ["remoram", "<big>remoram</big>\n\n<b>remoram</b> <i>H etc.</i>\n= removeram (<i>ppf. к</i> removeo)."],
  ["remoramen", "<big>remoramen</big>\n\n<b>remoramen, inis</b> <i>n</i> [remoror]\nзадержка, помеха, препятствие <i>O</i>."],
  ["remoratio", "<big>remoratio</big>\n\n<b>remoratio, onis</b> <i>f</i>\nзамедление <i>Aug</i>."],
  ["remorbesco", "<big>remorbesco</big>\n\n<b>re–morbesco, —, —, ere</b>\nвновь заболевать <i>Enn</i>."],
  ["remordeo", "<big>remordeo</big>\n\n<b>re–mordeo, mordi, morsum, ere</b>\n<b>1)</b> опять кусать, <i>перен.</i> огрызаться, отвечать на выпад выпадом (aliquem r. <i>H</i>);\n<b>2)</b> терзать, мучить, тревожить (desiderium remordet animum <i>L</i>; cura remordet aliquem <i>V</i>);\n<b>3)</b> быть острым <i>или</i> крепким на вкус (vinum remordens <i>CA</i>)."],
  ["Remoria", "<big>Remoria</big>\n\n<b>Remoria, ae</b> <i>f</i> <i>v. l.</i>\n= Remuria II."],
  ["remoris", "<big>remoris</big>\n\n<b>re–moris, e</b> [mora I]\nмедленный, медлительный <i>AV</i>."],
  ["remoror", "<big>remoror</big>\n\n<b>re–moror, atus sum, ari</b> <i>depon.</i>\n<b>1)</b> задерживаться, мешкать, оставаться (in concilio <i>Lcr</i>);\n<b>2)</b> задерживать, замедлять, мешать (aliquem ab aliqua re <i>Sl</i>; iter alicujus <i>Sl</i>); freno non remorante <i>O</i> без сдерживающей узды, <i>т. е.</i> во весь опор."],
  ["remosse", "<big>remosse</big>\n\n<b>remosse</b>\n<i>Lcr</i> = removisse (<i>inf. pf. к</i> removeo)."],
  ["remota", "<big>remota</big>\n\n<b>remota, orum</b> <i>n</i> <i>C</i>\n= rejectanea."],
  ["remote", "<big>remote</big>\n\n<b>remote</b> [remotus]\nотдалённо, далеко, на большом расстоянии <i>C, Aug</i>."],
  ["remotio", "<big>remotio</big>\n\n<b>remotio, onis</b> <i>f</i> [removeo]\n<b>1)</b> удаление, отведение (brachii <i>rhH</i>);\n<b>2)</b> <i>юр.</i> отвод (tutoris <i>Dig</i>);\n<b>3)</b> отклонение (criminis <i>C</i>)."],
  ["remotus", "<big>remotus</big>\n\n<b>remotus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> removeo;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> отдалённый, удалённый, дальний (locus <i>C, Cs etc.</i>; sedes <i>Cs</i>);\n<b>2)</b> далёкий, чуждый (aliqua re <i>и</i> ab aliqua re <i>C etc.</i>): r. culpa <i>C</i> невинный; r. a vino <i>Col</i> непьющий, трезвый; r. ab agro <i>C</i> не имеющий ничего общего с сельским хозяйством; esse bonum facile est, ubi, quod vetet esse, remotum est <i>O</i> <i>впоследствии погов.</i> нетрудно быть добродетельным там, где ничто этому не препятствует; te remoto <i>Lcn</i> за исключением тебя."],
  ["removeo", "<big>removeo</big>\n\n<b>re–moveo, movi, motum, ere</b>\nотодвигать, удалять (aliquid ab oculis <i>C</i>); устранять, изымать (aliquid ex oratione <i>Cs</i>): r. aliquem a vita <i>Lcr</i> умертвить кого-л.; r. a re publica <i>C</i> лишить участия в политической жизни; r. se a negotiis <i>C</i> удалиться от дел, оставить дела; r. praesidium <i>C</i> вывести гарнизон; r. praetorianos <i>Su</i> распустить (уволить) преторианцев; remoto joco <i>C</i> шутки в сторону (кроме шуток). — <i>См. тж.</i> remotus."],
  ["remugio", "<big>remugio</big>\n\n<b>re–mugio, —, —, ire</b>\nмычать, реветь в ответ (ad verba alicujus <i>O</i>); отзываться рёвом (antro <i>V</i>); откликаться шумом (nemus ventis remugit <i>H</i>)."],
  ["remulceo", "<big>remulceo</big>\n\n<b>re–mulceo, mulsi, mulsum, ere</b>\n<b>1)</b> приглаживать, гладить (crines <i>Ap</i>; aures equi <i>Ap</i>); поджимать (caudam <i>V</i>): remulsis antiis <i>Ap</i> с закинутыми назад кудрями || прижимать назад (remulsis auribus <i>Ap</i>);\n<b>2)</b> веселить, развлекать (animos audientium <i>Ap</i>);\n<b>3)</b> успокаивать (minas cordaque <i>Su</i>)."],
  ["remulcum", "<big>remulcum</big>\n\n<b>remulcum, i</b> <i>n</i> (<i>греч.</i>)\nбуксирный канат, бечева (navem remulco adducere <i>Cs</i> <i>и</i> trahere <i>L</i>)."],
  ["remulsus", "<big>remulsus</big>\n\n<b>remulsus, a, um</b> <i>part. pf. к</i>\nremulceo."],
  ["Remulus", "<big>Remulus</big>\n\n<b>Remulus, i</b> <i>m</i>\nРемул, <i>древнеиталийское имя</i>: R. Silvius, <i>один из царей Альбы</i> <i>O</i>."],
  ["remuneratio", "<big>remuneratio</big>\n\n<b>remuneratio, onis</b> <i>f</i> [remuneror]\nвоздаяние, возмещение, вознаграждение (officiorum <i>C</i>)."],
  ["remunerator", "<big>remunerator</big>\n\n<b>remunerator, oris</b> <i>m</i>\nвоздаятель <i>Vlg, Eccl</i>."],
  ["remunero", "<big>remunero</big>\n\n<b>re–munero, avi, atum, are</b> <i>Pt, Q, Tert</i>\n= remuneror."],
  ["remuneror", "<big>remuneror</big>\n\n<b>re–muneror, atus sum, ari</b> <i>depon.</i>\n<b>1)</b> воздавать, вознаграждать, награждать (meritum alicujus <i>L</i>; aliquem magno praemio <i>Cs</i>);\n<b>2)</b> отплачивать, карать (aliquem supplicio <i>Ctl</i>)."],
  ["Remuria", "<big>Remuria</big>\n\n<b>I Remuria, orum</b> <i>n</i> <i>O</i>\n= Lemuria.\n\n<b>II Remuria, ae</b> <i>f</i> [Remus]\n<i>место на вершине Авентинского холма, где Рем совершал ауспиции А</i> <i>V</i>."],
  ["remurmuro", "<big>remurmuro</big>\n\n<b>re–murmuro, —, —, are</b>\n<b>1)</b> журчать, шуметь в ответ (unda remurmurat <i>V</i>); шелестеть (pinus remurmurat aurae <i>St</i>);\n<b>2)</b> повторять (carmina ventosa remurmurat echo <i>Calp</i>);\n<b>3)</b> неодобрительно бормотать, роптать (r. aut deprecari <i>Fronto</i>)."],
  ["remus", "<big>remus</big>\n\n<b>I remus, i</b> <i>m</i>\nвесло: remos ducere <i>O</i> (impellere <i>V</i>) <i>или</i> remos reducere ad pectora <i>O</i> грести; remis ventisque <i>или</i> velis remisque <i>погов. C, V</i> всеми силами, во всю мочь; remi alarum <i>O</i> <i>или</i> pinnarum <i>Sil</i> крылья; remi corporis <i>O</i> руки и ноги (пловца).\n\n<b>II Remus, i</b> <i>m</i>\nРем, <i>брат-близнец Ромула, убитый последним <i>C, L</i>; поэт. иногда родоначальник римск. народа</i>: Remi nepotes <i>Ctl</i> <i>или</i> turba Remi <i>J</i> = populus Romanus; regna prima Remi <i>Prp</i> = Roma.\n\n<b>III Remus, i</b> <i>m sg. к</i>\nRemi."],
  ["ren", "<big>ren</big>\n\n<b>ren, renis</b> (<i>gen. pl.</i> um <i>или</i> ium) <i>m</i> <i>Pl sg. к</i>\nrenes."],
  ["renalis", "<big>renalis</big>\n\n<b>renalis, e</b> [ren]\nпочечный (passio <i>CA</i>)."],
  ["renarro", "<big>renarro</big>\n\n<b>re–narro, —, —, are</b>\nснова рассказывать, пересказывать (aliquid <i>V, O, St</i>)."],
  ["renascor", "<big>renascor</big>\n\n<b>re–nascor, natus sum, nasci</b> <i>depon.</i>\nрождаться опять, возрождаться (de corpore patrio parvus phoenix renascitur <i>O</i>); вновь отрастать (pinnae renascuntur <i>C</i>); отстраиваться (tot nascentia templa, tot renata <i>M</i>); вновь появляться, воскресать (multa renascentur, quae jam cecidere, vocabula <i>H</i>); возобновляться (bellum ex integro renascitur <i>T</i>)."],
  ["renato", "<big>renato</big>\n\n<b>re–nato, —, atum, are</b>\nобратно переплывать (eodem renatato stagno <i>Aug</i>)."],
  ["renatus", "<big>renatus</big>\n\n<b>renatus, a, um</b> <i>part. pf. к</i>\nrenascor."],
  ["renavigo", "<big>renavigo</big>\n\n<b>re–navigo, avi, —, are</b>\nплыть назад, возвращаться (<i>на корабле</i>) <i>C, PM</i>: Acheron invius renavigari <i>SenT</i> Ахеронт, который в обратном направлении переплыть нельзя."],
  ["reneo", "<big>reneo</big>\n\n<b>re–neo, —, —, ere</b>\nраспускать пряжу: <i>о Парках</i> fila r. <i>O</i> отдалить смерть, продлить жизнь."],
  ["renes", "<big>renes</big>\n\n<b>renes, um</b> <i>или</i> <b>-ium</b> <i>m</i>\n<b>1)</b> <i>анат.</i> почки <i>Pl, C, H, Scr, CC etc.</i>;\n<b>2)</b> чресла, поясница <i>Nem, Vlg</i>."],
  ["renidentia", "<big>renidentia</big>\n\n<b>renidentia, ae</b> <i>f</i>\nрадостная улыбка (infantium <i>Tert</i>)."],
  ["renideo", "<big>renideo</big>\n\n<b>re–nideo, —, —, ere</b>\n<b>1)</b> испускать лучи, сиять, блистать (auro <i>Lcr</i>; nocturno renidet luna mari <i>H</i>);\n<b>2)</b> сиять радостью, улыбаться: ore renidenti <i>O</i> с сияющим лицом."],
  ["renidesco", "<big>renidesco</big>\n\n<b>renidesco, —, —, ere</b> [<i>inchoat. к</i> renideo]\nсиять, сверкать, блистать (aere <i>Lcr</i>)."],
  ["renisus", "<big>renisus</big>\n\n<b>renisus, us</b> <i>m</i> [renitor]\nсопротивление, противодействие <i>CC, Aug</i>."],
  ["renitenter", "<big>renitenter</big>\n\n<b>renitenter</b>\nсопротивляясь <i>Aug</i>."],
  ["renitor", "<big>renitor</big>\n\n<b>re–nitor, nisus (nixus) sum, niti</b> <i>depon.</i>\nупираться, сопротивляться (trahere aliquem renitentem <i>Ap</i>; alter motus alteri renititur <i>PM</i>)."],
  ["renixus", "<big>renixus</big>\n\n<b>renixus, us</b> <i>m</i> <i>v. l.</i>\n= renisus."],
  ["reno", "<big>reno</big>\n\n<b>I re–no, —, —, are</b>\n<b>1)</b> плыть назад (per Stygias umbras <i>O</i>);\n<b>2)</b> опять всплывать (saxa renant <i>H</i>).\n\n<b>II reno, onis</b> <i>m</i> (<i>кельт.</i>)\n<b>1)</b> <i>предпол.</i> северный олень (renonis tegumentum <i>Cs</i>);\n<b>2)</b> шкура, меховая одежда <i>Vr, Sl, Is</i>."],
  ["renodis", "<big>renodis</big>\n\n<b>re–nodis, e</b> [nodus]\nподвязанный (capillus <i>Capit</i>)."],
  ["renodo", "<big>renodo</big>\n\n<b>re–nodo, (avi), atum, are</b>\nразвязывать, расплетать, распускать (comam <i>H</i>); отвязывать: renodatus pharetris <i>VF</i> сняв с себя колчан."],
  ["renosco", "<big>renosco</big>\n\n<b>re–nosco, —, —, ere</b>\nснова узнавать (vix nota alloquia <i>Eccl</i>)."],
  ["renovamen", "<big>renovamen</big>\n\n<b>renovamen, inis</b> <i>n</i> [renovo]\nобновление, новый образ, новый вид <i>O</i>."],
  ["renovatio", "<big>renovatio</big>\n\n<b>renovatio, onis</b> <i>f</i> [renovo]\nобновление, возобновление (mundi <i>C</i>): r. doctrinae <i>C</i> возвращение к учёным занятиям; r. annorum <i>C</i> наращение процентов на проценты, <i>т. е.</i> сложные проценты."],
  ["renovello", "<big>renovello</big>\n\n<b>re–novello, —, —, are</b>\nобновлять, сызнова засаживать (vineam <i>Col</i>)."],
  ["renovo", "<big>renovo</big>\n\n<b>re–novo, avi, atum, are</b>\n<b>1)</b> обновлять, восстанавливать (veteres colonias <i>C</i>); возобновлять (proelium <i>Cs, O</i>; foedus <i>L</i>): r. vulnera alicujus <i>O</i> бередить чьи-л. старые раны || омолаживать (senectus renovata <i>O</i>); освежать (vires <i>Q</i>; memoriam alicujus <i>C</i>); повторять (ea quae dicta sunt <i>C</i>); давать отдохнуть (r, corpora animosque militum <i>L</i>); воскрешать (exemplum alicujus <i>C</i>); вновь разжигать (animum ad odium <i>C</i>): r. agrum <i>O</i> сызнова вспахивать, перепахивать, поднимать пар, <i>но тж.</i> <i>O</i> оставлять под паром поле;\n<b>2)</b> начислять: fenus in singulos annos r. <i>C</i> начислять проценты на проценты."],
  ["renubo", "<big>renubo</big>\n\n<b>re–nubo, —, —, ere</b>\nвновь выходить замуж <i>Tert</i>."],
  ["renudo", "<big>renudo</big>\n\n<b>re–nudo, avi, atum, are</b>\nобнажать (brachia humero tenus <i>Ap</i>)."],
  ["renudus", "<big>renudus</big>\n\n<b>re–nudus, a, um</b>\nобнажённый <i>Tert</i>."],
  ["renui", "<big>renui</big>\n\n<b>renui</b> <i>pf. к</i>\nrenuo."],
  ["renumeratio", "<big>renumeratio</big>\n\n<b>renumeratio, onis</b> <i>f</i> <i>Eccl</i>\n= recapitulatio."],
  ["renumero", "<big>renumero</big>\n\n<b>re–numero, avi, atum, are</b>\nотсчитывать, выплачивать (alicui aurum <i>Pl</i>; pecuniam <i>Ter, Ap</i>)."],
  ["renuntiatio", "<big>renuntiatio</big>\n\n<b>renuntiatio, onis</b> <i>f</i> [renuntio]\n<b>1)</b> донесение, извещение, сообщение, уведомление <i>C, PJ, Dig</i>;\n<b>2)</b> расторжение (societatis <i>Dig</i>);\n<b>3)</b> отказ (vitae <i>CJ</i>)."],
  ["renuntiator", "<big>renuntiator</big>\n\n<b>renuntiator, oris</b> <i>m</i>\n<b>1)</b> разгласитель (consiliorum <i>Dig</i>);\n<b>2)</b> обличитель (erroris alicujus <i>Tert</i>)."],
  ["renuntio", "<big>renuntio</big>\n\n<b>re–nuntio, avi, atum, are</b>\n<b>1)</b> приносить известие, возвещать, доносить, сообщать, уведомлять (alicui aliquid <i>или</i> de aliqua re <i>C etc.</i>); докладывать (aliquid ad senatum <i>C</i>; in concilium <i>L</i>): r. legationem <i>C</i> дать отчёт о выполнении поручения;\n<b>2)</b> объявлять, провозглашать (aliquem consulem <i>C</i>); указывать, называть (hostium numerum <i>C</i>);\n<b>3)</b> расторгать, прекращать (condicionem <i>C</i>; r. alicui amicitiam <i>L</i>; societatem <i>и</i> societati <i>Dig</i>); отказываться, отрекаться, отклонять (alicui rei): r. rebus humanis <i>или</i> mundo <i>Eccl</i> уйти от мирских дел;\n<b>4)</b> отказываться от приглашения (r. ad aliquem <i>Pl</i>; ad cenam <i>Sen</i>)."],
  ["renuntius", "<big>renuntius</big>\n\n<b>re–nuntius, i</b> <i>m</i>\nпосыльный с ответом: nuntii renuntii <i>Pl, CTh</i> посыльные за ответом и с ответом."],
  ["renuo", "<big>renuo</big>\n\n<b>re–nuo, nui, (nutum), ere</b>\n<b>1)</b> отклонять жестом, отрицательно качать головой (annuendi renuendi que motus <i>Q</i>); выражать несогласие (alicui <i>T</i>);\n<b>2)</b> не соглашаться, не одобрять, возражать (aliquid <i>H etc.</i> <i>или</i> alicui rei <i>C, T</i>): renuente aliquo <i>O</i>, <i>M</i> против чьей-л. воли;\n<b>3)</b> отклонять, отвергать, отказываться (convivium <i>C</i>; non r. mori <i>Eccl</i>)."],
  ["renuto", "<big>renuto</big>\n\n<b>renuto, —, —, are</b> [<i>intens. к</i> renuo]\nотклонять, отказываться <i>Lcr, Eccl</i>."],
  ["renutus", "<big>renutus</big>\n\n<b>renutus, (us) </b> <i>m</i> [renuo]\nвыражение несогласия, отказ (renutu respondere <i>PJ</i>)."],
  ["reor", "<big>reor</big>\n\n<b>reor, ratus sum, reri</b> <i>depon.</i>\nсчитать, думать, полагать (r. rem incredibilem <i>Sl</i>): ratus ardere domum <i>Pt</i> полагая, что загорелся дом; ut rere (= reris) <i>V</i> (<i>вводно</i>) как ты полагаешь; quid praeterea reamur? <i>Lcr</i> на что нам ещё рассчитывать?; non isto, quo tu rere, modo <i>H</i> не так, как тебе кажется. — <i>См. тж.</i> ratus."],
  ["reorno", "<big>reorno</big>\n\n<b>re–orno, —, —, are</b>\nвновь украшать (aliquid <i>Tert</i>)."],
  ["repagula", "<big>repagula</big>\n\n<b>repagula, orum</b> <i>n</i> [repango]\n<b>1)</b> поперечный брус для запирания, засов, запор (valvas repagulis claudere <i>C</i>);\n<b>2)</b> барьер, преграда, препятствие (omnia r. pudoris perfringere <i>C</i>)."],
  ["repagulum", "<big>repagulum</big>\n\n<b>repagulum, i</b> <i>n</i> <i>Amm sg. к</i>\nrepagula."],
  ["repandirostrus", "<big>repandirostrus</big>\n\n<b>repandi–rostrus, a, um</b>\nс загнутым кверху рылом, курносый (pecus Nerei, <i>sc.</i> delphini <i>Pac ap. Q</i>)."],
  ["repando", "<big>repando</big>\n\n<b>re–pando, —, —, ere</b>\nвновь растворять, распахивать (fores <i>Ap</i>)."],
  ["repandus", "<big>repandus</big>\n\n<b>re–pandus, a, um</b>\nзагнутый назад (calceolus <i>C</i>); искривлённый (crura <i>Sen</i>); выгнутый вверх (dorsum delphini <i>PM</i>); распустившийся, раскрытый (lilium <i>Vlg</i>)."],
  ["repango", "<big>repango</big>\n\n<b>re–pango, —, —, ere</b>\nсеять, сажать (semen <i>Col</i>)."],
  ["reparabilis", "<big>reparabilis</big>\n\n<b>reparabilis, e</b> [reparo]\n<b>1)</b> поправимый (damnum <i>O, Lcn</i>): nulla r. arte <i>O</i> ничем не поправимый || могущий быть восстановленным, небезвозвратный (res <i>Sen</i>);\n<b>2)</b> постоянно обновляющийся (silva <i>Calp</i>) <i>или</i> воскресающий (Phoenix <i>Aus</i>);\n<b>3)</b> повторяющий (echo <i>Pers</i>)."],
  ["reparatio", "<big>reparatio</big>\n\n<b>reparatio, onis</b> <i>f</i>\nвосстановление, обновление (murorum <i>Veg</i>)."],
  ["reparator", "<big>reparator</big>\n\n<b>reparator, oris</b> <i>m</i> [reparo]\nвосстановитель, обновитель <i>St, Eccl</i>."],
  ["reparco", "<big>reparco</big>\n\n<b>re–parco, —, —, ere</b>\n<b>1)</b> щадить, бережливо относиться (alicui rei <i>Pl</i> <i>и</i> aliquid <i>Symm</i>);\n<b>2)</b> воздерживаться, отказываться (aliquid facere <i>Lcr</i>)."],
  ["reparo", "<big>reparo</big>\n\n<b>re–paro, avi, atum, are</b>\n<b>1)</b> вновь добывать, снова получать, опять находить (res amissas <i>H</i>); вновь приобретать (tribuniciam potestatem <i>L</i>); возобновлять (bellum <i>L</i>); восстанавливать (vires amissas <i>SenT</i> <i>или</i> novas <i>Lcn</i>); отстраивать (aedificia <i>PJ</i>);\n<b>2)</b> пополнять (exercitum <i>L</i>); поправлять (damnum <i>Just</i>); освежать, давать отдых (r. membra labori <i>O</i>);\n<b>3)</b> выменивать, обменивать (vina merce <i>H</i>);\n<b>4)</b> <i>поэт.</i> искать взамен, <i>т. е.</i> устремляться (classe latentes oras r. <i>H</i>)."],
  ["repastinatio", "<big>repastinatio</big>\n\n<b>repastinatio, onis</b> <i>f</i> [repastino]\n<b>1)</b> перекапывание, окапывание (agrorum <i>C, Col</i>);\n<b>2)</b> <i>перен.</i> исправление, улучшение (propagatio in primordio et r. in ultimo <i>Tert</i>)."],
  ["repastinatum", "<big>repastinatum</big>\n\n<b>re–pastinatum, i</b> <i>n</i> (<i>sc.</i> solum)\nвновь перекопанное поле <i>Col, PM</i>."],
  ["repastino", "<big>repastino</big>\n\n<b>re–pastino, avi, atum, are</b>\n<b>1)</b> перекапывать, окапывать (agrum <i>PM</i>; vineas <i>Col</i>);\n<b>2)</b> обрезать <i>или</i> чистить (ungues <i>Tert</i>);\n<b>3)</b> ограничивать, умерять (usum divitiarum <i>Tert</i>);\n<b>4)</b> обновлять (vitam <i>Tert</i>)."],
  ["repatesco", "<big>repatesco</big>\n\n<b>re–patesco, patui, —, ere</b>\nвновь распространяться <i>PM</i> (<i>v. l.</i>)."],
  ["repatrio", "<big>repatrio</big>\n\n<b>re–patrio, avi, —, are</b>\nвозвращаться на родину <i>Sol, Ambr</i>; возвращаться (<i>в речи</i>) (ad aliquid <i>Sol, Eccl</i>)."],
  ["repecto", "<big>repecto</big>\n\n<b>re–pecto, —, pexum, ere</b>\nвновь причёсывать, перечёсывать (coma repexa <i>O</i>; crinem r. <i>Cld</i>)."],
  ["repedo", "<big>repedo</big>\n\n<b>repedo, avi, —, are</b> [pes] (<i>тж.</i> gradum r. <i>Pac</i>)\nидти назад, уходить обратно, отступать <i>LM, Lcr, Amm</i>."],
  ["repello", "<big>repello</big>\n\n<b>re–pello, repuli</b> (<i>или</i> <b>reppuli</b> <i>из</i> repepuli), <b>repulsum, ere</b>\n<b>1)</b> отталкивать (r. alicui manum <i>Pl</i>): aera aere repulsa <i>O</i> удары меди о медь; tellurem impressa hasta r. <i>O</i> подпрыгнуть, оттолкнувшись от земли копьём; r. aliquem a spe <i>Cs</i> лишить кого-л. надежды; r. iracundiam <i>T</i> сдержать гнев || отражать (ictus <i>Cs, O</i>); отклонять, отводить, отвращать (pericula <i>C, Cs</i>); удерживать (aliquem a conatu <i>C</i>); отгонять (aliquem foribus <i>H</i>); прогонять (hostes in silvas <i>Cs</i>); отвергать, отбрасывать (preces <i>O</i>; amorem alicujus <i>O</i>; conubia alicujus <i>V</i>; omnem humanitatem <i>C</i>); не допускать, отказывать (a consulatu repelli <i>C</i>); отстранять, удалять (aliquem a gubernaculis civitatis <i>C</i>); опровергать (aliquem <i>C etc.</i>; criminationes allatas <i>C</i>);\n<b>2)</b> опрокидывать (mensas <i>O</i>); срывать (repagula <i>O</i>);\n<b>3)</b> толкать, вынуждать (aliquam ad meretricium quaestum <i>Pt</i>): repulsus veritatis viribus <i>Ph</i> поставленный в тупик очевидностью истины. — <i>См. тж.</i> repulsus I."],
  ["rependo", "<big>rependo</big>\n\n<b>re–pendo, pendi, pensum, ere</b>\n<b>1)</b> отвешивать, распределять по весу (pensa <i>Prp, O</i>);\n<b>2)</b> платить, давать (aurum pro aliqua re <i>C</i>): r. aliquem <i>H, Sil</i> выкупить кого-л.;\n<b>3)</b> отплачивать, воздавать (r. alicui mercedem <i>Ap</i>): r. vices <i>Prp</i> воздать тем же; r. gratiam <i>O</i> (beneficium <i>Sen</i>) отблагодарить за благодеяние (услугу); r. aliquid aliqua re <i>O, Sen, PJ</i> воздать за что-л. чём-л. || возмещать, вознаграждать (damna formae ingenio <i>O</i>); искупать (culpam <i>O</i>);\n<b>4)</b> взвешивать, тщательно рассматривать (facta <i>Cld</i>)."],
  ["repens", "<big>repens</big>\n\n<b>I repens, entis</b> <i>part. praes. к</i>\nrepo.\n\n<b>II repens, entis</b> <i>adj.</i>\n<b>1)</b> внезапный, неожиданный (adventus <i>C</i>; bellum <i>L</i>);\n<b>2)</b> недавний, новый (r. aut vetustate obscurus <i>T</i>).\n\n<b>III repens</b> <i>adv.</i> <i>O</i>\n= repente."],
  ["repenso", "<big>repenso</big>\n\n<b>repenso, avi, atum, are</b> [<i>intens. к</i> rependo]\n<b>1)</b> взвешивать (aliquid auro <i>Fl</i>);\n<b>2)</b> уравновешивать, воздавать, вознаграждать (bonis mala <i>VP</i>)."],
  ["repente", "<big>repente</big>\n\n<b>repente</b> [repens II]\nвнезапно, неожиданно, вдруг <i>Pl, C, Cs, V etc.</i>"],
  ["repentine", "<big>repentine</big>\n\n<b>repentine</b> <i>Lact</i>\n= repentino."],
  ["repentino", "<big>repentino</big>\n\n<b>repentino</b> (<i>тж.</i> de r. <i>Ap</i>)\nвнезапно, вдруг <i>Pl, C, Cs etc.</i>"],
  ["repentinus", "<big>repentinus</big>\n\n<b>repentinus, a, um</b> [repente]\n<b>1)</b> внезапный, неожиданный (adventus <i>Cs</i>); непредвиденный (pericutum <i>Cs</i>); внезапно появившийся, быстро выслужившийся (ignotus homo et r. <i>C</i>);\n<b>2)</b> спешно набранный (exereitus <i>L</i>);\n<b>3)</b> быстродействующий (venenum <i>T</i>);\n<b>4)</b> говорящий экспромтом (an idem sim r., qui praeparatus <i>Ap</i>)."],
  ["reperco", "<big>reperco</big>\n\n<b>reperco</b> <i>Pl</i>\n= reparco."],
  ["repercussibilis", "<big>repercussibilis</big>\n\n<b>repercussibilis, e</b>\nмогущий быть отражённым (sphaera <i>CA</i>)."],
  ["repercussio", "<big>repercussio</big>\n\n<b>repercussio, onis</b> <i>f</i> [repercutio]\nотражение, отбивание (lapidis <i>Aug</i>); отражённый свет (siderum <i>Sen</i>)."],
  ["repercussus", "<big>repercussus</big>\n\n<b>I repercussus, a, um</b> <i>part. pf. к</i>\nrepercutio.\n\n<b>II repercussus, us</b> <i>m</i> [repercutio]\nотражение, отражённый свет (solis <i>Sen</i>); отблеск (galearum <i>Fl</i>); отголосок (vocis <i>T</i>); отлив (maris <i>PJ</i>)."],
  ["repercutio", "<big>repercutio</big>\n\n<b>re–percutio, cussi, cussum, ere</b>\n<b>1)</b> отталкивать, отбрасывать назад (amnis saxo repercussus <i>QC</i>); отражать (voces repercussae <i>T</i>; lumen repercussum <i>V</i>); отбрасывать, парировать (orationes alicujus dicto aliquo <i>PM</i>);\n<b>2)</b> перечеканивать (monetam <i>Tert</i>);\n<b>3)</b> наносить ответный удар (quum percuteretur, non repercussit <i>Ambr</i>)."],
  ["reperibo", "<big>reperibo</big>\n\n<b>reperibo</b> <i>Caec, Pl арх. fut. к</i>\nreperio."],
  ["reperio", "<big>reperio</big>\n\n<b>reperio, repperi (reperi), repertum, ire</b> [re + *perio, <i>ср.</i> comperio]\n<b>1)</b> (вновь) находить, отыскать, разыскать (epistulam <i>C</i>; vadum in flumine <i>Cs</i>); <i>med.-pass.</i> находиться, бывать (reperiuntur homines qui... <i>Sl</i>) <i>или</i> оказываться: bello strenuus repertus est <i>T</i> он оказался дельным бойцом; repertus cum ferro <i>T</i> застигнутый с оружием (<i>в руках</i>);\n<b>2)</b> знакомиться, узнавать (aliquid percontationibus <i>Cs</i>);\n<b>3)</b> получать (occasionem <i>Pl</i>); приобретать (divitias <i>V</i>): plus est servasse repertum, quam quaesisse novum <i>Cld</i> важнее сохранить приобретённое, чем обрести новое || снискивать, стяжать (gloriam armis <i>Ter</i>);\n<b>4)</b> открывать (viam <i>C</i>); изобретать, придумывать (nihil novi r. <i>C</i>): quibus verbis gratias habitum eam, nondum, hercle, reperio <i>Ap</i> какими словами выразить мне свою благодарность, пока ещё, право, ума не приложу."],
  ["repertio", "<big>repertio</big>\n\n<b>repertio, onis</b> <i>f</i>\nизобретение <i>Ap</i> (<i>v. l.</i>)."],
  ["repertor", "<big>repertor</big>\n\n<b>repertor, oris</b> <i>m</i> [reperio]\nизобретатель, основатель (medicinae <i>V</i>): Cyrenaicae sectae r. <i>Ap</i> = Aristippus || виновник (perfidiae <i>Sl</i>); создатель, творец (hominum rerumque <i>V</i>; primi rhetoricae repertores <i>Ap</i>)."],
  ["repertorium", "<big>repertorium</big>\n\n<b>repertorium, i</b> <i>n</i> [reperio]\nперечень, список <i>Dig</i>."],
  ["repertrix", "<big>repertrix</big>\n\n<b>repertrix, icis</b> <i>f</i> [repertor]\nизобретательница (paupertas omnium artium r. <i>Ap</i>)."],
  ["repertum", "<big>repertum</big>\n\n<b>repertum, i</b> <i>n</i>\nизобретение <i>Lcr</i>."],
  ["repertus", "<big>repertus</big>\n\n<b>I repertus, us</b> <i>m</i>\n<b>1)</b> отыскание (filiae <i>Ap</i>);\n<b>2)</b> открытие, изобретение: repertu novo oriri <i>Ap</i> быть новоизобретённым.\n\n<b>II repertus, a, um</b> <i>part. pf. к</i>\nreperio."],
  ["repetentia", "<big>repetentia</big>\n\n<b>repetentia, ae</b> <i>f</i>\nвоспоминание <i>Lcr, Eccl</i>."],
  ["repetitio", "<big>repetitio</big>\n\n<b>repetitio, onis</b> <i>f</i> [repeto]\n<b>1)</b> повторение <i>C, Q</i>;\n<b>2)</b> <i>юр.</i> обратное требование <i>Ap, Dig</i>;\n<b>3)</b> <i>юр.</i> обращение к прошлым фактам <i>Dig</i>."],
  ["repetitor", "<big>repetitor</big>\n\n<b>repetitor, oris</b> <i>m</i> [repeto]\nтребующий обратно (nuptae ademptae <i>O</i>)."],
  ["repeto", "<big>repeto</big>\n\n<b>re–peto, petivi (petii), petitum, ere</b>\n<b>1)</b> вновь нападать, опять устремляться (aliquem cuspide r. <i>L</i>);\n<b>2)</b> вновь направляться, возвращаться (r. patriam <i>O</i>; penates <i>H</i>): auctorem scelus repetit <i>SenT</i> преступление обращается против (самого его) виновника; canem morsu r. <i>Sen</i> кусать (укусившую) собаку (<i>о безрассудном гневе</i>);\n<b>3)</b> опять добывать, вновь доставать, снова обретать (oppidum bello <i>L</i>; libertatem <i>Sl, L</i>); (вновь) ловить <i>или</i> поднимать (pilam, quae terram contigerat <i>Pt</i>); вновь надевать (cenatoria <i>Pt</i>); приносить, привозить обратно (sarcinas relictas <i>Cs</i>; aliquid domum <i>Pl</i>); приводить назад (filium <i>Pl</i>);\n<b>4)</b> предпринимать, начинать: hoc primus repetas opus, hoc postremus omittas <i>H</i> первым принимайся за этот труд и оставляй его последним;\n<b>5)</b> вновь начинать, возобновлять (pugnam <i>L</i>; propositum opus <i>O</i>); вновь вводить, опять усваивать (consuetudinem <i>C</i>); повторять (vetera <i>C</i>); воскрешать в памяти, вспоминать (praecepta alicujus <i>C</i>; aliquid memoria <i>или</i> memoriam alicujus rei <i>C etc.</i>): ordine r. <i>O</i> рассказать (всё) по порядку; r. viam <i>L</i> вернуться по тому же пути; repetita robora caedere <i>O</i> рубить дуб частыми ударами (топора);\n<b>6)</b> навёрстывать (praetermissa <i>C</i>);\n<b>7)</b> требовать назад (pecunias ereptas <i>C</i>; beneficia <i>Sen</i>): r. aliquid in antiquum jus <i>L</i> предъявлять старые права на что-л.; Salaminii Homerum repetunt <i>C</i> саламинцы считают Гомера своим земляком; pecuniae repetundae <i>C, T etc.</i> деньги, подлежащие обратному взысканию (<i>как незаконно полученные</i>); lex de pecuniis repetundis <i>или</i> pecuniarum repetundarum <i>C</i> закон о лихоимстве (вымогательстве); r. rationem <i>C</i> требовать отчёта; r. poenas ab aliquo <i>C</i> карать кого-л., налагать наказание на кого-л.;\n<b>8)</b> считать началом, вести, производить, выводить (populi origines ab Erechtheo <i>C</i>): oratio longe (alte, supra) repetita <i>C</i> речь, начатая издалека; r. ab ultimo initio <i>rhH</i> начать с самого начала; pectore r. suspiria <i>O</i> глубоко вздыхать."],
  ["repetundae", "<big>repetundae</big>\n\n<b>repetundae, arum</b> <i>f</i> [repeto 7]\n(<i>sc.</i> pecuniae) деньги, подлежащие обратному взысканию (<i>с административных лиц, как полученные ими в виде взяток или путём вымогательства</i>) <i>C, Sl etc.</i>"],
  ["repexus", "<big>repexus</big>\n\n<b>repexus, a, um</b> <i>part. pf. к</i>\nrepecto."],
  ["repignero", "<big>repignero</big>\n\n<b>re–pignero (-pignoro), —, —, are</b>\nбрать обратно залог <i>Dig</i>."],
  ["repigro", "<big>repigro</big>\n\n<b>repigro, (avi), atum, are</b>\nсдерживать (impetum <i>Ap</i>)."],
  ["replanto", "<big>replanto</big>\n\n<b>re–planto, avi, atum, are</b>\nвновь сажать <i>или</i> пересаживать (aliquid <i>Eccl</i>)."],
  ["replaudo", "<big>replaudo</big>\n\n<b>re–plaudo, —, —, ere</b>\nзвонко ударять, хлопать (dextra frontem <i>Ap</i>)."],
  ["repleo", "<big>repleo</big>\n\n<b>re–pleo, plevi, pletum, ere</b>\n<b>1)</b> опять наполнять (scrobem terra <i>V</i>; se esca <i>Ph</i>): luna replevit orbem <i>O</i> наступило полнолуние || восполнять, возмещать (consumpta <i>C</i>);\n<b>2)</b> пополнять, комплектовать (exercitum <i>L</i>); наполнять (galeas conchis <i>Su</i>; lagoenam vino <i>M</i>); преисполнять (patriam laetitia <i>VP</i>; eruditione repletus <i>Sil</i>): deo repletus <i>Capit</i> боговдохновенный; vita repletus <i>Lcn</i> проживший долгую жизнь || воодушевлять (pectora bello <i>St</i>); обильно снабжать (exercitum frumento <i>Cs</i>);\n<b>3)</b> заражать (homines repleti eadem vi morbi <i>L</i>; <i>перен.</i> populos multiplici sermone r. <i>V</i>);\n<b>4)</b> оплодотворять (equas <i>Pall</i>; virgo ab Apolline repleta <i>Just</i>);\n<b>5)</b> сплошь покрывать (insula silvis repleta <i>PM</i>)."],
  ["replerat", "<big>replerat</big>\n\n<b>replerat, replesti</b> <i>etc.</i> <i>Lcr, St</i> = repleverat (<i>ppf.</i>), replevisti (<i>pf.</i>) <i>etc.</i> <i>стяж. формы к</i>\nrepleo."],
  ["repletio", "<big>repletio</big>\n\n<b>repletio, onis</b> <i>f</i> [repleo]\n<b>1)</b> наполнение <i>Eccl</i>;\n<b>2)</b> восполнение, возмещение <i>CJ</i>."],
  ["repletus", "<big>repletus</big>\n\n<b>repletus, a, um</b> <i>part. pf. к</i>\nrepleo."],
  ["replicatio", "<big>replicatio</big>\n\n<b>replicatio, onis</b> <i>f</i> [replico]\n<b>1)</b> обратное движение, поворот, вращение (<i>sc.</i> mundi <i>C</i>);\n<b>2)</b> <i>юр.</i> реплика, возражение <i>Dig</i>;\n<b>3)</b> возобновление (proeliorum <i>Sid</i>)."],
  ["replico", "<big>replico</big>\n\n<b>re–plico, avi, atum, are</b>\n<b>1)</b> развёртывать, разворачивать, раскрывать (volumen <i>Amm</i>; <i>перен.</i> memoriam annalium <i>C</i>);\n<b>2)</b> отгибать назад (cervicem <i>PM</i>): labra r. <i>Q</i> кривить губы;\n<b>3)</b> обращать назад (vestigium suum <i>Ap</i>);\n<b>4)</b> отражать (radios <i>Sen</i>);\n<b>5)</b> размышлять, обдумывать (aliquid secum <i>Ap</i>);\n<b>6)</b> <i>юр.</i> выступать с возражением <i>Dig</i>."],
  ["replictus", "<big>replictus</big>\n\n<b>replictus, a, um</b> <i>St</i>\n= replicatus (<i>part. pf. к</i> replico)."],
  ["replum", "<big>replum</big>\n\n<b>replum, i</b> <i>n</i>\nдверная рама <i>Vtr</i>."],
  ["replumbo", "<big>replumbo</big>\n\n<b>re–plumbo, —, atum, are</b>\nочищать от свинца (aliquid <i>Sen</i>, <i>Dig</i>)."],
  ["repluo", "<big>repluo</big>\n\n<b>re–pluo, —, —, ere</b>\nснова падать дождём (caelo <i>Musa ap. Sen</i>)."],
  ["repo", "<big>repo</big>\n\n<b>repo, repsi, reptum, ere</b>\nползти, ползать (per viam <i>Sen</i>; formica repens <i>PM</i>); медленно течь (aqua repit <i>Col</i>); пробираться, проникать (ignis repit per artus <i>Lcr</i>)."],
  ["repolio", "<big>repolio</big>\n\n<b>re–polio, —, —, ire</b>\nснова очищать (frumenta <i>Col</i>)."],
  ["repondero", "<big>repondero</big>\n\n<b>re–pondero, —, —, are</b>\nотплачивать равным, <i>перен.</i> вознаграждать (r. alicui gloriam <i>Sid</i>)."],
  ["repono", "<big>repono</big>\n\n<b>re–pono, posui, positum, ere</b>\n<b>1)</b> класть обратно, опять ставить (lapidem suo loco <i>C</i>; vina mensis <i>V</i>); снова возлагать (insigne regium <i>C</i>); вправлять (ossa in suas sedes <i>CC</i>); помещать, раскладывать (ligna super foco <i>H</i>);\n<b>2)</b> вновь вносить, откладывать (pecuniam in aerario <i>Nep</i>); сберегать, сохранять (aliquid hiemi <i>V</i> <i>и</i> in hiemem <i>Q</i>): aliquid scriptis r. <i>Q</i> записать что-л. || затаивать (odium <i>T</i>); запечатлевать (aliquid sensibus imis <i>V</i>); хоронить, погребать (corpus tumulo <i>O</i>);\n<b>3)</b> класть в сторону, откладывать (arma <i>Cs</i>; onus <i>Ctl</i>; caestus artemque <i>V</i>): r. brachia <i>VF</i> опустить руки, <i>т. е.</i> отдыхать || приподнимать, оправлять (togam <i>Q</i>): jam arbusta reponunt falcem <i>V</i> кусты больше не нуждаются в подчистке; se r. in cubitum <i>H</i> вновь облокотиться, <i>т. е.</i> приподняться на своём застольном ложе || запрокидывать (cervicem <i>Lcr, Q</i>);\n<b>4)</b> возвращать (donata <i>H</i>); давать взамен, возмещать, воздавать, вознаграждать (alicui aliquid pro aliqua re <i>V etc.</i>);\n<b>5)</b> успокаивать (iram Mare; pontum et turbata litora <i>VF</i>); навёрстывать (tempora cessata <i>Sil</i>);\n<b>6)</b> восстанавливать, отстраивать (pontes ruptos <i>T</i>); снова представлять, вновь выводить на сцену (Achillem <i>H</i>); ставить ещё раз, повторять (fabulam <i>H</i>): r. aliquem in sceptra <i>V</i> вновь возвести кого-л. на престол;\n<b>7)</b> включать, причислять (aliquem in numero <i>и</i> in numerum deorum <i>C</i> <i>или</i> in deos <i>C</i>): aliquem in suis r. <i>Antonius ap. C</i> считать кого-л. своим (человеком); r. aliquid in artis loco <i>C</i> считать что-л. искусством;\n<b>8)</b> видеть, полагать, усматривать (beatam vitam in aliqua re <i>Sen</i>); полагать, возлагать, основывать (spem in aliqua re <i>Cs</i>): r. salutem in aliqua re <i>C</i> усматривать спасение в чём-л. (ждать спасения от чего-л.); repositum esse in aliqua re <i>C, L</i> успокоиться на чём-л.;\n<b>9)</b> приводить в ответ, выдвигать в противовес, возражать, отвечать (alicui aliquid <i>C, V, Sen etc.</i>)."],
  ["reporrigo", "<big>reporrigo</big>\n\n<b>re–porrigo, —, —, ere</b>\nснова <i>или</i> обратно протягивать (phialam <i>Pt</i>)."],
  ["reportatio", "<big>reportatio</big>\n\n<b>reportatio, onis</b> <i>f</i>\nодерживание (victoriarum ab inimicis <i>Aug</i>)."],
  ["reporto", "<big>reporto</big>\n\n<b>re–porto, avi, atum, are</b>\n<b>1)</b> нести назад, везти (доставлять) обратно (aurum <i>Pl</i>); вести, уводить назад (exercitum <i>C</i>; r. milites navibus in Siciliam <i>Cs</i>): r. se <i>bH, Ap</i> <i>или</i> r. pedem <i>V</i> возвратиться, вернуться;\n<b>2)</b> передавать, сообщать, доносить (audita verba <i>O</i>); возвещать (pacem <i>V</i>);\n<b>3)</b> уносить (duo signa <i>C</i>; praedam <i>C</i>);\n<b>4)</b> добывать, получать, приобретать (triumphum <i>PM</i>; gloriam <i>PJ</i>): r. victoriam ex, de <i>и</i> ab aliquo <i>C</i> одержать над кем-л. победу."],
  ["reposco", "<big>reposco</big>\n\n<b>I re–posco, —, —, ere</b>\nтребовать назад, обратно (fortuna cito reposcit, quae dedit <i>PS</i>): sibi parvos usus natura reposcit <i>Man</i> природа довольствуется малым; r. aliquid aliquem <i>C, Sen</i> <i>или</i> ab aliquo <i>C, Cs</i> требовать обратно что-л. от (с) кого-л.; r. rationem <i>L, Q</i> требовать отчёта; reposcor aliquid <i>Q etc.</i> от меня требуют чего-л.; r. poenas ab aliquo <i>Ctl</i> <i>и</i> r. aliquem ad poenam <i>V</i> требовать наказания кого-л.\n\n<b>II reposco, onis</b> <i>m</i>\nпредъявляющий требование (r. acerrimus <i>Amm</i>)."],
  ["repositio", "<big>repositio</big>\n\n<b>repositio, onis</b> <i>f</i> [repono]\nскладывание (feni <i>Pall</i>)."],
  ["repositorium", "<big>repositorium</big>\n\n<b>repositorium, i</b> <i>n</i> [repono]\n<b>1)</b> репозиторий, <i>высокий ящик из поставленных друг на друга подносов</i> (fercula) <i>для различных блюд</i> (<i>род судков или поставца</i>) (superiorem partem repositorii auferre <i>Pt</i>); блюдо (cum sue ingenti <i>Pt</i>);\n<b>2)</b> гробница <i>JV</i>."],
  ["repositum", "<big>repositum</big>\n\n<b>repositum, i</b> <i>m</i>\nотложенное про запас, запасы (ex reposito fundere <i>Sen</i>)."],
  ["repositus", "<big>repositus</big>\n\n<b>repositus</b> <i>v. l.</i>\n= repostus."],
  ["reposivi", "<big>reposivi</big>\n\n<b>reposivi</b> <i>Pl</i>\n= reposui (<i>pf. к</i> repono)."],
  ["repostor", "<big>repostor</big>\n\n<b>repostor, oris</b> <i>m</i> [repono]\nвосстановитель, воссоздатель (alicujus rei <i>O</i>)."],
  ["repostorium", "<big>repostorium</big>\n\n<b>repostorium, i</b> <i>n</i> [repono]\nхранилище, сокровищница <i>Capit</i>."],
  ["repostus", "<big>repostus</big>\n\n<b>repostus, a, um</b> [<i>из</i> repositus]\n<b>1.</b> <i>part. pf. к</i> repono;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> удалённый, отдалённый, далёкий (terrae <i>V</i>; gentes <i>V, VF</i>);\n<b>2)</b> сокрытый: alta mente r. <i>V</i> затаённый в глубине души;\n<b>3)</b> спокойный (vita <i>Amm</i>)."],
  ["repotatio", "<big>repotatio</big>\n\n<b>repotatio, onis</b> <i>f</i> <i>Vr</i>\n(<i>v. l.</i>)= repotia."],
  ["repotia", "<big>repotia</big>\n\n<b>repotia, orum</b> <i>n</i> [re + poto]\nпопойка после пира <i>Ap</i>; пирушка на другой день после торжества (<i>свадьбы и пр.</i>) <i>H, AG, Aus</i>."],
  ["repotialis", "<big>repotialis</big>\n\n<b>repotialis, e</b> [repotia]\nпокровительствующий попойкам (Liber <i>Pac</i>)."],
  ["repperi", "<big>repperi</big>\n\n<b>repperi</b> <i>pf. к</i>\nreperio."],
  ["reppuli", "<big>reppuli</big>\n\n<b>reppuli</b> <i>pf. к</i>\nrepello."],
  ["repraesentaneus", "<big>repraesentaneus</big>\n\n<b>repraesentaneus, a, um</b>\nналичный, непосредственно действующий (potestas <i>Tert</i>)."],
  ["repraesentatio", "<big>repraesentatio</big>\n\n<b>repraesentatio, onis</b> <i>f</i> [repraesento]\n<b>1)</b> наглядное представление, изображение <i>Q, PM, VM, AG</i>;\n<b>2)</b> немедленный платёж, уплата наличными деньгами <i>C, Dig</i>."],
  ["repraesentator", "<big>repraesentator</big>\n\n<b>repraesentator, oris</b> <i>m</i>\nнаглядное изображение, живой портрет (filius r. patris <i>Tert</i>)."],
  ["repraesento", "<big>repraesento</big>\n\n<b>re–praesento, avi, atum, are</b> [praesens]\n<b>1)</b> наглядно представлять, воображать (aliquid animo <i>QC, Q</i>); сразу же обнаруживать (improbitatem suam <i>C</i>);\n<b>2)</b> восстанавливать, возобновлять (memoriam alicujus rei <i>C</i>);\n<b>3)</b> воспроизводить, подражать (mores alicujus <i>H</i>; imaginem sceleris <i>Q</i>);\n<b>4)</b> тотчас приводить в исполнение, немедленно исполнять (poenam <i>Su</i>; minas <i>L</i>); тотчас же начинать (judicia <i>Q</i>); немедленно применять, сразу же пускать в ход (medicinam <i>C</i>); немедленно осуществлять, вводить (libertatem <i>C</i>);\n<b>5)</b> тотчас заплатить, платить наличными деньгами (pecuniam <i>C</i>);\n<b>6)</b> <i>med.</i> явиться, предстать (repraesentari judicio <i>Ap</i>)."],
  ["reprehendo", "<big>reprehendo</big>\n\n<b>re–prehendo, prehendi, prehensum, ere</b>\n<b>1)</b> задерживать, удерживать, останавливать (aliquem manu <i>L</i>); вновь ловить (elapsum semel non posse r. <i>Ph</i>);\n<b>2)</b> вновь занимать (locum <i>Ter</i>);\n<b>3)</b> навёрстывать (rem praetermissam <i>C</i>);\n<b>4)</b> опровергать (aliquid <i>C etc.</i>);\n<b>5)</b> осуждать, порицать (aliquid in aliquo <i>C etc.</i>; aliquem de <i>или</i> in aliqua re <i>C</i>): praeterita magis reprehendi possunt, quam corrigi <i>погов. L</i> прошлое легче порицать, чем исправить."],
  ["reprehensibilis", "<big>reprehensibilis</big>\n\n<b>reprehensibilis, e</b>\nдостойный порицания <i>Vlg, Eccl</i>."],
  ["reprehensio", "<big>reprehensio</big>\n\n<b>reprehensio, onis</b> <i>f</i> [reprehendo]\n<b>1)</b> удерживание, задержка, остановка (sine reprehensione <i>C</i>);\n<b>2)</b> <i>ритор.</i> опровержение <i>C, Q</i>;\n<b>3)</b> неодобрение, порицание (reprehensione dignus <i>Q</i>): sine reprehensione patiens alicujus rei <i>T</i> переносящий что-л. безропотно."],
  ["reprehenso", "<big>reprehenso</big>\n\n<b>reprehenso, —, —, are</b> [<i>intens. к</i> reprehendo]\nудерживать, останавливать (aliquem <i>L</i>)."],
  ["reprehensor", "<big>reprehensor</big>\n\n<b>reprehensor, oris</b> <i>m</i> [reprehendo]\n<b>1)</b> порицатель <i>C, O</i>;\n<b>2)</b> исправитель, вносящий улучшение (non accusator, sed r. <i>VM</i>)."],
  ["reprendo", "<big>reprendo</big>\n\n<b>re–prendo, prendi, prensum, ere</b> <i>v. l.</i>\n= reprehendo."],
  ["represse", "<big>represse</big>\n\n<b>represse</b>\nсдержанно (repressius pudentiusque peccare <i>AG</i>)."],
  ["repressi", "<big>repressi</big>\n\n<b>repressi</b> <i>pf. к</i>\nreprimo."],
  ["repressor", "<big>repressor</big>\n\n<b>repressor, oris</b> <i>m</i> [reprimo]\nограничивающий, сдерживающей (r. caedis <i>C</i>; r. tributorum <i>Eutr</i>)."],
  ["reprimo", "<big>reprimo</big>\n\n<b>re–primo, pressi, pressum, ere</b> [premo]\n<b>1)</b> прогонять, оттеснять (hostis repressus et remotus <i>Cs</i>): hostis repressus, non oppressus <i>C</i> отогнанный, но не сломленный враг || отражать (vim <i>Acc, C</i>);\n<b>2)</b> сдерживать, удерживать, останавливать (aliquem <i>Ter etc.</i>; impetum alicujus <i>C</i>; fletum <i>C</i>; susceptam objurgationem <i>C</i>): pedem retro r. <i>V</i> задержать шаги; r. se <i>Ter, C etc.</i> воздерживаться; repressus aliquamdiu <i>Pt</i> несколько успокоившись || <i>мед.</i> задерживать, закреплять (alvum <i>CC</i>);\n<b>3)</b> подавлять, обуздывать (odium <i>C, Sen</i>; furorem <i>C</i>); умерять, замедлять <i>или</i> прекращать (cursum <i>Cs</i>; itinera <i>C</i>): imber reprimitur <i>PM</i> дождь перестаёт."],
  ["reprobatio", "<big>reprobatio</big>\n\n<b>reprobatio, onis</b> <i>f</i> [reprobo]\nнеодобрение, осуждение, порицание <i>Vlg, Eccl</i>."],
  ["reprobatrix", "<big>reprobatrix</big>\n\n<b>reprobatrix, icis</b> <i>f</i>\nпорицательница <i>Tert</i>."],
  ["reprobo", "<big>reprobo</big>\n\n<b>re–probo, avi, atum, are</b>\nне одобрять, отвергать, осуждать, <i>тж.</i> браковать (aliquid <i>Dig</i>)."],
  ["reprobus", "<big>reprobus</big>\n\n<b>re–probus, a, um</b>\n<b>1)</b> поддельный, фальшивый (argentum <i>Vlg</i>; nummi <i>Dig</i>);\n<b>2)</b> превратный, дурной (sensus <i>Vlg</i>)."],
  ["repromissio", "<big>repromissio</big>\n\n<b>repromissio, onis</b> <i>f</i> [repromitto]\n<b>1)</b> взаимное обещание, обоюдное обязательство <i>C</i>;\n<b>2)</b> обет, обетование <i>Vlg Eccl</i>."],
  ["repromissor", "<big>repromissor</big>\n\n<b>repromissor, oris</b> <i>m</i>\nдающий обещание, поручитель <i>Vlg, Eccl</i>."],
  ["repromitto", "<big>repromitto</big>\n\n<b>re–promitto, misi, missum, ere</b>\n<b>1)</b> обещаться, обещать взамен <i>Pl, C</i>;\n<b>2)</b> снова обещать <i>Su</i>."],
  ["repropitio", "<big>repropitio</big>\n\n<b>re–propitio, —, —, are</b>\nснова делать милостивым (aliquem <i>Vlg, Tert</i>)."],
  ["repsi", "<big>repsi</big>\n\n<b>repsi</b> <i>pf. к</i>\nrepo."],
  ["reptabundus", "<big>reptabundus</big>\n\n<b>reptabundus, a, um</b> [repto]\nползущий, идущий ползком (r. atque ebrius <i>Sen</i>)."],
  ["reptatio", "<big>reptatio</big>\n\n<b>reptatio, onis</b> <i>f</i> [repto]\nползание (per manus et genua <i>Q</i>)."],
  ["reptatus", "<big>reptatus</big>\n\n<b>reptatus, us</b> <i>m</i> [repto]\nползучесть (vitium <i>PM</i>; limacum <i>Tert</i>)."],
  ["reptile", "<big>reptile</big>\n\n<b>reptile, is</b> <i>n</i> (<i>sc.</i> animal)\nпресмыкающееся <i>Vlg, Eccl</i>."],
  ["reptilis", "<big>reptilis</big>\n\n<b>reptilis, e</b> [repo]\nползучий, ползающий (cochleae <i>Sid</i>; animalia <i>Boet</i>)."],
  ["repto", "<big>repto</big>\n\n<b>repto, avi, atum, are</b> [<i>intens. к</i> repo]\n<b>1)</b> ползать, пресмыкаться (serpens reptavit placido tractu <i>Cld</i>);\n<b>2)</b> медленно передвигаться, с трудом ходить (r. fame <i>AG</i>; velut in tenebris <i>Amm</i>);\n<b>3)</b> переползать (ager reptatus, <i>sc.</i> ab angue <i>St</i>) <i>или</i> переплывать (amnis reptatus Achilli <i>Cld</i>)."],
  ["reptus", "<big>reptus</big>\n\n<b>reptus, i</b> <i>m</i> <i>Is</i>\n= reno II, 2."],
  ["repubesco", "<big>repubesco</big>\n\n<b>re–pubesco, —, —, ere</b>\nснова мужать, вновь крепнуть (revirescere ac r. <i>Col</i>)."],
  ["repudiatio", "<big>repudiatio</big>\n\n<b>repudiatio, onis</b> <i>f</i> [repudio]\nотклонение, отказ (r. supplicum <i>C</i>)."],
  ["repudiator", "<big>repudiator</big>\n\n<b>repudiator, oris</b> <i>m</i>\nотрицатель (creatoris <i>Tert</i>)."],
  ["repudio", "<big>repudio</big>\n\n<b>re–pudio, avi, atum, are</b> [pudeo]\n<b>1)</b> отталкивать (aliquem <i>C</i>); отвергать, отклонять, отстранять (preces alicujus <i>C</i>; hereditatem <i>Dig</i>; legem <i>C</i>);\n<b>2)</b> отсылать прочь, давать развод (r. uxorem, sponsam <i>Su</i>);\n<b>3)</b> презирать, пренебрегать (officium <i>C</i>)."],
  ["repudiosus", "<big>repudiosus</big>\n\n<b>repudiosus, a, um</b> [repudio]\nзазорный, предосудительный, достойный порицания (nuptiae <i>Pl</i>)."],
  ["repudium", "<big>repudium</big>\n\n<b>repudium, i</b> <i>n</i> [repudio]\nрасторжение (<i>брака или помолвки</i>): uxori r. remittere <i>Pl, T, Su</i> (renuntiare <i>Pl, Ter</i>, dicere <i>T</i>) объявить жене о разводе; uxorem repudio dimittere <i>Just</i> развестись с женой."],
  ["repuellasco", "<big>repuellasco</big>\n\n<b>re–puellasco, —, —, ere</b>\nвновь становиться девушкой <i>Vr</i>."],
  ["repuerasco", "<big>repuerasco</big>\n\n<b>re–puerasco, —, —, ere</b>\n<b>1)</b> снова становиться ребёнком <i>C</i>;\n<b>2)</b> вести себя как ребёнок, ребячиться <i>Pl, C</i>."],
  ["repugnanter", "<big>repugnanter</big>\n\n<b>repugnanter</b> [repugnans <i>part. praes. к</i> repugno]\nсопротивляясь, неохотно (facere aliquid <i>C</i>)."],
  ["repugnantia", "<big>repugnantia</big>\n\n<b>I repugnantia, ae</b> <i>f</i> [repugno]\n<b>1)</b> сопротивление, средство самозащиты (contra serpentes <i>PM</i>);\n<b>2)</b> противоречие, несогласие, несовместимость (rerum <i>C</i>).\n\n<b>II repugnantia, ium</b> <i>n</i>\nпротиворечия <i>C, Q</i>."],
  ["repugnatio", "<big>repugnatio</big>\n\n<b>repugnatio, onis</b> <i>f</i> [repugno]\nсопротивление, противодействие <i>Ap, CA</i>."],
  ["repugnator", "<big>repugnator</big>\n\n<b>repugnator, oris</b> <i>m</i>\nпротивник <i>Tert</i>."],
  ["repugnatorius", "<big>repugnatorius</big>\n\n<b>repugnatorius, a, um</b> [repugno]\nоказывающий сопротивление, оборонительный (res <i>Vtr</i>)."],
  ["repugno", "<big>repugno</big>\n\n<b>re–pugno, avi, atum, are</b>\n<b>1)</b> противодействовать, сопротивляться, оказывать сопротивление (fortiter <i>Cs</i>; facile est vincere non repugnantes <i>C</i>);\n<b>2)</b> противиться, противоречить, быть против, не соглашаться (alicui in aliqua re <i>PJ</i>; haec repugnant inter se <i>C</i>): invito et repugnanti <i>Hier</i> помимо и (даже) против воли || быть несовместимым (simulatio amicitiae repugnat <i>C</i>)."],
  ["repuli", "<big>repuli</big>\n\n<b>repuli</b> <i>v. l.</i>\n= reppuli."],
  ["repullesco", "<big>repullesco</big>\n\n<b>repullēsco, —, —, ere</b> [re + pullus I] <i>Col</i>\n= repullulo."],
  ["repullulo", "<big>repullulo</big>\n\n<b>re–pullulo, āvī, —, āre</b>\nвновь прорастать, снова давать ростки (e radicibus <i>PM</i>; plurimis ramis <i>Sen</i>)."],
  ["repulsa", "<big>repulsa</big>\n\n<b>repulsa, ae</b> <i>f</i> [repello]\n<b>1)</b> провал, поражение, неудача, безуспешные притязания (на государственную должность): r. consulatus <i>C</i> провал в соискании консульского поста;\n<b>2)</b> отказ, отклонение, отрицательный ответ <i>Prp, O, Sen</i>: repulsam ferre (accipere) <i>C</i> получить отказ."],
  ["repulsio", "<big>repulsio</big>\n\n<b>repulsio, onis</b> <i>f</i>\n<b>1)</b> отражение (violentiae per vim <i>Is</i>);\n<b>2)</b> опровержение <i>CA, Aug</i>."],
  ["repulso", "<big>repulso</big>\n\n<b>repulso, —, —, are</b> [<i>intens. к</i> repello]\n<b>1)</b> отражать (verba <i>Lcr</i>);\n<b>2)</b> отвергать, отклонять (vera dicta <i>Lcr</i>);\n<b>3)</b> отталкивать, отбивать (hostiles impetus <i>Ambr</i>)."],
  ["repulsor", "<big>repulsor</big>\n\n<b>repulsor, oris</b> <i>m</i>\nдающий отпор <i>Ambr</i>."],
  ["repulsorium", "<big>repulsorium</big>\n\n<b>repulsorium, i</b> <i>n</i>\nсредство (само)защиты <i>Ambr</i>."],
  ["repulsorius", "<big>repulsorius</big>\n\n<b>repulsorius, a, um</b> [repello]\nдающий отпор, отбивающий нападение (cohortes <i>Ambr</i>)."],
  ["repulsus", "<big>repulsus</big>\n\n<b>I repulsus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> repello;\n<b>2.</b> <i>adj.</i> отдалённый, удалённый, далёкий (publicis negotiis repulsior <i>Cato</i>): nimis a vera ratione r. <i>Lcr</i> слишком уж далёкий от здравого смысла.\n\n<b>II repulsus, us</b> <i>m</i> [repello]\n<b>1)</b> сопротивление (materiae <i>C</i>);\n<b>2)</b> отражение (effigies repulsu rejecta <i>Lcr</i>);\n<b>3)</b> стук, стучание (dentium <i>PM</i>); грохот, бряцание (rauci repulsus umbonum <i>Cld</i>)."],
  ["repumicatio", "<big>repumicatio</big>\n\n<b>repumicatio, onis</b> <i>f</i> [re + pumico]\nповторное сглаживание, срезание (gemmarum <i>PM</i>)."],
  ["repungo", "<big>repungo</big>\n\n<b>re–pungo, —, —, ere</b>\nснова колоть, <i>перен.</i> уязвлять (animum <i>C</i>)."],
  ["repurgium", "<big>repurgium</big>\n\n<b>repurgium, i</b> <i>n</i> [repurgo]\nновая очистка <i>CTh, CJ</i>."],
  ["repurgo", "<big>repurgo</big>\n\n<b>re–purgo, avi, atum, are</b>\n<b>1)</b> снова чистить, очищать, расчищать (iter <i>L</i>); полоть (agrum <i>Sen</i>);\n<b>2)</b> прояснять (caelum repurgatum <i>O</i>);\n<b>3)</b> удалять, устранять (quidquid in Aenea fuerat mortale <i>O</i>)."],
  ["reputatio", "<big>reputatio</big>\n\n<b>reputatio, onis</b> <i>f</i> [reputo]\n<b>1)</b> обдумывание, размышление (alicujus rei <i>PM, PJ, T etc.</i>);\n<b>2)</b> исчисление, расчёт (usurae reputatae <i>Dig</i>)."],
  ["reputesco", "<big>reputesco</big>\n\n<b>re–putesco, —, —, ere</b>\nстановиться зловонным, загнивать <i>Tert</i>."],
  ["reputo", "<big>reputo</big>\n\n<b>re–puto, avi, atum, are</b>\n<b>1)</b> исчислять, вычислять (defectiones solis <i>C</i>; annos <i>PM</i>);\n<b>2)</b> обдумывать, размышлять, взвешивать, соображать (aliquid secum <i>Nep</i> <i>или</i> cum animo suo <i>Sl, Ap</i>)."],
  ["requaero", "<big>requaero</big>\n\n<b>requaero</b> <i>Pl</i>\n(<i>v. l.</i>) = requiro."],
  ["requierunt", "<big>requierunt</big>\n\n<b>requierunt</b> <i>V</i>\n= requieverunt (<i>pf. к</i> requiesco)."],
  ["requies", "<big>requies</big>\n\n<b>requies, etis</b> (<i>acc.</i> requiem <i>и</i> requietem, <i>abl.</i> requie <i>и</i> requiete) <i>f</i> [requiesco]\nпокой, отдых, успокоение (animi, corporis <i>C</i>): r. curarum <i>C</i> отдохновение от забот."],
  ["requiesco", "<big>requiesco</big>\n\n<b>re–quiesco, quievi, quietum, ere</b>\n<b>1)</b> отдыхать (in sella <i>C</i>; sub umbra <i>V</i>; a strepitu <i>L</i>; a rei publicae muneribus <i>C</i>); покоиться (molli somno <i>Ctl</i>; in sepulcro <i>C</i>): hic requiescit... (<i>в надгробиях</i>) <i>Pt</i> здесь покоится... || спать (nullam partem noctis <i>C</i>); успокаиваться, находить успокоение (ex miseriis <i>Sl</i>; in aliqua re <i>C, Q</i>);\n<b>2)</b> давать отдых, останавливать (cursus <i>V</i>)."],
  ["requiesse", "<big>requiesse</big>\n\n<b>requiesse</b> <i>C</i>\n= requievisse (<i>inf. pf. к</i> requiesco)."],
  ["requietio", "<big>requietio</big>\n\n<b>requietio, onis</b> <i>f</i> <i>Vlg, Eccl</i>\n= requies."],
  ["requietus", "<big>requietus</big>\n\n<b>requietus, a, um</b> [requiesco]\n<b>1)</b> отдохнувший (miles <i>L</i>);\n<b>2)</b> находившийся под паром (ager <i>Col, O</i>);\n<b>3)</b> долго лежавший, старый (caseus <i>Col</i>)."],
  ["requirito", "<big>requirito</big>\n\n<b>requirito, —, —, are</b> [<i>intens. к</i> requiro]\nрасспрашивать, выведывать (res novas <i>Pl</i>)."],
  ["requiro", "<big>requiro</big>\n\n<b>requiro, quisivi, quisitum, ere</b> [re + quaero]\n<b>1)</b> отыскивать, подыскивать, искать (aliquem <i>Pl</i>; libros <i>C</i>): qui vera requirunt <i>Lcr</i> искатели правды;\n<b>2)</b> устремляться, держать путь (r. portus <i>V</i>);\n<b>3)</b> не видеть, не находить, не обнаруживать (prudentiam in aliqua re <i>C</i>); недосчитываться (multos, quos quondam vidi <i>O</i>);\n<b>4)</b> иметь необходимость, нуждаться, требовать (misericordiam <i>PS</i>): hoc requirit magnam diligentiam <i>Cs</i> это требует большой тщательности || <i>pass.</i> requiri быть нужным, требоваться (in bello multae virtutes requiruntur <i>V</i>);\n<b>5)</b> стараться узнать, расспрашивать, осведомляться (aliquid ab <i>или</i> ex aliquo <i>C etc.</i>; r. de aliqua re <i>C etc.</i>): recte requiris <i>C</i> твой вопрос законен;\n<b>6)</b> исследовать (rationes earum rerum, quas semper videmus <i>C</i>)."],
  ["requisita", "<big>requisita</big>\n\n<b>requisita, orum</b> <i>n</i> [requiro]\nпотребности, надобности (ad r. respondere <i>Q</i>): r. naturae <i>Sl, Spart etc.</i> естественные надобности."],
  ["requisitio", "<big>requisitio</big>\n\n<b>requisitio, onis</b> <i>f</i> [requiro]\nисследование (r, historiae antiquioris <i>AG</i>)."],
  ["requoquo", "<big>requoquo</big>\n\n<b>requoquo</b> <i>V</i>\n= recoquo."],
  ["rere", "<big>rere</big>\n\n<b>rere</b>\n= reris (<i>2 л. praes. sg. к</i> reor)."],
  ["reri", "<big>reri</big>\n\n<b>reri</b> <i>inf. к</i>\nreor."],
  ["res", "<big>res</big>\n\n<b>res, rei</b> (<i>редко</i> <i>Lcr</i> <b>rei</b>, <i>у него же иногда</i> rei <i>односложно</i>) <i>f</i>\n<b>1)</b> вещь, предмет: res cibi <i>Ph</i> съестное; res bonae <i>Nep</i> лакомства, <i>но тж.</i> <i>Pl, C etc.</i> ценные вещи, ценности; quid hoc rei est? <i>C</i> что это значит?;\n<b>2)</b> <i>pl.</i> мир, вселенная, природа (rerum contemplatio <i>CC, Q</i>): rerum natura <i>Lcr, C</i> сущность мира, природа (вселенной); caput rerum <i>O</i> мировая столица || <i>при</i> <i>superl.</i> rerum во всём свете, изо всех на свете (animal maximum rerum <i>PM</i>): dulcissime rerum! <i>H</i> милейший (мой)!;\n<b>3)</b> обстоятельство (<i>обычно не переводится</i>): hac re palam facta <i>C, L</i> когда это стало известно; quibus (<i>или</i> his) rebus <i>Cs etc.</i> вследствие чего (этого); de ea re <i>C etc.</i> об этом, по данному вопросу; multarum rerum cupidus <i>Sen</i> жаждущий многого; (in) omnibus rebus <i>Cs etc.</i> во всём; omnium rerum desperatio <i>C</i> полное отчаяние; res efficientes (= causae) <i>C</i> действующие причины; res effectae <i>C</i> действия (причин), следствия;\n<b>4)</b> состояние, положение, дела, обстоятельства (si res poscat, cogat <i>или</i> exigat <i>Cs, H, Just</i>): domi res tranquillae <i>T</i> внутри страны всё (было) спокойно; pro (<i>или</i> e) re nata <i>C, Ter</i> <i>или</i> pro re <i>C, Cs, L etc.</i> (<i>тж.</i> pro tempore et pro re <i>Cs</i> <i>и</i> ex re et ex tempore <i>C</i>) в зависимости от обстоятельств; summa rerum <i>Nep</i> вся совокупность обстоятельств, общее положение, <i>но тж.</i> <i>Cs</i> общая сумма, итог; res summae важнейшие дела (de summis rebus consilia capere <i>Nep</i>); quoquo modo se res habet <i>C</i> как бы то ни было; utraque re <i>Cs</i> в обоих отношениях; res secundae (florentes, prosperae) <i>Pl, C, H, L, Nep etc.</i> процветание, преуспеяние; res adversae (afflictae, miserae) <i>Pl, C, Cs, Sl</i> бедственное положение, несчастье; rem renuntiare <i>C</i> сообщить о положении вещей; omnibus (in) rebus (tota re) <i>C, Cs etc.</i> во всех отношениях; res venit ad arma <i>L</i> (дело) дошло до вооружённой схватки; in utraque re <i>C</i> в обоих случаях; sua re causa facere <i>Pt euph.</i> отправлять естественные надобности;\n<b>5)</b> факт, действительное положение, действительность (non re, sed opinione <i>C</i>): re vera <i>C</i> в действительности <i>или</i> поистине; rem loqui <i>Ter</i> говорить дело, <i>т. е.</i> правильно; re ipsa <i>C, Nep</i> на деле;\n<b>6)</b> сущность, суть (ad rem pertinere <i>C</i>; de re magis, quam de verbis, laborare <i>C</i>): quid ad rem? <i>C etc.</i> что тут существенного? (<i>т. е.</i> не всё ли равно?); sed hoc minus ad rem (<i>sc.</i> pertinet) <i>C</i> но это не так важно;\n<b>7)</b> содержание: res et ordo <i>H</i> содержание и порядок (форма);\n<b>8)</b> причина, основание: qua (hac, ea) re <i>или</i> ob eam (hanc) rem <i>Pl, C etc.</i> по этой причине, поэтому;\n<b>9)</b> деловые отношения, дело (rem gerere <i>C, H etc.</i>; rem cum aliquo transigere <i>C</i>): r. militaris (bellica) <i>C</i> военное дело, <i>но тж.</i> <i>H</i> воинский подвиг; r. navalis <i>или</i> nautica <i>C</i> мореплавание; r. frumentaria <i>C, Cs</i> продовольственное снабжение; r. rustica <i>Vr, Col, C</i> сельское хозяйство; r. pecuaria <i>Q</i> скотоводство; res divinae <i>C etc.</i> религия, религиозные обряды;\n<b>10)</b> судебное дело, процесс (rem judicare <i>C</i>);\n<b>11)</b> (<i>тж.</i> <i>pl.</i>) государство (r. Romana <i>L, H</i>; r. publica <i>или</i> respublica <i>C etc.</i>; rem restituere <i>Enn ap. C</i>; res Graecae <i>Nep</i>) <i>или</i> политическая деятельность (in media re publica versari <i>C</i>): res novae <i>Sl</i> государственный переворот; in re publica peccare <i>C</i> совершать политические ошибки;\n<b>12)</b> выгода, польза, интересы: aliquid in rem suam convertere <i>C</i> использовать что-л. в своих интересах; non ab re visum est <i>L</i> казалось не лишённым целесообразности; profecto rem habes nullam, haec negotia multarum nundinarum fore <i>C</i> ты, конечно, не заинтересован в том, чтобы это дело затянулось надолго; in rem fore credens <i>Sl</i> полагая целесообразным; ab re esse <i>Pl, L, Su</i> быть невыгодным; ab re consulere <i>Pl</i> давать дурные советы;\n<b>13)</b> имущество, состояние, достояние, добро (horno sine re <i>C</i>; r. amicos invenit <i>Pl</i>): r. familiaris <i>Sl, Q, Nep etc.</i> <i>или</i> res privatae <i>C</i> частная собственность; rem facere <i>Ter, H</i> наживать состояние; possessiones et res <i>Cs</i> недвижимое и движимое имущество; in tenui re <i>H</i> в бедности;\n<b>14)</b> власть, господство: summa rerum <i>L, Nep</i>, res maximae <i>Nep</i> <i>и</i> summae res <i>Nep</i> верховная власть (<i>ср.</i> 16); rerum potiri <i>Nep</i> захватить государственную власть;\n<b>15)</b> действие, деяние, дело: res gestae <i>Cs etc.</i> дела, подвиги;\n<b>16)</b> война (cum aliquo <i>L etc.</i>); сражение, битва (gladio comminus rem gerere <i>Cs</i>): summa rerum <i>L</i> генеральное сражение (<i>ср.</i> 14);\n<b>17)</b> случай, событие, явление, факт (r. inopinata <i>C</i>); <i>pl.</i> история (res populi Romani perscribere <i>L</i>, <i>тж.</i> res Romanas scribere <i>C</i>): res Neronis <i>T</i> история царствования Нерона; veteres res <i>C</i> древняя история; rerum scriptor <i>C, Sl</i> историк."],
  ["resacro", "<big>resacro</big>\n\n<b>re–sacro, —, —, are</b>\nснять проклятие, освободить от проклятия (aliquem <i>Nep</i>)."],
  ["resaevio", "<big>resaevio</big>\n\n<b>re–saevio, —, —, ire</b>\nснова свирепствовать, вновь буйствовать (ira resaevit <i>O</i>)."],
  ["resalutatio", "<big>resalutatio</big>\n\n<b>re–salutatio, onis</b> <i>f</i>\nответ на приветствие, ответный поклон <i>Su, Aug</i>."],
  ["resaluto", "<big>resaluto</big>\n\n<b>re–saluto, —, —, are</b>\nотвечать на поклон, на приветствие (aliquem <i>C, Sen, M etc.</i>)."],
  ["resalvo", "<big>resalvo</big>\n\n<b>re–salvo, —, —, are</b>\nвновь спасать (aliquem <i>Eccl</i>)."],
  ["resanesco", "<big>resanesco</big>\n\n<b>re–sanesco, sanui, —, </b>\nопять выздоравливать, поправляться, <i>перен.</i> образумиться (animi resanuit error <i>O</i>)."],
  ["resano", "<big>resano</big>\n\n<b>re–sano, —, —, are</b>\nизлечивать (debiles <i>Lact</i>); <i>перен.</i> исправлять (impios <i>Lact</i>)."],
  ["resarcio", "<big>resarcio</big>\n\n<b>re–sarcio, sarsi, sartum, ire</b>\n<b>1)</b> чинить, исправлять (tecta <i>L</i>; vestem <i>Ter</i>);\n<b>2)</b> возмещать, пополнять (detrimentum brevi tempore <i>Cs</i>; damnum liberalitate <i>Su</i>)."],
  ["rescindo", "<big>rescindo</big>\n\n<b>re–scindo, scidi, scissum, ere</b>\n<b>1)</b> вновь разрывать, вскрывать (ulcus ferro <i>V</i>); растравлять, бередить (vulnus <i>H, O</i>; cicatricem <i>H</i>; luctum <i>O</i>); нарушать, расстраивать (gratiam <i>H</i>); разламывать, разрушать, сносить (pontem, vallum <i>Cs</i>; tecta domosque <i>Fl</i>); срывать (vestem e membris <i>Tib</i>); прокладывать силой, пробивать (viam <i>Lcr</i>, locum firmatum <i>C</i>);\n<b>2)</b> отменять, уничтожать, объявлять недействительным (judicia, testamentum, praeturam <i>C</i>): r. quod sit factum legibus <i>Ter</i> отменить то, что установлено законами."],
  ["rescio", "<big>rescio</big>\n\n<b>re–scio, —, —, ire</b>\n(раз)узнать (aliquid <i>AG</i>)."],
  ["rescisco", "<big>rescisco</big>\n\n<b>re–scisco, scivi (scii), scitum, ere</b>\nузнавать (aliquid <i>Pl, Ter, C etc.</i>); получать сведения (de adventu alicujus <i>Nep</i>)."],
  ["rescissio", "<big>rescissio</big>\n\n<b>rescissio, onis</b> <i>f</i> [rescindo]\nотмена, аннулирование (emptionis, usucapionis <i>Dig</i>); уничтожение, устранение (mortis <i>Tert</i>)."],
  ["rescissorius", "<big>rescissorius</big>\n\n<b>rescissorius, a, um</b> [rescissio]\nкасающийся отмены <i>или</i> расторжения (<i>каких-л. правовых отношений</i>) (actio <i>Dig</i>)."],
  ["rescribo", "<big>rescribo</big>\n\n<b>re–scribo, scripsi, scriptum, ere</b>\n<b>1)</b> писать в ответ, письменно отвечать <i>C, O etc.</i> (r. epistulam ad aliquem <i>C</i>): r. litteris <i>или</i> ad litteras <i>C</i> отвечать на письмо;\n<b>2)</b> снова писать, переписывать, переделывать, перерабатывать (commentarius <i>Su</i>);\n<b>3)</b> письменно возражать (libro alicujus <i>Su</i>);\n<b>4)</b> снова набирать (legiones <i>L</i>): r. aliquem ad equum <i>Cs</i> перевести кого-л. в конницу <i>или</i> в сословие всадников;\n<b>5)</b> переложить на музыку (r. vocum sonis <i>C</i>);\n<b>6)</b> делать денежный перевод, переводить в уплату (argentum alicui <i>Ter</i>); уплачивать (aliquid numquam r. posse <i>H</i>); r. reliqua <i>C</i> записывать в остаток (<i>в качестве долга</i>)."],
  ["rescriptio", "<big>rescriptio</big>\n\n<b>rescriptio, onis</b> <i>f</i> <i>Dig</i>\n= rescriptum."],
  ["rescriptum", "<big>rescriptum</big>\n\n<b>rescriptum, i</b> <i>n</i> [rescribo]\nрескрипт, императорский указ <i>PJ, T, Dig</i>."],
  ["rescula", "<big>rescula</big>\n\n<b>rescula</b> <i>Ap</i>\n= recula."],
  ["resculpo", "<big>resculpo</big>\n\n<b>re–sculpo, sculpsi, —, ere</b>\nпреобразовывать, (воз)обновлять (aliquid <i>Eccl</i>)."],
  ["resecavi", "<big>resecavi</big>\n\n<b>resecavi</b> <i>Eccl</i>\n= resecui (<i>pf. к</i> reseco)."],
  ["resecis", "<big>resecis</big>\n\n<b>resecis</b> <i>gen. к</i>\nresex."],
  ["reseco", "<big>reseco</big>\n\n<b>re–seco, secui</b> (<b>secavi</b> <i>Eccl</i>), <b>sectum, are</b>\n<b>1)</b> обрезывать (capillos <i>O</i>; ungues <i>VM</i>): r. aliquid ad vivum <i>C</i> обрезать что-л. до мяса, <i>перен.</i> принимать (понимать) в слишком строгом смысле; de vivo r. <i>C</i> тратить за счёт основного капитала;\n<b>2)</b> жать, убирать: resecanda falce humus <i>O</i> ждущая серпа земля;\n<b>3)</b> устранять, удалять (nimia <i>C</i>);\n<b>4)</b> унимать, сдерживать, ограничивать (libidinem <i>C</i>): spatio brevi spem longam r. <i>H</i> умерять далеко идущие ожидания в соответствии с кратковременностью жизни."],
  ["resecro", "<big>resecro</big>\n\n<b>re–secro, avi, atum, are</b> [sacro]\nвновь <i>или</i> настоятельно просить (aliquem <i>Pl</i>): resecro, quod dudum obsecraveram <i>Pl</i> я вновь умоляю, о чём уже молил."],
  ["resectio", "<big>resectio</big>\n\n<b>resectio, onis</b> <i>f</i> [reseco]\nобрезывание, срезывание (<i>sc.</i> vitium <i>Col</i>)."],
  ["reseda", "<big>reseda</big>\n\n<b>reseda, ae</b> <i>f</i> [resedo]\nрезеда <i>PM</i>."],
  ["resedi", "<big>resedi</big>\n\n<b>resedi</b> <i>pf. к</i>\nresideo <i>и</i> resido."],
  ["resedo", "<big>resedo</big>\n\n<b>resedo, —, —, are</b>\nоблегчать, лечить, исцелять (morbos <i>PM</i>)."],
  ["resegmina", "<big>resegmina</big>\n\n<b>resegmina, um</b> <i>n</i> [reseco]\nобрезки (papyri <i>PM</i>)."],
  ["resemino", "<big>resemino</big>\n\n<b>re–semino, —, —, are</b>\nвновь сеять, вновь производить: se r. <i>O</i> возрождаться."],
  ["resequor", "<big>resequor</big>\n\n<b>re–sequor, secutus (sequutus) sum, sequi</b> <i>depon.</i>\nследовать, <i>перен.</i> отвечать (aliquem dictis <i>O</i>; gemitum resecuta querelis, <i>sc.</i> Echo <i>Aus</i>)."],
  ["reseratus", "<big>reseratus</big>\n\n<b>reseratus, (us) </b> <i>m</i> [resero I]\nоткрывание (voluminis <i>Sid</i>)."],
  ["resero", "<big>resero</big>\n\n<b>I re–sero, avi, atum, are</b>\n<b>1)</b> отпирать (fores <i>Enn</i>; carcerem <i>O</i>; reseratis foribus intrare <i>Pt</i>);\n<b>2)</b> выпускать на волю (reserata aura Favoni <i>Lcr</i>);\n<b>3)</b> открывать, давать доступ (Italiam gentibus <i>L</i>);\n<b>4)</b> открывать, выдавать (secreta <i>VF</i>);\n<b>5)</b> класть начало, начинать (annum <i>O</i>).\n\n<b>II re–sero, sevi, —, ere</b>\n<b>1)</b> вновь сеять, снова сажать (perticam <i>Col</i>);\n<b>2)</b> обсеменять, засаживать (agrum <i>Vr</i>)."],
  ["reservo", "<big>reservo</big>\n\n<b>re–servo, avi, atum, are</b>\n<b>1)</b> сберегать, откладывать, оставлять (aliquid in aliud tempus <i>C, Cs</i>; aliquem ad duriorem casum <i>bAl</i>): quae me ad majora reservo? <i>V</i> чего ещё большего мне остаётся ждать?;\n<b>2)</b> сохранять, удерживать: nihil ad similitudinem hominis r. <i>C</i> не сохранить ничего человеческого;\n<b>3)</b> предназначать, обрекать (alicui rei <i>C, V</i> <i>или</i> ad aliquid <i>C</i>);\n<b>4)</b> хранить, щадить <i>или</i> спасать (aliquem <i>T, Su</i>)."],
  ["reses", "<big>reses</big>\n\n<b>reses, idis</b> <i>adj.</i> [resideo]\n<b>1)</b> остающийся (plebs in urbe r. <i>L</i>); стоячий (aqua <i>Vr</i>): r. in corpore <i>Vr</i> неудобоваримый, непереваренный;\n<b>2)</b> вялый, ленивый, бездеятельный (r. ac segnis <i>L</i>); проведённый в бездействии (anni <i>Cld</i>); охладевший, притуплённый (animi <i>V</i>)."],
  ["resevi", "<big>resevi</big>\n\n<b>resevi</b> <i>pf. к</i>\nresero II."],
  ["resex", "<big>resex</big>\n\n<b>resex, ecis</b> <i>m</i> [reseco]\nподрезанная молодая лоза <i>Col</i>."],
  ["resibilo", "<big>resibilo</big>\n\n<b>re–sibilo, —, —, are</b>\nшипеть в ответ, <i>перен.</i> отвечать колкостями <i>Sid</i>."],
  ["resico", "<big>resico</big>\n\n<b>resico</b> <i>Cato</i>\n= reseco."],
  ["resideo", "<big>resideo</big>\n\n<b>re–sideo, sedi, sessum, ere</b> [sedeo]\n<b>1)</b> оставаться (сидеть) на месте (in villa <i>C</i>; alta arbore <i>Ph</i>; in equo <i>O</i>): resideamus, si placet <i>C</i> давайте сядем;\n<b>2)</b> находиться, содержаться (auctoritas residet in aliqua re <i>C</i>); сохраняться, оставаться (amor in aliquo residet erga aliquem <i>C</i>);\n<b>3)</b> справлять (ferias <i>Pl</i>): resideri mortuis <i>C</i> быть справляемым в честь усопших."],
  ["resido", "<big>resido</big>\n\n<b>re–sido, sedi, sessum, ere</b>\n<b>1)</b> садиться (aves residunt <i>PM</i>);\n<b>2)</b> останавливаться <i>или</i> селиться, поселяться (in villa <i>C</i>);\n<b>3)</b> опускаться, оседать (montes residunt <i>C</i>); понижаться, мелеть (flumina residunt <i>O</i>);\n<b>4)</b> льнуть, прилипать, облегать (residunt cruribus pelles <i>H</i>);\n<b>5)</b> успокаиваться, утихать, прекращаться (impetus ardorque residunt <i>L</i>; bellum resedit <i>V, H</i>; flamma recens resedit parva aqua <i>O</i>);\n<b>6)</b> излечиваться (lien vitiatus residit <i>PM</i>);\n<b>7)</b> ослабевать, утомляться (ex ira <i>V</i>; longiore certamine <i>L</i>)."],
  ["residuum", "<big>residuum</big>\n\n<b>residuum, i</b> <i>n</i> [residuus]\nостаток <i>C, Su etc.</i>"],
  ["residuus", "<big>residuus</big>\n\n<b>residuus, a, um</b> [resideo]\n<b>1)</b> оставшийся, остающийся, сохранившийся (odium <i>C</i>); уцелевший (Catilinae manus <i>Su</i>): residuae pecuniae <i>C</i> недоимки, долг;\n<b>2)</b> ленивый, бездеятельный <i>Acc</i>."],
  ["resignaculum", "<big>resignaculum</big>\n\n<b>resignaculum, i</b> <i>n</i> [resigno]\nсредство раскрытия (alicujus rei <i>Tert</i>)."],
  ["resignatrix", "<big>resignatrix</big>\n\n<b>resignatrix, icis</b> <i>f</i>\nвскрывающая (закрытое), <i>т. е.</i> нарушительница запрета (r. arboris Eva <i>Tert</i>)."],
  ["resigno", "<big>resigno</big>\n\n<b>re–signo, avi, atum, are</b>\n<b>1)</b> снимать печать, распечатывать, вскрывать (litteras <i>C</i>; testamentum <i>H</i>): r. lumina morte (<i>abl.</i> <i>temporis!</i>) <i>V</i> открывать глаза умирающим, <i>т. е.</i> возвращать их к жизни;\n<b>2)</b> открывать, делать известным (fata venientia <i>O</i>);\n<b>3)</b> уничтожать, отменять, объявлять недействительным (fidem tabularum <i>C</i>);\n<b>4)</b> платить, возвращать (quae dedit fortuna <i>H</i>)."],
  ["resilio", "<big>resilio</big>\n\n<b>re–silio, silui, sultum, ire</b> [salio]\n<b>1)</b> прыгать обратно (ranae resiliunt in lacus <i>O</i>); отпрыгивать (ad manipulus <i>L</i>); отскакивать (resilit grando a culmine tecti <i>O</i>); <i>перен.</i> не приставать, отскакивать (crimen resilit ab aliquo <i>C</i>);\n<b>2)</b> сокращаться, сжиматься (in breve spatium <i>O</i>);\n<b>3)</b> <i>юр.</i> отказываться (<i>sc.</i> ab emptione <i>Dig</i>)."],
  ["resimplicatus", "<big>resimplicatus</big>\n\n<b>re–simplicatus, a, um</b> [simplex]\nсложенный вдвое, двойной <i>CA</i>."],
  ["resimus", "<big>resimus</big>\n\n<b>re–simus, a, um</b>\nзагнутый вверх, вздёрнутый (nares <i>O</i>); выгнутый, согнутый (lamina aenea <i>CC</i>)."],
  ["resina", "<big>resina</big>\n\n<b>resina, ae</b> <i>f</i> (<i>греч.</i>)\nсмола, камедь <i>Pl, Cato, CC etc.</i>"],
  ["resinaceus", "<big>resinaceus</big>\n\n<b>resinaceus, a, um</b>\nсмолистый, камедистый (semen <i>PM</i>)."],
  ["resinalis", "<big>resinalis</big>\n\n<b>resinalis, e</b> <i>CA</i>\n= resinaceus."],
  ["resinatus", "<big>resinatus</big>\n\n<b>resinatus, a, um</b> [resina]\n<b>1)</b> приправленный растительными смолами, душистыми камедями (vinum <i>CC, PM, M</i>);\n<b>2)</b> намазанный смолой (<i>для уничтожения волос на теле</i>) (juventus <i>J</i>)."],
  ["resinosus", "<big>resinosus</big>\n\n<b>resinosus, a, um</b> [resina]\nбогатый камедью (ladanum <i>PM</i>); смолистый (odor <i>PM</i>)."],
  ["resipio", "<big>resipio</big>\n\n<b>re–sipio, —, —, ere</b> [sapio]\nиметь (при)вкус, отдавать (vinum resipit picem <i>PM</i>; <i>перен.</i> r. stilum Plautinum <i>AG</i>)."],
  ["resipiscentia", "<big>resipiscentia</big>\n\n<b>resipiscentia, ae</b> <i>f</i>\nраскаяние <i>Lact</i>."],
  ["resipisco", "<big>resipisco</big>\n\n<b>resipisco, ivi (ii)</b> <i>или</i> <b>ui, —, ere</b> [<i>inchoat. к</i> sapio]\n<b>1)</b> вновь прийти в себя, оправиться (vasto aestu <i>Prp</i>);\n<b>2)</b> (<i>об онемевших членах</i>) отойти (resipiscunt partes sopitae <i>Pt</i>);\n<b>3)</b> раскаиваться <i>Eccl</i>;\n<b>4)</b> образумиться, опомниться <i>Pl, Ter, C etc.</i>"],
  ["resistentia", "<big>resistentia</big>\n\n<b>resistentia, ae</b> <i>f</i>\nсопротивление, противодействие <i>Aug</i>."],
  ["resisto", "<big>resisto</big>\n\n<b>re–sisto, stiti, —, ere</b>\n<b>1)</b> останавливаться, делать остановку (in itinere <i>Cs</i>; media in voce <i>V</i>); останавливаться, задерживаться для разговора (cum aliquo <i>C</i>); оставаться (ibi <i>Cs</i>): resiste! <i>Ter</i> погоди!|| задерживаться, застревать (verba resistunt <i>O</i>);\n<b>2)</b> снова становиться на ноги (lapsus restitit <i>C</i>);\n<b>3)</b> противостоять, противодействовать, противиться, сопротивляться (alicui rei, contra <i>или</i> ad aliquid <i>C, Cs etc.</i>); ab nostris resistitur <i>Cs</i> наши (войска) оказывают сопротивление || выдержать натиск, устоять (adversus tempestatem <i>Vr</i>)."],
  ["resolubilis", "<big>resolubilis</big>\n\n<b>resolubilis, e</b> [resolvo]\n<b>1)</b> подверженный разложению (corpus <i>Eccl</i>);\n<b>2)</b> плавящийся, тающий (nives <i>Sid</i>)."],
  ["resolute", "<big>resolute</big>\n\n<b>resolute</b>\nнесдержанно, развязно (decachinnari <i>Tert</i>)."],
  ["resolutio", "<big>resolutio</big>\n\n<b>resolutio, onis</b> <i>f</i> [resolvo]\n<b>1)</b> ослабление, отпускание (lori <i>AG</i>);\n<b>2)</b> разложение (usque ad monadem <i>Macr</i>);\n<b>3)</b> расслабленность, слабость (oculorum, nervorum, alvi <i>CC</i>);\n<b>4)</b> расторжение, аннулирование (venditionis <i>Dig</i>)."],
  ["resolutus", "<big>resolutus</big>\n\n<b>resolutus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> resolvo;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> с отпущенными (не натянутыми) струнами, <i>т. е.</i> не настроенный (lyra <i>Pt</i>);\n<b>2)</b> изнеженный, расслабленный (corpora <i>Col</i>, membra resoluta languore <i>Aug</i>);\n<b>3)</b> необузданный (gaudium <i>Sil</i>)."],
  ["resolvo", "<big>resolvo</big>\n\n<b>re–solvo, solvi, solutum, ere</b>\n<b>1)</b> развязывать (nodum <i>CC</i>); распускать (vestem, capillos <i>O</i>); снимать (periscelides <i>Pt</i>); распрягать (equos <i>O</i>); освобождать (aliquem catenis <i>O</i>): luctantem animam nexosque artus r. <i>V</i> освободить борющуюся душу от оков тела (<i>об умирающем</i>);\n<b>2)</b> открывать (ora <i>V</i>; fauces in haec verba <i>O</i>); вскрывать (venas <i>T</i>; epistulam <i>C</i>); проламывать (muros ariete <i>Sil</i>);\n<b>3)</b> разгонять, рассеивать (nebulas <i>O</i>; tenebras <i>V</i>; curas <i>V</i>; tristitiam <i>PM</i>);\n<b>4)</b> распутывать, разъяснять (dolos, <i>sc.</i> Labyrinthi <i>V</i>);\n<b>5)</b> растворять, расплавлять (nivem <i>O</i>; aurum <i>L</i>): resolutum flumen <i>Su</i> вскрывшаяся река;\n<b>6)</b> разрыхлять, вскапывать (terram <i>Col</i>);\n<b>7)</b> расслаблять, изнеживать (corpus somno <i>O</i>); парализовать (membrum resolutum <i>CC</i>); ослаблять, разрушать (disciplinam militarem <i>T</i>);\n<b>8)</b> отменять, уничтожать (vectigalia <i>T</i>); прекращать (litem lite <i>H</i>); нарушать (jura pudoris <i>V</i>);\n<b>9)</b> распрямлять (terga <i>V</i>);\n<b>10)</b> обратно выплачивать, платить (pecuniam <i>Pl, Cato, Dig</i>);\n<b>11)</b> отменять, расторгать (transactio resoluta <i>Dig</i>). — <i>См. тж.</i> resolutus."],
  ["resonabilis", "<big>resonabilis</big>\n\n<b>resonabilis, e</b> [resono]\nдающий отзвук, откликающийся, отзывающийся (echo <i>O, Aus</i>)."],
  ["resonantia", "<big>resonantia</big>\n\n<b>resonantia, ae</b> <i>f</i> [resono]\nотголосок, отзвук <i>Vtr</i>."],
  ["resono", "<big>resono</big>\n\n<b>I re–sono, ui</b> <i>и</i> <b>avi, —, are</b>\n<b>1)</b> звучать в ответ, откликаться (gloria virtuti resonat <i>C</i>); давать отголосок (locus resonat <i>H</i>); оглашаться (magnis plangoribus F); звучать (arbusta cicadis resonant <i>V</i>; nervi resonant <i>C</i>); трещать <i>или</i> гудеть (ignis resonans caminis, <i>sc.</i> Aetnae <i>SenT</i>); жужжать (examina e quercu resonant <i>V</i>);\n<b>2)</b> оглашать (lucos cantu <i>V</i>);\n<b>3)</b> произносить, издавать звук: r. aliquem <i>V</i> повторять чьё-л. имя; r. triste <i>H</i> издавать печальный звук.\n\n<b>II resono, —, —, ere</b> <i>Pac, Acc, Enn</i>\n= resono I."],
  ["resonus", "<big>resonus</big>\n\n<b>resonus, a, um</b>\nзвучащий в ответ, дающий отзвук, откликающийся (voces <i>O</i>; ripae <i>Sil</i>)."],
  ["resorbeo", "<big>resorbeo</big>\n\n<b>re–sorbeo, —, —, ere</b>\nпроглатывать, поглощать (pontus resorbet saxa <i>V</i>); втягивать в себя (spiritum <i>Q</i>): mare accrescit aut resorbetur <i>T</i> на море то прилив, то отлив."],
  ["respectio", "<big>respectio</big>\n\n<b>respectio, onis</b> <i>f</i> [respicio]\nобозрение, смотр <i>Vlg</i>."],
  ["respecto", "<big>respecto</big>\n\n<b>re–specto, avi, atum, are</b> [<i>intens. к</i> respicio]\n<b>1)</b> глядеть назад, оглядываться (ad aliquid <i>L</i>);\n<b>2)</b> смотреть, глядеть (alius alium respectantes <i>T</i>);\n<b>3)</b> принимать во внимание, считаться (r. amorem alicujus <i>Ctl</i>);\n<b>4)</b> ожидать (aliquid ab aliquo <i>C</i>)."],
  ["respectus", "<big>respectus</big>\n\n<b>I respectus, a, um</b> <i>part. pf. к</i>\nrespicio.\n\n<b>II respectus, us</b> <i>m</i> [respicio]\n<b>1)</b> глядение назад: sine respectu fugere <i>L</i> бежать без оглядки; r. alicujus rei <i>C</i> взгляд назад на что-л.;\n<b>2)</b> уважение (respectum amicitiae habere <i>L</i>);\n<b>3)</b> точка зрения: respectu alicujus rei <i>L</i> с точки зрения (в отношении) чего-л.;\n<b>4)</b> прибежище, защита, поддержка (respectum ad aliquem habere <i>C</i>)."],
  ["respergo", "<big>respergo</big>\n\n<b>re–spergo, spersi, spersum, ere</b> [spargo]\nокроплять, обрызгивать, опрыскивать (aliquid aliqua re <i>C, L, Ctl etc.</i>; oleo convivas <i>Pt</i>); probro respersus <i>T</i> покрытый позором."],
  ["respersio", "<big>respersio</big>\n\n<b>respersio, onis</b> <i>f</i> [respergo]\nокропление, обрызгивание, опрыскивание <i>C</i>."],
  ["respersus", "<big>respersus</big>\n\n<b>respersus, (us) </b> <i>m</i> <i>PM</i>\n= respersio."],
  ["respicio", "<big>respicio</big>\n\n<b>re–spicio, spexi, spectum, ere</b> [specio]\n<b>1)</b> смотреть назад, оглядываться, озираться, поворачиваться (retro <i>C</i>): r. in aliquem <i>Ap</i> <i>или</i> ad aliquem <i>Ter etc.</i> оглядываться на кого-л.; respiciens Caesarem <i>Cs</i> повернувшись к Цезарю; r. spatium praeteriti temporis <i>C</i> вспоминать прошлое;\n<b>2)</b> принимать во внимание, иметь в виду, учитывать (aetatem alicujus <i>Ter</i>);\n<b>3)</b> иметь попечение, заботиться (r. commoda populi <i>C</i>; rem publicam <i>C</i>);\n<b>4)</b> надеяться, ожидать (aliquid ab aliquo <i>L</i>);\n<b>5)</b> видеть, наблюдать (r. volitare favillam <i>V</i>);\n<b>6)</b> относиться, касаться, принадлежать: ad hunc summa imperii respiciebat <i>Cs</i> в его (Бибула) руках находилось высшее командование."],
  ["respiramen", "<big>respiramen</big>\n\n<b>respiramen, inis</b> <i>n</i> [respiro]\nдыхательный канал, трахея <i>O</i>."],
  ["respiramentum", "<big>respiramentum</big>\n\n<b>respiramentum, i</b> <i>n</i>\nпередышка, возможность перевести дух (nusquam erat laxamentum et r. <i>Aug</i>)."],
  ["respiratio", "<big>respiratio</big>\n\n<b>respiratio, onis</b> <i>f</i> [respiro]\n<b>1)</b> дыхание (respirationem requirere <i>C</i>; acerbissimae respirationes <i>Su</i>);\n<b>2)</b> передышка, остановка, отдых (pugnare sine respiratione <i>L</i>);\n<b>3)</b> испарение (aquarum <i>C</i>)."],
  ["respiratus", "<big>respiratus</big>\n\n<b>respiratus, (us) </b> <i>m</i> <i>C, Ap</i>\n= respiratio 1."],
  ["respiro", "<big>respiro</big>\n\n<b>re–spiro, avi, atum, are</b>\n<b>1)</b> выдыхать, испускать (animam <i>C</i>; malignum aera <i>St</i>);\n<b>2)</b> ослабевать, уменьшаться (avaritia respirat <i>C</i>): ne punctum quidem temporis oppugnatio respiravit <i>C</i> штурм не ослабевал ни на минуту;\n<b>3)</b> переводить дыхание: sine respirem <i>Pl</i> дай дух перевести || <i>перен.</i> приходить в себя, опомниться, оправляться (a metu <i>C</i>)."],
  ["resplendentia", "<big>resplendentia</big>\n\n<b>resplendentia, ae</b> <i>f</i>\nблеск, сияние, свет (veritatis <i>Aug</i>)."],
  ["resplendeo", "<big>resplendeo</big>\n\n<b>re–splendeo, dui, —, ere</b>\nдавать отблеск, отражать свет, блистать, сиять <i>V, SenT, Sil etc.</i>"],
  ["respondeo", "<big>respondeo</big>\n\n<b>re–spondeo, spondi, sponsum, ere</b>\n<b>1)</b> отвечать (alicui aliquid <i>Ter etc.</i>): r. ad aliquid <i>или</i> alicui rei <i>C etc.</i> отвечать на что-л.; r. jus <i>или</i> de jure <i>C</i> давать советы <i>или</i> заключение по судебным делам; male respondent coacta ingenia <i>Sen</i> мысль плохо реагирует на принуждение; criminibus r. <i>C</i> защищаться от обвинений || откликаться при перекличке, отзываться (ad nomina <i>L</i>);\n<b>2)</b> являться (ad tempus <i>Sen</i> — <i>ср.</i> 3);\n<b>3)</b> платить: r. nominibus <i>C</i> платить долги; r. ad tempus <i>C</i> платить в срок (<i>ср.</i> 2);\n<b>4)</b> соответствовать (ad mensuram pondusque <i>Sen</i>); согласовываться (verba verbis respondent <i>C</i>): r. gloriae alicujus <i>C</i> не уступать чьей-л. славе;\n<b>5)</b> отражаться (sidera respondent in aqua <i>Lcr</i>);\n<b>6)</b> (<i>о земле</i>) отвечать (<i>на труд земледельца или землекопа</i>) (humus colono respondet <i>Col</i>): (opulentissima metalla) assidue plenius responsura fodienti <i>Sen</i> металлы достаются в наибольшем количестве тому, кто усердно копает; cum quarto r. <i>Col</i> давать урожай сам-четвёрт;\n<b>7)</b> находиться против, быть расположенным по другую сторону (contra respondet tellus <i>V</i>);\n<b>8)</b> быть в порядке, действовать нормально (venter alicui non respondet <i>Pt</i>)."],
  ["responsativus", "<big>responsativus</big>\n\n<b>responsativus, a, um</b>\nответный (sententiae <i>Is</i>)."],
  ["responsio", "<big>responsio</big>\n\n<b>responsio, onis</b> <i>f</i> [respondeo]\nответ, <i>тж.</i> возражение <i>Pl, C, Q, Dig</i>."],
  ["responsito", "<big>responsito</big>\n\n<b>responsito, avi, —, are</b> [<i>intens. к</i> respondeo]\nдавать заключение по судебным вопросам <i>C, AG, Dig</i>."],
  ["responso", "<big>responso</big>\n\n<b>responso, —, —, are</b> [<i>intens. к</i> respondeo]\n<b>1)</b> отвечать (alicui <i>Pl</i>); давать многократный отзвук (ripae circa responsant <i>V</i>);\n<b>2)</b> соответствовать, подходить (alicui rei <i>H</i>);\n<b>3)</b> не уступать, противиться (cupidinibus <i>H</i>): r. cenis opimis <i>H</i> устоять перед соблазном роскошных трапез."],
  ["responsor", "<big>responsor</big>\n\n<b>responsor, oris</b> <i>m</i> [respondeo]\nдающий ответ <i>Pl</i>."],
  ["responsorium", "<big>responsorium</big>\n\n<b>responsorium, i</b> <i>n</i> (<i>тж.</i> responsorius cantus) <i>культ.</i>\nреспонсорий, ответная партия (<i>в антифона льном, т. е. попеременном пении двух хоров</i>) <i>Eccl</i>."],
  ["responsum", "<big>responsum</big>\n\n<b>responsum, i</b> <i>n</i> [respondeo]\n<b>1)</b> ответ (r. dare <i>или</i> reddere <i>C, L etc.</i>);\n<b>2)</b> заключение, мнение, суждение (judicum <i>C</i>);\n<b>3)</b> изречение (oraculi <i>Nep</i>): responsa in pestilentiam data <i>Pt</i> ответы оракула в связи с моровым поветрием."],
  ["responsus", "<big>responsus</big>\n\n<b>responsus, us</b> <i>m</i> [respondeo]\n<b>1)</b> <i>Dig</i> = responsum 2;\n<b>2)</b> (<i>тж.</i> commensuum r. <i>Vtr</i>) соответствие, соразмерность, симметрия <i>Vtr</i>."],
  ["respublica", "<big>respublica</big>\n\n<b>res–publica, reipublicae</b> (<i>слитно или раздельно</i>) <i>f</i>\n<b>1)</b> государственные дела, государственные вопросы, государственная (политическая) деятельность: versari in rebus publicis <i>C</i> заниматься государственными делами; ad rem publicam accedere <i>C, Nep</i> посвятить себя политической деятельности;\n<b>2)</b> государственная собственность, казна (rem publicam dissipare <i>C</i>);\n<b>3)</b> государственные интересы, общественное благо (rei publicae causa <i>C</i>);\n<b>4)</b> государство, республика, общество (rem publicam defendere <i>C</i>); государственное устройство, политический строй (tria genera rerum publicarum <i>C</i>);\n<b>5)</b> политическое положение (in optima re publica <i>C</i>)."],
  ["respuo", "<big>respuo</big>\n\n<b>re–spuo, spui, (sputum), ere</b>\n<b>1)</b> выплёвывать, извергать (reliquias cibi <i>C</i>);\n<b>2)</b> с презрением отвергать, не принимать (condicionem <i>Cs</i>; r. atque in odio habere aliquid <i>O</i>): aliquem auribus r. <i>C</i> не желать слушать кого-л.;\n<b>3)</b> отталкивать, быть непроницаемым (mola ferri ictus respuens <i>PM</i>): omne respuens vulnus <i>PM</i> совершенно неуязвимый."],
  ["restagnatio", "<big>restagnatio</big>\n\n<b>restagnatio, onis</b> <i>f</i> [restagno]\nподъём, выход из берегов, разлив (Euphratis <i>PM</i>)."],
  ["restagno", "<big>restagno</big>\n\n<b>re–stagno, —, —, are</b>\nвыступать из берегов, разливаться (mare restagnans <i>O</i>); быть покрытым водой (locus late restagnat <i>Cs</i>)."],
  ["restauratio", "<big>restauratio</big>\n\n<b>restauratio, onis</b> <i>f</i>\nвосстановление (moenium <i>CJ</i>)."],
  ["restauro", "<big>restauro</big>\n\n<b>restauro, avi, atum, are</b> [re + *stauro, <i>ср.</i> sto]\nвосстанавливать (aedem <i>T</i>); возобновлять (bellum <i>Just</i>; matrimonium <i>Dig</i>)."],
  ["restavi", "<big>restavi</big>\n\n<b>restavi</b> — <i>см.</i>\nresto."],
  ["restiarius", "<big>restiarius</big>\n\n<b>restiarius, i</b> <i>m</i> [restis]\nканатный мастер <i>Fronto</i>."],
  ["restibilio", "<big>restibilio</big>\n\n<b>re–stibilio, —, —, ire</b> [stabilio]\nвосстанавливать (в первоначальном виде) <i>Pac</i>."],
  ["restibilis", "<big>restibilis</big>\n\n<b>restibilis, e</b> [re + stabilis]\nвосстанавливаемый, <i>т. е.</i> вновь приносящий плоды (platanus <i>PM</i>); ежегодно засеваемый (ager <i>Vr</i>)."],
  ["resticula", "<big>resticula</big>\n\n<b>resticula, ae</b> <i>f</i> [<i>demin. к</i> restis]\nверёвка, шнурок <i>Cato, Vr, C</i>."],
  ["resticulus", "<big>resticulus</big>\n\n<b>resticulus, i</b> <i>m</i> <i>Hier</i>\n= resticula."],
  ["restillo", "<big>restillo</big>\n\n<b>re–stillo, avi, atum, are</b>\n<b>1)</b> опять впускать каплями, вливать понемногу (quiddam quasi animulae alicu <i>C</i> — <i>v. l.</i>);\n<b>2)</b> обратно падать каплями <i>Eccl</i>."],
  ["restinctio", "<big>restinctio</big>\n\n<b>restinctio, onis</b> <i>f</i> [restinguo]\nутоление (sitis <i>C</i>)."],
  ["restinguo", "<big>restinguo</big>\n\n<b>re–stinguo, stinxi, stinctum, ere</b>\n<b>1)</b> гасить, тушить (ignem <i>C</i>; incendium <i>Hirt</i>);\n<b>2)</b> умерять, унимать, успокаивать (ardorem <i>C</i>; mentes inflammatas <i>C</i>; consilio et ratione restinctus <i>PJ</i>); охлаждать <i>или</i> разбавлять (Falerni pocula lympha <i>H</i>);\n<b>3)</b> утолять (sitim <i>C</i>);\n<b>4)</b> подавлять (morbum <i>PM</i>); прекращать (studia <i>C</i>); истреблять (cimices <i>PM</i>)."],
  ["restio", "<big>restio</big>\n\n<b>restio, onis</b> <i>m</i> [restis]\n<b>1)</b> канатчик <i>Su</i>;\n<b>2)</b> <i>шутл.</i> тот, кого избивают верёвкой <i>Pl</i>."],
  ["restipulatio", "<big>restipulatio</big>\n\n<b>restipulatio, onis</b> <i>f</i> [restipulor]\nвзаимное обязательство, взаимное обещание <i>C, G</i>."],
  ["restipulor", "<big>restipulor</big>\n\n<b>re–stipulor, —, ari</b> <i>depon.</i>\nвзаимно обязывать, требовать взаимного обязательства <i>или</i> обещания <i>C, VM, G</i>."],
  ["restis", "<big>restis</big>\n\n<b>restis, is</b> <i>f</i>\n<b>1)</b> верёвка, канат <i>Ter, L etc.</i>;\n<b>2)</b> черешок <i>или</i> хвостик (alii <i>PM</i>; caepis <i>M</i>)."],
  ["restiti", "<big>restiti</big>\n\n<b>restiti</b> <i>pf. к</i>\n<b>1)</b> resisto <i>и</i>\n<b>2)</b> resto."],
  ["restito", "<big>restito</big>\n\n<b>restito, —, —, are</b> [<i>intens. к</i> resto]\nостанавливаться, отставать, медлить, колебаться <i>Pl, Ter, L etc.</i>"],
  ["restituo", "<big>restituo</big>\n\n<b>restituo, ui, utum, ere</b> [re + statuo]\n<b>1)</b> снова ставить, ставить на прежнее место (statuam <i>C, Su</i>); вновь приводить в порядок (turbatas comas <i>O</i>; inclinatam aciem <i>Su</i>); вправлять (luxatum femur <i>CC</i>);\n<b>2)</b> возвращать (fagrum alicui <i>L</i>): r. aliquem in patriam <i>L</i> обратно доставить кого-л. <i>или</i> позволить кому-л. вернуться на родину; r. aliquem in regnum <i>C, L, Nep</i> снова возвести кого-л. на престол; r. aliquem <i>Su</i> (in integrum <i>C, Cs</i>) возвратить кому-л. собственность <i>или</i> все права и преимущества; r. aliquem a limine mortis <i>Ctl</i> спасти кого-л., уже погибавшего; aliquem in libertatem r. <i>L</i> вернуть кому-л. свободу; r. aliquem alicui <i>C, L</i> снова склонить кого-л. на чью-л. сторону <i>или</i> вновь соединить с кем-л., подчинить кому-л.;\n<b>3)</b> возобновлять (proelium <i>C, Cs, L</i>); восстанавливать, отстраивать (oppida vicosque <i>Cs</i>; muros <i>Nep</i>); спасать (rem, <i>sc.</i> publicam <i>Enn ap. C</i>); поправлять (rem impeditam et perditam <i>Ter</i>);\n<b>4)</b> возмещать, покрывать (damnum <i>L</i>);\n<b>5)</b> отменять, кассировать, объявлять недействительным (judicia, <i>sc.</i> Verris <i>C</i>; consilia <i>L</i>)."],
  ["restitutio", "<big>restitutio</big>\n\n<b>restitutio, onis</b> <i>f</i> [restituo]\n<b>1)</b> восстановление, реставрация (domus <i>Su</i>): r. urbis in majus <i>Just</i> восстановление и расширение города;\n<b>2)</b> возвращение из ссылки, восстановление во всех правах (<i>sc.</i> in pristinum statum <i>Dig</i>); помилование (damnatorum <i>C</i>);\n<b>3)</b> <i>юр.</i> возмещение, возврат <i>Dig</i>."],
  ["restitutor", "<big>restitutor</big>\n\n<b>restitutor, oris</b> <i>m</i> [restituo]\n<b>1)</b> восстановитель, (воз)обновитель, реставратор (templorum <i>L</i>);\n<b>2)</b> (<i>тж.</i> r. salutis <i>C</i>) избавитель, спаситель <i>Q</i>."],
  ["restitutorius", "<big>restitutorius</big>\n\n<b>restitutorius, a, um</b>\nвосстановительный: actio restitutoria <i>Dig</i> восстановление права на иск."],
  ["restitutrix", "<big>restitutrix</big>\n\n<b>restitutrix, icis</b> <i>f</i>\nвосстановительница (terra omnium receptrix et r. <i>Ap</i>)."],
  ["resto", "<big>resto</big>\n\n<b>re–sto, stiti</b> (<i>у</i> <i>Aug</i> <i>тж.</i> <b>restavi</b>), <b>—, are</b>\n<b>1)</b> оставаться (una spes alicui restat <i>L</i>); оставаться в живых, уцелеть, спастись (r. flammis <i>V</i>); предстоять (aliquid alicui restat <i>Lcr etc.</i>): ire tamen restat <i>H</i> придётся всё же уходить;\n<b>2)</b> сохраняться, длиться (amor restat <i>Prp</i>);\n<b>3)</b> противиться, сопротивляться (summa vi <i>L</i>); не поддаваться (laminae restant adversum pila <i>T</i>)."],
  ["restricte", "<big>restricte</big>\n\n<b>restricte</b> [restrictus]\n<b>1)</b> тщательно, строго, точно (observare, praefinire aliquid <i>C</i>);\n<b>2)</b> скудно, скупо (facere aliquid <i>PJ</i>)."],
  ["restrictio", "<big>restrictio</big>\n\n<b>restrictio, onis</b> <i>f</i> [restringo]\nограничение (epularum <i>Aug</i>)."],
  ["restrictus", "<big>restrictus</big>\n\n<b>restrictus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> restringo;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> строгий (imperium <i>T</i>; judicatio <i>Ap</i>);\n<b>2)</b> узкий, обтянутый (toga <i>Su</i>);\n<b>3)</b> короткий (digiti <i>Su</i>);\n<b>4)</b> скупой (homo <i>C</i>)."],
  ["restringo", "<big>restringo</big>\n\n<b>re–stringo, strinxi, strictum, ere</b>\n<b>1)</b> стягивать, связывать <i>или</i> сковывать (manus manicis <i>Ap</i>): r. manus ad terga <i>PM</i> связывать руки назад; r. ad aliquid <i>PJ, T</i> <i>или</i> alicui rei <i>Ctl</i> привязать к чему-л.;\n<b>2)</b> ограничивать (liberalitatem <i>Sen</i>; sumptus <i>PJ</i>);\n<b>3)</b> сжимать, стеснять (animum maestitia <i>T</i>);\n<b>4)</b> оттягивать, растягивать, <i>т. е.</i> раскрывать (restrictis labellis ridere <i>Ap</i>): r. dentes <i>Pl</i> скалить зубы; r. paralyticos <i>Tert</i> исцелять паралитиков;\n<b>5)</b> отводить назад (laevam <i>Q</i>). — <i>См. тж.</i> restrictus."],
  ["restruo", "<big>restruo</big>\n\n<b>re–struo, struxi, structum, ere</b>\nотстраивать заново (aras <i>Tert</i>); восстанавливать (fidem <i>Tert</i>)."],
  ["resudatio", "<big>resudatio</big>\n\n<b>resudatio, onis</b> <i>f</i>\nиспарина <i>CA</i>."],
  ["resudo", "<big>resudo</big>\n\n<b>re–sudo, —, —, are</b>\n<b>1)</b> (<i>тж.</i> r. umore <i>QC</i>) испускать влагу, увлажняться, отсыревать (solum resudat <i>QC</i>);\n<b>2)</b> выделять, извергать (congeriem ventris <i>Eccl</i>)."],
  ["resulco", "<big>resulco</big>\n\n<b>re–sulco, —, —, are</b>\nвновь вскрывать (vulnus <i>Eccl</i>)."],
  ["resultatio", "<big>resultatio</big>\n\n<b>resultatio, onis</b> <i>f</i> [resulto]\n<b>1)</b> отскакивание, <i>перен.</i> отражение, отзвук, звучание (citharae <i>Eccl</i>);\n<b>2)</b> противодействие <i>Eccl</i>."],
  ["resulto", "<big>resulto</big>\n\n<b>resulto, avi, atum, are</b> [<i>intens. к</i> resilio]\n<b>1)</b> отскакивать (tela galea resultant <i>V</i>); откатываться, отхлынуть (unda resultat <i>Pt</i>); отражаться, отдаваться (imago vocis resultat <i>V</i>);\n<b>2)</b> оглашаться (colles resultant clamore <i>V</i>);\n<b>3)</b> подпрыгивать, быть лишённым плавности (verba brevium syllabarum contextu resuitant <i>Q</i>);\n<b>4)</b> противиться, не подчиняться (nomina barbara resuitant versibus <i>PJ</i>);\n<b>5)</b> отражать (sonum <i>Ap</i>)."],
  ["resumo", "<big>resumo</big>\n\n<b>re–sumo, sumpsi, sumptum, ere</b>\n<b>1)</b> снова брать, опять браться, вновь приниматься (r. librum in manus <i>Q</i>; r. arma <i>T</i>); вновь принимать (speciem alicujus <i>O</i>); вновь надевать (praetextam <i>PJ</i>); поднимать (baculum elapsum <i>Sil</i>);\n<b>2)</b> возобновлять, вновь начинать (pugnam, militiam <i>T</i>);\n<b>3)</b> вновь добывать, снова обретать (vires <i>O</i>; dominationem <i>T</i>);\n<b>4)</b> ставить на ноги, исцелять (aegrotantem <i>CA</i>)."],
  ["resumptio", "<big>resumptio</big>\n\n<b>resumptio, onis</b> <i>f</i> [resumo 4]\nвосстановление здоровья, излечение <i>CA</i>."],
  ["resumptivus", "<big>resumptivus</big>\n\n<b>resumptivus</b>, <i>и</i> <b>resumptorius, a, um</b>\nвосстановительный, целебный (<i>sc.</i> remedia <i>CA</i>)."],
  ["resuo", "<big>resuo</big>\n\n<b>re–suo, (sui), sutum, ere</b>\nрасшивать, пороть (tunica resuta <i>Su</i>)."],
  ["resupino", "<big>resupino</big>\n\n<b>re–supino, avi, atum, are</b>\n<b>1)</b> закидывать, запрокидывать (caput <i>PM</i>); вздёргивать, заворачивать вверх (nares manu <i>Q</i>); повалить на спину (aliquem <i>L, J</i>): resupinari <i>PM</i> ложиться на спину; resupinatus <i>Pt</i> откинувшись (перегнувшись) назад;\n<b>2)</b> срывать, взламывать, силой открывать (valvas <i>Prp</i>); опрокидывать, разрушать (rem omnem <i>Acc</i>);\n<b>3)</b> наполнять гордостью (aliquem <i>Sen</i>)."],
  ["resupinus", "<big>resupinus</big>\n\n<b>re–supinus, a, um</b>\n<b>1)</b> загнутый (vomer <i>PM</i>); закинутый (откинувшийся) назад, запрокинутый (os, collum <i>O</i>); лежащий на спине, навзничь (reclinis et r. <i>Vop</i>);\n<b>2)</b> высокий, высокогорный, находящийся на холме (Elis <i>St</i>);\n<b>3)</b> надменный, гордый (Niobe <i>O</i>);\n<b>4)</b> изнеженный, ленивый (voluptas <i>Q</i>); беспечный (<i>sc.</i> homo <i>Dig</i>)."],
  ["resurgo", "<big>resurgo</big>\n\n<b>re–surgo, surrexi, surrectum, ere</b>\n<b>1)</b> снова вставать, вновь подниматься (somno <i>Ap</i>); вновь восставать (in ultionem <i>T</i>);\n<b>2)</b> выплывать, выныривать (de mediis aquis <i>O</i>); снова восходить (luna resurgit <i>O</i>);\n<b>3)</b> снова возникать, вновь вспыхивать (amor resurgens <i>V</i>; bellum resurgit <i>VP, T</i>); восстанавливаться, возрождаться (urbs resurgit <i>Prp</i>);\n<b>4)</b> воскресать (a mortuis <i>Vlg</i>)."],
  ["resurrectio", "<big>resurrectio</big>\n\n<b>resurrectio, onis</b> <i>f</i> [resurgo]\nвоскресение (ex mortuis <i>Vlg</i>; mortuorum <i>Vlg, Eccl</i>)."],
  ["resuscitatio", "<big>resuscitatio</big>\n\n<b>resuscitatio, onis</b> <i>f</i>\nвоскрешение <i>Tert</i>."],
  ["resuscitator", "<big>resuscitator</big>\n\n<b>resuscitator, oris</b> <i>m</i>\nвоскреситель <i>Tert</i>."],
  ["resuscito", "<big>resuscito</big>\n\n<b>re–suscito, —, —, are</b>\n<b>1)</b> снова пробуждать, опять возбуждать (veterem iram <i>O</i>);\n<b>2)</b> восстанавливать (templum <i>Lact</i>);\n<b>3)</b> воскрешать (r. ex mortuis <i>Eccl</i>);\n<b>4)</b> возобновлять (eandem litem <i>CJ</i>)."],
  ["resutus", "<big>resutus</big>\n\n<b>resutus, a, um</b> <i>part. pf. к</i>\nresuo."],
  ["retae", "<big>retae</big>\n\n<b>retae, arum</b> <i>f</i> [reto]\nрастущие на берегу <i>или</i> в русле реки деревья <i>AG</i>."],
  ["retalio", "<big>retalio</big>\n\n<b>re–talio, —, —, are</b>\nвозмещать, отплачивать (quod factum est <i>AG</i>)."],
  ["retango", "<big>retango</big>\n\n<b>re–tango, —, —, ere</b>\nчасто прикасаться (r. aliquid aliqua re <i>CA</i>)."],
  ["retardatio", "<big>retardatio</big>\n\n<b>retardatio, onis</b> <i>f</i> [retardo]\nзамедление, остановка (r. et mora <i>C</i>)."],
  ["retardo", "<big>retardo</big>\n\n<b>re–tardo, avi, atum, are</b>\n<b>1)</b> замедлять, задерживать (aliquem in via <i>C</i>; equum <i>Ap</i>); <i>med.-pass.</i> отставать, замедляться (motus stellarum retardantur <i>C</i>);\n<b>2)</b> удерживать, мешать (aliquem ab aliqua re <i>C</i>)."],
  ["retaxo", "<big>retaxo</big>\n\n<b>re–taxo, —, —, are</b>\nвновь порицать, осуждать (aliquem <i>Su</i>)."],
  ["rete", "<big>rete</big>\n\n<b>rete, is</b> (<i>abl.</i> e) <i>n</i>\nсеть, невод <i>Ter, Lcr, Vr, C etc.</i>"],
  ["retego", "<big>retego</big>\n\n<b>re–tego, texi, tectum, ere</b>\n<b>1)</b> вскрывать (thecam nummariam <i>C</i>); открывать, обнажать (pectus <i>O</i>; caput <i>Cs ap. PJ</i> — <i>ср.</i> 3): dentes retecti <i>Pers</i> оскаленные зубы || обнажать, вынимать из ножон (ensem Lcre): homo retectus <i>V</i> человек, не прикрытый щитом;\n<b>2)</b> обнаруживать, проливать свет, освещать (radiis r. orbem <i>V</i>); раскрывать, разоблачать (scelus <i>V</i>; arcanum, consilium <i>H</i>);\n<b>3)</b> снова закутывать (caput pallio <i>Pt</i> — <i>ср.</i> 4) <i>или</i> закрывать (argilla plaga retegatur <i>Pall</i>)."],
  ["retempto", "<big>retempto</big>\n\n<b>re–tempto</b> <i>v. l.</i>\n= retento II."],
  ["retendo", "<big>retendo</big>\n\n<b>re–tendo, tendi, tentum (tensum), ere</b>\nспускать, ослаблять (arcum <i>O, Ph</i>); <i>перен.</i> давать отдых (alterna quiete retendi <i>Q</i>)."],
  ["retentator", "<big>retentator</big>\n\n<b>retentator, eris</b> <i>m</i> [retento I]\nудерживающий <i>Eccl</i>."],
  ["retentatrix", "<big>retentatrix</big>\n\n<b>retentatrix, icis</b> <i>f</i> (<i>sc.</i> virtus)\nзадерживающая, закрепляющая <i>Macr</i>."],
  ["retentio", "<big>retentio</big>\n\n<b>retentio, onis</b> <i>f</i> [retineo]\nудерживание, сдерживание, останавливание (aurigae <i>C</i>); воздержание (assensionis <i>C</i>); задержание (pecuniae <i>C</i>); удержание, сохранение (veteris disciplinae <i>Tert</i>); право удержания за собой (dotis <i>Dig</i>); сохранение в силе, <i>т. е.</i> отказ в прощении (r. delicti <i>Tert</i>)."],
  ["retento", "<big>retento</big>\n\n<b>I retento, avi, atum, are</b> [<i>intens. к</i> retineo]\n<b>1)</b> удерживать, сдерживать (fugientes <i>T</i>); останавливать (aliquem <i>Pl etc.</i>; equum <i>Sil</i>);\n<b>2)</b> держать на расстоянии (caelum a terris <i>Lcr</i>);\n<b>3)</b> подавлять (iras <i>VF</i>);\n<b>4)</b> поддерживать, сохранять (vitas <i>C</i>).\n\n<b>II re–tento, avi, atum, are</b>\nснова пытаться, опять приниматься (r. studium <i>O</i>); пробовать (timide verba <i>O</i>); напрягать, упражнять (memoriam suam <i>Sen</i>)."],
  ["retentor", "<big>retentor</big>\n\n<b>retentor, oris</b> <i>m</i> [retineo]\nудерживающий, (крепко) обхватывающий (<i>sc.</i> serpentes <i>Ap</i>)."],
  ["retentorius", "<big>retentorius</big>\n\n<b>retentorius, a, um</b> [retento]\nсдерживающий (virtus animae <i>Eccl</i>)."],
  ["retentus", "<big>retentus</big>\n\n<b>I retentus, a, um</b> <i>part. pf. к</i>\n<b>1)</b> retendo <i>и</i>\n<b>2)</b> retineo.\n\n<b>II retentus, us</b> <i>m</i>\nудерживающее (хватательное) движение (<i>sc.</i> digitorum) <i>Cld</i>."],
  ["retergeo", "<big>retergeo</big>\n\n<b>re–tergeo, tersi, —, ere</b>\nвытирать, очищать (vulnera <i>CA</i>; fossas <i>Amm</i>)."],
  ["retero", "<big>retero</big>\n\n<b>re–tero, (trivi), tritum, ere</b>\nвытирать, стирать (aliquid <i>Naev, Sen</i>)."],
  ["retexo", "<big>retexo</big>\n\n<b>re–texo, texui, textum, ere</b>\n<b>1)</b> распускать (telam <i>C, O, St</i>);\n<b>2)</b> уменьшать, сокращать (luna plenum orbem retexuit <i>O</i>);\n<b>3)</b> перерабатывать, переделывать (scripta <i>H</i>; se <i>Matius ap. C</i>);\n<b>4)</b> делать недействительным, отменить, аннулировать (praeturam alicujus <i>C</i>; jura <i>Man</i>); брать назад (dicta <i>C</i>);\n<b>5)</b> вновь начинать, возобновлять (properata fata <i>O</i>; orbes cursu <i>V</i>); пересказывать (singula ordine <i>Ap</i>)."],
  ["retia", "<big>retia</big>\n\n<b>I retia, ae</b> <i>f</i> <i>Pl, Amm</i>\n= rete.\n\n<b>II retia</b> <i>pl. к</i>\nrete."],
  ["retiaculum", "<big>retiaculum</big>\n\n<b>retiaculum, i</b> <i>n</i>\n<b>1)</b> сеть, невод <i>Vlg, Eccl</i>;\n<b>2)</b> решётка <i>Vlg</i>;\n<b>3)</b> <i>архит.</i> ободок плетёной работы (<i>на колонне</i>) <i>Vlg</i>."],
  ["retiarius", "<big>retiarius</big>\n\n<b>retiarius, i</b> <i>m</i> [rete]\nретиарий, <i>гладиатор, вооружение которого состояло из fuscina (трезубца) <i>и</i> rete</i> <i>VM, O, Su</i>: ferula contra retiarium <i>погов. M</i> (идти) с розгой против хорошо вооружённого бойца."],
  ["reticentia", "<big>reticentia</big>\n\n<b>reticentia, ae</b> <i>f</i> [reticeo]\n<b>1)</b> молчание, умалчивание <i>Pac, Pl, C</i>;\n<b>2)</b> <i>ритор</i>, апосиопеза, фигура умолчания <i>C, Q</i>."],
  ["reticeo", "<big>reticeo</big>\n\n<b>re–ticeo, ticui, —, ere</b> [taceo]\n<b>1)</b> молчать, хранить молчание (de aliqua re <i>C</i>): r. alicui <i>L</i> не отвечать кому-л.;\n<b>2)</b> обходить молчанием, умалчивать, скрывать (aliquid <i>Pl, Ter, C etc.</i>): reticenda <i>Just</i> тайны, секреты."],
  ["reticesco", "<big>reticesco</big>\n\n<b>reticesco, —, —, ere</b> <i>Eccl</i>\n= reticeo 2."],
  ["reticulatus", "<big>reticulatus</big>\n\n<b>reticulatus, a, um</b> [reticulum]\nсетчатый, сетеобразный (structura <i>Vtr, PM</i>); решётчатый (fenestra <i>PM</i>)."],
  ["reticulum", "<big>reticulum</big>\n\n<b>reticulum, i</b> <i>n</i> [<i>demin. к</i> rete]\n<b>1)</b> сетка, сеточка <i>Sen</i>; корзинка (сумка) из сетки <i>C, H, J</i>; головная сетка <i>Pt, J</i>;\n<b>2)</b> ракетка (reticulo pilae fundantur <i>O</i>);\n<b>3)</b> решётка <i>или</i> колосник <i>Vr</i>;\n<b>4)</b> <i>анат.</i> сальник (r. jecoris <i>Vlg</i>)."],
  ["reticulus", "<big>reticulus</big>\n\n<b>reticulus, i</b> <i>m</i> <i>Vr, PM</i>\n= reticulum 1."],
  ["retinaculum", "<big>retinaculum</big>\n\n<b>retinaculum, i</b> <i>n</i> [retineo]\nсвязь, <i>pl.</i> скрепы, узы (vitae retinacula abrumpere <i>PJ</i>); поводья, вожжи (retinacula tendens auriga <i>V</i>): retinacula classis <i>V</i> корабельные канаты, причалы."],
  ["retinax", "<big>retinax</big>\n\n<b>retinax, acis</b> [retineo]\nзадерживающий (arbor <i>Symm</i>)."],
  ["retinens", "<big>retinens</big>\n\n<b>retinens, entis</b>\n<b>1.</b> <i>part. praes. к</i> retineo;\n<b>2.</b> <i>adj.</i> придерживающийся, привязанный, преданный (r. antiqui moris <i>T</i>)."],
  ["retinentia", "<big>retinentia</big>\n\n<b>retinentia, ae</b> <i>f</i> [retineo]\nзапоминание, память (actarum rerum <i>Lcr</i>)."],
  ["retineo", "<big>retineo</big>\n\n<b>re–tineo, tinui, tentum, ere</b> [teneo]\nудерживать, задерживать (aliquem <i>Pl, C etc.</i>; tempestate retentus <i>Cs</i>); сдерживать (equum <i>QC</i>; lacrimas <i>O</i>); держать (aliquem in vinculis <i>O</i>); сохранять за собой (amicos <i>H</i>; libertatem <i>C</i>): Fortunam citius reperias, quam retineas <i>PS</i> счастье легче найти, чем сохранить; ardor retinendge potentiae <i>T</i> страстное желание удержать власть; summos cum infimis pari jure r. <i>C</i> делать высших и низших равными перед законом || обуздывать (ferum animum <i>T</i>); сдерживать, подавлять (gaudia <i>O</i>); приковывать к себе, захватывать, пленять (animos in legendo <i>C</i>); сковывать, связывать (verba dolore retenta <i>O</i>); r, memoriam alicujus rei <i>C, Cs</i> <i>или</i> aliquid memoria r. <i>C</i> помнить о чём-л.; non retineo <i>AG</i> не помню."],
  ["retinnio", "<big>retinnio</big>\n\n<b>re–tinnio, —, —, ire</b>\nзвенеть (в ответ), звучать <i>Vr, C</i>."],
  ["retiolum", "<big>retiolum</big>\n\n<b>retiolum, i</b> <i>n</i> [<i>demin. к</i> rete]\nмаленькая сеть, сетка <i>Ap, Aug</i>."],
  ["retis", "<big>retis</big>\n\n<b>retis, is</b> <i>f</i> <i>Vr, Dig</i>\n= rete."],
  ["reto", "<big>reto</big>\n\n<b>reto, —, —, are</b>\nочищать русло реки (от растущих в нём деревьев; <i>см.</i> retae) <i>AG</i>."],
  ["retondeo", "<big>retondeo</big>\n\n<b>re–tondeo, —, (tonsum), ere</b>\nсостригать, <i>перен.</i> скашивать, сжинать (segetes <i>PM</i>)."],
  ["retono", "<big>retono</big>\n\n<b>re–tono, —, —, are</b>\nгрохотать в ответ, откликаться (mugienti fremitu <i>Ctl</i>)."],
  ["retorpesco", "<big>retorpesco</big>\n\n<b>re–torpesco, —, —, ere</b>\nснова цепенеть <i>Tert</i>."],
  ["retorqueo", "<big>retorqueo</big>\n\n<b>re–torqueo, torsi, tortum, ere</b>\nповорачивать назад (oculos ad urbem <i>C</i>; retortae undae <i>H</i>); откидывать назад (pantherae terga <i>V</i>); делать поворот (agmen retorquetur ad dextram <i>Cs</i>); скручивать назад (manus <i>H</i>); завивать (crinem <i>M</i>); обращать, направлять (animum ad praeterita <i>Sen</i>); изменять (mentem <i>V</i>): r. argumentum <i>Ap</i> использовать довод своего противника против него самого."],
  ["retorreo", "<big>retorreo</big>\n\n<b>re–torreo, —, tostum, ere</b>\n(вновь) сушить <i>PM</i>."],
  ["retorresco", "<big>retorresco</big>\n\n<b>re–torresco, —, —, ere</b>\n(вновь) засыхать <i>Col</i>."],
  ["retorride", "<big>retorride</big>\n\n<b>retorride</b>\nв сухом <i>или</i> чахлом виде (r. nata, <i>sc.</i> arbusta <i>или</i> virgulta <i>PM</i>)."],
  ["retorridus", "<big>retorridus</big>\n\n<b>re–torridus, a, um</b>\n<b>1)</b> обожжённый (manus Mucii <i>Sen</i>);\n<b>2)</b> засохший, иссохший (ramus <i>Sen</i>);\n<b>3)</b> угрюмый (vultus <i>Capit</i>);\n<b>4)</b> строптивый (mentes <i>Lampr</i>);\n<b>5)</b> опытный, бывалый (mus <i>Ph</i>)."],
  ["retorsi", "<big>retorsi</big>\n\n<b>retorsi</b> <i>pf. к</i>\nretorqueo."],
  ["retortus", "<big>retortus</big>\n\n<b>retortus, a, um</b> <i>part. pf. к</i>\nretorqueo."],
  ["retractatio", "<big>retractatio</big>\n\n<b>retractatio, onis</b> <i>f</i> [retracto]\n<b>1)</b> отказ, сопротивление (sine ulla retractatione <i>C, L</i>);\n<b>2)</b> пересмотр, изменение (sine retractatione —<i>sc.</i> sententiae alicujus — libere dicere aliquid <i>C</i>);\n<b>3)</b> припоминание (eorum, qui fuerunt <i>Sen</i>)."],
  ["retractator", "<big>retractator</big>\n\n<b>retractator, oris</b> <i>m</i>\nотвергающий, уклоняющийся (r. officii <i>Tert</i>)."],
  ["retractatus", "<big>retractatus</big>\n\n<b>I retractatus, a, um</b> [retracto]\nвновь обдуманный, пересмотренный, исправленный, переработанный <i>C</i>.\n\n<b>II retractatus, us</b> <i>m</i>\n<b>1)</b> (внутреннее) сопротивление, колебание (sine ullo retractatu <i>Tert</i>);\n<b>2)</b> повторный просмотр, повторение (doctrinarum <i>Tert</i>)."],
  ["retractio", "<big>retractio</big>\n\n<b>retractio, onis</b> <i>f</i> [retraho]\n<b>1)</b> сокращение, сужение, убыль (dierum <i>Macr</i>);\n<b>2)</b> ширина (graduum <i>Vr</i>);\n<b>3)</b> <i>Eccl</i> = retractatio."],
  ["retracto", "<big>retracto</big>\n\n<b>re–tracto, avi, atum, are</b>\n<b>1)</b> снова хватать, вновь брать, опять приниматься (r. arma <i>L</i>; ferrum <i>V</i>; gladios <i>Pt</i>); снова обрабатывать (agrum <i>Col</i>); заново разбирать (locum orationis <i>C</i>); вновь испытывать (gaudium <i>PJ</i>); вновь трогать, прикасаться, бередить (vulnera <i>O</i>);\n<b>2)</b> переделывать, перерабатывать (librum <i>Sen</i>); пересматривать (finitam causam <i>PJ</i>);\n<b>3)</b> снова восстанавливать в памяти, размышлять, обдумывать (aliquid diligenter <i>C</i>; r. aliquid secum <i>O</i>);\n<b>4)</b> противиться, отказываться, сопротивляться (quid retractas? <i>V</i>; nullo retractante <i>L</i>);\n<b>5)</b> брать назад, отрекаться (r. dicta <i>V</i>);\n<b>6)</b> умалять (opus <i>AG</i>)."],
  ["retractus", "<big>retractus</big>\n\n<b>I retractus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> retraho;\n<b>2.</b> <i>adj.</i> отдалённый, удалённый (a mari <i>L</i>); r. introrsus глубоко сидящий (oculi <i>Sen</i>).\n\n<b>II retractus, (us) </b> <i>m</i>\nоттягивание назад (machinae bellicae <i>Tert</i>)."],
  ["retraho", "<big>retraho</big>\n\n<b>re–traho, traxi, tractum, ere</b>\n<b>1)</b> тянуть назад, оттягивать (manum <i>C</i>; Hannibalem in Africam <i>C</i>); тащить обратно, возвращать (aliquem ex fuga <i>Sl</i>); привести, притащить (aliquem ad cruciatus <i>T</i>): quo fata trahunt retrahuntque <i>V</i> куда бы ни влекла судьба || se r. уклоняться, устраняться, увёртываться (ab ictu <i>O</i>) <i>или</i> уходить (a convivio <i>CC</i>);\n<b>2)</b> сокращать (ductas summa ad fastigia noctes <i>Man</i>);\n<b>3)</b> удерживать (consules a foedere <i>C</i>); спасать (ab interitu <i>Nep</i>): ex magnis detrimentis retractus <i>Su</i> отыгравшись после больших проигрышей;\n<b>4)</b> скрывать, утаивать (occulere et r. aliquid <i>L</i>; verba coram aliquo <i>Sen</i>); не проявлять, не обнаруживать (vires ingenii <i>Sen</i>);\n<b>5)</b> отнимать, вычёркивать (ex viginti trecentisque milibus centum quinquaginta <i>Su</i>);\n<b>6)</b> извлекать наружу (oblitterata monumenta <i>T</i>);\n<b>7)</b> относить: aliquid ad spem haud dubiam r. <i>T</i> истолковать что-л. в смысле твёрдой надежды;\n<b>8)</b> вовлекать, втягивать: in odium alicujus r. <i>C</i> навлечь чью-л. ненависть."],
  ["retranseo", "<big>retranseo</big>\n\n<b>re–transeo, —, —, ire</b>\nвозвращаться; ad soporem transire et r. <i>Aug</i> впадать в сон и вновь просыпаться."],
  ["retrarius", "<big>retrarius</big>\n\n<b>retrarius, a, um</b> [retro]\nобращенный назад, обратный <i>Aug</i>."],
  ["retrecto", "<big>retrecto</big>\n\n<b>retrecto</b> <i>v. l.</i>\n= retracto."],
  ["retribuo", "<big>retribuo</big>\n\n<b>re–tribuo, tribui, tributum, ere</b>\nобратно давать, возвращать (acceptam pecuniam alicui <i>L</i>); возмещать (aliquid alicui rei <i>Lcr</i>); воздавать (alicui quod meruit <i>C</i>)."],
  ["retributio", "<big>retributio</big>\n\n<b>retributio, onis</b> <i>f</i>\nвозмещение, воздаяние (r. meritorum <i>Sid</i>); отплата (mali pro malo <i>Aug</i>)."],
  ["retributor", "<big>retributor</big>\n\n<b>retributor, oris</b> <i>m</i>\nвоздаятель <i>Eccl</i>."],
  ["retrices", "<big>retrices</big>\n\n<b>retrices, um</b> <i>f</i>\nоросительные каналы <i>Cato</i>."],
  ["retrimentum", "<big>retrimentum</big>\n\n<b>retrimentum, i</b> <i>m</i> [retero]\nотходы, отбросы <i>Vr</i>; шлак (plumbi <i>CC</i>); выделения, экскременты <i>Vr, Macr</i>."],
  ["retrituro", "<big>retrituro</big>\n\n<b>re–trituro, —, —, are</b>\nснова молотить <i>или</i> перетирать (aliquid <i>Aug</i>)."],
  ["retritus", "<big>retritus</big>\n\n<b>retritus, a, um</b>\nвытертый, изношенный <i>Naev, Sen</i>."],
  ["retro", "<big>retro</big>\n\n<b>I retro</b> <i>adv.</i>\n<b>1)</b> назад <i>или</i> прочь (fugere <i>H</i>); обратно, вспять (vertere sidera <i>V</i>);\n<b>2)</b> сзади, позади (r. atque a tergo <i>C</i>);\n<b>3)</b> прежде, до, назад (r. usque ad Romulum <i>C</i>): quodcumque r. est <i>H</i> всё, что прошло, прошлое; r. principes <i>Lampr</i> прежние принцепсы; stipendia r. debita <i>Lampr</i> не выплаченное за прошлое жалованье;\n<b>4)</b> наоборот, с другой стороны (nunc vide rursus r. <i>C</i>): r. vivere <i>Sen</i> вести извращённый образ жизни;\n<b>5)</b> обратно <i>или</i> взамен (r. dare <i>и</i> reddere <i>Dig</i>).\n\n<b>II retro</b> <i>praep. cum acc.</i> (<i>очень редко</i>)\nза, позади (r. metas <i>Ap</i>)."],
  ["retroago", "<big>retroago</big>\n\n<b>retro–ago, egi, actum, ere</b> (<i>тж. раздельно</i>)\nповорачивать в обратную сторону, зачёсывать назад (capillos a fronte <i>Q</i>); обращать назад, изменять (ordinem <i>Q</i>); читать в обратном порядке (litteras <i>Q</i>): r. iram <i>Sen</i> подавить гнев; dactylus retroactus <i>Q</i> обратный дактиль, <i>т. е.</i> анапест."],
  ["retrocedo", "<big>retrocedo</big>\n\n<b>retro–cedo, cessi, cessum, ere</b> (<i>тж. раздельно</i>)\nидти назад, отступать <i>L, QC, Amm</i>."],
  ["retrocessio", "<big>retrocessio</big>\n\n<b>retrocessio, onis</b> <i>f</i> <i>Aug</i>\n= retrocessus."],
  ["retrocessus", "<big>retrocessus</big>\n\n<b>retrocessus, us</b> <i>m</i> [retrocedo]\nдвижение назад, обратный ход <i>Ap</i>."],
  ["retroduco", "<big>retroduco</big>\n\n<b>retro–duco, duxi, ductum, ere</b> (<i>тж. раздельно</i>)\nвести назад, нести обратно <i>Vtr, Dig</i>."],
  ["retroeo", "<big>retroeo</big>\n\n<b>retro–eo, —, —, ire</b> (<i>тж. раздельно</i>)\nидти назад <i>Sen, PM</i>."],
  ["retroflecto", "<big>retroflecto</big>\n\n<b>retro–flecto, flexi, flexum, ere</b> (<i>тж. раздельно</i>)\nзагибать назад, поворачивать в обратную сторону (apices capillorum <i>Pt</i>)."],
  ["retrogradior", "<big>retrogradior</big>\n\n<b>retro–gradior, gressus sum, gradi</b> <i>depon.</i>\nидти назад, двигаться обратно <i>PM</i>."],
  ["retrogradis", "<big>retrogradis</big>\n\n<b>retro–gradis, e</b>\nдвижущийся назад, обратный (fuga <i>Ap</i>)."],
  ["retrogradus", "<big>retrogradus</big>\n\n<b>retro–gradus, a, um</b> <i>Sen, PM</i>\n= retrogradis."],
  ["retrolego", "<big>retrolego</big>\n\n<b>retro–lego, —, —, ere</b> (<i>тж. раздельно</i>)\nпроплывать вдоль в обратном направлении (r. litora <i>Q</i>)."],
  ["retropendulus", "<big>retropendulus</big>\n\n<b>retro–pendulus, a, um</b>\nсвисающий, ниспадающий назад (crinium globi <i>Ap</i>)."],
  ["retrorsum", "<big>retrorsum</big>\n\n<b>retrorsum</b> <i>adv.</i> [retro + versum]\n<b>1)</b> назад, обратно (vela dare <i>H</i>);\n<b>2)</b> наоборот (ex terra aqua, deinde r. vicissim ex aqua terra <i>C</i>);\n<b>3)</b> (<i>во времени</i>) обратно, вспять (r. se actio refert <i>Dig</i>)."],
  ["retrorsus", "<big>retrorsus</big>\n\n<b>I retrorsus</b> <i>adv.</i> <i>Pt, VP, Sil</i>\n= retrorsum.\n\n<b>II retrorsus, a, um</b> [<i>из</i> retroversus]\nобращенный назад (retrorsa manu <i>PM</i>); retrorsior (<i>v. l.</i> retrosior) <i>Tert</i> более старый."],
  ["retrospiciens", "<big>retrospiciens</big>\n\n<b>retro–spiciens, entis</b>\nглядящий назад, озирающийся (sol orbem lunae r. <i>Vtr</i>)."],
  ["retroversum", "<big>retroversum</big>\n\n<b>retro–versum</b> <i>adv.</i> <i>Pl</i>\n= retrorsum."],
  ["retroversus", "<big>retroversus</big>\n\n<b>I retro–versus</b> <i>adv.</i> <i>Pt</i>\n= retrorsum.\n\n<b>II retro–versus, a, um</b>\nобращенный назад <i>O</i>; обратный (argumentum <i>Lact</i>)."],
  ["retrovors-", "<big>retrovors-</big>\n\n<b>retrovors-</b> <i>Pl</i>\n= retrovers-."],
  ["retrudo", "<big>retrudo</big>\n\n<b>re–trudo, —, (trusum), ere</b>\nотталкивать назад (aliquem invitum <i>Pl</i>); удалять (in metallum retrudendus <i>CTh</i>)."],
  ["retrusus", "<big>retrusus</big>\n\n<b>retrusus, a, um</b> [retrudo]\nсокрытый (r. et abditus <i>C</i>)."],
  ["rettudi", "<big>rettudi</big>\n\n<b>ret(t)udi</b> <i>pf. к</i>\nretundo."],
  ["rettuli", "<big>rettuli</big>\n\n<b>ret(t)uli</b> <i>pf. к</i>\nrefero."],
  ["retulit", "<big>retulit</big>\n\n<b>retulit</b> <i>pf. к</i>\nrefert."],
  ["retundo", "<big>retundo</big>\n\n<b>re–tundo, tudi</b> (<i>и</i> <b>rettudi</b>), <b>tusum (tunsum), ere</b>\n<b>1)</b> гнать назад, отталкивать (Boreas retundit pelagus <i>Lcn</i>);\n<b>2)</b> делать тупым, притуплять (gladium <i>V</i>; telum <i>O</i>): r. gladium alicujus <i>погов. C</i> = обезвреживать чьи-л. злодейские замыслы;\n<b>3)</b> сдерживать, умерять (impetum <i>L</i>); сломить, подавлять (aliquem <i>L</i>; improbitatem <i>C</i>): r. linguam alicujus <i>L</i> заставить кого-л. молчать; r. superbiam alicujus <i>Ph</i> сбить с кого-л. спесь."],
  ["retunsus", "<big>retunsus</big>\n\n<b>retu(n)sus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> retundo;\n<b>2.</b> <i>adj.</i> тупой (ferrum <i>V</i>; novacula <i>Pt</i>); притуплённый (ingenium <i>C</i>); ослабевший (fervor belli <i>Sil</i>); потускневший (stella crine non retuso <i>Cld</i>)."],
  ["returo", "<big>returo</big>\n\n<b>returo, —, —, are</b> <i>Vr</i>\n= obturo."],
  ["reubarbarum", "<big>reubarbarum</big>\n\n<b>reubarbarum</b> <i>Is</i>\n= rheubarbarum."],
  ["Reudigni", "<big>Reudigni</big>\n\n<b>Reudigni, orum</b> <i>m</i>\nревдигны, <i>свебское племя, жившее на правом берегу нижней Лабы</i> <i>T</i>."],
  ["reum-", "<big>reum-</big>\n\n<b>reum-</b> <i>v. l.</i>\n= rheum-."],
  ["reunctor", "<big>reunctor</big>\n\n<b>reunctor, oris</b> <i>m</i> [re + ungo]\nвтирающий мази (<i>помощник врача</i>) <i>PM</i>."],
  ["reus", "<big>reus</big>\n\n<b>reus, i</b> <i>m</i> [res 10]\n<b>1)</b> имеющий судебное дело; <i>pl.</i> обвиняемый и истец, <i>т. е.</i> стороны (<i>на суде</i>) (reos appello omnes, quorum de re disceptatur <i>C</i>);\n<b>2)</b> подсудимый, ответчик, виновный: aliquem reum faeere <i>C etc.</i> (agere <i>C, L, T</i>) обвинить кого-л., привлечь к судебной ответственности; Anyti r. <i>H</i> = Socrates;\n<b>3)</b> виновник (parricidii <i>Q</i>; de ambitu <i>C</i>);\n<b>4)</b> несущий обязанность, долженствующий, ответственный (culpae alienae <i>L</i>); r. suae partis tutandae <i>L</i> несущий ответственность за свою долю; r. voti <i>V</i> обязанный исполнить обет (<i>как добившийся исполнения своего желания</i>)."],
  ["revalesco", "<big>revalesco</big>\n\n<b>re–valesco, valui, —, ere</b>\n<b>1)</b> вновь выздоравливать, поправляться (ex capitali morbo <i>AG</i>);\n<b>2)</b> вновь усиливаться, набираться сил, крепнуть (Laodicea revaluit propriis opibus <i>T</i>);\n<b>3)</b> снова достигать влияния, приобретать значение, становиться вновь действительным (diplomata revalescunt <i>T</i>)."],
  ["revecto", "<big>revecto</big>\n\n<b>re–vecto, —, —, are</b>\nдоставлять обратно (corpus alicujus ad Macedoniam <i>JV</i>)."],
  ["reveho", "<big>reveho</big>\n\n<b>re–veho, vexi, vectum, ere</b>\n<b>1)</b> везти назад, приносить обратно (praedam <i>L</i>); <i>med.-pass.</i> revehi приходить назад, приезжать обратно (in castra <i>L</i>; Ithacam <i>H</i>); revehi ad superiorem aetatem <i>C</i> вернуться к прошлому (<i>в речи</i>);\n<b>2)</b> возить взад и вперёд, перевозить (aliquem <i>VF</i>)."],
  ["revelatio", "<big>revelatio</big>\n\n<b>revelatio, onis</b> <i>f</i> [revelo]\n<b>1)</b> обнажение (pudendorum <i>Eccl</i>);\n<b>2)</b> откровение <i>Eccl</i>."],
  ["revelator", "<big>revelator</big>\n\n<b>revelator, oris</b> <i>m</i>\nоткрывающий (сокровенное) <i>Tert</i>."],
  ["revelatorius", "<big>revelatorius</big>\n\n<b>revelatorius, a, um</b>\nсодержащий откровение <i>Eccl</i>."],
  ["revello", "<big>revello</big>\n\n<b>re–vello, veili (vulsi), vulsum, ere</b>\n<b>1)</b> вырывать, выдёргивать (tela de corpore <i>V</i>; arborem tellure <i>O</i>); отрывать (caput a cervice revulsum <i>V</i>); силой отнимать (puerum <i>O</i>): morte revelli <i>O</i> быть похищенным смертью || насильно уводить (aliquem urbe <i>V</i>);\n<b>2)</b> искоренять, изглаживать (aliquid ex omni memoria <i>C</i>): r. omnes injurias <i>C</i> предать забвению все обиды;\n<b>3)</b> отрывать, срывать (tabulam <i>C</i>; claustra portarum <i>C</i>): r. sepulcrum <i>rhH</i> разрыть могилу; r. humum <i>O</i> разрыхлять землю."],
  ["revelo", "<big>revelo</big>\n\n<b>re–velo, avi, atum, are</b>\nоткрывать, обнажать (frontem <i>T</i>; pectus <i>Fl</i>); раскрывать, разоблачать (fraudes <i>Ap</i>; tempus omnia revelat <i>Tert</i>); разъяснять (obscure dicta <i>CA</i>)."],
  ["revendo", "<big>revendo</big>\n\n<b>re–vendo, vendidi, —, ere</b>\nснова <i>или</i> обратно продавать (aliquid alicui <i>Dig</i>)."],
  ["reveneo", "<big>reveneo</big>\n\n<b>re–veneo, ii, —, ire</b>\nбыть снова <i>или</i> обратно продаваемым <i>Dig</i>."],
  ["revenio", "<big>revenio</big>\n\n<b>re–venio, veni, ventum, ire</b>\nприходить назад, возвращаться (in urbem <i>T</i>; domum <i>C</i>; <i>перен.</i> ex inimicitia in gratiam <i>Pl</i>)."],
  ["reventus", "<big>reventus</big>\n\n<b>reventus, us</b> <i>m</i> [revenio]\nвозвращение <i>Suevius ap. Macr</i>."],
  ["revera", "<big>revera</big>\n\n<b>re–vera</b> <i>adv.</i> (<i>тж. раздельно</i>) [res + verus]\nна самом деле, действительно <i>C etc.</i>"],
  ["reverbero", "<big>reverbero</big>\n\n<b>re–verbero, —, —, are</b>\n<b>1)</b> отбивать, отбрасывать (saxa <i>Sen</i>); <i>pass.</i> откатываться, отхлынуть (flumen saxis reverberatur <i>QC</i>);\n<b>2)</b> отражать (radios <i>Ap</i>; <i>перен.</i> iram Fortunae <i>Sen</i>)."],
  ["reverecunditer", "<big>reverecunditer</big>\n\n<b>reverecunditer</b> <i>Enn</i>\n= reverenter."],
  ["reverendus", "<big>reverendus</big>\n\n<b>reverendus, a, um</b>\n<b>1.</b> <i>part. fut. к</i> revereor;\n<b>2.</b> <i>adj.</i> внушающий уважение, почтенный (facies <i>J</i>; vetustas <i>AG</i>)."],
  ["reverens", "<big>reverens</big>\n\n<b>reverens, entis</b>\n<b>1.</b> <i>part. praes. к</i> revereor;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> почтительный (erga aliquem <i>T</i> <i>и</i> alicujus <i>PJ, Fl</i>);\n<b>2)</b> почтенный, достойный уважения (nomen alicujus <i>Fl</i>)."],
  ["reverenter", "<big>reverenter</big>\n\n<b>reverenter</b> [reverens]\nпочтительно, с уважением <i>PJ, T, Su</i>."],
  ["reverentia", "<big>reverentia</big>\n\n<b>reverentia, ae</b> <i>f</i> [reverens]\n<b>1)</b> робость, боязнь (alicujus rei <i>Col, Q, Prp</i>);\n<b>2)</b> почтение, уважение, благоговение (adversus aliquem <i>C, Sen</i> <i>или</i> alicujus, alicujus rei <i>PJ, O, T</i>; maxima debetur puero r. <i>J</i>);\n<b>3)</b> почтенность, величие (patris patriae <i>Vop</i>)."],
  ["revereor", "<big>revereor</big>\n\n<b>re–vereor, veritus sum, eri</b> <i>depon.</i>\n<b>1)</b> бояться, опасаться, робеть (adventum alicujus <i>Ter</i>; suspicionem <i>C</i>);\n<b>2)</b> питать уважение, почитать, уважать (aetate antecedentes <i>C</i>; virtutem <i>rhH</i>)."],
  ["revergo", "<big>revergo</big>\n\n<b>re–vergo, —, —, ere</b>\nобращаться, клониться (in commodum alicujus <i>Claudius Mamertinus ap. Sid</i>)."],
  ["reverro", "<big>reverro</big>\n\n<b>re–verro, —, —, ere</b>\nснова выметать (aliquid <i>Pl</i>)."],
  ["reversio", "<big>reversio</big>\n\n<b>reversio, onis</b> <i>f</i> [revertor]\n<b>1)</b> поворот (solis <i>C</i>); круговорот (reversiones temporum <i>Ap</i>);\n<b>2)</b> возвращение (profectio et r. <i>C</i>); возврат, рецидив (febrium <i>C</i>);\n<b>3)</b> <i>грам.</i> перестановка, анастрофа (<i>напр.</i> mecum <i>вм.</i> cum me <i>Q</i>)."],
  ["reverso", "<big>reverso</big>\n\n<b>re–verso, —, atum, are</b>\nповорачивать (panis coctus et reversatus <i>Is</i>); поворачиваться (versare et r. in tergum et in latera et in ventrem <i>Aug</i>)."],
  ["reverticulum", "<big>reverticulum</big>\n\n<b>reverticulum, i</b> <i>n</i> [revertor]\nвозвращение (annua solis reverticula <i>Ap</i>)."],
  ["reverto", "<big>reverto</big>\n\n<b>re–verto, verti, versum, ere</b> <i>C, Cs, Nep, V etc.</i>\n= revertor."],
  ["revertor", "<big>revertor</big>\n\n<b>re–vertor, reversus sum</b> (<i>чаще</i> <b>reverti</b>), <b>reverti</b> <i>semidepon.</i>\n<b>1)</b> приходить назад, возвращаться (ab Oceano in patriam <i>Just</i>; ex itinere <i>Cs, C</i>; domum <i>Pl etc.</i>; nescit vox missa reverti <i>H</i>): r. ad sanitatem <i>Cs</i> образумиться; in gratiam cum aliquo r. <i>L, Sen, Pt</i> примириться с кем-л.;\n<b>2)</b> вновь заговаривать, возвращаться (revertamur eodem unde digressi sumus <i>C</i>);\n<b>3)</b> обращаться, направляться (ira alicujus in aliquem revertitur <i>T</i>)."],
  ["revestio", "<big>revestio</big>\n\n<b>re–vestio, ivi (ii), itum, ire</b>\nснова одевать <i>или</i> надевать <i>Tert</i>."],
  ["revexi", "<big>revexi</big>\n\n<b>revexi</b> <i>pf. к</i>\nreveho."],
  ["revictio", "<big>revictio</big>\n\n<b>revictio, onis</b> <i>f</i> [revinco]\nопровержение <i>Ap</i>."],
  ["revicturus", "<big>revicturus</big>\n\n<b>revicturus, a, um</b> <i>part. fut. к</i>\nrevivo."],
  ["revictus", "<big>revictus</big>\n\n<b>revictus, a, um</b> <i>part. pf. к</i>\nrevinco."],
  ["revideo", "<big>revideo</big>\n\n<b>re–video, —, —, ere</b>\nопять посещать, вновь приходить (r. ad aliquem <i>Pl</i>)."],
  ["revigesco", "<big>revigesco</big>\n\n<b>re–vigesco, vigui, —, ere</b>\nвновь укрепляться <i>Jvc</i>."],
  ["revilesco", "<big>revilesco</big>\n\n<b>re–vilesco, —, —, ere</b>\nснова падать в цене, <i>перен.</i> утрачивать ценность <i>Sen</i>."],
  ["revimentum", "<big>revimentum</big>\n\n<b>revimentum, i</b> <i>n</i>\nкайма <i>или</i> оторочка <i>Fronto</i>."],
  ["revincibilis", "<big>revincibilis</big>\n\n<b>re–vincibilis, e</b>\nопровержимый <i>Tert</i>."],
  ["revincio", "<big>revincio</big>\n\n<b>re–vincio, vinxi, vinctum, ire</b>\n<b>1)</b> связывать назад: manus (<i>acc. gr.</i>) post terga revinctus <i>V</i> со связанными назад руками || привязывать (aliquem ad saxa <i>O</i>); укреплять (ancoras catenis <i>Cs</i>); обвивать (crines serpentum spiris <i>V</i>); украшать (templum velleribus niveis et festa fronde <i>V</i>); захватывать, приковывать (mentem amore <i>Ctl</i>): latus ense r. <i>Prp</i> опоясать(ся) мечом;\n<b>2)</b> отвязывать (aliquem <i>Col</i>)."],
  ["revinco", "<big>revinco</big>\n\n<b>re–vinco, vici, victum, ere</b>\n<b>1)</b> побеждать (в свою очередь), одолевать (victrices catervae revictae <i>H</i>); подавлять (conjurationem <i>T</i>);\n<b>2)</b> опровергать (crimen <i>L, Dig</i>; aliquem <i>C</i>);\n<b>3)</b> обличать (aliquem in culpa <i>AG</i>)."],
  ["reviresco", "<big>reviresco</big>\n\n<b>re–viresco, virui, —, ere</b>\n<b>1)</b> снова становиться зелёным, вновь зеленеть (silvae revirescunt <i>O</i>);\n<b>2)</b> обновляться, делаться вновь молодым, молодеть (senex revirescit <i>O</i>);\n<b>3)</b> снова крепнуть, становиться опять сильным, снова процветать (rei publicae vires revirescunt <i>rhH</i>)."],
  ["revisceratio", "<big>revisceratio</big>\n\n<b>re–visceratio, onis</b> <i>f</i>\nвосстановление плоти <i>Tert</i>."],
  ["revisio", "<big>revisio</big>\n\n<b>re–visio, onis</b> <i>f</i>\nновое посещение <i>Claudius Mamertinus ap. Sid</i>."],
  ["revisito", "<big>revisito</big>\n\n<b>re–visito, —, —, are</b>\nвновь посещать (urbem <i>PM</i>)."],
  ["reviso", "<big>reviso</big>\n\n<b>re–viso, visi, visum, ere</b>\n<b>1)</b> снова смотреть, опять осматривать (classem <i>V</i>);\n<b>2)</b> снова приходить, посещать (agrum <i>Col</i>; aliquem <i>C</i> <i>или</i> ad aliquem <i>Pl, AG</i>); возвращаться (furor ille revisit <i>Lcr</i>)."],
  ["revivesco", "<big>revivesco</big>\n\n<b>re–vivesco</b> <i>C</i>\n= revivisco."],
  ["revivificatus", "<big>revivificatus</big>\n\n<b>revivi–ficatus, a, um</b>\nвновь оживший, воскресший <i>Tert</i>."],
  ["revivisco", "<big>revivisco</big>\n\n<b>re–vivisco, vixi, —, ere</b>\n<b>1)</b> снова оживать, воскресать (gramina reviviscunt <i>Col</i>);\n<b>2)</b> вновь отрастать (pennae reviviscunt <i>PM</i>); поправляться, восстанавливаться (res publica reviviscit <i>C</i>; reviviscens amicitia <i>Pt</i>)."],
  ["revivo", "<big>revivo</big>\n\n<b>re–vivo, —, (victum), ere</b>\nснова жить, оживать <i>SenT, Eccl</i>."],
  ["revocabilis", "<big>revocabilis</big>\n\n<b>re–vocabilis, e</b> [revoco]\nвозвратимый, обратимый, поддающийся отмене (poena <i>Sen</i>): non r. безвозвратный (verbum <i>St</i>) <i>или</i> непоправимый (damnum <i>Cld</i>)."],
  ["revocamen", "<big>revocamen</big>\n\n<b>re–vocamen, inis</b> <i>n</i>\nпредостерегающий призыв, предостережение <i>O</i>."],
  ["revocatio", "<big>revocatio</big>\n\n<b>revocatio, onis</b> <i>f</i> [revoco]\n<b>1)</b> отозвание, возвращение (a bello <i>C</i>); призыв (ad aliquid <i>C</i>);\n<b>2)</b> удаление (lunae a sole <i>Vtr</i>);\n<b>3)</b> <i>юр.</i> право вернуться (domum <i>Dig</i>);\n<b>4)</b> <i>рит.</i> повторение (verbi <i>C, Q</i>)."],
  ["revocator", "<big>revocator</big>\n\n<b>revocator, oris</b> <i>m</i> [revoco]\nпризывающий, пробуждающий (r. animarum <i>Q</i>)."],
  ["revocatoria", "<big>revocatoria</big>\n\n<b>revocatoria, ae</b> <i>f</i> (<i>sc.</i> epistula)\nприказ об отозвании <i>CJ</i>."],
  ["revoco", "<big>revoco</big>\n\n<b>re–voco, avi, atum, are</b>\n<b>1)</b> звать назад, вызывать обратно (aliquem ab exsilio <i>T, Just</i> <i>и</i> ex exsilio <i>L</i>; revocari ad cenam <i>Pt</i>); отзывать (in Italiam <i>Cs</i>); призывать (ad misericordiam <i>C</i>); отводить (fluctus <i>O</i>): r. animum ab ira <i>O</i> смягчить свой гнев || отворачивать (oculos <i>O</i>): r. gradum <i>V</i> отступать <i>или</i> возвращаться;\n<b>2)</b> отклонять, отговаривать (aliquem ab aliqua re <i>C, O etc.</i>); удерживать, предохранять (aliquem a peccato <i>Aug</i>);\n<b>3)</b> вновь призывать, приглашать (in suffragium <i>L</i>; ad bibendum <i>Pt</i>); вновь созывать (veteranus <i>T</i>): revocari in memoriam alicujus rei <i>Just, Pt etc.</i> вновь вспоминать о чём-л. || требовать обратно (pecunias ab aliquo <i>PJ</i>): aliquem ad rationem r. <i>Cato</i> требовать от кого-л. отчёта;\n<b>4)</b> восстанавливать (vires <i>C</i>; priscos mores <i>L</i>): umbram alicujus r. in florem <i>Pt</i> воскресить кого-л. в виде цветка || возобновлять (studia intermissa <i>C</i>): (in) spiritum r. <i>Pt</i> приводить в чувство || возвращать (artus gelidos in calorem <i>O</i>): r. memoriam alicujus rei <i>Sen, Just</i> вспомнить о чём-л.; se r. возвращаться (ad pristina studia <i>C</i>) <i>или</i> <i>C</i> (<i>тж.</i> se ad se r. <i>C</i>) приходить в себя, опомниться;\n<b>5)</b> отменять, уничтожать (libertatem <i>T</i>); брать назад (r. promissum suum <i>Sen</i>); обрезывать(multa in vitibus <i>C</i>);\n<b>6)</b> стеснять, уплотнять, втискивать (r. in unam domum <i>C</i>); укорачивать (Phoebus revocaverat umbras <i>VF</i>);\n<b>7)</b> доводить, сводить, приводить (aliquid ad meliorem statum <i>C</i>): rem ad manus r. <i>C</i> доводить дело до рукопашного боя; aliquid ad sortem r. <i>C</i> бросить жребий о чём-л.; aliquid in dubium (<i>или</i> ad incertum) r. <i>C</i> подвергать что-либо сомнению, ставить что-л. под вопрос; omnia ad aliquid r. <i>C</i> использовать всё для чего-л.;\n<b>8)</b> относить (crimen ad se <i>C</i>); отсылать, направлять (rem ad populum <i>L</i>); сообразовать, подчинять (rationem ad veritatem <i>C</i>; consilia ad naturam <i>C</i>): aliquid in crimen r. <i>C</i> вменять что-л. в вину;\n<b>9)</b> <i>театр.</i> вызывать, требовать повторения: milies revocatum est <i>C</i> тысячу раз требовали повторения (вызывали на бис)."],
  ["revolo", "<big>revolo</big>\n\n<b>re–volo, avi, atum, are</b>\n<b>1)</b> лететь назад (trans mare <i>Vr</i>);\n<b>2)</b> спешить обратно, поспешно возвращаться (ad patrem <i>VP</i>)."],
  ["revols-", "<big>revols-</big>\n\n<b>revols-</b> <i>арх.</i>\n= revuls-."],
  ["revolubilis", "<big>revolubilis</big>\n\n<b>revolubilis, e</b> [revolvo] <i>поэт.</i>\nмогущий скатываться обратно (pondus <i>O</i>; unda <i>Sil</i>): non r. неотвратимый (fatorum carmen <i>Ctl</i>)."],
  ["revolutio", "<big>revolutio</big>\n\n<b>revolutio, onis</b> <i>f</i> [revolvo]\n<b>1)</b> откатывание (lapidis <i>Eccl</i>);\n<b>2)</b> круговорот (multae per diversa corpora revolutiones, <i>sc.</i> animae <i>Aug</i>)."],
  ["revolvo", "<big>revolvo</big>\n\n<b>re–volvo, volvi, volutum, ere</b>\n<b>1.</b> <i>act.</i>\n<b>1)</b> катить назад (fluctus <i>T</i>); проходить в обратном направлении (iter <i>V</i>);\n<b>2)</b> вновь разжигать (iras <i>T</i>);\n<b>3)</b> снова разворачивать, опять раскрывать, перечитывать (librum <i>PJ, Sen</i>);\n<b>4)</b> снова испытывать, опять переживать (casus iterum <i>V</i>); снова обдумывать, передумывать (dicta factaque <i>T</i>); вновь пересказывать (haec <i>V</i>);\n<b>2.</b> <i>med.-pass.</i> revolvi:\n<b>1)</b> катиться назад (amnis revolvitur <i>T</i>); (вновь) возвращаться (ad memoriam conjugii <i>T</i>); сваливаться, падать (equo, toro <i>V</i>); быть уносимым назад (saxa aestu revoluta <i>V</i>): revoluta saecula <i>O</i> минувшие века;\n<b>2)</b> быть распускаемым (pensa revoluta <i>V</i>);\n<b>3)</b> превращаться (in veterem figuram <i>V</i>);\n<b>4)</b> доходить, быть приведённым (ad eam sententiam <i>C</i>); снова впадать, вновь предаваться (in luxuriam <i>Just</i>; ad vitia <i>T</i>); возвращаться (к вопросу) (identidem in Tusculanum <i>C</i>): revolveris eodem <i>C</i> ты впадаешь в ту же ошибку; revoluta dies <i>V</i> вернувшийся, <i>т. е.</i> новый день."],
  ["revomo", "<big>revomo</big>\n\n<b>re–vomo, vomui, —, ere</b>\nвыплёвывать, извергать обратно (salsos pectore fluctus <i>V</i>): Charybdis vorat revomitque carinas <i>O</i> Харибда поглощает и вновь изрыгает корабли."],
  ["revor-", "<big>revor-</big>\n\n<b>revor-</b> <i>арх.</i>\n= rever-."],
  ["revulsio", "<big>revulsio</big>\n\n<b>revulsio, onis</b> <i>f</i> [revello]\nотрывание (unius schidae <i>PM</i>)."],
  ["revulsus", "<big>revulsus</big>\n\n<b>revulsus, a, um</b> <i>part. pf. к</i>\nrevello."],
  ["rex", "<big>rex</big>\n\n<b>rex, regis</b> <i>m</i>\n<b>1)</b> царь (r. Dareus <i>QC</i>; r. Macedonum <i>QC</i>; urbem Romam a principio reges habuere <i>T</i>): rex regum <i>L, VP</i> = Agamemnon, <i>но тж.</i> <i>Su</i> парфянский царь; r. mensae <i>Macr</i> <i>или</i> convivii <i>Sid</i> председатель пира;\n<b>2)</b> царевич, принц (reges Syriae <i>C</i>);\n<b>3)</b> <i>pl.</i> царская чета (Ptolemaeus Cleopatraque reges <i>L</i>);\n<b>4)</b> <i>со времени свержения царей в Риме</i> верховный жрец (r. sacrorum, sacrificorum <i>C</i> <i>или</i> sacrificus <i>L</i>);\n<b>5)</b> деспот: r. populi Romani <i>C</i> = Caesar;\n<b>6)</b> владыка, властелин, повелитель (r. Aeneas <i>V</i>): r. deorum atque hominum <i>V, C</i> = Juppiter; r. aquarum <i>или</i> aequoreus <i>O</i> = Neptunus; r. infernus <i>V</i> (Stygius <i>V</i> <i>или</i> umbrarum <i>O</i>)= Pluto; r. apum <i>Col, V, Pall</i> пчелиная матка (<i>у римлян считавшаяся самцом</i>);\n<b>7)</b> наставник, воспитатель (r. pueritiae <i>H</i>);\n<b>8)</b> покровитель, патрон <i>Pl, Ter, H</i>; богач <i>или</i> знатный человек (sive reges sive inopes <i>H</i>)."],
  ["rhabo", "<big>rhabo</big>\n\n<b>rhabo, onis</b> <i>m</i> <i>v. l.</i>\n= rabo I."],
  ["Rhadamanthus", "<big>Rhadamanthus</big>\n\n<b>Rhadamanthus (-os), i</b> <i>m</i>\nРадамант, <i>сын Юпитера и Европы, царь Крита, после смерти ставший вместе с Миносом и Эаком судьёй в царстве мёртвых</i> <i>C, V, O</i>."],
  ["Rhadamas", "<big>Rhadamas</big>\n\n<b>Rhadamas, antis</b> <i>m</i>\nРадамант, <i>вымышленное имя</i> <i>Pl</i>."],
  ["rhadine", "<big>rhadine</big>\n\n<b>rhadine</b> <i>adj. f</i> (<i>греч.</i>)\nстройная, изящная <i>Lcr</i>."],
  ["Rhaet-", "<big>Rhaet-</big>\n\n<b>Rhaet-</b> <i>v. l.</i>\n= Raet-."],
  ["rhagadium", "<big>rhagadium</big>\n\n<b>rhagadium, i</b> <i>n</i> <i>Veg</i>\n= rhagas."],
  ["rhagas", "<big>rhagas</big>\n\n<b>rhagas, adis</b> <i>f</i> (<i>греч.</i>; <i>лат.</i> fissura)\nтрещина (на коже) <i>PM, Aug</i>."],
  ["Rhamnenses", "<big>Rhamnenses</big>\n\n<b>Rhamnenses, ium</b> <i>m</i> <i>v. l.</i>\n= Ramnenses."],
  ["Rhamnes", "<big>Rhamnes</big>\n\n<b>Rhamnes, etis</b> <i>m</i>\nРамнет, <i>рутул, военачальник и прорицатель в войске Турна</i> <i>V</i>."],
  ["rhamnus", "<big>rhamnus</big>\n\n<b>rhamnus (-os), i</b> <i>f</i> (<i>греч.</i>) <i>бот.</i>\nкрушина <i>PM, Veg</i>."],
  ["Rhamnus", "<big>Rhamnus</big>\n\n<b>Rhamnus, untis</b> <i>f</i>\nРамнунт, <i>город и дем в сев.-вост. Аттике с храмом Немесиды</i> <i>PM, Lcn</i>."],
  ["Rhamnusia", "<big>Rhamnusia</big>\n\n<b>Rhamnusia, ae</b> <i>f</i> <i>Ctl, O</i>\n= Rhamnusis."],
  ["Rhamnusis", "<big>Rhamnusis</big>\n\n<b>Rhamnusis, idis</b> <i>f</i> [Rhamnus]\nрамнунтинка, <i>т. е.</i> Немесида <i>O</i>."],
  ["Rhamnusius", "<big>Rhamnusius</big>\n\n<b>Rhamnusius, a, um</b> [Rhamnus]\nрамнунтский <i>Ter, C</i>: virgo (<i>тж.</i> ultrix <i>O</i>) Rhamnusia <i>Ctl</i> = Nemesis."],
  ["Rhamses", "<big>Rhamses</big>\n\n<b>Rhamses, is</b> (<i>и</i> <b>ae</b>) <i>m</i>\nРамзес, <i>царь Египта</i> <i>PM, T</i>."],
  ["Rhanis", "<big>Rhanis</big>\n\n<b>Rhanis, idis</b> <i>f</i>\nРанида, <i>одна из нимф Дианы</i> <i>O</i>."],
  ["rhapsodia", "<big>rhapsodia</big>\n\n<b>rhapsodia, ae</b> <i>f</i> (<i>греч.</i>)\nрапсодия, песнь (versus Homeri ex secunda rhapsodia <i>Nep</i>)."],
  ["Rhea", "<big>Rhea</big>\n\n<b>I Rhea, ae</b> <i>f</i>\nРея, <i>древнеиталийское имя</i>:\n<b>1)</b> Rh. Silvia, <i>дочь царя Альбы Нумитора, жрица Весты, родившая от Марса Ромула и Рема</i> <i>L, AV etc.</i>;\n<b>2)</b> <i>имя весталки</i> <i>V</i>.\n\n<b>II Rhea, ae</b> <i>f</i>\nРея, <i>дочь Урана (Неба) и Геи (Земли), сестра и супруга Кроноса (Сатурна), мать Юпитера, отождествлявшаяся с Кибелой</i> <i>O, Aus</i>."],
  ["rhed-", "<big>rhed-</big>\n\n<b>rhed-</b> <i>v. l.</i>\n= raed-."],
  ["Rhedones", "<big>Rhedones</big>\n\n<b>Rhedones, um</b> <i>m</i> <i>v. l.</i>\n= Redones."],
  ["Rhegium", "<big>Rhegium</big>\n\n<b>Rhegium, i</b> <i>n</i> <i>v. l.</i>\n= Regium."],
  ["Rhenanus", "<big>Rhenanus</big>\n\n<b>Rhenanus, a, um</b> [Rhenus]\nрейнский (manus <i>M</i>; terrae <i>Sid</i>)."],
  ["Rhenigena", "<big>Rhenigena</big>\n\n<b>Rhenigena, ae</b> <i>m, f</i> [Rhenus + geno = gigno]\nуроженец берегов Рейна <i>M</i>."],
  ["rheno", "<big>rheno</big>\n\n<b>rheno</b> <i>v. l.</i>\n= reno II."],
  ["Rhenus", "<big>Rhenus</big>\n\n<b>I Rhenus, i</b> <i>m</i>\n<b>1)</b> Рейн <i>Cs, PM, T etc.</i>;\n<b>2)</b> <i>поэт.</i> жители рейнского бассейна (Rh. subactus <i>Lcn</i>).\n\n<b>II Rhenus, a, um</b> [Rhenus]\nрейнский: Rh. flumen <i>H</i> Рейн.\n\n<b>III Rhenus, i</b> <i>m</i>\nжитель берегов Рейна <i>Pers</i>."],
  ["Rhesus", "<big>Rhesus</big>\n\n<b>Rhesus, i</b> <i>m</i>\nРес, <i>сын Эйонея, царь Фракии, союзник троянцев, убитый Диомедом</i> <i>C, V, O</i>."],
  ["rhetor", "<big>rhetor</big>\n\n<b>rhetor, oris</b> <i>m</i> (<i>греч.</i>)\n<b>1)</b> ритор, учитель красноречия <i>C, Q etc.</i>;\n<b>2)</b> оратор <i>Nep</i>."],
  ["rhetorica", "<big>rhetorica</big>\n\n<b>rhetorica, orum</b> <i>n</i> <i>Enn, C</i>\n= rhetorice II."],
  ["rhetorice", "<big>rhetorice</big>\n\n<b>I rhetorice</b> [rhetoricus]\nпо-риторски, риторически <i>C</i>.\n\n<b>II rhetorice, es (rhetorica, ae)</b> <i>f</i> (<i>греч.</i>)\nриторика, искусство красноречия <i>C, Q</i>."],
  ["rhetoricor", "<big>rhetoricor</big>\n\n<b>rhetoricor, —, ari</b>\nговорить как ритор, витийствовать <i>Tert</i>."],
  ["rhetoricoteros", "<big>rhetoricoteros</big>\n\n<b>rhetoricoteros</b> <i>adj. m</i> [<i>греч. compar. к</i> rhetoricus]\nболее красноречивый <i>LM ap. C</i>."],
  ["rhetoricus", "<big>rhetoricus</big>\n\n<b>I rhetoricus, a, um</b>\nриторический, витийственный (ars <i>C</i>).\n\n<b>II rhetoricus, i</b> <i>m</i> <i>Q</i>\n= rhetor."],
  ["rhetoriscus", "<big>rhetoriscus</big>\n\n<b>rhetoriscus, i</b> <i>m</i> [<i>demin. к</i> rhetor]\nмолодой (начинающий) оратор <i>AG</i>."],
  ["rhetra", "<big>rhetra</big>\n\n<b>rhetra, ae</b> <i>f</i> (<i>греч.</i>)\nизречение (законодательное) (Lycurgi <i>Amm</i>)."],
  ["rheubarbarum", "<big>rheubarbarum</big>\n\n<b>rheubarbarum, i</b> <i>n</i> <i>бот.</i>\nревень <i>Is</i>."],
  ["rheuma", "<big>rheuma</big>\n\n<b>rheuma, atis</b> <i>n Veg, Hier</i> <i>и</i> <b>rheuma, ae</b> <i>f</i> <i>Is</i>\n= rheumatismus."],
  ["rheumaticus", "<big>rheumaticus</big>\n\n<b>rheumaticus, i</b> <i>m</i>\nстрадающий катаром <i>PM</i>."],
  ["rheumatismus", "<big>rheumatismus</big>\n\n<b>rheumatismus, i</b> <i>m</i> (<i>греч.</i>; <i>лат.</i> fluxio)\nистечение, катар (r. ventris, thoracis, gutturis <i>PM</i>)."],
  ["rhinoceros", "<big>rhinoceros</big>\n\n<b>rhinoceros (rinoceros), rotis</b> (<i>acc.</i> rotem <i>и</i> rota, <i>acc. pl.</i> rotas) <i>m</i> (<i>греч.</i>)\n<b>1)</b> носорог: rhinocerotis nasum habere <i>погов. M</i> иметь нос как у носорога, <i>т. е.</i> быть крайне хитрым и придирчивым;\n<b>2)</b> сосуд из рога носорога (magno cum rhinocerote lavari <i>J</i>)."],
  ["rhinoceroticus", "<big>rhinoceroticus</big>\n\n<b>rhinoceroticus, a, um</b> [rhinoceros]\nносорожий: rhinocerotica naris <i>погов. Sid</i> едкая насмешка."],
  ["Rhinocolura", "<big>Rhinocolura</big>\n\n<b>Rhinocolura, ae</b> <i>f</i>\nРиноколура, <i>египетский город на Средиземном море</i> (<i>близ палестинской границы</i>) <i>L, PM</i>."],
  ["Rhinthon", "<big>Rhinthon</big>\n\n<b>Rhint(h)on, onis</b> <i>m</i>\nРинтон, <i>греч. автор комедий, родом из Тарента, современник Птолемея <i>J</i> Египетского</i> <i>Vr, C, Col</i>."],
  ["Rhion", "<big>Rhion</big>\n\n<b>Rhion</b> <i>и</i> <b>Rhium, i</b> <i>n</i>\nРион, <i>мыс в Ахее у входа в Коринфский залив, против мыса Antirrhion</i> (<i>в Локриде Озолийской</i>) <i>L, PM</i>."],
  ["Rhiphaeus", "<big>Rhiphaeus</big>\n\n<b>Rhip(h)aeus, a, um</b> <i>v. l.</i>\n= Rip(h)aeus."],
  ["Rhium", "<big>Rhium</big>\n\n<b>Rhium, i</b> <i>v. l.</i>\n= Rhion."],
  ["Rhizinium", "<big>Rhizinium</big>\n\n<b>Rhizinium, i</b> <i>n</i> <i>PM v. l.</i>\n= Rhizon."],
  ["Rhizon", "<big>Rhizon</big>\n\n<b>Rhizon, onis</b> <i>f</i>\nРизой, <i>укреплённый город в Иллирии</i> (<i>ныне</i> Risano) <i>L</i>."],
  ["Rhizonitae", "<big>Rhizonitae</big>\n\n<b>Rhizonitae, arum</b> <i>m</i>\nжители города Rhizon <i>L</i>."],
  ["rho", "<big>rho</big>\n\n<b>rhō</b> <i>n indecl.</i> (<i>греч.</i>)\nро (<i>название греческой буквы</i> ρ, <i>соотв. русскому</i> p) <i>C, Aus</i>."],
  ["Rhoda", "<big>Rhoda</big>\n\n<b>Rhoda, ae</b> <i>f</i>\nРода, <i>греч. порт в Hispania Tarraconensis, в области индигетов</i> (<i>ныне</i> Rosas) <i>L</i>."],
  ["Rhodanitis", "<big>Rhodanitis</big>\n\n<b>Rhodanītis, idis</b> <i>adj. f</i> [Rhodanus]\nроданская (urbes <i>Sid</i>)."],
  ["Rhodanus", "<big>Rhodanus</big>\n\n<b>Rhodanus, ī</b> <i>m</i>\nРодан, <i>главная река юго-вост. Галлии</i> (<i>ныне</i> Rhône) <i>Cs, H, Mela etc.</i>"],
  ["Rhodiacus", "<big>Rhodiacus</big>\n\n<b>Rhodiacus, a, um</b> [Rhodos]\nродосский <i>PM, Vtr</i>."],
  ["Rhodiensis", "<big>Rhodiensis</big>\n\n<b>I Rhodiēnsis, e</b> [Rhodos]\nродосский <i>Su, AG</i>.\n\n<b>II Rhodiēnsis, is</b> <i>m</i>\nродосец <i>Cato</i>."],
  ["rhodinus", "<big>rhodinus</big>\n\n<b>rhodinus, a, um</b> (<i>греч.</i>)\nприготовленный из роз, розовый (unguentum <i>PM</i>)."],
  ["Rhodius", "<big>Rhodius</big>\n\n<b>I Rhodius, a, um</b> [Rhodos]\nродосский: opus Rhodium <i>M</i> = Колосс Родосский; genus Rhodium (<i>sc.</i> dicendi) <i>Q</i> родосский стиль красноречия (<i>промежуточный между «азиатской» велеречивостью и «аттической» сжатостью</i>).\n\n<b>II Rhodius, ī</b> <i>m</i>\nродосец <i>C</i>."],
  ["rhododaphne", "<big>rhododaphne</big>\n\n<b>rhododaphnē, ēs</b> <i>f</i> (<i>греч.</i>)\nолеандр <i>PM</i>."],
  ["rhododendron", "<big>rhododendron</big>\n\n<b>rhododendron, ī</b> <i>n</i> (<i>греч.</i>)\nрододендрон, альпийская роза <i>PM</i>."],
  ["rhodomeli", "<big>rhodomeli</big>\n\n<b>rhodomeli</b> <i>n indecl.</i> <i>и</i> <b>rhodomelum, ī</b> <i>n</i>\nрозовый мёд <i>Is, Pall</i>."],
  ["Rhodopa", "<big>Rhodopa</big>\n\n<b>Rhodopa, ae</b> <i>и</i> <b>Rhodopē, ēs</b> <i>f</i>\nРодопа, <i>горная цепь в зап. Фракии между Гебром и Нестом</i> <i>V, O</i>."],
  ["Rhodopeius", "<big>Rhodopeius</big>\n\n<b>Rhodopē(i)us, a, um</b> [Rhodopa]\nродопский, <i>поэт.</i> фракийский: Rh. vates <i>или</i> heros <i>O</i> = Orpheus; Rhodopeia conjunx <i>St</i> = Procne; Rhodopeia regna <i>O</i> = Фракия."],
  ["Rhodos", "<big>Rhodos</big>\n\n<b>Rhodos (-us), ī</b> <i>f</i>\nРодос:\n<b>1)</b> <i>о-в у юго-зап. побережья Малой Азии с городом того же названия</i> (<i>был известен тридцатитрёхметровой бронзовой статуей бога солнца — «Колоссом Родосским», культом Аполлона, риторической школой и мореходным искусством</i>) <i>C, L, PM, O etc.</i>;\n<b>2)</b> <i>нимфа о-ва Родос, возлюбленная бога солнца</i> <i>O</i>."],
  ["Rhoduntia", "<big>Rhoduntia</big>\n\n<b>Rhoduntia, ae</b> <i>f</i>\nРодунтия, <i>одна из трёх вершин горы Oeta</i> (<i>Rh., Callidromus, Tichius</i>) <i>L</i>."],
  ["rhoea", "<big>rhoea</big>\n\n<b>rhoea, ae</b> <i>и</i> <b>rhoeas, adis</b> <i>f</i> (<i>греч.</i>)\nдикий мак <i>PM</i>."],
  ["Rhoecus", "<big>Rhoecus</big>\n\n<b>Rhoecus</b> <i>Lcn</i>\n= Rhoetus."],
  ["Rhoeteia", "<big>Rhoeteia</big>\n\n<b>Rhoetēia, ae</b> <i>f</i> [Rhoeteum]\nтроянка <i>O</i>."],
  ["Rhoeteius", "<big>Rhoeteius</big>\n\n<b>Rhoetēius, a, um</b> <i>V, Sil</i>\n= Rhoeteus."],
  ["Rhoeteum", "<big>Rhoeteum</big>\n\n<b>Rhoetēum, ī</b> <i>n</i>\nРетей:\n<b>1)</b> <i>мыс и город в Троаде, на Геллеспонте</i> <i>Mela, L</i>;\n<b>2)</b> (<i>sc.</i> mare) <i>море у мыса Rhoeteum</i> (profundum <i>O</i>)."],
  ["Rhoeteus", "<big>Rhoeteus</big>\n\n<b>I Rhoetēus, a, um</b> [Rhoeteum]\nретейский, <i>поэт.</i> троянский <i>O, PM</i>.\n\n<b>II Rhoete͡us, eī</b> <i>и</i> <b>eos</b> <i>m</i>\nРетей, <i>имя рутула</i> <i>V</i>."],
  ["Rhoetus", "<big>Rhoetus</big>\n\n<b>Rhoetus, ī</b> <i>m</i>\nРет:\n<b>1)</b> <i>кентавр, раненный на свадьбе Пирифоя Дриантом</i> <i>V, O, VF</i>;\n<b>2)</b> <i>один из гигантов, убитый Вакхом</i> <i>H</i>;\n<b>3)</b> <i>товарищ Финея, убитый Персеем</i> <i>O</i>;\n<b>4)</b> <i>царь маррувиев (марсов), отец Анхемола</i> <i>V</i>."],
  ["rhombus", "<big>rhombus</big>\n\n<b>rhombus, ī</b> <i>m</i> (<i>греч.</i>)\n<b>1)</b> волшебный волчок, магическое колесо <i>Prp, O, M</i>;\n<b>2)</b> <i>зоол.</i> камбала <i>или</i> палтус <i>H, PM etc.</i>;\n<b>3)</b> ромб <i>Frontin</i>."],
  ["rhomphaea", "<big>rhomphaea</big>\n\n<b>rhomphaea, ae</b> <i>f</i> (<i>греч.</i>)\nромфея, <i>длинный меч с широким обоюдоострым клинком (у не римских народов), по др. — длинное метательное копьё</i> <i>L, Cld</i>."],
  ["rhomphaealis", "<big>rhomphaealis</big>\n\n<b>rhomphaeālīs, e</b>\nимеющий вид меча-ромфеи: romphaeale incendium <i>Eccl</i> огненный меч."],
  ["rhonchus", "<big>rhonchus</big>\n\n<b>rhonchus</b> <i>v. l.</i>\n= ronchus."],
  ["Rhosiacus", "<big>Rhosiacus</big>\n\n<b>Rhōsiacus, a, um</b> [Rhosus]\nрозосский (vasa <i>C</i>)."],
  ["Rhosius", "<big>Rhosius</big>\n\n<b>Rhosius, a, um</b> <i>PM</i>\n= Rhosiacus."],
  ["Rhosos", "<big>Rhosos</big>\n\n<b>Rhōsos (-us), ī</b> <i>f</i>\nРозос, <i>приморский город в вост. Киликии, славившийся керамическими изделиями</i>, <i>Mela, PM</i>."],
  ["Rhoxolani", "<big>Rhoxolani</big>\n\n<b>Rhoxolānī, ōrum</b> <i>m</i> <i>v. l.</i>\n= Roxolani."],
  ["rhus", "<big>rhus</big>\n\n<b>rhus, rhois</b> (<i>acc.</i> rhun) <i>n, f</i> (<i>греч.</i>) <i>бот.</i>\nсумах дубильный (<i>Rhus coriaria</i>, L.) <i>CC, PM</i>."],
  ["rhythmicus", "<big>rhythmicus</big>\n\n<b>rhythmicus, i</b> <i>m</i> (<i>греч.</i>)\nзнаток <i>или</i> учитель метрики <i>C, Q</i>."],
  ["rhythmus", "<big>rhythmus</big>\n\n<b>rhythmus, i</b> <i>m</i> (<i>греч.</i>; <i>лат.</i> numerus)\nритм, равномерность, размер, соразмерность <i>Vr, Q</i>."],
  ["rhytium", "<big>rhytium</big>\n\n<b>rhytium, i</b> <i>n</i> (<i>греч.</i>)\nсосуд в виде рога <i>M</i>."],
  ["rica", "<big>rica</big>\n\n<b>rica, ae</b> <i>f</i>\nголовной платок, косынка (caput rica velatus <i>AG</i>)."],
  ["ricinium", "<big>ricinium</big>\n\n<b>ricinium, i</b> <i>n</i> [<i>demin. к</i> rica]\nнебольшой головной платок <i>LM</i>."],
  ["ricinus", "<big>ricinus</big>\n\n<b>I ricinus, i</b> <i>m</i>\n<b>1)</b> род крупного клеща (<i>паразитирующего на домашних животных</i>) <i>Cato, Vr, Col, Pt</i> —<i>см.</i> pediculus II;\n<b>2)</b> <i>бот.</i> клещевина <i>PM</i>.\n\n<b>II ricinus, a, um</b> [rica]\nпокрытый головным платком (mitra <i>Vr</i>)."],
  ["ricto", "<big>ricto</big>\n\n<b>ricto, —, —, are</b>\nкричать (<i>о леопарде</i>) <i>Spart</i>."],
  ["rictum", "<big>rictum</big>\n\n<b>rictum, i</b> <i>n</i> <i>Lcr, C</i>\n= rictus."],
  ["rictus", "<big>rictus</big>\n\n<b>rictus, us</b> <i>m</i> [ringor]\n<b>1)</b> раскрывание рта, открытый рот, оскал: risu diducere rictum alicujus <i>H</i> вызывать смех у кого-л.; sint modici rictus <i>O</i> не следует слишком широко раскрывать рот;\n<b>2)</b> пасть, зев (canino latrare rictu <i>J</i>; r. serpentis <i>O</i>);\n<b>3)</b> <i>поэт.</i> разрез глаз: distento rictu lumina nudantur <i>Lcn</i> веки раскрываются, обнажая глаза."],
  ["ridendus", "<big>ridendus</big>\n\n<b>ridendus, a, um</b>\n<b>1.</b> <i>part. fut. к</i> rideo;\n<b>2.</b> <i>adj.</i> смешной, забавный <i>Pt, Aus, Aug</i>."],
  ["rideo", "<big>rideo</big>\n\n<b>rideo, risi, risum, ere</b>\n<b>1)</b> смеяться (de aliqua re <i>и</i> in aliqua re <i>C</i>): r. malis alienis <i>H</i> смеяться натянутым смехом || язвительно смеяться (dolis repertis <i>V</i>); r. alicui <i>V</i> <i>или</i> aliquem <i>Ctl, O</i> улыбаться кому-л.; fortuna mihi ridet <i>O</i> счастье мне благоприятствует;\n<b>2)</b> быть <i>или</i> казаться весёлым, милым (omnia nunc rident <i>V</i>; ille terrarum angulus mihi ridet <i>H</i>): tempestas ridet <i>Lcr</i> стоит чудесная погода;\n<b>3)</b> блистать (ager ridet coloribus <i>O</i>; ridet argento domus <i>H</i>);\n<b>4)</b> насмехаться, издеваться, осмеивать (dolorem alicujus <i>Aus</i>; aliquem <i>C, Pt</i>);\n<b>5)</b> не обращать внимания, ни во что не ставить, пренебрегать (perjuria alicujus <i>Tib</i>; spes inanes alicujus <i>VF</i>; famae mendacia <i>O</i>);\n<b>6)</b> шутить, говорить в шутку (haec ego non rideo <i>C</i>)."],
  ["rideor", "<big>rideor</big>\n\n<b>rideor, —, (eri) </b> <i>depon.</i> <i>Pt</i>\n= rideo."],
  ["ridibundus", "<big>ridibundus</big>\n\n<b>ridibundus, a, um</b> [rideo]\nсмеющийся <i>Pl, Cato, AG</i>."],
  ["ridica", "<big>ridica</big>\n\n<b>ridica, ae</b> <i>f</i>\nтычина, <i>четырёхгранная подпорка для виноградных лоз</i> <i>Col, Cato</i>."],
  ["ridicleus", "<big>ridicleus</big>\n\n<b>ridicl(e)us, a, um</b> <i>Pt</i>\n= ridiculus I."],
  ["ridicularia", "<big>ridicularia</big>\n\n<b>ridicularia, orum</b> <i>n</i> [ridiculus I]\nшутки <i>Pl, Cato</i>."],
  ["ridicularis", "<big>ridicularis</big>\n\n<b>ridicularis, is</b> <i>m</i> <i>Is</i>\n= ridicularius."],
  ["ridicularius", "<big>ridicularius</big>\n\n<b>ridicularius, i</b> <i>m</i> [ridiculus I]\nшутник, балагур <i>AG</i>."],
  ["ridicule", "<big>ridicule</big>\n\n<b>ridicule</b> [ridiculus I]\nсмешно, забавным образом (dicere multa <i>C</i>); до смешного (homo r. insanus <i>C</i>)."],
  ["ridiculosus", "<big>ridiculosus</big>\n\n<b>ridiculosus, a, um</b> <i>Hier</i>\n= ridiculus I."],
  ["ridiculum", "<big>ridiculum</big>\n\n<b>ridiculum, i</b> <i>n</i>\nшутка, острота, острое словцо (per r. dicere <i>C</i>)."],
  ["ridiculus", "<big>ridiculus</big>\n\n<b>I ridiculus, a, um</b> [rideo]\n<b>1)</b> смешной, смехотворный, забавный (homo <i>Pl, J</i>; vultus <i>Q</i>; dictum <i>Pl, Q</i>): ridiculum dictu (<i>вводно</i>) <i>Ap</i> смешно сказать;\n<b>2)</b> достойный осмеяния, жалкий (poema <i>H</i>).\n\n<b>II ridiculus, i</b> <i>m</i> [rideo]\nшутник, забавник, балагур <i>Pl, Ter</i>."],
  ["rien", "<big>rien</big>\n\n<b>rien, rienis</b> <i>m</i> <i>Pl, Pt</i>\n= ren."],
  ["rigatio", "<big>rigatio</big>\n\n<b>rigatio, onis</b> <i>f</i> [rigo]\nполивка, орошение <i>Col, Pall</i>."],
  ["rigator", "<big>rigator</big>\n\n<b>rigator, oris</b> <i>m</i> [rigo]\nороситель <i>Eccl</i>."],
  ["rigatus", "<big>rigatus</big>\n\n<b>rigatus, (us) </b> <i>m</i> <i>Ambr</i>\n= rigatio."],
  ["rigefacio", "<big>rigefacio</big>\n\n<b>rige–facio, feci, —, ere</b>\nприводить в оцепенение, леденить (aliquem <i>Frontin</i>)."],
  ["rigens", "<big>rigens</big>\n\n<b>rigens, entis</b>\n<b>1.</b> <i>part. praes. к</i> rigeo;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> застывший, замёрзший (aqua <i>M</i>); остановившийся, неподвижный (oculi <i>PM</i>);\n<b>2)</b> непреклонный, негнущийся (animus <i>SenT</i>)."],
  ["rigeo", "<big>rigeo</big>\n\n<b>rigeo, gui, —, ere</b> [<i>одного корня с</i> frigus]\n<b>1)</b> быть застывшим, быть оцепеневшим, быть окоченелым (prata rigent <i>H</i>; r. frigore <i>C, PJ</i>); быть неподвижным <i>или</i> твёрдым (terga boum plumbo insuto rigebant <i>V</i>); glacie r. <i>V</i> оледенеть;\n<b>2)</b> быть туго набитым, полным (auro <i>V</i> <i>и</i> ex auro <i>Sil</i>; semine <i>O</i>);\n<b>3)</b> торчать вверх, стоять дыбом (comae rigebant terrore <i>O</i>); вздыматься, подниматься, возвышаться (late riget Tmolus <i>O</i>; sine frondibus arbor riget <i>O</i>)."],
  ["rigesco", "<big>rigesco</big>\n\n<b>rigesco, gui, —, ere</b> [<i>inchoat. к</i> rigeo]\n<b>1)</b> становиться жестким, твердеть (sole rigescunt electra <i>O</i>); терять гибкость, коченеть, цепенеть, застывать (aquae rigescunt in grandines <i>PM</i>);\n<b>2)</b> вставать дыбом (metu capilli riguerunt <i>O</i>);\n<b>3)</b> становиться сильным, мужать (saecula rigescunt <i>Cld</i>)."],
  ["rigide", "<big>rigide</big>\n\n<b>rigide</b> [rigidus]\n<b>1)</b> жёстко, крепко (tectorium obsolidare <i>Vtr</i>);\n<b>2)</b> прямо, по прямой линии (pilam mittere <i>Sen</i>);\n<b>3)</b> непреклонно, строго (disciplinam astringere <i>VM</i>)."],
  ["rigiditas", "<big>rigiditas</big>\n\n<b>rigiditas, atis</b> <i>f</i> [rigidus]\nтвёрдость, крепость (<i>sc.</i> materiei <i>Vtr</i>)."],
  ["rigido", "<big>rigido</big>\n\n<b>rigido, —, —, are</b>\nделать твёрдым, непреклонным (virtus rigidari potest <i>Sen</i>)."],
  ["rigidus", "<big>rigidus</big>\n\n<b>rigidus, a, um</b> [rigeo]\n<b>1)</b> жёсткий, твёрдый (cornu <i>O</i>); застывший, замёрзший (tellus <i>V</i>; aqua <i>O</i>); леденящий (frigus <i>Lcr</i>);\n<b>2)</b> вздымающийся, прямой (columna <i>O</i>); торчащий вверх, стоящий дыбом (capilli <i>O</i>); тугой, вытянутый, негнущийся (crura <i>C</i>);\n<b>3)</b> закалённый в труде, выносливый (fossor <i>M</i>);\n<b>4)</b> неподвижный, невозмутимый (vultus <i>O</i>); непоколебимый, неумолимый (mens <i>O</i>); суровый, строгий (censor <i>O</i>; sententia <i>PJ</i>);\n<b>5)</b> грубый (signa <i>C</i>; mores <i>O</i>); свирепый, жестокий (fera <i>O</i>; ensis <i>V, O</i>)."],
  ["rigo", "<big>rigo</big>\n\n<b>rigo, avi, atum, are</b>\n<b>1)</b> проводить, распределять (aquam per agros <i>L</i>; <i>перен.</i> motus per membra rigantur <i>Lcr</i>);\n<b>2)</b> орошать, поливать (arva <i>H</i>; fluvius rigat campos <i>PM</i>; ora lacrimis r. <i>V</i>);\n<b>3)</b> поить (natos vitali rore <i>Poeta ap. C</i>);\n<b>4)</b> наполнять (lumen terras rigando complet <i>Lcr</i>)."],
  ["Rigodulum", "<big>Rigodulum</big>\n\n<b>Rigodulum, i</b> <i>n</i>\nРигодул, <i>город треверов в Gallia Belgica</i> (<i>ныне</i> Reol <i>на реке Мозель</i>) <i>T</i>."],
  ["rigor", "<big>rigor</big>\n\n<b>rigor, oris</b> <i>m</i> [rigeo]\n<b>1)</b> твёрдость, жёсткость (ferri <i>V</i>);\n<b>2)</b> напряжённость: r. nervorum <i>CC</i> судорога, столбняк;\n<b>3)</b> окоченелость, оцепенение (cervicis <i>PM</i>); холод (membra rigore torpent <i>L</i>);\n<b>4)</b> строгость, непреклонность, твёрдость (animi <i>T</i>; disciplinae militaris <i>T</i>); жёсткость, отсутствие гибкости, однообразие (r. accentus <i>Q</i>)."],
  ["rigoro", "<big>rigoro</big>\n\n<b>rigoro, (avi), atum, are</b> [rigor]\nделать тугим, выпрямлять, подпирать (traduces rigorati <i>PM</i>)."],
  ["rigua", "<big>rigua</big>\n\n<b>rigua, orum</b> <i>n</i>\n<b>1)</b> орошение <i>PM</i>;\n<b>2)</b> орошённые пространства, поливные поля <i>PM</i>."],
  ["rigui", "<big>rigui</big>\n\n<b>rigui</b> <i>pf. к</i>\nrigesco."],
  ["riguus", "<big>riguus</big>\n\n<b>I riguus, i</b> <i>m</i> [rigo]\n<b>1)</b> орошение <i>PM</i>;\n<b>2)</b> оросительный канал <i>PM</i>.\n\n<b>II riguus, a, um</b> [rigo]\n<b>1)</b> орошающий (amnis <i>V</i>);\n<b>2)</b> орошаемый, орошённый (hortus <i>O</i>);\n<b>3)</b> растущий на болоте, болотный (ulmus <i>PM</i>);\n<b>4)</b> тугой, переполненный (bos rigua lacte <i>Sol</i>)."],
  ["rima", "<big>rima</big>\n\n<b>rima, ae</b> <i>f</i>\n<b>1)</b> трещина, щель: rimas facere <i>O</i> (agere <i>C</i> <i>или</i> ducere <i>O</i>) трескаться; rimis dehiscere <i>V</i> давать брешь <i>или</i> течь; plenus rimarum <i>Ter</i> весь в трещинах, <i>т. е.</i> страшно болтливый, не умеющий хранить секреты; ignea rima <i>V</i> молния;\n<b>2)</b> лазейка, выход (aliquam reperire rimam <i>Pl</i>)."],
  ["rimabundus", "<big>rimabundus</big>\n\n<b>rimabundus, a, um</b> [rimor]\nобдумывающий, исследующий (aliquid <i>Ap</i>)."],
  ["rimator", "<big>rimator</big>\n\n<b>rimator, oris</b> <i>m</i> [rimor]\nисследователь, изыскатель <i>Eccl</i>."],
  ["rimo", "<big>rimo</big>\n\n<b>rimo, —, —, ire</b> <i>Pac, Sid</i>\n= rimor."],
  ["rimor", "<big>rimor</big>\n\n<b>rimor, atus sum, ari</b> [rima]\n<b>1)</b> взрывать, разрыхлять (terram rastris <i>V</i>);\n<b>2)</b> обшаривать, рыться, обыскивать, перерывать (ipsam humum <i>T</i>);\n<b>3)</b> осматривать, исследовать (exta <i>J</i>); стараться разузнать, выведывать (secreta <i>T</i>); разнюхивать (canes naribus auras rimantur <i>O</i>); разыскивать, «откопать» (fustem cunctis vastiorem <i>Ap</i>)."],
  ["rimosus", "<big>rimosus</big>\n\n<b>rimosus, a, um</b> [rima]\nщелистый, растрескавшийся, с трещинами (aedificium <i>Col</i>); пористый, губчатый (pulmo <i>AG</i>); утлый, дырявый (cumba <i>V</i>): auris rimosa <i>H</i> дырявое ухо (<i>о болтуне, не умеющем держать что-л. в секрете</i>)."],
  ["rimula", "<big>rimula</big>\n\n<b>rimula, ae</b> <i>f</i> [<i>demin. к</i> rima]\nмаленькая щель, трещинка <i>Boet, Aug, Lact</i>."],
  ["ringor", "<big>ringor</big>\n\n<b>ringor, (rictus sum), ringi</b> <i>depon.</i>\nразевать рот, скалить зубы, <i>перен.</i> хмуриться, втайне сердиться <i>Ter, H, Sid</i>: aliquem facere ringentem <i>Pt</i> раздражать кого-л."],
  ["rinocer-", "<big>rinocer-</big>\n\n<b>rinocer-</b> <i>v. l.</i>\n= rhinocer-."],
  ["ripa", "<big>ripa</big>\n\n<b>ripa, ae</b> <i>f</i>\nберег реки (r. fluminis <i>C, L, VP etc.</i>); <i>поэт.</i> (= litus) морской берег (r. aequoris <i>H</i>)."],
  ["ripariensis", "<big>ripariensis</big>\n\n<b>ripar(i)ensis, e</b> [ripa]\nбереговой, расположенный на берегу (legiones <i>и</i> milites <i>Vop, CTh, CJ</i>)."],
  ["riparius", "<big>riparius</big>\n\n<b>riparius, a, um</b> [ripa]\nбереговой, прибрежный (hirundo <i>PM</i>)."],
  ["ripensis", "<big>ripensis</big>\n\n<b>ripensis, e</b> <i>CTh</i>\n= riparensis."],
  ["Riphaeus", "<big>Riphaeus</big>\n\n<b>Rip(h)aeus, a, um</b>\n<b>1)</b> рифейский; Rip(h)aei montes <i>Mela, PM, Just etc.</i> Рифейские горы (<i>баснословные горы на сев. краю земли; по др.</i> — Уральские);\n<b>2)</b> <i>поэт.</i> северный, холодный (pruina <i>V</i>; arces <i>V</i>); бурный (Eurus <i>V</i>)."],
  ["ripula", "<big>ripula</big>\n\n<b>ripula, ae</b> <i>f</i> [<i>demin. к</i> ripa]\nбережок <i>C</i>."],
  ["riscus", "<big>riscus</big>\n\n<b>riscus, i</b> <i>m</i> (<i>греч.</i>)\nобтянутая кожей корзина из ивовых прутьев, сундук <i>Ter, Dig, Hier</i>."],
  ["risi", "<big>risi</big>\n\n<b>risi</b> <i>pf. к</i>\nrideo."],
  ["risibilis", "<big>risibilis</big>\n\n<b>risibilis, e</b> [rideo]\nсмешной, смехотворный <i>Eccl</i>."],
  ["risiloquium", "<big>risiloquium</big>\n\n<b>risi–loquium, i</b> <i>n</i>\nнасмешливая речь <i>Tert</i>."],
  ["risio", "<big>risio</big>\n\n<b>risio, onis</b> <i>f</i> [rideo]\nсмех <i>Pl</i>."],
  ["risito", "<big>risito</big>\n\n<b>risito, —, —, are</b> [<i>frequ. к</i> rideo]\nвысмеивать, осмеивать (aliquid <i>Naev</i>)."],
  ["risor", "<big>risor</big>\n\n<b>risor, oris</b> <i>m</i> [rideo]\nхохотун, насмешник <i>H</i>."],
  ["risorius", "<big>risorius</big>\n\n<b>risorius, a, um</b>\n<b>1)</b> смехотворный <i>Eccl</i>;\n<b>2)</b> смеющийся (os <i>Aug</i>)."],
  ["risus", "<big>risus</big>\n\n<b>I risus, a, um</b> <i>part. pf. к</i>\nrideo.\n\n<b>II risus, us</b> <i>m</i> [rideo]\nсмех, улыбка, <i>тж.</i> насмешка: risum movere <i>C, Ph</i> (facere, concitare <i>C</i>, praebere <i>Just</i>) возбуждать смех; risum edere <i>C</i> смеяться; risum captare <i>C, H</i> стараться рассмешить; risui esse alicui <i>L</i> <i>и</i> r. esse alicui <i>O</i> быть чьим-л. посмешищем."],
  ["rite", "<big>rite</big>\n\n<b>rite</b> <i>adv.</i> [ritus]\n<b>1)</b> по установленному обряду, с соблюдением церемониала, с надлежащими церемониями (r. deos colere <i>C</i>);\n<b>2)</b> по обычаю, по обыкновению, как принято (r. trahere plaustra <i>H</i>); как обыкновенно делается, в соответствии с установленными формами (testes r. affuerunt <i>rhH</i>); надлежащим образом, как следует (rebus jam r. paratis <i>V</i>);\n<b>3)</b> правильно, по праву, по справедливости (quae potest appellari r. sapientia <i>C</i>)."],
  ["ritualis", "<big>ritualis</big>\n\n<b>ritualis, e</b> [ritus]\nритуальный, обрядовый (libri <i>C</i>)."],
  ["ritualiter", "<big>ritualiter</big>\n\n<b>ritualiter</b>\nсогласно установленным обрядам (r. consecratus <i>Amm</i>)."],
  ["ritus", "<big>ritus</big>\n\n<b>ritus, us</b> <i>m</i>\n<b>1)</b> религиозный обряд, ритуал, церемониал богослужения, установленная форма, принятый порядок (sacrificii r. <i>L</i>): simulacrorum ritui deditus <i>Aug</i> идолопоклонник;\n<b>2)</b> обычай, обыкновение (alieni ritus moresque <i>L</i>): ritu тем или иным образом, по образу чего-либо, наподобие, как (ritu barbarico <i>Pl</i>; fluminis ritu <i>H</i>)."],
  ["rivalis", "<big>rivalis</big>\n\n<b>I rivalis, e</b> [rivus]\nживущий в ручье (allecula <i>Col</i>).\n\n<b>II rivalis, is</b> <i>m</i>\n<b>1)</b> пользующийся водой из того же ручья, сосед по оросительному каналу <i>AG, Dig</i>;\n<b>2)</b> соперник (<i>в любви</i>) <i>Pl, Ter, O etc.</i>"],
  ["rivalitas", "<big>rivalitas</big>\n\n<b>rivalitas, atis</b> <i>f</i> [rivalis]\nсоперничество <i>C, Ap</i>."],
  ["rivatim", "<big>rivatim</big>\n\n<b>rivatim</b> [rivus]\nручьями (fluere <i>Macr</i>)."],
  ["rivo", "<big>rivo</big>\n\n<b>rivo, —, —, are</b> [rivus]\nотводить (fontes in atria <i>Eccl</i>)."],
  ["rivulus", "<big>rivulus</big>\n\n<b>rivulus, i</b> <i>m</i> [<i>demin. к</i> rivus]\n<b>1)</b> ручеёк <i>Vr, C, Eccl</i>;\n<b>2)</b> маленький канал: rivulo labi <i>Ap</i> стекать по канальцу;\n<b>3)</b> узкий проход <i>CC</i>."],
  ["rivus", "<big>rivus</big>\n\n<b>rivus, i</b> <i>m</i>\n<b>1)</b> ручей (purae r. aquae <i>H</i>): e rivo flumina magna facere <i>погов. O</i> = делать из мухи слона;\n<b>2)</b> канава, канал (rivos ducere <i>O</i> <i>и</i> effodere <i>T</i>);\n<b>3)</b> поток (lacrimarum <i>O</i>; auri <i>Lcr</i>);\n<b>4)</b> течение, ход (fortunae <i>H</i>)."],
  ["rixa", "<big>rixa</big>\n\n<b>rixa, ae</b> <i>f</i>\n<b>1)</b> ссора, драка (jurgia primum, mox r. <i>T</i>): rixam fecisse <i>Pt</i> затеять ссору;\n<b>2)</b> борьба (cum oceano <i>Fl</i>)."],
  ["rixator", "<big>rixator</big>\n\n<b>rixator, oris</b> <i>m</i> [rixor]\nспорщик, сварливый человек <i>Q</i>."],
  ["rixatorius", "<big>rixatorius</big>\n\n<b>rixatorius, a, um</b>\nспорщицкий (r. et jurgiosus <i>Fronto</i>)."],
  ["rixo", "<big>rixo</big>\n\n<b>rixo, —, —, are</b> <i>Vr</i>\n= rixor."],
  ["rixor", "<big>rixor</big>\n\n<b>rixor, atus sum, ari</b> <i>depon.</i>\nссориться, шумно спорить (cum aliquo de aliqua re <i>C</i>): r. de lana caprina — <i>см.</i> lana || бороться (cum fortuna <i>Sen, Q</i>)."],
  ["rixosus", "<big>rixosus</big>\n\n<b>rixosus, a, um</b>\nсварливый, задиристый <i>Col, Vlg, Tert</i>."],
  ["robeus", "<big>robeus</big>\n\n<b>robeus, a, um</b> <i>v. l.</i>\n= rubeus I."],
  ["Robigalia", "<big>Robigalia</big>\n\n<b>Robigalia, ium</b> <i>n</i>\nРобигалии, <i>праздник в честь Robigo</i> (<i>25 апреля</i>) <i>Vr, PM</i>."],
  ["robigino", "<big>robigino</big>\n\n<b>robigino, —, —, are</b> [robigo]\nпокрываться ржавчиной, ржаветь (gladius situ robiginat <i>Ap</i>)."],
  ["robiginosus", "<big>robiginosus</big>\n\n<b>robiginosus, a, um</b> [robigo]\n<b>1)</b> ржавый, заржавелый (strigilis <i>Pl</i>);\n<b>2)</b> завистливый (robiginosis dentibus rodere <i>M</i>)."],
  ["robigo", "<big>robigo</big>\n\n<b>I robigo (rubigo), inis</b> <i>f</i> [<i>одного корня с</i> ruber, rufus]\n<b>1)</b> ржавчина (ferrum robigine roditur <i>O</i>);\n<b>2)</b> бездействие (ingenium longa rubigine laesum <i>O</i>);\n<b>3)</b> недостаток, порок (animorum <i>Sen</i>);\n<b>4)</b> гложущая зависть (r. dentium alicujus <i>M</i>);\n<b>5)</b> хлебная головня, ржавчина, ржа (<i>грибковая болезнь</i>) <i>Vr, Col, H, PM</i>.\n\n<b>II Robigo (Rubigo), inis</b> <i>f</i> <i>и</i> <b>Robigus (Rubigus), i</b> <i>m</i>\n<i>римское божество, предохраняющее злаки от хлебной головни</i> <i>Vr, O, AG, Eccl</i>."],
  ["robius", "<big>robius</big>\n\n<b>robius</b> <i>v. l.</i>\n= rubeus I."],
  ["robor", "<big>robor</big>\n\n<b>robor, oris</b> <i>n</i> <i>v. l.</i>\n= robur."],
  ["roborarium", "<big>roborarium</big>\n\n<b>roborarium, i</b> <i>n</i> [robur]\nзагон для диких животных, обнесённый крепким деревянным забором, охотничий парк <i>AG</i>."],
  ["roboreus", "<big>roboreus</big>\n\n<b>roboreus, a, um</b> [robur]\nдубовый (pons <i>O</i>; moles <i>Pt</i>)."],
  ["roboro", "<big>roboro</big>\n\n<b>roboro, avi, atum, are</b> [robur]\nделать крепким, сильным, укреплять (artus <i>Lcr</i>; animos <i>VM</i>; eloquentiam <i>C</i>)."],
  ["robur", "<big>robur</big>\n\n<b>robur, oris</b> <i>n</i>\n<b>1)</b> (<i>тж.</i> r. querceum <i>Col</i>) древесина дуба, дубовое дерево (navis facta ex robore <i>Cs</i>);\n<b>2)</b> изделие из дуба: r. sacrum (cavum) <i>V</i> = Троянский конь; r. aratri <i>V</i> дубовый плуг; r. praefixum ferro <i>V</i> дубовое копьё с железным наконечником; in robore accumbere <i>C</i> возлежать на дубовых ложах;\n<b>3)</b> твёрдая древесная порода (<i>дикая малина и пр.</i>) <i>V, St</i>;\n<b>4)</b> дуб (robora agitare <i>O</i>);\n<b>5)</b> ствол (annoso cum robore quercum eruere <i>V</i>);\n<b>6)</b> крепость, твёрдость, прочность (r. ferri <i>V</i>; saxi <i>Lcr</i>);\n<b>7)</b> сила, бодрость, мощь (crescere et robora sumere <i>Lcr</i>; r. juventae <i>L</i>; r. animi <i>C</i>);\n<b>8)</b> сердцевина, ядро, отборная часть, цвет, лучшая часть (militum <i>C</i>; virorum <i>PJ</i>; legionum <i>L</i>);\n<b>9)</b> опорная точка, база (r. belli <i>T</i>);\n<b>10)</b> робур, <i>подземная тюрьма в римском carcer Mamertinus для самых важных государственных преступников; по имени её основателя, Сервия Туллия, называлась также Tullianum</i> <i>L, T, H</i>."],
  ["roburneus", "<big>roburneus</big>\n\n<b>roburneus, a, um</b> <i>Col</i>\n= roboreus."],
  ["robus", "<big>robus</big>\n\n<b>I robus, a, um</b> <i>арх. J</i>\n= rufus I.\n\n<b>II robus, oris</b> <i>n</i> <i>Cato, Col</i>\n= robur."],
  ["robuste", "<big>robuste</big>\n\n<b>robuste</b>\nпрочно, крепко (alligare aliquem <i>Aug</i>)."],
  ["robusteus", "<big>robusteus</big>\n\n<b>robusteus, a, um</b> <i>Vtr</i>\n= roboreus."],
  ["robustus", "<big>robustus</big>\n\n<b>robustus, a, um</b> [robur]\n<b>1)</b> дубовый (fores <i>H</i>);\n<b>2)</b> твёрдый, крепкий (cornu <i>PM</i>);\n<b>3)</b> сильный, мощный, могучий (corpus <i>Su</i>; vox <i>PM</i>; animus <i>C</i>)."],
  ["rodo", "<big>rodo</big>\n\n<b>rodo, rosi, rosum, ere</b> [<i>одного корня с</i> rado]\n<b>1)</b> грызть, глодать, обгрызать (reliquias cibi <i>PM</i>; vitem <i>O</i>; ungues <i>H</i>); разъедать (ferrum rubigine roditur <i>O</i>);\n<b>2)</b> размывать (flumen rodit ripas <i>Lcr</i>);\n<b>3)</b> умалять, уничижать, злословить (aliquem absentem <i>H</i>): murmura secum r. <i>Pers</i> бормотать сквозь зубы;\n<b>4)</b> терзать, снедать (cura rodit aliquem <i>Aug</i>)."],
  ["rodus", "<big>rodus</big>\n\n<b>rodus</b> <i>v. l.</i>\n= rudus II <i>и</i> raudus."],
  ["rodusculum", "<big>rodusculum</big>\n\n<b>rodusculum, i</b> <i>n</i> <i>v. l.</i>\n= raudusculum."],
  ["rogalis", "<big>rogalis</big>\n\n<b>rogalis, e</b> [rogus]\nгорящий на костре (flamma <i>O</i>); сжигаемый на костре (cadaver <i>Sid</i>)."],
  ["rogamentum", "<big>rogamentum</big>\n\n<b>rogamentum, i</b> <i>n</i> [rogo] (<i>греч.</i> axioma)\nположение, утверждение <i>Ap</i>."],
  ["rogassit", "<big>rogassit</big>\n\n<b>rogassit</b> <i>арх. C</i>\n= rogaverit (<i>pf. conjct. к</i> rogo)."],
  ["rogaticius", "<big>rogaticius</big>\n\n<b>rogaticius, a, um</b> [rogo]\nзаимствованный (verba <i>Fronto</i>)."],
  ["rogatio", "<big>rogatio</big>\n\n<b>rogatio, onis</b> <i>f</i> [rogo]\n<b>1)</b> вопрос, запрос <i>C, Q</i>;\n<b>2)</b> внесение предложения, законопроект (rogationem ad populum ferre <i>Cs etc.</i>);\n<b>3)</b> просьба (injusta <i>VM</i>)."],
  ["rogatiuncula", "<big>rogatiuncula</big>\n\n<b>rogatiuncula, ae</b> <i>f</i> [<i>demin. к</i> rogatio]\n<b>1)</b> небольшой вопрос <i>C</i>;\n<b>2)</b> небольшое предложение, законопроектец <i>C</i>."],
  ["rogator", "<big>rogator</big>\n\n<b>rogator, oris</b> <i>m</i> [rogo]\n<b>1)</b> автор предложения, вносящий законопроект (r. legum <i>LM</i>);\n<b>2)</b> собиратель голосов на выборах, проводящий голосование: r. primus <i>C</i> собиратель голосов в centuria praerogativa; r. comitiorum <i>C</i> председательствующий в комициях;\n<b>3)</b> попрошайка, нищий <i>M</i>."],
  ["rogatus", "<big>rogatus</big>\n\n<b>rogatus, (us)</b> <i>m</i> (<i>встреч. тк.</i> <i>abl. sg.</i>) [rogo]\nпросьба: meo rogatu <i>C</i> по моей просьбе."],
  ["rogitatio", "<big>rogitatio</big>\n\n<b>rogitatio, onis</b> <i>f</i> <i>Pl</i>\n= rogatio 2."],
  ["rogito", "<big>rogito</big>\n\n<b>rogito, avi, atum, are</b> [<i>intens. к</i> rogo]\n<b>1)</b> спрашивать, расспрашивать (multa super aliquo <i>V</i>); разузнавать, осведомляться: r. pisces <i>Pl</i> осведомляться относительно (цены) рыбы;\n<b>2)</b> просить, упрашивать (aliquem ut veniat <i>Ter</i>)."],
  ["rogo", "<big>rogo</big>\n\n<b>rogo, avi, atum, are</b>\n<b>1)</b> спрашивать (aliquem aliquid <i>H</i> <i>или</i> de aliqua re <i>C etc.</i>): (etiam) rogas? <i>Pl, Ter, C</i> ты ещё спрашиваешь?; non sua sponte, sed rogatus a Gallis <i>Cs</i> не по собственному почину, а по просьбе галлов || запрашивать, обращаться с запросом: aliquem sententiam r. <i>C</i> запрашивать кого-л. о мнении <i>или</i> просить высказаться (в порядке голосования);\n<b>2)</b> вносить предложение, предлагать: r. populum (plebem, legem) <i>C</i> вносить законопроект на обсуждение народа; provinciam alicui r. <i>C</i> вносить предложение о передаче кому-л. провинции в управление; comitra consulibus rogandis <i>C</i> комиции для избрания консулов; aliquo rogante <i>C</i> по чьему-л. предложению; r. magistratum <i>C</i> выдвинуть кандидата на государственный пост; aliquem sacramento rogare <i>Cs, L</i> приводить кого-л. к присяге (<i>преим.</i> воинской); uti rogas (<i>сокращ.</i> U. R.) так, как ты предлагаешь (<i>формула согласия</i>): «uti rogas» omnes tribus jusserunt <i>L</i> все трибы высказались за (это) предложение;\n<b>3)</b> просить, упрашивать, испрашивать (aliquem de aliqua re <i>или</i> aliquid <i>C etc.</i>; aliquid ab aliquo <i>Pl, Sl</i>): r, pro aliquo <i>Pt</i> просить за кого-л. || приглашать (in consilium <i>PJ, AG</i>; ad prandium <i>Lampr</i>)."],
  ["rogus", "<big>rogus</big>\n\n<b>rogus, i</b> <i>m</i>\n<b>1)</b> костер (rogum exstruere <i>C</i>, accendere <i>L</i>): ad extremos rogos <i>O</i> до погребального костра, <i>т. е.</i> до самой кончины || <i>поэт.</i> истребление, уничтожение: defugiunt avidos carmina sola rogos <i>O</i> одна лишь поэзия ускользает от уничтожения;\n<b>2)</b> могила (rogi herbosi <i>Prp</i>)."],
  ["Roma", "<big>Roma</big>\n\n<b>Rōma, ae</b> <i>f</i>\n<b>1)</b> Рим, <i>столица Латия, а затем и всей Римской империи, на левом берегу Тибра, в 16 римск. милях (ок. 24 км) от моря; основан, по преданию, Ромулом в 753 г. до н. э. (у римлян обычно наз.</i> Urbs <i>Vr, C etc.</i>); <i>обычные эпитеты</i>: aeterna <i>Tib</i>, pulcherrima <i>V</i>, superba <i>Prp</i>, princeps urbium <i>H</i>, beata <i>H</i>, domina <i>H</i>, septemgemina <i>Sl</i>, <i>но тж.</i> ferox <i>H</i>;\n<b>2)</b> Рома, <i>богиня-покровительница города Рима</i> <i>L, T</i>."],
  ["Romana", "<big>Romana</big>\n\n<b>I Rōmāna, ae</b> <i>f</i>\nримлянка <i>L, VP etc.</i>\n\n<b>II Rōmāna, ōrum</b> <i>n</i>\n<b>1)</b> римские владения <i>Sen</i>;\n<b>2)</b> римская история <i>Sen</i>."],
  ["Romane", "<big>Romane</big>\n\n<b>Rōmānē</b> <i>AG</i>\n= Romano more (<i>см.</i> Romanus 1)."],
  ["Romanensis", "<big>Romanensis</big>\n\n<b>Rōmānēnsis, e</b> <i>Vr</i>\n= Romanicus."],
  ["Romanicus", "<big>Romanicus</big>\n\n<b>Rōmānicus, a, um</b> <i>и</i> <b>Rōmāniēnsis, e</b>\nримский <i>Cato</i>."],
  ["Romanula porta", "<big>Romanula porta</big>\n\n<b>Rōmānula porta</b> <i>f</i>\n<i>римск. ворота с зап. стороны Палатинского холма</i> <i>Vr</i>."],
  ["Romanus", "<big>Romanus</big>\n\n<b>I Rōmānus, a, um</b> [Roma]\nримский (civis, populus <i>C</i>); nomen Romanum <i>C, Sl, L</i> римский народ; lingua Romana <i>O, VP, PJ, Ap</i> = lingua Latina; ludi Romani (<i>тж.</i> magni <i>или</i> maximi) <i>C etc.</i> Римские игры (<i>самые древние римские игрища, ежегодно 4 сентября</i>); Romano more по-римски, <i>в смысле</i> прямо, просто, открыто (loqui <i>C</i>).\n\n<b>II Rōmānus, ī</b> <i>m</i>\nримлянин <i>C etc.</i>"],
  ["Romilius", "<big>Romilius</big>\n\n<b>Rōmilius, a, um</b> [Romulus]\n<i>название одной из римских триб</i> <i>Vr, C</i>."],
  ["romph-", "<big>romph-</big>\n\n<b>romph-</b> <i>v. l.</i>\n= rhomph-."],
  ["Romularis", "<big>Romularis</big>\n\n<b>Rōmulāris, e</b> <i>Sid</i>\n= Romuleus."],
  ["Romuleus", "<big>Romuleus</big>\n\n<b>Rōmuleus, a, um</b> <i>и</i> <b>Rōmulus, a, um</b> [Romulus]\nромулов (ensis <i>O</i>): Romulea fera <i>J</i> = lupa || <i>перен.</i> = Romanus (tellus <i>V</i>): Romulea urbs <i>O</i> = Roma."],
  ["Romulides", "<big>Romulides</big>\n\n<b>Rōmulidēs, ae</b> <i>m</i> <i>поэт.</i>\nсын (отпрыск) Ромула, <i>т. е.</i> римлянин <i>Lcr, V etc.</i>"],
  ["Romulius", "<big>Romulius</big>\n\n<b>Rōmulius</b> <i>v. l.</i>\n= Romilius."],
  ["Romulus", "<big>Romulus</big>\n\n<b>Rōmulus, ī</b> <i>m</i>\nРомул, <i>сын Реи Сильвии и Марса, брат-близнец Рема, миф. основатель и первый царь Рима</i> (<i>753-716 гг. до н. э.</i>), <i>по смерти обожествлённый под именем Quirinus</i> <i>C, L etc.</i>"],
  ["ronchisonus", "<big>ronchisonus</big>\n\n<b>ronchi–sonus, a, um</b>\nхрапящий (rhinoceros <i>Sid</i>)."],
  ["roncho", "<big>roncho</big>\n\n<b>roncho, —, —, are</b> [ronchus]\nхрапеть <i>Sid</i>."],
  ["ronchus", "<big>ronchus</big>\n\n<b>ronchus, i</b> <i>m</i> (<i>греч.</i>)\n<b>1)</b> храпение <i>M</i>;\n<b>2)</b> кваканье <i>Ap</i>;\n<b>3)</b> передразнивание, насмешка <i>M</i>."],
  ["ronco", "<big>ronco</big>\n\n<b>ronco</b> <i>v. l.</i>\n= roncho."],
  ["rorarii", "<big>rorarii</big>\n\n<b>rōrāriī, ōrum</b> <i>m</i>\nрорарии, <i>категория молодых легковооружённых солдат</i> (<i>они набирались из пятого цензового класса; в бою играли роль застрельщиков, но постоянное их место в строю было позади триариев</i>) <i>Vr, L</i>."],
  ["roratio", "<big>roratio</big>\n\n<b>rōrātio, ōnis</b> <i>f</i> [roro]\n<b>1)</b> появление росы (rorationes nocturnae <i>Ap</i>);\n<b>2)</b> опадание виноградных ягод (<i>вследствие холодных рос</i>) <i>PM</i>;\n<b>3)</b> падение капель в водяных часах <i>Eccl</i>."],
  ["roresco", "<big>roresco</big>\n\n<b>rōrēsco, —, —, ere</b> [ros]\nпревращаться в росу <i>O</i> (<i>v. l.</i>)."],
  ["roridus", "<big>roridus</big>\n\n<b>rōridus, a, um</b> [ros]\nпокрытый росой, мокрый от росы, росистый <i>Prp, Ap</i>."],
  ["rorifer", "<big>rorifer</big>\n\n<b>rōri–fer, fera, ferum</b> [ros]\nприносящий росу (undae <i>Lcr</i>; aura <i>SenT</i>)."],
  ["roro", "<big>roro</big>\n\n<b>rōro, āvī, ātum, āre</b> [ros]\n<b>1)</b> падать (росой): rorat <i>Col, Su</i> росит, падает роса || <i>перен.</i> капать, струить по капле, источать (vepres rorant sanguine <i>V</i>): pocula rorantia <i>C</i> бокалы, из которых вино льётся по каплям; rorans juvenis <i>Man</i> юноша-виночерпий (Ганимед), <i>т. е.</i> созвездие Водолея;\n<b>2)</b> быть мокрым, влажным, струиться (capilli rorantes <i>O</i>);\n<b>3)</b> покрывать росой, окроплять, орошать, увлажнять, смачивать (r. genas lacrimis <i>Lcr</i>; roratae rosae <i>O</i>)."],
  ["rorulentus", "<big>rorulentus</big>\n\n<b>rōrulentus, a, um</b> <i>Cato, Col, PM</i>\n= roridus."],
  ["ros", "<big>ros</big>\n\n<b>I ros, roris</b> <i>m</i>\n<b>1)</b> роса (r. nocturnus <i>Cs</i>; r. cadit <i>Pl etc.</i>);\n<b>2)</b> влага, жидкость (r. lacrimarum <i>O</i>): r. vitalis <i>Poeta ap. C</i> животворящая влага (<i>о материнском молоке</i>); rores pluvii <i>H</i> мелкий дождь, изморось; r. vivus <i>O</i> проточная вода; rores exigui vini <i>Eccl</i> капли вина;\n<b>3)</b> бальзам, мазь (r. Arabus <i>O</i>);\n<b>4)</b>: r. (marinus) <i>V, H, Col</i>, <i>тж.</i> r. maris <i>O бот.</i> розмарин.\n\n<b>II ros, roris</b> <i>Col</i>\n= rhus."],
  ["rosa", "<big>rosa</big>\n\n<b>rosa, ae</b> <i>f</i>\n<b>1)</b> роза, <i>собир.</i> розы (reticulum plenum rosae <i>C</i>): in rosa <i>C etc.</i> увенчанный розами; multa in rosa <i>H</i> на ложе из роз; quum regnat r. <i>M</i> когда царят розы, <i>т. е.</i> во время пиров; vivere in aeterna rosa <i>M</i> <i>или</i> in rosa jacere <i>погов. Sen</i> = жить среди вечных наслаждений; sub rosa dicta tacenda (<i>sc.</i> sunt) <i>поздн. погов.</i> сказанное под розой (<i>т. е.</i> в часы интимной попойки) не подлежит оглашению;\n<b>2)</b> розовое масло, розовая мазь <i>CC</i>;\n<b>3)</b> розовый куст (flores rosae <i>CC, H etc.</i>): r. laurea <i>Ap</i> олеандр."],
  ["rosaceum", "<big>rosaceum</big>\n\n<b>rosaceum, i</b> <i>n</i> [rosa]\nрозовое масло <i>PM</i>."],
  ["rosaceus", "<big>rosaceus</big>\n\n<b>rosaceus, a, um</b> [rosa]\n(сделанный) из роз, розовый (corona, oleum <i>PM</i>)."],
  ["rosans", "<big>rosans</big>\n\n<b>rosans, antis</b> <i>adj.</i>\nрозового цвета, розовый <i>V</i>."],
  ["rosarium", "<big>rosarium</big>\n\n<b>rosarium, i</b> <i>n</i> [rosa]\nместо, усаженное розовыми кустами, розовый сад, розарий <i>Col, V etc.</i>"],
  ["rosarius", "<big>rosarius</big>\n\n<b>rosarius, a, um</b> [rosa]\nприготовленный из роз, розовый <i>Su, Ap</i>."],
  ["rosatum", "<big>rosatum</big>\n\n<b>rosatum, i</b> <i>n</i> (<i>sc.</i> vinum)\nрозовое вино <i>Pall, Lampr</i>."],
  ["Roscianus", "<big>Roscianus</big>\n\n<b>Roscianus, a, um</b> [Roscius 2]\nросциев <i>C</i>."],
  ["roscidus", "<big>roscidus</big>\n\n<b>roscidus, a, um</b> [ros]\n<b>1)</b> покрытый росой, росистый (herba <i>V</i>): r. umor <i>PM</i> <i>и</i> roscida pruina <i>O</i> = ros; roscida luna <i>V</i> росистая лунная ночь;\n<b>2)</b> стекающий каплями, капающий (melia <i>V</i>);\n<b>3)</b> влажный (saxa <i>V</i>; tecta <i>M</i>): roscida dea <i>O</i> = Aurora; roscida caespitum <i>Ap</i> росистые луга."],
  ["Roscius", "<big>Roscius</big>\n\n<b>Roscius, a, um</b>\nРосций, <i>римск. nomen; наиболее известны</i>:\n<b>1)</b> Sextus R., <i>родом из Америи (Умбрия), обвинённый в 80 г. до н. э. Эруцием в отцеубийстве, но оправданный благодаря защите Цицерона</i> <i>C</i>;\n<b>2)</b> Q. R. Gallus, <i>по происхождению раб из Селония (близ Ланувия), самый значительный актёр времён Цицерона, умер в 62 г. до н. э., друг и один из подзащитных Цицерона; перен.</i> мастер, виртуоз <i>C</i>;\n<b>3)</b> L. R. Otho, <i>народный трибун в 67 г. до н. э., автор lex Roscia (theatralis), по которому 14 рядов скамей в театре отводились сословию всадников, а всаднический ценз был повышен до 400 000 сестерциев</i> <i>C</i>."],
  ["Rosea", "<big>Rosea</big>\n\n<b>Rosea, ae</b> <i>f</i>\nРозея, <i>плодородная область близ Reate</i> (<i>ныне</i> Le Roscie) <i>Vr, C</i>."],
  ["Roseanus", "<big>Roseanus</big>\n\n<b>Roseanus, a, um</b> <i>Vr</i>\n= Roseus I."],
  ["rosetum", "<big>rosetum</big>\n\n<b>rosetum, i</b> <i>n</i> [rosa]\nместо, усаженное розовыми кустами, розовый сад <i>Vr, V</i>."],
  ["Roseus", "<big>Roseus</big>\n\n<b>I Roseus, a, um</b> [Rosea]\nрозейский (rura <i>V</i>; cannabis <i>PM</i>).\n\n<b>II roseus, a, um</b> [rosa]\n<b>1)</b> розовый, из роз (strophium <i>V</i>; corona <i>Ap</i>); поросший розами (ripa <i>Cld</i>);\n<b>2)</b> розового цвета, розовый, румяный (labella <i>Ctl</i>; os <i>V</i>); dea rosea <i>Lcr</i> = Aurora."],
  ["rosi", "<big>rosi</big>\n\n<b>rosi</b> <i>pf. к</i>\nrodo."],
  ["Rosia", "<big>Rosia</big>\n\n<b>Rosia, ae</b> <i>f</i> <i>v. l.</i>\n= Rosea."],
  ["rosidus", "<big>rosidus</big>\n\n<b>rosidus, a, um</b> [ros]\nросистый (umor <i>Ctl</i>)."],
  ["rosio", "<big>rosio</big>\n\n<b>rosio, onis</b> <i>f</i> [rodo] <i>мед.</i>\nрези <i>или</i> ощущение жжения <i>CC, PM</i>."],
  ["Rosius", "<big>Rosius</big>\n\n<b>Rosius, a, um</b> <i>v. l.</i>\n= Roseus."],
  ["rosmarinus", "<big>rosmarinus</big>\n\n<b>rosmarinus (ros marinus)</b> — <i>см.</i>\nros I, 4."],
  ["rosor", "<big>rosor</big>\n\n<b>rosor, oris</b> <i>m</i> [rodo] <i>зоол.</i>\nгрызун <i>Ambr</i>."],
  ["rostellum", "<big>rostellum</big>\n\n<b>rostellum, i</b> <i>n</i> [<i>demin. к</i> rostrum]\n<b>1)</b> маленький клюв, клювик (pullorum <i>Col</i>);\n<b>2)</b> мордочка (muris <i>PM</i>)."],
  ["rostra", "<big>rostra</big>\n\n<b>rostra, orum</b> <i>n</i>\n= rostrum 4."],
  ["rostralis", "<big>rostralis</big>\n\n<b>rostralis, e</b> [rostrum 4]\nростральный (tabula <i>Sid</i>)."],
  ["rostrans", "<big>rostrans</big>\n\n<b>rostrans, antis</b> <i>adj.</i> [rostrum]\nвонзающийся остриём (vomer <i>PM</i>)."],
  ["rostratus", "<big>rostratus</big>\n\n<b>rostratus, a, um</b> [rostrum]\nснабжённый загнутым в виде птичьего клюва концом (falces <i>Col</i>); снабжённый ростром, <i>т. е.</i> тараном (navis <i>C, Su etc.</i>): corona rostrata <i>PM</i> (<i>или</i> navalis <i>V</i>) венок, украшенный маленькими золотыми корабельными рострами (<i>которым награждались отличившиеся в морских боях</i>); columna rostrata <i>L, Q, Su</i> ростральная колонна (<i>мраморная колонна на римском Форуме, украшенная носами карфагенских кораблей, которые были захвачены Гаем Дуилием при г. Mylae в 260 г. до н. э.</i>)."],
  ["rostrum", "<big>rostrum</big>\n\n<b>rostrum, i</b> <i>n</i> [rodo]\n<b>1)</b> клюв (avium <i>C</i>); морда (canis <i>O</i>); <i>шутл.</i> <i>или</i> <i>презр.</i> лицо, рожа (r. barbatum <i>Pt</i>); хоботок (apis <i>PM</i>); рыло (suis <i>C</i>);\n<b>2)</b> острый выступ (vomeris, insulae <i>PM</i>); носик (lucernae <i>PM</i>); ростр, корабельный клюв (<i>подводный таран в носовой части корабля для пробивания неприятельских судов</i>) (naves rostro ferire <i>или</i> percutere <i>C, L etc.</i>);\n<b>3)</b> носовая часть судна, нос корабля: puppis rostro subjuncta leones <i>V</i> корабль, нос которого украшен изображениями львов;\n<b>4)</b> <i>pl.</i> ростральная трибуна (<i>в Риме между Форумом и комицием, украшенная носами антийских кораблей в память о победе Гая Мения в 338 г. до н. э.</i>) (escendere in rostra <i>C</i>); <i>тж.</i> = forum <i>H, Sen</i>, <i>перен.</i> народное собрание, народные массы (rostra movere <i>Lcn</i>)."],
  ["rosulentus", "<big>rosulentus</big>\n\n<b>rosulentus, a, um</b> [rosa]\nполный роз (prata <i>Eccl</i>)."],
  ["rosum", "<big>rosum</big>\n\n<b>rosum</b> <i>supin. к</i>\nrodo."],
  ["rota", "<big>rota</big>\n\n<b>rota, ae</b> <i>f</i>\n<b>1)</b> колесо (rotarum strepitus <i>C</i>); диск (solis r. <i>Lcr, Ap</i>);\n<b>2)</b> (<i>тж.</i> r. figularis <i>Pt</i>, figuli <i>Sen</i> <i>и</i> figulina <i>CA</i>) гончарный круг <i>Tib, H</i>;\n<b>3)</b> <i>поэт.</i> колесница, повозка, экипаж (pedibus rotave <i>O</i>): r. Luciferi <i>Tib</i> колесница солнца;\n<b>4)</b> колесо (<i>орудие пытки</i>) (in rotam ascendere <i>C</i>);\n<b>5)</b> каток, вал: subjectis rotis <i>T, Vtr etc.</i> подложив катки, на катках;\n<b>6)</b> (<i>на состязаниях</i>) круг, оборот, объезд (septima r. <i>Prp</i>);\n<b>7)</b> смена, превратность, переменчивость (r. fortunae <i>C</i>; vinceris aut vincis, haec in amore r. est <i>Prp</i>) || rotae dispares <i>O</i> неравные стихи (<i>в элегическом двустишии</i>)."],
  ["rotabilis", "<big>rotabilis</big>\n\n<b>rotabilis, e</b> [roto]\nкругообразный, вращательный <i>Aug</i>."],
  ["rotalis", "<big>rotalis</big>\n\n<b>rotalis, e</b> [rota]\nснабжённый колёсами, колёсный (carpentum <i>Capit</i>)."],
  ["rotatilis", "<big>rotatilis</big>\n\n<b>rotatilis, e</b> [roto]\nкругообразный, круговой (gyrus <i>Sid</i>)."],
  ["rotatim", "<big>rotatim</big>\n\n<b>rotatim</b> [rota]\nкругообразно <i>Ap</i> (<i>v. l.</i>)."],
  ["rotatio", "<big>rotatio</big>\n\n<b>rotatio, onis</b> <i>f</i> [roto]\nкруговое (вращательное) движение <i>Vtr</i>."],
  ["rotator", "<big>rotator</big>\n\n<b>rotator, oris</b> <i>m</i> [roto]\nзаставляющий кружиться (Euan Bassari dum r. <i>St</i>)."],
  ["rotatus", "<big>rotatus</big>\n\n<b>rotatus, us</b> <i>m</i> [roto]\nкружение, вращение <i>St, Aus</i>."],
  ["rotella", "<big>rotella</big>\n\n<b>rotella, ae</b> <i>f</i> [<i>demin. к</i> rota]\nколесико <i>Is</i>."],
  ["roto", "<big>roto</big>\n\n<b>roto, avi, atum, are</b> [rota]\n<b>1)</b> вертеть, кружить, вращать (clipeum <i>VF</i>); заставлять кружиться (aper rotat ore rabidos canes <i>O</i>); размахивать (r. ensem <i>V</i>);\n<b>2)</b> закруглять, округлять (sermo rotatus <i>J</i>);\n<b>3)</b> вращаться, вертеться, кружиться (saxa rotantia <i>V</i>)."],
  ["rotula", "<big>rotula</big>\n\n<b>rotula, ae</b> <i>f</i> [<i>demin. к</i> rota]\nмаленькое колесо <i>Pl, Col, PM etc.</i>"],
  ["rotulus", "<big>rotulus</big>\n\n<b>rotulus, i</b> <i>m</i> <i>Calp</i>\n= rotula."],
  ["rotunda", "<big>rotunda</big>\n\n<b>rotunda, ae</b> <i>f</i>\nшарик, катышек <i>Scr</i>."],
  ["rotundatio", "<big>rotundatio</big>\n\n<b>rotundatio, onis</b> <i>f</i> [rotundo]\n<b>1)</b> округление <i>Vtr</i>;\n<b>2)</b> округлость, окружность, круг <i>Vtr</i>."],
  ["rotunde", "<big>rotunde</big>\n\n<b>rotunde</b> [rotundus]\n<b>1)</b> кругло, в виде круга (formare <i>Col</i>);\n<b>2)</b> округло, изящно (apte ac r. <i>C</i>)."],
  ["rotundifolius", "<big>rotundifolius</big>\n\n<b>rotundifolius, a, um</b> [rotundus + folium]\nкруглолистый <i>Ap</i>."],
  ["rotunditas", "<big>rotunditas</big>\n\n<b>rotunditas, atis</b> [rotundus]\nокруглость, шарообразность (pilae <i>Sen</i>; caeli <i>Aug</i>); <i>перен.</i> закруглённость (verborum <i>Macr</i>)."],
  ["rotundo", "<big>rotundo</big>\n\n<b>rotundo, avi, atum, are</b> [rotundus]\n<b>1)</b> делать круглым, округлять (orbem solis <i>VP</i>);\n<b>2)</b> платить круглую сумму, <i>т. е.</i> сполна (r. mille talenta <i>H</i>);\n<b>3)</b> <i>перен.</i> округлять, тщательно отделывать (elegi rotundati <i>Sid</i>)."],
  ["rotundula", "<big>rotundula</big>\n\n<b>rotundula, ae</b> <i>f</i> [<i>demin. к</i> rotundo]\nшарик, катышек <i>Ap</i>."],
  ["rotundus", "<big>rotundus</big>\n\n<b>rotundus, a, um</b> [rota]\n<b>1)</b> круглый (дискообразный <i>или</i> шаровидный) (folia <i>Ap</i>; caelum <i>C</i>); с ровными краями (toga <i>Q</i>): quadrata rotundis mutare <i>погов. H</i> = ставить всё вверх дном;\n<b>2)</b> замкнутый в себе, не зависящий от внешнего мира (in se ipso totus, teres atque r. <i>H</i>);\n<b>3)</b> закруглённый, стройный, изящный (verborum apta et quasi rotunda constructio <i>C</i>): ore rotundo loqui <i>H</i> говорить складно, стройно."],
  ["Roxane", "<big>Roxane</big>\n\n<b>Roxane, es</b> <i>f</i>\nРоксана, <i>дочь бактрийского наместника Оксиарта, жена Александра Македонского, умерщвлённая Кассандрам вместе с её сыном</i> <i>QC, Just</i>."],
  ["Roxolani", "<big>Roxolani</big>\n\n<b>Roxolani, orum</b> <i>m</i>\nроксоланы, <i>сарматское кочевое племя на побережье Азовского моря между Борисфеном (Днепром) и Танаисом (Доном</i>) <i>PM, T, Vop, Amm</i>."],
  ["rubedo", "<big>rubedo</big>\n\n<b>rubedo, inis</b> <i>f</i> [rubeo]\nкраснота <i>Eccl</i>."],
  ["rubefacio", "<big>rubefacio</big>\n\n<b>rubefacio, feci, factum, ere</b> [ruber + facio]\nокрашивать в красный цвет, обагрять (aliquid sanguine r. <i>O</i>)."],
  ["rubellianus", "<big>rubellianus</big>\n\n<b>rubellianus, a, um</b> [rubellus]\nкрасноватый (vitis <i>Col</i>)."],
  ["rubellus", "<big>rubellus</big>\n\n<b>rubellus, a, um</b> [<i>demin. к</i> ruber]\nкрасноватый <i>или</i> розоватый (vinum <i>M</i>)."],
  ["rubens", "<big>rubens</big>\n\n<b>rubens, entis</b>\n<b>1.</b> <i>part. praes. к</i> rubeo;\n<b>2.</b> <i>adj.</i> краснеющий, красноватый, красный, румяный (uva <i>V</i>; os <i>Tib</i>); яркий, ослепительный (dextera, <i>sc.</i> Jovis <i>H</i>; ver <i>V</i>)."],
  ["rubeo", "<big>rubeo</big>\n\n<b>rubeo, bui, —, ere</b> [ruber]\nбыть красным, окрашиваться в красный цвет, краснеть (litus sanguine rubet <i>O</i>); краснеть от стыда (rubeo, crede mihi <i>C</i>). — <i>См. тж.</i> rubens."],
  ["ruber", "<big>ruber</big>\n\n<b>ruber, bra, brum</b> [<i>одного корня с</i> rutilus, rufus]\nкрасный (sanguis <i>H</i>; flamma <i>O</i>; homo crine r. <i>M</i>); mare (<i>или</i> aequor) Rubrum <i>C, L, Sen, T</i> Красное море, <i>но чаще</i> <i>QC etc.</i> Персидский залив <i>и</i> Индийский океан."],
  ["rubesco", "<big>rubesco</big>\n\n<b>rubesco, bui, —, ere</b> [ruber]\nстановиться красным, краснеть (mare rubescebat radiis <i>V</i>); краснеть от стыда (coram pluribus <i>Sen</i>)."],
  ["rubeta", "<big>rubeta</big>\n\n<b>rubeta, ae</b> <i>f</i> [rubus] (<i>тж.</i> rana rubeta <i>Prp</i>)\nжаба <i>PM, J</i>."],
  ["rubetum", "<big>rubetum</big>\n\n<b>rubetum, i</b> <i>n</i> [rubus]\nежевиковый кустарник, место, поросшее ежевикой <i>O, Calp</i>."],
  ["rubeus", "<big>rubeus</big>\n\n<b>I rubeus, a, um</b> [ruber]\nкрасный (color <i>Vr, Col, Pall</i>).\n\n<b>II rubeus, a, um</b> [rubus]\nежевичный (virga <i>V</i>)."],
  ["Rubi", "<big>Rubi</big>\n\n<b>Rubi, orum</b> <i>m</i>\nРубы, <i>городок певцетов (Peuceti) в Апулии (ныне</i> Ruvo) <i>H</i>."],
  ["rubia", "<big>rubia</big>\n\n<b>rubia, ae</b> <i>f</i>\nмарена, крап <i>Vtr, PM</i>."],
  ["Rubicon", "<big>Rubicon</big>\n\n<b>Rubico(n), onis</b> <i>m</i>\nРубикон, <i>речка на границе между Умбрией и Галлией Цизальпинской</i> (<i>в 49 г. до н. э. её перешёл Цезарь, решивший начать гражданскую войну</i>) <i>C, PM, Lcn</i>."],
  ["rubicundulus", "<big>rubicundulus</big>\n\n<b>rubicundulus, a, um</b> [<i>demin. к</i> rubicundus]\nнемного румяный <i>или</i> зарумянившийся <i>J</i>."],
  ["rubicundus", "<big>rubicundus</big>\n\n<b>rubicundus, a, um</b> [rubeo]\nкрасный, ярко-красный (cornum <i>H</i>); румяный <i>или</i> загорелый (os <i>Pl</i>); краснолицый (homo <i>Ter</i>); красновато-жёлтый <i>или</i> золотистый (Ceres = messis <i>V</i>)."],
  ["rubidus", "<big>rubidus</big>\n\n<b>rubidus, a, um</b> [rubeo]\nкрасный, тёмно-красный, багровый (facies <i>Su</i>); тёмный (panis <i>Pl</i>)."],
  ["rubig-", "<big>rubig-</big>\n\n<b>I rubig-</b> <i>v. l.</i>\n= robig-.\n\n<b>II Rubig-</b> <i>v. l.</i>\n= Robig-."],
  ["rubor", "<big>rubor</big>\n\n<b>rubor, oris</b> <i>m</i> [rubeo]\n<b>1)</b> красный цвет, краснота (cocci <i>PM, Sol</i>; candore mixtus r. <i>C, O</i>): aliquem in ruborem dare <i>Pl</i> (<i>игра слов</i>) избить кого-л. до кровоподтёков; pudorem r. consequitur <i>C</i> стыд вызывает покраснение (румянец); causa ruboris esse <i>O</i> <i>и</i> ruborem alicui afferre <i>C, T</i> вогнать кого-л. в краску;\n<b>2)</b> стыд, позор (r. ac dedecus <i>T</i>): alicui rubori esse <i>O, T</i> быть постыдным для кого-л."],
  ["rubrica", "<big>rubrica</big>\n\n<b>rubrica, ae</b> <i>f</i> [ruber]\n<b>1)</b> (<i>тж.</i> r. terra <i>Col</i>) красная глина <i>Vtr, PM</i>;\n<b>2)</b> красная краска (<i>минеральная</i>) <i>Col, PM</i>; красная охра <i>H</i>; румяна (buccas rubrica intingere <i>Pl</i>);\n<b>3)</b> рубрика, заглавие закона (<i>написанное красной краской</i>) <i>Dig</i>; гражданский закон <i>Q, Pers</i>: se ad album ac rubricas transferre <i>Q</i> заниматься изучением преторских распоряжений и гражданских законов (<i>см.</i> rubricatus)."],
  ["rubricatus", "<big>rubricatus</big>\n\n<b>rubricatus, a, um</b> [rubrica]\nокрашенный в красный цвет <i>Priap, Aug</i>: libra rubricata (<i>вм.</i> libri rubricati) <i>Pt</i> красные книги (<i>т. е. книги юридического содержания, заголовки которых писались красной краской</i>)."],
  ["rubricosus", "<big>rubricosus</big>\n\n<b>rubricosus, a, um</b> [rubrica]\nпокрытый красной глиной (solum <i>Col, PM</i>)."],
  ["Rubrius", "<big>Rubrius</big>\n\n<b>Rubrius, a, um</b>\nРубрий, <i>римск. nomen</i> <i>Cs, C</i>."],
  ["rubrus", "<big>rubrus</big>\n\n<b>rubrus, a, um</b> <i>Sol, Is</i>\n= ruber."],
  ["rubui", "<big>rubui</big>\n\n<b>rubui</b> <i>pf. к</i>\nrubeo <i>и</i> rubesco."],
  ["rubus", "<big>rubus</big>\n\n<b>rubus, i</b> <i>m</i>\n<b>1)</b> ежевичный куст <i>Cs, V, PM</i>;\n<b>2)</b> ежевика <i>Prp, Calp</i>."],
  ["Rubustini", "<big>Rubustini</big>\n\n<b>Rubustini, orum</b> <i>m</i> <i>PM</i>\n= Rubi."],
  ["ructabundus", "<big>ructabundus</big>\n\n<b>ructabundus, a, um</b> [ructo]\nне перестающий рыгать, страдающий постоянной отрыжкой <i>Sen</i>."],
  ["ructamen", "<big>ructamen</big>\n\n<b>ructamen, inis</b> <i>n</i> <i>Eccl</i>\n= ructatio."],
  ["ructatio", "<big>ructatio</big>\n\n<b>ructatio, onis</b> <i>f</i> [ructo]\nотрыжка <i>CA</i>."],
  ["ructatrix", "<big>ructatrix</big>\n\n<b>ructatrix, icis</b> <i>f</i> [ructo]\nвызывающая отрыжку (mentha<i>M</i>)."],
  ["ructatus", "<big>ructatus</big>\n\n<b>ructatus, us</b> <i>m</i> <i>Aug</i>\n= ructus."],
  ["ructo", "<big>ructo</big>\n\n<b>ructo, avi, atum, are</b> [ructus]\n<b>1)</b> рыгать <i>Pl, C etc.</i>: r. aliquid <i>CC, M, J</i> отрыгать чём-л.;\n<b>2)</b> изрыгать, извергать (cruores ore <i>Sil</i>; humidum fumum <i>Pall</i>; <i>перен.</i> sapientiam <i>Tert</i>)."],
  ["ructor", "<big>ructor</big>\n\n<b>ructor, —, ari</b> <i>depon.</i> <i>C, Vr, H</i>\n= ructo."],
  ["ructuatio", "<big>ructuatio</big>\n\n<b>ructuatio</b> <i>Aug</i>\n= ructatio."],
  ["ructuo", "<big>ructuo</big>\n\n<b>ructuo, avi, —, are</b> <i>Sol</i>\n= ructo."],
  ["ructuosus", "<big>ructuosus</big>\n\n<b>ructuosus, a, um</b> [ructus]\nперемежаемый отрыжками (spiritus <i>Caelius ap. Q</i>)."],
  ["ructus", "<big>ructus</big>\n\n<b>ructus, us</b> <i>m</i> [ructo]\nрыгание, отрыжка <i>C, PM, Vtr</i>."],
  ["rudectus", "<big>rudectus</big>\n\n<b>rudectus, a, um</b> [rudus 1]\nполный щебня, <i>т. е.</i> сухой, тощий (terra <i>Cato</i>)."],
  ["rudens", "<big>rudens</big>\n\n<b>I rudens, entis</b> <i>part. praes. к</i>\nrudo.\n\n<b>II rudens, entis</b> (<i>abl.</i> e, <i>редко</i> i; <i>gen. pl.</i> um, <i>редко</i> ium) <i>m</i>\nканат (<i>преим.</i> корабельный) <i>Pac, Pl, C etc.</i>: laxare rudentes <i>V</i> отпустить канаты, <i>т. е.</i> приготовиться к отплытию; temerare rudentibus undas <i>VF</i> = пускаться в плавание; rudentibus apta fortuna <i>C</i> судьба, связанная с корабельными канатами, <i>т. е.</i> шаткая, непрочная."],
  ["rudera", "<big>rudera</big>\n\n<b>rudera, um</b> <i>n</i> <i>Su pl. к</i>\nrudus II <i>и</i> raudus."],
  ["ruderarius", "<big>ruderarius</big>\n\n<b>ruderarius, a, um</b> [rudus I]\nпредназначенный для щебня (cribrum <i>Ap</i>)."],
  ["ruderatio", "<big>ruderatio</big>\n\n<b>ruderatio, onis</b> <i>f</i> [rudus I]\n<b>1)</b> мощение щебнем <i>Vtr</i>;\n<b>2)</b> масса для изготовления каменного пола <i>Vtr</i>."],
  ["ruderatus", "<big>ruderatus</big>\n\n<b>ruderatus, a, um</b> <i>PM</i>\n= rudectus."],
  ["rudero", "<big>rudero</big>\n\n<b>rudero, —, atum, are</b> [rudus I]\nмостить <i>или</i> бетонировать (saxo <i>Vtr</i>)."],
  ["Rudiae", "<big>Rudiae</big>\n\n<b>Rudiae, arum</b> <i>f</i>\nРудии, <i>город в Апулии между Венувией и Брундизием, родина поэта Энния</i> <i>PM, Mela, Sil</i>."],
  ["rudiarius", "<big>rudiarius</big>\n\n<b>rudiarius, i</b> <i>m</i> [rudis II]\nотслуживший свои годы и получивший rudis гладиатор (<i>см.</i> rudis II, 3) <i>Su</i>."],
  ["rudicula", "<big>rudicula</big>\n\n<b>rudicula, ae</b> <i>f</i> [<i>demin. к</i> rudis II]\nпалочка для размешивания, мешалка <i>Cato, Col, PM</i>."],
  ["rudimentum", "<big>rudimentum</big>\n\n<b>rudimentum, i</b> <i>n</i> [<i>одного корня с</i> rudis I <i>и</i> erudio]\n<b>1)</b> начало, первооснова (rhetorices <i>Su</i>);\n<b>2)</b> первая проба, первый опыт, начатки, подготовка (r. militare <i>L</i>; rudimenta belli <i>L</i>): prima rudirnenta alicujus rei ponere <i>L</i> попробовать свои силы в чём-л.; accipite r., post experimentum <i>Ap</i> послушайте (сначала) общий очерк, после чего (будет дано) завершение;\n<b>3)</b> эскиз, набросок (Protogenis <i>Pt</i>)."],
  ["Rudinus", "<big>Rudinus</big>\n\n<b>I Rudinus, a, um</b> [Rudiae]\nрудийский: homo R. <i>C</i> = Энний.\n\n<b>II Rudinus, i</b> <i>m</i>\nжитель города Rudiae <i>Enn</i>."],
  ["rudis", "<big>rudis</big>\n\n<b>I rudis, e</b>\n<b>1)</b> необработанный, неотделанный (moles <i>O</i>; saxum <i>Q</i>; materies <i>Pt</i>); невозделанный (ager <i>Col</i>); сырой (lana <i>O, St</i>);\n<b>2)</b> беспорядочный, неубранный (capilli <i>SenT</i>); незаконченный (<i>sc.</i> opus <i>Q</i>);\n<b>3)</b> грубый, суровый (textum, vestis <i>O</i>); дикорастущий (herba <i>M</i>); жёсткий, незрелый (uva <i>M</i>);\n<b>4)</b> молодой, юный (agna <i>M</i>); ранний (anni <i>Q</i>): nati rudes <i>VF</i> новорождённые дети;\n<b>5)</b> грубый, неискусный (vox <i>Q, T</i>; lingua <i>L</i>; vetustas <i>L</i>): rudibus adhuc musicae saeculis <i>Ap</i> когда музыкальное искусство было ещё неразвито || (<i>тж.</i> r. artium bonarum <i>T</i>) неучёный, невежественный (animi <i>Q</i>); необученный (bellorum, <i>sc.</i> elephantus <i>Fl</i>); неопытный, невинный (filia <i>M</i>): r. (in) aliqua re <i>или</i> alicujus rei <i>C, T, O etc.</i>, <i>реже</i> ad aliquid <i>L, O, QC</i> <i>и</i> alicui rei <i>M</i> неопытный в чём-либо, несведущий в отношении чего-л.; r. somni <i>O</i> бессонный.\n\n<b>II rudis, is</b> <i>f</i>\n<b>1)</b> тонкая палочка для размешивания, мешалка <i>Cato, PM</i>;\n<b>2)</b> палка для фехтовальных упражнений солдат и гладиаторов <i>L, Su</i>;\n<b>3)</b> знак отличия освобождённого от службы гладиатора <i>C, Su etc.</i>: donari rude <i>погов. O</i> получить освобождение."],
  ["ruditas", "<big>ruditas</big>\n\n<b>ruditas, atis</b> <i>f</i> [rudis I]\nгрубость, невежественность (ruditatem eximere <i>Ap</i>)."],
  ["ruditus", "<big>ruditus</big>\n\n<b>ruditus, us</b> <i>m</i> [rudo]\nослиный крик <i>Ap</i>."],
  ["rudo", "<big>rudo</big>\n\n<b>rudo, ivi, —, ere</b>\n<b>1)</b> кричать, реветь (<i>о львах, оленях, ослах и пр.</i>) <i>V, O, Pers, Ap</i>;\n<b>2)</b> трещать, скрипеть (prora rudens <i>V</i>)."],
  ["rudor", "<big>rudor</big>\n\n<b>rudor, oris</b> <i>m</i> [rudo]\nкрик, рёв (<i>sc.</i> tubae <i>Ap</i>)."],
  ["rudus", "<big>rudus</big>\n\n<b>I rudus, eris</b> <i>n</i>\n<b>1)</b> обломки камней, щебень, бут, строительный мусор <i>Col, Ctl, T</i>; <i>pl.</i> развалины <i>Su</i>;\n<b>2)</b> рухляк, мергель <i>Cato, Vtr, PM, bH</i>.\n\n<b>II rudus, eris</b> <i>n</i> <i>v. l.</i>\n= raudus."],
  ["rudusculum", "<big>rudusculum</big>\n\n<b>rudusculum</b> <i>v. l.</i>\n= raudusculum."],
  ["rufesco", "<big>rufesco</big>\n\n<b>rufesco, —, —, ere</b> [rufus]\nкраснеть, румяниться <i>PM</i>."],
  ["rufius", "<big>rufius</big>\n\n<b>rufius, i</b> <i>m</i> (<i>галльск.</i>) <i>зоол.</i>\nрысь <i>PM</i>."],
  ["rufo", "<big>rufo</big>\n\n<b>rufo, —, —, are</b> [rufus]\nокрашивать в красный цвет (capillum <i>PM</i>)."],
  ["Rufrae", "<big>Rufrae</big>\n\n<b>Rufrae, arum</b> <i>f</i>\nРуфры, <i>город самнитян в сев. Кампании</i> <i>V, Sil</i>."],
  ["Rufrium", "<big>Rufrium</big>\n\n<b>Rufrium, i</b> <i>n</i>\nРуфрий, <i>город гирпинов в Самнии</i> (<i>ныне</i> Ruvo) <i>L</i>."],
  ["rufulus", "<big>rufulus</big>\n\n<b>I rufulus, a, um</b> [<i>demin. к</i> rufus]\nкрасноватый (radix mandragorae <i>PM</i>); рыжий (homo <i>Pl</i>).\n\n<b>II rufulus, i</b> <i>m</i>\nруфул, <i>военный трибун, назначаемый полководцем или избираемый войсками</i> (<i>в отличие от comitiatus, избираемого на комициях</i>) <i>L</i>."],
  ["rufus", "<big>rufus</big>\n\n<b>I rufus, a, um</b>\n<b>1)</b> ярко-красный (sanguis <i>CC</i>);\n<b>2)</b> рыжий (crinis <i>PM</i>); рыжеволосый (virgo <i>Ter</i>).\n\n<b>II Rufus, i</b> <i>m</i>\nРуф, <i>римск. agnomen</i> <i>L, C etc.</i>"],
  ["ruga", "<big>ruga</big>\n\n<b>ruga, ae</b> <i>f</i>\n<b>1)</b> морщина, складка (sulcare rugis cutem <i>O</i>; rugae stolarum <i>Vtr</i>): rugam cogere <i>O</i> выражать неудовольствие;\n<b>2)</b> <i>перен. pf.</i> старость (moram rugis afferre <i>H</i>);\n<b>3)</b> <i>pl.</i> гайка <i>PM</i>."],
  ["rugatio", "<big>rugatio</big>\n\n<b>rugatio, onis</b> <i>f</i> [rugo]\nсморщивание <i>CA</i>."],
  ["Rugii", "<big>Rugii</big>\n\n<b>Rugi(i), orum</b> <i>m</i>\nругии, <i>германск. племя на побережье Балтийского моря, к вост. от устья Одры</i> <i>T, Sid</i>."],
  ["ruginosus", "<big>ruginosus</big>\n\n<b>ruginosus, a, um</b> [ruga]\nморщинистый, сморщенный (cutis <i>CA</i>)."],
  ["rugio", "<big>rugio</big>\n\n<b>rugio, ivi (ii), —, ire</b>\nрыкать, реветь <i>Spart, Amm</i>; урчать (rugiens venter <i>Hier</i>)."],
  ["rugitus", "<big>rugitus</big>\n\n<b>rugitus, us</b> <i>m</i> [rugio]\nрыкание, рёв, рычание (leonum <i>Vop</i>; canum <i>Sol</i>); урчание (intestinorum <i>CA</i>)."],
  ["rugo", "<big>rugo</big>\n\n<b>rugo, avi, atum, are</b> [ruga]\n<b>1)</b> морщить (frontem <i>Hier</i>);\n<b>2)</b> морщиться (palliolum rugat <i>Pl</i>)."],
  ["rugositas", "<big>rugositas</big>\n\n<b>rugositas, atis</b> <i>f</i>\nсморщивание, нахмуренность (maeroris aut irae <i>Tert</i>)."],
  ["rugosus", "<big>rugosus</big>\n\n<b>rugosus, a, um</b> [ruga]\nморщинистый, сморщенный (genae <i>O</i>; senecta <i>Tib</i>; cortex <i>O</i>)."],
  ["ruidus", "<big>ruidus</big>\n\n<b>ruidus, a, um</b>\nшероховатый, неровный, <i>по др.</i> — падающий, опускающийся (pilum in pistura <i>PM</i> — <i>v. l.</i>)."],
  ["ruina", "<big>ruina</big>\n\n<b>ruina, ae</b> <i>f</i> [ruo]\n<b>1)</b> падение (grandinis <i>Lcr</i>; jumentorum sarcinarumque <i>L</i>); обвал (turrium <i>Cs</i>): ruinas facere in aliquid <i>H</i> упасть на что-л.; ruinam dare (trahere) <i>V</i> рушиться, обрушиваться, валиться; caeli r. <i>V</i> ливень, непогода; poli r. <i>VF</i> гром;\n<b>2)</b> устремление, нападение, набег: ruinam dare <i>V</i> броситься друг на друга; viam fecisse ruina <i>Lcn</i> пробиться напролом;\n<b>3)</b> ошибка, промах (ruinas facere <i>Lcr</i>; ruinae alicujus <i>C</i>);\n<b>4)</b> несчастье, бедствие, падение, гибель (urbis <i>L</i>; Clodius — r. rei publicae <i>C</i>); поражение, разгром (ruina fundere Gallos <i>L</i>); разорение, крах (jam perlucente ruina <i>J</i>); смерть (Neronis <i>PM</i>);\n<b>5)</b> <i>преим. pl.</i> развалины (Iliacae <i>V</i>; muri, Thebarum <i>L</i>)."],
  ["ruinosus", "<big>ruinosus</big>\n\n<b>ruinosus, a, um</b> [ruina]\n<b>1)</b> ветхий, близкий к обвалу, угрожающий падением (aedes <i>C</i>);\n<b>2)</b> рухнувший, обрушившийся, развалившийся (domus <i>O</i>)."],
  ["Rullus", "<big>Rullus</big>\n\n<b>Rullus, i</b> <i>m</i>\nРулл, <i>cognomen в роде Сервилиев; наиболее известен</i> P. Servilius R., <i>народный трибун в год консульства Цицерона, против которого последний произнёс три речи «De lege agraria»</i> <i>C</i>."],
  ["ruma", "<big>ruma</big>\n\n<b>ruma, ae</b> <i>f</i>\n<b>1)</b> сосец <i>Vr</i>;\n<b>2)</b> рубец (<i>первый отдел желудка жвачных</i>) <i>Eccl</i>."],
  ["rumen", "<big>rumen</big>\n\n<b>rumen, inis</b> <i>n</i> <i>PM</i>\n= ruma 2."],
  ["rumex", "<big>rumex</big>\n\n<b>rumex, icis</b>\n<b>1.</b> <i>f</i> щавель <i>Pl, V, PM</i>;\n<b>2.</b> <i>m</i> дротик, <i>род метательного снаряда</i> <i>LM, AG</i>."],
  ["rumifero", "<big>rumifero</big>\n\n<b>rumifero, —, —, are</b> <i>v. l.</i>\n= rumifico."],
  ["rumifico", "<big>rumifico</big>\n\n<b>rumifico, —, —, are</b> [rumor + facio]\nвосхвалять во всеуслышание, прославлять (aliquem probum <i>Pl</i>)."],
  ["rumigeratio", "<big>rumigeratio</big>\n\n<b>rumigeratio, onis</b> <i>f</i> [rumor + gero]\nраспускание слухов <i>Lampr</i>."],
  ["rumigerulus", "<big>rumigerulus</big>\n\n<b>rumigerulus, i</b> <i>m</i> [rumigeratio]\nразноситель слухов, сплетник <i>Amm, Hier</i>."],
  ["rumigo", "<big>rumigo</big>\n\n<b>rumigo, —, —, are</b> [ruma + ago]\nпережёвывать жвачку <i>Ap</i>."],
  ["Rumina", "<big>Rumina</big>\n\n<b>I Rumina, ae</b> <i>f</i> (<i>древнеиталийск.</i> rumis = <i>лат.</i> mamma)\nРумина, <i>римск. богиня грудных младенцев и детёнышей-сосунов</i> (<i>её храм находился близ смоковницы, под которой Ромул и Рем, по преданию, сосали грудь волчицы</i>) <i>Vr, Aug</i>."],
  ["Rumina ficus", "<big>Rumina ficus</big>\n\n<b>II Rumina ficus</b> <i>O</i>\n= ficus Ruminalis (<i>см.</i> Rumina I <i>и</i> Ruminalis)."],
  ["Ruminalis", "<big>Ruminalis</big>\n\n<b>I Ruminalis, e</b> <i>adj. к</i>\nRumina: ficus <i>L, PM</i> <i>или</i> arbor R. <i>T</i> смоковница-кормилица (<i>см.</i> Rumina 1).\n\n<b>II ruminalis, e</b> [rumen]\nжвачный (hostia <i>PM</i>)."],
  ["ruminatio", "<big>ruminatio</big>\n\n<b>ruminatio, onis</b> <i>f</i> [rumino]\n<b>1)</b> пережёвывание жвачки <i>PM</i>;\n<b>2)</b> возвращение (hiemis <i>PM</i>);\n<b>3)</b> повторение, передумывание, беспрестанное размышление (r. cotidiana <i>C</i>);\n<b>4)</b> удвоение, сдваивание (corticis <i>PM</i>)."],
  ["ruminator", "<big>ruminator</big>\n\n<b>ruminator, oris</b> <i>m</i>\nжвачное животное <i>Eccl</i>."],
  ["rumino", "<big>rumino</big>\n\n<b>rumino, —, —, are</b> [rumen]\n<b>1)</b> (<i>тж.</i> r. herbas <i>V, O etc.</i>) пережевывать жвачку <i>Col, PM, Vlg, Eccl</i>;\n<b>2)</b> <i>перен.</i> пережёвывать, повторять, твердить (carmina alicujus <i>Symm</i>; omnia, quae cognitione digna sunt <i>Aug</i>)."],
  ["ruminor", "<big>ruminor</big>\n\n<b>ruminor, —, ari</b> <i>depon.</i> <i>LA, Vr, AG</i>\n= rumino."],
  ["rumis", "<big>rumis</big>\n\n<b>rumis, is</b> <i>f</i> <i>PM</i>\n= ruma."],
  ["rumor", "<big>rumor</big>\n\n<b>rumor, oris</b> <i>m</i>\n<b>1)</b> глухой шум, гул: rumore secundo <i>V</i> под мерный всплеск вёсел, <i>но тж.</i> <i>V, H, T etc.</i> под шумные одобрения;\n<b>2)</b> <i>тж. pl.</i> слух, молва, пересуды, толки (varii rumores afferuntur de aliqua re <i>Cs</i>): rumores spargere <i>C</i> (dissipare <i>Ter, C</i>, serere <i>V, QC</i>) распускать слухи; aliquid rumore accipere <i>Cs</i> узнать что-л. по слухам; r. alicujus rei <i>C, T, Su</i> <i>или</i> de aliqua re <i>C, Cs</i> слух о чём-л.;\n<b>3)</b> общественное мнение (fama rumoresque hominum <i>L</i>): honestus r. <i>PS</i> доброе имя; claro rumore esse <i>T</i> пользоваться хорошей славой;\n<b>4)</b> хорошая репутация, одобрение (plebis rumorem affectare <i>T</i>);\n<b>5)</b> дурная слава, осуждение, хула: ii, quos r. asperserat <i>QC</i> те, которые подверглись осуждению."],
  ["rumpia", "<big>rumpia</big>\n\n<b>rumpia, ae</b> <i>f</i> <i>Enn ap. AG, L</i>\n= rhomphaea."],
  ["rumpo", "<big>rumpo</big>\n\n<b>rumpo, rupi, ruptum, ere</b>\n<b>1)</b> рвать, разрывать (vincula <i>C</i>; vestes <i>O</i>; teretes plagas <i>H</i>); разбивать (catenas <i>H</i>); ломать, сносить, разрушать (pontem <i>L</i>; claustra <i>H</i>); вскрывать (cicatrices <i>QC</i>); разрубать, рассекать, разрезать (lora gladio <i>QC</i>); пронзать (intima praecordia ferro <i>O</i>); вырывать, выкорчёвывать (radices solo <i>Col</i>); отрывать (funem a litore <i>V</i>); силой прокладывать (viam per hostes <i>V</i>); прорывать, прокапывать <i>или</i> пробивать (fontem <i>O</i>); radii se inter nubila rumpunt <i>V</i> лучи пробиваются сквозь облака; media agmina r. <i>V</i> прорваться сквозь (вражеские) полки; se nubibus imber ruperat <i>V</i> из туч хлынул ливень; rumpunt horrea messes <i>V</i> от (богатой) жатвы ломятся амбары; rumpi invidia <i>M</i> лопаться от зависти; r. vocem <i>V, T</i> подать голос, заговорить; r. questus pectore <i>V</i> разразиться жалобами; r. gemitum <i>Sil</i> издать стон, застонать; risum r. <i>C</i> захохотать; cicadae rumpunt arbusta <i>V</i> цикады наполняют кусты стрекотанием; r. reditum alicui <i>H</i> отрезать кому-либо путь к отступлению, возврату;\n<b>2)</b> нарушать (rupta foedera <i>V</i>; r. pacem <i>Eutr</i>); преступать (leges <i>Lcn</i>); расстраивать, расторгать (nuptias alicujus <i>H</i>); прекращать, прерывать (somnum <i>V</i>): r. moras <i>V, M, PM</i> перестать медлить."],
  ["rumpotinetum", "<big>rumpotinetum</big>\n\n<b>rumpotinetum, i</b> <i>n</i> [rumpotinus I]\nнизкорослый лесок, служащий для подпирания виноградных лоз <i>Col</i>."],
  ["rumpotinus", "<big>rumpotinus</big>\n\n<b>I rumpotinus, a, um</b> [rumpus + teneo]\nслужащий для подпирания виноградных лоз (arbores <i>Col</i>).\n\n<b>II rumpotinus, i</b> <i>f</i> [rumpus + teneo]\nдеревцо, обвиваемое лозой, <i>преим.</i> opulus (<i>см.</i>) <i>PM</i>."],
  ["rumpus", "<big>rumpus</big>\n\n<b>rumpus, i</b> <i>m</i>\nползучий виноградный побег, лоза <i>Vr</i>."],
  ["rumusculus", "<big>rumusculus</big>\n\n<b>rūmusculus, ī</b> <i>m</i> [<i>demin. к</i> rumor]\nговор, болтовня, пересуды <i>C, Hier, Aug</i>."],
  ["runa", "<big>runa</big>\n\n<b>runa, ae</b> <i>f</i> <i>предпол.</i>\nметательное копьё, дротик <i>Enn</i>."],
  ["runatus", "<big>runatus</big>\n\n<b>runatus, a, um</b> [runa]\nвооружённый метательным копьём <i>Enn</i>."],
  ["runcatio", "<big>runcatio</big>\n\n<b>runcatio, onis</b> <i>f</i> [runco]\n<b>1)</b> прополка <i>Col, PM</i>;\n<b>2)</b> сорная трава <i>Col</i>."],
  ["runcator", "<big>runcator</big>\n\n<b>runcator, oris</b> <i>m</i>\nполольщик <i>Col</i>."],
  ["runcina", "<big>runcina</big>\n\n<b>I runcina, ae</b> <i>f</i> (<i>греч.</i>)\nрубанок, струг <i>Vr, PM, Vlg, Eccl</i>.\n\n<b>II Runcina, ae</b> <i>f</i> [runco]\nРунцина, <i>богиня полольных или уборочных работ</i> <i>Aug</i>."],
  ["runcino", "<big>runcino</big>\n\n<b>runcino, —, —, are</b> [runcina]\n<b>1)</b> строгать <i>Vr, Eccl</i>;\n<b>2)</b> <i>шутл.</i> обмануть, надуть <i>Pl</i> (<i>v. l.</i>)."],
  ["runco", "<big>runco</big>\n\n<b>I runco, —, —, are</b>\n<b>1)</b> полоть (spinas <i>Cato</i>);\n<b>2)</b> ощипывать, выдёргивать волосы <i>Pers</i>;\n<b>3)</b> косить, убирать <i>Aug</i>.\n\n<b>II runco, onis</b> <i>m</i>\nполольное орудие, тяпка <i>Pall</i>."],
  ["ruo", "<big>ruo</big>\n\n<b>ruo, rui, rutum, ere</b> (<i>part. fut.</i> ruiturus)\n<b>1)</b> (<i>тж.</i> in se r. <i>T, Lcn</i>) рушиться, обрушиваться, валиться (pons ad terram ruit <i>L</i>; ruunt templa deorum <i>H</i>); падать (ruebant victores victi que <i>V</i>); низвергаться (imber aethere ruit <i>V</i>; ruunt de montis amnes <i>V</i>): sol ruit <i>VF</i> солнце заходит; nox ruit <i>V</i> (стремительно) наступает ночь, <i>но тж.</i> <i>V</i> ночь уходит;\n<b>2)</b> устремляться, бросаться, кидаться, ринуться (in aliquem <i>VF</i>; ad interitum <i>C</i>; per vetitum nefas <i>H</i>); вырываться, раздаваться (ruunt voces <i>V</i>); спешить (in castra <i>L</i>; ad convivium <i>T</i>); впадать (in crudelitatem <i>L</i>; in servitium <i>T</i>): r. in dicendo <i>C</i> говорить необдуманно; r. in agendo <i>C</i> поступать опрометчиво;\n<b>3)</b> валить, сваливать, опрокидывать (immanem molem <i>V</i>; naves <i>Lcr</i>);\n<b>4)</b> гнать, выпускать, вздымать (nubem atram ad caelum <i>V</i>); мчать, увлекать (navis ruit spumas salis <i>V</i>);\n<b>5)</b> разрушать, разметать (cumulos arenae <i>V</i>);\n<b>6)</b> выкапывать, сгребать, загребать (divitias aerisquc acervos <i>H</i>): ruta (u!) (et) caesa <i>C, Dig</i> (всё) добытое из почвы и срубленное, <i>т. е.</i> движимое имущество (<i>в отличие от</i> juncta — <i>или</i> vincta — fixaque)."],
  ["rupes", "<big>rupes</big>\n\n<b>rupes, is</b> <i>f</i> [rumpo]\n<b>1)</b> скала, утёс (urbs imposita rupibus <i>L</i>): rupem munire (pandere) <i>L</i> проложить дорогу через скалу;\n<b>2)</b> расселина, ущелье, скалистая пропасть (aliquem in r. protrudere <i>H</i>): cavae r. <i>V, L</i> рассеянны скал, пещеры в скалах, гроты."],
  ["rupeus", "<big>rupeus</big>\n\n<b>rupeus, a, um</b> [rupes]\nскальный, каменный, <i>перен.</i> безжизненный (saxa <i>Ambr</i>)."],
  ["rupex", "<big>rupex</big>\n\n<b>rupex, icis</b> <i>m</i> [rupes]\nгрубый человек, грубиян <i>LM, AG, Eccl</i>."],
  ["rupi", "<big>rupi</big>\n\n<b>rupi</b> <i>pf. к</i>\nrumpo."],
  ["rupicapra", "<big>rupicapra</big>\n\n<b>rupicapra, ae</b> <i>f</i> [rupes + capra]\nсерна <i>PM</i>."],
  ["rupico", "<big>rupico</big>\n\n<b>rupico, onis</b> <i>m</i> <i>Ap</i>\n= rupex."],
  ["Rupilius", "<big>Rupilius</big>\n\n<b>Rupilius, a, um</b>\nРупилий, <i>римск. nomen; наиболее известны</i>:\n<b>1)</b> P. R., <i>друг Сципиона Младшего, противник Гракхов, консул в 132 г. до н. э., автор ряда законов для Сицилии</i> <i>C</i>;\n<b>2)</b> P. R. Rex, <i>родом из Пренесте, враг Горация, объявленный вне закона Октавианом</i> <i>H</i>;\n<b>3)</b> A. R., <i>врач, современник Цицерона</i> <i>C</i>;\n<b>4)</b> <i>актёр времён Цицерона</i> <i>C</i>."],
  ["rupina", "<big>rupina</big>\n\n<b>rupina, ae</b> <i>f</i> [rupes]\nрасселина в скалах, каменистое ущелье <i>Ap</i>."],
  ["ruptio", "<big>ruptio</big>\n\n<b>ruptio, onis</b> <i>f</i> [rumpo]\nранение, увечье <i>Dig</i>."],
  ["ruptor", "<big>ruptor</big>\n\n<b>ruptor, oris</b> <i>m</i> [rumpo]\nразрушитель <i>Sid</i>; <i>перен.</i> нарушитель (pacis <i>T</i>; foederis <i>L</i>)."],
  ["ruptura", "<big>ruptura</big>\n\n<b>ruptura, ae</b> <i>f</i> [rumpo]\nразрыв <i>или</i> перелом <i>AG, Veg</i>."],
  ["ruptus", "<big>ruptus</big>\n\n<b>ruptus, a, um</b> <i>part. pf. к</i>\nrumpo."],
  ["rura", "<big>rura</big>\n\n<b>rura</b> <i>pl. к</i>\nrus."],
  ["ruralis", "<big>ruralis</big>\n\n<b>ruralis, e</b> [rus]\nдеревенский, сельский <i>Amm, Macr, Calp</i>."],
  ["ruraliter", "<big>ruraliter</big>\n\n<b>ruraliter</b>\nпо-деревенски <i>Eccl</i>."],
  ["ruratio", "<big>ruratio</big>\n\n<b>ruratio, onis</b> <i>f</i> <i>Ap</i>\n= rusticatio."],
  ["rurestris", "<big>rurestris</big>\n\n<b>rurestris, e</b> [rus]\nсельский, деревенский (arva <i>Ap</i>)."],
  ["ruri", "<big>ruri</big>\n\n<b>ruri</b> [<i>loc. к</i> rus]\nв деревне (habitare <i>C</i>)."],
  ["ruricola", "<big>ruricola</big>\n\n<b>I ruricola, ae</b> <i>adj.</i> [rus + colo]\n<b>1)</b> обрабатывающий землю (aratrum <i>O</i>);\n<b>2)</b> живущий в деревне, деревенский, сельский (Phryges <i>O</i>; bos <i>O</i>);\n<b>3)</b> полевой (Fauni <i>O</i>).\n\n<b>II ruricola, ae</b> <i>m, f</i>\nземледелец, сельский житель, крестьянин <i>Col, Calp, Sid</i>."],
  ["rurigena", "<big>rurigena</big>\n\n<b>rurigena, ae</b> <i>m, f</i> [rus + geno = gigno]\nуроженец деревни, крестьянин <i>O</i>."],
  ["ruro", "<big>ruro</big>\n\n<b>ruro, —, —, are</b> [rus]\nжить в деревне, заниматься сельским хозяйством, крестьянствовать (ruri r. <i>Pl</i>)."],
  ["ruror", "<big>ruror</big>\n\n<b>ruror, —, ari</b> <i>depon.</i> <i>Vr</i>\n= ruro."],
  ["rursum", "<big>rursum</big>\n\n<b>rursum</b> <i>adv.</i> <i>v. l.</i>\n= rursus."],
  ["rursus", "<big>rursus</big>\n\n<b>rursus (rursum)</b> <i>adv.</i> [<i>из</i> revorsus = reversus]\n<b>1)</b> назад, обратно (r. cadere <i>Pl</i>, trahere <i>C</i>): rursum vorsum <i>Pl</i> назад; rursum (ac) prorsum <i>Ter, Vr</i> взад и вперёд, <i>т. е.</i> то туда, то сюда;\n<b>2)</b> опять, снова, сызнова (dic r. <i>Ter</i>): bellum, pax r. <i>H</i> то война, то снова мир;\n<b>3)</b> (<i>тж.</i> r. in vicem <i>Cs</i>) наоборот, напротив, с другой стороны (quicquid dicunt, laudat; id r. si negant, laudat quoque <i>Ter</i>)."],
  ["rus", "<big>rus</big>\n\n<b>rus, ruris</b> <i>n</i>\n<b>1)</b> деревня, село, поместье (r. suburbanum <i>T</i>): r. <i>Pl, Ter</i> в деревню; rure <i>C</i> из деревни; rure <i>Pl, H</i> <i>или</i> (<i>loc.</i>) ruri в деревне;\n<b>2)</b> поле, пашня (rura colere <i>Hirt, PM</i>): aspera dumis rura <i>V</i> поля, поросшие колючим кустарником;\n<b>3)</b> деревенский характер, деревенщина (manent vestigia ruris <i>H</i>)."],
  ["ruscarius", "<big>ruscarius</big>\n\n<b>ruscarius, a, um</b> [ruscus]\nигличный, предназначенный для выпалывания иглицы (falcula <i>Cato, Col</i>)."],
  ["Ruscino", "<big>Ruscino</big>\n\n<b>Ruscino, onis</b> <i>f</i>\nРусцинон, <i>город в Gallia Narbonensis близ Пиренеев и Средиземного моря</i> (<i>ныне</i> Castel Roussillon) <i>PM, L</i>."],
  ["rusculum", "<big>rusculum</big>\n\n<b>rusculum, i</b> <i>n</i> [<i>demin. к</i> rus]\nнебольшое поместье, именьице <i>AG</i>."],
  ["ruscum", "<big>ruscum</big>\n\n<b>ruscum, i</b> <i>n</i> <i>v. l.</i>\n= ruscus."],
  ["ruscus", "<big>ruscus</big>\n\n<b>ruscus, i</b> <i>m</i> <i>бот.</i>\nиглица колючая (<i>Ruscus aculeatus</i>, L.) <i>V, Col, PM</i>."],
  ["Rusellae", "<big>Rusellae</big>\n\n<b>Rusellae, arum</b> <i>или</i> <b>Rusellana colonia</b> <i>f</i>\nРуселлы, <i>город в Этрурии на via Clodia, к вост. от Lacus Prelius</i> <i>PM</i>."],
  ["Rusellanus", "<big>Rusellanus</big>\n\n<b>I Rusellanus, a, um</b> [Rusellae]\nрусельский (ager <i>L</i>).\n\n<b>II Rusellanus, i</b> <i>m</i>\nжитель города Rusellae <i>L</i>."],
  ["Ruspina", "<big>Ruspina</big>\n\n<b>Ruspina, ae</b> <i>f</i>\nРуспина, <i>город в Зевгитане близ Leptis Parva</i> <i>bAfr, PM, Sil</i>."],
  ["ruspo", "<big>ruspo</big>\n\n<b>ruspo, —, —, are</b> <i>Tert</i>\n= ruspor."],
  ["ruspor", "<big>ruspor</big>\n\n<b>ruspor, —, ari</b> <i>depon.</i>\nобыскивать, осматривать, обследовать (aliquid <i>Acc, Ap</i>)."],
  ["russatus", "<big>russatus</big>\n\n<b>russatus, a, um</b> [russus]\nокрашенный в красный цвет, обагрённый (sanguine <i>Tert</i>)."],
  ["russeolus", "<big>russeolus</big>\n\n<b>russeolus, a, um</b> <i>Eccl</i>\n= russeus."],
  ["russesco", "<big>russesco</big>\n\n<b>russesco, —, —, ere</b>\nкраснеть, становиться красным <i>Enn</i>."],
  ["russeus", "<big>russeus</big>\n\n<b>russeus, a, um</b> [russus]\nкрасноватый (tunica <i>Pt</i>); рыжий <i>или</i> гнедой (equus <i>Pall</i>)."],
  ["russulus", "<big>russulus</big>\n\n<b>russulus, a, um</b> <i>Vop, Capit</i>\n= russeus."],
  ["russum", "<big>russum</big>\n\n<b>russum</b> <i>adv.</i>\n= rursum."],
  ["russus", "<big>russus</big>\n\n<b>russus, a, um</b>\nкрасный (gingiva <i>Ctl</i>; vinum <i>Vop</i>)."],
  ["rustarius", "<big>rustarius</big>\n\n<b>rustarius</b> <i>Cato</i>\n(<i>v. l.</i>) = ruscarius."],
  ["rustica", "<big>rustica</big>\n\n<b>rustica, ae</b> <i>f</i>\nкрестьянка <i>Pl, O</i>."],
  ["rusticanus", "<big>rusticanus</big>\n\n<b>rusticanus, a, um</b> [rusticus]\nдеревенский, сельский: vita rusticana <i>C</i> сельская жизнь, пребывание в деревне; homo r. <i>C</i> крестьянин, земледелец."],
  ["rusticatio", "<big>rusticatio</big>\n\n<b>rusticatio, onis</b> <i>f</i> [rusticus]\n<b>1)</b> жизнь в деревне, пребывание в поместье, <i>тж.</i> сельский быт <i>C</i>;\n<b>2)</b> сельское хозяйство <i>C, Col</i>."],
  ["rustice", "<big>rustice</big>\n\n<b>rustice</b> [rusticus]\n<b>1)</b> по-деревенски, по-крестьянски (loqui <i>C, AG</i>);\n<b>2)</b> грубо, неумело <i>C</i>."],
  ["rusticellus", "<big>rusticellus</big>\n\n<b>rusticellus, a, um</b> [<i>demin. к</i> rusticus]\nмужиковатый <i>Vr ap. PM</i>."],
  ["rusticitas", "<big>rusticitas</big>\n\n<b>rusticitas, atis</b> <i>f</i> [rusticus]\n<b>1)</b> сельская простота, простодушие, прямота <i>PJ, Calp</i>;\n<b>2)</b> неловкость, неуклюжесть, грубость (verborum <i>Q</i>);\n<b>3)</b> застенчивость, конфузливость <i>O, Sen</i>;\n<b>4)</b> сельское хозяйство <i>Pall</i>;\n<b>5)</b> поселяне, крестьянство <i>Pall, CJ</i>."],
  ["rusticor", "<big>rusticor</big>\n\n<b>rusticor, —, ari</b> <i>depon.</i> [rusticus]\n<b>1)</b> жить в деревне <i>C</i>;\n<b>2)</b> заниматься сельским хозяйством, крестьянствовать <i>Col</i>: rusticantes <i>AV</i> крестьяне;\n<b>3)</b> выражаться по-деревенски, <i>т. е.</i> неправильно <i>Sid</i>."],
  ["rusticula", "<big>rusticula</big>\n\n<b>rusticula, ae</b> <i>f</i> [rusticulus]\n(<i>sc.</i> gallina) рябчик <i>PM, M</i>."],
  ["rusticulus", "<big>rusticulus</big>\n\n<b>rusticulus, a, um</b> [<i>demin. к</i> rusticus]\n<b>1)</b> деревенский, сельский <i>C</i>;\n<b>2)</b> несколько неуклюжий, грубоватый (libellus <i>M</i>)."],
  ["rusticus", "<big>rusticus</big>\n\n<b>I rusticus, a, um</b> [rus]\n<b>1)</b> деревенский, сельский, крестьянский (vita <i>C</i>; numen <i>O</i>);\n<b>2)</b> простой, незатейливый, бесхитростный (mores <i>C</i>);\n<b>3)</b> неловкий, неуклюжий, грубый (homo <i>C</i>; r. et agrestis <i>C</i>).\n\n<b>II rusticus, i</b> <i>m</i> [rus]\n<b>1)</b> крестьянин, земледелец <i>Pl, C etc.</i>;\n<b>2)</b> грубый человек <i>Q</i>."],
  ["rusum", "<big>rusum</big>\n\n<b>rusum</b> <i>adv.</i> <i>арх. v. l.</i>\n= rursum."],
  ["rusus", "<big>rusus</big>\n\n<b>rusus, a, um</b> <i>AG</i>\n= russus."],
  ["ruta", "<big>ruta</big>\n\n<b>I rūta, ae</b> <i>f</i>\n<b>1)</b> <i>бот.</i> рута <i>O, Col, PM, M</i>: aliquem in rutae folium conjicere <i>погов. Pt</i> = заставить кого-л. спрятаться от страха;\n<b>2)</b> <i>перен.</i> горечь, неприятность <i>C</i>.\n\n<b>II rūta (et) caesa</b> <i>f</i> [ruo <i>и</i> caedo] <i>юр.</i>\nвырытое (добытое из земли) и срубленное, <i>т. е.</i> минеральные и лесные богатства (<i>которые при продаже земельного участка не переходят в собственность покупателя</i>) <i>C, Dig</i>."],
  ["rutabulum", "<big>rutabulum</big>\n\n<b>rutābulum, ī</b> <i>n</i> [ruo]\n<b>1)</b> кочерга <i>Cato, Su</i>;\n<b>2)</b> мешалка <i>Col</i>;\n<b>3)</b> <i>Naev</i> = membrum virile."],
  ["rutatus", "<big>rutatus</big>\n\n<b>rūtatus, a, um</b> [ruta I]\nприправленный рутой (mustum <i>PM</i>); украшенный, покрытый рутой (lacerti <i>M</i>)."],
  ["rutellum", "<big>rutellum</big>\n\n<b>rutellum, i</b> <i>n</i> [<i>demin. к</i> rutrum]\nлопатка, лопаточка <i>LM</i>."],
  ["Rutheni", "<big>Rutheni</big>\n\n<b>Rut(h)eni, orum</b> <i>m</i>\nрутены, <i>племя в Аквитании с центром Segodunum</i> (<i>ныне</i> Rodez) <i>Cs, PM, Lcn</i>."],
  ["rutilesco", "<big>rutilesco</big>\n\n<b>rutilesco, —, —, ere</b> [rutilus]\nстановиться красноватым, рыжеть (lepores rutilescunt <i>PM</i>)."],
  ["Rutilianus", "<big>Rutilianus</big>\n\n<b>Rutilianus, a, um</b> [Rutilius]\nрутилиев <i>G</i>."],
  ["Rutilius", "<big>Rutilius</big>\n\n<b>Rutilius, a, um</b>\nРутилий, <i>римск. nomen; наиболее известны</i>:\n<b>1)</b> P. R. Rufus, <i>философ-стоик, оратор, политик и историк, друг Лелия и Сципиона, консул в 105 г. до н. э., правитель провинции Азия в 99 г. до н. э.; умер в Смирне</i> <i>C</i>;\n<b>2)</b> P. R. Lupus, <i>народный трибун в 56 г. до н. э., сторонник Помпея</i> <i>Q</i>;\n<b>3)</b> Claudius R. Namatianus, <i>родом галл, был в Риме praefectus urbis ок. 415 г. н. э., автор стихотворного произведения</i> «Itinerarium»."],
  ["rutilo", "<big>rutilo</big>\n\n<b>rutilo, avi, atum, are</b> [rutilus]\n<b>1)</b> быть красноватым, румяниться, рдеть (Aurora rutilat <i>Acc</i>);\n<b>2)</b> блистать золотом (arma rutilant <i>V</i>), окрашивать в красноватый цвет, делать рыжим (comae rutilatae <i>L</i>)."],
  ["rutilus", "<big>rutilus</big>\n\n<b>rutilus, a, um</b> [<i>одного корня с</i> ruber]\nизжелта-красный, золотисто-красный (ignis <i>V</i>; nubes <i>Lcn</i>); ярко-красный (cruor <i>O</i>); русый <i>или</i> рыжий (capilli <i>O</i>; caput <i>Pl</i>); отливающий золотом (thorax <i>VF</i>): rutila pellis <i>VF</i> золотое руно; rutilum metallum <i>Lcn</i> = aurum."],
  ["rutinus", "<big>rutinus</big>\n\n<b>rutinus, a, um</b> [ruta]\nприготовленный из руты (oleum <i>CA</i>)."],
  ["rutrum", "<big>rutrum</big>\n\n<b>rutrum, i</b> <i>n</i> [ruo]\n<b>1)</b> заступ, лопата <i>Cato, L, O</i>;\n<b>2)</b> кельня (<i>лопаточка каменщиков</i>) <i>Cato, Vtr, PM</i>."],
  ["rutuba", "<big>rutuba</big>\n\n<b>I rutuba, ae</b> <i>f</i>\nсмятение, тревога <i>Vr</i>.\n\n<b>II Rutuba, ae</b> <i>m</i>\nРутуба:\n<b>1)</b> <i>река в Лигурии</i> <i>PM</i>;\n<b>2)</b> <i>имя гладиатора</i> <i>H</i>."],
  ["rutula", "<big>rutula</big>\n\n<b>rūtula, ae</b> <i>f</i> [<i>demin. к</i> ruta I]\nнемножко руты <i>C</i>."],
  ["Rutuli", "<big>Rutuli</big>\n\n<b>Rutulī, ōrum</b> <i>и</i> <b>um</b> <i>m</i>\nрутулы:\n<b>1)</b> <i>древний народ в Латии с главн. городом Ardea</i> <i>C, V, L</i>;\n<b>2)</b> <i>жители Сагунта</i> (<i>как колонии города Ardea</i>) <i>Sil</i>."],
  ["Rutulus", "<big>Rutulus</big>\n\n<b>Rutulus, a, um</b> [Rutuli]\nрутульский: R. rex <i>V</i> = Turnus."],
  ["rutund-", "<big>rutund-</big>\n\n<b>rutund-</b> <i>Cato, Lcr, Vr, Ap</i>\n= rotund-."],
  ["Rutupiae", "<big>Rutupiae</big>\n\n<b>Rutup(i)ae, ārum</b> <i>f</i>\nРутупии, <i>портовый город племени Cantii <i>или</i> Caverni</i> (<i>Британия</i>) (<i>ныне</i> Richborough) (<i>см.</i> Rutupinus)."],
  ["Rutupinus", "<big>Rutupinus</big>\n\n<b>Rutupīnus, a, um</b> [Rutupiae]\nрутупийский (fundus <i>J</i>; litora <i>Lcn</i>)."],
  ["rutus", "<big>rutus</big>\n\n<b>I rūtus, a, um</b> (ū долгое!) <i>\npart. pf. к</i> ruo (<i>только в выражении</i> ruta et caesa, <i>см.</i> ruta 2).\n\n<b>II rutus, ūs</b> <i>m</i> [ruo]\nразгребание <i>Vr</i>."],
  ["rythm-", "<big>rythm-</big>\n\n<b>rythm-</b> <i>v. l.</i>\n= rhythm-."]
]