export default [
  ["S", "<big>S</big>\n\n<b>S, s</b>\n<i>девятнадцатая буква латинского алфавита; в сокращениях</i>: S. = Sextus (<i>praenomen</i>) <i>или</i> semis, semissis, sacrum, suus; S. <i>или</i> Sp. = Spurius (<i>praenomen</i>); S. C. = senatus consultum; S. P. Q. R. = senatus populusque Romanus; <i>в письмах</i>: S. (D. P.) = salutem (dicit plurimam); S-V-B-E-E(Q)-V. = si vales bene est, ego (quidem) valeo."],
  ["Saba", "<big>Saba</big>\n\n<b>Saba, ae</b> <i>f</i>\nСаба, <i>главн. город сабеев в Arabia Felix</i> <i>PM</i>."],
  ["Sabad-", "<big>Sabad-</big>\n\n<b>Sabad-</b> <i>Ap</i>\n= Sabaz-."],
  ["Sabae", "<big>Sabae</big>\n\n<b>Sabae, arum</b> <i>f</i> <i>Eccl</i>\n= Saba."],
  ["Sabaea", "<big>Sabaea</big>\n\n<b>Sabaea, ae</b> <i>f</i> <i>H</i>\n(<i>тж.</i> terra S. <i>O</i>) = Arabia Felix."],
  ["Sabaeus", "<big>Sabaeus</big>\n\n<b>I Sabaeus, a, um</b> [Saba]\nсавский, сабейский, <i>поэт.</i> аравийский (tus <i>V</i>; myrrha <i>SenT</i>).\n\n<b>II Sabaeus, i</b> <i>m</i>\nжитель города Saba <i>V, Mela, PM, VF etc.</i>"],
  ["sabaja", "<big>sabaja</big>\n\n<b>sabaja, ae</b> <i>f</i>\nсабая, <i>ячменная брага</i> <i>Amm, Hier</i>."],
  ["sabajarius", "<big>sabajarius</big>\n\n<b>sabajarius, i</b> <i>m</i> [sabaja] <i>презр.</i>\nпьющий сабаю, <i>т. е.</i> бедняк <i>Amm</i>."],
  ["sabanum", "<big>sabanum</big>\n\n<b>sabanum, i</b> <i>n</i> (<i>греч.</i>)\nполотнище, холст <i>Pall, Veg</i>."],
  ["Sabaoth", "<big>Sabaoth</big>\n\n<b>Sabaoth</b> <i>f pl. indecl.</i> (<i>евр.</i>)\nСаваоф, <i>т. е.</i> (небесные) воинства <i>Eccl</i>: Dominus (Deus) S. <i>Eccl</i> бог воинств (<i>ср.</i> «бог браней» <i>у Пушкина</i>)."],
  ["Sabate", "<big>Sabate</big>\n\n<b>Sabate, es</b> <i>f</i>\nСабата, <i>болотистое место в южн. Этрурии с городом того же названия</i> (<i>см.</i> Sabatinus)."],
  ["Sabatinus", "<big>Sabatinus</big>\n\n<b>I Sabatinus, a, um</b> [Sabate]\nсабатийский (tribus <i>L</i>; lacus <i>Col, Frontin</i>).\n\n<b>II Sabatinus, i</b> <i>m</i>\nжитель города Sabate <i>и</i> берегов реки Sabatus <i>L</i>."],
  ["Sabatius", "<big>Sabatius</big>\n\n<b>Sabatius</b> <i>Sil</i>\n= Sabatinus."],
  ["Sabatus", "<big>Sabatus</big>\n\n<b>Sabatus, i</b> <i>m</i>\nСабат, <i>приток реки Calor, впадающей в Вольтурн (Кампания) (см.</i> Sabatinus)."],
  ["Sabazia", "<big>Sabazia</big>\n\n<b>Sabazia, orum</b> <i>n</i>\n<i>празднества в честь Вакха-Сабазия</i> <i>C, Eccl</i>."],
  ["Sabazius", "<big>Sabazius</big>\n\n<b>Sabazius, i</b> <i>m</i>\nСабазий:\n<b>1)</b> <i>фракийско-фригийское божество, впоследствии отождествлённое с Вакхом</i> <i>C</i>;\n<b>2)</b> <i>эпитет Юпитера во Фригии и на Крите</i> <i>VM</i>."],
  ["sabbata", "<big>sabbata</big>\n\n<b>sabbata, orum</b> <i>n</i> (<i>реже</i> <i>Vlg, Eccl, Just sg.</i>) (<i>евр.</i>)\nсуббота, <i>седьмой день недели и день отдыха</i> (<i>у евреев</i>) <i>Sen, O, PM, J etc.</i>: tricesima s. <i>H</i> великая суббота, <i>т. е.</i> йомкипур, судный день, <i>по др.</i> — праздник новолуния."],
  ["sabbatarius", "<big>sabbatarius</big>\n\n<b>I sabbatarius, a, um</b> [sabbata]\nсубботний (luxus <i>Sid</i>).\n\n<b>II sabbatarius, i</b> <i>m</i> [sabbata]\nблюститель субботы, <i>перен.</i> еврей <i>M</i>."],
  ["sabbatismus", "<big>sabbatismus</big>\n\n<b>sabbatismus, i</b> <i>m</i> [sabbata]\nсоблюдение субботы <i>Aug, Hier</i>."],
  ["sabbatizo", "<big>sabbatizo</big>\n\n<b>sabbatizo, —, —, are</b>\nсоблюдать субботние дни <i>Vlg, Eccl</i>."],
  ["sabbatum", "<big>sabbatum</big>\n\n<b>sabbatum, i</b> <i>n</i> <i>Vlg, Eccl</i>\n= sabbata."],
  ["Sabelli", "<big>Sabelli</big>\n\n<b>Sabelli, orum</b> <i>m</i>\nсабеллы:\n<b>1)</b> <i>общее название ряда племён сабинского происхождения</i> (<i>пелигнов, марруцинов, марсов, герников и др.</i>) <i>PM</i>;\n<b>2)</b> <i>Vr, H, Col</i> = Sabini."],
  ["Sabellicus", "<big>Sabellicus</big>\n\n<b>Sabellicus, a, um</b> <i>V, PM</i>\n= Sabellus I."],
  ["Sabellus", "<big>Sabellus</big>\n\n<b>I Sabellus, a, um</b> [Sabelli]\n<b>1)</b> сабельский, сабинский <i>V, H, L, Sil</i>;\n<b>2)</b> <i>перен.</i> неприхотливый, простой (mensa <i>J</i>).\n\n<b>II Sabellus, i</b> <i>m</i>\n<b>1)</b> <i>H sg. к</i> Sabelli;\n<b>2)</b> владелец поместья в области сабинян (<i>Гораций о себе</i>) <i>H</i>."],
  ["Sabina", "<big>Sabina</big>\n\n<b>I Sabina, ae</b> <i>f</i>\n<b>1)</b> сабинянка <i>Prp, O, J, St</i>;\n<b>2)</b> (<i>sc.</i> terra) сабинская земля <i>Col</i>.\n\n<b>II Sabina, ae</b> <i>f</i>\n<b>1)</b> <i>см.</i> Poppaea S.;\n<b>2)</b> Julia S. Augusta, <i>жена императора Адриана</i> <i>Spart</i>.\n\n<b>III Sabina (herba), ae</b> <i>f</i> <i>бот.</i>\nможжевельник казацкий (<i>Juniperus Sabina, <i>L</i>.</i>) (<i>употреблялся для культовых курений</i>) <i>Cato, O, PM</i>."],
  ["Sabine", "<big>Sabine</big>\n\n<b>Sabine</b>\nпо-сабински <i>Vr</i>."],
  ["Sabini", "<big>Sabini</big>\n\n<b>Sabini, orum</b> <i>m</i>\n<b>1)</b> сабиняне, <i>племя в гористой области к сев.-вост. от Рима, с городами Amiternum, Reate, Cures, Corfinium</i> <i>Vr, C, L, H etc.</i>;\n<b>2)</b> <i>область сабинян</i> <i>L</i>: ardui Sabini <i>H</i> скалистая страна сабинян;\n<b>3)</b> <i>поместье Горация в области сабинян, на Digentia, к сев. от Тибура</i> <i>H</i>."],
  ["sabinum", "<big>sabinum</big>\n\n<b>sabinum, i</b> <i>n</i>\n<b>1)</b> (<i>sc.</i> vinum) сабинское вино <i>H</i>;\n<b>2)</b> <i>Sil</i> = Sabini 2."],
  ["Sabinus", "<big>Sabinus</big>\n\n<b>I Sabinus, i</b> <i>m</i>\n<b>1)</b> сабинянин — <i>см.</i> Sabini 1;\n<b>2)</b> <i>римск. cognomen, преим. в роде Поппеев <i>T, PM</i>;</i>\n<b>3)</b> <i>мужское имя</i> <i>O, H, Su</i>.\n\n<b>II Sabinus, a, um</b>\nсабинский (ager <i>Vr, C</i>; virgines raptae <i>L</i>); Sabina herba — <i>см.</i> Sabina III."],
  ["Sabis", "<big>Sabis</big>\n\n<b>Sabis, is</b> (<i>acc.</i> im, <i>abl.</i> i) <i>m</i>\n<i>левый приток реки Mosa в Gallia Belgica</i> (<i>ныне</i> Sambre) <i>Cs</i>."],
  ["sablum", "<big>sablum</big>\n\n<b>sablum, i</b> <i>n</i> <i>Cato, PM</i>\n= sabulum."],
  ["Sabratensis", "<big>Sabratensis</big>\n\n<b>Sabratensis, e</b> [Sabrata]\nсабратский <i>Su</i>."],
  ["Sabratha", "<big>Sabratha</big>\n\n<b>Sabrat(h)a, ae</b> <i>f</i>\nСабрата, <i>город в Африке, близ Малого Cupma</i> <i>PM, Sol</i>."],
  ["Sabrina", "<big>Sabrina</big>\n\n<b>Sabrina, ae</b> <i>f</i>\nСабрина, <i>река в юго-зап. Британии</i> (<i>ныне</i> Severn) <i>T</i>."],
  ["sabuc-", "<big>sabuc-</big>\n\n<b>sabuc-</b> <i>v. l.</i>\n= sambuc-."],
  ["sabuletum", "<big>sabuletum</big>\n\n<b>sabuletum, i</b> <i>n</i> [sabulum]\nпесчаное место <i>PM</i>."],
  ["sabulo", "<big>sabulo</big>\n\n<b>sabulo, onis</b> <i>m</i>\nкрупнозернистый песок, гравий <i>Vr, Vtr, Col, Pall</i>."],
  ["sabulosus", "<big>sabulosus</big>\n\n<b>sabulosus, a, um</b> [sabulum]\nпесчаный, изобилующий песком (terra <i>Vr, PM</i>; loca <i>Col</i>)."],
  ["sabulum", "<big>sabulum</big>\n\n<b>sabulum, i</b> <i>n</i>\nкрупный песок, гравий <i>PM, QC</i>."],
  ["saburra", "<big>saburra</big>\n\n<b>saburra, ae</b> <i>f</i> [sabulum]\nпесчаный балласт <i>V, L, PM</i>."],
  ["saburralis", "<big>saburralis</big>\n\n<b>saburralis, e</b> [saburra]\nпесочный, <i>т. е.</i> служащий балластом (sacoma <i>Vr</i>)."],
  ["saburro", "<big>saburro</big>\n\n<b>saburro, (avi), atum, are</b>\n<b>1)</b> нагружать балластом (s. se arena <i>PM</i>);\n<b>2)</b> кормить до отвала: saburratus <i>Pl</i> наевшийся, сытый по горло."],
  ["Sabus", "<big>Sabus</big>\n\n<b>Sabus, i</b> <i>m</i>\nСаб:\n<b>1)</b> <i>миф. родоначальник сабинян</i> <i>Sil</i>;\n<b>2)</b> <i>царь Индии</i> <i>QC</i>."],
  ["Sacae", "<big>Sacae</big>\n\n<b>Sacae, arum</b> <i>m</i>\nсаки, <i>скифское кочевое племя к юго-вост. от массагетов, по верхнему течению Яксарта</i> <i>Ctl, PM, QC</i>."],
  ["saccaria", "<big>saccaria</big>\n\n<b>saccaria, ae</b> <i>f</i> [saccus]\nремесло носильщика, труд грузчика: saccariam facere <i>Ap</i> быть носильщиком, таскать мешки."],
  ["saccarius", "<big>saccarius</big>\n\n<b>I saccarius, a, um</b> [saccus]\nгружённый мешками (navis <i>Q</i>).\n\n<b>II saccarius, i</b> <i>m</i>\nгрузчик, носильщик <i>Dig</i>."],
  ["saccatum", "<big>saccatum</big>\n\n<b>saccatum, i</b> <i>n</i> [sacco]\n<b>1)</b> плохое вино, «бурда» <i>Is</i>;\n<b>2)</b> <i>Eccl</i> = urina."],
  ["saccelatio", "<big>saccelatio</big>\n\n<b>saccelatio, onis</b> <i>f</i> [saccellus 2] <i>мед.</i>\nприкладывание сухих компрессов <i>Veg</i>."],
  ["saccellus", "<big>saccellus</big>\n\n<b>saccellus, i</b> <i>m</i> [<i>demin. к</i> sacculus]\n<b>1)</b> мешочек, кошелёк (saccelli sonantes aere <i>Pt</i>);\n<b>2)</b> <i>мед.</i> мешочек с лекарством (<i>для прикладывания к больному месту</i>), <i>т. е.</i> сухой компресс <i>CC, Veg</i>."],
  ["sacceus", "<big>sacceus</big>\n\n<b>sacceus, a, um</b> [saccus]\nсшитый из мешочной ткани <i>или</i> в виде мешка (tunica <i>Hier</i>)."],
  ["saccharon", "<big>saccharon</big>\n\n<b>saccharon (-um), i</b> <i>n</i> (<i>греч.</i>)\n(тростниковый) сахар <i>PM</i>."],
  ["saccinus", "<big>saccinus</big>\n\n<b>saccinus, a, um</b> <i>Vlg</i>\n= sacceus."],
  ["sacciperium", "<big>sacciperium</big>\n\n<b>sacciperium, i</b> <i>n</i> [saccus + pera]\nсумка для денежного мешка <i>Pl</i>."],
  ["sacco", "<big>sacco</big>\n\n<b>sacco, (avi), atum, are</b> [saccus]\nпроцеживать, фильтровать (linteo saccari <i>PM</i>; s. vinum <i>M</i>): saccatus umor corporis <i>Lcr</i> = urina."],
  ["saccularius", "<big>saccularius</big>\n\n<b>saccularius, i</b> <i>m</i> [sacculus]\nкарманный вор, жулик <i>Dig</i>."],
  ["sacculus", "<big>sacculus</big>\n\n<b>sacculus, i</b> <i>m</i> [<i>demin. к</i> saccus]\nмешочек <i>LM, Ctl, Sen, Ap, Pt</i>."],
  ["saccus", "<big>saccus</big>\n\n<b>saccus, i</b> <i>m</i> (<i>греч.</i>)\n<b>1)</b> мешок, сумка (s, nummorum <i>H</i>): s. nivarius <i>M</i> мешок для процеживания снеговой воды; s. vinarius <i>PM</i> винный фильтр; ad saccum ire <i>погов. Pl</i> пойти с сумой (по миру);\n<b>2)</b> вретище <i>или</i> власяница <i>Eccl</i>."],
  ["sacellum", "<big>sacellum</big>\n\n<b>sacellum, i</b> <i>n</i> [<i>demin. к</i> sacrum]\nнебольшое святилище (fana sacellaque <i>L</i>; s. Priapi <i>Pt</i>)."],
  ["sacellus", "<big>sacellus</big>\n\n<b>sacellus, i</b> <i>m</i> <i>Pt</i>\n= saccellus."],
  ["sacer", "<big>sacer</big>\n\n<b>I sacer, era, erum</b>\n<b>1)</b> посвящённый (sacrum Soli animal <i>T</i>; anseres sacri Junonis <i>L</i>); предназначенный (magno operi <i>T</i>);\n<b>2)</b> священный, святой (fontes, jura <i>O</i>); внушающий благоговейное уважение, великий (Maro <i>M</i>): sacra profanaque <i>Sl</i> божеское и человеческое; Sacer mons <i>C, L</i> Священный холм (<i>на берегу Аниена, в области сабинян, в 3-х римск. милях от Рима</i>); Sacra via <i>C</i> (<i>тж.</i> Sacer clivus <i>H</i>) Священная улица (<i>в Риме, ведшая через Форум к храму Юпитера Капитолийского</i>); os sacrum <i>CA</i> крестцовая кость;\n<b>3)</b> обречённый подземным богам, <i>т. е.</i> преданный проклятию, проклятый, гнусный (intestabilis et s. <i>H</i>; homo sacerrimus <i>Pl</i>): auri sacra fames <i>V</i> отвратительная жажда золота;\n<b>4)</b> магический, таинственный (venenum <i>VF</i>); sacra passio <i>CA</i> падучая, эпилепсия.\n\n<b>II sacer, eris, ere</b> <i>арх. Pl, Vr</i>\n= sacer I."],
  ["sacerdos", "<big>sacerdos</big>\n\n<b>sacerdos, otis</b> <i>m, f</i> [sacer]\nжрец, жрица: Vestae s. <i>O</i> весталка; regina s. <i>V</i> = Rhea; summus s. <i>Vlg</i> первосвященник."],
  ["sacerdotalis", "<big>sacerdotalis</big>\n\n<b>I sacerdotalis, e</b> [sacerdos]\nжреческий (collegia <i>Lampr</i>): sedes s. <i>Amm</i> епископская кафедра; ordo s. <i>Eccl</i> духовенство.\n\n<b>II sacerdotalis, is</b> <i>m</i> (<i>sc.</i> vir)\nбывший жрец (<i>отслуживший срок священства в провинции</i>) <i>CTh, Tert</i>."],
  ["sacerdotium", "<big>sacerdotium</big>\n\n<b>sacerdotium, i</b> <i>n</i> [sacerdos]\nжречество, жреческий сан, звание жреца (s. inire <i>C</i>; susceptum s. <i>Ap</i>): s. summum <i>или</i> maximum <i>Eccl</i> сан первосвященника (<i>у евреев</i>)."],
  ["sacerdotula", "<big>sacerdotula</big>\n\n<b>sacerdotula, ae</b> <i>f</i> [<i>demin. к</i> sacerdos]\nмолодая жрица <i>Vr</i>."],
  ["sacersanctus", "<big>sacersanctus</big>\n\n<b>sacersanctus, a, um</b> <i>Tert</i>\n= sacrosanctus."],
  ["Saces", "<big>Saces</big>\n\n<b>Saces, ae</b> <i>m</i> <i>Cld sg. к</i>\nSacae."],
  ["sacoma", "<big>sacoma</big>\n\n<b>sacoma, atis</b> <i>n</i> (<i>греч.</i>)\nвес, противовес: ad s. appendere <i>Vtr</i> точно отвесить."],
  ["sacomarius", "<big>sacomarius</big>\n\n<b>sacomarius, a, um</b> [sacoma]\nиспользуемый для уравновешивания, <i>т. е.</i> в качестве гири (cucurbita <i>Hier</i>)."],
  ["sacramentum", "<big>sacramentum</big>\n\n<b>sacramentum, i</b> <i>n</i> [sacro]\n<b>1)</b> <i>воен.</i> обязательство о несении военной службы, присяга на верность, воинская присяга: aliquem militiae sacramento obligare <i>C</i> (rogare <i>Cs, L</i>, adigere <i>L</i>) приводить кого-л. к воинской присяге; dicere s. <i>Cs</i> <i>или</i> sacramento (<i>abl.</i>) <i>L</i> приносить присягу, присягать;\n<b>2)</b> военная служба (praemia sacramentorum <i>J</i>; longum Caesarum s. <i>T</i>);\n<b>3)</b> <i>юр.</i> денежный залог (<i>вносившийся в гражданском процессе обеими сторонами и не возвращавшийся проигравшей стороне</i>) (multae s. <i>C</i>); заклад (justo sacramento cum aliquo contendere <i>C</i>): s. alicujus justum judicare <i>C</i> решить процесс в чью-л. пользу;\n<b>4)</b> обязательство, обещание (s. amicitiae <i>Pt</i>); клятва (perfidum s. dicere <i>H</i>);\n<b>5)</b> (<i>в христ. религии</i>) таинство (s. baptismatis <i>Tert</i>)."],
  ["Sacrani", "<big>Sacrani</big>\n\n<b>Sacrani, orum</b> <i>m</i>\nсакраны, <i>древнее племя в Латии</i> (<i>см.</i> Sacranus)."],
  ["Sacranus", "<big>Sacranus</big>\n\n<b>Sacranus, a, um</b> [Sacrani]\nсакранский (acies <i>V</i>)."],
  ["sacrarium", "<big>sacrarium</big>\n\n<b>sacrarium, i</b> <i>n</i> [sacer]\n<b>1)</b> святилище, храм (s. Bonae Deae <i>C</i>; s. Jovis <i>Su</i>);\n<b>2)</b> помещение для хранения священной утвари (s. est locus, in quo sacra reponuntur <i>Dig</i>);\n<b>3)</b> <i>ирон.</i> вместилище (libidinum <i>C</i>);\n<b>4)</b> императорский дворец <i>Dig</i>;\n<b>5)</b> зал судебных заседаний <i>CJ</i>."],
  ["sacrate", "<big>sacrate</big>\n\n<b>sacrate</b> [sacratus]\n<b>1)</b> свято, благочестиво (vivere <i>Aug</i>);\n<b>2)</b> благоговейно, в мистическом почтении (accipere aliquid <i>Aug</i>)."],
  ["sacratio", "<big>sacratio</big>\n\n<b>sacratio, onis</b> <i>f</i>\nпосвящение <i>Macr</i>."],
  ["sacrator", "<big>sacrator</big>\n\n<b>sacrator, oris</b> <i>m</i>\nосвятивший (s. justitiae <i>Aug</i>)."],
  ["sacratus", "<big>sacratus</big>\n\n<b>sacratus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> sacro;\n<b>2.</b> <i>adj.</i> священный (templum <i>V</i>; jus <i>Sen</i>); обожествлённый, обожествляемый (<i>sc.</i> Augustus <i>O</i>)."],
  ["sacricola", "<big>sacricola</big>\n\n<b>sacricola, ae</b> <i>m</i> [sacrum + colo II]\n<b>1)</b> совершающий жертвоприношение, жрец <i>T, Ap, Amm, Macr</i>;\n<b>2)</b> почитатель, поклонник <i>Ap</i>."],
  ["sacrifer", "<big>sacrifer</big>\n\n<b>sacrifer, fera, ferum</b> [sacrum + fero]\nнесущий <i>или</i> везущий священные предметы (rates <i>O</i>)."],
  ["sacrificalis", "<big>sacrificalis</big>\n\n<b>sacrificalis, e</b> [sacrificium]\nжертвенный (apparatus <i>T</i>; ministeria et instrumenta <i>Tert</i>)."],
  ["sacrificatio", "<big>sacrificatio</big>\n\n<b>sacrificatio, onis</b> <i>f</i> [sacrifico]\nжертвоприношение <i>C</i>."],
  ["sacrificator", "<big>sacrificator</big>\n\n<b>sacrificator, oris</b> <i>m</i>\nсовершающий жертвоприношение <i>Tert</i>."],
  ["sacrificatus", "<big>sacrificatus</big>\n\n<b>sacrificatus, (us) </b> <i>m</i> <i>Ap</i>\n= sacrificatio."],
  ["sacrificialis", "<big>sacrificialis</big>\n\n<b>sacrificialis, e</b> <i>v. l.</i>\n= sacrificalis."],
  ["sacrificiolus", "<big>sacrificiolus</big>\n\n<b>sacrificiolus, i</b> <i>m</i> <i>Vr</i>\n= sacrificulus."],
  ["sacrificium", "<big>sacrificium</big>\n\n<b>sacrificium, i</b> <i>n</i> [sacrum + facio]\nжертва, жертвоприношение (s. facere <i>C</i> <i>и</i> procurare <i>Cs</i>)."],
  ["sacrifico", "<big>sacrifico</big>\n\n<b>sacrifico, avi, atum, are</b> [sacrum + facio]\nприносить в жертву (suem <i>O</i>); совершать жертвоприношение (apud deos <i>T</i>; Jovi aliqua re <i>Pl</i>): sacrificati <i>Eccl</i> приносившие жертвы богам (<i>о ранних христианах, вынужденных скрывать свою веру</i>)."],
  ["sacrificor", "<big>sacrificor</big>\n\n<b>sacrificor, atus sum, ari</b> <i>depon.</i> <i>Vr, Aug</i>\n= sacrifico."],
  ["sacrificulus", "<big>sacrificulus</big>\n\n<b>sacrificulus, i</b> <i>m</i> [<i>demin. к</i> sacrificus]\nприносящий жертву <i>L, Su</i>: rex s. <i>L</i> верховный жрец (<i>совершавший те жертвоприношения, которые в дореспубликанскую эпоху совершались царями</i>)."],
  ["sacrificus", "<big>sacrificus</big>\n\n<b>sacrificus, a, um</b> [sacrum + facio]\n<b>1)</b> приносящий жертву: rex s. <i>L</i> = rex sacrificulus (еж.);\n<b>2)</b> жертвенный (ritus <i>O</i>): dies s. <i>O</i> день жертвоприношения."],
  ["sacrilege", "<big>sacrilege</big>\n\n<b>sacrilege</b>\nсвятотатственно, кощунственно (contumeliosissime et s. <i>Tert</i>)."],
  ["sacrilegium", "<big>sacrilegium</big>\n\n<b>sacrilegium, i</b> <i>n</i> [sacrum + lego I]\n<b>1)</b> святотатство <i>L, Q, Just</i>: sacrilegia minuta puniuntur, magna in triumphis feruntur <i>погов. Sen</i> мелкие преступления караются, а большие прославляются (<i>ср.</i> «что сходит с рук ворам, за то воришек бьют»); осквернение, поругание святыни, кощунство <i>Nep, Sen etc.</i>;\n<b>2)</b> похищенные священные предметы (fur onustus sacrilegio <i>Ph</i>)."],
  ["sacrilegus", "<big>sacrilegus</big>\n\n<b>I sacrilegus, a, um</b> [sacrum + lego I]\nсвятотатственный (homo <i>Tet</i>; manus <i>H, O</i>; lingua <i>O</i>); нечестивый (artes meretricum <i>O</i>).\n\n<b>II sacrilegus, i</b> <i>m</i>\n<b>1)</b> осквернитель святыни <i>Pl, C, Nep</i>; нечестивец <i>Nep, Q etc.</i>;\n<b>2)</b> негодяй <i>Ter, Ap</i>."],
  ["Sacriportus", "<big>Sacriportus</big>\n\n<b>Sacriportus, us</b> <i>m</i>\nСакрипорт:\n<b>1)</b> <i>город в Тарентинском заливе, близ Тарента</i> <i>L</i>;\n<b>2)</b> <i>город в Латии, близ Пренесте</i> <i>L, VP, Lcn</i>."],
  ["sacrium", "<big>sacrium</big>\n\n<b>sacrium, i</b> <i>n</i>\nскифский янтарь <i>PM</i>."],
  ["sacro", "<big>sacro</big>\n\n<b>sacro, avi, atum, are</b> [sacer]\n<b>1)</b> освящать, объявлять неприкосновенным, священным, нерушимым (s. foedus <i>L</i>): lex sacrata <i>C, L</i> нерушимый закон;\n<b>2)</b> благоговейно чтить (patrem deorum hominumque aliqua re <i>L</i>);\n<b>3)</b> посвящать (laurum Phoebo <i>V</i>; opus alicui sacratum <i>O</i>);\n<b>4)</b> (по)жаловать (honorem alicui <i>V</i>);\n<b>5)</b> увековечивать, обессмертить (aliquem Lesbio plectro <i>H</i>): aliquem inter divos s. <i>Spart</i> причислить кого-л. к богам;\n<b>6)</b> обрекать на гибель, предавать проклятию, проклинать (caput alicujus <i>L</i>)."],
  ["sacrosanctus", "<big>sacrosanctus</big>\n\n<b>sacrosanctus, a, um</b> [sacer + sanctus]\nсвященный (memoria alicujus <i>PJ</i>); неприкосновенный (potestas tribunorum plebis <i>L</i>); нерушимый (foedus <i>C</i>)."],
  ["sacrufico", "<big>sacrufico</big>\n\n<b>sacrufico</b> <i>арх.</i> <i>Pl</i>\n= sacrifico."],
  ["sacrum", "<big>sacrum</big>\n\n<b>sacrum, i</b> <i>n</i> [sacer]\n<b>1)</b> священный предмет, <i>pl.</i> священная утварь (sacra ex aedibus eripere <i>C</i>); священное творение (sacra Maronis <i>M</i>);\n<b>2)</b> священный обряд, священнодействие, богослужение (sacra Orphica <i>C</i>);\n<b>3)</b> жертвоприношение, жертва (s. facere Herculi <i>L</i>): inter s. saxumque stare <i>погов. Pl</i> находиться между жертвенным животным и каменным ножом (фециала), <i>т. е.</i> между молотом и наковальней; hereditas sine sacris <i>Pl</i> наследство без обязательных жертвоприношений предкам покойного, <i>перен.</i> выгода, не сопряжённая с расходами (не требующая усилий);\n<b>4)</b> <i>pl.</i> тайны, таинства, мистерии (sacra profanare <i>T</i>)."],
  ["saeclaris", "<big>saeclaris</big>\n\n<b>saeclaris, e</b> <i>MA</i>\n= saecularis."],
  ["saeclum", "<big>saeclum</big>\n\n<b>saeclum, i</b> <i>n</i> <i>v. l.</i>\n= saeculum."],
  ["saecularis", "<big>saecularis</big>\n\n<b>saecularis, e</b> [saeculum]\n<b>1)</b> вековой, столетний: (ludi) saeculares <i>PM, T, Su</i> секулярные игры (<i>устраиваемые обычно раз в столетие</i>); carmen saeculare <i>H</i> песня, исполнявшаяся во время секулярных игр, <i>тж.</i> песня в честь столетнего юбилея;\n<b>2)</b> мирской, светский (homines <i>Hier</i>) <i>или</i> языческий (<i>sc.</i> Dido, Lucretia <i>Tert</i>)."],
  ["saeculariter", "<big>saeculariter</big>\n\n<b>saeculariter</b>\nпо-мирски <i>или</i> по--язычески <i>Eccl</i>."],
  ["saecularius", "<big>saecularius</big>\n\n<b>saecularius, a, um</b> <i>Eccl</i>\n= saecularis."],
  ["saeculum", "<big>saeculum</big>\n\n<b>saeculum (saeclum), i</b> <i>n</i> [<i>одного корня с</i> sero II]\n<b>1)</b> род, поколение, средний человеческий век (<i>исчислявшийся в</i> 33 1/3 <i>года</i>) (multa saecula hominum <i>C</i>; cornix novem saecula passa <i>O</i>);\n<b>2)</b> пол (s. muliebre <i>Lcr</i>);\n<b>3)</b> род <i>или</i> вид (leonum, ferarum <i>Lcr</i>; generatim saecla propagare <i>Lcr</i>);\n<b>4)</b> царствование, правление (s. beatissimum <i>T</i>);\n<b>5)</b> эпоха, период, время (fecunda culpae saecula <i>H</i>): ventura in saecula <i>Sil</i> в далёком будущем || дух времени, нравы эпохи (mitescunt saecula <i>V</i>; saecula aurea <i>V</i> <i>и</i> ferrea <i>Tib</i>);\n<b>6)</b> человеческий век (<i>наибольшая длительность которого определялась в 100 лет</i>), век, столетие (duobus saeculis ante <i>C</i>; aliquot saeculis post <i>C</i>);\n<b>7)</b> «мир», мирское состояние (<i>в противоположность монашескому</i>) (reverti ad s. <i>Eccl</i>);\n<b>8)</b> мир, вселенная (auctor saeculi <i>Eccl</i> = deus; solvere s. in favilla <i>Eccl</i>)."],
  ["saepe", "<big>saepe</big>\n\n<b>saepe</b>\nчасто, многократно (s. et multum <i>C</i>): feci et quidem saepius <i>C</i> (всё это) я делал и, притом, не раз."],
  ["saepenumero", "<big>saepenumero</big>\n\n<b>saepe–numero</b> (<i>тж. раздельно</i>) <i>C, Cs etc.</i>\n= saepe."],
  ["saepes", "<big>saepes</big>\n\n<b>saepes, is</b> <i>f</i>\nзабор, ограда, изгородь (saepibus prospectus impeditur <i>Cs</i>; segeti praetendere saepem <i>V</i>): portarum s. <i>O</i> = portae."],
  ["saepia", "<big>saepia</big>\n\n<b>saepia, ae</b> <i>f</i> <i>v. l.</i>\n= sepia."],
  ["saepicula", "<big>saepicula</big>\n\n<b>saepicula, ae</b> <i>f</i> [<i>demin. к</i> saepes]\nнебольшая ограда, заборчик <i>Ap</i>."],
  ["saepicule", "<big>saepicule</big>\n\n<b>saepicule</b> [<i>demin. к</i> saepe]\nчастенько, довольно часто <i>Pl, Ap</i>."],
  ["saepimen", "<big>saepimen</big>\n\n<b>saepimen, inis</b> <i>n</i> <i>Ap</i>\n= saepes."],
  ["saepimentum", "<big>saepimentum</big>\n\n<b>saepimentum, i</b> <i>n</i> <i>Vr, C</i>\n= saepes."],
  ["Saepinum", "<big>Saepinum</big>\n\n<b>Saepinum, i</b> <i>n</i>\nСепин, <i>город в Самнии, к сев. от Беневента</i> (<i>ныне</i> Sepino) <i>L</i>."],
  ["saepio", "<big>saepio</big>\n\n<b>saepio, saepsi, saeptum, ire</b>\n<b>1)</b> обносить забором, огораживать (segetem <i>Col</i>); обсаживать (vallum arboribus <i>L</i>); обносить, окружать (urbem raoenibus <i>C</i>; oppidum castris <i>C</i>); покрывать, окутывать (aliquid veste <i>Pl</i>); обнимать (omnia avido complexu <i>Lcr</i>): aliquid memoria s. <i>C</i> запомнить что-л.;\n<b>2)</b> защищать, охранять, ограждать (vias <i>L</i>; oculos membranis <i>C</i>; aliquid legibus <i>C</i>)."],
  ["saepissimus", "<big>saepissimus</big>\n\n<b>saepissimus, a, um</b> [<i>superl.</i> <i>при неупотр.</i> <i>posit.</i>]\nвесьма частый (discordia <i>Cato</i>)."],
  ["saeps", "<big>saeps</big>\n\n<b>saeps, is</b> <i>f</i> <i>C ap. Aus</i>\n= saepes."],
  ["saepta", "<big>saepta</big>\n\n<b>saepta, orum</b> <i>n</i> [saepio]\n<b>1)</b> ограда на форуме <i>или</i> на Марсовом поле для предвыборных собраний комициев <i>C, O, M</i>;\n<b>2)</b> плотина, запруда (шлюза) <i>Dig</i>."],
  ["saeptio", "<big>saeptio</big>\n\n<b>saeptio, onis</b> <i>f</i> [saepio]\nзабор <i>или</i> перегородка <i>Vtr</i>: saeptiones urbis <i>Vop</i> стены вокруг города."],
  ["saeptum", "<big>saeptum</big>\n\n<b>saeptum, i</b> <i>n</i> [saepio]\n<b>1)</b> забор, ограда, изгородь <i>C etc.</i>: saepta domorum <i>Lcr</i> стены домов, <i>т. е.</i> дома;\n<b>2)</b> перегородка, препона: (transversum) s. <i>CC</i> грудобрюшная преграда, диафрагма;\n<b>3)</b> <i>Vr etc.</i> = saepta."],
  ["saeptuose", "<big>saeptuose</big>\n\n<b>saeptuose</b> [saepio]\nукрыто, <i>перен.</i> скрыто, темно <i>LA</i>."],
  ["saeta", "<big>saeta</big>\n\n<b>saeta, ae</b> <i>f</i>\n<b>1)</b> жёсткие волосы (obsessa saetis cervix <i>Sen</i>): s. equina <i>C</i> конский волос;\n<b>2)</b> грива (equi, leonis <i>C</i>);\n<b>3)</b> щетина (apri <i>O</i>);\n<b>4)</b> рыболовная леса <i>O, M</i>;\n<b>5)</b> малярная кисть (saetis inducere <i>Vtr, PM</i>)."],
  ["Saetabis", "<big>Saetabis</big>\n\n<b>Saetabis, is</b> <i>f</i>\nСетабис, <i>город в Hispania Tarraconensis</i> <i>PM, Sil</i>."],
  ["Saetabus", "<big>Saetabus</big>\n\n<b>Saetabus, a, um</b> [Saetabis]\nсетабисский (sudaria <i>Ctl</i>)."],
  ["saetiger", "<big>saetiger</big>\n\n<b>I saetiger, gera, gerum</b> [saeta + gero]\n<b>1)</b> покрытый щетиной, щетинистый (sus <i>V, O</i>);\n<b>2)</b> ворсистый (vestis <i>Eccl</i>).\n\n<b>II saetiger, geri</b> <i>m</i> <i>поэт.</i>\nвепрь, кабан <i>O, M</i>."],
  ["saetosus", "<big>saetosus</big>\n\n<b>saetosus, a, um</b> [saeta]\nпокрытый щетиной, щетинистый (aper <i>V</i>); покрытый шерстью, волосатый (pectus <i>CC</i>; membra <i>H</i>)."],
  ["saetula", "<big>saetula</big>\n\n<b>saetula, ae</b> <i>f</i> [<i>demin. к</i> saeta]\nкороткая щетина <i>Eccl</i>."],
  ["saeve", "<big>saeve</big>\n\n<b>saeve</b> [saevus]\nсвирепо, жестоко (s. et atroci ter <i>Su</i>)."],
  ["saevidicus", "<big>saevidicus</big>\n\n<b>saevidicus, a, um</b> [saevus + dico]\nсказанный в гневе, гневный (dicta <i>Ter</i>)."],
  ["saevio", "<big>saevio</big>\n\n<b>saevio, ii, itum, ire</b> [saevus]\nсвирепствовать, неистовствовать (adversus <i>или</i> in aliquem <i>O, AG, Eutr</i> <i>и</i> alicui <i>Tib, O</i>); быть в бешенстве, беситься, злиться (anguis saevit <i>V</i>); бушевать (ventus saevit <i>O</i>): rursus resurgens saevit amor <i>V</i> с новой силой вспыхивает любовь; major in praecordiis dolor saevit <i>Pt</i> скорбь посильнее (этой) терзает мою грудь."],
  ["saevis", "<big>saevis</big>\n\n<b>saevis, e</b> <i>Amm</i>\n= saevus."],
  ["saevitas", "<big>saevitas</big>\n\n<b>saevitas, atis</b> <i>f</i> <i>Eccl</i>\n= saevitia."],
  ["saeviter", "<big>saeviter</big>\n\n<b>saeviter</b> <i>Enn, Pl</i>\n= saeve."],
  ["saevitia", "<big>saevitia</big>\n\n<b>saevitia, ae</b> <i>f</i> [saevus]\n<b>1)</b> свирепость, дикость, бешеный нрав (ferarum <i>Q</i>); жестокость, безжалостность (dictorum factorumque <i>Su</i>);\n<b>2)</b> бурность (maris <i>Sen, VP</i>); суровость (hiemis <i>PM</i>);\n<b>3)</b> дороговизна (annonae <i>T</i>)."],
  ["saevities", "<big>saevities</big>\n\n<b>saevities, ei</b> <i>f</i> <i>Ap</i>\n= saevitia."],
  ["saevitudo", "<big>saevitudo</big>\n\n<b>saevitudo, inis</b> <i>f</i> <i>Pl</i>\n= saevitia."],
  ["saevum", "<big>saevum</big>\n\n<b>I saevum</b> <i>adv.</i> <i>St, Cld</i>\n= saeve.\n\n<b>II saevum, i</b> <i>n</i> <i>Col, Pall</i>\n= sebum."],
  ["saevus", "<big>saevus</big>\n\n<b>saevus, a, um</b>\nсвирепый, лютый (leo <i>Lcr</i>; lupus <i>Tib</i>); злобный, злой (uxor <i>Ter</i>); страшный, ужасный (somnia <i>Tib</i>); грозный (Aeneas s. in armis <i>V</i>); бурный (aequora <i>V</i>); жестокий, суровый (gelu <i>V</i>; Alpes <i>J</i>)."],
  ["saffirinus", "<big>saffirinus</big>\n\n<b>saffirinus</b> <i>Dig</i>\n= sapphirinus."],
  ["saga", "<big>saga</big>\n\n<b>saga, ae</b> <i>f</i> [sagus]\n<b>1)</b> прорицательница, колдунья <i>C</i>;\n<b>2)</b> сводня <i>LM, Tib</i>."],
  ["sagacitas", "<big>sagacitas</big>\n\n<b>sagacitas, atis</b> <i>f</i> [sagax]\n<b>1)</b> чуткость (sensuum <i>Sen</i>); тонкое чутьё (canum <i>PM</i>; narium <i>C</i>);\n<b>2)</b> остроумие (consilii alicujus <i>VM</i>); проницательность, ум (hominis <i>C, Nep</i>)."],
  ["sagaciter", "<big>sagaciter</big>\n\n<b>sagaciter</b> [sagax]\n<b>1)</b> чутко, тонко (olfacere <i>PM</i>);\n<b>2)</b> проницательно (perspicere aliquid <i>Su</i>); точно, тщательно (investigare <i>C</i>)."],
  ["Sagalassenus", "<big>Sagalassenus</big>\n\n<b>Sagalassenus, a, um</b> [Sagalassus]\nсагаласский <i>L</i>."],
  ["Sagalassus", "<big>Sagalassus</big>\n\n<b>Sagalassus, i</b> <i>f</i>\nСагаласс, <i>город в Писидии, к югу от Апамеи</i> <i>PM</i>."],
  ["Sagana", "<big>Sagana</big>\n\n<b>Sagana, ae</b> <i>f</i>\nСагана, <i>имя колдуньи</i> <i>H</i>."],
  ["Sagaris", "<big>Sagaris</big>\n\n<b>Sagaris, is</b> <i>m</i>\nСагарис:\n<b>1)</b> <i>самая крупная река в Вифинии, впадающая в Чёрное море</i> (<i>ныне</i> Сакарья) <i>O, L</i>;\n<b>2)</b> <i>мужское имя</i> <i>V</i>."],
  ["Sagaritis", "<big>Sagaritis</big>\n\n<b>Sagaritis, idis</b> <i>f</i> [Sagaris]\nсагарисская (nympha <i>O</i>)."],
  ["Sagarius", "<big>Sagarius</big>\n\n<b>I Sagarius, i</b> <i>m</i> <i>Sol</i>\n= Sagaris.\n\n<b>II sagarius, a, um</b> [sagum]\nкасающийся плащей, платяной (negotiatio <i>Dig</i>)."],
  ["sagatus", "<big>sagatus</big>\n\n<b>sagatus, a, um</b> [sagum]\n<b>1)</b> одетый в сагум, закутанный в военный плащ <i>C, M, Capit</i>;\n<b>2)</b> сделанный из толстой шерстяной ткани (cuculli <i>Col</i> — <i>v. l.</i>)."],
  ["sagax", "<big>sagax</big>\n\n<b>sagax, acis</b> (<i>abl.</i> e <i>и</i> i) <i>adj.</i> [sagio]\n<b>1)</b> обладающий тонким чутьём (canis <i>C</i>); с тонким обонянием (nasus <i>Pl</i>); с тонким слухом, чуткий (anser <i>O</i>);\n<b>2)</b> острый, проницательный (mens <i>C</i>); остроумный, разумный (parentum cura <i>SenT</i>); умный (homo, animal <i>C</i>)."],
  ["sagda", "<big>sagda</big>\n\n<b>sagda, ae</b> <i>f</i>\nсагда, <i>светло-зелёный самоцвет</i> <i>PM, Sol</i>."],
  ["sagena", "<big>sagena</big>\n\n<b>sagena, ae</b> <i>f</i> (<i>греч.</i>; <i>лат.</i> everriculum 3)\nневод <i>Man, Dig, Vlg</i>."],
  ["Sagiarius", "<big>Sagiarius</big>\n\n<b>Sagiarius, i</b> <i>m</i> <i>PM</i>\n= Sagaris."],
  ["sagina", "<big>sagina</big>\n\n<b>sagina, ae</b> <i>f</i>\n<b>1)</b> откармливание, откорм (anserum <i>Vr</i>); кормление, питание, содержание (aliquem non auctoritate, sed sagina tenere <i>C</i>);\n<b>2)</b> корм, еда, пища (stomachum laxare saginis <i>J</i>);\n<b>3)</b> откормленное на убой животное, убойный скот (saginam caedere <i>Pl</i>);\n<b>4)</b> тучность, полнота (corporis <i>Ap</i>; ventris <i>Just</i>)."],
  ["saginarium", "<big>saginarium</big>\n\n<b>saginarium, i</b> <i>n</i> [sagino]\nместо для откорма домашних животных <i>Vr</i>."],
  ["saginatio", "<big>saginatio</big>\n\n<b>saginatio, onis</b> <i>f</i> [sagino]\nоткармливание, откорм <i>PM, Tert</i>."],
  ["sagino", "<big>sagino</big>\n\n<b>sagino, avi, atum, are</b> [sagina]\nоткармливать (porcum <i>Prp</i>; mulae saginatae <i>Sen</i>); вскармливать, питать (aliquem <i>L</i>); кормить (catulos <i>Q</i>; convivas <i>PM</i>)."],
  ["sagio", "<big>sagio</big>\n\n<b>sagio, —, —, ire</b>\nтонко ощущать, чуять (s. sentire acute est <i>C</i>)."],
  ["sagita", "<big>sagita</big>\n\n<b>sagita, ae</b> <i>f</i> <i>Pl</i>\n(<i>v. l.</i>) = sagitta."],
  ["sagitta", "<big>sagitta</big>\n\n<b>sagitta, ae</b> <i>f</i>\n<b>1)</b> стрела (sagittam impellere nervo <i>O</i>);\n<b>2)</b> ланцет <i>Veg</i>;\n<b>3)</b> стреловидный кончик молодого побега <i>Col, PM</i>;\n<b>4)</b> созвездие Стрелы <i>C, PM</i>."],
  ["sagittarius", "<big>sagittarius</big>\n\n<b>I sagittarius, a, um</b> [sagitta]\nпригодный для изготовления стрел (calamus <i>PM</i>).\n\n<b>II sagittarius, i</b> <i>m</i> [sagitta]\n<b>1)</b> стрелок, стрелец <i>C, Cs, QC etc.</i>;\n<b>2)</b> мастер, изготовляющий стрелы <i>Dig</i>;\n<b>3)</b> (= arcitenens) созвездие Стрельца <i>C, PM, Pt</i>."],
  ["sagittifer", "<big>sagittifer</big>\n\n<b>sagittifer, fera, ferum</b> [sagitta + fero]\nнесущий стрелы (pharetra <i>O, St</i>); вооружённый стрелами (Parthi <i>Ctl</i>); pecus sagittifera <i>Cld</i> дикобраз."],
  ["Sagittipotens", "<big>Sagittipotens</big>\n\n<b>Sagittipotens, entis</b> <i>m</i> [sagitta + potens]\nсозвездие Стрельца <i>C</i>."],
  ["sagitto", "<big>sagitto</big>\n\n<b>sagitto, avi, atum, are</b> [sagitta]\n<b>1)</b> пускать стрелы, стрелять из лука <i>QC, Just</i>;\n<b>2)</b> ранить стрелой (in bello sagittatus <i>CA</i>)."],
  ["sagittula", "<big>sagittula</big>\n\n<b>sagittula, ae</b> <i>f</i> [<i>demin. к</i> sagitta]\nмаленькая стрела <i>Ap</i>."],
  ["sagma", "<big>sagma</big>\n\n<b>sagma, ae</b> <i>f</i> <i>и</i> <b>sagma, atis</b> <i>n</i> (<i>греч.</i>)\nвьючное седло, вьюк <i>Veg, Vlg, Is</i>."],
  ["sagmarius", "<big>sagmarius</big>\n\n<b>sagmarius, i</b> <i>m</i> [sagma]\nвьючная лошадь <i>Lampr, Vop</i>."],
  ["sagmen", "<big>sagmen</big>\n\n<b>sagmen, inis</b> <i>n</i> [<i>одного корня с</i> sacer, sancio]\nпучок священной травы (<i>срывавшейся консулом или претором в городской крепости и носимый жрецами-фециалами и членами посольств в знак их неприкосновенности</i>) <i>L, PM, Dig</i>."],
  ["sagochlamys", "<big>sagochlamys</big>\n\n<b>sagochlamys, ydis</b> <i>f</i> [sagum + chlamys]\nвоенный плащ <i>Treb</i>."],
  ["Sagra", "<big>Sagra</big>\n\n<b>Sagra, ae</b> <i>m, f</i>\nCarpa, <i>река в южн. Бруттии, впадающая в Ионическое море</i> (<i>здесь ок. 580 г. до н. э. 10 000 локров разбили в 12 раз более многочисленное войско кротонцев</i>) <i>C, PM</i>."],
  ["sagulatus", "<big>sagulatus</big>\n\n<b>sagulatus, a, um</b>\nодетый в sagulum <i>Su</i>."],
  ["sagulum", "<big>sagulum</big>\n\n<b>sagulum, i</b> <i>n</i> [<i>demin. к</i> sagum]\nкороткий плащ <i>C, Cs, L etc.</i>"],
  ["sagum", "<big>sagum</big>\n\n<b>sagum, i</b> <i>n</i>\nсагум:\n<b>1)</b> <i>короткий военный или дорожный плащ</i>: sumere saga <i>или</i> ire ad saga <i>C</i> готовиться к войне, взяться за оружие; esse in sagis <i>C</i> быть вооружённым;\n<b>2)</b> <i>тёплый плащ из грубой шерсти</i> (<i>носившийся пастухами и пр.</i>) <i>PM</i>."],
  ["Saguntinus", "<big>Saguntinus</big>\n\n<b>I Saguntinus, a, um</b> [Saguntum]\nсагунтский <i>L</i>.\n\n<b>II Saguntinus, i</b> <i>m</i>\nжитель Сагунта <i>L</i>."],
  ["Saguntum", "<big>Saguntum</big>\n\n<b>Saguntum, i</b> <i>n</i> <i>и</i> <b>Saguntus (-os), i</b> <i>f</i>\nСагунт, <i>город седетанов в Hispania Tarraconensis, к сев. от Валенсии</i> (<i>ныне</i> Murviedro); <i>в 218 г. до н. э. взят и разрушен Ганнибалом, что послужило поводом ко II Пунической войне</i> <i>L, PM, St, Lcn etc.</i>"],
  ["sagus", "<big>sagus</big>\n\n<b>I sagus, a, um</b> <i>adj.</i> [sagio]\nпредсказывающий, пророческий, вещий (avis <i>St</i>).\n\n<b>II sagus, i</b> <i>m</i> <i>Enn, Vr</i>\n= sagum."],
  ["Sais", "<big>Sais</big>\n\n<b>Sais, is</b> <i>f</i>\nСаис, <i>древняя столица Нижнего Египта, к сев. от Навкратиса, с храмом богини Нейт</i> <i>PM, Mela</i>."],
  ["Saitae", "<big>Saitae</big>\n\n<b>Saitae, arum</b> <i>m</i>\nжители города Sais <i>C</i>."],
  ["sajo", "<big>sajo</big>\n\n<b>sajo, onis</b> <i>m</i>\nсудейский рассыльный <i>Eccl</i>."],
  ["sal", "<big>sal</big>\n\n<b>sal, salis</b> <i>m</i> (<i>в sg.</i> <i>изредка</i> <i>Vr, Lcr n</i>)\n<b>1)</b> соль (s. fossicius <i>или</i> fossilis <i>Vr</i>; s. marinus <i>Vr</i>); <i>pl.</i> крупинки соли, соль <i>Vr, Col</i>: addito grano salis <i>PM</i> с прибавлением крупинки <i>сопя, впоследствии — в форме</i> cum grano salis <i>погов.</i> со щепоткой соли, <i>т. е.</i> с оттенком шутливости, не совсем всерьёз;\n<b>2)</b> (<i>тж.</i> salis aequora <i>Cld</i>) морская вода, море (s. Tyrrhenus <i>V</i>; campi salis <i>V</i>);\n<b>3)</b> острота, остроумие (multi sales <i>Ap</i>); тонкая шутка, юмор (urbani sales <i>C</i>): s. niger <i>H</i> едкая насмешка, сарказм; sales suffusi felle <i>O</i> жёлчные насмешки;\n<b>4)</b> рассудок, ум (salem habere <i>Ter</i>);\n<b>5)</b> изящество, тонкий вкус (plus salis quam sumptus habere <i>Nep</i>)."],
  ["Salacia", "<big>Salacia</big>\n\n<b>Salacia, ae</b> <i>f</i>\nСалакия, <i>богиня бурного моря, мать Тритона <i>Vr, C</i>; перен.</i> море <i>Pac</i>."],
  ["salacitas", "<big>salacitas</big>\n\n<b>salacitas, atis</b> <i>f</i> [salax]\nпохотливость <i>Col, PM, Tert</i>."],
  ["salaco", "<big>salaco</big>\n\n<b>salaco, onis</b> <i>m</i> (<i>греч.</i>)\nхвастун <i>C</i>."],
  ["Salambo", "<big>Salambo</big>\n\n<b>Salambo, onis</b> <i>f</i>\nСаламбона, <i>сирийско-вавилонская богиня, прибл. соотв. римской Венере</i> <i>Lampr</i>."],
  ["Salamina", "<big>Salamina</big>\n\n<b>Salamina, ae</b> <i>f</i> <i>VM, Just</i>\n= Salamis."],
  ["Salaminiacus", "<big>Salaminiacus</big>\n\n<b>Salaminiacus, a, um</b> <i>Lcn, Sil</i>\n= Salaminius I."],
  ["Salaminius", "<big>Salaminius</big>\n\n<b>I Salaminius, a, um</b> [Salamis]\nсаламинский <i>C, Nep, H</i>.\n\n<b>II Salaminius, i</b> <i>m</i>\nжитель Саламина <i>C, PM</i>."],
  ["Salamis", "<big>Salamis</big>\n\n<b>Salamis, inis</b> (<i>acc.</i> ina) <i>f</i>\nСаламин:\n<b>1)</b> <i>о-в и город в Саронском заливе, между Аттикой и Арголидой, родина Эакидов (в том числе Эанта Теламонида); здесь в сентябре 480 г. до н. э. греки под командованием Фемистокла одержали морскую победу над персами</i> <i>C, Nep, H, VF etc.</i>;\n<b>2)</b> <i>город на вост. побережье Кипра, построенный, по преданию, Тевкром Теламонидом</i> <i>C, H, O, VP</i>."],
  ["Salapia", "<big>Salapia</big>\n\n<b>Salapia, ae</b> <i>f</i>\nСалапия, <i>древний город на вост. побережье Апулии, к сев. от города Канны</i> <i>L</i>."],
  ["Salapini", "<big>Salapini</big>\n\n<b>Salapini</b> <i>и</i> <b>Salapitani, orum</b> <i>m</i>\nжители города Salapia <i>C, L</i>."],
  ["salaputtium", "<big>salaputtium</big>\n\n<b>salaputtium (salaputium), i</b> <i>n</i> <i>презр.</i>\nкарапузик, человечишко <i>Ctl</i>."],
  ["salar", "<big>salar</big>\n\n<b>salar, aris</b> <i>m</i>\nфорель <i>Aus, Sid</i>."],
  ["Salaria", "<big>Salaria</big>\n\n<b>Salaria (via)</b> <i>f</i>\nСоляная дорога, <i>ведшая от римск. Porta Collina к городу Reate</i> <i>C, PM</i>."],
  ["salariarius", "<big>salariarius</big>\n\n<b>salariarius, i</b> <i>m</i>\nплатный служащий, наёмник <i>Dig</i>."],
  ["salarium", "<big>salarium</big>\n\n<b>salarium, i</b> <i>n</i> [salarius]\n<b>1)</b> соляной паёк (<i>выдававшийся военнослужащим и гражданским чиновникам</i>) <i>PM</i>;\n<b>2)</b> оклад, жалованье, содержание (tribuni militaris <i>PM</i>): salaria annua <i>Su</i> годовой оклад (<i>менее состоятельных сенаторов</i>) || гонорар (<i>врачей и пр.</i>) <i>Sen, Lampr, Dig</i>; суточные деньги <i>PJ, T, Su</i>."],
  ["salarius", "<big>salarius</big>\n\n<b>I salarius, a, um</b> [sal]\nсоляной: annona salaria <i>L</i> годовой доход от продажи соли, соляные поступления.\n\n<b>II salarius, i</b> <i>m</i>\nторговец солёной рыбой <i>M</i>."],
  ["Salassi", "<big>Salassi</big>\n\n<b>Salassi, orum</b> <i>m</i>\nсалассы, <i>воинственное племя в Gallia Transpadana, в долине Дурия, с главн. городом Augusta Praetoria</i> (<i>ныне</i> Аоста) <i>L, Su</i>."],
  ["salax", "<big>salax</big>\n\n<b>salax, acis</b> <i>adj.</i> [salio II, 4]\n<b>1)</b> похотливый (aries <i>O</i>; <i>sc.</i> senex <i>Pt</i>): s. deus <i>Priap</i> = Priapus;\n<b>2)</b> возбуждающий похоть (herba <i>O, M</i>)."],
  ["sale", "<big>sale</big>\n\n<b>sale, is</b> <i>n</i> <i>Enn, Vr</i>\n= sal."],
  ["salebra", "<big>salebra</big>\n\n<b>salebra, ae</b> <i>f</i> (<i>преим.</i> <i>pl.</i>) [salio II]\n<b>1)</b> бугорок, кочка, неровность <i>H, Prp etc.</i>: salebrae spiritus <i>VM</i> неровное (затруднённое) дыхание;\n<b>2)</b> трудность, затруднение, препятствие (in tantas salebras incidere <i>C</i>; haerere in salebra <i>C</i>);\n<b>3)</b> шероховатость, запинка: oratio sine salebris <i>C</i> плавная речь; s. tristitiae <i>VM</i> облако грусти."],
  ["salebratus", "<big>salebratus</big>\n\n<b>salebratus, a, um</b> <i>Sid</i>\n= salebrosus."],
  ["salebritas", "<big>salebritas</big>\n\n<b>salebritas, atis</b> <i>f</i> [salebra]\nшероховатость, бугристость, неровность <i>Ap</i>."],
  ["salebrosus", "<big>salebrosus</big>\n\n<b>salebrosus, a, um</b> [salebra]\nухабистый, бугристый, неровный (semita <i>Ap</i>; saxa <i>O</i>); неплавный, запинающийся (oratio <i>Q</i>)."],
  ["Salejus Bassus", "<big>Salejus Bassus</big>\n\n<b>Salejus Bassus</b> <i>m</i>\nСалей Басе, <i>римск, эпический поэт времён Веспасиана</i> <i>J, Q</i>."],
  ["Salent-", "<big>Salent-</big>\n\n<b>Salent-</b> <i>v. l.</i>\n= Salient-."],
  ["Salernitanus", "<big>Salernitanus</big>\n\n<b>Salernitanus, a, um</b> [Salernum]\nсалернский <i>PM, VM</i>."],
  ["Salernum", "<big>Salernum</big>\n\n<b>Salernum, i</b> <i>n</i> (<i>тж.</i> Salerni castrum <i>L</i>)\nСалерн, <i>древний город Кампании в глубине Пестанского залива, римск. колония с 194 г. до н. э.</i> (<i>ныне</i> Салерно) <i>H, PM</i>."],
  ["sales", "<big>sales</big>\n\n<b>sales</b> <i>pl. к</i>\nsal."],
  ["salgama", "<big>salgama</big>\n\n<b>salgama, orum</b> <i>n</i>\nмаринованные овощи <i>или</i> фрукты, соленья <i>Col</i>."],
  ["salgamarius", "<big>salgamarius</big>\n\n<b>salgamarius, i</b> <i>m</i> [salgama]\nторговец соленьями <i>Col</i>."],
  ["salgamum", "<big>salgamum</big>\n\n<b>salgamum, i</b> <i>n</i>\nпредметы питания <i>CJ, CTh</i>."],
  ["saliaris", "<big>saliaris</big>\n\n<b>I saliaris, e</b> [salio II]\nпрыгающий, подпрыгивающий, пляшущий (insulae saliares dictae quoniam ad ictus pedum moventur <i>PM</i>).\n\n<b>II Saliaris, e</b> [Salii I]\nсалийский (carmen <i>H</i>; sacra <i>Macr</i>); Saliares dapes <i>H</i> пышные пиры; Saliarem in modum epulari <i>C</i> устраивать роскошные трапезы; saltus S. <i>Sen</i> подпрыгивающая походка."],
  ["saliatus", "<big>saliatus</big>\n\n<b>saliatus, us</b> <i>m</i> [Salii 1]\nсалиат, <i>сан жреца-салия</i> <i>C</i>."],
  ["salictarius", "<big>salictarius</big>\n\n<b>I salictarius, a, um</b> [salictum]\nивовый: lupus s. <i>PM</i> дикий хмель, растущий среди ив.\n\n<b>II salictarius, i</b> <i>m</i>\nсторож ивовых насаждений <i>Cato</i>."],
  ["salictetum", "<big>salictetum</big>\n\n<b>salictetum, i</b> <i>n</i> <i>Dig</i>\n= salictum."],
  ["salictum", "<big>salictum</big>\n\n<b>salictum, i</b> <i>n</i> [<i>из</i> *saliceum <i>от</i> salix]\nместо, заросшее ивами, ивняк <i>Cato, Pl, C etc.</i>"],
  ["salientes", "<big>salientes</big>\n\n<b>salientes, ium</b> <i>m</i> [salio II]\n(<i>sc.</i> fontes <i>или</i> aquae) ключи, фонтаны <i>C, PM, Vtr etc.</i>"],
  ["salifodina", "<big>salifodina</big>\n\n<b>sali–fodina, ae</b> <i>f</i>\nсоляная копь <i>Vtr</i>."],
  ["saligneus", "<big>saligneus</big>\n\n<b>saligneus, a, um</b> <i>Cato, Col</i>\n= salignus."],
  ["salignus", "<big>salignus</big>\n\n<b>salignus, a, um</b> [salix]\nивовый (lectus <i>O</i>; fustis <i>H</i>)."],
  ["Salii", "<big>Salii</big>\n\n<b>I Salii, orum</b> <i>m</i> [salio II]\nсалии, «плясуны», «прыгуны», <i>две древних жреческих коллегии, из 12 патрициев каждая, посвященные Марсу (Mars Gradivus), хранительницы священных щитов (ancilia); жрецы старшей коллегии назывались</i> S. Palatini, <i>а младшей</i> — S. Agonales <i>или</i> Collini; <i>во время праздников в честь Марса (1, 15 и 19 марта) они исполняли военные пляски и древние песнопения</i> (axamenta) <i>Vr, L, H, C, V etc.</i>\n\n<b>II Salii, orum</b> <i>m</i>\nсалические (приморские) франки, <i>одно из зап.-германских племён</i> <i>Amm, Cld</i>."],
  ["salillum", "<big>salillum</big>\n\n<b>salillum, i</b> <i>n</i> [<i>demin. к</i> salinum]\nмаленькая солонка <i>Ctl</i>."],
  ["salinae", "<big>salinae</big>\n\n<b>I salinae, arum</b> <i>f</i> [salinus]\n<b>1)</b> соляные копи <i>C, Cs etc.</i>; солеварни, градирни (<i>где выпаривалась притекавшая по каналу морская вода</i>) <i>L</i>;\n<b>2)</b> место в Риме, где находились соляные амбары <i>L</i>;\n<b>3)</b> <i>перен.</i> остроты, шутки <i>C</i>.\n\n<b>II Salinae, arum</b> <i>f</i>\n<b>1)</b> <i>район Рима у Porta Trigemina</i> <i>L, Frontin, Sol</i>;\n<b>2)</b> <i>солеварни у Остии</i> (<i>близ Рима</i>) <i>L</i>."],
  ["salinator", "<big>salinator</big>\n\n<b>I salinator, oris</b> <i>m</i> [salinae]\nсолевар <i>или</i> солеторговец <i>Eccl</i>: s. aerarius <i>Cato</i> откупщик соляных промыслов.\n\n<b>II Salinator, oris</b> <i>m</i>\nСалинатор, <i>cognomen в роде Ливиев</i> <i>C</i>."],
  ["salinum", "<big>salinum</big>\n\n<b>salinum, i</b> <i>n</i> [salinus]\n(<i>sc.</i> vas) солонка <i>Pl, H</i>,"],
  ["salinus", "<big>salinus</big>\n\n<b>salinus, a, um</b> [sal]\nсоляной (<i>sc.</i> fodinae <i>C etc.</i>)."],
  ["salio", "<big>salio</big>\n\n<b>I salio, ii, itum, ire</b> [sal]\nсолить (pisces <i>CC</i>; pernas <i>Cato, Vr</i>).\n\n<b>II salio, salui (ii), saltum, ire</b>\n<b>1)</b> прыгать, скакать (passeres saliunt <i>PM</i>); спрыгивать (de muro <i>L</i>; in aquas <i>O</i>): sal (<i>или</i> mica salis) saliens <i>Tib, H</i> подпрыгивающая (на огне) соль (<i>благоприятная примета</i>);\n<b>2)</b> биться, стучать (cor salit <i>Pers</i>); пульсировать (vena Salit <i>O</i>); подёргиваться (supercilium salit <i>Pl</i>);\n<b>3)</b> бить ключом (aqua saliens <i>PJ</i>);\n<b>4)</b> (<i>о животных</i>) покрывать (ovis salitur <i>O</i>)."],
  ["salipotens", "<big>salipotens</big>\n\n<b>salipotens, entis</b> <i>adj.</i> [salum + potens]\nповелевающий морем (Neptunus <i>Pl</i>)."],
  ["Salisubsili", "<big>Salisubsili</big>\n\n<b>Salisubsili, orum</b> <i>m</i> [Salii + subsilio]\nпляшущие салии <i>Ctl</i>."],
  ["salitio", "<big>salitio</big>\n\n<b>salitio, onis</b> <i>f</i> [sallo II]\nвспрыгивание (на коня) <i>Veg</i>."],
  ["salito", "<big>salito</big>\n\n<b>salito, —, —, are</b> [<i>intens. к</i> salio II]\nпрыгать, подпрыгивать, скакать <i>Vr</i>."],
  ["salitura", "<big>salitura</big>\n\n<b>salitura, ae</b> <i>f</i> [salio I]\nзасолка, соление (musti <i>Col</i>)."],
  ["salitus", "<big>salitus</big>\n\n<b>salitus, a, um</b> <i>part. pf. к</i>\nsalio I."],
  ["saliunca", "<big>saliunca</big>\n\n<b>saliunca, ae</b> <i>f</i> <i>бот.</i>\nкельтский нард (<i>Valeriana Celtica</i>, L.) <i>V, PM</i>."],
  ["saliva", "<big>saliva</big>\n\n<b>saliva, ae</b> <i>f</i>\n<b>1)</b> (<i>тж.</i> <i>pl.</i>) слюна <i>Ctl, PM, Sen etc.</i>: salivam ciere <i>или</i> facere <i>PM</i> вызывать слюноотделение;\n<b>2)</b> желание, влечение: salivam alicui movere <i>Sen</i> возбуждать у кого-л. желание; s. mercurialis <i>Pers</i> жажда наживы;\n<b>3)</b> вкус (meri <i>Prp</i>; turdarum <i>Pers</i>): quicquid ad salivam facit <i>Pt</i> всё приятное на вкус;\n<b>4)</b> тягучая жидкость, слизь (cochlearum <i>PM</i>): lacrimationum salivae <i>PM</i> = lacrimae; siderum s. <i>PM</i> = mel."],
  ["salivarius", "<big>salivarius</big>\n\n<b>salivarius, a, um</b> [saliva]\nслюнообразный, тягучий, слизистый (lentor <i>PM</i>)."],
  ["salivatio", "<big>salivatio</big>\n\n<b>salivatio, onis</b> <i>f</i> [salivo]\nслюноотделение <i>CA</i>."],
  ["salivatum", "<big>salivatum</big>\n\n<b>salivatum, i</b> <i>n</i> [saliva]\nслюногонное средство <i>Col, PM</i>."],
  ["salivo", "<big>salivo</big>\n\n<b>salivo, —, —, are</b> [saliva]\n<b>1)</b> выделять в виде слизи (purpurae lentorem cujusdam cerae salivant <i>PM</i>);\n<b>2)</b> вызывать слюнотечение, лечить усиленным слюнотечением (aegrotum pecus <i>Col</i>)."],
  ["salivosus", "<big>salivosus</big>\n\n<b>salivosus, a, um</b> [saliva]\n<b>1)</b> полный слюны, слюнявый (labia <i>Ap</i>);\n<b>2)</b> страдающий слюнотечением (aegrotans <i>CA</i>);\n<b>3)</b> слюнообразный, слизистый, тягучий (umor <i>PM</i>)."],
  ["salix", "<big>salix</big>\n\n<b>salix, icis</b> <i>f</i>\n<b>1)</b> ива <i>или</i> верба <i>Pl, V, Col</i>;\n<b>2)</b> ивовый прут <i>Eccl</i>."],
  ["Sallentini", "<big>Sallentini</big>\n\n<b>Sallentini, orum</b> <i>m</i>\nсаллентины, <i>племя в южн. Калабрии, к юго-вост. от Тарента</i> <i>C, L etc.</i>"],
  ["Sallentinus", "<big>Sallentinus</big>\n\n<b>Sallentinus, a, um</b> [Sallentini]\nсаллентинский (campi <i>V</i>; cohortes <i>Sil</i>)."],
  ["sallio", "<big>sallio</big>\n\n<b>sallio</b> <i>Cato</i>\n= salio I."],
  ["sallit-", "<big>sallit-</big>\n\n<b>sallit-</b> <i>v. l.</i>\n= salit-."],
  ["sallo", "<big>sallo</big>\n\n<b>sallo, —, —, ere</b> <i>Vr, Sl</i>\n= salio I."],
  ["Sallustianus", "<big>Sallustianus</big>\n\n<b>I Sallustianus, a, um</b> [Sallustius]\nсаллюстиев (brevitas <i>Q</i>).\n\n<b>II Sallustianus, i</b> <i>m</i>\nсаллюстианец, <i>поклонник Т. Саллюстия Криспа</i> <i>Sen</i>."],
  ["Sallustius", "<big>Sallustius</big>\n\n<b>Sallustius, a, um</b>\nСаллюстий, <i>римск. nomen; наиболее известны</i>:\n<b>1)</b> C. S. Crispus, <i>родом из Амитерна, историк, автор «Conjuratio (Bellum) Catilinaex, Bellum Jugurtinum» и «Historiae» (86-34 гг. до н. э.</i>) <i>T, Q etc.</i>;\n<b>2)</b> S. Crispus, <i>внучатный племянник и приёмный сын предыдущего, славившийся своим богатством и щедростью, близкий к Августу и Тиберию</i> (<i>умер в 20 г. н. э.</i>) <i>H, T</i>;\n<b>3)</b> Cn. S., <i>друг Цицерона, отправившийся с ним в изгнание</i> <i>C</i>."],
  ["Salluvii", "<big>Salluvii</big>\n\n<b>Salluvii, orum</b> <i>и</i> <b>um</b> <i>m</i>\nсалувии, <i>лигурийско-кельтское племя в юго-вост. Галлии вокруг Массилии</i> (<i>Между рекой Родан и приморскими Альпами</i>) <i>L, PM</i>."],
  ["Salmacides", "<big>Salmacides</big>\n\n<b>Salmacidēs, ae</b> <i>m</i> [Salmacis]\nсыи Салмакиды, <i>т. е.</i> изнеженный человек <i>Enn</i>."],
  ["salmacidus", "<big>salmacidus</big>\n\n<b>salmacidus, a, um</b> [salma <i>из</i> salgama + acidus]\nсолёно-кислый (aquae <i>PM, Fl</i>)."],
  ["Salmacis", "<big>Salmacis</big>\n\n<b>Salmacis, idis</b> <i>f</i>\nСалмакида:\n<b>1)</b> <i>источник у Галикарнасса (Кария), вода которого, по преданию, обладала свойством изнеживать того, кто её пил</i> <i>O, Vtr, St</i>;\n<b>2)</b> <i>нимфа этого источника</i> (<i>voc.</i> Salmaci) <i>O</i>."],
  ["salmo", "<big>salmo</big>\n\n<b>salmo, ōnis</b> <i>m</i>\nлосось <i>PM, Aus</i>."],
  ["Salmona", "<big>Salmona</big>\n\n<b>Salmōna, ae</b> <i>f</i>\nСальмона, <i>приток реки Mosella</i> (<i>ныне</i> Salm) <i>Aus</i>."],
  ["Salmoneus", "<big>Salmoneus</big>\n\n<b>Salmōneus, eī</b> <i>и</i> <b>eos</b> <i>m</i>\nСалмоней, <i>сын Эола, брат Сизифа, отец Тиро, царь Фессалии и Элиды, сражённый Юпитером ей попытку сравняться с ним в метании молний</i> <i>V</i>."],
  ["Salmonis", "<big>Salmonis</big>\n\n<b>Salmōnis, idis</b> <i>f</i>\nдочь Салмонея, <i>т. е. Тиро</i> <i>Prp, O</i>."],
  ["Salo", "<big>Salo</big>\n\n<b>Salo, ōnis</b> <i>m</i>\nСалон, <i>река в Hispania Tarraconensis, близ города Bilbilis</i> <i>M</i>."],
  ["Salomon", "<big>Salomon</big>\n\n<b>Salomōn, ōnis</b> <i>m</i>\nСоломон, <i>сын Давида, 3-й царь Иудеи</i> <i>Vlg, Eccl</i>."],
  ["Salomoniacus", "<big>Salomoniacus</big>\n\n<b>Salomōniacus</b> <i>и</i> <b>Salomōnius, a, um</b>\nСоломонов <i>Sid, Eccl</i>."],
  ["Salonae", "<big>Salonae</big>\n\n<b>Salōnae, ārum</b> <i>и</i> <b>Salōna, ae</b> <i>f</i>\nСалоны, <i>главн. город Далматии</i> <i>PM, Mela, Cs</i>."],
  ["Saloninianus", "<big>Saloninianus</big>\n\n<b>Salōnīniānus</b> <i>и</i> <b>Salōnītānus, a, um</b> [Salona]\nсалонский <i>Treb, Aug</i>."],
  ["salpa", "<big>salpa</big>\n\n<b>salpa, ae</b> <i>f</i> (<i>греч.</i>)\n<i>предпол.</i> вяленая Треска <i>PM, O</i>."],
  ["Salpinas", "<big>Salpinas</big>\n\n<b>I Salpinas, atis</b> [Salpinum]\nсальпинский <i>L</i>.\n\n<b>II Salpinas, atis</b> <i>m</i>\nЖитель города Salpinum <i>L</i>."],
  ["Salpinum", "<big>Salpinum</big>\n\n<b>Salpinum, i</b> <i>n</i>\nСальпин, <i>город в Этрурии, к сев. от lacus Vohiniensis</i> <i>L</i>."],
  ["salpista", "<big>salpista</big>\n\n<b>salpista, ae</b> <i>m</i> (<i>греч.</i>)\nтрубач <i>Vop</i>."],
  ["salpuga", "<big>salpuga</big>\n\n<b>salpuga, ae</b> <i>f</i> <i>PM</i>\n= solipuga."],
  ["salsa", "<big>salsa</big>\n\n<b>salsa, orum</b> <i>n</i> [salsus]\n<b>1)</b> солёные кушанья <i>PM</i>;\n<b>2)</b> шутки, юмор (Qraecofum <i>C</i>)."],
  ["salsamen", "<big>salsamen</big>\n\n<b>salsamen, inis</b> <i>n</i> <i>Eccl</i>\n= salsamefitum."],
  ["salsamentarius", "<big>salsamentarius</big>\n\n<b>I salsamentarius, a, um</b> [salsamentum]\nпредназначенный для хранения солений (vasa <i>Col</i>).\n\n<b>II salsamentarius, i</b> <i>m</i>\nпродавец солёной рыбы <i>rhH, Su</i>."],
  ["salsamentum", "<big>salsamentum</big>\n\n<b>salsamentum, i</b> <i>n</i> [salsus]\n<b>1)</b> рыбный рассол <i>C</i>;\n<b>2)</b> солёная <i>или</i> маринованная рыба <i>Ter, Cato, Vr, Col</i>."],
  ["salse", "<big>salse</big>\n\n<b>salse</b> [salsus]\nсолоно, <i>перен.</i> остро, остроумно ((licere <i>C, Q</i>)."],
  ["salsedo", "<big>salsedo</big>\n\n<b>salsedo, inis</b> <i>f</i> [salsus]\nсолёность, солёный вкус <i>Pall</i>."],
  ["salsilago", "<big>salsilago</big>\n\n<b>salsilago, inis</b> <i>f</i> [Salsus]\n<b>1)</b> солёность <i>PM</i>;\n<b>2)</b> соляной раствор <i>PM</i>."],
  ["salsipotens", "<big>salsipotens</big>\n\n<b>salsipotens, entis</b> <i>adj.</i> [salsus + potens]\nповелевающий солёной стихией, <i>т. е.</i> морями (Neptunus <i>Pt</i>)."],
  ["salsitas", "<big>salsitas</big>\n\n<b>salsitas, itis</b> <i>f</i>\n<b>1)</b> солёность (liquoris <i>JV</i>);\n<b>2)</b> насмешливость, язвительность <i>Hier</i>."],
  ["salsitudo", "<big>salsitudo</big>\n\n<b>salsitudo, inis</b> <i>f</i> [salsus]\n<b>1)</b> солёность <i>Vtr</i>;\n<b>2)</b> скопление соли (salsitudines corporis <i>PM</i>)."],
  ["salsiusculus", "<big>salsiusculus</big>\n\n<b>salsiusculus, a, um</b>\nсолоноватый <i>Aug</i>."],
  ["salso", "<big>salso</big>\n\n<b>salso, avi, —, are</b> [salsus]\nсолить (aliquid <i>Aug</i>)."],
  ["salsugo", "<big>salsugo</big>\n\n<b>salsugo, inis</b> <i>f</i> <i>PM, Vtr</i>\n= Salsilago."],
  ["salsura", "<big>salsura</big>\n\n<b>salsura, ae</b> <i>f</i> [salsus]\n<b>1)</b> соление, маринование <i>Vr, Col</i>;\n<b>2)</b> соляной раствор, рассол: pabula salsurae <i>Col</i> маринованная рыба;\n<b>3)</b> дурное настроение (meae animae s. evenit <i>Pl</i>);\n<b>4)</b> остроумие (S. Attica <i>Eccl</i>)."],
  ["salsus", "<big>salsus</big>\n\n<b>salsus, a, um</b> [salio I]\n<b>1)</b> солёный, содержащий много соли (aqua <i>bAl etc.</i>; sanguis <i>Enn ap. Macr, Poeta ap. C</i>): vada salsa <i>V</i> = mare || посоленный (caseus <i>Col</i>); пересоленный (hoc salsum est <i>Ter</i>);\n<b>2)</b> острый, едкий, разъедающий (robigo <i>V</i>);\n<b>3)</b> остроумный, юмористический, сатирический (ridicula et salsa <i>C</i>): male s. <i>H</i> злой шутник; negotia salsa <i>C</i> забавные истории, смешные рассказы; salsiores sales <i>C</i> более остроумные шутки; aliquem ex insulso salsum facere <i>погов. Pl</i> научить кого-л. уму-разуму."],
  ["saltabundus", "<big>saltabundus</big>\n\n<b>saltābundus, a, um</b> [salto]\nтанцующий, пляшущий <i>AG</i>."],
  ["saltatim", "<big>saltatim</big>\n\n<b>saltātim</b> [saltus 1]\nпрыгая, вприпрыжку (currere <i>AG</i>)."],
  ["saltatio", "<big>saltatio</big>\n\n<b>saltātio, ōnis</b> <i>f</i> [salto]\nтанцы, пляска (<i>с пантомимой</i>) <i>Pl, C, Q, Su</i>."],
  ["saltatiuncula", "<big>saltatiuncula</big>\n\n<b>saltātiuncula, ae</b> <i>f</i> [<i>demin. к</i> saltatio]\nнебольшой танец <i>Vop</i>."],
  ["saltator", "<big>saltator</big>\n\n<b>saltātor, ōris</b> <i>m</i> [salto]\nтанцовщик, танцор, плясун <i>C, Q</i>, <i>Su</i>."],
  ["saltatorie", "<big>saltatorie</big>\n\n<b>saltātōriē</b> [saltatorius]\nтанцуя, приплясывая, в пляске (procurrere <i>Ap</i>)."],
  ["saltatorius", "<big>saltatorius</big>\n\n<b>saltātōrius, a, um</b> [saltator]\nтанцевальный, плясовой: orbis s. <i>C</i> хоровод; ludus s. <i>Macr</i> школа танцев."],
  ["saltatricula", "<big>saltatricula</big>\n\n<b>saltātricula, ae</b> <i>f</i> [<i>demin. к</i> saltatrix]\nмолоденькая танцовщица <i>AG</i>."],
  ["saltatrix", "<big>saltatrix</big>\n\n<b>saltātrīx, īcis</b> <i>f</i> [saltator]\nтанцовщица, плясунья <i>C, PM etc.</i>"],
  ["saltatus", "<big>saltatus</big>\n\n<b>saltātus, ūs</b> <i>m</i> <i>L, O</i>\n= saltatio."],
  ["saltem", "<big>saltem</big>\n\n<b>saltem</b> <i>adv.</i>\nпо крайней мере, во всяком случае: eripere aliquid aut minuere s. <i>C</i> устранить или, по крайней мере, ослабить что-л.; s. nobis parce <i>Pt</i> хоть нас-то Пощади; qui ego sum s., si non sum Sosia? <i>Pl</i> кто же я, наконец, если нё Сосия?; non s. <i>L, Q etc.</i> даже не; iter neque impervium neque s. durum <i>Q</i> путь не непроходимый и даже не трудный; vix s. <i>Q</i> лишь с трудом; Si tenet hoc nos <i>H</i> ведь это, в конце концов, занимательно."],
  ["salti", "<big>salti</big>\n\n<b>salti</b> <i>Acc арх. gen. sg. к</i>\nsaltus II."],
  ["salticus", "<big>salticus</big>\n\n<b>salticus, a, um</b> [saltus I]\nтанцующий (puella <i>Tert</i>)."],
  ["saltim", "<big>saltim</big>\n\n<b>saltim</b> <i>adv.</i> <i>J v. l.</i>\n= saltem."],
  ["saltito", "<big>saltito</big>\n\n<b>saltito, —, —, are</b> [<i>intens. к</i> salto]\nтанцевать, плясать <i>Vop, Macr</i>."],
  ["salto", "<big>salto</big>\n\n<b>salto, āvī, ātum, āre</b> [<i>intens. к</i> salio II]\n<b>1)</b> танцевать, плясать (<i>с пантомимой</i>) <i>C, Ap etc.</i>;\n<b>2)</b> <i>перен.</i> подпрыгивать, <i>т. е.</i> выражаться отрывисто, говорить обрывками фраз (orator saltat <i>C</i>);\n<b>3)</b> пантомимически представлять, изображать (puellam <i>O</i>; tragoediam <i>Su</i>); жестами выражать (carmina pleno theatro saltantur <i>O</i>; saltata poemata <i>O</i>)."],
  ["saltuarius", "<big>saltuarius</big>\n\n<b>saltuārius, ī</b> <i>m</i> [saltus II]\nлесник <i>или</i> пастбищный сторож <i>Pt, Dig</i>."],
  ["saltuatim", "<big>saltuatim</big>\n\n<b>saltuātim</b> [saltus I]\n<b>1)</b> вприпрыжку (currere <i>AG</i>);\n<b>2)</b> скачкообразно, отрывочно (scribere <i>AG</i>)."],
  ["saltuensis", "<big>saltuensis</big>\n\n<b>saltuēnsis, e</b> [saltus II]\nсвязанный с лесными <i>или</i> пастбищными угодьями (fundi, coloni <i>CJ</i>)."],
  ["saltuosus", "<big>saltuosus</big>\n\n<b>saltuōsus, a, um</b> [saltus II]\nпокрытый лесами, лесистый (loca <i>Sl, L</i> <i>и</i> loci <i>T</i>)."],
  ["saltus", "<big>saltus</big>\n\n<b>I saltus, ūs</b> <i>m</i> [salto II]\nскачок, прыжок: saltum dare <i>O</i> подскочить, прыгнуть; saltu se dare спрыгнуть (in fluvium <i>V</i>).\n\n<b>II saltus, ūs</b> <i>m</i>\n<b>1)</b> узкий проход (saltus praesidiis intercludere <i>L</i>); <i>перен.</i> трудное положение (ex saltu damni elicere — <i>v. l.</i> educere — aliquem foras <i>Pl</i>); ущелье (Thermepylarum <i>L</i>);\n<b>2)</b> лесистое место (s. nemorum <i>V</i>); покрытые лесами горы (s. Pyrenaeus <i>Nep</i>; silvae saltusque <i>V</i>);\n<b>3)</b> (<i>тж.</i> s. pascuus <i>Dig</i>) горное пастбище, <i>тж.</i> лесной выгон (in saltibus pascere <i>V</i>);\n<b>4)</b> имение, ферма с пастбищами <i>Dig</i>;\n<b>5)</b> земельная мера = 4 centuriae = 800 jugera = 201,5 га <i>Vr</i>;\n<b>6)</b> <i>Pl</i> (<i>шутл.</i>) = vasculum muliebre."],
  ["saluber", "<big>saluber</big>\n\n<b>saluber, bra, bre</b> [salus]\n<b>1)</b> здоровый, полезный для здоровья, целительный, целебный (locus <i>Vr</i>; somnus <i>V</i>; vicuis <i>C</i>);\n<b>2)</b> полезный, спасительный (oculi domini res agro saluberrimae <i>Col</i>); назидательный, поучительный (consilium, sententia <i>C</i>);\n<b>3)</b> здоровый, крепкий (corpus <i>Sl, L</i>); <i>перен.</i> здоровый, Правильный, хороший (quidquid est salsum aut salubre in oratione <i>C</i>)."],
  ["salubris", "<big>salubris</big>\n\n<b>salubris, e</b> <i>C</i>\n= saluber."],
  ["salubritas", "<big>salubritas</big>\n\n<b>salubritas, atis</b> <i>f</i> [saluber]\n<b>1)</b> целебность, благотворность (s. et sanitas <i>C</i>): fons medicae salubritatis <i>PM</i> целебный источник;\n<b>2)</b> здоровье, здоровое состояние (corporum <i>T</i>); здоровый характер (loci <i>C</i>; caeli <i>PJ</i>)."],
  ["salubriter", "<big>salubriter</big>\n\n<b>salubriter</b> [saluber]\n<b>1)</b> здорово, с пользой для здоровья (refrigerari <i>C</i>);\n<b>2)</b> целесообразно, благополучно, разумно (bellum trahere <i>L</i>); выгодно (aliquid emere <i>PJ</i>)."],
  ["salum", "<big>salum</big>\n\n<b>salum, i</b> <i>n</i> (<i>греч.</i>)\n<b>1)</b> открытое море, рейд (navis in salo fluctuat <i>C</i>; in salo stare <i>или</i> esse <i>bAfr</i>);\n<b>2)</b> море (immensum <i>O</i>; altum <i>H</i>);\n<b>3)</b> морское волнение, качка (salo nausea que confectus <i>Cs</i>);\n<b>4)</b> беспокойство, тревоги, треволнения (mentis s. et cogitationum dissensio <i>Ap</i>)."],
  ["salus", "<big>salus</big>\n\n<b>I salus, utis</b> <i>f</i> [salvus]\n<b>1)</b> здоровье, здоровое состояние: ad salutem reducere <i>C</i> вылечить; salutem alicui imprecari <i>Ap</i> желать кому-л. здоровья (<i>при чихании</i>); quod cum salute ejus fiat <i>Ter</i> только бы ему это не повредило; salutis custos <i>QC</i> личный врач;\n<b>2)</b> благо, благополучие, благосостояние, благоденствие (civium <i>C</i>); augurium salutis <i>C</i> гадание, при котором у богов спрашивали, дозволено ли в данном случае молиться о благе народа;\n<b>3)</b> спасение, избавление, сохранение жизни (certare pro salute <i>Sl</i>): saluti esse alicui <i>C</i> служить к чьему-л. спасению; verum est, quod pro salute fit mendacium <i>PS</i> ложь во спасение становится правдой; fuga salutem petere <i>Nep</i> спасаться бегством; salutem afferre <i>или</i> ad salutem vocare <i>C</i> спасать; salutem alicui dare (reddere) <i>C</i> даровать кому-л. жизнь;\n<b>4)</b> спаситель (Lentulus s. nostrae vitae <i>C</i>);\n<b>5)</b> средство к спасению, возможность спасения (nullam salutem reperire <i>C</i>);\n<b>6)</b> привет, поклон: salutem alicui nuntiare (dare, dicere) <i>C etc.</i> приветствовать кого-л.; salute data (<i>или</i> accepta) redditaque <i>L</i> по обмене (обменявшись) приветствиями; alicui salutem dicere jubere <i>Pl</i> просить передать поклон кому-л.; Trajanus Plinio salutem (<i>sc.</i> dicit) <i>PJ</i> Траян шлёт привет Плинию; salutem dicere alicui rei <i>погов. C</i> распрощаться с чём-л., отказаться от чего-л.;\n<b>7)</b> <i>ласк.</i> радость (quid agis, mea s.? <i>Pl</i>).\n\n<b>II Salus, utis</b> <i>f</i>\n<i>римск. богиня здоровья и благополучия</i> (<i>храм её находился на одной из вершин Квиринала</i>) <i>Pl, Ter, C etc.</i>"],
  ["salutare", "<big>salutare</big>\n\n<b>salutare, is</b> <i>n</i> (<i>sc.</i> remedium)\nспасительное <i>или</i> целебное средство <i>C, Vlg, Eccl</i>."],
  ["salutaris", "<big>salutaris</big>\n\n<b>salutaris, e</b> [salus]\n<b>1)</b> полезный для здоровья, здоровый, целебный, целительный (herba <i>O, Sen</i>); ars s. <i>H</i> врачевание, медицина;\n<b>2)</b> полезный, спасительный, благотворный (lex <i>C</i>): littera s. <i>C</i> спасительная буква (<i>т. е.</i> A, <i>начальная буква слова «absolvo»</i>, <i>которую писали судьи, голосовавшие за оправдание, в отличие от</i> littera tristis, <i>т. е.</i> C = <i>«condemno»</i>); digitus s. <i>Su</i> указательный палец (<i>так как поднятием этого пальца зрители давали согласие на пощаду для побеждённого гладиатора</i>);\n<b>3)</b> невредимый: salutaribus — <i>v. l.</i> salvis — rebus tuis <i>C</i> при безукоризненном состоянии твоих дел."],
  ["Salutaris collis", "<big>Salutaris collis</big>\n\n<b>Salutaris collis</b> <i>m</i>\n<i>одна из четырёх вершин Квиринала, на которой находился храм богини Salus</i> <i>Vr</i>."],
  ["salutariter", "<big>salutariter</big>\n\n<b>salutariter</b> <i>C, VM</i>\n= salubriter."],
  ["salutatio", "<big>salutatio</big>\n\n<b>salutatio, onis</b> <i>f</i> [saluto]\n<b>1)</b> приветствование, приветствие, привет, поклон: salutationem reddere <i>T</i> отвечать на приветствие; salutationem more militari facere приветствовать по-военному, салютовать (armis demissis <i>bAfr</i>);\n<b>2)</b> посещение, визит (dare se salutationi amicorum <i>C</i>);\n<b>3)</b> посещение для засвидетельствования почтения, официальный визит (<i>клиентов патрону</i>) (salutationes matutinae <i>Sen</i>);\n<b>4)</b> толпа посетителей, визитёры (ubi s. defluxit, litteris me involvo <i>C</i>)."],
  ["salutator", "<big>salutator</big>\n\n<b>salutator, oris</b> <i>m</i> [saluto]\n<b>1)</b> приветствующий, кланяющийся <i>St</i>;\n<b>2)</b> являющийся с визитом, визитёр <i>Col, Su, J</i>."],
  ["salutatorium", "<big>salutatorium</big>\n\n<b>salutatorium, i</b> <i>n</i> (<i>sc.</i> conclave)\nзал для приёмов <i>Eccl</i>."],
  ["salutatorius", "<big>salutatorius</big>\n\n<b>salutatorius, a, um</b> [salutator]\n<b>1)</b> отведённый для приёма посетителей, приёмный (cubicula <i>PM</i>);\n<b>2)</b> <i>грам.</i> звательный (casus)."],
  ["salutatrix", "<big>salutatrix</big>\n\n<b>salutatrix, icis</b> <i>f</i> [salutator]\n<b>1)</b> приветствующая (pica <i>M</i>); приветственная, поздравительная (charta <i>M</i>);\n<b>2)</b> явившаяся с официальным визитом (turba <i>J</i>)."],
  ["salutifer", "<big>salutifer</big>\n\n<b>salutifer, fera, ferum</b> [salus + fero]\nдающий здоровье, целительный, целебный, спасительный (aquae <i>M</i>; potio <i>Ap</i>): puer s. <i>O</i> = Aesculapius; s. anguis <i>St</i> змея Эскулапия (<i>символ врачевания</i>)."],
  ["salutificator", "<big>salutificator</big>\n\n<b>saluti–ficator, oris</b> <i>m</i>\nизбавитель, спаситель <i>Tert</i>."],
  ["salutiger", "<big>salutiger</big>\n\n<b>I saluti–ger, gera, gerum</b>\n<b>1)</b> несущий спасение (Juppiter <i>Aus</i>);\n<b>2)</b> приветственный (libelli <i>Aus</i>).\n\n<b>II salutiger, geri</b> <i>m</i> [salus + gero]\nгонец с приветом <i>Ap</i>."],
  ["salutigerulus", "<big>salutigerulus</big>\n\n<b>salutigerulus, a, um</b> [salus + gerulus]\nприносящий привет, передающий поклон (pueri <i>Pl</i>)."],
  ["saluto", "<big>saluto</big>\n\n<b>saluto, avi, atum, are</b> [<i>intens. к поздн.</i> salvo]\n<b>1)</b> приветствовать, посылать привет (s. aliquem <i>C etc.</i>); желать здоровья (sternumentis s. <i>PM</i>);\n<b>2)</b> приветствовать, именовать, величать (aliquem dominum <i>Sen, J</i>);\n<b>3)</b> поздравлять <i>C</i>; свидетельствовать почтение, приходить с визитом (venire salutandi causa <i>C</i>);\n<b>4)</b> благоговейно поклоняться (s. deos <i>Pl, Ter, O, C</i>);\n<b>5)</b> здороваться <i>или</i> прощаться (s. aliquem <i>Pl, C, Su, St</i>)."],
  ["Saluvii", "<big>Saluvii</big>\n\n<b>Saluvii</b> <i>v. l.</i>\n= Salluvii."],
  ["salvatio", "<big>salvatio</big>\n\n<b>salvatio, onis</b> <i>f</i> [salvo]\nизбавление, спасение <i>Vlg, Eccl</i>."],
  ["salvator", "<big>salvator</big>\n\n<b>salvator, oris</b> <i>m</i>\nизбавитель, спаситель <i>Eccl</i>."],
  ["salve", "<big>salve</big>\n\n<b>I salve</b> (<i>pl.</i> salvete) [<i>imper. к</i> salveo]\n<b>1)</b> здравствуй, привет тебе <i>Pl, Ter, V, O etc.</i>;\n<b>2)</b> будь здоров, прощай (s. atque vale <i>Pl</i>).\n\n<b>II salve</b> <i>adv.</i> [salvus]\n<b>1)</b> в добром здравии: quam s. agit? <i>Ap</i> как он поживает?;\n<b>2)</b> в полном порядке: satin' (satisne) s. (<i>sc.</i> agis <i>или</i> agitur)? <i>Ter, L</i> всё ли в порядке?"],
  ["salveo", "<big>salveo</big>\n\n<b>salveo, —, —, ere</b> [salvus]\nбыть здоровым, хорошо чувствовать себя, здравствовать: aliquem s. jubere <i>Pl, C, L</i> посылать кому-л. привет, приветствовать кого-л. <i>или</i> <i>Pt</i> желать кому-л. здоровья (<i>при чихании</i>); mane s. sibi dicere <i>Su</i> желать друг другу доброго утра; multum salveto <i>Pl</i> от души приветствую тебя; salvebis a filio meo <i>C</i> мой сын кланяется тебе."],
  ["salvia", "<big>salvia</big>\n\n<b>salvia, ae</b> <i>f</i>\nшалфей <i>PM</i>."],
  ["salvificator", "<big>salvificator</big>\n\n<b>salvificator, oris</b> <i>m</i> <i>Tert</i>\n= salvator."],
  ["salvifico", "<big>salvifico</big>\n\n<b>salvi–fico, —, —, are</b>\nспасать, избавлять (aliquem <i>Vlg, Eccl</i>)."],
  ["salvo", "<big>salvo</big>\n\n<b>salvo, avi, atum, are</b> [salvus]\nспасать, избавлять (aliquid <i>и</i> aliquem <i>Veg, Vlg, Eccl</i>)."],
  ["salvus", "<big>salvus</big>\n\n<b>salvus, a, um</b>\nневредимый, целый, находящийся в полной сохранности, нетронутый (s. et incolumis <i>Cs</i>); невскрытый (epistula <i>C</i>); целомудренный, непорочный (Penelope <i>Prp</i>): me salvo <i>Pt</i> пока я жив; salvis legibus <i>C</i> не нарушая законов; salva (bona) conscientia <i>Sen</i> по совести; salva fide <i>C</i> не нарушая данного слова, с чистой совестью; salvis rebus <i>C</i> без имущественного ущерба, благополучно <i>или</i> при хороших обстоятельствах; salvo tuo sermone <i>Pt</i> не входя в оценку твоего рассказа; salva veneratione, salva reverentia alicujus <i>Ap</i> из благоговейного почтения к кому-л.; <i>в клятвах</i> ne sim s. (si) <i>или</i> s. esse nolo (si) <i>C</i> пропади я пропадом (если); sunt vestra salva? <i>Pt</i> у вас всё в порядке?; sic me salvum habeatis <i>Pt</i> клянусь вам своим здоровьем; satisne (satin') salvae (<i>sc.</i> res essent)? <i>L</i> всё ли в надлежащем порядке?"],
  ["Samaei", "<big>Samaei</big>\n\n<b>Samaei, orum</b> <i>m</i>\nжители Same <i>L</i>."],
  ["samara", "<big>samara</big>\n\n<b>samara, ae</b> <i>f</i> <i>v. l.</i>\n= samera."],
  ["Samaria", "<big>Samaria</big>\n\n<b>Samaria, ae</b> <i>f</i>\nСамария (Шомрон), <i>область Палестины</i> (<i>между Галилеей и Иудеей, с главн. городом того же названия</i>) <i>PM, Vlg</i>."],
  ["Samaritanus", "<big>Samaritanus</big>\n\n<b>Samaritanus</b> <i>и</i> <b>Samariticus, a, um</b> [Samaria]\nсамаритянский <i>Eccl</i>."],
  ["Samarites", "<big>Samarites</big>\n\n<b>Samarites, ae</b> <i>m</i>\nсамаритянин, житель Самарии <i>T, Vop, Vlg</i>."],
  ["Samaritida", "<big>Samaritida</big>\n\n<b>Samaritida, ae</b> <i>и</i> <b>Samaritis, idis</b> <i>f</i>\nсамаритянка <i>Eccl</i>."],
  ["Samarobriva", "<big>Samarobriva</big>\n\n<b>Samarobriva, ae</b> <i>f</i>\n(«мост через реку Самара») <i>главн. город амбианов в Gallia Belgica</i> (<i>ныне</i> Amiens) <i>Cs, C</i>."],
  ["sambuca", "<big>sambuca</big>\n\n<b>sambuca, ae</b> <i>f</i> (<i>греч.</i>)\n<b>1)</b> самбука, <i>струнный инструмент треугольной формы</i> (<i>род арфы</i>) <i>Pl, Prp, Spart, Macr</i>;\n<b>2)</b> род подъёмного моста (<i>употр. при штурме городских стен</i>) <i>Vtr, Veg</i>."],
  ["sambuceus", "<big>sambuceus</big>\n\n<b>sambuceus, a, um</b> [sambucus]\nбузиновый, из бузины (furca <i>PM</i>; baculum <i>AV</i>)."],
  ["sambucina", "<big>sambucina</big>\n\n<b>sambucina, ae</b> <i>f</i> [sambuca + cano]\nиграющая на самбуке, арфистка <i>Pl</i> (<i>v. l.</i>)."],
  ["sambucistria", "<big>sambucistria</big>\n\n<b>sambucistria, ae</b> <i>f</i> (<i>греч.</i>) <i>L, Eccl</i>\n= sambucina."],
  ["sambucus", "<big>sambucus</big>\n\n<b>sambucus, i</b> <i>f</i>\nбузина <i>PM, Col, Pall</i>."],
  ["Same", "<big>Same</big>\n\n<b>Same, es</b> (<i>у</i> <i>O</i> <b>Samos, i</b>) <i>f</i>\nСама, <i>о-в и город к зап. от Итаки, впоследствии</i> Cephallenia <i>V, O, L, Sil, Aus</i>."],
  ["samera", "<big>samera</big>\n\n<b>samera, ae</b> <i>f</i>\nсемя вяза <i>Col, PM</i>."],
  ["Samia", "<big>Samia</big>\n\n<b>I Samia, orum</b> <i>n</i> [Samius]\nсамосская керамика <i>rhH, PM</i>.\n\n<b>II Samia, ae</b> <i>f</i> (<i>sc.</i> placenta)\nсамосский пирог <i>Tert</i>."],
  ["samio", "<big>samio</big>\n\n<b>samio, (avi), atum, are</b> [Samius]\nобтачивать на оселке из самосского камня (loricas <i>Veg</i>); начищать до блеска, полировать (ferramenta samiata <i>Vop</i>)."],
  ["Samiolus", "<big>Samiolus</big>\n\n<b>Samiolus, a, um</b> [Samos]\nсамосский (poterion <i>Pl</i>)."],
  ["Samiramis", "<big>Samiramis</big>\n\n<b>Samiramis, idis</b> <i>f</i> <i>QC, Treb</i>\n= Semiramis."],
  ["Samius", "<big>Samius</big>\n\n<b>I Samius, a, um</b> [Samos]\nсамосский: S. (vir, senex) <i>O</i> = Pythagoras; Samia Juno <i>C</i> Юнона Самосская (<i>Самос был одним из мест культа этой богини</i>); Samia terra <i>CC, PM</i> самосская глина (<i>славившаяся как керамический материал</i>); Samii rami <i>Pers</i> самосские ветви, <i>т. е. обе ветки символического у пифагорейцев ипсилона</i> (<i>T</i>), <i>правая из которых, прямая и идущая вверх, означала добродетель, а левая, кривая и загибающаяся вниз, была символом порока.</i>\n\n<b>II Samius, i</b> <i>m</i> [Samos]\nсамосец <i>C etc.</i>"],
  ["Samnis", "<big>Samnis</big>\n\n<b>I Samnis, itis</b> (<i>gen. pl.</i> ium) <i>m</i> [Samnium]\nсамнит <i>C, L, Sen etc.</i>\n\n<b>II Samnis, itis</b> <i>adj.</i> [Samnium]\nсамнитский (exercitus <i>L</i>; bellum <i>PM</i>): gladiatores Samnites <i>LM ap. C</i> гладиаторы в самнитском вооружении."],
  ["Samniticus", "<big>Samniticus</big>\n\n<b>Samniticus, a, um</b> [Samnium]\nсамнитский <i>Su, Fl, Capit</i>."],
  ["Samnium", "<big>Samnium</big>\n\n<b>Samnium, i</b> <i>n</i>\nСамний, <i>гористая область к сев. от Кампании, между Латием и Апулией, населённая сабинско-оскскими племенами</i> (<i>Caraceni, Caudini, Hirpini, Pentri</i>) <i>C, L, Fl etc.</i>"],
  ["Samos", "<big>Samos</big>\n\n<b>Samos</b> <i>и</i> <b>Samus, i</b> <i>f</i>\nСамос:\n<b>1)</b> <i>O</i> = Sarne;\n<b>2)</b> <i>о-в с одноимённым городом в Эгейском море у Ионического побережья, против Эфеса, с храмом Юноны</i> (<i>Juno Samia</i>) <i>C, V, H etc.</i>;\n<b>3)</b> Threicia S. <i>V, O</i> = Самофракия."],
  ["Samosata", "<big>Samosata</big>\n\n<b>Samosata, orum</b> <i>n</i> <i>и</i> <b>Samosata, ae</b> <i>f</i>\nСамосата, <i>главн. город Коммагены (Сирия), на зап. берегу Евфрата, родина Лукиана</i> <i>PM, Amm</i>."],
  ["Samothraca", "<big>Samothraca</big>\n\n<b>Samothraca, ae, Samothrace, es</b> <i>и</i> <b>Samothracia, ae</b> <i>f</i>\nСамофракия, <i>о-в в Эгейском море у побережья Фракии, против устья реки Гебр, с культом Кабиров</i> <i>Vr, C, L, V etc.</i>"],
  ["Samothraces", "<big>Samothraces</big>\n\n<b>Samothraces, um</b> <i>m</i>\n<b>1)</b> жители Самофракии <i>Vr, O</i>;\n<b>2)</b> (<i>sc.</i> dii) <i>Vr, J</i> = Кабиры, <i>божества, чтимые в Самофракии.</i>"],
  ["Samothracicus", "<big>Samothracicus</big>\n\n<b>Samothracicus, Samothracius</b> <i>и</i> <b>Samothracus, a, um</b>\nсамофракийский <i>Macr, Lcr, PM, VF</i>."],
  ["sampsa", "<big>sampsa</big>\n\n<b>sampsa, ae</b> <i>f</i>\nрастёртая мякоть маслины <i>Col</i>."],
  ["Sampsiceramus", "<big>Sampsiceramus</big>\n\n<b>Sampsiceramus, i</b> <i>m</i>\n<i>эмесский царёк (в Сирии), побеждённый Помпеем, тж. прозвище, данное в насмешку Цицероном Гн. Помпею</i> <i>C</i>."],
  ["sampsuchinus", "<big>sampsuchinus</big>\n\n<b>sampsuchinus, a, um</b> [sampsuchum]\nмайорановый (oleum <i>PM</i>)."],
  ["sampsuchum", "<big>sampsuchum</big>\n\n<b>sampsuchum, i</b> <i>n</i> (<i>греч.</i>)\nмайоран (<i>Origanum Maiorana</i>, L.) <i>PM, Col</i>."],
  ["sampsuchus", "<big>sampsuchus</big>\n\n<b>sampsuchus, i</b> <i>m, f</i> <i>CC</i>\n= sampsuchum."],
  ["Samus", "<big>Samus</big>\n\n<b>Samus, i</b> <i>f</i> <i>v. l.</i>\n= Samos."],
  ["sanabilis", "<big>sanabilis</big>\n\n<b>sanabilis, e</b> [sano]\n<b>1)</b> излечимый, поддающийся исцелению (vulnus <i>O</i>);\n<b>2)</b> целительный <i>CA, Aug</i>."],
  ["Sanates", "<big>Sanates</big>\n\n<b>Sanates, um</b> <i>m</i>\nсанаты, <i>народности вокруг Рима, обрабатывавшие, после покорения их Римом, земли римских землевладельцев</i> <i>LXIIT ap. AG</i>."],
  ["sanatio", "<big>sanatio</big>\n\n<b>sanatio, onis</b> <i>f</i> [sano]\nлечение, исцеление (corporum <i>C</i>; malorum <i>C</i>); оздоровление (nostrae naturae <i>Aug</i>)."],
  ["sanator", "<big>sanator</big>\n\n<b>sanator, oris</b> <i>m</i> [sano]\n(ис)целитель <i>Vlg, Eccl</i>."],
  ["sancaptis", "<big>sancaptis</big>\n\n<b>sancaptis, idis</b> <i>f</i> <i>v. l.</i>\n= saucaptis."],
  ["sancio", "<big>sancio</big>\n\n<b>sancio, sanxi, sanctum</b> (<i>редко</i> <b>sancitum</b>), <b>ire</b> [sacer]\n<b>1)</b> освящать, делать незыблемым, объявлять нерушимым (foedus <i>L</i>): sanctum est jure civili <i>C</i> по гражданскому праву положено (гражданский закон гласит); s. aliquid periculo vitae <i>QC</i> обязывать к чему-л. под страхом смертной казни;\n<b>2)</b> твёрдо устанавливать, узаконивать, предписывать в законодательном порядке (aliquid lege <i>Pl, C</i>); определять, санкционировать (poenam <i>QC, St</i>; legem iniquam in aliquem <i>H</i>): aliquid inexpiabili religione sanxisse <i>C</i> объявить что-л. грехом, которому нет прощения;\n<b>3)</b> подтверждать, подкреплять (aliquid jure jurando <i>Cs</i>); объявлять <i>или</i> признавать действительным (acta Caesaris <i>C</i>);\n<b>4)</b> запрещать: s. aliquid aliqua re <i>C</i> запрещать что-л. под страхом чего-л.;\n<b>5)</b> посвящать (alicui carmina sua <i>St</i>)."],
  ["sancte", "<big>sancte</big>\n\n<b>sancte</b> [sanctus]\n<b>1)</b> свято, нерушимо (observare promissa <i>C</i>);\n<b>2)</b> высоконравственно, безупречно (se gerere <i>C</i>); по совести (jurare <i>Pl</i>);\n<b>3)</b> благоговейно, благочестиво (pie sancteque colere aliquid <i>C</i>)."],
  ["sanctesco", "<big>sanctesco</big>\n\n<b>sanctesco, —, —, ere</b> [sanctus]\nстановиться священным, освящаться <i>Acc</i>."],
  ["sanctificatio", "<big>sanctificatio</big>\n\n<b>sanctificatio, onis</b> <i>f</i>\nосвящение <i>Sid, Eccl</i>."],
  ["sanctificator", "<big>sanctificator</big>\n\n<b>sanctificator, oris</b> <i>m</i>\nосвящающий <i>Eccl</i>."],
  ["sanctificium", "<big>sanctificium</big>\n\n<b>sanctificium, i</b> <i>n</i> <i>Tert</i>\n= sanctificatio."],
  ["sanctifico", "<big>sanctifico</big>\n\n<b>sancti–fico, —, —, are</b>\nосвящать <i>Eccl</i>."],
  ["sanctificus", "<big>sanctificus</big>\n\n<b>sanctificus, a, um</b>\nосвящающий <i>Eccl</i>."],
  ["sanctiloquus", "<big>sanctiloquus</big>\n\n<b>sancti–loquus, a, um</b>\nизрекающий святые слова <i>Eccl</i>."],
  ["sanctimonia", "<big>sanctimonia</big>\n\n<b>sanctimonia, ae</b> <i>f</i> <i>rhH, C, T</i>\n= sanctitas."],
  ["sanctimonialis", "<big>sanctimonialis</big>\n\n<b>I sanctimonialis, e</b>\nсвятой, благочестивый (vita <i>CJ</i>).\n\n<b>II sanctimonialis, is</b> <i>f</i> (<i>sc.</i> mulier)\nмонахиня <i>CJ, Eccl</i>."],
  ["sanctimonialiter", "<big>sanctimonialiter</big>\n\n<b>sanctimonialiter</b>\nсвято, благочестиво (tempus vitae degere <i>и</i> vitam vivere <i>CJ</i>)."],
  ["sanctimonium", "<big>sanctimonium</big>\n\n<b>sanctimonium, i</b> <i>n</i>\n<b>1)</b> святость, благочестие <i>Aug</i>;\n<b>2)</b> мученичество за веру <i>Eccl</i>."],
  ["sanctio", "<big>sanctio</big>\n\n<b>sanctio, onis</b> <i>f</i> [sancio]\n<b>1)</b> нерушимый закон, незыблемое узаконение, строжайшее постановление, закон с указанием кары за его нарушение: legis s. poenaque <i>C</i> закон и наказание за его нарушение;\n<b>2)</b> договорная статья, оговорка (s. foederis <i>C</i>)."],
  ["sanctitas", "<big>sanctitas</big>\n\n<b>sanctitas, atis</b> <i>f</i> [sanctus]\n<b>1)</b> святость (templi <i>L</i>); нерушимость (nuptiarum <i>C</i>); неприкосновенность (tribunatus <i>C</i>);\n<b>2)</b> благочестие: religionum sanctitates <i>C</i> благочестивое почитание богов, набожность;\n<b>3)</b> непорочность, нравственная чистота (pietas et s. <i>C</i>);\n<b>4)</b> (<i>в обращении к высшему духовенству</i>) святейшество (s. tua! <i>Hier, Aug etc.</i>)."],
  ["sanctitudo", "<big>sanctitudo</big>\n\n<b>sanctitudo, inis</b> <i>f</i> <i>Acc, C, Ap</i>\n= sanctitas."],
  ["sanctor", "<big>sanctor</big>\n\n<b>sanctor, oris</b> <i>m</i> [sancio]\nустановитель: s. legum <i>T</i> законодатель."],
  ["sanctuarium", "<big>sanctuarium</big>\n\n<b>sanctuarium, i</b> <i>n</i> [sanctus]\n<b>1)</b> секретный архив, <i>pl.</i> личные дела, секретная переписка (sanctuaria Mithridatis <i>PM</i>);\n<b>2)</b> святилище <i>Eccl</i>."],
  ["sanctus", "<big>sanctus</big>\n\n<b>sanctus, a, um</b> [sancio]\n<b>1.</b> <i>part. pf. к</i> sancio;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> освящённый, священный (loca <i>C</i>; fanum <i>Lcr</i>; fons <i>C</i>);\n<b>2)</b> святой (di <i>O, J</i>); нерушимый, незыблемый (jus jurandum, fides <i>C</i>);\n<b>3)</b> неприкосновенный (tribuni plebis <i>C</i>; Ennius sanctos appellat poetas <i>C</i>);\n<b>4)</b> секретный, особый (aerarium sanctius <i>C</i>);\n<b>5)</b> высокий, глубоко почитаемый (vates <i>V</i>; ordo <i>C</i>; nomen <i>C</i>; parens <i>J</i>); торжественный (oratio <i>Q</i>); праздничный (dies <i>H</i>);\n<b>6)</b> безупречный, чистый, добродетельный (mores <i>J</i>; vir <i>C</i>); целомудренный, непорочный (femina <i>C</i>); благочестивый (homo <i>C</i>)."],
  ["Sancus", "<big>Sancus</big>\n\n<b>Sancus (Sangus), i</b> <i>m</i> <i>и</i> <b>Semo Sancus</b> [sacer] <i>O, Prp, PM etc.</i>\n= Fidius."],
  ["sandala", "<big>sandala</big>\n\n<b>sandala, ae</b> <i>f</i> <i>v. l.</i>\n= scandala."],
  ["Sandaliarius", "<big>Sandaliarius</big>\n\n<b>Sandaliarius, i</b> <i>m</i> [sandalium]\n(<i>sc.</i> vicus) улица Сапожников, Сандальная улица <i>AG</i>: Apollo S. <i>Su</i> и статуя Аполлона, установленная на Сандальной улице."],
  ["sandaligerula", "<big>sandaligerula</big>\n\n<b>sandaligerula, ae</b> <i>f</i> [sandalium + gero]\nслужанка, носившая за своей госпожой, при выходе её из дому, сандалии <i>Pl</i>."],
  ["sandalium", "<big>sandalium</big>\n\n<b>sandalium, i</b> <i>n</i> (<i>греч.</i>)\nсандалия <i>Ter, Aug</i>."],
  ["sandapila", "<big>sandapila</big>\n\n<b>sandapila, ae</b> <i>f</i>\nпогребальные носилки у бедноты (<i>имущие классы пользовались в этом случае</i> lectica) <i>Su, J, M</i>."],
  ["sandapilarius", "<big>sandapilarius</big>\n\n<b>sandapilarius, i</b> <i>m</i>\nносильщик похоронных носилок <i>Sid</i>."],
  ["sandaraca", "<big>sandaraca</big>\n\n<b>sandaraca (-cha), ae</b> <i>f</i> (<i>греч.</i>)\n<b>1)</b> сандарак, <i>красящее вещество красного цвета</i> <i>PM, Vtr</i>;\n<b>2)</b> перга, цветень <i>PM</i>."],
  ["sandaracinus", "<big>sandaracinus</big>\n\n<b>sandaracinus, a, um</b> [sandaraca]\nцвета сандарака, ярко-красный <i>Naev</i>."],
  ["sandix", "<big>sandix</big>\n\n<b>sandix, icis</b> <i>m, f</i> <i>v. l.</i>\n= sandyx."],
  ["sandyx", "<big>sandyx</big>\n\n<b>sandyx, ycis</b> <i>f</i> (<i>греч.</i>)\n<b>1)</b> кустарниковое растение с алыми цветами <i>Vop</i>;\n<b>2)</b> <i>предпол.</i> сурик <i>Prp, V, PM</i>."],
  ["sane", "<big>sane</big>\n\n<b>sane</b> [sanus]\n<b>1)</b> разумно, благоразумно, здраво: s. sapio et sentio <i>Pl</i> я в здравом уме и в полном сознании;\n<b>2)</b> действительно, в самом деле, поистине (res s. difficilis <i>C</i>);\n<b>3)</b> (<i>в ответах</i>) да, конечно, понятно (s. quidem <i>Ter, C</i>; s. hercle <i>Pl, Ter, C</i>);\n<b>4)</b> ну что же, ладно (ite s. <i>Pl</i>); пускай, пусть (dicat s. <i>C</i>; sit ita s. <i>C</i>): haec faciant s. juvenes <i>M</i> пускай бы этим занимались уж юноши;\n<b>5)</b> (<i>при</i> <i>imper.</i>) ну, же (age s. <i>Ter, C etc.</i>; abi s. <i>Ter</i>);\n<b>6)</b> вполне, совершенно (homo s. innocens <i>C</i>): nihil s. <i>C</i> решительно ничего; non (<i>или</i> haud) s. <i>Vr, H, Ter, Sl, C etc.</i> совсем нет <i>или</i> не совсем, не особенно;\n<b>7)</b> очень (s. bene <i>или</i> bene s. <i>Ter, C</i>): s. quam <i>C</i> чрезвычайно, весьма;\n<b>8)</b> крепко, горячо (aliquem perbasiare <i>Pt</i>)."],
  ["sanesco", "<big>sanesco</big>\n\n<b>sanesco, —, —, ere</b> [sanus]\nвыздоравливать (corpus sanescit <i>CC</i>); заживать (vulnera sanescunt <i>Col</i>)."],
  ["Sangarius", "<big>Sangarius</big>\n\n<b>I Sangarius, i</b> <i>m</i> <i>L</i>\n= Sagaris.\n\n<b>II Sangarius, a, um</b>\nфригийский: S. puer <i>St</i> = Attis."],
  ["Sangualis", "<big>Sangualis</big>\n\n<b>Sangualis, e</b>\n= Sanqualis."],
  ["sanguen", "<big>sanguen</big>\n\n<b>sanguen, guinis</b> <i>n</i> <i>арх. Enn, Lcr, Pt</i>\n= sanguis."],
  ["sanguiculus", "<big>sanguiculus</big>\n\n<b>sanguiculus, i</b> <i>m</i> [<i>demin. к</i> sanguis] <i>предпол.</i>\nкровяная колбаса <i>PM</i>."],
  ["sanguilentus", "<big>sanguilentus</big>\n\n<b>sanguilentus</b> <i>Scr</i>\n= sanguinolentus 1."],
  ["sanguinalis", "<big>sanguinalis</big>\n\n<b>sanguinalis, e</b> [sanguis]\nкровяной: herba s. <i>Col предпол.</i> птичья гречиха (травка-муравка) (<i>Polygonum aviculare</i>, L.)."],
  ["sanguinarius", "<big>sanguinarius</big>\n\n<b>sanguinarius, a, um</b> [sanguis]\n<b>1)</b> кровяной, кровавый: herba sanguinaria <i>PM</i> = herba sanguinalis;\n<b>2)</b> кровожадный (saevus atque s. <i>PM</i>)."],
  ["sanguineus", "<big>sanguineus</big>\n\n<b>sanguineus, a, um</b> [sanguis]\n<b>1)</b> состоящий из крови, кровяной (imber <i>C</i>): liquor s. <i>PM</i> = sanguis; gutta sanguinea <i>O, PM</i> капля крови;\n<b>2)</b> обагрённый кровью, окровавленный (manus, caput <i>O</i>);\n<b>3)</b> кроваво-красный, кровавого цвета (luna <i>O</i>; sagulum <i>C, Sil</i>; flos <i>Col</i>);\n<b>4)</b> кровавый, кровопролитный (rixa <i>H</i>; bellum <i>VF</i>);\n<b>5)</b> кровожадный (Mavors <i>V</i>; leo <i>VF</i>)."],
  ["sanguino", "<big>sanguino</big>\n\n<b>sanguino, avi, —, are</b> [sanguis]\n<b>1)</b> обливаться кровью, быть в крови (lacerti sanguinantes <i>Q</i>); менструировать (femina sanguinans <i>Tert</i>);\n<b>2)</b> быть кроваво-красным (coluber sanguinans <i>Ap</i>);\n<b>3)</b> жаждать крови, быть кровожадным (eloquentia sanguinans <i>T</i>);\n<b>4)</b> обагрять кровью, сечь до крови (latus alicui <i>Vlg</i>)."],
  ["sanguinolentia", "<big>sanguinolentia</big>\n\n<b>sanguinolentia, ae</b> <i>f</i>\nкровоизлияние <i>CA</i>."],
  ["sanguinolentus", "<big>sanguinolentus</big>\n\n<b>sanguinolentus, a, um</b> [sanguis]\n<b>1)</b> покрытый кровью, весь в крови (pectus <i>O</i>); налитый кровью (oculi <i>CA</i>);\n<b>2)</b> кроваво-красный, красный как кровь (color <i>O</i>);\n<b>3)</b> кровожадный, <i>перен.</i> жестокий, обидный (littera <i>O</i>);\n<b>4)</b> кровавый, кровопролитный (seditio <i>Vr</i>); добытый кровью (palma <i>rhH</i>);\n<b>5)</b> сосущий кровь, эксплуататорский (centesimae <i>Sen</i>)."],
  ["sanguinosus", "<big>sanguinosus</big>\n\n<b>sanguinosus, a, um</b>\nполнокровный (homo <i>CA</i>)."],
  ["sanguinulentus", "<big>sanguinulentus</big>\n\n<b>sanguinulentus, a, um</b> <i>v. l.</i>\n= sanguinolentus."],
  ["sanguis", "<big>sanguis</big>\n\n<b>sanguis</b> (<i>у</i> <i>Pl, Lcr, V, O</i> <i>тж.</i> <b>is</b>), <b>inis</b> <i>m</i>\n<b>1)</b> кровь (per omne corpus s. diffunditur <i>C</i>): sanguinis missio <i>CC</i> кровопускание; sanguine subito ictus <i>Eutr</i> поражённый апоплексическим ударом (кровоизлиянием); sanguinem haurire <i>C, L</i> проливать чужую кровь; sanguinem dare <i>L</i> проливать свою кровь;\n<b>2)</b> кровопролитие, резня, убийство (ad sanguinem incitare <i>C</i>): s. civilis <i>Lcr</i> междоусобная война; pugnare plurimo sanguine <i>L</i> вести кровопролитный бой;\n<b>3)</b> жизненная сила, жизнь (integer aevi s. <i>V</i>); крепость, мощь (oratorum <i>C</i>); свежесть (sucum et sanguinem amittere <i>C</i>), <i>но тж.</i>: amittere sanguinem <i>Pt</i> похолодеть от ужаса, остолбенеть; quum est s. integer <i>Pl</i> пока сил не поубавилось;\n<b>4)</b> имущество, состояние (de sanguine aerarii detrahere <i>C</i>);\n<b>5)</b> кровное родство (sanguine conjunctus <i>C</i>); происхождение, род (ejusdem sanguinis esse <i>T</i>): sanguine contingere (attingere <i>PJ</i>) aliquem <i>QC</i> находиться с кем-л. в кровном родстве; longo sanguine censeri <i>V</i> славиться древностью рода;\n<b>6)</b> потомок, отпрыск, дитя (s. pauperum parentum <i>H</i>);\n<b>7)</b> сок (<i>sc.</i> arborum <i>Man</i>): s. Baccheus <i>St</i> = вино."],
  ["sanguisuga", "<big>sanguisuga</big>\n\n<b>sanguisuga, ae</b> <i>f</i> [sanguis + sugo]\nпиявка <i>CC, Col etc.</i>"],
  ["Sangus", "<big>Sangus</big>\n\n<b>Sangus, i</b> <i>m</i> <i>v. l.</i>\n= Sancus."],
  ["sanies", "<big>sanies</big>\n\n<b>sanies, ei</b> <i>f</i>\n<b>1)</b> гнойная кровь, гноевидная жидкость, сукровица (saniem emittere <i>Sen</i>);\n<b>2)</b> слизь (aranei, purpurarum <i>PM</i>); сок (olivae <i>PM</i>);\n<b>3)</b> <i>поэт.</i> ядовитая слюна (<i>sc.</i> Cerberi <i>H</i>); яд (serpentis <i>Sil</i>)."],
  ["saniosus", "<big>saniosus</big>\n\n<b>saniosus, a, um</b> [sanies]\nполный гнойной крови, гнойный (partus <i>PM</i>; ulcus <i>Aug</i>)."],
  ["sanitas", "<big>sanitas</big>\n\n<b>sanitas, atis</b> <i>f</i> [sanus]\n<b>1)</b> здоровье (sanitatem aegris medicina promittit <i>CC</i>);\n<b>2)</b> здравый смысл, рассудительность, разумность, благоразумие: ad sanitatem redire <i>C</i> (reverti <i>Cs</i>) образумиться; aliquem ad sanitatem reducere <i>C</i> (revocare <i>bAl, Pt</i>) образумить кого-л.; sanitate vacans <i>C</i> безрассудный;\n<b>3)</b> трезвость, спокойствие, уравновешенность (orationis <i>C</i>);\n<b>4)</b> прочность, надёжность, полнота (s. victoriae <i>T</i>); правильность (metri <i>Macr</i>)."],
  ["sanna", "<big>sanna</big>\n\n<b>sanna, ae</b> <i>f</i>\nнасмешливая гримаса <i>Pers, J</i>."],
  ["sannio", "<big>sannio</big>\n\n<b>sannio, onis</b> <i>m</i> [sanna]\nшут, скоморох, паяц <i>C, Amm</i>."],
  ["sano", "<big>sano</big>\n\n<b>sano, avi, atum, are</b> [sanus]\n<b>1)</b> лечить, излечивать, исцелять (aliquem <i>Nep</i>; tumorem <i>C</i>; avaritiam <i>Sen</i>); заживлять (vulnus <i>PM</i>); оздоровлять (partes aegras rei publicae <i>Sen</i>); образумить (mentes consceleratas <i>C</i>);\n<b>2)</b> прекращать, пресекать (discordias <i>L</i>); улаживать (sanata simultate <i>Ap</i>); утолять (dolores <i>Prp</i>); устранять (curas <i>Tib, Prp</i>); утешать, ободрять (animos consolatione <i>Cs</i>; litterae tuae me sanaverunt <i>C</i>);\n<b>3)</b> искупать (scelus <i>SenT</i>); возмещать, поправлять, вознаграждать (incommodum majoribus commodis <i>Cs</i>)."],
  ["Sanqualis", "<big>Sanqualis</big>\n\n<b>Sanqualis, e</b> [Sancus]\nпосвящённый богу Санку: S. avis <i>L etc.</i> = <i>предпол.</i> орлан-белохвост <i>или</i> скопа."],
  ["sansa", "<big>sansa</big>\n\n<b>sansa, ae</b> <i>f</i> <i>v. l.</i>\n= sampsa."],
  ["santerna", "<big>santerna</big>\n\n<b>santerna, ae</b> <i>f</i> <i>предпол.</i>\nбура (<i>для паяния золота</i>), паяльный материал <i>PM</i>."],
  ["Santones", "<big>Santones</big>\n\n<b>Santones, um</b> <i>и</i> <b>Santoni, orum</b> <i>m</i>\nсантоны, <i>кельт. племя в Аквитанской Галлии, к сев. от устья Гарумны, с главн. городом Mediolanum</i> (<i>ныне</i> Saintes) <i>Cs, PM, Lcn</i>."],
  ["Santonicus", "<big>Santonicus</big>\n\n<b>Santonicus, a, um</b> [Santones <i>и</i> Santoni]\nсантонский <i>PM, M, J etc.</i>"],
  ["sanus", "<big>sanus</big>\n\n<b>sanus, a, um</b>\n<b>1)</b> здоровый (corpus <i>CC</i>): sanum fieri <i>Cato</i> выздороветь; ad sanum coire <i>Prp</i> заживать;\n<b>2)</b> неповреждённый, целый, невредимый (res publica <i>C</i>; armamenta navis <i>Pl</i>);\n<b>3)</b> (<i>тж.</i> s. mentis <i>Pl</i>) здравый, рассудительный, разумный (homo, mens <i>C</i>; consilia <i>QC</i>): nil ego contulerim jucundo s. amico <i>H</i> пока я в своём уме, ничто для меня не сравнится с задушевным другом; male s. <i>C, V, Sen, O</i> безумный, не в своём уме; satis s. es (sanusne es)? <i>Sen</i> в уме ли ты?;\n<b>4)</b> основательный, дельный, солидный, толковый (orator, genus dicendi <i>C</i>);\n<b>5)</b> небурный, спокойный (clementia sani gurgitis <i>Cld</i>)."],
  ["sanxi", "<big>sanxi</big>\n\n<b>sanxi</b> <i>pf. к</i>\nsancio."],
  ["sapa", "<big>sapa</big>\n\n<b>sapa, ae</b> <i>f</i>\nгустой виноградный сок <i>Vr, Col, PM, O etc.</i>"],
  ["Sapaei", "<big>Sapaei</big>\n\n<b>Sapaei, orum</b> <i>m</i>\nсапеи, <i>фракийск. племя между Пангейским хребтом и Бистонским заливом</i> <i>O, PM</i>."],
  ["Sapaudia", "<big>Sapaudia</big>\n\n<b>Sapaudia, ae</b> <i>f</i>\nСапаудия, <i>область в Галлии</i> (<i>впоследствии Савойя</i>) <i>Amm</i>."],
  ["saperda", "<big>saperda</big>\n\n<b>saperda, ae</b> <i>m</i> (<i>греч.</i>) <i>предпол.</i>\nзасоленная сельдь <i>Vr, Pers</i>."],
  ["saphon", "<big>saphon</big>\n\n<b>saphon, onis</b> <i>m</i>\nпричальный <i>или</i> буксирный канат <i>Is</i>."],
  ["saphyr-", "<big>saphyr-</big>\n\n<b>saphyr-</b> <i>v. l.</i>\n= sapp(h)ir-."],
  ["sapide", "<big>sapide</big>\n\n<b>sapide</b> [sapidus]\nвкусно (percoctus <i>Ap</i>)."],
  ["sapidus", "<big>sapidus</big>\n\n<b>sapidus, a, um</b> [sapio]\nвкусный, аппетитный (intrimentum, tuccetum <i>Ap</i>)."],
  ["sapiens", "<big>sapiens</big>\n\n<b>sapiens, entis</b> [sapio]\n<b>1.</b> <i>adj.</i> разумный, рассудительный, благоразумный (homo <i>C etc.</i>; vita <i>Nep</i>): dictum sapienti sat est <i>погов. Pl</i> для умного довольно и (одного) слова || мудрый (facundia Homeri <i>AG</i>);\n<b>2.</b> <i>subst. m</i>\n<b>1)</b> мудрец (septem sapientes <i>C, VM</i>): octavus sapientum <i>шутл. H</i> восьмой из мудрецов, новоявленный мудрец;\n<b>2)</b> знаток, сведущий, искушённый (s. rerum humanarum <i>AG</i>)."],
  ["sapienter", "<big>sapienter</big>\n\n<b>sapienter</b> [sapiens]\nмудро, как подобает мудрецу (insipienter factum s. ferre <i>Pl</i>); благоразумно, рассудительно (facere, dicere <i>C</i>): non s. amare <i>O</i> любить без ума."],
  ["sapientia", "<big>sapientia</big>\n\n<b>sapientia, ae</b> <i>f</i> [sapiens]\n<b>1)</b> благоразумие, рассудительность, ум: quanta mea s. est <i>Pl</i> насколько я понимаю;\n<b>2)</b> мудрость, знание: s. constituendae civitatis <i>C</i> политическая наука;\n<b>3)</b> философия: sapientiae doctores <i>T</i> учителя философии."],
  ["sapientialis", "<big>sapientialis</big>\n\n<b>sapientialis, e</b>\nприсущий мудрости <i>Tert</i>."],
  ["sapientipotens", "<big>sapientipotens</big>\n\n<b>sapientipotens, entis</b> <i>adj.</i> [sapientia + potens]\nмогучий своей мудростью <i>Enn</i>."],
  ["sapin-", "<big>sapin-</big>\n\n<b>sapin-</b> <i>v. l.</i>\n= sappin-."],
  ["Sapinius", "<big>Sapinius</big>\n\n<b>Sapinius, a, um</b> [Sapis]\nсапиниев (tribus <i>L</i>)."],
  ["sapio", "<big>sapio</big>\n\n<b>sapio, ii (ivi, ui), —, ere</b>\n<b>1)</b> (<i>о вещах</i>) иметь вкус <i>или</i> запах (caseus jucunde sapit <i>Col</i>): bene s. <i>Col</i> быть вкусным; s. aliquid <i>Pt</i> иметь вкус <i>или</i> запах чего-л., пахнуть (отдавать) чём-л. (s. picem <i>PM</i>; s. crocum <i>C</i>); hominem pagina nostra sapit <i>M</i> наши страницы целиком посвящены человеку; s. aliquem <i>Pers</i> быть как кто-л., подражать кому-л.;\n<b>2)</b> ощущать вкус (alicui palatus non sapit <i>C</i>);\n<b>3)</b> быть рассудительным, быть разумным (si sapis, cavebis <i>Pl</i>): s. aude <i>H</i> не бойся следовать велениям разума; sapias <i>H</i> будь благоразумен; nemo solus satis sapit <i>Pl</i> в одиночестве никто не может быть по-настоящему умён || понимать: nihil s. <i>C</i> ничего не понимать, быть глупым; haud stulte s. <i>Ter</i> быть неглупым; nil parvum s. <i>H</i> не иметь вкуса к пустякам (презирать их); recta s. <i>C</i> разумно мыслить; aetate sapimus rectius <i>Ter</i> с годами мы становимся умнее; sapis <i>M</i> ты прав; hi sapient <i>Cs</i> они разберутся (кто из нас прав); sero s. <i>погов. C</i> быть крепким задним умом; sapienter s. <i>C</i> быть хитрым."],
  ["Sapis", "<big>Sapis</big>\n\n<b>Sapis, is</b> <i>m</i>\nСапис, <i>речка в Цизальпинской Галлии и Умбрии, впадающая в Адриатическое море к югу от Равенны</i> <i>PM, Sil</i>."],
  ["saplutus", "<big>saplutus</big>\n\n<b>saplutus, a, um</b> (<i>греч.</i>)\nнесметно богатый <i>Pt</i>."],
  ["sapo", "<big>sapo</big>\n\n<b>sapo, onis</b> <i>m</i> (<i>кельт.</i>)\n<b>1)</b> мыло <i>PM, M</i>;\n<b>2)</b> головная помада <i>PM</i>."],
  ["sapor", "<big>sapor</big>\n\n<b>sapor, oris</b> <i>m</i> [sapio]\n<b>1)</b> вкус (s. amarus <i>PM</i>; s. pomorum <i>O</i>);\n<b>2)</b> тонкое обращение, хороший тон, развитой вкус, такт (s. vernaculus <i>C</i>; homo sine sapore <i>C</i>); sermo publici saporis <i>Pt</i> речь в общепринятом вкусе;\n<b>3)</b> лакомство, изысканное блюдо (saporibus palatum suum delectare <i>Sen</i>);\n<b>4)</b> чувство вкуса, вкусовые ощущения (s. oris <i>Lcr</i>);\n<b>5)</b> <i>pl.</i> благовония (aspergere sapores <i>V</i>)."],
  ["saporatus", "<big>saporatus</big>\n\n<b>saporatus, a, um</b> [sapor]\nвкусно приготовленный (cibi <i>Amm</i>).:"],
  ["Sapphicus", "<big>Sapphicus</big>\n\n<b>Sapphicus, a, um</b> [Sappho]\nсапфический (Musa <i>Ctl</i>): versus S. (minor) малый сапфический стих –U– – –||UU–U–<u>U</u>; systema Sapphicum сапфическая система (строфа из трёх Sapphici minores и четвёртого versus Adonius)."],
  ["sapphiratus", "<big>sapphiratus</big>\n\n<b>sapp(h)iratus, a, um</b>\nсапфировый (lapilli <i>Sid</i>)."],
  ["sapphirinus", "<big>sapphirinus</big>\n\n<b>sapp(h)irinus, a, um</b> <i>Dig, Vlg, Hier</i>\n= sapp(h)iratus."],
  ["sapphirus", "<big>sapphirus</big>\n\n<b>sapp(h)irus, i</b> <i>f</i> (<i>евр.-греч.</i>)\n<b>1)</b> сапфир <i>PM, Eccl</i>;\n<b>2)</b> лазурит, ляпис-лазурь <i>Vlg, Eccl</i>."],
  ["Sappho", "<big>Sappho</big>\n\n<b>Sappho, us</b> <i>f</i>\nСапфо, <i>греч. лирическая поэтесса, уроженка Лесбоса, жившая в городе Митилене, младшая современница Алкея</i> (<i>VII-VI вв. до н. э.</i>) <i>C, H, O, Sen etc.</i>"],
  ["sappinea", "<big>sappinea</big>\n\n<b>sappinea, ae</b> <i>f</i> (<i>sc.</i> pars)\nнижняя часть (комель) ели <i>Vtr</i>."],
  ["sappineus", "<big>sappineus</big>\n\n<b>sappineus (-ius), a, um</b> [sappinus]\nеловый (nuces <i>Col</i>)."],
  ["sappinus", "<big>sappinus</big>\n\n<b>sappinus, i</b> <i>f</i>\n<b>1)</b> ель <i>или</i> пихта <i>Vr, PM</i>;\n<b>2)</b> гладкий ствол (<i>или</i> комель) ели <i>Vtr, PM</i>."],
  ["sappir-", "<big>sappir-</big>\n\n<b>sappir-</b> <i>v. l.</i>\n= sapp(h)ir-."],
  ["saprophago", "<big>saprophago</big>\n\n<b>saprophago, —, —, ere</b> (<i>греч.</i>)\nесть гнилые <i>или</i> испортившиеся кушанья <i>M</i>."],
  ["saprus", "<big>saprus</big>\n\n<b>saprus, a, um</b> (<i>греч.</i>)\nгнилой, разложившийся (caseus <i>PM</i>)."],
  ["sapsa", "<big>sapsa</big>\n\n<b>sapsa</b> <i>арх. Enn</i>\n= ea <i>или</i> sua ipsa."],
  ["saraballa", "<big>saraballa</big>\n\n<b>saraballa, ae</b> <i>f Aug</i> <i>и</i> <b>saraballum, i</b> <i>n</i> <i>Hier</i>\n= sarabara."],
  ["sarabara", "<big>sarabara</big>\n\n<b>sarabara, ae</b> <i>f</i> <i>и</i> <b>sarabarum, i</b> <i>n</i>\nшальвары, шаровары <i>Eccl</i>."],
  ["Saraceni", "<big>Saraceni</big>\n\n<b>Saraceni, orum</b> <i>m</i>\nсарацины, <i>арабское племя в Arabia Felix</i> <i>Amm, Veg, Eccl</i>."],
  ["saracenus", "<big>saracenus</big>\n\n<b>saracenus, a, um</b>\nсарацинский <i>Vop</i>."],
  ["Saranus", "<big>Saranus</big>\n\n<b>Saranus, i</b> <i>m</i> <i>v. l.</i>\n= Serranus."],
  ["sarcimen", "<big>sarcimen</big>\n\n<b>sarcimen, inis</b> <i>n</i> [sarcio]\nшов <i>Ap</i>."],
  ["sarcina", "<big>sarcina</big>\n\n<b>sarcina, ae</b> <i>f</i> [sarcio] (<i>тж.</i> <i>pl.</i>)\n<b>1)</b> связка, тюк, узел (sareina alicujus rei onustus <i>Ap</i>);\n<b>2)</b> <i>воен.</i> индивидуальное снаряжение солдата (<i>корзинка, топор, лопата, котелок, паёк муки, несколько кольев — общим весом ок. 30 кг; в походе s. носилась на вилообразном шесте</i>): sub sarcinis <i>Cs</i> в полном походном снаряжении;\n<b>3)</b> бремя, обуза (alicui sarcinam imponere <i>Pl</i>): s. sum tibi <i>O</i> я в тягость тебе;\n<b>4)</b> утробный плод (s. prima <i>O</i>);\n<b>5)</b> <i>pl.</i> пожитки, добро (uxor sarcinaeque <i>M</i>)."],
  ["sarcinalis", "<big>sarcinalis</big>\n\n<b>sarcinalis, e</b> <i>Amm</i>\n= sarcinarius."],
  ["sarcinarius", "<big>sarcinarius</big>\n\n<b>sarcinarius, a, um</b> [sarcina]\nвьючный (iumentum <i>Cs</i>)."],
  ["sarcinator", "<big>sarcinator</big>\n\n<b>sarcinator, oris</b> <i>m</i> [sarcio]\nпортной-починщик <i>LM, Pl, Dig</i>."],
  ["sarcinatrix", "<big>sarcinatrix</big>\n\n<b>I sarcinatrix, icis</b> <i>f</i>\nпортниха-починщица, штопальщица <i>Vr, Dig</i>.\n\n<b>II sarcinatrix, icis</b> <i>f</i> [sarcina]\nохраняющая пожитки <i>Fronto</i>."],
  ["sarcinatus", "<big>sarcinatus</big>\n\n<b>sarcinatus, a, um</b> [sarcina]\nнагружённый, навьюченный <i>Pl, Eccl</i>."],
  ["sarcinosus", "<big>sarcinosus</big>\n\n<b>sarcinosus, a, um</b> [sarcina]\nобременённый, отягощенный (aliqua re <i>Ap</i>)."],
  ["sarcinula", "<big>sarcinula</big>\n\n<b>sarcinula, ae</b> <i>f</i> [<i>demin. к</i> sarcina]\nмаленькая связка, узелок, свёрток, пачка <i>Ctl, AG, Aug</i>: sarcinulas expedire <i>Pt</i> (alligare <i>PJ</i>) уложить свои вещи; colligere sarcinulas <i>Pt, J</i> собрать (свои) пожитки."],
  ["sarcio", "<big>sarcio</big>\n\n<b>sarcio, sarsi, sartum, ire</b>\n<b>1)</b> чинить, ремонтировать (tunicam <i>J</i>; funes veteres <i>Cato</i>);\n<b>2)</b> лечить (rimas pedum <i>PM</i>);\n<b>3)</b> возмещать, исправлять, поправлять (detrimentum <i>Cs</i>; damnum <i>L</i>); заглаживать (injuriam <i>C</i>). — <i>См. тж.</i> sartus."],
  ["sarcophagus", "<big>sarcophagus</big>\n\n<b>I sarcophagus, a, um</b> (<i>греч.</i> «плотоядный»)\nпожирающий плоть: lapis s. <i>PM</i> гробовой камень (<i>род известняка, в соприкосновении с которым тело умершего быстро истлевало</i>).\n\n<b>II sarcophagus, i</b> <i>m</i>\nгроб из lapis s., саркофаг <i>J, Dig, Eccl</i>."],
  ["sarculatio", "<big>sarculatio</big>\n\n<b>sarculatio, onis</b> <i>f</i> [sarculum]\nрыхление, мотыжение <i>или</i> прополка <i>PM, Pall</i>."],
  ["sarculo", "<big>sarculo</big>\n\n<b>sarculo, avi, atum, are</b>\nокапывать <i>или</i> полоть (aliquid <i>Pall, Eccl</i>)."],
  ["sarculum", "<big>sarculum</big>\n\n<b>sarculum, i</b> <i>n</i> [sarrio]\nполольная кирка, тяпка <i>Cato, Col, H</i>."],
  ["sarculus", "<big>sarculus</big>\n\n<b>sarculus, i</b> <i>m</i> <i>Pall</i>\n= sarculum."],
  ["sarda", "<big>sarda</big>\n\n<b>I sarda, ae</b> <i>f</i> <i>PM</i>\n= sardina.\n\n<b>II Sarda, ae</b> <i>f</i> [Sardinia]\nсардинянка <i>C</i>."],
  ["Sardanapalicus", "<big>Sardanapalicus</big>\n\n<b>Sardanapalicus, a, um</b>\nсарданапалов (mos <i>Sid</i>)."],
  ["Sardanapallus", "<big>Sardanapallus</big>\n\n<b>Sardanapal(l)us, i</b> <i>m</i>\nСарданапал, <i>последний царь древнеассирийского царства, известный своей любовью к роскоши; будучи осаждён в своей столице Ниневии, сжёг себя вместе со своим двором</i> (<i>ок. 880 г. до н. э.</i>) <i>C, VP, VM etc.</i>"],
  ["Sardes", "<big>Sardes</big>\n\n<b>Sardes, ium</b> <i>v. l.</i>\n= Sardis."],
  ["Sardi", "<big>Sardi</big>\n\n<b>Sardi, orum</b> <i>m</i>\nжители острова Сардиния <i>C, Vr</i>."],
  ["Sardianus", "<big>Sardianus</big>\n\n<b>I Sardianus, a, um</b>\nсардский: s. balanus <i>или</i> glans <i>PM</i> = каштан.\n\n<b>II Sardianus, i</b> <i>m</i>\nжитель Сард <i>C</i>."],
  ["sardina", "<big>sardina</big>\n\n<b>sardina, ae</b> <i>f</i> <i>предпол.</i>\nсардина <i>Col</i>."],
  ["Sardinia", "<big>Sardinia</big>\n\n<b>Sardinia, ae</b> <i>f</i>\nостров Сардиния <i>Vr, C, L</i>."],
  ["Sardiniensis", "<big>Sardiniensis</big>\n\n<b>Sardiniensis, e</b>\nсардинский <i>Nep</i>."],
  ["Sardis", "<big>Sardis</big>\n\n<b>Sardis, ium</b> <i>f</i>\nСарды, <i>столица Лидии, на реке Пактол, на сев. склоне горы Тмол</i> <i>C, QC, H, O etc.</i>"],
  ["sardius", "<big>sardius</big>\n\n<b>sardius, i</b> <i>m</i> (<i>тж.</i> s. lapis <i>Vlg</i>)\nсардий (<i>предпол.</i> = сардоникс) <i>Vlg, Eccl</i>."],
  ["sardo", "<big>sardo</big>\n\n<b>sardo, —, —, are</b> <i>арх.</i>\nпонимать <i>Naev</i>."],
  ["Sardonius", "<big>Sardonius</big>\n\n<b>Sardonius</b> <i>V, Sol</i>, <b>Sardous</b> <i>O, Cld</i> <i>и</i> <b>Sardus, a, um</b> <i>PM, H</i>\n= Sardiniensis."],
  ["sardonychatus", "<big>sardonychatus</big>\n\n<b>sardonychatus, a, um</b> [sardonyx]\nукрашенный сардониксом (manus <i>M</i>)."],
  ["sardonychus", "<big>sardonychus</big>\n\n<b>sardonychus, i</b> <i>m</i> <i>Vlg</i>\n= sardonyx."],
  ["sardonyx", "<big>sardonyx</big>\n\n<b>sardonyx, ychis</b> (<i>acc. pl.</i> ychas) <i>m, f</i> (<i>греч.</i>)\nсардоникс <i>Pers, J, M</i>."],
  ["Sardous", "<big>Sardous</big>\n\n<b>Sardous</b> <i>и</i> <b>Sardus, a, um</b> — <i>см.</i>\nSardonius."],
  ["Sarephta", "<big>Sarephta</big>\n\n<b>Sarep(h)ta, ae</b> <i>f</i> (<i>евр.</i> Tsarphat)\nСарепта, <i>финикийский город между Тиром и Сидоном</i> <i>PM, Vlg, Hier</i>."],
  ["Sareptensis", "<big>Sareptensis</big>\n\n<b>Sareptensis, e</b> <i>и</i> <b>Sareptanus, a, um</b> [Sarepta]\nсарептский <i>Hier, Sid</i>."],
  ["sargus", "<big>sargus</big>\n\n<b>sargus, i</b> <i>m</i> (<i>греч.</i>)\n<i>рыба</i> сарг (<i>Sparus Sargus</i>, L.) <i>Enn, PM, O</i>."],
  ["sario", "<big>sario</big>\n\n<b>I sario (sarrio), ivi (ui), itum, ire</b>\nвзрыхлять, полоть (locum rastris <i>PM</i>; segetes <i>Vr, Col</i>): s. saxum <i>погов. M</i> = трудиться без пользы.\n\n<b>II sario, onis</b> <i>m</i> <i>предпол.</i>\nфорель <i>Aus</i>."],
  ["sarisa", "<big>sarisa</big>\n\n<b>sarisa, ae</b> <i>f</i> (<i>греч.</i>)\nсарис(с)а, <i>длинное македонское копьё</i> <i>L, QC</i>."],
  ["sarisophorus", "<big>sarisophorus</big>\n\n<b>sarisophorus, i</b> <i>m</i> (<i>греч.</i>)\nмакедонский копейщик <i>L, QC</i>."],
  ["sariss-", "<big>sariss-</big>\n\n<b>sariss-</b> <i>v. l.</i>\n= saris-."],
  ["saritio", "<big>saritio</big>\n\n<b>saritio, onis</b> <i>f</i> [sario I]\nрыхление <i>или</i> прополка <i>Col</i>."],
  ["saritor", "<big>saritor</big>\n\n<b>saritor, oris</b> <i>m</i> [sario]\n<b>1)</b> разрыхляющий почву <i>Vr</i>;\n<b>2)</b> полольщик <i>Col</i>."],
  ["saritorius", "<big>saritorius</big>\n\n<b>saritorius, a, um</b> [saritor]\nпрополочный (opera <i>Col</i>)."],
  ["saritura", "<big>saritura</big>\n\n<b>saritura, ae</b> <i>f</i> <i>Col, PM</i>\n= saritio."],
  ["Sarmatae", "<big>Sarmatae</big>\n\n<b>Sarmatae, arum</b> <i>m</i>\nсарматы, <i>древнее население вост.-европейской низменности</i> (<i>от Балтийского моря до Волги</i>) <i>PM, T, Lcn</i>."],
  ["Sarmatia", "<big>Sarmatia</big>\n\n<b>Sarmatia, ae</b> <i>f</i>\nСарматия, <i>обширная область вост. Европы между Вислой и Волгой</i> <i>Mela, PM</i>."],
  ["Sarmatica", "<big>Sarmatica</big>\n\n<b>Sarmatica, orum</b> <i>n</i> <i>Sen</i>\n= Sarmatia."],
  ["Sarmatice", "<big>Sarmatice</big>\n\n<b>Sarmatice</b> [Sarmatae]\nпо-сарматски (loqui <i>O</i>)."],
  ["Sarmaticus", "<big>Sarmaticus</big>\n\n<b>Sarmaticus, a, um</b>\nсарматский (hiems <i>St</i>): Sarmaticum mare <i>O</i> = Pontus Euxinus; Sarmatica laurus <i>M</i> победа (Домициана) над сарматами."],
  ["Sarmatis", "<big>Sarmatis</big>\n\n<b>Sarmatis, idis</b> <i>adj. f</i> [Sarmatae]\nсарматская (tellus <i>O</i>)."],
  ["sarmen", "<big>sarmen</big>\n\n<b>sarmen, inis</b> <i>n</i> <i>Pl</i>\n= sarmentum."],
  ["sarmenticius", "<big>sarmenticius</big>\n\n<b>sarmenticius, a, um</b> [sarmentum]\nиз хвороста (cinis <i>Col</i>): sarmenticii <i>Tert</i> сжигаемые на кострах (<i>насмешливое прозвище ранних христиан</i>)."],
  ["sarmentosus", "<big>sarmentosus</big>\n\n<b>sarmentosus, a, um</b> [sarmentum]\nполный сучьев, покрытый множеством веток <i>PM</i>."],
  ["sarmentum", "<big>sarmentum</big>\n\n<b>sarmentum, i</b> <i>n</i>\nветка, лоза, розга <i>Vr, Col, C</i>; <i>преим.</i> <i>pl.</i> сухие ветви, сучья, хворост (sarmenta arida <i>L</i>): fasces sarmentorum <i>L</i> связки хвороста, фашины."],
  ["Sarnus", "<big>Sarnus</big>\n\n<b>Sarnus, i</b> <i>m</i>\nСарн, <i>река в Кампании, впадающая в Кумский залив</i> (<i>ныне</i> Sarno) <i>V, PM, Lcn</i>."],
  ["Sarpedon", "<big>Sarpedon</big>\n\n<b>Sarpedon, onis</b> <i>m</i>\nСарпедон:\n<b>1)</b> <i>сын Юпитера и Европы, брат Миноса, царь Ликии, союзник троянцев, павший во время осады Трои от руки Патрокла</i> <i>C, V, Mela</i>;\n<b>2)</b> <i>мыс в Киликии, к юго-зап. от устья реки Каликадн</i> <i>L, PM, M</i>."],
  ["Sarpedonius", "<big>Sarpedonius</big>\n\n<b>Sarpedonius, a, um</b> [Sarpedon 2]\nсарпедонский (promunturium <i>L</i>)."],
  ["Sarra", "<big>Sarra</big>\n\n<b>Sarra, ae</b> <i>f</i>\nCappa, <i>древнее название города Тир (Финикия)</i> <i>Enn, Pl, AG</i>."],
  ["Sarracen-", "<big>Sarracen-</big>\n\n<b>Sarracen-</b> <i>v. l.</i>\n= Saracen-."],
  ["sarracum", "<big>sarracum</big>\n\n<b>sarracum</b> <i>v. l.</i>\n= serracum."],
  ["Sarranus", "<big>Sarranus</big>\n\n<b>Sarranus, a, um</b> [Sarra]\nтир(ий)ский, <i>перен.</i> финикийский (ostrum <i>V</i>; aulaea <i>J</i>)."],
  ["sarrapis", "<big>sarrapis</big>\n\n<b>sarrapis, idis</b> <i>f</i> (<i>перс.</i>)\nсаррапида, <i>туника персидского покроя</i> <i>Pl</i>."],
  ["Sarrastes", "<big>Sarrastes</big>\n\n<b>Sarrastes, um</b> <i>m</i>\nсаррасты, <i>племя в Кампании, на берегах реки Sarnus</i> <i>V, Sil</i>."],
  ["sarri-", "<big>sarri-</big>\n\n<b>sarri-</b> <i>v. l.</i>\n= sari-."],
  ["sarsi", "<big>sarsi</big>\n\n<b>sarsi</b> <i>pf. к</i>\nsarcio."],
  ["Sarsin-", "<big>Sarsin-</big>\n\n<b>Sarsin-</b> <i>v. l.</i>\n= Sassin-."],
  ["sartago", "<big>sartago</big>\n\n<b>sartago, inis</b> <i>f</i>\nкастрюля <i>или</i> сковорода <i>PM, J, Eccl</i>: s. loquendi <i>ирон. Pers</i> всякая всячина, смесь || <i>перен.</i> дьявольский водоворот (circumstrepit aliquem s. flagitiosorum amorum <i>Aug</i>)."],
  ["sartitector", "<big>sartitector</big>\n\n<b>sarti–tector, oris</b> <i>m</i> [sarcio]\nплотник <i>Is</i>."],
  ["sartor", "<big>sartor</big>\n\n<b>I sartor, oris</b> <i>m</i> <i>Aug</i>\n= sarcinator.\n\n<b>II sartor, oris</b> <i>m</i> <i>Pl</i>\n= saritor."],
  ["sartrix", "<big>sartrix</big>\n\n<b>sartrix, icis</b> <i>f</i> <i>Fronto, CA</i>\n= sarcinatrix I."],
  ["sartura", "<big>sartura</big>\n\n<b>I sartura, ae</b> <i>f</i> <i>PM</i>\n= saritio.\n\n<b>II sartura, ae</b> <i>f</i> [sarcio]\nпочиненное место, шов <i>или</i> заплата <i>Col, Sen</i>."],
  ["sartus", "<big>sartus</big>\n\n<b>sartus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> sarcio;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> находящийся в хорошем состоянии, исправный: s. (et) tectus в полной исправности (aedes <i>C</i>); omnia sarta tecta exigere <i>C</i> следить за исправностью строительных работ; sarta tecta aedium tueri <i>C</i> держать строения в исправности; sarta tecta aliquid habere <i>Pl</i> свято хранить (соблюдать) что-л.;\n<b>2)</b> невредимый (homo s. et tectus ab omni detrimento <i>C</i>)."],
  ["Sason", "<big>Sason</big>\n\n<b>Saso(n), onis</b> <i>f</i>\nСасон, <i>островок у побережья Далматии, к сев. от мыса Акрокеравн</i> <i>PM, Lcn, Sil</i>."],
  ["Sassina", "<big>Sassina</big>\n\n<b>Sassina, ae</b> <i>f</i>\nСассина, <i>город в Умбрии, родина Плавта</i> <i>M, Sil</i>."],
  ["Sassinas", "<big>Sassinas</big>\n\n<b>Sassinas, atis</b> [Sassina]\nсассинский <i>M</i>."],
  ["Sassinatis", "<big>Sassinatis</big>\n\n<b>Sassinatis, idis</b> <i>f</i> [Sassina]\nсассинская <i>Pl</i>."],
  ["sat", "<big>sat</big>\n\n<b>sat</b>\n= satis (<i>см.</i>)."],
  ["sata", "<big>sata</big>\n\n<b>sata, ōrum</b> <i>n</i> [sero II]\nпосевы <i>или</i> насаждения <i>V, L etc.</i>"],
  ["satageus", "<big>satageus</big>\n\n<b>satageus, a, um</b> <i>v. l.</i>\n= satagius."],
  ["satagito", "<big>satagito</big>\n\n<b>sat–agito, —, —, āre</b> (<i>тж. раздельно</i>)\nиметь много дела, быть очень занятым (s. rerum suarum <i>Pl</i>)."],
  ["satagius", "<big>satagius</big>\n\n<b>satagius, a, um</b> [satago]\nхлопотливый, полный тревоги, беспокойный (s. ac sibi molestus <i>Sen</i>)."],
  ["satago", "<big>satago</big>\n\n<b>satago, ēgī, āctum, ere</b> [satis + ago]\n<b>1)</b> удовлетворять (<i>заимодавца</i>), уплачивать, погашать долг <i>Pl</i>;\n<b>2)</b> иметь много дела, быть очень занятым (s. suarum rerum <i>Ter</i>); хлопотать, суетиться (circa aliquid <i>Vlg</i>);\n<b>3)</b> тревожиться, беспокоиться, волноваться, метаться (curris, stupes, satagis tamquam mus <i>Pt</i>);\n<b>4)</b> находиться в трудном положении (satagentibus succurrere <i>bAfr</i>)."],
  ["satan", "<big>satan</big>\n\n<b>satan</b> <i>indecl.</i> <i>и</i> <b>satanās, ae</b> <i>m</i> (<i>евр.</i>)\nпротивник, враг <i>Vlg</i>; сатана, дьявол <i>Eccl</i>."],
  ["sategi", "<big>sategi</big>\n\n<b>sategi</b> <i>pf. к</i>\nsatago."],
  ["satelles", "<big>satelles</big>\n\n<b>satelles, itis</b> <i>m, f</i>\n<b>1)</b> телохранитель <i>или</i> член свиты <i>O, T</i>; <i>преим.</i> <i>pl.</i> охрана <i>или</i> свита <i>Pl, C, L, H etc.</i>;\n<b>2)</b> спутник: s. Aurorae <i>C</i> = Lucifer; s. Orci <i>H</i> = Charon; s. Jovis <i>Acc ap. C</i> = aquila;\n<b>3)</b> пособник, сообщник (s. scelerum <i>C</i>)."],
  ["satellitium", "<big>satellitium</big>\n\n<b>satellitium, i</b> <i>n</i>\n<b>1)</b> охрана, телохранители, эскорт <i>JV</i>;\n<b>2)</b> <i>перен.</i> защита (scripturarum <i>Aug</i>);\n<b>3)</b> зависимое положение, подчинённость <i>Aug</i>."],
  ["satiabilis", "<big>satiabilis</big>\n\n<b>satiabilis, e</b> [satio I]\n<b>1)</b> насыщающий <i>Aug, CA</i>;\n<b>2)</b> могущий насытиться <i>Eccl</i>."],
  ["satianter", "<big>satianter</big>\n\n<b>satianter</b> [satio]\nдосыта (equi s. pasti <i>Ap</i>)."],
  ["satias", "<big>satias</big>\n\n<b>satias, atis</b> (<i>употр. преим.</i> <i>nom. sg.</i>) <i>f</i> (= satietas)\nпресыщение: ubi s. coepit fieri <i>Ter</i> когда начнёт приедаться; ad satiatem <i>Lcr</i> в изобилии."],
  ["satiate", "<big>satiate</big>\n\n<b>satiate</b> [satio I]\nвдоволь, достаточно <i>Vtr, Eccl</i>."],
  ["Saticula", "<big>Saticula</big>\n\n<b>Saticula, ae</b> <i>f</i>\nСатикула, <i>город в Самнии</i> (<i>на границе с Кампанией</i>) <i>L, VP</i>."],
  ["Saticulanus", "<big>Saticulanus</big>\n\n<b>I Saticulanus, a, um</b> [Saticula]\nсатикуланский <i>L</i>.\n\n<b>II Saticulanus, i</b> <i>m</i>\nжитель города Saticula <i>L</i>."],
  ["Saticulus", "<big>Saticulus</big>\n\n<b>Saticulus, a, um</b> <i>V</i>\n= Saticulanus I."],
  ["saties", "<big>saties</big>\n\n<b>saties, ei</b> <i>f</i> <i>PM, J</i>\n= satietas."],
  ["satietas", "<big>satietas</big>\n\n<b>satietas, atis</b> <i>f</i> [satis]\n<b>1)</b> достаточность, достаточное количество, изобилие (ad satietatem aliqua re instructus <i>QC</i>);\n<b>2)</b> пресыщенность, отвращение <i>или</i> скука: satietatem capere alicujus rei <i>Pl</i> ощущать отвращение к чему-л.; satietatem afferre <i>C</i> (parere <i>rhH</i>) внушать отвращение, надоедать; satietati occurrere <i>C</i> бороться со скукой; s. aliquem tenet alicujus rei <i>C</i> что-л. прискучило (надоело) кому-л.;\n<b>3)</b> отбросы <i>Sol</i> (<i>ср.</i> saturitas 4)."],
  ["satillum", "<big>satillum</big>\n\n<b>satillum</b> <i>adv.</i> [<i>demin. к</i> sat]\nсамая малость, чуточку: s. animae <i>Pl</i> искорка жизни."],
  ["satin'", "<big>satin'</big>\n\n<b>satin'</b> <i>и</i> <b>satine</b> [<i>из</i> satisne]\n(не)достаточно ли, в достаточной ли мере <i>Pl, C</i>."],
  ["satio", "<big>satio</big>\n\n<b>I satio, avi, atum, are</b> [satis]\n<b>1)</b> кормить досыта, насыщать (agnos <i>Lcr</i>);\n<b>2)</b> утолять (famem <i>O</i>; sitim <i>M</i>; iracundiam suam <i>Pt</i>); удовлетворять (desideria <i>C</i>): oculis non satiari posse <i>Sen</i> не быть в состоянии наглядеться; satiatus somno <i>L</i> хорошо выспавшийся; satiari prosperis adversisque <i>T</i> изведать вдоволь и счастья и горя;\n<b>3)</b> напитывать, поливать (terra satiata <i>Sen</i>); хорошо удобрять (solum stercore <i>Col, PM</i>);\n<b>4)</b> надоедать, наскучить, утомлять: assiduo satiatus aratro <i>Tib</i> утомлённый усиленной пахотой.\n\n<b>II satio, onis</b> <i>f</i> [sero II]\n<b>1)</b> сеяние, сев <i>C</i>; посадка <i>V, L</i>;\n<b>2)</b> <i>pl.</i> посевы, засеянные поля <i>Sen</i>; насаждения <i>Col, Pall</i>."],
  ["satira", "<big>satira</big>\n\n<b>satira, ae</b> <i>f</i> <i>v. l.</i>\n= satura."],
  ["Satiricon", "<big>Satiricon</big>\n\n<b>Satiricon, ī</b> <i>n</i>\nСатирикон, <i>традиционное заглавие романа Петрония.</i>"],
  ["satiricus", "<big>satiricus</big>\n\n<b>I satiricus, a, um</b> [satira]\nсатирический (carmen <i>Lact</i>; versus <i>Sid</i>).\n\n<b>II satiricus, ī</b> <i>m</i>\nсатирик <i>Sid</i>."],
  ["satirographus", "<big>satirographus</big>\n\n<b>satirographus, ī</b> <i>m</i> <i>Sid</i>\n= satiricus II."],
  ["satis", "<big>satis</big>\n\n<b>satis (sat)</b>\n<b>1.</b> <i>adv.</i>\n<b>1)</b> довольно, достаточно (sed de hoc s. <i>C</i>; s. laudare <i>C</i>; s. magnus <i>C</i>; s. saepe <i>Sl</i>; s. verborum dictum est <i>Sl</i>): satisne <i>или</i> satin'? <i>Pl, C etc.</i> достаточно ли?; satin' habes, si...? <i>Pl</i> разве мало с тебя того, что...?; non s. <i>C etc.</i> маловато, слишком мало; quantum s. <i>Sen</i> в достаточном количестве; nec non s. <i>C</i> не в такой уж малой степени; sed s. verborum est <i>Pl</i> но довольно слов; s. superque <i>Pl, Sl etc.</i> вполне <i>или</i> более чем достаточно, чересчур много (dicere <i>C</i>): non sentire quid sit s. <i>C</i> не знать чувства меры; nequeo s. proloqui, quam... <i>Ter</i> не могу выразить, до какой степени...; quidvis s. est, dum... <i>Ter</i> всё, что угодно, только бы...;\n<b>2)</b> изрядно, порядочно (s. nivis terris mittere <i>H</i>); неплохо, довольно хорошо (s. scire <i>Ter, C</i>): si me s. audias <i>H</i> если бы ты прислушалась к моим словам; s. recte <i>Pl</i> совершенно верно; non s. scio <i>AG</i> право, не знаю, неуверен; s. causae (<i>gen.</i>) <i>Cs</i> достаточная причина; s. habere <i>C etc.</i> считать достаточным, довольствоваться, удовлетворяться; s. agere <i>Cato, C</i> = satago; s. dare <i>Pl, AG</i> = satisdo; s. facio <i>Pt</i> = satisfacio; s. petere (exigere) <i>Dig</i> требовать обеспечения; s. accipere <i>Cato, Pl, C</i> получать обеспечение; s. cavere <i>C, Dig</i> давать обеспечение; verbis hoc satis erat cautum? <i>C</i> разве это было надлежащим образом оговорено?;\n<b>3)</b> более или менее, в известной степени (s. firmus <i>C</i>);\n<b>2.</b> <i>adv. compar.</i> satius лучше, выгоднее, удобнее, целесообразнее (inter feras satius est aetatem degere <i>C</i>): satius putare (existimare) <i>Nep</i> считать лучшим."],
  ["satisdatio", "<big>satisdatio</big>\n\n<b>satisdatio, onis</b> <i>f</i> [satisdo]\nобеспечение, ручательство, внесение залога, гарантия <i>C, Dig</i>: s. capitalis <i>PM</i> ручательство головой."],
  ["satisdator", "<big>satisdator</big>\n\n<b>satis–dator, oris</b> <i>m</i>\nпоручитель, гарант <i>Sid</i>."],
  ["satisdo", "<big>satisdo</big>\n\n<b>satis–do, dedi, datum, dare</b> (<i>тж. раздельно</i>)\nпредставлять обеспечение, давать ручательство, гарантировать: s. damni infecti <i>C</i> гарантировать, что возможные убытки будут возмещены; satisdato <i>C, Dig</i> с представлением гарантии; judicatum solvi s. <i>C</i> внести залог в обеспечение уплаты (<i>в случае присуждения исковой суммы другой стороне</i>)."],
  ["satisfacio", "<big>satisfacio</big>\n\n<b>satis–facio, feci, factum, ere</b> (<i>тж. раздельно</i>)\n<b>1)</b> удовлетворять, идти навстречу (precibus alicujus <i>C</i>; amico petenti <i>C</i>);\n<b>2)</b> платить (alicui in pecunia <i>Caelius ap. C</i> <i>и</i> pecuniam <i>Cato</i>);\n<b>3)</b> просить прощения, извиняться, оправдываться (publice <i>C</i>): satisfaciendi causa <i>Cs</i> чтобы принести свои извинения || давать удовлетворение, заглаживать (de <i>или</i> pro injuria <i>Cs, C</i> <i>и</i> injuriarum <i>rhH</i>);\n<b>4)</b> поплатиться: saepe satisfecit praedae venator <i>M</i> часто зверь мстил ловцу;\n<b>5)</b> повиноваться: s. officio suo <i>C</i> исполнить свой долг; s. legibus <i>C</i> понести наказание по законам; s. vitae <i>C</i> прожить достаточно долго; s. naturae <i>C</i> отдать долг природе (<i>т. е.</i> умереть);\n<b>6)</b> ясно показывать, разъяснять, убеждать (alicui <i>Nep</i>)."],
  ["satisfactio", "<big>satisfactio</big>\n\n<b>satisfactio, onis</b> <i>f</i> [satisfacio]\n<b>1)</b> удовлетворение (кредитора), погашение долга <i>Dig</i>;\n<b>2)</b> оправдание, извинение (satisfactionem alicujus accipere <i>Cs</i>);\n<b>3)</b> наказание, кара <i>T, Dig</i>."],
  ["satisfactionalis", "<big>satisfactionalis</big>\n\n<b>satisfactionalis, e</b>\nзащищающий, апологетический (liber <i>Eccl</i>)."],
  ["satisfio", "<big>satisfio</big>\n\n<b>satis–fio, factus sum, fieri</b> <i>pass. к</i>\nsatisfacio."],
  ["satius", "<big>satius</big>\n\n<b>satius</b> <i>compar. к</i>\nsatis (<i>см.</i>)."],
  ["sativus", "<big>sativus</big>\n\n<b>sativus, a, um</b> [sero II]\n<i>о растениях</i> посевной, разводимый, <i>т. е.</i> культурный (<i>в противоположность к дикорастущим</i>) (morus <i>PM</i>): sativa, orum <i>PM</i> культурные растения."],
  ["sato", "<big>sato</big>\n\n<b>sato, avi, —, are</b> [<i>intens. к</i> sero II]\nобычно сеять <i>или</i> сажать <i>Aug</i>."],
  ["sator", "<big>sator</big>\n\n<b>sator, oris</b> <i>m</i> [sero II]\n<b>1)</b> сеятель <i>Vr, Col</i>;\n<b>2)</b> создатель, основатель (omnium rerum seminator et s. <i>C</i>): s. hominum deorumque <i>V</i> = Juppiter || виновник (scelerum <i>Pl</i>)."],
  ["satorius", "<big>satorius</big>\n\n<b>satorius, a, um</b> [sator]\nупотребляемый для посева <i>или</i> посадки (qualum <i>Cato</i>; trimodium <i>Col</i>)."],
  ["satrapa", "<big>satrapa</big>\n\n<b>satrapa, ae</b> <i>m</i> <i>v. l.</i>\n= satrapes."],
  ["satrapea", "<big>satrapea</big>\n\n<b>satrapea, ae</b> <i>f</i> <i>QC</i>\n= satrapia."],
  ["satrapes", "<big>satrapes</big>\n\n<b>satrapes, ae</b> <i>и</i> <b>is</b> <i>m</i> (<i>перс.-греч.</i>)\nсатрап, наместник персидского царя <i>Nep, QC etc.</i>"],
  ["satrapia", "<big>satrapia</big>\n\n<b>satrapia, ae</b> <i>f</i>\nсатрапия, наместничество в древней Персии <i>PM</i>."],
  ["satraps", "<big>satraps</big>\n\n<b>satraps, apis</b> <i>m</i> <i>Sid</i>\n= satrapes."],
  ["Satricani", "<big>Satricani</big>\n\n<b>Satricani, orum</b> <i>m</i>\nжители города Satricum <i>L</i>."],
  ["Satricum", "<big>Satricum</big>\n\n<b>Satricum, i</b> <i>n</i>\nСатрик, <i>город в Латии, к сев.-вост. от Антия</i> <i>C, L</i>."],
  ["satullo", "<big>satullo</big>\n\n<b>satullo, —, —, are</b> [satullus]\nнасыщать (famem ventris <i>Vr</i>)."],
  ["satullus", "<big>satullus</big>\n\n<b>satullus, a, um</b> [<i>demin. к</i> satur]\nдовольно сытый, изрядно напитавшийся (agni sat ulli lacte <i>Vr</i>)."],
  ["satur", "<big>satur</big>\n\n<b>satur, tura, turum</b> [satis]\n<b>1)</b> сытый, насыщенный (s. omnium rerum <i>Ter</i>): ita s. pane fiam <i>Pt</i> клянусь своим хлебом насущным;\n<b>2)</b> полный, преисполненный (admiratione <i>Pt</i>); богатый, обильный (Tarentum, praesaepia <i>V</i>); густой, насыщенный, яркий (color <i>V, PM</i>); обильно пропитанный, хорошо прокрашенный (ostro <i>Sen</i>);\n<b>3)</b> откормленный, тучный, жирный (aves <i>M</i>)."],
  ["satura", "<big>satura</big>\n\n<b>satura, ae</b> <i>f</i> [satur]\n<b>1)</b> (<i>sc.</i> lanx) блюдо с различными плодами, <i>перен.</i> смесь, всякая всячина: per saturam <i>Sl</i> в беспорядке, вперемешку, без плана;\n<b>2)</b> (<i>v. l.</i> satira) сатира (<i>стихотворное произведение, вначале — типа драматическо-песенной импровизации, впоследствии (с Энния) — нравственно-назидательного и, наконец (с Луцилия и Горация) — обличительного содержания</i>) <i>L, J, St</i>."],
  ["Saturae palus", "<big>Saturae palus</big>\n\n<b>Saturae palūs</b> <i>f</i>\n<i>болотистое озеро в Латии, с юга примыкающее к Помптинским болотам</i> (<i>ныне</i> Lago di Paola) <i>V, Sil</i>."],
  ["saturatio", "<big>saturatio</big>\n\n<b>saturātio, ōnis</b> <i>f</i>\nнасыщение <i>Aug</i>."],
  ["saturator", "<big>saturator</big>\n\n<b>saturātor, ōris</b> <i>m</i>\nнасыщающий, питающий <i>Aug</i>."],
  ["saturatus", "<big>saturatus</big>\n\n<b>saturātus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> saturo;\n<b>2.</b> <i>adj.</i> насыщенный (saturatior color <i>PM</i>)."],
  ["satureja", "<big>satureja</big>\n\n<b>saturēja, ae</b> <i>f</i> <i>и</i> <b>saturēja, ōrum</b> <i>n</i> <i>бот.</i>\nчабер (<i>Satureja hortensis</i>, L.) <i>PM, O, M</i>."],
  ["Saturejanus", "<big>Saturejanus</big>\n\n<b>Saturējānus, a, um</b> [Saturejum]\nсатурейский, <i>т. е.</i> апулийский (caballus <i>H</i>)."],
  ["Saturejum", "<big>Saturejum</big>\n\n<b>Saturējum, ī</b> <i>n</i>\nСатурей, <i>город в Апулии</i> (<i>см.</i> Saturejanus)."],
  ["satureum", "<big>satureum</big>\n\n<b>satureum, ī</b> <i>n</i> <i>Pt</i>\n= satyrion 2."],
  ["saturic-", "<big>saturic-</big>\n\n<b>saturic-</b> <i>v. l.</i>\n= satiric-."],
  ["saturitas", "<big>saturitas</big>\n\n<b>saturitās, ātis</b> <i>f</i> [satur]\n<b>1)</b> сытость <i>Pl etc.</i>: saturitate ebrius <i>Pl</i> напившийся вволю;\n<b>2)</b> обилие, довольство (s. copiaque omnium rerum <i>C</i>);\n<b>3)</b> (<i>о цвете</i>) насыщенность <i>PM</i>;\n<b>4)</b> экскременты (saturitatem foris emittere <i>PM</i>)."],
  ["Saturnalia", "<big>Saturnalia</big>\n\n<b>Sāturnālia, ium</b> <i>и</i> <b>ōrum</b> <i>n</i>\nСатурналии, <i>древнеиталийский праздник, справлявшийся в течение ряда дней (с 17 декабря) в память о «золотом веке» (Сатурна) и отмечавшийся особым весельем</i>: semper S. agere <i>погов. Pt</i> = всегда кататься как сыр в масле; non semper erunt S. <i>погов. Sen</i> не вечно праздновать; secundis Saturnalibus <i>C</i> во второй день Сатурналий."],
  ["Saturnalicius", "<big>Saturnalicius</big>\n\n<b>Sāturnālicius, a, um</b> [Saturnalia]\nсатурнальный <i>Sen, M, Spart</i>."],
  ["Saturnalis", "<big>Saturnalis</big>\n\n<b>Sāturnālis, e</b> <i>Macr</i>\n= Saturnalicius."],
  ["Saturnia", "<big>Saturnia</big>\n\n<b>Sāturnia, ae</b> <i>f</i>\nСатурния:\n<b>1)</b> <i>древний город, миф. предшественник Рима, построенный Сатурном на Капитолийском холме</i> <i>Vr, V, O</i>;\n<b>2)</b> <i>город в Этрурии, прежде Aurinia, основанный, по преданию, пеласгами</i> (<i>на пути из Рима в Козу</i>) <i>L</i>;\n<b>3)</b> дочь Сатурна, <i>т. е. Юнона</i> <i>V, O</i>."],
  ["Saturnigena", "<big>Saturnigena</big>\n\n<b>Sāturni–gena, ae</b> <i>m</i> [geno]\nсын Сатурна, <i>т. е.</i> Юпитер <i>Aus, Sid</i>."],
  ["Saturninus", "<big>Saturninus</big>\n\n<b>Sāturnīnus, ī</b> <i>m</i>\nСатурнин, <i>cognomen ряда римских родов; наиболее известны</i>:\n<b>1)</b> L. Apulejus S. — <i>см.</i> Apulejus;\n<b>2)</b> L. Antonius S., <i>правитель Верхней Германии при Домициане, устроивший восстание против него (91 г. н. э.), но потерпевший поражение и казнённый</i> <i>M, Su</i>;\n<b>3)</b> Aemilius S., <i>полководец Валериана, а впоследствии Галлиена, провозглашённый императором, но вскоре убитый</i> (<i>ок. 260 г. н. э.</i>) <i>Treb</i>;\n<b>4)</b> S., <i>уроженец Галлии, полководец при Аврелиане и Пробе, в 280 г. н. э. провозгласил себя в Египте императором, но вскоре был убит солдатами Проба</i> <i>Vop</i>."],
  ["Saturnius", "<big>Saturnius</big>\n\n<b>I Sāturnius, ī</b> <i>m</i>\nсын Сатурна, <i>т. е.</i> Юпитер <i>или</i> Плутон <i>O, Cld</i>.\n\n<b>II Sāturnius, a, um</b> [Saturnus]\nсатурнов: S. mons <i>Vr, Just, Sol</i> = Capitolium; S. pater <i>V</i> = Juppiter; S. numerus <i>H</i> сатурнийский стих (<i>размер римского стихосложения до введения греческих размеров, точная схема которого не выяснена</i>); Saturnia arva <i>V</i> = Latium; Saturnia tellus <i>V</i> = Italia; Saturnia aetas <i>и</i> Saturnia regna <i>V</i> век Сатурна, <i>т. е.</i> «золотой»; Saturnia gens <i>O</i> = италики <i>или</i> латиняне; Saturnia proles <i>O</i> = Picus; Saturnia virgo <i>O</i> = Vesta; Saturnia stella <i>C</i> планета Сатурн; S. domitor maris <i>V</i> = Neptunus."],
  ["Saturnus", "<big>Saturnus</big>\n\n<b>Sāturnus, ī</b> <i>m</i> [sero II?]\nСатурн, <i>древне италийский бог посевов и земледелия, супруг богини Ops, впоследствии отождествлённый с греческим Кроносом и ставший поэтому отцом Юпитера, Нептуна, Плутона и Юноны; свергнутый Юпитером с небесного престола, воцарился в Латии; время его царствования</i> (Saturnia regna <i>V</i>) <i>считалось «золотым веком» <i>Vr, V, O etc.</i>; бог времени</i>: S. est appellatus, quod saturaretur annis <i>C</i> Сатурном он назван потому, что насыщается годами; Saturni dies <i>Tib</i> = суббота; Saturni stella <i>C</i> <i>или</i> S. <i>H</i> планета Сатурн."],
  ["saturo", "<big>saturo</big>\n\n<b>saturo, āvī, ātum, āre</b> [satur]\n<b>1)</b> насыщать, кормить, поить досыта (animalia ubertate mammarum <i>C</i>);\n<b>2)</b> наполнять (horrea <i>LJ</i>): necdum saturata dolorem (<i>acc. gr.</i>) <i>V</i> ещё не выместившая свой гнев (Юнона) || пропитывать (pallam murice <i>O</i>): s. sola fimo <i>V</i> обильно удобрять почву;\n<b>3)</b> удовлетворять (odium suum <i>C</i>): s. aliquem honoribus <i>C</i> осыпать кого-л. почестями;\n<b>4)</b> пресыщать, наскучивать, надоедать: hae(c) res vitae me saturant <i>Pl</i> это мне до смерти надоело."],
  ["satus", "<big>satus</big>\n\n<b>I satus, a, um</b> <i>part. pf. к</i>\nsero II.\n\n<b>II satus, us</b> <i>m</i> [sero II]\n<b>1)</b> сев, посев, сеяние <i>Cato</i>;\n<b>2)</b> сажание, посадка (vitis <i>C</i>);\n<b>3)</b> порождение, род (Hercules Jovis satu editus <i>C</i>)."],
  ["satyra", "<big>satyra</big>\n\n<b>satyra, ae</b> <i>f</i> <i>неправ. v. l. к</i>\nsatira <i>и</i> satura."],
  ["satyriasis", "<big>satyriasis</big>\n\n<b>satyriasis, is</b> <i>f</i> (<i>греч.</i>) <i>мед.</i>\nсатириазис, приапизм <i>CA</i>."],
  ["satyric-", "<big>satyric-</big>\n\n<b>satyric-</b> <i>v. l.</i>\n= satiric-."],
  ["satyrion", "<big>satyrion</big>\n\n<b>satyrion, ī</b> <i>n</i> (<i>греч.</i>)\nсатирий:\n<b>1)</b> <i>растение из семейства орхидных</i> <i>PM</i>;\n<b>2)</b> <i>пьянящий сок этого растения</i> <i>Pt</i> (<i>v. l.</i>)."],
  ["satyriscus", "<big>satyriscus</big>\n\n<b>satyriscus, ī</b> <i>m</i> [<i>demin. к</i> satyrus]\nмолодой <i>или</i> маленький сатир <i>C</i>."],
  ["satyrus", "<big>satyrus</big>\n\n<b>satyrus, ī</b> <i>m</i> (<i>греч.</i>)\n<b>1)</b> сатир (<i>satyri — мелкие лесные и полевые божества, соотв. римским fauni, с козлиными ногами и хвостом, остроконечными ушами, маленькими рожками и тупыми носами; постоянные спутники Вакха; считались символами грубых чувственных влечений</i>) <i>Lcr, C, H, O</i>: s. Phryx <i>O</i> = Марсий;\n<b>2)</b> <i>пародийный вид греческой драмы, в которой, наряду с трагическими героями, выступают комические хоры сатиров</i> (satyrorum scriptor <i>H</i>);\n<b>3)</b> <i>вид хвостатой обезьяны</i> <i>PM, Sol</i>."],
  ["saucaptis", "<big>saucaptis</big>\n\n<b>saucaptis, idis</b> <i>f</i>\n<i>род приправы</i> <i>Pl</i>."],
  ["sauciatio", "<big>sauciatio</big>\n\n<b>sauciātio, ōnis</b> <i>f</i> [saucio]\nнанесение раны, ранение <i>C</i>."],
  ["saucietas", "<big>saucietas</big>\n\n<b>saucietās, ātis</b> <i>f</i> [saucius]\nнездоровье, недомогание <i>CA</i>."],
  ["saucio", "<big>saucio</big>\n\n<b>saucio, āvī, ātum, āre</b> [saucius]\n<b>1)</b> ранить (aliquem telo <i>C</i>); исцарапать до крови (genas ungue <i>O</i>);\n<b>2)</b> поражать насмерть, убивать (Caesar, quem Brutus sauciavit <i>C</i>);\n<b>3)</b> рыть, бороздить, вспахивать (humum vomere <i>O</i>);\n<b>4)</b> подрывать, наносить ущерб, порочить (s. facta et famam <i>Pl</i>)."],
  ["saucius", "<big>saucius</big>\n\n<b>saucius, a, um</b>\n<b>1)</b> раненый (graviter, leviter <i>C</i>): s. umero <i>bH, QC</i> раненный в плечо; s. (vulnere <i>V</i>) pectus (<i>acc. gr.</i>) <i>Tib</i> раненный в грудь;\n<b>2)</b> изрытый, изборождённый, вспаханный (tellus saucia vomeribus <i>O</i>);\n<b>3)</b> обуреваемый, терзаемый (amore <i>Enn, Lcr</i>);\n<b>4)</b> захмелевший, опьянённый (mero <i>Just</i>; s. ebriusque <i>Pt</i>);\n<b>5)</b> утомлённый, разбитый (fatigationis hesternae s. <i>Ap</i>); измученный (fauces fame sauciae <i>Ap</i>); размягчённый, расслабленный (glacies saucia sole <i>O</i>);\n<b>6)</b> поражённый (formidine <i>Cld</i>); задетый, уязвлённый (dictis <i>St</i>);\n<b>7)</b> повреждённый, сломленный (malus s. Africo <i>H</i>);\n<b>8)</b> опороченный: s. de repetundis <i>Caelius ap. C</i> находящийся под подозрением в лихоимстве;\n<b>9)</b> удручённый, терзаемый (gravi cura <i>V</i>)."],
  ["Saufeius", "<big>Saufeius</big>\n\n<b>Saufēius, a, um</b>\nСауфей, <i>римск. nomen</i> <i>C, J</i>."],
  ["saurion", "<big>saurion</big>\n\n<b>saurion, ī</b> <i>n</i> (<i>греч.</i>)\nгорчица <i>PM</i>."],
  ["Sauroctonos", "<big>Sauroctonos</big>\n\n<b>Sauroctonos, ī</b> <i>m</i> (<i>греч.</i>)\nубивающий ящерицу, <i>эпитет Аполлона — согласно известной статуе Праксителя</i> <i>PM, M</i>."],
  ["Sauromat-", "<big>Sauromat-</big>\n\n<b>Sauromat-</b> <i>O, St etc.</i>\n= Sarmat-."],
  ["sav-", "<big>sav-</big>\n\n<b>sav-</b> <i>v. l.</i>\n= suav-."],
  ["savanum", "<big>savanum</big>\n\n<b>savanum</b> <i>v. l.</i>\n= sabanum."],
  ["saviatio", "<big>saviatio</big>\n\n<b>saviatio, onis</b> <i>v. l.</i>\n= suaviatio."],
  ["savillum", "<big>savillum</big>\n\n<b>savillum, i</b> <i>n</i>\nпирожное с сыром и мёдом <i>Cato</i>."],
  ["saviunculum", "<big>saviunculum</big>\n\n<b>saviunculum, i</b> <i>n</i> [<i>demin. к</i> savium = suavium]\n«поцелуйчик», <i>род печенья на меду</i> <i>Pt</i>."],
  ["saxatilis", "<big>saxatilis</big>\n\n<b>saxatilis, e</b> [saxum]\n<b>1)</b> живущий среди скал, любящий каменистые места (pisces <i>Col</i>; columbae <i>Vr</i>): piscatus s. <i>Pl</i> ловля рыбы между камнями;\n<b>2)</b> каменный, из камней (imber <i>Sol</i>)."],
  ["Saxetanum", "<big>Saxetanum</big>\n\n<b>Saxetanum, i</b> <i>n</i>\n<i>город в Hispania Baetica</i> (<i>см.</i> Saxetanus)."],
  ["Saxetanus", "<big>Saxetanus</big>\n\n<b>Saxetanus, a, um</b> [Saxetanum]\nсаксетанский (lacertus, <i>sc.</i> piscis <i>M</i>)."],
  ["saxetum", "<big>saxetum</big>\n\n<b>saxetum, i</b> <i>n</i> [saxum]\nскалистое (каменистое) место <i>C</i>."],
  ["saxeus", "<big>saxeus</big>\n\n<b>saxeus, a, um</b> [saxum]\n<b>1)</b> каменный (tecta <i>Lcr</i>, Niobe saxea facta <i>O</i>): umbra saxea <i>V</i> тень, отбрасываемая скалами;\n<b>2)</b> подобный камню по твёрдости (dentes <i>Ap</i>) <i>или</i> жестокости (<i>sc.</i> homo <i>PJ</i>);\n<b>3)</b> текущий среди камней <i>или</i> низвергающийся со скал (Anien <i>St</i>)."],
  ["saxialis", "<big>saxialis</big>\n\n<b>saxialis, e</b> [saxum]\nкаменный (terminus <i>Frontin</i>)."],
  ["saxifer", "<big>saxifer</big>\n\n<b>saxifer, fera, ferum</b> [saxum + fero]\nнесущий <i>или</i> мечущий камни: saxifera habena <i>VF</i> праща."],
  ["saxificus", "<big>saxificus</big>\n\n<b>saxificus, a, um</b> [saxum + facio]\nпревращающий в камень (Medusa <i>O</i>)."],
  ["saxifragum", "<big>saxifragum</big>\n\n<b>saxifragum, i</b> <i>n</i> <i>бот.</i>\nкамнеломка <i>PM</i>."],
  ["saxifragus", "<big>saxifragus</big>\n\n<b>saxifragus, a, um</b> [saxum + frango]\nразбивающий камни, ломающий скалы (undae <i>Enn</i>)."],
  ["saxitas", "<big>saxitas</big>\n\n<b>saxitas, atis</b> <i>f</i>\nтвёрдость камня <i>CA</i>."],
  ["Saxon", "<big>Saxon</big>\n\n<b>Saxo(n), onis</b> <i>m</i>\nсакс, <i>человек из германск. племени саксов</i> <i>Cld, Amm</i>."],
  ["saxositas", "<big>saxositas</big>\n\n<b>saxositas, atis</b> <i>f</i> <i>CA</i>\n= saxitas."],
  ["saxosus", "<big>saxosus</big>\n\n<b>saxosus, a, um</b> [saxum]\n<b>1)</b> скалистый, каменистый, утёсистый (montes <i>V</i>);\n<b>2)</b> растущий среди камней (frutex <i>PM</i>);\n<b>3)</b> текущий среди скал, струящийся по камням (Hypanis <i>V</i>)."],
  ["saxulum", "<big>saxulum</big>\n\n<b>saxulum, i</b> <i>n</i> [<i>demin. к</i> saxum]\nнебольшая скала <i>C</i>."],
  ["saxum", "<big>saxum</big>\n\n<b>I saxum, i</b> <i>n</i>\n<b>1)</b> скала (dejicere saxo <i>T</i> <i>и</i> e saxo <i>H</i>); камень (templa saxo struere <i>V</i>); глыба (e saxo sculpere aliquid <i>C</i>): saxa latentia <i>V</i> подводные камни; saxa concava <i>O</i> пещеры в скалах; s. silex <i>Vtr, L</i> кремень; s. quadratum <i>L</i> плитняк;\n<b>2)</b> каменная стена (aliquid saxo circumdare <i>O</i>);\n<b>3)</b> жертвенный (каменный) нож (inter sacrum saxumque <i>Pl</i> —<i>см.</i> sacrum 3).\n\n<b>II Saxum, i</b> <i>m</i>\n<b>1)</b> (<i>sc.</i> Tarpeium <i>C, L, H, Prp</i>) Тарпейская скала (<i>на Капитолии</i>);\n<b>2)</b> S. Sacrum <i>C, O</i> Священная скала (<i>на вершине Авентина, где Рем совершил ауспиции</i>);\n<b>3)</b> Rubra Saxa <i>C, L, T, M</i> Красные скалы, <i>скалистая местность и город в Этрурии на via Flaminia, у реки Cremera.</i>"],
  ["scabellum", "<big>scabellum</big>\n\n<b>scabellum (scabinum), i</b> <i>n</i> [<i>demin. к</i> scamnum]\n<b>1)</b> небольшая скамейка, скамеечка <i>или</i> низкая табуретка для ног <i>Cato, Vr</i>;\n<b>2)</b> высокая деревянная подошва с ударным приспособлением внутри (<i>род ножных кастаньет для отбивания такта, для объявления о начале или окончании действия театральной пьесы и т. п.</i>) (scabilla concrepant — aulaeum tollitur <i>C</i>)."],
  ["scabellus", "<big>scabellus</big>\n\n<b>scabellus, i</b> <i>m</i> <i>Is</i>\n= scabellum."],
  ["scaber", "<big>scaber</big>\n\n<b>scaber, bra, brum</b> [scabo]\n<b>1)</b> шероховатый, негладкий (unguis <i>O, CC</i>; lapis <i>PM</i>); <i>перен.</i> грубый, нескладный (versus <i>Macr</i>);\n<b>2)</b> неопрятный, нечистоплотный, грязный (<i>sc.</i> homo <i>H</i>; manus <i>O</i>);\n<b>3)</b> покрытый паршой <i>или</i> чесоточный (ovis <i>Pl, Cato, Col</i>)."],
  ["scabidus", "<big>scabidus</big>\n\n<b>scabidus, a, um</b> [scabies]\nпокрытый паршой <i>или</i> чесоточный, <i>перен.</i> зудящий, нестерпимый (concupiscentia <i>Tert</i>)."],
  ["scabies", "<big>scabies</big>\n\n<b>scabies, ei</b> <i>f</i> [scabo]\n<b>1)</b> шероховатость, шершавость (ferri, <i>sc.</i> robiginosi <i>V</i>);\n<b>2)</b> нечистота, грязь (s. vetusta testae <i>Ap</i>);\n<b>3)</b> парша, чесотка <i>или</i> струпья, сыпь (ovium <i>V</i>);\n<b>4)</b> <i>перен.</i> зуд, сильное влечение (s. lucri <i>H</i>); похоть <i>M, Aug</i>."],
  ["scabillum", "<big>scabillum</big>\n\n<b>scabillum, i</b> <i>n</i> <i>Cato etc.</i>\n= scabellum."],
  ["scabiola", "<big>scabiola</big>\n\n<b>scabiola, ae</b> <i>f</i> [scabies]\nзуд, <i>перен.</i> похоть <i>Aug</i>."],
  ["scabiosus", "<big>scabiosus</big>\n\n<b>scabiosus, a, um</b> [scabies]\n<b>1)</b> шероховатый, шершавый (coralium <i>PM</i>);\n<b>2)</b> испорченный (far <i>Pers</i>);\n<b>3)</b> покрытый паршой, струпьями <i>или</i> чесоточный (bos <i>Col</i>)."],
  ["scabitudo", "<big>scabitudo</big>\n\n<b>scabitudo, inis</b> <i>f</i> [scabies]\nзуд, раздражение, <i>перен.</i> проказа, зараза (omnem scabitudinem animo delere <i>Pt</i>)."],
  ["scabo", "<big>scabo</big>\n\n<b>scabo, scabi, —, ere</b>\n<b>1)</b> чесать, почёсывать (caput <i>LM, H</i>): aures alicujus s. <i>Sen, PM</i> почёсывать за ухом, <i>т. е.</i> приятно щекотать, <i>перен.</i> <i>Sen</i> ласкать слух приятной беседой;\n<b>2)</b> скоблить, выскабливать (laminas doliorum <i>PM</i>); скрести (tellurem pedibus <i>PM</i>)."],
  ["scabratus", "<big>scabratus</big>\n\n<b>scabratus, a, um</b> [scaber]\nсделанный шероховатым, выщербленный (vitis <i>Col</i>)."],
  ["scabredo", "<big>scabredo</big>\n\n<b>scabredo, inis</b> <i>f</i> <i>Hier</i>\n= scabritia."],
  ["scabreo", "<big>scabreo</big>\n\n<b>scabreo, —, —, ere</b> [scaber]\nбыть шероховатым (ostreis squamae scabrent <i>Enn</i>)."],
  ["scabres", "<big>scabres</big>\n\n<b>scabres, is</b> <i>f</i> <i>Vr</i>\n= scabritia."],
  ["scabritia", "<big>scabritia</big>\n\n<b>scabritia, ae</b> <i>f</i> [scaber]\n<b>1)</b> шероховатость, шершавость, негладкость (chartae, unguium <i>PM</i>);\n<b>2)</b> парша <i>или</i> сыпь <i>Col</i>."],
  ["scabrities", "<big>scabrities</big>\n\n<b>scabrities, ei</b> <i>f</i> <i>Col</i>\n= scabritia."],
  ["scabrosus", "<big>scabrosus</big>\n\n<b>scabrosus, a, um</b> <i>Eccl</i>\n= scabiosus 1."],
  ["Scaea porta", "<big>Scaea porta</big>\n\n<b>Scaea porta, ae</b> <i>f</i> (<i>тж.</i> <i>pl.</i>) (<i>греч.</i>)\nСкейские (Западные) ворота (<i>в Трое</i>) <i>V</i>."],
  ["scaena", "<big>scaena</big>\n\n<b>scaena, ae</b> <i>f</i> (<i>греч.</i>)\n<b>1)</b> сцена, эстрада, театральные подмостки, театр (in scaenam prodire <i>Nep, Su</i>): in scaena esse <i>Pl</i> быть актёром; scaenae ostentatio <i>L</i> театральная постановка;\n<b>2)</b> открытая спереди поляна (silvis s. coruscis <i>V</i>);\n<b>3)</b> поле деятельности, поприще (maxima s. oratori est contio <i>C</i>);\n<b>4)</b> общественность, широкая публика: scaenae servire <i>C</i> выступать публично, показываться <i>или</i> быть на людях; minus in scaena esse <i>C</i> меньше бросаться в глаза;\n<b>5)</b> внешний вид, видимость: scaenam criminis parare <i>T</i> создавать видимость преступления, <i>т. е.</i> построить ложное обвинение;\n<b>6)</b> внешний блеск, показная пышность (s. pompaque <i>C</i>)."],
  ["scaenarius", "<big>scaenarius</big>\n\n<b>scaenārius, a, um</b> <i>Amm</i>, <b>scaenāticus, a, um</b> <i>Vr</i> <i>и</i> <b>scaenātilis, e</b> <i>Vr</i>\n= scaenicus I."],
  ["scaenica", "<big>scaenica</big>\n\n<b>scaenica, ae</b> <i>f</i>\nактриса <i>CJ, Eccl</i>."],
  ["scaenice", "<big>scaenice</big>\n\n<b>scaenicē</b> [scaenicus I]\nкак на сцене, театрально <i>Q</i>."],
  ["scaenicus", "<big>scaenicus</big>\n\n<b>I scaenicus, a, um</b> [scaena]\n<b>1)</b> сценический, театральный (ludi <i>L</i>): s. artifex <i>C</i> актёр;\n<b>2)</b> представляемый на сцене, инсценированный (adulteria <i>O</i>).\n\n<b>II scaenicus, ī</b> <i>m</i>\n<b>1)</b> актёр <i>C, Q etc.</i>;\n<b>2)</b> <i>презр.</i> притворщик, кривляка (ille s. <i>T</i> = Nero)."],
  ["scaenofactorius", "<big>scaenofactorius</big>\n\n<b>scaeno–factōrius, a, um</b>\nизготовляющий шатры (ars <i>Vlg</i>)."],
  ["scaenographia", "<big>scaenographia</big>\n\n<b>scaenographia, ae</b> <i>f</i> (<i>греч.</i>) <i>архит.</i>\nперспективный план <i>Vtr</i>."],
  ["scaenopegia", "<big>scaenopegia</big>\n\n<b>scaenopēgia, ae</b> <i>f</i> <i>греч.</i>\nпраздник Кущей <i>Vlg</i>."],
  ["scaeptrum", "<big>scaeptrum</big>\n\n<b>scaeptrum, ī</b> <i>n</i> <i>v. l.</i>\n= sceptrum."],
  ["scaeva", "<big>scaeva</big>\n\n<b>I scaeva, ae</b> <i>m</i> [scaevus]\nлевша <i>Sen, Dig</i>.\n\n<b>II scaeva, ae</b> <i>f</i>\nпримета, знамение, предзнаменование (bona s. est mihi <i>Pl</i>).\n\n<b>III Scaeva, ae</b> <i>m</i>\nСцева, <i>римск. cognomen</i> <i>Cs, L, H</i>."],
  ["scaevitas", "<big>scaevitas</big>\n\n<b>scaevitās, ātis</b> <i>f</i> [scaevus]\n<b>1)</b> неумелость, бездарность (male doctorum hominum s. et invidentia <i>AG</i>);\n<b>2)</b> превратность (fortunae <i>Ap</i>); неудача, несчастье <i>Ap</i>."],
  ["Scaevola", "<big>Scaevola</big>\n\n<b>Scaevola, ae</b> <i>m</i> [<i>demin. к</i> Scaeva]\nСцевола, <i>cognomen в роде Муциев</i> <i>L, C</i>."],
  ["scaevus", "<big>scaevus</big>\n\n<b>scaevus, a, um</b>\n<b>1)</b> левый, направленный влево (itinera portarum <i>Vtr</i>);\n<b>2)</b> неловкий, неуклюжий, неумелый (homo <i>AG</i>; fabula <i>PM</i>);\n<b>3)</b> неудачный, несчастный, прискорбный (eventus <i>Ap</i>); жуткий, страшный (somnium <i>Ap</i>)."],
  ["scalae", "<big>scalae</big>\n\n<b>scalae, arum</b> (<i>sg.</i> <i>очень редко</i>: de vitiis nostris scalam nobis facimus <i>Aug</i>) <i>f</i> [scando]\n<b>1)</b> лестница: scalis habitare tribus <i>M</i> жить на третьем этаже, <i>т. е.</i> на чердаке;\n<b>2)</b> приставная лестница (scalas admovere, <i>sc.</i> muris <i>или</i> moenibus <i>Cs etc.</i>; murum scalis aggredi <i>Sl</i>);\n<b>3)</b> ступеньки (per ducentas scalas <i>M</i>)."],
  ["scalaria", "<big>scalaria</big>\n\n<b>scalaria, ium</b> <i>n</i> <i>Vtr</i>\n= scalae."],
  ["scalaris", "<big>scalaris</big>\n\n<b>scalaris, e</b> [scalae]\nимеющий форму лестницы, ступенчатый (forma <i>Vtr</i>)."],
  ["Scaldis", "<big>Scaldis</big>\n\n<b>Scaldis, is</b> <i>m</i>\nСкальд, <i>самая большая река в Gallia Belgica</i> (<i>ныне</i> Шельда, <i>франц.</i> Escaut) <i>Cs, PM</i>."],
  ["scalenus", "<big>scalenus</big>\n\n<b>scalenus, a, um</b> (<i>греч.</i>)\nнеравносторонний (<i>sc.</i> figura <i>Aus</i>)."],
  ["scalmus", "<big>scalmus</big>\n\n<b>scalmus, i</b> <i>m</i> (<i>греч.</i>)\nуключина <i>Vtr, VP</i>: navicula duorum scalmorum <i>C</i> двухвесельное судёнышко; scalmum nullum videre <i>C</i> не видеть и следа лодки."],
  ["scalpellum", "<big>scalpellum</big>\n\n<b>scalpellum, i</b> <i>n</i> [<i>demin. к</i> scalprum]\nножичек, скальпель, ланцет <i>C, Col, Sen etc.</i>"],
  ["scalpellus", "<big>scalpellus</big>\n\n<b>scalpellus, i</b> <i>m</i> [<i>demin. к</i> scalper] <i>CC</i>\n= scalpellum."],
  ["scalper", "<big>scalper</big>\n\n<b>scalper, pri</b> <i>m</i> <i>CC</i>\n= scalprum."],
  ["scalpo", "<big>scalpo</big>\n\n<b>scalpo, scalpsi, scalptum, ere</b>\n<b>1)</b> царапать, скоблить, скрести (aliquid unguibus <i>H</i>); чесать, почёсывать (caput digito uno <i>J</i>);\n<b>2)</b> пахать, возделывать: ager nec vinitori, nec olitori scalpitur <i>Ap</i> земля не годится ни под виноградники, ни под огороды;\n<b>3)</b> <i>перен.</i> щекотать, возбуждать (tremulo versu <i>Pers</i>);\n<b>4)</b> вырезывать, гравировать, высекать (gemmas <i>PM</i>; aliquid sepulcro alicujus <i>H</i>)."],
  ["scalpratus", "<big>scalpratus</big>\n\n<b>scalpratus, a, um</b> [scalprum]\nимеющий вид садовничьего ножа, снабжённый лезвием (ferramentum <i>Col</i>)."],
  ["scalprum", "<big>scalprum</big>\n\n<b>scalprum, i</b> <i>n</i> [scalpo]\nрежущий инструмент (резец, скальпель, садовничий нож, сапожный резак) <i>Cato, Col, L, H, Su, T</i>."],
  ["scalprus", "<big>scalprus</big>\n\n<b>scalprus, i</b> <i>m</i> <i>Is</i>\n= scalprum."],
  ["scalpsi", "<big>scalpsi</big>\n\n<b>scalpsi</b> <i>pf. к</i>\nscalpo."],
  ["scalptor", "<big>scalptor</big>\n\n<b>scalptor, oris</b> <i>m</i> [scalpo]\nрезчик, гравёр (pictor scalptorque <i>PJ</i>)."],
  ["scalptorium", "<big>scalptorium</big>\n\n<b>scalptorium, i</b> <i>n</i> [scalpo]\nскальпторий, банный скребок, <i>приспособление в форме руки для чесания тела</i> <i>M</i>."],
  ["scalptura", "<big>scalptura</big>\n\n<b>scalptura, ae</b> <i>f</i> [scalpo]\n<b>1)</b> вырезывание, гравировка <i>PM</i>;\n<b>2)</b> резная работа, резьба <i>Vtr, Su</i>."],
  ["scalpturio", "<big>scalpturio</big>\n\n<b>scalpturio (scalpurio), —, —, ire</b> <i>v. l.</i>\n= scalpurrio."],
  ["scalpurrigo", "<big>scalpurrigo</big>\n\n<b>scalpurrigo, inis</b> <i>f</i> [scalpurrio]\nпочёсывание (s. blandiens <i>Sol</i>)."],
  ["scalpurrio", "<big>scalpurrio</big>\n\n<b>scalpurrio, —, —, ire</b> [scalpo]\nцарапать, скрести (ungulis <i>Pl</i>)."],
  ["Scamander", "<big>Scamander</big>\n\n<b>Scamander, dri</b> <i>m</i>\nСкамандр:\n<b>1)</b> (<i>или</i> Xanthus) <i>река в Троаде, впадающая в Геллеспонт у Сигея</i> <i>V, PM, Ctl etc.</i>;\n<b>2)</b> <i>имя вольноотпущенника</i> <i>C</i>."],
  ["Scamandrius", "<big>Scamandrius</big>\n\n<b>Scamandrius, a, um</b> [Scamander]\nскамандровский (unda <i>Acc</i>)."],
  ["scambus", "<big>scambus</big>\n\n<b>scambus, a, um</b> (<i>греч.</i>; <i>лат.</i> varus)\nкривоногий <i>Su</i>."],
  ["scamellum", "<big>scamellum</big>\n\n<b>scamellum (scamilium), i</b> <i>n</i> [<i>demin. к</i> scamnum]\nскамеечка <i>Ap</i>."],
  ["scamillus", "<big>scamillus</big>\n\n<b>scamillus, i</b> <i>m</i>\nцоколь (колонны) <i>Vtr</i>."],
  ["scamma", "<big>scamma</big>\n\n<b>scamma, atis</b> <i>n</i> (<i>греч.</i>)\n<b>1)</b> посыпанная песком и окружённая рвом гимнастическая площадка <i>Hier</i>;\n<b>2)</b> <i>перен.</i> борьба <i>Tert</i>."],
  ["scammonea", "<big>scammonea</big>\n\n<b>scammonea (scammonia), ae</b> <i>f</i> <i>и</i> <b>scammonium, i</b> <i>n</i> (<i>греч.</i>) <i>бот.</i>\nскаммония (<i>Convolvulus scammonia</i>, L.), <i>разновидность вьюнка, сок которого употреблялся в качестве слабительного</i> <i>C, PM, Cato</i>."],
  ["scamnum", "<big>scamnum</big>\n\n<b>scamnum, i</b> <i>n</i>\n<b>1)</b> скамья, скамейка <i>Vr, O, M etc.</i>;\n<b>2)</b> престол, трон (s. regni <i>Enn ap. C</i>);\n<b>3)</b> нетронутая полоса земли (между двумя вспаханными) <i>Col, PM</i>."],
  ["scandala", "<big>scandala</big>\n\n<b>scandala, ae</b> <i>f</i>\nполба (s. — genus farris <i>PM</i>)."],
  ["scandalizo", "<big>scandalizo</big>\n\n<b>scandalizo, avi, atum, are</b> (<i>греч.</i>)\n<b>1)</b> вводить в соблазн (дурным примером) (aliquem <i>Eccl</i>);\n<b>2)</b> возмущать (aliquem <i>Eccl</i>)."],
  ["scandalum", "<big>scandalum</big>\n\n<b>scandalum, i</b> <i>n</i> (<i>греч.</i>)\n<b>1)</b> преткновение <i>Eccl</i>;\n<b>2)</b> соблазн <i>Eccl</i>;\n<b>3)</b> предмет ужаса <i>или</i> возмущения <i>Eccl</i>."],
  ["Scandia", "<big>Scandia</big>\n\n<b>Scandia, ae</b> <i>f</i> <i>PM</i>\n= Scandinavia."],
  ["Scandinavia", "<big>Scandinavia</big>\n\n<b>Scandinavia, ae</b> <i>f</i>\nСкандинавия <i>PM</i>."],
  ["scando", "<big>scando</big>\n\n<b>scando, scandi, scansum, ere</b>\n<b>1)</b> восходить, подниматься, взбираться, влезать (muros <i>L</i>; in aggerem <i>L</i>; ad nidum <i>Ph</i>; equum <i>V</i>; Capitolium <i>H</i>; vallum <i>Cs</i>);\n<b>2)</b> возноситься, подниматься, возвышаться (supra aliquem <i>T</i>);\n<b>3)</b> достигать (gradus aetatis adultae <i>Lcr</i>);\n<b>4)</b> <i>грам.</i> скандировать, размеренно читать (versus <i>Cld</i>)."],
  ["scandula", "<big>scandula</big>\n\n<b>I scandula, ae</b> <i>f</i> [scindo]\nгонт, дранка (кровельная) <i>Hirt, PM, Vtr</i>.\n\n<b>II scandula, ae</b> <i>f</i> <i>Is</i>\n= scandala."],
  ["scandularis", "<big>scandularis</big>\n\n<b>scandularis, e</b> [scandula I]\nкрытый гонтом (tectum <i>Ap</i>)."],
  ["scandularius", "<big>scandularius</big>\n\n<b>scandularius, i</b> <i>m</i> [scandula I]\nкровельщик <i>Dig</i>."],
  ["scansilis", "<big>scansilis</big>\n\n<b>scansilis, e</b> [scando]\n<b>1)</b> удобный для влезания (ficus <i>PM</i>);\n<b>2)</b> уступообразный, ступенчатый, расположенный по ступеням (lex annorum <i>PM</i>)."],
  ["scansio", "<big>scansio</big>\n\n<b>scansio, onis</b> <i>f</i> [scando]\n<b>1)</b> восхождение, подъём (simplici scansione scandere <i>Vtr</i>): scansiones sonorum <i>Vtr</i> звукоряд, гамма;\n<b>2)</b> <i>грам.</i> скандировка."],
  ["scansorius", "<big>scansorius</big>\n\n<b>scansorius, a, um</b> [scando]\nподъёмный (genus machinarum) <i>Vtr</i>."],
  ["Scantia silva", "<big>Scantia silva</big>\n\n<b>Scantia silva</b> <i>f</i>\nСкантийскик лес (<i>в Кампании, с aquae Scantiae, близ которых из земли вырывался огонь</i>) <i>C, PM</i>."],
  ["Scantinius", "<big>Scantinius</big>\n\n<b>Scantinius</b> <i>и</i> <b>Scantius, a, um</b>\nСкантиний <i>и</i> Скантий, <i>римск. nomina</i> <i>C, VM, Su, PM</i>."],
  ["scapha", "<big>scapha</big>\n\n<b>scapha, ae</b> <i>f</i> (<i>греч.</i>)\nлодка, челнок <i>Pl, C, H</i>."],
  ["scaphe", "<big>scaphe</big>\n\n<b>scaphe, es</b> <i>f</i> (<i>греч.</i>)\nладьеобразно вогнутый циферблат солнечных часов <i>Vtr</i>."],
  ["scaphisterium", "<big>scaphisterium</big>\n\n<b>scaphisterium, i</b> <i>n</i> (<i>греч.</i>)\nручная веялка <i>Col</i>."],
  ["scaphium", "<big>scaphium</big>\n\n<b>scaphium, i</b> <i>n</i> (<i>греч.</i>)\nладьеобразный сосуд, чаша <i>Lcr, Pl, C, M, J</i>."],
  ["scapho", "<big>scapho</big>\n\n<b>scapho, onis</b> <i>m</i> (<i>греч.</i>)\nносовой (корабельный) канат <i>Caec</i>."],
  ["scaphula", "<big>scaphula</big>\n\n<b>scaphula, ae</b> <i>f</i> [scapha]\n<b>1)</b> лодочка, челнок <i>Veg, Eccl</i>;\n<b>2)</b> ванна <i>CA</i>."],
  ["scapium", "<big>scapium</big>\n\n<b>scapium, i</b> <i>n</i> <i>v. l.</i>\n= scaphium."],
  ["scapr-", "<big>scapr-</big>\n\n<b>scapr-</b> <i>v. l.</i>\n= scabr-."],
  ["Scaptensula", "<big>Scaptensula</big>\n\n<b>Scaptensula, ae</b> <i>f</i> (<i>греч.</i> Skaptesyle)\nСкаптенсула, <i>городок в Македонии близ границы с Фракией</i> <i>Lcr</i>."],
  ["Scaptia", "<big>Scaptia</big>\n\n<b>Scaptia, ae</b> <i>f</i>\nСкаптия, <i>старинный, уже в древности исчезнувший, город в Латии</i> <i>PM</i>."],
  ["Scaptiensis", "<big>Scaptiensis</big>\n\n<b>Scaptiensis, e</b>\nпринадлежащий к Скаптийской трибе (<i>см.</i> Scaptius) <i>Su</i>."],
  ["Scaptius", "<big>Scaptius</big>\n\n<b>Scaptius, a, um</b> [Scaptia]\nскаптийский: Scaptia tribus <i>L</i> Скаптийская триба (<i>в Риме</i>)."],
  ["Scapula", "<big>Scapula</big>\n\n<b>Scapula, ae</b> <i>m</i>\n(«плечо») Скапула, <i>римск. cognomen в родах Квинкциев и Корнелиев</i> <i>C, Q</i>."],
  ["scapulae", "<big>scapulae</big>\n\n<b>scapulae, arum</b> <i>f</i> (<i>sg.</i> e <i>Vlg</i>)\n<b>1)</b> плечевые лопатки, плечи <i>Vr, PM</i>;\n<b>2)</b> спина (scapulas alicujus verberare <i>Pt</i>);\n<b>3)</b> верхняя часть, верх (machinae <i>Vtr</i>); хребет <i>или</i> вершина (montium <i>Tert</i>)."],
  ["Scapulanus", "<big>Scapulanus</big>\n\n<b>Scapulanus, a, um</b> <i>C adj. к</i>\nScapula."],
  ["scapus", "<big>scapus</big>\n\n<b>scapus, i</b> <i>m</i>\n<b>1)</b> стебель (lupini <i>Vr</i>);\n<b>2)</b> стержень (columnae <i>Vtr</i>);\n<b>3)</b> древко (hastae <i>Treb</i>);\n<b>4)</b> ткацкий навой (scapi sonantes <i>Lcr</i>);\n<b>5)</b> коромысло (trutinae <i>Vtr</i>);\n<b>6)</b> <i>архит.</i> тетива (s. scalarum <i>Vtr</i>);\n<b>7)</b> валик для рукописных свитков <i>Vr, PM</i>;\n<b>8)</b> дверной брус, косяк <i>Vtr</i>;\n<b>9)</b> <i>Veg, Aug</i> = membrum virile."],
  ["scarabaeus", "<big>scarabaeus</big>\n\n<b>scarabaeus, i</b> <i>m</i> (<i>греч.</i>)\nжук <i>PM, Aus</i>."],
  ["scarifatio", "<big>scarifatio</big>\n\n<b>scarifatio, onis</b> <i>f</i> [scarifo]\n<b>1)</b> надрезывание, насекание <i>Col, PM</i>;\n<b>2)</b> поверхностная вспашка <i>PM</i>."],
  ["scarificatio", "<big>scarificatio</big>\n\n<b>scarificatio, onis</b> <i>f</i> <i>Veg, PM</i>\n= scarifatio."],
  ["scarifico", "<big>scarifico</big>\n\n<b>scarifico, avi, atum, are</b> <i>Pall</i>\n= scarifo."],
  ["scarifo", "<big>scarifo</big>\n\n<b>scarifo, avi, atum, are</b>\nделать насечки, надрезать (s. gingivas <i>PM</i>)."],
  ["scariph-", "<big>scariph-</big>\n\n<b>scariph-</b> <i>v. l.</i>\n= scarif-."],
  ["scarus", "<big>scarus</big>\n\n<b>scarus, i</b> <i>m</i> (<i>греч.</i>)\nскар, <i>морская рыба, высоко ценившаяся римск. гастрономами</i> <i>Enn, H, PM, Su, Pt</i>."],
  ["scatebra", "<big>scatebra</big>\n\n<b>scatebra, ae</b> <i>f</i> [scateo]\n<b>1)</b> бурление, клокотание (fluvii <i>Acc</i>);\n<b>2)</b> бьющая ключом вода (fontis <i>PM</i>); <i>pl.</i> каскады <i>V</i>."],
  ["scateo", "<big>scateo</big>\n\n<b>scateo, —, —, ere</b>\n<b>1)</b> бить ключом (fons scatet <i>Poeta ap. C</i>);\n<b>2)</b> изобиловать (metallis scatet Hispania <i>PM</i>; s. verbis (<i>abl.</i>), <i>но</i> irarum <i>AG</i>);\n<b>3)</b> кишеть (pontus scatens beluis <i>H</i>)."],
  ["Scatin-", "<big>Scatin-</big>\n\n<b>Scatin-</b> <i>v. l.</i>\n= Scantin-."],
  ["scato", "<big>scato</big>\n\n<b>scato, —, —, ere</b> <i>арх. Enn, Lcr</i>\n= scateo."],
  ["scaturex", "<big>scaturex</big>\n\n<b>scaturex, igis</b> <i>m</i> <i>Vr, L</i>\n= scaturigo."],
  ["scaturiginosus", "<big>scaturiginosus</big>\n\n<b>scaturiginosus, a, um</b> [scaturigo]\nпокрытый бьющей из земли водой, заболоченный (terra <i>Col</i>)."],
  ["scaturigo", "<big>scaturigo</big>\n\n<b>scaturigo, inis</b> <i>f</i> [scateo]\n<b>1)</b> бьющая ключом вода, ключ, родник (turbida <i>L</i>);\n<b>2)</b> поток (sanguinis <i>Amm</i>); множество, туча (formicarum <i>Ap</i>)."],
  ["scaturio", "<big>scaturio</big>\n\n<b>scaturio, ivi, —, ire</b> [scateo]\n<b>1)</b> бить ключом (aqua scaturiens <i>Pall</i>); низвергаться бурным потоком (de summo vertice fons scaturit <i>Ap</i>);\n<b>2)</b> изобиловать (fontibus <i>Col</i>): s. aliqua re <i>Caelius ap. C</i> быть целиком поглощённым чём-л."],
  ["scaturr-", "<big>scaturr-</big>\n\n<b>scaturr-</b> <i>v. l.</i>\n= scatur-."],
  ["scaurus", "<big>scaurus</big>\n\n<b>I scaurus, a, um</b> (<i>греч.</i>)\nкосолапый <i>H</i>.\n\n<b>II Scaurus, i</b> <i>m</i>\nСкавр, <i>cognomen в родах Эмилиев и Аврелиев</i> <i>C etc.</i>"],
  ["scazon", "<big>scazon</big>\n\n<b>scazon, ontis</b> <i>m</i> (<i>греч.</i>)\nхромающий ямб (холиамб), <i>триметр, последняя стопа которого не ямб, а спондей или трохей</i> <i>PM, M</i>."],
  ["scelerate", "<big>scelerate</big>\n\n<b>scelerate</b> [sceleratus]\nзлодейски, преступно, преступным образом <i>C, Vlg</i>."],
  ["sceleratus", "<big>sceleratus</big>\n\n<b>sceleratus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> scelero;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> осквернённый злодеяниями, запятнанный преступлением, греховный (terra <i>V, O</i>): S. campus <i>L</i> <i>место в Риме у porta Collina, где погребались заживо весталки, нарушившие обет целомудрия</i>; S. vicus <i>Vr, L</i> <i>улица в Риме, где Туллия, жена Тарквиния Гордого, переехала через труп своего отца</i>; sedes scelerata <i>Tib, O</i> <i>и</i> sceleratum limen <i>V</i> местопребывание грешников в подземном царстве, ад;\n<b>2)</b> преступный, злодейский (homo <i>C</i>; audacia <i>Just</i>; amor habendi <i>O</i>): sceleratae poenae <i>V</i> наказания за преступления;\n<b>3)</b> гибельный, губительный (frigus <i>V</i>): scelerata castra <i>Su</i> роковой лагерь (<i>в котором умер Друз, отец императора Клавдия</i>) || злокачественный (lues <i>M</i>); отвратительный (poemata <i>M</i>)."],
  ["sceleritas", "<big>sceleritas</big>\n\n<b>sceleritas, atis</b> <i>f</i>\nпреступность (facti <i>Dig</i>)."],
  ["scelero", "<big>scelero</big>\n\n<b>scelero, (avi), atum, are</b> [scelus]\n<b>1)</b> осквернять злодеянием, запятнать преступлением (manus <i>V</i>);\n<b>2)</b> отравлять, напоить ядом (scelerata — <i>sc.</i> venenospicula <i>Sil</i>)."],
  ["scelerosus", "<big>scelerosus</big>\n\n<b>scelerosus, a, um</b> <i>Ter, Lcr, Ap</i>\n= scelestus."],
  ["sceleste", "<big>sceleste</big>\n\n<b>sceleste</b> [scelestus]\n<b>1)</b> злодейски, преступно, бесчестно (facere <i>L</i>);\n<b>2)</b> плутовато, хитро (suspicari <i>C</i>)."],
  ["scelestus", "<big>scelestus</big>\n\n<b>scelestus, a, um</b> [scelus]\nзлодейский, преступный, нечестивый, бесчестный (homo <i>Pl, C, Sl etc.</i>; facinus <i>C</i>; sermo <i>L</i>)."],
  ["sceletus", "<big>sceletus</big>\n\n<b>sceletus, i</b> <i>m</i> (<i>греч.</i>)\nосвобождённое от внутренностей и высушенное тело, мумия, <i>по др.</i>— скелет <i>Ap</i>."],
  ["scelio", "<big>scelio</big>\n\n<b>scelio, onis</b> <i>m</i> [scelus]\nзлодей, нечестивец, негодяй <i>Pt</i> (<i>v. l.</i>)."],
  ["scelotyrbe", "<big>scelotyrbe</big>\n\n<b>scelotyrbe, es</b> <i>f</i> (<i>греч.</i>)\nзаболевание коленных суставов <i>PM</i>."],
  ["scelus", "<big>scelus</big>\n\n<b>scelus, eris</b> <i>n</i>\n<b>1)</b> злодеяние, преступление: s. humanum <i>L</i> преступление против человечества; s. ac vis <i>L</i> гнусное насилие; facere (committere, admittere, edere, concipere, suscipere) s. <i>C etc.</i> совершить преступление; legatorum interfectorum s. <i>L</i> преступное убийство послов; divinum et humanum s. <i>L</i> преступление против богов и людей; scelere se obstringere (devincire) <i>C</i> запятнать себя преступлениями; expendere s. <i>V</i> претерпеть наказание за преступление;\n<b>2)</b> <i>pl.</i> преступные слова, бесчестные речи (audi, Juppiter, haec scelera! <i>L</i>);\n<b>3)</b> порочность, коварство, злоба (bestiarum <i>PM</i>);\n<b>4)</b> (<i>тж.</i> s. viri <i>Pl</i>) негодяй: s. artificis <i>V</i> интриган;\n<b>5)</b> беда, бедствие, несчастье (scelera naturae <i>PM</i>): quod hoc est s.! <i>Pl</i> что это за напасть такая!"],
  ["scema", "<big>scema</big>\n\n<b>scema</b> <i>Sen</i>\n= schema."],
  ["scen-", "<big>scen-</big>\n\n<b>scen-</b> <i>v. l.</i>\n= scaen-."],
  ["Scepsis", "<big>Scepsis</big>\n\n<b>Scepsis, is</b> <i>f</i>\nСкепсис, <i>город в сев.-зап. Мисии, родина философа Академии и ритора Метродора</i> <i>PM</i>."],
  ["Scepsius", "<big>Scepsius</big>\n\n<b>Scepsius, a, um</b> [Scepsis]\nскепсийский <i>C, O</i>."],
  ["sceptrifer", "<big>sceptrifer</big>\n\n<b>sceptrifer, fera, ferum</b> [sceptrum + fero]\nнесущий скипетр, скиптроносный <i>O, Sen, St, Sil</i>."],
  ["sceptriger", "<big>sceptriger</big>\n\n<b>sceptriger, gera, gerum</b> <i>Sil</i>\n= sceptrifer."],
  ["sceptrum", "<big>sceptrum</big>\n\n<b>sceptrum, i</b> <i>n</i> (<i>греч.</i>; <i>лат.</i> scipio)\n<b>1)</b> жезл, скипетр (s. eburnus <i>O</i> <i>и</i> eburneus <i>Su</i>);\n<b>2)</b> царство, царская власть, царское достоинство (s. Asiae <i>O</i>): reponere aliquem in sceptra <i>V</i> снова возвести кого-л. на престол;\n<b>3)</b> <i>шутл.</i> дубинка (paedagogorum <i>M</i>);\n<b>4)</b> <i>Priap</i> = membrum virile."],
  ["sceptuchus", "<big>sceptuchus</big>\n\n<b>sceptuchus, i</b> <i>m</i> (<i>греч.</i>)\nскептух, жезлоносец, <i>один из первых сановников в странах Востока</i> <i>T</i>."],
  ["scheda", "<big>scheda</big>\n\n<b>scheda, schida</b> <i>и</i> <b>scida, ae</b> <i>f</i> (<i>греч.</i>)\n<b>1)</b> полоска папирусовой бумаги <i>PM</i>;\n<b>2)</b> лист, страница <i>C, Q, M</i>."],
  ["schedia", "<big>schedia</big>\n\n<b>schedia, ae</b> <i>f</i> (<i>греч.</i>)\n(<i>sc.</i> navis) наскоро сколоченное судно, <i>преим.</i> плот <i>PM, Dig</i>."],
  ["schedium", "<big>schedium</big>\n\n<b>schedium, i</b> <i>n</i> (<i>греч.</i>)\nэкспромтное стихотворение, поэтический экспромт <i>Pt, Ap, Sid, Aus</i>."],
  ["schedula", "<big>schedula</big>\n\n<b>schedula, ae</b> <i>f</i> [<i>demin. к</i> scheda]\nлисток, страничка <i>Hier</i>."],
  ["schema", "<big>schema</big>\n\n<b>schema, atis</b> <i>n</i> (<i>dat. abl. pl.</i> atibus <i>и</i> asin) <i>и</i> <b>schema, ae</b> <i>f</i> (<i>греч.</i>; <i>лат.</i> habitus)\n<b>1)</b> вид, внешность (s. lepida <i>Pl</i>); образ, манера (imperata s. <i>Su</i>);\n<b>2)</b> одежда, одеяние (servile <i>Pl</i>);\n<b>3)</b> поза (corporis <i>Ap</i>);\n<b>4)</b> фигура (geometrica schemata <i>Vtr</i>);\n<b>5)</b> риторическая фигура, иносказание <i>Q</i>: non schemas loqui, sed directum <i>Pt</i> не произносить цветистых фраз, а говорить по существу."],
  ["schematismus", "<big>schematismus</big>\n\n<b>schematismus, i</b> <i>m</i> [schema]\nиносказательный стиль, фигуральный образ выражения <i>Q</i>."],
  ["schida", "<big>schida</big>\n\n<b>schida, ae</b> <i>f</i> <i>v. l.</i>\n= scheda."],
  ["schidiae", "<big>schidiae</big>\n\n<b>schidiae, arum</b> <i>f</i> (<i>греч.</i>)\nщепки, лучинки <i>Vtr</i>."],
  ["schinus", "<big>schinus</big>\n\n<b>schinus, i</b> <i>f</i> (<i>греч.</i>; <i>лат.</i> lentiscus)\nмастиковое деревцо <i>Vlg</i>."],
  ["schisma", "<big>schisma</big>\n\n<b>schisma, atis</b> <i>n</i> (<i>греч.</i>)\nсхизма, раскол <i>Eccl</i>."],
  ["schismaticus", "<big>schismaticus</big>\n\n<b>schismaticus, i</b> <i>m</i> (<i>греч.</i>)\nсхизматик, раскольник <i>Eccl</i>."],
  ["schistos", "<big>schistos</big>\n\n<b>schistos, a, on</b> (<i>греч.</i>)\nрасщеплённый, расколотый: schiston lac <i>PM</i> свернувшееся молоко || слоистый (сера <i>PM</i>): s. lapis <i>PM</i> сланец."],
  ["Schoeneia", "<big>Schoeneia</big>\n\n<b>Schoeneia (virgo)</b> <i>f</i> <i>O</i>\n= Schoeneis."],
  ["Schoeneis", "<big>Schoeneis</big>\n\n<b>Schoeneis, idis</b> <i>f</i>\nСхенеида, <i>дочь беотийского царя Схенея, т. е.</i> Аталанта <i>O</i>."],
  ["schoeniculae", "<big>schoeniculae</big>\n\n<b>schoeniculae, arum</b> <i>f</i>\nпроститутки, натиравшиеся схеном (<i>см.</i> schoenus 2) <i>Pl ap. Vr</i>."],
  ["schoenobates", "<big>schoenobates</big>\n\n<b>schoenobates, ae</b> <i>m</i> (<i>греч.</i>)\nканатоходец <i>J</i>."],
  ["schoenum", "<big>schoenum</big>\n\n<b>schoenum, i</b> <i>n</i> <i>Col</i>\n= schoenus."],
  ["schoenus", "<big>schoenus</big>\n\n<b>schoenus, i</b> <i>m</i> (<i>греч.</i>; <i>лат.</i> juncus)\n<b>1)</b> <i>ароматический злак</i> «верблюжье сено» (<i>Andropogon Schoenanthos</i>, L.) <i>Cato, Col</i>;\n<b>2)</b> схен, дешёвая душистая мазь из «верблюжьего сена» (<i>которой натирались римские проститутки</i>) <i>Pl</i>;\n<b>3)</b> схен, <i>египетско-персидская мера длины</i> = <i>30 стадиям</i> (<i>ок. 5,5 км</i>) (<i>по др.</i> — <i>вдвое большая</i>) <i>PM</i>."],
  ["schola", "<big>schola</big>\n\n<b>schola, ae</b> <i>f</i> (<i>греч.</i>)\n<b>1)</b> учёный доклад, лекция (de aliqua re <i>C</i>); учёное собеседование, диспут: scholas habere (explicare) <i>C</i> читать лекции; dierum quinque scholae <i>C</i> = Tusculanae disputationes;\n<b>2)</b> школа, учебное заведение (pueros in scholas deducere <i>T</i>);\n<b>3)</b> школа, философское направление, последователи одной школы (omnes scholae philosophorum <i>C</i>);\n<b>4)</b> галерея с сиденьями вокруг купального бассейна <i>Vtr</i>;\n<b>5)</b> сообщество, корпорация <i>CJ</i>."],
  ["scholaris", "<big>scholaris</big>\n\n<b>I scholaris, e</b> [schola]\nшкольный <i>Eccl</i>.\n\n<b>II scholaris, is</b> <i>m</i>\n<b>1)</b> член сообщества (<i>см.</i> schola 5) <i>CJ</i>;\n<b>2)</b> солдат дворцовой охраны <i>CTh</i>;\n<b>3)</b> школяр, студент <i>Eccl</i>."],
  ["scholastica", "<big>scholastica</big>\n\n<b>scholastica, orum</b> <i>n</i>\nшкольные риторические упражнения, школьные диспуты <i>Q, Sen</i>."],
  ["scholasticus", "<big>scholasticus</big>\n\n<b>I scholasticus, a, um</b> [schola]\nшкольный (lex <i>PJ</i>); учебный (controversia <i>Q</i>).\n\n<b>II scholasticus, i</b> <i>m</i>\n<b>1)</b> ритор, учитель красноречия <i>Vr, Q</i>;\n<b>2)</b> ученик, студент (<i>преим.</i> изучающий риторику) (turba scholasticorum <i>Pt</i>);\n<b>3)</b> <i>презр.</i> буквоед, учёный дурак <i>Pt, Ap</i>."],
  ["scholicus", "<big>scholicus</big>\n\n<b>scholicus, a, um</b> [schola]\nшкольный <i>Vr, AG</i>."],
  ["Sciathus", "<big>Sciathus</big>\n\n<b>Sciathus (-os), i</b> <i>f</i>\nСкиатос, <i>о-в в sinus Thermaicus Эгейского моря, с городом того же имени</i> <i>L, VF etc.</i>"],
  ["scibam", "<big>scibam</big>\n\n<b>scibam</b> <i>Pl, Ter, Lcr арх. impf. к</i>\nscio."],
  ["scibilis", "<big>scibilis</big>\n\n<b>scibilis, e</b> [scio]\nдоступный познанию, познаваемый (alicui <i>Tert</i>)."],
  ["scibo", "<big>scibo</big>\n\n<b>scibo</b> <i>Pl, Ter, Cato арх. fut. к</i>\nscio."],
  ["scicidi", "<big>scicidi</big>\n\n<b>scicidi</b> <i>Enn, Naev арх. pf. к</i>\nscindo."],
  ["scida", "<big>scida</big>\n\n<b>scida, ae</b> <i>f</i> <i>v. l.</i>\n= scheda."],
  ["scidi", "<big>scidi</big>\n\n<b>scidi</b> <i>pf. к</i>\nscindo."],
  ["sciens", "<big>sciens</big>\n\n<b>sciens, entis</b>\n<b>1.</b> <i>part. praes. к</i> scio;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> сведущий, умелый, опытный, искусный (alicujus rei <i>Sl</i>): scientem aliquem facere <i>Pl, Ter</i> поставить в известность (осведомить) кого-л.;\n<b>2)</b> делающий сознательно, намеренно, с умыслом (sciens hoc fecit <i>C</i>)."],
  ["scienter", "<big>scienter</big>\n\n<b>scienter</b> [sciens]\n<b>1)</b> умело, искусно (dicere <i>C</i>; tibiis cantare <i>Nep</i>);\n<b>2)</b> сознательно, преднамеренно (fallere <i>PJ</i>)."],
  ["scientia", "<big>scientia</big>\n\n<b>scientia, ae</b> <i>f</i> [scio]\n<b>1)</b> знание, сведение (alicujus rei <i>или</i> de aliqua re <i>C etc.</i>): alicujus scientiam fugere <i>C</i> быть неизвестным кому-л.; s. atque usus <i>C, Cs</i> теоретическая и практическая подготовка; s. futurorum <i>C</i> предвидение будущего;\n<b>2)</b> осведомлённость, понимание, опытность, умение, знакомство (s. linguae Gallicae <i>Cs</i>);\n<b>3)</b> отрасль знания, наука (s. juris <i>C</i>): s. rei militaris <i>C, Cs</i> военная наука."],
  ["scientiola", "<big>scientiola</big>\n\n<b>scientiola, ae</b> <i>f</i> [<i>demin. к</i> scientia]\nкое-какое (поверхностное) знание <i>Aug</i>."],
  ["scilicet", "<big>scilicet</big>\n\n<b>scilicet</b> <i>adv.</i> [<i>из</i> scire + licet]\n<b>1)</b> и подумать только!, о чудо!: ter sunt conati imponere Pelio Ossam s. atque Ossae frondosum involvere Olympum <i>V</i> трижды (гиганты) пытались взгромоздить Оссу на Пелион и, о диво, вкатить на Оссу лесистый Олимп;\n<b>2)</b> надо думать, разумеется, конечно, само собой (Catilina, homo s. permodestus <i>C</i>); quod s. perfacile est <i>Ap</i> что, конечно, весьма нетрудно; vincam s. <i>Ter</i> я непременно выйду победителем; s. facturum me esse <i>Ter</i> конечно, я (это) сделаю || <i>ирон.</i> как же, еще бы: s. is superis labor est <i>V</i> как же, самое подходящее для богов занятие;\n<b>3)</b> а именно, то есть: sub nomine alieno, nepotum s. et uxoris sororisque <i>Su</i> под чужим именем, а именно под именами племянников, жены и сестры;\n<b>4)</b> как известно (к сожалению): unda s. omnibus enaviganda <i>H</i> река (Стикс), которую, увы, всем ведь суждено переплыть."],
  ["scilla", "<big>scilla</big>\n\n<b>scilla, ae</b> <i>f</i> (<i>греч.</i>)\n<b>1)</b> <i>бот.</i> морской лук (<i>Scilla maritima</i>, L.) <i>Vr, Col, PM etc.</i>;\n<b>2)</b> скилла, <i>род мелкого краба</i> <i>Vr, C, H</i>."],
  ["scillinus", "<big>scillinus</big>\n\n<b>scillinus, a, um</b> <i>PM, CA</i> <i>и</i> <b>scillites, ae</b> <i>m, n Col, PM</i> [scilla 1]\nприготовленный из морского лука (acetum <i>PM</i>)."],
  ["scilliticus", "<big>scilliticus</big>\n\n<b>scilliticus, a, um</b> [scilla]\nприготовленный из морского лука <i>или</i> приправленный им <i>Col, CC</i>."],
  ["scimpodium", "<big>scimpodium</big>\n\n<b>scimpodium (-on), i</b> <i>n</i> (<i>греч.</i>)\nскимподий, небольшое ложе <i>AG</i>."],
  ["scin'", "<big>scin'</big>\n\n<b>scin'</b> [<i>из</i> scisne <i>от</i> scio]\nзнаешь ли <i>Pl</i>."],
  ["scindo", "<big>scindo</big>\n\n<b>scindo, scidi</b> (<i>арх.</i> <b>scicidi</b>), <b>scissum, ere</b>\n<b>1)</b> разрывать, раздирать (vestem <i>L, Prp</i>; epistulam <i>C</i>; tunica scissa <i>Pt</i>); распускать, приводить в беспорядок, растрёпывать, рвать (crines <i>V</i>); расцарапывать (genas <i>Lcn</i>); раскалывать (quercum cuneis <i>V</i>; scissa robora <i>Pt</i>); рассекать (aquas <i>O</i>); бороздить, прорезать (frontem cicatricibus <i>Pt</i>); прорывать (agmen <i>T</i>); сносить, разрушать (pontem <i>Pt, Lact</i>; vallum <i>Cs</i>; Pergamum <i>Pl</i>); разрезывать (aves in frusta <i>Sen</i>; aprum <i>Pt</i>); разделять (amnis in partes scinditur <i>O</i>; in duas factiones scindi <i>T</i>): in contraria studia scindi <i>V</i> разделиться на противоположные лагери;\n<b>2)</b> прерывать (verba fletu <i>O</i>); пресекать, мешать, нарушать (actionem <i>PJ</i>);\n<b>3)</b> прокладывать (viam <i>V</i>); пробираться, проходить (s. silvam <i>T</i>); рыть, взрывать (humum <i>M</i>);\n<b>4)</b> терзать, мучить (curae scindunt aliquem <i>Lcr</i>);\n<b>5)</b> бередить, растравлять (dolorem <i>C</i>)."],
  ["scindul-", "<big>scindul-</big>\n\n<b>scindul-</b> <i>v. l.</i>\n= scandul-."],
  ["scinifes", "<big>scinifes</big>\n\n<b>scinifes (-phes), um</b> <i>f</i>\nскинифы, <i>род насекомых-паразитов</i> <i>Pt, Is, Vlg</i>."],
  ["scintilla", "<big>scintilla</big>\n\n<b>scintilla, ae</b> <i>f</i>\nискра (silici scintillam excudere <i>V</i>; <i>перен.</i> s. belli <i>C</i>; s. ingenii <i>C</i>)."],
  ["scintillatio", "<big>scintillatio</big>\n\n<b>scintillatio, onis</b> <i>f</i> [scintillo]\nмерцание, сверкание: scintillationes oculorum <i>PM</i> искры в глазах."],
  ["scintillo", "<big>scintillo</big>\n\n<b>scintillo, avi, —, are</b> [scintilla]\nискриться, блистать, сверкать (scintillant oculi <i>Pl</i>); <i>перен.</i> вспыхивать, пылать (in multo fumo scintillans fides <i>Aug</i>)."],
  ["scintillosus", "<big>scintillosus</big>\n\n<b>scintillosus, a, um</b>\nискрящийся, искристый (fons <i>Eccl</i>)."],
  ["scintillula", "<big>scintillula</big>\n\n<b>scintillula, ae</b> <i>f</i> [<i>demin. к</i> scintilla]\nискорка <i>Pl, C, Tert</i>."],
  ["scio", "<big>scio</big>\n\n<b>scio, ivi (ii), itum, ire</b>\n<b>1)</b> знать (quum videbis, tum scies <i>Pl</i>): aliquid (pro) certo s. <i>Pl, C etc.</i> знать что-л. наверное; quod sciam <i>C</i> насколько я знаю; te sciente et vidente <i>C</i> с твоего ведома и (молчаливого) согласия; s. de, ex <i>и</i> ab aliquo <i>Pl, C, L etc.</i> знать от кого-л.; haud scio an <i>C, Cs etc.</i> может быть, возможно; non satis scio <i>L</i> право, не знаю, не вполне уверен;\n<b>2)</b> узнавать, испытывать (hostes id scient <i>L</i>): quum te scierit venisse <i>O</i> узнав о твоём прибытии; si scitum esset <i>Pt</i> если бы (это) стало известно; jam faxo (= fecero) sciatis <i>Pt</i> (<i>угрожающе</i>) уж вы у меня узнаете; scies <i>Ter</i> <i>и</i> scias <i>SenT</i> знай, имей в виду, будь уверен;\n<b>3)</b> знать толк, уметь, понимать, быть опытным: s. Graece <i>C</i> владеть греческим языком; aliqua re uti s. <i>C</i> уметь использовать что-л.; s. fidibus <i>Ter</i> уметь играть на струнных инструментах; s. de legibus instituendis <i>C</i> быть опытным законодателем;\n<b>4)</b> знать, быть знакомым (s. aliquem <i>Ap, Spart etc.</i>);\n<b>5)</b> замечать, видеть: cor dolet, quum scio ut nunc sum atque ut fui <i>Pl</i> сердце болит, когда я вижу, кто я теперь и кем я был; scires сразу видно (scires propaginem Gigantum e sanguine natam <i>O</i>) <i>или</i> можно подумать (scires a Pallade doctam <i>O</i>);\n<b>6)</b> решать, постановлять (plebs sciebat <i>L</i>). — <i>См. тж.</i> sciens."],
  ["sciolus", "<big>sciolus</big>\n\n<b>sciolus, i</b> <i>m</i> [<i>demin. к</i> scius]\nполузнайка <i>Eccl</i>."],
  ["Scipiades", "<big>Scipiades</big>\n\n<b>Scipiades (Scipiadas), ae</b> <i>m</i>\nСципиад, <i>член рода Сципионов</i> <i>Lcr, V, H, Cld, Sil</i>."],
  ["scipio", "<big>scipio</big>\n\n<b>I scipio, onis</b> <i>m</i> (<i>греч.</i>)\nпалка, посох <i>Pl, Ctl</i>; жезл (s. eburneus <i>L</i>).\n\n<b>II Scipio, onis</b> <i>m</i>\nСципион, <i>cognomen в роде Корнелиев</i> (<i>см.</i> Cornelius)."],
  ["scircula", "<big>scircula</big>\n\n<b>scircula, ae</b> <i>f</i> <i>v. l.</i>\n= surcula."],
  ["sciria", "<big>sciria</big>\n\n<b>sciria, ae</b> <i>f</i> (<i>греч.</i>)\nшкура (s. leonina <i>Tert</i> — <i>v. l.</i>)."],
  ["sciribilita", "<big>sciribilita</big>\n\n<b>sciribilita, ae</b> <i>f</i> <i>Pt v. l.</i>\n= scriblita."],
  ["Sciron", "<big>Sciron</big>\n\n<b>Sciron, onis</b> <i>m</i>\nСкирон:\n<b>1)</b> <i>разбойник, живший в Скиронских скалах между Мегарой и Афинами и убитый Тезеем</i> <i>O, St, AG</i>;\n<b>2)</b> <i>философ-эпикуреец времён Цицерона</i> <i>C</i>."],
  ["Scironis", "<big>Scironis</big>\n\n<b>Scironis, idis</b> <i>f</i> [Sciron 1]\nскиронова (petrae <i>SenT</i>)."],
  ["Scironius", "<big>Scironius</big>\n\n<b>I Scironius, a, um</b> [Sciron]\nскиронский: Scironia saxa <i>Mela, PM</i> <i>и</i> Scironia rupes <i>Cld</i> Скироновы скалы (<i>между Мегарой и Аттикой</i>).\n\n<b>II Scironius, i</b> <i>m</i> (<i>sc.</i> ventus)\n<i>сев.-зап. ветер</i> (<i>дувший с этих скал в сторону Афин</i>) <i>PM, Sen, Su</i>."],
  ["sciros", "<big>sciros</big>\n\n<b>sciros</b> <i>v. l.</i>\n= scirr(h)os."],
  ["scirpea", "<big>scirpea</big>\n\n<b>scirpea (scirpia), ae</b> <i>f</i> [scirpeus]\nплетёная корзина, плетёнка <i>Vr, O, Eccl</i>."],
  ["scirpeus", "<big>scirpeus</big>\n\n<b>scirpeus, a, um</b> [scirpus]\nтростниковый, камышовый, плетёный (ratis <i>Pl</i>; simulacrum <i>O</i>)."],
  ["scirpiculus", "<big>scirpiculus</big>\n\n<b>I scirpiculus, a, um</b> [scirpus]\nкамышовый, тростниковый, ситниковый (falces <i>Cato, Vr</i>).\n\n<b>II scirpiculus, i</b> <i>m</i>\nтростниковая корзина, плетёнка <i>Pl, Vr, Prp</i>."],
  ["scirpo", "<big>scirpo</big>\n\n<b>scirpo, avi, atum, are</b> [scirpus]\n<b>1)</b> связывать тростником <i>Vr</i>;\n<b>2)</b> плести, сплетать (dolia <i>Vr</i>)."],
  ["scirpula vitis", "<big>scirpula vitis</big>\n\n<b>scirpula vitis (uva)</b> <i>f</i>\n<i>сорт винограда</i> <i>PM, Col</i>."],
  ["scirpus", "<big>scirpus</big>\n\n<b>scirpus, i</b> <i>m</i>\n<b>1)</b> ситник, камыш, тростник <i>Pt, PM</i>: nodum in scirpo quaerere <i>погов. Pl, Ter</i> = искать трудностей там, где их нет;\n<b>2)</b> тростниковая сеть, <i>перен.</i> загадка <i>AG</i>."],
  ["scirrhos", "<big>scirrhos</big>\n\n<b>scirr(h)os, i</b> <i>m</i> (<i>греч.</i>)\nскирр, твёрдая опухоль <i>PM</i>."],
  ["scirrhosis", "<big>scirrhosis</big>\n\n<b>scirr(h)osis, is</b> <i>f</i> (<i>греч.</i>; <i>лат.</i> saxitas)\nзатвердение <i>CA</i>."],
  ["sciscitatio", "<big>sciscitatio</big>\n\n<b>sciscitatio, onis</b> <i>f</i> [sciscitor]\nразузнавание, исследование: diligentissima sciscitatione quaerere <i>Pt</i> осведомляться с живейшим интересом (о чём-л.)."],
  ["sciscitator", "<big>sciscitator</big>\n\n<b>sciscitator, oris</b> <i>m</i> [sciscitor]\nисследователь (alicujus rei <i>M</i>, <i>Eccl</i>)."],
  ["sciscito", "<big>sciscito</big>\n\n<b>sciscito, —, atum, are</b> <i>Pl, Amm, Aug</i>\n= sciscitor."],
  ["sciscitor", "<big>sciscitor</big>\n\n<b>sciscitor, atus sum, ari</b> <i>depon.</i> [<i>intens. к</i> scisco]\nстараться узнать, разузнавать, выведывать (aliquid <i>или</i> de aliqua re <i>C</i>; sententiam ex aliquo <i>C</i>); расспрашивать (s. aliquem <i>L, Su</i>)."],
  ["scisco", "<big>scisco</big>\n\n<b>scisco, scivi, scitum, ere</b> [<i>inchoat. к</i> scio]\n<b>1)</b> узнавать, разузнавать, выведывать (aliquid <i>Acc, Pac, Pl</i>);\n<b>2)</b> подавать голос (за), высказываться (в пользу), одобрять, принимать (legem, rogationem <i>C</i>);\n<b>3)</b> определять, постановлять (populus scivit aliquid <i>Pl</i>)."],
  ["scisse", "<big>scisse</big>\n\n<b>scisse</b> <i>inf. pf. к</i>\nscio."],
  ["scissilis", "<big>scissilis</big>\n\n<b>scissilis, e</b> [scindo]\n<b>1)</b> расщеплённый, волокнистый (alumen <i>CC</i>);\n<b>2)</b> разорванный, изодранный (pailiastrura <i>Ap</i>)."],
  ["scissim", "<big>scissim</big>\n\n<b>scissim</b> [scindo]\nшироко раскрываясь (dehiscere <i>Eccl</i>)."],
  ["scissio", "<big>scissio</big>\n\n<b>scissio, onis</b> <i>f</i> [scindo]\n<b>1)</b> разрыв (veli <i>Aug</i>); трещина (percutere domum scissionibus <i>Vlg</i>); разделение, расчленение (ratio scissionis <i>Macr</i>); <i>грам.</i> = diaeresis;\n<b>2)</b> щепка, лучина (scissiones lignorum <i>Vr</i>)."],
  ["scissor", "<big>scissor</big>\n\n<b>scissor, oris</b> <i>m</i> [scindo]\nнарезающий кушанья, выделяющий порции <i>Pt</i>."],
  ["scissura", "<big>scissura</big>\n\n<b>scissura, ae</b> <i>f</i> [scindo]\n<b>1)</b> расщепление, разделение (Nili <i>PM</i>);\n<b>2)</b> расщеп, разрыв, щель (unguiculi <i>Sen</i>);\n<b>3)</b> раскол, раздор (s. domestica <i>Eccl</i>)."],
  ["scissus", "<big>scissus</big>\n\n<b>scissus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> scindo;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> расщеплённый, разорванный (aures cervorum <i>PM</i>); волокнистый <i>или</i> слоистый (alumen <i>Col</i>);\n<b>2)</b> морщинистый (genae <i>Prp</i>);\n<b>3)</b> пронзительный, визгливый <i>или</i> срывающийся (vocum genus <i>C</i>)."],
  ["scisti", "<big>scisti</big>\n\n<b>scisti</b> <i>O стяж. pf. к</i>\nscio."],
  ["scitamenta", "<big>scitamenta</big>\n\n<b>scitamenta, orum</b> <i>n</i> [scitus I]\n<b>1)</b> лакомства, деликатесы <i>Pl, Ap, AG, Macr</i>;\n<b>2)</b> риторические тонкости, изящные выражения <i>AG</i>."],
  ["scitatio", "<big>scitatio</big>\n\n<b>scitatio, onis</b> <i>f</i> [scitor]\nрасспрашивание, выведывание <i>Amm</i>."],
  ["scitator", "<big>scitator</big>\n\n<b>scitator, oris</b> <i>m</i> [scitor]\nвопрошатель <i>Amm</i>."],
  ["scite", "<big>scite</big>\n\n<b>scite</b> [scitus I]\nискусно, умно, со вкусом, тонко (loqui <i>L</i>; facere aliquid <i>C, AG</i>; exornare aliquid <i>Sl</i>)."],
  ["scitor", "<big>scitor</big>\n\n<b>scitor, atus sum, ari</b> <i>depon.</i> <i>V, H, O</i>\n= sciscitor."],
  ["scitule", "<big>scitule</big>\n\n<b>scitule</b> [scitulus]\nискусно, изящно <i>Ap</i>."],
  ["scitulus", "<big>scitulus</big>\n\n<b>scitulus, a, um</b> [<i>demin. к</i> scitus I]\nизящный, хорошенький, миловидный (facies <i>Pl</i>; caupona <i>Ap</i>)."],
  ["scitum", "<big>scitum</big>\n\n<b>scitum, i</b> <i>n</i> [scisco]\n<b>1)</b> определение, постановление, решение (populi scito non parere <i>Nep</i>): plebis (plebei, populi) s. <i>C, L etc.</i> <i>или</i> plebiscitum <i>C</i> всенародное решение;\n<b>2)</b> философское положение, принцип, тезис <i>Sen, Capit</i>."],
  ["scitus", "<big>scitus</big>\n\n<b>I scitus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> scio <i>и</i> scisco;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> умный, ловкий, хитрый (homo <i>Pl</i>; mulier <i>AG</i>); искусный, умелый, опытный, знающий (s. alicujus rei <i>O</i>);\n<b>2)</b> славный, миловидный, приятный (puer <i>Ter</i>; virgo <i>Ter</i>); изящный (forma <i>Lampr</i>);\n<b>3)</b> тонкий, остроумный (sermo <i>C</i>; illud Catonis <i>C</i>);\n<b>4)</b> подходящий, годный, удобный (nox scita alicui rei <i>Pl</i>).\n\n<b>II scitus, (us)</b> (<i>встреч. тк. в</i> <i>abl.</i>) <i>m</i> [scisco]\nопределение, решение <i>C</i>: de senatus sententia plebisque scitu <i>L</i> по постановлению сената и по определению народа."],
  ["sciurus", "<big>sciurus</big>\n\n<b>sciurus, i</b> <i>m</i> (<i>греч.</i>)\nбелка <i>PM, M</i>."],
  ["scius", "<big>scius</big>\n\n<b>scius, a, um</b> [scio]\nзнающий, сведущий (Latinae linguae <i>Macr, Aug</i>; peritus et rerum s. <i>Lact</i>): s. aliquid facere <i>Lact</i> сознательно делающий что-л."],
  ["scivi", "<big>scivi</big>\n\n<b>scivi</b> <i>pf. к</i>\nscio <i>и</i> scisco."],
  ["sclingo", "<big>sclingo</big>\n\n<b>sclingo, —, —, ere</b>\nгоготать (<i>о гусях</i>) <i>Su</i>."],
  ["scloppus", "<big>scloppus</big>\n\n<b>scloppus, i</b> <i>m</i> <i>v. l.</i>\n= stloppus."],
  ["scobina", "<big>scobina</big>\n\n<b>scobina, ae</b> <i>f</i> [scobis]\nнапильник с крупной насечкой, рашпиль <i>Vr, PM, Tert</i>."],
  ["scobis", "<big>scobis</big>\n\n<b>scobis, is</b> <i>f</i> (<i>редко</i> <i>m</i>) [scabo]\nопилки <i>Vr, CC, Col, H etc.</i>: auri s. <i>Treb</i> золотая пудра (<i>для волос</i>); s. cutis <i>PM</i> шелушение кожи."],
  ["Scodra", "<big>Scodra</big>\n\n<b>Scodra, ae</b> <i>f</i>\nСкодра, <i>город в южн. Далматии, на сев. берегу Барбаны</i> (<i>ныне</i> Скутари) <i>L, PM</i>."],
  ["Scodrenses", "<big>Scodrenses</big>\n\n<b>Scodrenses, ium</b> <i>m</i>\nжители города Scodra <i>L</i>."],
  ["scol-", "<big>scol-</big>\n\n<b>scol-</b> <i>v. l.</i>\n= schol-."],
  ["scolax", "<big>scolax</big>\n\n<b>scolax, acis</b> <i>m</i>\nвосковой факел <i>Is</i>."],
  ["scolopax", "<big>scolopax</big>\n\n<b>scolopax, acis</b> <i>m</i> (<i>греч.</i>) <i>зоол.</i>\nбекас <i>Nem</i>."],
  ["scomber", "<big>scomber</big>\n\n<b>scomber, bri</b> <i>m</i> (<i>греч.</i>)\nскумбрия, макрель <i>Pl, Ctl, Col, PM, Pers, M</i>."],
  ["scomma", "<big>scomma</big>\n\n<b>scomma, atis</b> <i>n</i> (<i>греч.</i>)\nострота, (колкая) насмешка <i>Macr</i>."],
  ["scopa", "<big>scopa</big>\n\n<b>scopa, ae</b> <i>f</i>\n<b>1)</b> прут, лоза, розга <i>PM</i>: s. regia <i>PM бот.</i> марь (<i>Chenopodium</i>, L.);\n<b>2)</b> <i>pl.</i> метла, веник (scopis purgamenta everrere <i>Pt</i>): dissolvere scopas <i>погов. C</i> привести в беспорядок; scopae solutae <i>C</i> растрёпанная метёлка (<i>о нелепом, никчёмном человеке</i>)."],
  ["scoparius", "<big>scoparius</big>\n\n<b>scoparius, i</b> <i>m</i> [scopa]\nметельщик <i>Dig</i>."],
  ["Scopas", "<big>Scopas</big>\n\n<b>Scopas, ae</b> <i>m</i>\nСкопас:\n<b>1)</b> <i>член знатного фессалийского рода Скопадов в Кранноне и Фарсале</i> <i>C, Q</i>;\n<b>2)</b> <i>ваятель и зодчий из Пароса первой половины <i>JV</i> в. до н. э., строитель галикарнасского Мавзолея</i> <i>C, H, PM</i>."],
  ["scopio", "<big>scopio</big>\n\n<b>scopio, onis</b> <i>m</i>\nстебель, черенок (<i>виноградной кисти</i>) <i>Cato, Col</i>."],
  ["scopo", "<big>scopo</big>\n\n<b>I scopo, —, —, are</b> [scopa]\nвыметать, очищать (<i>sc.</i> Babylona <i>Vlg</i>).\n\n<b>II scopo, —, —, ere</b> (<i>греч.</i>)\nсозерцать, размышлять (s. spiritum suum <i>Vlg</i>)."],
  ["scopula", "<big>scopula</big>\n\n<b>scopula, ae</b> <i>f</i> [<i>demin. к</i> scopa]\n<b>1)</b> розга, прут <i>Col</i>;\n<b>2)</b> <i>pl.</i> веник, метёлка <i>Cato, Col</i>."],
  ["scopulosus", "<big>scopulosus</big>\n\n<b>scopulosus, a, um</b> [scopulus]\nутёсистый, скалистый (locus <i>C</i>); изобилующий скалами (mare <i>C</i>); <i>перен.</i> торчащий словно скала (terga, <i>sc.</i> monstri ingentis <i>VF</i>)."],
  ["scopulus", "<big>scopulus</big>\n\n<b>scopulus, i</b> <i>m</i> (<i>греч.</i>)\n<b>1)</b> горная вершина, пик (s. summus, qui montibus altis abit <i>VF</i>): s. Mavortis <i>O</i> = Areopagus;\n<b>2)</b> скала, утёс (scopuli et saxa <i>V</i>; ad scopulos allidi <i>Cs</i>): scopuli errantes <i>VF</i> = Symplegades;\n<b>3)</b> чёрствость, жестокость (scopulos et ferrum gestare in corde <i>O</i>);\n<b>4)</b> опасность (in scopulos incidere <i>C</i>);\n<b>5)</b> губитель (voragines scopuli que rei publicae <i>C</i>)."],
  ["scopus", "<big>scopus</big>\n\n<b>I scopus, i</b> <i>m</i> (<i>греч.</i>)\nмишень, цель <i>Su, Eccl</i>.\n\n<b>II scopus, i</b> <i>m</i> <i>Vr</i>\n= scopio."],
  ["scordalia", "<big>scordalia</big>\n\n<b>scordalia, ae</b> <i>f</i> [scordalus]\nссора, склока <i>Pt</i>."],
  ["scordalus", "<big>scordalus</big>\n\n<b>scordalus, i</b> <i>m</i>\nсварливый человек <i>Sen, Pt</i>."],
  ["Scordisci", "<big>Scordisci</big>\n\n<b>Scordisci, orum</b> <i>m</i>\nскордиски, <i>племя в Иллирии</i> <i>L</i>."],
  ["Scordus", "<big>Scordus</big>\n\n<b>Scordus (Scodrus), i</b> <i>m</i>\nСкорд, <i>гора в Иллирии</i> (<i>Далматии</i>) <i>L</i>."],
  ["scoria", "<big>scoria</big>\n\n<b>scoria, ae</b> <i>f</i> (<i>греч.</i>)\nметаллический шлак <i>PM, Pall</i>."],
  ["scorpiacum", "<big>scorpiacum</big>\n\n<b>scorpiacum, i</b> <i>n</i> [scorpio]\nсредство против ужалений скорпионов <i>Tert</i>."],
  ["scorpio", "<big>scorpio</big>\n\n<b>scorpio, onis</b> <i>m</i> (<i>греч.</i>)\n<b>1)</b> скорпион <i>O, PM</i>;\n<b>2)</b> род осадного орудия <i>Cs, Sl, L, Amm</i>;\n<b>3)</b> <i>рыба</i> бычок колючий (<i>Cottus Scorpius</i>, L.) <i>PM, Pt</i>;\n<b>4)</b> созвездие Скорпиона <i>C, O, Pt</i>;\n<b>5)</b> «скорпион», <i>род особо жестокого бича</i> (caedere aliquem scorpionibus <i>Vlg</i>)."],
  ["scorpios", "<big>scorpios</big>\n\n<b>scorpios (scorpius), i</b> <i>m</i> <i>C, O</i>\n= scorpio."],
  ["scortatio", "<big>scortatio</big>\n\n<b>scortatio, onis</b> <i>f</i> <i>Eccl</i>\n= scortatus."],
  ["scortator", "<big>scortator</big>\n\n<b>scortator, oris</b> <i>m</i> [scortor]\nразвратник, распутник <i>Pl, H, Vlg, Eccl</i>."],
  ["scortatus", "<big>scortatus</big>\n\n<b>scortatus, (us)</b> (<i>тк. в</i> <i>abl.</i>) <i>m</i> [scortor]\nразврат, распутство <i>Ap</i>."],
  ["scortea", "<big>scortea</big>\n\n<b>I scortea, ae</b> <i>f</i> [scorteus]\n<b>1)</b> одежда из шкур, тулуп, шуба <i>Sen, M</i>;\n<b>2)</b> кожаный бурдюк <i>Is</i>.\n\n<b>II scortea, orum</b> <i>m</i>\nкожаные изделия <i>Vr, O</i>."],
  ["scorteus", "<big>scorteus</big>\n\n<b>scorteus, a, um</b> [scortum]\n<b>1)</b> сделанный из шкуры, меховой <i>или</i> кожаный (pulvinus <i>CC</i>; paenula <i>Vr, M</i>);\n<b>2)</b> заскорузлый, состарившийся (scortum <i>Ap</i>)."],
  ["scortillum", "<big>scortillum</big>\n\n<b>scortillum, i</b> <i>n</i> [<i>demin. к</i> scortum]\nразвратница <i>Ctl</i>."],
  ["scortinus", "<big>scortinus</big>\n\n<b>scortinus, a, um</b> [scortum]\nкожаный <i>Su</i>."],
  ["scortor", "<big>scortor</big>\n\n<b>scortor, atus sum, ari</b> <i>depon.</i> [scortum]\nразвратничать <i>Pl, Ter, Vlg, Eccl</i>."],
  ["scortum", "<big>scortum</big>\n\n<b>scortum, i</b> <i>n</i>\n<b>1)</b> <i>арх.</i> шкура, кожа <i>Vr, Tert</i>;\n<b>2)</b> развратник, развратница <i>L etc.</i>: vino scortisque demersi <i>Pt</i> погрязшие в пьянстве и в разврате;\n<b>3)</b> любовник, любовница <i>C etc.</i>"],
  ["Scoti", "<big>Scoti</big>\n\n<b>Scōtī, ōrum</b> <i>m</i>\nскоты, <i>жители Каледонии <i>Amm</i> или Гибернии</i> <i>Cld</i>."],
  ["scotia", "<big>scotia</big>\n\n<b>I scotia, ae</b> <i>f</i> (<i>греч.</i>) <i>архит.</i>\n<b>1)</b> скоция, <i>разновидность облома базиса колонны</i> <i>Vtr</i>;\n<b>2)</b> водосточный жёлоб <i>Vtr</i>.\n\n<b>II Scōtia, ae</b> <i>f</i> <i>Is прибл.</i>\n= Caledonia."],
  ["Scoticus", "<big>Scoticus</big>\n\n<b>Scōticus, a, um</b> [Scotia]\nскотический (tela <i>Cld</i>)."],
  ["Scotinos", "<big>Scotinos</big>\n\n<b>Scotīnos, a, um</b> (<i>греч.</i> «тёмный»)\n<i>прозвище Гераклита Эфесского</i> <i>L, Sen</i>."],
  ["scotoma", "<big>scotoma</big>\n\n<b>scotōma, atis</b> <i>n</i> (<i>греч.</i>)\nпотемнение в глазах, головокружение, обморок <i>Is</i>."],
  ["Scotussa", "<big>Scotussa</big>\n\n<b>Scotūs(s)a, ae</b> <i>f</i>\nСкотуса:\n<b>1)</b> <i>город в Македонии, близ реки Стримон</i> <i>PM</i>;\n<b>2)</b> <i>город в Фессалии, близ Лариссы</i> <i>L</i>."],
  ["Scotussaeus", "<big>Scotussaeus</big>\n\n<b>Scotūs(s)aeus, a, um</b> [Scotus(s)a]\nскотусский (ager <i>L</i>)."],
  ["scrapta", "<big>scrapta</big>\n\n<b>scrapta, ae</b> <i>f</i>\nразвратница <i>Pl, Vr</i>."],
  ["scratia", "<big>scratia</big>\n\n<b>scratia (scratta, scrattia), ae</b> <i>f</i> <i>v. l.</i>\n= scrapta."],
  ["screator", "<big>screator</big>\n\n<b>screator, oris</b> <i>m</i> [screo]\nхаркающий, отхаркивающий <i>Pl</i>."],
  ["screatus", "<big>screatus</big>\n\n<b>screatus, us</b> <i>m</i> [screo]\nхарканье <i>Ter</i>."],
  ["screo", "<big>screo</big>\n\n<b>screo, —, —, are</b>\nхаркать <i>Pl</i>."],
  ["scriba", "<big>scriba</big>\n\n<b>scriba, ae</b> <i>m</i> [scribo]\n<b>1)</b> писец, секретарь (s. aedilicius <i>C</i>; quaestorius <i>Sil</i>); военный писарь (s. equitum <i>QC</i>);\n<b>2)</b> переписчик (s. librarius <i>Vr</i>)."],
  ["scribatus", "<big>scribatus</big>\n\n<b>scribatus, us</b> <i>m</i> [scriba]\nдолжность писца <i>CJ</i>."],
  ["scribilita", "<big>scribilita</big>\n\n<b>scrib(i)lita, ae</b> <i>f</i>\nпирог из муки, сыра и мёда <i>Pl, Cato, Pt, M</i>."],
  ["scribo", "<big>scribo</big>\n\n<b>I scribo, scripsi, scriptum, ere</b>\n<b>1)</b> чертить, вырезывать (письмена), гравировать (nomen suum in Trajani templo <i>Spart</i>); расписывать (frontem tabernae aliqua re <i>Ctl</i>); проводить (<i>остроконечным орудием</i>) (lineam <i>C</i>);\n<b>2)</b> писать (litteras <i>C</i>; aliquid alicui <i>или</i> ad aliquem <i>C etc.</i>): in vento et rapida s. aqua <i>Ctl</i> писать «вилами по воде» || исписывать, покрывать письмом (columnam litteris <i>QC</i>); записывать (senatus consultum <i>C</i>); предписывать (populo leges <i>C</i>); составлять опись (nihil erat scriptum <i>C</i>): scribendo adesse <i>или</i> ad scribendum esse <i>C</i> участвовать в составлении и подписании сенатского постановления;\n<b>3)</b> писать, составлять (testamentum <i>C</i>); сочинять (librum, orationem <i>C</i>): scribendi ars <i>L</i> искусство письменного изложения, литературность; scribendi ferre laborem <i>H</i> заниматься литературным трудом; s. gratias <i>AG</i> письменно благодарить;\n<b>4)</b> описывать (bellum <i>L</i>; res gestas <i>H</i>);\n<b>5)</b> письменно назначать (aliquem heredem <i>C, H</i>);\n<b>6)</b> платить в порядке письменного приказа (s. alicui nummos <i>Pl</i>): s. decem ab aliquo <i>H</i> сделать кому-л. письменное распоряжение об уплате 10000 сестерциев (кому-л.);\n<b>7)</b> набирать (milites, legiones <i>Sl</i>); вносить в списки (colonos <i>L</i>): s. aliquem gregis sui <i>H</i> причислять кого-л. к своим друзьям. — <i>См. тж.</i> scriptum.\n\n<b>II scribo, onis</b> <i>m</i> [scribo I, 7]\nвербовщик <i>Eccl</i>."],
  ["Scribonia", "<big>Scribonia</big>\n\n<b>Scribonia, ae</b> <i>f</i>\nСкрибония, <i>бывшая жена Октавиана и П. Сципиона Назики, сестра Л. Скрибония Либона, вышедшая замуж за Августа в 40 г. до н. э., мать Юлии, удалившаяся с ней в изгнание</i> <i>T</i>."],
  ["Scribonius", "<big>Scribonius</big>\n\n<b>Scribonius, a, um</b>\nСкрибоний, <i>римск. nomen; наиболее известны</i>:\n<b>1)</b> C. S. Curio, <i>претор в 121 г. до н. э., известный оратор</i> <i>C</i>;\n<b>2)</b> C. S. Curio, <i>сын предыдущего, народный трибун в 90 г. до н. э., консул в 76 г. до н. э., сторонник Суллы, впоследствии — Цицерона, противник Цезаря</i> <i>C, Fl, Su</i>;\n<b>3)</b> C. S. Curio, <i>сын предыдущего, сторонник Цезаря, погиб в 49 г. до н. э. в Африке, сражать против Юбы Нумидийского</i> <i>C, Su</i>;\n<b>4)</b> L. S. Libo, <i>друг Помпея и Цицерона, тесть Секста Помпея, консул в 34 г. до н. э.</i> <i>C</i>;\n<b>5)</b> S. Largus, <i>врач времён Тиберия и Клавдия, автор «Compositiones medicamentorum».</i>"],
  ["scriniolum", "<big>scriniolum</big>\n\n<b>scriniolum, i</b> <i>n</i> [<i>demin. к</i> scrinium]\nларчик, <i>перен.</i> тайник (recondere aliquid in scriniolo pectoris sui <i>Hier</i>)."],
  ["scrinium", "<big>scrinium</big>\n\n<b>scrinium, i</b> <i>n</i>\nкруглый ящик, ларец, хранилище (<i>для хранения книг, бумаг и пр.</i>) <i>Sl, H, PM etc.</i>"],
  ["scripse", "<big>scripse</big>\n\n<b>scripse</b> <i>LM, Aus</i>\n= scripsisse (<i>inf. pf. к</i> scribo)."],
  ["scripsti", "<big>scripsti</big>\n\n<b>scripsti</b> <i>Pl</i>\n= scripsisti (<i>pf. к</i> scribo)."],
  ["scriptilis", "<big>scriptilis</big>\n\n<b>scriptilis, e</b> [scribo]\nмогущий быть написанным <i>Amm</i>."],
  ["scriptio", "<big>scriptio</big>\n\n<b>scriptio, onis</b> <i>f</i> [scribo]\n<b>1)</b> писание, письмо (impedire scriptionem alicujus <i>C</i>);\n<b>2)</b> письменное сочинение (philosophiae scriptiones <i>C</i>);\n<b>3)</b> письменное выражение, текст: ex scriptione <i>C</i> согласно написанному, из написанного;\n<b>4)</b> запись (<i>долгов</i>) <i>Vr</i>."],
  ["scriptito", "<big>scriptito</big>\n\n<b>scriptito, avi, atum, are</b> [<i>intens. к</i> scribo]\n<b>1)</b> часто <i>или</i> много писать: haec ad me scribas velim vel potius scriptites <i>C</i> я хотел бы, чтобы ты писал мне об этом, да почаще;\n<b>2)</b> иметь обыкновение писать, сочинять (orationes multas <i>C</i>)."],
  ["scriptor", "<big>scriptor</big>\n\n<b>scriptor, oris</b> <i>m</i> [scribo]\n<b>1)</b> писец, секретарь (alicujus <i>C, T</i>);\n<b>2)</b> переписчик (s. librarius <i>H</i>);\n<b>3)</b> писатель: scriptores optimi (praestantissimi) <i>C, Q</i> писатели-классики;\n<b>4)</b> автор (libri, fabularum <i>Q</i>); повествователь: s. alicujus rei <i>H, Q</i> автор рассказа (сочинения) о чём-л.;\n<b>5)</b> составитель (testamenti <i>Su, Dig</i>): s. legum <i>C</i> законодатель; s. rerum <i>C, L</i> <i>или</i> historiarum <i>PM, J</i> летописец, историк;\n<b>6)</b> поэт (scriptorum chorus omnis amat nemus et fugit urbem <i>H</i>)."],
  ["scriptorium", "<big>scriptorium</big>\n\n<b>scriptorium, i</b> <i>n</i> [scriptorius]\n<b>1)</b> металлический грифель, стиль (<i>для письма по воску</i>) <i>Is</i>;\n<b>2)</b> скрипторий, комната для переписчиков (<i>в монастырях</i>) <i>Eccl</i>."],
  ["scriptorius", "<big>scriptorius</big>\n\n<b>scriptorius, a, um</b> [scribo]\nписчий (calamus, atramentum <i>CC</i>)."],
  ["scriptulum", "<big>scriptulum</big>\n\n<b>scriptulum, i</b> <i>n</i> [<i>demin. к</i> scriptum]\nчёрточка, линеечка <i>O</i>."],
  ["scriptum", "<big>scriptum</big>\n\n<b>scriptum, i</b> <i>n</i> [scribo]\n<b>1)</b> линия, черта: lusus duodecim scriptorum <i>Q</i> игра на доске, разграфлённой 12 линиями (на 25 квадратов);\n<b>2)</b> письмо, письменное произведение, сочинение, книга (Qrajorum antiquissima scripta <i>H</i>); aliquid scriptis mandare <i>C</i> записать что-л.; de scripto dicere <i>C</i> говорить по записке; sine scripto loqui <i>C</i> говорить без записки (наизусть); creber in scripto <i>C</i> часто пишущий (письма);\n<b>3)</b> письменное выражение, текст, буквальное значение: ex scripto буквально, в соответствии с буквой (legis <i>C</i>); s. sequi <i>C</i> придерживаться буквального смысла."],
  ["scriptura", "<big>scriptura</big>\n\n<b>scriptura, ae</b> <i>f</i> [scribo]\n<b>1)</b> писание, записывание, запись: mendum scripturae <i>C</i> ошибка в письме, описка; mandata scriptura dare <i>C</i> дать письменные поручения; scriptura aliquid persequi <i>или</i> per scripturam amplecti <i>C</i> записать (написать) что-л.;\n<b>2)</b> сочинение: s. diurna actorum <i>T</i> летопись, хроника; s. sacra, sancta <i>или</i> divina <i>Eccl</i> = Biblia;\n<b>3)</b> надпись (in arca lapidea <i>VM</i>);\n<b>4)</b> писаный закон, буква закона (per ignorantiam scripturae commissa <i>Su</i>);\n<b>5)</b> (<i>тж.</i> s. suprema <i>CTh</i>) письменное завещание <i>C, Q, Dig</i>;\n<b>6)</b> налог на пастбища, плата за выпас, сбор за пользование выгоном (vectigal ex scriptura <i>C</i>);\n<b>7)</b> очерк, очертание, линия (malarum s. <i>Pt</i>)."],
  ["scripturarius", "<big>scripturarius</big>\n\n<b>scripturarius, i</b> <i>m</i> [scriptura 6]\nсборщик платы за выпас <i>LM</i>."],
  ["scripturio", "<big>scripturio</big>\n\n<b>scripturio, —, —, ire</b> [<i>desiderat. к</i> scribo]\nиметь желание писать <i>Sid</i>."],
  ["scriptus", "<big>scriptus</big>\n\n<b>I scriptus, a, um</b> <i>part. pf. к</i>\nscribo.\n\n<b>II scriptus, us</b> <i>m</i> [scribo]\nписьмоводительство, должность писца <i>или</i> секретаря (s. quaestorius <i>Su</i>): scriptum facere <i>L, AG</i> быть писцом, занимать должность секретаря."],
  ["scripularis", "<big>scripularis</big>\n\n<b>scripularis, e</b> [scripulum]\nвесом в 1 скрупул (differentia <i>PM</i>)."],
  ["scripulatim", "<big>scripulatim</big>\n\n<b>scripulatim</b> [scripulum]\nпо скрупулам (colligere ac miscere aliquid <i>PM</i>)."],
  ["scripulum", "<big>scripulum</big>\n\n<b>scripulum, i</b> <i>n</i>\nскрупул:\n<b>1)</b> <i>мера веса</i> = 1/24 унции = 1/288 асса = 1,137г <i>C, Col, Vtr</i>;\n<b>2)</b> <i>мера площади</i>= 1/288 югера = 8,75 <i>кв. м</i> <i>Vr, Col</i>;\n<b>3)</b> <i>мера времени</i> = 1/24 часа <i>Fronto</i>;\n<b>4)</b> минута, <i>т. е.</i> наименьшая часть дугового <i>или</i> углового градуса <i>PM</i>."],
  ["scrobiculus", "<big>scrobiculus</big>\n\n<b>scrobiculus, i</b> <i>m</i> [<i>demin. к</i> scrobis]\nямка, ямочка <i>Vr, Col, PM</i>."],
  ["scrobis", "<big>scrobis</big>\n\n<b>scrobis, is</b> <i>m, f</i>\nяма (scrobem fodere <i>Vr</i> <i>и</i> effodere <i>T</i>): s. virginalis <i>Eccl</i> = cunnus."],
  ["scrofa", "<big>scrofa</big>\n\n<b>scrofa, ae</b> <i>f</i>\nсвинья, свиная матка <i>Pl, Vr, Col, Pt, Pall</i>."],
  ["scrofinus", "<big>scrofinus</big>\n\n<b>scrofinus, a, um</b> [scrofa]\nсвиной (fel <i>PM</i>)."],
  ["scrofipascus", "<big>scrofipascus</big>\n\n<b>scrofipascus, i</b> <i>m</i> [scrofa + pasco]\nсвиновод <i>Pl</i>."],
  ["scrofulae", "<big>scrofulae</big>\n\n<b>scrofulae, arum</b> <i>f</i>\nзолотушные опухоли, золотуха <i>Veg</i>."],
  ["scrotum", "<big>scrotum</big>\n\n<b>scrotum, i</b> <i>n</i> <i>анат.</i>\nмошонка <i>CC</i>."],
  ["scrupea", "<big>scrupea</big>\n\n<b>scrupea, ae</b> <i>f</i> <i>Acc</i>\n= scrupulus."],
  ["scrupeda", "<big>scrupeda</big>\n\n<b>scrupeda, ae</b> <i>m, f</i>\nс трудом волочащий ноги, прихрамывающий <i>Pl ap. AG, Vr</i>."],
  ["scrupeus", "<big>scrupeus</big>\n\n<b>scrupeus, a, um</b> [scrupus]\n<b>1)</b> крутой, обрывистый (saxum <i>Enn</i>);\n<b>2)</b> покрытый острыми камнями, каменистый (spelunca <i>V</i>; ora <i>VF</i>);\n<b>3)</b> <i>перен.</i> мучительный (difficultas <i>Aus</i>)."],
  ["scrupipeda", "<big>scrupipeda</big>\n\n<b>scrupipeda, ae</b> <i>m, f</i> <i>v. l.</i>\n= scrupeda."],
  ["scruposus", "<big>scruposus</big>\n\n<b>scruposus, a, um</b> [scrupus]\n<b>1)</b> <i>Pl, Lcr, Ap</i> (<i>v. l.</i>) = scrupeus;\n<b>2)</b> трудный (ratio <i>Lcr</i>)."],
  ["scrupula-", "<big>scrupula-</big>\n\n<b>scrupula-</b> <i>v. l.</i>\n= scripula-."],
  ["scrupulose", "<big>scrupulose</big>\n\n<b>scrupulose</b> [scrupulosus]\nкрайне тщательно, в высшей степени мелочно, с величайшей точностью <i>Col, Q, PM etc.</i>"],
  ["scrupulositas", "<big>scrupulositas</big>\n\n<b>scrupulositas, atis</b> <i>f</i>\nвеличайшая тщательность, точность (in aliqua disciplina <i>Col</i>)."],
  ["scrupulosus", "<big>scrupulosus</big>\n\n<b>scrupulosus, a, um</b> [scrupulus]\n<b>1)</b> каменистый (ager <i>Ap</i>);\n<b>2)</b> обрывистый, крутой (cotes <i>C</i>);\n<b>3)</b> крайне тщательный, до мелочей точный (disputatio <i>Q</i>; cura <i>VM</i>; lector <i>Ap</i>)."],
  ["scrupulum", "<big>scrupulum</big>\n\n<b>scrupulum, i</b> <i>n</i> <i>v. l.</i>\n= scripulum."],
  ["scrupulus", "<big>scrupulus</big>\n\n<b>scrupulus, i</b> <i>m</i> [<i>demin. к</i> scrupus]\n<b>1)</b> острый камешек <i>или</i> песчинка (scrupuli perparvuli <i>Sol</i>);\n<b>2)</b> беспокойство, опасение, недоумение, сомнение, внутренняя тревога (sine scrupulo <i>Col</i>): evellere alicui scrupulum ex animo <i>C</i> положить конец чьему-л. недоумению, успокоить кого-либо; scrupulum injicere alicui <i>C</i> внушить кому-л. сомнение;\n<b>3)</b> <i>pl.</i> ненужные тонкости, пустяки (<i>sc.</i> philosophorum <i>AG</i>)."],
  ["scrupus", "<big>scrupus</big>\n\n<b>scrupus, i</b> <i>m</i>\n<b>1)</b> острый камень <i>Pt</i>;\n<b>2)</b> <i>C</i> = scrupulus 2."],
  ["scruta", "<big>scruta</big>\n\n<b>scruta, orum</b> <i>n</i>\nстарые вещи, старье, ветошь, хлам <i>LM, H, Pt</i>."],
  ["scrutans", "<big>scrutans</big>\n\n<b>scrutans, antis</b>\n<b>1.</b> <i>part. praes. к</i> scrutor;\n<b>2.</b> <i>adj.</i> тщательный, обстоятельный, точный (alicujus rei scrutantissimus <i>Amm</i>)."],
  ["scrutanter", "<big>scrutanter</big>\n\n<b>scrutanter</b>\nобстоятельно (discutere <i>Ambr</i>)."],
  ["scrutaria", "<big>scrutaria</big>\n\n<b>scrutaria, ae</b> <i>f</i> [scruta]\nторговля старыми вещами, профессия старьёвщика: scrutariam facere <i>Ap</i> торговать старым хламом, <i>перен.</i> заниматься мелкими кражами."],
  ["scrutarius", "<big>scrutarius</big>\n\n<b>scrutarius, i</b> <i>m</i> [scruta]\nстарьёвщик <i>LM</i>."],
  ["scrutatio", "<big>scrutatio</big>\n\n<b>scrutatio, onis</b> <i>f</i> [scrutor]\n<b>1)</b> обыскивание, обыск <i>Sen</i>; таможенный досмотр <i>Q</i>;\n<b>2)</b> тщательное исследование (versus Vergilii <i>AG</i>)."],
  ["scrutator", "<big>scrutator</big>\n\n<b>scrutator, oris</b> <i>m</i> [scrutor]\n<b>1)</b> производящий обыск, обыскивающий <i>Su</i>;\n<b>2)</b> исследователь, изыскатель (fati <i>Lcn</i>; rerum futurarum <i>Lact</i>): s. profundi pelagi <i>St</i> = piscator; s. auri <i>Lcn</i> золотоискатель."],
  ["scrutillus", "<big>scrutillus</big>\n\n<b>scrutillus, i</b> <i>m</i>\nсвиная колбаса <i>Pl</i>."],
  ["scrutinium", "<big>scrutinium</big>\n\n<b>scrutinium, i</b> <i>n</i> [scrutor]\nисследование, осмотр <i>Ap, Amm, Vlg</i>."],
  ["scruto", "<big>scruto</big>\n\n<b>scruto, avi, atum, are</b> <i>VM, Sen</i>\n= scrutor."],
  ["scrutor", "<big>scrutor</big>\n\n<b>scrutor, atus sum, ari</b> <i>depon.</i> [scruta]\n<b>1)</b> перерыть, обыскать, обшарить (domos, naves <i>C</i>; omnia foramina parietum <i>Pt</i>); обрыскать, изъездить (mare <i>T</i>); прощупывать, рыться, ворошить (paleam <i>Pt</i>; gladio ignem s. <i>H</i>);\n<b>2)</b> испытывать, выведывать (animos ceterorum <i>T</i>); исследовать, стараться проникнуть, вникать (s. arcanum alicujus <i>H</i>; vetera <i>C</i>; origines nominum <i>Q</i>; verborum significationem <i>Dig</i>)."],
  ["scudicia", "<big>scudicia</big>\n\n<b>scudicia, ae</b> <i>f</i>\nзаступ <i>Is</i>."],
  ["sculna", "<big>sculna</big>\n\n<b>sculna, ae</b> <i>m</i> [<i>из</i> *seculna <i>от</i> sequor]\nпосредник, третейский судья <i>AG, Macr</i>."],
  ["sculpo", "<big>sculpo</big>\n\n<b>sculpo, sculpsi, sculptum, ere</b>\n<b>1)</b> вырезывать (gemmas faberrime <i>Ap</i>); высекать (marmora <i>PM</i>; ex saxo sculptus <i>C</i>): s. ebur <i>O</i> ваять статую из слоновой кости;\n<b>2)</b> запечатлевать (aliquid in animo alicujus sculptum <i>Ap</i>)."],
  ["sculponeae", "<big>sculponeae</big>\n\n<b>sculponeae, arum</b> <i>f</i> [sculpo]\nобувь на деревянной подошве <i>Cato, Pl</i>."],
  ["sculponeatus", "<big>sculponeatus</big>\n\n<b>sculponeatus, a, um</b>\nобутый в sculponeae (<i>см.</i>) <i>Vr</i>."],
  ["sculpsi", "<big>sculpsi</big>\n\n<b>sculpsi</b> <i>pf. к</i>\nsculpo."],
  ["sculptile", "<big>sculptile</big>\n\n<b>sculptile, is</b> <i>n</i> [sculptilis]\nрезное <i>или</i> скульптурное изображение, статуэтка (sculptilia deorum <i>Vlg</i>)."],
  ["sculptilis", "<big>sculptilis</big>\n\n<b>sculptilis, e</b> [sculpo]\nрезной, высеченный: sculptile opus dentis Numidae <i>O</i> изделие из слоновой кости."],
  ["sculptor", "<big>sculptor</big>\n\n<b>sculptor, oris</b> <i>m</i> [sculpo]\nрезчик, ваятель, скульптор <i>PJ, Vlg, Eccl</i>."],
  ["sculptura", "<big>sculptura</big>\n\n<b>sculptura, ae</b> <i>f</i> [seulpo]\n<b>1)</b> резьба, ваяние, скульптура <i>Q, Su</i>;\n<b>2)</b> резная <i>или</i> скульптурная работа <i>PM, Vtr</i>."],
  ["scultator", "<big>scultator</big>\n\n<b>scultator, oris</b> <i>m</i> [<i>из</i> auscultator]\nразведчик <i>Veg</i> (<i>v. l.</i>)."],
  ["scurra", "<big>scurra</big>\n\n<b>scurra, ae</b> <i>m</i>\n<b>1)</b> фат, праздношатающийся, вертопрах <i>Pl, rhH, C</i>;\n<b>2)</b> скоморох, шут, балагур <i>C</i>;\n<b>3)</b> льстец, прихлебатель <i>H</i>;\n<b>4)</b> солдат императорской охраны, наёмник <i>Treb, Lampr</i>."],
  ["scurrilis", "<big>scurrilis</big>\n\n<b>scurrīlis, e</b> [scurra]\nшутовской (jocus <i>C</i>); развесёлый (lusus <i>VM</i>)."],
  ["scurrilitas", "<big>scurrilitas</big>\n\n<b>scurrīlitās, ātis</b> <i>f</i> [scurrilis]\nшутовство, балагурство <i>Q, T, Sid, Vlg</i>."],
  ["scurriliter", "<big>scurriliter</big>\n\n<b>scurrīliter</b>\nшутовски <i>PJ, Just</i>."],
  ["scurror", "<big>scurror</big>\n\n<b>scurror, —, ārī</b> <i>depon.</i> [scurra]\nподольщаться, угодничать, льстить (alicui <i>H</i>)."],
  ["scurrula", "<big>scurrula</big>\n\n<b>scurrula, ae</b> <i>m</i> [<i>demin. к</i> scurra]\nмаленький балагур, весельчак <i>Ap, Eccl</i>."],
  ["scuta", "<big>scuta</big>\n\n<b>scuta, ae</b> <i>f</i> <i>LM</i>\n= scutra."],
  ["scutale", "<big>scutale</big>\n\n<b>scutale, is</b> <i>n</i> [scutum]\nремень пращи <i>L</i>."],
  ["scutarius", "<big>scutarius</big>\n\n<b>I scutarius, a, um</b> [scutum]\nизготовляющий щиты (fabrica <i>Veg</i>).\n\n<b>II scutarius, i</b> <i>m</i> [scutum]\n<b>1)</b> мастер, делающий щиты <i>Pl</i>;\n<b>2)</b> солдат-щитоносец, <i>т. е.</i> царский телохранитель <i>Amm, Vlg</i>."],
  ["scutatus", "<big>scutatus</big>\n\n<b>scutatus, a, um</b> [scutum]\nвооружённый щитом (cohortes <i>Cs</i>; equites <i>V</i>)."],
  ["scutella", "<big>scutella</big>\n\n<b>scutella, ae</b> <i>f</i> [<i>demin. к</i> scutra]\n<b>1)</b> чашка <i>C</i>;\n<b>2)</b> блюдце <i>Dig</i>."],
  ["scutica", "<big>scutica</big>\n\n<b>scutica, ae</b> <i>f</i> (<i>греч.</i>)\nремень, плётка, кнутик <i>J, Su</i>: ne scutica dignum horribili sectere flagello <i>H</i> не наказывай грозным бичом того, кто заслуживает лишь плётки."],
  ["scutigerulus", "<big>scutigerulus</big>\n\n<b>scutigerulus, i</b> <i>m</i> [scutum + gerulus]\nщитоносец (<i>слуга, носивший щит ва своим господином</i>) <i>Pl</i>."],
  ["scutra", "<big>scutra</big>\n\n<b>scutra, ae</b> <i>f</i>\nплоское блюдо <i>или</i> чаша <i>Pl, Cato, Vlg</i>."],
  ["scutriscum", "<big>scutriscum</big>\n\n<b>scutriscum, i</b> <i>n</i> [<i>demin. к</i> scutra]\nчашечка <i>или</i> блюдечко <i>Cato</i>."],
  ["scutula", "<big>scutula</big>\n\n<b>I scutula, ae</b> <i>f</i> (<i>греч.</i>)\nкаток, валик (<i>для перемещения тяжёлых предметов</i>) <i>Cato, Cs</i>.\n\n<b>II scutula, ae</b> <i>f</i> [<i>demin. к</i> scutra]\n<b>1)</b> плоская миска, блюдо (<i>прямоугольной или ромбоидальной формы</i>) <i>T, M</i>;\n<b>2)</b> (<i>тж.</i> s. oblonga <i>T</i>) прямоугольник <i>или</i> ромб <i>T, Vtr, Pall</i>."],
  ["scutulatus", "<big>scutulatus</big>\n\n<b>scutulatus, a, um</b> [scutula II]\n<b>1)</b> разделённый на прямоугольники <i>или</i> ромбы <i>или</i> выложенный четырёхугольными плитками (pavimentum <i>M</i>); клетчатый (textus vestis <i>PM</i>; <i>sc.</i> vestimenta <i>J</i>);\n<b>2)</b> серый в яблоках (color equi <i>Pall</i>)."],
  ["scutulum", "<big>scutulum</big>\n\n<b>scutulum, i</b> <i>n</i> [<i>demin. к</i> scutum]\n<b>1)</b> небольшой щит, щиток <i>C</i>;\n<b>2)</b> <i>pl. анат.</i> лопатки <i>CC</i>."],
  ["scutum", "<big>scutum</big>\n\n<b>scutum, i</b> <i>n</i>\n<b>1)</b> большой щит (<i>в форме выгнутого прямоугольника, прибл.</i> 120 X 80 <i>см</i>) <i>Cs, L etc.</i>: per s., per ocream <i>погов. Pt</i> = преодолевая все препятствия, не щадя усилий;\n<b>2)</b> <i>перен.</i> щит, оплот (Fabius Cunctator — s. imperii <i>Fl</i>)."],
  ["scyfus", "<big>scyfus</big>\n\n<b>scyfus, i</b> <i>m</i> <i>Treb</i>\n= scyphus."],
  ["Scylaceum", "<big>Scylaceum</big>\n\n<b>Scylaceum, i</b> <i>n</i>\nСкилакий, <i>мыс близ Scylacium</i> <i>V, PM, Mela</i>."],
  ["Scylaceus", "<big>Scylaceus</big>\n\n<b>Scylaceus, a, um</b> [Scylacium]\nскилакийский (litora <i>O</i>)."],
  ["Scylacium", "<big>Scylacium</big>\n\n<b>Scylacium, i</b> <i>n</i>\nСкилакий, <i>город на вост. побережье Бруттия</i> (<i>ныне</i> Squillace) <i>VP</i>."],
  ["Scylla", "<big>Scylla</big>\n\n<b>Scylla, ae</b> <i>f</i>\nСцилла (Скилла):\n<b>1)</b> <i>утёс на италийской стороне Сицилийского пролива, против водоворота Харибды на сицилийской стороне</i> <i>V, Mela etc.</i>;\n<b>2)</b> <i>дочь Форкиды и Кратаиса, из ревности превращенная волшебницей Киркой в морское, опоясанное свирепыми псами, чудовище, жившее в пещере утёса Сцилла и уничтожавшее всё, что проходило мимо этого утёса</i> <i>C, O</i>;\n<b>3)</b> <i>дочь мегарского царя Ниса, предавшая своего отца и родной город критскому царю Миносу и превращенная в птицу ciris</i> (<i>у поэтов</i> — <i>O, V, Prp</i> — <i>часто смешивается со Сциллой, дочерью Форкиды</i>)."],
  ["Scyllaeum", "<big>Scyllaeum</big>\n\n<b>Scyllaeum, i</b> <i>n</i>\nСкиллей:\n<b>1)</b> <i>вост. мыс Пелопоннеса на трезенском побережье Ареолиды</i> <i>L</i>;\n<b>2)</b> = утёс Сцилла (<i>см.</i> Scylla 1) <i>C, PM</i>."],
  ["Scyllaeus", "<big>Scyllaeus</big>\n\n<b>Scyllaeus, a, um</b> [Scylla]\nскиллейский (rabies <i>V</i>; undae <i>Lcn</i>; monstra <i>St</i>)."],
  ["scymnus", "<big>scymnus</big>\n\n<b>scymnus, i</b> <i>m</i> (<i>греч.</i>)\nмолодой зверь, детёныш (scymni leonum <i>Lcr</i>)."],
  ["scyphulus", "<big>scyphulus</big>\n\n<b>scyphulus, i</b> <i>m</i> [<i>demin. к</i> scyphus]\nбокальчик <i>PM</i>."],
  ["scyphus", "<big>scyphus</big>\n\n<b>scyphus, i</b> <i>m</i> (<i>греч.</i>)\nчаша, кубок, бокал <i>Pl, T, Pt</i>: inter scyphos <i>C</i> за стаканом вина."],
  ["Scyreis", "<big>Scyreis</big>\n\n<b>Scyreis, idis</b> <i>f</i> [Scyrus] <i>St</i>\n= Scyrias 2."],
  ["Scyrias", "<big>Scyrias</big>\n\n<b>Scyrias, adis</b> <i>f</i> [Scyrus]\n<b>1)</b> скиросская: S. puella <i>O</i> = Deidamia;\n<b>2)</b> скиросска <i>St</i>."],
  ["Scyrius", "<big>Scyrius</big>\n\n<b>Scyrius, a, um</b> [Scyrus]\nскиросский: S. juvenis <i>SenT</i> = Pyrrhus (<i>сын Ахилла</i>); Scyria pubes <i>V</i> = воины Пирра."],
  ["Scyrus", "<big>Scyrus</big>\n\n<b>Scyrus (-os), i</b> <i>f</i>\nСкирос:\n<b>1)</b> <i>скалистый о-в во Фракийском море, к сев. от Эвбеи, где царствовал Ликомед; здесь скрывался Ахилл и здесь дочь Ликомеда, Деидамия, родила от него Пирра</i> <i>Ctl, Mela, C</i>;\n<b>2)</b> <i>город во Фригии</i> <i>PM</i>."],
  ["scytala", "<big>scytala</big>\n\n<b>scytala, ae</b> <i>f</i> (<i>греч.</i>; <i>лат.</i> clava <i>Nep</i>)\nскитала:\n<b>1)</b> <i>валик, палка для передачи секретных донесений в Спарте</i> (<i>донесение писалось вдоль на обвитом вокруг палки ремне, который потом снимался; чтобы прочесть послание, адресат снова наматывал ремень на ту же палку</i>) <i>AG, Aus</i>;\n<b>2)</b> <i>род змеи</i> (<i>с телом якобы одинаковой толщины от головы до хвоста</i>) <i>PM, Lcn, Sol</i>."],
  ["scytale", "<big>scytale</big>\n\n<b>scytale, es</b> <i>f</i> <i>v. l.</i>\n= scytala."],
  ["scytalosagittipellifer", "<big>scytalosagittipellifer</big>\n\n<b>scytalo–sagitti–pelli–fer</b> (<i>v. l.</i> <b>-ger</b>), <b>eri</b> <i>m</i>\nвооружённый палицей и стрелами и одетый в (львиную) шкуру (<i>эпитет Геркулеса</i>) <i>Tert</i>."],
  ["scytanum", "<big>scytanum</big>\n\n<b>scytanum</b> (<i>v. l.</i> <b>scytatum</b>), <b>i</b> <i>n</i>\nпротрава (<i>для обработки материала перед окрашиванием</i>) <i>PM</i>."],
  ["Scytha", "<big>Scytha</big>\n\n<b>I Scytha</b> <i>и</i> <b>Scythes, ae</b> <i>m</i>\nскиф <i>C, PM, Lcn etc.</i>\n\n<b>II Scytha, ae</b> <i>adj. m</i> <i>SenT, M, St</i>\n= Scythicus."],
  ["Scythia", "<big>Scythia</big>\n\n<b>Scythia, ae</b> <i>f</i>\nСкифия, страна скифов <i>C, Mela, V, O</i>."],
  ["Scythicus", "<big>Scythicus</big>\n\n<b>Scythicus, a, um</b>\nскифский: amnis S. <i>H</i> = Tanais (<i>ныне</i> Дон); Diana Scythica <i>O</i> = Диана Тавридская; fretum Scythicum <i>O</i> = Pontus Euxinus (<i>ныне</i> Чёрное море)."],
  ["Scythis", "<big>Scythis</big>\n\n<b>Scythis, idis</b> <i>и</i> <b>Scythissa, ae</b> <i>f</i>\nскифянка <i>O, Nep</i>."],
  ["Scytholatronia", "<big>Scytholatronia</big>\n\n<b>Scytholatronia, ae</b> <i>f</i> [Scytha + latro] <i>шутл.</i>\nстрана скифов-наёмников <i>Pl</i> (<i>v. l.</i>)."],
  ["se", "<big>se</big>\n\n<b>I se</b> <i>acc. abl. к</i>\nsui.\n\n<b>V se (sed)</b> <i>арх. praep. cum abl.</i>\n(= sine 1) без: se fraude esto <i>LXIIT</i> да останется без наказания, <i>т. е.</i> пусть будет оправдан.\n\n<b>II se-</b> [<i>из</i> sex]\n<i>приставка</i> шести- (<i>напр.</i>, se–jugis I, se–mestris).\n\n<b>III se-</b> [<i>из</i> semi]\n<i>приставка</i> полу-, пол- (<i>напр.</i>, se–libra).\n\n<b>IV se-</b> [<i>из</i> sed]\n<i>приставка, выражающая отделение, устранение</i> (se–curus = sine cura; se–cedo; se–orsus)."],
  ["sebacius", "<big>sebacius</big>\n\n<b>sebacius (sebaceus), i</b> <i>m</i> [sebum]\nсальная свеча <i>Ap</i>."],
  ["Sebad-", "<big>Sebad-</big>\n\n<b>Sebad-</b> <i>Macr</i>\n= Sabaz-."],
  ["sebalis", "<big>sebalis</big>\n\n<b>sebalis, e</b> [sebum]\nсальный (fax <i>Amm</i>)."],
  ["Sebethis", "<big>Sebethis</big>\n\n<b>Sebethis, idis</b> <i>f</i>\nСебетида, дочь Себета (<i>нимфа</i>) <i>V</i>."],
  ["Sebethos", "<big>Sebethos</big>\n\n<b>Sebethos, i</b> <i>m</i>\nСебет:\n<b>1)</b> <i>речка в Кампании, впадающая в Путеоланский залив</i> <i>St</i>;\n<b>2)</b> <i>бог этой речки</i> (<i>см.</i> Sebethis)."],
  ["sebo", "<big>sebo</big>\n\n<b>sebo, —, —, are</b> [sebum]\nобливать салом, окунать в сало: s. candelas <i>Col</i> лить сальные свечи."],
  ["sebosus", "<big>sebosus</big>\n\n<b>sebosus, a, um</b> [sebum]\nсальный (medulla <i>PM</i>)."],
  ["sebum", "<big>sebum</big>\n\n<b>sebum, i</b> <i>n</i>\nсало <i>Cs, Col, PM</i>."],
  ["secabilis", "<big>secabilis</big>\n\n<b>secabilis, e</b> [seco]\nмогущий разрезаться, <i>т. е.</i> делимый <i>Aus, Eccl</i>."],
  ["secale", "<big>secale</big>\n\n<b>secale, is</b> <i>n</i> <i>предпол.</i>\nрожь (<i>Secale Cereale</i>, L.) <i>PM</i>."],
  ["secamenta", "<big>secamenta</big>\n\n<b>secamenta, orum</b> <i>n</i> [seco]\nрезная работа, резьба <i>PM</i>."],
  ["secedo", "<big>secedo</big>\n\n<b>se–cedo, cessi, cessum, ere</b>\n<b>1)</b> уходить, удаляться (de <i>или</i> ab aliqua re <i>Pl, Lcr etc.</i>; in abditam partem aedium <i>Sl</i>; in se ipse secede <i>Sen</i>);\n<b>2)</b> отделяться, отходить, откалываться (plebs a patribus secessit <i>Sl</i>);\n<b>3)</b> переходить (ad Christum <i>Lact</i>): ad reliqua secessimus <i>Pt</i> мы перешли к прочим делам."],
  ["secena", "<big>secena</big>\n\n<b>secena, ae</b> <i>f</i> [seco]\nрежущий инструмент <i>LA</i>."],
  ["secerno", "<big>secerno</big>\n\n<b>se–cerno, crevi, cretum, ere</b>\n<b>1)</b> отделять (aliquid aliqua re <i>или</i> ab, ex aliqua re <i>L, H, Su etc.</i>); выделять (aliquid praedae in publicum <i>L</i>); уводить, отрывать (aliquem populo <i>H</i>); разделять (terras ac mare <i>Lcr</i>);\n<b>2)</b> исключать, устранять, удалять (minus idoneos senatores <i>Su</i>);\n<b>3)</b> отличать (blandum amicum a vero <i>C</i>; honestum turpi <i>H</i>). — <i>См. тж.</i> secretum <i>и</i> secretus."],
  ["secespita", "<big>secespita</big>\n\n<b>secespita, ae</b> <i>f</i> [seco]\nжертвенный нож <i>Su</i>."],
  ["secessi", "<big>secessi</big>\n\n<b>secessi</b> <i>pf. к</i>\nsecedo."],
  ["secessio", "<big>secessio</big>\n\n<b>secessio, anis</b> <i>f</i> [secedo]\n<b>1)</b> отход в сторону: secessionem facere <i>Cs, L</i> собираться в стороне (<i>для обсуждения чего-либо</i>);\n<b>2)</b> отделение, уход, раскол (s. plebis <i>C</i>; secessionem facere in Aventinum montem <i>L</i>)."],
  ["secessus", "<big>secessus</big>\n\n<b>secessus, us</b> <i>m</i> [secedo]\n<b>1)</b> удаление, уединение (s. et otia <i>O</i>);\n<b>2)</b> уединённое место, убежище (in secessu sub rupe cava <i>V</i>): privatum secessum habere <i>Pt</i> сидеть в укромном месте;\n<b>3)</b> отделение (animi a corpore <i>AG</i>); уход, раскол (s. plebis <i>PM</i>)."],
  ["secius", "<big>secius</big>\n\n<b>secius</b> <i>adv.</i> <i>v. l.</i>\n= setius."],
  ["secludo", "<big>secludo</big>\n\n<b>secludo, clusi, clusum, ere</b> [se IV + claudo]\n<b>1)</b> запирать отдельно, отделять (incientes, <i>sc.</i> oves <i>Vr</i>); изолировать (a conspectu parentium seclusus <i>C</i>); <i>med.-pass.</i> secludi прятаться (sub ala alicujus <i>Prp</i>);\n<b>2)</b> отрезывать, разобщать (dextrum cornu a sinistro <i>Cs</i>): a corpore vitam s. <i>Pl</i> наложить на себя руки || удалять, отбрасывать (curas <i>V</i>): nemus seclusum <i>V</i> отдалённая (уединённая) роща."],
  ["seclum", "<big>seclum</big>\n\n<b>seclum, i</b> <i>n</i> <i>v. l.</i>\n= saeculum."],
  ["seclusorium", "<big>seclusorium</big>\n\n<b>seclusorium, i</b> <i>n</i> [secludo]\nклетка для домашней птицы, птичник, курятник <i>Vr</i>."],
  ["seco", "<big>seco</big>\n\n<b>seco, secui, sectum, are</b> (<i>part. fut.</i> secaturus)\n<b>1)</b> срезать, стричь (capillos <i>M</i>; unguem <i>H</i>); скашивать, снимать (с полей) (pabulum <i>Cs</i>); распиливать (marmora <i>H</i>); покрывать резьбой (dona secto elephanto gravia <i>V</i>); раскалывать (robur <i>V</i>); срубать: pyra taedis atque ilice secta <i>V</i> костёр из нарубленных сосен и дубов || обрубать (abietem <i>PM</i>);\n<b>2)</b> оперировать (aliquem <i>C</i>); ампутировать, отрезывать (alicui aliquid <i>Quintus C</i>);\n<b>3)</b> расцарапывать (genas ungue <i>H</i>); сечь, бичевать (aliquem flagellis <i>H</i>); избивать до крови, калечить (terga verbere <i>Tib</i>); оскоплять (sectus Gallus <i>M</i>); мучить, терзать (podagra aliquem secat <i>Ctl, M</i>);\n<b>4)</b> разрезывать, разделять (gallinam <i>J</i>): orbis sectus <i>H</i> полушарие, полмира;\n<b>5)</b> рассекать (porci ventrem <i>Pt</i>; mare puppe <i>O</i>; aethera pinnis <i>V</i>; pontum pectore <i>V</i>); проходить (viam ad naves <i>V</i>): s. aliquam spem <i>V</i> лелеять какую-л. надежду || проводить, описывать (arcum sub nubibus <i>V</i>);\n<b>6)</b> разделять, расчленять (causas in plura genera <i>C</i>);\n<b>7)</b> улаживать, решать (lites <i>H</i>; res magnas <i>H</i>)."],
  ["secor", "<big>secor</big>\n\n<b>secor</b> <i>v. l.</i>\n= sequor."],
  ["secordia", "<big>secordia</big>\n\n<b>secordia, ae</b> <i>f</i> <i>v. l.</i>\n= socordia."],
  ["secretarium", "<big>secretarium</big>\n\n<b>secretarium, i</b> <i>n</i> [secretus]\n<b>1)</b> потаённое место, тайник (secretaria terrae <i>Ap</i>; mentis <i>Aug</i>);\n<b>2)</b> уединение, отшельничество (avibus haec secretaria magis congruerint <i>Ap</i>)."],
  ["secrete", "<big>secrete</big>\n\n<b>secrete</b> <i>Tert, Aug</i>\n<i>и</i> secretim <i>Amm</i> = secreto."],
  ["secretio", "<big>secretio</big>\n\n<b>secretio, onis</b> <i>f</i> [secerno]\nотделение, разобщение <i>C</i>."],
  ["secreto", "<big>secreto</big>\n\n<b>secreto</b> [secretus]\n<b>1)</b> отдельно, особо (de aliqua re dicere secretius suo loco <i>Col</i>); наедине (loqui <i>Pl, H</i>);\n<b>2)</b> тайно, втайне, секретно (s. hoc audi, tecum habeto <i>C</i>)."],
  ["secretum", "<big>secretum</big>\n\n<b>secretum, i</b> <i>n</i>\n<b>1)</b> отдалённое место, уединение (in secreto <i>L, QC</i>; s. petere <i>Su</i>; in secreta se removere <i>H</i>);\n<b>2)</b> тайна, секрет (secreta pectoris aperire <i>T</i>); <i>pl.</i> секретные бумаги (in secretis alicujus reperire aliquid <i>Su</i>)."],
  ["secretus", "<big>secretus</big>\n\n<b>secretus, a, um</b> [secerno]\n<b>1)</b> особый, отдельный (secretis vocabulis appellari <i>Vr</i>); отделённый, изолированный, далёкий (locus a tumultu s. <i>L</i>); лишённый (aliqua re <i>и</i> alicujus rei <i>Lcr</i>); отдалённый, удалённый, одинокий (pars domus <i>O</i>; in locum secretiorem venire <i>Pt</i>); безлюдный (silva <i>O</i>);\n<b>2)</b> тайный, скрытый, сокровенный (cogitationes, libidines <i>T</i>);\n<b>3)</b> редкий, малоупотребительный (s. et extra vulgarem usum positus <i>Q</i>);\n<b>4)</b> жадный до чужих тайн (auris <i>H</i>)."],
  ["secrevi", "<big>secrevi</big>\n\n<b>secrevi</b> <i>pf. к</i>\nsecerno."],
  ["secta", "<big>secta</big>\n\n<b>secta, ae</b> <i>f</i> [sequor]\n<b>1)</b> путь, правило, образ действия, мыслей <i>или</i> жизни: sequi sectam alicujus <i>C, L</i> идти по чьим-л. стопам, быть чьим-л. приверженцем;\n<b>2)</b> учение, направление, школа (s. Stoicorum <i>T</i>; immota Catonis s. <i>Lcn</i>; duces diversarum sectarum <i>Q</i>);\n<b>3)</b> секта (sectae et haereses <i>Lact</i>);\n<b>4)</b> шайка (<i>sc.</i> latronum <i>Ap</i>)."],
  ["sectacula", "<big>sectacula</big>\n\n<b>sectacula, ae</b> <i>f</i> [sequor]\nряд, вереница: s. natalium <i>Ap</i> линия предков."],
  ["sectarius", "<big>sectarius</big>\n\n<b>sectarius, a, um</b> [seco]\nоскоплённый, кастрированный (vervex <i>Pl</i>)."],
  ["sectatio", "<big>sectatio</big>\n\n<b>sectatio, onis</b> <i>f</i> [sector II]\nпреследование (какой-л. цели), стремление (s. malorum praep arat mortem <i>Vlg</i>)."],
  ["sectator", "<big>sectator</big>\n\n<b>sectator, oris</b> <i>m</i> [sector]\n<b>1)</b> провожатый, спутник (comes vel s. <i>C</i>): sectatores <i>C</i> сопутствующие (политическому кандидату), <i>т. е.</i> клиенты; s. epularum <i>Sid</i> завсегдатай (чужих) пиров, парасит; s. domi <i>T</i> частый гость, друг дома;\n<b>2)</b> приверженец, последователь (Aristotelis <i>AG</i>; Diogenis <i>Ap</i>)."],
  ["sectatrix", "<big>sectatrix</big>\n\n<b>sectatrix, icis</b> <i>f</i>\nпоследовательница, сторонница (alicujus rei <i>Aug</i>)."],
  ["sectilis", "<big>sectilis</big>\n\n<b>sectilis, e</b> [seco]\n<b>1)</b> резной (ebur <i>O</i>);\n<b>2)</b> составной, мозаичный (pavimentum <i>Vtr, Su</i>);\n<b>3)</b> разрезной: porrum sectile <i>Pall, M, J</i> лук-порей."],
  ["sectio", "<big>sectio</big>\n\n<b>sectio, onis</b> <i>f</i> [seco]\n<b>1)</b> резание, разрезание, рассекание, расчленение (corporum <i>Vtr, PM</i>); деление (in infinitum <i>Q</i>);\n<b>2)</b> <i>мед.</i> оперирование, операция (sectiones punctionesque <i>PM</i>); кастрация (<i>sc.</i> pueri <i>Ap</i>);\n<b>3)</b> покупка <i>или</i> продажа с торгов по частям: exercendis apud aerarium sectionibus famosus <i>T</i> известный спекулянт по скупке на аукционах конфискованных имуществ;\n<b>4)</b> раздробление конфискованных поместий, парцеллирование земельных участков: sectionem facere <i>Eutr</i> назначить (производить) распродажу с торгов;\n<b>5)</b> распродаваемое с торгов имущество (sectionem oppidi vendere <i>Cs</i>)."],
  ["sectius", "<big>sectius</big>\n\n<b>sectius</b> <i>adv.</i> [<i>compar. к</i> secus II] <i>v. l.</i>\n= setius."],
  ["sectivus", "<big>sectivus</big>\n\n<b>sectivus, a, um</b> [seco]\nразрезываемый: porrum sectivum <i>Col, PM</i> лук-порей."],
  ["secto", "<big>secto</big>\n\n<b>secto, avi, —, are</b> (= sector II)\nсопровождать (ab aliquo sectari <i>Vr</i>)."],
  ["sector", "<big>sector</big>\n\n<b>I sector, oris</b> <i>m</i> [seco]\n<b>1)</b> режущий: s. feni <i>Col</i> косец; s. collorum <i>C</i> головорез, убийца, душегуб; s. zonarius <i>Pl</i> срезыватель кошельков, карманный вор;\n<b>2)</b> скупщик конфискованных поместий по частям (s. bonorum <i>C</i>): s. favoris <i>Lcn</i> распродающий свою благосклонность, <i>т. е.</i> продающий свой голос (<i>на выборах</i>);\n<b>3)</b> <i>мат.</i> сектор <i>Boet</i>.\n\n<b>II sector, atus sum, ari</b> <i>depon.</i> [<i>intens. к</i> sequor]\n<b>1)</b> неотступно следовать, постоянно сопровождать (s. aliquem totos dies <i>C</i>);\n<b>2)</b> быть в услужении (s. aliquem <i>Pl, C</i>);\n<b>3)</b> гоняться, охотиться (s. apros <i>V</i>);\n<b>4)</b> преследовать, добиваться, стремиться (s. praedam <i>Cs</i>; virtutes <i>T</i>); стараться узнать (longinqua <i>PJ</i>): mitte sectari, rosa quo locorum sera moretur <i>H</i> не хлопочи о том, чтобы узнать, где растёт запоздалая роза;\n<b>5)</b> охотно <i>или</i> усердно посещать (gymnasia <i>PJ</i>): aliquem disciplinae gratia s. <i>Ap</i> слушать чьи-л. лекции."],
  ["sectorius", "<big>sectorius</big>\n\n<b>sectorius, a, um</b> [sector I, 2]\nотносящийся к присуждению конфискованных имуществ (interdictum <i>Dig</i>)."],
  ["sectrix", "<big>sectrix</big>\n\n<b>sectrix, icis</b> <i>f</i> [sector I]\nпокупательница конфискованных поместий (по частям) (s. proscriptionum <i>PM</i>)."],
  ["sectura", "<big>sectura</big>\n\n<b>sectura, ae</b> <i>f</i> [seco]\n<b>1)</b> резание, рассекание <i>Vr, PM</i>;\n<b>2)</b> место разреза, надрез <i>PM</i>;\n<b>3)</b> <i>pl.</i> копи, рудники (secturae aerariae <i>Cs</i>)."],
  ["sectus", "<big>sectus</big>\n\n<b>sectus, a, um</b> <i>part. pf. к</i>\nseco."],
  ["secubatio", "<big>secubatio</big>\n\n<b>secubatio, onis</b> <i>f</i> <i>Sol</i>\n= secubitus."],
  ["secubito", "<big>secubito</big>\n\n<b>secubito, —, —, are</b> [<i>frequ. к</i> secubo]\nлежать <i>или</i> спать всегда одному <i>LM</i>."],
  ["secubitus", "<big>secubitus</big>\n\n<b>secubitus, us</b> <i>m</i> [secubo]\nлежание в одиночку, одинокий сон <i>Ctl, O</i>."],
  ["secubo", "<big>secubo</big>\n\n<b>secubo, bui, (bitum), are</b>\n<b>1)</b> лежать отдельно, спать одиноко <i>Ctl, Tib, O etc.</i>;\n<b>2)</b> вести одинокую жизнь, жить одиноко <i>Prp, Ap</i>."],
  ["secui", "<big>secui</big>\n\n<b>secuī</b> <i>pf. к</i>\nseco."],
  ["secul-", "<big>secul-</big>\n\n<b>sēcul-</b> <i>v. l.</i>\n= saecul-."],
  ["secula", "<big>secula</big>\n\n<b>secula, ae</b> <i>f</i> [seco]\nсерп (<i>в Кампании</i>) <i>Vr</i>."],
  ["secum", "<big>secum</big>\n\n<b>secum</b>\n= cum Se."],
  ["secunda", "<big>secunda</big>\n\n<b>secunda, ae</b> <i>f</i> [secundus]\n(<i>sc.</i> hora) второй час (ante secundam <i>H</i>)."],
  ["secundae", "<big>secundae</big>\n\n<b>secundae, arum</b> <i>f</i>\n<b>1)</b> (<i>sc.</i> partes) вторая роль, второе место (secundas alicui deferre <i>Q</i>); secundas agere <i>Sen</i> (tractare <i>H</i>) уступать, соглашаться, поддакивать;\n<b>2)</b> (<i>тж.</i> s. partus <i>PM</i>) (<i>sc.</i> membranae) послед, детское место <i>CC, Col, Sen, PM</i>."],
  ["secundani", "<big>secundani</big>\n\n<b>secundani, orum</b> <i>m</i> [secundus]\n(<i>sc.</i> milites) солдаты второго легиона <i>L, T</i>."],
  ["secundarium", "<big>secundarium</big>\n\n<b>secundarium, i</b> <i>n</i>\nвторостепенная вещь, мелочь <i>C</i>."],
  ["secundarius", "<big>secundarius</big>\n\n<b>secundarius, a, um</b> [secundus]\nвторостепенный, второго сорта (panis <i>Su</i>)."],
  ["secundatus", "<big>secundatus</big>\n\n<b>secundatus, us</b> <i>m</i> [secundus]\nвторое место, второстепенная роль (alteri primatum dare, alteri secundatum <i>Tert</i>)."],
  ["secunde", "<big>secunde</big>\n\n<b>secunde</b> [secundus]\nсчастливо, успешно (procedere <i>Cato</i>)."],
  ["secundicerius", "<big>secundicerius</big>\n\n<b>secundi–cerius, i</b> <i>m</i> [cera; <i>ср.</i> primicerius]\nчиновник второго ранга <i>CJ</i>."],
  ["secundinae", "<big>secundinae</big>\n\n<b>secundinae, arum</b> <i>f</i> <i>Vlg</i>\n= secundae 2."],
  ["secundo", "<big>secundo</big>\n\n<b>I secundo, avi, —, are</b> [secundus]\nспособствовать, благоприятствовать, споспешествовать, помогать (s. aliquid <i>V, Prp, O etc.</i>): secundante vento <i>T, Just</i> при попутном ветре.\n\n<b>II secundo</b> <i>adv.</i> [secundus]\n<b>1)</b> во-вторых (primum..., s. <i>C</i>);\n<b>2)</b> во второй раз (fossam circumire <i>bAfr</i>);\n<b>3)</b> дважды (s. vel tertio <i>Treb</i>)."],
  ["secundum", "<big>secundum</big>\n\n<b>I secundum</b> <i>adv.</i> [secundus]\n<b>1)</b> за, позади, следом: s. ire <i>Pl</i> следовать, идти за (кем-л.);\n<b>2)</b> во-вторых <i>Vr</i>.\n\n<b>II secundum</b> <i>praep. cum acc.</i>\n<b>1.</b> <i>место</i>:\n<b>1)</b> за (ite s. me <i>Pl</i>); позади (s. aram aliquid abscondere <i>Pl</i>);\n<b>2)</b> вдоль (copias ducere s. flumen <i>Cs</i>): s. lectum concidere <i>Ap</i> упасть рядом с кроватью; s. pedes statuae <i>Pt</i> у подножия статуи; aliquid s. viam ponere <i>Pt</i> положить что-л. на краю дороги;\n<b>2.</b> <i>во времени или по порядку</i>: тотчас же после, вслед за (s. Calendas Januarias <i>C</i>): s. ea <i>C, Sl</i> тотчас же после этого, вслед за этим; s. quietem <i>C</i> погрузившись в сон, во сне;\n<b>3.</b> <i>перен.</i>:\n<b>1)</b> сообразно, соответственно, в согласии с, по (s. legem facere aliquid <i>Q</i>; s. aliquem sentire <i>Su</i>; s. naturam vivere <i>C, Sen</i>): s. dignitalis suae gradus <i>Ap</i> в порядке возрастающей важности;\n<b>2)</b> <i>юр.</i> в пользу (s. aliquem rem judicare <i>C</i>; s. causam alicujus disputare <i>C</i>).\n\n<b>III secundum, i</b> <i>n</i>\nуспех, удача (si aliquid secundi evenisset <i>Nep</i>); <i>pl.</i> счастливые обстоятельства, успехи <i>L, H, T</i>."],
  ["secundus", "<big>secundus</big>\n\n<b>secundus, a, um</b> [sequor]\n<b>1)</b> следующий, второй, другой (secundum bellum Punicum <i>C</i>): secunda mensa <i>C</i> десерт; secundis Saturnalibus <i>C</i> на (во) второй день Сатурналий; secundo lumine <i>Acc, Enn ap. C</i> на следующий (другой) день; s. heres <i>C</i> второочередной наследник; secundae (partes) <i>C, H, Q</i> вторая роль (<i>на сцене</i>); s. ab aliquo <i>bAl, L, H</i> второй после кого-л., следующий за кем-л., стоящий на втором месте после кого-л.; eloquentia nulli secundus <i>Ap</i> никому не уступающий в красноречии; quicquam simile aut secundum <i>H</i> нечто похожее или (хотя бы) близкое;\n<b>2)</b> второсортный, качеством похуже: panis s. <i>H</i> чёрный хлеб;\n<b>3)</b> идущий в том же направлении: secundo flumine <i>Cs, L</i> <i>и</i> secunda aqua <i>L</i> вниз по течению реки; secundo mari <i>L</i> вдоль морского берега <i>или</i> (<i>к</i> 4) при благоприятной погоде на море; secundo sole <i>Nigidius ap. AG</i> по движению солнца; secundo vento <i>C, Lcn</i> по ветру, с попутным ветром; secunda vela <i>O</i> паруса, надутые попутным ветром; s. currus <i>V</i> быстро несущаяся колесница;\n<b>4)</b> благоприятный, предвещающий счастье (omen <i>H</i>); благоприятствующий (lex secunda populo <i>L</i>); доброжелательный (verba <i>O</i>); одобрительный (clamor <i>V</i>): secundo populo <i>C</i> при одобрении народа; Junone secunda <i>V</i> под покровительством <i>или</i> по наущению Юноны; secundis dis <i>V</i> с помощью богов; adi pede secundo <i>V</i> явись в добрый час; secundis auribus accipi <i>L</i> быть благосклонно выслушанным;\n<b>5)</b> счастливый, успешный, удачный (proelium <i>Cs</i>; navigatio <i>T</i>): res secundae <i>C, Cs etc.</i> преуспеяние, счастье."],
  ["secure", "<big>secure</big>\n\n<b>secure</b> [securus]\n<b>1)</b> беззаботно, спокойно, беспечно (lente ac s. aliquid facere <i>Su</i>);\n<b>2)</b> безопасно, в безопасности (intueri aliquid <i>PJ</i>)."],
  ["securiclatus", "<big>securiclatus</big>\n\n<b>securiclatus, a, um</b> [<i>из</i> *securiculatus <i>от</i> securicula]\nимеющий форму топора (<i>сильно расширяющегося к лезвию</i>) <i>или</i> ласточкина хвоста <i>Vtr</i>."],
  ["securicula", "<big>securicula</big>\n\n<b>securicula, ae</b> <i>f</i> [<i>demin. к</i> securis]\n<b>1)</b> небольшой топор, топорик <i>Pl, M</i>;\n<b>2)</b> <i>тех.</i> деревянный шип в форме ласточкина хвоста, сковородень <i>Vtr</i>."],
  ["securifer", "<big>securifer</big>\n\n<b>securifer, fera, ferum</b> [securis + fero] <i>O</i>\n= securiger."],
  ["securiger", "<big>securiger</big>\n\n<b>securiger, gera, geram</b> [securis + gero]\nвооружённый топором, секироносный (puella <i>O</i>; catervae <i>VF</i>)."],
  ["securis", "<big>securis</big>\n\n<b>securis, is</b> (<i>acc.</i> im, <i>реже</i> em; <i>abl.</i> i, <i>реже</i> e) <i>f</i> [seco]\n<b>1)</b> топор, боевая секира: s. anceps <i>Pl, O</i> (bipennis <i>Vr</i>) двойной (обоюдоострый) топор; securi ferire (percutere) <i>C</i> казнить топором, обезглавливать; securi Tenedia <i>C</i> тенедосским топором, <i>т. е.</i> беспощадно (<i>от тенедосского царя Тенеса, который казнил за всякое ложное обвинение</i>);\n<b>2)</b> дикторский топор (secures de fascibus demere <i>Fl</i>); <i>перен.</i> верховная власть (sumere aut ponere secures <i>H</i>), <i>преим.</i> римское владычество (Galliam securibus subjicere <i>Cs</i>);\n<b>3)</b> удар, урон, ущерб (gravem securim injicere alicui rei <i>C</i>);\n<b>4)</b> последний удар топора, <i>т. е.</i> окончание постройки судна (classis quadragesimo die a securi navigavit <i>PM</i>)."],
  ["securitas", "<big>securitas</big>\n\n<b>securitas, atis</b> <i>f</i> [securus]\n<b>1)</b> беззаботность, безмятежность, душевное спокойствие (s. est animi tranquillitas <i>C</i>): s. mortis <i>PM</i> невозмутимость перед лицом смерти;\n<b>2)</b> равнодушие, беспечность, небрежность (initium calamitatis est s. <i>AG</i>);\n<b>3)</b> безопасность, обеспеченность (itinerum <i>PM</i>; publica <i>T</i>);\n<b>4)</b> <i>юр.</i> обеспечение (долга), гарантия <i>Dig, CJ</i>."],
  ["securus", "<big>securus</big>\n\n<b>securus, a, um</b> [se IV + cura]\n<b>1)</b> беззаботный, безмятежный, спокойный (securam vitam agere <i>Sen</i>): s. de (pro <i>T</i>) aliqua re <i>C, L</i> <i>и</i> alicujus rei <i>V, H, O</i> спокойный за что-л.; <i>тж.</i> s. alicujus rei <i>V, H, O, Sen, PJ</i> не страшась чего-л. <i>или</i> не помышляя о чём-л.; non s. (ne) <i>L</i> озабоченный (тем, как бы не);\n<b>2)</b> беспечный, равнодушный, небрежный (homo <i>Q</i>); поверхностный, упрощённый (jurisdictio castrensis <i>T</i>);\n<b>3)</b> безопасный, ограждённый от опасностей, спокойный, надёжный (tempus, locus <i>L</i>): aliquid securius facere (praestare <i>QC</i>) ab aliqua re <i>L</i> обезопасить (оградить) что-л. от чего-л.; praeteriti doloris secura recordatio <i>C</i> спокойное воспоминание о минувшем бедствии;\n<b>4)</b> дающий успокоение (Lethaei latices <i>V</i>)."],
  ["secus", "<big>secus</big>\n\n<b>I secus</b> <i>n indecl.</i> (= sexus)\nпол (virile, muliebre <i>Pl, Sl, L, T</i>).\n\n<b>II secus</b> <i>adv.</i> [sequor]\n<b>1)</b> иначе, другим образом, не так, наоборот: non multo s. <i>C etc.</i> почти так, около того, в этом роде; exemplum ejus rei paulo s. simillimum <i>Ap</i> почти такой же точно пример (случай); longe s. <i>C</i> совсем иначе, далеко не так; s. ac (<i>или</i> quam) vis <i>Pl</i> иначе, чем ты хочешь; non (<i>или</i> haud) s. <i>C, H, O, Su</i> не иначе, именно так: non s. ac flumen <i>O</i> (время течёт) совсем как река;\n<b>2)</b> не так (как следует), нехорошо, плохо (bene aut s. <i>L</i>): s. existimare de aliquo <i>C</i> быть дурного мнения о ком-л. || неправильно, неверно (recte aut s. <i>C</i>): vere an s. — nihil ad hoc tempus <i>C</i> верно это или нет — в данный момент это неважно || несчастливо (beate aut s. <i>C</i>);\n<b>3)</b> меньше (non s. virium <i>T</i>). — <i>См. тж.</i> setius.\n\n<b>III secus</b> <i>praep. cum acc.</i> (<i>крайне редко</i>)\n(= secundum II, 1) вдоль <i>или</i> у берега (s. mare esse <i>Enn ap. Lad</i>)."],
  ["secutio", "<big>secutio</big>\n\n<b>secutio, onis</b> <i>f</i> [sequor]\nследование (s. alicujus <i>Aug</i>)."],
  ["secutor", "<big>secutor</big>\n\n<b>secutor, oris</b> <i>m</i> [sequor]\n<b>1)</b> сопровождающий, провожатый, спутник <i>Ap</i>;\n<b>2)</b> преследователь, вооружённый щитом и мечом, гладиатор, специально обученный для борьбы с ретиарием (<i>см.</i> retiarius) <i>Su, J</i>."],
  ["secutulejus", "<big>secutulejus</big>\n\n<b>secutulejus, a, um</b> [sequor]\nследующий, гоняющийся (за кем-л.): mulier secutuleja <i>Pt</i> публичная женщина."],
  ["secutus", "<big>secutus</big>\n\n<b>secutus, a, um</b> <i>part. pf. к</i>\nsequor."],
  ["sed", "<big>sed</big>\n\n<b>II sed</b> — <i>см.</i>\nse <i>V</i>.\n\n<b>III sed</b> <i>conj.</i>\nа, но (non sibi soli, s. patriae <i>C</i>); однако (plurima quidem proferre possumus, s. modus adhibendus est <i>Nep</i>); впрочем (s. quid opus est plura? <i>C</i>; s. revertamur ad rem <i>C</i>): s. haec hactenus <i>C</i> но (впрочем) довольно об этом.\n\n<b>I sed-</b>\n<i>приставка</i> — <i>см.</i> se- IV."],
  ["sedamen", "<big>sedamen</big>\n\n<b>sedamen, inis</b> <i>n</i> [sedo]\nуспокаивающее средство, успокоение (mors amoris una s. mali <i>SenT</i>)."],
  ["sedate", "<big>sedate</big>\n\n<b>sedate</b> [sedatus]\nспокойно, сдержанно (loqui <i>C</i>; dolorem ferre <i>C</i>)."],
  ["sedatio", "<big>sedatio</big>\n\n<b>sedatio, onis</b> <i>f</i> [sedo]\nуспокоение (animi, aegritudinis <i>C</i>)."],
  ["sedator", "<big>sedator</big>\n\n<b>sedator, oris</b> <i>m</i> [sedo]\nумиротворитель (militaris insaniae <i>Eccl</i>)."],
  ["sedatus", "<big>sedatus</big>\n\n<b>sedatus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> sedo;\n<b>2.</b> <i>adj.</i> сдержанный, спокойный, уравновешенный (homo <i>C</i>; cor <i>H</i>); медленный, тихий (amnis <i>C</i>)."],
  ["sedecennis", "<big>sedecennis</big>\n\n<b>sedecennis, e</b> [sedecim + annus]\nшестнадцатилетний (matrona <i>Aus</i>)."],
  ["sedecies", "<big>sedecies</big>\n\n<b>sedecies</b> [sedecim]\nшестнадцать раз <i>Rut, Dig</i>."],
  ["sedecim", "<big>sedecim</big>\n\n<b>sedecim</b> <i>adj. num. card.</i> [sex + decem]\nшестнадцать <i>Pl, Ter, Cs etc.</i>"],
  ["sedecula", "<big>sedecula</big>\n\n<b>sedecula, ae</b> <i>f</i> [<i>demin. к</i> sedes]\nсиденьице <i>C</i>."],
  ["sedentarius", "<big>sedentarius</big>\n\n<b>sedentarius, a, um</b> [sedeo]\n<b>1)</b> работающий сидя (sutor <i>Pl</i>);\n<b>2)</b> связанный с долгим сидением: fatigatio sedentaria <i>Ap</i> усталость от долгого сидения (в седле);\n<b>3)</b> требующий долгого сидения (opera <i>Col</i>): sedentaria necessitas assentiendi <i>PJ</i> необходимость выражать своё согласие сидя (в сенате), <i>т. е.</i> не поднимаясь для произнесения речи."],
  ["sedeo", "<big>sedeo</big>\n\n<b>sedeo, sedi, sessum, ere</b>\n<b>1)</b> сидеть, восседать (in sella <i>C</i>; in equo <i>C</i> <i>или</i> equo <i>L, QC</i>; sella curuli <i>L</i>; ad latus alicujus <i>Just</i>); сидеть верхом, <i>pass.</i> использоваться для верховой езды (equi sedentur <i>Spart</i>);\n<b>2)</b> заседать (in <i>или</i> pro tribunali <i>C</i>);\n<b>3)</b> оставаться, находиться (in villa totos dies <i>C</i>; domi <i>L</i>); сидеть без дела (compressis manibus domi s. <i>L</i>); (замкнуто) жить, пребывать (in interiore parte aedium <i>Nep</i>); <i>воен.</i> стоять (ad Trebiam <i>L</i>; ante moenia <i>L</i>): sedendo bellum gerere <i>L</i> вести позиционную войну;\n<b>4)</b> застревать (carina vado sedet <i>O</i>);\n<b>5)</b> застывать (pallor in ore sedet <i>O</i>);\n<b>6)</b> плотно облегать, сидеть (toga umero sedet <i>Q</i>);\n<b>7)</b> глубоко сидеть, засесть (amor in pectore sedet <i>O</i>): si mihi non animo fixum immotumque sederet <i>V</i> если бы у меня не было решено раз навсегда;\n<b>8)</b> оседать, садиться (montes sederunt <i>T</i>); опускаться (nebula campo sedet <i>L</i>): esca sedet <i>H</i> пища хорошо переваривается;\n<b>9)</b> низко расти, быть низкорослым, стлаться (lactuca sedens <i>M</i>);\n<b>10)</b> утихать, улечься (sedit rabies feritasque famesque <i>St</i>)."],
  ["sedes", "<big>sedes</big>\n\n<b>sedes, is</b> (<i>gen. pl.</i> um <i>и</i> ium) <i>f</i> [sedeo]\n<b>1)</b> сиденье, седалище, кресло (s. honoris ac dignitatis <i>C</i>): s. judicialis <i>CJ</i> судейская трибуна, судилище || стул, скамья (sedes ponere <i>L</i>); престол, трон (patria sede sedere <i>V</i>): s. apostolica <i>Eccl</i> папский престол;\n<b>2)</b> место жительства, жилище, обиталище, местопребывание (sedem sibi deligere <i>C</i>; in suas sedes reverti <i>Cs</i>); место (sedem bello legere <i>L</i>; priores sedes tenet Homerus <i>H</i>): animi s. et locus in cerebro est <i>C</i> психические явления локализованы в мозгу; infernae abdominis s. <i>Ap</i> нижняя часть живота; omni in sede ac loco <i>C</i> повсюду и везде || местонахождение (veteris Ilii <i>PM</i>): sedes Tartareae <i>или</i> silentum <i>O</i> подземное царство; figere sedem <i>J</i> осесть, поселиться; patria s. <i>O</i> <i>и</i> s. patriae <i>T</i> = patria; aliquid reducere in sedem vice <i>H</i> восстановить что-л. в прежнем виде;\n<b>3)</b> место отдохновения (s. meae senectae <i>H</i>); место упокоения (sedibus placidis in morte quiescere <i>V</i>);\n<b>4)</b> почва, основание, устой: sedem habere <i>C</i> иметь под собой (твёрдую) почву; aliquid a sedibus eruere <i>V</i> <i>или</i> sede sua moliri <i>L</i> <i>и</i> vellere <i>Sil</i> сдвинуть что-л. с места;\n<b>5)</b> <i>ритор.</i> остановка (во фразе), пауза (s. orationis <i>Q</i>);\n<b>6)</b> <i>анат.</i> седалище <i>PM, Ap</i>."],
  ["Sedetani", "<big>Sedetani</big>\n\n<b>Sedetani, orum</b> <i>m</i>\nседетаны, <i>племя в Hispania Tarraconensis</i> (<i>главн. город Сагунт</i>) <i>L</i>."],
  ["sediculum", "<big>sediculum</big>\n\n<b>sediculum, i</b> <i>n</i> <i>Vr</i>\n= sedecula."],
  ["sedigitalis", "<big>sedigitalis</big>\n\n<b>se–digitalis, e</b>\nв шесть пальцев шириной <i>Vtr</i>."],
  ["sedile", "<big>sedile</big>\n\n<b>sedile, is</b> <i>n</i> [sedeo]\n<b>1)</b> седалище, сиденье (<i>скамья, стул, кресло</i>) <i>V, H, PJ, Sen etc.</i>;\n<b>2)</b> сидение, отдых сидя (post iter primum s. <i>CC</i>); сидячий образ жизни (alvum astringit s. <i>CC</i>)."],
  ["sedimen", "<big>sedimen</big>\n\n<b>sedimen, inis</b> <i>n</i> [sedeo]\nосадок, отстой, гуща <i>CA, Is</i>."],
  ["sedimentum", "<big>sedimentum</big>\n\n<b>sedimentum, i</b> <i>n</i> [sedeo]\nоседание (molis <i>PM</i>)."],
  ["sedis", "<big>sedis</big>\n\n<b>sedis, is</b> <i>f</i> <i>L</i>\n(<i>v. l.</i>) = sedes."],
  ["seditio", "<big>seditio</big>\n\n<b>seditio, onis</b> <i>f</i> [sed 1 + eo]\n<b>1)</b> расхождение, разногласие, распря, раздор, спор, ссора (domestica <i>L</i>; s. inter aliquos <i>L</i>; in seditionibus esse <i>L</i>);\n<b>2)</b> мятеж, бунт, волнение (militum <i>L</i>);\n<b>3)</b> возмущение, восстание (civium <i>C</i>);\n<b>4)</b> раздражение (animi <i>C</i>); волнение (maris <i>St</i>)."],
  ["seditiose", "<big>seditiose</big>\n\n<b>seditiose</b> [seditiosus]\nмятежно <i>C, L, T</i>."],
  ["seditiosus", "<big>seditiosus</big>\n\n<b>seditiosus, a, um</b> [seditio]\n<b>1)</b> мятежный (oratio <i>Cs</i>; civis <i>C</i>);\n<b>2)</b> беспокойный, тревожный (vita <i>C</i>)."],
  ["sedo", "<big>sedo</big>\n\n<b>sedo, avi, atum, are</b> [<i>causat. к</i> sedeo]\n<b>1)</b> заставлять осесть, осаждать, прибивать к земле (pulverem <i>Ph</i>);\n<b>2)</b> сдерживать (ventos <i>O</i>); успокаивать, унимать (animos <i>C</i>; tempestas sedatur <i>C</i> — <i>ср.</i> 3); улаживать (discordias <i>C</i>); усмирять, подавлять (seditionem <i>C</i>; flammam <i>C</i>); утолять (sitim <i>Lcr, O, VM</i>; famem <i>PM</i>); насыщать, накормить (aliquem <i>Pl</i>);\n<b>3)</b> (<i>редко</i>) утихать (tempestas sedavit <i>Cn. Gellius ap. AG</i>)."],
  ["seduco", "<big>seduco</big>\n\n<b>se–duco, duxi, ductum, ere</b>\n<b>1)</b> (<i>тж.</i> s. a turba <i>Pt</i>) отводить в сторону (aliquem <i>C</i>); отодвигать в сторону (stipitem <i>O</i>); отворачивать (ocellos <i>Prp</i>);\n<b>2)</b> разделять, разобщать (unda seducit terras duas <i>O</i>); отделять (tellurem ab aethere <i>O</i>; quum mors anima seduxerit artus <i>V</i>): quiddam a corporibus seductum <i>Sen</i> нечто отличное от тел;\n<b>3)</b> держать в стороне, изымать (aliquid a plurium conscientia <i>L</i>); спасать (aliquem a peste <i>C</i>);\n<b>4)</b> совращать (aliquem <i>Dig, Eccl</i>). — <i>См. тж.</i> seductus."],
  ["seductibilis", "<big>seductibilis</big>\n\n<b>seductibilis, e</b> <i>и</i> <b>seductilis, e</b> [seduco]\nлегко поддающийся соблазну, нравственно нестойкий <i>Aug</i>."],
  ["seductio", "<big>seductio</big>\n\n<b>seductio, onis</b> <i>f</i> [seduco]\n<b>1)</b> отведение в сторону (alicujus <i>C</i>);\n<b>2)</b> разделение, разлучение (corporis animaeque <i>Lact</i>);\n<b>3)</b> совращение <i>Eccl</i>."],
  ["seductor", "<big>seductor</big>\n\n<b>seductor, oris</b> <i>m</i> [seduco]\nсовратитель, соблазнитель <i>Eccl</i>."],
  ["seductorius", "<big>seductorius</big>\n\n<b>seductorius, a, um</b>\nсовратительный, растлевающий (eloquium <i>Aug</i>)."],
  ["seductrix", "<big>seductrix</big>\n\n<b>seductrix, icis</b> <i>f</i>\nсовратительница <i>Tert</i>."],
  ["seductus", "<big>seductus</big>\n\n<b>seductus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> seduco;\n<b>2.</b> <i>adj.</i> удалённый, отдалённый, далёкий (terra <i>O</i>): in seducto <i>Sen</i> в удалении, в уединении."],
  ["sedularia", "<big>sedularia</big>\n\n<b>sedularia, orum</b> <i>n</i> [sedes]\nподушки для сидений <i>Dig</i>."],
  ["sedule", "<big>sedule</big>\n\n<b>sedule</b> <i>Col, Eccl</i>\n= sedulo."],
  ["sedulitas", "<big>sedulitas</big>\n\n<b>sedulitas, atis</b> <i>f</i> [sedulus]\n<b>1)</b> старательность, рвение, усердие, прилежание <i>C, H, O, PM</i>;\n<b>2)</b> хлопотливость, назойливость <i>H</i>."],
  ["sedulo", "<big>sedulo</big>\n\n<b>sedulo</b> <i>adv.</i> [sedulus]\n<b>1)</b> усердно, прилежно, ревностно (facere <i>Pl, Ter, C</i>; bonas artes colere <i>Ap</i>); внимательно (audire <i>PJ</i>; pensiculare aliquid <i>Ap</i>);\n<b>2)</b> намеренно, нарочно, умышленно (tempus terere <i>L</i>)."],
  ["sedulus", "<big>sedulus</big>\n\n<b>sedulus, a, um</b> [sedeo, <i>по др.</i> se IV + dolus]\n<b>1)</b> прилежный, деятельный, ревностный, усердный (homo <i>C</i>; apis <i>O</i>);\n<b>2)</b> тщательный <i>или</i> неутомимый (labor <i>SenT</i>); внимательный (spectator <i>H</i>);\n<b>3)</b> хлопотливый, назойливый (hospes <i>H</i>)."],
  ["sedum", "<big>sedum</big>\n\n<b>sedum, i</b> <i>n</i> <i>бот. предпол.</i>\nочиток (<i>Sedum</i>, L.) <i>Col, Pall, PM</i>."],
  ["Seduni", "<big>Seduni</big>\n\n<b>Seduni, orum</b> <i>m</i>\nседуны, <i>горное племя в верхнем течении Родана, с городом Seduni</i> (<i>ныне Sion, нем. Sitten в швейцарском кантоне Valais</i>) <i>Cs</i>."],
  ["Sedusii", "<big>Sedusii</big>\n\n<b>Sedusii, orum</b> <i>m</i>\nседузии, <i>одно из германск. племён в войске Ариовиста</i> <i>Cs</i>."],
  ["seges", "<big>seges</big>\n\n<b>seges, etis</b> <i>f</i>\n<b>1)</b> посев, хлеб на корню, хлеба (s. matura <i>Cs, L</i>; alta <i>O</i>; s. avenae <i>V</i>): prima s. <i>Col</i> всходы;\n<b>2)</b> нива, пашня, поле (s. ferax <i>H</i>);\n<b>3)</b> почва, основа, основание (gloriae <i>C</i>);\n<b>4)</b> множество, масса (osculationis <i>Ctl</i>; telorum <i>V</i>);\n<b>5)</b> выгода, польза (quae inde s.? <i>J</i>)."],
  ["Segesta", "<big>Segesta</big>\n\n<b>Segesta, ae</b> <i>f</i>\nСегеста, <i>город в сев.-зап. Сицилии, между Панормом и Лилибеем</i> <i>C</i>."],
  ["Segestani", "<big>Segestani</big>\n\n<b>Segestani, orum</b> <i>и</i> <b>Segestenses, ium</b> <i>m</i>\nжители города Segesta <i>C etc.</i>"],
  ["Segestanus", "<big>Segestanus</big>\n\n<b>Segestanus, a, um</b> [Segesta]\nсегестаиский <i>C</i>."],
  ["Segestes", "<big>Segestes</big>\n\n<b>Segestes, is</b> <i>m</i>\nСегест, <i>знатный херуск, отец Туснельды, тесть Арминия</i> <i>T</i>."],
  ["Segestica", "<big>Segestica</big>\n\n<b>Segestica, ae</b> <i>f</i>\nСегестика, <i>город в Hispania Tarraconensis</i> <i>L</i>."],
  ["segestre", "<big>segestre</big>\n\n<b>segestre, is</b> <i>n</i> (<i>греч.</i>)\nциновка, соломенная плетёнка, <i>тж.</i> меховое покрывало <i>LM, Vr, PM</i>."],
  ["segestria", "<big>segestria</big>\n\n<b>segestria, ae</b> <i>f</i> <i>Vr, Su</i>\n= segestre."],
  ["segetalis", "<big>segetalis</big>\n\n<b>segetalis, e</b> [seges]\nрастущий среди хлебов (giadiolus <i>Ap</i>)."],
  ["Segimerus", "<big>Segimerus</big>\n\n<b>Segimerus, i</b> <i>m</i>\n= Sigimerus."],
  ["segmen", "<big>segmen</big>\n\n<b>segmen, inis</b> <i>n</i> [seco]\nобрезок, кусочек (segmina unguium et capilli <i>Fabius Pictor ap. AG</i>): nulli secabile s. <i>Aus</i> = atomus II."],
  ["segmentatus", "<big>segmentatus</big>\n\n<b>segmentatus, a, um</b> [segmentum]\nотороченный, окаймлённый, отделанный бахромой (сипае <i>J</i>; amictus <i>Symm</i>)."],
  ["segmentum", "<big>segmentum</big>\n\n<b>segmentum, i</b> <i>n</i> [seco]\n<b>1)</b> отрезок, кусок <i>PM, Aus</i>;\n<b>2)</b> полоса, пояс, зона (mundi <i>PM</i>);\n<b>3)</b> <i>pl.</i> (<i>sc.</i> laminae) блёстки <i>или</i> галуны (segmenta aurea <i>VM</i>)."],
  ["segne", "<big>segne</big>\n\n<b>segne</b> <i>Amm</i>\n= segniter."],
  ["segnesco", "<big>segnesco</big>\n\n<b>segnesco, —, —, ere</b>\nзамедляться <i>CA</i>."],
  ["Segni", "<big>Segni</big>\n\n<b>Segni, orum</b> <i>m</i>\nсегны, <i>племя в Gallia Belgica, между областями эбуронов и треверов</i> <i>Cs</i>."],
  ["segnipes", "<big>segnipes</big>\n\n<b>segnipes, pedis</b> <i>m</i> [segnis + pes] <i>поэт.</i>\nмедленно плетущийся (<i>о старой лошади</i>) <i>J</i>."],
  ["segnis", "<big>segnis</big>\n\n<b>segnis, e</b>\n<b>1)</b> медлительный, вялый, ленивый (homo <i>Cs, Sen, T</i>; equus <i>V</i>); медленный (aqua <i>QC</i>; mors <i>L, T</i>): sis licet s. <i>SenT</i> не торопись; non s. stat remeatque dies <i>Tib</i> день (<i>т. е.</i> время) не стоит (на месте) и не возвращается; s. mora <i>L</i> длительное бездействие; occasionum haud s. <i>T</i> быстро использующий возможности; stellae segnes <i>VF</i> неподвижные звёзды; s. ad pericula <i>T</i> теряющийся перед лицом опасностей; s. ad credendum <i>L</i> недоверчивый;\n<b>2)</b> истощённый, бесплодный, неплодородный (campus <i>V</i>); неохотный, небрежный (militia <i>L</i>);\n<b>3)</b> затяжной (bellum <i>L</i>): aetas s. <i>QC</i> долговечность <i>или</i> преклонный возраст."],
  ["segnitas", "<big>segnitas</big>\n\n<b>segnitas, atis</b> <i>f</i> <i>Acc, C</i>\n= segnitia."],
  ["segniter", "<big>segniter</big>\n\n<b>segniter</b> [segnis]\nмедлительно, медленно, вяло (omnia gerere <i>L</i>); nihilo segnius <i>C</i> <i>и</i> non segnius <i>O, PJ</i> тем усерднее."],
  ["segnitia", "<big>segnitia</big>\n\n<b>segnitia, ae</b> <i>f</i> [segnis]\nмедленность, медлительность, вялость (alicujus in aliqua re <i>Q</i>): s. maris <i>T</i> безветрие на море, штиль."],
  ["segnities", "<big>segnities</big>\n\n<b>segnities, ei</b> <i>f</i> <i>Pl, V, Col, L etc.</i>\n= segnitia."],
  ["Segontiaci", "<big>Segontiaci</big>\n\n<b>Segontiaci, orum</b> <i>m</i>\nсегонтиаки, <i>племя в южн. Британии, против нын. о-ва Wight</i> <i>Cs</i>."],
  ["segredior", "<big>segredior</big>\n\n<b>se–gredior, —, gredi</b>\nуходить прочь, удаляться (ab oculis et auribus <i>Tert</i>)."],
  ["segregatim", "<big>segregatim</big>\n\n<b>segregatim</b> [segrego]\nотдельно, врозь <i>Eccl</i>."],
  ["segregatio", "<big>segregatio</big>\n\n<b>segregatio, onis</b> <i>f</i>\nотделение, обособление <i>Tert</i>."],
  ["segregis", "<big>segregis</big>\n\n<b>segregis</b> <i>gen. к</i>\nsegrex."],
  ["segrego", "<big>segrego</big>\n\n<b>segrego, avi, atum, are</b> [se IV + grex]\n<b>1)</b> отделять от стада (oves segregatae <i>Ph</i>);\n<b>2)</b> отделять, разлучать, изолировать, удалять (aliquem a re publica <i>C</i>; liberos a se <i>C</i>); исключать (aliquem ex senatu <i>Pl</i>, a numero civium <i>C</i>): s. suspicionem et culpam a se <i>Pl</i> ограждать себя от подозрения в преступлении; s. pugnam (Curiatiorum) <i>L</i> сразиться порознь с каждым из Куриациев;\n<b>3)</b> прекращать (sermonem <i>Pl</i>)."],
  ["segregus", "<big>segregus</big>\n\n<b>segregus, a, um</b> <i>Aus</i>\n= segrex."],
  ["segrex", "<big>segrex</big>\n\n<b>segrex, gregis</b> [segrego]\nотдельный, изолированный, обособленный (civitas situ s. <i>Sid</i>); уединённый (vita <i>Sen</i>)."],
  ["segullum", "<big>segullum</big>\n\n<b>segullum</b> <i>или</i> <b>segutilum, i</b> <i>n</i>\nвнешний признак (indicium) наличия золотых россыпей <i>PM</i>."],
  ["Segusiavi", "<big>Segusiavi</big>\n\n<b>Segusiavi, orum</b> <i>m</i>\nсегусиавы, <i>кельт. племя у слияния рек Родан и Арар, между областями секванов, эдуев и арвернов</i> <i>Cs</i>."],
  ["sei", "<big>sei</big>\n\n<b>sei</b> <i>арх. Pl</i>\n= si."],
  ["Seja", "<big>Seja</big>\n\n<b>Seja, ae</b> <i>f</i> [sero II]\nСея, <i>богиня сева</i> <i>PM, Macr, Aug</i>."],
  ["Sejanianus", "<big>Sejanianus</big>\n\n<b>Sejanianus, a, um</b> [Seianus]\nсеянов <i>Sen</i>."],
  ["Sejanus", "<big>Sejanus</big>\n\n<b>I Sejanus, a, um</b> [Sejus]\nпринадлежащий Сею: habere equum Sejanum <i>погов. AG</i> быть обладателем лошади Сея, <i>т. е.</i> быть неудачником (<i>по имени Cn. Sejus, лошадь которого приносила несчастье всем, кому она доставалась</i>).\n\n<b>II Sejanus, i</b> <i>m</i>\n(L. Aelius) Сеян (Л. Элий), <i>сын Сея Страбона, усыновлённый родом Элиев, долго бывший могущественным временщиком императора Тиберия, но в 31 г. н. э. казнённый им</i> <i>Su, T</i>."],
  ["sejuga", "<big>sejuga</big>\n\n<b>sejuga, ae</b> <i>f</i> <i>Is</i>\n= sejuges."],
  ["sejuges", "<big>sejuges</big>\n\n<b>sejuges, ium</b> <i>m</i> (<i>sc.</i> equi)\nшестёрка лошадей <i>L, PM</i>."],
  ["sejugis", "<big>sejugis</big>\n\n<b>I sejugis, e</b> [sex + jugum]\nзапряжённый шестёркой (currus <i>Ap</i>).\n\n<b>II sejugis, e</b> [se IV + jugum]\nотделённый, разобщённый (gentes <i>Sol</i>)."],
  ["sejugo", "<big>sejugo</big>\n\n<b>se–jugo, —, atum, are</b>\nотделять (aliqua re <i>Ap</i> <i>или</i> ab aliqua re <i>C, Ap</i>)."],
  ["sejunctim", "<big>sejunctim</big>\n\n<b>sejunctim</b> [sejungo]\nотдельно, порознь: duplici s. cernere Marte <i>Tib</i> вести бой сразу на обоих флангах."],
  ["sejunctio", "<big>sejunctio</big>\n\n<b>sejunctio, onis</b> <i>f</i> [sejungo]\n<b>1)</b> отделение (ab aliqua re <i>C, Q</i>);\n<b>2)</b> расхождение, раскол, раздор <i>Fl</i>."],
  ["sejunctus", "<big>sejunctus</big>\n\n<b>sejunctus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> sejungo;\n<b>2.</b> <i>adj.</i> далёкий, удалённый (corpore s. dolor <i>Lcr</i>); не имеющий ничего общего, несогласный: defensio sejuncta a voluntate acsententia legis <i>C</i> защита, не согласная ни с волей, ни со смыслом закона."],
  ["sejungo", "<big>sejungo</big>\n\n<b>se–jungo, junxi, junctum, ere</b>\n<b>1)</b> отделять (aliquem ab aliquo <i>C</i> <i>и</i> aliqua re <i>St</i>; Alpes Italiam a Gallia sejungunt <i>Nep</i>): s. se разлучаться, расставаться (ab aliquo <i>C</i>) <i>или</i> отказываться, воздерживаться (a <i>или</i> ex aliqua re <i>C</i>); s. se a labore forensi <i>T</i> уйти от общественной жизни; sejunctum est a re proposita <i>Nep</i> это не входит в (мой) план (не имеет ничего общего с ним);\n<b>2)</b> отличать (morbum ab aegrotatione <i>C</i>). — <i>См. тж.</i> sejunctus."],
  ["Sejus", "<big>Sejus</big>\n\n<b>Sejus, a, um</b>\nСей, <i>римск. nomen; наиболее известны</i>:\n<b>1)</b> M. S., <i>эдил в 74 г. до н. э., друг Цицерона и Аттика</i> <i>Vr, C</i>;\n<b>2)</b> Q. S. Postumus, <i>отравленный Клодием за отказ уступить свой дом</i> <i>C</i>;\n<b>3)</b> S. Tubero, <i>легат Германика, был обвинён в 24 г. н. э. в мятеже, но оправдан</i> <i>T</i>."],
  ["selas", "<big>selas</big>\n\n<b>selas</b> (<i>pl.</i> sela) <i>n indecl.</i> (<i>греч.</i>)\n<i>род огненного метеора</i> <i>Sen, Ap</i>."],
  ["selectio", "<big>selectio</big>\n\n<b>selectio, onis</b> <i>f</i> [seligo]\nвыбор, отбор <i>C, Aug</i>."],
  ["selector", "<big>selector</big>\n\n<b>selector, oris</b> <i>m</i> [seligo]\nпроизводящий отбор <i>Aug</i>."],
  ["selectus", "<big>selectus</big>\n\n<b>selectus, a, um</b> <i>part. pf. к</i>\nseligo."],
  ["selegi", "<big>selegi</big>\n\n<b>selegi</b> <i>pf. к</i>\nseligo."],
  ["Seleucea", "<big>Seleucea</big>\n\n<b>Seleucea (Seleucia), ae</b> <i>f</i>\nСелевкия, <i>название ряда городов, основанных Селевком I; наиболее известны</i>:\n<b>1)</b> <i>город на Тигре (Вавилония), к югу от нын. Багдада, столица парфянских царей</i> <i>C, PM, Fl</i>;\n<b>2)</b> (S. Pieria) <i>город в Сирии, к сев. от устья Оронта</i> <i>C, PM, Amm</i>;\n<b>3)</b> (S. Trachea) <i>город в Киликии</i> <i>PM, Amm</i>."],
  ["Seleucensis", "<big>Seleucensis</big>\n\n<b>Seleucensis, e</b> [Seleucea]\nселевкийский <i>T</i>."],
  ["Seleucenus", "<big>Seleucenus</big>\n\n<b>Seleucenus, a, um</b> <i>Capit</i>\n= Seleucensis."],
  ["Seleucia", "<big>Seleucia</big>\n\n<b>Seleucia</b> <i>v. l.</i>\n= Seleucea."],
  ["Seleucus", "<big>Seleucus</big>\n\n<b>Seleucus, i</b> <i>m</i>\nСелевк, <i>имя нескольких сирийских царей; наиболее известен</i> S. Nicator, <i>один из самых выдающихся полководцев Александра Македонского, основатель царства Селевкидов, царствовавший в Сирии с 312 до 281 г. до н. э.</i> <i>Pl, Just, Aus</i>."],
  ["selibra", "<big>selibra</big>\n\n<b>se–libra, ae</b> <i>f</i>\nполфунта (= 163,73 <i>г</i>) <i>Cato, Vr, PM, Pt</i>."],
  ["seligo", "<big>seligo</big>\n\n<b>seligo, legi, lectum, ere</b> [se + lego]\nвыбирать, избирать (selectae sententiae <i>C</i>): judices selecti <i>C</i> назначаемые претором судьи по уголовным делам; dii selecti <i>Vr, Aug</i> старшие боги (<i>Janus, Juppiter, Saturnus и др. — всего 20</i>)."],
  ["Selinus", "<big>Selinus</big>\n\n<b>Selinus, untis</b> <i>f, m</i>\nСелинунт:\n<b>1)</b> <i>город на южн. побережье Сицилии, основанный мегарскими колонистами</i> <i>V, Sil</i>;\n<b>2)</b> <i>приморский город и река в зап. Киликии</i> <i>L, Lcn</i>."],
  ["seliquastrum", "<big>seliquastrum</big>\n\n<b>seliquastrum, i</b> <i>n</i> [sella]\nвысокое седалище (<i>стул, кресло</i>) <i>Vr</i>."],
  ["sella", "<big>sella</big>\n\n<b>sella, ae</b> <i>f</i> [<i>из</i> *sedula <i>от</i> sedeo]\n<b>1)</b> стул, кресло <i>Pl, C, Sen etc.</i>;\n<b>2)</b> (<i>тж.</i> s. curulis <i>C, Cs etc.</i>) курульное кресло (<i>седалище высших, курульных, должностных лиц, из слоновой кости, мрамора или металла, без спинки, на изогнутых и скрещенных ножках</i>) <i>C etc.</i>;\n<b>3)</b> учительская кафедра <i>C</i>;\n<b>4)</b> (<i>тж.</i> s. regni <i>V</i>) престол, трон (s. aurea <i>Nep</i>);\n<b>5)</b> (<i>тж.</i> s. gestatoria <i>Su</i>) паланкин, сидячие носилки, портшез (sella vehi <i>PJ</i>);\n<b>6)</b> козлы, облучок <i>Ph</i>;\n<b>7)</b> (<i>тж.</i> s. pertusa <i>Cato</i> <i>или</i> familiarica <i>Vr</i>) стульчак <i>Scr</i>;\n<b>8)</b> седло <i>Veg, Sid, CJ</i>."],
  ["sellaria", "<big>sellaria</big>\n\n<b>sellaria, ae</b> <i>f</i> [sellarius]\nкомната (зал) со стульями <i>Pl, Su</i>."],
  ["sellariolus", "<big>sellariolus</big>\n\n<b>sellariolus, a, um</b> [<i>demin. к</i> sellarius]\nпредназначенный для сидения: popina sellariola <i>M</i> кабак."],
  ["sellaris", "<big>sellaris</big>\n\n<b>sellaris, e</b> [sella]\n<b>1)</b> относящийся к сидению: gestatio s. <i>CA</i> ношение сидячих носилок;\n<b>2)</b> верховой (jumenta <i>Veg</i>)."],
  ["sellarius", "<big>sellarius</big>\n\n<b>sellarius, i</b> <i>m</i>\nраспутник <i>T</i>."],
  ["sellisternium", "<big>sellisternium</big>\n\n<b>sellisternium, i</b> <i>n</i> [sella + sterno]\nселлистерний, <i>религиозное пиршество в честь женских божеств</i> (<i>изображения богинь ставились на стулья, тогда как при lectisternium изображения богов помещались на ложа</i>) <i>T</i>."],
  ["sellula", "<big>sellula</big>\n\n<b>sellula, ae</b> <i>f</i> [<i>demin. к</i> sella]\n<b>1)</b> небольшой стул <i>Hier</i>;\n<b>2)</b> небольшое носильное кресло, маленький паланкин, портшез <i>T</i>."],
  ["sellularius", "<big>sellularius</big>\n\n<b>I sellularius, a, um</b> [sellula]\nсвязанный с сидением, требующий постоянного сидения (artes <i>AG, Ap</i>; artifices <i>Ap</i>).\n\n<b>II sellularius, i</b> <i>m</i>\nремесленник, ведущий сидячий образ жизни <i>C, L</i>."],
  ["semad-", "<big>semad-</big>\n\n<b>semad-, semamb-, seman-</b> <i>v. l.</i>\n= semiad-, semiamb-, semian-."],
  ["sembella", "<big>sembella</big>\n\n<b>sembella, ae</b> <i>f</i> [<i>из</i> *semilibella] <i>Vr</i>\n= selibra."],
  ["semel", "<big>semel</big>\n\n<b>semel</b> <i>adv.</i>\n<b>1)</b> однажды, однократно, (один) раз (non s., sed centies <i>Pl</i>; s. in vita <i>PM</i>; non plus quam s. <i>C</i>): ne s. quidem <i>C etc.</i> ни разу; non s. <i>C</i>, s. et saepius <i>C</i> <i>или</i> plus (quam) s. <i>Vr, C</i> не раз; s. atque iterum <i>C</i> ещё и ещё раз;\n<b>2)</b> раз, поскольку, так как: s. quoniam venerat <i>O</i> так как он уже пришёл; ut (<i>или</i> ubi) s. <i>Ter, L etc.</i> коль скоро, если уж;\n<b>3)</b> раз, первый раз, во-первых (s. Romae, iterum Athenis <i>C</i>);\n<b>4)</b> сразу, разом, раз навсегда (alicui s. ignoscere <i>C</i>; s. et in perpetuum <i>Fl</i>): non s. <i>Sen</i> не сразу, не вдруг || одним словом (ut s. dicam <i>Q</i>)."],
  ["Semela", "<big>Semela</big>\n\n<b>Semela, ae</b> <i>и</i> <b>Semele, es</b> <i>f</i>\nСемела, <i>дочь Кадма и Гармонии, родившая от Юпитера Вакха</i> <i>C, O, H etc.</i>"],
  ["Semeleius", "<big>Semeleius</big>\n\n<b>Semele(i)us, a, um</b> [Semela]\nсемелин <i>H, St</i>: proles Semeleia <i>O</i> = Bacchus."],
  ["semen", "<big>semen</big>\n\n<b>semen, inis</b> <i>n</i> [sero II]\n<b>1)</b> семя, семена (s. manu spargere <i>C</i>; creatus semine Saturni <i>O</i>): s. rerum <i>O</i> элементы природы; naturae semina <i>Prp</i> природные задатки; semina flammae <i>V</i> = scintillae;\n<b>2)</b> саженец, рассада (s. ponere <i>V</i>);\n<b>3)</b> род (regio semine ortus <i>L</i>); порода (pecus boni seminis <i>Vr</i>);\n<b>4)</b> (<i>тж.</i> <i>pl.</i>) отпрыск, потомок, дитя, сын: semina Phoebi <i>O</i> = Aesculapius; Ilia Vestalis caelestia semina partu ediderat <i>O</i> весталка Илия родила от бога (Марса) двух детей (Ромула и Рема);\n<b>5)</b> первоисточник, основа, причина (discordiae <i>T</i>; malorum <i>C</i>);\n<b>6)</b> виновник (belli <i>C</i>);\n<b>7)</b> полба <i>Col, PM</i>."],
  ["semenstris", "<big>semenstris</big>\n\n<b>semenstris</b> <i>C, Cs</i>\n= semestris I <i>и</i> II."],
  ["sementatio", "<big>sementatio</big>\n\n<b>sementatio, onis</b> <i>f</i> <i>Tert</i>\n= sementis 1."],
  ["sementifer", "<big>sementifer</big>\n\n<b>sementifer, fera, ferum</b> [sementis + fero]\nплодоносный, хлебородный <i>V</i>."],
  ["sementis", "<big>sementis</big>\n\n<b>sementis, is</b> (<i>acc.</i> em <i>и</i> im, <i>abl.</i> e <i>и</i> i) <i>f</i> [semen]\n<b>1)</b> сеяние, сев, посев: sementem facere <i>Cato, Cs, L</i> сеять; ut sementem feceris, ita metes <i>погов. C</i> что посеешь, то и пожнёшь; sementem in ore facere alicui <i>ирон. Pl</i> бить кого-л. по лицу;\n<b>2)</b> время посевных работ <i>Cato, Col</i>;\n<b>3)</b> семя, семена <i>Col</i>;\n<b>4)</b> <i>pl.</i> всходы (sementes tenerae <i>O</i>)."],
  ["sementivus", "<big>sementivus</big>\n\n<b>sementivus, a, um</b> [semen]\nсвязанный с посевными работами, посевной (dies <i>O</i>; feriae <i>Vr</i>); озимый (genera frumenti <i>PM</i>); <i>предпол.</i> поспевающий лишь к посевным работам, <i>т. е.</i> осенний, поздний (pira <i>Cato</i>; mala <i>Vr</i>)."],
  ["semento", "<big>semento</big>\n\n<b>semento, —, atum, are</b> [sementis]\nдавать семя, обсеменяться (herbae sementaturae <i>PM</i>)."],
  ["semerm-", "<big>semerm-</big>\n\n<b>semerm-</b> <i>v. l.</i>\n= semierm-."],
  ["semessus", "<big>semessus</big>\n\n<b>semessus, a, um</b> <i>v. l.</i>\n= semesus."],
  ["semestris", "<big>semestris</big>\n\n<b>I semestris, e</b> [sex + mensis]\nшестимесячный (infans <i>L</i>; regnum, imperium <i>C</i>).\n\n<b>II semestris, e</b> [semi + mensis]\nполумесячный: luna s. <i>Ap</i> луна в середине месяца, <i>т. е.</i> полнолуние."],
  ["semestrium", "<big>semestrium</big>\n\n<b>semestrium, i</b> <i>n</i> [semi + mensis]\nполмесяца <i>Col</i>."],
  ["semesus", "<big>semesus</big>\n\n<b>semesus, a, um</b> [semi + edo II]\nполусъеденный, полуобглоданный (ossa <i>V</i>; pisces <i>H</i>)."],
  ["semet", "<big>semet</big>\n\n<b>se–met</b> <i>acc. abl. к</i>\nsuimet — <i>см.</i> -met."],
  ["semhians", "<big>semhians</big>\n\n<b>semhians, antis</b> <i>adj.</i> <i>v. l.</i>\n= semihians."],
  ["semi-", "<big>semi-</big>\n\n<b>semi-</b> [semis I]\n<i>приставка</i> наполовину, полу-, пол-."],
  ["semiacerbus", "<big>semiacerbus</big>\n\n<b>semi–acerbus, a, um</b>\nнаполовину несозревший, недозревший (uva <i>Pall</i>)."],
  ["semiadapertus", "<big>semiadapertus</big>\n\n<b>semi–adapertus, a, um</b> [semi + adaperio]\nполуоткрытый, приотворенный (janua <i>O</i>)."],
  ["semiadopertulus", "<big>semiadopertulus</big>\n\n<b>semi–adopertulus, a, um</b> [<i>demin. к</i> adopertus]\nполузакрытый (oculi <i>Ap</i>)."],
  ["semiagrestis", "<big>semiagrestis</big>\n\n<b>semi–agrestis, e</b>\nполудеревенский, мужиковатый <i>AV</i>."],
  ["semiambustus", "<big>semiambustus</big>\n\n<b>semi–ambustus, a, um</b>\nполусожжённый, полуобгоревший (cadaver <i>Su</i>)."],
  ["semiamictus", "<big>semiamictus</big>\n\n<b>semi–amictus, a, um</b> [amicio]\nполуодетый, полунагой <i>Ap</i>."],
  ["semiamputatus", "<big>semiamputatus</big>\n\n<b>semi–amputatus, a, um</b> [amputo]\nполусрезанный (ramuli <i>Ap</i>)."],
  ["semianimis", "<big>semianimis</big>\n\n<b>semi–animis, e</b>\nполубездыханный, полуживой (corpora <i>L</i>; germana, <i>sc.</i> Dido <i>V</i>); полумёртвый (digiti <i>V</i>)."],
  ["semianimus", "<big>semianimus</big>\n\n<b>semi–animus, a, um</b> <i>L</i>\n= semianimis."],
  ["semiapertus", "<big>semiapertus</big>\n\n<b>semi–apertus, a, um</b>\nполуоткрытый (fores <i>L</i>)."],
  ["semias", "<big>semias</big>\n\n<b>semi–as, assis</b> <i>m</i>\nпол-асса <i>Vr</i>."],
  ["semiatratus", "<big>semiatratus</big>\n\n<b>semi–atratus, a, um</b>\nодетый наполовину в чёрное <i>Vr</i>."],
  ["semiaxius", "<big>semiaxius</big>\n\n<b>semi–axius, a, um</b> [axis] <i>ирон.</i>\nнаполовину колесованный <i>Tert</i>."],
  ["semibarbarus", "<big>semibarbarus</big>\n\n<b>semi–barbarus, a, um</b>\nполуварварский <i>Su, Capit etc.</i>"],
  ["semibos", "<big>semibos</big>\n\n<b>semi–bos, bovis</b> <i>m</i>\nполубык <i>O</i>."],
  ["semicanaliculus", "<big>semicanaliculus</big>\n\n<b>semi–canaliculus, i</b> <i>m</i> <i>архит.</i>\nполовинная выемка в триглифе, полуканнелюра <i>Vtr</i>."],
  ["semicanus", "<big>semicanus</big>\n\n<b>semi–canus, a, um</b>\nполуседой (comae <i>Ap</i>)."],
  ["semicaper", "<big>semicaper</big>\n\n<b>semi–caper, capri</b> <i>m</i>\nполукозёл <i>O</i>."],
  ["semicentesima", "<big>semicentesima</big>\n\n<b>semi–centesima, ae</b> <i>f</i> (<i>sc.</i> pars)\nполпроцента <i>CTh</i>."],
  ["semicinctium", "<big>semicinctium</big>\n\n<b>semi–cinctium, i</b> <i>n</i> [cinctus]\nузкий пояс, поясок <i>M, Pt, Vlg, Is</i>."],
  ["semicirculatus", "<big>semicirculatus</big>\n\n<b>semi–circulatus, a, um</b>\nполукружный (ferramentum <i>CC</i>)."],
  ["semicirculus", "<big>semicirculus</big>\n\n<b>I semicirculus, a, um</b> <i>Col</i>\n= semicirculatus.\n\n<b>II semi–circulus, i</b> <i>m</i>\nполукруг <i>или</i> полуокружность <i>CC, Col</i>."],
  ["semiclausus", "<big>semiclausus</big>\n\n<b>semi–clausus, a, um</b>\nполузакрытый (os <i>Ap</i>)."],
  ["semiclusus", "<big>semiclusus</big>\n\n<b>semiclusus, a, um</b> <i>v. l.</i>\n= semiclausus."],
  ["semicoctus", "<big>semicoctus</big>\n\n<b>semi–coctus, a, um</b> [semi + coquo]\nнаполовину сваренный (hordeum <i>Col</i>)."],
  ["semicombustus", "<big>semicombustus</big>\n\n<b>semi–combustus, a, um</b>\nполусожжённый <i>Sid, Eccl</i>."],
  ["semiconfectus", "<big>semiconfectus</big>\n\n<b>semi–confectus, a, um</b>\nполуобработанный <i>Sid</i>."],
  ["semiconspicuus", "<big>semiconspicuus</big>\n\n<b>semi–conspicuus, a, um</b>\nнаполовину видимый, <i>т. е.</i> наполовину высунувшийся (per fenestras <i>Ap</i>)."],
  ["semicrematus", "<big>semicrematus</big>\n\n<b>semi–crematus, a, um</b> [cremo]\nнаполовину сожжённый, полуобгоревший <i>O, M</i>."],
  ["semicremus", "<big>semicremus</big>\n\n<b>semicremus, a, um</b> <i>O</i>\n= semicrematus."],
  ["semicrudus", "<big>semicrudus</big>\n\n<b>semi–crudus, a, um</b>\n<b>1)</b> полусырой (caro <i>Fl etc.</i>);\n<b>2)</b> переваривший лишь наполовину, <i>т. е.</i> только что поевший <i>St</i>."],
  ["semicubitalis", "<big>semicubitalis</big>\n\n<b>semi–cubitalis, e</b>\nдлиной в половину римского локтя (<i>т. е.</i> <i>в 22,18 см</i>) (hastile <i>L</i>)."],
  ["semicupa", "<big>semicupa</big>\n\n<b>semi–cupa, ae</b> <i>f</i>\nполбочки, <i>насмешл.</i> пузан <i>Amm</i>."],
  ["semidea", "<big>semidea</big>\n\n<b>semi–dea, ae</b> <i>f</i>\nполубогиня <i>O, Aus</i>."],
  ["semidecima", "<big>semidecima</big>\n\n<b>semi–decima, ae</b> <i>f</i>\nполудесятинный налог <i>CTh</i>."],
  ["semideus", "<big>semideus</big>\n\n<b>I semi–deus, a, um</b>\nполубожественный (dryades <i>O</i>): s. canis <i>Lcn</i> = Anubis.\n\n<b>II semi–deus, i</b> <i>m</i>\nполубог (semideique deique <i>O</i>)."],
  ["semidies", "<big>semidies</big>\n\n<b>semi–dies, ei</b> <i>m</i>\nполдня <i>Aus</i>."],
  ["semidigitalis", "<big>semidigitalis</big>\n\n<b>semi–digitalis, e</b>\nдлиной <i>или</i> шириной в половину римского дюйма, <i>т. е.</i> 9,25 <i>мм</i> (foramen <i>Vtr</i>)."],
  ["semidoctus", "<big>semidoctus</big>\n\n<b>semi–doctus, a, um</b>\n<b>1)</b> полуучёный, недоучившийся <i>Pl, C etc.</i>;\n<b>2)</b> неопытный, неумелый (manus <i>M</i>)."],
  ["semiermis", "<big>semiermis</big>\n\n<b>semiermis, e</b> [semi + arma]\nлишь наполовину вооружённый (exercitus <i>L</i>)."],
  ["semiermus", "<big>semiermus</big>\n\n<b>semiermus, a, um</b> <i>T</i>\n= semiermis."],
  ["semiesus", "<big>semiesus</big>\n\n<b>semiesus, a, um</b> <i>V</i>\n= semesus."],
  ["semifactus", "<big>semifactus</big>\n\n<b>semi–factus, a, um</b> [facio]\nсделанный до половины, полузавершённый (opera <i>T</i>)."],
  ["semifalarica", "<big>semifalarica</big>\n\n<b>semi–falarica, ae</b> <i>f</i>\nнебольшая falarica (<i>см.</i>) <i>AG</i>."],
  ["semifastigium", "<big>semifastigium</big>\n\n<b>semi–fastigium, i</b> <i>n</i>\nполфронтона <i>Vtr</i>."],
  ["semifer", "<big>semifer</big>\n\n<b>I semi–fer, fera, ferum</b> [ferus]\n<b>1)</b> полузвериный (pectus Tritonis <i>V</i>; caput, <i>sc.</i> Panis <i>Lcr</i>);\n<b>2)</b> полудикий (animal <i>PM</i>).\n\n<b>II semifer, feri</b> <i>m</i>\n<b>1)</b> полузверь, полуживотное <i>O</i>;\n<b>2)</b> полудикарь <i>PM, Sil</i>."],
  ["semiferus", "<big>semiferus</big>\n\n<b>semiferus, a, um</b> <i>Aug</i>\n= semifer I."],
  ["semiformis", "<big>semiformis</big>\n\n<b>semi–formis, e</b> [forma]\nполусформировавшийся (pulli <i>Col</i>): luna s. <i>Col</i> полумесяц."],
  ["semifultus", "<big>semifultus</big>\n\n<b>semi–fultus, a, um</b> [fulcio]\nполуопёршийся (s. subsellio <i>M</i>)."],
  ["semifumans", "<big>semifumans</big>\n\n<b>semi–fumans, antis</b> [fumo]\nнаполовину (ещё) курящийся, <i>перен.</i> не совсем угасший (dignitas <i>Sid</i>)."],
  ["semifunium", "<big>semifunium</big>\n\n<b>semi–funium, i</b> <i>n</i> [funis]\nлолуканат, верёвка <i>Cato</i>."],
  ["Semigaetulus", "<big>Semigaetulus</big>\n\n<b>Semi–gaetulus, a, um</b>\nполугетульский <i>Ap</i>."],
  ["semigenius", "<big>semigenius</big>\n\n<b>semi–genius, a, um</b> <i>грам.</i>\nразличного рода в обоих числах (nomen)."],
  ["Semigermanus", "<big>Semigermanus</big>\n\n<b>Semi–germanus, a, um</b>\nполугерманский <i>L</i>."],
  ["Semigraecus", "<big>Semigraecus</big>\n\n<b>Semi–graecus, a, um</b>\nполугреческий <i>Vr, Su, Fl</i>."],
  ["semigravis", "<big>semigravis</big>\n\n<b>semi–gravis, e</b>\nполупьяный, захмелевший <i>L</i>."],
  ["semigro", "<big>semigro</big>\n\n<b>se–migro, avi, —, are</b>\nпереселяться, переезжать, отделяться (a patre <i>C</i>)."],
  ["semihians", "<big>semihians</big>\n\n<b>semi–hians, antis</b> <i>adj.</i> [hio]\nполуоткрытый (labellum <i>Ctl</i> <i>и</i> labella <i>Ap</i>); издаваемый полуоткрытым ртом, <i>т. е.</i> приглушённый, чуть слышный (vox <i>Ap</i>)."],
  ["semihiulcus", "<big>semihiulcus</big>\n\n<b>semi–hiulcus, a, um</b>\nполуоткрытый, <i>перен.</i> запечатлеваемый полуоткрытым ртом (savium <i>Poeta ap. AG</i>)."],
  ["semihomo", "<big>semihomo</big>\n\n<b>semi–homo, inis</b>\n<b>1.</b> <i>subst. m</i> получеловек (semihomines Centauri <i>O</i>);\n<b>2.</b> <i>adj.</i> полудикий (Cacus <i>V</i>)."],
  ["semihora", "<big>semihora</big>\n\n<b>semi–hora, ae</b> <i>f</i>\nполчаса <i>C, CC, Pt</i>."],
  ["semiinanis", "<big>semiinanis</big>\n\n<b>semi–inanis, e</b> <i>v. l.</i>\n= semirianis."],
  ["semiinteger", "<big>semiinteger</big>\n\n<b>semi–integer, gra, grum</b>\nнаполовину невредимый <i>Amm</i>."],
  ["semijejunium", "<big>semijejunium</big>\n\n<b>sēmi–jējūnium, ī</b> <i>n</i>\nполупост <i>Tert</i>."],
  ["semijugerum", "<big>semijugerum</big>\n\n<b>sēmi–jūgerum, ī</b> <i>n</i>\nпол-югера <i>Col</i>."],
  ["semilacer", "<big>semilacer</big>\n\n<b>semi–lacer, cera, cerum</b>\nполуразорванный, полурастерзанный (Pelias <i>O</i>)."],
  ["semilater", "<big>semilater</big>\n\n<b>semi–later, eris</b> <i>m</i>\nполкирпича <i>Vtr</i>."],
  ["semilaterium", "<big>semilaterium</big>\n\n<b>semi–laterium, i</b> <i>n</i> <i>Vtr</i>\n= semilater."],
  ["semilautus", "<big>semilautus</big>\n\n<b>semi–lautus, a, um</b>\nполувымытый, грязноватый (crura <i>Ctl</i>)."],
  ["semiliber", "<big>semiliber</big>\n\n<b>semi–liber, bera, berum</b>\nполусвободный <i>C, Sen</i>."],
  ["semilibra", "<big>semilibra</big>\n\n<b>semi–libra, ae</b> <i>f</i> <i>AV, Ap</i>\n= selibra."],
  ["semilixa", "<big>semilixa</big>\n\n<b>semi–lixa, ae</b> <i>m</i> <i>бран.</i>\nполумаркитаит <i>L</i>."],
  ["semilixula", "<big>semilixula</big>\n\n<b>semi–lixula, ae</b> <i>f</i>\nполовина кренделя <i>или</i> небольшой крендель (из <i>муки и сыра</i>) <i>Vr</i>."],
  ["semimadidus", "<big>semimadidus</big>\n\n<b>semi–madidus, a, um</b>\nнаполовину мокрый, влажный (ager <i>Col</i>)."],
  ["semimarinus", "<big>semimarinus</big>\n\n<b>semi–marinus, a, um</b>\nполуморской, <i>т. е.</i> с рыбьим хвостом (corpora <i>Lcr</i>)."],
  ["semimas", "<big>semimas</big>\n\n<b>semi–mas, aris</b> <i>m</i>\n<b>1)</b> полумужчина, гермафродит <i>L, O</i>;\n<b>2)</b> кастрат (ovis <i>O</i>)."],
  ["semimaturus", "<big>semimaturus</big>\n\n<b>semi–maturus, a, um</b>\nполуспелый, недозревший (hordeum <i>Pall</i>)."],
  ["Semimedus", "<big>Semimedus</big>\n\n<b>Semi–medus, a, um</b>\nполумидийский <i>Ap</i>."],
  ["semimetopion", "<big>semimetopion</big>\n\n<b>semi–metopion, i</b> <i>n</i> [metopa] <i>архит.</i>\nполуметопа <i>Vtr</i>."],
  ["semimitra", "<big>semimitra</big>\n\n<b>semi–mitra, ae</b> <i>f</i>\nполумитра, <i>род женского головного убора</i> <i>Dig</i>."],
  ["semimortuus", "<big>semimortuus</big>\n\n<b>semi–mortuus, a, um</b>\nполумёртвый (anus <i>Ap</i>); полуомертвелый (membra <i>Ctl</i>)."],
  ["seminalia", "<big>seminalia</big>\n\n<b>seminalia, ium</b> <i>n</i>\nпосевы <i>Tert</i>."],
  ["seminalis", "<big>seminalis</big>\n\n<b>seminalis, e</b> [semen]\n<b>1)</b> семейной, посевной (vicia <i>Col</i>);\n<b>2)</b> плодовитый, жизнетворный (trames <i>PV</i>; viae <i>CA</i>);\n<b>3)</b> половой (membrum <i>Priap</i>)."],
  ["seminanis", "<big>seminanis</big>\n\n<b>seminanis, e</b> [semi + inanis]\nполупустой: orbis lunae s. <i>PM</i> полумесяц."],
  ["seminarium", "<big>seminarium</big>\n\n<b>seminarium, i</b> <i>n</i> [semen]\nрассадник, питомник <i>Cato, Vr, Col</i>; <i>перен.</i> школа (s. exiguum militum <i>L</i>); рассадник, источник (omnium malorum <i>Ap</i>)."],
  ["seminarius", "<big>seminarius</big>\n\n<b>seminarius, a, um</b> [semen]\nсеменной: pilum seminarium <i>Cato</i> пестик для толчения семян."],
  ["seminatio", "<big>seminatio</big>\n\n<b>seminatio, onis</b> <i>f</i> [semino]\nобсеменение, оплодотворение <i>Vr, Eccl</i>."],
  ["seminator", "<big>seminator</big>\n\n<b>seminator, oris</b> <i>m</i> [semino]\n<b>1)</b> сеятель (omnium rerum s. et sator <i>C</i>);\n<b>2)</b> создатель, виновник (omnium malorum <i>C</i>)."],
  ["seminex", "<big>seminex</big>\n\n<b>semi–nex, necis</b> <i>adj.</i>\nполумёртвый (artus <i>O</i>): s. vita <i>Sil</i> остаток жизни."],
  ["seminium", "<big>seminium</big>\n\n<b>seminium, i</b> <i>n</i> [semen]\n<b>1)</b> семя (<i>животное</i>) <i>Pl, Vr, Lcr</i>;\n<b>2)</b> порода (<i>животного</i>) <i>Vr, Lcr</i>."],
  ["seminiverbius", "<big>seminiverbius</big>\n\n<b>semini–verbius, i</b> <i>m</i> (<i>дословный перевод греч.</i> spermologos)\nсуеслов, пустой болтун <i>Vlg</i>."],
  ["semino", "<big>semino</big>\n\n<b>semino, avi, atum, are</b> [semen]\n<b>1)</b> сеять (hordeum <i>Col</i>); обсеменять, засевать (agrum <i>Col</i>);\n<b>2)</b> производить, рождать (aliquem <i>и</i> aliquid <i>Pl, Col, C, V</i>);\n<b>3)</b> рассевать, распространять (cultum dei per totam terram <i>Lact</i>)."],
  ["seminosus", "<big>seminosus</big>\n\n<b>seminosus, a, um</b> [semen]\nполный семян (cucurbita <i>Priap</i>)."],
  ["seminudus", "<big>seminudus</big>\n\n<b>semi–nudus, a, um</b>\n<b>1)</b> полунагой <i>L, Sen, Ap</i>;\n<b>2)</b> почти безоружный (pedes <i>L</i>)."],
  ["Seminumida", "<big>Seminumida</big>\n\n<b>Semi–numida, ae</b> <i>m</i>\nполунумидиец <i>Ap</i>."],
  ["semiobrutus", "<big>semiobrutus</big>\n\n<b>semi–obrutus, a, um</b>\nполузарытый (dolium <i>Ap</i>)."],
  ["semiorbis", "<big>semiorbis</big>\n\n<b>semi–orbis, is</b> <i>m</i>\nполукруг, полуокружность <i>Sen, Amm</i>."],
  ["semipaganus", "<big>semipaganus</big>\n\n<b>semi–paganus, i</b> <i>m</i>\nполумужик <i>Pers</i>."],
  ["semipatens", "<big>semipatens</big>\n\n<b>semi–patens, entis</b> [pateo]\nполуоткрытый (vestibula <i>Sid</i>)."],
  ["semipedalis", "<big>semipedalis</big>\n\n<b>semi–pedalis, e</b>\nвеличиной в половину римского фута, <i>т. е.</i> 14,78 <i>см</i> (apertura <i>Vtr</i>)."],
  ["semipedaneus", "<big>semipedaneus</big>\n\n<b>semi–pedaneus, a, um</b> <i>Col</i>\n= semipedalis."],
  ["semiperfectus", "<big>semiperfectus</big>\n\n<b>semi–perfectus, a, um</b>\n<b>1)</b> полуоконченный (opera <i>Su</i>);\n<b>2)</b> полусовершенный, неполный (virtutes <i>Ap</i>)."],
  ["Semipersa", "<big>Semipersa</big>\n\n<b>Semi–persa, ae</b> <i>m</i>\nполуперс <i>Ap</i>."],
  ["semipes", "<big>semipes</big>\n\n<b>semi–pes, pedis</b> <i>m</i>\n<b>1)</b> половина римского фута (14,78 <i>см</i>) <i>Cato, Vr, PM</i>;\n<b>2)</b> половина стихотворной стопы <i>Vr ap. AG, Aus</i>;\n<b>3)</b> хромающий на одну ногу <i>Eccl</i>."],
  ["semiphalarica", "<big>semiphalarica</big>\n\n<b>semiphalarica, ae</b> <i>f</i> <i>v. l.</i>\n= semifalarica."],
  ["semipiscina", "<big>semipiscina</big>\n\n<b>semi–piscina, ae</b> <i>f</i>\nнебольшой рыбный пруд <i>Vr</i>."],
  ["Semiplacentinus", "<big>Semiplacentinus</big>\n\n<b>Semi–placentinus, i</b> <i>m</i>\nнаполовину уроженец Плацентии <i>C</i>."],
  ["semiplenus", "<big>semiplenus</big>\n\n<b>semi–plenus, a, um</b>\n<b>1)</b> до половины полный, наполовину наполненный (scrobes <i>Col</i>);\n<b>2)</b> неукомплектованный, неполный (legio <i>VP</i>)."],
  ["semipuella", "<big>semipuella</big>\n\n<b>semi–puella, ae</b> <i>f</i>\nполудева (<i>sc.</i> Siren <i>Aus</i>)."],
  ["semipullatus", "<big>semipullatus</big>\n\n<b>semi–pullatus, a, um</b>\nодетый наполовину в чёрное <i>Sid</i>."],
  ["semiputatus", "<big>semiputatus</big>\n\n<b>semi–putatus, a, um</b>\nполуобрезанный, наполовину подстриженный (vitis <i>V</i>)."],
  ["semiquinaria", "<big>semiquinaria</big>\n\n<b>semi–quinaria, ae</b> <i>f</i> [quini] <i>стих.</i>\nполовина пятистопного стиха, <i>т. е.</i> полупентаметр."],
  ["Semiramis", "<big>Semiramis</big>\n\n<b>Semiramis, idis</b> <i>и</i> <b>idos</b> <i>f</i> (<i>acc.</i> im, <i>abl.</i> i <i>и</i> ide)\nСемирамида, <i>полумиф. царица Ассирии, жена царя Нина, правившая после смерти мужа за своего сына Ниния, прославившаяся своими завоеваниями и украсившая Вавилон грандиозными постройками и садами</i> <i>O, QC, Just</i>."],
  ["Semiramius", "<big>Semiramius</big>\n\n<b>Semiramius, a, um</b> [Semiramis]\n<b>1)</b> семирамидин (Semiramio sanguine cretus Polydaemon <i>O</i>);\n<b>2)</b> вавилонский (acus <i>M</i>)."],
  ["semirasus", "<big>semirasus</big>\n\n<b>semi–rasus, a, um</b> [rado]\nполуостриженный <i>или</i> наполовину бритый <i>Ctl, Ap</i>."],
  ["semireductus", "<big>semireductus</big>\n\n<b>semi–reductus, a, um</b> [reduco]\nнесколько откинувшийся назад (Venus <i>O</i>)."],
  ["semirefectus", "<big>semirefectus</big>\n\n<b>semi–refectus, a, um</b> [reficio]\nнаполовину исправленный, не вполне починенный, не совсем приведённый в порядок (classis <i>O</i>)."],
  ["semirotundus", "<big>semirotundus</big>\n\n<b>semi–rotundus, a, um</b>\nполукружный, полукруглый (suggestus <i>Ap</i>)."],
  ["semirutus", "<big>semirutus</big>\n\n<b>semi–rutus, a, um</b> [ruo]\nполуразорённый, полуразрушенный (urbs <i>L</i>; tecta <i>L, Lcn</i>): semiruta moenium <i>Ap</i> полуразвалившиеся стены."],
  ["semis", "<big>semis</big>\n\n<b>I semis</b> <i>indecl.</i>\nполовина: dividere in partes undecim s. <i>Vtr</i> разделить на 11,5 частей.\n\n<b>II semis, missis</b> <i>m</i> [semi + as]\n<b>1)</b> половина (semissem Africae possidere <i>PM</i>; panem semissem ponere <i>Pt</i>);\n<b>2)</b> пол-асса <i>H</i>: homo non semissis <i>погов. Vatinius ap. C</i> никуда не годный человек;\n<b>3)</b> половина золотого (semisses aureorum <i>Lampr</i>);\n<b>4)</b> пол-югера <i>Col, L, PM</i>;\n<b>5)</b> половина римского фута (duo pedes et s. <i>Col</i>);\n<b>6)</b> половина наследства: heres ex semisse <i>Dig</i> получающий половину наследства;\n<b>7)</b> (<i>тж.</i> usura semissium <i>или</i> semisses usurarum <i>Col</i> <i>и</i> semisses usurae <i>Dig</i>) 1/2 процента в месяц, <i>т. е.</i> 6 процентов годовых: semissibus magna copia (<i>sc.</i> pecuniae) est <i>C</i> при 6 процентах годовых можно собрать большие средства."],
  ["semisaucius", "<big>semisaucius</big>\n\n<b>semi–saucius, a, um</b>\nнадломленный, ослабленный (voluntas <i>Aug</i>)."],
  ["semisenex", "<big>semisenex</big>\n\n<b>semi–senex, senis</b> <i>m</i>\nполустарик <i>Pl</i>."],
  ["semiseptenaria", "<big>semiseptenaria</big>\n\n<b>semiseptenaria, ae</b> <i>f</i> <i>стих.</i>\n— hephthemimeres."],
  ["semisepultus", "<big>semisepultus</big>\n\n<b>semi–sepultus, a, um</b>\nполузарытый, полупогребённый (ossa <i>O</i>)."],
  ["semisermo", "<big>semisermo</big>\n\n<b>semi–sermo, onis</b> <i>m</i>\nмаловразумительный говор (s. barbarus <i>Hier</i>)."],
  ["semisiccus", "<big>semisiccus</big>\n\n<b>semi–siccus, a, um</b>\nполусухой (thalli <i>Pall</i>)."],
  ["semisicilicus", "<big>semisicilicus</big>\n\n<b>semi–sicilicus, i</b> <i>m</i>\nполусицилик, <i>т. е.</i> 3,411 <i>г</i> <i>Pall</i>."],
  ["semisomnis", "<big>semisomnis</big>\n\n<b>semisomnis, e</b> <i>Sen</i>\n= semisomnus."],
  ["semisomnus", "<big>semisomnus</big>\n\n<b>semi–somnus, a, um</b>\nполуспящий, заспанный, сонный <i>Pl, C, Sl, L</i>: s. sopor <i>Caelius ap. Q</i> полусон, забытьё."],
  ["semisonans", "<big>semisonans</big>\n\n<b>semisonans, antis</b> <i>adj.</i> [semi + sono]\nполугласный (litterae <i>Ap</i>)."],
  ["semisonarius", "<big>semisonarius</big>\n\n<b>semisonarius</b> <i>Pl</i>\n(<i>v. l.</i>) = semizonarius."],
  ["semisopitus", "<big>semisopitus</big>\n\n<b>semi–sopitus, a, um</b> <i>Ap</i> <i>и</i> <b>semi–soporus, a, um</b> <i>Sid</i>\n= semisomnus."],
  ["semispatha", "<big>semispatha</big>\n\n<b>semi–spatha, ae</b> <i>f</i>\nкороткий обоюдоострый меч <i>Veg</i>."],
  ["semissalis", "<big>semissalis</big>\n\n<b>semissalis, e</b> [semis II, 7]\nполуассовый: s. usura <i>Dig</i> = 6 процентов годовых."],
  ["semissarius", "<big>semissarius</big>\n\n<b>semissarius, a, um</b> [semis II, 6]\nполучающий половину наследства (heres <i>Dig</i>)."],
  ["semissis", "<big>semissis</big>\n\n<b>semissis</b> <i>gen. к</i>\nsemis II."],
  ["semisso", "<big>semisso</big>\n\n<b>semisso, —, —, are</b> [semis II, 5]\nвыжигать полуфутовый кусок кожи (s. pedes equorum <i>Veg</i>)."],
  ["semisupinus", "<big>semisupinus</big>\n\n<b>semi–supinus, a, um</b>\nполуоткинувшийся назад, полулежащий <i>O, M</i>."],
  ["semita", "<big>semita</big>\n\n<b>semita, ae</b> <i>f</i> [<i>по</i> <i>Vr</i> <i>из</i> *semiter «полудорога»]\n<b>1)</b> узкая дорожка, просёлочная дорога, тропа, <i>тж.</i> дорога вдоль улицы для пешеходов, тротуар (s. angusta, proclivis <i>L, C etc.</i>): decedere alicui desemita <i>Pl</i> <i>или</i> cedere semita <i>Sen</i> уступать кому-л. дорогу; qui sibi semitam non sapiunt, alteri monstrant viam <i>погов. Enn ap. C</i> (иные) не знают, по какой тропинке самим идти, а другим указывают дорогу; alicujus semita (<i>v. l.</i> semitam) facere viam <i>погов. Ph</i> пойти дальше по намеченному кем-л. пути, <i>т. е.</i> продолжить начатое им дело; in rectam semitam redire <i>Pl</i> попасть на правильный путь;\n<b>2)</b> <i>перен.</i> борозда, длинный след (spumea s. fugientis clavi, <i>sc.</i> navis <i>VF</i>);\n<b>3)</b> движение, бег (velox lunae s. <i>Cld</i>)."],
  ["semitactus", "<big>semitactus</big>\n\n<b>semi–tactus, a, um</b> [tango]\nслегка тронутый, чуть намазанный (unguento <i>M</i>)."],
  ["semitalis", "<big>semitalis</big>\n\n<b>semitalis, e</b> [semita]\nдорожный: dii semitales <i>V</i> боги просёлочных дорог."],
  ["semitarius", "<big>semitarius</big>\n\n<b>semitarius, a, um</b> [semita]\nпрячущийся в переулках, закоулочный (moechi <i>Ctl</i>)."],
  ["semitectus", "<big>semitectus</big>\n\n<b>semi–tectus, a, um</b> [tego]\nполуприкрытый, полуодетый (scapulae <i>Sen</i>; exercitus <i>Amm</i>)."],
  ["semito", "<big>semito</big>\n\n<b>semito, —, —, are</b> [semita]\nпрорезать тропинками (vineas <i>PM</i>)."],
  ["semitonium", "<big>semitonium</big>\n\n<b>semi–tonium, i</b> <i>n</i>\nполутон <i>Macr</i>."],
  ["semitractatus", "<big>semitractatus</big>\n\n<b>semi–tractatus, a, um</b>\nлишь наполовину отработанный (materia <i>Tert</i>)."],
  ["semitrepidus", "<big>semitrepidus</big>\n\n<b>semi–trepidus, a, um</b>\nслегка <i>или</i> наполовину дрожащий <i>Ap</i>."],
  ["semitritus", "<big>semitritus</big>\n\n<b>semi–tritus, a, um</b> [tero]\nнаполовину обмолоченный (frumenta <i>Col</i>)."],
  ["semiulc-", "<big>semiulc-</big>\n\n<b>semiulc-</b> <i>v. l.</i>\n= semulc-."],
  ["semiunc-", "<big>semiunc-</big>\n\n<b>semiunc-</b> <i>v. l.</i>\n= semunc-."],
  ["semiustulatus", "<big>semiustulatus</big>\n\n<b>semiustulatus, a, um</b> <i>v. l.</i>\n= semustulatus."],
  ["semiustulo", "<big>semiustulo</big>\n\n<b>semi–ustulo, —, atum, are</b>\nсжигать наполовину <i>Su</i>."],
  ["semiustus", "<big>semiustus</big>\n\n<b>semi–ustus, a, um</b> [uro]\nполусожжённый (cadaver <i>Su</i>); полуобгоревший (fax <i>O</i>)."],
  ["semivietus", "<big>semivietus</big>\n\n<b>semi–vietus, a, um</b>\nполусморщенный, полузавядший (uvae <i>Col</i>)."],
  ["semivir", "<big>semivir</big>\n\n<b>I semi–vir, viri</b> <i>m</i>\n<b>1)</b> получеловек (s. Chiron <i>O</i>); s. bos <i>O</i> = Minotaurus;\n<b>2)</b> полумужчина, гермафродит <i>O, PM</i>;\n<b>3)</b> кастрат <i>Vr, J, Sil</i>.\n\n<b>II semi–vir, viri</b> <i>adj. m</i>\nлишённый мужественности, женоподобный (comitatus, <i>sc.</i> Aeneae <i>V</i>)."],
  ["semivivus", "<big>semivivus</big>\n\n<b>semi–vivus, a, um</b>\nполуживой <i>C, Nep, Sen</i>; замирающий, томный (vox <i>C</i>)."],
  ["semivocales", "<big>semivocales</big>\n\n<b>semi–vocales, ium</b> <i>f</i> (<i>sc.</i> litterae)\nбуквы, названия которых начинаются с гласной (f, l, m, n, r, s, x) <i>Q</i>."],
  ["semivocalis", "<big>semivocalis</big>\n\n<b>semi–vocalis, e</b>\nполунемой, бессловесный: instrumentum rusticum semivocale <i>Vr</i> живой сельскохозяйственный инвентарь (<i>т. е.</i> скот) (<i>instrumentum rusticum vocale назывались рабы, а instrumentum rusticum mutum — мёртвый инвентарь</i>); signum semivocale <i>Veg</i> = звук трубы (<i>в отличие от signum vocale «словесной команды» и signum mutum «знамени»</i>)."],
  ["semivocus", "<big>semivocus</big>\n\n<b>semi–vocus, a, um</b>\nпроизносимый вполголоса (verba <i>Aug</i>).."],
  ["semivolucer", "<big>semivolucer</big>\n\n<b>semi–volucer, cris, cre</b>\nполуокрыленный, <i>т. е.</i> наполовину птица (puellae, <i>sc.</i> Sirenes <i>Sid</i>)."],
  ["semizonarius", "<big>semizonarius</big>\n\n<b>semi–zonarius, i</b> <i>m</i> [<i>греч.</i> zone]\nизготовляющий пояски (semicinctia) <i>Pl</i> (<i>v. l.</i>)."],
  ["Semnones", "<big>Semnones</big>\n\n<b>Semnones, um</b> <i>m</i>\nсемноны, <i>самая сильная племенная ветвь свебов, обитавшая к сев. от Исполинских гор</i> <i>T, VP</i>."],
  ["Semo", "<big>Semo</big>\n\n<b>Semo, onis</b> <i>m</i> [semen]\nСемон, <i>древнее божество земледелия</i> (<i>эпитет бога Sancus</i>) <i>L, O</i>."],
  ["semodialis", "<big>semodialis</big>\n\n<b>semodialis, e</b> [semodius]\nсодержащий (<i>или</i> ёмкостью в) полмодия <i>Cato</i>."],
  ["semodius", "<big>semodius</big>\n\n<b>semodius, i</b> <i>m</i> [semi + modius]\nполмодия, <i>т. е.</i> 4,377 <i>л</i> <i>Cato, Col, M, J</i>."],
  ["semotus", "<big>semotus</big>\n\n<b>semotus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> semoveo;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> отдалённый, уединённый (pars urbis <i>bAl</i>); находящийся вдали, в стороне (locus a militibus s. <i>Cs</i>);\n<b>2)</b> <i>перен.</i> далёкий, свободный (a curis <i>и</i> cura <i>Lcr</i>); отличающийся, отличный (natura semota a nostris rebus <i>Lcr</i>); непохожий, несовместимый (egestas semota ab dulci vita <i>Lcr</i>);\n<b>3)</b> секретный, тайный: semota dictio <i>T</i> доверительный разговор."],
  ["semoveo", "<big>semoveo</big>\n\n<b>se–moveo, movi, motum, ere</b>\n<b>1)</b> отдалять, отделять, разлучать (aliquem ab aliquo <i>C</i>);\n<b>2)</b> освобождать (aliquem a curis <i>или</i> cura <i>Lcr</i>); исключать, ставить вне (sententias alicujus a philosophia <i>C</i>); устранять, удалять (verba <i>C</i>). — <i>См. тж.</i> semotus."],
  ["semper", "<big>semper</big>\n\n<b>semper</b> [<i>одного корня с</i> semel]\n<b>1)</b> всегда, постоянно (quod s. movetur, aeternum est <i>C</i>): s. annorum <i>Ap</i> во все годы, навсегда; s. florens Homerus <i>Lcr</i> неувядающий (вечно юный) Гомер;\n<b>2)</b> <i>при сущ.</i> всегдашний, постоянный (Hasdrubal, pacis s. auctor <i>L</i>);\n<b>3)</b> всё ещё, по-прежнему (porticus s. verna <i>PJ</i>)."],
  ["semperflorium", "<big>semperflorium</big>\n\n<b>semper–florium, i</b> <i>n</i> <i>Ap</i>\n= sempervivum."],
  ["sempervivum", "<big>sempervivum</big>\n\n<b>semper–vivum, i</b> <i>n</i> [vivo] <i>бот. предпол.</i>\nбарвинок <i>PM</i>."],
  ["sempervivus", "<big>sempervivus</big>\n\n<b>semper–vivus, a, um</b>\nневянущий, вечнозелёный (herba <i>Ap</i>)."],
  ["sempiterne", "<big>sempiterne</big>\n\n<b>sempiterne</b> <i>Pac, Eccl</i>\n= sempiterno."],
  ["sempiternitas", "<big>sempiternitas</big>\n\n<b>sempiternitas, atis</b> <i>f</i>\nвечность <i>Ap</i>."],
  ["sempiterno", "<big>sempiterno</big>\n\n<b>sempiterno</b> <i>adv.</i> [semper]\n<b>1)</b> всегда, постоянно (permanere <i>Vtr</i>): s. nominabitur <i>Pt</i> его имя никогда не забудется;\n<b>2)</b> навсегда (monumenta alicui ponere <i>Cato</i>)."],
  ["sempiternum", "<big>sempiternum</big>\n\n<b>sempiternum</b> <i>adv.</i> <i>Pl</i>\n= sempiterno."],
  ["sempiternus", "<big>sempiternus</big>\n\n<b>sempiternus, a, um</b> [semper]\nвсегдашний, безостановочный, непреходящий, постоянный (cursus stellarum <i>C</i>); вечный (ignis Vestae <i>C</i>; memoria <i>C</i>; oblivio <i>C</i>; mundus <i>Ap</i>)."],
  ["Sempronianus", "<big>Sempronianus</big>\n\n<b>Sempronianus, a, um</b> [Sempronius]\nсемпрониев <i>C, L</i>."],
  ["Sempronius", "<big>Sempronius</big>\n\n<b>Sempronius, a, um</b>\nСемпроний, <i>римск. nomen; в этот род входили многие семьи, как, напр.</i>, Gracchi, Atratini, Blaesi, Longi, Tuditani <i>C, L, T etc.</i>"],
  ["semul", "<big>semul</big>\n\n<b>semul</b> <i>арх.</i> <i>Pl</i>\n= simul."],
  ["semulcus", "<big>semulcus</big>\n\n<b>sēmulcus, a, um</b> <i>Macr</i>\n= semihiulcus."],
  ["semuncia", "<big>semuncia</big>\n\n<b>sēmuncia, ae</b> <i>f</i> [semi + uncia]\n<b>1)</b> пол-унции, <i>т. е.</i> 1/24 асса = 13,644 <i>г</i> <i>Vr etc.</i>;\n<b>2)</b> 24-я часть (<i>югера и пр.</i>) <i>Col etc.</i>: heres ex semuncia <i>C</i> получающий 24-ю часть наследства;\n<b>3)</b> безделица, мелочь: brevis s. recti <i>Pers</i> капелька здравого смысла."],
  ["semuncialis", "<big>semuncialis</big>\n\n<b>sēmunciālis, e</b> [semuncia]\nвесом в пол-унции <i>PM, Sol</i>."],
  ["semunciarius", "<big>semunciarius</big>\n\n<b>sēmunciārius, a, um</b> [semuncia]\nвесом (размером) в пол-унции <i>или</i> составляющий 1/24 целого (asses <i>PM</i>): fenus semunciarium <i>L</i> 1/24 процента в месяц, <i>т. е.</i> 1/2 процента в год."],
  ["Semurius ager", "<big>Semurius ager</big>\n\n<b>Semūrius ager</b>\nСемурийское поле, <i>урочище близ Рима с храмом Аполлона</i> <i>C, Macr</i>."],
  ["semustulatus", "<big>semustulatus</big>\n\n<b>sēmustulātus, sēmustilātus</b> <i>и</i> <b>sēmiustulātus, a, um</b>\nполусожжённый <i>Vr, C</i>."],
  ["semustus", "<big>semustus</big>\n\n<b>sēmustus, a, um</b> <i>v. l.</i>\n= semiustus."],
  ["Sena", "<big>Sena</big>\n\n<b>I Sēna, ae</b> <i>f</i>\nСена, <i>город в Умбрии, в устье реки того же названия, в области галльских сенонов</i> (<i>ныне</i> Sinigaglia); <i>здесь, близ берегов реки Метавр, в 207 г. до н. э. римляне разбили войска Гасдрубала</i> <i>L, Eutr</i>.\n\n<b>II Sēna, ae</b> <i>m</i>\nСена, <i>река в Умбрии, близ города</i> Sena I <i>Sil, Lcn</i>."],
  ["senaculum", "<big>senaculum</big>\n\n<b>senaculum, i</b> <i>n</i> [senatus]\nздание <i>или</i> зал заседаний сената <i>Vr, L, VM etc.</i>"],
  ["Senanus", "<big>Senanus</big>\n\n<b>Senanus, a, um</b> [Sena II]\nсенский (aquae <i>CA</i>)."],
  ["senape", "<big>senape</big>\n\n<b>senape</b> <i>Pt</i> <i>и</i> <b>senapi(s)</b> <i>Pl</i>\n= sinapi."],
  ["senariolus", "<big>senariolus</big>\n\n<b>senariolus, i</b> <i>m</i> [<i>demin. к</i> senarius II]\nмаленький сенарий (шестистопный стишок) <i>C</i>."],
  ["senarius", "<big>senarius</big>\n\n<b>I senarius, a, um</b> [seni I]\nсостоящий из шести, шестерной (versus <i>C, Q</i>): numerus s. <i>Macr</i> шестёрка.\n\n<b>II senarius, i</b> <i>m</i>\nсенарий, шестистопный стих (<i>преим.</i> ямбический) <i>C, Q, Ph</i>."],
  ["senator", "<big>senator</big>\n\n<b>senator, oris</b> <i>m</i> [senatus]\nчлен сената, сенатор (<i>знаки достоинства: золотой перстень, tunica laticlavia, calcei nigri, украшенные lunula из серебра или слоновой кости; имущественный ценз для сенаторов был вначале 800 000, впоследствии 1 200 000 сестерциев</i>) <i>C, Cs, L etc.</i>"],
  ["senatorius", "<big>senatorius</big>\n\n<b>I senatorius, a, um</b> [senator]\nсенаторский (ordo <i>C</i>; census <i>Su</i>); сенатский (oratio <i>Sol</i>); направленный против сената (invidia <i>C</i>).\n\n<b>II senatorius, i</b> <i>m</i>\nвходящий в состав сената, имеющий звание сенатора, сенатор <i>Sl</i>."],
  ["senatus", "<big>senatus</big>\n\n<b>senatus, us</b> (<i>редко</i> <i>Pl, C, St</i> <b>i</b>) (<i>dat.</i> ui <i>и</i> u) <i>m</i> [senex]\n<b>1)</b> сенат, государственный совет Рима (<i>при Ромуле из 100 членов, при Цезаре — 900, при Августе — 600</i>) (s. populusque Romanus <i>C, Sl, L etc.</i>): in senatum legere <i>C, L</i> избирать в сенат; senatum legere <i>L</i> оглашать список сенаторов; in senatum venire <i>C</i> приходить в сенат, <i>но тж.</i> быть принятым в число сенаторов; (de) senatu movere <i>или</i> ex senatu ejicere <i>C</i> исключать из состава сената; senatus consultum <i>C etc.</i> сенатское постановление (<i>имеющее законодательную силу</i>); senatus auctoritas <i>C etc.</i> сенатское мнение (<i>без законодательной силы</i>);\n<b>2)</b> заседание сената: s. frequens <i>C</i> многочисленное, <i>т. е.</i> правомочное собрание сената; s. est (<i>или</i> habetur) <i>C etc.</i> происходит заседание сената; s. legitimus <i>Su</i> очередное заседание сената; s. indictus <i>Su</i> чрезвычайное заседание сената; senatum alicui dare <i>C, Sl etc.</i> дать кому-л. аудиенцию в сенате;\n<b>3)</b> сенаторские места в театре <i>Su</i>;\n<b>4)</b> высший совещательный орган, совет старейшин у неримских народов (<i>карфагенян, спартанцев и др.</i>) <i>Cs, L</i>."],
  ["senatusconsultum", "<big>senatusconsultum</big>\n\n<b>senatusconsultum, i</b> <i>n</i>\n— <i>см.</i> senatus I (senatus consultum)."],
  ["Seneca", "<big>Seneca</big>\n\n<b>Seneca, ae</b> <i>m</i>\nСенека, <i>cognomen в роде Аннеев</i> (<i>см.</i> Annaeus)."],
  ["senecio", "<big>senecio</big>\n\n<b>I senecio, onis</b> <i>m</i> [senex II]\n<b>1)</b> <i>арх.</i> старик, старец;\n<b>2)</b> <i>бот.</i> крестовник обыкновенный (<i>Senecio vulgaris</i>, L.) <i>PM, Ap</i>.\n\n<b>II Senecio, onis</b> <i>m</i>\nСенецион, <i>cognomen в роде Гервиниев и др.</i> <i>T</i>."],
  ["senecta", "<big>senecta</big>\n\n<b>senecta, ae</b> <i>f</i> [senectus]\n<b>1)</b> старость, престарелость <i>Pl, Ter, Vr etc.</i>;\n<b>2)</b> старая змеиная кожа (senectam exuere <i>PM</i>);\n<b>3)</b> старик: hac spirante senecta <i>Sil</i> пока жив этот старик (Фабий)."],
  ["senectus", "<big>senectus</big>\n\n<b>I senectus, a, um</b> [senex]\nстарый, престарелый (corpus <i>Sl</i>); старческий, преклонный (aetas <i>Pl, Lcr etc.</i>).\n\n<b>II senectus, utis</b> <i>f</i> [senex]\n<b>1)</b> старость, старческий возраст (vivere ad summam senectutem <i>C</i>);\n<b>2)</b> старое поколение, старики (s. semper agens aliquid <i>C</i>);\n<b>3)</b> мрачный вид, строгость (frontis <i>H</i>);\n<b>4)</b> седина (temporibus sparsa s. <i>V</i>);\n<b>5)</b> старая кожа змеи (senectutem exuere <i>PM</i>);\n<b>6)</b> ветхость (s. cariosa tabellarum <i>O</i>);\n<b>7)</b> выдержанность (vini <i>J</i>);\n<b>8)</b> зрелость (plena litteratae senectutis oratio <i>C</i>)."],
  ["Senensis", "<big>Senensis</big>\n\n<b>Senensis, e</b> [Sena I]\nсенский (proelium <i>C</i>)."],
  ["seneo", "<big>seneo</big>\n\n<b>seneo, —, —, ere</b> [senex]\n<b>1)</b> быть старым <i>Pac, Ctl</i>;\n<b>2)</b> быть дряхлым, немощным <i>Pac, Acc</i>."],
  ["senesco", "<big>senesco</big>\n\n<b>senesco, senui, —, ere</b> [<i>inchoat. к</i> seneo]\n<b>1)</b> стареть, стариться: tacitis senescimus annis <i>O</i> мы незаметно старимся;\n<b>2)</b> хиреть, слабеть, ослабевать (s. viribus <i>L</i>); приходить в упадок, ветшать (monumenta senescunt <i>Lcr</i>); истощаться, слабеть (veneno desidiae virtus senescit <i>Sil</i>); терять силу (morbus senescit <i>C</i>); подходить к концу, кончаться (hiems senescens <i>C</i>); тускнеть, меркнуть (laus senescit <i>C</i>): ager continua messe senescit <i>O</i> поле истощается постоянными жатвами; coma senescit <i>Su</i> волосы выпадают; luna senescit <i>C</i> (senescens <i>Ap</i>) луна на ущербе."],
  ["senex", "<big>senex</big>\n\n<b>I senex, senis</b> (<i>abl.</i> e, <i>gen. pl.</i> um) <i>adj.</i> (<i>compar.</i> senior)\n<b>1)</b> старый (magister <i>C</i>; cervus <i>O</i>);\n<b>2)</b> преклонный, старческий (anni <i>O</i>);\n<b>3)</b> древний (poetae <i>C</i>);\n<b>4)</b> зрелый (oratio, vis <i>C</i>);\n<b>5)</b> поздний (senibus saeculis <i>V</i>).\n\n<b>II senex, senis</b> <i>m, f</i>\nстарик, старец (<i>старше 60 лет, реже — от 45 до 60</i>) <i>C etc.</i>; старуха <i>Tib, VF</i>."],
  ["seni", "<big>seni</big>\n\n<b>I seni, ae, a</b> <i>adj. num. distr.</i> [sex]\n<b>1)</b> по шести: pueri senum (= senorum) annorum <i>C</i> мальчики шести лет каждый; senis horis <i>L</i> каждые шесть часов;\n<b>2)</b> шесть: s. pedes <i>H</i> шестистопный стих, <i>т. е.</i> гексаметр; s. ictus <i>H</i> сенарий; bis s. <i>V</i> двенадцать; s. deni <i>L, Vtr</i> шестнадцать <i>и</i> по шестнадцати.\n\n<b>II seni</b> <i>dat. sg. к</i>\nsenex."],
  ["Seniae balneae", "<big>Seniae balneae</big>\n\n<b>Seniae balneae</b> <i>f</i>\n<i>общественные бани в Риме</i> <i>C</i>."],
  ["senicis", "<big>senicis</big>\n\n<b>senicis</b> <i>арх. Pl gen. к</i>\nsenex."],
  ["seniculus", "<big>seniculus</big>\n\n<b>seniculus, i</b> <i>m</i> [senex]\nстаричок <i>Ap</i>."],
  ["senilis", "<big>senilis</big>\n\n<b>senilis, e</b> [senex]\nстарческий, стариковский (anni <i>O</i>; corpus <i>C</i>; ruga <i>O</i>); подобающий старику, свойственный старости (prudentia <i>C</i>): adulescens in quo est senile aliquid <i>C</i> юноша, в котором есть нечто стариковское; adoptio s. <i>T</i> усыновление стариком."],
  ["seniliter", "<big>seniliter</big>\n\n<b>seniliter</b> [senilis]\nпо-стариковски, старчески (tremere <i>Q</i>)."],
  ["senio", "<big>senio</big>\n\n<b>senio, onis</b> <i>m</i> [seni I]\nшестёрка, шесть очков (<i>в игре</i>) <i>Pers, M</i>: senionem mittere <i>Su</i> выбросить шесть очков."],
  ["senior", "<big>senior</big>\n\n<b>senior, oris</b>\n<b>1.</b> <i>adj. compar. к</i> senex;\n<b>2.</b> <i>subst. m</i>\n<b>1)</b> пожилой человек (<i>от 45 до 60 лет</i>) <i>H, L</i>;\n<b>2)</b> (очень) старый, дряхлый (Nestor <i>O</i>); <i>pl.</i> старейшие, старшие (seniores patrum <i>L</i>) <i>или</i> <i>L</i> совет старейшин (<i>в Карфагене</i>);\n<b>3)</b> <i>воен.</i> призывной самых старших возрастов (<i>от 45 до 60 лет</i>) (centuriae seniorum <i>H</i>)."],
  ["senium", "<big>senium</big>\n\n<b>senium, i</b> <i>n</i> [senex]\n<b>1)</b> старость, престарелость, дряхлость (morbus et s. <i>C</i>; senio confectus <i>C</i>);\n<b>2)</b> упадок, ослабление (famae <i>St</i>); ущерб (lunae <i>PM</i>);\n<b>3)</b> угрюмость, мрачность (morum <i>SenT</i>);\n<b>4)</b> скорбь, огорчение: ea res mihi senio est <i>Pl</i> это огорчает меня;\n<b>5)</b> <i>презр.</i> старикан <i>Ter</i>."],
  ["Senones", "<big>Senones</big>\n\n<b>Senones, um</b> (<i>sg.</i> Seno <i>и</i> Senon) <i>m</i>\nсеноны:\n<b>1)</b> <i>кельт. племя в Gallia Lugdunensis по обоим берегам верхнего течения Секваны, с главн. городом Agedincum</i> <i>Cs, Hirt, J</i>;\n<b>2)</b> <i>ветвь этого племени на вост. побережье Умбрии, между Равенной и Анконой</i> <i>L</i>."],
  ["sensa", "<big>sensa</big>\n\n<b>sensa, orum</b> <i>n</i> [sentio]\nмысли, мнения, взгляды, представления (s. mentis verbis explicare <i>C</i>)."],
  ["sensate", "<big>sensate</big>\n\n<b>sensate</b>\nразумно, рассудительно (loqui <i>Vlg</i>)."],
  ["sensatus", "<big>sensatus</big>\n\n<b>sensatus, a, um</b> [sensus]\nразумный, рассудительный <i>Vlg, Eccl</i>."],
  ["sensi", "<big>sensi</big>\n\n<b>sensi</b> <i>pf. к</i>\nsentio."],
  ["sensibilis", "<big>sensibilis</big>\n\n<b>sensibilis, e</b> [sentio]\n<b>1)</b> ощутительный, воспринимаемый, заметный (vox s. auditu(i) <i>Vtr</i>); чувственно воспринимаемый (pars intellegibilis, alia vero s. <i>Ap</i>);\n<b>2)</b> одарённый чувствами, чувствующий (s. et intellegens <i>Lact</i>)."],
  ["sensibiliter", "<big>sensibiliter</big>\n\n<b>sensibiliter</b>\nощутимо, заметно <i>Eccl</i>."],
  ["sensiculus", "<big>sensiculus</big>\n\n<b>sensiculus, i</b> <i>m</i> [<i>demin. к</i> sensus]\nкраткое изречение, небольшое положение <i>Q</i>."],
  ["sensifer", "<big>sensifer</big>\n\n<b>sensifer, fera, ferum</b> [sensus + fero]\nвызывающий ощущения (motus <i>Lcr</i>)."],
  ["sensificator", "<big>sensificator</big>\n\n<b>sensificator, oris</b> <i>m</i> [sensifico]\nвызывающий ощущения <i>Aug</i>."],
  ["sensifico", "<big>sensifico</big>\n\n<b>sensi–fico, —, —, are</b> [facio]\nодарять чувствами, одушевлять (carnem alicujus <i>Aug</i>)."],
  ["sensificus", "<big>sensificus</big>\n\n<b>sensi–ficus, a, um</b>\nдающий ощущения <i>Macr</i>."],
  ["sensilis", "<big>sensilis</big>\n\n<b>sensilis, e</b> [sentio]\nодарённый чувствами, чувствующий, чувствительный <i>Lcr</i>."],
  ["sensim", "<big>sensim</big>\n\n<b>sensim</b>\nмало-помалу, понемногу, постепенно, потихоньку, едва заметно (animos temptare <i>L</i>); исподволь (s. sine sensu aetas senescit <i>C</i>)."],
  ["sensorium", "<big>sensorium</big>\n\n<b>sensorium, i</b> <i>n</i>\nсенсорий, центральный орган чувств <i>Boet</i>."],
  ["sensti", "<big>sensti</big>\n\n<b>sensti</b> <i>Ter</i>\n= sensisti (<i>pf. к</i> sentio)."],
  ["sensualis", "<big>sensualis</big>\n\n<b>sensualis, e</b>\nчувственный <i>Eccl</i>."],
  ["sensualitas", "<big>sensualitas</big>\n\n<b>sensualitas, atis</b> <i>f</i>\nчувственность, способность чувствовать <i>Eccl</i>."],
  ["sensus", "<big>sensus</big>\n\n<b>I sensus, a, um</b> <i>part. pf. к</i>\nsentio.\n\n<b>II sensus, us</b> <i>m</i> [sentio]\n<b>1)</b> чувство, ощущение, восприятие, способность ощущения (in morte nullus s. est <i>C</i>): sensu expertia <i>QC</i> неодушевлённые вещи; s. videndi <i>C</i> (oculorum <i>Sen</i>) зрение, зрительное восприятие; s. doloris <i>C</i> чувство (ощущение) боли; s. alicujus rei <i>C, Sen etc.</i> восприятие чего-л.; res sensibus subjectae <i>C</i> чувственно воспринимаемые вещи;\n<b>2)</b> сознание, чувство (sine sensu jacere <i>C</i>);\n<b>3)</b> ум, рассудок, мышление (homo celer sensu <i>VP</i>): s. communis <i>H, Sen, Ph</i> здравый смысл (<i>ср.</i> 4); imi sensus <i>H</i> глубина сознания;\n<b>4)</b> понимание, суждение, вкус, такт (s. imperitorum <i>C</i>): s. communis <i>C</i> общепринятый взгляд (<i>ср.</i> 3);\n<b>5)</b> смысл, значение (verbi <i>O</i>); содержание (testamenti <i>H</i>);\n<b>6)</b> мысль, понятие, идея (sensibus celeber, verbis rudis <i>VP</i>);\n<b>7)</b> чувство, душевное состояние (s. amoris <i>или</i> amandi <i>C</i>);\n<b>8)</b> образ мыслей: eodem quo vos sensu sum <i>Nep</i> я ваш единомышленник;\n<b>9)</b> настроение (civium <i>C</i>);\n<b>10)</b> предложение, период (vehemens <i>Q</i>): verbo sensum cludere <i>Q</i> закончить предложение глаголом."],
  ["sententia", "<big>sententia</big>\n\n<b>sententia, ae</b> <i>f</i> [sentio]\n<b>1)</b> мнение, взгляд (sententiam mutare <i>C</i>): in sententia manere (permanere, perseverare) <i>C</i> оставаться при своём мнении; quot homines, tot sententiae <i>погов. C</i> сколько голов, столько умов; mea sententia <i>C</i> по-моему; de (ex) sententia alicujus <i>C</i> в соответствии с чьим-л. мнением <i>или</i> пожеланием; sententiam alicujus sequi <i>C</i> разделять чьё-л. мнение; ex animi mei sententia <i>C, Sl etc.</i> по моему глубокому убеждению <i>или</i> (<i>в клятвах</i>) клянусь совестью;\n<b>2)</b> решение, намерение, воля: stat (<i>или</i> est) s. Hannibali <i>L</i> Ганнибал решил; ex animi sententia <i>C</i> в соответствии с желанием, как того хотелось (хочется); omnia essent ex sententia, si... <i>C</i> всё обстояло бы как нельзя лучше, если бы...;\n<b>3)</b> образ мыслей (Aristippi <i>C</i>);\n<b>4)</b> (<i>в голосовании</i>) голос: sententiam dicere <i>C</i> (ferre, dare <i>L</i>) подавать голос, голосовать; in sententiam alicujus discedere <i>или</i> (pedibus) ire <i>L</i> присоединиться к чьему-л. мнению; sententiam rogare <i>C</i> поставить вопрос на голосование; manifesta causa secum habet sententiam <i>PS</i> ясное дело говорит само за себя;\n<b>5)</b> голос судьи, суждение, приговор (omnibus sententiis absolvi <i>C</i>);\n<b>6)</b> смысл, значение (verbi <i>C</i>); содержание, мысль (legis <i>C</i>); in hanc sententiam <i>C, L</i> в следующем смысле, следующим образом (loqui <i>C</i>);\n<b>7)</b> изречение, сентенция (gravis <i>C</i>); положение, мысль (philosophorum <i>C</i>);\n<b>8)</b> (пустая) фраза (sententiarum strepitus <i>Pt</i>)."],
  ["sententialis", "<big>sententialis</big>\n\n<b>sententialis, e</b>\nсентенциозный, назидательный <i>Eccl</i>."],
  ["sententialiter", "<big>sententialiter</big>\n\n<b>sententialiter</b>\nв форме сентенции <i>Macr, Tert</i>."],
  ["sententiola", "<big>sententiola</big>\n\n<b>sententiola, ae</b> <i>f</i> [<i>demin. к</i> sententia]\nнебольшое изречение <i>C, Q</i>."],
  ["sententiose", "<big>sententiose</big>\n\n<b>sententiose</b> [sententiosus]\nглубокомысленно, содержательно, сентенциозно (dicere <i>Q</i>).:"],
  ["sententiosus", "<big>sententiosus</big>\n\n<b>sententiosus, a, um</b> [sententia]\nбогатый мыслями, глубокомысленный, содержательный (s. et argutus <i>C</i>)."],
  ["senticetum", "<big>senticetum</big>\n\n<b>senticetum, i</b> <i>n</i> [sentis]\nтерновый кустарник, заросли терновника <i>Pl, Ap</i>."],
  ["senticosus", "<big>senticosus</big>\n\n<b>senticosus, a, um</b> [sentis]\nпоросший тернием, тернистый (terra <i>Julianus ap. Aug</i>); <i>перен.</i> колючий, запутанный (merces Protagorae <i>Ap</i>)."],
  ["sentina", "<big>sentina</big>\n\n<b>sentina, ae</b> <i>f</i>\n<b>1)</b> нижняя часть корабля для стока нечистот (s. navium <i>PM</i>); сток нечистот, клоака (Romam sicuti in sentinam confluere <i>Sl</i>);\n<b>2)</b> нечистоты <i>или</i> трюмная вода (sentinam exhaurire <i>C</i>);\n<b>3)</b> подонки (s. urbis, rei publicae <i>C</i>)."],
  ["Sentinas", "<big>Sentinas</big>\n\n<b>Sentinas, atis</b> [Sentinum]\n<b>1.</b> <i>adj.</i> сентинский (ager <i>L</i>);\n<b>2.</b> <i>subst. m</i> житель города Sentinum <i>PM</i>."],
  ["sentino", "<big>sentino</big>\n\n<b>sentino, —, —, are</b> [sentina]\n<b>1)</b> вычерпывать трюмную воду (navis sentinatur <i>Aug</i>);\n<b>2)</b> <i>перен.</i> быть в беде <i>Caec, Aug</i>."],
  ["sentinosus", "<big>sentinosus</big>\n\n<b>sentinosus, a, um</b> [sentina]\nполный трюмной воды, зловонный (navis <i>Cato</i>)."],
  ["Sentinum", "<big>Sentinum</big>\n\n<b>Sentinum, i</b> <i>n</i>\nСентин, <i>город в Умбрии на реке Aesis; здесь в 295 г. до н. э. римляне нанесли поражение самнитам и погиб П. Деций Мус</i> (<i>см.</i> Sentinas)."],
  ["sentio", "<big>sentio</big>\n\n<b>sentio, sensi, sensum, ire</b>\n<b>1)</b> чувствовать, ощущать (dolorem <i>Lcr</i>; famem <i>L, QC</i>): s. aliquem O = concumbere cum aliquo || воспринимать (sonitum <i>Pl</i>; varios odores <i>Lcr</i>); замечать (aliquem perterritum <i>L</i>; vacuos sine remige portus <i>V</i>);\n<b>2)</b> узнать, изведать, испытать (damna ac detrimenta <i>C</i>); quid quidem senserim <i>C</i> насколько я мог заметить;\n<b>3)</b> думать, полагать, считать (quid sentis? <i>C</i>): de aliqua re prava s. <i>C</i> иметь о чём-л. превратное представление; humiliter s. <i>C</i> быть низменного образа мыслей; ab aliquo <i>Pl</i> <i>и</i> cum aliquo s. <i>C</i> быть чьего-л. мнения, стоять на чьей-л. стороне; <i>но</i>: ab aliquo seorsum s. <i>Pl</i> расходиться в мнении с кем-л.; cum aliquo vera s. <i>C</i> разделять чьи-л. правильные взгляды; neglegere quid de se quisque sentiat <i>C</i> не считаться с тем, что о тебе думают другие; plus, quam aliquis, de aliqua re s. <i>Cs</i> смыслить в чём-л. больше кого-л.; aliquem bonum civem s. <i>C</i> считать кого-л. благонамеренным гражданином; non ut dictum, sed ut sensum est <i>C</i> (судить) не по словам, а по смыслу; contra (adversus <i>Nep</i>) aliquem s. <i>C</i> враждебно относиться к кому-л. <i>или</i> строить козни кому-л.;\n<b>4)</b> быть восприимчивым (s. morbos <i>PM</i>; s. pestilentem Africum <i>H</i>): urbis annona sensit desidem Aegyptum <i>PJ</i> на продовольственном снабжении города (Рима) тяжело отразился неурожай в Египте;\n<b>5)</b> выражать своё мнение, высказываться (libere in senatu <i>L</i>). — <i>См. тж.</i> sensa."],
  ["sentis", "<big>sentis</big>\n\n<b>sentis, is</b> <i>m</i> <i>преим.</i> <i>pl.</i>\nтерновник, терновый куст <i>Pl, Cs, V etc.</i>; <i>ирон.</i> руки с шипами, <i>т. е.</i> вороватые <i>Pl</i>."],
  ["sentisco", "<big>sentisco</big>\n\n<b>sentisco, —, —, ere</b> [<i>inchoat. к</i> sentio]\nощущать, воспринимать, замечать <i>Lcr</i>."],
  ["sentix", "<big>sentix</big>\n\n<b>sentix, icis</b> <i>m</i> [sentis] <i>бот.</i>\nшиповник <i>Ap</i>."],
  ["sentosus", "<big>sentosus</big>\n\n<b>sentosus</b> <i>и</i> <b>sentuosus, a, um</b> [sentis]\nтернистый (via <i>Eccl</i>)."],
  ["sentus", "<big>sentus</big>\n\n<b>sentus, a, um</b> [sentis]\n<b>1)</b> тернистый, колючий (vepres <i>Eccl</i>);\n<b>2)</b> заросший тернием, <i>т. е.</i> дикий (loca <i>V, O</i>);\n<b>3)</b> взъерошенный, лохматый (homo <i>Ter</i>)."],
  ["senui", "<big>senui</big>\n\n<b>senui</b> <i>pf. к</i>\nsenesco."],
  ["senus", "<big>senus</big>\n\n<b>senus, a, um</b> <i>adj. num. distr.</i> (<i>преим.</i> <i>pl.</i>, <i>см.</i> seni)\nшестерной; <i>тк. в сочетании с bis</i>: bis s. <i>SenT, St</i> двенадцатикратный."],
  ["seorsum", "<big>seorsum</big>\n\n<b>seorsum</b> <i>adv.</i> [seorsus I]\n<b>1)</b> отдельно, в отдельности, в частности (omnibus gratiam habeo et s. tibi <i>Ter</i>);\n<b>2)</b> далеко, в стороне, без (s. ab aliquo aliquid facere <i>Sl etc.</i>): abs te s. cogito <i>Pl</i> я думаю иначе, чем ты."],
  ["seorsus", "<big>seorsus</big>\n\n<b>I seorsus, a, um</b> [<i>из</i> sevorsus <i>от</i> se vertere]\nотдельный (vocabulum <i>Cato</i>).\n\n<b>II seorsus</b> <i>adv.</i>\n= seorsum."],
  ["separ", "<big>separ</big>\n\n<b>se–par, paris</b> (<i>встреч. тк. в</i> <i>abl. sg.</i> e <i>или</i> i) <i>adj.</i>\n<b>1)</b> отдельный (ossa separe urna contegere <i>VF</i>);\n<b>2)</b> отличный, различный (duo flumina natura separi <i>Sol</i>)."],
  ["separabilis", "<big>separabilis</big>\n\n<b>separabilis, e</b> [separo]\nотделимый (ab aliqua re <i>C</i>)."],
  ["separabiliter", "<big>separabiliter</big>\n\n<b>separabiliter</b>\nотдельно, обособленно <i>Aug</i>."],
  ["separate", "<big>separate</big>\n\n<b>separate</b> [separatus]\nотдельно, порознь <i>C</i>."],
  ["separatim", "<big>separatim</big>\n\n<b>separatim</b> [separo]\n<b>1)</b> отдельно, порознь (versum s. et divise legere <i>AG</i>);\n<b>2)</b> в общем, вообще, отвлечённо (s. dicere de aliqua re <i>C</i>)."],
  ["separatio", "<big>separatio</big>\n\n<b>separatio, onis</b> <i>f</i> [separo]\n<b>1)</b> отделение, разделение (distributio partium ac s. <i>C</i>);\n<b>2)</b> различение, различное употребление (nominum idem significantium <i>Q</i>)."],
  ["separativus", "<big>separativus</big>\n\n<b>separativus, a, um</b> <i>грам.</i>\nразделительный (conjunctio, praepositio)."],
  ["separator", "<big>separator</big>\n\n<b>separator, oris</b> <i>m</i>\nразделяющий, разобщающий <i>Tert, Aug</i>."],
  ["separatrix", "<big>separatrix</big>\n\n<b>separatrix, icis</b> <i>f</i>\nразделяющая, разъединяющая (voluntas conjunctrix, ac s. hujuscemodi rerum <i>Aug</i>)."],
  ["separatus", "<big>separatus</big>\n\n<b>I separatus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> separo;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> отдельный, особый (volumen <i>C</i>);\n<b>2)</b> отдалённый, уединённый (juga <i>H</i>);\n<b>3)</b> разный: separatis temporibus <i>Nep</i> в разные времена.\n\n<b>II separatus, us</b> <i>m</i>\nотделение, разделение <i>Ap</i>."],
  ["separo", "<big>separo</big>\n\n<b>se–paro, avi, atum, are</b>\n<b>1)</b> отделять, разделять, разобщать, обособлять (aliquos a cetero exercitu <i>QC</i>; equites ex omni populi summa <i>C</i>; Europam Asiamque fretum separat <i>PM</i>): suum consilium ab reliquis s. <i>Cs</i> отделять свой план от прочих, <i>т. е.</i> действовать по собственному плану; separata utilitate <i>C</i> независимо от пользы (не говоря уже о полезности);\n<b>2)</b> исключать (aliquem ab amicis <i>C</i>);\n<b>3)</b> различать, отличать: orator, quem a bono viro non separo <i>Q</i> оратор, который в моих глазах является человеком порядочным."],
  ["sepelibilis", "<big>sepelibilis</big>\n\n<b>sepelibilis, e</b> [sepelio]\nподдающийся сокрытию, могущий быть скрытым (stultitia <i>Pl</i>)."],
  ["sepelio", "<big>sepelio</big>\n\n<b>sepelio, pelivi (petii), pultum, ire</b>\n<b>1)</b> хоронить, погребать (aliquem <i>C etc.</i>); закапывать (ossa <i>O</i>); <i>реже</i> (<i>о трупах</i>) сжигать <i>L, Su</i>;\n<b>2)</b> губить, подрывать (tribuniciam potestatem <i>L</i>); уничтожать (aliquid in aeternum <i>Lcr</i>): sepultus sum <i>Ter</i> я пропал || подавлять, прекращать (bellum <i>C</i>); прогонять (somnum <i>Pl</i>);\n<b>3)</b> погружать: sepultus somno vinoque <i>V</i> объятый пьяным сном; sepeliri morbo <i>Ap</i> быть сломленным болезнью;\n<b>4)</b> погружать в сон, усыплять (custos sepultus <i>V</i>)."],
  ["sepes", "<big>sepes</big>\n\n<b>I sepes, pedis</b> <i>adj.</i> [sex + pes]\nшестиногий (formica <i>Ap</i>).\n\n<b>II sepes, is</b> <i>f</i> <i>v. l.</i>\n= saepes."],
  ["sephyrus", "<big>sephyrus</big>\n\n<b>sephyrus, i</b> <i>m</i> <i>v. l.</i>\n= zephyrus."],
  ["sepia", "<big>sepia</big>\n\n<b>sepia, ae</b> <i>f</i> (<i>греч.</i>)\n<b>1)</b> <i>зоол.</i> сепия, каракатица <i>C, PM</i>;\n<b>2)</b> чернила <i>Pers</i>."],
  ["sepicula", "<big>sepicula</big>\n\n<b>sepicula, ae</b> <i>f</i> <i>v. l.</i>\n= saepicula."],
  ["sepim-", "<big>sepim-</big>\n\n<b>sepim-</b> <i>v. l.</i>\n= saepim-."],
  ["sepio", "<big>sepio</big>\n\n<b>sepio</b> <i>v. l.</i>\n= saepio."],
  ["sepiola", "<big>sepiola</big>\n\n<b>sepiola, ae</b> <i>f</i> [<i>demin. к</i> sepia]\nмаленькая сепия <i>Pl</i>."],
  ["sepioticon", "<big>sepioticon</big>\n\n<b>sepioticon (-um), i</b> <i>n</i> (<i>греч.</i>; <i>лат.</i> atramentum)\nчернила <i>Eccl</i>."],
  ["sepis", "<big>sepis</big>\n\n<b>sepis</b> <i>gen. к</i>\nsepes II <i>и</i> seps."],
  ["Seplasia", "<big>Seplasia</big>\n\n<b>Seplasia, ae</b> <i>f</i> <i>и</i> <b>Seplasia, orum</b> <i>n</i>\nСепласия, <i>улица в Капуе, на которой продавались благовония</i> <i>C, VM, PM etc.</i>"],
  ["seplasiarius", "<big>seplasiarius</big>\n\n<b>seplasiarius, i</b> <i>m</i>\nторговец благовониями, парфюмер <i>Lampr</i>."],
  ["seplasium", "<big>seplasium</big>\n\n<b>seplasium, i</b> <i>n</i> [Seplasia]\nсеплазий, <i>род благовония</i> <i>Pt</i>."],
  ["sepono", "<big>sepono</big>\n\n<b>se–pono, posui, positum, ere</b>\n<b>1)</b> класть в сторону, откладывать (s. et recondere aliquid <i>C</i>); резервировать, выделять (pecuniam in <i>и</i> ad aliquid <i>L, Su</i>);\n<b>2)</b> предназначать (sibi tempus ad aliquid <i>C</i>); отводить (locum alicui rei <i>T</i>); завещать (vestem alicui <i>Ph</i>); отделять (aliquid ab aliqua re <i>C</i>); отбирать (unam de mille sagittis <i>O</i>);\n<b>3)</b> различать, отличать (aliquid aliqua re <i>H</i>);\n<b>4)</b> удалять, отстранять (aliquem <i>Su</i>); изгонять (aliquem a domo sua <i>T</i>); ссылать (aliquem in civitatem Massiliensem <i>T</i>); ставить вне, изымать (aliquid extra certamen s. <i>L</i>); отгонять (graves curas <i>O</i>). — <i>См. тж.</i> sepositus."],
  ["sepositio", "<big>sepositio</big>\n\n<b>sepositio, onis</b> <i>f</i> [sepono]\n<b>1)</b> откладывание, выделение (pecuniae <i>Veg</i>);\n<b>2)</b> <i>Dig</i> = separatio."],
  ["sepositus", "<big>sepositus</big>\n\n<b>sepositus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> sepono;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> изысканный (vestis <i>T</i>); отборный (grex <i>M</i>);\n<b>2)</b> отдалённый, дальний, далёкий (gens <i>M</i>; mare <i>SenT</i>)."],
  ["sepostus", "<big>sepostus</big>\n\n<b>sepostus</b> <i>Sil</i>\n= sepositus."],
  ["seps", "<big>seps</big>\n\n<b>I seps, sepis</b> (<i>acc.</i> sepa) <i>m, f</i> (<i>греч.</i>)\ncene:\n<b>1)</b> <i>род ядовитой ящерицы или змеи</i> <i>Lcn, PM</i>;\n<b>2)</b> <i>род насекомого</i> (<i>предпол.</i> сороконожка <i>или</i> мокрица) <i>PM</i>.\n\n<b>II seps, sepis</b> <i>f</i> <i>v. l.</i>\n= saepes."],
  ["sepse", "<big>sepse</big>\n\n<b>sepse</b> (= se ipse)\nсамого себя <i>C, Sen</i>."],
  ["sepsi", "<big>sepsi</big>\n\n<b>sepsi</b> <i>pf. к</i>\nsepio (= saepio)."],
  ["septa", "<big>septa</big>\n\n<b>septa</b> <i>v. l.</i>\n= saepta."],
  ["septage", "<big>septage</big>\n\n<b>septage</b> <i>PM</i>\n(<i>v. l.</i>) = siptace."],
  ["septas", "<big>septas</big>\n\n<b>septas, adis</b> <i>f</i> (<i>греч.</i> heptas)\nседмерица, семёрка <i>Macr</i>."],
  ["septem", "<big>septem</big>\n\n<b>septem</b> <i>num. card.</i>\nсемь: s. (sapientes) <i>C</i> семь греческих мудрецов; s. stellae <i>SenT</i> = septentriones; s. miracula <i>PM, VM</i> (s. mira <i>Lact</i> <i>и</i> s. spectacula <i>Vtr, AG</i>) семь чудес мира."],
  ["September", "<big>September</big>\n\n<b>I September, bris, bre</b> [septem]\nсентябрьский (Calendae <i>C</i>).\n\n<b>II September, bris</b> <i>m</i> (<i>sc.</i> mensis)\nсентябрь: Septembribus horis <i>H</i> в сентябре, ранней осенью."],
  ["septemcordis", "<big>septemcordis</big>\n\n<b>septem–cordis, e</b> [corda 1]\nсемиструнный (cithara <i>Is</i>)."],
  ["septemdecim", "<big>septemdecim</big>\n\n<b>septemdecim</b> <i>num. card.</i> <i>v. l.</i>\n= septendecim."],
  ["septemfluus", "<big>septemfluus</big>\n\n<b>septem–fluus, a, um</b>\nраспадающийся на семь рукавов (Nilus <i>O</i>)."],
  ["septemgeminus", "<big>septemgeminus</big>\n\n<b>septem–geminus, a, um</b>\nсемерной: s. Nilus <i>Ctl, V</i> семирукавный Нил; septemgemina Roma <i>St</i> семихолмный Рим."],
  ["septemmestris", "<big>septemmestris</big>\n\n<b>septem–mestris, e</b> [mensis]\nсемимесячный (infans <i>Cens</i>)."],
  ["septempedalis", "<big>septempedalis</big>\n\n<b>septempedalis, e</b> [septem + pes]\nсемифутовый (statua <i>Pt</i>)."],
  ["septemplex", "<big>septemplex</big>\n\n<b>septemplex, plicis</b> (<i>abl.</i> e <i>или</i> i) <i>adj.</i> [septem + plico]\nсемерной: s. Nilus <i>V</i> Нил с семью устьями; s. clipeus <i>V</i> семислойный щит (сшитый из семи кож)."],
  ["septempliciter", "<big>septempliciter</big>\n\n<b>septempliciter</b>\nсемикратно <i>Vlg, Aug</i>."],
  ["septemtr-", "<big>septemtr-</big>\n\n<b>septemtr-</b> <i>v. l.</i>\n= septentr-."],
  ["septemvir", "<big>septemvir</big>\n\n<b>septem–vir, viri</b> (<i>gen. pl.</i> orum <i>и</i> um) <i>m</i>\nсептемвир, член коллегии (комиссии) семи <i>C</i>."],
  ["septemvirales", "<big>septemvirales</big>\n\n<b>septemvirales, ium</b> <i>m</i> <i>C</i>\n= septemviri."],
  ["septemviralis", "<big>septemviralis</big>\n\n<b>septemviralis, e</b> [septemvir]\nотносящийся к (принадлежащий) септемвиру (auctoritas <i>C</i>)."],
  ["septemviratus", "<big>septemviratus</big>\n\n<b>septemviratus, us</b> <i>m</i> [septemvir]\nсептемвират, звание <i>или</i> должность септемвира <i>C, PJ</i>."],
  ["Septemzodium", "<big>Septemzodium</big>\n\n<b>Septemzodium, i</b> <i>n</i> <i>Amm</i>\n= Septizonium."],
  ["septenarius", "<big>septenarius</big>\n\n<b>septenarius, a, um</b> [septem]\nсостоящий из семи, семеричный (numerus <i>PM</i>): (versus) s. <i>C</i> септенарий, семистопный стих."],
  ["septendecim", "<big>septendecim</big>\n\n<b>septendecim</b> <i>adj. num. card.</i> [septem + decem]\nсемнадцать <i>C, T etc.</i>"],
  ["septeni", "<big>septeni</big>\n\n<b>septeni, ae, a</b> (<i>gen. pl.</i> um) <i>num. distr.</i>\n<b>1)</b> по семи: pueri annorum senum septenumque denum <i>L</i> мальчики 16-17 лет;\n<b>2)</b> семь (septena fila lyrae <i>O</i>)."],
  ["septennis", "<big>septennis</big>\n\n<b>septennis, e</b> <i>Capit, Lact</i>\n= septuennis."],
  ["septennium", "<big>septennium</big>\n\n<b>septennium, i</b> <i>n</i> [septem + annus]\nсемилетие <i>VP, Eutr, Eccl</i>."],
  ["septentrio", "<big>septentrio</big>\n\n<b>septentrio, onis</b> <i>m</i> (<i>преим.</i> <i>pl.</i> septentriones) [septem + trio]\n<b>1)</b> семизвездие: s. major <i>Vtr</i> Большая Медведица; s. minor <i>C</i> Малая Медведица; axis septentrionis <i>QC</i> северный полюс;\n<b>2)</b> север (ad septentriones vergere <i>QC</i>; ad septentriones jacere <i>Vr</i> <i>или</i> spectare <i>Vtr</i>; sub septentrionibus positum esse <i>Cs</i>);\n<b>3)</b> северный ветер (acer s. ortus est <i>L</i>);\n<b>4)</b> северный полюс <i>PM</i>."],
  ["septentrionalis", "<big>septentrionalis</big>\n\n<b>septentrionalis, e</b> [septentrio]\nсеверный (gentes <i>Vtr</i>): septentrionalia, ium <i>n</i> северные края <i>или</i> области <i>PM, T</i>."],
  ["septentrionarius", "<big>septentrionarius</big>\n\n<b>septentrionarius, a, um</b> [septentrio]\nсеверный (ventus <i>AG</i>)."],
  ["septenus", "<big>septenus</big>\n\n<b>septenus, a, um</b> <i>num. distr.</i> [septem]\nсемерной: septena fistula <i>Calp</i> семиствольная цевница; s. Ister <i>St</i> Истр с семью устьями."],
  ["Septicianus", "<big>Septicianus</big>\n\n<b>Septicianus, a, um</b>\nсептициев (<i>предпол. о некоем ювелире Септиции, уменьшившем пробу серебряных изделий</i>): Septiciana libra <i>M</i> фунт весом в 8,5 унций (<i>вместо 12</i>)."],
  ["septicollis", "<big>septicollis</big>\n\n<b>septi–collis, e</b>\nсемихолмный: s. arx <i>Eccl</i> = Roma."],
  ["septicus", "<big>septicus</big>\n\n<b>septicus, a, um</b>\nвызывающий нагноение, гнилостный (vis <i>PM</i>)."],
  ["septies", "<big>septies</big>\n\n<b>septies (septiens)</b> <i>adv. num.</i> [septem]\n<b>1)</b> семь раз, семижды: s. milliens (sestertium) <i>C</i> 700 миллионов сестерциев;\n<b>2)</b> в седьмой раз (s. consul <i>Spart</i>)."],
  ["septifluus", "<big>septifluus</big>\n\n<b>septifluus, a, um</b> [septem + fluo]\nтекущий семью струями, <i>т. е.</i> обитающий по берегам семи рек (Lydus <i>Pt</i>)."],
  ["septiforis", "<big>septiforis</big>\n\n<b>septi–foris, e</b>\nимеющий семь отверстий (fistula <i>Sid</i>)."],
  ["septiformis", "<big>septiformis</big>\n\n<b>septi–formis, e</b> [forma]\nсемиобразный, имеющий семь видов (gratia spiritus sancti <i>Aug</i>)."],
  ["septimana", "<big>septimana</big>\n\n<b>septimana, ae</b> <i>f</i>\n<b>1)</b> седмица, неделя <i>CTh, Vlg, Eccl</i>;\n<b>2)</b> семилетний возрастной период <i>Macr</i>."],
  ["septimani", "<big>septimani</big>\n\n<b>septimani, orum</b> <i>m</i> [septimus]\nсолдаты седьмого легиона <i>PM, T</i>."],
  ["septimanus", "<big>septimanus</big>\n\n<b>septimanus, a, um</b> [septimus]\nотносящийся к седьмому: Nonae septimanae <i>Vr</i> ноны, приходившиеся на седьмой день месяца (<i>в марте, мае, июле и октябре</i>) || семимесячный (fetura <i>Eccl</i>)."],
  ["Septimatrus", "<big>Septimatrus</big>\n\n<b>Septimatrus, uum</b> <i>f</i>\nсептиматры, <i>празднества в 7-й день после Idus</i> <i>Vr</i>."],
  ["Septimius", "<big>Septimius</big>\n\n<b>Septimius, a, um</b>\nСептимий, <i>римск. nomen; наиболее известны</i>:\n<b>1)</b> C. S., <i>авгур</i> <i>C</i>;\n<b>2)</b> Titius S., <i>поэт, друг Горация</i> <i>H</i>;\n<b>3)</b> S. Severus — <i>см.</i> Severus."],
  ["septimo", "<big>septimo</big>\n\n<b>septimo</b> <i>adv.</i> [septimus]\nв седьмой раз <i>C, AG, Treb etc.</i>"],
  ["septimontialis", "<big>septimontialis</big>\n\n<b>septimontialis, e</b>\nсвязанный с праздником Septimontium (sacrum <i>Su</i>)."],
  ["Septimontium", "<big>Septimontium</big>\n\n<b>Septi–montium, i</b> <i>n</i> [mons]\n<b>1)</b> Семихолмие, <i>т. е. основная территория Рима</i> <i>Vr</i>;\n<b>2)</b> праздник Семихолмия, <i>справлявшийся в Риме 11 декабря в память включения семи холмов в городскую черту Рима</i> <i>Vr, Tert</i>."],
  ["septimum", "<big>septimum</big>\n\n<b>septimum</b> <i>adv.</i> [septimus]\nв седьмой раз <i>C</i>."],
  ["septimus", "<big>septimus</big>\n\n<b>septimus, a, um</b> <i>adj. num. ord.</i> [septem]\nседьмой (annus <i>T</i>): s. casus <i>Q</i> = ablativus."],
  ["septingenarius", "<big>septingenarius</big>\n\n<b>septingenarius, a, um</b> [septingeni]\nсемисотенный, состоящий из семисот (grex <i>Vr</i>)."],
  ["septingeni", "<big>septingeni</big>\n\n<b>septingeni, ae, a</b> <i>num. distr.</i> [septingenti]\nпо семисот <i>PM</i>."],
  ["septingenteni", "<big>septingenteni</big>\n\n<b>septingenteni, ae, a</b> <i>Vlg</i>\n= septingeni."],
  ["septingentesimus", "<big>septingentesimus</big>\n\n<b>septingentesimus, a, um</b> <i>adj. num. ord.</i> [septingenti]\nсемисотый (annus <i>L</i>)."],
  ["septingenti", "<big>septingenti</big>\n\n<b>septingenti, ae, a</b> <i>adj. num. card.</i> [septem + centum]\nсемьсот <i>L, M etc.</i>"],
  ["septingenties", "<big>septingenties</big>\n\n<b>septingenties</b> <i>adv. num.</i> [septingenti]\nсемьсот раз <i>PM</i>."],
  ["septinuba", "<big>septinuba</big>\n\n<b>septi–nuba, ae</b> <i>f</i> (<i>sc.</i> mulier)\nсемь раз выходившая замуж <i>Julianus ap. Aug</i>."],
  ["septio", "<big>septio</big>\n\n<b>septio</b> <i>v. l.</i>\n= saeptio."],
  ["septiremis", "<big>septiremis</big>\n\n<b>septiremis, e</b> [septem + remus]\nс семью рядами вёсел (navis <i>QC</i>)."],
  ["Septizonium", "<big>Septizonium</big>\n\n<b>Septizonium, i</b> <i>n</i> [septem + zona]\nСептизоний, <i>название двух высоких зданий в Риме</i>:\n<b>1)</b> <i>построенного при императоре Тите в южн. части Рима</i> <i>Su</i>;\n<b>2)</b> <i>построенного при Септимий Севере у юго-вост. оконечности Circus Maximus</i> (<i>от первого из них следов не сохранилось, и некоторые отождествляют его со вторым</i>) <i>Spart</i>."],
  ["septuagenarius", "<big>septuagenarius</big>\n\n<b>septuagenarius, a, um</b> [septuageni]\n<b>1)</b> семидесятилетний (aetas <i>Eutr</i>; homo <i>Dig</i>);\n<b>2)</b> имеющий 70 дюймов в диаметре, (fistula <i>Frontin</i>)."],
  ["septuageni", "<big>septuageni</big>\n\n<b>septuageni, ae, a</b> <i>num. distr.</i> [septuaginta]\n<b>1)</b> по семидесяти <i>PM</i>;\n<b>2)</b> семьдесят <i>Frontin, CTh</i>."],
  ["septuagensumus", "<big>septuagensumus</big>\n\n<b>septuagensumus</b> <i>MA</i>\n= septuagesimus."],
  ["septuagesimus", "<big>septuagesimus</big>\n\n<b>septuagesimus, a, um</b> <i>adj. num. ord.</i> [septuaginta]\nсемидесятый <i>C, L etc.</i>"],
  ["septuagies", "<big>septuagies</big>\n\n<b>septuagies</b> <i>adv. num.</i>\nсемьдесят раз <i>Col</i>."],
  ["septuaginta", "<big>septuaginta</big>\n\n<b>septuaginta</b> <i>adj. num. card.</i>\nсемьдесят <i>C, L, Nep etc.</i>: S. (interpretes) <i>Eccl</i> 70 толковников (<i>которым, по преданию, приписывался первый перевод Библии с еврейского языка на греческий</i>)."],
  ["septuennis", "<big>septuennis</big>\n\n<b>septuennis, e</b> [septem + annus]\nсемилетний (puer <i>Pl</i>)."],
  ["septum", "<big>septum</big>\n\n<b>septum, i</b> <i>n</i> <i>v. l.</i>\n= saeptum."],
  ["septuman-", "<big>septuman-</big>\n\n<b>septuman-</b> <i>v. l.</i>\n= septiman-."],
  ["septumus", "<big>septumus</big>\n\n<b>septumus, a, um</b> <i>v. l.</i>\n= septimus."],
  ["septunx", "<big>septunx</big>\n\n<b>septunx, uncis</b> <i>m</i> [septem + uncia]\n<b>1)</b> семь унций, <i>т. е.</i> семь двенадцатых асса= 191,02 <i>г</i> <i>L</i>;\n<b>2)</b> семь двенадцатых (jugeri <i>Col</i>);\n<b>3)</b> семь штук: septunce perditus <i>M</i> опьяневший от семи (чаш вина)."],
  ["septuose", "<big>septuose</big>\n\n<b>septuose</b> <i>v. l.</i>\n= saeptuose."],
  ["septuplum", "<big>septuplum</big>\n\n<b>I septuplum, i</b> <i>n</i>\nнечто всемеро большее <i>Vlg, Eccl</i>.\n\n<b>II septuplum</b> <i>adv.</i>\nвсемеро, семикратно <i>Vlg, Eccl</i>."],
  ["septuplus", "<big>septuplus</big>\n\n<b>septuplus, a, um</b>\nсемикратный (retributio <i>Aug</i>)."],
  ["septus", "<big>septus</big>\n\n<b>septus</b> <i>v. l.</i>\n= saeptus (<i>от</i> saepio)."],
  ["sepulch-", "<big>sepulch-</big>\n\n<b>sepulch-</b> <i>v. l.</i>\n= sepulc-."],
  ["sepulcralis", "<big>sepulcralis</big>\n\n<b>sepulcralis, e</b> [sepulcrum]\nмогильный (arae <i>O</i>); погребальный (fax <i>O</i>)."],
  ["sepulcretum", "<big>sepulcretum</big>\n\n<b>sepulcretum, i</b> <i>n</i> [sepulcrum]\nместо погребения, кладбище <i>Ctl</i>."],
  ["sepulcrum", "<big>sepulcrum</big>\n\n<b>sepulcrum, i</b> <i>n</i> [sepelio]\n<b>1)</b> могила, гробница, могильный холм (monumentum sepulcri <i>C, Nep</i>);\n<b>2)</b> надгробный камень, надгробный памятник (s. exstruere <i>H</i> <i>и</i> facere <i>C</i>); надгробная надпись, надгробие (legere sepulcra <i>C</i>): ara sepulcri <i>V</i> погребальный костёр; s. inane <i>V</i> кенотаф (гробница без тела);\n<b>3)</b> погребение (leges de sepulcris <i>C</i>);\n<b>4)</b> умерший, покойник <i>Pl</i>; <i>pl.</i> тени усопших (placare sepulcra <i>O</i>)."],
  ["sepultor", "<big>sepultor</big>\n\n<b>sepultor, oris</b> <i>m</i> [sepelio]\n<b>1)</b> совершающий погребение (corporis <i>Aug</i>);\n<b>2)</b> умиротворитель (civilium turbinum <i>Tert</i>)."],
  ["sepultura", "<big>sepultura</big>\n\n<b>sepultura, ae</b> <i>f</i> [sepelio]\n<b>1)</b> погребение, похороны: sepultura aliquem afficere <i>и</i> corpus alicujus ad sepulturam dare <i>C</i> похоронить кого-л.;\n<b>2)</b> предание тела огню, кремация <i>T, Just</i>."],
  ["sepultus", "<big>sepultus</big>\n\n<b>sepultus, a, um</b> <i>part. pf. к</i>\nsepelio."],
  ["sequacitas", "<big>sequacitas</big>\n\n<b>sequacitas, atis</b> <i>f</i> [sequax]\nсклонность следовать (кому-л.), податливость <i>Sid</i>."],
  ["Sequana", "<big>Sequana</big>\n\n<b>Sequana, ae</b> <i>m</i>\nСеквана, <i>река в Галлии, прибл. совпадавшая с границей между племенами бельгов и кельтов</i> (<i>ныне</i> Seine) <i>Cs, PM</i>."],
  ["Sequani", "<big>Sequani</big>\n\n<b>Sequani, orum</b> <i>m</i>\nсекваны, <i>одно из крупнейших племён в Gallia Belgica, no правому берегу Родана, отделённое от гельветов Юрским хребтом</i> (<i>главн. город Vesontio</i>) <i>Cs, C</i>."],
  ["Sequanus", "<big>Sequanus</big>\n\n<b>Sequanus</b> <i>и</i> <b>Sequanicus, a, um</b> [Sequana]\nсекванский <i>PM, Lcn, M</i>."],
  ["sequax", "<big>sequax</big>\n\n<b>I sequax, acis</b> <i>adj.</i> [sequor]\n<b>1)</b> послушный, легко повинующийся (equus <i>O</i>);\n<b>2)</b> гоняющийся, тянущийся (за кормом) (capreae <i>V</i>); быстро следующий (за другим), теснящийся (undae <i>V</i>); подвижный, быстро распространяющийся (flamma <i>V</i>); всюду проникающий (fumus <i>V</i>); широко разрастающийся (hederae <i>Pers</i>);\n<b>3)</b> неотступно следующий (curae <i>Lcr</i>); преследующий по пятам (Latium = Latini <i>V</i>); легко усваиваемый (mores <i>Lcr</i>);\n<b>4)</b> гибкий, податливый (materia, homo <i>PM</i>).\n\n<b>II sequax, acis</b> <i>m</i>\nпоследователь, приверженец, поклонник (alicujus <i>Man, Eccl</i>)."],
  ["sequela", "<big>sequela</big>\n\n<b>sequela (sequella), ae</b> <i>f</i> [sequor]\n<b>1)</b> сопровождение, провожатый <i>Frontin</i>;\n<b>2)</b> следствие, результат <i>AG, Lad</i>."],
  ["sequens", "<big>sequens</big>\n\n<b>sequens, entis</b>\n<b>1.</b> <i>part. praes. к</i> sequor;\n<b>2.</b> <i>subst. n ритор.</i> постоянное определение, эпитет <i>Q</i>."],
  ["sequester", "<big>sequester</big>\n\n<b>I sequester, tris, tre</b> <i>и</i> <b>sequester, tra, trum</b> <i>adj.</i>\nслужащий посредником, посредствующий <i>St</i>: pace sequestra <i>V</i> с помощью (под охраной) мира.\n\n<b>II sequester, tris</b> <i>и</i> <b>tri</b> <i>m</i>\n<b>1)</b> посредник (inter patres ac plebem <i>Sen</i>);\n<b>2)</b> третье лицо, которому спорный предмет отдается на хранение (до решения спора) (apud sequestrem depositum <i>AG</i>)."],
  ["sequestra", "<big>sequestra</big>\n\n<b>sequestra, ae</b> <i>f</i>\nпосредница (pacis <i>St</i>)."],
  ["sequestrarius", "<big>sequestrarius</big>\n\n<b>sequestrarius, a, um</b> [sequestrum]\nкасающийся секвестра (депозита) (actio <i>Dig</i>)."],
  ["sequestratim", "<big>sequestratim</big>\n\n<b>sequestratim</b>\nотдельно, особо <i>Eccl</i>."],
  ["sequestratio", "<big>sequestratio</big>\n\n<b>sequestratio, onis</b> <i>f</i> [sequestro]\n<b>1)</b> сдача на хранение (в депозит) <i>CTh</i>:\n<b>2)</b> разделение, обособление <i>Eccl</i>."],
  ["sequestrator", "<big>sequestrator</big>\n\n<b>sequestrator, oris</b> <i>m</i>\nотвергающий, <i>перен.</i> служащий помехой (alicujus rei <i>Symm</i>)."],
  ["sequestratorium", "<big>sequestratorium</big>\n\n<b>sequestratorium, i</b> <i>n</i> [sequestro]\nхранилище (terra seminibus s. <i>Tert</i>)."],
  ["sequestro", "<big>sequestro</big>\n\n<b>sequestro, avi, atum, are</b>\n<b>1)</b> ставить вне, отделять (aliquid ab aliqua re <i>Macr etc.</i>);\n<b>2)</b> отвергать, отклонять (aliquid <i>Vop</i>): sequestrari nequit, quin... <i>Boet</i> не приходится отрицать, что...;\n<b>3)</b> отдавать на хранение (в депозит) <i>Dig, Eccl</i>;\n<b>4)</b> предавать земле, хоронить (s. corpora sepulturae <i>Tert</i>)."],
  ["sequestrum", "<big>sequestrum</big>\n\n<b>sequestrum, i</b> <i>n</i> [sequester I]\nхранение, депозит: sequestro (<i>dat.</i>) ponere <i>AG</i> (dare <i>Pl</i>) сдать на хранение, секвестровать."],
  ["sequior", "<big>sequior</big>\n\n<b>sequior, sequius</b> <i>adj.</i> [<i>compar. к</i> secus II]\nменее значительный, более слабый (sexus <i>Ap</i>); худший (vita <i>Dig</i>)."],
  ["sequius", "<big>sequius</big>\n\n<b>sequius</b> <i>adv.</i> [<i>compar. к</i> secus II] <i>Vr, L, Sen</i>\n= setius."],
  ["sequo", "<big>sequo</big>\n\n<b>sequo, —, —, ere</b> <i>rhH, AG</i>\n= sequor."],
  ["sequor", "<big>sequor</big>\n\n<b>sequor, secutus (sequutus) sum, sequi</b> <i>depon.</i>\n<b>1)</b> идти вслед (за), следовать (s. patrem in bello <i>L</i>; s. alicujus vestigia <i>O</i>): s. alicujus castra <i>Nep</i> служить в армии под чьим-л. начальством; diversa sequentes <i>H</i> живущие по-другому, ведущие иной образ жизни; si modo factum fortuna sequatur <i>V</i> только бы дело увенчалось успехом; Africanus sequens <i>PM</i> = junior;\n<b>2)</b> провожать, следить (nubem oculis s. <i>V</i>);\n<b>3)</b> отвечать (dicta sic voce s. <i>V</i>);\n<b>4)</b> преследовать (hostem <i>Cs, L, T</i>; agmine quadrato cum gladiis <i>C</i>);\n<b>5)</b> (<i>во времени</i>) следовать, наступать, наставать (bellum secutum est Macedonicum <i>Eutr</i>; hiems sequitur <i>L</i>): referre quae sequuntur <i>Pt</i> рассказывать о дальнейшем (о последующих событиях); secuto (sequente) die <i>PM</i> на следующий день; et quae sequuntur <i>C</i> и так далее;\n<b>6)</b> отыскивать, устремляться, держать путь (s. Italiam <i>V</i>; s. loca demissa ac palustria <i>Cs</i>; s. astra pennis <i>O</i>); добиваться, домогаться, стремиться, искать (s. alicujus amicitiam <i>C, Nep, Cs</i>; vitam tranquillam et quietam <i>C</i>): s. ferro extrema <i>V</i> заколоться; s. spem vanam <i>O</i> предаваться пустой надежде; s. fidem alicujus <i>C</i> стать под чью-л. защиту; s. virginem <i>V</i> добиваться руки девушки; id sequor (ut) <i>C</i> моё намерение заключается в том (чтобы); sequitur, ut de frumento empto vos doceam <i>C</i> далее я должен ознакомить вас с делом о закупке хлеба (<i>ср.</i> 7);\n<b>7)</b> (<i>о причинной связи</i>) следовать, наступать (magna poena illud scelus sequetur <i>C</i>): sequitur (ut) <i>C, Dig etc.</i> (из этого) следует (вытекает), что (<i>ср.</i> 6);\n<b>8)</b> следовать, повиноваться (alicujus consilium <i>C</i>): jussa secutus <i>Lcn</i> выполнив приказания || подражать, воспроизводить (alicujus exemplum <i>Nep</i>): liberi sequuntur patrem <i>L</i> дети наследуют звание (сословие) отца; s. leges <i>C</i> соблюдать законы; s. naturam ducem <i>C</i> руководствоваться указаниями природы;\n<b>9)</b> поддаваться, уступать (celerius rumpi quum sequi <i>Vr</i>): ramus volens facilisque sequitur <i>V</i> ветка легко отламывается; rem tene, verba sequentur <i>Q</i> держись сути дела, (а) слова (сами) придут;\n<b>10)</b> доставаться: urbes captae Aetolos secutae sunt <i>L</i> захваченные города достались этолинам; heredem non s. <i>H, Pt</i> не переходить по наследству."],
  ["sequut-", "<big>sequut-</big>\n\n<b>sequut-</b> <i>v. l.</i>\n= secut-."],
  ["Ser", "<big>Ser</big>\n\n<b>Ser, Seris</b> <i>m</i> <i>SenT sg. к</i>\nSeres."],
  ["Ser.", "<big>Ser.</big>\n\n<b>Ser.</b> <i>сокр. к</i>\nServius."],
  ["sera", "<big>sera</big>\n\n<b>I sera, ae</b> <i>f</i>\n<b>1)</b> планка, рейка (serae transversae <i>Col</i>);\n<b>2)</b> запор, засов, задвижка (excutere poste seram <i>O</i>).\n\n<b>II sera</b> [serus] <i>adv.</i>\nпоздно: s. comans narcissus <i>V</i> поздно зацветающий нарцисс."],
  ["Serapeum", "<big>Serapeum</big>\n\n<b>Serapeum, i</b> <i>n</i>\nхрам Сераписа <i>Tert</i>."],
  ["seraphim", "<big>seraphim</big>\n\n<b>seraphim (-in)</b> <i>m pl. indecl.</i> (<i>евр.</i>)\nсерафимы <i>Vlg, Eccl</i>."],
  ["Serapicus", "<big>Serapicus</big>\n\n<b>Serapicus, a, um</b> [Serapis]\nсераписов, <i>т. е.</i> торжественный (cena <i>Tert</i>)."],
  ["Serapion", "<big>Serapion</big>\n\n<b>Serapio(n), onis</b> <i>m</i>\nСерапион, <i>греч. имя</i> <i>C, Cs etc.</i>"],
  ["Serapis", "<big>Serapis</big>\n\n<b>Serapis, is</b> <i>и</i> <b>idis</b> <i>m</i>\nСерапис, <i>египетский бог подземного царства, впоследствии слившийся с Озирисом и отождествлённый в греко-римской религии с Плутоном или Эскулапием</i> <i>Vr, C, T etc.</i>"],
  ["serarius", "<big>serarius</big>\n\n<b>serarius, a, um</b> [serum I]\nпитающийся сывороткой (porcus <i>Cato</i>)."],
  ["Serbi", "<big>Serbi</big>\n\n<b>Serbi, orum</b> <i>m</i>\nсербы, <i>племя в вост.-европейской или азиатской Скифии</i> <i>PM</i>."],
  ["Serenator", "<big>Serenator</big>\n\n<b>Serenator, oris</b> <i>m</i> [sereno]\n«очиститель неба от туч», <i>эпитет Юпитера</i> <i>Ap</i>."],
  ["serene", "<big>serene</big>\n\n<b>serene</b>\nясно (serenius videre <i>Aug</i>)."],
  ["serenitas", "<big>serenitas</big>\n\n<b>serenitas, atis</b> <i>f</i> [serenus]\n<b>1)</b> ясность, безоблачность, ясная погода (caeli <i>C, Sen</i>; diei <i>bAfr</i>);\n<b>2)</b> безмятежность, спокойствие (oris et vultus <i>AG</i>): s. fortunae <i>L</i> благоденствие;\n<b>3)</b> (<i>как титулование</i>) светлость <i>или</i> сиятельство (tua s. <i>Capit</i>)."],
  ["sereno", "<big>sereno</big>\n\n<b>sereno, avi, atum, are</b> [serenus]\n<b>1)</b> делать ясным, прояснять (caelum <i>C, V etc.</i>): s. spem fronte <i>V</i> весёлым видом выражать надежду; nubila animi s. <i>PM</i> разгонять душевные тучи;\n<b>2)</b> быть ясным: luce serenanti <i>Poeta ap. C</i> в ясный день; <i>impers.</i> serenat <i>MF</i> стоит ясная погода."],
  ["serenum", "<big>serenum</big>\n\n<b>serenum, i</b> <i>n</i> [serenus]\nясная погода (serena caeli <i>Lcr</i>): sereno <i>L, PM, Sen, Su</i> в ясную погоду."],
  ["serenus", "<big>serenus</big>\n\n<b>serenus, a, um</b>\n<b>1)</b> ясный, погожий, безоблачный (caelum <i>C</i>; nox <i>V</i>; tempestas <i>Su</i>; dies <i>Tib</i>); недождевой (nubes <i>V</i>); предвещающий ясную погоду (Favonius <i>Pl</i>); яркий, светлый (color <i>PM</i>); прозрачный (aqua <i>M</i>); чистый (vox <i>Pers</i>);\n<b>2)</b> безмятежный, спокойный, радостный (vita <i>Lcr</i>; frons <i>C</i>): rebus serenis <i>Sil</i> в (при) счастливых обстоятельствах."],
  ["Seres", "<big>Seres</big>\n\n<b>Seres, um</b> <i>m</i> [sericum]\nсеры, <i>народ в Вост. Азии, отождествл. с китайцами</i> <i>V, PM, Sen</i>."],
  ["seresco", "<big>seresco</big>\n\n<b>I seresco, —, —, ere</b> [serenus]\nсохнуть (vestes dispansae in sole serescunt <i>Lcr</i>).\n\n<b>II seresco, —, —, ere</b> [serum I]\nпревращаться в сыворотку (lac serescit <i>PM</i>)."],
  ["Sergius", "<big>Sergius</big>\n\n<b>Sergius, a, um</b>\nСергий, <i>римск. nomen; наиболее известны</i>:\n<b>1)</b> L. S. Fidenas, <i>консул в 437 г. до н. э., победитель фиденатов и вейентов</i> <i>L</i>;\n<b>2)</b> C. S. Orata, <i>известный расточитель и сибарит времён Цицерона</i> <i>C</i>;\n<b>3)</b> L. S. Catilina — <i>см.</i> Catilina."],
  ["seria", "<big>seria</big>\n\n<b>I seria, ae</b> <i>f</i>\nбольшой глиняный сосуд, чан <i>Pl, Ter, Cato, L etc.</i>\n\n<b>II seria</b> <i>f <i>sg.</i> <i>и</i> n</i> <i>pl. к</i>\nserius."],
  ["serica", "<big>serica</big>\n\n<b>serica, orum</b> <i>n</i> [sericum]\nшёлковые ткани <i>или</i> платья <i>Prp, Sen, M</i>."],
  ["sericatus", "<big>sericatus</big>\n\n<b>sericatus, a, um</b> [sericum]\nодетый в шёлк <i>Su, Sid, Is</i>."],
  ["sericeus", "<big>sericeus</big>\n\n<b>sericeus, a, um</b> [serica]\nшёлковый <i>Fl</i>."],
  ["sericoblattea", "<big>sericoblattea</big>\n\n<b>sericoblatt(e)a, ae</b> <i>f</i> [sericus + blatta 2]\nпурпурная шёлковая одежда <i>CTh, CJ</i>."],
  ["sericula", "<big>sericula</big>\n\n<b>sericula</b> <i>PM</i>\n(<i>v. l.</i>)= securicula."],
  ["sericum", "<big>sericum</big>\n\n<b>sericum, i</b> <i>n</i> [Ser]\nшёлковая ткань <i>Amm</i>; <i>pl.</i> шелка (<i>ткани или одежда</i>) <i>Prp, Sen, M</i>."],
  ["sericus", "<big>sericus</big>\n\n<b>I sericus, a, um</b> [Ser]\nшёлковый (pulvinus <i>H</i>; toga <i>Q</i>).\n\n<b>II Sericus, a, um</b> [Seres]\nсерский (sagitta <i>H</i>)."],
  ["serie", "<big>serie</big>\n\n<b>serie</b> <i>AV</i>\n= serio."],
  ["series", "<big>series</big>\n\n<b>series, ei</b> <i>f</i> [<i>одного корня с</i> sero I]\nряд, вереница, цепь (rerum <i>C, AG</i>; juvenum <i>Tib</i>; annorum <i>H, Sen etc.</i>); ряд предков, род (vir hac serie dignus <i>O</i>)."],
  ["serietas", "<big>serietas</big>\n\n<b>serietas, atis</b> <i>f</i> [serius II]\nсерьёзный вид, серьёзность <i>Aus, Sid</i>."],
  ["serilia", "<big>serilia</big>\n\n<b>serilia, ium</b> <i>n</i> [sero I]\nтростниковые веревки <i>Pac</i>."],
  ["serio", "<big>serio</big>\n\n<b>serio</b>\nсерьёзно (nec joco nec s. <i>Pl</i>; joco seri ove <i>L</i>)."],
  ["seriola", "<big>seriola</big>\n\n<b>seriola, ae</b> <i>f</i> [<i>demin. к</i> seria I]\nнебольшой глиняный сосуд, кувшинчик <i>Pers, Pall</i>."],
  ["Seriphius", "<big>Seriphius</big>\n\n<b>Seriphius, a, um</b> [Seriphus]\nсерифосский <i>C, T</i>."],
  ["Seriphus", "<big>Seriphus</big>\n\n<b>Seriphus (-os), i</b> <i>f</i>\nСерифос, <i>один из Кикладских островков, к зап. от Пароса</i> (<i>ныне</i> Серфо <i>или</i> Серфанто) <i>O, PM, Sen, T</i>."],
  ["seris", "<big>seris</big>\n\n<b>seris, idis</b> (<i>acc.</i> im, <i>abl.</i> i) <i>f</i> (<i>греч.</i>)\nэндивий <i>или</i> цикорий <i>Vr, PM</i>."],
  ["serisapia", "<big>serisapia</big>\n\n<b>serisapia, ae</b> <i>f</i> [<i>предпол. от</i> serus + sapio]\nзапоздалая мудрость, задний ум (<i>шутливое название вымышленного блюда</i>) <i>Pt</i>."],
  ["seritas", "<big>seritas</big>\n\n<b>seritas, atis</b> <i>f</i> [serus]\nопоздание, позднее прибытие <i>Symm</i>."],
  ["serium", "<big>serium</big>\n\n<b>serium, i</b> <i>n</i> [serius II]\nсерьёзность, серьёзная вещь (aliquid in s. vertere <i>QC</i>); <i>преим.</i> <i>pl.</i> seria важные, серьёзные дела (amoto ludo quaeramus seria <i>H</i>; joca atque seria <i>Sl</i>)."],
  ["serius", "<big>serius</big>\n\n<b>I serius</b> <i>compar. к</i>\n<b>1)</b> serus <i>и</i>\n<b>2)</b> sero IV.\n\n<b>II serius, a, um</b>\nсерьёзный, важный (res <i>C</i>; verba <i>Tib, H</i>)."],
  ["sermo", "<big>sermo</big>\n\n<b>sermo, onis</b> <i>m</i> [sero I]\n<b>1)</b> разговор, беседа, диалог (sermones, quos scripsit Plato <i>AG</i>): s. litterarum <i>C</i> переписка; sermonem habere (conferre) cum aliquo <i>C</i> иметь беседу с кем-л.; in sermonem ingredi <i>C</i> <i>и</i> dare se in sermonem <i>Poeta ap. C</i> вступить в разговор; nulla sermonum vice <i>Amm</i> не обменявшись ни единым словом;\n<b>2)</b> содержание беседы, высказывание, слова (alicujus sermonem ad aliquem referre <i>Nep</i>): sermones habere <i>C</i> высказываться, делать заявления;\n<b>3)</b> обиходная речь, язык (sermo Graecus <i>C</i>): s. patrius <i>Lcr</i> родной язык; sermonis plenus orator <i>C</i> оратор, пользующийся обыденным языком; s. corporis <i>C</i> ораторская жестикуляция;\n<b>4)</b> говор, наречие, диалект (urbanus, rusticus <i>L</i>; cotidianus <i>Q</i>);\n<b>5)</b> сочинение на простом языке: sermones сатиры, послания (nostrorum sermonum judex <i>H</i>);\n<b>6)</b> способ изъясняться, изложение, слог (s. purus, elegans <i>C</i>): sermonis error <i>C</i> стилистическая ошибка;\n<b>7)</b> слух, молва, толки, пересуды (s. quidem multus, <i>sc.</i> est <i>C</i>; sermone multiplici populos replere <i>O</i>): in sermonem hominum venire <i>C</i> стать предметом толков; sermonem aliis dare <i>C</i> (praebere <i>L</i>) дать другим повод к пересудам;\n<b>8)</b> предмет толков (filius meus s. est per urbem <i>Pt</i>)."],
  ["sermocinanter", "<big>sermocinanter</big>\n\n<b>sermocinanter</b>\nбеседуя, в беседе (horas extrahere <i>Sid</i>)."],
  ["sermocinatio", "<big>sermocinatio</big>\n\n<b>sermocinatio, onis</b> <i>f</i> [sermocinor]\n<b>1)</b> разговор, беседа <i>Q, Ap, AG</i>;\n<b>2)</b> <i>ритор.</i> приведение чьих-л. слов, введение чужой речи <i>rhH, Q</i>."],
  ["sermocinatrix", "<big>sermocinatrix</big>\n\n<b>sermocinatrix, icis</b> <i>f</i> [sermocinor]\n<b>1)</b> <i>ритор.</i> искусство вести беседу <i>Q</i>;\n<b>2)</b> говорунья, болтунья (s. immodica <i>Ap</i>)."],
  ["sermocino", "<big>sermocino</big>\n\n<b>sermocino, —, —, are</b> <i>Is</i>\n= sermocinor."],
  ["sermocinor", "<big>sermocinor</big>\n\n<b>sermocinor, atus sum, ari</b> <i>depon.</i> [sermo]\nразговаривать, беседовать (cum aliquo <i>Cato, C</i>); болтать (comoedus sermocinatur <i>Ap</i>)."],
  ["sermonalis", "<big>sermonalis</big>\n\n<b>sermonalis, e</b> [sermo]\nговорящий <i>Tert</i>."],
  ["sermonor", "<big>sermonor</big>\n\n<b>sermonor, —, ari</b> <i>Ap</i>\n= sermocinor."],
  ["sermunculus", "<big>sermunculus</big>\n\n<b>sermunculus, i</b> <i>m</i> [<i>demin. к</i> sermo]\n<b>1)</b> разговоры, пересуды, болтовня: sermunculi urbani <i>C</i> городские толки;\n<b>2)</b> маленькая речь <i>или</i> статья <i>Hier</i>."],
  ["sero", "<big>sero</big>\n\n<b>I sero, (serui), sertum, ere</b>\n<b>1)</b> сплетать, соединять, связывать, сцеплять (corona serta <i>Lcn</i>): rosa serta <i>Ap</i> вейки (гирлянды) из роз; causa causam serens <i>C</i> сцепление причин; lorica serta <i>Nep</i> кольчуга (<i>см.</i> serta 1); bella ex bellis s. <i>Sl, L</i> вести войну за войной; s. orationes <i>L</i> произносить множество речей;\n<b>2)</b> завязывать, начинать (colloquia cum aliquo <i>L</i>; certamina <i>L</i> <i>или</i> proelia <i>T</i>): s. negotium <i>Pl</i> затевать (судебное) дело; fabulam argumento s. <i>L</i> сочинить пьесу с единством содержания; <i>pass.</i> seri следовать друг за другом, связываться (ordo rerum seritur <i>L</i>);\n<b>3)</b> обсуждать (multa inter se <i>V</i>).\n\n<b>II sero, sevi, satum, ere</b>\n<b>1)</b> сеять (frumenta <i>Cs</i>; semina <i>V</i>); сажать (arbores <i>C</i>); засевать, засаживать (agrum <i>Cato</i>): mihi istic nec seritur, nec metitur <i>погов. Pl</i> мне от этого ни посева, ни жатвы (<i>т. е.</i> ни тепло, ни холодно);\n<b>2)</b> рождать, порождать, производить на свет (genus humanum <i>C</i>); <i>part. pf.</i> satus рождённый, происходящий (aliquo <i>V</i>): satae Pelia <i>O</i> дочери Пелея; Anchisa satus <i>V</i> сын Анхиза; aurea prima sata est aetas <i>O</i> первым настал золотой век;\n<b>3)</b> вызывать, причинять (bella <i>Lampr</i>); возбуждать (discordias <i>L</i>);\n<b>4)</b> создавать (opinionem <i>Just</i>); порождать (rumores <i>V</i>; rixas <i>C</i>); насаждать, внедрять (mores <i>C</i>); учреждать, основывать (diuturnam rem publicam <i>C</i>);\n<b>5)</b> наносить (vasta vulnera <i>Lcr</i>).\n\n<b>III sero, avi, atum, are</b> [sera I]\n<b>1)</b> запирать (portas <i>Eccl</i>);\n<b>2)</b> <i>Vr</i> = resero I.\n\n<b>IV sero</b> <i>adv.</i> [serus]\n<b>1)</b> поздно (domum s. redire <i>C</i>): decima hora vel serius <i>M</i> в десять часов или попозднее; serius ocius <i>H</i> <i>или</i> serius aut citius <i>O</i> рано или поздно; spe serius <i>L</i> позднее, чем ожидалось;\n<b>2)</b> слишком поздно, с запозданием (venire <i>C, O</i>): s. sapere <i>погов. C</i> быть крепким задним умом."],
  ["serotinus", "<big>serotinus</big>\n\n<b>serotinus, a, um</b> [serus]\nпоздний, запоздалый (hiems <i>PM</i>); поздно поспевающий (pira <i>PM</i>)."],
  ["serpedo", "<big>serpedo</big>\n\n<b>serpedo, inis</b> <i>f</i> [serpo] <i>мед.</i>\nрожа <i>или</i> краснуха <i>Is</i>."],
  ["serpens", "<big>serpens</big>\n\n<b>serpens, entis</b>\n<b>1.</b> <i>part. praes. к</i> serpo;\n<b>2.</b> <i>adj.</i> ползающий, пресмыкающийся (bestia <i>C</i>);\n<b>3.</b> <i>subst. m, f</i>\n<b>1)</b> змей, змея (serpentis ictus <i>или</i> morsus <i>CC</i>);\n<b>2)</b> <i>pl.</i> ползающие насекомые-паразиты (serpentium multitudine exspirare <i>PM</i>): serpentium scabies <i>Ap</i> вшивая болезнь <i>или</i> чесотка;\n<b>3)</b> (<i>тж.</i> Draco <i>и</i> Anguis) созвездие Дракона <i>Vtr, O</i>."],
  ["serpentigena", "<big>serpentigena</big>\n\n<b>serpentigena, ae</b> <i>m</i> [serpens + gigno]\nродившийся от змеи <i>O</i>."],
  ["serpentinus", "<big>serpentinus</big>\n\n<b>serpentinus, a, um</b> [serpens]\nзмеиный <i>Eccl</i>."],
  ["serpentipes", "<big>serpentipes</big>\n\n<b>serpentipes, pedis</b> <i>adj.</i> [serpens + pes]\nсо змеевидными ногами, змееногий (Gigantes <i>O</i>)."],
  ["serperastra", "<big>serperastra</big>\n\n<b>serperastra, orum</b> <i>n</i>\n<b>1)</b> лубки (<i>для выпрямления кривых ног у детей</i>) <i>Vr</i>;\n<b>2)</b> <i>шутл.</i> «выпрямители», <i>командиры, поддерживающие дисциплину среди своих подчинённых</i> <i>C</i>."],
  ["serpillum", "<big>serpillum</big>\n\n<b>serpillum, i</b> <i>n</i> <i>v. l.</i>\n= serpullum."],
  ["serpo", "<big>serpo</big>\n\n<b>serpo, serpsi, —, ere</b>\n<b>1)</b> ползать, пресмыкаться (serpunt quae pedibus carent <i>PM</i>): <i>pass.</i> serpi angue <i>Sol</i> кишеть змеями || (<i>о ползучих растениях</i>) виться (hedera serpens <i>Macr</i>);\n<b>2)</b> потихоньку пробираться, мало-помалу продвигаться (flumen serpit <i>O</i>); незаметно прокрадываться, исподволь распространяться (serpit rumor <i>C</i>; malum obscure serpens <i>L</i>; serpunt contagia <i>V</i>)."],
  ["serpullum", "<big>serpullum</big>\n\n<b>serpullum</b> <i>и</i> <b>serpyllum, i</b> <i>n</i> (<i>греч.</i>) <i>бот.</i>\nтимьян (<i>Thymus Serpyllum</i>, L.) <i>Cato, Vr, V</i>."],
  ["serra", "<big>serra</big>\n\n<b>serra, ae</b> <i>f</i>\n<b>1)</b> пила (serra secare <i>PM</i> <i>или</i> scindere <i>Sen</i>): serram cum aliquo ducere <i>погов. Vr</i> перебраниваться с кем-л.;\n<b>2)</b> <i>воен.</i> «пила», змейка (извилистый строй) <i>Cato, AG, Veg</i>;\n<b>3)</b> пила-рыба <i>PM</i>;\n<b>4)</b> молотильная повозка (с зубчатыми колёсами) <i>Vlg, Hier</i>."],
  ["serrabilis", "<big>serrabilis</big>\n\n<b>serrabilis, e</b> [serro]\nгодный для распилки (ligna <i>PM</i> — <i>v. l.</i>)."],
  ["serraculum", "<big>serraculum</big>\n\n<b>serraculum, i</b> <i>n</i>\nруль <i>Dig</i>."],
  ["serracum", "<big>serracum</big>\n\n<b>serracum, i</b> <i>n</i> (<i>кельт.</i>)\n<b>1)</b> крестьянская двуколка <i>C ap. Q, Vtr etc.</i>;\n<b>2)</b> созвездие Большой Медведицы <i>J</i>."],
  ["serrago", "<big>serrago</big>\n\n<b>serrago, inis</b> <i>f</i> [serro]\nопилки <i>CA</i>."],
  ["Serranus", "<big>Serranus</big>\n\n<b>Serranus, i</b> <i>m</i>\nСерран, <i>cognomen в роде Аттилиев</i> (<i>по умбрскому городу Saranum</i>) <i>C, V, PM</i>."],
  ["serrata", "<big>serrata</big>\n\n<b>serrata, ae</b> <i>f</i> [serratus]\n(<i>sc.</i> herba) <i>бот. предпол.</i> очанка (<i>Euphrasia officinalis</i>, L.) <i>PM</i>."],
  ["serrati", "<big>serrati</big>\n\n<b>serrati, orum</b> <i>m</i> [serra]\n(<i>sc.</i> nummi) серебряные денарии с зазубренными краями <i>T</i>."],
  ["serratim", "<big>serratim</big>\n\n<b>serratim</b> [serra]\nнаподобие пилы, с зубцами (s. struantur dentes conjuncti muro <i>Vtr</i>; folia s. scissa <i>Ap</i>)."],
  ["serratorius", "<big>serratorius</big>\n\n<b>serratorius, a, um</b> [serro]\nслужащий для распиливания (machina <i>Amm</i>)."],
  ["serratula", "<big>serratula</big>\n\n<b>serratula, ae</b> <i>f</i> [serra] <i>бот.</i>\nбуквица <i>PM</i>."],
  ["serratura", "<big>serratura</big>\n\n<b>serratura, ae</b> <i>f</i> [serro]\nпиление, (рас)пилка <i>Pall</i>."],
  ["serratus", "<big>serratus</big>\n\n<b>serratus, a, um</b> [serra]\nпилообразный (dentes <i>PM</i>); зазубренный, зубчатый (folia <i>PM</i>): morsus s. <i>Pt</i> укус острыми словно у пилы зубами."],
  ["serro", "<big>serro</big>\n\n<b>serro, avi, atum, are</b> [serra]\nпилить, распиливать <i>Veg, Vlg, Eccl</i>."],
  ["serrula", "<big>serrula</big>\n\n<b>serrula, ae</b> <i>f</i> [<i>demin. к</i> serra]\nнебольшая пила <i>C, CC, Col, Pall</i>."],
  ["serta", "<big>serta</big>\n\n<b>I serta, ae</b> <i>f</i> <i>и</i> <b>serta, orum</b> <i>n</i> [sero I]\nгирлянда <i>Prp</i>: spicea s. <i>O</i> гирляида из колосьев.\n\n<b>II serta, ae</b> <i>f</i> (<i>или</i> s. Campanica <i>и</i> Campana) <i>бот.</i>\nжёлтый донник <i>Cato, PM</i>."],
  ["Sertorianus", "<big>Sertorianus</big>\n\n<b>Sertorianus, a, um</b> [Sertorius]\nсерториев (bellum <i>C</i>)."],
  ["Sertorius", "<big>Sertorius</big>\n\n<b>Sertorius, a, um</b>\nСерторий, <i>римск. nomen; наиболее известен</i> Q. S., <i>родом из Нурсии, сторонник Мария, выдающийся полководец, десять лет</i> (<i>82-72 гг. до н. э.</i>) <i>успешно сражавшийся в Испании против Суллы и Помпея; убит своим подчинённым Перпенной в 72 г. до н. э.</i> <i>C, Sl, L, VP</i>."],
  ["sertum", "<big>sertum</big>\n\n<b>sertum, i</b> <i>n</i> <i>C, V, Aus</i>\n= serta I."],
  ["sertus", "<big>sertus</big>\n\n<b>sertus, a, um</b> <i>part. pf. к</i>\nsero I."],
  ["serum", "<big>serum</big>\n\n<b>I serum, i</b> <i>n</i>\n<b>1)</b> сыворотка <i>V, Col, PM</i>;\n<b>2)</b> водянистая жидкость <i>PM</i>;\n<b>3)</b> <i>Ctl</i> = semen genitale.\n\n<b>II serum, i</b> <i>n</i> [serus]\nпозднее время: rem in s. trahere <i>L</i> запаздывать; s. diei <i>L</i> вечер; s. noctis <i>L</i> поздняя ночь.\n\n<b>III serum</b> <i>adv.</i> <i>V</i>\n= sero IV."],
  ["serus", "<big>serus</big>\n\n<b>serus, a, um</b>\n<b>1)</b> поздний (hiems <i>L</i>; vesper <i>Vr ap. AG</i>): s. redeas <i>H</i> вернись попозднее; s. adveniens <i>Ap</i> поздно приходящий, запоздавший || запоздалый (auxilium <i>L</i>; paenitentia <i>Ph</i>); более поздний, зрелый, немолодой (aetas <i>O, Tib</i>); поздно сбывающийся (spes <i>L</i>; portenta <i>C</i>); медленно <i>или</i> поздно растущий (arbor <i>V</i>); поздно поспевающий (ficus <i>Col</i>);\n<b>2)</b> сумеречный, предвечерний (cantus, <i>sc.</i> noctuarum <i>Ap</i>): sera lumina <i>V</i> = stellae;\n<b>3)</b> старый, многолетний (platanus <i>O</i>): s. alicujus rei <i>H</i> запаздывающий (отсталый) в чём-л.; s. abiit <i>O</i> он поздно ушёл; s. querelis <i>V</i> обращающийся с запоздалыми жалобами;\n<b>4)</b> длительный, затяжной (bellum <i>O</i>);\n<b>5)</b> дальний (amnis <i>VF</i>). — <i>См. тж.</i> sera II <i>и</i> serum II."],
  ["serva", "<big>serva</big>\n\n<b>serva, ae</b> <i>f</i> [servus II]\nрабыня, невольница <i>Pl, H, L etc.</i>"],
  ["servabilis", "<big>servabilis</big>\n\n<b>servabilis, e</b> [servo]\nмогущий быть спасённым: nulli s. <i>O</i> безнадёжно погибший."],
  ["servaculum", "<big>servaculum</big>\n\n<b>servaculum</b> <i>v. l.</i>\n= serraculum."],
  ["servans", "<big>servans</big>\n\n<b>servans, antis</b>\n<b>1.</b> <i>part. praes. к</i> servo;\n<b>2.</b> <i>adj.</i> соблюдающий (servantissimus aequi <i>V</i> <i>и</i> justi <i>Aus</i>)."],
  ["servasso", "<big>servasso</big>\n\n<b>servasso</b> <i>Pl</i>\n= servavero (<i>fut. II к</i> servo)."],
  ["servatio", "<big>servatio</big>\n\n<b>servatio, onis</b> <i>f</i> [servo]\nсоблюдение, следование (какому-л.) правилу (servationem rumpere <i>PJ</i>)."],
  ["servator", "<big>servator</big>\n\n<b>servator, oris</b> <i>m</i> [servo]\n<b>1)</b> наблюдатель, внимательно следящий, подстерегающий (s. alicujus rei <i>Lcn, St</i>);\n<b>2)</b> хранитель (rei publicae <i>C</i>): s. salutis <i>O</i> спаситель;\n<b>3)</b> исполнитель, соблюдающий (honesti <i>Lcn</i>; icti foederis <i>Cld</i>)."],
  ["servatrix", "<big>servatrix</big>\n\n<b>servatrix, icis</b> <i>f</i> [servator]\n<b>1)</b> хранительница, спасительница <i>Ter, C, St</i>;\n<b>2)</b> соблюдающая (s. convenientiae <i>Ap</i>)."],
  ["servibam", "<big>servibam</big>\n\n<b>servibam</b> <i>и</i> <b>servibo</b> <i>арх. Pl, Ter impf. и fut. к</i>\nservio."],
  ["serviculus", "<big>serviculus</big>\n\n<b>serviculus, i</b> <i>m</i> [servus]\nжалкий раб <i>Tert</i>."],
  ["Servilianus", "<big>Servilianus</big>\n\n<b>Servilianus, a, um</b> [Servilius]\nсервилиев <i>Su</i>."],
  ["servilicola", "<big>servilicola</big>\n\n<b>servilicola, ae</b> <i>f</i> <i>v. l.</i>\n= servulicola."],
  ["servilis", "<big>servilis</big>\n\n<b>servilis, e</b> [servus]\n<b>1)</b> рабский, невольничий (vestis <i>C</i>): bellum servile <i>C, Lcn</i> невольническая война; manus s. <i>H</i> войско, набранное из рабов; terror s. <i>L</i> страх перед рабами;\n<b>2)</b> выработавшийся в результате рабства, низкий (animus <i>T</i>); servilem in modum <i>Cs</i> по-рабски."],
  ["serviliter", "<big>serviliter</big>\n\n<b>serviliter</b> [servilis]\nрабски, как раб(ы) <i>C, Fl</i>."],
  ["Servilius", "<big>Servilius</big>\n\n<b>I Servilius, a, um</b>\nСервилий, <i>римск. nomen; наиболее известны</i>:\n<b>1)</b> C. S. Ahala, <i>будучи magister equitum диктатора Л. Квинкция Цинцинната, убил в 439 г. до н. э. Спурия Мелия</i> <i>L</i>;\n<b>2)</b> Q. S. Caepio, <i>в 105 г. до н. э. потерпел поражение от кимбров в Gallia Narbonensis, а в 95 г. до н. э. подвергся изгнанию</i> <i>C, Sl, VP, VM</i>;\n<b>3)</b> Q. S. Caepio, <i>quaestor urbanus в 100 г. до н. э., выступал против lex frumentaria Сатурнина; погиб в 90 г. до н. э. во время Союзнической войны</i> <i>C</i>;\n<b>4)</b> P. S. Vatia, <i>получивший прозвище isauricus</i> (<i>за успешные военные действия против исавров в Ликаонии в 78-76 гг. до н. э.</i>), <i>друг Цицерона, умер в 44 г. до н. э.</i> <i>C</i>;\n<b>5)</b> P. S. Casca, <i>народный трибун в 43 г. до н. э., участник заговора против Цезаря</i> <i>Su</i>;\n<b>6)</b> C. S. Glaucia, <i>претор в 100 г. до н. э., сторонник Мария, убит вместе с Сатурнином в 100 г. до н. э.</i> <i>C</i>;\n<b>7)</b> P. S. Rullus, <i>народный трибун в 63 г. до н. э., автор аграрного законопроекта, против которого Цицерон выступил со своими тремя «Orationes de lege agraria»</i> <i>C</i>."],
  ["Servilius lacus", "<big>Servilius lacus</big>\n\n<b>II Servilius lacus</b>\nСервилиев водоём, <i>у которого выставлялись головы убитых во время проскрипционного террора 81 г. до н. э.</i> <i>C</i>."],
  ["servio", "<big>servio</big>\n\n<b>servio, ivi (ii), itum, ire</b> [servus]\n<b>1)</b> быть рабом (невольником), быть порабощенным, в рабстве (apud aliquem <i>C</i> <i>или</i> alicui <i>Ter, L, Ap</i>): s. servitutem <i>C</i> находиться в полном рабстве;\n<b>2)</b> быть раболепным (s. humiliter <i>L</i>);\n<b>3)</b> быть подвластным, находиться в полном подчинении (populo Romano <i>C</i>);\n<b>4)</b> <i>юр.</i> быть отягощенным повинностями («сервитутами») (serviens fundus <i>Dig</i>; praedia serviebant <i>C</i>); быть объектом чьих-л. прав (aedes quae serviunt alicui <i>C</i>);\n<b>5)</b> служить (candelae luminibus et funeribus serviunt <i>PM</i>); угождать, быть к услугам (alicui <i>Ter etc.</i>): auribus alicujus s. <i>Cs</i> говорить приятное кому-л.;\n<b>6)</b> сообразоваться, приспособляться, приноравливаться (s. temporibus callidissime <i>Nep</i>; s. existimationi populi <i>C</i>): s. personae <i>C</i> быть верным (своей) роли; s. incertis rumoribus <i>Cs</i> довериться недостоверным слухам; s. legibus <i>C</i> повиноваться законам; leges mori serviunt <i>Pl</i> законы соответствуют правам;\n<b>7)</b> целиком уделять внимание, посвящать себя, усердно служить, заботиться (annonae urbis <i>Dig</i>; s. valetudini, gloriae, posteritati <i>C</i>)."],
  ["servitialis", "<big>servitialis</big>\n\n<b>servitialis, e</b> [servitium]\nслужащий, служилый (clerici <i>Is</i>)."],
  ["servitium", "<big>servitium</big>\n\n<b>servitium, i</b> <i>n</i> [servus]\n<b>1)</b> рабство, неволя (ducere aliquem in s. <i>L</i>): corporis servitio uti <i>Sl</i> держать (своё) тело в повиновении;\n<b>2)</b> (<i>тж.</i> <i>pl.</i>) рабы, невольники (servitii decem milia <i>T</i>; servitia excitare, incitare <i>или</i> concitare <i>C</i>);\n<b>3)</b> раболепие, низкопоклонство (publici servitii victor, <i>sc.</i> Nero <i>T</i>)."],
  ["servitricius", "<big>servitricius</big>\n\n<b>servitricius, a, um</b> [servus]\nрабский (stabulum <i>Pl</i>)."],
  ["servitudo", "<big>servitudo</big>\n\n<b>servitudo, inis</b> <i>f</i> [servus]\nрабство <i>L</i> (<i>v. l.</i>)."],
  ["servitus", "<big>servitus</big>\n\n<b>servitus, utis</b> <i>f</i> [servus]\n<b>1)</b> рабство, невольничество, неволя: esse in servitute <i>или</i> servire servitutem <i>C</i> находиться в рабстве, быть рабом;\n<b>2)</b> <i>собир.</i> рабы, невольники (s. crescit nova <i>H</i>);\n<b>3)</b> подчинённость, подвластность (patriam e servitute in libertatem vindicare <i>Nep</i>); подчинённое положение, бесправие: s. muliebris <i>L</i> полная подчинённость жены (мужу); s. officii <i>C</i> обязанность повиноваться долгу; s. juris <i>Q</i> обязанность подчиняться законам;\n<b>4)</b> <i>юр.</i> повинность, оброк (servitutem fundo imponere <i>C</i>)."],
  ["Servius", "<big>Servius</big>\n\n<b>Servius, i</b> <i>m</i>\nСервий, <i>римск. praenomen; наиболее известны</i>:\n<b>1)</b> S. Tullius — <i>см.</i> Tullius I;\n<b>2)</b> S. Maurus Honoratus, <i>грамматик конца IV в. н. э., комментатор Вергилия.</i>"],
  ["servo", "<big>servo</big>\n\n<b>servo, avi, atum, are</b>\n<b>1)</b> наблюдать, следить (s. sidera <i>V</i>; s. ortum caniculae <i>C</i>): s. de caelo <i>C etc.</i> (<i>об авгурах</i>) наблюдать небесные явления;\n<b>2)</b> быть внимательным: servantia lumina <i>O</i> бдительные глаза; serva! <i>Pl, Ter, H</i> будь осторожен (начеку)!; quantum oculi s. possunt <i>V</i> насколько видит глаз;\n<b>3)</b> сторожить, охранять (reos custodia <i>C</i>; greges <i>V</i>; quicquid servatur, cupimus magis <i>O</i>): intus s. <i>Pl</i> смотреть за домом; s. custodias <i>L</i> нести охрану;\n<b>4)</b> оставаться, пребывать, находиться: s. limen <i>V</i> сидеть дома; s. silvas <i>V</i> жить в лесах; s. signa <i>L</i> оставаться у знамён;\n<b>5)</b> соблюдать (ordinem <i>Cs, L etc.</i>; vigilias <i>L</i>; legem <i>C</i>; defuncti voluntas servatur <i>Dig</i>); сдерживать (promissa <i>C</i>);\n<b>6)</b> сберегать, беречь, сохранять (vestigia veteris formae <i>O</i>; s. vinum Caecubum <i>H</i>; s. se ad majora <i>L</i>; aequam mentem s. <i>H</i>);\n<b>7)</b> оберегать (pudicitiam <i>V</i>); спасать, выручать (aliquem ex periculo <i>Cs</i>): serva me, servabo te <i>погов. Pt</i> выручи меня, а я выручу тебя (= manus manum lavat)."],
  ["servol-", "<big>servol-</big>\n\n<b>servol-</b> <i>v. l.</i>\n= servul-."],
  ["servula", "<big>servula</big>\n\n<b>servula, ae</b> <i>f</i> [<i>demin. к</i> serva]\nмолодая рабыня <i>C</i>."],
  ["servulicola", "<big>servulicola</big>\n\n<b>servulicola, ae</b> <i>f</i> [servulus + colo]\nневольничья проститутка <i>Pl</i>."],
  ["servulus", "<big>servulus</big>\n\n<b>servulus, i</b> <i>m</i> [<i>demin. к</i> servus]\nмолоденький раб <i>Pl, Ter, C, Pt, Ap</i>."],
  ["servus", "<big>servus</big>\n\n<b>I servus, a, um</b>\n<b>1)</b> служилый, подневольный (personae <i>Dig</i>); находящийся в полном подчинении, зависимый, подвластный (civitas <i>L</i>); рабский, невольничий: manus serva <i>O</i> отряд невольников; aqua serva <i>O</i> питьевая вода для рабов; serva capita (= servi) <i>L</i> рабы;\n<b>2)</b> <i>юр.</i> обременённый повинностями (praedium <i>C</i>).\n\n<b>II servus, i</b> <i>m</i>\nраб, невольник <i>Pl, Ter, C etc.</i>: s. publicus <i>C, Pt</i> общественный блюститель порядка || <i>перен.</i> слуга (cupiditatum <i>C</i>)."],
  ["sesama", "<big>sesama</big>\n\n<b>sesama, ae</b> <i>f</i> <i>PM</i>\n= sesamum."],
  ["sesaminus", "<big>sesaminus</big>\n\n<b>sesaminus, a, um</b> [sesamum]\nкунжутный (oleum <i>PM</i>)."],
  ["sesamum", "<big>sesamum</big>\n\n<b>sesamum (sesamon), i</b> <i>n</i> (<i>греч.</i>) <i>бот.</i>\nсезам, кунжут <i>Pl, CC, PM etc.</i>"],
  ["sescen-", "<big>sescen-</big>\n\n<b>sescen-</b> <i>v. l.</i>\n= sexcen-."],
  ["Sesculixes", "<big>Sesculixes</big>\n\n<b>Sesc–ulixes, is</b> <i>m</i> [sesqui]\nПолуторный Одиссей, <i>т. е.</i> сверхплут (<i>заглавие сатиры Варрона</i>) <i>PM</i>."],
  ["sescuncia", "<big>sescuncia</big>\n\n<b>sescuncia, ae</b> <i>f</i> [<i>из</i> *sesquiuncia]\n<b>1)</b> 1,5 унции = 1/8 асса, <i>т. е.</i> 40,93 <i>г</i> <i>или</i> 42,9 <i>мм</i> <i>CC, PM</i>;\n<b>2)</b> 1/8 часть (hereditatis <i>Dig</i>)."],
  ["sescuncialis", "<big>sescuncialis</big>\n\n<b>sescuncialis, e</b> [sescuncia]\nполуторадюймовый (crassitudo <i>PM</i>)."],
  ["sescunx", "<big>sescunx</big>\n\n<b>sescunx, uncis</b> <i>m</i> <i>PM, Scr</i>\n= sescuncia 2."],
  ["sescuplex", "<big>sescuplex</big>\n\n<b>sescuplex, icis</b> <i>adj.</i> <i>Q</i>\n= sesquiplex."],
  ["sescuplum", "<big>sescuplum</big>\n\n<b>sescuplum, i</b> <i>n</i>\nполуторное количество <i>PM, Q</i>."],
  ["sescuplus", "<big>sescuplus</big>\n\n<b>sescuplus, a, um</b> [sesqui I]\nполуторный (ratio <i>Cens</i>)."],
  ["sese", "<big>sese</big>\n\n<b>sese</b> <i>intens. acc. abl. к</i>\nsui."],
  ["seselis", "<big>seselis</big>\n\n<b>seselis, is</b> <i>f</i> (<i>греч.</i>) <i>бот.</i>\nжабрица (<i>Seseli</i>, L.) <i>C</i>."],
  ["sesima", "<big>sesima</big>\n\n<b>sesima, ae</b> <i>f</i> <i>PM</i>\n= sesamum."],
  ["Sesostris", "<big>Sesostris</big>\n\n<b>Sesostris</b> <i>и</i> <b>Sesosis, idis</b> <i>m</i>\nСесострид, <i>египетский царь середины XIV в до н. э., обычно отождествляемый с Рамзесом</i> <i>Lcn, T, PM</i>."],
  ["sesqualter", "<big>sesqualter</big>\n\n<b>sesqualter</b> <i>Boet</i>\n= sesquialter."],
  ["sesquatus", "<big>sesquatus</big>\n\n<b>sesquatus, a, um</b> [sesqui]\nнаходящийся в отношении 1 : 1,5 (numeri <i>Aug</i>)."],
  ["sesque-", "<big>sesque-</big>\n\n<b>sesque-</b> <i>v. l.</i>\n= sesqui-."],
  ["sesqui", "<big>sesqui</big>\n\n<b>I sesqui</b> <i>adv.</i> [semis + que]\nнаполовину, в полтора раза: s. major <i>C</i> наполовину (в полтора раза) больший.\n\n<b>II sesqui-</b>\n<i>приставка</i>\n<b>1.</b> <i>при сущ. и прил.</i> полуторный (sesqui–modius);\n<b>2.</b> <i>при неправильных дробях</i> имеющий в числителе на одну единицу больше, чем в знаменателе (<i>который выражается порядковым числ.</i>): sesqui–tertius равный 4/3; sesquioctavus содержащий 9/8."],
  ["sesquialter", "<big>sesquialter</big>\n\n<b>sesqui–alter, era, erum</b>\nполуторный <i>C, Vtr</i>."],
  ["sesquicullearis", "<big>sesquicullearis</big>\n\n<b>sesqui–cul(l)earis, e</b>\nёмкостью в 1,5 culeus <i>Col</i>."],
  ["sesquicyathus", "<big>sesquicyathus</big>\n\n<b>sesqui–cyathus, i</b> <i>m</i>\nполуторный cyathus <i>CC</i>."],
  ["sesquidecimus", "<big>sesquidecimus</big>\n\n<b>sesqui–decimus, a, um</b>\nравный 11/10 <i>Boet</i>."],
  ["sesquidigitalis", "<big>sesquidigitalis</big>\n\n<b>sesqui–digitalis, e</b>\nсодержащий 1,5 digitus (foramen <i>Vtr</i>)."],
  ["sesquidigitus", "<big>sesquidigitus</big>\n\n<b>sesqui–digitus, i</b> <i>m</i>\nполтора digiti (римских дюйма) <i>Vtr</i>."],
  ["sesquihora", "<big>sesquihora</big>\n\n<b>sesqui–hora, ae</b> <i>f</i>\nполтора часа <i>PJ</i>."],
  ["sesquijugerum", "<big>sesquijugerum</big>\n\n<b>sesqui–jugerum, i</b> <i>n</i>\nполтора югера <i>PM</i>."],
  ["sesquilibra", "<big>sesquilibra</big>\n\n<b>sesqui–libra, ae</b> <i>f</i>\nполтора римских фунта <i>Cato, Col</i>."],
  ["sesquimensis", "<big>sesquimensis</big>\n\n<b>sesqui–mensis, is</b> <i>m</i>\nполтора месяца <i>Vr</i>."],
  ["sesquimodius", "<big>sesquimodius</big>\n\n<b>sesqui–modius, i</b> <i>m</i>\nполтора модия <i>Vr, C</i>."],
  ["sesquinonus", "<big>sesquinonus</big>\n\n<b>sesqui–nonus, a, um</b>\nравный 10/9 <i>Boet</i>."],
  ["sesquiobolus", "<big>sesquiobolus</big>\n\n<b>sesqui–obolus, i</b> <i>m</i>\nполтора обола <i>PM</i>."],
  ["sesquioctavus", "<big>sesquioctavus</big>\n\n<b>sesqui–octavus, a, um</b>\nсодержащий девять восьмых <i>C</i>."],
  ["sesquiopera", "<big>sesquiopera</big>\n\n<b>sesqui–opera, ae</b> <i>f</i>\nполуторный рабочий день <i>Col</i>."],
  ["sesquiopus", "<big>sesquiopus</big>\n\n<b>sesqui–opus, eris</b> <i>n</i> <i>Pl</i>\n= sesquiopera."],
  ["sesquipedalis", "<big>sesquipedalis</big>\n\n<b>sesqui–pedalis, e</b>\n<b>1)</b> длиной в полтора римских фута (tigna <i>Cs</i>);\n<b>2)</b> <i>перен.</i> страшно длинный (dentes <i>Ctl</i>; verba <i>H</i>)."],
  ["sesquipedaneus", "<big>sesquipedaneus</big>\n\n<b>sesqui–pedaneus, a, um</b> <i>PM</i>\n= sesquipedalis."],
  ["sesquipes", "<big>sesquipes</big>\n\n<b>sesqui–pes, pedis</b> <i>m</i>\nполтора римских фута <i>Vr, Col</i>."],
  ["sesquiplaga", "<big>sesquiplaga</big>\n\n<b>sesqui–plaga, ae</b> <i>f</i>\nполтора удара <i>T</i>."],
  ["sesquiplaris", "<big>sesquiplaris</big>\n\n<b>sesquiplaris, is</b> <i>m</i> [sesquiplex]\nсолдат, получающий (в виде награды) полуторный паёк <i>Veg</i>."],
  ["sesquiplex", "<big>sesquiplex</big>\n\n<b>sesquiplex, plicis</b> <i>adj.</i> [sesqui + plico]\nполуторный, в полтора раза больший <i>C</i>."],
  ["sesquiplus", "<big>sesquiplus</big>\n\n<b>sesquiplus, a, um</b> <i>v. l.</i>\n= sescuplus."],
  ["sesquisenex", "<big>sesquisenex</big>\n\n<b>sesqui–senex, senis</b>\nдоживший до полуторной старости, <i>т. е.</i> престарелый <i>Poeta ap. Vr</i>."],
  ["sesquitertius", "<big>sesquitertius</big>\n\n<b>sesqui–tertius, a, um</b>\nсодержащий четыре трети <i>C</i>."],
  ["Sesquiulysses", "<big>Sesquiulysses</big>\n\n<b>Sesquiulysses</b> <i>v. l.</i>\n= Sesculixes."],
  ["sessibulum", "<big>sessibulum</big>\n\n<b>sessibulum, i</b> <i>n</i> [sedeo]\nседалище, сиденье, кресло, стул <i>Pl, Ap</i>."],
  ["sessilis", "<big>sessilis</big>\n\n<b>sessilis, e</b> [sedeo]\n<b>1)</b> удобный для сидения (tergum equi <i>O</i>);\n<b>2)</b> устойчивый (obba <i>Pers</i>);\n<b>3)</b> <i>бот.</i> сидячий, с короткими черешками, <i>т. е.</i> растущий вширь (lactuca <i>PM, M, Priap</i>)."],
  ["sessimonium", "<big>sessimonium</big>\n\n<b>sessimonium, i</b> <i>n</i> [sedeo]\nзаседание, собрание, <i>по др.</i> — обиталище (deorum <i>Vtr</i> — <i>v. l.</i>)."],
  ["sessio", "<big>sessio</big>\n\n<b>sessio, onis</b> <i>f</i> [sedeo]\n<b>1)</b> сидение (medicamentum sessionis deambulatio est <i>Aug</i>); способ сидения (sessiones quaedam contra naturam sunt <i>C</i>);\n<b>2)</b> долгое пребывание, бездействие (pigra <i>Ap</i>);\n<b>3)</b> заседание (pomeridiana <i>C</i>; dies sessionum <i>Dig</i>);\n<b>4)</b> место для сидения (sessiones gymnasiorum <i>C</i>);\n<b>5)</b> <i>анат.</i> впадина, углубление: s. oculorum <i>CA</i> глазные орбиты;\n<b>6)</b> сидячая ванна <i>CA</i>;\n<b>7)</b> <i>анат.</i> седалище <i>CA</i>;\n<b>8)</b> <i>CA</i> = intestinum rectum."],
  ["sessito", "<big>sessito</big>\n\n<b>sessito, avi, —, are</b> [<i>intens. к</i> sedeo]\nпостоянно <i>или</i> долго сидеть <i>C</i>: regio sessitandi <i>Ap</i> = nates."],
  ["sessiuncula", "<big>sessiuncula</big>\n\n<b>sessiuncula, ae</b> <i>f</i> [<i>demin. к</i> sessio]\nмаленькое заседание, небольшое собрание, кружок <i>C</i>."],
  ["sessor", "<big>sessor</big>\n\n<b>sessor, oris</b> <i>m</i> [sedeo]\n<b>1)</b> сидящий в театре, зритель <i>H</i>;\n<b>2)</b> сидящий на коне, ездок, всадник <i>Sen, Su, Vlg, Eccl</i>;\n<b>3)</b> житель (s. urbis <i>Nep</i>)."],
  ["sessorium", "<big>sessorium</big>\n\n<b>sessorium, i</b> <i>n</i> [sedeo]\n<b>1)</b> седалище, стул <i>CA</i>;\n<b>2)</b> жилище, обиталище (viperae <i>Pt</i>)."],
  ["sessus", "<big>sessus</big>\n\n<b>sessus, (us) </b> <i>m</i> [sedeo]\nсидение (locum sessui impertire <i>Ap</i>)."],
  ["sestertiarius", "<big>sestertiarius</big>\n\n<b>sestertiarius, a, um</b> [sestertius]\nценой не выше сестерция, <i>перен.</i> грошовый, ничтожный (homo <i>Pt</i>)."],
  ["sestertiolum", "<big>sestertiolum</big>\n\n<b>sestertiolum, i</b> <i>n</i> [<i>demin. к</i> sestertium]\nтысяча <i>или</i> сто тысяч сестерциев: s. bis deciens <i>M</i> два миллиона сестерциев."],
  ["sestertium", "<big>sestertium</big>\n\n<b>sestertium, i</b> <i>n</i> (<i>первоначально</i> <i>gen. pl. к</i> sestertius)\n<b>1)</b> <i>при колич. числ.</i> тысяча сестерциев (<i>см.</i> sestertius): decem sestertia (<i>или</i> s.) <i>C etc.</i> 10000 сестерциев; <i>слово</i> s. <i>часто при этом опускается</i>: hodie sua octingenta possidet <i>Pt</i> у него теперь состояние в 800 000 сестерциев;\n<b>2)</b> <i>при наречном числ.</i> 100 000 сестерциев: decies s. <i>Pt</i> 1 000 000 сестерциев; sestertii centies <i>C</i>, centies sestertio <i>Sen</i> <i>и</i> s. centies <i>Pt</i> 10 миллионов сестерциев; summa milies sestertii <i>C</i> сумма в 100 миллионов сестерциев."],
  ["sestertius", "<big>sestertius</big>\n\n<b>sestertius, i</b> <i>m</i> [semis + tertius]\n(<i>sc.</i> nummus)\n<b>1)</b> сестерций, <i>римск. серебряная монета = до 217 г. до н. э.</i> 2 1/2 <i>ассам <i>C</i>, позднее = 4 ассам <i>PM</i>; обозначался</i> LLS (<i>т. е.</i> libra libra semis <i>или сокращённо</i> IIS, <i>откуда</i> HS): duo milia sestertium (= sestertiorum) <i>C</i> 2 000 сестерциев; nummo sestertio за бесценок (vendere <i>L</i>); amplo sestertio за большие деньги (comparare aliquid <i>Sol</i>); sestertio nummo aestimandus <i>Sen</i> не стоящий и гроша; decem sestertii <i>Vr</i> десять сестерциев (<i>см.</i> sestertium);\n<b>2)</b> 2,5 римских фута (<i>мера глубины</i>) <i>Col</i>."],
  ["Sestianus", "<big>Sestianus</big>\n\n<b>Sestianus, a, um</b> [Sestius]\nсестов (секстов) <i>Ctl, C, Col</i>."],
  ["Sestias", "<big>Sestias</big>\n\n<b>Sestias, adis</b> <i>и</i> <b>Sestis, idis</b> <i>f</i>\nуроженка <i>или</i> жительница города Sestos, <i>т. е.</i> Hero, <i>возлюбленная Леандра</i> <i>St, Sid</i>."],
  ["Sestius", "<big>Sestius</big>\n\n<b>Sestius (Sextius), a, um</b>\nСестий (Секстий), <i>римск. nomen; наиболее известны</i>:\n<b>1)</b> L. S. Lateranus, <i>народный трибун в 376-367 гг. до н. э., вместе с Л. Лицинием Столоном провёл leges Liciniae-Sextiae, открывшие плебеям доступ к консульству</i> <i>L</i>;\n<b>2)</b> C. S. Calvinus, <i>в 123 г. до н. э. основал город Aquae Sestiae</i> (<i>ныне</i> Aix, <i>близ Марселя</i>) <i>L, VP etc.</i>;\n<b>3)</b> P. S., <i>квестор в 63 г. до н. э., друг Цицерона, противник Каталины и Клодия</i> <i>C</i>;\n<b>4)</b> L. S., <i>сын предыдущего, сначала противник Цезаря и сторонник заговора против него, впоследствии помилованный Августом и получивший консульство</i> <i>C</i>."],
  ["Sestos", "<big>Sestos</big>\n\n<b>Sestos (-us), i</b> <i>f</i>\nСеет, <i>город в Херсонесе Фракийском на Геллеспонте, против Абидоса на малоазиатском берегу</i> <i>L, Lcn</i>."],
  ["Sestus", "<big>Sestus</big>\n\n<b>Sestus, a, um</b> <i>и</i> <b>Sestiacus, a, um</b> [Sestos]\nсестосский <i>St, Aus</i>: puella Sesta <i>O</i> = Hero."],
  ["set", "<big>set</big>\n\n<b>set</b> <i>арх.</i>\n= sed II."],
  ["seta", "<big>seta</big>\n\n<b>seta</b> <i>v. l.</i>\n= saeta."],
  ["Setia", "<big>Setia</big>\n\n<b>Setia, ae</b> <i>f</i>\nСетия, <i>город вольсков в Латии, на вост. стороне Помптинских болот</i> (<i>ныне</i> Sezza) <i>L, M, Sil</i>."],
  ["setiger", "<big>setiger</big>\n\n<b>setiger</b> <i>v. l.</i>\n= saetiger."],
  ["setinum", "<big>setinum</big>\n\n<b>setinum, i</b> <i>n</i> [Setia]\n(<i>sc.</i> vinum) сетийское вино <i>St</i>."],
  ["Setinus", "<big>Setinus</big>\n\n<b>I Setinus, a, um</b> [Setia]\nсетийский (ager <i>C</i>).\n\n<b>II Setinus, i</b> <i>m</i>\nжитель города Setia <i>PM</i>."],
  ["setius", "<big>setius</big>\n\n<b>setius (secius)</b> <i>adv.</i> [<i>compar. к</i> secus II]\n<b>1)</b> иначе, не так (quia dixi s. quam velles? <i>Pl</i>);\n<b>2)</b> <i>с отрицанием</i> менее, меньше (non s. <i>V etc.</i>; haud s. <i>V etc.</i>): nihilo s. <i>Pl, Ter, Cs etc.</i> тем не менее, всё же;\n<b>3)</b> не так хорошо, хуже (loqui de aliquo <i>Sen, T</i>)."],
  ["setosus", "<big>setosus</big>\n\n<b>setosus, a, um</b> <i>v. l.</i>\n= saetosus."],
  ["seu", "<big>seu</big>\n\n<b>seu</b>\n= sive (<i>см.</i>)."],
  ["Seuthes", "<big>Seuthes</big>\n\n<b>Seuthes, is</b> <i>m</i>\nСевт:\n<b>1)</b> <i>вождь фракийск. племени одризов</i> (<i>с 424 г. до н. э.</i>) <i>Nep</i>;\n<b>2)</b> <i>вождь ряда фракийск. племён между Пропонтидой и Чёрным морем</i> (<i>с 399 г. до н. э.</i>) <i>Nep</i>."],
  ["sevacius", "<big>sevacius</big>\n\n<b>sevacius, i</b> <i>m</i> <i>v. l.</i>\n= sebacius."],
  ["sevalis", "<big>sevalis</big>\n\n<b>sevalis</b> <i>v. l.</i>\n= sebalis."],
  ["sevehor", "<big>sevehor</big>\n\n<b>se–vehor, vectus sum, vehi</b> <i>depon.</i>\nотъезжать в сторону, <i>перен.</i> отклоняться (praescripto gyro <i>Prp</i> —<i>abl.</i>)."],
  ["severe", "<big>severe</big>\n\n<b>severe</b> [severus]\nстрого, сурово, жёстко (graviter et s. <i>C</i>)."],
  ["severitas", "<big>severitas</big>\n\n<b>severitas, atis</b> <i>f</i> [severus]\nстрогость, суровость (censoria <i>C</i>; disciplinae <i>Q</i>): s. aurium <i>PJ</i> строгий отзыв (слушателя); severitatem adhibere in aliquo <i>C</i> быть строгим к кому-л."],
  ["severiter", "<big>severiter</big>\n\n<b>severiter</b> <i>Pl, Ap</i>\n= severe."],
  ["severitudo", "<big>severitudo</big>\n\n<b>severitudo, inis</b> <i>f</i>\nстрогость, суровость (frontis <i>Pl</i>; morum <i>Ap</i>)."],
  ["severus", "<big>severus</big>\n\n<b>I severus, a, um</b>\n<b>1)</b> строгий, cуровый (pater, judex <i>C</i>); строгий, серьёзный (genus dicendi <i>C</i>): interdiu severa <i>Pt</i> серьёзные дела (отложим) на дневные часы || суровый, неумолимый (disciplina, judicium <i>C</i>; poena <i>Sl</i>);\n<b>2)</b> закалённый, суровый (vita s. <i>C</i>); воздержный, умеренный (agricola <i>Lcr</i>): veste s. <i>J</i> просто (скромно) одетый;\n<b>3)</b> крепкий (vinum <i>H</i>);\n<b>4)</b> жуткий, страшный (silentia noctis <i>Lcr</i>; amnis Cocyti <i>V</i>).\n\n<b>II Severus, i</b> <i>m</i>\nСевер, <i>римск. cognomen; наиболее известны</i>:\n<b>1)</b> Cornelius S., <i>римск. поэт, автор не дошедшей до нас поэмы «Bellum Siculum»</i> <i>Q, O, Sen</i>;\n<b>2)</b> Cassius S., <i>оратор и сатирик, родился ок. 50 г. до н. э., умер в ссылке в 33 г. н. э.</i> <i>Q, Sen, PM, T</i>;\n<b>3)</b> L. Septimius S., <i>римск. император с 193 по 211 гг. н. э., отец Каракаллы</i> <i>Eutr, Lampr</i>;\n<b>4)</b> Alexander S., <i>племянник предыдущего, римск. император с 222 по 235 гг. н. э.</i> <i>Spart, Eutr</i>;\n<b>5)</b> Flavius Valerius S., <i>римск. император с 306 по 307 гг. н. э.</i> <i>Eutr</i>;\n<b>6)</b> Sulpicius S., <i>христианский писатель IV-V вв. н. э., автор «Historia Sacra».</i>"],
  ["sevi", "<big>sevi</big>\n\n<b>sevi</b> <i>pf. к</i>\nsero II."],
  ["sevir", "<big>sevir</big>\n\n<b>sevir, viri</b> <i>m</i> [sex + vir]\nсевир, член коллегии шести, член шестёрки <i>Capit</i>: seviri Augustales <i>Pt</i> августальные севиры (<i>на них была возложена организация культа императоров в италийских колониях Рима</i>)."],
  ["seviralis", "<big>seviralis</big>\n\n<b>seviralis, e</b> [sevir]\nсевиральный (ludi <i>Capit</i>)."],
  ["seviratus", "<big>seviratus</big>\n\n<b>seviratus, us</b> <i>m</i> [sevir]\nсевират, звание <i>или</i> должность севира <i>Pt</i>."],
  ["sevo", "<big>sevo</big>\n\n<b>sevo</b> <i>v. l.</i>\n= sebo."],
  ["sevoco", "<big>sevoco</big>\n\n<b>se–voco, avi, atum, are</b>\n<b>1)</b> звать в сторону (aliquem <i>Cs</i>); отзывать (plebem in Aventinum <i>C</i>): s. aliquid ad se <i>C</i> унести с собой, присвоить себе что-л.;\n<b>2)</b> отделять, удалять, отвлекать (aliquid ab aliquo <i>C</i>): s. animum ab aliqua re <i>C</i> воздерживаться от чего-л.; s. mentem a sensibus <i>C</i> отвлекать мысль от чувственных восприятий, <i>т. е.</i> мыслить абстрактно."],
  ["sevosus", "<big>sevosus</big>\n\n<b>sevosus, a, um</b> <i>v. l.</i>\n= sebosus."],
  ["sevum", "<big>sevum</big>\n\n<b>sevum, i</b> <i>n</i> <i>Pall</i>\n= sebum."],
  ["sex", "<big>sex</big>\n\n<b>sex</b> <i>adj. num. card.</i>\nшесть <i>Pl, Lcr etc.</i>"],
  ["Sex.", "<big>Sex.</big>\n\n<b>Sex.</b> <i>сокр. к</i>\nSextus."],
  ["sexagenarius", "<big>sexagenarius</big>\n\n<b>sexagenarius, a, um</b> [sexageni]\n<b>1)</b> содержащий шестьдесят, состоящий из шестидесяти, <i>напр.</i> 60-дюймовый (в сечении) (fistula <i>Frontin</i>) <i>или</i> оплачиваемый 60 000 сестерциев (procuratio <i>CJ</i>);\n<b>2)</b> шестидесятилетний <i>Q, Su</i>: sexagenarios de ponte <i>погов. Vr</i> шестидесятилетних стариков долой с моста (<i>так как римлян, достигших этого возраста, молодёжь, якобы, неохотно пропускала через контрольные мостки, по которым проходили избиратели к месту выборов</i>)."],
  ["sexageni", "<big>sexageni</big>\n\n<b>sexageni, ae, a</b> (<i>gen. pl.</i> um) <i>num. distr.</i> [sexaginta] (<i>редко</i> <i>sg. Aug</i>)\n<b>1)</b> по шестидесяти <i>C, L etc.</i>;\n<b>2)</b> шестьдесят <i>C etc.</i>"],
  ["sexagesima", "<big>sexagesima</big>\n\n<b>sexagesima, ae</b> <i>f</i> (<i>sc.</i> pars)\n1/60-ая <i>PM</i>."],
  ["sexagesimus", "<big>sexagesimus</big>\n\n<b>sexagesimus, a, um</b> <i>num. ord.</i> [sexaginta]\nшестидесятый <i>C etc.</i>"],
  ["sexagies", "<big>sexagies</big>\n\n<b>sexagies (sexagiens)</b> <i>adv. num.</i> [sexaginta]\nшестьдесят раз <i>C, Cs</i>."],
  ["sexaginta", "<big>sexaginta</big>\n\n<b>sexaginta</b> <i>num. card.</i>\nшестьдесят <i>C, L etc.</i>"],
  ["sexangulatus", "<big>sexangulatus</big>\n\n<b>sexangulatus, a, um</b> <i>Sol, Is</i>\n= sexangulus."],
  ["sexangulus", "<big>sexangulus</big>\n\n<b>sex–angulus, a, um</b>\nшестиугольный (cera <i>O</i>; figura <i>PM</i>)."],
  ["sexatrus", "<big>sexatrus</big>\n\n<b>sexatrus, uum</b> <i>f</i>\nшестой день после Ид <i>Vr</i>."],
  ["sexcenarius", "<big>sexcenarius</big>\n\n<b>sexcenarius, a, um</b> [sexcenti]\nсодержащий шестьсот, состоящий из шестисот, шестисотенный (cohortes <i>Cs</i>)."],
  ["sexceni", "<big>sexceni</big>\n\n<b>sexceni, ae, a</b> <i>num. distr.</i> [sexcenti]\nпо шестисот <i>C etc.</i>"],
  ["sexcenteni", "<big>sexcenteni</big>\n\n<b>sexcenteni, ae, a</b> <i>Col, Su</i>\n= sexceni."],
  ["sexcentesimus", "<big>sexcentesimus</big>\n\n<b>sexcentesimus, a, um</b> <i>num. ord.</i> [sexcenti]\nшестисотый <i>PM</i>."],
  ["sexcenti", "<big>sexcenti</big>\n\n<b>sexcenti, ae, a</b> <i>num. card.</i>\nшестьсот <i>Pl, Ter, C</i>."],
  ["sexcenties", "<big>sexcenties</big>\n\n<b>sexcenties (sexcentiens)</b> <i>adv. num.</i> [sexcenti]\nшестьсот раз <i>C, PJ</i>."],
  ["sexcentoplagus", "<big>sexcentoplagus</big>\n\n<b>sexcentoplagus, a, um</b> [sexcenti + plaga]\nполучающий шестьсот ударов <i>Pl</i>."],
  ["sexdecim", "<big>sexdecim</big>\n\n<b>sexdecim</b> <i>v. l.</i>\n= sedecim."],
  ["sexennis", "<big>sexennis</big>\n\n<b>sexennis, e</b> [sex + annus]\nшестилетний <i>Pl, PM, Pt</i>."],
  ["sexennium", "<big>sexennium</big>\n\n<b>sexennium, i</b> <i>n</i> [sexennis]\n<b>1)</b> шестилетие <i>Pl, C, L</i>: jam ante proximum s. <i>Ap</i> уже свыше 6-и лет тому назад;\n<b>2)</b> <i>редко</i> шестой год: sexennio imperii <i>AV</i> на шестом году правления."],
  ["sexies", "<big>sexies</big>\n\n<b>sexies (sexiens)</b> <i>adv. num.</i> [sex]\nшесть раз, шестикратно <i>C, L, VP</i>."],
  ["sexis", "<big>sexis</big>\n\n<b>sexis</b> <i>n indecl.</i>\nчисло шесть, шестёрка <i>Vtr</i>."],
  ["sexjugis", "<big>sexjugis</big>\n\n<b>sexjugis, e</b>\n= sejugis I, II."],
  ["sexpertitus", "<big>sexpertitus</big>\n\n<b>sex–pertitus, a, um</b> [partio]\nразделённый на тесть частей (divisio <i>Vtr</i>)."],
  ["sexprimi", "<big>sexprimi</big>\n\n<b>sex–primi, orum</b> <i>m</i>\nшесть главных членов городского совета (<i>в муниципиях и колониях</i>) <i>C</i>."],
  ["sexsiens", "<big>sexsiens</big>\n\n<b>sexsiens</b> <i>MA</i>\n= sexies."],
  ["sextadecimani", "<big>sextadecimani</big>\n\n<b>sexta–decimani, orum</b> <i>m</i> [sextus + decimus]\nсолдаты шестнадцатого легиона <i>T</i>."],
  ["sextani", "<big>sextani</big>\n\n<b>sextani, orum</b> <i>m</i> [sextus]\nсолдаты шестого легиона <i>PM, Mela</i>."],
  ["sextans", "<big>sextans</big>\n\n<b>sextans, antis</b> <i>m</i> [sex]\nсекстант:\n<b>1)</b> 1/6 асса = 2 унциям, <i>т. е.</i> 54,58 <i>г</i> <i>L, PM</i> : non esse sextantis <i>погов. C</i> не стоить и гроша;\n<b>2)</b> одна шестая (jugeri <i>Vr, Col</i>; cyathi <i>M, Su</i>): heres ex (in) sextante <i>C, Dig</i> получающий шестую долю наследства;\n<b>3)</b> <i>мат.</i> единица (<i>как</i> 1/6 <i>от шести, считавшегося numerus perfectus</i>) <i>Vtr</i>."],
  ["sextantalis", "<big>sextantalis</big>\n\n<b>sextantalis, e</b> [sextans]\nсоставляющий одну шестую (<i>от</i> 12), <i>т. е.</i> двухдюймовый (fusi <i>Vtr</i>)."],
  ["sextantarius", "<big>sextantarius</big>\n\n<b>sextantarius, a, um</b> [sextans]\nравный 2 унциям (asses sextantario pondere <i>PM</i>)."],
  ["sextariolus", "<big>sextariolus</big>\n\n<b>sextariolus, i</b> <i>m</i> [<i>demin. к</i> sextarius]\nсосуд ёмкостью в один секстарий <i>Su</i>."],
  ["sextarius", "<big>sextarius</big>\n\n<b>sextarius, i</b> <i>m</i> [sextus]\nсекстарий = 1/6 конгия, <i>т. е.</i> 0,547 <i>л</i> (<i>мера жидкостей и сыпучих тел</i>) <i>C, H, Col, PM</i>."],
  ["Sextianus", "<big>Sextianus</big>\n\n<b>Sextianus, a, um</b> <i>v. l.</i>\n= Sestianus."],
  ["sexticeps", "<big>sexticeps</big>\n\n<b>sexti–ceps, ipis</b> [caput] <i>арх.</i>\nшестой <i>Vr</i>."],
  ["sextilis", "<big>sextilis</big>\n\n<b>sextilis, e</b> [sextus]\n<i>о месяце</i> шестой: s. (mensis) <i>C, H, L</i> шестой месяц римского года (начиная с марта), <i>т. е.</i> август (<i>позднее</i> mensis Augustus <i>Macr</i>) || секстильный (Kalendae <i>C</i>; Nonae <i>L</i>)."],
  ["Sextilius", "<big>Sextilius</big>\n\n<b>Sextilius, a, um</b>\nСекстилий, <i>римск. nomen</i> <i>M</i>."],
  ["Sextius", "<big>Sextius</big>\n\n<b>Sextius, a, um</b> <i>v. l.</i>\n= Sestius."],
  ["sexto", "<big>sexto</big>\n\n<b>sexto</b> <i>adv.</i> [sextus]\n<b>1)</b> шесть раз <i>Treb</i>;\n<b>2)</b> (<i>sc.</i> die) на шестой день <i>Col</i>."],
  ["sextula", "<big>sextula</big>\n\n<b>sextula, ae</b> <i>f</i> [sextus]\n<b>1)</b> шестая часть унции = 4,548 <i>г</i> <i>или</i> 1/72 асса <i>Vr</i>;\n<b>2)</b> 1/72 часть (heres ex duabus sextulis <i>C</i>)."],
  ["sextum", "<big>sextum</big>\n\n<b>sextum</b> <i>adv. num.</i>\nв шестой раз (s. consul <i>C, L</i>)."],
  ["sextus", "<big>sextus</big>\n\n<b>I sextus, a, um</b> <i>adj. num. ord.</i> [sex]\nшестой <i>C, L etc.</i>\n\n<b>II Sextus, i</b> <i>m</i>\nСекст, <i>римск. praenomen; в сокращениях</i> Sex."],
  ["sexualis", "<big>sexualis</big>\n\n<b>sexualis, e</b> [sexus]\nженского пола, женский (manus <i>CA</i>)."],
  ["sexungula", "<big>sexungula</big>\n\n<b>sex–ungula, ae</b> <i>f</i>\n«имеющая шесть когтей», <i>т. е.</i> жадная, хищная женщина <i>Pl</i> (<i>v. l.</i>)."],
  ["sexus", "<big>sexus</big>\n\n<b>sexus, us</b> <i>m</i>\n<b>1)</b> пол (s. virilis <i>L</i>; s. femininus <i>Su</i>): liberi utriusque sexus <i>Su</i> дети обоего пола; <i>но</i>: homines utriusque sexus <i>PM</i> гермафродиты;\n<b>2)</b> половой орган <i>PM, Lad</i>."],
  ["sexvir-", "<big>sexvir-</big>\n\n<b>sexvir-</b> <i>v. l.</i>\n= sevir-."],
  ["si", "<big>si</big>\n\n<b>si</b> <i>conj.</i>\n<b>1)</b> если (бы), в случае если: naturam si sequemur ducem, nunquam aberrabimus <i>C</i> если мы будем следовать указаниям природы, мы никогда не будем заблуждаться; dies deficiat, si velim numerare (aliquid) <i>C</i> не хватило бы дня, если бы я захотел перечислить (что-л.); si scirem, dicerem <i>C</i> если бы я знал, то сказал бы;\n<b>2)</b> когда, всякий раз как: si ferrum se inflexerat, hostes evellere non poterant <i>Cs</i> когда дротики загибались, враги не могли вырвать их (из щитов);\n<b>3)</b> если только, если вообще (unde rationem habemus, si habemus? <i>C</i>);\n<b>4)</b> если даже (non possum hoc dicere, si cupiam <i>C</i>): quae si dubia essent <i>Sl</i> если даже это сомнительно;\n<b>5)</b> <i>в косвенных вопросах</i> ли, не ли (hostes exspectabant, si nostri paludem transirent <i>Cs</i>; visam, si domi est <i>Ter</i>): si quid opus esset <i>C</i> (сказать), есть ли надобность в чём-л.;\n<b>6)</b> (о) если бы: o mihi praeteritos referat si Juppiter annos! <i>V</i> о, если бы Юпитер вернул мне ушедшие годы!"],
  ["siagones", "<big>siagones</big>\n\n<b>siagones, um</b> <i>m</i> <i>и</i> <b>siagonitae, arum</b> <i>f</i> (<i>греч.</i>)\nчелюстные мышцы <i>CA</i>."],
  ["sibe", "<big>sibe</big>\n\n<b>sibe</b> <i>Q арх.</i>\n= sibi."],
  ["sibi", "<big>sibi</big>\n\n<b>sibi</b> <i>dat. к</i>\nsui."],
  ["sibilatio", "<big>sibilatio</big>\n\n<b>sibilatio, onis</b> <i>f</i> [sibilo]\nшипение <i>или</i> свист <i>Vlg, CA</i>."],
  ["sibilatus", "<big>sibilatus</big>\n\n<b>sibilatus, us</b> <i>m</i> <i>CA</i>\n= sibilatio."],
  ["sibilo", "<big>sibilo</big>\n\n<b>sibilo, —, —, are</b> [sibilus I]\n<b>1)</b> шипеть (anguis sibilat <i>O</i>); свистеть (aura sibilat <i>Lcn</i>; philomela sibilans <i>Sid</i>);\n<b>2)</b> освистывать, ошикать (aliquem <i>C, H</i>)."],
  ["sibilus", "<big>sibilus</big>\n\n<b>I sibilus, i</b> (<i>pl.</i> sibili, <i>поэт. тж.</i> sibila) <i>m</i>\n<b>1)</b> шипение (anguis <i>Lcn</i>); свистение, свист (venti <i>V</i>);\n<b>2)</b> освистывание, шиканье (sibilis explodere aliquem <i>C</i>).\n\n<b>II sibilus, a, um</b>\nшипящий, свистящий (ora anguium <i>V</i>)."],
  ["sibimet", "<big>sibimet</big>\n\n<b>sibi–met</b> <i>dat. к</i>\nsuimet (<i>см.</i> sui)."],
  ["sibina", "<big>sibina</big>\n\n<b>sibina, ae</b> <i>f</i> (<i>греч.</i>)\nохотничье копьё, рогатина <i>Enn, Tert</i>."],
  ["sibones", "<big>sibones</big>\n\n<b>sibones</b> <i>AG</i>\n= sibina."],
  ["Sibulla", "<big>Sibulla</big>\n\n<b>Sibulla, ae</b> <i>f</i> <i>VM, T</i>\n= Sibylla."],
  ["sibus", "<big>sibus</big>\n\n<b>sibus, a, um</b>\nхитрый (<i>ср.</i> persibus)."],
  ["Sibuzates", "<big>Sibuzates</big>\n\n<b>Sibuzates, (i)um</b> <i>n</i>\nсибусаты, <i>племя в Аквитании</i> (<i>в зап. Пиренеях</i>) <i>Cs</i>."],
  ["Sibylla", "<big>Sibylla</big>\n\n<b>Sibylla, ae</b> <i>f</i>\nСивилла, <i>прорицательница</i> (omnes feminae vates Sibyllae sunt a veteribus nuncupatae <i>Lact</i>); <i>из римских сивилл была наиболее известна Кумская, по преданию, пророчившая ещё Энею и дожившая до баснословного возраста; в Риме, со времени Тарквиния Гордого, хранились libri sibyllini, к которым в критические для государства времена обращались за советом</i> (libros adire, consulere <i>или</i> inspicere <i>Vr, C, V, O, AG</i>)."],
  ["Sibyllinus", "<big>Sibyllinus</big>\n\n<b>Sibyllinus, a, um</b> [Sibylla]\nсивиллин (vaticinatio, libri <i>C</i>)."],
  ["sibyna", "<big>sibyna</big>\n\n<b>sibyna, ae</b> <i>f</i> <i>v. l.</i>\n= sibina."],
  ["sic", "<big>sic</big>\n\n<b>sic</b> <i>adv.</i> [si + ce]\n<b>1)</b> так, таким (следующим) образом, вот как (ego s. existimo <i>C</i>): s. se res habet <i>C</i> так обстоит дело; s. est vita hominum <i>C</i> такова человеческая жизнь; ut (<i>или</i> sicut)... s. <i>C etc.</i> как... так <i>или</i> хотя... но;\n<b>2)</b> <i>в утвердит. ответах</i> да <i>Ter, C</i>;\n<b>3)</b> <i>в клятвах, пожеланиях и мольбах</i> да, пусть: s. cytiso pastae distendant ubera vaccae! <i>V</i> да раздуются (от молока) сосцы твоих коров, вскормленных клевером!; s. vos felices videam! <i>Pt</i> клянусь вашим счастьем!;\n<b>4)</b> вследствие этого, при таких обстоятельствах (s. ad supplicium Numitori Remus deditur <i>L</i>);\n<b>5)</b> просто так, без последствий (istud scelus non potest s. abire <i>C</i>);\n<b>6)</b> до того, до такой степени, настолько (eum s. dilexi, ut... <i>C</i>);\n<b>7)</b> так себе, более или менее: s. tenuiter (s. satis, s. hoc) <i>Ter</i> так себе, ничего, кое-как."],
  ["sica", "<big>sica</big>\n\n<b>sica, ae</b> <i>f</i>\n<b>1)</b> кинжал (sica confodere aliquem <i>Su</i>): apri dentium sicae <i>PM</i> острые как кинжалы клыки кабана;\n<b>2)</b> убийство <i>C</i>."],
  ["Sicambri", "<big>Sicambri</big>\n\n<b>Sicambri, orum</b> <i>m</i> <i>v. l.</i>\n= Sigambri."],
  ["Sicani", "<big>Sicani</big>\n\n<b>Sicani, orum</b> <i>m</i>\nсиканы:\n<b>1)</b> <i>иберийское племя, переселившееся, по преданию, с Пиренейского п-ова на берега Тибра, а оттуда — в Сицилию</i> <i>V, Sil, AG</i>;\n<b>2)</b> <i>поэт. V</i> = Siculi."],
  ["Sicania", "<big>Sicania</big>\n\n<b>Sicania, ae</b> <i>f</i> <i>PM, O, Just</i>\n= Sicilia."],
  ["Sicanis", "<big>Sicanis</big>\n\n<b>Sicanis, idis</b> <i>f</i> [Sicani]\nсиканийская, <i>перен.</i> сицилийская <i>O</i>."],
  ["Sicanius", "<big>Sicanius</big>\n\n<b>Sicanius (Sicanus), a, um</b> [Sicani]\nсиканийский, <i>перен.</i> сицилийский (fluctus <i>V</i>; apes <i>M</i>)."],
  ["sicarius", "<big>sicarius</big>\n\n<b>sicarius, i</b> <i>m</i> [sica]\nубийца <i>H, Q, Su</i>: accusare aliquem inter sicanos <i>C</i> обвинять кого-л. в убийстве."],
  ["Sicca", "<big>Sicca</big>\n\n<b>Sicca, ae</b> <i>f</i>\nСикка, <i>город в вост. Нумидии</i> <i>Sl, VM, PM</i>."],
  ["siccaneus", "<big>siccaneus</big>\n\n<b>siccaneus, a, um</b> [sicco]\nсухой (locus <i>Col</i>)."],
  ["siccanus", "<big>siccanus</big>\n\n<b>siccanus, a, um</b> <i>PM</i>\n= siccaneus."],
  ["siccatio", "<big>siccatio</big>\n\n<b>siccatio, onis</b> <i>f</i> [sicco]\nвысушивание, просушка <i>PM, Vlg</i>."],
  ["siccativus", "<big>siccativus</big>\n\n<b>siccativus, a, um</b> [sicco]\nвысушивающий (virtus <i>CA</i>)."],
  ["sicce", "<big>sicce</big>\n\n<b>sicce</b> <i>adv.</i> [siccus]\n<b>1)</b> в сухом месте, сухо (bovem stabulare <i>Col</i>);\n<b>2)</b> просто, сжато (dicere <i>C</i>)."],
  ["Siccenses", "<big>Siccenses</big>\n\n<b>Siccenses, ium</b> <i>m</i>\nжители города Sicca <i>Sl</i>."],
  ["siccesco", "<big>siccesco</big>\n\n<b>siccesco, —, —, ere</b> [siccus]\nсохнуть (terra siccescens <i>PM</i>; numquam siccescunt oculi <i>CC</i>)."],
  ["siccificus", "<big>siccificus</big>\n\n<b>siccificus, a, um</b> <i>Macr</i>\n= siccativus."],
  ["siccine?", "<big>siccine?</big>\n\n<b>siccine?</b> <i>v. l.</i>\n= sicine?"],
  ["siccitas", "<big>siccitas</big>\n\n<b>siccitas, atis</b> <i>f</i> [siccus]\n<b>1)</b> (<i>тж.</i> <i>pl.</i>) сухость (paludum <i>Cs</i>);\n<b>2)</b> засуха (s. et inopia frugum <i>L</i>);\n<b>3)</b> крепость, крепкое телосложение, коренастость (corporis <i>C</i>);\n<b>4)</b> сухость, сжатость, простота (orationis <i>C</i>)."],
  ["sicco", "<big>sicco</big>\n\n<b>sicco, avi, atum, are</b> [siccus]\n<b>1)</b> сушить, высушивать (herbas <i>O</i>); осушать (paludes <i>Su</i>; lacrimas <i>Prp, O</i>); выпивать (calices <i>H, J</i>; siccato avide poculo <i>Pt</i>);\n<b>2)</b> доить (ovem <i>O</i>); высасывать (ubera <i>V, PM</i>);\n<b>3)</b> выжимать (madidos capillos digitis <i>O</i>);\n<b>4)</b> сохнуть, пересыхать (stagna siccaverunt <i>Lact</i>)."],
  ["siccoculus", "<big>siccoculus</big>\n\n<b>sicc–oculus, a, um</b>\nимеющий сухие глаза <i>Pl</i>."],
  ["siccum", "<big>siccum</big>\n\n<b>siccum, i</b> <i>n</i> [siccus]\nсуша, земля (in sicco <i>L</i>; extrahere aliquem in s. <i>Pt</i>)."],
  ["siccus", "<big>siccus</big>\n\n<b>siccus, a, um</b>\n<b>1)</b> сухой (ager <i>H</i>): siccis oculis <i>H</i> без слёз, <i>т. е.</i> равнодушно; s. sanguinis <i>Sil</i> не обагрённый кровью; signa sicca <i>O</i> = созвездия обеих Медведиц (<i>так как они не опускаются ниже морского горизонта</i>); aquae siccae <i>M</i> = снег || пустой, без вина (poculum <i>Tib</i>); пересохший (vox <i>O</i>);\n<b>2)</b> крепкий, плотный (lignum <i>V</i>);\n<b>3)</b> трезвый, воздержный, непьющий (homo <i>Pl, C</i>);\n<b>4)</b> жаркий, знойный (dies <i>H</i>); иссушающий (fervor <i>O</i>; medicamentum <i>Scr</i>); испытывающий жажду (siti s. <i>Pl</i>);\n<b>5)</b> ясный, не покрытый облаками (luna <i>Prp, PM</i>);\n<b>6)</b> питающийся всухомятку <i>H</i>;\n<b>7)</b> чёрствый, сухой, неласковый (puella <i>O, M</i> );\n<b>8)</b> необразованный, малограмотный (puer <i>Su</i>);\n<b>9)</b> крепкий, коренастый, плотного телосложения (mulier <i>Pl</i>);\n<b>10)</b> сжатый, сухой, простой (orator <i>C</i>)."],
  ["Sicelis", "<big>Sicelis</big>\n\n<b>Sicelis, idis</b> <i>f</i>\nсицилианка <i>O</i>: Sicelides Musae <i>V</i> сицилийские музы (<i>покровительницы пастушеской поэзии, родиной которой считалась именно Сицилия, как отечество Феокрита</i>)."],
  ["sicelisso", "<big>sicelisso</big>\n\n<b>sicelisso</b> <i>v. l.</i>\n= sicilisso."],
  ["sicera", "<big>sicera</big>\n\n<b>sicera, ae</b> <i>f</i> (<i>евр.</i>)\nсикера (<i>пьянящий напиток</i>) <i>Vlg, Eccl</i>."],
  ["Sichaeus", "<big>Sichaeus</big>\n\n<b>Sichaeus, i</b> <i>m</i> <i>v. l.</i>\n= Sychaeus."],
  ["Sichem", "<big>Sichem</big>\n\n<b>Sichem</b> <i>f indecl.</i>\nСихем (Ш'хем), <i>город в Иудее</i> <i>Vlg</i>."],
  ["Sicilia", "<big>Sicilia</big>\n\n<b>Sicilia, ae</b> <i>f</i>\nСицилия <i>Pl, C etc.</i>"],
  ["sicilicula", "<big>sicilicula</big>\n\n<b>sicilicula, ae</b> <i>f</i> [<i>demin. к</i> sicilis]\nмаленький серп, кинжальчик <i>Pl</i>."],
  ["sicilicus", "<big>sicilicus</big>\n\n<b>sicilicus, i</b> <i>m</i> [sicilis]\n<b>1)</b> 1/4 унции, <i>т. е.</i> 6,822 <i>г</i> <i>Dig</i>;\n<b>2)</b> 1/48 доля (jugeri <i>Col</i>; hereditatis <i>Dig</i>; horae <i>PM</i>);\n<b>3)</b> 1/4 римского дюйма <i>PM</i>."],
  ["Siciliensis", "<big>Siciliensis</big>\n\n<b>Siciliensis, e</b> <i>C</i>\n= Siculus I."],
  ["sicilimentum", "<big>sicilimentum</big>\n\n<b>sicilimentum, i</b> <i>n</i> [sicilio]\nсжатые остатки колосьев <i>Cato</i>."],
  ["sicilio", "<big>sicilio</big>\n\n<b>sicilio, —, —, ire</b> [sicilis]\nжать серпом оставшиеся после уборки колосья (s. prata <i>Vr, PM</i>)."],
  ["sicilis", "<big>sicilis</big>\n\n<b>sicilis, is</b> <i>f</i> [seco]\nсерп <i>Enn, Vr, PM, AG</i>."],
  ["sicilisso", "<big>sicilisso</big>\n\n<b>sicilisso, —, —, are</b>\nвыражаться в сицилийской манере <i>Pl</i>."],
  ["sicin'", "<big>sicin'</big>\n\n<b>sicin' (sicine)?</b> [sic + ce + ne]\nтак ли?, так-то? (s. mihi obsequens es? <i>Pl</i>; s. me liquisti? <i>Ctl</i>)."],
  ["sicinnista", "<big>sicinnista</big>\n\n<b>sicinnista, ae</b> <i>m</i>\nучастник пляски в sicinnium (<i>см.</i>) <i>Acc ap. AG</i>."],
  ["sicinnium", "<big>sicinnium</big>\n\n<b>sicinnium, i</b> <i>n</i> (<i>греч.</i>)\nпляска сатиров в греческой драме <i>AG</i>."],
  ["siclus", "<big>siclus</big>\n\n<b>siclus, i</b> <i>m</i> (<i>евр.</i>)\nсикль, шекель, <i>серебряная монета</i> <i>Vlg, Eccl</i>."],
  ["Sicoris", "<big>Sicoris</big>\n\n<b>Sicoris, is</b> <i>m</i>\nСикорис, <i>приток реки Hiberus, ныне</i> Segre <i>Cs, PM, Lcn, Aus</i>."],
  ["sicubi", "<big>sicubi</big>\n\n<b>sicubi</b> [si + alicubi]\nесли где-л. (s. est certamen <i>L</i>); где бы ни <i>или</i> когда бы ни (s. eum satietas hominum ceperit <i>Ter</i>)."],
  ["sicula", "<big>sicula</big>\n\n<b>sicula, ae</b> <i>f</i> [<i>demin. к</i> sica]\nмаленький кинжал <i>Ctl</i>."],
  ["Siculi", "<big>Siculi</big>\n\n<b>Siculi, (or)um</b> <i>m</i>\nсицилийцы <i>Vr, Col, C etc.</i>"],
  ["Siculus", "<big>Siculus</big>\n\n<b>I Siculus, a, um</b>\nсицилийский: S. pastor <i>V</i> = Theocritus; S. tyrannus <i>O</i> = Phalaris; S. juvencus <i>Pers</i> = juvencus Phalaridis; Sicula conjunx <i>J</i> = Proserpina; Sicula virgo <i>St</i> = Siren; Sicula fuga <i>Prp</i> Сицилийское бегство (<i>Секста Помпея после победы Октавия в морском сражении у берегов Сицилии</i>).\n\n<b>II Siculus, i</b> <i>m</i>\nсицилиец <i>C etc.</i>"],
  ["sicunde", "<big>sicunde</big>\n\n<b>sicunde</b> [si + alicunde]\nесли откуда-л., откуда бы ни: s. potes <i>C</i> откуда только можешь, <i>т. е.</i> приложив все старания; s. hostis appareat <i>Su</i> если откуда-л. покажется неприятель."],
  ["Sicuris", "<big>Sicuris</big>\n\n<b>Sicuris, is</b> <i>m</i> <i>v. l.</i>\n= Sicoris."],
  ["sicut", "<big>sicut</big>\n\n<b>sic–ut (sicuti)</b> <i>adv.</i>\n<b>1)</b> как (s. mari, ita terra <i>Cs</i>): s. ait Ennius <i>C</i> как говорит Энний || так как (feci, s. lex jubebat <i>C</i>): s... sic (ita) <i>C etc.</i> как... так;\n<b>2)</b> как на самом деле, как в действительности (quamvis felices sitis, s. estis <i>C</i>);\n<b>3)</b> подобно тому как (aliquem s. alterum patrem diligere <i>C</i>);\n<b>4)</b> как если бы, словно, якобы, как будто: s. salutatum ad Ciceronem introire <i>Sl</i> проникнуть к Цицерону как будто для (под предлогом) приветствия;\n<b>5)</b> поскольку, так как (s. cras hic aderit, hodie non venerit <i>Pl</i>)."],
  ["Sicyon", "<big>Sicyon</big>\n\n<b>Sicyon, onis</b> (<i>abl.</i> e <i>и</i> i) <i>m, f</i>\nСикион, <i>город в сев.-вост. Пелопоннесе, к зап. от Коринфа</i> <i>Pl, C, L, O</i>."],
  ["Sicyonia", "<big>Sicyonia</big>\n\n<b>Sicyonia, orum</b> <i>n</i> (= Sicyonii calcei)\nсикионская обувь <i>Lcr</i>."],
  ["Sicyonius", "<big>Sicyonius</big>\n\n<b>I Sicyonius, a, um</b> [Sicyon]\nсикионский (calcei <i>C</i>; oleum <i>CA</i>).\n\n<b>II Sicyonius, i</b> <i>m</i>\nжитель города Sicyon <i>C</i>."],
  ["Sida", "<big>Sida</big>\n\n<b>Sida, ae</b> <i>f</i>\nСида, <i>прибрежный город в Памфилии</i> <i>C, L</i>."],
  ["sideralis", "<big>sideralis</big>\n\n<b>sideralis, e</b> [sidus]\nзвёздный (dies <i>Is</i>): s. scientia <i>PM, Ap</i> астрономия."],
  ["sideraticius", "<big>sideraticius</big>\n\n<b>sideraticius, a, um</b> [sideratio 2]\nпоражённый солнечным ударом (jumenta <i>Veg</i>)."],
  ["sideratio", "<big>sideratio</big>\n\n<b>sideratio, onis</b> <i>f</i> [sideror]\n<b>1)</b> взаимное положение небесных светил (<i>в астрологии</i>) <i>Eccl</i>;\n<b>2)</b> солнечный удар <i>PM</i>."],
  ["sidere", "<big>sidere</big>\n\n<b>I sidere</b> <i>inf. к</i>\nsido.\n\n<b>II sidere</b> <i>abl. sg. к</i>\nsidus."],
  ["sidereus", "<big>sidereus</big>\n\n<b>sidereus, a, um</b> [sidus]\n<b>1)</b> звёздный, усеянный звёздами (caelum <i>O</i>): arx mundi (<i>или</i> sedes) siderea <i>V</i> звёздный свод, небо; Canis s. <i>O</i> созвездие Большого Пса; dea siderea <i>Prp</i> = Nox;\n<b>2)</b> солнечный (lux <i>O</i>): aestus s. <i>O</i> солнечный жар; deus s. <i>M</i> = <i>Sol</i>;\n<b>3)</b> небесный, божественный (vates Maro <i>Col</i>);\n<b>4)</b> лучезарный (os <i>VF</i>); блестящий (clipeus <i>V</i>)."],
  ["sideris", "<big>sideris</big>\n\n<b>sideris</b> <i>gen. к</i>\nsidus."],
  ["siderizusa", "<big>siderizusa</big>\n\n<b>siderizusa, ae</b> <i>f</i>\n(aqua) (<i>греч.</i>) железистая вода <i>CA</i>."],
  ["sideror", "<big>sideror</big>\n\n<b>sideror, atus sum, ari</b> <i>depon.</i> [sidus]\nбыть поражаемым небесными светилами, <i>преим.</i> солнечным зноем (s. Caniculae exortu <i>PM</i>)."],
  ["Sidetae", "<big>Sidetae</big>\n\n<b>Sidetae, arum</b> <i>m</i>\nжители города Sida <i>L</i>."],
  ["Sidicini", "<big>Sidicini</big>\n\n<b>Sidicini, orum</b> <i>m</i>\nсидицины, <i>племя в Кампании с главн. городом Teanum</i> <i>C, L</i>."],
  ["Sidicinus", "<big>Sidicinus</big>\n\n<b>Sidicinus, a, um</b> [Sidicini]\nсидицинский (ager <i>L</i>; aequora <i>V</i>)."],
  ["sido", "<big>sido</big>\n\n<b>sido, sidi (sedi), sessum, ere</b>\n<b>1)</b> садиться (sub arbore <i>O</i>); опускаться (nebula sidit <i>L, PM</i>); оседать (gutta balsami sidens ad ima vasa <i>PM</i>; sidebant campi <i>Lcr</i>);\n<b>2)</b> застревать, засесть (glans sedit <i>L</i>): s. in ingenio <i>O</i> врезаться в память, запомниться || оставаться лежать (pallia siderunt in lecto <i>Prp</i>); сесть на мель (navis sidit <i>L, T, Prp</i>);\n<b>3)</b> проходить, пропадать, исчезать (metus sidit <i>T</i>)."],
  ["Sidon", "<big>Sidon</big>\n\n<b>Sidon, onis</b> <i>и</i> <b>onis</b> <i>f</i>\nСидон, <i>старейший город Финикии, метрополия Тира</i> <i>C, V, PM etc.</i>"],
  ["Sidones", "<big>Sidones</big>\n\n<b>Sidones, um</b> <i>n</i>\nсидоняне, <i>перен.</i> финикияне <i>QC, Is</i>."],
  ["Sidonia", "<big>Sidonia</big>\n\n<b>Sidonia, ae</b> <i>f</i>\nобласть Сидона <i>Just</i>."],
  ["Sidonicus", "<big>Sidonicus</big>\n\n<b>Sidonicus, a, um</b>\nсидонский <i>Sl</i>."],
  ["Sidonii", "<big>Sidonii</big>\n\n<b>Sidonii, orum</b> <i>m</i> <i>O, QC</i>\n= Sidones."],
  ["Sidonis", "<big>Sidonis</big>\n\n<b>Sidonis, idis</b> (<i>тж.</i> <b>do</b>)\n<b>1.</b> <i>adj. f</i> сидонская (concha <i>O</i>): S. tellus = Финикия;\n<b>2.</b> <i>subst. f</i> сидонянка, <i>т. е.</i> Европа, Дидона <i>или</i> её сестра Анна <i>O</i>."],
  ["Sidonius", "<big>Sidonius</big>\n\n<b>I Sidonius</b> <i>и</i> <b>Sidonicus, a, um</b>\n<b>1)</b> сидонский, <i>перен.</i> тирийский <i>или</i> финикийский: Sidonium ostrum <i>H</i> = пурпур; Sidonius amor <i>M</i> любовь к сидонянке (<i>т. е. любовь Юпитера к Европе</i>); Sidonia chlamys <i>V</i> (vestis <i>Prp</i>) сидонская (<i>т. е.</i> пурпурная) одежда;\n<b>2)</b> фиванский (<i>Фивы были основаны, по преданию, финикиянином Кадмом</i>): S. hospes <i>O</i> = Cadmus;\n<b>3)</b> карфагенский (Sidonii duces <i>Sil</i>).\n\n<b>II Sidonius, i</b> <i>m</i>\n(C. Sollius S. Apollinaris) Сидоний Аполлинарис, <i>христианско-латинский поэт <i>V</i> в. н. э.</i>"],
  ["sidus", "<big>sidus</big>\n\n<b>sidus, eris</b> <i>n</i>\n<b>1)</b> созвездие: s. Vergiliarum <i>L</i> = Pleiades; s. natalicium <i>C</i> звезда, «под которой родился человек», <i>т. е.</i> судьба, удел; s. Julium <i>H</i> звезда Юлиев, <i>т. е.</i> судьба Цезарей || <i>pl.</i> зодиак <i>V</i>;\n<b>2)</b> звезда, небесное светило: s. fervidum <i>H</i> = Canicula (Sirius); lucida sidera <i>H</i> = Dioscuri; s. aetherium <i>O</i> = солнце <i>или</i> луна; s. utrumque <i>Pt</i> восходящее и заходящее солнце <i>или</i> <i>PM</i> солнце и луна;\n<b>3)</b> <i>pl.</i> (<i>тж.</i> sidera mundi <i>Lcr</i>) звёздное небо, небесная высь (Hercules ad sidera missus <i>J</i>): sidera tangere <i>O</i> (ferire <i>H</i>) vertice <i>погов.</i> коснуться головой неба, <i>т. е.</i> чувствовать себя великим, вознестись над миром; ad sidera высоко вверх (jactare voces ad sidera <i>V</i>); ad sidera ferre <i>V</i> превозносить до небес;\n<b>4)</b> время года (s. hibernum <i>V</i>): mutato sidere <i>V</i> в другое время года;\n<b>5)</b> край, область, <i>тж.</i> климат (s. patrium <i>PM</i>): sub nostro sidere <i>J</i> в наших краях; s. occidentale <i>Amm</i> запад;\n<b>6)</b> день: s. brumale <i>O</i> самый короткий (зимний) день; sideribus dubiis <i>J</i> чуть свет;\n<b>7)</b> ночь: exactis sideribus <i>Prp</i> когда погасли звёзды, <i>т. е.</i> ночь прошла;\n<b>8)</b> ненастье, непогода, буря (grave sidus et imbrem vitare <i>O</i>): confectum s. <i>PM</i> конец бури;\n<b>9)</b> зной: radiis sideris niger <i>O</i> чёрный от солнечных лучей, <i>т. е.</i> загорелый || солнечный удар (pestifero sidere ictus <i>L</i>);\n<b>10)</b> светоч, светило, краса, украшение, слава (s. Fabiae gentis <i>O</i>)."],
  ["siem", "<big>siem</big>\n\n<b>siem</b> <i>арх. praes. conjct.</i>\n(= sim) <i>от</i> sum."],
  ["Sigalion", "<big>Sigalion</big>\n\n<b>Sigalion, onis</b> <i>m</i> (<i>греч.</i> sigao молчу)\nСигалион, <i>бог молчания</i> (<i>у египтян</i>) <i>Aus</i>."],
  ["Sigamber", "<big>Sigamber</big>\n\n<b>Sigamber, bra, brum</b> [Sigambri]\nсигамбрский <i>T</i>."],
  ["Sigambra", "<big>Sigambra</big>\n\n<b>Sigambra, ae</b> <i>f</i>\nженщина из племени сигамбров <i>O</i>."],
  ["Sigambri", "<big>Sigambri</big>\n\n<b>Sigambri (Sugambri), orum</b> <i>m</i>\nсигамбры, <i>германск. племя между реками Siga</i> (<i>ныне</i> Sieg) <i>и Рейном, к сев. от убиев</i> <i>Cs, H, T, Cld</i>."],
  ["Sigeius", "<big>Sigeius</big>\n\n<b>Sigeius, a, um</b> <i>O</i>\n= Sigeus."],
  ["Sigeum", "<big>Sigeum</big>\n\n<b>Sigeum, i</b> <i>n</i>\nСигей, <i>мыс и одноимённый с ним город у сев.-зап. оконечности Малой Азии, у входа в Геллеспонт</i> <i>C, L</i>."],
  ["Sigeus", "<big>Sigeus</big>\n\n<b>Sigeus, a, um</b>\nсигейский, <i>перен.</i> троянский (campi <i>V</i>; litora <i>O</i>) <i>или</i> римский (colonus <i>Sil</i>)."],
  ["sigillaria", "<big>sigillaria</big>\n\n<b>sigillaria, orum</b> (<i>abl.</i> us <i>или</i> ibus) <i>n</i>\n<b>1)</b> «праздник фигурок» (<i>последние дни Сатурналиев, когда римляне обменивались подарками в виде фигурок</i>) <i>Tib, Su, Spart, Macr</i>;\n<b>2)</b> фигурки, статуэтки <i>или</i> рельефные изображения, которые дарились в «праздник фигурок» <i>Sen, Spart</i>;\n<b>3)</b> римский рынок, на котором продавались изображения <i>Su, AG</i>."],
  ["sigillaricia", "<big>sigillaricia</big>\n\n<b>sigillaricia, orum</b> <i>n</i> [sigillum]\n(<i>sc.</i> dona) подарки к празднику sigillaria (<i>см.</i>) в виде маленьких фигурок из воска, гипса или глины <i>Spart</i>."],
  ["sigillaricium", "<big>sigillaricium</big>\n\n<b>sigillaricium, i</b> <i>n</i> <i>Vop</i>\n= sigillaria."],
  ["sigillaricius", "<big>sigillaricius</big>\n\n<b>sigillaricius, a, um</b> <i>Vop</i>\n= signatorius."],
  ["sigillatim", "<big>sigillatim</big>\n\n<b>sigillatim</b> <i>v. l.</i>\n= singillatim."],
  ["sigillatus", "<big>sigillatus</big>\n\n<b>sigillatus, a, um</b> [sigillum]\nукрашенный фигурками <i>или</i> рельефными изображениями (scyphi <i>C</i>)."],
  ["sigillo", "<big>sigillo</big>\n\n<b>sigillo, avi, —, are</b> [sigillum]\n<b>1)</b> запечатлевать, отмечать (aliquid <i>Eccl</i>);\n<b>2)</b> изгонять знаком креста (draconem <i>Eccl</i>)."],
  ["sigillum", "<big>sigillum</big>\n\n<b>sigillum, i</b> <i>n</i> [<i>demin. к</i> signum]\n<b>1)</b> маленькое скульптурное изображение, фигурка, статуэтка (ornare sigillis <i>O</i>; sigilla aedificiorum <i>PM</i>);\n<b>2)</b> печать (s. anuli impressum <i>QC</i>): liber signatus sigillis septem <i>Vlg</i> книга за семью печатями."],
  ["Sigimerus", "<big>Sigimerus</big>\n\n<b>Sigimerus, i</b> <i>m</i>\nСигимер, <i>предводитель херусков, отец Арминия</i> <i>VP</i>."],
  ["siglum", "<big>siglum</big>\n\n<b>siglum, i</b> <i>n</i>\nинициал, аббревиатурное обозначение <i>CJ</i>."],
  ["sigma", "<big>sigma</big>\n\n<b>sigma, atis</b> <i>n</i> (<i>греч.</i>)\nполукруглое обеденное ложе (<i>от древнего начертания строчной греческой буквы «сигма»</i>) <i><i>M, Lampr, Spart</i>; тж.</i> ванна полукруглой формы <i>Sid</i>."],
  ["signaculum", "<big>signaculum</big>\n\n<b>signaculum, i</b> <i>n</i> [signo]\n<b>1)</b> отличительный знак (oleo inscriptum <i>Eccl</i>);\n<b>2)</b> печать, печатка (anulus faberrimo signaculo <i>Ap</i>);\n<b>3)</b> знак креста <i>Eccl</i>."],
  ["signanter", "<big>signanter</big>\n\n<b>signanter</b> <i>Aus</i>\n= signate."],
  ["signate", "<big>signate</big>\n\n<b>signate</b> [signo]\nясно, выразительно (loqui <i>AG</i>); точно (proprie signateque verbum ponere <i>Aug</i>)."],
  ["signatio", "<big>signatio</big>\n\n<b>signatio, onis</b> <i>f</i>\nсовершение знамения (s. crucis <i>Tert</i>)."],
  ["signator", "<big>signator</big>\n\n<b>signator, oris</b> <i>m</i> [signo]\n<b>1)</b> прикладывающий печать (<i>преим.</i> к завещанию) как свидетель: signatores falsi <i>Sl</i> подделыватели завещания;\n<b>2)</b> брачный свидетель (venire cum signatoribus <i>J</i>)."],
  ["signatorius", "<big>signatorius</big>\n\n<b>signatorius, a, um</b> [signator]\nслужащий (снабжённый) печатью (anulus <i>VM, Dig</i>)."],
  ["signatus", "<big>signatus</big>\n\n<b>signatus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> signo;\n<b>2.</b> <i>adj.</i> ясный, выразительный (quid expressius aut signatius? <i>Tert</i>)."],
  ["Signia", "<big>Signia</big>\n\n<b>Signia, ae</b> <i>f</i>\nСигния, <i>город в Латии, к юго-вост. от Рима</i> (<i>ныне</i> Segni) <i>L, Sil</i>."],
  ["signifer", "<big>signifer</big>\n\n<b>I signifer, fera, ferum</b> [signum + fero]\nносящий знаки, украшенный изображениями (puppis <i>Lcn</i>; crater <i>VF</i>); звёздный (caelum <i>Lcn</i>): (orbis) s. <i>или</i> polus s. <i>Lcn, Amm, C, Sen</i> зодиак.\n\n<b>II signifer, feri</b> <i>m</i>\n<b>1)</b> несущий знамя, знаменосец <i>C, Cs, L etc.</i>;\n<b>2)</b> предводитель, руководитель (s. juventutis <i>C</i>)."],
  ["signifex", "<big>signifex</big>\n\n<b>signifex, ficis</b> <i>m</i> [signum + facio]\nрезчик, ваятель, скульптор <i>Ap, Sid, Eccl</i>."],
  ["significabilis", "<big>significabilis</big>\n\n<b>significabilis, e</b> [significo]\nзнаменующий, имеющий значение, обозначающий <i>Aug</i>: vox s. <i>Vr</i> членораздельные звуки."],
  ["significans", "<big>significans</big>\n\n<b>significans, antis</b>\n<b>1.</b> <i>part. praes. к</i> significo;\n<b>2.</b> <i>adj.</i> выразительный, ясный, наглядный (descriptio <i>Q</i>; vocabulum <i>PM</i>)."],
  ["significanter", "<big>significanter</big>\n\n<b>significanter</b> [significans]\nвыразительно, ясно, наглядно, вразумительно (dicere <i>Q, AG</i>)."],
  ["significantia", "<big>significantia</big>\n\n<b>significantia, ae</b> <i>f</i> [significo]\n<b>1)</b> выразительность, наглядность (<i>sc.</i> verborum <i>Q</i>);\n<b>2)</b> значимость, знаменательность <i>Eccl</i>."],
  ["significatio", "<big>significatio</big>\n\n<b>significatio, onis</b> <i>f</i> [significo]\n<b>1)</b> показание <i>или</i> объявление, извещение, уведомление: s. Gallorum <i>Cs</i> образ действий (поведение) галлов; litterarum s. <i>C</i> письменное извещение;\n<b>2)</b> подача знака, сигнализация (ignibus s. facta est <i>Cs</i>); внешнее выражение (voluntatis <i>C</i>); знамение (non dubiae deorum significationes <i>C</i>); предвестник (caeli murmur non dubiam habet significationem <i>PM</i>);\n<b>3)</b> знак, признак (benevolentiae <i>C</i>; virtutis <i>C</i>);\n<b>4)</b> знаки одобрения, одобрение (significationes et acclamationes multitudinis <i>L</i>);\n<b>5)</b> значение, смысл (scripti <i>C</i>; verborum <i>PM, Q, AG</i>);\n<b>6)</b> <i>ритор.</i> подчёркивание, логическое ударение <i>C</i>; выразительность, сила <i>rhH, Q</i>."],
  ["significative", "<big>significative</big>\n\n<b>significative</b>\nнаглядно, вразумительно <i>Aug</i>."],
  ["significativus", "<big>significativus</big>\n\n<b>significativus, a, um</b>\nобозначающий, указывающий (enuntiatio alicujus rei significativa <i>Dig</i>)."],
  ["significator", "<big>significator</big>\n\n<b>significator, oris</b> <i>m</i>\nделающий указания <i>Hier</i>."],
  ["significatorius", "<big>significatorius</big>\n\n<b>significatorius, a, um</b> <i>Tert</i>\n= significativus."],
  ["significatus", "<big>significatus</big>\n\n<b>significatus, us</b> <i>m</i> [significo]\n<b>1)</b> знак, примета, предвестник (tempestatum <i>Vtr, PM</i>);\n<b>2)</b> значение (s. duplex verbi <i>AG</i>);\n<b>3)</b> сигнал: bucina significatu longinquior <i>Ap</i> звук рожка слышен дальше (чем звук человеческого голоса);\n<b>4)</b> обозначение, именование <i>Eccl</i>."],
  ["significo", "<big>significo</big>\n\n<b>significo, avi, atum, are</b> [signum + facio]\n<b>1)</b> подавать знак, делать знаки (manu <i>PJ</i>; aliquid per gestum <i>O</i>; inter se <i>C</i>): s. silentium <i>C</i> дать знак молчать || сигнализировать (fumo atque ignibus <i>Cs</i>);\n<b>2)</b> изъявлять, высказывать, выражать (voluntatem <i>C</i>; iram vultu et voce <i>Q</i>): s. deditionem <i>Cs</i> изъявить намерение сдаться;\n<b>3)</b> отмечать, намекать, указывать (s. Zenonem <i>C</i>);\n<b>4)</b> обозначать (uno verbo res duas s. <i>C</i>); значить (verba quae idem significant <i>Q</i>);\n<b>5)</b> предвещать (futura <i>C</i>)."],
  ["Signinus", "<big>Signinus</big>\n\n<b>I Signinus, a, um</b> [Signia]\nсигнийский (vinum <i>CC</i>): (opus) Signinum <i>Col, Vtr, PM</i> сигнийский штукатурный состав.\n\n<b>II Signinus, i</b> <i>m</i>\nжитель города Signia <i>L</i>."],
  ["signitenens", "<big>signitenens</big>\n\n<b>signitenens, entis</b> <i>adj.</i> [signum + teneo]\nпокрытый созвездиями, звёздный <i>Enn ap. Vr</i>."],
  ["signo", "<big>signo</big>\n\n<b>signo, avi, atum, are</b> [signum]\n<b>1)</b> обозначать (sonos notis <i>C</i>): hoc titulo tua signetur imago <i>O</i> на твоём изображении пусть будет начертана следующая надпись || отмечать (humum limite <i>O</i>); выражать (aliquid verbis <i>Q</i>): s. moenia aratro <i>O</i> провести плугом линию для (городских) стен; s. humum pede certo <i>H</i> ходить твёрдыми шагами; aliquid in cortice s. <i>V</i> вырезать что-л. на коре; ceram figuris s. <i>O</i> покрывать воск изображениями, лепить из воска;\n<b>2)</b> запечатывать, снабжать печатью (volumina <i>H</i>; testamentum <i>Dig</i>; nomen pectore <i>O</i>); устанавливать (jura <i>Lcn</i>);\n<b>3)</b> чеканить (argentum signatum <i>C</i>);\n<b>4)</b> отмечать, отличать, награждать (aliquem honore <i>V</i>);\n<b>5)</b> замечать, наблюдать (aliquem oculis <i>V</i>)."],
  ["signum", "<big>signum</big>\n\n<b>signum, i</b> <i>n</i>\n<b>1)</b> знак, отметка, клеймо (s. imprimere pecori <i>V</i>); признак (doloris <i>C</i>; timoris <i>Cs</i>): s. pedis <i>O</i> след ноги; s. dare (mittere) alicui rei <i>C, Cs etc.</i> обнаруживать признак чего-л. || симптом <i>или</i> предвестник (morborum <i>V</i>);\n<b>2)</b> <i>воен.</i> сигнал, приказ, команда (s. proelii committendi dare <i>Cs</i>); <i>pl.</i> сигнальные трубы, рожки (signa canunt <i>или</i> concinunt <i>Cs, L etc.</i>): signo dato <i>T</i> по данному сигналу; s. dare receptui <i>C, L</i> <i>или</i> recipiendi <i>Cs</i> дать сигнал к отступлению;\n<b>3)</b> <i>воен.</i> значок (<i>как боевое знамя, преим. в виде орла</i>) (s. militare <i>Cs, PM</i>; legionis <i>Cs, L</i>): signa movere <i>L</i> (tollere <i>bAl</i>, convellere <i>L</i>, ferre <i>Cs</i>) двинуться, отправиться, выступить в поход; signa inferre alicui <i>или</i> in (adversus, contra) aliquem <i>Cs, L etc.</i> совершать нападение на (атаковать) кого-л.; signa conferre вступить в бой (cum aliquo <i>C, L</i>) <i>или</i> собраться, присоединиться (ad aliquem <i>L</i>); collatis signis <i>L</i> в открытом бою; signa relinquere <i>Sl</i> <i>или</i> a signis discedere <i>Cs</i> бежать с поля сражения, дезертировать; signa (con)vertere <i>Cs, L</i> сделать поворот, повернуться кругом; signa ad hostem convertere <i>L</i> оказать неприятелю сопротивление; sub signis <i>C, L, T</i> в строю;\n<b>4)</b> <i>воен.</i> отряд (<i>преим.</i> cohors <i>или</i> manipulus) (signa hostium <i>L</i>);\n<b>5)</b> <i>воен.</i> пароль, лозунг (s. «Felicitatis» <i>bAfr</i>);\n<b>6)</b> доказательство, довод (clarissimis signis crimina refellere <i>C</i>): s. rerum <i>Q</i> вещественное доказательство;\n<b>7)</b> изображение, изваяние, фигура, статуя (s. aeneum <i>C</i>; s. Jovis <i>C</i>; Veneris s. marmoreum <i>Pt</i>);\n<b>8)</b> печать (s. epistulae detrahere <i>C</i>; aliquid signo obsignare <i>Pl, C</i>);\n<b>9)</b> созвездие (signorum ortus et obitus <i>C</i>): duodecim signa <i>Pt</i> <i>или</i> signorum partes <i>Sen</i> 12 знаков зодиака;\n<b>10)</b> прозвище (alicui s. apponere <i>Vop</i>)."],
  ["sil", "<big>sil</big>\n\n<b>sil</b> <i>pf. к</i>\nsino II."],
  ["sil", "<big>sil</big>\n\n<b>sil, silis</b> <i>n</i>\n<b>1)</b> жёлтая охра <i>PM, Vtr</i>;\n<b>2)</b> <i>PM, CC</i> = seselis."],
  ["Sila", "<big>Sila</big>\n\n<b>Sila, ae</b> <i>f</i>\nСила, <i>лес в южн. Бруттии</i> <i>C, V</i>."],
  ["silaceus", "<big>silaceus</big>\n\n<b>silaceus, a, um</b> [sil]\nжёлтый как охра (color <i>PM</i>)."],
  ["silanus", "<big>silanus</big>\n\n<b>I silanus, i</b> <i>m</i> (<i>дорич.</i>)\nводомёт, фонтан (<i>вода которого извергалась обычно из уст каменного Силена</i>) <i>Lcr, CC</i>.\n\n<b>II Silanus, a, um</b>\n<b>1.</b> <i>adj.</i> [Sila] силанский (caseus <i>Eccl</i>);\n<b>2.</b> <i>subst.</i> Силан, <i>римск. cognomen в роде Юлиев</i> <i>C, Sl, L</i>."],
  ["Silarus", "<big>Silarus</big>\n\n<b>Silarus, i</b> <i>m</i>\nСилар, <i>пограничная река между Кампанией и Луканией, впадающая в Тирренское море</i> (<i>ныне</i> Silaro) <i>V, PM, Sil</i>."],
  ["silaus", "<big>silaus</big>\n\n<b>silaus, i</b> <i>m</i>\nсельдерей (<i>Apium graveolens</i>, L.) <i>PM</i>."],
  ["Silena", "<big>Silena</big>\n\n<b>Silena, ae</b> <i>и</i> <b>Silene, es</b> <i>f</i>\nСилена, <i>т. е.</i> курносая как Силен <i>Lcr</i>."],
  ["silenda", "<big>silenda</big>\n\n<b>silenda, orum</b> <i>n</i> [sileo]\nто, о чём не следует говорить, тайны (<i>см. тж.</i> silens) <i>L, QC</i>."],
  ["Silenicus", "<big>Silenicus</big>\n\n<b>Silenicus, a, um</b> [Silenus]\nсиленов (corymbi <i>PM</i>)."],
  ["silens", "<big>silens</big>\n\n<b>silens, entis</b> (<i>abl.</i> e <i>и</i> i; <i>gen. pl.</i> um <i>и</i> ium)\n<b>1.</b> <i>part. praes. к</i> sileo;\n<b>2.</b> <i>adj.</i> спокойный, тихий (ventus <i>Col</i>; nox <i>L, Tib, V, O</i>); безмолвный (lucus <i>V, Sil</i>): luna silenti <i>Cato, Col</i> в безлунную ночь, в новолуние, <i>т. е.</i> в конце месяца; sarmentum s. <i>Col</i> лоза, не дающая ещё побегов; ovum s. <i>Col</i> яйцо, в котором ещё не шевелится зародыш;\n<b>3.</b> <i>subst. m pl.</i>\n<b>1)</b> усопшие, мёртвые <i>V</i>: rex silentum <i>O</i> = Pluto;\n<b>2)</b> пифагорейцы (<i>как давшие обет молчания</i> <i>O</i>)."],
  ["silenter", "<big>silenter</big>\n\n<b>silenter</b>\nбезмолвно, бесшумно, молча <i>Vlg, Eccl</i>."],
  ["silentiarius", "<big>silentiarius</big>\n\n<b>silentiarius, i</b> <i>m</i> [silentium]\nсиленциарий, <i>раб, на обязанности которого лежало обеспечить соблюдение всей домашней прислугой полной тишины</i> <i>CTh, CJ</i>."],
  ["silentiose", "<big>silentiose</big>\n\n<b>silentiose</b> <i>Eccl</i>\n= silenter."],
  ["silentiosus", "<big>silentiosus</big>\n\n<b>silentiosus, a, um</b> [silentium]\nмолчаливый, безмолвный <i>Ap, Eccl</i>."],
  ["silentium", "<big>silentium</big>\n\n<b>silentium, i</b> <i>n</i> [sileo] (<i>тж.</i> <i>pl.</i>)\n<b>1)</b> безмолвие, молчание: simul ipsa silentia terrent <i>V</i> да и само безмолвие наполняет страхом || держание в тайне, неразглашение (alicujus rei <i>и</i> de aliqua re <i>C etc.</i>): ceteris s. fuit <i>C</i> прочие замолчали; s. tenere (obtinere) <i>L</i> хранить молчание, молчать; (cum) silentio <i>C, L</i> <i>или</i> per s. <i>L</i> молча, в молчании; silentio aliquid praeterire <i>C</i> (praetervehi <i>C</i>, transmittere <i>T</i>) обойти что-л. молчанием, умолчать о чём-л.; s. significare (fieri jubere) <i>C</i> приказать замолчать, предложить соблюдать тишину; s. facere <i>C</i> заставить молчать <i>или</i> <i>Pl</i> хранить молчание, молчать; silentio facto <i>Pt</i> когда воцарилось молчание;\n<b>2)</b> тишина (silentia ruris <i>O</i>): s. lunae <i>V</i> тишина лунной ночи;\n<b>3)</b> покой, спокойствие, бездействие (vitam silentio transire <i>Sl</i>; biduum s. fuit <i>L</i>)."],
  ["silentus", "<big>silentus</big>\n\n<b>silentus, a, um</b> <i>Laevius ap. AG</i>\n= silentiosus."],
  ["Silenus", "<big>Silenus</big>\n\n<b>Silenus, i</b> <i>m</i>\nСилён:\n<b>1)</b> <i>сын Меркурия или Пана, воспитатель и постоянный спутник Вакха, весёлый, вечно пьяный, тучный, курносый старик</i> <i>C, H</i> || <i>pl.</i> силены, <i>лесные божества, близкие к сатирам</i> <i>H, V, O, C, Ctl etc.</i>;\n<b>2)</b> <i>спутник Ганнибала, автор истории II Пунической войны</i> (<i>на греч. языке</i>) <i>C, Nep, L</i>."],
  ["sileo", "<big>sileo</big>\n\n<b>sileo, lui, —, ere</b>\n<b>1)</b> (<i>тж.</i> s. verbum facere <i>bH</i>) молчать, хранить молчание (de aliqua re <i>C etc.</i>); безмолвствовать, быть безмолвным, быть тихим, не издавать ни звука (silet nox <i>V</i>);\n<b>2)</b> умолкать: haec effata, silet <i>V</i> сказав это, (Дидона) умолкает || <i>перен.</i> бездействовать (silent leges inter arma <i>C</i>);\n<b>3)</b> умалчивать, обходить молчанием (s. merita alicujus <i>C</i>): res siletur <i>C</i> об этом не говорят."],
  ["siler", "<big>siler</big>\n\n<b>siler, eris</b> <i>n</i> <i>бот.</i>\nбересклет (<i>Evonymus Europaeus</i>, L.), <i>по др.</i>, ива (<i>разновидность</i>) <i>V, PM</i>."],
  ["silesco", "<big>silesco</big>\n\n<b>silesco, —, —, ere</b> [<i>inchoat. к</i> sileo]\nумолкать (silescunt turbae <i>Ter</i>; domus alta silescit <i>V</i>); утихать (venti silescunt <i>O</i>)."],
  ["silex", "<big>silex</big>\n\n<b>silex, icis</b> <i>m, f</i>\n<b>1)</b> твёрдый камень, булыжник, <i>преим.</i> кремень: sternere viam silici <i>L</i> вымостить дорогу камнем; silici scintillam excudere <i>V</i> высечь из кремня искру; motura verba silices <i>O</i> слова, способные растрогать (даже) камни;\n<b>2)</b> <i>поэт.</i> утёс, скала (silicum cavernae <i>Lcr</i>; Prometheus silici restrictus catena <i>Ctl</i>)."],
  ["Silianus", "<big>Silianus</big>\n\n<b>Silianus, a, um</b> [Silius]\nсилиев (negotium <i>C</i>)."],
  ["silicarius", "<big>silicarius</big>\n\n<b>silicarius, i</b> <i>m</i> [silex]\nмостильщик <i>Frontin</i>."],
  ["siliceo", "<big>siliceo</big>\n\n<b>siliceo, onis</b> <i>m</i> [silex]\nжестокосердный человек <i>Laevius ap. AG</i> (<i>v. l.</i>)."],
  ["silicernium", "<big>silicernium</big>\n\n<b>silicernium, i</b> <i>n</i>\n<b>1)</b> поминальная трапеза <i>Caec, Vr</i>;\n<b>2)</b> <i>род колбасы</i> <i>Eccl</i>;\n<b>3)</b> <i>бран.</i> старая развалина <i>Ter</i>."],
  ["silicernius", "<big>silicernius</big>\n\n<b>silicernius, i</b> <i>m</i> <i>Eccl</i>\n= silicernium 3."],
  ["siliceus", "<big>siliceus</big>\n\n<b>siliceus, a, um</b> [silex]\nкремнёвый (saxa <i>Vtr</i>)."],
  ["silicia", "<big>silicia</big>\n\n<b>silicia, ae</b> <i>f</i> (= fenum Graecum) <i>бот.</i>\nгреческое сено, треуголка (<i>Trigonella Fenum Graecum</i>, L.) <i>PM</i>."],
  ["silicis", "<big>silicis</big>\n\n<b>silicis</b> <i>gen. sg. к</i>\nsilex."],
  ["silico", "<big>silico</big>\n\n<b>silico, onis</b> <i>m</i> <i>v. l.</i>\n= siliceo."],
  ["silicula", "<big>silicula</big>\n\n<b>silicula, ae</b> <i>f</i> [<i>demin. к</i> siliqua]\nмаленький стручок <i>Vr</i>."],
  ["siliginarius", "<big>siliginarius</big>\n\n<b>siliginarius, i</b> <i>m</i> [siligo]\nбулочник, пирожник <i>Dig</i>."],
  ["siligineus", "<big>siligineus</big>\n\n<b>siligineus, a, um</b> [siligo]\nприготовленный из пшеничной муки, пшеничный (farina <i>Cato</i>; panis <i>Sen</i>; turdi <i>Pt</i>): esurienti panis cibarius s. videtur <i>погов. Vr</i> голодному чёрный хлеб кажется белым."],
  ["siligo", "<big>siligo</big>\n\n<b>siligo, inis</b> <i>f</i>\n<b>1)</b> пшеница высшего качества <i>Cato, Vr, Col</i>;\n<b>2)</b> пшеничная мука тонкого помола <i>PM, J</i>."],
  ["siliqua", "<big>siliqua</big>\n\n<b>siliqua, ae</b> <i>f</i>\n<b>1)</b> стручок <i>PM, V</i>; <i>pl.</i> бобовые растения <i>H</i>;\n<b>2)</b> <i>бот.</i> (<i>тж.</i> s. Graeca <i>Col</i>) сладкие рожки (<i>Ceratonia siliqua</i>, L.);\n<b>3)</b> <i>Col</i> = silicia;\n<b>4)</b> 1/144 унции, <i>т. е.</i> 0,189 <i>г</i> <i>Veg</i>;\n<b>5)</b> 1/24 солида (<i>см.</i> solidus II) <i>CJ</i>."],
  ["siliquastrum", "<big>siliquastrum</big>\n\n<b>siliquastrum, i</b> <i>n</i> <i>бот. предпол.</i>\nстручковый перец, <i>по др.</i>, багряник (<i>Cereis siliquastrum</i>, L.) <i>PM</i>."],
  ["siliquatarius", "<big>siliquatarius</big>\n\n<b>siliquatarius, i</b> <i>m</i> [siliquaticum]\nсборщик подати с продаваемых товаров <i>Eccl</i>."],
  ["siliquaticum", "<big>siliquaticum</big>\n\n<b>siliquaticum, i</b> <i>n</i> [siliqua 5]\nподать <i>или</i> пошлина в размере 1/24 стоимости <i>Eccl</i>."],
  ["siliquor", "<big>siliquor</big>\n\n<b>siliquor, —, ari</b> <i>depon.</i> [siliqua]\nобразовать стручки <i>PM</i>."],
  ["silis", "<big>silis</big>\n\n<b>silis</b> <i>gen. sg. к</i>\nsil."],
  ["Silius", "<big>Silius</big>\n\n<b>Silius, a, um</b>\nСилий, <i>римск. nomen; наиболее известны</i>:\n<b>1)</b> P. S. Nerva, <i>пропретор в Вифинии и Понте</i> <i>C</i>;\n<b>2)</b> T. S., <i>военный трибун при Цезаре</i> <i>Cs</i>;\n<b>3)</b> C. S., <i>консул в 13 г. н. э., римск. полководец в Германии и Галлии</i> <i>T</i>;\n<b>4)</b> C. S. Italicus, <i>поэт второй половины 1 в. н. э., автор поэмы «Punica»</i> (<i>о II Пунической войне</i>) <i>PJ, M</i>."],
  ["sillographus", "<big>sillographus</big>\n\n<b>sillographus, i</b> <i>m</i> (<i>греч.</i>)\nсиллограф, <i>т. е.</i> пародист <i>или</i> сатирик <i>Amm</i>."],
  ["sillybus", "<big>sillybus</big>\n\n<b>sillybus, i</b> <i>m</i> <i>v. l.</i>\n= sittybos."],
  ["silo", "<big>silo</big>\n\n<b>silo, onis</b> <i>m</i> [silus]\nкурносый <i>или</i> плосконосый человек <i>Vr</i>."],
  ["silphium", "<big>silphium</big>\n\n<b>silphium (silpium), i</b> <i>n</i> (<i>греч.</i>) <i>Cato, PM</i>\n= laserpitium."],
  ["silua", "<big>silua</big>\n\n<b>silua, ae</b> <i>f</i> (<i>в три слога</i> <i>H</i>)\n= silva."],
  ["Silures", "<big>Silures</big>\n\n<b>Silures, um</b> <i>m</i>\nсилуры, <i>народность в Британии</i> <i>T, PM</i>."],
  ["silurus", "<big>silurus</big>\n\n<b>silurus, i</b> <i>m</i> (<i>греч.</i>) <i>предпол.</i>\nсом (<i>Siturus Glanis</i>, L.) <i>LM, PM, J etc.</i>"],
  ["silus", "<big>silus</big>\n\n<b>silus, a, um</b>\nс приплюснутым <i>или</i> вздёрнутым носом, курносый <i>C</i>."],
  ["silva", "<big>silva</big>\n\n<b>silva, ae</b> <i>f</i>\n<b>1)</b> лес (densa <i>C</i>; pinae <i>V, Sil</i>); silvarum dea <i>O</i> = Diana; silvarum numina <i>O</i> = Fauni et Satyri; silvae publicae <i>L</i> государственные леса;\n<b>2)</b> парк, сад (signa in silva disposita <i>C</i>; inter silvas Academi <i>H</i>);\n<b>3)</b> <i>тж. pl.</i> растения, деревья (viridem ab humo silvam convellere <i>V</i>);\n<b>4)</b> множество, обилие, масса (rerum sententiarumque <i>C</i>);\n<b>5)</b> материал, материя (s. atque materia omnium argumentationum <i>C</i>);\n<b>6)</b> черновые записи, черновик (materiam, quam ex tempore scribunt,... silvam vocant <i>Q</i>);\n<b>7)</b> <i>перен.</i> дебри, <i>т. е.</i> мирская суета (in silva manere <i>Eccl</i>)."],
  ["Silvanus", "<big>Silvanus</big>\n\n<b>Silvanus, i</b> <i>m</i> [silva]\n<b>1)</b> Сильван, <i>бог лесов, полей и стад, покровитель сельских жителей</i> <i>C, V, H</i>;\n<b>2)</b> <i>pl.</i> лесные и полевые божества <i>O, Lcn</i>."],
  ["silvaticus", "<big>silvaticus</big>\n\n<b>silvaticus, a, um</b> [silva]\n<b>1)</b> служащий для обработки дерева (falx <i>Cato, Col</i>);\n<b>2)</b> лесной, живущий <i>или</i> растущий в лесу, дикий (porcus <i>Pt</i>; pirus <i>Vr</i>)."],
  ["silvesco", "<big>silvesco</big>\n\n<b>silvesco, —, —, ere</b> [silva]\n<b>1)</b> превращаться в дерево, деревенеть (vitis silvescit <i>C</i>);\n<b>2)</b> густо разрастаться (crines silvescentes <i>Eccl</i>)."],
  ["silvester", "<big>silvester</big>\n\n<b>silvester, tris, tre</b> <i>SenT, Col, PM</i>\n= silvestris."],
  ["silvestris", "<big>silvestris</big>\n\n<b>silvestris, tre</b> [silva]\n<b>1)</b> лесной, находящийся <i>или</i> растущий в лесу, дикорастущий (faba <i>PM</i>; oliva <i>O</i>); umbra s. <i>O</i> лесная тень; materia s. <i>L</i> срубленный лес, дрова;\n<b>2)</b> дикий (belua <i>C</i>; homo <i>H</i>; mel <i>PM</i>);\n<b>3)</b> степной, пастушеский, сельский (Musa <i>Lcr, V</i>);\n<b>4)</b> лесистый, поросший лесом (mons <i>Vr, C</i>; locus <i>C</i>)."],
  ["Silvia", "<big>Silvia</big>\n\n<b>Silvia, ae</b> <i>f</i>\n<b>1)</b> Rea S. (Ilia) — <i>см.</i> Rea;\n<b>2)</b> Сильвия, <i>дочь Тиррена, сына Атиса, миф. основателя Этрурии</i> <i>V</i>."],
  ["silvicola", "<big>silvicola</big>\n\n<b>silvicola, ae</b> <i>m</i> [silva + colo II]\nобитатель лесов, лесной житель (Faunus s. <i>V</i>)."],
  ["silvicultrix", "<big>silvicultrix</big>\n\n<b>silvicultrix, icis</b> <i>f</i> [silva + colo II]\nживущая в лесу, обитательница лесов (cerva s. <i>Ctl</i>)."],
  ["silvifragus", "<big>silvifragus</big>\n\n<b>silvifragus, a, um</b> [silva + frango]\nломающий леса (flabra <i>Lcr</i>)."],
  ["silviger", "<big>silviger</big>\n\n<b>silviger, gera, gerum</b> [silva + gero]\nпокрытый (поросший) лесом, лесистый (montes <i>PM</i>)."],
  ["Silvius", "<big>Silvius</big>\n\n<b>Silvius, i</b> <i>m</i>\nСильвий, <i>сын Энея и Лавинии, родоначальник династии Сильвиев в Альбе Лонге</i> <i>V</i>."],
  ["silvosus", "<big>silvosus</big>\n\n<b>silvosus, a, um</b> [silva]\n<b>1)</b> лесистый (saltus <i>L</i>);\n<b>2)</b> ветвистый, густой (arbor <i>Vtr</i>)."],
  ["silvula", "<big>silvula</big>\n\n<b>silvula, ae</b> <i>f</i> [<i>demin. к</i> silva]\nлесок, рощица <i>Col, Sid</i>."],
  ["sim", "<big>sim</big>\n\n<b>sim</b> <i>1 л. sg. praes. conjct. к</i>\nsum."],
  ["sima", "<big>sima</big>\n\n<b>sima, ae</b> <i>f</i> <i>архит.</i>\n«каблучок» (<i>вид облома</i>) <i>Vtr</i>."],
  ["Simbruinus", "<big>Simbruinus</big>\n\n<b>Simbruinus</b> <i>или</i> <b>Simbruvinus, a, um</b> [Simbruvium]\nсимбрувийский (stagna <i>T</i>)."],
  ["Simbruvium", "<big>Simbruvium</big>\n\n<b>Simbruvium, i</b> <i>n</i>\nСимбрувий, <i>местность и озеро в области эквов, к вост. от Рима</i> <i>Sil</i>."],
  ["simeitu", "<big>simeitu</big>\n\n<b>simeitu</b> <i>v. l.</i>\n= simitu."],
  ["simia", "<big>simia</big>\n\n<b>sīmia, ae</b> <i>f</i>\n<b>1)</b> обезьяна <i>C, H etc.</i>;\n<b>2)</b> <i>перен.</i> глупый подражатель <i>PJ</i>."],
  ["simil", "<big>simil</big>\n\n<b>simil</b> <i>арх. Naev</i>\n= similis."],
  ["simila", "<big>simila</big>\n\n<b>I simila, ae</b> <i>f</i>\nтончайшая пшеничная мука <i>CC, M, Vlg</i>.\n\n<b>II Simila, ae</b> <i>f</i> <i>v. l.</i>\n= Stimula."],
  ["similagineus", "<big>similagineus</big>\n\n<b>similāgineus, a, um</b> [similago]\nприготовленный из муки тончайшего помола (panis <i>Vlg</i>)."],
  ["similago", "<big>similago</big>\n\n<b>similāgo, inis</b> <i>f</i> <i>Cato, PM, Vlg, Eccl</i>\n= simila I."],
  ["similatio", "<big>similatio</big>\n\n<b>similātio, ōnis</b> <i>f</i> [similis]\nсходство <i>Aug</i>."],
  ["simile", "<big>simile</big>\n\n<b>simile, is</b> <i>n</i> [similis]\n<b>1)</b> уподобление, сравнение, аналогия (simili uti <i>C</i>): (et) similia <i>или</i> similiaque <i>Q</i> и тому подобное;\n<b>2)</b> подобие <i>Q</i>."],
  ["similigenus", "<big>similigenus</big>\n\n<b>simili–genus, a, um</b>\nподобного рода, сходный <i>CA</i>."],
  ["similis", "<big>similis</big>\n\n<b>similis, e</b> (<i>superl.</i> simillimus, <i>арх.</i> simillumus)\nпохожий, подобный, сходный (s. alicujus <i>или</i> alicui rei <i>C etc.</i>; homines moribus inter se similes <i>C</i>): veri (<i>реже</i> vero) s. <i>C, Cs, L etc.</i> правдоподобный; s. aliqua re, <i>реже</i> in aliquid сходный чём-л. (в чём-л.) (s. in speciem Junonis <i>Ap</i>): Mercurio s. vocemque coloremque (<i>acc. gr.</i>) <i>V</i> похожий на Меркурия и голосом, и цветом кожи; s. ac (atque) <i>или</i> ut (tamquam) si <i>Ter, Vr, C etc.</i> такой же как, подобный; simile aliquid a me atque a ceteris factum est <i>C</i> я поступил так же, как и другие. — <i>См. тж.</i> simile."],
  ["similitas", "<big>similitas</big>\n\n<b>similitās, ātis</b> <i>f</i> <i>Caec, Vtr</i>\n= similitudo."],
  ["similiter", "<big>similiter</big>\n\n<b>similiter</b> [similis]\nподобно, сходным образом (s. atque uno modo <i>C</i>)."],
  ["similitudo", "<big>similitudo</big>\n\n<b>similitūdo, inis</b> <i>f</i> [similis]\n<b>1)</b> подобие, сходство (inter aliquos <i>и</i> cum aliquo <i>C etc.</i>): ad similitudinem alicujus rei <i>Ap</i> наподобие чего-л.; veri s. <i>C, L</i> правдоподобие, вероятность;\n<b>2)</b> уподобление, аналогия (similitudine uti <i>L</i>): valetudinis s. <i>C</i> сравнение с (физическим) здоровьем;\n<b>3)</b> <i>ритор.</i> единообразие, однообразие (s. est satietatis mater <i>C</i>)."],
  ["similo", "<big>similo</big>\n\n<b>similo, —, —, āre</b>\nуподоблять (per omnia similari alicui <i>Vlg</i>)."],
  ["simininus", "<big>simininus</big>\n\n<b>sīminīnus, a, um</b> [simia]\nобезьяний <i>Ap</i>."],
  ["simiolus", "<big>simiolus</big>\n\n<b>sīmiolus, ī</b> <i>m</i> [<i>demin. к</i> simius]\nмаленькая обезьяна, обезьянка <i>C</i>."],
  ["simitu", "<big>simitu</big>\n\n<b>simītū</b> <i>adv.</i> [simul + itus] <i>арх. LM, Pl</i>\n= simul I."],
  ["simius", "<big>simius</big>\n\n<b>sīmius, ī</b> <i>m</i> <i>Ph, H, Sen, M</i>\n= simia."],
  ["simma", "<big>simma</big>\n\n<b>simma, atis</b> <i>n</i> <i>v. l.</i>\n= sigma."],
  ["simo", "<big>simo</big>\n\n<b>I simo, avi, atum, are</b> [simus I]\nприплюснуть (nares <i>LM</i>); <i>тех.</i> сглаживать (projecturas <i>Vtr</i>).\n\n<b>II Simo, onis</b> <i>m</i>\nСимон, <i>имя старика в латинской комедии</i> <i>Pl, Ter, H</i>."],
  ["Simois", "<big>Simois</big>\n\n<b>Simois, entis (-entos)</b>\nСимоэнт:\n<b>1)</b> <i>приток Скамандра в Троаде</i> <i>V, H, O, PM</i>;\n<b>2)</b> <i>река в Эпире</i> <i>V</i>."],
  ["Simonides", "<big>Simonides</big>\n\n<b>Simonides, is</b> <i>m</i>\nСимонид, <i>родом с о-ва Кеос, греч. поэт, создатель мнемотехники</i> (<i>556-467 гг. до н. э.</i>) <i>C, Ph, Q</i>."],
  ["Simonideus", "<big>Simonideus</big>\n\n<b>Simonideus, a, um</b> [Simonides]\nсимонидов (lacrimae <i>Ctl</i>)."],
  ["simpla", "<big>simpla</big>\n\n<b>simpla, ae</b> <i>f</i> [simplus]\nординарная цена <i>Vr, Dig</i>."],
  ["simplaris", "<big>simplaris</big>\n\n<b>simplaris, e</b> [simplus]\nполучающий обычный (не усиленный) паёк (armaturae <i>Veg</i>)."],
  ["simplarius", "<big>simplarius</big>\n\n<b>simplarius, a, um</b>\nпростой, <i>т. е.</i> обусловленный гарантией в размере лишь суммы сделки (venditio <i>Dig</i>)."],
  ["simplex", "<big>simplex</big>\n\n<b>simplex, icis</b> <i>adj.</i> [<i>одного корня с</i> semel]\n<b>1)</b> простой, несоставной (tibia <i>H</i>; verba <i>Q</i> — <i>ср.</i> 6); чистый, несмешанный (aqua <i>O</i>); неразветвлённый <i>или</i> прямой (iter <i>C</i>); несложный (causa <i>C</i>); ординарный, несдвоенный (ordo <i>L</i>): plus vice simplici <i>H</i> не раз;\n<b>2)</b> неизменный, постоянный (fortuna <i>Nep</i>);\n<b>3)</b> простой, обыкновенный, обычный (mors <i>Sl, Sen etc.</i>);\n<b>4)</b> прямой, безусловный (necessitudo <i>C</i>);\n<b>5)</b> независимый, самостоятельный (quaedam sunt in rebus simplicia, quaedam copulata <i>C</i>);\n<b>6)</b> естественный, безыскусственный, простой (cibus <i>PM</i>; modus Aeolius <i>Ap</i>); прямодушный, правдивый, честный (vir apertus et s. <i>C</i>; verba <i>Su</i> —<i>ср.</i> 1): mente simplicissima <i>Pt</i> в душевной простоте, простодушно, без задних мыслей;\n<b>7)</b> безобидный, незлобивый (cervus <i>PM</i>)."],
  ["simplicitas", "<big>simplicitas</big>\n\n<b>simplicitas, atis</b> <i>f</i> [simplex]\n<b>1)</b> простота, несложность (<i>sc.</i> primordiorum <i>Lcr</i>);\n<b>2)</b> простота, нетребовательность (anti quorum in cibo capiendo <i>VM</i>);\n<b>3)</b> естественность, безыскусственность (puerilis <i>L</i>); прямодушие, прямота, честность (priorum <i>J</i>)."],
  ["simpliciter", "<big>simpliciter</big>\n\n<b>simpliciter</b> [simplex]\n<b>1)</b> просто, только, исключительно (s. ad amicitiam petendam venire <i>L</i>);\n<b>2)</b> безыскусственно, просто, естественно (loqui <i>C</i>);\n<b>3)</b> напрямик, прямо, открыто, простодушно (s. et candide <i>C</i>)."],
  ["simplico", "<big>simplico</big>\n\n<b>simplico, —, —, are</b>\nпроизводить нечто простое: s. radicem <i>Pall</i> пускать лишь один корень."],
  ["simplum", "<big>simplum</big>\n\n<b>simplum, i</b> <i>n</i>\nпростое, недвойное, ординарное <i>C, L, Dig</i>: s. solvere <i>Pl</i> уплатить одну лишь взятую в долг сумму (без надбавок)."],
  ["simplus", "<big>simplus</big>\n\n<b>simplus, a, um</b> <i>Eccl</i>\n= simplex 3."],
  ["simpuium", "<big>simpuium</big>\n\n<b>simpuium, i</b> <i>n</i> [<i>из</i> <i>греч.</i> sipye] <i>C v. l.</i>\n— simpuvium."],
  ["simpulator", "<big>simpulator</big>\n\n<b>simpulator, oris</b> <i>m</i> [simpulum]\nсобутыльник <i>Eccl</i>."],
  ["simpulum", "<big>simpulum</big>\n\n<b>simpulum, i</b> <i>n</i>\nразливная ложка <i>или</i> ковш (<i>для жертвенных возлияний вина</i>): excitare fluctus in simpulo <i>погов. C</i> поднимать бурю в стакане воды."],
  ["simpuvium", "<big>simpuvium</big>\n\n<b>simpuvium, i</b> <i>n</i>\nжертвенная чаша для возлияний <i>C, Vr, J, Eccl</i>."],
  ["simul", "<big>simul</big>\n\n<b>I simul</b> <i>adv.</i> (<i>тж.</i> s. una <i>Pl etc.</i>)\nодновременно, в одно и то же время, совместно с (s. abire <i>C</i>); сразу (non ali qua res una, sed plures s. continuae <i>Q</i>): salve, s. autem vale <i>Pl</i> здравствуй, но тут же и прощай; duo s. bella <i>L</i> две одновременных войны; s. cum вместе с (s. cum occasu solis <i>Sl</i>; voluntas s. cum spe <i>C</i>; s. esse cum aliquo <i>C</i>); s... s. <i>или</i> s... et (ac, atque) как ..., так, отчасти... отчасти (s. terra s. mari <i>T</i>; s. spernere, s. metuere <i>L</i>; s. inflatus exacerbatusque <i>L</i>).\n\n<b>II simul</b> <i>praep. cum abl.</i>\nвместе, совместно с (s. nobis <i>O</i>).\n\n<b>III simul</b> <i>и</i> <b>s. ac</b> (<i>или</i> <b>s. atque</b>), <b>s. ut (primum)</b> <i>conj.</i>\nкак только, лишь только (s. aliquid audiero, scribam ad te <i>C</i>): s. in arido consteterunt, in hostes impetum fecerunt <i>Cs</i> как только (римляне) вышли на сушу, они атаковали неприятеля; Cingetorix, s. atque de Caesaris adventu cognitum est, ad eum venit <i>Cs</i> как только стало известно о прибытии Цезаря, Цингеториг пришёл к нему; s. nubes condidit lunam <i>H</i> когда туча закрыла луну."],
  ["simulac", "<big>simulac</big>\n\n<b>simulac</b> <i>v. l.</i>\n= simul III."],
  ["simulacrum", "<big>simulacrum</big>\n\n<b>simulacrum, i</b> <i>n</i> [simulo]\n<b>1)</b> образ, изображение, статуя (deorum <i>C</i>; Herculis <i>L</i>);\n<b>2)</b> подобие, видимость (pristinae dignitatis <i>C</i>): nec omnino s. aliquod ac vestigium <i>Cs</i> ни малейшего подобия; s. pugnae <i>L, V</i> <i>или</i> belli <i>Lcr</i> военные манёвры; ad s. alicujus rei <i>Su etc.</i> наподобие чего-л.;\n<b>3)</b> тень (мертвеца), призрак, привидение (Creusae <i>V</i>; parentis <i>O</i>);\n<b>4)</b> сновидение (simulacra inania somni <i>O</i>);\n<b>5)</b> характеристика, описание свойств (s. viri <i>L</i>);\n<b>6)</b> (<i>в философии Эпикура</i>) отображение, форма, познавательный образ <i>Lcr</i>."],
  ["simulamen", "<big>simulamen</big>\n\n<b>simulamen, inis</b> <i>n</i> [simulo]\nподражание, воспроизведение, изображение <i>O, Aus</i>."],
  ["simulamentum", "<big>simulamentum</big>\n\n<b>simulamentum, i</b> <i>n</i> [simulo]\nпритворство, обман <i>AG, Eccl</i>."],
  ["simulans", "<big>simulans</big>\n\n<b>simulans, antis</b>\n<b>1.</b> <i>part. praes. к</i> simulo;\n<b>2.</b> <i>adj.</i> (<i>встреч. тк. в</i> <i>compar.</i>) подражающий, подражательный (vocum simulantior ales <i>O</i> —<i>sc.</i> psittacus)."],
  ["simulanter", "<big>simulanter</big>\n\n<b>simulanter</b> <i>Ap</i>\n= simulate."],
  ["simulate", "<big>simulate</big>\n\n<b>simulate</b> [simulo]\nдля вида, притворно, лицемерно (sive ex animo sive s. <i>C</i>)."],
  ["simulatio", "<big>simulatio</big>\n\n<b>simulatio, onis</b> <i>f</i> [simulo]\nвидимость, притворство, лицемерие, симулирование (s. virtutis, amicitiae <i>C</i>): s. timoris <i>Cs</i> притворный страх; per simulationem <i>или</i> simulatione alicujus rei <i>Cs etc.</i> под видом (под предлогом) чего-л.; simulatione rei publicae <i>Brutus ap. C</i> якобы в интересах государства; spei specie ac simulatione <i>C</i> внушая ложные надежды; hostium simulatione <i>C</i> якобы для борьбы с врагом."],
  ["simulato", "<big>simulato</big>\n\n<b>simulato</b> <i>Lampr</i>\n= simulate."],
  ["simulator", "<big>simulator</big>\n\n<b>simulator, oris</b> <i>m</i> [simulo]\n<b>1)</b> подражающий, подражатель (alicujus rei <i>O, Cld</i>);\n<b>2)</b> показывающий вид, притворяющийся, прикидывающийся, притворщик, лицемер (s. alicujus rei <i>Sl, T</i> <i>или</i> in aliqua re <i>C</i>)."],
  ["simulatorie", "<big>simulatorie</big>\n\n<b>simulatorie</b>\nпритворно, мнимо <i>Eccl</i>."],
  ["simulatorius", "<big>simulatorius</big>\n\n<b>simulatorius, a, um</b>\nпритворный, лицемерный <i>Eccl</i>."],
  ["simulatque", "<big>simulatque</big>\n\n<b>simulatque</b> — <i>см.</i>\nsimul III."],
  ["simulatrix", "<big>simulatrix</big>\n\n<b>simulatrix, icis</b> <i>f</i> [simulator]\nсовершающая превращения, волшебница (s. Circe <i>St</i>)."],
  ["simulatus", "<big>simulatus</big>\n\n<b>simulatus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> simulo;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> притворный, лицемерный (lacrimae <i>L</i>; amicitia <i>Cs, Nep</i>; verba <i>V</i>): simulata mente <i>V</i> притворно, неискренно;\n<b>2)</b> мнимый: latices simulati fontis Averni <i>V</i> вода, зачерпнутая якобы из Авернского источника."],
  ["simulo", "<big>simulo</big>\n\n<b>simulo, avi, atum, are</b> [similis]\n<b>1)</b> делать похожим, уподоблять (aliquid alicui rei <i>V etc.</i>): Minerva simulata Mentori <i>C</i> Минерва, принявшая вид Ментора;\n<b>2)</b> изображать, воспроизводить, представлять (cupressum <i>H</i>; vultum alicujus <i>H</i>); подражать (s. Catonem <i>H</i>): s. anum <i>O</i> принять вид старухи;\n<b>3)</b> притворяться, прикидываться, симулировать (in amicitia nihil fictum est, nihil simulatum <i>C</i>): s. mortem <i>Ter, C</i> притвориться мёртвым; s. lacrimas <i>O</i> притвориться плачущим; nec simulatum quidquam potest esse diurnum <i>C</i> никакое притворство не может продолжаться долго. — <i>См. тж.</i> simulatus."],
  ["simultas", "<big>simultas</big>\n\n<b>simultas, atis</b> <i>f</i> [similis]\nсоперничество, вражда: habere (gerere) simultatem <i>C</i> <i>или</i> esse in simultate cum aliquo <i>Nep</i> быть во враждебных отношениях с кем-л.; sanata simultate <i>Ap</i> по улажении спора."],
  ["simulter", "<big>simulter</big>\n\n<b>simulter</b> <i>Pl</i>\n= similiter."],
  ["simulus", "<big>simulus</big>\n\n<b>simulus, a, um</b> [<i>demin. к</i> simus]\nс вздёрнутым носиком <i>Lcr</i>."],
  ["simus", "<big>simus</big>\n\n<b>I simus, a, um</b>\nплосконосый <i>или</i> курносый (puer sima nare <i>M</i>; capella <i>V</i>).\n\n<b>II simus</b> <i>1 л. pl. praes. conjct. к</i>\nsum."],
  ["sin", "<big>sin</big>\n\n<b>sin</b> <i>conj.</i> [<i>из</i> si ne]\nесли же, а если: sin autem (<i>или</i> s. vero) <i>Pl, Ter, C etc.</i> но если; s. minus (aliter, secus) <i>Pl, C etc.</i> если же нет, в противном же случае."],
  ["sinapi", "<big>sinapi</big>\n\n<b>sinapi</b> <i>indecl.</i> <i>и</i> <b>sinapis, is</b> (<i>acc.</i> im, <i>abl.</i> i <i>или</i> e) <i>f</i> (<i>греч.</i>)\nгорчица <i>Enn, Pl, CC, Col etc.</i>"],
  ["sinapismus", "<big>sinapismus</big>\n\n<b>sinapismus, i</b> <i>m</i> (<i>греч.</i>)\nгорчичный компресс, горчичник <i>CA</i>."],
  ["sinapizo", "<big>sinapizo</big>\n\n<b>sinapizo (-isso), —, —, are</b> (<i>греч.</i>)\nлечить горчичниками (aliquam partem corporis <i>CA</i>)."],
  ["sincere", "<big>sincere</big>\n\n<b>sincere</b> [sincerus]\n<b>1)</b> хорошо, честно (administrare provincias <i>Pl, C etc.</i>);\n<b>2)</b> искренно, откровенно, открыто (pronuntiare <i>Cs</i>; loqui <i>C</i>)."],
  ["sinceritas", "<big>sinceritas</big>\n\n<b>sinceritas, atis</b> <i>f</i> [sincerus]\n<b>1)</b> сохранность, невредимость, здоровье (s. et animi et corporis <i>VM</i>);\n<b>2)</b> неиспорченность, чистота (vitae <i>VM</i>);\n<b>3)</b> искренность, честность (verborum <i>AG</i>)."],
  ["sinceriter", "<big>sinceriter</big>\n\n<b>sinceriter</b> <i>AG, CJ etc.</i>\n= sincere."],
  ["sincerus", "<big>sincerus</big>\n\n<b>sincerus, a, um</b>\n<b>1)</b> настоящий, неподдельный, подлинный (Stoicus <i>AG</i>); чистый, несмешанный (aqua <i>Sen</i>): sincerum vas <i>H</i> чистый (хорошо вымытый) сосуд, <i>но тж.</i> нетронутый, гладкий (sincerum vas incrustare <i>H</i>) || ненарумяненный, ненакрашенный (genae <i>O</i>); естественный, полноценный (fucata a sinceris secernere <i>C</i>);\n<b>2)</b> непорочный, целомудренный, девственный (Minerva <i>O</i>); незапятнанный (fama <i>AG</i>); безмятежный (gaudium <i>L, Just</i>);\n<b>3)</b> здоровый, нетронутый, целый, невредимый (corpus <i>O, AG</i>; pars, <i>sc.</i> corporis <i>O</i>); здравый (prudens sincerumque judicium <i>C</i>);\n<b>4)</b> честный, прямой, правдивый, беспристрастный, искренний (Thucydides rerum gestarum pronuntiator s. <i>C</i>);\n<b>5)</b> чистый, исключительный: sincerum equestre proelium <i>L</i> сражение при участии одной лишь конницы;\n<b>6)</b> не затронутый внешними влияниями, сохранивший старинные нравы и обычаи (populus <i>T</i>)."],
  ["sincinium", "<big>sincinium</big>\n\n<b>sincinium, i</b> <i>n</i> [cano]\nпение без сопровождения, соло <i>Is</i>."],
  ["sincipitamentum", "<big>sincipitamentum</big>\n\n<b>sincipitamentum, i</b> <i>n</i> [sinciput]\nполголовы (sincipitamenta porcina <i>Pl</i>)."],
  ["sinciput", "<big>sinciput</big>\n\n<b>sinciput, pitis</b> <i>n</i> [semi + caput]\n<b>1)</b> полголовы, передняя часть головы (fumosum s. <i>Pers</i>; sincipita verrina <i>PM</i>);\n<b>2)</b> <i>презр.</i> голова, башка: non tibi sanum est s. <i>Pl</i> ты не в своём уме."],
  ["sinciputamentum", "<big>sinciputamentum</big>\n\n<b>sinciputamentum, i</b> <i>n</i> <i>v. l.</i>\n= sincipitamentum."],
  ["Sinda", "<big>Sinda</big>\n\n<b>Sinda, ae</b> <i>f</i>\nСинда, <i>город в вост. Писидии</i> <i>PM</i>."],
  ["Sindes", "<big>Sindes</big>\n\n<b>Sindes, ae</b> <i>и</i> <b>is</b> <i>и</i> <b>Sindus, i</b> <i>m</i>\nрека Синд (= Инд) <i>T, PM</i>."],
  ["Sindi", "<big>Sindi</big>\n\n<b>Sindi, orum</b> <i>m</i>\nсинды, <i>одно из скифских племён на берегах Palus Maeotis</i> <i>VF</i>."],
  ["sindon", "<big>sindon</big>\n\n<b>sindon, onis</b> <i>f</i> (<i>греч.</i>)\nрод тонкой хлопчатобумажной ткани, муслин <i>или</i> кисея, <i>тж.</i> платье из этой ткани <i>M, Aus, Vlg</i>."],
  ["sine", "<big>sine</big>\n\n<b>I sine</b> <i>praep. cum abl.</i>\nбез (s. spe <i>C</i>; malum nullum; est sine aliquo bono <i>PM</i>): s. dubio <i>C</i> без сомнения s. ullo periculo <i>Ter</i> без всякой опасности; homo s. re <i>C</i> неимущий человек; s. sanguine <i>C</i> без кровопролития; lectio s. delectatione <i>C</i> неинтересное чтение; cum fratre an s.? <i>C</i> с братом или без (него)?\n\n<b>II sine</b> <i>imper. к</i>\nsino II."],
  ["singilio", "<big>singilio</big>\n\n<b>singilio, onis</b> <i>m</i>\nсингилион, <i>род простой короткой одежды</i> <i>Treb</i>."],
  ["singillatim", "<big>singillatim</big>\n\n<b>singillatim</b> <i>v. l.</i>\n= singulatim."],
  ["singlariter", "<big>singlariter</big>\n\n<b>singlariter</b> <i>Lcr</i>\n= singulariter."],
  ["singularis", "<big>singularis</big>\n\n<b>I singularis, e</b> [singuli]\n<b>1)</b> отдельный, одиночный (homo <i>Cs, C</i>); уединённый, изолированный (locus <i>Su</i>);\n<b>2)</b> единоличный: imperium singulare <i>C</i> единовластие, самодержавие; certamen singulare <i>Eutr</i> единоборство;\n<b>3)</b> личный, частный (odium <i>C</i>);\n<b>4)</b> особый, особенный, своеобразный (sunt in te quaedam singularia <i>C</i>);\n<b>5)</b> единственный в своём роде, замечательный, превосходный, несравненный, беспримерный (vir, amicus <i>C</i>); исключительный (crudelitas <i>Cs</i>); неслыханный, небывалый (audacia <i>C</i>);\n<b>6)</b> <i>грам.</i> единственный (numerus <i>Vr, Q</i>);\n<b>7)</b> <i>юр.</i> исключительный, чрезвычайный (jus <i>Dig</i>).\n\n<b>II singularis, is</b> <i>m</i>\n<b>1)</b> воин отборной конницы <i>T</i>;\n<b>2)</b> (<i>в императорскую эпоху</i>) чиновник для поручении при префекте провинции <i>CJ</i>.\n\n<b>III singularis, is</b> <i>f</i>\nвдова <i>Eccl</i>."],
  ["singularitas", "<big>singularitas</big>\n\n<b>singularitas, atis</b> <i>f</i>\n<b>1)</b> одиночество, холостое состояние <i>Tert</i>;\n<b>2)</b> <i>грам.</i> единственное число."],
  ["singulariter", "<big>singulariter</big>\n\n<b>singulariter</b> [singularis I]\n<b>1)</b> отдельно, поодиночке, порознь (inter se s. apta <i>Lcr</i>);\n<b>2)</b> единственно в своём роде, исключительно, необыкновенно (aliquem diligere <i>C</i>);\n<b>3)</b> <i>грам.</i> в единственном числе (dicere <i>AG</i>)."],
  ["singularius", "<big>singularius</big>\n\n<b>singularius, a, um</b> [singularis I]\n<b>1)</b> одиночный, отдельный: litterae singulariae <i>AG</i> инициалы, аббревиатуры;\n<b>2)</b> ординарный, простой (catenae <i>Pl</i>);\n<b>3)</b> необыкновенный, исключительный, редкостный (velocitas <i>AG</i>)."],
  ["singulatim", "<big>singulatim</big>\n\n<b>singulatim</b> <i>и</i> <b>singillatim</b> [singuli]\nпоодиночке, отдельно, порознь (s. effigies cernere <i>Lcr</i>; s. et discretim <i>Ap</i>): s. potius, quam generatim atque universe, de aliqua re loqui <i>C</i> предпочитать говорить о чём-л. в конкретных частностях, чем в общем и целом."],
  ["singuli", "<big>singuli</big>\n\n<b>singuli, ae, a</b> <i>num. distr.</i> [<i>pl. к</i> singulus]\n<b>1)</b> по одному: singuli singulorum deorum sacerdotes <i>C</i> по одному жрецу для каждого из богов;\n<b>2)</b> поодиночке, порознь, каждый отдельно (legiones singulas ponere <i>C</i>; in singulis rebus <i>C</i>): singulis diebus (in singulos dies) <i>C</i> каждый день, ежедневно; ne agam de singulis <i>C</i> чтобы не входить мне в отдельные частности. — <i>См. тж.</i> singulus."],
  ["singultim", "<big>singultim</big>\n\n<b>singultim</b> [singultus]\nрыдая, всхлипывая (loqui <i>H</i>)."],
  ["singultio", "<big>singultio</big>\n\n<b>singultio, —, —, ire</b> [singultus]\n<b>1)</b> рыдать, всхлипывать <i>Ap</i>;\n<b>2)</b> икать <i>CC, PM</i>;\n<b>3)</b> кудахтать <i>Col</i>;\n<b>4)</b> биться, трепетать (vena singultit <i>Pers</i>)."],
  ["singulto", "<big>singulto</big>\n\n<b>singulto, (avi), atum, are</b> [singultus]\n<b>1)</b> рыдать, всхлипывать: singultatis oscula mista sonis <i>O</i> поцелуи вперемешку с всхлипываниями;\n<b>2)</b> говорить отрывисто (singultantia verba <i>St, Calp</i>);\n<b>3)</b> (<i>об умирающей</i>) хрипеть: s. animam <i>O</i> с хрипением испустить дух."],
  ["singultus", "<big>singultus</big>\n\n<b>singultus, us</b> <i>m</i> [<i>одного корня с</i> gluttio]\n<b>1)</b> рыдание, всхлипывание (fletus cum singultu <i>C</i>);\n<b>2)</b> икота <i>Sen, PM</i>;\n<b>3)</b> предсмертное хрипение <i>V, Sen</i>;\n<b>4)</b> карканье (corvorum <i>PM</i>); кудахтанье (gallinarum <i>Col</i>);\n<b>5)</b> бульканье <i>или</i> журчание (in ampullis, quibus non hians exitus <i>PJ</i>)."],
  ["singulus", "<big>singulus</big>\n\n<b>singulus, a, um</b> <i>num. distr.</i>\n<b>1)</b> одиночный, единичный, отдельный, единственный (vestigium <i>Pl</i>): singula de nobis anni praedantur euntes <i>H</i> каждый уходящий год кое-что отнимает у нас; singula quid referam? <i>O</i> к чему ссылаться на частные примеры?; singula excipere <i>Pt</i> излагать подробности; mala singula <i>Pt</i> каждому (досталось) по яблоку;\n<b>2)</b> <i>грам.</i> единственный (numerus <i>AG</i>). — <i>См. тж.</i> singuli."],
  ["Sinis", "<big>Sinis</big>\n\n<b>Sinis, is</b> <i>m</i> (<i>у греков</i> Pityokamptes «сосносгибатель»)\nСинис, <i>разбойник, живший на Коринфском перешейке и разрывавший людей, привязывая их к пригнутым к земле соснам; убит Тезеем</i> <i>Prp, O</i>."],
  ["sinister", "<big>sinister</big>\n\n<b>sinister, tra, trum</b>\n<b>1)</b> левый (manus <i>L, Nep</i>; oculus <i>PJ, CA</i>); находящийся слева (montes <i>Cs</i>): rota sinisterior <i>O</i> повозка, заехавшая слишком влево;\n<b>2)</b> неправильный, неверный (interpretatio <i>T</i>); превратный, испорченный (mores <i>V</i>);\n<b>3)</b> неблагоприятный, несчастный (pugna <i>Prp</i>); пагубный (arboribus Notus s. <i>V</i>): sinistro pede <i>погов.</i> с левой ноги, <i>т. е. в</i> недобрый час (proficisci <i>Ap</i>) || дурной (fama <i>T, J</i>); (<i>по греч. верованиям</i>) зловещий (omen, aves <i>O</i>);\n<b>4)</b> (<i>у римск. авгуров</i>) благоприятный, счастливый (avis <i>Pl</i>; tonitrus <i>O</i>) (<i>счастливые предзнаменования ожидались с востока, т. е. справа для греч. прорицателей, становившихся лицом к северу, и слева для авгуров, которые обращались на юг</i>). — <i>См. тж.</i> sinistra, sinistri <i>и</i> sinistrum."],
  ["sinisteritas", "<big>sinisteritas</big>\n\n<b>sinisteritas, atis</b> <i>f</i> [sinister]\nнеловкость, неумелость <i>PJ, Sid</i>."],
  ["sinistra", "<big>sinistra</big>\n\n<b>sinistra, ae</b> <i>f</i> [sinister]\nлевая рука <i>Cs, Ctl, O etc.</i> <i>или</i> сторона: dextra ac sinistra <i>Cs</i> справа и слева; a (<i>или</i> sub) sinistra <i>Cs etc.</i> <i>или</i> ad sinistram слева (alicui <i>Pt</i>)."],
  ["sinistre", "<big>sinistre</big>\n\n<b>sinistre</b> [sinister]\nнеблагоприятно, недоброжелательно, дурно (aliquem excipere <i>H</i> <i>и</i> accipere <i>T</i>)."],
  ["sinistri", "<big>sinistri</big>\n\n<b>sinistri, orum</b> <i>m</i> [sinister]\nнаходящиеся на левом фланге, левофланговые <i>L</i>."],
  ["sinistrorsum", "<big>sinistrorsum</big>\n\n<b>sinistrorsum</b> <i>adv.</i> [sinister + versus]\nв левую сторону, налево, влево (abire <i>H</i>)."],
  ["sinistrorsus", "<big>sinistrorsus</big>\n\n<b>sinistrorsus</b> <i>adv.</i> <i>Cs, Su</i>\n= sinistrorsum."],
  ["sinistroversus", "<big>sinistroversus</big>\n\n<b>sinistroversus</b> <i>Lact</i>\n= sinistrorsum."],
  ["sinistrum", "<big>sinistrum</big>\n\n<b>sinistrum, i</b> <i>n</i>\n<b>1)</b> левая сторона (in s. <i>Q</i>; a sinistro <i>Q</i>);\n<b>2)</b> зло, порок (studiosus sinistri <i>O</i>)."],
  ["sino", "<big>sino</big>\n\n<b>I sino, —, situm, ere</b>\nставить, класть (<i>употр. тк. в</i> <i>part. pf.</i>, <i>см.</i> situs 1).\n\n<b>II sino, sivi (sii), situm, ere</b>\n<b>1)</b> допускать, разрешать, позволять: non patiar, non sinam <i>C</i> я не допущу, не позволю; sine позволь: sine te exorem <i>Pl</i> дай упросить тебя <i>или</i> ладно, пусть, пускай (sine vivat <i>H</i>): sine sciam <i>L</i> позволь мне узнать; sinentibus nobis <i>PJ</i> с нашего согласия; sine sis loqui me <i>Pl</i> дай же мне сказать; ne istuc Juppiter sirit (= siverit) <i>L</i> не дай, Юпитер; animus maternus? sino <i>Ter</i> материнские чувства? допустим; quantum corpus sinebat <i>T</i> насколько позволяли физические силы; lanas non s. esse rudes <i>O</i> прилежно обрабатывать шерсть; dum res sinit <i>O</i> пока это (ещё) возможно;\n<b>2)</b> щадить: s. animam <i>V</i> пощадить жизнь;\n<b>3)</b> отпускать: sine me <i>Ter</i> оставь меня в покое;\n<b>4)</b> оставлять (pontem intactum <i>QC</i>); предоставлять (s. arma viris <i>V</i>): non s. vanas esse preces <i>O</i> не оставлять просьб неисполненными."],
  ["Sinon", "<big>Sinon</big>\n\n<b>Sinon, onis</b> <i>m</i>\nСинон, <i>грек, обманом убедивший троянцев втащить деревянного коня в город и тем погубивший Трою</i> <i>V</i>."],
  ["Sinopa", "<big>Sinopa</big>\n\n<b>Sinopa, ae</b> <i>и</i> <b>Sinope, es</b> <i>f</i>\nСинопа:\n<b>1)</b> <i>город в Пафлагонии, на Чёрном море, родина кинического философа Диогена</i> (<i>ныне</i> Синоп) <i>C, PM, T</i>;\n<b>2)</b> <i>L, PM</i> = Sinuessa."],
  ["Sinopensis", "<big>Sinopensis</big>\n\n<b>Sinopensis, e, Sinopeus</b> <i>и</i> <b>Sinopicus, a, um</b> [Sinopa]\nсинопский <i>L, Pl, O, CC</i>."],
  ["Sintice", "<big>Sintice</big>\n\n<b>Sintice, es</b> <i>f</i>\nСинтика, <i>область в Македонии с главн. городом Heraclea</i> <i>L</i>."],
  ["Sintii", "<big>Sintii</big>\n\n<b>Sinti(i), orum</b> <i>m</i>\nжители области Sintice <i>L</i>."],
  ["Sinuessa", "<big>Sinuessa</big>\n\n<b>Sinuessa, ae</b> <i>f</i>\nСинуэсса, <i>город в Латии на границе с Кампанией, славившийся вином и целебными источниками</i> <i>C, L, O</i>."],
  ["Sinuessanus", "<big>Sinuessanus</big>\n\n<b>Sinuessanus, a, um</b> [Sinuessa]\nсинуэсский (deversoriolum <i>C</i>; aquae <i>PM</i>)."],
  ["sinum", "<big>sinum</big>\n\n<b>sinum, i</b> <i>n</i>\nширокий глиняный сосуд, жбан (<i>для молока, вина и пр.</i>) <i>Vr, Col, V etc.</i>"],
  ["sinuo", "<big>sinuo</big>\n\n<b>sinuo, avi, atum, are</b> [sinus II]\n<b>1)</b> сгибать, изгибать (terga <i>V</i>; corpus <i>O</i>): s. arcum <i>O</i> натягивать лук; s. vela <i>Ap</i> надувать паруса; corpus in orbes s. <i>O</i> извиваться; muri sinuati <i>T</i> извилистые стены;\n<b>2)</b> выдалбливать, проделывать извилистые ходы (adhuc sana rodendo sinuat, <i>sc.</i> pus <i>CC</i>)."],
  ["sinuose", "<big>sinuose</big>\n\n<b>sinuose</b> [sinuosus]\nизвилисто, запутанно (dicere <i>AG</i>)."],
  ["sinuosus", "<big>sinuosus</big>\n\n<b>sinuosus, a, um</b> [sinus II]\n<b>1)</b> извилистый (flexus anguis <i>V</i>);\n<b>2)</b> складчатый, весь в складках, сборчатый (vestis <i>O</i>);\n<b>3)</b> полный отступлений, запутанный, многосложный (narratio <i>Q</i>; quaestio <i>AG</i>): sinuoso in pectore <i>погов. Pers</i> в глубине души."],
  ["sinus", "<big>sinus</big>\n\n<b>I sinus, i</b> <i>m</i> <i>Pl</i>\n= sinum.\n\n<b>II sinus, us</b> <i>m</i>\n<b>1)</b> изогнутость, кривизна (falcis <i>Col</i>; nubium <i>Sen</i>); изгиб, извив (draconis <i>Poeta ap. C</i>; serpentis <i>O</i>): excipere hostem sinu <i>T</i> охватить противника с фланга || выпуклость, вздутие (velorum <i>Prp</i>); <i>поэт.</i> парус (sinus implere secundos <i>V</i>; totos pandere sinus <i>J</i>); вьющаяся прядь, завиток, локон (comarum <i>O</i>);\n<b>2)</b> складка (sinum ex toga facere <i>L</i>); пазуха <i>или</i> карман (litteras in sinu ponere <i>L</i>): sinus ponderare <i>Prp</i> обыскать карманы; aliquid in sinum conferre <i>Lampr</i> присвоить себе что-л.; in sinum alicujus demandare <i>Pt</i> отдавать кому-л. на хранение; in sinum suum spuere <i>Pt</i> плюнуть себе за пазуху (<i>для того, чтобы отвратить гнев Немесиды за гордые слова</i>); rapinarum s. <i>PM</i> укрыватель награбленного;\n<b>3)</b> одежда, платье (induere regales sinus <i>O</i>; manum demittere in sinum <i>Pt</i>); сеть звероловная <i>M</i> <i>или</i> рыболовная <i>J</i>;\n<b>4)</b> грудь, лоно, объятия (sinu fovere aliquem <i>Prp</i>; in sinu philosophiae compelli <i>C</i>): in sinu gaudere <i>C</i> втайне радоваться || материнское чрево (concipere sinu <i>O</i>);\n<b>5)</b> нежная любовь, забота: in sinu alicujus esse <i>C</i> быть чьим-л. любимцем; homines ex (<i>или</i> de) sinu alicujus <i>C</i> чьё-л. ближайшее окружение <i>или</i> чьи-л. закадычные друзья;\n<b>6)</b> защита, опека (aliquem sinu suo recipere <i>C</i>);\n<b>7)</b> глубина, недра (alti et obscuri quaestionum sinus <i>AG</i>); середина, средоточие, центр: in sinu urbis <i>Sl</i> в центре (сердце) города;\n<b>8)</b> власть, распоряжение (opes in sinu alicujus est <i>T</i>; res publica in sinum Vespasiani cessit <i>T</i>);\n<b>9)</b> источник средств (novos sinus exquirere <i>T</i>);\n<b>10)</b> залив, бухта (naves ex sinu abducere <i>Cs</i>); побережье залива (s. Campaniae <i>T</i>);\n<b>11)</b> круг, кольцо (sinus terrarum <i>T</i>);\n<b>12)</b> впадина, ущелье, пропасть (s. montium <i>QC</i>; terra in ingentem sinum consedit <i>L</i>)."],
  ["sion", "<big>sion</big>\n\n<b>I sion, i</b> <i>n</i> <i>v. l.</i>\n= sium.\n\n<b>II Sion</b> <i>f indecl.</i>\nСион, <i>гора в юго-зап. части Иерусалима</i> <i>Vlg, Eccl</i>."],
  ["siparium", "<big>siparium</big>\n\n<b>siparium, i</b> <i>n</i> [<i>demin. к</i> siparum]\n<b>1)</b> завеса, театральный занавес (<i>опускавшийся в антрактах; главный занавес</i>— aulaeum): complicitis sipariis <i>Ap</i> когда внутренние занавесы были убраны; post s. <i>погов. C</i> за кулисами, тайно;\n<b>2)</b> комедия: verba cothurno, non tantum sipario fortiora <i>Sen</i> слова, своей силой превышающие уровень трагедии, а не только комедии;\n<b>3)</b> навес, тент <i>Q</i>."],
  ["siparum", "<big>siparum</big>\n\n<b>siparum</b> <i>и</i> <b>sipharum, i</b> <i>n</i> <i>Sen, Fronto</i>\n= supparum."],
  ["Siphnos", "<big>Siphnos</big>\n\n<b>Siphnos (-us), i</b> <i>f</i>\nСифнос, <i>богатый металлическими рудами о-в из группы Киклад, к зап. от Пароса</i> <i>Mela, PM</i>."],
  ["sipho", "<big>sipho</big>\n\n<b>sipho, onis</b> <i>m</i> (<i>греч.</i>)\n<b>1)</b> труба, <i>преим.</i> пожарная <i>Sen, PM, Dig</i>;\n<b>2)</b> сифон, ливер, насос (aliquid siphonibus implere <i>J</i>; aquam in modum siphonis exprimere <i>Sen</i>);\n<b>3)</b> трубка <i>или</i> кишка (vinum bibere per siphonem <i>CC</i>)."],
  ["siphon", "<big>siphon</big>\n\n<b>siphon, onis</b> <i>m</i> <i>Is</i>\n= sipho 1."],
  ["siphunculus", "<big>siphunculus</big>\n\n<b>siphunculus, i</b> <i>m</i> [<i>demin. к</i> sipho]\nмаленький насос <i>PJ</i>."],
  ["sipo", "<big>sipo</big>\n\n<b>I sipo, onis</b> <i>m</i> <i>v. l.</i>\n= sipho.\n\n<b>II sipo</b> <i>v. l.</i>\n= sup(p)o."],
  ["sipolindrum", "<big>sipolindrum</big>\n\n<b>sipolindrum</b> <i>v. l.</i>\n= cepolendrum."],
  ["Sipontinus", "<big>Sipontinus</big>\n\n<b>Sipontinus, a, um</b> [Sipontum]\nсипонтийский (siccitas <i>C</i>)."],
  ["Sipontum", "<big>Sipontum</big>\n\n<b>Sipontum, i</b> <i>n</i>\nСипонт, <i>приморский город в Апулии</i> (<i>ныне</i> Maria di Siponto) <i>C, L, PM</i>."],
  ["siptace", "<big>siptace</big>\n\n<b>siptace, es</b> <i>f</i> <i>PM</i>\n= psittacus."],
  ["sipunculus", "<big>sipunculus</big>\n\n<b>sipunculus, i</b> <i>m</i> <i>Fronto</i>\n= siphunculus."],
  ["Sipus", "<big>Sipus</big>\n\n<b>Sipus, untis</b> <i>f</i> <i>Lcn, Sil</i>\n= Sipontum."],
  ["Sipylus", "<big>Sipylus</big>\n\n<b>Sipylus (-os), i</b> <i>m</i>\nСипил:\n<b>1)</b> <i>один из сыновей Ниобы</i> <i>O</i>;\n<b>2)</b> <i>гора на границе Лидии и Фригии, близ Магнесии</i> <i>C, L, Prp etc.</i>"],
  ["siquando", "<big>siquando</big>\n\n<b>si–quando</b> <i>adv.</i> (<i>тж. раздельно</i>)\nесли когда-л., если же, если уж <i>O etc.</i>: s. taceant omnes, tunc sortito coguntur dicere <i>C</i> (по сиракузскому обычаю) если все молчат, то оратор назначается жеребьёвкой."],
  ["siquidem", "<big>siquidem</big>\n\n<b>si–quidem</b> <i>conj.</i> (<i>тж. раздельно</i>)\n<b>1)</b> так как, поскольку, ибо, ведь: s. eorum consilio nostris rebus perfrui possumus <i>C</i> ведь именно благодаря их разуму мы в состоянии наслаждаться .своими благами;\n<b>2)</b> если действительно, если только: s. hanc sentinam ejecerit <i>C</i> (как было бы счастливо государство), если бы только смогло освободиться от этой скверны (<i>т. е.</i> Катилины)."],
  ["siraeum", "<big>siraeum</big>\n\n<b>siraeum, ī</b> <i>n</i> (<i>греч.</i>) <i>PM</i>\n= sapa."],
  ["sirbenus", "<big>sirbenus</big>\n\n<b>sirbēnus, a, um</b> (<i>греч.</i>)\nневнятно бормочущий, косноязычный <i>Fronto</i>."],
  ["sircitula", "<big>sircitula</big>\n\n<b>sircitula, ae</b> <i>f</i>\n<i>сорт винограда</i> <i>Col</i>."],
  ["siremps", "<big>siremps</big>\n\n<b>sīremps (sīrempse)</b> <i>adj.</i> [<i>из</i> similis re ipsa]\nсовершенно такой же, равный, одинаковый (lex <i>Pl, Cato, Sen</i>)."],
  ["Siren", "<big>Siren</big>\n\n<b>Sīrēn, ēnis</b> <i>f</i> (<i>acc.</i> ēna — <i>pl.</i> ēnas)\nСирена; <i>обычно</i> <i>pl.</i> Sirenes, <i>баснословные существа с телами птиц и головами дев, обитавшие где-то на южн. побережье Италии, завлекавшие своим пением мореходов и убивавшие их</i> <i>Prp, O, C etc.</i>: Sirenum scopuli <i>V</i> скалы Сирен (<i>между Суррентом и Капреей</i>); Siren desidia <i>H</i> лень-обольстительница."],
  ["Sirena", "<big>Sirena</big>\n\n<b>Sīrēna, ae</b> <i>f</i> <i>Hier, Amm</i>\n= Siren."],
  ["Sirenius", "<big>Sirenius</big>\n\n<b>Sīrēnius (-ēus), a, um</b> [Siren]\nсиренин (scopuli <i>AG</i>; cantus <i>Hier</i>)."],
  ["Siriacus", "<big>Siriacus</big>\n\n<b>Sīriacus, a, um</b> [Sirius]\nпалящий, знойный (calor <i>Eccl</i>)."],
  ["siric-", "<big>siric-</big>\n\n<b>sīric-</b> <i>v. l.</i>\n= seric-."],
  ["sirit", "<big>sirit</big>\n\n<b>sīrit</b> (= siverit) <i>арх. pf. conjct. к</i>\nsino II."],
  ["Sirius", "<big>Sirius</big>\n\n<b>I Sīrius, ī</b> <i>m</i> (<i>греч.</i>; <i>лат.</i> Canicula)\nСириус (<i>в созвездии Большого Пса; восход 18-го, по др., 25-26 июля</i>) (S. torrens <i>V</i>; arentes findit S. agros <i>Tib</i>).\n\n<b>II Sīrius, a, um</b> <i>adj. к</i>\nSirius I: ardor S. <i>V</i> палящий зной (<i>в июле — августе</i>)."],
  ["Sirmio", "<big>Sirmio</big>\n\n<b>Sirmio, ōnis</b> <i>f</i>\nСирмион, <i>п-ов на озере Benacus</i> (<i>ныне</i> Lago di Garda), <i>где находилось поместье Катулла</i> <i>Ctl</i>."],
  ["Sirmium", "<big>Sirmium</big>\n\n<b>Sirmium, ī</b> <i>n</i>\nСирмий, <i>город в Нижней Паннонии, на реке Savus</i> <i>PM, Eutr, Sid, Aus</i>."],
  ["sirp-", "<big>sirp-</big>\n\n<b>sirp-</b> <i>v. l.</i>\n= scirp-."],
  ["sirpe", "<big>sirpe</big>\n\n<b>sirpe, is</b> <i>n</i> <i>Pl, Sol</i>\n= laserpitium."],
  ["sirus", "<big>sirus</big>\n\n<b>sīrus, ī</b> <i>m</i> (<i>греч.</i>)\nподземный амбар, хлебный подвал <i>Col, QC etc.</i>"],
  ["sis", "<big>sis</big>\n\n<b>I sīs</b> [<i>из</i> si + vis <i>от</i> volo]\nесли угодно, пожалуйста, будь добр <i>Pl, Ter</i>: age nunc, refer animum, s., ad veritatem <i>C</i> ну а теперь обрати, пожалуйста, внимание на то, что действительно происходит.\n\n<b>II sīs</b> <i>2 л. sg. conjct. praes. к</i>\nsum I.\n\n<b>III sīs</b> <i>арх. Lcr</i>\n= suis III (<i>dat. abl. pl. к</i> suus)."],
  ["sisamum", "<big>sisamum</big>\n\n<b>sīsamum, ī</b> <i>n</i> <i>v. l.</i>\n= sesamum."],
  ["Sisapo", "<big>Sisapo</big>\n\n<b>Sisapō, ōnis</b> <i>f</i>\nСизапон, <i>город в Hispania Baetica, к сев. от Кордубы</i> <i>C, PM</i>."],
  ["Sisenna", "<big>Sisenna</big>\n\n<b>Sīsenna, ae</b> <i>m</i>\nСизенна, <i>cognomen в роде Корнелиев; наиболее известен</i>: L. Cornelius S., <i>современник Суллы, историк своей эпохи</i> <i>C, Sl, VP, O</i>."],
  ["siser", "<big>siser</big>\n\n<b>siser, eris</b> <i>n</i> (<i>греч.</i>) <i>бот.</i>\nсахарный корень (<i>Sium Sisarum</i>, L.) <i>Col, H, PM</i>."],
  ["sissem", "<big>sissem</big>\n\n<b>sissem, sissent</b> <i>C, L</i>\n<i>стяж. формы</i> <i>ppf. к</i> sino II."],
  ["sisto", "<big>sisto</big>\n\n<b>sisto, stitī (stetī), statum, ere</b> [sto]\n<b>1)</b> ставить, помещать, располагать (aciem in litore <i>V</i>); приводить (victimam ante aras <i>O</i>): Annam huc siste sororem <i>V</i> приведи (позови) сюда сестру Анну; s. aliquem in portu <i>Ap</i> доставить кого-л. в порт; vadimonium s. <i>C</i> явиться на суд; dotem s. alicui <i>Pl</i> возвратить приданое кому-л. || бросать, метать (alicui jaculum in ore <i>V</i>);\n<b>2)</b> воздвигать, возводить, сооружать (templum <i>T</i>; effigiem alicujus <i>T</i>);\n<b>3)</b> вызывать в суд (aliquem <i>C</i>): se s. являться, предстать (in Graecia alicui <i>C</i>): sisto ego tibi me <i>Pl</i> вот я перед тобой;\n<b>4)</b> останавливать, задерживать, сдерживать (hostem, equum <i>V</i>; aquam fluviis <i>V</i>): nec jam sisti poterat <i>T</i> его нельзя уже было остановить || кончать, прекращать (querelas <i>O</i>; labores <i>O</i>); утолять (sitim <i>O</i>); удерживать (lacrimas <i>O</i>); останавливаться, застывать (sistunt amnes <i>V</i>; stetit sanguis <i>O</i>): s. pedem <i>O</i> (gradum <i>V</i>) <i>или</i> se s. <i>V</i> останавливаться || застревать (hasta stetit medio tergo <i>O</i>);\n<b>5)</b> становиться (hinc stetit Volcanus <i>H</i>);\n<b>6)</b> оставаться, продолжаться, дольше существовать, длиться (aliquid s. non potest <i>C</i>): sisti non posse <i>L</i> не быть в состоянии вынести (устоять);\n<b>7)</b> укреплять (mobiles dentes <i>PM</i>; rem Romanam <i>V</i>; animum <i>Pl</i>);\n<b>8)</b> делаться, становиться (judex s. vitaeque necisque <i>Man</i>)."],
  ["sistratus", "<big>sistratus</big>\n\n<b>sīstrātus, a, um</b> [sistrum]\nнесущий систры (sistrata turba <i>M</i>)."],
  ["sistrum", "<big>sistrum</big>\n\n<b>sīstrum, ī</b> <i>n</i> (<i>греч.</i>)\nсистр, трещотка (<i>род кастаньет, употреблявшихся в Египте при богослужении в честь Исиды <i>O, Sen, J etc.</i>, тж. для военных сигналов</i>: vocare agmina sistro <i>V</i>)."],
  ["sisurna", "<big>sisurna</big>\n\n<b>sisur(n)a, ae</b> <i>f</i> (<i>греч.</i>)\nовчина, тулуп <i>Amm</i>."],
  ["sisymbrium", "<big>sisymbrium</big>\n\n<b>sisymbrium, ī</b> <i>n</i> (<i>греч.</i>) <i>бот.</i>\nсисимбрий, <i>предпол.</i> тимьян <i>или</i> мята <i>Vr, PM, O</i>."],
  ["Sisyphides", "<big>Sisyphides</big>\n\n<b>Sīsyphidēs, ae</b> <i>m</i>\nсын Сизифа, <i>т. е.</i> Одиссей, <i>родившийся, по преданию, у Антиклеи от Сизифа до её выхода замуж за Лаэрта</i> <i>O</i>."],
  ["Sisyphius", "<big>Sisyphius</big>\n\n<b>Sīsyphius, a, um</b> [Sisyphus]\nсизифов (labores <i>Prp</i>); <i>перен.</i> коринфский (Isthmos <i>Sil</i>)."],
  ["Sisyphus", "<big>Sisyphus</big>\n\n<b>Sīsyphus (-os), ī</b> <i>m</i>\nСизиф, <i>сын Эола и Энареты, основатель и царь Коринфа, известный своей хитростью и разбоем; убит Тезеем, в подземном мире осуждён на вечное вкатывание на гору вечно скатывающейся обратно каменной глыбы</i> <i>C, H, O</i>."],
  ["sitanius", "<big>sitanius</big>\n\n<b>sitanius, a, um</b> (<i>греч.</i>)\n(сбора <i>или</i> урожая) нынешнего года (triticum <i>PM</i>)."],
  ["sitarchia", "<big>sitarchia</big>\n\n<b>sitarchia (-cia), ae</b> <i>f</i> (<i>греч.</i>)\nдорожный ящик <i>или</i> мешок с провизией <i>Ap, Vlg, Eccl</i>."],
  ["sitella", "<big>sitella</big>\n\n<b>sitella, ae</b> <i>f</i> [<i>demin. к</i> situla]\nнебольшой сосуд, из которого тянули жребий при выборах, избирательная урна <i>Pl, L</i>: de aliquo sitellam deferre <i>C</i> поставить чью-л. кандидатуру на голосование."],
  ["Sithon", "<big>Sithon</big>\n\n<b>I Sithon, onis</b> <i>m</i>\nСитон, <i>миф. царь Херсонеса Фракийского.</i>\n\n<b>II Sithon, onis</b> <i>и</i> <b>Sithonius, a, um</b> [Sithon I]\nситонский, <i>перен.</i> фракийский (nix <i>H, V</i>; Aquilo <i>O</i>)."],
  ["Sithonis", "<big>Sithonis</big>\n\n<b>Sithonis, idis</b> <i>f</i>\n<b>1.</b> <i>adj.</i> фракийская (unda <i>O</i>);\n<b>2.</b> <i>subst.</i> фракиянка <i>O</i>."],
  ["Sithonius", "<big>Sithonius</big>\n\n<b>I Sithonius, a, um</b> <i>v. l.</i>\n= Sithon II.\n\n<b>II Sithonius, i</b> <i>m</i>\nфракиец <i>H</i>."],
  ["siticen", "<big>siticen</big>\n\n<b>siticen, inis</b> <i>m</i> [situs I, 2 + cano]\nмузыкант на похоронах <i>Cato, AG</i>."],
  ["siticulosus", "<big>siticulosus</big>\n\n<b>siticulosus, a, um</b> [sitis I]\n<b>1)</b> жаждущий, томимый жаждой (corvus <i>Priap</i>);\n<b>2)</b> сухой, безводный (Apulia <i>H</i>; solum <i>Col</i>);\n<b>3)</b> засушливый, знойный (aestas <i>Priap</i>);\n<b>4)</b> вызывающий жажду (melimela et reliqua dulcia <i>PM</i>)."],
  ["sitiens", "<big>sitiens</big>\n\n<b>sitiens, entis</b>\n<b>1.</b> <i>part. praes. к</i> sitio;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> жадный (avidus sitiensque <i>O</i>; s. famae <i>Sil</i> — <i>gen.</i>);\n<b>2)</b> безводный (Afri = Africa <i>V</i>; hortus <i>O</i>);\n<b>3)</b> знойный, палящий (Canicula <i>O</i>)."],
  ["sitienter", "<big>sitienter</big>\n\n<b>sitienter</b> [sitio]\n<b>1)</b> с желанием пить (haurire aquas <i>Ap</i>);\n<b>2)</b> жадно, страстно (expetere aliquid <i>C</i>)."],
  ["sitio", "<big>sitio</big>\n\n<b>sitio, ivi (ii), itum, ire</b> [sitis I]\n<b>1)</b> испытывать жажду, хотеть пить (s. mediis in aquis <i>O</i>; esurire et s. <i>Ap</i>); <i>pass.</i> sitiri быть предметом желания: quo plus sunt potae, plus sitiuntur aquae <i>O</i> (<i>при водянке</i>) чем больше пьют воду, тем больше её хочется;\n<b>2)</b> жаждать, страстно желать (s. honores <i>C</i>): sitientes aures <i>C</i> уши, жаждущие послушать новости;\n<b>3)</b> жаждать влаги, быть безводным, быть пересохшим, сохнуть, томиться от жажды (sitiunt agri <i>C</i>): s. Tagum <i>M</i> томиться по реке Таг;\n<b>4)</b> жить в жарком климате (loca sitientia <i>PM</i>);\n<b>5)</b> быть знойным (canicula sitiens <i>O</i>). — <i>См. тж.</i> sitiens."],
  ["sitis", "<big>sitis</big>\n\n<b>I sitis, is</b> (<i>acc.</i> im, <i>abl.</i> i) <i>f</i>\n<b>1)</b> жажда: sitim explere <i>C</i> (restinguere <i>C</i>, exstinguere <i>O</i>, pellere <i>H</i>) утолять жажду;\n<b>2)</b> сухость, безводность (regio siti deserta <i>V</i>);\n<b>3)</b> сильное желание, страсть, томление, жажда (s. libertatis <i>C</i>).\n\n<b>II sitis</b> <i>2 л. pl. praes. conjct. к</i>\nsum.\n\n<b>III sitis</b> <i>dat. abl. pl. к</i>\nsitus I."],
  ["sititor", "<big>sititor</big>\n\n<b>sititor, oris</b> <i>m</i> [sitio]\nжаждущий (s. aquae <i>M</i>; s. novitatis <i>Ap</i>)."],
  ["sitive", "<big>sitive</big>\n\n<b>sitive</b> [situs II]\nсмотря по местоположению <i>Tert</i> (<i>v. l.</i>)."],
  ["sitona", "<big>sitona</big>\n\n<b>sitona, ae</b> <i>m</i> (<i>греч.</i>)\nуполномоченный по закупке продовольствия <i>Dig</i>."],
  ["sitonia", "<big>sitonia</big>\n\n<b>sitonia, ae</b> <i>f</i> (<i>греч.</i>)\nпродовольственное снабжение <i>Dig</i>."],
  ["sittace", "<big>sittace</big>\n\n<b>sittace</b> <i>v. l.</i>\n= siptace."],
  ["sittybos", "<big>sittybos</big>\n\n<b>sittybos</b>, <i>f</i> (<i>pl.</i> sittyboe) <i>m</i> (<i>греч.</i>)\nполоска пергамента на свитке с названием сочинения и именем автора, <i>т. е.</i> титульный лист, заглавие <i>C</i>."],
  ["situla", "<big>situla</big>\n\n<b>situla, ae</b> <i>f</i>\n<b>1)</b> ведро для воды <i>Pl, Dig etc.</i>;\n<b>2)</b> ковш, винная кружка <i>Vop</i>;\n<b>3)</b> сосуд для метания жребия <i>или</i> избирательная урна <i>Pl</i>."],
  ["situlus", "<big>situlus</big>\n\n<b>situlus, i</b> <i>m</i> <i>Cato, Vtr</i>\n= situla."],
  ["situs", "<big>situs</big>\n\n<b>I situs, a, um</b>\n<b>1.</b> <i>part. pf. к</i> sino I;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> построенный, возведённый (urbs a Philippo sita <i>T</i>);\n<b>2)</b> похороненный, погребённый (Aeneas s. est super Numicium flumen <i>L</i>);\n<b>3)</b> находящийся, живущий, обитающий (populus s. post Tigrim <i>QC</i>); помещающейся (lingua in ore sita <i>C</i>); расположенный, лежащий (urbs sita in media insula <i>C</i>; ante oculos omnium <i>Aug</i>); помещённый, вложенный (argentum apud aliquem situm <i>Pl</i>);\n<b>4)</b> покоящийся, основывающийся (salus patriae in pernicie hostis sita est <i>Nep</i>); зависящий: hoc situm est in vobis <i>C</i> (in vestra manu <i>Ap</i>) это зависит от вас; quantum in me situm est <i>C</i> насколько от меня зависит; quae sunt in casu sita <i>C</i> то, что зависит от случая.\n\n<b>II situs, us</b> <i>m</i> [sino I]\n<b>1)</b> положение, расположение (oppidi <i>Cs</i>; foliorum <i>PM</i>; membrorum <i>C</i>): locorum s. <i>C, QC</i> географические условия;\n<b>2)</b> строение, постройка, здание (regalis s. pyramidum <i>H</i>): s. araneosus <i>Ctl</i> паутина;\n<b>3)</b> страна света (a meridiano situ ad septentrionem <i>PM</i>).\n\n<b>III situs, us</b> <i>m</i> [<i>одного корня с</i> sitis?]\n<b>1)</b> длительное лежание, неподвижность, заброшенность (campus situ durescit <i>V</i>); бездеятельность, бездействие (pectora situ pereunt <i>O</i>; gladius situ robiginat <i>Ap</i>): verborum s. <i>Sen</i> словесная ветошь, <i>т. е.</i> устарелые слова; memoria longo lassa sublabens situ <i>SenT</i> память, ослабевшая от долгого бездействия;\n<b>2)</b> увядание, одряхление (senectus victa situ <i>O</i>);\n<b>3)</b> забытость, безвестность (in situ jacere <i>Prp</i>);\n<b>4)</b> плесень, гниль (canescunt tecta situ <i>O</i>): situ non expugnabile robur <i>St</i> не поддающийся гниению дуб."],
  ["sium", "<big>sium</big>\n\n<b>sium, i</b> <i>n</i> (<i>кельт.-греч.</i>) <i>бот.</i>\nпоручейник (<i>Sium latifolium</i>, L.) <i>PM</i>."],
  ["sive", "<big>sive</big>\n\n<b>si–ve</b> <i>или</i> <b>seu</b> <i>conj.</i>\n<b>1)</b> или если (si nocte s. luce <i>L</i>): postulo, s. aequum est, te oro <i>Ter</i> я требую, а если нужно, то умоляю тебя;\n<b>2)</b> или (Plato s. quis alius <i>C</i>; Paris s. Alexander <i>PM</i>): s... s. (vel, aut, sin) или ... или, либо ... либо (s. casu s. consilio <i>Cs</i>); <i>тж.</i> ли ... или: requirunt spemque metumque inter dubii, seu vivere credant, sive, extrema pati, nec jam exaudire vocatos <i>V</i> (спутники Энея), колеблясь между надеждой и страхом, спрашивают себя, считать ли живыми (их товарищей), или они, перейдя предел жизни, не слышат уже призывов."],
  ["sivi", "<big>sivi</big>\n\n<b>sivi</b> <i>pf. к</i>\nsino II."],
  ["smaragdinus", "<big>smaragdinus</big>\n\n<b>smaragdinus, a, um</b> [smaragdus]\nизумрудный (lapis <i>Vlg</i>; prata <i>Eccl</i>; emplastrum <i>CC</i>)."],
  ["smaragdus", "<big>smaragdus</big>\n\n<b>smaragdus, i</b> <i>m, f</i> (<i>греч.</i>)\nизумруд, смарагд <i>PS ap. Pt, Lcn, M, Cld</i>."],
  ["smaris", "<big>smaris</big>\n\n<b>smaris, idis</b> <i>f</i>\nсмарида, <i>род морской рыбы невысокого качества</i> <i>O, PM</i>."],
  ["smecticus", "<big>smecticus</big>\n\n<b>smecticus, a, um</b> (<i>греч.</i>)\nочищающий (vis <i>PM</i>)."],
  ["smegma", "<big>smegma</big>\n\n<b>smegma, atis</b> <i>n</i> (<i>dat. abl. pl.</i> <i>тж.</i> atis)\n(<i>греч.</i>) туалетное (очищающее) средство (притирание <i>или</i> благовоние) <i>PM, Vlg</i>."],
  ["Smerdis", "<big>Smerdis</big>\n\n<b>Smerdis, is</b> <i>m</i>\nСмердис:\n<b>1)</b> <i>брат Камбиса, умерщвлённый по его приказанию</i> <i>Just</i>;\n<b>2)</b> <i>самозванец Oropastes, выдававший себя за убитого Смердиса</i> («Лжесмердис») <i>Just</i>."],
  ["smila", "<big>smila</big>\n\n<b>smila, ae</b> <i>f</i> (<i>греч.</i>) <i>Eccl</i>\n= scalprum."],
  ["smilax", "<big>smilax</big>\n\n<b>I smilax, acis</b> <i>f</i> (<i>греч.</i>)\n<b>1)</b> <i>бот.</i> сассапарель (<i>Smilax aspera</i>, L.) <i>PM</i>;\n<b>2)</b> тисовое дерево <i>PM</i>;\n<b>3)</b> разновидность дуба (<i>Quercus ilex</i>, L.) <i>PM</i>;\n<b>4)</b> повилика <i>O</i>.\n\n<b>II Smilax, acis</b> <i>f</i>\nСмилака, <i>девушка, превращенная в повилику</i> <i>O</i>."],
  ["smile", "<big>smile</big>\n\n<b>smile, es</b> <i>f</i> <i>Is</i>\n= smila."],
  ["Smintheus", "<big>Smintheus</big>\n\n<b>I Sminthe͡us, eī</b> <i>и</i> <b>eos</b> <i>m</i>\nСминтей, <i>эпитет Аполлона</i> (<i>предпол. от города Sminthe в Троаде</i>) <i>O</i>.\n\n<b>II Sminthēus, a, um</b> [Smintheus I]\nсминтейский: Sminthea spolia <i>SenT</i> = Astynome, <i>дочь аполлонова жреца Хриса.</i>"],
  ["smyrna", "<big>smyrna</big>\n\n<b>I smyrna, ae</b> <i>f</i> (<i>греч.</i>)\nмирра <i>Lcr, Dig</i>.\n\n<b>II Smyrna, ae</b> <i>f</i>\nСмирна, <i>приморский город на ионическом побережье Малой Азии, считавшийся, по одной из версий, родиной Гомера</i> (<i>ныне</i> Измир) <i>C, PM, AG, St</i>."],
  ["Smyrnaeus", "<big>Smyrnaeus</big>\n\n<b>I Smyrnaeus, a, um</b> [Smyrna]\nсмирнский (origanum <i>PM</i>): S. vates <i>Lcn</i> = Humerus || <i>перен.</i> гомеровский (plectra <i>Sil</i>).\n\n<b>II Smyrnaeus, i</b> <i>m</i>\nжитель города Smyrna <i>C, L</i>."],
  ["sobol-", "<big>sobol-</big>\n\n<b>sobol-</b> <i>v. l.</i>\n= subol-."],
  ["sobrie", "<big>sobrie</big>\n\n<b>sobrie</b> [sobrius]\n<b>1)</b> трезво, умеренно, воздержно (vivere <i>C</i>);\n<b>2)</b> рассудительно, разумно (rem accurare <i>Pl</i>)."],
  ["sobriefactus", "<big>sobriefactus</big>\n\n<b>sobrie–factus, a, um</b>\nобразумившийся (sermone alicujus <i>Ap</i>)."],
  ["sobrietas", "<big>sobrietas</big>\n\n<b>sobrietas, atis</b> <i>f</i> [sobrius]\nтрезвость, умеренность, воздержность <i>Sen, VF, Dig</i>; рассудительность (consiliorum <i>Amm</i>)."],
  ["sobrina", "<big>sobrina</big>\n\n<b>sobrina, ae</b> <i>f</i> [sobrinus]\nдвоюродная сестра (<i>первоначально</i> со стороны матери) <i>Pl, T, Dig</i>."],
  ["sobrinus", "<big>sobrinus</big>\n\n<b>sobrinus, i</b> <i>m</i> [<i>из</i> *sororinus <i>от</i> soror, <i>sc.</i> matris]\nдвоюродный брат (<i>первоначально</i> со стороны матери) <i>Ter, C, Dig</i>."],
  ["sobrio", "<big>sobrio</big>\n\n<b>sobrio, —, —, are</b> [sobrius]\nотрезвлять (aliquem <i>Eccl</i>)."],
  ["sobrius", "<big>sobrius</big>\n\n<b>sobrius, a, um</b>\n<b>1)</b> трезвый (homo <i>Pl, C etc.</i>; convictus <i>T</i>): uva sobria <i>PM</i> виноград, дающий непьянящее (некрепкое) вино;\n<b>2)</b> умеренный, воздержный (homines frugi ac sobrii <i>C</i>);\n<b>3)</b> рассудительный, благоразумный (orator, homo <i>C</i>)."],
  ["soccatus", "<big>soccatus</big>\n\n<b>soccatus, a, um</b> [soccus]\nобутый в лёгкие сандалии <i>Sen, Amm</i>."],
  ["soccellus", "<big>soccellus</big>\n\n<b>soccellus, i</b> <i>m</i> <i>Is</i>\n= socculus."],
  ["soccifer", "<big>soccifer</big>\n\n<b>socci–fer, fera, ferum</b> <i>Sid</i>\n= soccatus."],
  ["soccito", "<big>soccito</big>\n\n<b>soccito, —, —, are</b>\nщебетать <i>или</i> петь (<i>о дрозде</i>) <i>Su</i>."],
  ["socculus", "<big>socculus</big>\n\n<b>socculus, i</b> <i>m</i> [<i>demin. к</i> soccus]\n<b>1)</b> небольшая сандалия (<i>носившаяся женщинами и римск. щеголями, а также комическими актёрами</i>) <i>Sen, PJ etc.</i>;\n<b>2)</b> комический стиль <i>Q</i>."],
  ["soccus", "<big>soccus</big>\n\n<b>soccus, i</b> <i>m</i>\n<b>1)</b> сокк, <i>лёгкая греч. обувь</i> (<i>у римлян — обувь женщин, щеголей и комических актёров</i>) <i>Pl, C, Sen, Su</i>;\n<b>2)</b> комедия: hunc socci cepere pedem, grandesque cothurni <i>H</i> этот размер (<i>т. е.</i> ямб) усвоили как комедия, так и величественная трагедия || комический стиль, комедийный жанр: poemata apta socco <i>Ap</i> поэмы комедийного жанра. — <i>См. тж.</i> cothurnus."],
  ["socer", "<big>socer</big>\n\n<b>socer, erī</b> <i>m</i>\nтесть <i>или</i> свёкор: soceri <i>V</i> родители жены <i>или</i> мужа; s. magnus <i>Dig</i> дед мужа <i>или</i> жены."],
  ["socerus", "<big>socerus</big>\n\n<b>socerus, ī</b> <i>m</i> <i>Pl</i>\n= socer."],
  ["socia", "<big>socia</big>\n\n<b>socia, ae</b> <i>f</i> [socius]\nподруга <i>или</i> спутница (vitae s. virtus <i>C</i>); (<i>тж.</i> s. tori <i>O</i>) супруга <i>Sl</i>."],
  ["sociabilis", "<big>sociabilis</big>\n\n<b>sociabilis, e</b> [socio]\n<b>1)</b> общительный, дружелюбный, миролюбивый, уживчивый (natura nos sociabiles fecit <i>Sen</i>);\n<b>2)</b> близкий, тесный (consortio inter binos Lacedaemoniorum reges <i>L</i>);\n<b>3)</b> легко связывающийся, соединимый (glutino <i>PM</i>)."],
  ["socialia", "<big>socialia</big>\n\n<b>socialia, ium</b> <i>n</i> [socialis]\nдела союзников <i>T</i>."],
  ["socialis", "<big>socialis</big>\n\n<b>socialis, e</b> [socius]\n<b>1)</b> товарищеский, приятельский, дружеский (beneficium <i>Sen</i>; consilium <i>Aug</i>); общительный, общественный (homo sociale animal est <i>Sen</i>);\n<b>2)</b> брачный, супружеский (amor, foedus <i>O</i>); свадебный (carmen <i>O</i>);\n<b>3)</b> союзный, союзнический (exercitus <i>L</i>; copiae <i>T</i>; lex <i>C</i>): bellum sociale <i>L, Nep, Fl, J</i> союзническая война (<i>т. е.</i> против италийских союзников Рима); dii sociales <i>L</i> боги, которым участники союза молились при заключении договора."],
  ["socialitas", "<big>socialitas</big>\n\n<b>socialitas, atis</b> <i>f</i> [socialis]\nобщество, ближайшее окружение <i>PJ</i>."],
  ["socialiter", "<big>socialiter</big>\n\n<b>socialiter</b> [socialis]\nдружелюбно, дружески (cedere de aliqua re <i>H</i>)."],
  ["sociatrix", "<big>sociatrix</big>\n\n<b>sociatrix, icis</b> <i>f</i> [socio]\nобъединяющая, примиряющая (gratia <i>VF</i>)."],
  ["sociennus", "<big>sociennus</big>\n\n<b>sociennus, i</b> <i>m</i> [socius]\nтоварищ, приятель <i>Pl</i> (<i>v. l.</i>)."],
  ["societas", "<big>societas</big>\n\n<b>societas, atis</b> <i>f</i> [socius]\n<b>1)</b> общность, общее (nulla s. mihi cum aliquo est <i>C</i>): s. miseriarum <i>Pt</i> совместно пережитые несчастья || общение, связь, союз, (близкие) отношения (s. hominum inter ipsos <i>C</i>); соучастие, сообщничество (s. sceleris <i>C</i>); сотрудничество (s. consilii <i>C</i>); совместное ведение (s. belli <i>Sl</i>); сочетание (gravitatis cum humanitate <i>C</i>): societate cum aliquo <i>PM</i> сообща с кем-л.;\n<b>2)</b> товарищество, объединение (societatem inire <i>или</i> coire cum aliquo <i>C</i>); политический союз, коалиция (societatem conjungere <i>Sl</i> <i>или</i> facere cum aliquo <i>L</i>): s. foedusque <i>Cs</i> <i>и</i> foedus societatis <i>C</i> союзный договор; s. alicujus <i>T</i> союз с кем-л.;\n<b>3)</b> торговое общество, компания (societatem gerere <i>C</i>);\n<b>4)</b> общество генеральных откупщиков (публиканов): societatis magister <i>C</i> руководитель общества откупщиков."],
  ["socio", "<big>socio</big>\n\n<b>socio, avi, atum, are</b> [socius]\n<b>1)</b> соединять, объединять (homines inter se <i>C</i>); совместно предпринимать, сообща затевать (parricidium <i>Just</i>): sociatus labor <i>O</i> совместный труд || завязывать, заводить (sermonem cum aliquo <i>QC</i>);\n<b>2)</b> сочетать (cognitionem cum exercitatione <i>C</i>; se vinclo jugali alicui s. <i>V</i>); вместе запрягать (s. juvencos imposito aratro <i>St</i>); сочетать браком, выдавать замуж (s. natam alicui <i>Su</i>): coetus utilitatis communi one sociatus <i>C</i> объединение, основанное на общности интересов;\n<b>3)</b> принимать (s. aliquem domo <i>V</i>);\n<b>4)</b> делить, разделять, вместе испытывать (s. periculum cum aliquo <i>C</i>);\n<b>5)</b> <i>юр.</i> приобщать (fisco sociandus fundus <i>CJ</i>)."],
  ["sociofraudus", "<big>sociofraudus</big>\n\n<b>sociofraudus, a, um</b> [socius + fraudo]\nобманывающий товарищей <i>Pl</i>."],
  ["socius", "<big>socius</big>\n\n<b>I socius, a, um</b> [<i>одного корня с</i> sequor]\n<b>1)</b> общий, совместный (consilia <i>C</i>): ignes socii <i>O</i> брачные факелы; socii anni <i>O</i> годы брачного сожительства;\n<b>2)</b> находящийся в союзе, союзный (civitas <i>Q</i>; cohortes <i>T</i>; classis <i>O</i>): arma socia <i>Sil</i> военный союз; nocte socia <i>C</i> под покровом ночи.\n\n<b>II socius, i</b> <i>m</i>\n<b>1)</b> товарищ, спутник (s. consiliorum <i>Pl</i>); сообщник, соучастник (s. belli <i>C</i>); компаньон (rei pecuniariae <i>C</i>): s. sanguinis (<i>или</i> generis) <i>O</i> кровный родственник <i>или</i> брат; s. culpae <i>C</i> сообщник; s. regni <i>C</i> соправитель; s. tori <i>O</i> супруг;\n<b>2)</b> союзник (populi Romani <i>C</i>): socii et nomen Latinum <i>Sl</i> италийские союзники и латиняне; bellum sociorum <i>C</i> война с союзниками;\n<b>3)</b> генеральный откупщик <i>C</i>."],
  ["socordia", "<big>socordia</big>\n\n<b>socordia, ae</b> <i>f</i> [socors]\n<b>1)</b> беспечность, беззаботность, нерадивость <i>Ter, Sl, L etc.</i>;\n<b>2)</b> ограниченность, тупоумие <i>Su, T</i>."],
  ["socorditer", "<big>socorditer</big>\n\n<b>socorditer</b> (<i>употр. тк. в</i> <i>compar.</i>)\nбеспечно, беззаботно, небрежно (agere <i>L, T</i>)."],
  ["socors", "<big>socors</big>\n\n<b>socors, cordis</b> <i>adj.</i> [se IV + cor]\n<b>1)</b> беспечный, беззаботный, небрежный, нерадивый (homo s. futuri <i>T</i>);\n<b>2)</b> ограниченный, слабоумный, тупой (animus <i>T</i>; ingenio s. <i>L</i>)."],
  ["socos", "<big>socos</big>\n\n<b>socos</b>, <i>acc.</i> on <i>m</i>\nцвет гиацинта <i>или</i> аметиста (hunc colorem Indi socon vocant <i>PM</i>)."],
  ["Socrates", "<big>Socrates</big>\n\n<b>Socrates, is</b> <i>m</i>\nСократ, <i>родом из Афин, основатель идеалистической философской школы, казнённый по обвинению в «преступлениях» против государства и официальной религии</i> (<i>469-399 гг. до н. э.</i>) <i>Pl, C etc.</i>"],
  ["Socraticus", "<big>Socraticus</big>\n\n<b>I Socraticus, a, um</b>\nсократовский, сократический (philosophi <i>C</i>).\n\n<b>II Socraticus, i</b> <i>m</i>\nсократик, ученик Сократа <i>C</i>."],
  ["socrualis", "<big>socrualis</big>\n\n<b>socrualis, e</b> [socrus]\nотносящийся к тёще <i>или</i> к свекрови (hereditas <i>Sid</i>)."],
  ["socrus", "<big>socrus</big>\n\n<b>socrus, us</b> <i>f</i> [socer]\nтёща <i>или</i> свекровь <i>Ter, C etc.</i>: s. (magna) <i>Dig</i> бабка мужа <i>или</i> жены; s. major <i>Dig</i> прабабка мужа <i>или</i> жены."],
  ["sodalicium", "<big>sodalicium</big>\n\n<b>sodalicium, i</b> <i>n</i> [sodalicius]\n<b>1)</b> сотоварищество, дружеский союз <i>Ctl, VM, J</i>; <i>перен.</i> пирующая компания <i>rhH</i>;\n<b>2)</b> тайное общество, объединение (для заговора, для подкупа, для собирания голосов): lex Licinia de sodaliciis <i>C</i> <i>закон 55 г. до н. э., каравший изгнанием за участие в тайных обществах, которые занимались подкупом избирателей.</i>"],
  ["sodalicius", "<big>sodalicius</big>\n\n<b>sodalicius, a, um</b> [sodalis]\nотносящийся к обществу, товарищеский (jus <i>O</i>); <i>перен.</i> келейный, тайный (collegia <i>Dig</i>)."],
  ["sodalis", "<big>sodalis</big>\n\n<b>I sodalis, e</b>\nтоварищеский, дружественный, дружеский (turba <i>O</i>).\n\n<b>II sodalis, is</b> (<i>abl.</i> i <i>и</i> e) <i>m</i>\n<b>1)</b> товарищ, приятель, друг <i>Pl, Ter, C etc.</i>; собрат (s. alicujus in aliqua re <i>C</i>); спутник (Eurus s. hiemis <i>H</i>);\n<b>2)</b> собутыльник <i>C</i>;\n<b>3)</b> член общества, соучастник, сообщник <i>Pl, C</i>;\n<b>4)</b> <i>pl.</i> члены жреческой коллегии (sodales Augustales <i>T</i>)."],
  ["sodalitas", "<big>sodalitas</big>\n\n<b>sodalitas, atis</b> <i>f</i>\n<b>1)</b> дружеский союз, содружество, товарищество (intima <i>T</i>): homo summa sodalitate <i>C</i> человек, имеющий множество друзей;\n<b>2)</b> застольное общество, пирующая компания, приятельский кружок <i>C</i>;\n<b>3)</b> религиозное братство, жреческая коллегия (s. Lupercorum <i>C</i>);\n<b>4)</b> тайный союз, общество, политическая клика <i>C</i>."],
  ["sodalitius", "<big>sodalitius</big>\n\n<b>sodalitius, a, um</b> <i>v. l.</i>\n= sodalicius."],
  ["sodes", "<big>sodes</big>\n\n<b>sodes</b> [<i>из</i> si + audes]\nпожалуйста, если угодно, сделай милость (dic, s. <i>Ter, J</i>; duc nos, s. <i>Ter</i>): jube, s., nummos curari <i>C</i> благоволи уплатить деньги."],
  ["Sodoma", "<big>Sodoma</big>\n\n<b>Sodoma, orum</b> <i>n</i> (<i>реже в</i> <i>sg.</i> <i>и</i> <b>Sodoma, ae</b> <i>f</i>)\nСодом, <i>город в древней Палестине</i> <i>Vlg, Eccl</i>."],
  ["Sodomi", "<big>Sodomi</big>\n\n<b>Sodomi, orum</b> <i>и</i> <b>Sodomitae, arum</b> <i>m</i>\nжители Содома <i>Eccl</i>."],
  ["sodomia", "<big>sodomia</big>\n\n<b>sodomia, ae</b> <i>f</i>\nсодомия <i>Eccl</i>."],
  ["Sodomita", "<big>Sodomita</big>\n\n<b>Sodomita, ae</b> <i>m, f</i>\nсодомский (arbor <i>Aug</i>; cinis <i>Eccl</i>)."],
  ["Sodomitae", "<big>Sodomitae</big>\n\n<b>Sodomitae, arum</b> <i>m</i> <i>v. l.</i>\n= Sodomi."],
  ["Sodomitanus", "<big>Sodomitanus</big>\n\n<b>Sodomitanus, a, um</b> <i>Eccl</i>\n= Sodomita."],
  ["Sogdiana regio", "<big>Sogdiana regio</big>\n\n<b>Sogdiana regio</b> <i>f</i>\nСогдиана, <i>область между реками Oxus <i>и</i> Jaxartes</i> <i>QC</i>."],
  ["Sogdiani", "<big>Sogdiani</big>\n\n<b>Sogdiani, orum</b> <i>m</i>\nжители Sogdiana regio <i>QC, PM</i>."],
  ["sol", "<big>sol</big>\n\n<b>I sol, solis</b> <i>m</i>\n<b>1)</b> солнце (s. oritur, occidit <i>C, Cs, L etc.</i>): s. oriens <i>Cs etc.</i> восток; s. occidens <i>H</i> запад; sole primo <i>J</i>, orto <i>H</i> <i>или</i> novo <i>V</i> с восходом солнца; medio sole <i>Ph</i> в полдень; supremo sole <i>H etc.</i> на закате; patria alio sub sole jacens <i>V</i> другое (новое) отечество; nondum omnium dierum sol occidit <i>погов. L</i> не в последний же раз закатилось солнце;\n<b>2)</b> (<i>тж.</i> <i>pl.</i>) солнечное сияние, свет солнца (in sole ambulare <i>C</i>): soles et nubila <i>O</i> дни погожие и пасмурные; soles assidui <i>Lcr</i> <i>или</i> nimii <i>M</i> солнечный зной;\n<b>3)</b> день (s. niger <i>H</i>): tres soles totidem noctes <i>V</i> три дня и столько же ночей, трое суток;\n<b>4)</b> (<i>редко</i>) год: femina (canis), binos quae tulerit soles <i>Nem</i> собака, достигшая двухлетнего возраста;\n<b>5)</b> общественность, общество (aliquid in solem producere <i>C</i>): in solem procedere <i>C</i> публично выступить (с речью);\n<b>6)</b> <i>перен.</i> светило, светоч (Publius Africanus, s. alter <i>C</i>).\n\n<b>II Sol, Solis</b> <i>m</i>\n<i>бог Солнца, сын Гипериона и Теи, брат Луны и Авроры, впоследствии отождествлённый с Аполлоном</i> <i>C etc.</i>: Solis urbs <i>PM</i> = Heliopolis."],
  ["solaciolum", "<big>solaciolum</big>\n\n<b>solaciolum, i</b> <i>n</i> [<i>demin. к</i> solacium]\nнебольшое (слабое) утешение (doloris <i>Ctl</i>)."],
  ["solacium", "<big>solacium</big>\n\n<b>solacium, i</b> <i>n</i> [solor]\n<b>1)</b> утешение, утеха, отрада (hoc est mihi s. <i>Cs</i>; aves — solatia ruris <i>O</i>); облегчение (s. malorum <i>C</i>); <i>pl.</i> слова утешения (solacia dicere <i>или</i> dare alicui <i>O, C</i>);\n<b>2)</b> возмещение, вознаграждение за потерю (s. damni <i>Lcn</i>; sine solacio agere <i>T</i>; aliquid pro suo solacio habere <i>CJ</i>)."],
  ["solae", "<big>solae</big>\n\n<b>solae</b> <i>Pl, Ter</i>\n= solius (<i>gen. f sg. к</i> solus)."],
  ["solamen", "<big>solamen</big>\n\n<b>solamen, inis</b> <i>n</i> [solor]\nутешение, отрада (luctum solamine fallere <i>VF</i>); облегчение (s. mali <i>V</i>)."],
  ["solamentum", "<big>solamentum</big>\n\n<b>solamentum, i</b> <i>n</i> <i>Eccl</i>\n= solamen."],
  ["solanum", "<big>solanum</big>\n\n<b>solanum, i</b> <i>n</i> <i>бот.</i>\nпаслён <i>CC, PM</i>."],
  ["solanus", "<big>solanus</big>\n\n<b>solanus, i</b> <i>m</i> [sol]\n(<i>sc.</i> ventus) восточный ветер <i>Vtr</i>."],
  ["solaris", "<big>solaris</big>\n\n<b>solaris, e</b> [sol]\nсолнечный (lumen <i>O</i>): circulus s. <i>PM</i> орбита солнца || обращенный к солнцу (pars arboris <i>PM</i>)."],
  ["solarium", "<big>solarium</big>\n\n<b>I solarium, i</b> <i>n</i> [solarius]\n<b>1)</b> солнечные часы <i>Pl ap. AG, Vr, C</i>;\n<b>2)</b> форум (<i>в Риме, где находились установленные Сципионом Назикой солнечные часы</i>): ad s. versari <i>C</i> бывать на форуме, <i>т. е.</i> вращаться в обществе;\n<b>3)</b> часы (<i>вообще</i>); s. ex aqua <i>C</i> водяные часы;\n<b>4)</b> солярий (плоская крыша, терраса, вышка, балкон) <i>Pl, Su, Dig</i>.\n\n<b>II solarium, i</b> <i>n</i> [solum II]\n(<i>sc.</i> vectigal) земельный налог <i>Dig</i>."],
  ["solarius", "<big>solarius</big>\n\n<b>solarius, a, um</b> [sol]\nсолнечный (horologium <i>PM</i>)."],
  ["solati-", "<big>solati-</big>\n\n<b>solati-</b> <i>v. l.</i>\n= solaci-."],
  ["solatio", "<big>solatio</big>\n\n<b>solatio</b> <i>v. l.</i>\n= solicatio."],
  ["solator", "<big>solator</big>\n\n<b>solator, oris</b> <i>m</i> [solor]\nутешитель <i>Tib, St</i>."],
  ["solatus", "<big>solatus</big>\n\n<b>I solatus, a, um</b> [sol]\nсильно обожжённый солнцем <i>или</i> поражённый солнечным ударом <i>PM</i>.\n\n<b>II solatus, a, um</b> <i>part. pf. к</i>\nsolo.\n\n<b>III solatus, a, um</b> <i>part. pf. к</i>\nsolor."],
  ["soldurius", "<big>soldurius</big>\n\n<b>soldurius, i</b> <i>m</i> (<i>кельт.</i>; <i>лат.</i> devotus)\nпреданный человек, связанный клятвой дружинник (<i>у галльск. предводителей</i>) <i>Cs</i>."],
  ["soldus", "<big>soldus</big>\n\n<b>soldus, a, um</b> <i>syncop.</i> <i>Vr, H</i>\n= solidus I."],
  ["solea", "<big>solea</big>\n\n<b>solea, ae</b> <i>f</i> [solum II, 4]\n<b>1)</b> сандалия, <i>лёгкая обувь, состоящая из подошвы с ремнями</i> (soleae ligneae <i>Pt</i>): soleas demere <i>Pl</i> (deponere <i>M</i>) разуваться (<i>садясь за стол</i>); soleas poscere <i>H, PJ</i> просить сандалии, <i>т. е.</i> собираться уходить;\n<b>2)</b> ножная колодка (<i>надевавшаяся на арестованных</i>) <i>rhH, C</i>;\n<b>3)</b> подошва копыта <i>Veg</i>;\n<b>4)</b> <i>зоол.</i> морской язык, <i>вид камбалы</i> (<i>Pleuronectes Solea</i>, L.) <i>O, PM</i>;\n<b>5)</b> выжималка (<i>для выжимания оливкового масла</i>) <i>Col</i>."],
  ["solearis", "<big>solearis</big>\n\n<b>solearis, e</b> [solea]\nимеющий форму подошвы (cella <i>Spart</i>)."],
  ["solearius", "<big>solearius</big>\n\n<b>solearius, i</b> <i>m</i> [solea]\nбашмачник, изготовляющий сандалии <i>Pl</i>."],
  ["soleatus", "<big>soleatus</big>\n\n<b>soleatus, a, um</b> [solea]\nобутый в сандалии <i>C, Sen, Pt, AG</i>."],
  ["solemn-", "<big>solemn-</big>\n\n<b>solemn-</b> <i>и</i> <b>solenti-</b> <i>v. l.</i>\n= sollemn-."],
  ["Solenses", "<big>Solenses</big>\n\n<b>Solenses, ium</b> <i>m</i>\nжители города Soli <i>C</i>."],
  ["soleo", "<big>soleo</big>\n\n<b>soleo</b>, (<i>арх.</i> <b>solui</b>), <b>solitus sum, ere</b>\nиметь обыкновение (aliquid dicere <i>Sl</i>): lubens fecero et solens <i>Pl</i> сделаю (это) охотно, да и не впервые мне это || обыкновенно случаться, часто бывать (quod in plerisque negotiis solet <i>Sl</i>): posteriores cogitationes sapientiores solent esse <i>C</i> более поздние мысли обычно бывают и более разумными || обыкновенно поступать, часто делать: ita ut soleo <i>Ap</i> согласно моему обыкновению; ut solet <i>impers.</i> как обычно происходит; solet fieri <i>Pt</i> это бывает || находиться обычно в связи (cum aliquo <i>Pl</i>). — <i>См. тж.</i> solitum <i>и</i> solitus."],
  ["soler-", "<big>soler-</big>\n\n<b>soler-</b> <i>v. l.</i>\n= soller-."],
  ["soli", "<big>soli</big>\n\n<b>I soli</b> <i>gen. к</i>\nsolum II.\n\n<b>II soli</b>\n<b>1.</b> <i>dat. к</i> sol;\n<b>2.</b> <i>dat.</i> <i>и арх.</i> <i>gen. к</i> solus.\n\n<b>III Soli (Soloe), orum</b> <i>m</i>\nСолы, <i>приморский город в Киликии, к зап. от устья Пирама (позднее Pompejopolis), родина Хрисиппа, Менандра и Арата</i> <i>C, L, PM, Mela</i>."],
  ["soliar", "<big>soliar</big>\n\n<b>soliar, aris</b> <i>n</i>\nковёр для solium <i>Vr</i>."],
  ["solicatio", "<big>solicatio</big>\n\n<b>solicatio, onis</b> <i>f</i> [sol]\nдлительное пребывание на солнце <i>CA</i>."],
  ["solicit-", "<big>solicit-</big>\n\n<b>solicit-</b> <i>v. l.</i>\n= sollicit-."],
  ["solidamen", "<big>solidamen</big>\n\n<b>solidamen, inis</b> <i>n</i> <i>Eccl</i>\n= solidamentum."],
  ["solidamentum", "<big>solidamentum</big>\n\n<b>solidamentum, i</b> <i>n</i> [solido]\nоснова, опора (caeli <i>Aug</i>; corporis <i>Lact</i>; pietatis <i>Aug</i>)."],
  ["solidatio", "<big>solidatio</big>\n\n<b>solidatio, onis</b> <i>f</i> [solidus I]\n<b>1)</b> уплотнение <i>Vtr</i>;\n<b>2)</b> укрепление <i>Vtr</i>."],
  ["solide", "<big>solide</big>\n\n<b>solide</b> [solidus I]\n<b>1)</b> плотно, без пустот <i>или</i> отверстий <i>Col, Pt</i>;\n<b>2)</b> основательно, изрядно, хорошо, вполне (scire aliquid <i>Pl</i>)."],
  ["solidesco", "<big>solidesco</big>\n\n<b>solidesco, —, —, ere</b> [solidus I]\n<b>1)</b> уплотняться, твердеть (moles sub aqua solidescit <i>Vtr</i>);\n<b>2)</b> срастаться (rupta cartilago non solidescit <i>PM</i>)."],
  ["solidipes", "<big>solidipes</big>\n\n<b>solidipes, pedis</b> <i>adj.</i> [solidus + pes]\nнепарнокопытный, однокопытный <i>PM</i>."],
  ["soliditas", "<big>soliditas</big>\n\n<b>soliditas, atis</b> <i>f</i> [solidus I]\n<b>1)</b> плотность (corporum <i>C</i>);\n<b>2)</b> прочность, твёрдость, крепость <i>Vtr, Pall</i>; толщина (arboris <i>Pall</i>); <i>pl.</i> плотные массы <i>Vtr</i>;\n<b>3)</b> общая масса, совокупность (possessionis <i>CJ</i>)."],
  ["solido", "<big>solido</big>\n\n<b>solido, avi, atum, are</b>\n<b>1)</b> уплотнять, скреплять (aream creta <i>V</i>);\n<b>2)</b> укреплять (muros <i>T</i>; rem Romanam <i>AV</i>);\n<b>3)</b> сращивать (ossa fracta <i>PM</i>)."],
  ["solidum", "<big>solidum</big>\n\n<b>I solidum, i</b> <i>n</i> [solidus I]\n<b>1)</b> плотное вещество, нечто твёрдое: solido offendere <i>H</i> натолкнуться на нечто твёрдое;\n<b>2)</b> твёрдая почва (solido procedere <i>L</i>);\n<b>3)</b> целое, полная сумма, капитал (s. solvere alicui <i>C</i>): in s. appellare <i>T</i> требовать весь капитал сполна.\n\n<b>II solidum</b> <i>adv.</i>\nкрепко, сильно (iratus <i>Ap</i>)."],
  ["solidus", "<big>solidus</big>\n\n<b>I solidus, a, um</b>\n<b>1)</b> плотный (corpus <i>Ter</i>); массивный (vasa auro solida <i>T</i>); крепкий, твёрдый (terra <i>C</i>; adamas, telum <i>V</i>); прочный (structura <i>Vtr</i>);\n<b>2)</b> твёрдый, непоколебимый (mens <i>H</i>); прочный, надёжный (fides <i>T</i>); незыблемый, нерушимый (laus <i>C</i>); подлинный, истинный (libertas <i>L</i>; gaudium <i>Ter</i>);\n<b>3)</b> полный, целый, весь (dies <i>H</i>); цельный (aper <i>PM</i>): consulatus s. <i>L</i> консульство, длившееся круглый год || сплошной, нераздвоенный (ungula <i>PM, Sol</i>): decies (<i>sc.</i> sestertium) solidum <i>H</i> целый миллион (сестерциев); solida (<i>sc.</i> sestertia) centum <i>Pt</i> добрых 100 000 сестерциев.\n\n<b>II solidus, i</b> <i>m</i> (<i>sc.</i> nummus)\nсолид, <i>золотая монета, введённая вместо aureus = 25 denarii</i> <i>Ap, Dig, CJ etc.</i>"],
  ["solifer", "<big>solifer</big>\n\n<b>solifer, fera, ferum</b> [sol + fero]\nприносящий солнце: plaga solifera <i>SenT</i> край восходящего солнца, восток."],
  ["soliferreum", "<big>soliferreum</big>\n\n<b>soliferreum, i</b> <i>n</i>\n= solliferreum."],
  ["solifuga", "<big>solifuga</big>\n\n<b>solifuga, ae</b> <i>f</i> [sol + fugio]\nтарантул <i>PM, Sol, Eccl</i>."],
  ["soligena", "<big>soligena</big>\n\n<b>soligena, ae</b> <i>m, f</i> [sol + gigno]\nрождённый солнцем (Aeetes <i>VF</i>)."],
  ["soliloquium", "<big>soliloquium</big>\n\n<b>soli–loquium, i</b> <i>n</i> [solus]\nмонолог <i>Aug</i>."],
  ["solipuga", "<big>solipuga</big>\n\n<b>solipuga</b> <i>и</i> <b>solipugna, ae</b> <i>f</i>\nбелый ядовитый муравей <i>C ap. PM, Lcn</i>."],
  ["solis", "<big>solis</big>\n\n<b>I solis</b> <i>gen. sg. к</i>\nsol.\n\n<b>II solis</b> <i>dat. abl. pl. к</i>\nsolus."],
  ["solistimus", "<big>solistimus</big>\n\n<b>solistimus, a, um</b> <i>v. l.</i>\n= sollistimus."],
  ["solistit-", "<big>solistit-</big>\n\n<b>solistit-</b> <i>v. l.</i>\n= solstit-."],
  ["Solitanus", "<big>Solitanus</big>\n\n<b>Solitanus, a, um</b> (<i>предпол. от</i> promunturium Solis <i>в Африке</i>)\nсолитанский (cochleae <i>PM, Vr</i>)."],
  ["solitarie", "<big>solitarie</big>\n\n<b>solitarie</b>\nодиноко, отдельно <i>Eccl</i>."],
  ["solitarius", "<big>solitarius</big>\n\n<b>solitarius, a, um</b> [solus]\n<b>1)</b> склонный к одиночеству, необщительный (homo solus ac s. <i>Ap</i>);\n<b>2)</b> одинокий (vita <i>Q</i>; cena <i>PJ</i>);\n<b>3)</b> одиночный, единичный, особый (stellae <i>PM</i>; virtus <i>C</i>)."],
  ["solitas", "<big>solitas</big>\n\n<b>solitas, atis</b> <i>f</i> [solus]\nодиночество <i>Acc, Ap, Tert</i>."],
  ["solitatim", "<big>solitatim</big>\n\n<b>solitatim</b> <i>Fronto</i>\n= solitarie."],
  ["solitaurilia", "<big>solitaurilia</big>\n\n<b>solitaurilia, ium</b> <i>n</i> <i>VM</i>\n= suovetaurilia."],
  ["solito", "<big>solito</big>\n\n<b>I solito, avi, —, are</b> [<i>intens. к</i> soleo]\nиметь обыкновение (aliquid facere <i>AG</i>).\n\n<b>II solito</b> <i>adv.</i>\nпо обыкновению, обычно <i>Is</i>."],
  ["solitudo", "<big>solitudo</big>\n\n<b>solitudo, inis</b> <i>f</i> [solus]\n<b>1)</b> уединение, пустынность, безлюдье (loci <i>C</i>; in agris <i>C</i>): s. ante ostium <i>Ter</i> никого нет перед дверью || <i>pl.</i> пустынные места <i>C, Cs, L</i>;\n<b>2)</b> одиночество, беспомощность, беззащитность (liberorum, viduarum <i>C</i>);\n<b>3)</b> нехватка <i>или</i> отсутствие (magistratuum <i>L</i>)."],
  ["solitum", "<big>solitum</big>\n\n<b>solitum, i</b> <i>n</i> [solitus]\nобыкновение, нечто обычное: ex solito <i>VP, Sen</i> по обыкновению, обыкновенно; praeter <i>V, H</i> (super <i>Sen</i>, ultra <i>T</i>) s. против обыкновения; plus solito <i>L</i> больше (сверх) обыкновенного; solito major <i>L</i> больше обыкновенного, необычайно большой."],
  ["solitus", "<big>solitus</big>\n\n<b>solitus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> soleo;\n<b>2.</b> <i>adj.</i> обыкновенный, обычный, привычный, общепринятый (cibus <i>CC</i>; mos <i>O, Sen, QC</i>; labor <i>O</i>): solitum quicquam liberae civitatis <i>L</i> нечто присущее свободному государству."],
  ["solium", "<big>solium</big>\n\n<b>solium, i</b> <i>n</i> [<i>одного корня с</i> sedeo]\n<b>1)</b> престол, трон (s. Jovis <i>O</i>; s. regale <i>L, QC</i>);\n<b>2)</b> царская власть, царство (solio depellere <i>T</i>);\n<b>3)</b> кресло (in solio sedens <i>O</i>);\n<b>4)</b> ванна (s. calidae aquae <i>CC</i>; in solio lavari <i>M</i>);\n<b>5)</b> гроб, саркофаг (jacere in solio <i>QC</i>)."],
  ["solivagus", "<big>solivagus</big>\n\n<b>solivagus</b> <i>adv.</i> [solus + vagor]\n<b>1)</b> одиноко бродящий, живущий особняком (bestiae <i>C</i>); движущийся один (caelum <i>C</i>);\n<b>2)</b> односторонний, узкий (cognitio <i>C</i>)."],
  ["sollemne", "<big>sollemne</big>\n\n<b>sollemne, is</b> <i>n</i> [sollemnis]\n<b>1)</b> торжество (s. ludorum <i>L</i>); торжественный обряд (s. nuptiarum <i>Su, T</i>; s. funeris <i>T</i>); празднество (s. instituere <i>L</i>);\n<b>2)</b> обыкновение, обычай (nostrum <i>C</i>): sollemnia repetere <i>T</i> заниматься повседневными делами; sollemnia insanire <i>H</i> дурачиться как все; s. est <i>H</i> общепринято; sollemnia juris <i>Dig, C, J</i> юридические формальности;\n<b>3)</b> <i>pl.</i> жертвы (exta sollemnium <i>L</i>)."],
  ["sollemnis", "<big>sollemnis</big>\n\n<b>sollemnis, e</b> [solius]\n<b>1)</b> ежегодно празднуемый, каждый год справляемый, ежегодный (sacra <i>Lcr</i>; ludi <i>C</i>);\n<b>2)</b> торжественный (verba <i>C, O</i>); праздничный (epulae <i>C</i>); священный (fax <i>O</i>);\n<b>3)</b> общепринятый, заведённый, установленный (caerimoniae <i>VM</i>; mos <i>Su</i>); обыкновенный, обычный (iter <i>C</i>). — <i>См. тж.</i> sollemne."],
  ["sollemnitas", "<big>sollemnitas</big>\n\n<b>sollemnitas, atis</b> <i>f</i> [sollemnis]\n<b>1)</b> торжество, празднество <i>AG, Vop, Lampr etc.</i>;\n<b>2)</b> <i>юр.</i> форма, формальность (juris <i>Dig, CJ</i>)."],
  ["sollemniter", "<big>sollemniter</big>\n\n<b>sollemniter</b> [sollemnis]\n<b>1)</b> торжественно (convivium instituere <i>Just</i>);\n<b>2)</b> обычным способом, как установлено (se purificare <i>PM</i>);\n<b>3)</b> <i>юр.</i> с соблюдением правовых форм (testamenti voluntatem complere <i>Dig</i>)."],
  ["sollemnizo", "<big>sollemnizo</big>\n\n<b>sollemnizo, —, —, are</b>\nторжественно справлять (pascha <i>Aug</i>)."],
  ["sollenn-", "<big>sollenn-</big>\n\n<b>sollenn-</b> <i>v. l.</i>\n= sollemn-."],
  ["sollers", "<big>sollers</big>\n\n<b>sollers, ertis</b> (<i>abl. sg.</i> i <i>или</i> e) <i>adj.</i> [solius + ars]\n<b>1)</b> искусный, умелый (s. lyrae <i>H</i>); изобретательный, хитроумный (Ulixes <i>O</i>; s. alicujus rei <i>Sl, Sil etc.</i> <i>и</i> ad aliquid <i>Amm, Lact</i>);\n<b>2)</b> замысловатый, сложный (geometriae sollertissimae formulae <i>Ap</i>);\n<b>3)</b> тонкий (descriptio partium <i>C</i>);\n<b>4)</b> затейливый, плодовитый (natura <i>C</i>); плодородный (fundus <i>Cato</i>)."],
  ["sollerter", "<big>sollerter</big>\n\n<b>sollerter</b> [sollers]\nискусно, затейливо, ловко, хитроумно <i>C, T, Vop etc.</i>"],
  ["sollertia", "<big>sollertia</big>\n\n<b>sollertia, ae</b> <i>f</i> [sollers]\n<b>1)</b> мастерство, изобретательность, большое искусство, ловкость (cogitandi <i>C</i>; venandi <i>Sen</i>): perpendiculi s. <i>Ap</i> тонкое (искусное) применение отвеса (<i>в строительном искусстве</i>);\n<b>2)</b> остроумие, хитрость (ingenii <i>Sl, Vtr</i>; naturae <i>C</i>)."],
  ["sollicitatio", "<big>sollicitatio</big>\n\n<b>sollicitatio, onis</b> <i>f</i> [sollicito]\n<b>1)</b> тревога, беспокойство, забота: s. alicujus rei <i>Ter</i> беспокойство по поводу чего-л.;\n<b>2)</b> подстрекательство, происки <i>C, Sen</i>;\n<b>3)</b> соблазн (sollicitationibus expugnari <i>Sen</i>)."],
  ["sollicitator", "<big>sollicitator</big>\n\n<b>sollicitator, oris</b> <i>m</i> [sollicito]\nподстрекатель (servi <i>Dig</i>); разрушитель (alienarum nuptiarum <i>Dig</i>)."],
  ["sollicite", "<big>sollicite</big>\n\n<b>sollicite</b> [sollicitus]\n<b>1)</b> озабоченно, встревоженно, беспокойно (bona mutabilia s. possidentur <i>Sen</i>);\n<b>2)</b> заботливо, тщательно (curam alicujus rei agere <i>Su</i>; aliquem excipere <i>PJ</i>; se custodire <i>Sen</i>)."],
  ["sollicito", "<big>sollicito</big>\n\n<b>sollicito, avi, atum, are</b> [sollicitus]\n<b>1)</b> трясти, потрясать, колебать: s. mundum de suis sedibus <i>Lcr</i> потрясать мир в его основаниях || взрывать, взрыхлять, вспахивать (tellurem <i>V</i>); волновать, бороздить (remis freta <i>V</i>);\n<b>2)</b> бряцать, ударять (s. stamina pollice <i>O</i>);\n<b>3)</b> возбуждать (lentum cupidinem cantu <i>H</i>; aegrum stomachum <i>H</i>); гнать, преследовать (arcu feras <i>O</i>); раздражать, ослаблять (stomachum vomitu <i>CC</i>); расшатывать, нарушать (statum civitatis <i>L</i>; pacem <i>L</i>); беспокоить, тревожить, удручать (haec cura me sollicitat <i>C</i>); выводить из себя, раздражать, гневить (Jovem <i>L</i>; hostes <i>Lcn</i>);\n<b>4)</b> прельщать, обольщать, соблазнять (plebem <i>Sl</i>; oculos <i>Sen</i>; aliquem donis <i>O</i>); побуждать, склонять (aliquem ad venenum dandum <i>C</i>): sollicitor putare nullos esse deos <i>O</i> я склонен думать, что никаких богов нет || возмущать, возбуждать, подстрекать (servos ad aliquem necandum <i>C</i>); призывать (aliquem ut aliquid faciat <i>Cs</i>); убеждать, уговаривать (aliquem precibus ne spem ponat <i>O</i>); приглашать (aliquem in Italiam <i>Just</i>). — <i>См. тж.</i> sollicitus."],
  ["sollicitudo", "<big>sollicitudo</big>\n\n<b>sollicitudo, inis</b> <i>f</i> [sollicitus]\n<b>1)</b> волнение, беспокойство, тревога (cura et s. <i>Ter, C</i>; omnem sollicitudinem pro aliqua re deponere <i>Sen</i>);\n<b>2)</b> забота (sollicitudines domesticae <i>C</i>): s. alicujus rei <i>C, Q etc.</i> забота по поводу чего-л.;\n<b>3)</b> попечение, обязанность (ad sollicitudinem suam revocare <i>Dig</i>)."],
  ["sollicitus", "<big>sollicitus</big>\n\n<b>sollicitus, a, um</b> [solius + cieo]\n<b>1)</b> взволнованный, волнующийся (mare <i>V</i>); безостановочный (motus <i>Lcr</i>); потрясаемый (Hispaniae armis sollicitae <i>Sl</i>); возбуждённый, озабоченный, встревоженный, обеспокоенный, мятущийся (animus <i>H</i>; homo <i>C etc.</i>; anxius atque s. <i>Sen</i>): futuri exspectatione s. <i>Sen</i> испытывающий страх перед будущим || дрожащий (manus <i>O</i>): sollicitum aliquem habere <i>Pl, C</i> тревожить кого-л.;\n<b>2)</b> внушающий беспокойство, тревожный, беспокойный (nox <i>L</i>; pax <i>L</i>); волнующий, лишающий покоя (amor, cura <i>O</i>; timor <i>O</i>): sollicitis annis consumere vitam <i>Man</i> проводить жизнь в треволнениях;\n<b>3)</b> боязливый, робкий (equus, lepus <i>O</i>; preces <i>H, O</i>);\n<b>4)</b> насторожившийся, внимательный, чуткий (canes ad strepitus solliciti <i>L</i>)."],
  ["solliferreum", "<big>solliferreum</big>\n\n<b>solliferreum, i</b> <i>n</i> [solius + ferrum]\nметательное копьё целиком из железа <i>L, AG</i>."],
  ["sollistimus", "<big>sollistimus</big>\n\n<b>sollistimus, a, um</b> [<i>superl. к</i> solius]\nполный, совершенный, <i>культ.</i> благоприятный (<i>см.</i> tripudium) <i>C, L</i>."],
  ["sollus", "<big>sollus</big>\n\n<b>sollus, a, um</b> [<i>одного корня с</i> salvus <i>и</i> solidus] <i>арх.</i>\nцелый, полный <i>LM</i>."],
  ["solo", "<big>solo</big>\n\n<b>I solo, avi, atum, are</b> [solus]\nопустошать, делать безлюдным (s. populis urbes <i>Sl</i>; solatae peste domus <i>SenT</i>).\n\n<b>II Solo</b> <i>v. l.</i>\n= Solo(n)."],
  ["Soloe", "<big>Soloe</big>\n\n<b>Soloe, orum</b> <i>v. l.</i>\n= Soli III."],
  ["soloecismus", "<big>soloecismus</big>\n\n<b>soloecismus, i</b> <i>m</i> (<i>греч.</i>)\n<b>1)</b> солецизм, синтаксическая погрешность, неправильное соединение слов <i>rhH, Q, Sinnius Capito ap. AG</i>;\n<b>2)</b> погрешность (<i>вообще</i>), ошибка, промах <i>M, J, Eccl</i>."],
  ["soloecista", "<big>soloecista</big>\n\n<b>soloecista, ae</b> <i>f</i> (<i>греч.</i>)\nгрешащий солецизмами <i>Hier</i>."],
  ["soloecum", "<big>soloecum</big>\n\n<b>soloecum, i</b> <i>n</i> <i>C, AG</i>\n= soloecismus."],
  ["Solon", "<big>Solon</big>\n\n<b>Solo(n), onis</b> <i>m</i>\nСолон, <i>афинский законодатель, архонт в 594 г. до н. э., один из «семи мудрецов», автор элегий; умер в 559 г. до н. э.</i> <i>Pl, C, L etc.</i>"],
  ["Solonium", "<big>Solonium</big>\n\n<b>Solonium, i</b> <i>n</i> <i>или</i> <b>Solonius ager</b>\n<i>местность в южн. Латии</i> <i>C, L</i>."],
  ["solor", "<big>solor</big>\n\n<b>solor, atus sum, ari</b> <i>depon.</i>\n<b>1)</b> утешать (aliquem verbis amicis <i>O</i>; lenire dolentem solando F);\n<b>2)</b> облегчать (laborem cantu <i>V</i>); унимать (lacrimas <i>O</i>); ослаблять (metum <i>V</i>); смягчать, умерять (aestum fluviis <i>H</i>); утолять (famem <i>V</i>);\n<b>3)</b> подкреплять, освежать (fessos opibus <i>V</i>);\n<b>4)</b> возмещать, вознаграждать (s. aliquam dote <i>T</i>)."],
  ["solox", "<big>solox</big>\n\n<b>I solox, ocis</b> <i>adj.</i>\nвсклокоченный, взъерошенный (lana <i>Fronto</i>); покрытый свалявшейся шерстью (pecus <i>LM</i>).\n\n<b>II solox, ocis</b> <i>f</i> (<i>sc.</i> vestis)\nодежда из грубошёрстной ткани <i>Tert</i>."],
  ["solporeus", "<big>solporeus</big>\n\n<b>solporeus</b> <i>Enn</i>\n= sulphureus."],
  ["solpuga", "<big>solpuga</big>\n\n<b>solpuga, ae</b> <i>f</i> <i>v. l.</i>\n= solipuga."],
  ["solsequia", "<big>solsequia</big>\n\n<b>sol–sequia, ae</b> <i>f</i> <i>и</i> <b>solsequium, i</b> <i>n</i> [sequor] <i>предпол.</i>\nподсолнечник <i>Is</i>."],
  ["solstitialis", "<big>solstitialis</big>\n\n<b>solstitialis, e</b> [solstitium]\n<b>1)</b> относящийся к летнему солнцестоянию (dies <i>C</i>; tempus <i>O</i>): nox s. <i>O</i> самая короткая ночь; sidus solstitiale <i>Just</i> = Sirius; s. orbis <i>C</i> (circulus <i>Vr</i>) тропик Рака; morbus s. <i>Pl</i> тепловой удар <i>или</i> сильная лихорадка;\n<b>2)</b> относящийся к разгару лета, летний: solstitiali tempore <i>L</i> в разгар лета;\n<b>3)</b> солнечный (orbis <i>L</i> — <i>ср.</i> 1)."],
  ["solstitium", "<big>solstitium</big>\n\n<b>solstitium, i</b> <i>n</i> [sol + sisto]\n<b>1)</b> солнцестояние, солнцеворот (aestivum, brumale <i>Col</i>);\n<b>2)</b> летнее солнцестояние <i>Cato, Vr, C</i>;\n<b>3)</b> летняя пора, летний зной (s. pecori defendere <i>V</i>)."],
  ["solubilis", "<big>solubilis</big>\n\n<b>solubilis, e</b> [solvo]\n<b>1)</b> подверженный разложению <i>или</i> разрушению, тленный (caro <i>Eccl</i>; mundus <i>MF</i>);\n<b>2)</b> разлагающий (aer <i>CA</i>)."],
  ["solum", "<big>solum</big>\n\n<b>I solum</b> <i>adv.</i> [solus]\nтолько, лишь (suis s. incommodis commoveri <i>C</i>): non s..., sed etiam <i>C etc.</i> не только..., но и; non s. не то что, не говоря уже (fessus jam vivendo, non s. rebus gerendis <i>VM</i>).\n\n<b>II solum, i</b> <i>n</i>\n<b>1)</b> земля, почва (s. fertile <i>QC</i>; membra solo ponere <i>O</i>): ea, quae solo continentur <i>PJ</i> <i>и</i> res soli <i>Dig</i> недра <i>или</i> недвижимости;\n<b>2)</b> ровное место, основание (urbem ad s. diruere <i>QC</i> <i>и</i> a solo evertere <i>Eutr</i>): murus quod fuit ante, s. <i>O</i> пустое место там, где прежде стояли стены (Трои); aequare solo <i>L</i> срыть до основания; Cereale s. <i>V</i> плоский хлеб (<i>используемый в виде скатерти или блюда</i>); quod(cumque) in s. (venit) <i>погов. Vr, C</i> = всё, что ни попадётся (что ни придёт в голову);\n<b>3)</b> пол (s. marmoreum <i>C</i>);\n<b>4)</b> подошва йоги (s. hominis <i>Vr</i>): loca nullius ante trita solo <i>Lcr</i> места, куда прежде не ступала ничья нога || подошва обуви (calcei sine solo <i>Is</i>);\n<b>5)</b> страна, область, край (s. patriae <i>C</i>): s. genitale <i>Vop</i> (natale <i>O</i>) родина; s. vertere (mutare) <i>C etc.</i> эмигрировать <i>или</i> отправляться в изгнание, <i>ирон. Pt</i> менять поле своей деятельности; s. caeleste <i>O</i> небесный свод, небо."],
  ["solummodo", "<big>solummodo</big>\n\n<b>solum–modo</b>\nтолько, всего лишь <i>Q, Dig</i>."],
  ["Soluntinus", "<big>Soluntinus</big>\n\n<b>Soluntinus, i</b> <i>m</i>\nжитель города Solus <i>C</i>."],
  ["solus", "<big>solus</big>\n\n<b>I solus, a, um</b> (<i>gen.</i> ius, <i>dat.</i> i, <i>редко по 2-му скл.</i>)\n<b>1)</b> один лишь (res consulibus solis nota <i>L</i>): soli ex omnibus <i>C</i> только они одни из всех-; solae triginta minae <i>Ter</i> всего лишь 30 мин; soli duo homines <i>C</i> только два человека; ex aliquo solo quaerere <i>Cs</i> допрашивать кого-л. с глазу на глаз; omnem rem narrabit sola soli <i>Ter</i> она расскажет ему всё наедине || один-единственный (sola via salutis <i>VF</i>);\n<b>2)</b> один, одинокий, покинутый (sola sum: habeo hic neminem <i>Ter</i>);\n<b>3)</b> уединённый, пустынный, безлюдный (loca <i>Pl, Lcr, C etc.</i>; insula <i>Ctl</i>): sola sub nocte <i>V</i> в глухую ночь.\n\n<b>II Solus, untis</b> <i>f</i>\nСолунт, <i>город на сев. побережье Сицилии, между Панормом и Гимерой</i> <i>PM</i>."],
  ["solute", "<big>solute</big>\n\n<b>solute</b> [solutus]\n<b>1)</b> свободно, беспрепятственно (moveri <i>C</i>); легко, без труда (dicere <i>C</i>);\n<b>2)</b> беспечно, беззаботно (s. ac neglegenter agere <i>L</i>);\n<b>3)</b> безудержно, разнузданно (lascivire <i>T</i>)."],
  ["solutilis", "<big>solutilis</big>\n\n<b>solutilis, e</b> [solutus]\nлегко расползающийся, разваливающийся (navis <i>Su</i>)."],
  ["solutim", "<big>solutim</big>\n\n<b>solutim</b> <i>Tert</i>\n= solute."],
  ["solutio", "<big>solutio</big>\n\n<b>solutio, onis</b> <i>f</i> [solvo]\n<b>1)</b> развязанность: s. linguae <i>C</i> плавная, свободная речь;\n<b>2)</b> разложение, ослабление, надломленность (totius hominis <i>C</i>); расслабленность (ventris <i>PM</i>);\n<b>3)</b> уплата, платёж (rerum creditarum <i>C</i>; aeris alieni <i>L</i>);\n<b>4)</b> решение, разъяснение (captionis sophisticae <i>AG</i>);\n<b>5)</b> <i>стих.</i> разрешение (spondei in dactylum)."],
  ["solutor", "<big>solutor</big>\n\n<b>solutor, oris</b> <i>m</i> [solvo]\n<b>1)</b> вскрывающий (signaculi <i>Aug</i>);\n<b>2)</b> плательщик <i>Tert</i>."],
  ["solutus", "<big>solutus</big>\n\n<b>solutus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> solvo;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> распущенный (crines <i>H etc.</i>); развязанный, неподпоясанный (tunica <i>Q</i>); рыхлый (solutior terra facilius patet radicibus <i>Sen</i>), <i>но</i>: terrae solutae <i>H</i> земля, освободившаяся от снега || расслабленный, расстроенный (stomachus <i>Scr</i>): s. mero <i>Pt</i> обессилевший от вина, захмелевший; gestu solutae manus <i>Sen</i> движением дрожащей руки;\n<b>2)</b> свободный, независимый, вольный (homo, animus <i>C</i>); нестеснённый, неограниченный, свободный (optio eligendi <i>C</i>); свободный от долгов (praedia <i>C</i>); беззаботный (s. in paupertate <i>H</i>): s. gaudio vultus <i>Pt</i> просиявшее от радости лицо; solutum est можно, разрешается (aliquid facere <i>T</i>); s. (ab) aliqua re <i>C, H, PM etc.</i> <i>или</i> alicujus rei <i>H</i> свободный от чего-л. <i>или</i> не подверженный (не подвластный) чему-л.; solutis curis <i>Ctl</i> отбросив заботы;\n<b>3)</b> разнузданный (libido <i>L</i>); развязный <i>или</i> безудержный (risus <i>H</i>); распущенный, распутный (dicta factaque <i>T</i>);\n<b>4)</b> вялый (lenitas <i>C</i>); беспечный, небрежный (s. in gestu <i>C</i>);\n<b>5)</b> не связанный ритмически, нестихотворный, прозаический (oratio <i>AG</i>); свободный (carmen <i>Q</i>);\n<b>6)</b> искусный, умелый: s. ad dicendum <i>или</i> in dicendo <i>C</i> бегло говорящий, хорошо владеющий языком;\n<b>7)</b> расплывчатый <i>или</i> бессвязный (verba soluta atque diffluentia <i>C</i>)."],
  ["solvo", "<big>solvo</big>\n\n<b>solvo, solvi</b> (<i>поэт. тж.</i> <b>solui</b>), <b>solutum, ere</b> [se + luo]\n<b>1)</b> отвязывать (navem <i>Cs etc.</i>; canem <i>Ph</i>; lorum de pera <i>Pt</i>); распрягать (equum <i>H</i>); вскрывать (epistulam <i>C</i>); разрывать (vinculum epistulae <i>QC</i>); расплетать, распускать (crines <i>H</i>); распускать, расстёгивать (zonam <i>H</i>); развязывать, распутывать (vinctos <i>C</i>; nodum <i>H, Tib</i>); распоясывать (tunicam <i>Tib</i>); распускать, поднимать (vela <i>V</i>); снимать (juga tauris <i>V</i>); раскрывать, расширять: s. artum animum <i>H</i> развеселиться, <i>по др.</i> расщедриться; s. versus <i>Q</i> переложить стихи прозой; s., <i>тж.</i> ancoram (<i>или</i> navem) s. <i>C etc.</i> поднимать якорь, сниматься с якоря, отчаливать (s. e portu <i>Pl</i>; a terra <i>Cs</i>);\n<b>2)</b> платить, уплачивать (aes alienum <i>Sl, L</i>; pecuniam creditam <i>Cs</i>; mercedem <i>Pt</i>): s. pro aliqua re <i>C</i> платить за что-л.; s. aliquid ab aliquo <i>Pl, C</i> распорядиться кому-л. об уплате чего-л.; solvendo non esse <i>C</i> не быть платёжеспособным; in solutum (pro soluto) <i>Sen, Dig</i> в уплату;\n<b>3)</b> нести, терпеть, отбывать (poenam <i>C, Sl</i>);\n<b>4)</b> исполнять, выполнять (vota <i>Pl etc.</i>); соблюдать, держать, сдерживать (fidem <i>Ter etc.</i>);\n<b>5)</b> отплачивать, возмещать, отблагодарить (s. beneficia <i>Caelius ap. C</i>);\n<b>6)</b> искупать (injuriam <i>O</i>): alicui suprema s. <i>T</i> <i>или</i> exsequias s. <i>V</i> воздавать кому-л. последние почести;\n<b>7)</b> выдавать (praemia <i>C</i>);\n<b>8)</b> освобождать (aliquem ab obligatione <i>Dig</i>; militem sacramento <i>Dig</i>); избавлять (aliquem aliquo <i>V</i>; terras perpetua formidine <i>V</i>; aliquem cura <i>C</i>; luctu <i>V</i>);\n<b>9)</b> оправдывать, реабилитировать (aliquem crimine <i>C</i>);\n<b>10)</b> давать волю, разнуздывать, развязывать (s. cupiditates <i>QC</i>; s. linguam ad jurgia <i>O</i>); <i>поэт.</i> разверзать, открывать (os <i>O</i>); расслаблять (infantiam deliciis <i>Q</i>; alvum <i>CC</i>);\n<b>11)</b> разрушать, ломать (pontem <i>T</i>); разбивать (navem <i>Q</i> —<i>ср.</i> 1): s. frontem alicujus <i>Pt</i> раскроить лоб кому-л.;\n<b>12)</b> расторгать (foedus <i>Eutr</i>; nuptiae divortio solutae <i>Dig</i>);\n<b>13)</b> рассеивать, разгонять (faces solvebant noctem <i>PJ</i>): solvi in ventos <i>VF</i> развеяться по ветру;\n<b>14)</b> растворять, растоплять, плавить (nives <i>O</i>); распускать (coetum <i>O</i>; convivium <i>Q</i>; agmina <i>V</i>); разлучать (amantes <i>O</i>); закрывать, смыкать (alicui lumina <i>V</i>);\n<b>15)</b> <i>med.-pass.</i> solvi размягчаться, оттаивать, таять (terrae solutae <i>H</i>); разрежаться (gravitas aeris solvitur <i>Sen</i>); разлагаться, гнить (viscera solvuntur <i>V</i>); расслабляться, слабеть, становиться вялым (membra solvuntur <i>V</i>): solvi in somnos <i>V</i> <i>и</i> solvi somno <i>Ap</i> погрузиться в сон; <i>но</i>: somno solutus <i>C</i> проснувшийся; gaudio solutus <i>O</i> вне себя от радости; (morte) solvi <i>O</i> умереть; inter cenandum solutus est <i>Sen</i> он скончался во время трапезы;\n<b>16)</b> прекращать, снимать (obsidionem <i>QC</i>): hiems solvitur <i>H</i> зима кончается || подрывать, разрушать (imperium <i>L</i>); нарушать (pacem <i>Eutr</i>); отбрасывать, подавлять (pudorem <i>V</i>);\n<b>17)</b> разъяснять, объяснять (carmina <i>O</i>; errorem <i>Ph</i>); решать, разгадывать (aenigma <i>Q, J</i>). — <i>См. тж.</i> solutus."],
  ["Solyma", "<big>Solyma</big>\n\n<b>Solyma, orum</b> <i>n M</i> <i>и</i> <b>Solyma, ae</b> <i>f</i> <i>Eccl</i>\n= Hierosolyma."],
  ["Solymi", "<big>Solymi</big>\n\n<b>Solymi, orum</b> <i>m</i>\n<b>1)</b> <i>PM</i> = Solyma;\n<b>2)</b> жители Иерусалима <i>T</i>."],
  ["Solymus", "<big>Solymus</big>\n\n<b>I Solymus, a, um</b> [Solyma]\nиерусалимский (leges <i>J</i>).\n\n<b>II Solymus, i</b> <i>m</i>\nСолим, <i>один из спутников Энея, миф. основатель города Sulmo, родины Овидия</i> <i>O</i>."],
  ["somniator", "<big>somniator</big>\n\n<b>somniator, oris</b> <i>m</i> [somnio]\nвидящий сны, сновидец <i>Sen, Vlg, Eccl</i>."],
  ["somniculose", "<big>somniculose</big>\n\n<b>somniculose</b> [somniculosus]\nсонно, сонливо, вяло (agere <i>Pl</i>)."],
  ["somniculosus", "<big>somniculosus</big>\n\n<b>somniculosus, a, um</b> [somnus]\n<b>1)</b> сонный, сонливый, вялый (senectus <i>C</i>; genus mancipiorum <i>Col</i>; glis <i>M</i>);\n<b>2)</b> оглушающий, смертоносный (aspis <i>AG</i>)."],
  ["somnifer", "<big>somnifer</big>\n\n<b>somnifer, fera, ferum</b> [somnus + fero]\n<b>1)</b> наводящий сон, снотворный (vis papaveris <i>PM</i>); усыпляющий (virga Mercurii <i>O</i>);\n<b>2)</b> смертоносный, смертельный (venenum <i>O</i>)."],
  ["somnificus", "<big>somnificus</big>\n\n<b>somnificus, a, um</b> [somnium + facio]\n<b>1)</b> снотворный, усыпительный (medicamentum <i>PM</i>);\n<b>2)</b> смертоносный (vis aspidum <i>PM</i>)."],
  ["somnio", "<big>somnio</big>\n\n<b>somnio, avi, atum, are</b> [somnium]\n<b>1)</b> видеть сон (сны), грезить (s. de aliquo <i>C</i>; s. totas noctes <i>C</i>): hic vigilans somniat <i>Pl</i> он видит сны наяву || видеть во сне (s. aliquid <i>C</i>): s. somnium <i>Pl</i> видеть сон;\n<b>2)</b> (<i>тж.</i> s. ineptias <i>Col</i>) бредить, нести вздор <i>Pl etc.</i>"],
  ["somnior", "<big>somnior</big>\n\n<b>somnior, —, ari</b> <i>depon.</i> <i>Ap, Pt</i>\n= somnio."],
  ["somniosus", "<big>somniosus</big>\n\n<b>somniosus, a, um</b>\nсонливый, сонный <i>CA</i>."],
  ["somnium", "<big>somnium</big>\n\n<b>somnium, i</b> <i>n</i> [somnus]\n<b>1)</b> сон, сновидение (s. videre <i>O</i>): per somnia <i>C</i> во сне; interpres somnium (somniorum) <i>Poeta ap. C, Q</i> толкователь снов;\n<b>2)</b> <i>pl. поэт.</i> сон, дрема <i>V</i>;\n<b>3)</b> игра воображения, вздор (fabulae! somnia! <i>Ter</i>)."],
  ["somnolentia", "<big>somnolentia</big>\n\n<b>somnolentia (somnulentia), ae</b> <i>f</i>\nсонливость <i>Sid</i>."],
  ["somnolentus", "<big>somnolentus</big>\n\n<b>somnolentus (somnulentus), a, um</b> <i>Ap, Sol</i>\n= somniculosus."],
  ["somnurnus", "<big>somnurnus</big>\n\n<b>somnurnus, a, um</b> [somnus]\nявившийся во сне, сонный (imago <i>Vr</i>)."],
  ["somnus", "<big>somnus</big>\n\n<b>somnus, i</b> <i>m</i>\n<b>1)</b> сон, дремота (altus <i>H, L, QC</i>; levis <i>H</i>): somnum capere <i>C</i> заснуть; somno se dare <i>C</i> ложиться спать; somno excitare <i>C</i> будить ото сна; somnum mentiri <i>Pt</i> притворяться спящим; somnum tenere <i>C</i> бороться со сном; somnum videre <i>C</i> спать; somnos ducere <i>V</i> продолжать спать <i>или</i> <i>H</i> (<i>тж.</i> facere somnos <i>O, J</i>) нагонять (наводить) сон, усыплять; per somnum (in somno, in somnis) <i>C etc.</i> во сне, во время сна; s. longus <i>H</i>, ferreus <i>V</i>, frigidus <i>VF</i> <i>и</i> niger <i>Sil</i> вечный сон, смерть;\n<b>2)</b> сонливость, вялость (somno deditus <i>C</i>); спячка, бездеятельность (in somnum conversa civitas <i>VP</i>);\n<b>3)</b> <i>поэт.</i> ночь (dies somnusque <i>V</i>)."],
  ["sona", "<big>sona</big>\n\n<b>sona, ae</b> <i>f</i> <i>v. l.</i>\n= zona."],
  ["sonabilis", "<big>sonabilis</big>\n\n<b>sonabilis, e</b> [sono]\nзвучный, звонкий (sistrum <i>O</i>)."],
  ["sonans", "<big>sonans</big>\n\n<b>sonans, antis</b>\n<b>1.</b> <i>part. praes. к</i> sono;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> (полно)звучный (verba <i>C</i>); гудящий (concha <i>O</i>);\n<b>2)</b> <i>грам.</i> гласный (littera <i>Ap</i>)."],
  ["sonax", "<big>sonax</big>\n\n<b>sonax, acis</b> <i>adj.</i> [sono]\nзвонкий, звучный, зычный, громкий (concha <i>Ap</i>)."],
  ["sonchus", "<big>sonchus</big>\n\n<b>sonchus (-os), i</b> <i>m</i> (<i>греч.</i>) <i>бот.</i>\nосот <i>PM</i>."],
  ["soniger", "<big>soniger</big>\n\n<b>soni–ger, gera, gerum</b>\nгромовой, гулкий (fragor <i>Aug</i>)."],
  ["sonipes", "<big>sonipes</big>\n\n<b>sonipes, pedis</b> [sonus + pes]\n<b>1.</b> <i>adj.</i> звонконогий, издающий звонкий топот (turba, <i>sc.</i> in Isidis sacris <i>GF</i>);\n<b>2.</b> <i>subst. m поэт.</i> конь <i>Ctl, V</i>."],
  ["sonitus", "<big>sonitus</big>\n\n<b>sonitus, us</b> <i>m</i> (<i>арх. gen.</i> i) [sono]\nзвук, плеск (remorum <i>Cs</i>); шум, вой (ventorum <i>Lcr</i>); лязг, бряцание (armorum <i>V, L</i>); грохот (tubae <i>H</i>); гром (Olympi <i>V</i>); топот (pedum <i>O</i>; ungularum <i>Pl</i>); треск (flammae <i>Nep</i>): s. verborum inanium <i>Sen</i> пустые слова."],
  ["sonivius", "<big>sonivius</big>\n\n<b>sonivius, a, um</b> [sonus]\nиздающий звук, производящий шум, <i>тк.</i> <i>культ.</i>: tripudium sonivium <i>C</i> счастливое предзнаменование (<i>когда священные куры клевали с такой жадностью, что корм с шумом падал обратно на землю</i>)."],
  ["sono", "<big>sono</big>\n\n<b>sono, ui, itum, are</b> (<i>арх. Enn, Pac, Acc, Lcr</i> <b>ere</b>) (<i>part. fut.</i> sonaturus)\n<b>1)</b> звучать, раздаваться (vox sonat <i>Pl</i>); издавать звук (tibiae sonant <i>O</i>); оглашаться (domus clamore sonat <i>L</i>); шипеть (spuma sonans <i>O</i>); шуметь, шелестеть (silva sonat <i>H</i>); бряцать (arma sonant <i>VF</i>); журчать (fons sonat <i>O</i>): aures sonant <i>CC</i> звенит в ушах || трещать (flamma sonat <i>O</i>); щебетать (hirundo sonat <i>V</i>): litterae sonantes <i>Ap</i> гласные буквы || урчать (circa stomachum alicui sonat <i>Pt</i>);\n<b>2)</b> играть, петь (sonans carmen lyra <i>H</i>; s. plectro <i>H</i>);\n<b>3)</b> произносить, говорить: s. raucum (rauca) <i>O, V</i> хрипло говорить; s. confusum <i>C</i> произносить неясные звуки; s. femineum <i>O</i> смеяться по-женски;\n<b>4)</b> воспевать, восхвалять, прославлять (magno ore aliquem <i>O</i>; bella <i>O</i>): atavos et avorum antiqua nomina s. <i>V</i> похваляться древними именами дедов и прадедов;\n<b>5)</b> давать отголосок, отзываться (ripae sonant <i>V</i>);\n<b>6)</b> выдавать звуком (s. furem <i>Prp</i>);\n<b>7)</b> значить, означать: quid haec vox sonat? <i>C</i> что значит это слово?; unum s. <i>C</i> иметь тот же смысл. — <i>См. тж.</i> sonans."],
  ["sonor", "<big>sonor</big>\n\n<b>sonor, oris</b> <i>m</i> [sono]\nзвон, звук (lepidus <i>Lcr</i>); голос (animalium immeditati sonores <i>Ap</i>); шум (sonorem dant silvae <i>V</i>)."],
  ["sonore", "<big>sonore</big>\n\n<b>sonore</b> [sonorus]\nзвучно, громко (oscitare <i>AG</i>)."],
  ["sonorus", "<big>sonorus</big>\n\n<b>sonorus, a, um</b> [sonor]\nзвонкий (cithara <i>Tib</i>); журчащий, рокочущий (flumen <i>V</i>); шумящий (nemus <i>St</i>); ревущий, завывающий (tempestas <i>V</i>)."],
  ["sons", "<big>sons</big>\n\n<b>sons, sontis</b>\n<b>1.</b> <i>adj.</i> виновный, преступный (reus <i>Pl</i>; anima <i>V</i>);\n<b>2.</b> <i>subst. m</i> преступник (punire sontes <i>C</i>)."],
  ["Sontiates", "<big>Sontiates</big>\n\n<b>Sontiates, um</b> <i>m</i>\nсонтиаты, <i>кельт. племя в Аквитании, к югу от Гарумны</i> <i>Cs</i>."],
  ["sonticus", "<big>sonticus</big>\n\n<b>sonticus, a, um</b> [sons]\n<b>1)</b> опасный, серьёзный (morbus <i>AG, Dig</i>);\n<b>2)</b> важный, уважительный (causa <i>Naev, Cato, Tib</i>)."],
  ["sonus", "<big>sonus</big>\n\n<b>sonus, i</b> <i>m</i> (<i>редко</i> <i>Ap, Amm gen.</i> us) [sono]\n<b>1)</b> звук (gravis, acutus <i>C</i>); звон (tubarum <i>C</i>); журчание, шум (fluminis <i>L</i>): sonum dare (reddere) <i>V, VF etc.</i> издавать звуки, звучать || отзвук, резонанс (balnei <i>Pt</i>);\n<b>2)</b> голос (cycni <i>H</i>): concordi sono <i>O</i> в один голос || шум (confusae urbis <i>V</i>);\n<b>3)</b> слово (ficti soni <i>O</i>): sonos voce (ore) reddere (edere) <i>O</i> ронять слова, говорить;\n<b>4)</b> речь (medio in sono <i>Q</i>);\n<b>5)</b> тон, способ изложения, стиль (historiae majorem sonum addere <i>C</i>)."],
  ["Sopater", "<big>Sopater</big>\n\n<b>Sopater, tri</b> <i>m</i>\nСопатер, <i>мужское имя</i> <i>C</i>."],
  ["sophia", "<big>sophia</big>\n\n<b>sophia, ae</b> <i>f</i> (<i>греч.</i>)\nмудрость <i>Enn, Sen, M</i>."],
  ["sophisma", "<big>sophisma</big>\n\n<b>sophisma, atis</b> <i>n</i> (<i>греч.</i>; <i>лат.</i> captio)\nсофизм, ложное умозаключение <i>Sen, AG</i>."],
  ["sophismation", "<big>sophismation</big>\n\n<b>sophismation, ī</b> <i>n</i> (<i>греч.</i>)\nнебольшой софизм <i>AG</i>."],
  ["sophista", "<big>sophista</big>\n\n<b>sophista, ae</b> <i>m</i> <i>AG, Ap, Sid</i>\n= sophistes."],
  ["sophistes", "<big>sophistes</big>\n\n<b>sophistēs, ae</b> <i>m</i> (<i>греч.</i>)\nсофист <i>C etc.</i>"],
  ["sophistice", "<big>sophistice</big>\n\n<b>I sophisticē</b>\nсофистически (interpretari legem <i>CJ</i>).\n\n<b>II sophisticē, ēs</b> <i>f</i>\nсофистическое искусство <i>Ap</i>."],
  ["sophisticus", "<big>sophisticus</big>\n\n<b>sophisticus, a, um</b>\nсофистический (captio <i>AG</i>)."],
  ["sophistria", "<big>sophistria</big>\n\n<b>sophistria, ae</b> <i>f</i>\nсофистика <i>Hier</i>."],
  ["Sophocles", "<big>Sophocles</big>\n\n<b>Sophoclēs, is</b> <i>и</i> <b>ī</b> <i>m</i>\nСофокл, <i>греч. автор трагедий, род. в Колоне (близ Афин) в 496 г. до н. э., умер в 406 г. до н. э.</i> <i>C, H, Q, AG</i>."],
  ["Sophocleus", "<big>Sophocleus</big>\n\n<b>Sophoclēus, a, um</b> [Sophocles]\nсофоклов (cothurnus <i>V, O</i>)."],
  ["Sophonisba", "<big>Sophonisba</big>\n\n<b>Sophonī(s)ba, ae</b> <i>f</i>\nСофони(с)ба, <i>дочь карфагенянина Гасдрубала, вышедшая замуж сначала за нумидийского царя Сифака, а затем за Масиниссу, который её отравил</i> <i>L</i>."],
  ["sophos", "<big>sophos</big>\n\n<b>I sophōs</b> <i>adv.</i> (<i>греч.</i>)\nславно!, отлично!, браво! (tergeminum mugire «s.» <i>M</i>; «s.» clamare <i>Pt</i>).\n\n<b>II sophos (-us), ī</b> <i>m</i> (<i>греч.</i>)\nмудрец <i>M</i>.\n\n<b>III sophos, a, um</b> <i>adj.</i>\nмудрый (gubernator <i>Ph</i>)."],
  ["Sophroniscus", "<big>Sophroniscus</big>\n\n<b>Sōphroniscus, ī</b> <i>m</i>\nСофрониск, <i>ваятель, отец философа Сократа</i> <i>VM, Sen</i>."],
  ["Sophrosyne", "<big>Sophrosyne</big>\n\n<b>Sōphrosynē, ēs</b> <i>f</i>\nСофросина, <i>дочь Дионисия Старшего</i> <i>Nep</i>."],
  ["sopio", "<big>sopio</big>\n\n<b>I sopio, ivi (ii), itum, ire</b>\n<b>1)</b> наводить сон, усыплять (aliquem herbis <i>O</i>): alto somno sopitus <i>QC</i> погружённый в глубокий сон;\n<b>2)</b> успокаивать: ventus sopitur <i>PM</i> ветер утихает; ignis sopitus <i>V</i> чуть тлеющий огонь; quiete <i>и</i> leto sopitus <i>Lcr</i> усопший;\n<b>3)</b> оглушать, лишать чувств (sopitus subito ictu <i>L</i>): quies sopita <i>L</i> крепкий сон.\n\n<b>II sopio, onis</b> <i>m</i>\n= membrum virile <i>Ctl</i>."],
  ["sopitio", "<big>sopitio</big>\n\n<b>sopitio, onis</b> <i>m</i> <i>Pt</i>\n(<i>v. l.</i>) = sopio II."],
  ["Sopolis", "<big>Sopolis</big>\n\n<b>Sopolis, is</b> <i>m</i>\nСополис, <i>живописец времён Цицерона</i> <i>C, PM</i>."],
  ["sopor", "<big>sopor</big>\n\n<b>sopor, oris</b> <i>m</i> [sopio I]\n<b>1)</b> беспамятство, бесчувственность (junci semine somnum allicere, sed modum servandum, ne s. fiat <i>PM</i>);\n<b>2)</b> крепкий, глубокий сон (soporem discutere ex oculis <i>Pl</i>): s. perpetuus <i>H</i> непробудный сон, смерть; sopori dare <i>Pl</i> убивать;\n<b>3)</b> снотворный напиток, усыпительное средство (dare alicui soporem <i>Nep</i>; e papavere s. gignitur <i>PM</i>);\n<b>4)</b> сонливость, вялость, лень (s. et ignavia <i>T</i>);\n<b>5)</b> висок (laevus <i>St</i>)."],
  ["soporatus", "<big>soporatus</big>\n\n<b>soporatus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> soporo;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> спящий (hostis <i>O</i>);\n<b>2)</b> одарённый снотворной силой (ramus <i>V</i>)."],
  ["soporifer", "<big>soporifer</big>\n\n<b>soporifer, fera, ferum</b> [sopor + fero]\nнавевающий сон (nox <i>Pt</i>); снотворный, усыпляющий (papaver <i>V</i>)."],
  ["soporo", "<big>soporo</big>\n\n<b>soporo, (avi), atum, are</b> [sopor]\n<b>1)</b> усыплять (aliquem <i>CC, PM</i>; opium mentem soporat <i>Scr</i>);\n<b>2)</b> успокаивать, унимать (dolorem <i>QC</i>); <i>перен.</i> гасить (imbre rogum <i>Sl</i>);\n<b>3)</b> делать снотворным, снабжать усыпительной силой (ramum vi Stygia s. <i>V</i>);\n<b>4)</b> спать: soporantes <i>Ambr</i> спящие."],
  ["soporus", "<big>soporus</big>\n\n<b>soporus, a, um</b>\n<b>1)</b> наводящий сон (nox <i>V, Lcn, St</i>);\n<b>2)</b> усыплённый, сонный (dapibus vinoque <i>VF</i>)."],
  ["Sora", "<big>Sora</big>\n\n<b>Sora, ae</b> <i>f</i>\nСора, <i>город вольсков в Латии на реке Лирис, к сев. от Арпина</i> <i>L, Sil, J, PM</i>."],
  ["Soracte", "<big>Soracte</big>\n\n<b>Soracte, is</b> (<i>abl.</i> e <i>и</i> i) <i>n</i>\nСоракт, <i>высокая гора в южн. Этрурии, к сев. от Рима, с храмом Аполлона</i> <i>Vr, H, V</i>."],
  ["soracum", "<big>soracum</big>\n\n<b>soracum, i</b> <i>n</i> (<i>греч.</i>)\nкорзина <i>или</i> сундук <i>Pl</i>."],
  ["Soranus", "<big>Soranus</big>\n\n<b>Soranus, a, um</b> [Sora]\nсорский <i>C, L</i>."],
  ["Sorax", "<big>Sorax</big>\n\n<b>Sorax, actis</b> <i>m</i> <i>PM</i>\n= Soracte."],
  ["sorbeo", "<big>sorbeo</big>\n\n<b>sorbeo, sorbui</b> (<i>редко</i> <b>sorpsi</b>), <b>—, ere</b>\n<b>1)</b> хлебать, втягивать в себя, поглощать, вбирать (aquam <i>QC</i>); всасывать (terra arida sorbet flumina <i>Frontin</i>): simul flare sorbereque haud facile est <i>погов. Pl</i> нелегко одновременно дуть и втягивать в себя, <i>т. е.</i> заниматься разными делами;\n<b>2)</b> <i>перен.</i> проглатывать (aliquid animo <i>C</i>); <i>pass.</i> идти ко дну, тонуть (navigia sorbentur <i>Sen</i>); переносить, терпеть (odia <i>C</i>)."],
  ["sorbilis", "<big>sorbilis</big>\n\n<b>sorbilis, e</b> [sorbeo]\nмогущий быть выпитым, <i>т. е.</i> жидкий (ovum <i>CC, CA, Pt</i>)."],
  ["sorbillo", "<big>sorbillo</big>\n\n<b>sorbillo</b> <i>v. l.</i>\n= sorbilo I."],
  ["sorbilo", "<big>sorbilo</big>\n\n<b>I sorbilo, —, —, are</b> [<i>demin. к</i> sorbeo]\nотхлёбывать, потягивать (vinum <i>Ap</i>).\n\n<b>II sorbilo</b> <i>adv.</i>\nотхлёбывая, мелкими глотками, по капле, <i>перен.</i> бедно, скудно (victitare <i>Pl</i>)."],
  ["sorbitio", "<big>sorbitio</big>\n\n<b>sorbitio, onis</b> <i>f</i> [sorbeo]\n<b>1)</b> хлебание, потягивание (s. dira cicutae <i>Pers</i>);\n<b>2)</b> похлёбка, суп, питьё (sorbitionem gustare <i>Ph</i>)."],
  ["sorbo", "<big>sorbo</big>\n\n<b>sorbo, —, —, ere</b> <i>VM, Ap</i>\n= sorbeo."],
  ["sorbum", "<big>sorbum</big>\n\n<b>sorbum, i</b> <i>n</i>\nрябина (<i>ягода</i>) <i>Cato, V, PM</i>."],
  ["sorbus", "<big>sorbus</big>\n\n<b>sorbus, i</b> <i>f</i>\nрябина (<i>дерево</i>) <i>Col, PM</i>."],
  ["sordeo", "<big>sordeo</big>\n\n<b>sordeo, dui, —, ere</b> [sordes]\n<b>1)</b> быть нечистым, быть грязным (toga sordet <i>Sen</i>; sordentes ganeae <i>AG</i>);\n<b>2)</b> не пользоваться уважением, казаться ничтожным, внушать презрение, вызывать отвращение (sordent tibi munera nostra <i>V</i>): sordentia verba <i>AG</i> грубые (непристойные) слова; pretium sordet <i>H</i> цена представляется (мне) слишком низкой; oculis sordet vicina voluptas <i>St</i> доступное наслаждение не нравится (нам)."],
  ["sordes", "<big>sordes</big>\n\n<b>sordes, is</b> <i>f</i> (<i>преим.</i> <i>pl.</i>; <i>gen. pl.</i> erum <i>Pl</i>)\n<b>1)</b> грязь, неопрятность, нечистота (sordi sepultus <i>Lcr</i> <i>и</i> sordibus oppletus <i>CJ</i>; ungues sine sordibus <i>H</i>): sordes allinere alicui rei <i>C</i> запачкать что-л.;\n<b>2)</b> неопрятное платье, плачевное состояние, жалкий вид (s. patris <i>Just</i>); <i>преим.</i> вретище, скорбная одежда, траур (s. lugubres <i>C</i>): s. suscipere <i>T</i> надеть траур;\n<b>3)</b> низость, низменность, презренность (hominis, vitae <i>C</i>; verborum <i>T</i>);\n<b>4)</b> низкое происхождение (obscuritas et s. <i>T</i>);\n<b>5)</b> отвратительная жадность, скаредность (s. in re familiari <i>C</i>);\n<b>6)</b> низкий человек (o s.! <i>C</i>); подонки общества, отребье (s. urbis et faex <i>C</i>)."],
  ["sordesco", "<big>sordesco</big>\n\n<b>sordesco, dui, —, ere</b> [sordeo]\nстановиться грязным, покрываться грязью, пачкаться (manibus alicujus <i>H</i>); <i>перен.</i> зарастать сорняком (ager sordescit <i>AG</i>); осквернять себя (lucrandi aviditate <i>Amm</i>)."],
  ["sordidatus", "<big>sordidatus</big>\n\n<b>sordidatus, a, um</b> [sordido]\n<b>1)</b> неопрятно одетый <i>Pl, Ter, C</i>;\n<b>2)</b> одетый в траур <i>или</i> в скорбную одежду просителя (flens ac s. <i>C</i>);\n<b>3)</b> <i>перен.</i> запятнанный, нечистый (conscientia <i>Sid</i>)."],
  ["sordide", "<big>sordide</big>\n\n<b>sordide</b> [sordes]\n<b>1)</b> грязно, в грязи (per plateas trahi <i>Lampr</i>);\n<b>2)</b> низко, низменно, недостойно (loqui <i>Pl</i>);\n<b>3)</b> в низах <i>или</i> в бедности (sordidius et abjectius nati <i>T</i>);\n<b>4)</b> крайне скупо, скряжнически (aliquid facere <i>Su</i>)."],
  ["sordido", "<big>sordido</big>\n\n<b>sordido, avi, atum, are</b>\nгрязнить, пачкать (terram pulvere <i>Sid</i>); осквернять (templum malis cogitationibus <i>Lact</i>)."],
  ["sordidulus", "<big>sordidulus</big>\n\n<b>sordidulus, a, um</b> [<i>demin. к</i> sordidus]\n<b>1)</b> грязноватый (toga <i>Pl</i>);\n<b>2)</b> подловатый (servulus <i>Pl</i>)."],
  ["sordidus", "<big>sordidus</big>\n\n<b>sordidus, a, um</b> [sordes]\n<b>1)</b> грязный, нечистый (toga <i>Pt</i>; fumus <i>H</i>); неопрятный (vestis <i>L</i>); испачканный (calcatis uvis <i>O</i>);\n<b>2)</b> одетый в траур (sordidi nati <i>H</i>);\n<b>3)</b> низкий, низменный, презренный (hostis, vita <i>C</i>; nomen <i>Just</i>; ingenium <i>Su</i>); подлый, позорный (adulterium <i>L</i>; lucrum <i>Q</i>); пошлый (orator <i>C</i>);\n<b>4)</b> жалкий, убогий (amictus, rus <i>V</i>);\n<b>5)</b> чрезвычайно скупой, алчный, жадный, скаредный (homo <i>C</i>; cupido <i>H</i>)."],
  ["sordifluus", "<big>sordifluus</big>\n\n<b>sordi–fluus, a, um</b> [fluo] <i>Jvc</i>\n= sordidus."],
  ["sorditudo", "<big>sorditudo</big>\n\n<b>sorditudo, inis</b> <i>f</i> [sordes]\nгрязь <i>Pl</i>."],
  ["sordulentus", "<big>sordulentus</big>\n\n<b>sordulentus, a, um</b> <i>Tert</i>\n= sordidus."],
  ["sorex", "<big>sorex</big>\n\n<b>sorex, icis</b> <i>m</i>\nземлеройка <i>Ter, Vr, Col etc.</i>: occentus soricis <i>VM</i> свист землеройки (<i>считавшийся дурной приметой</i>)."],
  ["soricinus", "<big>soricinus</big>\n\n<b>soricinus, a, um</b> [sorex]\nиздаваемый (пойманной) землеройкой (nenia <i>Pl</i>)."],
  ["sorites", "<big>sorites</big>\n\n<b>sorites, ae</b> <i>m</i> (<i>греч.</i>; <i>лат.</i> acervus)\nсорит, цепь умозаключений <i>C, Sen</i>."],
  ["soror", "<big>soror</big>\n\n<b>soror, oris</b> <i>f</i>\nсестра <i>Pl, Ter, C etc.</i>: s. patruelis <i>C, O</i> двоюродная сестра; novem <i>или</i> doctae sorores <i>O, Tib</i> музы; (tres) sorores <i>Prp etc.</i> = Parcae; vipereae sorores <i>O</i> = Furiae."],
  ["sororcula", "<big>sororcula</big>\n\n<b>sororcula, ae</b> <i>f</i> [<i>demin. к</i> soror]\nсестрица <i>Pl</i>."],
  ["sororicida", "<big>sororicida</big>\n\n<b>sororicida, ae</b> <i>m</i> [soror + caedo]\nубийца сестры <i>C</i>."],
  ["sororiculatus", "<big>sororiculatus</big>\n\n<b>sororiculatus, a, um</b>\n<i>слово неизвестного значения</i>: sororiculata vestis <i>PM</i> <i>род платья.</i>"],
  ["sororio", "<big>sororio</big>\n\n<b>sororio, —, —, are</b> [soror]\n(<i>о женских грудях</i>) подрастать вместе, как сестры, <i>т. е.</i> одновременно набухать (mammae sororiantes <i>PM</i>)."],
  ["sororius", "<big>sororius</big>\n\n<b>sororius, a, um</b> [soror]\nотносящийся к сестре, сестрин <i>Pl, C, O, Just</i>."],
  ["sorpsi", "<big>sorpsi</big>\n\n<b>sorpsi</b> <i>pf. к</i>\nsorbo."],
  ["sors", "<big>sors</big>\n\n<b>I sors, sortis</b> <i>f</i> [sero I]\n<b>1)</b> жребий <i>C, L</i>; <i>pl.</i> палочки, служившие для метания <i>или</i> вытягивания жребия (sortes miscere <i>C</i>; sortes in hydriam conjicere <i>C</i>): s. alicujus exit <i>C</i> (excidit <i>L</i>) жребий выпадает кому-л.; aliquid revocatur ad sortem <i>C</i> о чём-л. бросают жребий (прибегают к жеребьёвке); sortem ducere <i>C etc.</i> тянуть <i>или</i> бросать жребий; provincias in sortem conjicere <i>L</i> распределять провинции по жребию; extra sortem <i>C</i> не прибегая к жеребьёвке; caeca sorte <i>H</i> по воле слепого случая; sortes extenuatae <i>или</i> attenuatae <i>L</i> уменьшившиеся в объёме таблички с предсказаниями (<i>что считалось дурной приметой</i>);\n<b>2)</b> (<i>тж.</i> s. oraculi <i>L</i>) оракул, изречение оракула, прорицание (sortes per carmina dictae <i>H</i>): sortes Lyciae <i>V</i> прорицания Аполлона Ликийского;\n<b>3)</b> часть, доля, участие (praedae <i>V, O</i>): in nullam sortem bonorum natus <i>L</i> родившийся без права на долю в имуществе; s. prima первенец (Saturni <i>O</i>);\n<b>4)</b> удел, участь, судьба, предназначение (humana <i>L</i>; futura <i>V</i>; s. est sua cuique ferenda <i>Man</i>): s. suprema <i>H</i> кончина, смерть;\n<b>5)</b> общественное положение, место в обществе, звание (homo humillimae sortis <i>L</i>): s. prior <i>O</i> преимущество;\n<b>6)</b> служба, должность, профессия: res meae sortis est <i>L</i> это относится к моим служебным обязанностям; sorte abesse <i>C</i> отсутствовать по делам службы;\n<b>7)</b> пол: s. feminea (<i>или</i> altera) <i>O</i> женский пол;\n<b>8)</b> вид, разновидность, сорт, характер (beneficia primae sortis <i>Sen</i>); оборот (nova s. pugnae <i>V</i>);\n<b>9)</b> ссудный капитал, ссуда (sortem exsolvere <i>L</i>).\n\n<b>II sors, sordis</b> <i>f</i> <i>Ambr</i>\n= sordes."],
  ["sorsum", "<big>sorsum</big>\n\n<b>sorsum</b> <i>adv.</i> <i>Pl, Ctl, Lcr, Priap</i>\n= seorsum."],
  ["sorticula", "<big>sorticula</big>\n\n<b>sorticula, ae</b> <i>f</i> [<i>demin. к</i> sors]\nтабличка для бросания жребия <i>Su</i>."],
  ["sortifer", "<big>sortifer</big>\n\n<b>sortifer, fera, ferum</b> [sors + fero]\nизрекающий пророчества, прорицающий <i>Lcn</i>."],
  ["sortiger", "<big>sortiger</big>\n\n<b>sortiger, gera, gerum</b> <i>Lcn</i>\n(<i>v. l.</i>) = sortifer."],
  ["sortilegus", "<big>sortilegus</big>\n\n<b>I sortilegus, a, um</b> [sors + lego]\nпрорицающий, пророческий (Delphi <i>H</i>).\n\n<b>II sortilegus, i</b> <i>m</i>\nпрорицатель <i>Vr, C, Lcn</i>."],
  ["sortio", "<big>sortio</big>\n\n<b>sortio, ivi, itum, ire</b> <i>арх. Enn, Pl, Vr</i>\n= sortior."],
  ["sortior", "<big>sortior</big>\n\n<b>sortior, itus sum, iri</b> <i>depon.</i> [sors I]\n<b>1)</b> бросать жребий, решать жеребьёвкой (alicui <i>Pl</i>; inter se <i>C, L</i>): s. de aliqua re <i>T, Su etc.</i> <i>или</i> aliquid <i>C etc.</i> бросать жребий о чём-л.; regna vini s. talis <i>H</i> тянуть жребий, кому быть председателем пира;\n<b>2)</b> распределять, делить (laborem <i>V</i>): aequa lege necessitas sortitur insignes et imos <i>H</i> по одному и тому же закону необходимость воздаёт и великим, и малым;\n<b>3)</b> получать по жребию (provinciam <i>C, L</i>); unam potentiam sortiti prodesse <i>Sen</i> получившие в удел лишь одну силу — делать добро;\n<b>4)</b> получать, приобретать (amicum casu <i>H</i>); обретать (brevis vitae spatium <i>O</i>; exitum facilem <i>Su</i>);\n<b>5)</b> искать, разыскивать, выбирать (fortunam oculis <i>V</i>)."],
  ["sortis", "<big>sortis</big>\n\n<b>I sortis, is</b> <i>f</i> <i>Pl, Ter</i>\n= sors I.\n\n<b>II sortis</b> <i>gen. к</i>\nsors I."],
  ["sortitio", "<big>sortitio</big>\n\n<b>sortitio, onis</b> <i>f</i> [sortior]\nбросание жребия, жеребьёвка <i>Pl, C, Su</i>: sortitione <i>C</i> наудачу, по жребию."],
  ["sortito", "<big>sortito</big>\n\n<b>sortito</b> [sortior]\n<b>1)</b> по жребию <i>C</i>;\n<b>2)</b> по определению судьбы <i>Pl, H</i>."],
  ["sortitor", "<big>sortitor</big>\n\n<b>sortitor, oris</b> <i>m</i> [sortior]\nбросающий <i>или</i> вынимающий жребий <i>SenT</i>."],
  ["sortitus", "<big>sortitus</big>\n\n<b>I sortitus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> sortior;\n<b>2.</b> <i>adj.</i> доставшийся по жребию, определённый судьбой, предназначенный <i>C, QC etc.</i>\n\n<b>II sortitus, us</b> <i>m</i> [sortior]\n<b>1)</b> бросание жребия, жеребьёвка <i>Pl, C, V</i>;\n<b>2)</b> вотивная табличка <i>St</i>; судьба, участь, удел <i>St</i>."],
  ["Sosius", "<big>Sosius</big>\n\n<b>Sosius, a, um</b>\nСосий, <i>римск. nomen; наиболее известны</i>:\n<b>1)</b> C. S., <i>претор в 49 г. до н. э., завоеватель Иерусалима, консул в 32 г. до н. э., приверженец Антония, впоследствии примирившийся с Октавианом</i> <i>C</i>;\n<b>2)</b> <i>братья</i> Sosii, <i>известные римск. книготорговцы времён Горация</i> <i>H</i>."],
  ["sospes", "<big>sospes</big>\n\n<b>I sospes, itis</b> <i>adj.</i>\n<b>1)</b> невредимый (s. et incolumis <i>PJ</i>); уцелевший (navis s. ab ignibus <i>H</i>): versa est in cinerem sospite Troja viro <i>O</i> Троя обращена в пепел, но муж (мой, Одиссей,) жив;\n<b>2)</b> благополучный, счастливый (dies <i>Pl</i>).\n\n<b>II sospes, itis</b> <i>m</i>\nспаситель <i>Enn</i>."],
  ["sospita", "<big>sospita</big>\n\n<b>sospita, ae</b> <i>f</i> [sospes]\nспасительница (Juno <i>C etc.</i>)."],
  ["sospitalis", "<big>sospitalis</big>\n\n<b>sospitalis, e</b> [sospes]\nспасительный <i>Pl, Amm, Macr</i>."],
  ["sospitator", "<big>sospitator</big>\n\n<b>sospitator, oris</b> <i>m</i> [sospito]\nспаситель, избавитель <i>Ap, Eccl</i>."],
  ["sospitatrix", "<big>sospitatrix</big>\n\n<b>sospitatrix, icis</b> <i>f</i>\nспасительница, избавительница (dea <i>Ap</i>)."],
  ["sospito", "<big>sospito</big>\n\n<b>sospito, —, —, are</b> [sospes]\nспасать, сохранять, охранять (aliquem <i>Pl, Ctl, L</i>)."],
  ["Sotades", "<big>Sotades</big>\n\n<b>Sotades, is</b> <i>m</i>\nСотад, <i>уроженец Маронеи (Фракия), живший в Александрии (первая половина III в. до н. э.) греч. поэт, писавший на ионическом диалекте непристойные стихи</i> <i>M</i>."],
  ["Sotadicus", "<big>Sotadicus</big>\n\n<b>Sotadicus, a, um</b> [Sotades]\nсотадический: Sotadici (<i>sc.</i> versus) <i>Q, PJ, AG</i> стихи Сотада <i>или</i> во вкусе Сотада."],
  ["Sotas", "<big>Sotas</big>\n\n<b>Sotas</b> <i>и</i> <b>Sotes, ae</b> <i>m</i>\nСот, <i>имя врача</i> <i>M</i>."],
  ["soter", "<big>soter</big>\n\n<b>soter, eris</b> <i>m</i> (<i>acc.</i> era) (<i>греч.</i>)\nспаситель, избавитель <i>C, Eccl</i>."],
  ["soteria", "<big>soteria</big>\n\n<b>soteria, orum</b> <i>n</i> (<i>греч.</i>)\nсотерии, <i>подарки по случаю выздоровления</i> <i>M, St</i>."],
  ["Sotes", "<big>Sotes</big>\n\n<b>Sotes</b> <i>v. l.</i>\n= Sotas."],
  ["Sp.", "<big>Sp.</big>\n\n<b>Sp.</b> <i>сокр. к</i>\nSpurius."],
  ["spadicum", "<big>spadicum</big>\n\n<b>spadicum, i</b> <i>n</i> <i>Amm</i>\n= spadix I, 1."],
  ["spadix", "<big>spadix</big>\n\n<b>spadix, icis</b> (<i>греч.</i>)\n1.\n<b>1)</b> <i>m</i> пальмовая ветвь с плодом <i>AG</i>;\n<b>2)</b> <i>род струнного инструмента</i> <i>Q</i>;\n<b>2.</b> <i>adj.</i> бурокрасный, коричневый, каштанового цвета (equus <i>V</i>)."],
  ["spado", "<big>spado</big>\n\n<b>spado, onis</b> <i>m</i> (<i>греч.</i>)\n<b>1)</b> скопец, кастрат <i>H, L, J etc.</i>: gallus s. <i>Pt</i> каплун;\n<b>2)</b> мерин <i>Veg</i>;\n<b>3)</b> бесплодное растение (spadones surculi <i>Col</i>)."],
  ["spadonatus", "<big>spadonatus</big>\n\n<b>spadonatus, us</b> <i>m</i> [spado]\nсостояние евнуха, оскоплённость <i>Tert</i>."],
  ["spadoninus", "<big>spadoninus</big>\n\n<b>spadoninus, a, um</b> [spado]\nбесплодный (laurus <i>PM</i>)."],
  ["spadonius", "<big>spadonius</big>\n\n<b>spadonius, a, um</b> <i>PM</i>\n= spadoninus."],
  ["spaerita", "<big>spaerita</big>\n\n<b>spaerita, ae</b> <i>m</i> (<i>греч.</i>)\nкруглый пирожок <i>Cato</i>."],
  ["Span-", "<big>Span-</big>\n\n<b>Span-</b> <i>CA, Vlg, Eccl</i>\n= Hispan-."],
  ["spargo", "<big>spargo</big>\n\n<b>spargo, sparsi, sparsum, ere</b>\n<b>1)</b> сыпать, рассыпать (nummos populo <i>C</i>); сеять (semina humo <i>O</i>);\n<b>2)</b> брызгать, разбрызгивать, кропить (aquas <i>V</i>);\n<b>3)</b> бросать (fulmina in terras <i>O</i>): una sparsus manu <i>SenT</i> поверженный одной рукой (Герион) || отбрасывать прочь, удалять (Alciden ab Argis <i>VF</i>); метать (tela per Achivos <i>V</i>);\n<b>4)</b> рассеивать, разгонять (aper canes spargit <i>O</i>); взрывать, поднимать (pedibus arenam <i>V</i>); распылять, расточать, растрачивать (tempus <i>Sen</i>; <i>sc.</i> bona <i>H</i>); распределять, рассылать (exercitum per provinciam <i>T</i>): se in fugam s. <i>L</i> бежать врассыпную, разбегаться || широко раскидывать, распускать (cupressus spargit ramos <i>PM</i>);\n<b>5)</b> разлучать (fratres <i>Just</i>); разделять, расчленять (legiones <i>T</i>; genera in species <i>PM</i>); разрывать, рассекать (corpora <i>O</i>): s. bellum <i>T</i> вести войну в разных местах;\n<b>6)</b> осыпать, усыпать (humum foliis <i>V</i>); усеивать (caelum astris <i>O</i>); обрызгивать, окроплять (aliquid lacrima <i>O</i>); орошать (imbribus terras <i>Sen</i>); мочить, увлажнять, омывать (corpus fluviali lympha <i>V</i>); покрывать (virgulta fimo <i>V</i>); заливать (terras lumine <i>V</i>);\n<b>7)</b> увешивать, украшать (porticum tabellis <i>O</i>); разносить (nomen alicujus per urbes <i>O</i>); распространять (odorem <i>H</i>; <i>перен.</i> dementiam- <i>sc.</i> suam in proximos <i>Sen</i>): spargitur <i>impers. T</i> распространяется (разносится) слух. — <i>См. тж.</i> sparsus."],
  ["sparsilis", "<big>sparsilis</big>\n\n<b>sparsilis, e</b> [spargo]\nлегко развевающийся <i>Tert</i>."],
  ["sparsim", "<big>sparsim</big>\n\n<b>sparsim</b> [sparsus]\nрассеянно, разбросанно, отовсюду, там и сям (defluere <i>Ap</i>; commeminisse aliquid <i>AG</i>)."],
  ["sparsio", "<big>sparsio</big>\n\n<b>sparsio, onis</b> <i>f</i> [spargo]\n<b>1)</b> разбрасывание, рассеивание <i>St</i>;\n<b>2)</b> прыскание, разбрызгивание <i>Sen</i>."],
  ["sparsus", "<big>sparsus</big>\n\n<b>sparsus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> spargo;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> рассыпанный, разбросанный, рассеянный (gens sparsa per orbem <i>V</i>); растрёпанный, разметавшийся (capilli <i>O</i>);\n<b>2)</b> пёстрый (color columbarum <i>Ap</i>); испещрённый (anguis maculis sparsus <i>L</i>): sparsa pellis albo <i>V</i> шкура в белых крапинках || веснушчатый (os <i>L</i>);\n<b>3)</b> забрызганный, залитый (cruore sparsae manus <i>V</i>): humanitatis sale sparsus <i>C</i> с примесью тонкого юмора."],
  ["Sparta", "<big>Sparta</big>\n\n<b>Sparta, ae</b> <i>и</i> <b>Sparte, es</b> <i>f</i>\nСпарта (Лакедемон), <i>столица Лаконии, в долине реки Эврот, между горными хребтами Тайгет и Парной</i> <i>Pl, C, O etc.</i>"],
  ["Spartacus", "<big>Spartacus</big>\n\n<b>Spartacus, i</b> <i>m</i>\nСпартак, <i>уроженец Фракии, организатор и руководитель восстания римск. рабов (т. наз. Гладиаторская война 73-71 гг. до н. э.), одержавший ряд блестящих побед над римск. войсками, но павший в сражении с армией Красса</i> <i>L, Fl, VP, H</i>."],
  ["Spartanus", "<big>Spartanus</big>\n\n<b>I Spartanus, a, um</b> (= Lacedaemonius)\nспартанский <i>L, V, VM etc.</i>\n\n<b>II Spartanus, i</b> <i>m</i>\nспартанец <i>Pl, Nep, T</i>."],
  ["spartarius", "<big>spartarius</big>\n\n<b>spartarius, a, um</b> [spartum]\nизобилующий альфой (Carthago <i>PM</i>)."],
  ["Sparte", "<big>Sparte</big>\n\n<b>Sparte, es</b> <i>f</i> <i>v. l.</i>\n= Sparta."],
  ["spartea", "<big>spartea</big>\n\n<b>spartea, ae</b> <i>f</i> (<i>sc.</i> solea)\nподошва из альфы <i>Col</i>."],
  ["sparteolus", "<big>sparteolus</big>\n\n<b>sparteolus, i</b> <i>m</i> [sparteus]\nпожарный (снабжённый верёвками из альфы) <i>Tert</i>."],
  ["sparteus", "<big>sparteus</big>\n\n<b>sparteus, a, um</b> [spartum]\nизготовленный из альфы (funis <i>Cato, Col</i>; solea <i>Col</i>)."],
  ["Sparti", "<big>Sparti</big>\n\n<b>Sparti (-toe), orum</b> <i>m</i> (<i>греч.</i>)\nспарты, <i>миф. воинственное племя, выросшее из посеянных Кадмом драконьих зубов</i> <i>Vr ap. AG</i>."],
  ["Spartiacus", "<big>Spartiacus</big>\n\n<b>Spartiacus</b> <i>Ap</i>, <b>Spartiaticus</b> <i>Pl</i> <i>и</i> <b>Sparticus, a, um</b> <i>V</i>\n= Spartanus I."],
  ["Spartianus", "<big>Spartianus</big>\n\n<b>Spartianus, i</b> <i>m</i>\n(Aelius) Спартиан, <i>современник императоров Диоклетиана и Константина, один из шести «Scriptores Historiae Augustae».</i>"],
  ["Spartiates", "<big>Spartiates</big>\n\n<b>Spartiates, ae</b> <i>m</i>\nспартиат, <i>полноправный гражданин Спарты</i> <i>Pl, C</i>."],
  ["Spartiaticus", "<big>Spartiaticus</big>\n\n<b>Spartiaticus</b> <i>Pt</i>, <b>Sparticus</b> <i>V</i>\n= Spartanus I."],
  ["Spartoe", "<big>Spartoe</big>\n\n<b>Spartoe</b> <i>v. l.</i>\n= Sparti."],
  ["sparton", "<big>sparton</big>\n\n<b>sparton, i</b> <i>n</i> <i>v. l.</i>\n= spartum."],
  ["spartum", "<big>spartum</big>\n\n<b>spartum, i</b> <i>n</i> (<i>греч.</i>)\n<b>1)</b> <i>бот.</i> альфа <i>или</i> эспарто (<i>Stipa tenacissima</i>, L.), <i>идущая на изготовление плетёных изделий</i> <i>Vr, L, PM etc.</i>;\n<b>2)</b> канат из альфы <i>Cato, PM</i>."],
  ["sparulus", "<big>sparulus</big>\n\n<b>sparulus, i</b> <i>m</i> [<i>demin. к</i> sparus I]\nмаленький лещ <i>O, M</i>."],
  ["sparum", "<big>sparum</big>\n\n<b>sparum, i</b> <i>n</i> <i>LM</i>\n= sparus II."],
  ["sparus", "<big>sparus</big>\n\n<b>I sparus, i</b> <i>m</i> <i>предпол.</i>\nлещ <i>CC</i>.\n\n<b>II sparus, i</b> <i>m</i>\nнебольшое копьё, дротик <i>Vr, Sl, L, V</i>."],
  ["spasma", "<big>spasma</big>\n\n<b>spasma, atis</b> <i>n</i> <i>PM</i>\n= spasmus."],
  ["spasmus", "<big>spasmus</big>\n\n<b>spasmus, i</b> <i>m</i> (<i>греч.</i>)\nспазм, судорога <i>PM, Scr, CA</i>."],
  ["spasticus", "<big>spasticus</big>\n\n<b>spasticus, a, um</b> (<i>греч.</i>)\nстрадающий судорогой, спазматический <i>PM</i>."],
  ["spatalium", "<big>spatalium</big>\n\n<b>spatalium, i</b> <i>n</i> (<i>греч.</i>)\nбраслет <i>PM, Tert</i>."],
  ["spatalocinaedus", "<big>spatalocinaedus</big>\n\n<b>spatalocinaedus, i</b> <i>m</i> (<i>греч.</i>)\nутончённый развратник <i>Pt</i>."],
  ["spatha", "<big>spatha</big>\n\n<b>spatha, ae</b> <i>f</i> (<i>греч.</i>)\n<b>1)</b> ложечка <i>или</i> лопатка для размешивания <i>CC</i>;\n<b>2)</b> тонкая деревянная пластинка для уплотнения утка на вертикальном ткацком станке <i>Sen</i>;\n<b>3)</b> широкий обоюдоострый меч <i>T</i>;\n<b>4)</b> черешок пальмового листа <i>PM</i>;\n<b>5)</b> кокосовая <i>или</i> финиковая пальма <i>PM, Scr</i>."],
  ["spathalium", "<big>spathalium</big>\n\n<b>spathalium, i</b> <i>n</i> <i>v. l.</i>\n= spatalium."],
  ["spathula", "<big>spathula</big>\n\n<b>spat(h)ula, ae</b> <i>f</i> [<i>demin. к</i> spatha]\nпальмовая ветвь <i>Vlg</i>."],
  ["spatiator", "<big>spatiator</big>\n\n<b>spatiator, oris</b> <i>m</i> [spatior]\nгуляющий, фланёр, праздношатающийся <i>Cato</i>."],
  ["spatiolum", "<big>spatiolum</big>\n\n<b>spatiolum, i</b> <i>n</i> [<i>demin. к</i> spatium]\nнебольшое пространство <i>Pall, Eccl</i>."],
  ["spatior", "<big>spatior</big>\n\n<b>spatior, atus sum, ari</b> <i>depon.</i> [spatium]\n<b>1)</b> идти, шествовать (ad aras <i>V</i>); прохаживаться, прогуливаться (in xysto <i>C</i>): aliquot horis spatiatus <i>Pt</i> погуляв несколько часов;\n<b>2)</b> расширяться (alae spatiantes <i>O</i>); распространяться (radices spatiantur <i>PM</i>); развиваться, усиливаться (vires morbi, antequam spatientur, opprimere <i>Sen</i>)."],
  ["spatiose", "<big>spatiose</big>\n\n<b>spatiose</b> [spatiosus]\n<b>1)</b> обширно, широко, далеко (increscere <i>O</i>);\n<b>2)</b> долго <i>Prp</i>."],
  ["spatiositas", "<big>spatiositas</big>\n\n<b>spatiositas, atis</b> <i>f</i>\nпротяжение <i>Sid</i>."],
  ["spatiosus", "<big>spatiosus</big>\n\n<b>spatiosus, a, um</b> [spatium]\n<b>1)</b> обширный, большой (insula <i>PM</i>); просторный (domus <i>PJ</i>); широкий (via, quae ducit ad perditionem <i>Vlg</i>); пространный (spatiosa res est sapientia <i>Sen</i>); крупный (taurus <i>O</i>);\n<b>2)</b> длинный (vox <i>Q</i>); долгий, продолжительный (nox <i>O</i>; tempus, bellum <i>O</i>)."],
  ["spatium", "<big>spatium</big>\n\n<b>spatium, i</b> <i>n</i>\n<b>1)</b> пространство, протяжение (castrorum <i>C</i>); простор, место (s. non erat agitandi equos <i>Nep</i>);\n<b>2)</b> величина, размеры (colli <i>O</i>; elephantis <i>Lcn</i>); длина (viae <i>O</i>): in s. trahere <i>O</i> вытягивать (растягивать) в длину; in s. fugere <i>O</i> бежать прямо;\n<b>3)</b> расстояние, промежуток (inter muros <i>L</i>); отрезок, путь (s. longum itineris <i>Cs</i>): aequo spatio abesse (distare) ab aliqua re <i>Cs etc.</i> находиться на равном расстоянии от чего-л.; s. propinquitatis <i>Cs</i> небольшое (близкое) расстояние; vitale s. <i>CC</i>, <i>тж.</i> s. aetatis <i>Pl</i>, aevi <i>O</i> <i>и</i> vitae <i>O, PM</i> жизненный путь; addere in spatia <i>V, Sil</i> <i>и</i> spatia corripere <i>V, Su</i> ускорить прохождение пути;\n<b>4)</b> ристалище, ипподром (in spatio decurrere <i>C</i>): extremum (supremum) s. <i>Su</i> конец ристалища;\n<b>5)</b> место для гулянья (spatia facere <i>C</i>): communia spatia <i>C</i> места для общественных гуляний;\n<b>6)</b> промежуток времени, время, период, срок (s. triginta dierum <i>C</i>): eodem spatio <i>Cs</i> в (за) то же самое время; spatio brevi <i>H</i> в короткое время; perexiguo instermisso spatio <i>Cs</i> <i>или</i> spatio minimo interposito <i>Pt</i> по прошествии короткого времени, после коротенькой паузы; sex dies spatii ad aliquam rem conficiendam postulare <i>Cs</i> требовать шестидневного срока для совершения чего-л.; s. praeteriti temporis <i>C</i> прошлое, минувшее; spatio carpi <i>O</i> разрушаться от времени; s. animo dare <i>QC</i> дать срок для размышления; irae s. dare <i>L, Sen</i> дать остыть гневу; requiem spatiumque furori petere <i>V</i> просить передышки, чтобы дать улечься страсти; dolor spatio evanescit <i>O</i> с течением времени скорбь ослабевает; extremum s. vitae <i>J</i> предел жизни, кончина; spatio pugnae defatigati <i>Cs</i> утомлённые долгой битвой; <i>но</i>: pugnae s. dare <i>QC</i> сделать перерыв в битве;\n<b>7)</b> мера, размер, количество (trochaeus eodem spatio est quo choreus <i>C</i>)."],
  ["spatula", "<big>spatula</big>\n\n<b>I spatula</b> <i>v. l.</i>\n= spat(h)ula.\n\n<b>II spatula, ae</b> <i>f</i> (<i>греч.</i>)\nизысканность, нега, роскошь <i>Vr</i> (<i>v. l.</i>)."],
  ["speca", "<big>speca</big>\n\n<b>speca</b> <i>Vr</i>\n= spica."],
  ["specellatus", "<big>specellatus</big>\n\n<b>specellatus</b> <i>v. l.</i>\n= specillatus."],
  ["specialis", "<big>specialis</big>\n\n<b>I specialis, e</b> [species]\n<b>1)</b> особый, частный (genera <i>Sen</i>; quaestiones <i>Q</i>);\n<b>2)</b> особенный, своеобразный (aliquid speciale habere <i>Treb</i>).\n\n<b>II specialis, is</b> <i>m</i> (<i>sc.</i> amicus)\nзакадычный друг (s. et intimus <i>Sid</i>)."],
  ["specialitas", "<big>specialitas</big>\n\n<b>specialitas, atis</b> <i>f</i>\nособенность, своеобразие <i>Fronto, Tert, Is</i>."],
  ["specialiter", "<big>specialiter</big>\n\n<b>specialiter</b> [specialis]\nособенно, в особенности, в частности <i>Col, CC, Dig etc.</i>"],
  ["speciatim", "<big>speciatim</big>\n\n<b>speciatim</b> <i>Vulc</i>\n= specialiter."],
  ["speciatus", "<big>speciatus</big>\n\n<b>speciatus, a, um</b>\nизображённый, выраженный (in materia <i>Tert</i>)."],
  ["species", "<big>species</big>\n\n<b>species, ei</b> <i>f</i> (<i>арх. gen.</i> specie <i>и</i> specii; <i>gen. pl.</i> specierum <i>и</i> <i>dat. abl.</i> speciebus — <i>тк.</i> <i>поздн.</i>) [specio]\n<b>1)</b> зрение (acuta <i>Vtr</i>);\n<b>2)</b> взгляд, взор (speciem aliquo vertere <i>Lcr</i>): prima specie <i>C, QC</i> с первого взгляда;\n<b>3)</b> вид, внешность, наружность, образ (horribilis <i>Cs</i>): una specie однообразный (ager <i>Sl</i>); specie ornatus <i>C</i> красивый, прелестный;\n<b>4)</b> видение, явление, призрак (nocturna <i>L</i>): s. quietis <i>O</i> сновидение;\n<b>5)</b> изображение, статуя (s. Jovis <i>C</i>);\n<b>6)</b> красота, прелесть (caeli <i>C</i>; armorum, vaccae <i>O</i>; s. in dicendo <i>C</i>); блеск, сверкание (s. candorque caeli <i>C</i>); величие (s. dignitasque <i>C</i>); пышность (triumphi <i>L</i>);\n<b>7)</b> внушительность, важность (consulis <i>C</i>);\n<b>8)</b> видимость, призрачность: (sub) specie <i>QC, C, L</i> <i>или</i> per (in) speciem <i>C, Cs, L</i> под видом, для вида, якобы; speciem alicujus praebere <i>Cs, L, QC</i>, habere, prae se ferre <i>Q</i> казаться кем-л.; ad speciem <i>C, Ap</i> с виду, на вид, <i>но тж. как и</i> specie tenus <i>Amm</i> по видимости, мнимо; in speciem alicujus rei <i>O</i> наподобие чего-л.; specie alicujus rei <i>C, L etc.</i> под предлогом чего-л.; specie recusationis <i>T</i> притворно отказываясь; specie non moveri <i>V</i> не смущаться соображениями внешних приличий;\n<b>9)</b> представление, понятие, идея (s. boni viri <i>C</i>): Aristoteles primus species (<i>pl.</i>) labefactavit <i>C</i> Аристотель первый поколебал теорию идей (Платона);\n<b>10)</b> идеал, образец (eloquentiae <i>C</i>; libertatis <i>Nep</i>);\n<b>11)</b> вид, разновидность (unum genus est possidendi, species infinitae <i>Dig</i>): primum illud genus quaerimus, ex quo ceterae species suspensae sunt <i>Sen</i> мы ищем тот первичный род, к которому относятся прочие виды;\n<b>12)</b> <i>юр.</i> (= casus) особый случай (s., de qua quaeritur <i>Dig</i>);\n<b>13)</b> товарная статья (<i>плоды, хлеб и пр.</i>) (s. vetustate corrupta <i>CJ</i>); статья в описи, отдельный предмет (res per speciem enumeratae <i>Dig</i>; species auri vel argenti <i>Dig</i>);\n<b>14)</b> специя, приправа, пряность (species superficiem, cui opponuntur, exulcerant <i>Macr</i>)."],
  ["specietas", "<big>specietas</big>\n\n<b>specietas, atis</b> <i>f</i> [species 11]\nвидовая особенность <i>Fronto</i>."],
  ["specificatus", "<big>specificatus</big>\n\n<b>speci–ficatus, a, um</b> [species + facio]\nобособленный, особый (proprietas <i>Boet</i>)."],
  ["specificus", "<big>specificus</big>\n\n<b>specificus, a, um</b>\nспецифический, видовой (differentia <i>Boet</i>)."],
  ["specillatus", "<big>specillatus</big>\n\n<b>specillatus, a, um</b> [<i>одного корня с</i> speculum]\nукрашенный маленькими зеркальцами (patina <i>Vop</i>)."],
  ["specillum", "<big>specillum</big>\n\n<b>specillum, i</b> <i>n</i>\n<b>1)</b> [specio] хирургический зонд <i>Vr, C, CC, PM</i>;\n<b>2)</b> [<i>demin. к</i> speculum] зеркальце <i>Aug</i>."],
  ["specimen", "<big>specimen</big>\n\n<b>specimen, inis</b> <i>n</i> [specio]\n<b>1)</b> проба, образчик, наглядное доказательство (ingenii <i>C</i>): s. dare alicui justitiae <i>L</i> доказать кому-л. свою справедливость;\n<b>2)</b> образец, пример, идеал (s. innocentiae, humanitatis <i>C</i>; s. sationis et insitionis <i>Lcr</i>): stultitiae maximum s. <i>Ap</i> наиболее характерная черта глупости."],
  ["specio", "<big>specio</big>\n\n<b>specio (spicio), spexi, spectum, ere</b> <i>арх.</i>\nсмотреть, глядеть: supercilio s. <i>Pl</i> глядеть исподлобья, презрительно; s. avem <i>культ. Vr</i> наблюдать полёт священных птиц; nunc specimen specitur <i>погов. Pl</i> теперь идёт проба (делается испытание)."],
  ["speciose", "<big>speciose</big>\n\n<b>speciose</b> [speciosus]\nпрекрасно, великолепно (vestitus <i>L</i>; dictum <i>Q</i>)."],
  ["speciosus", "<big>speciosus</big>\n\n<b>speciosus, a, um</b> [species]\n<b>1)</b> миловидный, прелестный, прекрасный (mulier <i>O</i>; puer <i>Pt</i>); красивый, великолепный (aedificium <i>VM</i>): cultu s. <i>Pt</i> роскошно одетый;\n<b>2)</b> блестящий, блистательный (eloquentia <i>Q</i>); громкий (nomen <i>T</i>); видный, выдающийся, выделяющийся (familia <i>VP</i>); многозначительный (vocabula <i>H</i>); замечательный, внушительный (opes <i>T</i>)."],
  ["spectabilis", "<big>spectabilis</big>\n\n<b>spectabilis, e</b> [specto]\n<b>1)</b> заметный, видимый (s. undique campus <i>O</i>);\n<b>2)</b> замечательный (heros <i>O</i>; victoria <i>T</i>); великолепный (aliqua re <i>O</i>); veterrimo et spectabili opere <i>Ap</i> (бронзовые изделия) замечательной работы старинных мастеров."],
  ["spectabilitas", "<big>spectabilitas</big>\n\n<b>spectabilitas, atis</b> <i>f</i> (<i>в обращении</i>) <i>прибл.</i>\nвысокородие (s. tua! <i>Aug</i>)."],
  ["spectaclum", "<big>spectaclum</big>\n\n<b>spectaclum, i</b> <i>n</i> <i>Pt, Prp</i>\n= spectaculum."],
  ["spectaculum", "<big>spectaculum</big>\n\n<b>spectaculum, i</b> <i>n</i> [specto]\n<b>1)</b> вид, зрелище (magnificum <i>L</i>; luctuosum, crudele <i>C</i>): s. praebere <i>C</i> представлять зрелище <i>или</i> <i>O</i> быть видимым; spectacula alicujus rei capere <i>O</i> осматривать (обозревать) что-л.; aliquem ad s. ducere <i>Pt</i> привлекать к себе чьи-л. взоры;\n<b>2)</b> представление, зрелище, спектакль (s. gladiatorum <i>C, L</i>);\n<b>3)</b> <i>преим. pl.</i> зрительный зал, места для зрителей (resonant spectacula plausu <i>O</i>);\n<b>4)</b> чудо мира (septem spectacula <i>AG</i>);\n<b>5)</b> осматривание, обозревание (descendere ad s. Euboeae insulae <i>L</i>)."],
  ["spectamen", "<big>spectamen</big>\n\n<b>spectamen, inis</b> <i>n</i> [specto]\n<b>1)</b> признак, свойство <i>Pl, Eccl</i>;\n<b>2)</b> вид, зрелище (miserum funestumque <i>Ap</i>)."],
  ["spectamentum", "<big>spectamentum</big>\n\n<b>spectamentum, i</b> <i>n</i> <i>Fronto</i>\n= spectamen 2."],
  ["spectate", "<big>spectate</big>\n\n<b>spectate</b> [spectatus]\nвеликолепно, превосходно (spectatissime florere <i>PM</i>)."],
  ["spectatio", "<big>spectatio</big>\n\n<b>spectatio, onis</b> <i>f</i> [specto]\n<b>1)</b> смотрение, глядение, созерцание (alicujus rei <i>C, Vtr etc.</i>);\n<b>2)</b> исследование, проба, испытание (pecuniae <i>C</i>);\n<b>3)</b> отношение, точка зрения: nomine amplior, quam spectatione gentis <i>Fl</i> более значительный номинально, чем с точки зрения народа."],
  ["spectativus", "<big>spectativus</big>\n\n<b>spectativus, a, um</b> [specto]\nумозрительный, теоретический (pars philosophiae <i>Q</i>)."],
  ["spectator", "<big>spectator</big>\n\n<b>spectator, oris</b> <i>m</i> [specto]\n<b>1)</b> зритель (ludorum <i>C</i>); наблюдатель, созерцатель (caeli <i>L</i>): testis et s. <i>C</i> непосредственный свидетель;\n<b>2)</b> критик, исследователь (virtutis s. ac judex <i>L</i>)."],
  ["spectatrix", "<big>spectatrix</big>\n\n<b>spectatrix, icis</b> <i>f</i> [spectator]\n<b>1)</b> зрительница <i>или</i> свидетельница <i>Pl, O, Sen, Lcn</i>;\n<b>2)</b> исследовательница, испытательница (posteritas incorrupta praeteritorum s. <i>Amm</i>)."],
  ["spectatus", "<big>spectatus</big>\n\n<b>spectatus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> specto;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> испытанный, изведанный (homo <i>C</i>; fides <i>O</i>; rebus spectata juventus <i>V</i>);\n<b>2)</b> прекрасный, превосходный (vir <i>C</i>); почтенный (femina <i>C</i>);\n<b>3)</b> замечательный (paeninsula <i>PM</i>)."],
  ["spectile", "<big>spectile</big>\n\n<b>spectile (spetile), is</b> <i>n</i>\nбрюшная часть свиной туши <i>Pl</i>."],
  ["spectio", "<big>spectio</big>\n\n<b>spectio, onis</b> <i>f</i> [specio]\n<b>1)</b> наблюдение авгуров (<i>за полётом птиц и пр.</i>) <i>Vr</i>;\n<b>2)</b> право проводить ауспиции (spectionem habere <i>C</i>)."],
  ["specto", "<big>specto</big>\n\n<b>specto, avi, atum, are</b> [<i>intens. к</i> specio]\n<b>1)</b> смотреть, глядеть (in <i>или</i> ad aliquem <i>C etc.</i>); созерцать, рассматривать, наблюдать (mottis siderum <i>Sen</i>); замечать, (у)видеть (aliquem spectantem <i>O</i>): spectantibus omnibus <i>C</i> на виду у всех; s. alte <i>C</i> смотреть вверх; s. ludos <i>H, Su</i> смотреть игры (состязания); studium spectandi <i>Su</i> стремление к зрелищам; spectatum veniunt, veniunt spectentur ut ipsae <i>O</i> (римлянки приходят в театр не только), чтобы посмотреть, но и чтобы на них посмотрели; s. aliquem <i>H</i> разглядывать кого-л. с удивлением;\n<b>2)</b> принимать во внимание, учитывать (causam <i>C</i>; rem, non verba <i>C</i>);\n<b>3)</b> оценивать, определять (tempus <i>Cs</i>): s. rem <i>Pl, Cs etc.</i> определять (создавшееся) положение;\n<b>4)</b> исследовать (exta <i>L</i>); испытывать, пробовать (aurum in ignibus <i>O</i>); судить (s. aliquid ex <i>или</i> ab aliqua re <i>C, Sen etc.</i>);\n<b>5)</b> иметь в виду, добиваться, стремиться (s. arma et bellum <i>L</i>; s. ad suam gloriam <i>C</i>): s. magna (alte) <i>C</i> ставить перед собой высокие цели;\n<b>6)</b> клониться, иметь целью (ea consilia ad salutem civium spectant <i>C</i>): quorsum spectat oratio? <i>C</i> к чему ведёт речь (какова цель речи)?;\n<b>7)</b> относиться, касаться (oratio mea ad te unum spectat <i>C</i>);\n<b>8)</b> быть обращенным, быть расположенным, лежать (s. ad meridiem <i>C</i>): s. inter occasum solem et septentriones <i>Cs</i> находиться на северо-западе."],
  ["spectrum", "<big>spectrum</big>\n\n<b>spectrum, i</b> <i>n</i> [specio]\n<b>1)</b> представление, мысленный образ <i>C</i>;\n<b>2)</b> (при)видение <i>Eccl</i>."],
  ["spectus", "<big>spectus</big>\n\n<b>spectus, us</b> <i>m</i> [specio]\nвид (protervus <i>Pac</i>)."],
  ["specula", "<big>specula</big>\n\n<b>I specula, ae</b> <i>f</i> [<i>demin. к</i> spes]\nслабая надежда, проблеск надежды <i>Pl, C, Ap</i>.\n\n<b>II specula, ae</b> <i>f</i> [specio]\n<b>1)</b> дозорная (сторожевая) башня, наблюдательный пост: e(x) (ab <i>V</i>) specula signum dare <i>L</i> сигнализировать с наблюдательного пункта; in speculis esse <i>погов. C, L, O</i> подсматривать, высматривать, глядеть в оба, не спускать глаз;\n<b>2)</b> <i>поэт.</i> возвышение, возвышенное место (alta <i>V</i>); высота (s. montis <i>V</i>);\n<b>3)</b> городская стена (clamorem ex speculis tollere <i>V</i>);\n<b>4)</b> высматривание, наблюдение (turrim speculae causa aedificare <i>Vop</i>)."],
  ["speculabilis", "<big>speculabilis</big>\n\n<b>speculabilis, e</b> [speculor]\nнаходящийся на виду, ясно видный (longe speculabile proris Sunion <i>St</i>)."],
  ["speculabundus", "<big>speculabundus</big>\n\n<b>speculabundus, a, um</b> [speculor]\nподсматривающий, наблюдающий, подстерегающий (s. signa ex rupe <i>Su</i>)."],
  ["speculamen", "<big>speculamen</big>\n\n<b>speculamen, inis</b> <i>n</i> [speculor]\nглядение <i>Eccl</i>."],
  ["specular", "<big>specular</big>\n\n<b>specular, aris</b> <i>n</i> <i>Tert sg. к</i>\nspecularia."],
  ["specularia", "<big>specularia</big>\n\n<b>specularia, ium</b> <i>и</i> <b>orum</b> <i>n</i> [specularis]\n<b>1)</b> оконная слюда <i>Sen, PM</i>;\n<b>2)</b> слюдяные окна <i>PJ, Col, J etc.</i>"],
  ["specularis", "<big>specularis</big>\n\n<b>specularis, e</b> [speculum]\nзеркальный, светлый как зеркало: lapis s. <i>Pt</i> слюда (<i>которая заменяла оконное стекло</i>); speculari ratione <i>Sen</i> подобно зеркалу, по принципу зеркала."],
  ["specularium", "<big>specularium</big>\n\n<b>specularium, i</b> <i>n</i> [speculum]\nзеркало <i>Dig</i>."],
  ["specularius", "<big>specularius</big>\n\n<b>specularius, i</b> <i>m</i>\nзеркальный мастер <i>Dig, CJ</i>."],
  ["speculatio", "<big>speculatio</big>\n\n<b>speculatio, onis</b> <i>f</i> [speculor]\n<b>1)</b> разведка <i>Amm</i>;\n<b>2)</b> <i>филос.</i> созерцание, умозрение <i>Boet</i>."],
  ["speculativus", "<big>speculativus</big>\n\n<b>speculativus, a, um</b>\nумозрительный (philosophia <i>Boet</i>)."],
  ["speculator", "<big>speculator</big>\n\n<b>speculator, oris</b> <i>m</i> [speculor]\n<b>1)</b> разведчик, лазутчик <i>Vr, C, Cs etc.</i>;\n<b>2)</b> ординарец, вестовой <i>T, Su</i>;\n<b>3)</b> телохранитель, охранник <i>Sen</i>;\n<b>4)</b> испытатель, исследователь (naturae <i>C</i>; futuri <i>Sil</i>)."],
  ["speculatoria", "<big>speculatoria</big>\n\n<b>speculatoria, ae</b> <i>f</i>\n<b>1)</b> (<i>sc.</i> navis) разведывательное судно <i>L</i>;\n<b>2)</b> (<i>sc.</i> caliga) сапог разведчика <i>Tert</i>."],
  ["speculatorius", "<big>speculatorius</big>\n\n<b>speculatorius, a, um</b> [speculator]\nразведывательный, дозорный, сторожевой (naves <i>L</i>): caligae speculatoriae <i>Su</i> сапоги, которые носили разведчики."],
  ["speculatrix", "<big>speculatrix</big>\n\n<b>speculatrix, icis</b> <i>f</i> [speculator]\n<b>1)</b> разведчица, выслеживающая (s. et vindex facinorum <i>C</i>);\n<b>2)</b> созерцательница (s. villa profundi, <i>sc.</i> maris <i>St</i>)."],
  ["speculoclarus", "<big>speculoclarus</big>\n\n<b>speculo–clarus, a, um</b>\nблестящий как зеркало <i>Pl</i>."],
  ["speculor", "<big>speculor</big>\n\n<b>speculor, atus sum, ari</b> <i>depon.</i> [specio]\n<b>1)</b> оглядываться, осматриваться (in omnes partes <i>O</i>);\n<b>2)</b> подсматривать (per rimam foris <i>Pt</i>); подстерегать (aliquem, omnia <i>C</i>); разведывать, разузнавать, высматривать (locum <i>V</i>; consilia alicujus <i>Sl</i>);\n<b>3)</b> наблюдать, созерцать, смотреть (obitus et ortus signorum <i>V</i>; incendia de muris <i>Just</i>)."],
  ["speculum", "<big>speculum</big>\n\n<b>speculum, i</b> <i>n</i> [specio]\n<b>1)</b> зеркало (<i>у древних — металла</i>) (imago speculi <i>Lcr</i>, se in speculo contemplari <i>Ap</i> <i>и</i> in s. inspicere <i>Ter</i>): speculo placere <i>шутл. O</i> встретить одобрительный отзыв зеркала, <i>т. е.</i> быть в состоянии нравиться;\n<b>2)</b> зеркальная гладь (s. lympharum <i>Ph</i>);\n<b>3)</b> отображение, подобие (naturae <i>C</i>); подражание (Homerici operis <i>Macr</i>)."],
  ["specus", "<big>specus</big>\n\n<b>specus, us</b> (<i>abl. pl.</i> ubus, <i>реже</i> ibus) <i>m</i> (<i>редко</i> <i>f</i> <i>или</i> <i>n</i>)\n<b>1)</b> пещера, грот (s. tenebroso hiatu <i>O</i>): inferni specus <i>SenT</i> <i>и</i> invisus caelo s. <i>Sil</i> = Tartarus;\n<b>2)</b> шахта, рудник (specus ad argentum inveniendum <i>Vtr</i>); <i>воен.</i> подкоп (specus fodiendo intra moenia penetrare <i>Vtr</i>);\n<b>3)</b> подземный водопровод, канал (s. subterraneus <i>C</i>);\n<b>4)</b> впадина, углубление, полость (alvi <i>Ph</i>; vulneris <i>V</i>)."],
  ["spelaeum", "<big>spelaeum</big>\n\n<b>spelaeum, i</b> <i>n</i> (<i>греч.</i>)\nпещера <i>или</i> логово (ferarum <i>V, CLd</i>)."],
  ["spelunca", "<big>spelunca</big>\n\n<b>I spelunca, ae</b> <i>f</i> (<i>греч.</i>)\nпещера, грот <i>C, V, Eccl</i>.\n\n<b>II Spelunca, ae</b> <i>f</i>\n<i>название поместья императора Тиберия близ Террацины</i> <i>Su, T, PM</i>."],
  ["speluncosus", "<big>speluncosus</big>\n\n<b>speluncosus, a, um</b>\nполный пещер (loca <i>CA</i>)."],
  ["spepondi", "<big>spepondi</big>\n\n<b>spepondi</b> <i>AG</i>\n= spopondi."],
  ["sperabilis", "<big>sperabilis</big>\n\n<b>sperabilis, e</b> [spero]\nна который можно надеяться, возможный (salus vitae <i>Pl</i>)."],
  ["sperata", "<big>sperata</big>\n\n<b>sperata, ae</b> <i>f</i> [spero]\nневеста <i>или</i> возлюбленная <i>Pl</i>: s. uxor <i>Pl</i> милая супруга."],
  ["sperator", "<big>sperator</big>\n\n<b>sperator, oris</b> <i>m</i>\nнадеющийся <i>Aug</i>."],
  ["speratus", "<big>speratus</big>\n\n<b>I speratus, a, um</b> <i>part. pf. к</i>\nspero: potiri sperata <i>L</i> дождаться осуществления своих надежд.\n\n<b>II speratus, i</b> <i>m</i>\nжених, суженый <i>Pl</i>."],
  ["Spercheis", "<big>Spercheis</big>\n\n<b>Spercheis, idis</b> <i>f</i> [Spercheus]\nсперхейская (undae <i>O</i>)."],
  ["Spercheus", "<big>Spercheus</big>\n\n<b>Spercheus</b> <i>и</i> <b>Sperchius (-os), i</b> <i>m</i>\nСперхей, <i>река в южн. Фессалии, впадающая в Малийский залив</i> <i>V, O, L, PM, St</i>."],
  ["Sperchiae", "<big>Sperchiae</big>\n\n<b>Sperchiae, arum</b> <i>f</i>\nСперхии, <i>город в долине реки Сперхей</i> <i>L</i>."],
  ["Sperchionides", "<big>Sperchionides</big>\n\n<b>Sperchionides, ae</b> <i>m</i>\nжитель долины Сперхея <i>O</i>."],
  ["Sperchios", "<big>Sperchios</big>\n\n<b>Sperchios (-us)</b> <i>v. l.</i>\n= Spercheus."],
  ["speres", "<big>speres</big>\n\n<b>speres</b> (<i>abl.</i> ibus) <i>арх. Enn, Vr pl. к</i>\nspes."],
  ["spergo", "<big>spergo</big>\n\n<b>spergo, —, —, ere</b> <i>Col</i>\n= spargo."],
  ["spermaticus", "<big>spermaticus</big>\n\n<b>spermaticus, a, um</b> (<i>греч.</i>)\nсеменной (pori <i>CA</i>)."],
  ["spernax", "<big>spernax</big>\n\n<b>spernax, acis</b> [sperno]\nпрезирающий (vir s. mortis <i>Sil</i>); презрительный (in aliqua re <i>Sid</i>)."],
  ["sperno", "<big>sperno</big>\n\n<b>sperno, sprevi, spretum, ere</b>\n<b>1)</b> отделять, отстранять, удалять (se a malis s. <i>Enn</i>);\n<b>2)</b> отвергать (veteres amicitias <i>C</i>); презирать, пренебрегать (aliquem <i>Ter, C</i>; minas pelagi <i>Lcn</i>): spretae injuria formae <i>V</i> обида (Юноны) из-за непризнанной (Парисом её) красоты; nil spernat auris, nec tamen credat statim <i>Ph</i> слушать нужно всё, но не спешить с доверием; qui pocula Massici non spernit <i>H</i> который не прочь хлебнуть массийского вина; jussa s. <i>T</i> не исполнить приказаний; spernendus alicujus rei <i>T</i> достойный презрения в отношении чего-л.; haud spernendus <i>L etc.</i> неплохой, заслуживающий внимания;\n<b>3)</b> презрительно отзываться, насмехаться (s. hostium paucitatem <i>QC</i>)."],
  ["spernor", "<big>spernor</big>\n\n<b>spernor, —, ari</b> <i>depon.</i>\nпрезирать (aliquem <i>J</i>)."],
  ["spero", "<big>spero</big>\n\n<b>spero, avi, atum, are</b> [spes]\n<b>1)</b> надеяться, уповать (s. omnia ex victoria <i>Cs</i>; s. pacem <i>C</i>): bene <i>или</i> recte s. <i>C</i> надеяться на хорошее; s. melius <i>O</i> рассчитывать на (нечто) лучшее;\n<b>2)</b> ожидать, предвидеть, чаять (aliquid de aliqua re <i>C</i>): qui non sperabitur <i>H</i> нежданный, непредвиденный;\n<b>3)</b> полагать, думать, надеяться (spero me causam tibi probavisse <i>C</i>); помнить, иметь в виду: sperate deos memores fandi atque nefandi <i>V</i> помните, что боги не забывают ни добрых дел, ни беззаконий; <i>но</i>: deos sperabo teque <i>Pl</i> буду надеяться на богов и на тебя;\n<b>4)</b> опасаться, бояться (s. tantum dolorem <i>V</i>). — <i>См, тж.</i> speratus."],
  ["spes", "<big>spes</big>\n\n<b>spes, ei</b> <i>f</i>\n<b>1)</b> надежда, упование, чаяние: qua spe? <i>V</i> в надежде на что?; s. alicujus rei <i>C etc.</i> <i>и</i> de aliqua re <i>Pl, Cs, Q</i> надежда на что-л.; ad spem alicujus rei <i>L, QC</i> в надежде на что-л.; s. in aliquo est <i>C</i> надежда возлагается на кого-л.; aliquid in spe est <i>C</i> есть надежда (виды) на что-л.; s. me tenet (spem habeo <i>или</i> spe ducor) <i>C</i> я надеюсь; in spe habere (ponere) aliquid <i>C</i> надеяться (рассчитывать) на что-л.; spem alicui facere (dare <i>или</i> afferre) <i>C</i> подать (внушить) кому-л. надежду, обнадёжить кого-л.; in spem venire (ingredi, adduci, induci) <i>C</i> возыметь надежду; a spe repelli <i>Cs</i> потерять надежду; spe dejici (labi) <i>Cs</i> обмануться в надежде; adulescens summae spei <i>Cs</i> юноша, подающий большие надежды; spe dominationis <i>или</i> in spe potentiae <i>T</i> в надежде достигнуть власти, <i>т. е.</i> из честолюбивых побуждений;\n<b>2)</b> ожидание, предвидение, чаяние: spe serius <i>C</i> позже, чем думали; spe celerius <i>C</i> быстрее, чем можно было ожидать; praeter <i>или</i> contra spem <i>C, Cs, L</i> против ожидания (сверх всякого чаяния); spes (<i>pl.</i>) heredis Iuli respice <i>V</i> подумай о будущем (своего) наследника Иула; s. cogitationum et consiliorum <i>C</i> замыслы и планы; scribere aliquem in spem secundam <i>T</i> назначить кого-л. второочередным наследником (на <i>случай смерти основного</i>);\n<b>3)</b> потомство, молодняк (s. gregis <i>V</i>): s. anni <i>O</i> молодые всходы, <i>т. е.</i> виды на урожай;\n<b>4)</b> опасение, боязнь (naufragii <i>Lcn</i>)."],
  ["spetile", "<big>spetile</big>\n\n<b>spetile, is</b> <i>n</i> <i>v. l.</i>\n= spectile."],
  ["Speusippus", "<big>Speusippus</big>\n\n<b>Speusippus, i</b> <i>m</i>\nСпевсипп, <i>племянник Платона и преемник его в руководстве Академией</i> <i>C</i>."],
  ["speusticus", "<big>speusticus</big>\n\n<b>speusticus, a, um</b> (<i>греч.</i>)\nприготовленный наспех (panis <i>PM</i>)."],
  ["spexi", "<big>spexi</big>\n\n<b>spexi</b> <i>pf. к</i>\nspecio."],
  ["sphaera", "<big>sphaera</big>\n\n<b>sphaera, ae</b> <i>f</i> (<i>греч.</i>; <i>лат.</i> globus <i>или</i> pila)\n<b>1)</b> шар <i>C, Macr</i>;\n<b>2)</b> небесный глобус <i>C</i>;\n<b>3)</b> орбита (habent suas sphaeras stellae <i>C</i>);\n<b>4)</b> мяч (sphaerae ludus <i>CA</i>)."],
  ["sphaeralis", "<big>sphaeralis</big>\n\n<b>sphaeralis, e</b> <i>Macr</i>\n= sphaericus."],
  ["sphaerica", "<big>sphaerica</big>\n\n<b>sphaerica, ae</b> <i>f</i>\nнебесная механика <i>Boet</i>."],
  ["sphaericus", "<big>sphaericus</big>\n\n<b>sphaericus, a, um</b> [sphaera]\n<b>1)</b> шарообразный <i>или</i> круговой (motus <i>Macr</i>);\n<b>2)</b> <i>мат.</i> кубический (numerus <i>Boet</i>)."],
  ["sphaerion", "<big>sphaerion</big>\n\n<b>sphaerion, i</b> <i>n</i>\nшарик, пилюля <i>CC</i>."],
  ["sphaerista", "<big>sphaerista</big>\n\n<b>sphaerista, ae</b> <i>m</i>\nиграющий в мяч <i>Sid</i>."],
  ["sphaeristerium", "<big>sphaeristerium</big>\n\n<b>sphaeristerium, i</b> <i>n</i> [sphaera]\n<b>1)</b> место для игры в мяч <i>PM, Su</i>;\n<b>2)</b> игра в мяч <i>Lampr, Sid</i>."],
  ["sphaerita", "<big>sphaerita</big>\n\n<b>sphaerita, ae</b> <i>f</i> <i>v. l.</i>\n= spaerita."],
  ["sphaeroides", "<big>sphaeroides</big>\n\n<b>sphaeroides, es</b> <i>adj.</i> (<i>греч.</i>)\nшарообразный <i>Vtr</i>."],
  ["sphaeromachia", "<big>sphaeromachia</big>\n\n<b>sphaeromachia, ae</b> <i>f</i> (<i>греч.</i>)\nсферомахия, <i>т. е.</i> кулачный бой в наручных шарообразных перчатках <i>Sen, St</i>."],
  ["sphaerula", "<big>sphaerula</big>\n\n<b>sphaerula, ae</b> <i>f</i> [<i>demin. к</i> sphaera]\nшарик <i>Vlg, Aug</i>."],
  ["sphagnos", "<big>sphagnos</big>\n\n<b>sphagnos, i</b> <i>m</i> (<i>греч.</i>)\nсфагн, <i>род душистого мха</i> <i>PM</i>."],
  ["spher-", "<big>spher-</big>\n\n<b>spher-</b> <i>v. l.</i>\n= sphaer-."],
  ["sphincter", "<big>sphincter</big>\n\n<b>sphincter, eris</b> <i>m</i> (<i>греч.</i>) <i>анат.</i>\nсфинктер <i>CA</i>."],
  ["sphinga", "<big>sphinga</big>\n\n<b>sphinga, ae</b> <i>f</i> <i>Sid, Is</i>\n= sphinx."],
  ["sphingatus", "<big>sphingatus</big>\n\n<b>sphingatus, a, um</b> [sphinx]\nимеющий форму сфинкса (effigies <i>Is</i>)."],
  ["sphinx", "<big>sphinx</big>\n\n<b>sphinx, ingis (ingos)</b> <i>f</i>\nсфинкс, <i>миф. существо</i>:\n<b>1)</b> <i>в Египте — с телом льва и головой человека</i> <i>PM</i>;\n<b>2)</b> <i>в Фивах «Семивратных» — с телом льва, головой женщины и крыльями птицы, задававшее загадки прохожим и растерзывавшее тех, кто их не разгадывал</i> <i>Pl, O, SenT, Aus, Su</i>."],
  ["sphond-", "<big>sphond-</big>\n\n<b>sphond-</b> <i>v. l.</i>\n= spond-."],
  ["spica", "<big>spica</big>\n\n<b>spica, ae</b> <i>f</i>\n<b>1)</b> (<i>у злаков</i>) колос (spicas desecare <i>L</i>);\n<b>2)</b> (<i>у других растений</i>) колосовидный кончик <i>или</i> пучок (nardi <i>Veg</i>);\n<b>3)</b> самая яркая звезда в созвездии Девы <i>Col</i>;\n<b>4)</b> треугольный кирпич <i>или</i> плитка (<i>для кладки в форме колоса</i>) <i>Vtr</i>;\n<b>5)</b> стрела <i>или</i> дротик <i>Lcr</i>."],
  ["spicatus", "<big>spicatus</big>\n\n<b>spicatus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> spicor;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> остроконечный, заострённый: spicata testacea <i>Vtr</i> расположение кирпичей в виде колоса;\n<b>2)</b> оканчивающийся колосообразной кистью (herbae spicatae <i>PM</i>)."],
  ["spiceus", "<big>spiceus</big>\n\n<b>spiceus, a, um</b> [spica]\nсделанный из колосьев (corona <i>Tib, H</i>): messis spicea <i>V</i> уборка колосьев, жатва."],
  ["spicifer", "<big>spicifer</big>\n\n<b>spicifer, fera, ferum</b> [spica + fero]\nдающий колосья, взращивающий нивы (Nilus <i>M</i>; ora <i>Sil</i>): spicifera dea <i>MF</i> = Ceres."],
  ["spicilegium", "<big>spicilegium</big>\n\n<b>spicilegium, i</b> <i>n</i> [spica + lego I]\nсбор колосьев, оставшихся после жатвы <i>Vr</i>."],
  ["spicio", "<big>spicio</big>\n\n<b>spicio, —, —, ere</b> <i>Pl</i>\n= specio."],
  ["spico", "<big>spico</big>\n\n<b>spico, —, atum, are</b>\nзаострять <i>или</i> снабжать острым наконечником (exigui dentes spicant hastilia <i>GF</i>). — <i>См. тж.</i> spicatus <i>и</i> spicor."],
  ["spicor", "<big>spicor</big>\n\n<b>spicor, —, ari</b> <i>depon.</i> [spica]\nколоситься (grana spicantur <i>PM</i>)."],
  ["spiculator", "<big>spiculator</big>\n\n<b>spiculator, oris</b> <i>f</i> [spicio = specio]\n<b>1)</b> охранник, телохранитель <i>Vlg</i>;\n<b>2)</b> палач <i>Hier</i>."],
  ["spiculo", "<big>spiculo</big>\n\n<b>spiculo, avi, atum, are</b> [spiculum]\nзаострять, оттачивать (telum <i>PM</i>); снабжать остриём (cauda scorpionis spiculata <i>Sol</i>)."],
  ["spiculum", "<big>spiculum</big>\n\n<b>spiculum, i</b> <i>n</i>\n<b>1)</b> кончик, остриё (calami <i>H</i>; hastae <i>O</i>);\n<b>2)</b> стрела, дротик <i>V, O</i>;\n<b>3)</b> жало (apium <i>V</i>; scorpionis <i>O</i>);\n<b>4)</b> луч (solis <i>Eccl</i>)."],
  ["spicum", "<big>spicum</big>\n\n<b>spicum, i</b> <i>n</i> <i>и</i> <b>spicus, i</b> <i>m</i> <i>v. l.</i>\n= spica."],
  ["spina", "<big>spina</big>\n\n<b>spina, ae</b> <i>f</i>\n<b>1)</b> <i>бот.</i> шип (spinis surgit paliurus <i>V</i>);\n<b>2)</b> терновый куст, терновник (s. alba <i>PM</i>): spinis coronatus <i>Eccl</i> в терновом венце;\n<b>3)</b> <i>зоол.</i> игла, колючка (animantes spinis hirsutae <i>C</i>);\n<b>4)</b> зубочистка (argentea <i>Pt</i> — <i>v. l.</i> pinna);\n<b>5)</b> кость (<i>рыбья</i>) (spinae piscium <i>Q</i>);\n<b>6)</b> хребет, позвоночный столб: caput spina excipit <i>CC</i> позвоночный столб служит опорой голове; s. sacra <i>Su, Eccl</i> крестцовая кость || <i>поэт.</i> спина (s. viret, <i>sc.</i> ranae <i>O</i>);\n<b>7)</b> <i>pl.</i> заботы, терзания (spinas animo evellere <i>H</i>);\n<b>8)</b> <i>pl.</i> тонкости, хитросплетения: spinas partiendi et definiendi praetermittere <i>C</i> обойти замысловатые тонкости расчленений и определений; s. disserendi <i>C</i> хитроумная диалектика."],
  ["spinalis", "<big>spinalis</big>\n\n<b>spinalis, e</b> [spina 6]\nспинномозговой (medulla <i>Macr</i>)."],
  ["spinea", "<big>spinea</big>\n\n<b>spinea, ae</b> <i>f</i> <i>PM</i>\n= spionia."],
  ["spineola", "<big>spineola</big>\n\n<b>spineola (rosa), ae</b> <i>f</i>\nшиповник <i>PM</i>."],
  ["spinetum", "<big>spinetum</big>\n\n<b>spinetum, i</b> <i>n</i> [spina] (<i>тк.</i> <i>pl.</i>)\nтерн, терновник <i>V, PM, Sen</i>: Aristotelis spineta <i>ирон. Hier</i> Аристотелевы дебри."],
  ["spineus", "<big>spineus</big>\n\n<b>spineus, a, um</b> [spina]\nшиповатый (vincula <i>O</i>); колючий (frutices <i>Sol</i>); терновый (corona <i>Lact</i>)."],
  ["sping-", "<big>sping-</big>\n\n<b>sping-</b> <i>v. l.</i>\n= sphing-."],
  ["spinifer", "<big>spinifer</big>\n\n<b>spinifer, fera, ferum</b> [spina + fero]\nпокрытый тёрном, терновый, колючий (prunus <i>Pall</i>)."],
  ["spiniger", "<big>spiniger</big>\n\n<b>spiniger, gera, gerum</b> [spina + gero] <i>C, Eccl</i>\n= spinifer."],
  ["spiniola", "<big>spiniola</big>\n\n<b>spiniola, ae</b> <i>f</i> <i>PM</i>\n= spineola."],
  ["Spino", "<big>Spino</big>\n\n<b>Spino, onis</b> <i>m</i>\nСпинон, <i>речка близ Рима, которой воздавались божеские почести</i> <i>C</i>."],
  ["spinosulus", "<big>spinosulus</big>\n\n<b>spinosulus, a, um</b> [<i>demin. к</i> spinosus 2]\nнесколько запутанный <i>Hier</i>."],
  ["spinosus", "<big>spinosus</big>\n\n<b>spinosus, a, um</b> [spina]\n<b>1)</b> поросший тёрном, тернистый (loca <i>Vr</i>); покрытый тёрном, колючий (folia <i>PM</i>);\n<b>2)</b> запутанный, замысловатый, хитроумный (genus disserendi <i>C</i>, oratio <i>C</i>);\n<b>3)</b> жгучий, мучительный (curae <i>Ctl</i>)."],
  ["spinther", "<big>spinther</big>\n\n<b>I spint(h)er, eris</b> <i>n</i> (<i>греч.</i>)\nэластичный браслет, обвивавший верхнюю часть руки <i>Pl</i>.\n\n<b>II Spinther, eris</b> <i>m</i>\nСпинтер, <i>cognomen Лентула</i> <i>PM, Q</i>."],
  ["spinthria", "<big>spinthria</big>\n\n<b>spint(h)ria, ae</b> <i>m</i> (<i>греч.</i>)\nactor impudicitiae, qua serie connexi pudendisque concatenati se invicem ineunt <i>Su, T, Aus</i>."],
  ["spinturnicium", "<big>spinturnicium</big>\n\n<b>spinturnicium, i</b> <i>n</i> <i>Pl demin. к</i>\nspinturnix."],
  ["spinturnix", "<big>spinturnix</big>\n\n<b>spinturnix, icis</b> <i>f</i>\nзловещая птица, <i>предпол.</i> филин <i>PM</i>."],
  ["spinula", "<big>spinula</big>\n\n<b>spinula, ae</b> <i>f</i> [<i>demin. к</i> spina]\n<b>1)</b> спинка <i>Ap</i>;\n<b>2)</b> маленький шип, колючка <i>Eccl</i>."],
  ["spinulentus", "<big>spinulentus</big>\n\n<b>spinulentus, a, um</b> [spina]\nтернистый, колючий <i>Sol</i>."],
  ["spionia", "<big>spionia</big>\n\n<b>spionia, ae</b> <i>f</i>\n<i>сорт винограда</i> <i>Col, PM</i>."],
  ["spionicus", "<big>spionicus</big>\n\n<b>spionicus, a, um</b>\nдобываемый из винограда spionia (gustus <i>Col</i>)."],
  ["spira", "<big>spira</big>\n\n<b>spira, ae</b> <i>f</i> (<i>греч.</i>)\n<b>1)</b> изгиб, извив (serpentis <i>V, O</i>; <i>sc.</i> intestinorum <i>Lact</i>);\n<b>2)</b> спиральные прожилки (<i>на дереве</i>) <i>PM</i>;\n<b>3)</b> бублик, крендель <i>Cato</i>;\n<b>4)</b> круглый базис колонны <i>Vtr, PM</i>;\n<b>5)</b> шнурок для подвязывания головного убора, завязка <i>J</i>;\n<b>6)</b> кольцеобразная заколка для волос <i>PM, VF</i>;\n<b>7)</b> жгут из волос, коса <i>PM, VF</i>;\n<b>8)</b> <i>воен.</i> отряд <i>Enn</i>."],
  ["spirabilis", "<big>spirabilis</big>\n\n<b>spirabilis, e</b> [spiro]\n<b>1)</b> годный для дыхания, <i>т. е.</i> воздухообразный (s. natura, cui nomen est aer <i>C</i>);\n<b>2)</b> дыхательный (viscera <i>PM</i>);\n<b>3)</b> поддерживающий жизнь, жизненный (lumen caeli <i>V</i>)."],
  ["spiraculum", "<big>spiraculum</big>\n\n<b>spiraculum, i</b> <i>n</i> [spiro]\nотдушина (saevi spiracula Ditis <i>V</i>): spiracula fontis <i>VF</i> = fons."],
  ["spiramen", "<big>spiramen</big>\n\n<b>spiramen, inis</b> <i>n</i> [spiro]\n<b>1)</b> дыхательное отверстие (s. naris <i>Enn, Lcn</i>);\n<b>2)</b> дыхание <i>Lcn, St, Amm</i>;\n<b>3)</b> отдушина <i>Lcn</i>."],
  ["spiramentum", "<big>spiramentum</big>\n\n<b>spiramentum, i</b> <i>n</i>\n<b>1)</b> дыхательное отверстие: s. animae <i>V</i> дыхательный канал, трахея;\n<b>2)</b> веяние (venti <i>Vtr</i>); выдыхание, испарение (cavernarum <i>Just</i>); запах (aceti <i>Vtr</i>; odoraminum <i>Macr</i>);\n<b>3)</b> передышка, остановка, пауза (s. temporum <i>T</i>)."],
  ["spiratio", "<big>spiratio</big>\n\n<b>spiratio, onis</b> <i>f</i> [spiro]\nдыхание <i>Scr</i>."],
  ["spiritalis", "<big>spiritalis</big>\n\n<b>spiritalis, e</b> [spiritus]\n<b>1)</b> духовой, пневматический (machina <i>Vtr</i>);\n<b>2)</b> дыхательный (arteria <i>Eccl</i>; fistula <i>Lact</i>);\n<b>3)</b> духовный, невещественный <i>Eccl</i>."],
  ["spiritalitas", "<big>spiritalitas</big>\n\n<b>spiritalitas, atis</b> <i>f</i>\nдуховность <i>Tert</i>."],
  ["spiritaliter", "<big>spiritaliter</big>\n\n<b>spiritaliter</b>\nдуховно <i>Tert</i>."],
  ["spiritualis", "<big>spiritualis</big>\n\n<b>spiritualis, e</b> <i>v. l.</i>\n= spiritalis."],
  ["spiritus", "<big>spiritus</big>\n\n<b>spiritus, us</b> <i>m</i> [spiro]\n<b>1)</b> веяние, дуновение (vehementior s. ventus est, s. leviter fluens — aer <i>Sen</i>); испарение (s. pestilens <i>Capit</i>); благоухание (s. suavis unguenti <i>Lcr</i>; s. florum <i>AG</i>); ветер (frigidus <i>PM</i>);\n<b>2)</b> дыхание (vita tenetur spiritu <i>C</i>): usque ad extremum spiritum. <i>C</i> до последнего вздоха, до самой смерти; s. angustior <i>C</i> короткое дыхание, одышка; extremum spiritum edere (effundere) <i>C</i> испустить дух, умереть; uno spirito <i>C</i> на переводя дыхания, одним духом;\n<b>3)</b> вдыхание (huius caeli <i>C</i>);\n<b>4)</b> вздох (latere imo petitus s. <i>H</i>): nullum otiosum spiritum ducere <i>C</i> не иметь ни одной спокойной минуты;\n<b>5)</b> змеиное шипение <i>V</i>;\n<b>6)</b> (<i>тж.</i> caeli s. <i>C</i>) воздух: spiritum ducere <i>C</i> (movere, trahere <i>CC</i> <i>или</i> recipere <i>Pt</i>) вдыхать, дышать;\n<b>7)</b> жизненная сила, жизнь (spiritum patriae reddere <i>C</i>): aliquid spiritu luere <i>St</i> заплатить жизнью за что-л.;\n<b>8)</b> душа, дух: dum memor ipse mei, dum s. hos regit artus <i>V</i> пока я в полном сознании, пока душа управляет этим (моим) телом;\n<b>9)</b> личность, лицо (carissimus alicui s. <i>VP</i>);\n<b>10)</b> настроение, образ мыслей (hostilis <i>L</i>); unius spiritu vivere <i>QC</i> быть проникнутым общим настроением;\n<b>11)</b> характер (avidus <i>H</i>);\n<b>12)</b> воодушевление, вдохновение (poeticus, sublimis <i>Q</i>);\n<b>13)</b> мужество, энергия (magnos spiritus alicui facere <i>или</i> imprimere <i>L</i>);\n<b>14)</b> высокомерие, заносчивость, гордыня (spiritus patricii <i>L</i>): magnos spiritus sibi sumere <i>Cs</i> возгордиться, зазнаться; regius s. <i>C</i> царственное величие;\n<b>15)</b> недовольство, озлобление (alicujus spiritus mitigare <i>T</i>);\n<b>16)</b> <i>грам.</i> придыхание (asper, lenis <i>Aus</i>)."],
  ["spiro", "<big>spiro</big>\n\n<b>spiro, avi, atum, are</b>\n<b>1)</b> дуть, веять (venti a mari spirant <i>QC</i>): audire zephyros s. secundos <i>V</i> слышать порывы попутных зефиров || <i>поэт.</i> насылать ветер (di spirant secundi <i>V</i>) <i>или</i> благоприятствовать (alicui <i>Prp</i>);\n<b>2)</b> шуметь, клокотать, бурлить, волноваться (freta spirantia <i>V</i>; spirat e pectore flamma <i>V</i>); шипеть (hydra spirat <i>V</i>);\n<b>3)</b> дышать (dum s. potero <i>C</i>); жить (spirat adhuc amor <i>H</i>): s. querulum <i>M</i> жалобно вздыхать; spirantia exta <i>V</i> (ещё) трепещущие внутренности; spirantia signa (aera) <i>V</i> словно живые (кажущиеся одушевлёнными) изваяния;\n<b>4)</b> выдыхать, извергать (ignem naribus <i>Lcr, V</i>; sulphur pectore <i>Cld</i>); издавать, испускать (divinum odorem <i>V</i>);\n<b>5)</b> быть воодушевлённым, быть полным вдохновения: s. tragicum <i>H</i> быть преисполненным трагического духа; quod spiro et placeo tuum est <i>H</i> (лишь) благодаря тебе (Мельпомена) я полон вдохновения и нравлюсь (читателям); s. bellum <i>Lcr</i> быть полным воинского духа; s. majora <i>QC</i> стремиться к большему;\n<b>6)</b> звучать (dulcius <i>Q</i>);\n<b>7)</b> благоухать, пахнуть (thymbra graviter spirans <i>V</i>; s. floribus <i>St</i>)."],
  ["spissa", "<big>spissa</big>\n\n<b>spissa, ae</b> <i>f</i> [spissus]\n(<i>sc.</i> vestis) плотная (тёплая) одежда <i>Sen</i>."],
  ["spissamentum", "<big>spissamentum</big>\n\n<b>spissamentum, i</b> <i>n</i> [spisso]\nзатычка, пробка <i>Col, Sen</i>."],
  ["spissatio", "<big>spissatio</big>\n\n<b>spissatio, onis</b> <i>f</i> [spisso]\nуплотнение, сгущение <i>Sen</i>."],
  ["spisse", "<big>spisse</big>\n\n<b>spisse</b> [spissus]\n<b>1)</b> густо, тесно (ponere arbores <i>Col</i>); плотно (calcare carbones <i>PM</i>);\n<b>2)</b> медленно (pervenire <i>C</i>);\n<b>3)</b> часто: s. basiare <i>Pt</i> осыпать частыми поцелуями."],
  ["spissesco", "<big>spissesco</big>\n\n<b>spissesco, —, —, ere</b> [spissus]\nгустеть, сгущаться, уплотняться <i>Lcr, CC</i>."],
  ["spissigradus", "<big>spissigradus</big>\n\n<b>spissigradus, a, um</b> [spisse + gradior]\nмедленно идущий (homo <i>Pl</i>)."],
  ["spissitas", "<big>spissitas</big>\n\n<b>spissitas, atis</b> <i>f</i> [spissus]\nгустота, плотность <i>Vtr, PM</i>."],
  ["spissitudo", "<big>spissitudo</big>\n\n<b>spissitudo, inis</b> <i>f</i> [spissus]\n<b>1)</b> плотность, сгущённость (aeris <i>Sen</i>; mellis <i>Scr</i>);\n<b>2)</b> густой рой, множество (muscarum <i>Eccl</i>)."],
  ["spisso", "<big>spisso</big>\n\n<b>spisso, avi, atum, are</b> [spissus]\n<b>1)</b> сгущать, уплотнять (lac igne spissatur <i>PM</i>): vis spissandi <i>PM</i> вяжущее свойство;\n<b>2)</b> ускорять, быстро выполнять (s. officia <i>Pt</i>)."],
  ["spissus", "<big>spissus</big>\n\n<b>spissus, a, um</b>\n<b>1)</b> густой (coma <i>H</i>; umbrae <i>V</i>): spississima nox <i>Pt</i> непроглядная ночная тьма; spissa ramis laurea <i>H</i> густоветвистый (тенистый) лавр || частый (grando <i>O</i>; basia <i>Pt</i>); плотный (tunica <i>Pl</i>); уплотнённый, слежавшийся (arena <i>V</i>); сгустившийся, свернувшийся (sanguis <i>QC</i>); твёрдый, крепкий (litus <i>O</i>); переполненный, набитый (theatrum <i>H</i>);\n<b>2)</b> медленный, медлительный (opus, exitus <i>C</i>): pro spisso (<i>v. l.</i> perspisso) <i>Pl</i> нескоро, с запозданием."],
  ["spithama", "<big>spithama</big>\n\n<b>spithama, ae</b> <i>f</i> (<i>греч.</i>) <i>PM</i>\n= dodrans."],
  ["splanchna", "<big>splanchna</big>\n\n<b>splanchna, orum</b> <i>n</i> (<i>греч.</i>) <i>анат.</i>\nвнутренности <i>CA</i>."],
  ["splen", "<big>splen</big>\n\n<b>splen, enis</b> <i>m</i> (<i>греч.</i>; <i>лат.</i> lien)\nселезёнка <i>Col, PM, Pers, Vtr</i>."],
  ["splendentia", "<big>splendentia</big>\n\n<b>splendentia, ae</b> <i>f</i> <i>Hier</i>\n= splendor."],
  ["splendeo", "<big>splendeo</big>\n\n<b>splendeo, dui, —, ere</b>\n<b>1)</b> блестеть, сиять, сверкать (claro colore <i>Lcr</i>; marmore purius <i>H</i>);\n<b>2)</b> <i>перен.</i> блистать, выделяться, отличаться (virtus splendet per sese <i>C</i>)."],
  ["splendesco", "<big>splendesco</big>\n\n<b>splendesco, dui, —, ere</b> [<i>inchoat. к</i> splendeo]\nприобретать блеск (gladius usu splendescit <i>Ap</i>; ignis splendescit <i>SenT</i>); заблистать, воссиять (s. oratione <i>C</i>)."],
  ["splendico", "<big>splendico</big>\n\n<b>splendico, —, —, are</b> <i>Ap</i>\n= splendeo."],
  ["splendide", "<big>splendide</big>\n\n<b>splendide</b> [splendidus]\n<b>1)</b> блистательно, блестяще, прекрасно, отлично, великолепно (dicere, ornare aliquid <i>C</i>): s. natus <i>PJ, Sen</i> высокородный, знатный;\n<b>2)</b> ясно (loqui <i>C</i>)."],
  ["splendido", "<big>splendido</big>\n\n<b>splendido, —, —, are</b>\nчистить до блеска (dentes <i>Ap</i>)."],
  ["splendidus", "<big>splendidus</big>\n\n<b>splendidus, a, um</b> [splendeo]\n<b>1)</b> блестящий (gladius <i>Fronto</i>); сияющий, сверкающий (stella <i>Enn</i>; vitrum <i>H</i>);\n<b>2)</b> прекрасный, отличный, превосходный, блистательный, славный (vir <i>C</i>; facta <i>H</i>; civitas <i>Nep</i>);\n<b>3)</b> чистый, ясный (caelum <i>PM</i>); звонкий (vox <i>C</i>);\n<b>4)</b> светлый, прозрачный (umor <i>Lcr</i>)."],
  ["splendor", "<big>splendor</big>\n\n<b>splendor, oris</b> <i>m</i> [splendeo]\n<b>1)</b> блеск, сверкание, блистание (auri <i>rhH</i>; flammae <i>O</i>);\n<b>2)</b> величие, слава, известность (summorum hominum <i>C</i>; imperii <i>C</i>);\n<b>3)</b> великолепие, пышность (domus <i>AG</i>; floris <i>O</i>);\n<b>4)</b> краса, украшение (ordinis <i>C</i>);\n<b>5)</b> живость, свежесть, благозвучие (verborum <i>C</i>);\n<b>6)</b> звонкость, ясность, чистота (vocis <i>C</i>)."],
  ["splendorifer", "<big>splendorifer</big>\n\n<b>splendori–fer, fera, ferum</b>\nсияющий, лучезарный (lumen <i>Tert</i>)."],
  ["spleneticus", "<big>spleneticus</big>\n\n<b>spleneticus (spleniticus), i</b> <i>m</i> <i>Ap, Pall, Eccl</i>\n= splenicus."],
  ["spleniatus", "<big>spleniatus</big>\n\n<b>spleniatus, a, um</b> [splenium]\nпокрытый пластырем (mentum <i>M</i>)."],
  ["splenicus", "<big>splenicus</big>\n\n<b>splenicus, i</b> <i>m</i> [splen]\nстрадающий болезнью селезёнки <i>PM</i>."],
  ["splenites", "<big>splenites</big>\n\n<b>splenites, ae</b> <i>m</i> <i>CA</i>\n= splenicus."],
  ["spleniticus", "<big>spleniticus</big>\n\n<b>spleniticus</b> <i>v. l.</i>\n= spleneticus."],
  ["splenium", "<big>splenium</big>\n\n<b>splenium, i</b> <i>n</i> (<i>греч.</i>)\n<b>1)</b> <i>бот.</i> кочедыжник <i>PM</i>;\n<b>2)</b> пластырь <i>PM</i>; косметический пластырёк, мушка <i>PJ, M</i>."],
  ["spodiacus", "<big>spodiacus</big>\n\n<b>spodiacus, a, um</b> (<i>греч.</i>) <i>Scr</i>\n= cinereus."],
  ["spodium", "<big>spodium</big>\n\n<b>spodium, i</b> <i>n</i> (<i>греч.</i>)\n<b>1)</b> пепел, зола <i>PM</i>;\n<b>2)</b> металлический шлак, окалина <i>PM</i>."],
  ["spodos", "<big>spodos</big>\n\n<b>spodos, i</b> <i>f</i> <i>PM</i>\n= spodium 2."],
  ["Spoletina", "<big>Spoletina</big>\n\n<b>Spoletina, orum</b> <i>n</i> (<i>sc.</i> vina)\nсполетинские вина <i>M</i>."],
  ["Spoletinus", "<big>Spoletinus</big>\n\n<b>I Spoletinus, a, um</b> [Spoletium]\nсполетинский <i>C</i>.\n\n<b>II Spoletinus, i</b> <i>m</i>\nжитель города Spoletium <i>L</i>."],
  ["Spoletium", "<big>Spoletium</big>\n\n<b>Spoletium, i</b> <i>n</i>\nСполетий, <i>город в южн. Умбрии, на via Flaminia</i> (<i>ныне</i> Spoleto) <i>L, VP</i>."],
  ["spolia", "<big>spolia</big>\n\n<b>spolia, ae</b> <i>f</i> <i>Aug</i>\n= spolium."],
  ["spoliarium", "<big>spoliarium</big>\n\n<b>spoliarium, i</b> <i>n</i> [spolio]\n<b>1)</b> сполиарий, <i>место в амфитеатре, где добивали тяжело раненых и раздевали убитых гладиаторов</i> <i>Sen, Lampr</i>;\n<b>2)</b> разбойничий притон, вертеп <i>Sen, PJ</i>."],
  ["spoliatio", "<big>spoliatio</big>\n\n<b>spoliatio, onis</b> <i>f</i> [spolio]\n<b>1)</b> отнятие, лишение (dignitatis <i>C</i>);\n<b>2)</b> ограбление, расхищение (oppidorum fanorumque <i>C</i>)."],
  ["spoliator", "<big>spoliator</big>\n\n<b>spoliator, oris</b> <i>m</i> [spolio]\nграбитель, расхититель <i>C, L, J</i>."],
  ["spoliatrix", "<big>spoliatrix</big>\n\n<b>spoliatrix, icis</b> <i>f</i>\nрасхитительница <i>C, M</i>."],
  ["spoliatus", "<big>spoliatus</big>\n\n<b>spoliatus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> spolio;\n<b>2.</b> <i>adj.</i> разорённый, опустошённый, ограбленный, обездоленный (regnum <i>C</i>)."],
  ["spolio", "<big>spolio</big>\n\n<b>spolio, avi, atum, are</b> [spolium]\nснимать одежду, раздевать (aliquem <i>C</i>; s. corpus caesi hostis <i>L</i>); обдирать (folliculos leguminum dentibus <i>Pt</i>); грабить (aliquem <i>C</i>; domos <i>Sl</i>); похищать, отнимать силой, лишать (s. aliquem aliqua re <i>C, V etc.</i>; s. dignitatem alicujus <i>C</i>): spoliatus suos capillos (<i>acc. gr.</i>) <i>O</i> лишившийся волос."],
  ["spolium", "<big>spolium</big>\n\n<b>spolium, i</b> <i>n</i>\n<b>1)</b> снятая шкура (leonis <i>O</i>): s. pecudis <i>O</i> овчина, руно || сброшенная старая кожа (<i>sc.</i> serpentis <i>Lcr</i>);\n<b>2)</b> <i>преим. pl.</i> снятая с убитого неприятеля одежда <i>или</i> доспехи (spolia caesorum hostium <i>L</i> <i>или</i> de hostibus <i>QC</i>): spolia opima <i>L, V</i> доспехи, снятые с неприятельского полководца; spolium mendici <i>Pt</i> нищенские лохмотья;\n<b>3)</b> <i>преим. pl.</i> военная добыча (spolia hostium <i>C</i>, agrorum <i>L</i>): spolia classium <i>C</i> срезанные в качестве трофеев корабельные носы (<i>см.</i> rostrum); virtutis spolia <i>PS</i> добытая доблестью, <i>т. е.</i> заслуженная награда;\n<b>4)</b> украденное, награбленное (s. sceleris <i>O</i>)."],
  ["sponda", "<big>sponda</big>\n\n<b>sponda, ae</b> <i>f</i>\n<b>1)</b> подножие (s. pedesque <i>O</i>; s. grabati <i>Pt</i>);\n<b>2)</b> ложе, постель, диван <i>V, H, O, M, Su</i>;\n<b>3)</b> смертный одр <i>или</i> погребальные носилки (in orciniana sponda ferri <i>M</i>)."],
  ["spondaeus", "<big>spondaeus</big>\n\n<b>spondaeus, i</b> <i>m</i> <i>v. l.</i>\n= spondeus."],
  ["spondaicus", "<big>spondaicus</big>\n\n<b>spondaicus, a, um</b> <i>v. l.</i>\n= spondiacus."],
  ["spondalia", "<big>spondalia</big>\n\n<b>spondalia</b> <i>или</i> <b>spondaulia, orum</b> <i>n</i> <i>предпол.</i>\nпеснь в сопровождении флейты <i>C</i> (<i>v. l.</i>)."],
  ["sponden", "<big>sponden</big>\n\n<b>sponden</b> <i>Pl</i>\n= spondesne (<i>к</i> spondeo)."],
  ["spondeo", "<big>spondeo</big>\n\n<b>spondeo, spopondi, sponsum, ere</b>\n<b>1)</b> торжественно обязаться, клятвенно обещать (s. alicui pecunias, agros <i>C</i>; fidem <i>O</i>);\n<b>2)</b> выступать поручителем, ручаться, гарантировать (jam non promittere, sed s. <i>Sen</i>): aliquem vocare sponsum (<i>supin.</i>) <i>H</i> звать кого-л. в поручители; s. pro aliquo <i>C, H, Sen</i> поручиться за кого-л.; s. aliquid <i>Pl etc.</i> ручаться за что-л.;\n<b>3)</b> сговорить, помолвить (filiam suam s. alicui uxorem <i>Pl</i>);\n<b>4)</b> предвещать (nihil placidum <i>O</i>; clara de aliquo <i>Su</i>); подавать надежду (ingenium Philippi magnum spondebat virum <i>Just</i>). — <i>См. тж.</i> sponsus."],
  ["spondeum", "<big>spondeum</big>\n\n<b>spondeum, i</b> <i>n</i> (<i>греч.</i>)\n<b>1)</b> чаша для возлияний <i>Ap</i>;\n<b>2)</b> стихотворение, написанное спондеями <i>C ap. Boet</i>."],
  ["spondeus", "<big>spondeus</big>\n\n<b>spondēus, ī</b> <i>m</i> (<i>греч.</i>)\nспондей, <i>стихотворная стопа, состоящая из двух долгих слогов</i>: – – <i>C, H, Q, Aus</i>."],
  ["spondiacus", "<big>spondiacus</big>\n\n<b>spondiacus, a, um</b> [spondius]\nспондаический, состоящий из спондеев (versus)."],
  ["spondialia", "<big>spondialia</big>\n\n<b>spondialia, orum</b> <i>n</i>\nпеснь при жертвоприношении в сопровождении флейты <i>C</i> (<i>v. l.</i>)."],
  ["spondilium", "<big>spondilium</big>\n\n<b>spondilium, i</b> <i>n</i> <i>Is</i>\n= spondylium."],
  ["spondius", "<big>spondius</big>\n\n<b>spondius, i</b> <i>m</i> <i>v. l.</i>\n= spondeus."],
  ["spondylium", "<big>spondylium</big>\n\n<b>spondylium, i</b> <i>n</i> (<i>греч.</i>)\n<b>1)</b> <i>бот.</i> борщевик (<i>Heradeum Spondylium</i>, L.) <i>PM</i>;\n<b>2)</b> <i>Is</i> = spondylus 1;\n<b>3)</b> <i>CA</i> = spondylus 2."],
  ["spondylus", "<big>spondylus</big>\n\n<b>spondylus, i</b> <i>m</i> (<i>греч.</i>)\n<b>1)</b> круглый позвонок (<i>шейный или спинной</i>) <i>PM, Veg</i>;\n<b>2)</b> мясистая часть устриц и других моллюсков <i>Col, PM</i>;\n<b>3)</b> спондил (<i>род моллюска с шиповатой раковиной</i>) <i>Sen etc.</i>"],
  ["sponge-", "<big>sponge-</big>\n\n<b>sponge-</b> <i>v. l.</i>\n= spongi-."],
  ["spongia", "<big>spongia</big>\n\n<b>spongia, ae</b> <i>f</i> (<i>греч.</i>)\n<b>1)</b> губка (spongiam exprimere <i>CC</i>);\n<b>2)</b> род кольчуги <i>L, Su</i>;\n<b>3)</b> род пористого камня, <i>предпол.</i> пемза <i>Vtr, Pall</i>;\n<b>4)</b> сплетённые корни спаржи <i>Col, PM</i>."],
  ["spongiola", "<big>spongiola</big>\n\n<b>spongiola, ae</b> <i>f</i> [<i>demin. к</i> spongia]\n<b>1)</b> корневище спаржи <i>Col</i>;\n<b>2)</b> губчатый нарост на шиповнике <i>PM</i>."],
  ["spongiosus", "<big>spongiosus</big>\n\n<b>spongiosus, a, um</b> [spongia]\nгубчатый, пористый (pulmo <i>CC</i>; pumex <i>PM</i>)."],
  ["spongos", "<big>spongos</big>\n\n<b>spongos (-us), i</b> <i>m</i> (<i>греч.</i>)\nгубка <i>Jvc</i>."],
  ["spons", "<big>spons</big>\n\n<b>spons, spontis</b> <i>f</i> (<i>встреч. тк.</i> <i>abl. и gen.</i>) — <i>см.</i>\nsponte."],
  ["sponsa", "<big>sponsa</big>\n\n<b>sponsa, ae</b> <i>f</i> [sponsus II]\nобручённая, невеста <i>Pl, Ter etc.</i>: suam cuique sponsam, mihi meam <i>погов. C</i> = у всякого свой вкус."],
  ["sponsalia", "<big>sponsalia</big>\n\n<b>sponsalia, ium</b> <i>и</i> <b>orum</b> <i>n</i> [spondeo]\n<b>1)</b> обручение, помолвка <i>C, L, Sen, Su, Dig</i>;\n<b>2)</b> праздник по случаю обручения <i>C</i>;\n<b>3)</b> свадебные подарки <i>CJ</i>."],
  ["sponsalicius", "<big>sponsalicius</big>\n\n<b>sponsalicius, a, um</b> <i>CJ, Sid</i>\n= sponsalis."],
  ["sponsalis", "<big>sponsalis</big>\n\n<b>sponsalis, e</b> [sponsus]\nсвязанный с обручением (dies <i>Vr</i>; tabulae <i>Hier</i>)."],
  ["sponsio", "<big>sponsio</big>\n\n<b>sponsio, onis</b> <i>f</i> [spondeo]\n<b>1)</b> (<i>тж.</i> s. voti <i>C</i>) торжественное обещание, клятвенное обязательство (sponsione se obstringere <i>L</i>); предварительное соглашение (pacem non foedere, sed per sponsionem facere <i>L</i>);\n<b>2)</b> <i>юр.</i> спонсия, <i>взаимное соглашение между тяжущимися сторонами о внесении денежного залога в пользу той, которая выиграет</i> (sponsionem facere cum aliquo de aliqua re <i>C</i>): sponsione lacessere (provocare <i>G</i>) aliquem <i>C</i> предлагать кому-л. внести денежный залог, <i>т. е.</i> привлекать к судебной ответственности; sponsionem (<i>или</i> sponsione) vincere <i>C</i> выиграть процесс; sponsione condemnari <i>C</i> проиграть процесс;\n<b>3)</b> ручательство, поручительство (pro aliquo <i>Pl</i>);\n<b>4)</b> внесённый залог, гарантийная сумма <i>Vr</i>."],
  ["sponsiuncula", "<big>sponsiuncula</big>\n\n<b>sponsiuncula, ae</b> <i>f</i> [<i>demin. к</i> sponsio]\nнебольшой залог, ставка (<i>в пари</i>) <i>Pt</i>."],
  ["sponso", "<big>sponso</big>\n\n<b>sponso, —, —, are</b> [spondeo]\nобручиться <i>Dig</i> (s. aliquam sibi <i>Vlg</i> <i>и</i> s. aliquam <i>Aug</i>)."],
  ["sponsor", "<big>sponsor</big>\n\n<b>sponsor, oris</b> <i>m</i> [spondeo]\n<b>1)</b> поручитель (pro aliquo <i>C</i>; alicujus rei <i>C, Lcn</i>): quo sponsore <i>H</i> за поручительством которого; conjugii s. et obses <i>O</i> = Hymenaeus;\n<b>2)</b> крёстный отец <i>Tert</i>."],
  ["sponsum", "<big>sponsum</big>\n\n<b>sponsum, i</b> <i>n</i> [spondeo]\nторжественное обещание, обет, обязательство <i>Vr, C, H</i>."],
  ["sponsus", "<big>sponsus</big>\n\n<b>I sponsus, a, um</b> <i>part. pf. к</i>\nspondeo.\n\n<b>II sponsus, i</b> <i>m</i>\n<b>1)</b> обручённый, жених <i>Pl, C, H etc.</i>;\n<b>2)</b> претендент на руку (sponsi Penelopae <i>H</i>).\n\n<b>III sponsus, us</b> <i>m</i>\n<b>1)</b> торжественное обещание <i>Vr, Dig, C</i>;\n<b>2)</b> поручительство, ручательство <i>C</i>."],
  ["spontalis", "<big>spontalis</big>\n\n<b>spontalis, e</b> [spons]\nдобровольный, преднамеренный <i>Ap</i>."],
  ["spontaliter", "<big>spontaliter</big>\n\n<b>spontaliter</b>\nдобровольно, по собственной воле (aliquid facere <i>Sid</i>)."],
  ["spontanee", "<big>spontanee</big>\n\n<b>spontanee</b> <i>Vlg, Hier</i>\n= spontaliter."],
  ["spontaneus", "<big>spontaneus</big>\n\n<b>spontaneus, a, um</b> [spons]\nдобровольный, произвольный (mors <i>Macr, Aug</i>): spontanea voluntate <i>CTh</i> по собственной воле."],
  ["sponte", "<big>sponte</big>\n\n<b>sponte</b> (<i>abl.</i> <i>к</i> <i>неупотр.</i> *spons, <i>gen.</i> spontis)\n<b>1)</b> с разрешения (s. principis <i>T</i>);\n<b>2)</b> (<i>с pron.</i> mea, tua, sua <i>etc.</i>) по доброй воле, по собственному побуждению, добровольно, сам собой (sua s. recte facere <i>Ter</i>): homo suae spontis <i>CC</i> человек, владеющий собой, <i>т. е.</i> способный отвечать за свои поступки || <i>редко самостоят.</i> (Italiam non s. sequor <i>V</i>; non s. nocens <i>SenT</i>);\n<b>3)</b> своими силами, собственными средствами, <i>т. е.</i> один, без чужой помощи (nec sua s., sed eorum auxiliis <i>C</i>);\n<b>4)</b> (взятый) сам по себе (res quae sua s. scelerata est <i>C</i>);\n<b>5)</b> по собственному разумению (почину), впервые (aliquid sua s. instituere <i>C</i>)."],
  ["spontivus", "<big>spontivus</big>\n\n<b>spontivus, a, um</b> [sponte]\nпреднамеренный (damnum <i>Sol</i>)."],
  ["spopondi", "<big>spopondi</big>\n\n<b>spopondi</b> <i>pf. к</i>\nspondeo."],
  ["Sporades", "<big>Sporades</big>\n\n<b>Sporades, um</b> <i>f</i> (<i>греч.</i> «рассеянные», <i>sc.</i> insulae)\nСпорады, <i>группа о-вов Эгейского моря между Кикладами, Малой Азией и Критом</i> <i>PM, Mela</i>."],
  ["sporta", "<big>sporta</big>\n\n<b>sporta, ae</b> <i>f</i>\n<b>1)</b> корзина, плетёнка <i>Cato, Vr, Col, M etc.</i>;\n<b>2)</b> решето <i>или</i> сито <i>PM</i>."],
  ["sportella", "<big>sportella</big>\n\n<b>sportella, ae</b> <i>f</i> [<i>demin. к</i> sporta]\n<b>1)</b> корзиночка <i>Su, Pt</i>;\n<b>2)</b> корзинка со съестными припасами <i>C</i>."],
  ["sportula", "<big>sportula</big>\n\n<b>sportula, ae</b> <i>f</i> [<i>demin. к</i> sporta]\n<b>1)</b> корзинка <i>Pl, Ap</i>;\n<b>2)</b> корзинка с провизией <i>или</i> с деньгами (<i>обыкн. 10 сестерциев</i>), посылаемая в подарок (<i>напр.</i>, <i>патроном своим клиентам</i>) <i>Su, M, J</i>;\n<b>3)</b> подарок <i>PJ, Dig</i>;\n<b>4)</b> угощение <i>J</i>."],
  ["sprerunt", "<big>sprerunt</big>\n\n<b>sprerunt</b> <i>Eccl</i>\n= spreverunt (<i>pf. к</i> sperno)."],
  ["spretio", "<big>spretio</big>\n\n<b>spretio, onis</b> <i>f</i> [sperno]\nпрезрение <i>L</i> (<i>v. l.</i>), <i>Eccl</i>."],
  ["spretor", "<big>spretor</big>\n\n<b>spretor, oris</b> <i>m</i> [sperno]\nпрезирающий (deorum <i>O</i>; alicujus rei <i>Ap, Nem</i>)."],
  ["spretus", "<big>spretus</big>\n\n<b>I spretus, a, um</b> <i>part. pf. к</i>\nsperno.\n\n<b>II spretus, us</b> <i>m</i>\nпрезрение, пренебрежение <i>Ap, Sid</i>."],
  ["sprevi", "<big>sprevi</big>\n\n<b>sprevi</b> <i>pf. к</i>\nsperno."],
  ["spuma", "<big>spuma</big>\n\n<b>spuma, ae</b> <i>f</i> [spuo]\nпена (rates spumas agunt <i>Pt</i>): spumas agere in ore <i>C</i> испускать ртом пену <i>или</i> говорить с пеной у рта; s. caustica <i>или</i> Batava <i>M</i> едкая мазь, <i>которой галлы и германцы окрашивали волосы в рыжий цвет</i>; s. argenti <i>V, PM etc.</i> серебряный глёт."],
  ["spumabundus", "<big>spumabundus</big>\n\n<b>spumabundus, a, um</b> [spumo]\nпенящийся <i>Ap</i>."],
  ["spumatio", "<big>spumatio</big>\n\n<b>spumatio, onis</b> <i>f</i> <i>CA</i>\n= spumatus."],
  ["spumatus", "<big>spumatus</big>\n\n<b>spumatus, us</b> <i>m</i> [spumo]\nиспускание пены <i>или</i> пена <i>St</i>."],
  ["spumesco", "<big>spumesco</big>\n\n<b>spumesco, —, —, ere</b> [spuma]\nначинать пениться, запениться (remo alicujus <i>O</i>)."],
  ["spumeus", "<big>spumeus</big>\n\n<b>spumeus, a, um</b> [spuma]\n<b>1)</b> пенящийся, пенистый, покрытый пеной (amnis <i>V</i>; aper <i>M</i>);\n<b>2)</b> похожий на пену (sucus <i>PM</i>; color equorum <i>Pall</i>)."],
  ["spumidus", "<big>spumidus</big>\n\n<b>spumidus, a, um</b> [spuma]\nпенящийся, пенистый (umor <i>Ap</i>)."],
  ["spumifer", "<big>spumifer</big>\n\n<b>spumifer, fera, ferum</b> [spuma + fero]\nнесущий пену, покрытый пеной, пенящийся (amnis, fons <i>O</i>; fluctus <i>St</i>)."],
  ["spumiger", "<big>spumiger</big>\n\n<b>spumiger, gera, gerum</b> [spuma + gero] <i>Lcr, Man</i>\n= spumifer."],
  ["spumo", "<big>spumo</big>\n\n<b>spumo, avi, atum, are</b> [spuma]\n<b>1)</b> пениться, испускать пену, покрываться пеной (spumans salum <i>V</i>; aper spumans <i>V</i>);\n<b>2)</b> покрывать пеной (saxa spumata <i>Poeta ap. C</i>);\n<b>3)</b> извергать (наподобие пены), изрыгать (s. ex ore scelus <i>rhH</i>)."],
  ["spumosus", "<big>spumosus</big>\n\n<b>spumosus, a, um</b> [spuma]\n<b>1)</b> пенистый, полный пены (undae <i>O</i>; litora <i>Ctl</i>);\n<b>2)</b> <i>перен.</i> напыщенный (<i>sc.</i> carmen <i>Pers</i>)."],
  ["spuo", "<big>spuo</big>\n\n<b>spuo, spui, sputum, ere</b>\n<b>1)</b> плевать <i>CC etc.</i>: s. in sinum <i>PM, Pt</i> — <i>см.</i> sinus;\n<b>2)</b> <i>поэт.</i> выплёвывать, извергать (terram = pulverem <i>V</i>)."],
  ["spurcamen", "<big>spurcamen</big>\n\n<b>spurcamen, inis</b> <i>n</i> [spurco]\nгрязь, навоз (gregis spurcamina <i>Eccl</i>)."],
  ["spurcatus", "<big>spurcatus</big>\n\n<b>spurcatus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> spurco;\n<b>2.</b> <i>adj.</i> грязный, мерзкий, отвратительный (helluo spurcatissimus <i>C</i>)."],
  ["spurce", "<big>spurce</big>\n\n<b>spurce</b> [spurcus]\nгрязно, отвратительно, гадко, мерзко <i>Cato, Col, rhH, C etc.</i>"],
  ["spurcidicus", "<big>spurcidicus</big>\n\n<b>spurcidicus, a, um</b> [spurce + dico]\nсоставленный в не пристойных выражениях (versus <i>Pl</i>)."],
  ["spurcificus", "<big>spurcificus</big>\n\n<b>spurcificus, a, um</b> [spurcus + facio]\nпозорно поступающий, совершающий постыдные поступки (homo <i>Pt</i>)."],
  ["spurciloquium", "<big>spurciloquium</big>\n\n<b>spurci–loquium, i</b> <i>n</i> [loquor]\nсквернословие <i>Tert</i>."],
  ["spurcitia", "<big>spurcitia</big>\n\n<b>spurcitia, ae</b> <i>f</i> [spurcus]\nгрязь, нечистота, гадость <i>Col, PM</i>."],
  ["spurcities", "<big>spurcities</big>\n\n<b>spurcities, ei</b> <i>f</i> <i>Lcr, Ap, Vr</i>\n= spurcitia."],
  ["spurco", "<big>spurco</big>\n\n<b>spurco, avi, atum, are</b> [spurcus]\nпачкать, марать, загрязнять (aliquem <i>и</i> aliquid <i>Pl, Dig</i>; <i>перен.</i> spurcari impuris moribus <i>Ctl</i>). — <i>См. тж.</i> spurcatus."],
  ["spurcus", "<big>spurcus</big>\n\n<b>spurcus, a, um</b>\n<b>1)</b> грязный, нечистый (saliva <i>Ctl</i>; umor <i>Ap</i>);\n<b>2)</b> гадкий, мерзкий, отвратительный (tempestas <i>C, Su</i>; vita <i>AG</i>; homo <i>C</i>); позорный (amputatio digitorum <i>CTh</i>)."],
  ["Spurinna", "<big>Spurinna</big>\n\n<b>Spurin(n)a, ae</b> <i>m</i>\nСпурин(н)а, <i>римск. nomen (этрусск. происхождения); наиболее известны</i>:\n<b>1)</b> S., <i>жрец-haruspex, предсказавший, якобы, Цезарю, что в день Мартовских Ид ему грозит смертельная опасность</i> <i>C, Su, VM</i>;\n<b>2)</b> Vestricius S., <i>политик и поэт середины I в. н. э., друг Плиния Младшего</i> <i>PJ, T</i>."],
  ["spurium", "<big>spurium</big>\n\n<b>spurium, i</b> <i>n</i>\n<b>1)</b> vasculum muliebre <i>Is</i>;\n<b>2)</b> морское животное этой формы <i>Ap</i>."],
  ["spurius", "<big>spurius</big>\n\n<b>I spurius, a, um</b>\n<b>1)</b> внебрачный, родившийся от неизвестного отца (filius <i>G, Ap</i>);\n<b>2)</b> ложный (vates <i>Aus</i>); подложный (versus <i>Aus</i>).\n\n<b>II spurius, i</b> <i>m</i>\nвнебрачный ребёнок от неизвестного отца <i>Dig, CJ, Just, Ap</i>.\n\n<b>III Spurius, i</b> <i>m</i>\nСпурий, <i>римск. praenomen</i> (<i>в сокр.</i> Sp.)."],
  ["sputamen", "<big>sputamen</big>\n\n<b>sputamen, inis</b> <i>n CA</i> <i>и</i> <b>sputamentum, i</b> <i>n</i> <i>Tert</i>\n= sputum."],
  ["sputatilicus", "<big>sputatilicus</big>\n\n<b>sputatilicus, a, um</b> [sputo]\nдостойный оплевания, <i>т. е.</i> отвратительный, гадкий (crimina <i>Sisenna ap. C</i>)."],
  ["sputator", "<big>sputator</big>\n\n<b>sputator, oris</b> <i>m</i> [sputo]\nпостоянно плюющий <i>Pl</i>."],
  ["sputo", "<big>sputo</big>\n\n<b>sputo, —, —, are</b> [<i>intens. к</i> spuo]\nплевать, выплёвывать (sanguinem <i>Pl</i>; cum sanguine dentes <i>O</i>): morbus qui sputatur <i>Pl</i> болезнь, которая (<i>по верованиям древних</i>) изгонялась плевками, <i>т. е.</i> эпилепсия."],
  ["sputum", "<big>sputum</big>\n\n<b>sputum, i</b> <i>n</i> [spuo]\n<b>1)</b> слюна, плевок <i>или</i> мокрота, слизь <i>Lcr, VM, CC, Pt etc.</i>;\n<b>2)</b> тонкий налёт <i>или</i> слой (hoc linitur sputo caryota <i>M</i>)."],
  ["sputus", "<big>sputus</big>\n\n<b>sputus, us</b> <i>m</i> <i>CA, Lact, Aug</i>\n= sputum 1."],
  ["squalentia", "<big>squalentia</big>\n\n<b>I squalentia, ae</b> <i>f</i>\nгрязь <i>Tert</i>.\n\n<b>II squalentia, ium</b> <i>n</i>\nпустыни <i>PM</i>."],
  ["squaleo", "<big>squaleo</big>\n\n<b>squaleo, —, —, ere</b>\n<b>1)</b> быть покрытым коркой, быть шероховатым, шершавым, жёстким (squalentes conchae <i>V</i>); быть чешуйчатым (squalentia terga lacerti <i>V</i>); быть покрытым (pulvere <i>Lcn</i>; rubigine <i>Q</i>; auro <i>V</i>): squalentia tela venenis <i>O</i> напоённые ядом стрелы;\n<b>2)</b> изобиловать, кишеть (humus squalet serpentibus <i>O</i>);\n<b>3)</b> быть пересохшим, сухим (squalent fontes <i>St</i>);\n<b>4)</b> быть грязным, нечёсанным (barba, coma squalens <i>V, O</i>);\n<b>5)</b> быть запущенным, невозделанным (squalent arva <i>V</i>): squalens litus <i>T</i> пустынный берег;\n<b>6)</b> быть в трауре, скорбеть (luget senatus, squalent munici pia <i>C</i>)."],
  ["squales", "<big>squales</big>\n\n<b>squales, is</b> <i>f</i> [squaleo]\nгрязь <i>Pac, Vr</i>."],
  ["squalide", "<big>squalide</big>\n\n<b>squalide</b> [squalidus]\nбез прикрас, незатейливо, грубо (dicere <i>C</i>)."],
  ["squaliditas", "<big>squaliditas</big>\n\n<b>squaliditas, atis</b> <i>f</i>\nбеспорядочность (rerum <i>Amm</i>)."],
  ["squalidus", "<big>squalidus</big>\n\n<b>squalidus, a, um</b> [squaleo]\n<b>1)</b> шероховатый, шершавый (corpora, membra <i>Lcr</i>);\n<b>2)</b> грубый, простой (haec, <i>sc.</i> verba <i>C</i>);\n<b>3)</b> грязный, неопрятный (corpora <i>L</i>; homo <i>Ter</i>): barba s. <i>Ap</i> с грязной бородой || одетый в рубище (reus <i>O</i>);\n<b>4)</b> запущенный, невозделанный (humus <i>O</i>): squalida siccitas <i>QC</i> безводная пустынность."],
  ["squalitas", "<big>squalitas</big>\n\n<b>squalitas, atis</b> <i>f</i> <i>Acc, LM</i>\n= squalor."],
  ["squalitudo", "<big>squalitudo</big>\n\n<b>squalitudo, inis</b> <i>f</i> <i>Acc</i>\n= squalor."],
  ["squalor", "<big>squalor</big>\n\n<b>squalor, oris</b> <i>m</i> [squaleo]\n<b>1)</b> шероховатость, шершавость (materiae <i>Lcr</i>);\n<b>2)</b> грубость (saeculi <i>Q</i>);\n<b>3)</b> грязь, неопрятность (homo squaloris plenus ac pulveris <i>C</i>);\n<b>4)</b> скорбная одежда, вретище, траур (s. sordesque <i>C</i>);\n<b>5)</b> пустынность, неприветливость, негостеприимность (locorum <i>QC</i>; tenebrarum <i>Amm</i>)."],
  ["squalus", "<big>squalus</big>\n\n<b>I squalus, a, um</b>\nгрязный (vestis <i>Enn</i>).\n\n<b>II squalus, i</b> <i>m</i>\nакула <i>Vr, O, PM</i>."],
  ["squama", "<big>squama</big>\n\n<b>squama, ae</b> <i>f</i>\n<b>1)</b> чешуя <i>C, O, V etc.</i>;\n<b>2)</b> <i>pl.</i> чешуйчатая броня <i>V, Sil</i>;\n<b>3)</b> <i>тж. pl.</i> окалина (ferri <i>PM</i>; aeris <i>CC</i>);\n<b>4)</b> <i>мед.</i> катаракта (in oculis <i>PM</i>);\n<b>5)</b> кожица, шелуха, плёнка (milii <i>PM</i>);\n<b>6)</b> <i>поэт.</i> рыба <i>J</i>;\n<b>7)</b> грубость, неблагозвучие (sermonis Celtici <i>Sid</i>)."],
  ["squamatim", "<big>squamatim</big>\n\n<b>squamatim</b> [squama]\nчешуеобразно, наподобие чешуи (nucamenta s. compacta <i>PM</i>)."],
  ["squamatio", "<big>squamatio</big>\n\n<b>squamatio, onis</b> <i>f</i> [squama]\nобразование чешуи <i>Is</i>."],
  ["squamatus", "<big>squamatus</big>\n\n<b>I squamatus, a, um</b> <i>Vlg, Tert</i>\n= squameus.\n\n<b>II squamatus, i</b> <i>m</i> <i>Is предпол.</i>\n= squatus."],
  ["squameus", "<big>squameus</big>\n\n<b>squameus, a, um</b> [squama]\nчешуйчатый, покрытый чешуёй (anguis <i>V</i>; serpentium pedicae <i>Ap</i>): auro s. <i>Cld</i> одетый в блистающую золотом чешуйчатую броню."],
  ["squamifer", "<big>squamifer</big>\n\n<b>squamifer, fera, ferum</b> [squama + fero]\nпокрытый чешуёй, чешуйчатый (pisces <i>C</i>; turba, <i>sc.</i> serpentium <i>SenT</i>)."],
  ["squamiger", "<big>squamiger</big>\n\n<b>I squamiger, gera, gerum</b> [squama + gero] <i>O, PM</i>\n= squamifer.\n\n<b>II squamiger, geri</b> <i>m</i> <i>поэт.</i>\nрыба <i>Lcr</i>."],
  ["squamm-", "<big>squamm-</big>\n\n<b>squamm-</b> <i>v. l.</i>\n= squam-."],
  ["squamosus", "<big>squamosus</big>\n\n<b>squamosus, a, um</b> [squama]\n<b>1)</b> покрытый чешуёй, чешуйчатый (corpus, <i>sc.</i> piscium <i>Poeta ap. C</i>);\n<b>2)</b> шероховатый, шершавый (lingua <i>Lcn</i>)."],
  ["squamula", "<big>squamula</big>\n\n<b>squamula, ae</b> <i>f</i> [<i>demin. к</i> squama]\nчешуйка <i>CC</i>."],
  ["squarrosus", "<big>squarrosus</big>\n\n<b>squarrosus, a, um</b>\nпокрытый паршой <i>LM</i>."],
  ["squatina", "<big>squatina</big>\n\n<b>squatina, ae</b> <i>f</i> <i>зоол.</i>\nморской ангел (<i>Squatina angelus</i>, L.), <i>разновидность акулы</i> <i>PM</i>."],
  ["squatus", "<big>squatus</big>\n\n<b>squatus, i</b> <i>m</i> <i>PM, Is</i>\n= squatina."],
  ["squilla", "<big>squilla</big>\n\n<b>squilla, ae</b> <i>f</i> <i>v. l.</i>\n= scilla 2."],
  ["st", "<big>st</big>\n\n<b>st!</b> <i>interj.</i>\nтс!, тише! (st, st, tacete <i>Pl, Ter</i>)."],
  ["Stabiae", "<big>Stabiae</big>\n\n<b>Stabiae, arum</b> <i>f</i>\nСтабии, <i>город в Кампании, близ морского побережья, между Помпеями и Суррентом, засыпанный во время извержения Везувия в 79 г. н. э.</i> <i>O, PJ</i>."],
  ["Stabianum", "<big>Stabianum</big>\n\n<b>Stabianum, i</b> <i>n</i>\n<i>название предместья близ Стабий</i> <i>C</i>."],
  ["stabianus", "<big>stabianus</big>\n\n<b>stabianus, a, um</b> [Stabiae]\nстабийский (litus <i>Sen</i>)."],
  ["stabilia", "<big>stabilia</big>\n\n<b>stabilia, ium</b> <i>n</i>\n(нечто) устойчивое, прочное, надёжное (s. meliora incertis <i>C</i>)."],
  ["stabilimen", "<big>stabilimen</big>\n\n<b>stabilimen, inis</b> <i>n</i> <i>Acc</i>\n= stabilimentum."],
  ["stabilimentum", "<big>stabilimentum</big>\n\n<b>stabilimentum, i</b> <i>n</i> [stabilio]\n<b>1)</b> средство укрепления (favorum <i>PM</i>); укрепляющее средство (ventris stabilimenta: pane et assa bubula <i>Pl</i>);\n<b>2)</b> опорная точка (Sicilia et Sardinia stabilimenta bellorum <i>VM</i>); оплот, устой (fidei Christianae <i>Aug</i>)."],
  ["stabilio", "<big>stabilio</big>\n\n<b>stabilio, ivi, itum, ire</b> [stabilis]\nутверждать, укреплять (stipites <i>Cs</i>; dentes mobiles <i>PM</i>; rem publicam <i>C</i>); устанавливать (aliquid probe <i>C</i>)."],
  ["stabilis", "<big>stabilis</big>\n\n<b>stabilis, e</b> [sto]\n<b>1)</b> твёрдо стоящий на ногах (equus <i>L</i>);\n<b>2)</b> постоянный, служащий постоянным местожительством (domus <i>Pl</i>);\n<b>3)</b> неизменный, верный, стойкий (amicus <i>C</i>); твёрдый, непреклонный (sententia <i>C</i>): stabili pede <i>O</i> <i>или</i> gradu <i>L</i> твёрдой ногой || верный, надёжный (stabili tramite currere <i>SenT</i>); прочный, устойчивый (regnum <i>SenT</i>); непреходящий (bonum <i>C</i>); постоянно текущий (aquae <i>PM</i>): stabile est <i>Pl etc.</i> твёрдо решено (что)."],
  ["stabilitas", "<big>stabilitas</big>\n\n<b>stabilitas, atis</b> <i>f</i> [stabilis]\n<b>1)</b> прочность, устойчивость (dentium <i>PM</i>);\n<b>2)</b> твёрдость, стойкость, выдержка (peditum in proeliis <i>Cs</i>);\n<b>3)</b> прочность, надёжность, неизменность (amicitiae, fortunae <i>C</i>)."],
  ["stabiliter", "<big>stabiliter</big>\n\n<b>stabiliter</b> [stabilis]\nпрочно, надёжно, устойчиво (aliquid fundare <i>Su</i>; permanere ad summum exitum vitae <i>Vtr</i>); уверенно (spem in aliquo collocare <i>Aug</i>)."],
  ["stabilitor", "<big>stabilitor</big>\n\n<b>stabilitor, oris</b> <i>m</i> [stabilio]\nутверждающий, зиждитель (Juppiter stator stabilitorque <i>Sen</i>)."],
  ["stabularia", "<big>stabularia</big>\n\n<b>stabularia, ae</b> <i>f</i>\nтрактирщица <i>Aug</i>."],
  ["stabularius", "<big>stabularius</big>\n\n<b>stabularius, i</b> <i>m</i> [stabulum]\n<b>1)</b> конюх <i>Col</i>;\n<b>2)</b> трактирщик, содержатель постоялого двора <i>Sen</i>."],
  ["stabulatio", "<big>stabulatio</big>\n\n<b>stabulatio, onis</b> <i>f</i> [stabulor]\n<b>1)</b> скотный двор, загон, хлев (ovilis <i>Ap</i>): s. hiberna <i>Col</i> зимовка скота;\n<b>2)</b> помещение (для людей) <i>AG, Macr</i>."],
  ["stabulo", "<big>stabulo</big>\n\n<b>stabulo, —, —, are</b>\n<b>1)</b> помещаться, находиться в конюшне, в стойле, в хлеву <i>или</i> птичнике <i>V, St</i>;\n<b>2)</b> помещать в стойло, в хлев (s. alienum pecus in suo fundo <i>Vr</i>);\n<b>3)</b> обитать, жить (in foribus <i>V</i>)."],
  ["stabulor", "<big>stabulor</big>\n\n<b>stabulor, atus sum, ari</b> <i>depon.</i> <i>Vr, Col, O etc.</i>\n= stabulo 1."],
  ["stabulum", "<big>stabulum</big>\n\n<b>stabulum, i</b> <i>n</i> [sto]\n<b>1)</b> стоянка, местопребывание, место, прибежище (stabile s. habere <i>Pl</i>): s. confidentiae <i>Pl</i> надёжное убежище;\n<b>2)</b> логовище (ferarum <i>V</i>);\n<b>3)</b> стойло, хлев (pecudum <i>Col</i>); конюшня (equorum <i>Sol</i>); птичник, курятник (avium cohortalium <i>Col</i>); <i>pl.</i> скотный двор: s. apium <i>Col</i> улей; s. piscium <i>Col</i> рыбный водоём, пруд;\n<b>4)</b> <i>поэт.</i> пастбище, выгон <i>V</i>;\n<b>5)</b> стадо, скот (nutritor stabuli <i>M</i>);\n<b>6)</b> пастушья хижина (stabula pastorum <i>C</i>);\n<b>7)</b> постоялый двор <i>PJ, M, Pt</i>;\n<b>8)</b> дом разврата, притон (s. flagitii <i>Pl</i>)."],
  ["stacta", "<big>stacta</big>\n\n<b>stacta, ae</b> <i>f</i> (<i>греч.</i>)\nсок мирры, мирровое масло <i>Pl, Lcr</i>."],
  ["stacte", "<big>stacte</big>\n\n<b>stacte, es</b> <i>f</i> <i>Pl, PM</i>\n= stacta."],
  ["stadiatus", "<big>stadiatus</big>\n\n<b>stadiatus, a, um</b> [stadium]\nснабжённый стадионом (porticus <i>Vtr</i>)."],
  ["stadiodromos", "<big>stadiodromos</big>\n\n<b>stadiodromos, i</b> <i>m</i> (<i>pl.</i> -moe) (<i>греч.</i>)\nучастник состязаний в беге <i>PM, Cens, Eccl</i>."],
  ["stadium", "<big>stadium</big>\n\n<b>stadium, i</b> <i>n</i> (<i>греч.</i>)\n<b>1)</b> ристалище, стадион (s. currere <i>C</i>);\n<b>2)</b> поприще (s. artis rhetoricae <i>rhH</i>);\n<b>3)</b> состязание (in stadio laudis aliquem exercere <i>C</i>);\n<b>4)</b> стадий (олимпийский), <i>греч. мера длины в 125 римск. passus или 625 футов</i> = <i>185 м</i> <i>C, PM, QC</i>."],
  ["stadius", "<big>stadius</big>\n\n<b>stadius, i</b> <i>m</i> <i>Macr</i>\n= stadium 4."],
  ["Stagira", "<big>Stagira</big>\n\n<b>Stagira, orum</b> <i>n</i>\nСтагиры, <i>македонский город в Халкидике, между озером Больбе и Стримонским заливом, родина Аристотеля</i> <i>PM, Amm</i>."],
  ["Stagirites", "<big>Stagirites</big>\n\n<b>Stagirites, ae</b> <i>m</i>\nстагирит, <i>уроженец Стагир, т. е.</i> (<i>преим.</i>) Аристотель <i>C, Vop</i>."],
  ["stagnantia", "<big>stagnantia</big>\n\n<b>stagnantia, ium</b> <i>n</i> [stagno I]\nзатопленные места <i>PM</i>."],
  ["stagnensis", "<big>stagnensis</big>\n\n<b>stagnensis, e</b> [stagnum I]\nпрудовой (pisces <i>Aug</i>)."],
  ["stagneus", "<big>stagneus</big>\n\n<b>stagneus, a, um</b> [stagnum II]\nсделанный из сплава (<i>или</i> покрытый сплавом) серебра и свинца (pyxis <i>PM</i>)."],
  ["stagno", "<big>stagno</big>\n\n<b>I stagno, avi, itum, are</b> [stagnum]\n<b>1)</b> выходить из берегов, разливаться (aquae stagnantes <i>QC</i>);\n<b>2)</b> быть наводнённым, находиться под водой, покрываться водой: orbis stagnat paludibus <i>O</i> мир затоплен болотом, <i>т. е.</i> земля превратилась в болото;\n<b>3)</b> затоплять, наводнять (Tiberis plana urbis stagnavit <i>T</i>).\n\n<b>II stagno, avi, atum, are</b>\n<b>1)</b> останавливать, делать неподвижным, стоячим (aqua bitumine stagnatur <i>Just</i>);\n<b>2)</b> предохранять, обеспечивать (s. se adversus insidias <i>Just</i>)."],
  ["stagnosus", "<big>stagnosus</big>\n\n<b>stagnosus, a, um</b> [stagnum I]\nпокрытый водой, наводнённый, полный стоячей воды (loca <i>Ap</i>)."],
  ["stagnum", "<big>stagnum</big>\n\n<b>I stagnum, i</b> <i>n</i>\n<b>1)</b> выступившая из берегов вода: озеро, пруд, болото, лужа (fontes et stagna <i>Poeta ap. C</i>);\n<b>2)</b> медленно текущая вода (dulcia stagna Caystri <i>V</i>): Phrixeae stagna sororis <i>O</i> = Hellespontus;\n<b>3)</b> искусственный пруд, бассейн: s. calidae aquae <i>T</i> купальный бассейн; s. navale <i>T, St</i> искусственный водоём для военно-морских упражнений.\n\n<b>II stagnum, i</b> <i>n</i>\n<b>1)</b> смесь свинца с серебром, свинцовый блеск <i>PM, Su</i>;\n<b>2)</b> олово <i>Is</i>."],
  ["stagonias", "<big>stagonias</big>\n\n<b>stagonias, ae</b> <i>m</i> (<i>греч.</i>; <i>лат.</i> masculum tus <i>V</i>)\nстагоний, «мужской» (<i>т. е.</i> лучший) фимиам <i>PM</i>."],
  ["stagonitis", "<big>stagonitis</big>\n\n<b>stagonitis, idis</b> <i>f</i> (<i>греч.</i>) <i>PM</i>\n= galbanum."],
  ["Staienus", "<big>Staienus</big>\n\n<b>Staienus, i</b> <i>m</i>\n(C. S. <i>или</i> C. Aelius S. Paetus) Стайен, <i>подкупленный судья в процессе Оппианика, впоследствии осуждённый</i> <i>C</i>."],
  ["stalagmium", "<big>stalagmium</big>\n\n<b>stalagmium, i</b> <i>n</i> (<i>греч.</i>)\nсерьги с подвесками <i>Caec, Pl</i>."],
  ["stamen", "<big>stamen</big>\n\n<b>stamen, inis</b> <i>m</i> [sto]\n<b>1)</b> ткацкая основа (<i>на вертикальном станке</i>): s. intendere <i>Sen</i> навивать основу;\n<b>2)</b> нить: s, ducere (torquere) <i>O</i> прясть; stamine fallere somnum <i>Prp</i> разгонять сон прядением;\n<b>3)</b> <i>поэт.</i> нить Парок, нить жизни, жизнь, судьба (stamina fati alicujus <i>O</i>): queri nimio de stamine <i>J</i> жаловаться на (свою) слишком затянувшуюся жизнь;\n<b>4)</b> струна (stamina pollice sollicitare <i>O</i>);\n<b>5)</b> древесное волокно <i>PM</i>;\n<b>6)</b> цветочная тычинка <i>PM</i>;\n<b>7)</b> головная повязка жрецов (puniceo stamine vincire comas <i>Prp</i>);\n<b>8)</b> ткань <i>или</i> одеяние (pretioso stamine aliquem velare <i>Cld</i>)."],
  ["staminaria", "<big>staminaria</big>\n\n<b>staminaria, ae</b> <i>f</i> [stamen]\nпряха <i>Laberius ap. AG</i>."],
  ["staminatus", "<big>staminatus</big>\n\n<b>staminatus, a, um</b> (<i>греч.</i> stamnos кувшин)\nналитый в кувшин <i>или</i> кружку: staminatas (<i>sc.</i> potiones) ducere <i>Pt</i> пить целыми кувшинами."],
  ["stamineus", "<big>stamineus</big>\n\n<b>stamineus, a, um</b> [stamen]\n<b>1)</b> обмотанный нитками (rota <i>Prp</i>);\n<b>2)</b> волокнистый (vena ligni <i>PM</i>)."],
  ["stanneus", "<big>stanneus</big>\n\n<b>stanneus, a, um</b> <i>PM, Col, Ap etc.</i>\n= stagneus."],
  ["stannum", "<big>stannum</big>\n\n<b>stannum, i</b> <i>n</i> <i>PM, Su</i>\n(<i>v. l.</i>)= stagnum II."],
  ["staphis", "<big>staphis</big>\n\n<b>staphis, idis</b> <i>f</i> (<i>греч.</i>) (<i>тж.</i> s. agria) <i>бот.</i>\n(<i>предпол.</i>) вшивика, мытник (<i>Pedicularis palustris</i>, L.) <i>CC, PM, Pall</i>."],
  ["staphyle", "<big>staphyle</big>\n\n<b>staphyle, es</b> <i>f</i> (<i>греч.</i>; <i>лат.</i> vitis alba) <i>бот.</i>\nпереступень белый (<i>Bryonia alba</i>, L.) <i>PM</i>."],
  ["staphylinos", "<big>staphylinos</big>\n\n<b>staphylinos (-us), i</b> <i>m, f</i> (<i>греч.</i>) <i>бот.</i>\nпастернак (<i>разновидность</i>) <i>Col, PM</i>."],
  ["staphylodendron", "<big>staphylodendron</big>\n\n<b>staphylodendron, i</b> <i>m</i> (<i>греч.</i>)\nфисташковое дерево (<i>Staphylea pinnata</i>, L.) <i>PM</i>."],
  ["Staseas", "<big>Staseas</big>\n\n<b>Staseas, ae</b> <i>m</i>\nСтасей, <i>неаполитанский философ-перипатетик, друг Цицерона</i> <i>C</i>."],
  ["Stata", "<big>Stata</big>\n\n<b>Stata, ae</b> <i>f</i> (<i>sc.</i> mater) <i>C</i>\n= Vesta."],
  ["statarius", "<big>statarius</big>\n\n<b>I statarius, a, um</b> [sto]\n<b>1)</b> стоящий на одном месте, <i>т. е.</i> сражающийся в строю (miles <i>L</i>);\n<b>2)</b> спокойный, уравновешенный (orator <i>C</i>): comoedia stataria (<i>в отличие от</i> motoria) <i>Ter</i> спокойная, лишённая бурных событий пьеса.\n\n<b>II statarius, i</b> <i>m</i>\nактёр в comoedia stataria (<i>см.</i> statarius I, 2) <i>C</i>."],
  ["Statellas", "<big>Statellas</big>\n\n<b>Statellas, atis</b> [Stat(i)elli]\nстательский (ager <i>L</i>)."],
  ["Statellates", "<big>Statellates</big>\n\n<b>Statellates</b> <i>и</i> <b>Statieilenses, ium</b> <i>m</i>\nжители города Aquae Statiellorum <i>PM, C</i>."],
  ["stater", "<big>stater</big>\n\n<b>stater, eris</b> <i>m</i> (<i>греч.</i>)\n<b>1)</b> <i>Boet</i> = semuncia;\n<b>2)</b> <i>Vlg, Eccl</i> = siclus."],
  ["statera", "<big>statera</big>\n\n<b>statera, ae</b> <i>f</i> (<i>греч.</i>)\n<b>1)</b> весы <i>C, Vtr, Su, Pt</i>;\n<b>2)</b> поперечный брус (коромысло) <i>или</i> ярмо <i>St</i>;\n<b>3)</b> блюдо <i>или</i> миска <i>PM</i>;\n<b>4)</b> ценность, достоинство <i>PM</i>."],
  ["staticulum", "<big>staticulum</big>\n\n<b>staticulum, ī</b> <i>m</i> [statua]\nстатуэтка, фигурка <i>PM</i>; идол, кумир <i>Tert</i>."],
  ["staticulus", "<big>staticulus</big>\n\n<b>staticulus, ī</b> <i>m</i> [<i>demin. к</i> status II, 2]\nстатикул, <i>род плавного, медлительного танца</i> (staticulum saltare <i>или</i> facere <i>Pl</i>)."],
  ["Statieillas", "<big>Statieillas</big>\n\n<b>Statieillās, ātis</b> <i>v. l.</i>\n= Statellas."],
  ["Statiellenses", "<big>Statiellenses</big>\n\n<b>Statiellēnsēs</b> <i>v. l.</i>\n= Statellates."],
  ["Statielli", "<big>Statielli</big>\n\n<b>Stat(i)ellī, ōrum</b> <i>m</i>\nстателлы, <i>племя в Лигурии</i> <i>PM, L</i>."],
  ["Statilius", "<big>Statilius</big>\n\n<b>Statīlius, a, um</b>\nСтатилий, <i>римск. nomen; наиболее известны</i>:\n<b>1)</b> L. S., <i>соучастник в заговоре Катилины.</i> <i>C, Sl</i>;\n<b>2)</b> T. S. Taurus, <i>полководец Октавиана, в 26 г. до н. э. консул</i> (<i>вместе с Августом</i>) <i>VP, T</i>."],
  ["statim", "<big>statim</big>\n\n<b>statim</b> [sto]\n<b>1)</b> твёрдо, стойко, непреклонно (rem gerere <i>Pl</i>); неподвижно, незыблемо (stant signa <i>Pl</i>);\n<b>2)</b> постоянно, регулярно (aliquid facere <i>PM, Ap</i>): non s. deterius est, quod diversum est <i>T</i> отличающееся (от привычного) не всегда является худшим;\n<b>3)</b> тотчас, немедленно: s. scietis <i>Pt</i> скоро узнаете; s. ut (ac, atque, quum, quam) <i>C etc.</i> как только; s. a prima luce <i>Col</i> с самого же рассвета."],
  ["statio", "<big>statio</big>\n\n<b>statio, ōnis</b> <i>f</i> [sto]\n<b>1)</b> стояние: manere in statione <i>Lcr</i> стоять неподвижно, не двигаться; stationem facere <i>Vtr</i> остановиться;\n<b>2)</b> твёрдая позиция, боевое положение (in statione manus parare <i>O</i>);\n<b>3)</b> положение, состояние (permutata rerum statione <i>Pt</i>);\n<b>4)</b> место, местопребывание: qua positus fueris in statione, mane <i>O</i> в каком месте ты окажешься (помещённым), там и оставайся;\n<b>5)</b> надлежащее место, порядок (comas in statione ponere <i>O</i>);\n<b>6)</b> <i>воен.</i> пост, караул, стража, пикет (custodiae stationesque <i>L</i>): stationem agere <i>L, T</i> <i>или</i> in statione esse <i>Cs</i> (manere <i>O</i>), <i>тж.</i> stationis jacere <i>Pt</i> быть в карауле, стоять на часах, — <i>перен.</i> быть бдительным, смотреть в оба; villam statione circumdare <i>T</i> расставить вокруг дома караулы; de praesidio et statione vitae decedere <i>C</i> уйти с жизненного поста; statione peracta <i>Lcn</i> с окончанием караульной смены, <i>перен.</i> когда окончится земное существование;\n<b>7)</b> отряд в карауле, стража (s. equitum <i>Cs</i>);\n<b>8)</b> <i>воен.</i> место расположения, квартиры (stationes hibernae <i>Amm</i>); стоянка кораблей (s. male fida carinis <i>V</i>); якорная стоянка, бухта, рейд (stationes classibus occupare <i>L</i>);\n<b>9)</b> государственный пост, положение, звание, сан (s. imperatoria <i>Lampr</i>; stationi paternae succedere <i>VP</i>);\n<b>10)</b> общественное место, место сборищ (in stationibus sedere tempusque conterere <i>PJ</i>);\n<b>11)</b> установление, установленное, обычный порядок (s. seu consuetudo <i>Vtr</i>);\n<b>12)</b> почтовая станция <i>CTh</i>;\n<b>13)</b> податной пункт <i>CJ</i>."],
  ["stationalis", "<big>stationalis</big>\n\n<b>statiōnālis, e</b> [statio]\nстоящий на месте, неподвижный (stella <i>PM</i>)."],
  ["stationarius", "<big>stationarius</big>\n\n<b>I statiōnārius, a, um</b>\n<b>1)</b> караульный, сторожевой <i>или</i> гарнизонный (milites <i>Dig, Amm</i>);\n<b>2)</b> неподвижный (sidera <i>Is</i>).\n\n<b>II statiōnārius, ī</b> <i>m</i> [statio 7, 12, 13]\n<b>1)</b> солдат пограничной стражи <i>Dig, Amm, Aug</i>;\n<b>2)</b> почтальон, курьер <i>CTh</i>;\n<b>3)</b> служащий податного пункта <i>CJ</i>."],
  ["Statius", "<big>Statius</big>\n\n<b>Statius, a, um</b>\nСтатий, <i>римск. nomen; наиболее известны</i>:\n<b>1)</b> S. Albius Oppianicus — <i>см.</i> Cluentius;\n<b>2)</b> L. S. Murcus, <i>легат Цезаря, впоследствии соучастник заговора против него; в 41 г. до н. э. убит Помпеем Младшим</i> <i>C, VP</i>;\n<b>3)</b> C. Caecilius S. — <i>см.</i> Caecilius;\n<b>4)</b> P. Papinius S., <i>поэт времён Домициана, автор поэм «Silvae», «Thebais» и «Achilleis».</i>"],
  ["stativa", "<big>stativa</big>\n\n<b>I statīva, ōrum</b> <i>n</i> [stativus]\n(<i>sc.</i> castra)\n<b>1)</b> военный стан, лагерь (s. ponere <i>L</i>);\n<b>2)</b> остановка в пути, привал <i>PM</i>.\n\n<b>II statīva, ae</b> <i>f</i> <i>Veg, Lampr</i>\n= stativa I, 1."],
  ["stativus", "<big>stativus</big>\n\n<b>statīvus, a, um</b> [sto]\n<b>1)</b> стоячий, неподвижный (aquae <i>Vr</i>); постоянный (castra <i>C, Cs</i>);\n<b>2)</b> выставленный (praesidium <i>C, L</i>);\n<b>3)</b> установленный (feriae <i>Macr</i>)."],
  ["stator", "<big>stator</big>\n\n<b>I stator, oris</b> <i>m</i> [sto]\nординарец <i>или</i> слуга римского чиновника в провинции <i>C, Dig</i>.\n\n<b>II stator, oris</b> <i>m</i> [sisto]\n<b>1)</b> останавливающий отступающих (Juppiter S. <i>C, L, O</i>);\n<b>2)</b> хранитель (hujus urbis <i>C</i>)."],
  ["statua", "<big>statua</big>\n\n<b>statua, ae</b> <i>f</i> [statuo]\n<b>1)</b> статуя (simulacra deorum et statuae hominum <i>C</i>);\n<b>2)</b> колонна <i>Q, Vop</i>."],
  ["statuaria", "<big>statuaria</big>\n\n<b>statuaria, ae</b> <i>f</i> (<i>sc.</i> ars)\nискусство ваяния статуй, статуарная скульптура <i>PM</i>."],
  ["statuarius", "<big>statuarius</big>\n\n<b>I statuarius, a, um</b> [statua]\nстатуарный (ars <i>PM</i>).\n\n<b>II statuarius, i</b> <i>m</i>\nсоздатель статуй, ваятель, скульптор <i>Sen, PM, Q, Vtr</i>."],
  ["statuliber", "<big>statuliber</big>\n\n<b>statu–liber, i</b> <i>m</i> <i>и</i> <b>statulibera, ae</b> <i>f</i>\nраб <i>или</i> рабыня, которые, согласно завещанию, должны были получить вольную <i>Dig</i>."],
  ["statumen", "<big>statumen</big>\n\n<b>statumen, inis</b> <i>n</i> [statuo]\nподпора, устой <i>Col, PM, Vtr</i>: statumina navis <i>Cs</i> рёбра (каркас, остов) корабля."],
  ["statuminatio", "<big>statuminatio</big>\n\n<b>statuminatio, onis</b> <i>f</i> [statumen]\nоснование, основа <i>Vtr</i>."],
  ["statumino", "<big>statumino</big>\n\n<b>statumino, —, —, are</b> [statumen]\nподпирать (vineam <i>PM</i>): s. saxo <i>Vtr</i> класть каменный фундамент."],
  ["statunculum", "<big>statunculum</big>\n\n<b>statunculum, i</b> <i>n</i>\nстатуэтка, фигурка <i>Pt</i>."],
  ["statuo", "<big>statuo</big>\n\n<b>statuo, tui, tutum, ere</b> [<i>от</i> statum, <i>supin. к</i> sisto]\n<b>1)</b> ставить (crateras <i>V</i>; aliquem in medio <i>L</i>);\n<b>2)</b> останавливать, ставить на якорь (navem <i>Pl</i>);\n<b>3)</b> сажать (arborem agro <i>H</i>); устанавливать (machinas <i>Pl</i>; terminos alicui <i>Sen</i>); воздвигать, возводить (moenia <i>V</i>): media statuaris in urbe <i>O</i> (<i>ирон.</i>) тебе будет поставлен памятник в центре города || сооружать (aram alicui <i>Pl, O</i>); водружать (signifer, statue signum! <i>L</i>); основывать (urbem <i>V</i>; regnum <i>C</i>);\n<b>4)</b> приводить (aliquem ante oculos <i>C</i>): s. exemplum in aliquem (in aliquo) <i>C</i> показывать (ставить в) пример кому-л.;\n<b>5)</b> выставлять, формулировать (condiciones <i>C</i>); устанавливать, определять (tempus colloquio <i>L</i>; improbis poena statuenda est <i>C</i>): statuto (<i>v. l.</i> stato) tempore <i>QC</i> в определённое время || назначать, ставить (aliquem regem <i>QC</i>);\n<b>6)</b> выносить определение, постановлять, решать (s. exsilium in reum <i>T</i>; s. res privatas <i>O</i>; s. de capite alicujus <i>C</i>): de se s. <i>T</i> лишать себя жизни;\n<b>7)</b> принимать решение, решаться (s. apud animum suum <i>L</i> <i>или</i> cum animo suo <i>C</i>);\n<b>8)</b> считать, полагать (aliquem hostem <i>C</i>; sic statuo et judico <i>C</i>)."],
  ["statura", "<big>statura</big>\n\n<b>statura, ae</b> <i>f</i> [sto]\nрост, величина (homo staturae procerae <i>Su</i>; pampini <i>Col</i>)."],
  ["staturosus", "<big>staturosus</big>\n\n<b>staturosus, a, um</b>\nогромный, исполинский (gigantes <i>Aug</i>)."],
  ["status", "<big>status</big>\n\n<b>I status, a, um</b>\n<b>1.</b> <i>part. pf. к</i> sisto;\n<b>2.</b> <i>adj.</i> установленный, назначенный, определённый (dies <i>L</i>; tempus <i>PM</i>; caerimoniae <i>C</i>); постоянный, неизменный (numinum vagantium ambitus <i>Ap</i>).\n\n<b>II status, us</b> <i>m</i> [sto]\n<b>1)</b> стояние (s. erectus <i>C</i>);\n<b>2)</b> поза (minax <i>H</i>; signi <i>C</i>): motus et s. <i>C</i> манеры и осанка;\n<b>3)</b> рост, вышина (canis <i>Col</i>; plantae <i>Pall</i>);\n<b>4)</b> позиция: hostem statu movere <i>L</i> (e statu dimovere <i>C</i>) выбить противника из позиции; aliquem de statu movere (dejicere) <i>C</i> привести кого-л. в замешательство;\n<b>5)</b> (<i>тж.</i> s. rerum <i>T</i>) состояние, положение (caeli <i>Col, L</i>; civitatis <i>C</i>); <i>pl.</i> обстоятельства (vitae <i>C</i>): res hoc statu est <i>L</i> дело обстоит так;\n<b>6)</b> обеспеченность, благосостояние (nullum statum habere <i>VP</i>);\n<b>7)</b> гражданское состояние, сословие, звание, общественная ступень (de statu declinare <i>C</i>);\n<b>8)</b> <i>юр.</i> <i>и</i> <i>ритор.</i>: s. <i>или</i> s. causae <i>C, Q</i> положение дела, состояние вопроса;\n<b>9)</b> <i>грам.</i> глагольная форма <i>Q</i>;\n<b>10)</b> (<i>тж.</i> legitimus s. <i>Dig</i>) совершеннолетие (мужчины), <i>т. е.</i> 25-летний возраст (ad statum suum pervenire <i>Dig</i>)."],
  ["statutio", "<big>statutio</big>\n\n<b>statutio, onis</b> <i>f</i> [statuo]\nустанавливание, установка (tigni <i>Vtr</i>)."],
  ["statutum", "<big>statutum</big>\n\n<b>statutum, i</b> <i>n</i> [statuo]\nпостановление, определение (legibus et statutis parere <i>Lact</i>)."],
  ["statutus", "<big>statutus</big>\n\n<b>statutus, a, um</b>\nрослый <i>Pl</i>."],
  ["ste", "<big>ste</big>\n\n<b>ste</b> <i>Pl ap. AG, Caec</i>\n= <i>усеч.</i> iste."],
  ["steatoma", "<big>steatoma</big>\n\n<b>steatoma, atis</b> <i>n</i> (<i>греч.</i>)\nжировая опухоль, жировик <i>CC, PM, Veg</i>."],
  ["stega", "<big>stega</big>\n\n<b>stega, ae</b> <i>f</i> (<i>греч.</i>)\nпалуба <i>Pl</i>."],
  ["stegnus", "<big>stegnus</big>\n\n<b>stegnus, a, um</b> (<i>греч.</i>)\nплотный, <i>перен.</i> сжимающий потовые поры, <i>т. е.</i> сухой, без испарины (febres constrictae, quas stegnas vocant <i>PM</i>)."],
  ["stela", "<big>stela</big>\n\n<b>stela, ae</b> <i>f</i> (<i>греч.</i>)\nстолб, столп (stelae lapideae <i>PM</i>); надгробная плита, стела <i>Pt</i>."],
  ["stelio", "<big>stelio</big>\n\n<b>stelio</b> <i>Pt</i>\n= stellio."],
  ["stella", "<big>stella</big>\n\n<b>I stella, ae</b> <i>f</i>\n<b>1)</b> звезда: s. <i>или</i> s. errans <i>C</i> (erratica <i>Vr, Sen</i>) планета (s. Saturni <i>C</i>); s. diurna <i>Pl</i> утренняя звезда; s. comans <i>O</i> <i>или</i> crinita <i>C</i> комета; stellae fulgentes <i>Lcr</i> молнии;\n<b>2)</b> созвездие (s. Leonis <i>H</i>);\n<b>3)</b> солнце (s. serena <i>O</i>);\n<b>4)</b> светлячок <i>PM</i>;\n<b>5)</b> зрачок <i>Cld</i>;\n<b>6)</b> (<i>тж.</i> s. marina <i>Veg</i>) морская звезда <i>PM</i>.\n\n<b>II stella, ae</b> <i>f</i> <i>v. l.</i>\n= stela.\n\n<b>III Stella, ae</b> <i>m</i>\n(L. Arruntius) Стелла, <i>римск. поэт времён Траяна</i> <i>M</i>."],
  ["stellans", "<big>stellans</big>\n\n<b>stellans, antis</b> <i>adj.</i> [stella]\n<b>1)</b> усеянный звёздами, звёздный (nox <i>Poeta ap. C</i>; caelum <i>Lcr</i>);\n<b>2)</b> сверкающий, блистающий (gemma <i>O</i>): frons s. <i>M</i> лоб в язвах."],
  ["stellaris", "<big>stellaris</big>\n\n<b>I stellaris, e</b> [stella]\nзвёздный (essentia <i>Macr</i>).\n\n<b>II stellaris, is</b> <i>f</i> (<i>sc.</i> ars)\nастрономия <i>Tert</i>."],
  ["Stellatis ager", "<big>Stellatis ager</big>\n\n<b>Stellatis ager</b> (<i>или</i> <b>campus</b>)\n<i>плодородная область в Кампании, к сев. от Капуи</i> <i>C, L, Su</i>."],
  ["stellatura", "<big>stellatura</big>\n\n<b>stellatura, ae</b> <i>f</i> [stellio 2]\nпротивозаконное удержание части солдатского пайка в пользу военных интендантов <i>Lampr, Spart, CTh</i>."],
  ["stellatus", "<big>stellatus</big>\n\n<b>stellatus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> stello I;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> усеянный звёздами (ora Tauri <i>O</i>); звездчатый (salamandra animal stellatum <i>PM</i>);\n<b>2)</b> блистающий, сверкающий (ensis s. iaspide fulva <i>V</i>);\n<b>3)</b> многоокий (Argus <i>O</i>)."],
  ["stellifer", "<big>stellifer</big>\n\n<b>stellifer, fera, ferum</b> [stella + fero]\nзвездоносный, усеянный звёздами (caeli cursus <i>C</i>; polus <i>SenT</i>)."],
  ["stelliger", "<big>stelliger</big>\n\n<b>stelliger, gera, gerum</b> [stella + gero] <i>Vr, C, St etc.</i>\n= stellifer."],
  ["stellimicans", "<big>stellimicans</big>\n\n<b>stelli–micans, antis</b>\nсверкающий звёздами (signa <i>Vr</i>)."],
  ["stellio", "<big>stellio</big>\n\n<b>stellio, onis</b> <i>m</i>\n<b>1)</b> стеллион, звездчатая ящерица (<i>Stellio vulgaris, по др. Lacerta gecko</i>, L.) <i>V, PM, O</i>;\n<b>2)</b> хитрец, пройдоха <i>Ap</i>."],
  ["stellionatus", "<big>stellionatus</big>\n\n<b>stellionatus, us</b> <i>m</i> [stellio 2]\nмошенничество <i>Dig</i>."],
  ["stello", "<big>stello</big>\n\n<b>I stello, —, —, are</b>\nусеивать звёздами <i>PM, Symm</i>. — <i>См. тж.</i> stellans <i>и</i> stellatus.\n\n<b>II stello, onis</b> <i>m</i> <i>v. l.</i>\n= stellio."],
  ["stellula", "<big>stellula</big>\n\n<b>stellula, ae</b> <i>f</i> [<i>demin. к</i> stella 1]\nзвёздочка, астериск <i>Hier</i>."],
  ["stemma", "<big>stemma</big>\n\n<b>stemma, atis</b> <i>n</i> (<i>греч.</i>)\n<b>1)</b> гирлянда, венок (<i>преим. для увенчания изображений предков</i>) <i>Sen, PM etc.</i>;\n<b>2)</b> родословное дерево, родословие <i>Sen, St, Su, J</i>; древность (argenti sui s. narrare <i>M</i>)."],
  ["Stentor", "<big>Stentor</big>\n\n<b>Stentor, oris</b> <i>m</i>\nСтентор, <i>глашатай греков в Троянской войне, голос которого равнялся по силе голосу пятидесяти человек</i> <i>J</i>."],
  ["Stephaniscidium", "<big>Stephaniscidium</big>\n\n<b>Stephaniscidium, i</b> <i>f</i> <i>Pl demin. к</i>\nStephanium."],
  ["Stephanium", "<big>Stephanium</big>\n\n<b>Stephanium, i</b> <i>f</i>\nСтефания, <i>имя служанки</i> <i>Pl</i>."],
  ["sterceja", "<big>sterceja</big>\n\n<b>sterceja, ae</b> <i>f</i> [stercus]\nнянька <i>Tert</i>."],
  ["stercer-", "<big>stercer-</big>\n\n<b>stercer-</b> <i>v. l.</i>\n= stercor-."],
  ["stercilinum", "<big>stercilinum</big>\n\n<b>stercilinum, i</b> <i>n</i> <i>Pl, Cato</i>\n= sterquilinium."],
  ["stercorarius", "<big>stercorarius</big>\n\n<b>stercorarius, a, um</b> [stercus]\nнавозный (crates <i>Vr</i>)."],
  ["stercoratio", "<big>stercoratio</big>\n\n<b>stercoratio, onis</b> <i>f</i> [stercoro]\nунавоживание <i>Vr, Col, PM</i>."],
  ["stercoratum", "<big>stercoratum</big>\n\n<b>stercoratum, i</b> <i>n</i>\nунавоженная почва <i>PM</i>."],
  ["stercoratus", "<big>stercoratus</big>\n\n<b>stercoratus, a, um</b>\nунавоженный (locus <i>Col</i>)."],
  ["stercoreus", "<big>stercoreus</big>\n\n<b>stercoreus, a, um</b> [stercus]\nгрязный, гадкий (miles <i>Pl</i>)."],
  ["stercoris", "<big>stercoris</big>\n\n<b>stercoris</b> <i>gen. к</i>\nstercus."],
  ["stercoro", "<big>stercoro</big>\n\n<b>stercoro, avi, atum, are</b> [stercus]\n<b>1)</b> удобрять навозом, унавоживать (agrum <i>C</i>);\n<b>2)</b> очищать от навоза (latrinas <i>Dig</i>): stercorata colluvies <i>Col</i> навозная куча."],
  ["stercorosus", "<big>stercorosus</big>\n\n<b>stercorosus, a, um</b> [stercus]\n<b>1)</b> унавоженный (locus, herbae <i>Col</i>);\n<b>2)</b> грязный (aqua <i>Col</i>)."],
  ["sterculinium", "<big>sterculinium</big>\n\n<b>sterculinium (sterculinum), i</b> <i>n</i> <i>Pl, Ter, Cato</i>\n= sterquilinium."],
  ["stercus", "<big>stercus</big>\n\n<b>stercus, oris</b> <i>n</i>\n<b>1)</b> навоз (novalia stercore satiare <i>Col</i>); помёт, кал (equinum <i>Vr</i>; columbinum <i>PM</i>);\n<b>2)</b> шлак, нагар (ferri <i>Scr</i>)."],
  ["stereobates", "<big>stereobates</big>\n\n<b>stereobates, ae</b> <i>m</i> (<i>греч.</i>)\nпостамент, цоколь <i>Vtr</i>."],
  ["stericula", "<big>stericula</big>\n\n<b>stericula, ae</b> <i>f</i> <i>v. l.</i>\n= sterilicula."],
  ["sterilesco", "<big>sterilesco</big>\n\n<b>sterilesco, —, —, ere</b> [sterilis]\n<b>1)</b> становиться бесплодным (caprae sterilescunt pinguitudine <i>PM</i>);\n<b>2)</b> высыхать, лишаться молока (mamma sterilescit <i>PM</i>)."],
  ["sterilicula", "<big>sterilicula</big>\n\n<b>sterilicula, ae</b> <i>f</i> [sterilis]\nматка ещё не рожавшей свиньи <i>Pt</i> (<i>v. l.</i>)."],
  ["sterilis", "<big>sterilis</big>\n\n<b>sterilis, e</b>\n<b>1)</b> неплодородный (ager <i>V</i>); не приносящий плодов (platanus, avena <i>V</i>); бесплодный (uxor <i>Sen, J</i>; vacca <i>V</i>): vir s. <i>Ctl</i> евнух;\n<b>2)</b> пустой (manus <i>Pl</i>); напрасный, тщетный, бесполезный, неприбыльный (labor <i>M</i>); неурожайный (annus <i>Sen</i>); не приносящий дохода (nummi, pecunia <i>Dig</i>); лишённый (aliqua re, ab aliqua re <i>и</i> alicujus rei <i>Lcr, Pers, PM etc.</i>); безлюдный, пустынный (prospectus <i>Pl</i>): s. ab aquis <i>Ap</i> безводный; s. sonitu <i>Lcr</i> беззвучный; s. amor <i>O</i> любовь без взаимности; s. epistula <i>PJ</i> письмо без подношений;\n<b>3)</b> делающий бесплодным, лишающий плодородия (robigo <i>H</i>; hiems <i>M</i>)."],
  ["sterilitas", "<big>sterilitas</big>\n\n<b>sterilitas, atis</b> <i>f</i> [sterilis]\nнеплодородие (agrorum <i>C</i>); бесплодие (mulierum <i>PM</i>); скудость, бедность (fortunae <i>PM</i>): s. caelestis <i>PM</i> неблагоприятные для урожая условия погоды."],
  ["sterilus", "<big>sterilus</big>\n\n<b>sterilus, a, um</b> <i>Lcr</i>\n= sterilis."],
  ["sternax", "<big>sternax</big>\n\n<b>sternax, acis</b> <i>adj.</i> [sterno]\n<b>1)</b> сбрасывающий на землю (всадника), норовистый (equus <i>V, Sil</i>);\n<b>2)</b> падающий ниц, умоляющий на коленях (cives <i>Sid</i>)."],
  ["sterno", "<big>sterno</big>\n\n<b>sterno, stravi, stratum, ere</b>\n<b>1)</b> стлать, расстилать, распростирать (vestem <i>O</i>); раскладывать, раскидывать, разбрасывать (corpora passim <i>L</i>); рассыпать (arenam <i>O</i>); настилать (pontes super asseres <i>QC</i>): s. corpus <i>L</i>, se s. <i>или</i> sterni <i>V etc.</i> растянуться (всем телом), ложиться; stratus humi <i>J</i> стелющийся по земле <i>или</i> <i>L, O etc.</i> поверженный на землю, лежащий (распростёртый) на земле; membra (<i>acc. gr.</i>) stratus <i>H</i> растянувшись (всем) телом; somno se s. <i>V</i> <i>или</i> sterni <i>L</i> ложиться спать; sterni gladiis <i>Lcn</i> пасть под ударами мечей; stratus ad pedes alicui <i>C</i> припавший к чьим-л. ногам;\n<b>2)</b> устилать, усыпать (nemus foliis <i>H</i>); усаживать, отделывать, украшать (triclinium argento <i>Eutr</i>);\n<b>3)</b> покрывать, застилать (lectulos pelliculis <i>C</i>); перекрывать (naves pontibus <i>T</i>); приготовлять, стлать (lecti strati <i>Pt</i>); покрывать попоной, седлать (equi strati <i>L</i>);\n<b>4)</b> мостить (emporium lapide <i>L</i>): viam per mare s. <i>Lcr</i> проложить морской путь;\n<b>5)</b> делать ровным, выравнивать (iter alicui <i>Sil</i>);\n<b>6)</b> валить, срубать (silvas dolabris <i>Col</i>); рушить, разрушать (muros ariete <i>L</i>; Trojam a culmine <i>V</i>);\n<b>7)</b> (<i>тж.</i> s. caede, morte <i>или</i> leto <i>V</i>) повергать на землю, бить, убивать (aliquem jaculo <i>Lcn</i>; pecus ferro <i>H</i>; sterni vulnere <i>V</i>);\n<b>8)</b> поражать, повергать в уныние (corda <i>V</i>; afflicti et strati <i>C</i>); губить (aliquem exitio gravi <i>H</i>);\n<b>9)</b> унимать (ventos <i>H</i>); успокаивать (aequora <i>V</i>; odia militum <i>T</i>)."],
  ["sternumentum", "<big>sternumentum</big>\n\n<b>sternumentum, i</b> <i>n</i> [sternuo]\n<b>1)</b> чихание <i>C, CC, PM</i>;\n<b>2)</b> чихательный порошок <i>CC, PM</i>."],
  ["sternuo", "<big>sternuo</big>\n\n<b>sternuo, nui, —, ere</b>\n<b>1)</b> чихать <i>Col, PM</i>: dum tu sternuas <i>погов. Pl</i> не успеешь чихнуть, <i>т. е.</i> в одно мгновение;\n<b>2)</b> (<i>об огне</i>) издавать треск, трещать <i>или</i> вспыхивать (lumen sternuit <i>O</i>);\n<b>3)</b> возвещать чиханием (omen alicui <i>Prp</i>)."],
  ["sternutamentum", "<big>sternutamentum</big>\n\n<b>sternutamentum, i</b> <i>n</i> [sternuto]\nчихание <i>Sen, Scr, CA</i>."],
  ["sternutatio", "<big>sternutatio</big>\n\n<b>sternutatio, onis</b> <i>f</i>\nчихание <i>Scr, Ap, CA</i>."],
  ["sternuto", "<big>sternuto</big>\n\n<b>sternuto, avi, —, are</b> [<i>frequ. к</i> sternuo]\nчихать <i>Pt, Macr</i>."],
  ["Sterope", "<big>Sterope</big>\n\n<b>Sterope, es</b> <i>f</i>\nСтеропа, <i>дочь Атланта, возлюбленная Марса, превращенная в одну из Плеяд</i> <i>O</i>."],
  ["Steropes", "<big>Steropes</big>\n\n<b>Steropes, ae</b> <i>m</i>\nСтероп, <i>один из циклопов</i> <i>V, O</i>."],
  ["sterquilinium", "<big>sterquilinium</big>\n\n<b>sterquilinium (-num), i</b> <i>n</i> [stercus]\nнавозная куча, свалка нечистот <i>Cato, Vr, Ph</i>: gallus in suo sterquilino plurimum potest <i>погов. Sen</i> петух на своей навозной куче всемогущ."],
  ["sterteja", "<big>sterteja</big>\n\n<b>sterteja, ae</b> <i>f</i> [sterto]\nхрапунья <i>Pt</i>."],
  ["Stertinius", "<big>Stertinius</big>\n\n<b>Stertinius, a, um</b>\nСтертиний, <i>римск. nomen; наиболее известны</i>:\n<b>1)</b> L. S., <i>претор в Испании в 199-196 гг. до н. э.</i> <i>L</i>;\n<b>2)</b> L. S., <i>римск. полководец в Германии в 15-16 гг. н. э.</i> <i>T</i>;\n<b>3)</b> <i>философ-стоик в Риме времён Августа</i> (<i>Гораций называет его sapientum octavus</i>)."],
  ["sterto", "<big>sterto</big>\n\n<b>sterto, —, —, ere</b>\nхрапеть (totam noctem <i>Pl</i>): vigilanti s. naso <i>J</i> = притворяться спящим."],
  ["Stesichorus", "<big>Stesichorus</big>\n\n<b>Stesichorus, i</b> <i>m</i>\nСтесихор, <i>лирический поэт, родом из Гимеры (Сицилия) (632-556 гг. до н. э.</i>) <i>C, H, Q</i>."],
  ["steti", "<big>steti</big>\n\n<b>steti</b> <i>pf. к</i>\nsto <i>и</i> sisto."],
  ["Stheneboea", "<big>Stheneboea</big>\n\n<b>Stheneboea, ae</b> <i>f</i>\nСфенебея, <i>дочь ликийского царя Иобата, жена Прэта, влюбившаяся в своего пасынка Беллерофонта</i> <i>J</i>."],
  ["Stheneboeius", "<big>Stheneboeius</big>\n\n<b>Stheneboeius, a, um</b>\nсфенебеин: S. heros <i>Sid</i> = Bellerophon."],
  ["Stheneleis", "<big>Stheneleis</big>\n\n<b>Stheneleis, idis</b> <i>f</i> [Sthenelus]\nсфенелова: S. volucris <i>O</i> = Cycnus."],
  ["Stheneleius", "<big>Stheneleius</big>\n\n<b>Stheneleius, a, um</b> [Sthenelus]\nсфенелов: S. hostis <i>O</i> = Eurystheus."],
  ["Sthenelus", "<big>Sthenelus</big>\n\n<b>Sthenelus, i</b> <i>m</i>\nСфенел:\n<b>1)</b> <i>сын Капанея, один из «эпигонов» «семерых против Фив», возница Диомеда при осаде Трои</i> <i>V, H</i>;\n<b>2)</b> <i>царь Лигурии, отец Кикна, превращенного в лебедя</i> <i>O</i>;\n<b>3)</b> <i>царь Аргоса и Микен, сын Персея и Андромеды, отец Эврисфея</i> <i>O</i>;\n<b>4)</b> <i>рутул, убитый Палладой</i> <i>V</i>."],
  ["stibadium", "<big>stibadium</big>\n\n<b>stibadium, i</b> <i>m</i> (<i>греч.</i>)\nкресло, диван <i>или</i> кушетка (<i>полукруглой формы</i>) <i>PM, M, Sid</i>."],
  ["stibi", "<big>stibi</big>\n\n<b>stibi, is</b> <i>m</i> (<i>греч.</i>)\nсурьма <i>CC, PM</i>."],
  ["stibinus", "<big>stibinus</big>\n\n<b>stibinus, a, um</b> [stibi]\nсурьмяный (lapides <i>Vlg</i>)."],
  ["stibium", "<big>stibium</big>\n\n<b>stibium, i</b> <i>m</i> <i>Scr, Vlg, Eccl</i>\n= stibi."],
  ["Stichus", "<big>Stichus</big>\n\n<b>Stichus, i</b> <i>m</i>\nСтих, <i>имя раба и название комедии Плавта.</i>"],
  ["sticula", "<big>sticula</big>\n\n<b>sticula, ae</b> <i>f</i>\n<i>сорт винограда</i> <i>Col</i>."],
  ["stigma", "<big>stigma</big>\n\n<b>I stigma, atis</b> <i>m</i> (<i>греч.</i>)\n<b>1)</b> выжженный на теле знак, клеймо <i>Sen, Q, Su, PM</i>;\n<b>2)</b> позор, бесчестье <i>Su, PM</i>;\n<b>3)</b> парез, царапина (<i>сделанная во время бритья</i>) <i>M</i>.\n\n<b>II stigma, ae</b> <i>f</i> <i>Pt</i>\n= stigma I."],
  ["stigmatias", "<big>stigmatias</big>\n\n<b>stigmatias, ae</b> <i>m</i> [stigma]\nимеющий клеймо, клеймёный (<i>о рабе</i>) <i>C</i>."],
  ["stigmo", "<big>stigmo</big>\n\n<b>stigmo, avi, —, are</b> [stigma]\nклеймить (aliquem <i>Eccl</i>)."],
  ["stigmosus", "<big>stigmosus</big>\n\n<b>stigmosus, a, um</b> [stigma]\nпокрытый клеймами, клеймёный <i>PJ, Pt</i>."],
  ["stilasco", "<big>stilasco</big>\n\n<b>stilasco, stilavi, —, ere</b> [stilus 3]\nдавать стебли <i>Col</i>."],
  ["Stilbon", "<big>Stilbon</big>\n\n<b>Stilbon, ontis</b> <i>m</i> (<i>греч.</i> «сверкающий»)\nСтильбон, <i>т. е.</i> планета Меркурий <i>Aus</i>."],
  ["Stilicho", "<big>Stilicho</big>\n\n<b>Stilicho, onis</b> <i>m</i>\nСтилихон, <i>родом вандал, тесть и полководец императора Гонория и по приказу последнего казнённый в 408 г. н. э.</i> <i>Cld</i>."],
  ["Stilichonius", "<big>Stilichonius</big>\n\n<b>Stilichonius, a, um</b>\nстилихониев: Stilichonia virgo <i>Cld</i> = Maria, <i>дочь Стилихона, жена Гонория.</i>"],
  ["stilicidium", "<big>stilicidium</big>\n\n<b>stilicidium, i</b> <i>n</i> <i>Lcr</i>\n= stillicidium."],
  ["stilla", "<big>stilla</big>\n\n<b>stilla, ae</b> <i>f</i> [<i>demin. к</i> stiria]\n<b>1)</b> капля (cruoris <i>Sen</i>);\n<b>2)</b> немножко, чуть-чуть (olei <i>Ap, M</i>; pauculae temporum stillae <i>Aug</i>)."],
  ["stillanter", "<big>stillanter</big>\n\n<b>stillanter</b> <i>Ambr</i>\n= stillatim."],
  ["stillarium", "<big>stillarium</big>\n\n<b>stillarium, i</b> <i>m</i> [stilla]\nмаленькое добавление, мелочь <i>Sen</i>."],
  ["stillaticius", "<big>stillaticius</big>\n\n<b>stillaticius, a, um</b>\nструящийся по каплям, капающий (resina <i>PM</i>)."],
  ["stillatim", "<big>stillatim</big>\n\n<b>stillatim</b> [stilio]\nпо каплям (cadere <i>Vr</i>)."],
  ["stillatio", "<big>stillatio</big>\n\n<b>stillatio, onis</b> <i>f</i> [stilla]\nстекание по каплям (pluviae <i>Hier</i>).:"],
  ["stillicidium", "<big>stillicidium</big>\n\n<b>stillicidium, i</b> <i>m</i> [stilla + cado]\n<b>1)</b> капание (mellis <i>Tert</i>); капающая жидкость (s. arborum <i>PM</i>): per stillicidia <i>Sen</i> капля по капле;\n<b>2)</b> кровельный жёлоб <i>C, Vtr, Dig</i>."],
  ["stillo", "<big>stillo</big>\n\n<b>stillo, avi, itum, are</b> [stilla]\n<b>1)</b> капать, струиться по Каплям, сочиться (mella stillabant de ilice <i>O</i>);\n<b>2)</b> выделять по каплям, струить каплями, источать (rorem ex oculis <i>H</i>)."],
  ["stilo", "<big>stilo</big>\n\n<b>stilo, avi, —, are</b> <i>v. l.</i>\n= stilasco."],
  ["Stilpon", "<big>Stilpon</big>\n\n<b>Stilpo(n), onis</b> <i>m</i>\nСтильпон, <i>философ мегарской школы</i> <i>C, PM, Sen</i>."],
  ["stilus", "<big>stilus</big>\n\n<b>stilus, i</b> <i>m</i>\n<b>1)</b> <i>воен.</i> заострённый кол: stili caeci <i>bAfr, Sil, Amm</i> замаскированные железные острия (<i>разбрасывавшиеся для того, чтобы препятствовать движению неприятельской конницы</i>);\n<b>2)</b> остроконечная палочка для разных сельскохозяйственных работ <i>Col, Pall</i>;\n<b>3)</b> стебель, черенок <i>Col</i>;\n<b>4)</b> стиль (<i>палочка с одним острым концом для письма по воску и с другим тупым для стирания написанного</i>): stilum vertere <i>C, H</i> поворачивать стиль, <i>т. е.</i> стирать <i>или</i> исправлять написанное; stilis ulmeis aliquem conscribere <i>шутл. Pl</i> расписать кого-л. вязовыми грифелями, <i>т. е.</i> розгами;\n<b>5)</b> упражнение в письме, письмо, писание (s. est optimus dicendi effector <i>C</i>);\n<b>6)</b> склад речи, слог, стиль, способ изложения (s. paene Atticus <i>C</i>; s. totius orationis <i>C</i>): simili stilo <i>Ap</i> в сходной манере; s. exercitatus <i>C</i> опытное перо;\n<b>7)</b> мнение, суждение, высказывание в письменной форме: stili ad unum sermonem congruentes <i>Ap</i> поданные заключения (декурионов), совпадающие друг с другом по содержанию;\n<b>8)</b> письменность, литература (Asinius Pollionon minima pars Romani stili <i>VM</i>);\n<b>9)</b> речь, язык (Graecus s. <i>Hier</i>)."],
  ["stimmi", "<big>stimmi</big>\n\n<b>stimmi, is</b> <i>m</i> <i>PM</i>\n= stibi."],
  ["Stimula", "<big>Stimula</big>\n\n<b>Stimula, ae</b> <i>f</i> [stimulus]\nСтимула, <i>богиня, возбуждавшая вакханок к неистовству; отождествлялась с Семелой</i> <i>O, Aug</i>."],
  ["stimulatio", "<big>stimulatio</big>\n\n<b>stimulatio, onis</b> <i>f</i> [stimulo]\nпоощрение, побуждение, подстрекательство <i>PM, T</i>."],
  ["stimulator", "<big>stimulator</big>\n\n<b>stimulator, oris</b> <i>m</i> [stimulo]\nподстрекатель (perfidus <i>Cld</i>)."],
  ["stimulatrix", "<big>stimulatrix</big>\n\n<b>stimulatrix, icis</b> <i>f</i> [stimulo]\nподстрекательница <i>Pl</i>."],
  ["stimuleus", "<big>stimuleus</big>\n\n<b>stimuleus, a, um</b> [stimulus]\nсостоящий из ударов остроконечной палкой (supplicium <i>Pl</i>)."],
  ["stimulo", "<big>stimulo</big>\n\n<b>stimulo, avi, atum, are</b> [stimulus]\n<b>1)</b> колоть, пришпоривать (equos calcaribus <i>VM</i>); погонять, подстёгивать (verbere currus <i>Lcn</i>);\n<b>2)</b> беспокоить, тревожить, мучить (conscientia aliquem stimulat <i>C, QC, Ap</i>);\n<b>3)</b> возбуждать, подстрекать, поощрять (aliquem ad <i>или</i> in aliquid <i>C, L, T, Just</i>); побуждать, приказывать (festinare fugam <i>V</i>): stimulante fame <i>O</i> под влиянием голода || вызывать (sitim <i>PM</i>; jurgia vino stimulata <i>O</i>); разжигать (iram <i>O</i>); восстанавливать, натравливать (aliquem in aliquem <i>L</i>)."],
  ["stimulosus", "<big>stimulosus</big>\n\n<b>stimulosus, a, um</b> [stimulus]\nвозбуждающий (desiderium venereae voluptatis <i>CA</i>)."],
  ["stimulus", "<big>stimulus</big>\n\n<b>stimulus, i</b> <i>m</i> [<i>одного корня с</i> instigo]\n<b>1)</b> остроконечная палка, которой погоняли животных, стрекало (stimulo increpare boves <i>Tib</i>);\n<b>2)</b> <i>воен.</i> рожон, кол, <i>вбивавшийся в землю острым концом вверх для преграждения пути неприятельской коннице</i> (<i>род заострённой надолбы</i>) <i>Cs</i>: adversus stimulum <i>погов. Ter</i> против рожна;\n<b>3)</b> <i>перен.</i> жало, мучение, мука (doloris <i>C</i>): amoris stimuli <i>L</i> любовные терзания, ревность;\n<b>4)</b> стимул, побуждение, поощрение (aliquem stimulis gloriae concitare <i>C</i>)."],
  ["stinguo", "<big>stinguo</big>\n\n<b>I stinguo, —, —, ere</b>\nколоть (<i>глагол, сохранившийся лишь в приставочных формах</i>: distinguo, instinguo <i>и т. п.</i>).\n\n<b>II stinguo, —, —, ere</b>\nтушить, гасить (ardorem <i>Lcr</i>); <i>med.-pass.</i> stingui гаснуть, потухать <i>Lcr</i>."],
  ["stipatio", "<big>stipatio</big>\n\n<b>stipatio, onis</b> <i>f</i> [stipo]\n<b>1)</b> давка, толкотня (ingens s. <i>Ap</i>); стечение народа, толпа <i>Vr, PJ</i>;\n<b>2)</b> толпа провожатых <i>C</i>;\n<b>3)</b> множество (aggressionum et enthymematum <i>Q</i>)."],
  ["stipator", "<big>stipator</big>\n\n<b>stipator, oris</b> <i>m</i> [stipo]\nпостоянный провожатый <i>C, Sl</i>; телохранитель <i>Vr, C, H, Just</i>; неизменный спутник <i>C</i>."],
  ["stipatrix", "<big>stipatrix</big>\n\n<b>stipatrix, icis</b> <i>f</i>\nсопутствующая (turmae stipatrices <i>Ambr</i>)."],
  ["stipatus", "<big>stipatus</big>\n\n<b>stipatus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> stipo;\n<b>2.</b> <i>adj.</i> окружённый, сопутствуемый (ab omni ordine, sexu, aetate stipatissimus <i>Sid</i>)."],
  ["stipendialis", "<big>stipendialis</big>\n\n<b>stipendialis, e</b> [stipendium]\nсвязанный с уплатой дани (foedus <i>Sid</i>)."],
  ["stipendiarius", "<big>stipendiarius</big>\n\n<b>I stipendiarius, a, um</b> <i>adj.</i> [stipendium]\n<b>1)</b> состоящий на жалованье, служащий за плату, наёмный (miles <i>Veg</i>; cohors <i>bAfr</i>);\n<b>2)</b> обложенный данью, платящий дань (civitas <i>L</i>): Aeduos sibi stipendiarios facere <i>Cs</i> обложить эдуев данью;\n<b>3)</b> являющийся данью, назначенный в виде дани (vectigal <i>C</i>).\n\n<b>II stipendiarius, i</b> <i>m</i>\nплатящий дань, данник (socii stipendiariique <i>C</i>)."],
  ["stipendior", "<big>stipendior</big>\n\n<b>stipendior, atus sum, ari</b> <i>depon.</i> [stipendium]\nбыть оплачиваемым, состоять на жалованье, <i>т. е.</i> служить (alicui <i>PM</i>)."],
  ["stipendiosus", "<big>stipendiosus</big>\n\n<b>stipendiosus, a, um</b>\nдавно состоящий на службе (milites <i>Veg</i>)."],
  ["stipendium", "<big>stipendium</big>\n\n<b>stipendium, i</b> <i>m</i> [stips I + pendo]\n<b>1)</b> (<i>тж.</i> s. militare <i>L, PM</i>) солдатское жалованье, плата за военную службу (s. militibus dare, numerare <i>или</i> persolvere <i>C</i>): stipendia merere <i>C, L, Nep</i> (mereri <i>T</i>, facere <i>Sl, L</i>) нести военную службу, служить в армии;\n<b>2)</b> оклад, жалованье (s. de publico statuere alicui <i>L</i>);\n<b>3)</b> военная служба (initia stipendiorum meorum <i>VP</i>): homo nullius stipendii <i>Sl</i> человек, не служивший в армии; stipendia emereri <i>L, Sl</i> отслужить свои годы, отбыть срок военной службы;\n<b>4)</b> год военной службы, поход (plurimorum stipendiorum milites <i>L</i>);\n<b>5)</b> служба, дело <i>Sen</i>: in Asiam stipendio educi <i>Pt</i> быть направленным в Азию в служебном порядке || подвиг (duodecim stipendia Herculis <i>Just</i>);\n<b>6)</b> дань, контрибуция (imponere s. victis <i>Cs</i>);\n<b>7)</b> налог, взнос (stipendia pendere <i>Sl</i>);\n<b>8)</b> возмездие, кара (dira stipendia alicui ferre <i>Ctl</i>): s. aliquem manet <i>H</i> кому-л. предстоит наказание."],
  ["stipes", "<big>stipes</big>\n\n<b>stipes, itis</b> <i>m</i> (<i>редко</i> <i>Ap f</i>)\n<b>1)</b> ствол (arborum stipites cum ramis <i>QC</i>); <i>поэт.</i> дерево (consternunt terram concusso stipite frondes <i>V</i>); ветвь (virides comae in niveo stipite <i>M</i>);\n<b>2)</b> столб, свая, жердь, кол <i>Cs, O etc.</i>; бревно (s. acernus <i>Prp</i>); пень (in carioso stipite formicarum nidificia <i>Ap</i>); полено (semicremus <i>O</i>);\n<b>3)</b> <i>бран.</i> дубина, чурбан <i>Ter</i>."],
  ["stipidosus", "<big>stipidosus</big>\n\n<b>stipidosus, a, um</b> [stipes]\nдеревянистый (radix <i>Ap</i>)."],
  ["stipo", "<big>stipo</big>\n\n<b>stipo, avi, atum, are</b>\nнабивать, наполнять (majorem numerum assium in cella <i>Vr</i>); заполнять (tectum omne fartim <i>Ap</i>); скоплять, собирать (mella <i>V</i>); нагромождать, сгонять в кучу (naves in arto <i>L</i>): s. Platona (= Platonem) Menandro <i>H</i> упаковывать книги Платона вместе с произведениями Менандра; magna stipante caterva <i>V</i> в сопровождении большой толпы; stipatus gregibus amicorum <i>C</i> окружённый толпой друзей."],
  ["stips", "<big>stips</big>\n\n<b>I stips, stipis</b> <i>f</i>\n<b>1)</b> денежный взнос, даяние, пожертвование, лепта (stipem conferre <i>L</i>): s. amnica <i>Ap</i> сбор (плата) за перевоз;\n<b>2)</b> подаяние, милостыня (largifica <i>Lcr</i>): stipem tollere <i>C</i> запретить попрошайничество;\n<b>3)</b> заработок, прибыль (exigua <i>QC</i>).\n\n<b>II stips, stipis</b> <i>m</i> <i>Pt</i>\n= stipes."],
  ["stipticus", "<big>stipticus</big>\n\n<b>stipticus</b> <i>v. l.</i>\n= stypticus."],
  ["stipula", "<big>stipula</big>\n\n<b>stipula, ae</b> <i>f</i> [<i>одного корня с</i> stipo]\n<b>1)</b> стебель, соломинка: stipulae leves <i>V</i> жнивьё; flamma de stipula <i>O</i> соломенный огонь, <i>т. е.</i> мимолётная вспышка;\n<b>2)</b> <i>pl.</i> солома (domus stipulis tecta <i>O</i>): in stipula carpere somnum <i>O</i> спать на соломе;\n<b>3)</b> тростниковая свирель, дудка (stridens s. <i>V</i>)."],
  ["stipulatio", "<big>stipulatio</big>\n\n<b>stipulatio, onis</b> <i>f</i> [stipulor]\n<b>1)</b> формальное обещание, условие договора <i>C, Dig</i>;\n<b>2)</b> договор, соглашение <i>C, Nep, Sen, Dig</i>."],
  ["stipulatiuncula", "<big>stipulatiuncula</big>\n\n<b>stipulatiuncula, ae</b> <i>f</i> [<i>demin. к</i> stipulatio]\nмаленькое обещание, незначительное соглашеньице <i>C</i>."],
  ["stipulator", "<big>stipulator</big>\n\n<b>stipulator, oris</b> <i>m</i> [stipulor]\nдоговаривающийся, ставящий условия <i>Su, Dig</i>."],
  ["stipulatus", "<big>stipulatus</big>\n\n<b>stipulatus, us</b> <i>m</i>\nформальное обещание, условие договора <i>Q, G, Dig</i>."],
  ["stipulo", "<big>stipulo</big>\n\n<b>stipulo, —, —, are</b> <i>Symm</i>\n= stipulor."],
  ["stipulor", "<big>stipulor</big>\n\n<b>stipulor, atus sum, ari</b> <i>depon.</i> [stipulus]\nвыговаривать себе (какое-л. условие), обусловливать, договариваться, требовать формального обещания: stipulatus es? quo presente? <i>C</i> перед тобой обязались? при ком?; pecunia stipulata (<i>pass.</i>) <i>C</i> выговоренная (условленная, обещанная) сумма; quantumvis stipulare <i>J</i> требуй сколько тебе угодно."],
  ["stipulus", "<big>stipulus</big>\n\n<b>stipulus, a, um</b> <i>арх.</i>\nкрепкий, прочный <i>CJ</i>."],
  ["stiria", "<big>stiria</big>\n\n<b>stiria, ae</b> <i>f</i>\nледяная сосулька <i>V, PM, M etc.</i>"],
  ["stiriacus", "<big>stiriacus</big>\n\n<b>stiriacus, a, um</b> [stiria]\nзамороженный, замёрзший (gutta <i>Sol</i>)."],
  ["stiricidium", "<big>stiricidium</big>\n\n<b>stiricidium, i</b> <i>n</i> [stiria + cado]\nснегопад (крупными хлопьями) <i>Cato</i>."],
  ["stirpes", "<big>stirpes</big>\n\n<b>stirpes, is</b> <i>f</i> <i>L</i>\n= stirps."],
  ["stirpesco", "<big>stirpesco</big>\n\n<b>stirpesco, —, —, ere</b> [stirps]\nдавать стебли, пускать ростки <i>PM</i>."],
  ["stirpis", "<big>stirpis</big>\n\n<b>stirpis, is</b> <i>f</i> <i>L</i>\n= stirps."],
  ["stirpitus", "<big>stirpitus</big>\n\n<b>stirpitus</b> <i>adv.</i> [stirps]\n<b>1)</b> со стволом и с корнями (eruere <i>CJ</i>; transferre arborem <i>Dig</i>);\n<b>2)</b> целиком, полностью, совершенно (errorem extrahere <i>C</i>)."],
  ["stirps", "<big>stirps</big>\n\n<b>stirps, stirpis</b> <i>f</i> (<i>редко</i> <i>арх.</i> <i>и</i> <i>V m</i>)\n<b>1)</b> нижняя часть ствола с корнями (arbores per stirpes suas aluntur <i>C</i>): a (<i>или</i> cum) stirpe <i>Sl, L, V etc.</i> совершенно, полностью, целиком;\n<b>2)</b> ствол <i>PM</i>;\n<b>3)</b> побег, ветвь, сук (rami stirpesque <i>Lcr</i>);\n<b>4)</b> дерево <i>или</i> куст (stirpes et herbae <i>C, T</i>); молодое деревцо, саженец <i>V, Col</i>; растение (<i>вообще</i>) (stirpium naturas persequi <i>C</i>);\n<b>5)</b> корень волоса (a stirpe tollere capillos <i>Tib</i>);\n<b>6)</b> род, происхождение (incerta <i>L</i>; homo Italicae stirpis <i>L</i>): a stirpe <i>V</i> c малолетства;\n<b>7)</b> предок, родоначальник (familiam a stirpe ad hanc aetatem enumerare <i>Nep</i>);\n<b>8)</b> семья, поколение, род (exstincta in Alexandro s. <i>Just</i>);\n<b>9)</b> потомство (stirpem ex se relinquere <i>L</i>); отпрыск, потомок: s. Achillea <i>V</i> = Neoptolemus;\n<b>10)</b> первоначало, источник, основание (virtutis <i>C</i>; juris <i>C</i>): ab stirpe interire <i>Sl</i> <i>или</i> exstingui <i>L</i> погибнуть до основания (окончательно);\n<b>11)</b> первичные свойства, исконный характер (gentis <i>L</i>)."],
  ["stiva", "<big>stiva</big>\n\n<b>stiva, ae</b> <i>f</i>\nрукоять плуга <i>Vr, C, V, O etc.</i>"],
  ["stlata", "<big>stlata</big>\n\n<b>stlata, ae</b> <i>f</i>\nторговое, <i>позднее</i> пиратское судно <i>AG, Aus</i>."],
  ["stlataria", "<big>stlataria</big>\n\n<b>stlataria, orum</b> <i>n</i> [stlatarius]\nзаморские, <i>т. е.</i> драгоценные ткани <i>Enn</i>."],
  ["stlatarius", "<big>stlatarius</big>\n\n<b>stlatarius, a, um</b> <i>adj.</i> [stlata]\nдоставленный на кораблях, привозной, <i>т. е.</i> дорогой, драгоценный (purpura <i>J</i>)."],
  ["stlatta", "<big>stlatta</big>\n\n<b>stlatta, ae</b> <i>f</i> <i>v. l.</i>\n= stlata."],
  ["stlembus", "<big>stlembus</big>\n\n<b>stlembus, a, um</b>\nтяжёлый, медлительный (pedibus <i>LM</i>)."],
  ["stlis", "<big>stlis</big>\n\n<b>stlis, itis</b> <i>f</i> <i>арх. C, Q</i>\n= lis."],
  ["stlocus", "<big>stlocus</big>\n\n<b>stlocus, i</b> <i>m</i> <i>арх.</i> <i>Q</i>\n= locus."],
  ["stloppus", "<big>stloppus</big>\n\n<b>stloppus, i</b> <i>m</i>\nщелчок, шлепок, удар (<i>по надутой щеке</i>) <i>Pers</i>."],
  ["sto", "<big>sto</big>\n\n<b>sto, steti, statum, are</b> (<i>part. fut.</i> staturus; <i>3 л. pl. pf. поэт. иногда V</i> steterunt)\n<b>1)</b> стоять (ad januam <i>C</i>; stant ambo, non sedent <i>Pl</i>; circum aliquem <i>C</i>): stans pede in uno <i>погов. H</i> стоя на одной ноге, <i>т. е.</i> не переводя дыхания, с величайшей быстротой || стоять, находиться (eo loco <i>VF</i>; sub armis <i>Hirt</i>; ad ancoram in salo <i>L, bAfr</i>); стоять дыбом (comae steterunt <i>V</i>); торчать, вздыматься, подниматься вверх (turris ad auras stat <i>V</i>; stabat acuta silex <i>V</i>): stat nive Soracte <i>H</i> возвышается покрытый снегом Соракт || твёрдо держаться (in acie s. ac pugnare <i>L</i>): animo s. <i>C, H</i> быть бодрым <i>или</i> сохранять здравый смысл;\n<b>2)</b> длиться, продолжаться (diu pugna stetit <i>L</i>); существовать, оставаться, сохраняться (sine aliqua re s. non posse <i>C</i>);\n<b>3)</b> быть полным (aliqua re): ager sentibus stat <i>Caec</i> поле сплошь поросло терновником; caelum pulvere stat <i>V</i> небо затмилось тучами пыли; lumina flamma stant <i>V</i> глаза сверкают огнём;\n<b>4)</b> выставляться на продажу, продаваться (certo aere <i>Su</i>);\n<b>5)</b> стоить, обходиться (magno pretio <i>H</i>; gratis s. alicui <i>C</i>; multo sanguine ea victoria Poenis stetit <i>L</i>);\n<b>6)</b> быть на (чьей-л.) стороне, стоять за, защищать (s. cum, pro <i>или</i> ab aliquo <i>C, L, Nep</i>; s. pro causa meliore <i>QC</i>): eventus belli, unde (= a quo) jus stabat, ei victoriam dedit <i>L</i> исход войны дал победу тому, на чьей стороне было право;\n<b>7)</b> быть против, противодействовать, сопротивляться, бороться (s. contra, adversus <i>или</i> in aliquem <i>C, L, Nep, VP etc.</i>; in Trojam s. <i>O</i>): totius Asiae steterunt vires <i>L</i> поднялись (против Рима) все силы Азии;\n<b>8)</b> покоиться, держаться, зависеть (stat salus in armis <i>VF</i>; regnum stat concordia civium <i>L</i>): per Trebonium stetit, quominus milites oppido potirentur <i>Cs</i> от Требония зависело, что (<i>т. е.</i> из-за Требония) бойцы не овладели городом; certa stant omnia lege <i>Man</i> всё подчинено определённому закону; per ignorantiam stetit, ut... <i>PJ</i> по неведению случилось то, что...;\n<b>9)</b> останавливаться (equus s. loco nescit <i>V</i>); застывать (glacies stat iners <i>H</i>; stant ora metu <i>VF</i>); быть неподвижным, спокойным (stantes aquae <i>O</i>); засесть, вонзиться (alto stat vulnere mucro <i>V</i>); прекращаться, утихать, успокаиваться (ira, seditio stat <i>T</i>);\n<b>10)</b> оставаться верным, придерживаться, соблюдать (in aliqua re, aliqua re <i>и</i> alicui rei): in fide s. <i>C</i> держать слово; alicujus decreto s. <i>Cs</i> подчиняться чьему-л. постановлению; fama rerum s. <i>L</i> придерживаться традиции; s. voluntati alicujus <i>Dig</i> исполнить чью-л. волю;\n<b>11)</b> встречать одобрение, иметь успех, нравиться (fabula stat <i>H</i>);\n<b>12)</b> быть твёрдым, определённым, решённым (stat sua cuique dies <i>V</i>; certus merenti stat favor <i>Cld</i>): alicui sententia stetit aliquid facere <i>L</i> кто-л. решил что-либо сделать || <i>impers.</i> stat решено (non stat, quid faciamus <i>L</i>)."],
  ["Stobi", "<big>Stobi</big>\n\n<b>Stobi, orum</b> <i>m</i>\nСтобы, <i>город в сев. Македонии</i> <i>L, PM</i>."],
  ["Stoica", "<big>Stoica</big>\n\n<b>Stoica, orum</b> <i>n</i>\nстоическая философия <i>C</i>."],
  ["Stoice", "<big>Stoice</big>\n\n<b>Stoice</b> [Stoicus]\nстоически, подобно стоику (agere, dicere <i>C</i>)."],
  ["Stoicida", "<big>Stoicida</big>\n\n<b>Stoicida, ae</b> <i>m</i> <i>ирон.</i>\n(мнимый) ученик стоиков <i>J</i>."],
  ["Stoicus", "<big>Stoicus</big>\n\n<b>I Stoicus, a, um</b> (<i>греч.</i>)\nстоический <i>C, H, J</i>.\n\n<b>II Stoicus, i</b> <i>m</i>\nстоик, философ стоической школы <i>C, H</i>."],
  ["stola", "<big>stola</big>\n\n<b>stola, ae</b> <i>f</i> (<i>греч.</i>)\n<b>1)</b> стола, <i>длинное просторное платье римск. женщин <i>Vr, C, O</i>, etc; костюм флейтистов</i> <i>O</i>;\n<b>2)</b> <i>перен.</i> римская матрона <i>PM, St</i>."],
  ["stolatus", "<big>stolatus</big>\n\n<b>stolatus, a, um</b> [stola]\n<b>1)</b> одетый в столу (statuae stolatae <i>Vtr</i>): stolatae, arum <i>f Pt</i> почтенные матроны; Ulixes s. <i>Caligula ap. Su</i> Одиссей в столе (<i>о хитрой Ливии</i>);\n<b>2)</b> свойственный порядочной женщине (pudor <i>M</i>)."],
  ["stolide", "<big>stolide</big>\n\n<b>stolide</b> [stolidus]\nглупо, нелепо, бессмысленно <i>Sl, L, PM, T</i>."],
  ["stoliditas", "<big>stoliditas</big>\n\n<b>stoliditas, atis</b> <i>f</i> [stolidus]\nглупость, безрассудство <i>AG, Ap, Fl</i>."],
  ["stolidus", "<big>stolidus</big>\n\n<b>stolidus, a, um</b>\n<b>1)</b> глупый, нелепый, бессмысленный <i>Enn, Pl, Ter, Sl etc.</i>;\n<b>2)</b> не имеющий силы, не оказывающий действия, бессильный (genus causarum <i>C</i>)."],
  ["stolo", "<big>stolo</big>\n\n<b>I stolo, onis</b> <i>m</i>\nкорневой побег, паразитарный отпрыск корня (<i>отнимающий соки у ствола</i>) <i>Vr, PM</i>.\n\n<b>II Stolo, onis</b> <i>m</i>\nСтолон, <i>cognomen в роде Лициниев</i> <i>Vr</i>."],
  ["stomacace", "<big>stomacace</big>\n\n<b>stomacace, es</b> <i>f</i> (<i>греч.</i>) <i>предпол.</i>\nцинга <i>PM, Sen</i>."],
  ["stomachabundus", "<big>stomachabundus</big>\n\n<b>stomachabundus, a, um</b> [stomachor]\nраздражительный, раздражённый, негодующий, сердитый <i>AG</i>."],
  ["stomachanter", "<big>stomachanter</big>\n\n<b>stomachanter</b>\nгневно, сердито <i>Aug</i>."],
  ["stomachicus", "<big>stomachicus</big>\n\n<b>I stomachicus, a, um</b> [stomachus]\nжелудочный (passio <i>CA</i>).\n\n<b>II stomachicus, i</b> <i>m</i>\nстрадающий желудком <i>Sen, PM, Scr</i>."],
  ["stomachor", "<big>stomachor</big>\n\n<b>stomachor, atus sum, ari</b> <i>depon.</i> [stomachus]\nдосадовать, сердиться, негодовать (irascens et stomachans <i>C</i>); s. cum aliquo <i>C</i> браниться с кем-л.; s. aliqua re <i>C</i> <i>и</i> ob aliquid <i>H</i> сердиться на что-л. (из-за чего-л.); s. aliquid (id, omnia <i>Ter, C etc.</i>) негодовать на что-л. (на это, на всё)."],
  ["stomachose", "<big>stomachose</big>\n\n<b>stomachose</b> [stomachosus]\nс досадой, сердито, с раздражением, негодующе (rescribere alicui <i>C</i>)."],
  ["stomachosus", "<big>stomachosus</big>\n\n<b>stomachosus, a, um</b> [stomachus]\nсердитый, негодующий, раздражённый <i>C, H, Sen</i>."],
  ["stomachus", "<big>stomachus</big>\n\n<b>stomachus, i</b> <i>m</i> (<i>греч.</i>)\n<b>1)</b> пищевод, пищеварительный канал (s. ad ventriculum fertur <i>CC</i>);\n<b>2)</b> (= ventriculus) желудок (aeger <i>H</i>): boni stomachi esse <i>Q</i> быть удобоваримым; in stomacho ridere <i>погов. C</i> смеяться в кулак;\n<b>3)</b> настроение, расположение духа (bonus s. <i>Q, M</i>);\n<b>4)</b> вкус (ludi apparatissimi, sed non tui stomachi <i>C</i>);\n<b>5)</b> раздражительность, дурное настроение, досада (epistula plena stomachi <i>C</i>): stomachum in aliquem erumpere <i>C</i> отводить душу (срывать злость) на ком-л.; stomachum alicui movere (facere) <i>C</i> раздражать кого-либо || неудовольствие (summo cum labore stomachoque facere aliquid <i>C</i>); гнев (gravis Pelidae s. <i>H</i>)."],
  ["stomatice", "<big>stomatice</big>\n\n<b>stomatice, es</b> <i>f</i> (<i>греч.</i>)\nлекарство от заболеваний полости рта <i>PM, Scr</i>."],
  ["stomis", "<big>stomis</big>\n\n<b>stomis, idis</b> <i>f</i> (<i>греч.</i>)\nмундштук флейтиста <i>LM</i>."],
  ["stomoma", "<big>stomoma</big>\n\n<b>stomoma, atis</b> <i>n</i> (<i>греч.</i>)\nмелкая окалина <i>PM, CC</i>."],
  ["storax", "<big>storax</big>\n\n<b>storax, acis</b> <i>m</i> <i>Sol, Vlg</i>\n= styrax."],
  ["storea", "<big>storea</big>\n\n<b>storea, ae</b> <i>f</i>\nрогожа, циновка <i>Cs, L</i>."],
  ["storia", "<big>storia</big>\n\n<b>storia, ae</b> <i>f</i> <i>v. l.</i>\n= storea."],
  ["straba", "<big>straba</big>\n\n<b>straba</b> <i>v. l.</i>\n= strava."],
  ["strabo", "<big>strabo</big>\n\n<b>I strabo, onis</b> <i>m</i> (<i>греч.</i>)\n<b>1)</b> косоглазый, страдающий косоглазием <i>C, H, Pt</i>;\n<b>2)</b> завистливый человек, завистник <i>LM, Vr</i>.\n\n<b>II Strabo, onis</b> <i>m</i>\nСтрабон:\n<b>1)</b> <i>римск. nomen</i> <i>PM</i>;\n<b>2)</b> <i>греч. географ, родом из Амасии (Понт), умер ок. 25 г. н. э.</i> <i>Eccl</i>."],
  ["strabonus", "<big>strabonus</big>\n\n<b>strabonus, i</b> <i>m</i> <i>Pt</i>\n= strabo I, 1."],
  ["strabus", "<big>strabus</big>\n\n<b>strabus, a, um</b> (<i>греч.</i>)\nкосоглазый <i>Vr</i>."],
  ["strages", "<big>strages</big>\n\n<b>strages, is</b> <i>f</i> [sterno]\n<b>1)</b> падение (arborum <i>L</i>); обвал, разрушение (tectorum <i>L</i>; aedificiorum <i>T</i>);\n<b>2)</b> опустошение, разорение (stragem facere <i>L</i> <i>или</i> dare alicui rei <i>V</i>);\n<b>3)</b> умерщвление, убийство (principum <i>Just</i>); поражение (наголову), разгром (exercituum <i>VM</i>); резня, кровопролитие, истребление (Gallos strage fundere <i>L</i>); stragem hostium edere <i>C, Just</i> (dare <i>L</i>, facere <i>C</i>, efficere <i>C</i>, ciere <i>V</i>) разбить врагов;\n<b>4)</b> смерть, гибель (hominum <i>T</i>; canum volucrumque <i>O</i>);\n<b>5)</b> беспорядочная куча, груда (armorum <i>L</i>); груда тел (hominum, elephantorum <i>L</i>)."],
  ["stragula", "<big>stragula</big>\n\n<b>stragula, ae</b> <i>f</i>\nпокрывало <i>Pt</i>; ковёр <i>Ap</i>; попона <i>Dig</i>."],
  ["stragulatus", "<big>stragulatus</big>\n\n<b>stragulatus, a, um</b> <i>Vlg</i>\n= stragulus."],
  ["stragulum", "<big>stragulum</big>\n\n<b>stragulum, i</b> <i>n</i> [stragulus]\n<b>1)</b> покрывало, одеяло <i>C, Sen etc.</i>;\n<b>2)</b> попона, чепрак <i>M</i>;\n<b>3)</b> подстилка <i>PM, Sol</i>."],
  ["stragulus", "<big>stragulus</big>\n\n<b>stragulus, a, um</b> [sterno]\nгодный для расстилания: vestis stragula <i>C, L, H</i> подстилка, матрац, пуховик (<i>см.</i> stragula <i>и</i> stragulum)."],
  ["stramen", "<big>stramen</big>\n\n<b>stramen, inis</b> <i>n</i> [sterno]\nподстилка (<i>из соломы, травы и т. п.</i>) <i>V, O, PM etc.</i>"],
  ["stramentarius", "<big>stramentarius</big>\n\n<b>stramentarius, a, um</b> [stramentum]\nпредназначенный для резки соломы (falces <i>Cato</i>)."],
  ["stramenticius", "<big>stramenticius</big>\n\n<b>stramenticius, a, um</b>\nсоломенный (casae <i>bH</i>; vavato <i>Pt</i>)."],
  ["stramentum", "<big>stramentum</big>\n\n<b>stramentum, i</b> <i>n</i> [sterno]\n<b>1)</b> подстилка (<i>преим. из соломы</i>) <i>Cato, Pl, H</i>;\n<b>2)</b> солома (casae stramentis tectae <i>Cs</i>);\n<b>3)</b> вьючное седло <i>или</i> попона <i>Cs, Ap</i>."],
  ["stramineus", "<big>stramineus</big>\n\n<b>stramineus, a, um</b> [stramen]\nсоломенный (casa <i>Prp, O</i>): Quirites straminei <i>O</i> соломенные чучела (<i>которые, в качестве символической жертвы, ежегодно бросались в Тибр</i>)."],
  ["strangulabilis", "<big>strangulabilis</big>\n\n<b>strangulabilis, e</b> [strangulo]\nмогущий быть задушенным <i>Tert</i>."],
  ["strangulatio", "<big>strangulatio</big>\n\n<b>strangulatio, onis</b> <i>f</i>\n<b>1)</b> удушье <i>PM</i>;\n<b>2)</b> судорога (vulvae <i>PM</i>)."],
  ["strangulator", "<big>strangulator</big>\n\n<b>strangulator, oris</b> <i>m</i>\nдушитель <i>Spart, Eccl</i>."],
  ["strangulatus", "<big>strangulatus</big>\n\n<b>strangulatus, us</b> <i>m</i> <i>PM</i>\n= strangulatio 2."],
  ["strangulo", "<big>strangulo</big>\n\n<b>strangulo, avi, atum, are</b>\n<b>1)</b> душить, (у)давить (aliquem <i>C etc.</i>); <i>med.-pass.</i> strangulari задыхаться (difficultate spirandi <i>CC</i>);\n<b>2)</b> заглушать (vocem <i>Q</i>); глушить (arborem <i>PM</i>); делать бесплодным (solum <i>PM</i>);\n<b>3)</b> мучить (dolor strangulat <i>O</i>);\n<b>4)</b> прятать, скрывать в себе (arca strangulat divitias <i>St</i>); утаивать (deposita vestis strangulatur <i>Pt</i>);\n<b>5)</b> вынуждать (causae, quibus strangulatur venditor <i>CTh</i>)."],
  ["stranguria", "<big>stranguria</big>\n\n<b>stranguria, ae</b> <i>f</i> (<i>греч.</i>)\nстрангурия, затруднённое мочеиспускание <i>Cato, C, PM</i>."],
  ["strata", "<big>strata</big>\n\n<b>strata, ae</b> <i>f</i> [sterno]\n(<i>sc.</i> via) мощёная улица, мостовая <i>Eutr, Aug</i>."],
  ["strategema", "<big>strategema</big>\n\n<b>strategema, atis</b> <i>n</i> (<i>греч.</i>)\nвоенная хитрость <i>C, VM, Fl</i>; <i>перен.</i> хитрость, хитрый поступок <i>C</i>."],
  ["strategeum", "<big>strategeum</big>\n\n<b>strategeum (-ium), i</b> <i>n</i>\nпалатка полководца <i>Eccl</i>."],
  ["strategia", "<big>strategia</big>\n\n<b>strategia, ae</b> <i>f</i> (<i>греч.</i>)\nвоенный округ (dividi in praefecturas, quas strategias vocant <i>PM</i>)."],
  ["strategus", "<big>strategus</big>\n\n<b>strategus, i</b> <i>m</i> (<i>греч.</i>)\n<b>1)</b> полководец <i>Pl</i>;\n<b>2)</b> председатель пира <i>Pl</i>."],
  ["stratioticus", "<big>stratioticus</big>\n\n<b>stratioticus, a, um</b> (<i>греч.</i>)\nсолдатский (mores <i>Pl</i>): homo s. <i>Pl</i> воин; nuntius s. <i>Pl</i> военный посыльный, гонец."],
  ["Stratius", "<big>Stratius</big>\n\n<b>Stratius, i</b> <i>m</i>\nСтратий, <i>врач царя Эвмена</i> <i>L</i>."],
  ["Stratocles", "<big>Stratocles</big>\n\n<b>Stratocles, is</b> <i>m</i>\nСтратокл, <i>комический актёр</i> <i>J, Q</i>."],
  ["Straton", "<big>Straton</big>\n\n<b>Strato(n), onis</b> <i>m</i>\nСтратон:\n<b>1)</b> <i>родом из Лампсака, ученик Теофраста, философ-перипатетик на рубеже IV <i>и</i> III вв. до н. э.</i> <i>C, Sen</i>;\n<b>2)</b> <i>римск. врач времён Цицерона</i> <i>C</i>."],
  ["Stratonicea", "<big>Stratonicea</big>\n\n<b>Stratonicea, ae</b> <i>f</i>\nСтратоникея, <i>город в Карий, близ реки Marsyas</i> <i>L, PM</i>."],
  ["Stratonicensis", "<big>Stratonicensis</big>\n\n<b>Stratonicensis, e</b> <i>и</i> <b>Stratoniceus, a, um</b> [Stratonicea]\nстратоникейский <i>C, L, T</i>."],
  ["Stratonicis", "<big>Stratonicis</big>\n\n<b>Stratonicis, ictis</b> <i>f</i>\nСтратоникида, <i>эпитет Венеры в Смирне</i> <i>T</i>."],
  ["stratopedum", "<big>stratopedum</big>\n\n<b>stratopedum, i</b> <i>m</i> (<i>греч.</i>; <i>лат.</i> castra)\nлагерь <i>Obs</i>."],
  ["strator", "<big>strator</big>\n\n<b>strator, oris</b> <i>m</i> [sterno]\n<b>1)</b> слуга, седлавший коня, конюх <i>Spart, Amm</i>; императорский конюшни <i>Amm, CJ, CTh</i>;\n<b>2)</b> член свиты <i>или</i> телохранитель пропретора <i>Dig, CJ</i>;\n<b>3)</b> тюремщик <i>CJ</i>;\n<b>4)</b> разливающий вино (s. laguncularum <i>Vlg</i>)."],
  ["stratorium", "<big>stratorium</big>\n\n<b>stratorium, i</b> <i>m</i> [sterno]\nложе, постель <i>Vlg</i>."],
  ["stratum", "<big>stratum</big>\n\n<b>stratum, i</b> <i>n</i>\n<b>1)</b> одеяло, покрывало <i>O, Su</i>; подстилка, постель (lecti mollia strata <i>Lcr</i>); ложе (strato surgere <i>V</i>; stratis reponere collapsa membra <i>V</i>);\n<b>2)</b> вьючное седло <i>Sen</i>; попона <i>или</i> чепрак <i>L</i>: qui asinum non potest, s. caedit <i>Pt</i> — <i>см.</i> asinus;\n<b>3)</b> мостовая (strata saxea viarum <i>Lcr</i>);\n<b>4)</b> настил, пол <i>PM</i> (<i>v. l.</i>), <i>Vtr</i>."],
  ["stratura", "<big>stratura</big>\n\n<b>stratura, ae</b> <i>f</i> [sterno]\n<b>1)</b> мощение (viarum <i>Su</i>);\n<b>2)</b> слой удобрения <i>Pall</i>."],
  ["stratus", "<big>stratus</big>\n\n<b>I stratus, a, um</b> <i>part. pf. к</i>\nsterno.\n\n<b>II stratus, us</b> <i>m</i> [sterno]\n<b>1)</b> расстилание <i>Vr</i>;\n<b>2)</b> попона, чепрак <i>Sol</i>;\n<b>3)</b> подстилка <i>или</i> ковёр <i>AG, Dig</i>.\n\n<b>III Stratus, i</b> <i>f</i>\nСтрат, <i>главн. город Акарнании</i> <i>C, L, PM</i>."],
  ["strava", "<big>strava</big>\n\n<b>strava (-ba), ae</b> <i>f</i>\nкуча трофейного оружия <i>Lact</i>."],
  ["stravi", "<big>stravi</big>\n\n<b>stravi</b> <i>pf. к</i>\nsterno."],
  ["strebula", "<big>strebula</big>\n\n<b>strebula, orum</b> <i>m</i> (<i>тж.</i> strebula caro <i>Eccl</i>)\nбедренная часть туши жертвенного животного <i>Pl, Vr</i>."],
  ["strena", "<big>strena</big>\n\n<b>strena, ae</b> <i>f</i>\n<b>1)</b> примета, предзнаменование <i>Pl</i>;\n<b>2)</b> праздничный (<i>преим.</i> новогодний) подарок <i>Su, Symm, Aus</i>."],
  ["strenue", "<big>strenue</big>\n\n<b>strenue</b> [strenuus]\nдеятельно, усердно, бодро, проворно (fortiter ac s. <i>L</i>)."],
  ["strenuitas", "<big>strenuitas</big>\n\n<b>strenuitas, atis</b> <i>f</i> [strenuus]\nдеятельный характер, рвение, усердие, бодрость, проворство <i>Vr, O</i>."],
  ["strenuo", "<big>strenuo</big>\n\n<b>strenuo, —, —, are</b> [strenuus]\nторопиться, суетиться, хлопотать <i>Pl</i> (<i>v. l.</i>)."],
  ["strenuosus", "<big>strenuosus</big>\n\n<b>strenuosus, a, um</b> <i>Pl</i>\n= strenuus."],
  ["strenuus", "<big>strenuus</big>\n\n<b>strenuus, a, um</b>\n<b>1)</b> деятельный, бодрый, проворный (vir fortis ac s. <i>Nep</i>);\n<b>2)</b> крепкий (corpus <i>AG</i>); сильный (remedium <i>QC</i>);\n<b>3)</b> быстроходный (navis <i>O</i>); быстрый (mors <i>QC</i>): strenua inertia <i>ирон. H</i> хлопотливое ничегонеделание; ad tristem partem strenua est suspicio <i>погов. PS</i> подозрение (всегда) направлено в дурную сторону;\n<b>4)</b> предприимчивый, решительный, смелый, отважный: s. bello (<i>abl.</i>) <i>Nep</i> <i>или</i> militiae (<i>gen.</i>) <i>T</i> дельный воин;\n<b>5)</b> беспокойный, волнующийся (quieti et strenui <i>T</i>)."],
  ["strepito", "<big>strepito</big>\n\n<b>strepito, —, —, āre</b> [<i>intens. к</i> strepo]\nиздавать оглушительный шум (strepitantia arma <i>Tib, Sil</i>); сильно кричать (corvi inter se strepitant <i>V</i>)."],
  ["strepitus", "<big>strepitus</big>\n\n<b>strepitus, ūs</b> (<i>редко</i> <i>Enn</i> <b>ī</b>) <i>m</i> [strepo]\n<b>1)</b> шум, гам, гул, грохот (rotarum <i>Cs</i>; fluminum <i>C</i>): strepitum non faciens <i>O</i> бесшумный || <i>ирон.</i> трескотня (sententiarum <i>Pt</i>);\n<b>2)</b> звучание, звук, звон (citharae <i>H</i>; testudinis aureae <i>H</i>)."],
  ["strepo", "<big>strepo</big>\n\n<b>strepo, puī, pitum, ere</b>\nшуметь <i>T</i>; грохотать (aequor remis strepit <i>T</i>; clamoribus s. <i>L</i>): symphonia strepente <i>Pt</i> при звуках оглушительной музыки || бряцать, лязгать, звенеть (arma strepunt <i>Sl</i>); греметь (cornua strepunt <i>V</i>); звучать, оглашаться (campus murmure strepit <i>V</i>); выкрикивать, кричать (s. haec <i>L</i>)."],
  ["stria", "<big>stria</big>\n\n<b>stria, ae</b> <i>f</i>\n<b>1)</b> жёлоб, паз <i>Vtr</i>;\n<b>2)</b> складка: striis dependere <i>Ap</i> ниспадать складками;\n<b>3)</b> борозда (aratrum vomere striam facit <i>Vtr</i>)."],
  ["striatura", "<big>striatura</big>\n\n<b>striatura, ae</b> <i>f</i>\nжелобчатость <i>Vtr</i>; бороздчатость (cavari striaturis <i>Sid</i>)."],
  ["striatus", "<big>striatus</big>\n\n<b>striatus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> strio;\n<b>2.</b> <i>adj.</i> складчатый, изборождённый морщинами, морщинистый (folia <i>PM</i>; frons <i>Ap</i>); желобчатый (columna <i>Vtr</i>); бороздчатый (virgula <i>Sen</i>)."],
  ["stribiligo", "<big>stribiligo</big>\n\n<b>stribiligo (stribligo), inis</b> <i>f</i> (<i>греч.</i>)\nязыковая погрешность, солецизм <i>AG, Eccl</i>."],
  ["striblita", "<big>striblita</big>\n\n<b>striblita, ae</b> <i>f</i> <i>v. l.</i>\n= scriblita."],
  ["stribula", "<big>stribula</big>\n\n<b>stribula, ae</b> <i>f</i> <i>v. l.</i>\n= strebula."],
  ["stricosus", "<big>stricosus</big>\n\n<b>stricosus, a, um</b> <i>v. l.</i>\n= strigosus."],
  ["stricte", "<big>stricte</big>\n\n<b>stricte</b> [strictus]\n<b>1)</b> тесно, сжато, плотно (circumligare ventrem fasciis <i>AG</i>); коротко (putare vitem <i>Pall</i>);\n<b>2)</b> точно, строго (interpretari <i>Dig</i>)."],
  ["strictim", "<big>strictim</big>\n\n<b>strictim</b>\n<b>1)</b> сжато (dicere <i>Vr</i>; perscribere aliquid <i>Sl</i>);\n<b>2)</b> слегка, поверхностно, вскользь (aspicere <i>C</i>);\n<b>3)</b> коротко, наголо (attondere <i>Pl</i>);\n<b>4)</b> плотно (juncta crates <i>Pall</i>)."],
  ["strictio", "<big>strictio</big>\n\n<b>strictio, onis</b> <i>f</i> [stringo]\n<b>1)</b> сжатие, сужение <i>CA</i>;\n<b>2)</b> строгость, суровость <i>Eccl</i>."],
  ["strictivilla", "<big>strictivilla</big>\n\n<b>strictivilla (-vella)</b> <i>v. l.</i>\n= strittivilla."],
  ["strictivus", "<big>strictivus</big>\n\n<b>strictivus, a, um</b> [stringo]\nсорванный, собранный (olea <i>Cato</i>)."],
  ["strictor", "<big>strictor</big>\n\n<b>strictor, oris</b> <i>m</i> [stringo]\nсрывающий, собиратель (olearum <i>Cato</i>)."],
  ["strictura", "<big>strictura</big>\n\n<b>strictura, ae</b> <i>f</i> [stringo]\n<b>1)</b> сокращение, сужение (<i>sc.</i> stomachi <i>CA</i>);\n<b>2)</b> ущемление, гнёт (s. dura <i>Jvc</i>);\n<b>3)</b> сварочное железо <i>V</i>; железный брус <i>LM, Vr, PM</i>."],
  ["strictus", "<big>strictus</big>\n\n<b>strictus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> stringo;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> тугой, крепко натянутый, плотный (artus <i>T</i>);\n<b>2)</b> узкий, тесный (janua <i>O</i>); краткий, сжатый (orator <i>Q</i>);\n<b>3)</b> суровый, холодный (aura <i>Aus</i>);\n<b>4)</b> крутой, подверженный запорам (alvus <i>Veg</i>);\n<b>5)</b> точный (stricto jure <i>Dig</i>);\n<b>6)</b> строгий (judex <i>Sen</i>);\n<b>7)</b> вынутый из ножен, обнажённый (ensis <i>SenT</i>; ferrum <i>V</i>)."],
  ["strideo", "<big>strideo</big>\n\n<b>strideo, stridi, —, ere</b> <i>и</i> <b>strido, stridi, —, ere</b>\nшипеть (anguis stridet <i>V</i>); шипеть <i>или</i> трещать (<i>на огне</i>) (exta stridentia <i>Pt</i>); визжать (cardo stridet <i>V</i>): stridens stipula <i>V</i> визгливая дудка || шуметь (mare stridet <i>V</i>); свистеть (sagitta stridet <i>V</i>); скрипеть (plaustra strident <i>V</i>); жужжать, гудеть (apes stridunt <i>V</i>); скрежетать (aeger dientibus stridet <i>CC</i>); стрекотать (gryllus stridet <i>PM</i>); шелестеть (silvae stridunt <i>V</i>); лепетать, шептать (puella stridet <i>O</i>)."],
  ["strido", "<big>strido</big>\n\n<b>strido, stridi, —, ere</b> <i>V</i>\n= strideo."],
  ["stridor", "<big>stridor</big>\n\n<b>stridor, oris</b> <i>m</i> [strideo]\nшипение (serpentis <i>O</i>); свист (pennarum <i>PM</i>); скрежет, щёлканье (dentium <i>O</i>); треск, скрип (januae <i>O</i>); шум (procellae <i>Prp</i>); лязг (ferri <i>V</i>); звук (lituum <i>Lcn</i>); визг (rudentum <i>V</i>); гудение, жужжание (apium <i>O</i>); хрюканье (suis <i>PM</i>); шёпот (tribuni <i>C</i>)."],
  ["stridulus", "<big>stridulus</big>\n\n<b>stridulus, a, um</b> [strideo]\nгудящий <i>или</i> свистящий (cornus, <i>sc.</i> hasta <i>V</i>); шипящий (fax <i>O</i>); скрипучий (plaustra <i>O</i>); трескучий (vox <i>Sen</i>); шумящий (fraxinus <i>Cld</i>)."],
  ["striga", "<big>striga</big>\n\n<b>I striga, ae</b> <i>f</i> [stringo]\nполоса скошенного хлеба <i>или</i> травы, прокос <i>Col</i>.\n\n<b>II striga, ae</b> <i>f</i> [strix]\nстарая колдунья, ведьма <i>Pt</i>."],
  ["striges", "<big>striges</big>\n\n<b>striges, um</b> <i>pl. к</i>\nstrix I."],
  ["strigilecula", "<big>strigilecula</big>\n\n<b>strigilecula, ae</b> <i>f</i> [<i>demin. к</i> strigilis]\nбанный скребок <i>Ap</i>."],
  ["strigilis", "<big>strigilis</big>\n\n<b>strigilis, is</b> (<i>abl.</i> i) <i>f</i> [stringo]\n<b>1)</b> скребница, чесалка <i>C, H, Sen</i>;\n<b>2)</b> желобчатость (<i>колонны</i>) <i>Vtr</i>;\n<b>3)</b> ушной шприц <i>CC</i>;\n<b>4)</b> самородок золота <i>PM</i>."],
  ["striglium", "<big>striglium</big>\n\n<b>striglium, striglibus</b> <i>Vtr, J</i>\n= strigilium, strigilibus (<i>к</i> strigilis)."],
  ["strigmentum", "<big>strigmentum</big>\n\n<b>strigmentum, i</b> <i>n</i> [stringo]\n<b>1)</b> оскрёбки <i>PM</i>;\n<b>2)</b> удалённая грязь <i>CC, PM</i>."],
  ["strigo", "<big>strigo</big>\n\n<b>strigo, —, —, are</b> [striga I]\nостанавливаться во время пахоты <i>PM</i>; <i>перен.</i> отдыхать, переводить дух <i>Sen</i>."],
  ["strigosus", "<big>strigosus</big>\n\n<b>strigosus, a, um</b>\n<b>1)</b> тощий, худой (equus <i>L</i>);\n<b>2)</b> сухой, бесцветный (orator <i>C</i>)."],
  ["stringo", "<big>stringo</big>\n\n<b>stringo, strinxi, strictum, ere</b>\n<b>1)</b> натягивать (arcum <i>PJ</i>; habenas <i>St</i>); свёртывать, убирать (vela <i>Sen</i>); сжимать (pedes vinculis <i>O</i>); затягивать (nodum <i>L</i>); стягивать (strictum frigore vulnus <i>L</i>);\n<b>2)</b> замораживать, сковывать льдом (stricti amnes <i>VF</i>);\n<b>3)</b> сжато излагать, кратко выражать (narrationis rem <i>Q</i>);\n<b>4)</b> срывать, обрывать, снимать (frondes <i>V</i>; rubos <i>L</i>); жать, косить (hordea <i>V</i>); очищать от ветвей (s. silvis remos <i>V</i>);\n<b>5)</b> уменьшать, транжирить (rem ingluvie <i>H</i>);\n<b>6)</b> вынимать из ножен, вытаскивать, обнажать (gladium <i>Cs, Pt</i>; ferrum <i>V, L etc.</i>): s. bellum <i>Fl</i> готовиться к войне; s. manum in aliquem <i>O</i> вооружиться против кого-л.; s. iambum — in hostes <i>O</i> устремиться с (сатирическими) ямбами на врагов.;\n<b>7)</b> слегка ранить, оцарапать (corpus <i>V</i>; pedem dente <i>O</i>);\n<b>8)</b> задевать, касаться (s. undas alis <i>O</i>); соприкасаться, граничить (Scytharum gens ultima Asiae stringit <i>QC</i>): s. litora <i>SenT</i> достигать берегов;\n<b>9)</b> порочить, оскорблять (nomen alicujus <i>O</i>): s. animum <i>V</i> сжать сердце, глубоко тронуть. — <i>См. тж.</i> strictus."],
  ["stringor", "<big>stringor</big>\n\n<b>stringor, oris</b> <i>m</i> [stringo]\nболь от стягивания, болезненное прикосновение (aquae gelidae <i>Lcr</i>)."],
  ["strio", "<big>strio</big>\n\n<b>strio, avi, atum, are</b> [stria]\nпокрывать складками <i>или</i> бороздами (морщинами) (asparagus striatur <i>PM</i>); <i>архит.</i> покрывать каннелюрами (columnas striari oportet <i>Vtr</i>). — <i>См. тж.</i> striatus."],
  ["strittabilla", "<big>strittabilla</big>\n\n<b>strittabilla, ae</b> <i>f</i> [stritto]\nвихляющаяся на ходу <i>Vr</i>."],
  ["strittivilla", "<big>strittivilla</big>\n\n<b>strittivilla, ae</b> <i>f</i> [stringo + villus]\nудаляющая волосы (со своего тела) (<i>о распутницах</i>) <i>Pl ap. AG</i>."],
  ["stritto", "<big>stritto</big>\n\n<b>stritto, —, —, are</b>\nвихляться на ходу, плохо держаться на ногах («sistere aegre» <i>Vr</i>)."],
  ["strix", "<big>strix</big>\n\n<b>I strix, igis</b> <i>f</i>\nсова ушастая, сипуха (<i>высасывающая, по поверью, кровь у детей</i>) <i>Pl, Tib, O, PM etc.</i>\n\n<b>II strix, igis</b> <i>f</i> <i>Vtr</i>\n= stria 1."],
  ["strobilus", "<big>strobilus</big>\n\n<b>strobilus, i</b> <i>m</i> (<i>греч.</i>)\nшишка хвойного дерева <i>Dig</i>."],
  ["stroma", "<big>stroma</big>\n\n<b>stroma, atis</b> <i>n</i> (<i>греч.</i>)\nпёстрое покрывало, ковёр <i>Capit, Hier</i>."],
  ["strongyla", "<big>strongyla</big>\n\n<b>strongyla, ae</b> <i>f</i> (<i>греч.</i>)\nскульптурный бюст <i>Tert</i>."],
  ["stropha", "<big>stropha</big>\n\n<b>stropha, ae</b> <i>f</i> (<i>греч.</i>)\n<b>1)</b> <i>стих.</i> строфа, поворот, <i>т. е.</i> отрывок драмы, исполнявшийся хором при движении справа налево (s. et antistropha <i>Macr</i>);\n<b>2)</b> <i>pl.</i> телодвижения, пантомима (elegantes strophae <i>Pt</i>);\n<b>3)</b> уловка, хитрость (aliquam stropham invenire <i>PJ</i>): remotis strophis ac fucis <i>Sen</i> отбросив уловки и ложь; verbosae strophae <i>Ph</i> хитроумные речи."],
  ["Strophades", "<big>Strophades</big>\n\n<b>Strophades, um</b> <i>f</i>\nСтрофады, <i>два островка в Ионическом море, к юго-вост. от Закинфа; по преданию, местопребывание Гарпий</i> <i>V, VF, PM</i>."],
  ["strophiarius", "<big>strophiarius</big>\n\n<b>strophiarius, i</b> <i>m</i> [strophium]\nизготовляющий строфии <i>или</i> торгующий ими <i>Pl</i>."],
  ["strophiolum", "<big>strophiolum</big>\n\n<b>strophiolum, i</b> <i>n</i> [<i>demin. к</i> strophium]\nвеночек <i>PM, Tert</i>."],
  ["strophium", "<big>strophium</big>\n\n<b>strophium, i</b> <i>n</i> (<i>греч.</i>)\n<b>1)</b> строфий, повязка для грудей (<i>род корсажа</i>) <i>Vr, C, Ctl</i>;\n<b>2)</b> венок <i>V, Eccl</i>;\n<b>3)</b> кручёная бечева, канат <i>Ap</i>."],
  ["Strophius", "<big>Strophius</big>\n\n<b>Strophius, i</b> <i>m</i>\nСтрофий, <i>миф. царь Фокиды, отец Пилада</i> <i>O</i>."],
  ["strophoma", "<big>strophoma</big>\n\n<b>strophoma, atis</b> <i>n</i> <i>Hier</i>\n= strophus."],
  ["strophosus", "<big>strophosus</big>\n\n<b>strophosus, a, um</b> [strophus]\nстрадающий резями в животе <i>Veg</i>."],
  ["strophus", "<big>strophus</big>\n\n<b>strophus, i</b> <i>m</i> (<i>греч.</i>)\nрези в животе <i>Veg, Ap</i>."],
  ["stroppus", "<big>stroppus</big>\n\n<b>stroppus</b> <i>v. l.</i>\n= struppus."],
  ["structe", "<big>structe</big>\n\n<b>structe</b>\nкрасиво, изящно (prodire <i>Tert</i>); складно (scribere <i>Fronto</i>)."],
  ["structilis", "<big>structilis</big>\n\n<b>structilis, e</b> [struo]\n<b>1)</b> строительный (caementum <i>M</i>; lapis <i>Dig</i>);\n<b>2)</b> сложенный из кирпичей (canalis <i>Vtr</i>)."],
  ["structio", "<big>structio</big>\n\n<b>structio, onis</b> <i>f</i> [struo]\nрасположение, сооружение (spatia structionis <i>Pall</i>); построение, организация (studii <i>Eccl</i>)."],
  ["structor", "<big>structor</big>\n\n<b>structor, oris</b> <i>m</i> [struo]\n<b>1)</b> (<i>тж.</i> s. parietarius <i>Eccl</i>) строитель, каменщик <i>C, Capit, Dig etc.</i>: s. capillaturae <i>Tert</i> парикмахер;\n<b>2)</b> устроитель пиршества, <i>т. е.</i> раб, накрывающий на стол <i>Pt, J, M etc.</i>"],
  ["structorius", "<big>structorius</big>\n\n<b>structorius, a, um</b>\nстроительный (operae <i>Tert</i>)."],
  ["structura", "<big>structura</big>\n\n<b>structura, ae</b> <i>f</i> [struo]\n<b>1)</b> строение, сооружение, постройка (genus structurae antiquae <i>L</i>);\n<b>2)</b> расположение, порядок (verborum <i>C</i>); построение (carminis <i>O</i>); покрой, фасон (togae <i>Macr</i>)."],
  ["structus", "<big>structus</big>\n\n<b>structus, us</b> <i>m</i> [struo]\nнагромождение, куча (lignorum <i>Eccl</i>)."],
  ["strues", "<big>strues</big>\n\n<b>strues, is</b> <i>f</i> [struo]\n<b>1)</b> куча, груда (pomorum <i>Sen</i>; corporum <i>T</i>);\n<b>2)</b> костёр (lignorum <i>L etc.</i>);\n<b>3)</b> кучка жертвенных пирожков <i>Cato, O</i>;\n<b>4)</b> беспорядочная толпа (<i>sc.</i> militum <i>L</i>)."],
  ["struix", "<big>struix</big>\n\n<b>struix, icis</b> <i>f</i> [struo]\nкуча, груда, ворох (patinaria <i>Pl</i>); множество (malorum <i>Naev</i>)."],
  ["struma", "<big>struma</big>\n\n<b>struma, ae</b> <i>f</i> [struo]\nопухоль желёз (<i>преим. шейных</i>) <i>CC, C, J etc.</i>"],
  ["strumosus", "<big>strumosus</big>\n\n<b>strumosus, a, um</b> [struma]\nс опухшими железами <i>или</i> золотушный (homo <i>J</i>; pecudes <i>Col</i>)."],
  ["struo", "<big>struo</big>\n\n<b>struo, struxi, structum, ere</b> [sterno]\n<b>1)</b> класть друг на друга, накладывать рядами <i>или</i> слоями (trabes, lateres <i>Cs</i>); громоздить (montes ad sidera <i>O</i>); заваливать, загромождать (altaria donis <i>V</i>);\n<b>2)</b> раскладывать, расставлять (penum ordine <i>V</i>): structae avenae <i>O</i> многоствольная тростниковая свирель;\n<b>3)</b> накапливать (rem <i>Pers</i>; opes <i>Pt</i>);\n<b>4)</b> строить, выстраивать, возводить (muros saxo <i>QC</i>; domum <i>H</i>; acervum <i>H</i>; aggerem <i>T</i>); размещать, располагать (bene structa collocatio <i>C</i>; verba <i>C</i>); закладывать, класть (initia alicui rei <i>T</i>);\n<b>5)</b> устраивать (convivia <i>T</i>);\n<b>6)</b> <i>воен.</i> выстраивать (omnes armatos in campo <i>L</i>; acies manipulatim structa <i>L</i>);\n<b>7)</b> готовить (mortem alicui <i>T</i>; sibi sollicitudinem <i>C</i>); подготовлять, замышлять, создавать (s. alicui insidias <i>C, L</i>): quid struit? <i>V</i> что у него на уме? || возбуждать (s. odium in alios <i>C</i>);\n<b>8)</b> загромождать, затыкать (aures habere structas <i>Eccl</i>)."],
  ["struppus", "<big>struppus</big>\n\n<b>struppus, i</b> <i>m</i> (<i>греч.</i>)\n<b>1)</b> кручёный ремень <i>Vtr, Gracchus ap. AG, Is</i>;\n<b>2)</b> венок из цветов <i>PM</i>."],
  ["struthea", "<big>struthea</big>\n\n<b>struthea, orum</b> <i>n</i> [strutheus]\nворобьиные яблоки (<i>мелкий сорт айвы</i>) <i>Pl</i>."],
  ["strutheus", "<big>strutheus</big>\n\n<b>strutheus, a, um</b> (<i>греч.</i>)\nворобьиный: struthea mala <i>PM</i> — <i>см.</i> struthea."],
  ["struthio", "<big>struthio</big>\n\n<b>struthio, onis</b> <i>m</i> (<i>греч.</i>)\nстраус <i>Capit, Lampr, Vop, Vlg</i>."],
  ["struthion", "<big>struthion</big>\n\n<b>struthion, i</b> <i>n</i> (<i>греч.</i>) <i>бот.</i>\nмыльнянка (<i>Saponaria officinalis</i>, L.) <i>CC, PM</i>."],
  ["struthocamelus", "<big>struthocamelus</big>\n\n<b>struthocamelus, i</b> <i>m</i> <i>и</i> <i>f</i> <i>Sen, PM, Pt, Lampr</i>\n= struthio."],
  ["strutio", "<big>strutio</big>\n\n<b>strutio, onis</b> <i>m</i> <i>v. l.</i>\n= struthio."],
  ["struxi", "<big>struxi</big>\n\n<b>struxi</b> <i>pf. к</i>\nstruo."],
  ["Strymon", "<big>Strymon</big>\n\n<b>Strymo(n), onis (onos)</b> <i>m</i>\nСтримон, <i>пограничная река между Фракией и Македонией, впадающая в Стримонский залив</i> <i>Nep, L, Sen, V, Prp etc.</i>; <i>перен.</i> Фракия <i>St</i>."],
  ["Strymonis", "<big>Strymonis</big>\n\n<b>Strymonis, idis</b> <i>f</i> [Strymo(n)]\nстримонская, <i>перен.</i> фракиянка <i>или</i> амазонка <i>Prp</i>."],
  ["Strymonius", "<big>Strymonius</big>\n\n<b>Strymonius, a, um</b> [Strymo(n)]\nстримонский (grues <i>V</i>); <i>поэт.</i> фракийский <i>или</i> северный <i>O, St</i>."],
  ["stuc", "<big>stuc</big>\n\n<b>stuc</b> <i>Pl</i>\n= istuc II."],
  ["studeo", "<big>studeo</big>\n\n<b>studeo, ui, —, ere</b>\n<b>1)</b> усердно работать (над чём-л.), прилежно заниматься (alicui rei: s. legibus cognoscendis <i>C</i>; s. agriculturae <i>Cs</i>): qui somno studeat <i>Pl</i> любитель (побольше) поспать;\n<b>2)</b> стараться: omnibus modis huic rei studendum, ut... <i>Cs</i> здесь надо всячески стараться, чтобы... || добиваться, стремиться (s. laudi <i>C</i>; se praestare ceteris <i>Sl</i>): scire studeo, quid egeris <i>C</i> я желал бы знать, что ты сделал; communibus commodis s. <i>PJ</i> посвящать себя общественному благу; eadem aeque s. <i>Ter</i> иметь одни и те же симпатии (интересы) || упражняться (s. memoriae <i>Q</i>): novis rebus s. <i>C, Cs</i> помышлять о перевороте, стремиться к ниспровержению существующего строя; s. (in) aliquid добиваться чего-л. (s. hoc unum <i>C, H</i>);\n<b>3)</b> быть на (чьей-л.) стороне, быть настроенным, действовать (в пользу кого-л.), поддерживать, сочувствовать, быть преданным (alicui <i>или</i> alicui rei, <i>редко</i> alicujus <i>C etc.</i>): Scauro studet <i>C</i> он стоит за Скавра; qui rebus Atheniensium student <i>Nep</i> сторонники Афин; neque s. neque odisse <i>Su</i> не питать ни склонности, ни ненависти;\n<b>4)</b> изучать, учиться (diligenter apud aliquem <i>Q</i>): anni, quibus studuimus <i>Q</i> годы нашего учения; ab parvulis labori ac duritiae s. <i>Cs</i> с малолетства приучаться к суровой трудовой жизни; s. litteris (artibus) <i>C</i> предаться изучению, заниматься науками."],
  ["studiose", "<big>studiose</big>\n\n<b>studiose</b> [studiosus]\n<b>1)</b> старательно, усердно, ревностно (s. diligenterque aliquid facere <i>C</i>);\n<b>2)</b> намеренно, умышленно (de aliquo malitiose dicere <i>C</i>)."],
  ["studiosus", "<big>studiosus</big>\n\n<b>studiosus, a, um</b> [studium]\n<b>1)</b> старательный, усердный, ревностный, прилежный: s. alicujus rei, <i>реже</i> alicui rei прилежно занимающийся чём-л. (s. iitterarum <i>Nep, Pt</i>);\n<b>2)</b> стремящийся (к чему-л.), усиленно добивающийся (чего-л.) (s. sapientiae <i>Q</i>) <i>или</i> весьма расположенный, склонный, питающий страсть (к чему-либо) (s. venandi <i>C</i>); s. in argento <i>Pt</i> большой любитель серебряных изделий;\n<b>3)</b> приверженный, привязанный, преданный, стоящий на (чьей-л.) стороне (s. Catonis <i>Nep</i>; s. nobilitatis <i>C</i>);\n<b>4)</b> преданный наукам, пытливый, любознательный (lector <i>Ap</i>); учёный (disputatio <i>Q</i>); учащийся, изучающий (alicujus doctrinae <i>Q</i>); посвященный наукам (otium <i>PJ</i>)."],
  ["studium", "<big>studium</big>\n\n<b>studium, i</b> <i>n</i> [studeo]\n<b>1)</b> старание, усердие, рвение (s. laudis <i>C</i>): magno (<i>или</i> summo) studio discere <i>C</i> весьма прилежно учиться; s. conferre in (ad) aliquid <i>C</i> <i>или</i> s. ponere in aliqua re <i>C</i> приложить старание к чему-л.;\n<b>2)</b> влечение, стремление, страсть: s. quaestus (<i>или</i> lucri) <i>C</i> страсть к наживе, корыстолюбие; s. habendi <i>C</i> любостяжание; s. novarum rerum <i>Sl</i> стремление к реформам, желание совершить переворот; studia civilia <i>Sl</i> гражданские раздоры; vitae s. <i>C</i> образ жизни; studio <i>C etc.</i> по влечению, из склонности <i>или</i> намеренно, умышленно, <i>тж.</i> усердно (gradum celerare <i>V</i>); fugae studio <i>V</i> из желания (поскорее) бежать; bono studio <i>Ap</i> с благими намерениями; studio ac voluntate <i>C</i> всеми помыслами, всей душой;\n<b>3)</b> деятельное участие (s. favorque transibit <i>PJ</i>); приверженность, привязанность, расположение, преданность (studiis odiisque carere <i>Lcn</i>); благожелательность, благосклонность (alicujus rei <i>или</i> in aliquid, in <i>или</i> erga aliquem <i>C etc.</i>): s. rei publicae <i>Sl, T</i> любовь к отечеству, патриотизм; non studio, sed officio <i>C</i> не по сердечному влечению, а из чувства долга; s. atque aures <i>C</i> сочувственное внимание, благосклонное отношение; senatum in studia diducere <i>T</i> — <i>см.</i> diduco 3;\n<b>4)</b> (<i>тж.</i> s. partium <i>C</i>) пристрастие, пристрастность: sine ira et studio <i>T</i> без гнева, (но) и без пристрастия; studia externa <i>T</i> пристрастие к (всему) иноземному; sine ullo studio <i>C</i> совершенно беспристрастно;\n<b>5)</b> занятие, профессия (s. rerum rusticarum <i>Col</i>); любимое занятие (suo quisque studio maxime ducitur <i>C</i>);\n<b>6)</b> научные занятия, изучение (s. litterarum <i>C</i>; in studiis vitam agere <i>O</i>): cruda studia <i>Pt</i> незаконченное обучение, <i>т. е.</i> недоучившаяся молодёжь;\n<b>7)</b> наука, отрасль науки (studia adulescentiam alunt <i>C</i>)."],
  ["stulte", "<big>stulte</big>\n\n<b>stulte</b> [stultus]\nглупо, неумно, нелепо <i>Pl, C, L etc.</i>: haud s. sapit <i>Ter</i> он совсем не глуп."],
  ["stultiloquentia", "<big>stultiloquentia</big>\n\n<b>stultiloquentia, ae</b> <i>f</i> [stulte + loquor]\nглупые речи, вздор, чепуха <i>Pl</i>."],
  ["stultiloquium", "<big>stultiloquium</big>\n\n<b>stultiloquium, i</b> <i>n</i> <i>Pl</i>\n= stultiloquentia."],
  ["stultiloquus", "<big>stultiloquus</big>\n\n<b>stulti–loquus, a, um</b>\nговорящий глупости <i>Pl</i>."],
  ["stultitia", "<big>stultitia</big>\n\n<b>stultitia, ae</b> <i>f</i> [stultus]\nглупость, неразумие, скудоумие, недомыслие (aliquid stultitia facere <i>Ter</i>; hominum ineptiae ac stultitiae <i>C</i>)."],
  ["stultities", "<big>stultities</big>\n\n<b>stultities, ei</b> <i>f</i> <i>Ap</i>\n(<i>v. l.</i>)= stultitia."],
  ["stultividus", "<big>stultividus</big>\n\n<b>stultividus, a, um</b> [stultus + video]\nимеющий глупые взгляды, нелепо судящий, глупый <i>Pl</i>."],
  ["stultus", "<big>stultus</big>\n\n<b>I stultus, a, um</b>\nглупый, неразумный (persona <i>C</i>; laetitia <i>Sl</i>); глупо проведённый (dies <i>Tib</i>).\n\n<b>II stultus, i</b> <i>m</i>\nглупец, дурак <i>Ter, C etc.</i>"],
  ["stupa", "<big>stupa</big>\n\n<b>stupa, ae</b> <i>f</i>\n<i>v. l.</i>.= stuppa."],
  ["stupefacio", "<big>stupefacio</big>\n\n<b>stupefacio, feci, factum, ere</b> [stupeo + facio]\n(<i>pass.</i> stupefio)\n<b>1)</b> оглушать, лишать чувств (membra stupefecerat horror <i>Sil</i>); ошеломлять, изумлять (agmina stupefacta <i>VF</i>);\n<b>2)</b> заглушать (publicus pavor privatos luctus stupefecit <i>L</i>)."],
  ["stupefio", "<big>stupefio</big>\n\n<b>stupe–fio, factus sum, fieri</b>\nлишаться чувств, <i>перен.</i> быть поражённым, быть ошеломлённым, остолбенеть, изумиться (aliqua re <i>V, Prp etc.</i>)."],
  ["stupendus", "<big>stupendus</big>\n\n<b>stupendus, a, um</b> [stupeo]\nизумительный, удивительный (aliqua re <i>VF</i>)."],
  ["stupeo", "<big>stupeo</big>\n\n<b>stupeo, ui, —, ere</b>\n<b>1)</b> останавливаться (stupuit Ixionis orbis <i>O</i>); быть неподвижным (unda, quae stupet pigro lacu <i>M</i>); застывать, замирать, неметь (stupuerunt verba palato <i>O</i>): curae ingentes stupent <i>SenT</i> великие потрясения безмолвны || затихать, прекращаться (seditio stupuit <i>L</i>);\n<b>2)</b> лишиться чувств, быть без сознания (stupentes frigida aqua spargere <i>Sen</i>);\n<b>3)</b> быть поражённым, изумляться (haec quum loqueris, nos stupemus <i>C</i>): s. (in) aliqua re <i>или</i> (ad) aliquid <i>V, H, Sen, Q etc.</i> поражаться чему-л., быть изумлённым <i>или</i> восхищённым чём-л.; quid nunc stupes? <i>Pt</i> ты чего уставился (на меня)?"],
  ["stupesco", "<big>stupesco</big>\n\n<b>stupesco, —, —, ere</b> [<i>inchoat. к</i> stupeo]\nстолбенеть, поражаться, изумляться <i>C, Vlg, Aug</i>."],
  ["stupeus", "<big>stupeus</big>\n\n<b>stupeus, a, um</b> <i>v. l.</i>\n= stuppeus."],
  ["stupiditas", "<big>stupiditas</big>\n\n<b>stupiditas, atis</b> <i>f</i> [stupidus]\nтупоумие, недомыслие, ограниченность, глупость <i>Acc, C</i>."],
  ["stupidus", "<big>stupidus</big>\n\n<b>stupidus, a, um</b> [stupeo]\n<b>1)</b> остолбенелый, поражённый: studio s. <i>Ter</i> увлечённый до потери рассудка || озадаченный (s. sine animo asto <i>Pl</i>); растерявшийся (timore <i>rhH</i>);\n<b>2)</b> тупоумный, глупый (s. et bardus <i>C</i>)."],
  ["stupor", "<big>stupor</big>\n\n<b>stupor, oris</b> <i>m</i>\n<b>1)</b> оцепенение, оглушённость (sensus <i>C</i>);\n<b>2)</b> неподвижность, неуклюжесть, тяжеловесность (linguae <i>C</i>);\n<b>3)</b> замешательство, смущение, изумление (s. et admiratio incessit omnes <i>Just</i>);\n<b>4)</b> тупоумие, бестолковость, глупость (pecudis <i>C</i>)."],
  ["stuporatus", "<big>stuporatus</big>\n\n<b>stuporatus, a, um</b> [stupor]\nвызывающий изумление (gloria <i>Tert</i>)."],
  ["stuppa", "<big>stuppa</big>\n\n<b>stuppa, ae</b> <i>f</i>\nпакля, конопать <i>V, Cs, L etc.</i>"],
  ["stupparius", "<big>stupparius</big>\n\n<b>stupparius, a, um</b> [stuppa]\nслужащий для обработки пакли (malleus <i>PM</i>)."],
  ["stuppeus", "<big>stuppeus</big>\n\n<b>stuppeus, a, um</b> [stuppa]\nсделанный из пакли (vincula <i>V</i>): stuppea flamma <i>V</i> горящая пакля; stuppea messis <i>GF</i> пенька <i>или</i> конопля."],
  ["stuprator", "<big>stuprator</big>\n\n<b>stuprator, oris</b> <i>m</i> [stupro]\nосквернитель, растлитель <i>Sen, Q, Su, Ap</i>."],
  ["stupro", "<big>stupro</big>\n\n<b>stupro, avi, itum, are</b>\nосквернять (pulvinar <i>C</i>); растлевать, портить (mores <i>Tert</i>); бесчестить, позорить (filiam <i>Pl, C</i>)."],
  ["stuprosus", "<big>stuprosus</big>\n\n<b>stuprosus, a, um</b> [stuprum]\nразвращённый, распутный (mens <i>VM</i>)."],
  ["stuprum", "<big>stuprum</big>\n\n<b>stuprum, i</b> <i>n</i>\nбесчестье, позор, срам, разврат: inferre (offerre) alicui s. <i>C</i> обесчестить кого-л.; stupri consuetudinem habere cum aliquo <i>Su</i> находиться с кем-л. в преступной связи."],
  ["sturninus", "<big>sturninus</big>\n\n<b>sturninus, a, um</b> [sturnus]\nсерый как скворец (equus <i>Hier</i>)."],
  ["sturnus", "<big>sturnus</big>\n\n<b>sturnus, i</b> <i>m</i>\nскворец (<i>Sturnus vulgaris</i>, L.) <i>PM, M, St etc.</i>"],
  ["Stygialis", "<big>Stygialis</big>\n\n<b>Stygialis, e</b> <i>и</i> <b>Stygius, a, um</b> [Styx]\n<b>1)</b> стигийский: S. Juppiter <i>или</i> rex <i>V</i> = Pluto; Stygia Juno <i>St</i> = Proserpina; Stygia cumba <i>V</i> = cumba Charonis; Stygia palus <i>V</i> = Styx;\n<b>2)</b> <i>поэт.</i> адский, страшный, зловещий, смертоносный (vis <i>V</i>; bubo <i>O</i>);\n<b>3)</b> колдовской, волшебный (preces <i>SenT</i>; carmen <i>Lcn</i>)."],
  ["stylobates", "<big>stylobates</big>\n\n<b>stylobates, ae</b> <i>и</i> <b>is</b> <i>m</i> (<i>греч.</i>)\nстилобат, <i>общий постамент ряда колонн</i> <i>Vr, Vtr</i>."],
  ["stylus", "<big>stylus</big>\n\n<b>stylus, i</b> <i>m</i> <i>v. l.</i>\n= stilus."],
  ["Stymphala", "<big>Stymphala</big>\n\n<b>Stymphala, orum</b> <i>n</i> <i>Lcn</i>\n= Stymphalus."],
  ["Stymphalicus", "<big>Stymphalicus</big>\n\n<b>Stymphalicus, a, um</b> <i>Pl</i>\n= Stymphalius."],
  ["Stymphalis", "<big>Stymphalis</big>\n\n<b>Stymphalis, idis</b> <i>f</i> [Stymphalus]\nстимфальская (undae <i>O</i>): Stymphalides (<i>sc.</i> aves) <i>M</i> Стимфалиды, <i>хищные меднокрылые чудовища, истреблённые Геркулесом.</i>"],
  ["Stymphalius", "<big>Stymphalius</big>\n\n<b>Stymphalius, a, um</b> [Stymphalus]\nстимфальский (monstra <i>Ctl</i>)."],
  ["Stymphalus", "<big>Stymphalus</big>\n\n<b>Stymphalus, i</b> <i>m</i> <i>и</i> <b>Stymphalum, i</b> <i>n</i>\nСтимфал, <i>область, город, гора и озеро в Аркадии, где жили хищные Стимфалиды</i> <i>PM, Su, St</i>."],
  ["stypsis", "<big>stypsis</big>\n\n<b>stypsis, is</b> <i>f</i> (<i>греч.</i>)\nвяжущий вкус (vini <i>CA, Eccl</i>)."],
  ["stypteria", "<big>stypteria</big>\n\n<b>stypteria, ae</b> <i>f</i> (<i>греч.</i>)\nквасцы <i>Dig</i>."],
  ["stypticus", "<big>stypticus</big>\n\n<b>stypticus, a, um</b> (<i>греч.</i>)\nвяжущий, скрепляющий (medicamenta <i>PM</i>); терпкий (vinum <i>Pall</i>)."],
  ["styracinus", "<big>styracinus</big>\n\n<b>styracinus, a, um</b>\nприготовленный из стиракса (oleum <i>CA</i>)."],
  ["styrax", "<big>styrax</big>\n\n<b>styrax, acis</b> <i>m</i> (<i>греч.</i>)\n<b>1)</b> стиракс <i>или</i> сторакс, <i>дерево Styrax officinale</i> <i>V, PM, CA</i>;\n<b>2)</b> ароматический бальзам деревьев <i>Styrax officinale <i>или</i> Liquidambar orientale</i> <i>PM</i>."],
  ["Styx", "<big>Styx</big>\n\n<b>Styx</b>, <i>gen.</i> <b>Stygis</b> <i>и</i> <b>Stygos</b> <i>f</i>\nСтикс:\n<b>1)</b> <i>река или озеро в подземном царстве</i> (<i>водами Стикса клялись боги</i>: per Stygias aquas dejerare <i>Ap</i>) <i>C, V, O etc.</i>;\n<b>2)</b> <i>поэт.</i> подземное царство <i>V, O, M etc.</i>;\n<b>3)</b> <i>речка в сев. Аркадии, вода которой считалась ледяной и ядовитой</i> <i>Sen, QC</i>."],
  ["su-", "<big>su-</big>\n\n<b>su-</b>\n<i>приставка</i> = sub- <i>перед начальным</i> s <i>с последующей согласной основного слова</i> (<i>напр.</i> su–spicio; <i>однако</i>: sub–sterno, sub–stituo, sub–sto <i>и др.</i>)."],
  ["sua", "<big>sua</big>\n\n<b>sua, orum</b> <i>n</i> [suus]\n(<i>sc.</i> bona) имущество, добро, достояние: se suaque defendere <i>Cs</i> защищать себя и своё достояние; omnia sua secum portare <i>Sen, VM</i> всё своё имущество носить с собой."],
  ["Suada", "<big>Suada</big>\n\n<b>Suada, ae</b> <i>v. l.</i>\n= Suadela."],
  ["Suadela", "<big>Suadela</big>\n\n<b>I Suadela, ae</b> <i>f</i> [suadeo]\nСвада, <i>богиня убедительности, красноречия</i> <i>Enn, H</i>.\n\n<b>II suadela, ae</b> <i>f</i>\nубеждение, увещевание <i>Pl, Ap</i>."],
  ["suadenter", "<big>suadenter</big>\n\n<b>suadenter</b>\nубедительно (In litibus loqui <i>Eccl</i>)."],
  ["suadeo", "<big>suadeo</big>\n\n<b>suadeo, suasi, suasum, ere</b> [<i>одного корня с</i> suavis]\n<b>1)</b> советовать, предлагать, рекомендовать (succurrere alicui <i>V</i>; pacem <i>C</i>); давать совет (alicui de aliqua re <i>Q</i>);\n<b>2)</b> приглашать, призывать (somnum inire <i>V</i>): sidera somnos suadent <i>V</i> звёзды зовут ко сну; suadeo cenemus <i>Pt</i> предлагаю приступить к обеду; s. brevem fructum <i>Cld</i> манить кратковременными наслаждениями; tantum religio potuit s. malorum <i>Lcr</i> вот какие злодеяния смогло внушить суеверие; autumno suadente <i>Lcr</i> по велению осени;\n<b>3)</b> (= persuadeo) уговаривать, убеждать (ita faciam, ut suades <i>C</i>): s. alicui <i>C etc.</i> (<i>редко</i> aliquem <i>Ap, Tert</i>) убеждать кого-л."],
  ["suadibilis", "<big>suadibilis</big>\n\n<b>suadibilis, e</b> [suadeo]\n<b>1)</b> сговорчивый, уступчивый (sapientia <i>Vlg</i>);\n<b>2)</b> убедительный (doctrina <i>Aug</i>)."],
  ["suadus", "<big>suadus</big>\n\n<b>suadus, a, um</b> [suadeo]\nубедительный (facundia <i>Symm</i>); влекущий, манящий (cruor s. manibus <i>St</i>)."],
  ["Suani", "<big>Suani</big>\n\n<b>Suani, orum</b> <i>m</i>\nсваны, <i>племя на Черноморском побережье Колхиды</i> <i>PM</i>."],
  ["suarius", "<big>suarius</big>\n\n<b>I suarius, a, um</b> [sus]\nсвиной (forum <i>Dig</i>): negotiator s. <i>PM</i> свиноторговец.\n\n<b>II suarius, i</b> <i>m</i>\nсвинопас <i>PM</i>; свиноторговец <i>Symm</i>."],
  ["suasi", "<big>suasi</big>\n\n<b>suasi</b> <i>pf. к</i>\nsuadeo."],
  ["suasio", "<big>suasio</big>\n\n<b>suasio, onis</b> <i>f</i> [suadeo]\nуговаривание, увещевание, убеждение <i>Sen, AG</i>; предложение, рекомендация (s. legis Serviliae <i>C</i>)."],
  ["suasor", "<big>suasor</big>\n\n<b>suasor, oris</b> <i>m</i> [suadeo]\nсоветующий, уговаривающий, рекомендующий (s. legis <i>L</i>): me suasore et impulsore <i>Pl</i> по моему совету и побуждению."],
  ["suasoria", "<big>suasoria</big>\n\n<b>suasoria, ae</b> <i>f</i> [suasorius]\n(<i>sc.</i> oratio) увещевательная <i>или</i> защитительная речь (<i>посвященная убеждению в чём-либо, защите или рекомендации чего-л.</i>) <i>Q, Pt</i>."],
  ["suasorius", "<big>suasorius</big>\n\n<b>suasorius, a, um</b> [suadeo]\n<b>1)</b> убеждающий, увещевательный (pars quae suasoria dicitur <i>Q</i>);\n<b>2)</b> склоняющий, имеющий целью уговорить, обольстительный (oscula <i>Ap</i>)."],
  ["suasum", "<big>suasum</big>\n\n<b>I suasum, i</b> <i>m</i> [suadeo]\nубеждение, увещевание <i>Tert</i>.\n\n<b>II suasum, i</b> <i>n</i>\nпятно от воды с сажей <i>Pl</i>."],
  ["suasus", "<big>suasus</big>\n\n<b>suasus, us</b> <i>m</i> <i>Ter, Dig, Ap</i>\n= suasio."],
  ["suave", "<big>suave</big>\n\n<b>suave</b> <i>V, H, Aug</i>\n= suaviter."],
  ["suaveolens", "<big>suaveolens</big>\n\n<b>suave–olens, entis</b> <i>adj.</i> (<i>тж. раздельно</i>)\nприятно пахнущий, душистый <i>Ctl, Priap</i>."],
  ["suaveolentia", "<big>suaveolentia</big>\n\n<b>suaveolentia, ae</b> <i>f</i>\nблаговоние, благоухание <i>Sid, Aug</i>."],
  ["suaviatio", "<big>suaviatio</big>\n\n<b>suaviatio</b> <i>и</i> <b>saviatio, onis</b> <i>f</i>\nпоцелуй, ласка <i>Pl, AG</i>."],
  ["suavidicus", "<big>suavidicus</big>\n\n<b>suavidicus, a, um</b> [suave + dico]\nприятно говорящий, сладостный (versus <i>Lcr</i>)."],
  ["suavifico", "<big>suavifico</big>\n\n<b>suavi–fico, —, atum, are</b> [facio]\nделать приятным (aliquid <i>Eccl, Is</i>)."],
  ["suavillum", "<big>suavillum</big>\n\n<b>suāvillum</b> <i>v. l.</i>\n= savillum."],
  ["suaviloquens", "<big>suaviloquens</big>\n\n<b>suāviloquēns, entis</b> <i>adj.</i>\nсладкоречивый, сладостный (carmen <i>Lcr</i>)."],
  ["suaviloquentia", "<big>suaviloquentia</big>\n\n<b>suāviloquentia, ae</b> <i>f</i> [suaviloquens]\nсладкоречивость, приятная речь <i>C, Aug</i>."],
  ["suaviloquium", "<big>suaviloquium</big>\n\n<b>suāviloquium, ī</b> <i>m</i> <i>Aug</i>\n= suaviloquentia."],
  ["suaviludius", "<big>suaviludius</big>\n\n<b>suāvi–lūdius, ī</b> <i>m</i> [ludus]\nлюбитель зрелищ <i>Tert</i>."],
  ["suaviolum", "<big>suaviolum</big>\n\n<b>suaviolum, i</b> <i>m</i> [<i>demin. к</i> suavium]\nнежный поцелуй <i>Ctl</i>."],
  ["suavior", "<big>suavior</big>\n\n<b>suavior, atus sum, ari</b> <i>depon.</i> [suavium]\nцеловать (os oculosque <i>Ctl</i>; aliquem <i>C</i>)."],
  ["suavis", "<big>suavis</big>\n\n<b>suavis, e</b>\nприятный, привлекательный (odor <i>C</i>; vox <i>AG</i>; color <i>PM</i>): nondum mihi suave erat venire <i>Pt</i> у меня не было ещё желания прийти || милый, любезный (homo <i>C, Ter</i>); вкусный (<i>sc.</i> cibus <i>Pl, H</i>); сладкий (quod suave est aliis, aliis fit amarum <i>Lcr</i>)."],
  ["suavisaviatio", "<big>suavisaviatio</big>\n\n<b>suavi–saviatio, onis</b> <i>f</i>\nнежные поцелуи <i>Pl</i>."],
  ["suavisonus", "<big>suavisonus</big>\n\n<b>suavisonus, a, um</b> [suavis + sono]\nсладкозвучный (echo <i>Acc</i>)."],
  ["suavitas", "<big>suavitas</big>\n\n<b>suavitas, atis</b> <i>f</i> [suavis]\nприятность, привлекательность (oris, vocis <i>Nep</i>; odoris, cibi <i>C</i>); удовольствие, наслаждение, прелесть (vitae <i>C</i>); приятность, любезность (orationis, hominis <i>C</i>)."],
  ["suaviter", "<big>suaviter</big>\n\n<b>suaviter</b> [suavis]\nприятно (legere <i>PJ</i>); с удовольствием (meminisse <i>C</i>): sibi s. facere <i>Pt</i> вкусно (по)есть, пировать; s. sit potius <i>Pt</i> лучше уж будем веселиться || <i>в ответах</i> очень хорошо, прекрасно: quid agis? — S. <i>H</i> как дела? — Неплохо."],
  ["suavitudo", "<big>suavitudo</big>\n\n<b>suavitudo, inis</b> <i>f</i> <i>Pl, rhH</i>\n= suavitas."],
  ["suavium", "<big>suavium</big>\n\n<b>suavium, i</b> <i>m</i>\n<b>1)</b> рот, сложенный для поцелуя, ротик <i>Pl, Ap, AG</i>;\n<b>2)</b> поцелуй (dare alicui s. <i>Pl, C</i>): meum s. <i>ласк. Ter</i> душенька."],
  ["sub", "<big>sub</big>\n\n<b>I sub</b> <i>praep. cum acc. и abl.</i>\n<b>1)</b> под (s. terra habitare <i>C</i>; vitam s. divo agere <i>H</i>; exercitum s. jugum <i>и</i> s. jugo mittere <i>Cs, L</i>; sub nomine pacis bellum latet <i>C</i>): s. oculos (s. sensum) cadere (subjectum esse) <i>C</i> быть чувственно воспринимаемым <i>или</i> явственно зримым; s. imperium (potestatem) redigere <i>Nep</i> (subjimgere <i>C</i>) подчинить; s. oculis alicujus <i>Cs</i> на глазах у кого-л.; sub armis esse <i>Cs</i> находиться при оружии; s. sarcinis <i>Cs</i> с поклажей, неся на себе дорожные вещи; s. septemtrionibus positus <i>Cs</i> расположенный к северу (на севере);\n<b>2)</b> близ, у, к, при, до: s. oriente(m) <i>Fl</i> к востоку; s. monte <i>Cs</i> у подошвы горы; s. hac condicione <i>L, Su</i> с этим условием; s. exceptione <i>Su</i> с оговоркой; s. poena mortis <i>Su</i> под страхом смертной казни; s. specie <i>Just</i> (s. titulo <i>L, PJ</i>) alicujus rei под предлогом (под видом) чего-л.; s. finem adventare <i>V</i> подходить к концу (к цели); s. frigido sudore <i>CC</i> в холодном поту; finem s. utrumque <i>H</i> по обе стороны границы; s. moenia succedere <i>Cs</i> подступать к стенам; s. muro consistere <i>Cs</i> стоять у стен; turrim s. astra educere <i>V</i> возводить башню до небес; s. ictum venire <i>L</i> подойти на расстояние выстрела; esse s. ictu <i>Sen</i> <i>или</i> s. jactu teli <i>L</i> находиться на расстоянии выстрела (в пределах досягаемости метательных снарядов); s. sinistra <i>Cs</i> слева; s. vite <i>Col</i> в тени виноградника; silvis s. altis <i>O</i> в глубине леса; s. pectore <i>V</i> в глубине души;\n<b>3)</b> около, к моменту наступления, под (s. eodem tempore <i>O</i>; s. idem tempus <i>T</i>): s. noctem <i>H, Cs</i> <i>или</i> s. nocte <i>V</i> к ночи, под вечер; s. luce <i>L, O</i> <i>и</i> s. lucem <i>Cs</i> на рассвете;\n<b>4)</b> во время, в течение: s. acie <i>L</i> в разгар сражения; s. decessu suo <i>Hirt</i> во время его отъезда; s. die nitido <i>O</i> средь бела дня; primis s. annis <i>O</i> в первые годы; s. bruma <i>Cs</i> во время зимнего солнцестояния; s. exitu anni <i>L</i> на исходе года; s. hoc casu <i>V</i> при таких обстоятельствах;\n<b>5)</b> вслед за, тотчас же после: s. haec dicta (s. hanc vocem) <i>L</i> после этих слов;\n<b>6)</b> под властью, в царствование (s. Augusto <i>Sen, PM</i>; s. rege pio <i>Cld</i>); под управлением, под начальством (s. aliquo <i>и</i> sub alicujus signis militare <i>L, VP</i>; egregio s. principe <i>Cld</i>); под руководством (s. aliquo magistro artes edoceri <i>L</i>).\n\n<b>II sub-</b>\n<i>приставка, означающая главным образом</i>:\n<b>1)</b> <i>нахождение внутри или под</i> (sub–aeratus); <i>помещение внутрь или под</i> (sub–do); <i>скрытость</i> (sub–ausculto);\n<b>2)</b> <i>немного, несколько</i> (sub–agrestis)."],
  ["subabsurde", "<big>subabsurde</big>\n\n<b>sub–absurde</b>\nнесколько нескладно, глуповато (dicere <i>C</i>)."],
  ["subabsurdus", "<big>subabsurdus</big>\n\n<b>sub–absurdus, a, um</b>\nнесколько нескладный, довольно нелепый, глуповатый (subabsurda dicendo risus moventur <i>C</i>): tempus subabsurdum <i>C</i> неудачно выбранный момент."],
  ["subaccuso", "<big>subaccuso</big>\n\n<b>sub–accuso, —, —, are</b>\nнемного порицать, несколько осуждать (aliquem <i>и</i> aliquid <i>C</i>)."],
  ["subacer", "<big>subacer</big>\n\n<b>sub–acer, acris, acre</b>\nнесколько острый <i>или</i> едкий (gustus alicujus rei <i>Is</i>)."],
  ["subacidus", "<big>subacidus</big>\n\n<b>sub–acidus, a, um</b>\nнемного кислый, кисловатый (vinum <i>Cato</i>)."],
  ["subactio", "<big>subactio</big>\n\n<b>subactio, onis</b> <i>f</i> [subigo]\n<b>1)</b> обработка, размешивание (bacillorum subactiones, <i>sc.</i> calcis <i>Vtr</i>); разжёвывание (ciborum <i>Eccl</i>);\n<b>2)</b> образование, развитие (ingenii <i>C</i>)."],
  ["subactor", "<big>subactor</big>\n\n<b>subactor, oris</b> <i>m</i> [subigo]\nрастлитель, развратитель <i>Lampr</i>."],
  ["subactus", "<big>subactus</big>\n\n<b>I subactus, a, um</b> <i>part. pf. к</i>\nsubigo.\n\n<b>II subactus, us</b> <i>m</i>\nобработка, разминание (<i>sc.</i> panis <i>PM</i>)."],
  ["subadjuva", "<big>subadjuva</big>\n\n<b>sub–adjuva, ae</b> <i>m</i>\nмладший помощник <i>CJ</i>."],
  ["subadmoveo", "<big>subadmoveo</big>\n\n<b>sub–admoveo, —, —, ere</b>\nпридвигать, приближать <i>Col</i>."],
  ["subadr-", "<big>subadr-</big>\n\n<b>subadr-</b> <i>v. l.</i>\n= subarr-."],
  ["subads-", "<big>subads-</big>\n\n<b>subads-</b> <i>v. l.</i>\n= subass-."],
  ["subaemulatio", "<big>subaemulatio</big>\n\n<b>sub–aemulatio, onis</b> <i>f</i>\nскрытое соперничество <i>Ambr</i>."],
  ["subaeratus", "<big>subaeratus</big>\n\n<b>sub–aeratus, a, um</b>\nсодержащий внутри медь (aurum <i>Pers</i>)."],
  ["subagit-", "<big>subagit-</big>\n\n<b>subagit-</b> <i>v. l.</i>\n= subigit-."],
  ["subagrestis", "<big>subagrestis</big>\n\n<b>sub–agrestis, e</b>\nгрубоватый (consilium <i>C</i>; ingenium <i>Amm</i>)."],
  ["subalapa", "<big>subalapa</big>\n\n<b>sub–alapa, ae</b> <i>или</i> <b>subalapo, onis</b> <i>m</i>\nнегодяй, мошенник <i>Pt</i> (<i>v. l. к</i> sub alapa)."],
  ["subalaris", "<big>subalaris</big>\n\n<b>sub–alaris, e</b>\n<b>1)</b> находящийся под крыльями (plumae <i>Lampr</i>);\n<b>2)</b> носимый под мышкой (telum <i>Nep</i>)."],
  ["subalbens", "<big>subalbens</big>\n\n<b>subalbens, entis</b> <i>Eccl</i>\n= subalbicans."],
  ["subalbicans", "<big>subalbicans</big>\n\n<b>sub–albicans, antis</b> <i>adj.</i>\nбеловатый <i>Vr</i>."],
  ["subalbidus", "<big>subalbidus</big>\n\n<b>subalbidus, a, um</b> <i>CC, PM, CA</i>\n= subalbicans."],
  ["subalbus", "<big>subalbus</big>\n\n<b>subalbus, a, um</b> <i>Vr</i>\n= subalbicans."],
  ["subalpinus", "<big>subalpinus</big>\n\n<b>sub–alpinus, a, um</b>\nприальпийский (montes <i>PM</i>)."],
  ["subalternus", "<big>subalternus</big>\n\n<b>sub–alternus, a, um</b>\nподчинённый <i>Boet, Eccl</i>."],
  ["subamarus", "<big>subamarus</big>\n\n<b>sub–amarus, a, um</b>\nгорьковатый <i>C</i>."],
  ["subaquaneus", "<big>subaquaneus</big>\n\n<b>sub–aquaneus, a, um</b>\nподводный (viventia <i>Tert</i>)."],
  ["subaquilus", "<big>subaquilus</big>\n\n<b>sub–aquilus, a, um</b>\nнесколько бурый, довольно тёмный (corpus <i>Pl</i>; vultus <i>Treb</i>)."],
  ["subarator", "<big>subarator</big>\n\n<b>sub–arator, oris</b> <i>m</i>\nпашущий рядом <i>PM</i>."],
  ["subare", "<big>subare</big>\n\n<b>subare</b> <i>inf. к</i>\nsubo."],
  ["subaresco", "<big>subaresco</big>\n\n<b>sub–aresco, —, —, ere</b>\nслегка высыхать, подсыхать <i>Vtr</i>."],
  ["subargutulus", "<big>subargutulus</big>\n\n<b>sub–argutulus, a, um</b>\nплутоватый, с хитрецой <i>AG</i>."],
  ["subarmale", "<big>subarmale</big>\n\n<b>subarmale, is</b> <i>n</i> <i>или</i> <b>subarmalis, is</b> <i>f</i> [armus]\n<i>род платья, верхний край которого, предположительно, проходил под мышкой</i> <i>Treb, Vop, Spart</i>."],
  ["subaro", "<big>subaro</big>\n\n<b>sub–aro, —, (atum), are</b>\nподрыть плугом (aliquid <i>PM</i>)."],
  ["subarroganter", "<big>subarroganter</big>\n\n<b>sub–arroganter</b>\nнесколько надменно, немного высокомерно <i>C</i>."],
  ["subasper", "<big>subasper</big>\n\n<b>sub–asper, era, erum</b>\nнемного шероховатый, грубоватый <i>CC</i>."],
  ["subassentiens", "<big>subassentiens</big>\n\n<b>sub–assentiens, entis</b> <i>adj.</i>\nидущий на некоторые уступки, отчасти соглашающийся: subassentientibus umeris <i>Q</i> как бы в виде уступки пожимая плечами."],
  ["subasso", "<big>subasso</big>\n\n<b>sub–asso, —, atum, are</b>\nслегка <i>или</i> постепенно поджаривать <i>CA</i>."],
  ["subatio", "<big>subatio</big>\n\n<b>subatio, onis</b> <i>f</i> [subo]\nтечка (<i>у свиней</i>) <i>PM</i>."],
  ["subaudio", "<big>subaudio</big>\n\n<b>sub–audio, —, itum, ire</b>\n<b>1)</b> слегка слышать, улавливать краем уха (s. nocturnis vocibus <i>Ap</i>);\n<b>2)</b> подразумевать: subaudito <i>Dig</i> подразумевая (при этом)."],
  ["subauratus", "<big>subauratus</big>\n\n<b>subauratus, a, um</b>\nслегка позолоченный (anulus <i>Pt</i>)."],
  ["subausculto", "<big>subausculto</big>\n\n<b>sub–ausculto, —, —, are</b>\nподслушивать, прислушиваться <i>Pl, C</i>."],
  ["subausterus", "<big>subausterus</big>\n\n<b>sub–austerus, a, um</b>\nнемного терпкий (vinum <i>CC</i>)."],
  ["subbajulo", "<big>subbajulo</big>\n\n<b>sub–bajulo, —, —, are</b>\nподнимать, взваливать (tantam molem scapulis <i>Aug</i>)."],
  ["subbalbe", "<big>subbalbe</big>\n\n<b>sub–balbe</b>\nнемного заикаясь <i>Spart</i>."],
  ["Subballio", "<big>Subballio</big>\n\n<b>Sub–ballio, onis</b> <i>m</i> <i>шутл.</i>\nПодбаллион, <i>т. е.</i> раб Баллиона <i>Pl</i>."],
  ["subbasilicanus", "<big>subbasilicanus</big>\n\n<b>sub–basilicanus, a, um</b>\n(постоянно) гуляющий вокруг базилик, праздношатающийся <i>Pl</i>."],
  ["subbibo", "<big>subbibo</big>\n\n<b>sub–bibo, bibi, —, ere</b>\nнемного отпить, отхлебнуть <i>Su</i>."],
  ["subblandior", "<big>subblandior</big>\n\n<b>sub–blandior, —, iri</b> (<i>арх. fut.</i> subblandibor)\nслегка польстить, приласкать (alicui <i>Pl</i>)."],
  ["subbrachia", "<big>subbrachia</big>\n\n<b>sub–brachia, orum</b> <i>n</i> (= alae)\nподмышки <i>Is</i>."],
  ["subbrevis", "<big>subbrevis</big>\n\n<b>sub–brevis, e</b>\nкоротковатый (folium saliuncae <i>PM</i>)."],
  ["subcaelestis", "<big>subcaelestis</big>\n\n<b>sub–caelestis, e</b>\nподнебесный <i>Tert</i>."],
  ["subcaeruleus", "<big>subcaeruleus</big>\n\n<b>sub–caeruleus, a, um</b>\nголубоватый (creta <i>CC</i>)."],
  ["subcandidus", "<big>subcandidus</big>\n\n<b>sub–candidus, a, um</b>\nбеловатый (folium <i>PM</i>)."],
  ["subcavus", "<big>subcavus</big>\n\n<b>sub–cavus, a, um</b>\nполый внизу (area <i>Cato</i>): loca subcava terrae <i>Lcr</i> подземные полости."],
  ["subceno", "<big>subceno</big>\n\n<b>sub–ceno, —, —, are</b>\nобъедать снизу <i>Galba ap. Q</i>."],
  ["subcent-", "<big>subcent-</big>\n\n<b>subcent-</b> <i>v. l.</i>\n= succent-."],
  ["subcerno", "<big>subcerno</big>\n\n<b>subcerno</b> <i>v. l.</i>\n= succerno."],
  ["subcin-", "<big>subcin-</big>\n\n<b>subcin-</b> <i>v. l.</i>\n= succin-."],
  ["subcinericius", "<big>subcinericius</big>\n\n<b>sub–cinericius, a, um</b> [cinis]\nиспечённый в золе (panis <i>Vlg, Eccl</i>)."],
  ["subcisivus", "<big>subcisivus</big>\n\n<b>subcisivus</b> <i>v. l.</i>\n= subsicivus."],
  ["subcoeno", "<big>subcoeno</big>\n\n<b>subcoeno</b> <i>v. l.</i>\n= subceno."],
  ["subcoer-", "<big>subcoer-</big>\n\n<b>subcoer-</b> <i>v. l.</i>\n= subcaer-."],
  ["subcoloratus", "<big>subcoloratus</big>\n\n<b>sub–coloratus, a, um</b>\nслегка окрашенный, <i>т. е.</i> потемневший (charta <i>Is</i>)."],
  ["subcontumeliose", "<big>subcontumeliose</big>\n\n<b>sub–contumeliose</b>\nнесколько оскорбительно (aliquem tractare <i>C</i>)."],
  ["subcortex", "<big>subcortex</big>\n\n<b>sub–cortex, icis</b> <i>m</i>\nподкорковый слой дерева, <i>т. е.</i> заболонь <i>Veg</i>."],
  ["subcr-", "<big>subcr-</big>\n\n<b>subcr-</b> <i>v. l.</i>\n= succr-."],
  ["subcrassulus", "<big>subcrassulus</big>\n\n<b>sub–crassulus, a, um</b>\nтолстоватый, полноватый (corporis qualitate <i>Capit</i>)."],
  ["subcrispus", "<big>subcrispus</big>\n\n<b>sub–crispus, a, um</b>\nнесколько курчавый, немного завивающийся (capillus <i>C</i>)."],
  ["subcrudus", "<big>subcrudus</big>\n\n<b>sub–crudus, a, um</b>\n<b>1)</b> недоваренный, сыроватый (brassica <i>Cato</i>);\n<b>2)</b> не совсем созревший (ulcus <i>CC</i>)."],
  ["subcruentus", "<big>subcruentus</big>\n\n<b>sub–cruentus, a, um</b>\nнемного окровавленный, слегка кровоточащий <i>CC</i>."],
  ["subcumbo", "<big>subcumbo</big>\n\n<b>subcumbo</b> <i>v. l.</i>\n= succumbo."],
  ["subcuneo", "<big>subcuneo</big>\n\n<b>sub–cuneo, (avi), atum, are</b> [cuneus]\nскреплять клиньями (postes subcuneati <i>Vtr</i>)."],
  ["subcurator", "<big>subcurator</big>\n\n<b>sub–curator, oris</b> <i>m</i>\nпомощник управляющего <i>Dig</i>."],
  ["subcurr-", "<big>subcurr-</big>\n\n<b>subcurr-</b> <i>v. l.</i>\n= succurr-."],
  ["subcurvus", "<big>subcurvus</big>\n\n<b>sub–curvus, a, um</b>\nнесколько изогнутый <i>Amm</i>."],
  ["subcuss-", "<big>subcuss-</big>\n\n<b>subcuss-</b> <i>v. l.</i>\n= succuss-."],
  ["subcustos", "<big>subcustos</big>\n\n<b>sub–custos, odis</b> <i>m</i>\nпомощник сторожа, младший привратник <i>Pl</i>."],
  ["subcutaneus", "<big>subcutaneus</big>\n\n<b>sub–cutaneus, a, um</b>\nподкожный (morbus <i>AV</i>)."],
  ["subdealbo", "<big>subdealbo</big>\n\n<b>sub–dealbo, —, —, are</b>\nслегка белить, <i>т. е.</i> покрывать инеем <i>Vr</i>."],
  ["subdebilis", "<big>subdebilis</big>\n\n<b>sub–debilis, e</b>\nполупарализованный, полурасслабленный (femur <i>Su</i>)."],
  ["subdebilitatus", "<big>subdebilitatus</big>\n\n<b>sub–debilitatus, a, um</b>\nнесколько обессиленный, <i>перен.</i> несколько павший духом, приунывший <i>C</i> (<i>v. l.</i>)."],
  ["subdefectio", "<big>subdefectio</big>\n\n<b>sub–defectio, onis</b> <i>f</i>\nнекоторое ослабление <i>Aug</i>."],
  ["subdeficio", "<big>subdeficio</big>\n\n<b>sub–deficio, —, —, ere</b>\nмало-помалу ослабевать, несколько уставать <i>QC, Aug</i>."],
  ["subdialia", "<big>subdialia</big>\n\n<b>subdialia, ium</b> <i>n</i> [subdialis]\nоткрытая галерея, терраса, балкон <i>PM</i>."],
  ["subdialis", "<big>subdialis</big>\n\n<b>subdialis, e</b> [sub + dio]\nнаходящийся под открытым небом (petrae <i>PM</i>)."],
  ["subdidi", "<big>subdidi</big>\n\n<b>subdidi</b> <i>pf. к</i>\nsubdo."],
  ["subdifficilis", "<big>subdifficilis</big>\n\n<b>sub–difficilis, e</b>\nнесколько трудный, трудноватый (quaestio <i>C</i>)."],
  ["subdiffido", "<big>subdiffido</big>\n\n<b>sub–diffido, diffisus sum, diffidere</b> <i>semidepon.</i>\nне совсем доверять, не вполне верить <i>C</i>."],
  ["subdisjunctivus", "<big>subdisjunctivus</big>\n\n<b>sub–disjunctivus, a, um</b>\nне вполне противопоставляемый, <i>т. е.</i> не содержащий абсолютной противоположности, допускающий обе возможности (verba subdisjunctivae orationis <i>Dig</i>)."],
  ["subditicius", "<big>subditicius</big>\n\n<b>subditicius (-tius), a, um</b> [subdo]\nподложный, поддельный, ненастоящий <i>Lampr</i>."],
  ["subditivus", "<big>subditivus</big>\n\n<b>subditivus, a, um</b>\n<b>1)</b> <i>Pl, C, Su</i> = subditicius;\n<b>2)</b> сокрытый (secreta <i>Eccl</i>)."],
  ["subditus", "<big>subditus</big>\n\n<b>I subditus, a, um</b> <i>part. pf. к</i>\nsubdo.\n\n<b>II subditus, (us) </b> <i>m</i>\nподнесение (odoris subditu lethargicos excitare <i>PM</i>)."],
  ["subdiu", "<big>subdiu</big>\n\n<b>sub–diu</b> <i>adv.</i>\nднём <i>Pl</i> (<i>v. l.</i>)."],
  ["subdival", "<big>subdival</big>\n\n<b>subdival, alis</b> <i>n</i> <i>Tert</i>\n= subdialia."],
  ["subdivalis", "<big>subdivalis</big>\n\n<b>subdivalis, e</b> <i>Amm</i>\n= subdialis."],
  ["subdivido", "<big>subdivido</big>\n\n<b>sub–divido, —, —, ere</b>\nподразделять <i>Eccl</i>."],
  ["subdivisio", "<big>subdivisio</big>\n\n<b>sub–divisio, onis</b> <i>f</i>\nподразделение <i>CJ, Eccl</i>."],
  ["subdivum", "<big>subdivum</big>\n\n<b>sub–divum, i</b> <i>n</i>\nоткрытый воздух: subdivo <i>Tert</i> под открытым небом."],
  ["subdo", "<big>subdo</big>\n\n<b>sub–do, didi, ditum, ere</b>\n<b>1)</b> подкладывать, класть под (pugionem pulvino <i>Su</i>);\n<b>2)</b> подставлять, ставить под (testam mensae pedi <i>O</i>; furcas vitibus <i>PM</i>): radiis juga subdita matutinis <i>O</i> горы, озарённые утренними лучами; s. calcaria equo <i>L</i> пришпорить коня;\n<b>3)</b> погружать, окунать (caput fonti <i>O</i>): se s. aquis <i>O</i> погрузиться (нырнуть) в воду; populi Rhodopae subditi <i>PM</i> народы, живущие у подножия Родопских гор;\n<b>4)</b> разводить под, раскладывать внизу (ignem <i>Cato</i>);\n<b>5)</b> добавлять, присоединять (versus <i>AG</i>): s. tauros aratro <i>T</i> запрячь быков в плуг;\n<b>6)</b> вкладывать (colla vinclis <i>Tib</i>); внушать, вдохнуть (alicui spiritus <i>L</i>): s. alicui ignem <i>L</i> (faces <i>C, Sen, Q</i>, stimulos <i>O</i>) ad aliquid побуждать (воодушевлять) к чему-л.; ignem seditioni s. <i>L</i> разжигать мятеж;\n<b>7)</b> ставить вместо, подставлять (aliquem in locum alicujus <i>C</i>);\n<b>8)</b> подменивать, прибегать к подлогу: s. rumorem <i>T</i> распространять ложный слух; s. testamentum <i>T</i> представить подложное (подделать) завещание; crimina s. <i>T</i> возводить ложные обвинения; subditus <i>L, Q etc.</i> подставное лицо (лжесвидетель <i>и т. п.</i>) <i>или</i> чужой (filius <i>CJ</i>);\n<b>9)</b> подчинять, покорять (aliquem imperio alicujus <i>T</i>);\n<b>10)</b> подвергать (exquisitis poenis subdi <i>CJ</i>);\n<b>11)</b> бросать, оставлять на произвол (s. aliquid tempestatibus, casibus <i>PJ</i>)."],
  ["subdoceo", "<big>subdoceo</big>\n\n<b>sub–doceo, —, —, ere</b>\nучить в качестве заместителя <i>или</i> помощника (aliquem <i>C</i>); быть учителем в помощь (alicui <i>Aug</i>)."],
  ["subdoctor", "<big>subdoctor</big>\n\n<b>sub–doctor, oris</b> <i>m</i>\nпомощник учителя <i>Aus</i>."],
  ["subdole", "<big>subdole</big>\n\n<b>subdole</b> [subdolus]\nхитровато, несколько коварно, плутовато <i>Pl, C, Sl</i>."],
  ["subdolens", "<big>subdolens</big>\n\n<b>sub–dolens, entis</b>\nнемного болезненный (faucium sensus <i>CA</i>)."],
  ["subdolositas", "<big>subdolositas</big>\n\n<b>sub–dolositas, atis</b> <i>f</i>\nлукавство, плутоватость <i>Is</i>."],
  ["subdolosus", "<big>subdolosus</big>\n\n<b>subdolosus, a, um</b> <i>Eccl</i>\n= subdolus."],
  ["subdolus", "<big>subdolus</big>\n\n<b>sub–dolus, a, um</b>\nхитроватый, несколько коварный, плутоватый <i>Pl, Cs, Sl etc.</i>; обманчивый (placidi pellacia ponti <i>Lcr</i>)."],
  ["subdomo", "<big>subdomo</big>\n\n<b>sub–domo, —, —, are</b>\nпокорить, одолеть (sic solent superbi subdomari <i>Pt</i>)."],
  ["subdubito", "<big>subdubito</big>\n\n<b>sub–dubito, —, —, are</b>\nнесколько сомневаться, недоумевать <i>C</i>."],
  ["subduco", "<big>subduco</big>\n\n<b>sub–duco, duxi, ductum, ere</b>\n<b>1)</b> извлекать (из-под), вынимать, украдкой брать (ensem capiti <i>V</i>; lapides ex turri <i>Cs</i>): ne collapsa ruant subductis tecta columnis <i>J</i> чтобы не обрушилось здание, когда окажутся выдернутыми (его) устои;\n<b>2)</b> вытаскивать, вытягивать на сушу (navem <i>Pl, Cs, L</i>);\n<b>3)</b> забирать, убирать, отнимать, лишать (cibum alicui <i>C</i>; morte subductus <i>CJ</i>); похищать (aliquem furto <i>L, CTh</i>; ignem aetheria domo = caelo <i>H</i>; subducta vestimenta alicujus <i>Pt</i>);\n<b>4)</b> ограждать (aliquem periculo <i>VP</i>; aliquid ventis <i>PJ</i>); скрывать, утаивать (aliquem oculis alicujus <i>QC</i>); избавлять, спасать (aliquem manibus alicujus <i>V</i>): s. ab ictu corpus <i>Pt</i> увёртываться от ударов; se s. <i>или</i> <i>med.-pass.</i> subduci тайком уйти, удалиться (ex acie <i>QC</i>; a colloquio <i>Pt</i>) <i>или</i> ускользнуть, избавиться (a vulnere <i>O</i>): hic fons nascitur simulque subducitur <i>PJ</i> здесь зарождается источник и тут же пропадает (уходит в землю); colles se s. incipiunt <i>V</i> холмы начинают понижаться (мало-помалу переходят в равнину); s. vela <i>bAl</i> убирать паруса;\n<b>5)</b> незаметно уводить, отводить (milites in primam aciem <i>Sl</i>; cohortes e dextro cornu <i>L</i>);\n<b>6)</b> <i>мед.</i> очищать (alvum <i>CC, AG</i>);\n<b>7)</b> (при)поднимать, вздёргивать (tunicam <i>H, O</i>; supercilia <i>H etc.</i>);\n<b>8)</b> подсчитывать (summam <i>C</i>): s. rationem <i>C</i> (calculos <i>C</i>; ratiunculam <i>Pl</i>) подводить итог; bene subducta ratione <i>Ter, C</i> хорошо обдумав, точно рассчитав."],
  ["subductarius", "<big>subductarius</big>\n\n<b>subductarius, a, um</b> [subduco]\nслужащий для подъёма, подъёмный (funis <i>Cato</i>)."],
  ["subductio", "<big>subductio</big>\n\n<b>subductio, onis</b> <i>f</i> [subduco]\n<b>1)</b> вытаскивание на берег (navium <i>Vtr, Cs</i>);\n<b>2)</b> подсчёт, исчисление <i>C</i>;\n<b>3)</b> восторг, экстаз <i>Aug</i>."],
  ["subductisupercilicarptor", "<big>subductisupercilicarptor</big>\n\n<b>subducti–supercili–carptor, oris</b> <i>m</i> <i>презр.-ирон.</i>\nхмурящий брови хулитель <i>Laevius ap. AG</i>."],
  ["subductus", "<big>subductus</big>\n\n<b>subductus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> subduco;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> удалённый (omnibus ventis <i>PJ</i>);\n<b>2)</b> впалый (oculi <i>Sol</i>)."],
  ["subdulcis", "<big>subdulcis</big>\n\n<b>sub–dulcis, e</b>\nсладковатый (radix <i>PM</i>)."],
  ["subdurus", "<big>subdurus</big>\n\n<b>sub–durus, a, um</b>\nнесколько твёрдый, жестковатый (genus verrucae <i>CC</i>): illud subdurum tibi suadere (est) <i>Quintus C</i> это трудновато (будет) тебе втолковать."],
  ["subedo", "<big>subedo</big>\n\n<b>sub–edo, edi, esum, ere</b>\n<b>1)</b> подгрызать, отъедать снизу (fiber salicem subest <i>Pt</i>);\n<b>2)</b> (<i>о воде</i>) подмывать, размывать снизу (scopulum <i>O</i>)."],
  ["subegi", "<big>subegi</big>\n\n<b>subegi</b> <i>pf. к</i>\nsubigo."],
  ["subeo", "<big>subeo</big>\n\n<b>sub–eo, ii (ivi), itum, ire</b>\n<b>1)</b> подходить (под), подползать, забираться, укрываться (in aliquid, aliquid <i>или</i> alicui rei): s. tectum <i>Cs</i> войти в дом; s. virgulta <i>QC</i> забраться под хворост <i>или</i> в кусты; luna sub orbem solis subiit <i>L</i> луна скрылась за солнечным диском; currum s. <i>V</i> быть впряжённым в колесницу;\n<b>2)</b> входить, вступать, проникать (s. lucos <i>V, H</i> <i>или</i> luco <i>V</i>): s. aquas <i>O</i> погрузиться в воду;\n<b>3)</b> в(о)сходить, подниматься, взбираться, влезать (in montem <i>L</i>; iniquissimum locum <i>Cs</i>; adversum flumen <i>QC</i>); всходить, давать всходы (herbae subeunt <i>V</i>); вырастать (barba subit <i>M</i>);\n<b>4)</b> нападать (aliquem <i>V</i>); наступать (ex inferiore loco subeuntes, <i>sc.</i> hostes <i>Cs</i>);\n<b>5)</b> подходить, приближаться, подступать (ad muros <i>C</i>, muros <i>L</i> <i>и</i> muro <i>V</i>; portae <i>и</i> ad portam <i>L</i>): s. tonantem <i>St</i> обратиться к громовержцу; s. palmae <i>V</i> приближаться к победе;\n<b>6)</b> подкрадываться, подбираться (lumina fessa sopor subit <i>O</i>);\n<b>7)</b> приходить в голову, вспоминаться (subiit cari genitoris imago <i>V</i>): vix subeunt ipsi verba Latina mihi <i>O</i> я с трудом припоминаю (даже) латинские слова;\n<b>8)</b> овладевать, охватывать, обуревать (paenitentia subit aliquem <i>QC</i>; cogitatio animum subit <i>L</i>): ne subeant animo taedia justa tuo <i>O</i> чтобы не возникло в твоей душе справедливое отвращение;\n<b>9)</b> следовать, становиться на место, сменять (primae legioni tertia subiit <i>L</i>); приходить на смену, наступать (subiit argentea proles <i>O</i>);\n<b>10)</b> подпадать под власть, переходить, доставаться (clarum subit Alba Latinum <i>O</i>);\n<b>11)</b> брать на себя, взваливать на себя, поднимать, нести (s. dorso onus <i>H</i>; aliquem umeris <i>V</i>);\n<b>12)</b> принимать на себя, подвергаться, претерпевать, переносить, испытывать (s. periculum, labores, injuriam <i>C</i>): s. poenam <i>C etc.</i> подвергнуться наказанию; s. crimen <i>Hirt</i> принять на себя вину; s. condiciones <i>C, T</i> принимать условия; primus jussa subi <i>Cld</i> будь первым исполнителем (своих) приказаний; subeundae pro re publica tempestales <i>C</i> невзгоды, которые приходится переносить ради общего блага. — <i>См. тж.</i> subitus."],
  ["suber", "<big>suber</big>\n\n<b>suber, eris</b> <i>n</i>\n<b>1)</b> пробковое дерево, пробковый дуб (<i>Quercus suber</i>, L.) <i>V, PM</i>;\n<b>2)</b> кора пробкового дерева <i>V</i>."],
  ["suberectus", "<big>suberectus</big>\n\n<b>suberectus, a, um</b> [suberigo]\nприподнявшийся (effultus in cubiculo suberectusque <i>Ap</i>)."],
  ["subereus", "<big>subereus</big>\n\n<b>subereus, a, um</b> [suber]\nпробковый (robur <i>Col</i>)."],
  ["suberies", "<big>suberies</big>\n\n<b>suberies, ei</b> <i>f</i> <i>Is</i>\n= suber."],
  ["suberigo", "<big>suberigo</big>\n\n<b>sub–erigo, —, erectum, ere</b>\nприподнимать, вздымать (aliquid unda <i>Sil</i>)."],
  ["suberinus", "<big>suberinus</big>\n\n<b>suberinus, a, um</b> <i>Sid</i>\n= subereus."],
  ["subero", "<big>subero</big>\n\n<b>subero</b> <i>fut. к</i>\nsubsum."],
  ["suberro", "<big>suberro</big>\n\n<b>sub–erro, —, —, are</b>\nблуждать внизу, <i>т. е.</i> извиваться, змеиться (fluvii, quicumque Italis montibus suberrant <i>Cld</i>)."],
  ["subest", "<big>subest</big>\n\n<b>subest</b> <i>3 л. sg. praes. к</i>\nsubsum."],
  ["subesus", "<big>subesus</big>\n\n<b>sub–esus, a, um</b> [edo]\nподорванный, надломленный <i>Hier</i>."],
  ["subf-", "<big>subf-</big>\n\n<b>subf-</b> <i>v. l.</i>\n= suff-."],
  ["subg-", "<big>subg-</big>\n\n<b>subg-</b> <i>v. l.</i>\n= sugg-."],
  ["subhaereo", "<big>subhaereo</big>\n\n<b>sub–haereo, —, —, ere</b>\nбыть связанным (immutabili veritati <i>Aug</i>): in suspicione et crimine s. (<i>v. l.</i> haerere) <i>VM</i> навлекать подозрение в преступных намерениях."],
  ["subhastarius", "<big>subhastarius</big>\n\n<b>sub–hastarius, a, um</b>\nназначенный к продаже с торгов (possessiones <i>CTh</i>)."],
  ["subhastatio", "<big>subhastatio</big>\n\n<b>sub–hastatio, onis</b> <i>f</i>\nпродажа с торгов <i>CJ, CTh</i>."],
  ["subhasto", "<big>subhasto</big>\n\n<b>subhasto, avi, atum, are</b> [sub + hasta]\nпродавать с торгов (aliquid <i>Sol, CJ</i>)."],
  ["subhirci", "<big>subhirci</big>\n\n<b>sub–hirci, orum</b> <i>m</i> <i>Is</i>\n= subbrachia."],
  ["subhorridus", "<big>subhorridus</big>\n\n<b>sub–horridus, a, um</b>\nгрубоватый, несколько суровый (s. atque incultus <i>C</i>)."],
  ["subhumidus", "<big>subhumidus</big>\n\n<b>sub–humidus, a, um</b>\nнемного влажный (oculi <i>CC</i>)."],
  ["subices", "<big>subices</big>\n\n<b>subices, (um) </b> <i>f</i> [subjicio]\nоснования, <i>поэт.</i> воздушные пространства, верхние слон воздуха (s. umidae, unde oritur imber <i>Enn ap. AG</i>)."],
  ["subicio", "<big>subicio</big>\n\n<b>subicio</b> <i>v. l.</i>\n= subjicio."],
  ["subidus", "<big>subidus</big>\n\n<b>subidus, a, um</b> [subo]\nохваченный страстью, влекомый похотью <i>Poeta ap. AG</i> (<i>v. l.</i>)."],
  ["subigitatio", "<big>subigitatio</big>\n\n<b>subigitatio, onis</b> <i>f</i> [subigito]\nгрубое (бесстыдное) заигрывание, неприличное приставание, распутничание <i>Pl</i>."],
  ["subigitatrix", "<big>subigitatrix</big>\n\n<b>subigitatrix, icis</b> <i>f</i> [subigito]\nгрубо заигрывающая, распутница <i>Pl</i>."],
  ["subigito", "<big>subigito</big>\n\n<b>subigito, —, —, are</b> [<i>frequ. к</i> subigo]\n<b>1)</b> соблазнять, склонять (aliquem blanditiis <i>Ap</i>);\n<b>2)</b> грубо заигрывать, приставать (s. aliquem <i>Pl</i> <i>и</i> aliquam <i>Ter</i>)."],
  ["subigo", "<big>subigo</big>\n\n<b>subigo, egi, actum, ere</b> [sub + ago]\n<b>1)</b> пригонять (sues in umbrosum locum <i>Vr</i>); прогонять, заставлять пройти (hostes sub jugum <i>Just</i>); приводить, доставлять (naves ad castellum <i>L</i>); гнать, вести (ratem conto <i>V</i>);\n<b>2)</b> заставлять (aliquem metu aliquid facere <i>L</i>); принуждать (aliquem ad <i>и</i> in deditionem <i>L, QC</i>);\n<b>3)</b> подчинять, покорять, порабощать (gentes <i>Nep</i>);\n<b>4)</b> теснить, притеснять, мучить (aliquem inopia <i>Cs</i>; bello <i>L</i>);\n<b>5)</b> обрабатывать (opus digitis <i>O</i>); взрыхлять (terram <i>Sen</i>); утаптывать (corium — <i>sc.</i> calcispilis <i>Cato</i>); выделывать (pellem <i>PM</i>);\n<b>6)</b> волновать (pontum remis <i>VF</i>);\n<b>7)</b> точить (secures in cote <i>V</i>);\n<b>8)</b> мять, месить (farinam <i>Cato</i>);\n<b>9)</b> укрощать, приручать (beluam <i>C</i>; subacta cervix, <i>sc.</i> juvencae <i>H</i>; taurum s. <i>Ap</i>);\n<b>10)</b> обучать, школить (homines subacti bellis <i>L</i>); развивать (ingenium <i>C</i>)."],
  ["Subigus", "<big>Subigus</big>\n\n<b>Subigus, i</b> <i>m</i>\nСубиг, <i>deus qui adest, ut novam nuptam maritus bene subigat</i> <i>Tert, Aug</i>."],
  ["subimpudens", "<big>subimpudens</big>\n\n<b>sub–impudens, entis</b> <i>adj.</i>\nнесколько бесстыдный <i>C</i>."],
  ["subina", "<big>subina</big>\n\n<b>subina</b> <i>v. l.</i>\n= sibina."],
  ["subinanis", "<big>subinanis</big>\n\n<b>sub–inanis, e</b>\nнемного суетный, несколько пустой, тщеславный <i>C</i>."],
  ["subinde", "<big>subinde</big>\n\n<b>sub–inde</b> <i>adv.</i>\n<b>1)</b> вскоре после, тотчас после, вслед за этим, сейчас же (s. aliud bellum oritur <i>C</i>): semper eosdem an s. alios? <i>T</i> (выбрать) всё тех же или на смену им других?;\n<b>2)</b> от времени до времени (omnibus prodest s. animum relaxare <i>Sen</i>);\n<b>3)</b> часто, многократно, то и дело: erit pergratum mihi id s. respicere <i>PJ</i> мне будет очень приятно часто глядеть на это; ut s. sciat, quantum... <i>Pt</i> чтобы в любой момент он мог узнать, сколько."],
  ["subindo", "<big>subindo</big>\n\n<b>sub–indo, —, —, ere</b>\nсверх того добавлять <i>AV</i>."],
  ["subinduco", "<big>subinduco</big>\n\n<b>sub–induco, —, —, ere</b>\nнезаметно <i>или</i> исподтишка вводить <i>Eccl</i>."],
  ["subinfero", "<big>subinfero</big>\n\n<b>sub–infero, tuli, —, ferre</b>\nдобавлять, присовокуплять <i>Vlg, Eccl</i>."],
  ["subinflatus", "<big>subinflatus</big>\n\n<b>sub–inflatus, a, um</b>\nнесколько раздутый (pectus <i>Eccl</i>) <i>или</i> одутловатый (vultus <i>CA</i>)."],
  ["subinfluo", "<big>subinfluo</big>\n\n<b>sub–influo, —, —, ere</b>\nтечь, протекать внизу <i>Sen</i> (<i>v. l.</i>)."],
  ["subinjectus", "<big>subinjectus</big>\n\n<b>subinjectus, a, um</b> [sub + injicio]\nположенный (на что-л.) (subinjecta manu <i>Sen</i>)."],
  ["subinsulsus", "<big>subinsulsus</big>\n\n<b>sub–insulsus, a, um</b>\nдовольно безвкусный, пошловатый <i>C</i>."],
  ["subintellego", "<big>subintellego</big>\n\n<b>sub–intellego, —, —, ere</b>\n<b>1)</b> подмечать, начинать понимать <i>Tert, Hier</i>;\n<b>2)</b> подразумевать <i>Aug</i>."],
  ["subintro", "<big>subintro</big>\n\n<b>sub–intro, avi, atum, are</b>\nпробираться, проникать <i>Eccl</i>."],
  ["subintroduco", "<big>subintroduco</big>\n\n<b>subintroduco, —, —, ere</b> <i>Vlg, Eccl</i>\n= subinduco."],
  ["subintroeo", "<big>subintroeo</big>\n\n<b>sub–introeo, ii, —, ire</b>\n<b>1)</b> входить <i>Vlg</i>;\n<b>2)</b> принимать (speciem alicujus <i>Eccl</i>)."],
  ["subinvicem", "<big>subinvicem</big>\n\n<b>subinvicem</b> <i>Is</i>\n= invicem."],
  ["subinvideo", "<big>subinvideo</big>\n\n<b>sub–invideo, vidi, visum, ere</b>\nслегка завидовать (alicui <i>C</i>)."],
  ["subinvisus", "<big>subinvisus</big>\n\n<b>subinvisus, a, um</b> [subinvideo]\nнемного неприятный, не пользующийся любовью (nomen alicujus apud aliquem <i>C</i>)."],
  ["subinvito", "<big>subinvito</big>\n\n<b>sub–invito, —, —, are</b>\nнамёком предлагать, давать понять (s. aliquem, ut aliquid faciat <i>C</i>)."],
  ["subirascor", "<big>subirascor</big>\n\n<b>sub–irascor, iratus (sum), irasci</b> <i>depon.</i>\nнесколько сердиться, немного гневаться (alicui rei <i>C</i>)."],
  ["subiratus", "<big>subiratus</big>\n\n<b>sub–iratus, a, um</b>\nнемного сердитый (alicui <i>C</i>)."],
  ["subitaneus", "<big>subitaneus</big>\n\n<b>subitaneus, a, um</b> [subitus]\nнеожиданный, внезапный (imber <i>Col</i>; timor <i>Vlg</i>)."],
  ["subitaria", "<big>subitaria</big>\n\n<b>subitaria, orum</b> <i>n</i>\nимпровизированная речь <i>Ap</i>."],
  ["subitarius", "<big>subitarius</big>\n\n<b>subitarius, a, um</b> [subitus]\nспешный, срочно построенный (aedificia <i>AG</i>): subitari! gradus <i>T</i> временный амфитеатр || наскоро набранный, наспех мобилизованный (legiones, exercitus <i>L</i>); неподготовленный, импровизированный, экспромтный (dictio <i>AG, Ap</i>); res subitariae <i>Ap</i> импровизации."],
  ["subitatio", "<big>subitatio</big>\n\n<b>subitatio, onis</b> <i>f</i>\nвнезапное появление (insperatae salutis <i>Vlg</i>)."],
  ["subito", "<big>subito</big>\n\n<b>subito</b> [subitus]\n<b>1)</b> внезапно, неожиданно (bellum s. natum <i>C</i>): Irus et est s., qui modo Croesus erat <i>O</i> и вот недавний Крез (первый богач) сразу стал Иром (последним нищим) || экспромтом, без приготовления (dicere <i>C</i>);\n<b>2)</b> наспех (perarata littera <i>O</i>);\n<b>3)</b> тотчас же (deportare aliquem <i>Lampr</i>)."],
  ["subitum", "<big>subitum</big>\n\n<b>subitum, i</b> <i>n</i> [subeo]\n<b>1)</b> внезапность, неожиданность, неожиданное обстоятельство (subita rerum <i>L</i>; subita belli <i>L, T</i>): per <i>и</i> in s. <i>Sil</i> неожиданно, внезапно; subita proferre <i>PJ</i> говорить без подготовки (экспромтом);\n<b>2)</b> неотложное дело: si tibi nihil subiti est <i>Pl</i> если тебе не к спеху."],
  ["subitus", "<big>subitus</big>\n\n<b>subitus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> subeo;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> внезапный, неожиданный (bellum <i>Cs</i>; tempestas <i>C</i>; casus <i>O</i>; subitam avertere curam <i>Lcr</i>); скоропостижный (mors <i>J, Q</i>);\n<b>2)</b> экспромтный, импровизированный (dictio, oratio <i>C</i>);\n<b>3)</b> внезапно нахлынувший (multitudo <i>bAfr</i>);\n<b>4)</b> наскоро собранный, плохо обученный (miles <i>T</i>);\n<b>5)</b> только что возникший, случайный, мгновенный (imagines <i>PJ</i>);\n<b>6)</b> крутой (clivi <i>St</i>)."],
  ["subjaceo", "<big>subjaceo</big>\n\n<b>sub–jaceo, jacui, —, ere</b>\n<b>1)</b> лежать внизу, находиться под (frumentum tegulis subjacet <i>PM</i>); простираться внизу (mare subjacens <i>PJ</i>);\n<b>2)</b> относиться, быть связанным (causa, cui subjacent lites <i>Q</i>);\n<b>3)</b> быть подверженным, находиться во власти (vita multis casibus subjacet <i>Ap</i>); подлежать (capitali supplicio <i>CJ</i>);\n<b>4)</b> быть очевидным (palam subjacet <i>Tert</i>)."],
  ["subjacto", "<big>subjacto</big>\n\n<b>subjacto</b> <i>v. l.</i>\n= subjecto."],
  ["subjecta", "<big>subjecta</big>\n\n<b>subjecta, orum</b> <i>n</i> [subjectus I, 2]\nнизины, низменности <i>T, Aus</i>."],
  ["subjecte", "<big>subjecte</big>\n\n<b>subjecte</b> (<i>встреч. тк.</i> <i>superl.</i>) [subjectus II]\nпокорно, униженно: haec quam potest demississime et subjectissime exponit <i>Cs</i> он сказал это в высшей степени смиренно и покорно."],
  ["subjectibilis", "<big>subjectibilis</big>\n\n<b>subjectibilis, e</b> [subjicio]\nпокорившийся (alicui <i>Vlg</i>)."],
  ["subjectio", "<big>subjectio</big>\n\n<b>subjectio, onis</b> <i>f</i> [subjicio]\n<b>1)</b> представление, изложение: sub aspectum <i>C</i> (sub oculos <i>Q, AG</i>) s. наглядное изложение;\n<b>2)</b> подмена, подделка, подлог (testamenti <i>L</i>);\n<b>3)</b> присовокупление, добавление (rationis <i>rhH</i>);\n<b>4)</b> ответ на возражение <i>rhH, Q</i>;\n<b>5)</b> <i>тех.</i> подставка, основание (<i>sc.</i> catapultae <i>Vtr</i>);\n<b>6)</b> покорность, смирение <i>Eccl</i>."],
  ["subjectivus", "<big>subjectivus</big>\n\n<b>subjectivus, a, um</b> [subjectus I, 2, 2]\n<b>1)</b> добавляемый, присоединяемый <i>Tert</i>;\n<b>2)</b> <i>грам.</i> относящийся к подлежащему: pars subjectiva <i>Ap</i> подлежащее, субъект."],
  ["subjecto", "<big>subjecto</big>\n\n<b>subjecto, —, —, are</b> [<i>intens. к</i> subjicio]\n<b>1)</b> класть вниз, подкладывать (manus <i>O</i>): stimulos alicui s. <i>H</i> подгонять (подстрекать) кого-л.;\n<b>2)</b> бросать вверх, подбрасывать (arenam alte <i>V</i>)."],
  ["subjector", "<big>subjector</big>\n\n<b>subjector, oris</b> <i>m</i> [subjicio]\nподделыватель (testamenti <i>C</i>)."],
  ["subjectum", "<big>subjectum</big>\n\n<b>subjectum, i</b> <i>n</i> [subjectus I, 2]\n(<i>sc.</i> verbum) <i>грам.</i> подлежащее, субъект."],
  ["subjectus", "<big>subjectus</big>\n\n<b>I subjectus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> subjicio;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> лежащий внизу, простирающийся у ног (sinus <i>T</i>): mittere aliquid subjectas in undas <i>O</i> сбросить что-л. в расстилающееся внизу море;\n<b>2)</b> прилегающий, примыкающий, смежный, соседний (rivus castris s. <i>Cs</i>); следующий (versus <i>Vr</i>); syllaba longa brevi subjecta vocatur iambus <i>H</i> короткий слог с последующим долгим называется ямбом;\n<b>3)</b> подчинённый, покорённый, подвластный (imperio alicujus <i>C, Cs</i>): parcere subjectis (<i>dat.</i>) <i>V</i> щадить покорившихся;\n<b>4)</b> подверженный, отданный во власть (tristi servitio s. <i>L</i>; mare ventis subjectum <i>C</i>): sub incertos casus s. <i>C</i> зависящий от неопределённых обстоятельств; alicui subjectum esse <i>C</i> находиться в чьём-л. распоряжении; s. invidiae <i>H, J</i> являющийся предметом зависти;\n<b>5)</b> относящийся: res sensibus (<i>или</i> sub sensus) subjecta <i>C</i> чувственно постигаемые вещи; ira libidini subjecta est <i>Sen</i> гнев относится к категории страстей;\n<b>6)</b> подставной (falsus emptor <i>Dig</i>).\n\n<b>II subjectus, i</b> <i>m</i>\n<b>1)</b> подданный <i>PM</i>;\n<b>2)</b> подчинённый <i>Col</i>.\n\n<b>III subjectus, (us) </b> <i>m</i>\nподкладывание, прикладывание (<i>лекарства или компресса</i>) <i>PM</i>."],
  ["subjicio", "<big>subjicio</big>\n\n<b>subjicio, jeci, jectum, ere</b> [sub + jacio]\n<b>1)</b> бросать, ставить, класть под, подкладывать, подставлять (aliquid alicui rei, <i>реже</i> sub aliquid): s. epistulam pulvino <i>QC</i> (sub pulvinum <i>Nep</i>) положить письмо под подушку; ignem alicui rei s. <i>C, O, bAfr</i> развести огонь под чём-л., поджечь что-л.; s. caudam utero <i>Ph</i> поджать хвост; ossa subjecta corpori <i>C</i> кости, являющиеся основанием тела; subjectis scutulis <i>Cs</i> подложив (под суда) катки; aliquid oculis s. <i>C, L</i> сделать что-л. очевидным; aliquid cogitationi suae s. <i>C</i> обдумать что-л.; subjici bestiis <i>CJ, Dig</i> быть брошенным (на растерзание) зверям; multa sub unum aspectum s. <i>Q</i> охватить многое в одном (кратком) обзоре; notionem (sententiam) voci <i>и</i> sub voce s. <i>C, G</i> вложить смысл в слово, связать слово с (определённым) понятием; alicui verbo duas res s. <i>Cs</i> понимать какое-л. слово в двояком смысле;\n<b>2)</b> подчинять, покорять (provinciam alicui <i>T</i>; se potestati <i>C</i> <i>и</i> sub potestatem alicujus <i>rhH</i>); s. species generi <i>Q</i> подчинить виды роду, <i>т. е.</i> объединить виды в роде; fas, justum, pium, aequum, mansuetum subj ici possunt honestati <i>Q</i> (высшая) правда, законность, добродетель, справедливость, кротость, (всё это) может быть объединено в понятии честности;\n<b>3)</b> подвергать, обрекать, ставить в зависимость: s. navigationem hiemi <i>Cs</i> подвергать плавание опасностям зимних бурь; s. aliquid praeconi <i>L, Sen</i> (voci praeconis <i>или</i> sub praeconem <i>C</i>) назначить что-л. к продаже с молотка; terram ferro s. <i>C</i> обрабатывать землю;\n<b>4)</b> ставить вместо, подставлять, замещать, заменять (integras copias vulneratis <i>bAl</i>; verbum pro verbo <i>C</i>);\n<b>5)</b> совершать подлог, подменять, подделывать (testamentum <i>C</i>; librum <i>Nep</i>); s. testem <i>Q</i> подставлять (подкупать) свидетеля;\n<b>6)</b> бросать вверх, подбрасывать (mataras inter carros rotasque <i>Cs</i>); поднимать: s. aliquem in equum <i>L</i> посадить кого-л. на коня; corpus saltu in equum s. <i>V</i> вскочить на коня; alnus se subjicit <i>V</i> ольха растёт в вышину; flamma subjecta <i>V</i> взвившееся вверх пламя;\n<b>7)</b> пододвигать, подводить, приближать, ставить близ, размещать подле (s. legiones castris <i>Cs</i>; s. se iniquis locis <i>Cs</i>): subjecti Orientis orae <i>H</i> жители востока;\n<b>8)</b> давать, передавать, вручать (alicui aliquid <i>C etc.</i>);\n<b>9)</b> внушать (alicui spem <i>L</i>; amor carmina subjicit <i>Prp</i>): sibi aliquid s. <i>Sulpicius ap. C</i> представить (вообразить) себе что-л. <i>или</i> вспомнить о чём-л.;\n<b>10)</b> присоединять, прибавлять (syllabam longam brevi <i>H</i>);\n<b>11)</b> отвечать, возражать (pauca alicui <i>V</i>). — <i>См. тж.</i> subjectus <i>и</i> subjecta."],
  ["subjugale", "<big>subjugale</big>\n\n<b>subjugale, is</b> <i>n</i> (<i>sc.</i> animal)\nподъяремное животное <i>Vlg</i>."],
  ["subjugalis", "<big>subjugalis</big>\n\n<b>subjugalis, e</b> <i>Vlg, Eccl</i>\n= subjugus."],
  ["subjugatio", "<big>subjugatio</big>\n\n<b>subjugatio, onis</b> <i>f</i>\nпорабощение, покорение <i>Eccl</i>."],
  ["subjugator", "<big>subjugator</big>\n\n<b>subjugator, oris</b> <i>m</i> [subjugo]\nпоработитель, покоритель (malorum <i>Ap</i>)."],
  ["subjugius", "<big>subjugius</big>\n\n<b>subjugius, a, um</b> [sub + jugum]\nяремный (lora <i>Cato, Vtr</i>)."],
  ["subjugo", "<big>subjugo</big>\n\n<b>subjugo, avi, atum, are</b> [sub + jugum]\n<b>1)</b> прогонять под игом (exercitum <i>Eutr</i>);\n<b>2)</b> покорять, подчинять, порабощать (hostes <i>Cld</i>; alienae dicioni aliquem <i>Lact</i>)."],
  ["subjugus", "<big>subjugus</big>\n\n<b>subjugus, a, um</b> [sub + jugum]\nподъяремный, запряжённый в ярмо: sudjugum molae machinariae dare <i>Ap</i> запрячь (осла) в ярмо мельничного жёрнова."],
  ["subjunctivus", "<big>subjunctivus</big>\n\n<b>subjunctivus, a, um</b> [subjungo] <i>грам.</i>\nсослагательный (modus)."],
  ["subjunctorium", "<big>subjunctorium</big>\n\n<b>subjunctorium, i</b> <i>n</i> [subjungo]\n<b>1)</b> запряжка <i>CTh</i>;\n<b>2)</b> упряжное животное <i>Ambr</i>."],
  ["subjungo", "<big>subjungo</big>\n\n<b>sub–jungo, junxi, junctum, ere</b>\n<b>1)</b> запрягать, впрягать (tigres curru <i>dat. V</i>);\n<b>2)</b> присоединять, прикреплять: puppis rostro subjuncta leones <i>V</i> корабельный нос, украшенный львами; utrumque nomen uni s. <i>Lcr</i> объединить оба слова (понятия) в одном; omnia oratori s. <i>C</i> относить всё к области (включать в сферу) ораторского искусства; carmina percussis nervis s. <i>O</i> сопровождать песню струнной музыкой;\n<b>3)</b> добавлять, присовокуплять (praeces <i>PJ</i>);\n<b>4)</b> подчинять, покорять, порабощать (s. urbes sub imperium <i>C</i> <i>или</i> imperio alicujus <i>VP</i>): mihi res, non me rebus s. conor <i>H</i> я стараюсь не подчиняться обстоятельствам, а подчинять их себе;\n<b>5)</b> подводить, подкладывать (immortalia fundamenta rebus s. <i>Lcr</i>);\n<b>6)</b> подставлять (litteram <i>AG</i>)."],
  ["sublabor", "<big>sublabor</big>\n\n<b>sub–labor, lapsus sum, labi</b> <i>depon.</i>\n<b>1)</b> соскальзывать, откатываться назад, <i>перен.</i> слабеть, никнуть: spes sublapsa <i>V</i> угасшая надежда; memoria sublabens <i>Sen</i> — <i>см.</i> situs III, 1;\n<b>2)</b> падать, валиться, разваливаться, рушиться (aedificia vetustate sublapsa <i>PJ</i>): imperfecta necesse est sublabantur aut succidant <i>Sen</i> (всё) несовершенное неизбежно приходит в упадок или (даже) гибнет;\n<b>3)</b> медленно просачиваться, впитываться, распространяться (lues sublabitur <i>V</i>)."],
  ["sublacrimans", "<big>sublacrimans</big>\n\n<b>sub–lacrimans, antis</b>\nнемного слезящийся (oculi <i>Veg</i>)."],
  ["sublamina", "<big>sublamina</big>\n\n<b>sub–lamina, ae</b> <i>f</i>\nнижняя пластинка, нижняя доска, подкладка <i>Cato</i>."],
  ["sublapsus", "<big>sublapsus</big>\n\n<b>sublapsus, a, um</b> <i>part. pf. к</i>\nsublabor."],
  ["Sublaqueum", "<big>Sublaqueum</big>\n\n<b>Sublaqueum, i</b> <i>n</i>\nСублаквей, <i>город эквов на реке Аниен, к юго-вост. от Тибура</i> (<i>ныне</i> Subiaco) <i>PM, T</i>."],
  ["sublate", "<big>sublate</big>\n\n<b>sublate</b> [sublatus]\n<b>1)</b> высоко, на повышенном уровне (Nilus s. fluens <i>Amm</i>);\n<b>2)</b> возвышенно (dicere <i>C</i>);\n<b>3)</b> высокомерно, гордо, надменно (dicere de se <i>C</i>)."],
  ["sublateo", "<big>sublateo</big>\n\n<b>sub–lateo, —, —, ere</b>\nбыть скрытым (febricula sublatens <i>CA</i>)."],
  ["sublatio", "<big>sublatio</big>\n\n<b>sublatio, onis</b> <i>f</i> [tollo]\n<b>1)</b> возвышение, подъём (animi <i>C</i>); повышение: s. <i>или</i> s. soni <i>Q</i> повышение голоса, ударение;\n<b>2)</b> уничтожение, кассирование, отмена (judicii <i>Q</i>);\n<b>3)</b> похищение (s. furtiva <i>Ambr</i>)."],
  ["sublatus", "<big>sublatus</big>\n\n<b>sublatus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> tollo;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> высокий, повышенный (vox <i>AG</i>);\n<b>2)</b> высокопарный, гордый, надменный: s. modus gladiis <i>Lcn</i> своеволие мечей, разнузданное насилие; s. aliqua re возгордившийся чём-л. (hac victoria <i>Cs</i>) <i>или</i> вследствие чего-л. (rebus secundis <i>V</i>)."],
  ["sublavo", "<big>sublavo</big>\n\n<b>sub–lavo, —, —, are</b>\nмыть снизу, подмывать (se aliqua re <i>Capit</i>)."],
  ["sublectio", "<big>sublectio</big>\n\n<b>sublectio, onis</b> <i>f</i> [sublego]\nсобирание, сбор <i>Tert</i>."],
  ["sublecto", "<big>sublecto</big>\n\n<b>sublecto, —, —, are</b> [<i>одного корня с</i> allecto]\nподольщаться, <i>перен.</i> дурачить <i>Pl</i>."],
  ["sublectus", "<big>sublectus</big>\n\n<b>sublectus, a, um</b> <i>part. pf. к</i>\nsublego."],
  ["sublego", "<big>sublego</big>\n\n<b>sub–lego, legi, lectum, ere</b>\n<b>1)</b> подбирать (s. quodcumque jaceret inutile <i>H</i>);\n<b>2)</b> похищать, красть (liberos parenti bus <i>Pl</i>);\n<b>3)</b> подслушивать (alicujus sermonem <i>Pl</i>; carmina <i>V</i>);\n<b>4)</b> избирать вместо (кого-л.) (s. aliquem in locum demortui <i>L</i>);\n<b>5)</b> пополнять новыми выборами (senatum <i>Just</i>)."],
  ["sublestus", "<big>sublestus</big>\n\n<b>sublestus, a, um</b>\nслабый (vinum <i>Pl</i>); жалкий, шаткий (fides <i>Pl</i>)."],
  ["sublevatio", "<big>sublevatio</big>\n\n<b>sublevatio, onis</b> <i>f</i> [sublevo]\nоблегчение, смягчение (s. et medicina <i>C</i>)."],
  ["sublevo", "<big>sublevo</big>\n\n<b>sub–levo, avi, atum, are</b>\n<b>1)</b> приподнимать, поднимать (aliquem ad pedes stratum <i>C</i>); подпирать (sinistra mentum <i>rhH</i>); поддерживать, держать, носить (aliquem umeris <i>PM</i>): jubis equorum sublevari <i>Cs</i> держаться за конские гривы; s. se <i>Cs</i> подняться, встать;\n<b>2)</b> поддерживать, помогать (defendere et s. aliquem <i>C</i>; s. patriam pecunia <i>Nep</i>);\n<b>3)</b> облегчать, ослаблять, понижать, смягчать, уменьшать (pericula <i>C</i>; laborem <i>Cs</i>; calamitatem, odia <i>C</i>; metum <i>T</i>)."],
  ["sublica", "<big>sublica</big>\n\n<b>sublica, ae</b> <i>f</i>\nстолб, кол, свая <i>Cs, L, Vtr</i>."],
  ["sublices", "<big>sublices</big>\n\n<b>sublices, um</b> <i>f</i>\nмостовые сваи, подпоры <i>Sl</i>."],
  ["sublicius", "<big>sublicius</big>\n\n<b>sublicius, a, um</b> [sublica]\nпокоящийся на столбах, свайный: pons s. <i>L, PM, Sen, T</i> мост на сваях (<i>построенный при Анке Марции деревянный мост через Тибр</i>)."],
  ["sublido", "<big>sublido</big>\n\n<b>sub–lido, —, lisum, ere</b> [laedo]\n<b>1)</b> надламывать (trabes sublisae <i>Vtr</i>);\n<b>2)</b> <i>перен.</i> сдавливать, приглушать (murmur voce <i>Eccl</i>)."],
  ["subligaculum", "<big>subligaculum</big>\n\n<b>subligaculum, i</b> <i>n</i> [subligo]\nнабедренная повязка <i>Vr, C</i>."],
  ["subligar", "<big>subligar</big>\n\n<b>subligar, aris</b> <i>n</i> <i>M, J, PM</i>\n= subligaculum."],
  ["subligatio", "<big>subligatio</big>\n\n<b>subligatio, onis</b> <i>f</i> [subligo]\nподвязывание (uvarum <i>Pall</i>)."],
  ["subligo", "<big>subligo</big>\n\n<b>subligo, avi, atum, are</b>\nподвязывать (vites <i>Cato</i>); подпоясывать, опоясывать (lateri ensem <i>V</i>): subligatus <i>M</i> с повязкой на бёдрах."],
  ["sublime", "<big>sublime</big>\n\n<b>I sublime</b> <i>adv.</i> [sublimis]\n<b>1)</b> высоко наверху (humine an s. <i>C</i>); вверх (aer s. fertur <i>C</i>; sublimius caput attollere <i>O</i>);\n<b>2)</b> возвышенно, в высоком стиле (dicere <i>Q</i>).\n\n<b>II sublime, is</b> <i>n</i>\nвысота, вышина, высь (per s. volare <i>PM</i>)."],
  ["sublimen", "<big>sublimen</big>\n\n<b>sublimen (-em)</b> <i>adv.</i> <i>Pl, Ter</i>\n= sublime I, 1."],
  ["sublimis", "<big>sublimis</big>\n\n<b>sublimis, e</b>\n<b>1)</b> высокий, возвышенный, вздымающийся (columna <i>O</i>; cacumen montis <i>O</i>); поднятый (flagellum <i>H</i>); рослый, крупный (armenta <i>Col</i>); высоко находящийся (aranea <i>Ctl</i>): aliquem sublimem ferre <i>Pl</i> нести кого-л. на плечах; sublimem aliquem rapere <i>Pl, Ter, V etc.</i> унести кого-л. ввысь; s. in equo <i>V</i> верхом на коне; s. abiit <i>L</i> он поднялся вверх (на небо);\n<b>2)</b> высокий, возвышенный, величественный (mens <i>O</i>; carmina <i>J</i>; facinora <i>Ap</i>; vir <i>Vr</i>);\n<b>3)</b> высокомерный (s. cupidusque <i>H</i>)."],
  ["sublimitas", "<big>sublimitas</big>\n\n<b>sublimitas, atis</b> <i>f</i> [sublimis]\n<b>1)</b> высокий рост (corporis <i>Q</i>);\n<b>2)</b> возвышенность, приподнятость (cellarum <i>Col</i>): sublimitatis avidus тянущийся вверх (natura cucurbitarum <i>PM</i>);\n<b>3)</b> высокий стиль, возвышенность, величие (Platonica <i>PM</i>)."],
  ["sublimiter", "<big>sublimiter</big>\n\n<b>sublimiter</b> [sublimis]\n<b>1)</b> вверх, отвесно, прямо (stare <i>Cato</i>);\n<b>2)</b> высоко (attollere caput <i>O</i>);\n<b>3)</b> возвышенно (dicere <i>Q</i>)."],
  ["sublimitus", "<big>sublimitus</big>\n\n<b>sublimitus</b> <i>adv.</i>\nвверх, наверх (equum insilire <i>Fronto</i>)."],
  ["sublimo", "<big>sublimo</big>\n\n<b>sublimo, avi, atum, are</b> [sublimis]\n<b>1)</b> высоко поднимать, вздымать, воздевать (candentem facem in caelo <i>Enn</i> — <i>о солнце</i>): se s. подниматься, взлетать (nubium tenus <i>Ap</i>);\n<b>2)</b> возвышать, высоко возносить (humilia <i>Ap</i>); возвеличивать (aliquem <i>Macr</i>)."],
  ["sublimus", "<big>sublimus</big>\n\n<b>I sublimus, a, um</b> <i>арх. Acc, Enn, Lcr, Sl</i>\n= sublimis.\n\n<b>II sub–limus, a, um</b>\nнемного косящий (oculi <i>Tert</i>)."],
  ["sublinguium", "<big>sublinguium</big>\n\n<b>sub–linguium, ī</b> <i>n</i> [lingua] <i>предпол.</i>\nнадгортанник <i>Is</i>."],
  ["sublingulo", "<big>sublingulo</big>\n\n<b>sub–lingulo, ōnis</b> <i>m</i> [lingo]\nлюбящий подлизывать блюда: s. coqui <i>Pl</i> поварёнок."],
  ["sublino", "<big>sublino</big>\n\n<b>sub–lino, lēvī, litum, ere</b>\n<b>1)</b> подмазывать, намазывать снизу (aliquid alicui rei <i>PM</i>);\n<b>2)</b> наводить грунт, грунтовать (s. caeruleum sandicem <i>PM</i>);\n<b>3)</b> окрашивать (aliquid atramento <i>PM</i>): alicujus (alicui) os s. <i>погов. Pl</i> дурачить кого-л."],
  ["sublisus", "<big>sublisus</big>\n\n<b>sublisus, a, um</b> <i>part. pf. к</i>\nsublido."],
  ["sublitus", "<big>sublitus</big>\n\n<b>sublitus, a, um</b> <i>part. pf. к</i>\nsublino."],
  ["sublividus", "<big>sublividus</big>\n\n<b>sub–lividus, a, um</b>\nсиневатый <i>CC</i>."],
  ["sublucanus", "<big>sublucanus</big>\n\n<b>sub–lucanus, a, um</b> [sub + lux]\nпредрассветный: sublucanis temporibus <i>PM</i> в предрассветные часы."],
  ["subluceo", "<big>subluceo</big>\n\n<b>sub–luceo, —, —, ere</b>\n<b>1)</b> быть слабо освещенным (Aries sublucet <i>C</i>); слабо светить, чуть озарять (crepuscula sublucent <i>O</i>);\n<b>2)</b> отсвечивать, отливать: violae nigrae purpura sublucet <i>V</i> тёмно-лиловый цвет отливает пурпуром; s. mixto rubore <i>O</i> иметь примесь красноты."],
  ["sublucidus", "<big>sublucidus</big>\n\n<b>sub–lucidus, a, um</b>\nслабо освещенный, чуть озарённый, полутёмный (lucus <i>Ap</i>; silvae <i>Amm</i>)."],
  ["subluo", "<big>subluo</big>\n\n<b>sub–luo, lui, lutum, ere</b>\n<b>1)</b> подмывать (aliquid aqua calida <i>CC, Col</i>);\n<b>2)</b> протекать мимо, омывать (montem flumen subluit <i>Cs</i>)."],
  ["sublustris", "<big>sublustris</big>\n\n<b>sub–lustris, e</b> [sub + lux]\nслабо озарённый, еле освещенный: s. nox <i>L, V, H</i> вечерние сумерки, ночной полумрак; s. umbra noctis <i>V</i> прозрачная тьма ночи; lumina sublustria первые проблески (eloquentiae Latinae <i>AG</i>)."],
  ["subluteus", "<big>subluteus</big>\n\n<b>sub–luteus, a, um</b>\nжелтоватый, отливающий желтизной (color columbarum <i>Ap</i>)."],
  ["sublutus", "<big>sublutus</big>\n\n<b>sublutus, a, um</b> <i>part. pf. к</i>\nsubluo."],
  ["subluvies", "<big>subluvies</big>\n\n<b>subluvies, ei</b> <i>f</i> [subluo]\n<b>1)</b> грязь: via subluvie caenosa lubrica <i>Ap</i> дорога, скользкая от густой грязи;\n<b>2)</b> нарыв на ноге <i>Col, PM</i>."],
  ["submaestus", "<big>submaestus</big>\n\n<b>sub–maestus, a, um</b>\nнемного грустный, приунывший <i>Amm</i>."],
  ["subman-", "<big>subman-</big>\n\n<b>sub–man-</b> <i>v. l.</i>\n= summan-."],
  ["submergo", "<big>submergo</big>\n\n<b>sub–mergo, mersi, mersum, ere</b>\n<b>1)</b> погружать (ferrum in unda <i>O</i>); топить (classem ponto <i>V</i>; navem <i>T</i>); <i>med.-pass.</i> погружаться, тонуть (voraginibus <i>C</i>);\n<b>2)</b> прекращать (publicam lectionem <i>Eccl</i>)."],
  ["submersio", "<big>submersio</big>\n\n<b>submersio, onis</b> <i>f</i> [submergo]\n<b>1)</b> погружение, потопление <i>Eccl</i>;\n<b>2)</b> подавление (libidinum <i>Aug</i>)."],
  ["submersus", "<big>submersus</big>\n\n<b>I submersus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> submergo;\n<b>2.</b> <i>adj.</i> живущий в воде, водяной <i>или</i> морской (beluae <i>C</i>).\n\n<b>II submersus, (us) </b> <i>m</i> <i>Tert</i>\n= submersio."],
  ["submerus", "<big>submerus</big>\n\n<b>sub–merus, a, um</b>\nпочти чистый (vinum <i>Pl</i>)."],
  ["submigratio", "<big>submigratio</big>\n\n<b>sub–migratio, onis</b> <i>f</i>\nвыселение, переселение <i>Amm</i>."],
  ["subminia", "<big>subminia</big>\n\n<b>subminia, ae</b> <i>f</i> [sub + minium]\nсубминия, <i>женское платье красноватого цвета</i> <i>Pl</i>."],
  ["subministratio", "<big>subministratio</big>\n\n<b>sub–ministratio, onis</b> <i>f</i>\nподача, доставление, дарование (incorruptibilitatis <i>Tert</i>)."],
  ["subministrator", "<big>subministrator</big>\n\n<b>subministrator, oris</b> <i>m</i> [subministro]\nпоставщик, организатор (s. libidinum testisque <i>Sen</i>)."],
  ["subministratus", "<big>subministratus</big>\n\n<b>sub–ministratus, (us) </b> <i>Macr</i>\n= eubministratio."],
  ["subministro", "<big>subministro</big>\n\n<b>sub–ministro, avi, atum, are</b>\nпоставлять, снабжать, давать (alicui pecuniam <i>C</i>, frumentum <i>Cs</i>, tela <i>C, Q</i>); предоставлять (auxilia hostibus <i>Cs</i>); доставлять (occasiones alicui <i>Su</i>); внушать (timores <i>Sen</i>)."],
  ["submisse", "<big>submisse</big>\n\n<b>submisse</b> [submissus]\n<b>1)</b> тихо, спокойно, без подъёма (dicere <i>C</i>);\n<b>2)</b> скромно, кротко (se gerere <i>C</i>);\n<b>3)</b> покорно, униженно (alicui supplicare <i>C</i>)."],
  ["submissim", "<big>submissim</big>\n\n<b>submissim</b> <i>Su, AG</i>\n= submisse."],
  ["submissio", "<big>submissio</big>\n\n<b>submissio, onis</b> <i>f</i> [stibmitto]\n<b>1)</b> понижение (vocis <i>C</i>);\n<b>2)</b> спокойный тон (orationis <i>C</i>);\n<b>3)</b> ослабление, принижение (nec elatio, nec s. <i>C</i>);\n<b>4)</b> смирение, кротость <i>Eccl</i>."],
  ["submissus", "<big>submissus</big>\n\n<b>I submissus, a, um</b> [submitto]\n<b>1)</b> опущенный, склонённый (brachia <i>O</i>): submisso vertice <i>O</i> склонив голову, нагнувшись;\n<b>2)</b> ниспадающий (capillus <i>Su</i>);\n<b>3)</b> тихий, спокойный, сдержанный (vox <i>C</i>);\n<b>4)</b> кроткий, покорный (civitates <i>Hirt</i>; preces <i>Lcn</i>);\n<b>5)</b> низменный, низкий (adulatio <i>Q</i>).\n\n<b>II submissus, us</b> <i>m</i>\nдопущение, введение (erroris <i>Tert</i>)."],
  ["submitto", "<big>submitto</big>\n\n<b>sub–mitto, misi, missum, ere</b>\n<b>1)</b> подставлять (aliquid alicui rei <i>Col</i>);\n<b>2)</b> (<i>о животных</i>) подводить, припускать (tauris vaccas <i>Pall</i>);\n<b>3)</b> опускать (fasces <i>L</i>); склонять (caput <i>O</i>); бросать (in Tiberim submissus <i>Lampr</i>): flexu genu s. <i>O</i>, <i>тж.</i> se s. ad genua <i>или</i> genu submitti <i>QC</i> опускаться на колени; submissa fastigio planities <i>L</i> равнина, идущая под уклон;\n<b>4)</b> понижать, <i>med.-pass.</i> убывать, понижаться (Tiberis aestate submittitur <i>PJ</i>): s. animum <i>L</i> пасть духом; s. furorem <i>V</i> унять свой гнев, успокоиться; s. orationem <i>Q</i> умерять тон речи; s. pretia <i>PM</i> понижать (сбавлять) цены;\n<b>5)</b> подчинять (Se alicui <i>Just</i>): s. imperium alicui <i>L</i> подчинить (свою) власть власти другого; s. animos amori <i>V</i> подчинить (свою) гордость силе любви; se culpae s. <i>O</i> провиниться; se s. in amicitia <i>C</i> быть запросто (на равной ноге) с друзьями;\n<b>6)</b> отпускать, отращивать (crinem <i>T</i>; barbam <i>Sen</i>);\n<b>7)</b> выращивать, разводить (vitulos <i>V</i>; tauros <i>V</i>);\n<b>8)</b> производить, рождать (capreas <i>H</i>): tibi tellus submittit flores <i>Lcr</i> земля устилает твой (Венеры) путь цветами;\n<b>9)</b> посылать, присылать (milites auxilio <i>Cs</i>);\n<b>10)</b> (тайно) подсылать (aliquem alicui <i>C</i>);\n<b>11)</b> посылать на выручку (iaborantibus <i>C</i>);\n<b>12)</b> поднимать (oculos <i>O</i>; falces aliae submissae, aliae in terram demissae <i>QC</i>)."],
  ["submoen-", "<big>submoen-</big>\n\n<b>sub–moen-</b> <i>v. l.</i>\n= summoen-."],
  ["submoleste", "<big>submoleste</big>\n\n<b>sub–moleste</b> [submolestus]\nс некоторой досадой, с известным неудовольствием (aliquid ferre <i>C</i>)."],
  ["submolestus", "<big>submolestus</big>\n\n<b>sub–molestus, a, um</b>\nдовольно неприятный, несколько досадный (alicui <i>C</i>)."],
  ["submoneo", "<big>submoneo</big>\n\n<b>sub–moneo, ui, —, ere</b>\nговорить по секрету, шепнуть (s. aliquem <i>Ter</i>): nullo submonente <i>Su</i> без чьего бы то ни было подсказывания."],
  ["submorosus", "<big>submorosus</big>\n\n<b>sub–morosus, a, um</b>\nнемного брюзгливый, довольно сердитый (ridicula <i>C</i>)."],
  ["submosse", "<big>submosse</big>\n\n<b>submosse</b> <i>H</i>\n= submovisse (<i>inf. pf. к</i> submoveo)."],
  ["submotus", "<big>submotus</big>\n\n<b>submotus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> submoveo;\n<b>2.</b> <i>adj.</i> отдалённый, дальний (terrae <i>Mela</i>; locus <i>Amm</i>)."],
  ["submoveo", "<big>submoveo</big>\n\n<b>sub–moveo, movi, motum, ere</b>\n<b>1)</b> удалять, прогонять (hostes a porta <i>Cs</i>; ex muro <i>Cs</i>);\n<b>2)</b> изгонять (aliquem Urbe atque Italia <i>Su</i>);\n<b>3)</b> разгонять, рассеивать (turbam <i>L</i>): summovendo iter facere <i>L</i> оттеснив толпу, дать дорогу; submoto <i>L</i> (= quum submotum esset) когда место было очищено (от народа); cui submovetur <i>Sen</i> тот, перед кем (ликторы) очищают путь;\n<b>4)</b> устранять, отстранять (legatos <i>L</i>): s. aliquem a re publica <i>Q, VP</i> держать кого-л. вдали от Государственных дел;\n<b>5)</b> удерживать (aliquem a bello <i>L</i>, a maleficio <i>C</i>);\n<b>6)</b> отгонять прочь, отбрасывать (curas <i>H</i>; pudorem <i>Col</i>);\n<b>7)</b> отводить, отвращать (ictus <i>O</i>);\n<b>8)</b> отодвигать, выдвигать вперёд, заставлять отступить (maris litora <i>H</i>);\n<b>9)</b> отдалять, отделять (Alpes Germaniam ab Italia submovent <i>PM</i>). — <i>См. тж.</i> submotus."],
  ["submultiplex", "<big>submultiplex</big>\n\n<b>sub–multiplex, icis</b>, <i>и</i> <b>submultiplus, a, um</b>\nсодержащийся целое число раз (в большем числе) <i>Boet</i>."],
  ["submurmuratio", "<big>submurmuratio</big>\n\n<b>sub–murmuratio, onis</b> <i>f</i>\nбормотание про себя <i>Aug</i>."],
  ["submurmuro", "<big>submurmuro</big>\n\n<b>sub–murmuro, —, —, are</b>\n<b>1)</b> бормотать про себя <i>Aug</i>;\n<b>2)</b> нашёптывать (<i>sc.</i> alicui <i>Aug</i>)."],
  ["submussus", "<big>submussus</big>\n\n<b>sub–mussus, i</b> <i>m</i> [musso]\nцедящий сквозь зубы, бормотун <i>Naev</i>."],
  ["submuto", "<big>submuto</big>\n\n<b>sub–muto, —, —, are</b>\nзаменять (verba pro verbis <i>C</i>)."],
  ["subnascor", "<big>subnascor</big>\n\n<b>sub–nascor, natus sum, nasci</b> <i>depon.</i>\n<b>1)</b> вырастать (расти) снизу: viridari ab subnatis herbis <i>O</i> окрашиваться в зелёный цвет от растущих внизу трав (<i>о воде</i>); cortex subnascente alio expellitur <i>PM</i> (старая) кора вытесняется другой, вырастающей под ней;\n<b>2)</b> возникать вновь, вновь образовываться (ulcera subnascebantur <i>Sen</i>)."],
  ["subnato", "<big>subnato</big>\n\n<b>sub–nato, —, —, are</b>\nплавать внизу <i>Ap</i>: pars subnatat unda, pars exstat aquis <i>Sil</i> часть (тела Скирона) плавает под волнами, часть выдаётся над водой."],
  ["subnatus", "<big>subnatus</big>\n\n<b>subnatus, a, um</b> <i>part. pf. к</i>\nsubnascor."],
  ["subnavigo", "<big>subnavigo</big>\n\n<b>sub–navigo, avi, —, are</b>\nплыть мимо, огибать (Cyprum <i>Vlg</i>)."],
  ["subnecto", "<big>subnecto</big>\n\n<b>sub–necto, nexui, nexum, ere</b>\n<b>1)</b> подвязывать (velum antennis — <i>dat. O</i>); подбирать, застёгивать (fibula vestem subnectit <i>V</i>); подпоясывать, прикреплять (cingula mammae <i>V</i>); скреплять, убирать (crines auro <i>V</i>);\n<b>2)</b> присоединять, добавлять (fabulam <i>Just</i>)."],
  ["subnego", "<big>subnego</big>\n\n<b>sub–nego, avi, —, are</b>\nпочти отказывать (aliquid alicui <i>C</i>)."],
  ["subnervo", "<big>subnervo</big>\n\n<b>subnervo, avi, —, are</b> [sub + nervus]\n<b>1)</b> подрезать сухожилия (s. equum, taurum <i>Eccl</i>);\n<b>2)</b> обезвреживать, опровергать (calumnias <i>Ap</i>)."],
  ["subneuter", "<big>subneuter</big>\n\n<b>sub–neuter, tra, trum</b> <i>Ap</i>\n= alteruter."],
  ["subnexus", "<big>subnexus</big>\n\n<b>subnexus, a, um</b> <i>part. pf. к</i>\nsubnecto."],
  ["subniger", "<big>subniger</big>\n\n<b>sub–niger, nigra, nigrum</b>\nчерноватый (color cutis <i>CC</i>): subnigris oculis <i>Pl</i> темноглазый."],
  ["subnixus", "<big>subnixus</big>\n\n<b>subnixus (subnisus), a, um</b> [sub + nitor]\n<b>1)</b> опирающийся, опёртый, упирающийся, прислонившийся, <i>перен.</i> поддерживаемый (validis propinquitatibus <i>T</i>; exercitus Galliarum opibus s. <i>T</i>): s. Verticibus caeli <i>C</i> упираясь в полюса; solio s. <i>V</i> опершись на престол, <i>т. е.</i> сидя на престоле; mentum (<i>acc. gr.</i>) mitra s. <i>V</i> с подвязанной к подбородку митрой; subnixis alis <i>Pl</i> подбоченившись; urbs muro subnixa <i>V</i> город, окружённый (защищенный) стеной;\n<b>2)</b> возлагающий надежду, рассчитывающий, полагающийся (auxiliis — <i>abl. L</i>);\n<b>3)</b> возгордившийся (victoria Cannensi <i>L</i>);\n<b>4)</b> уверенный, твёрдый (animus <i>L</i>)."],
  ["subnotatio", "<big>subnotatio</big>\n\n<b>subnotatio, onis</b> <i>f</i> [subnoto]\nподпись <i>CJ</i>."],
  ["subnoto", "<big>subnoto</big>\n\n<b>sub–noto, avi, atum, are</b>\n<b>1)</b> помечать внизу, приписывать (aliquid in inferiori linea <i>Ap</i>); записывать (nomina <i>Su</i>); подписывать (libellos <i>PJ</i>);\n<b>2)</b> отмечать, указывать (aliquem digito <i>M</i>)."],
  ["subnuba", "<big>subnuba</big>\n\n<b>subnuba, ae</b> <i>f</i> [sub + nubo]\nналожница <i>O</i>."],
  ["subnubilus", "<big>subnubilus</big>\n\n<b>sub–nubilus, a, um</b>\nнемного облачный, довольно пасмурный (nox <i>Cs</i>); полутёмный, тенистый (limes longa s. umbra <i>O</i>)."],
  ["subo", "<big>subo</big>\n\n<b>subo, —, —, are</b> (<i>греч.</i>)\nнаходиться в состоянии полового возбуждения <i>Lcr, H, PM etc.</i>"],
  ["subobscenus", "<big>subobscenus</big>\n\n<b>sub–obscenus, a, um</b>\nне совсем приличный, несколько непристойный (ridiculum <i>C</i>)."],
  ["subobscure", "<big>subobscure</big>\n\n<b>subobscure</b> [subobscurus]\nтемновато, не совсем ясно (dicere <i>AG, Eccl</i>)."],
  ["subobscurus", "<big>subobscurus</big>\n\n<b>sub–obscurus, a, um</b>\nтемноватый <i>или</i> малозаметный (leprae species <i>Vlg</i>); малопонятный (ingressio <i>C</i>)."],
  ["subocularis", "<big>subocularis</big>\n\n<b>sub–ocularis, e</b>\nнаходящийся под глазами (venae <i>Veg</i>)."],
  ["subodiosus", "<big>subodiosus</big>\n\n<b>sub–odiosus, a, um</b>\nнесколько досадный, довольно неприятный <i>C</i>."],
  ["suboffendo", "<big>suboffendo</big>\n\n<b>sub–offendo, —, —, ere</b>\nдопустить некоторый промах, возбудить известное неудовольствие (apud aliquem <i>C</i>)."],
  ["subolat", "<big>subolat</big>\n\n<b>subolat</b> <i>Ter</i>\n= suboleat (<i>praes. conjct. к</i> suboleo)."],
  ["suboleo", "<big>suboleo</big>\n\n<b>sub–oleo, —, —, ere</b>\nпопахивать; (hoc) subolet mihi <i>Pl, Ter</i> мне это заметно, я это чувствую."],
  ["suboles", "<big>suboles</big>\n\n<b>suboles, is</b> <i>f</i> [<i>одного корня с</i> alo, abolesco, proles]\n<b>1)</b> поросль, побег (serere ex subolibus <i>Col</i>); молодняк, приплод (s. gregis <i>H</i>);\n<b>2)</b> потомство, дети (producere subolem <i>H</i>); потомок, отпрыск, дитя (imperatorum <i>L</i>; deum = deorum <i>V</i>); молодёжь (Romae <i>H</i>); молодое поколение (robur et s. militum <i>Asinius Pollio ap. C</i>)."],
  ["subolesco", "<big>subolesco</big>\n\n<b>subolesco, —, —, ere</b> [suboles]\nподрастать (juventus subolescens <i>L</i>)."],
  ["subolfacio", "<big>subolfacio</big>\n\n<b>sub–olfacio, —, —, ere</b>\nчуять <i>Pt</i>."],
  ["suborior", "<big>suborior</big>\n\n<b>sub–orior, —, iri</b> <i>depon.</i>\nвозрождаться, вновь возникать (materiae ex infinito suboritur copia <i>Lcr</i>); образовываться, восстанавливаться (opulentia tot saeculis suboriens <i>PM</i>)."],
  ["subornator", "<big>subornator</big>\n\n<b>subornator, oris</b> <i>m</i> [suborno]\nподстрекатель, совратитель <i>Lampr, Amm</i>."],
  ["suborno", "<big>suborno</big>\n\n<b>sub–orno, avi, atum, are</b>\n<b>1)</b> снабжать, наделять (aliquem pecunia <i>Antonius ap. C</i>);\n<b>2)</b> одевать, наряжать (aliquem in militum cultum <i>Sen</i>; in modum lictorum <i>VP</i>; myrtea subornatus gausapa <i>Pt</i>);\n<b>3)</b> вооружать, одарять (aliquem praeceptis contra mortem <i>Sen</i>);\n<b>4)</b> подговаривать (falsum testem <i>C</i>); подсылать, подкупать (aliquem ad caedem <i>L</i>);\n<b>5)</b> нанимать <i>или</i> назначать (bucinatorem habere subornatum <i>Pt</i>);\n<b>6)</b> подстрекать (in bellum <i>Just</i>)."],
  ["subortus", "<big>subortus</big>\n\n<b>subortus, us</b> <i>m</i> [suborior]\nвозрождение, восстановление: ex alio atque alio lucem jactare subortu <i>Lcr</i> испускать всё новые и новые лучи."],
  ["subostendo", "<big>subostendo</big>\n\n<b>sub–ostendo, endi, ensum, ere</b>\nпоказывать мимоходом <i>или</i> между прочим, давать понять (aliquid <i>Tert</i>)."],
  ["subp-", "<big>subp-</big>\n\n<b>subp-</b> <i>v. l.</i>\n= supp-."],
  ["subradio", "<big>subradio</big>\n\n<b>sub–radio, —, —, are</b>\nпоказывать в ясном свете (resurrectionem <i>Tert</i>)."],
  ["subrado", "<big>subrado</big>\n\n<b>sub–rado, rasi, rasum, ere</b>\n<b>1)</b> соскабливать снизу, подскабливать (arbor strigili subrasa <i>Pall</i>);\n<b>2)</b> (<i>о реке</i>) омывать (barbaros fines <i>Amm</i>)."],
  ["subrancidus", "<big>subrancidus</big>\n\n<b>sub–rancidus, a, um</b>\nнесколько протухший (caro <i>C</i>)."],
  ["subraucus", "<big>subraucus</big>\n\n<b>sub–raucus, a, um</b>\nнемного хриплый <i>или</i> глуховатый (vox <i>C</i>)."],
  ["subrectio", "<big>subrectio</big>\n\n<b>subrectio, onis</b> <i>f</i> [subrigo]\nвоздвижение (phallorum fascinorumque <i>Eccl</i>)."],
  ["subrectito", "<big>subrectito</big>\n\n<b>sub–rectito, —, —, are</b> [<i>frequ. к</i> surgo]\nиметь обыкновение подниматься, вставать, уходить (e convivio <i>Cato ap. AG</i>)."],
  ["subrectus", "<big>subrectus</big>\n\n<b>subrectus, a, um</b> <i>part. pf. к</i>\nsubrigo."],
  ["subrefectus", "<big>subrefectus</big>\n\n<b>sub–refectus, a, um</b>\nкое-как восстановившийся, оправившийся <i>VP</i>."],
  ["subregulus", "<big>subregulus</big>\n\n<b>sub–regulus, i</b> <i>m</i>\nвассальный царёк <i>Amm</i>."],
  ["subrelinquo", "<big>subrelinquo</big>\n\n<b>sub–relinquo, —, —, ere</b>\nоставлять (после себя) (aliquem <i>Vlg</i>)."],
  ["subremaneo", "<big>subremaneo</big>\n\n<b>sub–remaneo, —, —, ere</b>\nоставаться <i>Tert</i>."],
  ["subremigo", "<big>subremigo</big>\n\n<b>sub–remigo, —, —, are</b>\n(как бы) грести, помогать грести (brachiis <i>PM</i>): dextra puppim tenet ac laeva subremigat <i>V</i> (нимфа Кимодокея) правой рукой держится за корму, левой гребёт."],
  ["subrenalis", "<big>subrenalis</big>\n\n<b>sub–renalis, e</b>\nпочечный (morbus <i>Veg</i>)."],
  ["subrepente", "<big>subrepente</big>\n\n<b>sub–repente</b>\nдовольно неожиданно <i>Eccl</i>."],
  ["subrepo", "<big>subrepo</big>\n\n<b>sub–repo, repsi, reptum, ere</b>\nподползать (sub tabulas <i>C</i>); подкрадываться (s. moenia urbis <i>H</i>; ne pro vero falsa subrepant <i>Sen</i>); незаметно подкатываться (subrepit vinea muris <i>Lcn</i>); прокрадываться, вползать (terror subrepit pectora <i>Sil</i>): s. alicui <i>C</i> Застигать кого-л. врасплох."],
  ["subrepsti", "<big>subrepsti</big>\n\n<b>subrepsti</b> <i>Ctl</i>\n= subrepsisti (<i>pf. к</i> subrepo)."],
  ["subrepticius", "<big>subrepticius</big>\n\n<b>subrepticius, a, um</b> [subrepo]\nукрытый, тайный (amor <i>Pl</i>)."],
  ["subreptio", "<big>subreptio</big>\n\n<b>I subreptio, onis</b> <i>f</i> [subripio]\nпохищение, кража <i>Ap</i>.\n\n<b>II subreptio, onis</b> <i>f</i> [subrepo]\nприменение обманных средств, мошенничество <i>CJ</i>."],
  ["subreptivus", "<big>subreptivus</big>\n\n<b>subreptivus, a, um</b> [subrepo]\nобманный, поддельный <i>CTh</i>."],
  ["subreptus", "<big>subreptus</big>\n\n<b>I subreptus, a, um</b> <i>part. pf. к</i>\nsubrepo.\n\n<b>II subreptus, a, um</b> <i>part. pf. к</i>\nsubripio."],
  ["subrexi", "<big>subrexi</big>\n\n<b>subrexi</b> <i>pf. к</i>\nsubrigo."],
  ["subrideo", "<big>subrideo</big>\n\n<b>sub–rideo, risi, risum, ere</b>\nулыбаться <i>Vr, C, V, O etc.</i>"],
  ["subridicule", "<big>subridicule</big>\n\n<b>sub–ridicule</b>\nдовольно смешно <i>C</i>."],
  ["subrigo", "<big>subrigo</big>\n\n<b>subrigo (surrigo <i>и</i> surgo)</b>, <b>rexi, rectum, ere</b> [sub + rego]\n<b>1)</b> поднимать (mucronem <i>L</i>): subrecta cuspide <i>L</i> с поднятым щитом; terrae motus subrigit plana <i>Sen</i> землетрясение вздымает равнины;\n<b>2)</b> выпрямлять (lumbos <i>Pl</i>);\n<b>3)</b> настораживать (aures <i>V</i>);\n<b>4)</b> подниматься, вставать (e lectulo <i>C</i>; sella <i>и</i> de sella <i>C</i>); вставать с постели (ante lucem <i>C</i>): s. humo <i>O</i> подняться с земли; s. ad dicendum <i>C</i> подняться, чтобы начать речь; s. ad aetherias auras <i>или</i> ad lumina vitae <i>V</i> появиться на свет; non recordor, unde ceciderim, sed unde surrexerim <i>C</i> я вспоминаю не откуда упал, а откуда поднялся, <i>т. е.</i> стараюсь помнить о своих успехах и забыть о неудачах; s. in altum <i>Cld</i> подняться из низов, возвыситься; s. alicui <i>C</i> вставать перед кем-л.; s. ab aliquo <i>Aug</i> расстаться с кем-л.;\n<b>5)</b> подниматься, вздыматься: ignis surgit ab ara <i>O</i> огонь поднимается с жертвенника; surgens a puppi ventus <i>V</i> поднимающийся с кормы (<i>т. е.</i> попутный) ветер || порываться, устремляться (animi juvenum ad magna surgentes <i>Pt</i>);\n<b>6)</b> подниматься войной, двинуться: in Teucros surgit ab Arpis <i>V</i> (сын Тидея) идет войной на тевкров со стороны Арп;\n<b>7)</b> всходить (luna surgit <i>V</i>; astra ignea surgunt <i>V</i>): surgente die <i>L</i> с наступлением дня, с рассветом;\n<b>8)</b> расти: messes surgunt <i>V</i> посевы всходят; Ascanius surgens <i>V</i> подрастающий Асканий; surgens Carthaginis arx <i>V</i> воздвигаемый кремль Карфагена;\n<b>9)</b> возвышаться (supra prosam orationem <i>Q</i>);\n<b>10)</b> возникать (surgentia bella <i>V</i>): quae nunc animo sententia surgit? <i>V</i> какая мысль возникает теперь (у тебя)?; pugna aspera surgit <i>V</i> завязывается жестокий бой; rumor surrexit <i>T</i> появился слух; surgit amari aliquid <i>Lcr</i> начинает ощущаться какая-то горечь;\n<b>11)</b> начать говорить (surgit ad hos Ajax <i>O</i>)."],
  ["subriguus", "<big>subriguus</big>\n\n<b>sub–riguus, a, um</b>\nслегка орошённый (aut umidum aut subriguum solum <i>PM</i>)."],
  ["subringor", "<big>subringor</big>\n\n<b>sub–ringor, —, ringi</b> <i>depon.</i>\nнемного морщиться, выражать некоторое неудовольствие <i>C</i>."],
  ["subripio", "<big>subripio</big>\n\n<b>subripio, ripui, reptum, ere</b> [sub + rapio]\n<b>1)</b> красть, воровать (vasa ex sacro <i>C</i>); похищать (aliquid ab aliquo <i>C</i> <i>или</i> alicui <i>Sen</i>); хитростью захватывать (urbem <i>C</i>): virtus nec eripi nec subripi potest <i>C</i> добродетель не может быть ни (насильно) отнята, ни (тайком) похищена; qua materia subripiam diem? <i>O</i> каким делом заполнить мне день?;\n<b>2)</b> укрывать (crimina oculis alicujus <i>O</i>): s. aliquem morti <i>H</i> вырвать кого-л. у смерти; se s. alicui <i>Pl</i> ускользнуть (скрыться) от кого-л."],
  ["subrisio", "<big>subrisio</big>\n\n<b>subrisio, onis</b> <i>f</i> [subrideo]\nсмешок, улыбка <i>Hier</i>."],
  ["subrogatio", "<big>subrogatio</big>\n\n<b>subrogatio, onis</b> <i>f</i> [subrogo]\nизбрание взамен (кого-л.), заместительный выбор, замещение <i>Ambr</i>."],
  ["subrogo", "<big>subrogo</big>\n\n<b>sub–rogo, avi, atum, are</b>\n<b>1)</b> предложить перевыбрать (aliquem <i>L, C</i>); заново <i>или</i> взамен избирать (aliquem alicui <i>или</i> in locum alicujus <i>C, L, Dig, CJ</i>); дополнительно избрать (sibi aliquem collegam <i>C</i>);\n<b>2)</b> (<i>о законе</i>) дополнять (lex subrogatur, id est, adjicitur aliquid primae legi <i>Dig</i>)."],
  ["subrostrani", "<big>subrostrani</big>\n\n<b>sub–rostrani, orum</b> <i>m</i> [sub + rostra]\nзавсегдатаи общественных площадей (и слушающие всех ораторов), праздношатающиеся <i>Caelius ap. C</i>."],
  ["subrotatus", "<big>subrotatus</big>\n\n<b>subrotatus, a, um</b> [sub + rota]\nпоставленный на колёса, колёсный (aries <i>Vtr</i>)."],
  ["subrotundus", "<big>subrotundus</big>\n\n<b>sub–rotundus, a, um</b>\nкругловатый, округлый (radicula <i>CC</i>)."],
  ["subrubeo", "<big>subrubeo</big>\n\n<b>sub–rubeo, —, —, ere</b>\nбыть красноватым, рдеть, румяниться (purpureo mero <i>O</i>; grossi incipiunt s. <i>Pall</i>)."],
  ["subruber", "<big>subruber</big>\n\n<b>sub–ruber, bra, brum</b>\nкрасноватый (caro <i>CC</i>)."],
  ["subrubicundus", "<big>subrubicundus</big>\n\n<b>sub–rubicundus, a, um</b> <i>CC, Sen</i>\n= subruber."],
  ["subrufus", "<big>subrufus</big>\n\n<b>sub–rufus, a, um</b>\nкрасноватый (color <i>PM</i>); рыжеватый <i>Pl</i>."],
  ["subrumo", "<big>subrumo</big>\n\n<b>subrumo, —, —, are</b> [sub + ruma]\nкормить сосцами (agnos <i>Col</i>)."],
  ["subrumus", "<big>subrumus</big>\n\n<b>sub–rumus, a, um</b> [ruma]\nсосущий матку (agnus <i>Vr</i>)."],
  ["subruo", "<big>subruo</big>\n\n<b>sub–ruo, rui, rutum, ere</b>\n<b>1)</b> делать подкоп, подкапывать (arbores a radicibus <i>Cs</i>; murum <i>Cs, L</i>);\n<b>2)</b> подрывать, разрушать (libertatem <i>L</i>);\n<b>3)</b> губить, свергать (aliquem muneri bus <i>H</i>)."],
  ["subrupio", "<big>subrupio</big>\n\n<b>subrupio, rupui, ruptum, ere</b> <i>Pl</i>\n= subripio."],
  ["subrupticius", "<big>subrupticius</big>\n\n<b>subrupticius, a, um</b> [subripio]\nпохищенный (filius <i>Pl</i>)."],
  ["subrustice", "<big>subrustice</big>\n\n<b>subrustice</b>\nнесколько по-деревенски, грубовато <i>AG</i>."],
  ["subrusticus", "<big>subrusticus</big>\n\n<b>sub–rusticus, a, um</b>\nнемного деревенский, грубоватый <i>C, Ambr</i>."],
  ["subrutilo", "<big>subrutilo</big>\n\n<b>sub–rutilo, —, —, are</b>\nотливать красноватым цветом (iaspis subrutilans <i>Hier</i>)."],
  ["subrutilus", "<big>subrutilus</big>\n\n<b>sub–rutilus, a, um</b>\nкрасноватый (color <i>PM</i>); рыжеватый (frutex <i>PM</i>; caput <i>Su</i>)."],
  ["subrutus", "<big>subrutus</big>\n\n<b>subrutus, a, um</b> <i>part. pf. к</i>\nsubruo."],
  ["subsalsus", "<big>subsalsus</big>\n\n<b>sub–salsus, a, um</b>\nсолоноватый (aqua <i>CC</i>)."],
  ["subsannatio", "<big>subsannatio</big>\n\n<b>sub–sannatio, onis</b> <i>f</i> [sanna]\nнасмешливая гримаса, издевательство <i>Vlg</i>."],
  ["subsannator", "<big>subsannator</big>\n\n<b>subsannator, oris</b> <i>m</i> [subsanno]\nнасмешник <i>Vlg</i>."],
  ["subsannatorius", "<big>subsannatorius</big>\n\n<b>subsannatorius, a, um</b>\nнасмешливый, издевательский (sermo <i>Hier</i>)."],
  ["subsanno", "<big>subsanno</big>\n\n<b>subsanno, avi, —, are</b> [sanna]\nиздеваться, глумиться (s. aliquem <i>Vlg, Eccl</i>)."],
  ["subscribendarius", "<big>subscribendarius</big>\n\n<b>sub–scribendarius, i</b> <i>m</i>\nделопроизводитель <i>CTh</i>."],
  ["subscribo", "<big>subscribo</big>\n\n<b>sub–scribo, scripsi, scriptum, ere</b>\n<b>1)</b> делать внизу надпись, надписывать (alicui rei): s. aliquid sepulcro <i>O</i> написать что-л. на могиле || приписывать внизу (earum litterarum exemplum s. <i>C</i>);\n<b>2)</b> <i>юр.</i> выступать с письменным обвинением, письменно подтвердить чьё-л. обвинение: in L. Popillium subscripsit L. Gellius, quod is pecuniam accepisset <i>C</i> против Л. Попиллия Л. Геллий письменно заявил, что тот дал себя подкупить; alicui accusanti s. <i>Su</i> поддержать чьё-л. обвинение;\n<b>3)</b> (<i>о цензоре</i>) письменно излагать причину наложения взыскания (s. causam <i>C</i>; s. de judicio corrupto <i>C</i>);\n<b>4)</b> ставить подпись, подписывать (s. alicujus judicium <i>PJ</i>; s. testamento <i>Dig</i>): s. rationibus <i>или</i> rationes <i>Dig</i> подписывать счета, <i>т. е.</i> подтверждать их правильность; de supplicio capite damnati s. <i>Su</i> подписать смертный приговор;\n<b>5)</b> записывать, регистрировать (numerum aratorum <i>C</i>);\n<b>6)</b> поддерживать, присоединяться (odiis accusitionibusque Hannibalis <i>L</i>)."],
  ["subscriptio", "<big>subscriptio</big>\n\n<b>subscriptio, onis</b> <i>f</i> [subscribo]\n<b>1)</b> надпись (внизу) <i>C</i>;\n<b>2)</b> <i>юр.</i> субскрипция, обвинительное заявление <i>или</i> подпись под (чьим-л.) обвинительным заявлением <i>C, Sen, Q, AG</i>;\n<b>3)</b> (цензорская) помета (<i>об основаниях наложенного взыскания</i>) (s. censoria <i>C</i>);\n<b>4)</b> подпись <i>C, Dig</i>;\n<b>5)</b> перечень, список: jugerum (<i>gen. pl.</i>) s. atque professio <i>C</i> количество югеров согласно (произведённой) регистрации и (поданным) заявлениям."],
  ["subscriptor", "<big>subscriptor</big>\n\n<b>subscriptor, oris</b> <i>m</i> [subscribo]\n<b>1)</b> подписавший обвинительное заявление (<i>своё или чужое</i>), поддерживающий обвинение <i>C</i>;\n<b>2)</b> поддерживающий, одобряющий: subscriptores approbatoresque hujus verbi <i>AG</i> (писатели), которые принимали и одобряли это слово."],
  ["subscriptus", "<big>subscriptus</big>\n\n<b>subscriptus, a, um</b> <i>part. pf. к</i>\nsubscribo."],
  ["subscruposus", "<big>subscruposus</big>\n\n<b>sub–scruposus, a, um</b>\nдовольно мелочный, несколько придирчивый (meritorum examinator <i>Amm</i>)."],
  ["subscudo", "<big>subscudo</big>\n\n<b>subscudo, inis</b> <i>f</i> <i>Aug</i>\n= subscus."],
  ["subscus", "<big>subscus</big>\n\n<b>subs–cus, cudis</b> <i>f</i> [subs = sub (<i>как</i> abs = ab)+cudo II] <i>тех.</i>\nскрепа (шип) в виде двойного ласточкина хвоста <i>Pac, Cato, Vtr, Eccl</i>."],
  ["subseciv-", "<big>subseciv-</big>\n\n<b>subseciv-</b> <i>v. l.</i>\n= subsiciv-."],
  ["subseco", "<big>subseco</big>\n\n<b>sub–seco, secui, sectum, are</b>\nподсекать (carnem circa os <i>CC</i>); подрезывать, скашивать (manipulum falce <i>Vr</i>); обрезывать, подстригать (ungues ferro <i>O</i>); срезывать (papavereas comas ungue <i>O</i>)."],
  ["subsecundarius", "<big>subsecundarius</big>\n\n<b>sub–secundarius, a, um</b>\nпоследующий, позднейший: subsecundaria tempora <i>AG</i> часы досуга."],
  ["subsecutio", "<big>subsecutio</big>\n\n<b>subsecutio, onis</b> <i>f</i> [subsequor]\nпродолжение, последующее <i>Eccl</i>."],
  ["subsecutus", "<big>subsecutus</big>\n\n<b>subsecutus, a, um</b> <i>part. pf. к</i>\nsubsequor."],
  ["subsedi", "<big>subsedi</big>\n\n<b>subsedi</b> <i>pf. к</i>\nsubsido."],
  ["subsellium", "<big>subsellium</big>\n\n<b>sub–sellium, i</b> <i>n</i> [sella]\n<b>1)</b> скамья (senatorum, judicum <i>C</i>): imi subsellii vir <i>погов. Pl</i> человек совсем маленький (незначительный);\n<b>2)</b> <i>преим. pl.</i> судейская скамья, <i>перен.</i> судебное установление: rem ab subselliis in rostra deferre <i>C</i> перенести дело из суда на (публичную) трибуну;\n<b>3)</b> суд, судопроизводство: utraque subsellia <i>C</i> уголовное и гражданское судопроизводство; versatus in utrisque subselliis <i>C</i> действовавший и как судья и как защитник."],
  ["subsentator", "<big>subsentator</big>\n\n<b>sub–sentator, oris</b> <i>m</i> [sentio]\nльстец <i>Pl</i>."],
  ["subsentio", "<big>subsentio</big>\n\n<b>sub–sentio, sensi, —, ire</b>\n(тайком) подмечать, заприметить <i>Ter</i>."],
  ["subsequenter", "<big>subsequenter</big>\n\n<b>subsequenter</b>\nвпоследствии, в дальнейшем <i>Aug</i>."],
  ["subsequor", "<big>subsequor</big>\n\n<b>sub–sequor, secutus sum, sequi</b> <i>depon.</i>\n<b>1)</b> (<i>тж.</i> vestigia s. <i>Su</i>) следовать, идти по пятам (s. aliquem <i>Pl, Cs etc.</i>; fugientem <i>Pt</i>): s. signa <i>Cs</i> следовать за (своими) знамёнами; Caesar subsequebatur omnibus copiis <i>Cs</i> (за конницей) Цезарь следовал со всем войском; ducis consilia subsequitur favor militum <i>L</i> указания полководца одобряются бойцами; manus digitis subsequitur verba <i>C</i> (движения) пальцев руки сопровождают речь; subsequenti anno <i>PM</i> в следующем году; Platonem s. <i>C</i> следовать (подражать) Платону; male s. verba dicentis <i>Sen</i> не поспевать записывать слова говорящего;\n<b>2)</b> воспроизводить (hos motus <i>C</i>)."],
  ["subsericus", "<big>subsericus</big>\n\n<b>sub–sericus, a, um</b>\nполушёлковый (vestis <i>Lampr, Treb</i>; tunica <i>Vop</i>)."],
  ["subsero", "<big>subsero</big>\n\n<b>I sub–sero, —, sertum, ere</b> [sero I]\nподводить, вводить (super hoc pauca <i>Amm</i>): subsertis manibus <i>Ap</i> сложив руки.\n\n<b>II sub–sero, —, —, ere</b> [sero II]\nсажать, сеять (вместо чего-л.) (vineam <i>Col</i>)."],
  ["subsertus", "<big>subsertus</big>\n\n<b>subsertus, a, um</b> <i>part. pf. к</i>\nsubsero i."],
  ["subservio", "<big>subservio</big>\n\n<b>sub–servio, —, —, ire</b>\n<b>1)</b> быть готовым к услугам, служить (alicui <i>Pl</i>);\n<b>2)</b> поддерживать, подтверждать (orationi alicujus <i>Ter</i>)."],
  ["subsesquiquartus", "<big>subsesquiquartus</big>\n\n<b>sub–sesquiquartus, a, um</b>\nнаходящийся в отношении 4 к 5 <i>Boet</i>."],
  ["subsessa", "<big>subsessa</big>\n\n<b>subsessa, ae</b> <i>f</i> [subsido]\nзасада <i>Veg</i>."],
  ["subsessor", "<big>subsessor</big>\n\n<b>subsessor, oris</b> <i>m</i> [subsido]\nсидящий в засаде (cum venabulo <i>Pt</i>): s. matrimonii alieni <i>VM</i> соблазнитель чужой жены."],
  ["subsicivum", "<big>subsicivum</big>\n\n<b>subsicivum, i</b> <i>n</i> [subsicivus]\n(оставшийся после размежевания) клочок земли <i>Vr, Su</i>."],
  ["subsicivus", "<big>subsicivus</big>\n\n<b>subsicivus, a, um</b> [subseco]\n<b>1)</b> отсечённый, отрезанный (<i>sc.</i> ager <i>Vr, Su</i>; <i>см.</i> subsicivum);\n<b>2)</b> остающийся свободным: subsiciva tempora <i>C</i> часы досуга;\n<b>3)</b> побочный (operae <i>C</i>); второстепенный, случайный (philosophia non est res subsiciva, ordinaria est <i>C</i>); дополнительный, остающийся (sollicitudo <i>Ap</i>)."],
  ["subsicuus", "<big>subsicuus</big>\n\n<b>subsicuus, a, um</b> [subsequor]\nпоследующий (metus <i>JV</i>)."],
  ["subsidentia", "<big>subsidentia</big>\n\n<b>subsidentia, ae</b> <i>f</i> [subsido]\nосадок, отстой (aquarum <i>Vtr</i>)."],
  ["subsidi", "<big>subsidi</big>\n\n<b>sub–sidi</b> — <i>см.</i>\nsubsido."],
  ["subsidialis", "<big>subsidialis</big>\n\n<b>subsidialis, e</b> <i>Amm</i>\n= subsidiarius."],
  ["subsidiarii", "<big>subsidiarii</big>\n\n<b>subsidiarii, orum</b> <i>m</i> [subsidiarius]\nвспомогательные войска, войсковой резерв <i>L</i>."],
  ["subsidiarius", "<big>subsidiarius</big>\n\n<b>subsidiarius, a, um</b> [subsidium]\nвспомогательный, резервный (cohortes <i>Cs, L</i>; naves <i>bAl</i>); сохраняемый в запасе (palmes <i>Col</i>): actio subsidiaria <i>Dig, CJ</i> субсидиарный иск (<i>опекаемого к государственному чиновнику в том случае, если назначенный последним опекун окажется неплатежеспособным</i>)."],
  ["subsidior", "<big>subsidior</big>\n\n<b>subsidior, —, ari</b> <i>depon.</i> [subsidium]\nнаходиться в резерве <i>Hirt</i>."],
  ["subsidium", "<big>subsidium</big>\n\n<b>subsidium, i</b> <i>n</i> [sub + sedeo]\n<b>1)</b> тыловой эшелон, резерв (<i>т. е.</i> triarii, <i>занимавшие место за вторым эшелоном</i> — principes): legiones in subsidiis locare <i>L</i> расположить легионы в резерве;\n<b>2)</b> вспомогательный отряд, подкрепление (funditores Baleares subsidio — <i>или</i> in s. <i>T</i> — mittere <i>Cs</i>);\n<b>3)</b> помощь, поддержка, защита (subsidio venire <i>C</i>): subsidio esse alicui alicui rei <i>C etc.</i> оказывать кому-л. помощь (служить защитой) против чего-л.; s. dominationi aliquem extulisse <i>T</i> выдвинуть кого-л. для укрепления своей власти;\n<b>4)</b> средство (помощи): subsidia ad omnes casus comparare <i>Cs</i> раздобыть средства на все возможные случаи; quod aliud s.? <i>T</i> что еще остаётся делать?;\n<b>5)</b> укрытие, убежище: modicis navigiis pauca subsidia <i>T</i> кое-какие укрытия для мелких судов."],
  ["subsido", "<big>subsido</big>\n\n<b>sub–sido, sedi</b> (<b>sidi</b> <i>Amm, v. l.</i>), <b>sessum, ere</b>\n<b>1)</b> садиться, приседать (adversus emissa tela <i>L</i>): poplite subsidens <i>V</i> припав на колено || опускаться (in genua <i>Sen</i>);\n<b>2)</b> засесть: s. in insidiis <i>L</i> устроить засаду || находиться в засаде, подстерегать (devictam Asiam <i>V</i>, <i>т. е.</i> victorem Asiae, <i>sc.</i> Agamemnonem; leonem <i>Sil</i>);\n<b>3)</b> (<i>о самках</i>) отдаваться (maribus <i>Lcr</i>);\n<b>4)</b> оседать, селиться (in Sicilia <i>C</i>);\n<b>5)</b> оставаться, останавливаться, задерживаться (in castris <i>Cs</i>; in via <i>C</i>);\n<b>6)</b> застревать, садиться на мель (in Nilo <i>L</i>): extremus galeaque ima subsedit Acestes <i>V</i> (когда тянули жребий), имя Ацеста застряло в самой глубине шлема (<i>т. е.</i> было вынуто последним);\n<b>7)</b> оседать, опускаться (pessum subsederunt urbes <i>Lcr</i>): limus mundi subsedit ut faex <i>Lcr</i> мировой ил осел словно гуща;\n<b>8)</b> отстаиваться (aqua Nili liquescit ac subsidit <i>bAl</i>);\n<b>9)</b> опадать, схлынуть (undae subsidunt <i>V</i>); убывать, ослабевать, прекращаться (vitia subsidunt <i>Sen</i>): venti subsidunt <i>Prp</i> ветры утихают;\n<b>10)</b> уступать давлению, поддаваться: ebur subsidit digitis <i>O</i> слоновая кость поддаётся пальцам (Пигмалиона), <i>т. е.</i> становится податливой, размягчается."],
  ["subsignanus", "<big>subsignanus</big>\n\n<b>subsignanus, a, um</b> [sub + signum]\nнаходящийся под знамёнами: milites subsignani <i>T</i> субсигнаны, солдаты легионов."],
  ["subsignatio", "<big>subsignatio</big>\n\n<b>subsignatio, onis</b> <i>f</i> [subsigno]\n<b>1)</b> подписание, подпись <i>Dig</i>;\n<b>2)</b> обязательство <i>CTh, Tert</i>."],
  ["subsigno", "<big>subsigno</big>\n\n<b>sub–signo, avi, atum, are</b>\n<b>1)</b> приписывать (внизу), <i>т. е.</i> приводить, цитировать (Ciceronis sententiam <i>PM</i>);\n<b>2)</b> записывать, выписывать, вносить (praedia apud aerarium <i>C</i>);\n<b>3)</b> (подписью) гарантировать, заверять (aliquid apud aliquem <i>PJ</i>): praedia subsignata <i>C</i> заложенные имения; s. fidem <i>PJ</i> давать слово, ручаться."],
  ["subsilio", "<big>subsilio</big>\n\n<b>sub–silio, silui (silii), —, ire</b> [salio II]\n<b>1)</b> подпрыгивать, подскакивать, взвиваться (adusque caelum <i>Ap</i>; ad tecta domorum <i>Lcr</i>);\n<b>2)</b> спрыгивать вниз <i>Sen</i>."],
  ["subsimilis", "<big>subsimilis</big>\n\n<b>sub–similis, e</b>\nнесколько похожий (alicui rei <i>CC</i>)."],
  ["subsimus", "<big>subsimus</big>\n\n<b>sub–simus, a, um</b>\nс немного вздёрнутым носом (boves <i>Vr</i>)."],
  ["subsipio", "<big>subsipio</big>\n\n<b>sub–sipio, —, —, ere</b> [sapio]\n(= non plane sapere) иметь какой-л. слабый привкус <i>Vr</i>."],
  ["subsistentia", "<big>subsistentia</big>\n\n<b>subsistentia, ae</b> <i>f</i> [subsisto]\nреальность, сущность <i>Eccl</i>."],
  ["subsisto", "<big>subsisto</big>\n\n<b>sub–sisto, stiti, —, ere</b>\n<b>1)</b> останавливаться, делать остановку (in itinere <i>Cs</i>; ad insulam Tauridem <i>bAl</i>; in dicendo <i>Q</i>);\n<b>2)</b> оставаться, задерживаться (domi <i>VP</i>); находиться, пребывать (intra tecta <i>PJ</i>; paucos dies apud aliquem <i>QC</i>): inter priorem paupertatem s. <i>T</i> оставаться по-прежнему бедным;\n<b>3)</b> останавливаться, переставать, прекращаться (clamor substitit <i>O</i>);\n<b>4)</b> оказывать сопротивление, сопротивляться, противостоять (Hannibali atque ejus armis <i>L</i>; s. aliquem <i>L</i>);\n<b>5)</b> совладать, справляться, выдерживать (ancorae funesque non substiterunt <i>Cs</i>): s. tantis periclis <i>Cld</i> выдержать такие опасности; s. sumptui <i>Brutus ap. C</i> оказаться в состоянии нести (подобные) расходы;\n<b>6)</b> оказывать поддержку, помогать (aerumnis alicujus <i>Ap</i>)."],
  ["subsitus", "<big>subsitus</big>\n\n<b>sub–situs, a, um</b>\nнаходящийся внизу (convallis <i>Ap</i>)."],
  ["subsolanus", "<big>subsolanus</big>\n\n<b>I sub–solanus, a, um</b>\nобращенный к солнцу, <i>т. е.</i> восточный (Indorum montes <i>PM</i>).\n\n<b>II subsolanus, i</b> <i>m</i> (<i>sc.</i> ventus)\nвосточный ветер (s. vel auster <i>CC</i>)."],
  ["subsortior", "<big>subsortior</big>\n\n<b>sub–sortior, itus sum, iri</b> <i>depon.</i>\nвновь <i>или</i> дополнительно избирать по жребию (judicem <i>C</i>): s. in alicujus locum <i>C</i> избирать по жребию взамен кого-л."],
  ["subsortitio", "<big>subsortitio</big>\n\n<b>subsortitio, onis</b> <i>f</i> [subsortior]\nперевыборы <i>или</i> доизбрание по жребию (judicum <i>C</i>)."],
  ["subspargo", "<big>subspargo</big>\n\n<b>sub–spargo, —, —, ere</b>\nтайком сеять, подсыпать (semina versutiis haereticorum <i>Tert</i>)."],
  ["substantia", "<big>substantia</big>\n\n<b>substantia, ae</b> <i>f</i> [substo]\n<b>1)</b> сущность, существо, суть (rerum pretium non in substantia, sed in arte positum <i>Dig</i>): non tam ex substantia, quam ex quantite <i>Dig</i> не столько по существу, сколько количественно (по размерам);\n<b>2)</b> (<i>тж.</i> s. bonorum <i>Dig</i>) достояние, имущество: s. facultatum <i>T</i> средства (к существованию); salva rerum substantia <i>Dig</i> без нанесения материального ущерба;\n<b>3)</b> средства пропитания (omni substantia carens <i>Eccl</i>)."],
  ["substantialis", "<big>substantialis</big>\n\n<b>substantialis, e</b> [substantia]\n<b>1)</b> существенный (differentia <i>Tert</i>);\n<b>2)</b> обладающий самостоятельным существованием, субстанциальный (potestates <i>Amm</i>)."],
  ["substantialitas", "<big>substantialitas</big>\n\n<b>substantialitas, atis</b> <i>f</i>\nсубстанциальность, существенность <i>Eccl</i>."],
  ["substantialiter", "<big>substantialiter</big>\n\n<b>substantialiter</b>\nпо существу <i>Tert</i>."],
  ["substantiola", "<big>substantiola</big>\n\n<b>substantiola, ae</b> <i>f</i> [<i>demin. к</i> substantia 2]\nнебольшое имущество, состояньице <i>Hier</i>."],
  ["substantivalis", "<big>substantivalis</big>\n\n<b>substantivalis, e</b> <i>Tert</i>\n= substantialis."],
  ["substantivum", "<big>substantivum</big>\n\n<b>substantivum, i</b> <i>n</i> (<i>sc.</i> nomen) <i>грам.</i>\nимя существительное."],
  ["substantivus", "<big>substantivus</big>\n\n<b>substantivus, a, um</b> [substantia]\n<b>1)</b> <i>Tert</i> = substantialis;\n<b>2)</b> существительный: verbum substantivum <i>грам.</i> глагол sum."],
  ["substerno", "<big>substerno</big>\n\n<b>sub–sterno, stravi, stratum, ere</b>\n<b>1)</b> подстилать (segetem ovibus <i>Cato</i>);\n<b>2)</b> подкладывать, класть (подо что-либо): substratus Numidae mortuo Romanus <i>L</i> римлянин, лежавший под трупом нумидийца (на поле сражения); se s. alicui <i>Ctl</i> = indulgere veneri; s. brachia collo <i>Ctl</i> обвивать руками шею;\n<b>3)</b> расстилать, распростирать (pelage late substrata <i>Lcr</i>);\n<b>4)</b> выстилать (nidos mollissime <i>C</i>); устилать, покрывать (lectum stragula veste <i>Ap</i>);\n<b>5)</b> подчинять, отдавать в жертву (aliquid libidini suae <i>C</i>; pudicitiam alicui <i>Su</i>);\n<b>6)</b> предлагать, предоставлять (delicias multas <i>Lcr</i>)."],
  ["substillum", "<big>substillum</big>\n\n<b>substillum, i</b> <i>n</i>\nизморось <i>Tert</i>."],
  ["substillus", "<big>substillus</big>\n\n<b>substillus, a, um</b> [sub + stilio]\nвыделяющийся по каплям, капающий (lotium <i>Cato</i>)."],
  ["substineo", "<big>substineo</big>\n\n<b>substineo</b> <i>v. l.</i>\n= sustineo."],
  ["substiti", "<big>substiti</big>\n\n<b>substiti</b> <i>pf. к</i>\nsubsisto."],
  ["substituo", "<big>substituo</big>\n\n<b>substituo, stitui, stitutum, ere</b> [sub + statuo]\n<b>1)</b> ставить позади (post elephantus armaturas leves <i>bAfr</i>);\n<b>2)</b> ставить вместо, подставлять, назначать взамен (pontificem in locum Scipionis <i>Su</i>): s. animo aliquid <i>L</i> представлять себе (воображать) что-л.; s. aliquem alicui <i>L</i> (<i>или</i> pro aliquo <i>C, L</i>) заменять (замещать) кого-л. кем-л.; s. heredem alicui <i>Su, Dig</i> назначить наследника-заместителя (<i>на случай смерти основного наследника</i>); crimini substitui <i>PJ</i> быть обвинённым; aliquem alicujus criminis reum s. <i>QC</i> свалить на кого-л. вину за что-л."],
  ["substitutio", "<big>substitutio</big>\n\n<b>substitutio, onis</b> <i>f</i>\nподстановка, замена, замещение <i>Eccl</i>: ex substitutione heredem esse <i>Dig</i> быть заместителем наследника (в случае его смерти)."],
  ["substitutivus", "<big>substitutivus</big>\n\n<b>substitutivus, a, um</b> [substituo]\nподчинённый, условный (propositio <i>Ap</i>)."],
  ["substitutus", "<big>substitutus</big>\n\n<b>substitutus, a, um</b> <i>part. pf. к</i>\nsubstituo."],
  ["substo", "<big>substo</big>\n\n<b>sub–sto, —, —, are</b>\n<b>1)</b> быть в наличии, существовать: nullo dolore substante <i>CC</i> когда никакой боли нет;\n<b>2)</b> держаться крепко, выдерживать: metuo, ut substet <i>Ter</i> боюсь, выдержит ли он."],
  ["substomachor", "<big>substomachor</big>\n\n<b>sub–stomachor, —, ari</b>\nбыть в неважном настроении (s. taedio <i>Aug</i>)."],
  ["substramen", "<big>substramen</big>\n\n<b>substramen, inis</b> <i>n</i> [substerno]\n<b>1)</b> подстилка (molle <i>Vr</i>);\n<b>2)</b> валик, каток (substramina aderant plaustris <i>Sil</i>)."],
  ["substramentum", "<big>substramentum</big>\n\n<b>substramentum, i</b> <i>n</i> <i>Cato</i>\n= substramen 1."],
  ["substratus", "<big>substratus</big>\n\n<b>I substratus, a, um</b> <i>part. pf. к</i>\nsubsterno.\n\n<b>II substratus, (us)</b> <i>m</i> (<i>тк. в</i> <i>abl.</i>)\nподстилка <i>PM</i>."],
  ["substrepens", "<big>substrepens</big>\n\n<b>substrepens, entis</b> <i>adj.</i> [sub + strepo]\nбормочущий (tertiata verba <i>Ap</i>)."],
  ["substrictus", "<big>substrictus</big>\n\n<b>substrictus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> substringo;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> худой, тощий (ilia, crura <i>O</i>);\n<b>2)</b> собранный, обтянутый (tunica <i>AG</i>)."],
  ["substrido", "<big>substrido</big>\n\n<b>sub–strido, —, —, ere</b>\nнемного шипеть (ira exundante <i>Amm</i>)."],
  ["substringo", "<big>substringo</big>\n\n<b>sub–stringo, strinxi, strictum, ere</b>\n<b>1)</b> подтягивать, стягивать (caput equi loro <i>Nep</i>);\n<b>2)</b> подвязывать, подбирать (comas auro <i>Lcn</i>); завязывать (crimen nodo <i>T</i>);\n<b>3)</b> убирать (carbasa <i>M</i>): s. effusa <i>Q</i> сокращать длинноты;\n<b>4)</b> сдерживать (bilem <i>J</i>);\n<b>5)</b> напрягать: s. aurem <i>H</i> насторожить уши. — <i>См. тж.</i> substrictus."],
  ["substructio", "<big>substructio</big>\n\n<b>substructio, onis</b> <i>f</i> [substruo]\nподведение фундамента <i>Vtr</i>; фундамент, основание <i>C, Cs, Col, L, Vtr</i>."],
  ["substructum", "<big>substructum</big>\n\n<b>substructum, i</b> <i>n</i> <i>Vtr</i>\n= substructio."],
  ["substructus", "<big>substructus</big>\n\n<b>substructus, a, um</b> <i>part. pf. к</i>\nsubstruo."],
  ["substruo", "<big>substruo</big>\n\n<b>sub–struo, struxi, structum, ere</b>\n<b>1)</b> строить внизу, подводить (fundamentum <i>Pl</i>); снабжать фундаментом: s. Capitolium saxo quadrato <i>L</i> подводить под Капитолий фундамент из тёсаного камня; intervalla s. ad libramenta <i>Vtr</i> застроить промежутки (между горами) для нивелировки (пути);\n<b>2)</b> настилать, шоссировать, мостить (vias glarea <i>L</i>)."],
  ["subsultim", "<big>subsultim</big>\n\n<b>subsultim</b> [subsilio]\nпрыжками, вприпрыжку (decurrere <i>Su</i>)."],
  ["subsulto", "<big>subsulto</big>\n\n<b>subsulto, —, —, are</b> [<i>intens. к</i> subsilio]\nподпрыгивать, подскакивать <i>Pl, Q</i>."],
  ["subsum", "<big>subsum</big>\n\n<b>sub–sum, fui, esse</b>\n<b>1)</b> находиться под, внизу, за <i>или</i> внутри: s. intra cutem <i>Plancus ap. C</i> быть под кожей; lingua subest palato <i>V</i> язык находится под нёбом; sol Oceano subest <i>H</i> солнце находится за океаном (<i>т. е.</i> зашло);\n<b>2)</b> находиться вблизи, быть рядом (templa mari subsunt <i>O</i>); быть близким: nox jam suberat <i>Cs</i> уже приближалась ночь;\n<b>3)</b> лежать в основе, содержаться, заключаться: alicui rei ratio non subest <i>C</i> что-л. лишено основания; nihil potest esse diuturnum, cui non subest ratio <i>QC</i> не может долго просуществовать то, что не имеет разумного основания; oportet causas subesse easdem <i>C</i> в основе должны лежать одни и те же причины;\n<b>4)</b> скрываться, таиться (silentio facinus subest <i>QC</i>; auro multa mala subsunt <i>Tib</i>): in ea re nulla subest suspicio <i>C</i> в этом нет ничего подозрительного;\n<b>5)</b> быть подчинённым, входить в состав (pars subest generi <i>C</i>): s. notitiae alicujus <i>O</i> быть известным кому-л., находиться в чьём-л. поле зрения;\n<b>6)</b> быть налицо, иметься, существовать (si ulla spes subest <i>C</i>; suberunt vestigia priscae fraudis <i>V</i>): si subest occasio <i>Aug</i> если представляется случай."],
  ["subsurdus", "<big>subsurdus</big>\n\n<b>sub–surdus, a, um</b>\nглуховатый (vox <i>Q</i>)."],
  ["subsutus", "<big>subsutus</big>\n\n<b>sub–sutus, a, um</b>\nобшитый, отороченный (vestis <i>H</i>)."],
  ["subtabidus", "<big>subtabidus</big>\n\n<b>sub–tabidus, a, um</b>\nнемного ослабевший <i>Amm</i>."],
  ["subtalaris", "<big>subtalaris</big>\n\n<b>sub–talaris, e</b>\nдоходящий до щиколотки (calcei <i>Is</i>)."],
  ["subtectio", "<big>subtectio</big>\n\n<b>sub–tectio, onis</b> <i>f</i> [subtego]\nприкрытие <i>Aug</i>."],
  ["subtegmen", "<big>subtegmen</big>\n\n<b>sub–tegmen, inis</b> <i>n</i> <i>v. l.</i>\n= subtemen."],
  ["subtego", "<big>subtego</big>\n\n<b>sub–tego, texi, tectum, ere</b>\nприкрывать (foliis ficulneis subtectus <i>Aug</i>); окутывать (aliquem nubilo <i>Aug</i>)."],
  ["subtegulaneus", "<big>subtegulaneus</big>\n\n<b>subtegulaneus, a, um</b> [sub + tegula]\nнаходящийся под кровлей, крытый (pavimenta <i>PM</i>)."],
  ["subtemen", "<big>subtemen</big>\n\n<b>sub–temen, inis</b> <i>n</i> [*subtexmen <i>от</i> subtego] уток (s. inseritur radiis <i>O</i>); пряжа (s. nere <i>Pl, Ter</i>); нить (Parcarum <i>H</i>)."],
  ["subtendo", "<big>subtendo</big>\n\n<b>sub–tendo, tendi, tentum (tensum), ere</b>\nстягивать снизу (lecti loris subtenti <i>Cato</i>)."],
  ["subtenuis", "<big>subtenuis</big>\n\n<b>sub–tenuis, e</b>\nдостаточно тонкий (saetae <i>Vr</i>)."],
  ["subter", "<big>subter</big>\n\n<b>I subter</b> <i>adv.</i>\nвнизу, снизу (omnia, quae supra sunt et s. <i>C</i>).\n\n<b>II subter</b> <i>praep. cum acc. и abl.</i>\nвниз, под (aliquid s. lectum mittere <i>Pt</i>): s. fastigia tecti aliquem ducere <i>V</i> вести кого-л. под крышу (своего дома); s. murum hostium advehi <i>L</i> подъехать под (самые) стены неприятеля; omnia s. se habere <i>C</i> иметь всё под собой, <i>т. е.</i> быть выше всего; s. densa testudine <i>V</i> под толстым панцирем «черепахи» (<i>т. е.</i> военного строя; <i>см.</i> testudo 4 <i>и</i> 5)."],
  ["subteractus", "<big>subteractus</big>\n\n<b>subter–actus, a, um</b> [ago]\nвыросший снизу (subteractae radices <i>CC</i>)."],
  ["subteradnexus", "<big>subteradnexus</big>\n\n<b>subter–adnexus (-annexus), a, um</b> (<i>тж. раздельно</i>)\nпривязанный снизу (carpento rotali <i>Capit</i>)."],
  ["subtercavatus", "<big>subtercavatus</big>\n\n<b>subter–cavatus, a, um</b>\nвырытый в земле, подземный (contegere subtercavata <i>Sol</i>)."],
  ["subtercurrens", "<big>subtercurrens</big>\n\n<b>subter–currens, entis</b>\nпробегающий внизу (Vergiliae <i>Vtr</i> — <i>v. l.</i>)."],
  ["subtercutaneus", "<big>subtercutaneus</big>\n\n<b>subter–cutaneus, a, um</b> [cutis]\nподкожный (umor <i>Veg</i>)."],
  ["subterduco", "<big>subterduco</big>\n\n<b>subter–duco, duxi, —, ere</b>\nуводить: se s. alicui <i>Pl</i> от кого-л. тайно убегать, ускользать; si huic occasioni tempus sese subterduxerit <i>Pl</i> если будет упущен этот случай."],
  ["subterfluo", "<big>subterfluo</big>\n\n<b>subter–fluo, —, —, ere</b>\nпротекать внизу (s. terras <i>Sen</i> — <i>v. l.</i>; s. sub montes <i>Vtr</i>)."],
  ["subterfugio", "<big>subterfugio</big>\n\n<b>subter–fugio, fugi, —, ere</b>\nтанком уходить, незаметно ускользать (alicui <i>Pl</i>); избегать, ускользать, увёртываться (periculum <i>C</i>; tempestatem belli <i>L</i>)."],
  ["subterfundo", "<big>subterfundo</big>\n\n<b>subter–fundo, —, —, are</b>\nукреплять снизу (quum terra subterfundaretur <i>Lact</i>)."],
  ["subterhabeo", "<big>subterhabeo</big>\n\n<b>subter–habeo, —, habitum, ere</b>\nставить на последнем плане, пренебрегать (meis amoribus subterhabitis <i>Ap</i>)."],
  ["subterius", "<big>subterius</big>\n\n<b>subterius</b> <i>adv.</i> [subter I]\n(по)ниже (s.-mediosuperius <i>Is</i>)."],
  ["subterjacio", "<big>subterjacio</big>\n\n<b>subter–jacio, —, —, ere</b>\nбросать внизу (hordei grana <i>Pall</i>)."],
  ["subterlabor", "<big>subterlabor</big>\n\n<b>subter–labor, —, labi</b> <i>depon.</i>\n<b>1)</b> протекать под, омывать внизу (flumina antiquos subterlabentia muros <i>V</i>);\n<b>2)</b> уходить (от преследования), ускользать (celeritate <i>L</i>)."],
  ["subterlino", "<big>subterlino</big>\n\n<b>subter–lino, —, —, ere</b>\nсмазывать <i>или</i> натирать снизу (plantas aegri <i>PM</i>)."],
  ["subterluo", "<big>subterluo</big>\n\n<b>subter–luo, —, —, ere</b>\nомывать снизу (stelliger Eridanus gurgite sidereo subterluit Oriona <i>Cld</i>)."],
  ["subtermeo", "<big>subtermeo</big>\n\n<b>subter–meo, —, —, are</b>\nвнизу проходить, протекать (s. junctos pontes <i>Cld</i>; sidera subtermeant — <i>sc.</i> terras — et rursus eadem resurgunt <i>PM</i>)."],
  ["subternato", "<big>subternato</big>\n\n<b>subter–nato, —, —, are</b>\nпроплывать снизу <i>Sol</i>."],
  ["subternus", "<big>subternus</big>\n\n<b>subternus, a, um</b> [subter I]\nнаходящийся в преисподней, адский (antra <i>Eccl</i>)."],
  ["subtero", "<big>subtero</big>\n\n<b>sub–tero, trivi, tritum, ere</b>\n<b>1)</b> стирать снизу: subtritae ungulae <i>Pl</i> стёршиеся копыта;\n<b>2)</b> растирать, толочь (sextarium salis <i>Col</i>)."],
  ["subterpendens", "<big>subterpendens</big>\n\n<b>subter–pendens, entis</b>\nсвисающий вниз (mala <i>Pall</i>)."],
  ["subterraneum", "<big>subterraneum</big>\n\n<b>subterraneum, i</b> <i>n</i> [subterraneus]\nподземелье <i>Ap, PJ</i>."],
  ["subterraneus", "<big>subterraneus</big>\n\n<b>subterraneus, a, um</b> [sub + terra]\nподземный (specus <i>C</i>): subterranea regna <i>J</i> подземное царство."],
  ["subterrenus", "<big>subterrenus</big>\n\n<b>subterrenus, a, um</b> <i>Ap</i>\n= subterraneus."],
  ["subterreus", "<big>subterreus</big>\n\n<b>subterreus, a, um</b> <i>Eccl</i>\n= subterraneus."],
  ["subterseco", "<big>subterseco</big>\n\n<b>subter–seco, —, —, are</b> (<i>тж. раздельно</i>)\nрассекать снизу (medium Capricornum <i>C</i>)."],
  ["subtervacans", "<big>subtervacans</big>\n\n<b>subter–vacans, antis</b> <i>adj.</i>\nпустой внизу (locus <i>Sen</i>)."],
  ["subtervolo", "<big>subtervolo</big>\n\n<b>subter–volo, —, —, are</b>\nлетать под (чём-л.): s. astra <i>St</i> взлетать до звёзд."],
  ["subtervolvo", "<big>subtervolvo</big>\n\n<b>subter–volvo, —, —, ere</b>\nкатить внизу (amnium cursus <i>Amm</i>)."],
  ["subtexo", "<big>subtexo</big>\n\n<b>sub–texo, texui, textum, ere</b>\n<b>1)</b> притыкать, <i>перен.</i> пришивать снизу, пристёгивать: s. lunam alutae <i>J</i> прикрепить луночку к сапогу (сенаторскому);\n<b>2)</b> (снизу) окутывать (caelum fumo <i>V</i>): s. nubes alicui rei <i>O</i> заволакивать (снизу) тучами что-л.; sol diem subtexit Olympo <i>VF</i> солнце залило дневным светом Олимп;\n<b>3)</b> присоединять, добавлять (fabulae aliquid <i>L</i>): subtexta malis bona sunt <i>Man</i> добро и зло смешаны друг с другом;\n<b>4)</b> слагать, составлять (carmina <i>Tib</i>)."],
  ["subtextus", "<big>subtextus</big>\n\n<b>subtextus, a, um</b> <i>part. pf. к</i>\nsubtexo."],
  ["subtililoquentia", "<big>subtililoquentia</big>\n\n<b>subtili–loquentia, ae</b> <i>f</i>\nтонкие речи <i>Tert</i>."],
  ["subtililoquus", "<big>subtililoquus</big>\n\n<b>subtili–loquus, a, um</b>\nтонко рассуждающий <i>Tert</i>."],
  ["subtilis", "<big>subtilis</big>\n\n<b>subtilis, e</b> [<i>из</i> *subtexilis «тонко сотканный»]\n<b>1)</b> тонкий (filum <i>Lcr</i>);\n<b>2)</b> мелкий (farina <i>PM</i>);\n<b>3)</b> острый, отточенный (acies gladii <i>Sen</i>);\n<b>4)</b> тонкий, утончённый, изнеженный (palatum <i>H</i>);\n<b>5)</b> тонкий, изощрённый, остроумный (scriptor, epistula <i>C</i>);\n<b>6)</b> тщательный, точный (definitio <i>C</i>);\n<b>7)</b> изысканный, изящный (sententia <i>PM</i>);\n<b>8)</b> простой, ясный, точный (oratio <i>C</i>)."],
  ["subtilitas", "<big>subtilitas</big>\n\n<b>subtilitas, atis</b> <i>f</i> [subtilis]\n<b>1)</b> тонкость (linearum <i>PM</i>);\n<b>2)</b> нежность (florum <i>PM</i>);\n<b>3)</b> острота (ferramentorum <i>PM</i>);\n<b>4)</b> тонкость, изящество (sententiarum <i>C</i>; columnae <i>Vtr</i>);\n<b>5)</b> изощрённость, остроумие (sermonis, disputandi <i>C</i>);\n<b>6)</b> простота, ясность, точность (scriptoris, orationis <i>C</i>);\n<b>7)</b> тонкий приём, остроумный способ (impetum morbi subtilitate lenire <i>Pt</i>);\n<b>8)</b> ловкость, искусство (cum ingenti subtilitate aliquid facere <i>Pt</i>)."],
  ["subtiliter", "<big>subtiliter</big>\n\n<b>subtiliter</b> [subtilis]\n<b>1)</b> тонко (dividere aliquid <i>PM</i>); теснейшим образом (connexae res <i>Lcr</i>);\n<b>2)</b> тонко, остроумно (judicare <i>C</i>; s. magis, quam dilucide, dici <i>C</i>);\n<b>3)</b> точно, ясно, просто.(privatas causas agere <i>C</i>);\n<b>4)</b> обстоятельно (scribere <i>C</i>)."],
  ["subtimeo", "<big>subtimeo</big>\n\n<b>sub–timeo, —, —, ere</b>\nнемного бояться, побаиваться, втайне опасаться <i>C</i>."],
  ["subtinnio", "<big>subtinnio</big>\n\n<b>sub–tinnio, —, —, ire</b>\nтихо бренчать <i>Tert</i>."],
  ["subtractio", "<big>subtractio</big>\n\n<b>subtractio, onis</b> <i>f</i> [subtraho]\nуход, отступление (in perditionem <i>Vlg</i>)."],
  ["subtractus", "<big>subtractus</big>\n\n<b>subtractus, a, um</b> <i>part. pf. к</i>\nsubtraho."],
  ["subtraho", "<big>subtraho</big>\n\n<b>sub–traho, traxi, tractum, ere</b>\n<b>1)</b> вытаскивать, извлекать снизу (aliquid alicui rei): tellus pedibus subtracta <i>Lcr</i> ускользнувшая из-под ног земля (во <i>время землетрясения</i>);\n<b>2)</b> (тайно) уносить (impedimenta clandestina fuga <i>Hirt</i>); удалять: aggerem cuniculis s. <i>Cs</i> сносить насыпи посредством подкопов (под них);\n<b>3)</b> уводить (hastatos ex acie <i>L</i>);\n<b>4)</b> отводить прочь (oculos <i>T</i>);\n<b>5)</b> отнимать (cibum alicui <i>C</i>);\n<b>6)</b> вырывать, освобождать, спасать (aliquem irae alicujus <i>T</i>);\n<b>7)</b> (<i>чаще</i> se s. <i>и</i> subtrahi) уклоняться, освобождаться, избегать, ускользать (se labori bellique viaeque <i>O</i>): se s. ab omni parte rei publicae <i>C</i> отказаться от всякого участия в государственных делах;\n<b>8)</b> обходить молчанием, не упоминать, опускать (nomen alicujus <i>T</i>; aliquem <i>QC</i>)."],
  ["subtristis", "<big>subtristis</big>\n\n<b>sub–tristis, e</b>\nнемного грустный <i>Ter, Amm, Eccl</i>."],
  ["subtritus", "<big>subtritus</big>\n\n<b>subtritus, a, um</b> <i>part. pf. к</i>\nsubtero."],
  ["subtunicalis", "<big>subtunicalis</big>\n\n<b>sub–tunicalis, is</b> <i>f</i> <i>Hier</i>\n= subucula."],
  ["subturpiculus", "<big>subturpiculus</big>\n\n<b>sub–turpiculus, a, um</b> [<i>demin. к</i> subturpis]\nнемножко постыдный (palinodia <i>C</i>)."],
  ["subturpis", "<big>subturpis</big>\n\n<b>sub–turpis, e</b>\nдовольно безобразный, до известной степени позорный <i>C</i>."],
  ["subtus", "<big>subtus</big>\n\n<b>subtus</b> <i>adv.</i>\nвнизу <i>Cato, Vr, L</i>."],
  ["subtusus", "<big>subtusus</big>\n\n<b>subtusus, a, um</b> [sub + tundo]\nнемного избитый, покрытый небольшими синяками (s. genas — <i>acc. gr. Tib</i>)."],
  ["subucula", "<big>subucula</big>\n\n<b>subucula, ae</b> <i>f</i> [<i>одного корня с</i> exuo]\nнижняя рубашка, сорочка (subucula subest tunicae <i>H</i>)."],
  ["subula", "<big>subula</big>\n\n<b>subula, ae</b> <i>f</i>\nшило <i>Col, M, Pall</i>: subula leonem excipere <i>погов. Sen</i> идти с шилом на льва, <i>т. е.</i> покушаться с негодными средствами."],
  ["subulcus", "<big>subulcus</big>\n\n<b>subulcus, i</b> <i>m</i> [sus]\nсвинопас <i>Cato, Vr, Col, M</i>."],
  ["subulo", "<big>subulo</big>\n\n<b>subulo, onis</b> <i>m</i>\n<b>1)</b> <i>Enn</i> = tibicen;\n<b>2)</b> молодой олень с остроконечными рогами <i>PM</i>;\n<b>3)</b> <i>LM ap. Aus</i> = paedico II."],
  ["subunctio", "<big>subunctio</big>\n\n<b>sub–unctio, onis</b> <i>f</i>\nлёгкое втирание <i>CA</i>."],
  ["Subura", "<big>Subura</big>\n\n<b>Subura, ae</b> <i>f</i>\nСубура, <i>район Рима в низине между Эсквилином, Квириналом и Виминалом с улицей того же названия, весьма людный и оживлённый, с большим количеством притонов</i> <i>Vr, L, Pers, J, M etc.</i>"],
  ["Suburanus", "<big>Suburanus</big>\n\n<b>Suburanus, a, um</b> [Subura]\nсубурский: clivus s. <i>M</i> = collis Esquilinus; magistra Suburana <i>M</i> публичная женщина из Субуры."],
  ["suburbanitas", "<big>suburbanitas</big>\n\n<b>sub–urbanitas, atis</b> <i>f</i>\nблизость к городу (Риму) (provinciae <i>C</i>)."],
  ["suburbanum", "<big>suburbanum</big>\n\n<b>suburbanum, i</b> <i>n</i> [suburbanus I]\nзагородное поместье, дача (<i>преим.</i> под Римом) <i>C, Pt, Amm</i>."],
  ["suburbanus", "<big>suburbanus</big>\n\n<b>I sub–urbanus, a, um</b>\nпригородный, загородный, находящийся близ города (Рима) (villa <i>Dig</i>; rus <i>C</i>); взращённый в окрестностях Рима (caulis <i>H</i>); peregrinatio suburbana <i>T</i> поездка к городским воротам.\n\n<b>II suburbanus, i</b> <i>m</i>\nжитель римских предместий <i>O</i>."],
  ["suburbicarius", "<big>suburbicarius</big>\n\n<b>suburbicarius, a, um</b> <i>CTh</i>\n= suburbanus I."],
  ["suburbium", "<big>suburbium</big>\n\n<b>suburbium, i</b> <i>n</i> [sub + urbs]\nпригород, предместье <i>C</i>."],
  ["suburgeo", "<big>suburgeo</big>\n\n<b>sub–urgeo, —, —, ere</b>\nпригонять, пододвигать (proram ad saxa <i>V</i>)."],
  ["suburo", "<big>suburo</big>\n\n<b>sub–uro, —, ustum, ere</b>\nслегка обжигать, немного опалять (crura aliqua re <i>Su</i>)."],
  ["Suburr-", "<big>Suburr-</big>\n\n<b>Suburr-</b> <i>v. l.</i>\n= Subur-."],
  ["subus", "<big>subus</big>\n\n<b>subus</b> <i>dat. abl. pl. к</i>\nsus."],
  ["subustio", "<big>subustio</big>\n\n<b>sub–ustio, onis</b> <i>f</i>\nотапливание снизу (thermarum <i>CJ</i>)."],
  ["subvas", "<big>subvas</big>\n\n<b>sub–vas, vadis</b> <i>m</i> <i>арх.</i>\nвторой поручитель, <i>т. е.</i> ручающийся за поручителя (vades et subvades <i>LXIIT ap. AG</i>)."],
  ["subvectio", "<big>subvectio</big>\n\n<b>subvectio, onis</b> <i>f</i> [subveho]\nподвоз, доставка (frumenti <i>L</i>): ab re frumentaria duris subvectionibus laborare <i>Cs</i> испытывать продовольственные затруднения в связи с плохой доставкой."],
  ["subvecto", "<big>subvecto</big>\n\n<b>sub–vecto, avi, —, are</b> [<i>intens. к</i> subveho]\nперевозить (aliquid cymba <i>V</i>); доставлять, переносить (saxa umeris <i>V</i>)."],
  ["subvectus", "<big>subvectus</big>\n\n<b>I subvectus, a, um</b> <i>part. pf. к</i>\nsubveho.\n\n<b>II subvectus, us</b> <i>m</i> <i>T</i>\n= subvectio."],
  ["subveho", "<big>subveho</big>\n\n<b>sub–veho, vexi, vectum, ere</b>\n(под)возить (вверх), доставлять (frumentum navibus <i>Cs</i>; commeatus ex Samnio subyehebantur <i>L</i>); привозить (cineres alicujus Romam Tiberi <i>Su</i>)."],
  ["subvello", "<big>subvello</big>\n\n<b>sub–vello, —, vulsum, ere</b>\nвыщипывать волосы, общипывать (femina <i>Scipio ap. AG</i>)."],
  ["subvelo", "<big>subvelo</big>\n\n<b>sub–velo, —, —, are</b>\nслегка заволакивать (sol subvelabatur nubilo <i>Aug</i>)."],
  ["subvenibo", "<big>subvenibo</big>\n\n<b>subvenibo</b> <i>арх. Pl fut. к</i>\nsubvenio."],
  ["subvenio", "<big>subvenio</big>\n\n<b>sub–venio, veni, ventum, ire</b>\n<b>1)</b> приходить на помощь, оказывать помощь (homini perdito <i>C</i>; civitati <i>Cs</i>); помогать: priusquam ex castris subveniretur <i>Sl</i> прежде чем из лагеря подоспела подмога; s. vitae alicujus <i>Cs</i> спасать чью-л. жизнь; s. saluti alicujus <i>C</i> спасать кого-либо; s. necessitati <i>C</i> помочь в нужде;\n<b>2)</b> происходить, появляться, прибывать (nocte <i>PM</i>);\n<b>3)</b> попадаться, встречаться (aliqua vox subvenit <i>AG</i>)."],
  ["subventio", "<big>subventio</big>\n\n<b>subventio, onis</b> <i>f</i> [subvenio]\nподмога, помощь <i>Eccl</i>."],
  ["subvento", "<big>subvento</big>\n\n<b>subvento, —, —, are</b> [<i>intens. к</i> subvenio]\nприходить на помощь, помогать (alicui <i>Pl</i>)."],
  ["subverbustus", "<big>subverbustus</big>\n\n<b>sub–verbustus, a, um</b> [<i>предпол. из</i> sub verubus ustus, <i>по др.</i> sub + verbera]\nклеймёный <i>или</i> избитый <i>Pl, Tert</i>."],
  ["subvereor", "<big>subvereor</big>\n\n<b>sub–vereor, —, </b> <i>depon.</i>\nнемного бояться <i>C</i>."],
  ["subversio", "<big>subversio</big>\n\n<b>subversio, onis</b> <i>f</i> [subverto]\nразрушение (urbium <i>Vlg</i>); истребление (generis humani <i>Eccl</i>)."],
  ["subverso", "<big>subverso</big>\n\n<b>subverso, —, —, are</b> [<i>intens. к</i> subverto]\nопрокидывать, разорять <i>Pl</i>."],
  ["subversor", "<big>subversor</big>\n\n<b>subversor, oris</b> <i>m</i> [subverto]\nразрушитель, нарушитель (legum <i>T</i>)."],
  ["subversus", "<big>subversus</big>\n\n<b>subversus, a, um</b> <i>part. pf. к</i>\nsubverto."],
  ["subverto", "<big>subverto</big>\n\n<b>sub–verto, verti, versum, ere</b>\n<b>1)</b> опрокидывать, низвергать, (по)валить (statuas <i>Su</i>); переворачивать (mensam <i>Su</i>); сбивать с ног: calceus, si pede major erit, subvertet <i>H</i> сапог, если он будет больше ступни, собьёт с ног;\n<b>2)</b> опрокидывать, разрушать (pristinam majestatem soliorum <i>Lcr</i>); ниспровергать, уничтожать (fidem, probitatem ceterasque artes bonas <i>Sl</i>); разорять (aliquem <i>Ter</i>); опровергать (interpretationem adversarii <i>Q</i>)."],
  ["subvespertinus", "<big>subvespertinus</big>\n\n<b>sub–vespertinus, i</b> <i>m</i> (<i>sc.</i> ventus)\nзападный <i>или</i> юго-западный ветер, <i>т. е.</i> favonius <i>или</i> zephyrus <i>Veg</i>."],
  ["subvesperus", "<big>subvesperus</big>\n\n<b>subvesperus, i</b> <i>m</i> <i>Vtr</i>\n= subvespertinus."],
  ["subvexi", "<big>subvexi</big>\n\n<b>subvexi</b> <i>pf. к</i>\nsubveho."],
  ["subvexus", "<big>subvexus</big>\n\n<b>subvexus, a, um</b> [<i>одного корня с</i> convexus]\nподнимающийся вверх: omnia fastigio leni subvexa <i>L</i> всюду подъём был отлогий."],
  ["subviridis", "<big>subviridis</big>\n\n<b>sub–viridis, e</b>\nзеленоватый (folium <i>PM</i>)."],
  ["subvolo", "<big>subvolo</big>\n\n<b>sub–volo, —, —, are</b>\nвзлетать, возноситься (in caelestem locum <i>C</i>)."],
  ["subvols-", "<big>subvols-</big>\n\n<b>subvols-</b> <i>арх.</i>\n= subvuls-."],
  ["subvolt-", "<big>subvolt-</big>\n\n<b>subvolt-</b> <i>арх.</i>\n— subvult-."],
  ["subvolvo", "<big>subvolvo</big>\n\n<b>sub–volvo, —, —, ere</b>\nвкатывать (saxa manibus <i>V</i>)."],
  ["subvorto", "<big>subvorto</big>\n\n<b>subvorto, vorti, vorsum, ere</b> <i>арх.</i>\n= subverto."],
  ["subvulsus", "<big>subvulsus</big>\n\n<b>sub–vulsus (-volsus), a, um</b> <i>part. pf. к</i>\nsubvello."],
  ["subvulturius", "<big>subvulturius</big>\n\n<b>sub–vulturius (-volturius), a, um</b>\nпочти такого цвета как коршун, сероватый (corpus <i>Pl</i>)."],
  ["suc-", "<big>suc-</big>\n\n<b>suc-</b>\n<i>приставка</i>= sub- <i>перед начальным c основного слова.</i>"],
  ["succa-", "<big>succa-</big>\n\n<b>suc–ca-</b>\n= subca-."],
  ["succano", "<big>succano</big>\n\n<b>succano</b> <i>Vr</i>\n= succino."],
  ["succedaneus", "<big>succedaneus</big>\n\n<b>I succedaneus, a, um</b> [succedo]\nследующий (в качестве преемника), замещающий, служащий заместителем: Pharnacem succedaneum Eumeni dare <i>Just</i> сделать Фарнака заместителем (преемником) Эвмена; tergum suum stultitiae alicujus succedaneum subdere <i>Pl</i> подставлять свою спину (для расплаты) за чью-л. глупость.\n\n<b>II succedaneus, i</b> <i>m</i>\nзаместитель, преемник, <i>т. е.</i> принимающий на себя (s. functionis paternae <i>CJ</i>; s. alieni periculi <i>Dig</i>)."],
  ["succedo", "<big>succedo</big>\n\n<b>succedo, cessi, cessum, ere</b> [sub + cedo]\n<b>1)</b> входить под (что-л.) <i>или</i> внутрь (чего-л.), проникать (aliquid <i>и</i> alicui rei): quis nostris successit aedibus? <i>V</i> кто пожаловал в наш дом?; s. tecto et umbrae <i>V</i> спрятаться в закрытое место и в тень; nubes soli succedunt <i>Lcr</i> облака заходят под (<i>т. е.</i> застилают) солнце; s. aquae <i>O</i> погрузиться в воду; s. tumulo terrae <i>V</i> быть похороненным;\n<b>2)</b> возноситься (alto caelo <i>V</i>); возвышаться, достигать (ad summum honorem <i>Lcr</i>); всходить, подниматься (in montem <i>Lcr</i>): fons, quo (= in quem) mare succedit <i>Cs</i> источник, которого достигает морской прилив;\n<b>3)</b> подходить, подступать (muris <i>и</i> muros, ad moenia <i>и</i> moenibus <i>L</i>); идти на приступ (ad castra hostium <i>L</i>): succedentes <i>L</i> наступающие, нападающие;\n<b>4)</b> идти вслед, следовать (aetas succedit aetati <i>C</i>);\n<b>5)</b> наследовать (regno <i>PM</i> <i>и</i> patri in regno <i>Just</i>); вступать (in paternas opes <i>L</i>); замещать, сменять: in jura affinitatis s. <i>Just</i> стать в силу брака союзником (чьим-л.); defatigatis s. <i>Cs</i> сменять уставших; in stationem s. <i>Cs</i> сменять на страже; alicui successum est <i>C</i> у кого-л. есть наследник (преемник); in locum alicujus s. <i>Cs</i> занимать чьё-л. место; succedunt suis singula facta locis <i>O</i> отдельные события проходят (в воспоминании) друг за другом;\n<b>6)</b> следовать (<i>во времени</i>), наступать (tristibus laeta succedunt <i>Hier</i>): s. orationi alicujus <i>C</i> выступать после чьей-л. речи;\n<b>7)</b> примыкать (ad alteram partem <i>Cs</i>); относиться, принадлежать (comparativo generi <i>Q</i>);\n<b>8)</b> принимать на себя: s. oneri <i>V</i> брать на себя ношу;\n<b>9)</b> давать (получать) исход, складываться (hoc bene successit <i>Ter</i>): alicui melius succedit <i>Pt</i> кому-л. больше везёт;\n<b>10)</b> удаваться, иметь успех (haec successerunt <i>C</i>): negotium succedit sub manus <i>Pl</i> дело идёт на лад; si successisset coeptis <i>L</i> если бы удалось то, что предпринято; hac (via) non successit <i>Ter</i> этим путём ничего не вышло;\n<b>11)</b> попадать, подвергаться (jugo servitutis <i>CJ</i>; dominationi <i>Just</i>): sub acumen stili s. <i>C</i> попадать под остриё стиля, <i>т. е.</i> подвергаться литературной обработке."],
  ["succendo", "<big>succendo</big>\n\n<b>succendo, cendi, censum, ere</b> [sub + candeo]\n<b>1)</b> поджигать (aliquid face subdita <i>L</i>); зажигать (rogum <i>L</i>): s. aras <i>Sen</i> возжигать огонь на алтарях;\n<b>2)</b> возбуждать (furorem <i>Lcn</i>); воспламенять (aliquem <i>Prp, Sen</i>): luctu succensus <i>VF</i> охваченный (объятый) скорбью; dulcedine succensus <i>J</i> восхищённый прелестью;\n<b>3)</b> зажигать багрянцем, делать багровым (rubor succendit ora <i>Lcn</i>)."],
  ["succeno", "<big>succeno</big>\n\n<b>suc–ceno</b> <i>v. l.</i>\n= subceno."],
  ["succenseo", "<big>succenseo</big>\n\n<b>succenseo</b> <i>v. l.</i>\n= suscenseo."],
  ["succensio", "<big>succensio</big>\n\n<b>I succensio, onis</b> <i>f</i> [succendo]\n<b>1)</b> пожар (immensa <i>Amm</i>; flamma succensionis <i>Vlg</i>): s. matutina <i>Tert</i> утренняя заря;\n<b>2)</b> разжигание (gehennae ignis succensione non indiget <i>Eccl</i>);\n<b>3)</b> растапливание, топка (lavacri <i>Amm</i>).\n\n<b>II succensio, onis</b> <i>f</i> <i>v. l.</i>\n= suscensio."],
  ["succensus", "<big>succensus</big>\n\n<b>succensus, a, um</b> <i>part. pf. к</i>\n<b>1)</b> succendo <i>и</i>\n<b>2)</b> succenseo."],
  ["succentivus", "<big>succentivus</big>\n\n<b>succentivus, a, um</b> [succino]\nвторящий, аккомпанирующий (tibia <i>Vr</i>)."],
  ["succentor", "<big>succentor</big>\n\n<b>succentor, oris</b> <i>m</i> [succino]\n<b>1)</b> подпевала <i>Aug, Is</i>;\n<b>2)</b> <i>перен.</i> запевала, инициатор (fabularum crudelium <i>Amm</i>)."],
  ["succenturio", "<big>succenturio</big>\n\n<b>I succenturio, —, atum, are</b> [sub + centurio]\nзачислять в центурию (для её пополнения), <i>перен.</i> держать в резерве: ero succenturiatus, si quid deficies <i>Ter</i> я буду в резерве, если ты сколько-нибудь ослабеешь.\n\n<b>II succenturio, onis</b> <i>m</i> [sub + centurio]\nподцентурион, заместитель центуриона <i>L</i>."],
  ["succerno", "<big>succerno</big>\n\n<b>suc–cerno, crevi, cretum, ere</b>\n<b>1)</b> пропускать через сито, просеивать (cribro <i>Pl, PM</i>);\n<b>2)</b> встряхивать <i>LJ</i>."],
  ["successio", "<big>successio</big>\n\n<b>successio, onis</b> <i>f</i> [succedo]\n<b>1)</b> наследование, вступление (in locum alicujus <i>C</i>; principis <i>PM</i>); передача по наследству, преемственность: per successiones <i>PJ</i> в порядке наследственной передачи; jura successionum <i>T</i> унаследованные права;\n<b>2)</b> следование, наступление, появление (doloris amotio successionem efficit voluptatis <i>C</i>);\n<b>3)</b> (<i>тж.</i> s. prospera <i>C ap. Aug</i>) успешное завершение, успех (proelii <i>Eccl</i>)."],
  ["successivus", "<big>successivus</big>\n\n<b>successivus, a, um</b> [succedo]\nпоследующий (proles <i>Lact</i> — <i>v. l.</i>)."],
  ["successor", "<big>successor</big>\n\n<b>successor, oris</b> <i>m</i> [succedo]\nнаследник, преемник (alicujus m alicui <i>C, Sen, VP etc.</i>): studii s. et heres <i>O</i> преемник и наследник (моих) трудов."],
  ["successorius", "<big>successorius</big>\n\n<b>successorius, a, um</b> <i>юр.</i>\nнаследственный (jus <i>Amm</i>; edictum <i>Dig</i>)."],
  ["successus", "<big>successus</big>\n\n<b>I successus, a, um</b> <i>part. pf. к</i>\nsuccedo.\n\n<b>II successus, us</b> <i>m</i> [succedo]\n<b>1)</b> подход, приближение, продвижение (s. et incursus hostium <i>Cs</i>);\n<b>2)</b> ход, движение, бег (equorum <i>V</i>); последовательность, течение (temporis <i>Just</i>);\n<b>3)</b> исход, результат (prosperus <i>L</i>);\n<b>4)</b> хороший исход, успех, удача (plenus successibus annus <i>O</i>): successu exsultare <i>V</i> прыгать от счастья;\n<b>5)</b> потомок, отпрыск, дитя (sua manu suos exstinguere successus <i>CA</i> — <i>о Медее</i>)."],
  ["succidaneus", "<big>succidaneus</big>\n\n<b>succidaneus, a, um</b> <i>v. l.</i>\n= succedaneus I."],
  ["succidia", "<big>succidia</big>\n\n<b>succidia, ae</b> <i>f</i> [sus + caedo]\n<b>1)</b> часть свиной туши, окорок <i>Vr</i>: succidias humanas facere <i>Cato ap. AG</i> устраивать резню;\n<b>2)</b> <i>перен.</i> хозяйственный резерв, подсобный доход (hortum agricolae succidiam alteram appellant <i>C</i>)."],
  ["succido", "<big>succido</big>\n\n<b>I succido, cidi, —, ere</b> [sub + cado]\n<b>1)</b> подпадать (sub vocabulum <i>Vr</i>);\n<b>2)</b> падать (aeger succidit <i>V</i>); рушиться (horrisono fragore <i>Lcr</i>); проваливаться, оседать (terra succidit <i>Lcr</i>); подкашиваться (genua succidunt <i>Pl</i>); слабеть (mens succidit et incurvatur <i>Sen</i>).\n\n<b>II succido, cidi, cisum, ere</b> [sub + caedo]\n<b>1)</b> подрубать, срубать (arbores <i>Cs, Col</i>); подрезывать (succisi poplites <i>Pt</i>);\n<b>2)</b> срезывать (florem aratro <i>V</i>); скашивать (frumenta <i>Cs, V</i>);\n<b>3)</b> вырезывать, убивать (gregem imbellem ferro <i>Sil</i>);\n<b>4)</b> высекать (hemicyclium <i>Vtr</i>)."],
  ["succidus", "<big>succidus</big>\n\n<b>succidus, a, um</b> <i>v. l.</i>\n= sucidus."],
  ["succiduus", "<big>succiduus</big>\n\n<b>succiduus, a, um</b> [succido I]\n<b>1)</b> готовый упасть, шатающийся, подкашивающийся (genu, poples <i>O</i>);\n<b>2)</b> гаснущий (flammae <i>St</i>)."],
  ["succincte", "<big>succincte</big>\n\n<b>succincte</b>\nкратко, сжато (docere <i>Amm</i>; fari <i>Sid</i>)."],
  ["succinctorium", "<big>succinctorium</big>\n\n<b>succinctorium, i</b> <i>n</i> [succingo]\nпередник <i>Aug</i>."],
  ["succinctulus", "<big>succinctulus</big>\n\n<b>succinctulus, a, um</b> [<i>demin. к</i> succinctus]\nаккуратно подпоясанный <i>Ap</i>."],
  ["succinctus", "<big>succinctus</big>\n\n<b>succinctus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> succingo;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> подготовившийся, готовый (pronus et s. ad aliquid <i>Q</i>; alicui rei <i>O</i>);\n<b>2)</b> уплотнённый, сжатый (libellus <i>M</i>)."],
  ["succingo", "<big>succingo</big>\n\n<b>succingo, cinxi, cinctum, ere</b> [sub + cingo]\n<b>1)</b> подпоясывать, подбирать (вверх): succincta vestem (<i>acc. gr.</i>) ritu Dianae <i>O</i> (нимфа), подпоясав (подобрав) платье по примеру Дианы; succincta comas (<i>acc. gr.</i>) pinus <i>O</i> сосна с высоко подобранной хвоей, <i>т. е.</i> голоствольная;\n<b>2)</b> препоясывать (succinctus pugione <i>C</i>, pharetra <i>V</i>, cingulo <i>Pt</i>); опоясывать: Scylla feris atram canibus succingitur alvum <i>O</i> чёрный живот Сциллы опоясан свирепыми псами || окружать (Carthago succincta portubus <i>C</i>);\n<b>3)</b> вооружать, снабжать (scientia <i>Q</i>); укреплять (animum aliqua re <i>Pt</i>). — <i>См. тж.</i> succinctus."],
  ["succingulum", "<big>succingulum</big>\n\n<b>succingulum, i</b> <i>n</i> [succingo]\nпояс, перевязь <i>Pl</i>."],
  ["succino", "<big>succino</big>\n\n<b>succino, —, —, ere</b> [sub + cano]\n<b>1)</b> вторить, подпевать <i>Calp, Pt</i>; повторять (за кем-л.), поддакивать (succinit alter <i>H</i>);\n<b>2)</b> бормотать: s. ambages <i>Pers</i> плести небылицы."],
  ["succinu-", "<big>succinu-</big>\n\n<b>succinu-</b> <i>v. l.</i>\n= sucinu-."],
  ["succinxi", "<big>succinxi</big>\n\n<b>succinxi</b> <i>pf. к</i>\nsuccingo."],
  ["succipio", "<big>succipio</big>\n\n<b>succipio</b> <i>v. l.</i>\n= suscipio."],
  ["succisio", "<big>succisio</big>\n\n<b>succisio, onis</b> <i>f</i> [succido II]\nрубка (lignorum <i>Vlg</i>); срезание (florum <i>Hier</i>); стрижка (pilorum <i>Sid</i>)."],
  ["succisivus", "<big>succisivus</big>\n\n<b>succisivus, a, um</b> <i>v. l.</i>\n= subcisivus."],
  ["succisus", "<big>succisus</big>\n\n<b>succisus, a, um</b> <i>part. pf. к</i>\nsuccido II."],
  ["succlamatio", "<big>succlamatio</big>\n\n<b>succlamatio, onis</b> <i>f</i> [succlamo]\n(ответный) возглас, восклицание <i>L, Su</i>."],
  ["succlamo", "<big>succlamo</big>\n\n<b>suc–clamo, avi, atum, are</b>\nкричать в ответ (alicui <i>L</i>): succlamatum est <i>L etc.</i> закричали в ответ."],
  ["succo", "<big>succo</big>\n\n<b>succo, onis</b> <i>m</i> <i>v. l.</i>\n= suco."],
  ["succollo", "<big>succollo</big>\n\n<b>succollo, avi, atum, are</b> [sub + collum]\nбрать на плечи, нести на плечах (fessum <i>Vr</i>; <i>sc.</i> lecticam <i>Su</i>)."],
  ["succosus", "<big>succosus</big>\n\n<b>succosus, a, um</b> <i>v. l.</i>\n= sucosus."],
  ["succrepo", "<big>succrepo</big>\n\n<b>suc–crepo, —, —, are</b>\nдавать снизу трещины (succrepante fragili solo <i>Aug</i>)."],
  ["succresco", "<big>succresco</big>\n\n<b>suc–cresco, crevi, cretum, ere</b>\n<b>1)</b> расти под (чём-л.), внизу (sub ordine pilorum alius ordo succrescit <i>CC</i>); вырастать (ab imo <i>O</i>);\n<b>2)</b> нарастать, прибывать, восполняться (vina per se succrescunt <i>O</i>);\n<b>3)</b> подрастать, созревать (orator succrescit <i>C</i>)."],
  ["succretus", "<big>succretus</big>\n\n<b>succretus, a, um</b> <i>part. pf. к</i>\nsuccresco."],
  ["succuba", "<big>succuba</big>\n\n<b>succuba, ae</b> <i>f</i> [succubo] <i>Ap</i>\n= concubina."],
  ["succubo", "<big>succubo</big>\n\n<b>suc–cubo, —, —, are</b>\nлежать внизу: s. alicui rei <i>и</i> s. aliquid <i>Ap</i> лежать под чём-л."],
  ["succubui", "<big>succubui</big>\n\n<b>succubui</b> <i>pf. к</i>\nsuccumbo."],
  ["succulentus", "<big>succulentus</big>\n\n<b>succulentus, a, um</b> <i>v. l.</i>\n= suculentus."],
  ["succumbo", "<big>succumbo</big>\n\n<b>succumbo, cubui, cubitum, ere</b> [sub + cumbo = cubo]\n<b>1)</b> опускаться, падать (victima ferro succumbens <i>Ctl</i>);\n<b>2)</b> смыкаться, смежаться (oculi succubuerunt <i>O</i>);\n<b>3)</b> заболеть, слечь в постель: aggravata valetudine Augustus Nolae succubuit <i>Su</i> когда состояние (его) здоровья ухудшилось, Август в Ноле слёг;\n<b>4)</b> отдаваться, быть наложницей (alicui <i>Vr, Ctl, O, M etc.</i>);\n<b>5)</b> поддаваться (власти), подчиняться, уступать (tempori <i>L</i>; fortunae <i>C</i>);\n<b>6)</b> падать (становиться) жертвой, не устоять (s. inimicis <i>C</i>): s. culpae <i>V, O</i> поддаться (преступному) соблазну || быть сражённым: s. animo <i>C</i> пасть духом; philosopho succubuit orator <i>C</i> оратор был побеждён философом; hac perculsus plaga non succubuit <i>Nep</i> (Эвмен), получив этот удар, не был им сражён."],
  ["succurro", "<big>succurro</big>\n\n<b>suc–curro, curri, cursum, ere</b>\n<b>1)</b> пробегать под (чем-либо), проходить снизу (alicui rei <i>Lcr, Vr</i>);\n<b>2)</b> приходить (спешить) на помощь (laborantibus <i>C, Cs</i>; alicui auxilio <i>Cs</i>);\n<b>3)</b> помогать (afflictis <i>Nep</i>): venenis fungorum s. <i>PM</i> помогать (быть средством) от грибных ядов; s. vitae alicujus <i>C</i> спасать чью-л. жизнь;\n<b>4)</b> способствовать, содействовать, служить средством: his tantis malis haec subsidia succurrebant, quominus omnis deleretur exercitus <i>Cs</i> в этих тяжёлых условиях следующие обстоятельства помешали распадению всей армии;\n<b>5)</b> представляться, приходить в голову (succurrit versus ille Homericus <i>Su</i>): nomen non succurrit <i>Sen</i> имя не вспоминается; non dubito legentibus illud succursurum, unde... <i>L</i> читателям, конечно, придёт в голову вопрос, откуда...;\n<b>6)</b> выступать против, идти навстречу: licet omnes in me terrores impendeant, succurram atque subibo <i>C</i> пусть мне угрожают всяческие ужасы, я встречу (их) лицом к лицу."],
  ["succus", "<big>succus</big>\n\n<b>succus, i</b> <i>m</i> <i>v. l.</i>\n= sucus."],
  ["succussator", "<big>succussator</big>\n\n<b>succussator, oris</b> <i>m</i> [succutio]\nтряская лошадь <i>LM</i>."],
  ["succussi", "<big>succussi</big>\n\n<b>succussi</b> <i>pf. к</i>\nsuccutio."],
  ["succussio", "<big>succussio</big>\n\n<b>succussio, onis</b> <i>f</i> [succutio]\nсотрясение, колебание, (подземный) толчок (<i>sc.</i> terrae <i>Sen</i>)."],
  ["succussor", "<big>succussor</big>\n\n<b>succussor, oris</b> <i>m</i> <i>LM</i>\n= succussator."],
  ["succussus", "<big>succussus</big>\n\n<b>I succussus, a, um</b> <i>part. pf. к</i>\nsuccutio.\n\n<b>II succussus, us</b> <i>m</i>\nтрясение, дрожание <i>Pac ap. C, Ap, Tert</i>."],
  ["succustos", "<big>succustos</big>\n\n<b>suc–custos, odis</b> <i>m</i>\nпомощник сторожа <i>Pl</i>."],
  ["succutio", "<big>succutio</big>\n\n<b>succutio, cussi, cussum, ere</b> [sub + quatio]\n<b>1)</b> встряхивать, подбрасывать: succutitur currus alte <i>O</i> колесница (Фаэтонта) взлетает высоко вверх;\n<b>2)</b> <i>перен.</i> потрясать, расстраивать (vultus tristitiae salebra succussus <i>VM</i>); волновать (vestem carneam <i>Aug</i>)."],
  ["sucerda", "<big>sucerda</big>\n\n<b>sucerda, ae</b> <i>f</i> [sus]\nсвиной навоз <i>LM</i>."],
  ["sucidia", "<big>sucidia</big>\n\n<b>sucidia, ae</b> <i>f</i> <i>v. l.</i>\n= succidia."],
  ["sucidus", "<big>sucidus</big>\n\n<b>sucidus, a, um</b> [sucus]\n<b>1)</b> сочный (ficus <i>CA</i>); сырой (solum <i>Ap</i>); влажный, потный (lana <i>Vr, CC, J etc.</i>);\n<b>2)</b> свежий, пышный, полный (puella <i>Pl</i> — <i>v. l.</i>)."],
  ["sucinacius", "<big>sucinacius</big>\n\n<b>sucinacius, a, um</b> [sucinum]\nянтарного цвета (vinum <i>Is</i>)."],
  ["sucinum", "<big>sucinum</big>\n\n<b>sucinum, i</b> <i>n</i> [sucus]\nянтарь <i>PM, T</i>; <i>pl.</i> янтарные украшения <i>PM, J, M</i>."],
  ["sucinus", "<big>sucinus</big>\n\n<b>sucinus, a, um</b>\nиз янтаря, янтарный (gemma <i>M</i>; novacula <i>PM</i>)."],
  ["suco", "<big>suco</big>\n\n<b>suco, onis</b> <i>m</i> [sugo]\nкровосос, пиявка (<i>о ростовщике</i>) <i>C</i>."],
  ["sucoph-", "<big>sucoph-</big>\n\n<b>sucoph-</b> <i>v. l.</i>\n= sycoph-."],
  ["sucositas", "<big>sucositas</big>\n\n<b>sucositas, atis</b> <i>f</i>\nсочность <i>CA</i>."],
  ["sucosus", "<big>sucosus</big>\n\n<b>sucosus, a, um</b> [sucus]\n<b>1)</b> сочный (poma <i>C</i>; solum <i>Col</i>);\n<b>2)</b> богатый (colliberti <i>Pt</i>)."],
  ["Sucro", "<big>Sucro</big>\n\n<b>Sucro, onis</b> <i>m</i>\nСукрон:\n<b>1)</b> <i>река в Hispania Tarraconensis</i> (<i>ныне</i> Jucar) <i>Mela, PM</i>;\n<b>2)</b> <i>город в устье этой реки</i> (<i>ныне</i> Alcira) <i>L, Sen, PM</i>;\n<b>3)</b> <i>мужское имя</i> <i>V</i>."],
  ["Sucronensis", "<big>Sucronensis</big>\n\n<b>Sucronensis, e</b> [Sucro]\nсукронский (proelium <i>C</i>)."],
  ["sucula", "<big>sucula</big>\n\n<b>sucula, ae</b> <i>f</i> [<i>demin. к</i> sus]\n<b>1)</b> маленькая свинья, свинка <i>Pl</i>: S. <i>или</i> Suculae <i>C, PM</i> = Гиады (<i>созвездие</i>);\n<b>2)</b> <i>тех.</i> ворот, лебёдка <i>Pl, Cato, Vtr</i>."],
  ["suculentus", "<big>suculentus</big>\n\n<b>suculentus, a, um</b> [sucus]\nсочный (arvina <i>Ap</i>): suculenta gracilitas <i>Ap</i> стройность в сочетании со свежестью (<i>или</i> пышностью) форм."],
  ["suculus", "<big>suculus</big>\n\n<b>suculus, i</b> <i>m</i> [sus]\nпоросёнок <i>CJ</i>."],
  ["sucus", "<big>sucus</big>\n\n<b>sucus (succus), i</b> <i>m</i> [sugo]\n<b>1)</b> сок, влага (pomorum <i>H</i>; herbarum, uvae <i>Tib</i>); suci plenus <i>Ter etc.</i> налитой, полный (cervix <i>Ap</i>) || жидкость, жидкая часть (ciborum <i>C</i>);\n<b>2)</b> напиток, снадобье (purgantes pectora suci <i>O</i>);\n<b>3)</b> вкус, качество (habere sucum <i>Pt</i>; ova suci melioris <i>H</i>);\n<b>4)</b> крепость, мощь (s. ac sanguis civitatis <i>C</i>); сочность, сила (orationis <i>C</i>)."],
  ["sudariolum", "<big>sudariolum</big>\n\n<b>sudariolum, i</b> <i>n</i> [<i>demin. к</i> sudarium]\n(носовой) платочек <i>Ap, Hier</i>."],
  ["sudarium", "<big>sudarium</big>\n\n<b>sudarium, i</b> <i>n</i> [sudor]\n(носовой) платок (<i>для вытирания пота</i>) <i>Ctl, Q, Pt etc.</i>"],
  ["sudatio", "<big>sudatio</big>\n\n<b>sudatio, onis</b> <i>f</i> [sudo]\n<b>1)</b> потение, испарина <i>Sen, CC</i>;\n<b>2)</b> парильня, баня <i>Vtr</i>;\n<b>3)</b> потогонное средство <i>CC</i>."],
  ["sudator", "<big>sudator</big>\n\n<b>sudator, oris</b> <i>m</i>\nпотливый человек <i>PM</i>."],
  ["sudatorium", "<big>sudatorium</big>\n\n<b>sudatorium, i</b> <i>n</i>\nпотогонная баня (balnea et sudatoria <i>Sen</i>)."],
  ["sudatorius", "<big>sudatorius</big>\n\n<b>sudatorius, a, um</b> [sudo]\nпотогонный (unctio <i>Pl</i>)."],
  ["sudatrix", "<big>sudatrix</big>\n\n<b>sudatrix, tricis</b> <i>adj. f</i> [sudo]\nсмоченная потом <i>или</i> вгоняющая в пот (toga <i>M</i>)."],
  ["sudiculum", "<big>sudiculum</big>\n\n<b>sudiculum, i</b> <i>n</i> [sudo]\n«потогон»: s. flagri <i>бран. Pl</i> обливающий своим потом кнуты, <i>т. е.</i> достойный лишь плетей."],
  ["sudis", "<big>sudis</big>\n\n<b>sudis, is</b> <i>f</i>\n<b>1)</b> кол, свая (sudes stipitesque <i>Cs</i>); остроконечный кол (scutum et s. <i>Tib</i>);\n<b>2)</b> шип, остриё (erectae in terga sudes <i>J</i>);\n<b>3)</b> острый выступ (sudes saxeae <i>Ap</i>);\n<b>4)</b> <i>род морской рыбы</i> <i>PM</i>."],
  ["sudo", "<big>sudo</big>\n\n<b>sudo, avi, atum, are</b>\n<b>1)</b> потеть (in balneo <i>CC</i>): s. frigido sudore <i>CC</i> обливаться холодным потом || пропитывать потом (vestis sudata <i>Q</i>): s. et algere <i>H</i> то потеть, то зябнуть;\n<b>2)</b> испускать из себя, выделять (tura et balsama <i>T</i>; roscida mella <i>V</i>);\n<b>3)</b> истекать, струиться (sanguine <i>V, L</i>);\n<b>4)</b> выделяться: sudantia ligno balsama <i>V</i> бальзам, струящийся из дерева;\n<b>5)</b> в поте лица работать, мучительно трудиться: sudandum est pro communibus commodis <i>C</i> необходимо упорно работать для общего блага; ad supervacua sudatur <i>Sen</i> (многие) работают ради излишеств;\n<b>6)</b> изготовлять упорным трудом (multo labore sudatus thorax <i>Sil</i>)."],
  ["sudor", "<big>sudor</big>\n\n<b>sudor, oris</b> <i>m</i> [sudo]\n<b>1)</b> пот (s. a fronte defluens <i>C</i>): sudorem excutere <i>Nep</i> (movere, ciere <i>PM</i>) вызывать пот;\n<b>2)</b> влага, жидкость (veneni <i>O</i>); истечение (smyrnae <i>Lcr</i>): s. maris <i>Lcr</i> морская вода;\n<b>3)</b> тяжёлый труд, упорная работа (multo sudore ac labore <i>C</i>): multi sudoris esse <i>C</i> требовать упорного труда."],
  ["sudorifer", "<big>sudorifer</big>\n\n<b>sudori–fer, fera, ferum</b> [fero]\nпотогонный (vapores <i>CA</i>)."],
  ["sudorus", "<big>sudorus</big>\n\n<b>sudorus, a, um</b> [sudor]\nпотный, вспотевший (corpus <i>Ap</i>)."],
  ["suduculum", "<big>suduculum</big>\n\n<b>suduculum, i</b> <i>n</i> <i>v. l.</i>\n= sudiculum."],
  ["sudum", "<big>sudum</big>\n\n<b>I sudum, i</b> <i>n</i> [sudus]\nясная погода, безоблачное небо <i>V, C</i>: quum s. est <i>Pl</i> при ясной погоде.\n\n<b>II sudum</b> <i>adv.</i>\nясно, ярко (praenitere <i>Eccl</i>)."],
  ["sudus", "<big>sudus</big>\n\n<b>sudus, a, um</b>\nсухой, безоблачный, ясный (ver <i>V</i>;tempestas <i>Ap</i>)."],
  ["Sueba", "<big>Sueba</big>\n\n<b>Sueba, ae</b> <i>f</i>\nсвебка, <i>женщина племенной группы свебов</i> <i>Cs</i>."],
  ["Suebi", "<big>Suebi</big>\n\n<b>Suebi, orum</b> <i>m</i>\nсвебы (свевы), <i>собирательное название ряда племён в сев.-вост. Германии</i> <i>Cs, T, Lcn etc.</i>"],
  ["Suebia", "<big>Suebia</big>\n\n<b>Suebia, ae</b> <i>f</i>\nобласть свебов <i>T</i>."],
  ["Suebicus", "<big>Suebicus</big>\n\n<b>Suebicus</b> <i>v. l.</i>\n= Suebus I."],
  ["Suebus", "<big>Suebus</big>\n\n<b>I Suebus, a, um</b> [Suebi]\nсвебский <i>T, Prp, Sil</i>.\n\n<b>II Suebus, i</b> <i>m</i>\nсвеб (свев) <i>Cld</i>."],
  ["suemus", "<big>suemus</big>\n\n<b>suemus</b> <i>и</i> <b>svemus</b> <i>1-е л. pl. praes. к</i>\nsuesco: appellare suemus <i>Lcr</i> обыкновенно называем."],
  ["sueris", "<big>sueris</big>\n\n<b>sueris</b> <i>Vr gen. sg. к</i>\nsus."],
  ["suerunt", "<big>suerunt</big>\n\n<b>suerunt</b> <i>стяж. pf. к</i>\nsuesco."],
  ["suesco", "<big>suesco</big>\n\n<b>suesco, suevi (svevi), suetum (svetum), ere</b>\n<b>1)</b> приучаться, привыкать (alicui rei <i>T</i>); <i>pf.</i> привыкнуть, иметь обыкновение (aliquid facere <i>Lcr, Prp</i>): quod suesti <i>C</i> как ты обыкновенно делаешь;\n<b>2)</b> приучать (aliquem aliqua re <i>T</i>). — <i>См. тж.</i> suetus."],
  ["Suessa", "<big>Suessa</big>\n\n<b>Suessa, ae</b> <i>f</i>\nСвесса:\n<b>1)</b> S. Aurunca, <i>город в южн. Латии, на зап. склоне mons Massicus, главн. город аврунков</i> <i>Cato, C, L, VP</i>;\n<b>2)</b> S. Pometia, <i>город вольсков в Латии</i> <i>C, L, Sil, Aus</i>."],
  ["Suessanus", "<big>Suessanus</big>\n\n<b>Suessanus, a, um</b> [Suessa]\nсвесский <i>Cato, C</i>."],
  ["suesse", "<big>suesse</big>\n\n<b>suesse</b> <i>Lcr стяж. inf. pf. к</i>\nsuesco."],
  ["Suessiones", "<big>Suessiones</big>\n\n<b>Suessiones, um</b> <i>m</i>\nсвессионы, <i>племя в Gallia Belgica, между реками Матрона и Изара, с главн. городом Noviodunum</i> (<i>ныне</i> Soissons) <i>Cs, PM</i>."],
  ["Suessula", "<big>Suessula</big>\n\n<b>Suessula, ae</b> <i>f</i>\nСвессула, <i>город в Кампании, к юго-вост. от Капуи, место сражения в 342 г. до н. э.</i> (<i>I Самнитская война</i>) <i>L</i>."],
  ["Suessulani", "<big>Suessulani</big>\n\n<b>Suessulani, orum</b> <i>m</i>\nжители города Suessula <i>L</i>."],
  ["Suetonius", "<big>Suetonius</big>\n\n<b>Suetonius, a, um</b>\nСветоний, <i>римск. nomen; наиболее известны</i>:\n<b>1)</b> C. S. Paulinus, <i>военачальник при императорах Клавдии и Нероне, наместник в Британии с 59 по 61 гг. н. э.</i> <i>T</i>;\n<b>2)</b> S. Lenis, <i>военный трибун XIII легиона, отец историка Светония</i> <i>Su</i>;\n<b>3)</b> C. S. Tranquillus, <i>личный секретарь императора Адриана, историк, автор «Vitae duodecim imperatorum»</i> (<i>ок. 75-160 гг. н. э.</i>)."],
  ["suetum", "<big>suetum</big>\n\n<b>suetum, i</b> <i>n</i> [suetus]\nпривычка, обыкновение <i>Ap</i>."],
  ["suetus", "<big>suetus</big>\n\n<b>suetus</b> (<i>поэт.</i> svetus), <b>a, um</b>\n<b>1.</b> <i>part. pf. к</i> suesco;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> приучившийся, привыкший (alicui rei <i>V, T etc.</i>): secundis rebus oriri sueta mala <i>Sl ap. Aug</i> пороки, которые обыкновенно порождаются преуспеянием;\n<b>2)</b> вошедший в обыкновение, ставший привычным: sueta apud paludes proelia <i>T</i> сражения обыкновенно происходят в болотистых местах."],
  ["suetus", "<big>suetus</big>\n\n<b>I suetus, a, um</b> <i>part. pf. к</i>\nsugo.\n\n<b>II suetus, us</b> <i>m</i>\nсосание <i>Vr, PM</i>."],
  ["Suev-", "<big>Suev-</big>\n\n<b>Suev-</b> <i>v. l.</i>\n= Sueb-."],
  ["suevi", "<big>suevi</big>\n\n<b>suevi</b> <i>pf. к</i>\nsuesco."],
  ["suf-", "<big>suf-</big>\n\n<b>suf-</b>\n<i>приставка</i> = sub- <i>перед начальным</i> f <i>основного слова.</i>"],
  ["sufes", "<big>sufes</big>\n\n<b>sufes, etis</b> <i>m</i> (<i>финик.</i> «судья»)\nсуфет, <i>высшее должностное лицо в Карфагене</i> <i>L, Sen</i>."],
  ["suffarcino", "<big>suffarcino</big>\n\n<b>suf–farcino, avi, atum, are</b>\nнабивать, пичкать (cum libris <i>Pl</i>); нагружать: bellule suffarcinatus <i>Ap</i> изрядно наевшись."],
  ["suffeci", "<big>suffeci</big>\n\n<b>suffeci</b> <i>pf. к</i>\nsufficio."],
  ["suffectio", "<big>suffectio</big>\n\n<b>suffectio, onis</b> <i>f</i> [sufficio]\n<b>1)</b> добавление (unguentorum <i>Eccl</i>);\n<b>2)</b> замещение, восполнение (alicujus rei <i>Tert</i>)."],
  ["suffectura", "<big>suffectura</big>\n\n<b>suffectura, ae</b> <i>f</i> <i>Tert</i>\n= suffectio 2."],
  ["suffectus", "<big>suffectus</big>\n\n<b>suffectus, a, um</b> <i>part. pf. к</i>\nsufficio: consul s. <i>L</i> дополнительно (<i>т. е.</i> взамен выбывшего) избранный консул."],
  ["Suffenus", "<big>Suffenus</big>\n\n<b>Suffenus, i</b> <i>m</i>\nСуффен, <i>второстепенный стихотворец, современник Катулла</i> <i>Ctl</i>."],
  ["suffercitus", "<big>suffercitus</big>\n\n<b>suffercitus</b> <i>и</i> <b>sufferctus, a, um</b> <i>LM</i>\n= suffertus."],
  ["sufferentia", "<big>sufferentia</big>\n\n<b>sufferentia, ae</b> <i>f</i>\nтерпеливость, терпение <i>Vlg, Eccl</i>."],
  ["suffermentatus", "<big>suffermentatus</big>\n\n<b>suf–fermentatus, a, um</b>\nнемного забродивший <i>Tert</i>."],
  ["suffero", "<big>suffero</big>\n\n<b>suf–fero, sustuli, —, ferre</b> [fero]\n<b>1)</b> подставлять (для побоев) (corium <i>Pl</i>);\n<b>2)</b> предлагать, давать (lac <i>Vr</i>);\n<b>3)</b> поддерживать: s. se <i>Su</i> держаться прямо;\n<b>4)</b> переносить, выдерживать (laborem, solem <i>Pl</i>); терпеть (sitim <i>Pl</i>): vix suffero anhelitum <i>Pl</i> едва могу отдышаться; poenas alicujus rei s. <i>C</i> терпеть (нести) наказание за что-л.; poenas alicui s. (<i>по аналогии с</i> poenas dare) <i>Pl</i> быть наказанным кем-л.; ad praetorem s. (<i>sc.</i> se rapi) <i>Pl</i> согласиться на передачу дела претору (в суд)."],
  ["suffertus", "<big>suffertus</big>\n\n<b>suffertus, a, um</b> [sub + farcio]\nнаполненный, набитый: aliquid sufferti tinnire <i>Su</i> петь что-л. полным голосом."],
  ["suffervefacio", "<big>suffervefacio</big>\n\n<b>suf–fervefacio, —, factum, ere</b> [sub + fervefacio]\nподогревать (aliquid <i>PM</i>)."],
  ["suffervefio", "<big>suffervefio</big>\n\n<b>suffervefio, —, fieri</b> <i>PM pass. к</i>\nsuffervefacio."],
  ["sufferveo", "<big>sufferveo</big>\n\n<b>suf–ferveo, —, —, ere</b>\nслегка вскипать <i>Ap</i>."],
  ["suffes", "<big>suffes</big>\n\n<b>suffes, etis</b> <i>m</i> <i>v. l.</i>\n= sufes."],
  ["Suffetius", "<big>Suffetius</big>\n\n<b>Suffetius, i</b> <i>m</i>\n= Fufetius (<i>см.</i> Mettius Fufetius)."],
  ["suffibulum", "<big>suffibulum</big>\n\n<b>suf–fibulum, i</b> <i>n</i> [fibula]\nсуффибул, <i>белый головной платок жрецов и весталок</i> <i>Vr</i>."],
  ["sufficiens", "<big>sufficiens</big>\n\n<b>sufficiens, entis</b>\n<b>1.</b> <i>part. praes. к</i> sufficio;\n<b>2.</b> <i>adj.</i> достаточный, подходящий, пригодный (vires <i>L</i>; aetas <i>QC</i>)."],
  ["sufficienter", "<big>sufficienter</big>\n\n<b>sufficienter</b>\nдостаточно <i>AV, PJ, Dig etc.</i>"],
  ["sufficientia", "<big>sufficientia</big>\n\n<b>sufficientia, ae</b> <i>f</i>\nдостаточность, довление <i>Vlg, Eccl</i>."],
  ["sufficio", "<big>sufficio</big>\n\n<b>sufficio, feci, fectum, ere</b> [facio]\n<b>1)</b> покрывать краской, красить, пропитывать (lanam medicamentis <i>C</i>): oculos (<i>acc. gr.</i>) suffectus sanguine <i>V</i> с глазами, налитыми кровью; nubes sole suffecta <i>Sen</i> озаренное солнцем облако;\n<b>2)</b> давать, доставлять (cunctarum rerum abundantiam alicui s. <i>Just</i>): salices umbram sufficiunt <i>V</i> ивы дают тень; milites excursionibus s. <i>L</i> отправлять солдат для набегов; animos viresque s. <i>V</i> даровать мужество и силу || подавать, подносить (pilas ludentibus <i>Pt</i>);\n<b>3)</b> избирать взамен, переизбирать (collegam <i>L</i>; aliquem in locum alicujus <i>или</i> alicui <i>L</i>);\n<b>4)</b> заменять, восстанавливать (prolem generando <i>V</i>);\n<b>5)</b> быть достаточным, хватать, быть в состоянии, выдерживать: non videntur tempora suffectura <i>Q</i> времени, пожалуй, не хватит; solum sufficiens operi firmo <i>QC</i> участок, способный выдержать тяжёлую постройку; s. non posse <i>C</i> не (быть в состоянии) совладать; s. cupiditati alicujus <i>C</i> удовлетворить чью-л. жадность; s. alicui rei <i>Pt</i> быть пригодным для чего-л.; nec sufficit umbo ictibus <i>V</i> щит не мог отражать (защищать от) ударов; sufficit, ut sinas <i>PJ</i> достаточно, чтобы ты разрешил; s. adversus aliquem <i>L</i> быть в состоянии справиться с кем-л.; s. tibi debet, si... <i>PJ</i> хватит с тебя, если... — <i>См. тж.</i> suffectus."],
  ["suffigo", "<big>suffigo</big>\n\n<b>suf–figo, fixi, fixum, ere</b>\n<b>1)</b> прикреплять, прибивать, приколачивать (lintea antennae <i>Lcn</i>): s. aliquem cruci <i>C</i> (in cruce <i>H, Ctl</i> <i>или</i> in crucem <i>Just</i>) пригвоздить кого-л. к кресту; caput Galbae hasta suffixum <i>Su</i> голова Гальбы, насаженная на копьё;\n<b>2)</b> подпирать (columnam mento <i>Pl</i> — <i>ирон.</i>): novos stimulos dolori s. <i>SenT</i> давать новую пищу скорби;\n<b>3)</b> обивать (aliquid auro <i>SenT</i>); усаживать (crepidas aureis clavis <i>PM</i>)."],
  ["suffimen", "<big>suffimen</big>\n\n<b>suffimen, inis</b> <i>n</i> <i>O</i>\n= suffimentum."],
  ["suffimento", "<big>suffimento</big>\n\n<b>suffimento, —, —, are</b>\nокуривать <i>или</i> коптить (bovem <i>Veg</i>)."],
  ["suffimentum", "<big>suffimentum</big>\n\n<b>suffimentum, i</b> <i>n</i> [suffio]\nблаговонное курение, курительное благовоние <i>C, PM, Veg</i>."],
  ["suffio", "<big>suffio</big>\n\n<b>suffio, ii (ivi), itum, ire</b> [<i>одного корня с</i> fumus]\n<b>1)</b> курить, воскурять (thymo <i>V</i>);\n<b>2)</b> окуривать (urna suffita <i>O</i>);\n<b>3)</b> опалять <i>или</i> коптить (carnem haedorum <i>PM</i>);\n<b>4)</b> согревать (terras ignibus aetheriis <i>Lcr</i>)."],
  ["suffitio", "<big>suffitio</big>\n\n<b>suffitio, onis</b> <i>f</i> [suffio]\nокуривание <i>Col, PM, Eccl</i>."],
  ["suffitor", "<big>suffitor</big>\n\n<b>suffitor, oris</b> <i>m</i> [suffio]\nокуривающий <i>PM</i>."],
  ["suffitus", "<big>suffitus</big>\n\n<b>I suffitus, a, um</b> <i>part. pf. к</i>\nsuffio.\n\n<b>II suffitus, us</b> <i>m</i>\n<b>1)</b> курение, окуривание <i>PM</i>;\n<b>2)</b> дым от курения <i>PM</i>."],
  ["suffixus", "<big>suffixus</big>\n\n<b>suffixus, a, um</b> <i>part. pf. к</i>\nsuffigo."],
  ["sufflamen", "<big>sufflamen</big>\n\n<b>sufflamen, inis</b> <i>n</i> [sufflo]\n<b>1)</b> тормоз (rotam astringere sufflamine <i>J</i>);\n<b>2)</b> задержка, затяжка, препятствие (longum s. litis <i>J</i>)."],
  ["sufflamino", "<big>sufflamino</big>\n\n<b>sufflamino, —, —, are</b> [sufflamen]\nтормозить, останавливать (rotam Ixionis <i>Sen</i>); сдерживать, умерять (aliquem <i>Augustus ap. Sen</i>)."],
  ["sufflammo", "<big>sufflammo</big>\n\n<b>suf–flammo, —, —, are</b>\nразжигать (calumniam invidia <i>Sid</i>)."],
  ["sufflatio", "<big>sufflatio</big>\n\n<b>sufflatio, onis</b> <i>f</i> [sufflo]\nвздымание, вспучивание, подъём (bullantium aquarum <i>PM</i>)."],
  ["sufflatorium", "<big>sufflatorium</big>\n\n<b>sufflatorium, i</b> <i>n</i> [sufflo]\nкузнечный мех <i>Vlg</i>."],
  ["sufflatus", "<big>sufflatus</big>\n\n<b>I sufflatus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> sufflo;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> разбухший, раздутый (corpus <i>Vr</i>);\n<b>2)</b> надутый, чванящийся (multiplici scientia <i>Vr</i>); выспренний (in dicendo <i>AG</i>);\n<b>3)</b> рассерженный <i>Pl</i>.\n\n<b>II sufflatus, us</b> <i>m</i>\nдуновение, дыхание, веяние (odoris <i>PM</i>)."],
  ["sufflavus", "<big>sufflavus</big>\n\n<b>suf–flavus, a, um</b>\nжелтоватый, близкий к белокурому (capillus <i>Su</i>)."],
  ["sufflo", "<big>sufflo</big>\n\n<b>suf–flo, avi, atum, are</b>\n<b>1)</b> дуть (buccis suis <i>M</i>); надувать (sibi buccas <i>Pl, Vr</i>): s. aliquem <i>Pt</i> дуть на кого-л.; se s. alicui <i>Pl</i> рассердиться на кого-л.;\n<b>2)</b> раздувать (languidos ignes <i>PM</i>)."],
  ["suffocabilis", "<big>suffocabilis</big>\n\n<b>suffocabilis, e</b>\nудушливый, душный (locus <i>CA</i>)."],
  ["suffocatio", "<big>suffocatio</big>\n\n<b>suffocatio, onis</b> <i>f</i>\nудушье <i>PM, Sen</i>."],
  ["suffoco", "<big>suffoco</big>\n\n<b>suffoco, avi, atum, are</b> [sub + faux]\nдушить, удавливать (aliquem <i>C</i>): s. urbem fame <i>C</i> уморить город голодом || <i>med.-pass.</i> задыхаться <i>Lcr etc.</i>"],
  ["suffodio", "<big>suffodio</big>\n\n<b>suf–fodio, fodi, fossum, ere</b>\n<b>1)</b> подкапывать, подрывать (muros <i>T</i>);\n<b>2)</b> прокапывать насквозь (montes <i>VP</i>) <i>или</i> под землёй копать, рыть (specum <i>QC</i>);\n<b>3)</b> подкалывать, снизу пронзать (equos <i>Cs, V</i>)."],
  ["suffossio", "<big>suffossio</big>\n\n<b>suffossio, onis</b> <i>f</i> [suffodio]\nподкапывание, подрывание <i>Sen, Vlg</i>."],
  ["suffossor", "<big>suffossor</big>\n\n<b>suffossor, oris</b> <i>m</i>\nведущий подкоп <i>Vtr</i> (<i>v. l.</i>)."],
  ["suffossus", "<big>suffossus</big>\n\n<b>suffossus, a, um</b> <i>part. pf. к</i>\nsuffodio."],
  ["suffragatio", "<big>suffragatio</big>\n\n<b>suffragatio, onis</b> <i>f</i> [suffragor]\nподача голосов, голосование (в чью-л. пользу), <i>тж.</i> избрание: s. urbana <i>C</i> избрание городским населением; s. militaris <i>C</i> избрание солдатами; s. consulatus <i>C</i> представление к избранию консулом, выдвижение кандидатуры в консулы."],
  ["suffragator", "<big>suffragator</big>\n\n<b>suffragator, oris</b> <i>m</i> [suffragor]\nголосующий в (чью-л.) пользу, сторонник <i>C, PJ</i>: s. quaesturae <i>Sen</i> голосующий за кандидатуру в квесторы || <i>перен.</i> приверженец <i>Pl, Vr, PJ</i>."],
  ["suffragatorius", "<big>suffragatorius</big>\n\n<b>suffragatorius, a, um</b> [suffragor]\nприуроченный к выборам, связанный с выборами: amicitia suffragatoria <i>Quintus C</i> дружба на время выборов."],
  ["suffragatrix", "<big>suffragatrix</big>\n\n<b>suffragatrix, icis</b> <i>f</i>\nпокровительница <i>или</i> сторонница <i>Aug</i>."],
  ["suffraginosus", "<big>suffraginosus</big>\n\n<b>suffraginosus, a, um</b> [suffrago I]\nстрадающий опухолями суставов (mula <i>Col</i>)."],
  ["suffragium", "<big>suffragium</big>\n\n<b>suffragium, i</b> <i>n</i> [suffragor]\n<b>1)</b> избирательный голос: s. ferre <i>C, Nep</i> подавать голос; cunctis suffragiis <i>C</i> единогласно;\n<b>2)</b> (<i>тж.</i> <i>pl.</i>) подача голосов, голосование (populi <i>H</i>): jus suffragi! <i>C etc.</i> право голоса, избирательное право; s. inire <i>или</i> in s. ire <i>L</i> приступить к голосованию; in s. revocari <i>L</i> ещё раз голосовать; tacita suffragia <i>PJ</i> тайное голосование; testularum <i>и</i> testarum s. <i>Nep</i> голосование черепками (остракизм);\n<b>3)</b> избирательное право (s. novorum civium <i>C</i>): civitas sine suffragio <i>L</i> права гражданства без избирательного права;\n<b>4)</b> всадническая центурия (с правом голосования): sex suffragia <i>C</i> три двойных древнейших всаднических центурии (патрицианских), имевших шесть голосов (Tities, Ramnes, Luceres);\n<b>5)</b> суждение, решение: suffragio tuo <i>C</i> по твоему мнению;\n<b>6)</b> благоприятное мнение, одобрение (vento sae plebis suffragia <i>H</i>)."],
  ["suffrago", "<big>suffrago</big>\n\n<b>I suffrago, inis</b> <i>f</i>\n<b>1)</b> ляжка, лодыжка (<i>у животного</i>) <i>Col, PM</i>;\n<b>2)</b> отпрыск, побег (<i>виноградной лозы</i>) <i>Col</i>.\n\n<b>II suffrago, —, —, are</b>\nсовершаться, делаться (s. et prosperare <i>Vlg</i>)."],
  ["suffragor", "<big>suffragor</big>\n\n<b>suffragor, atus sum, ari</b> <i>depon.</i>\n<b>1)</b> подавать голос, голосовать: s. alicui <i>C</i> голосовать за кого-л.; s. ad aliquid <i>C</i> подавать голос в пользу чего-л.;\n<b>2)</b> поддерживать, благоприятствовать, оказывать поддержку (alicui <i>и</i> alicui rei): s. legi <i>C</i> говорить в пользу закона (законопроекта); suffragante aliquo <i>C, Nep</i> при чьей-л. поддержке (по чьей-л. рекомендации); si memoria suffragatur <i>Q</i> если память позволяет (в состоянии удержать)."],
  ["suffrenatio", "<big>suffrenatio</big>\n\n<b>suf–frenatio, onis</b> <i>f</i> [freno]\nвзнуздывание, <i>перен.</i> укрепление (lapis multa suffrenatione devinctus <i>PM</i>)."],
  ["suffrendens", "<big>suffrendens</big>\n\n<b>suf–frendens, entis</b> [frendo]\nнемного взбешённый <i>Amm</i>."],
  ["suffrico", "<big>suffrico</big>\n\n<b>suf–frico, —, —, are</b>\nслегка тереть (vasa <i>Col</i>) <i>или</i> растирать (palpebras medicamentis <i>CC</i>)."],
  ["suffrigide", "<big>suffrigide</big>\n\n<b>suffrigide</b>\nдовольно холодно, <i>перен.</i> несколько вяло <i>AG</i>."],
  ["suffrigidus", "<big>suffrigidus</big>\n\n<b>suf–frigidus, a, um</b>\nпрохладный, <i>перен.</i> слабоватый (argumentum <i>Amm</i>)."],
  ["suffringo", "<big>suffringo</big>\n\n<b>suf–fringo, fregi, fractum, ere</b> [frango]\nподламывать, переламывать (crura alicui <i>C</i>)."],
  ["suffudi", "<big>suffudi</big>\n\n<b>suffudi</b> <i>pf. к</i>\nsuffundo."],
  ["suffugio", "<big>suffugio</big>\n\n<b>suf–fugio, fugi, —, ere</b>\nубегать, скрываться (in tecta <i>L</i>); уклоняться, избегать: manuum tactum et ictum s. <i>Lcr</i> быть недоступным осязанию; s. sensum <i>Lcr</i> быть неощутимым; s. aliquem <i>Su</i> отшатнуться (отстраниться) от кого-л."],
  ["suffugium", "<big>suffugium</big>\n\n<b>suffugium, i</b> <i>n</i> [suffugio]\n<b>1)</b> убежище, укрытие: s. alicujus rei (aut imbris aut hiemis <i>PJ</i>), <i>реже</i> alicui rei (hiemi <i>T</i>) <i>и</i> adversus aliquid (adversus caeli rigorem <i>Sen</i>) убежище от чего-л.; suffugia Garamantum <i>T</i> места убежища у гарамантов;\n<b>2)</b> средство против (чего-л.) (urgentium malorum <i>T</i>)."],
  ["suffulcio", "<big>suffulcio</big>\n\n<b>suf–fulcio, fulsi, fultum, ire</b> [fulcio]\nподпирать, поддерживать: porticus suffulta columnis <i>Lcr</i> портик, подпираемый колоннами; lectica Syris suffulta <i>M</i> носилки, несомые сирийцами; s. artus <i>Lcr</i> поддерживать (питать) тело."],
  ["suffultus", "<big>suffultus</big>\n\n<b>suffultus, a, um</b> <i>part. pf. к</i>\nsuffulcio."],
  ["suffumigatio", "<big>suffumigatio</big>\n\n<b>suf–fumigatio, onis</b> <i>f</i>\nокуривание (curare suffumigalionibus <i>Veg</i>)."],
  ["suffumigo", "<big>suffumigo</big>\n\n<b>suf–fumigo, —, —, are</b>\n(снизу) окуривать (dolia cera <i>Col</i>; aliquid sulpure <i>CC</i>)."],
  ["suffumo", "<big>suffumo</big>\n\n<b>suf–fumo, —, —, are</b> <i>перен.</i>\nпокуривать фимиам, немного кадить (in epistulis <i>Hier</i>)."],
  ["suffundo", "<big>suffundo</big>\n\n<b>suf–fundo, fudi, fusum, ere</b>\n<b>1)</b> лить вниз, сливать (aquam frigidam <i>Pl</i>); разливать, наливать (merum <i>O</i>); <i>med.-pass.</i> литься снизу, разливаться: sanguis cordi suffusus <i>C</i> кровь, приливающая (снизу) к сердцу; rubor (pallor) suffunditur alicui <i>Sen</i> краснота (бледность) разливается по чьему-л. лицу; suffusa unda <i>O</i> водянка;\n<b>2)</b> заливать, мочить: lumina tepido rore (= lacrimis) s. <i>O</i> орошать глаза теплой влагой (слёз); suffusi cruore oculi <i>PM</i> налитые кровью глаза; s. caelum caligine <i>Lcr</i> покрывать небо мраком; calore suffusus aether <i>C</i> эфир, проникнутый теплом; lacrimis oculos (<i>acc. gr.</i>) suffusus <i>V</i> с глазами, полными слёз; felle suffusus <i>O</i> пропитанный (напоённый) жёлчью, <i>но тж.</i> <i>PM</i> страдающий разлитием жёлчи; suffundi pudore <i>PJ</i> <i>или</i> suffundi <i>Tert</i> краснеть от стыда; animus nulla malevolentia suffusus <i>C</i> характер, лишённый какой бы то ни было недоброжелательности."],
  ["suffuror", "<big>suffuror</big>\n\n<b>suf–furor, —, ari</b> <i>depon.</i>\nпотихоньку красть, украдкой уносить <i>Pl</i>."],
  ["suffusculus", "<big>suffusculus</big>\n\n<b>suffusculus, a, um</b> [<i>demin. к</i> suffuscus]\nчуть-чуть смуглый <i>Ap, Amm</i>."],
  ["suffuscus", "<big>suffuscus</big>\n\n<b>suf–fuscus, a, um</b>\nсмугловатый, темноватый, с коричневым оттенком (margarita <i>T</i>)."],
  ["suffusio", "<big>suffusio</big>\n\n<b>suffusio, onis</b> <i>f</i> [suffundo]\n<b>1)</b> разлитие: s. fellis <i>PM</i> разлитие жёлчи, желтуха; s. (oculi) <i>CC, Scr, PM, Sen</i> помутнение хрусталика, катаракта; s. (pedum) <i>Veg</i> отёк ног (<i>у животных</i>);\n<b>2)</b> настой (agrestis cucumeris <i>Pall</i>)."],
  ["suffusorium", "<big>suffusorium</big>\n\n<b>suffusorium, i</b> <i>n</i> [suffundo]\nковшик <i>Hier</i>."],
  ["suffusus", "<big>suffusus</big>\n\n<b>suffusus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> suffundo;\n<b>2.</b> <i>adj.</i> стыдливый, застенчивый <i>Tert</i>."],
  ["sug-", "<big>sug-</big>\n\n<b>sug-</b>\n<i>приставка</i> = sub- <i>перед начальным</i> g <i>основного слова.</i>"],
  ["Sugambri", "<big>Sugambri</big>\n\n<b>Sugambri, orum</b> <i>m</i> <i>v. l.</i>\n= Sigambri."],
  ["suggero", "<big>suggero</big>\n\n<b>sug–gero, gessi, gestum, ere</b>\n<b>1)</b> наносить, нагромождать (humum <i>Prp</i>);\n<b>2)</b> подносить, подкладывать, подбрасывать: flamma suggeritur costis aeni <i>V</i> огонь разводится под стенками котла; invidiae flammam s. <i>L</i> разжигать ненависть;\n<b>3)</b> воздвигать, строить (theatra celsis columnis <i>Sil</i>);\n<b>4)</b> приносить, давать (tela alicui <i>V</i>); доставлять, давать (tellus alimenta suggerit <i>O</i>): cibum animalibus s. <i>T</i> кормить животных; sumptum alicui rei s. <i>Ter</i> покрывать расходы на что-л.; alicui ludum s. <i>C</i> сыграть с кем-л. шутку;\n<b>5)</b> внушать, советовать (questus <i>QC</i>): aliquo suggerente <i>AV</i> по чьему-л. внушению (наущению);\n<b>6)</b> добавлять, вставлять (verba quae desunt <i>C</i>); присоединять (ratiunculas sententiae <i>C</i>): Bruto statim Horatium Pulvillum s. <i>L</i> ставить Горация Пульвилла (хронологически) непосредственно за Брутом."],
  ["suggestio", "<big>suggestio</big>\n\n<b>suggestio, onis</b> <i>f</i> [suggero]\n<b>1)</b> добавление, прибавка (euggestione augeri <i>CA</i>);\n<b>2)</b> доклад с предложением <i>Dig, CJ</i>;\n<b>3)</b> внушение, намёк <i>Vop</i>;\n<b>4)</b> <i>ритор.</i> добавление, указание <i>Q</i>."],
  ["suggestum", "<big>suggestum</big>\n\n<b>suggestum, i</b> <i>n</i> [suggero]\n<b>1)</b> насыпь, возвышение <i>Vr</i>;\n<b>2)</b> помост, эстрада, трибуна (in s. ascendere <i>C</i>)."],
  ["suggestus", "<big>suggestus</big>\n\n<b>I suggestus, us</b> <i>m</i> [suggero]\n<b>1)</b> насыпь <i>или</i> кладка (s. lapideus <i>Col</i>); возвышенное место, возвышение (in suggestu sedere <i>Su</i>): s. comae <i>St</i> высокая причёска;\n<b>2)</b> ораторская трибуна: aliquid pro suggestu pronuntiare <i>Cs</i> высказать что-л. с трибуны || <i>воен.</i> лагерная трибуна (<i>перед преторием, с</i> sella castrensis <i>военачальника</i>) <i>Cs, bAfr</i>;\n<b>3)</b> высота: se tanto fortunarum suggestu dejicere <i>Ap</i> пасть с высоты столь великого счастья;\n<b>4)</b> учреждение, устройство (circensium <i>Tert</i>);\n<b>5)</b> поданная мысль, предложение, проект <i>Dig, CJ</i>.\n\n<b>II suggestus, a, um</b> <i>part. pf. к</i>\nsuggero."],
  ["suggill-", "<big>suggill-</big>\n\n<b>suggill-</b> <i>v. l.</i>\n= sugill-."],
  ["sugglutio", "<big>sugglutio</big>\n\n<b>sug–glutio, —, —, ire</b>\nикать <i>Veg</i>."],
  ["suggrandis", "<big>suggrandis</big>\n\n<b>sug–grandis, e</b>\nдовольно большой, просторный (cubiculum <i>C</i>)."],
  ["suggredior", "<big>suggredior</big>\n\n<b>sug–gredior, gressus sum, gredi</b> <i>depon.</i> [gradior]\nподходить, приближаться (propius <i>T</i>); нападать, атаковать (aliquem acie <i>T</i>)."],
  ["suggrunda", "<big>suggrunda</big>\n\n<b>sug–grunda, ae</b> <i>f</i> [grunda «кровельный жёлоб»]\nкровельный навес <i>Vr, PM, Vtr, Dig</i>."],
  ["suggrundatio", "<big>suggrundatio</big>\n\n<b>suggrundatio, onis</b> <i>f Vtr</i> <i>и</i> <b>suggrundium, i</b> <i>n</i> <i>Vtr, Dig</i>\n= suggrunda."],
  ["sugillatio", "<big>sugillatio</big>\n\n<b>sugillatio (suggillatio), onis</b> <i>f</i> [sugillo]\n<b>1)</b> синяк (<i>от удара, ушиба</i>) <i>PM</i>;\n<b>2)</b> насмешка, колкость, оскорбление (alicujus <i>L, PM</i>): s. admonitionis <i>Sen</i> язвительный укор."],
  ["sugillatus", "<big>sugillatus</big>\n\n<b>sugillatus, us</b> <i>m</i> <i>Tert</i>\n= sugillatio 1."],
  ["sugillo", "<big>sugillo</big>\n\n<b>sugillo, avi, atum, are</b>\n<b>1)</b> избивать до синяков (athletam <i>Sen</i>); подбивать (oculos alicui <i>Vr</i>): sugillata (<i>sc.</i> loca) <i>PM</i> синяки, кровоподтёки;\n<b>2)</b> издеваться, оскорблять (viros <i>L</i>; miserias <i>Pt</i>);\n<b>3)</b> подсказывать, внушать (verba alicui <i>Eccl</i>)."],
  ["sugo", "<big>sugo</big>\n\n<b>sugo, suxi, suctum, ere</b>\nсосать (matris mammam <i>Vr</i>); <i>перен.</i> всасывать (errorem cum lacte nutricis <i>C</i>)."],
  ["sui", "<big>sui</big>\n\n<b>I sui, orum</b> <i>m</i> [suus]\nсвои (<i>т. е.</i> родственники, близкие; друзья, спутники, <i>тж.</i> сограждане, приверженцы, солдаты <i>и т. п.</i>) (non solum alieni, sed etiam sui <i>C</i>): multos reddere suos <i>Nep</i> сделать многих своими сторонниками; Caesar suos a proelio continebat <i>Cs</i> Цезарь удерживал своих солдат от сражения.\n\n<b>II sui</b> <i>gen. sg. и pl.</i> (<i>nom.</i> <i>нет</i>, <i>dat.</i> sibi, <i>acc. и abl.</i> se <i>и</i> sese)\nсебя: sui conservandi causa <i>C</i> для собственного спасения; se inflexere <i>Cs</i> сгибаться, гнуться; noscere se ipsum <i>C</i> познать самого себя; <i>в dat. ethicus</i> quid sibi vult? <i>L etc.</i> что (это) значит? <i>или</i> что ему, собственно, угодно?; a Caesare invitor, sibi ut sim legatus <i>C</i> Цезарь приглашает меня стать его легатом; inter se <i>C etc.</i> друг друга, между собой; esse apud sese <i>Ter</i> быть в своём уме.\n\n<b>III sui</b> <i>dat. sg. к</i>\nsus."],
  ["suile", "<big>suile</big>\n\n<b>suile, is</b> <i>n</i> [sus]\nсвиной хлев <i>Vr, Col</i>."],
  ["suilla", "<big>suilla</big>\n\n<b>suilla, ae</b> <i>f</i> [suillus]\n(<i>sc.</i> caro) свинина <i>CC, PM</i>."],
  ["suillinus", "<big>suillinus</big>\n\n<b>suillinus</b> <i>GT</i>\n= suillus."],
  ["suillus", "<big>suillus</big>\n\n<b>suillus, a, um</b> [sus]\nсвиной (caro <i>Vr</i>): s. grex <i>L</i> стадо свиней; suilli fungi <i>M</i> свиные грибы, <i>предпол.</i> свинушки."],
  ["Suiones", "<big>Suiones</big>\n\n<b>Suiones, um</b> <i>m</i>\nсвионы, <i>общее наименование древнейших обитателей Скандинавии</i> (<i>особенно нын. Швеции</i>) <i>T</i>."],
  ["suis", "<big>suis</big>\n\n<b>I suis, is</b> <i>m</i> <i>Vr, Eccl</i>\n= sus.\n\n<b>II suis</b> <i>gen. sg. к</i>\nsus.\n\n<b>III suis</b> <i>dat. abl. pl. к</i>\nsuus."],
  ["sulcabilis", "<big>sulcabilis</big>\n\n<b>sulcabilis, e</b> [sulco]\nпахотный (terra <i>Ambr</i>)."],
  ["sulcamen", "<big>sulcamen</big>\n\n<b>sulcamen, inis</b> <i>n</i> [sulco]\nборозда (glebae Siculae sulcamina <i>Ap</i>)."],
  ["sulcator", "<big>sulcator</big>\n\n<b>sulcator, oris</b> <i>m</i> [sulco]\n<b>1)</b> бороздящий, <i>т. е.</i> пахарь (campi <i>Eccl</i>); <i>перен.</i> проплывающий (flumen siccae s. arenae <i>Lcn</i>): s. ponti <i>Sil</i> = navita;\n<b>2)</b> разрывающий, терзающий (lateris — <i>sc.</i> Tityi — s. vultur <i>Cld</i>)."],
  ["Sulci", "<big>Sulci</big>\n\n<b>Sulci, orum</b> <i>m</i>\nСулки, <i>портовый город на юго-зап. побережье Сардинии</i> <i>Mela, Cld etc.</i>"],
  ["Sulcitani", "<big>Sulcitani</big>\n\n<b>Sulcitani, orum</b> <i>m</i>\nжители города Sulci <i>bAfr, PM</i>."],
  ["sulco", "<big>sulco</big>\n\n<b>sulco, avi, atum, are</b> [sulcus]\n<b>1)</b> бороздить, взрыхлять (humum vomere <i>O</i>); рыть, вскапывать (fossam <i>Vr</i>; alveum <i>PM</i>); (<i>о ползающих змеях</i>) бороздить (arenam <i>O</i>) <i>или</i> прокладывать (iter cauda <i>Lcn</i>);\n<b>2)</b> проплывать (maria <i>PM</i>);\n<b>3)</b> морщить, покрывать морщинами, бороздить (cutem rugis <i>O</i>)."],
  ["sulcus", "<big>sulcus</big>\n\n<b>sulcus, i</b> <i>m</i>\n<b>1)</b> борозда: sulcum imprimere <i>C</i> (ducere <i>Col, PM</i>) проводить борозду; sulcis committere (mandare) <i>V</i> бросать в борозды, <i>т. е.</i> сеять;\n<b>2)</b> пахота: altero sulco seminare <i>Col</i> сеять по вторичной вспашке;\n<b>3)</b> ров, ямка, щель <i>V</i>;\n<b>4)</b> складка, морщина (uteri <i>M</i>; genarum <i>Cld</i>);\n<b>5)</b> рубец, рана (rubent in pectore sulci <i>Cld</i>);\n<b>6)</b> колея (<i>sc.</i> rotarum <i>Cld</i>);\n<b>7)</b> изгиб (viperae <i>Ap</i>)."],
  ["sulf-", "<big>sulf-</big>\n\n<b>sulf-</b> <i>v. l.</i>\n= sulp-."],
  ["Sulla", "<big>Sulla</big>\n\n<b>Sulla, ae</b> <i>m</i>\nСулла, <i>cognomen в роде Корнелиев; наиболее известны</i>:\n<b>1)</b> L. Cornelius S. (Felix), <i>жил с 138 по 78 гг. до н. э.; как глава аристократической партии в 86 г. до н. э. был объявлен вне закона, но в 82 г. до н. э. захватил диктатуру и удерживал её до 79 г. до н. э.</i> <i>C</i>;\n<b>2)</b> Faustus Cornelius S., <i>сын предыдущего, сторонник Помпея, пал в 46 г. до н. э. в Африке</i> <i>C</i>;\n<b>3)</b> P. Cornelius S., <i>племянник Л. Суллы, сторонник Цезаря; его защищал в 62 г. до н. э. Цицерон от обвинения в причастности к заговору Катилины</i> <i>C</i>."],
  ["Sullanus", "<big>Sullanus</big>\n\n<b>I Sullanus, a, um</b> [Sulla]\nсулланский (crudelitas <i>C</i>; bellum <i>PM</i>).\n\n<b>II Sullanus, i</b> <i>m</i>\nсторонник Л. Суллы <i>C</i>."],
  ["sullaturio", "<big>sullaturio</big>\n\n<b>sullaturio, —, —, ire</b> [<i>ирон. desiderat. к</i> Sulla]\nстремиться подражать Л. Сулле, корчить из себя Суллу <i>C</i>."],
  ["Sulmo", "<big>Sulmo</big>\n\n<b>Sulmo, onis</b> <i>m</i>\nСульмон:\n<b>1)</b> <i>город пелигнов в области сабинян, место рождения Овидия</i> (<i>ныне</i> Sulmona) <i>C, O, L</i>;\n<b>2)</b> <i>город вольсков в Латии</i> <i>PM</i>;\n<b>3)</b> <i>мужское имя</i> <i>V</i>."],
  ["Sulmonensis", "<big>Sulmonensis</big>\n\n<b>I Sulmonensis, e</b> [Sulmo]\nсульмонский (ager <i>PM</i>).\n\n<b>II Sulmonensis, is</b> <i>m</i>\nжитель города Sulmo <i>Cs, PM</i>."],
  ["sulp-", "<big>sulp-</big>\n\n<b>sulp-</b> <i>v. l.</i>\n= sulph-."],
  ["sulphur", "<big>sulphur</big>\n\n<b>sulphur, uris</b> <i>n</i> (<i>m</i> <i>у</i> <i>Veg и Tert</i>)\ncepa <i>Cato, Lcr etc.</i>: s. vivum, <i>тж.</i> <i>pl. L, V</i> самородная сера; s. aethereum <i>Lcn</i> <i>или</i> s. sacrum <i>Pers</i> небесный огонь, молния || <i>pl.</i> куски серы, <i>тж.</i> сернистые испарения <i>или</i> сернистые ванны <i>V, O, Q</i>."],
  ["sulphurans", "<big>sulphurans</big>\n\n<b>sulphurans, antis</b>\nсернистый (aquae <i>Tert</i>)."],
  ["sulphuraria", "<big>sulphuraria</big>\n\n<b>sulphuraria, ae</b> <i>f</i> <i>Dig</i>\n= sulphuratio."],
  ["sulphuratio", "<big>sulphuratio</big>\n\n<b>sulphuratio, onis</b> <i>f</i> [sulphur]\nместорождение серы <i>Sen</i>."],
  ["sulphuratum", "<big>sulphuratum</big>\n\n<b>sulphuratum, i</b> <i>n</i> [sulphur]\n<b>1)</b> серная спичка, серник <i>M</i>;\n<b>2)</b> <i>PM</i> = sulphuratio."],
  ["sulphuratus", "<big>sulphuratus</big>\n\n<b>sulphuratus, a, um</b> [sulphur]\nпропитанный серой (ramentum <i>M</i>); сернистый (aqua <i>Sen, PM</i>)."],
  ["sulphureus", "<big>sulphureus</big>\n\n<b>sulphureus, a, um</b> [sulphur]\nсерный, сернистый (aqua <i>V</i>; odor <i>PM</i>; fornax <i>O</i>)."],
  ["sulphurosus", "<big>sulphurosus</big>\n\n<b>sulphurosus, a, um</b> <i>Vtr</i>\n= sulphureus."],
  ["Sulpicia", "<big>Sulpicia</big>\n\n<b>Sulpicia, ae</b> <i>f</i>\nСульпиция, <i>римск. поэтесса I в. н. э.</i>"],
  ["Sulpicianus", "<big>Sulpicianus</big>\n\n<b>Sulpicianus</b> <i>v. l.</i>\n= Sulpicius I."],
  ["Sulpicius", "<big>Sulpicius</big>\n\n<b>I Sulpicius, a, um</b> [Sulpicius II]\nсульпициев <i>C, Cs, Q, H</i>.\n\n<b>II Sulpicius, a, um</b>\nСульпиций, <i>римск. nomen; наиболее известны</i>:\n<b>1)</b> Servius S. Camerinus Cornutus, <i>консул в 500 г. до н. э., победитель латинян в 496 г. до н. э.</i> <i>L</i>;\n<b>2)</b> Servius S. Galba, <i>один из крупнейших ораторов своего времени</i> (<i>середина II в. до н. э.</i>) <i>C, Q, VM</i>;\n<b>3)</b> P. S. Rufus, <i>народный трибун в 88 г. до н. э., вначале сторонник, а затем противник Суллы, по приказу которого умерщвлён в 82 г. до н. э.</i> <i>C</i>;\n<b>4)</b> Servius S. Rufus, <i>оратор, правовед, друг Цицерона, претор в 65 г. до н. э., консул в 51 г. до н. э.</i> (<i>умер в 43 г. до н. э.</i>) <i>C</i>."],
  ["sultis", "<big>sultis</big>\n\n<b>sultis</b> <i>Pl</i>\n= si vultis."],
  ["sum", "<big>sum</big>\n\n<b>I sum, fuī, esse</b>\n<b>1.</b> <i>как самостоятельный глагол</i> (<i>verbum substantivum</i>)\n<b>1)</b> быть, существовать (pericla, quae non sunt <i>PS</i>): omnes qui sunt, qui fuerunt, qui futuri sunt <i>C</i> все (те), которые существуют, которые существовали, которые будут существовать;\n<b>2)</b> происходить, совершаться, приключаться, бывать, случаться: senatus hodie fuerat futurus <i>C</i> сенат должен был собраться (заседание сената должно было состояться) сегодня; solis defectio fuit <i>C</i> произошло солнечное затмение; quid tibi est? <i>C</i> что с тобой?;\n<b>3)</b> быть налицо, быть в наличии, иметься: flumen est Arar, quod in Rhodanum influit <i>Cs</i> есть река Арар, которая впадает в Родан; ut nunc quidem est <i>C</i> при нынешних, по крайней мере, обстоятельствах; sunt, qui, quod sentiunt, non audent dicere <i>C</i> есть (люди), которые (<i>т. е.</i> некоторые) не решаются высказывать то, что думают; <i>или с</i> <i>conjct.</i>: sunt verba et voces, quibus hunc lenire dolorem possis <i>H</i> существуют слова и звуки, посредством которых ты мог бы унять это страдание || <i>редко</i> est = sunt: est, quibus Eleae concurrit palma quadrigae <i>Prp</i> есть (такие), которые в Элее оспаривают первенство в состязании квадриг;\n<b>4)</b> быть верным, действительным: sunt ista <i>C</i> да, это так; est, ut dicis <i>C</i> ты правильно говоришь; (verum) esto <i>C</i> но, допустим;\n<b>5)</b> быть, находиться (Romae, ruri, in foro, apud exercitum, ante oculos <i>C</i>): mons Jura est inter Sequanos et Helvetios <i>Cs</i> гора Юра находится между областями секванов и гельветов; certum non habeo, ubi sis aut ubi futurus sis <i>C</i> я точно не знаю, ни где ты находишься, ни куда собираешься; apud aliquem esse <i>C</i> быть у кого-л. в гостях; in venationibus esse <i>Cs</i> заниматься охотой; totum in eo est (ut) <i>C</i> всё дело в том (что, чтобы); esse in armis <i>L</i> быть при оружии; in aere alieno esse <i>C</i> быть в долгу; in odio esse <i>C</i> быть предметом ненависти; ab aliquo esse <i>Ter etc.</i> происходить от кого-л. <i>или</i> <i>C</i> держать чью-л. сторону; pro aliquo esse <i>C</i> считаться кем-л.; melli esse <i>H</i> быть сладким как мёд; liber est de amicitia <i>C</i> книга говорит о дружбе;\n<b>6)</b> пребывать, <i>тж.</i> состоять в браке, находиться в связи (com aliqua <i>Ter</i>): est ubi... <i>C</i> бывает, случается, что...; est quod <i>T, C</i> <i>или</i> cur... <i>C</i> есть причина тому, что...;\n<b>7)</b> содержаться, быть написанным: in lege est <i>C</i> закон гласит; exemplum litterarum, in quo erat illas undecim esse legiones <i>C</i> копия письма, в котором было сказано, что этих легионов было одиннадцать; est apud Platonem <i>C</i> у Платона сказано;\n<b>8)</b> изредка являться, приходить: ad me bene mane Dionysius init <i>C</i> ко мне рано утром пришёл Дионисий;\n<b>9)</b> состоять: creticus est ex longa et brevi et longa <i>C</i> кретик (критский стих) состоит из (слогов) долгого, краткого и долгого;\n<b>10)</b> длиться, продолжаться (obsidio triginta dies fuit <i>L</i>);\n<b>11)</b> принадлежать, быть присущим, свойственным: ei morbo nomen est avaritia <i>C</i> имя этой болезни — алчность; alicujus militiae vacatio est <i>C</i> кто-л. свободен от военной службы; nihil est mihi cum eo <i>C</i> у меня ничего общего с ним нет; secum esse <i>C</i> жить для себя; cum eo mibi omnia sunt <i>C</i> мы с ним в прекрасных отношениях; terminus nullus falso est <i>Sen</i> у заблуждения нет предела;\n<b>12)</b> находиться в зависимости, зависеть: quantum in me est <i>C</i> насколько (это) зависит от меня; non est id in nobis <i>C</i> это зависит не от нас; non est in medico semper, relevetur ut aeger <i>O</i> не всегда во власти врача исцелить больного;\n<b>13)</b> обстоять: res ita est <i>Sl etc.</i> так обстоит дело; contra esse <i>C</i> противоречить;\n<b>14)</b> значить: sapienti vivere est cogitare <i>C</i> для мудреца жить значит мыслить;\n<b>15)</b> <i>с</i> <i>dat.</i> быть, служить, способствовать, приносить: aliquid alicui est laudi <i>Nep</i> что-л. кому-либо приносит славу; magno documento esse <i>C</i> являться важным доказательством; impedimento esse <i>C</i> быть препятствием; usui esse <i>Nep</i> служить на пользу; saluti esse <i>C, Cs</i> способствовать спасению, быть на благо; cui bono fuit? <i>C</i> кому это принесло пользу?;\n<b>16)</b> относиться, касаться: res mei consilii non est <i>C</i> это меня не касается;\n<b>17)</b> <i>с dat.</i> <i>gerund.</i> быть (при)годным, подходящим: oneri ferendo esse <i>L</i> быть в состоянии нести бремя; solvendo <i>C</i> (<i>или</i> aeri alieno <i>L</i>) esse быть платёжеспособным;\n<b>18)</b> <i>в pf.</i> больше не существовать, погибнуть: fuimus Troes, fuit Ilium <i>V</i> нет уж нас, троянцев, нет больше Илиона; sive erimus, seu nos fata fuisse volent <i>Tib</i> (<i>pl.</i> = <i>sg.</i>) буду ли я жив, или судьбе будет угодно, чтобы я погиб;\n<b>2.</b> <i>как глагол-связка</i> (<i>verbum copulativum</i>)\n<b>1)</b> быть, служить, являться, представлять собой: praeclara res est <i>C</i> это превосходно; nos numerus sumus <i>H</i> мы представляем собой массу (толпу); hoc (<i>или</i> id) est <i>C etc.</i> то есть; se Atticos volunt (esse) <i>C</i> они желают быть аттическими ораторами;\n<b>2)</b> <i>с gen. poss.</i> принадлежать, быть присущим, быть признаком, свойством (domus est patris <i>C</i>): temeritas est florentis aetatis <i>C</i> отвага — свойство цветущего возраста; cujusvis hominis est errare <i>C</i> всякому человеку свойственно заблуждаться; quid hoc sit hominis? <i>Pl</i> (<i>негодующе</i>) что это за человек такой?; nulli (= nullius) consilii esse <i>Ter</i> растеряться, быть в смущении;\n<b>3)</b> быть преданным: nominum, non causarum toti erant <i>L</i> (римляне в то время) были преданы личностям, а не делам (принципам); novarum rerum esse <i>L</i> быть сторонником переворота; suarum rerum esse <i>L</i> быть поглощённым своими (личными) интересами;\n<b>4)</b> надлежать, подобать, быть обязанностью: adulescentis est majores natu revereri <i>C</i> юноше надлежит почитать старших по возрасту; non est moris Graecorum <i>C</i> не в обычаях греков;\n<b>5)</b> <i>с gen. gerundiv.</i> служить, способствовать: conservandae libertatis esse <i>Sl</i> служить целям сохранения свободы; ea, quae diutinae obsidionis tolerandae erant <i>L</i> то, что помогало (могло помочь) выдержать длительную осаду;\n<b>6)</b> <i>с gen. и abl. qualit.</i> иметь, обладать: nullius animi esse <i>C</i> быть лишённым твёрдости (характера); res est magni laboris <i>C</i> это весьма трудно; tantae molis erat <i>V</i> вот каких усилий стоило (это); aegro corpore esse <i>C</i> быть больным; jumenta summi laboris <i>Cs</i> весьма выносливые лошади; tenuissima valetudine esse <i>Cs</i> обладать очень слабым здоровьем; esse humili statura <i>Nep</i> быть маленького роста; eodem jure esse <i>C</i> иметь одни и те же права;\n<b>7)</b> <i>в sg.</i> + <i>gen.</i> составлять, образовывать, состоять: classis est ducentarum navium <i>Nep</i> флот состоит из двенадцати кораблей; via erat decem dierum <i>Nep</i> дорога имела протяжение в десять дневных переходов;\n<b>8)</b> стоить: ager nunc multo pluris est, quam tunc fuit <i>C</i> (это) воле стоит теперь много дороже, чем стоило тогда; vestimenta, quae vix fuissent decem sestertiorum <i>Pt</i> носильные вещи, которые стоили никак не больше 10 сестерциев;\n<b>9)</b> быть важным, иметь значение: magni erant mihi tuae litterae <i>C</i> твоё письмо было для меня очень важно (дорого);\n<b>10)</b> <i>impers.</i> est возможно (est te videre? <i>Pt</i>) <i>или</i> бывает, случается: est, ubi damnum praestet facere, quam lucrum <i>Pl</i> бывает, что убыток приходится предпочесть прибыли. — <i>См. тж.</i> futurus.\n\n<b>II sum</b> <i>Enn</i>\n= eum.\n\n<b>III sum-</b>\n<i>приставка</i>= sub- <i>перед начальным</i> m <i>основного слова</i> (sum–ministro = sub–ministro)."],
  ["sumbol-", "<big>sumbol-</big>\n\n<b>sumbol-</b> <i>и</i> <b>sumbul-</b> <i>v. l.</i>\n= symbol-."],
  ["sumen", "<big>sumen</big>\n\n<b>sūmen, inis</b> <i>n</i> [<i>из</i> *sugmen <i>от</i> sugo]\n<b>1)</b> вымя, сосец (<i>преим.</i> свиньи) <i>LM, Pl, M etc.</i>;\n<b>2)</b> свинья <i>J</i>;\n<b>3)</b> <i>перен.</i> благодатный край, житница (Italiae <i>Vr, PM</i>)."],
  ["suminatus", "<big>suminatus</big>\n\n<b>sūminātus, a, um</b> [sumen]\nкормящий сосцами (<i>о животных</i>) <i>Eccl</i>: sus suminata <i>Lampr</i> свиноматка."],
  ["summa", "<big>summa</big>\n\n<b>summa, ae</b> <i>f</i> [summus]\n<b>1)</b> высшая должность, первое место: s. ordinis consiliique <i>C</i> первое место среди сословий и в управлении (страной); summam habere apud aliquos <i>Pl</i> быть первым среди кого-л.;\n<b>2)</b> высшая точка, вершина, верх, совершенство: s. meriti <i>O</i> высшая заслуга; s. carminis <i>Pt</i> вершина поэзии; summam alicui rei dare <i>Q</i> довести что-л. до совершенства;\n<b>3)</b> (главная) суть, сущность, основное: litterae, quarum s. erat... <i>T</i> письмо, суть которого заключалась в следующем...; summae rerum <i>C</i> основные моменты; s. exercitus (<i>или</i> belli) <i>Cs, L</i> главные силы (армии) (<i>см. тж.</i> 4); s. postulatorum <i>L</i> главные требования;\n<b>4)</b> сумма, итог: haec s. est <i>V</i> вот и всё || денежная сумма: de summa nil decedet <i>Ter</i> капитал ничуть не уменьшится || общее количество (pecuniae <i>L, QC</i>); совокупность, целое, общая масса (exercitus <i>Cs, Lcn</i>)-. vitae s. brevis <i>H</i> кратковременность жизни; summam facere (subducere) <i>C</i> подводить итог; s. <i>или</i> s. summarum общий итог (s. ergo summarum haec erit <i>Sen</i>), <i>но тж.</i> вселенная, мироздание (s. summarum est aeterna <i>Lcr</i>); s. belli <i>Cs, L</i> <i>или</i> imperii <i>Cs</i> главное командование; s. victoriae <i>Cs</i> окончательная (полная) победа; s. rerum <i>Cs, L, T</i> общее положение вещей, <i>преим.</i> высшее управление государственными делами <i>C</i>, <i>иногда</i> <i>L</i> общественное благо, <i>тж.</i> <i>O</i> вселенная; ad summam <i>Sen, Ap</i> <i>или</i> in (omni) summa <i>C, L etc.</i> в итоге, в общем, в конце концов, <i>тж.</i> <i>Lcr, C, Just</i> (одним) словом, короче говоря."],
  ["summalis", "<big>summalis</big>\n\n<b>summalis, e</b> [summa]\nитоговый, совокупный <i>Tert</i>."],
  ["summano", "<big>summano</big>\n\n<b>sum–mano, —, —, are</b>\nтечь под, протекать внизу (sub terra <i>Vtr</i>); <i>шутл.</i> мочить: quia vestimenta summano..., ob eam rem me Summanum vocant <i>Pl</i> так как я мочу одежду,... то меня и зовут Сумманом (<i>см.</i> Summanus)."],
  ["Summanus", "<big>Summanus</big>\n\n<b>Summanus, i</b> <i>m</i>\nСумман, <i>римск. божество ночных зарниц и ночной росы</i> (<i>см. под</i> summano <i>игру слов</i> summano <i>и</i> S.) <i>Pl, C, O, PM</i>."],
  ["summarium", "<big>summarium</big>\n\n<b>summārium, ī</b> <i>n</i> [summa]\nкраткое изложение <i>Sen</i>."],
  ["summarius", "<big>summarius</big>\n\n<b>summārius, ī</b> <i>m</i>\nвьючное животное <i>C</i>."],
  ["summas", "<big>summas</big>\n\n<b>summas, atis</b> (<i>gen. pl.</i> um <i>Pl</i>) <i>adj.</i>\nвысокородный, благородный, знатный (matrona <i>Pl</i>; virgo <i>Ap</i>): <i>pl.</i> summates <i>Amm</i> знать."],
  ["summatim", "<big>summatim</big>\n\n<b>summatim</b> [summa]\n<b>1)</b> поверхностно, сверху (radicem eradere <i>Col</i>);\n<b>2)</b> в общих чертах, вкратце (aliquid perscribere <i>C</i>); в основных чертах (cognoscere <i>C</i>);\n<b>3)</b> слегка, вскользь (aliquid attingere <i>Lcr, Q, Su</i>)."],
  ["summatus", "<big>summatus</big>\n\n<b>summatus, us</b> <i>m</i> [summa]\nверховенство, главенство, господство: imperium ac summatum petere <i>Lcr</i> стремиться к власти и господству."],
  ["summe", "<big>summe</big>\n\n<b>summe</b> [summus]\nв высшей степени, чрезвычайно, крайне (aliquem sollicitare <i>C</i>; jucundum <i>C</i>)."],
  ["summer-", "<big>summer-</big>\n\n<b>summer-</b> <i>v. l.</i>\n= submer-."],
  ["summin-", "<big>summin-</big>\n\n<b>summin-</b> <i>v. l.</i>\n= submin-."],
  ["summiss-", "<big>summiss-</big>\n\n<b>summiss-</b> <i>v. l.</i>\n= submiss-."],
  ["summitas", "<big>summitas</big>\n\n<b>summitas, atis</b> <i>f</i>\nвысота, верх, вершина, высшая точка <i>Pall, Ap, Cens, Macr etc.</i>"],
  ["summitto", "<big>summitto</big>\n\n<b>summitto</b> <i>v. l.</i>\n= submitto."],
  ["summo", "<big>summo</big>\n\n<b>summo</b> <i>adv.</i> [summus]\nв конечном счёте, в конце концов <i>Q</i>."],
  ["Summoenianus", "<big>Summoenianus</big>\n\n<b>Summoenianus, a, um</b> [Summoenium]\nсумменийский, <i>перен.</i> распутный (uxores <i>M</i>)."],
  ["Summoenium", "<big>Summoenium</big>\n\n<b>Summoenium, i</b> <i>n</i> [sub + moenia]\nСуммений, <i>окраинный квартал в Риме, изобиловавший домами разврата</i> <i>M</i>."],
  ["summolest-", "<big>summolest-</big>\n\n<b>summolest-</b> <i>v. l.</i>\n= submolest-."],
  ["summoneo", "<big>summoneo</big>\n\n<b>summoneo</b> <i>v. l.</i>\n= submoneo."],
  ["summopere", "<big>summopere</big>\n\n<b>summopere</b> [summo + opere]\nвсеми силами, всячески <i>C</i>."],
  ["summorosus", "<big>summorosus</big>\n\n<b>summorosus, a, um</b> <i>v. l.</i>\n= submorosus."],
  ["summotenus", "<big>summotenus</big>\n\n<b>summo–tenus</b> <i>adv.</i>\nдо самого верху (florescere <i>Ap</i>)."],
  ["summotus", "<big>summotus</big>\n\n<b>summotus, a, um</b> <i>part. pf. к</i>\nsummoveo."],
  ["summoveo", "<big>summoveo</big>\n\n<b>summoveo</b> <i>v. l.</i>\n= submoveo."],
  ["summula", "<big>summula</big>\n\n<b>summula, ae</b> <i>f</i> [<i>demin. к</i> summa]\nнебольшая сумма <i>Sen, Ap, Eccl</i>."],
  ["summum", "<big>summum</big>\n\n<b>I summum, i</b> <i>n</i> [summus]\n<i>преим. pl.</i>\n<b>1)</b> вершина (summa collium <i>T</i>); верхушка, гребень (tecti <i>V</i>);\n<b>2)</b> высшая точка, верх: summa malorum <i>L</i> верх несчастий; summa ducum <i>O</i> главный из военачальников; ad s. perducere <i>C</i> довести до совершенства;\n<b>3)</b> конец, окончание: in summo <i>Pt</i> в конце (концов), напоследок;\n<b>4)</b> конечность (alicui summa riguerunt <i>Sen</i>): summa pedum <i>CA</i> кончики пальцев ног.\n\n<b>II summum</b> <i>adv.</i>\nсамое большее, в крайнем случае: hodie aut s. cras <i>C</i> сегодня или, в крайнем случае, завтра; bis terve s. <i>C</i> два, максимум три."],
  ["summus", "<big>summus</big>\n\n<b>summus, a, um</b> [<i>superl. к</i> superus, <i>наряду с</i> supremus]\n<b>1)</b> высочайший, верхний (jugum montis <i>Cs</i>): s. mons <i>Cs</i> вершина горы; summa arbor <i>O</i> верхушка дерева; summum vestimentum <i>Pl</i> край платья; summa urbs <i>C</i> (<i>или</i> arx <i>O</i>) городской кремль; summum pectus <i>CA</i> верхняя часть груди: de summo pectore dicere <i>AG</i> — <i>см.</i> pectus; summi digiti <i>Q</i> кончики пальцев; summo profundo <i>O</i> на поверхности моря; summum corpus <i>PM</i> поверхность тела, кожа; summi et infimi <i>C</i> (общественные) верхи и низы; summa voce <i>Pl, H</i> самым высоким голосом;\n<b>2)</b> высший, предельный (summum jus summa injuria, <i>sc.</i> est <i>C</i>): summo studio <i>C</i> с величайшим усердием; omnia summa facere <i>C</i> сделать всё возможное || крайний, последний: summa senectus <i>C</i> глубокая старость; summum silentium <i>C</i> глубокая тишина; summa paupertas <i>Nep</i> крайняя бедность; hiems summa <i>C</i> зима в разгаре; summo jure <i>C</i> по всей строгости закона; summa dies <i>V, O</i> последний день (жизни), кончина; summi laboris esse <i>Cs</i> быть весьма работоспособным; summo carmine <i>H</i> в заключительном стихотворении;\n<b>3)</b> главный, лучший, важнейший: summi Peripatetici <i>C</i> славнейшие из перипатетиков; summi hominum <i>Lcn</i> лучшие из людей, цвет человечества; summo loco natus <i>Cs</i> весьма знатного рода; summa cena <i>Pt</i> <i>или</i> mensa <i>M</i> главное блюдо (трапезы); s. sacerdos <i>Vlg</i> первосвященник; summum proelium <i>Fl</i> генеральное сражение; s. vir et imperator <i>C</i> выдающийся человек и полководец; s. amicus <i>Ter</i> лучший друг; summum bonum <i>C</i> высшее благо; de summis rebus consilium capere <i>Cs</i> принимать решения по важнейшим вопросам; summam manum imponere alicui rei <i>Q</i> довершить что-л.; s. severitatis et munificentiae <i>T</i> обладающий неограниченным правом казнить и миловать; summum argumentum <i>Ap</i> решающий (неопровержимый) довод;\n<b>4)</b> (все)общий, совокупный: summa res publica <i>C</i> совокупность государственных интересов; summa salus rei publicae <i>C</i> общая безопасность государства; summae copiae <i>T</i> совокупность (вооружённых) сил;\n<b>5)</b> критический, опасный (summo rei publicae tempore <i>C</i>);\n<b>6)</b> худший, ужасный (turpitudo <i>C</i>); тягчайший (scelus <i>Sl</i>). — <i>См. тж.</i> summum."],
  ["summussus", "<big>summussus</big>\n\n<b>summussus</b> <i>v. l.</i>\n= submussus."],
  ["summuto", "<big>summuto</big>\n\n<b>summuto</b> <i>v. l.</i>\n= submuto."],
  ["sumo", "<big>sumo</big>\n\n<b>sumo, sumpsi, sumptum, ere</b> [sub + emo]\n<b>1)</b> брать (librum in manus <i>C</i>; arma <i>Sl</i>): grata s. manu <i>H</i> принимать с благодарностью || хватать, схватывать (ferrum ad aliquem interficiendum <i>L</i>): sumpsisse probra, deinde saxa, postremo ferrum <i>T</i> пустить в ход сначала ругательства, затем камни и, наконец, оружие; s. aliquem generum <i>T</i> сделать кого-л. своим зятем || брать в свои руки, захватывать (proelio sumpta Thessalia <i>Fl</i>);\n<b>2)</b> собирать, убирать (frumentum ex agris <i>Cs</i>);\n<b>3)</b> брать взаймы, занимать (pecuniam ab aliquo <i>Ter, C etc.</i>);\n<b>4)</b> принимать (cognomen <i>C</i>); s. vultus acerbos <i>O</i> принять суровый вид; s. animum ex eventu <i>T</i> принимать решение в зависимости от исхода (дела); s. sibi imperatorias partes <i>Cs</i> присвоить себе роль полководца; s. sibi arrogantiam <i>Cs</i> обнаглеть; s. animum <i>T, O</i> (при)ободриться; s. animum censoris <i>H</i> принять на себя роль критика; s. superbiam <i>H</i> проникнуться гордостью; s. vires <i>H</i> усиливаться; supplicium de (<i>или</i> ex) aliquo s. <i>Cs, L</i> казнить кого-л.; poenam s. <i>C</i> карать, мстить; s. gaudia <i>O, Calp</i> наслаждаться радостями; s. laudem <i>O</i> стяжать славу; sponte mortem sumpsisse <i>T</i> окончить жизнь самоубийством;\n<b>5)</b> принимать внутрь (venenum <i>Nep</i>); есть (panem perfusum aqua frigida <i>Su</i>; cibum <i>Nep, Macr</i>); выпивать (cyathos <i>H</i>; aquam <i>M</i>);\n<b>6)</b> надевать (vestimenta <i>C</i>; togam virilem <i>C, Sen etc.</i>; stolam <i>Pt</i>; pallium <i>C</i>): verbo subito sumpta est sententia <i>Ap</i> мысль облечена в импровизированные слова;\n<b>7)</b> усыновлять (liberos <i>Sl</i>);\n<b>8)</b> приводить, упоминать (homines notos <i>C</i>): s. aliquid argumenti loco <i>C</i> привести что-л. в качестве доказательства;\n<b>9)</b> утверждать (aliquid pro certo <i>C</i>);\n<b>10)</b> выбирать, избирать (sibi philosophiae studium <i>C</i>; aliquem sibi imperatorem <i>Nep</i>);\n<b>11)</b> устанавливать, назначать (colloquendi tempus <i>Nep</i>): dictaturae sumebantur ad tempus <i>T</i> диктатуры устанавливались на определённый срок;\n<b>12)</b> покупать, приобретать (obsonia parvo <i>H</i>);\n<b>13)</b> нанимать (navem in aliquem locum <i>C</i>);\n<b>14)</b> тратить, расходовать (laborem frustra <i>Cs</i>): curis sumptus <i>Poeta ap. C</i> измученный заботами;\n<b>15)</b> предпринимать, начинать (operam <i>C</i>; bellum <i>Sl</i>); s. aliquem celebrare <i>H</i> взяться прославить кого-л.; sumptis inimicitiis <i>C</i> с установлением враждебных отношений; mihi non sumo tantum (ut) <i>C</i> я не претендую на то (чтобы); sumpsi hoc mihi, ut ad te scriberem <i>C</i> я решил написать тебе;\n<b>16)</b> полагать, принимать (за основу) (aliquid ad concludendum <i>C</i>)."],
  ["sumpse", "<big>sumpse</big>\n\n<b>sumpse</b> <i>Naev</i>\n= sumpsisse (<i>inf. pf. к</i> sumo)."],
  ["sumpsi", "<big>sumpsi</big>\n\n<b>sumpsi</b> <i>pf. к</i>\nsumo."],
  ["sumpsio", "<big>sumpsio</big>\n\n<b>sumpsio, onis</b> <i>f</i> <i>Cato</i>\n= sumptio."],
  ["sumptifacio", "<big>sumptifacio</big>\n\n<b>sumpti–facio, feci, factum, ere</b>\nтратить, расходовать (aliquid <i>Pl</i>)."],
  ["sumptio", "<big>sumptio</big>\n\n<b>sumptio, onis</b> <i>f</i> [sumo]\n<b>1)</b> взятие, принятие <i>Cato, Vtr</i>;\n<b>2)</b> <i>лог.</i> (пред)посылка <i>C</i>."],
  ["sumptito", "<big>sumptito</big>\n\n<b>sumptito, avi, —, are</b> [<i>frequ. к</i> sumo]\nчасто <i>или</i> в усиленной дозе принимать (<i>sc.</i> medicamentum <i>PM</i>)."],
  ["sumptuarius", "<big>sumptuarius</big>\n\n<b>sumptuarius, a, um</b> [sumptus I]\nрасходный, касающийся расходов: rationes sumptuariae <i>C</i> счёт расходов; lex sumptuaria <i>C, Su, AG</i> закон о расходах на роскошь."],
  ["sumptuose", "<big>sumptuose</big>\n\n<b>sumptuose</b> [sumptuosus I]\nс большими издержками, дорого, роскошно (convivia facere <i>Ctl</i>; aliquid emere <i>PM</i>)."],
  ["sumptuositas", "<big>sumptuositas</big>\n\n<b>sumptuositas, atis</b> <i>f</i>\nрасточительность <i>Sid</i>."],
  ["sumptuosus", "<big>sumptuosus</big>\n\n<b>I sumptuosus, a, um</b> [sumptus I]\n<b>1)</b> сопряжённый с большими расходами, дорогостоящий (cena <i>C, Sen</i>; bellum <i>L</i>): portus operis sumptuosissimi <i>Su</i> порт, обошедшийся чрезвычайно дорого;\n<b>2)</b> много тратящий, расточительный <i>Pl, C, Sen etc.</i>\n\n<b>II sumptuosus, i</b> <i>m</i>\nрасточитель <i>QC</i>."],
  ["sumptus", "<big>sumptus</big>\n\n<b>I sumptus, a, um</b> <i>part. pf. к</i>\nsumo.\n\n<b>II sumptus, us</b> (<i>dat.</i> ui <i>и</i> u; <i>gen. арх.</i> i <i>Pt</i>) <i>m</i>\nрасход, трата: suo sumptu alere <i>Cs</i> содержать на свой счёт; sumptu publico <i>C</i> на общественный счёт; sumptu (= sumptui) parcere <i>C</i> быть экономным в издержках; aliquid nemini ne minimo quidem fuit sumptui <i>C</i> что-л. никому ни во что не обошлось; sumptum (<i>или</i> sumptus) facere (insumere, impendere) in aliquid <i>или</i> alicujus rei <i>C</i> нести расходы по чему-л.; sumptum suum exercere <i>Ter</i> оправдывать расходы по своему содержанию."],
  ["Sunion", "<big>Sunion</big>\n\n<b>Sunion</b> <i>и</i> <b>Sunium, i</b> <i>n</i>\nСуний, <i>юго-вост. мыс Аттики</i> (<i>ныне</i> Capo Colonni) <i>Ter, C, L</i>."],
  ["sunto", "<big>sunto</big>\n\n<b>sunto</b> <i>imper. fut. pl. к</i>\nsum."],
  ["suo", "<big>suo</big>\n\n<b>I suo, sui, sutum, ere</b>\nшить, сшивать (tegumenta corporum <i>C</i>); скреплять (alvaria corticibus <i>V</i>; naves loris <i>Vr ap. AG</i>): aliquid suo capite s. <i>погов. Ter</i> нажить какую-л. беду.\n\n<b>II suo</b> <i>dat. abl. sg. к</i>\nsuus <i>и</i> suum."],
  ["suomet", "<big>suomet</big>\n\n<b>suo–met</b> <i>и</i> <b>suo–pte</b> <i>усил. формы к</i>\nsuo (<i>от</i> suus)."],
  ["suovetaurilia", "<big>suovetaurilia</big>\n\n<b>suovetaurilia, ium</b> <i>n</i> [sus + ovis + taurus]\nжертвоприношение свиньи, овцы и быка (<i>при очистительных обрядах</i>) <i>Cato, Vr, L etc.</i>"],
  ["sup-", "<big>sup-</big>\n\n<b>sup-</b>\n<i>приставка</i> = sub- <i>перед начальным</i> p <i>основного слова.</i>"],
  ["supellecticarius", "<big>supellecticarius</big>\n\n<b>supellecticarius, i</b> <i>m</i> [supellex]\nзаведующий предметами домашнего обихода <i>Pt</i> (<i>v. l.</i>), <i>Dig</i>."],
  ["supellectilis", "<big>supellectilis</big>\n\n<b>supellectilis</b> <i>gen. к</i>\nsupellex."],
  ["supellex", "<big>supellex</big>\n\n<b>supellex, lectilis</b> <i>f</i> (<i>без</i> <i>pl.</i>)\n<b>1)</b> предметы домашнего обихода, утварь, обстановка (modica <i>Nep</i>; elegantior <i>C</i>);\n<b>2)</b> снаряжение (militaris <i>L</i>);\n<b>3)</b> убранство, украшение (oratoria <i>C</i>): amici optima vitae s. <i>C</i> друзья — лучшее украшение жизни;\n<b>4)</b> средства, ресурсы: s. verborum <i>Q</i> словарный запас; tecum habita, et noris, quam sit tibi curta supellex <i>Pers</i> останься с собой наедине, и ты увидишь, как беден ты духом (<i>к пустому честолюбцу</i>)."],
  ["super", "<big>super</big>\n\n<b>I super, supera, superum</b> <i>арх.</i>\n= superus (s. inferque vicinus <i>Cato</i>): super ignis <i>Lcr</i> верхний (небесный) огонь.\n\n<b>II super</b> <i>adv.</i>\n<b>1)</b> наверху, вверху (astare <i>Sl</i>);\n<b>2)</b> сверху (aliquid cernere <i>V</i>): s. e vallo prospectare <i>V</i> стоя наверху с вала смотреть;\n<b>3)</b> наверх, поверх (tigna injicere <i>Cs</i>); вверх (struem cumulare <i>QC</i>);\n<b>4)</b> сверх того: pauca s. referam <i>O</i> я добавлю (лишь) немногое; satis superque <i>C, L, H</i> <i>и</i> s. quam satis est <i>H</i> сверх меры, больше чем нужно; s. esse оставаться, быть в остатке: Atheniensibus praeter arma et naves nihil erat s. <i>Nep</i> у афинян ничего, кроме оружия и кораблей, не осталось.\n\n<b>III super</b> <i>praep. cum acc. и abl.</i>\n<b>1.</b> <i>с acc.</i> (<i>преим. в смысле направления</i>)\n<b>1)</b> вверх на, наверх к (s. astra ferri <i>St</i>; s. vallum praecipitari <i>Sl</i>); на (s. aliquid assidere <i>C</i>): s. hoc positus officium <i>Pt</i> назначенный на эту должность;\n<b>2)</b> над: scuto s. caput elato <i>L</i> с поднятым над головой щитом; s. armamentarium ponere <i>QC</i> поставить во главе арсенала;\n<b>3)</b> поверх, через, по (s. corpora vadere <i>Sl</i>);\n<b>4)</b> выше, дальше, за (пределами) (s. Gangen <i>Sil</i>; s. Numidiam <i>Sl</i>): alii s. alios <i>L</i> одни за другими <i>или</i> <i>Sen</i> одни на других;\n<b>5)</b> сверх, помимо (s. tres modios <i>L</i>); свыше (decem milia <i>T</i>): s. aliquid esse <i>Sl</i> брать верх над чём-л.; s. omnia <i>VM, L, V</i> прежде всего, больше всего || выше (s. omnes tragoedos <i>Q</i>);\n<b>6)</b> в течение, во время (s. cenam <i>PJ, Su etc.</i>);\n<b>2.</b> <i>с abl.</i> (<i>преим. в смысле положения</i>)\n<b>1)</b> сверху, поверх, на <i>или</i> над (requiescere fronde s. viridi <i>V</i>): s. musculo lateres struere <i>Cs</i> класть кирпичи на навес; s. cervice pendere <i>H</i> висеть над шеей (головой);\n<b>2)</b> в течение, во время: nocte s. media <i>V</i> среди ночи, в глухую ночь;\n<b>3)</b> касательно, насчёт, о (s. aliqua re scribere <i>C</i>); в отношении, из-за (s. scelere suspectus <i>Sl</i>): civiles s. Urbe curae <i>H</i> тревоги за судьбы Рима; s. sua laude moliri laborem <i>V</i> потрудиться ради собственной славы;\n<b>4)</b> сверх, помимо: s. his <i>H</i> кроме этого.\n\n<b>IV super-</b>\n<i>приставка, обозначающая</i>:\n<b>1)</b> <i>превышение</i>: «через», «пере-», «пре-» (super–fundo), <i>а также</i> «на-», «над-» (super–do, super–ficies);\n<b>2)</b> <i>остаток, излишек</i> (super–sum, super–fio)."],
  ["supera", "<big>supera</big>\n\n<b>I supera</b> <i>adv. и praep.</i> <i>Lcr, C</i>\n= supra.\n\n<b>II supera, orum</b> <i>n</i> [superus]\nвысоты, высшие области, горний мир (s. alta <i>C, V</i>; s. ardua <i>V</i>): s. convexa <i>V</i> небесный свод."],
  ["superabilis", "<big>superabilis</big>\n\n<b>superabilis, e</b> [supero]\n<b>1)</b> (удобо)переходимый (murus <i>L</i>);\n<b>2)</b> преодолимый (per vim <i>O</i>);\n<b>3)</b> победимый (Romani <i>T</i>);\n<b>4)</b> излечимый (caecitas, debilitas <i>T</i>)."],
  ["superabundanter", "<big>superabundanter</big>\n\n<b>super–abundanter</b>\nв огромном множестве <i>Vlg</i>."],
  ["superabundantia", "<big>superabundantia</big>\n\n<b>super–abundantia, ae</b> <i>f</i>\nчрезвычайное изобилие, преизбыток <i>Vlg</i>."],
  ["superaccommodo", "<big>superaccommodo</big>\n\n<b>super–accommodo, —, —, are</b>\nсверху прилаживать, прикладывать (aliquid <i>CC</i>)."],
  ["superacervo", "<big>superacervo</big>\n\n<b>super–acervo, —, —, are</b>\nнагромождать (aliquid <i>Tert</i>)."],
  ["superaddo", "<big>superaddo</big>\n\n<b>super–addo, —, additum, ere</b>\nдобавлять (aliquid alicui rei): tumulo s. carmen <i>V</i> на могиле начертать стихи; laurus superaddita busto <i>Prp</i> лавр, посаженный на могиле."],
  ["superadjicio", "<big>superadjicio</big>\n\n<b>super–adjicio, jeci, jectum, ere</b>\nещё добавлять (aliquid <i>Pall, Macr</i>)."],
  ["superadornatus", "<big>superadornatus</big>\n\n<b>super–adornatus, a, um</b>\nукрашенный сверху, покрытый украшениями (materia <i>Sen</i>)."],
  ["superads-", "<big>superads-</big>\n\n<b>superads-</b> <i>v. l.</i>\n= superas-."],
  ["superadultus", "<big>superadultus</big>\n\n<b>super–adultus, a, um</b>\nпереступивший зрелый возраст (virgo <i>Vlg</i>)."],
  ["superaedificatio", "<big>superaedificatio</big>\n\n<b>super–aedificatio, onis</b> <i>f</i>\nнадстраивание <i>Tert</i>."],
  ["superaedificium", "<big>superaedificium</big>\n\n<b>super–aedificium, i</b> <i>n</i>\nнадстройка <i>Ambr</i>."],
  ["superaedifico", "<big>superaedifico</big>\n\n<b>super–aedifico, avi, atum, are</b>\nнадстраивать <i>Eccl</i>."],
  ["superaggero", "<big>superaggero</big>\n\n<b>super–aggero, —, —, are</b>\n<b>1)</b> засыпать сверху (scrobem <i>Col</i>);\n<b>2)</b> нагромождать (aliquid <i>Sid</i>)."],
  ["superamentum", "<big>superamentum</big>\n\n<b>superamentum, i</b> <i>n</i> [supero]\nостаток (materiarum <i>Dig</i>)."],
  ["superans", "<big>superans</big>\n\n<b>superans, antis</b>\n<b>1.</b> <i>part. praes. к</i> supero;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> высоко вздымающийся (mons <i>Sol</i>);\n<b>2)</b> одержавший верх (ignis <i>Lcr</i>)."],
  ["superaspergo", "<big>superaspergo</big>\n\n<b>super–aspergo, —, aspersum, ere</b>\n<b>1)</b> посыпать сверху (farinam <i>Veg</i>);\n<b>2)</b> <i>перен.</i> испещрять (albis maculis <i>Is</i>)."],
  ["superasto", "<big>superasto</big>\n\n<b>super–asto, —, —, are</b>\nстоять наверху <i>Sl</i>."],
  ["superatio", "<big>superatio</big>\n\n<b>superatio, onis</b> <i>f</i> [supero]\nпревышение, <i>перен.</i> преодоление (contrariorum <i>Vtr</i>)."],
  ["superator", "<big>superator</big>\n\n<b>superator, oris</b> <i>m</i> [supero]\nпобедитель (populi Etrusci <i>O</i>): s. Gorgonae <i>O</i> = Perseus."],
  ["superaugmentum", "<big>superaugmentum</big>\n\n<b>super–augmentum, i</b> <i>n</i>\nдополнительное добавление <i>Is</i>."],
  ["superauratus", "<big>superauratus</big>\n\n<b>super–auratus, a, um</b>\nпозолоченный, <i>перен.</i> золотистый (cervix sparuli <i>O</i>)."],
  ["superbe", "<big>superbe</big>\n\n<b>superbe</b> [superbus]\nгордо, надменно <i>C, Cs, L etc.</i>"],
  ["superbia", "<big>superbia</big>\n\n<b>superbia, ae</b> <i>f</i> [superbus]\n<b>1)</b> высокомерие, чванство, надменность (insolens <i>C</i>);\n<b>2)</b> гордость, чувство собственного достоинства: sume superbiam quaesitam meritis <i>H</i> проникнись (муза) заслуженной гордостью;\n<b>3)</b> высокое качество (in piris <i>PM</i>)."],
  ["superbificus", "<big>superbificus</big>\n\n<b>superbificus, a, um</b> [superbus + facio]\nвнушающий гордость <i>SenT</i>."],
  ["superbiloquentia", "<big>superbiloquentia</big>\n\n<b>superbiloquentia, ae</b> <i>f</i> [superbus + loquor]\nвысокомерная речь <i>Poeta ap. C</i>."],
  ["superbio", "<big>superbio</big>\n\n<b>superbio, —, —, ire</b> [superbus]\n<b>1)</b> гордиться, хвалиться (aliqua re <i>O, Ph, T etc.</i>; de aliqua re <i>Aug</i>; adversus aliquem <i>Aug</i>);\n<b>2)</b> блистать (radiis auri <i>Cld</i>); славиться (aliqua re <i>PM, Cld</i>)."],
  ["superbus", "<big>superbus</big>\n\n<b>I superbus, a, um</b> [super]\n<b>1)</b> высокомерный, надменный (insolens et s. <i>C</i>; superba fastidia pati <i>V</i>); горделивый, чванный, важный (gallus <i>M</i>); гордый, чванящийся (aliqua re <i>H, QC</i>);\n<b>2)</b> беспощадный, грозный (bellum <i>V</i>);\n<b>3)</b> неумолимый, строгий (oculi <i>O</i>; jura <i>L</i>);\n<b>4)</b> щепетильный, разборчивый, своенравный, взыскательный (dens <i>H</i>; aures, quarum est judicium superbissimum <i>C</i>);\n<b>5)</b> гордый, благородный, славный (populus <i>V</i>; animae virtute superbae <i>Sil</i>);\n<b>6)</b> великолепный, пышный (apparatus <i>L</i>; triumphus <i>H</i>);\n<b>7)</b> превосходный, отличный (pira <i>Col</i>).\n\n<b>II Superbus, i</b> <i>m</i>\nСуперб, <i>cognomen Тарквиния II, последнего (седьмого) римского царя</i> <i>C, L, O</i>."],
  ["supercado", "<big>supercado</big>\n\n<b>super–cado, cecidī, —, ere</b>\nпадать сверху (supercecidit ignis <i>Vlg</i>); низвергаться (supercadentes aquae <i>CA</i>)."],
  ["supercaelestis", "<big>supercaelestis</big>\n\n<b>super–caelestis, e</b>\nнаднебесный <i>Tert</i>."],
  ["supercalco", "<big>supercalco</big>\n\n<b>super–calco, —, —, āre</b>\nтоптать, утаптывать (tabulatum <i>Col</i>)."],
  ["supercedo", "<big>supercedo</big>\n\n<b>super–cedo, cessi, —, ere</b>\nпереступить, перешагнуть (duodecimum annum aetatis <i>Ap</i>)."],
  ["supercerto", "<big>supercerto</big>\n\n<b>super–certo, —, —, are</b>\nупорно бороться, отстаивать <i>Vlg</i>."],
  ["superciliosus", "<big>superciliosus</big>\n\n<b>superciliosus, a, um</b> [supercilium]\n<b>1)</b> хмурый, мрачный (censor <i>Sen</i>);\n<b>2)</b> самонадеянный, надменный <i>Eccl</i>."],
  ["supercilium", "<big>supercilium</big>\n\n<b>super–cilium, i</b> <i>n</i>\n<b>1)</b> бровь: supercilia contrahere <i>Q</i> хмурить брови; s. (ad frontem) tollere <i>Ctl, C</i> высоко поднять брови; demere supercilio nubem <i>H</i> разгладить чело, <i>т. е.</i> отбросить печаль, повеселеть;\n<b>2)</b> нахмуренность, мрачный вид, суровость (contegere libidines supercilio <i>C</i>);\n<b>3)</b> надменность, высокомерие, спесь (Campanum <i>C</i>; asperitas verborum et s. <i>Sen</i>);\n<b>4)</b> выступ, возвышенность, острый край: infimum s. tumuli <i>L</i> нижний выступ холма;\n<b>5)</b> берег (amnis <i>Ap</i>)."],
  ["supercontego", "<big>supercontego</big>\n\n<b>super–contego, texi, —, ere</b>\nприкрывать сверху (aliquem <i>и</i> aliquid aliqua re <i>Sil, CC</i>)."],
  ["supercorruo", "<big>supercorruo</big>\n\n<b>super–corruo, —, —, ere</b>\n(по)валиться, рухнуть <i>VM</i>."],
  ["supercreatus", "<big>supercreatus</big>\n\n<b>super–creatus, a, um</b>\nдополнительно образовавшийся (infusio umoris <i>CA</i>)."],
  ["supercresco", "<big>supercresco</big>\n\n<b>super–cresco, crevi, —, ere</b>\n<b>1)</b> расти сверху, нарастать (caro supercrescens <i>CC</i>);\n<b>2)</b> присоединяться (alicui rei <i>Q</i>);\n<b>3)</b> превосходить, перегонять (aliquid <i>Aug</i>)."],
  ["supercubo", "<big>supercubo</big>\n\n<b>super–cubo, avi, —, are</b>\nлежать наверху <i>Col</i>: s. tauro <i>Ap</i> лежать на спине быка (<i>о Европе, похищенной Юпитером</i>)."],
  ["supercurro", "<big>supercurro</big>\n\n<b>super–curro, —, —, ere</b>\n<b>1)</b> опережать (на бегу) <i>Veg</i>;\n<b>2)</b> превосходить: ager vectigali supercurrit <i>PJ</i> земельный участок (даёт дохода) больше, чем налог (на него)."],
  ["superdeligo", "<big>superdeligo</big>\n\n<b>super–deligo, —, —, are</b>\nпривязывать сверху (spongiam <i>CC</i>)."],
  ["superdico", "<big>superdico</big>\n\n<b>super–dico, —, —, ere</b>\nдополнительно сказать, добавить <i>Dig, Tert</i>."],
  ["superdo", "<big>superdo</big>\n\n<b>super–do, dedi, datum, are</b>\nкласть сверху, прикладывать (linamenta <i>CC</i>)."],
  ["superduco", "<big>superduco</big>\n\n<b>super–duco, duxi, ductum, ere</b>\n<b>1)</b> распростирать сверху (pampinum <i>Sid</i>);\n<b>2)</b> приводить (в дом) (novercam liberis <i>Capit, CJ</i>);\n<b>3)</b> брать лишнее, перебирать <i>CJ</i>;\n<b>4)</b> добавлять <i>Tert</i>."],
  ["superductio", "<big>superductio</big>\n\n<b>superductio, onis</b> <i>f</i>\nпоправка по перечёркнутому <i>Dig</i>."],
  ["superedo", "<big>superedo</big>\n\n<b>super–edo, —, —, ere</b>\nзакусывать, заедать (<i>для уничтожения неприятного запаха</i>) (si radicem betae superadunt, odor- <i>sc.</i> alii — exstinguitur <i>PM</i>)."],
  ["supereffluo", "<big>supereffluo</big>\n\n<b>super–effluo, —, —, ere</b>\nлиться через край, <i>перен.</i> быть переполненным (mensura supereffluens <i>Vlg</i>)."],
  ["superegredior", "<big>superegredior</big>\n\n<b>super–egredior, —, di</b>\nвыходить за пределы, <i>т. е.</i> преодолевать (plebejas opiniones <i>Ambr</i>)."],
  ["superelevo", "<big>superelevo</big>\n\n<b>super–elevo, —, —, are</b>\nпревозносить (ne superelevent se iniquitates vestrae <i>Vlg</i>)."],
  ["superemico", "<big>superemico</big>\n\n<b>super–emico, —, —, are</b>\nперепрыгивать, перехлёстывать (unda terram superemicat <i>Sid</i>)."],
  ["supereminens", "<big>supereminens</big>\n\n<b>supereminens, entis</b>\n<b>1.</b> <i>part. praes. к</i> superemineo;\n<b>2.</b> <i>adj.</i> (<i>тк. в</i> <i>compar. и superl.</i>) выдающийся, великий <i>Eccl</i>."],
  ["supereminentia", "<big>supereminentia</big>\n\n<b>super–eminentia, ae</b> <i>f</i>\nсверхвеличие <i>Eccl</i>."],
  ["superemineo", "<big>superemineo</big>\n\n<b>super–emineo, —, —, ere</b>\nпревосходить, превышать (omnes viros <i>V</i>): s. undas umero <i>V</i> возвышаться плечами над водой."],
  ["superemorior", "<big>superemorior</big>\n\n<b>super–emorior, —, mori</b>\n(на чём-л.) умирать (construere nidum, replere odoribus et s. <i>PM</i> — <i>о фениксе</i>)."],
  ["superenato", "<big>superenato</big>\n\n<b>super–enato, —, —, are</b>\nплыть поверх: s. amnem <i>Lcn</i> плыть по реке."],
  ["supereo", "<big>supereo</big>\n\n<b>super–eo, —, —, ire</b> (<i>тж. раздельно</i>)\nпереходить (salsas lacunas <i>Lcr</i> — <i>v. l.</i>)."],
  ["supererogo", "<big>supererogo</big>\n\n<b>super–erogo, —, —, are</b>\nперерасходовать <i>Vlg, CJ</i>."],
  ["superescit", "<big>superescit</big>\n\n<b>superescit</b> <i>арх. Enn, Acc fut. к</i>\nsupersum."],
  ["superesse", "<big>superesse</big>\n\n<b>superesse</b> <i>inf. к</i>\nsupersum."],
  ["superevolo", "<big>superevolo</big>\n\n<b>super–evolo, —, —, are</b>\nперелетать (nubiferam Alpem <i>Lcn</i>)."],
  ["superexactio", "<big>superexactio</big>\n\n<b>super–exactio, onis</b> <i>f</i>\nперебор при взимании налогов <i>CTh, CJ</i>."],
  ["superexalto", "<big>superexalto</big>\n\n<b>super–exalto, —, atum, are</b>\n<b>1)</b> возвеличивать превыше всего, превозносить (aliquem in omnia saecula <i>Aug</i>);\n<b>2)</b> превозноситься, превосходить (superexaltat misericordia judicium <i>Vlg</i>)."],
  ["superexcurro", "<big>superexcurro</big>\n\n<b>super–excurro, —, —, ere</b>\nпревосходить, превышать <i>Dig</i>."],
  ["superexeo", "<big>superexeo</big>\n\n<b>super–exeo, —, —, ire</b>\nпревышать, опережать (in annos <i>Aus</i>)."],
  ["superexigo", "<big>superexigo</big>\n\n<b>super–exigo, —, —, ere</b>\nвзыскивать сверх должного (damna <i>CJ</i>)."],
  ["superextendo", "<big>superextendo</big>\n\n<b>super–extendo, —, —, ere</b>\nнатягивать сверху (cutem in aliquo <i>Vlg</i>): s. se <i>Vlg</i> напрягаться."],
  ["superextollo", "<big>superextollo</big>\n\n<b>superextollo, —, —, ere</b> <i>Tert</i>\n= superexalto."],
  ["superfero", "<big>superfero</big>\n\n<b>super–fero, tuli, latum, ferre</b>\n<b>1.</b> <i>act.</i> носить через, переносить (aliquid <i>PM</i>);\n<b>2.</b> <i>med.-pass.</i>\n<b>1)</b> нестись, плыть через (pisces superferuntur <i>PM</i>);\n<b>2)</b> задерживаться, запаздывать (si superferatur partus <i>PM</i>)."],
  ["superfeto", "<big>superfeto</big>\n\n<b>super–feto, —, —, are</b>\nбыть вторично оплодотворяемой (до созревания предыдущего плода) <i>PM</i>."],
  ["superficialis", "<big>superficialis</big>\n\n<b>superficialis, e</b> [superficies]\n<b>1)</b> <i>мат.</i> плоскостной, двухмерный (numerus <i>Eccl</i>);\n<b>2)</b> поверхностный, неопределённый (officium <i>Tert</i>)."],
  ["superficiarius", "<big>superficiarius</big>\n\n<b>I superficiarius, a, um</b> [superficies] <i>юр.</i>\nсуперфициарный, находящийся (построенный) на чужой земле (praedia <i>Dig</i>); <i>перен.</i> mathematice superficiaria est: in alieno aedificat <i>Sen</i> математика — (наука) суперфициарная: она строит на чужой земле.\n\n<b>II superficiarius, i</b> <i>m</i> <i>юр.</i>\nсуперфициарий, арендатор, землепользователь <i>Dig</i>."],
  ["superficies", "<big>superficies</big>\n\n<b>superficies, ei</b> <i>f</i> [super + facies]\n<b>1)</b> верхняя часть (aedis <i>PM</i>);\n<b>2)</b> поверхность (aquae <i>Col</i>);\n<b>3)</b> <i>юр.</i> наземная часть строения (<i>в противоположность к земельному участку и фундаменту</i>) <i>C, L, Dig</i>;\n<b>4)</b> <i>мат.</i> плоскость, двухмерное протяжение <i>Macr</i>."],
  ["superficium", "<big>superficium</big>\n\n<b>superficium, i</b> <i>n</i> <i>Dig</i>\n= superficies 3."],
  ["superfio", "<big>superfio</big>\n\n<b>super–fio, —, fieri</b>\nоставаться, быть в остатке (de divitiis alicujus <i>Pl</i>)."],
  ["superfixus", "<big>superfixus</big>\n\n<b>super–fixus, a, um</b>\nвоткнутый (сверху), наколотый (capita hostium <i>L</i>)."],
  ["superflexus", "<big>superflexus</big>\n\n<b>super–flexus, a, um</b>\nнаклонённый <i>Sid</i>."],
  ["superflorescens", "<big>superflorescens</big>\n\n<b>super–florescens, entis</b>\nвновь зацветающий <i>PM</i>."],
  ["superflua", "<big>superflua</big>\n\n<b>superflua, orum</b> <i>n</i>\nотходы, выделения (corporis <i>CA</i>)."],
  ["superflue", "<big>superflue</big>\n\n<b>superflue</b> <i>Eccl</i>\n= superfluo I."],
  ["superfluitas", "<big>superfluitas</big>\n\n<b>superfluitas, atis</b> <i>f</i>\nизбыточность, чрезмерность <i>Eccl</i>."],
  ["superfluo", "<big>superfluo</big>\n\n<b>I superfluo</b> <i>adv.</i>\nизлишне <i>Eccl, CJ</i>.\n\n<b>II super–fluo, fluxi, —, ere</b>\n<b>1)</b> литься через край (fons superfluit <i>PM</i>); разливаться, выступать из берегов (Nilus superfluens <i>T</i>);\n<b>2)</b> находиться в изобилии, изобиловать (pecunia non superfluens <i>Sen</i>);\n<b>3)</b> иметься в избытке: nihil neque deest neque superfluit <i>Q</i> нет ни в чём недостатка, но нет и избытка; nimia superfluentis populi frequentia <i>Sen</i> перенаселённость;\n<b>4)</b> иметь в изобилии (s. armis <i>Sil</i>);\n<b>5)</b> быть многословным, словоохотливым (orator superfluens <i>T</i>);\n<b>6)</b> течь мимо: s. aures <i>Q</i> пролететь мимо ушей."],
  ["superfluum", "<big>superfluum</big>\n\n<b>superfluum, i</b> <i>n</i>\nизбыток, излишек <i>или</i> остаток <i>Dig, CJ</i>."],
  ["superfluus", "<big>superfluus</big>\n\n<b>superfluus, a, um</b> [superfluo I]\n<b>1)</b> переливающийся через край, выступающий из берегов (flumina <i>PJ</i>);\n<b>2)</b> (из)лишний, не необходимый <i>Sen, Eutr, Capit etc.</i>"],
  ["superforaneus", "<big>superforaneus</big>\n\n<b>super–foraneus, a, um</b> [forum]\nизлишний, бесполезный (labor <i>Symm</i>; superforaneum aliquid putare <i>Sid</i>)."],
  ["superfore", "<big>superfore</big>\n\n<b>superfore</b> <i>inf. fut. к</i>\nsupersum."],
  ["superfrutico", "<big>superfrutico</big>\n\n<b>super–frutico, —, —, are</b>\nбурно разрастаться <i>Tert</i>."],
  ["superfudi", "<big>superfudi</big>\n\n<b>superfudi</b> <i>pf. к</i>\nsuperfundo."],
  ["superfugio", "<big>superfugio</big>\n\n<b>super–fugio, —, —, ere</b>\nбежать через, перебегать (undas <i>VF</i>)."],
  ["superfui", "<big>superfui</big>\n\n<b>superfui</b> <i>pf. к</i>\nsupersum."],
  ["superfulgeo", "<big>superfulgeo</big>\n\n<b>super–fulgeo, —, —, ere</b>\nсверху озарять (templa <i>St</i>)."],
  ["superfundo", "<big>superfundo</big>\n\n<b>super–fundo, fudi, fusum, ere</b>\n<b>1)</b> лить сверху, наливать (unguentum <i>PJ</i>);\n<b>2)</b> сыпать, обрушивать: s. magnam vim telorum <i>T</i> осыпать градом стрел;\n<b>3)</b> обрушиться превосходящими силами, смять (equites equosque <i>T</i>);\n<b>4)</b> <i>med.-pass.</i> <i>или</i> se s. разливаться (Tiberis superfunditur <i>PM</i>); устремляться, нахлынуть (hostes superfusi <i>L</i>); laetitia se superfundens <i>L</i> бьющее через край веселье;\n<b>5)</b> распространяться, рассеиваться, расселяться (montibus Caucasiis <i>PM</i>): se s. in Asiam <i>L</i> распространиться на Азию."],
  ["superfusio", "<big>superfusio</big>\n\n<b>superfusio, onis</b> <i>f</i>\n<b>1)</b> поливка (pavimenti <i>Pall</i>);\n<b>2)</b> пролитие (aquarum caelestium <i>Amm</i>)."],
  ["superfusus", "<big>superfusus</big>\n\n<b>superfusus, a, um</b> <i>part. pf. к</i>\nsuperfundo."],
  ["superfuturus", "<big>superfuturus</big>\n\n<b>superfuturus, a, um</b> <i>part. fut. к</i>\nsupersum."],
  ["supergaudeo", "<big>supergaudeo</big>\n\n<b>super–gaudeo, —, —, ere</b>\nликовать <i>Vlg, Eccl</i>."],
  ["supergestus", "<big>supergestus</big>\n\n<b>super–gestus, a, um</b> [gero]\n<b>1)</b> нагромождённый, насыпанный (terra <i>Col</i>);\n<b>2)</b> закупоренный, закрытый (oriticium <i>Ap</i>)."],
  ["supergloriosus", "<big>supergloriosus</big>\n\n<b>super–gloriosus, a, um</b>\nдостославный <i>Vlg</i>."],
  ["supergradior", "<big>supergradior</big>\n\n<b>supergradior</b> <i>PM</i>\n= supergredior."],
  ["supergredio", "<big>supergredio</big>\n\n<b>supergredio, —, —, ere</b> <i>Ap</i>\n= supergredior."],
  ["supergredior", "<big>supergredior</big>\n\n<b>super–gredior, gressus sum, gredi</b> [gradior]\n<b>1)</b> шагать через, переступать, переходить (limen <i>Col</i>);\n<b>2)</b> претерпевать, переносить, преодолевать (necessitates <i>Sen</i>);\n<b>3)</b> превосходить, превышать (aliquem pulchritudine <i>T</i>)."],
  ["supergressus", "<big>supergressus</big>\n\n<b>supergressus, a, um</b> <i>part. pf. к</i>\nsupergredio(r): per supergressum <i>Tert</i> свыше всякой меры."],
  ["superhabeo", "<big>superhabeo</big>\n\n<b>super–habeo, —, —, ere</b>\n<b>1)</b> иметь наверху (superhabendum est cataplasma <i>CC</i>);\n<b>2)</b> иметь сверх (того) (aliquid <i>Boet</i>)."],
  ["superhumerale", "<big>superhumerale</big>\n\n<b>super–humerale, is</b> <i>n</i>\nверхнее облачение первосвященника, эфод <i>Vlg, Eccl</i>.."],
  ["superi", "<big>superi</big>\n\n<b>superi, orum</b> <i>m</i> [superus]\n<b>1)</b> (<i>sc.</i> dii) небесные боги (dei superi et imi <i>H</i>): vi superum (= superorum) <i>V</i> по воле богов;\n<b>2)</b> живые (люди) (<i>в отличие от</i> inferi, <i>вошедших в царство теней</i>): apud <i>VP</i> (ad <i>V</i>) superos среди живых."],
  ["superiaceo", "<big>superiaceo</big>\n\n<b>superiaceo</b> <i>v. l.</i>\n= superjaceo."],
  ["superiacio", "<big>superiacio</big>\n\n<b>super–iacio</b> <i>v. l.</i>\n= superjacio."],
  ["superilligo", "<big>superilligo</big>\n\n<b>super–illigo, —, atum, are</b>\nпривязывать сверху (folia superilligata <i>PM</i>)."],
  ["superillino", "<big>superillino</big>\n\n<b>super–illino, —, illitum, ere</b>\nсверху смазывать <i>или</i> втирать (totum corpus aliqua re <i>CC</i>; aliquid <i>PM</i>)."],
  ["superimmineo", "<big>superimmineo</big>\n\n<b>super–immineo, —, —, ere</b>\nвисеть, нависать <i>Sen</i>: ense s. <i>V</i> замахнуться мечом."],
  ["superimpendeo", "<big>superimpendeo</big>\n\n<b>super–impendeo, —, —, ere</b>\nнависать: silvae superimpendentes <i>Ctl</i> склонившиеся (над долиной Темпе) леса."],
  ["superimpendo", "<big>superimpendo</big>\n\n<b>super–impendo, —, —, ere</b>\nцеликом расходовать, полностью отдавать (superimpendi pro aliquo <i>Vlg</i>)."],
  ["superimpono", "<big>superimpono</big>\n\n<b>super–impono, posui, positum, ere</b>\n<b>1)</b> класть сверху, накладывать (saxum ingens <i>L</i>; exuvias omnes <i>V</i>);\n<b>2)</b> ставить (statuam monumento — <i>dat. L</i>)."],
  ["superimpositus", "<big>superimpositus</big>\n\n<b>superimpositus, a, um</b> <i>part. pf. к</i>\nsuperimpono."],
  ["superincendo", "<big>superincendo</big>\n\n<b>super–incendo, —, —, ere</b>\nразжигать ещё больше, распалять (aliquem <i>VF</i>)."],
  ["superincerno", "<big>superincerno</big>\n\n<b>super–incerno, —, —, ere</b>\nсверху просеивать (terram cribris <i>PM</i>)."],
  ["superincido", "<big>superincido</big>\n\n<b>I super–incido, —, —, ere</b>\nсверху падать, устремляться, обрушиваться (superincidentia tela <i>L</i>; superincidens caelestis aqua <i>Col</i>).\n\n<b>II super–incido, —, —, ere</b>\nнадрезать (cutem <i>CC</i>)."],
  ["superincresco", "<big>superincresco</big>\n\n<b>super–incresco, crevi, —, ere</b>\nнарастать сверху <i>CC</i>."],
  ["superincubo", "<big>superincubo</big>\n\n<b>super–incubo, —, —, are</b>\nлежать сверху <i>L</i>."],
  ["superincumbo", "<big>superincumbo</big>\n\n<b>super–incumbo, cubui, —, ere</b>\nложиться сверху <i>Col, O, Aug</i>."],
  ["superincurvatus", "<big>superincurvatus</big>\n\n<b>super–incurvatus, a, um</b>\nнагнувшийся сверху <i>Ap</i>."],
  ["superindicticius", "<big>superindicticius</big>\n\n<b>super–indicticius</b> <i>и</i> <b>superindictus, a, um</b>\n(<i>о налоге</i>) дополнительный, добавочный <i>CTh, CJ</i>."],
  ["superindictio", "<big>superindictio</big>\n\n<b>super–indictio, onis</b> <i>f</i> <i>и</i> <b>superindictum, i</b> <i>n</i>\nдобавочный налог <i>CTh, CJ</i>."],
  ["superinduco", "<big>superinduco</big>\n\n<b>super–induco, duxi, ductum, ere</b>\n<b>1)</b> сверху покрывать (tabellas cera <i>Just</i>);\n<b>2)</b> класть сверху (terram <i>PM</i>);\n<b>3)</b> наводить (magnum malum in aliquem <i>Vlg</i>);\n<b>4)</b> дополнительно привести (упомянуть) (aliquid <i>Tert</i>)."],
  ["superinducticius", "<big>superinducticius</big>\n\n<b>super–inducticius, a, um</b>\nподставной, ложный (fratres <i>Tert</i>)."],
  ["superinductio", "<big>superinductio</big>\n\n<b>superinductio, onis</b> <i>f</i> <i>Dig v. l.</i>\n— superductio."],
  ["superindumentum", "<big>superindumentum</big>\n\n<b>super–indumentum, i</b> <i>n</i>\nверхнее облачение <i>Tert</i>."],
  ["superinduo", "<big>superinduo</big>\n\n<b>super–induo, indui, indutum, ere</b>\nнадевать, накидывать (paenulam <i>Su</i>)."],
  ["superinfundo", "<big>superinfundo</big>\n\n<b>super–infundo, fudi, fusum, ere</b>\nналивать сверху (aquam <i>CC</i>)."],
  ["superingero", "<big>superingero</big>\n\n<b>super–ingero, —, gestum, ere</b>\n<b>1)</b> наносить, нагромождать, накладывать (montem <i>St</i>; acervos <i>PM</i>);\n<b>2)</b> всходить; s. ortus <i>Tib</i> (<i>v. l.</i>) всходить, показываться (над горизонтом)."],
  ["superingestus", "<big>superingestus</big>\n\n<b>superingestus, a, um</b> <i>part. pf. к</i>\nsuperingero."],
  ["superingredior", "<big>superingredior</big>\n\n<b>super–ingredior, —, di</b>\nсверху спускаться, нападать (eruca superingrediens hortos <i>Col</i>)."],
  ["superinjectus", "<big>superinjectus</big>\n\n<b>superinjectus, a, um</b> <i>part. pf. к</i>\nsuperinjicio."],
  ["superinjicio", "<big>superinjicio</big>\n\n<b>super–injicio</b>\nнабрасывать, наваливать, накладывать, насыпать (frondes <i>V</i>; terram <i>O, PM</i>): s. textum rude sedili <i>O</i> покрыть сиденье грубой тканью."],
  ["superinspicio", "<big>superinspicio</big>\n\n<b>super–inspicio, —, —, ere</b>\nиметь наблюдение, надзирать (s. sacra ecclesiae <i>Sid</i>)."],
  ["superinsterno", "<big>superinsterno</big>\n\n<b>super–insterno, stravi, stratum, ere</b>\nраскладывать, накладывать, настилать (tabulas <i>L</i>; toros <i>Sil</i>)."],
  ["superinstrepo", "<big>superinstrepo</big>\n\n<b>super–instrepo, —, —, ere</b>\nгреметь сверху, скрипеть (над головой) (axis superinstrepit <i>Sil</i>)."],
  ["superinstruo", "<big>superinstruo</big>\n\n<b>super–instruo, —, structum, ere</b>\nнадстраивать (aliquid <i>CJ</i>; ordines vasorum superinstructi <i>Col</i>)."],
  ["superinsultans", "<big>superinsultans</big>\n\n<b>super–insultans, antis</b>\nнабрасывающийся <i>Sil</i>."],
  ["superintego", "<big>superintego</big>\n\n<b>super–intego, —, —, ere</b>\nсверху прикрывать (ora fossarum <i>PM</i>)."],
  ["superintendo", "<big>superintendo</big>\n\n<b>super–intendo, —, —, ere</b>\nиметь надзор <i>Eccl</i>."],
  ["superintentor", "<big>superintentor</big>\n\n<b>super–intentor, oris</b> <i>m</i>\nнаблюдатель, надзиратель <i>Eccl</i>."],
  ["superinundo", "<big>superinundo</big>\n\n<b>super–inundo, —, —, are</b>\nзатоплять (aliqua re <i>Tert</i>)."],
  ["superinungo", "<big>superinungo</big>\n\n<b>super–inungo, —, —, ere</b>\nнамазывать <i>или</i> натирать сверху (oculos collyrio <i>CC</i>)."],
  ["superior", "<big>superior</big>\n\n<b>superior, ius</b> [<i>compar. к</i> superus]\n<b>1)</b> вверху расположенный, выше находящийся (pars collis <i>Cs</i>): in inferiorem locum de superiore motus <i>C</i> движение с более высокого места в более низменное; de loco superiore dicere <i>C</i> говорить с возвышения; domus s. <i>C</i> верхняя часть дома; superiora castra <i>Cs</i> выше расположенная часть лагеря;\n<b>2)</b> предшествующий, предыдущий, прежний, истекший (dies <i>Cs</i>; tempora <i>Cs</i>): superiore nocte <i>C</i> в прошлую ночь; s. uxor <i>C</i> прежняя жена; superiores <i>C</i> предшественники (<i>ср.</i> 4); aetas superiorum <i>C</i> прошлое; in superiore vita <i>C</i> в прежние годы жизни; in vita superiore <i>C</i> в прежней жизни;\n<b>3)</b> ранее упомянутый, первый (из названных) (genus <i>C</i>);\n<b>4)</b> старший: Dionysius S. <i>Nep</i> Дионисий Старший; s. aetas <i>C, Cs</i> старший (преклонный) возраст; superiores ordines <i>Cs</i> старшие звания (чины); superiores <i>C</i> люди старшие по положению;\n<b>5)</b> более высокий, превосходящий: s. loco <i>C</i> более знатный по происхождению; s. fortuna <i>C</i> более зажиточный;\n<b>6)</b> более сильный (causa <i>C</i>): equitatu s. <i>Cs</i> обладающий большей (лучшей) конницей; primo proelio Catilina s. discessit <i>St</i> из первого сражения Катилина вышел победителем."],
  ["superius", "<big>superius</big>\n\n<b>superius</b>\n<b>1.</b> <i>adj. compar. n</i> <i>к</i> superus;\n<b>2.</b> <i>adv. compar. к</i> supra."],
  ["superjaceo", "<big>superjaceo</big>\n\n<b>super–jaceo, —, —, ere</b>\nлежать (находиться) сверху <i>CC, Aug</i>."],
  ["superjacio", "<big>superjacio</big>\n\n<b>super–jacio, jeci, jectum, ere</b>\n<b>1)</b> перебрасывать (arbores sagittis superjaci nequeunt <i>PM</i>);\n<b>2)</b> бросать сверху, набрасывать, накидывать (vestem <i>O</i>): se rogo s. <i>VM</i> броситься в костёр;\n<b>3)</b> насыпать, возводить (aggerem <i>Su</i>);\n<b>4)</b> переступать, переливаться: pontus scopulos superjacit unda <i>V</i> море волнами перехлёстывает через утёсы; s. augendo fidem <i>L</i> переходить пределы правдоподобия, <i>т. е.</i> чрезмерно раздувать, преувеличивать; aequor superjectum (<i>sc.</i> terris) <i>H</i> разлившееся море;\n<b>5)</b> превысить, превзойти <i>или</i> побороть (aliquem <i>Sen</i>)."],
  ["superjacto", "<big>superjacto</big>\n\n<b>super–jacto, —, —, are</b>\n<b>1)</b> подбрасывать, швырять (infantes <i>VM</i>);\n<b>2)</b> перепрыгивать, перескакивать (transversa navigia <i>PM</i>)."],
  ["superjactus", "<big>superjactus</big>\n\n<b>superjactus</b> <i>Sl, PM, T</i>\n= superjectus."],
  ["superjectio", "<big>superjectio</big>\n\n<b>superjectio, onis</b> <i>f</i>\n<b>1)</b> набрасывание (vestis <i>Eccl</i>);\n<b>2)</b> <i>ритор.</i> преувеличение, гипербола <i>Q</i>."],
  ["superjectus", "<big>superjectus</big>\n\n<b>I superjectus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> superjacio;\n<b>2.</b> <i>adj.</i> расположенный над <i>или</i> дальше: Phrygia Troadi superjecta <i>PM</i> Фригия, расположенная за Троадой.\n\n<b>II superjectus, (us) </b> <i>m</i>\nвспрыгивание <i>Col</i>."],
  ["superjumentarius", "<big>superjumentarius</big>\n\n<b>super–jumentarius, i</b> <i>m</i>\nнадсмотрщик над погонщиками вьючного скота, старший погонщик <i>Su</i>."],
  ["superlabor", "<big>superlabor</big>\n\n<b>super–labor, —, labi</b> <i>depon.</i>\nскользить наверху, над головой (sidera superlabebantur <i>Sen</i>); скользить поверх (струн) (digito superlabente <i>Sid</i>)."],
  ["superlacrimo", "<big>superlacrimo</big>\n\n<b>super–lacrimo, —, —, are</b>\nструить слёзы, <i>перен.</i> выделять каплями сок, сочиться (vitis superlacrimat <i>Col</i>)."],
  ["superlatio", "<big>superlatio</big>\n\n<b>superlatio, onis</b> <i>f</i> [superfero]\n<b>1)</b> превышение, преувеличение (veritatis <i>C</i>); гипербола <i>rhH</i>;\n<b>2)</b> <i>грам.</i> превосходная степень <i>Q</i>."],
  ["superlativus", "<big>superlativus</big>\n\n<b>superlativus, a, um</b> <i>грам.</i>\nпревосходный (nomen, gradus)."],
  ["superlatus", "<big>superlatus</big>\n\n<b>superlatus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> superfero;\n<b>2.</b> <i>adj.</i> преувеличенный, раздутый (verba <i>rhH, C</i>)."],
  ["superlaudabilis", "<big>superlaudabilis</big>\n\n<b>super–laudabilis, e</b>\nсверхславный <i>Vlg</i>."],
  ["superlex", "<big>superlex</big>\n\n<b>superlex</b> <i>Eccl</i>\n= supellex."],
  ["superliminare", "<big>superliminare</big>\n\n<b>super–liminare, is</b> <i>n</i>\nверхняя перекладина двери, притолока <i>Vlg</i>."],
  ["superlinio", "<big>superlinio</big>\n\n<b>super–linio, —, —, ire</b>\nсверху обмазывать (dolium <i>Pall</i>)."],
  ["superlino", "<big>superlino</big>\n\n<b>super–lino, levi, litum, ere</b> <i>PM</i>\n= superlinio."],
  ["superlucror", "<big>superlucror</big>\n\n<b>super–lucror, atus sum, ari</b>\nдополнительно заработать (alia quinque talenta <i>Vlg</i>)."],
  ["supermando", "<big>supermando</big>\n\n<b>super–mando, —, —, ere</b>\nзатем (дополнительно) жевать (raphanos <i>PM</i>)."],
  ["supermeo", "<big>supermeo</big>\n\n<b>super–meo, —, —, are</b>\n<b>1)</b> течь поверх (aquae dulces supermeant alias <i>PM</i>);\n<b>2)</b> проплывать (amnem <i>Amm</i>)."],
  ["supermetior", "<big>supermetior</big>\n\n<b>super–metior, mensus sum, iri</b>\nсверх меры отмерять (alimenta <i>Tert</i>)."],
  ["supermico", "<big>supermico</big>\n\n<b>super–mico, —, —, are</b>\nзатмить, превзойти (beneficia alicujus <i>Sen</i>)."],
  ["supermitto", "<big>supermitto</big>\n\n<b>super–mitto, misi, —, ere</b>\nналивать, доливать (aquam potioni <i>Just</i>)."],
  ["supermolaris lapis", "<big>supermolaris lapis</big>\n\n<b>super–molaris lapis</b>\nверхний жёрнов <i>Ambr</i>."],
  ["supermundialis", "<big>supermundialis</big>\n\n<b>super–mundialis, e</b>\nнадмирный (substantia <i>Tert</i>)."],
  ["supermunio", "<big>supermunio</big>\n\n<b>super–munio, —, —, ire</b>\nЗащищать сверху (alvearia porticibus <i>Col</i>)."],
  ["superna", "<big>superna</big>\n\n<b>superna, orum</b> <i>n</i>\nверх, высота (ex supernis in infima jactus <i>AG</i>)."],
  ["supernas", "<big>supernas</big>\n\n<b>supernas, atis</b> <i>adj.</i> [supernus]\nпроисходящий с севера, из областей mare Superum, <i>т. е.</i> Адриатического моря (abies <i>PM</i>; <i>sc.</i> ventus <i>Vtr</i>)."],
  ["supernato", "<big>supernato</big>\n\n<b>super–nato, avi, —, are</b>\nплыть поверх, переплывать <i>Col, PM, Ap</i>."],
  ["supernatus", "<big>supernatus</big>\n\n<b>super–natus, a, um</b> [nascor]\nвыросший <i>или</i> образовавшийся сверху (ulcus <i>CC</i>; caulis <i>PM</i>)."],
  ["superne", "<big>superne</big>\n\n<b>superne</b> [supernus]\n<b>1)</b> сверху (ruere <i>Lcr</i>; se proruere in praedam <i>Ap</i> — <i>об орле</i>);\n<b>2)</b> поверх (circumjicere pallium <i>Ap</i>); сверху, в верхней части (mulier formosa s. <i>H</i>);\n<b>3)</b> вверх (tendere <i>PM</i>)."],
  ["supernumerarius", "<big>supernumerarius</big>\n\n<b>super–numerarius, a, um</b>\nизбыточный, сверхкомплектный <i>Veg, CTh, Eccl</i>."],
  ["supernus", "<big>supernus</big>\n\n<b>supernus, a, um</b> [super]\n<b>1)</b> верхний (pars <i>PM</i>);\n<b>2)</b> высоко расположенный, возвышенный (Tusculum <i>H</i>);\n<b>3)</b> высший, небесный (numen <i>O</i>)."],
  ["supero", "<big>supero</big>\n\n<b>supero, avi, atum, are</b> [superus]\n<b>1)</b> подниматься, всходить (sol superabat ex mari <i>Pl</i>);\n<b>2)</b> возвышаться (capite <i>V</i>); превышать (nubes cacumine <i>O</i>);\n<b>3)</b> превосходить (aliquem virtute <i>C</i>; fastigium alicujus rei <i>Hirt</i>): scribendi arte rudem vetustatem s. <i>L</i> литературностью изложения затмить незрелые сочинения прошлого; duritia ferrum s. <i>O</i> быть твёрже железа; materiam superabat opus <i>O</i> обработка была (ещё) лучше, чем материал || одерживать верх, получать перевес (equitatu <i>Nep</i>; gladio <i>Cs</i>): superat sententia Sabini <i>Cs</i> мнение Сабина получает перевес; numero s. <i>L</i> численно превосходить || одолевать (maximas nationes <i>Cs</i>): bello superati <i>Cs</i> побеждённые на войне;\n<b>4)</b> быть выше, служить опровержением: falsa (<i>acc. pl.</i>) vita moresque mei superant <i>Sl</i> моя жизнь и мой характер выше (<i>т. е.</i> служат опровержением) клеветы;\n<b>5)</b> преодолевать (Musarum scopulos <i>Enn</i>; labores <i>V</i>); выносить (injurias fortunae <i>C</i>): superanda omnis fortuna ferendo est <i>V</i> любые невзгоды следует превозмочь терпением;\n<b>6)</b> покорять, смягчать (aliquem donis <i>V</i>); успокаивать, унимать (iram votis <i>O</i>);\n<b>7)</b> изобиловать, быть в избытке (pecunia superabat <i>C</i>): alicui divitiae superant <i>Sl</i> кто-л. чрезвычайно богат; et deesse et s. <i>PS</i> то не хватать, то быть чрезмерным;\n<b>8)</b> оставаться, быть в остатке: si, quod superaret pecuniae, rettulisses <i>C</i> если бы ты (Веррес) оставшиеся деньги вернул (казне); si de quincunce remota est uncia, quid superat? <i>H</i> если от пяти унций отнята одна унция, что остаётся?; aliquot horis die superante <i>L</i> за несколько часов остающегося (<i>т. е.</i> до конца) дня;\n<b>9)</b> переживать, оставаться в живых: vita s. <i>Cs</i> остаться (быть) в живых; s. captae urbi <i>V</i> пережить взятие города;\n<b>10)</b> переходить (ripas fluminis <i>Cs</i>): Tauro monte superato <i>L</i> перевалив через Таврские горы;\n<b>11)</b> проходить, проплывать мимо, огибать (promunturium <i>L</i>); миновать (isthmon cursu <i>O</i>)."],
  ["superobductus", "<big>superobductus</big>\n\n<b>super–obductus, a, um</b>\nнадетый поверх (pallium <i>Eccl</i>)."],
  ["superobruo", "<big>superobruo</big>\n\n<b>super–obruo, obrui, obrutum, ere</b>\nзабрасывать, заваливать (aliquem ingestis armis <i>Prp</i>; aliquem saxis <i>Aus</i>)."],
  ["superoccidens", "<big>superoccidens</big>\n\n<b>super–occidens, entis</b>\nзаходящий после, <i>т. е.</i> на следующий день (luna <i>Macr</i>)."],
  ["superoccupo", "<big>superoccupo</big>\n\n<b>super–occupo, —, —, are</b>\nзастигать врасплох (aliquem <i>V</i>)."],
  ["superordinatio", "<big>superordinatio</big>\n\n<b>super–ordinatio, onis</b> <i>f</i>\nдополнительное избрание <i>Ambr</i>."],
  ["superordino", "<big>superordino</big>\n\n<b>super–ordino, —, —, are</b>\nдобавлять, присоединять (testamentum <i>Vlg</i>)."],
  ["superpendens", "<big>superpendens</big>\n\n<b>super–pendens, entis</b> <i>adj.</i>\nнависший (saxa <i>L</i>)."],
  ["superpingo", "<big>superpingo</big>\n\n<b>super–pingo, —, pictum, ere</b>\nрисовать поверх <i>или</i> сверх (aliquid <i>Sol</i>)."],
  ["superplaudo", "<big>superplaudo</big>\n\n<b>super–plaudo, —, —, ere</b>\nхлопать сверху (pinnis <i>Sol</i>)."],
  ["superpolluo", "<big>superpolluo</big>\n\n<b>super–polluo, ui, —, ere</b>\nосквернять свыше меры (superpolluit iniquitas omnem terram <i>Vlg</i>)."],
  ["superpondium", "<big>superpondium</big>\n\n<b>super–pondium, i</b> <i>n</i>\nдополнительный груз, довесок <i>Ap</i>."],
  ["superpono", "<big>superpono</big>\n\n<b>super–pono, posui, positum, ere</b>\n<b>1)</b> класть наверх, положить (manum <i>O</i>); возлагать (capiti decus <i>L</i>);\n<b>2)</b> ставить (statuam Jano <i>Su</i>); воздвигать (turrim congestis pilis <i>Su</i>); располагать наверху (villam colli <i>Su</i>);\n<b>3)</b> ставить во главе (aliquem <i>L, Pt, Hier</i>);\n<b>4)</b> ставить на первом плане, ставить выше, предпочитать (alicui rei aliquid <i>Sen</i>);\n<b>5)</b> ставить на втором плане, ставить ниже (aliquid alicui rei <i>Q, Col</i>)."],
  ["superpositio", "<big>superpositio</big>\n\n<b>super–positio, onis</b> <i>f</i> <i>мед.</i>\nобострение, пароксизм <i>CA</i>."],
  ["superpositus", "<big>superpositus</big>\n\n<b>superpositus, a, um</b> <i>part. pf. к</i>\nsuperpono."],
  ["superpostulatio", "<big>superpostulatio</big>\n\n<b>super–postulatio, onis</b> <i>f</i>\nчрезмерное требование <i>CTh</i>."],
  ["superrado", "<big>superrado</big>\n\n<b>super–rado, —, rasum, ere</b>\nсоскабливать сверху (clavos pedum <i>PM</i>)."],
  ["superrimus", "<big>superrimus</big>\n\n<b>superrimus</b> <i>Vr</i>\n= supremus."],
  ["superruo", "<big>superruo</big>\n\n<b>super–ruo, —, —, ere</b>\nустремляться, бросаться (alicui rei <i>и</i> aliquem <i>Ap</i>)."],
  ["supersapio", "<big>supersapio</big>\n\n<b>super–sapio, —, —, ere</b>\nбыть чрезвычайно вкусным <i>Tert</i>."],
  ["superscando", "<big>superscando</big>\n\n<b>super–scando (-scendo), —, —, ere</b>\nшагать через, переступать <i>Col, Tert</i>: s. corpora strata somno <i>L</i> шагать через тела спящих."],
  ["superscribo", "<big>superscribo</big>\n\n<b>super–scribo, scripsi, scriptum, ere</b>\nписать поверх (deleta et superscripta <i>Su</i>); надписывать (nomen alicujus <i>Dig</i>)."],
  ["superscriptio", "<big>superscriptio</big>\n\n<b>super–scriptio, onis</b> <i>f</i>\nнадпись (s. scripta litteris hebraicis <i>Vlg</i>)."],
  ["supersedeo", "<big>supersedeo</big>\n\n<b>super–sedeo, sēdī, sessum, ēre</b>\n<b>1)</b> сидеть на (чём-л.), сидеть наверху (elephanto — <i>abl. Su</i>; aliquid <i>Ap</i>);\n<b>2)</b> быть во главе, ведать: litibus s. <i>Cato</i> разбирать тяжбы (<i>ср.</i> 4);\n<b>3)</b> отказываться, пренебрегать (aliquid facere <i>L, PJ, Su</i>);\n<b>4)</b> избегать (aliqua re <i>C, Cs</i>,<i>реже</i> alicui rei <i>bAfr, AG</i> <i>и</i> aliquid <i>rhH, AG</i>); уклоняться, воздерживаться: s. labore itineris <i>C</i> отказаться от трудностей путешествия; s. proelio <i>Cs</i> уклониться от боя; s. operam <i>AG</i> не оказать помощи; quod quum acciderit, omnino narratione supersedendum est <i>C</i> в этом случае (когда суду обстоятельства дела и без того известны), защитнику следует вовсе отказаться от изложения (дела); supersedissem loqui <i>L</i> я не хотел бы говорить; supersederi litibus <i>L</i> отложить разбор тяжб (<i>ср.</i> 2)."],
  ["superseminator", "<big>superseminator</big>\n\n<b>super–sēminātor, ōris</b> <i>m</i>\nследующий (новый) сеятель <i>Tert</i>."],
  ["supersemino", "<big>supersemino</big>\n\n<b>super–sēmino, āvī, ātum, āre</b>\nзаново сеять, совершать новый посев <i>Eccl</i>."],
  ["supersero", "<big>supersero</big>\n\n<b>super–sero, sēvī, —, ere</b> <i>Eccl</i>\n= supersemino."],
  ["supersessus", "<big>supersessus</big>\n\n<b>supersessus, a, um</b> <i>part. pf. к</i>\nsupersedeo."],
  ["supersido", "<big>supersido</big>\n\n<b>super–sido, —, —, ere</b>\nсадиться (alicui rei <i>Amm</i>)."],
  ["supersigno", "<big>supersigno</big>\n\n<b>super–signo, —, —, are</b>\nзапечатывать <i>Vlg</i>."],
  ["supersilio", "<big>supersilio</big>\n\n<b>super–silio, —, —, ire</b> [salio]\nвспрыгивать <i>Col</i>."],
  ["supersisto", "<big>supersisto</big>\n\n<b>super–sisto, stiti, —, ere</b>\nстановиться (на что-л.): s. tribunal ligneum <i>Ap</i> стать на деревянный помост."],
  ["superspergo", "<big>superspergo</big>\n\n<b>super–spergo, —, spersum, ere</b>\nобрызгивать, усеивать (aliquid aliqua re <i>Sol, Eccl</i>)."],
  ["superspero", "<big>superspero</big>\n\n<b>super–spero, avi, —, are</b>\nвсей душой уповать <i>Vlg, Eccl</i>."],
  ["superspicio", "<big>superspicio</big>\n\n<b>super–spicio, spexi, —, ere</b>\nглядеть далеко <i>Sol</i>."],
  ["superstagno", "<big>superstagno</big>\n\n<b>super–stagno, avi, —, are</b>\nразливаться озером(amnis superstagnat <i>T</i>)."],
  ["superstatumino", "<big>superstatumino</big>\n\n<b>super–statumino, —, —, are</b>\nкласть в основание (rudus <i>Pall</i>)."],
  ["supersterno", "<big>supersterno</big>\n\n<b>super–sterno, stravi, stratum, ere</b>\nнастилать (fossam ponte <i>Amm</i>); нагромождать: obrutus superstratis cumulis Gallorum <i>L</i> заваленный кучами (тел павших) галлов."],
  ["superstes", "<big>superstes</big>\n\n<b>superstes, stitis</b> <i>adj.</i> [super + sto]\n<b>1)</b> <i>юр. арх.</i> присутствующий, являющийся свидетелем: superstitibus praesentibus <i>C</i> в присутствии свидетелей;\n<b>2)</b> переживший, оставшийся в живых (alicui, <i>реже</i> alicujus <i>C, L etc.</i>); s. filio pater <i>L</i> отец, переживший сына; fama s. <i>H, O</i> посмертная слава; s. opus <i>O</i> пережившее творца, <i>т. е.</i> неумирающее произведение."],
  ["superstitio", "<big>superstitio</big>\n\n<b>superstitio, onis</b> <i>f</i> [super + sisto]\n<b>1)</b> суеверие (anilis <i>C</i>): horum sententiae non modo superstitionem tollunt, in qua inest timor inanis deorum, sed etiam religionem <i>C</i> высказывания этих (философов) устраняют не только суеверие, которому присущ бессмысленный страх перед богами, но и религию;\n<b>2)</b> суеверный ужас, благоговение (s. error insanus est <i>Sen</i>); благоговейное почитание, точное соблюдение (praeceptorum <i>Q</i>);\n<b>3)</b> предмет благоговейного почитания: caput Stigii fontis, una s. <i>V</i> исток Стигийских вод, единственное, перед чем благоговеют (боги);\n<b>4)</b> священный характер, святость (templi <i>Just</i>);\n<b>5)</b> (<i>тж.</i> <i>pl.</i>) суеверные обряды, культ (не римский) (superstitiones atque cura deorum <i>Just</i>); (иноземная) религия (novas superstitiones introducere <i>Q</i>)."],
  ["superstitiose", "<big>superstitiose</big>\n\n<b>superstitiose</b> [superstitiosus]\n<b>1)</b> суеверно (dicere aliquid <i>C, Su</i>);\n<b>2)</b> благоговейно, с величайшим почтением, крайне заботливо: s. inhaerere alicui rei <i>Q</i> чрезвычайно тщательно придерживаться (ни на волос не отступать от) чего-л."],
  ["superstitiosus", "<big>superstitiosus</big>\n\n<b>superstitiosus, a, um</b> [superstitio]\n<b>1)</b> суеверный (gentes <i>Mela</i>; saeculum <i>Tert</i>): superstitiosa sollicitudo <i>C</i> суеверный страх;\n<b>2)</b> пророческий, вещий (homo <i>Pl</i>; vox <i>Poeta ap. C</i>)."],
  ["superstitis", "<big>superstitis</big>\n\n<b>superstitis</b> <i>gen. к</i>\nsuperstes."],
  ["superstito", "<big>superstito</big>\n\n<b>superstito, —, —, are</b> [superstes]\n<b>1)</b> быть в постоянном наличии, быть обильным (alicui <i>Pl</i>);\n<b>2)</b> сохранять, хранить (regnum sospitare superstitareque <i>Enn</i>)."],
  ["supersto", "<big>supersto</big>\n\n<b>super–sto, —, —, are</b>\nстоять (на чём-л.) наверху (alicui rei <i>и</i> aliquid): s. columnis <i>L</i> стоять (покоиться) на колоннах; lapsum superstans <i>V</i> попирая ногами падшего; agger pondere superstantium procubuit <i>L</i> вал под тяжестью стоявших (на нём) провалился."],
  ["superstratus", "<big>superstratus</big>\n\n<b>superstratus, a, um</b> <i>part. pf. к</i>\nsupersterno."],
  ["superstrictus", "<big>superstrictus</big>\n\n<b>superstrictus, a, um</b> <i>part. pf. к</i>\nsuperstringo."],
  ["superstringo", "<big>superstringo</big>\n\n<b>super–stringo, strinxi, strictum, ere</b>\nплотно сжать, стянуть (latera balteis <i>Sid</i>); superstrictis manibus <i>Ap</i> закрывшись сложенными вместе руками."],
  ["superstructus", "<big>superstructus</big>\n\n<b>superstructus, a, um</b> <i>part. pf. к</i>\nsuperstruo."],
  ["superstruo", "<big>superstruo</big>\n\n<b>super–struo, struxi, structum, ere</b>\nстроить сверху, надстраивать (ligneam compagem <i>T</i>)."],
  ["supersubstantialis", "<big>supersubstantialis</big>\n\n<b>super–substantialis, e</b>\nнасущный (panis noster s. <i>Vlg</i>)."],
  ["supersum", "<big>supersum</big>\n\n<b>super–sum, fui, esse</b>\n<b>1)</b> оставаться в живых, уцелеть (ex pugna <i>bH</i>): ex eo proelio circiter milia hominum octoginta superfuerunt <i>Cs</i> после этого сражения уцелело около 80 000 человек;\n<b>2)</b> пережить, жить после: s. patri <i>L</i> пережить отца;\n<b>3)</b> быть ещё в живых, сохраниться: supersint aliqui nuntii et hostium cladis et nostrae virtutis <i>L</i> пусть сохранятся некоторые вестники и поражения врагов, и нашей доблести (<i>слова консула Ливия</i>);\n<b>4)</b> оставаться, быть в остатке (supersunt labes et luctus <i>VF</i>): non multum aestatis supererat <i>C</i> лета оставалось немного, <i>т. е.</i> лето близилось к концу; modo vita supersit <i>V</i> только бы хватило жизни; bellum ea tempestate nullum supererat <i>T</i> никаких войн в то время уже не было; quum malae causae superest magna audacia <i>J</i> когда с преступлением сочетается большая дерзость;\n<b>5)</b> быть в избытке, изобиловать (verba alicui supersunt <i>C</i>): cui tanta erat res et supererat? <i>Ter</i> (Демифон,) у которого было столько денег, да ещё в избытке?;\n<b>6)</b> быть лишним, ненужным: ut neque absit quicquam, neque supersit <i>C</i> чтобы ничего не отсутствовало и ничего не было бы лишнего;\n<b>7)</b> быть сильнее, совладать: labori superesse <i>V</i> справиться с делом; s. dolori <i>O</i> перенести страдание; s. temporibus <i>Nep</i> пережить тяжёлое время;\n<b>8)</b> превосходить, быть выше (aliqua re <i>VF</i>);\n<b>9)</b> не хватать, недоставать (quid superest ad honoris mei tribunal et columen? <i>Ap</i>);\n<b>10)</b> оказывать поддержку, помогать <i>Su</i>;\n<b>11)</b> находиться во главе, управлять (alicui <i>и</i> alicui rei <i>Su etc.</i>)."],
  ["supertectus", "<big>supertectus</big>\n\n<b>supertectus, a, um</b> <i>part. pf. к</i>\nsupertego."],
  ["supertego", "<big>supertego</big>\n\n<b>super–tego, texi, tectum, ere</b>\nпокрывать (сверху), накрывать (ossa favilla <i>Tib</i> —<i>in tmesi</i>): s. aliquem tunica <i>Ap</i> надеть на кого-л. тунику."],
  ["supertendo", "<big>supertendo</big>\n\n<b>super–tendo, —, —, ere</b>\nнатягивать поверх (chordas concavo ligno <i>Aug</i>)."],
  ["superterrenus", "<big>superterrenus</big>\n\n<b>super–terrenus, a, um</b>\nназемный <i>Tert</i>."],
  ["supertertius", "<big>supertertius</big>\n\n<b>super–tertius, a, um</b>\nсодержащий целое с третью, <i>т. е.</i> равный 4 к 3 (proportio <i>Cens</i>)."],
  ["supertraho", "<big>supertraho</big>\n\n<b>super–traho, —, —, ere</b>\nпротаскивать поверх (семян) (crates dentatas <i>PM</i>)."],
  ["superunctio", "<big>superunctio</big>\n\n<b>super–unctio, onis</b> <i>f</i>\nсмазывание, натирание <i>CA</i>."],
  ["superundo", "<big>superundo</big>\n\n<b>super–undo, —, —, are</b>\nизобиловать (gratia <i>Eccl</i>)."],
  ["superungo", "<big>superungo</big>\n\n<b>super–ungo, unxi, unctum, ere</b>\nсмазывать <i>CC, Scr, Eccl</i>."],
  ["superurgeo", "<big>superurgeo</big>\n\n<b>super–urgeo, —, —, ere</b> [urgeo]\nтеснить сверху, надавливать: fluctu superurgente <i>T</i> под тяжестью (заливавших) волн."],
  ["superus", "<big>superus</big>\n\n<b>superus, a, um</b> [super]\n<b>1)</b> верхний, вышний, небесный (dii <i>C, H etc.</i>; limen <i>Pl</i>): superae res <i>C</i> небесные явления <i>и</i> небо; (mare) Superum <i>C</i> Адриатическое и Ионическое моря (<i>в отличие от</i> mare Inferum = Этрусское море); Juppiter S. <i>Ctl</i> Юпитер Капитолийский (<i>в отличие от</i> Juppiter Inferus = Pluto);\n<b>2)</b> верхний (<i>в отличие от</i> подземный), наземный, земной (aurae, orae <i>V</i>). — <i>См. тж.</i> supera, superi, superius, summus, supremus."],
  ["supervacaneus", "<big>supervacaneus</big>\n\n<b>supervacaneus, a, um</b> [supervacuus]\n<b>1)</b> излишний, ненужный, бесполезный (litterae <i>C</i>; oratio <i>L</i>); supervacaneum est disserere de aliqua re <i>Sl</i> бесполезно (незачем) спорить о чём-л.;\n<b>2)</b> побочный (operae <i>C</i>); сверхурочный (opus <i>C</i>);\n<b>3)</b> запасный (vasa <i>Cato</i>)."],
  ["supervaco", "<big>supervaco</big>\n\n<b>super–vaco, —, —, are</b>\nбыть излишним <i>AG</i>."],
  ["supervacue", "<big>supervacue</big>\n\n<b>supervacue</b> <i>Vlg, Eccl</i>\n= supervacuo."],
  ["supervacuitas", "<big>supervacuitas</big>\n\n<b>supervacuitas, atis</b> <i>f</i>\nтщета, суетность <i>Vlg</i>."],
  ["supervacuo", "<big>supervacuo</big>\n\n<b>supervacuo</b> <i>adv.</i>\nизлишне, бесполезно <i>PM, Q, Dig</i>."],
  ["supervacuus", "<big>supervacuus</big>\n\n<b>super–vacuus, a, um</b>\nизлишний, ненужный (auctionem facere rerum supervacuarum <i>Pt</i>); бесполезный (praecepta dare scienti supervacuum est <i>Sen</i>): vel ex supervacuo <i>L</i> даже в избытке; in supervacuum бесцельно, зря (tumultuari <i>Sen</i>)."],
  ["supervado", "<big>supervado</big>\n\n<b>super–vado, —, —, ere</b>\n<b>1)</b> переходить, перелезать (ruinas muri <i>L</i>; munimenta <i>L</i>);\n<b>2)</b> проходить, преодолевать (omnes asperitates <i>Sl</i>)."],
  ["supervagor", "<big>supervagor</big>\n\n<b>super–vagor, atus sum, ari</b>\nчрезмерно разрастаться <i>Col</i>."],
  ["supervaleo", "<big>supervaleo</big>\n\n<b>super–valeo, —, —, ere</b>\nбыть весьма могущественным <i>Vlg</i>."],
  ["supervalesco", "<big>supervalesco</big>\n\n<b>super–valesco, —, —, ere</b>\nстановиться ещё могущественнее <i>Vlg</i>."],
  ["supervector", "<big>supervector</big>\n\n<b>super–vector, —, ari</b>\nпроноситься (super aquas <i>Tert</i>)."],
  ["supervehor", "<big>supervehor</big>\n\n<b>super–vehor, vectus sum, vehi</b> <i>depon.</i>\n<b>1)</b> переезжать, переходить (montem <i>Ctl</i>);\n<b>2)</b> огибать, миновать (promunturium Calabriae <i>L</i>); <i>pass.</i> supervectus <i>AG</i> перевезённый."],
  ["supervenio", "<big>supervenio</big>\n\n<b>super–venio, veni, ventum, ire</b>\n<b>1)</b> набежать, нахлынуть: unda supervanit undam <i>H</i> волна набегает на волну || накрывать, покрывать: crura (<i>acc.</i>) loquentis terra supervenit <i>O</i> (Мирра ещё) говорит, но земля (уже) покрыла её ноги; lapso s. <i>V</i> броситься на упавшего (врага);\n<b>2)</b> (неожиданно) приходить, появляться: superveniunt legati <i>QC</i> тут приходят послы; huic laetitiae Quinctius supervenit <i>L</i> среди ликования (аргосцев) вдруг явился Квинкций || наступать, наставать (grata superveniet quae non sperabitur hora <i>H</i>);\n<b>3)</b> (<i>о животных</i>) покрывать <i>Col, PM</i>;\n<b>4)</b> застигать врасплох: munientibus supervenit Marcellus <i>L</i> когда (солдаты Гиппократа) строили укрепления, они были застигнуты врасплох Марцеллом;\n<b>5)</b> присоединяться, прибавляться (febris vulneri supervenit <i>CC</i>);\n<b>6)</b> приходить на помощь (alicui <i>V, QC etc.</i>);\n<b>7)</b> превосходить, затмевать (aliquid aliqua re <i>St</i>);\n<b>8)</b> превышать, длиться дольше (s. quintam et vigesimam lunam <i>Col</i>)."],
  ["superventores", "<big>superventores</big>\n\n<b>superventores, um</b> <i>m</i> [supervenio 4]\nрезервные отряды для внезапных атак <i>Amm</i>."],
  ["superventus", "<big>superventus</big>\n\n<b>I superventus, a, um</b> <i>part. pf. к</i>\nsupervenio.\n\n<b>II superventus, us</b> <i>m</i>\n<b>1)</b> неожиданный приход, внезапное появление <i>PM, T</i>;\n<b>2)</b> <i>воен.</i> внезапное нападение <i>Veg</i>."],
  ["supervestio", "<big>supervestio</big>\n\n<b>super–vestio, —, itum, ire</b>\nоблекать, одевать (aliquem <i>Vlg, Eccl</i>)."],
  ["supervinco", "<big>supervinco</big>\n\n<b>super–vinco, —, —, ere</b>\nпобеждать, одолевать (aliquem <i>Eccl</i>)."],
  ["supervivo", "<big>supervivo</big>\n\n<b>super–vivo, vixi, —, ere</b>\nпереживать (alicui <i>и</i> alicui rei <i>PJ, Ap, Spart etc.</i>): Olympias non diu filiis supervixit <i>Just</i> Олимпиада (<i>дочь Пирра Эпирского</i>) ненадолго пережила своих сыновей."],
  ["supervolito", "<big>supervolito</big>\n\n<b>super–volito, avi, —, are</b>\nпорхать сверху, пролетать: s. tecta alis <i>V</i> порхать над домом; s. alicui <i>T</i> пролетать над кем-л."],
  ["supervolo", "<big>supervolo</big>\n\n<b>super–volo, —, —, are</b>\nлетать над, пролетать сверху (s. totum orbem <i>O</i>): tremebunda supervolat hasta <i>V</i> копьё, дрожа, пролетает над головой (Мага)."],
  ["supervolvo", "<big>supervolvo</big>\n\n<b>super–volvo, volvi, volutum, ere</b>\nперекатываться <i>Col</i>."],
  ["supinalis", "<big>supinalis</big>\n\n<b>supinalis, e</b> [supino 1]\nразрушающий (<i>эпитет Юпитера</i>) <i>Aug</i>."],
  ["supinatio", "<big>supinatio</big>\n\n<b>supinatio, onis</b> <i>f</i>\nвозврат: s. stomachi <i>CA</i> рвота."],
  ["supine", "<big>supine</big>\n\n<b>supine</b> [supinus]\nотвернувшись, <i>т. е.</i> с пренебрежением (beneficium accipere <i>Sen</i>)."],
  ["supinitas", "<big>supinitas</big>\n\n<b>supinitas, atis</b> <i>f</i> [supinus]\nоткинутость: est odiosa omnis s. <i>Q</i> (оратору) ни в коем случае не пристало откидываться назад."],
  ["supino", "<big>supino</big>\n\n<b>supino, avi, atum, are</b> [supinus]\n<b>1)</b> опрокидывать (aliquem in terga <i>St</i>): manus supinata <i>Q</i> рука ладонью вверх; supinatus humi <i>Ap</i> лежащий навзничь на земле;\n<b>2)</b> переворачивать, взрывать, вспахивать (glaebas <i>V</i>);\n<b>3)</b> откидывать назад: nasum nidore supinari <i>H</i> с жадностью вдыхать кухонный чад;\n<b>4)</b> преисполнять гордостью, делать высокомерным (aliquem <i>Sen</i> — <i>v. l.</i>)."],
  ["supinum", "<big>supinum</big>\n\n<b>supinum, i</b> <i>n</i> [supinus]\n(<i>sc.</i> verbum) <i>грам.</i> супин:\n<b>1)</b> <i>позднее название форм <i>acc. и abl.</i> (<i>или</i> loc.) отглагольных существительных, образованных с помощью суффикса -tu (у</i> <i>Q</i> — verbum participiale);\n<b>2)</b> (<i>устаревшее значение</i>) = gerundium (<i>предпол. «двухсторонняя», т. е. глагольно-существительная форма; ср. в</i> supinus 2: carmen supinum)."],
  ["supinus", "<big>supinus</big>\n\n<b>supinus, a, um</b> [<i>одного корня с</i> sub <i>и</i> super]\n<b>1)</b> обращенный вверх, <i>т. е.</i> склонённый назад, лежащий лицом вверх (на спине): supinum caput <i>Q</i> запрокинутая назад голова; supina cathedra <i>PM</i> стул с откинутой назад спинкой; s. sursum in caelum conspicit <i>Pl</i> (кто-л.) лёжа на спине, глядит в небо; tendere supinas manus ad caelum <i>V</i> воздевать руки ладонями вверх к небу (<i>молитвенная поза</i>); supino jactu tela mittere <i>L</i> бросать копья снизу вверх; modo versa modo supina (<i>sc.</i> manu) gesticulari mucrone <i>Pl</i> вращательным движением руки фехтовать мечом;\n<b>2)</b> обращенный (движущийся) назад, обратный (motus corporis <i>C</i>): unda supina <i>O</i> текущая вспять волна; carmen supinum <i>M</i> двухсторонние (обратимые) стихи (читаемые с обоих концов одинаковым размером, <i>напр.</i>: Roma tibi subito motibus ibit amor <i>Sid</i>; <i>такой стих, не всегда имеющий ясный смысл, назывался тж.</i> recurrens, cancrinus, reciprocus, palindromus, serpentinus);\n<b>3)</b> наклонный, отлого спускающийся (collis <i>V</i>; vallis <i>L</i>);\n<b>4)</b> расстилающийся (mare <i>PM</i>; camporum planities <i>Ap</i>);\n<b>5)</b> беззаботный, беспечный, ленивый (animus <i>Ctl</i>; otiosus et s. <i>Q</i>);\n<b>6)</b> задирающий нос, чванный <i>Pers, M</i>."],
  ["supo", "<big>supo</big>\n\n<b>supo</b> <i>v. l.</i>\n= suppo."],
  ["suppactus", "<big>suppactus</big>\n\n<b>suppactus, a, um</b> <i>part. pf. к</i>\nsuppingo."],
  ["suppaenitet", "<big>suppaenitet</big>\n\n<b>sup–paenitet, —, ere</b> <i>impers.</i>\nнемного досадно (aliquem alicujus rei <i>C</i>)."],
  ["suppaetulus", "<big>suppaetulus</big>\n\n<b>sup–paetulus, a, um</b>\nчуть-чуть томный (oculi <i>Vr</i>)."],
  ["suppallidus", "<big>suppallidus</big>\n\n<b>sup–pallidus, a, um</b>\nбледноватый <i>CC</i>."],
  ["suppalpor", "<big>suppalpor</big>\n\n<b>sup–palpor, —, ari</b> <i>depon.</i>\nласкаться, подлаживаться (alicui <i>Pl</i>)."],
  ["suppar", "<big>suppar</big>\n\n<b>sup–par, paris</b> <i>adj.</i>\nпочти равный: huic aetati suppares <i>C</i> почти современники этой эпохи; supparis aevi esse alicui <i>Aus</i> быть чьим-л. ровесником; musicae suppari gressu <i>Ap</i> в такт музыке."],
  ["supparasitor", "<big>supparasitor</big>\n\n<b>sup–parasitor, —, ari</b>\nвести себя как парасит, подлаживаться (alicui <i>Pl</i>)."],
  ["supparatura", "<big>supparatura</big>\n\n<b>supparatura, ae</b> <i>f</i> [supparo II]\nвосстановление (generis <i>Tert</i>)."],
  ["supparo", "<big>supparo</big>\n\n<b>I sup–paro, —, —, are</b> [paro I]\nприлаживать, присоединять (aliquid alicui rei <i>Tert</i>).\n\n<b>II sup–paro, —, —, are</b> [paro II]\nделать почти равным (aliquid alicui rei <i>Tert</i>)."],
  ["supparum", "<big>supparum</big>\n\n<b>supparum (sipharum), i</b> <i>n</i> <i>и</i> <b>supparus, i</b> <i>m</i>\nсуппар:\n<b>1)</b> верхний парус, <i>род брамселя</i> <i>Lcn</i>;\n<b>2)</b> <i>часть женской одежды</i> (<i>с короткими рукавами, надевавшаяся поверх subucula</i>) <i>Pl, Vr, Tert</i>;\n<b>3)</b> флаг <i>или</i> вымпел <i>Tert</i>."],
  ["suppateo", "<big>suppateo</big>\n\n<b>sup–pateo, —, —, ere</b>\nпростираться, тянуться внизу (fovea, que fruticibus imis suppatet <i>Ap</i>): campi suppatentes <i>Ap</i> расстилающиеся внизу поля."],
  ["suppedaneum", "<big>suppedaneum</big>\n\n<b>sup–pedaneum, i</b> <i>n</i> (<i>sc.</i> scabellum)\nскамеечка для ног, подножие <i>Vlg, Lact</i>."],
  ["suppeditatio", "<big>suppeditatio</big>\n\n<b>suppeditatio, onis</b> <i>f</i> [suppedito]\nизобилие, избыток (bonorum <i>C</i>)."],
  ["suppedito", "<big>suppedito</big>\n\n<b>suppedito, avi, atum, are</b> [sub + pes]\n<b>1)</b> иметься в достаточном количестве, вдоволь, вполне хватать: ne charta quidem tibi suppeditat <i>C</i> у тебя и бумаги не хватает; cui si vita suppeditavisset, consul factus esset <i>C</i> если бы он (Т. Торкват) достаточно долго прожил, он стал бы консулом; undique mihi suppeditat, quod dicam <i>C</i> у меня вполне достаточно что сказать;\n<b>2)</b> изобиловать (aliqua re): omnia, quibus nos suppeditamus, eget ille <i>C</i> всё, что у нас в избытке (вдоволь), (и чего) у него (Катилины) нет; gaudiis gaudium suppeditat <i>Pl</i> одна радость рождает другие, <i>т. е.</i> радостям нет конца; dicere non suppeditat <i>Lcr</i> трудно (невозможно) сказать;\n<b>3)</b> доставлять, снабжать, давать вдоволь (frumentum toti Italiae <i>C</i>): parare, quae suppeditent ad victum <i>C</i> доставлять всё необходимое для пропитания; s. sumptum <i>C</i> <i>или</i> sumptibus (alicui) <i>Ter</i> давать кому-л. на расходы; s. omnium rerum abundantiam <i>C</i> снабжать в изобилии всем; unde ei suppeditata doctrina est <i>Ap</i> оттуда он и получил своё образование; vix arvis suppeditatus <i>Lcr</i> едва могущий прокормиться тем, что даёт ему поле;\n<b>4)</b> быть пригодным, подходить (для чего-л.): s. labori <i>Pl</i> справиться с работой."],
  ["suppedo", "<big>suppedo</big>\n\n<b>suppedo, —, —, ere</b> <i>C demin. к</i>\npedo II."],
  ["suppellex", "<big>suppellex</big>\n\n<b>suppellex</b> <i>Pl v. l.</i>\n= supellex."],
  ["suppernatus", "<big>suppernatus</big>\n\n<b>suppernatus, a, um</b> [sub + perna]\nподрубленный: alnus suppernata securi <i>Ctl</i> ольха, срубленная топором."],
  ["supperturbor", "<big>supperturbor</big>\n\n<b>sup–perturbor, —, ari</b>\nнемного смутиться <i>Sen</i> (<i>v. l.</i>)."],
  ["suppes", "<big>suppes</big>\n\n<b>sup–pes, pedis</b>\nс вывороченными ступнями <i>Pt</i>."],
  ["suppetiae", "<big>suppetiae</big>\n\n<b>suppetiae, arum</b> (<i>встреч. тк. в</i> <i>nom.</i> <i>и</i> <i>acc.</i>) <i>f</i> [suppeto]\nпомощь, поддержка: auxilia mihi et s. sunt domi <i>Pl</i> есть у меня дома и помощь и поддержка; suppetias на помощь, на выручку (venire <i>bAfr</i> <i>и</i> advenire <i>Pl</i>, proficisci, mittere <i>bAfr</i>)."],
  ["suppetior", "<big>suppetior</big>\n\n<b>suppetior, atus sum, ari</b> <i>depon.</i> [suppetiae]\nидти на помощь, оказывать поддержку <i>Ap</i>."],
  ["suppeto", "<big>suppeto</big>\n\n<b>sup–peto, petivi (petii), petitum, ere</b>\n<b>1)</b> быть в достаточном количестве, иметься вдоволь, хватать: ne pabuli quidem satis magna copia suppetebat <i>Cs</i> не было даже в достаточно большом количестве кормов; quibuscumque vires suppetebant ad arma ferenda, praesto fuere <i>L</i> у кого только хватало сил носить оружие, явились в полной готовности; si Vita suppetet <i>C</i> если хватит жизни; s. cotidianis sumptibus <i>C</i> хватать на повседневные расходы;\n<b>2)</b> быть в распоряжении, под рукой: crimina suppetunt alicui <i>C</i> кто-л. располагает достаточным количеством обвинительных материалов;\n<b>3)</b> соответствовать, находиться в соответствии: utinam, quae dicis, dictis facta suppetant <i>Pl</i> о, если бы сказанным тобою словам соответствовали дела, <i>т. е.</i> если бы случилось так, как ты говоришь; s. doloribus <i>H</i> становиться жертвой страданий;\n<b>4)</b> <i>юр.</i> выступать от чьего-л. имени <i>Dig</i>."],
  ["suppilo", "<big>suppilo</big>\n\n<b>suppilo, —, atum, are</b> [sub+ *pilo, <i>ср.</i> compilo]\nтайком уносить, красть (aliquid alicui <i>Pl</i>); обкрадывать (aliquem <i>Pl</i>)."],
  ["suppingo", "<big>suppingo</big>\n\n<b>sup–pingo, pegi, pactum, ere</b> [pango]\nснизу приколачивать, прибивать (fulmentas soccis <i>Pl</i>): auro suppactum solum <i>Pl</i> подошва, подбитая золотом."],
  ["suppinguis", "<big>suppinguis</big>\n\n<b>sup–pinguis, e</b>\nнемного сальный (cutis <i>CC</i>)."],
  ["supplantatio", "<big>supplantatio</big>\n\n<b>supplantātio, ōnis</b> <i>f</i> [supplanto]\nподножка, <i>перен.</i> происки, козни, интриги <i>Vlg, Eccl</i>."],
  ["supplantator", "<big>supplantator</big>\n\n<b>supplantātor, ōris</b> <i>m</i>\nдающий подножку, <i>перен.</i> интриган <i>Eccl</i>."],
  ["supplanto", "<big>supplanto</big>\n\n<b>sup–planto, āvī, ātum, āre</b> [planta II]\n<b>1)</b> подставлять ногу, валить, подставляя ногу (aliquem <i>C, Sen</i>);\n<b>2)</b> опрокидывать (vites in terram <i>PM</i>);\n<b>3)</b> искажать, извращать, коверкать (verba <i>Pers</i>)."],
  ["supplau-", "<big>supplau-</big>\n\n<b>supplau-</b> <i>v. l.</i>\n= supplo-."],
  ["supplementum", "<big>supplementum</big>\n\n<b>supplementum, i</b> <i>n</i> [suppleo]\n<b>1)</b> восполнение (gregis <i>Col</i>); пополнение, комплектование <i>VP, Just</i>; <i>воен.</i> новый набор: legiones supplemento explere <i>L</i> укомплектовать легионы новым пополнением; supplementi nomine <i>Cs</i> под видом комплектования;\n<b>2)</b> людское пополнение, подкрепление (supplementa distribuere <i>L</i>);\n<b>3)</b> средство, орудие (artis magicae <i>Ap</i>)."],
  ["suppleo", "<big>suppleo</big>\n\n<b>sup–pleo, plevi, pletum, ere</b>\nпополнять (bibliothecam <i>C</i>); комплектовать (legionem <i>L</i>): naves remigio s. <i>L</i> комплектовать суда гребцами || заполнять, восполнять, восстанавливать: inania moenia s. <i>O</i> заселить опустевший город; referendis praeteritis verbis scriptum s. <i>C</i> восстановлением пропущенных слов восполнить текст || наполнять (sanguine venas <i>O</i>): suppletae lucernae <i>Prp</i> заправленные (маслом) светильники || восстанавливать, возмещать (damna incendiorum <i>Su</i>); замещать (locum parentis alicui <i>Sen</i>); присоединять (вдобавок), добавлять (ceteros <i>C</i>; quantumcumque mensurae deest <i>Col</i>); написать в дополнение (Gallici belli imperfectum librum s. <i>Su</i>)."],
  ["suppletio", "<big>suppletio</big>\n\n<b>suppletio, onis</b> <i>f</i>\nвосполнение <i>Eccl</i>."],
  ["suppletus", "<big>suppletus</big>\n\n<b>suppletus, a, um</b> <i>part. pf. к</i>\nsuppleo."],
  ["supplex", "<big>supplex</big>\n\n<b>sup–plex, plicis</b> <i>adj.</i> [sub + plico <i>или</i> placo]\nумоляющий, молящий (vox, verba <i>C</i>): s. alicui <i>C</i> умоляющий кого-л.; s. misericordiae alicujus <i>C</i> молящий о чьём-л. милосердии; dona supplicia <i>V</i> умилостивительные дары."],
  ["supplicamentum", "<big>supplicamentum</big>\n\n<b>supplicamentum, i</b> <i>n</i>\n<b>1)</b> <i>Ap</i> = supplicatio;\n<b>2)</b> кара, возмездие, <i>тж.</i> мучение <i>Tert</i>."],
  ["supplicatio", "<big>supplicatio</big>\n\n<b>supplicatio, onis</b> <i>f</i> [supplico]\n<b>1)</b> суппликация, общее молебствие (<i>умилостивительное или благодарственное</i>): supplicationem decernere <i>C, Cs, L, T</i>, constituere <i>C</i> <i>или</i> indicere <i>L</i> назначить суппликацию; s. viginti dierum <i>или</i> dies <i>Cs</i> двадцатидневная суппликация;\n<b>2)</b> изъявление покорности <i>Amm</i>."],
  ["supplice", "<big>supplice</big>\n\n<b>supplice</b> <i>Vt</i>\n(<i>v. l.</i>) — suppliciter."],
  ["supplicis", "<big>supplicis</big>\n\n<b>supplicis</b> <i>gen. к</i>\nsupplex."],
  ["suppliciter", "<big>suppliciter</big>\n\n<b>suppliciter</b> [supplex]\nумоляюще, смиренно (loqui <i>Cs</i>); слёзно (petere <i>Pt</i>)."],
  ["supplicium", "<big>supplicium</big>\n\n<b>supplicium, i</b> <i>n</i> [supplico]\n<b>1)</b> коленопреклонение (<i>молящего(ся) или казнимого</i>) <i>L</i>;\n<b>2)</b> покорная просьба, ходатайство (legatos cum suppliciis mittere <i>Sl</i>);\n<b>3)</b> <i>pl.</i> мольба, молитва (Supplicia muliebria <i>Sl</i>);\n<b>4)</b> жертва, жертвоприношение (precibus suppliciisque deos placare <i>L</i>);\n<b>5)</b> казнь, кара (s. exsilii <i>или</i> deportationis <i>CTh</i>): s. ultimum <i>или</i> summum <i>Dig</i> смертная казнь; gravissimum s. ei rei constitutum est <i>Cs</i> за это установлено самое суровое наказание; supplicio aliquem afficere <i>Cs, Su</i>, ad s. aliquem dare <i>Nep</i> (tradere <i>L</i>, dedere <i>Cs</i>) <i>или</i> s. sumere ex, ab <i>и</i> de aliquo <i>Ter, C, T</i> карать <i>или</i> казнить кого-л.; s. (per)solvere <i>C, T</i> (luere <i>T, Just</i>) нести наказание; s. dare alicui <i>Pl, Ter, Nep</i> подвергаться наказанию со стороны кого-л.; ad ultimum s. progredi <i>Cs</i> покончить жизнь самоубийством;\n<b>6)</b> пытка, мучение (cruciatu supplicioque perire <i>C</i>): s. inclusum <i>C</i> пытка заточения;\n<b>7)</b> увечье (причинённое пыткой) (supplicia sua ostendere <i>Vtr</i>): supplicio afficere aliquem <i>QC</i> причинить кому-л. увечье."],
  ["supplico", "<big>supplico</big>\n\n<b>supplico, avi, atum, are</b> [supplex]\n<b>1)</b> становиться на колени, припадать к ногам (alicui <i>Pl, Ter, C etc.</i>; aliquem <i>Acc</i>); коленопреклонённо просить, смиренно умолять, молить (alicui pro aliqua re <i>C, Q</i>);\n<b>2)</b> молиться, возносить моления (diis <i>Sl</i>)."],
  ["supplicue", "<big>supplicue</big>\n\n<b>supplicue</b> <i>Ap</i>\n= suppliciter."],
  ["supplodo", "<big>supplodo</big>\n\n<b>sup–plodo, plosi, plosum, ere</b> [plaudo]\n<b>1)</b> стучать о землю, топать (s. pedem <i>C, Sen</i>);\n<b>2)</b> <i>перен.</i> растаптывать, уничтожать (calumniam <i>Macr</i>);\n<b>3)</b> рукоплескать (alicui <i>Tert</i>)."],
  ["supplosio", "<big>supplosio</big>\n\n<b>supplosio, onis</b> <i>f</i> [supplodo]\nстучание о землю, Топанье (peciis <i>C</i>)."],
  ["suppo", "<big>suppo</big>\n\n<b>suppo, —, —, are</b> [suppus]\nопрокидывать (aliquid <i>Acc</i>)."],
  ["suppoenitet", "<big>suppoenitet</big>\n\n<b>suppoenitet</b> <i>v. l.</i>\n= suppaenitet."],
  ["suppono", "<big>suppono</big>\n\n<b>sup–pono, posui, positum, ere</b>\n<b>1)</b> класть под, подкладывать (ova gallinis <i>C, Vr</i>);\n<b>2)</b> подводить, подставлять (colla oneri <i>O</i>): s. tauros jugo <i>O</i> надевать на быков ярмо; ignem tectis s. <i>O</i> поджигать дома; ignis suppositus cineri <i>H</i> скрытый под пеплом огонь; pecus alicui rei s. <i>O</i> загонять скот под что-л.; s. aliquid caelo <i>H</i> поставить что-л. под открытым небом; suppositae undae <i>O</i> расстилающееся внизу море; plaustro supposuisse rotam <i>Tib</i> поставить повозку на колёса;\n<b>3)</b> погружать (pectora fluminibus <i>O</i>);\n<b>4)</b> закапывать, зарывать: s. aliquid terrae <i>O</i> посадить (посеять) что-л.; aliquem tumulo (terrae, humo) s. <i>O</i> похоронить кого-л.;\n<b>5)</b> прикладывать (снизу), приставлять: s. cultros <i>V</i> приставлять ножи (к горлу жертв), закалывать; s. falcem aristis <i>V</i> жать серпом колосья; se s. criminibus pro aliquo <i>C</i> принять на себя обвинения вместо кого-л.;\n<b>6)</b> подчинять, делать подвластным (aliquid ingenio suo <i>O</i>; suppositus deo <i>O</i>): generi partes s. <i>C</i> подчинять роду (<i>т. е.</i> подводить под род) виды;\n<b>7)</b> ставить внизу, присоединять, добавлять, прилагать (exemplum epistulae <i>C</i>);\n<b>8)</b> ставить ниже, ценить меньше: Latio Samon s. <i>O</i> ставить Самос ниже Латия, <i>т. е.</i> предпочитать Латий Самосу;\n<b>9)</b> подставлять, заменять (aliquem in locum alicujus <i>C</i>, pro aliquo <i>C</i> <i>или</i> alicui <i>Pl, Ter</i>); подменять (puerum <i>Pl</i>);\n<b>10)</b> подделывать: s. testamenta (falsa) <i>C</i> делать подлог в Завещаниях, представлять подложные завещания."],
  ["supportatorium", "<big>supportatorium</big>\n\n<b>supportatorium, i</b> <i>n</i>\nподпора <i>Ambr</i>."],
  ["supporto", "<big>supporto</big>\n\n<b>sup–porto, avi, atum, are</b>\n<b>1)</b> привозить, подвозить (frumentum ex Sequanis <i>Cs</i>); доставлять (commeatum navibus <i>L</i>);\n<b>2)</b> подпирать, поддерживать <i>Eccl</i>."],
  ["suppositicius", "<big>suppositicius</big>\n\n<b>suppositicius, a, um</b> [suppono]\n<b>1)</b> заменяющий: s. sibi ipsi <i>M</i> заменяющий сам себя, <i>т. е.</i> не нуждающийся в замене (Hermes);\n<b>2)</b> подставной, фальшивый (explorator <i>Pl</i>)."],
  ["suppositio", "<big>suppositio</big>\n\n<b>suppositio, onis</b> <i>f</i> [suppono]\n<b>1)</b> подкладывание (ovorum <i>Col</i>);\n<b>2)</b> подмена (pueri <i>Pl</i>);\n<b>3)</b> <i>юр.</i> заклад <i>CJ</i>."],
  ["suppositorium", "<big>suppositorium</big>\n\n<b>suppositorium, i</b> <i>n</i>\nподставка <i>или</i> поднос <i>Eccl</i>."],
  ["suppositorius", "<big>suppositorius</big>\n\n<b>suppositorius, a, um</b> [suppono]\nнаходящийся внизу: cella suppositoria <i>Vop</i> тёплое помещение в банях (= tepidaria), <i>так как в римских банях оно находилось под холодным</i> (<i>frigidaria</i>)."],
  ["suppositus", "<big>suppositus</big>\n\n<b>suppositus, a, um</b> <i>part. pf. к</i>\nsuppono."],
  ["supposivi", "<big>supposivi</big>\n\n<b>supposivi</b> <i>Pl</i>\n= supposui (<i>pf. к</i> suppono)."],
  ["suppostrix", "<big>suppostrix</big>\n\n<b>suppostrix, icis</b> <i>f</i> [suppono]\nмошенница: s. puerorum <i>Pl</i> мошеннически подменивающая детей."],
  ["suppostus", "<big>suppostus</big>\n\n<b>suppostus, a, um</b> <i>V, Sil</i>\n= suppositus."],
  ["suppraefectus", "<big>suppraefectus</big>\n\n<b>sup–praefectus, a, um</b>\nвторостепенный, побочный (venti <i>Sen</i> — <i>v. l.</i>)."],
  ["suppressi", "<big>suppressi</big>\n\n<b>suppressi</b> <i>pf. к</i>\nsupprimo."],
  ["suppressio", "<big>suppressio</big>\n\n<b>suppressio, onis</b> <i>f</i> [supprimo]\n<b>1)</b> давление: suppressiones nocturnae <i>PM</i> ночные кошмары;\n<b>2)</b> утайка, присвоение, расхищение: suppressiones judiciales <i>C</i> присвоение судебных издержек;\n<b>3)</b> подавление (veritatis <i>Eccl</i>)."],
  ["suppressor", "<big>suppressor</big>\n\n<b>suppressor, oris</b> <i>m</i> [supprimo 4]\nукрыватель (alieni servi <i>Dig</i>)."],
  ["suppressus", "<big>suppressus</big>\n\n<b>suppressus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> supprimo;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> пониженный, приглушённый (murmur <i>VF</i>): suppressa voce dicere <i>C</i> тихо говорить;\n<b>2)</b> укороченный, (как бы) срезанный (mentum <i>Vr</i>)."],
  ["supprimo", "<big>supprimo</big>\n\n<b>sup–primo, pressi, pressum, ere</b> [sub + premo]\n<b>1)</b> погружать (в воду), топить (naves <i>L, Just</i>);\n<b>2)</b> сдерживать, задерживать (hostem <i>Cs</i>; impetum <i>L</i>); (при)останавливать (fugam <i>O</i>; sanguinem <i>CC</i>); подавлять, успокаивать, унимать (aegritudinem <i>C</i>; iram <i>L</i>; furorem <i>Pt</i>): s. lora manu <i>O</i> натянуть поводья; s, iter <i>Cs</i> прекратить (дальнейшее) движение, остановиться; s. vocem <i>O</i> умолкать, <i>но</i>: vox suppressa <i>O</i> тихий голос, шёпот;\n<b>3)</b> скрывать, замалчивать, обходить молчанием (senatus consultum <i>L</i>; nomen alicujus <i>T</i>);\n<b>4)</b> утаивать (pecuniam <i>C</i>; testamentum <i>Su</i>)."],
  ["suppromus", "<big>suppromus</big>\n\n<b>sup–promus, i</b> <i>m</i>\nпомощник ключника <i>или</i> эконома <i>Pl</i>."],
  ["suppudet", "<big>suppudet</big>\n\n<b>sup–pudet, —, —, ere</b> <i>impers.</i>:\nsuppudet aliquem alicujus rei <i>C</i> кому-л. немного Стыдно за что-л."],
  ["suppurantia", "<big>suppurantia</big>\n\n<b>suppurantia, ium</b> <i>n</i>\nгнойники <i>PM</i>."],
  ["suppurata", "<big>suppurata</big>\n\n<b>suppurata, orum</b> <i>n</i>\nгноящиеся язвы <i>PM</i>."],
  ["suppuratio", "<big>suppuratio</big>\n\n<b>suppuratio, onis</b> <i>f</i> [suppuro]\nнагноение, гнойник, нарыв <i>CC, Sen, PM etc.</i>"],
  ["suppuratorius", "<big>suppuratorius</big>\n\n<b>suppuratorius, a, um</b>\nвызывающий нагноение (medicamenta <i>PM</i>)."],
  ["suppuro", "<big>suppuro</big>\n\n<b>suppuro, avi, atum, are</b> [sub + pus]\n<b>1)</b> гноиться (sub carne <i>Cato</i>): suppuratus гноящийся (aures <i>PM</i>);\n<b>2)</b> вызывать расстройство: voluptates corpori ingestae suppurant <i>Sen</i> тонкие яства вызывают расстройство в организме; infame lucrum quandoque suppuraturum <i>Sen</i> бесчестная нажива, которая со временем станет разъедать словно гнойник; suppurata tristitia <i>Sen</i> <i>и</i> euppuratus dolor <i>QC</i> гложущая скорбь;\n<b>3)</b> порождать (словно гной) (scorpium terra suppurat <i>Tert</i>)."],
  ["suppus", "<big>suppus</big>\n\n<b>suppus, a, um</b> (= supinus)\nопрокинутый навзничь <i>Lcr</i>."],
  ["supputarius", "<big>supputarius</big>\n\n<b>supputarius</b> <i>v. l.</i>\n= supputatorius."],
  ["supputatio", "<big>supputatio</big>\n\n<b>supputatio, onis</b> <i>f</i> [supputo II]\nисчисление, расчёт (frugum <i>Vlg</i>; temporum <i>Aug</i>)."],
  ["supputator", "<big>supputator</big>\n\n<b>supputator, oris</b> <i>m</i>\nсчётчик <i>Eccl</i>."],
  ["supputatorius", "<big>supputatorius</big>\n\n<b>supputatorius, a, um</b>\nсчитающий, счётный (gesticuli digitorum <i>Tert</i>)."],
  ["supputo", "<big>supputo</big>\n\n<b>I sup–puto, avi, atum, are</b> [puto I]\nобрезывать, подрезать (semina fruticantia <i>PM</i>; oleas <i>Cato</i>).\n\n<b>II sup–puto, avi, atum, are</b> [puto II]\nвычислять, исчислять (numeros mensurasque <i>Sen</i>): s. articulis <i>O</i> считать по пальцам."],
  ["supra", "<big>supra</big>\n\n<b>I supra</b> <i>adv.</i>\n<b>1)</b> сверху, вверху: jumenta in flumine s. atque infra constituere <i>Cs</i> расставить вьючных животных вверху и внизу по течению реки; mare quod s. alluit <i>V</i> (= mare Superum) Адриатическое море;\n<b>2)</b> наверху (<i>относительно подземного мира</i>), на земле (omnia quae s. sunt <i>C</i>);\n<b>3)</b> выше: toto vertice s. esse <i>V</i> быть на голову выше (окружающих); ut s. dixi <i>C etc.</i> как я выше (раньше, уже) сказал; s, repetere <i>Sl</i> вернуться назад (в повествовании);\n<b>4)</b> сверх, больше: tantum, ut nihil s. possit (<i>sc.</i> addi) <i>C</i> столько, что больше невозможно; septuaginta et s. <i>Pt</i> семьдесят с лишком; s. adjicere <i>C</i> надбавлять; nihil s. deos lacessere <i>H</i> ничего больше не требовать от богов; s. quam cuiquam credibile est <i>Sl</i> больше, чем кто-л. может себе представить.\n\n<b>II supra</b> <i>praep. cum acc.</i>\n1) над, на, поверх, выше (s. eum locum <i>Cs</i>): exire s. terram <i>C</i> выйти (подняться) на поверхность земли; accumbere s. aliquem <i>C</i> возлежать (за столом) выше кого-л.; esse s. caput <i>Sl, L</i> висеть над головой, угрожать <i>или</i> <i>C</i> сидеть на шее; s. nos fortuna negotia curat <i>Pt</i> минуя нас судьба вершит дела;\n<b>2)</b> по ту сторону, за: s. ripam Rheni <i>PM</i> по ту сторону Рейна; s, Suessulam <i>L</i> выше, <i>т. е.</i> дальше Свессулы, за Свессулой;\n<b>3)</b> до, прежде, раньше: s. hanc memoriam <i>Cs</i> до нашего времени; s. septingentesimum annum <i>L</i> 700 лет тому назад;\n<b>4)</b> сверх, свыше: s. quattuor milia hominum <i>L</i> свыше 4 000 человек; s. bonum atque honestum <i>Sl</i> более, чем хорошо и благородно; s. hominem (<i>или</i> hominis fortunam) <i>C</i> выше человека; s. Coclites Muciosque id facinus est <i>L</i> этот подвиг выше (деяний) Коклитов и Муциев; s. morem <i>V</i> больше обыкновенного;\n<b>5)</b> во главе: s. bibliothecam esse <i>Vtr</i> управлять библиотекой; aliquem s. somnum habere <i>QC</i> иметь кого-л. для охраны сна (ночного покоя);\n<b>6)</b> во время: s. cenam <i>QC</i> за столом."],
  ["suprafatus", "<big>suprafatus</big>\n\n<b>supra–fatus, a, um</b> [for]\nвышеупомянутый <i>Sid</i>."],
  ["supragravo", "<big>supragravo</big>\n\n<b>supra–gravo, —, —, ire</b>\nперегружать <i>Tert</i>."],
  ["supralat-", "<big>supralat-</big>\n\n<b>supralat-</b> <i>rhH, C</i>\n= superlat-."],
  ["suprapositio", "<big>suprapositio</big>\n\n<b>supra–positio, onis</b> <i>f</i> <i>мед.</i>\nобострение, пароксизм <i>CA</i>."],
  ["suprascando", "<big>suprascando</big>\n\n<b>supra–scando, —, —, ere</b>\nпереходить, переступать (fines <i>L</i>)."],
  ["supravivo", "<big>supravivo</big>\n\n<b>supra–vivo, —, —, ere</b>\nпережить (alicui <i>Spart</i>)."],
  ["suprema", "<big>suprema</big>\n\n<b>suprema, orum</b> <i>n</i> [supremus]\n<b>1)</b> последние минуты, смертный чае (alicujus <i>Q, T</i>);\n<b>2)</b> последние почести, последний долг: s. solvere alicui <i>T</i> отдать кому-л. последний долг;\n<b>3)</b> последняя воля, завещание <i>T, Dig</i>;\n<b>4)</b> Останки <i>Amm, Sol</i>."],
  ["supremitas", "<big>supremitas</big>\n\n<b>supremitas, atis</b> <i>f</i>\n<b>1)</b> конец, кончик (naris <i>Amm</i>);\n<b>2)</b> предел (жизни), кончина (sepultura supremitatis honor est <i>Amm</i>)."],
  ["supremo", "<big>supremo</big>\n\n<b>supremo</b> <i>PM adv.</i>\n= supremum I."],
  ["supremum", "<big>supremum</big>\n\n<b>I supremum</b> <i>adv.</i> [supremus]\n<b>1)</b> в последний раз (conspicere aliquid <i>O</i>);\n<b>2)</b> навсегда (dividi ab aliquo <i>T</i>).\n\n<b>II supremum, i</b> <i>n</i> [supremus]\nпоследняя черта, крайний предел, крайность: ventum ad s. est <i>V</i> дело дошло до крайности, <i>т. е.</i> здесь — предел."],
  ["supremus", "<big>supremus</big>\n\n<b>supremus, a, um</b> [<i>superl. к</i> superus, <i>наряду с</i> summus]\n<b>1)</b> высший, высочайший, всевышний (Juppiter <i>Pl, H</i>); верхний: supremi montes <i>Lcr, V</i> вершины гор;\n<b>2)</b> крайний, последний (nox <i>V</i>; verba <i>T</i>): suprema spes <i>Lcn</i> последняя надежда; supremo sole <i>H</i> на закате; supremum certamen <i>L</i> последнее (решающее) сражение; dicere suprema voce <i>O</i> сказать на прощание; supremam manum imponere alicui rei <i>O</i> завершить что-л.; supremis suis annis <i>PM</i> на склоне жизни;\n<b>3)</b> последний (предсмертный) (oscula <i>O</i>); смертный, погребальный (funera <i>O</i>): s. dies <i>C</i> <i>и</i> supremum tempus <i>H</i> день смерти <i>или</i> погребения; supremi tori <i>O</i> смертный одр; supremum ferrum <i>QC</i> смертоносное оружие; suprema sors <i>V</i> гибель; supremum diem obire <i>Nep</i> скончаться; ore supremo vocare <i>O</i> звать умирающими устами; suprema judicia <i>Q</i> предсмертная (последняя) воля, завещание; supremi ignes <i>O</i> погребальный костёр; s. honor <i>V</i> (<i>или</i> suprema officia <i>T</i>, suprema cura <i>Su, PM</i>) погребальные почести; supremi tituli <i>PJ</i> надгробная надпись;\n<b>4)</b> чрезвычайный, крайний (macies <i>V</i>); жесточайший (supplicium <i>C</i>)."],
  ["supter", "<big>supter</big>\n\n<b>supter</b> <i>v. l.</i>\n= subter."],
  ["suptil-", "<big>suptil-</big>\n\n<b>suptil-</b> <i>v. l.</i>\n= subtil-."],
  ["Sur-", "<big>Sur-</big>\n\n<b>Sur-</b> <i>Lcr</i>\n= Syr-."],
  ["sur-", "<big>sur-</big>\n\n<b>sur-</b>\n<i>приставка</i>= sub- <i>перед начальным</i> r <i>основного слова.</i>"],
  ["sura", "<big>sura</big>\n\n<b>sura, ae</b> <i>f</i>\n<b>1)</b> икра (ноги) <i>Pl, C, V, H etc.</i>;\n<b>2)</b> малая берцовая кость <i>CC</i>."],
  ["surclus", "<big>surclus</big>\n\n<b>surclus</b> <i>Vr</i>\n= surculus."],
  ["surcula", "<big>surcula</big>\n\n<b>surcula, ae</b> <i>f</i>\n<i>сорт винограда</i> <i>PM</i>."],
  ["surculaceus", "<big>surculaceus</big>\n\n<b>surculaceus, a, um</b>\nдеревянистый (durities <i>PM</i>)."],
  ["surcularis", "<big>surcularis</big>\n\n<b>surcularis, e</b>\nгодный для взращивания побегов (terra <i>Col</i>)."],
  ["surcularius", "<big>surcularius</big>\n\n<b>surcularius, a, um</b> [surculus]\n<b>1)</b> живущий среди сучьев (cicada <i>PM</i>);\n<b>2)</b> поросший молодыми деревцами (ager <i>Vr</i>)."],
  ["surculo", "<big>surculo</big>\n\n<b>surculo, —, —, are</b> [surculus]\nочищать от сучков, подрезывать (plantas <i>Col</i>)."],
  ["surculose", "<big>surculose</big>\n\n<b>surculose</b> [surculosus]\nкак дерево, подобно древесине (arescere <i>PM</i>)."],
  ["surculosus", "<big>surculosus</big>\n\n<b>surculosus, a, um</b> [surculus]\nдеревянистый, одеревенелый (radix <i>PM</i>)."],
  ["surculus", "<big>surculus</big>\n\n<b>surculus, i</b> <i>m</i> [<i>demin. к</i> surus]\n<b>1)</b> сук, сучок, побег <i>V, Cato, PM etc.</i>;\n<b>2)</b> черенок (<i>для прививки</i>) <i>C</i>;\n<b>3)</b> саженец <i>C, Ap</i>;\n<b>4)</b> заноза, колючка, шип (surculum extrahere <i>PM</i>);\n<b>5)</b> палочка <i>AG</i>;\n<b>6)</b> деревцо <i>или</i> куст <i>Col, CC, Ap</i>."],
  ["surdaster", "<big>surdaster</big>\n\n<b>surdaster, tra, trum</b> [surdus]\nглуховатый, тугоухий <i>C, Aug</i>."],
  ["surdesco", "<big>surdesco</big>\n\n<b>surdesco, —, —, ere</b> [surdus]\nглохнуть <i>Aug</i>."],
  ["surditas", "<big>surditas</big>\n\n<b>surditas, atis</b> <i>f</i> [surdus]\nглухота <i>C, CC</i>."],
  ["surdo", "<big>surdo</big>\n\n<b>surdo, —, —, are</b> <i>Calp</i>\n(<i>v. l.</i>) = exsurdo."],
  ["surdus", "<big>surdus</big>\n\n<b>surdus, a, um</b>\n<b>1)</b> глухой (homo <i>Pl etc.</i>; aures <i>Tib</i>): surdo narrare <i>Ter</i> (canere, dicere <i>L</i>) <i>и</i> surdo asello fabellam narrare <i>погов. H</i> = бросать слова на ветер;\n<b>2)</b> невосприимчивый, равнодушный, бесчувственный (mens <i>O</i>); невнемлющий (dii in vota surdi <i>O</i>): s. ad aliquid <i>или</i> alicui rei <i>L, O etc.</i>, <i>реже</i> alicujus rei <i>Col, Sil</i> глухой к чему-л. || непонимающий (in Graeco sermone <i>C</i>); неуслышанный (vota <i>Pers</i>);\n<b>3)</b> глухо звучащий (locus <i>Vtr</i>); глухой, неслышный, беззвучный (vox <i>Q</i>);\n<b>4)</b> немой, безмолвный (lyra <i>Prp</i>);\n<b>5)</b> невоспетый, безвестный (opus <i>St</i>; nomen <i>Sil</i>);\n<b>6)</b> тайный (surdo verbere caedere <i>J</i>);\n<b>7)</b> слабый, неясный, тусклый, вялый (color <i>PM</i>): spirare surdum <i>Pers</i> издавать слабый запах;\n<b>8)</b> пустой, бессмысленный (res surdae ac sensu carentes <i>PM</i>)."],
  ["surena", "<big>surena</big>\n\n<b>surena, ae</b> <i>m</i>\nпервый министр (<i>парфянских царей</i>) <i>T, Amm</i>."],
  ["surgedum", "<big>surgedum</big>\n\n<b>surge–dum</b> [surgo]\nвставай же <i>Pl</i>."],
  ["surgo", "<big>surgo</big>\n\n<b>surgo</b> <i>v. l.</i>\n= subrigo."],
  ["surio", "<big>surio</big>\n\n<b>surio, —, —, ire</b>\n(<i>о самцах</i>) находиться в состоянии полового возбуждения <i>Ap, Eccl</i>."],
  ["surpere", "<big>surpere</big>\n\n<b>surpere</b> <i>Lcr</i>\n= subripere (<i>см.</i> subripio)."],
  ["surpiculus", "<big>surpiculus</big>\n\n<b>I surpiculus, a, um</b> <i>v. l.</i>\n= scirpiculus I.\n\n<b>II surpiculus</b> <i>и</i> <b>scirpiculus, i</b> <i>m</i>\nплетёная корзиночка, плетёнка <i>LM, Pl, Prp</i>."],
  ["surpite", "<big>surpite</big>\n\n<b>surpite</b> <i>H</i>\n= subripite (<i>2 л. pl. imper. к</i> subripio). surpui <i>Pl, H</i> = subripui (<i>pf. к</i> subripio)."],
  ["surrancidus", "<big>surrancidus</big>\n\n<b>surrancidus, a, um</b> <i>v. l.</i>\n= subrancidus."],
  ["surraucus", "<big>surraucus</big>\n\n<b>surraucus, a, um</b> <i>v. l.</i>\n= subraucus."],
  ["surrectio", "<big>surrectio</big>\n\n<b>I surrectio, onis</b> <i>f</i> [subrigo]\nвоскресение <i>Eccl</i>.\n\n<b>II surrectio</b> <i>v. l.</i>\n= subrectio."],
  ["surrectito", "<big>surrectito</big>\n\n<b>surrectito</b> <i>v. l.</i>\n= subrectito."],
  ["surrectus", "<big>surrectus</big>\n\n<b>surrectus, a, um</b> <i>part. pf. к</i>\nsurgo (subrigo)."],
  ["surrefectus", "<big>surrefectus</big>\n\n<b>surrefectus, a, um</b> <i>v. l.</i>\n= subrefectus."],
  ["surremigo", "<big>surremigo</big>\n\n<b>surremigo</b> <i>v. l.</i>\n= subremigo."],
  ["Surrentini", "<big>Surrentini</big>\n\n<b>Surrentini, orum</b> <i>m</i>\nжители города Surrentum <i>L</i>."],
  ["Surrentinus", "<big>Surrentinus</big>\n\n<b>Surrentinus, a, um</b> [Surrentum]\nсуррентский (vina <i>PM, M</i>; colles <i>O</i>)."],
  ["Surrentum", "<big>Surrentum</big>\n\n<b>Surrentum, i</b> <i>n</i>\nСуррент, <i>приморский город в Кампании, к юго-вост. от Неаполя</i> (<i>ныне</i> Sorrento) <i>T, PM, Sil</i>."],
  ["surrepo", "<big>surrepo</big>\n\n<b>surrepo</b> <i>v. l.</i>\n= subrepo."],
  ["surrepsit", "<big>surrepsit</big>\n\n<b>surrepsit</b> <i>Pl</i>\n= surripuerit (<i>pf. conjct. к</i> surripio)."],
  ["surrept-", "<big>surrept-</big>\n\n<b>surrept-</b> <i>v. l.</i>\n= subrept-."],
  ["surreptus", "<big>surreptus</big>\n\n<b>surreptus, a, um</b> <i>part. pf. к</i>\nsurrepo."],
  ["surrexe", "<big>surrexe</big>\n\n<b>surrexe</b> <i>H</i>\n= surrexisse (<i>inf. pf. к</i> surgo)."],
  ["surrexi", "<big>surrexi</big>\n\n<b>surrexi</b> <i>pf. к</i>\nsurgo."],
  ["surrid-", "<big>surrid-</big>\n\n<b>surrid-</b> <i>v. l.</i>\n= subrid-."],
  ["surrigo", "<big>surrigo</big>\n\n<b>sur–rigo</b> <i>v. l.</i>\n= subrigo."],
  ["surringor", "<big>surringor</big>\n\n<b>surringor</b> <i>v. l.</i>\n= subringor."],
  ["surripio", "<big>surripio</big>\n\n<b>surripio</b> <i>v. l.</i>\n= subripio."],
  ["surrogo", "<big>surrogo</big>\n\n<b>surrogo</b> <i>v. l.</i>\n= subrogo."],
  ["surrostrani", "<big>surrostrani</big>\n\n<b>surrostrani</b> <i>v. l.</i>\n= subrostrani."],
  ["surrot-", "<big>surrot-</big>\n\n<b>surrot-</b> <i>v. l.</i>\n= subrot-."],
  ["surru-", "<big>surru-</big>\n\n<b>surru-</b> <i>v. l.</i>\n= subru-."],
  ["sursum", "<big>sursum</big>\n\n<b>sursum</b> [<i>из</i> sub + versum]\n<b>1)</b> вверх, наверх: s. deorsum <i>Ter, C</i> вверх и вниз; omnia s. deorsum versare <i>Sen</i> перевернуть всё вверх дном; s. versus <i>Vr, C etc.</i> вверх;\n<b>2)</b> вверху, наверху <i>C, Pt etc.</i>"],
  ["sursus", "<big>sursus</big>\n\n<b>sursus</b> <i>Lcr</i>\n= sursum."],
  ["surus", "<big>surus</big>\n\n<b>surus, i</b> <i>m</i>\nшест, кол <i>Enn, Vr</i>."],
  ["sus", "<big>sus</big>\n\n<b>I sus, suis</b> (<i>арх. gen.</i> <b>sueris</b>) (<i>gen. pl.</i> suum, <i>dat. pl.</i> subus, <i>реже</i> suibus) <i>m, f</i>\nсвинья; (<i>тж.</i> s. masculus <i>L</i>) кабан, вепрь <i>Vr, C, PM etc.</i>: s. Minervam (<i>sc.</i> docet) <i>погов. C</i> = яйцо курицу учит.\n\n<b>II sus-</b> [<i>из</i> *subs]\n<i>приставка</i>= sub- <i>перед начальными</i> c, p, t <i>основного слова, напр.</i>: sus–cipio, sus–pendo, sus–tineo."],
  ["Susa", "<big>Susa</big>\n\n<b>Susa, orum</b> <i>n</i>\nСузы:\n<b>1)</b> <i>главн. город персидской области Сузианы</i> (<i>ныне</i> Хузистана), <i>резиденция персидских царей</i> (<i>начиная с Кира</i>) <i>PM, QC, Prp</i>;\n<b>2)</b> <i>PM</i> = Susiane."],
  ["suscenseo", "<big>suscenseo</big>\n\n<b>sus–censeo</b> (<i>v. l.</i> <b>succenseo</b>), <b>censui, censum, ere</b>\nгневаться, быть раздражённым, сердиться (alicui <i>C etc.</i>; aliquid <i>Ter etc.</i>): peccata non suscensenda sunt <i>AG</i> (если верно стоическое учение о неотвратимости судьбы, то) проступки не должны вызывать гнева."],
  ["suscensio", "<big>suscensio</big>\n\n<b>suscensio, onis</b> <i>f</i> [suscenseo]\nгнев <i>Symm</i>."],
  ["suscepi", "<big>suscepi</big>\n\n<b>suscepi</b> <i>pf. к</i>\nsuscipio."],
  ["susceptio", "<big>susceptio</big>\n\n<b>susceptio, onis</b> <i>f</i> [suscipio]\nпринятие на себя (laborum, causae <i>C</i>); принятие, приём (peregrinorum <i>Hier</i>)."],
  ["suscepto", "<big>suscepto</big>\n\n<b>suscepto, avi, —, are</b> [<i>intens. к</i> suscipio]\nпредпринимать <i>Ap</i> (<i>v. l.</i>)."],
  ["susceptor", "<big>susceptor</big>\n\n<b>susceptor, oris</b> <i>m</i> [suscipio]\n<b>1)</b> предприниматель <i>Just</i>;\n<b>2)</b> приёмщик (vestium <i>CJ</i>; vini <i>Dig</i>);\n<b>3)</b> инкассатор, казначей (s. aurarius <i>CJ</i>);\n<b>4)</b> укрыватель (<i>беглых рабов и пр.</i>) <i>Dig</i>;\n<b>5)</b> покровитель, защита <i>Vlg</i>."],
  ["susceptum", "<big>susceptum</big>\n\n<b>susceptum, i</b> <i>n</i> [suscipio]\nпредприятие, начинание <i>O</i>."],
  ["susceptus", "<big>susceptus</big>\n\n<b>I susceptus, a, um</b> <i>part. pf. к</i>\nsuscipio.\n\n<b>II susceptus, i</b> <i>m</i> [suscipio]\n<b>1)</b> <i>юр.</i> подзащитный, клиент <i>Amm, Dig, Eccl</i>;\n<b>2)</b> пациент <i>Eccl</i>."],
  ["suscipio", "<big>suscipio</big>\n\n<b>suscipio, cepi, ceptum, ere</b> [sub + capio]\n<b>1)</b> подхватывать, поддерживать (aliquem ruentem <i>V</i>); подпирать (labentem domum <i>Sen</i>): s. ignem foliis <i>V</i> поддерживать огонь листьями; s. aquam <i>O</i> подставлять рот под (льющуюся) воду, <i>т. е.</i> пить воду; cruorem pateris s. <i>V</i> принимать (жертвенную) кровь в чаши;\n<b>2)</b> поднимать новорождённого с земли (<i>обряд признания ребёнка своим</i>), <i>т. е.</i> признавать, принимать (puerum <i>Ter</i>): suscipiendorum liberorum causa <i>T</i> в целях узаконения (прижитых) детей;\n<b>3)</b> рождать, приживать, иметь (liberos ex, <i>реже</i> de aliqua <i>Ter etc.</i>): de te suscepta suboles <i>V</i> от тебя рождённый ребёнок (<i>слова Дидоны Энею</i>) || <i>med.-pass.</i> рождаться на свет (dies natalis meus, quo susceptus sum <i>C</i>);\n<b>4)</b> принимать, допускать: s. aliquem in populi Romani civitatem <i>C</i> принимать кого-л. в римское гражданство; quaerimoniam alicujus s. <i>C юр.</i> взять на себя представительство чьей-л. жалобы, <i>т. е.</i> защиту претензии; s. aliquem erudiendum <i>Q</i> принимать кого-л. в учение;\n<b>5)</b> <i>юр.</i> защищать на суде (reum <i>Q</i>; causam, litem <i>Dig</i>);\n<b>6)</b> принимать вид, напускать на себя (judicis severitatem <i>C</i>): s. personam viri boni <i>C</i> надеть на себя личину честного человека;\n<b>7)</b> брать: s. sermonem <i>Q</i> брать слово; s. partes <i>C</i> взять на себя роль;\n<b>8)</b> вводить, учреждать (ferias <i>Vr</i>); усваивать, воспринимать (cogitationem sobrii hominis <i>C</i>): s. morbos <i>Lcr</i> заболевать разными болезнями; s. ignominiam <i>C</i> покрыть себя бесславием; s. odium <i>Nep</i> воспылать ненавистью; s. inimicitias <i>Ter, C</i> проникнуться враждой <i>или</i> стать жертвой ненависти (<i>ср.</i> 12); s. consilium <i>C</i> задумать, принять решение; suscepit vita consuetudoque communis <i>C</i> так уже заведено самой жизнью;\n<b>9)</b> присвоить (tantum sibi auctoritatis <i>C</i>): s. facinus <i>или</i> s. in se crimen <i>C</i> пойти (отважиться) на преступление, <i>но</i>: s. in se alicujus crimen <i>C</i> принять на себя чью-л. вину;\n<b>10)</b> принимать на себя, предпринимать, начинать (negotium <i>C</i>): s. gloriam alicujus tuendam <i>C</i> принять на себя защиту чьей-л. славы; aes alienum amicorum s. <i>C</i> взять на себя долги друзей; bellum s. <i>C</i> предпринять (начать) войну;\n<b>11)</b> начать говорить: dicam equidem nec te suspensum, nate, tenebo, suscipit Anchises <i>V</i> я скажу и не буду держать тебя в неведении, сын мой,— начинает Анхиз;\n<b>12)</b> подвергаться, испытывать, претерпевать, терпеть (dolorem <i>C</i>): s. inimicitias <i>C</i> навлечь на себя вражду (<i>ср.</i> 8); s. pericula <i>C</i> подвергаться опасностям; s. poenam <i>C</i> нести наказание; s. invidiam apud aliquem <i>C</i> быть предметом чьей-л. ненависти."],
  ["suscitabulum", "<big>suscitabulum</big>\n\n<b>suscitabulum, i</b> <i>n</i>\nвозбудитель, задающий тон (s. vocis <i>Vr</i>)."],
  ["suscitatio", "<big>suscitatio</big>\n\n<b>suscitatio, onis</b> <i>f</i> [suscito]\nпробуждение, <i>перен.</i> воскресение (multorum <i>Tert</i>)."],
  ["suscitator", "<big>suscitator</big>\n\n<b>suscitator, oris</b> <i>f</i>\nвоскреситель (mortuorum <i>Tert</i>); обновитель (litterarum jam sepultarum <i>Sid</i>)."],
  ["suscito", "<big>suscito</big>\n\n<b>sus–cito, avi, atum, are</b>\n<b>1)</b> вздымать, поднимать: s. lintea <i>O</i> вздувать паруса; s. aures <i>VF</i> настораживать уши; terga telluris s. <i>V</i> поднимать (плугом), взрывать, вскапывать почву; s. undas <i>Lcn</i> вздымать волны;\n<b>2)</b> поднимать, заставлять встать (aliquem a subselliis <i>C</i>); поднимать с постели (<i>о больном</i>), ставить на ноги (aliquem <i>H</i>); будить (aliquem e somno <i>C</i>); пробуждать (tacentem Musam <i>H</i>); воскрешать (mortuos <i>Aug</i>): Vesuvius attonitas suscitat urbes <i>VF</i> Везувий ставит на ноги перепуганные города;\n<b>3)</b> отгонять прочь, отпугивать (s. vulturium <i>Ctl</i>);\n<b>4)</b> возбуждать, разжигать (sopitos ignes <i>V</i>); развязывать (bellum civile <i>C</i>; caedem <i>V</i>): s. in arma viros <i>V</i> призвать мужей к оружию; pede crepitum s. <i>Prp</i> топать ногой; s. poenam alicui <i>St</i> навлечь кару на кого-л.; s. clamores <i>Ph</i> вызывать восклицания;\n<b>5)</b> воздвигать (delubra deum = deorum <i>Lcr</i>);\n<b>6)</b> создавать, сочинять (fictas sententias <i>Enn</i>)."],
  ["suscus", "<big>suscus</big>\n\n<b>suscus, udis</b> <i>f</i> <i>Aug</i>\n= subscus."],
  ["Susiane", "<big>Susiane</big>\n\n<b>Susiane, es</b> <i>f</i>\nСузиана, <i>область в древней Персидской империи</i> (<i>прибл. нын.</i> Хузистан) <i>PM</i>."],
  ["Susianus", "<big>Susianus</big>\n\n<b>I Susianus, a, um</b> [Susa <i>и</i> Susiane]\nсузийский <i>QC</i>.\n\n<b>II Susianus, i</b> <i>m</i>\nжитель Суз <i>или</i> Сузианы <i>PM, QC</i>."],
  ["susinus", "<big>susinus</big>\n\n<b>susinus, a, um</b> (<i>евр.</i>)\nлилейный (unguentum <i>CC, PM</i>)."],
  ["Susis", "<big>Susis</big>\n\n<b>Susis, idis</b> <i>f</i> [Susa]\nсузийская, <i>перен.</i> персидская <i>QC, Sid</i>."],
  ["suspectio", "<big>suspectio</big>\n\n<b>suspectio, onis</b> <i>f</i>\n<b>1)</b> [suspicio I] уважение, почтение <i>Eccl</i>;\n<b>2)</b> [suspicio II] подозрение <i>Frontin, CA</i>."],
  ["suspecto", "<big>suspecto</big>\n\n<b>I suspecto, avi, atum, are</b> [<i>intens. к</i> suspicio]\n<b>1)</b> снизу глядеть, смотреть, созерцать (tabulam pictam <i>Ter</i>);\n<b>2)</b> глядеть с недоверием, подозревать (aliquem <i>и</i> aliquid <i>T, AV</i>): suspectari alicui <i>T</i> быть у кого-л. под подозрением.\n\n<b>II suspecto</b> <i>adv.</i>\nподозрительно (decedentes <i>Dig</i>)."],
  ["suspector", "<big>suspector</big>\n\n<b>I suspector, —, ari</b> <i>depon.</i> <i>Amm</i>\n= suspecto I.\n\n<b>II suspector, oris</b> <i>m</i>\nпоклонник, почитатель (s. admiratorque <i>Sid</i>)."],
  ["suspectus", "<big>suspectus</big>\n\n<b>I suspectus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> suspicio;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> подозрительный, внушающий подозрение (опасение), подозреваемый (aliqua re <i>QC</i> <i>или</i> de, <i>реже</i> super aliqua re <i>C, Sl</i>, <i>тж.</i> propter (ad) aliquid <i>QC, L</i>, alicujus rei <i>L, QC, T</i>, in aliqua re <i>и</i> ob aliquid <i>T</i>): aliquem suspectum habere <i>Cs, Sl</i> подозревать кого-л., подозрительно относиться к кому-л.;\n<b>2)</b> питающий подозрение, подозрительно относящийся, недоверчивый <i>Spart, Dig, Tert</i>.\n\n<b>II suspectus, us</b> <i>m</i> [suspicio I]\n<b>1)</b> взгляд снизу вверх: aspectu idemque suspectu <i>PM</i> при взгляде сверху, а также снизу;\n<b>2)</b> высота (turris vasto suspectu <i>V</i>);\n<b>3)</b> почтительное удивление, восхищение (alicujus rei <i>O, Sen etc.</i>)."],
  ["suspendiosus", "<big>suspendiosus</big>\n\n<b>suspendiosus, i</b> <i>m</i>\nповесившийся (самоубийца) <i>Vr, PM, Dig</i>."],
  ["suspendium", "<big>suspendium</big>\n\n<b>suspendium, i</b> <i>n</i> [suspendo]\nповешение: suspendio perire <i>C</i> <i>или</i> vitam finire <i>Su, Pt</i> повеситься; suspendia collo praebere <i>O</i> давать повод к самоубийству через повешение."],
  ["suspendo", "<big>suspendo</big>\n\n<b>sus–pendo, pendi, pensum, ere</b>\n<b>1)</b> подвешивать, вешать (aliquid aliqua re <i>или</i> in, de, ex, ab aliqua re): s. aliquid ex alta pinu <i>V</i> повесить что-л. на верхушке сосны; s. (e) collo <i>PM</i> повесить на шею; s. aliquem arbori infelici (<i>loc.</i>) <i>C, L</i> вздёрнуть кого-л. на виселицу; se s. de <i>или</i> e ficu <i>C, Q</i> повеситься на фиговом дереве || прицеплять (aratra jugo suspensa <i>V</i>): piscem s. hamo <i>O</i> ловить рыбу крючком; naso (adunco) s. aliquem (aliquid) <i>погов. H</i> презирать кого (что)-л., насмехаться над кем (чем)-л.;\n<b>2)</b> взвешивать (aliquid in trutina <i>J</i>);\n<b>3)</b> вешать (в храме), <i>т. е.</i> приносить в дар (богам), посвящать (vestimenta deo maris <i>H</i>);\n<b>4)</b> поднимать, поддерживать (на известной высоте), подпирать: contignationem tignis s. <i>Cs</i> сделать пастил на балках; s. murum furculis <i>L</i> подпереть стену вилами; tellurem tenui sulco s. <i>V</i> взрыхлять почву поверхностной вспашкой; s. castra saxis <i>Sil</i> разбить лагерь на вершине скал;\n<b>5)</b> ставить в зависимость: extrinsecus suspensas habere rationes alicujus rei <i>C</i> считать что-л. зависящим от внешних причин; nunquam credideris felicem quemquam ex felicitate suspensum <i>Sen</i> никогда не считай счастливым того, кто зависит от счастливых случайностей;\n<b>6)</b> вперять, направлять, устремлять: vultum mentemque tabella picta s. <i>H</i> пристально созерцать картину;\n<b>7)</b> ставить в тупик, оставлять в нерешительности (aliquem ambiguis responsis <i>Su</i>); medio responso rem suspendisse <i>L</i> неопределённостью ответа оставить дело нерешённым; s. exspectationem audientium <i>QC</i> держать слушателей в выжидательном положении;\n<b>8)</b> сдерживать, удерживать (fletum <i>O</i>; spiritum <i>Q</i>; lacrimas <i>O</i>);\n<b>9)</b> устранять (causas morbi <i>Veg</i>);\n<b>10)</b> останавливать, прекращать (sermonem <i>Q</i>). — <i>См. тж.</i> suspensus."],
  ["suspense", "<big>suspense</big>\n\n<b>suspense</b>\nв нерешительности, колеблясь <i>Aug</i>."],
  ["suspensio", "<big>suspensio</big>\n\n<b>suspensio, onis</b> <i>f</i> [suspendo]\n<b>1)</b> <i>архит.</i> упорная арка <i>Vtr</i>;\n<b>2)</b> неясное произношение, проглатывание (litterae <i>Is</i>);\n<b>3)</b> поднимание (palpebrarum <i>CA</i>)."],
  ["suspensura", "<big>suspensura</big>\n\n<b>suspensura, ae</b> <i>f</i> [suspendo]\nсводчатое строение, свод (balneorum <i>Sen</i>)."],
  ["suspensus", "<big>suspensus</big>\n\n<b>suspensus, a, um</b>\n<b>1.</b> <i>part. pf. к</i> suspendo;\n<b>2.</b> <i>adj.</i>\n<b>1)</b> парящий (alae <i>L</i>);\n<b>2)</b> поднятый (aulaea <i>H</i>);\n<b>3)</b> насторожённый (aures <i>Prp</i>);\n<b>4)</b> скользящий, чуть прикасающийся (per undas <i>Poeta ap. C</i>; fluctu s. <i>V</i>): suspensis dentibus <i>Lcr</i> чуть касаясь зубами; suspenso (digitis) gradu <i>Ter, H</i> <i>и</i> suspenso pede <i>Ph</i> на цыпочках; suspensa manu <i>PJ</i> осторожно, слегка;\n<b>5)</b> зависящий (ex bono casu <i>C</i>): aliquid suspensum habere ex aliqua re <i>C</i> считать что-л. зависимым от чего-л.;\n<b>6)</b> висящий, висячий: rupes suspensa saxis <i>V</i> утёс, нависший над скалами; Roma cenaculis suspensa <i>C</i> Рим, ютящийся по чердакам;\n<b>7)</b> колеблющийся, нерешительный, неуверенный: s. metu <i>L</i> нерешительный от страха, оробевший; suspensis animis <i>L</i> в нерешительности; suspensum aliquem habere (tenere) <i>C</i> держать кого-л. в неведении <i>или</i> в недоумении; in suspensum relinquere <i>Sen, PJ</i> оставлять нерешённым, <i>т. е.</i> в прежнем положении; inter spem metumque s. <i>L</i> колеблющийся между надеждой и страхом, <i>т. е.</i> питающий смешанную со страхом надежду; quid ageretur s. <i>Hirt</i> не понимающий, что происходит; res suspensa <i>L</i> критическое положение;\n<b>8)</b> тревожный, обеспокоенный (vultus <i>C</i>); робкий (timor <i>O</i>): aliquem suspensum terrere <i>V</i> пугать и расстраивать кого-л.;\n<b>9)</b> утихший, слабый (aura <i>Lcr</i>);\n<b>10)</b> рыхлый (terra <i>Col</i>)."],
  ["suspexi", "<big>suspexi</big>\n\n<b>suspexi</b> <i>pf. к</i>\nsuspicio I."],
  ["suspicabilis", "<big>suspicabilis</big>\n\n<b>suspicabilis, e</b> [suspicor]\nоснованный на догадках: s. ars <i>Eccl</i> = medicina."],
  ["suspicax", "<big>suspicax</big>\n\n<b>suspicax, acis</b> <i>adj.</i> [suspicor]\n<b>1)</b> подозрительный, недоверчивый (animus <i>T</i>);\n<b>2)</b> подозрительный, внушающий подозрение (silentium <i>T</i>)."],
  ["suspicientia", "<big>suspicientia</big>\n\n<b>suspicientia, ae</b> <i>f</i> [suspicio I, 4]\nуважение, восхищение <i>Eccl</i>."],
  ["suspicio", "<big>suspicio</big>\n\n<b>I su–spicio, spexi, spectum, ere</b> [specio]\n<b>1)</b> глядеть вверх, взирать (in <i>и</i> ad caelum <i>C</i>); созерцать (caelum <i>C, O, Su</i>): nec s. nec circumspicere <i>C</i> не глядеть ни вверх, ни вокруг;\n<b>2)</b> быть обращенным: Pleiades s. <i>O</i> быть обращенным в сторону Плеяд; Olympus suspexit nubes <i>Lcn</i> Олимп упирается в облака;\n<b>3)</b> мысленно созерцать, уноситься мыслью: nihil altum s. <i>C</i> не думать ни о чём высоком;\n<b>4)</b> взирать с уважением, чтить, почитать (viros, etiam si decidunt, magna conantes <i>Sen</i>; ingenuas artes <i>O</i>; naturam <i>C</i>): s. eloquentiam <i>C</i> восхищаться красноречием;\n<b>5)</b> смотреть с подозрением, с недоверием: Bomilcar suspectus regi et ipse eum suspiciens <i>Sl</i> Бомилькар, который вызвал подозрение у царя (Югурты) и который сам стал его подозревать.\n\n<b>II suspicio, onis</b> <i>f</i> [suspicio I, 5]\n<b>1)</b> подозрение: omnis s. in servos commovebatur <i>C</i> всё подозрение пало на рабов; s. insequitur aliquam rem <i>C</i> что-л. вызывает подозрение; s. in aliquem convenit (cadit) <i>C</i> подозрение падает на кого-л.; in suspicionem cadere <i>C</i> (incidere <i>C</i>, venire <i>Nep</i>) попасть под (внушить) подозрение; alicui suspicionem alicujus rei dare (habere) <i>C</i> вызвать в ком-л. подозрение; <i>но тж.</i> suspicionem habere <i>C</i> подозревать; sine ulla suspicione <i>Pt</i> не возбуждая никаких подозрений;\n<b>2)</b> предположение, догадка (alicujus rei <i>C</i>): non sciebamus, quo mi iteremus suspiciones nostras <i>Pt</i> мы не знали, что предположить;\n<b>3)</b> основание подозревать: nulla s. (sub) est <i>C</i> нет повода для подозрений;\n<b>4)</b> намёк, видимость: nulla s. vulneris <i>Pt</i> никакого подобия (<i>т. е.</i> решительно никакой) раны."],
  ["suspiciose", "<big>suspiciose</big>\n\n<b>suspiciose</b> [suspiciosus]\nподозрительно, возбуждая подозрения (dicere aliquid <i>C</i>)."],
  ["suspiciosus", "<big>suspiciosus</big>\n\n<b>suspiciosus, a, um</b> [suspicio II]\n<b>1)</b> подозрительный, склонный к подозрениям, недоверчивый (in aliquem <i>C</i>);\n<b>2)</b> подозрительный, наводящий на подозрения (negotium <i>C</i>); внушающий опасения (tempus <i>C</i>)."],
  ["suspico", "<big>suspico</big>\n\n<b>suspico, —, —, are</b> <i>Pl</i>\n= suspicor."],
  ["suspicor", "<big>suspicor</big>\n\n<b>suspicor, atus sum, ari</b> <i>depon.</i> [suspicio I, 5]\n<b>1)</b> подозревать (aliquid de aliquo <i>C</i>): s. aliquem <i>Pl</i> подозревать кого-л.;\n<b>2)</b> предполагать, догадываться: placiturum tibi esse librum meum suspicabar <i>C</i> я предполагал, что книга моя тебе понравится."],
  ["suspiratio", "<big>suspiratio</big>\n\n<b>suspiratio, onis</b> <i>f</i> [suspiro]\nвздох, вздохи <i>PM, Q</i>."],
  ["suspiratus", "<big>suspiratus</big>\n\n<b>suspiratus, us</b> <i>m</i> [suspiro]\nвздох: suspiratibus haustis <i>O</i> глубоко вздыхая."],
  ["suspiriosus", "<big>suspiriosus</big>\n\n<b>suspiriosus, a, um</b> [suspirium]\nстрадающий одышкой <i>PM, CA</i>; запалённый (mula <i>Col</i>)."],
  ["suspiritus", "<big>suspiritus</big>\n\n<b>suspiritus, us</b> <i>m</i> [suspiro]\nглубокий вздох, вздохи <i>Pl, C, L</i>: longos trahens suspiritus <i>Ap</i> испуская глубокие вздохи."],
  ["suspirium", "<big>suspirium</big>\n\n<b>suspirium, i</b> <i>n</i> [suspiro]\n<b>1)</b> глубокий вдох: non dare suspiria <i>Lcn</i> не давать перевести дыхание;\n<b>2)</b> вздох(и): suspiria trahere <i>O</i> вздыхать;\n<b>3)</b> воздыхание (sine cura, sine suspirio <i>C</i>);\n<b>4)</b> одышка (morbus qui s. dici potest <i>Sen</i>)."],
  ["suspiro", "<big>suspiro</big>\n\n<b>su–spiro, avi, atum, are</b>\n<b>1)</b> вздыхать: s. ab imis pectoribus <i>O</i> глубоко вздыхать; s. in aliquo <i>Ctl, O</i> <i>или</i> s. aliquem <i>H, J</i> вздыхать (тосковать) по ком-л. || томиться (e. alios amores <i>Tib</i>);\n<b>2)</b> испарять, выделять (umentes nebulas <i>Lcn</i>): s. atro vapore <i>Sil</i> выделять чёрные испарения."],
  ["susque deque", "<big>susque deque</big>\n\n<b>susque deque</b> (= sursum deorsum)\nснизу и сверху, вверх и вниз, и так и этак, <i>т. е.</i> безразлично, равнодушно: s. d, habet <i>Pl</i> (fert <i>AG</i>) aliquid ему (что-л.) всё равно; s. d. esse <i>LM ap. AG</i> быть безразличным (неважным); de Octavio s. d. <i>C</i> насчёт Октавия я не забочусь, <i>т. е.</i> не в Октавии дело."],
  ["sussilio", "<big>sussilio</big>\n\n<b>sussilio</b> <i>v. l.</i>\n= subsilio."],
  ["sussulto", "<big>sussulto</big>\n\n<b>sussulto</b> <i>v. l.</i>\n= subsulto."],
  ["sustentaculum", "<big>sustentaculum</big>\n\n<b>sustentaculum, i</b> <i>m</i> [sustento]\nопора, оплот (victoriae <i>T</i>); <i>перен.</i> (<i>тж.</i> sustentacula alimentorum <i>Aug</i>) питание (corporis <i>Aug</i>)."],
  ["sustentatio", "<big>sustentatio</big>\n\n<b>sustentatio, onis</b> <i>f</i> [sustento]\n<b>1)</b> задержка, отсрочка (habere aliquam moram et sustentationem <i>C</i>);\n<b>2)</b> сдерживание, сдержанность (s. sui patientia nominatur <i>Lact</i>);\n<b>3)</b> содержание, питание (alicujus <i>Dig</i>);\n<b>4)</b> <i>ритор.</i> воздержание, задержка (<i>о целью вызвать у слушателей минутное сомнение</i>) <i>Q</i>."],
  ["sustentatrix", "<big>sustentatrix</big>\n\n<b>sustentatrix, icis</b> <i>f</i> [sustento]\nподдерживающая, питающая <i>JV</i>."],
  ["sustentatus", "<big>sustentatus</big>\n\n<b>sustentatus, us</b> <i>m</i> [sustento]\nподдерживание <i>Ap, Aus, Hier</i>."],
  ["sustento", "<big>sustento</big>\n\n<b>sustento, avi, atum, ire</b> [<i>intens. к</i> sustineo]\n<b>1)</b> поддерживать, подпирать (machinam mundi <i>Lcr</i>; aliquem ruentem dextra <i>V</i>): navis sustentatur <i>V</i> судно держится на поверхности;\n<b>2)</b> подкреплять, оказывать поддержку (aliquem omnibus copiis <i>C</i>): litteris sustentor et recreor <i>C</i> научные занятия меня поддерживают и подкрепляют || поддерживать, оберегать (rem publicam <i>C</i>);\n<b>3)</b> сохранять (valetudinem <i>C</i>);\n<b>4)</b> содержать, питать (familiam <i>Ter</i>, exercitum <i>C</i>);\n<b>5)</b> выдерживать, выносить, переносить (belli difficultates <i>C</i>; maerorem <i>C</i>; vitia alicujus <i>Sl</i>); держаться, сопротивляться: s. famem pecore adacto <i>Cs</i> справиться с голодом благодаря пригнанному скоту; aegre sustentatum est <i>Cs</i> сопротивляться было трудно;\n<b>6)</b> выдерживать, отражать, отбивать (vim hostium <i>Sl</i>; impetum legionum <i>T</i>);\n<b>7)</b> управлять: s. munus <i>CTh</i> состоять на (государственной) службе;\n<b>8)</b> откладывать, отсрочивать (aliquid ad adventum alicujus <i>C</i>): sustentando aut prolatando <i>C</i> путём отсрочек и проволочек."],
  ["sustentus", "<big>sustentus</big>\n\n<b>sustentus, us</b> <i>m</i> <i>Hier</i>\n= sustentatus."],
  ["sustinentia", "<big>sustinentia</big>\n\n<b>sustinentia, ae</b> <i>f</i> [sustineo]\nвыдержка, терпение <i>Lact</i>."],
  ["sustineo", "<big>sustineo</big>\n\n<b>sus–tineo, tinui, tentum, ere</b> [teneo]\n<b>1)</b> поддерживать (amicum fortuna <i>C</i>; quos fortuna premit <i>Lcn</i>; aliquo auxilio sustineri <i>Sen</i>); служить опорой, подпирать: columnae porticum sustinent <i>C</i> колонны подпирают портик; aer volatus alitum sustinet <i>C</i> воздух поддерживает полёт пернатых, <i>т. е.</i> служит опорой при полёте || держать (aliquid manu <i>O</i>);\n<b>2)</b> удерживать, подхватывать (labentem <i>C</i>): se s. (a lapsu) <i>Cs, L</i> удержаться от падения, устоять на ногах;\n<b>3)</b> выдерживать, нести (onus <i>H</i>; aliquid dentibus <i>Pt</i>): bovem vivum umeris s. <i>C</i> носить живого быка на спине || носить на себе (vestem <i>O</i>);\n<b>4)</b> выносить, терпеть (perpetuum dolorem <i>C</i>); нести, оплачивать (sumptum litis <i>Dig</i>); переносить (poenam <i>C</i>; aestatem <i>Hirt</i>);\n<b>5)</b> брать на себя, выполнять: honorem s. <i>Sl</i> занимать пост; personam s. <i>C</i> выполнять роль; Thaida s. <i>J</i> играть роль Таиды; personam alicujus s. <i>Dig</i> выступать чьим-л. представителем; s. munus in re publica <i>C</i> нести (выполнять) государственную повинность;\n<b>6)</b> содержать, питать, кормить (nepotes <i>V</i>): re frumentaria sustinemur <i>C</i> хлебом (Сицилии) мы питаемся;\n<b>7)</b> поддерживать, (с)охранять, оберегать (civitatis dignitatem <i>C</i>): s. animos pugnantium <i>L</i> поддерживать бодрость (в) сражающихся;\n<b>8)</b> выдерживать, держаться, не уступать: s. res gravissimas <i>C</i> справиться (совладать) с труднейшими делами; sustinebunt se tot senatoribus non credidisse? <i>C</i> хватит ли у них решимости не поверить стольким сенаторам?; s. impetum hostis <i>Cs</i> устоять против натиска неприятеля; exercitus s. non potuit <i>Cs</i> войско не смогло держаться; nec ultra sustinuere (= sustinuerunt) certamen Galli, quin terga verterent <i>L</i> галлы не смогли дольше выдержать боя и обратились в бегство; s. aliquem rogantem <i>Brutus ap. C</i> не уступать чьим-л. просьбам; senatus querentes eos non sustinuit <i>L</i> сенат не устоял против их просьб, <i>т. е.</i> уступил их просьбам;\n<b>9)</b> задерживать, сдерживать, останавливать (equos <i>C, Cs</i>; exercitum <i>Cs</i>): s. dextram alicujus ab aliqua re <i>V</i> отводить чью-л. руку от чего-л.; undam de flumine cavis palmis s. <i>V</i> зачерпнуть пригоршней воду из реки; s. signa <i>L</i> остановиться с войском; s. remos <i>C</i> перестать грести; s. impetum irae <i>Sen</i> подавить приступ гнева; se s. ab aliqua re <i>C</i> воздержаться от чего-л.; sustinete hic <i>Vlg</i> побудьте здесь;\n<b>10)</b> задерживать, откладывать, затягивать (aliquid in noctem <i>L</i> <i>и</i> ad noctem <i>Cs</i>): s. bellum consilio <i>L</i> преднамеренно затянуть войну; s. extremum diem fati <i>O</i> оттягивать день (своей) кончины."],
  ["sustollo", "<big>sustollo</big>\n\n<b>sus–tollo, —, —, ere</b>\n<b>1)</b> поднимать (vultus ad aethera <i>O</i>; onus <i>Sen</i>; vela <i>Ctl</i>): machina sustollit multa magno pondere <i>Lcr</i> машина поднимает множество больших тяжестей;\n<b>2)</b> воздвигать (columnas <i>Pl</i>; novum opus <i>CTh</i>);\n<b>3)</b> похищать (aliquam <i>Pl</i>);\n<b>4)</b> разрушать, уничтожать (aedes <i>Pl</i>); отменять (censum <i>Lact</i>)."],
  ["sustringo", "<big>sustringo</big>\n\n<b>sustringo</b> <i>v. l.</i>\n= substringo."],
  ["sustuli", "<big>sustuli</big>\n\n<b>sustuli</b> <i>pf. к</i>\ntollo."],
  ["susum", "<big>susum</big>\n\n<b>susum</b> <i>арх. Pt</i>\n= sursum."],
  ["susurna", "<big>susurna</big>\n\n<b>susurna, ae</b> <i>f</i> <i>Amm</i>\n= sisur(n)a."],
  ["susurramen", "<big>susurramen</big>\n\n<b>susurramen, inis</b> <i>n</i> [susurro]\nбормотание (magicum <i>Ap</i>)."],
  ["susurratio", "<big>susurratio</big>\n\n<b>susurratio, onis</b> <i>f</i> <i>Vlg, Eccl</i>\n= susurramen."],
  ["susurrator", "<big>susurrator</big>\n\n<b>susurrator, oris</b> <i>m</i>\nшептун, сплетник <i>Caelius ap. C, Eccl</i>."],
  ["susurrio", "<big>susurrio</big>\n\n<b>susurrio, onis</b> <i>m</i> <i>Vlg</i>\n= susurro II."],
  ["susurrium", "<big>susurrium</big>\n\n<b>susurrium, i</b> <i>n</i>\nшушуканье, сплетни (malarum ancillarum <i>Aug</i>)."],
  ["susurro", "<big>susurro</big>\n\n<b>I susurro, —, —, are</b> [susurrus I]\n<b>1)</b> жужжать (apes susurrant <i>V</i>);\n<b>2)</b> свистеть (ventus susurrans <i>V</i>); шелестеть (platanus susurrat <i>Calp</i>);\n<b>3)</b> шептать(ся) (cum aliquo <i>O</i>);\n<b>4)</b> напевать вполголоса (cantica Nili <i>M</i>); бормотать (versum Persii <i>Lampr</i>): ut vaga fama susurrat <i>O</i> если верить неясным и вполголоса передаваемым слухам.\n\n<b>II susurro, onis</b> <i>m</i> <i>Eccl</i>\n= susurrator."],
  ["susurrus", "<big>susurrus</big>\n\n<b>I susurrus, i</b> <i>m</i>\n<b>1)</b> жужжание (<i>sc.</i> apum <i>V</i>);\n<b>2)</b> шёпот, бормотание, лепет (mulierculae <i>C</i>; lenes susurri <i>H</i>): Susurri <i>pl. O</i> Шёпоты, <i>т. е.</i> неясные слухи.\n\n<b>II susurrus, a, um</b>\nшепчущий, нашёптывающий (lingua <i>O</i>)."],
  ["suta", "<big>suta</big>\n\n<b>suta, orum</b> <i>n</i> [sutus]\nшитьё, нечто сшитое: aerea suta <i>V</i> (сшитые вместе) медные латы."],
  ["sutela", "<big>sutela</big>\n\n<b>sutela, ae</b> <i>f</i> [suo I]\nсшитое, <i>перен.</i> нечто злостно состряпанное, <i>т. е.</i> замыслы, козни <i>Pl</i>."],
  ["Suthul", "<big>Suthul</big>\n\n<b>Suthul, ulis</b> (<i>тж.</i> <i>indecl.</i>) <i>n</i>\nСутул, <i>укреплённое место в Нумидии</i> <i>Sl</i>."],
  ["sutilis", "<big>sutilis</big>\n\n<b>sutilis, e</b> [suo I]\n<b>1)</b> (рас)шитый <i>или</i> отделанный (balteus auro s. <i>V</i>): domus s. <i>VF</i> сшитый (из кож) шалаш;\n<b>2)</b> плетёный (cymba <i>V, Ap</i>; corona <i>O</i>)."],
  ["sutor", "<big>sutor</big>\n\n<b>sutor, oris</b> <i>m</i> [suo I]\n<b>1)</b> сапожник, башмачник <i>Pl, Vr, Sen etc.</i>: s. ne supra crepidam (<i>sc.</i> judicet) <i>погов. PM</i> пусть сапожник судит не выше сапога (<i>ср.</i> всяк сверчок знай свой шесток);\n<b>2)</b> компилятор (fabularum <i>Aug</i>)."],
  ["sutorius", "<big>sutorius</big>\n\n<b>I sutorius, a, um</b> [sutor]\nсапожный (atramentum <i>C, CC, PM</i>).\n\n<b>II sutorius, i</b> <i>m</i>\nбывший сапожник <i>C</i>."],
  ["sutrina", "<big>sutrina</big>\n\n<b>sutrina, ae</b> <i>f</i> [sutrinus I]\n<b>1)</b> (<i>sc.</i> officina) мастерская (лавка) сапожника <i>PM, Ap, Tert</i>;\n<b>2)</b> (<i>sc.</i> ars) сапожное ремесло <i>Vr, Lact</i>."],
  ["sutrinum", "<big>sutrinum</big>\n\n<b>sutrinum, i</b> <i>n</i> <i>Sen</i>\n= sutrina 2."],
  ["sutrinus", "<big>sutrinus</big>\n\n<b>I sutrinus, a, um</b> [sutor]\nсапожный (taberna <i>T</i>; ars <i>Vr, PM</i>).\n\n<b>II Sutrinus</b> <i>и</i> <b>Sutrius, a, um</b> [Sutrium]\nсутрийский (ager <i>L</i>).\n\n<b>III Sutrinus, i</b> <i>m</i>\nжитель города Sutrium <i>L, Tert</i>."],
  ["Sutrium", "<big>Sutrium</big>\n\n<b>Sutrium, i</b> <i>n</i>\nСутрий, <i>город в южн. Этрурии, к вост. от Tarquinii</i> (<i>ныне</i> Sutri) <i>Pl, L, VP</i>."],
  ["Sutrius", "<big>Sutrius</big>\n\n<b>Sutrius</b> <i>v. l.</i>\n= Sutrinus II."],
  ["sutura", "<big>sutura</big>\n\n<b>sutura, ae</b> <i>f</i> [suo I]\nшов: triplex scutale, crebris suturis duratum <i>L</i> праща из трёх кож, скреплённых частым швами; s. calvariae <i>CC</i> черепной шов."],
  ["sutus", "<big>sutus</big>\n\n<b>sutus, a, um</b> <i>part. pf. к</i>\nsuo."],
  ["suum", "<big>suum</big>\n\n<b>suum, i</b> <i>n</i> [suus]\nсвоё (<i>т. е.</i> дело, имущество, достояние, обыкновение <i>и пр.</i>): sui nihil deperdere <i>Cs</i> ничего не утерять из своего имущества; suum cuique tribuere <i>C, Dig</i> каждому отдавать (воздавать) своё; de suo от себя <i>или</i> на свой счёт, из своих средств (de suo aliquid parare <i>Pt</i>)."],
  ["suus", "<big>suus</big>\n\n<b>suus, a, um</b> (<i>gen. pl.</i> <i>тж.</i> suum <i>Ter</i>)\n<b>1)</b> свой (<i>тк. для 3-го л.</i>), его, ему принадлежащий <i>или</i> свойственный, (свой) собственный (<i>иногда с усил. частицами</i> -met <i>и</i> -pte): sua manu scribere <i>Nep</i> собственноручно писать; sua culpa perire <i>Su</i> погибнуть по своей (собственной) вине; aliquid suum facere <i>L</i> присвоить себе что-л., овладеть чём-л.; sua morte defunctus <i>Su</i> умерший своей, <i>т. е.</i> естественной смертью; s. accusator fuit <i>Nep</i> он был своим собственным обвинителем; suo Marte pugnare <i>L</i> сражаться на свой лад, по-своему; hunc sui cives e civitate ejecerunt <i>C</i> его (Ганнибала) собственные сограждане изгнали из государства;\n<b>2)</b> надлежащий, естественный, подлинный, установленный <i>или</i> нормальный: natura propria et sua <i>C</i> собственная и особая природа (сущность); nomen (<i>или</i> verbum) proprium et suum <i>C</i> подлинное и собственное значение слова, слово в собственном своём смысле; suum numerum habere <i>C</i> быть в надлежащем числе (комплектным); suae aetatis factus <i>Dig, CJ</i> достигший совершеннолетия; suae mentis non esse <i>Dig</i> быть не в своём уме; vix sua erat <i>O</i> она была сама не своя; suo anno <i>C</i> в надлежащем году; suo tempore <i>C</i> своевременно, в своё время (<i>ср.</i> 6); sua tempora complere <i>C</i> выполнить установленные сроки; suo jure <i>Dig</i> с полным правом; sui juris esse <i>Dig</i> быть полноправным, <i>т. е.</i> свободным;\n<b>3)</b> принадлежащий себе, свободный: ancilla, quae mea fuit hodie, sua nunc est <i>Pl</i> служанка, сегодня (ещё) принадлежавшая мне, теперь свободна;\n<b>4)</b> самостоятельный, независимый (in disputando <i>C</i>): inaestimabile bonum est suum fieri <i>Sen</i> неоценимое благо — быть свободным;\n<b>5)</b> личный: quod est cujusque maxime suum <i>C</i> то, что в каждом есть наиболее личного (своеобразного); stat sua cuique dies <i>V</i> каждому предназначен свой день (<i>т. е.</i> кончины);\n<b>6)</b> благоприятный, выгодный, удобный: suo loco pugnare <i>или</i> pugnam facere <i>Sl</i> сражаться в удобной местности; suo vento <i>H</i> (aestu <i>L</i>) ire идти с попутным ветром (течением); alicui suam occasionem dare <i>L</i> предоставить кому-л. благоприятный случай; ex incommodo . alieno suam occasionem petere <i>L</i> стремиться извлечь для себя выгоду из чужой беды; populo suo uti <i>C</i> иметь народ на своей стороне; suo tempore bellum incipere <i>L</i> начать войну в благоприятный момент (<i>ср.</i> 2); sua fortuna pugnare <i>VP</i> успешно воевать; aliquem suum facere <i>L, Pt, Ap, St</i> привлечь кого-л. на свою сторону. — <i>См. тж.</i> suum."],
  ["suxi", "<big>suxi</big>\n\n<b>suxi</b> <i>pf. к</i>\nsugo."],
  ["svemus", "<big>svemus</big>\n\n<b>svemus</b> — <i>см.</i>\nsuemus."],
  ["svetum", "<big>svetum</big>\n\n<b>svetum</b> — <i>см.</i>\nsuesco."],
  ["svetus", "<big>svetus</big>\n\n<b>svetus</b> — <i>см.</i>\nsuetus."],
  ["svevi", "<big>svevi</big>\n\n<b>svevi</b> — <i>см.</i>\nsuesco."],
  ["Sybaris", "<big>Sybaris</big>\n\n<b>Sybaris, is</b>\nСибарис:\n<b>1)</b> <i>f</i> <i>греч. город в южн. Лукании, близ Тарентинского залива</i> (<i>в 510 г. до н. э. разрушен кротонцами, в 443 г. до н. э. восстановлен под именем Thurii</i>) <i>Vr, C, L, H, O</i>;\n<b>2)</b> <i>m</i> <i>речка на вост. побережье Лукании, на берегах которой находился город Сибарис</i> <i>PM, O</i>;\n<b>3)</b> <i>m</i> <i>мужское имя</i> <i>H</i>."],
  ["Sybarita", "<big>Sybarita</big>\n\n<b>Sybarita, ae</b> <i>m</i>\n<b>1)</b> сибарит, житель города Sybaris <i>Sen, Q</i>;\n<b>2)</b> <i>перен.</i> искатель наслаждений, сибарит <i>Sen</i>."],
  ["Sybaritanus", "<big>Sybaritanus</big>\n\n<b>I Sybaritanus, a, um</b> [Sybaris]\nсибаританский <i>Vr, PM</i>.\n\n<b>II Sybaritanus, i</b> <i>m</i> <i>Just</i>\n= Sybarita."],
  ["Sybaritis", "<big>Sybaritis</big>\n\n<b>Sybaritis, idis</b> <i>f</i>\nСибаритида, сибаритянка (<i>название стихотворения</i>) <i>O</i>."],
  ["sybotes", "<big>sybotes</big>\n\n<b>sybotes, ae</b> <i>m</i> (<i>греч.</i>)\nсвинопас <i>Man</i>."],
  ["sycaminon", "<big>sycaminon</big>\n\n<b>sycaminon, i</b> <i>n</i> (<i>греч.</i>) <i>Dig</i>\n= sycaminos."],
  ["sycaminos", "<big>sycaminos</big>\n\n<b>sycaminos (-us), i</b> <i>f</i>\nтутовое дерево, шелковица <i>CC</i>."],
  ["Sychaeus", "<big>Sychaeus</big>\n\n<b>I Sychaeus, i</b> <i>m</i>\nСихей, <i>финикиянин, муж Дидоны, убитый Пигмалионом</i> <i>V, O</i>.\n\n<b>II Sychaeus, a, um</b> [Sychaeus I]\nсихеев (cinis <i>V</i>)."],
  ["Sycolatronia", "<big>Sycolatronia</big>\n\n<b>Syco–latronia, ae</b> <i>f</i> (<i>v. l.</i> <b>Scytholatronia</b>) <i>шутл.</i>\nстрана фигокрадов <i>Pl</i>."],
  ["sycomorus", "<big>sycomorus</big>\n\n<b>sycomorus, i</b> <i>f</i> <i>CC</i>\n= sycaminos."],
  ["sycophanta", "<big>sycophanta</big>\n\n<b>sycophanta, ae</b> <i>m</i> (<i>греч.</i>)\n<b>1)</b> сикофант, доносчик (<i>в древней Аттике, доносивший на нарушителей закона, запрещавшего вывоз фиг из страны</i>) <i>Pl, Ter</i>;\n<b>2)</b> интриган, клеветник, обманщик <i>Pl</i>;\n<b>3)</b> подхалим <i>Pl, Eccl</i>."],
  ["sycophantia", "<big>sycophantia</big>\n\n<b>sycophantia, ae</b> <i>f</i> [sycophanta] (<i>тж.</i> <i>pl.</i>)\nинтриги, мошенничество, обман <i>Pt</i>."],
  ["sycophantiose", "<big>sycophantiose</big>\n\n<b>sycophantiose</b> [sycophanta]\nковарно, мошеннически (agere quicquam <i>Pl</i>)."],
  ["sycophantor", "<big>sycophantor</big>\n\n<b>sycophantor, —, ari</b> <i>depon.</i> [sycophanta]\nобманывать, мошенничать: sycophantae huic s. <i>Pl</i> перемошенничать этого мошенника."],
  ["sycosis", "<big>sycosis</big>\n\n<b>sycosis, is</b> <i>f</i> (<i>греч.</i>) <i>мед.</i>\nмясистый нарост <i>Scr</i>."],
  ["Sycurium", "<big>Sycurium</big>\n\n<b>Sycurium, i</b> <i>n</i>\nСикурий, <i>город в Фессалии, у подошвы горы Occa</i> <i>L</i>."],
  ["Syene", "<big>Syene</big>\n\n<b>Syene, es</b> <i>f</i>\nСиена, <i>самый южный город в Верхнем Египте</i> (<i>ныне</i> Ассуан) <i>PM, Lcn</i>."],
  ["Syenites", "<big>Syenites</big>\n\n<b>I Syenites, ae</b> <i>m</i>\nжитель города Syene <i>PM</i>.\n\n<b>II Syenites, ae</b> <i>adj. m</i>\nсиенский (lapis <i>O</i>)."],
  ["Sygambr-", "<big>Sygambr-</big>\n\n<b>Sygambr-</b> <i>v. l.</i>\n= Sigambr-."],
  ["Sylla", "<big>Sylla</big>\n\n<b>Sylla, ae</b> <i>m</i> <i>v. l.</i>\n= Sulla."],
  ["syllaba", "<big>syllaba</big>\n\n<b>syllaba, ae</b> <i>f</i> (<i>греч.</i>)\n<b>1)</b> слог: syllaba longa brevi subjecta vocatur iambus <i>H</i> долгий слог с предшествующим кратким называется ямбом; auceps syllabarum <i>C</i> буквоед, педант;\n<b>2)</b> <i>pl.</i> стихи: syllabas vatis (= Catulli) amare <i>M</i> любить стихотворения поэта (Катулла)."],
  ["syllabatim", "<big>syllabatim</big>\n\n<b>syllabatim</b> [syllaba]\nслог за слогом, по слогам (aliquid alicui dictare, dicere <i>C</i>)."],
  ["syllabus", "<big>syllabus</big>\n\n<b>syllabus, i</b> <i>m</i> (<i>греч.</i>)\nсписок, перечень <i>Aug</i>."],
  ["syllepsis", "<big>syllepsis</big>\n\n<b>syllepsis, is</b> <i>f</i> (<i>греч.</i>) <i>ритор.</i>\nсиллепс, <i>отнесение сказуемого к ряду подлежащих, различных по роду, числу или лицу, напр.</i>: hic <i>arma</i>, hic <i>currus fuit</i> <i>V</i> здесь (на Самосе) было оружие (Юноны), здесь (была и её) колесница."],
  ["syllogismus", "<big>syllogismus</big>\n\n<b>syllogismus, i</b> <i>m</i> (<i>греч.</i>)\nсиллогизм <i>Sen, PJ, AG</i>: s. rhetoricus <i>Q</i> = enthymema."],
  ["syllogisticus", "<big>syllogisticus</big>\n\n<b>syllogisticus, a, um</b> [syllogismus]\nсиллогистический <i>Q</i>."],
  ["sylv-", "<big>sylv-</big>\n\n<b>sylv-</b> <i>v. l.</i>\n= silv-."],
  ["Symaetheus", "<big>Symaetheus</big>\n\n<b>Symaetheus</b> <i>и</i> <b>Symaethius, a, um</b> [Symaethus]\nсиметский (aquae <i>O</i>): Symaethia flumina <i>V</i> притоки Симета; S. heros <i>O</i> = Acis."],
  ["Symaethis", "<big>Symaethis</big>\n\n<b>Symaethis, idis</b> <i>f</i> [Symaethus]\nсиметская (nympha <i>O</i>)."],
  ["Symaethum", "<big>Symaethum</big>\n\n<b>Symaethum, i</b> <i>n</i> <i>и</i> <b>Symaethus, i</b> <i>m</i>\nСимет, <i>река на вост. побережье Сицилии, впадающая в море южнее Катании</i> <i>PM, Sil, LJ</i>."],
  ["symbola", "<big>symbola</big>\n\n<b>symbola, ae</b> <i>f</i> (<i>греч.</i>)\n<b>1)</b> взнос, пай, доля (участника общего пиршества): symbolarum collatores <i>Pl</i> участники складчины; ut de symbolis essemus <i>Ter</i> чтобы нам организовать трапезу в складчину;\n<b>2)</b> пир, угощение: paratae jam sunt scapulis symbolae <i>Pl</i> для (моих) лопаток готово уже угощение, <i>т. е.</i> побои."],
  ["symbolice", "<big>symbolice</big>\n\n<b>symbolice</b> [symbolum]\nсимволически, фигурально (appellare <i>AG</i>)."],
  ["symbolum", "<big>symbolum</big>\n\n<b>symbolum, i</b> <i>n</i> <i>и</i> <b>symbolus, i</b> <i>m</i> (<i>греч.</i>)\n<b>1)</b> отличительный признак, печать: s., expressa in cera imago <i>Pt</i> знак, оттиск на воске || опознавательный знак (<i>род удостоверения личности</i>) (ostendere symbolum alicui <i>Pl</i>); перстень с печатью <i>PM</i>;\n<b>2)</b> символ (исповедание) веры <i>Aug</i>;\n<b>3)</b> (= symbola 2) пир: dantes symbola <i>Vlg</i> чревоугодники."],
  ["Symmachus", "<big>Symmachus</big>\n\n<b>Symmachus, i</b> <i>m</i>\n(Q. Aurelius) Симмах, <i>римск. оратор и политик IV в. н. э.</i> <i>Amm, Macr, Sid, Aus</i>."],
  ["symmetria", "<big>symmetria</big>\n\n<b>symmetria, ae</b> <i>f</i> (<i>греч.</i>)\nсимметрия <i>Vr, Vtr, PM</i>."],
  ["symmystes", "<big>symmystes</big>\n\n<b>symmystes (-a), ae</b> <i>m</i> (<i>греч.</i>)\nсоучастник мистерий <i>Hier</i>."],
  ["sympasma", "<big>sympasma</big>\n\n<b>sympasma, atis</b> <i>n</i> (<i>греч.</i>) <i>мед.</i>\nприсыпка <i>CA</i>."],
  ["sympathia", "<big>sympathia</big>\n\n<b>sympathia, ae</b> <i>f</i> (<i>греч.</i>)\nвзаимное сочувствие, симпатия <i>Vr, PM</i>; взаимное влечение <i>или</i> соответствие (s. stellarum et symphoniarum <i>Vtr</i>)."],
  ["symphonia", "<big>symphonia</big>\n\n<b>symphonia, ae</b> <i>f</i> (<i>греч.</i>)\n<b>1)</b> музыка, концерт: quum s. caneret <i>C</i> когда (пока) играла музыка; ad symphoniam <i>Pt</i> под звуки музыки;\n<b>2)</b> оркестр: aliquid ad symphoniam canere <i>Sen</i> петь что-л. в сопровождении оркестра."],
  ["symphoniacus", "<big>symphoniacus</big>\n\n<b>I symphoniacus, a, um</b> [symphonia]\nмузыкантский, концертный (artes <i>Eccl</i>): symphoniaci pueri (<i>или</i> servi) <i>C</i> музыканты оркестра; symphoniaca herba <i>Pall, Veg etc.</i> = hyoscyamus.\n\n<b>II symphoniacus, i</b> <i>m</i>\nмузыкант <i>Pt</i>."],
  ["Symplegades", "<big>Symplegades</big>\n\n<b>Symplegades, um</b> <i>f</i> (<i>реже</i> <i>sg.</i>) (<i>греч.</i> «сталкивающиеся»)\nСимплегады:\n<b>1)</b> <i>два скалистых островка в Боспоре, у входа в Чёрное море, которые, по преданию, то расходились, то сталкивались, раздавливая всё, что попадало между ними, но после того, как прошёл корабль Арго, стали неподвижны</i> <i>O, VF, Lcn etc.</i>;\n<b>2)</b> <i>M, Aus шутл.</i> = nates."],
  ["Symplegas", "<big>Symplegas</big>\n\n<b>I Symplegas, ados</b> <i>f sg. к</i>\nSymplegades.\n\n<b>II symplegas, adis</b> <i>f</i>\nсцепление, плотное скопление (limi <i>Rut</i>)."],
  ["symplegma", "<big>symplegma</big>\n\n<b>symplegma, atis</b> <i>n</i> (<i>греч.</i> «сплетение»)\n<b>1)</b> симплегма, <i>скульптурная группа сплетённых тел</i> (<i>борцов</i>) <i>PM</i>;\n<b>2)</b> <i>ирон.</i> крепкое объятие (symplegmate copulari <i>M</i>)."],
  ["symposiacus", "<big>symposiacus</big>\n\n<b>symposiacus, a, um</b> [symposium]\nпиршественный, застольный (quaestiunculae <i>AG</i>)."],
  ["symposium", "<big>symposium</big>\n\n<b>symposium, i</b> <i>n</i> (<i>греч.</i>)\nпир, пиршество, <i>одно из произведений Платона и Ксенофонта</i> <i>Nep, AG, PM</i>."],
  ["sympoticus", "<big>sympoticus</big>\n\n<b>sympoticus, a, um</b> <i>AG</i>\n= symposiacus."],
  ["synagoga", "<big>synagoga</big>\n\n<b>synagoga, ae</b> <i>f</i> (<i>греч.</i>)\nсинагога <i>Eccl</i>."],
  ["synaloephe", "<big>synaloephe</big>\n\n<b>synaloephe (synaliphe), es</b> <i>f</i>\nсиналефа, стяжение двух слогов в один (<i>вследствие красиса, элизии или синэресиса</i>) <i>Q</i>."],
  ["synanche", "<big>synanche</big>\n\n<b>synanche, es</b> <i>f</i> (<i>греч.</i>)\nвоспаление горла <i>AG, CA, Veg</i>."],
  ["Synapothnescontes", "<big>Synapothnescontes</big>\n\n<b>Synapothnescontes</b> (<i>греч.</i> «умирающие вместе»; <i>лат.</i> Commorientes)\n<i>название комедии Дифила</i> <i>Ter</i>."],
  ["Synaristosae", "<big>Synaristosae</big>\n\n<b>Synaristosae, arum</b> <i>f</i> (<i>греч.</i> «вместе завтракающие»)\n<i>название комедий Менандра и Цецилия</i> <i>PM, AG</i>."],
  ["syncopatus", "<big>syncopatus</big>\n\n<b>syncopatus, a, um</b> (<i>греч.</i>)\nупавший в обморок <i>Veg</i>."],
  ["syncope", "<big>syncope</big>\n\n<b>syncope, es</b> <i>f</i>\n<b>1)</b> <i>лингв.</i> синкопа, <i>выпадение внутреннего слога или звука</i> <i>Macr</i>;\n<b>2)</b> обморок <i>Veg</i>."],
  ["syndicus", "<big>syndicus</big>\n\n<b>syndicus, i</b> <i>m</i> (<i>греч.</i>)\nсиндик, поверенный города, общества <i>и т. п.</i> <i>Dig</i>."],
  ["synecdoche", "<big>synecdoche</big>\n\n<b>synecdoche, es</b> <i>f</i> (<i>греч.</i> «соподразумеваемость») <i>ритор.</i>\nсинекдоха, <i>название части вместо целого, причины вместо следствия, собственного имени вместо нарицательного и пр.</i> <i>Q</i>."],
  ["synedrus", "<big>synedrus</big>\n\n<b>synedrus, i</b> <i>m</i> (<i>греч.</i> «заседатель»)\nсинедр, <i>член высшего совета в Македонии</i> <i>L</i>."],
  ["Synephebi", "<big>Synephebi</big>\n\n<b>Synephebi, orum</b> <i>m</i> (<i>греч.</i> «юные сверстники»)\n<i>название комедии Сжатия Цецилия</i> <i>C</i>."],
  ["syngrapha", "<big>syngrapha</big>\n\n<b>syngrapha, ae</b> <i>f</i> (<i>греч.</i> «письменный документ»)\nдолговая расписка, платёжное обязательство <i>G, Spart, Amm</i>: s. sestertii centies <i>C</i> обязательство в уплате десяти миллионов сестерциев; syngrapham facere cum aliquo <i>C</i> получить расписку с кого-л.; per syngrapham <i>C</i> по долговой расписке."],
  ["syngraphus", "<big>syngraphus</big>\n\n<b>syngraphus, i</b> <i>m</i> (<i>греч.</i>)\n<b>1)</b> договор, контракт <i>Pl</i>;\n<b>2)</b> проходное свидетельство, пропуск <i>Pl</i>."],
  ["synizesis", "<big>synizesis</big>\n\n<b>synizesis, is</b> <i>f</i> (<i>греч.</i>)\nсинизесис, <i>стяжение двух гласных в один</i> (<i>напр.</i>, <i>в</i> s. haud mihi deero <i>H</i>, <i>Sat. 1, 9, 56, тж. 2, 1, 17 <i>и</i> 2, 2, 98, где ее произносится односложно</i>, или в fluviorum — <i>произносится трёхсложно</i> fluvjorum — <i>V</i>, <i>Georg. I, 482</i>)."],
  ["Synnada", "<big>Synnada</big>\n\n<b>I Synnada, ae</b> <i>f</i> <i>Cld</i>\n= Synnada II.\n\n<b>II Synnada, orum</b> <i>n</i> <i>и</i> <b>Synnas, adis</b> <i>f</i>\nСиннады, <i>город в Большой Фригии</i> (<i>славившийся мрамором</i>) <i>C, PM, M, St</i>."],
  ["Synnadensis", "<big>Synnadensis</big>\n\n<b>Synnadensis, e</b> <i>и</i> <b>Synnadicus, a, um</b> [Synnada]\nсиннадийский (forum <i>C</i>; lapis <i>PM</i>)."],
  ["Synnas", "<big>Synnas</big>\n\n<b>I Synnas, adis</b> <i>adj. f</i> [Synnada]\nсиннадийская (columnae <i>Capit</i>).\n\n<b>II Synnas, adis</b> <i>f</i> <i>v. l.</i>\n= Synnada II."],
  ["synodia", "<big>synodia</big>\n\n<b>synodia, ae</b> <i>f</i> (<i>греч.</i>)\nсогласное (стройное) пение (synodiam recinere <i>Vr</i>)."],
  ["synoditae", "<big>synoditae</big>\n\n<b>synoditae, arum</b> <i>m</i> (<i>греч.</i>)\nсинодиты, монашествующие <i>CJ</i>."],
  ["synodus", "<big>synodus</big>\n\n<b>I synodus, i</b> <i>f</i> (<i>греч.</i>)\nвысшая священническая коллегия, синод <i>CJ, Amm, Eccl</i>.\n\n<b>II synodus, dontis</b> <i>m</i> (<i>греч.</i>) <i>O, PM</i>\n= dentex."],
  ["synoecium", "<big>synoecium</big>\n\n<b>synoecium, i</b> <i>n</i> (<i>греч.</i>)\nсинэкий, общая комната, общежитие <i>Pt</i>."],
  ["synoneton", "<big>synoneton</big>\n\n<b>synoneton, i</b> <i>n</i> (<i>греч.</i>)\nоптовая покупка, скупка <i>CTh</i>."],
  ["synonymum", "<big>synonymum</big>\n\n<b>synonymum (-on), i</b> <i>n</i> (<i>греч.</i>)\nсиноним <i>Fronto</i>."],
  ["synopsis", "<big>synopsis</big>\n\n<b>synopsis, is</b> <i>f</i> (<i>греч.</i>)\nсводка, сжатый перечень <i>Dig</i>."],
  ["syntexis", "<big>syntexis</big>\n\n<b>syntexis, is</b> <i>f</i> (<i>греч.</i>) <i>мед.</i>\nболезненное истощение, худосочие <i>PM, CA</i>."],
  ["synthema", "<big>synthema</big>\n\n<b>synthema, atis</b> <i>n</i> (<i>греч.</i>)\nподорожная <i>Hier</i>."],
  ["synthesinus", "<big>synthesinus</big>\n\n<b>synthesinus, a, um</b> [synthesis 2] (<i>греч.</i>):\nsynthesina vestis <i>Su</i> домашнее платье (надевавшееся к столу)."],
  ["synthesis", "<big>synthesis</big>\n\n<b>synthesis, is</b> <i>f</i> (<i>греч.</i>)\n<b>1)</b> набор, комплект (посуды, одежды <i>и т. п.</i>) <i>M, Dig</i>; сервиз <i>M, St</i>;\n<b>2)</b> домашнее платье <i>M</i>."],
  ["syntonum", "<big>syntonum</big>\n\n<b>syntonum, i</b> <i>n</i> (<i>греч.</i>; <i>лат.</i> scabellum)\nсинтон, <i>музыкальный ударный инструмент</i> <i>Q</i>."],
  ["Syphax", "<big>Syphax</big>\n\n<b>Syphax, acis</b> <i>m</i>\nСифак, <i>царь массесилиев в зап. Нумидии (во время II Пунической войны), союзник сначала римлян, а затем карфагенян, зять Гасдрубала; в 203 г. до н. э. был разбит Масиниссой и попал в плен к римлянам, где и умер</i> <i>Sl, L, Prp, Cld</i>."],
  ["Syracosius", "<big>Syracosius</big>\n\n<b>I Syracosius (Syracusius), a, um</b>\nсиракузский <i>C, V</i>.\n\n<b>II Syracosius (Syracusius), i</b> <i>m</i>\nжитель города Siracusae <i>C, PM</i>."],
  ["Syracusae", "<big>Syracusae</big>\n\n<b>Syracusae, arum</b> <i>f</i>\nСиракузы, <i>город на вост. побережье Сицилии, основанный коринфскими выходцами в 735 г до н. э.</i> (<i>ныне</i> Siragossa) <i>C, L, Nep etc.</i>"],
  ["Syracusanus", "<big>Syracusanus</big>\n\n<b>I Syracusanus, a, um</b>\nсиракузский <i>C</i>.\n\n<b>II Syracusanus, i</b> <i>m</i>\nжитель города Syracusae <i>C</i>."],
  ["Syracusius", "<big>Syracusius</big>\n\n<b>Syracusius</b> <i>C, PM</i>\n= Syracusanus I, II."],
  ["Syria", "<big>Syria</big>\n\n<b>Syria, ae</b> <i>f</i>\nСирия, <i>страна между рекой Евфрат, Средиземным морем, Таврским хребтом и Аравией <i>C, Mela, PM etc.</i>; иногда</i> <i>C, Su</i> = Assyria."],
  ["Syriace", "<big>Syriace</big>\n\n<b>Syriace</b>\nпо-сирийски (loqui <i>Vlg</i>)."],
  ["Syriacus", "<big>Syriacus</big>\n\n<b>Syriacus, Syriaticus</b> <i>и</i> <b>Syricus, a, um</b>\nсирийский <i>C, PM, Dig, Col</i>."],
  ["syringa", "<big>syringa</big>\n\n<b>syringa, ae</b> <i>f</i> (<i>греч.</i>) <i>мед.</i>\nспринцовка, <i>перен.</i> промывание <i>Veg</i>."],
  ["syrinx", "<big>syrinx</big>\n\n<b>I syrinx, ingis</b> (<i>acc.</i> inga) <i>f</i> (<i>греч.</i>)\nтростник, <i>перен.</i> тростниковая свирель (семиствольная) <i>O, Amm</i>.\n\n<b>II Syrinx, ingis</b> <i>f</i>\nСиринга, <i>аркадская нимфа, превращенная в тростник</i> <i>O</i>."],
  ["Syriscus", "<big>Syriscus</big>\n\n<b>Syriscus, i</b> <i>m</i> [<i>demin. к</i> Syrus]\nСириек, <i>прозвище раба</i> <i>Ter</i>."],
  ["Syrius", "<big>Syrius</big>\n\n<b>I Syrius, a, um</b>\nсирийский <i>C, PM, Su</i>.\n\n<b>II Syrius, i</b> <i>m</i>\nсириец <i>Just</i>."],
  ["syrma", "<big>syrma</big>\n\n<b>syrma, atis</b> <i>n</i> (<i>греч.</i>)\n<b>1)</b> сирма, <i>длинное платье со шлейфом</i> (<i>преим. трагических актёров</i>) <i>SenT, J, Sol, Sid</i>;\n<b>2)</b> трагедия <i>M, Sid, Vop</i>: syrmata volvere <i>J</i> прочитывать трагедии."],
  ["syrmaticus", "<big>syrmaticus</big>\n\n<b>syrmaticus, a, um</b> [syrma]\nс трудом волочащий ноги (jumentum <i>Veg</i>)."],
  ["Syrophoenissa", "<big>Syrophoenissa</big>\n\n<b>Syrophoenissa, ae</b> <i>f</i>\nсирофиникиянка <i>Vlg, Hier</i>."],
  ["Syrophoenix", "<big>Syrophoenix</big>\n\n<b>Syro–phoenix, icis</b> <i>m</i>\nсирофиникиянин <i>LM, J</i>."],
  ["Syros", "<big>Syros</big>\n\n<b>Syros, i</b> <i>f</i>\nСирое, <i>один из Кикладских о-вов, между Андросом и Паросом, ридина Ферекида</i> (<i>ныне</i> Сира) <i>O, Mela</i>."],
  ["Syrticus", "<big>Syrticus</big>\n\n<b>Syrticus, a, um</b> [Syrtis]\nсиртский <i>Sen, Sid</i>: Syrticae gentes <i>Sen</i> племена области обоих Сиртов."],
  ["Syrtis", "<big>Syrtis</big>\n\n<b>Syrtis, is</b> <i>f</i> (<i>греч.</i> «отмель»)\nСирт:\n<b>1)</b> S. major <i>и</i> S. minor, <i>тж. <i>pl. PM, Sl, L etc.</i>, два изобилующих мелями залива на сев. побережье Африки, между Карфагеном и Киреной</i> (<i>ныне заливы</i> Сидра и Кабес);\n<b>2)</b> побережье обоих Сиртов <i>H, Lcn</i>;\n<b>3)</b> отмель <i>или</i> подводная скала <i>C</i>."],
  ["Syrus", "<big>Syrus</big>\n\n<b>I Syrus, a, um</b> [Syria]\nсирийский <i>H, M etc.</i>\n\n<b>II Syrus, i</b> <i>m</i>\n<b>1)</b> сириец <i>Pl, C, L etc.</i>;\n<b>2)</b> <i>мужское имя</i> (<i>раба</i>) <i>Ter</i>;\n<b>3)</b> Publilius S., <i>римск. мимограф I века до н. э., автор «Sententiae».</i>\n\n<b>III syrus, i</b> <i>m</i> (<i>греч.</i>)\nметла, веник <i>Vr</i>."],
  ["systole", "<big>systole</big>\n\n<b>systole, es</b> <i>f</i> (<i>греч.</i>; <i>лат.</i> correptio)\nсокращение долгого по природе слога <i>Is</i>."],
  ["systylos", "<big>systylos</big>\n\n<b>systylos, on</b>\nс близко друг от друга расположенными колоннами (<i>т. е.</i> на расстоянии двойного диаметра одной колонны) <i>Vir.</i>"]
]